INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'اللہ کے نام سے شروع جو بہت مہربان رحمت والا'),
(2, 1, 2, 'سب خوبیاں اللہ کو جو مالک سارے جہان والوں کا،'),
(3, 1, 3, 'بہت مہربان رحمت والا،'),
(4, 1, 4, 'روز جزا کا مالک،'),
(5, 1, 5, 'ہم تجھی کو پوجیں اور تجھی سے مدد چاہیں،'),
(6, 1, 6, 'ہم کو سیدھا راستہ چلا،'),
(7, 1, 7, 'راستہ ان کا جن پر تو نے احسان کیا، نہ ان کا جن پر غضب ہوا اور نہ بہکے ہوؤں کا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم'),
(9, 2, 2, 'وہ بلند رتبہ کتاب (قرآن) کوئی شک کی جگہ نہیں، اس میں ہدایت ہے ڈر والوں کو'),
(10, 2, 3, 'وہ جو بے دیکھے ایمان لائیں اور نماز قائم رکھیں اور ہماری دی ہوئی روزی میں سے ہماری میں اٹھائیں-'),
(11, 2, 4, 'اور وہ کہ ایمان لائیں اس پر جو اے محبوب تمہاری طرف اترا اور جو تم سے پہلے اترا اور آخرت پر یقین رکھیں،'),
(12, 2, 5, 'وہی لوگ اپنے رب کی طرف سے ہدایت پر ہیں اور وہی مراد کو پہنچنے والے۔'),
(13, 2, 6, 'بیشک وہ جن کی قسمت میں کفر ہے ا نہیں برابر ہے، چاہے تم انہیں ڈراؤ، یا نہ ڈراؤ، وہ ایمان لانے کے نہیں۔'),
(14, 2, 7, 'اللہ نے ان کے دلوں پر اور کانوں پر مہر کردی اور ان کی آنکھوں پر گھٹاٹوپ ہے، اور ان کے لئے بڑا عذاب،'),
(15, 2, 8, 'اور کچھ لوگ کہتے ہیں کہ ہم اللہ اور پچھلے دن پر ایمان لائے اور وہ ایمان والے نہیں،'),
(16, 2, 9, 'فریب دیا چاہتے ہیں اللہ اور ایمان والوں کو اور حقیقت میں فریب نہیں دیتے مگر اپنی جانوں کو اور انہیں شعور نہیں۔'),
(17, 2, 10, 'ان کے دلوں میں بیماری ہے تو اللہ نے ان کی بیماری اور بڑھائی اور ان کے لئے دردناک عذاب ہے، بدلا ان کے جھوٹ کا -'),
(18, 2, 11, 'اوران سے کہا جائے زمین میں فساد نہ کرو، تو کہتے ہیں ہم تو سنوارنے والے ہیں،'),
(19, 2, 12, 'سنتا ہے وہی فسادی ہیں مگر انہیں شعور نہیں،'),
(20, 2, 13, 'اور جب ان سے کہا جائے ایمان لاؤ جیسے اور لوگ ایمان لائے تو کہیں کیا ہم احمقوں کی طرح ایمان لے آئیں سنتا ہے وہی احمق ہیں مگر جانتے نہیں -'),
(21, 2, 14, 'اور جب ایمان والوں سے ملیں تو کہیں ہم ایمان لائے اور جب اپنے شیطانوں کے پاس اکیلے ہوں تو کہیں ہم تمہارے ساتھ ہیں، ہم تو یونہی ہنسی کرتے ہیں-'),
(22, 2, 15, 'اللہ ان سے استہزاء فرماتا ہے (جیسا کہ اس کی شان کے لائق ہے) اور انہیں ڈھیل دیتا ہے کہ اپنی سرکشی میں بھٹکتے رہیں۔'),
(23, 2, 16, 'یہ لوگ جنہوں نے ہدایت کے بدلے گمراہی خریدی تو ان کا سودا کچھ نفع نہ لایا اور وہ سودے کی راہ جانتے ہی نہ تھے -'),
(24, 2, 17, 'ان کی کہاوت اس طرح ہے جس نے آگ روشن کی۔ تو جب اس سے آس پاس سب جگمگا اٹھا اللہ ان کا نور لے گیا اور انہیں اندھیریوں میں چھوڑ دیا کہ کچھ نہیں سوجھتا -'),
(25, 2, 18, 'بہرے، گونگے، ندھے تو وہ پھر آ نے والے نہیں،'),
(26, 2, 19, 'یا جیسے آسمان سے اترتا پانی کہ ان میں اندھیریاں ہیں اور گرج اور چمک اپنے کانوں میں انگلیاں ٹھونس رہے ہیں، کڑک کے سبب، موت کے ڈر سے اور اللہ کافروں کو، گھیرے ہوئے ہے -'),
(27, 2, 20, 'بجلی یوں معلوم ہوتی ہے کہ ان کی نگاہیں اچک لے جائے گی جب کچھ چمک ہوئی اس میں چلنے لگے اور جب اندھیرا ہوا کھڑے رہ گئے اور اللہ چاہتا تو ان کے کان اور آنکھیں لے جاتا بیشک اللہ سب کچھ کرسکتا ہے -'),
(28, 2, 21, 'اے لوگو! اپنے رب کو پوجو جس نے تمہیں اور تم سے اگلوں کو پیدا کیا، یہ امید کرتے ہوئے، کہ تمہیں پرہیزگاری ملے -'),
(29, 2, 22, 'جس نے تمہارے لئے زمین کو بچھونا اور آسمان کو عمارت بنایا اور آسمان سے پانی اتارا تو اس سے کچھ پھل نکالے تمہارے کھانے کو۔ تو اللہ کے لئے جان بوجھ کر برابر والے نہ ٹھہراؤ'),
(30, 2, 23, 'اور اگر تمہیں کچھ شک ہو اس میں جو ہم نے اپنے (اس خاص) بندے پر اتارا تو اس جیسی ایک سورت تو لے آؤ اور اللہ کے سوا، اپنے سب حمایتیوں کو بلالو، اگر تم سچے ہو۔'),
(31, 2, 24, 'پھر اگر نہ لا سکو اور ہم فرمائے دیتے ہیں کہ ہر گز نہ لا سکو گے تو ڈرو اس آگ سے، جس کا ایندھن آدمی اور پتھر ہیں تیار رکھی ہے کافروں کے لئے -'),
(32, 2, 25, 'اور خوشخبری دے، انہیں جو ایمان لائے اور اچھے کام کئے، کہ ان کے لئے باغ ہیں، جن کے نیچے نہریں رواں جب انہیں ان باغوں سے کوئی پھل کھانے کو دیا جائے گا، (صورت دیکھ کر) کہیں گے، یہ تو وہی رزق ہے جو ہمیں پہلے ملا تھا اور وہ (صورت میں) ملتا جلتا انہیں دیا گیا اور ان کے لئے ان باغوں میں ستھری بیبیاں ہیں اور وہ ان میں ہمیشہ رہیں گے -'),
(33, 2, 26, 'بیشک اللہ اس سے حیا نہیں فرماتا کہ مثال سمجھانے کو کیسی ہی چیز کا ذکر فرمائے مچھر ہو یا اس سے بڑھ کر تو وہ جو ایمان لائے، وہ تو جانتے ہیں کہ یہ ان کے رب کی طرف سے حق ہے رہے کافر، وہ کہتے ہیں ایسی کہاوت میں اللہ کا کیا مقصُود ہے، اللہ بہتیروں کو اس سے گمراہ کرتا ہے اور بہتیروں کو ہدایت فرماتا ہے اور اس سے انہیں گمراہ کرتا ہے جو بے حکم ہیں-'),
(34, 2, 27, 'وہ جو اللہ کے عہد کو توڑ دیتے ہیں پکا ہونے کے بعد، اور کاٹتے ہیں اس چیز کو جس کے جوڑنے کا خدا نے حکم دیا ہے اور زمین میں فساد پھیلاتے ہیں وہی نقصان میں ہیں۔'),
(35, 2, 28, 'بھلا تم کیونکر خدا کے منکر ہو گے، حالانکہ تم مردہ تھے اس نے تمہیں جِلایا پھر تمہیں مارے گا پھر تمہیں جِلائے گا پھر اسی کی طرف پلٹ کر جاؤ گے -'),
(36, 2, 29, 'وہی ہے جس نے تمہارے لئے بنایا جو کچھ زمین میں ہے۔ پھر آسمان کی طرف استوا (قصد) فرمایا تو ٹھیک سات آسمان بنائے وہ سب کچھ جانتا ہے -'),
(37, 2, 30, 'اور یاد کرو جب تمہارے رب نے فرشتوں سے فرمایا، میں زمین میں اپنا نائب بنانے والا ہوں بولے کیا ایسے کو نائب کرے گا جو اس میں فساد پھیلائے گا اور خونریزیاں کرے گا اور ہم تجھے سراہتے ہوئے، تیری تسبیح کرتے اور تیری پاکی بولتے ہیں، فرمایا مجھے معلوم ہے جو تم نہیں جانتے-'),
(38, 2, 31, 'اور اللہ تعالیٰ نے آدم کو تمام (اشیاء کے) نام سکھائے پھر سب (اشیاء) کو ملائکہ پر پیش کرکے فرمایا سچے ہو تو ان کے نام تو بتاؤ'),
(39, 2, 32, 'بولے پاکی ہے تجھے ہمیں کچھ علم نہیں مگر جتنا تو نے ہمیں سکھایا بے شک تو ہی علم و حکمت والا ہے -'),
(40, 2, 33, 'فرمایا اے آدم بتادے انہیں سب (اشیاء کے) نام جب اس نے (یعنی آدم نے) انہیں سب کے نام بتادیئے فرمایا میں نہ کہتا تھا کہ میں جانتا ہوں آسمانوں اور زمین کی سب چھپی چیزیں اور میں جانتا ہوں جو کچھ تم ظاہر کرتے اور جو کچھ تم چھپاتے ہو -'),
(41, 2, 34, 'اور (یاد کرو) جب ہم نے فرشتوں کو حکم دیا کہ آدم کو سجدہ کو تو سب نے سجدہ کیا سوائے ابلیس کے کہ منکر ہوا اور غرور کیا اور کافر ہوگیا-'),
(42, 2, 35, 'اور ہم نے فرمایا اے آدم تو اور تیری بیوی جنت میں رہو اور کھاؤ اس میں سے بے روک ٹوک جہاں تمہارا جی چاہے مگر اس پیڑ کے پاس نہ جانا کہ حد سے بڑھنے والوں میں ہوجاؤ گے-'),
(43, 2, 36, 'تو شیطان نے اس سے (یعنی جنت سے) انہیں لغزش دی اور جہاں رہتے تھے وہاں سے انہیں الگ کردیا اور ہم نے فرمایا نیچے اترو آپس میں ایک تمہارا دوسرے کا دشمن اور تمہیں ایک وقت تک زمین میں ٹھہرنا اور برتنا ہے -'),
(44, 2, 37, 'پھر سیکھ لیے آدم نے اپنے رب سے کچھ کلمے تو اللہ نے اس کی توبہ قبول کی بیشک وہی ہے بہت توبہ قبول کرنے والا مہربان۔'),
(45, 2, 38, 'ہم نے فرمایا تم سب جنت سے اتر جاؤ پھر اگر تمہارے پاس میری طرف سے کوئی ہدایت آئے تو جو میری ہدایت کا پیرو ہوا اسے نہ کوئی اندیشہ نہ کچھ غم -'),
(46, 2, 39, 'اور وہ جو کفر کریں گے اور میری آیتیں جھٹلائیں گے وہ دوزخ والے ہیں، ان کو ہمیشہ اس میں رہنا -'),
(47, 2, 40, 'اے یعقوب کی اولاد یاد کرو میرا وہ احسان جو میں نے تم پر کیا اور میرا عہد پورا کرو میں تمہارا عہد پورا کروں گا اور خاص میرا ہی ڈر رکھو -'),
(48, 2, 41, 'اور ایمان لاؤ اس پر جو میں نے اتارا اس کی تصدیق کرتا ہوا جو تمہارے ساتھ ہے اور سب سے پہلے اس کے منکر نہ بنو اور میری آیتوں کے بدلے تھوڑے دام نہ لو اور مجھی سے ڈرو -'),
(49, 2, 42, 'اور حق سے باطل کو نہ ملاؤ اور دیدہ و دانستہ حق نہ چھپاؤ -'),
(50, 2, 43, 'اور نماز قائم رکھو اور زکوٰة دو اور رکوع کرنے والوں کے ساتھ رکوع کرو-'),
(51, 2, 44, 'کیا لوگوں کو بھلائی کا حکم دیتے ہو اور اپنی جانوں کو بھولتے ہو حالانکہ تم کتاب پڑھتے ہو تو کیا تمہیں عقل نہیں -'),
(52, 2, 45, 'اور صبر اور نماز سے مدد چاہو اور بیشک نماز ضرور بھاری ہے مگر ان پر (نہیں) جو دل سے میری طرف جھکتے ہیں -'),
(53, 2, 46, 'جنہیں یقین ہے کہ انہیں اپنے رب سے ملنا ہے اور اسی کی طرف پھرنا-'),
(54, 2, 47, 'اے اولاد یعقوب یاد کرو میرا وہ احسان جو میں نے تم پر کیا اور یہ کہ اس سارے زمانہ پر تمہیں بڑائی دی-'),
(55, 2, 48, 'اور ڈرو اس دن سے جس دن کوئی جان دوسرے کا بدلہ نہ ہوسکے گی اور نہ (کافر کے لئے) کوئی سفارش مانی جائے اور نہ کچھ لے کر (اس کی) جان چھوڑی جائے اور نہ ان کی مدد ہو-'),
(56, 2, 49, 'اور (یاد کرو) جب ہم نے تم کو فرعون والوں سے نجات بخشی کہ وہ تم پر برا عذاب کرتے تھے تمہارے بیٹوں کو ذبح کرتے اور تمہاری بیٹیوں کو زندہ رکھتے اور اس میں تمہارے رب کی طرف سے بڑی بلا تھی (یا بڑا انعام)'),
(57, 2, 50, 'اور جب ہم نے تمہارے لئے دریا پھاڑ دیا تو تمہیں بچالیا اور فرعون والوں کو تمہاری آنکھوں کے سامنے ڈبو دیا -'),
(58, 2, 51, 'اور جب ہم نے موسیٰ سے چالیس رات کا وعدہ فرمایا پھر اس کے پیچھے تم نے بچھڑے کی پوجا شروع کردی اور تم ظالم تھے -'),
(59, 2, 52, 'پھر اس کے بعد ہم نے تمہیں معافی دی کہ کہیں تم احسان مانو -'),
(60, 2, 53, 'اور جب ہم نے موسیٰ کو کتاب عطا کی اور حق و باطل میں تمیز کردینا کہ کہیں تم راہ آؤ -'),
(61, 2, 54, 'اور جب موسیٰ نے اپنی قوم سے کہا اے میری قوم تم نے بچھڑا بناکر اپنی جانوں پر ظلم کیا تو اپنے پیدا کرنے والے کی طرف رجوع لاؤ تو آپس میں ایک دوسرے کو قتل کردو یہ تمہارے پیدا کرنے والے کے نزدیک تمہارے لیے بہتر ہے تو اس نے تمہاری توبہ قبول کی بیشک وہی ہے بہت توبہ قبول کرنے والا مہربان -'),
(62, 2, 55, 'اور جب تم نے کہا اے موسیٰ ہم ہرگز تمہارا یقین نہ لائیں گے جب تک اعلانیہ خدا کو نہ دیکھ لیں تو تمہیں کڑک نے آلیا اور تم دیکھ رہے تھے ۔'),
(63, 2, 56, 'پھر مرے پیچھے ہم نے تمہیں زندہ کیا کہ کہیں تم احسان مانو-'),
(64, 2, 57, 'اور ہم نے ابر کو تمہارا سائبان کیا اور تم پر من اور سلویٰ اتارا کھاؤ ہماری دی ہوئی ستھری چیزیں اور انہوں نے کچھ ہمارا نہ بگاڑا ہاں اپنی ہی جانوں کو بگاڑ کرتے تھے- اور جب ہم نے فرمایا اس بستی میں جاؤ -'),
(65, 2, 58, 'پھر اس میں جہاں چاہو بے روک ٹوک کھاؤ اور دروازہ میں سجدہ کرتے داخل ہو اور کہو ہمارے گناہ معاف ہوں ہم تمہاری خطائیں بخش دیں گے اور قریب ہے کہ نیکی والوں کو اور زیادہ دیں-'),
(66, 2, 59, 'تو ظالموں نے اور بات بدل دی جو فرمائی گئی تھی اس کے سوا تو ہم نے آسمان سے ان پر عذاب اتارا بدلہ ان کی بے حکمی کا۔'),
(67, 2, 60, 'اور جب موسیٰ نے اپنی قوم کے لئے پانی مانگا تو ہم نے فرمایا اس پتھر پر اپنا عصا مارو فوراً اس میں سے بارہ چشمے بہ نکلے ہر گروہ نے اپنا گھاٹ پہچان لیا کھاؤ اور پیؤ خدا کا دیا اور زمین میں فساد اٹھاتے نہ پھرو'),
(68, 2, 61, 'اور جب تم نے کہا اے موسیٰ ہم سے تو ایک کھانے پر ہرگز صبر نہ ہوگا تو آپ اپنے رب سے دعا کیجئے کہ زمین کی اگائی ہوئی چیزیں ہمارے لئے نکالے کچھ ساگ اور ککڑی اور گیہوں اور مسور اور پیاز فرمایا کیا ادنیٰ چیز کو بہتر کے بدلے مانگتے ہو اچھا مصر یا کسی شہر میں اترو وہاں تمہیں ملے گا جو تم نے مانگا اور ان پر مقرر کردی گئی خواری اور ناداری اور خدا کے غضب میں لوٹے یہ بدلہ تھا اس کا کہ وہ اللہ کی آیتوں کا انکار کرتے اور انبیاء کو ناحق شہید کرتے یہ بدلہ تھا ان کی نافرمانیوں اور حد سے بڑھنے کا-'),
(69, 2, 62, 'بیشک ایمان والے نیز یہودیوں اور نصرانیوں اور ستارہ پرستوں میں سے وہ کہ سچے دل سے اللہ اور پچھلے دن پر ایمان لائیں اور نیک کام کریں ان کا ثواب ان کے رب کے پاس ہے اور نہ انہیں کچھ اندیشہ ہو اور نہ کچھ غم'),
(70, 2, 63, 'اور جب ہم نے تم سے عہد لیا اور تم پر طور کو اونچا کیا لو جو کچھ ہم تم کو دیتے ہیں زور سے اور اس کے مضمون یاد کرو اس امید پر کہ تمہیں پرہیزگاری ملے-'),
(71, 2, 64, 'پھر اس کے بعد تم پھر گئے تو اگر اللہ کا فضل اور اس کی رحمت تم پر نہ ہوتی تو تم ٹوٹے والوں میں ہو جاتے -'),
(72, 2, 65, 'اور بیشک ضرور تمہیں معلوم ہے تم میں کے وہ جنہوں نے ہفتہ میں سرکشی کی تو ہم نے ان سے فرمایا کہ ہوجاؤ بندر دھتکارے ہوئے -'),
(73, 2, 66, 'تو ہم نے (اس بستی کا) یہ واقعہ اس کے آگے اور پیچھے والوں کے لیے عبرت کردیا اور پرہیزگاروں کے لیے نصیحت-'),
(74, 2, 67, 'اور جب موسیٰ نے اپنی قوم سے فرمایا خدا تمہیں حکم دیتا ہے کہ ایک گائے ذبح کرو بولے کہ آپ ہمیں مسخرہ بناتے ہیں فرمایا خدا کی پناہ کہ میں جاہلوں سے ہوں-'),
(75, 2, 68, 'بولے اپنے رب سے دعا کیجئے کہ وہ ہمیں بتادے گائے کیسی کہا وہ فرماتا ہے کہ وہ ایک گائے ہے نہ بوڑھی اور نہ اَدسر بلکہ ان دونوں کے بیچ میں تو کرو جس کا تمہیں حکم ہوتا ہے،'),
(76, 2, 69, 'بولے اپنے رب سے دعا کیجئے ہمیں بتادے اس کا رنگ کیا ہے کہا وہ فرماتا ہے وہ ایک پیلی گائے ہے جس کی رنگت ڈہڈہاتی دیکھنے والوں کو خوشی دیتی،'),
(77, 2, 70, 'بولے اپنے رب سے دعا کیجئے کہ ہمارے لیے صاف بیان کردے وہ گائے کیسی ہے بیشک گائیوں میں ہم کو شبہ پڑگیا اور اللہ چاہے تو ہم راہ پا جائیں گے -'),
(78, 2, 71, 'کہا وہ فرماتا ہے کہ وہ ایک گائے ہے جس سے خدمت نہیں لی جاتی کہ زمین جوتے اور نہ کھیتی کو پانی دے بے عیب ہے جس میں کوئی داغ نہیں بولے اب آپ ٹھیک بات لائے تو اسے ذبح کیا اور (ذبح) کرتے معلوم نہ ہوتے تھے'),
(79, 2, 72, 'اور جب تم نے ایک خون کیا تو ایک دوسرے پر اس کی تہمت ڈالنے لگے اور اللہ کو ظاہر کرنا تھا جو تم چھپاتے تھے،'),
(80, 2, 73, 'تو ہم نے فرمایا اس مقتول کو اس گائے کا ایک ٹکڑا مارو اللہ یونہی مرُدے جلائے گا۔ اور تمہیں اپنی نشانیاں دکھاتا ہے کہ کہیں تمہیں عقل ہو'),
(81, 2, 74, 'پھر اس کے بعد تمہارے دل سخت ہوگئے تو وہ پتھروں کی مثل ہیں بلکہ ان سے بھی زیادہ کرّے اور پتھروں میں تو کچھ وہ ہیں جن سے ندیاں بہہ نکلتی ہیں اور کچھ وہ ہیں جو پھٹ جاتے ہیں تو ان سے پانی نکلتا ہے اور کچھ وہ ہیں جو اللہ کے ڈر سے گر پڑتے ہیں اور اللہ تمہارے کوتکوں سے بے خبر نہیں،'),
(82, 2, 75, 'تو اے مسلمانو! کیا تمہیں یہ طمع ہے کہ یہ (یہودی) تمہارا یقین لائیں گے اور ان میں کا تو ایک گروہ وه تھا کہ اللہ کا کلام سنتے پھر سمجھنے کے بعد اسے دانستہ بدل دیتے، ف۱۲۶)'),
(83, 2, 76, 'اور جب مسلمانوں سے ملیں تو کہیں ہم ایمان لائے اور جب آپس میں اکیلے ہوں تو کہیں وہ علم جو اللہ نے تم پر کھولا مسلمانوں سے بیان کیے دیتے ہو کہ اس سے تمہارے رب کے یہاں تمہیں پر حجت لائیں کیا تمہیں عقل نہیں -'),
(84, 2, 77, 'کیا تم نہیں جانتے کہ اللہ جانتا ہے جو کچھ وہ چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں-'),
(85, 2, 78, 'اور ان میں کچھ اَن پڑھ ہیں کہ جو کتاب کو نہیں جانتے مگر زبانی پڑھ لینا یا کچھ اپنی من گھڑت اور وہ نرے گمان میں ہیں -'),
(86, 2, 79, 'تو خرابی ہے ان کے لئے جو کتاب اپنے ہاتھ سے لکھیں پھر کہہ دیں یہ خدا کے پاس سے ہے کہ اس کے عوض تھوڑے دام حاصل کریں تو خرابی ہے ان کے لئے ان کے ہاتھوں کے لکھے سے اور خرابی ان کے لئے اس کمائی سے-'),
(87, 2, 80, 'اور بولے ہمیں تو آگ نہ چھوئے گی مگر گنتی کے دن تم فرمادو کیا خدا سے تم نے کوئی عہد لے رکھا ہے جب تو اللہ ہرگز اپنا عہد خلاف نہ کرے گا یا خدا پر وہ بات کہتے ہو جس کا تمہیں علم نہیں -'),
(88, 2, 81, 'ہاں کیوں نہیں جو گناہ کمائے اور اس کی خطا اسے گھیر لے وہ دوزخ والوں میں ہے انہیں ہمیشہ اس میں رہنا -'),
(89, 2, 82, 'اور جو ایمان لائے اور اچھے کام کیے وہ جنت والے ہیں انہیں ہمیشہ اس میں رہنا -'),
(90, 2, 83, 'اور جب ہم نے بنی اسرائیل سے عہد لیا کہ اللہ کے سوا کسی کو نہ پوجو اور ماں باپ کے ساتھ بھلائی کرو، اور رشتہ داروں اور یتیموں اور مسکینوں سے اور لوگوں سے اچھی بات کہو اور نماز قائم رکھو اور زکوٰة دو پھر تم پھِر گئے مگر تم میں کے تھوڑے اور تم رد گردان ہو-'),
(91, 2, 84, 'اور جب ہم نے تم سے عہد لیا کہ اپنوں کا خون نہ کرنا اور اپنوں کو اپنی بستیوں سے نہ نکالنا پھر تم نے اس کا اقرار کیا اور تم گواہ ہو-'),
(92, 2, 85, 'پھر یہ جو تم ہو اپنوں کو قتل کرنے لگے اور اپنے میں سے ایک گروہ کو ان کے وطن سے نکالتے ہو ان پر مدد دیتے ہو (ان کے مخالف کو) گناہ اور زیادتی میں اور اگر وہ قیدی ہو کر تمہارے پاس آئیں تو بدلا دے کر چھڑا لیتے ہو اور ان کا نکالنا تم پر حرام ہے تو کیا خدا کے کچھ حکموں پر ایمان لاتے ہو اور کچھ سے انکار کرتے ہو تو جو تم میں ایسا کرے اس کا بدلہ کیا ہے مگر یہ کہ دنیا میں رسوا ہو اور قیامت میں سخت تر عذاب کی طرف پھیرے جائیں گے اور اللہ تمہارے کوتکوں سے بے خبر نہیں -'),
(93, 2, 86, 'یہ ہیں وہ لوگ جنہوں نے آخرت کے بدلے دنیا کی زندگی مول لی تو نہ ان پر سے عذاب ہلکا ہو اور نہ ان کی مدد کی جائے -'),
(94, 2, 87, 'اور بے شک ہم نے موسیٰ کو کتاب عطا کی اور اس کے بعد پے در پے رسول بھیجے اور ہم نے عیسیٰ بن مریم کو کھیلی نشانیاں عطا فرمائیں اور پاک روح سے اس کی مدد کی تو کیا جب تمہارے پاس کوئی رسول وہ لے کر آئے جو تمہارے نفس کی خواہش نہیں تکبر کرتے ہو تو ان (انبیاء) میں ایک گروہ کو تم جھٹلاتے ہو اور ایک گروہ کو شہید کرتے ہو -'),
(95, 2, 88, 'اور یہودی بولے ہمارے دلوں پر پردے پڑے ہیں بلکہ اللہ نے ان پر لعنت کی ان کے کفر کے سبب تو ان میں تھوڑے ایمان لاتے ہیں-'),
(96, 2, 89, 'اور جب ان کے پاس اللہ کی وہ کتاب (قرآن) آئی جو ان کے ساتھ والی کتاب (توریت) کی تصدیق فرماتی ہے اور اس سے پہلے وہ اسی نبی کے وسیلہ سے کافروں پر فتح مانگتے تھے تو جب تشریف لایا انکے پاس وہ جانا پہچانا اس سے منکر ہو بیٹھے تو اللہ کی لعنت منکروں پر -'),
(97, 2, 90, 'کس برے مولوں انہوں نے اپنی جانوں کو خریدا کہ اللہ کے اتارے سے منکر ہوں اس کی جلن سے کہ اللہ اپنے فضل سے اپنے جس بندے پر چاہے وحی اتارلے تو غضب پر غضب کے سزاوار ہوئے اور کافروں کے لیے ذلت کا عذاب ہے -'),
(98, 2, 91, 'اور جب ان سے کہا جائے کہ اللہ کے اتارے پر ایمان لاؤ تو کہتے ہیں وہ جو ہم پر اترا اس پر ایمان لاتے ہیں اور باقی سے منکر ہوتے ہیں حالانکہ وہ حق ہے ان کے پاس والے کی تصدیق فرماتا ہوا تم فرماؤ کہ پھر اگلے انبیاء کو کیوں شہید کیا اگر تمہیں اپنی کتاب پر ایمان تھا -'),
(99, 2, 92, 'اور بیشک تمہارے پاس موسیٰ کھلی نشانیاں لے کر تشریف لایا پھر تم نے اس کے بعد بچھڑے کو معبود بنالیا اور تم ظالم تھے-'),
(100, 2, 93, 'اور (یاد کرو) جب ہم نے تم سے پیمان لیا او ر کوہ ِ طور کو تمہارے سروں پر بلند کیا، لو جو ہم تمہیں دیتے ہیں زور سے اور سنو بولے ہم نے سنا اور نہ مانا اور ان کے دلوں میں بچھڑا رچ رہا تھا ان کے کفر کے سبب تم فرمادو کیا برا حکم دیتا ہے تم کو تمہارا ایمان اگر ایمان رکھتے ہو-'),
(101, 2, 94, 'تم فرماؤ اگر پچھلا گھر اللہ کے نزدیک خالص تمہارے لئے ہو، نہ اوروں کے لئے تو بھلا موت کی آرزو تو کرو اگر سچے ہو-'),
(102, 2, 95, 'اور ہرگز کبھی اس کی آرزو نہ کریں گے ان بداعمالیوں کے سبب جو آگے کرچکے اور اللہ خوب جانتا ہے ظالموں کو-'),
(103, 2, 96, 'اور بیشک تم ضرور انہیں پاؤ گے کہ سب لوگوں سے زیادہ جینے کی ہوس رکھتے ہیں اور مشرکوں سے ایک کو تمنا ہے کہ کہیں ہزار برس جئے اور وہ اسے عذاب سے دور نہ کرے گا اتنی عمر دیا جانا اور اللہ ان کے کوتک دیکھ رہا ہے،'),
(104, 2, 97, 'تم فرمادو جو کوئی جبریل کا دشمن ہو تو اس (جبریل) نے تو تمہارے دل پر اللہ کے حکم سے یہ قرآن اتارا اگلی کتابوں کی تصدیق فرماتا اور ہدایت و بشارت مسلمانوں کو-'),
(105, 2, 98, 'جو کوئی دشمن ہو اللہ اور اس کے فرشتوں اور اس کے رسولوں اور جبریل اور میکائیل کا تو اللہ دشمن ہے کافروں کا'),
(106, 2, 99, 'اور بیشک ہم نے تمہاری طرف روشن آیتیں اتاریں اور ان کے منکر نہ ہوں گے مگر فاسق لوگ -'),
(107, 2, 100, 'اور کیا جب کبھی کوئی عہد کرتے ہیں ان میں کا ایک فریق اسے پھینک دیتا ہے بلکہ ان میں بہتیروں کو ایمان نہیں -'),
(108, 2, 101, 'اور جب ان کے پاس تشریف لایا اللہ کے یہاں سے ایک رسول ان کی کتابوں کی تصدیق فرماتا تو کتاب والوں سے ایک گروہ نے اللہ کی کتاب پیٹھ پیچھے پھینک دی گویا وہ کچھ علم ہی نہیں رکھتے -'),
(109, 2, 102, 'اور اس کے پیرو ہوئے جو شیطان پڑھا کرتے تھے سلطنت سلیمان کے زمانہ میں اور سلیمان نے کفر نہ کیا ہاں شیطان کافر ہوئے لوگوں کو جادو سکھاتے ہیں اور وہ (جادو) جو بابل میں دو فرشتوں ہاروت و ماروت پر اترا اور وہ دونوں کسی کو کچھ نہ سکھاتے جب تک یہ نہ کہہ لیتے کہ ہم تو نری آزمائش ہیں تو اپنا ایمان نہ کھو تو ان سے سیکھتے وہ جس سے جدائی ڈالیں مرد اور اس کی عورت میں اور اس سے ضرر نہیں پہنچا سکتے کسی کو مگر خدا کے حکم سے اور وہ سیکھتے ہیں جو انہیں نقصان دے گا نفع نہ دے گا اور بیشک ضرور انہیں معلوم ہے کہ جس نے یہ سودا لیا آخرت میں اس کا کچھ حصہ نہیں اور بیشک کیا بری چیز ہے وہ جس کے بدلے انہوں نے اپنی جانیں بیچیں کسی طرح انہیں علم ہوتا-'),
(110, 2, 103, 'اور اگر وہ ایمان لاتے اور پرہیزگاری کرتے تو اللہ کے یہاں کا ثواب بہت اچھا ہے کسی طرح انہیں علم ہوتا -'),
(111, 2, 104, 'اے ایمان والو! راعنا نہ کہو اور یوں عرض کرو کہ حضور ہم پر نظر رکھیں اور پہلے ہی سے بغور سنو اور کافروں کے لئے دردناک عذاب ہے-'),
(112, 2, 105, 'وہ جو کافر ہیں کتابی یا مشرک وہ نہیں چاہتے کہ تم پر کوئی بھلائی اترے تمہارے رب کے پاس سے اور اللہ اپنی رحمت سے خاص کرتا ہے جسے چاہے اور اللہ بڑے فضل والا ہے -'),
(113, 2, 106, 'جب کوئی آیت منسوخ فرمائیں یا بھلا دیں تو اس سے بہتر یا اس جیسی لے آئیں گے کیا تجھے خبر نہیں کہ اللہ سب کچھ کرسکتا ہے-'),
(114, 2, 107, 'کیا تجھے خبر نہیں کہ اللہ ہی کے لئے ہے آسمانوں اور زمین کی بادشاہی اور اللہ کے سوا تمہارا نہ کوئی حمایتی نہ مددگار،'),
(115, 2, 108, 'کیا یہ چاہتے ہو کہ اپنے رسول سے ویسا سوال کرو جو موسیٰ سے پہلے ہوا تھا اور جو ایمان کے بدلے کفر لے وہ ٹھیک راستہ بہک گیا -'),
(116, 2, 109, 'بہت کتابیوں نے چاہا کاش تمہیں ایمان کے بعد کفر کی طرف پھیردیں اپنے دلوں کی جلن سے بعد اس کے کہ حق ان پر خوب ظاہر ہوچکا ہے تو تم چھوڑو اور درگزر کرو یہاں تک کہ اللہ اپنا حکم لائے بیشک اللہ ہر چیز پر قادر ہے -'),
(117, 2, 110, 'اور نماز قائم رکھو اور زکوٰة دو اور اپنی جانوں کے لئے جو بھلائی آگے بھیجو گے اسے اللہ کے یہاں پاؤ گے بیشک اللہ تمہارے کام دیکھ رہا ہے-'),
(118, 2, 111, 'اور اہل کتاب بولے، ہرگز جنت میں نہ جائے گا مگر وہ جو یہودی یا نصرانی ہو یہ ان کی خیال بندیاں ہیں تم فرماؤ لا ؤ اپنی دلیل اگر سچے ہو-'),
(119, 2, 112, 'ہاں کیوں نہیں جس نے اپنا منہ جھکایا اللہ کے لئے اور وہ نیکو کار ہے تو اس کا نیگ اس کے رب کے پاس ہے اور انہیں کچھ اندیشہ ہو اور نہ کچھ غم-'),
(120, 2, 113, 'اور یہودی بولے نصرانی کچھ نہیں اور نصرانی بولے یہودی کچھ نہیں حالانکہ وہ کتاب پڑھتے ہیں، اسی طرح جاہلوں نے ان کی سی بات کہی تو اللہ قیامت کے دن ان میں فیصلہ کردے گا جس بات میں جھگڑ رہے ہیں-'),
(121, 2, 114, 'اور اس سے بڑھ کر ظالم کون جو اللہ کی مسجدوں کو روکے ان میں نامِ خدا لئے جانے سے اور ان کی ویرانی میں کوشش کرے ان کو نہ پہنچتا تھا کہ مسجدوں میں جائیں مگر ڈرتے ہوئے ان کے لئے دنیا میں رسوائی ہے اور ان کے لئے آخرت میں بڑا عذاب -'),
(122, 2, 115, 'اور پورب و پچھم سب اللہ ہی کا ہے تو تم جدھر منہ کرو ادھر وجہ اللہ (خدا کی رحمت تمہاری طرف متوجہ) ہے بیشک اللہ وسعت والا علم والا ہے،'),
(123, 2, 116, 'اور بولے خدا نے اپنے لیے اولاد رکھی پاکی ہے اسے بلکہ اسی کی مِلک ہے جو کچھ آسمانوں اور زمین میں ہے سب اس کے حضور گردن ڈالے ہیں،'),
(124, 2, 117, 'نیا پیدا کرنے والا آسمانوں اور زمین کا اور جب کسی بات کا حکم فرمائے تو اس سے یہی فرماتا ہے کہ ہو جا وہ فورا ً ہوجاتی ہے-'),
(125, 2, 118, 'اور جاہل بولے اللہ ہم سے کیوں نہیں کلام کرتا یا ہمیں کوئی نشانی ملے ان سے اگلوں نے بھی ایسی ہی کہی ان کی سی بات اِن کے اُن کے دل ایک سے ہیں بیشک ہم نے نشانیاں کھول دیں یقین والوں کے لئے -'),
(126, 2, 119, 'بیشک ہم نے تمہیں حق کے ساتھ بھیجا خوشخبری دیتا اور ڈر سناتا اور تم سے دوزخ والوں کا سوال نہ ہوگا -'),
(127, 2, 120, 'اور ہرگز تم سے یہود اور نصاری ٰ راضی نہ ہوں گے جب تک تم ان کے دین کی پیروی نہ کرو تم فرمادو اللہ ہی کی ہدایت ہدایت ہے اور (اے سننے والے کسے باشد) اگر تو ان کی خواہشوں کا پیرو ہوا بعد اس کے کہ تجھے علم آچکا تو اللہ سے تیرا کوئی بچانے والا نہ ہوگا او ر نہ مددگار'),
(128, 2, 121, 'جنہیں ہم نے کتاب دی ہے وہ جیسی چاہئے اس کی تلاوت کرتے ہیں وہی اس پر ایمان رکھتے ہیں اور جو اس کے منکر ہوں تو وہی زیاں کار ہیں -'),
(129, 2, 122, 'اے اولاد یعقوب یاد کرو میرا احسان جو میں نے تم پر کیا او ر وہ جو میں نے اس زمانہ کے سب لوگوں پر تمہیں بڑائی دی -'),
(130, 2, 123, 'اور ڈرو اس دن سے کہ کوئی جان دوسرے کا بدلہ نہ ہوگی اور نہ اس کو کچھ لے کر چھوڑیں اور نہ کافر کو کوئی سفارش نفع دے اور نہ ان کی مدد ہو -'),
(131, 2, 124, 'اور جب ابراہیم کو اس کے رب نے کچھ باتوں سے آزمایا تو اس نے وہ پوری کر دکھائیں فرمایا میں تمہیں لوگوں کا پیشوا بنانے والا ہوں عرض کی اور میری اولاد سے فرمایا میرا عہد ظالموں کو نہیں پہنچتا-'),
(132, 2, 125, 'اور (یاد کرو) جب ہم نے اس گھر کو لوگوں کے لئے مرجع اور امان بنایا اور ابراہیم کے کھڑے ہونے کی جگہ کو نماز کا مقام بناؤ اور ہم نے تاکید فرمائی ابراہیم و اسماعیل ؑ کو کہ میرا گھر خوب ستھرا کرو طواف کرنے والوں اور اعتکاف والوں اور رکوع و سجود والوں کے لئے -'),
(133, 2, 126, 'اور جب عرض کی ابراہیم ؑ نے کہ اے میرے رب اس شہر کو امان والا کردے اور اس کے رہنے والوں کو طرح طرح کے پھلوں سے روزی دے جو ان میں سے اللہ اور پچھلے دن پر ایمان لائیں فرمایا اور جو کافر ہوا تھوڑا برتنے کو اسے بھی دوں گا پھر اسے عذاب ِ دوزخ کی طرف مجبور کردوں گا اور بہت بری جگہ ہے پلٹنے کی-'),
(134, 2, 127, 'اور جب اٹھاتا تھا ابراہیم ؑ اس گھر کی نیویں اور اسمٰعیل ؑ یہ کہتے ہوئے اے رب ہمارے ہم سے قبول فرما بیشک تو ہی ہے سنتا جانتا،'),
(135, 2, 128, 'اے رب ہمارے اور کر ہمیں تیرے حضور گردن رکھنے والا اور ہماری اولاد میں سے ایک امت تیری فرمانبردار اور ہمیں ہماری عبادت کے قاعدے بتا اور ہم پر اپنی رحمت کے ساتھ رجوع فرما بیشک تو ہی ہے بہت توبہ قبول کرنے والا مہربان۔'),
(136, 2, 129, 'اے رب ہمارے اور بھیج ان میں ایک رسول انہیں میں سے کہ ان پر تیری آیتیں تلاوت فرمائے اور انہیں تیری کتاب اور پختہ علم سکھائے اور انہیں خوب ستھرا فرمادے بیشک تو ہی ہے غالب حکمت والا-'),
(137, 2, 130, 'اور ابراہیم کے دین سے کون منہ پھیرے سوا اس کے جو دل کا احمق ہے اور بیشک ضرور ہم نے دنیا میں اسے چن لیا اور بیشک وہ آخرت میں ہمارے خاص قرب کی قابلیت والوں میں ہے -'),
(138, 2, 131, 'جبکہ اس سے اس کے رب نے فرمایا گردن رکھ عرض کی میں نے گردن رکھی اس کے لئے جو رب ہے سارے جہان کا-'),
(139, 2, 132, 'اور اسی دین کی وصیت کی ابراہیم ؑ نے اپنے بیٹوں کو اور یعقوب نے کہ اے میرے بیٹو! بیشک اللہ نے یہ دین تمہارے لئے چن لیا تو نہ مرنا مگر مسلمان -'),
(140, 2, 133, 'بلکہ تم میں کے خود موجود تھے جب یعقوب ؑ کو موت آئی جبکہ اس نے اپنے بیٹوں سے فرمایا میرے بعد کس کی پوجا کروگے بولے ہم پوجیں گے اسے جو خدا ہے آپ کا اور آپ کے آباء ابراہیم ؑ و اسمٰعیل ؑ و اسحاقؑ کا ایک خدا اور ہم اس کے حضور گردن رکھے ہیں،'),
(141, 2, 134, 'یہ ایک امت ہے کہ گزرچکی ان کے لیے ہے جو انہوں نے کمایا اور تمہارے لئے ہے جو تم کماؤ اور ان کے کاموں کی تم سے پرسش نہ ہوگی-'),
(142, 2, 135, 'اور کتابی بولے یہودی یا نصرانی ہوجاؤ راہ پاؤگے تم فرماؤ بلکہ ہم تو ابراہیم ؑ کا دین لیتے ہیں جو ہر باطل سے جدا تھے اور مشرکوں سے نہ تھے -'),
(143, 2, 136, 'یوں کہو کہ ہم ایمان لائے اللہ پر اس پر جو ہماری طرف اترا اور جو اتارا گیا ابراہیم ؑ و اسمٰعیل ؑ و اسحاقؑ و یعقوبؑ اور ان کی اولاد پر اور جو عطا کئے گئے موسیٰ ؑ و عیسیٰ ؑ اور جو عطا کئے گئے باقی انبیاء اپنے رب کے پاس سے ہم ان پر ایمان میں فرق نہیں کرتے اور ہم اللہ کے حضور گردن رکھے ہیں-'),
(144, 2, 137, 'پھر اگر وہ بھی یونہی ایمان لائے جیسا تم لائے جب تو وہ ہدایت پاگئے اور اگر منہ پھیریں تو وہ نری ضد میں ہیں تو اے محبوب! عنقریب اللہ ان کی طرف سے تمہیں کفایت کرے گا اور وہی ہے سنتا جانتا -'),
(145, 2, 138, 'ہم نے اللہ کی رینی (رنگائی) لی اور اللہ سے بہتر کس کی رینی؟ (رنگائی) اور ہم اسی کو پوجتے ہیں،'),
(146, 2, 139, 'تم فرماؤ اللہ کے بارے میں جھگڑتے ہو حالانکہ وہ ہمارا بھی مالک ہے اور تمہارا بھی اور ہماری کرنی (ہمارے اعمال) ہمارے ساتھ اور تمہاری کرنی (تمہارے اعمال) تمہارے ساتھ اور ہم نِرے اسی کے ہیں-'),
(147, 2, 140, 'بلکہ تم یوں کہتے ہو کہ ابراہیم ؑ و اسمٰعیل ؑ و اسحاقؑ و یعقوب ؑ اور ان کے بیٹے یہودی یا نصرانی تھے، تم فرماؤ کیا تمہیں علم زیادہ ہے یا اللہ کو اور اس سے بڑھ کر ظالم کون جس کے پاس اللہ کی طرف کی گواہی ہو اور وہ اسے چھپائے اور خدا تمہارے کوتکوں (برے اعمال) سے بے خبر نہیں-'),
(148, 2, 141, 'وہ ایک گروہ ہے کہ گزر گیا ان کے لئے ان کی کمائی اور تمہارے لئے تمہاری کمائی اور ان کے کاموں کی تم سے پرسش نہ ہوگی۔'),
(149, 2, 142, 'اب کہیں گے بیوقوف لوگ، کس نے پھیردیامسلمانوں کو، ان کے اس قبلہ سے، جس پر تھے تم فرمادو کہ پورب پچھم (مشرق مغرب) سب اللہ ہی کا ہے جسے چاہے سیدھی راہ چلاتا ہے۔'),
(150, 2, 143, 'اور بات یوں ہی ہے کہ ہم نے تمہیں کیا سب امتوں میں افضل، کہ تم لوگوں پر گواہ ہو اور یہ رسول تمہارے نگہبان و گواہ اور اے محبوب! تم پہلے جس قبلہ پر تھے ہم نے وہ اسی لئے مقرر کیا تھا کہ دیکھیں کون رسول کی پیروی کرتا ہے اور کون الٹے پاؤں پھر جاتا ہے۔ اور بیشک یہ بھاری تھی مگر ان پر، جنہیں اللہ نے ہدایت کی اور اللہ کی شان نہیں کہ تمہارا ایمان اکارت کرے بیشک اللہ آدمیوں پر بہت مہربان، رحم والا ہے۔'),
(151, 2, 144, 'ہم دیکھ رہے ہیں بار بار تمہارا آسمان کی طرف منہ کرنا تو ضرور ہم تمہیں پھیردیں گے اس قبلہ کی طرف جس میں تمہاری خوشی ہے ابھی اپنا منہ پھیر دو مسجد حرام کی طرف اور اے مسلمانو! تم جہاں کہیں ہو اپنا منہ اسی کی طرف کرو اور وہ جنہیں کتاب ملی ہے ضرور جانتے کہ یہ انکے رب کی طرف سے حق ہے اور اللہ ان کے کوتکوں (اعمال) سے بے خبر نہیں-'),
(152, 2, 145, 'اور اگر تم ان کتابیوں کے پاس ہر نشانی لے کر آ ؤ وہ تمہارے قبلہ کی پیروی نہ کریں گے اور نہ تم ان کے قبلہ کی پیروی کرو اور وہ آپس میں بھی ایک دوسرے کے قبلہ کے تابع نہیں اور (اے سننے والے کسے باشد) اگر تو ان کی خواہشوں پر چلا بعد اس کے کہ تجھے علم مل چکا تو اس وقت تو ضرور ستم گر ہوگا۔'),
(153, 2, 146, 'جنہیں ہم نے کتاب عطا فرمائی وہ اس نبی کو ایسا پہچانتے ہیں جیسے آدمی اپنے بیٹوں کو پہچانتا ہے اور بیشک ان میں ایک گروہ جان بوجھ کر حق چھپاتے ہیں -'),
(154, 2, 147, '(اے سننے والو) یہ حق ہے تیرے رب کی طرف سے (یا حق وہی ہے جو تیرے رب کی طرف سے ہو) تو خبردار توشک نہ کرنا -'),
(155, 2, 148, 'اور ہر ایک کے لئے توجہ ایک سمعت ہے کہ وہ اسی طرف منہ کرتا ہے تو یہ چاہو کہ نیکیوں میں اوروں سے آگے نکل جائیں تو کہیں ہو اللہ تم سب کو اکٹھا لے آئے گا بیشک اللہ جو چاہے کرے-'),
(156, 2, 149, 'اور جہاں سے ا ٓ ؤ اپنا منہ مسجد حرام کی طرف کرو اور وہ ضرور تمہارے رب کی طرف سے حق ہے اور اللہ تمہارے کاموں سے غافل نہیں-'),
(157, 2, 150, 'اور اے محبوب تم جہاں سے آ ؤ اپنا منہ مسجد حرام کی طرف کرو اور اے مسلمانو! تم جہاں کہیں ہو اپنا منہ اسی کی طرف کرو کہ لوگوں کو تم پر کوئی حجت نہ رہے مگر جو ان میں ناانصافی کریں تو ان سے نہ ڈرو اور مجھ سے ڈرو اور یہ اس لئے ہے کہ میں اپنی نعمت تم پر پوری کروں اور کسی طرح تم ہدایت پاؤ،'),
(158, 2, 151, 'جیسا کہ ہم نے تم میں بھیجا ایک رسول تم میں سے کہ تم پر ہماری آیتیں تلاوت فرماتا ہے اور تمہیں پاک کرتا اور کتاب اور پختہ علم سکھاتا ہے اور تمہیں وہ تعلیم فرماتا ہے جس کا تمہیں علم نہ تھا،'),
(159, 2, 152, 'تو میری یاد کرو میں تمہارا چرچا کروں گا اور میرا حق مانو اور میری ناشکری نہ کرو-'),
(160, 2, 153, 'اے ایمان والو! صبر اور نماز سے مدد چاہو بیشک اللہ صابروں کے ساتھ ہے -'),
(161, 2, 154, 'اور جو خدا کی راہ میں مارے جائیں انہیں مردہ نہ کہو بلکہ وہ زندہ ہیں ہاں تمہیں خبرنہیں-'),
(162, 2, 155, 'اور ضرور ہم تمہیں آزمائیں گے کچھ ڈر اور بھوک سے اور کچھ مالوں اور جانوں اور پھلوں کی کمی سے اور خوشخبری سنا ان صبر والوں کو -'),
(163, 2, 156, 'کہ جب ان پر کوئی مصیبت پڑے تو کہیں ہم اللہ کے مال ہیں اور ہم کو اسی کی طرف پھرنا-'),
(164, 2, 157, 'یہ لوگ ہیں جن پر ان کے رب کی دروُ دیں ہیں اوررحمت اور یہی لوگ راہ پر ہیں،'),
(165, 2, 158, 'بیشک صفا اور مروہ اللہ کے نشانوں سے ہیں تو جو اس گھر کا حج یا عمرہ کرے اس پر کچھ گناہ نہیں کہ ان دونوں کے پھیرے کرے اور جو کوئی بھلی بات اپنی طرف سے کرے تو اللہ نیکی کا صلہ دینے خبردار ہے-'),
(166, 2, 159, 'بیشک وہ جو ہماری اتاری ہوئی روشن باتوں اور ہدایت کو چھپاتے ہیں بعد اس کے کہ لوگوں کے لئے ہم اسے کتاب میں واضح فرماچکے ہیں ان پر اللہ کی لعنت ہے اور لعنت کرنے والوں کی لعنت -'),
(167, 2, 160, 'مگر وہ جو توبہ کریں اور سنواریں اور ظاہر کریں تو میں ان کی توبہ قبول فرماؤں گا اور میں ہی ہوں بڑا توبہ قبول فرمانے والا مہربان،'),
(168, 2, 161, 'بیشک وہ جنہوں نے کفر کیا اور کافر ہی مرے ان پر لعنت ہے اللہ اور فرشتوں اور آدمیوں سب کی،'),
(169, 2, 162, 'ہمیشہ رہیں گے اس میں نہ ان پر سے عذاب ہلکا ہو اور نہ انہیں مہلت دی جائے،'),
(170, 2, 163, 'اور تمہارا معبود ایک معبود ہے اس کے سوا کوئی معبود نہیں مگر وہی بڑی رحمت والا مہربان'),
(171, 2, 164, 'بیشک آسمانوں اور زمین کی پیدائش اور رات و دن کا بدلتے آنا اور کشتی کہ دریا میں لوگوں کے فائدے لے کر چلتی ہے اور وہ جو اللہ نے آسمان سے پانی اتار کر مردہ زمین کو اس سے جِلا دیا اور زمین میں ہر قسم کے جانور پھیلائے اور ہواؤں کی گردش اور وہ بادل کہ آسمان و زمین کے بیچ میں حکم کا باندھا ہے ان سب میں عقلمندوں کے لئے ضرور نشانیاں ہیں،'),
(172, 2, 165, 'اور کچھ لوگ اللہ کے سوا اور معبود بنالیتے ہیں کہ انہیں اللہ کی طرح محبوب رکھتے ہیں اور ایمان والوں کو اللہ کے برابر کسی کی محبت نہیں اور کیسے ہو اگر دیکھیں ظالم وہ وقت جب کہ عذاب ان کی آنکھوں کے سامنے آئے گا اس لئے کہ سارا زور خدا کو ہے اور اس لئے کہ اللہ کا عذاب بہت سخت ہے،'),
(173, 2, 166, 'جب بیزار ہوں گے پیشوا اپنے پیروؤں سے اور دیکھیں گے عذاب اور کٹ جائیں گی ان کی سب ڈوریں'),
(174, 2, 167, 'اور کہیں گے پیرو کاش ہمیں لوٹ کر جانا ہوتا (دنیا میں) تو ہم ان سے توڑ دیتے جیسے انہوں نے ہم سے توڑ دی، یونہی اللہ انہیں دکھائے گا ان کے کام ان پر حسرتیں ہوکر اور وہ دوزخ سے نکلنے والے نہیں'),
(175, 2, 168, 'اے لوگوں کھاؤ جو کچھ زمین میں حلال پاکیزہ ہے اور شیطان کے قدم پر قدم نہ رکھو، بیشک وہ تمہارا کھلا دشمن ہے،'),
(176, 2, 169, 'وہ تو تمہیں یہی حکم دے گا بدی اور بے حیائی کا اور یہ کہ اللہ پر وہ بات جوڑو جس کی تمہیں خبر نہیں،'),
(177, 2, 170, 'اور جب ان سے کہا جائے اللہ کے اتارے پر چلو تو کہیں بلکہ ہم تو اس پر چلیں گے جس پر اپنے باپ دادا کو پایا کیا اگرچہ ان کے باپ دادا نہ کچھ عقل رکھتے ہوں نہ ہدایت'),
(178, 2, 171, 'اور کافروں کی کہاوت اس کی سی ہے جو پکارے ایسے کو کہ خالی چیخ و پکار کے سوا کچھ نہ سنے بہرے، گونگے، اندھے تو انہیں سمجھ نہیں'),
(179, 2, 172, 'اے ایمان والو! کھاؤ ہماری دی ہوئی ستھری چیزیں اور اللہ کا احسان مانو اگر تم اسی کو پوجتے ہو'),
(180, 2, 173, 'اس نے یہی تم پر حرام کئے ہیں مردار اور خون اور سُور کا گوشت اور وہ جانور جو غیر خدان کا نام لے کر ذبح کیا گیا تو جو نا چار ہو نہ یوں کہ خواہش سے کھائے اور نہ یوں کہ ضرورت سے آگے بڑھے تو اس پر گناہ نہیں، بیشک اللہ بخشنے والا مہربان ہے،'),
(181, 2, 174, 'وہ جو چھپاتے ہیں اللہ کی کتاب اور اسکے بدلے ذلیل قیمت لیتےہیں وہ اپنے پیٹ میں آگ ہی بھرتے ہیں اور اللہ قیامت کے دن ان سے بات نہ کرے گا اور نہ انہیں ستھرا کرے، اور ان کے لئے دردناک عذاب ہے،'),
(182, 2, 175, 'وہ لوگ ہیں جنہوں نے ہدایت کے بدلے گمراہی مول لی اور بخشش کے بدلے عذاب، تو کس درجہ انہیں آگ کی سہار (برداشت) ہے'),
(183, 2, 176, 'یہ اس لئے کہ اللہ نے کتاب حق کے ساتھ اتاری، اور بے شک جو لوگ کتاب میں اختلاف ڈالنے لگے وہ ضرور پرلے سرے کے جھگڑالو ہیں،'),
(184, 2, 177, 'کچھ اصل نیکی یہ نہیں کہ منہ مشرق یا مغرب کی طرف کرو ہاں اصلی نیکی یہ کہ ایمان لائے اللہ اور قیامت اور فرشتوں اور کتاب اور پیغمبروں پر اور اللہ کی محبت میں اپنا عزیز مال دے رشتہ داروں اور یتیموں اور مسکینوں اور راہ گیر اور سائلوں کو اور گردنیں چھوڑانے میں اور نماز قائم رکھے اور زکوٰة دے اور اپنا قول پورا کرنے والے جب عہد کریں اور صبر والے مصیبت اور سختی میں اور جہاد کے وقت یہی ہیں جنہوں نے اپنی بات سچی کی اور یہی پرہیزگار ہیں،'),
(185, 2, 178, 'اے ایمان والوں تم پر فرض ہے کہ جو ناحق مارے جائیں ان کے خون کا بدلہ لو آزاد کے بدلے آزاد اور غلام کے بدلے غلام اور عورت کے بدلے عورت تو جس کے لئے اس کے بھائی کی طرف سے کچھ معافی ہوئی۔ تو بھلائی سے تقا ضا ہو اور اچھی طرح ادا، یہ تمہارے رب کی طرف سے تمہارا بوجھ پر ہلکا کرنا ہے اور تم پر رحمت تو اس کے بعد جو زیادتی کرے اس کے لئے دردناک عذاب ہے'),
(186, 2, 179, 'اور خون کا بدلہ لینے میں تمہاری زندگی ہے اے عقل مندو کہ تم کہیں بچو،'),
(187, 2, 180, 'تم پر فرض ہوا کہ جب تم میں کسی کو موت آئے اگر کچھ مال چھوڑے تو وصیت کرجائے اپنے ماں باپ اور قریب کے رشتہ داروں کے لئے موافق دستور یہ واجب ہے پرہیزگاروں پر،'),
(188, 2, 181, 'تو جو وصیت کو سن سنا کر بدل دے اس کا گناہ انہیں بدلنے والوں پر ہے بیشک اللہ سنتا جانتا ہے،'),
(189, 2, 182, 'پھر جسے اندیشہ ہوا کہ وصیت کرنے والے نے کچھ بے انصافی یا گناہ کیا تو اس نے ان میں صلح کرادی اس پر کچھ گناہ نہیں بیشک اللہ بخشنے والا مہربان ہے'),
(190, 2, 183, 'اے ایمان والو! تم پر روزے فرض کیے گئے جیسے اگلوں پر فرض ہوئے تھے کہ کہیں تمہیں پرہیزگاری ملے، ف۳۲۵)'),
(191, 2, 184, 'گنتی کے دن ہیں تو تم میں جو کوئی بیمار یا سفر میں ہو تو اتنے روزے اور دنوں میں اور جنہیں اس کی طاقت نہ ہو وہ بدلہ دیں ایک مسکین کا کھانا پھر جو اپنی طرف سے نیکی زیادہ کرے تو وہ اس کے لئے بہتر ہے اور روزہ رکھنا تمہارے لئے زیادہ بھلا ہے اگر تم جانو'),
(192, 2, 185, 'رمضان کا مہینہ جس میں قرآن اترا لوگوں کے لئے ہدایت اور رہنمائی اور فیصلہ کی روشن باتیں تو تم میں جو کوئی یہ مہینہ پائے، ضرور اس کے روزے رکھے اور جو بیمار یا سفر میں ہو تو اتنے روزے اور دنوں میں اللہ تم پر آسانی چاہتا ہے اور تم پر دشواری نہیں چاہتا اور اس لئے کہ تم گنتی پوری کرو اور اللہ کی بڑائی بولو اس پر کہ اس نے تمہیں ہدایت کی اور کہیں تم حق گزار ہو،'),
(193, 2, 186, 'اور اے محبوب جب تم سے میرے بندے مجھے پوچھیں تو میں نزدیک ہوں دعا قبول کرتا ہوں پکارنے والے کی جب مجھے پکارے تو انہیں چاہئے میرا حکم مانیں اور مجھ پر ایمان لائیں کہ کہیں راہ پائیں،'),
(194, 2, 187, 'روزہ کی راتوں میں اپنی عورتوں کے پاس جانا تمہارے لئے حلال ہوا وہ تمہاری لباس ہیں اور تم ان کے لباس، اللہ نے جانا کہ تم اپنی جانوں کو خیانت میں ڈالتے تھے تو اس نے تمہاری توبہ قبول کی اور تمہیں معاف فرمایا تو اب ان سے صحبت کرو اور طلب کرو جو اللہ نے تمہارے نصیب میں لکھا ہو اور کھاؤ اور پیئو یہاں تک کہ تمہارے لئے ظاہر ہوجائے سفیدی کا ڈورا سیاہی کے ڈورے سے (پوپھٹ کر) پھر رات آنے تک روزے پورے کرو اور عورتوں کو ہاتھ نہ لگاؤ جب تم مسجدوں میں اعتکاف سے ہو یہ اللہ کی حدیں ہیں ان کے پاس نہ جاؤ اللہ یوں ہی بیان کرتا ہے لوگوں سے اپنی آیتیں کہ کہیں انہیں پرہیزگاری ملے،'),
(195, 2, 188, 'اور آپس میں ایک دوسرے کا مال ناحق نہ کھاؤ اور نہ حاکموں کے پاس ان کا مقدمہ اس لئے پہنچاؤ کہ لوگوں کا کچھ مال ناجائز طور پر کھاؤ جان بوجھ کر،'),
(196, 2, 189, 'تم سے نئے چاند کو پوچھتے ہیں تم فرمادو وہ وقت کی علامتیں ہیں لوگوں اور حج کے لئے اور یہ کچھ بھلائی نہیں کہ گھروں میں پچھیت (پچھلی دیوار) توڑ کر آ ؤ ہاں بھلائی تو پرہیزگاری ہے، اور گھروں میں دروازوں سے آ ؤ اور اللہ سے ڈرتے رہو اس امید پر کہ فلاح پاؤ'),
(197, 2, 190, 'اور اللہ کی راہ میں لڑو ان سے جو تم سے لڑتے ہیں اور حد سے نہ بڑھو اللہ پسند نہیں رکھتا حد سے بڑھنے والوں کو،'),
(198, 2, 191, 'اور کافروں کو جہاں پاؤ مارو اور انہیں نکال دو جہاں سے انہوں نے تمہیں نکا لا تھا اور ان کا فساد تو قتل سے بھی سخت ہے اور مسجد حرام کے پاس ان سے نہ لڑو جب تک وہ تم سے وہاں نہ لڑیں اور اگر تم سے لڑیں تو انہیں قتل کرو کافروں کی یہی سزا ہے،'),
(199, 2, 192, 'پھر اگر وہ باز رہیں تو بیشک اللہ بخشنے والا مہربان ہے،'),
(200, 2, 193, 'اور ان سے لڑو یہاں تک کہ کوئی فتنہ نہ رہے اور ایک اللہ کی پوجا ہو پھر اگر وہ باز آئیں تو زیادتی نہیں مگر ظالموں پر،'),
(201, 2, 194, 'ماہ حرام کے بدلے ماہ حرام اور ادب کے بدلے ادب ہے جو تم پر زیادتی کرے اس پر زیادتی کرو اتنی ہی جتنی اس نے کی اور اللہ سے ڈرتے رہو اور جان رکھو کہ اللہ ڈر والوں کے ساتھ ہے،'),
(202, 2, 195, 'اور اللہ کی راہ میں خرچ کرو اور اپنے ہاتھوں، ہلاکت میں نہ پڑو اور بھلائی والے ہو جاؤ بیشک بھلائی والے اللہ کے محبوب ہیں،'),
(203, 2, 196, 'اور حج اور عمرہ اللہ کے لئے پورا کرو پھر اگر تم روکے جاؤ تو قربانی بھیجو جو میسر آئے اور اپنے سر نہ منڈاؤ جب تک قربانی اپنے ٹھکانے نہ پہنچ جائے پھر جو تم میں بیمار ہو یا اس کے سر میں کچھ تکلیف ہے تو بدلے دے روزے یا خیرات یا قربانی، پھر جب تم اطمینان سے ہو تو جو حج سے عمرہ ملانے کا فائدہ اٹھائے اس پر قربانی ہے جیسی میسر آئے پھر جسے مقدور نہ ہو تو تین روزے حج کے دنوں میں رکھے اور سات جب اپنے گھر پلٹ کر جاؤ یہ پورے دس ہوئے یہ حکم اس کے لئے ہے جو مکہ کا رہنے والا نہ ہو اور اللہ سے ڈرتے رہو اور جان رکھو کہ اللہ کا عذاب سخت ہے،'),
(204, 2, 197, 'حج کے کئی مہینہ ہیں جانے ہوئے تو جو ان میں حج کی نیت کرے تو نہ عورتوں کے سامنے صحبت کا تذکرہ ہو نہ کوئی گناہ، نہ کسی سے جھگڑا حج کے وقت تک اور تم جو بھلائی کرو اللہ اسے جانتا ہے اور توشہ ساتھ لو کہ سب سے بہتر توشہ پرہیزگاری ہے اور مجھ سے ڈرتے رہو اے عقل والو، ف۳۷۸)'),
(205, 2, 198, 'تم پر کچھ گناہ نہیں کہ اپنے رب کا فضل تلاش کرو، تو جب عرفات سے پلٹو تو اللہ کی یاد کرو مشعر حرام کے پاس اور اس کا ذکر کرو جیسے اس نے تمہیں ہدایت فرمائی اور بیشک اس سے پہلے تم بہکے ہوئے تھے، ف۳۸۳)'),
(206, 2, 199, 'پھر بات یہ ہے کہ اے قریشیو! تم بھی وہیں سے پلٹو جہاں سے لوگ پلٹتے ہیں اور اللہ سے معافی مانگو، بیشک اللہ بخشنے والا مہربان ہے،'),
(207, 2, 200, 'پھر جب اپنے حج کے کام پورے کرچکو تو اللہ کا ذکر کرو جیسے اپنے باپ دادا کا ذکر کرتے تھے بلکہ اس سے زیادہ اور کوئی آدمی یوں کہتا ہے کہ اے رب ہمارے ہمیں دنیا میں دے اور آخرت میں اس کا کچھ حصہ نہیں،'),
(208, 2, 201, 'اور کوئی یوں کہتا ہے کہ اے رب ہمارے! ہمیں دنیا میں بھلائی دے اور ہمیں آخرت میں بھلائی دے اور ہمیں عذاب دوزخ سے بچا'),
(209, 2, 202, 'ایسوں کو ان کی کمائی سے بھاگ (خوش نصیبی) ہے اور اللہ جلد حساب کرنے والا ہے'),
(210, 2, 203, 'اور اللہ کی یاد کرو گنے ہوئے دنوں میں تو جلدی کرکے دو دن میں چلا جائے اس پر کچھ گنا نہیں اور جو رہ جائے تو اس پر گناہ نہیں پرہیزگار کے لئے اور اللہ سے ڈرتے رہو اور جان رکھو کہ تمہیں اسی کی طرف اٹھنا ہے،'),
(211, 2, 204, 'اور بعض آدمی وہ ہیں کہ دنیا کی زندگی میں اس کی بات تجھے بھلی لگے اور اپنے دل کی بات پر اللہ کو گواہ لائے اور وہ سب سے بڑا جھگڑالو ہے،'),
(212, 2, 205, 'اور جب پیٹھ پھیرے تو زمین میں فساد ڈالتا پھرے اور کھیتی اور جانیں تباہ کرے اور اللہ فسادسے راضی نہیں'),
(213, 2, 206, 'اور جب اس سے کہا جائے کہ اللہ سے ڈرو تو اسے اور ضد چڑھے گنا ہ کی ایسے کو دوزخ کافی ہے اور وہ ضرور بہت برا بچھونا ہے،'),
(214, 2, 207, 'اور کوئی آدمی اپنی جان بیچتا ہے اللہ کی مرضی چاہنے میں اور اللہ بندوں پر مہربان ہے،'),
(215, 2, 208, 'اے ایمان والو! اسلام میں پورے داخل ہو اور شیطان کے قدموں پر نہ چلے بیشک وہ تمہارا کھلا دشمن ہے،'),
(216, 2, 209, 'اور اگر اس کے بعد بھی بچلو کہ تمہارے پاس روشن حکم آچکے تو جان لو کہ اللہ زبردست حکمت والا ہے،'),
(217, 2, 210, 'کاہے کے انتظار میں ہیں مگر یہی کہ اللہ کا عذاب آئے چھائے ہوئے بادلوں میں اور فرشتے اتریں اور کام ہوچکے اور سب کاموں کی رجوع اللہ کی طرف ہے،'),
(218, 2, 211, 'بنی اسرائیل سے پوچھو ہم نے کتنی روشن نشانیاں انہیں دیں اور جو اللہ کی آئی ہوئی نعمت کو بدل دے تو بیشک اللہ کا عذاب سخت ہے،'),
(219, 2, 212, 'کافروں کی نگاہ میں دنیا کی زندگی آراستہ کی گئی اور مسلمانوں سے ہنستے ہیں اور ڈر والے ان سے اوپر ہوں گے قیامت کے دن اور خدا جسے چاہے بے گنتی دے،'),
(220, 2, 213, 'لوگ ایک دین پر تھے پھر اللہ نے انبیاء بھیجے خوشخبری دیتے اور ڈر سناتے اور ان کے ساتھ سچی کتاب اتاری کہ وہ لوگوں میں ان کے اختلافوں کا فیصلہ کردے اور کتاب میں اختلاف اُنہیں نے ڈالا جن کو دی گئی تھی بعداس کے کہ ان کے پاس روشن حکم آچکے آپس میں سرکشی سے تو اللہ نے ایمان والوں کو وہ حق بات سوجھا دی جس میں جھگڑ رہے تھے اپنے حکم سے، اور اللہ جسے چاہے سیدھی راہ دکھائے،'),
(221, 2, 214, 'کیا اس گمان میں ہو کہ جنت میں چلے جاؤ گے اور ابھی تم پر اگلوں کی سی روداد (حالت) نہ آئی پہنچی انہیں سختی اور شدت اور ہلا ہلا ڈالے گئے یہاں تک کہ کہہ اٹھا رسول اور اس کے ساتھ ایمان والے کب آئے گی اللہ کی مدد سن لو بیشک اللہ کی مدد قریب ہے،'),
(222, 2, 215, 'تم سے پوچھتے ہیں کیا خرچ کریں، تم فرماؤ جو کچھ مال نیکی میں خرچ کرو تو و ہ ماں باپ اور قریب کے رشتہ داروں اور یتیموں اور محتاجوں اور راہ گیر کے لئے ہے اور جو بھلائی کرو بیشک اللہ اسے جانتا ہے'),
(223, 2, 216, 'تم پر فرض ہوا خدا کی راہ میں لڑنا اور وہ تمہیں ناگوار ہے اور قریب ہے کہ کوئی بات تمہیں بری لگے اور وہ تمہارے حق میں بہتر ہو اور قریب ہے کہ کوئی بات تمہیں پسند آئے اور وہ تمہارے حق میں بری ہو اور اللہ جانتا ہے اور تم نہیں جانتے'),
(224, 2, 217, 'تم سے پوچھتے ہیں ماہ حرام میں لڑنے کا حکم تم فرماؤ اس میں لڑنا بڑا گناہ ہے اور اللہ کی راہ سے روکنا اور اس پر ایمان نہ لانا اور مسجد حرام سے روکنا، اور اس کے بسنے والوں کو نکال دینا اللہ کے نزدیک یہ گناہ اس سے بھی بڑے ہیں اور ان کا فساد قتل سے سخت تر ہے اور ہمیشہ تم سے لڑتے رہیں گے یہاں تک کہ تمہیں تمہارے دین سے پھیردیں اگر بن پڑے اور تم میں جو کوئی اپنے دین سے پھرے پھر کافر ہوکر مرے تو ان لوگوں کا کیا اکارت گیا دنیا میں اور آخرت میں اور وہ دوزخ والے ہیں انہیں اس میں ہمیشہ رہنا،'),
(225, 2, 218, 'وه جو ایمان لائے اور وہ جنہوں نے اللہ کے لئے اپنے گھر بار چھوڑے اور اللہ کی راہ میں لڑے وہ رحمت الٰہی کے امیداور ہیں، اور اللہ بخشنے والا مہربان ہے'),
(226, 2, 219, 'تم سے شراب اور جوئے کا حکم پوچھتے ہیں، تم فرمادو کہ ان دونوں میں بڑا گناہ ہے اور لوگوں کے کچھ دنیوی نفع بھی اور ان کا گناہ ان کے نفع سے بڑا ہے تم سے پوچھتے ہیں کیا خرچ کریں تم فرماؤ جو فاضل بچے اسی طرح اللہ تم سے آیتیں بیان فرماتا ہے کہ کہیں تم دنیا،'),
(227, 2, 220, 'اور ا ٓ خرت کے کام سوچ کر کرو اور تم سے یتیموں کا مسئلہ پوچھتے ہیں تم فرماؤ ان کا بھلا کرنا بہتر ہے اور اگر اپنا ان کا خرچ ملالو تو وہ تمہارے بھائی ہیں اور خدا خوب جانتا ہے بگاڑنے والے کو سنوارنے والے سے، اور اللہ چاہتا ہے تو تمہیں مشقت میں ڈالتا، بیشک اللہ زبردست حکمت والا ہے،'),
(228, 2, 221, 'اور شرک والی عورتوں سے نکاح نہ کرو جب تک مسلمان نہ ہوجائیں اور بیشک مسلمان لونڈی مشرکہ سے اچھی ہے اگرچہ وہ تمہیں بھاتی ہو اور مشرکوں کے نکاح میں نہ دو جب تک وہ ایمان نہ لائیں اور بیشک مسلمان غلام مشرک سے اچھا ہے اگرچہ وہ تمہیں بھاتا ہو، وہ دوزخ کی طرف بلاتے ہیں اور اللہ جنت اور بخشش کی طرف بلاتا ہے اپنے حکم سے اور اپنی آیتیں لوگوں کے لئے بیان کرتا ہے کہ کہیں وہ نصیحت مانیں،'),
(229, 2, 222, 'اور تم سے پوچھتے ہیں حیض کا حکم تم فرماؤ وہ ناپاکی ہے تو عورتوں سے الگ رہو حیض کے دنوں اور ان سے نزدیکی نہ کرو جب تک پاک نہ ہولیں پھر جب پاک ہوجائیں تو ان کے پاس جاؤ جہاں سے تمہیں اللہ نے حکم دیا، بیشک اللہ پسند کرتا ہے بہت توبہ کرنے والوں کو اور پسند رکھتا ہے سھتروں کو،'),
(230, 2, 223, 'تمہاری عورتیں تمہارے لئے کھیتیاں ہیں، تو آؤ اپنی کھیتیوں میں جس طرح چاہو اور اپنے بھلے کا کام پہلے کرو اور اللہ سے ڈرتے رہو اور جان رکھو کہ تمہیں اس سے ملنا ہے اور اے محبوب بشارت دو ایمان والوں کو،'),
(231, 2, 224, 'اور اللہ کو اپنی قسموں کا نشانہ نہ بنالو کہ احسان اور پرہیزگاری او ر لوگوں میں صلح کرنے کی قسم کرلو، اور اللہ سنتا جانتا ہے،'),
(232, 2, 225, 'اور تمہیں نہیں پکڑ تا ان قسموں میں جو بے ارادہ زبان سے نکل جائے ہاں اس پر گرفت فرماتا ہے جو کام تمہارے دلوں نے کئے اور اللہ بخشنے والا حلم والا ہے،'),
(233, 2, 226, 'اور وہ جو قسم کھا بیٹھتے ہیں اپنی عورتوں کے پاس جانے کی انہیں چار مہینے کی مہلت ہے، پس اگر اس مدت میں پھر آئے تو اللہ بخشنے والا مہربان ہے'),
(234, 2, 227, 'اور اگر چھوڑ دینے کا ارادہ پکا کرلیا تو اللہ سنتا جانتا ہے'),
(235, 2, 228, 'اور طلاق والیاں اپنی جانوں کو روکے رہیں تین حیض تک اور انہیں حلال نہیں کہ چھپائیں وہ جو اللہ نے ان کے پیٹ میں پیدا کیا اگر اللہ اور قیامت پر ایمان رکھتی ہیں اور ان کے شوہروں کو اس مدت کے اندر ان کے پھیر لینے کا حق پہنچتا ہے اگر ملاپ چاہیں اور عورتوں کا بھی حق ایسا ہی ہے جیسا ان پر ہے شرع کے موافق اور مردوں کو ان پر فضیلت ہے، اور اللہ غالب حکمت والا ہے،'),
(236, 2, 229, 'یہ طلاق دو بار تک ہے پھر بھلائی کے ساتھ روک لینا ہے یا نکوئی (اچھے سلوک) کے ساتھ چھوڑ دینا ہے اور تمہیں روا نہیں کہ جو کچھ عورتوں کو دیا اس میں سے کچھ واپس لو مگر جب دونوں کو اندیشہ ہو کہ اللہ کی حدیں قائم نہ کریں گے پھر اگر تمہیں خوف ہو کہ وہ دونوں ٹھیک انہیں حدوں پر نہ رہیں گے تو ان پر کچھ گناہ نہیں اس میں جو بدلہ دے کر عورت چھٹی لے، یہ اللہ کی حدیں ہیں ان سے آگے نہ بڑھو اور جو اللہ کی حدوں سے آگے بڑھے تو وہی لوگ ظالم ہیں،'),
(237, 2, 230, 'پھر اگر تیسری طلاق اسے دی تو اب وہ عورت اسے حلال نہ ہوگی جب تک دوسرے خاوند کے پاس نہ رہے، پھر وہ دوسرا اگر اسے طلاق دے دے تو ان دونوں پر گناہ نہیں کہ پھر آپس میں مل جائیں اگر سمجھتے ہوں کہ اللہ کی حدیں نباہیں گے، اور یہ اللہ کی حدیں ہیں جنہیں بیان کرتا ہے دانش مندوں کے لئے،'),
(238, 2, 231, 'اور جب تم عورتوں کو طلاق دو اور ان کی میعاد آلگے تو اس وقت تک یا بھلائی کے ساتھ روک لو یا نکوئی (اچھے سلوک) کے ساتھ چھوڑ دو اور انہیں ضرر دینے کے لئے روکنا نہ ہو کہ حد سے بڑھو اور جو ایسا کرے وہ اپنا ہی نقصان کرتا ہے اور اللہ کی آیتوں کو ٹھٹھا نہ بنالو اور یاد کرو اللہ کا احسان جو تم پر ہے اور و ہ جو تم پر کتاب اور حکمت اتاری تمہیں نصیحت دینے کو اور اللہ سے ڈرتے رہو اور جان رکھو کہ اللہ سب کچھ جانتا ہے'),
(239, 2, 232, 'اور جب تم عورتوں کو طلاق دو اور ان کی میعاد پوری ہوجائے تو اے عورتوں کے والِیو انہیں نہ روکو اس سے کہ اپنے شوہروں سے نکاح کرلیں جب کہ آپس میں موافق شرع رضا مند ہوجائیں یہ نصیحت اسے دی جاتی ہے جو تم میں سے اللہ اور قیامت پر ایمان رکھتا ہو یہ تمہارے لئے زیادہ ستھرا اور پاکیزہ ہے اور اللہ جانتا ہے اور تم نہیں جانتے،'),
(240, 2, 233, 'اور مائیں دودھ پلائیں اپنے بچوں کو پورے دو برس اس کے لئے جو دودھ کی مدت پوری کرنی چاہئے اور جس کا بچہ ہے اس پر عورتوں کا کھانا پہننا ہے حسب دستور کسی جان پر بوجھ نہ رکھا جائے گا مگر اس کے مقدور بھر ماں کو ضرر نہ دیا جائے اس کے بچہ سے اور نہ اولاد والے کو اس کی اولاد سے یا ماں ضرر نہ دے اپنے بچہ کو اور نہ اولاد والا اپنی اولاد کو اور جو باپ کا قائم مقام ہے اس پر بھی ایسا ہی واجب ہے پھر اگر ماں باپ دونوں آپس کی رضا اور مشورے سے دودھ چھڑانا چاہیں تو ان پر گناہ نہیں اور اگر تم چاہو کہ دائیوں سے اپنے بچوں کو دودھ پلواؤ تو بھی تم پر مضائقہ نہیں جب کہ جو دینا ٹھہرا تھا بھلائی کے ساتھ انہیں ادا کردو، اور اللہ سے ڈرتے رہو اور جان رکھو کہ اللہ تمہارے کام دیکھ رہا ہے،'),
(241, 2, 234, 'اور تم میں جو مریں اور بیبیاں چھوڑیں وہ چار مہینے دس دن اپن ے آپ کو روکے رہیں تو جب ان کی عدت پوری ہوجائے تو اے والیو! تم پر مؤاخذہ نہیں اس کام میں جو عورتیں اپنے معاملہ میں موافق شرع کریں، اور اللہ کو تمہارے کاموں کی خبر ہے،'),
(242, 2, 235, 'اور تم پر گناہ نہیں اس بات میں جو پردہ رکھ کر تم عورتوں کے نکاح کا پیام د و یا اپنے دل میں چھپا رکھو اللہ جانتا ہے کہ اب تم ان کی یاد کرو گے ہاں ان سے خفیہ وعدہ نہ کر رکھو مگر یہ کہ اتنی بات کہو جو شرع میں معروف ہے، اور نکاح کی گرہ پکی نہ کرو جب تک لکھا ہوا حکم اپنی میعاد کو نہ پہنچ لے اور جان لو کہ اللہ تمہارے دل کی جانتا ہے تو اس سے ڈرو اور جان لو کہ اللہ بخشنے والا حلم والا ہے،'),
(243, 2, 236, 'تم پر کچھ مطالبہ نہیں تم عورتوں کو طلاق دو جب تک تم نے ان کو ہاتھ ن ہ لگایا ہو یا کوئی مہر مقرر کرلیا ہو اور ان کو کچھ برتنے کو دو مقدور والے پر اس کے لائق اور تنگدست پر اس کے لائق حسب دستور کچھ برتنے کی چیز یہ واجب ہے بھلائی والوں پر'),
(244, 2, 237, 'اور اگر تم نے عورتوں کو بے چھوئے طلاق دے دی اور ان کے لئے کچھ مہر مقرر کرچکے تھے تو جتنا ٹھہرا تھا اس کا آدھا واجب ہے مگر یہ کہ عورتیں کچھ چھوڑدیں یا وہ زیاد ه دے جس کے ہاتھ میں نکاح کی گرہ ہے اور اے مرَدو تمہارا زیادہ دینا پرہیزگاری سے نزدیک تر ہے اور آپس میں ایک دوسرے پر احسان کو بُھلا نہ دو بیشک اللہ تمہارے کام دیکھ رہا ہے'),
(245, 2, 238, 'نگہبانی کرو سب نمازوں کی اور بیچ کی نماز کی اور کھڑے ہو اللہ کے حضور ادب سے'),
(246, 2, 239, 'پھر اگر خوف میں ہو تو پیادہ یا سوار جیسے بن پڑے پھر جب اطمینان سے ہو تو اللہ کی یاد کرو جیسا اس نے سکھایا جو تم نہ جانتے تھے،'),
(247, 2, 240, 'اور جو تم میں مریں اور بیبیاں چھوڑ جائیں، وہ اپنی عورتوں کے لئے وصیت کرجائیں سال بھر تک نان نفقہ دینے کی بے نکالے پھر اگر وہ خود نکل جائیں تو تم پر اس کا مؤاخذہ نہیں جو انہوں نے اپنے معاملہ میں مناسب طور پر کیا، اور اللہ غالب حکمت والا ہے،'),
(248, 2, 241, 'اور طلاق والیوں کے لئے بھی مناسب طور پر نان و نفقہ ہے، یہ واجب ہے پرہیزگاروں پر،'),
(249, 2, 242, 'اللہ یونہی بیان کرتا ہے تمہارے لئے اپنی آیتیں کہ کہیں تمہیں سمجھ ہو،'),
(250, 2, 243, 'اے محبوب کیا تم نے نہ دیکھا تھا انہیں جو اپنے گھروں سے نکلے اور وہ ہزاروں تھے موت کے ڈر سے، تو اللہ نے ان سے فرمایا مرجاؤ پھر انہیں زندہ فرمادیا، بیشک اللہ لوگوں پر فضل کرنے والا ہے مگر اکثر لوگ ناشکرے ہیں'),
(251, 2, 244, 'اور لڑو اللہ کی راہ میں اور جان لو کہ اللہ سنتا جانتا ہے،'),
(252, 2, 245, 'ہے کوئی جو اللہ کو قرض حسن دے تو اللہ اس کے لئے بہت گنُا بڑھا دے اور اللہ تنگی اور کشائیش کرتا ہے اور تمہیں اسی کی طرف پھر جانا،'),
(253, 2, 246, 'اے محبوب! کیا تم نے نہ دیکھا بنی اسرائیل کے ایک گروہ کو جو موسیٰ کے بعد ہوا جب اپنے ایک پیغمبر سے بولے ہمارے لیے کھڑا کردو ایک بادشاہ کہ ہم خدا کی راہ میں لڑیں، نبی نے فرمایا کیا تمہارے انداز ایسے ہیں کہ تم پر جہاد فرض کیا جائے تو پھر نہ کرو، بولے ہمیں کیا ہوا کہ ہم اللہ کی راہ میں نہ لڑیں حالانکہ ہم نکالے گئے ہیں اپنے وطن اور اپنی اولاد سے تو پھر جب ان پر جہاد فرض کیا گیا منہ پھیر گئے مگر ان میں کے تھوڑے اور اللہ خوب جانتا ہے ظالموں کو،'),
(254, 2, 247, 'اور ان سے ان کے نبی نے فرمایا بیشک اللہ نے طالوت کو تمہارا بادشاہ بنا کر بھیجا ہے بولے اسے ہم پر بادشاہی کیونکر ہوگی اور ہم اس سے زیادہ سلطنت کے مستحق ہیں اور اسے مال میں بھی وسعت نہیں دی گئی فرمایا اسے اللہ نے تم پر چن لیا اور اسے علم اور جسم میں کشادگی زیادہ دی اور اللہ اپنا ملک جسے چاہ ے دے اور اللہ وسعت والا علم والا ہے'),
(255, 2, 248, 'اور ان سے ان کے نبی نے فرمایا اس کی بادشاہی کی نشانی یہ ہے کہ آئے تمہارے پاس تابوت جس میں تمہارے رب کی طرف سے دلوں کا چین ہے اور کچھ بچی ہوئی چیزیں معزز موسی ٰ او ر معزز ہارون کے ترکہ کی اٹھاتے لائیں گے اسے فرشتے، بیشک اس میں بڑی نشانی ہے تمہارے لئے اگر ایمان رکھتے ہو،'),
(256, 2, 249, 'پھر جب طالوت لشکروں کو لے کر شہر سے جدا ہوا بولا بیشک اللہ تمہیں ایک نہر سے آزمانے والا ہے تو جو اس کا پانی پئے وہ میرا نہیں اور جو نہ پیئے وہ میرا ہے مگر وہ جو ایک چلُو اپنے ہاتھ سے لے لے تو سب نے اس سے پیا مگر تھوڑوں نے پھر جب طالوت اور اس کے ساتھ کے مسلمان نہر کے پار گئے بولے ہم میں آج طاقت نہیں جالوت اور اس کے لشکروں کی بولے وہ جنہیں اللہ سے ملنے کا یقین تھا کہ بارہا کم جماعت غالب آئی ہے زیادہ گروہ پر اللہ کے حکم سے، اور اللہ صابروں کے ساتھ ہے'),
(257, 2, 250, 'پھر جب سامنے آئے جالوت اور اس کے لشکروں کے عرض کی اے رب ہمارے ہم پر صبر انڈیل اور ہمارے پاؤں جمے رکھ کافر لوگوں پرہماری مدد کر،'),
(258, 2, 251, 'تو انہوں نے ان کو بھگا دیا اللہ کے حکم سے، اور قتل کیا داؤد نے جالوت کو اور اللہ نے اسے سلطنت اور حکمت عطا فرمائی اور اسے جو چاہا سکھایا اور اگر اللہ لوگوں میں بعض سے بعض کو دفع نہ کرے تو ضرور زمین تباہ ہوجائے مگر اللہ سارے جہان پر فضل کرنے والا ہے،'),
(259, 2, 252, 'یہ اللہ کی آیتیں ہیں کہ ہم اے محبوب تم پر ٹھیک ٹھیک پڑھتے ہیں، اور تم بےشک رسولوں میں ہو۔'),
(260, 2, 253, 'یہ رسول ہیں کہ ہم نے ان میں ایک کو دوسرے پر افضل کیا ان میں کسی سے اللہ نے کلام فرمایا اور کوئی وہ ہے جسے سب پر درجوں بلند کیا اور ہم نے مریم کے بیٹے عیسیٰ کو کھلی نشانیاں دیں اور پاکیزہ روح سے اس کی مدد کی اور اللہ چاہتا تو ان کے بعد والے آپس میں نہ لڑتے نہ اس کے کہ ان کے پاس کھلی نشانیاں آچکیں لیکن وہ مختلف ہوگئے ان میں کوئی ایمان پر رہا اور کوئی کافر ہوگیا اور اللہ چاہتا تو وہ نہ لڑتے مگر اللہ جو چاہے کرے'),
(261, 2, 254, 'اے ایمان والو! اللہ کی راہ میں ہمارے دیئے میں سے خرچ کرو وہ دن آنے سے پہلے جس میں نہ خرید و فروخت ہے اور نہ کافروں کے لئے دوستی اور نہ شفاعت، اور کافر خود ہی ظالم ہیں'),
(262, 2, 255, 'اللہ ہے جس کے سوا کوئی معبود نہیں وہ آپ زندہ اور اوروں کا قائم رکھنے والا اسے نہ اونگھ آئے نہ نیند اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں وہ کون ہے جو اس کے یہاں سفارش کرے بغیر اس کے حکم کے جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے اور وہ نہیں پاتے اس کے علم میں سے مگر جتنا وہ چاہے اس کی کرسی میں سمائے ہوئے آسمان اور زمین اور اسے بھاری نہیں ان کی نگہبانی اور وہی ہے بلند بڑائی والا'),
(263, 2, 256, 'کچھ زبردستی نہیں دین میں بیشک خوب جدا ہوگئی ہے نیک راہ گمراہی سے تو جو شیطان کو نہ مانے اور اللہ پر ایمان لائے اس نے بڑی محکم گرہ تھامی جسے کبھی کھلنا نہیں، اور اللہ سنتا جانتا ہے،'),
(264, 2, 257, 'اللہ والی ہے مسلمانوں کا انہیں اندھیریوں سے نور کی طرف نکلتا ہے، اور کافروں کے حمایتی شیطان ہیں وہ انہیں نور سے اندھیریوں کی طرف نکالتے ہیں یہی لوگ دوزخ والے ہیں انہیں ہمیشہ اس میں رہنا،'),
(265, 2, 258, 'اے محبوب! کیا تم نے نہ دیکھا تھا اسے جو ابراہیم سے جھگڑا اس کے رب کے بارے میں اس پر کہ اللہ نے اسے بادشاہی دی جبکہ ابراہیم نے کہا کہ میرا رب وہ ہے جو جِلاتا اور مارتا ہے بولا میں جِلاتا اور مارتا ہوں ابراہیم نے فرمایا تو اللہ سورج کو لاتا ہے پورب (مشرق) سے تو اس کو پچھم (مغرب) سے لے آ تو ہوش اڑ گئے کافروں کے، اور اللہ راہ نہیں دکھاتا ظالموں کو،'),
(266, 2, 259, 'یا اس کی طرح جو گزرا ایک بستی پر اور وہ ڈھئی (مسمار ہوئی) پڑی تھی اپنی چھتوں پر بولا اسے کیونکر جِلائے گا اللہ اس کی موت کے بعد تو اللہ نے اسے مردہ رکھا سو برس پھر زندہ کردیا، فرمایا تو یہاں کتنا ٹھہرا، عرض کی دن بھر ٹھہرا ہوں گا یا کچھ کم، فرمایا نہیں تجھے سو برس گزر گئے اور اپنے کھانے اور پانی کو دیکھ کہ اب تک بو نہ لایا اور اپنے گدھے کو دیکھ کہ جس کی ہڈیاں تک سلامت نہ رہیں اور یہ اس لئے کہ تجھے ہم لوگوں کے واسطے نشانی کریں اور ان ہڈیوں کو دیکھ کیونکر ہم انہیں اٹھان دیتے پھر انہیں گوشت پہناتے ہیں جب یہ معاملہ اس پر ظاہر ہوگیا بولا میں خوب جانتا ہوں کہ اللہ سب کچھ کرسکتا ہے،'),
(267, 2, 260, 'اور جب عرض کی ابراہیم نے اے رب میرے مجھے دکھا دے تو کیونکر مردے جِلائے گا فرمایا کیا تجھے یقین نہیں عرض کی یقین کیوں نہیں مگر یہ چاہتا ہوں کہ میرے دل کو قرار آجائے فرمایا تو اچھا، چار پرندے لے کر اپنے ساتھ ہلالے پھر ان کا ایک ایک ٹکڑا ہر پہاڑ پر رکھ دے پھر انہیں بلا وہ تیرے پاس چلے آئیں گے پاؤں سے دوڑتے اور جان رکھ کہ اللہ غالب حکمت والا ہے'),
(268, 2, 261, 'ان کی کہاوت جو اپنے مال اللہ کی راہ میں خرچ کرتے ہیں اس دانہ کی طرح جس نے اگائیں سات بالیں ہر بال میں سو دانے اور اللہ اس سے بھی زیادہ بڑھائے جس کے لئے چاہے اور اللہ وسعت والا علم والا ہے،'),
(269, 2, 262, 'وہ جو اپنے مال اللہ کی راہ میں خرچ کرتے ہیں پھر دیئے پیچھے نہ احسان رکھیں نہ تکلیف دیں ان کا نیگ (انعام) ان کے رب کے پاس ہے اور انہیں نہ کچھ اندیشہ ہو نہ کچھ غم،'),
(270, 2, 263, 'اچھی بات کہنا اور درگزر کرنا اس خیرات سے بہتر ہے جس کے بعد ستانا ہو اور اللہ بے پراہ حلم والا ہے،'),
(271, 2, 264, 'اے ایمان والوں اپنے صدقے باطل نہ کردو احسان رکھ کر اور ایذا دے کر اس کی طرح جو اپنا مال لوگوں کے دکھاوے کے لئے خرچ کرے اور اللہ اور قیامت پر ایمان نہ لائے، تو اس کی کہاوت ایسی ہے جیسے ایک چٹان کہ اس پر مٹی ہے اب اس پر زور کا پانی پڑا جس نے اسے نرا پتھر کر چھوڑا اپنی کمائی سے کسی چیز پر قابو نہ پائیں گے، اور اللہ کافروں کو راہ نہیں دیتا،'),
(272, 2, 265, 'اور ان کی کہاوت جو اپنے مال اللہ کی رضا چاہنے میں خرچ کرتے ہیں اور اپنے دل جمانے کو اس باغ کی سی ہے جو بھوڑ (رتیلی زمین) پر ہو اس پر زور کا پانی پڑا تو دُونے میوے لایا پھر اگر زور کا مینھ اسے نہ پہنچے تو اوس کافی ہے اور اللہ تمہارے کام دیکھ رہا ہے'),
(273, 2, 266, 'کیا تم میں کوئی اسے پسند رکھے گا کہ اس کے پاس ایک باغ ہو کھجوروں اور انگوروں کا جس کے نیچے ندیاں بہتیں اس کے لئے اس میں ہر قسم کے پھلوں سے ہے اور اسے بڑھاپا آیا اور اس کے ناتوان بچے ہیں تو آیا اس پر ایک بگولا جس میں آگ تھی تو جل گیا ایسا ہی بیان کرتا ہے اللہ تم سے اپنی آیتیں کہ کہیں تم دھیان لگاؤ'),
(274, 2, 267, 'اے ایمان والو! اپنی پاک کمائیوں میں سے کچھ دو اور اس میں سے جو ہم نے تمہارے لئے زمین سے نکالا اور خاص ناقص کا ارادہ نہ کرو کہ دو تو اس میں سے اور تمہیں ملے تو نہ لوگے جب تک اس میں چشم پوشی نہ کرو اور جان رکھو کہ اللہ بے پروانہ سراہا گیا ہے۔'),
(275, 2, 268, 'شیطان تمہیں اندیشہ دلاتا ہے محتاجی کا اور حکم دیتا ہے بے حیائی کا اور اللہ تم سے وعدہ فرماتا ہے بخشش اور فضل کا اور اللہ وسعت و الا علم والا ہے،'),
(276, 2, 269, 'اللہ حکمت دیتا ہے جسے چاہے اور جسے حکمت ملی اسے بہت بھلائی ملی، اور نصیحت نہیں مانتے مگر عقل والے،'),
(277, 2, 270, 'اور تم جو خرچ کرو یا منت مانو اللہ کو اس کی خبر ہے اور ظالموں کا کوئی مددگار نہیں،'),
(278, 2, 271, 'اگر خیرات اعلانیہ دو تو وہ کیا ہی اچھی بات ہے اور اگر چھپا کر فقیروں کو دو یہ تمہارے لئے سب سے بہتر ہے اور اسمیں تمہارے کچھ گناہ گھٹیں گے، اور اللہ کو تمہارے کاموں کی خبر ہے،'),
(279, 2, 272, 'انہیں راہ دینا تمہارے ذمہ لازم نہیں، ہاں اللہ راہ دیتا ہے جسے چاہتا ہے، اور تم جو اچھی چیز دو تو تمہارا ہی بھلا ہے اور تمہیں خرچ کرنا مناسب نہیں مگر اللہ کی مرضی چاہنے کے لئے، اور جو مال دو تمہیں پورا ملے گا اور نقصان نہ دیئے جاؤ گے،'),
(280, 2, 273, 'ان فقیروں کے لئے جو راہ خدا میں روکے گئے زمین میں چل نہیں سکتے نادان انہیں تونگر سمجھے بچنے کے سبب تو انہیں ان کی صورت سے پہچان لے گا لوگوں سے سوال نہیں کرتے کہ گڑ گڑانا پڑے اور تم جو خیرات کرو اللہ اسے جانتا ہے،'),
(281, 2, 274, 'وہ جو اپنے مال خیرات کرتے ہیں رات میں اور دن میں چھپے اور ظاہر ان کے لئے ان کا نیگ (انعام، حصہ) ہے ان کے رب کے پاس ان کو نہ کچھ اندیشہ ہو نہ کچھ غم،'),
(282, 2, 275, 'وہ جو سود کھاتے ہیں قیامت کے دن نہ کھڑے ہوں گے مگر، جیسے کھڑا ہوتا ہے وہ جسے آسیب نے چھو کر مخبوط بنادیا ہو اس لئے کہ انہوں نے کہا بیع بھی تو سود ہی کے مانند ہے، اور اللہ نے حلال کیا بیع کو اور حرام کیا سود، تو جسے اس کے رب کے پاس سے نصیحت آئی اور وہ باز رہا تو اسے حلال ہے جو پہلے لے چکا، اور اس کا کام خدا کے سپرد ہے اور جو اب ایسی حرکت کرے گا تو وہ دوزخی ہے وہ اس میں مدتو ں رہیں گے'),
(283, 2, 276, 'اللہ ہلاک کرتا ہے سود کو اور بڑھاتا ہے خیرات کو اور اللہ کو پسند نہیں آتا کوئی ناشکرا بڑا گنہگار،'),
(284, 2, 277, 'بیشک وہ جو ایمان لائے اور اچھے کام کئے اور نماز قائم کی اور زکوٰة دی ان کا نیگ (انعام) ان کے رب کے پاس ہے، اور نہ انہیں کچھ اندیشہ ہو، نہ کچھ غم،'),
(285, 2, 278, 'اے ایمان والو! اللہ سے ڈرو اور چھوڑ دو جو باقی رہ گیا ہے سود اگر مسلمان ہو'),
(286, 2, 279, 'پھر اگر ایسا نہ کرو تو یقین کرلو اللہ اور اللہ کے رسول سے لڑائی کا اور اگر تم توبہ کرو تو اپنا اصل مال لے لو نہ تم کسی کو نقصان پہچاؤ نہ تمہیں نقصان ہو'),
(287, 2, 280, 'اور اگر قرضدار تنگی والا ہے تو اسے مہلت دو آسانی تک، اور قرض اس پر بالکل چھوڑ دینا تمہارے لئے اور بھلا ہے اگر جانو'),
(288, 2, 281, 'اور ڈرو اس دن سے جس میں اللہ کی طرف پھرو گے، اور ہر جان کو اس کی کمائی پوری بھردی جائے گی اور ان پر ظلم نہ ہوگا'),
(289, 2, 282, 'اے ایمان والو! جب تم ایک مقرر مدت تک کسی دین کا لین دین کرو تو اسے لکھ لو اور چاہئے کہ تمہارے درمیان کوئی لکھنے والا ٹھیک ٹھیک لکھے اور لکھنے والا لکھنے سے انکار نہ کرے جیسا کہ اسے اللہ نے سکھایا ہے تو اسے لکھ دینا چاہئے اور جس بات پر حق آتا ہے وہ لکھا تا جائے اور اللہ سے ڈرے جو اس کا رب ہے اور حق میں سے کچھ رکھ نہ چھوڑے پھر جس پر حق آتا ہے اگر بے عقل یا ناتواں ہو یا لکھا نہ سکے تو اس کا ولی انصاف سے لکھائے، اور دو گواہ کرلو اپنے مردوں میں سے پھر اگر دو مرد نہ ہوں تو ایک مرد اور دو عورتیں ایسے گواہ جن کو پسند کرو کہ کہیں ان میں ایک عورت بھولے تو اس کو دوسری یاد دلادے، اور گواہ جب بلائے جائیں تو آنے سے انکار نہ کریں اور اسے بھاری نہ جانو کہ دین چھوٹا ہو یا بڑا اس کی میعاد تک لکھت کرلو یہ اللہ کے نزدیک زیادہ انصاف کی بات ہے اس میں گواہی خوب ٹھیک رہے گی اور یہ اس سے قریب ہے کہ تمہیں شبہ نہ پڑے مگر یہ کہ کوئی سردست کا سودا دست بدست ہو تو اس کے نہ لکھنے کا تم پر گناہ نہیں اور جب خرید و فروخت کرو تو گواہ کرلو اور نہ کسی لکھنے والے کو ضَرر دیا جائے، نہ گواہ کو (یا، نہ لکھنے والا ضَرر دے نہ گواہ) اور جو تم ایسا کرو تو یہ تمہارا فسق ہوگا، اور اللہ سے ڈرو اور اللہ تمہیں سکھاتا ہے، اور اللہ سب کچھ جانتا ہے،'),
(290, 2, 283, 'اور اگر تم سفر میں ہو اور لکھنے والا نہ پاؤ تو گِرو (رہن) ہو قبضہ میں دیا ہوا اور اگر تم ایک کو دوسرے پر اطمینان ہو تو وہ جسے اس نے امین سمجھا تھا اپنی امانت ادا کردے اور اللہ سے ڈرے جو اس کا رب ہے اور گواہی نہ چھپاؤ اور جو گواہی چھپائے گا تو اندر سے اسکا دل گنہگار ہے اور اللہ تمہارے کاموں کو جانتا ہے،'),
(291, 2, 284, 'اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے، اور اگر تم ظاہر کرو جو کچھ تمہارے جی میں ہے یا چھپاؤ اللہ تم سے اس کا حساب لے گا تو جسے چاہے گا بخشے گا اور جسے چاہے گا سزادے گا اور اللہ ہر چیز پر قادر ہے،'),
(292, 2, 285, 'سب نے مانا اللہ اور اس کے فرشتوں اور اس کی کتابوں اور اس کے رسولوں کو یہ کہتے ہوئے کہ ہم اس کے کسی رسول پر ایمان لانے میں فرق نہیں کرتے اور عرض کی کہ ہم نے سنا اور مانا تیری معافی ہو اے رب ہمارے! اور تیری ہی طرف پھرنا ہے،'),
(293, 2, 286, 'اللہ کسی جان پر بوجھ نہیں ڈالتا مگر اس کی طاقت بھر، اس کا فائدہ ہے جو اچھا کمایا اور اس کا نقصان ہے جو برائی کمائی اے رب ہمارے! ہمیں نہ پکڑ اگر ہم بھولیں یا چوُکیں اے رب ہمارے! اور ہم پر بھاری بوجھ نہ رکھ جیسا تو نے ہم سے اگلوں پر رکھا تھا، اے رب ہمارے! اور ہم پر وہ بوجھ نہ ڈال جس کی ہمیں سہار (برداشت) نہ ہو اور ہمیں معاف فرمادے اور بخش دے اور ہم پر مہر کر تو ہمارا مولیٰ ہے۔ تو کافروں پر ہمیں مدد دے۔');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الم،'),
(295, 3, 2, 'اللہ ہے جس کے سوا کسی کی پوجا نہیں آپ زندہ اور ونکا قائم رکھنے والا،'),
(296, 3, 3, 'اس نے تم پر یہ سچی کتاب اتاری اگلی کتابوں کی تصدیق فرماتی اور اس نے اس سے پہلے توریت اور انجیل اتاری،'),
(297, 3, 4, 'لوگوں کو راہ دکھاتی اور فیصلہ اتارا، بیشک وہ جو اللہ کی آیتوں سے منکر ہوئے ان کے لئے سخت عذاب ہے اور اللہ غالب بدلہ لینے والا ہے،'),
(298, 3, 5, 'اللہ پر کچھ چھپا ہوا نہیں زمین میں نہ آسمان میں،'),
(299, 3, 6, 'وہی ہے کہ تمہاری تصویر بناتا ہے ماؤں کے پیٹ میں جیسی چاہے اس کے سوا کسی کی عبادت نہیں عزت والا حکمت والا'),
(300, 3, 7, 'وہی ہے جس نے تم پر یہ کتاب اتاری اس کی کچھ آیتیں صاف معنی رکھتی ہیں وہ کتاب کی اصل ہیں اور دوسری وہ ہیں جن کے معنی میں اشتباہ ہے وہ جن کے دلوں میں کجی ہے وہ اشتباہ والی کے پیچھے پڑتے ہیں گمراہی چاہنے اور اس کا پہلو ڈھونڈنے کو اور اس کا ٹھیک پہلو اللہ ہی کو معلوم ہے اور پختہ علم والے کہتے ہیں ہم اس پر ایمان لائے سب ہمارے رب کے پاس سے ہے اور نصیحت نہیں مانتے مگر عقل والے'),
(301, 3, 8, 'اے رب ہمارے دل ٹیڑھے نہ کر بعد اس کے کہ تو نے ہمیں ہدایت دی اور ہمیں اپنے پاس سے رحمت عطا کر بیشک تو ہے بڑا دینے والا،'),
(302, 3, 9, 'اے رب ہمارے بیشک تو سب لوگوں کو جمع کرنے والا ہے اس دن کے لئے جس میں کوئی شبہ نہیں بیشک اللہ کا وعدہ نہیں بدلتا'),
(303, 3, 10, 'بیشک وہ جو کافر ہوئے ان کے مال اور ان کی اولاد اللہ سے انہیں کچھ نہ بچاسکیں گے اور وہی دوزخ کے ایندھن ہیں،'),
(304, 3, 11, 'جیسے فرعون والوں اور ان سے اگلوں کا طریقہ، انہوں نے ہماری آیتیں جھٹلائیں تو اللہ نے ان کے گناہوں پر ان کو پکڑا اور اللہ کا عذاب سخت،'),
(305, 3, 12, 'فرمادو، کافروں سے کوئی دم جاتا ہے کہ تم مغلوب ہوگے اور دوزخ کی طرف ہانکے جاؤ گے اور وہ بہت ہی برا بچھونا،'),
(306, 3, 13, 'بیشک تمہارے لئے نشانی تھی دو گروہوں میں جو آپس میں بھڑ پڑے ایک جتھا اللہ کی راہ میں لڑتا اور دوسرا کافر کہ انہیں آنکھوں دیکھا اپنے سے دونا سمجھیں، اور اللہ اپنی مدد سے زور دیتا ہے جسے چاہتا ہے بیشک اس میں عقلمندوں کے لئے ضرور دیکھ کر سیکھنا ہے،'),
(307, 3, 14, 'لوگوں کے لئے آراستہ کی گئی ان خواہشوں کی محبت عورتوں اور بیٹے اور تلے اوپر سونے چاندے کے ڈھیر اور نشان کئے ہوئے گھوڑے اور چوپائے اور کھیتی یہ جیتی دنیا کی پونجی ہے اور اللہ ہے جس کے پاس اچھا ٹھکانا'),
(308, 3, 15, 'تم فرماؤ کیا میں تمہیں اس سے بہتر چیز بتادوں پرہیزگاروں کے لئے ان کے رب کے پاس جنتیں ہیں جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں گے اور ستھری بیبیاں اور اللہ کی خوشنودی اور اللہ بندوں کو دیکھتا ہے'),
(309, 3, 16, 'وہ جو کہتے ہیں ے رب ہمارے! ہم ایمان لائے تو ہمارے گناہ معاف کر اور ہمیں دوزخ کے عذاب سے بچالے،'),
(310, 3, 17, 'صبر والے اور سچے اور ادب والے اور راہِ خدا میں خرچنے والے اور پچھلے پہر سے معافی مانگنے والے'),
(311, 3, 18, 'اور اللہ نے گواہی دی کہ اس کے سوا کوئی معبود نہیں اور فرشتوں نے اور عالموں نے انصاف سے قائم ہوکر اس کے سوا کسی کی عبادت نہیں عزت والا حکمت والا،'),
(312, 3, 19, 'بیشک اللہ کے یہاں اسلام ہی دین ہے اور پھوٹ میں نہ پڑے کتابی مگر اس کے کہ انہیں علم آچکا اپنے دلو ں کی جلن سے اور جو اللہ کی آیتوں کا منکر ہو تو بیشک اللہ جلد حساب لینے والا ہے،'),
(313, 3, 20, 'پھر اے محبوب! اگر وہ تم سے حجت کریں تو فرمادو میں اپنا منہ اللہ کے حضور جھکائے ہوں اور جومیرے پیرو ہوئے اور کتابیوں اور اَن پڑھوں سے فرماؤ کیا تم نے گردن رکھی پس اگر وہ گردن رکھیں جب تو راہ پاگئے اور اگر منہ پھیریں تو تم پر تو یہی حکم پہنچادینا ہے اور اللہ بندوں کو کو دیکھ رہا ہے،'),
(314, 3, 21, 'وہ جو اللہ کی آیتوں سے منکر ہوتے اور پیغمبروں کو ناحق شہید کرتے اور انصاف کا حکم کرنے والوں کو قتل کرتے ہیں انہیں خوشخبری دو درناک عذاب کی،'),
(315, 3, 22, 'یہ ہیں وہ جنکے اعمال اکارت گئے دنیا و آخرت میں اور ان کا کوئی مددگار نہیں'),
(316, 3, 23, 'کیا تم نے انہیں دیکھا جنہیں کتاب کا ایک حصہ ملا کتاب اللہ کی طرف بلائے جاتے ہیں کہ وہ ان کا فیصلہ کرے پھر ان میں کا ایک گروہ اس سے روگرداں ہوکر پھر جاتا ہے'),
(317, 3, 24, 'یہ جرأت انہیں اس لئے ہوئی کہ وہ کہتے ہیں ہرگز ہمیں آگ نہ چھوئے گی مگر گنتی کے دنوں اور ان کے دین میں انہیں فریب دیا اس جھوٹ نے جو باندھتے تھے'),
(318, 3, 25, 'تو کیسی ہوگی جب ہم انہیں اکٹھا کریں گے اس دن کے لئے جس میں شک نہیں اور ہر جان کو اس کی کمائی پوری بھر (بالکل پوری) دی جائے گی اور ان پر ظلم نہ ہوگا،'),
(319, 3, 26, 'یوں عرض کر، اے اللہ! ملک کے مالک تو جسے چاہے سلطنت دے اور جس سے چاہے چھین لے، اور جسے چاہے عزت دے اور جسے چاہے ذلت دے، ساری بھلائی تیرے ہی ہاتھ ہے، بیشک تو سب کچھ کرسکتا ہے'),
(320, 3, 27, 'تو دن کا حصّہ رات میں ڈالے اور رات کا حصہ دن میں ڈالے اور مردہ سے زندہ نکالے اور زندہ سے مردہ نکالے اور جسے چاہے بے گنتی دے،'),
(321, 3, 28, 'مسلمان کافروں کو اپنا دوست نہ بنالیں مسلمانوں کے سوا اور جو ایسا کرے گا اسے اللہ سے کچھ علاقہ نہ رہا مگر یہ کہ تم ان سے کچھ ڈرو اور اللہ تمہیں اپنے غضب سے ڈراتا ہے اور اللہ ہی کی طرف پھرنا ہے،'),
(322, 3, 29, 'تم فرمادو کہ اگر تم اپنے جی کی بات چھپاؤ یا ظاہر کرو اللہ کو سب معلوم ہے، اور جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے، اور ہر چیز پر اللہ کا قابو ہے،'),
(323, 3, 30, 'جس دن ہر جان نے جو بھلا کیا حاضر پائے گی اور جو برا کام کیا، امید کرے گی کاش مجھ میں اور اس میں دور کا فاصلہ ہوتا اور اللہ تمہیں اپنے عذاب سے ڈراتا ہے، اور اللہ بندوں پر مہربان ہے،'),
(324, 3, 31, 'اے محبوب! تم فرمادو کہ لوگو اگر تم اللہ کو دوست رکھتے ہو تو میرے فرمانبردار ہوجاؤ اللہ تمہیں دوست رکھے گا اور تمہارے گناہ بخش دے گا اور اللہ بخشنے والا مہربان ہے،'),
(325, 3, 32, 'تم فرمادو کہ حکم مانو اللہ اور رسول کا پھر اگر وہ منہ پھیریں تو اللہ کو خوش نہیں آتے کافر،'),
(326, 3, 33, 'بیشک اللہ نے چن لیا آدم اور نوح اور ابراہیم کی آ ل اولاد اور عمران کی آ ل کو سارے جہاں سے'),
(327, 3, 34, 'یہ ایک نسل ہے ایک دوسرے سے اور اللہ سنتا جانتا ہے،'),
(328, 3, 35, 'جب عمران کی بی بی نے عرض کی اے رب میرے! میں تیرے لئے منت مانتی ہو جو میرے پیٹ میں ہے کہ خالص تیری ہی خدمت میں رہے تو تو مجھ سے قبول کرلے بیشک تو ہی سنتا جانتا،'),
(329, 3, 36, 'پھر جب اسے جنا بولی، اے رب میرے! یہ تو میں نے لڑکی جنی اور اللہ جو خوب معلوم ہے جو کچھ وہ جنی، اور وہ لڑکا جو اس نے مانگا اس لڑکی سا نہیں اور میں نے اس کا نام مریم رکھا اور میں اسے اور اس کی اولاد کو تیری پناہ میں دیتی ہوں راندے ہوئے شیطان سے،'),
(330, 3, 37, 'تو اسے اس کے رب نے اچھی طرح قبول کیا اور اسے اچھا پروان چڑھایا اور اسے زکریا کی نگہبانی میں دیا، جب زکریا اس کے پاس اس کی نماز پڑھنے کی جگہ جاتے اس کے پاس نیا رزق پاتے کہا اے مریم! یہ تیرے پاس کہاں سے آیا، بولیں وہ اللہ کے پاس سے ہے، بیشک اللہ جسے چاہے بے گنتی دے'),
(331, 3, 38, 'یہاں پکارا زکریا اپنے رب کو بولا اے رب! میرے مجھے اپنے پاس سے دے ستھری اولاد، بیشک تو ہی ہے دعا سننے والا،'),
(332, 3, 39, 'تو فرشتوں نے اسے آواز دی اور وہ اپنی نماز کی جگہ کھڑا نماز پڑھ رہا تھا بیشک اللہ آپ کو مژدہ دیتا ہے یحییٰ کا جو اللہ کی طرف کے ایک کلمہ کی تصدیق کرے گا اور سردار اور ہمیشہ کے لیے عورتوں سے بچنے والا اور نبی ہمارے خاصوں میں سے'),
(333, 3, 40, 'بولا اے میرے رب میرے لڑکا کہاں سے ہوگا مجھے تو پہنچ گیا بڑھا پا اور میری عورت بانجھ فرمایا اللہ یوں ہی کرتا ہے جو چاہے'),
(334, 3, 41, 'عرض کی اے میرے رب میرے لئے کوئی نشانی کردے فرمایا تیری نشانی یہ ہے کہ تین دن تو لوگوں سے بات نہ کرے مگر اشارہ سے اور اپنے رب کی بہت یاد کر اور کچھ دن رہے اور تڑکے اس کی پاکی بول،'),
(335, 3, 42, 'اور جب فرشتوں نے کہا، اے مریم، بیشک اللہ نے تجھے چن لیا اور خوب ستھرا کیا اور آج سارے جہاں کی عورتوں سے تجھے پسند کیا'),
(336, 3, 43, 'اے مریم اپنے رب کے حضور ادب سے کھڑی ہو اور اس کے لئے سجدہ کر اور رکوع والوں کے ساتھ رکوع کر،'),
(337, 3, 44, 'یہ غیب کی خبریں ہیں کہ ہم خفیہ طور پر تمہیں بتاتے ہیں اور تم ان کے پاس نہ تھے جب وہ اپنی قلموں سے قرعہ ڈالتے تھے کہ مریم کس کی پرورش میں رہیں اور تم ان کے پاس نہ تھے جب وہ جھگڑ رہے تھے'),
(338, 3, 45, 'اور یاد کرو جب فرشتو ں نے مریم سے کہا، اے مریم! اللہ تجھے بشارت دیتا ہے اپنے پاس سے ایک کلمہ کی جس کا نام ہے مسیح عیسیٰ مریم کا بیٹا رُو دار (باعزت) ہوگا دنیا اور آخرت میں اور قرب والا'),
(339, 3, 46, 'اور لوگوں سے بات کرے گا پالنے میں اور پکی عمر میں اور خاصوں میں ہوگا،'),
(340, 3, 47, 'بولی اے میرے رب! میرے بچہ کہاں سے ہوگا مجھے تو کسی شخص نے ہاتھ نہ لگایا فرمایا اللہ یوں ہی پیدا کرتا ہے جو چاہے جب، کسی کام کا حکم فرمائے تو اس سے یہی کہتا ہے کہ ہوجا وہ فوراً ہوجاتا ہے،'),
(341, 3, 48, 'اور اللہ سکھائے گا کتاب اور حکمت اور توریت اور انجیل،'),
(342, 3, 49, 'اور رسول ہوگا بنی اسرائیل کی طرف، یہ فرماتا ہو کہ میں تمہارے پاس ایک نشانی لایا ہوں تمہارے رب کی طرف سے کہ میں تمہارے لئے مٹی سے پرند کی سی مور ت بناتا ہوں پھر اس میں پھونک مارتا ہوں تو وہ فوراً پرند ہوجاتی ہے اللہ کے حکم سے اور میں شفا دیتا ہوں مادر زاد اندھے اور سفید داغ والے کو اور میں مُردے جلاتا ہوں اللہ کے حکم سے اور تمہیں بتاتا ہوں جو تم کھاتے اور جو اپنے گھروں میں جمع کر رکھتے ہو، بیشک ان باتوں میں تمہارے لئے بڑی نشانی ہے اگر تم ایمان رکھتے ہو،'),
(343, 3, 50, 'اور تصدیق کرتا آیا ہوں اپنے سے پہلے کتاب توریت کی اور اس لئے کہ حلال کروں تمہارے لئے کچھ وہ چیزیں جو تم پر حرام تھیں اور میں تمہارے پاس پاس تمہارے رب کی طرف سے نشانی لایا ہوں، تو اللہ سے ڈرو اور میرا حکم مانو،'),
(344, 3, 51, 'بیشک میرا تمہارا سب کا رب اللہ ہے تو اسی کو پوجو یہ ہے سیدھا راستہ،'),
(345, 3, 52, 'پھر جب عیسیٰ نے ان سے کفر پایا بولا کون میرے مددگار ہوتے ہیں اللہ کی طرف، حواریوں نے کہا ہم دینِ خدا کے مددگار ہیں ہم اللہ پر ایمان لائے، اور آپ گواہ ہوجائیں کہ ہم مسلمان ہیں'),
(346, 3, 53, 'اے رب ہمارے! ہم اس پر ایمان لائے جو تو نے اتارا اور رسول کے تابع ہوئے تو ہمیں حق پر گواہی دینے والوں میں لکھ لے،'),
(347, 3, 54, 'اور کافروں نے مکر کیا اور اللہ نے ان کے ہلاک کی خفیہ تدبیر فرمائی اور اللہ سب سے بہتر چھپی تدبیر والا ہے'),
(348, 3, 55, 'یاد کرو جب اللہ نے فرمایا اے عیسیٰ میں تجھے پوری عمر تک پہنچاؤں گا اور تجھے اپنی طرف اٹھالوں گا اور تجھے کافروں سے پاک کردوں گا اور تیرے پیروؤں کو قیامت تک تیرے منکروں پر غلبہ دوں گا پھر تم سب میری طرف پلٹ کر آؤ گے تو میں تم میں فیصلہ فرمادوں گا جس بات میں جھگڑتے ہو،'),
(349, 3, 56, 'تو وہ جو کافر ہوئے میں انہیں دنیاو آخرت میں سخت عذاب کروں گا، اور انکا کوئی مددگار نہ ہوگا،'),
(350, 3, 57, 'اور وہ جو ایمان لائے اور اچھے کام کئے اللہ ان کا نیگ (انعام) انہیں بھرپور دے گا اور ظالم اللہ کو نہیں بھاتے،'),
(351, 3, 58, 'یہ ہم تم پر پڑھتے ہیں کچھ آیتیں اور حکمت والی نصیحت،'),
(352, 3, 59, 'عیسیٰ کی کہاوت اللہ کے نزدیک آدم کی طرح ہے اسے مٹی سے بنایا پھر فرمایا ہوجا وہ فوراً ہوجاتا ہے،'),
(353, 3, 60, 'اے سننے والے! یہ تیرے رب کی طرف سے حق ہے تو شک والوں میں نہ ہونا،'),
(354, 3, 61, 'پھر اے محبوب! جو تم سے عیسیٰ کے بارے میں حجت کریں بعد اس کے کہ تمہیں علم آچکا تو ان سے فرما دو آ ؤ ہم بلائیں اپنے بیٹے اور تمہارے بیٹے اور اپنی عورتیں اور تمہاری عورتیں اور اپنی جانیں اور تمہاری جانیں، پھر مباہلہ کریں تو جھوٹوں پر اللہ کی لعنت ڈالیں'),
(355, 3, 62, 'یہی بیشک سچا بیان ہے اور اللہ کے سوا کوئی معبود نہیں اور بیشک اللہ ہی غالب ہے حکمت والا،'),
(356, 3, 63, 'پھر اگر وہ منہ پھیریں تو اللہ فسادیوں کو جانتا ہے،'),
(357, 3, 64, 'تم فرماؤ، اے کتابیو! ایسے کلمہ کی طرف آؤ جو ہم میں تم میں یکساں ہے یہ کہ عبادت نہ کریں مگر خدا کی اور اس کا شریک کسی کو نہ کریں اور ہم میں کوئی ایک دوسرے کو رب نہ بنالے اللہ کے سوا پھر اگر وہ نہ مانیں تو کہہ دو تم گواہ رہو کہ ہم مسلمان ہیں،'),
(358, 3, 65, 'اے کتاب والو! ابراہیم کے باب میں کیوں جھگڑتے ہو توریت و انجیل تو نہ اتری مگر ان کے بعد تو کیا تمہیں عقل نہیں'),
(359, 3, 66, 'سنتے ہو یہ جو تم ہو اس میں جھگڑے جس کا تمہیں علم تھا تو اس میں کیوں جھگڑتے ہو جس کا تمہیں علم ہی نہیں اور اللہ جانتا ہے اور تم نہیں جانتے'),
(360, 3, 67, 'ابراہیم نہ یہودی تھے نہ نصرانی بلکہ ہر باطل سے جدا مسلمان تھے، اور مشرکوں سے نہ تھے'),
(361, 3, 68, 'بیشک سب لوگوں سے ابراہیم کے زیادہ حق دار وہ تھے جو ان کے پیرو ہوئے اور یہ نبی اور ایمان والے اور ایمان والوں کا ولی اللہ ہے،'),
(362, 3, 69, 'کتابیوں کا ایک گروہ دل سے چاہتا ہے کہ کسی طرح تمہیں گمراہ کردیں، اور وہ اپنے ہی آپ کو گمراہ کرتے ہیں اور انہیں شعور نہیں'),
(363, 3, 70, 'اے کتابیو! اللہ کی آیتوں سے کیوں کفر کرتے ہو حالانکہ تم خود گواہی ہو'),
(364, 3, 71, 'اے کتابیو! حق میں باطل کیوں ملاتے ہو اور حق کیوں چھپاتے ہو حالانکہ تمہیں خبر ہے،'),
(365, 3, 72, 'اور کتابیوں کا ایک گروہ بولا وہ جو ایمان والوں پر اترا صبح کو اس پر ایمان لاؤ اور شام کو منکر ہوجاؤ شاید وہ پھر جائیں'),
(366, 3, 73, 'اور یقین نہ لاؤ مگر اس کا جو تمہارے دین کا پیرو ہو تم فرمادو کہ اللہ ہی کی ہدایت ہدایت ہے (یقین کا ہے کا نہ لاؤ) اس کا کہ کسی کو ملے جیسا تمہیں ملا یا کوئی تم پر حجت لاسکے تمہارے رب کے پاس تم فرمادو کہ فضل تو اللہ ہی کے ہاتھ ہے جسے چاہے دے، اور اللہ وسعت والا علم والا ہے،'),
(367, 3, 74, 'اپنی رحمت سے خاص کرتا ہے جسے چاہے اور اللہ بڑے فضل والا ہے،'),
(368, 3, 75, 'اور کتابیوں میں کوئی وہ ہے کہ اگر تو اس کے پاس ایک ڈھیر امانت رکھے تو وہ تجھے ادا کردے گا اور ان میں کوئی وہ ہے کہ اگر ایک اشرفی اس کے پاس امانت رکھے تو وہ تجھے پھیر کر نہ دے گا مگر جب تک تو اس کے سر پر کھڑا رہے یہ اس لئے کہ وہ کہتے ہیں کہ اَن پڑھوں کے معاملہ میں ہم پر کوئی مؤاخذہ نہیں اور اللہ پر جان بوجھ کر جھوٹ باندھتے ہیں'),
(369, 3, 76, 'ہاں کیوں نہیں جس نے اپنا عہد پورا کیا اور پرہیزگاری کی اور بیشک پرہیزگار اللہ کو خوش آتے ہیں،'),
(370, 3, 77, 'جو اللہ کے عہد اور اپنی قسموں کے بدلے ذلیل دام لیتے ہیں آخرت میں ان کا کچھ حصہ نہیں اور اللہ نہ ان سے بات کرے نہ ان کی طرف نظر فرمائے قیامت کے دن اور نہ انہیں پاک کرے، اور ان کے لئے دردناک عذاب ہے'),
(371, 3, 78, 'اور ان میں کچھ وہ ہیں جو زبان پھیر کر کتاب میں میل (ملاوٹ) کرتے ہیں کہ تم سمجھو یہ بھی کتاب میں ہے اور وہ کتاب میں نہیں، اور وہ کہتے ہیں یہ اللہ کے پاس سے ہے اور وہ اللہ کے پاس سے نہیں، اور اللہ پر دیدہ و دانستہ جھوٹ باندھتے ہیں'),
(372, 3, 79, 'کسی آدمی کا یہ حق نہیں کہ اللہ اسے کتاب اور حکم و پیغمبری دے پھر وہ لوگوں سے کہے کہ اللہ کو چھوڑ کر میرے بندے ہوجاؤ ہاں یہ کہے گا کہ اللہ والے ہوجاؤ اس سبب سے کہ تم کتاب سکھاتے ہو اور اس سے کہ تم درس کرتے ہو'),
(373, 3, 80, 'اور نہ تمہیں یہ حکم دے گا کہ فرشتوں اور پیغمبروں کو خدا ٹھیرالو، کیا تمہیں کفر کا حکم دے گا بعد اس کے کہ تم مسلمان ہولیے'),
(374, 3, 81, 'اور یاد کرو جب اللہ نے پیغمبروں سے ان کا عہد لیا جو میں تم کو کتاب اور حکمت دوں پھر تشریف لائے تمہارے پاس وہ رسول کہ تمہاری کتابوں کی تصدیق فرمائے تو تم ضرور ضرور اس پر ایمان لانا اور ضرور ضرور اس کی مدد کرنا، فرمایا کیوں تم نے اقرار کیا اور اس پر میرا بھاری ذمہ لیا؟ سب نے عرض کی، ہم نے اقرار کیا، فرمایا تو ایک دوسرے پر گواہ ہوجاؤ اور میں آپ تمہارے ساتھ گواہوں میں ہوں،'),
(375, 3, 82, 'تو جو کوئی اس کے بعد پھرے تو وہی لوگ فاسق ہیں'),
(376, 3, 83, 'تو کیا اللہ کے دین کے سوا اور دین چاہتے ہیں اور اسی کے حضور گردن رکھے ہیں جو کوئی آسمانوں اور زمین میں ہیں خوشی سے سے مجبوری سے'),
(377, 3, 84, 'اور اُسی کی طرف پھیریں گے، یوں کہو کہ ہم ایمان لائے اللہ پر اور اس پر جو ہماری طرف اترا اور جو اترا ابراہیم اور اسماعیل اور اسحاق اور یعقوب اور ان کے بیٹوں پر اور جو کچھ ملا موسیٰ اور عیسیٰ اور انبیاء کو ان کے رب سے، ہم ان میں کسی پر ایمان میں فرق نہیں کرتے اور ہم اسی کے حضور گردن جھکائے ہیں'),
(378, 3, 85, 'اور جو اسلام کے سوا کوئی دین چاہے گا وہ ہرگز اس سے قبول نہ کیا جائے گا اور وہ آخرت میں زیاں کاروں سے ہے،'),
(379, 3, 86, 'کیونکر اللہ ایسی قوم کی ہدایت چاہے جو ایمان لاکر کافر ہوگئے اور گواہی دے چکے تھے کہ رسول سچا ہے اور انہیں کھلی نشانیاں آچکی تھیں اور اللہ ظالموں کو ہدایت نہیں کرتا'),
(380, 3, 87, 'ان کا بدلہ یہ ہے کہ ان پر لعنت ہے اللہ اور فرشتوں اور آدمیوں کی سب کی،'),
(381, 3, 88, 'ہمیشہ اس میں رہیں، نہ ان پر سے عذاب ہلکا ہو اور نہ انہیں مہلت دی جائے،'),
(382, 3, 89, 'مگر جنہوں نے اس کے بعد توبہ کی اور آپا سنبھالا تو ضرور اللہ بخشنے والا مہربان ہے،'),
(383, 3, 90, 'بیشک وہ جو ایمان لاکر کافر ہوئے پھر اور کفر میں بڑھے ان کی توبہ ہرگز قبول نہ ہوگی اور وہی ہیں بہکے ہوئے،'),
(384, 3, 91, 'وہ جو کافر ہوئے اور کافر ہی مرے ان میں کسی سے زمین بھر سونا ہرگز قبول نہ کیا جائے گا اگرچہ اپنی خلاصی کو دے، ان کے لئے دردناک عذاب ہے اور ان کا کوئی یار نہیں۔'),
(385, 3, 92, 'تم ہرگز بھلائی کو نہ پہنچو گے جب تک راہِ خدا میں اپنی پیاری چیز نہ خرچ کرو اور تم جو کچھ خرچ کرو اللہ کو معلوم ہے،'),
(386, 3, 93, 'سب کھانے بنی اسرائیل کو حلال تھے مگر وہ جو یعقوبؑ نے اپنے اوپر حرام کرلیا تھا توریت اترنے سے پہلے تم فرماؤ توریت لاکر پڑھو اگر سچے ہو'),
(387, 3, 94, 'تو اس کے بعد جو اللہ پر جھوٹ باندھے تو وہی ظالم ہیں،'),
(388, 3, 95, 'تم فرماؤ اللہ سچا ہے، تو ابراہیم کے دین پر چلو جو ہر باطل سے جدا تھے اور شرک والوں میں نہ تھے،'),
(389, 3, 96, 'بیشک سب میں پہلا گھر جو لوگوں کی عبادت کو مقرر ہوا وہ ہے جو مکہ میں ہے برکت والا اور سارے جہان کا راہنما'),
(390, 3, 97, 'اس میں کھلی نشانیاں ہیں ابراہیم کے کھڑے ہونے کی جگہ اور جو اس میں آئے امان میں ہو اور اللہ کے لئے لوگوں پر اس گھر کا حج کرنا ہے جو اس تک چل سکے اور جو منکر ہو تو اللہ سارے جہان سے بے پرواہ ہے'),
(391, 3, 98, 'تم فرماؤ اے کتابیو! اللہ کی آیتیں کیوں نہیں مانتے اور تمہارے کام اللہ سامنے ہیں،'),
(392, 3, 99, 'تم فرماؤ اے کتابیو! کیوں اللہ کی راہ سے روکتے ہو اسے جو ایمان لائے اسے ٹیڑھا کیا چاہتے ہو اور تم خود اس پر گواہ ہو اور اللہ تمہارے کوتکوں (برے اعمال، کرتوت) سے بے خبر نہیں،'),
(393, 3, 100, 'اے ایمان والو! اگر تم کچھ کتابیوں کے کہے پر چلے تو وہ تمہارے ایمان کے بعد کافر کر چھوڑیں گے'),
(394, 3, 101, 'اور تم کیوں کر کفر کروگے تم پر اللہ کی آیتیں پڑھی جاتی ہیں اور تم میں اس کا رسول تشریف لایا اور جس نے اللہ کا سہارا لیا تو ضرور وہ سیدھی راہ دکھایا گیا،'),
(395, 3, 102, 'اے ایمان والو! اللہ سے ڈرو جیسا اس سے ڈرنے کا حق ہے اور ہرگز نہ مرنا مگر مسلمان،'),
(396, 3, 103, 'اور اللہ کی رسی مضبوط تھام لو سب مل کر اور آپس میں پھٹ نہ جانا (فرقوں میں نہ بٹ جانا) اور اللہ کا احسان اپنے اوپر یاد کرو جب تم میں بیر تھا اس نے تمہارے دلوں میں ملاپ کردیا تو اس کے فضل سے تم آپس میں بھائی ہوگئے اور تم ایک غار دوزخ کے کنارے پر تھے تو اس نے تمہیں اس سے بچادیا اللہ تم سے یوں ہی اپنی آیتیں بیان فرماتا ہے کہ کہیں تم ہدایت پاؤ،'),
(397, 3, 104, 'اور تم میں ایک گروہ ایسا ہونا چاہئے کہ بھلائی کی طرف بلائیں اور اچھی بات کا حکم دیں اور بری سے منع کریں اور یہی لوگ مراد کو پہنچے'),
(398, 3, 105, 'اور ان جیسے نہ ہونا جو آپس میں پھٹ گئے اور ان میں پھوٹ پڑگئی بعد اس کے کہ روشن نشانیاں انہیں آچکی تھیں اور ان کے لیے بڑا عذاب ہے،'),
(399, 3, 106, 'جس دن کچھ منہ اونجالے ہوں گے اور کچھ منہ کالے تو وہ جن کے منہ کالے ہوئے کیا تم ایمان لاکر کافر ہوئے تو اب عذاب چکھو اپنے کفر کا بدلہ،'),
(400, 3, 107, 'اور وہ جن کے منہ اونجالے ہوئے وہ اللہ کی رحمت میں ہیں وہ ہمیشہ اس میں رہیں گے،'),
(401, 3, 108, 'یہ اللہ کی آیتیں ہیں کہ ہم ٹھیک ٹھیک تم پر پڑھتے ہیں، اور اللہ جہاں والوں پر ظلم نہیں چاہتا'),
(402, 3, 109, 'اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے، اور اللہ ہی کی طرف سب کاموں کی رجوع ہے،'),
(403, 3, 110, 'تم بہتر ہو ان امتوں میں جو لوگوں میں ظاہر ہوئیں بھلائی کا حکم دیتے ہو اور برائی سے منع کرتے ہو اور اللہ پر ایمان رکھتے ہو، اور اگر کتابی ایمان لاتے تو ان کا بھلا تھا، ان میں کچھ مسلمان ہیں اور زیادہ کافر،'),
(404, 3, 111, 'وہ تمہارے کچھ نہ بگاڑیں گے مگر یہی ستانا اور اگر تم سے لڑیں تو تمہارے سامنے سے پیٹھ پھیر جائیں گے پھر ان کی مدد نہ ہوگی،'),
(405, 3, 112, 'ان پر جمادی گئی خواری جہاں ہوں امان نہ پائیں مگر اللہ کی ڈور اور آدمیوں کی ڈور سے اور غضب الٰہی کے سزاوار ہوئے اور ان پر جمادی گئی محتاجی یہ اس لئے کہ وہ اللہ کی آیتوں سے کفر کرتے اور پیغمبروں کو ناحق شہید کرتے، یہ اس لئے کہ نا فرمانبردار اور سرکش تھے،'),
(406, 3, 113, 'سب ایک سے نہیں کتابیوں میں کچھ وہ ہیں کہ حق پر قائم ہیں اللہ کی آیتیں پڑھتے ہیں رات کی گھڑیوں میں اور سجدہ کرتے ہیں'),
(407, 3, 114, 'اللہ اور پچھلے دن پر ایمان لاتے ہیں اور بھلائی کا حکم دیتے اور برائی سے منع کرتے ہیں اور نیک کاموں پر دوڑتے ہیں، اور یہ لوگ لائق ہیں،'),
(408, 3, 115, 'اور وہ جو بھلائی کریں ان کا حق نہ مارا جائے گا اور اللہ کو معلوم ہیں ڈر والے'),
(409, 3, 116, 'وہ جو کافر ہوئے ان کے مال اور اولاد ان کو اللہ سے کچھ نہ بچالیں گے اور وہ جہنمی ہیں ان کو ہمیشہ اس میں رہنا'),
(410, 3, 117, 'کہاوت اس کی جو اس دنیا میں زندگی میں خرچ کرتے ہیں اس ہوا کی سی ہے جس میں پالا ہو وہ ایک ایسی قوم کی کھیتی پر پڑی جو اپنا ہی برا کرتے تھے تو اسے بالکل مارگئی اور اللہ نے ان پر ظلم نہ کیا ہاں وہ خود اپنی جانوں پر ظلم کرتے ہیں،'),
(411, 3, 118, 'اے ایمان والو! غیروں کو اپنا راز دار نہ بناؤ وہ تمہاری برائی میں کمی نہیں کرتے ان کی آرزو ہے، جتنی ایذا پہنچے بیَر ان کی باتوں سے جھلک اٹھا اور وہ جو سینے میں چھپائے ہیں اور بڑا ہے، ہم نے نشانیاں تمہیں کھول کر سنادیں اگر تمہیں عقل ہو'),
(412, 3, 119, 'سنتے ہو یہ جو تم ہو تم تو انہیں چاہتے ہو اور وہ تمہیں نہیں چاہتے اور حال یہ کہ تم سب کتابوں پر ایمان لاتے ہو اور وہ جب تم سے ملتے ہیں کہتے ہیں ہم ایمان لائے اور اکیلے ہوں تو تم پر انگلیاں چبائیں غصہ سے تم فرمادو کہ مرجاؤ اپنی گھٹن (قلبی جلن) میں اللہ خوب جانتا ہے دلوں کی بات،'),
(413, 3, 120, 'تمہیں کوئی بھلائی پہنچے تو انہیں برا لگے اور تم کہ برائی پہنچے تو اس پر خوش ہوں، اور اگر تم صبر اور پرہیزگاری کیے رہو تو ان کا داؤ تمہارا کچھ نہ بگاڑے گا، بیشک ان کے سب کام خدا کے گھیرے میں ہیں،'),
(414, 3, 121, 'اور یاد کرو اے محبوب! جب تم صبح کو اپنے دولت خانہ سے برآمد ہوئے مسلمانوں کو لڑائی کے مور چوں پر قائم کرتے اور اللہ سنتا جانتا ہے،'),
(415, 3, 122, 'جب تم میں کے دو گروہوں کا ارادہ ہوا کہ نامردی کرجائیں اور اللہ ان کا سنبھالنے والا ہے اور مسلمانوں کو اللہ ہی پر بھروسہ چاہئے،'),
(416, 3, 123, 'اور بیشک اللہ نے بدر میں تمہاری مدد کی جب تم بالکل بے سر و سامان تھے تو اللہ سے ڈرو کہیں تم شکر گزار ہو،'),
(417, 3, 124, 'جب اے محبوب تم مسلمانوں سے فرماتے تھے کیا تمہیں یہ کافی نہیں کہ تمہارا رب تمہاری مدد کرے تین ہزار فرشتہ اتار کر،'),
(418, 3, 125, 'ہا ں کیوں نہیں اگر تم صبرو تقویٰ کرو اور کافر اسی دم تم پر آپڑیں تو تمہارا رب تمہاری مدد کو پانچ ہزار فرشتے نشان والے بھیجے گا'),
(419, 3, 126, 'اور یہ فتح اللہ نے نہ کی مگر تمہاری خوشی کے لئے اور اسی لئے کہ اس سے تمہارے دلوں کو چین ملے اور مدد نہیں مگر اللہ غالب حکمت والے کے پاس سے'),
(420, 3, 127, 'اس لئے کہ کافروں کا ایک حصہ کاٹ دے یا انہیں ذلیل کرے کہ نامراد پھر جائیں،'),
(421, 3, 128, 'یہ بات تمہارے ہاتھ نہیں یا انہیں توبہ کی توفیق دے یا ان پر عذاب کرے کہ وہ ظالم ہیں،'),
(422, 3, 129, 'اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے جسے چاہے بخشے اور جسے چاہے عذاب کرے، اور اللہ بخشنے والا مہربان،'),
(423, 3, 130, 'اے ایمان والوں سود دونا دون نہ کھاؤ اللہ سے ڈرو اس امید پر کہ تمہیں فلاح ملے،'),
(424, 3, 131, 'اور اس آگ سے بچو جو کافروں کے لئے تیار رکھی ہے'),
(425, 3, 132, 'اور اللہ و رسول کے فرمانبردار رہو اس امید پر کہ تم رحم کیے جاؤ،'),
(426, 3, 133, 'اور دوڑو اپنے رب کی بخشش اور ایسی جنت کی طرف جس کی چوڑان میں سب آسمان و زمین پرہیزگاروں کے لئے تیار کر رکھی ہے'),
(427, 3, 134, 'وہ جو اللہ کے راہ میں خرچ کرتے ہیں خوشی میں اور رنج میں اور غصہ پینے والے اور لوگوں سے درگزر کرنے والے، اور نیک لوگ اللہ کے محبوب ہیں،'),
(428, 3, 135, 'اور وہ کہ جب کوئی بے حیائی یا اپنی جانوں پر ظلم کریں اللہ کو یاد کرکے اپنے گناہوں کی معافی چاہیں اور گناہ کون بخشے سوا اللہ کے، اور اپنے کیے پر جان بوجھ کر اڑ نہ جائیں،'),
(429, 3, 136, 'ایسوں کو بدلہ ان کے رب کی بخشش اور جنتیں ہیں جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں اور کامیوں (نیک لوگوں) کا اچھا نیگ (انعام، حصہ) ہے'),
(430, 3, 137, 'تم سے پہلے کچھ طریقے برتاؤ میں آچکے ہیں تو زمین میں چل کر دیکھو کیسا انجام ہوا جھٹلانے والوں کا'),
(431, 3, 138, 'یہ لوگوں کو بتانا اور راہ دکھانا اور پرہیزگاروں کو نصیحت ہے،'),
(432, 3, 139, 'اور نہ سستی کرو اور نہ غم کھاؤ تم ہی غالب آؤ گے اگر ایمان رکھتے ہو،'),
(433, 3, 140, 'اگر تمہیں کوئی تکلیف پہنچی تو وہ لوگ بھی ویسی ہی تکلیف پاچکے ہیں اور یہ دن ہیں جن میں ہم نے لوگوں کے لیے باریاں رکھی ہیں اور اس لئے کہ اللہ پہچان کرادے ایمان والوں کی اور تم میں سے کچھ لوگوں کو شہادت کا مرتبہ دے اور اللہ دوست نہیں رکھتا ظالموں کو،'),
(434, 3, 141, 'اور اس لئے کہ اللہ مسلمانوں کا نکھار کردے اور کافروں کو مٹادے'),
(435, 3, 142, 'کیا اس گمان میں ہو کہ جنت میں چلے جاؤ گے اور ابھی اللہ نے تمہارے غازیوں کا امتحان نہ لیا اور نہ صبر والوں آزمائش کی'),
(436, 3, 143, 'اور تم تو موت کی تمنا کیا کرتے تھے اس کے ملنے سے پہلے تو اب وہ تمہیں نظر آئی آنکھوں کے سامنے،'),
(437, 3, 144, 'اور محمد تو ایک رسول ان سے پہلے اور رسول ہوچکے تو کیا اگر وہ انتقال فرمائیں یا شہید ہوں تو تم الٹے پاؤں پھر جاؤں گے اور جو الٹے پاؤں پھرے گا اللہ کا کچھ نقصان نہ کرے گا، اور عنقریب اللہ شکر والوں کو صلہ دے گا،'),
(438, 3, 145, 'اور کوئی جان بے حکم خدا مر نہیں سکتی سب کا وقت لکھا رکھا ہے اور دنیا کا انعام چاہے ۰ف۲۶۱) ہم اس میں سے اسے دیں اور جو آخرت کا انعام چاہے ہم اس میں سے اسے دیں اور قریب ہے کہ ہم شکر والوں کو صلہ عطا کریں،'),
(439, 3, 146, 'اور کتنے ہی انبیاء نے جہاد کیا ان کے ساتھ بہت خدا والے تھے، تو نہ سست پڑے ان مصیبتوں سے جو اللہ کی راہ میں انہیں پہنچیں اور نہ کمزور ہوئے اور نہ دبے اور صبر والے اللہ کو محبوب ہیں،'),
(440, 3, 147, 'اور وہ کچھ بھی نہ کہتے تھے سوا اس دعا کے کہ اے ہمارے رب بخش دے ہمارے گناہ اور جو زیادتیاں ہم نے اپنے کام کیں اور ہمارے قدم جما دے اور ہمیں ان کافر لوگوں پر مدد دے'),
(441, 3, 148, 'تو اللہ نے انہیں دنیا کا انعام دیا اور آخرت کے ثواب کی خوبی اور نیکی والے اللہ کو پیارے ہیں،'),
(442, 3, 149, 'اے ایمان والو! اگر تم کافروں کے کہے پر چلے تو وہ تمہیں الٹے پاؤں لوٹادیں گے پھر ٹوٹا کھا کے پلٹ جاؤ گے'),
(443, 3, 150, 'بلکہ اللہ تمہارا مولا ہے اور وہ سب سے بہتر مددگار،'),
(444, 3, 151, 'کوئی دم جاتا ہے کہ ہم کافروں کے دلوں میں رعب ڈالیں گے کہ انہوں نے اللہ کا شریک ٹھہرایا جس پر اس نے کوئی سمجھ نہ اتاری اور ان کا ٹھکانا دوزخ ہے، اور کیا برا ٹھکانا ناانصافوں کا،'),
(445, 3, 152, 'اور بیشک اللہ نے تمہیں سچ کر دکھایا اپنا وعدہ جب کہ تم اس کے حکم سے کافروں کو قتل کرتے تھے یہاں تک کہ جب تم نے بزدلی کی اور حکم میں جھگڑا ڈالا اور نافرمانی کی بعد اس کے کہ اللہ تمہیں دکھا چکا تمہاری خوشی کی بات تم میں کوئی دنیا چاہتا تھا اور تم میں کوئی آخرت چاہتا تھا پھر تمہارا منہ ان سے پھیردیا کہ تمہیں آزمائے اور بیشک اس نے تمہیں معاف کردیا، اور اللہ مسلمانوں پر فضل کرتا ہے،'),
(446, 3, 153, 'جب تم منہ اٹھائے چلے جاتے تھے اور پیٹھ پھیر کر کسی کو نہ دیکھتے اور دوسری جماعت میں ہمارے رسول تمہیں پکار رہے تھے تو تمہیں غم کا بدلہ غم دیا اور معافی اس لئے سنائی کہ جو ہاتھ سے گیا اور جو افتاد پڑی اس کا رنج نہ کرو اور اللہ کوتمہارے کاموں کی خبر ہے،'),
(447, 3, 154, 'پھر تم پر غم کے بعد چین کی نیند اتاری کہ تمہاری ایک جماعت کو گھیرے تھی اور ایک گروہ کو اپنی جان کی پڑی تھی اللہ پر بے جا گمان کرتے تھے جاہلیت کے سے گمان، کہتے کیا اس کام میں کچھ ہمارا بھی اختیار ہے تم فرمادو کہ اختیار تو سارا اللہ کا ہے اپنے دلوں میں چھپاتے ہیں جو تم پر ظاہر نہیں کرتے کہتے ہیں، ہمارا کچھ بس ہوتا تو ہم یہاں نہ مارے جاتے، تم فرمادو کہ اگر تم اپنے گھروں میں ہوتے جب بھی جن کا مارا جانا لکھا جاچکا تھا اپنی قتل گاہوں تک نکل آتے اور اس لئے کہ اللہ تمہارے سینوں کی بات آزمائے اور جو کچھ تمہارے دلو ں میں ہے اسے کھول دے اور اللہ دلوں کی بات جانتا ہے'),
(448, 3, 155, 'بیشک وہ جو تم میں سے پھرگئے جس دن دونوں فوجیں ملی تھیں انہیں شیطان ہی نے لغزش دی ان کے بعض اعمال کے باعث اور بیشک اللہ نے انہیں معاف فرمادیا، بیشک اللہ بخشنے والا حلم والا ہے،'),
(449, 3, 156, 'اے ایمان والو! ان کافروں کی طرح نہ ہونا جنہوں نے اپنے بھائیوں کی نسبت کہا کہ جب وہ سفر یا جہاد کو گئے کہ ہمارے پاس ہوتے تو نہ مرتے اور نہ مارے جاتے اس لئے اللہ ان کے دلوں میں اس کا افسوس رکھے، اور اللہ جِلاتا اور مارتا ہے اور اللہ تمہارے کام دیکھ رہا ہے،'),
(450, 3, 157, 'اور بیشک اگر تم اللہ کی راہ میں مارے جاؤ یا مرجاؤ تو اللہ کی بخشش اور رحمت ان کے سارے دھن دولت سے بہتر ہے،'),
(451, 3, 158, 'اور اگر تم مرو یا مارے جاؤ تو اللہ کی طرف اٹھنا ہے'),
(452, 3, 159, 'تو کیسی کچھ اللہ کی مہربانی ہے کہ اے محبوب! تم ان کے لئے نرم دل ہوئے اور اگر تند مزاج سخت دل ہوتے تو وہ ضرور تمہارے گرد سے پریشان ہوجاتے تو تم انہیں معاف فرماؤ او ر ان کی شفاعت کرو اور کاموں میں ان سے مشورہ لو اور جو کسی بات کا ارادہ پکا کرلو تو اللہ پر بھروسہ کرو بیشک توکل والے اللہ کو پیارے ہیں،'),
(453, 3, 160, 'اگر اللہ تمہاری مدد کرے تو کوئی تم پر غالب نہیں آسکتا اور اگر وہ تمہیں چھوڑ دے تو ایسا کون ہے جو پھر تمہاری مدد کرے اور مسلمانوں کو اللہ ہی پر بھروسہ چاہئے،'),
(454, 3, 161, 'اور کسی نبی پر یہ گمان نہیں ہوسکتا کہ وہ کچھ چھپا رکھے اور جو چھپا رکھے وہ قیامت کے دن اپنی چھپائی چیز لے کر آئے گا پھر ہر جان کو ان کی کمائی بھرپور دی جائے گی اور ان پر ظلم نہ ہوگا،'),
(455, 3, 162, 'تو کیا جو اللہ کی مرضی پر چلا وہ اس جیسا ہوگا جس نے اللہ کا غضب اوڑھا اور اس کا ٹھکانا جہنم ہے، اور کیا بری جگہ پلٹنے کی،'),
(456, 3, 163, 'وہ اللہ کے یہاں درجہ درجہ ہیں اور اللہ ان کے کام دیکھتا ہے،'),
(457, 3, 164, 'بیشک اللہ کا بڑا احسان ہوا مسلمانوں پر کہ ان میں انہیں میں سے ایک رسول بھیجا جو ان پر اس کی آیتیں پڑھتا ہے اور انہیں پاک کرتا ہے اور انہیں کتاب و حکمت سکھاتا ہے اور وہ ضرور اس سے پہلے کھلی گمراہی میں تھے'),
(458, 3, 165, 'کیا جب تمہیں کوئی مصیبت پہنچے کہ اس سے دونی تم پہنچا چکے ہو تو کہنے لگو کہ یہ کہاں سے آئی تم فرمادو کہ وہ تمہاری ہی طرف سے آئی بیشک اللہ سب کچھ کرسکتا ہے،'),
(459, 3, 166, 'اور وہ مصیبت جو تم پر آئی جس دن دونوں فوجیں ملی تھیں وہ اللہ کے حکم سے تھی اور اس لئے کہ پہچان کرادے ایمان والوں کی،'),
(460, 3, 167, 'اور اس لئے کہ پہچان کرادے، ان کی جو منافق ہوئے اور ان سے کہا گیا کہ ا ٓؤ اللہ کی راہ میں لڑو یا دشمن کو ہٹاؤ بولے اگر ہم لڑائی ہوتی جانتے تو ضرو ر تمہارا ساتھ دیتے، اور اس دن ظاہری ایمان کی بہ نسبت کھلے کفر سے زیادہ قریب ہیں، اپنے منہ سے کہتے ہیں جو ان کے دل میں نہیں اور اللہ کو معلوم ہے جو چھپارہے ہیں'),
(461, 3, 168, 'وہ جنہوں نے اپنے بھائیوں کے بارے میں کہا اور آپ بیٹھ رہے کہ وہ ہمارا کہا مانتے تو نہ مارے جاتے تم فرمادو تو اپنی ہی موت ٹال دو اگر سچے ہو'),
(462, 3, 169, 'اور جو اللہ کی راہ میں مارے گئے ہر گز انہیں مردہ نہ خیال کرنا، بلکہ وہ اپنے رب کے پاس زندہ ہیں روزی پاتے ہیں'),
(463, 3, 170, 'شاد ہیں اس پر جو اللہ نے انہیں اپنے فضل سے دیا اور خوشیاں منارہے ہیں اپنے پچھلوں کی جو ابھی ان سے نہ ملے کہ ان پر نہ کچھ اندیشہ ہے اور نہ کچھ غم،'),
(464, 3, 171, 'خوشیاں مناتے ہیں اللہ کی نعمت اور فضل کی اور یہ کہ اللہ ضائع نہیں کرتا اجر مسلمانوں کا'),
(465, 3, 172, 'وہ جو اللہ و رسول کے بلانے پر حاضر ہوئے بعد اس کے کہ انہیں زخم پہنچ چکا تھا ان کے نکوکاروں اور پرہیزگاروں کے لئے بڑا ثواب ہے،'),
(466, 3, 173, 'وہ جن سے لوگوں نے کہا کہ لوگوں نے تمہارے لئے جتھا جوڑا تو ان س ے ڈرو تو ان کا ایمان اور زائد ہوا اور بولے اللہ ہم کو بس ہے اور کیا اچھا کارساز'),
(467, 3, 174, 'تو پلٹے اللہ کے احسان اور فضل سے کہ انہیں کوئی برائی نہ پہنچی اور اللہ کی خوشی پر چلے اور اللہ بڑے فضل والا ہے'),
(468, 3, 175, 'وہ تو شیطان ہی ہے کہ اپنے دوستوں سے دھمکاتا ہے تو ان سے نہ ڈرو اور مجھ سے ڈرو اگر ایمان رکھتے ہو'),
(469, 3, 176, 'اور اے محبوب! تم ان کا کچھ غم نہ کرو جو کفر پر دوڑتے ہیں وہ اللہ کا کچھ بگاڑیں گے اور اللہ چاہتا ہے کہ آخرت میں ان کا کوئی حصہ نہ رکھے اور ان کے لئے بڑا عذاب ہے،'),
(470, 3, 177, 'وہ جنہوں نے ایمان کے بدلے کفر مول لیا اللہ کا کچھ نہ بگاڑیں گے اور ان کے لئے دردناک عذاب ہے،'),
(471, 3, 178, 'اور ہرگز کافر اس گمان میں نہ رہیں کہ وہ جو ہم انہیں ڈھیل دیتے ہیں کچھ ان کے لئے بھلا ہے، ہم تو اسی لئے انہیں ڈھیل دیتے ہیں کہ اور گناہ میں بڑھیں اور ان کے لئے ذلت کا عذاب ہے،'),
(472, 3, 179, 'اللہ مسلمانوں کو اس حال پر چھوڑنے کا نہیں جس پر تم ہو جب تک جدا نہ کردے گندے کو ستھرے سے اور اللہ کی شان یہ نہیں کہ اے عام لوگو! تمہیں غیب کا علم دے دے ہاں اللہ چن لیتا ہے اپنے رسولوں سے جسے چاہے تو ایمان لاؤ اللہ اور اس کے رسولوں پر اور اگر ایمان لاؤ اور پرہیزگاری کرو تو تمہارے لئے بڑا ثواب ہے،'),
(473, 3, 180, 'اور جو بخل کرتے ہیں اس چیز میں جو اللہ نے انہیں اپنے فضل سے دی ہرگز اسے اپنے لئے اچھا نہ سمجھیں بلکہ وہ ان کے لئے برا ہے، عنقریب وہ جس میں بخل کیا تھا قیامت کے دن ان کے گلے کا طوق ہوگا اور اللہ ہی وارث ہے آسمانوں اور زمین کا اور اللہ تمہارے کاموں سے خبردار ہے،'),
(474, 3, 181, 'بیشک اللہ نے سنا جنہوں نے کہا کہ اللہ محتاج ہے اور ہم غنی اور ہم غنی اب ہم لکھ رکھیں گے ان کا کہا اور انبیاء کو ان کا ناحق شہید کرنا اور فرمائیں گے کہ چکھو آگ کا عذاب،'),
(475, 3, 182, 'یہ بدلا ہے اس کا جو تمہارے ہاتھوں نے آگے بھیجا اور اللہ بندوں پر ظلم نہیں کرتا،'),
(476, 3, 183, 'وہ جو کہتے ہیں اللہ نے ہم سے اقرار کر لیا ہے کہ ہم کسی رسول پر ایمان نہ لائیں جب تک ایسی قربانی کا حکم نہ لائے جس آ گ کھائے تم فرمادو مجھ سے پہلے بہت رسول تمہارے پاس کھلی نشانیاں اور یہ حکم لے کر آئے جو تم کہتے ہو پھر تم نے انہیں کیوں شہید کیا اگر سچے ہو'),
(477, 3, 184, 'تو اے محبوب! اگر وہ تمہاری تکذیب کرتے ہیں تو تم سے اگلے رسولوں کی بھی تکذیب کی گئی ہے جو صاف نشانیاں اور صحیفے اور چمکتی کتاب لے کر آئے تھے'),
(478, 3, 185, 'ہر جان کو موت چکھنی ہے، اور تمہارے بدلے تو قیامت ہی کو پورے ملیں گے، جو آگ سے بچا کر جنت میں داخل کیا گیا وہ مراد کو پہنچا، اور دنیا کی زندگی تو یہی دھوکے کا مال ہے'),
(479, 3, 186, 'بیشک ضرور تمہاری آزمائش ہوگی تمہارے مال اور تمہاری جانوں میں اور بیشک ضرور تم اگلے کتاب والوں اور مشرکوں سے بہت کچھ برا سنو گے اور اگر تم صبر کرو اور بچتے رہو تو یہ بڑی ہمت کا کام ہے،'),
(480, 3, 187, 'اور یاد کرو جب اللہ نے عہد لیا ان سے جنہیں کتاب عطا فرمائی کہ تم ضرور اسے لوگوں سے بیان کردینا اور نہ چھپانا تو انہوں نے اسے اپنی پیٹھ کے پیچھے پھینک دیا اور اس کے بدلے ذلیل دام حاصل کیے تو کتنی بری خریداری ہے'),
(481, 3, 188, 'ہر گز نہ سمجھنا انہیں جو خوش ہوتے ہیں اپنے کیے پر اور چاہتے ہیں کہ بے کیے ان کی تعریف ہو ایسوں کو ہرگز عذاب سے دور نہ جاننا اور ان کے لیے دردناک عذاب ہے'),
(482, 3, 189, 'اور اللہ ہی کے لئے ہے آسمانوں اور زمین کی بادشاہی اور اللہ ہر چیز پر قادر ہے،'),
(483, 3, 190, 'بیشک آسمانوں اور زمین کی پیدائش اور رات اور دن کی باہم بدلیوں میں نشانیاں ہیں عقلمندوں کے لئے'),
(484, 3, 191, 'جو اللہ کی یاد کرت ہیں کھڑے اور بیٹھے اور کروٹ پر لیٹے اور آسمانوں اور زمین کی پیدائش میں غور کرتے ہیں اے رب ہمارے! تو نے یہ بیکار نہ بنایا پاکی ہے تجھے تو ہمیں دوزخ کے عذاب سے بچالے،'),
(485, 3, 192, 'اے رب ہمارے! بیشک جسے تو دوزخ میں لے جائے اسے ضرور تو نے رسوائی دی اور ظالموں کا کوئی مددگار نہیں،'),
(486, 3, 193, 'اے رب ہمارے ہم نے ایک منادی کو سنا کہ ایمان کے لئے ندا فرماتا ہے کہ اپنے رب پر ایمان لاؤ تو ہم ایمان لائے اے رب ہمارے تو ہمارے گنا بخش دے اور ہماری برائیاں محو فرمادے اور ہماری موت اچھوں کے ساتھ کر'),
(487, 3, 194, 'اے رب ہمارے! اور ہمیں دے وہ جس کا تو نے ہم سے وعدہ کیا ہے اپنے رسولوں کی معرفت اور ہمیں قیامت کے دن رسوانہ کر، بیشک تو وعدہ خلاف نہیں کرتا،'),
(488, 3, 195, 'تو ان کی دعا سن لی ان کے رب نے کہ میں تم میں کام والے کی محنت اکارت نہیں کرتا مرد ہو یا عورت تم آپس میں ایک ہو تو وہ جنہوں نے ہجرت کی اور اپنے گھروں سے نکالے گئے اور میری راہ میں ستائے گئے اور لڑے اور مارے گء ے میں ضرور ان کے سب گناہ اتار دوں گا اور ضرور انہیں باغوں میں لے جاؤں گا جن کے نیچے نہریں رواں اللہ کے پاس کا ثواب، اور اللہ ہی کے پاس اچھا ثواب ہے،'),
(489, 3, 196, 'اے سننے والے! کافروں کا شہروں میں اہلے گہلے پھرنا ہرگز تجھے دھوکا نہ دے'),
(490, 3, 197, 'تھوڑا برتنا، ان کا ٹھکانا دوزخ ہے، اور کیا ہی برا بچھونا،'),
(491, 3, 198, 'لیکن وہ جو اپنے رب سے ڈرتے ہیں ان کے لئے جنتیں ہیں جن کے نیچے نہریں بہیں ہمیشہ ان میں رہیں اللہ کی طرف کی، مہمانی اور جو اللہ پاس ہے وہ نیکوں کے لئے سب سے بھلا'),
(492, 3, 199, 'اور بیشک کچھ کتابیں ایسے ہیں کہ اللہ پر ایمان لاتے ہیں اور اس پر جو تمہاری طرف اترا اور جو ان کی طرف اترا ان کے دل اللہ کے حضور جھکے ہوئے اللہ کی آیتوں کے بدلے ذلیل دام نہیں لیتے یہ وہ ہیں، جن کا ثواب ان کے رب کے پاس ہے اور اللہ جلد حساب کرنے والا ہے،'),
(493, 3, 200, 'اے ایمان والو! صبر کرو اور صبر میں دشمنوں سے آگے رہو اور سرحد پر اسلامی ملک کی نگہبانی کرو اور اللہ سے ڈرتے رہو اس امید پر کہ کامیاب ہو،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اے لوگو! اپنے رب سے ڈرو جس نے تمہیں ایک جان سے پیدا کیا اور اسی میں سے اس کا جوڑا بنایا اور ان دونوں سے بہت سے مرد و عورت پھیلا دیئے اور اللہ سے ڈرو جس کے نام پر مانگتے ہو اور رشتوں کا لحاظ رکھو بیشک اللہ ہر وقت تمہیں دیکھ رہا ہے،'),
(495, 4, 2, 'اور یتیموں کو ان کے مال دو اور ستھرے کے بدلے گندا نہ لو اور ان کے مال اپنے مالوں میں ملاکر نہ کھا جاؤ، بیشک یہ بڑا گناہ ہے'),
(496, 4, 3, 'ا ور اگر تمہیں اندیشہ ہو کہ یتیم لڑکیوں میں انصاف نہ کرو گے تو نکاح میں لاؤ جو عورتیں تمہیں خوش آئیں دو دو اور تین تین او ر چار چار پھر اگر ڈرو کہ دو بیبیوں کو برابر نہ رکھ سکو گے تو ایک ہی کرو یا کنیزیں جن کے تم مالک ہو یہ اس سے زیادہ قریب ہے کہ تم سے ظلم نہ ہو'),
(497, 4, 4, 'اور عورتوں کو ان کے مہر خوشی سے دو پھر اگر وہ اپنے دل کی خوشی سے مہر میں سے تمہیں کچھ دے دیں تو اسے کھاؤ رچتا پچتا'),
(498, 4, 5, 'اور بے عقلوں کو ان کے مال نہ دو جو تمہارے پاس ہیں جن کو اللہ نے تمہاری بسر اوقات کیا ہے اور انہیں اس میں سے کھلاؤ اور پہناؤ اور ان سے اچھی بات کہو'),
(499, 4, 6, 'اور یتیموں کو آزماتے رہو یہاں تک کہ جب وہ نکاح کے قابل ہوں تو اگر تم ان کی سمجھ ٹھیک دیکھو تو ان کے مال انہیں سپرد کردو اور انہیں نہ کھاؤ حد سے بڑھ کر اور اس جلدی میں کہ کہیں بڑے نہ ہوجائیں اور جسے حاجت نہ ہو وہ بچتا رہے اور جو حاجت مند ہو وہ بقدر مناسب کھائے، پھر جب تم ان کے مال انہیں سپرد کرو تو ان پر گواہ کرلو، اور اللہ کافی ہے حساب لینے کو،'),
(500, 4, 7, 'مردوں کے لئے حصہ ہے اس میں سے جو چھوڑ گئے ماں باپ اور قرابت والے اور عورتوں کے لئے حصہ ہے اس میں سے جو چھوڑ گئے ماں باپ اور قرابت والے ترکہ تھوڑا ہو یا بہت، حصہ ہے اندازہ باندھا ہوا'),
(501, 4, 8, 'پھر بانٹتے وقت اگر رشتہ دار اور یتیم اور مسکین آجائیں تو اس میں سے انہیں بھی کچھ دو اور ان سے اچھی بات کہو'),
(502, 4, 9, 'اور ڈریں وہ لوگ اگر اپنے بعد ناتواں اولاد چھوڑتے تو ان کا کیسا انہیں خطرہ ہوتا تو چاہئے کہ اللہ سے ڈریں اور سیدھی بات کریں'),
(503, 4, 10, 'وہ جو یتیموں کا مال ناحق کھاتے ہیں وہ تو اپنے پیٹ میں نری آ گ بھرتے ہیں اور کوئی دم جاتا ہے کہ بھڑتے دھڑے (آتش کدے) میں جائیں گے،'),
(504, 4, 11, 'اللہ تمہیں حکم دیتا ہے تمہاری اولاد کے بارے میں بیٹے کا حصہ دو بیٹیوں برابر ہے پھر اگر نری لڑکیاں ہوں اگرچہ دو سے اوپر تو ان کو ترکہ کی دو تہائی اور اگر ایک لڑکی ہو تو اس کا آدھا اور میت کے ماں باپ کو ہر ایک کو اس کے ترکہ سے چھٹا اگر میت کے اولاد ہو پھر اگر اس کی اولاد نہ ہو اور ماں باپ چھوڑے تو ماں کا تہا ئی پھر اگر اس کے کئی بہن بھا ئی ہو ں تو ماں کا چھٹا بعد اس و صیت کے جو کر گیا اور دین کے تمہارے باپ اور تمہارے بیٹے تم کیا جانو کہ ان میں کون تمہارے زیادہ کام آئے گا یہ حصہ باندھا ہوا ہے اللہ کی طرف سے بیشک اللہ علم والا حکمت والا ہے،'),
(505, 4, 12, 'اور تمہاری بیبیاں جو چھوڑ جائیں اس میں سے تمہیں آدھا ہے اگر ان کی اولاد نہ ہو، پھر اگر ان کی اولاد ہو تو ان کے ترکہ میں سے تمہیں چوتھائی ہے جو وصیت وہ کر گئیں اور دَین نکال کر اور تمہارے ترکہ میں عورتوں کا چوتھائی ہے اگر تمہارے اولاد نہ ہو پھر اگر تمہارے اولاد ہو تو ان کا تمہارے ترکہ میں سے آٹھواں جو وصیت تم کر جاؤ اور دین نکال کر، اور اگر کسی ایسے مرد یا عورت کا ترکہ بٹنا ہو جس نے ماں باپ اولاد کچھ نہ چھوڑے اور ماں کی طرف سے اس کا بھائی یا بہن ہے تو ان میں سے ہر ایک کو چھٹا پھر اگر وہ بہن بھائی ایک سے زیادہ ہوں تو سب تہائی میں شریک ہیں میت کی وصیت اور دین نکال کر جس میں اس نے نقصان نہ پہنچایا ہو یہ اللہ کا ارشاد ہے اور اللہ علم والا حلم والا ہے،'),
(506, 4, 13, 'یہ اللہ کی حدیں ہیں اور جو حکم مانے اللہ اور اللہ کے رسول کا اللہ اسے باغوں میں لے جائے گا جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں گے، اور یہی ہے بڑی کامیابی،'),
(507, 4, 14, 'اور جو اللہ اور اس کے رسول کی نافرمانی کرے اور اس کی کل حدوں سے بڑھ جائے اللہ اسے آگ میں داخل کرے گا جس میں ہمیشہ رہے گا اور اس کے لئے خواری کا عذاب ہے'),
(508, 4, 15, 'اور تمہاری عورتوں میں جو بدکاری کریں ان پر خاص اپنے میں کے چار مردوں کی گواہی لو پھر اگر وہ گواہی دے دیں تو ان عورتوں کو گھر میں بند رکھو یہاں تک کہ انہیں موت اٹھالے یا اللہ ان کی کچھ راہ نکالے'),
(509, 4, 16, 'اور تم میں جو مرد عورت ایسا کریں ان کو ایذا دو پھر اگر وہ توبہ کرلیں اور نیک ہوجائیں تو ان کا پیچھا چھوڑ دو، بیشک اللہ بڑا توبہ قبول کرنے والا مہربان ہے'),
(510, 4, 17, 'وہ توبہ جس کا قبول کرنا اللہ نے اپنے فضل سے لازم کرلیا ہے وہ انہیں کی ہے جو نادانی سے برائی کر بیٹھیں پھر تھوڑی دیر میں توبہ کرلیں ایسوں پر اللہ اپنی رحمت سے رجوع کرتا ہے، اور اللہ علم و حکمت والا ہے،'),
(511, 4, 18, 'اور وہ توبہ ان کی نہیں جو گناہوں میں لگے رہتے ہیں، یہاں تک کہ جب ان میں کسی کو موت آئے تو کہے اب م یں نے توبہ کی اور نہ ان کی جو کافر مریں ان کے لئے ہم نے دردناک عذاب تیار رکھا ہے'),
(512, 4, 19, 'اے ایمان والو! تمہیں حلال نہیں کہ عورتوں کے وارث بن جاؤ زبردستی اور عورتوں کو روکو نہیں اس نیت سے کہ جو مہر ان کو دیا تھا اس میں سے کچھ لے لو مگر اس صورت میں کہ صریح بے حیا ئی کا کام کریں اور ان سے اچھا برتاؤ کرو پھر اگر وہ تمہیں پسند نہ آئیں تو قریب ہے کہ کوئی چیز تمہیں ناپسند ہو اور اللہ اس میں بہت بھلائی رکھے'),
(513, 4, 20, 'اور اگر تم ایک بی بی کے بدلے دوسری بدلنا چاہو اور اسے ڈھیروں مال دے چکے ہو تو اس میں سے کچھ واپس نہ لو کیا اسے واپس لو گے جھوٹ باندھ کر اور کھلے گناہ سے'),
(514, 4, 21, 'اور کیونکر اسے واپس لوگے حالانکہ تم میں ایک دوسرے کے سامنے بے پردہ ہولیا اور وہ تم سے گاڑھا عہد لے چکیں'),
(515, 4, 22, 'اور باپ دادا کی منکوحہ سے نکاح نہ کرو مگر جو ہو گزرا وہ بیشک بے حیائی اور غضب کا کام ہے، اور بہت بری راہ'),
(516, 4, 23, 'حرام ہوئیں تم پر تمہاری مائیں اور بیٹیاں اور بہنیں اور پھوپھیاں اور خالائیں اور بھتیجیاں اور بھانجیاں اور تمہاری مائیں جنہوں نے دودھ پلایا اور دودھ کی بہنیں اور عورتوں کی مائیں اور ان کی بیٹیاں جو تمہاری گود میں ہیں ان بیبیوں سے جن سے تم صحبت کرچکے ہو تو پھر اگر تم نے ان سے صحبت نہ کی ہو تو ان کی بیٹیوں سے حرج نہیں اور تمہاری نسلی بیٹوں کی بیبیں اور دو بہنیں اکٹھی کرنا مگر جو ہو گزرا بیشک اللہ بخشنے والا مہربان ہے،'),
(517, 4, 24, 'اور حرام ہیں شوہر دار عورتیں مگر کافروں کی عورتیں جو تمہاری ملک میں آجائیں یہ اللہ کا نوشتہ ہے تم پر اور ان کے سوا جو رہیں وہ تمہیں حلال ہیں کہ اپنے مالوں کے عوض تلاش کرو قید لاتے نہ پانی گراتے تو جن عورتوں کو نکاح میں لانا چاہو ان کے بندھے ہوئے مہر انہیں دو، اور قرار داد کے بعد اگر تمہارے آپس میں کچھ رضامندی ہوجاوے تو اس میں گناہ نہیں بیشک اللہ علم و حکمت والا ہے،'),
(518, 4, 25, 'اور تم میں بے مقدوری کے باعث جن کے نکاح میں آزاد عورتیں ایمان والیاں نہ ہوں تو ان سے نکاح کرے جو تمہارے ہاتھ کی مِلک ہیں ایمان والی کنیزیں اور اللہ تمہارے ایمان کو خوب جانتا ہے، تم میں ایک دوسرے سے ہے تو ان سے نکاح کرو ان کے مالکوں کی اجازت سے اور حسب دستور ان کے مہر انہیں دو قید میں آتیاں نہ مستی نکالتی اور نہ یار بناتی جب وہ قید میں آجائیں پھر برا کام کریں تو ان پر اس سزا کی آدھی ہے جو آزاد عورتوں پر ہے یہ اس کے لئے جسے تم میں سے زنا کا اندیشہ ہے، اور صبر کرنا تمہارے لئے بہتر ہے اور اللہ بخشنے والا مہربان ہے،'),
(519, 4, 26, 'اللہ چاہتا ہے کہ اپنے احکام تمہارے لئے بیان کردے اور تمہیں اگلوں کی روشیں بتادے اور تم پر اپنی رحمت سے رجوع فرمائے اور اللہ علم و حکمت والا ہے،'),
(520, 4, 27, 'اور اللہ تم پر اپنی رحمت سے رجوع فرمانا چاہتا ہے، اور جو اپنے مزوں کے پیچھے پڑے ہیں وہ چاہتے ہیں کہ تم سیدھی راہ سے بہت الگ ہوجاؤ'),
(521, 4, 28, 'اللہ چاہتا ہے کہ تم پر تخفیف کرے اور آدمی کمزور بنایا گیا'),
(522, 4, 29, 'اے ایمان والو! آپس میں ایک دوسرے کے مال ناحق نہ کھاؤ مگر یہ کہ کوئی سودا تمہاری باہمی رضامندی کا ہو اور اپنی جانیں قتل نہ کرو بیشک اللہ تم پر مہربان ہے'),
(523, 4, 30, 'اور جو ظلم و زیادتی سے ایسا کرے گا تو عنقریب ہم اسے آگ میں داخل کریں گے اور یہ اللہ کو آسان ہے،'),
(524, 4, 31, 'اگر بچتے رہو کبیرہ گناہوں سے جن کی تمہیں ممانعت ہے تو تمہارے اور گناہ ہم بخش دیں گے اور تمہیں عزت کی جگہ داخل کریں گے،'),
(525, 4, 32, 'اور ا س کی آرزو نہ کرو جس سے اللہ نے تم میں ایک کو دوسرے پر بڑائی دی مردوں کے لئے ان کی کمائی سے حصہ ہے، اور عورتوں کے لئے ان کی کمائی سے حصہ اور اللہ سے اس کا فضل مانگو، بیشک اللہ سب کچھ جانتا ہے،'),
(526, 4, 33, 'اور ہم نے سب کے لئے مال کے مستحق بنادیے ہیں جو کچھ چھوڑ جائیں ماں باپ اور قرابت والے اور وہ جن سے تمہارا حلف بندھ چکا انہیں ان کا حصہ دو، بیشک ہر چیز اللہ کے سامنے ہے،'),
(527, 4, 34, 'مرد افسر ہیں عورتوں پر اس لیے کہ اللہ نے ان میں ایک کو دوسرے پر فضیلت دی اور اس لئے کہ مردوں نے ان پر اپنے مال خرچ کیے تو نیک بخت عورتیں ادب والیاں ہیں خاوند کے پیچھے حفاظت رکھتی ہیں جس طرح اللہ نے حفاظت کا حکم دیا اور جن عورتوں کی نافرمانی کا تمہیں اندیشہ ہو تو انہیں سمجھاؤ اور ان سے الگ سوؤ اور انہیں مارو پھر اگر وہ تمہارے حکم میں آجائیں تو ان پر زیادتی کی کوئی راہ نہ چاہو بیشک اللہ بلند بڑا ہے'),
(528, 4, 35, 'اور اگر تم کو میاں بی بی کے جھگڑے کا خوف ہو تو ایک پنچ مرد والوں کی طرف سے بھیجو اور ایک پنچ عورت والوں کی طرف سے یہ دونوں اگر صلح کرانا چاہیں گے تو اللہ ان میں میل کردے گا، بیشک اللہ جاننے والا خبردار ہے'),
(529, 4, 36, 'اور اللہ کی بندگی کرو اور اس کا شریک کسی کو نہ ٹھہراؤ اور ماں باپ سے بھلائی کرو اور رشتہ داروں اور یتیموں اور محتاجوں اور پاس کے ہمسائے اور دور کے ہمسائے اور کروٹ کے ساتھی اور راہ گیر اور اپنی باندی غلام سے بیشک اللہ کو خوش نہیں آتا کوئی اترانے والے بڑائی مارنے والا'),
(530, 4, 37, 'جو آپ بخل کریں اور اوروں سے بخل کے لئے کہیں اور اللہ نے جو انہیں اپنے فضل سے دیا ہے اسے چھپائيں اور کافروں کے لئے ہم نے ذلت کا عذاب تیار کر رکھا ہے،'),
(531, 4, 38, 'اور وہ جو اپنے مال لوگوں کے دکھاوے کو خرچ کرتے ہیں اور ایمان نہیں لاتے اللہ اور نہ قیامت پر، اور جس کا مصاحب شیطان ہوا، تو کتنا برا مصاحب ہے،'),
(532, 4, 39, 'اور ان کا کیا نقصان تھا اگر ایمان لاتے اللہ اور قیامت پر اور اللہ کے دیے میں سے اس کی راہ میں خرچ کرتے اور اللہ ان کو جانتا ہے،'),
(533, 4, 40, 'اللہ ایک ذرہ بھر ظلم نہیں فرماتا اور اگر کوئی نیکی ہو تو اسے دونی کرتا اور اپنے پاس سے بڑا ثواب دیتا ہے،'),
(534, 4, 41, 'تو کیسی ہوگی جب ہم ہر امت سے ایک گواہ لائیں اور اے محبوب! تمہیں ان سب پر گواہ اور نگہبان بناکر لائیں'),
(535, 4, 42, 'اس دن تمنا کریں گے وہ جنہوں نے کفر کیا اور رسول کی نافرمانی کی کاش انہیں مٹی میں دبا کر زمین برابر کردی جائے، اور کوئی بات اللہ سے نہ چھپاسکیں گے'),
(536, 4, 43, 'اے ایمان والو! نشہ کی حالت میں نماز کے پاس نہ جاؤ جب تک اتنا ہوش نہ ہو کہ جو کہو اسے سمجھو اور نہ ناپاکی کی حالت میں بے نہائے مگر مسافری میں اور اگر تم بیمار ہو یا سفر میں یا تم میں سے کوئی قضائے حاجت سے آیا ہو یا تم نے عورتو ں کو چھوا اور پانی نہ پایا تو پاک مٹی سے تیمم کرو تو اپنے منہ اور ہاتھوں کا مسح کرو بیشک اللہ معاف کرنے والا بخشنے والا ہے،'),
(537, 4, 44, 'کیا تم نے انہیں نہ دیکھا جن کو کتاب سے ایک حصہ ملا گمراہی مول لیتے ہیں اور چاہتے ہیں کہ تم بھی راہ سے بہک جاؤ،'),
(538, 4, 45, 'اور اللہ خوب جانتا ہے تمہارے دشمنوں کو اور اللہ کافی ہے والی اور اللہ کافی ہے مددگار،'),
(539, 4, 46, 'کچھ یہودی کلاموں کو ان کی جگہ سے پھیرتے ہیں اور کہتے ہیں ہم نے سنا اور نہ مانا اور سنیئے آپ سنائے نہ جائیں اور راعنا کہتے ہیں زبانیں پھیر کر اور دین میں طعنہ کے لئے اور اگر وہ کہتے کہ ہم نے سنا اور مانا اور حضور ہماری بات سنیں اور حضور ہم پر نظر فرمائیں تو ان کے لئے بھلائی اور راستی میں زیادہ ہوتا لیکن ان پر تو اللہ نے لعنت کی ان کے کفر کے سبب تو یقین نہیں رکھتے مگر تھوڑا'),
(540, 4, 47, 'اے کتاب والو! ایمان لاؤ اس پر جو ہم نے اتارا تمہارے ساتھ والی کتاب کی تصدیق فرماتا قبل اس کے کہ ہم بگاڑ دیں کچھ مونہوں کو تو انہیں پھیر دیں ان کی پیٹھ کی طرف یا انہیں لعنت کریں جیسی لعنت کی ہفتہ والوں پر اور خدا کا حکم ہوکر رہے،'),
(541, 4, 48, 'بیشک اللہ اسے نہیں بخشتا کہ اس کے ساتھ کفر کیا جائے اور کفر سے نیچے جو کچھ ہے جسے چاہے معاف فرمادیتا ہے اور جس نے خدا کا شریک ٹھہرایا اس نے بڑا گناہ کا طوفان باندھا،'),
(542, 4, 49, 'کیا تم نے انہیں نہ دیکھا جو خود اپنی ستھرائی بیان کرتے ہیں بلکہ اللہ جسے چاہے ستھرا کرے اور ان پر ظلم نہ ہوگا دانہ خرما کے ڈورے برابر'),
(543, 4, 50, 'دیکھو کیسا اللہ پر جھوٹ باندھا رہے ہیں اور یہ کافی ہے صریح گناہ،'),
(544, 4, 51, 'کیا تم نے، وہ نہ دیکھے جنہیں کتاب کا ایک حصہ ملا ایمان لاتے ہیں بت اور شیطان پر اور کافروں کو کہتے ہیں کہ یہ مسلمانوں سے زیادہ راہ پر ہیں،'),
(545, 4, 52, 'یہ ہیں جن پر اللہ نے لعنت کی اور جسے خدا لعنت کرے تو ہر گز اسکا کوئی یار نہ پائے گا'),
(546, 4, 53, 'کیا ملک میں ان کا کچھ حصہ ہے ایسا ہو تو لوگوں کو تِل بھر نہ دیں،'),
(547, 4, 54, 'یا لوگوں سے حسد کرتے ہیں اس پر جو اللہ نے انہیں اپنے فضل سے دیا تو ہم نے تو ابراہیم کی اولاد کو کتاب اور حکمت عطا فرمائی اور انہیں بڑا ملک دیا'),
(548, 4, 55, 'تو ان میں کوئی اس پر ایمان لایا اور کسی نے اس سے منہ پھیرا اور دوزخ کافی ہے بھڑکتی آگ'),
(549, 4, 56, 'جنہوں نے ہماری آیتوں کا انکار کیا عنقریب ہم ان کو آگ میں داخل کریں گے، جب کبھی ان کی کھالیں پک جائیں گی ہم ان کے سوا اور کھالیں انہیں بدل دیں گے کہ عذاب کا مزہ لیں، بیشک اللہ غالب حکمت والا ہے،'),
(550, 4, 57, 'اور جو لوگ ایمان لائے اور اچھے کام کیے عنقریب ہم انہیں باغوں میں لے جائیں گے جن کے نیچے نہریں رواں ان میں ہمیشہ رہیں گے، ان کے لیے وہاں ستھری بیبیاں ہیں اور ہم انہیں وہاں داخل کریں گے جہاں سایہ ہی سایہ ہوگا'),
(551, 4, 58, 'بیشک اللہ تمہیں حکم دیتا ہے کہ امانتیں جن کی ہیں انہیں سپرد کرو اور یہ کہ جب تم لوگوں میں فیصلہ کرو تو انصاف کے ساتھ فیصلہ کرو بیشک اللہ تمہیں کیا ہی خوب نصیحت فرماتا ہے، بیشک اللہ سنتا دیکھتا ہے،'),
(552, 4, 59, 'اے ایمان والو! حکم مانو اللہ کا اور حکم مانو رسول کا اور ان کا جو تم میں حکومت والے ہیں پھر اگر تم میں کسی بات کا جھگڑا اٹھے تو اسے اللہ اور رسول کے حضور رجوع کرو اگر اللہ اور قیامت پر ایمان رکھتے ہو یہ بہتر ہے اور اس کا انجام سب سے اچھا،'),
(553, 4, 60, 'کیا تم نے انہیں نہ دیکھا جن کا دعویٰ ہے کہ وہ ایمان لائے اس پر جو تمہاری طرف اترا اور اس پر جو تم سے پہلے اترا پھر چاہتے ہیں کہ شیطان کو اپنا پنچ بنائیں اور ان کو تو حکم یہ تھا کہ اسے اصلاً نہ مانیں اور ابلیس یہ چاہتا ہے کہ انہیں دور بہکاوے'),
(554, 4, 61, 'اور جب ان سے کہا جائے کہ اللہ کی اتاری ہوئی کتاب اور رسول کی طرف آؤ تو تم دیکھو گے کہ منافق تم سے منہ موڑ کر پھر جاتے ہیں،'),
(555, 4, 62, 'کیسی ہوگی جب ان پر کوئی افتاد پڑے بدلہ اسکا جو انکے ہاتھوں نے آگے بھیجا پھر اے محبوب! تمہارے حضور حاضر ہوں، اللہ کی قسم کھاتے کہ ہمارا مقصود تو بھلائی اور میل ہی تھا'),
(556, 4, 63, 'ان کے دلوں کی تو بات اللہ جانتا ہے تو تم ان سے چشم پوشی کرو اور انہیں سمجھا دو اور ان کے معاملہ میں ان سے رسا بات کہو'),
(557, 4, 64, 'اور ہم نے کوئی رسول نہ بھیجا مگر اس لئے کہ اللہ کے حکم سے اس کی اطاعت کی جائے اور اگر جب وہ اپنی جانوں پر ظلم کریں تو اے محبوب! تمہارے حضور حاضر ہوں اور پھر اللہ سے معافی چاہیں اور رسول ان کی شفاعت فرمائے تو ضرور اللہ کو بہت توبہ قبول کرنے والا مہربان پائیں'),
(558, 4, 65, 'تو اے محبوب! تمہارے رب کی قسم وہ مسلمان نہ ہوں گے جب تک اپنے آپس کے جھگڑے میں تمہیں حاکم نہ بنائئیں پھر جو کچھ تم حکم فرما دو اپنے دلوں میں اس سے رکاوٹ نہ پائیں اور جی سے مان لیں'),
(559, 4, 66, 'اور اگر ہم ان پر فرض کرتے کہ اپنے آپ کو قتل کردو یا اپنے گھر بار چھوڑ کر نکل جاؤ تو ان میں تھوڑے ہی ایسا کرتے، اور اگر وہ کرتے جس بات کی انہیں نصیحت دی جاتی ہے تو اس میں ان کا بھلا تھا اور ایمان پر خوب جمنا'),
(560, 4, 67, 'اور ایسا ہوتا تو ضرور ہم انہیں اپنے پاس سے بڑا ثواب دیتے'),
(561, 4, 68, 'اور ضرور ان کو سیدھی راہ کی ہدایت کرتے،'),
(562, 4, 69, 'اور جو اللہ اور اس کے رسول کا حکم مانے تو اُسے ان کا ساتھ ملے گا جن پر اللہ نے فضل کیا یعنی انبیاء اور صدیق اور شہید اور نیک لوگ یہ کیا ہی اچھے ساتھی ہیں،'),
(563, 4, 70, 'یہ اللہ کا فضل ہے، اور اللہ کافی ہے جاننے والا،'),
(564, 4, 71, 'اے ایمان والو! ہوشیاری سے کام لو پھر دشمن کی طرف تھوڑے تھوڑے ہوکر نکلو یا اکٹھے چلو'),
(565, 4, 72, 'اور تم میں کوئی وہ ہے کہ ضرور دیر لگائے گا پھر اگر تم پر کوئی افتاد پڑے تو کہے خدا کا مجھ پر احسان تھا کہ میں ان کے ساتھ حاضر نہ تھا،'),
(566, 4, 73, 'اور اگر تمہیں اللہ کا فضل ملے تو ضرو ر کہے گویا تم اس میں کوئی دوستی نہ تھی اے کاش میں ان کے ساتھ ہوتا تو بڑی مراد پاتا،'),
(567, 4, 74, 'تو انہیں اللہ کی راہ میں لڑنا چاہئے جو دنیا کی زندگی بیچ کر آخرت لیتے ہیں اور جو اللہ کی راہ میں لڑے پھر مارا جائے یا غالب آئے تو عنقریب ہم اسے بڑا ثواب دیں گے،'),
(568, 4, 75, 'اور تمہیں کیا ہوا کہ نہ لڑو اللہ کی راہ میں اور کمزور مردوں اور عورتوں اور بچوں کے واسطے یہ دعا کررہے ہیں کہ اے ہمارے رب ہمیں اس بستی سے نکال جس کے لوگ ظالم ہیں اور ہمیں اپنے پاس سے کوئی حمایتی دے دے اور ہمیں اپنے پاس سے کوئی مددگار دے دے،'),
(569, 4, 76, 'ایمان والے اللہ کی راہ میں لڑتے ہیں اور کفار شیطان کی راہ میں لڑتے ہیں تو شیطان کے دوستوں سے لڑو بیشک شیطان کا داؤ کمزور ہے'),
(570, 4, 77, 'کیا تم نے انہیں نہ دیکھا جن سے کہا گیا اپنے ہاتھ روک لو اور نماز قائم رکھو اور زکوٰة دو پھر جب ان پر جہاد فرض کیا گیا تو ان میں بعضے لوگوں سے ایسا ڈرنے لگے جیسے اللہ سے ڈرے یا اس سے بھی زائد اور بولے اے رب ہمارے! تو نے ہم پر جہاد کیوں فرض کردیا تھوڑی مدت تک ہمیں اور جینے دیا ہوتا، تم فرما دو کہ دنیا کا برتنا تھوڑا ہے اور ڈر والوں کے لئے آخرت اچھی اور تم پر تاگے برابر ظلم نہ ہوگا'),
(571, 4, 78, 'تم جہاں کہیں ہو موت تمہیں آلے گی اگرچہ مضبوط قلعوں میں ہو اور اُنہیں کوئی بھلائی پہنچے تو کہیں یہ اللہ کی طرف سے ہے اور انہیں کوئی برائی پہنچے تو کہیں یہ حضور کی طرف آئی تم فرمادو سب اللہ کی طرف سے ہے تو ان لوگوں کو کیا ہوا کوئی بات سمجھتے معلوم ہی نہیں ہوتے،'),
(572, 4, 79, 'اے سننے والے تجھے جو بھلائی پہنچے وہ اللہ کی طرف سے ہے اور جو برائی پہنچے وہ تیری اپنی طرف سے ہے اور اے محبوب ہم نے تمہیں سب لوگوں کے لئے رسول بھیجا اور اللہ کافی ہے گواہ'),
(573, 4, 80, 'جس نے رسول کا حکم مانا بیشک اس نے اللہ کا حکم مانا اور جس نے منہ پھیرا تو ہم نے تمہیں ان کے بچانے کو نہ بھیجا'),
(574, 4, 81, 'اور کہتے ہیں ہم نے حکم مانا پھر جب تمہارے پاس سے نکل کر جاتے ہیں تو ان میں ایک گروہ جو کہہ گیا تھا اس کے خلاف رات کو منصوبے گانٹھتا ہے اور اللہ لکھ رکھتا ہے ان کے رات کے منصوبے تو اے محبوب تم ان سے چشم پوشی کرو اور اللہ پر بھروسہ رکھو اور اللہ کافی ہے کام بنانے کو،'),
(575, 4, 82, 'تو کیا غور نہیں کرتے قرآن میں اور اگر وہ غیر خدا کے پاس سے ہوتا تو ضرور اس میں بہت اختلاف پاتے'),
(576, 4, 83, 'اور جب ان کے پاس کوئی بات اطمینان یا ڈر کی آتی ہے اس کا چرچا کر بیٹھتے ہیں اور اگر اس میں رسول اور اپنے ذی اختیار لوگوں کی طرف رجوع لاتے تو ضرور اُن سے اُ س کی حقیقت جان لیتے یہ جو بعد میں کاوش کرتے ہیں اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی تو ضرور تم شیطان کے پیچھے لگ جاتے مگر تھوڑے'),
(577, 4, 84, 'تو اے محبوب اللہ کی راہ میں لڑو تم تکلیف نہ دیئے جاؤ گے مگر اپنے دم کی اور مسلمانوں کو آمادہ کرو قریب ہے کہ اللہ کافروں کی سختی روک دے اور اللہ کی آنچ (جنگی طاقت) سب سے سخت تر ہے اور اس کا عذاب سب سے کڑا (زبردست)'),
(578, 4, 85, 'جو اچھی سفارش کرے اس کے لئے اس میں سے حصہ ہے اور جو بری سفارش کرے اس کے لئے اس میں سے حصہ ہے اور اللہ ہر چیز پر قادر ہے،'),
(579, 4, 86, 'اور جب تمہیں کوئی کسی لفظ سے سلام کرے تو اس سے بہتر لفظ جواب میں کہو یا یا وہی کہہ دو، بیشک اللہ ہر چیز پر حساب لینے والا ہے'),
(580, 4, 87, 'ا لله کہ اس کے سوا کسی کی بندگی نہیں اور وہ ضرور تمہیں اکٹھا کرے گا قیامت کے دن جس میں کچھ شک نہیں اور اللہ سے زیادہ کس کی بات سچی'),
(581, 4, 88, 'تو تمہیں کیا ہوا کہ منافقوں کے بارے میں دو فریق ہوگئے اور اللہ نے انہیں اوندھا کردیا ان کے کوتکوں (کرتوتوں) کے سبب کیا یہ چاہتے ہیں کہ اسے راہ دکھاؤ جسے اللہ نے گمراہ کیا اور جسے اللہ گمراہ کرے تو ہرگز اس کے لئے راہ نہ پائے گا،'),
(582, 4, 89, 'وہ تو یہ چاہتے ہیں کہ کہیں تم بھی کافر ہوجاؤ جیسے وہ کافر ہوئے تو تم سب ایک سے ہو جاؤ ان میں کسی کو اپنا دوست نہ بناؤ جب تک اللہ کی راہ میں گھر بار نہ چھوڑیں پھر اگر وہ منہ پھیریں تو انہیں پکڑو اور جہاں پاؤ قتل کرو، اور ان میں کسی کو نہ دوست ٹھہراؤ نہ مددگار'),
(583, 4, 90, 'مگر جو ایسی قوم سے علاقے رکھتے ہیں کہ تم میں ان میں معاہدہ ہے یا تمہارے پاس یوں آئے کہ ان کے دلوں میں سکت نہ رہی کہ تم سے لڑیں یا اپنی قوم سے لڑیں اور اللہ چاہتا تو ضرور انہیں تم پر قابو دیتا تو وہ بے شک تم سے لڑتے پھر اگر وہ تم سے کنارہ کریں اور نہ لڑیں اور صلح کا پیام ڈالیں تو اللہ نے تمہیں ان پر کوئی راہ نہ رکھی'),
(584, 4, 91, 'اب کچھ اور تم ایسے پاؤ گے جو یہ چاہتے ہیں کہ تم سے بھی امان میں رہیں اور اپنی قوم سے بھی امان میں رہیں جب کبھی انکی قوم انہیں فساد کی طرف پھیرے تو اس پر اوندھے گرتے ہیں پھر اگر وہ تم سے کنارہ نہ کریں اور صلح کی گردن نہ ڈالیں اور اپنے ہاتھ سے نہ روکیں تو انہیں پکڑو اور جہاں پاؤ قتل کرو، اور یہ ہیں جن پر ہم نے تمہیں صریح اختیار دیا'),
(585, 4, 92, 'اور مسلمانوں کو نہیں پہنچتا کہ مسلمان کا خون کرے مگر ہاتھ بہک کر اور جو کسی مسلمان کو نادانستہ قتل کرے تو اس پر ایک مملوک مسلمان کا آزاد کرنا ہے اور خون بہا کر مقتول کے لوگوں کو سپرد کی جائے مگر یہ کہ وہ معاف کردیں پھر اگر وہ اس قوم سے ہو جو تمہاری دشمن ہے اور خود مسلمان ہے تو صرف ایک مملوک مسلمان کا آزاد کرنا اور اگر وہ اس قوم میں ہو کہ تم میں ان میں معاہدہ ہے تو اس کے لوگوں کو خوں بہا سپرد کیا جائے اور ایک مسلمان مملوک آزاد کرنا تو جس کا ہاتھ نہ پہنچے وہ لگاتار دو مہینے کے روزے یہ اللہ کے یہاں اس کی توبہ ہے اور اللہ جاننے والا حکمت والا ہے،'),
(586, 4, 93, 'اور جو کوئی مسلمان کو جان بوجھ کر قتل کرے تو اس کا بدلہ جہنم ہے کہ مدتوں اس میں رہے اور اللہ نے اس پر غضب کیا اور اس پر لعنت کی اور اس کے لئے تیار رکھا بڑا عذاب،'),
(587, 4, 94, 'اے ایمان والو! جب تم جہاد کو چلو تو تحقیق کرلو اور جو تمہیں سلام کرے اس سے یہ نہ کہو کہ تو مسلمان نہیں تم جیتی دنیا کا اسباب چاہتے ہو تو اللہ کے پاس بہتری غنیمتیں ہیں پہلے تم بھی ایسے ہی تھے پھر اللہ نے تم پر احسان کیا تو تم پر تحقیق کرنا لازم ہے بیشک اللہ کو تمہارے کاموں کی خبر ہے،'),
(588, 4, 95, 'برابر نہیں وہ مسلمان کہ بے عذر جہاد سے بیٹھ رہیں اور وہ کہ راہ خدا میں اپنے مالوں اور جانوں سے جہاد کرتے ہیں اللہ نے اپنے مالوں اور جانوں کے ساتھ جہاد کرنے والوں کا درجہ بیٹھنے والوں سے بڑا کیا اور اللہ نے سب سےبھلائی کا وعدہ فرمایا اور اللہ نے جہاد والوں کو بیٹھنے والوں پر بڑے ثواب سے فضیلت دی ہے،'),
(589, 4, 96, 'اُس کی طرف سے درجے اور بخشش اور رحمت اور اللہ بخشنے والا مہربان ہے،'),
(590, 4, 97, 'وہ لوگ جنکی جان فرشتے نکالتے ہیں اس حال میں کہ وہ اپنے اوپر ظلم کرتے تھے انسے فرشتے کہتے ہیں تم کاہے میں تھے کہتے ہیں کہ ہم زمین میں کمزور تھے کہتے ہیں کیا اللہ کی زمین کشادہ نہ تھی کہ تم اسمیں ہجرت کرتے، تو ایسوں کا ٹھکانا جہنم ہے، اور بہت بری جگہ پلٹنے کی'),
(591, 4, 98, 'مگر وہ جو دبالیے گئے مرد اور عورتیں اور بچے جنہیں نہ کوئی تدبیر بن پڑے نہ راستہ جانیں،'),
(592, 4, 99, 'تو قریب ہے اللہ ایسوں کو معاف فرمائے اور اللہ معاف فرمانے والا بخشنے والا ہے،'),
(593, 4, 100, 'اور جو اللہ کی راہ میں گھر بار چھوڑ کر نکلے گا وہ زمین میں بہت جگہ اور گنجائش پائے گا، اور جو اپنے گھر سے نکلا اللہ و رسول کی طرف ہجرت کرتا پھر اسے موت نے آلیا تو اس کا ثواب اللہ کے ذمہ پر ہوگیا اور اللہ بخشنے والا مہربان ہے'),
(594, 4, 101, 'اور جب تم زمین میں سفر کرو تو تم پر گناہ نہیں کہ بعض نمازیں قصر سے پڑھو اگر تمہیں اندیشہ ہو کہ کافر تمہیں ایذا دیں گے بیشک کفار تمہارے کھلے دشمن ہیں،'),
(595, 4, 102, 'اور اے محبوب! جب تم ان میں تشریف فرما ہو پھر نماز میں ان کی امامت کرو تو چاہئے کہ ان میں ایک جماعت تمہارے ساتھ ہو اور وہ اپنے ہتھیار لیے رہیں پھر جب وہ سجدہ کرلیں تو ہٹ کر تم سے پیچھے ہوجائیں اور اب دوسری جماعت آئے جو اس وقت تک نماز میں شریک نہ تھی اب وہ تمہارے مقتدی ہوں اور چاہئے کہ اپنی پناہ اور اپنے ہتھیار لیے رہیں کافروں کی تمنا ہے کہ کہیں تم اپنے ہتھیاروں اور اپنے اسباب سے غافل ہو جاؤ تو ایک دفعہ تم پر جھک پڑیں اور تم پر مضائقہ نہیں اگر تمہیں مینھ کے سبب تکلیف ہو یا بیمار ہو کہ اپنے ہتھیار کھول رکھو اور اپنی پناہ لیے رہو بیشک اللہ نے کافروں کے لئے خواری کا عذاب تیار کر رکھا ہے،'),
(596, 4, 103, 'پھر جب تم نماز پڑھ چکو تو اللہ کی یاد کرو کھڑے اور بیٹھے اور کروٹوں پر لیٹے پھر جب مطمئن ہو جاؤ تو حسب دستور نماز قائم کرو بیشک نماز مسلمانوں پر وقت باندھا ہوا فرض ہے'),
(597, 4, 104, 'اور کافروں کی تلاش میں سستی نہ کرو اگر تمہیں دکھ پہنچتا ہے تو انہیں بھی دکھ پہنچتا ہے جیسا تمہیں پہنچتا ہے، اورتم اللہ سے وہ امید رکھتےہو جو وہ نہیں رکھتے، ور اللہ جاننے والا حکمت والا ہے'),
(598, 4, 105, 'اے محبوب! بیشک ہم نے تمہاری طرف سچی کتاب اتاری کہ تم لوگوں میں فیصلہ کرو جس طرح تمہیں اللہ دکھائے اور دغا والوں کی طرف سے نہ جھگڑو'),
(599, 4, 106, 'اور اللہ سے معافی چاہو بیشک اللہ بخشنے والا مہربان ہے،'),
(600, 4, 107, 'اور ان کی طرف سے نہ جھگڑو جو اپنی جانوں کو خیانت میں ڈالتے ہیں بیشک اللہ نہیں چاہتا کسی بڑے دغا باز گنہگار کو'),
(601, 4, 108, 'آدمیوں سے چھپتے ہیں اور اللہ نہیں چھپتے اور ا لله ان کے پاس ہے جب دل میں وہ بات تجویز کرتے ہیں جو اللہ کو ناپسند ہے اور اللہ ان کے کاموں کو گھیرے ہوئے ہے،'),
(602, 4, 109, 'سنتے ہو یہ جو تم ہو دنیا کی زندگی میں تو ان کی طرف سے جھگڑے تو ان کی طرف سے کون جھگڑے گا اللہ سے قیامت کے دن یا کون ان کا وکیل ہوگا،'),
(603, 4, 110, 'اور جو کوئی برائی یا اپنی جان پر ظلم کرے پھر اللہ سے بخشش چاہے تو اللہ کو بخشنے والا مہربان پائے گا،'),
(604, 4, 111, 'اور جو گناہ کمائے تو ا س کی کمائی اسی کی جان پر پڑے اور اللہ علم و حکمت والا ہے'),
(605, 4, 112, 'اور جو کوئی خطا یا گناہ کمائے پھر اسے کسی بے گناہ پر تھوپ دے اس نے ضرور بہتان اور کھلا گناہ اٹھایا،'),
(606, 4, 113, 'اور اے محبوب! اگر اللہ کا فضل و رحمت تم پر نہ ہوتا تو ان میں کے کچھ لوگ یہ چاہتے کہ تمہیں دھوکا دے دیں اور وہ اپنے ہی آپ کو بہکا رہے ہیں اور تمہارا کچھ نہ بگاڑیں گے اور اللہ نے تم پر کتاب اور حکمت اتاری اور تمہیں سکھا دیا جو کچھ تم نہ جانتے تھے اور اللہ کا تم پر بڑا فضل ہے'),
(607, 4, 114, 'ان کے اکثر مشوروں میں کچھ بھلائی نہیں مگر جو حکم دے خیرات یا اچھی بات یا لوگوں میں صلح کرنے کا اور جو اللہ کی رضا چاہنے کو ایسا کرے اسے عنقریب ہم بڑا ثواب دیں گے،'),
(608, 4, 115, 'اور جو رسول کا خلاف کرے بعد اس کے کہ حق راستہ اس پر کھل چکا اور مسلمانوں کی راہ سے جدا راہ چلے ہم اُسے اُس کے حال پر چھوڑ دیں گے اور اسے دوزخ میں داخل کریں گے اور کیا ہی بری جگہ پلٹنے کی'),
(609, 4, 116, 'اللہ اسے نہیں بخشتا کہ اس کا کوئی شریک ٹھہرایا جائے اور اس سے نیچے جو کچھ ہے جسے چاہے معاف فرما دیتا ہے اور جو اللہ کا شریک ٹھہرائے وہ دور کی گمراہی میں پڑا،'),
(610, 4, 117, 'یہ شرک والے اللہ سوا نہیں پوجتے مگر کچھ عورتوں کو اور نہیں پوجتے مگر سرکش شیطان کو'),
(611, 4, 118, 'جس پر اللہ نے لعنت کی اور بولا قسم ہے میں ضرور تیرے بندوں میں سے کچھ ٹھہرایا ہوا حصہ لوں گا'),
(612, 4, 119, 'قسم ہے میں ضرور بہکادوں گا اور ضرور انہیں آرزوئیں دلاؤں گا اور ضرور انہیں کہوں گا کہ وہ چوپایوں کے کان چیریں گے اور ضرور انہیں کہوں گا کہ وہ اللہ کی پیدا کی ہوئی چیزیں بدل دیں گے، اور جو اللہ کو چھوڑ کر شیطان کو دوست بنائے وہ صریح ٹوٹے میں پڑا'),
(613, 4, 120, 'شیطان انہیں وعدے دیتا ہے اور آرزوئیں دلاتا ہے اور شیطان انہیں وعدے نہیں دیتا مگر فریب کے'),
(614, 4, 121, 'اُن کا ٹھکانا دوزخ ہے اس سے بچنے کی جگہ نہ پائیں گے،'),
(615, 4, 122, 'اور جو ایمان لائے اور اچھے کام کیے کچھ دیر جاتی ہے کہ ہم انہیں باغوں میں لے جائیں گے جن کے نیچے نہریں بہیں ہمیشہ ہمیشہ ان میں رہیں اللہ کا سچا وعدہ اور اللہ سے زیادہ کس کی بات سچی،'),
(616, 4, 123, 'کام نہ کچھ تمہارے خیالوں پر ہے اور نہ کتاب والوں کی ہوس پر جو برائی کرے گا اس کا بدلہ پائے گا اور اللہ کے سوا نہ کوئی اپنا حمایتی پائے گا نہ مددگار'),
(617, 4, 124, 'اور جو کچھ بھلے کام کرے گا مرد ہو یا عورت اور ہو مسلمان تو وہ جنت میں داخل کیے جائیں گے اور انہیں تِل بھر نقصان نہ دیا جائے گا'),
(618, 4, 125, 'اور اس سے بہتر کس کا دین جس نے اپنا منہ اللہ کے لئے جھکا دیا اور وہ نیکی والا ہے اور ابراہیم کے دین پر جو ہر باطل سے جدا تھا اور اللہ نے ابراہیم کو اپنا گہرا دوست بنایا'),
(619, 4, 126, 'اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں، اور ہر چیز پر اللہ کا قابو ہے'),
(620, 4, 127, 'اور تم سے عورتوں کے بارے میں فتویٰ پوچھتے ہیں تم فرمادو کہ اللہ تمہیں ان کا فتویٰ دیتا ہے اور وہ جو تم پر قرآن میں پڑھا جاتا ہے ان یتیم لڑکیوں کے بارے میں تم انہیں نہیں دیتے جو ان کا مقرر ہے اور انہیں نکاح میں بھی لانے سے منہ پھیرتے ہو اور کمزور بچوں کے بارے میں اور یہ کہ یتیموں کے حق میں انصاف پر قائم رہو اور تم جو بھلائی کرو تو اللہ کو اس کی خبر ہے،'),
(621, 4, 128, 'اور اگر کوئی عورت اپنے شوہر کی زیادتی یا بے رغبتی کا اندیشہ کرے تو ان پر گناہ نہیں کہ آپس میں صلح کرلیں اور صلح خوب ہے اور دل لالچ کے پھندے میں ہیں اور اگر تم نیکی اور پرہیزگاری کرو تو اللہ کو تمہارے کاموں کی خبر ہے'),
(622, 4, 129, 'اور تم سے ہرگز نہ ہوسکے گا کہ عورتوں کو برابر رکھو اور چاہے کتنی ہی حرص کرو تو یہ تو نہ ہو کہ ایک طرف پورا جھک جاؤ کہ دوسری کو ادھر میں لٹکتی چھوڑ دو اور اگر تم نیکی اور پرہیزگاری کرو تو بیشک اللہ بخشنے والا مہربان ہے،'),
(623, 4, 130, 'اور اگر وہ دونوں جدا ہوجائیں تو اللہ اپنی کشائش سے تم میں ہر ایک کو دوسرے سے بے نیاز کردے گا اور اللہ کشائش والا حکمت والا ہے،'),
(624, 4, 131, 'اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں، اور بیشک تاکید فرمادی ہے ہم نے ان سے جو تم سے پہلے کتاب دیئے گئے اور تم کو کہ اللہ سے ڈرتے رہو اور اگر کفر کرو تو بیشک اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں اور اللہ بے نیاز ہے سب خوبیوں سراہا،'),
(625, 4, 132, 'اور اللہ کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں، اور اللہ کافی ہے کارساز،'),
(626, 4, 133, 'اے لوگوں وہ چاہے تو تمہیں لے جائے اور اوروں کو لے آئے اور اللہ کو اس کی قدرت ہے،'),
(627, 4, 134, 'جو دنیا کا انعام چاہے تو اللہ ہی کے پاس دنیا و آخرت دونوں کا انعام ہے اور اللہ ہی سنتا دیکھتا ہے،'),
(628, 4, 135, 'اے ایمان والو! انصاف پر خوب قائم ہوجاؤ اللہ کے لئے گواہی دیتے چاہے اس میں تمہارا اپنا نقصان ہو یا ماں باپ کا یا رشتہ داروں کا جس پر گواہی دو وہ غنی ہو یا فقیر ہو بہرحال اللہ کو اس کا سب سے زیادہ اختیار ہے تو خواہش کے پیچھے نہ جاؤ کہ حق سے الگ پڑو اگر تم ہیر پھیر کرو یا منہ پھیرو تو اللہ کو تمہارے کاموں کی خبر ہے'),
(629, 4, 136, 'اے ایمان والو ایمان رکھو اللہ اور اللہ کے رسول پر اور اس کتاب پر جو اپنے ان رسول پر اتاری اور اس کتاب پر جو پہلے اتاردی اور جو نہ مانے اللہ اور اس کے فرشتوں اور کتابوں اور رسولوں اور قیامت کو تو وہ ضرور دور کی گمراہی میں پڑا،'),
(630, 4, 137, 'بیشک وہ لوگ جو ایمان لائے پھر کافر ہوئے پھر ایمان لائے پھر کافر ہوئے پھر اور کفر میں بڑھے اللہ ہرگز نہ انہیں بخشے نہ انہیں راہ دکھائے،'),
(631, 4, 138, 'خوشخبری دو منافقوں کو کہ ان کے لئے دردناک عذاب ہے'),
(632, 4, 139, 'وہ جو مسلمانوں کو چھوڑ کر کافروں کو دوست بناتے ہیں کیا ان کے پاس عزت ڈھونڈتے ہیں تو عزت تو ساری اللہ کے لیے ہے'),
(633, 4, 140, 'اور بیشک اللہ تم پر کتاب میں اتار چکا کہ جب تم اللہ کی آیتوں کو سنو کہ ان کا انکار کیا جاتا اور ان کی ہنسی بنائی جاتی ہے تو ان لوگوں کے ساتھ نہ بیٹھو جب تک وہ اور بات میں مشغول نہ ہوں ورنہ تم بھی انہیں جیسے ہو بیشک اللہ منافقوں اور کافروں سب کو جہنم میں اکٹھا کرے گا'),
(634, 4, 141, 'وہ جو تمہاری حالت تکا کرتے ہیں تو اگر اللہ کی طرف سے تم کو فتح ملے کہیں کیا ہم تمہارے ساتھ نہ تھے اور اگر کافروں کا حصہ ہو تو ان سے کہیں کیا ہمیں تم پر قابو نہ تھا اور ہم نے تمہیں مسلمانوں سے بچایا تو اللہ تم سب میں قیامت کے دن فیصلہ کردے گا اور اللہ کافروں کو مسلمان پر کوئی راہ نہ دے گا'),
(635, 4, 142, 'بیشک منافق لوگ اپنے گمان میں اللہ کو فریب دیا چاہتے ہیں اور وہی انہیں غافل کرکے مارے گا اور جب نماز کو کھڑے ہوں تو ہارے جی سے لوگوں کو دکھاوا کرتے ہیں اور اللہ کو یاد نہیں کرتے مگر تھوڑا'),
(636, 4, 143, 'بیچ میں ڈگمگا رہے ہیں نہ اِدھر کے نہ اُدھر کے اور جسے اللہ گمراہ کرے تو اس کے لئے کوئی راہ نہ پائے گا،'),
(637, 4, 144, 'اے ایمان والو! کافروں کو دوست نہ بناؤ مسلمانوں کے سوا کیا یہ چاہتے ہو کہ اپنے اوپر اللہ کے لئے صریح حجت کرلو'),
(638, 4, 145, 'بیشک منافق دوزخ کے سب سے نیچے طبقہ میں ہیں اور تو ہرگز ان کا کوئی مددگار نہ پائے گا'),
(639, 4, 146, 'مگر وہ جنہوں نے توبہ کی اور سنورے اور اللہ کی رسی مضبوط تھامی اور اپنا دین خالص اللہ کے لئے کرلیا تو یہ مسلمانوں کے ساتھ ہیں اور عنقریب اللہ مسلمانوں کو بڑا ثواب دے گا،'),
(640, 4, 147, 'اور اللہ تمہیں عذاب دے کر کیا کرے گا اگر تم حق مانو، اور ایمان لاؤ اور اللہ ہے صلہ دینے والا جاننے والا،'),
(641, 4, 148, 'اللہ پسند نہیں کرتا بری بات کا اعلان کرنا مگر مظلوم سے اور اللہ سنتا جانتا ہے،'),
(642, 4, 149, 'اگر تم کوئی بھلائی اعلانیہ کرو یا چھپ کر یا کسی کی برائی سے درگزرو تو بیشک اللہ معاف کرنے والا قدرت والا ہے'),
(643, 4, 150, 'وہ جو اللہ اور اس کے رسولوں کو نہیں مانتے اور چاہتے ہیں کہ اللہ سے اس کے رسولوں کو جدا کردیں اور کہتے ہیں ہم کسی پر ایمان لائے اور کسی کے منکر ہوئے اور چاہتے ہیں کہ ایمان و کفر کے بیچ میں کوئی راہ نکال لیں'),
(644, 4, 151, 'یہی ہیں ٹھیک ٹھیک کافر اور ہم نے کافروں کے لئے ذلت کا عذاب تیار کر رکھا ہے،'),
(645, 4, 152, 'اور وہ جو اللہ اور اس کے سب رسولوں پر ایمان لائے اور ان میں سے کسی پر ایمان میں فرق نہ کیا انہیں عنقریب اللہ ان کے ثواب دے گا اور اللہ بخشنے والا مہربان ہے'),
(646, 4, 153, 'اے محبوب! اہل کتاب تم سے سوال کرتے ہیں کہ ان پر آسمان سے ایک کتاب اتاردو تو وہ تو موسیٰ سے اس سے بھی بڑا سوال کرچکے کہ بولے ہمیں ا لله کو اعلانیہ دکھادو تو انہیں کڑک نے آ لیا ان کے گناہوں پر پھر بچھڑا لے بیٹھے بعداس کے لئے روشن آیتیں ان کے پاس آچکیں تو ہم نے یہ معاف فرمادیا اور ہم نے موسیٰ کو روشن غلبہ دیا'),
(647, 4, 154, 'پھر ہم نے ان پر طور کو اونچا کیا ان سے عہد لینے کو اور ان سے فرمایا کہ دروازے میں سجدہ کرتے داخل ہو اور ان سے فرمایا کہ ہفتہ میں حد سے نہ بڑھو اور ہم نے ان سے گاڑھا عہد لیا'),
(648, 4, 155, 'تو ان کی کیسی بدعہدیوں کے سبب ہم نے ان پر لعنت کی اور اس لئے کہ وہ آیات الٰہی کے منکر ہوئے اور انبیاء کو ناحق شہید کرتے اور ان کے اس کہنے پر کہ ہمارے دلوں پر غلاف ہیں بلکہ اللہ نے ان کے کفر کے سبب ان کے دلوں پر مہر لگادی ہے تو ایمان نہیں لاتے مگر تھوڑے،'),
(649, 4, 156, 'اور اس لئے کہ انہوں نے کفر کیا اور مریم پر بڑا بہتان اٹھایا،'),
(650, 4, 157, 'اور ان کے اس کہنے پر کہ ہم نے مسیح عیسیٰ بن مریم اللہ کے رسول کو شہید کیا اور ہے یہ کہ انہوں نے نہ اسے قتل کیا اور نہ اسے سولی دی بلکہ ان کے لئے ان کی شبیہ کا ایک بنادیا گیا اور وہ جو اس کے بارہ میں اختلاف کررہے ہیں ضرور اس کی طرف سے شبہ میں پڑے ہوئے ہیں انہیں اس کی کچھ بھی خبر نہیں مگر یہی گمان کی پیروی اور بیشک انہوں نے اس کو قتل نہیں کیا'),
(651, 4, 158, 'بلکہ اللہ نے اسے اپنی طرف اٹھالیا اور اللہ غالب حکمت والا ہے،'),
(652, 4, 159, 'کوئی کتابی ایسا نہیں جو اس کی موت سے پہلے اس پر ایمان نہ لائے اور قیامت کے دن وہ ان پر گواہ ہوگا'),
(653, 4, 160, 'تو یہودیوں کے بڑے ظلم کے سبب ہم نے وہ بعض ستھری چیزیں کہ ان کے لئے حلال تھیں ان پر حرام فرمادیں اور اس لئے کہ انہوں نے بہتوں کو اللہ کی راہ سے روکا،'),
(654, 4, 161, 'اور اس لئے کہ وہ سود لیتے حالانکہ وہ اس سے منع کیے گئے تھے اور لوگوں کا مال ناحق کھا جاتے اور ان میں جو کافر ہوئے ہم نے ان کے لئے دردناک عذاب تیار کر رکھا ہے،'),
(655, 4, 162, 'ہاں جو اُن میں علم کے پکے اور ایمان والے ہیں وہ ایمان لاتے ہیں اس پر جو اے محبوب تمہاری طرف اُترا اور جو تم سے پہلے اُترا اور نماز قائم رکھنے والے اور زکوٰة دینے والے اور اللہ اور قیامت پر ایمان لانے والے ایسوں کو عنقریب ہم بڑا ثواب دیں گے،'),
(656, 4, 163, 'بیشک اے محبوب! ہم نے تمہاری طرف وحی بھیجی جیسے دحی نوح اور اس کے بعد پیغمبروں کو بھیجی اور ہم نے ابراہیم اور اسمٰعیل اور اسحاق اور یعقوب اور ان کے بیٹوں اور عیسیٰ اور ایوب اور یونس اور ہارون اور سلیمان کو وحی کی اور ہم نے داؤد کو زبور عطا فرمائی'),
(657, 4, 164, 'اور رسولوں کو جن کا ذکر آگے ہم تم سے فرماچکے اور ان رسولوں کو جن کا ذکر تم سے نہ فرمایا اور اللہ نے موسیٰ سے حقیقتاً کلام فرمایا'),
(658, 4, 165, 'رسول خوشخبری دیتے اور ڈر سناتے کہ رسولوں کے بعد اللہ کے یہاں لوگوں کو کوئی عذر نہ رہے اور اللہ غالب حکمت والا ہے،'),
(659, 4, 166, 'لیکن اے محبوب! اللہ اس کا گواہ ہے جو اس نے تمہاری طرف اتارا وہ اس نے اپنے علم سے اتارا ہے اور فرشتے گواہ ہیں اور اللہ کی گواہی کافی،'),
(660, 4, 167, 'وہ جنہوں نے کفر کیا اور اللہ کی راہ سے روکا بیشک وہ دور کی گمراہی میں پڑے،'),
(661, 4, 168, 'بیشک جنہوں نے کفر کیا اور حد سے بڑھے اللہ ہرگز انہیں نہ بخشے گا اور نہ انہیں کوئی راہ دکھائے،'),
(662, 4, 169, 'مگر جہنم کا راستہ کہ اس میں ہمیشہ ہمیشہ رہیتں گے اور یہ اللہ کو آسان ہے،'),
(663, 4, 170, 'اے لوگو! تمہارے پاس یہ رسول حق کے ساتھ تمہارے رب کی طرف سے تشریف لائے تو ایمان لاؤ اپنے بھلے کو اور اگر تم کفر کرو تو بیشک اللہ ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے، اور اللہ علم و حکمت والا ہے،'),
(664, 4, 171, 'اے کتاب والو! اپنے دین میں زیادتی نہ کرو اور اللہ پر نہ کہو مگر سچ مسیح عیسیٰ مریم کا بیٹا ا لله کا رسول ہی ہے اور اس کا ایک کلمہ کہ مریم کی طرف بھیجا اور اس کے یہاں کی ایک روح تو اللہ اور اس کے رسولوں پر ایمان لاؤ اور تین نہ کہو باز رہو اپنے بھلے کو اللہ تو ایک ہی خدا ہے پاکی اُسے اس سے کہ اس کے کوئی بچہ ہو اسی کا مال ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ کافی کارساز،'),
(665, 4, 172, 'مسیح اللہ کا بندہ بننے سے کچھ نفرت نہیں کرتا اور نہ مقرب فرشتے اور جو اللہ کی بندگی سے نفرت اور تکبر کرے تو کوئی دم جاتا ہے کہ وہ ان سب کو اپنی طرف ہانکے گا'),
(666, 4, 173, 'تو وہ جو ایمان لائے اور اچھے کام کیے ان کی مزدوری انہیں بھرپور دے کر اپنے فضل سے انہیں اور زیادہ دے گا اور وہ جنہوں نے نفرت اور تکبر کیا تھا انہیں دردناک سزا دے گا اور اللہ کے سوا نہ اپنا کوئی حمایتی پائیں گے نہ مددگار،'),
(667, 4, 174, 'اے لوگو! بیشک تمہارے پاس اللہ کی طرف سے واضح دلیل آئی اور ہم نے تمہاری طرف روشن نور اتارا'),
(668, 4, 175, 'تو وہ جو اللہ پر ایمان لائے اور اس کی رسی مضبوط تھامی تو عنقریب اللہ انہیں اپنی رحمت اور اپنے فضل میں داخل کرے گا اور انہیں اپنی طرف سیدھی راہ دکھائے گا،'),
(669, 4, 176, 'اے محبوب! تم سے فتویٰ پوچھتے ہیں تم فرمادو کہ اللہ تمہیں کلالہ میں فتویٰ دیتا ہے، اگر کسی مرد کا انتقال ہو جو بے اولاد ہے اور اس کی ایک بہن ہو تو ترکہ میں اس کی بہن کا آدھا ہے اور مرد اپنی بہن کا وارث ہوگا اگر بہن کی اولاد نہ ہو پھر اگر دو بہنیں ہوں ترکہ میں ان کا دو تہائی اور اگر بھائی بہن ہوں مرد بھی اور عورتیں بھی تو مرد کا حصہ دو عورتوں کے برابر، ا لله تمہارے لئے صاف بیان فرماتا ہے کہ کہیں بہک نہ جاؤ، اور اللہ ہرچیز جانتا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اے ایمان والو! اپنے قول پورے کرو تمہارے لئے حلال ہوئے بے زبان مویشی مگر وہ جو آگے سنایا جائے گا تم کو لیکن شکار حلال نہ سمجھو جب تم احرام میں ہو بیشک اللہ حکم فرماتا ہے جو چاہے،'),
(671, 5, 2, 'اے ایمان والو! حلال نہ ٹھہرالو اللہ کے نشان اور نہ ادب والے مہینے اور نہ حرم کو بھیجی ہوئی قربانیاں اور نہ جن کے گلے میں علامتیں آویزاں اور نہ ان کا مال و آبرو جو عزت والے گھر کا قصد کرکے آئیں اپنے رب کا فضل اوراس کی خوشی چاہتے اور جب احرام سے نکلو تو شکار کرسکتے ہو اور تمہیں کسی قوم کی عداوت کہ انہوں نے تم کو مسجد حرام سے روکا تھا، زیادتی کرنے پر نہ ابھارے اور نیکی اور پرہیزگاری پر ایک دوسرے کی مدد کرو اور گناہ اور زیادتی پر باہم مدد نہ دو اور اللہ سے ڈرتے رہو بیشک اللہ کا عذاب سخت ہے،'),
(672, 5, 3, 'تم پر حرام ہے مُردار اور خون اور سور کا گوشت اور وہ جس کے ذبح میں غیر خدا کا نام پکارا گیا اور جو گلا گھونٹنے سے مرے اور بے دھار کی چیز سے مارا ہوا اور جو گر کر مرا اور جسے کسی جانور نے سینگ مارا اور جسے کوئی درندہ کھا گیا مگر جنہیں تم ذبح کرلو، اور جو کسی تھان پر ذبح کیا گیا اور پانسے ڈال کر بانٹا کرنا یہ گناہ کا کا م ہے، آج تمہارے دین کی طرف سے کافروں کی آس نوٹ گئی تو اُن سے نہ ڈرو اور مجھ سے ڈرو آج میں نے تمہارے لئے دین کامل کردیا اور تم پر اپنی نعمت پوری کردی اور تمہارے لئے اسلام کو دین پسند کیا تو جو بھوک پیاس کی شدت میں ناچار ہو یوں کہ گناہ کی طرف نہ جھکے تو بیشک اللہ بخشنے والا مہربان ہے،'),
(673, 5, 4, 'اے محبوب! تم سے پوچھتے ہیں کہ اُن کے لئے کیا حلال ہوا تم فرمادو کہ حلال کی گئیں تمہارے لئے پاک چیزیں اور جو شکاری جانور تم نے سدھالیے انہیں شکار پر دوڑاتے جو علم تمہیں خدا نے دیا اس سے انہیں سکھاتے تو کھاؤ اس میں سے جو وہ مار کر تمہارے لیے رہنے دیں اور اس پر اللہ کا نام لو اور اللہ سے ڈرتے رہو بیشک اللہ کو حساب کرتے دیر نہیں لگتی،'),
(674, 5, 5, 'آج تمہارے لئے پاک چیزیں حلال ہوئیں، اور کتابیوں کا کھانا تمہارے لیے حلال ہوا، اور تمہارا کھانا ان کے لئے حلال ہے، اور پارسا عورتیں مسلمان اور پارسا عورتیں ان میں سے جن کو تم سے پہلے کتاب ملی جب تم انہیں ان کے مہر دو قید میں لاتے ہوئے نہ مستی نکالتے اور نہ ناآشنا بناتے اور جو مسلمان سے کافر ہو اس کا کیا دھرا سب اکارت گیا اور وہ آخرت میں زیاں کار ہے'),
(675, 5, 6, 'اے ایمان والو جب نماز کو کھڑے ہونا چاہو تو اپنا منہ دھوؤ اور کہنیوں تک ہاتھ اور سروں کا مسح کرو اور گٹوں تک پاؤ ں دھوؤ اور اگر تمہیں نہانے کی حاجت ہو تو خوب ستھرے ہولو اور اگر تم بیمار ہو یا سفر میں ہو یا تم میں سے کوئی قضائے حاجت سے آیا یا تم نے عورتوں سے صحبت کی اور ان صورتوں میں پانی نہ پایا مٹی سے تیمم کرو تو اپنے منہ اور ہاتھوں کا اس سے مسح کرو، اللہ نہیں چاہتا کہ تم پر کچھ تنگی رکھے ہاں یہ چاہتا ہے کہ تمہیں خوب ستھرا کردے اور اپنی نعمت تم پر پوری کردے کہ کہیں تم احسان مانو،'),
(676, 5, 7, 'اور یاد کرو اللہ کا احسان اپنے اوپر اور وہ عہد جو اس نے تم سے لیا جبکہ تم نے کہا ہم نے سنا اور مانا اور اللہ سے ڈرو بیشک اللہ دلوں کی بات جانتا ہے،'),
(677, 5, 8, 'اے ایمان والوں اللہ کے حکم پر خوب قائم ہوجاؤ انصاف کے ساتھ گواہی دیتے اور تم کو کسی قوم کی عداوت اس پر نہ اُبھارے کہ انصاف نہ کرو، انصاف کرو، وہ پرہیزگاری سے زیادہ قریب ہے، اور اللہ سے ڈرو، بیشک اللہ کو تمہارے کامو ں کی خبر ہے،'),
(678, 5, 9, 'ایمان والے نیکو کاروں سے اللہ کا وعدہ ہے کہ ان کے لئے بخشش اور بڑا ثواب ہے،'),
(679, 5, 10, 'اور وہ جنہوں نے کفر کیا اور ہماری آیتیں جھٹلائیں وحی دوزخ والے ہیں'),
(680, 5, 11, 'اے ایمان والو! اللہ کا احسان اپنے اوپر یاد کرو جب ایک قوم نے چاہا کہ تم پر دست درازی کریں تو اس نے ان کے ہاتھ تم پر سے روک دیئے اور اللہ سے ڈرو اور مسلمانوں کو اللہ ہی پر بھروسہ چاہئے،'),
(681, 5, 12, 'اور بیشک اللہ نے بنی اسرائیل سے عہد کیا اور ہم نے ان میں بارہ سردار قائم کیے اور اللہ نے فرمایا بیشک میں تمہارے ساتھ ہوں ضرور اگر تم نماز قائم رکھو اور زکوٰة دو اور میرے سوالوں پر ایمان لاؤ اور ان کی تعظیم کرو اور اللہ کو قرض حسن دو بیشک میں تمہارے گناہ اتار دوں گا اور ضرور تمہیں باغوں میں لے جاؤں گا جن کے نیچے نہریں رواں، پھر اس کے بعد جو تم میں سے کفر کرے وہ ضرور سیدھی راہ سے بہکا'),
(682, 5, 13, 'تو ان کی کیسی بد عہدیوں پر ہم نے انہیں لعنت کی اور ان کے دل سخت کردیئے اللہ کی باتوں کو ان کے ٹھکانوں سے بدلتے ہیں اور بُھلا بیٹھے بڑا حصہ ان نصیحتوں کا جو انہیں دی گئیں اور تم ہمیشہ ان کی ایک نہ ایک دغا پر مطلع ہوتے رہو گے سوا تھوڑوں کے تو انہیں معاف کردو اور ان سے درگزرو بیشک احسان والے ا لله کو محبوب ہیں،'),
(683, 5, 14, 'اور وہ جنہوں نے دعویٰ کیا کہ ہم نصاریٰ ہیں ہم نے ان سے عہد لیا تو وہ بھلا بیٹھے بڑا حصہ ان نصیحتوں کا جو انہیں دی گئیں تو ہم نے ان کے آپس میں قیامت کے دن تک بَیر اور بغض ڈال دیا اور عنقریب اللہ انہیں بتادے گا جو کچھ کرتے تھے'),
(684, 5, 15, 'اے کتاب والو بیشک تمہارے پاس ہمارے یہ رسول تشریف لائے کہ تم پر ظاہر فرماتے ہیں بہت سی وہ چیزیں جو تم نے کتاب میں چھپا ڈالی تھیں اور بہت سی معاف فرماتے ہیں بیشک تمہارے پاس اللہ کی طرف سے ایک نور آیا اور روشن کتاب'),
(685, 5, 16, 'اللہ اس سے ہدایت دیتا ہے اسے جو اللہ کی مرضی پر چلا سلامتی کے ساتھ اور انہیں اندھیریوں سے روشنی کی طرف لے جاتا ہے اپنے حکم سے اور انہیں سیدھی راہ دکھاتا ہے،'),
(686, 5, 17, 'بیشک کافر ہوئے وہ جنہوں نے کہا کہ اللہ مسیح بن مریم ہی ہے تم فرما دو پھر اللہ کا کوئی کیا کرسکتا ہے اگر وہ چاہے کہ ہلاک کردے مسیح بن مریم اور اس کی ماں اور تمام زمین والوں کو اور اللہ ہی کے لیے ہے سلطنت آسمانوں اور زمین اور ان کے درمیان کی جو چاہے پیدا کرتا ہے، اور اللہ سب کچھ کرسکتا ہے،'),
(687, 5, 18, 'اور یہودی اور نصرانی بولے کہ ہم اللہ کے بیٹے اور اس کے پیارے ہیں تم فرما دو پھر تمہیں کیوں تمہارے گناہوں پر عذاب فرماتا ہے بلکہ تم آدمی ہو اس کی مخلوقات سے جسے چاہے بخشتا ہے اور جسے چاہے سزا دیتا ہے، اور اللہ ہی کے لئے ہے سلطنت آسمانوں اور زمین اور اس کے درمیان کی، اور اسی کی طرف پھرنا ہے،'),
(688, 5, 19, 'اے کتاب والو! بیشک تمہارے پاس ہمارے یہ رسول تشریف لائے کہ تم پر ہمارے احکام ظاہر فرماتے ہیں بعد اس کے کہ رسولوں کا آنا مدتوں بند رہا تھا کہ کبھی کہو کہ ہمارے پاس کوئی خوشی اور ڈر سنانے والا نہ آیا تو یہ خوشی اور ڈر سنانے والے تمہارے پاس تشریف لائے ہیں، اور اللہ کو سب قدرت ہے،'),
(689, 5, 20, 'اور جب موسیٰ نے کہا اپنی قوم سے اے میری قوم اللہ کا احسان اپنے اوپر یاد کرو کہ تم میں سے پیغمبر کیے اور تمہیں بادشاہ کیا اور تمہیں وہ دیا جو آج سارے جہان میں کسی کو نہ دیا'),
(690, 5, 21, 'اے قوم اس پاک زمین میں داخل ہو جو اللہ نے تمہارے لیے لکھی ہے اور پیچھے نہ پلٹو کہ نقصان پر پلٹو گے،'),
(691, 5, 22, 'بولے اے موسیٰ اس میں تو بڑے زبردست لوگ ہیں، اور ہم اس میں ہرگز داخل نہ ہوں گے جب تک وہ وہاں سے نکل نہ جائیں ہاں وہ وہاں سے نکل جائیں تو ہم وہاں جائیں،'),
(692, 5, 23, 'دو مرد کہ اللہ سے ڈرنے والوں میں سے تھے ا لله نے انہیں نوازا بولے کہ زبردستی دروازے میں ان پر داخل ہو اگر تم دروازے میں داخل ہوگئے تو تمہارا ہی غلبہ ہے اور اللہ ہی پر بھروسہ کرو اگر تمہیں ایمان ہے،'),
(693, 5, 24, 'بولے اے موسیٰ ہم تو وہاں کبھی نہ جائیں گے جب تک وہ وہاں ہیں تو آپ جائیے اور آپ کا رب تم دونوں لڑو ہم یہاں بیٹھے ہیں،'),
(694, 5, 25, 'موسیٰ نے عرض کی کہ اے رب! میرے مجھے اختیار نہیں مگر اپنا اور اپنے بھائی کا تو تو ہم کو ان بے حکموں سے جدا رکھ'),
(695, 5, 26, 'فرمایا تو وہ زمین ان پر حرام ہے چالیس برس تک بھٹکتے پھریں زمین میں تو تم ان بے حکموں کا افسوس نہ کھاؤ،'),
(696, 5, 27, 'اور انہیں پڑھ کر سناؤ آدم کے دو بیٹوں کی سچی خبر جب دونوں نے ایک ایک نیاز پیش کی تو ایک کی قبول ہوئی اور دوسرے کی نہ قبول ہوئی بولا قسم ہے میں تجھے قتل کردوں گا کہا اللہ اسی سے قبول کرتا ہے، سے ڈر ہے'),
(697, 5, 28, 'بیشک اگر تو اپنا ہاتھ مجھ پر بڑھائے گا کہ مجھے قتل کرے تو میں اپنا ہاتھ تجھ پر نہ بڑھاؤں گا کہ تجھے قتل کروں میں اللہ سے ڈرتا ہوں جو مالک ہے سارے جہاں کا،'),
(698, 5, 29, 'میں تو یہ چاہتا ہوں کہ میرا اور تیرا گناہ دونوں تیرے ہی پلہ پڑے تو تو دوز خی ہوجائے، اور بے انصافوں کی یہی سزا ہے،'),
(699, 5, 30, 'تو اسکے نفس نے اسے بھائی کے قتل کا چاؤ دلایا تو اسے قتل کردیا تو رہ گیا نقصان میں'),
(700, 5, 31, 'تو اللہ نے ایک کوا بھیجا زمین کریدتا کہ اسے دکھائے کیونکر اپنے بھائی کی لاش چھپائے بولا ہائے خرابی میں اس کوے جیسا بھی نہ ہوسکا کہ میں اپنے بھائی کی لاش چھپاتا تو پچتاتا رہ گیا'),
(701, 5, 32, 'اس سبب سے ہم نے بنی اسرائیل پر لکھ دیا کہ جس نے کوئی جان قتل کی بغیر جان کے بدلے یا زمین میں فساد کیے تو گویا اس نے سب لوگوں کو قتل کیا اور جس نے ایک جان کو جِلا لیا اس نے گویا سب لوگوں کو جلالیا، اور بیشک ان کے پاس ہمارے رسول روشن دلیلوں کے ساتھ آئے پھر بیشک ان میں بہت اس کے بعد زمین میں زیادتی کرنے والے ہیں'),
(702, 5, 33, 'وہ کہ اللہ اور اس کے رسول سے لڑتے اور ملک میں فساد کرتے پھرتے ہیں ان کا بدلہ یہی ہے کہ گن گن کر قتل کیے جائیں یا سولی دیے جائیں یا ان کے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کاٹے جائیں یا زمین سے دور کردیے جائیں، یہ دنیا میں ان کی رسوائی ہے، اور آخرت میں ان کے لیے بڑا غداب،'),
(703, 5, 34, 'مگر وہ جنہوں نے توبہ کرلی اس سے پہلے کہ تم ان پر قابو پاؤ تو جان لو کہ اللہ بخشنے والا مہربان ہے،'),
(704, 5, 35, 'اے ایمان والو! اللہ سے ڈرو اور اس کی طرف وسیلہ ڈھونڈو اور اس کی راہ میں جہاد کرو اس امید پر کہ فلاح پاؤ،'),
(705, 5, 36, 'بیشک وہ جو کافر ہوئے جو کچھ زمین میں ہے سب اور اس کی برابر اور اگر ان کی ملک ہو کہ اسے دے کر قیامت کے عذاب سے اپنی جان چھڑائیں تو ان سے نہ لیا جائے گا اور ان کے لئے دکھ کا عذاب ہے'),
(706, 5, 37, 'دوزخ سے نکلنا چاہیں گے اور وہ اس سے نہ نکلیں گے اور ان کو دوامی سزا ہے،'),
(707, 5, 38, 'اور جو مرد یا عورت چور ہو تو ان کا ہاتھ کاٹو ان کے کیے کا بدلہ اللہ کی طرف سے سزا، اور اللہ غالب حکمت والا ہے،'),
(708, 5, 39, 'تو جو اپنے ظلم کے بعد توبہ کرے اور سنور جائے تو اللہ اپنی مہر سے اس پر رجوع فرمائے گا بیشک اللہ بخشنے والا مہربان ہے،'),
(709, 5, 40, 'کیا تجھے معلوم نہیں کہ اللہ کے لئے ہے آسمانوں اور زمین کی بادشاہی، سزا دیتا ہے جسے چاہے اور بخشتا ہے جسے چاہے، اور اللہ سب کچھ کرسکتا ہے،'),
(710, 5, 41, 'اے رسول! تمہیں غمگین نہ کریں وہ جو کفر پڑ دوڑتے ہیں جو کچھ وہ اپنے منہ سے کہتے ہیں ہم ایمان لائے اور ان کے دل مسلمان نہیں اور کچھ یہودی جھوٹ خوب سنتے ہیں اور لوگوں کی خوب سنتے ہیں جو تمہارے پاس حاضر نہ ہوئے اللہ کی باتوں کو ان کے ٹھکانوں کے بعد بدل دیتے ہیں، کہتے ہیں یہ حکم تمہیں ملے تو مانو اور یہ نہ ملے تو بچو اور جسے اللہ گمراہ کرنا چاہے تو ہرگز تو اللہ سے اس کا کچھ بنا نہ سکے گا، وہ ہیں کہ اللہ نے ان کا دل پاک کرنا نہ چاہا، انہیں دنیا میں رسوائی ہے، اور انہیں آخرت میں بڑا عذاب،'),
(711, 5, 42, 'بڑے جھوٹ سننے والے بڑے حرام خور تو اگر تمہارے حضور حاضر ہوں تو ان میں فیصلہ فرماؤ یا ان سے منہ پھیرلو اور اگر تم ان سے منہ پھیرلو گے تو وہ تمہارا کچھ نہ بگاڑیں گے اور اگر ان میں فیصلہ فرماؤ تو انصاف سے فیصلہ کرو، بیشک انصاف والے اللہ کو پسند ہیں،'),
(712, 5, 43, 'اور وہ تم سے کیونکر فیصلہ چاہیں گے، حالانکہ ان کے پاس توریت ہے جس میں اللہ کا حکم موجود ہے بایں ہمہ اسی سے منہ پھیرتے ہیں اور وہ ایمان لانے والے نہیں،'),
(713, 5, 44, 'بیشک ہم نے توریت اتاری اس میں ہدایت اور نور ہے، اس کے مطابق یہود کو حکم دیتے تھے ہمارے فرمانبردار نبی اور عالم اور فقیہہ کہ ان سے کتاب اللہ کی حفاظت چاہی گئی تھی اور وہ اس پر گواہ تھے تو لوگوں سے خوف نہ کرو اور مجھ سے ڈرو اور میری آیتوں کے بدلے ذلیل قیمت نہ لو اور جو اللہ کے اتارے پر حکم نہ کرے وہی لوگ کافر ہیں،'),
(714, 5, 45, 'اور ہم نے توریت میں ان پر واجب کیا کہ جان کے بدلے جان اور آنکھ کے بدلے آنکھ اور ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور زخموں میں بدلہ ہے پھر جو دل کی خوشی سے بدلہ کراوے تو وہ اس کا گناہ اتار دے گا اور جو اللہ کے اتارے پر حکم نہ کرے تو وہی لوگ ظالم ہیں،'),
(715, 5, 46, 'اور ہم ان نبیوں کے پیچھے ان کے نشانِ قدم پر عیسیٰ بن مریم کو لائے تصدیق کرتا ہوا توریت کی جو اس سے پہلے تھی اور ہم نے اسے انجیل عطا کی جس میں ہدایت اور نور ہے اور تصدیق فرماتی ہے توریت کی کہ اس سے پہلی تھی اور ہدایت اور نصیحت پرہیزگاروں کو،'),
(716, 5, 47, 'اور چاہئے کہ انجیل والے حکم کریں اس پر جو اللہ نے اس میں اتارا اور جو اللہ کے اتارے پر حکم نہ کریں تو وہی لوگ فاسق ہیں،'),
(717, 5, 48, 'اور اے محبوب ہم نے تمہاری طرف سچی کتاب اتاری اگلی کتابوں کی تصدیق فرماتی اور ان پر محافظ و گواہ تو ان میں فیصلہ کرو اللہ کے اتارے سے اور اسے سننے والے ان کی خواہشوں کی پیروی نہ کرنا اپنے پاس آیا ہوا حق چھوڑ کر، ہم نے تم سب کے لیے ایک ایک شریعت اور راستہ رکھا اور اللہ چاہتا تو تم سب کو ایک ہی امت کردیتا مگر منظور یہ ہے کہ جو کچھ تمہیں دیا اس میں تمہیں آزمائے تو بھلائیوں کی طرف سبقت چاہو، تم سب کا پھرنا اللہ ہی کی طرف ہے تو وہ تمہیں بتادے گا جس بات میں تم جھگڑتے تھے'),
(718, 5, 49, 'اور یہ کہ اے مسلمان! اللہ کے اتارے پر حکم کر اور ان کی خواہشوں پر نہ چل اور ان سے بچتا رہ کہ کہیں تجھے لغزش نہ دے دیں کسی حکم میں جو تیری طرف اترا پھر اگر وہ منہ پھیریں تو جان لو کہ اللہ ان کے بعض گناہوں کی سزا ان کو پہنچایا چاہتا ہے اور بیشک بہت آدمی بے حکم ہیں،'),
(719, 5, 50, 'تو کیا جاہلیت کا حکم چاہتے ہیں اور اللہ سے بہتر کس کا حکم یقین والوں کے لیے،'),
(720, 5, 51, 'اے ایمان والو! یہود و نصاریٰ کو دوست نہ بناؤ وہ آپس میں ایک دوسرے کے دوست ہیں اور تم میں جو کوئی ان سے دوستی رکھے گا تو وہ انہیں میں سے ہے بیشک اللہ بے انصافوں کو راہ نہیں دیتا'),
(721, 5, 52, 'اب تم انہیں دیکھو گے جن کے دلوں میں آزار ہے کہ یہود و نصاریٰ کی طرف دوڑتے ہیں کہتے ہیں ہم ڈرتے ہیں کہ ہم پر کوئی گردش آجائے تو نزدیک ہے کہ اللہ فتح لائے یا اپنی طرف سے کوئی حکم پھر اس پر جو اپنے دلوں میں چھپایا تھا پچھتائے رہ جائیں'),
(722, 5, 53, 'اور ایمان والے کہتے ہیں کیا یہی ہیں جنہوں نے اللہ کی قسم کھائی تھی اپنے حلف میں پوری کوشش سے کہ وہ تمہارے ساتھ ہیں ان کا کیا دھرا سب اکارت گیا تو رہ گئے نقصان میں'),
(723, 5, 54, 'اے ایمان والو! تم میں جو کوئی اپنے دین سے پھرے گا تو عنقریب اللہ ایسے لوگ لائے گا کہ وہ اللہ کے پیارے اور اللہ ان کا پیارا مسلمانوں پر نرم اور کافروں پر سخت اللہ کی راہ میں لڑیں گے اور کسی ملامت کرنے والے کی ملامت کا اندیشہ نہ کریں گے یہ اللہ کا فضل ہے جسے چاہے دے، اور اللہ وسعت والا علم والا ہے،'),
(724, 5, 55, 'تمہارے دوست نہیں مگر اللہ اور اس کا رسول اور ایمان والے کہ نماز قائم کرتے ہیں اور زکوٰة دیتے ہیں اور اللہ کے حضور جھکے ہوئے ہیں'),
(725, 5, 56, 'اور جو اللہ اور اس کے رسول اور مسلمانوں کو اپنا دوست بنائے تو بیشک اللہ ہی کا گروہ غالب ہے،'),
(726, 5, 57, 'اے ایمان والو! جنہوں نے تمہارے دین کو ہنسی کھیل بنالیا ہے وہ جو تم سے پہلے کتاب دیے گئے اور کافر ان میں کسی کو اپنا دوست نہ بناؤ اور اللہ سے ڈرتے رہو اگر ایمان رکھتے ہو'),
(727, 5, 58, 'اور جب تم نماز کے لئے اذان دو تو اسے ہنسی کھیل بناتے ہیں یہ اس لئے کہ وہ نرے بے عقل لوگ ہیں'),
(728, 5, 59, 'تم فرماؤ اے کتابیوں تمہیں ہمارا کیا برا لگا یہی نہ کہ ہم ایمان لائے اللہ پر اور اس پر جو ہماری طرف اترا اور اس پر جو پہلے اترا اور یہ کہ تم میں اکثر بے حکم ہیں،'),
(729, 5, 60, 'تم فرماؤ کیا میں بتادوں جو اللہ کے یہاں اس سے بدتر درجہ میں ہیں وہ جن پر اللہ نے لعنت کی اور ان پر غضب فرمایا اور ان میں سے کردیے بندر اور سور اور شیطان کے پجاری ان کا ٹھکانا زیادہ برا ہے اور یہ سیدھی راہ سے زیادہ بہکے،'),
(730, 5, 61, 'اور جب تمہارے پاس آئیں ہم مسلمان ہیں اور وہ آتے وقت بھی کافر تھے اور جاتے وقت بھی کافر، اور اللہ خوب جانتا ہے جو چھپا رہے ہیں'),
(731, 5, 62, 'اور ان میں تم بہتوں کو دیکھو گے کہ گناہ اور زیادتی اور حرام خوری پر دوڑتے ہیں بیشک بہت ہی برے کام کرتے ہیں،'),
(732, 5, 63, 'انہیں کیوں نہیں منع کرتے ان کے پادری اور درویش گناہ کی بات کہنے اور حرام کھانے سے، بیشک بہت ہی برے کام کررہے ہیں'),
(733, 5, 64, 'اور یہودی بولے اللہ کا ہاتھ بندھا ہوا ہے ان کے ہاتھ باندھے جائیں اور ان پر اس کہنے سے لعنت ہے بلکہ اس کے ہاتھ کشادہ ہیں عطا فرماتا ہے جیسے چاہے اور اے محبوب! یہ جو تمہاری طرف تمہارے رب کے پاس سے اترا اس سے ان میں بہتوں کو شرارت اور کفر میں ترقی ہوگی اور ان میں ہم نے قیامت تک آپس میں دشمنی اور بیر ڈال دیا جب کبھی لڑائی کی آگ بھڑکاتے ہیں اللہ اسے بجھا دیتا ہے اور زمین میں فساد کے لیے دوڑتے پھرتے ہیں، اور اللہ فسادیوں کو نہیں چاہتا،'),
(734, 5, 65, 'اور اگر کتاب والے ایمان لاتے اور پرہیزگاری کرتے تو ضرور ہم ان کے گناہ اتار دیتے اور ضرور انہیں چین کے باغوں میں لے جاتے'),
(735, 5, 66, 'اور اگر وہ قائم رکھتے توریت اور انجیل اور جو کچھ ان کی طرف ان کے رب کی طرف سے اترا تو انہیں رزق ملتا اوپر سے اور ان کے پاؤں کے نیچے سے ان میں کوئی گروہ اگر اعتدال پر ہے اور ان میں اکثر بہت ہی برے کام کررہے ہیں'),
(736, 5, 67, 'اے رسول پہنچا دو جو کچھ اترا تمہیں تمہارے رب کی طرف سے اور ایسا نہ ہو تو تم نے اس کا کوئی پیام نہ پہنچایا اور اللہ تمہاری نگہبانی کرے گا لوگوں سے بیشک اللہ کافروں کو راہ نہیں دیتا،'),
(737, 5, 68, 'تم فرمادو، اے کتابیو! تم کچھ بھی نہیں ہو جب تک نہ قائم کرو توریت اور انجیل اور جو کچھ تمہاری طرف تمہارے رب کے پاس سے اترا اور بیشک اے محبوب! وہ جو تمہاری طرف تمہارے رب کے پاس سے اترا اس میں بہتوں کو شرارت اور کفر کی اور ترقی ہوگی تو تم کافروں کا کچھ غم نہ کھاؤ،'),
(738, 5, 69, 'بیشک وہ جو اپنے آپ کو مسلمان کہتے ہیں اور اسی طرح یہودی اور ستارہ پرت اور نصرانی ان میں جو کوئی سچے دل سے اللہ اور قیامت پر ایمان لائے اور اچھے کام کرے توان پر نہ کچھ اندیشہ ہے اور نہ کچھ غم،'),
(739, 5, 70, 'بیشک ہم نے بنی اسرائیل سے عہد لیا اور ان کی طرف رسول بھیجے، جب کبھی ان کے پاس کوئی رسول وہ بات لے کر آیا جو ان کے نفس کی خواہش نہ تھی ایک گروہ کو جھٹلایا اور ایک گروہ کو شہید کرتے ہیں'),
(740, 5, 71, 'اور اس گمان میں ہیں کہ کوئی سزا نہ ہوگی تو اندھے اور بہرے ہوگئے پھر اللہ نے ان کی توبہ قبول کی پھر ان میں بہتیرے اندھے اور بہرے ہوگئے اور اللہ ان کے کام دیکھ رہا ہے،'),
(741, 5, 72, 'بیشک کافر ہیں وہ جو کہتے ہیں کہ اللہ وہی مسیح مریم کا بیٹا ہے اور مسیح نے تو یہ کہا تھا، اے بنی اسرائیل اللہ کی بندگی کرو جو میرا رب اور تمہارا رب، بیشک جو اللہ کا شریک ٹھہرائے تو اللہ نے اس پر جنت حرام کردی اور اس کا ٹھکانا دوزخ ہے اور ظالموں کا کوئی مددگار نہیں،'),
(742, 5, 73, 'بیشک کافر ہیں وہ جو کہتے ہیں اللہ تین خداؤں میں کا تیسرا ہے اور خدا تو نہیں مگر ایک خدا اور اگر اپنی بات سے باز نہ آئے تو جو ان میں کافر مریں گے ان کو ضرور دردناک عذاب پہنچے گا،'),
(743, 5, 74, 'تو کیوں نہیں رجوع کرتے اللہ کی طرف اور اس سے بخشش مانگتے، اور اللہ بخشنے والا مہربان،'),
(744, 5, 75, 'مسیح بن مریم نہیں مگر ایک رسول اس سے پہلے بہت رسول ہو گزرے اور اس کی ماں صدیقہ ہے دونوں کھانا کھاتے تھے دیکھو تو ہم کیسی صاف نشانیاں ان کے لئے بیان کرتے ہیں پھر دیکھو وہ کیسے اوندھے جاتے ہیں،'),
(745, 5, 76, 'تم فرماؤ کیا اللہ کے سوا ایسے کو پوجتے ہو جو تمہارے نقصان کا مالک نہ نفع کا اور اللہ ہی سنتا جانتا ہے،'),
(746, 5, 77, 'تم فرماؤ اے کتاب والو! اپنے دین میں ناحق زیادتی نہ کرو اور ایسے لوگوں کی خواہش پر نہ چلو جو پہلے گمراہ ہوچکے اور بہتوں کو گمراہ کیا اور سیدھی راہ سے بہک گئے'),
(747, 5, 78, 'لعنت کیے گئے وہ جنہوں نے کفر کیا بنی اسرائیل میں داؤد اور عیسیٰ بن مریم کی زبان پر یہ بدلہ ان کی نافرمانی اور سرکشی کا،'),
(748, 5, 79, 'جو بری بات کرتے آپس میں ایک دوسرے کو نہ روکتے ضرور بہت ہی برے کام کرتے تھے'),
(749, 5, 80, 'ان میں تم بہت کو دیکھو گے کہ کافروں سے دوستی کرتے ہیں، کیا ہی بری چیز اپنے لیے خود آگے بھیجی یہ کہ اللہ کا ان پر غضب ہوا اور وہ عذاب میں ہمیشہ رہیں گے'),
(750, 5, 81, 'اور اگر وہ ایمان لاتے اللہ اور ان نبی پر اور اس پر جو ان کی طرف اترا تو کافروں سے دوستی نہ کرتے مگر ان میں تو بہتیرے فاسق ہیں،'),
(751, 5, 82, 'ضرور تم مسلمانوں کا سب سے بڑھ کر دشمن یہودیوں اور مشرکوں کو پاؤ گے اور ضرور تم مسلمانوں کی دوستی میں سب سے زیادہ قریب ان کو پاؤ گے جو کہتے تھے ہم نصاریٰ ہیں یہ اس لئے کہ ان میں عالم اور درویش ہیں اور یہ غرور نہیں کرتے -'),
(752, 5, 83, 'اور جب سنتے ہیں وہ جو رسول کی طرف اترا تو ان کی آنکھیں دیکھو کہ آنسوؤں سے ابل رہی ہیں اس لیے کہ وہ حق کو پہچان گئے، کہتے ہیں اے رب ہمارے! ہم ایمان لائے تو ہمیں حق کے گواہوں میں لکھ لے'),
(753, 5, 84, 'اور ہمیں کیا ہوا کہ ہم ایمان نہ لائیں اللہ پر اور اس حق پر کہ ہمارے پاس آیا اور ہم طمع کرتے ہیں کہ ہمیں ہمارا رب نیک لوگوں کے ساتھ داخل کرے'),
(754, 5, 85, 'تو اللہ نے ان کے اس کہنے کے بدلے انہیں باغ دیے جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں گے، یہ بدلہ ہے نیکوں کا'),
(755, 5, 86, 'اور وہ جنہوں کفر کیا اور ہماری آیتیں جھٹلائیں وہ ہیں دوزخ والے،'),
(756, 5, 87, 'اے ایمان والو! حرام نہ ٹھہراؤ وہ ستھری چیزیں کہ اللہ نے تمہارے لیے حلال کیں اور حد سے نہ بڑھو، بیشک حد سے بڑھنے والے اللہ کو ناپسند ہیں،'),
(757, 5, 88, 'اور کھاؤ جو کچھ تمہیں اللہ نے روزی دی حلال پاکیزہ او ر ڈرو اللہ سے جس پر تمہیں ایمان ہے،'),
(758, 5, 89, 'اللہ تمہیں نہیں پکڑتا تمہاری غلط فہمی کی قسموں پر ہاں ان قسموں پر گرفت فرماتے ہے جنہیں تم نے مضبوط کیا تو ایسی قسم کا بدلہ دس مسکینوں کو کھانا دینا اپنے گھر والوں کو جو کھلاتے ہو اس کے اوسط میں سے یا انہیں کپڑے دینا یا ایک بردہ آزاد کرنا تو جو ان میں سے کچھ نہ پائے تو تین دن کے روزے یہ بدلہ ہے تمہاری قسموں کا، جب قسم کھاؤ اور اپنی قسموں کی حفاظت کرو اسی طرح اللہ تم سے اپنی آیتیں بیان فرماتا ہے کہ کہیں تم احسان مانو،'),
(759, 5, 90, 'اے ایمان والو! شراب اور جوا اور بت اور پانسے ناپاک ہی ہیں شیطانی کام تو ان سے بچتے رہنا کہ تم فلاح پاؤ،'),
(760, 5, 91, 'شیطان یہی چاہتا ہے کہ تم میں بَیر اور دشمنی ڈلوا دے شراب اور جوئے میں اور تمہیں اللہ کی یاد اور نماز سے روکے تو کیا تم باز آئے،'),
(761, 5, 92, 'اور حکم مانو اللہ کا اور حکم مانو رسول کا اور ہوشیار رہو، پھر اگر تم پھر جاؤ تو جان لو کہ ہمارے رسول کا ذمہ صرف واضح طور پر حکم پہنچادینا ہے'),
(762, 5, 93, 'جو ایمان لائے اور نیک کام کیے ان پر کچھ گناہ نہیں جو کچھ انہوں نے چکھا جب کہ ڈریں اور ایمان رکھیں اور نیکیاں کریں پھر ڈریں اور ایمان رکھیں پھر ڈریں اور نیک رہیں، اور اللہ نیکوں کو دوست رکھتا ہے'),
(763, 5, 94, 'اے ایمان والوں ضرور اللہ تمہیں آزمائے گا ایسے بعض شکار سے جس تک تمہارا ہاتھ اور نیزے پہنچیں کہ اللہ پہچان کرادے ان کی جو اس سے بن دیکھے ڈرتے ہیں، پھر اس کے بعد جو حد سے بڑھے اس کے لئے دردناک عذاب ہے،'),
(764, 5, 95, 'اے ایمان والو! شکار نہ مارو جب تم احرام میں ہو اور تم میں جو اسے قصداً قتل کرے تو اس کا بدلہ یہ ہے کہ ویسا ہی جانور مویشی سے دے تم میں کہ دو ثقہ آدمی اس کا حکم کریں یہ قربانی ہو کہ کعبہ کو پہنچتی یا کفار ہ دے چند مسکینوں کا کھانا یا اس کے برابر روزے کہ اپنے کام کا وبال چکھے اللہ نے معاف کیا جو ہو گزرا اور جو اب کرے گا اس سے بدلہ لے گا، اور اللہ غالب ہے بدلہ لینے والا،'),
(765, 5, 96, 'حلال ہے تمہارے لیے دریا کا شکار اور اس کا کھانا تمہارے اور مسافروں کے فائدے کو اور تم پرحرام ہے خشکی کا شکار جب تک تم احرام میں ہو اور اللہ سے ڈرو جس کی طرف تمہیں اٹھنا ہے،'),
(766, 5, 97, 'اللہ نے ادب والے گھر کعبہ کو لوگوں کے قیام کا باعث کیا اور حرمت والے مہینہ اور حرم کی قربانی اور گلے میں علامت آویزاں جانوروں کو یہ اس لیے کہ تم یقین کرو کہ اللہ جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں اور یہ کہ اللہ سب کچھ جانتا ہے،'),
(767, 5, 98, 'جان رکھو کہ اللہ کا عذاب سخت ہے اور اللہ بخشنے والا مہربان،'),
(768, 5, 99, 'رسول پر نہیں مگر حکم پہنچانا اور اللہ جانتا ہے جو تم ظاہر کرتے اور جو تم چھپاتے ہو'),
(769, 5, 100, 'تم فرمادو کہ گندہ اور ستھرا برابر نہیں اگرچہ تجھے گندے کی کثرت بھائے، تو اللہ سے ڈرتے رہو اے عقل والو! کہ تم فلاح پاؤ،'),
(770, 5, 101, 'اے ایمان والو! ایسی باتیں نہ پوچھو جو تم پر ظاہر کی جائیں تو تمہیں بری لگیں اور اگر انہیں اس وقت پوچھو گے کہ قرآن اتر رہا ہے تو تم پر ظاہر کردی جائیں گی، اللہ انہیں معاف کرچکا ہے اور اللہ بخشنے والا حلم والا ہے،'),
(771, 5, 102, 'تم سے اگلی ایک قوم نے انہیں پوچھا پھر ان سے منکر ہو بیٹھے،'),
(772, 5, 103, 'اللہ نے مقرر نہیں کیا ہے کان چِرا ہوا اور نہ بجار اور نہ وصیلہ اور نہ حامی ہاں کافر لوگ اللہ پر جھوٹا افترا باندھتے ہیں اور ان میں اکثر نرے بے عقل ہیں'),
(773, 5, 104, 'اور جب ان سے کہا جائے آؤ اس طرف جو اللہ نے اُتارا اور رسول کی طرف کہیں ہمیں وہ بہت ہے جس پر ہم نے اپنے باپ دادا کو پایا، کیا اگرچہ ان کے باپ دادا نہ کچھ جانیں نہ راہ پر ہوں'),
(774, 5, 105, 'اے ایمان والو! تم اپنی فکر رکھو تمہارا کچھ نہ بگاڑے گا جو گمراہ ہوا جب کہ تم راہ پر ہو تم سب کی رجوع اللہ ہی کی طرف ہے پھر وہ تمہیں بتا دے گا جو تم کرتے تھے،'),
(775, 5, 106, 'اے ایمان والوں تمہاری آپس کی گواہی جب تم میں کسی کو موت آئے وصیت کرتے وقت تم میں کے دو معتبر شخص ہیں یا غیروں میں کے دو جب تم ملک میں سفر کو جاؤ پھر تمہیں موت کا حادثہ پہنچے، ان دونوں کو نماز کے بعد روکو وہ اللہ کی قسم کھائیں اگر تمہیں کچھ شک پڑے ہم حلف کے بدلے کچھ مال نہ خریدیں گے اگرچہ قریب کا رشتہ دار ہو اور اللہ کی گواہی نہ چھپائیں گے ایسا کریں تو ہم ضرور گنہگاروں میں ہیں،'),
(776, 5, 107, 'پھر اگر پتہ چلے کہ وہ کسی گناہ کے سزاوار ہوئے تو ان کی جگہ دو اور کھڑے ہوں ان میں سے کہ اس گناہ یعنی جھوٹی گواہی نے ان کا حق لے کر ان کو نقصان پہنچایا جو میت سے زیادہ قریب ہوں تو اللہ کی قسم کھائیں کہ ہماری گواہی زیادہ ٹھیک ہے ان دو کی گواہی سے اور ہم حد سے نہ بڑھے ایسا ہو تو ہم ظالموں میں ہوں،'),
(777, 5, 108, 'یہ قریب تر ہے اس سے کہ گواہی جیسی چاہیے ادا کریں یا ڈریں کہ کچھ قسمیں رد کردی جائیں ان کی قسموں کے بعد اور اللہ سے ڈرو اور حکم سنو، اور اللہ بے حکموں کو راہ نہیں دیتا،'),
(778, 5, 109, 'جس دن اللہ جمع فرمائے گا رسولوں کو پھر فرمائے گا تمہیں کیا جواب ملا عرض کریں گے ہمیں کچھ علم نہیں، بیشک تو ہی ہے سب غیبوں کا جاننے والا'),
(779, 5, 110, 'جب اللہ فرمائے گا اے مریم کے بیٹے عیسیٰ! یاد کر میرا احسان اپنے اوپر اور اپنی ماں پر جب میں نے پاک روح سے تیری مدد کی تو لوگوں سے باتیں کرتا پالنے میں اور پکی عمر ہوکر اور جب میں نے تجھے سکھائی کتاب اور حکمت اور توریت اور انجیل اور جب تو مٹی سے پرند کی سی مورت میرے حکم سے بناتا پھر اس میں پھونک مارتا تو وہ میرے حکم سے اڑنے لگتی اور تو مادر زاد اندھے اور سفید داغ والے کو میرے حکم سے شفا دیتا اور جب تو مُردوں کو میرے حکم سے زندہ نکالتا اور جب میں نے بنی اسرائیل کو تجھ سے روکا جب تو ان کے پاس روشن نشانیاں لے کر آیا تو ان میں کے کافر بولے کہ یہ تو نہیں مگر کھلا جادو،'),
(780, 5, 111, 'اور جب میں نے حواریوں کے دل میں ڈالا کہ مجھ پر اور میرے رسول پر ایمان لاؤ بولے ہم ایمان لائے اور گواہ رہ کہ ہم مسلمان ہیں'),
(781, 5, 112, 'جب حواریوں نے کہا اے عیسیٰ بن مریم! کیا آپ کا رب ایسا کرے گا کہ ہم پر آسمان سے ایک خوان اُتارے کہا اللہ سے ڈرو! اگر ایمان رکھتے ہو'),
(782, 5, 113, 'بولے ہم چاہتے ہیں کہ اس میں سے کھائیں اور ہمارے دل ٹھہریں اور ہم آنکھوں دیکھ لیں کہ آپ نے ہم سے سچ فرمایا اور ہم اس پر گواہ ہوجائیں'),
(783, 5, 114, 'عیسیٰ بن مریم نے عرض کی، اے اللہ! اے رب ہمارے! ہم پر آسمان سے ایک خوان اُتار کہ وہ ہمارے لیے عید ہو ہمارے اگلے پچھلوں کی اور تیری طرف سے نشانی اور ہمیں رزق دے اور تو سب سے بہتر روزی دینے والا ہے،'),
(784, 5, 115, 'اللہ نے فرمایا کہ میں اسے تم پر اُتارتا ہوں، پھر اب جو تم میں کفر کرے گا تو بیشک میں اسے وہ عذاب دوں گا کہ سارے جہان میں کسی پر نہ کروں گا'),
(785, 5, 116, 'اور جب اللہ فرمائے گا اے مریم کے بیٹے عیسیٰ! کیا تو نے لوگوں سے کہہ دیا تھا کہ مجھے اور میری ماں کو دو خدا بنالو اللہ کے سوا عرض کرے گا، پاکی ہے تجھے مجھے روا نہیں کہ وہ بات کہوں جو مجھے نہیں پہنچتی اگر میں نے ایسا کہا ہو تو ضرور تجھے معلوم ہوگا تو جانتا ہے جو میرے جی میں ہے اور میں نہیں جانتا جو تیرے علم میں ہے، بیشک تو ہی ہے سب غیبوں کا خوب جاننے والا'),
(786, 5, 117, 'میں نے تو ان سے نہ کہا مگر وہی جو تو نے مجھے حکم دیا تھا کہ ا لله کو پوجو جو میرا بھی رب اور تمھا ر ا بھی رب اور میں ان پر مطلع تھا جب تک ان میں رہا، پھر جب تو نے مجھے اٹھالیا تو تُو ہی ان پر نگاہ رکھتا تھا، اور ہر چیز تیرے سامنے حاضر ہے'),
(787, 5, 118, 'اگر تو انہیں عذاب کرے تو وہ تیرے بندے ہیں، اور اگر تو انہیں بخش دے تو بیشک تو ہی ہے غالب حکمت والا'),
(788, 5, 119, 'اللہ نے فرمایا کہ یہ ہے وہ دن جس میں سچوں کو ان کا سچ کام آئے گا، ان کے لئے باغ ہیں جن کے نیچے نہریں رواں ہمیشہ ہمیشہ رہیں گے، اللہ ان سے راضی اور وہ اللہ سے راضی، یہ ہے بڑی کامیابی،'),
(789, 5, 120, 'اللہ ہی کے لئے ہے آسمانوں اور زمین اور جو کچھ ان میں ہے سب کی سلطنت، اور وہ ہر چیز پر قادر ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سب خوبیاں اللہ کو جس نے آسمان اور زمین بنائے اور اندھیریاں اور روشنی پیدا کی اس پر کافر لوگ اپنے رب کے برابر ٹھہراتے ہیں'),
(791, 6, 2, 'وہی ہے جس نے تمہیں مٹی سے پیدا کیا پھر ایک میعاد کا حکم رکھا اور ایک مقررہ وعدہ اس کے یہاں ہے پھر تم لوگ شک کرتے ہو،'),
(792, 6, 3, 'اور وہی اللہ ہے آسمانوں اور زمین کا اسے تمہارا چھپا اور ظاہر سب معلوم ہے اور تمہارے کام جانتا ہے،'),
(793, 6, 4, 'اور ان کے پاس کوئی بھی نشانی اپنے رب کی نشانیوں سے نہیں آتی مگر اس سے منہ پھیرلیتے ہیں،'),
(794, 6, 5, 'تو بیشک انہوں نے حق کو جھٹلایا جب ان کے پاس آیا، تو اب انہیں خبر ہوا چاہتی ہے اس چیز کی جس پر ہنس رہے تھے'),
(795, 6, 6, 'کیا انہوں نے نہ د یکھا کہ ہم نے ان سے پہلے کتنی سنگتیں کھپا دیں انہیں ہم نے زمین میں وہ جماؤ دیا جو تم کو نہ دیا اور ان پر موسلا دھار پانی بھیجا اور ان کے نیچے نہریں بہائیں تو انہیں ہم نے ان کے گناہوں کے سبب ہلاک کیا اور ان کے بعد اور سنگت اٹھائی'),
(796, 6, 7, 'اور اگر ہم تم پر کاغذ میں کچھ لکھا ہوا اتارتے کہ وہ اسے اپنے ہاتھوں سے چھوتے جب بھی کافر کہتے کہ یہ نہیں مگر کھلا جادو،'),
(797, 6, 8, 'اور بولے ان پر کوئی فرشتہ کیوں نہ اتارا گیا، اور اگر ہم فرشتہ اتارتے تو کام تمام ہوگیا ہوتا پھر انہیں مہلت نہ دی جاتی'),
(798, 6, 9, 'اور اگر ہم نبی کو فرشتہ کرتے جب بھی اسے مرد ہی بناتے اور ان پر وہی شبہ رکھتے جس میں اب پڑے ہیں،'),
(799, 6, 10, 'اور ضرور اے محبوب تم سے پہلے رسولوں کے ساتھ بھی ٹھٹھا کیا گیا تو وہ جو ان سے ہنستے تھے ان کی ہنسی انہیں کو لے بیٹھی'),
(800, 6, 11, 'تم فرمادو زمین میں سیر کرو پھر دیکھو کہ جھٹلانے والوں کا کیسا انجام ہوا'),
(801, 6, 12, 'تم فرماؤ کس کا ہے جو کچھ آسمانوں اور زمین میں تم فرماؤ اللہ کا ہے اس نے اپنے کرم کے ذمہ پر رحمت لکھ لی ہے بیشک ضرور تمہیں قیامت کے دن جمع کرے گا اس میں کچھ شک نہیں، وہ جنہوں نے اپنی جان نقصان میں ڈالی ایمان نہیں لاتے،'),
(802, 6, 13, 'اور اسی کا ہے جو بستا ہے رات اور دن میں اور وہی ہے سنتا جانتا'),
(803, 6, 14, 'تم فرماؤ کیا اللہ کے سوا کسی اور کو والی بناؤں وہ اللہ جس نے آسمان اور زمین پیدا کیے اور وہ کھلاتا ہے اور کھانے سے پاک ہے تم فرماؤ مجھے حکم ہوا ہے کہ سب سے پہلے گردن رکھوں اور ہرگز شرک والوں میں سے نہ ہونا،'),
(804, 6, 15, 'تم فرماؤ اگر میں اپنے رب کی نافرمانی کروں تو مجھے بڑے دن کے عذاب کا ڈر ہے،'),
(805, 6, 16, 'اس دن جس سے عذاب پھیر دیا جائے ضرور اس پر اللہ کی مہر ہوئی، اور یہی کھلی کامیابی ہے،'),
(806, 6, 17, 'اور اگر تجھے اللہ کوئی برائی پہنچائے تو اس کے سوا اس کا کوئی دور کرنے والا نہیں، اور اگر تجھے بھلائی پہنچائے تو وہ سب کچھ کرسکتا ہے'),
(807, 6, 18, 'اور وہی غالب ہے اپنے بندوں پر، اور وہی ہے حکمت والا خبردار،'),
(808, 6, 19, 'تم فرماؤ سب سے بڑی گواہی کس کی تم فرماؤ کہ اللہ گواہ ہے مجھ میں اور تم میں اور میر ی طر ف اس قر آ ن کی وحی ہوئی ہے کہ میں اس سے تمھیں ڈراؤ ں اورجن جن کو پہنچے تو کیا تم یہ گواہی دیتے ہو کہ اللہ کے ساتھ اور خدا ہیں، تم فرماؤ کہ میں یہ گواہی نہیں دیتا تم فرماؤ کہ وہ تو ایک ہی معبود ہے اور میں بیزار ہوں ان سے جن کو تم شریک ٹھہراتے ہو'),
(809, 6, 20, 'جن کو ہم نے کتاب دی اس نبی کو پہچانتے ہیں جیسا اپنے بیٹے کو پہچانتے ہیں جنہوں نے اپنی جان نقصان میں ڈالی وہ ایمان نہیں لاتے،'),
(810, 6, 21, 'اور اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے یا اس کی آیتیں جھٹلائے، بیشک ظالم فلاح نہ پائیں گے،'),
(811, 6, 22, 'اور جس دن ہم سب کو اٹھائیں گے پھر مشرکوں سے فرمائیں گے کہاں ہیں تمہارے وہ شریک جن کا تم دعویٰ کرتے تھے،'),
(812, 6, 23, 'پھر ان کی کچھ بناوٹ نہ رہی مگر یہ کہ بولے ہمیں اپنے رب اللہ کی قسم کہ ہم مشرک نہ تھے،'),
(813, 6, 24, 'دیکھو کیسا جھوٹ باندھا خود اپنے اوپر اور گم گئیں ان سے جو باتیں بناتے تھے،'),
(814, 6, 25, 'اور ان میں کوئی وہ ہے جو تمہاری طرف کان لگاتا ہے اور ہم نے ان کے دلوں پر غلاف کردیے ہیں کہ اسے نہ سمجھیں اور ان کے کانٹ میں ٹینٹ (روئی) اور اگر ساری نشانیاں دیکھیں تو ان پر ایمان نہ لائیں گے یہاں تک کہ جب تمہارے حضور تم سے جھگڑتے حاضر ہوں تو کافر کہیں یہ تو نہیں مگر اگلوں کی داستانیں'),
(815, 6, 26, 'اور وہ اس سے روکتے اور اس سے دور بھاگتے ہیں اور بلاک نہیں کرتے مگر اپنی جانیں اور انہیں شعور نہیں،'),
(816, 6, 27, 'اور کبھی تم دیکھو جب وہ آگ پر کھڑے کئے جائیں گے تو کہیں گے کاش کسی طرح ہم واپس بھیجے جائیں اور اپنے رب کی آیتیں نہ جھٹلائیں اور مسلمان ہوجائیں،'),
(817, 6, 28, 'بلکہ ان پر کھل گیا جو پہلے چھپاتے تھے اور اگر واپس بھیجے جائیں تو پھر وہی کریں جس سے منع کیے گئے تھے اور بیشک وہ ضرور جھوٹے ہیں،'),
(818, 6, 29, 'اور بولے وہ تو یہی ہماری دنیا کی زندگی ہے اور ہمیں اٹھنا نہیں'),
(819, 6, 30, 'اور کبھی تم دیکھو جب اپنے رب کے حضور کھڑے کیے جائیں گے، فرمائے گا کیا یہ حق نہیں کہیں گے کیوں نہیں، ہمیں اپنے رب کی قسم، فرمائے گا تو اب عذاب چکھو بدلہ اپنے کفر کا،'),
(820, 6, 31, 'بیشک ہار میں رہے وہ جنہوں نے اپنے رب سے ملنے کا انکار کیا، یہاں تک کہ جب ان پر قیامت اچانک آگئی بولے ہائے افسوس ہمارا اس پر کہ اس کے ماننے میں ہم نے تقصیر کی، اور وہ اپنے بوجھ اپنی پیٹھ پر لادے ہوئے ہیں ارے کتنا برُا بوجھ اٹھائے ہوئے ہیں'),
(821, 6, 32, 'اور دنیا کی زندگی نہیں مگر کھیل کود اور بیشک پچھلا گھر بھلا ان کے لئے جو ڈرتے ہیں تو کیا تمہیں سمجھ نہیں،'),
(822, 6, 33, 'ہمیں معلوم ہے کہ تمہیں رنج دیتی ہے وہ بات جو یہ کہہ رہے ہیں تو وہ تمہیں نہیں جھٹلاتے بلکہ ظالم اللہ کی آیتوں سے انکار کرتے ہیں'),
(823, 6, 34, 'اور تم سے پہلے رسول جھٹلائے گئے تو انہوں نے صبر کیا اس جھٹلانے اور ایذائیں پانے پر یہاں تک کہ انہیں ہماری مدد آئی اور اللہ کی باتیں بدلنے والا کوئی نہیں اور تمہارے پاس رسولوں کی خبریں آ ہی چکیں ہیں'),
(824, 6, 35, 'اور اگر ان کا منہ پھیرنا تم پر شاق گزرا ہے تو اگر تم سے ہوسکے تو زمین میں کوئی سرنگ تلاش کرلو یا آسمان میں زینہ پھر ان کے لیے نشانی لے آؤ اور اللہ چاہتا تو انہیں ہدایت پر اکٹھا کردیتا تو اے سننے والے تو ہرگز نادان نہ بن،'),
(825, 6, 36, 'مانتے تو وہی ہیں جو سنتے ہیں اور ان مردہ دلوں کو اللہ اٹھائے گا پھر اس کی طرف ہانکے جائیں گے'),
(826, 6, 37, 'اور بولے ان پر کوئی نشانی کیوں نہ اتری ان کے رب کی طرف سے تم فرماؤ کہ اللہ قادر ہے کہ کوئی نشانی اتارے لیکن ان میں بہت نرے (بالکل) جاہل ہیں'),
(827, 6, 38, 'اور نہیں کوئی زمین میں چلنے والا اور نہ کوئی پرند کہ اپنے پروں پر اڑتا ہے مگر تم جیسی اُمتیں ہم نے اس کتاب میں کچھ اٹھا نہ رکھا پھر اپنے رب کی طرف اٹھائے جائیں گے'),
(828, 6, 39, 'اور جنہوں نے ہماری آیتیں جھٹلائیں بہرے اور گونگے ہیں اندھیروں میں اللہ جسے چاہے گمراہ کرے اور جسے چاہے سیدھے راستہ ڈال دے'),
(829, 6, 40, 'تم فرماؤ بھلا بتاؤ تو اگر تم پر اللہ کا عذاب آئے یا قیامت قائم ہو کیا اللہ کے سوا کسی اور کو پکارو گے اگر سچے ہو'),
(830, 6, 41, 'بلکہ اسی کو پکارو گے تو وہ اگر چاہے جس پر اسے پکارتے ہو اسے اٹھالے اور شریکوں کو بھول جاؤ گے'),
(831, 6, 42, 'اور بیشک ہم نے تم سے پہلی اُمتوں کی طرف رسول بھیجے تو انہیں سختی اور تکلیف سے پکڑا کہ وہ کسی طرح گڑگڑائیں'),
(832, 6, 43, 'تو کیوں نہ ہوا کہ جب ان پر ہمارا عذاب آیا تو گڑگڑائے ہوتے لیکن ان کے دل تو سخت ہوگئے اور شیطان نے ان کے کام ان کی نگاہ میں بھلے کر دکھائے،'),
(833, 6, 44, 'پھر جب انہوں نے بھلا دیا جو نصیحتیں ان کو کی گئیں تھیں ہم نے ان پر ہر چیز کے دروازے کھول دیے یہاں تک کہ جب خوش ہوئے اس پر جو انہیں ملا تو ہم نے اچانک انہیں پکڑلیا اب وہ آس ٹوٹے رہ گئے،'),
(834, 6, 45, 'تو جڑ کاٹ دی گئی ظالموں کی اور سب خوبیاں سراہا اللہ رب سارے جہاں کا'),
(835, 6, 46, 'تم فرماؤ بھلا بتاؤ تو اگر اللہ تمہارے کان آنکھ لے لے اور تمہارے دلوں پر مہر کردے تو اللہ سوا کون خدا ہے کہ تمہیں یہ چیزیں لادے دیکھو ہم کس کس رنگ سے آیتیں بیان کرتے ہیں پھر وہ منہ پھیر لیتے ہیں،'),
(836, 6, 47, 'تم فرماؤ بھلا بتاؤ تو اگر تم پر اللہ کا عذاب آئے اچانک یا کھلم کھلا تو کون تباہ ہوگا سوا ظالموں کے'),
(837, 6, 48, 'اور ہم نہیں بھیجتے رسولوں کو مگر خوشی اور ڈر سناتے تو جو ایمان لائے اور سنورے ان کو نہ کچھ اندیشہ نہ کچھ غم،'),
(838, 6, 49, 'اور جنہوں نے ہماری آیتیں جھٹلائیں انہیں عذاب پہنچے گا بدلہ ان کی بے حکمی کا،'),
(839, 6, 50, 'تم فرمادو میں تم سے نہیں کہتا میرے پاس اللہ کے خزانے ہیں اور نہ یہ کہوں کہ میں آپ غیب جان لیتا ہوں اور نہ تم سے یہ کہوں کہ میں فرشتہ ہوں میں تو اسی کا تا بع ہوں جو مجھے وحی آتی ہے تم فر ماؤ کیا برابر ہو جائیں گے اندھے اور انکھیا رے تو کیا تم غور نہیں کرتے،'),
(840, 6, 51, 'اور اس قرآن سے انہیں ڈراؤ جنہیں خوف ہو کہ اپنے رب کی طرف یوں اٹھائے جائیں کہ اللہ کے سوا نہ ان کا کوئی حمایتی ہو نہ کوئی سفارشی اس امید پر کہ وہ پرہیزگار ہوجائیں'),
(841, 6, 52, 'اور دور نہ کرو انہیں جو اپنے رب کو پکارتے ہیں صبح اور شام اس کی رضا چاہتے تم پر ان کے حساب سے کچھ نہیں اور ان پر تمہارے حساب سے کچھ نہیں پھر انہیں تم دور کرو تو یہ کام انصاف سے بعید ہے'),
(842, 6, 53, 'اور یونہی ہم نے ان میں ایک دوسرے کے لئے فتنہ بنایا کہ مالدار کافر محتاج مسلمانوں کو دیکھ کر کہیں کیا یہ ہیں جن پر اللہ نے احسان کیا ہم میں سے کیا اللہ خوب نہیں جانتا حق ماننے والوں کو،'),
(843, 6, 54, 'اور جب تمہارے حضور وہ حاضر ہوں جو ہماری آیتوں پر ایمان لاتے ہیں تو ان سے فرماؤ تم پر سلام تمہارے رب نے اپنے ذمہ کرم پر رحمت لازم کرلی ہے کہ تم میں جو کوئی نادانی سے کچھ برائی کر بیٹھے پھر اس کے بعد توبہ کرے اور سنور جائے تو بیشک اللہ بخشنے والا مہربان ہے،'),
(844, 6, 55, 'اور اسی طرح ہم آیتوں کو مفصل بیان فرماتے ہیں اور اس لیے کہ مجرموں کا راستہ ظاہر ہوجائے'),
(845, 6, 56, 'تم فرماؤ مجھے منع کیا گیا ہے کہ انہیں پوجوں جن کو تم اللہ کے سوا پوجتے ہو تم فرماؤ میں تمہاری خواہشوں پر نہیں چلتا یوں ہو تو میں بہک جاؤں اور راہ پر نہ رہوں،'),
(846, 6, 57, 'تم فرماؤ میں تو اپنے رب کی طرف سے روشن دلیل پر ہوں اور تم اسے جھٹلاتے ہو میرے پاس نہیں جس کی تم جلدی مچارہے ہو حکم نہیں مگر اللہ کا وہ حق فرماتا ہے اور وہ سب سے بہتر فیصلہ کرنے والا،'),
(847, 6, 58, 'تم فرماؤ اگر میرے پاس ہوتی وہ چیز جس کی تم جلدی کررہے ہو تو مجھ میں تم میں کام ختم ہوچکا ہوتا اور اللہ خوب جانتا ہے ستمگاروں کو،'),
(848, 6, 59, 'اور اسی کے پاس ہیں کنجیاں غیب کی انہیں وہی جانتا ہے اور جانتا ہے جو کچھ خشکی اور تری میں ہے، اور جو پتّا گرتا ہے وہ اسے جانتا ہے اور کوئی دانہ نہیں زمین کی اندھیریوں میں اور نہ کوئی تر اور نہ خشک جو ایک روشن کتاب میں لکھا نہ ہو'),
(849, 6, 60, 'اور وہی ہے جو رات کو تمہاری روحیں قبض کرتا ہے اور جانتا ہے جو کچھ دن میں کماؤ پھر تمہیں دن میں اٹھاتا ہے کہ ٹھہرائی ہوئی میعاد پوری ہو پھر اسی کی طرف پھرنا ہے پھر وہ بتادے گا جو کچھ تم کرتے تھے،'),
(850, 6, 61, 'اور وہی غالب ہے اپنے بندوں پر اور تم پر نگہبان بھیجتا ہے یہاں تک کہ جب تم میں کسی کو موت آتی ہے ہمارے فرشتے اس کی روح قبض کرتے ہیں اور وہ قصور نہیں کرتے'),
(851, 6, 62, 'پھر پھیرے جاتے ہیں اپنے سچے مولیٰ اللہ کی طرف سنتا ہے اسی کا حکم اور وہ سب سے جلد حساب کرنے والا'),
(852, 6, 63, 'تم فرماؤ وہ کون ہے جو تمہیں نجات دیتا ہے جنگل اور دریا کی آفتوں سے جسے پکارتے ہو گِڑ گِڑا کر اور آہستہ کہ اگر وہ ہمیں اس سے بچاوے تو ہم ضرور احسان مانیں گے'),
(853, 6, 64, 'تم فرماؤ اللہ تمہیں نجات دیتا ہے اس سے اور ہر بے چینی سے پھر تم شریک ٹھہراتے ہو'),
(854, 6, 65, 'تم فرماؤ وہ قادر ہے کہ تم پر عذاب بھیجے تمہارے اوپر سے یا تمہارے پاؤں کے تلے (نیچے) سے یا تمہیں بھڑا دے مختلف گروہ کرکے اور ایک کو دوسرے کی سختی چکھائے، دیکھو ہم کیونکر طرح طرح سے آیتیں بیان کرتے ہیں کہ کہیں ان کو سمجھ ہو'),
(855, 6, 66, 'اور اسے جھٹلایا تمہاری قوم نے اور یہی حق ہے، تم فرماؤ میں تم پر کچھ کڑوڑا (حاکمِ اعلیٰ) نہیں'),
(856, 6, 67, 'ہر چیز کا ایک وقت مقرر ہے اور عنقریب جان جاؤ گے'),
(857, 6, 68, 'اور اے سننے والے! جب تو انہیں دیکھے جو ہماری آیتوں میں پڑتے ہیں تو ان سے منہ پھیر لے جب تک اور بات میں پڑیں، اور جو کہیں تجھے شیطان بھلاوے تو یاد آئے پر ظالموں کے پاس نہ بیٹھ،'),
(858, 6, 69, 'اور پرہیز گاروں پر ان کے حساب سے کچھ نہیں ہاں نصیحت دینا شاید وہ باز آئیں'),
(859, 6, 70, 'اور چھوڑ دے ان کو جنہوں نے اپنا دین ہنسی کھیل بنا لیا اور انہیں دنیا کی زندگانی نے فریب دیا اور قرآن سے نصیحت دو کہ کہیں کوئی جان اپنے کئے پر پکڑی نہ جائے اللہ کے سوا نہ اس کا کوئی حمایتی ہو نہ سفارشی اور اگر اپنے عوض سارے بدلے دے تو اس سے نہ لیے جائیں یہ ہیں وہ جو اپنے کیے پر پکڑے گئے انہیں پینے کا کھولتا پانی اور درد ناک عذاب بدلہ ان کے کفر کا،'),
(860, 6, 71, 'تم فرماؤ کیا ہم اللہ کے سوا اس کو پوجیں جو ہمارا نہ بھلا کرے نہ برُا اور الٹے پاؤں پلٹا دیے جائیں بعد اس کے کہ اللہ نے ہمیں راہ دکھائی اس کی طرح جسے شیطان نے زمین میں راہ بھلادی حیران ہے اس کے رفیق اسے راہ کی طرف بلا رہے ہیں کہ ادھر آ، تم فرماؤ کہ اللہ ہی کی ہدایت ہدایت ہے اور ہمیں حکم ہے کہ ہم اس کے لیے گردن رکھ دیں جو رب ہے سارے جہان'),
(861, 6, 72, 'اور یہ کہ نماز قائم رکھو اور اس سے ڈرو، اور وہی ہے جس کی طرف اٹھنا ہے،'),
(862, 6, 73, 'اور وہی ہے جس نے آسمان و زمین ٹھیک بنائے اور جس دن فنا ہوئی ہر چیز کو کہے گا ہو جا وہ فوراً ہوجائے گی، اس کی بات سچی ہے، اور اسی کی سلطنت ہے جس دن صور پھونکا جائے گا ہر چھپے اور ظاہر کو جاننے والا، اور وہی حکمت والا خبردار،'),
(863, 6, 74, 'اور یاد کرو جب ابراہیم نے اپنے باپ آزر سے کہا، کیا تم بتوں کو خدا بناتے ہو، بیشک میں تمہیں اور تمہاری قوم کو کھلی گمراہی میں پاتا ہوں'),
(864, 6, 75, 'اور اسی طرح ہم ابراہیم کو دکھاتے ہیں ساری بادشاہی آسمانوں اور زمین کی اور اس لیے کہ وہ عین الیقین والوں میں ہوجائے'),
(865, 6, 76, 'پھر جب ان پر رات کا اندھیرا آیا ایک تارا دیکھا بولے اسے میرا رب ٹھہراتے ہو پھر جب وہ ڈوب گیا بولے مجھے خوش نہیں آتے ڈوبنے والے،'),
(866, 6, 77, 'پھر جب چاند چمکتا دیکھا بولے اسے میرا رب بتاتے ہو پھر جب وہ ڈوب گیا کہا اگر مجھے میرا رب ہدایت نہ کرتا تو میں بھی انہیں گمراہوں میں ہوتا'),
(867, 6, 78, 'پھر جب سورج جگمگاتا دیکھا بولے اسے میرا رب کہتے ہو یہ تو ان سب سے بڑا ہے، پھر جب وہ ڈوب گیا کہا اے قوم میں بیزار ہوں ان چیزوں سے جنہیں تم شریک ٹھہراتے ہو'),
(868, 6, 79, 'میں نے اپنا منہ اس کی طرف کیا جس نے آسمان اور زمین بنائے ایک اسی کا ہوکر اور میں مشرکین میں نہیں،'),
(869, 6, 80, 'اور ان کی قوم ان سے جھگڑے لگی کہا کیا اللہ کے بارے میں مجھ سے جھگڑتے ہو تو وہ مجھے راہ بتا چکا اور مجھے ان کا ڈر نہیں جنہیں تم شریک بتاتے ہو ہاں جو میرا ہی رب کوئی بات چاہے میرے رب کا علم ہر چیز کو محیط ہے، تو کیا تم نصیحت نہیں مانتے'),
(870, 6, 81, 'اور میں تمہارے شریکوں سے کیونکر ڈروں اور تم نہیں ڈرتے کہ تم نے اللہ کا شریک اس کو ٹھہرایا جس کی تم پر اس نے کوئی سند نہ اتاری، تو دونوں گروہوں میں امان کا زیادہ سزا وار کون ہے اگر تم جانتے ہو،'),
(871, 6, 82, 'وہ جو ایمان لائے اور اپنے ایمان میں کسی ناحق کی آمیزش نہ کی انہیں کے لیے امان ہے اور وہی راہ پر ہیں،'),
(872, 6, 83, 'اور یہ ہماری دلیل ہے کہ ہم نے ابراہیم کو اس کی قوم پر عطا فرمائی، ہم جسے چاہیں درجوں بلند کریں بیشک تمہارا رب علم و حکمت والا ہے'),
(873, 6, 84, 'اور ہم نے انہیں اسحاق اور یعقوب عطا کیے، ان سب کو ہم نے راہ دکھائی اور ان سے پہلے نوح کو راہ دکھائی اور میں اس کی اولاد میں سے داؤد اور سلیمان اور ایوب اور یوسف اور موسیٰ اور ہارون کو، اور ہم ایسا ہی بدلہ دیتے ہیں نیکوکاروں کو،'),
(874, 6, 85, 'اور زکریا اور یحییٰ اور عیسیٰ اور الیاس کو یہ سب ہمارے قرب کے لائق ہیں'),
(875, 6, 86, 'اور اسماعیل اور یسع اور یونس اور لوط کو، اور ہم نے ہر ایک کو اس کے وقت میں سب پر فضیلت دی'),
(876, 6, 87, 'اور کچھ ان کے باپ دادا اور اولاد اور بھائیوں میں سے بعض کو اور ہم نے انہیں چن لیا اور سیدھی راہ دکھائی،'),
(877, 6, 88, 'یہ اللہ کی ہدایت ہے کہ اپنے بندوں میں جسے چاہے دے، اور اگر وہ شرک کرتے تو ضرور ان کا کیا اکارت جاتا،'),
(878, 6, 89, 'یہ ہیں جن کو ہم نے کتاب اور حکم اور نبوت عطا کی تو اگر یہ لوگ اس سے منکر ہوں تو ہم نے اس کیلئے ایک ایسی قوم لگا رکھی ہے جو انکار والی نہیں'),
(879, 6, 90, 'یہ ہیں جن کو اللہ نے ہدایت کی تو تم انہیں کی راہ چلو تم فرماؤ میں قرآن پر تم سے کوئی اجرت نہیں مانگتا وہ تو نہیں مگر نصیحت سارے جہان کو'),
(880, 6, 91, 'اور یہود نے اللہ کی قدر نہ جانی جیسی چاہیے تھی جب بولے ا لله نے کسی آدمی پر کچھ نہیں اتارا، تم فرماؤ کس نے اُتاری وہ کتاب جو موسیٰ لائے تھے روشنی اور لوگوں کے لیے ہدایت جس کے تم نے الگ الگ کاغذ بنالیے ظاہر کرتے ہو اور بہت سے چھپالیتے ہو اور تمہیں وہ سکھایا جاتا ہے جو نہ تم کو معلوم تھا نہ تمہارے باپ دادا کو، اللہ کہو پھر انہیں چھوڑ دو ان کی بیہودگی میں انہیں کھیلتا'),
(881, 6, 92, 'اور یہ ہے برکت والی کتاب کہ ہم نے اُتاری تصدیق فرماتی ان کتابوں کی جو آگے تھیں اور اس لیے کہ تم ڈر سناؤ سب بستیوں کے سردار کو اور جو کوئی سارے جہاں میں اس کے گرد ہیں اور جو آخرت پر ایمان لاتے ہیں اس کتاب پر ایمان لاتے ہیں اور اپنی نماز کی حفاظت کرتے ہیں،'),
(882, 6, 93, 'اور اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے یا کہے مجھے وحی ہوئی اور اسے کچھ وحی نہ ہوئی اور جو کہے ابھی میں اُتارتا ہوں ایسا جیسا اللہ نے اُتارا اور کبھی تم دیکھوں جس وقت ظالم موت کی سختیوں میں ہیں اور فرشتے ہاتھ پھیلاتے ہوئے ہیں کہ نکالو اپنی جانیں، آج تمہیں خواری کا عذاب دیا جائے گا بدلہ اس کا کہ اللہ پر جھوٹ لگاتے تھے اور اس کی آیتوں سے تکبر کرتے،'),
(883, 6, 94, 'اور بیشک تم ہمارے پاس اکیلے آئے جیسا ہم نے تمہیں پہلی بار پیدا کیا تھا اور پیٹھ پیچھے چھوڑ آئے جو مال و متاع ہم نے تمہیں دیا تھا اور ہم تمہارے ساتھ تمہارے ان سفارشیوں کو نہیں دیکھتے جن کا تم اپنے میں ساجھا بتاتے تھے بیشک تمہارے آپس کی ڈور کٹ گئی اور تم سے گئے جو دعوے کرتے تھے'),
(884, 6, 95, 'بیشک اللہ دانے اور گٹھلی کو چیر نے والا ہے زندہ کو مردہ سے نکالنے اور مردہ کو زندہ سے نکالنے والا یہ ہے اللہ تم کہاں اوندھے جاتے ہو'),
(885, 6, 96, 'تاریکی چاک کرکے صبح نکالنے والا اور اس نے رات کو چین بنایا اور سورج اور چاند کو حساب یہ سادھا (سدھایا ہوا) ہے زبردست جاننے والے کا،'),
(886, 6, 97, 'اور وہی ہے جس نے تمہارے لیے تارے بنائے کہ ان سے راہ پاؤ خشکی اور تری کے اندھیروں میں، ہم نے نشانیاں مفصل بیان کردیں علم والوں کے لیے،'),
(887, 6, 98, 'اور وہی ہے جس نے تم کو ایک جان سے پیدا کیا پھر کہیں تمہیں ٹھہرنا ہے اور کہیں امانت رہنا بیشک ہم نے مفصل آیتیں بیان کردیں سمجھ والوں کے لیے،'),
(888, 6, 99, 'اور وہی ہے جس نے آسمان سے پانی اُتارا، تو ہم نے اس سے ہر اُگنے والی چیز نکالی تو ہم نے اس سے نکالی سبزی جس میں سے دانے نکالتے ہیں ایک دوسرے پر چڑھے ہوئے اور کھجور کے گابھے سے پاس پاس گچھے اور انگور کے باغ اور زیتون اور انار کسی بات میں ملتے اور کسی بات میں الگ، اس کا پھل دیکھو جب پھلے اور اس کا پکنا بیشک اس میں نشانیاں ہیں ایمان والوں کے لیے،'),
(889, 6, 100, 'اور اللہ کا شریک ٹھہرایا جنوں کو حالانکہ اسی نے ان کو بنایا اور اس کے لیے بیٹے اور بیٹیاں گڑھ لیں جہالت سے، پاکی اور برتری ہے اس کو ان کی باتوں سے،'),
(890, 6, 101, 'بے کسی نمو نہ کے آسمانوں اور زمین کا بنانے والا، اسکے بچہ کہاں سے ہو حالانکہ اس کی عورت نہیں اور اس نے ہر چیز پیدا کی اور وہ سب کچھ جانتا ہے،'),
(891, 6, 102, 'یہ ہے اللہ تمہارا رب اور اس کے سوا کسی کی بندگی نہیں ہر چیز کا بنانے والا تو اسے پوجو وہ ہر چیز پر نگہبان ہے'),
(892, 6, 103, 'آنکھیں اسے احاطہ نہیں کرتیں اور سب آنکھیں اس کے احاطہ میں ہیں اور وہی ہے پورا باطن پورا خبردار،'),
(893, 6, 104, 'تمہارے پاس آنکھیں کھولنے والی دلیلیں آئیں تمہارے رب کی طرف سے تو جس نے دیکھا تو اپنے بھلے کو اور جو اندھا ہوا اپنے برُے کو، اور میں تم پر نگہبان نہیں،'),
(894, 6, 105, 'اور ہم اسی طرح آیتیں طرح طرح سے بیان کرتے اور اس لیے کہ کافر بول اٹھیں کہ تم تو پڑھے ہو اور اس لیے کہ اسے علم والوں پر واضح کردیں،'),
(895, 6, 106, 'اس پر چلو جو تمہیں تمہارے رب کی طرف سے وحی ہوتی ہے اس کے سوا کوئی معبود نہیں اور مشرکوں سے منہ پھیر لو'),
(896, 6, 107, 'اور اللہ چاہتا تو وہ شرک نہیں کرتے، اور ہم نے تمہیں ان پر نگہبان نہیں کیا اور تم ان پر کڑوڑے (حاکمِ اعلیٰ) نہیں،'),
(897, 6, 108, 'اور انہیں گالی نہ دو وہ جن کو وہ اللہ کے سوا پوجتے ہیں کہ وہ اللہ کی شان میں بے ادبی کریں گے زیادتی اور جہالت سے یونہی ہم نے ہر اُمت کی نگاہ میں اس کے عمل بھلے کردیے ہیں پھر انہیں اپنے رب کی طرف پھرنا ہے اور وہ انہیں بتادے گا جو کرتے تھے،'),
(898, 6, 109, 'اور انہوں نے اللہ کی قسم کھائی اپنے حلف میں پوری کوشش سے کہ اگر ان کے پاس کوئی نشانی آئی تو ضرور اس پر ایمان لائیں گے، م فرما دو کہ نشانیاں تو اللہ کے پاس ہیں اور تمہیں کیا خبر کہ جب وہ آئیں تو یہ ایمان نہ لائینگے'),
(899, 6, 110, 'اور ہم پھیردیتے ہیں ان کے دلوں اور آنکھوں کو جیسا وہ پہلی بار ایمان نہ لائے تھے اور انہیں چھوڑ دیتے ہیں کہ اپنی سرکشی میں بھٹکا کریں،'),
(900, 6, 111, 'اور اگر ہم ان کی طرف فرشتے اُتارتے اور ان سے مردے باتیں کرتے اور ہم ہر چیز ان کے سامنے اٹھا لاتے جب بھی وہ ایمان لانے والے نہ تھے مگر یہ کہ خدا چاہتا و لیکن ان میں بہت نرے جاہل ہیں'),
(901, 6, 112, 'اور اسی طرح ہم نے ہر نبی کے دشمن کیے ہیں آدمیوں اور جنوں میں کے شیطان کہ ان میں ایک دوسرے پر خفیہ ڈالتا ہے بناوٹ کی بات دھوکے کو، اور تمہارا رب چاہتا تو وہ ایسا نہ کرتے تو انہیں ان کی بناوٹوں پر چھوڑ دو'),
(902, 6, 113, 'اور اس لیے کہ اس کی طرف ان کے دل جھکیں جنہیں آخرت پر ایمان نہیں اور اسے پسند کریں اور گناہ کمائیں جو انہیں کمانا ہے،'),
(903, 6, 114, 'تو کیا اللہ کے سوا میں کسی اور کا فیصلہ چاہوں اور وہی ہے جس نے تمہاری طرف مفصل کتاب اُتاری اور جنکو ہم نے کتاب دی وہ جانتے ہیں کہ یہ تیرے رب کی طرف سے سچ اترا ہے تو اے سننے والے تو ہر گز شک والوں میں نہ ہو،'),
(904, 6, 115, 'اور پوری ہے تیرے رب کی بات سچ اور انصاف میں اس کی باتوں کا کوئی بدلنے والا نہیں اور وہی ہے سنتا جانتا،'),
(905, 6, 116, 'اور اے سننے والے زمین میں اکثر وہ ہیں کہ تو ان کے کہے پر چلے تو تجھے اللہ کی راہ سے بہکا دیں، وہ صرف گمان کے پیچھے ہیں اور نری اٹکلیں (فضول اندازے) دوڑاتے ہیں'),
(906, 6, 117, 'تیرا رب خوب جانتا ہے کہ کون بہکا اس کی راہ سے اور وہ خوب جانتا ہے ہدایت والوں کو،'),
(907, 6, 118, 'تو کھاؤ اسمیں سے جس پر اللہ کا نام لیا گیا اگر تم اسکی آیتیں مانتے ہو،'),
(908, 6, 119, 'اور تمہیں کیا ہوا کہ اس میں سے نہ کھاؤ جس پر اللہ کا نام لیا گیا وہ تم سے مفصل بیان کرچکا جو کچھ تم پر حرام ہوا مگر جب تمہیں اس سے مجبوری ہو اور بیشک بہتیرے اپنی خواہشوں سے گمراہ کرتے ہیں بے جانے بیشک تیرا رب حد سے بڑھنے والوں کو خوب جانتا ہے،'),
(909, 6, 120, 'اور چھوڑ دو کھلا اور چھپا گناہ، وہ جو گناہ کماتے ہیں عنقریب اپنی کمائی کی سزا پائیں گے،'),
(910, 6, 121, 'اور اُسے نہ کھاؤ جس پر اللہ کا نام نہ لیا گیا اور وہ بیشک حکم عدولی ہے، اور بیشک شیطان اپنے دوستوں کے دلوں میں ڈالتے ہیں کہ تم سے جھگڑیں اور اگر تم ان کا کہنا مانو تو اس وقت تم مشرک ہو'),
(911, 6, 122, 'اور کیا وہ کہ مردہ تھا تو ہم نے اسے زندہ کیا اور اس کے لیے ایک نور کردیا جس سے لوگوں میں چلتا ہے وہ اس جیسا ہوجائے گا جو اندھیریوں میں ہے ان سے نکلنے والا نہیں، یونہی کافروں کی آنکھ میں ان کے اعمال بھلے کردیے گئے ہیں،'),
(912, 6, 123, 'اور اسی طرح ہم نے ہر بستی میں اس کے مجرموں کے سرغنہ کیے کہ اس میں داؤ کھیلیں اور داؤں نہیں کھیلتے مگر اپنی جانوں پر اور انہیں شعورنہیں'),
(913, 6, 124, 'اور جب ان کے پاس کوئی نشانی آئے تو کہتے ہی ہم ہر گز ایمان نہ لائیں گے جب تک ہمیں بھی ویسا ہی نہ ملے جیسا اللہ کے رسولوں کو ملا اللہ خوب جانتا ہے جہاں اپنی رسالت رکھے عنقریب مجرموں کو اللہ کے یہاں ذلت پہنچے گی اور سخت عذاب بدلہ ان کے مکر کا،'),
(914, 6, 125, 'اور جسے اللہ راہ دکھانا چاہے اس کا سینہ اسلام کے لیے کھول دیتا ہے اور جسے گمراہ کرنا چاہے اس کا سینہ تنگ خوب رکا ہوا کر دیتا ہے گویا کسی کی زبردستی سے آسمان پر چڑھ رہا ہے، اللہ یونہی عذاب ڈالتا ہے ایمان نہ لانے والوں کو،'),
(915, 6, 126, 'اور یہ تمہارے رب کی سیدھی راہ ہے ہم نے آیتیں مفصل بیان کردیں نصیحت ماننے والوں کے لیے،'),
(916, 6, 127, 'ان کے لیے سلامتی کا گھر ہے اپنے رب کے یہاں اور وہ ان کا مولیٰ ہے یہ ان کے کاموں کا پھل ہے،'),
(917, 6, 128, 'اور جس دن اُن سب کو اٹھانے گا اور فرمائے گا، اے جن کے گروہ! تم نے بہت آدمی گھیرلیے اور ان کے دوست آدمی عرض کریں گے اے ہمارے رب! ہم میں ایک نے دوسرے سے فائدہ اٹھایا اور ہم اپنی اس میعاد کو پہنچ گئے جو تو نے ہمارے لیے مقرر فرمائی تھی فرمائے گا آگ تمہارا ٹھکانا ہے ہمیشہ اس میں رہو مگر جسے خدا چاہے اے محبوب! بیشک تمہارا رب حکمت والا علم والا ہے،'),
(918, 6, 129, 'اور یونہی ہم ظالموں میں ایک کو دوسرے پر مسلط کرتے ہیں بدلہ ان کے کیے کا'),
(919, 6, 130, 'اے جنوں اور آدمیوں کے گروہ! کیا تمہارے پاس تم میں کے رسول نہ آئے تھے تم پر میری آیتیں پڑھتے اور تمہیں یہ دن دیکھنے سے ڈراتے کہیں گے ہم نے اپنی جانوں پر گواہی دی اور انہیں دنیا کی زندگی نے فریب دیا اور خود اپنی جانوں پر گواہی دیں گے کہ وہ کافر تھے'),
(920, 6, 131, 'یہ اس لیے کہ تیرا رب بستیوں کو ظلم سے تباہ نہیں کرتا کہ ان کے لوگ بے خبر ہوں'),
(921, 6, 132, 'اور ہر ایک کے لیے ان کے کاموں سے درجے ہیں اور تیرا رب ان کے اعمال سے بے خبر نہیں،'),
(922, 6, 133, 'اور اے محبوب! تمہارا رب بے پروا ہے رحمت والا، اے لوگو! وہ چاہے تو تمہیں لے جائے اور جسے چاہے تمہاری جگہ لادے جیسے تمہیں اوروں کی اولاد سے پیدا کیا'),
(923, 6, 134, 'بیشک جس کا تمہیں وعدہ دیا جاتا ہے ضرور آنے والی ہے اور تم تھکا نہیں سکتے،'),
(924, 6, 135, 'تم فرماؤ اے میری قوم! تم اپنی جگہ پر کام کیے جاؤ میں اپنا کام کرتا ہوں تو اب جاننا چاہتے ہو کس کا رہتا ہے آحرت کا گھر، بیشک ظالم فلاح نہیں پاتے،'),
(925, 6, 136, 'اور اللہ نے جو کھیتی اور مویشی پیدا کیے ان میں اسے ایک حصہ دار ٹھہرایا تو بولے یہ اللہ کا ہے ان کے خیال میں اور یہ ہمارے شریکوں کا تو وہ جو ان کے شریکوں کا ہے وہ تو خدا کو نہیں پہنچتا، اور جو خدا کا ہے وہ ان کے شریکوں کو پہنچتا ہے، کیا ہی برا حکم لگاتے ہیں'),
(926, 6, 137, 'اور یوں ہی بہت مشرکوں کی نگاہ میں ان کے شریکوں نے اولاد کا قتل بھلا کر دکھایا ہے کہ انہیں ہلاک کریں اور ان کا دین اُن پر مشتبہ کردیں اور اللہ چاہتا تو ایسا نہ کرتے تو تم انہیں چھوڑ دو وہ ہیں اور ان کے افتراء،'),
(927, 6, 138, 'اور بولے یہ مویشی اور کھیتی روکی ہوئی ہے اسے وہی کھائے جسے ہم چاہیں اپنے جھوٹے خیال سے اور کچھ مویشی ہیں جن پر چڑھنا حرام ٹھہرایا اور کچھ مویشی کے ذبح پر اللہ کا نام نہیں لیتے یہ سب اللہ پر جھوٹ باندھنا ہے، عنقریب وہ انہیں بدلے دے گا ان کے افتراؤں کا،'),
(928, 6, 139, 'اور بولے جو ان مویشیوں کے پیٹ میں ہے وہ نرا (خالص) ہمارے مردوں کا ہے اور ہماری عورتوں پر حرام ہے، اور مرا ہوا نکلے تو وہ سب اس میں شریک ہیں، قریب ہے کہ اللہ انہیں اِن کی اُن باتوں کا بدلہ دے گا، بیشک وہ حکمت و علم والا ہے،'),
(929, 6, 140, 'بیشک تباہ ہوئے وہ جو اپنی اولاد کو قتل کرتے ہیں احمقانہ جہالت سے اور حرام ٹھہراتے ہیں وہ جو اللہ نے انہیں روزی دی اللہ پر جھوٹ باندھنے کو بیشک وہ بہکے اور راہ نہ پائی'),
(930, 6, 141, 'اور وہی ہے جس نے پیدا کیے باغ کچھ زمین پر چھئے (چھائے) ہوئے اور کچھ بے چھئے (پھیلے) اور کھجور اور کھیتی جس میں رنگ رنگ کے کھانے اور زیتون اور انار کسی بات میں ملتے اور کسی میں الگ کھاؤ اس کا پھل جب پھل لائے اور اس کا حق دو جس دن کٹے اور بے جا نہ خرچو بیشک بے جا خرچنے والے اسے پسند نہیں،'),
(931, 6, 142, 'اور مویشی میں سے کچھ بوجھ اٹھانے والے اور کچھ زمین پر بچھے کھاؤ اس میں سے جو اللہ نے تمہیں روزی دی اور شیطان کے قدموں پر نہ چلو، بیشک وہ تمہارا صریح دشمن ہے،'),
(932, 6, 143, 'آٹھ نر و مادہ ایک جوڑا بھیڑ کا اور ایک جوڑا بکری کا، تم فرماؤ کیا اس نے دونوں نر حرام کیے یا دونوں مادہ یا وہ جسے دنوں مادہ پیٹ میں لیے ہیں کسی علم سے بتاؤ اگر تم سچے ہو'),
(933, 6, 144, 'اور ایک جوڑا اونٹ کا اور ایک جوڑا گائے کا، تم فرماؤ کیا اس نے دونوں نر حرام کیے یا دونوں مادہ یا وہ جسے دونوں مادہ پیٹ میں لیے ہیں کیا تم موجود تھے جب اللہ نے تمہیں یہ حکم دیا تو اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے کہ لوگوں کو اپنی جہالت سے گمراہ کرے، بیشک اللہ ظالموں کو راہ نہیں دکھاتا،'),
(934, 6, 145, 'تم فرماؤ میں نہیں پاتا اس میں جو میری طرف وحی ہوئی کسی کھانے والے پر کوئی کھانا حرام مگر یہ کہ مردار ہو یا رگوں کا بہتا خون یا بد جانور کا گوشت وہ نجاست ہے یا وہ بے حکمی کا جانور جس کے ذبح میں غیر خدا کا نام پکارا گیا تو جو ناچار ہوا نہ یوں کہ آپ خواہش کرے اور نہ یوں کہ ضرورت سے بڑھے تو بے شیک اللہ بخشنے والا مہربان ہے'),
(935, 6, 146, 'اور یہودیوں پر ہم نے حرام کیا ہر ناخن والا جانور اور گائے اور بکری کی چربی ان پر حرام کی مگر جو ان کی پیٹھ میں لگی ہو یا آنت یا ہڈی سے ملی ہو، ہم نے یہ ان کی سرکشی کا بدلہ دیا اور بیشک ہم ضرور سچے ہیں،'),
(936, 6, 147, 'پھر اگر وہ تمہیں جھٹلائیں تو تم فرماؤ کہ تمہارا رب وسیع رحمت والا ہے اور اس کا عذاب مجرموں پر سے نہیں ٹالا جاتا'),
(937, 6, 148, 'اب کہیں گے مشرک کہ اللہ چاہتا تو نہ ہم شرک کرتے نہ ہمارے باپ دادا نہ ہم کچھ حرام ٹھہراتے ایسا ہی ان کے اگلوں نے جھٹلایا تھا یہاں تک کہ ہمارا عذاب چکھا تم فرماؤ کیا تمہارے پاس کوئی علم ہے کہ اسے ہمارے لیے نکالو، تم تو نرے گمان (خام خیال) کے پیچھے ہو اور تم یونہی تخمینے کرتے ہو'),
(938, 6, 149, 'تم فرماؤ تو اللہ ہی کی حجت پوری ہے تو وہ چاہتا تو سب کی ہدایت فرماتا،'),
(939, 6, 150, 'تم فرماؤ لاؤ اپنے وہ گواہ جو گواہی دیں کہ اللہ نے اسے حرام کیا پھر اگر وہ گواہی دے بیٹھیں تو تُو اے سننے والے! ان کے ساتھ گواہی نہ دینا اور ان کی خواہشوں کے پیچھے نہ چلنا جو ہماری آیتیں جھٹلاتے ہیں اور جو آخرت پر ایمان نہیں لاتے اور اپنے رب کا برابر والا ٹھہراتے ہیں'),
(940, 6, 151, 'تم فرماؤ آؤ میں تمہیں پڑھ کر سناؤں جو تم پر تمہارے رب نے حرام کیا یہ کہ اس کا کوئی شریک نہ کرو اور ماں باپ کے ساتھ بھلائی کرو اور اپنی اولاد قتل نہ کرو مفلسی کے باعث، ہم تمہیں اور انہیں سب کو رزق دیں گے اور بے حیائیوں کے پاس نہ جاؤ جو ان میں کھلی ہیں اور جو چھپی اور جس جان کی اللہ نے حرمت رکھی اسے ناحق نہ مارو یہ تمہیں حکم فرمایا ہے کہ تمہیں عقل ہو'),
(941, 6, 152, 'اور یتیموں کے مال کے پاس نہ جاؤ مگر بہت اچھے طریقہ سے جب تک وہ اپنی جوانی کو پہنچے اور ناپ اور تول انصاف کے ساتھ پوری کرو، ہم کسی جان پر بوجھ نہیں ڈالتے مگر اس کے مقدور بھر، اور جب بات کہو تو انصاف کی کہو اگرچہ تمہارے رشتہ دار کا معاملہ ہو اور اللہ ہی کا عہد پورا کرو، یہ تمہیں تاکید فرمائی کہ کہیں تم نصیحت مانو،'),
(942, 6, 153, 'اور یہ کہ یہ ہے میرا سیدھا راستہ تو اس پر چلو اور اور راہیں نہ چلو کہ تمہیں اس کی راہ سے جدا کردیں گی، یہ تمہیں حکم فرمایا کہ کہیں تمہیں پرہیزگاری ملے،'),
(943, 6, 154, 'پھر ہم نے موسیٰ کو کتاب عطا فرمائی پورا احسان کرنے کو اس پر جو نیکوکار ہے اور ہر چیز کی تفصیل اور ہدایت اور رحمت کہ کہیں وہ اپنے رب سے ملنے پر ایمان لائیں'),
(944, 6, 155, 'اور یہ برکت والی کتاب ہم نے اُتاری تو اس کی پیروی کرو اور پرہیزگاری کرو کہ تم پر رحم ہو،'),
(945, 6, 156, 'کبھی کہو کہ کتاب تو ہم سے پہلے دو گروہوں پر اُتری تھی اور ہمیں ان کے پڑھنے پڑھانے کی کچھ خبر نہ تھی'),
(946, 6, 157, 'یا کہو کہ اگر ہم پر کتاب اُترتی تو ہم ان سے زیادہ ٹھیک راہ پر ہوتے تو تمہارے پاس تمہارے رب کی روشن دلیل اور ہدایت او ر رحمت آئی تو اس سے زیادہ ظالم کون جو اللہ کی آیتوں کو جھٹلائے اور ان سے منہ پھیرے عنقریب وہ جو ہماری آیتوں سے منہ پھیرتے ہیں ہم انہیں بڑے عذاب کی سزا دیں گے بدلہ ان کے منہ پھیرنے کا،'),
(947, 6, 158, 'کاہے کے انتظار میں ہیں مگر یہ کہ آئیں ان کے پاس فرشتے یا تمہارے رب کا عذاب یا تمہارے رب کی ایک نشانی آئے جس دن تمہارے رب کی وہ ایک نشانی آئے گی کسی جان کو ایمان لانا کام نہ دے گا جو پہلے ایمان نہ لائی تھی یا اپنے ایمان میں کوئی بھلائی نہ کمائی تھی تم فرماؤ رستہ دیکھو ہم بھی دیکھتے ہیں،'),
(948, 6, 159, 'وہ جنہوں نے اپنے دین میں جُدا جُدا راہیں نکالیں او رکئی گروہ ہوگئے اے محبوب! تمہیں ان سے کچھ علا قہ نہیں ان کا معاملہ اللہ ہی کے حوالے ہے پھر وہ انہیں بتادے گا جو کچھ وہ کرتے تھے'),
(949, 6, 160, 'جو ایک نیکی لائے تو اس کے لیے اس جیسی دس ہیں اور جو برائی لائے تو اسے بدلہ نہ ملے گا مگر اس کے برابر اور ان پر ظلم نہ ہوگا،'),
(950, 6, 161, 'تم فرماؤ بیشک مجھے میرے رب نے سیدھی راہ دکھائی ٹھیک دین ابراہیم کی ملّت جو ہر باطل سے جُدا تھے، اور مشرک نہ تھے'),
(951, 6, 162, 'تم فرماؤ بیشک میری نماز اور میری قربانیاں اور میرا جینا اور میرا مرنا سب اللہ کے لیے ہے جو رب سارے جہان کا'),
(952, 6, 163, 'اس کا کوئی شریک نہیں، مجھے یہی حکم ہوا ہے اور میں سب سے پہلا مسلمان ہوں'),
(953, 6, 164, 'تم فرماؤ کیا اللہ کے سوا اور رب چاہوں حالانکہ وہ ہر چیز کا رب ہے اور جو کوئی کچھ کمائے وہ اسی کے ذمہ ہے، اور کوئی بوجھ اٹھانے والی جان دوسرے کا بوجھ نہ اٹھائے گی پھر تمہیں اپنے رب کی طرف پھرنا ہے وہ تمہیں بتادے گا جس میں اختلاف کرتے تھے،'),
(954, 6, 165, 'اور وہی ہے جس نے زمین میں تمہیں نائب کیا اور تم میں ایک کو دوسرے پر درجوں بلندی دی کہ تمہیں آزمائے اس چیز میں جو تمہیں عطا کی بیشک تمہارے رب کو عذاب کرتے دیر نہیں لگتی اور بیشک وہ ضرور بخشنے والا مہربان ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المص'),
(956, 7, 2, 'اے محبوب! ایک کتاب تمہاری طرف اُتاری گئی تو تمہارا جی اس سے نہ رُکے اس لیے کہ تم اس سے ڈر سناؤ اور مسلمانوں کو نصیحت،'),
(957, 7, 3, 'اے لوگو! اس پر چلو جو تمہاری طرف تمہارے رب کے پاس سے اُترا اور اسے چھوڑ کر اور حاکموں کے پیچھے نہ جاؤ، بہت ہی کم سمجھتے ہو،'),
(958, 7, 4, 'اور کتنی ہی بستیاں ہم نے ہلاک کیں تو ان پر ہمارا عذاب رات میں آیا جب وہ دوپہر کو سوتے تھے'),
(959, 7, 5, 'تو ان کے منہ سے کچھ نہ نکلا جب ہمارا عذاب ان پر آیا مگر یہی بولے کہ ہم ظالم تھے'),
(960, 7, 6, 'تو بیشک ضرور ہمیں پوچھنا ہے ان سے جن کے پاس رسول گئے اور بیشک ضرور ہمیں پوچھنا ہے رسولوں سے'),
(961, 7, 7, 'تو ضرور ہم ان کو بتادیں گے اپنے علم سے اور ہم کچھ غائب نہ تھے،'),
(962, 7, 8, 'اور اس دن تول ضرور ہونی ہے تو جن کے پلے بھاری ہوئے وہی مراد کو پہنچے،'),
(963, 7, 9, 'اور جن کے پلے ہلکے ہوئے تو وہی ہیں جنہوں نے اپنی جان گھاٹے میں ڈالی ان زیادتیوں کا بدلہ جو ہماری آیتوں پر کرتے تھے'),
(964, 7, 10, 'اور بیشک ہم نے تمہیں زمین میں جماؤ (ٹھکانا) دیا اور تمہارے لیے اس میں زندگی کے اسباب بنائے بہت ہی کم شکر کرتے ہو'),
(965, 7, 11, 'اور بیشک ہم نے تمہیں پیدا کیا پھر تمہارے نقشے بنائے پھر ہم نے ملائکہ سے فرمایا کہ آدم کو سجدہ کرو، تو وہ سب سجدے میں گرے مگر ابلیس، یہ سجدہ کرنے والوں میں نہ ہوا،'),
(966, 7, 12, 'فرمایا کس چیز نے تجھے روکا کہ تو نے سجدہ نہ کیا جب میں نے تجھے حکم دیا تھا بولا میں اس سے بہتر ہوں تو نے مجھے آگ سے بنایا اور اسے مٹی سے بنایا'),
(967, 7, 13, 'فرمایا تو یہاں سے اُ تر جا تجھے نہیں پہنچتا کہ یہاں رہ کر غرور کرے نکل تو ہے ذلت والوں میں'),
(968, 7, 14, 'بولا مجھے فرصت دے اس دن تک کہ لوگ اٹھائے جائیں،'),
(969, 7, 15, 'فرمایا تجھے مہلت ہے'),
(970, 7, 16, 'بولا تو قسم اس کی کہ تو نے مجھے گمراہ کیا میں ضرور تیرے سیدھے راستہ پر ان کی تاک میں بیٹھوں گا'),
(971, 7, 17, 'پھر ضرور میں ان کے پاس آؤں گا ان کے آگے اور ان کے پیچھے او ر ان کے دائیں اور ان کے بائیں سے اور تو ان میں سے اکثر کو شکر گزار نہ پائے گا'),
(972, 7, 18, 'فرمایا یہاں سے نکل جا رد کیا گیا راندہ ہوا، ضرور جو اُن میں سے تیرے کہے پر چلا میں تم سب سے جہنم بھردوں گا'),
(973, 7, 19, 'اور اے آدم تو اور تیرا جوڑا جنت میں رہو تو اُس سے جہاں چاہو کھاؤ اور اس پیڑ کے پاس نہ جانا کہ حد سے بڑھنے والوں میں ہو گے،'),
(974, 7, 20, 'پھر شیطان نے ان کے جی میں خطرہ ڈالا کہ ان پر کھول دے ان کی شرم کی چیزیں جو ان سے چھپی تھیں اور بولا تمہیں تمہارے رب نے اس پیڑ سے اسی لیے منع فرمایا ہے کہ کہیں تم دو فرشتے ہوجاؤ یا ہمیشہ جینے والے'),
(975, 7, 21, 'اور ان سے قسم کھائی کہ میں تم دونوں کا خیر خواہ ہوں،'),
(976, 7, 22, 'تو اُتار لایا انہیں فریب سے پھر جب انہوں نے وہ پیڑ چکھا ان پر اُن کی شرم کی چیزیں کھل گئیں اور اپنے بدن پر جنت کے پتے چپٹانے لگے، اور انہیں ان کے رب نے فرمایا کیا میں نے تمہیں اس پیڑ سے منع نہ کیا اور نہ فرمایا تھا کہ شیطان تمہارا کھلا دشمن ہے،'),
(977, 7, 23, 'دونوں نے عرض کی، اے رب ہمارے! ہم نے اپنا آپ بُرا کیا، تو اگر تُو ہمیں نہ بخشے اور ہم پر رحم نہ کرے تو ہم ضرور نقصان والوں میں ہوئے،'),
(978, 7, 24, 'فرمایا اُترو تم میں ایک دوسرے کا دشمن ہے اور تمہیں زمین میں ایک وقت تک ٹھہرنا اور برتنا ہے،'),
(979, 7, 25, 'فرمایا اسی میں جیوگے اور اسی میں مرو گے اور اسی میں اٹھائے جاؤ گے'),
(980, 7, 26, 'اے آدم کی اولاد! بیشک ہم نے تمہاری طرف ایک لباس وہ اُتارا کہ تمہاری شرم کی چیزیں چھپائے اور ایک وہ کہ تمہاری آرائش ہو اور پرہیزگاری کا لباس وہ سب سے بھلا یہ اللہ کی نشانیوں میں سے ہے کہ کہیں وہ نصیحت مانیں،'),
(981, 7, 27, 'اے آدم کی اولاد! خبردار! تمہیں شیطان فتنہ میں نہ ڈالے جیسا تمہارے ماں باپ کو بہشت سے نکالا اتروا دیئے ان کے لباس کہ ان کی شرم کی چیزیں انہیں نظر پڑیں، بیشک وہ اور اس کا کنبہ تمہیں وہاں سے دیکھتے ہیں کہ تم انہیں نہیں دیکھتے بیشک ہم نے شیطانوں کو ان کا دوست کیا ہے جو ایمان نہیں لاتے،'),
(982, 7, 28, 'اور جب کوئی بے حیائی کریں تو کہتے ہیں ہم نے اس پر اپنے باپ دادا کو پایا اور اللہ نے ہمیں اس کا حکم دیا تو فرماؤ بیشک اللہ بے حیائی کا حکم نہیں دیتا، کیا اللہ پر وہ بات لگاتے ہو جس کی تمہیں خبر نہیں،'),
(983, 7, 29, 'تم فرماؤ میرے رب نے انصاف کا حکم دیا ہے، اور اپنے منہ سیدھے کرو ہر نماز کے وقت اور اس کی عبادت کرو نرے (خالص) اس کے بندے ہوکر، جیسے اس نے تمہارا آغاز کیا ویسے ہی پلٹو گے'),
(984, 7, 30, 'ایک فرقے کو راہ دکھائی اور ایک فرقے کو گمراہی ثابت ہوئی انہوں نے اللہ کو چھوڑ کر شیطانوں کو والی بنایا اور سمجھتے یہ ہیں کہ وہ راہ پر ہیں،'),
(985, 7, 31, 'اے آدم کی اولاد! اپنی زینت لو جب مسجد میں جاؤ اور کھاؤ اور پیو اور حد سے نہ بڑھو، بیشک حد سے بڑھنے والے اسے پسند نہیں،'),
(986, 7, 32, 'تم فرماؤ کس نے حرام کی اللہ کی وہ زینت جو اس نے اپنے بندو ں کے لیے نکالی اور پاک رزق تم فرماؤ کہ وہ ایمان والوں کے لیے ہے دنیا میں اور قیامت میں تو خاص انہی کی ہے، ہم یونہی مفصل آیتیں بیان کرتے ہیں علم والوں کے لیے'),
(987, 7, 33, 'تم فرماؤ میرے رب نے تو بے حیائیاں حرام فرمائی ہیں جو ان میں کھلی ہیں اور جو چھپی اور گناہ اور ناحق زیادتی اور یہ کہ اللہ کا شریک کرو جس کی اس نے سند نہ اتاری اور یہ کہ اللہ پر وہ بات کہو جس کا علم نہیں رکھتے،'),
(988, 7, 34, 'اور ہر گروہ کا ایک وعدہ ہے تو جب ان کا وعدہ آئے گا ایک گھڑی نہ پیچھے ہو نہ آگے،'),
(989, 7, 35, 'اے آدم کی اولاد! اگر تمہارے پاس تم میں کے رسول آئیں میری آیتیں پڑھتے تو جو پرہیزگاری کرے اور سنورے تو اس پر نہ کچھ خوف اور نہ کچھ غم،'),
(990, 7, 36, 'اور جنہوں نے ہماری آیتیں جھٹلائیں اور ان کے مقابل تکبر کیا وہ دوز خی ہیں انہیں اس میں ہمیشہ رہنا،'),
(991, 7, 37, 'تو اس سے بڑھ کر ظالم کون جس نے اللہ پر جھوٹ باندھا یا اس کی آیتیں جھٹلائیں، انہیں ان کے نصیب کا لکھا پہنچے گا یہاں تک کہ جب ان کے پاس ہمارے بھیجے ہوئے ان کی جان نکالنے آئیں تو ان سے کہتے ہیں کہاں ہیں وہ جن کو تم اللہ کے سوا پوجتے تھے، کہتے ہیں وہ ہم سے گم گئے اور اپنی جانوں پر آپ گواہی دیتے ہیں کہ وہ کافر تھے،'),
(992, 7, 38, 'اللہ ان سے فرماتا ہے کہ تم سے پہلے جو اور جماعتیں جن اور آدمیوں کی آگ میں گئیں، انہیں میں جاؤ جب ایک گروہ داخل ہوتا ہے دوسرے پر لعنت کرتا ہے یہاں تک کہ جب سب اس میں جا پڑے تو پچھلے پہلوں کو کہیں گے اے رب ہمارے! انہوں نے ہم کو بہکایا تھا تو انہیں آگ کا دُونا عذاب دے، فرمائے گا سب کو دُونا ہے مگر تمہیں خبر نہیں'),
(993, 7, 39, 'اور پہلے پچھلوں سے کہیں گے تو تم کچھ ہم سے اچھے نہ رہے تو چکھو عذاب بدلہ اپنے کیے کا'),
(994, 7, 40, 'وہ جنہوں نے ہماری آیتیں جھٹلائیں اور ان کے مقابل تکبر کیا ان کے لیے آسمان کے دروازے نہ کھولے جائیں گے اور نہ وہ جنت میں داخل ہوں جب تک سوئی کے ناکے اونٹ داخل نہ ہو اور مجرموں کو ہم ایسا ہی بدلہ دیتے ہیں'),
(995, 7, 41, 'انہیں آگ ہی بچھونا اور آگ ہی اوڑھنا اور ظالموں کو ہم ایسا ہی بدلہ دیتے ہیں،'),
(996, 7, 42, 'اور وہ جو ایمان لائے اور طاقت بھر اچھے کام کیے ہم کسی پر طاقت سے زیادہ بوجھ نہیں رکھتے، وہ جنت والے ہیں، انہیں اس میں ہمیشہ رہنا،'),
(997, 7, 43, 'اور ہم نے ان کے سینوں سے کینے کھینچ لیے ان کے نیچے نہریں بہیں گی اور کہیں گے سب خوبیاں اللہ کو جس نے ہمیں اس کی راہ دکھائی اور ہم راہ نہ پاتے اگر اللہ ہمیں راہ نہ دکھاتا، بیشک ہمارے رب کے رسول حق لائے اور ندا ہوئی کہ یہ جنت تمہیں میراث ملی صلہ تمہارے اعمال کا،'),
(998, 7, 44, 'اور جنت والوں نے دوزخ والوں کو پکارا کہ ہمیں تو مل گیا جو سچا وعدہ ہم سے ہمارے رب نے کیا تھا تو کیا تم نے بھی پایا جو تمہارے رب نے سچا وعدہ تمہیں دیا تھا بولے، ہاں! اور بیچ میں منادی نے پکار دیا کہ اللہ کی لعنت ظالموں پر'),
(999, 7, 45, 'جو اللہ کی راہ سے روکتے ہیں اور اسے کجی چاہتے ہیں اور آخرت کا انکار رکھتے ہیں،'),
(1000, 7, 46, 'اور جنت و دوزخ کے بیچ میں ایک پردہ ہے اور اعراف پر کچھ مرد ہوں گے کہ دونوں فریق کو ان کی پیشانیوں سے پہچانیں گے اور وہ جنتیوں کو پکاریں گے کہ سلام تم پر یہ جنت میں نہ گئے اور اس کی طمع رکھتے ہیں،'),
(1001, 7, 47, 'اور جب ان کی آنکھیں دوزخیوں کی طرف پھریں گی کہیں گے اے ہمارے رب! ظالموں کے ساتھ نہ کر،'),
(1002, 7, 48, 'اور اعراف والے کچھ مردوں کو پکاریں گے جنہیں ان کی پیشانی سے پہچانتے ہیں کہیں گے تمہیں کیا کام آیا تمہارا جتھا اور وہ جو تم غرور کرتے تھے'),
(1003, 7, 49, 'کیا یہ ہیں وہ لوگ جن پر تم قسمیں کھاتے تھے کہ اللہ ان پر اپنی رحمت کچھ نہ کرے گا ان سے تو کہا گیا کہ جنت میں جاؤ نہ تم کو اندیشہ نہ کچھ غم،'),
(1004, 7, 50, 'اور دوزخی بہشتیوں کو پکاریں گے کہ ہمیں اپنے پانی کا فیض دو یا اس کھانے کا جو اللہ نے تمہیں دیا کہیں گے بیشک اللہ نے ان دونوں کو کافروں پر حرام کیا ہے'),
(1005, 7, 51, 'جنہوں نے اپنے دین کو کھیل تماشا بنایا اور دنیا کی زیست نے انہیں فریب دیا تو آج ہم انہیں چھوڑ دیں گے جیسا انہوں نے اس دن کے ملنے کا خیال چھوڑا تھا اور جیسا ہماری آیتوں سے انکار کرتے تھے،'),
(1006, 7, 52, 'اور بیشک ہم ان کے پاس ایک کتاب لائے جسے ہم نے ایک بڑے علم سے مفصل کیا ہدایت و رحمت ایمان والوں کے لیے،'),
(1007, 7, 53, 'کاہے کی راہ دیکھتے ہیں مگر اس کی کہ اس کتاب کا کہا ہوا انجام سامنے آئے جس دن اس کا بتایا انجام واقع ہوگا بول اٹھیں گے وہ جو اسے پہلے سے بھلائے بیٹھے تھے کہ بیشک ہمارے رب کے رسول حق لائے تھے تو ہیں کوئی ہمارے سفارشی جو ہماری شفاعت کریں یا ہم واپس بھیجے جائیں کہ پہلے کاموں کے خلاف کام کریں بیشک انہوں نے اپنی جانیں نقصان میں ڈالیں اور ان سے کھوئے گئے جو بہتان اٹھاتے تھے'),
(1008, 7, 54, 'بیشک تمہارا رب اللہ ہے جس نے آسمان اور زمین چھ دن میں بنائے پھر عرش پر استواء فرمایا جیسا اس کی شان کے لائق ہے رات دن کو ایک دوسرے سے ڈھانکتا ہے کہ جلد اس کے پیچھے لگا آتا ہے اور سورج اور چاند اور تاروں کو بنایا سب اس کے حکم کے دبے ہوئے، سن لو اسی کے ہاتھ ہے پیدا کرنا اور حکم دینا، بڑی برکت والا ہے اللہ رب سارے جہان کا،'),
(1009, 7, 55, 'اپنے رب سے دعا کرو گڑگڑاتے اور آہستہ، بیشک حد سے بڑھنے والے اسے پسند نہیں،'),
(1010, 7, 56, 'اور زمین میں فساد نہ پھیلاؤ اس کے سنورنے کے بعد اور اس سے دعا کرو ڈرتے اور طمع کرتے، بیشک اللہ کی رحمت نیکوں سے قریب ہے،'),
(1011, 7, 57, 'اور وہی ہے کہ ہوائیں بھیجتا ہے اس کی رحمت کے آگے مژدہ سناتی یہاں تک کہ جب اٹھا لائیں بھاری بادل ہم نے اسے کسی مردہ شہر کی طرف چلایا پھر اس سے پانی اتارا پھر اس سے طرح طرح کے پھل نکالے اسی طرح ہم مُردوں کو نکالیں گے کہیں تم نصیحت مانو،'),
(1012, 7, 58, 'اور جو اچھی زمین ہے اس کا سبزہ اللہ کے حکم سے نکلتا ہے اور جو خراب ہے اس میں نہیں نکلتا مگر تھوڑا بمشکل ہم یونہی طرح طرح سے آیتیں بیان کرتے ہیں ان کے لیے جو احسان مانیں،'),
(1013, 7, 59, 'بیشک ہم نے نوح کو اس کی قوم کی طرف بھیجا تو اس نے کہا میری قوم اللہ کو پوجو اسکے سوا تمہارا کوئی معبود نہیں بیشک مجھے تم پر بڑے دن کے عذاب کا ڈر ہے'),
(1014, 7, 60, 'اس کی قوم کے سردار بولے بیشک ہم تمہیں کھلی گمراہی میں دیکھتے ہیں،'),
(1015, 7, 61, 'کہا اے میری قوم مجھ میں گمراہی کچھ نہیں میں تو رب العالمین کا رسول ہوں،'),
(1016, 7, 62, 'تمہیں اپنے رب کی رسالتیں پہنچاتا اور تمہارا بھلا چاہتا اور میں اللہ کی طرف سے وہ علم رکھتا ہوں جو تم نہیں رکھتے،'),
(1017, 7, 63, 'اور کیا تمہیں اس کا اچنبا ہوا کہ تمہارے پاس رب کی طرف سے ایک نصیحت آئی تم میں کے ایک مرد کی معرفت کہ وہ تمہیں ڈرائے اور تم ڈرو اور کہیں تم پر رحم ہو،'),
(1018, 7, 64, 'تو انہوں نے اسے جھٹلایا تو ہم نے اسے اور جو اس کے ساتھ کشتی میں تھے نجات دی اور اپنی آیتیں جھٹلانے والوں کو ڈبو دیا، بیشک وہ اندھا گروہ تھا'),
(1019, 7, 65, 'اور عاد کی طرف ان کی برادری سے ہود کو بھیجا کہا اے میری قوم اللہ کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں، تو کیا تمہیں ڈر نہیں'),
(1020, 7, 66, 'اس کی قوم کے سردار بولے بیشک ہم تمہیں بیوقوف سمجھتے ہیں اور بیشک ہم تمہیں جھوٹوں میں گمان کرتے ہیں'),
(1021, 7, 67, 'کہا اے میری قوم مجھے بے وقوفی سے کیا علاقہ میں تو پروردگار عالم کا رسول ہوں،'),
(1022, 7, 68, 'تمہیں اپنے رب کی رسالتیں پہنچاتا ہوں اور تمہارا معتمد خیرخواہ ہوں'),
(1023, 7, 69, 'اور کیا تمہیں اس کا اچنبا ہوا کہ تمہارے پاس تمہارے رب کی طرف سے ایک نصیحت آئی تم میں سے ایک مرد کی معرفت کہ وہ تمہیں ڈرائے اور یاد کرو جب اس نے تمہیں قوم نوح کا جانشین کیا اور تمہارے بدن کا پھیلاؤ بڑھایا تو اللہ کی نعمتیں یاد کرو کہ کہیں تمہارا بھلا ہو،'),
(1024, 7, 70, 'بولے کیا تم ہمارے پاس اس لیے آئے ہو کہ ہم ایک اللہ کو پوجیں اور جو ہمارے باپ دادا پوجتے تھے، انہیں چھوڑ دیں تو لاؤ جس کا ہمیں وعدہ دے رہے ہو اگر سچے ہو،'),
(1025, 7, 71, 'کہا ضرور تم پر تمہارے رب کا عذاب اور غضب پڑ گیا کیا مجھ سے خالی ان ناموں میں جھگڑ رہے ہو جو تم نے اور تمہارے باپ دادا نے رکھ لیے اللہ نے ان کی کوئی سند نہ اتاری، تو راستہ دیکھو میں بھی تمہارے ساتھ دیکھتا ہوں،'),
(1026, 7, 72, 'تو ہم نے اسے اور اس کے ساتھ والوں کو اپنی ایک بڑی رحمت فرماکر نجات دی اور جو ہماری آیتیں جھٹلاتے تھے ان کی جڑ کاٹ دی اور وہ ایمان والے نہ تھے،'),
(1027, 7, 73, 'اور ثمود کی طرف ان کی برادری سے صالح کو بھیجا کہا اے میری قوم اللہ کو پوجو اس کے سوا تمہارا کوئی معبود نہیں، بیشک تمہارے پاس تمہارے رب کی طرف سے روشن دلیل آئی یہ اللہ کا ناقہ ہے تمہارے لیے نشانی، تو اسے چھوڑ دو کہ اللہ کی زمین میں کھائے اور اسے برائی سے ہاتھ نہ لگاؤ کہ تمہیں درد ناک عذاب آئے گا،'),
(1028, 7, 74, 'اور یاد کرو جب تم کو عاد کا جانشین کیا اور ملک میں جگہ دی کہ نرم زمین میں محل بناتے ہو اور پہاڑوں میں مکان تراشتے ہو تو اللہ کی نعمتیں یاد کرو اور زمین میں فساد مچاتے نہ پھرو،'),
(1029, 7, 75, 'اس کی قوم کے تکبر والے کمزور مسلمانوں سے بولے کیا تم جانتے ہو کہ صالح اپنے رب کے رسول ہیں، بولے وہ جو کچھ لے کے بھیجے گئے ہم اس پر ایمان رکھتے ہیں'),
(1030, 7, 76, 'متکبر بولے جس پر تم ایمان لائے ہمیں اس سے انکار ہے،'),
(1031, 7, 77, 'پس ناقہ کی کُوچیں کاٹ دیں اور اپنے رب کے حکم سے سرکشی کی اور بولے اے صالح! ہم پر لے آؤ جس کا تم سے وعد دے رہے ہو اگر تم رسول ہو،'),
(1032, 7, 78, 'تو انہیں زلزلے نے ا ٓلیا تو صبح کو اپنے گھروں میں اوندھے پڑے رہ گئے،'),
(1033, 7, 79, 'تو صالح نے ان سے منہ پھیرا اور کہا اے میری قوم! بیشک میں نے تمہیں اپنے رب کی رسالت پہنچادی اور تمہارا بھلا چاہا مگر تم خیر خواہوں کے غرضی (پسند کرنے والے) ہی نہیں،'),
(1034, 7, 80, 'اور لوط کو بھیجا جب اس نے اپنی قوم سے کہا کیا وہ بے حیائی کرتے ہو جو تم سے پہلے جہان میں کسی نے نہ کی،'),
(1035, 7, 81, 'تم تو مردوں کے پاس شہوت سے جاتے ہو عورتیں چھوڑ کر، بلکہ تم لوگ حد سے گزر گئے'),
(1036, 7, 82, 'اور اس کی قوم کا کچھ جواب نہ تھا مگر یہی کہنا کہ ان کو اپنی بستی سے نکال دو، یہ لوگ تو پاکیزگی چاہتے ہیں'),
(1037, 7, 83, 'تو ہم نے اسے اور اس کے گھر والوں کو نجات دی مگر اس کی عورت وہ رہ جانے والوں میں ہوئی'),
(1038, 7, 84, 'اور ہم نے ان پر ایک مینھ برسایا تو دیکھو کیسا انجام ہوا مجرموں کا'),
(1039, 7, 85, 'اور مدین کی طرف ان کی برادری سے شعیب کو بھیجا کہا اے میری قوم! اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں، بے شک تمہارے پاس تمہارے رب کی طرف سے روشن دلیل آئی تو ناپ اور تول پوری کرو اور لوگوں کی چیزیں گھٹا کر نہ دو اور زمین میں انتظام کے بعد فساد نہ پھیلاؤ، یہ تمہارا بھلا ہے اگر ایمان لاؤ،'),
(1040, 7, 86, 'اور ہر راستہ پر یوں نہ بیٹھو کہ راہگیروں کو ڈراؤ اور اللہ کی راہ سے انہیں روکو جو اس پر ایمان لائے اور اس میں کجی چاہو، اور یاد کرو جب تم تھوڑے تھے اس نے تمہیں بڑھادیا اور دیکھو فسادیوں کا کیسا انجام ہوا،'),
(1041, 7, 87, 'اور اگر تم میں ایک گروہ اس پر ایمان لایا جو میں لے کر بھیجا گیا اور ایک گروہ نے نہ مانا تو ٹھہرے رہو یہاں تک کہ اللہ ہم میں فیصلہ کرے اور اللہ کا فیصلہ سب سے بہتر'),
(1042, 7, 88, 'اس کی قوم کے متکبر سردار بولے، اے شعیب قسم ہے کہ ہم تمہیں اور تمہارے ساتھ والے مسلمانوں کو اپنی بستی سے نکا ل دیں گے یا تم ہمارے دین میں ا ٓجا ؤ کہا کیا اگرچہ ہم بیزار ہوں'),
(1043, 7, 89, 'ضرور ہم اللہ پر جھوٹ باندھیں گے اگر تمہارے دین میں آجائیں بعد اس کے کہ اللہ نے ہمیں اس سے بچایا ہے اور ہم مسلمانوں میں کسی کا کام نہیں کہ تمہارے دین میں آئے مگر یہ کہ اللہ چاہے جو ہمارا رب ہے، ہمارے رب کا علم ہر چیز کو محیط ہے، اللہ ہی پر بھروسہ کیا اے ہمارے رب! ہم میں اور ہماری قوم میں حق فیصلہ کر اور تیرا فیصلہ سب سے بہتر ہے،'),
(1044, 7, 90, 'اور اسکی قوم کے کافر سردار بولے کہ اگر تم شعیب کے تابع ہوئے تو ضرور نقصان میں رہو گے،'),
(1045, 7, 91, 'تو انہیں زلز لہ نے ا ٓ لیا تو صبح اپنے گھروں میں اوندھے پڑے رہ گئے'),
(1046, 7, 92, 'شعیب کو جھٹلانے والے گویا ان گھروں میں کبھی رہے ہی نہ تھے، شعیب کو جھٹلانے والے ہی تباہی میں پڑے،'),
(1047, 7, 93, 'تو شعیب نے ان سے منہ پھیرا اور کہا اے میری قوم! میں تمہیں رب کی رسالت پہنچا چکا اور تمہارے بھلے کو نصیحت کی تو کیونکر غم کروں کافروں کا،'),
(1048, 7, 94, 'اور نہ بھیجا ہم نے کسی بستی میں کوئی نبی مگر یہ کہ اس کے لوگوں کو سختی اور تکلیف میں پکڑا کہ وہ کسی طرح زاری کریں'),
(1049, 7, 95, 'پھر ہم نے برائی کی جگہ بھلائی بدل دی یہاں تک کہ وہ بہت ہوگئے اور بولے بیشک ہمارے باپ دادا کو رنج و راحت پہنچے تھے تو ہم نے انہیں اچانک ان کی غفلت میں پکڑ لیا'),
(1050, 7, 96, 'اور اگر بستیو ں والے ایمان لاتے اور ڈرتے تو ضرور ہم ان پر آسمان اور زمین سے برکتیں کھول دیتے مگر انہوں نے تو جھٹلایا تو ہم نے انہیں ان کے کیے پر گرفتار کیا'),
(1051, 7, 97, 'کیا بستیوں والے نہیں ڈرتے کہ ان پر ہمارا عذاب رات کو آئے جب وہ سوتے ہوں'),
(1052, 7, 98, 'یا بستیوں والے نہیں ڈرتے کہ ان پر ہمارا عذاب دن چڑھے آئے جب وہ کھیل رہے ہوں'),
(1053, 7, 99, 'کیا اللہ کی خفی تدبیر سے بے خبر ہیں تو اللہ کی خفی تدبیر سے نذر نہیں ہوتے مگر تباہی والے'),
(1054, 7, 100, 'اور کیا وہ جو زمین کے ما لکوں کے بعد اس کے وارث ہوئے انہیں اتنی ہدایت نہ ملی کہ ہم چاہیں تو انہیں ان کے گناہوں پر آ فت پہنچائیں اور ہم ان کے دلوں پر مہر کرتے ہیں کہ وہ کچھ نہیں سنتے'),
(1055, 7, 101, 'یہ بستیاں ہیں جن کے احوال ہم تمہیں سناتے ہیں اور بیشک ان کے پاس ان کے رسول روشن دلیلیں لے کر آئے تو وہ اس قابل نہ ہوئے کہ وہ اس پر ایمان لاتے جسے پہلے جھٹلاچکے تھے اللہ یونہی چھاپ لگادیتا ہے کا فروں کے دلوں پر'),
(1056, 7, 102, 'اور ان میں اکثر کو ہم نے قول کا سچا نہ پایا اور ضرور ان میں اکثر کو بے حکم ہی پایا،'),
(1057, 7, 103, 'پھر ان کے بعد ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ فرعون اور اس کے درباریوں کی طرف بھیجا تو انہوں نے ان نشانیوں پر زیادتی کی تو دیکھو کیسا انجام ہوا مفسدوں کا،'),
(1058, 7, 104, 'اور موسیٰ نے کہا اے فرعون! میں پرور دگا ر عالم کا رسول ہوں،'),
(1059, 7, 105, 'مجھے سزاوار ہے کہ اللہ پر نہ کہوں مگر سچی بات میں تم سب کے پاس تمہارے رب کی طرف سے نشانی لےکر آیا ہوں تو بنی اسرائیل کو میرے ساتھ چھوڑ دے'),
(1060, 7, 106, 'بولا اگر تم کوئی نشانی لے کر آئے ہو تو لاؤ اگر سچے ہو،'),
(1061, 7, 107, 'تو موسیٰ نے اپنا عصا ڈال دیا وہ فورا ً ایک ظاہر اژدہا ہوگیا'),
(1062, 7, 108, 'اور اپنا ہاتھ گریبان میں ڈال کر نکا لا تو وہ دیکھنے والوں کے سامنے جگمگانے لگا'),
(1063, 7, 109, 'قوم فرعون کے سردار بولے یہ تو ایک علم والا جادوگر ہے'),
(1064, 7, 110, 'تمہیں تمہارے ملک سے نکا لا چاہتا ہے تو تمہارا کیا مشورہ ہے،'),
(1065, 7, 111, 'بولے انہیں اور ان کے بھائی کو ٹھہرا اور شہروں میں لوگ جمع کرنے والے بھیج دے،'),
(1066, 7, 112, 'کہ ہر علم والے جادوگر کو تیرے پاس لے آئیں'),
(1067, 7, 113, 'اور جادوگر فرعون کے پاس آئے بولے کچھ ہمیں انعام ملے گا اگر ہم غالب آئیں،'),
(1068, 7, 114, 'بولا ہاں اور اس وقت تم مقرب ہوجا ؤ گے،'),
(1069, 7, 115, 'بولے اے موسیٰ یا تو آپ ڈالیں یا ہم ڈالنے والے ہوں'),
(1070, 7, 116, 'کہا تمہیں ڈالو جب انہوں نے ڈالا لوگوں کی آنکھوں پر جادو کردیا اور انہیں ڈرایا اور بڑا جادو لائے،'),
(1071, 7, 117, 'اور ہم نے موسیٰ کو وحی فرمائی کہ اپنا عصا ڈال تو ناگاہ ان کی بناوٹوں کو نگلنے لگا'),
(1072, 7, 118, 'تو حق ثابت ہوا اور ان کا کام باطل ہوا،'),
(1073, 7, 119, 'تو یہاں وہ مغلوب پڑے اور ذلیل ہوکر پلٹے'),
(1074, 7, 120, 'اور جادوگر سجدے میں گرادیے گئے'),
(1075, 7, 121, 'بولے ہم ایمان لائے جہان کے رب پر،'),
(1076, 7, 122, 'جو رب ہے موسیٰ اور ہارون کا،'),
(1077, 7, 123, 'فرعون بولا تم اس پر ایمان لے آئے قبل اس کے کہ میں تمہیں اجازت دوں، یہ تو بڑا جعل (فریب) ہے جو تم سب نے شہر میں پھیلایا ہے کہ شہر والوں کو اس سے نکال دو تو اب جان جاؤ گے'),
(1078, 7, 124, 'قسم ہے کہ میں تمہارے ایک طرف کہ ہاتھ اور دوسری طرف کے پاؤں کاٹوں گا پھر تم سب کو سُو لی دوں گا'),
(1079, 7, 125, 'بولے ہم اپنے رب کی طرف پھرنے والے ہیں'),
(1080, 7, 126, 'اور تجھے ہمارا کیا برا لگا یہی نہ کہ ہم اپنے رب کی نشانیوں پر ایمان لائے جب وہ ہمارے پاس آئیں، اے رب ہمارے! ہم پر صبر انڈیل دے اور ہمیں مسلمان اٹھا'),
(1081, 7, 127, 'اور قوم فرعون کے سردار بولے کیا تو موسیٰ اور اس کی قوم کو اس لیے چھوڑ تا ہے کہ وہ زمین میں فساد پھیلائیں اور موسیٰ تجھے اور تیرے ٹھہرائے ہوئے معبودوں کو چھوڑدے بولا اب ہم ان کے بیٹوں کو قتل کریں گے اور ان کی بیٹیاں زندہ رکھیں گے اور ہم بیشک ان پر غالب ہیں'),
(1082, 7, 128, 'موسیٰ نے اپنی قوم سے فرمایا اللہ کی مدد چاہو اور صبر کرو بیشک زمین کا مالک اللہ ہے اپنے بندوں میں جسے چاہے وارث بنائے اور آخر میدان پرہیزگاروں کے ہاتھ ہے'),
(1083, 7, 129, 'بولے ہم ستائے گئے آپ کے آنے سے پہلے اور آپ کے تشریف لانے کے بعد کہا قریب ہے کہ تمہارا رب تمہارے دشمن کو ہلاک کرے اور اس کی جگہ زمین کا مالک تمہیں بنائے پھر دیکھے کیسے کام کرتے ہو'),
(1084, 7, 130, 'اور بیشک ہم نے فرعون والوں کو برسوں کے قحط اور پھلوں کے گھٹانے سے پکڑا کہ کہیں وہ نصیحت مانیں'),
(1085, 7, 131, 'تو جب انہیں بھلائی ملتی کہتے یہ ہمارے لیے ہے اور جب برائی پہنچتی تو موسیٰ اور اس کے ساتھ والوں سے بدشگونی لیتے سن لو ان کے نصیبہ کی شامت تو اللہ کے یہاں ہے لیکن ان میں اکثر کو خبر نہیں،'),
(1086, 7, 132, 'اور بولے تم کیسی بھی نشانی لے کر ہمارے پاس آؤ کہ ہم پر اس سے جادو کرو ہم کسی طرح تم پر ایمان لانے والے نہیں'),
(1087, 7, 133, 'تو بھیجا ہم نے ان پر طوفان اور ٹڈی اور گھن (یا کلنی یا جوئیں) اور مینڈک اور خون جدا جدا نشانیاں تو انہوں نے تکبر کیا اور وہ مجرم قوم تھی'),
(1088, 7, 134, 'اور جب ان پر عذاب پڑتا کہتے اے موسیٰ ہمارے لیے اپنے رب سے دعا کرو اس عہد کے سبب جو اس کا تمہارے پاس ہے بیشک اگر تم ہم پر عذاب اٹھادو گے تو ہم ضرور تم پر ایمان لائیں گے اور بنی اسرائیل کو تمہارے ساتھ کردیں گے،'),
(1089, 7, 135, 'پھر جب ہم ان سے عذاب اٹھالیتے ایک مدت کے کیے جس تک انہیں پہنچنا ہے جبھی وہ پھر جاتے،'),
(1090, 7, 136, 'تو ہم نے ان سے بدلہ لیا تو انہیں دریا میں ڈبو دیا اس لیے کہ ہماری آیتیں جھٹلاتے اور ان سے بے خبر تھے'),
(1091, 7, 137, 'اور ہم نے اس قوم کو جو دبا لی گئی تھی اس زمین کے پورب پچھم کا وارث کیا جس میں ہم نے برکت رکھی اور تیرے رب کا اچھا وعدہ بنی اسرائیل پر پورا ہوا، بدلہ ان کے صبر کا، اور ہم نے برباد کردیا جو کچھ فرعون اور اس کی قوم بناتی اور جو چنائیاں اٹھاتے (تعمیر کرتے) تھے،'),
(1092, 7, 138, 'اور ہم نے بنی اسرائیل کو دریا پار اتارا تو ان کا گزر ایک ایسی قوم پر ہوا کہ اپنے بتوں کے آگے آسن مارے (جم کر بیٹھے) تھے بولے اے موسیٰ! ہمیں ایک خدا بنادے جیسا ان کے لیے اتنے خدا ہیں، بولا تم ضرور جا ہل لوگ ہو،'),
(1093, 7, 139, 'یہ حال تو بربادی کا ہے جس میں یہ لوگ ہیں اور جو کچھ کررہے ہیں نرا باطل ہے،'),
(1094, 7, 140, 'کہا کیا اللہ کے سوا تمہارا اور کوئی خدا تلاش کروں حالانکہ اس نے تمہیں زمانے بھر پر فضیلت دی'),
(1095, 7, 141, 'اور یاد کرو جب ہم نے تمہیں فرعون والوں سے نجات بحشی کہ تمہیں بری مار دیتے تمہارے بیٹے ذبح کرتے اور تمہاری بیٹیاں باقی رکھتے، اور اس میں رب کا بڑا فضل ہوا'),
(1096, 7, 142, 'اور ہم نے موسیٰ سے تیس رات کا وعدہ فرمایا اور ان میں دس اور بڑھا کر پوری کیں تو اس کے رب کا وعدہ پوری چالیس رات کا ہوا اور موسیٰ نے اپنے بھائی ہارون سے کہا میری قوم پر میرے نائب رہنا اور اصلاح کرنا اور فسادیوں کی راہ کو دخل نہ دینا،'),
(1097, 7, 143, 'اور جب موسیٰ ہمارے وعدہ پر حاضر ہوا اور اس سے اس کے رب نے کلام فرمایا عرض کی اے رب میرے! مجھے اپنا دیدار دکھا کہ میں تجھے دیکھوں فرمایا تو مجھے ہر گز نہ دیکھ سکے گا ہاں اس پہاڑ کی طرف دیکھ یہ اگر اپنی جگہ پر ٹھہرا رہا تو عنقریب تو مجھے دیکھ لے گا پھر جب اس کے رب نے پہاڑ پر اپنا نو ر چمکایا اسے پاش پاش کردیا اور موسیٰ گرا بیہوش پھر جب ہوش ہوا بولا پاکی ہے تجھے میں تیری طرف رجوع لایا اور میں سب سے پہلا مسلمان ہوں'),
(1098, 7, 144, 'فرمایا اے موسیٰ میں نے تجھے لوگوں سے چن لیا اپنی رسالتوں اور اپنے کلام سے، تو لے جو میں نے تجھے عطا فرمایا اور شکر والوں میں ہو،'),
(1099, 7, 145, 'اور ہم نے اس کے لیے تختیوں میں لکھ دی ہر چیز کی نصیحت اور ہر چیز کی تفصیل، اور فرمایا اے موسیٰ اسے مضبوطی سے لے اور اپنی قوم کو حکم دے کر اس کی اچھی باتیں اختیار کریں عنقریب میں تمہیں دکھاؤں گا بے حکموں کا گھر'),
(1100, 7, 146, 'اور میں اپنی آیتوں سے انہیں پھیردوں گا جو زمین میں ناحق اپنی بڑا ئی چاہتے ہیں اور اگر سب نشانیاں دیکھیں ان پر ایمان نہ لائیں اور اگر ہدایت کی راہ دیکھیں اس میں چلنا پسند نہ کریں اور گمراہی کا راستہ نظر پڑے تو اس میں چلنے کو موجود ہوجائیں، یہ اس لیے کہ انہوں نے ہماری آیتیں جھٹلائیں اور ان سے بے خبر بنے،'),
(1101, 7, 147, 'اور جنہوں نے ہماری آیتیں اور آخرت کے دربار کو جھٹلایا ان کا سب کیا دھرا اَکارت گیا، انہیں کیا بدلہ ملے گا مگر وہی جو کرتے تھے،'),
(1102, 7, 148, 'اور موسیٰ کے بعد اس کی قوم اپنے زیوروں سے ایک بچھڑا بنا بیٹھی بے جان کا دھڑ گائے کی طرف آواز کرتا، کیا نہ دیکھا کہ وہ ان سے نہ بات کرتا ہے اور نہ انہیں کچھ راہ بتائے اسے لیا اور وہ ظالم تھے'),
(1103, 7, 149, 'اور جب پچھتائے اور سمجھے کہ ہم بہکے بولے اگر ہمارا رب ہم پر مہر ہ کرے اور ہمیں نہ بخشے تو ہم تباہ ہوئے،'),
(1104, 7, 150, 'اور جب موسیٰ اپنی قوم کی طرف پلٹا غصہ میں بھرا جھنجلایا ہوا کہا تم نے کیا بری میری جانشینی کی میرے بعد کیا تم نے اپنے رب کے حکم سے جلدی کی اور تختیاں ڈال دیں اور اپنے بھائی کے سر کے بال پکڑ کر اپنی طرف کھینچنے لگا کہا اے میرے ماں جائے قوم نے مجھے کمزور سمجھا اور قریب تھا کہ مجھے مار ڈالیں تو مجھ پر دشمنوں کو نہ ہنسا اور مجھے ظالموں میں نہ ملا'),
(1105, 7, 151, 'عرض کی اے میرے رب! مجھے اور میرے بھائی کو بخش دے اور ہمیں اپنی رحمت کے اندر لے لے اور تو سب مہر والوں سے بڑھ کر مہر والا'),
(1106, 7, 152, 'بیشک وہ جو بچھڑا لے بیٹھے عنقریب انہیں ان کے رب کا غضب اور ذلت پہنچناہے دنیا کی زندگی میں، اور ہم ایسی ہی بدلہ دیتے ہیں بہتان ہایوں (باندھنے والوں) کو،'),
(1107, 7, 153, 'اور جنہوں نے برائیاں کیں اور ان کے بعد توبہ کی اور ایمان لائے تو اس کے بعد تمہارا رب بخشنے والا مہربان ہے'),
(1108, 7, 154, 'اور جب موسیٰ کا غصہ تھما تختیاں اٹھالیں اور ان کی تحریر میں ہدایت اور رحمت ہے ان کے لیے جو اپنے رب سے ڈرتے ہیں،'),
(1109, 7, 155, 'اور موسیٰ نے اپنی قوم سے سترّ ۷۰، مرد ہمارے وعدہ کے لیے چنے پھر جب انہیں زلزلہ نے لیا موسیٰ نے عرض کی اے رب میرے! تو چاہتا تو پہلے ہی انہیں اور مجھے ہلاک کردیتا کیا تو ہمیں اس کام پر ہلاک فرمائے گا جو ہمارے بے عقلوں نے کیا وہ نہیں مگر تیرا آزمانا، تو اس سے بہکائے جسے چاہے اور راہ دکھائے جسے چاہے تو ہمارا مولیٰ ہے تو ہمیں بخش دے اور ہم پر مہر کر اور تو سب سے بہتر بخشنے والا ہے،'),
(1110, 7, 156, 'اور ہمارے لیے اس دنیا میں بھلائی لکھ اور آخرت میں بیشک ہم تیری طرف رجوع لائے، فرمایا میرا عذاب میں جسے چاہوں دوں اور میری رحمت ہر چیز کو گھیرے ہے تو عنقریب میں نعمتوں کو ان کے لیے لکھ دوں گا جو ڈرتے اور زکوٰة دیتے ہیں اور وہ ہماری آیتوں پر ایمان لاتے ہیں،'),
(1111, 7, 157, 'وہ جو غلامی کریں گے اس رسول بے پڑھے غیب کی خبریں دینے والے کی جسے لکھا ہوا پائیں گے اپنے پاس توریت اور انجیل میں وہ انہیں بھلائی کا حکم دے گا اور برائی سے منع کرے گا اور ستھری چیزیں ان کے لیے حلال فرمائے گا اور گندی چیزیں ان پر حرام کرے گا اور ان پر سے وہ بوجھ اور گلے کے پھندے جو ان پر تھے اتا رے گا، تو وہ جو اس پر ایمان لائیں اور اس کی تعظیم کریں اور اسے مدد دیں اور اس نور کی پیروی کریں جو اس کے ساتھ اترا وہی بامراد ہوئے،'),
(1112, 7, 158, 'تم فرماؤ اے لوگو! میں تم سب کی طرف اس اللہ کا رسول ہوں کہ آسمانوں اور زمین کی بادشاہی اسی کو ہے اس کے سوا کوئی معبود نہیں جلائے اور مارے تو ایمان لاؤ اللہ اور اس کے رسول بے پڑھے غیب بتانے والے پر کہ اللہ اور اس کی باتوں پر ایمان لاتے ہیں اور ان کی غلامی کرو کہ تم راہ پاؤ،'),
(1113, 7, 159, 'اور موسیٰ کی قوم سے ایک گروہ ہے کہ حق کی راہ بتا تا اور اسی سے انصاف کرتا،'),
(1114, 7, 160, 'اور ہم نے انہیں بانٹ دیا بارہ قبیلے گروہ گروہ، اور ہم نے وحی بھیجی موسیٰ کو جب اس سے اس کی قوم نے پانی ما نگا کہ اس پتھر پر اپنا عصا مارو تو اس میں سے بارہ چشمے پھوٹ نکلے ہر گروہ نے اپنا گھاٹ پہچان لیا، اور ہم نے ان پر ابر کا سائبان کیا اور ان پر من و سلویٰ اتارا، کھاؤ ہماری دی ہوئی پاک چیزیں اور انہوں نے ہمارا کچھ نقصان نہ کیا لیکن اپنی ہی جانوں کا برا کرتے ہیں،'),
(1115, 7, 161, 'اور یاد کرو جب ان سے فرمایا گیا اس شہر میں بسو اور اس میں جو چاہو کھاؤ اور کہو گناہ اترے اور دروازے میں سجدہ کرتے داخل ہو ہم تمہارے گناہ بخش دیں گے، عنقریب نیکوں کو زیادہ عطا فرمائیں گے،'),
(1116, 7, 162, 'تو ان میں کے ظالموں نے بات بدل دی اس کے خلاف جس کا انہیں حکم تھا تو ہم نے ان پر آسمان سے عذاب بھیجا بدلہ ان کے ظلم کا'),
(1117, 7, 163, 'اور ان سے حال پوچھو اس بستی کا کہ دریا کنارے تھی جب وہ ہفتے کے بارے میں حد سے بڑھتے جب ہفتے کے دن ان کی مچھلیاں پانی پر تیرتی ان کے سامنے آتیں اور جو دن ہفتے کا نہ ہوتا نہ آتیں اس طرح ہم انہیں آزمانتے تھے ان کی بے حکمی کے سبب،'),
(1118, 7, 164, 'اور جب ان میں سے ایک گروہ نے کہا کیوں نصیحت کرتے ہو ان لوگوں کو جنہیں اللہ ہلاک کرنے والا ہے یا انہیں سخت عذاب دینے والا، بولے تمہارے رب کے حضور معذرت کو اور شاید انہیں ڈر ہو'),
(1119, 7, 165, 'پھر جب بھلا بیٹھے جو نصیحت انہیں ہوئی تھی ہم نے بچالیے وہ جو برائی سے منع کرتے تھے اور ظالموں کو برے عذاب میں پکڑا بدلہ ان کی نافرمانی کا،'),
(1120, 7, 166, 'پھر جب انہوں نے ممانعت کے حکم سے سرکشی کی ہم نے ان سے فرمایا ہوجاؤ بند ر دھتکارے ہوئے'),
(1121, 7, 167, 'اور جب تمہارے رب نے حکم سنادیا کہ ضرور قیامت کے دن تک ان پر ایسے کو بھیجتا رہوں گا جو انہیں بری مار چکھائے بیشک تمہارا رب ضرور جلد عذاب والا ہے اور بیشک وہ بخشنے والا مہربان ہے'),
(1122, 7, 168, 'اور انہیں ہم نے زمین میں متفرق کردیا گروہ گروہ، ان میں کچھ نیک ہیں اور کچھ اور طرح کے اور ہم نے انہیں بھلائیوں اور برائیوں سے آزمایا کہ کہیں وہ رجوع لائیں'),
(1123, 7, 169, 'پھر ان کی جگہ ان کے بعد وہ ناخلف آئے کہ کتاب کے وارث ہوئے اس دنیا کا مال لیتے ہیں اور کہتے اب ہماری بخشش ہوگی اور اگر ویسا ہی مال ان کے پاس اور آئے تو لے لیں کیا ان پر کتاب میں عہد نہ لیا گیا کہ اللہ کی طرف نسبت نہ کریں مگر حق اور انہوں نے اسے پڑھا اور بیشک پچھلا گھر بہتر ہے پرہیزگاروں کو تو کیا تمہیں عقل نہیں،'),
(1124, 7, 170, 'اور وہ جو کتاب کو مضبوط تھامتے ہیں اور انہوں نے نماز قائم رکھی، ہم نیکوں کا نیگ (اجر) نہیں گنواتے،'),
(1125, 7, 171, 'اور جب ہم نے پہاڑ ان پر اٹھایا گویا وہ سائبان ہے اور سمجھے کہ وہ ان پر گر پڑے گا تو جو ہم نے تمہیں دیا زور سے اور یاد کرو جو اس میں ہے کہ کہیں تم پرہیزگار ہو،'),
(1126, 7, 172, 'اور اے محبوب! یاد کرو جب تمہارے رب نے اولاد آدم کی پشت سے ان کی نسل نکالی اور انہیں خود ان پر گواہ کیا، کیا میں تمہارا رب نہیں سب بولے کیوں نہیں ہم گواہ ہوئے کہ کہیں قیامت کے دن کہو کہ ہمیں اس کی خبر نہ تھی'),
(1127, 7, 173, 'یا کہو کہ شرک تو پہلے ہمارے باپ دادا نے کیا اور ہم ان کے بعد بچے ہوئے تو کیا تو ہمیں اس پر ہلاک فرمائے گا جو اہل باطل نے کیا'),
(1128, 7, 174, 'اور ہم اسی طرح آیتیں رنگ رنگ سے بیان کرتے ہیں اور اس لیے کہ کہیں وہ پھر آئیں'),
(1129, 7, 175, 'اور اے محبوب! انہیں اس کا احوال سناؤ جسے ہم نے اپنی آیتیں دیں تو وہ ان سے صاف نکل گیا تو شیطان اس کے پیچھے لگا تو گمراہوں میں ہوگیا،'),
(1130, 7, 176, 'اور ہم چاہتے تو آیتوں کے سبب اسے اٹھالیتے مگر وہ تو زمین پکڑ گیا اور اپنی خواہش کا تابع ہوا تو اس کا حال کتے کی طرح ہے تو اس پر حملہ کرے تو زبان نکالے اور چھوڑ دے تو زبان نکالے یہ حال ہے ان کا جنہوں نے ہماری آیتیں جھٹلائیں تو تم نصیحت سناؤ کہ کہیں وہ دھیان کریں،'),
(1131, 7, 177, 'کیا بری کہاوت ہے ان کی جنہوں نے ہماری آیتیں جھٹلائیں اور اپنی ہی جان کا برا کرتے تھے،'),
(1132, 7, 178, 'جسے اللہ راہ دکھائے تو وہی راہ پر ہے، اور جسے گمراہ کرے تو وہی نقصان میں رہے،'),
(1133, 7, 179, 'اور بیشک ہم نے جہنم کے لیے پیدا کیے بہت جن اور آدمی اور دل رکھتے ہیں جن میں سمجھ نہیں اور وہ آنکھیں جن سے دیکھتے نہیں اور وہ کان جن سے سنتے نہیں وہ چوپایوں کی طرح ہیں بلکہ ان سے بڑھ کر گمراہ وہی غفلت میں پڑے ہیں،'),
(1134, 7, 180, 'اور اللہ ہی کے ہیں بہت اچھے نام تو اسے ان سے پکارو اور انہیں چھوڑ دو جو اس کے ناموں میں حق سے نکلتے ہیں وہ جلد اپنا کیا پائیں گے،'),
(1135, 7, 181, 'اور ہمارے بنائے ہوؤں میں ایک گروہ وہ ہے کہ حق بتائیں اور اس پر انصاف کریں'),
(1136, 7, 182, 'اور جنہوں نے ہماری آیتیں جھٹلائیں جلد ہم انہیں آہستہ آہستہ عذاب کی طرف لے جائیں گے جہاں سے انہیں خبر نہ ہوگی'),
(1137, 7, 183, 'اور میں انہیں ڈھیل دوں گا بیشک میری خفیہ تدبیر بہت پکی ہے'),
(1138, 7, 184, 'کیا سوچتے نہیں کہ ان کے صاحب کو جنوں سے کچھ علاقہ نہیں، وہ تو صا ف ڈر سنانے والے ہیں'),
(1139, 7, 185, 'کیا انہو ں نے نگاہ نہ کی آسمانوں اور زمین کی سلطنت میں اور جو جو چیز اللہ نے بنائی اور یہ کہ شاید ان کا وعدہ نزدیک آگیا ہو تو اس کے بعد اور کونسی بات پر یقین لائیں گے'),
(1140, 7, 186, 'جسے اللہ گمراہ کرے اسے کوئی راہ دکھانے والا نہیں اور انہیں چھوڑتا ہے کہ اپنی سرکشی میں بھٹکا کریں،'),
(1141, 7, 187, 'تم سے قیامت کو پوچھتے ہیں کہ وہ کب کو ٹھہری ہے تم فرماؤ اس کا علم تو میرے رب کے پاس ہے، اسے وہی اس کے وقت پر ظاہر کرے گا بھاری پڑ رہی ہے آسمانوں او رزمین میں، تم پر نہ آئے گی مگر اچانک، تم سے ایسا پوچھتے ہیں گویا تم نے اسے خوب تحقیق کر رکھا ہے، تم فرماؤ اس کا علم تو اللہ ہی کے اس ہے لیکن بہت لوگ جانتے نہیں'),
(1142, 7, 188, 'تم فرماؤ میں اپنی جان کے بھلے برے کا خودمختار نہیں مگر جو اللہ چاہے اور اگر میں غیب جان لیا کرتا تو یوں ہوتا کہ میں نے بہت بھلائی جمع کرلی، اور مجھے کوئی برائی نہ پہنچی میں تو یہی ڈر اور خوشی سنانے والا ہوں انہیں جو ایمان رکھتے ہیں،'),
(1143, 7, 189, 'وہی ہے جس نے تمہیں ایک جان سے پیدا کیا اور اسی میں سے اس کا جوڑا بنایا کہ اس سے چین پائے پھر جب مرد اس پر چھایا اسے ایک ہلکا سا پیٹ رہ گیا تو اسے لیے پھراکی پھر جب بوجھل پڑی دونوں نے اپنے رب سے دعا کی ضرور اگر تو ہمیں جیسا چاہیے بچہ دے گا تو بیشک ہم شکر گزار ہوں گے،'),
(1144, 7, 190, 'پھر جب اس نے انہیں جیسا چاہیے بچہ عطا فرمایا انہوں نے اس کی عطا میں اس کے ساجھی ٹھہرائے تو اللہ کو برتری ہے ان کے شرک سے'),
(1145, 7, 191, 'کیا اسے شریک کرتے ہیں جو کچھ نہ بنائے اور وہ خود بنائے ہوئے ہیں،'),
(1146, 7, 192, 'اور نہ وہ ان کو کوئی مدد پہنچاسکیں اور نہ اپنی جانو ں کی مدد کریں'),
(1147, 7, 193, 'اور اگر تم انہیں راہ کی طرف بلاؤ تو تمہارے پیچھے نہ آئیں تم پر ایک سا ہے چاہے انہیں پکارو یا چپ رہو'),
(1148, 7, 194, 'بیشک وہ جن کو تم اللہ کے سوا پوجتے ہو تمہاری طرح بندے ہیں تو انہیں پکارو پھر وہ تمہیں جواب دیں اگر تم سچے ہو،'),
(1149, 7, 195, 'کیا ان کے پاؤں ہیں جن سے چلیں یا ان کے ہاتھ ہیں جن سے گرفت کریں یا ان کے آنکھیں ہیں جن سے دیکھیں یا ان کے کان ہیں جن سے سنیں تم فرماؤ کہ اپنے شریکوں کو پکارو اور مجھ پر داؤ چلو اور مجھے مہلت نہ دو'),
(1150, 7, 196, 'بیشک میرا والی اللہ ہے جس نے کتاب ا تاری اور وہ نیکوں کو دوست رکھتا ہے'),
(1151, 7, 197, 'اور جنہیں اس کے سوا پوجتے ہو وہ تمہاری مدد نہیں کرسکتے، اور نہ خود اپنی مدد کریں'),
(1152, 7, 198, 'اور اگر تم انہیں راہ کی طرف بلاؤ تو نہ سنیں اور تو انہیں دیکھے کہ وہ تیری طرف دیکھ رہے ہیں اور انہیں کچھ بھی نہیں سوجھتا،'),
(1153, 7, 199, 'اے محبوب! معاف کرنا اختیار کرو اور بھلائی کا حکم دو اور جاہلوں سے منہ پھیرلو،'),
(1154, 7, 200, 'اور اے سننے والے اگر شیطان تجھے کوئی کونچا دے (کسی برے کام پر اکسائے) تو اللہ کی پناہ مانگ بیشک وہی سنتا جانتا ہے،'),
(1155, 7, 201, 'بیشک وہ جو ڈر والے ہیں جب انہیں کسی شیطانی خیال کی ٹھیس لگتی ہے ہوشیار ہوجاتے ہیں اسی وقت ان کی آنکھیں کھل جاتی ہیں'),
(1156, 7, 202, 'اور وہ جو شیطانوں کے بھائی ہیں شیطان انہیں گمراہی میں کھینچتے ہیں پھر کمی نہیں کرتے،'),
(1157, 7, 203, 'اور اے محبوب! جب تم ان کے پاس کوئی آیت نہ لاؤ تو کہتے ہیں تم نے دل سے کیوں نہ بنائی تم فرما ؤ میں تو اسی کی پیروی کرتا ہوں جو میری طرف میرے رب سے وحی ہوتی ہے یہ تمہارے رب کی طرف سے آنکھیں کھولنا ہے اور ہدایت اور رحمت مسلمانوں کے لیے،'),
(1158, 7, 204, 'اور جب قرآن پڑھا جائے تو اسے کان لگا کر سنو اور خاموش رہو کہ تم پر رحم ہو'),
(1159, 7, 205, 'اور اپنے رب کو اپن ے دل میں یاد کرو زاری اور ڈر سے اور بے آواز نکلے زبان سے صبح اور شام اور غافلوں میں نہ ہونا،'),
(1160, 7, 206, 'بیشک وہ جو تیرے رب کے پاس ہیں اس کی عبادت سے تکبر نہیں کرتے اور اس کی پاکی بولتے اور اسی کو سجدہ کرتے ہیں السجدة ۔۵');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'اے محبوب! تم سے غنیمتوں کو پوچھتے ہیں تم فرماؤ غنیمتوں کے مالک اللہ اور رسول ہیں تو اللہ ڈرو اور اپنے آ پس میں میل (صلح صفائی) رکھو اور اللہ اور رسول کا حکم مانو اگر ایمان رکھتے ہو،'),
(1162, 8, 2, 'ایمان والے وہی ہیں کہ جب اللہ یاد کیا جائے ان کے دل ڈر جائیں اور جب ان پر اس کی آیتیں پڑھی جائیں ان کا ایمان ترقی پائے اور اپنے رب ہی پر بھروسہ کریں'),
(1163, 8, 3, 'وہ جو نماز قائم رکھیں اور ہمارے دیے سے کچھ ہماری راہ میں خرچ کریں،'),
(1164, 8, 4, 'یہی سچے مسلمان ہیں، ان کے لیے درجے ہیں ان کے رب کے پاس اور بخشش ہے اور عزت کی روزی'),
(1165, 8, 5, 'جس طرح اے محبوب! تمہیں تمہارے رب نے تمہارے گھر سے حق کے ساتھ برآمد کیا اور بیشک مسلمانوں کا ایک گروہ اس پر ناخوش تھا'),
(1166, 8, 6, 'سچی بات میں تم سے جھگڑتے تھے بعد اس کے کہ ظاہر ہوچکی گویا وہ آنکھوں دیکھی موت کی طرف ہانکے جاتے ہیں'),
(1167, 8, 7, 'اور یاد کرو جب اللہ نے تمہیں وعدہ دیا تھا کہ ان دونوں گروہوں میں ایک تمہارے لیے ہے اور تم یہ چاہتے تھے کہ تمہیں وہ ملے جس میں کانٹے کا کھٹکا نہیں (کوئی نقصان نہ ہو) اور اللہ یہ چاہتا تھا کہ اپنے کلام سے سچ کو سچ کر دکھائے اور کافروں کی جڑ کا ٹ دے'),
(1168, 8, 8, 'کہ سچ کو سچ کرے اور جھوٹ کو جھوٹا پڑے برا مانیں مجرم،'),
(1169, 8, 9, 'جب تم اپنے رب سے فریا د کرتے تھے تو اس نے تمہاری سن لی کہ میں تمہیں مدد دینے والا ہوں ہزاروں فرشتوں کی قطار سے'),
(1170, 8, 10, 'اور یہ تو اللہ نے کیا مگر تمہاری خوشی کو اور اس لیے کہ تمہارے دل چین پائیں، اور مدد نہیں مگر اللہ کی طرف سے بیشک اللہ غالب حکمت والا ہے،'),
(1171, 8, 11, 'جب اس نے تمہیں اونگھ سے گھیر دیا تو اس کی طرف سے چین (تسکین) تھی اور آسمان سے تم پر پانی اتارا کہ تمہیں اس سے ستھرا کردے اور شیطان کی ناپاکی تم سے دور فرمادے اور تمہارے دلو ں کی ڈھارس بندھائے اور اس سے تمہارے قدم جمادے'),
(1172, 8, 12, 'جب اے محبوب! تمہارا رب فرشتوں کو وحی بھیجتا تھا کہ میں تمہارے ساتھ ہوں تم مسلمانوں کو ثابت رکھو عنقریب میں کافروں کے دلوں میں ہیبت ڈالوں گا تو کافروں کی گردنوں سے اوپر مارو اور ان کی ایک ایک پور (جوڑ) پر ضرب لگا ؤ'),
(1173, 8, 13, 'یہ اس لیے کہ انہوں نے اللہ اور اس کے رسول سے مخا لفت کی اور جو اللہ اور اس کے رسول سے مخالفت کرے تو بیشک اللہ کا عذاب سخت ہے،'),
(1174, 8, 14, 'یہ تو چکھو اور اس کے ساتھ یہ ہے کہ کافروں کو آ گ کا عذاب ہے'),
(1175, 8, 15, 'اے ایمان والو! جب کافروں کے لام (لشکر) سے تمہارا مقابلہ ہو تو انہیں پیٹھ نہ دو'),
(1176, 8, 16, 'اور جو اس دن انہیں پیٹھ دے گا مگر لڑائی کا ہنرَ کرنے یا اپنی جماعت میں جاملنے کو، تو وہ اللہ کے غضب میں پلٹا اور اس کا ٹھکانا دوزخ ہے، اور کیا بری جگہ ہے پلٹنے کی،'),
(1177, 8, 17, 'تو تم نے انہیں قتل نہ کیا بلکہ اللہ نے انہیں قتل کیا، اور اے محبوب! وہ خاک جو تم نے پھینکی تھی بلکہ اللہ نے پھینکی اور اس لیے کہ مسلمانوں کو اس سے اچھا انعام عطا فرمائے، بیشک اللہ سنتا جانتا ہے،'),
(1178, 8, 18, 'یہ تو لو اور اس کے ساتھ یہ ہے کہ اللہ کافروں کا داؤ سست کرنے والا ہے،'),
(1179, 8, 19, 'اے کافرو! اگر تم فیصلہ مانگتے ہو تو یہ فیصلہ تم پر آچکا اور اگر با ز ا ٓ ؤ تو تمہارا بھلا ہے اور اگر تم پھر شرارت کرو تو ہم پھر نہ دیں گے اور تمہارا جتھا تمہیں کچھ کام نہ دے گا چاہے کتنا ہی بہت ہو اور اس کے ساتھ یہ ہے کہ اللہ مسلمانوں کے ساتھ ہے،'),
(1180, 8, 20, 'اے ایمان والو! اللہ اور اس کے رسول کا حکم مانو اور سن سنا کہ اسے نہ پھرو،'),
(1181, 8, 21, 'اور ان جیسے نہ ہونا جنہوں نے کہا ہم نے سنا او ر وہ نہیں سنتے'),
(1182, 8, 22, 'بیشک سب جانوروں میں بدتر اللہ کے نزدیک وہ ہیں جو بہرے گونگے ہیں جن کو عقل نہیں'),
(1183, 8, 23, 'اور اگر اللہ ان میں کچھ بھلائی جانتا تو انہیں سنادیتا اور اگر سنا دیتا جب بھی انجام کا ر منہ پھیر کر پلٹ جاتے'),
(1184, 8, 24, 'اے ایمان والو! اللہ اور اس کے رسول کے بلانے پر حاضر ہو جب رسول تمہیں اس چیز کے لیے بلائیں جو تمہیں زندگی بخشے گی اور جان لو کہ اللہ کا حکم آدمی اور اس کے دلی ارادوں میں حائل ہوجا تا ہے اور یہ کہ تمہیں اس کی طرف اٹھنا ہے،'),
(1185, 8, 25, 'اور اس فتنہ سے ڈرتے رہو جو ہرگز تم میں خاص ظالموں کو ہی نہ پہنچے گا اور جان لو کہ اللہ کا عذاب سخت ہے،'),
(1186, 8, 26, 'اور یاد کرو جب تم تھوڑے تھے ملک میں دبے ہوئے ڈرتے تھے کہ کہیں لوگ تمہیں اچک نہ لے جائیں تو اس نے تمہیں جگہ دی اور اپنی مدد سے زور دیا اور ستھری چیزیں تمہیں روزی دیں کہ کہیں تم احسان مانو،'),
(1187, 8, 27, 'اے ایمان والو! اللہ اور رسول سے دغا نہ کرو اور نہ اپنی امانتوں میں دانستہ خیانت،'),
(1188, 8, 28, 'اور جان رکھو کہ تمہارے مال اور تمہاری اولاد سب فتنہ ہے اور اللہ کے پاس بڑا ثواب ہے'),
(1189, 8, 29, 'اے ایمان والو! اگر اللہ سے ڈرو گے تو تمہیں وہ دیگا جس سے حق کو باطل سے جدا کرلو اور تمہاری برائیاں اتار دے گا اور تمہیں بخش دے گا اور اللہ بڑے فضل والا ہے،'),
(1190, 8, 30, 'اور اے محبوب یاد کرو جب کافر تمہارے ساتھ مکر کرتے تھے کہ تمہیں بند کرلیں یا شہید کردیں یا نکا ل دیں اپنا سا مکر کرتے تھے اور اللہ اپنی خفیہ تدبیر فرماتا تھا اور اللہ کی خفیہ تدبیر سب سے بہتر،'),
(1191, 8, 31, 'اور جب ان پر ہماری آیتیں پڑھی جائیں تو کہتے ہیں ہاں ہم نے سنا ہم چاہتے تو ایسی ہم بھی کہہ دیتے یہ تو نہیں مگر اگلوں کے قصے'),
(1192, 8, 32, 'اور جب بولے کہ اے اللہ اگر یہی (قرآن) تیری طرف سے حق ہے تو ہم پر آسمان سے پتھر برسا یا کوئی دردناک عذاب ہم پر لا،'),
(1193, 8, 33, 'اور اللہ کا کام نہیں کہ انہیں عذاب کرے جب تک اے محبوب! تم ان میں تشریف فرما ہو اور اللہ انہیں عذاب کرنے والا نہیں جب تک وہ بخشش مانگ رہے ہیں'),
(1194, 8, 34, 'اور انہیں کیا ہے کہ اللہ انہیں عذاب نہ کرے وہ تو مسجد حرام سے روک رہے ہیں اور وہ اس کے اہل نہیں اس کے اولیاء تو پرہیزگار ہی ہیں مگر ان میں اکثر کو علم نہیں،'),
(1195, 8, 35, 'اور کعبہ کے پاس ان کی نماز نہیں مگر سیٹی اور تالی تو اب عذاب چکھو بدلہ اپنے کفر کا،'),
(1196, 8, 36, 'بیشک کافر اپنے مال خرچ کرتے ہیں کہ اللہ کی راہ سے روکیں تو اب انہیں خرچ کریں گے پھر وہ ان پر پچھتاوا ہوں گے پھر مغلوب کردیے جائیں گے اور کافروں کا حشر جہنم کی طرف ہوگا،'),
(1197, 8, 37, 'اس لیے کہ اللہ گندے کو ستھرے سے جدا فرمادے اور نجاستوں کو تلے اوپر رکھ کر سب ایک ڈھیر بناکر جہنم میں ڈال دے وہی نقصان پانے والے ہیں'),
(1198, 8, 38, 'تم کافروں سے فرماؤ اگر وہ باز رہے تو جو ہو گزرا وہ انہیں معاف فرمادیا جائے گا اور اگر پھر وہی کریں تو اگلوں کا دستور گزر چکا ہے'),
(1199, 8, 39, 'اور اگر ان سے لڑو یہاں تک کہ کوئی فساد باقی نہ رہے اور سارا دین اللہ ہی کا ہوجائے، اگر پھر وہ باز رہیں تو اللہ ان کے کام دیکھ رہا ہے،'),
(1200, 8, 40, 'اور اگر وہ پھریں تو جان لو کہ اللہ تمہارا مولیٰ ہے تو کیا ہی اچھا مولیٰ اور کیا ہی اچھا مددگار،'),
(1201, 8, 41, 'اور جان لو کہ جو کچھ غنیمت لو تو اس کا پانچواں حصہ خاص اللہ اور رسول و قرابت داروں اور یتیموں اور محتاجوں اور مسافروں کا ہے اگر تم ایمان لائے ہو اللہ پر اور اس پر جو ہم نے اپنے بندے پر فیصلہ کے دن اتارا جس دن دونوں فوجیں ملیں تھیں اور اللہ سب کچھ کرسکتا ہے،'),
(1202, 8, 42, 'جب تم نالے کے کنا رے تھے اور کافر پرلے کنا رے اور قا فلہ تم سے ترائی میں اور اگر تم آپس میں کوئی وعدہ کرتے تو ضرور وقت پر برابر نہ پہنچتے لیکن یہ اس لیے کہ اللہ پورا کرے جو کام ہونا ہے کہ جو ہلاک ہو دلیل سے ہلاک ہو اور جو جئے دلیل سے جئے اور بیشک اللہ ضرور سنتا جانتا ہے،'),
(1203, 8, 43, 'جب کہ اے محبوب! اللہ ٹمہیں کافروں کو تمہاری خواب میں تھو ڑا دکھاتا تھا اور اے مسلمانو! اگر وہ تمہیں بہت کرکے دکھاتا تو ضرور تم بزدلی کرتے اور معاملہ میں جھگڑا ڈالتے مگر اللہ نے بچا لیا بیشک وہ دلوں کی بات جانتا ہے،'),
(1204, 8, 44, 'او ر جب لڑتے وقت تمہیں کا فر تھو ڑے کرکے دکھائے اور تمہیں ان کی نگاہوں میں تھو ڑا کیا کہ اللہ پو را کرے جو کام ہونا ہے اور اللہ کی طرف سب کاموں کی رجوع ہے،'),
(1205, 8, 45, 'اے ایمان والو! جب کسی فوج سے تمہارا مقابلہ ہو تو ثابت قدم رہو اور اللہ کی یاد بہت کرو کہ تم مراد کو پہنچو،'),
(1206, 8, 46, 'اور اللہ اور اس کے رسول کا حکم مانو اور آپس میں جھگڑ و نہیں کہ پھر بز د لی کرو گے اور تمہاری بندھی ہوئی ہوا جاتی رہے گی اور صبر کرو، بیشک اللہ صبر والوں کے ساتھ ہے'),
(1207, 8, 47, 'اور ان جیسے نہ ہوتا جو اپنے گھر سے نکلے اتراتے اور لوگوں کے دکھانے کو اور اللہ کی راہ سے روکتے اور ان کے سب کام اللہ کے قابو میں ہیں،'),
(1208, 8, 48, 'اور جبکہ شیطان نے ان کی نگاہ میں ان کے کام بھلے کر دکھائے اور بولا آج تم پر کوئی شخص غالب آنے والا نہیں اور تم میری پناہ میں ہو تو جب دونوں لشکر آمنے سامنے ہوئے الٹے پاؤں بھاگا اور بولا میں تم سے الگ ہوں میں وہ دیکھتا ہوں جو تمہیں نظر نہیں آتا میں اللہ سے ڈرتا ہوں اور اللہ کا عذاب سخت ہے،'),
(1209, 8, 49, 'جب کہتے تھے منافق اور وہ جن کے دلوں میں ا ٓزا ر ہے کہ یہ مسلمان اپنے دین پر مغرور ہیں اور جو اللہ پر بھروسہ کرے تو بیشک اللہ غالب حکمت والا ہے،'),
(1210, 8, 50, 'اور کبھی تو دیکھے جب فرشتے کافروں کی جان نکالتے ہیں مار رہے ہیں ان کے منہ پر او ر ان کی پیٹھ پر اور چکھو آ گ کا عذاب،'),
(1211, 8, 51, 'یہ بدلہ ہے اس کا جو تمہارے ہاتھوں نے آگے بھیجا اور اللہ بندوں پر ظلم نہیں کرتا'),
(1212, 8, 52, 'جیسے فرعون والوں اور ان سے اگلوں کا دستور وہ اللہ کی آیتوں کے منکر ہوئے تو اللہ نے انہیں ان کے گناہوں پر پکڑا بیشک اللہ قوت والا سخت عذاب و ا لا ہے،'),
(1213, 8, 53, 'یہ اس لیے کہ اللہ کسی قوم سے جو نعمت انہیں دی تھی بدلتا نہیں جب تک وہ خود نہ بدل جائیں اور بیشک اللہ سنتا جانتا ہے'),
(1214, 8, 54, 'جیسے فرعون والوں اور ان سے اگلوں کا دستور، انہوں نے اپنے رب کی آیتیں جھٹلائیں تو ہم نے ان کو ان کے گناہوں کے سبب ہلاک کیا اور ہم نے فرعون والوں کو ڈبو دیا اور وہ سب ظالم تھے،'),
(1215, 8, 55, 'بیشک سب جانوروں (کا فر و ں) میں بدتر اللہ کے نزدیک وہ ہیں جنہوں نے کفر کیا اور ایمان نہیں لاتے،'),
(1216, 8, 56, 'وہ جن سے تم نے معاہدہ کیا تھا پھر ہر با ر اپنا عہد توڑ دیتے ہیں اور ڈرتے نہیں'),
(1217, 8, 57, 'تو اگر تم انہیں کہیں لڑائی میں پا ؤ تو انہیں ایسا قتل کرو جس سے ان کے پس ماندوں کو بھگاؤ اس امید پر کہ شاید انہیں عبرت ہو'),
(1218, 8, 58, 'اور اگر تم کسی قوم سے دغا کا اندیشہ کرو تو ان کا عہد ان کی طرف پھینک دو برابری پر بیشک دغا والے اللہ کو پسند نہیں،'),
(1219, 8, 59, 'اور ہرگز کا فر اس گھمنڈ میں نہ رہیں کہ وہ ہاتھ سے نکل گئے بیشک وہ عاجز نہیں کرتے'),
(1220, 8, 60, 'اور ان کے لیے تیار رکھو جو قوت تمہیں بن پڑے اور جتنے گھوڑے باندھ سکو کہ ان سے ان کے دلوں میں دھاک بٹھاؤ جو اللہ کے دشمن ہیں اور ان کے سوا کچھ اوروں کے دلوں میں جنہیں تم نہیں جانتے اللہ انہیں جانتا ہے اور اللہ کی راہ میں جو کچھ خرچ کرو گے تمہیں پورا دیا جائے گا اور کسی طرح گھاٹے میں نہ رہو گے،'),
(1221, 8, 61, 'اور اگر وہ صلح کی طرف جھکیں تو تم بھی جھکو اور اللہ پر بھروسہ رکھو، بیشک وہی ہے سنتا جانتا،'),
(1222, 8, 62, 'اور اگر وہ تمہیں فریب دیا چاہیں تو بیشک اللہ تمہیں کافی ہے، وہی ہے جس نے تمہیں زور دیا اپنی مدد کا اور مسلمانوں کا،'),
(1223, 8, 63, 'اور ان کے دلوں میں میل کردیا اگر تم زمین میں جو کچھ ہے سب خرچ کردیتے ان کے دل نہ ملا سکتے لیکن اللہ نے ان کے دل ملادیئے، بیشک وہی ہے غالب حکمت والا،'),
(1224, 8, 64, 'اے غیب کی خبریں بتانے والے (نبی) اللہ تمہیں کافی ہے اور یہ جتنے مسلمان تمہارے پیرو ہوئے،'),
(1225, 8, 65, 'اے غیب کی خبریں بتانے والے! مسلمانوں کو جہاد کی ترغیب دو اگر تم میں کے بیس صبر والے ہوں گے دو سو پر غالب ہوں گے اور اگر تم میں کے سو ہوں تو کافروں کے ہزا ر پر غالب آئیں گے اس لیے کہ وہ سمجھ نہیں رکھتے،'),
(1226, 8, 66, 'اب اللہ نے تم پر سے تخفیف فرمائی اور اسے علم کہ تم کمزو ر ہو تو اگر تم میں سو صبر والے ہوں د و سو پر غالب آئیں گے اور اگر تم میں کے ہزار ہوں تو دو ہزار پر غالب ہوں گے اللہ کے حکم سے اور اللہ صبر والوں کے ساتھ ہے،'),
(1227, 8, 67, 'کسی نبی کو لائئق نہیں کہ کافروں کو زندہ قید کرلے جب تک زمین میں ان کا خون خوب نہ بہائے تم لوگ دنیا کا مال چا ہتے ہو او ر اللہ آخرت چاہتا ہے اور اللہ غالب حکمت والا ہے'),
(1228, 8, 68, 'اگراللہ پہلے ایک بات لکھ نہ چکا ہوتا تو اے مسلمانو! تم نے جو کافروں سے بدلے کا مال لے لیا اس میں تم پر بڑا عذاب آتا،'),
(1229, 8, 69, 'تو کھاؤ جو غنیمت تمہیں ملی حلال پاکیزہ اور اللہ سے ڈرتے رہو بیشک اللہ بخشنے والا مہربان ہے،'),
(1230, 8, 70, 'اے غیب کی خبریں بتانے والے جو قیدی تمہارے ہاتھ میں ہیں ان سے فرماؤ اگر اللہ نے تمہارے دل میں بھلائی جانی تو جو تم سے لیا گیا اس سے بہتر تمہیں عطا فرمائے گا اور تمہیں بخش دے گا اور اللہ بخشنے والا مہربان ہے'),
(1231, 8, 71, 'اور اے محبوب اگر وہ تم سے دغا چاہیں گے تو اس سے پہلے اللہ ہی کی خیانت کرچکے ہیں جس پر اس نے اتنے تمہارے قابو میں دے دیے اور اللہ جاننے والا حکمت والا ہے،'),
(1232, 8, 72, 'بیشک جو ایمان لائے اور اللہ کے لیے گھر بار چھوڑے اور اللہ کی راہ میں اپنے مالوں اور جانوں سے لڑے اور وہ جنہوں نے جگہ دی اور مدد کی وہ ایک دوسرے کے وارث ہیں اور وہ جو ایمان لائے اور ہجرت نہ کی انہیں ان کا ترکہ کچھ نہیں پہنچتا جب تک ہجرت نہ کریں اور اگر وہ دین میں تم سے مدد چاہیں تو تم پر مدد دینا واجب ہے مگر ایسی قوم پر کہ تم میں ان میں معاہدہ ہے، اور اللہ تمہارے کام دیکھ رہا ہے،'),
(1233, 8, 73, 'اور کافر ا ٓ پس میں ایک دوسرے کے وارث ہیں ایسا نہ کرو گے تو زمین میں فتنہ اور بڑا فساد ہوگا'),
(1234, 8, 74, 'اور وہ جو ایمان لائے اور ہجرت کی اور اللہ کی راہ میں لڑے اور جنہوں نے جگہ دی اور مدد کی وہی سچے ایمان والے ہیں، ان کے لیے بخشش ہے اور عزت کی روزی'),
(1235, 8, 75, 'اور جو بعد کو ایمان لائے اور ہجرت کی اور تمہارے ساتھ جہاد کیا وہ بھی تمہیں میں سے ہیں اور رشتہ والے ایک دوسرے سے زیادہ نزدیک ہیں اللہ کی کتاب میں بیشک اللہ سب کچھ جانتا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'بیزاری کا حکم سنانا ہے اللہ اور اس کے رسول کی طرف سے ان مشرکوں کو جن سے تمہارا معاہدہ تھا اور وہ قائم نہ رہے'),
(1237, 9, 2, 'تو چا ر مہینے زمین پر چلو پھرو اور جان رکھو کہ تم اللہ کو تھکا نہیں سکتے اور یہ کہ اللہ کافروں کو رسوا کرنے والا ہے'),
(1238, 9, 3, 'اور منادی پکار دیتا اللہ اور اس کے رسول کی طرف سے سب لوگوں میں بڑے حج کے دن کہ اللہ بیزار ہے، مشرکوں سے اور اس کا رسول تو اگر تم توبہ کرو تو تمہا را بھلا ہے اور اگر منہ پھیرو تو جان لو کہ اللہ کو نہ تھکا سکو گے اور کافروں کو خوشخبری سناؤ دردناک عذاب کی،'),
(1239, 9, 4, 'مگر وہ مشرک جن سے تمہارہ معاہدہ تھا پھر انہوں نے تمہارے عہد میں کچھ کمی نہیں کی اور تمہارے مقابل کسی کو مدد نہ دی تو ان کا عہد ٹھہری ہوئی مدت تک پورا کرو، بیشک اللہ پرہیزگاروں کو دوست رکھتا ہے،'),
(1240, 9, 5, 'پھر جب حرمت والے مہینے نکل جائیں تو مشرکوں کو مارو جہاں پا ؤ اور انہیں پکڑو اور قید کرو اور ہر جگہ ان کی تاک میں بیٹھو پھر اگر وہ توبہ کریں اور نماز قائم رکھیں اور زکوٰة دیں تو ان کی راہ چھوڑ دو بیشک اللہ بخشنے والا مہربان ہے،'),
(1241, 9, 6, 'اور اے محبوب اگر کوئی مشرک تم سے پناہ مانگے تو اسے پناہ دو کہ وہ اللہ کا کلام سنے پھر اسے اس کی امن کی جگہ پہنچا دو یہ اس لیے کہ وہ نادان لوگ ہیں'),
(1242, 9, 7, 'مشرکوں کے لیے اللہ اور اس کے رسول کے پاس کوئی عہد کیونکر ہوگا مگر وہ جن سے تمہارا معاہدہ مسجد حرام کے پاس ہوا تو جب تک وہ تمہارے لیے عہد پر قائم رہیں تم ان کے لیے قائم رہو، بیشک پرہیزگار اللہ کو خوش آتے ہیں،'),
(1243, 9, 8, 'بھلا کیونکر ان کا حال تو یہ ہے کہ تم پر قابو پائیں تو نہ قرابت کا لحاظ کریں نہ عہد کا، اپنے منہ سے تمہیں راضی کرتے ہیں اور ان کے دلوں میں انکار ہے اور ان میں اکثر بے حکم ہیں'),
(1244, 9, 9, 'اللہ کی آیتوں کے بدلے تھو ڑے دام مول لیے تو اس کی راہ سے روکا بیشک وہ بہت ہی بڑے کام کرتے ہیں،'),
(1245, 9, 10, 'کسی مسلمان میں نہ قراب کا لحاظ کریں نہ عہد کا اور وہی سرکش ہیں،'),
(1246, 9, 11, 'پھر اگر وہ توبہ کریں اور نماز قائم رکھیں اور زکوٰة دیں تو وہ تمہارے دینی بھائی ہیں اور ہم آیتیں مفصل بیان کرتے ہیں جاننے والوں کے لیے'),
(1247, 9, 12, 'اور اگر عہد کرکے اپنی قسمیں توڑیں اور تمہارے دین پر منہ آئیں تو کفر کے سرغنوں سے لڑو بیشک ان کی قسمیں کچھ نہیں اس امید پر کہ شاید وہ باز آئیں'),
(1248, 9, 13, 'کیا اس قوم سے نہ لڑو گے جنہوں نے اپنی قسمیں توڑیں اور رسول کے نکالنے کا ارادہ کیا حالانکہ انہیں کی طرف سے پہلی ہوتی ہے، کیا ان سے ڈرتے ہو تو اللہ کا زیادہ مستحق ہے کہ اس سے ڈرو اگر ایمان رکھتے ہو،'),
(1249, 9, 14, 'تو ان سے لڑو اللہ انہیں عذاب دیگا تمہارے ہاتھوں اور انہیں رسوا کرے گا اور تمہیں ان پر مدد دے گا اور ایمان والوں کا جی ٹھنڈا کرے گا،'),
(1250, 9, 15, 'اور ان کے دلوں کی گھٹن دور فرمائے گا اور اللہ جس کی چاہے تو یہ قبول فرمائے اور اللہ علم و حکمت والا ہے،'),
(1251, 9, 16, 'کیا اس گمان میں ہو کہ یونہی چھوڑ دیئے جاؤ گے اور ابھی اللہ نے پہچان نہ کرائی ان کی جو تم میں سے جہاد کریں گے اور اللہ اور اس کے رسول اور مسلمانوں کے سوا کسی کو اپنا محرم راز نہ بنائیں گے اور اللہ تمہارے کاموں سے خبردار ہے،'),
(1252, 9, 17, 'مشرکوں کو نہیں پہنچتا کہ اللہ کی مسجدیں آباد کریں خود اپنے کفر کی گواہی دے کر ان کا تو سب کیا دھرا اِکا رت ہے اور وہ ہمیشہ آگ میں رہیں گے'),
(1253, 9, 18, 'اللہ کی مسجدیں وہی آباد کرتے ہیں جو اللہ اور قیامت پر ایمان لاتے اور نما ز قائم کرتے ہیں اور زکوٰة دیتے ہیں اور اللہ کے سوا کسی سے نہیں ڈرتے تو قریب ہے کہ یہ لوگ ہدایت والوں میں ہوں،'),
(1254, 9, 19, 'تو کیا تم نے حا جیوں کی سبیل اور مسجد حرام کی خدمت اس کے برابر ٹھہرا لی جو اللہ اور قیامت پر ایمان لایا اور اللہ کی راہ میں جہاد کیا، وہ اللہ کے نزدیک برابر نہیں، اور اللہ ظالموں کو راہ نہیں دیتا'),
(1255, 9, 20, 'وہ جو ایمان لائے اور ہجرت کی اور اپنے مال و جان سے اللہ کی راہ میں لڑے، اللہ کے یہاں ان کا درجہ بڑا ہے اور وہی مراد کو پہنچے'),
(1256, 9, 21, 'ان کا رب انہیں خوشی سنا تا ہے اپنی رحمت اور اپنی رضا کی اور ان باغوں کی جن میں انہیں دائمی نعمت ہے'),
(1257, 9, 22, 'ہمیشہ ہمیشہ ان میں رہیں گے، بیشک اللہ کے پاس بڑا ثواب ہے،'),
(1258, 9, 23, 'اے ایمان والو! اپنے باپ اور اپنے بھائیوں کو دوست نہ سمجھو اگر وہ ایمان پر کفر پسند کریں، اور تم میں جو کوئی ان سے دوستی کرے گا تو وہی ظالم ہیں'),
(1259, 9, 24, 'تم فرماؤ اگر تمہارے باپ اور تمہارے بیٹے اور تمہارے بھائی اور تمہاری عورتیں اور تمہارا کنبہ اور تمہاری کمائی کے مال او ر وہ سودا جس کے نقصان کا تمہیں ڈر ہے اور تمہارے پسند کا مکان یہ چیزیں اللہ اور اس کے رسول اور اس کی راہ میں لڑے سے زیاد ہ پیاری ہوں تو راستہ دیکھو یہاں تک کہ اللہ اپنا حکم لائے اور اللہ فاسقوں کو راہ نہیں دیتا،'),
(1260, 9, 25, 'بیشک اللہ نے بہت جگہ تمہاری مدد کی اور حنین کے دن جب تم اپنی کثرت پر اترا گئے تھے تو وہ تمہارے کچھ کام نہ ا ٓئی اور زمین اتنی وسیع ہوکر تم پر تنگ ہوگئی پھر تم پیٹھ دے کر پھرگئے،'),
(1261, 9, 26, 'پھر اللہ نے اپنی تسکین اتا ری اپنے رسول پر اور مسلمانوں پر اور وہ لشکر اتارے جو تم نے نہ دیکھے اور کافروں کو عذاب دیا اور منکروں کی یہی سزا ہے،'),
(1262, 9, 27, 'پھر اس کے بعد اللہ جسے چاہے گا توبہ دے گا اور اللہ بخشنے والا مہربان ہے،'),
(1263, 9, 28, 'اے ایمان والو! مشرک نرے ناپاک ہیں تو اس برس کے بعد وہ مسجد حرام کے پاس نہ آنے پائیں اور اگر تمہاری محتاجی کا ڈر ہے تو عنقریب اللہ تمہیں دولت مند کردے گا اپنے فضل سے اگر چاہے بیشک اللہ علم و حکمت والا ہے،'),
(1264, 9, 29, 'لڑو ان سے جو ایمان نہیں لاتے اللہ پر اور قیامت پر اور حرام نہیں مانتے اس چیز کو جس کو حرام کیا اللہ اور اس کے رسول نے اور سچے دین کے تابع نہیں ہوتے یعنی وہ جو کتاب دیے گئے جب تک اپنے ہاتھ سے جزیہ نہ دیں ذلیل ہوکر'),
(1265, 9, 30, 'اور یہودی بولے عزیر اللہ کا بیٹا ہے اور نصرانی بولے مسیح اللہ کا بیٹا ہے، یہ باتیں وہ اپنے منہ سے بکتے ہیں اگلے کافرو ں کی سی بات بناتے ہیں، اللہ انہیں مارے، کہاں اوندھے جاتے ہیں'),
(1266, 9, 31, 'انہوں نے اپنے پادریوں اور جوگیوں کو اللہ کے سوا خدا بنالیا اور مسیح بن مریم کو اور انہیں حکم نہ تھا مگر یہ کہ ایک اللہ کو پوجیں اس کے سوا کسی کی بندگی نہیں، اسے پاکی ہے ان کے شرک سے،'),
(1267, 9, 32, 'چاہتے ہیں کہ اللہ کا نور اپنے منہ سے بُجھا دیں اور اللہ نہ مانے گا مگر اپنے نور کا پورا کرنا پڑے برا مانیں کافر،'),
(1268, 9, 33, 'وہی ہے جس نے اپنا رسول ہدایت اور سچے دین کے ساتھ بھیجا کہ اسے سب دینوں پر غالب کرے پڑے برا مانیں مشرک،'),
(1269, 9, 34, 'اے ایمان والو! بیشک بہت پادری اور جوگی لوگوں کا مال ناحق کھا جاتے ہیں اور اللہ کی راہ سے روکتے ہیں، اور وہ کہ جوڑ کر رکھتے ہیں سونا اور چاندی اور اسے اللہ کی راہ میں خرچ نہیں کرتے انہیں خوشخبری سناؤ دردناک عذاب کی،'),
(1270, 9, 35, 'جس دن تپایا جائے گا جہنم کی آ گ میں پھر اس سے داغیں گے ان کی پیشانیاں اور کروٹیں اور پیٹھیں یہ ہے وہ جو تم نے اپنے لیے جوڑ کر رکھا تھا اب چکھو مزا اس جوڑنے کا،'),
(1271, 9, 36, 'بیشک مہینوں کی گنتی اللہ کے نزدیک بارہ مہینے ہے اللہ کی کتاب میں جب سے اس نے آسمان اور زمین بنائے ان میں سے چار حرمت والے ہیں یہ سیدھا دین ہے، تو ان مہینوں میں اپنی جان پر ظلم نہ کرو اور مشرکوں سے ہر وقت لڑو جیسا وہ تم سے ہر وقت لڑتے ہیں، اور جان لو کہ اللہ پرہیزگاروں کے ساتھ ہے'),
(1272, 9, 37, 'ان کا مہینے پیچھے ہٹانا نہیں مگر اور کفر میں بڑھنا اس سے کافر بہکائے جاتے ہیں ایک برس اسے حلال ٹھہراتے ہیں اور دوسرے برس اسے حرام مانتے ہیں کہ اس گنتی کے برابر ہوجائیں جو اللہ نے حرام فرمائی اور اللہ کے حرام کیے ہوئے حلال کرلیں، ان کے برے کا م ان کی آنکھوں میں بھلے لگتے ہیں، اور اللہ کافروں کو راہ نہیں دیتا،'),
(1273, 9, 38, 'اے ایمان والو! تمہیں کیا ہوا جب تم سے کہا جائے کہ خدا کی راہ میں کوچ کرو تو بوجھ کے مارے زمین پر بیٹھ جاتے ہو کیا تم نے دنیا کی زندگی آخرت کے بدلے پسند کرلی اور جیتی دنیا کا اسباب آخرت کے سامنے نہیں مگر تھوڑا'),
(1274, 9, 39, 'اگر نہ کوچ کرو گے انہیں سخت سزا دے گا اور تمہاری جگہ اور تمہاری جگہ اور لوگ لے آئے گا اور تم اس کا کچھ نہ بگا ڑ سکوگے، اور اللہ سب کچھ کرسکتا ہے،'),
(1275, 9, 40, 'اگر تم محبوب کی مدد نہ کرو تو بیشک اللہ نے ان کی مدد فرمائی جب کافروں کی شرارت سے انہیں باہر تشریف لے جانا ہوا صرف دو جان سے جب وہ دونوں غار میں تھے جب اپنے یار سے فرماتے تھے غم نہ کھا بیشک اللہ ہمارے ساتھ ہے تو اللہ نے اس پر اپنا سکینہ اتارا اور ان فوجوں سے اس کی مدد کی جو تم نے نہ دیکھیں اور کافروں کی بات نیچے ڈالی اللہ ہی کا بول بالا ہے اور اللہ غالب حکمت والا ہے،'),
(1276, 9, 41, 'کوچ کرو ہلکی جان سے چاہے بھاری دل سے اور اللہ کی راہ میں لڑو اپنے مال اور جان سے، یہ تمہارے لیے بہتر ہے اگر جانو'),
(1277, 9, 42, 'اگر کوئی قریب مال یا متوسط سفر ہوتا تو ضرور تمہارے ساتھ جاتے مگر ان پر تو مشقت کا راستہ دور پڑ گیا، اور اب اللہ کی قسم کھائیں گے کہ ہم سے بن پڑتا تو ضرور تمہارے ساتھ چلتے اپنی جانو کو ہلاک کرتے ہیں اور اللہ جانتا ہے کہ وہ بیشک ضرور جھوٹے ہیں،'),
(1278, 9, 43, 'اللہ تمہیں معاف کرے تم نے انہیں کیوں اِذن دے دیا جب تک نہ کھلے تھے تم پر سچے اور ظاہر نہ ہوئے تھے جھوٹے،'),
(1279, 9, 44, 'اور وہ جو اللہ اور قیامت پر ایمان رکھتے ہیں تم سے چھٹی نہ مانگیں گے اس سے کہ اپنے مال اور جان سے جہاد کریں، اور اللہ خوب جا نتا ہے پرہیزگا روں کو،'),
(1280, 9, 45, 'تم سے یہ چھٹی وہی مانگتے ہیں جو اللہ اور قیامت پر ایمان نہیں رکھتے اور ان کے دل شک میں پڑے ہیں تو وہ اپنے شک میں ڈانواں ڈول ہیں'),
(1281, 9, 46, 'انہیں نکلنا منظور ہوتا تو اس کا سامان کرتے مگر خدا ہی کو ان کا اٹھنا پسند ہوا تو ان میں کاہلی بھردی اور فرمایا گیا کہ بیٹھ رہو بیٹھ رہنے والے کے ساتھ'),
(1282, 9, 47, 'اگر وہ تم میں نکلتے تو ان سے سوا نقصان کے تمہیں کچھ نہ بڑھتنا اور تم میں فتنہ ڈالنے کو تمہارے بیچ یں غرابیں دوڑاتے (فساد ڈالتے) اور تم میں ان کے جاسوس موجود ہیں اور اللہ خوب جانتا ہے ظالموں کو،'),
(1283, 9, 48, 'بیشک انہوں نے پہلے ہی فتنہ چا ہا تھا اور اے محبوب! تمہارے لیے تدبیریں الٹی پلٹیں یہاں تک کہ حق آیا اور اللہ کا حکم ظاہر ہوا اور انہیں ناگوار تھا،'),
(1284, 9, 49, 'اور ان میں کوئی تم سے یوں عرض کرتا ہے کہ مجھے رخصت دیجیے اور فتنہ میں نہ ڈالیے سن لو وہ فتنہ ہی میں پڑے اور بیشک جہنم گھیرے ہوئے ہے کافروں کو،'),
(1285, 9, 50, 'اگر تمہیں بھلائی پہنچے تو انہیں برا لگے اور اگر تمہیں کوئی مصیبت پہنچے تو کہیں ہم نے اپنا کام پہلے ہی ٹھیک کرلیا تھا اور خوشیاں مناتے پھر جائیں،'),
(1286, 9, 51, 'تم فرماؤ ہمیں نہ پہنچے گا مگر جو اللہ نے ہمارے لیے لکھ دیا وہ ہمارا مولیٰ ہے اور مسلمانوں کو اللہ ہی پر بھروسہ چاہیے،'),
(1287, 9, 52, 'تم فرماؤ تم ہم پر کس چیز کا انتظار کرتے ہو مگر دو خوبیوں میں سے ایک کا اور ہم تم پر اس انتظار میں ہیں کہ اللہ تم پر عذاب ڈالے اپنے پاس سے یا ہمارے ہاتھوں تو اب راہ دیکھو ہم بھی تمہارے ساتھ راہ دیکھ رہے ہیں'),
(1288, 9, 53, 'تم فرماؤ کہ دل سے خرچ کرو یا ناگواری سے تم سے ہر گز قبول نہ ہوگا بیشک تم بے حکم لوگ ہو،'),
(1289, 9, 54, 'اور وہ جو خرچ کرتے ہیں اس کا قبول ہونا بند نہ ہوا مگر اسی لیے کہ وہ اللہ اور رسول سے منکر ہوئے اور نماز کو نہیں آتے مگر جی ہارے اور خرچ نہیں کرتے مگر ناگواری سے'),
(1290, 9, 55, 'تو تمہیں ان کے مال اور ان کی اولاد کا تعجب نہ آئے، اللہ ہی چاہتا ہے کہ دنیا کی زندگی میں ان چیزوں سے ان پر وبال ڈالے اور اگر کفر ہی پر ان کا دم نکل جائے'),
(1291, 9, 56, 'اور اللہ کی قسمیں کھاتے ہیں کہ وہ تم میں سے ہیں اور تم میں سے ہیں نہیں ہاں وہ لوگ ڈرتے ہیں'),
(1292, 9, 57, 'اگر پائیں کوئی پناہ یا غار یا سما جانے کی جگہ تو رسیاں تڑاتے ادھر پھر جائیں گے'),
(1293, 9, 58, 'اور ان میں کوئی وہ ہے کہ صدقے بانٹنے میں تم پر طعن کرتا ہے تو اگر ان میں سے کچھ ملے تو راضی ہوجائیں اور نہ ملے تو جبھی وہ ناراض ہیں،'),
(1294, 9, 59, 'اور کیا اچھا ہوتا اگر وہ اس پر راضی ہوتے جو اللہ و رسول نے ان کو دیا اور کہتے ہمیں اللہ کافی ہے اب دیتا ہے ہمیں اللہ اپنے فضل سے اور اللہ کا رسول، ہمیں اللہ ہی کی طرف رغبت ہے'),
(1295, 9, 60, 'زکوٰة تو انہیں لوگوں کے لیے ہے محتاج اور نرے نادار اور جو اسے تحصیل کرکے لائیں اور جن کے دلوں کو اسلام سے الفت دی جائے اور گردنیں چھڑانے میں اور قرضداروں کو اور اللہ کی راہ میں اور مسافر کو، یہ ٹھہرایا ہوا ہے اللہ کا، اور اللہ علم و حکمت والا ہے'),
(1296, 9, 61, 'اور ان میں کوئی وہ ہیں کہ ان غیب کی خبریں دینے والے کو ستاتے ہیں اور کہتے ہیں وہ تو کان ہیں، تم فرماؤ تمہارے بھلے کے لیے کا ن ہیں اللہ پر ایمان لاتے ہیں اور مسلمانوں کی بات پر یقین کرتے ہیں اور جو تم میں مسلمان ہیں ان کے واسطے رحمت ہیں، اور جو رسول اللہ کو ایذا دیتے ہیں ان کے لیے دردناک عذاب ہے،'),
(1297, 9, 62, 'تمہارے سامنے اللہ کی قسم کھاتے ہیں کہ تمہیں راضی کرلیں اور اللہ و رسول کا حق زائد تھا کہ اسے راضی کرتے اگر ایمان رکھتے تھے،'),
(1298, 9, 63, 'کیا انہیں خبر نہیں کہ جو خلاف کرے اللہ اور اس کے رسول کا تو اس کے لیے جہنم کی آ گ ہے کہ ہمیشہ اس میں رہے گا، یہی بڑی رسوائی ہے،'),
(1299, 9, 64, 'منافق ڈرتے ہیں کہ ان پر کوئی سورة ایسی اترے جو ان کے دلوں کی چھپی جتادے تم فرماؤ ہنسے جاؤ اللہ کو ضرور ظاہر کرنا ہے جس کا تمہیں ڈر ہے،'),
(1300, 9, 65, 'اور اے محبوب اگر تم ان سے پوچھو تو کہیں گے کہ ہم تو یونہی ہنسی کھیل میں تھے تم فرماؤ کیا اللہ اور اس کی آیتوں اور اس کے رسول سے ہنستے ہو،'),
(1301, 9, 66, 'بہانے نہ بنا ؤ تم کافر ہوچکے مسلمان ہوکر اگر ہم تم میں سے کسی کو معاف کریں تو اوروں کو عذاب دیں گے اس لیے کہ وہ مجرم تھے'),
(1302, 9, 67, 'منافق مرد اور منافق عورتیں ایک تھیلی کے چٹے بٹے ہیں برائی کا حکم دیں اور بھلائی سے منع کریں اور اپنی مٹھی بند رکھیں وہ اللہ کو چھوڑ بیٹھے تو اللہ نے انہیں چھوڑدیا بیشک منافق وہی پکے بے حکم ہیں،'),
(1303, 9, 68, 'اللہ نے منافق مردوں اور منافق عورتوں اور کافروں کو جہنم کی آگ کا وعدہ دیا ہے جس میں ہمیشہ رہیں گے، وہ انہیں بس ہے اور اللہ کی ان پر لعنت ہے اور ان کے لیے قائم رہنے والا عذاب ہے'),
(1304, 9, 69, 'جیسے وہ جو تم سے پہلے تھے تم سے زور میں بڑھ کر تھے اور ان کے مال اور اولاد سے زیادہ، تو وہ اپنا حصہ برت گئے تو تم نے اپنا حصہ برتا جیسے اگلے اپنا حصہ برت گئے اور تم بیہودگی میں پڑے جیسے وہ پڑے تھے ان کے عمل اکارت گئے دنیا اور آخرت میں اور وہی لوگ گھاٹے میں ہیں'),
(1305, 9, 70, 'کیا انہیں اپنے سے اگلوں کی خبر نہ آئی نوح کی قوم اور عاد اور ثمود اور ابراہیم کی قوم اور مدین والے اور وہ بستیاں کہ الٹ دی گئیں ان کے رسول روشن دلیلیں ان کے پاس لائے تھے تو اللہ کی شان نہ تھی کہ ان پر ظلم کرتا بلکہ وہ خود ہی اپنی جانوں پر ظالم تھے'),
(1306, 9, 71, 'اور مسلمان مرد اور مسلمان عورتیں ایک دوسرے کے رفیق ہیں بھلائی کا حکم دیں اور برائی سے منع کریں اور نماز قائم رکھیں اور زکوٰة دیں اور اللہ و رسول کا حکم مانیں، یہ ہیں جن پر عنقریب اللہ رحم کرے گا، بیشک اللہ غالب حکمت والا ہے،'),
(1307, 9, 72, 'اللہ نے مسلمان مردوں اور مسلمان عورتوں کو باغوں کا وعدہ دیا ہے جن کے نیچے نہریں رواں ان میں ہمیشہ رہیں گے اور پاکیزہ مکانوں کا بسنے کے باغوں میں، اور اللہ کی رضا سب سے بڑی یہی ہے بڑی مراد پانی،'),
(1308, 9, 73, 'اے غیب کی خبریں دینے والے (نبی) جہاد فرماؤ کافروں اور منافقوں پر اور ان پر سختی کرو، اور ان کا ٹھکانا دوزخ ہے، اور کیا ہی بری جگہ پلٹنے کی،'),
(1309, 9, 74, 'اللہ کی قسم کھاتے ہیں کہ انہوں نے نہ کہا اور بیشک ضرور انہوں نے کفر کی بات کہی اور اسلام میں آکر کافر ہوگئے اور وہ چاہا تھا جو انہیں نہ ملا اور انہیں کیا برا لگا یہی نہ کہ اللہ و رسول نے انہیں اپنے فضل سے غنی کردیا تو اگر وہ توبہ کریں تو ان کا بھلا ہے، اور اگر منہ پھیریں تو اللہ انہیں سخت عذاب کرے گا دنیا اور آخرت میں، اور زمین میں کوئی نہ ان کا حمایتی ہوگا اور نہ مددگار'),
(1310, 9, 75, 'اور ان میں کوئی وہ ہیں جنہوں نے اللہ سے عہد کیا تھا کہ اگر ہمیں اپنے فضل سے دے تو ہم ضرور خیرات کریں گے اور ہم ضرور بھلے آدمی ہوجائیں گے'),
(1311, 9, 76, 'تو جب اللہ نے انہیں اپنے فضل سے دیا اس میں بخل کرنے لگے اور منہ پھیر کر پلٹ گئے،'),
(1312, 9, 77, 'تو اس کے پیچھے اللہ نے ان کے دلوں میں نفاق رکھ دیا اس دن تک کہ اس سے ملیں گے بدلہ اس کا کہ انہوں نے اللہ سے وعدہ جھوٹا کیا اور بدلہ اس کا کہ جھوٹ بولتے تھے'),
(1313, 9, 78, 'کیا انہیں خبر نہیں کہ اللہ ان کے دل کی چھپی اور ان کی سرگوشی کو جانتا ہے اور یہ کہ اللہ سب غیبوں کا بہت جاننے والا ہے'),
(1314, 9, 79, 'اور جو عیب لگاتے ہیں ان مسلمانوں کو کہ دل سے خیرات کرتے ہیں اور ان کو جو نہیں پاتے مگر اپنی محنت سے تو ان سے ہنستے ہیں اللہ ان کی ہنسی کی سزا دے گا، اور ان کے لیے دردناک عذاب ہے،'),
(1315, 9, 80, 'تم ان کی معافی چاہو یا نہ چاہو، اگر تم ستر بار ان کی معافی چاہو گے تو اللہ ہرگز انھیں نہیں بخشے گا یہ اس لیے کہ وہ اللہ اور اس کے رسول سے منکر ہوئے، اور اللہ فاسقوں کو راہ نہیں دیتا'),
(1316, 9, 81, 'پیچھے رہ جانے والے اس پر خوش ہوئے کہ وہ رسول کے پیچھے بیٹھ رہے اور انہیں گوارا نہ ہوا کہ اپنے مال اور جان سے اللہ کی راہ میں لڑیں اور بولے اس گرمی میں نہ نکلو، تم فرماؤ جہنم کی آگ سب سے سخت گرم ہے، کسی طرح انہیں سمجھ ہو تی'),
(1317, 9, 82, 'تو انہیں چاہیے تھوڑا ہنسیں اور بہت روئیں بدلہ اس کا جو کماتے تھے'),
(1318, 9, 83, 'پھر اے محبوب! اگر اللہ تمہیں ان میں سے کسی گروہ کی طرف واپس لے جائے اور وہ تم سے جہاد کو نکلنے کی اجازت مانگے تو تم فرمانا کہ تم کبھی میرے ساتھ نہ چلو اور ہرگز میرے ساتھ کسی دشمن سے نہ لڑو، تم نے پہلی دفعہ بیٹھ رہنا پسند کیا تو بیٹھ رہو پیچھے رہ جانے والوں کے ساتھ'),
(1319, 9, 84, 'اور ان میں سے کسی کی میت پر کبھی نماز نہ پڑھنا اور نہ اس کی قبر پر کھڑے ہونا، بیشک اللہ اور رسول سے منکر ہوئے اور فسق ہی میں مر گئے'),
(1320, 9, 85, 'اور ان کے مال یا اولاد پر تعجب نہ کرنا، اللہ یہی چاہتا ہے کہ اسے دنیا م یں ان پر وبال کرے اور کفر ہی پر ان کا دم نکل جائے،'),
(1321, 9, 86, 'اور جب کوئی سورت اترے کہ اللہ پر ایمان لاؤ اور اس کے رسول کے ہمراہ جہاد کرو تو ان کے مقدور والے تم سے رخصت مانگتے ہیں اور کہتے ہیں ہمیں چھوڑ دیجیے کہ بیٹھ رہنے والوں کے ساتھ ہولیں،'),
(1322, 9, 87, 'انہیں پسند آیا کہ پیچھے رہنے والی عورتوں کے ساتھ ہوجائیں اور ان کے دلوں پر مہُر کردی گئیں تو وہ کچھ نہیں سمجھتے'),
(1323, 9, 88, 'لیکن رسول اور جو ان کے ساتھ ایمان لائے انہوں نے اپنے مالوں جانوں سے جہاد کیا، اور انہیں کے لیے بھلائیاں ہیں اور یہی مراد کو پہنچے،'),
(1324, 9, 89, 'اللہ نے ان کے لیے تیار کر رکھی ہیں بہشتیں جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں گے، یہی بڑی مراد ملنی ہے،'),
(1325, 9, 90, 'اور بہانے بنانے والے گنوار آئے کہ انہیں رخصت دی جائے اور بیٹھ رہے وہ جنہوں نے اللہ و رسول سے جھوٹ بولا تھا جلد ان میں کے کافروں کو دردناک عذاب پہنچے گا'),
(1326, 9, 91, 'ضعیفوں پر کچھ حرج نہیں اور نہ بیماروں پر اور نہ ان پر جنہیں خرچ کا مقدور نہ ہو جب کہ اللہ اور رسول کے خیر خواہ رہیں نیکی والوں پر کوئی راہ نہیں اور اللہ بخشنے والا مہربان ہے،'),
(1327, 9, 92, 'اور نہ ان پر جو تمہارے حضور حاضر ہوں کہ تم انہیں سواری عطا فرماؤ تم سے یہ جواب پائیں کہ میرے پاس کوئی چیز نہیں جس پر تمہیں سوار کروں اس پر یوں واپس جائیں کہ ان کی آنکھوں سے آنسو ابلتے ہوں اس غم سے کہ خرچ کا مقدور نہ پایا،'),
(1328, 9, 93, 'مؤاخذہ تو ان سے ہے جو تم سے رخصت مانگتے ہیں اور وہ دولت مند ہیں انہیں پسند آیا کہ عورتوں کے ساتھ پیچھے بیٹھ رہیں اور اللہ نے ان کے دلوں پر مہر کردی تو وہ کچھ نہیں جانتے'),
(1329, 9, 94, 'تم سے بہانے بنائیں گے جب تم ان کی طرف لوٹ کر جاؤ گے تم فرمانا، بہانے نہ بناؤ ہم ہرگز تمہارا یقین نہ کریں گے اللہ نے ہمیں تمہاری خبریں دے دی ہیں، اور اب اللہ و رسول تمہارے کام دیکھیں گے پھر اس کی طرف پلٹ کر جاؤ گے جو چھپے اور ظاہر سب کو جانتا ہے وہ تمہیں جتادے گا جو کچھ تم کرتے تھے،'),
(1330, 9, 95, 'اب تمہارے آگے اللہ کی قسمیں کھائیں گے جب تم ان کی طرف پلٹ کر جاؤ گے اس لیے کہ تم ان کے خیال میں نہ پڑو تو ہاں تم ان کا خیال چھوڑو وہ تو نرے پلید ہیں اور ان کا ٹھکانا جہنم ہے بدلہ اس کا جو کماتے تھے'),
(1331, 9, 96, 'تمہارے آگے قسمیں کھاتے ہیں کہ تم ان سے راضی ہوجاؤ تو اگر تم ان سے راضی ہوجاؤ تو بیشک اللہ تو فاسق لوگوں سے راضی نہ ہوگا'),
(1332, 9, 97, 'گنوار کفر اور نفاق میں زیادہ سخت ہیں اور اسی قابل ہیں کہ اللہ نے جو حکم اپنے رسول پر اتارے اس سے جاہل رہیں، اور اللہ علم و حکمت والا ہے،'),
(1333, 9, 98, 'اور کچھ گنوار وہ ہیں کہ جو اللہ کی راہ میں خرچ کریں تو اسے تاوان سمجھیں اور تم پر گردشیں آنے کے انتظار میں رہیں انہیں پر ہے بری گردش اور اللہ سنتا جانتا ہے،'),
(1334, 9, 99, 'اور کچھ گاؤں والے وہ ہیں جو اللہ اور قیامت پر ایمان رکھتے ہیں اور جو خرچ کریں اسے اللہ کی نزدیکیوں اور رسول سے دعائیں لینے کا ذریعہ سمجھیں ہاں ہاں وہ ان کے لیے باعث قرب ہے اللہ جلد انہیں اپنی رحمت میں داخل کرے گا، بیشک اللہ بخشنے والا مہربان ہے،'),
(1335, 9, 100, 'اور سب میں اگلے پہلے مہاجر اور انصار اور جو بھلائی کے ساتھ ان کے پیرو ہوئے اللہ ان سے راضی اور وہ اللہ سے راضی اور ان کے لیے تیار کر رکھے ہیں باغ جن کے نیچے نہریں بہیں ہمیشہ ہمیشہ ان میں رہیں، یہی بڑی کامیابی ہے،'),
(1336, 9, 101, 'اور تمہارے آس پاس کے کچھ گنوار منافق ہیں، اور کچھ مدینہ والے، ان کی خو ہوگئی ہے نفاق، تم انہیں نہیں جانتے، ہم انھیں جانتے ہیں جلد ہم انہیں دوبارہ عذاب کریں گے پھر بڑے عذاب کی طرف پھیرے جائیں گے'),
(1337, 9, 102, 'اور کچھ اور ہیں جو اپنے گناہوں کے مقر ہوئے اور ملایا ایک کام اچھا اور دوسرا بڑا قریب ہے کہ اللہ ان کی توبہ قبول کرے، بیشک اللہ بخشنے والا مہربان ہے،'),
(1338, 9, 103, 'اے محبوب! ان کے مال میں سے زکوٰة تحصیل کرو جس سے تم انھیں ستھرا اور پاکیزہ کردو اور ان کے حق میں دعائے خیر کرو بیشک تمہاری دعا ان کے دلوں کا چین ہے، اور اللہ سنتا جانتا ہے،'),
(1339, 9, 104, 'کیا انہیں خبر نہیں کہ اللہ ہی اپنے بندوں کی توبہ قبول کرتا اور صدقے خود اپنی دست قدرت میں لیتا ہے اور یہ کہ اللہ ہی توبہ قبول کرنے والا مہربان ہے'),
(1340, 9, 105, 'اور تم فرما ؤ کام کرو اب تمہارے کام دیکھے گا اللہ اور اس کے رسول اور مسلمان، اور جلد اس کی طرف پلٹوگے جو چھپا اور کھلا سب جانتا ہے تو وہ تمہارے کام تمہیں جتاوے گا،'),
(1341, 9, 106, 'اور کچھ موقوف رکھے گئے اللہ کے حکم پر، یا ان پر عذاب کرے یا ان کی توبہ قبول کرے اور اللہ علم و حکمت والا ہے،'),
(1342, 9, 107, 'اور وہ جنہوں نے مسجد بنائی نقصان پہنچانے کو اور کفر کے سبب اور مسلمانوں میں تفرقہ ڈالنے کو اور اس کے انتظار میں جو پہلے سے اللہ اور اس کے رسول کا مخالف ہے اور وہ ضرور قسمیں کھائیں گے ہم نے تو بھلائی چاہی، اور اللہ گواہ ہے کہ وہ بیشک جھوٹے ہیں،'),
(1343, 9, 108, 'اس مسجد میں تم کبھی کھڑے نہ ہونا، بیشک وہ مسجد کہ پہلے ہی دن سے جس کی بنیاد پرہیزگاری پر رکھی گئی ہے وہ اس قابل ہے کہ تم اس میں کھڑے ہو، اس میں وہ لوگ ہیں کہ خوب ستھرا ہونا چاہتے ہیں اور ستھرے اللہ کو پیارے ہیں،'),
(1344, 9, 109, 'تو کیا جس نے اپنی بنیاد رکھی اللہ سے ڈر اور اس کی رضا پر وہ بھلا یا وہ جس نے اپنی نیو چنی ایک گراؤ (ٹوٹے ہوئے کناروں والے) گڑھے کے کنارے تو وہ اسے لے کر جہنم کی آ گ میں ڈھے پڑا اور اللہ ظالموں کو راہ نہیں دیتا،'),
(1345, 9, 110, 'وہ تعمیر جو چنی (کی) ہمیشہ ان کے دلوں میں کھٹکتی رہے گی مگر یہ کہ ان کے دل ٹکڑے ٹکڑے ہوجائیں اور اللہ علم و حکمت والا ہے،'),
(1346, 9, 111, 'بیشک اللہ نے مسلمانوں سے ان کے مال اور جان خریدلیے ہیں اس بدلے پر کہ ان کے لیے جنت ہے اللہ کی راہ میں لڑیں تو ماریں اور مریں اس کے ذمہ کرم پر سچا وعدہ توریت اور انجیل اور قرآن میں اور اللہ سے زیادہ قول کا پورا کون تو خوشیاں مناؤ اپنے سودے کی جو تم نے اس سے کیا ہے، اور یہی بڑی کامیابی ہے،'),
(1347, 9, 112, 'توبہ والے عبادت والے سراہنے والے روزے والے رکوع والے سجدہ والے بھلائی کے بتانے والے اور برائی سے روکنے والے اور اللہ کی حدیں نگاہ رکھنے والے اور خوشی سناؤ مسلمانوں'),
(1348, 9, 113, 'نبی اور ایمان والوں کو لائق نہیں کہ مشرکوں کی بخشش چاہیں اگرچہ وہ رشتہ دار ہوں جبکہ انہیں کھل چکا کہ وہ دوزخی ہیں'),
(1349, 9, 114, 'اور ابراہیم کا اپنے باپ کی بخشش چاہنا وہ تو نہ تھا مگر ایک وعدے کے سبب جو اس سے کرچکا تھا پھر جب ابراہیم کو کھل گیا کہ وہ اللہ کا دشمن ہے اس سے تنکا توڑ دیا (لاتعلق ہوگیا) بیشک ابراہیم بہت آہیں کرنے والا متحمل ہے،'),
(1350, 9, 115, 'اور اللہ کی شان نہیں کہ کسی قوم کو ہدایت کرکے گمراہ فرمائے جب تک انہیں صاف نہ بتادے کہ کسی چیز سے انہیں بچنا ہے بیشک اللہ سب کچھ جانتا ہے،'),
(1351, 9, 116, 'بیشک اللہ ہی کے لیے ہے آسمانوں اور زمین کی سلطنت، جِلاتا ہے اور مارتا ہے، اور اللہ کے سوا نہ تمہارا کوئی والی اور نہ مددگار،'),
(1352, 9, 117, 'بیشک اللہ کی رحمتیں متوجہ ہوئیں ان غیب کی خبریں بتانے والے اور ان مہاجرین اور انصار پر جنہوں نے مشکل کی گھڑی میں ان کا ساتھ دیا بعد اس کے کہ قریب تھا کہ ان میں کچھ لوگوں کے دل پھر جائیں پھر ان پر رحمت سے متوجہ ہوا بیشک وہ ان پر نہایت مہربان رحم والا ہے'),
(1353, 9, 118, 'اور ان تین پر جو موقوف رکھے گئے تھے یہاں تک کہ جب زمین اتنی وسیع ہوکر ان پر تنگ ہوگئی اور ہو اپنی جان سے تنگ آئے اور انہیں یقین ہوا کہ اللہ سے پناہ نہیں مگر اسی کے پاس، پھر ان کی توبہ قبول کی کہ تائب رہیں، بیشک اللہ ہی توبہ قبول کرنے والا مہربان ہے،'),
(1354, 9, 119, 'اے ایمان والو! اللہ سے ڈرو اور سچوں کے ساتھ ہو'),
(1355, 9, 120, 'مدینہ والوں اور ان کے گرد دیہات والوں کو لائق نہ تھا کہ رسول اللہ سے پیچھے بیٹھ رہیں اور نہ یہ کہ ان کی جان سے اپنی جان پیاری سمجھیں یہ اس لیے کہ انہیں جو پیاس یا تکلیف یا بھوک اللہ کی راہ میں پہنچتی ہے اور جہاں ایسی جگہ قدم رکھتے ہیں جس سے کافروں کو غیظ آئے اور جو کچھ کسی دشمن کا بگاڑتے ہیں اس سب کے بدلے ان کے لیے نیک عمل لکھا جاتا ہے بیشک اللہ نیکوں کا نیگ (اجر) ضائع نہیں کرتا،'),
(1356, 9, 121, 'اور جو کچھ خرچ کرتے ہیں چھوٹا یا بڑا اور جو نالا طے کرتے ہیں سب ان کے لیے لکھا جاتا ہے تاکہ اللہ ان کے سب سے بہتر کاموں کا انہیں صلہ دے'),
(1357, 9, 122, 'اور مسلمانوں سے یہ تو ہو نہیں سکتا کہ سب کے سب نکلیں تو کیوں نہ ہو کہ ان کے ہر گروہ میں سے ایک جماعت نکلے کہ دین کی سمجھ حاصل کریں اور واپس آکر اپنی قوم کو ڈر سنائیں اس امید پر کہ وہ بچیں'),
(1358, 9, 123, 'اے ايما ن والوں جہاد کرو ان کافروں سے جو تمہارے قريب ہيں اور چاہیئے کہ وہ تم میں سختی پائیں، اور جان رکھو کہ اللہ پرہیزگاروں کے ساتھ ہے'),
(1359, 9, 124, 'اور جب کوئی سورت اترتی ہے تو ان میں کوئی کہنے لگتا ہے کہ اس نے تم میں کس کے ایمان کو ترقی دی تو وہ جو ایمان والے ہیں ان کے ایمان کو ترقی دی اور وہ خوشیاں منارہے ہیں،'),
(1360, 9, 125, 'اور جن کے دلوں میں آزار ہے انہیں اور پلیدی پر پلیدی بڑھائی اور وہ کفر ہی پر مر گئے،'),
(1361, 9, 126, 'کیا انہیں نہیں سوجھتا ک ہ ہر سال ایک یا دو بار آزمائے جاتے ہیں پھر نہ تو توبہ کرتے ہیں نہ نصیحت مانتے ہیں،'),
(1362, 9, 127, 'اور جب کوئی سورت اترتی ہے ان میں ایک دوسرے کو دیکھنے لگتا ہے کہ کوئی تمہیں دیکھتا تو نہیں پھر پلٹ جاتے ہیں اللہ نے ان کے دل پلٹ دیئے ہیں کہ وہ ناسمجھ لوگ ہیں'),
(1363, 9, 128, 'بیشک تمہارے پاس تشریف لائے تم میں سے وہ رسول جن پر تمہارا مشقت میں پڑنا گِراں ہے تمہاری بھلائی کے نہایت چاہنے والے مسلمانوں پر کمال مہربان مہربان'),
(1364, 9, 129, 'پھر اگر وہ منہ پھیریں تو تم فرمادو کہ مجھے اللہ کافی ہے اس کے سوا کسی کی بندگی نہیں، میں نے اسی پر بھروسہ کیا اور وہ بڑے عرش کا مالک ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'یہ حکمت والی کتاب کی آیتیں ہیں،'),
(1366, 10, 2, 'کیا لوگوں کو اس کا اچنبا ہوا کہ ہم نے ان میں سے ایک مرد کو وحی بھیجی کہ لوگوں کو ڈر سناؤ اور ایمان والوں کو خوشخبری دو کہ ان کے لیے ان کے رب کے پاس سچ کا مقام ہے، کافر بولے بیشک یہ تو کھلا جادوگر ہے'),
(1367, 10, 3, 'بیشک تمہارا رب اللہ ہے جس نے آسمان اور زمین چھ دن میں بنائے پھر عرش پر استوا فرمایا جیسا اس کی شان کے لائق ہے کام کی تدبیر فرما تا ہے کوئی سفارشی نہیں مگر اس کی اجازت کے بعد یہ ہے اللہ تمہارا رب تو اس کی بندگی کرو تو کیا تم دھیان نہیں کرتے،'),
(1368, 10, 4, 'اسی کی طرف تم سب کو پھرنا ہے اللہ کا سچا وعدہ بیشک وہ پہلی بار بناتا ہے پھر فنا کے بعد دوبارہ بنائے گا کہ ان کو جو ایمان لائے اور اچھے کام کیے انصاف کا صلہ دے اور کافروں کے لیے پینے کو کھولتا پانی اور دردناک عذاب بدلہ ان کے کفر کا،'),
(1369, 10, 5, 'وہی ہے جس نے سورج کو جگمگاتا بنا یا اور چاند چمکتا اور اس کے لیے منزلیں ٹھہرائیں کہ تم برسوں کی گنتی اور حساب جانو، اللہ نے اسے نہ بنایا مگر حق نشانیاں مفصل بیان فرماتا ہے علم والوں کے لیے'),
(1370, 10, 6, 'بیشک رات اور دن کا بدلتا آنا اور جو کچھ اللہ نے آسمانوں اور زمین میں پیدا کیا ان میں نشانیاں ہیں ڈر والوں کے لیے،'),
(1371, 10, 7, 'بیشک وہ جو ہمارے ملنے کی امید نہیں رکھتے اور دنیا کی زندگی پسند کر بیٹھے اور اس پر مطمئن ہوگئے اور وہ جو ہماری آیتوں سے غفلت کرتے ہیں'),
(1372, 10, 8, 'ان لوگوں کا ٹھکانا دوزخ ہے بدلہ ان کی کمائی کا،'),
(1373, 10, 9, 'بیشک جو ایمان لائے اور اچھے کام کیے ان کا رب ان کے ایمان کے سبب انھیں راہ دے گا ان کے نیچے نہریں بہتی ہوں گی نعمت کے باغوں میں،'),
(1374, 10, 10, 'ان کی دعا اس میں یہ ہوگی کہ اللہ تجھے پاکی ہے اور ان کے ملتے وقت خوشی کا پہلا بول سلام ہے اور ان کی دعا کا خاتمہ یہ ہے کہ سب خوبیوں کو سراہا اللہ جو رب ہے سارے جہان کا'),
(1375, 10, 11, 'اور اگر اللہ لوگوں پر برائی ایسی جلدبھیجتا جیسی وہ بھلائی کی جلدی کرتے ہیں تو ان کا وعدہ پورا ہوچکا ہوتا تو ہم چھوڑتے انہیں جو ہم سے ملنے کی امید نہیں رکھتے کہ اپنی سرکشی میں بھٹکا کریں'),
(1376, 10, 12, 'اور جب آدمی کو تکلیف پہنچتی ہے ہمیں پکارتا ہے لیٹے اور بیٹھے اور کھڑے پھر جب ہم اس کی تکلیف دور کردیتے ہیں چل دیتا ہے گویا کبھی کسی تکلیف کے پہنچنے پر ہمیں پکارا ہی نہ تھا یونہی بھلے کر دکھائے ہیں حد سے بڑھنے والے کو ان کے کام'),
(1377, 10, 13, 'اور بیشک ہم نے تم سے پہلی سنگتیں (قومیں) ہلاک فرمادیں جب وہ حد سے بڑھے اور ان کے رسول ان کے پاس روشن دلیلیں لے کر آئے اور وہ ایسے تھے ہی نہیں کہ ایمان لاتے، ہم یونہی بدلہ دیتے ہیں مجرموں کو،'),
(1378, 10, 14, 'پھر ہم نے ان کے بعد تمہیں زمین میں جانشین کیا کہ دیکھیں تم کیسے کام کرتے ہو'),
(1379, 10, 15, 'اور جب ان پر ہماری روشن آیتیں پڑھی جاتی ہیں تو وہ کہنے لگتے ہیں جنہیں ہم سے ملنے کی امید نہیں کہ اس کے سوا اور قرآن لے آیئے یا اسی کو بدل دیجیے تم فرماؤ مجھے نہیں پہنچتا کہ میں اسے اپنی طرف سے بدل دوں میں تو اسی کا تابع ہوں جو میری طرف وحی ہوتی ہے میں اگر اپنے رب کی نافرمانی کروں تو مجھے بڑے دن کے عذاب کا ڈر ہے'),
(1380, 10, 16, 'تم فرماؤ اگر اللہ چاہتا تو میں اسے تم پر نہ پڑھتا نہ وہ تم کو اس سے خبردار کرتا تو میں اس سے پہلے تم میں اپنی ایک عمر گزار چکا ہوں تو کیا تمہیں عقل نہیں'),
(1381, 10, 17, 'تو اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے یا اس کی آیتیں جھٹلائے، بیشک مجرموں کا بھلا نہ ہوگا،'),
(1382, 10, 18, 'اور اللہ کے سوا ایسی چیز کو پوجتے ہیں جو ان کا کچھ بھلا نہ کرے اور کہتے ہیں کہ یہ اللہ کے یہاں ہمارے سفارشی ہیں تم فرماؤ کیا اللہ کو وہ بات بتاتے ہو جو اس کے علم میں نہ آسمانوں میں ہے نہ زمین میں اسے پاکی اور برتری ہے ان کے شرک سے،'),
(1383, 10, 19, 'اور لوگ ایک ہی امت تھے پھر مختلف ہوئے، اور اگر تیرے رب کی طرف سے ایک بات پہلے نہ ہوچکی ہوتی تو یہیں ان کے اختلافوں کا ان پر فیصلہ ہوگیا ہوتا'),
(1384, 10, 20, 'اور کہتے ہیں ان پر ان کے رب کی طرف سے کوئی نشانی کیوں نہیں اتری تم فرماؤ غیب تو اللہ کے لیے ہے اب راستہ دیکھو میں بھی تمہارے ساتھ راہ دیکھ رہا ہوں،'),
(1385, 10, 21, 'اور جب کہ ہمارے آدمیوں کو رحمت کا مزہ دیتے ہیں کسی تکلیف کے بعد جو انہیں پہنچی تھی جبھی وہ ہماری آیتوں کے ساتھ داؤں چلتے ہیں تم فرمادو اللہ کی خفیہ تدبیر سب سے جلد ہوجاتی ہے بیشک ہمارے فرشتے تمہارے مکر لکھ رہے ہیں'),
(1386, 10, 22, 'وہی ہے کہ تمہیں خشکی اور تری میں چلاتا ہے یہاں تک کہ جب تم کشتی میں ہو اور وہ اچھی ہوا سے انھیں لے کر چلیں اور اس پر خوش ہوئے ان پر آندھی کا جھونکا آیا اور ہر طرف لہروں نے انہیں آلیا اور سمجھ لے کہ ہم گِھر گئے اس وقت اللہ کو پکارتے ہیں نرے اس کے بندے ہوکر، کہ اگر تو اس سے ہمیں بچالے گا تو ہم ضرور شکر گزار ہوں گے'),
(1387, 10, 23, 'پھر اللہ جب انہیں بچا لیتا ہے جبھی وہ زمین میں ناحق زیادتی کرنے لگتے ہیں اے لوگو! تمہاری زیادتی تمہارے ہی جانوں کا وبال ہے دنیا کے جیتے جی برت لو (فائد اٹھالو)، پھر تمہیں ہماری طرف پھرنا ہے اس وقت ہم تمہیں جتادیں گے جو تمہارے کوتک تھے'),
(1388, 10, 24, 'دنیا کی زندگی کی کہا وت تو ایسی ہی ہے جیسے وہ پانی کہ ہم نے آسمان سے اتارا تو اس کے سبب زمین سے اگنے والی چیزیں سب گھنی ہوکر نکلیں جو کچھ آدمی اور چوپائے کھاتے ہیں یہاں تک کہ جب زمین میں اپنا سنگھار لے لیا اور خوب آراستہ ہوگئی اور اس کے مالک سمجھے کہ یہ ہمارے بس میں آگئی ہمارا حکم اس پر آیا رات میں یا دن میں تو ہم نے اسے کردیا کاٹی ہوئی گویا کل تھی ہی نہیں ہم یونہی آیتیں مفصل بیان کرتے ہیں غور کرنے والوں کے لیے'),
(1389, 10, 25, 'اور اللہ سلامتی کے گھر کی طرف پکارتا ہے اور جسے چاہے سیدھی راہ چلاتا ہے'),
(1390, 10, 26, 'بھلائی والوں کے لیے بھلائی ہے اور اس سے بھی زائد اور ان کے منہ پر نہ چڑھے گی سیاہی اور نہ خواری وہی جنت والے ہیں وہ اس میں ہمیشہ رہیں گے،'),
(1391, 10, 27, 'اور جنہوں نے برائیاں کمائیں تو برائی کا بدلہ اسی جیسا اور ان پر ذلت چڑھے گی، انہیں اللہ سے بچانے والا کوئی نہ ہوگا، گویا ان کے چہروں پر اندھیری رات کے ٹکڑے چڑھا دیئے ہیں وہی دوزخ والے ہیں، وہ اس میں ہمیشہ رہیں گے،'),
(1392, 10, 28, 'اور جس دن ہم ان سب کو اٹھائیں گے پھر مشرکوں سے فرمائیں گے اپنی جگہ رہو تم اور تمہارے شریک تو ہم انہیں مسلمانوں سے جدا کردیں گے اور ان کے شریک ان سے کہیں گے تم ہمیں کب پوجتے تھے'),
(1393, 10, 29, 'تو اللہ گواہ کافی ہے ہم میں اور تم میں کہ ہمیں تمہارے پوجنے کی خبر بھی نہ تھی،'),
(1394, 10, 30, 'یہاں ہر جان جا نچ لے گی جو آگے بھیجا اور اللہ کی طرف پھیرے جائیں گے جو ان کا سچا مولیٰ ہے اور ان کی ساری بناوٹیں ان سے گم ہوجائیں گی'),
(1395, 10, 31, 'تم فرماؤ تمہیں کون روزی دیتا ہے آسمان اور زمین سے یا کون مالک ہے کان اور آنکھوں کا اور کون نکالتا ہے زندہ کو مردے سے اور نکالتا ہے مردہ کو زندہ سے اور کون تمام کاموں کی تدبیر کرتا ہے تو اب کہیں گے کہ اللہ تو تم فرماؤ تو کیوں نہیں ڈرتے'),
(1396, 10, 32, 'تو یہ اللہ ہے تمہارا سچا رب پھر حق کے بعد کیا ہے مگر گمراہی پھر کہاں پھرے جاتے ہو،'),
(1397, 10, 33, 'یونہی ثابت ہوچکی ہے تیرے رب کی بات فاسقوں پر تو وہ ایمان نہیں لائیں گے،'),
(1398, 10, 34, 'تم فرماؤ تمہارے شریکوں میں کوئی ایسا ہے کہ اول بنائے پھر فنا کے بعد دوبارہ بنائے تم فرماؤ اللہ اوّل بناتا ہے پھر فنا کے بعد دوبارہ بنائے گا تو کہاں اوندھے جاتے ہو'),
(1399, 10, 35, 'تم فرماؤ تمہارے شریکوں میں کوئی ایسا ہے کہ حق کی راہ دکھائے تم فرماؤ کہ اللہ حق کی راہ دکھاتا ہے، تو کیا جو حق کی راہ دکھائے اس کے حکم پر چلنا چاہیے یا اس کے جو خود ہی راہ نہ پائے جب تک راہ نہ دکھایا جائے تو تمہیں کیا ہوا کیسا حکم لگاتے ہو،'),
(1400, 10, 36, 'اور ان میں اکثر تو نہیں چلتے مگر گمان پر بیشک گمان حق کا کچھ کام نہیں دیتا، بیشک اللہ ان کاموں کو جانتا ہے،'),
(1401, 10, 37, 'اور اس قرآن کی یہ شان نہیں کہ کوئی اپنی طرف سے بنالے بے اللہ کے اتارے ہاں وہ اگلی کتابوں کی تصدیق ہے اور لوح میں جو کچھ لکھا ہے سب کی تفصیل ہے اس میں کچھ شک نہیں ہے پروردگار عالم کی طرف سے ہے،'),
(1402, 10, 38, 'کیا یہ کہتے ہیں کہ انہوں نے اسے بنالیا ہے تم فرماؤ تو اس جیسی کوئی ایک سورة لے آؤ اور اللہ کو چھوڑ کر جو مل سکیں سب کو بلا لاؤ اگر تم سچے ہو،'),
(1403, 10, 39, 'بلکہ اسے جھٹلایا جس کے علم پر قابو نہ پایا اور ابھی انہوں نے اس کا انجام نہیں دیکھا ایسے ہی ان سے اگلوں نے جھٹلایا تھا تو دیکھو ظالموں کیسا انجام ہوا'),
(1404, 10, 40, 'اور ان میں کوئی اس پر ایمان لاتا ہے اور ان میں کوئی اس پر ایمان نہیں لاتا ہے، اور تمہارا رب مفسدوں کو خوب جانتا ہے'),
(1405, 10, 41, 'اور اگر وہ تمہیں جھٹلائیں تو فرمادو کہ میرے لیے میری کرنی اور تمہارے لیے تمہاری کرنی (اعمال) تمہیں میرے کام سے علاقہ نہیں اور مجھے تمہارے کام سے لاتعلق نہیں'),
(1406, 10, 42, 'اور ان میں کوئی وہ ہیں جو تمہاری طرف کان لگاتے ہیں تو کیا تم بہروں کو سنا دو گے اگرچہ انہیں عقل نہ ہو'),
(1407, 10, 43, 'اور ان میں کوئی تمہاری طرف تکتا ہے کیا تم اندھوں کو راہ دکھا دو گے اگرچہ وہ نہ سوجھیں،'),
(1408, 10, 44, 'بیشک اللہ لوگوں پر کچھ ظلم نہیں کرتا ہاں لوگ ہی اپنی جانوں پر ظلم کرتے ہیں'),
(1409, 10, 45, 'اور جس دن انہیں اٹھائے گا گویا دنیا میں نہ رہے تھے مگر اس دن کی ایک گھڑی آپس میں پہچان کریں گے کہ پورے گھاٹے میں رہے وہ جنہوں نے اللہ سے ملنے کو جھٹلایا اور ہدایت پر نہ تھے'),
(1410, 10, 46, 'اور اگر ہم تمہیں دکھا دیں کچھ اس میں سے جو انہیں وعدہ دے رہے ہیں یا تمہیں پہلے ہی اپنے پاس بلالیں بہرحال انہیں ہماری طرف پلٹ کر آنا ہے پھر اللہ گواہ ہے ان کے کاموں پر،'),
(1411, 10, 47, 'اور ہر امت میں ایک رسول ہوا جب ان کا رسول ان کے پاس آتا ان پر انصاف کا فیصلہ کردیا جاتا اور ان پر ظلم نہیں ہوتا،'),
(1412, 10, 48, 'اور کہتے ہیں یہ وعدہ کب آئے گا اگر تم سچے ہو'),
(1413, 10, 49, 'تم فرماؤ میں اپنی جان کے برے بھلے کا (ذاتی) اختیار نہیں رکھتا مگر جو اللہ چاہے ہر گروہ کا ایک وعدہ ہے جب ان کا وعدہ آئے گا تو ایک گھڑی نہ پیچھے ہٹیں نہ آگے بڑھیں،'),
(1414, 10, 50, 'تم فرماؤ بھلا بتاؤ تو اگر اس کا عذاب تم پر رات کو آئے یا دن کو تو اس میں وہ کونسی چیز ہے کہ مجرموں کو جس کی جلدی ہے،'),
(1415, 10, 51, 'تو کیا جب ہو پڑے گا اس وقت اس کا یقین کرو گے کیا اب مانتے ہو پہلے تو اس کی جلدی مچارہے تھے،'),
(1416, 10, 52, 'پھر ظالموں سے کہا جائے گا ہمیشہ کا عذاب چکھو تمہیں کچھ اور بدلہ نہ ملے گا مگر وہی جو کماتے تھے'),
(1417, 10, 53, 'اور تم سے پوچھتے ہیں کیا وہ حق ہے، تم فرماؤ، ہاں! میرے رب کی قسم بیشک وہ ضرور حق ہے، اور تم کچھ تھکا نہ سکو گے'),
(1418, 10, 54, 'اور اگر ہر ظالم جان، زمین میں جو کچھ ہے سب کی مالک ہوتی، ضرور اپنی جان چھڑانے میں دیتی اور دل میں چپکے چپکے پشیمان ہوئے جب عذاب دیکھا اور ان میں انصاف سے فیصلہ کردیا گیا اور ان پر ظلم نہ ہوگا،'),
(1419, 10, 55, 'سن لو بیشک اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور زمین میں سن لو بیشک اللہ کا وعدہ سچا ہے مگر ان میں اکثر کو خبر نہیں،'),
(1420, 10, 56, 'وہ جِلاتا اور مارتا ہے اور اسی کی طرف پھرو گے،'),
(1421, 10, 57, 'اے لوگو! تمہارے پاس تمہارے رب کی طرف سے نصیحت آئی اور دلوں کی صحت اور ہدایت اور رحمت ایمان والوں کے لیے،'),
(1422, 10, 58, 'تم فرماؤ اللہ ہی کے فضل اور اسی کی رحمت اور اسی پر چاہیے کہ خوشی کریں وہ ان کے سب دھن دولت سے بہتر ہے،'),
(1423, 10, 59, 'تم فرماؤ بھلا بتاؤ تو وہ جو اللہ نے تمہارے لیے رزق اتارا اس میں تم نے اپنی طرف سے حرام و حلال ٹھہرالیا تم فرماؤ کیا اللہ نے اس کی تمہیں اجازت دی یا اللہ پر جھوٹ باندھتے ہو'),
(1424, 10, 60, 'اور کیا گمان ہے ان کا، جو اللہ پر جھوٹ باندھتے ہیں کہ قیامت میں ان کا کیا حال ہوگا، بیشک اللہ لوگوں پر فضل کرتا ہے مگر اکثر لوگ شکر نہیں کرتے،'),
(1425, 10, 61, 'اور تم کسی کام میں ہو اور اس کی طرف سے کچھ قرآن پڑھو اور تم لوگ کوئی کام کرو ہم تم پر گواہ ہوتے ہیں جب تم اس کو شروع کرتے ہو، اور تمہارے رب سے ذرہ بھر کوئی چیز غائب نہیں زمین میں نہ آسمان میں اور نہ اس سے چھوٹی اور نہ اس سے بڑی کوئی چیز نہیں جو ایک روشن کتاب میں نہ ہو'),
(1426, 10, 62, 'سن لو بیشک اللہ کے ولیوں پر نہ کچھ خو ف ہے نہ کچھ غم'),
(1427, 10, 63, 'وہ جو ایمان لائے اور پرہیزگاری کرتے ہیں،'),
(1428, 10, 64, 'انہیں خوشخبری ہے دنیا کی زندگی میں اور آخرت میں، اللہ کی باتیں بدل نہیں سکتیں یہی بڑی کامیابی ہے،'),
(1429, 10, 65, 'اور تم ان کی باتو ں کا غم نہ کرو بیشک عزت ساری اللہ کے لیے ہے وہی سنتا جانتا ہے،'),
(1430, 10, 66, 'سن لو بیشک اللہ ہی کے مِلک ہیں جتنے آسمانوں میں ہیں اور جتنے زمینوں میں اور کاہے کے پیچھے جارہے ہیں وہ جو اللہ کے سوا شریک پکار رہے ہیں، وہ تو پیچھے نہیں جاتے مگر گمان کے اور وہ تو نہیں مگر اٹکلیں دوڑاتے'),
(1431, 10, 67, 'وہی ہے جس نے تمہارے لیے رات بنائی کہ اس میں چین پاؤ اور دن بنایا تمہاری آنکھوں کھولتا بیشک اس میں نشانیاں ہیں سننے والوں کے لیے'),
(1432, 10, 68, 'بولے اللہ نے اپنے لیے اولاد بنائی پاکی اس کو، وہی بے نیاز ہے، اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں تمہارے پاس اس کی کوئی بھی سند نہیں، کیا اللہ پر وہ بات بتاتے ہو جس کا تمہیں علم نہیں،'),
(1433, 10, 69, 'تم فرماؤ وہ جو اللہ پر جھوٹ باندھتے ہیں ان کا بھلا نہ ہوگا،'),
(1434, 10, 70, 'دنیا میں کچھ برت لینا (فائدہ اٹھانا) ہے پھر انہیں ہماری طرف واپس آنا پھر ہم انہیں سخت عذاب چکھائیں گے بدلہ ان کے کفر کا،'),
(1435, 10, 71, 'اور انہیں نوح کی خبر پڑھ کر سناؤ جب اس نے اپنی قوم سے کہا اے میری قوم اگر تم پر شاق گزرا ہے میرا کھڑا ہونا اور اللہ کی نشانیاں یاد دلانا تو میں نے اللہ ہی پر بھروسہ کیا تو مِل کر کام کرو اور اپنے جھوٹے معبودوں سمیت اپنا کام پکا کرلو تمہارے کام میں تم پر کچھ گنجلک (الجھن) نہ رہے پھر جو ہو سکے میرا کرلو اور مجھے مہلت نہ دو'),
(1436, 10, 72, 'پھر اگر تم منہ پھیرو تو میں تم سے کچھ اجرت نہیں مانگتا میرا اجر تو نہیں مگر اللہ پر اور مجھے حکم ہے کہ میں مسلمانوں سے ہوں،'),
(1437, 10, 73, 'تو انہوں نے اسے جھٹلایا تو ہم نے اسے اور جو اس کے ساتھ کشتی میں تھے ان کو نجات دی اور انہیں ہم نے نائب کیا اور جنہوں نے ہماری آیتیں جھٹلائیں ان کو ہم نے ڈبو دیا تو دیکھو ڈرائے ہوؤں کا انجام کیسا ہوا،'),
(1438, 10, 74, 'پھر اس کے بعد اور رسول ہم نے ان کی قوموں کی طرف بھیجے تو وہ ان کے پاس روشن دلیلیں لائے تو وہ ایسے نہ تھے کہ ایمان لاتے اس پر جسے پہلے جھٹلا چکے تھے، ہم یونہی مہر لگادیتے ہیں سرکشوں کے دلوں پر،'),
(1439, 10, 75, 'پھر ان کے بعد ہم نے موسٰی اور ہارون کو فرعون اور اس کے درباریوں کی طرف اپنی نشانیاں دے کر بھیجا تو انہوں نے تکبر کیا اور وہ مجرم لوگ تھے،'),
(1440, 10, 76, 'تو جب ان کے پاس ہماری طرف سے حق آیا بولے یہ تو ضرور کھلا جادو ہے،'),
(1441, 10, 77, 'موسیٰ نے کہا کیا حق کی نسبت ایسا کہتے ہو جب وہ تمہارے پاس آیا کیا یہ جادو ہے اور جادوگر مراد کو نہیں پہنچتے،'),
(1442, 10, 78, 'بولے کیا تم ہمارے پاس اس لیے آئے ہو کہ ہمیں اس سے پھیردو جس پر ہم نے اپنے باپ دادا کو پایا اور زمین میں تمہیں دونوں کی بڑائی رہے، اور ہم تم پر ایمان لانے کے نہیں،'),
(1443, 10, 79, 'اور فرعون بولا ہر جادوگر علم والے کو میرے پاس لے آؤ،'),
(1444, 10, 80, 'پھر جب جادوگر آئے ان سے موسیٰ نے کہا ڈالو جو تمہیں ڈالنا ہے'),
(1445, 10, 81, 'پھر جب انہوں نے ڈالا موسیٰ نے کہا یہ جو تم لائے یہ جادو ہے اب اللہ اسے باطل کردے گا، اللہ مفسدوں کا کام نہیں بناتا،'),
(1446, 10, 82, 'اور اللہ اپنی باتوں سے حق کو حق کر دکھاتا ہے پڑے برا مانیں مجرم،'),
(1447, 10, 83, 'تو موسیٰ پر ایمان نہ لائے مگر اس کی قوم کی اولاد سے کچھ لوگ فرعون اور اس کے درباریوں سے ڈرتے ہوئے کہ کہیں انہیں ہٹنے پر مجبور نہ کردیں اور بیشک فرعون زمین پر سر اٹھانے والا تھا، اور بیشک وہ حد سے گزر گیا'),
(1448, 10, 84, 'اور موسیٰ نے کہا اے میری قوم اگر تم اللہ پر ایمان لائے تو اسی پر بھروسہ کرو اگر تم اسلام رکھتے ہو،'),
(1449, 10, 85, 'بولے ہم نے اللہ پر بھروسہ کیا الہٰی ہم کو ظالم لوگوں کے لیے آزمائش نہ بنا'),
(1450, 10, 86, 'اور اپنی رحمت فرماکر ہمیں کافروں سے نجات دے'),
(1451, 10, 87, 'اور ہم نے موسیٰ اور اس کے بھائی کو وحی بھیجی کہ مصر میں اپنی قوم کے لیے مکانات بناؤ اور اپنے گھروں کو نماز کی جگہ کرو اور نماز قائم رکھو، اور مسلمانوں کو خوشخبری سناؤ'),
(1452, 10, 88, 'اور موسیٰ نے عرض کی اے رب ہمارے تو نے فرعون اور اس کے سرداروں کو آرائش اور مال دنیا کی زندگی میں دیے، اے رب ہمارے! اس لیے کہ تیری راہ سے بہکادیں، اے رب ہمارے! ان کے مال برباد کردے اور ان کے دل سخت کردے کہ ایمان نہ لائیں جب تک دردناک عذاب نہ دیکھ لیں'),
(1453, 10, 89, 'فرمایا تم دونوں کی دعا قبول ہوئی تو ثابت قدم رہو اور نادانوں کی راہ نہ چلو'),
(1454, 10, 90, 'اور ہم بنی اسرائیل کو دریا پار لے گئے تو فرعون اور اس کے لشکروں نے ان کا پیچھا کیا سرکشی اور ظلم سے یہاں تک کہ جب اسے ڈوبنے نے ا ٓ لیا بولا میں ایمان لایا کہ کوئی سچا معبود نہیں سوا اس کے جس پر بنی اسرائیل ایمان لائے اور میں مسلمان ہوں'),
(1455, 10, 91, 'کیا اب اور پہلے سے نافرمان رہا اور تو فسادی تھا'),
(1456, 10, 92, 'آج ہم تیری لاش کو اوترا دیں (باقی رکھیں) گے تو اپنے پچھلوں کے لیے نشانی ہو اور بیشک لوگ ہما ری آ یتو ں سے غافل ہیں،'),
(1457, 10, 93, 'اور بیشک ہم نے بنی اسرائیل کو عزت کی جگہ دی اور انہیں ستھری روزی عطا کی تو اختلاف میں نہ پڑے مگر علم آنے کے بعد بیشک تمہارا رب قیامت کے دن ان میں فیصلہ کردے گا جس بات میں جھگڑتے تھے'),
(1458, 10, 94, 'اور اے سننے والے! اگر تجھے کچھ شبہ ہو اس میں جو ہم نے تیری طرف اتارا تو ان سے پوچھ دیکھ جو تجھ سے پہلے کتاب پڑھنے والے ہیں بیشک تیرے پاس تیرے رب کی طرف سے حق آیا تو تُو ہر گز شک والوں میں نہ ہو،'),
(1459, 10, 95, 'اور ہرگز ان میں نہ ہونا جنہوں نے اللہ کی آیتیں جھٹلائیں کہ تو خسارے والوں میں ہوجائے گا،'),
(1460, 10, 96, 'بیشک وہ جن پر تیرے رب کی بات ٹھیک پڑچکی ہے ایمان نہ لائیں گے،'),
(1461, 10, 97, 'اگرچہ سب نشانیاں ان کے پاس آئیں جب تک دردناک عذاب نہ دیکھ لیں'),
(1462, 10, 98, 'تو ہوئی ہوتی نہ کوئی بستی کہ ایمان لاتی تو اس کا ایمان کام آتا ہاں یونس کی قوم، جب ایمان لائے ہم نے ان سے رسوائی کا عذاب دنیا کی زندگی میں ہٹادیا اور ایک وقت تک انہیں برتنے دیا'),
(1463, 10, 99, 'اور اگر تمہارا رب چاہتا زمین میں جتنے ہیں سب کے سب ایمان لے آتے تو کیا تم لوگوں کو زبردستی کرو گے یہاں تک کہ مسلمان ہوجائیں'),
(1464, 10, 100, 'اور کسی جان کی قدرت نہیں کہ ایمان لے آئے مگر اللہ کے حکم سے اور عذاب ان پر ڈالنا ہے جنہیں عقل نہیں،'),
(1465, 10, 101, 'تم فرماؤ دیکھو آسمانوں اور زمین میں کیا ہے اور آیتیں اور رسول انہیں کچھ نہیں دیتے جن کے نصیب میں ایمان نہیں،'),
(1466, 10, 102, 'تو انہیں کاہے کا انتظار ہے مگر انہیں لوگوں کے سے دنوں کا جو ان سے پہلے ہو گزرے تم فرماؤ تو انتظار کرو میں بھی تمہارے ساتھ انتظار میں ہوں'),
(1467, 10, 103, 'پھر ہم اپنے رسولوں اور ایمان والوں کو نجات دیں گے بات یہی ہے ہمارے ذمہ کرم پر حق ہے مسلمانوں کو نجات دینا،'),
(1468, 10, 104, 'تم فرماؤ، اے لوگو! اگر تم میرے دین کی طرف سے کسی شبہ میں ہو تو میں تو اسے نہ پوجوں کا جسے تم اللہ کے سوا پوجتے ہو ہاں اس اللہ کو پوجتا ہوں جو تمہاری جان نکالے گا اور مجھے حکم ہے کہ ایمان والوں میں ہوں،'),
(1469, 10, 105, 'اور یہ کہ اپنا منہ دین کے لیے سیدھا رکھ سب سے الگ ہوکر اور ہرگز شرک والوں میں نہ ہونا،'),
(1470, 10, 106, 'اور اللہ کے سوا اس کی بندگی نہ کر جو نہ تیرا بھلا کرسکے نہ برا، پھر اگر ایسا کرے تو اس وقت تو ظالموں سے ہوگا،'),
(1471, 10, 107, 'اور اگر تجھے اللہ کوئی تکلیف پہنچائے تو اس کا کوئی ٹالنے والا نہیں اس کے سوا، اور اگر تیرا بھلا چاہے تو اس کے فضل کے رد کرنے والا کوئی نہیں اسے پہنچا تا ہے اپنے بندوں میں جسے چاہے، اور وہی بخشنے والا مہربان ہے،'),
(1472, 10, 108, 'تم فرماؤ اے لوگو! تمہارے پاس تمہارے رب کی طرف سے حق آیا تو جو راہ پر آیا وہ اپنے بھلے کو راہ پر آیا اور جو بہکا وہ اپنے برے کو بہکا اور کچھ میں کڑوڑا (حاکمِ اعلیٰ) نہیں'),
(1473, 10, 109, 'اور اس پر چلو جو تم پر وحی ہوتی ہے اور صبر کرو یہاں تک کہ اللہ حکم فرمائے اور وہ سب سے بہتر حکم فرمانے والا ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'یہ ایک کتاب ہے جس کی آیتیں حکمت بھری ہیں پھر تفصیل کی گئیں حکمت والے خبردار کی طرف سے،'),
(1475, 11, 2, 'کہ بندگی نہ کرو مگر اللہ کی بیشک میں تمہارے لیے اس کی طرف سے ڈر اور خوشی سنانے والا ہوں'),
(1476, 11, 3, 'اور یہ کہ اپنے رب سے معافی مانگو پھر اس کی طرف توبہ کرو تمہیں بہت اچھا برتنا (فائدہ اٹھانا) دے گا ایک ٹھہرائے وعدہ تک اور ہر فضیلت والے کو اس کا فضل پہنچائے گا اور اگر منہ پھیرو تو میں تم پر بڑے دن کے عذاب کا خوف کرتا ہوں،'),
(1477, 11, 4, 'تمہیں اللہ ہی کی طرف پھرنا ہے اور وہ ہر شے پر قادر'),
(1478, 11, 5, 'سنو وہ اپنے سینے دوہرے کرتے (منہ چھپاتے) ہیں کہ اللہ سے پردہ کریں سنو جس وقت وہ اپنے کپڑوں سے سارا بدن ڈھانپ لیتے ہیں اس وقت بھی اللہ ان کا چھپا اور ظاہر سب کچھ جانتا ہے بیشک وہ دلوں کی بات جاننے والا ہے،'),
(1479, 11, 6, 'اور زمین پر چلنے والا کوئی ایسا نہیں جس کا رزق اللہ کے ذمہٴ کرم پر نہ ہو اور جانتا ہے کہ کہاں ٹھہرے گا اور کہاں سپرد ہوگا سب کچھ ایک صاف بیان کرنے والی کتاب میں ہے،'),
(1480, 11, 7, 'اور وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں بنایا اور اس کا عرش پانی پر تھا کہ تمہیں آزمائے تم میں کس کا کام اچھا ہے، اور اگر تم فرماؤ کہ بیشک تم مرنے کے بعد اٹھائے جاؤ گے تو کافر ضرور کہیں گے کہ یہ تو نہیں مگر کھلا جادو'),
(1481, 11, 8, 'اور اگر ہم ان سے عذاب کچھ گنتی کی مدت تک ہٹادیں تو ضرور کہیں گے کس چیز نے روکا ہے سن لو جس دن ان پر آئے گا ان سے پھیرا نہ جائے گا، اور انہیں گھیرے گا وہی عذاب جس کی ہنسی اڑاتے تھے'),
(1482, 11, 9, 'اور اگر ہم آدمی کو اپنی کسی رحمت کا مزہ دیں پھر اسے اس سے چھین لیں ضرور وہ بڑا ناامید ناشکرا ہے'),
(1483, 11, 10, 'اور اگر ہم اسے نعمت کا مزہ دیں اس مصیبت کے بعد جس اسے پہنچی تو ضرور کہے گا کہ برائیاں مجھ سے دور ہوئیں بیشک وہ خوش ہونے والا بڑائی مارنے والا ہے'),
(1484, 11, 11, 'مگر جنہوں نے صبر کیا اور اچھے کام کیے ان کے لیے بخشش اور بڑا ثواب ہے،'),
(1485, 11, 12, 'تو کیا جو وحی تمہاری طرف ہوتی ہے اس میں سے کچھ تم چھوڑ دو گے اور اس پر دل تنگ ہوگے اس بناء پر کہ وہ کہتے ہیں ان کے ساتھ کوئی خزانہ کیوں نہ اترا ان کے ساتھ کوئی فرشتہ آتا، تم تو ڈر سنانے والے ہو اور اللہ ہر چیز پر محافظ ہے،'),
(1486, 11, 13, 'کیا یہ کہتے ہیں کہ انھوں نے اسے جی سے بنالیا، تم فرماؤ کہ تم ایسی بنائی ہوئی دس سورتیں لے آؤ اور اللہ کے سوا جو مل سکیں سب کو بلالو اگر تم سچے ہو'),
(1487, 11, 14, 'تو اے مسلمانو اگر وہ تمہاری اس بات کا جواب نہ دے سکیں تو سمجھ لو کہ وہ اللہکے علم ہی سے اترا ہے اور یہ کہ اس کے سوا کوئی سچا معبود نہیں تو کیا اب تم مانو گے'),
(1488, 11, 15, 'جو دنیا کی زندگی اور آرائش چاہتا ہو ہم اس میں ان کا پورا پھل دے دیں گے اور اس میں کمی نہ دیں گے،'),
(1489, 11, 16, 'یہ ہیں وہ جن کے لیے آخرت میں کچھ نہیں مگر آ گ اور اکارت گیا جو کچھ وہاں کرتے تھے اور نابود ہوئے جو ان کے عمل تھے'),
(1490, 11, 17, 'تو کیا وہ جو اپنے رب کی طرف سے روشن دلیل پر ہو اور اس پر اللہ کی طرف سے گواہ آئے اور اس سے پہلے موسیٰ کی کتاب پیشوا اور رحمت وہ اس پر ایمان لاتے ہیں، اور جو اس کا منکر ہو سارے گروہوں میں تو آگ اس کا وعدہ ہے تو اے سننے والے! تجھے کچھ اس میں شک نہ ہو، بیشک وہ حق ہے تیرے رب کی طرف سے لیکن بہت آدمی ایمان نہیں رکھتے،'),
(1491, 11, 18, 'اور اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے اور اپنے رب کے حضور پیش کیے جائیں گے اور گواہ کہیں گے یہ ہیں جنہوں نے اپنے رب پر جھوٹ بولا تھا، ارے ظا لموں پر خدا کی لعنت'),
(1492, 11, 19, 'جو اللہ کی راہ سے روکتے ہیں اور اس میں کجی چاہتے ہیں، اور وہی آخرت کے منکر ہیں،'),
(1493, 11, 20, 'وہ تھکانے والے نہیں زمین میں اور نہ اللہ سے جدا ان کے کوئی حمایتی انہیں عذاب پر عذاب ہوگا وہ نہ سن سکتے تھے اور نہ دیکھتے'),
(1494, 11, 21, 'وہی ہیں جنہوں نے اپنی جانیں گھاٹے میں ڈالیں اور ان سے کھوئی گئیں جو باتیں جوڑتے تھے خواہ نخواہ'),
(1495, 11, 22, '(ضرور) وہی آخرت میں سب سے زیادہ نقصان میں ہیں'),
(1496, 11, 23, 'بیشک جو ایمان لائے اور اچھے کام کیے اور اپنے رب کی طرف رجوع لائے وہ جنت والے ہیں، وہ اس میں ہمیشہ رہیں گے،'),
(1497, 11, 24, 'دونوں فریق کا حال ایسا ہے جیسے ایک اندھا اور بہرا اور دوسرا دیکھتا اور سنتا کيا ان دونوں حال کا ايک سا ہے تو کياتم دہيان نہيں کرتے'),
(1498, 11, 25, 'اور بے شک ہم نے نوح کو اس کی قوم کی طرف بھيجا کہ میں تمہارے لیے صریح ڈر سنانے والا ہوں'),
(1499, 11, 26, 'کہ اللہ کے سوا کسی کو نہ پوجو، بیشک میں تم پر ایک مصیبت والے دن کے عذاب سے ڈرتا ہوں'),
(1500, 11, 27, 'تو اس کی قوم کے سردار جو کافر ہوئے تھے بولے ہم تو تمہیں اپنے ہی جیسا آدمی دیکھتے ہیں اور ہم نہیں دیکھتے کہ تمہاری پیروی کسی نے کی ہو مگر ہمارے کمینوں نے سرسری نظر سے اور ہم تم میں اپنے اوپر کوئی بڑائی نہیں پاتے بلکہ ہم تمہیں جھوٹا خیال کرتے ہیں،'),
(1501, 11, 28, 'بولا اے میری قوم! بھلا بتاؤ تو اگر میں اپنے رب کی طرف سے دلیل پر ہوں اور اس نے مجھے اپنے پاس سے رحمت بخشی تو تم اس سے اندھے رہے، کیا ہم اسے تمہارے گلے چپیٹ (چپکا) دیں اور تم بیزار ہو'),
(1502, 11, 29, 'اور اے قوم! میں تم سے کچھ اس پر مال نہیں مانگتا میرا اجر تو اللہ ہی پر ہے اور میں مسلمانوں کو دور کرنے والا نہیں بیشک وہ اپنے رب سے ملنے والے ہیں لیکن میں تم کو نرے جاہل لوگ پا تا ہوں'),
(1503, 11, 30, 'اور اے قوم مجھے اللہ سے کون بچالے گا اگر میں انہیں دور کروں گا، تو کیا تمہیں دھیان نہیں،'),
(1504, 11, 31, 'اور میں تم سے نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں اور نہ یہ کہ میں غیب جان جانتا ہوں اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں اور میں انہیں نہیں کہتا جن کو تمہاری نگاہیں حقیر سمجھتی ہیں کہ ہرگز انہیں اللہ کوئی بھلائی نہ دے گا، اللہ خوب جانتا ہے جو ان کے دلوں میں ہے ایسا کروں تو ضرور میں ظالموں میں سے ہوں'),
(1505, 11, 32, 'بولے اے نوح تم ہم سے جھگڑے اور بہت ہی جھگڑے تو لے ا ٓ ؤ جس کا ہمیں وعدے دے رہے ہو اگر تم سچے ہو،'),
(1506, 11, 33, 'بولا وہ تو اللہ تم پر لائے گا اگر چاہے اور تم تھکا نہ سکو گے'),
(1507, 11, 34, 'اور تمہیں میری نصیحت نفع نہ دے گی اگر میں تمہارا بھلا چاہوں جبکہ اللہ تمہاری گمراہی چاہے، وہ تمہارا رب ہے، اور اسی کی طرف پھرو گے'),
(1508, 11, 35, 'کیا یہ کہتے ہیں کہ انہوں نے اپنے جی سے بنالیا تم فرماؤ اگر میں نے بنالیا ہوگا تو میرا گناہ مجھ پر ہے اور میں تمہارے گناہ سے الگ ہوں،'),
(1509, 11, 36, 'اور نوح کو وحی ہوئی تمہاری قوم سے مسلمان نہ ہوں گے مگر جتنے ایمان لاچکے تو غم نہ کھا اس پر جو وہ کرتے ہیں'),
(1510, 11, 37, 'اور کشتی بناؤ ہمارے سامنے اور ہمارے حکم سے اور ظالموں کے بارے میں مجھ سے بات نہ کرنا وہ ضرور ڈوبائے جائیں گے'),
(1511, 11, 38, 'اور نوح کشتی بناتا ہے اور جب اس کی قوم کے سردار اس پر گزرتے اس پر ہنستے بولا اگر تم ہم پر ہنستے ہو تو ایک وقت ہم تم پر ہنسیں گے جیسا تم ہنستے ہو'),
(1512, 11, 39, 'تو اب جان جاؤ گے کس پر آتا ہے وہ عذاب کہ اسے رسوا کرے اور اترتا ہے وہ عذاب جو ہمیشہ رہے'),
(1513, 11, 40, 'یہاں تک کہ کہ جب ہمارا حکم آیا اور تنور اُبلا ہم نے فرمایا کشتی میں سوار کرلے ہر جنس میں سے ایک جوڑا نر و مادہ اور جن پر بات پڑچکی ہے ان کے سوا اپنے گھر والوں اور باقی مسلمانوں کو اور اس کے ساتھ مسلمان نہ تھے مگر تھوڑے'),
(1514, 11, 41, 'اور بولا اس میں سوار ہو اللہ کے نام پر اس کا چلنا اور اس کا ٹھہرنا بیشک میرا رب ضرور بخشنے والا مہربان ہے،'),
(1515, 11, 42, 'اور وہی انہیں لیے جارہی ہے ایسی موجوں میں جیسے پہاڑ اور نوح نے اپنے بیٹے کو پکارا اور وہ اس سے کنارے تھا اے میرے بچے ہمارے ساتھ سوار ہوجا اور کافروں کے ساتھ نہ ہو'),
(1516, 11, 43, 'بولا اب میں کسی پہاڑ کی پناہ لیتا ہوں وہ مجھے پانی سے بچالے گا، کہا آج اللہ کے عذاب سے کوئی بچانے والا نہیں مگر جس پر وہ رحم کرے اور ان کے بیچ میں موج آڑے آئی تو وہ ڈوبتوں میں رہ گیا'),
(1517, 11, 44, 'اور حکم فرمایا گیا کہ اے زمین! اپنا پانی نگل لے اور اے آسمان! تھم جا اور پانی خشک کردیا گیا اور کام تمام ہوا اور کشتی کوہ ِ جودی پر ٹھہری اور فرمایا گیا کہ دور ہوں بے انصاف لوگ،'),
(1518, 11, 45, 'اور نوح نے اپنے رب کو پکارا عرض کی اے میرے رب میرا بیٹا بھی تو میرا گھر والا ہے اور بیشک تیرا وعدہ سچا ہے اور تو سب سے بڑا حکم والا'),
(1519, 11, 46, 'فرمایا اے نوح! وہ تیرے گھر والوں میں نہیں بیشک اس کے کام بڑے نالائق ہیں، تو مجھ سے وہ بات نہ مانگ جس کا تجھے علم نہیں میں تجھے نصیحت فرماتا ہوں کہ نادان نہ بن،'),
(1520, 11, 47, 'عرض کی اے رب میرے میں تیری پناہ چاہتا ہوں کہ تجھ سے وہ چیز مانگوں جس کا مجھے علم نہیں، اور اگر تو مجھے نہ بخشے اور رحم نہ کرے تو میں زیاں کار ہوجاؤں،'),
(1521, 11, 48, 'فرمایا گیا اے نوح! کشتی سے اتر ہماری طرف سے سلام اور برکتوں کےساتھ جو تجھ پر ہیں اور تیرے ساتھ کے کچھ گروہوں پر اور کچھ گروہ ہیں جنہیں ہم دنیا برتنے دیں گے پھر انہیں ہماری طرف سے دردناک عذاب پہنچے گا'),
(1522, 11, 49, 'یہ غیب کی خبریں ہم تمہاری طرف وحی کرتے ہیں انہیں نہ تم جانتے تھے نہ تمہاری قوم اس سے پہلے، تو صبر کرو بیشک بھلا انجام پرہیزگاروں کا'),
(1523, 11, 50, 'اور عاد کی طرف ان کے ہم قوم ہود کو کہا اے میری قوم! اللہ کو پوجو اس کے سوا تمہارا کوئی معبود نہیں، تم تو بڑے مفتری (بالکل جھوٹے الزام عائد کرنے والے) ہو'),
(1524, 11, 51, 'اے قوم! میں اس پر تم سے کچھ اجرت نہیں مانگتا، میری مزدوری تو اسی کے ذمہ ہے جس نے مجھے پیدا کیا تو کیا تمہیں عقل نہیں'),
(1525, 11, 52, 'اور اے میری قوم اپنے رب سے معافی چاہو پھر اس کی طرف رجوع لاؤ تم پر زور کا پانی بھیجے گا، اور تم میں جتنی قوت ہے اس سے زیادہ دے گا اور جرم کرتے ہوئے روگردانی نہ کرو'),
(1526, 11, 53, 'بولے اے ہود تم کوئی دلیل لے کر ہمارے پاس نہ آئے اور ہم خالی تمہارے کہنے سے اپنے خداؤں کو چھوڑنے کے نہیں نہ تمہاری بات پر یقین لائیں،'),
(1527, 11, 54, 'ہم تو یہی کہتے ہیں کہ ہمارے کسی خدا کی تمہیں بری جھپٹ (پکڑ) پہنچی کہا میں اللہ کو گواہ کرتا ہوں اور تم سب گواہ ہوجاؤ کہ میں بیزار ہوں ان سب سے جنہیں تم اللہ کے سوا اس کا شریک ٹھہراتے ہو،'),
(1528, 11, 55, 'تم سب مل کر میرا برا چاہو پھر مجھے مہلت نہ دو'),
(1529, 11, 56, 'میں نے اللہ پر بھروسہ کیا جو میرا رب ہے اور تمہارا رب، کوئی چلنے والا نہیں جس کی چوٹی اس کے قبضہٴ قدرت میں نہ ہو بیشک میرا رب سیدھے راستہ پر ملتا ہے،'),
(1530, 11, 57, 'پھر اگر تم منہ پھیرو تو میں تمہیں پہنچا چکا جو تمہاری طرف لے کر بھیجا گیا اور میرا رب تمہاری جگہ اوروں کو لے آئے گا اور تم اس کا کچھ نہ بگاڑ سکو گے بیشک میرا رب ہر شے پر نگہبان ہے'),
(1531, 11, 58, 'اور جب ہمارا حکم آیا ہم نے ہود اور اس کے ساتھ کے مسلمانوں کو اپنی رحمت فرما کر بچالیا اور انہیں سخت عذاب سے نجات دی،'),
(1532, 11, 59, 'اور یہ عاد ہیں کہ اپنے رب کی آیتوں سے منکر ہوئے اور اس کے رسولوں کی نافرمانی کی اور ہر بڑے سرکش ہٹ دھرم کے کہنے پر چلے،'),
(1533, 11, 60, 'اور ان کے پیچھے لگی اس دنیا میں لعنت اور قیامت کے دن، سن لو! بیشک عاد اپنے رب سے منکر ہوئے، ارے دور ہوں عاد ہود کی قوم،'),
(1534, 11, 61, 'اور ثمود کی طرف ان کے ہم قوم صا لح کو کہا اے میری قوم اللہ کو پوجو اس کے سوا تمہارا کوئی معبود نہیں اس نے تمہیں زمین میں پیدا کیا اور اس میں تمہیں بسایا تو اس سے معافی چاہو پھر اس کی طرف رجوع لاؤ، بیشک میرا رب قریب ہے دعا سننے والا،'),
(1535, 11, 62, 'بولے اے صا لح! اس سے پہلے تو تم ہم میں ہونہار معلوم ہوتے تھے کیا تم ہمیں اس سے منع کرتے ہو کہ اپنے باپ دادا کے معبودوں کو پوجیں اور بیشک جس بات کی طرف ہمیں بلاتے ہو ہم اس سے ایک بڑے دھوکا ڈالنے والے شک میں ہیں،'),
(1536, 11, 63, 'بولا اے میری قوم! بھلا بتاؤ تو اگر میں اپنے رب کی طرف سے روشن دلیل پر ہوں اور اس نے مجھے اپنے پاس سے رحمت بخشی تو مجھے اس سے کون بچائے گا اگر میں اس کی نافرمانی کروں تو تم مجھے سوا نقصان کے کچھ نہ بڑھاؤ گے'),
(1537, 11, 64, 'اور اے میری قوم! یہ اللہ کا ناقہ ہے تمہارے لیے نشانی تو اسے چھوڑ دو کہ اللہ کی زمین میں کھائے اور اسے بری طرح ہاتھ نہ لگانا کہ تم کو نزدیک عذاب پہنچے گا'),
(1538, 11, 65, 'تو انہوں نے اس کی کونچیں کاٹیں تو صا لح نے کہا اپنے گھرو ں میں تین دن اور برت لو (فائدہ اٹھالو) یہ وعدہ ہے کہ جھوٹا نہ ہوگا'),
(1539, 11, 66, 'پھر جب ہمارا حکم آیا ہم نے صا لح اور اس کے ساتھ کے مسلمانوں کو اپنی رحمت فرماکر بچالیا اور اس دن کی رسوائی سے، بیشک تمہارا رب قومی عزت والا ہے،'),
(1540, 11, 67, 'اور ظالموں کو چنگھاڑ نے آ لیا تو صبح اپنے گھروں میں گھٹنوں کے بل پڑے رہ گئے،'),
(1541, 11, 68, 'گویا کبھی یہاں بسے ہی نہ تھے، سن لو! بیشک ثمود اپنے رب سے منکر ہوئے ارے لعنت ہو ثمود پر،'),
(1542, 11, 69, 'اور بیشک ہمارے فرشتے ابراہیم کے پاس مژدہ لے کر آئے بولے سلام کہا کہا سلام پھر کچھ دیر نہ کی کہ ایک بچھڑا بھنا لے آئے'),
(1543, 11, 70, 'پھر جب دیکھا کہ ان کے ہاتھ کھانے کی طرف نہیں پہنچتے ان کو اوپری سمجھا اور جی ہی جی میں ان سے ڈرنے لگا، بولے ڈریے نہیں ہم قوم لوط کی طرف بھیجے گئے ہیں،'),
(1544, 11, 71, 'اور اس کی بی بی کھڑی تھی وہ ہنسنے لگی تو ہم نے اسے اسحاق کی خوشخبری دی اور اسحاق کے پیچھے یعقوب کی'),
(1545, 11, 72, 'بولی ہائے خرابی کیا میرے بچہ ہوگا اور میں بوڑھی ہوں اور یہ ہیں میرے شوہر بوڑھے بیشک یہ تو اچنبھے کی بات ہے،'),
(1546, 11, 73, 'فرشتے بولے کیا اللہ کے کام کا اچنبھا کرتی ہو اللہ کی رحمت اور اس کی برکتیں تم پر اس گھر والو! بیشک وہی ہے سب خوبیوں والا عزت والا،'),
(1547, 11, 74, 'پھر جب ابراہیم کا خوف زائل ہوا اور اسے خوشخبری ملی ہم سے قوم لوط کے بارے میں جھگڑنے لگا،'),
(1548, 11, 75, 'بیشک ابراہیم تحمل والا بہت آہیں کرنے والا رجوع کرنے والا ہے'),
(1549, 11, 76, 'اے ابراہیم اس خیال میں نہ پڑ بیشک تیرے رب کا حکم آچکا اور بیشک ان پر عذاب آنے والا ہے کہ پھیرا نہ جائے گا،'),
(1550, 11, 77, 'اور جب لوط کے یہاں ہمارے فرشتے آئے اسے ان کا غم ہوا او ر ان کے سبب دل تنگ ہوا اور بولا یہ بڑی سختی کا دن ہے'),
(1551, 11, 78, 'اور اس کے پاس کی قوم دوڑتی آئی، ور انہیں آگے ہی سے برے کاموں کی عادت پڑی تھی کہا اے قوم! یہ میری قوم کی بیٹیاں ہیں یہ تمہارے لیے ستھری ہیں تو اللہ سے ڈرو اور مجھے میرے مہمانوں میں رسوا نہ کرو، کیا تم میں ایک آدمی بھی نیک چلن نہیں،'),
(1552, 11, 79, 'بولے تمہیں معلوم ہے کہ تمہاری قوم کی بیٹیوں میں ہمارا کوئی حق نہیں اور تم ضرور جانتے ہو جو ہماری خواہش ہے،'),
(1553, 11, 80, 'بولے اے کاش! مجھے تمہارے مقابل زور ہوتا یا کسی مضبوط پائے کی پناہ لیتا'),
(1554, 11, 81, 'فرشتے بولے اے لوط ہم تمہارے رب کے بھیجے ہوئے ہیں وہ تم تک نہیں پہنچ سکتے تو اپنے گھر والوں کو راتوں رات لے جاؤ اور تم میں کوئی پیٹھ پھیر کر نہ دیکھے سوائے تمہاری عورت کے اسے بھی وہی پہنچنا ہے جو انہیں پہنچے گا بیشک ان کا وعدہ صبح کے وقت کا ہے کیا صبح قریب نہیں،'),
(1555, 11, 82, 'پھر جب ہمارا حکم آیا ہم نے اس بستی کے اوپر کو اس کا نیچا کردیا اور اس پر کنکر کے پتھر لگا تار برسائے،'),
(1556, 11, 83, 'جو نشان کیے ہوئے تیرے رب کے پاس ہیں اور وہ پتھر کچھ ظالموں سے دور نہیں'),
(1557, 11, 84, 'اور مدین کی طرف ان کے ہم قوم شعیب کو کہا اے میری قوم اللہ کو پوجو اس کے سوا کوئی معبود نہیں اور ناپ اور تول میں کمی نہ کرو بیشک میں تمہیں آسودہ حال دیکھتا ہوں اور مجھے تم پر گھیر لینے والے دن کا عذاب کا ڈر ہے'),
(1558, 11, 85, 'اور اے میری قوم ناپ اور تول انصاف کے ساتھ پوری کرو اور لوگوں کو ان کی چیزیں گھٹا کر نہ دو اور زمین میں فساد مچاتے نہ پھرو،'),
(1559, 11, 86, 'اللہ کا دیا جو بچ رہے وہ تمہارے لیے بہتر ہے اگر تمہیں یقین ہو اور میں کچھ تم پر نگہبان نہیں'),
(1560, 11, 87, 'بولے اے شعیب! کیا تمہاری نماز تمہیں یہ حکم دیتی ہے کہ ہم اپنے باپ دادا کے خداؤں کو چھوڑ دیں یا اپنے مال میں جو چا ہیں نہ کریں ہاں جی تمہیں بڑے عقلمند نیک چلن ہو،'),
(1561, 11, 88, 'کہا اے میری قوم بھلا بتاؤ تو اگر میں اپنے رب کی طرف سے ایک روشن دلیل پر ہوں او راس نے مجھے اپنے پاس سے اچھی روزی دی اور میں نہیں چاہتا ہوں کہ جس بات سے تمہیں منع کرتا ہوں آپ اس کے خلاف کرنے لگوں میں تو جہاں تک بنے سنوارنا ہی چاہتا ہوں، اور میری توفیق اللہ ہی کی طرف سے ہے، میں نے اسی پر بھروسہ کیا اور اسی کی طرف رجوع ہوتا ہوں،'),
(1562, 11, 89, 'اور اے میری قوم تمہیں میری ضد یہ نہ کموادے کہ تم پر پڑے جو پڑا تھا نوح کی قوم یا ہود کی قوم یا صا لح کی قوم پر، اور لوط کی قوم تو کچھ تم سے دور نہیں'),
(1563, 11, 90, 'اور اپنے رب سے معافی چاہو پھر اس کی طرف رجوع لاؤ، بیشک میرا رب مہربان محبت والا ہے،'),
(1564, 11, 91, 'بولے اے شعیب! ہماری سمجھ میں نہیں آتیں تمہاری بہت سی باتیں اور بیشک ہم تمہیں اپنے میں کمزور دیکھتے ہیں اور اگر تمہارا کنبہ نہ ہوتا تو ہم نے تمہیں پتھراؤ کردیا ہوتا اور کچھ ہماری نگاہ میں تمہیں عزت نہیں،'),
(1565, 11, 92, 'کہا اے میری قوم کیا تم پر میرے کنبہ کا دباؤ اللہ سے زیادہ ہے اور اسے تم نے اپنی پیٹھ کے پیچھے ڈال رکھا بیشک جو کچھ تم کرتے ہو سب میرے رب کے بس میں ہے،'),
(1566, 11, 93, 'اور اے قوم تم اپنی جگہ اپنا کام کیے جا ؤ میں اپنا کام کرتا ہوں، اب جاننا چاہتے ہو کس پر آتا ہے وہ عذاب کہ اسے رسوا کرے گا اور کون جھوٹا ہے، اور انتظار کرو میں بھی تمہارے ساتھ انتظار میں ہوں،'),
(1567, 11, 94, 'اور جب ہمارا حکم آیا ہم نے شعیب اور اس کے ساتھ کے مسلمانوں کو اپنی رحمت فرماکر بچالیا اور ظالموں کو چنگھاڑ نے آ لیا تو صبح اپنے گھروں میں گھٹنوں کے بل پڑے رہ گئے،'),
(1568, 11, 95, 'گویا کبھی وہاں بسے ہی نہ تھے، ارے دُور ہوں مدین جیسے دور ہوئے ثمود'),
(1569, 11, 96, 'اور بیشک ہم نے موسیٰ کو اپنی آیتوں اور صریح غلبے کے ساتھ،'),
(1570, 11, 97, 'فرعون اور ا س کے درباریوں کی طرف بھیجا تو وہ فرعون کے کہنے پر چلے اور فرعون کا کام راستی کا نہ تھا'),
(1571, 11, 98, 'اپنی قوم کے آگے ہوگا قیامت کے دن تو انہیں دوزخ میں لا اتارے گا اور و ه کیا ہی برا گھاٹ اترنے کا،'),
(1572, 11, 99, 'اور ان کے پیچھے پڑی اس جہان میں لعنت اور قیامت کے دن کیا ہی برا انعام جو انہیں ملا،'),
(1573, 11, 100, 'یہ بستیوں کی خبریں ہیں کہ ہم تمہیں سناتے ہیں ان میں کوئی کھڑی ہے اور کوئی کٹ گئی'),
(1574, 11, 101, 'اور ہم نے ان پر ظلم نہ کیا خود انہوں نے اپنا برا کیا تو ان کے معبود جنہیں اللہ کے سوا پوجتے تھے ان کے کچھ کام نہ آئے جب تمہارے رب کا حکم آیا اور ان سے انہیں ہلاک کے سوا کچھ نہ بڑھا،'),
(1575, 11, 102, 'اور ایسی ہی پکڑ ہے تیرے رب کی جب بستیوں کو پکڑتا ہے ان کے ظلم پر، بیشک اس کی پکڑ دردناک کرّ ی ہے'),
(1576, 11, 103, 'بیشک اس میں نشانی ہے اس کے لیے جو آخرت کے عذاب سے ڈرے وہ دن ہے جس میں سب لوگ اکٹھے ہوں گے اور وہ دن حاضری کا ہے'),
(1577, 11, 104, 'اور ہم اسے پیچھے نہیں ہٹاتے مگر ایک گنی ہوئی مدت کے لیئے'),
(1578, 11, 105, 'جب وہ دن آئے گا کوئی بے حکم خدا بات نہ کرے گا تو ان میں کوئی بدبخت ہے اور کوئی خوش نصیب'),
(1579, 11, 106, 'تو وہ جو بدبخت ہیں وہ تو دوزخ میں ہیں وہ اس گدھے کی طرح رینکیں گے'),
(1580, 11, 107, 'وہ اس میں رہیں گے جب تک آسمان و زمین رہیں مگر جتنا تمہارے رب نے چاہا بیشک تمہارا رب جب جو چاہے کرے،'),
(1581, 11, 108, 'اور وہ جو خوش نصیب ہوئے وہ جنت میں ہیں ہمیشہ اس میں رہیں گے جب تک آسمان و زمین مگر جتنا تمہارے رب نے چاہا یہ بخشش ہے کبھی ختم نہ ہوگی،'),
(1582, 11, 109, 'تو اے سننے والے! دھوکا میں نہ پڑ اس سے جیسے یہ کافر پوجتے ہیں یہ ویسا ہی پوجتے ہیں جیسا پہلے ان کے باپ دادا پوجتے تھے اور بیشک ہم ان کا حصہ انہیں پورا پھیردیں گے جس میں کمی نہ ہوگی،'),
(1583, 11, 110, 'اور بیشک ہم نے موسیٰ کو کتاب دی تو اس میں پھوٹ پڑگئی اگر تمہارے رب کی ایک بات پہلے نہ ہوچکی ہوتی تو جبھی ان کا فیصلہ کردیا جاتا اور بیشک وہ اس کی طرف سے دھوکا ڈالنے والے شک میں ہیں'),
(1584, 11, 111, 'اور بیشک جتنے ہیں ایک ایک کو تمہارا رب اس کا عمل پورا بھردے گا اسے ان کے کا موں کی خبر ہے،'),
(1585, 11, 112, 'تو قائم رہو جیسا تمہیں حکم ہے اور جو تمہارے ساتھ رجوع لایا ہے اور اے لوگو! سرکشی نہ کرو بیشک وہ تمہارے کام دیکھ رہا ہے،'),
(1586, 11, 113, 'اور ظالموں کی طرف نہ جھکو کہ تمہیں آ گ چھوئے گی اور اللہ کے سوا تمہارا کوئی حما یتی نہیں پھر مدد نہ پاؤ گے،'),
(1587, 11, 114, 'اور نماز قائم رکھو دن کے دونوں کناروں اور کچھ رات کے حصوں میں بیشک نیکیاں برائیوں کو مٹادیتی ہیں، یہ نصیحت ہے نصیحت ماننے والوں کو،'),
(1588, 11, 115, 'اور صبر کرو کہ اللہ نیکوں کا نیگ (اجر) ضائع نہیں کرتا،'),
(1589, 11, 116, 'تو کیوں نہ ہوئے تم میں سے اگلی سنگتوں (قوموں) میں ایسے جن میں بھلائی کا کچھ حصہ لگا رہا ہوتا کہ زمین میں فساد سے روکتے ہاں ان میں تھوڑے تھے وہی جن کو ہم نے نجات دی اور ظالم اسی عیش کے پیچھے پڑے رہے جو انہیں دیا گیا اور وہ گنہگار تھے،'),
(1590, 11, 117, 'اور تمہارا رب ایسا نہیں کہ بستیوں کو بے وجہ ہلاک کردے اور ان کے لوگ اچھے ہوں،'),
(1591, 11, 118, 'اور اگرتمہارا رب چاہتا تو سب آدمیوں کو ایک ہی امت کردیتا اور وہ ہمیشہ اختلاف میں رہیں گے'),
(1592, 11, 119, 'مگر جن پر تمہارے رب نے رحم کیا اور لوگ اسی لیے بنائے ہیں اور تمہارے رب کی بات پوری ہوچکی کہ بیشک ضرور جہنم بھر دوں گا جنوں اور آدمیوں کو ملا کر'),
(1593, 11, 120, 'اور سب کچھ ہم تمہیں رسولوں کی خبریں سناتے ہیں جس سے تمہا را دل ٹھیرائیں اور اس سورت میں تمہارے پاس حق آیا اور مسلمانوں کو پند و نصیحت'),
(1594, 11, 121, 'اور کافروں سے فرماؤ تم اپنی جگہ کام کیے جاؤ ہم اپنا کام کرتے ہیں'),
(1595, 11, 122, 'اور راہ دیکھو ہم بھی راہ دیکھتے ہیں'),
(1596, 11, 123, 'اور اللہ ہی کے لیے ہیں آسمانوں اور زمین کے غیب اور اسی کی طرف سب کاموں کی رجوع ہے تو اس کی بندگی کرو اور اس پر بھروسہ رکھو، اور تمہارا رب تمہارے کا موں سے غافل نہیں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'یہ روشن کتاب کی آیتیں ہیں'),
(1598, 12, 2, 'بیشک ہم نے اسے عربی قرآن اتارا کہ تم سمجھو،'),
(1599, 12, 3, 'ہم تمہیں سب اچھا بیان سناتے ہیں اس لیے کہ ہم نے تمہاری طرف اس قرآن کی وحی بھیجی اگرچہ بیشک اس سے پہلے تمہیں خبر نہ تھی،'),
(1600, 12, 4, 'یاد کرو جب یوسف نے اپنے با پ سے کہا اے میرے باپ میں نے گیارہ تارے اور سورج اور چاند دیکھے انہیں اپنے لیے سجدہ کرتے دیکھا'),
(1601, 12, 5, 'کہا اے میرے بچے اپنا خواب اپنے بھائیوں سے نہ کہنا وہ تیرے ساتھ کوئی چا ل چلیں گے بیشک شیطان آدمی کا کھلا دشمن ہے'),
(1602, 12, 6, 'اور اسی طرح تجھے تیرا رب چن لے گا اور تجھے باتوں کا انجام نکا لنا سکھائے گا اور تجھ پر اپنی نعمت پوری کرے گا اور یعقوب کے گھر والوں پر جس طرح تیرے پہلے دنوں باپ دادا ابراہیم ؑ اور اسحق ؑ پر پوری کی بیشک تیرا رب علم و حکمت والا ہے،'),
(1603, 12, 7, 'بیشک یوسف اور اس کے بھائیوں میں پوچھنے والوں کے لیے نشانیاں ہیں'),
(1604, 12, 8, 'جب بولے کہ ضرور یوسف اور اس کا بھائی ہمارے باپ کو ہم سے زیادہ پیارے ہیں اور ہم ایک جماعت ہیں بیشک ہمارے باپ صراحةً ان کی محبت میں ڈوبے ہوئے ہیں'),
(1605, 12, 9, 'یوسف کو مار ڈالو یا کہیں زمین میں پھینک آؤ کہ تمہارے باپ کا منہ صرف تمہاری ہی طرف رہے اور اس کے بعد پھر نیک ہوجانا'),
(1606, 12, 10, 'ان میں ایک کہنے والا بولا یوسف کو مارو نہیں اور اسے اندھے کنویں میں ڈال دو کہ کوئی چلتا اسے آکر لے جائے اگر تمہیں کرنا ہے'),
(1607, 12, 11, 'بولے اے ہمارے باپ! آپ کو کیا ہوا کہ یوسف کے معامل ہ میں ہمارا اعتبار نہیں کرتے اور ہم تو اس کے خیر خواہ ہیں،'),
(1608, 12, 12, 'کل اسے ہمارے ساتھ بھیج دیجئے کہ میوے کھائے اور کھیلے اور بیشک ہم اس کے نگہبان ہیں'),
(1609, 12, 13, 'بولا بیشک مجھے رنج دے گا کہ اسے لے جاؤ اور ڈرتا ہوں کہ اسے بھیڑیا کھالے اور تم اس سے بے خبر رہو'),
(1610, 12, 14, 'بولے اگر اسے بھیڑیا کھا جائے اور ہم ایک جماعت ہیں جب تو ہم کسی مصرف کے نہیں'),
(1611, 12, 15, 'پھر جب اسے لے گئے اور سب کی رائے یہی ٹھہری کہ اسے اندھے کنویں میں ڈال دیں اور ہم نے اسے وحی بھیجی کہ ضرور تو انہیں ان کا یہ کام جتادے گا ایسے وقت کہ وہ نہ جانتے ہوں گے'),
(1612, 12, 16, 'اور رات ہوئے اپنے باپ کے پاس روتے ہوئے آئے'),
(1613, 12, 17, 'بولے اے ہمارے باپ ہم دوڑ کرتے نکل گئے اور یوسف کو اپنے اسباب کے پاس چھوڑا تو اسے بھیڑیا کھا گیا اور آپ کسی طرح ہمارا یقین نہ کریں گے اگرچہ ہم سچے ہوں'),
(1614, 12, 18, 'اور اس کے کر ُتے پر ایک جھوٹا خون لگا لائے کہا بلکہ تمہارے دلوں نے ایک بات تمہارے واسطے بنالی ہے تو صبر اچھا، اور اللہ ہی مدد چاہتا ہوں ان باتوں پر جو تم بتارہے ہو'),
(1615, 12, 19, 'اور ایک قافلہ آیا انہوں نے اپنا پانی لانے والا بھیجا تو اس نے اپنا ڈول ڈال بولا آہا کیسی خوشی کی بات ہے یہ تو ایک لڑکا ہے اور اسے ایک پونجی بناکر چھپالیا اور اللہ جانتا ہے جو وہ کرتے ہیں،'),
(1616, 12, 20, 'اور بھائیوں نے اسے کھوٹے داموں گنتی کے روپوں پر بیچ ڈالا اور انہیں اس میں کچھ رغبت نہ تھی'),
(1617, 12, 21, 'اور مصر کے جس شخص نے اسے خریدا وہ اپنی عورت سے بولا انہیں عزت سے رکھو شاید ان سے ہمیں نفع پہنچے یا ان کو ہم بیٹا بنالیں اور اسی طرح ہم نے یوسف کو اس زمین میں جماؤ (رہنے کا ٹھکانا) دیا اور اس لیے کہ اسے باتوں کا انجام سکھائیں ۰ف۵۳) اور اللہ اپنے کام پر غالب ہے مگر اکثر آدمی نہیں جانتے،'),
(1618, 12, 22, 'اور جب اپنی پوری قوت کو پہنچا ہم نے اسے حکم اور علم عطا فرمایا اور ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(1619, 12, 23, 'اور وہ جس عورت کے گھر میں تھا اس نے اسے لبھایا کہ اپنا آپا نہ روکے اور دروازے سب بند کردیے اور بولی آؤ تمہیں سے کہتی ہوں کہا اللہ کی پناہ وہ عزیز تو میرا رب یعنی پرورش کرنے والا ہے اس نے مجھے اچھی طرح رکھا بیشک ظالموں کا بھلا نہیں ہوتا،'),
(1620, 12, 24, 'اور بیشک عورت نے اس کا ارادہ کیا اور وہ بھی عورت کا ارادہ کرتا اگر اپنے رب کی دلیل نہ دیکھ لیتا ہم نے یوں ہی کیا کہ اس سے برائی اور بے حیائی کو پھیر دیں بیشک وہ ہمارے چنے ہوئے بندوں میں سے ہے'),
(1621, 12, 25, 'اور دونوں دروازے کی طرف دوڑے اور عورت نے اس کا کر ُتا پیچھے سے چیر لیا اور دونوں کو عورت کا میاں دروازے کے پاس ملا بولی کیا سزا ہے اس کی جس نے تیری گھر والی سے بدی چاہی مگر یہ کہ قید کیا جائے یا دکھ کی مار'),
(1622, 12, 26, 'کہا اس نے مجھ کو لبھایا کہ میں اپنی حفاظت نہ کروں اور عورت کے گھر والوں میں سے ایک گواہ نے گواہی دی اگر ان کا کر ُتا آگے سے چرا ہے تو عورت سچی ہے اور انہوں نے غلط کہا'),
(1623, 12, 27, 'اور اگر ان کا کر ُتا پیچھے سے چاک ہوا تو عورت جھوٹی ہے اور یہ سچے'),
(1624, 12, 28, 'پھر جب عزیز نے اس کا کر ُتا پیچھے سے چرا دیکھا بولا بیشک یہ تم عورتوں کا چرتر (فریب) ہے بیشک تمہارا چرتر (فریب) بڑا ہے'),
(1625, 12, 29, 'اے یوسف! تم اس کا خیال نہ کرو اور اے عورت! تو اپنے گناہ کی معافی مانگ بیشک تو خطاواروں میں ہے'),
(1626, 12, 30, 'اور شہر میں کچھ عورتیں بولیں کہ عزیز کی بی بی اپنے نوجوان کا دل لبھاتی ہ ے بیشک ان کی محبت اس کے دل میں پَیر گئی (سماگئی) ہے ہم تو اسے صر یح خود رفتہ پاتے ہیں'),
(1627, 12, 31, 'تو جب زلیخا نے ان کا چرچا سنا تو ان عورتوں کو بلا بھیجا اور ان کے لیے مسندیں تیار کیں اور ان میں ہر ایک کو ایک چھری دی اور یوسف سے کہا ان پر نکل آؤ جب عورتوں نے یوسف کو دیکھا اس کی بڑائی بولنے لگیں اور اپنے ہاتھ کاٹ لیے اور بولیں اللہ کو پاکی ہے یہ تو جنس بشر سے نہیں یہ تو نہیں مگر کوئی معزز فرشتہ،'),
(1628, 12, 32, 'زلیخا نے کہا تو يہ ہیں وہ جن پر مجھے طعنہ دیتی تھیں اور بیشک میں نے ان کا جِی لبھانا چاہا تو انہوں نے اپنے آپ کو بچا یا اور بیشک اگر وہ یہ کام نہ کریں گے جو میں ان سے کہتی ہوں تو ضرور قید میں پڑیں گے اور وہ ضرور ذلت اٹھائیں گے'),
(1629, 12, 33, 'یوسف نے عرض کی اے میرے رب! مجھے قید خانہ زیادہ پسند ہے اس کام سے جس کی طرف یہ مجھے بلاتی ہیں اور اگر تو مجھ سے ان کا مکر نہ پھیرے گا تو میں ان کی طرف مائل ہوں گا اور نادان بنوں گا،'),
(1630, 12, 34, 'تو اس کے رب نے اس کی سن لی اور اس سے عورتوں کا مکر پھیردیا، بیشک وہی سنتا جانتا ہے'),
(1631, 12, 35, 'پھر سب کچھ نشانیاں دیکھ دکھا کر پچھلی مت انہیں یہی آئی کہ ضرور ایک مدت تک اسے قیدخانہ میں ڈالیں'),
(1632, 12, 36, 'اور اس کے ساتھ قیدخانہ میں دو جوان داخل ہوئے ان میں ایک بولا میں نے خواب میں دیکھا کہ شراب نچوڑتا ہوں اور دوسرا بولا میں نے خواب دیکھا کہ میرے سر پر کچھ روٹیاں ہیں جن میں سے پرند کھاتے ہیں، ہمیں اس کی تعبیر بتایے، بیشک ہم آپ کو نیکو کار دیکھتے ہیں'),
(1633, 12, 37, 'یوسف نے کہا جو کھانا تمہیں ملا کرتا ہے وہ تمہارے پاس نہ آنے پائے گا کہ میں اس کی تعبیر اس کے آنے سے پہلے تمہیں بتادوں گا یہ ان علموں میں سے ہے جو مجھے میرے رب نے سکھایا ہے، بیشک میں نے ان لوگوں کا دین نہ مانا جو اللہ پر ایمان نہیں لاتے اور وہ آخرت سے منکر ہیں،'),
(1634, 12, 38, 'اور میں نے اپنے باپ دادا ابراہیم ؑ اور اسحق ؑ اور یعقوب کا دین اختیار کیا ہمیں نہیں پہنچتا کہ کسی چیز کو اللہ کا شریک ٹھہرائیں، یہ اللہ کا ایک فضل ہے ہم پر اور لوگوں پر مگر اکثر لوگ شکر نہیں کرتے'),
(1635, 12, 39, 'اے میرے قیدخانہ کے دونوں ساتھیو! کیا جدا جدا رب اچھے یا ایک اللہ جو سب پر غالب،'),
(1636, 12, 40, 'تم اس کے سوا نہیں پوجتے مگر نرے نام (فرضی نام) جو تم نے اور تمہارے باپ دادا نے تراش لیے ہیں اللہ نے ان کی کوئی سند نہ اتاری، حکم نہیں مگر اللہ کا اس نے فرمایا کہ اس کے سوا کسی کو نہ پوجو یہ سیدھا دین ہے لیکن اکثر لوگ نہیں جانتے'),
(1637, 12, 41, 'اے قید خانہ کے دونوں ساتھیو! تم میں ایک تو اپنے رب (بادشاہ) کو شراب پلائے گا رہا دوسرا وہ سو ُلی دیا جائے گا تو پرندے اس کا سر کھائیں گے حکم ہوچکا اس بات کا جس کا تم سوال کرتے تھے'),
(1638, 12, 42, 'اور یوسف نے ان دونوں میں سے جسے بچتا سمجھا اس سے کہا اپنے رب (بادشاہ) کے پاس میرا ذکر کرنا تو شیطان نے اسے بھلا دیا کہ اپنے رب (بادشاہ) کے سامنے یوسف کا ذکر کرے تو یوسف کئی برس اور جیل خانہ میں رہا'),
(1639, 12, 43, 'اور بادشاہ نے کہا میں نے خواب میں دیکھیں سات گائیں فربہ کہ انہیں سات دُبلی گائیں کھارہی ہیں اور سات بالیں ہری اور دوسری سات سوکھی اے درباریو! میرے خواب کا جواب دو اگر تمہیں خواب کی تعبیر آتی ہو،'),
(1640, 12, 44, 'بولے پریشان خوابیں ہیں اور ہم خواب کی تعبیر نہیں جانتے،'),
(1641, 12, 45, 'اور بولا وہ جو ان دونوں میں سے بچا تھا اور ایک مدت بعد اسے یاد آیا میں تمہیں اس کی تعبیر بتاؤں گا مجھے بھیجو'),
(1642, 12, 46, 'اے یوسف! اے صدیق! ہمیں تعبیر دیجئے سات فربہ گایوں کی جنہیں سات دُبلی کھاتی ہیں اور سات ہری بالیں اور دوسری سات سوکھی شاید میں لوگوں کی طرف لوٹ کر جاؤں شاید وہ آگاہ ہوں'),
(1643, 12, 47, 'کہا تم کھیتی کرو گے سات برس لگارتار تو جو کاٹو اسے اس کی بال میں رہنے دو مگر تھوڑا جتنا کھالو'),
(1644, 12, 48, 'پھر اس کے بعد سات برس کرّے (سخت تنگی والے) آئیں گے کہ کھا جائیں گے جو تم نے ان کے لیے پہلے جمع کر رکھا تھا مگر تھوڑا جو بچالو'),
(1645, 12, 49, 'پھر ان کے بعد ایک برس آئے گا جس میں لوگوں کو مینھ دیا جائے گا اور اس میں رس نچوڑیں گے'),
(1646, 12, 50, 'اور بادشاہ بولا کہ انہیں میرے پاس لے آؤ تو جب اس کے پاس ایلچی آیا کہا اپنے رب (بادشاہ) کے پاس پلٹ جا پھر اس سے پوچھ کیا حال ہے اور عورتوں کا جنہوں نے اپنے ہاتھ کاٹے تھے، بیشک میرا رب ان کا فریب جانتا ہے'),
(1647, 12, 51, 'بادشاہ نے کہا اے عورتو! تمہارا کیا کام تھا جب تم نے یوسف کا دل لبھانا چاہا، بولیں اللہ کو پاکی ہے ہم نے ان میں کوئی بدی نہیں پائی عزیز کی عورت بولی اب اصلی بات کھل گئی، میں نے ان کا جی لبھانا چاہا تھا اور وہ بیشک سچے ہیں'),
(1648, 12, 52, 'یوسف نے کہا یہ میں نے اس لیے کیا کہ عزیز کو معلوم ہوجائے کہ میں نے پیٹھ پیچھے اس کی خیانت نہ کی اور اللہ دغا بازوں کا مکر نہیں چلنے دیتا،'),
(1649, 12, 53, 'اور میں اپنے نفس کو بے قصور نہیں بتاتا بیشک نفس تو برائی کا بڑا حکم دینے والا ہے مگر جس پر میرا رب رحم کرے بیشک میرا رب بخشنے والا مہربان ہے'),
(1650, 12, 54, 'اور بادشاہ بولا انہيں میرے پاس لے آؤ کہ میں انہیں اپنے لیے چن لوں پھر جب اس سے بات کی کہا بیشک آج آپ ہمارے یہاں معزز معتمد ہیں'),
(1651, 12, 55, 'یوسف نے کہا مجھے زمین کے خزانوں پر کردے بیشک میں حفاظت والا علم والا ہوں'),
(1652, 12, 56, 'اور یوں ہی ہم نے یوسف کو اس ملک پر قدرت بخشی اس میں جہاں چاہے رہے ہم اپنی رحمت جسے چاہیں پہنچائیں اور ہم نیکوں کا نیگ (اَجر) ضائع نہیں کرتے،'),
(1653, 12, 57, 'اور بیشک آخرت کا ثواب ان کے لیے بہتر جو ایمان لائے اور پرہیزگار رہے'),
(1654, 12, 58, 'اور یوسف کے بھائی آئے تو اس کے پاس حاضر ہوئے تو یوسف نے انہیں پہچان لیا اور وہ اس سے انجان رہے'),
(1655, 12, 59, 'اور جب ان کا سامان مہیا کردیا کہ اپنا سوتیلا بھائی میرے پاس لے آؤ کیا نہیں دیکھتے کہ میں پورا ناپتا ہوں اور میں سب سے بہتر مہمان نواز ہوں،'),
(1656, 12, 60, 'پھر اگر اسے لیکر میرے پاس نہ آؤ تو تمہارے لیے میرے یہاں ماپ نہیں اور میرے پاس نہ پھٹکنا،'),
(1657, 12, 61, 'بولے ہم اس کی خواہش کریں گے اس کے باپ سے اور ہمیں یہ ضرور کرنا،'),
(1658, 12, 62, 'اور یوسف نے اپنے غلاموں سے کہا ان کی پونجی ان کی خورجیوں میں رکھ دو شاید وہ اسے پہچانیں جب اپنے گھر کی طرف لوٹ کر جائیں شاید وہ واپس آئیں،'),
(1659, 12, 63, 'پھر جب وہ اپنے باپ کی طرف لوٹ کر گئے بولے اے ہمارے باپ ہم سے غلہ روک دیا گیا ہے تو ہمارے بھائی کو ہمارے پاس بھیج دیجئے کہ غلہ لائیں اور ہم ضرور اس کی حفاظت کریں گے،'),
(1660, 12, 64, 'کہا کیا اس کے بارے میں تم پر ویسا ہی اعتبار کرلوں جیسا پہلے اس کے بھائی کے بارے میں کیا تھا تو اللہ سب سے بہتر نگہبان اور وہ ہر مہربان سے بڑھ کر مہربان،'),
(1661, 12, 65, 'اور جب انہوں نے اپنا اسباب کھولا اپنی پونجی پائی کہ ان کو پھیر دی گئی ہے، بولے اے ہمارے باپ اب اور کیا چاہیں، یہ ہے ہماری پونجی ہمیں واپس کردی گئی اور ہم اپنے گھر کے لیے غلہ لائیں اور اپنے بھائی کی حفاظت کریں اور ایک اونٹ کا بوجھ اور زیادہ پائیں، یہ دنیا بادشاہ کے سامنے کچھ نہیں'),
(1662, 12, 66, 'کہا میں ہرگز اسے تمہارے ساتھ نہ بھیجوں گا جب تک تم مجھے کا اللہ کا یہ عہد نہ دے دو کہ ضرور اسے لے کر آ ؤ گے مگر یہ کہ تم گھِر جاؤ پھر انہوں نے یعقوب کو عہد دے دیا کہا اللہ کا ذمہ ہے ان باتوں پر جو کہہ رہے ہیں،'),
(1663, 12, 67, 'اور کہا اے میرے بیٹوں! ایک دروازے سے نہ داخل ہونا اور جدا جدا دروا زوں سے جانا میں تمہیں اللہ سے بچا نہیں سکتا حکم تو سب اللہ ہی کا ہے، میں نے اسی پر بھروسہ کیا اور بھروسہ کرنے والوں کو اسی پر بھروسہ چاہیے،'),
(1664, 12, 68, 'اور جب وہ داخل ہوئے جہاں سے ان کے باپ نے حکم دیا تھا وہ کچھ انہیں کچھ انہیں اللہ سے بچا نہ سکتا ہاں یعقوب کے جی کی ایک خواہش تھی جو اس نے پوری کرلی، اور بیشک وہ صاحب علم ہے ہمارے سکھائے سے مگر اکثر لوگ نہیں جانتے'),
(1665, 12, 69, 'اور جب وہ یوسف کے پاس گئے اس نے اپنے بھائی کو اپنے پاس جگہ دی کہا یقین جان میں ہی تیرا بھائی ہوں تو یہ جو کچھ کرتے ہیں اس کا غم نہ کھا'),
(1666, 12, 70, 'پھر جب ان کا سامان مہیا کردیا پیالہ اپنے بھائی کے کجاوے میں رکھ دیا پھر ایک منادی نے ندا کی اے قافلہ والو! بیشک تم چور ہو،'),
(1667, 12, 71, 'بولے اور ان کی طرف متوجہ ہوئے تم کیا نہیں پاتے،'),
(1668, 12, 72, 'بولے، بادشاہ کا پیمانہ نہیں ملتا اور جو اسے لائے گا اس کے لیے ایک اونٹ کا بوجھ ہے اور میں اس کا ضامن ہوں،'),
(1669, 12, 73, 'بولے خدا کی قسم! تمہیں خوب معلوم ہے کہ ہم زمین میں فساد کرنے نہ آئے اور نہ ہم چور ہیں،'),
(1670, 12, 74, 'بولے پھر کیا سزا ہے اس کی اگر تم جھوٹے ہو'),
(1671, 12, 75, 'بولے اس کی سزا یہ ہے کہ جس کے اسباب میں ملے وہی اس کے بدلے میں غلام بنے ہمارے یہاں ظالموں کی یہی سزا ہے'),
(1672, 12, 76, 'تو اول ان کی خرُجیوں سے تلاشی شروع کی اپنے بھائی کی خرُجی سے پہلے پھر اسے اپنے بھائی کی خرُجی سے نکال لیا ہم نے یوسف کو یہی تدبیر بتائی بادشاہی قانون میں اسے نہیں پہنچتا تھا کہ اپنے بھائی کو لے لے مگر یہ کہ خدا چاہے ہم جسے چاہیں درجوں بلند کریں اور ہر علم والے اوپر ایک علم والا ہے'),
(1673, 12, 77, 'بھائی بولے اگر یہ چوری کرے تو بیشک اس سے پہلے اس کا بھائی چوری کرچکا ہے تو یوسف نے یہ بات اپنے دل میں رکھی اور ان پر ظاہر نہ کی، جی میں کہا تم بدتر جگہ ہو اور اللہ خوب جانتا ہے جو باتیں بناتے ہو،'),
(1674, 12, 78, 'بولے اے عزیز! اس کے ایک باپ ہیں بوڑھے بڑے تو ہم میں اس کی جگہ کسی کو لے لو، بیشک ہم تمہارے احسان دیکھ رہے ہیں،'),
(1675, 12, 79, 'کہا خدا کی پناہ کہ ہم میں مگر اسی کو جس کے پاس ہمارا مال ملا جب تو ہم ظالم ہوں گے،'),
(1676, 12, 80, 'پھر جب اس سے نا امید ہوئے الگ جاکر سرگوشی کرنے لگے، ان کا بڑا بھائی بولا کیا تمہیں خبر نہیں کہ تمہارے باپ نے تم سے اللہ کا عہد لے لیا تھا اور اس سے پہلے یوسف کے حق میں تم نے کیسی تقصیر کی تو میں یہاں سے نہ ٹلوں گا یہاں تک کہ میرے باپ اجازت دیں یا اللہ مجھے حکم فرمائے اور اس کا حکم سب سے بہتر،'),
(1677, 12, 81, 'اپنے باپ کے پاس لوٹ کر جاؤ پھر عرض کرو اے ہمارے باپ بیشک آپ کے بیٹے نے چوری کی اور ہم تو اتنی ہی بات کے گواہ ہوئے تھے جتنی ہمارے علم میں تھی اور ہم غیب کے نگہبان نہ تھے'),
(1678, 12, 82, 'اور اس بستی سے پوچھ دیکھئے جس میں ہم تھے اور اس قافلہ سے جس میں ہم آئے، اور ہم بیشک سچے ہیں'),
(1679, 12, 83, 'کہا تمہارے نفس نے تمہیں کچھ حیلہ بنادیا، تو اچھا صبر ہے، قریب ہے کہ اللہ ان سب کو مجھ سے لا، ملائے بیشک وہی علم و حکمت والا ہے،'),
(1680, 12, 84, 'اور ان سے منہ پھیرا اور کہا ہائے افسوس! یوسف کی جدائی پر اور اس کی آنکھیں غم سے سفید ہوگئیں وہ غصہ کھا تا رہا،'),
(1681, 12, 85, 'بولے خدا کی قسم! آپ ہمیشہ یوسف کی یاد کرتے رہیں گے یہاں تک کہ گور کنارے جا لگیں یا جان سے گزر جائیں،'),
(1682, 12, 86, 'کہا میں تو اپنی پریشانی اور غم کی فریاد اللہ ہی سے کرتا ہوں اور مجھے اللہ کی وہ شانیں معلوم ہیں جو تم نہیں جانتے'),
(1683, 12, 87, 'اے بیٹو! جا ؤ یوسف اور اس کے بھائی کا سراغ لگاؤ اور اللہ کی رحمت سے مایوس نہ ہو، بیشک اللہ کی رحمت سے نا امید نہیں ہوتے مگر کافر لوگ'),
(1684, 12, 88, 'پھر جب وہ یوسف کے پاس پہنچے بولے اے عزیز ہمیں اور ہمارے گھر والوں کو مصیبت پہنچی اور ہم بے قدر پونجی لے کر آئے ہیں تو آپ ہمیں پورا ناپ دیجئے اور ہم پر خیرات کیجئے بیشک اللہ خیرات والوں کو صلہ دیتا ہے'),
(1685, 12, 89, 'بولے کچھ خبر ہے تم نے یوسف اور اس کے بھائی کے ساتھ کیا کِیا تھا جب تم نادان تھے'),
(1686, 12, 90, 'بولے کیا سچ مچ آپ ہی یوسف ہیں، کہا میں یوسف ہوں اور یہ میرا بھائی، بیشک اللہ نے ہم پر احسان کیا بیشک جو پرہیزگاری اور صبر کرے تو اللہ نیکوں کا نیگ (اجر) ضائع نہیں کرتا'),
(1687, 12, 91, 'بولے خدا کی قسم! بیشک اللہ نے آپ کو ہم پر فضیلت دی اور بیشک ہم خطاوار تھے'),
(1688, 12, 92, 'کہا آج تم پر کچھ ملامت نہیں، اللہ تمہیں معاف کرے، اور وہ سب مہربانوں سے بڑھ کر مہربان ہے'),
(1689, 12, 93, 'میرا یہ کرتا لے جاؤ اسے میرے باپ کے منہ پر ڈالو ان کی آنکھیں کھل جائیں گی اور اپنے سب گھر بھر کو میرے پاس لے آ ؤ،'),
(1690, 12, 94, 'جب قافلہ مصر سے جدا ہوا یہاں ان کے باپ نے کہا بیشک میں یوسف کی خوشبو پا تا ہوں اگر مجھے یہ نہ کہو کہ سٹھ (بہک) گیا ہے،'),
(1691, 12, 95, 'بیٹے بولے خدا کی قسم! آپ اپنی اسی پرانی خود رفتگی میں ہیں'),
(1692, 12, 96, 'پھر جب خوشی سنانے والا آیا اس نے وہ کرتا یعقوب کے منہ پر ڈالا اسی وقت اس کی آنکھیں پھر آئیں (دیکھنے لگیں) کہ میں نہ کہتا تھا کہ مجھے اللہ کی وہ شانیں معلوم ہیں جو تم نہیں جانتے'),
(1693, 12, 97, 'بولے اے ہمارے باپ! ہمارے گناہوں کی معافی مانگئے بیشک ہم خطاوار ہیں،'),
(1694, 12, 98, 'کہا جلد میں تمہاری بخشش اپنے رب سے چاہو ں گا، بیشک وہی بخشنے والا مہربان ہے'),
(1695, 12, 99, 'پھر جب وہ سب یوسف کے پاس پہنچے اس نے اپنے ماں باپ کو اپنے پاس جگہ دی اور کہا مصر میں داخل ہو اللہ چاہے تو امان کے ساتھ'),
(1696, 12, 100, 'اور اپنے ماں باپ کو تخت پر بٹھایا اور سب اس کے لیے سجدے میں گرے اور یوسف نے کہا اے میرے باپ یہ میرے پہلے خواب کی تعبیر ہے بیشک اسے میرے رب نے سچا کیا، اور بیشک اس نے مجھ پر احسان کیا کہ مجھے قید سے نکالا اور آپ سب کو گاؤں سے لے آیا بعد اس کے کہ شیطان نے مجھ میں اور میرے بھائیوں میں ناچاقی کرادی تھی، بیشک میرا رب جس بات کو چاہے آسان کردے بیشک وہی علم و حکمت والا ہے'),
(1697, 12, 101, 'اے میرے رب بیشک تو نے مجھے ایک سلطنت دی اور مجھے کچھ باتوں کا انجام نکالنا سکھایا، اے آسمانوں اور زمین کے بنانے والے تو میرا کام بنانے والا ہے دنیا اور آخرت میں، مجھے مسلمان اٹھا اور ان سے مِلا جو تیرے قرب خاص کے لائق ہیں'),
(1698, 12, 102, 'یہ کچھ غیب کی خبریں ہیں جو ہم تمہاری طرف وحی کرتے ہیں اور تم ان کے پاس نہ تھے جب انہوں نے اپنا کام پکا کیا تھا اور وہ داؤں چل رہے تھے'),
(1699, 12, 103, 'اور اکثر آدمی تم کتنا ہی چاہو ایمان نہ لائیں گے،'),
(1700, 12, 104, 'اور تم اس پر ان سے کچھ اجرت نہ مانگتے یہ تو نہیں مگر سارے جہان کو نصیحت،'),
(1701, 12, 105, 'اور کتنی نشانیاں ہیں آسمانوں اور زمین میں کہ اکثر لوگ ان پر گزرتے ہیں اور ان سے بے خبر رہتے ہیں،'),
(1702, 12, 106, 'اور ان میں اکثر وہ ہیں کہ اللہ پر یقین نہیں لاتے مگر شرک کرتے ہوئے'),
(1703, 12, 107, 'کیا اس سے نڈر ہو بیٹھے کہ اللہ کا عذاب انہیں آکر گھیر لے یا قیامت ان پر اچانک آجائے اور انہیں خبر نہ ہو،'),
(1704, 12, 108, 'تم فرماؤ یہ میری راہ ہے میں اللہ کی طرف بلاتا ہوں میں اور جو میرے قدموں پرچلیں دل کی آنکھیں رکھتے ہیں ۰ف۲۳۳) اور اللہ کو پاکی ہے اور میں شریک کرنے والا نہیں،'),
(1705, 12, 109, 'اور ہم نے تم سے پہلے جتنے رسول بھیجے سب مرد ہی تھے جنہیں ہم وحی کرتے اور سب شہر کے ساکن تھے تو یہ لوگ زمین پرچلے نہیں تو دیکھتے ان سے پہلوں کا کیا انجام ہوا اور بیشک آخرت کا گھر پرہیزگاروں کے لیے بہتر تو کیا تمہیں عقل نہیں،'),
(1706, 12, 110, 'یہاں تک جب رسولوں کو ظاہری اسباب کی امید نہ رہی اور لوگ سمجھے کہ رسولوں نے غلط کہا تھا اس وقت ہماری مدد آئی تو جسے ہم نے چاہا بچالیا گیا اور ہمارا عذاب مجرموں سے پھیرا نہیں جاتا،'),
(1707, 12, 111, 'بیشک ان کی خبروں سے عقل مندوں کی آنکھیں کھلتی ہیں یہ کوئی بناوٹ کی بات نہیں لیکن اپنوں سے اگلے کاموں کی تصدیق ہے اور ہر چیز کا مفصل بیان اور مسلمانوں کے لیے ہدایت اور رحمت،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'یہ کتاب کی آیتیں ہیں اور وہ جو تمہاری طرف تمہارے رب کے پاس سے اترا حق ہے مگر اکثر آدمی ایمان نہیں لاتے'),
(1709, 13, 2, 'اللہ ہے جس نے آسمانوں کو بلند کیا بے ستونوں کے کہ تم دیکھو پھر عرش پر استوا فرمایا جیسا اس کی شان کے لائق ہے اور سورج اور چاند کو مسخر کیا ہر ایک، ایک ٹھہرائے ہوئے وعدہ تک چلتا ہے اللہ کام کی تدبیر فرماتا اور مفصل نشانیاں بتاتا ہے کہیں تم اپنے رب رب کا ملنا یقین کرو'),
(1710, 13, 3, 'اور وہی ہے جس نے زمین کو پھیلا اور اس میں لنگر اور نہریں بنائیں، اور زمین ہر قسم کے پھل دو دو طرح کے بنائے رات سے دن کو چھپا لیتا ہے، بیشک اس میں نشانیاں ہیں دھیان کرنے والوں کو'),
(1711, 13, 4, 'اور زمین کے مختلف قطعے ہیں اور ہیں پاس پاس اور باغ ہیں انگوروں کے اور کھیتی اور کھجور کے پیڑ ایک تھالے (تھال) سے اُگے اور الگ الگ سب کو ایک ہی پانی دیا جا تا ہے اور پھلوں میں ہم ایک کو دوسرے سے بہتر کرتے ہیں، بیشک اس میں نشانیاں ہیں عقل مندوں کے لیے'),
(1712, 13, 5, 'اور اگر تم تعجب کرو تو اچنبھا تو ان کے اس کہنے کا ہے کہ کیا ہم مٹی ہو کر پھر نئے بنیں گے وہ ہیں جو اپنے رب سے منکر ہوئے اور وہ ہیں جن کی گردنوں میں طوق ہوں گے اور وہ دوزخ والے ہیں انھیں اسی میں رہنا،'),
(1713, 13, 6, 'اور تم سے عذاب کی جلدی کرتے ہیں رحمت سے پہلے اور ان اگلوں کی سزائیں ہوچکیں اور بیشک تمہارا رب تو لوگوں کے ظلم پر بھی انہیں ایک طرح کی معافی دیتا ہے اور بیشک تمہارے رب کا عذاب سخت ہے'),
(1714, 13, 7, 'اور کا فر کہتے ہیں ان پر ان کی طرف سے کوئی نشانی کیو ں نہیں اتری تم تو ڈر سنانے والے ہو اور ہر قوم کے ہادی'),
(1715, 13, 8, 'اللہ جانتا ہے جو کچھ کسی مادہ کے پیٹ میں ہے اور پیٹ جو کچھ گھٹتے بڑھتے ہیں اور ہر چیز اس کے پاس ایک اندازے سے ہے'),
(1716, 13, 9, 'ہر چھپے اور کھلے کا جاننے والا سب سے بڑا بلندی والا'),
(1717, 13, 10, 'برابر ہیں جو تم میں بات آہستہ کہے اور جو آواز سے اور جو رات میں چھپا ہے اور جو دن میں راہ چلتا ہے'),
(1718, 13, 11, 'آدمی کے لیے بدلی والے فرشتے ہیں اس کے آگے پیچھے کہ بحکم خدا اس کی حفاظت کرتے ہیں بیشک اللہ کسی قوم سے اپنی نعمت نہیں بدلتا جب تک وہ خود اپنی حالت نہ بدلیں، اور جب کسی قوم سے برائی چاہے تو وہ پھر نہیں سکتی، اور اس کے سوا ان کا کوئی حمایتی نہیں'),
(1719, 13, 12, 'وہی ہے تمہیں بجلی دکھاتا ہے ڈر کو اور امید کو اور بھاری بدلیاں اٹھاتا ہے،'),
(1720, 13, 13, 'اور گر ج اسے سراہتی ہوئی اس کی پاکی بولتی ہے اور فرشتے اس کے ڈر سے اور کڑک بھیجتا ہے تو اسے ڈالتا ہے جس پر چاہے اور وہ اللہ میں جھگڑتے ہوتے ہیں اور اس کی پکڑ سخت ہے،'),
(1721, 13, 14, 'اسی کا پکارنا سچا ہے اور اُس کے سوا جن کو پکارتے ہیں وہ ان کی کچھ بھی نہیں سنتے مگر اس کی طرح جو پانی کے سامنے اپنی ہتھیلیاں پھیلائے بیٹھا ہے کہ اس کے منہ میں پہنچ جائے اور وہ ہرگز نہ پہنچے گا، اور کافروں کی ہر دعا بھٹکتی پھرتی ہے،'),
(1722, 13, 15, 'اور اللہ ہی کو سجدہ کرتے ہیں جتنے آسمانوں اور زمین میں ہیں خوشی سے خواہ مجبوری سے اور ان کی پرچھائیاں ہر صبح و شام السجدة ۔۲'),
(1723, 13, 16, 'تم فرماؤ کون رب ہے آسمانوں اور زمین کا، تم خود ہی فرما ؤ اللہ تم فرما ؤ تو کیا اس کے سوا تم نے وہ حمایتی بنائے ہیں جو اپنا بھلا برا نہیں کرسکتے ہیں تم فرما ؤ کیا برابر ہوجائیں گے اندھا اور انکھیارا (بینا) یا کیا برابر ہوجائیں گی اندھیریاں اور اجالا کیا اللہ کے لیے ایسے شریک ٹھہراتے ہیں جنہوں نے اللہ کی طرح کچھ بنایا تو انہیں ان کا اور اس کا بنانا ایک سا معلوم ہوا تم فرما ؤ اللہ ہر چیز کا بنانے والا ہے اور وہ اکیلا سب پر غالب ہے'),
(1724, 13, 17, 'اس نے آسمان سے پانی اتارا تو نالے اپنے اپنے لائق بہہ نکلے تو پانی کی رو اس پر ابھرے ہوئے جھاگ اٹھا لائی، اور جس پر آگ دہکاتے ہیں گہنا یا اور اسباب بنانے کو اس سے بھی ویسے ہی جھاگ اٹھتے ہیں اللہ بتاتا ہے کہ حق و باطل کی یہی مثال ہے، تو جھاگ تو پھک (جل) کر دور ہوجاتا ہے، اور وہ جو لوگوں کے کام آئے زمین میں رہتا ہے اللہ یوں ہی مثالیں بیان فرماتا ہے،'),
(1725, 13, 18, 'جن لوگوں نے اپنے رب کا حکم مانا انہیں کے لیے بھلائی ہے اور جنہوں نے اس کا حکم نہ مانا اگر زمین میں جو کچھ ہے وہ سب اور اس جیسا اور ان کی ملِک میں ہوتا تو اپنی جان چھڑانے کو دے دیتے یہی ہیں جن کا برُ ا حساب ہوگا اور ان کا ٹھکانا جہنم ہے، اور کیا ہی بُرا بچھونا،'),
(1726, 13, 19, 'تو کیا وہ جانتا ہے جو کچھ تمہاری طرف تمہارے رب کے پاس سے اترا حق ہے وہ اس جیسا ہوگا جو اندھا ہے نصیحت وہی مانتے ہیں جنہیں عقل ہے،'),
(1727, 13, 20, 'وہ جو اللہ کا عہد پورا کرتے ہیں اور قول باندھ کر پھرتے نہیں'),
(1728, 13, 21, 'اور وہ کہ جوڑتے ہیں اسے جس کے جوڑنے کا اللہ نے حکم دیا اور اپنے رب سے ڈرتے ہیں اور حساب کی بُرائی سے اندیشہ رکھتے ہیں'),
(1729, 13, 22, 'اور وہ جنہوں نے صبر کیا اپنے رب کی رضا چاہنے کو اور نماز قائم رکھی اور ہمارے دیئے سے ہماری راہ میں چھپے اور ظاہر کچھ خرچ کیا اور برائی کے بدلے بھلائی کرکے ٹالتے ہیں انہیں کے لیے پچھلے گھر کا نفع ہے،'),
(1730, 13, 23, 'بسنے کے باغ جن میں وہ داخل ہوں گے اور جو لائق ہوں ان کے باپ دادا اور بیبیوں اور اولاد میں اور فرشتے ہر دروازے سے ان پر یہ کہتے آئیں گے،'),
(1731, 13, 24, 'سلامتی ہو تم پر تمہارے صبر کا بدلہ تو پچھلا گھر کیا ہی خوب ملا،'),
(1732, 13, 25, 'اور وہ جو اللہ کا عہد اس کے پکے ہونے کے بعد توڑتے اور جس کے جوڑنے کو اللہ نے فرمایا اسے قطع کرتے اور زمین میں فساد پھیلاتے ہیں ان کا حصہ لعنت ہی ہے اور اُن کا نصیبہ بُرا گھر'),
(1733, 13, 26, 'اللہ جس کے لیے چاہے رزق کشادہ اور تنگ کرتا ہے، اور کا فر دنیا کی زندگی پر اترا گئے (نازاں ہوئے) اور دنیا کی زندگی آخرت کے مقابل نہیں مگر کچھ دن برت لینا،'),
(1734, 13, 27, 'اور کافر کہتے ان پر کوئی نشانی ان کے رب کی طرف سے کیوں نہ اتری، تم فرماؤ بیشک اللہ جسے چاہے گمراہ کرتا ہے اور اپنی راہ اسے دیتا ہے جو اس کی طرف رجوع لائے،'),
(1735, 13, 28, 'وہ جو ایمان لائے اور ان کے دل اللہ کی یاد سے چین پاتے ہیں، سن لو اللہ کی یاد ہی میں دلوں کا چین ہے'),
(1736, 13, 29, 'وہ جو ایمان لائے اور اچھے کام کیے ان کو خوشی ہے اور اچھا انجام'),
(1737, 13, 30, 'اسی طرح ہم نے تم کو اس امت میں بھیجا جس سے پہلے امتیں ہو گزریں کہ تم انہیں پڑھ کر سناؤ جو ہم نے تمہاری طرف وحی کی اور وہ رحمن کے منکر ہورہے ہیں تم فرماؤ وہ میرا رب ہے اس کے سوا کسی کی بندگی نہیں میں نے اسی پر بھروسہ کیا اور اسی کی طرف میری رجوع ہے،'),
(1738, 13, 31, 'اور اگر کوئی ایسا قرآن آتا جس سے پہاڑ ٹل جاتے یا زمین پھٹ جاتی یا مردے باتیں کرتے جب بھی یہ کافر نہ مانتے بلکہ سب کام اللہ ہی کے اختیار ميں ہیں تو کیا مسلمان اس سے نا امید نہ ہوئے کہ اللہ چاہتا تو سب آدمیوں کو ہدایت کردیتا اور کافروں کو ہمیشہ کے لیے یہ سخت دھمک (ہلادینے والی مصیبت) پہنچتی رہے گی یا ان کے گھروں کے نزدیک اترے گی یہاں تک کہ اللہ کا وعدہ آئے بیشک اللہ وعدہ خلاف نہیں کرتا'),
(1739, 13, 32, 'اور بیشک تم سے اگلے رسولوں سے بھی ہنسی کی گئی تو میں نے کافروں کو کچھ دنوں ڈھیل دی پھر انہیں پکڑا تو میرا عذاب کیسا تھا،'),
(1740, 13, 33, 'تو کیا وہ ہر جان پر اس کے اعمال کی نگہداشت رکھتا ہے اور وہ اللہ کے شریک ٹھہراتے ہیں، تم فرماؤ ان کا نام تو لو یا اسے وہ بتاتے ہو جو اس کے علم میں ساری زمین میں نہیں یا یوں ہی اوپری بات بلکہ کافروں کی نگاہ میں ان کا فریب اچھا ٹھہرا ہے اور راہ سے روکے گئے اور جسے اللہ گمراہ کرے اسے کوئی ہدایت کرنے والا نہیں،'),
(1741, 13, 34, 'انہیں دنیا کے جیتے عذاب ہوگا اور بیشک آخرت کا عذاب سب سے سخت اور انہیں اللہ سے بچانے والا کوئی نہیں،'),
(1742, 13, 35, 'احوال اس جنت کا کہ ڈر والوں کے لیے جس کا وعدہ ہے، اس کے نیچے نہریں بہتی ہیں، اس کے میوے ہمیشہ اور اس کا سایہ ڈر والوں کا تو یہ انجام ہے اور کافروں کا انجام آ گ،'),
(1743, 13, 36, 'اور جن کو ہم نے کتاب دی وہ اس پر خوش ہوتے جو تمہاری طرف اترا اور ان گروہوں میں کچھ وہ ہیں کہ اس کے بعض سے منکر ہیں، تم فرماؤ مجھے تو یہی حکم ہے کہ اللہ کی بندگی کروں اور اس کا شریک نہ ٹھہراؤں، ميں اسی کی طرف بلاتا ہوں اور اسی کی طرف مجھے پھرنا'),
(1744, 13, 37, 'اور اسی طرح ہم نے اسے عربی فیصلہ اتارا اور اے سننے والے! اگر تو ان کی خواہشوں پر چلے گا بعد اس کے کہ تجھے علم آچکا تو اللہ کے آگے نہ تیرا کوئی حمایتی ہوگا نہ بچانے والا،'),
(1745, 13, 38, 'اور بیشک ہم نے تم سے پہلے رسول بھیجے اور ان کے لیے بیبیاں اور بچے کیے اور کسی رسول کا کام نہیں کہ کوئی نشانی لے آئے مگر اللہ کے حکم سے، ہر وعدہ کی ایک لکھت ہے'),
(1746, 13, 39, 'اللہ جو چاہے مٹاتا اور ثابت کرتا ہے اور اصل لکھا ہوا اسی کے پاس'),
(1747, 13, 40, 'اور اگر ہمیں تمہیں دکھا د یں کوئی وعدہ جو انہیں دیا جاتا ہے یا پہلے ہی اپنے پاس بلائیں تو بہرحال تم پر تو ضرور پہنچانا ہے اور حساب لینا ہمارا ذمہ'),
(1748, 13, 41, 'کیا انہیں نہیں سوجھتا کہ ہر طرف سے ان کی آبادی گھٹاتے آرہے ہیں اور اللہ حکم فرماتا ہے اس کا حکم پیچھے ڈالنے والا کوئی نہیں اور اسے حساب لیتے دیر نہیں لگتی،'),
(1749, 13, 42, 'اور ان سے اگلے فریب کرچکے ہیں تو ساری خفیہ تدبیر کا مالک تو اللہ ہی ہے جانتا ہے جو کچھ کوئی جان کمائے اور اب جاننا چاہتے ہیں کافر، کسے ملتا ہے پچھلا گھر'),
(1750, 13, 43, 'اور کافر کہتے ہی تم رسول نہیں، تم فرماؤ اللہ گواہ کافی ہے مجھ میں اور تم میں اور وہ جسے کتاب کا علم ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'ایک کتاب ہے کہ ہم نے تمہاری طرف اتاری کہ تم لوگوں کو اندھیریوں سے اجالے میں لا ؤ ان کے رب کے حکم سے اس کی راہ کی طرف جو عزت والا سب خوبیوں والا ہے'),
(1752, 14, 2, 'اللہ کہ اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں اور کافروں کی خرابی ہے ایک سخت عذاب سے'),
(1753, 14, 3, 'جنہیں آخرت سے دنیا کی زندگی پیاری ہے اور اللہ کی راہ سے روکتے اور اس میں کجی چاہتے ہیں، وہ دور کی گمراہی میں ہیں'),
(1754, 14, 4, 'اور ہم نے ہر رسول اس کی قوم ہی کی زبان میں بھیجا کہ وہ انہیں صاف بتائے پھراللہ گمراہ کرتا ہے جسے چاہے اور وہ راہ دکھاتا ہے جسے چاہے، اور وہی عزت و حکمت والا ہے،'),
(1755, 14, 5, 'اور بیشک ہم نے موسیٰ کو اپنی نشانیاں دے کر بھیجا کہ اپنی قوم کو اندھیریوں سے اجالے میں لا، اور انہیں اللہ کے دن یا د دِلا بیشک اس میں نشانیاں ہیں ہر بڑے صبرے والے شکر گزار کرو،'),
(1756, 14, 6, 'اور جب موسیٰ نے اپنی قوم سے کہا یاد کرو اپنے اوپر اللہ کا احسان جب اس نے تمہیں فرعون والوں سے نجات دی جو تم کو بری مار دیتے تھے اور تمہارے بیٹوں کو ذبح کرتے اور تمہاری بیٹیاں زندہ رکھتے، اور اس میں تمہارے رب کا بڑا فضل ہوا،'),
(1757, 14, 7, 'اور یاد کرو جب تمہارے رب نے سنادیا کہ اگر احسان مانو گے تو میں تمہیں اور دونگا اور اگر ناشکری کرو تو میرا عذاب سخت ہے،'),
(1758, 14, 8, 'اور موسیٰ نے کہا اگر تم اور زمین میں جتنے ہیں سب کا فر ہوجاؤ تو بیشک اللہ بے پروہ سب خوبیوں والا ہے،'),
(1759, 14, 9, 'کیا تمہیں ان کی خبریں نہ آئیں جو تم سے پہلے تھی نوح کی قوم اور عاد اور ثمود اور جو ان کے بعد ہوئے، انہیں اللہ ہی جانے ان کے پاس ان کے رسول روشن دلیلیں لے کر آئے تو وہ اپنے ہاتھ اپنے منہ کی طرف لے گئے اور بولے ہم منکر ہیں اس کے جو تمہارے ہاتھ بھیجا گیا اور جس راہ کی طرف ہمیں بلاتے ہو اس میں ہمیں وہ شک ہے کہ بات کھلنے نہیں دیتا،'),
(1760, 14, 10, 'ان کے رسولوں نے کہا کیا اللہ میں شک ہے آسمان اور زمین کا بنانے والا، تمہیں بلاتا ہے کہ تمہارے کچھ گناہ بخشے اور موت کے مقرر وقت تک تمہاری زندگی بے عذاب کاٹ دے، بولے تم تو ہمیں جیسے آدمی ہو تم چاہتے ہو کہ ہمیں اس سے باز رکھو جو ہمارے باپ دادا پوجتے تھے اب کوئی روشن سند ہمارے پاس لے آؤ'),
(1761, 14, 11, 'ان کے رسولوں نے ان سے کہا ہم ہیں تو تمہاری طرح انسان مگر اللہ اپنے بندوں میں جس پر چاہے احسان فرماتا ہے اور ہمارا کام نہیں کہ ہم تمہارے پاس کچھ سند لے آئیں مگر اللہ کے حکم سے، اور مسلمانوں کو اللہ ہی پر بھروسہ چاہیے'),
(1762, 14, 12, 'اور ہمیں کیا ہوا کہ اللہ پر بھروسہ نہ کریں اس نے تو ہماری راہیں ہمیں دکھادیں اور تم جو ہمیں ستا رہے ہو ہم ضرور اس پر صبر کریں گے، اور بھروسہ کرنے والوں کو اللہ ہی پر بھروسہ چاہیے،'),
(1763, 14, 13, 'اور کافروں نے اپنے رسولوں سے کہا ہم ضرور تمہیں اپنی زمین سے نکال دیں گے یا تم ہمارے دین پر کچھ ہوجاؤ، تو انہیں ان کے رب نے وحی بھیجی کہ ہم ضرور ظالموں کو ہلاک کریں گے'),
(1764, 14, 14, 'اور ضرور ہم تم کو ان کے بعد زمین میں بسائیں گے یہ اس لیے ہے جو میرے حضو ر کھڑے ہونے سے ڈرے اور میں نے جو عذاب کا حکم سنایا ہے، اس سے خوف کرے،'),
(1765, 14, 15, 'اور انہوں نے فیصلہ مانگا اور ہر سرکش ہٹ دھرم نا مُراد ہوا'),
(1766, 14, 16, 'جہنم اس کے پیچھے لگی اور اسے پیپ کا پانی پلایا جائے گا،'),
(1767, 14, 17, 'بہ مشکل اس کا تھوڑا تھوڑا گھونٹ لے گا اور گلے سے نیچے اتارنے کی امید نہ ہوگی اور اسے ہر طرف سے موت آئے گی اور مرے گا نہیں، اور اس کے پیچھے ایک گاڑھا عذاب'),
(1768, 14, 18, 'اپنے رب سے منکروں کا حال ایسا ہے کہ ان کے کام ہیں جیسے راکھ کہ اس پر ہوا کا سخت جھونکا آیا آندھی کے دن میں ساری کمائی میں سے کچھ ہاتھ نہ لگا، یہی ہے دور کی گمراہی،'),
(1769, 14, 19, 'کیا تو نے نہ دیکھا کہ اللہ نے آسمان اور زمین حق کے ساتھ بنائے اگر چاہے تو تمہیں لے جائے اور ایک نئی مخلوق لے آئے'),
(1770, 14, 20, 'اور یہ اللہ پر کچھ دشوار نہیں،'),
(1771, 14, 21, 'اور سب اللہ کے حضور اعلانیہ حاضر ہوں گے تو جو کمزور تھے بڑائی والوں سے کہیں گے ہم تمہارے تابع تھے کیا تم سے ہوسکتا ہے کہ اللہ کے عذاب میں سے کچھ ہم پر سے ٹال دو کہیں گے اللہ ہمیں ہدایت کرتا تو ہم تمہیں کرتے ہم پر ایک سا ہے چاہے بے قراری کریں یا صبر سے رہیں ہمیں کہیں پناہ نہیں،'),
(1772, 14, 22, 'اور شیطان کہے گا جب فیصلہ ہوچکے گا بیشک اللہ نے تم کو سچا وعدہ دیا تھا اور میں نے جو تم کو وعدہ دیا تھا وہ میں نے تم سے جھوٹا کیا اور میرا تم پر کچھ قابو نہ تھا مگر یہی کہ میں نے تم کو بلایا تم نے میری مان لی تو اب مجھ پر الزام نہ رکھو خود اپنے اوپر الزام رکھو نہ میں تمہاری فریاد کو پہنچ سکوں نہ تم میری فریاد کو پہنچ سکو، وہ جو پہلے تم نے مجھے شریک ٹھہرایا تھا میں اس سے سخت بیزار ہوں، بیشک ظالموں کے لیے دردناک عذاب ہے،'),
(1773, 14, 23, 'اور وہ جو ایمان لائے اور اچھے کام کیے وہ باغوں میں داخل کیے جائیں گے جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں اپنے رب کے حکم سے، اس میں ان کے ملتے وقت کا اکرام سلام ہے'),
(1774, 14, 24, 'کیا تم نے نہ دیکھا اللہ نے کیسی مثال بیان فرمائی پاکیزہ بات کی جیسے پاکیزہ درخت جس کی جڑ قائم اور شاخیں آسمان میں،'),
(1775, 14, 25, 'ہر وقت پھل دیتا ہے اپنے رب کے حکم سے اور اللہ لوگوں کے لیے مثالیں بیان فرماتا ہے کہ کہیں وہ سمجھیں'),
(1776, 14, 26, 'اور گندی بات کی مثال جیسے ایک گندہ پیڑ کہ زمین کے اوپر سے کاٹ دیا گیا اب اسے کوئی قیام نہیں'),
(1777, 14, 27, 'اللہ ثابت رکھتا ہے ایمان والوں کو حق بات پر دنیا کی زندگی میں اور آخرت میں اور اللہ ظالموں کو گمراہ کرتا ہے اور اللہ جو چاہے کرے،'),
(1778, 14, 28, 'کیا تم نے انہیں نہ دیکھا جنہوں نے اللہ کی نعمت ناشکری سے بدل دی اور اپنی قوم کو تباہی کے گھر لا اتار،'),
(1779, 14, 29, 'وہ جو دوزخ ہے اس کے اندر جائیں گے، اور کیا ہی بری ٹھہرنے کی جگہ،'),
(1780, 14, 30, 'اور اللہ کے لیے برابر والے ٹھہراے کہ اس کی راہ سے بہکاویں تم فرماؤ کچھ برت لو کہ تمہارا انجام آگ ہے'),
(1781, 14, 31, 'میرے ان بندوں سے فرماؤ جو ایمان لائے کہ نماز قائم رکھیں اور ہمارے دیے میں سے کچھ ہماری راہ میں چھپے اور ظاہر خرچ کریں اس دن کے آنے سے پہلے جس میں نہ سوداگری ہوگی نہ یارانہ'),
(1782, 14, 32, 'اللہ ہے جس نے آسمان اور زمین بنائے اور آسمان سے پانی اتارا تو اس سے کچھ پھل تمہارے کھانے کو پیدا کیے اور تمہارے لیے کشتی کو مسخر کیا کہ اس کے حکم سے دریا میں چلے اور تمہارے لیے ندیاں مسخر کیں،'),
(1783, 14, 33, 'اور تمہارے لیے سورج اور چاند مسخر کیے جو برابر چل رہے ہیں اور تمہارے لیے رات اور دن مسخر کیے'),
(1784, 14, 34, 'اور تمہیں بہت کچھ منہ مانگا دیا، اور اگر اللہ کی نعمتیں گنو تو شمار نہ کرسکو گے، بیشک آدمی بڑا ظالم ناشکرا ہے'),
(1785, 14, 35, 'اور یاد کرو جب ابراہیم نے عرض کی اے میرے رب اس شہر کو امان والا کردے اور مجھے اور میرے بیٹوں کو بتوں کے پوجنے سے بچا'),
(1786, 14, 36, 'اے میرے رب بیشک بتوں نے بہت لوگ بہکائے دیے تو جس نے میرا ساتھ دیا وہ تو میرا ہے اور جس نے میرا کہا نہ مانا تو بیشک تو بخشنے والا مہربان ہے'),
(1787, 14, 37, 'اے میرے رب میں نے اپنی کچھ اولاد ایک نالے میں بسائی جس میں کھیتی نہیں ہوتی تیرے حرمت والے گھر کے پاس اے میرے رب اس لیے کہ وہ نماز قائم رکھیں تو تو لوگوں کے کچھ دل ان کی طرف مائل کردے اور انہیں کچھ پھل کھانے کو دے شاید وہ احسان مانیں،'),
(1788, 14, 38, 'اے ہمارے رب تو جانتا ہے جو ہم چھپاتے ہیں اور ظاہر کرتے اور اللہ پر کچھ چھپا نہیں زمین میں اور نہ آسمان میں'),
(1789, 14, 39, 'سب خوبیاں اللہ کو جس نے مجھے بڑھاپے میں اسماعیل و اسحاق دیئے بیشک میرا رب دعا سننے والا ہے،'),
(1790, 14, 40, 'اے میرے رب! مجھے نماز قائم کرنے والا رکھ اور کچھ میری اولاد کو اے ہمارے رب اور ہماری دعا سن لے،'),
(1791, 14, 41, 'اے ہمارے رب مجھے بخش دے اور میرے ماں باپ کو اور سب مسلمانوں کو جس دن حساب قائم ہوگا،'),
(1792, 14, 42, 'اور ہرگز اللہ کو بےخبر نہ جاننا ظالموں کے کام سے انہیں ڈھیل نہیں دے رہا ہے مگر ایسے دن کے لیے جس میں'),
(1793, 14, 43, 'آنکھیں کھلی کی کھلی رہ جائیں گی بے تحاشا دوڑے نکلیں گے اپنے سر اٹھائے ہوئے کہ ان کی پلک ان کی طرف لوٹتی نہیں اور ان کے دلوں میں کچھ سکت نہ ہوگی'),
(1794, 14, 44, 'اور لوگوں کو اس دن سے ڈراؤ جب ان پر عذاب آئے گا تو ظالم کہیں گے اے ہمارے رب! تھوڑی دیر ہمیں مہلت دے کہ ہم تیرا بلانا مانیں اور رسولوں کی غلامی کریں تو کیا تم پہلے قسم نہ کھاچکے تھے کہ ہمیں دنیا سے کہیں ہٹ کر جانا نہیں'),
(1795, 14, 45, 'اور تم ان کے گھروں میں بسے جنہوں نے اپنا برا کیا تھا اور تم پر خوب کھل گیا ہم نے ان کے ساتھ کیسا کیا اور ہم نے تمہیں مثالیں دے کر بتادیا'),
(1796, 14, 46, 'اور بیشک وہ اپنا سا داؤں (فریب) چلے اور ان کا داؤں اللہ کے قابو میں ہے، اور ان کا داؤں کچھ ایسانہ تھا جس سے یہ پہاڑ ٹل جائیں'),
(1797, 14, 47, 'تو ہر گز خیال نہ کرنا کہ اللہ اپنے رسولوں سے وعدہ خلاف کرے گا بیشک اللہ غالب ہے بدلہ لینے والا،'),
(1798, 14, 48, 'جس دن بدل دی جائے گی زمین اس زمین کے سوا اور آسمان اور لوگ سب نکل کھڑے ہوں گے ایک اللہکے سامنے جو سب پر غالب ہے'),
(1799, 14, 49, 'اور اس دن تم مجرموں کو دیکھو گے کہ بیڑیوں میں ایک دوسرے سے جڑے ہوں گے'),
(1800, 14, 50, 'ان کے کرُتے رال ہوں گے اور ان کے چہرے آ گ ڈھانپ لے گی'),
(1801, 14, 51, 'اس لیے کہ اللہ ہر جان کو اس کی کمائی کا بدلہ دے، بیشک اللہ کو حساب کرتے کچھ دیر نہیں لگتی،'),
(1802, 14, 52, 'یہ لوگوں کو حکم پہنچانا ہے اور اس لیے کہ وہ اس سے ڈرائے جائیں اور اس لیے کہ وہ جان لیں کہ وہ ایک ہی معبود ہے اور اس لیے کہ عقل والے نصیحت مانیں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'یہ آیتیں ہیں کتاب اور روشن قرآن کی-'),
(1804, 15, 2, 'بہت آرزوئیں کریں گے کافر کاش مسلمان ہوتے،'),
(1805, 15, 3, 'انہیں چھوڑو کہ کھائیں اور برتیں اور امید انہیں کھیل میں ڈالے تو اب جانا چاہتے ہیں'),
(1806, 15, 4, 'اور جو بستی ہم نے ہلاک کی اس کا ایک جانا ہوا نوشتہ تھا'),
(1807, 15, 5, 'کو ئی گروہ اپنے وعدہ سے آگے نہ بڑھے نہ پیچھے ہٹے،'),
(1808, 15, 6, 'اور بولے کہ اے وہ جن پر قرآن اترا بیشک مجنون ہو'),
(1809, 15, 7, 'ہمارے پاس فرشتے کیوں نہیں لاتے اگر تم سچے ہو'),
(1810, 15, 8, 'ہم فرشتے بیکار نہیں اتارتے اور وہ اتریں تو انہیں مہلت نہ ملے'),
(1811, 15, 9, 'بیشک ہم نے اتارا ہے یہ قرآن اور بیشک ہم خود اس کے نگہبان ہیں'),
(1812, 15, 10, 'اور بیشک ہم نے تم سے پہلے اگلی امتوں میں رسول بھیجے،'),
(1813, 15, 11, 'اور ان کے پاس کوئی رسول نہیں آتا مگر اس سے ہنسی کرتے ہیں'),
(1814, 15, 12, 'ایسے ہی ہم اس ہنسی کو ان مجرموں کے دلوں میں راہ دیتے ہیں،'),
(1815, 15, 13, 'وہ اس پر ایمان نہیں لاتے اور اگلوں کی راہ پڑچکی ہے'),
(1816, 15, 14, 'اور اگر ہم ان کے لیے آسمان میں کوئی دروازہ کھول دیں کہ دن کو اس میں چڑھتے،'),
(1817, 15, 15, 'جب بھی یہی کہتے کہ ہماری نگاہ باندھ دی گئی ہے بلکہ ہم پر جادو ہوا ہے'),
(1818, 15, 16, 'اور بیشک ہم نے آسمان میں برج بنائے اور اسے دیکھنے والو ں کے لیے آراستہ کیا'),
(1819, 15, 17, 'اور اسے ہم نے ہر شیطان مردود سے محفوظ رکھا'),
(1820, 15, 18, 'مگر جو چوری چھپے سننے جائے تو اس کے پیچھے پڑتا ہے روشن شعلہ'),
(1821, 15, 19, 'اور ہم نے زمین پھیلائی اور اس میں لنگر ڈالے اور اس میں ہر چیز اندازے سے اگائی،'),
(1822, 15, 20, 'اور تمہارے لیے اس میں روزیاں کردیں اور وہ کردیے جنہیں تم رزق نہیں دیتے'),
(1823, 15, 21, 'اور کوئی چیز نہیں جس کے ہمارے پاس خزانے نہ ہوں اور ہم اسے نہیں اتارتے مگر ایک معلوم انداز سے،'),
(1824, 15, 22, 'اور ہم نے ہوائیں بھیجیں بادلوں کو با رور کرنے والیاں تو ہم نے آسمان سے پانی اتارا پھر وہ تمہیں پینے کو دیا اور تم کچھ اس کے خزانچی نہیں'),
(1825, 15, 23, 'اور بیشک ہم ہی جِلائیں اور ہم ہی ماریں اور ہم ہی وارث ہیں'),
(1826, 15, 24, 'اور بیشک ہمیں معلوم ہیں جو تم میں آگے بڑھے اور بیشک ہمیں معلوم ہیں جو تم میں پیچھے رہے،'),
(1827, 15, 25, 'اور بیشک تمہارا رب ہی تمہیں قیامت میں اٹھائے گا بیشک وہی علم و حکمت والا ہے،'),
(1828, 15, 26, 'اور بیشک ہم نے آدمی کو بجتی ہوئی مٹی سے بنایا جو اصل میں ایک سیاہ بودار گارا تھی، ف۳۳)'),
(1829, 15, 27, 'اور جِن کو اس سے پہلے بنایا بے دھوئیں کی آگ سے،'),
(1830, 15, 28, 'اور یاد کرو جب تمہارے رب نے فرشتوں سے فرمایا کہ میں آدمی کو بنانے والا ہوں بجتی مٹی سے جو بدبودار سیاہ گارے سے ہے،'),
(1831, 15, 29, 'تو جب میں اسے ٹھیک کرلوں اور اور میں اپنی طرف کی خاص معز ز ر وح پھونک دوں تو اس کے لیے سجدے میں گر پڑنا،'),
(1832, 15, 30, 'تو جتنے فرشتے تھے سب کے سب سجدے میں گرے،'),
(1833, 15, 31, 'سوا ابلیس کے، اس نے سجدہ والوں کا ساتھ نہ مانا'),
(1834, 15, 32, 'فرمایا اے ابلیس تجھے کیا ہوا کہ سجدہ کرنے والوں سے الگ رہا،'),
(1835, 15, 33, 'بولا مجھے زیبا نہیں کہ بشر کو سجدہ کروں جسے تو نے بجتی مٹی سے بنایا جو سیاہ بودار گارے سے تھی،'),
(1836, 15, 34, 'فرمایا تو جنت سے نکل جا کہ تو مردود ہے،'),
(1837, 15, 35, 'اور بیشک قیامت تک تجھ پر لعنت ہے'),
(1838, 15, 36, 'بولا اے میرے رب تو مجھے مہلت دے اس دن تک کہ وہ اٹھائے جائیں'),
(1839, 15, 37, 'فرمایا تو ان میں سے ہے جن کو اس معلوم،'),
(1840, 15, 38, 'وقت کے دن تک مہلت ہے،'),
(1841, 15, 39, 'بولا اے رب میرے! قسم اس کی کہ تو نے مجھے گمراہ کیا میں انہیں زمین میں بھلاوے دوں گا اور ضرور میں ان سب کو بے راہ کروں گا'),
(1842, 15, 40, 'مگر جو ان میں تیرے چنے ہوئے بندے ہیں،'),
(1843, 15, 41, 'فرمایا یہ راستہ سیدھا میری طرف آتا ہے،'),
(1844, 15, 42, 'بیشک میرے بندوں پر تیرا کچھ قابو نہیں سوا ان گمراہوں کے جو تیرا ساتھ دیں،'),
(1845, 15, 43, 'اور بیشک جہنم ان سب کا وعدہ ہے،'),
(1846, 15, 44, 'اس کے سات دروازے ہیں، ہر دروازے کے لیے ان میں سے ایک حصہ بٹا ہوا ہے،'),
(1847, 15, 45, 'بیشک ڈر والے باغوں اور چشموں میں ہیں'),
(1848, 15, 46, 'ان میں داخل ہو سلامتی کے ساتھ امان میں،'),
(1849, 15, 47, 'اور ہم نے ان کے سینوں میں جو کچھ کینے تھے سب کھینچ لیے آپس میں بھائی ہیں تختوں پر روبرو بیٹھے،'),
(1850, 15, 48, 'نہ انہیں اس میں کچھ تکلیف پہنچے نہ وہ اس میں سے نکالے جائیں،'),
(1851, 15, 49, 'خبردو (۵۴) میرے بندوں کو کہ بیشک میں ہی ہوں بخشے والا مہربان،'),
(1852, 15, 50, 'اور میرا ہی عذاب دردناک عذاب ہے،'),
(1853, 15, 51, 'اور انہیں احوال سناؤ ابراہیم کے مہمانوں کا،'),
(1854, 15, 52, 'جب وہ اس کے پاس آئے تو بولے سلام کہا ہمیں تم سے ڈر معلوم ہوتا ہے،'),
(1855, 15, 53, 'انہوں نے کہا ڈریے نہیں ہم آپ کو ایک علم والے لڑکے کی بشارت دیتے ہیں،'),
(1856, 15, 54, 'کہا کیا اس پر مجھے بشارت دیتے ہو کہ مجھے بڑھاپا پہنچ گیا اب کاہے پر بشارت دیتے ہو،'),
(1857, 15, 55, 'کہا ہم نے آپ کو سچی بشارت دی ہے آپ ناامید نہ ہوں،'),
(1858, 15, 56, 'کہا اپنے رب کی رحمت سے کون ناامید ہو مگر وہی جو گمراہ ہوئے، ف۶۱)'),
(1859, 15, 57, 'کہا پھر تمہارا کیا کام ہے اے فرشتو!'),
(1860, 15, 58, 'بولے ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں،'),
(1861, 15, 59, 'مگر لوط کے گھر والے، ان سب کو ہم بچالیں گے،'),
(1862, 15, 60, 'مگر اس کی عورت ہم ٹھہراچکے ہیں کہ وہ پیچھے رہ جانے والوں میں ہے، ف۶۵)'),
(1863, 15, 61, 'تو جب لوط کے گھر فرشتے آئے، ۶۶)'),
(1864, 15, 62, 'کہا تم تو کچھ بیگانہ لوگ ہو، ف۶۷)'),
(1865, 15, 63, 'کہا بلکہ ہم تو آپ کے پاس وہ لائے ہیں جس میں یہ لوگ شک کرتے تھے،'),
(1866, 15, 64, 'اور ہم آپ کے پاس سچا حکم لائے ہیں اور ہم بیشک سچے ہیں،'),
(1867, 15, 65, 'تو اپنے گھر والوں کو کچھ رات رہے لے کر باہر جایے اور آپ ان کے پیچھے چلئے اور تم میں کوئی پیچھے پھر کر نہ دیکھے اور جہاں کو حکم ہے سیدھے چلے جایئے،'),
(1868, 15, 66, 'اور ہم نے اسے اس حکم کا فیصلہ سنادیا کہ صبح ہوتے ان کافروں کی جڑ کٹ جائے گی،'),
(1869, 15, 67, 'اور شہر والے خوشیاں مناتے آئے،'),
(1870, 15, 68, 'لوط نے کہا یہ میرے مہمان ہیں مجھے فضیحت (رُسوا) نہ کرو، ف۷۵)'),
(1871, 15, 69, 'اور اللہ سے ڈرو اور مجھے رسوا نہ کرو،'),
(1872, 15, 70, 'بولے کیا ہم نے تمہیں منع نہ کیا تھا کہ اوروں کے معاملہ میں دخل نہ دو،'),
(1873, 15, 71, 'کہا یہ قوم کی عورتیں میری بیٹیاں ہیں اگر تمہیں کرنا ہے،'),
(1874, 15, 72, 'اے محبوب تمہاری جان کی قسم بیشک وہ اپنے نشہ میں بھٹک رہے ہیں،'),
(1875, 15, 73, 'تو دن نکلتے انہیں چنگھاڑ نے آلیا'),
(1876, 15, 74, 'تو ہم نے اس بستی کا اوپر کا حصہ اس نے نیچے کا حصہ کردیا اور ان پر کنکر کے پتھر برسائے،'),
(1877, 15, 75, 'بیشک اس میں نشانیاں ہیں فراست والوں کے لیے،'),
(1878, 15, 76, 'اور بیشک وہ بستی اس راہ پر ہے جو اب تک چلتی ہے،'),
(1879, 15, 77, 'بیشک اس میں نشانیاں ہیں ایمان والوں کو،'),
(1880, 15, 78, 'اور بیشک جھاڑی والے ضرور ظالم تھے، ف۸۲)'),
(1881, 15, 79, 'تو ہم نے ان سے بدلہ لیا اور بیشک دونوں بستیاں کھلے راستہ پر پڑتی ہیں،'),
(1882, 15, 80, 'اور بیشک حجر والو ں نے رسولوں کو جھٹلایا'),
(1883, 15, 81, 'اور ہم نے ان کو اپنی نشانیاں دیں تو وہ ان سے منہ پھیرے رہے،'),
(1884, 15, 82, 'اور وہ پہاڑوں میں گھر تراشتے تھے بے خوف'),
(1885, 15, 83, 'تو انہیں صبح ہوتے چنگھاڑ نے آلیا'),
(1886, 15, 84, 'تو ان کی کمائی کچھ ان کے کام نہ آئی'),
(1887, 15, 85, 'اور ہم نے آسمان اور زمین اور جو کچھ ان کے درمیان ہے عبث نہ بنایا، اور بیشک قیامت آنے والی ہے تو تم اچھی طرح درگزر کرو، ف۹۳)'),
(1888, 15, 86, 'بیشک تمہارا رب ہی بہت پیدا کرنے والا جاننے والا ہے'),
(1889, 15, 87, 'اور بیشک ہم نے تم کو سات آیتیں دیں جو دہرائی جاتی ہیں اور عظمت والا قرآن،'),
(1890, 15, 88, 'اپنی آنکھ اٹھاکر اس چیز کو نہ دیکھو جو ہم نے ان کے جوڑوں کو برتنے کو دی اور ان کا کچھ غم نہ کھاؤ اور مسلمانوں کو اپنی رحمت کے پروں میں لے لو،'),
(1891, 15, 89, 'اور فرماؤ کہ میں ہی ہوں صاف ڈر سنانے والا (اس عذاب سے)،'),
(1892, 15, 90, 'جیسا ہم نے بانٹنے والوں پر اتارا،'),
(1893, 15, 91, 'جنہوں نے کلامِ الٰہی کو تکے بوٹی کرلیا، ف۹۹)'),
(1894, 15, 92, 'تو تمہارے رب کی قسم ہم ضرور ان سب سے پوچھیں گے،'),
(1895, 15, 93, 'جو کچھ وہ کرتے تھے،'),
(1896, 15, 94, 'تو اعلانیہ کہہ دو جس بات کا تمہیں حکم ہے اور مشرکوں سے منہ پھیر لو، ف۱۰۳)'),
(1897, 15, 95, 'بیشک ان ہنسنے والوں پر ہم تمہیں کفا یت کرتے ہیں'),
(1898, 15, 96, 'جو ا لله کے ساتھ دوسرا معبود ٹھہراتے ہیں تو اب جان جائیں گے،'),
(1899, 15, 97, 'اور بیشک ہمیں معلوم ہے کہ ان کی باتوں سے تم دل تنگ ہوتے ہو،'),
(1900, 15, 98, 'تو اپنے رب کو سراہتے ہوئے اس کی پاکی بولو اور سجدہ والوں میں ہو،'),
(1901, 15, 99, 'اور مرتے دم تک اپنے رب کی عبادت میں رہو،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'اب آتا ہے اللہ کا حکم تو اس کی جلدی نہ کرو پاکی اور برتری ہے اسے ان شریکوں سے،'),
(1903, 16, 2, 'ملائکہ کو ایمان کی جان یعنی وحی لے کر اپنے جن بندوں پر چاہے اتارتا ہے کہ ڈر سناؤ کہ میرے سوا کسی کی بندگی نہیں تو مجھ سے ڈرو'),
(1904, 16, 3, 'اس نے آسمان اور زمین بجا بنائے وہ ان کے شرک سے برتر ہے،'),
(1905, 16, 4, '(اس نے) آدمی کو ایک نِتھری بوند سے بنایا تو جبھی کھلا جھگڑالو ہے،'),
(1906, 16, 5, 'اور چوپائے پیدا کیے ان میں تمہارے لیے گرم لباس اور منفعتیں ہیں اور ان میں سے کھاتے ہو،'),
(1907, 16, 6, 'اور تمہارا ان میں تجمل ہے جب انہیں شام کو واپس لاتے ہو اور جب چرنے کو چھوڑتے ہو،'),
(1908, 16, 7, 'اور وہ تمہارے بوجھ اٹھا کر لے جاتے ہیں ایسے شہر کی طرف کہ اس تک نہ پہنچتے مگر ادھ مرے ہوکر، بیشک تمہارا رب نہایت مہربان رحم والا ہے'),
(1909, 16, 8, 'اور گھوڑے اور خچر اور گدھے کہ ان پر سوار ہو اور زینت کے لیے، اور وہ پیدا کرے گا جس کی تمہیں خبر نہیں،'),
(1910, 16, 9, 'اور بیچ کی راہ ٹھیک اللہ تک ہے اور کوئی راہ ٹیڑھی ہے اور چاہتا تو تم سب کو راہ پر لاتا،'),
(1911, 16, 10, 'وہی ہے جس نے آسمان سے پانی اتارا اس سے تمہارا پینا ہے اور اس سے درخت ہیں جن سے چَراتے ہو'),
(1912, 16, 11, 'اس پانی سے تمہارے لیے کھیتی اگاتا ہے اور زیتون اور کھجور اور انگور اور ہر قسم کے پھل بیشک اس میں نشانی ہے دھیان کرنے والوں کو،'),
(1913, 16, 12, 'اور اس نے تمہارے لیے مسخر کیے رات اور دن اور سورج اور چاند، اور ستارے اس کے حکم کے باندھے ہیں بیشک اس آیت میں نشانیاں ہیں عقل مندوں کو'),
(1914, 16, 13, 'اور وہ جو تمہارے لیے زمین میں پیدا کیا رنگ برنگ بیشک اس میں نشانی ہے یاد کرنے والوں کو'),
(1915, 16, 14, 'اور وہی ہے جس نے تمہارے لیے دریا مسخر کیا کہ اس میں سے تازہ گوشت کھاتے ہو اور اس میں سے گہنا (زیور) نکالتے ہو جسے پہنتے ہو اور تو اس میں کشتیاں دیکھے کہ پانی چیر کر چلتی ہیں اور اس لیے کہ تم اس کا فضل تلاش کرو اور کہیں احسان مانو،'),
(1916, 16, 15, 'اور اس نے زمین میں لنگر ڈالے کہ کہیں تمہیں لے کر نہ کانپے اور ندیاں اور رستے کہ تم راہ پاؤ'),
(1917, 16, 16, 'اور علامتیں اور ستارے سے وہ راہ پاتے ہیں'),
(1918, 16, 17, 'تو کیا جو بنائے وہ ایسا ہوجائے گا جو نہ بنائے تو کیا تم نصیحت نہیں مانتے،'),
(1919, 16, 18, 'اور اگر اللہ کی نعمتیں گنو تو انہیں شمار نہ کرسکو گے بیشک اللہ بخشنے والا مہربان ہے'),
(1920, 16, 19, 'اور اللہ جانتا ہے جو چھپاتے اور ظاہر کرتے ہو،'),
(1921, 16, 20, 'اور اللہ کے سوا جن کو پوجتے ہو ہیں (۳۲) وہ کچھ بھی نہیں بناتے اور وہ خود بنائے ہوئے ہیں'),
(1922, 16, 21, 'مُردے ہیں زندہ نہیں اور انہیں خبر نہیں لوگ کب اٹھائے جایں گے'),
(1923, 16, 22, 'تمہارا معبوثد ایک معبود ہے تو وہ جو آخرت پر ایمان نہیں لاتے ان کے دل منکر ہیں اور وہ مغرور ہیں'),
(1924, 16, 23, 'فی الحقیقت اللہ جانتا ہے جو چھپاتے اور جو ظاہر کرتے ہیں، بیشک وہ مغروروں کو پسند نہیں فرماتا،'),
(1925, 16, 24, 'اور جب ان سے کہا جائے تمہارے رب نے کیا اتارا کہیں اگلوں کی کہانیاں ہیں'),
(1926, 16, 25, 'کہ قیامت کے دن اپنے بوجھ پورے اٹھائیں اور کچھ بوجھ ان کے جنہیں اپنی جہالت سے گمراہ کرتے ہیں، سن لو کیا ہی برا بوجھ اٹھاتے ہیں،'),
(1927, 16, 26, 'بیشک ان سے اگلوں نے فریب کیا تھا تو اللہ نے ان کی چنائی کو نیو سے (تعمیر کو بنیاد) سے لیا تو اوپر سے ان پر چھت گر پڑی اور عذاب ان پر وہاں سے آیا جہاں کی انہیں خبر نہ تھی'),
(1928, 16, 27, 'پھر قیامت کے دن انہیں رسوا کرے گا اور فرمائے گا کہاں ہیں میرے وہ شریک جن میں تم جھگڑتے تھے علم والے کہیں گے آج ساری رسوائی اور برائی کافروں پر ہے'),
(1929, 16, 28, 'وہ کہ فرشتے ان کی جان نکالتے ہیں اس حال پر کیا وہ اپنا برا کررہے تھے اب صلح ڈالیں گے کہ ہم تو کچھ برائی نہ کرتے تھے ہاں کیوں نہیں، بیشک اللہ خوب جانتا ہے جو تمہارے کوتک (برے اعمال) تھے'),
(1930, 16, 29, 'اب جہنم کے دروازوں میں جاؤ کہ ہمیشہ اس میں رہو، تو کیا ہی برا ٹھکانا مغروروں کا،'),
(1931, 16, 30, 'اور ڈر والوں سے کہا گیا تمہارے رب رب نے کیا اتارا، بولے خوبی جنہوں نے اس دنیا میں بھلائی کی ان کے لیے بھلائی ہے اور بیشک پچھلا گھر سب سے بہتر، اور ضرور کیا ہی اچھا گھر پرہیزگاروں کا'),
(1932, 16, 31, 'بسنے کے باغ جن میں جائیں گے ان کے نیچے نہریں رواں انہیں وہاں ملے گا جو چاہیں اللہ ایسا ہی صلہ دیتا ہے پرہیزگاروں کو'),
(1933, 16, 32, 'وہ جن کی جان نکالتے ہیں فرشتے ستھرے پن میں یہ کہتے ہوئے کہ سلامتی ہو تم پر جنت میں جاؤ بدلہ اپنے کیے کا،'),
(1934, 16, 33, 'کاہے کے انتظار میں ہیں مگر اس کے کہ فرشتے ان پر آئیں یا تمہارے رب کا عذاب آئے ان سے اگلوں نے ایسا ہی کیا اور اللہ نے ان پر کچھ ظلم نہ کیا، ہاں وہ خود ہی اپنی جانوں پر ظلم کرتے تھے،'),
(1935, 16, 34, 'تو ان کی بری کمائیاں ان پر پڑیں اور انہیں گھیرلیا اس نے جس پر ہنستے تھے،'),
(1936, 16, 35, 'اور مشرک بولے اللہ چاہتا تو اس کے سوا کچھ نہ پوجنے نہ ہم اور نہ ہمارے باپ دادا اور نہ اس سے جدا ہو کر ہم کوئی چیز حرام ٹھہراتے جیسا ہی ان سے اگلوں نے کیا تو رسولوں پر کیا ہے مگر صاف پہنچا دینا،'),
(1937, 16, 36, 'اور بیشک ہر امت میں ہم نے ایک رسول بھیجا کہ اللہ کو پوجو اور شیطان سے بچو تو ان میں کسی کو اللہ نے راہ دکھائی اور کسی پر گمراہی ٹھیک اتری تو زمین میں چل پھر کر دیکھو کیسا انجام ہوا جھٹلانے والوں کا'),
(1938, 16, 37, 'اگر تم ان کی ہدایت کی حرص کرو تو بیشک اللہ ہدایت نہیں دیتا جسے گمراہ کرے اور ان کا کوئی مددگار نہیں،'),
(1939, 16, 38, 'اور انہوں نے اللہ کی قسم کھائی اپنے حلف میں حد کی کوشش سے کہ اللہ مُردے نہ اٹھائے گا ہاں کیوں نہیں (۷۹) سچا وعدہ اس کے ذمہ پر لیکن اکثر لوگ نہیں جانتے'),
(1940, 16, 39, 'اس لیے کہ انہیں صاف بتادے جس بات میں جھگڑتے تھے اور اس لیے کہ کافر جان لیں کہ وہ جھوٹے تھے'),
(1941, 16, 40, 'جو چیز ہم چاہیں اس سے ہمارا فرمانا یہی ہوتا ہے کہ ہم کہیں ہوجا وہ فوراً ہوجاتی ہے،'),
(1942, 16, 41, 'اور جنہوں نے اللہ کی راہ مں ی اپنے گھر بار چھوڑے مظلوم ہوکر ضرور ہم انہیں دنیا میں اچھی جگہ دیں گے اور بیشک آخرت کا ثواب بہت بڑا ہے کسی طرح لوگ جانتے، ف۸۶)'),
(1943, 16, 42, 'وہ جنہوں نے صبر کیا اور اپنے رب ہی پر بھروسہ کرتے ہیں'),
(1944, 16, 43, 'اور ہم نے تم سے پہلے نہ بھیجے مگر مرد جن کی طرف ہم وحی کرتے، تو اے لوگو! علم والوں سے پوچھو اگر تمہیں علم نہیں، ف۹۰)'),
(1945, 16, 44, 'روشن دلیلیں اور کتابیں لے کر اور اے محبوب ہم نے تمہاری ہی طرف یہ یاد گار اتاری کہ تم لوگوں سے بیان کردو جو ان کی طرف اترا اور کہیں وہ دھیان کریں،'),
(1946, 16, 45, 'تو کیا جو لوگ بڑے مکر کرتے ہیں اس سے نہیں ڈرتے کہ اللہ انہیں زمین میں دھنسادے یا انہیں وہاں سے عذاب آئے جہاں سے انہیں خبر نہ ہو،'),
(1947, 16, 46, 'یا انہیں چلتے پھرتے پکڑ لے کہ وہ تھکا نہیں سکتے، ف۹۸)'),
(1948, 16, 47, 'یا انہیں نقصان دیتے دیتے گرفتار کرلے کہ بیشک تمہارا رب نہایت مہربان رحم والا ہے،'),
(1949, 16, 48, 'اور کیا انہوں نے نہ دیکھا کہ جو چیز اللہ نے بنائی ہے اس کی پرچھائیاں دائیں اور بائیں جھکتی ہیں اللہ کو سجدہ کرتی اور وہ اس کے حضور ذلیل ہیں'),
(1950, 16, 49, 'اور اللہ ہی کو سجدہ کرتے ہیں جو کچھ آسمانوں میں ہیں اور جو کچھ زمین میں چلنے والا ہے اور فرشتے اور وہ غرور نہیں کرتے،'),
(1951, 16, 50, 'اپنے اوپر اپنے رب کا خوف کرتے ہیں اور وہی کرتے ہیں جو انہیں حکم ہو،'),
(1952, 16, 51, 'اور اللہ نے فرمادیا دو خدا نہ ٹھہراؤ وہ تو ایک ہی معبود ہے تو مجھ ہی سے ڈرو، ف۱۰۶)'),
(1953, 16, 52, 'اور اسی کا ہے جو کچھ آسمانوں اور زمین میں ہے اور اسی کی فرمانبرداری لازم ہے، تو اللہ کے سوا کسی دوسرے سے ڈرو گے،'),
(1954, 16, 53, 'اور تمہارے پاس جو نعمت ہے سب اللہ کی طرف سے ہے پھر جب تمہیں تکلیف پہنچتی ہے تو اسی کی طرف پناہ لے جاتے ہو، ف۱۰۹)'),
(1955, 16, 54, 'پھر جب وہ تم سے برائی ٹال دیتا ہے تو تم میں ایک گروہ اپنے رب کا شریک ٹھہرانے لگتا ہے، ف۱۱۰)'),
(1956, 16, 55, 'کہ ہماری دی ہوئی نعمتوں کی ناشکری کریں، تو کچھ برت لو (۱۱۱) کہ عنقریب جان جاؤ گے،'),
(1957, 16, 56, 'اور انجانی چیزوں کے لیے ہماری دی ہوئی روزی میں سے حصہ مقرر کرتے ہیں خدا کی قسم تم سے ضرور سوال ہونا ہے جو کچھ جھوٹ باندھتے تھے'),
(1958, 16, 57, 'اور اللہ کے لیے بیٹیاں ٹھہراتے ہیں پاکی ہے اس کو اور اپنے لیے جو اپنا جی چاہتا ہے،'),
(1959, 16, 58, 'اور جب ان میں کسی کو بیٹی ہونے کی خوشخبری دی جاتی ہے تو دن بھر اس کا منہ کالا رہتا ہے اور وہ غصہ کھا تا ہے،'),
(1960, 16, 59, 'لوگوں سے چھپا پھرتا ہے اس بشارت کی برائی کے سبب، کیا اسے ذلت کے ساتھ رکھے گا یا اسے مٹی میں دبادے گا ارے بہت ہی برا حکم لگاتے ہیں،'),
(1961, 16, 60, 'جو آخرت پر ایمان نہیں لاتے انہیں کا بر ا حال ہے، اور اللہ کی شان سب سے بلند، اور وہی عزت و حکمت والا ہے،'),
(1962, 16, 61, 'اور اگر اللہ لوگوں کو ان کے ظلم پر گرفت کرتا تو زمین پر کوئی چلنے والا نہیں چھوڑتا لیکن انہیں ایک ٹھہرائے وعدے تک مہلت دیتا ہے پھر جب ان کا وعدہ آئے گا نہ ایک گھڑی پیچھے ہٹیں نہ آگے بڑھیں،'),
(1963, 16, 62, 'اور اللہ کے لیے وہ ٹھہراتے ہیں جو اپنے لیے ناگوار ہے اور ان کی زبانیں جھوٹ کہتی ہیں کہ ان کے لیے بھلائی ہے تو آپ ہی ہوا کہ ان کے لیے آگ ہے اور وہ حد سے گزارے ہوئے ہیں،'),
(1964, 16, 63, 'خدا کی قسم ہم نے تم سے پہلے کتنی امتوں کی طرف رسول بھیجے تو شیطان نے ان کے کوتک (برُے اعمال) ان کی آنکھوں میں بھلے کر دکھائے تو آج وہی ان کا رفیق ہے اور ان کے لیے دردناک عذاب ہے،'),
(1965, 16, 64, 'اور ہم نے تم پر یہ کتاب نہ اتاری مگر اس لیے کہ تم لوگوں پر روشن کردو جس بات میں اختلاف کریں اور ہدایت اور رحمت ایمان والوں کے لیے،'),
(1966, 16, 65, 'اور اللہ نے آسمان سے پانی اتارا تو اس سے زمین کو زندہ کردیا اس کے مرے پیچھے بیشک اس میں نشانی ہے ان کو جو کان رکھتے ہیں،'),
(1967, 16, 66, 'اور بیشک تمہارے لیے چوپایوں میں نگاہ حاصل ہونے کی جگہ ہے ہم تمہیں پلاتے ہیں اس چیز میں سے جو ان کے پیٹ میں ہے، گوبر اور خون کے بیچ میں سے خالص دودھ گلے سے سہل اترتا پینے والوں کے لیے،'),
(1968, 16, 67, 'اور کھجور اور انگور کے پھلوں میں سے کہ اس سے نبیذ بناتے ہو اور اچھا رزق بیشک اس میں نشانی ہے عقل والوں کو،'),
(1969, 16, 68, 'اور تمہارے رب نے شہد کی مکھی کو الہام کیا کہ پہاڑوں میں گھر بنا اور درختوں میں اور چھتوں میں،'),
(1970, 16, 69, 'پھر ہر قسم کے پھل میں سے کھا اور اپنے رب کی راہیں چل کر تیرے لیے نرم و آسان ہیں، اس کے پیٹ سے ایک پینے کی چیز رنگ برنگ نکلتی ہے، جس میں لوگوں کی تندرستی ہے، بیشک اس میں نشانی ہے دھیان کرنے والوں کو،'),
(1971, 16, 70, 'اور اللہ نے تمہیں پیدا کیا پھر تمہاری جان قبض کرے گا اور تم میں کوئی سب سے ناقض عمر کی طرف پھیرا جاتا ہے کہ جاننے کے بعد کچھ نہ جانے بیشک اللہ کچھ جانتا ہے سب کچھ کرسکتا ہے،'),
(1972, 16, 71, 'اور اللہ نے تم میں ایک دوسرے پر رزق میں بڑائی دی تو جنہیں بڑائی دی ہے وہ اپنا رزق اپنے باندی غلاموں کو نہ پھیر دیں گے کہ وہ سب اس میں برابر ہوجائیں تو کیا اللہ کی نعمت سے مکرتے ہیں،'),
(1973, 16, 72, 'اور اللہ نے تمہارے لیے تمہاری جنس سے عورتیں بنائیں اور تمہارے لیے تمہاری عورتوں سے بیٹے اور پوتے نواسے پیدا کیے اور تمہیں ستھری چیزوں سے روز ی دی تو کیا جھوٹی بات پر یقین لاتے ہیں اور اللہ کے فضل سے منکر ہوتے ہیں،'),
(1974, 16, 73, 'اور اللہ کے سوا ایسوں کو پوجتے ہیں جو انہیں آسمان اور زمین سے کچھ بھی روزی دینے کا اختیار نہیں رکھتے نہ کچھ کرسکتے ہیں،'),
(1975, 16, 74, 'تو اللہ کے لیے مانند نہ ٹھہراؤ بیشک اللہ جانتا ہے اور تم نہیں جانتے،'),
(1976, 16, 75, 'اللہ نے ایک کہاوت بیان فرمائی ایک بندہ ہے دوسرے کی ملک آپ کچھ مقدور نہیں رکھتا اور ایک جسے ہم نے اپنی طرف سے اچھی روزی عطا فرمائی تو وہ اس میں سے خرچ کرتا ہے چھپے اور ظاہر کیا وہ برابر ہوجائیں گے سب خوبیاں اللہ کو ہیں بلکہ ان میں اکثر کو خبر نہیں'),
(1977, 16, 76, 'اور اللہ نے کہاوت بیان فرمائی دو مرد ایک گونگا جو کچھ کام نہیں کرسکتا اور وہ اپنے آقا پر بوجھ ہے جدھر بھیجے کچھ بھلائی نہ لائے کیا برابر ہوجائے گا ہ اور وہ جو انصاف کا حکم کرتا ہے اور وہ سیدھی راہ پر ہے، ف۱۶۷)'),
(1978, 16, 77, 'اور اللہ ہی کے لیے ہیں آسمانوں اور زمین کی چھپی چیزیں اور قیامت کا معاملہ نہیں مگر جیسے ایک پلک کا مارنا بلکہ اس سے بھی قریب بیشک اللہ سب کچھ کرسکتا ہے،'),
(1979, 16, 78, 'اور اللہ نے تمہیں تمہاری ماؤوں کے پیٹ سے پیدا کیا کہ کچھ نہ جانتے تھے اور تمہیں کان اور آنکھ اور دل دیئے کہ تم احسان مانو، ف۱۷۲)'),
(1980, 16, 79, 'کیا انہوں نے پرندے نے پرندے نہ دیکھے حکم کے باندھے آسمان کی فضا میں، انہیں کوئی نہیں روکتا سوا اللہ کے بیشک اس میں نشانیاں ہیں ایمان والوں کو، ف۱۷۴)'),
(1981, 16, 80, 'اور اللہ نے تمہیں گھر دیئے بسنے کو اور تمہارے لیے چوپایوں کی کھالوں سے کچھ گھر بناےٴ جو تمھیں ہلکے پڑتے ہیں تمھارے سفر کے دن اور منزلوں پرٹھہر نے کے دن، اور ان کی اون اور ببری (رونگٹوں) اور بالوں سے کچھ گرہستی (خانگی ضروریات) کا سامان اور برتنے کی چیزیں ایک وقت تک،'),
(1982, 16, 81, 'اور اللہ نے تمہیں اپنی بنائی ہوئی چیزوں سے سائے دیئے اور تمہارے لیے پہاڑوں میں چھپنے کی جگہ بنائی اور تمہارے لیے کچھ پہنادے بنائے کہ تمہیں گرمی سے بچائیں اور کچھ پہناوے کہ لڑائیں میں تمہاری حفاظت کریں یونہی اپنی نعمت تم پر پوری کرتا ہے کہ تم فرمان مانو'),
(1983, 16, 82, 'پھر اگر وہ منہ پھیریں تو اے محبوب! تم پر نہیں مگر صاف پہنچا دینا،'),
(1984, 16, 83, 'اللہ کی نعمت پہنچانتے ہیں پھر اس سے منکر ہوتے ہیں اور ان میں اکثر کافر ہیں،'),
(1985, 16, 84, 'اور جس دن ہم اٹھائیں گے ہر امت میں سے ایک گواہ پھر کافروں کو نہ اجازت ہو نہ وہ منائے جائیں، ف۱۹۳)'),
(1986, 16, 85, 'اور ظلم کرنے والے جب عذاب دیکھیں گے اسی وقت سے نہ وہ ان پر سے ہلکا ہو نہ انہیں مہلت ملے،'),
(1987, 16, 86, 'اور شرک کرنے والے جب اپنے شریکوں کو دیکھیں گے کہیں گے اے ہمارے رب! یہ ہیں ہمارے شریک کہ ہم تیرے سوا پوجتے تھے، تو وہ ان پر بات پھینکیں گے کہ تم بیشک جھوٹے ہو،'),
(1988, 16, 87, 'اور اس دن اللہ کی طرف عاجزی سے گریں گے اور ان سے گم ہوجائیں گی جو بناوٹیں کرتے تھے، ف۱۹۹)'),
(1989, 16, 88, 'جنہوں نے کفر کیا اور اللہ کی راہ سے روکا ہم نے عذاب پر عذاب بڑھایا بدلہ ان کے فساد کا،'),
(1990, 16, 89, 'اور جس دن ہم ہر گروہ میں ایک گواہ انہیں میں سے اٹھائیں گے کہ ان پر گواہی دے اور اے محبوب! تمہیں ان سب پر شاہد بناکر لائیں گے، اور ہم نے تم پر یہ قرآن اتارا کہ ہر چیز کا روشن بیان ہے اور ہدایت اور رحمت اور بشارت مسلمانوں کو،'),
(1991, 16, 90, 'بیشک اللہ حکم فرماتا ہے انصاف اور نیکی اور رشتہ داروں کے دینے کا اور منع فرماتا بے حیائی اور برُی بات اور سرکشی سے تمہیں نصیحت فرماتا ہے کہ تم دھیان کرو،'),
(1992, 16, 91, 'اور اللہ کا عہد پورا کرو جب قول باندھو اور قسمیں مضبوط کرکے نہ توڑو اور تم اللہ کو اپنے اوپر ضامن کرچکے ہو، بیشک تمہارے کام جانتا ہے،'),
(1993, 16, 92, 'اور اس عورت کی طرح نہ ہو جس نے اپنا سُوت مضبوطی کے بعد ریزہ ریزہ کرکے توڑ دیا اپنی قسمیں آپس میں ایک بے اصل بہانہ بناتے ہو کہ کہیں ایک گروہ دوسرے گروہ سے زیادہ نہ ہو اللہ تو اس سے تمہیں آزماتا ہے اور ضرور تم پر صاف ظاہر کردے گا قیامت کے دن جس بات میں جھگڑتے تھے،'),
(1994, 16, 93, 'اور اللہ چاہتا تو تم کو ایک ہی امت کرتا لیکن اللہ گمراہ کرتا ہے جسے چاہے، اور راہ دیتا ہے جسے چاہے، اور ضرور تم سے تمہارے کام پوچھے جائیں گے،'),
(1995, 16, 94, 'اور اپنی قسمیں آپس میں بے اصل بہانہ نہ بنالو کہ کہیں کوئی پاؤں جمنے کے بعد لغزش نہ کرے اور تمہیں برائی چکھنی ہو بدلہ اس کا کہ اللہ کی راہ سے روکتے تھے اور تمہیں بڑا عذاب ہو'),
(1996, 16, 95, 'اور اللہ کے عہد پر تھوڑے دام مول نہ لو بیشک وہ جو اللہ کے پاس ہے تمہارے لیے بہتر ہے اگر تم جانتے ہو،'),
(1997, 16, 96, 'جو تمہارے پاس ہے ہوچکے گا اور جو اللہ کے پاس ہے ہمیشہ رہنے والا، اور ضرور ہم صبر کرنے والوں کو ان کا وہ صلہ دیں گے جو ان کے سب سے اچھے کام کے قابل ہو،'),
(1998, 16, 97, 'جو اچھا کام کرے مرد ہو یا عورت اور ہو مسلمان تو ضرور ہم اسے اچھی زندگی جِلائیں گے اور ضرور انہیں ان کا نیگ (اجر) دیں گے جو ان کے سب سے بہتر کام کے لائق ہوں،'),
(1999, 16, 98, 'تو جب تم قرآن پڑھو تو اللہ کی پناہ مانگو شیطان مردود سے،،'),
(2000, 16, 99, 'بیشک اس کا کوئی قابو ان پر نہیں جو ایمان لائے اور اپنے رب ہی پر بھروسہ رکھتے ہیں'),
(2001, 16, 100, 'اس کا قابو تو انہیں پر ہے جو اس سے دوستی کرتے ہیں اور اسے شریک ٹھہراتے ہیں،'),
(2002, 16, 101, 'اور جب ہم ایک آیت کی جگہ دوسری آیت بدلیں اور اللہ خوب جانتا ہے جو اتارتا ہے کافر کہیں تم تو دل سے بنا لاتے ہو بلکہ ان میں اکثر کو علم نہیں،'),
(2003, 16, 102, 'تم فرماؤ اسے پاکیزگی کی روح نے اتارا تمہارے رب کی طرف سے ٹھیک ٹھیک کہ اس سے ایمان والوں کو ثابت قدم کرے اور ہدایت اور بشارت مسلمانوں کو،'),
(2004, 16, 103, 'اور بیشک ہم جانتے ہیں کہ وہ کہتے ہیں، یہ تو کوئی آدمی سکھاتا ہے، جس کی طرف ڈھالتے ہیں اس کی زبان عجمی ہے اور یہ روشن عربی زبان'),
(2005, 16, 104, 'بیشک وہ جو اللہ کی آیتوں پر ایمان نہیں لاتے اللہ انھیں راہ نهیں دیتا اور ان کے لے درد ناک عذاب ہے،'),
(2006, 16, 105, 'جھوٹ بہتان دہی باندھتے ہین جو اللہ کی آیتوں پر ایمان نہیں رکھتے اور دہی جھوٹے ہیں،'),
(2007, 16, 106, 'جو ایمان لا کر اللہ کا منکر ہو سوا اس کے مجبور کیا جا ےٴ اور اس کا دل ایمان پر جما ہوا ہو، ہاں وہ جو دل کھول کر کافر ہو ان پر اللہ کا غضب ہے اور ان کو بڑاعذاب ہے،'),
(2008, 16, 107, 'یہ اس لےٴ کہ انھوں نے دنیا کی زندگی آخرت سے پیاری جانی، اور اس لےٴ کہ اللہ (ایسے) کافروں کو راہ نہیں دیتا،'),
(2009, 16, 108, 'یہ ہیں وه جن کے دل اور کان اور آنکھو ں پر اللہ نے مہر کر دی ہے اور وہی غفلت مین پڑے ہیں،'),
(2010, 16, 109, 'آپ ہی ہوا کہ آخرت میں وہی خراب'),
(2011, 16, 110, 'پھر بیشک تمہارا رب ان کے لیے جنہوں نے اپنے گھر چھوڑے بعد اس کے کہ ستائے گئے پھر انہوں نے جہاد کیا اور صابر رہے بیشک تمہارا رب اس کے بعد ضرور بخشنے والا ہے مہربان،'),
(2012, 16, 111, 'جس دن ہر جان اپنی ہی طرف جھگڑتی آئے گی اور ہر جان کو اس کا کیا پورا بھردیا جائے گا اور ان پر ظلم نہ ہوگا'),
(2013, 16, 112, 'اور اللہ نے کہاوت بیان فرمائی ایک بستی کہ امان و اطمینان سے تھی ہر طرف سے اس کی روزی کثرت سے آتی تو وہ اللہ کی نعمتوں کی ناشکری کرنے لگی تو اللہ نے اسے یہ سزا چکھائی کہ اسے بھوک اور ڈر کا پہناوا پہنایا بدلہ ان کے کیے کا،'),
(2014, 16, 113, 'اور بیشک ن کے پاس انہیں میں سے ایک رسول تشریف لایا تو انہوں نے اسے جھٹلایا تو انہیں عذاب نے پکڑا اور وہ بے انصاف تھے،'),
(2015, 16, 114, 'تو اللہ کی دی ہوئی روزی حلال پاکیزہ کھاؤ اور اللہ کی نعمت کا شکر کرو اگر تم اسے پوجتے ہو،'),
(2016, 16, 115, 'تم پر تو یہی حرام کیا ہے مُردار اور خون اور سور کا گوشت اور وہ جس کے ذبح کرتے وقت غیر خدا کا نام پکارا گیا پھر جو لاچار ہو نہ خواہش کرتا اور نہ حد سے بڑھتا تو بیشک اللہ بخشنے والا مہربان ہے،'),
(2017, 16, 116, 'اور نہ کہو اسے جو تمہاری زبانیں جھوٹ بیان کرتی ہیں یہ حلال ہے اور یہ حرام ہے کہ اللہ پر جھوٹ باندھو بیشک جو اللہ پر جھوٹ باندھتے ہیں ان کا بھلا نہ ہوگا،'),
(2018, 16, 117, 'تھوڑا برتنا ہے اور ان کے لیے دردناک عذاب'),
(2019, 16, 118, 'اور خاص یہودیوں پر ہم نے حرام فرمائیں وہ چیزیں جو پہلے تمہیں ہم نے سنائیں اور ہم نے ان پر ظلم نہ کیا ہاں وہی اپنی جانوں پر ظلم کرتے تھے،'),
(2020, 16, 119, 'پھر بیشک تمہارا رب ان کے لیے جو نادانی سے برائی کر بیٹھیں پھر اس کے بعد توبہ کریں اور سنور جائیں بیشک تمہارا رب اس کے بعد ضرور بخشنے والا مہربان ہے،'),
(2021, 16, 120, 'بیشک ابراہیم ایک امام تھا اللہ کا فرمانبردار اور سب سے جدا اور مشرک نہ تھا،'),
(2022, 16, 121, 'اس کے احسانوں پر شکر کرنے والا، اللہ نے اسے چن لیا اور اسے سیدھی راہ دکھائی،'),
(2023, 16, 122, 'اور ہم نے اسے دنیا میں بھلائی دی اور بیشک وہ آخرت میں شایان قرب ہے،'),
(2024, 16, 123, 'پھر ہم نے تمہیں وحی بھیجی کہ دین ابراہیم کی پیروی کرو جو ہر باطل سے الگ تھا اور مشرک نہ تھا،'),
(2025, 16, 124, 'ہفتہ تو انہیں پر رکھا گیا تھا جو اس میں مختلف ہوگئے اور بیشک تمہارا رب قیا مت کے دن ان میں فیصلہ کردے گا جس بات میں اختلاف کرتے تھے،'),
(2026, 16, 125, 'اپنے رب کی راہ کی طرف بلاؤ پکی تدبیر اور اچھی نصیحت سے اور ان سے اس طریقہ پر بحث کرو جو سب سے بہتر ہو بیشک تمہارا رب خوب جانتا ہے جو اس کی راہ سے بہکا اور وہ خوب جانتا ہے راہ والوں کو،'),
(2027, 16, 126, 'اور اگر تم سزا دو تو ایسی ہی سزا دو جیسی تمہیں تکلیف پہونچائی تھی اور اگر تم صبر کرو تو بیشک صبر والوں کو صبر سب سے اچھا،'),
(2028, 16, 127, 'اور اے محبوب! تم صبر کرو اور تمہارا صبر اللہ ہی کی توفیق سے ہے اور ان کا غم نہ کھاؤ اور ان کے فریبوں سے دل تنگ نہ ہو، ف۲۸۹)'),
(2029, 16, 128, 'بیشک اللہ ان کے ساتھ ہے جو ڈرتے ہیں اور جو نیکیاں کرتے ہیں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاکی ہے اسے جو اپنے بندے کو، راتوں رات لے گیا مسجد حرام سے مسجد اقصیٰ تک جس کے گرداگرد ہم نے برکت رکھی کہ ہم اسے اپنی عظیم نشانیاں دکھائیں، بیشک وہ سنتا دیکھتا ہے،'),
(2031, 17, 2, 'اور ہم نے موسیٰ کو کتاب عطا فرمائی اور اسے بنی اسرائیل کے لیے ہدایت کیا کہ میرے سوا کسی کو کارسام نہ ٹھہراؤ،'),
(2032, 17, 3, 'اے ان کی اولاد! جن کو ہم نے نوح کے ساتھ سوار کیا بیشک وہ بڑا شکرا گزار بندہ تھا'),
(2033, 17, 4, 'اور ہم نے بنی اسرائیل کو کتاب میں وحی بھیجی کہ ضرور تم زمین میں دوبارہ فساد مچاؤ گے اور ضرور بڑا غرور کرو گے'),
(2034, 17, 5, 'پھر جب ان میں پہلی بار کا وعدہ آیا ہم نے تم پر اپنے بندے بھیجے سخت لڑائی والے تو وہ شہروں کے اندر تمہاری تلاش کو گھسے اور یہ ایک وعدہ تھا جسے پورا ہونا تھا،'),
(2035, 17, 6, 'پھر ہم نے ان پر اُلٹ کر تمہارا حملہ کردیا اور تم کو مالوں اور بیٹوں سے مدد دی اور تمہارا جتھا بڑھا دیا،'),
(2036, 17, 7, 'اگر تم بھلائی کرو گے اپنا بھلا کرو گے اور اگر بُرا کرو گے تو اپنا، پھر جب دوسری بار کا وعدہ آیا کہ دشمن تمہارا منہ بگاڑ دیں اور مسجد میں داخل ہوں جیسے پہلی بار داخل ہوئے تھے اور جس چیز پر قابو پائیں تباہ کرکے برباد کردیں،'),
(2037, 17, 8, 'قریب ہے کہ تمہارا رب تم پر رحم کرے اور اگر تم پھر شرارت کرو تو ہم پھر عذاب کریں گے اور ہم نے جہنم کو کافروں کا قید خانہ بنایا ہے،'),
(2038, 17, 9, 'بیشک یہ قرآن وہ راہ دکھاتا ہے جو سب سے سیدھی ہے اور خوشی سناتا ہے ایمان والوں کو جو اچھے کام کریں کہ ان کے لیے بڑا ثواب ہے'),
(2039, 17, 10, 'اور یہ کہ جو آخرت پر ایمان نہیں لاتے ہم نے ان کے لیے دردناک عذاب تیار کر رکھا ہے،'),
(2040, 17, 11, 'اور آدمی برائی کی دعا کرتا ہے جیسے بھلائی مانگتا ہے اور آدمی بڑا جلد باز ہے'),
(2041, 17, 12, 'اور ہم نے رات اور دن کو دو نشانیاں بنایا تو رات کی نشانی مٹی ہوئی رکھی اور دن کی نشانیاں دکھانے والی کہ اپنے کا فضل تلاش کرو اور برسوں کی گنتی اور حساب جانو اور ہم نے ہر چیز خوب جدا جدا ظاہر فرمادی'),
(2042, 17, 13, 'اور ہر انسان کی قسمت ہم نے اس کے گلے سے لگادی اور اس کے لیے قیامت کے دن ایک نوشتہ نکالیں گے جسے کھلا ہوا پائے گا'),
(2043, 17, 14, 'فرمایا جائے گا کہ اپنا نامہ (نامہٴ اعمال) پڑھ آج تو خود ہی اپنا حساب کرنے کو بہت ہے،'),
(2044, 17, 15, 'جو راہ پر آیا وہ اپنے ہی بھلے کو راہ پر آیا اور جو بہکا تو اپنے ہی برے کو بہکا اور کوئی بوجھ اٹھانے والی جان دوسرے کا بوجھ نہ اٹھائے گی اور ہم عذاب کرنے والے نہیں جب تک رسول نہ بھیج لیں'),
(2045, 17, 16, 'اور جب ہم کسی بستی کو ہلاک کرنا چاہتے ہیں اس کے خوشحالوں پر احکام بھیجتے ہیں پھر وہ اس میں بے حکمی کرتے ہیں تو اس پر بات پوری ہوجاتی ہے تو ہم اسے تباہ کرکے برباد کردیتے ہیں،'),
(2046, 17, 17, 'اور ہم نے کتنی ہی سنگتیں (قومیں) نوح کے بعد ہلاک کردیں اور تمہارا رب کافی ہے اپنے بندوں کے گناہوں سے خبردار دیکھنے والا'),
(2047, 17, 18, 'جو یہ جلدی والی چاہے ہم اسے اس میں جلد دے دیں جو چاہیں جسے چاہیں پھر اس کے لیے جہنم کردیں کہ اس میں جائے مذمت کیا ہوا دھکے کھاتا،'),
(2048, 17, 19, 'اور جو آخرت چاہے اور اس کی سی کوشش کرے اور ہو ایمان والا تو انہیں کی کوشش ٹھکانے لگی،'),
(2049, 17, 20, 'ہم سب کو مدد دیتے ہیں اُن کو بھی اور اُن کو بھی، تمہارے رب کی عطا سے اور تمہارے رب کی عطا پر روک نہیں،'),
(2050, 17, 21, 'دیکھو ہم نے ان میں ایک کو ایک پر کیسی بڑائی دی اور بیشک آخرت درجوں میں سب سے بڑی اور فضل میں سب سے اعلیٰ ہے،'),
(2051, 17, 22, 'اے سننے والے اللہ کے ساتھ دوسرا خدا نہ ٹھہرا کہ تُو بیٹھ رہے گا مذمت کیا جاتا بیکس'),
(2052, 17, 23, 'اور تمہارے رب نے حکم فرمایا کہ اس کے سوا کسی کو نہ پُوجو اور ماں باپ کے ساتھ اچھا سلوک کرو، اگر تیرے سامنے ان میں ایک یا دونوں بڑھاپے کو پہنچ جائیں تو ان سے ہُوں، نہ کہنا اور انہیں نہ جھڑکنا اور ان سے تعظیم کی بات کہنا'),
(2053, 17, 24, 'اور ان کے لیے عاجزی کا بازو بچھا نرم دلی سے اور عرض کر کہ اے میرے رب تو ان دونوں پر رحم کر جیسا کہ ان دنوں نے مجھے چھٹپن (بچپن) میں پالا'),
(2054, 17, 25, 'تمہارا رب خوب جانتا ہے جو تمہارے دلوں میں ہے اگر تم لائق ہوئے تو بیشک وہ توبہ کرنے والوں کو بخشنے والا ہے،'),
(2055, 17, 26, 'اور رشتہ داروں کو ان کا حق دے اور مسکین اور مسافر کو اور فضول نہ اڑا'),
(2056, 17, 27, 'بیشک اڑانے والے شیطانوں کے بھائی ہیں اور شیطان اپنے رب کا بڑا ناشکرا ہے'),
(2057, 17, 28, 'اور اگر تو ان سے منہ پھیرے اپنے رب کی رحمت کے انتظار میں جس کی تجھے امید ہے تو ان سے آسان بات کہہ'),
(2058, 17, 29, 'اور اپنا ہاتھ اپنی گردن سے بندھا ہوا نہ رکھ اور نہ پورا کھول دے کہ تو بیٹھ رہے ملامت کیا ہوا تھکا ہوا'),
(2059, 17, 30, 'بیشک تمہارا رب جسے چاہے رزق کشادہ دیتا اور کستا ہے (تنگی دیتا ہے) بیشک وہ اپنے بندوں کو خوب جانتا دیکھتا ہے،'),
(2060, 17, 31, 'اور اپنی اولاد کو قتل نہ کرو مفلسی کے ڈر سے ہم انہیں بھی رزق دیں گے اور تمہیں بھی، بیشک ان کا قتل بڑی خطا ہے،'),
(2061, 17, 32, 'اور بدکاری کے پاس نہ جاؤ بیشک وہ بے حیائی ہے، اور بہت ہی بری راہ،'),
(2062, 17, 33, 'اور کوئی جان جس کی حرمت اللہ نے رکھی ہے ناحق نہ مارو، اور جو ناحق نہ مارا جائے تو بیشک ہم نے اس کے وارث کو قابو دیا تو وہ قتل میں حد سے نہ بڑھے ضرور اس کی مدد ہونی ہے'),
(2063, 17, 34, 'اور یتیم کے مال کے پاس تو جاؤ مگر اس راہ سے جو سب سے بھلی ہے یہاں تک کہ وہ اپنی جوانی کو پہنچے اور عہد پورا کرو بیشک عہد سے سوال ہونا ہے،'),
(2064, 17, 35, 'اور ماپو تو پورا ماپو اور برابر ترازو سے تولو، یہ بہتر ہے اور اس کا انجام اچھا،'),
(2065, 17, 36, 'اور اس بات کے پیچھے نہ پڑ جس کا تجھے علم نہیں بیشک کان اور آنکھ اور دل ان سب سے سوال ہونا ہے'),
(2066, 17, 37, 'اور زمین میں اتراتا نہ چل بیشک ہر گز زمین نہ چیر ڈالے گا، اور ہرگز بلندی میں پہاڑوں کو نہ پہنچے گا'),
(2067, 17, 38, 'یہ جو کچھ گزرا ان میں کی بُری بات تیرے رب کو ناپسند ہے،'),
(2068, 17, 39, 'یہ ان وحیوں میں سے ہے جو تمہارے رب نے تمہاری طرف بھیجی حکمت کی باتیں اور اے سننے والے اللہ ساتھ دوسرا خدا نہ ٹھہرا کہ تو جہنم میں پھینکا جائے گا طعنہ پاتا دھکے کھاتا،'),
(2069, 17, 40, 'کیا تمہارے رب نے تم کو بیٹے چن دیے اور اپنے لیے فرشتوں سے بیٹیاں بنائیں بیشک تم بڑا بول بولتے ہو'),
(2070, 17, 41, 'اور بیشک ہم نے اس قرآن میں طرح طرح سے بیان فرمایا کہ وہ سمجھیں اور اس سے انھیں نہیں بڑھتی مگر نفرت'),
(2071, 17, 42, 'تم فرماؤ اگر اس کے ساتھ اور خدا ہوتے جیسا یہ بکتے ہیں جب تو وہ عرش کے مالک کی طرف کوئی راہ ڈھونڈ نکالتے'),
(2072, 17, 43, 'اسے پاکی اور برتری ان کی باتوں سے بڑی برتری،'),
(2073, 17, 44, 'اس کی پاکی بولتے ہیں ساتوں آسمان اور زمین اور جو کوئی ان میں ہیں اور کوئی چیز نہیں جو اسے سراہتی ہوتی اس کی پاکی نہ بولے ہاں تم ان کی تسبیح نہیں سمجھتے بیشک وہ حلم والا بخشنے والا ہے'),
(2074, 17, 45, 'اور اے محبوب! تم نے قرآن پڑھا ہم نے تم پر اور ان میں کہ آخرت پر ایمان ہیں لاتے ایک چھپا ہوا پردہ کردیا'),
(2075, 17, 46, 'اور ہم نے ان کے دلوں پر غلاف ڈال دیے ہیں کہ اسے نہ سمجھیں اور ان کے کانوں میں ٹینٹ (روئی) اور جب تم قرآن میں اپنے اکیلے رب کی یاد کرتے ہو وہ پیٹھ پھیر کر بھاگتے ہیں نفرت کرتے،'),
(2076, 17, 47, 'ہم خوب جانتے ہیں جس لیے وہ سنتے ہیں جب تمہاری طرف کان لگاتے ہیں اور جب آپس میں مشورہ کرتے ہیں جبکہ ظالم کہتے ہیں تم پیچھے نہیں چلے مگر ایک ایسے مرد کے جس پر جادو ہوا'),
(2077, 17, 48, 'دیکھو انہوں نے تمہیں کیسی تشبیہیں دیں تو گمراہ ہوئے کہ راہ نہیں پاسکتے،'),
(2078, 17, 49, 'اور بولے کیا جب ہم ہڈیاں اور ریزہ ریزہ ہوجائیں گے کیا سچ مچ نئے بن کر اٹھیں گے'),
(2079, 17, 50, 'تم فرماؤ کہ پتھر یا لوہا ہوجاؤ،'),
(2080, 17, 51, 'یا اور کوئی مخلوق جو تمہارے خیال میں بڑی ہو (۱۰۴) تو اب کہیں گے ہمیں کون پھر پیدا کرتے گا، تم فرماؤ وہی جس نے تمہیں پہلی بار پیدا کیا، تو اب تمہاری طرف مسخرگی سے سر ہِلا کر کہیں گے یہ کب ہے تم فرماؤ شاید نزدیک ہی ہو،'),
(2081, 17, 52, 'جس دن وہ تمہیں بُلائے گا تو تم اس کی حمد کرتے چلے آؤ گے اور سمجھو گے کہ نہ رہے (۱۰۸) تھے مگر تھوڑا،'),
(2082, 17, 53, 'اور میرے بندوں سے فرماؤ وہ بات کہیں جو سب سے اچھی ہو بیشک شیطان ان کے آپس میں فساد ڈالتا ہے، بیشک شیطان آدمی کا کھلا دشمن ہے،'),
(2083, 17, 54, 'تمہارا رب تمہیں خوب جانتا ہے، وہ چاہے تو تم پر رحم کرے چاہے تو تمہیں عذاب کرے، اور ہم نے تم کو ان پر کڑوڑا (حاکمِ اعلیٰ) بناکر نہ بھیجا'),
(2084, 17, 55, 'اور تمہارا رب خوب جانتا ہے جو کوئی آسمانوں اور زمین میں ہیں اور بیشک ہم نے نبیوں میں ایک کو ایک پر بڑائی دی اور داؤد کو زبور عطا فرمائی'),
(2085, 17, 56, 'تم فرماؤ پکارو انہیں جن کو اللہ کے سوا گمان کرتے ہو تو وہ اختیار نہیں رکھتے تم سے تکلیف دو کرنے اور نہ پھیر دینے کا'),
(2086, 17, 57, 'وہ مقبول بندے جنہیں یہ کافر پوجتے ہیں وہ آپ ہی اپنے رب کی طرف وسیلہ ڈھونڈتے ہیں کہ ان میں کون زیادہ مقرب ہے اس کی رحمت کی امید رکھتے اور اس کے عذاب سے ڈرتے ہیں بیشک تمہارے رب کا عذاب ڈر کی چیز ہے،'),
(2087, 17, 58, 'اور کوئی بستی نہیں مگر یہ کہ ہم اسے روزِ قیامت سے پہلے نیست کردیں گے یا اسے سخت عذاب دیں گے یہ کتاب میں لکھا ہوا ہے،'),
(2088, 17, 59, 'اور ہم ایسی نشانیاں بھیجنے سے یوں ہی باز رہے کہ انہیں اگلوں نے جھٹلایا اور ہم نے ثمود کو ناقہ دیا آنکھیں کھولنے کو تو انہوں نے اس پر ظلم کیا اور ہم ایسی نشانیاں نہیں بھیجتے مگر ڈرانے کو'),
(2089, 17, 60, 'اور جب ہم نے تم سے فرمایا کہ سب لوگ تمہارے رب کے قابو میں ہیں اور ہم نے نہ کیا وہ دکھاوا جو تمہیں دکھایا تھا مگر لوگوں کی آزمائش کو اور وہ پیڑ جس پر قرآن میں لعنت ہے اور ہم انہیں ڈراتے ہیں تو انھیں نہیں بڑھتی مگر بڑی سرکشی،'),
(2090, 17, 61, 'اور یاد کرو جب ہم نے فرشتوں کو حکم دیا کہ آدم کو سجدہ کرو تو ان سب نے سجدہ کیا سوا ابلیس کے، بولا کیا میں اسے سجدہ کروں جسے تو نے مٹی سے بنایا'),
(2091, 17, 62, 'بولا دیکھ تو جو یہ تو نے مجھ سے معزز رکھا اگر تو نے مجھے قیامت تک مہلت دی تو ضرور میں اس کی اولاد کو پیس ڈالوں گا مگر تھوڑا'),
(2092, 17, 63, 'فرمایا، دور ہو تو ان میں جو تیری پیروی کرے گا تو بیشک سب کا بدلہ جہنم ہے بھرپور سزا،'),
(2093, 17, 64, 'اور ڈگا دے (بہکادے) ان میں سے جس پر قدرت پائے اپنی آواز سے اور ان پر لام باندھ (فوج چڑھا) لا اپنے سواروں اور اپنے پیادوں کا اور ان کا ساجھی ہو مالوں اور بچو ں میں اور انہیں وعدہ دے اور شیطان انہیں وعدہ نہیں دیتا مگر فریب سے،'),
(2094, 17, 65, 'بیشک جو میرے بندے ہیں ان پر تیرا کچھ قابو نہیں، اور تیرا رب کافی ہے کام بنانے کو'),
(2095, 17, 66, 'تمہارا رب وہ ہے کہ تمہارے لیے دریا میں کشتی رواں کرتا ہے کہ تم اس کا فضل تلاش کرو، بیشک وہ تم پر مہربان ہے،'),
(2096, 17, 67, 'اور جب تمہیں دریا میں مصیبت پہنچتی ہے تو اس کے سوا جنہیں پوجتے ہیں سب گم ہوجاتے ہیں پھر جب تمہیں خشکی کی طرف نجات دیتا ہے تو منہ پھیر لیتے ہیں اور انسان بڑا ناشکرا ہے،'),
(2097, 17, 68, 'کیا تم اس سے نڈر ہوئے کہ وہ خشکی ہی کا کوئی کنارہ تمہارے ساتھ دھنسادے یا تم پر پتھراؤ بھیجے پھر اپنا کوئی حمایتی نہ پاؤ'),
(2098, 17, 69, 'یا اس سے نڈر ہوئے کہ تمہیں دوبارہ دریا میں لے جائے پھر تم پر جہاز توڑنے والی آندھی بھیجے تو تم کو تمہارے کفر کے سبب ڈبو دے پھر اپنے لیے کوئی ایسا نہ پاؤ کہ اس پر ہمارا پیچھا کرے'),
(2099, 17, 70, 'اور بیشک ہم نے اولادِ آدم کو عزت دی اور ان کی خشکی اور تری میں سوار کیا اور ان کو ستھری چیزیں روزی دیں اور ان کو اپنی بہت مخلوق سے افضل کیا'),
(2100, 17, 71, 'جس دن ہم ہر جماعت کو اس کے امام کے ساتھ بلائیں گے تو جو اپنا نامہ داہنے ہاتھ میں دیا گیا یہ لوگ اپنا نامہ پڑھیں گے اور تاگے بھر ان کا حق نہ دبایا جائے گا'),
(2101, 17, 72, 'اور جو اس زندگی میں اندھا ہو وہ آخرت میں اندھا ہے اور اوربھی زیادہ گمراہ،'),
(2102, 17, 73, 'اور وہ تو قریب تھا کہ تمہیں کچھ لغزش دیتے ہماری وحی سے جو ہم نے تم کو بھیجی کہ تم ہماری طرف کچھ اور نسبت کردو، اور ایسا ہوتا تو وہ تم کو اپنا گہرا دو ست بنالیتے'),
(2103, 17, 74, 'اور اگر ہم تمہیں ثابت قدم نہ رکھتے تو قریب تھا کہ تم ان کی طرف کچھ تھوڑا سا جھکتے'),
(2104, 17, 75, 'اور ایسا ہوتا تو ہم تم کو دُونی عمر اور دو چند موت کا مزہ دیتے پھر تم ہمارے مقابل اپنا کوئی مددگار نہ پاتے،'),
(2105, 17, 76, 'اور بیشک قریب تھا کہ وہ تمہیں اس زمین سے ڈگا دیں (کھسکادیں) کہ تمہیں اس سے باہر کردیں اور ایسا ہوتا تو وہ تمہارے پیچھے نہ ٹھہرتے مگر تھوڑا'),
(2106, 17, 77, 'دستور ان کا جو ہم نے تم سے پہلے رسول بھیجے اور تم ہمارا قانون بدلتا نہ پاؤ گے،'),
(2107, 17, 78, 'نماز قائم رکھو سورج ڈھلنے سے رات کی اندھیری تک اور صبح کا قرآن بیشک صبح کے قرآن میں فرشتے حاضر ہوتے ہیں'),
(2108, 17, 79, 'اور رات کے کچھ حصہ میں تہجد کرو یہ خاص تمہارے لیے زیادہ ہے قریب ہے کہ تمہیں تمہارا رب ایسی جگہ کھڑا کرے جہاں سب تمہاری حمد کریں'),
(2109, 17, 80, 'اور یوں عرض کرو کہ اے میرے رب مجھے سچی طرح داخل کر اور سچی طرح باہر لے جا اور مجھے اپنی طرف سے مددگار غلبہ دے'),
(2110, 17, 81, 'اور فرماؤ کہ حق آیا اور باطل مٹ گیا بیشک باطل کو مٹنا ہی تھا'),
(2111, 17, 82, 'اور ہم قرآن میں اتارتے ہیں وہ چیز (۱۷۹) جو ایمان والوں کے لیے شفا اور رحمت ہے اور اس سے ظالموں کو نقصان ہی بڑھتا ہے،'),
(2112, 17, 83, 'اور جب ہم آدمی پر احسان کرتے ہیں منہ پھیرلیتا ہے اور اپنی طرف دور ہٹ جاتا ہے اور جب اسے برائی پہنچے تو ناامید ہوجاتا ہے'),
(2113, 17, 84, 'تم فرماؤ سب اپنے کینڈے (انداز) پر کام کرتے ہیں تو تمہارا رب خوب جانتا ہے کون زیادہ راہ پر ہے،'),
(2114, 17, 85, 'اور تم سے روح کو پوچھتے ہیں ہیں، تم فرماؤ روح میرے رب کے حکم سے ایک چیز ہے اور تمہیں علم نہ ملا مگر تھوڑا'),
(2115, 17, 86, 'اور اگر ہم چاہتے تو یہ وحی جو ہم نے تمہاری طرف کی اسے لے جاتے پھر تم کوئی نہ پاتے کہ تمہارے لیے ہمارے حضور اس پر وکالت کرتا'),
(2116, 17, 87, 'مگر تمہارے رب کی رحمت بیشک تم پر اس کا بڑا فضل ہے'),
(2117, 17, 88, 'تم فرماؤ اگر آدمی اور جن سب اس بات پر متفق ہوجائیں کہ اس قرآن کی مانند لے آئیں تو اس کا مثل نہ لاسکیں گے اگرچہ ان میں ایک دوسرے کا مددگار ہو'),
(2118, 17, 89, 'اور بیشک ہم نے لوگوں کے لیے اس قرآن میں ہم قسم کی مثل طرح طرح بیان فرمائی تو اکثر آدمیوں نے نہ مانا مگر ناشکری کرنا'),
(2119, 17, 90, 'اور بولے کہ ہم تم پر ہرگز ایمان نہ لائیں گے یہاں تک کہ تم ہمارے لیے زمین سے کوئی چشمہ بہا دو'),
(2120, 17, 91, 'یا تمہارے لیے کھجوروں اور انگوروں کا کوئی باغ ہو پھر تم اس کے لیے اندر بہتی نہریں رواں کرو'),
(2121, 17, 92, 'یا تم ہم پر آسمان گرا دو جیسا تم نے کہا ہے ٹکڑے ٹکڑے یااللہ اور فرشتوں کو ضامن لے آؤ'),
(2122, 17, 93, 'یا تمہارے لیے طلائی گھر ہو یا تم آسمان پر چڑھ جاؤ اور ہم تمہارے چڑھ جانے پر بھی ہرگز ایمان نہ لائیں گے جب تک ہم پر ایک کتاب نہ اتارو جو ہم پڑھیں، تم فرماؤ پاکی ہے میرے رب کو میں کون ہوں مگر آدمی اللہ کا بھیجا ہوا'),
(2123, 17, 94, 'اور کس بات نے لوگوں کو ایمان لانے سے روکا جب ان کے پاس ہدایت آئی مگر اسی نے کہ بولے کیا اللہ نے آدمی اللہ کا بھیجا ہوا اور کس بات نے لوگوں کو ایمان لانے سے روکا جب ان کے پاس ہدایت آئی مگر اسی نے کہ بولے کیا اللہ نے آدمی کو رسول بناکر بھیجا'),
(2124, 17, 95, 'تم فرماؤ اگر زمین میں فرشتے ہوتے چین سے چلتے تو ان پر ہم رسول بھی فرشتہ اتارتے'),
(2125, 17, 96, 'تم فرماؤ اللہ بس ہے گواہ میرے تمہارے درمیان (۲۰۰) بیشک وہ اپنے بندوں کو جانتا دیکھتا ہے،'),
(2126, 17, 97, 'اور جسے اللہ راہ دے وہی راہ پر ہے اور جسے گمراہ کرے تو ان کے لیے اس کے سوا کوئی حمایت والے نہ پاؤ گے اور ہم انہیں قیامت کے دن ان کے منہ کے بل اٹھائیں گے اندھے اور گونگے اور بہرے ان کا ٹھکانا جہنم ہے جب کبھی بجھنے پر آئے گی ہم اسے اور بھڑکادیں گے،'),
(2127, 17, 98, 'یہ ان کی سزا ہے اس پر کہ انہوں نے ہماری آیتوں سے انکار کیا اور بولے کیا جب ہم ہڈیاں اور ریزہ ریزہ ہوجائیں گے تو کیا سچ مچ ہم نئے بن کر اٹھائے جائیں گے،'),
(2128, 17, 99, 'اور کیا وہ نہیں دیکھتے کہ وہ اللہ جس نے آسمان اور زمین بنائے ان لوگوں کی مثل بناسکتا ہے اور اس نے ان کے لیے ایک میعاد ٹھہرا رکھی ہے جس میں کچھ شبہ نہیں تو ظالم نہیں مانتے بے ناشکری کیے'),
(2129, 17, 100, 'تم فرماؤ اگر تم لوگ میرے رب کی رحمت کے خزانوں کے مالک ہوتے تو انہیں بھی روک رکھتے اس ڈر سے کہ خرچ نہ ہوجائیں، اور آدمی بڑا کنجوس ہے،'),
(2130, 17, 101, 'اور بیشک ہم نے موسیٰ کو نو روشن نشانیاں دیں تو بنی اسرائیل سے پوچھو جب وہ ان کے پاس آیا تو اس سے فرعون نے کہا، اے موسیٰ! میرے خیال میں تو تم پر جادو ہوا'),
(2131, 17, 102, 'کہا یقیناً تو خوب جانتا ہے کہ انہیں نہ اتارا مگر آسمانوں اور زمین کے مالک نے دل کی آنکھیں کھولنے والیاں اور میرے گمان میں تو اے فرعون! تو ضرور ہلاک ہونے والا ہے'),
(2132, 17, 103, 'تو اس نے چاہا کہ ان کو زمین سے نکال دے تو ہم نے اسے اور اس کے ساتھیوں کو سب کو ڈبو دیا'),
(2133, 17, 104, 'اور اس کے بعد ہم نے بنی اسرائیل سے فرمایا اس زمین میں بسو پھر جب آخرت کا وعدہ آئے گا ہم تم سب کو گھال میل (لپیٹ کر) لے آئیں گے'),
(2134, 17, 105, 'اور ہم نے قرآن کو حق ہی کے ساتھ اتارا اور حق وہی کے لیے اترا اور ہم نے تمہیں نہ بھیجا مگر خوشی اور ڈر سناتا،'),
(2135, 17, 106, 'اور قرآن ہم نے جدا جدا کرکے اتارا کہ تم اسے لوگوں پر ٹھہر ٹھہر کر پڑھو اور ہم نے اسے بتدریج رہ رہ کر اتارا'),
(2136, 17, 107, 'تم فرماؤ کہ تم لوگ اس پر ایمان لاؤ یا نہ لاؤ بیشک وہ جنہیں اس کے اترنے سے پہلے علم ملا اب ان پر پڑھا جاتا ہے، ٹھوڑی کے بل سجدہ میں گر پڑتے ہیں،'),
(2137, 17, 108, 'اور کہتے ہیں پاکی ہے ہمارے رب کو بیشک ہمارے اب کا وعدہ پورا ہوتا تھا'),
(2138, 17, 109, 'اور تھوڑی کے بل گرتے ہیں روتے ہوئے اور یہ قرآن ان کے دل کا جھکنا بڑھاتا ہے، (السجدة) ۴'),
(2139, 17, 110, 'تم فرماؤ اللہ کہہ کر پکارو رحمان کہہ کر، جو کہہ کر پکارو سب اسی کے اچھے نام ہیں اور اپنی نماز نہ بہت آواز سے پڑھو نہ بالکل آہستہ اور ان دنوں کے بیچ میں راستہ چاہو'),
(2140, 17, 111, 'اور یوں کہو سب خوبیاں اللہ کو جس نے اپنے لیے بچہ اختیار نہ فرمایا اور بادشاہی میں کوئی اس کا شریک نہیں اور کمزوری سے کوئی اس کا حمایتی نہیں اور اس کی بڑائی بولنے کو تکبیر کہو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سب خوبیاں اللہ کو جس نے اپنے بندے پر کتاب اتاری اور اس میں اصلاً (بالکل، ذرا بھی) کجی نہ رکھی،'),
(2142, 18, 2, 'عدل والی کتاب کہ اللہ کے سخت عذاب سے ڈرائے اور ایمان والوں کو جو نیک کام کریں بشارت دے کہ ان کے لیے اچھا ثواب ہے،'),
(2143, 18, 3, 'جس میں ہمیشہ رہیں گے،'),
(2144, 18, 4, 'اور ان کو ڈرائے جو کہتے ہیں کہ اللہ نے اپنا کوئی بچہ بنایا،'),
(2145, 18, 5, 'اس بارے میں نہ وہ کچھ علم رکھتے ہیں نہ ان کے باپ دادا کتنا بڑا بول ہے کہ ان کے منہ سے نکلتا ہے، نِرا جھوٹ کہہ رہے ہیں،'),
(2146, 18, 6, 'تو کہیں تم اپنی جان پر کھیل جاؤ گے ان کے پیچھے اگر وہ اس بات پر ایمان نہ لائیں غم سے'),
(2147, 18, 7, 'بیشک ہم نے زمین کا سنگھار کیا جو کچھ اس پر ہے کہ انہیں آزمائیں ان میں کس کے کام بہتر ہیں'),
(2148, 18, 8, 'اور بیشک جو کچھ اس پر ہے ایک دن ہم اسے پٹ پر میدان (سفید زمین) کو چھوڑیں گے'),
(2149, 18, 9, 'کیا تمہیں معلوم ہوا کہ پہاڑ کی کھوہ اور جنگل کے کنارے والے ہماری ایک عجیب نشانی تھے،'),
(2150, 18, 10, 'جب ان نوجوانوں نے غار میں پناہ لی پھر بولے اے ہمارے رب ہمیں اپنے پاس سے رحمت دے اور ہمارے کام میں ہمارے لیے راہ یابی کے سامان کر،'),
(2151, 18, 11, 'تو ہم نے اس غار میں ان کے کے کانوں پر گنتی کے کئی برس تھپکا'),
(2152, 18, 12, 'پھر ہم نے انھیں جگایا کہ دیکھیں دو گروہوں میں کون ان کے ٹھہرنے کی مدت زیادہ ٹھیک بتاتا ہے،'),
(2153, 18, 13, 'ہم ان کا ٹھیک ٹھیک حال تمہیں سنائیں، وہ کچھ جوان تھے کہ اپنے رب پر ایمان لائے اور ہم نے ان کو ہدایت بڑھائی،'),
(2154, 18, 14, 'اور ہم نے ان کی ڈھارس بندھائی جب کھڑے ہوکر بولے کہ ہمارا رب وہ ہے جو آسمان اور زمین کا رب ہے ہم اس کے سوا کسی معبود کو نہ پوجیں گے ایسا ہو تو ہم نے ضرور حد سے گزری ہوئی بات کہی،'),
(2155, 18, 15, 'یہ جو ہماری قوم ہے اس نے اللہ کے سوا خدا بنا رکھے ہیں، کیوں نہیں لاتے ان پر کوئی روشن سند، تو اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے'),
(2156, 18, 16, 'اور جب تم ان سے اور جو کچھ وہ اللہ سوا پوجتے ہیں سب سے الگ ہوجاؤ تو غار میں پناہ لو تمہارا رب تمہارے لیے اپنی رحمت پھیلادے گا اور تمہارے کام میں آسانی کے سامان بنادے گا،'),
(2157, 18, 17, 'اور اے محبوب! تم سورج کو دیکھو گے کہ جب نکلتا ہے تو ان کے غار سے داہنی طرف بچ جاتا ہے اور جب ڈوبتا ہے تو ان سے بائیں طرف کترا جاتا ہے حالانکہ وہ اس غار کے کھلے میدان میں میں ہیں یہ اللہ کی نشانیوں میں سے ہے جسے اللہ راہ دے تو وہی راہ پر ہے، اور جسے گمراہ کرے تو ہرگز اس کا کوئی حمایتی راہ دکھانے والا نہ پاؤ گے،'),
(2158, 18, 18, 'اور تم انھیں جاگتا سمجھو اور وہ سوتے ہیں اور ہم ان کی داہنی بائیں کروٹیں بدلتے ہیں اور ان کا کتا اپنی کلائیاں پھیلائے ہوئے ہے غار کی چوکھٹ پر اے سننے! والے اگر تو انہیں جھانک کر دیکھے تو ان سے پیٹھ پھیر کر بھاگے اور ان سے ہیبت میں بھر جائے'),
(2159, 18, 19, 'اور یوں ہی ہم نے ان کو جگایا کہ آپس میں ایک دوسرے سے احوال پوچھیں ان میں ایک کہنے والا بولا تم یہاں کتنی دیر رہے، کچھ بولے کہ ایک دن رہے یا دن سے کم دوسرے بولے تمہارا رب خوب جانتا ہے جتنا تم ٹھہرے تو اپنے میں ایک کو یہ چاندی لے کر شہر میں بھیجو پھر وہ غور کرے کہ وہاں کون سا کھانا زیادہ ستھرا ہے کہ تمہارے لیے اس میں سے کھانے کو لائے اور چاہیے کہ نرمی کرے اور ہرگز کسی کو تمہاری اطلاع نہ دے،'),
(2160, 18, 20, 'بیشک اگر وہ تمہیں جان لیں گے تو تمہیں پتھراؤ کریں گے یا اپنے دین میں پھیر لیں گے اور ایسا ہوا تو تمہارا کبھی بھلا نہ ہوگا،'),
(2161, 18, 21, 'اور اسی طرح ہم نے ان کی اطلاع کردی کہ لوگ جان لیں کہ اللہ کا وعدہ سچا ہے اور قیامت میں کچھ شبہ نہیں، جب وہ لوگ ان کے معاملہ میں باہم جھگڑنے لگے تو بولے ان کے غار پر کوئی عمارت بناؤ، ان کا رب انہیں خوب جانتا ہے، وہ بولے جو اس کام میں غالب رہے تھے قسم ہے کہ ہم تو ان پر مسجد بنائیں گے'),
(2162, 18, 22, 'اب کہیں گے کہ وہ تین ہیں چوتھا ان کا کتا اور کچھ کہیں گے پانچ ہیں، چھٹا ان کا کتا بے دیکھے الاؤتکا (تیر تکا) بات اور کچھ کہیں گے سات ہیں اور آٹھواں ان کا کتا تم فرماؤ میرا رب ان کی گنتی خوب جانتا ہے انہیں نہیں جانتے مگر تھوڑے تو ان کے بارے میں بحث نہ کرو مگر اتنی ہی بحث جو ظاہر ہوچکی'),
(2163, 18, 23, 'اور ان کے بارے میں کسی کتابی سے کچھ نہ پوچھو، اور ہر گز کسی بات کو نہ کہنا میں کل یہ کردوں گا،'),
(2164, 18, 24, 'مگر یہ کہ اللہ چاہے اور اپنے رب کی یاد کر جب تو بھول جائے اور یوں کہو کہ قریب ہے میرا رب مجھے اس سے نزدیک تو راستی کی راہ دکھائے،'),
(2165, 18, 25, 'اور وہ اپنے غار میں تین سو برس ٹھہرے نو اوپر، ف۵۲)'),
(2166, 18, 26, 'تم فرماؤ اللہ خوب جانتا ہے وہ جتنا ٹھہرے اسی کے لیے ہیں آسمانوں اور زمینوں کے سب غیب، وہ کیا ہی دیکھتا اور کیا ہی سنتا ہے اس کے سوا ان کا کوئی والی نہیں، اور وہ اپنے حکم میں کسی کو شریک نہیں کرتا،'),
(2167, 18, 27, 'اور تلاوت کرو جو تمہارے رب کی کتاب تمہیں وحی ہوئی اس کی باتوں کا کوئی بدلنے والا نہیں اور ہرگز تم اس کے سوا پناہ نہ پاؤ گے،'),
(2168, 18, 28, 'اور اپنی جان ان سے مانوس رکھو جو صبح و شام اپنے رب کو پکارتے ہیں اس کی رضا چاہتے ہیں اور تمہاری آنکھیں انہیں چھوڑ کر اور پر نہ پڑیں کیا تم دنیا کی زندگانی کا سنگھار چاہو گے، اور اس کا کہا نہ مانو جس کا دل ہم نے اپنی یاد سے غافل کردیا اور وہ اپنی خواہش کے پیچھے چلا اور اس کا کام حد سے گزر گیا،'),
(2169, 18, 29, 'اور فرما دو کہ حق تمہارے رب کی طرف سے ہے تو جو چاہے ایمان لائے اور جو چاہے کفر کرے بیشک ہم نے ظالموں کے لیے وہ آگ تیار کر رکھی ہے جس کی دیواریں انہیں گھیر لیں گی، اور اگر پانی کے لیے فریاد کریں تو ان کی فریاد رسی ہوگی اس پانی سے کہ چرخ دے (کھولتے ہوئے) دھات کی طرح ہے کہ ان کے منہ بھون دے گا کیا ہی برا پینا ہے اور دوزخ کیا ہی بری ٹھہرنے کی جگہ،'),
(2170, 18, 30, 'بیشک جو ایمان لائے اور نیک کام کیے ہم ان کے نیگ (اجر) ضائع نہیں کرتے جن کے کام اچھے ہوں،'),
(2171, 18, 31, 'ان کے لیے بسنے کے باغ ہیں ان کے نیچے ندیاں بہیں وہ اس میں سونے کے کنگن بہنائے جایں گے اور سبز کپڑے کریب اور قناویز کے پہنیں گے وہاں تختوں پر تکیہ لگائے کیا ہی اچھا ثواب اور جنت کی کیا ہی اچھی آرام کی جگہ،'),
(2172, 18, 32, 'اور ان کے سامنے دو مردوں کا حال بیان کرو کہ ان میں ایک کو ہم نے انگوروں کے دو باغ دیے اور ان کو کھجوروں سے ڈھانپ لیا اور ان کے بیچ میں کھیتی رکھی'),
(2173, 18, 33, 'دونوں باغ اپنے پھل لائے اور اس میں کچھ کمی نہ دی اور دونوں کے بیچ میں ہم نے نہر بہائی'),
(2174, 18, 34, 'اور وہ پھل رکھتا تھا تو اپنے ساتھی سے بولا اور وہ اس سے رد و بدل کرتا تھا میں تجھ سے مال میں زیادہ ہوں اور آدمیوں کا زیادہ زور رکھتا ہوں'),
(2175, 18, 35, 'اپنے باغ میں گیا اور اپنی جان پر ظلم کرتا ہوا بولا مجھے گمان نہیں کہ یہ کبھی فنا ہو،'),
(2176, 18, 36, 'اور میں گمان نہیں کرتا کہ قیامت قائم ہو اور اگر میں اپنے رب کی طرف پھر گیا بھی تو ضرور اس باغ سے بہتر پلٹنے کی جگہ پاؤں گا'),
(2177, 18, 37, 'اس کے ساتھی نے اس سے اُلٹ پھیر کرتے ہوئے جواب دیا کیا تو اس کے ساتھ کفر کرتا ہے جس نے تجھے مٹی سے بنایا پھر نطفہ سے پھر تجھے ٹھیک مرد کیا'),
(2178, 18, 38, 'لیکن میں تو یہی کہتا ہوں کہ وہ اللہ ہی میرا رب ہے او ر میں کسی کو اپنے رب کا شریک نہیں کرتا ہوں،'),
(2179, 18, 39, 'اور کیوں نہ ہوا کہ جب تو اپنے باغ میں گیا تو کہا ہوتا جو چاہے اللہ، ہمیں کچھ زور نہیں مگر اللہ کی مدد کا اگر تو مجھے اپنے سے مال و اولاد میں کم دیکھتا تھا'),
(2180, 18, 40, 'تو قریب ہے کہ میرا رب مجھے تیرے باغ سے اچھا دے اور تیرے باغ پر آسمان سے بجلیاں اتارے تو وہ پٹ پر میدان (سفید زمین) ہوکر رہ جائے'),
(2181, 18, 41, 'یا اس کا پانی زمین میں دھنس جائے پھر تو اسے ہرگز تلاش نہ کرسکے'),
(2182, 18, 42, 'اور اس کے پھل گھیر لیے گئے تو اپنے ہاتھ ملتا رہ گیا اس لاگت پر جو اس باغ میں خرچ کی تھی اور وہ اپنی ٹیٹوں پر (اوندھے منہ) گرا ہوا تھا اور کہہ رہا ہے، اے کاش! میں نے اپنے رب کا کسی کو شریک نہ کیا ہوتا،'),
(2183, 18, 43, 'اور اس کے پاس کوئی جماعت نہ تھی کہ اللہ کے سامنے اس کی مدد کرتی نہ وہ بدلہ لینے کے قابل تھا'),
(2184, 18, 44, 'یہاں کھلتا ہے کہ اختیار سچے اللہ کا ہے، اس کا ثواب سب سے بہتر اور اسے ماننے کا انجام سب سے بھلا،'),
(2185, 18, 45, 'اور ان کے سامنے زندگانی دنیا کی کہاوت بیان کرو جیسے ایک پانی ہم نے آسمان اتارا تو اس کے سبب زمین کا سبزہ گھنا ہوکر نکلا کہ سوکھی گھاس ہوگیا جسے ہوائیں اڑائیں اور اللہ ہر چیز پر قابو والا ہے'),
(2186, 18, 46, 'مال اور بیٹے یہ جیتی دنیا کا سنگھار ہے اور باقی رہنے والی اچھی باتیں ان کا ثواب تمہارے رب کے یہاں بہتر اور وہ امید میں سب سے بھلی،'),
(2187, 18, 47, 'اور جس دن ہم پہاڑوں کو چلائیں گے اور تم زمین کو صاف کھلی ہوئی دیکھو گے اور ہم انہیں اٹھائیں گے تو ان میں سے کسی کو نہ چھوڑیں گے،'),
(2188, 18, 48, 'اور سب تمہارے رب کے حضور پرا باندھے پیش ہوں گے بیشک تم ہمارے پاس ویسے ہی آئےجیسا ہم نے تمہیں پہلی بار بنایا تھا بلکہ تمہارا گمان تھا کہ ہم ہر گز تمہارے لیے کوئی وعدہ کا وقت نہ رکھیں گے،'),
(2189, 18, 49, 'اور نامہٴ اعمال رکھا جائے گا تو تم مجرموں کو دیکھو گے کہ اس کے لکھے سے ڈرتے ہوں گے اور کہیں گے ہائے خرابی ہماری اس نوشتہ کو کیا ہوا نہ اس نے کوئی چھوٹا گناہ چھوڑا نہ بڑا جسے گھیر لیا ہو اور اپنا سب کیا انہوں نے سامنے پایا، اور تمہارا رب کسی پر ظلم نہیں کرتا'),
(2190, 18, 50, 'اور یاد کرو جب ہم نے فرشتوں کو فرمایا کہ آدم کو سجدہ کرو تو سب نے سجدہ کیا سوا ابلیس کے، قومِ جن سے تھا تو اپنے رب کے حکم سے نکل گیا بھلا کیا اسے اور اس کی اولاد و میرے سوا دوست بناتے ہو اور وہ ہمارے دشمن ہیں ظالموں کو کیا ہی برا بدل (بدلہ) ملا،'),
(2191, 18, 51, 'نہ میں نے آسمانوں اور زمین کو بناتے وقت انہیں سامنے بٹھالیا تھا، نہ خود ان کے بناتے وقت اور نہ میری شان، کہ گمراہ کرنے والوں کو بازوں بناؤں'),
(2192, 18, 52, 'اور جس دن فرمائے گا کہ پکارو میرے شریکوں کو جو تم گمان کرتے تھے تو انہیں پکاریں گے وہ انہیں جواب نہ دیں گے اور ہم ان کے درمیان ایک ہلاکت کا میدان کردیں گے'),
(2193, 18, 53, 'اور مجرم دوزخ کو دیکھیں گے تو یقین کریں گا کہ انہیں اس میں گرنا ہے اور اس سے پھرنے کی کوئی جگہ نہ پائیں گے،'),
(2194, 18, 54, 'اور بیشک ہم نے لوگوں کے لیے اس قرآن میں ہر قسم کی مثل طرح طرح بیان فرمائی اور آدمی ہر چیز سے بڑھ کر جھگڑالو ہے'),
(2195, 18, 55, 'اور آدمیوں کو کسی چیز نے اس سے روکا کہ ایمان لاتے جب ہدایت ان کے پاس آئی اور اپنے رب سے معافی مانگتے مگر یہ کہ ان پر اگلوں کا دستور آئے یا ان پر قسم قسم کا عذاب آئے،'),
(2196, 18, 56, 'اور ہم رسولوں کو نہیں بھیجتے مگر خوشی ڈر سنانے والے اور جو کافر ہیں وہ باطل کے ساتھ جھگڑتے ہیں کہ اس سے حق کو ہٹادیں اور انہوں نے میری آیتوں کی اور جو ڈر انہیں سناتے گئے تھے،'),
(2197, 18, 57, 'ان کی ہنسی بنالی اور اس سے بڑھ کر ظالم کون جسے اس کے رب کی آیتیں یاد دلائی جائیں تو وہ ان سے منہ پھیرلے اور اس کے ہاتھ جو آگے بھیج چکے اسے بھول جائے ہم نے ان کے دلوں پر غلاف کردیے ہیں کہ قرآن نہ سمجھیں اور ان کے کانوں میں گرانی اور اگر تم انہیں ہدایت کی طرف بلاؤ تو جب بھی ہرگز کبھی راہ نہ پائیں گے'),
(2198, 18, 58, 'اور تمہارا رب بخشنے والا مہر وا لا ہے، اگر وہ انہیں ان کے کیے پر پکڑتا تو جلد ان پر عذاب بھیجتا بلکہ ان کے لیے ایک وعدہ کا وقت ہے جس کے سامنے کوئی پناہ نہ پائیں گے،'),
(2199, 18, 59, 'اور یہ بستیاں ہم نے تباہ کردیں جب انہوں نے ظلم کیا اور ہم نے ان کی بربادی کا ایک وعدہ رکھا تھا،'),
(2200, 18, 60, 'اور یاد کرو جب موسیٰ نے اپنے خادم سے کہا میں باز نہ رہوں گا جب تک وہاں نہ پہنچوں جہاں دو سمندر ملے ہیں یا قرنوں (مدتوں تک) چلا جاؤں'),
(2201, 18, 61, 'پھر جب وہ دونوں ان دریاؤں کے ملنے کی جگہ پہنچے اپنی مچھلی بھول گئے اور اس نے سمندر میں اپنی راہ لی سرنگ بناتی،'),
(2202, 18, 62, 'پھر جبب وہاں سے گزر گئے موسیٰ نے خادم سے کہا ہمارا صبح کا کھانا لاؤ بیشک ہمیں اپنے اس سفر میں بڑی مشقت کا سامنا ہوا،'),
(2203, 18, 63, 'بولا بھلا دیکھئے تو جب ہم نے اس چٹان کے پاس جگہ لی تھی تو بیشک میں مچھلی کو بھول گیا، اور مجھے شیطان ہی نے بھلا دیا کہ میں اس کا مذکور کروں اور اس نے تو سمندر میں اپنی راہ لی، اچنبھا ہے،'),
(2204, 18, 64, 'موسیٰ نے کہا یہی تو ہم چاہتے تھے تو پیچھے پلٹے اپنے قدموں کے نشان دیکھتے،'),
(2205, 18, 65, 'تو ہمارے بندوں میں سے ایک بندہ پایا جسے ہم نے اپنے پاس سے رحمت دی اور اسے اپنا علم لدنی عطا کیا'),
(2206, 18, 66, 'اس سے موسیٰ نے کہا کیا میں تمہارے ساتھ رہوں اس شرط پر کہ تم مجھے سکھادو گے نیک بات جو تمہیں تعلیم ہوئی'),
(2207, 18, 67, 'کہا آپ میرے ساتھ ہرگز نہ ٹھہر سکیں گے'),
(2208, 18, 68, 'اور اس بات پر کیونکر صبر کریں گے جسے آپ کا علم محیط نہیں'),
(2209, 18, 69, 'کہا عنقریب اللہ چاہے تو تم مجھے صابر پاؤ گے اور میں تمہارے کسی حکم کے خلاف نہ کروں گا،'),
(2210, 18, 70, 'کہا تو اگر آپ میرے ساتھ رہنے ہیں تو مجھ سے کسی بات کو نہ پوچھنا جب تک میں خود اس کا ذکر نہ کروں'),
(2211, 18, 71, 'اب دونوں چلے یہاں تک کہ جب کشتی میں سوار ہوئے اس بندہ نے اسے چیر ڈالا موسیٰ نے کہا کیا تم نے اسے اس لیے چیرا کہ اس کے سواروں کو ڈبا دو بیشک یہ تم نے بری بات کی،'),
(2212, 18, 72, 'کہا میں نہ کہتا تھا کہ آپ میرے ساتھ ہرگز نہ ٹھہر سکیں گے'),
(2213, 18, 73, 'کہا مجھ سے میری بھول پر گرفت نہ کرو اور مجھ پر میرے کام میں مشکل نہ ڈالو،'),
(2214, 18, 74, 'پھر دونوں چلے یہاں تک کہ جب ایک لڑکا ملا اس بندہ نے اسے قتل کردیا، موسیٰ نے کہا کیا تم نے ایک ستھری جان بے کسی جان کے بدلے قتل کردی، بیشک تم نے بہت بری بات کی،'),
(2215, 18, 75, 'کہا میں نے آپ سے نہ کہا تھا کہ آپ ہرگز میرے ساتھ نہ ٹھہرسکیں گے'),
(2216, 18, 76, 'کہا اس کے بعد میں تم سے کچھ پوچھوں تو پھر میرے ساتھ نہ رہنا، بیشک میری طرف سے تمہارا عذر پورا ہوچکا،'),
(2217, 18, 77, 'پھر دونوں چلے یہاں تک کہ جب ایک گاؤں والوں کے پاس آئے ان دہقانوں سے کھانا مانگا انہوں نے انہیں دعوت دینی قبول نہ کی پھر دونوں نے اس گاؤں میں ایک دیوار پا ئی کہ گرا چاہتی ہے اس بندہ نے اسے سیدھا کردیا، موسیٰ نے کہا تم چاہتے تو اس پر کچھ مزدوری لے لیتے'),
(2218, 18, 78, 'کہا یہ میری اور آپ کی جدائی ہے اب میں آپ کو ان باتوں کا پھیر (بھید) بتاؤں گا جن پر آپ سے صبر نہ ہوسکا'),
(2219, 18, 79, 'وہ جو کشتی تھی وہ کچھ محتاجوں کی تھی کہ دریا میں کام کرتے تھے، تو میں نے چاہا کہ اسے عیب دار کردوں اور ان کے پیچھے ایک بادشاہ تھا کہ ہر ثابت کشتی زبردستی چھین لیتا'),
(2220, 18, 80, 'اور وہ جو لڑکا تھا اس کے ماں باپ مسلمان تھے تو ہمیں ڈر ہوا کہ وہ ان کو سرکشی اور کفر پر چڑھاوے'),
(2221, 18, 81, 'تو ہم نے چاہا کہ ان دونوں کا رب اس سے بہتر ستھرا اور اس سے زیادہ مہربانی میں قریب عطا کرے'),
(2222, 18, 82, 'رہی وہ دیوار وہ شہر کے دو یتیم لڑکوں کی تھی اور اس کے نیچے ان کا خزانہ تھا اور ان کا باپ نیک آدمی تھا تو آپ کے رب نے چاہا کہ وہ دونوں اپنی جوانی کو پہنچیں اور اپنا خزانہ نکالیں، آپ کے رب کی رحمت سے اور یہ کچھ میں نے اپنے حکم سے نہ کیا یہ پھیر ہے ان باتوں کا جس پر آپ سے صبر نہ ہوسکا'),
(2223, 18, 83, 'اور تم سے ذوالقرنین کو پوچھتے ہیں تم فرماؤ میں تمہیں اس کا مذکور پڑھ کر سناتا ہوں،'),
(2224, 18, 84, 'بیشک ہم نے اسے زمین میں قابو دیا اور ہر چیز کا ایک سامان عطا فرمایا'),
(2225, 18, 85, 'تو وہ ایک سامان کے پیچھے چلا'),
(2226, 18, 86, 'یہاں تک کہ جب سورج ڈوبنے کی جگہ پہنچا اسے ایک سیاہ کیچڑ کے چشمے میں ڈوبتا پایا اور وہاں ایک قوم ملی ہم نے فرمایا اے ذوالقرنین یا تو تُو انہیں عذاب دے یا ان کے ساتھ بھلائی اختیار کرے'),
(2227, 18, 87, 'عرض کی کہ وہ جس نے ظلم کیا اسے تو ہم عنقریب سزادیں گے پھر اپنے رب کی طرف پھیرا جائے گا وہ اسے بری مار دے گا،'),
(2228, 18, 88, 'اور جو ایمان لایا اور نیک کام کیا تو اس کا بدلہ بھلائی ہے اور عنقریب ہم اسے آسان کام کہیں گے'),
(2229, 18, 89, 'پھر ایک سامان کے پیچھے چلا'),
(2230, 18, 90, 'یہاں تک کہ جب سورج نکلنے کی جگہ پہنچا، اسے ایسی قوم پر نکلتا پایا جن کے لیے ہم نے سورج سے کوئی آڑ نہیں رکھی'),
(2231, 18, 91, 'بات یہی ہے، اور جو کچھ اس کے پاس تھا سب کو ہمارا علم محیط ہے'),
(2232, 18, 92, 'پھر ایک ساما ن کے پیچھے چلا'),
(2233, 18, 93, 'یہاں تک کہ جب دو پہاڑوں کے بیچ پہنچا ان سے ادھر کچھ ایسے لوگ پائے کہ کوئی بات سمجھتے معلوم نہ ہوتے تھے'),
(2234, 18, 94, 'انھوں نے کہا، اے ذوالقرنین! بیشک یاجوج ماجوج زمین میں فساد مچاتے ہیں تو کیا ہم آپ کے لیے کچھ مال مقرر کردیں اس پر کہ آپ ہم میں اور ان میں ایک دیوار بنادیں'),
(2235, 18, 95, 'کہا وہ جس پر مجھے میرے رب نے قابو دیا ہے بہتر ہے تو میری مدد طاقت سے کرو میں تم میں اور ان میں ایک مضبوط آڑ بنادوں'),
(2236, 18, 96, 'میرے پاس لوہے کے تختے لاؤ یہاں تک کہ وہ جب دیوار دونوں پہاڑوں کے کناروں سے برابر کردی، کہا دھونکو، یہاں تک کہ جب اُسے آگ کردیا کہا لاؤ، میں اس پر گلا ہوا تانبہ اُنڈیل دوں،'),
(2237, 18, 97, 'تو یاجوج و ماجوج اس پر نہ چڑھ سکے اور نہ اس میں سوراخ کرسکے،'),
(2238, 18, 98, 'کہا یہ میرے رب کی رحمت ہے، پھر جب میرے رب کا وعدہ آئے گا اسے پاش پاش کردے گا اور میرے رب کا وعدہ سچا ہے'),
(2239, 18, 99, 'اور اس دن ہم انہیں چھوڑ دیں گے کہ ان کا ایک گروہ دوسرے پر ریلا (سیلاب کی طرح) آوے گا اور صُور پھونکا جائے گا تو ہم سب کو اکٹھا کر لائیں گے'),
(2240, 18, 100, 'اور ہم اس دن جہنم کافروں کے سامنے لائیں گے'),
(2241, 18, 101, 'وہ جن کی آنکھوں پر میری یاد سے پردہ پڑا تھا اور حق بات سن نہ سکتے تھے'),
(2242, 18, 102, 'تو کیا کافر یہ سمجھتے ہیں کہ میرے بندوں کو میرے سوا حمایتی بنالیں گے بیشک ہم نے کافروں کی مہمانی کو جہنم تیار کر رکھی ہے،'),
(2243, 18, 103, 'تم فرماؤ کیا ہم تمہیں بتادیں کہ سب سے بڑھ کر ناقص عمل کن کے ہیں'),
(2244, 18, 104, 'ان کے جن کی ساری کوشش دنیا کی زندگی میں گم گئی اور وہ اس خیال میں ہیں کہ اچھا کام کررہے ہیں،'),
(2245, 18, 105, 'یہ لوگ جنہوں نے اپنے رب کی آیتیں اور اس کا ملنا نہ مانا تو ان کا کیا دھرا سب اکارت ہے تو ہم ان کے لیے قیامت کے دن کوئی تول نہ قائم کریں گے'),
(2246, 18, 106, 'یہ ان کا بدلہ ہے جہنم، اس پر کہ انہوں نے کفر کیا اور میری آیتوں اور میرے رسولوں کی ہنسی بنائی،'),
(2247, 18, 107, 'بیشک جو ایمان لائے اور اچھے کام کیے فردوس کے باغ ان کی مہمانی ہے'),
(2248, 18, 108, 'وہ ہمیشہ ان ہی میں رہیں گے ان سے جگہ بدلنا نہ چاہیں گے'),
(2249, 18, 109, 'تم فرمادو اگر سمندر میرے رب کی باتوں کے لیے، سیاہی ہو تو ضرور سمندر ختم ہوجائے گا اور میرے رب کی باتیں ختم نہ ہوں گی اگرچہ ہم ویسا ہی اور اس کی مدد کو لے آئیں'),
(2250, 18, 110, 'تو فرماؤ ظاہر صورت بشری میں تو میں تم جیسا ہوں مجھے وحی آتی ہے کہ تمہارا معبود ایک ہی معبود ہے تو جسے اپنے رب سے ملنے کی امید ہو اسے چاہیے کہ نیک کام کرے اور اپنے رب کی بندگی میں کسی کو شریک نہ کرے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کھیٰعص'),
(2252, 19, 2, 'یہ مذکور ہے تیرے رب کی اس رحمت کا جو اس نے اپنے بندہ زکریا پر کی،'),
(2253, 19, 3, 'جب اس نے اپنے رب کو آہستہ پکارا'),
(2254, 19, 4, 'عرض کی اے میرے رب میری ہڈی کمزور ہوگئی اور سرے سے بڑھاپے کا بھبھوکا پھوٹا (شعلہ چمکا) اور اے میرے رب میں تجھے پکار کر کبھی نامراد نہ رہا'),
(2255, 19, 5, 'اور مجھے اپنے بعد اپنے قرابت والوں کا ڈر ہے اور میری عورت بانجھ ہے تو مجھے اپنے پاس سے کوئی ایسا دے ڈال جو میرا کام اٹھائے'),
(2256, 19, 6, 'وہ میرا جانشین ہو اور اولاد یعقوب کا وارث ہو، اور اے میرے رب! اسے پسندیدہ کر'),
(2257, 19, 7, 'اے زکریا ہم تجھے خوشی سناتے ہیں ایک لڑکے کی جن کا نام یحییٰ ہے اس کے پہلے ہم نے اس نام کا کوئی نہ کیا،'),
(2258, 19, 8, 'عرض کی اے میرے رب! میرے لڑکا کہاں سے ہوگا میری عورت تو بانجھ ہے اور میں بڑھاپے سے سوکھ جانے کی حالت کو پہنچ گیا'),
(2259, 19, 9, 'فرمایا ایسا ہی ہے تیرے رب نے فرمایا وہ مجھے آسان ہے اور میں نے تو اس سے پہلے تجھے اس وقت بنایا جب تک کچھ بھی نہ تھا'),
(2260, 19, 10, 'عرض کی اے میرے رب! مجھے کوئی نشانی دے دے فرمایا تیری نشانی یہ ہے کہ تو تین رات دن لوگوں سے کلام نہ کرے بھلا چنگا ہوکر'),
(2261, 19, 11, 'تو اپنی قوم پر مسجد سے باہر آیا تو انہیں اشارہ سے کہا کہ صبح و شام تسبیح کرتے رہو،'),
(2262, 19, 12, 'اے یحییٰ کتاب مضبوط تھام، اور ہم نے اسے بچپن ہی میں نبوت دی'),
(2263, 19, 13, 'اور اپنی طرف سے مہربانی اور ستھرائی اور کمال ڈر والا تھا'),
(2264, 19, 14, 'اور اپنے ماں باپ سے اچھا سلوک کرنے والا تھا زبردست و نافرمان نہ تھا'),
(2265, 19, 15, 'اور سلامتی ہے اس پر جس دن پیدا ہوا اور جس دن مرے گا اورجس دن مردہ اٹھایا جائے گا'),
(2266, 19, 16, 'اور کتاب میں مریم کو یاد کرو جب اپنے گھر والوں سے پورب کی طرف ایک جگہ الگ گئی'),
(2267, 19, 17, 'تو ان سے ادھر ایک پردہ کرلیا، تو اس کی طرف ہم نے اپنا روحانی (روح الامین) بھیجا وہ اس کے سامنے ایک تندرست آدمی کے روپ میں ظاہر ہوا،'),
(2268, 19, 18, 'بولی میں تجھ سے رحمان کی پناہ مانگتی ہوں اگر تجھے خدا کا ڈر ہے،'),
(2269, 19, 19, 'بولا میں تیرے رب کا بھیجا ہوا ہوں، کہ میں تجھے ایک ستھرا بیٹا دوں،'),
(2270, 19, 20, 'بولی میرے لڑکا کہاں سے ہوگا مجھے تو کسی آدمی نے ہاتھ نہ لگایا نہ میں بدکار ہوں،'),
(2271, 19, 21, 'کہا یونہی ہے تیرے رب نے فرمایا ہے کہ یہ مجھے آسان ہے، اور اس لیے کہ ہم اسے لوگوں کے واسطے نشانی کریں اور اپنی طرف سے ایک رحمت اور یہ کام ٹھہرچکا ہے'),
(2272, 19, 22, 'اب مریم نے اسے پیٹ میں لیا پھر اسے لیے ہوئے ایک دور جگہ چلی گئی'),
(2273, 19, 23, 'پھر اسے جننے کا درد ایک کھجور کی جڑ میں لے آیا بولی ہائے کسی طرح میں اس سے پہلے مرگئی ہوتی اور بھولی بسری ہوجاتی،'),
(2274, 19, 24, 'تو اسے اس کے تلے سے پکارا کہ غم نہ کھا بیشک تیرے رب نے نیچے ایک نہر بہادی ہے'),
(2275, 19, 25, 'اور کھجور کی جڑ پکڑ کر اپنی طرف ہلا تجھ پر تازی پکی کھجوریں گریں گی'),
(2276, 19, 26, 'تو کھا اور پی اور آنکھ ٹھنڈی رکھ پھر اگر تو کسی آدمی کو دیکھے تو کہہ دینا میں نے آج رحمان کا روزہ مانا ہے تو آج ہرگز کسی آدمی سے بات نہ کرو ں گی'),
(2277, 19, 27, 'تو اسے گود میں لے اپنی قوم کے پاس آئی بولے اے مریم! بیشک تو نے بہت بری بات کی،'),
(2278, 19, 28, 'اے ہارون کی بہن تیرا باپ برا آدمی نہ تھا اور نہ تیری ماں بدکار،'),
(2279, 19, 29, 'اس پر مریم نے بچے کی طرف اشارہ کیا وہ بولے ہم کیسے بات کریں اس سے جو پالنے میں بچہ ہے'),
(2280, 19, 30, 'بچہ نے فرمایا میں اللہ کا بندہ اس نے مجھے کتاب دی اور مجھے غیب کی خبریں بتانے والا (نبی) کیا'),
(2281, 19, 31, 'اور اس نے مجھے مبارک کیا میں کہیں ہوں اور مجھے نماز و زکوٰة کی تاکید فرمائی جب تک جیوں،'),
(2282, 19, 32, 'اور اپنی ماں سے اچھا سلوک کرنے والا اور مجھے زبردست بدبخت نہ کیا،'),
(2283, 19, 33, 'اور وہی سلامتی مجھ پر جس دن میں پیدا ہوا اور جس دن مروں اور جس دن زندہ اٹھایا جاؤں'),
(2284, 19, 34, 'یہ ہے عیسیٰ مریم کا بیٹا سچی بات جس میں شک کرتے ہیں'),
(2285, 19, 35, 'اللہ کو لائق نہیں کہ کسی کو اپنا بچہ ٹھہرائے پاکی ہے اس کو جب کسی کام کا حکم فرماتا ہے تو یونہی کہ اس سے فرماتا ہے ہوجاؤ وہ فوراًٰ ہوجاتا ہے،'),
(2286, 19, 36, 'اور عیسیٰ نے کہا بیشک اللہ رب ہے میرا اور تمہارا تو اس کی بندگی کرو، یہ راہ سیدھی ہے،'),
(2287, 19, 37, 'پھر جماعتیں آپس میں مختلف ہوگئیں تو خرابی ہے، کافروں کے لیے ایک بڑے دن کی حاضری سے'),
(2288, 19, 38, 'کتنا سنیں گے اور کتنا دیکھیں گے جس دن ہمارے پاس حاضر ہونگے مگر آج ظالم کھلی گمراہی میں ہیں'),
(2289, 19, 39, 'اور انہیں ڈر سناؤ پچھتاوے کے دن کا جب کام ہوچکے گا اور وہ غفلت میں ہیں اور نہیں مانتے،'),
(2290, 19, 40, 'بیشک زمین اور جو کچھ اس پر ہے سب کے وارث ہم ہوں گے اور وہ ہماری ہی طرف پھریں گے'),
(2291, 19, 41, 'اور کتاب میں ابراہیم کو یاد کرو بیشک وہ صدیق تھا (نبی) غیب کی خبریں بتاتا،'),
(2292, 19, 42, 'جب اپنے باپ سے بولا اے میرے باپ کیوں ایسے کو پوجتا ہے جو نہ سنے نہ دیکھے اور نہ کچھ تیرے کام آئے'),
(2293, 19, 43, 'اے میرے باپ بیشک میرے پاس وہ علم آیا جو تجھے نہ آیا تو تُو میرے پیچھے چلا آ میں تجھے سیدھی راہ دکھاؤں'),
(2294, 19, 44, 'اے میرے باپ شیطان کا بندہ نہ بن بیشک شیطان رحمان کا نافرمان ہے،'),
(2295, 19, 45, 'اے میرے باپ میں ڈرتا ہوں کہ تجھے رحمن کا کوئی عذاب پہنچے تو تُو شیطان کا رفیق ہوجائے'),
(2296, 19, 46, 'بولا کیا تو میرے خداؤں سے منہ پھیرتا ہے، اے ابراہیم بیشک اگر تو باز نہ آیا تو میں تجھے پتھراؤ کروں گا اور مجھ سے زمانہ دراز تک بے علاقہ ہوجا'),
(2297, 19, 47, 'کہا بس تجھے سلام ہے قریب ہے کہ میں تیرے لیے اپنے رب سے معافی مانگوں گا بیشک وہ مجھ مہربان ہے،'),
(2298, 19, 48, 'اور میں ایک کنارے ہوجاؤں گا تم سے اور ان سب سے جن کو اللہ کے سوا پوجتے ہو اور اپنے رب کو پوجوں گا قریب ہے کہ میں اپنے رب کی بندگی سے بدبخت نہ ہوں'),
(2299, 19, 49, 'پھر جب ان سے اور اللہ کے سوا ان کے معبودوں سے کنارہ کر گیا ہم نے اسے اسحاق اور یعقوب عطا کیے، اور ہر ایک کو غیب کی خبریں بتانے والا کیا،'),
(2300, 19, 50, 'اور ہم نے انہیں اپنی رحمت عطا کی اور ان کے لیے سچی بلند ناموری رکھی'),
(2301, 19, 51, 'اور کتاب میں موسیٰ کو یاد کرو، بیشک وہ چنا ہوا تھا اور رسول تھا غیب کی خبریں بتانے والا،'),
(2302, 19, 52, 'اور اسے ہم نے طور کی داہنی جانب سے ندا فرمائی اور اسے اپنا راز کہنے کو قریب کیا'),
(2303, 19, 53, 'اور اپنی رحمت سے اس کا بھائی ہارون عطا کیا (غیب کی خبریں بتانے والا نبی)'),
(2304, 19, 54, 'اور کتاب میں اسماعیل کو یاد کرو بیشک وہ وعدے کا سچا تھا اور رسول تھا غیب کی خبریں بتاتا،'),
(2305, 19, 55, 'اور اپنے گھر والوں کو نماز اور زکوٰة کا حکم دیتا اور اپنے رب کو پسند تھا'),
(2306, 19, 56, 'اور کتاب میں ادریس کو یاد کرو بیشک وہ صدیق تھا غیب کی خبریں دیتا،'),
(2307, 19, 57, 'اور ہم نے اسے بلند مکان پر اٹھالیا'),
(2308, 19, 58, 'یہ ہیں جن پر ا لله نے احسان کیا غیب کی خبریں بتانے میں سے آدم کی اولاد سے اور ان میں جن کو ہم نے نوح کے ساتھ سوار کیا تھا اور ابراہیم اور یعقوب کی اولاد سے اور ان میں سے جنہیں ہم نے راہ دکھائی اور چن لیا جب ان پر رحمن کی آیتیں پڑھی جاتیں گر پڑتے سجدہ کرتے اور روتے (السجدة) ۵'),
(2309, 19, 59, 'تو ان کے بعد ان کی جگہ وہ ناخلف آئے جنہوں نے نمازیں گنوائیں اور اپنی خواہشوں کے پیچھے ہوئے تو عنقریب وہ دوزخ میں غی کا جنگل پائیں گے'),
(2310, 19, 60, 'مگر جو تائب ہوئے اور ایمان لائے اور اچھے کام کیے تو یہ لوگ جنت میں جائیں گے اور انہیں کچھ نقصان نہ دیا جائے گا'),
(2311, 19, 61, 'بسنے کے باغ جن کا وعدہ رحمن نے اپنے بندوں سے غیب میں کیا بیشک اس کا وعدہ آنے والا ہے،'),
(2312, 19, 62, 'وہ اس میں کوئی بیکار بات نہ سنیں گے مگر سلام اور انہیں اس میں ان کا رزق ہے صبح و شام'),
(2313, 19, 63, 'یہ وہ باغ ہے جس کا وارث ہم اپنے بندوں میں سے اسے کریں گے جو پرہیزگار ہے،'),
(2314, 19, 64, '(اور جبریل نے محبوب سے عرض کی (ٖف ۱۰۹) ہم فرشتے نہیں اترتے مگر حضور کے رب کے حکم سے اسی کا ہے جو ہمارے آگے ہے اور جو ہمارے پیچھے اور جو اس کے درمیان ہے اور حضور کا رب بھولنے والا نہیں'),
(2315, 19, 65, 'آسمانوں اور زمین اور جو کچھ ان کے بیچ میں ہے سب کا مالک تو اے پوجو اور اس کی بندگی پر ثابت رہو، کیا اس کے نام کا دوسرا جانتے ہو'),
(2316, 19, 66, 'اور آدمی کہتا ہے کیا جب میں مرجاؤں گا تو ضرور عنقریب جِلا کر نکالا جاؤں گا'),
(2317, 19, 67, 'اور کیا آدمی کو یاد نہیں کہ ہم نے اس سے پہلے اسے بنایا اور وہ کچھ نہ تھا'),
(2318, 19, 68, 'تو تمہارے رب کی قسم ہم انہیں اور شیطانوں سب کو گھیر لائیں گے اور انہیں دوزخ کے آس پاس حاضر کریں گے گھٹنوں کے بل گرے،'),
(2319, 19, 69, 'پھر ہم ہر گروہ سے نکالیں گے جو ان میں رحمن پر سب سے زیادہ بے باک ہوگا'),
(2320, 19, 70, 'پھر ہم خوب جانتے ہیں جو اس آگ میں بھوننے کے زیادہ لائق ہیں،'),
(2321, 19, 71, 'اور تم میں کوئی ایسا نہیں جس کا گزر دوزخ پر نہ ہو تمہارے رب کے ذمہ پر یہ ضرور ٹھہری ہوئی بات ہے'),
(2322, 19, 72, 'پھر ہم ڈر والوں کو بچالیں گے اور ظالموں کو اس میں چھوڑ دیں گے گھٹنوں کے بل گرے،'),
(2323, 19, 73, 'اور جب ان پر ہماری روشن آیتیں پڑھی جاتی ہیں کافر مسلمانوں سے کہتے ہیں کون سے گروہ کا مکان اچھا اورمجلس بہتر ہے'),
(2324, 19, 74, 'اور ہم نے ان سے پہلے کتنی سنگتیں کھپادیں (قومیں ہلاک کردیں) کہ وہ ان سے بھی سامان اور نمود میں بہتر تھے،'),
(2325, 19, 75, 'تم فرماؤ جو گمراہی میں ہو تو اسے رحمن خوب ڈھیل دے یہاں تک کہ جب وہ دیکھیں وہ چیز جس کا انہیں وعدہ دیا جاتا ہے یا تو عذاب یا قیامت تو اب جان لیں گے کہ کس کا برا درجہ ہے اور کس کی فوج کمزور'),
(2326, 19, 76, 'او رجنہوں نے ہدایت پائی اللہ انھیں اور ہدایت بڑھائے گا اور باقی رہنے والی نیک باتوں کا تیرے رب کے یہاں سب سے بہتر ثواب اور سب سے بھلا انجام'),
(2327, 19, 77, 'تو کیا تم نے اسے دیکھا جو ہماری آیتوں سے منکر ہوا اور کہتا ہے مجھے ضرو ر مال و اولاد ملیں گے'),
(2328, 19, 78, 'کیا غیب کو جھانک آیا ہے یا رحمن کے پاس کوئی قرار رکھا ہے،'),
(2329, 19, 79, 'ہرگز نہیں اب ہم لکھ رکھیں گے جو وہ کہتا ہے اور اسے خوب لمبا عذاب دیں گے،'),
(2330, 19, 80, 'اور جو چیزیں کہہ رہا ہے ان کے ہمیں وارث ہوں گے اور ہمارے پاس اکیلا آئے گا'),
(2331, 19, 81, 'اور اللہ کے سوا اور خدا بنالیے کہ وہ انہیں زور دیں'),
(2332, 19, 82, 'ہرگز نہیں کوئی دم جاتا ہے کہ وہ ان کی بندگی سے منکر ہوں گے اور ان کے مخالفت ہوجائیں گے'),
(2333, 19, 83, 'کیا تم نے نہ دیکھا کہ ہم نے کافروں پر شیطان بھیجے کہ وہ انہیں خوب اچھالتے ہیں'),
(2334, 19, 84, 'تو تم ان پر جلدی نہ کرو، ہم تو ان کی گنتی پوری کرتے ہیں'),
(2335, 19, 85, 'جس دن ہم پرہیزگاروں کو رحمن کی طرف لے جائیں گے مہمان بناکر'),
(2336, 19, 86, 'اور مجرموں کو جہنم کی طرف ہانکیں گے پیاسے'),
(2337, 19, 87, 'لوگ شفاعت کے مالک نہیں مگر وہی جنہوں نے رحمن کے پاس قرار رکھا ہے'),
(2338, 19, 88, 'اور کافر بولے رحمن نے اولاد اختیار کی،'),
(2339, 19, 89, 'بیشک تم حد کی بھاری بات لائے'),
(2340, 19, 90, 'قریب ہے کہ آسمان اس سے پھٹ پڑیں اور زمین شق ہوجائے اور پہاڑ گر جائیں ڈھ کر (مسمار ہوکر)'),
(2341, 19, 91, 'اس پر کہ انہوں نے رحمن کے لیے اولاد بتائی،'),
(2342, 19, 92, 'اور رحمن کے لائق نہیں کہ اولاد اختیار کرے'),
(2343, 19, 93, 'آسمانوں اور زمین میں جتنے ہیں سب اس کے حضور بندے ہو کر حاضر ہوں گے،'),
(2344, 19, 94, 'بیشک وہ ان کا شمار جانتا ہے اور ان کو ایک ایک کرکے گن رکھا ہے'),
(2345, 19, 95, 'اور ان میں ہر ایک روز قیامت اس کے حضور اکیلا حاضر ہوگا'),
(2346, 19, 96, 'بیشک وہ جو ایمان لائے اور اچھے کام کیے عنقریب ان کے لیے رحمن محبت کردے گا'),
(2347, 19, 97, 'تو ہم نے یہ قرآن تمہاری زبان میں یونہی آسان فرمایا کہ تم اس سے ڈر والوں کو خوشخبری دو اور جھگڑالو لوگوں کو اس سے ڈر سناؤ،'),
(2348, 19, 98, 'اور ہم نے ان سے پہلے کتنی سنگتیں کھپائیں (قومیں ہلاک کیں) کیا تم ان میں کسی کو دیکھتے ہو یا ان کی بھنک (ذرا بھی آواز) سنتے ہو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طٰهٰ'),
(2350, 20, 2, 'اے محبوب! ہم نے تم پر یہ قرآن اس لیے نہ اتارا کہ مشقت میں پڑو'),
(2351, 20, 3, 'ہاں اس کو نصیحت جو ڈر رکھتا ہو'),
(2352, 20, 4, 'اس کا اتارا ہوا جس نے زمین اور اونچے آسمان بنائے،'),
(2353, 20, 5, 'وہ بڑی مہر والا، اس نے عرش پر استواء فرمایا جیسا اس کی شان کے لائق ہے،'),
(2354, 20, 6, 'اس کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں اور جو کچھ ان کے بیچ میں اور جو کچھ اس گیلی مٹی کے نیچے ہے'),
(2355, 20, 7, 'اور اگر تو بات پکار کر کہے تو وہ تو بھید کو جانتا ہے اور اسے جو اس سے بھی زیادہ چھپا ہے'),
(2356, 20, 8, 'اللہ کہ اس کے سوا کسی کی بندگی نہیں، اسی کے ہیں سب اچھے نام'),
(2357, 20, 9, 'اور کچھ تمہیں موسیٰ کی خبر آئی'),
(2358, 20, 10, 'جب اس نے ایک آگ دیکھی تو اپنی بی بی سے کہا ٹھہرو مجھے ایک آگ نظر پڑی ہے شاید میں تمہارے لیے اس میں سے کوئی چنگاری لاؤں یا آ گ پر راستہ پاؤں،'),
(2359, 20, 11, 'پھر جب آگ کے پاس آیا ندا فرمائی گئی کہ اے موسیٰ،'),
(2360, 20, 12, 'بیشک میں تیرا رب ہوں تو تو اپنے جوتے اتار ڈال بیشک تو پاک جنگل طویٰ میں ہے'),
(2361, 20, 13, 'اور میں نے تجھے پسند کیا اب کان لگا کر سن جو تجھے وحی ہوتی ہے،'),
(2362, 20, 14, 'بیشک میں ہی ہوں اللہ کہ میرے سوا کوئی معبود نہیں تو میری بندگی کر اور میری یاد کے لیے نماز قائم رکھ'),
(2363, 20, 15, 'بیشک قیامت آنے والی ہے قریب تھا کہ میں اسے سب سے چھپاؤں کہ ہر جان اپنی کوشش کا بدلہ پائے'),
(2364, 20, 16, 'تو ہرگز تجھے اس کے ماننے سے وہ باز نہ رکھے جو اس پر ایمان نہیں لاتا اور اپنی خواہش کےم پیچھے چلا پھر تو ہلاک ہوجائے،'),
(2365, 20, 17, 'اور یہ تیرے داہنے ہاتھ میں کیا ہے اے موسیٰ'),
(2366, 20, 18, 'عرض کی یہ میرا عصا ہے میں اس پر تکیہ لگاتا ہوں اور اس سے اپنی بکریوں پر پتے جھاڑتا ہوں اور میرے اس میں اور کام ہیں'),
(2367, 20, 19, 'فرمایا اسے ڈال دے اے موسیٰ،'),
(2368, 20, 20, 'تو موسیٰ نے ڈال دیا تو جبھی وہ دوڑتا ہوا سانپ ہوگیا'),
(2369, 20, 21, 'فرمایا اسے اٹھالے اور ڈر نہیں، اب ہم اسے پھر پہلی طرح کردیں گے'),
(2370, 20, 22, 'اور اپنا ہاتھ اپنے بازو سے ملا خوب سپید نکلے گا بے کسی مرض کے ایک اور نشانی'),
(2371, 20, 23, 'کہ ہم تجھے اپنی بڑی بڑی نشانیاں دکھائیں،'),
(2372, 20, 24, 'فرعون کے پاس جا اس نے سر اٹھایا'),
(2373, 20, 25, 'عرض کی اے میرے رب میرے لیے میرا سینہ کھول دے'),
(2374, 20, 26, 'اور میرے لیے میرا کام آسان کر،'),
(2375, 20, 27, 'اور میری زبان کی گرہ کھول دے،'),
(2376, 20, 28, 'کہ وہ میری بات سمجھیں،'),
(2377, 20, 29, 'اور میرے لیے میرے گھر والوں میں سے ایک وزیر کردے،'),
(2378, 20, 30, 'وہ کون میرا بھائی ہارون،'),
(2379, 20, 31, 'اس سے میری کمر مضبوط کر،'),
(2380, 20, 32, 'اور اسے میرے کام میں شریک کر'),
(2381, 20, 33, 'کہ ہم بکثرت تیری پاکی بولیں،'),
(2382, 20, 34, 'اور بکثرت تیری یاد کریں'),
(2383, 20, 35, 'بیشک تو ہمیں دیکھ رہا ہے'),
(2384, 20, 36, 'فرمایا اے موسیٰ تیری مانگ تجھے عطا ہوئی،'),
(2385, 20, 37, 'اور بیشک ہم نے تجھ پر ایک بار اور احسان فرمایا'),
(2386, 20, 38, 'جب ہم نے تیری ماں کو الہام کیا جو الہام کرنا تھا'),
(2387, 20, 39, 'کہ اس بچے کو صندوق میں رکھ کر دریا میں ڈال دے، و دریا اسے کنارے پر ڈالے کہ اسے وہ اٹھالے جو میرا دشمن اور اس کا دشمن اور میں نے تجھ پر اپنی طرف کی محبت ڈالی اور اس لیے کہ تو میری نگاہ کے سامنے تیار ہو'),
(2388, 20, 40, 'تیری بہن چلی پھر کہا کیا میں تمہیں وہ لوگ بتادوں جو اس بچہ کی پرورش کریں تو ہم تجھے تیری ماں کے پاس پھیر لائے کہ اس کی آنکھ ٹھنڈی ہو اور غم نہ کرے اور تو نے ایک جان کو قتل کیا تو ہم نے تجھے غم سے نجات دی اور تجھے خوب جانچ لیا تُو تو کئی برس مدین والوں میں رہا پھر تو ایک ٹھہرائے وعدہ پر حاضر ہوا اے موسیٰ!'),
(2389, 20, 41, 'اور میں نے تجھے خاص اپنے لیے بنایا'),
(2390, 20, 42, 'تو اور تیرا بھائی دونوں میری نشانیاں لے کر جاؤ اور میری یاد میں سستی نہ کرنا،'),
(2391, 20, 43, 'دونوں فرعون کے پاس جاؤ بیشک اس نے سر اٹھایا،'),
(2392, 20, 44, 'تو اس سے نرم بات کہنا اس امید پر کہ وہ دھیان کرے یا کچھ ڈرے'),
(2393, 20, 45, 'دونوں نے عرض کیا، اے ہمارے رب! بیشک ہم ڈرتے ہیں کہ وہ ہم پر زیادتی کرے یا شرارت سے پیش آئے،'),
(2394, 20, 46, 'فرمایا ڈرو نہیں میں تمہارے ساتھ ہوں سنتا اور دیکھتا'),
(2395, 20, 47, 'تو اس کے پاس جاؤ اور اس سے کہو کہ ہم تیرے رب کے بھیجے ہوئے ہیں تو اولاد یعقوب کو ہمارے ساتھ چھوڑ دے اور انہیں تکلیف نہ دے بیشک ہم تیرے رب کی طرف سے نشانی لائے ہیں اور سلامتی اسے جو ہدایت کی پیروی کرے'),
(2396, 20, 48, 'بیشک ہماری طرف وحی ہوتی ہے کہ عذاب اس پر ہے جو جھٹلائے اور منہ پھیرے'),
(2397, 20, 49, 'بولا تو تم دونوں کا خدا کون ہے اے موسیٰ،'),
(2398, 20, 50, 'کہا ہمارا رب وہ ہے جس نے ہر چیز کو اس کے لائق صورت دی پھر راہ دکھائی'),
(2399, 20, 51, 'بولا اگلی سنگتوں (قوموں) کا کیا حال ہے'),
(2400, 20, 52, 'کہا ان کا علم میرے رب کے پاس ایک کتاب میں ہے میرا رب نہ بہکے نہ بھولے،'),
(2401, 20, 53, 'وہ جس نے تمہارے لیے زمین کو بچھونا کیا اور تمہارے لیے اس میں چلتی راہیں رکھیں اور آسمان سے پانی اتارا تو ہم نے اس سے طرح طرح کے سبزے کے جوڑے نکالے'),
(2402, 20, 54, 'تم کھاؤ اور اپنے مویشیوں کو چَراؤ بیشک اس میں نشانیاں ہیں عقل والوں کو،'),
(2403, 20, 55, 'ہم نے زمین ہی سے تمہیں بنایا اور اسی میں تمہیں پھر لے جائیں گے اور اسی سے تمہیں دوبارہ نکالیں گے'),
(2404, 20, 56, 'اور بیشک ہم نے اسے اپنی سب نشانیاں دکھائیں تو اس نے جھٹلایا اور نہ مانا'),
(2405, 20, 57, 'بولا کیا تم ہمارے پاس اس لیے آئے ہو کہ ہمیں اپنے جادو کے سبب ہماری زمین سے نکال دو اے موسیٰ'),
(2406, 20, 58, 'تو ضرور ہم بھی تمہارے آگے ویسا ہی جادو لائیں گے تو ہم میں اور اپنے میں ایک وعدہ ٹھہرادو جس سے نہ ہم بدلہ لیں نہ تم ہموار جگہ ہو،'),
(2407, 20, 59, 'موسیٰ نے کہا تمہارا وعدہ میلے کا دن ہے اور یہ کہ لوگ دن چڑھے جمع کیے جائیں'),
(2408, 20, 60, 'تو فرعون پھرا اور اپنے داؤں (فریب) اکٹھے کیے پھر آیا'),
(2409, 20, 61, 'ان سے موسیٰ نے کہا تمہیں خرابی ہو اللہ پر جھوٹ نہ باندھو کہ وہ تمہیں عذاب سے ہلاک کردے اور بیشک نامراد رہا جس نے جھوٹ باندھا'),
(2410, 20, 62, 'تو اپنے معاملہ میں باہم مختلف ہوگئے اور چھپ کر مشاورت کی،'),
(2411, 20, 63, 'بولے بیشک یہ دونوں ضرور جادوگر ہیں چاہتے ہیں کہ تمہیں تمہاری زمین زمین سے اپنے جادو کے زور سے نکال دیں اور تمہارا اچھا دین لے جائیں،'),
(2412, 20, 64, 'تو اپنا داؤ (فریب) پکا کرلو پھر پرا باندھ (صف باندھ) کر آ ؤ آج مراد کو پہنچا جو غالب رہا،'),
(2413, 20, 65, 'بولے اے موسیٰ یا تو تم ڈالو یا ہم پہلے ڈالیں'),
(2414, 20, 66, 'موسیٰ نے کہا بلکہ تمہیں ڈالو جبھی ان کی رسیاں اور لاٹھیاں ان کے جادو کے زور سے ان کے خیال میں دوڑتی معلوم ہوئیں'),
(2415, 20, 67, 'تو اپنے جی میں موسیٰ نے خوف پایا،'),
(2416, 20, 68, 'ہم نے فرمایا ڈر نہیں بیشک تو ہی غالب ہے،'),
(2417, 20, 69, 'اور ڈال تو دے جو تیرے داہنے ہاتھ میں ہے اور ان کی بناوٹوں کو نگل جائے گا، وہ جو بناکر لائے ہیں وہ تو جادوگر کا فریب ہے، اور جادوگر کا بھلا نہیں ہوتا کہیں آوے'),
(2418, 20, 70, 'تو سب جادوگر سجدے میں گرالیے گئے بولے ہم اس پر ایمان لائے جو ہارون اور موسیٰ کا رب ہے'),
(2419, 20, 71, 'فرعون بولا کیا تم اس پر ایمان لائے قبل اس کے کہ میں تمہیں اجازت دوں، بیشک وہ تمہارا بڑا ہے جس نے تم سب کو جادو سکھایا تو مجھے قسم ہے ضرور میں تمہارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کاٹوں گا اور تمہیں کھجور کے ڈھنڈ (تنے) پر سُولی چڑھاؤں گا، اور ضرور تم جان جاؤ گے کہ ہم میں کس کا عذاب سخت اور دیرپا ہے'),
(2420, 20, 72, 'بولے ہم ہرگز تجھے ترجیح نہ دیں گے ان روشن دلیلوں پر جو ہمارے پاس آئیں ہمیں اپنے پیدا کرنے والے والے کی قسم تو تُو کر چُک جو تجھے کرنا ہے تو اس دنیا ہی کی زندگی میں تو کرے گا'),
(2421, 20, 73, 'بیشک ہم اپنے رب پر ایمان لائے کہ وہ ہماری خطائیں بخش دے اور وہ جو تو نے ہمیں مجبور کیا جادو پر اور ا لله بہتر ہے اور سب سے زیادہ باقی رہنے والا'),
(2422, 20, 74, 'بیشک جو اپنے رب کے حضور مجرم ہوکر آئے تو ضرور اس کے لیے جہنم ہے جس میں نہ مرے نہ جئے'),
(2423, 20, 75, 'اور جو اس کے حضور ایمان کے ساتھ آئے کہ اچھے کام کیے ہوں تو انہیں کے درجے اونچے،'),
(2424, 20, 76, 'بسنے کے باغ جن کے نیچے نہریں بہیں ہمیشہ ان میں رہیں، اور یہ صلہ ہے اس کا جو پاک ہوا'),
(2425, 20, 77, 'اور بیشک ہم نے موسیٰ کو وحی کی کہ راتوں رات میرے بندوں کو لے چل اور ان کے لیے دریا میں سوکھا راستہ نکال دے تجھے ڈر نہ ہوگا کہ فرعون آلے اور نہ خطرہ'),
(2426, 20, 78, 'تو ان کے پیچھے فرعون پڑا اپنے لشکر لے کر تو انہیں دریا نے ڈھانپ لیا جیسا ڈھانپ لیا،'),
(2427, 20, 79, 'اور فرعون نے اپنی قوم کو گمراہ کیا اور راہ نہ دکھائی'),
(2428, 20, 80, 'اے بنی اسرائیل بیشک ہم نے تم کو تمہارے دشمن سے نجات دی اور تمہیں طور کی داہنی طرف کا وعدہ دیا اور تم پر من اور سلوی ٰ اتارا'),
(2429, 20, 81, 'کھاؤ جو پاک چیزیں ہم نے تمہیں روزی دیں اور اس میں زیادتی نہ کرو کہ تم پر میرا غضب اترے اور جس پر میرا غضب اترا بیشک وہ گرا'),
(2430, 20, 82, 'اور بیشک میں بہت بخشنے والا ہوں اسے جس نے توبہ کی اور ایمان لایا اور اچھا کام کیا پھر ہدایت پر رہا'),
(2431, 20, 83, 'اور تو نے اپنی قوم سے کیوں جلدی کی اے موسیٰ'),
(2432, 20, 84, 'عرض کی کہ وہ یہ ہیں میرے پیچھے اور اے میرے رب تیری طرف میں جلدی کرکے حاضر ہوا کہ تو راضی ہو،'),
(2433, 20, 85, 'فرمایا، تو ہم نے تیرے آنے کے بعد تیری قوم بلا میں ڈالا اور انہیں سامری نے گمراہ کردیا،'),
(2434, 20, 86, 'تو موسیٰ اپنی قوم کی طرف پلٹا غصہ میں بھرا افسوس کرتا کہا اے میری قوم کیا تم سے تمہارے رب نے اچھا وعدہ نہ تھا کیا تم پر مدت لمبی گزری یا تم نے چاہا کہ تم پر تمہارے رب کا غضب اترے تو تم نے میرا وعدہ خلاف کیا'),
(2435, 20, 87, 'بولے ہم نے آپ کا وعدہ اپنے اختیار سے خلاف نہ کیا لیکن ہم سے کچھ بوجھ اٹھوائے گئے اس قوم کے گہنے کے تو ہم نے انہیں ڈال دیا پھر اسی طرح سامری نے ڈالا'),
(2436, 20, 88, 'تو اس نے ان کے لیے ایک بچھڑا نکالا بے جان کا دھڑ گائے کی طرح بولتا یہ ہے تمہارا معبود اور موسیٰ کا معبود تو بھول گئے'),
(2437, 20, 89, 'تو کیا نہیں دیکھتے کہ وہ انہیں کسی بات کا جواب نہیں دیتا اور ان کے سوا کسی برے بھلے کا اختیار نہیں رکھتا'),
(2438, 20, 90, 'اور بیشک ان سے ہارون نے اس سے پہلے کہا تھا کہ اے میری قوم یونہی ہے کہ تم اس کے سبب فتنے میں پڑے اور بیشک تمہارا رب رحمن ہے تو میری پیروی کرو اور میرا حکم مانو،'),
(2439, 20, 91, 'بولے ہم تو اس پر آسن مارے جمے (پوجا کے لیے بیٹھے) رہیں گے جب تک ہمارے پاس موسیٰ لوٹ کے آئیں'),
(2440, 20, 92, 'موسیٰ نے کہا، اے ہارون! تمہیں کس بات نے روکا تھا جب تم نے انہیں گمراہ ہوتے دیکھا تھا کہ میرے پیچھے آتے'),
(2441, 20, 93, 'تو کیا تم نے میرا حکم نہ مانا،'),
(2442, 20, 94, 'کہا اے میرے ماں جائے! نہ میری ڈاڑھی پکڑو اور نہ میرے سر کے بال مجھے یہ ڈر ہوا کہ تم کہو گے تم نے بنی اسرائیل میں تفرقہ ڈال دیا اور تم نے میری بات کا انتظار نہ کیا'),
(2443, 20, 95, 'موسیٰ نے کہا اب تیرا کیا حال ہے اے سامری!'),
(2444, 20, 96, 'بولا میں نے وہ دیکھا جو لوگوں نے نہ دیکھا تو ایک مٹھی بھر لی فرشتے کے نشان سے پھر اسے ڈال دیا اور میرے جی کو یہی بھلا لگا'),
(2445, 20, 97, 'کہا تو چلتا بن کہ دنیا کی زندگی میں تیری سزا یہ ہے کہ تو کہے چھو نہ جا اور بیشک تیرے لیے ایک وعدہ کا وقت ہے جو تجھ سے خلاف نہ ہوگا اور اپنے اس معبود کو دیکھ جس کے سامنے تو دن بھر آسن مارے (پوجا کے لیے بیٹھا) رہا قسم ہے ہم ضرور اسے جلائیں گے پھر ریزہ ریزہ کرکے دریا میں بہائیں گے'),
(2446, 20, 98, 'تمہارا معبود تو وہی اللہ ہے جس کے سوا کسی کی بندگی نہیں ہر چیز کو اس کا علم محیط ہے،'),
(2447, 20, 99, 'ہم ایسا ہی تمہارے سامنے اگلی خبریں بیان فرماتے ہیں اور ہم نے تم کو اپنے پاس سے ایک ذکر عطا فرمایا'),
(2448, 20, 100, 'جو اس سے منہ پھیرے تو بیشک وہ قیامت کے دن ایک بوجھ اٹھائے گا'),
(2449, 20, 101, 'وہ ہمیشہ اس میں رہیں گے اور وہ قیامت کے دن ان کے حق میں کیا ہی بڑا بوجھ ہوگا،'),
(2450, 20, 102, 'جس دن صُور پھونکا جائے گا اور ہم اس دن مجرموں کو اٹھائیں گے نیلی آنکھیں'),
(2451, 20, 103, 'آپس میں چپکے چپکے کہتے ہوں گے کہ تم دنیا میں نہ رہے مگر دس رات'),
(2452, 20, 104, 'ہم خوب جانتے ہیں جو وہ کہیں گے جبکہ ان میں سب سے بہتر رائے والا کہے گا کہ تم صرف ایک ہی دن رہے تھے'),
(2453, 20, 105, 'اور تم سے پہاڑوں کو پوچھتے ہیں تم فرماؤ انہیں میرا رب ریزہ ریزہ کرکے اڑا دے گا،'),
(2454, 20, 106, 'تو زمین کو پٹ پر (چٹیل میدان) ہموار کر چھوڑے گا'),
(2455, 20, 107, 'کہ تو اس میں نیچا اونچا کچھ نہ دیکھے،'),
(2456, 20, 108, 'اس دن پکارنے والے کے پیچھے دوڑیں گے اس میں کجی نہ ہوگی اور سب آوازیں رحمن کے حضو ر پست ہوکر رہ جائیں گی تو تُو نہ سنے گا مگر بہت آہستہ آواز'),
(2457, 20, 109, 'اس دن کسی کی شفاعت کام نہ دے گی، مگر اس کی جسے رحمن نے اذن دے دیا ہے اور اس کی بات پسند فرمائی،'),
(2458, 20, 110, 'وہ جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے اور ان کا علم اسے نہیں گھیر سکتا'),
(2459, 20, 111, 'اور سب منہ جھک جائیں گے اس زندہ قائم رکھنے والے کے حضور اور بیشک نامراد رہا جس نے ظلم کا بوجھ لیا'),
(2460, 20, 112, 'اور جو کچھ نیک کام کرے اور ہو مسلمان تو اسے نہ زیادتی کا خوف ہوگا اور نہ نقصان کا'),
(2461, 20, 113, 'اور یونہی ہم نے اسے عربی قرآن اتارا اور اس میں طرح طرح سے عذاب کے وعدے دیے کہ کہیں انہیں ڈر ہو یا ان کے دل میں کچھ سوچ پیدا کرے'),
(2462, 20, 114, 'تو سب سے بلند ہے ا لله سچا بادشاہ اور قرآن میں جلدی نہ کرو جب تک اس کی وحی تمہیں پوری نہ ہولے اور عرض کرو کہ اے میرے رب! مجھے علم زیادہ دے،'),
(2463, 20, 115, 'اور بیشک ہم نے آدم کو اس سے پہلے ایک تاکیدی حکم دیا تھا تو وہ بھول گیا اور ہم نے اس کا قصد نہ پایا،'),
(2464, 20, 116, 'اور جب ہم نے فرشتوں سے فرمایا کہ آدم کو سجدہ کرو تو سب سجدہ میں گرے مگر ابلیس، اس نے نہ مانا،'),
(2465, 20, 117, 'تو ہم نے فرمایا، اے آدم! بیشک یہ تیرا اور تیری بی بی کا دشمن ہے تو ایسا نہ ہو کہ وہ تم دونوں کو جنت سے نکال دے پھر تو مشقت میں پڑے'),
(2466, 20, 118, 'بیشک تیرے لیے جنت میں یہ ہے کہ نہ تو بھوکا ہو اور نہ ننگا ہو،'),
(2467, 20, 119, 'اور یہ کہ تجھے نہ اس میں پیاس لگے نہ دھوپ'),
(2468, 20, 120, 'تو شیطان نے اسے وسوسہ دیا بولا، اے آدم! کیا میں تمہیں بتادوں ہمیشہ جینے کا پیڑ اور وہ بادشاہی کہ پرانی نہ پڑے'),
(2469, 20, 121, 'تو ان دونوں نے اس میں سے کھالیا اب ان پر ان کی شرم کی چیزیں ظاہر ہوئیں اور جنت کے پتے اپنے اوپر چپکانے لگے اور آدم سے اپنے رب کے حکم میں لغزش واقع ہوئی تو جو مطلب چاہا تھا اس کی راہ نہ پائی'),
(2470, 20, 122, 'پھر اس کے رب نے چن لیا تو اس پر اپنی رحمت سے رجوع فرمائی اور اپنے قرب خاص کی راہ دکھائی،'),
(2471, 20, 123, 'فرمایا تم دونوں مل کر جنت سے اترو تم میں ایک دوسرے کا دشمن ہے، پھر اگر تم سب کو میری طرف سے ہدایت آئے، تو جو میری ہدایت کا پیرو ہو ا وہ نہ بہکے نہ بدبخت ہو'),
(2472, 20, 124, 'اور جس نے میری یاد سے منہ پھیرا تو بیشک اس کے لیے تنگ زندگانی ہے اور ہم اسے قیامت کے دن اندھا اٹھائیں گے،'),
(2473, 20, 125, 'کہے گا اے رب میرے! مجھے تو نے کیوں اندھا اٹھایا میں تو انکھیارا (بینا) تھا'),
(2474, 20, 126, 'فرمائے گا یونہی تیرے پاس ہماری آیتیں آئیں تھیں تو نے انہیں بھلادیا اور ایسے ہی آج تیری کوئی نہ لے گا'),
(2475, 20, 127, 'اور ہم ایسا ہی بدلہ دیتے ہیں جو حد سے بڑھے اور اپنے رب کی آیتوں پر ایمان نہ لائے، اور بیشک آخرت کا عذاب سب سے سخت تر اور سب سے دیرپا ہے،'),
(2476, 20, 128, 'تو کیا انہیں اس سے راہ نہ ملی کہ ہم نے ان سے پہلے کتنی سنگتیں (قومیں) ہلاک کردیں کہ یہ ان کے بسنے کی جگہ چلتے پھرتے ہیں بیشک اس میں نشانیاں ہیں عقل والوں کو'),
(2477, 20, 129, 'اور اگر تمہارے رب کی ایک بات نہ گزر چکی ہوتی تو ضرور عذاب انھیں لپٹ جاتا اور اگر نہ ہوتا ایک وعدہ ٹھہرایا ہوا'),
(2478, 20, 130, 'تو ان کی باتوں پر صبر کرو اور اپنے رب کو سراہتے ہوئے اس کی پاکی بولو سورج چمکنے سے پہلے اور اس کے ڈوبنے سے پہلے اور رات کی گھڑیوں میں اس کی پاکی بولو اور دن کے کناروں پر اس امید پر کہ تم راضی ہو'),
(2479, 20, 131, 'اور اے سننے والے اپنی آنکھیں نہ پھیلا اس کی طرف جو ہم نے کافروں کے جوڑوں کو برتنے کے لیے دی ہے جتنی دنیا کی تازگی کہ ہم انہیں اس کے سبب فتنہ میں ڈالیں اور تیرے رب کا رزق سب سے اچھا اور سب سے دیرپا ہے،'),
(2480, 20, 132, 'اور اپنے گھر والوں کو نماز کا حکم دے اور خود اس پر ثابت رہ، کچھ ہم تجھ سے روزی نہیں مانگتے ہم تجھے روزی دیں گے اور انجام کا بھلا پرہیزگاری کے لیے،'),
(2481, 20, 133, 'اور کافر بولے یہ اپنے رب کے پاس سے کوئی نشانی کیوں نہیں لاتے اور کیا انہیں اس کا بیان نہ آیا جو اگلے صحیفوں میں ہے'),
(2482, 20, 134, 'اور اگر ہم انہیں کسی عذاب سے ہلاک کردیتے رسول کے آنے سے پہلے تو ضرور کہتے اے ہمارے رب! تو نے ہماری طرف کوئی رسول کیوں نہ بھیجا کہ ہم تیری آیتوں پر چلتے قبل اس کے کہ ذلیل و رسوا ہوتے،'),
(2483, 20, 135, 'تم فرماؤ سب راہ دیکھ رہے ہیں تو تم بھی راہ دیکھو تو اب جان جاؤ گے کہ کون ہیں سیدھی راہ والے اور کس نے ہدایت پائی،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'لوگوں کا حساب نزدیک اور وہ غفلت میں منہ پھیرے ہیں'),
(2485, 21, 2, 'جب ان کے رب کے پاس سے انہیں کوئی نئی نصیحت آتی ہے تو اسے نہیں سنتے مگر کھیلتے ہوئے،'),
(2486, 21, 3, 'ان کے دل کھیل میں پڑے ہیں اور ظالموں نے آپس میں خفیہ مشورت کی کہ یہ کون ہیں ایک تم ہی جیسے آدمی تو ہیں کیا جادو کے پاس جاتے ہو دیکھ بھال کر،'),
(2487, 21, 4, 'نبی نے فرمایا میرا رب جانتا ہے آسمانوں اور زمین میں ہر بات کو، اور وہی ہے سنتا جانتا'),
(2488, 21, 5, 'بلکہ بولے پریشان خوابیں ہیں بلکہ ان کی گڑھت (گھڑی ہوئی چیز) ہے بلکہ یہ شاعر ہیں تو ہمارے پاس کوئی نشانی لائیں جیسے اگلے بھیجے گئے تھے'),
(2489, 21, 6, 'ان سے پہلے کوئی بستی ایمان نہ لائی جسے ہم نے ہلاک کیا، تو کیا یہ ایمان لائیں گے'),
(2490, 21, 7, 'اور ہم نے تم سے پہلے نہ بھیجے مگر مرد جنہیں ہم وحی کرتے تو اے لوگو! علم والوں سے پوچھو اگر تمہیں علم نہ ہو'),
(2491, 21, 8, 'اور ہم نے انہیں خالی بدن نہ بنایا کہ کھانا نہ کھائیں اور نہ وہ دنیا میں ہمیشہ رہیں،'),
(2492, 21, 9, 'پھر ہم نے اپنا وعدہ انہیں سچا کر دکھایا تو انہیں نجات دی اور جن کو چاہی اور حد سے بڑھنے والوں کو ہلاک کردیا'),
(2493, 21, 10, 'بیشک ہم سے تمہاری طرف ایک کتاب اتاری جس میں تمہاری ناموری ہے تو کیا تمہیں عقل نہیں'),
(2494, 21, 11, 'اور کتنی ہی بستیاں ہم نے تباہ کردیں کہ وہ ستمگار تھیں اور ان کے بعد اور قوم پیدا کی،'),
(2495, 21, 12, 'تو جب انہوں نے ہمارا عذاب پایا جبھی وہ اس سے بھاگنے لگے'),
(2496, 21, 13, 'نہ بھاگو اور لوٹ کے جاؤ ان آسائشوں کی طرف جو تم کو دی گئیں تھیں اور اپنے مکانوں کی طرف شاید تم سے پوچھنا ہو'),
(2497, 21, 14, 'بولے ہائے خرابی ہماری بیشک ہم ظالم تھے'),
(2498, 21, 15, 'تو وہ یہی پکارتے رہے یہاں تک کہ ہم نے انہیں کردیا کاٹے ہوئے بجھے ہوئے،'),
(2499, 21, 16, 'اور ہم نے آسمان اور زمین اور جو کچھ ان کے درمیان ہے عبث نہ بنائے'),
(2500, 21, 17, 'اگر ہم کوئی بہلاوا اختیار کرنا چاہتے تو اپنے پاس سے اختیار کرتے اگر ہمیں کرنا ہوتا'),
(2501, 21, 18, 'بلکہ ہم حق کو باطل پر پھینک مارتے ہیں تو وہ اس کا بھیجہ نکال دیتا ہے تو جبھی وہ مٹ کر رہ جاتا ہے اور تمہاری خرابی ہے ان باتوں سے جو بناتے ہو'),
(2502, 21, 19, 'اور اسی کے ہیں جتنے آسمانوں اور زمین میں ہیں اور اس کے پاس والے اس کی عبادت سے تکبر نہیں کرتے اور نہ تھکیں،'),
(2503, 21, 20, 'رات دن اس کی پاکی بولتے ہیں اور سستی نہیں کرتے،'),
(2504, 21, 21, 'کیا انہوں نے زمین میں سے کچھ ایسے خدا بنالیے ہیں کہ وہ کچھ پیدا کرتے ہیں'),
(2505, 21, 22, 'اگر آسمان و زمین میں اللہ کے سوا اور خدا ہوتے تو ضرور وہ تباہ ہوجاتے تو پاکی ہے اللہ عرش کے مالک کو ان باتوں سے جو یہ بناتے ہیں'),
(2506, 21, 23, 'اس سے نہیں پوچھا جاتا جو وہ کرے اور ان سب سے سوال ہوگا'),
(2507, 21, 24, 'کیا اللہ کے سوا اور خدا بنا رکھے ہیں، تم فرماؤ اپنی دلیل لاؤ یہ قرآن میرے ساتھ والوں کا ذکر ہے اور مجھ سے اگلوں کا تذکرہ بلکہ ان میں اکثر حق کو نہیں جانتے تو وہ رو گرداں، ہیں'),
(2508, 21, 25, 'اور ہم نے تم سے پہلے کوئی رسول نہ بھیجا مگر یہ کہ ہم اس کی طرف وحی فرماتے کہ میرے سوا کوئی معبود نہیں تو مجھی کو پوجو،'),
(2509, 21, 26, 'اور بولے رحمن نے بیٹا اختیار کیا پاک ہے وہ بلکہ بندے ہیں عزت والے'),
(2510, 21, 27, 'بات میں اس سے سبقت نہیں کرتے اور وہ اسی کے حکم پر کاربند ہوتے ہیں،'),
(2511, 21, 28, 'وہ جانتا ہے جو ان کے آگے ہے اور جو ان کے پیچھے ہے اور شفاعت نہیں کرتے مگر اس کے لیے جسے وہ پسند فرمائے اور وہ اس کے خوف سے ڈر رہے ہیں،'),
(2512, 21, 29, 'اور ان میں جو کوئی کہے کہ میں اللہ کے سوا معبود ہوں تو اسے ہم جہنم کی جزا دیں گے، ہم ایسی ہی سزا دیتے ہیں ستمگاروں کو،'),
(2513, 21, 30, 'کیا کافروں نے یہ خیال نہ کیا کہ آسمان اور زمین بند تھے تو ہم نے انہیں کھولا اور ہم نے ہر جاندار چیز پانی سے بنائی تو کیا وہ ایمان لائیں گے،'),
(2514, 21, 31, 'اور زمین میں ہم نے لنگر ڈالے کہ انھیں لے کر نہ کانپے اور ہم نے اس میں کشادہ راہیں رکھیں کہ کہیں وہ راہ پائیں'),
(2515, 21, 32, 'اور ہم نے آسمان کو چھت بنایا نگاہ رکھی گئی اور وہ اس کی نشانیوں سے روگرداں ہیں'),
(2516, 21, 33, 'اور وہی ہے جس نے بنائے رات اور دن اور سورج اور چاند ہر ایک ایک گھیرے میں پیر رہا ہے'),
(2517, 21, 34, 'اور ہم نے تم سے پہلے کسی آدمی کے لیے دنیا میں ہمیشگی نہ بنائی تو کیا اگر تم انتقال فرماؤ تو یہ ہمیشہ رہیں گے'),
(2518, 21, 35, 'ہر جان کو موت کا مزہ چکھنا ہے، اور ہم تمہاری آزمائش کرتے ہیں برائی اور بھلائی سے جانچنے کو اور ہماری ہی طرف تمہیں لوٹ کر آنا ہے'),
(2519, 21, 36, 'اور جب کافر تمہیں دیکھتے ہیں تو تمہیں نہیں ٹھہراتے مگر ٹھٹھا کیا یہ ہیں وہ جو تمہارے خداؤں کو برا کہتے ہیں اور وہ رحمن ہی کی یاد سے منکر ہیں'),
(2520, 21, 37, 'آدمی جلد باز بنایا گیا، اب میں تمہیں اپنی نشانیاں دکھاؤں گا مجھ سے جلدی نہ کرو'),
(2521, 21, 38, 'اور کہتے ہیں کب ہوگا یہ وعدہ اگر تم سچے ہو،'),
(2522, 21, 39, 'کسی طرح جانتے کافر اس وقت کو جب نہ روک سکیں گے اپنے مونہوں سے آگے اور نہ اپنی پیٹھوں سے اور نہ ان کی مدد ہو'),
(2523, 21, 40, 'بلکہ وہ ان پر اچانک آپڑے گی تو انہیں بے حواس کردے گی پھر نہ وہ اسے پھیرسکیں گے اور نہ انہیں مہلت دی جائے گی'),
(2524, 21, 41, 'اور بیشک تم سے اگلے رسولوں کے ساتھ ٹھٹھا کیا گیا تو مسخرگی کرنے والوں کا ٹھٹھا انہیں کو لے بیٹھا'),
(2525, 21, 42, 'تم فرماؤ شبانہ روز تمہاری کون نگہبانی کرتا ہے رحمان سے بلکہ وہ اپنے رب کی یاد سے منہ پھیرے ہیں'),
(2526, 21, 43, 'کیا ان کے کچھ خدا ہیں جو ان کو ہم سے بچاتے ہیں وہ اپنی ہی جانوں کو نہیں بچاسکتے اور نہ ہماری طرف سے ان کی یاری ہو،'),
(2527, 21, 44, 'بلکہ ہم نے ان کو اور ان کے باپ دادا کو برتاوا دیا یہاں تک کہ زندگی ان پر دراز ہوئی تو کیا نہیں دیکھتے کہ ہم زمین کو اس کے کناروں سے گھٹاتے آرہے ہیں تو کیا یہ غالب ہوں گے'),
(2528, 21, 45, 'تم فرماؤ کہ میں تم کو صرف وحی سے ڈراتا ہوں اور بہرے پکارنا نہیں سنتے جب ڈرائے جائیں،'),
(2529, 21, 46, 'اور اگر انہیں تمہارے رب کے عذاب کی ہوا چھو جائے تو ضرور کہیں گے ہائے خرابی ہماری بیشک ہم ظالم تھے'),
(2530, 21, 47, 'اور ہم عدل کی ترازوئیں رکھیں گے قیامت کے دن تو کسی جان پر کچھ ظلم نہ ہوگا، اور اگر کوئی چیز رائی کے دانہ کے برابر ہو تو ہم اسے لے آئیں گے، اور ہم کافی ہیں حساب کو،'),
(2531, 21, 48, 'اور بیشک ہم نے موسیٰ اور ہارون کو فیصلہ دیا اور اجالا اور پرہیزگاروں کو نصیحت'),
(2532, 21, 49, 'وہ جو بے دیکھے اپنے رب سے ڈرتے ہیں اور انہیں قیامت کا اندیشہ لگا ہوا ہے،'),
(2533, 21, 50, 'اور یہ ہے برکت والا ذکر کہ ہم نے اتارا تو کیا تم اس کے منکر ہو،'),
(2534, 21, 51, 'اور بیشک ہم نے ابراہیم کو پہلے ہی سے اس کی نیک راہ عطا کردی اور ہم اس سے خبردار تھے،'),
(2535, 21, 52, 'جب اس نے اپنے باپ اور قوم سے کہا یہ مورتیں کیا ہیں جن کے آگے تم آسن مارے (پوجا کے لیے بیٹھے) ہو'),
(2536, 21, 53, 'بولے ہم نے اپنے دادا کو ان کی پوجا کرتے پایا'),
(2537, 21, 54, 'کہا بے شک تم اور تمہارے باپ دادا سب کھلی گمراہی میں ہو،'),
(2538, 21, 55, 'بولے کیا تم ہمارے پاس حق لائے ہو یا یونہی کھیلتے ہو'),
(2539, 21, 56, 'کہا بلکہ تمہارا رب وہ ہے جو رب ہے آسمان اور زمین کا جس نے انہیں پیدا کیا، اور میں اس پر گواہوں میں سے ہوں،'),
(2540, 21, 57, 'اور مجھے اللہ کی قسم ہے میں تمہارے بتوں کا برا چاہوں گا بعد اس کے کہ تم پھر جاؤ پیٹھ دے کر'),
(2541, 21, 58, 'تو ان سب کو چورا کردیا مگر ایک کو جو ان کا سب سے بڑا تھا کہ شاید وہ اس سے کچھ پوچھیں'),
(2542, 21, 59, 'بولے کس نے ہمارے خداؤں کے ساتھ یہ کام کیا بیشک وہ ظالم ہے،'),
(2543, 21, 60, 'ان میں کے کچھ بولے ہم نے ایک جوان کو انہیں برا کہتے سنا جسے ابراہیم کہتے ہیں'),
(2544, 21, 61, 'بولے تو اسے لوگوں کے سامنے لاؤ شاید وہ گواہی دیں'),
(2545, 21, 62, 'بولے کیا تم نے ہمارے خداؤں کے ساتھ یہ کام کیا اے ابراہیم'),
(2546, 21, 63, 'فرمایا بلکہ ان کے اس بڑے نے کیا ہوگا تو ان سے پوچھو اگر بولتے ہوں'),
(2547, 21, 64, 'تو اپنے جی کی طرف پلٹے اور بولے بیشک تمہیں ستمگار ہو'),
(2548, 21, 65, 'پھر اپنے سروں کے بل اوندھائے گئے کہ تمہیں خوب معلوم ہے یہ بولتے نہیں'),
(2549, 21, 66, 'کہا تو کیا اللہ کے سوا ایسے کو پوجتے ہو جو نہ تمہیں نفع دے اور نہ نقصان پہنچائے'),
(2550, 21, 67, 'تف ہے تم پر اور ان بتوں پر جن کو اللہ کے سوا پوجتے ہو، تو کیا تمہیں عقل نہیں'),
(2551, 21, 68, 'بولے ان کو جلادو اور اپنے خداؤں کی مدد کروں اگر تمہیں کرنا ہے'),
(2552, 21, 69, 'ہم نے فرمایا اے آگ ہو جا ٹھنڈی اور سلامتی ابراہیم پر'),
(2553, 21, 70, 'اور انہوں نے اس کا برا چاہا تو ہم نے انہیں سب سے بڑھ کر زیاں کار کردیا'),
(2554, 21, 71, 'اور ہم اسے اور لوط کو نجات بخشی اس زمین کی طرف جس میں ہم نے جہاں والوں کے لیے برکت رکھی'),
(2555, 21, 72, 'اور ہم نے اسے اسحاق عطا فرمایا اور یعقوب پوتا، اور ہم نے ان سب کو اپنے قرب خاص کا سزاوار کیا،'),
(2556, 21, 73, 'اور ہم نے انہیں امام کیا کہ ہمارے حکم سے بلاتے ہیں اور ہم نے انہیں وحی بھیجی اچھے کام کرنے اور نماز برپا رکھنے اور زکوٰة دینے کی اور وہ ہماری بندگی کرتے تھے،'),
(2557, 21, 74, 'اور لوط کو ہم نے حکومت اور علم دیا اور اسے اس بستی سے نجات بخشی جو گندے کام کرتی تھی بیشک وہ برُے لوگ بے حکم تھے،'),
(2558, 21, 75, 'اور ہم نے اسے اپنی رحمت میں داخل کیا، بیشک وہ ہمارے قرب خاص سزاواروں میں ہے،'),
(2559, 21, 76, 'اور نوح کو جب اس سے پہلے اس نے ہمیں پکارا تو ہم نے اس کی دعا قبول کی اور اسے اور اس کے گھر والوں کو بڑی سختی سے نجات دی'),
(2560, 21, 77, 'اور ہم نے ان لوگوں پر اس کو مدد دی جنہوں نے ہماری آیتیں جھٹلائیں، بیشک وہ برے لوگ تھے تو ہم نے ان سب کو ڈبو دیا،'),
(2561, 21, 78, 'اور داؤد اور سلیمان کو یاد کرو جب کھیتی کا ایک جھگڑا چکاتے تھے، جب رات کو اس میں کچھ لوگوں کی بکریاں چھوٹیں اور ہم ان کے حکم کے وقت حاضر تھے،'),
(2562, 21, 79, 'ہم نے وہ معاملہ سلیمان کو سمجھادیا اور دونوں کو حکومت اور علم عطا کیا اور داؤد کے ساتھ پہاڑ مسخر فرمادیے کہ تسبیح کرتے اور پرندے اور یہ ہمارے کام تھے،'),
(2563, 21, 80, 'اور ہم نے اسے تمہارا ایک پہناوا بنانا سکھایا کہ تمہیں تمہاری آنچ سے (زخمی ہونے سے) بچائے تو کیا تم شکر کروگے،'),
(2564, 21, 81, 'اور سلیمان کے لیے تیز ہوا مسخر کردی کہ اس کے حکم سے چلتی اس زمین کی طرف جس میں ہم نے برکت رکھی اور ہم کو ہر چیز معلوم ہے،'),
(2565, 21, 82, 'اور شیطانوں میں سے جو اس کے لیے غوطہ لگاتے اور اس کے سوا اور کام کرتے اور ہم انہیں روکے ہوئے تھے'),
(2566, 21, 83, 'اور ایوب کو (یاد کرو) جب اس نے اپنے رب کو پکارا کہ مجھے تکلیف پہنچی اور تو سب مہر والوں سے بڑھ کر مہر والا ہے،'),
(2567, 21, 84, 'تو ہم نے اس کی دعا سن لی تو ہم نے دور کردی جو تکلیف اسے تھی اور ہم نے اسے اس کے گھر والے اور ان کے ساتھ اتنے ہی اور عطا کیے اپنے پاس سے رحمت فرما کر اور بندی والوں کے لیے نصیحت'),
(2568, 21, 85, 'اور اسماعیل اور ادریس اور ذوالکفل کو (یاد کرو)، وہ سب صبر والے تھے'),
(2569, 21, 86, 'اور انہیں ہم نے اپنی رحمت میں داخل کیا، بیشک وہ ہمارے قربِ خاص کے سزاواروں میں ہیں،'),
(2570, 21, 87, 'اور ذوالنون، کو (یاد کرو) جب چلا غصہ میں بھرا تو گمان کیا کہ ہم اس پر تنگی نہ کریں گے تو اندھیریوں میں پکارا کوئی معبود نہیں سوا تیرے پاکی ہے تجھ کو، بیشک مجھ سے بے جا ہوا'),
(2571, 21, 88, 'تو ہم نے اس کی پکار سن لی اور سے غم سے نجات بخشی اور ایسی ہی نجات دیں گے مسلمانوں کو'),
(2572, 21, 89, 'اور زکریا کو جب اس نے اپنے رب کو پکارا، اے میرے رب مجھے اکیلا نہ چھوڑ اور تو سب سے بہتر اور وارث'),
(2573, 21, 90, 'تو ہم نے اس کی دعا قبول کی اور اسے یحییٰ عطا فرمایا اور اس کے لیے اس کی بی بی سنواری بیشک وہ بھلے کاموں میں جلدی کرتے تھے اور ہمیں پکارتے تھے امید اور خوف سے، اور ہمارے حضور گڑگڑاتے ہیں،'),
(2574, 21, 91, 'اور اس عورت کو اس نے اپنی پارسائی نگاہ رکھی تو ہم نے اس میں اپنی روح پھونکی اور اسے اور اس کے بیٹے کو سارے جہاں کے لیے نشانی بنایا'),
(2575, 21, 92, 'بیشک تمہارا یہ دین ایک ہی دین ہے اور میں تمہارا رب ہوں تو میری عبادت کرو،'),
(2576, 21, 93, 'اور اَوروں نے اپنے کام آپس میں ٹکڑے ٹکڑے کرلیے سب کو ہماری طرف پھرنا ہے،'),
(2577, 21, 94, 'تو جو کچھ بھلے کام کرے اور ہو ایمان والا تو اس کی کوشش کی بے قدری نہیں، اور ہم اسے لکھ رہے ہیں،'),
(2578, 21, 95, 'اور حرام ہے اس بستی پر جسے ہم نے ہلاک کردیا کہ پھر لوٹ کر آئیں'),
(2579, 21, 96, 'یہاں تک کہ جب کھولے جائیں گے یاجوج و ماجوج اور وہ ہر بلندی سے ڈھلکتے ہوں گے،'),
(2580, 21, 97, 'اور قریب آیا سچا وعدہ تو جبھی آنکھیں پھٹ کر رہ جائیں گی کافروں کی کہ ہائے ہماری خرابی بیشک ہم اس سے غفلت میں تھے بلکہ ہم ظالم تھے'),
(2581, 21, 98, 'بیشک تم اور جو کچھ اللہ کے سوا تم پوجتے ہو سب جہنم کے ایندھن ہو، تمہیں اس میں جانا،'),
(2582, 21, 99, 'اگر یہ خدا ہوتے جہنم میں نہ جاتے، اور ان سب کو ہمیشہ اس میں رہنا'),
(2583, 21, 100, 'وہ اس میں رینکیں گے اور وہ اس میں کچھ نہ سنیں گے'),
(2584, 21, 101, 'بیشک وہ جن کے لیے ہمارا وعدہ بھلائی کا ہوچکا وہ جنہم سے دور رکھے گئے ہیں'),
(2585, 21, 102, 'وہ اس کی بھنک (ہلکی سی آواز بھی) نہ سنیں گے اور وہ اپنی من مانتی خواہشوں میں ہمیشہ رہیں گے،'),
(2586, 21, 103, 'انہیں غم میں نہ ڈالے گی وہ سب سے بڑی گھبراہٹ اور فرشتے ان کی پیشوائی کو آئیں گے کہ یہ ہے تمہارا وہ دن جس کا تم سے وعدہ تھا،'),
(2587, 21, 104, 'جس دن ہم آسمان کو لپیٹیں گے جیسے سجل فرشتہ نامہٴ اعمال کو لپیٹتا ہے، جیسے پہلے اسے بنایا تھا ویسے ہی پھر کردیں گے یہ وعدہ ہے ہمارے ذمہ، ہم کو اس کا ضرور کرنا،'),
(2588, 21, 105, 'اور بیشک ہم نے زبور میں نصیحت کے بعد لکھ دیا کہ اس زمین کے وارث میرے نیک بندے ہوں گے'),
(2589, 21, 106, 'بیشک یہ قرآن کافی ہے عبادت والوں کو'),
(2590, 21, 107, 'اور ہم نے تمہیں نہ بھیجا مگر رحمت سارے جہان کے لیے'),
(2591, 21, 108, 'تم فرماؤ مجھے تو یہی وحی ہوتی ہے کہ تمہارا خدا نہیں مگر ایک اللہ تو کیا تم مسلمان ہوتے ہو،'),
(2592, 21, 109, 'پھر اگر وہ منہ پھیریں تو فرمادو میں نے تمہیں لڑائی کا اعلان کردیا، برابری پر اور میں کیا جانوں کہ پاس ہے یا دور ہے وہ جو تمہیں وعدہ دیا جاتا ہے'),
(2593, 21, 110, 'بیشک اللہ جانتا ہے آواز کی بات اور جانتا ہے جو تم چھپاتے ہو'),
(2594, 21, 111, 'اور میں کیا جانوں شاید وہ تمہاری جانچ ہو اور ایک وقت تک برتوانا'),
(2595, 21, 112, 'نبی نے عرض کی کہ اے میرے رب حق فیصلہ فرمادے اور ہمارے رب رحمنٰ ہی کی مدد درکار ہے ان باتوں پر جو تم بتاتے ہو،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اے لوگو! اپنے رب سے ڈرو بیشک قیامت کا زلزلہ بڑی سخت چیز ہے،'),
(2597, 22, 2, 'جس دن تم اسے دیکھو گے ہر دودھ پلانے والی اپنے دودھ پیتے کو بھول جائے گی اور ہر گابھ اپنا گابھ ڈال دے گی اور تو لوگوں کو دیکھے گا جیسے نشہ میں ہیں اور نشہ میں نہ ہوں گے مگر ہے یہ کہ اللہ کی مار کڑی ہے،'),
(2598, 22, 3, 'اور کچھ لوگ وہ ہیں کہ اللہ کے معاملہ میں جھگڑتے ہیں بے جانے بوجھے، اور ہر سرکش شیطان کے پیچھے ہو لیتے ہیں'),
(2599, 22, 4, 'جس پر لکھ دیا گیا ہے کہ جو اس کی دوستی کرے گا تو یہ ضرور اسے گمراہ کردے گا اور اسے عذاب دوزخ کی راہ بتائے گا'),
(2600, 22, 5, 'اے لوگو! اگر تمہیں قیامت کے دن جینے میں کچھ شک ہو تو یہ غور کرو کہ ہم نے تمہیں پیدا کیا مٹی سے پھر پانی کی بوند سے پھر خون کی پھٹک سے (ف۹۱۲ پھر گوشت کی بوٹی سے نقشہ بنی اور بے بنی تاکہ ہم تمہارے لیے اپنی نشانیاں ظاہر فرمائیں اور ہم ٹھہرائے رکھتے ہیں ماؤں کے پیٹ میں جسے چاہیں ایک مقرر میعاد تک پھر تمہیں نکالتے ہیں بچہ پھر اس لیے کہ تم اپنی جوانی کو پہنچو اور تم میں کوئی پہلے ہی مرجاتا ہے اور کوئی سب میں نکمی عمر تک ڈالا جاتا ہے کہ جاننے کے بعد کچھ نہ جانے اور تو زمین کو دیکھے مرجھائی ہوئی پھر جب ہم نے اس پر پانی اتارا تر و تازہ ہوئی اور ابھر آئی اور ہر رونق دار جوڑا اُگا لائی'),
(2601, 22, 6, 'یہ اس لیے ہے کہ اللہ ہی حق ہے اور یہ کہ وہ مردے جِلائے گا اور یہ کہ وہ سب کچھ کرسکتا ہے،'),
(2602, 22, 7, 'اور اس لیے کہ قیامت آنے والی اس میں کچھ شک نہیں، اور یہ کہ اللہ اٹھائے گا انہیں جو قبروں میں ہیں،'),
(2603, 22, 8, 'اور کوئی آدمی وہ ہے کہ اللہ کے بارے میں یوں جھگڑتا ہے کہ نہ تو علم نہ کوئی دلیل اور نہ کوئی روشن نوشتہ (تحریر)'),
(2604, 22, 9, 'حق سے اپنی گردن موڑے ہوئے تاکہ اللہ کی راہ سے بہکادے اس کے لیے دنیا میں رسوائی ہے اور قیامت کے دن ہم اسے آگ کا عذاب چکھائیں گے'),
(2605, 22, 10, 'یہ اس کا بدلہ ہے جو تیرے ہاتھوں نے آگے بھیجا اور اللہ بندوں پر ظلم نہیں کرتا'),
(2606, 22, 11, 'اور کچھ آدمی اللہ کی بندگی ایک کنارہ پر کرتے ہیں پھر اگر انہیں کوئی بھلائی پہنچ گئی جب تو چین سے ہیں اور جب کوئی جانچ آکر پڑی منہ کے بل پلٹ گئے، ف۳۲) دنیا اور آخرت دونوں کا گھاٹا یہی ہے صر یح نقصان'),
(2607, 22, 12, 'اللہ کے سوا ایسے کو پوجتے ہیں جو ان کا برا بھلا کچھ نہ کرے یہی ہے دور کی گمراہی،'),
(2608, 22, 13, 'ایسے کو پوجتے نہیں جس کے نفع سے نقصان کی توقع زیادہ ہے بیشک کیا ہی برا مولیٰ اور بیشک کیا ہی برا رفیق،'),
(2609, 22, 14, 'بیشک اللہ داخل کرے گا انہیں جو ایمان لائے اور بھلے کام کیے باغوں میں جن کے نیچے نہریں رواں، بیشک اللہ کرتا ہے جو چاہے'),
(2610, 22, 15, 'جو یہ خیال کرتا ہو کہ اللہ اپنے نبی کی مدد نہ فرمائے گا دنیا اور آخرت میں تو اسے چاہیے کہ اوپر کو ایک رسی تانے پھر اپنے آپ کو پھانسی دے لے پھر دیکھے کہ اس کا یہ داؤں کچھ لے گیا اس بات کو جس کی اسے جلن ہے'),
(2611, 22, 16, 'اور بات یہی ہے کہ ہم نے یہ قرآن اتارا روشن آیتیں اور یہ کہ اللہ راہ دیتا ہے جسے چاہے،'),
(2612, 22, 17, 'بیشک مسلمان اور یہودی اور ستارہ پرست اور نصرانی اور آتش پرست اور مشرک، بیشک اللہ ان سب میں قیامت کے دن فیصلہ کردے گا بیشک ہر چیز اللہ کے سامنے ہے،'),
(2613, 22, 18, 'کیا تم نے نہ دیکھا کہ اللہ کے لیے سجدہ کرتے ہیں وہ جو آسمانوں اور زمین میں ہیں اور سورج اور چاند اور تارے اور پہاڑ اور درخت اور چوپائے اور بہت آدمی اور بہت وہ ہیں جن پر عذاب مقرر ہوچکا اور جسے اللہ ذلیل کرے اسے کوئی عزت دینے والا نہیں، بیشک اللہ جو چاہے کرے، (السجدة) ۶'),
(2614, 22, 19, 'یہ دو فریق ہیں کہ اپنے رب میں جھگڑے تو جو کافر ہوئے ان کے لیے آگ کے کپڑے بیونتے (کاٹے) گئے ہیں اور ان کے سروں پر کھولتا پانی ڈالا جائے گا'),
(2615, 22, 20, 'جس سے گل جائے گا جو کچھ ان کے پیٹوں میں ہے اور ان کی کھالیں'),
(2616, 22, 21, 'اور ان کے لیے لوہے کے گرز ہیں'),
(2617, 22, 22, 'جب گھٹن کے سبب اس میں سے نکلنا چاہیں گے اور پھر اسی میں لوٹا دیے جائیں گے، اور حکم ہوگا کہ چکھو آگ کا عذاب،'),
(2618, 22, 23, 'بیشک اللہ داخل کرے گا انہیں جو ایمان لائے اور اچھے کام کیے بہشتوں میں جن کے نیچے نہریں بہیں اس میں پہنائے جائیں گے سونے کے کنگن اور موتی اور وہاں ان کی پوشاک ریشم ہے'),
(2619, 22, 24, 'اور انہیں پاکیزہ بات کی ہدایت کی گئی اور سب خوبیوں سراہے کی راہ بتائی گئی'),
(2620, 22, 25, 'بیشک وہ جنہوں نے کفر کیا اور روکتے ہیں اللہ کی راہ اور اس ادب والی مسجد سے جسے ہم نے سب لوگوں کے لیے مقرر کیا کہ اس میں ایک سا حق ہے وہاں کے رہنے والے اور پردیسی کا، اور جو اس میں کسی زیادتی کا ناحق ارادہ کرے ہم اسے دردناک عذاب چکھائیں گے'),
(2621, 22, 26, 'اور جب کہ ہم نے ابراہیم کو اس گھر کا ٹھکانا ٹھیک بتادیا اور حکم دیا کہ میرا کوئی شریک نہ کر اور میرا گھر ستھرا رکھ طواف والوں اور اعتکاف والوں اور رکوع سجدے والوں کے لیے'),
(2622, 22, 27, 'اور لوگوں میں حج کی عام ندا کردے وہ تیرے پاس حاضر ہوں گے پیادہ اور ہر دبلی اونٹنی پر کہ ہر دور کی راہ سے آتی ہیں'),
(2623, 22, 28, 'تاکہ وہ اپنا فائدہ پائیں اور اللہ کا نام لیں جانے ہوئے دنوں میں اس پر کہ انہیں روزی دی بے زبان چوپائے تو ان میں سے خود کھاؤ اور مصیبت زدہ محتاج کو کھلاؤ'),
(2624, 22, 29, 'پھر اپنا میل کچیل اتاریں اور اپنی منتیں پوری کریں اور اس آزاد گھر کا طواف کریں'),
(2625, 22, 30, 'بات یہ ہے اور جو اللہ کی حرمتوں کی تعظیم کرے تو وہ اس کے لیے اس کے رب کے یہاں بھلا ہے، اور تمہارے لیے حلال کیے گئے بے زبان چوپائے سوا ان کے جن کی ممانعت تم پر پڑھی جاتی ہے تو دور ہو بتوں کی گندگی سے اور بچو جھوٹی بات سے،'),
(2626, 22, 31, 'ایک اللہ کے ہوکر کہ اس کا ساجھی کسی کو نہ کرو، اور جو اللہ کا شریک کرے وہ گویا گرا آسمان سے کہ پرندے اسے اچک لے جاتے ہیں یا ہوا اسے کسی دور جگہ پھینکتی ہے'),
(2627, 22, 32, 'بات یہ ہے، اور جو اللہ کے نشانوں کی تعظیم کرے تو یہ دلوں کی پرہیزگاری سے ہے'),
(2628, 22, 33, 'تمہارے لیے چوپایوں میں فائدے ہیں ایک مقررہ میعاد تک پھر ان کا پہنچنا ہے اس آزاد گھر تک'),
(2629, 22, 34, 'اور ہر امت کے لیے ہم نے ایک قربانی مقرر فرمائی کہ اللہ کا نام لیں اس کے دیے ہوئے بے زبان چوپایوں پر تو تمہارا معبود ایک معبود ہے تو اسی کے حضور گردن رکھو اور اے محبوب خوشی سنادو ان تواضع والوں کو،'),
(2630, 22, 35, 'کہ جب اللہ کا ذکر ہوتا ہے ان کے دل ڈرنے لگتے ہیں اور جو افتاد پڑے اس کے سنے والے اور نماز برپا رکھنے والے اور ہمارے دیے سے خرچ کرتے ہیں'),
(2631, 22, 36, 'اور قربانی کے ڈیل دار جانور اور اونٹ اور گائے ہم نے تمہارے لیے اللہ کی نشانیوں سے کیے تمہارے لیے ان میں بھلائی ہے تو ان پر اللہ کا نام لو ایک پاؤں بندھے تین پاؤں سے کھڑے پھر جب ان کی کروٹیں گرجائیں تو ان میں سے خود کھاؤ اور صبر سے بیٹھنے والے اور بھیک مانگنے والے کو کھلاؤ، ہم نے یونہی ان کو تمہارے بس میں دے دیا کہ تم احسان مانو،'),
(2632, 22, 37, 'اللہ کو ہرگز نہ ان کے گوشت پہنچتے ہیں نہ ان کے خون ہاں تمہاری پرہیزگاری اس تک باریاب ہوتی ہے یونہی ان کو تمہارے پس میں کردیا کہ تم اللہ کی بڑائی بولو اس پر کہ تم کو ہدایت فرمائی، اور اے محبوب! خوشخبری سناؤ نیکی والوں کو'),
(2633, 22, 38, 'بیشک اللہ بلائیں ٹالتا ہے، مسلمانوں کی بیشک اللہ دوست نہیں رکھتا ہر بڑے دغا باز ناشکرے کو'),
(2634, 22, 39, 'پروانگی (اجازت) عطا ہوئی انہیں جن سے کافر لڑتے ہیں اس بناء پر کہ ان پر ظلم ہوا اور بیشک اللہ ان کی مدد کرنے پر ضرور قادر ہے،'),
(2635, 22, 40, 'وہ جو اپنے گھروں سے ناحق نکالے گئے صرف اتنی بات پر کہ انہوں نے کہا ہمارا رب اللہ ہے اور اللہ اگر آدمیوں میں ایک کو دوسرے سے دفع نہ فرماتا تو ضرور ڈھادی جاتیں خانقاہیں اور گرجا اور کلیسے اور مسجدیں جن میں اللہ کا بکثرت نام لیا جاتا ہے، اور بیشک اللہ ضرور مدد فرمائے گا اس کی جو اس کے دین کی مدد کرے گا بیشک ضرور اللہ قدرت والا غالب ہے،'),
(2636, 22, 41, 'وہ لوگ کہ اگر ہم انہیں زمین میں قابو دیں تو نماز برپا رکھیں اور زکوٰة اور بھلائی کا حکم کریں اور برائی سے روکیں اور اللہ ہی کے لیے سب کاموں کا انجام،'),
(2637, 22, 42, 'اور اگر یہ تمہاری تکذیب کرتے ہیں تو بیشک ان سے پہلے جھٹلا چکی ہے نوح کی قوم اور عاد اور ثمود'),
(2638, 22, 43, 'اور ابراہیم کی قوم اور لوط کی قوم،'),
(2639, 22, 44, 'اور مدین والے اور موسیٰ کی تکذیب ہوئی تو میں نے کافرو ں کو ڈھیل دی پھر انہیں پکڑا تو کیسا ہوا میرا عذاب'),
(2640, 22, 45, 'اور کتنی ہی بستیاں ہم نے کھپادیں (ہلاک کردیں) کہ وہ ستمگار تھیں تو اب وہ اپنی چھتوں پر ڈہی (گری) پڑی ہیں اور کتنے کنویں بیکار پڑے اور کتنے محل گچ کیے ہوئے'),
(2641, 22, 46, 'تو کیا زمین میں نہ چلے کہ ان کے دل ہوں جن سے سمجھیں یا کان ہوں جن سے سنیں تو یہ کہ آنکھیں اندھی نہیں ہوتیں بلکہ وہ دل اندھے ہوتے ہیں جو سینو ں میں ہیں،'),
(2642, 22, 47, 'اور یہ تم سے عذاب مانگنے میں جلدی کرتے ہیں اور اللہ ہرگز اپنا وعدہ جھوٹا نہ کرے گا اور بیشک تمہارے رب کے یہاں ایک دن ایسا ہے جیسے تم لوگوں کی گنتی میں ہزار برس'),
(2643, 22, 48, 'اور کتنی بستیاں کہ ہم نے ان کو ڈھیل دی اس حال پر کہ وہ ستمگار تھیں پھر میں نے انہیں پکڑا اور میری ہی طرف پلٹ کر آتا ہے'),
(2644, 22, 49, 'تم فرمادو کہ اے لوگو! میں تو یہی تمہارے لیے صریح ڈر سنانے والا ہوں،'),
(2645, 22, 50, 'تو جو ایمان لائے اور اچھے کام کیے ان کے لیے بخشش ہے اور عزت کی روزی'),
(2646, 22, 51, 'اور وہ جو کوشش کرتے ہیں ہماری آیتوں میں ہار جیت کے ارادہ سے وہ جہنمی ہیں،'),
(2647, 22, 52, 'اور ہم نے تم سے پہلے جتنے رسول یا نبی بھیجے سب پر کبھی یہ واقعہ گزرا ہے کہ جب انہوں نے پڑھا تو شیطان نے ان کے پڑھنے میں لوگوں پر کچھ اپنی طرف سے ملادیا تو مٹا دیتا ہے اللہ اس شیطان کے ڈالے ہوئے کو پھر اللہ اپنی آیتیں پکی کردیتا ہے اور اللہ علم و حکمت والا ہے،'),
(2648, 22, 53, 'تاکہ شیطان کے ڈالے ہوئے کو فتنہ کردے ان کے لیے جن کے دلوں میں بیماری ہے اور جن کے دل سخت ہیں اور بیشک ستمگار دُھرکے (پرلے درجے کے) جھگڑالو ہیں،'),
(2649, 22, 54, 'اور اس لیے کہ جان لیں وہ جن کو علم ملا ہے کہ وہ تمہارے رب کے پاس سے حق ہے تو اس پر ایمان لائیں تو جھک جائیں اس کے لیے ان کے دل، اور بیشک اللہ ایمان والوں کو سیدھی راہ چلانے والا ہے،'),
(2650, 22, 55, 'اور کافر اس سے ہمیشہ شک میں رہیں گے یہاں تک کہ ان پر قیامت آجائے اچانک یا ان پر ایسے دن کا عذاب آئے جس کا پھل ان کے لیے کچھ اچھا نہ ہو'),
(2651, 22, 56, 'بادشاہی اس دن اللہ ہی کی ہے، وہ ان میں فیصلہ کردے گا، تو جو ایمان لائے اور اچھے کام کیے وہ چین کے باغوں میں ہیں،'),
(2652, 22, 57, 'اور جنہوں نے کفر کیا اور ہماری آیتیں جھٹلائیں ان کے لیے ذلت کا عذاب ہے،'),
(2653, 22, 58, 'اور وہ جنہوں نے اللہ کی راہ میں اپنے گھر بار چھوڑے پھر مارے گئے یا مرگئے تواللہ ضرور انہیں اچھی روزی دے گا اور بیشک اللہ کی روزی سب سے بہتر ہے،'),
(2654, 22, 59, 'ضرور انہیں ایسی جگہ لے جائے گا جسے وہ پسند کریں گے اور بیشک اللہ علم اور حلم والا ہے،'),
(2655, 22, 60, 'بات یہ ہے اور جو بدلہ لے جیسی تکلیف پہنچائی گئی تھی پھر اس پر زیادتی کی جائے تو بیشک اللہ اس کی مدد فرمائے گا بیشک اللہ معاف کرنے والا بخشنے والا ہے،'),
(2656, 22, 61, 'یہ اس لیے کہ اللہ تعالیٰ رات کو ڈالتا ہے دن کے حصہ میں اور دن کو لاتا ہے رات کے حصہ میں اور اس لیے کہ اللہ سنتا دیکھتا ہے،'),
(2657, 22, 62, 'یہ اس لیے کہ اللہ ہی حق ہے اور اس کے سوا جسے پوجتے ہیں وہی باطل ہے اور اس لیے کہ اللہ ہی بلندی بڑائی والا ہے،'),
(2658, 22, 63, 'کیا تو نے نہ دیکھا کہ اللہ نے آسمان سے پانی اتارا تو صبح کو زمین ہریالی ہوگئی، بیشک اللہ پاک خبردار ہے،'),
(2659, 22, 64, 'اسی کا مال ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے، اور بیشک اللہ ہی بے نیاز سب خوبیوں سراہا ہے،'),
(2660, 22, 65, 'کیا تو نے نہ دیکھا کہ اللہ نے تمہارے بس میں کردیا جو کچھ زمین میں ہے اور کشتی کہ دریا میں اس کے حکم سے چلتی ہے اور وہ روکے ہوئے ہے آسمان کو کہ زمین پر نہ گر پڑے مگر اس کے حکم سے، بیشک اللہ آدمیوں پر بڑی مہر والا مہربان ہے'),
(2661, 22, 66, 'اور وہی ہے جس نے تمہیں زندہ کیا اور پھر تمہیں مارے گا پھر تمہیں جِلائے گا بیشک آدمی بڑا ناشکرا ہے'),
(2662, 22, 67, 'ہر امت کے لیے ہم نے عبادت کے قاعدے بنادیے کہ وہ ان پر چلے تو ہرگز وہ تم سے اس معاملہ میں جھگڑا نہ کریں اور اپنے رب کی طرف بلاؤ بیشک تم سیدھی راہ پر ہو،'),
(2663, 22, 68, 'اور اگر وہ تم سے جھگڑیں تو فرمادو کہ اللہ خوب جانتا ہے تمہارے کوتک (تمہاری کرتوت)'),
(2664, 22, 69, 'اللہ تم پر فیصلہ کردے گا قیامت کے دن جس بات میں اختلاف کرتے ہو'),
(2665, 22, 70, 'کیا تو نے نہ جانا کہ اللہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہے، بیشک یہ سب ایک کتاب میں ہے بیشک یہ اللہ پر آسان ہے'),
(2666, 22, 71, 'اور اللہ کے سوا ایسوں کو پوجتے ہیں جن کی کوئی سند اس نے نہ اتاری اور ایسوں کو جن کا خود انہیں کچھ علم نہیں اور ستمگاروں کا کوئی مددگار نہیں'),
(2667, 22, 72, 'اور جب ان پر ہماری روشن آیتیں پڑھی جائیں تو تم ان کے چہروں پر بگڑنے کے آثار دیکھو گے جنہوں نے کفر کیا، قریب ہے کہ لپٹ پڑیں ان کو جو ہماری آیتیں ان پر پڑھتے ہیں، تم فرمادو کیا میں تمہیں بتادوں جو تمہارے اس حال سے بھی بدتر ہے وہ آگ ہے، اللہ نے اس کا وعدہ دیا ہے کافروں کو، اور کیا ہی بری پلٹنے کی جگہ،'),
(2668, 22, 73, 'اے لوگو! ایک کہاوت فرمائی جاتی ہے اسے کان لگا کر سنو وہ جنہیں اللہ کے سوا تم پوجتے ہو ایک مکھی نہ بناسکیں گے اگرچہ سب اس پر اکٹھے ہوجائیں اور اگر مکھی ان سے کچھ چھین کرلے جائے تو اس سے چھڑا نہ سکیں کتنا کمزور چاہنے والا اور وہ جس کو چاہا'),
(2669, 22, 74, 'اللہ کی قدر نہ جانی جیسی چاہیے تھی بیشک اللہ قوت والا غالب ہے،'),
(2670, 22, 75, 'اللہ چن لیتا ہے فرشتوں میں سے رسول اور آدمیوں میں سے بیشک اللہ سنتا دیکھتا ہے،'),
(2671, 22, 76, 'جانتا ہے جو ان کے آگے ہے اور جو ان کے پیچھے ہے اور سب کاموں کی رجوع اللہ کی طرف ہے،'),
(2672, 22, 77, 'اے ایمان والو! رکوع اور سجدہ کرو اور اپنے رب کی بندگی کرو اور بھلے کام کرو اس امید پر کہ تمہیں چھٹکارا ہو، (السجدة) عندالشافعیؒ،'),
(2673, 22, 78, 'اور اللہ کی راہ میں جہاد کرو جیسا حق ہے جہاد کرنے کا اس نے تمہیں پسند کیا اور تم پر دین میں کچھ تنگی نہ رکھی تمہارے باپ ابراہیم کا دین اللہ نے تمہارا نام مسلمان رکھا ہے اگلی کتابوں میں اور اس قرآن میں تاکہ رسول تمہارا نگہبان و گواہ ہو اور تم اور لوگوں پر گواہی دو تو نماز برپا رکھو اور زکوٰة دو اور اللہ کی رسی مضبوط تھام لو وہ تمہارا مولیٰ ہے تو کیا ہی اچھا مولیٰ اور کیا ہی اچھا مددگار،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'بیشک مراد کو پہنچے ایمان والے'),
(2675, 23, 2, 'جو اپنی نماز میں گڑگڑاتے ہیں'),
(2676, 23, 3, 'اور وہ جو کسی بیہودہ بات کی طرف التفات نہیں کرتے'),
(2677, 23, 4, 'اور وہ کہ زکوٰة دینے کا کام کرتے ہیں'),
(2678, 23, 5, 'اور وہ جو اپنی شرمگاہوں کی حفاظت کرتے ہیں،'),
(2679, 23, 6, 'مگر اپنی بیبیوں یا شرعی باندیوں پر جو ان کے ہاتھ کی مِلک ہیں کہ ان پر کوئی ملامت نہیں'),
(2680, 23, 7, 'تو جو ان دو کے سوا کچھ اور چاہے وہی حد سے بڑھنے والے ہیں'),
(2681, 23, 8, 'اور وہ جو اپنی امانتوں اور اپنے عہد کی رعایت کرتے ہیں'),
(2682, 23, 9, 'اور وہ جو اپنی نمازوں کی نگہبانی کرتے ہیں'),
(2683, 23, 10, 'یہی لوگ وارث ہیں،'),
(2684, 23, 11, 'کہ فردوس کی میراث پائیں گے، وہ اس میں ہمیشہ رہیں گے،'),
(2685, 23, 12, 'اور بیشک ہم نے آدمی کو چنی ہوئی (انتخاب کی) مٹی سے بنایا،'),
(2686, 23, 13, 'پھر اسے پانی کی بوند کیا ایک مضبوط ٹھہراؤ میں'),
(2687, 23, 14, 'پھر ہم نے اس پانی کی بوند کو خون کی پھٹک کیا پھر خون کی پھٹک کو گوشت کی بوٹی پھر گوشت کی بوٹی کو ہڈیاں پھر ان ہڈیوں پر گوشت پہنایا، پھر اسے اور صورت میں اٹھان دی تو بڑی برکت والا ہے اللہ سب سے بہتر بتانے والا،'),
(2688, 23, 15, 'پھر اس کے بعد تم ضرور مرنے والے ہو،'),
(2689, 23, 16, 'پھر تم سب قیامت کے دن اٹھائے جاؤ گے،'),
(2690, 23, 17, 'اور بیشک ہم نے تمہارے اوپر سات راہیں بنائیں اور ہم خلق سے بے خبر نہیں'),
(2691, 23, 18, 'اور ہم نے آسمان سے پانی اتارا ایک اندازہ پر پھر اسے زمین میں ٹھہرایا اور بیشک ہم اس کے لے جانے پر قادر ہیں'),
(2692, 23, 19, 'تو اس سے ہم نے تمہارے باغ پیدا کیے کھجوروں اور انگوروں کے تمہارے لیے ان میں بہت سے میوے ہیں اور ان میں سے کھاتے ہو'),
(2693, 23, 20, 'اور وہ پیڑ پیدا کیا کہ طور سینا سے نکلتا ہے لے کر اگتا ہے تیل اور کھانے والوں کے لیے سالن'),
(2694, 23, 21, 'اور بیشک تمہارے لیے چوپایوں میں سمجھنے کا مقام ہے، ہم تمہیں پلاتے ہیں اس میں سے جو ان کے پیٹ میں ہے اور تمہارے لیے ان میں بہت فائدے ہیں اور ان سے تمہاری خوراک ہے'),
(2695, 23, 22, 'اور ان پر اور کشتی پر سوار کیے جاتے ہو،'),
(2696, 23, 23, 'اور بیشک ہم نے نوح کو اس کی قوم کی طرف بھیجا تو اس نے کہا اے میری قوم اللہ کو پوجو اس کے سوا تمہارا کوئی خدا نہیں، تو کیا تمہیں ڈر نہیں'),
(2697, 23, 24, 'تو اس کی قوم کے جن سرداروں نے کفر کیا بولے یہ تو نہیں مگر تم جیسا آدمی چاہتا ہے کہ تمہارا بڑا بنے اور اللہ چاہتا تو فرشتے اتارتا ہم نے تو یہ اگلے باپ داداؤں میں نہ سنا'),
(2698, 23, 25, 'وہ تو نہیں مگر ایک دیوانہ مرد تو کچھ زمانہ تک اس کا انتظار کیے رہو'),
(2699, 23, 26, 'نوح نے عرض کی اے میرے رب! میری مدد فرما اس پر کہ انہوں نے مجھے جھٹلایا،'),
(2700, 23, 27, 'تو ہم نے اسے وحی بھیجی کہ ہماری نگاہ کے سامنے اور ہمارے حکم سے کشتی بنا پھر جب ہمارا حکم آئے اور تنور ابلے تو اس میں بٹھالے ہر جوڑے میں سے دو اور اپنے گھر والے مگر ان میں سے وہ جن پر بات پہلے پڑچکی اور ان ظالموں کے معاملہ میں مجھ سے بات نہ کرنا یہ ضرور ڈبوئے جائیں گے،'),
(2701, 23, 28, 'پھر جب ٹھیک بیٹھ لے کشتی پر تُو اور تیرے ساتھ والے تو کہہ سب خوبیاں اللہ کو جس نے ہمیں ان ظالموں سے نجات دی،'),
(2702, 23, 29, 'اور عرض کر کہ اے میرے رب مجھے برکت والی جگہ اتار اور تو سب سے بہتر اتارنے والا ہے،'),
(2703, 23, 30, 'بیشک اس میں ضرو ر نشانیاں اور بیشک ضرور ہم جانچنے والے تھے'),
(2704, 23, 31, 'پھر ان کے بعد ہم نے اور سنگت (قوم) پیدا کی'),
(2705, 23, 32, 'تو ان میں ایک رسول انہیں میں سے بھیجا کہ اللہ کی بندگی کرو اس کے سوا تمہارا کوئی خدا نہیں، تو کیا تمہیں ڈر نہیں'),
(2706, 23, 33, 'اور بولے اس قوم کے سردار جنہوں نے کفر کیا اور آخرت کی حاضری کو جھٹلایا اور ہم نے انہیں دنیا کی زندگی میں چین دیا کہ یہ تو نہیں مگر جیسا آدمی جو تم کھاتے ہو اسی میں سے کھاتا ہے اور جو تم پیتے ہو اسی میں سے پیتا ہے'),
(2707, 23, 34, 'اور اگر تم کسی اپنے جیسے آدمی کی اطاعت کرو جب تو تم ضرور گھاٹے میں ہو،'),
(2708, 23, 35, 'کیا تمہیں یہ وعدہ دیتا ہے کہ تم جب مرجاؤ گے اور مٹی اور ہڈیاں ہوجاؤ گے اس کے بعد پھر نکالے جاؤ گے،'),
(2709, 23, 36, 'کتنی دور ہے کتنی دور ہے جو تمہیں وعدہ دیا جاتا ہے'),
(2710, 23, 37, 'وہ تو نہیں مگر ہماری دنیا کی زندگی کہ ہم مرتے جیتے ہیں اور ہمیں اٹھنا نہیں'),
(2711, 23, 38, 'وہ تو نہیں مگر ایک مرد جس نے اللہ پر جھوٹ باندھا اور ہم اسے ماننے کے نہیں'),
(2712, 23, 39, 'عرض کی کہ اے میرے رب میری مدد فرما اس پر کہ انہوں نے مجھے جھٹلایا،'),
(2713, 23, 40, 'اللہ نے فرمایا کچھ دیر جاتی ہے کہ یہ صبح کریں گے پچھتاتے ہوئے'),
(2714, 23, 41, 'تو انہیں آلیا سچی چنگھاڑ نے تو ہم نے انہیں گھاس کوڑا کردیا تو دور ہوں ظالم لوگ،'),
(2715, 23, 42, 'پھر ان کے بعد ہم نے اور سنگتیں (قومیں) پیدا کیں'),
(2716, 23, 43, 'کوئی امت اپنی میعاد سے نہ پہلے جائے نہ پیچھے رہے'),
(2717, 23, 44, 'پھر ہم نے اپنے رسول بھیجے ایک پیچے دوسرا جب کسی امت کے پاس اس کا رسول آیا انہوں نے اسے جھٹلایا تو ہم نے اگلوں سے پچھلے ملادیے اور انہیں کہانیاں کر ڈالا تو دور ہوں وہ لوگ کہ ایمان نہیں لاتے،'),
(2718, 23, 45, 'پھر ہم نے موسیٰ اور اس کے بھائی ہارون کو اپنی آیتوں اور روشن سند کے ساتھ بھیجا،'),
(2719, 23, 46, 'فرعون اور اس کے درباریوں کی طرف تو انہوں نے غرور کیا اور وہ لوگ غلبہ پائے ہوئے تھے،'),
(2720, 23, 47, 'تو بولے کیا ہم ایمان لے آئیں اپنے جیسے دو آدمیوں پر اور ان کی قوم ہماری بندگی کررہی ہے،'),
(2721, 23, 48, 'تو انہوں نے ان دونوں کو جھٹلایا تو ہلاک کیے ہوؤں میں ہوگئے'),
(2722, 23, 49, 'اور بیشک ہم نے موسیٰ کو کتاب عطا فرمائی کہ ان کو ہدایت ہو،'),
(2723, 23, 50, 'اور ہم نے مریم اور اس کے بیٹے کو نشانی کیا اور انہیں ٹھکانا دیا ایک بلند زمین جہاں بسنے کا مقام اور نگاہ کے سامنے بہتا پانی،'),
(2724, 23, 51, 'اے پیغمبرو! پاکیزہ چیزیں کھاؤ اور اچھا کام کرو، میں تمہارے کاموں کو جانتا ہوں'),
(2725, 23, 52, 'اور بیشک یہ تمہارا دین ایک ہی دین ہے اور میں تمہارا رب ہوں تو مجھ سے ڈرو،'),
(2726, 23, 53, 'تو ان کی امتوں نے اپنا کام آپس میں ٹکڑے ٹکڑے کرلیا ہر گروہ جو اس کے پاس ہے اس پر خوش ہے،'),
(2727, 23, 54, 'تو تم ان کو چھوڑ دو ان کے نشہ میں ایک وقت تک'),
(2728, 23, 55, 'کیا یہ خیال کررہے ہیں کہ وہ جو ہم ان کی مدد کررہے ہیں مال اور بیٹوں سے'),
(2729, 23, 56, 'یہ جلد جلد ان کو بھلائیاں دیتے ہیں بلکہ انہیں خبر نہیں'),
(2730, 23, 57, 'بیشک وہ جو اپنے رب کے ڈر سے سہمے ہوئے ہیں'),
(2731, 23, 58, 'اور وہ جو اپنے رب کی آیتوں پر ایمان لاتے ہیں'),
(2732, 23, 59, 'اور وہ جو اپنے رب کا کوئی شریک نہیں کرتے،'),
(2733, 23, 60, 'اور وہ جو دیتے ہیں جو کچھ دیں اور ان کے دل ڈر رہے ہیں یوں کہ ان کو اپنے رب کی طرف پھرنا ہے،'),
(2734, 23, 61, 'یہ لوگ بھلائیوں میں جلدی کرتے ہیں اور یہی سب سے پہلے انہیں پہنچے'),
(2735, 23, 62, 'اور ہم کسی جان پر بوجھ نہیں رکھتے مگر اس کی طاقت بھر اور ہمارے پاس ایک کتاب ہے کہ حق بولتی ہے اور ان پر ظلم نہ ہوگا، ف۹۸)'),
(2736, 23, 63, 'بلکہ ان کے دل اس سے غفلت میں ہیں اور ان کے کام ان کاموں سے جدا ہیں جنہیں وہ کررہے ہیں،'),
(2737, 23, 64, 'یہاں تک کہ جب ہم نے ان کے امیروں کو عذاب میں پکڑا تو جبھی وہ فریاد کرنے لگے،'),
(2738, 23, 65, 'آج فریاد نہ کرو، ہماری طرف سے تمہاری مدد نہ ہوگی،'),
(2739, 23, 66, 'بیشک میری آیتیں تم پر پڑھی جاتی تھیں تو تم اپنی ایڑیوں کے بل الٹے پلٹتے تھے'),
(2740, 23, 67, 'خدمت حرم پر بڑائی مارتے ہو رات کو وہاں بیہودہ کہانیاں بکتے'),
(2741, 23, 68, 'حق کو چھوڑے ہوئے کیا انہوں نے بات کو سوچا نہیں یا ان کے پاس وہ آیا جو ان کے باپ دادا کے پاس نہ آیا تھا'),
(2742, 23, 69, 'یا انہوں نے اپنے رسول کو نہ پہچانا تو وہ اسے بیگانہ سمجھ رہے ہیں'),
(2743, 23, 70, 'یا کہتے ہیں اسے سودا ہے بلکہ وہ تو ان کے پاس حق لائے اور ان میں اکثر حق برا لگتا ہے'),
(2744, 23, 71, 'اور اگر حق ان کی خواہشوں کی پیروی کرتا تو ضرور آسمان اور زمین اور جو کوئی ان میں ہیں سب تباہ ہوجاتے بلکہ ہم تو ان کے پاس وہ چیز لائے جس میں ان کی ناموری تھی تو وہ اپنی عزت سے ہی منہ پھیرے ہوئے ہیں،'),
(2745, 23, 72, 'کیا تم ان سے کچھ اجرت مانگتے ہو تو تمہارے رب کا اجر سب سے بھلا اور وہ سب سے بہتر روزی دینے والا'),
(2746, 23, 73, 'اور بیشک تم انہیں سیدھی راہ کی طرف بلاتے ہو'),
(2747, 23, 74, 'اور بیشک جو آخرت پر ایما ن نہیں لاتے ضرور سیدھی راہ سے کترائے ہوئے ہیں،'),
(2748, 23, 75, 'اور اگر ہم ان پر رحم کریں اور جو مصیبت ان پر پڑی ہے ٹال دیں تو ضرور بھٹ پنا (احسان فراموشی) کریں گے اپنی سرکشی میں بہکتے ہوئے'),
(2749, 23, 76, 'اور بیشک ہم نے انہیں عذاب میں پکڑا تو نہ وہ اپنے رب کے حضور میں جھکے اور نہ گڑگڑاتے ہیں'),
(2750, 23, 77, 'یہاں تک کہ جب ہم نے ان پر کھولا کسی سخت عذاب کا دروازہ تو وہ اب اس میں ناامید پڑے ہیں،'),
(2751, 23, 78, 'اور وہی ہے جس نے بنائے تمہارے لیے کان اور آنکھیں اور دل تم بہت ہی کم حق مانتے ہو'),
(2752, 23, 79, 'اور وہی ہے جس نے تمہیں زمین میں پھیلایا اور اسی کی طرف اٹھنا ہے'),
(2753, 23, 80, 'اور وہی جٕلائے اور مارے اور اسی کے لیے ہیں رات اور دن کی تبدیلیاں تو کیا تمہیں سمجھ نہیں'),
(2754, 23, 81, 'بلکہ انہوں نے وہی کہی جو اگلے کہتے تھے،'),
(2755, 23, 82, 'بولے کیا جب ہم مرجائیں اور مٹی اور ہڈیاں ہوجائیں کیا پھر نکالے جائیں گے،'),
(2756, 23, 83, 'بیشک یہ وعدہ ہم کو اور ہم سے پہلے ہمارے باپ دادا کو دیا گیا یہ تو نہیں مگر وہی اگلی داستانیں'),
(2757, 23, 84, 'تم فرماؤ کس کا مال ہے زمین اور جو کچھ اس میں ہے اگر تم جانتے ہو'),
(2758, 23, 85, 'اب کہیں گے کہ اللہ کا تم فرماؤ پھر کیوں نہیں سوچتے'),
(2759, 23, 86, 'تم فرماؤ کون ہے مالک سوتوں آسمانوں کا اور مالک بڑے عرش کا،'),
(2760, 23, 87, 'اب کہیں گے یہ اللہ ہی کی شان ہے، تم فرماؤ پھر کیوں نہیں ڈرتے'),
(2761, 23, 88, 'تم فرماؤ کس کے ہاتھ ہے ہر چیز کا قابو اور وہ پناہ دیتا ہے اور اس کے خلاف کوئی پناہ نہیں دے سکتا اگر تمہیں علم ہو'),
(2762, 23, 89, 'اب کہیں گے یہ اللہ ہی کی شان ہے، تم فرماؤ پھر کس جادو کے فریب میں پڑے ہو'),
(2763, 23, 90, 'بلکہ ہم ان کے پاس حق لائے اور وہ بیشک جھوٹے ہیں'),
(2764, 23, 91, 'اللہ نے کوئی بچہ اختیار نہ کیا اور نہ اس کے ساتھ کوئی دوسرا خدا یوں ہوتا تو ہر خدا اپنی مخلوق لے جاتا اور ضرور ایک دوسرے پر اپنی تعلی چاہتا پاکی ہے اللہ کو ان باتوں سے جو یہ بناتے ہیں'),
(2765, 23, 92, 'جاننے والا ہر نہاں و عیاں کا تو اسے بلندی ہے ان کے شرک سے،'),
(2766, 23, 93, 'تم عرض کرو کہ اے میرے رب! اگر تو مجھے دکھائے جو انہیں وعدہ دیا جاتا ہے،'),
(2767, 23, 94, 'تو اے میرے رب! مجھے ان ظالموں کے ساتھ نہ کرنا'),
(2768, 23, 95, 'اور بیشک ہم قادر ہیں کہ تمہیں دکھادیں جو انہیں وعدہ دے رہے ہیں'),
(2769, 23, 96, 'سب سے اچھی بھلائی سے برائی کو دفع کرو ہم خوب جانتے ہیں جو باتیں یہ بناتے ہیں'),
(2770, 23, 97, 'اور تم عرض کرو کہ اے میرے رب تیری پناہ شیاطین کے وسوسو ں سے'),
(2771, 23, 98, 'اور اے میرے رب تیری پناہ کہ وہ میرے پاس آئیں،'),
(2772, 23, 99, 'یہاں تک کہ جب ان میں کسی کو موت آئے تو کہتا ہے کہ اے میرے رب مجھے واپس پھر دیجئے،'),
(2773, 23, 100, 'شاید اب میں کچھ بھلائی کماؤں اس میں جو چھوڑ آیا ہوں ہشت یہ تو ایک بات ہے جو وہ اپنے منہ سے کہتا ہے اور ان کے آگے ایک آڑ ہے اس دن تک جس دن اٹھائے جائیں گے،'),
(2774, 23, 101, 'تو جب صُور پھونکا جائے گا تو نہ ان میں رشتے رہیں گے اور نہ ایک دوسرے کی بات پوچھے'),
(2775, 23, 102, 'تو جن کی تولیں بھاری ہولیں وہی مراد کچھ پہنچے،'),
(2776, 23, 103, 'اور جن کی تولیں ہلکی پڑیں وہی ہیں جنہوں نے اپنی جانیں گھاٹے میں ڈالیں ہمیشہ دوزخ میں رہیں گے،'),
(2777, 23, 104, 'ان کے منہ پر آگ لپیٹ مارے گی اور وہ اس میں منہ چڑائے ہوں گے'),
(2778, 23, 105, 'کیا تم پر میری آیتیں نہ پڑھی جاتی تھیں تو تم انہیں جھٹلاتے تھے،'),
(2779, 23, 106, 'کہیں گے اے ہمارے رب ہم پر ہماری بدبختی غالب آئی اور ہم گمراہ لوگ تھے،'),
(2780, 23, 107, 'اے رب ہمارے ہم کو دوزخ سے نکال دے پھر اگر ہم ویسے ہی کریں تو ہم ظالم ہیں'),
(2781, 23, 108, 'رب فرمائے گا دھتکارے (خائب و خاسر) پڑے رہو اس میں اور مجھ سے بات نہ کرو'),
(2782, 23, 109, 'بیشک میرے بندوں کا ایک گروہ کہتا تھا اے ہمارے رب! ہم ایمان لائے تو ہمیں بخش دے اور ہم پر رحم کر اور تو سب سے بہتر رحم کرنے والا ہے،'),
(2783, 23, 110, 'تو تم نے انہیں ٹھٹھا بنالیا یہاں تک کہ انہیں بنانے کے شغل میں میری یاد بھول گئے اور تم ان سے ہنسا کرتے،'),
(2784, 23, 111, 'بیشک آج میں نے ان کے صبر کا انہیں یہ بدلہ دیا کہ وہی کامیاب ہیں،'),
(2785, 23, 112, 'فرمایا تم زمین میں کتنا ٹھہرے برسوں کی گنتی سے'),
(2786, 23, 113, '، بولے ہم ایک دن رہے یا دن کا حصہ تو گننے والوں سے دریافت فرما'),
(2787, 23, 114, 'فرمایا تم نہ ٹھہرے مگر تھوڑا اگر تمہیں علم ہوتا،'),
(2788, 23, 115, 'تو کیا یہ سمجھتے ہو کہ ہم نے تمہیں بیکار بنایا اور تمہیں ہماری طرف پھرنا نہیں'),
(2789, 23, 116, 'تو بہت بلندی والا ہے اللہ سچا بادشاہ کوئی معبود نہیں سوا اس کے عزت والے عرش کا مالک،'),
(2790, 23, 117, 'اور جو اللہ کے ساتھ کسی دوسرے خدا کو پوجے جس کی اس کے پاس کوئی سند نہیں تو اس کا حساب اس کے رب کے یہاں ہے، بیشک کافروں کا چھٹکارا نہیں،'),
(2791, 23, 118, 'اور تم عرض کرو، اے میرے رب بخش دے اور رحم فرما اور تو سب سے برتر رحم کرنے والا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'یہ ایک سورة ہے کہ ہم نے اتاری اور ہم نے اس کے احکام فرض کیے اور ہم نے اس میں روشن آیتیں نازل فرمائیں کہ تم دھیان کرو،'),
(2793, 24, 2, 'جو عورت بدکار ہو اور جو مرد تو ان میں ہر ایک کو سو کوڑے لگاؤ اور تمہیں ان پر ترس نہ آئے اللہ کے دین میں اگر تم ایمان لاتے ہو اللہ اور پچھلے دن پر اور چاہیے کہ ان کی سزا کے وقت مسلمانوں کا ایک گروہ حاضر ہو'),
(2794, 24, 3, 'بدکار مرد نکاح نہ کرے مگر بدکار عورت یا شرک والی سے، اور بدکار عورت سے نکاح نہ کرے مگر بدکار مرد یا مشرک اور یہ کام ایمان والوں پر حرام ہے'),
(2795, 24, 4, 'اور جو پارسا عورتوں کو عیب لگائیں پھر چار گواہ معائنہ کے نہ لائیں تو انہیں اسی کوڑے لگاؤ اور ان کی گواہی کبھی نہ مانو اور وہی فاسق ہیں،'),
(2796, 24, 5, 'مگر جو اس کے بعد توبہ کرلیں اور سنور جائیں تو بیشک اللہ بخشنے والا مہربان ہے،'),
(2797, 24, 6, 'اور وہ جو اپنی عورتوں کو عیب لگائیں اور ان کے پاس اپنے بیان کے سوا گواہ نہ ہوں تو ایسے کسی کی گواہی یہ ہے کہ چار بار گواہی دے اللہ کے نام سے کہ وہ سچا ہے'),
(2798, 24, 7, 'اور پانچویں یہ کہ اللہ کی لعنت ہو اس پر اگر جھوٹا ہو،'),
(2799, 24, 8, 'اور عورت سے یوں سزا ٹل جائے گی کہ وہ اللہ کا نام لے کر چار بار گواہی دے کہ مرد جھوٹا ہے'),
(2800, 24, 9, 'اور پانچویں یوں کہ عورت پر غضب اللہ کا اگر مرد جھوٹا ہے اور پانچویں یوں کہ عورت پر غضب اللہ کا اگر مرد سچا ہو'),
(2801, 24, 10, 'اور اگر اللہ کا فضل اور اس کی رحمت تم پر نہ ہوتی اور یہ کہ اللہ توبہ قبول فرماتا حکمت والا ہے،'),
(2802, 24, 11, 'تو تمہارا پردہ کھول دیتا بیشک وہ کہ یہ بڑا بہتان لائے ہیں تمہیں میں کی ایک جماعت ہے اسے اپنے لیے برا نہ سمجھو، بلکہ وہ تمہارے لیے بہتر ہے ان میں ہر شخص کے لیے وہ گناہ ہے جو اس نے کمایا اور ان میں وہ جس نے سب سے بڑا حصہ لیا اس کے لیے بڑا عذاب ہے'),
(2803, 24, 12, 'کیوں نہ ہوا ہوا جب تم نے اسے سنا تھا کہ مسلمان مردوں اور مسلمان عورتوں نے اپنوں پر نیک گمان کیا ہوتا اور کہتے یہ کھلا بہتان ہے'),
(2804, 24, 13, 'اس پر چار گواہ کیوں نہ لائے، تو جب گواہ نہ لائے تو وہی اللہ کے نزدیک جھوٹے ہیں،'),
(2805, 24, 14, 'اور اگر اللہ کا فضل اور اس کی رحمت تم پر دنیا اور آخرت میں نہ ہو تی تو جس چرچے میں تم پڑے اس پر تمہیں بڑا عذاب پہنچتا،'),
(2806, 24, 15, 'جب تم ایسی بات اپنی زبانوں پر ایک دوسرے سے سن کر لاتے تھے اور اپنے منہ سے وہ نکالتے تھے جس کا تمہیں علم نہیں اور اسے سہل سمجھتے تھے اور وہ اللہ کے نزدیک بڑی بات ہے'),
(2807, 24, 16, 'اور کیوں نہ ہوا جب تم نے سنا تھا کہا ہوتا کہ ہمیں نہیں پہنچتا کہ ایسی بات کہیں الہٰی پاکی ہے تجھے یہ بڑا بہتان ہے،'),
(2808, 24, 17, 'اللہ تمہیں نصیحت فرماتا ہے کہ اب کبھی ایسا نہ کہنا اگر ایمان رکھتے ہو،'),
(2809, 24, 18, 'اور اللہ تمہارے لیے آیتیں صاف بیان فرماتا ہے، اور اللہ علم و حکمت والا ہے،'),
(2810, 24, 19, 'وہ لوگ جو چاہتے ہیں کہ مسلمانوں میں برا چرچا پھیلے ان کے لیے دردناک عذاب ہے دنیا اور آخرت میں اور اللہ جانتا ہے اور تم نہیں جانتے،'),
(2811, 24, 20, 'اور اگر اللہ کا فضل اور اس کی رحمت تم پر نہ ہوتی اور یہ کہ اللہ تم پر نہایت مہربان مہروالا ہے،'),
(2812, 24, 21, 'تو تم اس کا مزہ چکھتے اے ایمان والو! شیطان کے قدموں پر نہ چلو، اور جو شیطان کے قدموں پر چلے تو وہ تو بے حیائی اور بری ہی بات بتائے گا اور اگر اللہ کا فضل اور اس کی رحمت تم پر نہ ہوتی تو تم میں کوئی بھی کبھی ستھرا نہ ہوسکتا ہاں اللہ ستھرا کردیتا ہے جسے چاہے اور اللہ سنتا جانتا ہے،'),
(2813, 24, 22, 'اور قسم نہ کھائیں وہ جو تم میں فضیلت والے اور گنجائش والے ہیں قرابت والوں اور مسکینوں اور اللہ کی راہ میں ہجرت کرنے والوں کو دینے کی، اور چاہیے کہ معاف کریں اور درگزریں، کیا تم اسے دوست نہیں رکھتے کہ اللہ تمہاری بخشش کرے، اور اللہ بخشنے والا مہربان ہے'),
(2814, 24, 23, 'بیشک وہ جو عیب لگاتے ہیں انجان پارسا ایمان والیوں کو ان پر لعنت ہے دنیا اور آخرت میں اور ان کے لیے بڑا عذاب ہے'),
(2815, 24, 24, 'جس دن ان پر گواہی دیں گے ان کی زبانیں اور ان کے ہاتھ اور ان کے پاؤں جو کچھ کرتے تھے،'),
(2816, 24, 25, 'اس دن اللہ انہیں ان کی سچی سزا پوری دے گا اور جان لیں گے کہ اللہ ہی صریح حق ہے،'),
(2817, 24, 26, 'گندیاں گندوں کے لیے اور گندے گندیوں کے لیے اور ستھریاں ستھروں کے لیے اور ستھرے ستھریوں کے لیے وہ پاک ہیں ان باتوں سے جو یہ کہہ رہے ہیں، ان کے لیے بخشش اور عزت کی روزی ہے'),
(2818, 24, 27, 'اے ایمان والو! اپنے گھروں کے سوا اور گھروں میں نہ جاؤ جب تک اجازت نہ لے لو اور ان کے ساکنوں پر سلام نہ کرلو یہ تمہارے لیے بہتر ہے کہ تم دھیان کرو،'),
(2819, 24, 28, 'پھر اگر ان میں کسی کو نہ پاؤ جب بھی بے ما لکوں کی اجازت کے ان میں نہ جاؤ اور اگر تم سے کہا جائے واپس جاؤ تو واپس ہو یہ تمہارے لیے بہت ستھرا ہے، اللہ تمہارے کاموں کو جانتا ہے،'),
(2820, 24, 29, 'اس میں تم پر کچھ گناہ نہیں کہ ان گھروں میں جاؤ جو خاص کسی کی سکونت کے نہیں اور ان کے برتنے کا تمہیں اختیار ہے، اور اللہ جانتا ہے جوتم ظاہر کرتے ہو، اور جو تم چھپاتے ہو،'),
(2821, 24, 30, 'مسلمان مردوں کو حکم دو اپنی نگاہیں کچھ نیچی رکھیں اور اپنی شرمگاہوں کی حفاظت کریں یہ ان کے لیے بہت ستھرا ہے، بیشک اللہ کو ان کے کاموں کی خبر ہے،'),
(2822, 24, 31, 'اور مسلمان عورتوں کو حکم دو اپنی نگاہیں کچھ نیچی رکھیں اور اپنی پارسائی کی حفاظت کریں اور اپنا بناؤ نہ دکھائیں مگر جتنا خود ہی ظاہر ہے اور وہ دوپٹے اپنے گریبانوں پر ڈالے رہیں، اور اپنا سنگھار ظاہر نہ کریں مگر اپنے شوہروں پر یا اپنے باپ یا شوہروں کے باپ یا اپنے بیٹوں یا شوہروں کے بیٹے یا اپنے بھائی یا اپنے بھتیجے یا اپنے بھانجے یا اپنے دین کی عورتیں یا اپنی کنیزیں جو اپنے ہاتھ کی ملک ہوں یا نوکر بشرطیکہ شہوت والے مرد نہ ہوں یا وہ بچے جنہیں عورتوں کی شرم کی چیزوں کی خبر نہیں اور زمین پر پاؤں زور سے نہ رکھیں کہ جانا جائے ان کا چھپا ہوا سنگھار اور اللہ کی طرف توبہ کرو اے مسلمانو! سب کے سب اس امید پر کہ تم فلاح پاؤ،'),
(2823, 24, 32, 'اور نکاح کردو اپنوں میں ان کا جو بے نکاح ہوں اور اپنے لائق بندوں اور کنیزوں کا اگر وہ فقیر ہوں تو اللہ انہیں غنی کردے گا اپنے فضل کے سبب اور اللہ وسعت والا علم والا ہے،'),
(2824, 24, 33, 'اور چاہیے کہ بچے رہیں وہ جو نکاح کا مقدور نہیں رکھتے یہاں تک کہ اللہ مقدور والا کردے اپنے فضل سے اور تمہارے ہاتھ کی مِلک باندی غلاموں میں سے جو یہ چاہیں کہ کچھ مال کمانے کی شرط پر انہیں آزادی لکھ دو تو لکھ دو اگر ان میں کچھ بھلائی جانو اور اس پر ان کی مدد کرو اللہ کے مال سے جو تم کو دیا اور مجبور نہ کرو اپنی کنیزوں کو بدکاری پر جب کہ وہ بچنا چاہیں تاکہ تم دنیوی زندگی کا کچھ مال چاہو اور جو انہیں مجبور کرے گا تو بیشک اللہ بعد اس کے کہ وہ مجبوری ہی کی حالت پر رہیں بخشنے والا مہربان ہے'),
(2825, 24, 34, 'اور بیشک ہم نے اتاریں تمہاری طرف روشن آیتیں اور کچھ ان لوگوں کا بیان جو تم سے پہلے ہو گزرے اور ڈر والوں کے لیے نصیحت،'),
(2826, 24, 35, 'اللہ نور ہے آسمانوں اور زمینوں کا، اس کے نور کی مثال ایسی جیسے ایک طاق کہ اس میں چراغ ہے وہ چراغ ایک فانوس میں ہے وہ فانوس گویا ایک ستارہ ہے موتی سا چمکتا روشن ہوتا ہے برکت والے پیڑ زیتون سے جو نہ پورب کا نہ پچھم کا قریب ہے کہ اس کا تیل بھڑک اٹھے اگرچہ اسے آگ نہ چھوئے نور پر نور ہے اللہ اپنے نور کی راہ بتاتا ہے جسے چاہتا ہے، اور اللہ مثالیں بیان فرماتا ہے لوگوں کے لیے، اور اللہ سب کچھ جانتا ہے،'),
(2827, 24, 36, 'ان گھروں میں جنہیں بلند کرنے کا اللہ نے حکم دیا ہے اور ان میں اس کا نام لیا جاتا ہے، اللہ کی تسبیح کرتے ہیں ان میں صبح اور شام'),
(2828, 24, 37, 'وہ مرد جنہیں غافل نہیں کرتا کوئی سودا اور نہ خرید و فروخت اللہ کی یاد اور نماز برپا رکھنے اور زکوٰة دینے سے ڈرتے ہیں اس دن سے جس میں الٹ جائیں گے دل اور آنکھیں'),
(2829, 24, 38, 'تاکہ اللہ انہیں بدلہ دے ان کے سب سے بہتر کام کام اور اپنے فضل سے انہیں انعام زیادہ دے، اور اللہ روزی دیتا ہے جسے چاہے بے گنتی،'),
(2830, 24, 39, 'اور جو کافر ہوئے ان کے کام ایسے ہیں جیسے دھوپ میں چمکتا ریتا کسی جنگل میں کہ پیاسا اسے پانی سمجھے، یہاں تک جب اس کے پاس آیا تو اسے کچھ نہ پایا اور اللہ کو اپنے قریب پایا تو اس نے اس کا حساب پورا بھردیا، اور اللہ جلد حساب کرلیتا ہے'),
(2831, 24, 40, 'یا جیسے اندھیریاں کسی کنڈے کے (گہرائی والے) دریا میں اس کے اوپر موج مو ج کے اوپر اور موج اس کے اوپر بادل، اندھیرے ہیں ایک پر ایک جب اپنا ہاتھ نکالے تو سوجھائی دیتا معلوم نہ ہو اور جسے اللہ نور نہ دے اس کے لیے کہیں نور نہیں'),
(2832, 24, 41, 'کیا تم نے نہ دیکھا کہ اللہ کی تسبیح کرتے ہیں جو کوئی آسمانوں اور زمین میں ہیں اور پرندے پر پھیلائے سب نے جان رکھی ہے اپنی نماز اور اپنی تسبیح، اور اللہ ان کے کاموں کو جانتا ہے،'),
(2833, 24, 42, 'اور اللہ ہی کے لیے ہے سلطنت آسمانوں اور زمین کی اور اللہ ہی کی طرف پھر جانا،'),
(2834, 24, 43, 'کیا تو نے نہ دیکھا کہ اللہ نرم نرم چلاتا ہے بادل کو پھر انہیں آپس میں مِلاتا ہے پھر انہیں تہہ پر تہہ کردیتا ہے تو تُو دیکھے کہ اس کے بیچ میں سے مینہ نکلتا ہے اور اتارتا ہے آسمان سے اس میں جو برف کے پہاڑ ہیں ان میں سے کچھ اولے پھر ڈالنا ہے انہیں جس پر چاہے اور پھیردیتا ہے انہیں جس سے چاہے قریب ہے کہ اس کی بجلی کی چمک آنکھ لے جائے'),
(2835, 24, 44, 'اللہ بدلی کرتا ہے رات اور دن کی بیشک اس میں سمجھنے کا مقام ہے نگاہ والوں کو،'),
(2836, 24, 45, 'اور اللہ نے زمین پر ہر چلنے والا پانی سے بنایا تو ان میں کوئی اپنے پیٹ پر چلتا ہے اور ان میں کوئی دو پاؤں پر چلتا ہے اور ان میں کوئی چار پاؤں پر چلتا ہے اللہ بناتا ہے جو چاہے، بیشک اللہ سب کچھ کرسکتا ہے،'),
(2837, 24, 46, 'بیشک ہم نے اتاریں صاف بیان کرنے والی آیتیں اور اللہ جسے چاہے سیدھی راہ دکھائے'),
(2838, 24, 47, 'اور کہتے ہیں ہم ایمان لائے اللہ اور رسول پر اور حکم مانا پھر کچھ ان میں کے اس کے بعد پھر جاتے ہیں اور وہ مسلمان نہیں'),
(2839, 24, 48, 'اور جب بلائے جائیں اللہ اور اس کے رسول کی طرف کہ رسول ان میں فیصلہ فرمائے تو جبھی ان کا ایک فریق منہ پھیر جاتا ہے،'),
(2840, 24, 49, 'اور اگر ان میں ڈگری ہو (ان کے حق میں فیصلہ ہو) تو اس کی طرف آئیں مانتے ہوئے'),
(2841, 24, 50, 'کیا ان کے دلوں میں بیماری ہے یا شک رکھتے ہیں کیا یہ ڈرتے ہیں کہ اللہ و رسول ان پر ظلم کریں گے بلکہ خود ہی ظالم ہیں،'),
(2842, 24, 51, 'مسلمانوں کی بات تو یہی ہے جب اللہ اور رسول کی طرف بلائے جائیں کہ رسول ان میں فیصلہ فرمائے کہ عرض کریں ہم نے سنا اور حکم مانا او ریہی لوگ مراد کو پہنچے،'),
(2843, 24, 52, 'اور جو حکم مانے اللہ اور اس کے رسول کا اور اللہ سے ڈرے اور پرہیزگاری کرے تو یہی لوگ کامیاب ہیں،'),
(2844, 24, 53, 'اور انہوں نے اللہ کی قسم کھائی اپنے حلف میں حد کی کوشش سے کہ اگر تم انہیں حکم دو گے تو وہ ضرور جہاد کو نکلیں گے تم فرماؤ قسمیں نہ کھاؤ موافق شرع حکم برداری چاہیے، اللہ جانتا ہے جو تم کرتے ہو'),
(2845, 24, 54, 'تم فرماؤ حکم مانو اللہ کا اور حکم مانو رسول کا پھر اگر تم منہ پھیرو تو رسول کے ذمہ وہی ہے جس اس پر لازم کیا گیا اور تم پر وہ ہے جس کا بوجھ تم پر رکھا گیا اور اگر رسول کی فرمانبرداری کرو گے راہ پاؤ گے، اور رسول کے ذمہ نہیں مگر صاف پہنچا دینا'),
(2846, 24, 55, 'اللہ نے وعدہ دیا ان کو جو تم میں سے ایمان لائے اور اچھے کام کیے کہ ضرور انہیں زمین میں خلافت دے گا جیسی ان سے پہلوں کو دی اور ضرور ان کے لیے جمادے گا ان کا وہ دین جو ان کے لیے پسند فرمایا ہے اور ضرور ان کے اگلے خوف کو امن سے بدل دے گا میری عبادت کریں میرا شریک کسی کو نہ ٹھہرائیں، اور جو اس کے بعد ناشکری کرے تو وہی لوگ بے حکم ہیں،'),
(2847, 24, 56, 'اور نماز برپا رکھو اور زکوٰة دو اور رسول کی فرمانبرداری کرو اس امید پر کہ تم پر رحم ہو،'),
(2848, 24, 57, 'ہرگز کافروں کو خیال نہ کرنا کہ وہ کہیں ہمارے قابو سے نکل جائیں زمین میں اور ان کا ٹھکانا آ گ ہے، اور ضرور کیا ہی برا انجام،'),
(2849, 24, 58, 'اے ایمان والو! چاہیے کہ تم سے اذن لیں تمہارے ہاتھ کے مال غلام اور جو تم میں ابھی جوانی کو نہ پہنچے تین وقت نمازِ صبح سے پہلے اور جب تم اپنے کپڑے اتار رکھتے ہو دوپہر کو اور نماز عشاء کے بعد یہ تین وقت تمہاری شرم کے ہیں ان تین کے بعد کچھ گناہ نہیں تم پر نہ ان پر آمدورفت رکھتے ہیں تمہارے یہاں ایک دوسرے کے پاس اللہ یونہی بیان کرتا ہے تمہارے لیے آیتیں، اور اللہ علم و حکمت والا ہے،'),
(2850, 24, 59, 'اور جب تم میں لڑکے جوانی کو پہنچ جائیں تو وہ بھی اذن مانگیں جیسے ان کے اگلوں نے اذن مانگا، اللہ یونہی بیان فرماتا ہے تم سے اپنی آیتیں، اور اللہ علم و حکمت والا ہے،'),
(2851, 24, 60, 'اور بوڑھی خانہ نشین عورتیں جنہیں نکاح کی آرزو نہیں ان پر کچھ گناہ نہیں کہ اپنے بالائی کپڑے اتار رکھیں جب کہ سنگھار نہ چمکائیں اور ان سے بھی بچنا ان کے لیے اور بہتر ہے، ور اللہ سنتا جانتا ہے،'),
(2852, 24, 61, 'نہ اندھے پر تنگی اور نہ لنگڑے پر مضائقہ اور نہ بیمار پر روک اور نہ تم میں کسی پر کہ کھاؤ اپنی اولاد کے گھر یا اپنے باپ کے گھر یا اپنی ماں کے گھر یا اپنے بھائیوں کے یہاں یا اپنی بہنوں کے گھر ے یا اپنے چچاؤں کے یہاں یا اپنی پھپیوں کے گھر یا اپنے ماموؤں کے یہاں یا اپنی خالاؤں کے گھر یا جہاں کی کنجیاں تمہارے قبضہ میں ہیں یا اپنے دوست کے یہاں تم پر کوئی الزام نہیں کہ مل کر کھاؤ یا الگ الگ پھر جب کسی گھر میں جاؤ تو اپنوں کو سلام کرو ملتے وقت کی اچھی دعا اللہ کے پاس سے مبارک پاکیزہ، اللہ یونہی بیان فرماتا ہے تم سے آیتیں کہ تمہیں سمجھ ہو،'),
(2853, 24, 62, 'ایمان والے تو وہی ہیں جو اللہ اور اس کے رسول پر یقین لائے اور جب رسول کے پاس کسی ایسے کام میں حاضر ہوئے ہوں جس کے لیے جمع کیے گئے ہوں تو نہ جائیں جب تک ان سے اجازت نہ لے لیں وہ جو تم سے اجازت مانگتے ہیں وہی ہیں جو اللہ اور اس کے رسول پر ایمان لاتے ہیں پھر جب وہ تم سے اجازت مانگیں اپنے کسی کام کے لیے تو ان میں جسے تم چاہو اجازت دے دو اور ان کے لیے اللہ سے معافی مانگو بیشک اللہ بخشنے والا مہربان ہے،'),
(2854, 24, 63, 'رسول کے پکارنے کو آپس میں ایسا نہ ٹھہرالو جیسا تم میں ایک دوسرے کو پکارتا ہے بیشک اللہ جانتا ہے جو تم میں چپکے نکل جاتے ہیں کسی چیز کی آڑ لے کر تو ڈریں وہ جو رسول کے حکم کے خلاف کرتے ہیں کہ انہیں کوئی فتنہ پہنچے یا ان پر دردناک عذاب پڑے'),
(2855, 24, 64, 'سن لو! بیشک اللہ ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے، بیشک وہ جانتا ہے جس حال پر تم ہو اور اس دن کو جس میں اس کی طرف پھیرے جائیں گے تو وہ انہیں بتادے گا جو کچھ انہوں نے کیا، اور اللہ سب کچھ جانتا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بڑی برکت والا ہے وہ کہ جس نے اتارا قرآن اپنے بندہ پر جو سارے جہان کو ڈر سنانے والا ہو'),
(2857, 25, 2, 'وہ جس کے لیے ہے آسمانوں اور زمین کی بادشاہت اور اس نے نہ اختیار فرمایا بچہ اور اس کی سلطنت میں کوئی ساجھی نہیں اس نے ہر چیز پیدا کرکے ٹھیک اندازہ پر رکھی،'),
(2858, 25, 3, 'اور لوگوں نے اس کے سوا اور خدا ٹھہرالیے کہ وہ کچھ نہیں بناتے اور خود پیدا کیے گئے ہیں اور خود اپنی جانوں کے برے بھلے کے مالک نہیں اور نہ مرنے کا اختیار نہ جینے کا نہ اٹھنے کا،'),
(2859, 25, 4, 'اور کافر بولے یہ تو نہیں مگر ایک بہتان جو انہوں نے بنالیا ہے اور اس پر اور لوگوں نے انہیں مدد دی ہے بیشک وہ ظلم اور جھوٹ پر آئے،'),
(2860, 25, 5, 'اور بولے اگلوں کی کہانیاں ہیں جو انہوں نے لکھ لی ہیں تو وہ ان پر صبح و شام پڑھی جاتی ہیں،'),
(2861, 25, 6, 'تم فرماؤ اسے تو اس نے اتارا ہے جو آسمانوں اور زمین کی ہر بات جانتا ہے بیشک وہ بخشنے والا مہربان ہے'),
(2862, 25, 7, 'اور بولے اور رسول کو کیا ہوا کھانا کھاتا ہے اور بازاروں میں چلتا ہے (ف۹۱۶ کیوں نہ اتارا گیا ان کے ساتھ کوئی فرشتہ کہ ان کے ساتھ ڈر سناتا'),
(2863, 25, 8, 'یا غیب سے انہیں کوئی خزانہ مل جاتا یا ان کا کوئی باغ ہوتا جس میں سے کھاتے اور ظالم بولے تم تو پیروی نہیں کرتے مگر ایک ایسے مرد کی جس پر جادو ہوا'),
(2864, 25, 9, 'اے محبوب دیکھو کیسی کہاوتیں تمہارے لیے بنارہے ہیں تو گمراہ ہوئے کہ اب کوئی راہ نہیں پاتے،'),
(2865, 25, 10, 'بڑی برکت والا ہے وہ کہ اگر چاہے تو تمہارے لیے بہت بہتر اس سے کردے جنتیں جن کے نیچے نہریں بہیں اور کرے گا تمہارے لیے اونچے اونچے محل،'),
(2866, 25, 11, 'بلکہ یہ تو قیامت کو جھٹلاتے ہیں، اور جو قیامت کو جھٹلائے ہم نے اس کے لیے تیار کر رکھی ہے بھڑکتی ہوئی آگ،'),
(2867, 25, 12, 'جب وہ انہیں دور جگہ سے دیکھے گی تو سنیں گے اس کا جوش مارنا اور چنگھاڑنا،'),
(2868, 25, 13, 'اور جب اس کی کسی تنگ جگہ میں ڈالے جائیں گے زنجیروں میں جکڑے ہوئے تو وہاں موت مانگیں گے'),
(2869, 25, 14, 'فرمایا جائے گا آج ایک موت نہ مانگو اور بہت سی موتیں مانگو'),
(2870, 25, 15, 'تم فرماؤ کیا یہ بھلا یا وہ ہمیشگی کے باغ جس کا وعدہ ڈر والوں کو ہے، وہ ان کا صلہ اور انجام ہے،'),
(2871, 25, 16, 'ان کے لیے وہاں من مانی مرادیں ہیں جن میں ہمیشہ رہیں گے، تمہارے رب کے ذمہ وعدہ ہے مانگا ہوا، ف۲۸)'),
(2872, 25, 17, 'اور جس دن اکٹھا کرے گا انہیں اور جن کوا لله کے سوا پوجتے ہیں پھر ان معبودو ں سے فرمائے گا کیا تم نے گمراہ کردیے یہ میرے بندے یا یہ خود ہی راہ بھولے'),
(2873, 25, 18, 'وہ عرض کریں گے پاکی ہے تجھ کو ہمیں سزاوار (حق) نہ تھا کہ تیرے سوا کسی اور کو مولیٰ بنائیں لیکن تو نے انہیں اور ان کے باپ داداؤں کو برتنے دیا یہاں تک کہ وہ تیری یاد بھول گئے اور یہ لوگ تھے ہی ہلاک ہونے والے'),
(2874, 25, 19, 'تو اب معبودوں نے تمہاری بات جھٹلادی تو اب تم نہ عذاب پھیرسکو نہ اپنی مدد کرسکو اور تم میں جو ظالم ہے ہم اسے بڑا عذاب چکھائیں گے،'),
(2875, 25, 20, 'اور ہم نے تم سے پہلے جتنے رسول بھیجے سب ایسے ہی تھے کھانا کھاتے اور بازاروں میں چلتے اور ہم نے تم میں ایک کو دوسرے کی جانچ کیا ہے اور اے لوگو! کیا تم صبر کرو گے اور اے محبوب! تمہارا رب دیکھتا ہے'),
(2876, 25, 21, 'اور بولے وہ جو ہمارے ملنے کی امید نہیں رکھتے ہم پر فرشتے کیوں نہ اتارے یا ہم اپنے رب کو دیکھتے بیشک اپنے جی میں بہت ہی اونچی کھینچی (سرکشی کی) اور بڑی سرکشی پر آئے'),
(2877, 25, 22, 'جس دن فرشتوں کو دیکھیں گے وہ دن مجرموں کی کوئی خوشی کا نہ ہوگا اور کہیں گے الٰہی ہم میں ان میں کوئی آڑ کردے رکی ہوئی'),
(2878, 25, 23, 'اور جو کچھ انہوں نے کام کیے تھے ہم نے قصد فرماکر انہیں باریک باریک غبار، کے بکھرے ہوئے ذرے کردیا کہ روزن کی دھوپ میں نظر آتے ہیں'),
(2879, 25, 24, 'جنت والوں کا اس دن اچھا ٹھکانا اور حساب کے دوپہر کے بعد اچھی آرام کی جگہ،'),
(2880, 25, 25, 'اور جس دن پھٹ جائے گا آسمان بادلوں سے اور فرشتے اتارے جائیں گے پوری طرح'),
(2881, 25, 26, 'اس دن سچی بادشاہی رحمن کی ہے، اور وہ دن کافروں پر سخت ہے'),
(2882, 25, 27, 'اور جس دن ظالم اپنے ہاتھ چبا چبا لے گا کہ ہائے کسی طرح سے میں نے رسول کے ساتھ راہ لی ہوتی،'),
(2883, 25, 28, 'وائے خرابی میری ہائے کسی طرح میں نے فلانے کو دوست نہ بنایا ہوتا،'),
(2884, 25, 29, 'بیشک اس نے مجھے بہکادیا میرے پاس آئی ہوئی نصیحت سے اور شیطان آدمی کو بے مدد چھوڑ دیتا ہے'),
(2885, 25, 30, 'اور رسول نے عرض کی کہ اے میرے رب! میری قوم نے اس قرآن کو چھوڑنے کے قابل ٹھہرایا'),
(2886, 25, 31, 'اور اسی طرح ہم نے ہر نبی کے لیے دشمن بنادیے تھے مجرم لوگ اور تمہارا رب کافی ہے ہدایت کرنے اور مدد دینے کو،'),
(2887, 25, 32, 'اور کافر بولے قرآن ان پر ایک ساتھ کیوں نہ اتار دیا ہم نے یونہی بتدریج سے اتارا ہے کہ اس سے تمہارا دل مضبوط کریں اور ہم نے اسے ٹھہر ٹھہر کر پڑھا'),
(2888, 25, 33, 'اور وہ کوئی کہاوت تمہارے پاس نہ لائیں گے مگر ہم حق اور اس سے بہتر بیان لے آئیں گے،'),
(2889, 25, 34, 'وہ جو جہنم کی طرف ہانکے جائیں گے اپنے منہ کے بل ان کا ٹھکانا سب سے برا اور وہ سب سے گمراہ،'),
(2890, 25, 35, 'اور بیشک ہم نے موسیٰ کو کتاب عطا فرمائی اور اس کے بھائی ہارون کو وزیر کیا،'),
(2891, 25, 36, 'تو ہم نے فرمایا تم دونوں جاؤ اس قوم کی طرف جس نے ہماری آیتیں جھٹلائیں پھر ہم نے انہیں تباہ کرکے ہلاک کردیا،'),
(2892, 25, 37, 'اور نوح کی قوم کو جب انہوں نے رسولوں کو جھٹلایا ہم نے ان کو ڈبو دیا اور انہیں لوگوں کے لیے نشانی کردیا اور ہم نے ظالموں کے لیے دردناک عذاب تیار کر رکھا ہے،'),
(2893, 25, 38, 'اور عاد اور ثمود اور کنوئیں والوں کو اور ان کے بیچ میں بہت سی سنگتیں (قومیں)'),
(2894, 25, 39, 'اور ہم نے سب سے مثالیں بیان فرمائیں اور سب کو تباہ کرکے مٹادیا،'),
(2895, 25, 40, 'اور ضرور یہ ہو آئے ہیں اس بستی پر جس پر برا برساؤ برسا تھا تو کیا یہ اسے دیکھتے نہ تھے بلکہ انہیں جی اٹھنے کی امید تھی ہی نہیں'),
(2896, 25, 41, 'اور جب تمہیں دیکھتے ہیں تو تمہیں نہیں ٹھہراتے مگر ٹھٹھا کیا یہ ہیں جن کو اللہ نے رسول بناکر بھیجا،'),
(2897, 25, 42, 'قریب تھا کہ یہ ہمیں ہمارے خداؤں سے بہکادیں اگر ہم ان پر صبر نہ کرتے اور اب جانا چاہتے ہیں جس دن عذاب دیکھیں گے کہ کون گمراہ تھا'),
(2898, 25, 43, 'کیا تم نے اسے دیکھا جس نے اپنی جی کی خواہش کو اپنا خدا بنالیا تو کیا تم اس کی نگہبانی کا ذمہ لو گے'),
(2899, 25, 44, 'یا یہ سمجھتے ہو کہ ان میں بہت کچھ سنتے یا سمجھتے ہیں وہ تو نہیں مگر جیسے چوپائے بلکہ ان سے بھی بدتر گمراہ'),
(2900, 25, 45, 'اے محبوب! کیا تم نے اپنے رب کو نہ دیکھا کہ کیسا پھیلا سایہ اور اگر چاہتا تو اسے ٹھہرایا ہوا کردیتا پھر ہم نے سورج کو اس پر دلیل کیا،'),
(2901, 25, 46, 'پھر ہم نے آہستہ آہستہ اسے اپنی طرف سمیٹا'),
(2902, 25, 47, 'اور وہی ہے جس نے رات کو تمہارے لیے پردہ کیا اور نیند کو آرام اور دن بنایا اٹھنے کے لیے'),
(2903, 25, 48, 'اور وہی ہے جس نے ہوائیں بھیجیں اپنی رحمت کے آگے مژدہ سنائی ہوئی اور ہم نے آسمان سے پانی اتارا پاک کرنے والا،'),
(2904, 25, 49, 'تاکہ ہم ا سسے زندہ کریں کسی مردہ شہر کو اور اسے پلائیں اپنے بنائے ہوئے بہت سے چوپائے اور آدمیوں کو،'),
(2905, 25, 50, 'اور بیشک ہم نے ان میں پانی کے پھیرے رکھے کہ وہ دھیان کریں تو بہت لوگوں نے نہ مانا مگر ناشکری کرنا،'),
(2906, 25, 51, 'اور ہم چاہتے تو ہر بستی میں ایک ڈر سنانے والا بھیجتے'),
(2907, 25, 52, 'تو کافروں کا کہا نہ مان اور اس قرآن سے ان پر جہاد کر بڑا جہاد،'),
(2908, 25, 53, 'اور وہی ہے جس نے ملے ہوئے رواں کیے دو سمندر یہ میٹھا ہے نہایت شیریں اور یہ کھاری ہے نہایت تلخ اور ان کے بیچ میں پردہ رکھا اور روکی ہوئی آڑ'),
(2909, 25, 54, 'اور وہی ہے جس نے پانی سے بنایا آدمی پھر اس کے رشتے اور سسرال مقرر کی اور تمہارا رب قدرت والا ہے'),
(2910, 25, 55, 'اور اللہ کے سوا ایسوں کو پوجتے ہیں جو ان کا بھلا برا کچھ نہ کریں اور کافر اپنے رب کے مقابل شیطان کو مدد دیتا ہے'),
(2911, 25, 56, 'اور ہم نے تمہیں نہ بھیجا مگر خوشی اور ڈر سناتا،'),
(2912, 25, 57, 'تم فرماؤ میں اس پر تم سے کچھ اجرت نہیں مانگتا مگر جو چاہے کہ اپنے رب کی طرف راہ لے،'),
(2913, 25, 58, 'اور بھروسہ کرو اس زندہ پر جو کبھی نہ مرے گا اور اسے سراہتے ہوئے اس کی پاکی بولو اور وہی کافی ہے اپنے بندوں کے گناہوں پر خبردار'),
(2914, 25, 59, 'جس نے آسمان اور زمین اور جو کچھ ان کے درمیان ہے چھ دن میں بنائے پھر عرش پر استواء فرمایا جیسا کہ اس کی شان کے لائق ہے وہ بڑی مہر والا تو کسی جاننے والے سے اس کی تعریف پوچھ'),
(2915, 25, 60, 'اور جب ان سے کہا جائے رحمن کو سجدہ کرو کہتے ہیں رحمن کیا ہے، کیا ہم سجدہ کرلیں جسے تم کہو اور اس حکم نے انہیں اور بدکنا بڑھایا السجدة ۔۷'),
(2916, 25, 61, 'بڑی برکت والا ہے وہ جس نے آسمان میں برج بنائے اور ان میں چراغ رکھا اور چمکتا چاند،'),
(2917, 25, 62, 'اور وہی ہے جس نے رات اور دن کی بدلی رکھی اس کے لیے جو دھیان کرنا چاہے یا شکر کا ارادہ کرے،'),
(2918, 25, 63, 'اور رحمن کے وہ بندے کہ زمین پر آہستہ چلتے ہیں اور جب جاہل ان سے بات کرتے ہیں تو کہتے ہیں بس سلام'),
(2919, 25, 64, 'اور وہ جو رات کاٹتے ہیں اپنے رب کے لیے سجدے اور قیام میں'),
(2920, 25, 65, 'اور وہ جو عرض کرتے ہیں اے ہمارے رب! ہم سے پھیر دے جہنم کا عذاب، بیشک اس کا عذاب گلے کا غل (پھندا) ہے'),
(2921, 25, 66, 'بیشک وہ بہت ہی بری ٹھہرنے کی جگہ ہے،'),
(2922, 25, 67, 'اور وہ کہ جب خرچ کرتے ہیں نہ حد سے بڑھیں اور نہ تنگی کریں اور ان دونوں کے بیچ اعتدال پر رہیں'),
(2923, 25, 68, 'اور وہ جو اللہ کے ساتھ کسی دوسرے معبود کو نہیں پوجتے اور اس جان کو جس کی اللہ نے حرمت رکھی ناحق نہیں مارتے اور بدکاری نہیں کرتے اور جو یہ کام کرے وہ سزا پائے گا،'),
(2924, 25, 69, 'بڑھایا جائے گا اس پر عذابِ قیامت کے دن اور ہمیشہ اس میں ذلت سے رہے گا،'),
(2925, 25, 70, 'مگر جو توبہ کرے اور ایمان لائے اور اچھا کام کرے اور اچھا کام کرے تو ایسوں کی برائیوں کو اللہ بھلائیوں سے بدل دے گا اور اللہ بخشنے والا مہربان ہے،'),
(2926, 25, 71, 'اور جو توبہ کرے اور اچھا کام کرے تو وہ اللہ کی طرف رجوع لایا جیسی چاہیے تھی،'),
(2927, 25, 72, 'اور جو جھوٹی گواہی نہیں دیتے اور جب بیہودہ پر گذرتے ہیں اپنی عزت سنبھالے گزر جاتے ہیں،'),
(2928, 25, 73, 'اور وہ کہ جب کہ انہیں ان کے رب کی آیتیں یاد د لائی جائیں تو ان پر بہرے اندھے ہوکر نہیں گرتے'),
(2929, 25, 74, 'اور وہ جو عرض کرتے ہیں، اے ہمارے رب! ہمیں دے ہماری بیبیوں اور اولاد سے آنکھوں کی ٹھنڈک اور ہمیں پرہیزگاروں کا پیشوا بنا'),
(2930, 25, 75, 'ان کو جنت کا سب سے اونچا بالا خانہ انعام ملے گا بدلہ ان کے صبر کا اور وہاں مجرے اور سلام کے ساتھ ان کی پیشوائی ہوگی'),
(2931, 25, 76, 'ہمیشہ اس میں رہیں گے، کیا ہی اچھی ٹھہرنے اور بسنے کی جگہ،'),
(2932, 25, 77, 'تم فرماؤ تمہاری کچھ قدر نہیں میرے رب کے یہاں اگر تم اسے نہ پوجو تو تم نے تو جھٹلایا تو اب ہوگا وہ عذاب کہ لپٹ رہے گا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طٰسم'),
(2934, 26, 2, 'یہ آیتیں ہیں روشن کتا ب کی'),
(2935, 26, 3, 'کہیں تم اپنی جان پر کھیل جاؤ گے ان کے غم میں کہ وہ ایمان نہیں لائے'),
(2936, 26, 4, 'اگر ہم چاہیں تو آسمان سے ان پر کوئی نشانی اتاریں کہ ان کے اونچے اونچے اس کے حضور جھکے رہ جائیں'),
(2937, 26, 5, 'اور نہیں آتی ان کے پاس رحمٰان کی طرف سے کوئی نئی نصیحت مگر اس سے منہ پھیر لیتے ہیں'),
(2938, 26, 6, 'تو بیشک انہوں نے جھٹلایا تو اب ان پر آیا چاہتی ہیں خبریں ان کے ٹھٹھے کی'),
(2939, 26, 7, 'کیا انہوں نے زمین کو نہ دیکھا ہم نے اس میں کتنے عزت والے جوڑے اگائے'),
(2940, 26, 8, 'بیشک اس میں ضرور نشانی ہے اور ان کے اکثر ایمان لانے والے نہیں،'),
(2941, 26, 9, 'اور بیشک تمہارا رب ضرور وہی عزت والا مہربان ہے'),
(2942, 26, 10, 'اور یاد کرو جب تمہارے رب نے موسیٰ کو ندا فرمائی کہ ظالم لوگوں کے پاس جا،'),
(2943, 26, 11, 'جو فرعون کی قوم ہے کیا وہ نہ ڈریں گے'),
(2944, 26, 12, 'عرض کی کہ اے میرے رب میں ڈرتا ہوں کہ مجھے جھٹلا ئیں گے،'),
(2945, 26, 13, 'اور میرا سینہ تنگی کرتا ہے اور میری زبان نہیں چلتی تو توُ ہا رون کو بھی رسول کر،'),
(2946, 26, 14, 'اور ان کا مجھ پر ایک الزام ہے تو میں ڈرتا ہو ں کہیں مجھے کر دیں،'),
(2947, 26, 15, 'فرما یا یوں نہیں تم دو نوں میری آئتیں لے کر جا ؤ ہم تمھا رے ساتھ سنتے ہیں'),
(2948, 26, 16, 'تو فرعون کے پاس جاؤ پھر اس سے کہو ہم دونو ں اسکے رسل ہیں جو رب ہے سارے جہا ں کا،'),
(2949, 26, 17, 'کہ تو ہما رے ساتھ بنی اسرائیل کو چھوڑ دے'),
(2950, 26, 18, 'بو لا کیا ہم نے تمھیں اپنے یہاں بچپن میں نہ پالا اور تم نے ہما رے یہا ں اپنی عمر کے کئی برس گزارے، ف۲۰)'),
(2951, 26, 19, 'اور تم نے کیا اپنا وہ کام جو تم نے کیا اور تم نا شکر تھے'),
(2952, 26, 20, 'موسٰی نے فر ما یا میں نے وہ کام کیا جب کہ مجھے راہ کی خبر نہ تھی'),
(2953, 26, 21, 'تو میں تمھا رے یہا ں سے نکل گیا جب کے تم سے ڈرا تو میرے رب نے مجھے حکم عطا فرمایا اور مجھے پیغمبروں سے کیا،'),
(2954, 26, 22, 'اور یہ کوئی نعمت ہے جس کا تو مجھ پر احسان جتاتا ہے کہ تو نے غَلام بناکر رکھے بنی اسرائیل'),
(2955, 26, 23, 'فرعون بولا اور سارے جہان کا رب کیا ہے'),
(2956, 26, 24, 'موسیٰ نے فرمایا رب آسمانوں اور زمین کا اور جو کچھ ان کے درمیان میں ہے، اگر تمہیں یقین ہو'),
(2957, 26, 25, 'اپنے آس پاس والوں سے بولا کیا تم غور سے سنتے نہیں'),
(2958, 26, 26, 'موسیٰ نے فرمایا رب تمہارا اور تمہارے اگلے باپ داداؤں کا'),
(2959, 26, 27, 'بولا تمہارے یہ رسول جو تمہاری طرف بھیجے گئے ہیں ضرور عقل نہیں رکھتے'),
(2960, 26, 28, 'موسیٰ نے فرمایا رب پورب (مشرق) اور پچھم (مغرب) کا اور جو کچھ ان کے درمیان ہے اگر تمہیں عقل ہو'),
(2961, 26, 29, 'بولا اگر تم نے میرے سوا کسی اور کو خدا ٹھہرایا تو میں ضرور تمہیں قید کردوں گا'),
(2962, 26, 30, 'فرمایا کیا اگرچہ میں تیرے پاس کوئی روشن چیز لاؤں'),
(2963, 26, 31, 'کہا تو لاؤ اگر سچے ہو،'),
(2964, 26, 32, 'تو موسیٰ نے اپنا عصا ڈال دیا جبھی وہ صریح اژدہا ہوگیا'),
(2965, 26, 33, 'اور اپنا ہاتھ نکالا تو جبھی وہ دیکھنے والوں کی نگاہ میں جگمگانے لگا'),
(2966, 26, 34, 'بولا اپنے گرد کے سرداروں سے کہ بیشک یہ دانا جادوگر ہیں،'),
(2967, 26, 35, 'چاہتے ہیں، کہ تمہیں تمہارے ملک سے نکال دیں اپنے جادو کے زور سے، تب تمہارا کیا مشورہ ہے'),
(2968, 26, 36, 'وہ بولے انہیں ان کے بھائی کو ٹھہرائے رہو اور شہروں میں جمع کرنے والے بھیجو،'),
(2969, 26, 37, 'کہ وہ تیرے پاس لے آئیں ہر بڑے جادوگر دانا کو'),
(2970, 26, 38, 'تو جمع کیے گئے جادوگر ایک مقرر دن کے وعدے پر'),
(2971, 26, 39, 'اور لوگوں سے کہا گیا کیا تم جمع ہوگئے'),
(2972, 26, 40, 'شاید ہم ان جادوگروں ہی کی پیروی کریں اگر یہ غالب آئیں'),
(2973, 26, 41, 'پھر جب جادوگر آئے فرعون سے بولے کیا ہمیں کچھ مزدوری ملے گی اگر ہم غالب آئے،'),
(2974, 26, 42, 'بولا ہاں اور اس وقت تم میرے مقرب ہوجاؤ گے'),
(2975, 26, 43, 'موسیٰ نے ان سے فرمایا ڈالو جو تمہیں ڈالنا ہے'),
(2976, 26, 44, 'تو انہوں نے اپنی رسیاں اور لاٹھیاں ڈالیں اور بولے فرعون کی عزت کی قسم بیشک ہماری ہی جیت ہے،'),
(2977, 26, 45, 'تو موسیٰ نے اپنا عصا ڈالا جبھی وہ ان کی بناوٹوں کو نگلنے لگا'),
(2978, 26, 46, 'اب سجدہ میں گرے،'),
(2979, 26, 47, 'جادوگر، بولے ہم ایمان لائے اس پر جو سارے جہان کا رب ہے،'),
(2980, 26, 48, 'جو موسیٰ اور ہارون کا رب ہے،'),
(2981, 26, 49, 'فرعون بولا کیا تم اس پر ایمان لائے قبل اس کے کہ میں تمہیں اجازت دوں، بیشک وہ تمہارا بڑا ہے جس نے تمہیں جادو سکھایا تو اب جاننا چاہتے ہو مجھے قسم ہے! بیشک میں تمہارے ہاتھ اور دوسری طرف کے پاؤں کاٹوں گا اور تم سب کو سولی دوں گا'),
(2982, 26, 50, 'وہ بولے کچھ نقصان نہیں ہم اپنے رب کی طرف پلٹنے والے ہیں'),
(2983, 26, 51, 'ہمیں طمع ہے کہ ہمارا رب ہماری خطائیں بخش دے اس پر کہ سب سے پہلے ایمان لائے'),
(2984, 26, 52, 'اور ہم نے موسٰی کو وحی بھیجی کہ راتوں را ت میرے بندو ں کو لے نکل بیشک تمھارا پیچھا ہو نا ہے، ف۵۵)'),
(2985, 26, 53, 'اب فرعون نے شہروں میں جمع کرنے والے بھیجے'),
(2986, 26, 54, 'کہ یہ لوگ ایک تھوڑی جماعت ہیں،'),
(2987, 26, 55, 'اور بیشک ہم سب کا دل جلاتے ہیں'),
(2988, 26, 56, 'اور بیشک ہم سب چوکنے ہیں'),
(2989, 26, 57, 'تو ہم نے انہیں باہر نکالا باغوں اور چشموں،'),
(2990, 26, 58, 'اور خزانوں اور عمدہ مکانوں سے،'),
(2991, 26, 59, 'ہم نے ایسا ہی کیا اور ان کا وارث کردیا بنی اسرائیل کو'),
(2992, 26, 60, 'تو فرعونیوں نے ان کا تعاقب کیا دن نکلے،'),
(2993, 26, 61, 'پھر جب آمنا سامنا ہوا دونوں گروہوں کا موسیٰ والوں نے کہا ہم کو انہوں نے آلیا'),
(2994, 26, 62, 'موسیٰ نے فرمایا یوں نہیں بیشک میرا رب میرے ساتھ ہے وہ مجھے اب راہ دیتا ہے،'),
(2995, 26, 63, 'تو ہم نے موسیٰ کو وحی فرمائی کہ دریا پر اپنا عصا مار تو جبھی دریا پھٹ گیا تو ہر حصہ ہوگیا جیسے بڑا پہاڑ'),
(2996, 26, 64, 'اور وہاں قریب لائے ہم دوسروں کو'),
(2997, 26, 65, 'اور ہم نے بچالیا موسیٰ اور اس کے سب ساتھ والوں کو'),
(2998, 26, 66, 'پھر دوسروں کو ڈبو دیا'),
(2999, 26, 67, 'بیشک اس میں ضرور نشانی ہے اور ان میں اکثر مسلمان نہ تھے'),
(3000, 26, 68, 'اور بیشک تمہارا رب وہی عزت والا مہربان ہے'),
(3001, 26, 69, 'اور ان پر پڑھو خبر ابراہیم کی'),
(3002, 26, 70, 'جب اس نے اپنے باپ اور اپنی قوم سے فرمایا تم کیا پوجتے ہو'),
(3003, 26, 71, 'بولے ہم بتوں کو پوجتے ہیں پھر ان کے سامنے آسن مارے رہتے ہیں،'),
(3004, 26, 72, 'فرمایا کیا وہ تمہاری سنتے ہیں جب تم پکارو،'),
(3005, 26, 73, 'یا تمہارا کچھ بھلا برا کرتے ہیں'),
(3006, 26, 74, 'بولے بلکہ ہم نے اپنے باپ دادا کو ایسا ہی کرتے پایا،'),
(3007, 26, 75, 'فرمایا تو کیا تم دیکھتے ہو یہ جنہیں پوج رہے ہو،'),
(3008, 26, 76, 'تم اور تمہارے اگلے باپ دادا'),
(3009, 26, 77, 'بیشک وہ سب میرے دشمن ہیں مگر پروردگار عالم'),
(3010, 26, 78, 'وہ جس نے مجھے پیدا کیا تو وہ مجھے راہ دے گا'),
(3011, 26, 79, 'اور وہ جو مجھے کھلاتا اور پلاتا ہے'),
(3012, 26, 80, 'اور جب میں بیمار ہوں تو وہی مجھے شفا دیتا ہے'),
(3013, 26, 81, 'اور وہ مجھے وفات دے گا پھر مجھے زندہ کرے گا'),
(3014, 26, 82, 'اور وہ جس کی مجھے آس لگی ہے کہ میری خطائیں قیامت کے دن بخشے گا'),
(3015, 26, 83, 'اے میرے رب مجھے حکم عطا کر اور مجھے ان سے ملادے جو تیرے قرب خاص کے سزاوار ہیں'),
(3016, 26, 84, 'اور میری سچی ناموری رکھ پچھلوں میں'),
(3017, 26, 85, 'اور مجھے ان میں کر جو چین کے باغوں کے وارث ہیں'),
(3018, 26, 86, 'اور میرے باپ کو بخش دے بیشک وہ گمراہ،'),
(3019, 26, 87, 'اور مجھے رسوا نہ کرنا جس دن سب اٹھائے جائیں گے'),
(3020, 26, 88, 'جس دن نہ مال کام آئے گا نہ بیٹے،'),
(3021, 26, 89, 'مگر وہ جو اللہ کے حضور حاضر ہوا سلامت دل لے کر'),
(3022, 26, 90, 'اور قریب لائی جائے گی جنت پرہیزگاروں کے لیے'),
(3023, 26, 91, 'اور ظاہر کی جائے گی دوزخ گمراہوں کے لیے،'),
(3024, 26, 92, 'اور ان سے کہا جائے گا کہاں ہیں وہ جن کو تم پوجتے تھے،'),
(3025, 26, 93, 'اللہ کے سوا، کیا وہ تمہاری مدد کریں گے یا بدلہ لیں گے،'),
(3026, 26, 94, 'تو اوندھا دیے گئے جہنم میں وہ اور سب گمراہ'),
(3027, 26, 95, 'اور ابلیس کے لشکر سارے'),
(3028, 26, 96, 'کہیں گے اور وہ اس میں باہم جھگڑے ہوں گے،'),
(3029, 26, 97, 'خدا کی قسم بیشک ہم کھلی گمراہی میں تھے،'),
(3030, 26, 98, 'جبکہ انہیں رب العالمین کے برابر ٹھہراتے تھے،'),
(3031, 26, 99, 'اور ہمیں نہ بہکایا مگر مجرموں نے'),
(3032, 26, 100, 'تو اب ہمارا کوئی سفارشی نہیں'),
(3033, 26, 101, 'اور نہ کوئی غم خوار دوست'),
(3034, 26, 102, 'تو کسی طرح ہمیں پھر جانا ہوتا کہ ہم مسلمان ہوجاتے،'),
(3035, 26, 103, 'بیشک اس میں ضرور نشانی ہے، اور ان میں بہت ایمان والے نہ تھے،'),
(3036, 26, 104, 'اور بیشک تمہارا رب وہی عزت والا مہربان ہے،'),
(3037, 26, 105, 'نوح کی قوم نے پیغمبروں کو جھٹلایا'),
(3038, 26, 106, 'جبکہ ان سے ان کے ہم قوم نوح نے کہا کیا تم ڈرتے نہیں'),
(3039, 26, 107, 'بیشک میں تمہارے لیے اللہ کا بھیجا ہوا امین ہوں'),
(3040, 26, 108, 'تو اللہ سے ڈرو اور میرا حکم مانو'),
(3041, 26, 109, 'اور میں اس پر تم سے کچھ اجرت نہیں مانگتا میرا اجر تو اسی پر ہے جو سارے جہان کا رب ہے،'),
(3042, 26, 110, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3043, 26, 111, 'بولے کیا ہم تم پر ایمان لے آئیں اور تمہارے ساتھ کمینے ہو ےٴ ہیں'),
(3044, 26, 112, 'فرمایا مجھے کیا خبر ان کے کام کیا ہیں'),
(3045, 26, 113, 'ان کا حساب تو میرے رب ہی پر ہے اگر تمہیں حِس ہو'),
(3046, 26, 114, 'اور میں مسلمانوں کو دور کرنے والا نہیں'),
(3047, 26, 115, 'میں تو نہیں مگر صاف ڈر سنانے والا'),
(3048, 26, 116, 'بولے اے نوح! اگر تم باز نہ آئے تو ضرور سنگسار کیے جاؤ گے'),
(3049, 26, 117, 'عرض کی اے میرے رب میری قوم نے مجھے جھٹلایا'),
(3050, 26, 118, 'تو مجھ میں اور ان میں پورا فیصلہ کردے اور مجھے اور میرے ساتھ والے مسلمانوں کو نجات دے'),
(3051, 26, 119, 'تو ہم نے بچالیا اسے اور اس کے ساتھ والوں کو بھری ہوئی کشتی میں'),
(3052, 26, 120, 'پھر اس کے بعد ہم نے باقیوں کو ڈبو دیا،'),
(3053, 26, 121, 'بیشک اس میں ضرور نشانی ہے، اور ان میں اکثر مسلمان نہ تھے،'),
(3054, 26, 122, 'اور بیشک تمہارا رب ہی عزت والا مہربان ہے،'),
(3055, 26, 123, 'عاد نے رسولوں کو جھٹلایا'),
(3056, 26, 124, 'جبکہ ان سے ان کے ہم قوم ہود نے فرمایا کیا تم ڈرتے نہیں،'),
(3057, 26, 125, 'بیشک میں تمہارے لیے امانتدار رسول ہوں،'),
(3058, 26, 126, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3059, 26, 127, 'اور میں تم سے اس پر کچھ اجرت نہیں مانگتا، میرا اجر تو اسی پر ہے جو سارے جہان کا رب،'),
(3060, 26, 128, 'کیا ہر بلندی پر ایک نشان بناتے ہو راہ گیروں سے ہنسنے کو'),
(3061, 26, 129, 'اور مضبوط محل چنتے ہو اس امید پر کہ تم ہمیشہ رہوگے'),
(3062, 26, 130, 'اور جب کسی پر گرفت کرتے ہو تو بڑی بیدردی سے گرفت کرتے ہو'),
(3063, 26, 131, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3064, 26, 132, 'اور اس سے ڈرو جس نے تمہاری مدد کی ان چیزوں سے کہ تمہیں معلوم ہیں'),
(3065, 26, 133, 'تمہاری مدد کی چوپایوں اور بیٹوں،'),
(3066, 26, 134, 'اور باغوں اور چشموں سے،'),
(3067, 26, 135, 'بیشک مجھے تم پر ڈر ہے ایک بڑے دن کے عذاب کا'),
(3068, 26, 136, 'بولے ہمیں برابر ہے چاہے تم نصیحت کرو یا ناصحوں میں نہ ہو'),
(3069, 26, 137, 'یہ تو نہیں مگر وہی اگلوں کی ریت'),
(3070, 26, 138, 'اور ہمیں عذاب ہونا نہیں'),
(3071, 26, 139, 'تو انہوں نے اسے جھٹلایا تو ہم نے انہیں بلاک کیا بیشک اس میں ضرور نشانی ہے، اور ان میں بہت مسلمان نہ تھے،'),
(3072, 26, 140, 'اور بیشک تمہارا رب ہی عزت والا مہربان ہے،'),
(3073, 26, 141, 'ثمود نے رسولوں کو جھٹلایا،'),
(3074, 26, 142, 'جبکہ ان سے ان کے ہم قوم صا لح نے فرمایا کیا ڈرتے نہیں،'),
(3075, 26, 143, 'بیشک میں تمہارے لیے اللہ کا امانتدار رسول ہوں،'),
(3076, 26, 144, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3077, 26, 145, 'اور میں تم سے کچھ اس پر اجرت نہیں مانگتا، میرا اجر تو اسی پر ہے جو سارے جہان کا رب ہے،'),
(3078, 26, 146, 'کیا تم یہاں کی نعمتوں میں چین سے چھوڑ دیے جاؤ گے'),
(3079, 26, 147, 'باغوں اور چشموں،'),
(3080, 26, 148, 'اور کھیتوں اور کھجوروں میں جن کا شگوفہ نرم نازک،'),
(3081, 26, 149, 'اور پہاڑوں میں سے گھر تراشتے ہو استادی سے'),
(3082, 26, 150, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3083, 26, 151, 'اور حد سے بڑھنے والوں کے کہنے پر نہ چلو'),
(3084, 26, 152, 'وہ جو زمین میں فساد پھیلاتے ہیں اور بناؤ نہیں کرتے'),
(3085, 26, 153, 'بولے تم پر تو جادو ہوا ہے'),
(3086, 26, 154, 'تم تو ہمیں جیسے آدمی ہو، تو کوئی نشانی لاؤ اگر سچے ہو'),
(3087, 26, 155, 'فرمایا یہ ناقہ ہے ایک دن اس کے پینے کی باری اور ایک معین دن تمہاری باری،'),
(3088, 26, 156, 'اور اسے برائی کے ساتھ نہ چھوؤ کہ تمہیں بڑے دن کا عذاب آلے گا'),
(3089, 26, 157, 'اس پر انہوں نے اس کی کونچیں کاٹ دیں پھر صبح کو پچھتاتے رہ گئے'),
(3090, 26, 158, 'تو انہیں عذاب نے آلیا بیشک اس میں ضرور نشانی ہے، اور ان میں بہت مسلمان نہ تھے،'),
(3091, 26, 159, 'اور بیشک تمہارا رب ہی عزت والا مہربان ہے،'),
(3092, 26, 160, 'لوط کی قوم نے رسولوں کو جھٹلایا،'),
(3093, 26, 161, 'جب کہ ان سے ان کے ہم قوم لوط نے فرمایا کیا تم نہیں ڈرتے،'),
(3094, 26, 162, 'بیشک میں تمہارے لیے اللہ کا امانتدار رسول ہوں،'),
(3095, 26, 163, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3096, 26, 164, 'اور میں اس پر تم سے کچھ اجرت نہیں مانگتا میرا اجر تو اسی پر ہے جو سارے جان کا رب ہے،'),
(3097, 26, 165, 'کیا مخلوق میں مردوں سے بدفعلی کرتے ہو'),
(3098, 26, 166, 'اور چھوڑتے ہو وہ جو تمہارے لیے تمہارے رب نے جوروئیں بنائیں، بلکہ تم لوگ حد سے بڑھنے والے ہو'),
(3099, 26, 167, 'بولے اے لوط! اگر تم باز نہ آئے تو ضرور نکال دیے جاؤ گے'),
(3100, 26, 168, 'فرمایا میں تمہارے کام سے بیزار ہوں'),
(3101, 26, 169, 'اے میرے رب! مجھے اور میرے گھر والوں کو ان کے کام سے بچا'),
(3102, 26, 170, 'تو ہم نے اسے اور اس کے سب گھر والوں کو نجات بخشی'),
(3103, 26, 171, 'مگر ایک بڑھیا کہ پیچھے رہ گئی'),
(3104, 26, 172, 'پھر ہم نے دوسروں کو ہلاک کردیا،'),
(3105, 26, 173, 'اور ہم نے ان پر ایک برساؤ برسایا تو کیا ہی برا برساؤ تھا ڈرائے گئیوں کا،'),
(3106, 26, 174, 'بیشک اس میں ضرور نشانی ہے، اور ان میں بہت مسلمان نہ تھے،'),
(3107, 26, 175, 'اور بیشک تمہارا رب ہی عزت والا مہربان ہے،'),
(3108, 26, 176, 'بن والوں نے رسولوں کو جھٹلایا'),
(3109, 26, 177, 'جب ان سے شعیب نے فرمایا کیا ڈرتے نہیں،'),
(3110, 26, 178, 'بیشک میں تمہارے لیے اللہ کا امانتدار رسول ہوں،'),
(3111, 26, 179, 'تو اللہ سے ڈرو اور میرا حکم مانو،'),
(3112, 26, 180, 'اور میں اس پر تم سے کچھ اجرت نہیں مانگتا میرا اجر تو اسی پر ہے جو سارے جہان کا رب ہے'),
(3113, 26, 181, 'ناپ پورا کرو اور گھٹانے والوں میں نہ ہو'),
(3114, 26, 182, 'اور سیدھی ترازو سے تولو،'),
(3115, 26, 183, 'اور لوگوں کی چیزیں کم کرکے نہ دو اور زمین میں فساد پھیلاتے نہ پھرو'),
(3116, 26, 184, 'اور اس سے ڈرو جس نے تم کو پیدا کیا اور اگلی مخلوق کو،'),
(3117, 26, 185, 'بولے تم پر جادو ہوا ہے،'),
(3118, 26, 186, 'تم تو نہیں مگر ہم جیسے آدمی اور بیشک ہم تمہیں جھوٹا سمجھتے ہیں،'),
(3119, 26, 187, 'تو ہم پر آسمان کا کوئی ٹکڑا گرا دو اگر تم سچے ہو'),
(3120, 26, 188, 'فرمایا میرا رب خوب جانتا ہے جو تمہارے کوتک (کرتوت) ہیں'),
(3121, 26, 189, 'تو انہوں نے اسے جھٹلایا تو انہیں شامیانے والے دن کے عذاب نے آلیا، بیشک وہ بڑے دن کا عذاب تھا'),
(3122, 26, 190, 'بیشک اس میں ضرور نشانی ہے، اور ان میں بہت مسلمان نہ تھے،'),
(3123, 26, 191, 'اور بیشک تمہارار ب ہی عزت والا مہربان ہے،'),
(3124, 26, 192, 'اور بیشک یہ قرآن رب العالمین کا اتارا ہوا ہے،'),
(3125, 26, 193, 'اسے روح الا مین لے کر اترا'),
(3126, 26, 194, 'تمہارے دل پر کہ تم ڈر سناؤ،'),
(3127, 26, 195, 'روشن عربی زبان میں،'),
(3128, 26, 196, 'اور بیشک اس کا چرچا اگلی کتابوں میں ہے'),
(3129, 26, 197, 'اور کیا یہ ان کے لیے نشانی نہ تھی کہ اس نبی کو جانتے ہیں بنی اسرائیل کے عالم'),
(3130, 26, 198, 'اور اگر ہم اسے کسی غیر عربی شخص پر اتارتے،'),
(3131, 26, 199, 'کہ وہ انہیں پڑھ کر سناتا جب بھی اس پر ایمان نہ لاتے'),
(3132, 26, 200, 'ہم نے یونہی جھٹلانا پیرا دیا ہے مجرموں کے دلوں میں'),
(3133, 26, 201, 'وہ اس پر ایمان نہ لائیں گے یہاں تک کہ دیکھیں دردناک عذاب،'),
(3134, 26, 202, 'تو وہ اچانک ان پر آجائے گا اور انہیں خبر نہ ہوگی،'),
(3135, 26, 203, 'تو کہیں گے کیا ہمیں کچھ مہلت ملے گی'),
(3136, 26, 204, 'تو کیا ہمارے عذاب کی جلدی کرتے ہیں،'),
(3137, 26, 205, 'بھلا دیکھو تو اگر کچھ برس ہم انہیں برتنے دیں'),
(3138, 26, 206, 'پھر آئے ان پر جس کا وہ وعدہ دیے جاتے ہیں'),
(3139, 26, 207, 'تو کیا کام آئے گا ان کے وہ جو برتتے تھے'),
(3140, 26, 208, 'اور ہم نے کوئی بستی ہلاک نہ کی جسے ڈر سنانے والے نہ ہوں،'),
(3141, 26, 209, 'نصیحت کے لیے، اور ہم ظلم نہیں کرتے'),
(3142, 26, 210, 'او راس قرآن کو لے کر شیطان نہ اترے'),
(3143, 26, 211, 'اور وہ اس قابل نہیں اور نہ وہ ایسا کرسکتے ہیں'),
(3144, 26, 212, 'وہ تو سننے کی جگہ سے دور کردیے گئے ہیں'),
(3145, 26, 213, 'تو اللہ کے سوا دوسرا خدا نہ پوج کہ تجھ پر عذاب ہوگا،'),
(3146, 26, 214, 'اور اے محبوب! اپنے قریب تر رشتہ داروں کو ڈراؤ'),
(3147, 26, 215, 'اور اپنی رحمت کا بازو بچھاؤ اپنے پیرو مسلمانوں کے لیے'),
(3148, 26, 216, 'تو اگر وہ تمہارا حکم نہ مانیں تو فرمادو میں تمہارے کاموں سے بے علاقہ ہوں،'),
(3149, 26, 217, 'اور اس پر بھروسہ کرو جو عزت والا مہر والا ہے'),
(3150, 26, 218, 'جو تمہیں دیکھتا ہے جب تم کھڑے ہوتے ہو'),
(3151, 26, 219, 'اور نمازیوں میں تمہارے دورے کو'),
(3152, 26, 220, 'بیشک وہی سنتا جانتا ہے'),
(3153, 26, 221, 'کیا میں تمہیں بتادوں کہ کس پر اترتے ہیں شیطان،'),
(3154, 26, 222, 'اترتے ہیں بڑے بہتان والے گناہگار پر'),
(3155, 26, 223, 'شیطان اپنی سنی ہوئی ان پر ڈالتے ہیں اور ان میں اکثر جھوٹے ہیں'),
(3156, 26, 224, 'اور شاعروں کی پیرو ی گمراہ کرتے ہیں'),
(3157, 26, 225, 'کیا تم نے نہ دیکھا کہ وہ ہر نالے میں سرگرداں پھرتے ہیں'),
(3158, 26, 226, 'اور وہ کہتے ہیں جو نہیں کرتے'),
(3159, 26, 227, 'مگر وہ جو ایمان لائے اور اچھے کام کیے اور بکثرت اللہ کی یاد کی اور بدلہ لیا بعد اس کے کہ ان پر ظلم ہوا اور اب جاننا چاہتے ہیں ظالم کہ کس کروٹ پر پلٹا کھائیں گے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'یہ آیتیں ہیں قرآن اور روشن کتاب کی'),
(3161, 27, 2, 'ہدایت اور خوشخبری ایمان والوں کو،'),
(3162, 27, 3, 'وہ جو نماز برپا رکھتے ہیں اور زکوٰة دیتے ہیں اور وہ آخرت پر یقین رکھتے ہیں،'),
(3163, 27, 4, 'وہ جو آخرت پر ایمان نہیں لاتے ہم نے ان کے کوتک (برے اعمال) ان کی نگاہ میں بھلے کر دکھائے ہیں'),
(3164, 27, 5, 'تو وہ بھٹک رہے ہیں یہ وہ ہیں جن کے لیے برا عذاب ہے اور یہی آخرت میں سب سے بڑھ کر نقصان میں'),
(3165, 27, 6, 'اور بیشک تم قرآن سکھائے جاتے اور حکمت والے علم والے کی طرف سے'),
(3166, 27, 7, 'جب کہ موسیٰ نے اپنی گھر والی سے کہا مجھے ایک آگ نظر پڑی ہے عنقریب میں تمہارے پاس اس کی کوئی خبر لاتا ہوں یا اس میں سے کوئی چمکتی چنگاری لاؤں گا کہ تم تاپو'),
(3167, 27, 8, 'پھر جب آگ کے پاس آیا ندا کی گئی کہ برکت دیا گیا وہ جو اس آگ کی جلوہ گاہ میں ہے یعنی موسیٰ اور جو اس کے آس پاس میں یعنی فرشتے اور پاکی ہے اللہ کو جو رب ہے سارے جہان کا،'),
(3168, 27, 9, 'اے موسیٰ بات یہ ہے کہ میں ہی ہوں اللہ عزت والا حکمت والا،'),
(3169, 27, 10, 'اور اپنا عصا ڈال دے پھر موسیٰ نے اسے دیکھا لہراتا ہوا گویا سانپ ہے پیٹھ پھیر کر چلا اور مڑ کر نہ دیکھا، ہم نے فرمایا اے موسیٰ ڈر نہیں، بیشک میرے حضور رسولوں کو خوف نہیں ہوتا'),
(3170, 27, 11, 'ہاں جو کوئی ز یادتی کرے پھر برائی کے بعد بھلائی سے بدلے تو بیشک میں بخشنے والا مہربان ہوں'),
(3171, 27, 12, 'اور اپنا ہاتھ اپنے گریبان میں ڈال نکلے گا سفید چمکتا بے عیب نو نشانیوں میں فرعون اور اس کی قوم کی طرف، بیشک وہ بے حکم لوگ ہیں،'),
(3172, 27, 13, 'پھر جب ہماری نشانیاں آنکھیں کھولتی ان کے پاس آئیں بولے یہ تو صریح جادو ہے،'),
(3173, 27, 14, 'اور ان کے منکر ہوئے اور ان کے دلوں میں ان کا یقین تھا ظلم اور تکبر سے تو دیکھو کیسا انجام ہوا فسادیوں کا'),
(3174, 27, 15, 'اور بیشک ہم نے داؤد اور سلیمان کو بڑا علم عطا فرمایا اور دونوں نے کہا سب خوبیاں اللہ کو جس نے ہمیں اپنے بہت سے ایمان والے بندوں پر فضیلت بخشی'),
(3175, 27, 16, 'اور سلیمان داؤد کا جانشین ہوا اور کہا اے لوگو! ہمیں پرندوں کی بولی سکھائی گئی اور ہر چیز میں سے ہم کو عطا ہوا بیشک یہی ظاہر فضل ہے'),
(3176, 27, 17, 'اور جمع کیے گئے سلیمان کے لیے اس کے لشکر جنوں اور آدمیوں اور پرندوں سے تو وہ روکے جاتے تھے'),
(3177, 27, 18, 'یہاں تک کہ جب چیونٹیوں کے نالے پر آئے ایک چیونٹی بولی اے چیونٹیو! اپنے گھروں میں چلی جاؤ تمہیں کچل نہ ڈالیں سلیمان اور ان کے لشکر بے خبری میں'),
(3178, 27, 19, 'تو اس کی بات مسکرا کر ہنسا اور عرض کی اے میرے رب! مجھے توفیق دے کہ میں شکر کروں تیرے احسان کا جو تو نے مجھ پر اور میرے ماں باپ پر کیے اور یہ کہ میں وہ بھلا کام کرو ں جو تجھے پسند آئے اور مجھے اپنی رحمت سے اپنے ان بندوں میں شامل کر جو تیرے قرب خاص کے سزاوار ہیں'),
(3179, 27, 20, 'اور پرندوں کا جائزہ لیا تو بولا مجھے کیا ہوا کہ میں ہد ہد کو نہیں دیکھتا یا وہ واقعی حاضر نہیں،'),
(3180, 27, 21, 'ضرور میں اسے سخت عذاب کرو ں گا یا ذبح کردوں گا یا کوئی روشن سند میرے پاس لائے'),
(3181, 27, 22, 'تو ہد ہد کچھ زیادہ دیر نہ ٹھہرا اور آکر عرض کی کہ میں وہ بات دیکھ کر آیا ہوں جو حضور نے نہ دیکھی اور میں شہر سبا سے حضور کے پاس ایک یقینی خبر لایا ہوں،'),
(3182, 27, 23, 'میں نے ایک عورت دیکھی کہ ان پر بادشاہی کررہی ہے اور اسے ہر چیز میں سے ملا ہے اور اس کا بڑا تخت ہے'),
(3183, 27, 24, 'میں نے اسے اور اس کی قوم کو پایا کہ اللہ کو چھوڑ کر سورج کو سجدہ کرتے ہیں اور شیطان نے ان کے اعمانل ان کی نگاہ میں سنوار کر ان کو سیدھی راہ سے روک دیا تو وہ راہ نہیں پاتے،'),
(3184, 27, 25, 'کیوں نہیں سجدہ کرتے اللہ کو جو نکالتا ہے آسمانوں اور زمین کی چھپی چیزیں اور جانتا ہے جو کچھ تم چھپاتے ہو اور ظاہر کرتے ہو'),
(3185, 27, 26, 'اللہ ہے کہ اس کے سوا کوئی سچا معبود نہیں وہ بڑے عرش کا مالک ہے، السجد ة۔۸)'),
(3186, 27, 27, 'سلیمان نے فرمایا اب ہم دیکھیں گے کہ تو نے سچ کہا یا تو جھوٹوں میں ہے'),
(3187, 27, 28, 'میرا یہ فرمان لے جان کر ان پر ڈال پھر ان سے الگ ہٹ کر دیکھ کہ وہ کیا جواب دیتے ہیں'),
(3188, 27, 29, 'وہ عورت بولی اے سردارو! بیشک میری طرف ایک عزت والا خط ڈالا گیا'),
(3189, 27, 30, 'بیشک وہ سلیمان کی طرف سے ہے اور بیشک وہ اللہ کے نام سے ہے نہایت مہربان رحم والا،'),
(3190, 27, 31, 'یہ کہ مجھ پر بلندی نہ چاہو اور گردن رکھتے میرے حضور حاضر ہو'),
(3191, 27, 32, 'بولی اے سردارو! میرے اس معاملے میں مجھے رائے دو میں کسی معاملے میں کوئی قطعی فیصلہ نہیں کرتی جب تک تم میرے پاس حاضر نہ ہو،'),
(3192, 27, 33, 'وہ بولے ہم زور والے اور بڑی سخت لڑائی والے ہیں اور اختیار تیرا ہے تو نظر کر کہ کیا حکم دیتی ہے'),
(3193, 27, 34, 'بولی بیشک بادشاہ جب کسی بستی میں داخل ہوتے ہیں اسے تباہ کردیتے ہیں اور اس کے عزت والوں کو ذلیل اور ایسا ہی کرتے ہیں'),
(3194, 27, 35, 'اور میں ان کی طرف ایک تحفہ بھیجنے والی ہوں پھر دیکھوں گی کہ ایلچی کیا جواب لے کر پلٹے'),
(3195, 27, 36, 'پھر جب وہ سلیمان کے پاس آیا فرمایا کیا مال سے میری مدد کرتے ہو تو جو مجھے اللہ نے دیا وہ بہتر ہے اس سے جو تمہیں دیا بلکہ تمہیں اپنے تحفہ پر خوش ہوتے ہو'),
(3196, 27, 37, 'پلٹ جا ان کی طرف تو ضرور ہم ان پر وہ لشکر لائیں گے جن کی انہیں طاقت نہ ہوگی اور ضرور ہم ان کو اس شہر سے ذلیل کرکے نکال دیں گے یوں کہ وہ پست ہوں گے'),
(3197, 27, 38, 'سلیمان نے فرمایا، اے درباریو! تم میں کون ہے کہ وہ اس کا تخت میرے پاس لے آئے قبل اس کے کہ وہ میرے حضور مطیع ہوکر حاضر ہوں'),
(3198, 27, 39, 'ایک بڑا خبیث جن بولا کہ میں وہ تخت حضور میں حاضر کردوں گا قبل اس کے کہ حضور اجلاس برخاست کریں اور میں بیشک اس پر قوت والا امانتدار ہوں'),
(3199, 27, 40, 'اس نے عرض کی جس کے پاس کتاب کا علم تھا کہ میں اسے حضور میں حاضر کردوں گا ایک پل مارنے سے پہلے پھر جب سلیمان نے تخت کو اپنے پاس رکھا دیکھا کہ یہ میرے رب کے فضل سے ہے، تاکہ مجھے آزمائے کہ میں شکر کرتا ہوں یا ناشکری، اور جو شکر کرے وہ اپنے بھلے کو شکر کرتا ہے اور جو ناشکری کرے تو میرا رب بے پرواہ ہے سب خوبیوں والا،'),
(3200, 27, 41, 'سلیمان نے حکم دیا عورت کا تخت اس کے سامنے وضع بدل کر بیگانہ کردو کہ ہم دیکھیں کہ وہ راہ پاتی ہے یا ان میں ہوتی ہے جو ناواقف رہے،'),
(3201, 27, 42, 'پھر جب وہ آئی اس سے کہا گیا کیا تیرا تخت ایسا ہی ہے، بولی گویا یہ وہی ہے اور ہم کو اس واقعہ سے پہلے خبر مل چکی اور ہم فرمانبردار ہوئے'),
(3202, 27, 43, 'اور اسے روکا اس چیز نے جسے وہ اللہ کے سوا پوجتی تھی، بیشک وہ کافر لوگوں میں سے تھی،'),
(3203, 27, 44, 'اس سے کہا گیا صحن میں آ پھر جب اس نے اسے دیکھا گہرا پانی سمجھی اور اپنی ساقیں کھولیں سلیمان نے فرمایا یہ تو ایک چکنا صحن ہے شیشوں جڑا عورت نے عرض کی اے میرے رب! میں نے اپنی جان پر ظلم کیا اور اب سلیمانے کے ساتھ اللہ کے حضور گردن رکھتی ہوں جو رب سارے جہان کا'),
(3204, 27, 45, 'اور بیشک ہم نے ثمود کی طرف ان کے ہم قوم صالح کو بھیجا کہ اللہ کو پوجو تو جبھی وہ دو گروہ ہوگئے جھگڑا کرتے'),
(3205, 27, 46, 'صا لح نے فرمایا اے میری قوم! کیوں برائی کی جلدی کرتے ہو بھلائی سے پہلے اللہ سے بخشش کیوں نہیں مانگتے شاید تم پر رحم ہو'),
(3206, 27, 47, 'بولے ہم نے برُا شگون کیا تم سے اور تمہارے ساتھیوں سے فرمایا تمہاری بدشگونی اللہ کے پاس ہے بلکہ تم لوگ فتنے میں پڑے ہو'),
(3207, 27, 48, 'اور شہر میں نو شخص تھے کہ زمین میں فساد کرتے اور سنوار نہ چاہتے،'),
(3208, 27, 49, 'آپس میں اللہ کی قسمیں کھا کر بولے ہم ضرور رات کو چھاپا ماریں گے صا لح اور اس کے گھر والوں پر پھر اس کے وارث سے کہیں گے اس گھر والوں کے قتل کے وقت ہم حاضر نہ تھے اور بیشک ہم سچے ہیں،'),
(3209, 27, 50, 'او رانہوں نے اپنا سا مکر کیا اور ہم نے اپنی خفیہ تدبیر فرمائی اور وہ غافل رہے،'),
(3210, 27, 51, 'تو دیکھو کیسا انجام ہوا ان کے مکر کا ہم نے ہلاک کردیا انہیں اور ان کی ساری قوم کو'),
(3211, 27, 52, 'تو یہ ہیں ان کے گھر ڈھے پڑے بدلہ ان کے ظلم کا، بیشک اس میں نشانی ہے جاننے والوں کے لیے،'),
(3212, 27, 53, 'اور ہم نے ان کو بچالیا جو ایمان لائے اور ڈرتے تھے'),
(3213, 27, 54, 'اور لوط کو جب اس نے اپنی قوم سے کہا کیا بے حیائی پر آتے ہو اور تم سوجھ رہے ہو'),
(3214, 27, 55, 'کیا تم مردوں کے پاس مستی سے جاتے ہو عورتیں چھوڑ کر بلکہ تم جاہل لوگ ہو'),
(3215, 27, 56, 'تو اس کی قوم کا کچھ جواب نہ تھا مگر یہ کہ بولے لوط کے گھرانے کو اپنی بستی سے نکال دو یہ لوگ تو ستھراپن چاہتے ہیں'),
(3216, 27, 57, 'تو ہم نے اسے اور اس کے گھر والوں کو نجات دی مگر اس کی عورت کو ہم نے ٹھہرادیا تھا کہ وہ رہ جانے والوں میں ہے'),
(3217, 27, 58, 'اور ہم نے ان پر ایک برساؤ برسایا تو کیا ہی برا برساؤ تھا ڈرا ئے ہوؤں کا،'),
(3218, 27, 59, 'تم کہو سب خوبیاں اللہ کو اور سلام اس کے چنے ہوئے بندے پر کیا اللہ بہتر یا ان کے ساختہ شریک'),
(3219, 27, 60, 'ا وہ جس نے آسمان و زمین بنائے اور تمہارے لیے آسمان سے پانی اتارا تو ہم نے اس سے باغ اگائے رونق والے تمہاری طاقت نہ تھی کہ ان کے پیڑ اگاتے کیا اللہ کے ساتھ کوئی اور خدا ہے بلکہ وہ لوگ راہ سے کتراتے ہیں'),
(3220, 27, 61, 'یا وہ جس نے زمین بسنے کو بنائی اور اس کے بیچ میں نہریں نکالیں اور اس کے لیے لنگر بنائے اور دونوں سمندروں میں آڑ رکھی کیا اللہ کے ساتھ اور خدا ہے، بلکہ ان میں اکثر جاہل ہیں'),
(3221, 27, 62, 'یا وہ جو لاچار کی سنتا ہے جب اسے پکارے اور دور کردیتا ہے برائی اور تمہیں زمین کا وارث کرتا ہے کیا اللہ کے ساتھ اور خدا ہے، بہت ہی کم دھیان کرتے ہو،'),
(3222, 27, 63, 'یا وہ جو تمہیں راہ دکھاتا ہے اندھیریوں میں خشکی اور تری کی اور وہ کہ ہوائیں بھیجتا ہے، اپنی رحمت کے آگے خوشحبری سناتی کیا اللہ کے ساتھ کوئی اور خدا ہے، برتر ہے اللہ ان کے شرک سے،'),
(3223, 27, 64, 'یا وہ جو خلق کی ابتداء فرماتا ہے پھر اسے دوبارہ بنائے گا اور وہ جو تمہیں آسمانوں اور زمین سے روزی دیتا ہے کیا اللہ کے ساتھ کوئی اور خدا ہے، تم فرماؤ کہ اپنی دلیل لاؤ اگر تم سچے ہو'),
(3224, 27, 65, 'تم فرماؤ غیب نہیں جانتے جو کوئی آسمانوں اور زمین میں ہیں مگر اللہ اور انہیں خبر نہیں کہ کب اٹھا ےٴ جائیں گے،'),
(3225, 27, 66, 'کیا ان کے علم کا سلسلہ آخرت کے جانے تک پہو نچ گیا کوئی نہیں وہ اس کی طرف سے شک میں ہیں بلکہ وہ اس سے اندھے ہیں،'),
(3226, 27, 67, 'اور کافر بولے کیا جب ہم اور ہمارے باپ دادا مٹی ہوجائیں گے کیا ہم پھر نکالے جائیں گے'),
(3227, 27, 68, 'بیشک اس کا وعدہ دیا گیا ہم کو اور ہم سے پہلے ہمارے باپ داداؤں کو یہ تو نہیں مگر اگلوں کی کہانیاں،'),
(3228, 27, 69, 'تم فرماؤ زمین میں چل کر دیکھو، کیسا ہوا نجام مجرموں کا'),
(3229, 27, 70, 'اور تم ان پر غم نہ کھاؤ اور ان کے مکر سے دل تنگ نہ ہو'),
(3230, 27, 71, 'اور کہتے ہیں کب آئے گا یہ وعدہ اگر تم سچے ہو،'),
(3231, 27, 72, 'تم فرماؤ قریب ہے کہ تمہارے پیچھے آ لگی ہو بعض وہ چیز جس کی تم جلدی مچارہے ہو'),
(3232, 27, 73, 'اور بیشک تیرا رب فضل والا ہے آدمیوں پر لیکن اکثر آدمی حق نہیں مانتے'),
(3233, 27, 74, 'اور بیشک تمہارا رب جانتا ہے جو ان کے سینوں میں چھپی ہے اور جو وہ ظاہر کرتے ہیں'),
(3234, 27, 75, 'اور جتنے غیب ہیں آسمانوں اور زمین کے سب ایک بتانے والی کتاب میں ہیں'),
(3235, 27, 76, 'بیشک یہ قرآن ذکر فرماتا ہے بنی اسرائیل سے اکثر وہ باتیں جس میں وہ اختلاف کرتے ہیں'),
(3236, 27, 77, 'اور بیشک وہ ہدایت اور رحمت ہے مسلمانوں کے لیے،'),
(3237, 27, 78, 'بیشک تمہارا رب ان کے آپس میں فیصلہ فرماتا ہے اپنے حکم سے اور وہی ہے عزت و الا علم والا،'),
(3238, 27, 79, 'تو تم اللہ پر بھروسہ کرو، بیشک تم روشن حق پر ہو،'),
(3239, 27, 80, 'بیشک تمہارے سنائے نہیں سنتے مردے اور نہ تمہارے سنائے بہرے پکار سنیں جب پھریں پیٹھ دے کر'),
(3240, 27, 81, 'اور اندھوں کو گمراہی سے تم ہدایت کرنے والے نہیں، تمہارے سنائے تو وہی سنتے ہیں جو ہماری آیتوں پر ایمان لاتے ہیں اور ہو مسلمان ہیں،'),
(3241, 27, 82, 'اور جب بات ان پر آپڑے گی ہم زمین سے ان کے لیے ایک چوپایہ نکالیں گے جو لوگوں سے کلام کرے گا اس لیے کہ لوگ ہماری آیتوں پر ایمان نہ لاتے تھے'),
(3242, 27, 83, 'اور جس دن اٹھائیں گے ہم ہر گروہ میں سے ایک فوج جو ہماری آیتوں کو جھٹلاتی ہے تو ان کے اگلے روکے جائیں گے کہ پچھلے ان سے آملیں،'),
(3243, 27, 84, 'یہاں تک کہ جب سب حاضر ہولیں گے فرمائے گا کیا تم نے میری آیتیں جھٹلائیں حالانکہ تمہارا علم ان تک نہ پہنچتا تھا یا کیا کام کرتے تھے'),
(3244, 27, 85, 'اور بات پڑچکی ان پر ان کے ظلم کے سبب تو وہ اب کچھ نہیں بولتے'),
(3245, 27, 86, 'کیا انہوں نے نہ دیکھا کہ ہم نے رات بنائی کہ اس میں آرام کریں اور دن کو بنایا سوجھانے والا، بیشک اس میں ضرور نشانیاں ہیں ان لوگوں کے لیے کہ ایمان رکھتے ہیں'),
(3246, 27, 87, 'اور جس دن پھونکا جائے گا صُور تو گھبرائے جائیں گے جتنے آسمانوں میں ہیں اور جتنے زمین میں ہیں مگر جسے خدا چاہے اور سب اس کے حضور حاضر ہوئے عاجزی کرتے'),
(3247, 27, 88, 'اور تو دیکھے گا پہاڑوں کو خیال کرے گا کہ وہ جمے ہوئے ہیں اور وہ چلتے ہوں گے بادل کی چال یہ کام ہے اللہ کا جس نے حکمت سے بنائی ہر چیز، بیشک اسے خبر ہے تمہارے کاموں کی،'),
(3248, 27, 89, 'جو نیکی لائے اس کے لیے اس سے بہتر صلہ ہے اور ان کو اس دن کی گھبراہٹ سے امان ہے'),
(3249, 27, 90, 'اور جو بدی لائے تو ان کے منہ اوندھائے گئے آ گ میں تمہیں کیا بدلہ ملے گا مگر اسی کا جو کرتے تھے'),
(3250, 27, 91, 'مجھے تو یہی حکم ہوا ہے کہ پوجوں اس شہر کے رب کو جس نے اسے حرمت والا کیا ہے اور سب کچھ اسی کا ہے، اور مجھے حکم ہوا ہے کہ فرمانبرداروں میں ہوں،'),
(3251, 27, 92, 'اور یہ کہ قرآن کی تلاوت کروں تو جس نے راہ پائی اس نے اپنے بھلے کو راہ پائی اور جو بہکے تو فرمادو کہ میں تو یہی ڈر سنانے والا ہوں'),
(3252, 27, 93, 'اور فرماؤ کہ سب خوبیاں اللہ کے لیے عنقریب وہ تمہیں اپنی نشانیاں دکھائے گا تو انہیں پہچان لو گے اور اے محبوب! تمہارا رب غافل نہیں، اے لوگو! تمہارے اعمال سے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طٰسم'),
(3254, 28, 2, 'یہ آیتیں ہیں روشن کتاب'),
(3255, 28, 3, 'ہم تم پر پڑھیں موسیٰ اور فرعون کی سچی خبر ان لوگوں کے لیے جو ایمان رکھتے ہیں،'),
(3256, 28, 4, 'بیشک فرعون نے زمین میں غلبہ پایا تھا اور اس کے لوگوں کو اپنا تابع بنایا ان میں ایک گروہ کو کمزور دیکھتا ان کے بیٹوں کو ذبح کرتا اور ان کی عورتوں کو زندہ رکھتا بیشک وہ فسادی تھا،'),
(3257, 28, 5, 'اور ہم چاہتے تھے کہ ان کمزوریوں پر احسان فرمائیں اور ان کو پیشوا بنائیں اور ان کے ملک و مال کا انہیں کو وارث بنائیں'),
(3258, 28, 6, 'اور انہیں زمین میں قبضہ دیں اور فرعون اور ہامان اور ان کے لشکروں کو وہی دکھادیں جس کا انہیں ان کی طرف سے خطرہ ہے'),
(3259, 28, 7, 'اور ہم نے موسیٰ کی ماں کو الہام فرمایا کہ اسے دودھ پلا پھر جب تجھے اس سے اندیشہ ہو تو اسے دریا میں ڈال دے اور نہ ڈر اور نہ غم کر بیشک ہم اسے تیری طرف پھیر لائیں اور اسے رسول بنائیں گے'),
(3260, 28, 8, 'تو اسے اٹھالیا فرعون کے گھر والوں نے کہ وہ ان کا دشمن اور ان پر غم ہو بیشک فرعون اور ہامان اور ان کے لشکر خطا کار تھے'),
(3261, 28, 9, 'اور فرعون کی بی بی نے کہا یہ بچہ میری اور تیری آنکھوں کی ٹھنڈک ہے، اسے قتل نہ کرو، شاید یہ ہمیں نفع دے یا ہم اسے بیٹا بنالیں اور وہ بے خبر تھے'),
(3262, 28, 10, 'اور صبح کو موسیٰ کی ماں کا دل بے صبر ہوگیا ضرو ر قریب تھا کہ وہ اس کا حال کھول دیتی اگر ہم نہ ڈھارس بندھاتے اس کے دل پر کہ اسے ہمارے وعدہ پر یقین رہے'),
(3263, 28, 11, 'اور اس کی ماں نے اس کی بہن سے کہا اس کے پیچھے چلی جا تو وہ اسے دور سے دیکھتی رہی اور ان کو خبر نہ تھی'),
(3264, 28, 12, 'اور ہم نے پہلے ہی سب دائیاں اس پر حرام کردی تھیں تو بولی کیا میں تمہیں بتادوں ایسے گھر والے کہ تمہارے اس بچہ کو پال دیں اور وہ اس کے خیر خواہ ہیں'),
(3265, 28, 13, 'تو ہم نے اسے اس کی ماں کی طرف پھیرا کہ ماں کی آنکھ ٹھنڈی ہو اور غم نہ کھائے اور جان لے کہ اللہ کا وعدہ سچا ہے، لیکن اکثر لوگ نہیں جانتے'),
(3266, 28, 14, 'اور جب اپنی جوانی کو پہنچا اور پورے زور پر آیا ہم نے اسے حکم اور علم عطا فرمایا اور ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(3267, 28, 15, 'اور اس شہر میں داخل ہوا جس وقت شہر والے دوپہر کے خواب میں بے خبر تھے تو اس میں دو مرد لڑتے پائے، ایک موسیٰ، کے گروہ سے تھا اور دوسرا اس کے دشمنوں سے تو وہ جو اس کے گروہ سے تھا اس نے موسیٰ سے مدد مانگی، اس پر جو اس کے دشمنوں سے تھا، تو موسیٰ نے اس کے گھونسا مارا تو اس کا کام تما م کردیا کہا یہ کام شیطان کی طرف سے ہوا بیشک وہ دشمن ہے کھلا گمراہ کرنے والا،'),
(3268, 28, 16, 'عرض کی، اے میرے رب! میں نے اپنی جان پر زیادتی کی تو مجھے بخش دے تو رب نے اسے بخش دیا، بیشک وہی بخشنے والا مہربان ہے،'),
(3269, 28, 17, 'عرض کی اے میرے رب جیسا تو نے مجھ پر احسان کیا تو اب ہرگز میں مجرموں کا مددگار نہ ہوں گا،'),
(3270, 28, 18, 'تو صبح کی، اس شہر میں ڈرتے ہوئے اس انتظار میں کہ کیا ہوتا ہے جبھی دیکھا کہ وہ جس نے کل ان سے مدد چاہی تھی فریاد کررہا ہے موسیٰ نے اس سے فرمایا بیشک تو کھلا گمراہ ہے'),
(3271, 28, 19, 'تو جب موسیٰ نے چاہا کہ اس پر گرفت کرے جو ان دونوں کا دشمن ہے وہ بولا اے موسیٰ کیا تم مجھے ویسا ہی قتل کرنا چاہتے ہو جیسا تم نے کل ایک شخص کو قتل کردیا، تم تو یہی چاہتے ہو کہ زمین میں سخت گیر بنو اور اصلاح کرنا نہیں چاہتے'),
(3272, 28, 20, 'اور شہر کے پرلے کنارے سے ایک شخص دوڑ تا آیا، کہا اے موسیٰ! بیشک دربار والے آپ کے قتل کا مشورہ کررہے ہیں تو نکل جایے میں آپ کا خیر خواہ ہوں'),
(3273, 28, 21, 'تو اس شہر سے نکلا ڈرتا ہوا اس انتظار میں کہ اب کیا ہوتا ہے عرض کی، اے میرے رب! مجھے ستمگاروں سے بچالے'),
(3274, 28, 22, 'اور جب مدین کی طرف متوجہ ہوا کہا قریب ہے کہ میرا رب مجھے سیدھی راہ بتائے'),
(3275, 28, 23, 'اور جب مدین کے پانی پر آیا وہاں لوگوں کے ایک گروہ کو دیکھا کہ اپنے جانوروں کو پانی پلارہے ہیں، اور ان سے اس طرف دو عورتیں دیکھیں کہ اپنے جانوروں کو روک رہی ہیں موسیٰ نے فرمایا تم دونوں کا کیا حال ہے وہ بولیں ہم پانی نہیں پلاتے جب تک سب چرواہے پلاکر پھیر نہ لے جائیں اور ہمارے باپ بہت بوڑھے ہیں'),
(3276, 28, 24, 'تو موسیٰ نے ان دونوں کے جانوروں کو پانی پلا دیا پھر سایہ کی طرف پھرا عرض کی اے میرے رب! میں اس کھانے کا جو تو میرے لیے اتارے محتاج ہوں'),
(3277, 28, 25, 'تو ان دونوں میں سے ایک اس کے پاس آئی شرم سے چلتی ہوئی بولی میرا باپ تمہیں بلاتا ہے کہ تمہیں مزدوری دے اس کی جو تم نے ہمارے جانوروں کو پانی پلایا ہے جب موسیٰ اس کے پاس آیا اور اسے باتیں کہہ سنائیں اس نے کہا ڈریے نہیں، آپ بچ گئے ظالموں سے'),
(3278, 28, 26, 'ان میں کی ایک بولی اے میرے باپ! ان کو نوکر رکھ لو بیشک بہتر نوکر وہ جو طاقتور اور امانتدار ہو'),
(3279, 28, 27, 'کہا میں چاہتا ہوں کہ اپنی دونوں بیٹیوں میں سے ایک تمہیں بیاہ دوں اس مہر پر کہ تم آٹھ برس میری ملازمت کرو پھر اگر پورے دس برس کرلو تو تمہاری طرف سے ہے اور میں تمہیں مشقت میں ڈالنا نہیں چاہتا قریب ہے انشاء اللہ تم مجھے نیکوں میں پاؤ گے'),
(3280, 28, 28, 'موسیٰ نے کہا یہ میرے اور آپ کے درمیان اقرار ہوچکا، میں ان دونوں میں جو میعاد پوری کردوں تو مجھ پر کوئی مطالبہ نہیں، اور ہمارے اس کہے پر اللہ کا ذمہ ہے'),
(3281, 28, 29, 'پھر جب موسیٰ نے اپنی میعاد پوری کردی اور اپنی بی بی کو لے کر چلا طُور کی طرف سے ایک آگ دیکھی اپنی گھر والی سے کہا تم ٹھہرو مجھے طُور کی طرف سے ایک آگ نظر پڑی ہے شاید میں وہاں سے کچھ خبر لاؤ ں یا تمہارے لیے کوئی آ گ کی چنگاری لاؤں کہ تم تاپو،'),
(3282, 28, 30, 'پھر جب آگ کے پاس حاضر ہوا ندا کی گئی میدان کے دہنے کنارے سے برکت والے مقام میں پیڑ سے کہ اے موسیٰ! بیشک میں ہی ہوں اللہ رب سارے جہان کا'),
(3283, 28, 31, 'اور یہ کہ ڈال دے اپنا عصا پھر جب موسیٰ نے اسے دیکھا لہراتا ہوا گویا سانپ ہے پیٹھ پھیر کر چلا اور مڑ کر نہ دیکھا اے موسیٰ سامنے آ اور ڈر نہیں، بیشک تجھے امان ہے'),
(3284, 28, 32, 'اپنا ہاتھ گریبان میں ڈال نکلے گا سفید چمکتا بے عیب اور اپنا ہاتھ سینے پر رکھ لے خوف دور کرنے کو تو یہ دو حُجتیں ہیں تیرے رب کی فرعون اور اس کے درباریوں کی طرف، بیشک وہ بے حکم لوگ ہیں،'),
(3285, 28, 33, 'عرض کی اے میرے رب! میں نے ان میں ایک جان مار ڈالی ہے تو ڈرتا ہوں کہ مجھے قتل کردیں،'),
(3286, 28, 34, 'اور میرا بھائی ہارون اس کی زبان مجھ سے زیادہ صاف ہے تو اسے میری مدد کے لیے رسول بنا، کہ میری تصدیق کرے مجھے ڈر ہے کہ وہ مجھے جھٹلائیں گے،'),
(3287, 28, 35, 'فرمایا، قریب ہے کہ ہم تیرے بازو کو تیرے بھائی سے قوت دیں گے اور تم دونوں کو غلبہ عطا فرمائیں گے تو وہ تم دونوں کا کچھ نقصان نہ کرسکیں گے، ہماری نشانیوں کے سبب تم دونوں اور جو تمہاری پیروی کریں گے غالب آؤ گے'),
(3288, 28, 36, 'پھر جب موسیٰ ان کے پاس ہماری روشن نشانیاں لایا بولے یہ تو نہیں مگر بناوٹ کا جادو اور ہم نے اپنے اگلے باپ داداؤں میں ایسا نہ سنا'),
(3289, 28, 37, 'اور موسیٰ نے فرمایا میرا رب خوب جانتا ہے جو اس کے پاس سے ہدایت لایا اور جس کے لیے آخرت کا گھر ہوگا بیشک ظالم مراد کو نہیں پہنچتے'),
(3290, 28, 38, 'اور فرعون بولا، اے درباریو! میں تمہارے لیے اپنے سوا کوئی خدا نہیں جانتا تو اے ہامان! میرے لیے گارا پکا کر ایک محل بنا کہ شاید میں موسیٰ کے خدا کو جھانک آؤں اور بیشک میرے گمان میں تو وہ جھوٹا ہے'),
(3291, 28, 39, 'اور اس نے اور اس کے لشکریوں نے زمین میں بے جا بڑائی چاہی اور سمجھے کہ انہیں ہماری طرف پھرنا نہیں،'),
(3292, 28, 40, 'تو ہم نے اسے اور اس کے لشکر کو پکڑ کر دریا میں پھینک دیا تو دیکھو کیسا انجام ہوا ستمگاروں کا،'),
(3293, 28, 41, 'اور انہیں ہم نے دوزخیوں کا پیشوا بنایا کہ آگ کی طرف بلاتے ہیں اور قیامت کے دن ان کی مدد نہ ہوگی،'),
(3294, 28, 42, 'اور اس دنیا میں ہم نے ان کے پیچھے لعنت لگائی اور قیامت کے دن ان کا برا ہے،'),
(3295, 28, 43, 'اور بیشک ہم نے موسیٰ کو کتاب عطا فرمائی بعد اس کے کہ اگلی سنگتیں (قومیں) ہلاک فرمادیں جس میں لوگوں کے دل کی آنکھیں کھولنے والی باتیں اور ہدایت اور رحمت تاکہ وہ نصیحت مانیں،'),
(3296, 28, 44, 'اور تم طور کی جانت مغرب میں نہ تھے جبکہ ہم نے موسیٰ کو رسالت کا حکم بھیجا اور اس وقت تم حاضر نہ تھے،'),
(3297, 28, 45, 'مگر ہوا یہ کہ ہم نے سنگتیں پیدا کیں کہ ان پر زمانہ دراز گزرا اور نہ تم اہلِ مدین میں مقیم تھے ان پر ہماری آیتیں پڑھتے ہوئے، ہاں ہم رسول بنانے والے ہوئے'),
(3298, 28, 46, 'اور نہ تم طور کے کنارے تھے جب ہم نے ندا فرمائی ہاں تمہارے رب کی مہر ہے (کہ تمہیں غیب کے علم دیے) کہ تم ایسی قوم کو ڈر سناؤ جس کے پاس تم سے پہلے کوئی ڈر سنانے والا نہ آیا یہ امید کرتے ہوئے کہ ان کو نصیحت ہو،'),
(3299, 28, 47, 'اور اگر نہ ہوتا کہ کبھی پہنچتی انہیں کوئی مصیبت اس کے سبب جو ان کے ہاتھوں نے آگے بھیجا تو کہتے، اے ہمارے رب! تو نے کیوں نہ بھیجا ہماری طرف کوئی رسول کہ ہم تیری آیتوں کی پیروی کرتے اور ایمان لاتے'),
(3300, 28, 48, 'پھر جب ان کے پاس حق آیا ہماری طرف سے بولے انہیں کیوں نہ دیا گیا جو موسیٰ کو دیا گیا کیا اس کے منکر نہ ہوئے تھے جو پہلے موسیٰ کو دیا گیا بولے دو جادو ہیں ایک دوسرے کی پشتی (امداد) پر، اور بولے ہم ان دونوں کے منکر ہیں'),
(3301, 28, 49, 'تم فرماؤ تو اللہ کے پاس سے کوئی کتاب لے آؤ جو ان دونوں کتابوں سے زیادہ ہدایت کی ہو میں اس کی پیروی کروں گا اگر تم سچے ہو'),
(3302, 28, 50, 'پھر اگر وہ یہ تمہارا فرمانا قبول نہ کریں تو جان لو کہ بس وہ اپنی خواہشو ں ہی کے پیچھے ہیں، اور اس سے بڑھ کر گمراہ کون جو اپنی خواہش کی پیروی کرے اللہ کی ہدایت سے جدا، بیشک اللہ ہدایت ہیں فرماتا ظالم لوگوں کو،'),
(3303, 28, 51, 'اور بیشک ہم نے ان کے لیے بات مسلسل اتاری کہ وہ دھیان کریں،'),
(3304, 28, 52, 'جن کو ہم نے اس سے پہلے کتاب دی وہ اس پر ایمان لاتے ہیں،'),
(3305, 28, 53, 'اور جب ان پر یہ آیتیں پڑھی جاتی ہیں کہتے ہیں ہم اس پر ایمان لائے، بیشک یہی حق ہے ہمارے رب کے پا س سے ہم اس سے پہلے ہی گردن رکھ چکے تھے'),
(3306, 28, 54, 'ان کو ان کا اجر دوبالا دیا جائے گا بدلہ ان کے صبر کا اور وہ بھلائی سے برائی کو ٹالتے ہیں اور ہمارے دیے سے کچھ ہماری راہ میں خرچ کرتے ہیں'),
(3307, 28, 55, 'اور جب بیہودہ بات سنتے ہیں اس سے تغافل کرتے ہیں اور کہتے ہیں ہمارے لیے ہمارے عمل اور تمہارے لیے تمہارے عمل، بس تم پر سلام ہم جاہلوں کے غرضی (چاہنے والے) نہیں'),
(3308, 28, 56, 'بیشک یہ نہیں کہ تم جسے اپنی طرف سے چاہو ہدایت کردو ہاں اللہ ہدایت فرماتا ہے جسے چاہے، اور وہ خوب جانتا ہے ہدایت والوں کو'),
(3309, 28, 57, 'اور کہتے ہیں اگر ہم تمہارے ساتھ ہدایت کی پیروی کریں تو لوگ ہمارے ملک سے ہمیں اچک لے جائیں گے کیا ہم نے انہیں جگہ نہ دی امان والی حرم میں جس کی طرف ہر چیز کے پھل لائے جاتے ہیں ہمارے پاس کی روزی لیکن ان میں اکثر کو علم نہیں'),
(3310, 28, 58, 'اور کتنے شہر ہم نے ہلاک کردیے جو اپنے عیش پر اترا گئے تھے تو یہ ہیں ان کے مکان کہ ان کے بعد ان میں سکونت نہ ہوئی مگر کم اور ہمیں وارث ہیں'),
(3311, 28, 59, 'اور تمہارا رب شہروں کو ہلاک نہیں کرتا جب تک ان کے اصل مرجع میں رسول نہ بھیجے جو ان پر ہماری آیتیں پڑھے اور ہم شہروں کو ہلاک نہیں کرتے مگر جبکہ ان کے ساکن ستمگار ہوں'),
(3312, 28, 60, 'اور جو کچھ چیز تمہیں دی گئی ہے اور دنیوی زندگی کا برتاوا اور اس کا سنگھارہے اور جواللہ کے پاس ہے اور وہ بہتر اور زیادہ باقی رہنے والا تو کیا تمہیں عقل نہیں'),
(3313, 28, 61, 'تو کیا وہ جسے ہم نے اچھا وعدہ دیا تو وہ اس سے ملے گا اس جیسا ہے جسے ہم نے دنیوی زندگی کا برتاؤ برتنے دیا پھر وہ قیامت کے دن گرفتار کرکے حاضر لایا جائے گا'),
(3314, 28, 62, 'اور جس دن انہیں ندا کرے گا تو فرمائے گا کہاں ہیں میرے وہ شریک جنہیں تم گمان کرتے تھے،'),
(3315, 28, 63, 'کہیں گے وہ جن پر بات ثابت ہوچکی اے ہمارے رب یہ ہیں وہ جنہیں ہم نے گمراہ کیا، ہم نے انہیں گمراہ کیا جیسے خود گمراہ ہوئے تھے ہم ان سے بیزار ہوکر تیری طرف رجوع لاتے ہیں وہ ہم کو نہ پوجتے تھے'),
(3316, 28, 64, 'اور ان سے فرمایا جائے گا اپنے شریکوں کو پکارو تو وہ پکاریں گے تو وہ ان کی نہ سنیں گے اور دیکھیں گے عذاب، کیا اچھا ہوتا اگر وہ راہ پاتے'),
(3317, 28, 65, 'اور جس دن انہیں ندا کرتے گا تو فرمائے گا تم نے رسولوں کو کیا جواب دیا'),
(3318, 28, 66, 'تو اس دن ان پر خبریں اندھی ہوجائیں گی تو وہ کچھ پوجھ گچھ نہ کریں گے'),
(3319, 28, 67, 'تو وہ جس نے توبہ کی اور ایمان لایا اور اچھا کام کیا قریب ہے کہ وہ راہ یاب ہو،'),
(3320, 28, 68, 'اور تمہارا رب پیدا کرتا ہے جو چاہے اور پسند فرماتا ہے ان کا کچھ اختیار نہیں، پاکی اور برتری ہے اللہ کو ان کے شرک سے،'),
(3321, 28, 69, 'اور تمہارا رب جانتا ہے جو ان کے سینوں میں چھپا ہے اور جو ظاہر کرتے ہیں'),
(3322, 28, 70, 'اور وہی ہے اللہ کہ کوئی خدا نہیں اس کے سوا اسی کی تعریف ہے دنیا اور آخرت میں اور اسی کا حکم ہے اور اسی کی طرف پھر جاؤ گے،'),
(3323, 28, 71, 'تم فرماؤ بھلا دیکھو تو اگر اللہ ہمیشہ تم پر قیامت تک رات رکھے تو اللہ کے سوا کون خدا ہے جو تمہیں روشنی لادے تو کیا تم سنتے نہیں'),
(3324, 28, 72, 'تم فرماؤ بھلا دیکھو تو اگر اللہ قیامت تک ہمیشہ دن رکھے تو اللہ کے سوا کون خدا ہے جو تمہیں رات لادے جس میں آرام کرو تو کیا تمہیں سوجھتا نہیں'),
(3325, 28, 73, 'اور اس نے اپنی مہر سے تمہارے لیے رات اور دن بنائے کہ رات میں آرام کرو اور دن میں اس کا فضل ڈھونڈو اور اس لیے کہ تم حق مانو'),
(3326, 28, 74, 'اور جس دن انہیں ندا کرتے گا تو فرمائے گا، کہاں ہیں؟ میرے وہ شریک جو تم بکتے تھے،'),
(3327, 28, 75, 'اور ہر گروہ میں سے ایک گواہ نکال کر فرمائیں گے اپنی دلیل لاؤ تو جان لیں گے کہ حق اللہ کا ہے اور ان سے کھوئی جائیں گی جو بناوٹیں کرتے تھے'),
(3328, 28, 76, 'بیشک قارون موسیٰ کی قوم سے تھا پھر اس نے ان پر زیادتی کی اور ہم نے اس کو اتنے خزانے دیے جن کی کنجیاں ایک زور آور جماعت پر بھاری تھیں، جب اس سے اس کی قوم نے کہا اِترا نہیں بیشک اللہ اِترانے والوں کو دوست نہیں رکھتا،'),
(3329, 28, 77, 'اور جو مال تجھے اللہ نے دیا ہے اس سے آخرت کا گھر طلب کر اور دنیا میں اپنا حصہ نہ بھول اور احسان کر جیسا اللہ نے تجھ پر احسان کیا اور زمیں میں فساد نہ چاه بے شک اللہ فسادیوں کو دوست نہیں رکھتا،'),
(3330, 28, 78, 'بو لا یہ تو مجھے ایک علم سے ملا ہے جو میرے پاس ہے اور کیا اسے یہ نہیں معلوم کہ اللہ نے اس سے پہلے وہ سنگتیں (قومیں) ہلاک فرمادیں جن کی قوتیں اس سے سخت تھیں اور جمع اس سے زیادہ اور مجرموں سے ان کے گناہوں کی پوچھ نہیں'),
(3331, 28, 79, 'تو اپنی قومی پر نکلا اپنی آرائش میں بولے وہ جو دنیا کی زندگی چاہتے ہیں کسی طرح ہم کو بھی ایسا ملتا جیسا قارون کو ملا بیشک اس کا بڑا نصیب ہے،'),
(3332, 28, 80, 'اور بولے وہ جنہیں علم دیا گیا خرابی ہو تمہاری، اللہ کا ثواب بہتر ہے اس کے لیے جو ایمان لائے اور اچھے کام کرے اور یہ انہیں کو ملتا ہے جو صبر والے ہیں'),
(3333, 28, 81, 'تو ہم نے اسے اور اس کے گھر کو زمین میں دھنسایا تو اس کے پاس کوئی جماعت نہ تھی کہ اللہ سے بچانے میں اس کی مدد کرتی اور نہ وہ بدلہ لے سکا'),
(3334, 28, 82, 'اور کل جس نے اس کے مرتبہ کی آرزو کی تھی صبح کہنے لگے عجب بات ہے اللہ رزق وسیع کرتا ہے اپنے بندوں میں جس کے لیے چاہے اور تنگی فرماتا ہے اگر اللہ ہم پر احسان فرماتا تو ہمیں بھی دھنسادیتا، اے عجب، کافروں کا بھلا نہیں،'),
(3335, 28, 83, 'یہ آخرت کا گھر ہم ان کے لیے کرتے ہیں جو زمین میں تکبر نہیں چاہتے اور نہ فساد، اور عاقبت پرہیزگاروں ہی کی ہے،'),
(3336, 28, 84, 'جو نیکی لائے اس کے لیے اس سے بہتر ہے اور جو بدی لائے بدکام والوں کو بدلہ نہ ملے گا مگر جتنا کیا تھا،'),
(3337, 28, 85, 'بیشک جس نے تم پر قرآن فرض کیا وہ تمہیں پھیر لے جائے گا جہاں پھرنا چاہتے ہو تم فرماؤ، میرا رب خوب جانتا ہے اسے جو ہدایت لایا اور جو کھلی گمراہی میں ہے'),
(3338, 28, 86, 'اور تم امید نہ رکھتے تھے کہ کتاب تم پر بھیجی جائے گی ہاں تمہارے رب نے رحمت فرمائی تو تم ہرگز کافروں کی پشتی (مدد) نہ کرنا'),
(3339, 28, 87, 'اور ہرگز وہ تمہیں اللہ کی آیتوں سے نہ روکیں بعد اس کے کہ وہ تمہاری طرف اتاری گئیں اور اپنے رب کی طرف بلاؤ اور ہرگز شرک والوں میں سے نہ ہونا'),
(3340, 28, 88, 'اور اللہ کے ساتھ دوسرے خدا کو نہ پوج اس کے سوا کوئی خدا نہیں ہر چیز فانی ہے، سوا اس کی ذات کے، اسی کا حکم ہے اور اسی کی طرف پھر جاؤ گے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الم'),
(3342, 29, 2, 'کیا لوگ اس گھمنڈ میں ہیں کہ اتنی بات پر چھوڑ دیے جائیں گے کہ کہیں ہم ایمان لائے، اور ان کی آزمائش نہ ہوگی'),
(3343, 29, 3, 'اور بیشک ہم نے ان سے اگلوں کو جانچا تو ضرور اللہ سچوں کو دیکھے گا اور ضرور جھوٹوں کو دیکھے گا'),
(3344, 29, 4, 'یا یہ سمجھے ہوئے ہیں وہ جو برے کام کرتے ہیں کہ ہم سے کہیں نکل جائیں گے کیا ہی برا حکم لگاتے ہیں،'),
(3345, 29, 5, 'جسے اللہ سے ملنے کی امید ہو تو بیشک اللہ کی میعاد ضرور آنے والی ہے اور وہی سنتا جانتا ہے'),
(3346, 29, 6, 'اور جو اللہ کی راہ میں کوشش کرے تو اپنے ہی بھلے کو کوشش کرتا ہے بیشک اللہ بے پرواہ ہے سارے جہان سے'),
(3347, 29, 7, 'اور جو ایمان لائے اور اچھے کام کیے ہم ضرور ان کی برائیاں اتار دیں گے اور ضرور انہیں اس کام پر بدلہ دیں گے جو ان کے سب کاموں میں اچھا تھا'),
(3348, 29, 8, 'اور ہم نے آدمی کو تاکید کی اپنے ماں باپ کے ساتھ بھلائی کی اور اگر تو وہ تجھ سے کوشش کریں کہ تو میرا شریک ٹھہرائے جس کا تجھے علم نہیں تو تُو ان کا کہا نہ مان میری ہی طرف تمہارا پھرنا ہے تو میں بتادوں گا تمہیں جو تم کرتے تھے'),
(3349, 29, 9, 'اور جو ایمان لائے اور اچھے کام کیے ضرور ہم انہیں نیکوں میں شامل کریں گے'),
(3350, 29, 10, 'اور بعض آدمی کہتے ہیں ہم اللہ پر ایمان لائے پھر جب اللہ کی راہ میں انہیں کوئی تکلیف دی جاتی ہے تو لوگوں کے فتنہ کو اللہ کے عذاب کے برابر سمجھتے ہیں اور اگر تمہارے رب کے پاس سے مدد آئے تو ضرور کہیں گے ہم تو تمہارے ہی ساتھ تھے کیا اللہ خوب نہیں جانتا جو کچھ جہاں بھر کے دلوں میں ہے'),
(3351, 29, 11, 'اور ضرور اللہ ظاہر کردے گا ایمان والوں کو اور ضرور ظاہر کردے گا منافقوں کو'),
(3352, 29, 12, 'اور کافر مسلمانوں سے بولے ہماری راہ پر چلو اور ہم تمہارے گناہ اٹھالیں گے حالانکہ وہ ان کے گناہوں میں سے کچھ نہ اٹھائیں گے، بیشک وہ جھوٹے ہیں،'),
(3353, 29, 13, 'اور بیشک ضرور اپنے بوجھ اٹھائیں گے اور اپنے بوجھوں کے ساتھ اور بوجھ اور ضرور قیامت کے دن پوچھے جائیں گے جو کچھ بہتان اٹھاتے تھے'),
(3354, 29, 14, 'اور بیشک ہم نے نوح کو اس کی قوم کی طرف بھیجا تو وہ ان میں پچاس سال کم ہزار برس رہا تو انہیں طوفان نے ا ٓ لیا اور وہ ظالم تھے'),
(3355, 29, 15, 'تو ہم نے اسے اور کشتی والوں کو بچالیا اور اس کشتی کو سارے جہاں کے لیے نشانی کیا'),
(3356, 29, 16, 'اور ابراہیم کو جب اس نے اپنی قوم سے فرمایا کہ اللہ کو پوجو اور اس سے ڈرو، اس میں تمہارا بھلا ہے اگر تم جانتے،'),
(3357, 29, 17, 'تم تو اللہ کے سوا بتوں کو پوجتے ہو اور نرا جھوٹ گڑ ھتے ہو بے شک وه جنھیں تم اللہ کے سوا پو جتے ہو تمہاری روزی کے کچھ مالک نہیں تو اللہ کے پاس رزق ڈھونڈو اور اس کی بندگی کرو اور اس کا احسان مانو، تمہیں اسی کی طرف پھرنا ہے'),
(3358, 29, 18, 'اور اگر تم جھٹلاؤ تو تم سے پہلے کتنے ہی گروہ جھٹلا چکے ہیں اور رسول کے ذمہ نہیں مگر صاف پہنچا دینا،'),
(3359, 29, 19, 'اور کیا انہوں نے نہ دیکھا اللہ کیونکر خلق کی ابتداء فرماتا ہے پھر اسے دوبارہ بنائے گا بیشک یہ اللہ کو آسان ہے'),
(3360, 29, 20, 'تم فرماؤ زمین میں سفر کرکے دیکھو اللہ کیونکر پہلے بناتا ہے پھر اللہ دوسری اٹھان اٹھاتا ہے بیشک اللہ سب کچھ کرسکتا ہے،'),
(3361, 29, 21, 'عذاب دیتا ہے جسے چاہے اور رحم فرماتا ہے جس پر چاہے اور تمہیں اسی کی طرف پھرنا ہے،'),
(3362, 29, 22, 'اور نہ تم زمین میں قابو سے نکل سکو اور نہ آسمان میں اور تمہارے لیے اللہ کے سوا نہ کوئی کام بنانے والا اور نہ مددگار،'),
(3363, 29, 23, 'اور وہ جنہوں نے میری آیتوں اور میرے ملنے کو نہ مانا وہ ہیں جنہیں میری رحمت کی آس نہیں اور ان کے لیے دردناک عذاب ہے'),
(3364, 29, 24, 'تو اس کی قوم کو کچھ جواب بن نہ آیا مگر یہ بولے انہیں قتل کردو یا جلادو تو اللہ نے اسے آگ سے بچالیا بیشک اس میں ضرور نشانیاں ہیں ایمان والوں کے لیے'),
(3365, 29, 25, 'اور ابراہیم نے فرمایا تم نے تو اللہ کے سوا یہ بت بنالیے ہیں جن سے تمہاری دوستی یہی دنیا کی زندگی تک ہے پھر قیامت کے دن تم میں ایک دوسرے کے ساتھ کفر کرے گا اور ایک دوسرے پر لعنت ڈالے گا اور تم سب کا ٹھکانا جہنم ہے اور تمہارا کوئی مددگار نہیں'),
(3366, 29, 26, 'تو لوط اس پر ایمان لایا اور ابراہیم نے کہا میں اپنے رب کی طرف ہجرت کرتا ہوں بیشک وہی عزت و حکمت والا ہے،'),
(3367, 29, 27, 'اور ہم نے اسے اسحاق اور یعقوب عطا فرمائے اور ہم نے اس کی اولاد میں نبوت اور کتاب رکھی اور ہم نے دنیا میں اس کا ثواب اسے عطا فرمایا اور بیشک آخرت میں وہ ہمارے قرب خاص کے سزاواروں میں ہے'),
(3368, 29, 28, 'اور لوط کو نجات دی جب اس نے اپنی قوم سے فرمایا تم بیشک بے حیائی کا کام کرتے ہو، کہ تم سے پہلے دنیا بھر میں کسی نے نہ کیا'),
(3369, 29, 29, 'کیا تم مردوں سے بدفعلی کرتے ہو اور راہ مارتے ہو اور اپنی مجلس میں بری بات کرتے ہو تو اس کی قوم کا کچھ جواب نہ ہوا مگر یہ کہ بولے ہم پر اللہ کا عذاب لاؤ اگر تم سچے ہو'),
(3370, 29, 30, 'عرض کی، اے میرے رب! میری مدد کر ان فسادی لوگوں پر'),
(3371, 29, 31, 'اور جب ہمارے فرشتے ابراہیم کے پاس مژدہ لے کر آئے بولے ہم ضرور اس شہر والوں کو ہلاک کریں گے بیشک اس کے بسنے والے ستمگاروں ہیں،'),
(3372, 29, 32, 'کہا اس میں تو لوط ہے فرشتے بولے ہمیں خوب معلوم ہے جو کوئی اس میں ہے، ضرور ہم اسے اور اس کے گھر والوں کو نجات دیں گے مگر اس کی عورت کو، وہ رہ جانے والوں میں ہے'),
(3373, 29, 33, 'اور جب ہمارے فرشتے لوط کے پاس آئے ان کا آنا اسے ناگوار ہوا اور ان کے سبب دل تنگ ہوا اور انہوں نے کہا نہ ڈریے اور نہ غم کیجئے بیشک ہم آپ کو اور آپ کے گھر والوں کو نجات دیں گے مگر آپ کی عورت وہ رہ جانے والوں میں ہے،'),
(3374, 29, 34, 'بیشک ہم اس شہر والوں پر آسمان سے عذاب اتارنے والے ہیں بدلہ ان کی نافرمانیوں کا،'),
(3375, 29, 35, 'اور بیشک ہم نے اس سے روشن نشانی باقی رکھی عقل والوں کے لیے'),
(3376, 29, 36, 'مدین کی طرف ان کے ہم قوم شعیب کو بھیجا تو اس نے فرمایا، اے میری قوم! اللہ کی بندگی کرو اور پچھلے دن کی امید رکھو اور زمین میں فساد پھیلاتے نہ پھرو،'),
(3377, 29, 37, 'تو انہوں نے اسے جھٹلایا تو انہیں زلزلے نے آلیا تو صبح اپنے گھروں میں گھٹنوں کے بل پڑے رہ گئے'),
(3378, 29, 38, 'اور عاد اور ثمود کو ہلاک فرمایا اور تمہیں ان کی بستیاں معلوم ہوچکی ہیں اور شیطان نے ان کے کوتک ان کی نگاہ میں بھلے کر دکھائے اور انہیں راہ سے روکا اور انہیں سوجھتا تھا'),
(3379, 29, 39, 'اور قارون اور فرعون اور ہامان کو اور بیشک ان کے پاس موسیٰ روشن نشانیاں لے کر آیا تو انہوں نے زمین میں تکبر کیا اور وہ ہم سے نکل کر جانے والے نہ تھے'),
(3380, 29, 40, 'تو ان میں ہر ایک کو ہم نے اس کے گناہ پر پکڑا تو ان میں کسی پر ہم نے پتھراؤ بھیجا اور ان میں کسی کو چنگھاڑ نے آلیا اور ان میں کسی کو زمین میں دھنسادیا اور ان میں کسی کو ڈبو دیا اور اللہ کی شان نہ تھی کہ ان پر ظلم کرے ہاں وہ خود ہی اپنی جانوں پر ظلم کرتے تھے،'),
(3381, 29, 41, 'ان کی مثال جنہوں نے اللہ کے سوا اور مالک بنالیے ہیں مکڑی کی طرح ہے، اس نے جالے کا گھر بنایا اور بیشک سب گھروں میں کمزور گھر مکڑی کا گھر کیا اچھا ہوتا اگر جانتے'),
(3382, 29, 42, 'اللہ جانتا ہے جس چیز کی اس کے سوا پوجا کرتے ہیں اور وہی عزت و حکمت والا ہے'),
(3383, 29, 43, 'اور یہ مثالیں ہم لوگوں کے لیے بیان فرماتے ہیں، اور انہیں نہیں سمجھتے مگر علم والے'),
(3384, 29, 44, 'اللہ نے آسمان اور زمین حق بنائے، بیشک اس میں نشانی ہے مسلمانوں کے لیے،'),
(3385, 29, 45, 'اے محبوب! پڑھو جو کتاب تمہاری طرف وحی کی گئی اور نماز قائم فرماؤ، بیشک نماز منع کرتی ہے بے حیائی اور بری بات سے اور بیشک اللہ کا ذکر سب سے بڑا اور اللہ جانتا ہے جو تم کرتے ہو،'),
(3386, 29, 46, 'اور اے مسلمانو! کتابیوں سے نہ جھگڑو مگر بہتر طریقہ پر مگر وہ جنہوں نے ان میں سے ظلم کیا اور کہو ہم ایمان لائے اس پر جو ہماری طرف اترا اور جو تمہاری طرف اترا اور ہمارا تمہارا ایک معبود ہے اور ہم اس کے حضور گردن رکھتے ہیں'),
(3387, 29, 47, 'اور اے محبوب! یونہی تمہاری طرف کتاب اتاری تو وہ جنہیں ہم نے کتا ب عطا فرمائی اس پر ایمان لاتے ہیں، اور کچھ ان میں سے ہیں جو اس پر ایمان لاتے ہیں، اور ہماری آیتوں سے منکر نہیں ہوتے مگر'),
(3388, 29, 48, 'اور اس سے پہلے تم کوئی کتاب نہ پڑھتے تھے اور نہ اپنے ہاتھ سے کچھ لکھتے تھے یوں ہوتا تو باطل ضرور شک لاتے'),
(3389, 29, 49, 'بلکہ وہ روشن آیتیں ہیں ان کے سینوں میں جن کو علم دیا گیا اور ہماری آیتوں کا انکار نہیں کرتے مگر ظالم'),
(3390, 29, 50, 'اور بولے کیوں نہ اتریں کچھ نشانیاں ان پر ان کے رب کی طرف سے تم فرماؤ نشانیاں تو اللہ ہی کے پاس ہیں اور میں تو یہی صاف ڈر سنانے والا ہوں'),
(3391, 29, 51, 'اور کیا یہ انہیں بس نہیں کہ ہم نے تم پر کتاب اتاری جو ان پر پڑھی جاتی ہے بیشک اس میں رحمت اور نصیحت ہے ایمان والوں کے لیے،'),
(3392, 29, 52, 'تم فرماؤ اللہ بس ہے میرے اور تمہارے درمیان گواہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہے، اور وہ جو باطل پر یقین لائے اور اللہ کے منکر ہوئے وہی گھاٹے میں ہیں،'),
(3393, 29, 53, 'اور تم سے عذاب کی جلدی کرتے ہیں اور اگر ایک ٹھہرائی مدت نہ ہوتی تو ضرور ان پر عذاب آجاتا اور ضرور ان پر اچانک آئے گا جب وہ بے خبر ہوں گے،'),
(3394, 29, 54, 'تم سے عذاب کی جلدی مچاتے ہیں، اور بیشک جہنم گھیرے ہوئے ہے کافروں کو'),
(3395, 29, 55, 'جس دن انہیں ڈھانپے گا عذاب ان کے اوپر اور ان کے پاؤں کے نیچے سے اور فرمائے گا چکھو اپنے کیے کا مزہ'),
(3396, 29, 56, 'اے میرے بندو! جو ایمان لائے بیشک میری زمین وسیع ہے تو میری ہی بندگی کرو'),
(3397, 29, 57, 'ہر جان کو موت کا مز ہ چکھنا ہے پھر ہماری ہی طرف پھروگے'),
(3398, 29, 58, 'اور بیشک جو ایمان لائے اور اچھے کام کیے ضرور ہم انہیں جنت کے بالا خانوں پر جگہ دیں گے جن کے نیچے نہریں بہتی ہوں گی ہمیشہ ان میں رہیں گے، کیا ہی اچھا اجر کام والوں کا'),
(3399, 29, 59, 'وہ جنہوں نے صبر کیا اور اپنے رب ہی پر بھروسہ رکھتے ہیں'),
(3400, 29, 60, 'اور زمین پر کتنے ہی چلنے والے ہیں کہ اپنی روزی ساتھ نہیں رکھتے اللہ روزی دیتا ہے انہیں اور تمہیں اور وہی سنتا جانتا ہے'),
(3401, 29, 61, 'اور اگر تم ان سے پوچھو کس نے بنائے آسمان اور زمین اور کام میں لگائے سورج اور چاند تو ضرور کہیں گے اللہ نے، تو کہاں اوندھے جاتے ہیں'),
(3402, 29, 62, 'اللہ کشادہ کرتا ہے رزق اپنے بندوں میں جس کے لیے چاہے اور تنگی فرماتا ہے جس کے لیے چاہے، بیشک اللہ سب کچھ جانتا ہے،'),
(3403, 29, 63, 'اور جو تم ان سے پوچھو کس نے اتارا آسمان سے پانی تو اس کے سبب زمین زندہ کردی مَرے پیچھے ضرور کہیں گے اللہ نے تم فرماؤ سب خوبیاں اللہ کو، بلکہ ان میں اکثر بے عقل ہیں'),
(3404, 29, 64, 'اور یہ دنیا کی زندگی تو نہیں مگر کھیل کود اور بیشک آخرت کا گھر ضرور وہی سچی زندگی ہے کیا اچھا تھا اگر جانتے'),
(3405, 29, 65, 'پھر جب کشتی میں سوار ہوتے ہیں اللہ کو پکارتے ہیں ایک اسی عقیدہ لاکر پھر جب وہ انہیں خشکی کی طرف بچا لاتا ہے جبھی شرک کرنے لگتے ہیں'),
(3406, 29, 66, 'کہ ناشکری کریں ہماری دی ہوئی نعمت کی اور برتیں۔ تو اب جانا چاہتے ہیں،'),
(3407, 29, 67, 'اور کیا انہوں نے یہ نہ دیکھا کہ ہم نے حرمت والی زمین پناہ بنائی اور ان کے آس پاس والے لوگ اچک لیے جاتے ہیں تو کیا باطل پر یقین لاتے ہیں اور اللہ کی دی ہوئی نعمت سے ناشکری کرتے ہیں،'),
(3408, 29, 68, 'اور اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے یا حق کو جھٹلائے جب وہ اس کے پاس آئے، کیا جہنم میں کافروں کا ٹھکانا نہیں'),
(3409, 29, 69, 'اور جنہوں نے ہماری راہ میں کوشش کی ضرور ہم انہیں اپنے راستے دکھادیں گے اور بیشک اللہ نیکوں کے ساتھ ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الم'),
(3411, 30, 2, 'رومی مغلوب ہوئے،'),
(3412, 30, 3, 'پاس کی زمین میں اور اپنی مغلوبی کے عنقریب غالب ہوں گے'),
(3413, 30, 4, 'چند برس میں حکم اللہ ہی کا ہے آگے اور پیچھے اور اس دن ایمان والے خوش ہوں گے،'),
(3414, 30, 5, 'اللہ کی مدد سے مدد کرتا ہے جس کی چاہے، اور وہی عزت والا مہربان،'),
(3415, 30, 6, 'اللہ کا وعدہ اللہ اپنا وعدہ خلاف نہیں کرتا لیکن بہت لوگ نہیں جانتے'),
(3416, 30, 7, 'جانتے ہیں آنکھوں کے سامنے کی دنیوی زندگی اور وہ آخرت سے پورے بے خبر ہیں،'),
(3417, 30, 8, 'کیا انہوں نے اپنے جی میں نہ سوچا کہ، اللہ نے پیدا نہ کیے آسمان اور زمین اور جو کچھ ان کے درمیان ہے مگر حق اور ایک مقرر میعاد سے اور بیشک بہت سے لوگ اپنے رب سے ملنے کا انکار رکھتے ہیں'),
(3418, 30, 9, 'اور کیا انہوں نے زمین میں سفر نہ کیا کہ دیکھتے کہ ان سے اگلوں کا انجام کیسا ہوا وہ ان سے زیادہ زور آور تھے اور زمین جوتی اور آباد کی ان کی آبادی سے زیادہ اور ان کے رسول ان کے پاس روشن نشانیاں لائے تو اللہ کی شان نہ تھی کہ ان پر ظلم کرتا ہاں وہ خود ہی اپنی جانوں پر ظلم کرتے تھے'),
(3419, 30, 10, 'پھر جنہوں نے حد بھر کی برائی کی ان کا انجام یہ ہوا کہ اللہ کی آیتیں جھٹلانے لگے اور ان کے ساتھ تمسخر کرتے،'),
(3420, 30, 11, 'اللہ پہلے بناتا ہے پھر دوبارہ بنائے گا پھر اس کی طرف پھروگے'),
(3421, 30, 12, 'اور جس دن قیامت قائم ہوگی مجرموں کی آس ٹوٹ جائے گی'),
(3422, 30, 13, 'اور ان کے شریک ان کے سفارشی نہ ہوں گے اور وہ اپنے شریکوں سے منکر ہوجائیں گے،'),
(3423, 30, 14, 'اور جس دن قیامت قائم ہوگی اس دن الگ ہوجائیں گے'),
(3424, 30, 15, 'تو وہ جو ایمان لائے اور اچھے کام کیے باغ کی کیاری میں ان کی خاطرداری ہوگی'),
(3425, 30, 16, 'اور جو کافر ہوئے اور ہماری آیتیں اور آخرت کا ملنا جھٹلایا وہ عذاب میں لادھرے (ڈال دیے) جائیں گے'),
(3426, 30, 17, 'تو اللہ کی پاکی بولو جب شام کرو اور جب صبح ہو'),
(3427, 30, 18, 'اور اسی کی تعریف ہے آسمانوں اور زمین میں اور کچھ دن رہے اور جب تمہیں دوپہر ہو'),
(3428, 30, 19, 'وہ زندہ کو نکالتا ہے مردے سے اور مردے کو نکالتا ہے زندہ سے اور زمین کو جٕلا تا ہے اس کے مرے پیچھے اور یوں ہی تم نکالے جاؤ گے'),
(3429, 30, 20, 'اور اس کی نشانیوں سے ہے یہ کہ تمہیں پیدا کیا مٹی سے پھر جبھی تو انسان ہو دنیا میں پھیلے ہوئے،'),
(3430, 30, 21, 'اور اس کی نشانیوں سے ہے کہ تمہارے لیے تمہاری ہی جنس سے جوڑے بنائے کہ ان سے آرام پاؤ اور تمہارے آپس میں محبت اور رحمت رکھی بیشک اس میں نشانیاں ہیں دھیان کرنے والوں کے لیے،'),
(3431, 30, 22, 'اور اس کی نشانیوں سے ہے آسمانوں اور زمین کی پیدائش اور تمہاری زبانوں اور رنگتوں کا اختلاف بیشک اس میں نشانیاں ہیتں جاننے والوں کے لیے،'),
(3432, 30, 23, 'اور اس کی نشانیوں میں ہے رات اور دن میں تمہارا سونا اور اس کا فضل تلاش کرنا بیشک اس میں نشانیاں ہیں سننے والوں کے لیے'),
(3433, 30, 24, 'اور اس کی نشانیوں سے ہے کہ تمہیں بجلی دکھاتا ہے ڈراتی اور امید دلاتی اور آسمان سے پانی اتارتا ہے، تو اس سے زمین کو زندہ کرتا ہے اس کے مرے پیچھے، بیشک اس میں نشانیاں ہیں عقل والوں کے لیے'),
(3434, 30, 25, 'اور اس کی نشانیوں سے ہے کہ اس کے حکم سے آسمان اور زمین قائم ہیں پھر جب تمہیں زمین سے ایک ندا فرمائے گا جبھی تم نکل پڑو گے'),
(3435, 30, 26, 'اور اسی کے ہیں جو کوئی آسمانوں اور زمین میں ہیں، سب اس کے زیر حکم ہیں،'),
(3436, 30, 27, 'اور وہی ہے کہ اول بنا تا ہے پھر اسے دوبارہ بنائے گا اور یہ تمہاری سمجھ میں اس پر زیا دہ آسان ہونا چاہیے اور اسی کے لیے ہے سب سے برتر شان آسمانوں اور زمین میں اور وہی عزت و حکمت والا ہے،'),
(3437, 30, 28, 'تمہارے لیے ایک کہاوت بیان فرماتا ہے خود تمہارے اپنے حال سے کیا تمہارے لیے تمہارے ہاتھ کے غلاموں میں سے کچھ شریک ہیں اس میں جو ہم نے تمہیں روزی دی تو تم سب اس میں برابر ہو تم ان سے ڈرو جیسے آپس میں ایک دوسرے سے ڈرتے ہو ہم ایسی مفصل نشانیاں بیان فرماتے ہیں عقل والوں کے لیے،'),
(3438, 30, 29, 'بلکہ ظالم اپنی خواہشوں کے پیچھے ہولیے بے جانے تو اسے کون ہدایت کرے جسے خدا نے گمراہ کیا اور ان کا کوئی مددگار نہیں'),
(3439, 30, 30, 'تو اپنا منہ سیدھا کرو اللہ کی اطاعت کے لیے ایک اکیلے اسی کے ہوکر اللہ کی ڈالی ہوئی بنا جس پر لوگوں کو پیدا کیا اللہ کی بنائی چیز نہ بدلنا یہی سیدھا دین ہے، مگر بہت لوگ نہیں جانتے'),
(3440, 30, 31, 'اس کی طرف رجوع لاتے ہوئے اور اس سے ڈرو اور نماز قائم رکھو اور مشرکوں سے نہ ہو،'),
(3441, 30, 32, 'ان میں سے جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کردیا اور ہوگئے گروہ گروہ، ہر گروہ جو اس کے پاس ہے اسی پر خوش ہے'),
(3442, 30, 33, 'اور جب لوگوں کو تکلیف پہنچتی ہے تو اپنے رب کو پکارتے ہیں اس کی طرف رجوع لاتے ہوئے پھر جب وہ انہیں اپنے پاس سے رحمت کا مزہ دیتا ہے جبھی ان میں سے ایک گروہ اپنے رب کا شریک ٹھہرانے لگتا ہے،'),
(3443, 30, 34, 'کہ ہمارے دیے کی ناشکری کریں، تو برت لو اب قریب جاننا چاہتے ہو'),
(3444, 30, 35, 'یا ہم نے ان پر کوئی سند اتاری کہ ہو انہیں ہمارے شریک بتارہی ہے'),
(3445, 30, 36, 'اور جب ہم لوگوں کو رحمت کا مزہ دیتے ہیں اس پر خوش ہوجاتے ہیں اور اگر انہیں کوئی برائی پہنچے بدلہ اس کا جو ان کے ہاتھوں نے بھیجا جبھی وہ ناامید ہوجاتے ہیں'),
(3446, 30, 37, 'اور کیا انہوں نے نہ دیکھا کہ اللہ رزق وسیع فرماتا ہے جس کے لیے چاہے اور تنگی فرماتا ہے جس کے لیے چاہے بیشک اس میں نشانیاں ہیں ایمان والوں کے لیے،'),
(3447, 30, 38, 'تو رشتہ دار کو اس کا حق دو اور مسکین اور مسافر کو یہ بہتر ہے ان کے لیے جو اللہ کی رضا چاہتے ہیں اور انہیں کا کام بنا،'),
(3448, 30, 39, 'اور تم جو چیز زیادہ لینے کو دو کہ دینے والے کے مال بڑھیں تو وہ اللہ کے یہاں نہ بڑھے گی اور جو تم خیرات دو اللہ کی رضا چاہتے ہوئے تو انہیں کے دُونے ہیں'),
(3449, 30, 40, 'اللہ ہے جس نے تمہیں پیدا کیا پھر تمہیں روزی دی پھر تمہیں مارے گا پھر تمہیں جِلائے گا کیا تمہارے شریکوں میں بھی کوئی ایسا ہے جو ان کاموں میں سے کچھ کرے پاکی اور برتری ہے اسے ان کے شرک سے،'),
(3450, 30, 41, 'چمکی خرابی خشکی اور تری میں ان برائیوں سے جو لوگوں کے ہاتھوں نے کمائیں تاکہ انہیں ان کے بعض کوتکوں (برے کاموں) کا مزہ چکھائے کہیں وہ باز آئیں'),
(3451, 30, 42, 'تم فرماؤ زمین میں چل کر دیکھو کیا انجام ہوا اگلوں کا، ان میں بہت مشرک تھے'),
(3452, 30, 43, 'تو اپنا منہ سیدھا کر عبادت کے لیے قبل اس کے کہ وہ دن آئے جسے اللہ کی طرف ٹلنا نہیں اس دن الگ پھٹ جائیں گے'),
(3453, 30, 44, 'جو کفر کرے اس کے کفر کا وبال اسی پر اور جو اچھا کام کریں وہ اپنے ہی لیے تیاری کررہے ہیں'),
(3454, 30, 45, 'تاکہ صلہ دے انہیں جو ایمان لائے اور اچھے کام کیے اپنے فضل سے، بیشک وہ کافروں کو دوست نہیں رکھتا،'),
(3455, 30, 46, 'اور اس کی نشانیوں سے ہے کہ ہوائیں بھیجتا ہے مژدہ سناتی اور اس لیے کہ تمہیں اپنی رحمت کا ذائقہ دے اور اس لیے کہ کشتی اس کے حکم سے چلے اور اس لیے کہ اس کا فضل تلاش کرو اور اس لیے کہ تم حق مانو'),
(3456, 30, 47, 'اور بیشک ہم نے تم سے پہلے کتنے رسول ان کی قوم کی طرف بھیجے تو وہ ان کے پاس کھلی نشانیاں لائے پھر ہم نے مجرموں سے بدلہ لیا اور ہمارے ذمہٴ کرم پر ہے مسلمانوں کی مدد فرمانا'),
(3457, 30, 48, 'اللہ ہے کہ بھیجتا ہے ہوائیں کہ ابھارتی ہیں بادل پھر اسے پھیلادیتا ہے آسمان میں جیسا چاہے اور اسے پارہ پارہ کرتا ہے تو تُو دیکھے کہ اس کے یبچ میں مینھ نکل رہا ہے پھر جب اسے پہنچاتا ہے اپنے بندوں میں جس کی طرف چاہے جبھی وہ خوشیاں مناتے ہیں،'),
(3458, 30, 49, 'اگرچہ اس کے اتارنے سے پہلے آس توڑے ہوئے تھے،'),
(3459, 30, 50, 'تو اللہ کی رحمت کے اثر دیکھو کیونکر زمین کو جِلاتا ہے اس کے مَرے پیچھے بیشک مردوں کو زندہ کرے گا اور وہ سب کچھ کرسکتا ہے،'),
(3460, 30, 51, 'اور اگر ہم کوئی ہوا بھیجیں جس سے وہ کھیتی کو زرد دیکھیں تو ضرور اس کے بعد ناشکری کرنے لگے'),
(3461, 30, 52, 'اس لیے کہ تم مُردوں کو نہیں سناتے اور نہ بہروں کو پکارنا سناؤ جب وہ پیٹھ دے کر پھیریں'),
(3462, 30, 53, 'اور نہ تم اندھوں کو ان کی گمراہی سے راہ پر لاؤ، تو تم اسی کو سناتے ہو جو ہماری آیتوں پر ایمان لائے تو وہ گردن رکھے ہوئے ہیں،'),
(3463, 30, 54, 'اللہ ہے جس نے تمہیں ابتداء میں کمزور بنایا پھر تمہیں ناتوانی سے طاقت بخشی پھر قوت کے بعد کمزوری اور بڑھاپا دیا، بناتا ہے جو چاہے اور وہی علم و قدرت والا ہے،'),
(3464, 30, 55, 'اور جس دن قیامت قائم ہوگی مجرم قسم کھائیں گے کہ نہ رہے تھے مگر ایک گھڑی وہ ایسے ہی اوندھے جاتے تھے'),
(3465, 30, 56, 'اور بولے وہ جن کو علم اور ایمان مِلا بیشک تم رہے اللہ کے لکھے ہوئے میں اٹھنے کے دن تک تو یہ ہے وہ دن اٹھنے کا لیکن تم نہ جانتے تھے'),
(3466, 30, 57, 'تو اس دن ظالموں کو نفع نہ دے گی ان کی معذرت اور نہ ان سے کوئی راضی کرنا مانگے'),
(3467, 30, 58, 'اور بیشک ہم نے لوگوں کے لیے اس قرآن میں ہر قسم کی مثال بیان فرمائی اور اگر تم ان کے پاس کوئی نشانی لاؤ تو ضرور کافر کہیں گے تم تو نہیں مگر باطل پر،'),
(3468, 30, 59, 'یوں ہی مہر کردیتا ہے اللہ جاہلوں کے دلوں پر'),
(3469, 30, 60, 'تو صبرکرو بیشک اللہ کا وعدہ سچا ہے اور تمہیں سبک نہ کردیں وہ جو یقین نہیں رکھتے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الم'),
(3471, 31, 2, 'یہ حکمت والی کتاب کی آیتیں ہیں،'),
(3472, 31, 3, 'ہدایت اور رحمت ہیں نیکوں کے لیے،'),
(3473, 31, 4, 'وہ جو نماز قائم رکھیں اور زکوٰة دیں اور آخرت پر یقین لائیں،'),
(3474, 31, 5, 'وہی اپنے رب کی ہدایت پر ہیں اور انہیں کا کام بنا،'),
(3475, 31, 6, 'اور کچھ لوگ کھیل کی باتیں خریدتے ہیں کہ اللہ کی راہ سے بہکادیں بے سمجھے اور اسے ہنسی بنالیں، ان کے لیے ذلت کا عذاب ہے،'),
(3476, 31, 7, 'اور جب اس پر ہماری آیتیں پڑھی جائیں تو تکبر کرتا ہوا پھرے جیسے انہیں سنا ہی نہیں جیسے اس کے کانوں میں ٹینٹ (روئی کا پھایا) ہے تو اسے دردناک عذاب کا مژدہ دو،'),
(3477, 31, 8, 'بیشک جو ایمان لائے اور اچھے کام کیے ان کے لیے چین کے باغ ہیں،'),
(3478, 31, 9, 'ہمیشہ ان میں رہیں گے، اللہ کا وعدہ ہے سچا، اور وہی عزت و حکمت والا ہے،'),
(3479, 31, 10, 'اس نے آسمان بنائے بے ایسے ستونوں کے جو تمہیں نظر آئیں اور زمین میں ڈالے لنگر کہ تمہیں لے کر نہ کانپے اور اس میں ہر قسم کے جانور پھیلائے، اور ہم نے آسمان سے پانی اتارا تو زمین میں ہر نفیس جوڑا اگایا'),
(3480, 31, 11, 'یہ تو اللہ کا بنایا ہوا ہے مجھے وہ دکھاؤ جو اس کے سوا اوروں نے بنایا بلکہ ظالم کھلی گمراہی میں ہیں،'),
(3481, 31, 12, 'اور بیشک ہم نے لقمان کو حکمت عطا فرمائی کہ اللہ کا شکر کر اور جو شکر کرے وہ اپنے بھلے کو شکر کرتا ہے اور جو ناشکری کرے تو بیشک اللہ بے پرواہ ہے سب خوبیاں سراہا،'),
(3482, 31, 13, 'اور یاد کرو جب لقمان نے اپنے بیٹے سے کہا اور وہ نصیحت کرتا تھا اے میرے بیٹے اللہ کا کسی کو شریک نہ کرنا، بیشک شرک بڑا ظلم ہے'),
(3483, 31, 14, 'اور ہم نے آدمی کو اس کے ماں باپ کے بارے میں تاکید فرمائی اس کی ماں نے اسے پیٹ میں رکھا کمزوری پر کمزوری جھیلتی ہوئی اور اس کا دودھ چھوٹنا دو برس میں ہے یہ کہ حق مان میرا اور اپنے ماں باپ کا آخر مجھی تک آنا ہے،'),
(3484, 31, 15, 'اور اگر وہ دونوں تجھ سے کوشش کریں کہ میرا شریک ٹھہرائے ایسی چیز کو جس کا تجھے علم نہیں تو ان کا کہنا نہ مان اور دنیا میں اچھی طرح ان کا ساتھ دے اور اس کی راہ چل جو میری طرف رجوع لایا پھر میری ہی طرف تمہیں پھر آنا ہے تو میں بتادوں گا جو تم کرتے تھے'),
(3485, 31, 16, 'اے میرے بیٹے برائی اگر رائی کے دانہ برابر ہو پھر وہ پتھر کی چٹان میں یا آسمان میں یا زمین میں کہیں ہو اللہ اسے لے آئے گا بیشک اللہ ہر باریکی کا جاننے والا خبردار ہے'),
(3486, 31, 17, 'اے میرے بیٹے! نماز برپا رکھ اور اچھی بات کا حکم دے اور بری بات سے منع کر اور جو افتاد تجھ پر پڑے اس پر صبر کر، بیشک یہ ہمت کے کام ہیں'),
(3487, 31, 18, 'اور کسی سے بات کرنے میں اپنا رخسارہ کج نہ کر اور زمین میں اِتراتا نہ چل، بیشک اللہ کو نہیں بھاتا کوئی اِتراتا فخر کرتا،'),
(3488, 31, 19, 'اور میانہ چال چل اور اپنی آواز کچھ پست کر بیشک سب آوازوں میں بری آواز، آواز گدھے کی'),
(3489, 31, 20, 'کیا تم نے نہ دیکھا کہ اللہ نے تمہارے لیے کام میں لگائے جو کچھ آسمانوں اور زمین میں ہیں اور تمہیں بھرپور دیں اپنی نعمتیں ظاہر اور چھپی اور بعضے آدمی اللہ کے بارے میں جھگڑتے ہیں یوں کہ نہ علم نہ عقل نہ کوئی روشن کتاب'),
(3490, 31, 21, 'اور جب ان سے کہا جائے اس کی پیروی کرو جو اللہ نے اتارا تو کہتے ہیں بلکہ ہم تو اس کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کوپایا کیا اگرچہ شیطان ان کو عذاب دوزخ کی طرف بلاتا ہو'),
(3491, 31, 22, 'تو جو اپنا منہ اللہ کی طرف جھکادے اور ہو نیکوکار تو بیشک اس نے مضبوط گرہ تھامی، اور اللہ ہی کی طرف ہے سب کاموں کی انتہا،'),
(3492, 31, 23, 'اور جو کفر کرے تو تم اس کے کفر سے غم نہ کھاؤ، انھیں ہماری ہی طرف پھرنا ہے ہم انہیں بتادیں گے جو کرتے تھے بیشک اللہ دلوں کی بات جانتا ہے،'),
(3493, 31, 24, 'ہم انہیں کچھ برتنے دیں گے پھر انہیں بے بس کرکے سخت عذاب کی طرف لے جائیں گے'),
(3494, 31, 25, 'اور اگر تم ان سے پوچھو کس نے بنائے آسمان اور زمین تو ضرور کہیں گے اللہ نے، تم فرماؤ سب خوبیاں اللہ کو بلکہ ان میں اکثر جانتے نہیں،'),
(3495, 31, 26, 'اللہ ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے بیشک اللہ ہی بے نیاز ہے سب خوبیوں سراہا،'),
(3496, 31, 27, 'اور اگر زمین میں جتنے پیڑ ہیں سب قلمیں ہوجائیں اور سمندر اس کی سیاہی ہو اس کے پیچھے سات سمندر اور تو اللہ کی باتیں ختم نہ ہوں گی بیشک اللہ عزت و حکمت والا ہے،'),
(3497, 31, 28, 'تم سب کا پیدا کرنا اور قیامت میں اٹھانا ایسا ہی ہے جیسا ایک جان کا بیشک اللہ سنتا دیکھتا ہے'),
(3498, 31, 29, 'اے سننے والے کیا تو نے نہ دیکھا کہ اللہ رات لاتا ہے دن کے حصے میں اور دن کرتا ہے رات کے حصے میں اور اس نے سورج اور چاند کام میں لگائے ہر ایک ایک مقرر میعاد تک چلتا ہے اور یہ کہ اللہ تمہارے کاموں سے خبردار ہے،'),
(3499, 31, 30, 'یہ اس لیے کہ اللہ ہی حق ہے اور اس کے سوا جن کو پوجتے ہیں سب باطل ہیں اور اس لیے کہ اللہ ہی بلند بڑائی والا ہے،'),
(3500, 31, 31, 'کیا تو نے نہ دیکھا کہ کشتی دریا میں چلتی ہے، اللہ کے فضل سے تاکہ تمہیں وہ اپنی کچھ نشانیاں دکھائے بیشک اس میں نشانیاں ہیں ہر بڑے صبر کرنے والے شکرگزار کو'),
(3501, 31, 32, 'اور جب ان پر آپڑتی ہے کوئی موج پہاڑوں کی طرح تو اللہ کو پکارتے ہیں نرے اسی پر عقیدہ رکھتے ہوئے پھر جب انہیں خشکی کی طرف بچا لاتا ہے تو ان میں کوئی اعتدال پر رہتا ہے اور ہماری آیتوں کا انکار نہ کرے گا مگر ہر بڑا بے وفا ناشکرا،'),
(3502, 31, 33, 'اے لوگو! اپنے رب سے ڈرو اور اس دن کا خوف کرو جس میں کوئی باپ بچہ کے کام نہ آئے گا، اور نہ کوئی کامی (کاروباری) بچہ اپنے باپ کو کچھ نفع دے بیشک اللہ کا وعدہ سچا ہے تو ہرگز تمہیں دھوکا نہ دے دنیا کی زندگی اور ہرگز تمہیں اللہ کے علم پر دھوکہ نہ دے وہ بڑا فریبی'),
(3503, 31, 34, 'بیشک اللہ کے پاس ہے قیامت کا علم اور اتارتا ہے مینھ اور جانتا ہے جو کچھ ماؤں کے پیٹ میں ہے، اور کوئی جان نہیں جانتی کہ کل کیا کمائے گی اور کوئی جان نہیں جانتی کہ کس زمین میں مرے گی، بیشک اللہ جاننے والا بتانے والا ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الم'),
(3505, 32, 2, 'کتاب کا اتارنا بیشک پروردگار عالم کی طرف سے ہے،'),
(3506, 32, 3, 'کیا کہتے ہیں ان کی بنائی ہوئی ہے بلکہ وہی حق ہے تمہارے رب کی طرف سے کہ تم ڈراؤ ایسے لوگوں کو جن کے پاس تم سے پہلے کوئی ڈر سنانے والا نہ آیا اس امید پر کہ وہ راہ پائیں،'),
(3507, 32, 4, 'اللہ ہے جس نے آسمان اور زمین اور جو کچھ ان کے بیچ میں ہے چھ دن میں بنائے پھر عرش پر استوا فرمایا اس سے چھوٹ کر (لا تعلق ہوکر) تمہارا کوئی حمایتی اور نہ سفارشی تو کیا تم دھیان نہیں کرتے،'),
(3508, 32, 5, 'کام کی تدبیر فرماتا ہے آسمان سے زمین تک پھر اسی کی طرف رجوع کرے گا اس دن کہ جس کی مقدار ہزار برس ہے تمہاری گنتی میں'),
(3509, 32, 6, 'یہ ہے ہر نہاں اور عیاں کا جاننے والا عزت و رحمت والا،'),
(3510, 32, 7, 'وہ جس نے جو چیز بتائی خوب بنائی اور پیدائش انسان کی ابتدا مٹی سے فرمائی'),
(3511, 32, 8, 'پھر اس کی نسل رکھی ایک بے قدر پانی کے خلاصہ سے'),
(3512, 32, 9, 'پھر اسے ٹھیک کیا اور اس میں اپنی طرف کی روح پھونکی اور تمہیں کان اور آنکھیں اور دل عطا فرمائے کیا ہی تھوڑا حق مانتے ہو،'),
(3513, 32, 10, 'اور بولے کیا جب ہم مٹی میں مل جائیں گے کیا پھر نئے بنیں گے، بلکہ وہ اپنے رب کے حضور حاضری سے منکر ہیں'),
(3514, 32, 11, 'تم فرماؤ تمہیں وفات دیتا ہے موت کا فرشتہ جو تم پر مقرر ہے پھر اپنے رب کی طرف واپس جاؤ گے'),
(3515, 32, 12, 'اور کہیں تم دیکھو جب مجرم اپنے رب کے پاس سر نیچے ڈالے ہوں گے اے ہمارے رب اب ہم نے دیکھا اور سنا ہمیں پھر بھیج کہ نیک کام کریں ہم کو یقین آگیا'),
(3516, 32, 13, 'اور اگر ہم چاہتے ہر جان کو اس کی ہدایت فرماتے مگر میری بات قرار پاچکی کہ ضرور جہنم کو بھردوں گا ان جِنوں اور آدمیوں سب سے'),
(3517, 32, 14, 'اب چکھو بدلہ اس کا کہ تم اپنے اس دن کی حاضری بھولے تھے ہم نے تمہیں چھوڑ دیا اب ہمیشہ کا عذاب چکھو اپنے کیے کا بدلہ،'),
(3518, 32, 15, 'ہماری آیتوں پر وہی ایمان لاتے ہیں کہ جب وہ انہیں یاد دلائی جاتی ہیں سجدہ میں گر جاتے ہیں اور اپنے رب کی تعریف کرتے ہوئے اس کی پاکی بولتے ہیں اور تکبر نہیں کرتے، السجدة۔۹'),
(3519, 32, 16, 'ان کی کروٹیں جدا ہوتی ہیں خوابگاہوں ہسے اور اپنے رب کو پکارتے ہیں ڈرتے اور امید کرتے اور ہمارے دیے ہوئے سے کچھ خیرات کرتے ہیں،'),
(3520, 32, 17, 'تو کسی جی کو نہیں معلوم جو آنکھ کی ٹھنڈک ان کے لیے چھپا رکھی ہے صلہ ان کے کاموں کا'),
(3521, 32, 18, 'تو کیا جو ایمان والا ہے وہ اس جیسا ہوجائے گا جو بے حکم ہے یہ برابر نہیں،'),
(3522, 32, 19, 'جو ایمان لائے اور اچھے کام کیے ان کے لیے بسنے کے باغ ہیں، ان کے کاموں کے صلہ میں مہمانداری'),
(3523, 32, 20, 'رہے وہ جو بے حکم ہیں ان کا ٹھکانا آ گ ہے، جب کبھی اس میں سے نکلنا چاہیں گے پھر اسی میں پھیر دیے جائیں گے اور ان سے کہا جائے گا چکھو اس آگ کا عذاب جسے تم جھٹلاتے تھے،'),
(3524, 32, 21, 'اور ضرور ہم انہیں چکھائیں گے کچھ نزدیک کا عذاب اس بڑے عذاب سے پہلے جسے دیکھنے والا امید کرے کہ ابھی باز آئیں گے،'),
(3525, 32, 22, 'اور اس سے بڑھ کر ظالم کون جسے اس کے رب کی آیتوں سے نصیحت کی گئی پھر اس نے ان سے منہ پھیر لیا بیشک ہم مجرموں سے بدلہ لینے والے ہیں،'),
(3526, 32, 23, 'اور بیشک ہم نے موسیٰ کو کتاب عطا فرمائی تو تم اس کے ملنے میں شک نہ کرو اور ہم نے اسے بنی اسرائیل کے لیے ہدایت کیا،'),
(3527, 32, 24, 'اور ہم نے ان میں سے کچھ امام بنائے کہ ہمارے حکم سے بناتے جبکہ انہوں نے صبر کیا اور وہ ہماری آیتوں پر یقین لاتے تھے،'),
(3528, 32, 25, 'بیشک تمہارا رب ان میں فیصلہ کردے گا قیامت کے دن جس بات میں اختلاف کرتے تھے'),
(3529, 32, 26, 'اور کیا انہیں اس پر ہدایت نہ ہوئی کہ ہم نے ان سے پہلے کتنی سنگتیں (قومیں) ہلاک کردیں کہ آج یہ ان کے گھروں میں چل پھر رہے ہیں بیشک اس میں ضرور نشانیاں ہیں تو کیا سنتے نہیں'),
(3530, 32, 27, 'اور کیا نہیں دیکھتے کہ ہم پانی بھیجتے ہیں خشک زمین کی طرف پھر اس سے کھیتی نکالتے ہیں کہ اس میں سے ان کے چوپائے اور وہ خود کھاتے ہیں تو کیا انہیں سوجھتانہیں'),
(3531, 32, 28, 'اور کہتے ہیں یہ فیصلہ کب ہوگا اگر تم سچے ہو'),
(3532, 32, 29, 'تم فرماؤ فیصلہ کے دن کافروں کو ان کا ایمان لانا نفع نہ دے گا اور نہ انہیں مہلت ملے'),
(3533, 32, 30, 'تو ان سے منہ پھیرلو اور انتظار کرو بیشک انہیں بھی انتظار کرنا ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اے غیب کی خبریں بتانے والے (نبی) اللہ کا یوں ہی خوف رکھنا اور کافروں اور منافقوں کی نہ سننا بیشک اللہ علم و حکمت والا ہے،'),
(3535, 33, 2, 'اور اس کی پیروی رکھنا جو تمہارے رب کی طرف سے تمہیں وحی ہوتی ہے، اے لوگو! اللہ تمہارے کام دیکھ رہا ہے،'),
(3536, 33, 3, 'اور اے محبوب! تم اللہ پر بھروسہ رکھو، اور اللہ بس ہے کام بنانے والا،'),
(3537, 33, 4, 'اللہ نے کسی آدمی کے اندر دو دل نہ رکھے اور تمہاری ان عورتوں کو جنہیں تم کے برابر کہہ دو تمہاری ماں نہ بنایا اور نہ تمہارے لے پالکوں کو تمہارا بیٹا بنایا یہ تمہارے اپنے منہ کا کہنا ہے اور اللہ حق فرماتا ہے اور وہی راہ دکھاتا ہے'),
(3538, 33, 5, 'انہیں ان کے باپ ہی کا کہہ کر پکارو یہ اللہ کے نزدیک زیادہ ٹھیک ہے پھر اگر تمہیں ان کے باپ معلوم نہ ہوں تو دین میں تمہارے بھائی ہیں اور بشریت میں تمہارے چچا زاد اور تم پر اس میں کچھ گناہ نہیں جو نا دانستہ تم سے صادر ہوا ہاں وہ گناہ ہے جو دل کے قصد سے کرو اور اللہ بخشنے والا مہربان ہے،'),
(3539, 33, 6, 'یہ نبی مسلمانوں کا ان کی جان سے زیادہ مالک ہے اور اس کی بیبیاں ان کی مائیں ہیں اور رشتہ والے اللہ کی کتاب میں ایک دوسرے سے زیادہ قریب ہیں بہ نسبت اور مسلمانوں اور مہاجروں کے مگر یہ کہ تم اپنے دوستوں پر احسان کرو یہ کتاب میں لکھا ہے'),
(3540, 33, 7, 'اور اے محبوب! یاد کرو جب ہم نے نبیوں سے عہد لیا اور تم سے اور نوح اور ابراہیم اور موسی ٰ اور عیسیٰ بن مریم سے اور ہم نے ان سے گاڑھا عہد لیا،'),
(3541, 33, 8, 'تاکہ سچوں سے ان کے سچ کا سوال کرے اور اس نے کافروں کے لیے دردناک عذاب تیار کر رکھا ہے،'),
(3542, 33, 9, 'اے ایمان والو! اللہ کا احسان اپنے اوپر یاد کرو جب تم پر کچھ لشکر آئے تو ہم نے ان پر آندھی اور وہ لشکر بھیجے جو تمہیں نظر نہ آئے اور اللہ تمہارے کام دیکھتا ہے'),
(3543, 33, 10, 'جب کافر تم پر آئے تمہارے اوپر سے اور تمہارے نیچے سے اور جبکہ ٹھٹک کر رہ گئیں نگاہیں اور دل گلوں کے پاس آگئے اور تم اللہ پر طرح طرح کے گمان کرنے لگے امید و یاس کے'),
(3544, 33, 11, 'وہ جگہ تھی کہ مسلمانوں کی جانچ ہوئی اور خوب سختی سے جھنجھوڑے گئے،'),
(3545, 33, 12, 'اور جب کہنے لگے منافق اور جن کے دلوں میں روگ تھا ہمیں اللہ و رسول نے وعدہ نہ دیا تھا مگر فریب کا'),
(3546, 33, 13, 'اور جب ان میں سے ایک گروہ نے کہا اے مدینہ والو! یہاں تمہارے ٹھہرنے کی جگہ نہیں تم گھروں کو واپس چلو اور ان میں سے ایک گروہ نبی سے اذن مانگتا تھا یہ کہہ کر ہمارے گھر بے حفاظت ہیں، اور وہ بے حفاظت نہ تھے، وہ تو نہ چاہتے تھے مگر بھاگنا،'),
(3547, 33, 14, 'اور اگر ان پر فوجیں مدینہ کے اطراف سے آئیں پھر ان سے کفر چاہتیں تو ضرور ان کا مانگا دے بیٹھتے اور اس میں دیر نہ کرتے مگر تھوڑی'),
(3548, 33, 15, 'اور بیشک اس سے پہلے وہ اللہ سے عہد کرچکے تھے کہ پیٹھ نہ پھیریں گے، اور اللہ کا وعدہ پوچھا جائے گا'),
(3549, 33, 16, 'تم فرماؤ ہرگز تمہیں بھاگنا نفع نہ دے گا اگر موت یا قتل سے بھاگو اور جب بھی دنیا نہ برتنے دیے جاؤ گے مگر تھوڑی'),
(3550, 33, 17, 'تم فرماؤ وہ کون ہے جو اللہ کا حکم تم پر سے ٹال دے اگر وہ تمہارا برا چاہے یا تم پر مہر (رحم) فرمانا چاہے اور وہ اللہ کے سوا کوئی حامی نہ پائیں گے نہ مددگار،'),
(3551, 33, 18, 'بیشک اللہ جانتا ہے تمہارے ان کو جو اوروں کو جہاد سے روکتے ہیں اور اپنے بھائیوں سے کہتے ہیں ہماری طرف چلے آؤ اور لڑائی میں نہیں آتے مگر تھوڑے'),
(3552, 33, 19, 'تمہاری مدد میں گئی کرتے (کمی کرتے) ہیں پھر جب ڈر کا وقت آئے تم انہیں دیکھو گے تمہاری طرف یوں نظر کرتے ہیں کہ ان کی آنکھیں گھوم رہی ہیں جیسے کسی پر موت چھائی ہو پھر جب ڈر کا وقت نکل جائے تمہیں طعنے دینے لگیں تیز زبانوں سے مال غنیمت کے لالچ میں یہ لوگ ایمان لائے ہی نہیں تو اللہ نے ان کے عمل اکارت کردیے اور یہ اللہ کو آسان ہے،'),
(3553, 33, 20, 'وہ سمجھ رہے ہیں کہ کافروں کے لشکر ابھی نہ گئے اور اگر لشکر دوبارہ آئیں تو ان کی خواہش ہوگی کہ کسی طرح گا نؤں میں نکل کر تمہاری خبریں پوچھتے اور اگر وہ تم میں رہتے جب بھی نہ لڑتے مگر تھوڑے'),
(3554, 33, 21, 'بیشک تمہیں رسول اللہ کی پیروی بہتر ہے اس کے لیے کہ اللہ اور پچھلے دن کی امید رکھتا ہو اور اللہ کو بہت یاد کرے'),
(3555, 33, 22, 'اور جب مسلمانوں نے کافروں کے لشکر دیکھے بولے یہ ہے وہ جو ہمیں وعدہ دیا تھا اللہ اور اس کے رسول نے اور سچ فرمایا اللہ اور اس کے رسول نے اور اس سے انہیں نہ بڑھا مگر ایمان اور اللہ کی رضا پر راضی ہونا،'),
(3556, 33, 23, 'مسلمانوں میں کچھ وہ مرد ہیں جنہوں نے سچا کردیا جو عہد اللہ سے کیا تھا تو ان میں کوئی اپنی منت پوری کرچکا اور کوئی راہ دیکھ رہا ہے اور وہ ذرا نہ بدلے'),
(3557, 33, 24, 'تاکہ اللہ سچوں کو ان کے سچ کا صلہ دے اور منافقوں کو عذاب کرے اگر چاہے، یا انہیں توبہ دے، بیشک اللہ بخشنے والا مہربان ہے،'),
(3558, 33, 25, 'اور اللہ نے کافروں کو ان کے دلوں کی جلن کے ساتھ پلٹایا کہ کچھ بھلا نہ پایا اور اللہ نے مسلمانوں کو لڑائی کی کفایت فرمادی اور اللہ زبردست عزت والا ہے،'),
(3559, 33, 26, 'اور جن اہلِ کتاب نے ان کی مدد کی تھی انہیں ان کے قلعوں سے اتارا اور ان کے دلوں میں رُعب ڈالا ان میں ایک گروہ کو تم قتل کرتے ہو اور ایک گروہ کو قید'),
(3560, 33, 27, 'اور ہم نے تمہارے ہاتھ لگائے ان کی زمین اور ان کی زمین اور ان کے مکان اور ان کے مال اور وہ زمین جس پر تم نے ابھی قدم نہیں رکھا ہے اور اللہ ہر چیز پر قادر ہے،'),
(3561, 33, 28, 'اے غیب بتانے والے (نبی)! اپنی بیبیوں سے فرمادے اگر تم دنیا کی زندگی اور اس کی آرائش چاہتی ہو تو آؤ میں تمہیں مال دُوں اور اچھی طرح چھوڑ دُوں'),
(3562, 33, 29, 'اور اگر تم اللہ اور اس کے رسول اور آخرت کا گھر چاہتی ہو تو بیشک اللہ نے تمہاری نیکی والیوں کے لیے بڑا اجر تیار کر رکھا ہے،'),
(3563, 33, 30, 'اے نبی کی بیبیو! جو تم میں صریح حیا کے خلاف کوئی جرأت کرے اس پر اوروں سے دُونا عذاب ہوگا اور یہ اللہ کو آسان ہے،'),
(3564, 33, 31, 'اور جو تم میں فرمانبردار رہے اللہ اور رسول کی اور اچھا کام کرے ہم اسے اوروں سے دُونا ثواب دیں گے اور ہم نے اس کے لیے عزت کی روزی تیار کر رکھی ہے'),
(3565, 33, 32, 'اے نبی کی بیبیو! تم اور عورتوں کی طرح نہیں ہو اگر اللہ سے ڈرو تو بات میں ایسی نرمی نہ کرو کہ دل کا روگی کچھ لالچ کرے ہاں اچھی بات کہو'),
(3566, 33, 33, 'اور اپنے گھروں میں ٹھہری رہو اور بے پردہ نہ رہو جیسے اگلی جاہلیت کی بے پردگی اور نماز قائم رکھو اور زکوٰة دو اور اللہ اور اس کے رسول کا حکم مانو ا لله تو یہی چاہتا ہے، اے نبی کے گھر والو! کہ تم سے ہر ناپاکی دور فرما دے اور تمہیں پاک کرکے خوب ستھرا کردے'),
(3567, 33, 34, 'اور یاد کرو جو تمہارے گھروں میں پڑھی جاتی ہیں اللہ کی آیتیں اور حکمت بیشک اللہ ہر باریکی جانتا خبردار ہے،'),
(3568, 33, 35, 'بیشک مسلمان مرد اور مسلمان عورتیں اور ایمان والے اور ایمان والیاں اور فرمانبردار اور فرمانبرداریں اور سچے اور سچیاں اور صبر والے اور صبر والیاں اور عاجزی کرنے والے اور عاجزی کرنے والیاں اور خیرات کرنے والے اور خیرات کرنے والیاں اور روزے والے اورروزے والیاں اور اپنی پارسائی نگاہ رکھنے والے اور نگاہ رکھنے والیاں اور اللہ کو بہت یاد کرنے والے اور یاد کرنے والیاں ان سب کے لیے اس نے بخشش اور بڑا ثواب تیار کر رکھا ہے،'),
(3569, 33, 36, 'اور نہ کسی مسلمان مرد نہ مسلمان عورت کو پہنچتا ہے کہ جب اللہ و رسول کچھ حکم فرمادیں تو انہیں اپنے معاملہ کا کچھ اختیار رہے اور جو حکم نہ مانے اللہ اور اس کے رسول کا وہ بیشک صر یح گمراہی بہکا،'),
(3570, 33, 37, 'اور اے محبوب! یاد کرو جب تم فرماتے تھے اس سے جسے اللہ نے اسے نعمت دی اور تم نے اسے نعمت دی کہ اپنی بی بی اپنے پاس رہنے دے اور اللہ سے ڈر اور تم اپنے دل میں رکھتے تھے وہ جسے اللہ کو ظاہر کرنا منظور تھا اور تمہیں لوگوں کے طعنہ کا اندیشہ تھا اور اللہ زیادہ سزاوار ہے کہ اس کا خوف رکھو پھر جب زید کی غرض اس سے نکل گئی تو ہم نے وہ تمہارے نکاح میں دے دی کہ مسلمانوں پر کچھ حرج نہ رہے ان کے لے پالکوں (منہ بولے بیٹوں) کی بیبیوں میں جب ان سے ان کا کام ختم ہوجائے اور اللہ کا حکم ہوکر رہنا،'),
(3571, 33, 38, 'نبی پر کوئی حرج نہیں اس بات میں جو اللہ نے اس کے لیے مقرر فرمائی اللہ کا دستور چلا آرہا ہے اس میں جو پہلے گزر چکے اور اللہ کا کام مقرر تقدیر ہے'),
(3572, 33, 39, 'وہ جو اللہ کے پیام پہنچاتے اور اس سے ڈرتے اور اللہ کے سوا کسی کا خوف نہ کرے، اور اللہ بس ہے حساب لینے والا'),
(3573, 33, 40, 'محمد تمہارے مردوں میں کسی کے باپ نہیں ہاں اللہ کے رسول ہیں اور سب نبیوں کے پچھلے اور اللہ سب کچھ جانتا ہے،'),
(3574, 33, 41, 'اے ایمان والو اللہ کو بہت زیادہ یاد کرو،'),
(3575, 33, 42, 'اور صبح و شام اس کی پاکی بولو'),
(3576, 33, 43, 'وہی ہے کہ درود بھیجتا ہے تم پر وہ اور اس کے فرشتے کہ تمہیں اندھیریوں سے اجالے کی طرف نکالے اور وہ مسلمانوں پر مہربان ہے،'),
(3577, 33, 44, 'ان کے لیے ملتے وقت کی دعا سلام ہے اور ان کے لیے عزت کا ثواب تیار کر رکھا ہے،'),
(3578, 33, 45, '(اے غیب کی خبریں بتانے والے (نبی) بیشک ہم نے تمہیں بھیجا حاضر ناظر اور خوشخبری دیتا اور ڈر سناتا'),
(3579, 33, 46, 'اور اللہ کی طرف اس کے حکم سے بلاتا او ر چمکادینے دینے والا ا ٓ فتاب'),
(3580, 33, 47, 'اور ایمان والوں کو خوشخبری دو کہ ان کے لیے اللہ کا بڑا فضل ہے،'),
(3581, 33, 48, 'اور کافروں اور منافقوں کی خوشی نہ کرو اور ان کی ایذا پر درگزر فرماؤ اور اللہ پر بھروسہ رکھو، اور اللہ بس ہے کارساز،'),
(3582, 33, 49, 'اے ایمان والو! جب تم مسلمان عورتوں سے نکاح کرو پھر انہیں بے ہاتھ لگائے چھوڑ دو تو تمہارے لیے کچھ عدت نہیں جسے گنو تو انہیں کچھ فائدہ دو اور اچھی طرح سے چھوڑ دو'),
(3583, 33, 50, 'اے غیب بتانے والے (نبی)! ہم نے تمہارے لیے حلال فرمائیں تمہاری وہ بیبیاں جن کو تم مہر دو اور تمہارے ہاتھ کا مال کنیزیں جو اللہ نے تمہیں غنیمت میں دیں اور تمہارے چچا کی بیٹیاں اور پھپیوں کی بیٹیاں اور ماموں کی بیٹیاں اور خالاؤں کی بیٹیاں جنہوں نے تمہارے ساتھ ہجرت کی اور ایمان والی عورت اگر وہ اپنی جان نبی کی نذر کرے اگر نبی اسے نکاح میں لانا چاہے یہ خاص تمہارے لیے ہہے امت کے لیے نہیں ہمیں معلوم ہے جو ہم نے مسلمانوں پر مقرر کیا ہے ان کی بیبیوں اور ان کے ہاتھ کی مال کنیزوں میں یہ خصوصیت تمہاری اس لیے کہ تم پر کوئی تنگی نہ ہو، اور اللہ بخشنے والا مہربان،'),
(3584, 33, 51, 'پیچھے ہٹاؤ ان میں سے جسے چاہو اور اپنے پاس جگہ دو جسے چاہو اور جسے تم نے کنارے کردیا تھا اسے تمہارا جی چاہے تو اس میں بھی تم پر کچھ گناہ نہیں یہ امر اس سے نزدیک تر ہے کہ ان کی آنکھیں ٹھنڈی ہوں اور غم نہ کریں اور تم انہیں جو کچھ عطا فرماؤ اس پر وہ سب کی سب راضی رہیں اور اللہ جانتا ہے جو تم سب کے دلوں میں ہے، اور اللہ علم و حلم والا ہے،'),
(3585, 33, 52, 'ان کے بعد اور عورتیں تمہیں حلال نہیں اور نہ یہ کہ ان کے عوض اور بیبیاں بدلو اگرچہ تمہیں ان کا حسن بھائے مگر کنیز تمہارے ہاتھ کا مالک اور اللہ ہر چیز پر نگہبان ہے،'),
(3586, 33, 53, 'اے ایمان والو! نبی کے گھروں میں نہ حاضر ہو جب تک اذن نہ پاؤ مثلاً کھانے کے لیے بلائے جاؤ نہ یوں کہ خود اس کے پکنے کی راہ تکو ہاں جب بلائے جاؤ تو حاضر ہو اور جب کھا چکو تو متفرق ہوجاؤ نہ یہ کہ بیٹھے باتوں میں دل بہلاؤ بیشک اس میں نبی کو ایذا ہوتی تھی تو وہ تمہارا لحاظ فرماتے تھے اور اللہ حق فرمانے میں نہیں شرماتا، اور جب تم ان سے برتنے کی کوئی چیز مانگو تو پردے کے باہر مانگو، اس میں زیادہ ستھرائی ہے تمہارے دلوں اور ان کے دلوں کی اور تمہیں نہیں پہنچتا کہ رسول اللہ کو ایذا دو اور نہ یہ کہ ان کے بعد کبھی ان کی بیبیوں سے نکاح کرو بیشک یہ اللہ کے نزدیک بڑی سخت بات ہے'),
(3587, 33, 54, 'اگر تم کوئی بات ظاہر کرو یا چھپاؤ تو بیشک سب کچھ جانتا ہے،'),
(3588, 33, 55, 'ان پر مضائقہ نہیں ان کے باپ اور بیٹوں اور بھائیوں اور بھتیجوں اور بھانجوں اور اپنے دین کی عورتوں اور اپنی کنیزوں میں اور اللہ سے ڈرتی ہو، بیشک اللہ ہر چیز اللہ کے سامنے ہے،'),
(3589, 33, 56, 'بیشک اللہ اور اس کے فرشتے درود بھیجتے ہیں اس غیب بتانے والے (نبی) پر، اے ایمان والو! ان پر درود اور خوب سلام بھیجو'),
(3590, 33, 57, 'بیشک جو ایذا دیتے ہیں اللہ اور اس کے رسول کو ان پر اللہ کی لعنت ہے دنیا اور آخرت میں اور اللہ نے ان کے لیے ذلت کا عذاب تیار کر رکھا ہے'),
(3591, 33, 58, 'اور جو ایمان والے مردوں اور عورتوں کو بے کئے ستاتے ہیں انہوں نے بہتان اور کھلا گناہ اپنے سر لیا'),
(3592, 33, 59, 'اے نبی! اپنی بیبیوں اور صاحبزادیو ں اور مسلمانوں کی عورتوں سے فرمادو کہ اپنی چادروں کا ایک حصہ اپنے منہ پر ڈالے رہیں یہ اس سے نزدیک تر ہے کہ ان کی پہچان ہو تو ستائی نہ جائیں اور اللہ بخشنے والا مہربان ہے،'),
(3593, 33, 60, 'اگر باز نہ آئے منافق اور جن کے دلوں میں روگ ہے اور مدینہ میں جھوٹ اڑانے والے تو ضرور ہم تمہیں ان پر شہ دیں گے پھر وہ مدینہ میں تمہارے پاس نہ رہیں گے مگر تھوڑے دن'),
(3594, 33, 61, 'پھٹکارے ہوئے، جہاں کہیں ملیں پکڑے جائیں اور گن گن کر قتل کیے جائیں،'),
(3595, 33, 62, 'اللہ کا دستور چلا آتا ہے ان لوگوں میں جو پہلے گزر گئے اور تم اللہ کا دستور ہرگز بدلتا نہ پاؤ گے،'),
(3596, 33, 63, 'لوگ تم سے قیامت کا پوچھتے ہیں تم فرماؤ اس کا علم تو اللہ ہی کے پاس ہے، اور تم کیا جانو شاید قیامت پاس ہی ہو'),
(3597, 33, 64, 'بیشک اللہ نے کافروں پر لعنت فرمائی اور ان کے لیے بھڑکتی آگ تیار کر رکھی ہے،'),
(3598, 33, 65, 'اس میں ہمیشہ رہیں گے اس میں نہ کوئی حمایتی پائیں گے نہ مددگار'),
(3599, 33, 66, 'جس دن ان کے منہ الٹ الٹ کر آگ میں تلے جائیں گے کہتے ہوں گے ہائے کسی طرح ہم نے اللہ کا حکم مانا ہوتا اور رسول کا حکم مانا'),
(3600, 33, 67, 'اور کہیں گے اے ہمارے رب! ہم اپنے سرداروں اور اپنے بڑوں کے کہنے پر چلے تو انہوں نے ہمیں راہ سے بہکادیا،'),
(3601, 33, 68, 'اے ہمارے رب! انہیں آگ کا دُونا عذاب دے اور ان پر بڑی لعنت کر،'),
(3602, 33, 69, 'اے ایمان والو! ان جیسے نہ ہونا جنہوں نے موسیٰ کو ستایا تو اللہ نے اسے بَری فرمادیا اس بات سے جو انہوں نے کہی اور موسیٰ اللہ کے یہاں آبرو والا ہے'),
(3603, 33, 70, 'اے ایمان والو! اللہ سے ڈرو اور سیدھی بات کہو'),
(3604, 33, 71, 'تمہارے اعمال تمہارے لیے سنواردے گا اور تمہارے گناہ بخش دے گا، اور جو اللہ اور اس کے رسول کی فرمانبرداری کرے اس نے بڑی کامیابی پائی،'),
(3605, 33, 72, 'بیشک ہم نے امانت پیش فرمائی آسمانوں اور زمین اور پہاڑوں پر تو انہوں نے اس کے اٹھانے سے انکار کیا اور اس سے ڈر گئے اور آدمی نے اٹھالی، بیشک وہ اپنی جان کو مشقت میں ڈالنے والا بڑا نادان ہے،'),
(3606, 33, 73, 'تاکہ اللہ عذاب منافق مردوں اور منافق عورتوں اور مشرک مردوں اور مشرک عورتوں کو اور اللہ توبہ قبول فرمائے مسلمان مردوں اور مسلمان عورتوں کی، اور اللہ بخشنے والا مہربان ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'سب خوبیاں اللہ کو کہ اسی کا مال ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں اور آخرت میں اسی کی تعریف ہے اور وہی ہے حکمت والا خبردار،'),
(3608, 34, 2, 'جانتا ہے جو کچھ زمین میں جاتا ہے اور جو زمین سے نکلتا ہے اور جو آسمان سے اترتا ہے اور جو اس میں چڑھتا ہے اور وہی ہے مہربان بخشنے والا،'),
(3609, 34, 3, 'اور کافر بولے ہم پر قیامت نہ آئے گی تم فرماؤ کیوں نہیں میرے رب کی قسم بیشک ضرور آئے گی غیب جاننے والا اس سے غیب نہیں ذرہ بھر کوئی چیز آسمانوں میں اور نہ زمین میں اور نہ اس سے چھوٹی اور نہ بڑی مگر ایک صاف بتانے والی کتاب میں ہے'),
(3610, 34, 4, 'تاکہ صلہ دے انہیں جو ایمان لائے اور اچھے کام کیے یہ ہیں جن کے لیے بخشش ہے اور عزت کی روزی'),
(3611, 34, 5, 'اور جنہوں نے ہماری آیتوں میں ہرانے کی کوشش کی ان کے لیے سخت عذاب دردناک میں سے عذاب ہے،'),
(3612, 34, 6, 'اور جنہیں علم والا وہ جانتے ہیں کہ جو کچھ تمہاری طرف تمہارے رب کے پاس سے اترا وہی حق ہے اور عزت والے سب خوبیوں سراہے کی راہ بتاتا ہے،'),
(3613, 34, 7, 'اور کافر بولے کیا ہم تمہیں ایسا مرد بتادیں جو تمہیں خبر دے کہ جب تم پرزہ ہوکر بالکل ریزہ ہوکر بالکل ریزہ ریزہ ہوجاؤ تو پھر تمہیں نیا بَننا ہے،'),
(3614, 34, 8, 'کیا اللہ پر اس نے جھوٹ باندھا یا اسے سودا ہے بلکہ وہ جو آخرت پر ایمان نہیں لاتے عذاب اور دور کی گمراہی میں ہیں،'),
(3615, 34, 9, 'تو کیا انہوں نے نہ دیکھا جو ان کے آگے اور پیچھے ہے آسمان اور زمین ہم چاہیں تو انہیں زمین میں دھنسادیں یا ان پر آسمان کا ٹکڑا گرادیں، بیشک اس میں نشانی ہے ہر رجوع لانے والے بندے کے لیے'),
(3616, 34, 10, 'اور بیشک ہم نے داؤد کو اپنا بڑا فضل دیا اے پہاڑو! اس کے ساتھ اللہ کی رجوع کرو اور اے پرندو! اور ہم نے اس کے لیے لوہا نرم کیا'),
(3617, 34, 11, 'کہ وسیع زِر ہیں بنا اور بنانے میں اندازے کا لحاظ رکھ اور تم سب نیکی کرو، بیشک تمہارے کام دیکھ رہا ہوں،'),
(3618, 34, 12, 'اور سلیمان کے بس میں ہوا کردی اس کی صبح کی منزل ایک مہینہ کی راہ اور شام کی منزل ایک مہینے کی راہ اور ہم نے اس کے لیے پگھلے ہوئے تانبے کا چشمہ بہایا اور جنوں میں سے وہ جو اس کے آگے کا م کرتے اس کے رب کے حکم سے ادر جو ان میں ہما رے حکم سے پھرے ہم اسے بھڑ کتی آ گ کا عذاب چکھائیں گے،'),
(3619, 34, 13, 'اس کے لیے بناتے جو وہ چاہتا اونچے اونچے محل اور تصویریں اور بڑے حوضوں کے برابر لگن اور لنگردار دیگیں اے داؤد والو! شکر کرو اور میرے بندوں میں کم ہیں شکر والے،'),
(3620, 34, 14, 'پھر جب ہم نے اس پر موت کا حکم بھیجا جنوں کو اس کی موت نہ بتائی مگر زمین کی دیمک نے کہ اس کا عصا کھاتی تھی، پھر جب سلیمان زمین پر آیا جِنوں کی حقیقت کھل گئی اگر غیب جانتے ہوتے تو اس خواری کے عذاب میں نہ ہوتے'),
(3621, 34, 15, 'بیشک سبا کے لیے ان کی آبادی میں نشانی تھی دو باغ دہنے اور بائیں اپنے رب کا رزق کھاؤ اور اس کا شکر ادا کرو پاکیزہ شہر اور بخشنے والا رب'),
(3622, 34, 16, 'تو انہوں نے منہ پھیرا تو ہم نے ان پر زور کا اہلا (سیلاب) بھیجا اور ان کے باغوں کے عوض دو باغ انہیں بدل دیے جن میں بکٹا (بدمزہ) میوہ اور جھاؤ اور کچھ تھوڑی سی بیریاں'),
(3623, 34, 17, 'ہم نے انہیں یہ بدلہ دیا ان کی ناشکری کی سزا، اور ہم کسے سزا دیتے ہیں اسی کو جو نا شکرا ہے،'),
(3624, 34, 18, 'اور ہم نے کیے تھے ان میں اور ان شہروں میں ہم نے برکت رکھی سر راہ کتنے شہر اور انہیں منزل کے اندازے پر رکھا ان میں چلو راتوں اور دنوں امن و امان سے'),
(3625, 34, 19, 'تو بولے اے ہمارے رب! ہمیں سفر میں دوری ڈال اور انہوں نے خود اپنا ہی نقصان کیا تو ہم نے انہیں کہانیاں کردیا اور انہیں پوری پریشانی سے پراگندہ کردیا بیشک اس میں ضروری نشانیاں ہیں ہر بڑے صبر والے ہر بڑے شکر والے کے لیے'),
(3626, 34, 20, 'اور بیشک ابلیس نے انہیں اپنا گمان سچ کر دکھایا تو وہ اس کے پیچھے ہولیے مگر ایک گروہ کہ مسلمان تھا'),
(3627, 34, 21, 'اور شیطان کا ان پر کچھ قابو نہ تھا مگر اس لیے کہ ہم دکھادیں کہ کون آخرت پر ایمان لاتا اور کون اس سے شک میں ہے، اور تمہارا رب ہر چیز پر نگہبان ہے،'),
(3628, 34, 22, 'تم فرماؤ پکارو انہیں جنہیں اللہ کے سوا سمجھے بیٹھے ہو وہ ذرہ بھر کے مالک نہیں آسمانوں میں اور نہ زمین میں اور نہ ان کا ان دونوں میں کچھ حصہ اور نہ اللہ کا ان میں سے کوئی مددگار،'),
(3629, 34, 23, 'اور اس کے پاس شفاعت کام نہیں دیتی مگر جس کے لیے وہ اذن فرمائے، یہاں تک کہ جب اذن دے کر ان کے دلوں کی گھبراہٹ دور فرمادی جاتی ہے، ایک دوسرے سے کہتے ہیں تمہارے ربنے کیا ہی بات فرمائی، وہ کہتے ہیں جو فرمایا حق فرمایا اور وہی ہے بلند بڑائی والا،'),
(3630, 34, 24, 'تم فرماؤ کون جو تمہیں روزی دیتا ہے آسمانوں اور زمین سے تم خود ہی فرماؤ اللہ اور بیشک ہم یا تم یا تو ضرور ہدایت پر ہیں یا کھلی گمراہی میں'),
(3631, 34, 25, 'تم فرماؤ ہم نے تمہارے گمان میں اگر کوئی جرم کیا تو اس کی تم سے پوچھ نہیں، نہ تمہارے کوتکوں کا ہم سے سوال'),
(3632, 34, 26, 'تم فرماؤ ہمارا رب ہم سب کو جمع کرے گا پھر ہم میں سچا فیصلہ فرمادے گا اور وہی ہے بڑا نیاؤ چکانے (درست فیصلہ کرنے) والا سب کچھ جانتا،'),
(3633, 34, 27, 'تم فرماؤ مجھے دکھاؤ تو وہ شریک جو تم نے اس سے ملائے ہیں ہشت، بلکہ وہی ہے اللہ عزت والا حکمت والا،'),
(3634, 34, 28, 'اور اے محبوب! ہم نے تم کو نہ بھیجا مگر ایسی رسالت سے جو تمام آدمیوں کو گھیرنے والی ہے خوشخبری دیتا اور ڈر سناتا لیکن بہت لوگ نہیں جانتے'),
(3635, 34, 29, 'اور کہتے ہیں یہ وعدہ کب آئے گا اگر تم سچے ہو،'),
(3636, 34, 30, 'تم فرماؤ تمہارے لیے ایک ایسے دن کا وعدہ جس سے تم نہ ایک گھڑی پیچھے ہٹ سکو اور نہ آگے بڑھ سکو'),
(3637, 34, 31, 'اور کافر بولے ہم ہرگز نہ ایمان لائیں گے اس قرآن پر اور نہ ان کتابوں پر جو اس سے آگے تھیں اور کسی طرح تو دیکھے جب ظالم اپنے رب کے پاس کھڑے کیے جائیں گے، ان میں ایک دوسرے پر بات ڈالے گا وہ جو دبے تھے ان سے کہیں گے جو اونچے کھینچتے (بڑے بنے ہوئے) تھے اگر تم نہ ہوتے تو ہم ضرور ایمان لے آتے،'),
(3638, 34, 32, 'وہ جو اونچے کھینچتے تھے ان سے کہیں گے جو دبے ہوئے تھے کیا ہم نے تمہیں روک دیا ہدایت سے بعد اس کے کہ تمہارے پاس آئی بلکہ تم خود مجرم تھے،'),
(3639, 34, 33, 'اور کہیں گے وہ جو دبے ہوئے تھے ان سے جو اونچے کھینچتے تھے بلکہ رات دن کا داؤں (فریب) تھا جبکہ تم ہمیں حکم دیتے تھے کہ اللہ کا انکار کریں اور اس کے برابر والے ٹھہرائیں، اور دل ہی دل میں پچھتانے لگے جب عذاب دیکھا اور ہم نے طوق ڈالے ان کی گردنوں میں جو منکر تھے وہ کیا بدلہ پائیں گے مگر وہی جو کچھ کرتے تھے'),
(3640, 34, 34, 'اور ہم نے جب کبھی کسی شہر میں کوئی ڈر سنانے والا بھیجا وہاں کے آسودوں (امیروں) نے یہی کہا کہ تم جو لے کر بھیجے گئے ہم اس کے منکر ہیں'),
(3641, 34, 35, 'اور بولے ہم مال اور اولاد میں بڑھ کر ہیں اور ہم پر عذاب ہونا نہیں'),
(3642, 34, 36, 'تم فرماؤ بیشک میرا رب رزق وسیع کرتا ہے جس کے لیے چاہے اور تنگی فرماتا ہے لیکن بہت لوگ نہیں جانتے،'),
(3643, 34, 37, 'اور تمہارے مال اور تمہاری اولاد اس قابل نہیں کہ تمہیں ہمارے قریب تک پہنچائیں مگر وہ جو ایمان لائے اور نیکی کی ان کے لیے دُونا دُوں (کئی گنا) صلہ ان کے عمل کا بدلہ اور وہ بالاخانوں میں امن و امان سے ہیں'),
(3644, 34, 38, 'اور ہو جو ہماری آیتوں میں ہرانے کی کوشش کرتے ہیں وہ عذاب میں لادھرے جائیں گے'),
(3645, 34, 39, 'تم فرماؤ بیشک میرا رب رزق وسیع فرماتا ہے اپنے بندوں میں جس کے لیے چاہے اور تنگی فرماتا ہے جس کے لیے چاہے اور جو چیز تم اللہ کی راہ میں خرچ کرو وہ اس کے بدلے اور دے گا اور وہ سب سے بہتر رزق دینے والا'),
(3646, 34, 40, 'اور جس دن ان سب کو اٹھائے گا پھر فرشتوں سے فرمائے گا کیا یہ تمہیں پوجتے تھے'),
(3647, 34, 41, 'وہ عرض کریں گے پاکی ہے تجھ کو تو ہمارا دوست ہے نہ وہ بلکہ وہ جِنوں کو پوجتے تھے ان میں اکثر انہیں پر یقین لائے تھے'),
(3648, 34, 42, 'تو آج تم میں ایک دوسرے کو بھلے برے کا کچھ اختیار نہ رکھے گا او رہم فرمائیں گے ظالموں سے اس آگ کا عذاب چکھو جسے تم جھٹلاتے تھے'),
(3649, 34, 43, 'اور جب ان پر ہماری روشن آیتیں پڑھی جائیں تو کہتے ہیں یہ تو نہیں مگر ایک مرد کہ تمہیں روکنا چاہتے ہیں تمہارے باپ دادا کے معبودو ں سے اور کہتے ہیں یہ تو نہیں مگر بہتان جوڑا ہوا، اور کافروں نے حق کو کہا جب ان کے پاس آیا یہ تو نہیں مگر کھلا جادو،'),
(3650, 34, 44, 'اور ہم نے انہیں کچھ کتابیں نہ دیں جنہیں پڑھتے ہوں نہ تم سے پہلے ان کے پاس کوئی ڈر سنانے والا آیا'),
(3651, 34, 45, 'اور ان سے اگلوں نے جھٹلایا اور یہ اس کے دسویں کو بھی نہ پہنچے جو ہم نے انہیں دیا تھا پھر انہوں نے میرے رسولوں کو جھٹلایا تو کیسا ہوا میرا انکا کرنا'),
(3652, 34, 46, 'تم فرماؤ میں تمہیں ایک نصیحت کرتا ہوں کہ اللہ کے لیے کھڑے رہو دو دو اور اکیلے اکیلے پھر سوچو کہ تمہارے ان صاحب میں جنون کی کوئی بات نہیں، وہ تو نہیں مگر نہیں مگر تمہیں ڈر سنانے والے ایک سخت عذاب کے آگے'),
(3653, 34, 47, 'تم فرماؤ میں نے تم سے اس پر کچھ اجر مانگا ہو تو وہ تمہیں کو میرا اجر تو اللہ ہی پر ہے اور وہ ہر چیز پر گواہ ہے،'),
(3654, 34, 48, 'تم فرماؤ بیشک میرا رب حق پر القا فرماتا ہے بہت جاننے والا سب نبیوں کا،'),
(3655, 34, 49, 'تم فرماؤ حق آیا اور باطل نہ پہل کرے اور نہ پھر کر آئے'),
(3656, 34, 50, 'تم فرماؤ اگر میں بہکا تو اپنے ہی برے کو بہکا اور اگر میں نے راہ پائی تو اس کے سبب جو میرا رب میری طرف وحی فرماتا ہے بیشک وہ سننے والا نزدیک ہے'),
(3657, 34, 51, 'اور کسی طرح تو دیکھے جب وہ گھبراہٹ میں ڈالے جائیں گے پھر بچ کر نہ نکل سکیں گے اور ایک قریب جگہ سے پکڑ لیے جائیں گے'),
(3658, 34, 52, 'اور کہیں گے ہم اس پر ایمان لائے اور اب وہ اسے کیونکر پائیں اتنی دور جگہ سے'),
(3659, 34, 53, 'کہ پہلے تو اس سے کفر کرچکے تھے، اور بے دیکھے پھینک مارتے ہیں دور مکان سے'),
(3660, 34, 54, 'اور روک کردی گئی ان میں اور اس میں جسے چاہتے ہیں جیسے ان کے پہلے گروہوں سے کیا گیا تھا بیشک وہ دھوکا ڈالنے والے شک میں تھے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سب خوبیاں اللہ کو جو آسمانوں اور زمین کا بنانے والا فرشتوں کو رسول کرنے والا جن کے دو دو تین تین چار چار پر ہیں، بڑھاتا ہے آفرینش (پیدائش) میں جو چاہے بیشک اللہ ہر چیز پر قادر ہے،'),
(3662, 35, 2, 'اللہ جو رحمت لوگوں کے لیے کھولے اس کا کوئی روکنے والا نہیں، اور جو کچھ روک لے تو اس کی روک کے بعد اس کا کوئی چھوڑنے والا نہیں، اور وہی عزت و حکمت والا ہے،'),
(3663, 35, 3, 'اے لوگو! اپنے اوپر اللہ کا احسان یاد کرو کیا اللہ کے سوا اور بھی کوئی خالق ہے کہ آسمان اور زمین سے تمہیں روزی دے، اس کے سوا کوئی معبود نہیں، تو تم کہاں اوندھے جاتے ہو'),
(3664, 35, 4, 'اور اگر یہ تمہیں جھٹلائیں تو بیشک تم سے پہلے کتنے ہی رسول جھٹلائے گئے اور سب کام اللہ ہی کی طرف سے پھرتے ہیں'),
(3665, 35, 5, 'اے لوگو! بیشک اللہ کا وعدہ سچ ہے تو ہرگز تمہیں دھوکا نہ دے دنیا کی زندگی، اور ہرگز تمہیں اللہ کے حکم پر فریب نہ دے وہ بڑا فریبی'),
(3666, 35, 6, 'بیشک شیطان تمہارا دشمن ہے تو تم بھی اسے دشمن سمجھو وہ تو اپنے گروہ کو اسی لیے بلاتا ہے کہ دوزخیوں میں ہوں'),
(3667, 35, 7, 'کافروں کے لیے سخت عذاب ہے، اور جو ایمان لائے اور اچھے کام کیے ان کے لیے بخشش اور بڑا ثواب ہے،'),
(3668, 35, 8, 'تو کیا وہ جس کی نگاہ میں اس کا برا کام آراستہ کیا گیا کہ اس نے اسے بھلا سمجھا ہدایت والے کی طرح ہوجائے گا اس لیے اللہ گمراہ کرتا ہے جسے چاہے اور راہ دیتا ہے جسے چاہے، تو تمہاری جان ان پر حسرتوں میں نہ جائے اللہ خوب جانتا ہے جو کچھ وہ کرتے ہیں،'),
(3669, 35, 9, 'اور اللہ ہے جس نے بھیجیں ہوائیں کہ بادل ابھارتی ہیں، پھر ہم اسے کسی مردہ شہر کی طرف رواں کرتے ہیں تو اس کے سبب ہم زمین کو زندہ فرماتے ہیں اس کے مرے پیچھے یونہی حشر میں اٹھنا ہے'),
(3670, 35, 10, 'جسے عزت کی چاہ ہو تو عزت تو سب اللہ کے ہاتھ ہے اسی کی طرف چڑھتا ہے پاکیزہ کلام اور جو نیک کام سے وہ اسے بلند کرتا ہے اور وہ جو برے داؤں (فریب) کرتے ہیں ان کے لیے سخت عذاب ہے اور انہیں کا مکر برباد ہوگا (ف۲۸'),
(3671, 35, 11, 'اور اللہ نے تمہیں بنایا مٹی سے پھر پانی کی بوند سے پھر تمہیں کیا جوڑے جوڑے اور کسی مادہ کو پیٹ نہیں رہتا اور نہ وہ جتنی ہے مگر اس کے علم سے، اور جس بڑی عمر والے کو عمر دی جائے یا جس کسی کی عمر کم رکھی جائے یہ سب ایک کتاب میں ہے بیشک یہ اللہ کو آسان ہے'),
(3672, 35, 12, 'اور دونوں سمندر ایک سے نہیں یہ میٹھا ہے خوب میٹھا پانی خوشگوار اور یہ کھاری ہے تلخ اور ہر ایک میں سے تم کھاتے ہو تازہ گوشت اور نکالتے ہو پہننے کا ایک گہنا (زیور) اور تو کشتیوں کو اس میں دیکھے کہ پانی چیرتی ہیں تاکہ تم اس کا فضل تلاش کرو اور کسی طرح حق مانو'),
(3673, 35, 13, 'رات لاتا ہے دن کے حصہ میں اور دن لاتا ہے رات کے حصہ میں اور اس نے کام میں لگائے سورج اور چاند ہر ایک ایک مقرر میعاد تک چلتا ہے یہ ہے اللہ تمہارا رب اسی کی بادشاہی ہے، اور اس کے سوا جنہیں تم پوجتے ہو دانہ خُرما کے چھلکے تک کے مالک نہیں،'),
(3674, 35, 14, 'تم انہیں پکارو تو وہ تمہاری پکار نہ سنیں، اور بالفرض سن بھی لیں تو تمہاری حاجت روانہ کرسکیں اور قیامت کے دن وہ تمہارے شرک سے منکر ہوں گے اور تجھے کوئی نہ بتائے گا اس بتانے والے کی طرح'),
(3675, 35, 15, 'اے لوگو! تم سب اللہ کے محتاج اور اللہ ہی بے نیاز ہے سب خوبیوں سراہا،'),
(3676, 35, 16, 'وہ چاہے تو تمہیں لے جائے اور نئی مخلوق لے آئے'),
(3677, 35, 17, 'اور یہ اللہ پر کچھ دشوار نہیں،'),
(3678, 35, 18, 'اور کوئی بوجھ اٹھانے والی جان دوسرے کا بوجھ نہ اٹھائے گی اور اگر کوئی بوجھ والی اپنا بوجھ بٹانے کو کسی کو بلائے تو اس کے بوجھ میں سے کوئی کچھ نہ اٹھائے گا اگرچہ قریب رشتہ دار ہو اے محبوب! تمہارا ڈر سناتا انہیں کو کام دیتا ہے جو بے دیکھے اپنے رب! سے ڈرتے ہیں اور نماز قائم رکھتے ہیں، اور جو ستھرا ہوا تو اپنے ہی بھلے کو ستھرا ہوا اور اللہ ہی کی طرف پھرنا ہے،'),
(3679, 35, 19, 'اور برابر نہیں اندھا اور انکھیارا'),
(3680, 35, 20, 'اور نہ اندھیریاں اور اجالا'),
(3681, 35, 21, 'اور نہ سایہ اور نہ تیز دھوپ'),
(3682, 35, 22, 'اور برابر نہیں زندے اور مردے بیشک اللہ سنا تا ہے جسے چاہے اور تم نہیں سنانے والے انہیں جو قبروں میں پڑے ہیں'),
(3683, 35, 23, 'تم تو یہی ڈر سنانے والے ہو'),
(3684, 35, 24, 'اے محبوب! بیشک ہم نے تمہیں حق کے ساتھ بھیجا خوشخبری دیتا اور ڈر سنا تا اور جو کوئی گروہ تھا سب میں ایک ڈر سنانے والا گزر چکا'),
(3685, 35, 25, 'اور اگر یہ تمہیں جھٹلائیں تو ان سے اگلے بھی جھٹلاچکے ہیں ان کے پاس ان کے رسول آئے روشن دلیلیں اور صحیفے اور چمکتی کتاب لے کر،'),
(3686, 35, 26, 'پھر میں نے کافروں کو پکڑا تو کیسا ہوا میرا انکار'),
(3687, 35, 27, 'کیا تو نے نہ دیکھا کہ اللہ نے آسمان سے پانی اتارا تو ہم نے اس سے پھل نکالے رنگ برنگ اور پہاڑوں میں راستے ہیں سفید اور سرخ رنگ رنگ کے او رکچھ کالے بھوچنگ (سیاہ کالے)'),
(3688, 35, 28, 'اور آدمیوں اور جانوروں اور چوپایوں کے رنگ یونہی طرح طرح کے ہیں اللہ سے اس کے بندوں میں وہی ڈرتے ہیں جو علم والے ہیں بیشک اللہ بخشنے والا عزت والا ہے،'),
(3689, 35, 29, 'بیشک وہ جو اللہ کی کتاب پڑھتے ہیں اور نماز قائم رکھتے اور ہمارے دیے سے کچھ ہماری راہ میں خرچ کرتے ہیں پوشیدہ اور ظاہر وہ ایسی تجارت کے امیدوار ہیں جس میں ہرگز ٹوُٹا (نقصان) نہیں،'),
(3690, 35, 30, 'تاکہ ان کے ثواب انہیں بھرپور دے اور اپنے فضل سے اور زیادہ عطا کرے، بیشک وہ بخشنے والا قدر فرمانے والا ہے،'),
(3691, 35, 31, 'اور ہو کتاب جو ہم نے تمہاری طرف وحی بھیجی وہی حق ہے اپنے سے اگلی کتابوں کی تصدیق فرماتی ہوئی، بیشک اللہ اپنے بندوں سے خبردار دیکھنے والا ہے'),
(3692, 35, 32, 'پھر ہم نے کتاب کا وارث کیا اپنے چُنے ہوئے بندوں کو تو ان میں کوئی اپنی جان پر ظلم کرتا ہے اور ان میں کوئی میانہ چال پر ہے، اور ان میں کوئی وہ ہے جو اللہ کے حکم سے بھلائیوں میں سبقت لے گیا یہی بڑا فضل ہے،'),
(3693, 35, 33, 'بسنے کے باغوں میں داخل ہوں گے وہ ان میں سونے کے کنگن اور موتی پہنائے جائیں گے اور وہاں ان کی پوشاک ریشمی ہے،'),
(3694, 35, 34, 'اور کہیں گے سب خوبیاں اللہ کو جس نے ہمارا غم دور کیا بیشک ہمارا رب بخشنے والا قدر فرمانے والا ہے'),
(3695, 35, 35, 'وہ جس نے ہمیں آرام کی جگہ اتارا اپنے فضل سے، ہمیں اس میں نہ کوئی تکلیف پہنچے نہ ہمیں اس میں کوئی تکان لاحق ہو،'),
(3696, 35, 36, 'اور جنہوں نے کفر کیا ان کے لیے جہنم کی آگ ہے نہ ان کی قضا آئے کہ مرجائیں اور نہ ان پر اس کا عذاب کچھ ہلکا کیاجائے، ہم ایسی ہی سزا دیتے ہیں ہر بڑے ناشکرے کو،'),
(3697, 35, 37, 'اور وہ اس میں چلاتے ہوں گے اے ہمارے رب! ہمیں نکال کہ ہم اچھا کام کریں اس کے خلاف جو پہلے کرتے تھے اور کیا ہم نے تمہیں وہ عمر نہ دی تھی جس میں سمجھ لیتا جسے سمجھنا ہوتا اور ڈر سنانے والا تمہارے پاس تشریف لایا تھا تو اب چکھو کہ ظالموں کا کوئی مددگار نہیں،'),
(3698, 35, 38, 'بیشک اللہ جاننے والا ہے آسمانوں اور زمین کی ہر چھپی بات کا، بیشک وہ دلوں کی بات جانتا ہے،'),
(3699, 35, 39, 'وہی ہے جس نے تمہیں زمین میں اگلوں کا جانشین کیا تو جو کفر کرے اس کا کفر اسی پر پڑے اور کافروں کو ان کا کفر ان کے رب کے یہاں نہیں بڑھائے گا مگر بیزاری اور کافروں کو ان کا کفر نہ بڑھائے گا مگر نقصان'),
(3700, 35, 40, 'تم فرماؤ بھلا بتلاؤ تو اپنے وہ شریک جنہیں اللہ کے سوا پوجتے ہو مجھے دکھاؤ انہوں نے زمین میں سے کونسا حصہ بنایا یا آسمانوں میں کچھ ان کا ساجھا ہے یا ہم نے انہیں کوئی کتاب دی ہے کہ وہ اس کی روشن دلیلوں پر ہیں بلکہ ظالم آپس میں ایک دوسرے کو وعدہ نہیں دیتے مگر فریب کا'),
(3701, 35, 41, 'بیشک اللہ روکے ہوئے ہے آسمانوں اور زمین کو کہ جنبش نہ کریں اور اگر وہ ہٹ جائیں تو انہیں کون روکے اللہ کے سوا، بیشک وہ علم بخشنے والا ہے،'),
(3702, 35, 42, 'اور انہوں نے اللہ کی قسم کھائی اپنی قسموں میں حد کی کوشش سے کہ اگر ان کے پاس کوئی ڈر سنانے والا آیا تو وہ ضرور کسی نہ کسی گروہ سے زیادہ راہ پر ہوں گے پھر جب ان کے پاس ڈر سنانے والا تشریف لایا تو اس نے انہیں نہ بڑھا مگر نفرت کرنا'),
(3703, 35, 43, 'اپنی جان کو زمین میں اونچا کھینچنا اور برا داؤں اور برا داؤں (فریب) اپنے چلنے والے ہی پر پڑتا ہے تو کا ہے کے انتظار میں ہیں مگر اسی کے جو اگلوں کا دستور ہوا تو تم ہرگز اللہ کے دستور کو بدلتا نہ پاؤ گے اور ہرگز اللہ کے قانون کو ٹلتا نہ پاؤ گے،'),
(3704, 35, 44, 'اور کیا انہوں نے زمین میں سفر نہ کیا کہ دیکھتے ان سے اگلوں کا کیسا انجام ہوا اور وہ ان سے زور میں سخت تھے اور اللہ وہ نہیں جس کے قابو سے نکل سکے کوئی شئے آسمانوں اور نہ زمین میں، بیشک وہ علم و قدرت والا ہے،'),
(3705, 35, 45, 'اور اگر اللہ لوگوں کو ان کے کیے پر پکڑتا تو زمین کی پیٹھ پر کوئی چلنے والا نہ چھوڑتا لیکن ایک مقرر میعاد تک انہیں ڈھیل دیتا ہے پھر جب ان کا وعدہ آئے گا تو بیشک اللہ کے سب بندے اس کی نگاہ میں ہیں');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یسٰں'),
(3707, 36, 2, 'حکمت والے قرآن کی قسم،'),
(3708, 36, 3, 'بیشک تم'),
(3709, 36, 4, 'سیدھی راہ پر بھیجے گئے ہو'),
(3710, 36, 5, 'عزت والے مہربان کا اتارا ہوا،'),
(3711, 36, 6, 'تاکہ تم اس قوم کو ڈر سناؤ جس کے باپ دادا نہ ڈرائے گئے تو وہ بے خبر ہیں،'),
(3712, 36, 7, 'بیشک ان میں اکثر پر بات ثابت ہوچکی ہے تو وہ ایمان نہ لائیں گے'),
(3713, 36, 8, 'ہم نے ان کی گردنو ں میں طوق کردیے ہیں کہ وہ ٹھوڑیوں تک ہیں تو یہ اوپر کو منہ اٹھائے رہ گئے'),
(3714, 36, 9, 'اور ہم نے ان کے آگے دیوار بنادی اور ان کے پیچھے ایک دیوار اور انہیں اوپر سے ڈھانک دیا تو انہیں کچھ نہیں سوجھتا'),
(3715, 36, 10, 'اور انہیں ایک سا ہے تم انہیں ڈراؤٕ یا نہ ڈراؤ وہ ایمان لانے کے نہیں،'),
(3716, 36, 11, 'تم تو اسی کو ڈر سناتے ہو جو نصیحت پر چلے اور رحمن سے بے دیکھے ڈرے، تو اسے بخشش اور عزت کے ثواب کی بشارت دو'),
(3717, 36, 12, 'بیشک ہم مُردوں کو جِلائیں گے اور ہم لکھ رہے ہیں جو انہوں نے آگے بھیجا اور جو نشانیاں پیچھے چھوڑ گئے اور ہر چیز ہم نے گن رکھی ہے ایک بتانے والی کتاب میں'),
(3718, 36, 13, 'اور ان سے نشانیاں بیان کرو اس شہر والوں کی جب ان کے پاس فرستادے (رسول) آئے،'),
(3719, 36, 14, 'جب ہم نے ان کی طرف دو بھیجے پھر انہوں نے ان کو جھٹلایا تو ہم نے تیسرے سے زور دیا اب ان سب نے کہا کہ بیشک ہم تمہاری طرف بھیجے گئے ہیں،'),
(3720, 36, 15, 'بولے تم تو نہیں مگر ہم جیسے آدمی اور رحمن نے کچھ نہیں اتارا تم نرے جھوٹے ہو،'),
(3721, 36, 16, 'وہ بولے ہمارا رب جانتا ہے کہ بیشک ضرور ہم تمہاری طرف بھیجے گئے ہیں،'),
(3722, 36, 17, 'اور ہمارے ذمہ نہیں مگر صاف پہنچا دینا'),
(3723, 36, 18, 'بولے ہم تمہیں منحوس سمجھتے ہیں بیشک اگر تم باز نہ آئے تو ضرور ہم تمہیں سنگسار کریں گے بیشک ہمارے ہاتھوں تم پر دکھ کی مار پڑے گی،'),
(3724, 36, 19, 'انہوں نے فرمایا تمہاری نحوست تو تمہارے ساتھ ہے کیا اس پر بدکتے ہو کہ تم سمجھائے گئے بلکہ تم حد سے بڑھنے والے لوگ ہو'),
(3725, 36, 20, 'اور شہر کے پرلے کنارے سے ایک مرد دوڑتا آیا بولا، اے میری قوم! بھیجے ہوؤں کی پیروی کرو،'),
(3726, 36, 21, 'ایسوں کی پیروی کرو جو تم سے کچھ نیگ (اجر) نہیں مانگتے اور وہ راہ پر ہیں،'),
(3727, 36, 22, 'اور مجھے کیا ہے کہ اس کی بندگی نہ کروں جس نے مجھے پیدا کیا اور اسی کی طرف تمہیں پلٹنا ہے،'),
(3728, 36, 23, 'کیا اللہ کے سوا اور خدا ٹھہراؤں کہ اگر رحمٰن میرا کچھ برا چاہے تو ان کی سفارش میرے کچھ کام نہ آئے اور نہ وہ مجھے بچاسکیں،'),
(3729, 36, 24, 'بیشک جب تو میں کھلی گمراہی میں ہو'),
(3730, 36, 25, 'مقرر میں تمہارے رب پر ایمان لایا تو میری سنو'),
(3731, 36, 26, 'اس سے فرمایا گیا کہ جنت میں داخل ہو کہا کسی طرح میری قوم جانتی،'),
(3732, 36, 27, 'جیسی میرے رب نے میری مغفرت کی اور مجھے عزت والوں میں کیا'),
(3733, 36, 28, 'اور ہم نے اس کے بعد اس کی قوم پر آسمان سے کوئی لشکر نہ اتارا اور نہ ہمیں وہاں کوئی لشکر اتارنا تھا،'),
(3734, 36, 29, 'وہ تو بس ایک ہی چیخ تھی جبھی وہ بجھ کر رہ گئے،'),
(3735, 36, 30, 'اور کہا گیا کہ ہائے افسوس ان بندوں پر جب ان کے پاس کوئی رسول آتا ہے تو اس سے ٹھٹھا ہی کرتے ہیں،'),
(3736, 36, 31, 'کیا انہوں نے نہ دیکھا ہم نے ان سے پہلے کتنی سنگتیں ہلاک فرمائیں کہ وہ اب ان کی طرف پلٹنے والے نہیں'),
(3737, 36, 32, 'اور جتنے بھی ہیں سب کے سب ہمارے حضور حاضر لائے جائیں گے'),
(3738, 36, 33, 'اور ان کے لیے ایک نشانی مردہ زمین ہے ہم نے اسے زندہ کیا اور پھر اس سے اناج نکالا تو اس میں سے کھاتے ہیں،'),
(3739, 36, 34, 'اور ہم نے اس میں باغ بنائے کھجوروں اور انگو روں کے اور ہم نے اس میں کچھ چشمے بہائے کہ،'),
(3740, 36, 35, 'اس کے پھلوں میں سے کھائیں اور یہ ان کے ہاتھ کے بنائے نہیں تو کیا حق نہ مانیں گے'),
(3741, 36, 36, 'پاکی ہے اسے جس نے سب جوڑے بنائے ان چیزوں سے جنہیں زمین اگاتی ہے اور خود ان سے اور ان چیزوں سے جن کی انہیں خبر نہیں'),
(3742, 36, 37, 'اور ان کے لیے ایک نشانی رات ہے ہم اس پر سے دن کھینچ لیتے ہیں جبھی وہ اندھیروں میں ہیں،'),
(3743, 36, 38, 'اور سورج چلتا ہے اپنے ایک ٹھہراؤ کے لیے یہ حکم ہے زبردست علم والے کا'),
(3744, 36, 39, 'اور چاند کے لیے ہم نے منزلیں مقرر کیں یہاں تک کہ پھر ہوگیا جیسے کھجور کی پرانی ڈال (ٹہنی)'),
(3745, 36, 40, 'سورج کو نہیں پہنچتا کہ چاند کو پکڑے اور نہ رات دن پر سبقت لے جائے اور ہر ایک، ایک گھیرے میں پیر رہا ہے،'),
(3746, 36, 41, 'اور ان کے لیے نشانی یہ ہے کہ انہیں ان بزرگوں کی پیٹھ میں ہم نے بھری کشتی میں سوار کیا'),
(3747, 36, 42, 'اور ان کے لیے ویسی ہی کشتیاں بنادیں جن پر سوار ہوتے ہیں،'),
(3748, 36, 43, 'اور ہم چاہیں تو انہیں ڈبودیں تو نہ کوئی ان کی فریاد کو پہنچنے والا ہو اور نہ وہ بچائے جائیں،'),
(3749, 36, 44, 'مگر ہماری طرف کی رحمت اور ایک وقت تک برتنے دینا'),
(3750, 36, 45, 'اور جب ان سے فرمایا جاتا ہے ڈرو تم اس سے جو تمہارے سامنے ہے اور جو تمہارے پیچھے آنے والا ہے اس امید پر کہ تم پر مہر ہو تو منہ پھیر لیتے ہیں،'),
(3751, 36, 46, 'اور جب کبھی ان کے رب کی نشانیوں سے کوئی نشانی ان کے پاس آتی ہے تو اس سے منہ پھیرلیتے ہیں،'),
(3752, 36, 47, 'اور جب ان سے فرمایا جائے اللہ کے دیے میں سے کچھ اس کی راہ میں خرچ کرو تو کافر مسلمانوں کے لیے کہتے ہیں کہ کیا ہم اسے کھلائیں جسے اللہ چاہتا تو کھلادیتا تم تو نہیں مگر کھلی گمراہی میں،'),
(3753, 36, 48, 'اور کہتے ہیں کب آئے گا یہ وعدہ اگر تم سچے ہو'),
(3754, 36, 49, 'راہ نہیں دیکھتے مگر ایک چیخ کی کہ انہیں آلے گی جب وہ دنیا کے جھگڑے میں پھنسے ہوں گے،'),
(3755, 36, 50, 'تو نہ وصیت کرسکیں گے اور نہ اپنے گھر پلٹ کرجائیں'),
(3756, 36, 51, 'اور پھونکا جائے گا صور جبھی وہ قبروں سے اپنے رب کی طرف دوڑتے چلیں گے،'),
(3757, 36, 52, 'کہیں گے ہائے ہماری خرابی کس نے ہمیں سوتے سے جگادیا یہ ہے وہ جس کا رحمٰن نے وعدہ دیا تھا اور رسولوں نے حق فرمایا'),
(3758, 36, 53, 'وہ تو نہ ہوگی مگر ایک چنگھاڑ جبھی وہ سب کے سب ہمارے حضور حاضر ہوجائیں گے'),
(3759, 36, 54, 'تو آج کسی جان پر کچھ ظلم نہ ہوگا اور تمہیں بدلا نہ ملے گا اپنے کیے کا،'),
(3760, 36, 55, 'بیشک جنت والے آج دل کے بہلاووں میں چین کرتے ہیں'),
(3761, 36, 56, 'وہ اور ان کی بیبیاں سایوں میں ہیں، تختوں پر تکیہ لگائے،'),
(3762, 36, 57, 'ان کے لیے اس میں میوہ ہے اور ان کے لیے ہے اس میں جو مانگیں،'),
(3763, 36, 58, 'ان پر سلام ہوگا، مہربان رب کا فرمایا ہوا'),
(3764, 36, 59, 'اور آج الگ پھٹ جاؤ، اے مجرمو!'),
(3765, 36, 60, 'اے اولاد آدم کیا میں نے تم سے عہد نہ لیا تھا کہ شیطان کو نہ پوجنا بیشک وہ تمہارا کھلا دشمن ہے،'),
(3766, 36, 61, 'اور میری بندگی کرنا یہ سیدھی راہ ہے،'),
(3767, 36, 62, 'اور بیشک اس نے تم میں سے بہت سی خلقت کو بہکادیا، تو کیا تمہیں عقل نہ تھی'),
(3768, 36, 63, 'یہ ہے وہ جہنم جس کا تم سے وعدہ تھا،'),
(3769, 36, 64, 'آج اسی میں جاؤ بدلہ اپنے کفر کا،'),
(3770, 36, 65, 'آج ہم ان کے مونھوں پر مہر کردیں گے اور ان کے ہاتھ ہم سے بات کریں گے اور ان پاؤں ان کے کئے کی گواہی دیں گے'),
(3771, 36, 66, 'اور اگر ہم چاہتے تو ان کی آنکھیں مٹادیتے پھر لپک کر رستہ کی طرف جاتے تو انہیں کچھ نہ سوجھتا'),
(3772, 36, 67, 'اور اگر ہم چاہتے تو ان کے گھر بیٹھے ان کی صورتیں بدل دیتے نہ آگے بڑھ سکتے نہ پیچھے لوٹتے'),
(3773, 36, 68, 'اور جسے ہم بڑی عمر کا کریں اسے پیدائش میں الٹا پھیریں تو کیا سمجھے نہیں'),
(3774, 36, 69, 'اور ہم نے ان کو شعر کہنا نہ سکھایا اور نہ وہ ان کی شان کے لائق ہے، وہ تو نہیں مگر نصیحت اور روشن قرآن'),
(3775, 36, 70, 'کہ اسے ڈرائے جو زندہ ہو اور کافروں پر بات ثابت ہوجائے'),
(3776, 36, 71, 'اور کیا انہوں نے نہ دیکھا کہ ہم نے اپنے ہاتھ کے بنائے ہوئے چوپائے ان کے لیے پیدا کیے تو یہ ان کے مالک ہیں،'),
(3777, 36, 72, 'اور انہیں ان کے لیے نرم کردیا تو کسی پر سوار ہوتے ہیں اور کسی کو کھاتے ہیں،'),
(3778, 36, 73, 'اور ان کے لیے ان میں کئی طرح کے نفع اور پینے کی چیزیں ہیں تو کیا شکر نہ کریں گے'),
(3779, 36, 74, 'اور انہوں نے اللہ کے سوا اور خدا ٹھہرالیے کہ شاید ان کی مدد ہو'),
(3780, 36, 75, 'وہ ان کی مدد نہیں کرسکتے اور وہ ان کے لشکر سب گرفتار حاضر آئیں گے'),
(3781, 36, 76, 'تو تم ان کی بات کا غم نہ کرو بیشک ہم جانتے ہیں جو وہ چھپاتے ہیں اور ظاہر کرتے ہیں'),
(3782, 36, 77, 'اور کیا آدمی نے نہ دیکھا کہ ہم نے اسے پانی کی بوند سے بنایا جبھی وہ صریح جھگڑالو ہے'),
(3783, 36, 78, 'اور ہمارے لیے کہاوت کہتا ہے اور اپنی پیدائش بھول گیا بولا ایسا کون ہے کہ ہڈیوں کو زندہ کرے جب وہ بالکل گل گئیں،'),
(3784, 36, 79, 'تم فرماؤ وہ زندہ کرے گا جس نے پہلی بار انہیں بنایا، اور اسے ہر پیدائش کا علم ہے'),
(3785, 36, 80, 'جس نے تمہارے لیے ہرے پیڑ میں ا ٓ گ پیدا کی جبھی تم اس سے سلگاتے ہو'),
(3786, 36, 81, 'اور کیا وہ جس نے آسمان اور زمین بنائے ان جیسے اور نہیں بناسکتا کیوں نہیں اور وہی بڑا پیدا کرنے والا سب کچھ جانتا،'),
(3787, 36, 82, 'اس کا کام تو یہی ہے کہ جب کسی چیز کو چاہے تو اس سے فرمائے ہو جا وہ فوراً ہوجاتی ہے،'),
(3788, 36, 83, 'تو پاکی ہے، اسے جس کے ہاتھ ہر چیز کا قبضہ ہے، اور اسی کی طرف پھیرے جاؤ گے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'قسم ان کی کہ باقاعدہ صف باندھیں'),
(3790, 37, 2, 'پھر ان کی کہ جھڑک کر چلائیں'),
(3791, 37, 3, 'پھر ان جماعتوں کی، کہ قرآن پڑھیں،'),
(3792, 37, 4, 'بیشک تمہارا معبود ضرور ایک ہے،'),
(3793, 37, 5, 'مالک آسمانوں اور زمین کا اور جو کچھ ان کے درمیان ہے اور مالک مشرقوں کا'),
(3794, 37, 6, 'اور بیشک ہم نے نیچے کے آسمان کو تاروں کے سنگھار سے آراستہ کیا،'),
(3795, 37, 7, 'اور نگاہ رکھنے کو ہر شیطان سرکش سے'),
(3796, 37, 8, 'عالم بالا کی طرف کان نہیں لگاسکتے اور ان پر ہر طرف سے مار پھینک ہوتی ہے'),
(3797, 37, 9, 'انہیں بھگانے کو اور ان کے لیے ہمیشہ کا عذاب،'),
(3798, 37, 10, 'مگر جو ایک آدھ بار اُچک لے چلا تو روشن انگار اس کے پیچھے لگا،'),
(3799, 37, 11, 'تو ان سے پوچھو کیا ان کی پیدائش زیادہ مضبوط ہے یا ہماری اور مخلوق آسمانوں اور فرشتوں وغیرہ کی بیشک ہم نے ان کو چپکتی مٹی سے بنایا'),
(3800, 37, 12, 'بلکہ تمہیں اچنبھا آیا اور وہ ہنسی کرتے ہیں'),
(3801, 37, 13, 'اور سمجھائے نہیں سمجھتے،'),
(3802, 37, 14, 'اور جب کوئی نشانی دیکھتے ہیں ٹھٹھا کرتے ہیں،'),
(3803, 37, 15, 'اور کہتے ہیں یہ تو نہیں مگر کھلا جادو،'),
(3804, 37, 16, 'کیا جب ہم مر کر مٹی اور ہڈیاں ہوجائیں گے کیا ہم ضرور اٹھائے جائیں گے،'),
(3805, 37, 17, 'اور کیا ہمارے اگلے باپ دادا بھی'),
(3806, 37, 18, 'تم فرماؤ ہاں یوں کہ ذلیل ہو کے،'),
(3807, 37, 19, 'تو وہ تو ایک ہی جھڑک ہے جبھی وہ دیکھنے لگیں گے،'),
(3808, 37, 20, 'اور کہیں گے ہائے ہماری خرابی ان سے کہا جائے گا یہ انصاف کا دن ہے'),
(3809, 37, 21, 'یہ ہے وہ فیصلے کا دن جسے تم جھٹلاتے تھے'),
(3810, 37, 22, 'ہانکو ظالموں اور ان کے جوڑوں کو اور جو کچھ وہ پوجتے تھے،'),
(3811, 37, 23, 'اللہ کے سوا، ان سب کو ہانکو راہِ دوزخ کی طرف،'),
(3812, 37, 24, 'اور انہیں ٹھہراؤ ان سے پوچھنا ہے'),
(3813, 37, 25, 'تمہیں کیا ہوا ایک دوسرے کی مدد کیوں نہیں کرتے'),
(3814, 37, 26, 'بلکہ وہ آج گردن ڈالے ہیں'),
(3815, 37, 27, 'اور ان میں ایک نے دوسرے کی طرف منہ کیا آپس میں پوچھتے ہوئے،'),
(3816, 37, 28, 'بولے تم ہمارے دہنی طرف سے بہکانے آتے تھے'),
(3817, 37, 29, 'جواب دیں گے تم خود ہی ایمان نہ رکھتے تھے'),
(3818, 37, 30, 'اور ہمارا تم پر کچھ قابو نہ تھا بلکہ تم سرکش لوگ تھے،'),
(3819, 37, 31, 'تو ثابت ہوگئی ہم پر ہمارے رب کی بات ہمیں ضرور چکھنا ہے'),
(3820, 37, 32, 'تو ہم نے تمہیں گمراہ کیا کہ ہم خود گمراہ تھے،'),
(3821, 37, 33, 'تو اس دن وہ سب کے سب عذاب میں شریک ہیں'),
(3822, 37, 34, 'مجرموں کے ساتھ ہم ایسا ہی کرتے ہیں،'),
(3823, 37, 35, 'بیشک جب ان سے کہا جاتا تھا کہ اللہ کے سوا کسی کی بندگی نہیں تو اونچی کھینچتے (تکبر کرتے) تھے'),
(3824, 37, 36, 'اور کہتے تھے کیا ہم اپنے خداؤں کو چھوڑدیں ایک دیوانہ شاعر کے کہنے سے'),
(3825, 37, 37, 'بلکہ وہ تو حق لائے ہیں اور انہوں نے رسولوں کی تصدیق فرمائی'),
(3826, 37, 38, 'بیشک تمہیں ضرور دکھ کی مار چکھنی ہے،'),
(3827, 37, 39, 'تو تمہیں بدلہ نہ ملے گا مگر اپنے کیے کا'),
(3828, 37, 40, 'مگر جو اللہ کے چُنے ہوئے بندے ہیں'),
(3829, 37, 41, 'ان کے لیے وہ روزی ہے جو ہمارے علم میں ہیں،'),
(3830, 37, 42, 'میوے اور ان کی عزت ہوگی،'),
(3831, 37, 43, 'چین کے باغوں میں،'),
(3832, 37, 44, 'تختوں پر ہوں گے آمنے سامنے'),
(3833, 37, 45, 'ان پر دورہ ہوگا نگاہ کے سامنے بہتی شراب کے جام کا'),
(3834, 37, 46, 'سفید رنگ پینے والوں کے لیے لذت'),
(3835, 37, 47, 'نہ اس میں خمار ہے اور نہ اس سے ان کا سَر پِھرے'),
(3836, 37, 48, 'اور ان کے پاس ہیں جو شوہروں کے سوا دوسری طرف آنکھ اٹھا کر نہ دیکھیں گی'),
(3837, 37, 49, 'بڑی آنکھوں والیاں گویا وہ انڈے ہیں پوشیدہ رکھے ہوئے'),
(3838, 37, 50, 'تو ان میں ایک نے دوسرے کی طرف منہ کیا پوچھتے ہوئے'),
(3839, 37, 51, 'ان میں سے کہنے والا بولا میرا ایک ہمنشین تھا'),
(3840, 37, 52, 'مجھ سے کہا کرتا کیا تم اسے سچ مانتے ہو'),
(3841, 37, 53, 'کیا جب ہم مر کر مٹی اور ہڈیاں ہوجائیں گے تو کیا ہمیں جزا سزا دی جائے گی'),
(3842, 37, 54, 'کہا کیا تم جھانک کر دیکھو گے'),
(3843, 37, 55, 'پھر جھانکا تو اسے بیچ بھڑکتی آگ میں دیکھا'),
(3844, 37, 56, 'کہا خدا کی قسم قریب تھا کہ تو مجھے ہلاک کردے'),
(3845, 37, 57, 'اور میرا رب فضل نہ کرے تو ضرور میں بھی پکڑ کر حاضر کیا جاتا'),
(3846, 37, 58, 'تو کیا ہمیں مرنا نہیں،'),
(3847, 37, 59, 'مگر ہماری پہلی موت اور ہم پر عذاب نہ ہوگا'),
(3848, 37, 60, 'بیشک یہی بڑی کامیابی ہے،'),
(3849, 37, 61, 'ایسی ہی بات کے لیے کامیوں کو کام کرنا چاہیے،'),
(3850, 37, 62, 'تو یہ مہمانی بھلی یا تھوہڑ کا پیڑ'),
(3851, 37, 63, 'بیشک ہم نے اسے ظالموں کی جانچ کیا ہے'),
(3852, 37, 64, 'بیشک وہ ایک پیڑ ہے کہ جہنم کی جڑ میں نکلتا ہے'),
(3853, 37, 65, 'اس کا شگوفہ جیسے دیووں کے سر'),
(3854, 37, 66, 'پھر بیشک وہ اس میں سے کھائیں گے پھر اس سے پیٹ بھریں گے،'),
(3855, 37, 67, 'پھر بیشک ان کے لیے اس پر کھولتے پانی کی ملونی (ملاوٹ) ہے'),
(3856, 37, 68, 'پھر ان کی بازگشت ضرور بھڑکتی آگ کی طرف ہے'),
(3857, 37, 69, 'بیشک انہوں نے اپنے باپ دادا گمراہ پائے،'),
(3858, 37, 70, 'تو وہ انہیں کے نشان قدم پر دوڑے جاتے ہیں'),
(3859, 37, 71, 'اور بیشک ان سے پہلے بہت سے اگلے گمراہ ہوئے'),
(3860, 37, 72, 'اور بیشک ہم نے ان میں ڈر سنانے والے بھیجے'),
(3861, 37, 73, 'تو دیکھو ڈرائے گیوں کا کیسا انجام ہوا'),
(3862, 37, 74, 'مگر اللہ کے چُنے ہوئے بندے'),
(3863, 37, 75, 'اور بیشک ہمیں نوح نے پکارا تو ہم کیا ہی اچھے قبول فرمانے والے'),
(3864, 37, 76, 'اور ہم نے اسے اور اس کے گھر والوں کو بڑی تکلیف سے نجات دی،'),
(3865, 37, 77, 'اور ہم نے اسی کی اولاد باقی رکھی'),
(3866, 37, 78, 'اور ہم نے پچھلوں میں اس کی تعریف باقی رکھی'),
(3867, 37, 79, 'نوح پر سلام ہو جہاں والوں میں'),
(3868, 37, 80, 'بیشک ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(3869, 37, 81, 'بیشک وہ ہمارے اعلیٰ درجہ کے کامل الایمان بندوں میں ہے،'),
(3870, 37, 82, 'پھر ہم نے دوسروں کو ڈبو دیا'),
(3871, 37, 83, 'اور بیشک اسی کے گروہ سے ابراہیم ہے'),
(3872, 37, 84, 'جبکہ اپنے رب کے پاس حاضر ہوا غیر سے سلامت دل لے کر'),
(3873, 37, 85, 'جب اس نے اپنے باپ اور اپنی قوم سے فرمایا تم کیا پوجتے ہو،'),
(3874, 37, 86, 'کیا بہتان سے اللہ کے سوا اور خدا چاہتے ہو،'),
(3875, 37, 87, 'تو تمہارا کیا گمان سے رب العالمین پر'),
(3876, 37, 88, 'پھر اس نے ایک نگاہ ستاروں کو دیکھا'),
(3877, 37, 89, 'پھر کہا میں بیمار ہونے والا ہوں'),
(3878, 37, 90, 'تو وہ اس پر پیٹھ دے کر پھر گئے'),
(3879, 37, 91, 'پھر ان کے خداؤں کی طرف چھپ کر چلا تو کہا کیا تم نہیں کھاتے'),
(3880, 37, 92, 'تمہیں کیا ہوا کہ نہیں بولتے'),
(3881, 37, 93, 'تو لوگوں کی نظر بچا کر انہیں دہنے ہاتھ سے مارنے لگا'),
(3882, 37, 94, 'تو کافر اس کی طرف جلدی کرتے آئے'),
(3883, 37, 95, 'فرمایا کیا اپنے ہاتھ کے تراشوں کو پوجتے ہو،'),
(3884, 37, 96, 'اور اللہ نے تمہیں پیدا کیا اور تمہارے اعمال کو'),
(3885, 37, 97, 'بولے اس کے لیے ایک عمارت چنو پھر اسے بھڑکتی آگ میں ڈال دو،'),
(3886, 37, 98, 'تو انہوں نے اس پر داؤں چلنا (فریب کرنا) چاہا ہم نے انہیں نیچا دکھایا'),
(3887, 37, 99, 'اور کہا میں اپنے رب کی طرف جانے والا ہوں اب وہ مجھے راہ دے گا'),
(3888, 37, 100, 'الٰہی مجھے لائق اولاد دے،'),
(3889, 37, 101, 'تو ہم نے اسے خوشخبری سنائی ایک عقل مند لڑکے کی،'),
(3890, 37, 102, 'پھر جب وہ اس کے ساتھ کام کے قابل ہوگیا کہا اے میرے بیٹے میں نے خواب دیکھا میں تجھے ذبح کرتا ہوں اب تو دیکھ تیری کیا رائے ہے کہا اے میرے باپ کی جیئے جس بات کا آپ کو حکم ہوتا ہے، خدا نے چاہتا تو قریب ہے کہ آپ مجھے صابر پائیں گے،'),
(3891, 37, 103, 'تو جب ان دونوں نے ہمارے حکم پر گردن رکھی اور باپ نے بیٹے کو ماتھے کے بل لٹایا اس وقت کا حال نہ پوچھ'),
(3892, 37, 104, 'اور ہم نے اسے ندائی فرمائی کہ اے ابراہیم،'),
(3893, 37, 105, 'بیشک تو نے خواب سچ کردکھایا ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(3894, 37, 106, 'بیشک یہ روشن جانچ تھی،'),
(3895, 37, 107, 'اور ہم نے ایک بڑا ذبیحہ اس کے فدیہ میں دے کر اسے بچالیا'),
(3896, 37, 108, 'اور ہم نے پچھلوں میں اس کی تعریف باقی رکھی،'),
(3897, 37, 109, 'سلام ہو ابراہیم پر'),
(3898, 37, 110, 'ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(3899, 37, 111, 'بیشک وہ ہمارے اعلیٰ درجہ کے کامل الایمان بندوں میں ہیں،'),
(3900, 37, 112, 'اور ہم نے اسے خوشخبری دی اسحاق کی کہ غیب کی خبریں بتانے والا نبی ہمارے قربِ خاص کے سزاواروں میں'),
(3901, 37, 113, 'اور ہم نے برکت اتاری اس پر اور اسحاق پر اور ان کی اولاد میں کوئی اچھا کام کرنے والا اور کوئی اپنی جان پر صریح ظلم کرنے والا'),
(3902, 37, 114, 'اور بیشک ہم نے موسیٰ اور ہارون پر احسان فرمایا'),
(3903, 37, 115, 'اور انہیں اور ان کی قوم کو بڑی سختی سے نجات بخشی'),
(3904, 37, 116, 'اور ان کی ہم نے مدد فرمائی تو وہی غالب ہوئے'),
(3905, 37, 117, 'اور ہم نے ان دونوں کو روشن کتاب عطا فرمائی'),
(3906, 37, 118, 'اور ان کو سیدھی راہ دکھائی،'),
(3907, 37, 119, 'اور پچھلوں میں ان کی تعریف باقی رکھی،'),
(3908, 37, 120, 'سلام ہو موسیٰ اور ہارون پر،'),
(3909, 37, 121, 'بیشک ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(3910, 37, 122, 'بیشک وہ دونوں ہمارے اعلیٰ درجہ کے کامل الایمان بندوں میں ہیں،'),
(3911, 37, 123, 'اور بیشک الیاس پیغمبروں سے ہے'),
(3912, 37, 124, 'جب اس نے اپنی قوم سے فرمایا کیا تم ڈرتے نہیں'),
(3913, 37, 125, 'کیا بعل کو پوجتے ہو اور چھوڑتے ہو سب سے اچھا پیدا کرنے والے اللہ کو،'),
(3914, 37, 126, 'جو رب ہے تمہارا اور تمہارے اگلے باپ دادا کا'),
(3915, 37, 127, 'پھر انہو ں نے اسے جھٹلایا تو وہ ضرور پکڑے آئیں گے'),
(3916, 37, 128, 'مگر اللہکے چُنے ہوئے بندے'),
(3917, 37, 129, 'اور ہم نے پچھلوں میں اس کی ثنا باقی رکھی،'),
(3918, 37, 130, 'سلام ہو الیاس پر،'),
(3919, 37, 131, 'بیشک ہم ایسا ہی صلہ دیتے ہیں نیکوں کو،'),
(3920, 37, 132, 'بیشک وہ ہمارے اعلیٰ درجہ کے کامل الایمان بندوں میں ہے،'),
(3921, 37, 133, 'اور بیشک لوط پیغمبروں میں ہے،'),
(3922, 37, 134, 'جبکہ ہم نے اسے اور اس کے سب گھر والوں کو نجات بخشی،'),
(3923, 37, 135, 'مگر ایک بڑھیا کہ رہ جانے والوں میں ہوئی'),
(3924, 37, 136, 'پھر دوسروں کو ہم نے ہلاک فرمادیا'),
(3925, 37, 137, 'او ربیشک تم ان پر گزرتے ہو صبح کو،'),
(3926, 37, 138, 'اور رات میں تو کیا تمہیں عقل نہیں'),
(3927, 37, 139, 'اور بیشک یونس پیغمبروں سے ہے،'),
(3928, 37, 140, 'جبکہ بھری کشتی کی طرف نکل گیا'),
(3929, 37, 141, 'تو قرعہ ڈالا تو ڈھکیلے ہوؤں میں ہوا،'),
(3930, 37, 142, 'پھر اسے مچھلی نے نگل لیا اور وہ اپنے آپ کو ملامت کرتا تھا'),
(3931, 37, 143, 'تو اگر وہ تسبیح کرنے والا نہ ہوتا'),
(3932, 37, 144, 'ضرور اس کے پیٹ میں رہتا جس دن تک لوگ اٹھائے جائیں گے'),
(3933, 37, 145, 'پھر ہم نے اسے میدان میں ڈال دیا اور وہ بیمار تھا'),
(3934, 37, 146, 'اور ہم نے اس پر کدو کا پیڑ اگایا'),
(3935, 37, 147, 'اور ہم نے اسے لاکھ آدمیوں کی طرف بھیجا بلکہ زیادہ،'),
(3936, 37, 148, 'تو وہ ایمان لے آئے تو ہم نے انہیں ایک وقت تک برتنے دیا'),
(3937, 37, 149, 'تو ان سے پوچھو کیا تمہارے رب کے لیے بیٹیاں ہیں اور ان کے بیٹے'),
(3938, 37, 150, 'یا ہم نے ملائکہ کو عورتیں پیدا کیا اور وہ حاضر تھے'),
(3939, 37, 151, 'سنتے ہو بیشک وہ اپنے بہتان سے کہتے ہیں،'),
(3940, 37, 152, 'کہ اللہ کی اولاد ہے اور بیشک وہ ضرور جھوٹے ہیں،'),
(3941, 37, 153, 'کیا اس نے بیٹیاں پسند کیں بیٹے چھوڑ کر،'),
(3942, 37, 154, 'تمہیں کیا ہے، کیسا حکم لگاتے ہو'),
(3943, 37, 155, 'تو کیا دھیان نہیں کرتے'),
(3944, 37, 156, 'یا تمہارے لیے کوئی کھلی سند ہے،'),
(3945, 37, 157, 'تو اپنی کتاب لاؤ اگر تم سچے ہو،'),
(3946, 37, 158, 'اور اس میں اور جنوں میں رشتہ ٹھہرایا اور بیشک جنوں کو معلوم ہے کہ وہ ضرور حاضر لائے جائیں گے'),
(3947, 37, 159, 'پاکی ہے اللہ کو ان باتوں سے کہ یہ بتاتے ہیں،'),
(3948, 37, 160, 'مگر اللہ کے چُنے ہوئے بندے'),
(3949, 37, 161, 'تو تم اور جو کچھ تم اللہ کے سوا پوجتے ہو'),
(3950, 37, 162, 'تم اس کے خلاف کسی کو بہکانے والے نہیں'),
(3951, 37, 163, 'مگر اسے جو بھڑکتی آگ میں جانے والا ہے'),
(3952, 37, 164, 'اور فرشتے کہتے ہیں ہم میں ہر ایک کا ایک مقام معلوم ہے'),
(3953, 37, 165, 'اور بیشک ہم پر پھیلائے حکم کے منتظر ہیں،'),
(3954, 37, 166, 'اور بیشک ہم اس کی تسبیح کرنے والے ہیں،'),
(3955, 37, 167, 'اور بیشک وہ کہتے تھے'),
(3956, 37, 168, 'اگر ہمارے پاس اگلوں کی کوئی نصیحت ہوتی'),
(3957, 37, 169, 'تو ضرور ہم اللہ کے چُنے ہوئے بندے ہوتے'),
(3958, 37, 170, 'تو اس کے منکر ہوئے تو عنقریب جان لیں گے'),
(3959, 37, 171, 'اور بیشک ہمارا کلام گزر چکا ہے ہمارے بھیجے ہوئے بندوں کے لیے،'),
(3960, 37, 172, 'کہ بیشک انہیں کی مدد ہوگی،'),
(3961, 37, 173, 'اور بیشک ہمارا ہی لشکر غالب آئے گا،'),
(3962, 37, 174, 'تو ایک وقت تم ان سے منہ پھیر لو'),
(3963, 37, 175, 'اور انہیں دیکھتے رہو کہ عنقریب وہ دیکھیں گے'),
(3964, 37, 176, 'تو کیا ہمارے عذاب کی جلدی کرتے ہیں،'),
(3965, 37, 177, 'پھر جب اترے گا ان کے آنگن میں تو ڈرائے گیوں کی کیا ہی بری صبح ہوگی،'),
(3966, 37, 178, 'اور ایک وقت تک ان سے منہ پھیرلو،'),
(3967, 37, 179, 'اور انتظار کرو کہ وہ عنقریب دیکھیں گے،'),
(3968, 37, 180, 'پاکی ہے تمہارے رب کو عزت والے رب کو ان کی باتوں سے'),
(3969, 37, 181, 'اور سلام ہے پیغمبروں پر'),
(3970, 37, 182, 'اور سب خوبیاں اللہ کو سارے جہاں کا رب ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'اس نامور قرآن کی قسم'),
(3972, 38, 2, 'بلکہ کافر تکبر اور خلاف میں ہیں'),
(3973, 38, 3, 'ہم نے ان سے پہلے کتنی سنگتیں کھپائیں تو اب وہ پکاریں اور چھوٹنے کا وقت نہ تھا'),
(3974, 38, 4, 'اور انہیں اس کا اچنبھا ہوا کہ ان کے پاس انہیں میں کا ایک ڈر سنانے والا تشریف لایا اور کافر بولے یہ جادوگر ہے بڑا جھوٹا،'),
(3975, 38, 5, 'کیا اس نے بہت خداؤں کا ایک خدا کردیا بیشک یہ عجیب بات ہے،'),
(3976, 38, 6, 'اور ان میں کے سردار چلے کہ اس کے پاس سے چل دو اور اپنے خداؤں پر صابر رہو بیشک اس میں اس کا کوئی مطلب ہے،'),
(3977, 38, 7, 'یہ تو ہم نے سب سے پہلے دینِ نصرانیت میں بھی نہ سنی یہ تو نری نئی گڑھت ہے،'),
(3978, 38, 8, 'کیا ان پر قرآن اتارا گیا ہم سب میں سے بلکہ وہ شک میں ہیں میری کتاب سے بلکہ ابھی میری مار نہیں چکھی ہے'),
(3979, 38, 9, 'کیا وہ تمہارے رب کی رحمت کے خزانچی ہیں وہ عزت والا بہت عطا فرمانے والا ہے'),
(3980, 38, 10, 'کیا ان کے لیے ہے سلطنت آسمانوں اور زمین کی اور جو کچھ ان کے درمیان ہے، تو رسیاں لٹکا کر چڑھ نہ جائیں'),
(3981, 38, 11, 'یہ ایک ذلیل لشکر ہے انہیں لشکروں میں سے جو وہیں بھگادیا جائے گا'),
(3982, 38, 12, 'ان سے پہلے جھٹلا چکے ہیں نوح کی قوم اور عاد اور چومیخا کرنے والے فرعون'),
(3983, 38, 13, 'اور ثمود اور لوط کی قوم اور بن والے یہ ہیں وہ گروہ'),
(3984, 38, 14, 'ان میں کوئی ایسا نہیں جس نے رسولوں کو نہ جھٹلایا ہو تو میرا عذاب لازم ہوا'),
(3985, 38, 15, 'اور یہ راہ نہیں دیکھتے مگر ایک چیخ کی جسے کوئی پھیر نہیں سکتا،'),
(3986, 38, 16, 'اور بولے اے ہمارے رب ہمارا حصہ ہمیں جلد دے دے حساب کے دن سے پہلے'),
(3987, 38, 17, 'تم ان کی باتوں پر صبر کرو اور ہمارے بندے داؤد نعمتوں والے کو یاد کرو بیشک وہ بڑا رجوع کرنے والا ہے'),
(3988, 38, 18, 'بیشک ہم نے اس کے ساتھ پہاڑ مسخر فرمادیے کہ تسبیح کرتے شام کو اور سورج چمکتے'),
(3989, 38, 19, 'اور پرندے جمع کیے ہوئے سب اس کے فرمانبردار تھے'),
(3990, 38, 20, 'اور ہم نے اس کی سلطنت کو مضبوط کیا اور اسے حکمت اور قولِ فیصل دیا'),
(3991, 38, 21, 'اور کیا تمہیں اس دعوے والوں کی بھی خبر آئی جب وہ دیوار کود کر داؤد کی مسجد میں آئے'),
(3992, 38, 22, 'جب وہ داؤد پر داخل ہوئے تو وہ ان سے گھبرا گیا انہوں نے عرض کی ڈریے نہیں ہم دو فریق ہیں کہ ایک نے دوسرے پر زیادتی کی ہے تو ہم میں سچا فیصلہ فرمادیجئے اور خلافِ حق نہ کیجئے اور ہمیں سیدھی راہ بتایے،'),
(3993, 38, 23, 'بیشک یہ میرا بھائی ہے اس کے پاس ننانوے دُنبیاں ہیں اور میرے پاس ایک دُنبی، اب یہ کہتا ہے وہ بھی مجھے حوالے کردے اور بات میں مجھ پر زور ڈالتا ہے،'),
(3994, 38, 24, 'داؤد نے فرمایا بیشک یہ تجھ پر زیادتی کرتا ہے کہ تیری دُنبی اپنی دُنبیوں میں ملانے کو مانگتا ہے، اور بیشک اکثر ساجھے والے ایک دوسرے پر زیادتی کرتے ہیں مگر جو ایمان لائے اور اچھے کام کیے اور وہ بہت تھوڑے ہیں اب داؤد سمجھا کہ ہم نے یہ اس کی جانچ کی تھی تو اپنے رب سے معافی مانگی اور سجدے میں گر پڑا اور رجوع لایا، (السجدة ۱۰)'),
(3995, 38, 25, 'تو ہم نے اسے یہ معاف فرمایا، اور بیشک اس کے لیے ہماری بارگاہ میں ضرور قرب اور اچھا ٹھکانا ہے،'),
(3996, 38, 26, 'اے داؤد بیشک ہم نے تجھے زمین میں نائب کیا تو لوگوں میں سچا حکم کر اور خواہش کے پیچھے نہ جانا کہ تجھے اللہ کی راہ سے بہکادے گی، بیشک وہ جو اللہ کی راہ سے بہکتے ہیں ان کے لیے سخت عذاب ہے اس پر کہ وہ حساب کے دن کو بھول بیٹھے'),
(3997, 38, 27, 'اور ہم نے آسمان اور زمین اور جو کچھ ان کے درمیان ہے بیکار نہ بنائے، یہ کافروں کا گمان ہے تو کافروں کی خرابی ہے آگ سے،'),
(3998, 38, 28, 'کیا ہم انہیں جو ایمان لائے اور اچھے کام کیے ان جیسا کردیں جو زمین میں فساد پھیلاتے ہیں، یا ہم پرہیزگاروں کو شریر بے حکموں کے برابر ٹھہرادیں'),
(3999, 38, 29, 'یہ ایک کتاب ہے کہ ہم نے تمہاری طرف اتاری برکت والی تاکہ اس کی آیتوں کو سوچیں اور عقلمند نصیحت مانیں،'),
(4000, 38, 30, 'اور ہم نے داؤد کو سلیمان عطا فرمایا، کیا اچھا بندہ بیشک وہ بہت رجوع لانے والا'),
(4001, 38, 31, 'جبکہ اس پر پیش کیے گئے تیسرے پہر کو کہ روکئے تو تین پاؤں پر کھڑے ہوں چوتھے سم کا کنارہ زمین پر لگائے ہوئے اور چلائے تو ہوا ہوجائیں'),
(4002, 38, 32, 'تو سلیمان نے کہا مجھے ان گھوڑوں کی محبت پسند آئی ہے اپنے رب کی یاد کے لیے پھر انہیں چلانے کا حکم دیا یہاں تک کہ نگاہ سے پردے میں چھپ گئے'),
(4003, 38, 33, 'پھر حکم دیا کہ انہیں میرے پاس واپس لاؤ تو ان کی پنڈلیوں اور گردنوں پر ہاتھ پھیرنے لگا'),
(4004, 38, 34, 'اور بیشک ہم نے سلیمان کو جانچا اور اس کے تخت پر ایک بے جان بدن ڈال دیا پھر رجوع لایا'),
(4005, 38, 35, 'عرض کی اے میرے رب مجھے بخش دے اور مجھے ایسی سلطنت عطا کر کہ میرے بعد کسی کو لائق نہ ہو بیشک تو ہی بڑی دین والا،'),
(4006, 38, 36, 'تو ہم نے ہوا اس کے بس میں کردی کہ اس کے حکم سے نرم نرم چلتی جہاں وہ چاہتا،'),
(4007, 38, 37, 'اور دیو بس میں کردیے ہر معمار اور غوطہ خور'),
(4008, 38, 38, 'اور دوسرے اور بیڑیوں میں جکڑے ہوئے'),
(4009, 38, 39, 'یہ ہماری عطا ہے اب تو چاہے تو احسان کر یا روک رکھ تجھ پر کچھ حساب نہیں،'),
(4010, 38, 40, 'اور بیشک اس کے لیے ہماری بارگاہ میں ضرور قرب اور اچھا ٹھکانا ہے،'),
(4011, 38, 41, 'اور یاد کرو ہمارے بندہ ایوب کو جب اس نے اپنے رب کو پکارا کہ مجھے شیطان نے تکلیف اور ایذا لگادی،'),
(4012, 38, 42, 'ہم نے فرمایا زمین پر اپنا پاؤں مار یہ ہے ٹھنڈا چشمہ نہانے اور پینے کو'),
(4013, 38, 43, 'اور ہم نے اسے اس کے گھر والے اور ان کے برابر اور عطا فرمادیے اپنی رحمت کرنے اور عقلمندوں کی نصیحت کو،'),
(4014, 38, 44, 'اور فرمایا کہ اپنے ہاتھ میں ایک جھاڑو لے کر اس سے مار دے اور قسم نہ توڑ بے ہم نے اسے صابر پایا کیا اچھا بندہ بیشک وہ بہت رجوع لانے والا ہے،'),
(4015, 38, 45, 'اور یاد کرو ہمارے بندوں ابراہیم اور اسحاق اور یعقوب قدرت اور علم والوں کو'),
(4016, 38, 46, 'بیشک ہم نے انہیں ایک کھری بات سے امتیاز بخشا کہ وہ اس گھر کی یاد ہے'),
(4017, 38, 47, 'اور بیشک وہ ہمارے نزدیک چُنے ہوئے پسندیدہ ہیں،'),
(4018, 38, 48, 'اور یاد کرو اسماعیل اور یسع اور ذو الکفل کو اورسب اچھے ہیں،'),
(4019, 38, 49, 'یہ نصیحت ہے اور بیشک پرہیزگاروں کا ٹھکانا،'),
(4020, 38, 50, 'بھلا بسنے کے باغ ان کے لیے سب دروازے کھلے ہوئے،'),
(4021, 38, 51, 'ان میں تکیہ لگائے ان میں بہت سے میوے اور شراب مانگتے ہیں،'),
(4022, 38, 52, 'اور ان کے پاس وہ بیبیاں ہیں کہ اپنے شوہر کے سوا اور کی طرف آنکھ نہیں اٹھاتیں، ایک عمر کی'),
(4023, 38, 53, 'یہ ہے وہ جس کا تمہیں وعدہ دیا جاتا ہے حساب کے دن،'),
(4024, 38, 54, 'بیشک یہ ہمارا رزق ہے کہ کبھی ختم نہ ہوگا'),
(4025, 38, 55, 'ان کو تو یہ ہے اور بیشک سرکشوں کا برا ٹھکانا،'),
(4026, 38, 56, 'جہنم کہ اس میں جائیں گے تو کیا ہی برا بچھونا'),
(4027, 38, 57, 'ان کو یہ ہے تو اسے چکھیں کھولتا پانی اور پیپ'),
(4028, 38, 58, 'اور اسی شکل کے اور جوڑے'),
(4029, 38, 59, 'ان سے کہا جائے گا یہ ایک اور فوج تمہارے ساتھ دھنسی پڑتی ہے جو تمہاری تھی وہ کہیں گے ان کو کھلی جگہ نہ ملیو آگ میں تو ان کو جانا ہی ہے،'),
(4030, 38, 60, 'وہاں بھی تنگ جگہ میں رہیں تابع بولے بلکہ تمہیں کھلی جگہ نہ ملیو، یہ مصیبت تم ہمارے آگے لائے تو کیا ہی برا ٹھکانا'),
(4031, 38, 61, 'وہ بولے اے ہمارے رب جو یہ مصیبت ہمارے آگے لایا اسے آگ میں دُونا عذاب بڑھا،'),
(4032, 38, 62, 'اور بولے ہمیں کیا ہوا ہم ان مردوں کو نہیں دیکھتے جنہیں برا سمجھتے تھے'),
(4033, 38, 63, 'کیا ہم نے انہیں ہنسی بنالیا یا آنکھیں ان کی طرف سے پھر گئیں'),
(4034, 38, 64, 'بیشک یہ ضرور حق ہے دوزخیوں کا باہم جھگڑ،'),
(4035, 38, 65, 'تم فرماؤ میں ڈر سنانے والا ہی ہوں اور معبود کوئی نہیں مگر ایک اللہ سب پر غالب،'),
(4036, 38, 66, 'مالک آسمانوں اور زمین کا اور جو کچھ ان کے درمیان ہے صاحب عزت بڑا بخشنے والا،'),
(4037, 38, 67, 'تم فرماؤ وہ بڑی خبر ہے،'),
(4038, 38, 68, 'تم اس سے غفلت میں ہو'),
(4039, 38, 69, 'مجھے عالم بالا کی کیا خبر تھی جب وہ جھگڑتے تھے'),
(4040, 38, 70, 'مجھے تو یہی وحی ہوتی ہے کہ میں نہیں مگر روشن ڈر سنانے والا'),
(4041, 38, 71, 'جب تمہارے رب نے فرشتوں سے فرمایا کہ میں مٹی سے انسان بناؤں گا'),
(4042, 38, 72, 'پھر جب میں اسے ٹھیک بنالوں اور اس میں اپنی طرف کی روح پھونکوں تو تم اس کے لیے سجدے میں گرنا،'),
(4043, 38, 73, 'تو سب فرشتو ں نے سجدہ کیا ایک ایک نے کہ کوئی باقی نہ رہا،'),
(4044, 38, 74, 'مگر ابلیس نے اس نے غرور کیا اور وہ تھا ہی کافروں میں'),
(4045, 38, 75, 'فرمایا اے ابلیس تجھے کس چیز نے روکا کہ تو اس کے لیے سجدہ کرے جسے میں نے اپنے ہاتھوں سے بنایا تجھے غرور آگیا یا تو تھا ہی مغروروں میں'),
(4046, 38, 76, 'بولا میں اس سے بہتر ہوں تو نے مجھے آگ سے بنایا اور اسے مٹی سے پیدا کیا،'),
(4047, 38, 77, 'فرمایا تو جنت سے نکل جا کہ تو راندھا (لعنت کیا) گیا'),
(4048, 38, 78, 'اور بیشک تجھ پر میری لعنت ہے قیامت تک'),
(4049, 38, 79, 'بولا اے میرے رب ایسا ہے تو مجھے مہلت دے اس دن تک کہ اٹھائے جائیں'),
(4050, 38, 80, 'فرمایا تو تُو مہلت والوں میں ہے،'),
(4051, 38, 81, 'اس جانے ہوئے وقت کے دن تک'),
(4052, 38, 82, 'بولا تیری عزت کی قسم ضرور میں ان سب کو گمراہ کردوں گا،'),
(4053, 38, 83, 'مگر جو ان میں تیرے چنے ہوئے بندے ہیں،'),
(4054, 38, 84, 'فرمایا تو سچ یہ ہے اور میں سچ ہی فرماتا ہوں،'),
(4055, 38, 85, 'بیشک میں ضرور جہنم بھردوں گا تجھ سے اور ان میں سے جتنے تیری پیروی کریں گے سب سے،'),
(4056, 38, 86, 'تم فرماؤ میں اس قرآن پر تم سے کچھ اجر نہیں مانگتا اور میں بناوٹ والوں سے نہیں،'),
(4057, 38, 87, 'وہ تو نہیں مگر نصیحت سارے جہان کے لیے،'),
(4058, 38, 88, 'اور ضرور ایک وقت کے بعد تم اس کی خبر جانو گے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'کتاب اتارنا ہے اللہ عزت و حکمت والے کی طرف سے،'),
(4060, 39, 2, 'بیشک ہم نے تمہاری طرف یہ کتاب حق کے ساتھ اتاری تو اللہ کو کو پوجو نرے اس کے بندے ہوکر،'),
(4061, 39, 3, 'ہاں خالص اللہ ہی کی بندگی ہے اور وہ جنہوں نے اس کے سوا اور والی بنالیے کہتے ہیں ہم تو انہیں صرف اتنی بات کے لیے پوجتے ہیں کہ یہ ہمیں اللہ کے پاس نزدیک کردیں، اللہ ان پر فیصلہ کردے گا اس بات کا جس میں اختلاف کررہے ہیں بیشک اللہ راہ نہیں دیتا اسے جو جھوٹا بڑا ناشکرا ہو'),
(4062, 39, 4, 'اللہ اپنے لیے بچہ بناتا تو اپنی مخلوق میں سے جسے چاہتا چن لیتا پاکی ہے اسے وہی ہے ایک اللہ سب پر غالب،'),
(4063, 39, 5, 'اس نے آسمان اور زمین حق بنائے رات کو دن پر لپیٹتا ہے اور دن کو رات پر لپیٹتا ہے اور اس نے سورج اور چاند کو کام میں لگایا ہر ایک، ایک ٹھہرائی میعاد کے لیے چلتا ہے سنتا ہے وہی صاحب عزت بخشنے والا ہے،'),
(4064, 39, 6, 'اس نے تمہیں ایک جان سے بنایا پھر اسی سے اس کا جوڑ پیدا کیا اور تمہارے لیے چوپایوں میں سے آٹھ جوڑے تھے تمہیں تمہاری ماؤں کے پیٹ میں بناتا ہے ایک طرح کے بعد اور طرح تین اندھیریوں میں یہ ہے اللہ تمہارا رب اسی کی بادشاہی ہے، اس کے سوا کسی کی بندگی نہیں، پھر کہیں پھیرے جاتے ہو'),
(4065, 39, 7, 'اگر تم ناشکری کرو تو بیشک اللہ بے نیاز ہے تم سے اور اپنے بندوں کی ناشکری اسے پسند نہیں، اور اگر شکر کرو تو اسے تمہارے لیے پسند فرماتا ہے اور کوئی بوجھ اٹھانے والی جان دوسرے کا بوجھ نہیں اٹھائے گی پھر تمہیں اپنے رب ہی کی طرف پھرنا ہے تو وہ تمہیں بتادے گا جو تم کرتے تھے بیشک وہ دلوں کی بات جانتا ہے،'),
(4066, 39, 8, 'اور جب آدمی کو کوئی تکلیف پہنچتی ہے اپنے رب کو پکارتا ہے اسی طرف جھکا ہوا پھر جب اللہ نے اسے اپنے پاس سے کوئی نعمت دی تو بھول جاتا ہے جس لیے پہلے پکارا تھا اور اللہ کے برابر والے ٹھہرانے لگتا ہے تاکہ اس کی راہ سے بہکادے تم فرماؤ تھوڑے دن اپنے کفر کے ساتھ برت لے بیشک تو دوزخیوں میں ہے،'),
(4067, 39, 9, 'کیا وہ جسے فرمانبرداری میں رات کی گھڑیاں گزریں سجود میں اور قیام میں آخرت سے ڈرتا اور اپنے رب کی رحمت کی آس لگائے کیا وہ نافرمانوں جیسا ہو جائے گا تم فرماؤ کیا برابر ہیں جاننے والے اور انجان، نصیحت تو وہی مانتے ہیں جو عقل والے ہیں،'),
(4068, 39, 10, 'تم فرماؤ اے میرے بندو! جو ایمان لائے اپنے سے ڈرو، جنہوں نے بھلائی کی ان کے لیے اس دنیا میں بھلائی ہے اور اللہ کی زمین وسیع ہے صابروں ہی کو ان کا ثواب بھرپور دیا جائے گا بے گنتی'),
(4069, 39, 11, 'تم فرماؤ مجھے حکم ہے کہ اللہ کو پوجوں نرا اس کا بندہ ہوکر،'),
(4070, 39, 12, 'اور مجھے حکم ہے کہ میں سب سے پہلے گردن رکھوں'),
(4071, 39, 13, 'تم فرماؤ بالفرض اگر مجھ سے نافرمانی ہوجائے تو مجھے اپنے رب سے ایک بڑے دن کے عذاب کا ڈر ہے'),
(4072, 39, 14, 'تم فرماؤ میں اللہ ہی کو پوجتا ہوں نرا اس کا بندہ ہوکر،'),
(4073, 39, 15, 'تو تم اس کے سوا جسے چاہو پوجو تم فرماؤ پوری ہار انہیں جو اپنی جان اور اپنے گھر والے قیامت کے دن ہار بیٹھے ہاں ہاں یہی کھلی ہار ہے،'),
(4074, 39, 16, 'ان کے اوپر آگ کے پہاڑ ہیں اور ان کے نیچے پہاڑ اس سے اللہ ڈراتا ہے اپنے بندوں اے میرے بندو! تم مجھ سے ڈرو'),
(4075, 39, 17, 'اور وہ جو بتوں کی پوجا سے بچے اور اللہ کی طرف رجوع ہوئے انہیں کے لیے خوشخبری ہے تو خوشی سناؤ میرے ان بندوں کو،'),
(4076, 39, 18, 'جو کان لگا کر بات سنیں پھر اس کے بہتر پر چلیں یہ ہیں جن کو اللہ نے ہدایت فرمائی اور یہ ہیں جن کو عقل ہیں'),
(4077, 39, 19, 'تو کیا وہ جس پر عذاب کی بات ثابت ہوچکی نجات والوں کے برابر ہوجائے گا تو کیا تم ہدایت دے کر آگ کے مستحق کو بچالو گے'),
(4078, 39, 20, 'لیکن جو اپنے رب سے ڈرے ان کے لیے بالا خانے ہیں ان پر بالا خانے بنے ان کے نیچے نہریں بہیں، اللہ کا وعدہ، اللہ وعدہ خلاف نہیں کرتا،'),
(4079, 39, 21, 'کیا تو نے نہ دیکھا کہ اللہ نے آسمان سے پانی اتارا پھر اس سے زمین میں چشمے بنائے پھر اس سے کھیتی نکالتا ہے کئی رنگت کی پھر سوکھ جاتی ہے تو تُو دیکھے کہ وہ پیلی پڑ گئی پھر اسے ریزہ ریزہ کردیتا ہے، بیشک اس میں دھیان کی بات ہے عقل مندوں کو'),
(4080, 39, 22, 'تو کیا وہ جس کا سینہ اللہ نے اسلام کے لیے کھول دیا تو وہ اپنے رب کی طرف سے نور پر ہے اس جیسا ہوجائے گا جو سنگدل ہے تو خرابی ہے ان کی جن کے دل یادِ خدا کی طرف سے سخت ہوگئے ہیں وہ کھلی گمراہی میں ہیں،'),
(4081, 39, 23, 'اللہ نے اتاری سب سے اچھی کتاب کہ اول سے آخر تک ایک سی ہے دوہرے بیان والی اس سے بال کھڑے ہوتے ہیں ان کے بدن پر جو اپنے رب سے ڈرتے ہیں، پھر ان کی کھالیں اور دل نرم پڑتے ہیں یادِ خدا کی طرف رغبت میں یہ اللہ کی ہدایت ہے راہ دکھائے اس سے جسے چاہے، اور جسے اللہ گمراہ کرے اسے کوئی راہ دکھانے والا نہیں،'),
(4082, 39, 24, 'تو کیا وہ جو قیامت کے دن برے عذاب کی ڈھال نہ پائے گا اپنے چہرے کے سوا نجات والے کی طرح ہوجائے گا اور ظالموں سے فرمایا جائے گا اپنا کمایا چکھو'),
(4083, 39, 25, 'ان سے اگلوں نے جھٹلایا تو انہیں عذاب آیا جہاں سے انہیں خبر نہ تھی'),
(4084, 39, 26, 'اور اللہ نے انہیں دنیا کی زندگی میں رسوائی کا مز ہ چکھایا اور بیشک آخرت کا عذاب سب سے بڑا، کیا اچھا تھا اگر وہ جانتے'),
(4085, 39, 27, 'اور بیشک ہم نے لوگوں کے لیے اس قرآن میں ہر قسم کی کہاوت بیان فرمائی کہ کسی طرح انہیں دھیان ہو'),
(4086, 39, 28, 'عربی زبان کا قرآن جس میں اصلاً کجی نہیں کہ کہیں وہ ڈریں'),
(4087, 39, 29, 'اللہ ایک مثال بیان فرماتا ہے ایک غلام میں کئی بدخو آقا شریک اور ایک نرے ایک مولیٰ کا، کیا ان دونوں کا حال ایک سا ہے سب خوبیاں اللہ کو بلکہ ان کے اکثر نہیں جانتے'),
(4088, 39, 30, 'بیشک تمہیں انتقال فرمانا ہے اور ان کو بھی مرنا ہے'),
(4089, 39, 31, 'پھر تم قیامت کے دن اپنے رب کے پاس جھگڑو گے'),
(4090, 39, 32, 'تو اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے اور حق کو جھٹلائے جب اس کے پاس آئے، کیا جہنم میں کافروں کا ٹھکانہ نہیں،'),
(4091, 39, 33, 'اور وہ جو یہ سچ لے کر تشریف لائے اور وہ جنہوں نے ان کی تصدیق کی'),
(4092, 39, 34, 'یہی ڈر والے ہیں، ان کے لےیے ہے، جو وہ چاہیں اپنے رب کے پاس، نیکوں کا یہی صلہ ہے،'),
(4093, 39, 35, 'تاکہ اللہ ان سے اتار دے برے سے برا کام جو انہوں نے کیا اور انہیں ان کے ثواب کا صلہ دے اچھے سے اچھے کام پر جو وہ کرتے تھے،'),
(4094, 39, 36, 'کیا اللہ اپنے بندے کو کافی نہیں اور تمہیں ڈراتے ہیں اس کے سوا اوروں سے اور جسے اللہ گمراہ کرے اس کی کوئی ہدایت کرنے والا نہیں،'),
(4095, 39, 37, 'اور جسے اللہ ہدایت دے اسے کوئی بہکانے والا نہیں، کیا اللہ عزت والا بدلہ لینے والا نہیں'),
(4096, 39, 38, 'اور اگر تم ان سے پوچھو آسمان اور زمین کس نے بنائے تو ضرور کہیں گے اللہ نے تم فرماؤ بھلا بتاؤ تو وہ جنہیں تم اللہ کے سوا پوجتے ہو اگر اللہ مجھے کوئی تکلیف پہنچانا چاہے تو کیا وہ اس کی بھیجی تکلیف ٹال دیں گے یا وہ مجھ پر مہر (رحم) فرمانا چاہے تو کیا وہ اس کی مہر کو روک رکھیں گے تم فرماؤ اللہ مجھے بس ہے بھروسے والے اس پر بھروسہ کریں،'),
(4097, 39, 39, 'تم فرماؤ، اے میری قوم! اپنی جگہ کام کیے جاؤ میں اپنا کام کرتا ہوں تو آگے جان جاؤ گے،'),
(4098, 39, 40, 'کس پر آتا ہے وہ عذاب کہ اسے رسوا کرے گا اور کس پر اترتا ہے عذاب کہ رہ پڑے گا'),
(4099, 39, 41, 'بیشک ہم نے تم پر یہ کتاب لوگوں کی ہدایت کو حق کے ساتھ اتاری تو جس نے راہ پائی تو اپنے بھلے کو اور جو بہکا وہ اپنے ہی برے کو بہکا اور تم کچھ ان کے ذمہ دار نہیں'),
(4100, 39, 42, 'اللہ جانوں کو وفات دیتا ہے ان کی موت کے وقت اور جو نہ مریں انہیں ان کے سوتے میں پھر جس پر موت کا حکم فرمادیا اسے روک رکھتا ہے اور دوسری ایک میعاد مقرر تک چھوڑ دیتا ہے بیشک اس میں ضرور نشانیاں ہیں سوچنے والوں کے لیے'),
(4101, 39, 43, 'کیا انہوں نے اللہ کے مقابل کچھ سفارشی بنا رکھے ہیں تم فرماؤ کیا اگرچہ وہ کسی چیز کے مالک نہ ہوں اور نہ عقل رکھیں،'),
(4102, 39, 44, 'تم فرماؤ شفاعت تو سب اللہ کے ہاتھ میں ہے اسی کے لیے ہے آسمانوں اور زمین کی بادشاہی، پھر تمہیں اسی کی طرف پلٹنا ہے'),
(4103, 39, 45, 'اور جب ایک اللہ کا ذکر کیا جاتا ہے دل سمٹ جاتے ہیں ان کے جو آخرت پر ایمان نہیں لاتے اور جب اس کے سوا اوروں کا ذکر ہوتا ہے جبھی وہ خوشیاں مناتے ہیں،'),
(4104, 39, 46, 'تم عرض کرو اے اللہ! آسمانوں اور زمین کے پیدا کرنے والے نہاں اور عیاں کے جاننے والے تو اپنے بندوں میں فیصلہ فرمائے گا جس میں وہ اختلاف رکھتے تھے'),
(4105, 39, 47, 'اور اگر ظالموں کے لیے ہوتا جو کچھ زمین میں ہے سب اور اس کے ساتھ اس جیسا تو یہ سب چھڑائی (چھڑانے) میں دیتے روز ِ قیامت کے بڑے عذاب سے اور انہیں اللہ کی طرف سے وہ بات ظاہر ہوئی جو ان کے خیال میں نہ تھی'),
(4106, 39, 48, 'اور ان پر اپنی کمائی ہوئی برائیاں کھل گئیں اور ان پر آپڑا وہ جس کی ہنسی بناتے تھے'),
(4107, 39, 49, 'پھر جب آدمی کو کوئی تکلیف پہنچتی ہے تو ہمیں بلاتا ہے پھر جب اسے ہم اپنے پاس سے کوئی نعمت عطا فرمائیں کہتا ہے یہ تو مجھے ایک علم کی بدولت ملی ہے بلکہ وہ تو آزمائش ہے مگر ان میں بہتوں کو علم نہیں'),
(4108, 39, 50, 'ان سے اگلے بھی ایسے ہی کہہ چکے تو ان کا کمایا ان کے کچھ کام نہ آیا،'),
(4109, 39, 51, 'تو ان پر پڑ گئیں ان کی کمائیوں کی برائیاں اور وہ جو ان میں ظالم عنقریب ان پر پڑیں گی ان کی کمائیوں کی برائیاں اور وہ قابو سے نہیں نکل سکتے'),
(4110, 39, 52, 'کیا انہیں معلوم نہیں کہ اللہ روزی کشادہ کرتا ہے جس کے لیے چاہے اور تنگ فرماتا ہے، بیشک اس میں ضرور نشانیاں ہیں ایمان والوں کے لیے،'),
(4111, 39, 53, 'تم فرماؤ اے میرے وہ بندو! جنہوں نے اپنی جانوں پر زیادتی کی اللہ کی رحمت سے ناامید نہ ہو، بیشک اللہ سب گناہ بخش دیتا ہے بیشک وہی بخشنے والا مہربان ہے،'),
(4112, 39, 54, 'اور اپنے رب کی طرف رجوع لاؤ اور اس کے حضور گردن رکھو قبل اس کے کہ تم پر عذاب آئے پھر تمہاری مدد نہ ہو،'),
(4113, 39, 55, 'اور اس کی پیروی کرو جو اچھی سے اچھی تمہارے رب سے تمہاری طرف اتاری گئی قبل اس کے کہ عذاب تم پر اچانک آجائے اور تمہیں خبر نہ ہو'),
(4114, 39, 56, 'کہ کہیں کوئی جان یہ نہ کہے کہ ہائے افسوس! ان تقصیروں پر جو میں نے اللہ کے بارے میں کیں اور بیشک میں ہنسی بنایا کرتا تھا'),
(4115, 39, 57, 'یا کہے اگر اللہ مجھے راہ دکھاتا تو میں ڈر والوں میں ہوتا،'),
(4116, 39, 58, 'یا کہے جب عذاب دیکھے کسی طرح مجھے واپسی ملے کہ میں نیکیاں کروں'),
(4117, 39, 59, 'ہاں کیوں نہیں بیشک تیرے پاس میری آیتیں آئیں تو تُو نے انہیں جھٹلایا اور تکبر کیا اور تو کافر تھا'),
(4118, 39, 60, 'اور قیامت کے دن تم دیکھو گے انہیں جنہوں نے اللہ پر جھوٹ باندھا کہ ان کے منہ کالے ہیں کیا مغرور ٹھکانا جہنم میں نہیں'),
(4119, 39, 61, 'اور اللہ بچائے گا پرہیزگاروں کو ان کی نجات کی جگہ نہ انہیں عذاب چھوئے اور نہ انہیں غم ہو،'),
(4120, 39, 62, 'اللہ ہر چیز کا پیدا کرنے والا ہے، اور وہ ہرچیز کا مختار ہے،'),
(4121, 39, 63, 'اسی کے لیے ہیں آسمانوں اور زمین کی کنجیاں اور جنہوں نے اللہ کی آیتوں کا انکار کیا وہی نقصان میں ہیں،'),
(4122, 39, 64, 'تم فرماؤ تو کیا اللہ کے سوا دوسرے کے پوجنے کو مجھ سے کہتے ہو، اے جاہلو!'),
(4123, 39, 65, 'اور بیشک وحی کی گئی تمہاری طرف اور تم سے اگلوں کی طرف کہ اسے سننے والے اگر تو نے اللہ کا شریک کیا تو ضرور تیرا سب کیا دھرا اَکارت جائے گا اور ضرور تو ہار میں رہے گا،'),
(4124, 39, 66, 'بلکہ اللہ ہی کی بندگی کر اور شکر والوں سے ہو'),
(4125, 39, 67, 'اور انہوں نے اللہ کی قدر نہ کی جیسا کہ اس کا حق تھا اور وہ قیامت کے دن سب زمینوں کو سمیٹ دے گا اور اس کی قدرت سے سب آسمان لپیٹ دیے جائیں گے اور ان کے شرک سے پاک اور برتر ہے،'),
(4126, 39, 68, 'اور صُور پھونکا جائے گا تو بے ہوش ہوجائیں گے جتنے آسمانوں میں ہیں اور جتنے زمین میں مگر جسے اللہ چاہے پھر وہ دوبارہ پھونکا جائے گا جبھی وہ دیکھتے ہوئے کھڑے ہوجائیں گے'),
(4127, 39, 69, 'اور زمین جگمگا اٹھے گی اپنے رب کے نور سے اور رکھی جائے گی کتاب اور لائے جائیں گے انبیاء اور یہ نبی اور اس کی امت کے ان پر گواہ ہونگے اور لوگوں میں سچا فیصلہ فرمادیا جائے گا اور ان پر ظلم نہ ہوگا،'),
(4128, 39, 70, 'اور ہر جان کو اس کا کیا بھرپور دیا جائے گا اور اسے خوب معلوم جو وہ کرتے تھے'),
(4129, 39, 71, 'اور کافر جہنم کی طرف ہانکے جائیں گے گروہ گروہ یہاں تک کہ جب وہاں پہنچیں گے اس کے دروازے کھولے جائیں گے اور اس کے داروغہ ان سے کہیں گے کیا تمہارے پاس تمہیں میں سے وہ رسول نہ آئے تھے جو تم پر تمہارے رب کی آیتیں پڑھتے تھے اور تمہیں اس دن سے ملنے سے ڈراتے تھے، کہیں گے کیوں نہیں مگر عذاب کا قول کافروں پر ٹھیک اترا'),
(4130, 39, 72, 'فرمایا جائے گا جاؤ جہنم کے دروازوں میں اس میں ہمیشہ رہنے، تو کیا ہی برا ٹھکانا متکبروں کا،'),
(4131, 39, 73, 'اور جو اپنے رب سے ڈرتے تھے ان کی سواریاں گروہ گروہ جنت کی طرف چلائی جائیں گی، یہاں تک کہ جب وہاں پہنچیں گے اور اس کے دروازے کھلے ہوئے ہوں گے اور اس کے داروغہ ان سے کہیں گے سلام تم پر تم خوب رہے تو جنت میں جاؤ ہمیشہ رہنے،'),
(4132, 39, 74, 'اور وہ کہیں گے سب خوبیاں اللہ کو جس نے اپنا وعدہ ہم سے سچا کیا اور ہمیں اس زمین کا وارث کیا کہ ہم جنت میں رہیں جہاں چاہیں، تو کیا ہی اچھا ثواب کامیوں (اچھے کام کرنیوالوں) کا'),
(4133, 39, 75, 'اور تم فرشتوں کو دیکھو گے عرش کے آس پاس حلقہ کیے اپنے رب کی تعریف کے ساتھ ا س کی پاکی بولتے اور لوگوں میں سچا فیصلہ فرمادیا جائے گا اور کہا جائے گا کہ سب خوبیاں اللہ کو جو سارے جہاں کا رب');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حٰمٓ'),
(4135, 40, 2, 'یہ کتاب اتارنا ہے اللہ کی طرف سے جو عزت والا، علم والا،'),
(4136, 40, 3, 'گناہ بخشنے والا اور توبہ قبول کرنے والا سخت عذاب کرنے والا بڑے انعام والا اس کے سوا کوئی معبود نہیں، اسی کی طرف پھرنا ہے'),
(4137, 40, 4, 'اللہ کی آیتوں میں جھگڑا نہیں کرتے مگر کافر تو اے سننے والے تجھے دھوکا نہ دے ان کا شہروں میں اہل گہلے (اِتراتے) پھرنا'),
(4138, 40, 5, 'ان سے پہلے نوح کی قوم اور ان کے بعد کے گروہوں نے جھٹلایا، اور ہر امت نے یہ قصد کیا کہ اپنے رسول کو پکڑ لیں اور باطل کے ساتھ جھگڑے کہ اس سے حق کو ٹال دیں تو میں نے انہیں پکڑا، پھر کیسا ہوا میرا عذاب'),
(4139, 40, 6, 'اور یونہی تمہارے رب کی بات کافروں پر ثابت ہوچکی ہے کہ وہ دوزخی ہیں،'),
(4140, 40, 7, 'وہ جو عرش اٹھاتے ہیں اور جو اس کے گرد ہیں اپنے کی تعریف کے ساتھ اس کی پاکی بولتے اور اس پر ایمان لاتے اور مسلمانوں کی مغفرت مانگتے ہیں اے رب ہمارے تیرے رحمت و علم میں ہر چیز کی سمائی ہے تو انہیں بخش دے جنہوں نے توبہ کی اور تیری راہ پر چلے اور انہیں دوزخ کے عذاب سے بچالے،'),
(4141, 40, 8, 'اے ہمارے رب! اور انہیں بسنے کے باغوں میں داخل کر جن کا تو نے ان سے وعدہ فرمایا ہے اور ان کو جو نیک ہوں ان کے باپ دادا اور بیبیوں اور اولاد میں بیشک تو ہی عزت و حکمت والا ہے،'),
(4142, 40, 9, 'اور انہیں گناہوں کی شامت سے بچالے، اور جسے تو اس دن گناہوں کی شامت سے بچائے تو بیشک تو نے اس پر رحم فرمایا، اور یہی بڑی کامیابی ہے،'),
(4143, 40, 10, 'بیشک جنہوں نے کفر کیا ان کو ندا کی جائے گی کہ ضرور تم سے اللہ کی بیزاری اس سے بہت زیادہ ہے جیسے تم آج اپنی جان سے بیزار ہو جب کہ تم ایمان کی طرف بلائے جاتے تو تم کفر کرتے،'),
(4144, 40, 11, 'کہیں گے اے ہمارے رب تو نے ہمیں دوبارہ مردہ کیا اور دوبارہ زندہ کیا اب ہم اپنے گناہوں پر مُقِر ہوئے تو آگ سے نکلنے کی بھی کوئی راہ ہے'),
(4145, 40, 12, 'یہ اس پر ہوا کہ جب ایک اللہ پکارا جاتا تو تم کفر کرتے اور ان کا شریک ٹھہرایا جاتا تو تم مان لیتے تو حکم اللہ کے لیے ہے جو سب سے بلند بڑا،'),
(4146, 40, 13, 'وہی ہے کہ تمہیں اپنی نشانیاں دکھاتا ہے اور تمہارے لیے آسمان سے روزی اتارتا ہے اور نصیحت نہیں مانتا مگر جو رجوع لائے'),
(4147, 40, 14, 'تو اللہ کی بندگی کرو نرے اس کے بندے ہوکر پڑے برا مانیں کافر،'),
(4148, 40, 15, 'بلند درجے دینے والا عرش کامالک ایمان کی جان وحی ڈالتا ہے اپنے حکم سے اپنے بندوں میں جس پر چاہے کہ وہ ملنے کے دن سے ڈرائے'),
(4149, 40, 16, 'جس دن وہ بالکل ظاہر ہوجائیں گے اللہ پر ان کا کچھ حال چھپا نہ ہوگا آج کسی کی بادشاہی ہے ایک اللہ سب پر غا لب کی، ف۳۷)'),
(4150, 40, 17, 'آج ہر جان اپنے کےٴ کا بدلہ پاےٴ گی آج کسی پر زیادتی نہیں، بیشک اللہ جلد حساب لینے والا ہے،'),
(4151, 40, 18, 'اور انہیں ڈراؤ اس نزدیک آنے والی آفت کے دن سے جب دل گلوں کے پاس آجائیں گے غم میں بھرے، اور ظالموں کا نہ کوئی دوست نہ کوئی سفارشی جس کا کہا مانا جائے'),
(4152, 40, 19, 'اللہ جانتا ہے چوری چھپے کی نگاہ اور جو کچھ سینوں میں چھپا ہے'),
(4153, 40, 20, 'اور اللہ سچا فیصلہ فرماتا ہے، اور اس کے سوا جن کو پوجتے ہیں وہ کچھ فیصلہ نہیں کرتے بیشک اللہ ہی سنتا اور دیکھتا ہے'),
(4154, 40, 21, 'تو کیا انہوں نے زمین میں سفر نہ کیا کہ دیکھتے کیسا انجام ہوا ان سے اگلوں کا ان کی قوت اور زمین میں جو نشانیاں چھوڑ گئے ان سے زائد تو اللہ نے انہیں ان کے گناہوں پر پکڑا، اور اللہ سے ان کا کوئی بچانے والا نہ ہوا'),
(4155, 40, 22, 'یہ اس لیے کہ ان کے پاس ان کے رسول روشن نشانیاں لے کر آئے پھر وہ کفر کرتے تو اللہ نے انہیں پکڑا، بیشک اللہ زبردست عذاب والا ہے،'),
(4156, 40, 23, 'اور بیشک ہم نے موسیٰ کو اپنی نشانیوں اور روشن سند کے ساتھ بھیجا،'),
(4157, 40, 24, 'فرعون اور ہامان اور قارون کی طرف تو وہ بولے جادوگر ہے بڑا جھوٹا'),
(4158, 40, 25, 'پھر جب وہ ان پر ہمارے پاس سے حق لایا بولے جو اس پر ایمان لائے ان کے بیٹے قتل کرو اور عورتیں زندہ رکھو اور کافروں کا داؤ نہیں مگر بھٹکتا پھرتا'),
(4159, 40, 26, 'اور فرعون بولا مجھے چھوڑو میں موسیٰ کو قتل کروں اور وہ اپنے رب کو پکارے میں ڈرتا ہوں کہیں وہ تمہارا دین بدل دے یا زمین میں فساد چمکائے'),
(4160, 40, 27, 'اور موسیٰ نے کہا میں تمہارے اور اپنے رب کی پناہ لیتا ہوں ہر متکبر سے کہ حساب کے دن پر یقین نہیں لاتا'),
(4161, 40, 28, 'اور بولا فرعون والوں میں سے ایک مرد مسلمان کہ اپنے ایمان کو چھپاتا تھا کیا ایک مرد کو اس پر مارے ڈالتے ہو کہ وہ کہتا ہے کہ میرا رب اللہ ہے اور بیشک وہ روشن نشانیاں تمہارے پاس تمہارے رب کی طرف سے لائے اور اگر بالفرض وہ غلط کہتے ہیں تو ان کی غلط گوئی کا وبال ان پر، اور اگر وہ سچے ہیں تو تمہیں پہنچ جائے گا کچھ وہ جس کا تمہیں وعدہ دیتے ہیں بیشک اللہ راہ نہیں دیتا اسے جو حد سے بڑھنے والا بڑا جھوٹا ہو'),
(4162, 40, 29, 'اے میری قوم! آج بادشاہی تمہاری ہے اس زمین میں غلبہ رکھتے ہو تو اللہ کے عذاب سے ہمیں کون بچالے گا اگر ہم پر آئے فرعون بولا میں تو تمہیں وہی سمجھاتا ہوں جو میری سوجھ ہے اور میں تمہیں وہی بتاتا ہوں جو بھلائی کی راہ ہے،'),
(4163, 40, 30, 'اور وہ ایمان والا بولا اے میری قوم! مجھے تم پر اگلے گروہوں کے دن کا سا خوف ہے'),
(4164, 40, 31, 'جیسا دستور گزرا نوح کی قوم اور عاد اور ثمود اور ان کے بعد اوروں کا اور اللہ بندوں پر ظلم نہیں چاہتا'),
(4165, 40, 32, 'اور اے میری قوم میں تم پر اس دن سے ڈراتا ہوں جس دن پکار مچے گی'),
(4166, 40, 33, 'جس دن پیٹھ دے کر بھاگو گے اللہ سے تمہیں کوئی بچانے والا نہیں، اور جسے اللہ گمراہ کرے اس کا کوئی راہ دکھانے والا نہیں،'),
(4167, 40, 34, 'اور بیشک اس سے پہلے تمہارے پاس یوسف روشن نشانیاں لے کر آئے تو تم ان کے لائے ہوئے سے شک ہی میں رہے، یہاں تک کہ جب انہوں نے انتقال فرمایا تم بولے ہرگز اب اللہ کوئی رسول نہ بھیجے گا اللہ یونہی گمراہ کرتا ہے اسے جو حد سے بڑھنے والا شک لانے والا ہے'),
(4168, 40, 35, 'وہ جو اللہ کی آیتوں میں جھگڑا کرتے ہیں بغیر کسی سند کے، کہ انہیں ملی ہو، کس قدر سخت بیزاری کی بات ہے اللہ کے نزدیک اور ایمان لانے والوں کے نزدیک، اللہ یوں ہی مہر کردیتا ہے متکبر سرکش کے سارے دل پر'),
(4169, 40, 36, 'اور فرعون بولا اے ہامان! میرے لیے اونچا محل بنا شاید میں پہنچ جاؤں راستوں تک،'),
(4170, 40, 37, 'کا ہے کے راستے آسمان کے تو موسیٰ کے خدا کو جھانک کر دیکھوں اور بیشک میرے گمان میں تو وہ جھوٹا ہے اور یونہی فرعون کی نگاہ میں اس کا برا کام بھلا کر دکھا گیا اور وہ راستے میں روکا گیا، اور فرعون کا داؤ ہلاک ہونے ہی کو تھا،'),
(4171, 40, 38, 'اور وہ ایمان والا بولا، اے میری قوم! میرے پیچھے چلو میں تمہیں بھلائی کی راہ بتاؤں،'),
(4172, 40, 39, 'اے میری قوم! یہ دنیا کا جینا تو کچھ برتنا ہی ہے اور بیشک وه پچھلا ہمیشہ رہنے کا گھر ہے،'),
(4173, 40, 40, 'جو برُا کام کرے تو اسے بدلہ نہ ملے گا مگر اتنا ہی اور جو اچھا کام کرے مرد خواه عورت اور جو مسلمان تو وہ جنت میں داخل کیے جائیں گے وہاں بے گنتی رزق پائیں گے'),
(4174, 40, 41, 'اور اے میری قوم مجھے کیا ہوا میں تمہیں بلاتا ہوں نجات کی طرف اور تم مجھے بلاتے ہو دوزخ کی طرف'),
(4175, 40, 42, 'مجھے اس طرف بلاتے ہو کہ اللہ کا انکا کروں اور ایسے کو اس کا شریک کروں جو میرے علم میں نہیں، اور میں تمہیں اس عزت والے بہت بخشنے والے کی طرف بلاتا ہوں،'),
(4176, 40, 43, 'آپ ہی ثابت ہوا کہ جس کی طرف مجھے بلاتے ہو اسے بلانا کہیں کام کا نہیں دنیا میں نہ آخرت میں اور یہ ہمارا پھرنا اللہ کی طرف ہے اور یہ کہ حد سے گزرنے والے ہی دوزخی ہیں،'),
(4177, 40, 44, 'تو جلد وہ وقت آتا ہے کہ جو میں تم سے کہہ رہا ہوں، اسے یاد کرو گے اور میں اپنے کام اللہ کو سونپتا ہوں، بیشک اللہ بندوں کو دیکھتا ہے'),
(4178, 40, 45, 'تو اللہ نے اسے بچالیا ان کے مکر کی برائیوں سے اور فرعون والوں کو برے عذاب نے آ گھیرا،'),
(4179, 40, 46, 'آ گ جس پر صبح و شام پیش کیے جاتے ہیں اور جس دن قیامت قائم ہوگی، حکم ہوگا فرعون والوں کو سخت تر عذاب میں داخل کرو،'),
(4180, 40, 47, 'اور جب وہ آگ میں باہم جھگڑیں گے تو کمزور ان سے کہیں گے جو بڑے بنتے تھے ہم تمہارے تابع تھے تو کیا تم ہم سے آگ کا کوئی حصہ گھٹا لوگے،'),
(4181, 40, 48, 'وہ تکبر والے بولے ہم سب آگ میں ہیں بیشک اللہ بندوں میں فیصلہ فرماچکا'),
(4182, 40, 49, 'اور جو آگ میں ہیں اس کے داروغوں سے بولے اپنے رب سے دعا کرو ہم پر عذاب کا ایک دن ہلکا کردے،'),
(4183, 40, 50, 'انہوں نے کہا کیا تمہارے پاس تمہارے رسول نشانیاں نہ لاتے تھے بولے کیوں نہیں بولے تو تمہیں دعا کرو اور کافروں کی دعا نہیں، مگر بھٹکتے پھرنے کو،'),
(4184, 40, 51, 'بیشک ضرور ہم اپنے رسولوں کی مدد کریں گے اور ایمان والوں کی دنیا کی زندگی میں اور جس دن گواہ کھڑے ہوں گے'),
(4185, 40, 52, 'جس دن ظالموں کو ان کے بہانے کچھ کام نہ دیں گے اور ان کے لیے لعنت ہے اور ان کے لیے بُرا گھر'),
(4186, 40, 53, 'اور بیشک ہم نے موسیٰ کو رہنمائی عطا فرمائی اور بنی اسرائیل کو کتاب کا وارث کیا'),
(4187, 40, 54, 'عقلمندوں کی ہدایت اور نصیحت کو تو اے محبوب،'),
(4188, 40, 55, 'تم صبر کرو بیشک اللہ کا وعدہ سچا ہے اور اپنوں کے گناہوں کی معافی چاہو اور اپنے رب کی تعریف کرتے ہوئے صبح اور شام اس کی پاکی بولو'),
(4189, 40, 56, 'وہ جو اللہ کی آیتوں میں جھگڑا کرتے ہیں بغیر کسی سند کے جو انہیں ملی ہو ان کے دلوں میں نہیں مگر ایک بڑائی کی ہوس جسے نہ پہنچیں گے تو تم اللہ کی پناہ مانگو بیشک وہی سنتا دیکھتا ہے،'),
(4190, 40, 57, 'بیشک آسمانوں اور زمین کی پیدائش آدمیوں کی پیدائش سے بہت بڑی لیکن بہت لوگ نہیں جانتے'),
(4191, 40, 58, 'اور اندھا اور انکھیارا برابر نہیں اور نہ وہ جو ایمان لائے اور اچھے کام کیے اور بدکار کتنا کم دھیان کرتے ہو،'),
(4192, 40, 59, 'بیشک قیامت ضرور آنے والی ہے اس میں کچھ شک نہیں لیکن بہت لوگ ایمان نہیں لاتے'),
(4193, 40, 60, 'اور تمہارے رب نے فرمایا مجھ سے دعا کرو میں قبول کروں گا بیشک وہ جو میری عبادت سے اونچے کھینچتے (تکبر کرتے) ہیں عنقریب جہنم میں جائیں گے ذلیل ہوکر،'),
(4194, 40, 61, 'اللہ ہے جس نے تمہارے لیے رات بنائی کہ اس میں آرام پاؤ اور دن بنایا آنکھیں کھولتا بیشک اللہ لوگوں پر فضل والا ہے لیکن بہت آدمی شکر نہیں کرتے،'),
(4195, 40, 62, 'وہ ہے اللہ تمہارا رب ہر چیز کا بنانے والا اس کے سوا کسی کی بندگی نہیں تو کہاں اوندھے جاتے ہو'),
(4196, 40, 63, 'یونہی اوندھے ہوتے ہیں وہ جو اللہ کی آیتوں کا انکار کرتے ہیں'),
(4197, 40, 64, 'اللہ ہے جس نے تمہارے لیے زمین ٹھہراؤ بنائی اور آسمان چھت اور تمہاری تصویر کی تو تمہاری صورتیں اچھی بنائیں اور تمہیں ستھری چیزیں روزی دیں یہ ہے اللہ تمہارا رب، تو بڑی برکت والا ہے اللہ رب سارے جہان کا،'),
(4198, 40, 65, 'وہی زندہ ہے اس کے سوا کسی کی بندگی نہیں تو اسے پوجو نرے اسی کے بندے ہوکر، سب خوبیاں اللہ کو جو سارے جہاں کا رب،'),
(4199, 40, 66, 'تم فرماؤ میں منع کیا گیا ہوں کہ انہیں پوجوں جنہیں تم اللہ کے سوا پوجتے ہو جبکہ میرے پاس روشن دلیلیں میرے رب کی طرف سے آئیں اور مجھے حکم ہوا ہے کہ رب العالمین کے حضور گردن رکھوں،'),
(4200, 40, 67, 'وہی ہے جس نے تمہیں مٹی سے بنایا پھر پانی کی بوند سے پھر خون کی پھٹک سے پھرتمہیں نکالتا ہے بچہ پھرتمہیں باقی رکھتا ہے کہ اپنی جوانی کو پہنچو پھر اس لیے کہ بوڑھے ہو اور تم میں کوئی پہلے ہی اٹھالیا جاتا ہے اور اس لیے کہ تم ایک مقرر وعدہ تک پہنچو اور اس لیے کہ سمجھو'),
(4201, 40, 68, 'وہی ہے کہ جِلاتا ہے اور مارتا ہے پھر جب کوئی حکم فرماتا ہے تو اس سے یہی کہتا ہے کہ ہو جا جبھی وہ ہوجاتا ہے'),
(4202, 40, 69, 'کیا تم نے انہیں نہ دیکھا جو اللہ کی آیتوں میں جھگڑے ہیں کہاں پھیرے جاتے ہیں'),
(4203, 40, 70, 'وہ جنہوں نے جھٹلائی کتاب اور جو ہم نے اپنے رسولوں کے ساتھ بھیجا، ف۱۵۰) وہ عنقریب جان جائیں گے'),
(4204, 40, 71, 'جب ان کی گردنوں میں طوق ہوں گے اور زنجیریں گھسیٹے جائیں گے،'),
(4205, 40, 72, 'کھولتے پانی میں، پھر آگ میں دہکائے جائیں گے'),
(4206, 40, 73, 'پھر ان سے فرمایا جائے گا کہاں گئے وہ جو تم شریک بناتے تھے'),
(4207, 40, 74, 'اللہ کے مقابل، کہیں گے وہ تو ہم سے گم گئے بلکہ ہم پہلے کچھ پوجتے ہی نہ تھے اللہ یونہی گمراہ کرتا ہے کافروں کو،'),
(4208, 40, 75, 'یہ اس کا بدلہ ہے جو تم زمین میں باطل پر خوش ہوتے تھے اور اس کا بدلہ ہے جو تم اتراتے تھے،'),
(4209, 40, 76, 'جاؤ جہنم کے دروازوں میں اس میں ہمیشہ رہنے، تو کیا ہی برا ٹھکانا مغروروں کا'),
(4210, 40, 77, 'تو تم صبر کرو بیشک اللہ کا وعدہ سچا ہے، تو اگر ہم تمہیں دکھادیں کچھ وہ چیز جس کا انہیں وعدہ دیا جاتا ہے یا تمہیں پہلے ہی وفات دیں بہرحال انہیں ہماری ہی طرف پھرنا'),
(4211, 40, 78, 'اور بیشک ہم نے تم سے پہلے کتنے رسول بھیجے کہ جن میں کسی کا احوال تم سے بیان فرمایا اور کسی کا احوال نہ بیان فرمایا اور کسی رسول کو نہیں پہنچتا کہ کوئی نشانی لے آئے بے حکم خدا کے، پھر جب اللہ کا حکم آئے گا سچا فیصلہ فرمادیا جائے گا اور باطل والوں کا وہاں خسارہ،'),
(4212, 40, 79, 'اللہ ہے جس نے تمہارے لیے چوپائے بنائے کہ کسی پر سوار ہو اور کسی کا گوشت کھاؤ،'),
(4213, 40, 80, 'اور تمہارے لیے ان میں کتنے ہی فائدے ہیں اور اس لیے کہ تم ان کی پیٹھ پر اپنے دل کی مرادوں کو پہنچو اور ان پر اور کشتیوں پر سوار ہوتے ہو،'),
(4214, 40, 81, 'اور وہ تمہیں اپنی نشانیاں دکھاتا ہے تو اللہ کی کونسی نشانی کا انکار کرو گے'),
(4215, 40, 82, 'کیا انہوں نے زمین میں سفر نہ کیا کہ دیکھتے ان سے اگلوں کا کیسا انجام ہوا، وہ ان سے بہت تھے اور ان کی قوت اور زمین میں نشانیاں ان سے زیادہ تو ان کے کیا کام آیا جو انہوں نے کمایا،'),
(4216, 40, 83, 'تو جب ان کے پاس ان کے رسول روشن دلیلیں لائے، تو وہ اسی پر خوش رہے جو ان کے پاس دنیا کا علم تھا اور انہیں پر الٹ پڑا جس کی ہنسی بناتے تھے'),
(4217, 40, 84, 'پھر جب انہوں نے ہمارا عذاب دیکھا بولے ہم ایک اللہ پر ایمان لائے اور جو اس کے شریک کرتے تھے ان کے منکر ہوئے'),
(4218, 40, 85, 'تو ان کے ایمان نے انہیں کام نہ دیا جب انہوں نے ہمارا عذاب دیکھ لیا، اللہ کا دستور جو اس کے بندوں میں گزر چکا اور وہاں کافر گھاٹے میں رہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حٰمٓ'),
(4220, 41, 2, 'یہ اتارا ہے بڑے رحم والے مہربان کا،'),
(4221, 41, 3, 'ایک کتاب ہے جس کی آیتیں مفصل فرمائی گئیں عربی قرآن عقل والوں کے لیے،'),
(4222, 41, 4, 'خوشخبری دیتا اور ڈر سناتا تو ان میں اکثر نے منہ پھیرا تو وہ سنتے ہی نہیں'),
(4223, 41, 5, 'اور بولے ہمارے دل غلاف میں ہیں اس بات سے جس کی طرف تم ہمیں بلاتے ہو اور ہمارے کانوں میں ٹینٹ (روئی) ہے اور ہمارے اور تمہارے درمیان روک ہے تو تم اپنا کام کرو ہم اپنا کام کرتے ہیں'),
(4224, 41, 6, 'تم فرماؤ آدمی ہونے میں تو میں تمہیں جیسا ہوں مجھے وحی ہوتی ہے کہ تمہارا معبود ایک ہی معبود ہے، تو اس کے حضور سیدھے رہو اور اس سے معافی مانگو اور خرابی ہے شرک والوں کو،'),
(4225, 41, 7, 'وہ جو زکوٰة نہیں دیتے اور وہ آخرت کے منکر ہیں'),
(4226, 41, 8, 'بیشک جو ایمان لائے اور اچھے کام کیے ان کے لیے بے انتہا ثواب ہے'),
(4227, 41, 9, 'تم فرماؤ کیا تم لوگ اس کا انکار رکھتے ہو جس نے دو دن میں زمین بنائی اور اس کے ہمسر ٹھہراتے رہو وہ ہے سارے جہان کا رب'),
(4228, 41, 10, 'اور اس میں اس کے اوپر سے لنگر ڈالے (بھاری بوجھ رکھے) اور اس میں برکت رکھی اور اس میں اس کے بسنے والوں کی روزیاں مقرر کیں یہ سب ملاکر چار دن میں ٹھیک جواب پوچھنے والوں کو،'),
(4229, 41, 11, 'پھر آسمان کی طرف قصد فرمایا اور وہ دھواں تھا تو اس سے اور زمین سے فرمایا کہ دونوں حاضر ہو خوشی سے چاہے ناخوشی سے، دونوں نے عرض کی کہ ہم رغبت کے ساتھ حاضر ہوئے،'),
(4230, 41, 12, 'تو انہیں پورے سات آسمان کردیا دو دن میں اور ہر آسمان میں اسی کے کام کے احکام بھیجے اور ہم نے نیچے کے آسمان کو چراغوں سے آراستہ کیا اور نگہبانی کے لیے یہ اس عزت والے علم والے کا ٹھہرایا ہوا ہے،'),
(4231, 41, 13, 'پھر اگر وہ منہ پھیریں تو تم فرماؤ کہ میں تمہیں ڈراتا ہوں ایک کڑ ک سے جیسی کڑ ک عاد اور ثمود پر آئی تھی'),
(4232, 41, 14, 'جب رسول ان کے آگے پیچھے پھرتے تھے کہ اللہ کے سوا کسی کو نہ پوجو، بولے ہمارا رب چاہتا تو فرشتے اتارتا تو جو کچھ تم لے کر بھیجے گئے ہم اسے نہیں مانتے'),
(4233, 41, 15, 'تو وہ جو عاد تھے انہیں نے زمین میں ناحق تکبر کیا اور بولے ہم سے زیادہ کس کا زور، اور کیا انہوں نے نہ جانا کہ اللہ جس نے انہیں بنایا ان سے زیادہ قوی ہے، اور ہماری آیتوں کا انکار کرتے تھے،'),
(4234, 41, 16, 'تو ہم نے ان پر ایک آندھی بھیجی سخت گرج کی ان کی شامت کے دنوں میں کہ ہم انہیں رسوائی کا عذاب چکھائیں دنیا کی زندگی میں اور بیشک آخرت کے عذاب میں سب سے بڑی رسوائی ہے اور ان کی مدد نہ ہوگی،'),
(4235, 41, 17, 'اور رہے ثمود انہیں ہم نے راہ دکھائی تو انہوں نے سوجھنے پر اندھے ہونے کو پسند کیا تو انہیں ذلت کے عذاب کی کڑ ک نے آ لیا سزا ان کے کیے کی'),
(4236, 41, 18, 'اور ہم نے انہیں بچالیا جو ایمان لائے اور ڈرتے تھے'),
(4237, 41, 19, 'اور جس دن اللہ کے دشمن آگ کی طرف ہانکے جائیں گے تو ان کے اگلوں کو روکیں گے،'),
(4238, 41, 20, 'یہاں تک کہ پچھلے آ ملیں یہاں تک کہ جب وہاں پہنچیں گے ان کے کان اور ان کی آنکھیں اور ان کے چمڑے سب ان پر ان کے کیے کی گواہی دیں گے'),
(4239, 41, 21, 'اور وه اپنی کھالوں سے کہیں گے تم نے ہم پر کیوں گواہی دی، وہ کہیں گی ہمیں اللہ نے بلوایا جس نے ہر چیز کو گویائی بخشی اور اس نے تمہیں پہلی بار بنایا اور اسی کی طرف تمہیں پھرنا ہے،'),
(4240, 41, 22, 'اور تم اس سے کہاں چھپ کر جاتے کہ تم پر گواہی دیں تمہارے کان اور تمہاری آنکھیں اور تمہاری کھالیں لیکن تم تو یہ سمجھے بیٹھے تھے کہ اللہ تمہارے بہت سے کام نہیں جانتا'),
(4241, 41, 23, 'اور یہ ہے تمہارا وہ گمان جو تم نے اپنے رب کے ساتھ کیا اور اس نے تمہیں ہلاک کردیا تو اب رہ گئے ہارے ہوؤں میں،'),
(4242, 41, 24, 'پھر اگر وہ صبر کریں تو آگ ان کا ٹھکانا ہے اور اگر وہ منانا چاہیں تو کوئی ان کا منانا نہ مانے،'),
(4243, 41, 25, 'اور ہم نے ان پر کچھ ساتھی تعینات کیے انہوں نے انہیں بھلا کردیا جو ان کے آگے ہے اور جو ان کے پیچھے اور ان پر بات پوری ہوئی ان گروہوں کے ساتھ جو ان سے پہلے گزر چکے جن اور آدمیوں کے، بیشک وہ زیاں کار تھے،'),
(4244, 41, 26, 'اور کافر بولے یہ قرآن نہ سنو اور اس میں بیہودہ غل کرو شاید یونہی تم غالب آؤ'),
(4245, 41, 27, 'تو بیشک ضرور ہم کافروں کو سخت عذاب چکھائیں گے اور بیشک ہم ان کے بُرے سے بُرے کام کا انہیں بدلہ دیں گے'),
(4246, 41, 28, 'یہ ہے اللہ کے دشمنوں کا بدلہ آ گ، اس میں انہیں ہمیشہ رہنا ہے، سزا اس کی کہ ہماری آیتوں کا انکار کرتے تھے،'),
(4247, 41, 29, 'اور کافر بولے اے ہمارے رب ہمیں دکھا وہ دونوں جن اور آدمی جنہوں نے ہمیں گمراہ کیا کہ ہم انہیں اپنے پاؤں تلے ڈالیں کہ وہ ہر نیچے سے نیچے رہیں'),
(4248, 41, 30, 'بیشک وہ جنہوں نے کہا ہمارا رب اللہ ہے پھر اس پر قائم رہے ان پر فرشتے اترتے ہیں کہ نہ ڈرو اور نہ غم کرو اور خوش ہو اس جنت پر جس کا تمہیں وعدہ دیا جاتا تھا'),
(4249, 41, 31, 'ہم تمہارے دوست ہیں دنیا کی زندگی میں اور آخرت میں اور تمہارے لیے ہے اس میں جو تمہارا جی چاہے اور تمہارے لیے اس میں جو مانگو،'),
(4250, 41, 32, 'مہمانی بخشنے والے مہربان کی طرف سے،'),
(4251, 41, 33, 'اور اس سے زیادہ کس کی بات اچھی جو اللہ کی طرف بلائے اور نیکی کرے اور کہے میں مسلمان ہوں'),
(4252, 41, 34, 'اور نیکی اور بدی برابر نہ ہوجائیں گی، اے سننے والے برائی کو بھلائی سے ٹال جبھی وہ کہ تجھ میں اور اس میں دشمنی تھی ایسا ہوجائے گا جیسا کہ گہرا دوست'),
(4253, 41, 35, 'اور یہ دولت نہیں ملتی مگر صابروں کو، اور اسے نہیں پاتا مگر بڑے نصیب والا،'),
(4254, 41, 36, 'اور اگر تجھے شیطان کا کوئی کونچا (تکلیف) پہنچے تو اللہ کی پناہ مانگ بیشک وہی سنتا جانتا ہے،'),
(4255, 41, 37, 'اور اس کی نشانیوں میں سے ہیں رات اور دن اور سورج اور چاند سجدہ نہ کرو سورج کو اور نہ چاند کو اور اللہ کو سجدہ کرو جس نے انہیں پیدا کیا اگر تم اس کے بندے ہو،'),
(4256, 41, 38, 'تو اگر یہ تکبر کریں تو وہ جو تمہارے رب کے پاس ہیں رات دن اس کی پاکی بولتے ہیں اور اکتاتے نہیں، السجدة ۔۱۱)'),
(4257, 41, 39, 'اور اس کی نشانیوں سے ہے کہ تو زمین کو دیکھے بے قدر پڑی پھر جب ہم نے اس پر پانی اتارا تر و تازہ ہوئی اور بڑھ چلی، بیشک جس نے اسے جِلایا ضرور مردے جِلائے گا، بیشک وہ سب کچھ کرسکتا ہے،'),
(4258, 41, 40, 'بیشک وہ جو ہماری آیتوں میں ٹیڑھے چلتے ہیں ہم سے چھپے نہیں تو کیا جو آ گ میں ڈالا جائے گا وہ بھلا، یا جو قیامت میں امان سے آئے گا جو جی میں آئے کرو بیشک وہ تمہارے کام دیکھ رہا ہے،'),
(4259, 41, 41, 'بیشک جو ذکر سے منکر ہوئے جب وہ ان کے پاس آیا ان کی خرابی کا کچھ حال نہ پوچھ، اور بیشک وہ عزت والی کتاب ہے'),
(4260, 41, 42, 'باطل کو اس کی طرف راہ نہیں نہ اس کے آگے سے نہ اس کے پیچھے سے اتارا ہوا ہے حکمت والے سب خوبیوں سراہے کا،'),
(4261, 41, 43, 'تم سے نہ فرمایا جائے مگر وہی جو تم سے اگلے رسولوں کو فرمایا، کہ بیشک تمہارا رب بخشش والا اور دردناک عذاب والا ہے'),
(4262, 41, 44, 'اور اگر ہم اسے عجمی زبان کا قرآن کرتے تو ضرور کہتے کہ اس کی آیتیں کیوں نہ کھولی گئیں کیا کتاب عجمی اور نبی عربی تم فرماؤ وہ ایمان والوں کے لیے ہدایت اور شفا ہے اور وہ جو ایمان نہیں لاتے ان کے کانوں میں ٹینٹ (روئی) ہے اور وہ ان پر اندھا پن ہے گویا وہ دور جگہ سے پکارے جاتے ہیں'),
(4263, 41, 45, 'اور بیشک ہم نے موسیٰ کو کتاب عطا فرمائی تو اس میں اختلاف کیا گیا اور اگر ایک بات تمہارے رب کی طرف سے گزر نہ چکی ہوتی تو جبھی ان کا فیصلہ ہوجاتا اور بیشک وہ ضرور اس کی طرف سے ایک دھوکا ڈالنے والے شک میں ہیں،'),
(4264, 41, 46, 'جو نیکی کرے وہ اپنے بھلے کو اور جو برائی کرے اپنے بُرے کو، اور تمہارا رب بندوں پر ظلم نہیں کرتا،'),
(4265, 41, 47, 'قیامت کے علم کا اسی پر حوالہ ہے اور کوئی پھل اپنے غلاف سے نہیں نکلتا اور نہ کسی مادہ کو پیٹ رہے اور نہ جنے مگر اس کے علم سے اور جس دن انہیں ندا فرمائے گا کہاں ہیں میرے شریک کہیں گے ہم تجھ سے کہہ چکے ہیں کہ ہم میں کوئی گواہ نہیں'),
(4266, 41, 48, 'اور گم گیا ان سے جسے پہلے پوجتے تھے اور سمجھ لیے کہ انہیں کہیں بھاگنے کی جگہ نہیں،'),
(4267, 41, 49, 'آدمی بھلائی مانگنے سے نہیں اُکتاتا اور کوئی برائی پہنچے تو ناامید آس ٹوٹا'),
(4268, 41, 50, 'اور اگر ہم اسے کچھ اپنی رحمت کا مزہ دیں اس تکلیف کے بعد جو اسے پہنچی تھی تو کہے گا یہ تو میری ہے اور میرے گمان میں قیامت قائم نہ ہوگی اور اگر میں رب کی طرف لوٹایا بھی گیا تو ضرور میرے لیے اس کے پاس بھی خوبی ہی ہے تو ضرور ہم بتادیں گے کافروں کو جو انہوں نے کیا اور ضرور انہیں گاڑھا عذاب چکھائیں گے'),
(4269, 41, 51, 'اورجب ہم آدمی پر احسان کرتے ہیں تو منہ پھیر لیتا ہے اور اپنی طرف دور ہٹ جاتا ہے اور جب اسے تکلیف پہنچتی ہے تو چوڑی دعا والا ہے'),
(4270, 41, 52, 'تم فرماؤ بھلا بتاؤ اگر یہ قرآن اللہکے پاس سے ہے پھر تم اس کے منکر ہوئے تو اس سے بڑھ کر گمراہ کون جو دور کی ضد میں ہے'),
(4271, 41, 53, 'ابھی ہم انہیں دکھائیں گے اپنی آیتیں دنیا بھر میں اور خود ان کے آپے میں یہاں تک کہ ان پر کھل جائے کہ بیشک وہ حق ہے کیا تمہارے رب کا ہر چیز پر گواہ ہونا کافی نہیں،'),
(4272, 41, 54, 'سنو انہیں ضرور اپنے رب سے ملنے میں شک ہے سنو! وہ ہر چیز کو محیط ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حٰمٓ'),
(4274, 42, 2, 'عٓسٓقٓ'),
(4275, 42, 3, 'یونہی وحی فرماتا ہے تمہاری طرف اور تم سے اگلوں کی طرف اللہ عزت و حکمت والا،'),
(4276, 42, 4, 'اسی کا ہے جو کچھ آسمان میں ہے اور جو کچھ زمین میں ہے، اور وہی بلندی و عظمت والا ہے،'),
(4277, 42, 5, 'قریب ہوتا ہے کہ آسمان اپنے اوپر سے شق ہوجائیں اور فرشتے اپنے رب کی تعریف کے ساتھ اس کی پاکی بولتے اور زمین والوں کے لیے معافی مانگتے ہیں سن لو بیشک اللہ ہی بخشنے والا مہربان ہے،'),
(4278, 42, 6, 'اور جنہوں نے اللہ کے سوا اور والی بنارکھے ہیں وہ اللہ کی نگاہ میں ہیں اور تم ان کے ذمہ دار نہیں،'),
(4279, 42, 7, 'اور یونہی ہم نے تمہاری طرف عربی قرآن وحی بھیجا کہ تم ڈراؤ سب شہروں کی اصل مکہ والوں کو اور جتنے اس کے گرد ہیں اور تم ڈراؤ اکٹھے ہونے کے دن سے جس میں کچھ شک نہیں ایک گروہ جنت میں ہے اور ایک گروہ دوزخ میں،'),
(4280, 42, 8, 'اور اللہ چاہتا تو ان سب کو ایک دین پر کردیتا لیکن اللہ اپنی رحمت میں لیتا ہے جسے چاہے اور ظالموں کا نہ کوئی دوست نہ مددگار'),
(4281, 42, 9, 'کیا اللہ کے سوا اور والی ٹھہرالیے ہیں تو اللہ ہی والی ہے اور وہ مُردے جِلائے گا، اور وہ سب کچھ کرسکتا ہے'),
(4282, 42, 10, 'تم جس بات میں اختلاف کرو تو اس کا فیصلہ اللہ کے سپرد ہے یہ ہے اللہ میرا رب میں نے اس پر بھروسہ کیا، اور میں اس کی طرف رجوع لاتا ہوں'),
(4283, 42, 11, 'آسمانوں اور زمین کا بنانے والا، تمہارے لیے تمہیں میں سے جوڑے بنائے اور نر و مادہ چوپائے، اس سے تمہاری نسل پھیلاتا ہے، اس جیسا کوئی نہیں، اور وہی سنتا دیکھتا ہے،'),
(4284, 42, 12, 'اسی کے لیے ہیں آسمانوں اور زمین کی کنجیاں روزی وسیع کرتا ہے جس کے لیے چاہے اور تنگ فرماتا ہے بیشک وہ سب کچھ جانتا ہے،'),
(4285, 42, 13, 'تمہارے لیے دین کی وہ راہ ڈالی جس کا حکم اس نے نوح کو دیا اور جو ہم نے تمہاری طرف وحی کی اور جس کا حکم ہم نے ابراہیم اور موسیٰ اور عیسیٰ کو دیا کہ دین ٹھیک رکھو اور اس میں پھوٹ نہ ڈالو مشرکوں پر بہت ہی گراں ہے وہ جس کی طرف تم انہیں بلاتے ہو، اور اللہ اپنے قریب کے لیے چن لیتا ہے جسے چاہے اور اپنی طرف راہ دیتا ہے اسے جو رجوع لائے'),
(4286, 42, 14, 'اور انہوں نے پھوٹ نہ ڈالی مگر بعد اس کے کہ انہیں علم آچکا تھا آپس کے حسد سے اور اگر تمہارے رب کی ایک بات گزر نہ چکی ہوتی ایک مقرر میعاد تک تو کب کا ان میں فیصلہ کردیا ہوتا اور بیشک وہ جو ان کے بعد کتاب کے وارث ہوئے وہ اس سے ایک دھوکا ڈالنے والے شک میں ہیں'),
(4287, 42, 15, 'تو اسی لیے بلاؤ اور ثابت قدم رہو جیسا تمہیں حکم ہوا ہے اور ان کی خواہشوں پر نہ چلو اور کہو کہ میں ایمان لایا اس پر جو کوئی کتاب اللہ نے اتاری اور مجھے حکم ہے کہ میں تم میں انصاف کروں اللہ ہمارا اور تمہارا سب کا رب ہے ہمارے لیے ہمارا عمل اور تمہارے لیے تمہارا کیا کوئی حجت نہیں ہم میں اور تم میں اللہ ہم سب کو جمع کرے گا اور اسی کی طرف پھرنا ہے،'),
(4288, 42, 16, 'اور وہ جو اللہ کے بارے میں جھگڑتے ہیں بعد اس کے کہ مسلمان اس کی دعوت قبول کرچکے ہیں ان کی دلیل محض بے ثبات ہے ان کے رب کے پاس اور ان پر غضب ہے اور ان کے لیے سخت عذاب ہے'),
(4289, 42, 17, 'اللہ ہے جس نے حق کے ساتھ کتاب اتاری اور انصاف کی ترازو اور تم کیا جانو شاید قیامت قریب ہی ہو'),
(4290, 42, 18, 'اس کی جلدی مچارہے ہیں وہ جو اس پر ایمان نہیں رکھتے اور جنہیں اس پر ایمان ہے وہ اس سے ڈر رہے ہیں اورجانتے ہیں کہ بیشک وہ حق ہے، سنتے ہو بیشک جو قیامت میں شک کرتے ہیں ضرور دُور کی گمراہی میں ہیں،'),
(4291, 42, 19, 'اللہ اپنے بندوں پر لطف فرماتا ہے جسے چاہے روزی دیتا ہے اور وہی قوت و عزت والا ہے،'),
(4292, 42, 20, 'جو آخرت کی کھیتی چاہے ہم اس کے لیے اس کی کھیتی بڑھائیں اور جو دنیا کی کھیتی چاہے ہم اسے اس میں سے کچھ دیں گے اور آخرت میں اس کا کچھ حصہ نہیں'),
(4293, 42, 21, 'یا ان کے لیے کچھ شریک ہیں جنہوں نے ان کے لیے وہ دین نکال دیا ہے کہ اللہ نے اس کی اجازت نہ دی اور اگر ایک فیصلہ کا وعدہ نہ ہوتا تو یہیں ان میں فیصلہ کردیا جاتا اور بیشک ظالموں کے لیے دردناک عذاب ہے'),
(4294, 42, 22, 'تم ظالموں کو دیکھو گے کہ اپنی کمائیوں سے سہمے ہوئے ہوں گے اور وہ ان پر پڑ کر رہیں گی اور جو ایمان لائے اور اچھے کام کیے وہ جنت کی پھلواریوں میں ہیں، ان کے لیے ان کے رب کے پاس ہے جو چاہیں، یہی بڑا فضل ہے،'),
(4295, 42, 23, 'یہ ہے وہ جس کی خوشخبری دیتا ہے اللہ اپنے بندوں کو جو ایمان لائے اور اچھے کام کیے، تم فرماؤ میں اس پر تم سے کچھ اجرت نہیں مانگتا مگر قرابت کی محبت اور جو نیک کام کرے ہم اس کے لیے اس میں اور خوبی بڑھائیں، بیشک اللہ بخشنے والا قدر فرمانے والا ہے،'),
(4296, 42, 24, 'یا یہ کہتے ہیں کہ انہوں نے اللہ پر جھوٹ باندھ لیا اور اللہ چاہے تو تمہارے اوپر اپنی رحمت و حفاظت کی مہر فرمادے اور مٹا تا ہے باطل کو اور حق کو ثابت فرماتا ہے اپنی باتوں سے بیشک وہ دلوں کی باتیں جانتا ہے،'),
(4297, 42, 25, 'اور وہی ہے جو اپنے بندوں کی توبہ قبول فرماتا اور گناہوں سے درگزر فرماتا ہے اور جانتا ہے جو کچھ تم کرتے ہو،'),
(4298, 42, 26, 'اور دعا قبول فرماتا ہے ان کی جو ایمان لائے اور اچھے کام کیے اور انہیں اپنے فضل سے اور انعام دیتا ہے، اور کافروں کے لیے سخت عذاب ہے،'),
(4299, 42, 27, 'اور اگر اللہ اپنے سب بندوں کا رزق وسیع کردیتا تو ضرور زمین میں فساد پھیلاتے لیکن وہ اندازہ سے اتارتا ہے جتنا چاہے، بیشک وہ بندوں سے خبردار ہے انہیں دیکھتا ہے،'),
(4300, 42, 28, 'اور وہی ہے کہ مینہ اتارتا ہے ان کے نا امید ہونے پر اور اپنی رحمت پھیلاتا ہے اور وہی کام بنانے والا ہے سب خوبیوں سراہا،'),
(4301, 42, 29, 'اور اس کی نشانیوں سے ہے آسمانوں اور زمین کی پیدائش اور جو چلنے والے ان میں پھیلائے، اور وہ ان کے اکٹھا کرنے پر جب چاہے قادر ہے،'),
(4302, 42, 30, 'اور تمہیں جو مصیبت پہنچی وہ اس کے سبب سے ہے جو تمہارے ہاتھوں نے کمایا اور بہت کچھ تو معاف فرمادیتا ہے،'),
(4303, 42, 31, 'اور تم زمین میں قابو سے نہیں نکل سکتے اور نہ اللہ کے مقابل تمہارا کوئی دوست نہ مددگار'),
(4304, 42, 32, 'اور اس کی نشانیوں سے ہیں دریا میں چلنے والیاں جیسے پہاڑیاں،'),
(4305, 42, 33, 'وہ چاہے تو ہوا تھما دے اس کی پیٹھ پر ٹھہری رہ جائیں بیشک اس میں ضرور نشانیاں ہیں ہر بڑے صابر شاکر کو'),
(4306, 42, 34, 'یا انہیں تباہ کردے لوگوں کے گناہوں کے سبب اور بہت معاف فرمادے'),
(4307, 42, 35, 'اور جان جائیں وہ جو ہماری آیتوں میں جھگڑتے ہیں، کہ انہیں کہیں بھا گنے کی جگہ نہیں،'),
(4308, 42, 36, 'تمہیں جو کچھ ملا ہے وہ جیتی دنیا میں برتنےکا ہے اور وہ جو اللہ کے پاس ہے بہتر ہے اور زیادہ باقی رہنے والا ان کے لیے جو ایمان لائے اور اپنے رب پر بھروسہ کرتے ہیں'),
(4309, 42, 37, 'اور وہ جو بڑے بڑے گناہوں اور بے حیائیوں سے بچتے ہیں اور جب غصہ آئے معاف کردیتے ہیں،'),
(4310, 42, 38, 'اور وہ جنہوں نے اپنے رب کا حکم مانا اور نماز قائم رکھی اور ان کا کام ان کے آپس کے مشورے سے ہے اور ہمارے دیے سے کچھ ہماری راہ میں خرچ کرتے ہیں،'),
(4311, 42, 39, 'اور وہ کہ جب انہیں بغاوت پہنچے بدلہ لیتے ہیں'),
(4312, 42, 40, 'اور برائی کا بدلہ اسی کی برابر برائی ہے تو جس نے معاف کیا اور کام سنوارا تو اس کا اجر اللہ پر ہے، بیشک وہ دوست نہیں رکھتا ظالموں کو'),
(4313, 42, 41, 'اور بے شک جس نے اپنی مظلو می پر بدلہ لیا ان پر کچھ مواخذہ کی راه نہیں،'),
(4314, 42, 42, 'مواخذہ تو انہیں پر ہے جو لوگوں پر ظلم کرتے ہیں اور زمین میں ناحق سرکشی پھیلاتے ہیں ان کے لیے دردناک عذاب ہے،'),
(4315, 42, 43, 'اور بیشک جس نے صبر کیا اور بخش دیا تو یہ ضرور ہمت کے کام ہیں،'),
(4316, 42, 44, 'اور جسے اللہ گمراہ کرے اس کا کوئی رفیق نہیں اللہ کے مقابل اور تم ظالموں کو دیکھو گے کہ جب عذاب دیکھیں گے کہیں گے کیا واپس جانے کا کوئی راستہ ہے'),
(4317, 42, 45, 'اور تم انہیں دیکھو گے کہ آگ پر پیش کیے جاتے ہیں ذلت سے دبے لچے چھپی نگاہوں دیکھتے ہیں اور ایمان والے کہیں گے بیشک ہار (نقصان) میں وہ ہیں جو اپنی جانیں اور اپنے گھر والے ہار بیٹھے قیامت کے دن سنتے ہو بیشک ظالم ہمیشہ کے عذاب میں ہیں،'),
(4318, 42, 46, 'اور ان کے کوئی دوست نہ ہوئے کہ اللہ کے مقابل ان کی مدد کرتے اور جسے اللہ گمراہ کرے اس کے لیے کہیں راستہ نہیں'),
(4319, 42, 47, 'اپنے رب کا حکم مانو اس دن کے آنے سے پہلے جو اللہ کی طرف سے ٹلنے والا نہیں اس دن تمہیں کوئی پناہ نہ ہوگی اور نہ تمہیں انکار کرتے بنے'),
(4320, 42, 48, 'تو اگر وہ منہ پھیریں تو ہم نے تمہیں ان پر نگہبان بناکر نہیں بھیجا تم پر تو نہیں مگر پہنچادینا اور جب ہم آدمی کو اپنی طرف سے کسی رحمت کا مزہ دیتے ہیں اور اس پر خوش ہوجاتا ہے اور اگر انہیں کوئی برائی پہنچے بدلہ اس کا جو ان کے ہاتھوں نے آگے بھیجا تو انسان بڑا ناشکرا ہے'),
(4321, 42, 49, 'اللہ ہی کے لیے ہے آسمانوں اور زمین کی سلطنت پیدا کرتا ہے جو چاہے جسے چاہے بیٹیاں عطا فرمائے اور جسے چاہے بیٹے دے'),
(4322, 42, 50, 'یا دونوں ملا دے بیٹے اور بیٹیاں اور جسے چاہے بانجھ کردے بیشک وہ علم و قدرت والا ہے،'),
(4323, 42, 51, 'اور کسی آدمی کو نہیں پہنچتا کہ اللہ اس سے کلام فرمائے مگر وحی کے طور پر یا یوں کہ وہ بشر پر وہ عظمت کے ادھر ہو یا کوئی فرشتہ بھیجے کہ وہ اس کے حکم سے وحی کرے جو وہ چاہے بیشک وہ بلندی و حکمت والا ہے،'),
(4324, 42, 52, 'اور یونہی ہم نے تمہیں وحی بھیجی ایک جان فزا چیز اپنے حکم سے، اس سے پہلے نہ تم کتاب جانتے تھے نہ احکام شرع کی تفصیل ہاں ہم نے اسے نور کیا جس سے ہم راہ دکھاتے ہیں اپنے بندوں سے جسے چاہتے ہیں، اور بیشک تم ضرور سیدھی راہ بتاتے ہو'),
(4325, 42, 53, 'اللہ کی راہ کہ اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں، سنتے ہو سب کام اللہ ہی کی طرف پھیرتے ہیں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حٰمٓ'),
(4327, 43, 2, 'روشن کتاب کی قسم'),
(4328, 43, 3, 'ہم نے اسے عربی قرآن اتارا کہ تم سمجھو'),
(4329, 43, 4, 'اور بیشک وہ اصل کتاب میں ہمارے پاس ضرور بلندی و حکمت والا ہے،'),
(4330, 43, 5, 'تو کیا ہم تم سے ذکر کا پہلو پھیردیں اس پر کہ تم لوگ حد سے بڑھنے والے ہو'),
(4331, 43, 6, 'اور ہم نے کتنے ہی غیب بتانے والے (نبی) اگلوں میں بھیجے،'),
(4332, 43, 7, 'اور ان کے پاس جو غیب بتانے والا (نبی) آیا اس کی ہنسی ہی بنایا کیے'),
(4333, 43, 8, 'تو ہم نے وہ ہلاک کردیے جو ان سے بھی پکڑ میں سخت تھے اور اگلوں کا حال گزر چکا ہے،'),
(4334, 43, 9, 'اور اگر تم ان سے پوچھو کہ آسمان اور زمین کس نے بنائے تو ضرور کہیں گے انہیں بنایا اس عزت والے علم والے نے'),
(4335, 43, 10, 'جس نے تمہارے لیے زمین کو بچھونا کیا اورتمہارے لیے اس میں راستے کیے کہ تم راہ پاؤ'),
(4336, 43, 11, 'اور وہ جس نے آسمان سے پانی اتارا ایک اندازے سے تو ہم نے اس سے ایک مردہ شہر زندہ فرمادیا، یونہی تم نکالے جاؤ گے'),
(4337, 43, 12, 'اور جس نے سب جوڑے بنائے اور تمہارے لیے کشتیوں اور چوپایوں سے سواریاں بنائیں،'),
(4338, 43, 13, 'کہ تم ان کی پیٹھوں پر ٹھیک بیٹھو پھر اپنے رب کی نعمت یاد کرو جب اس پر ٹھیک بیٹھ لو اور یوں کہو پاکی ہے اسے جس نے اس سواری کو ہمارے بس میں کردیا اور یہ ہمارے بوتے (قابو) کی نہ تھی،'),
(4339, 43, 14, 'اور بیشک ہمیں اپنے رب کی طرف پلٹنا ہے'),
(4340, 43, 15, 'اور اس کے لیے اس کے بندوں میں سے ٹکڑا ٹھہرایا بیشک آدمی کھلا ناشکرا ہے'),
(4341, 43, 16, 'کیا اس نے اپنے لیے اپنی مخلوق میں سے بیٹیاں لیں اور تمہیں بیٹوں کے ساتھ خاص کیا'),
(4342, 43, 17, 'اور جب ان میں کسی کو خوشخبری دی جائے اس چیز کی جس کا وصف رحمن کے لیے بتاچکا ہے تو دن بھر اس کا منہ کالا رہے اور غم کھایا کرے'),
(4343, 43, 18, 'اور کیا وہ جو گہنے (زیور) میں پروان چڑھے اور بحث میں صاف بات نہ کرے'),
(4344, 43, 19, 'اور انہوں نے فرشتوں کو، کہ رحمٰن کے بندے ہیں عورتیں ٹھہرایا کیا ان کے بناتے وقت یہ حاضر تھے اب لکھ لی جائے گی ان کی گواہی اور ان سے جواب طلب ہوگا'),
(4345, 43, 20, 'اور بولے اگر رحمٰن چاہتا ہم انہیں نہ پوجتے انہیں اس کی حقیقت کچھ معلوم نہیں یونہی اٹکلیں دوڑاتے ہیں'),
(4346, 43, 21, 'یا اس سے قبل ہم نے انہیں کوئی کتاب دی ہے جسے وہ تھامے ہوئے ہیں'),
(4347, 43, 22, 'بلکہ بولے ہم نے اپنے باپ دادا کو ایک دین پر پایا اور ہم ان کی لکیر پر چل رہے ہیں'),
(4348, 43, 23, 'اور ایسے ہی ہم نے تم سے پہلے جب کسی شہر میں ڈر سنانے والا بھیجا وہاں کے آسُودوں (امیروں) نے یہی کہا کہ ہم نے اپنے باپ دادا کو ایک دین پر پایا اور ہم ان کی لکیر کے پیچھے ہیں'),
(4349, 43, 24, 'نبی نے فرمایا اور کیا جب بھی کہ میں تمہارے پاس وہ لاؤں جو سیدھی راہ ہو اس سے جس پر تمہارے باپ دادا تھے بولے جو کچھ تم لے کر بھیجے گئے ہم اسے نہیں مانتے'),
(4350, 43, 25, 'تو ہم نے ان سے بدلہ لیا تو دیکھو جھٹلانے والوں کا کیسا انجام ہوا،'),
(4351, 43, 26, 'اور جب ابراہیم نے اپنے باپ اور اپنی قوم سے فرمایا میں بیزار ہوں تمہارے معبودو ں سے،'),
(4352, 43, 27, 'سوا اس کے جس نے مجھے پیدا کیا کہ ضرور وہ بہت جلد مجھے راہ دے گا،'),
(4353, 43, 28, 'اور اسے اپنی نسل میں باقی کلام رکھا کہ کہیں وہ باز آئیں'),
(4354, 43, 29, 'بلکہ میں نے انہیں اور ان کے باپ دادا کو دنیا کے فائدے دیے یہاں تک کہ ان کے پاس حق اور صاف بتانے والا رسول تشریف لایا'),
(4355, 43, 30, 'اور جب ان کے پاس حق آیا بولے یہ جادو ہے اور ہم اس کے منکر ہیں،'),
(4356, 43, 31, 'اور بولے کیوں نہ اتارا گیا یہ قرآن ان دو شہروں کے کسی بڑے آدمی پر'),
(4357, 43, 32, 'کیا تمہارے رب کی رحمت وہ بانٹتے ہیں ہم نے ان میں ان کی زیست کا سامان دنیا کی زندگی میں بانٹا اور ان میں ایک دوسرے پر درجوں بلندی دی کہ ان میں ایک دوسرے کی ہنسی بنائے اور تمہارے رب کی رحمت ان کی جمع جتھا سے بہتر'),
(4358, 43, 33, 'اور اگر یہ نہ ہوتا کہ سب لوگ ایک دین پر ہوجائیں تو ہم ضرور رحمٰن کے منکروں کے لیے چاندی کی چھتیں اور سیڑھیاں بناتے جن پر چڑھتے،'),
(4359, 43, 34, 'اور ان کے گھروں کے لیے چاندی کے دروازے اور چاندی کے تخت جن پر تکیہ لگاتے،'),
(4360, 43, 35, 'اور طرح طرح کی آرائش اور یہ جو کچھ ہے جیتی دنیا ہی کا اسباب ہے، اور آخرت تمہارے رب کے پاس پرہیزگاروں کے لیے ہے'),
(4361, 43, 36, 'اور جسے رند تو آئے (شب کوری ہو) رحمن کے ذکر سے ہم اس پر ایک شیطان تعینات کریں کہ وہ اس کا ساتھی رہے،'),
(4362, 43, 37, 'اور بیشک وہ شیاطین ان کو راہ سے روکتے ہیں اور سمجھتے یہ ہیں کہ وہ راہ پر ہیں،'),
(4363, 43, 38, 'یہاں تک کہ جب کافر ہمارے پاس آئے گا اپنے شیطان سے کہے گا ہائے کسی طرح مجھ میں تجھ میں پورب پچھم کا فاصلہ ہوتا تو کیا ہی برا ساتھی ہے،'),
(4364, 43, 39, 'اور ہرگز تمہارا اس سے بھلا نہ ہوگا آج جبکہ تم نے ظلم کیا کہ تم سب عذاب میں شریک ہو،'),
(4365, 43, 40, 'تو کیا تم بہروں کو سناؤ گے یا اندھوں کو راہ دکھاؤ گے اور انہیں جو کھلی گمراہی میں ہیں'),
(4366, 43, 41, 'تو اگر ہم تمہیں لے جائیں تو ان سے ہم ضرور بدلہ لیں گے'),
(4367, 43, 42, 'یا تمہیں دکھادیں جس کا انہیں ہم نے وعدہ دیا ہے تو ہم ان پر بڑی قدرت والے ہیں،'),
(4368, 43, 43, 'تو مضبوط تھامے رہو اسے جو تمہاری طرف وحی کی گئی بیشک تم سیدھی راہ پر ہو،'),
(4369, 43, 44, 'اور بیشک وہ شرف ہے تمہارے لیے اور تمہاری قوم کے لیے اور عنقریب تم سے پوچھا جائے گا'),
(4370, 43, 45, 'اور ان سے پوچھو جو ہم نے تم سے پہلے رسول بھیجے کیا ہم نے رحمان کے سوا کچھ اور خدا ٹھہرائے جن کو پوجا ہو'),
(4371, 43, 46, 'اور بیشک ہم نے موسیٰ کو اپنی نشایوں کے ساتھ فرعون اور اس کے سرداروں کی طرف بھیجا تو اس نے فرمایا بیشک میں اس کا رسول ہوں جو سارے جہاں کا مالک ہے،'),
(4372, 43, 47, 'پھر جب وہ ان کے پاس ہماری نشانیاں لایا جبھی وہ ان پر ہنسنے لگے'),
(4373, 43, 48, 'اور ہم انہیں جو نشانی دکھاتے وہ پہلے سے بڑی ہوتی اور ہم نے انہیں مصیبت میں گرفتار کیا کہ وہ بام آئیں'),
(4374, 43, 49, 'اور بولے کہ اے جادوگر ہمارے لیے اپنے رب سے دعا کر کہ اس عہد کے سبب جو اس کا تیرے پاس ہے بیشک ہم ہدایت پر آئیں گے'),
(4375, 43, 50, 'پھر جب ہم نے ان سے وہ مصیبت ٹال دی جبھی وہ عہد توڑ گئے'),
(4376, 43, 51, 'اور فرعون اپنی قوم میں پکارا کہ اے میری قوم! کیا میرے لیے مصر کی سلطنت نہیں اور یہ نہریں کہ میرے نیچے بہتی ہیں تو کیا تم دیکھتے نہیں'),
(4377, 43, 52, 'یا میں بہتر ہوں اس سے کہ ذلیل ہے اور بات صاف کرتا معلوم نہیں ہوتا'),
(4378, 43, 53, 'تو اس پر کیوں نہ ڈالے گئے سونے کے کنگن یا اس کے ساتھ فرشتے آتے کہ اس کے پاس رہتے'),
(4379, 43, 54, 'پھر اس نے اپنی قوم کو کم عقل کرلیا تو وہ اس کے کہنے پر چلے بیشک وہ بے حکم لوگ تھے،'),
(4380, 43, 55, 'پھر جب انہوں نے وہ کیا جس پر ہمارا غضب ان پر آیا ہم نے ان سے بدلہ لیا تو ہم نے ان سب کو ڈبودیا،'),
(4381, 43, 56, 'انہیں ہم نے کردیا اگلی داستان اور کہاوت پچھلوں کے لیے'),
(4382, 43, 57, 'اور جب ابنِ مریم کی مثال بیان کی جائے، جبھی تمہاری قوم اس سے ہنسنے لگتے ہیں'),
(4383, 43, 58, 'اور کہتے ہیں کیا ہمارے معبود بہتر ہیں یا وہ انہوں نے تم سے یہ نہ کہی مگر ناحق جھگڑے کو بلکہ وہ ہیں جھگڑالو لوگ'),
(4384, 43, 59, 'وہ تو نہیں مگر ایک بندہ جس پر ہم نے احسان فرمایا اور اسے ہم نے بنی اسرائیل کے لیے عجیب نمونہ بنایا'),
(4385, 43, 60, 'اور اگر ہم چاہتے تو زمین میں تمہارے بدلے فرشتے بساتے'),
(4386, 43, 61, 'اور بیشک عیسی ٰ قیامت کی خبر ہے تو ہرگز قیامت میں شک نہ کرنا اور میرے پیرو ہونا یہ سیدھی راہ ہے،'),
(4387, 43, 62, 'اور ہرگز شیطان تمہیں نہ روک دے بیشک وہ تمہارا کھلا دشمن ہے،'),
(4388, 43, 63, 'اور جب عیسیٰ روشن نشانیاں لایا اس نے فرمایا میں تمہارے پاس حکمت لے کر آیا اور اس لیے میں تم سے بیان کردوں بعض وہ باتیں جن میں تم اختلاف رکھتے ہو تو اللہ سے ڈرو اور میرا حکم مانو،'),
(4389, 43, 64, 'بیشک اللہ میرا رب اور تمہارا رب تو اسے پوجو، یہ سیدھی راہ ہے'),
(4390, 43, 65, 'پھر وہ گروہ آپس میں مختلف ہوگئے تو ظالموں کی خرابی ہے ایک درد ناک دن کے عذاب سے'),
(4391, 43, 66, 'کاہے کے انتظار میں ہیں مگر قیامت کے کہ ان پر اچانک آجائے اور انہیں خبر نہ ہو،'),
(4392, 43, 67, 'گہرے دوست اس دن ایک دوسرے کے دشمن ہوں گے مگر پرہیزگار'),
(4393, 43, 68, 'ان سے فرمایا جائے گا اے میرے بندو آج نہ تم پر خوف نہ تم کو غم ہو،'),
(4394, 43, 69, 'وہ جو ہماری آیتوں پر ایمان لائے اور مسلمان تھے،'),
(4395, 43, 70, 'داخل ہو جنت میں تم اور تمہاری بیبیاں اور تمہاری خاطریں ہوتیں'),
(4396, 43, 71, 'ان پر دورہ ہوگا سونے کے پیالوں اور جاموں کا اور اس میں جو جی چاہے اور جس سے آنکھ کو لذت پہنچے اور تم اس میں ہمیشہ رہو گے،'),
(4397, 43, 72, 'اور یہ ہے وہ جنت جس کے تم وارث کیے گئے اپنے اعمال سے،'),
(4398, 43, 73, 'تمہارے لیے اس میں بہت میوے ہیں کہ ان میں سے کھاؤ'),
(4399, 43, 74, 'بیشک مجرم جہنم کے عذاب میں ہمیشہ رہنے والے ہیں،'),
(4400, 43, 75, 'وہ کبھی ان پر سے ہلکا نہ پڑے گا اور وہ اس میں بے آ س رہیں گے'),
(4401, 43, 76, 'اور ہم نے ان پر کچھ ظلم نہ کیا، ہاں وہ خود ہی ظالم تھے'),
(4402, 43, 77, 'اور وہ پکاریں گے اے مالک تیرا رب ہمیں تمام کرچکے وہ فرمائے گا تمہیں تو ٹھہرنا'),
(4403, 43, 78, 'بیشک ہم تمہارے پاس حق لائے مگر تم میں اکثر کو حق ناگوار ہے،'),
(4404, 43, 79, 'کیا انہوں نے اپنے خیال میں کوئی کام پکا کرلیا ہے'),
(4405, 43, 80, 'تو ہم اپنا کام پکا کرنے والے ہیں کیا اس گھمنڈ میں ہیں کہ ہم ان کی آہستہ بات اور ان کی مشورت نہیں سنتے، ہاں کیوں نہیں اور ہمارے فرشتے ان کے پاس لکھ رہے ہیں،'),
(4406, 43, 81, 'تم فرماؤ بفرض محال رحمٰن کے کوئی بچہ ہوتا، تو سب سے پہلے میں پوجتا'),
(4407, 43, 82, 'پاکی ہے آسمانوں اور زمین کے رب کو، عرش کے رب کو ان باتوں سے جو یہ بناتے ہیں'),
(4408, 43, 83, 'تو تم انہیں چھوڑو کہ بیہودہ باتیں کریں اور کھیلیں یہاں تک کہ اپنے اس دن کو پائیں جس کا ان سے وعدہ ہے'),
(4409, 43, 84, 'اور وہی آسمان والوں کا خدا اور زمین والوں کا خدا اور وہی حکمت و علم والا ہے،'),
(4410, 43, 85, 'اور بڑی برکت والا ہے وہ کہ اسی کے لیے ہے سلطنت آسمانوں اور زمین کی اور جو کچھ ان کے درمیان ہے اور اسی کے پاس ہے قیامت کا علم، اور تمہیں اس کی طرف پھرنا،'),
(4411, 43, 86, 'اور جن کو یہ اللہ کے سوا پوجتے ہیں شفاعت کا اختیار نہیں رکھتے، ہاں شفاعت کا اختیار انہیں ہے جو حق کی گواہی دیں اور علم رکھیں'),
(4412, 43, 87, 'اور اگر تم ان سے پوچھو انہیں کس نے پیدا کیا تو ضرور کہیں گے اللہ نے تو کہاں اوندھے جاتے ہیں'),
(4413, 43, 88, 'مجھے رسول کے اس کہنے کی قسم کہ اے میرے رب! یہ لوگ ایمان نہیں لاتے،'),
(4414, 43, 89, 'تو ان سے درگزر کرو اور فرماؤ بس سلام ہے کہ آگے جان جائیں گے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حٰمٓ'),
(4416, 44, 2, 'قسم اس روشن کتاب کی'),
(4417, 44, 3, 'بیشک ہم نے اسے برکت والی رات میں اتارا بیشک ہم ڈر سنانے والے ہیں'),
(4418, 44, 4, 'اس میں بانٹ دیا جاتا ہے ہر حکمت والا کام'),
(4419, 44, 5, 'ہمارے پاس کے حکم سے، بیشک ہم بھیجنے والے ہیں'),
(4420, 44, 6, 'تمہارے رب کی طرف سے رحمت، بیشک وہی سنتا جانتا ہے،'),
(4421, 44, 7, 'وہ جو رب ہے آسمانوں اور زمین کا اور جو کچھ ان کے درمیان ہے، اگر تمہیں یقین ہو'),
(4422, 44, 8, 'اس کے سوا کسی کی بندگی نہیں وہ جِلائے اور مارے، تمہارا رب اور تمہارے اگلے باپ دادا کا رب،'),
(4423, 44, 9, 'بلکہ وہ شک میں پڑے کھیل رہے ہیں'),
(4424, 44, 10, 'تو تم اس دن کے منتظر رہو جب آسمان ایک ظاہر دھواں لائے گا،'),
(4425, 44, 11, 'کہ لوگوں کو ڈھانپ لے گا یہ ہے دردناک عذاب،'),
(4426, 44, 12, 'اس دن کہیں گے، اے ہمارے رب! ہم پر سے عذاب کھول دے ہم ایمان لاتے ہیں'),
(4427, 44, 13, 'کہاں سے ہو انہیں نصیحت ماننا حالانکہ ان کے پاس صاف بیان فرمانے والا رسول تشریف لاچکا'),
(4428, 44, 14, 'اس سے روگرداں ہوئے اور بولے سکھایا ہوا دیوانہ ہے'),
(4429, 44, 15, 'ہم کچھ دنوں کو عذاب کھولے دیتے ہیں تم پھر وہی کرو گے'),
(4430, 44, 16, 'جس دن ہم سب سے بڑی پکڑ پکڑیں گے بیشک ہم بدلہ لینے والے ہیں،'),
(4431, 44, 17, 'اور بیشک ہم نے ان سے پہلے فرعون کی قوم کو جانچا اور ان کے پاس ایک معزز رسول تشریف لایا'),
(4432, 44, 18, 'کہ اللہ کے بندوں کو مجھے سپرد کردو بیشک میں تمہارے لیے امانت والا رسول ہوں،'),
(4433, 44, 19, 'اور اللہ کے مقابل سرکشی نہ کرو، میں تمہارے پاس ایک روشن سند لاتا ہوں'),
(4434, 44, 20, 'اور میں پناہ لیتا ہوں اپنے رب اور تمہارے رب کی اس سے کہ تم مجھے سنگسار کرو'),
(4435, 44, 21, 'اور اگر میرا یقین نہ لاؤ تو مجھ سے کنارے ہوجاؤ'),
(4436, 44, 22, 'تو اس نے اپنے رب سے دعا کی کہ یہ مجرم لوگ ہیں،'),
(4437, 44, 23, 'ہم نے حکم فرمایا کہ میرے بندوں کو راتوں رات لے نکل ضرور تمہارا پیچھا کیا جائے گا'),
(4438, 44, 24, 'اور دریا کو یونہی جگہ جگہ سے چھوڑ دے بیشک وہ لشکر ڈبو دیا جائے گا'),
(4439, 44, 25, 'کتنے چھوڑ گئے باغ اور چشمے،'),
(4440, 44, 26, 'او رکھیت اور عمدہ مکانات'),
(4441, 44, 27, 'اور نعمتیں جن میں فارغ البال تھے'),
(4442, 44, 28, 'ہم نے یونہی کیا اور ان کا وارث دوسری قوم کو کردیا'),
(4443, 44, 29, 'تو ان پر آسمان اور زمین نہ روئے اور انہیں مہلت نہ دی گئی'),
(4444, 44, 30, 'اور بیشک ہم نے بنی اسرائیل کو ذلت کے عذاب سے نجات بخشی'),
(4445, 44, 31, 'فرعون سے، بیشک وہ متکبر حد سے بڑھنے والوں سے،'),
(4446, 44, 32, 'اور بیشک ہم نے انہیں دانستہ چن لیا اس زمانے والوں سے،'),
(4447, 44, 33, 'ہم نے انہیں وہ نشانیاں عطا فرمائیں جن میں صریح انعام تھا'),
(4448, 44, 34, 'بیشک یہ کہتے ہیں،'),
(4449, 44, 35, 'وہ تو نہیں مگر ہمارا ایک دفعہ کا مرنا اور ہم اٹھائے نہ جائیں گے'),
(4450, 44, 36, 'تو ہمارے باپ دادا کو لے آؤ اگر تم سچے ہو'),
(4451, 44, 37, 'کیا وہ بہتر ہیں یا تبع کی قوم اور جو ان سے پہلے تھے ہم نے انہیں ہلاک کردیا بیشک وہ مجرم لوگ تھے'),
(4452, 44, 38, 'اور ہم نے نہ بنائے آسمان اور زمین اور جو کچھ ان کے درمیان ہے کھیل کے طور پر'),
(4453, 44, 39, 'ہم نے انہیں نہ بنایا مگر حق کے ساتھ لیکن ان میں اکثر جانتے نہیں'),
(4454, 44, 40, 'بیشک فیصلہ کا دن ان سب کی میعاد ہے،'),
(4455, 44, 41, 'جس دن کوئی دوست کسی دوست کے کچھ کام نہ آئے گا اور نہ ان کی مدد ہوگی'),
(4456, 44, 42, 'مگر جس پر اللہ رحم کرے بیشک وہی عزت والا مہربان ہے،'),
(4457, 44, 43, 'بیشک تھوہڑ کا پیڑ'),
(4458, 44, 44, 'گنہگاروں کی خوراک ہے'),
(4459, 44, 45, 'گلے ہوئے تانبے کی طرح پیٹوں میں جوش مارتا ہے،'),
(4460, 44, 46, 'جیسا کھولتا پانی جوش مارے'),
(4461, 44, 47, 'اسے پکڑو ٹھیک بھڑکتی آگ کی طرف بزور گھسیٹتے لے جاؤ،'),
(4462, 44, 48, 'پھر اس کے سر کے اوپر کھولتے پانی کا عذاب ڈالو'),
(4463, 44, 49, 'چکھ، ہاں ہاں تو ہی بڑا عزت والا کرم والا ہے'),
(4464, 44, 50, 'بیشک یہ ہے وہ جس میں تم شبہہ کرتے تھے'),
(4465, 44, 51, 'بیشک ڈر والے امان کی جگہ میں ہیں'),
(4466, 44, 52, 'باغوں اور چشموں میں،'),
(4467, 44, 53, 'پہنیں گے کریب اور قنادیز آمنے سامنے'),
(4468, 44, 54, 'یونہی ہے، اور ہم نے انہیں بیاہ دیا نہایت سیاہ اور روشن بڑی آنکھوں والیوں سے،'),
(4469, 44, 55, 'اس میں ہر قسم کا میوہ مانگیں گے امن و امان سے'),
(4470, 44, 56, 'اس میں پہلی موت کے سوا پھر موت نہ چکھیں گے اور اللہ نے انہیں آگ کے عذاب سے بچالیا،'),
(4471, 44, 57, 'تمہارے رب کے فضل سے، یہی بڑی کامیابی ہے،'),
(4472, 44, 58, 'تو ہم نے اس قرآن کو تمہاری زبان میں آسان کیا کہ وہ سمجھیں'),
(4473, 44, 59, 'تو تم انتظار کرو وہ بھی کسی انتظار میں ہیں');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حٰمٓ'),
(4475, 45, 2, 'کتاب کا اتارنا ہے اللہ عزت و حکمت والے کی طرف سے،'),
(4476, 45, 3, 'بیشک آسمانوں اور زمین میں نشانیاں ہیں ایمان والوں کے لیے'),
(4477, 45, 4, 'اور تمہاری پیدائش میں اور جو جو جانور وہ پھیلاتا ہے ان میں نشانیاں ہیں یقین والوں کے لیے،'),
(4478, 45, 5, 'اور رات اور دن کی تبدیلیوں میں اور اس میں کہ اللہ نے آسمان سے روزی کا سبب مینہ اتارا تو اس سے زمین کو اس کے مَرے پیچھے زندہ کیا اور ہواؤں کی گردش میں نشانیاں ہیں عقل مندوں کے لیے،'),
(4479, 45, 6, 'یہ اللہ کی آیتیں ہیں کہ ہم تم پر حق کے ساتھ پڑھتے ہیں، پھر اللہ اور اس کی آیتوں کو چھوڑ کر کونسی بات پر ایمان لائیں گے،'),
(4480, 45, 7, 'خرابی ہے ہر بڑے بہتان ہائے گنہگار کے لیے'),
(4481, 45, 8, 'اللہ کی آیتوں کو سنتا ہے کہ اس پر پڑھی جاتی ہیں پھر ہٹ پر جمتا ہے غرور کرتا گویا انہیں سنا ہی نہیں تو اسے خوشخبری سناؤ درد ناک عذاب کی،'),
(4482, 45, 9, 'اور جب ہماری آیتوں میں سے کسی پر اطلاع پائے اس کی ہنسی بناتا ہے ان کے لیے خواری کا عذاب،'),
(4483, 45, 10, 'ان کے پیچھے جہنم ہے اور انہیں کچھ کام نہ دے گا ان کا کمایا ہوا اور نہ وہ جو اللہ کے سوا حمایتی ٹھہرا رکھے تھے اور ان کے لیے بڑا عذاب ہے،'),
(4484, 45, 11, 'یہ راہ دکھانا ہے اور جنہوں نے اپنے رب کی آیتوں کو نہ مانا ان کے لیے دردناک عذاب میں سے سخت تر عذاب ہے،'),
(4485, 45, 12, 'اللہ ہے جس نے تمہارے بس میں دریا کردیا کہ اس میں اس کے حکم سے کشتیاں چلیں اور اس لیے کہ اس کا فضل تلاش کرو اور اس لیے کہ حق مانو'),
(4486, 45, 13, 'اور تمہارے لیے کام میں لگائے جو کچھ آسمان میں ہیں اور جو کچھ زمین میں اپنے حکم سے بے شک اس میں نشا نیاں ہیں سوچنے والوں کے لئے،'),
(4487, 45, 14, 'ایمان وا لوں سے فرماؤ درگزریں ان سے جو اللہ کے دنوں کی امید نہیں رکھتے تاکہ اللہ ایک قوم کو اس کی کمائی کا بدلہ دے'),
(4488, 45, 15, 'جو بھلا کام کرے تو اپنے لیے اور برا کرے تو اپنے برے کو پھر اپنے رب کی طرف پھیرے جاؤ گے'),
(4489, 45, 16, 'اور بیشک ہم نے بنی اسرائیل کو کتاب اور حکومت اور نبوت عطا فرمائی اور ہم نے انہیں ستھری روزیاں دیں اور انہیں ان کے زمانے والوں پر فضیلت بخشی،'),
(4490, 45, 17, 'اور ہم نے انہیں اس کام کی روشن دلیلیں دیں تو انہوں نے اختلاف نہ کیا مگر بعد اس کے کہ علم ان کے پاس آچکا آپس کے حسد سے بیشک تمہارا رب قیامت کے دن ان میں فیصلہ کردے گا جس بات میں اختلاف کرتے یں،'),
(4491, 45, 18, 'پھر ہم نے اس کام کے عمدہ راستہ پر تمہیں کیا تو اسی راہ پر چلو اور نادانوں کی خواہشوں کا ساتھ نہ دو'),
(4492, 45, 19, 'بیشک وہ اللہ کے مقابل تمہیں کچھ کام نہ دیں گے، اور بیشک ظالم ایک دوسرے کے دوست ہیں اور ڈر والوں کا دوست اللہ'),
(4493, 45, 20, 'یہ لوگوں کی آنکھیں کھولنا ہے اور ایمان والوں کے لیے ہدایت و رحمت،'),
(4494, 45, 21, 'کیا جنہوں نے برائیوں کا ارتکاب کیا یہ سمجھتے ہیں کہ ہم انہیں ان جیسا کردیں گے جو ایمان لائے اور اچھے کام کیے کہ ان کی ان کی زندگی اور موت برابر ہوجائے کیا ہی برا حکم لگاتے ہیں،'),
(4495, 45, 22, 'اور اللہ نے آسمان اور زمین کو حق کے ساتھ بنایا اور اس لیے کہ ہر جان اپنے کیے کا بدلہ پائے اور ان پر ظلم نہ ہوگا،'),
(4496, 45, 23, 'بھلا دیکھو تو وہ جس نے اپنی خواہش کو اپنا خدا ٹھہرالیا اور اللہ نے اسے با وصف علم کے گمراہ کیا اور اس کے کان اور دل پر مہر لگادی اور اس کی آنکھوں پر پردہ ڈالا تو اللہ کے بعد اسے کون راہ دکھائے، تو کیا تم دھیان نہیں کرتے،'),
(4497, 45, 24, 'اور بولے وہ تو نہیں مگر یہی ہماری دنیا کی زندگی مرتے ہیں اور جیتے ہیں اور ہمیں ہلاک نہیں کرتا مگر زمانہ اور انہیں اس کا علم نہیں وہ تو نرے گمان دوڑاتے ہیں'),
(4498, 45, 25, 'اور جب ان پر ہماری روشن آیتیں پڑھی جائیں تو بس ان کی حجت یہی ہوتی ہے کہ کہتے ہیں کہ ہمارے باپ دادا کو لے آؤ اگر تم سچے ہو'),
(4499, 45, 26, 'تم فرماؤ اللہ تمہیں جِلاتا ہے پھر تم کو مارے گا پھر تم سب کو اکٹھا کریگا قیامت کے دن جس میں کوئی شک نہیں لیکن بہت آدمی نہیں جانتے'),
(4500, 45, 27, 'اور اللہ ہی کے لیے ہے آسمانوں اور زمین کی سلطنت، اور جس دن قیامت قائم ہوگی باطل والوں کی اس دن ہار ہے'),
(4501, 45, 28, 'اور تم ہر گرو ه کو دیکھو گے زانو کے بل گرے ہوئے ہر گروہ اپنے نامہٴ اعمال کی طرف بلایا جائے گا آج تمہیں تمہارے کیے کا بدلہ دیا جائے گا،'),
(4502, 45, 29, 'ہمارا یہ نوشتہ تم پر حق بولتا ہے، ہم لکھتے رہے تھے جو تم نے کیا،'),
(4503, 45, 30, 'تو وہ جو ایمان لائے اور اچھے کام کیے ان کا رب انہیں اپنی رحمت میں لے گا یہی کھلی کامیابی ہے،'),
(4504, 45, 31, 'اور جو کافر ہوئے ان سے فرمایا جائے گا، کیا نہ تھا کہ میری آیتیں تم پر پڑھی جاتی تھیں تو تم تکبر کرتے تھے اور تم مجرم لوگ تھے،'),
(4505, 45, 32, 'اور جب کہا جاتا بیشک اللہ کا وعدہ سچا ہے اور قیامت میں شک نہیں تم کہتے ہم نہیں جانتے قیامت کیا چیز ہے ہمیں تو یونہی کچھ گمان سا ہوتا ہے اور ہمیں یقین نہیں،'),
(4506, 45, 33, 'اور ان پر کھل گئیں ان کے کاموں کی برائیاں اور انہیں گھیرلیا اس عذاب نے جس کی ہنسی بناتے تھے،'),
(4507, 45, 34, 'اور فرمایا جائے گا آج ہم تمہیں چھوڑدیں گے جیسے تم اپنے اس دن کے ملنے کو بھولے ہوئے تھے اور تمہارا ٹھکانا آ گ ہے اور تمہارا کوئی مددگار نہیں'),
(4508, 45, 35, 'یہ اس لیے کہ تم نے اللہ کی آیتوں کا ٹھٹھا (مذاق) بنایا اور دنیا کی زندگی نے تمہیں فریب دیا تو آج نہ وہ آگ سے نکالے جائیں اور نہ ان سے کوئی منانا چاہے'),
(4509, 45, 36, 'تو اللہ ہی کے لیے سب خوبیاں ہیں آسمانوں کا رب اور زمین کا رب اور سارے جہاں کا رب،'),
(4510, 45, 37, 'اور اسی کے لیے بڑائی ہے آسمانوں اور زمین میں، اور وہی عزت و حکمت والا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حٰمٓ'),
(4512, 46, 2, 'یہ کتاب اتارنا ہے اللہ عزت و حکمت والے کی طرف سے،'),
(4513, 46, 3, 'ہم نے نہ بنائے آسمان اور زمین اور جو کچھ ان کے درمیان ہے مگر حق کے ساتھ اور ایک مقرر میعاد پر اور کافر اس چیز سے کہ ڈرائے گئے منہ پھیرے ہیں'),
(4514, 46, 4, 'تم فرماؤ بھلا بتاؤ تو وہ جو تم اللہ کے سوا پوجتے ہو مجھے دکھاؤ انہوں نے زمین کا کون سا ذرہ بنایا یا آسمان میں ان کا کوئی حصہ ہے، میرے پاس لاؤ اس سے پہلی کوئی کتاب یا کچھ بچا کھچا علم اگر تم سچے ہو'),
(4515, 46, 5, 'اور اس سے بڑھ کر گمراہ کون جو اللہ کے سوا ایسوں کو پوجے جو قیامت تک اس کی نہ سنیں اور انہیں ان کی پوجا کی خبر تک نہیں'),
(4516, 46, 6, 'اور جب لوگوں کا حشر ہوگا وہ ان کے دشمن ہوں گے اور ان سے منکر ہوجائیں گے'),
(4517, 46, 7, 'اور جب ان پر پڑھی جائیں ہماری روشن آیتیں تو کافر اپنے پاس آئے ہوئے حق کو کہتے ہیں یہ کھلا جادو ہے'),
(4518, 46, 8, 'کیا کہتے ہیں انہوں نے اسے جی سے بنایا تم فرماؤ اگر میں نے اسے جی سے بنالیا ہوگا تو تم اللہ کے سامنے میرا کچھ اختیار نہیں رکھتے وہ خوب جانتا ہے جن باتوں میں تم مشغول ہو اور وہ کافی ہے میرے اور تمہارے درمیان گواہ، اور وہی بخشنے والا مہربان ہے'),
(4519, 46, 9, 'تم فرماؤ میں کوئی انوکھا رسول نہیں اور میں نہیں جانتا میرے ساتھ کیا کیا جائے گا اور تمہارے ساتھ کیا میں تو اسی کا تابع ہوں جو مجھے وحی ہوتی ہے اور میں نہیں مگر صاف ڈر سنانے والا،'),
(4520, 46, 10, 'تم فرماؤ بھلا دیکھو تو اگر وہ قرآن اللہ کے پاس سے ہو اور تم نے اس کا انکار کیا اور بنی اسرائیل کا ایک گواہ اس پر گواہی دے چکا تو وہ ایمان لایا اور تم نے تکبر کیا بیشک اللہ راہ نہیں دیتا ظالموں کو،'),
(4521, 46, 11, 'اور کافروں نے مسلمانوں کو کہا اگر اس میں کچھ بھلائی ہو تو یہ ہم سے آگے اس تک نہ پہنچ جاتے اور جب انہیں اس کی ہدایت نہ ہوئی تو اب کہیں گے کہ یہ پرانا بہتان ہے،'),
(4522, 46, 12, 'اور اس سے پہلے موسیٰ کی کتاب سے پیشوا اور مہربانی، اور یہ کتاب ہے تصدیق فرماتی عربی زبان میں کہ ظالموں کو ڈر سنائے، اور نیکوں کو بشارت،'),
(4523, 46, 13, 'بیشک وہ جنہوں نے کہا ہمارا رب اللہ ہے پھر ثابت قدم رہے نہ ان پر خوف نہ ان کو غم'),
(4524, 46, 14, 'وہ جنت والے ہیں ہمیشہ اس میں رہیں گے ان کے اعمال کا انعام،'),
(4525, 46, 15, 'اور ہم نے آدمی کو حکم کیا اپنے ماں باپ سے بھلائی کرے، ا س کی ماں نے اسے پیٹ میں رکھا تکلیف سے اور جنا اس کو تکلیف سے، اور اسے اٹھائے پھرنا اور اس کا دودھ چھڑانا تیس مہینے میں ہے یہاں تک کہ جب اپنے زور کو پہنچا اور چالیس برس کا ہوا عرض کی اے میرے رب! میرے دل میں ڈال کہ میں تیری نعمت کا شکر کروں جو تو نے مجھ پر اور میرے ماں باپ پر کی اور میں وہ کام کروں جو تجھے پسند آئے اور میرے لیے میری اولاد میں صلاح (نیکی) رکھ میں تیری طرف رجوع لایا اور میں مسلمان ہوں'),
(4526, 46, 16, 'یہ ہیں وہ جن کی نیکیاں ہم قبول فرمائیں گے اور ان کی تقصیروں سے درگزر فرمائیں گے جنت والوں میں، سچا وعدہ جو انہیں دیا جاتا تھا'),
(4527, 46, 17, 'اور وہ جس نے اپنے ماں باپ سے کہا اُف تم سے دل پک گیا (بیزار ہے) کیا مجھے یہ وعدہ دیتے ہو کہ پھر زندہ کیا جاؤں گا حالانکہ مجھ پر سے پہلے سنگتیں گزر چکیں اور وہ دونوں اللہ سے فریاد کرتے ہیں تیری خرابی ہو ایمان لا، بیشک اللہ کا وعدہ سچا ہے تو کہتا ہے یہ تو نہیں مگر اگلوں کی کہانیاں'),
(4528, 46, 18, 'یہ وہ ہیں جن پر بات ثابت ہوچکی ان گروہوں میں جو ان سے پلے گزرے جن اور آدمی، بیشک وہ زیاں کار تھے،'),
(4529, 46, 19, 'اور ہر ایک کے لیے اپنے اپنے عمل کے درجے ہیں اور تاکہ اللہ ان کے کام انہیں پورے بھردے'),
(4530, 46, 20, 'اور ان پر ظلم نہ ہوگا، اور جس دن کافر آگ پر پیش کیے جائیں گے، ان سے فرمایا جائے گا تم اپنے حصہ کی پاک چیزیں اپنی دنیا ہی کی زندگی میں فنا کرچکے اور انہیں برت چکے تو آج تمہیں ذلت کا عذاب بدلہ دیا جائے گا سزا اس کی کہ تم زمین میں ناحق تکبر کرتے تھے اور سزا اس کی کہ حکم عدولی کرتے تھے'),
(4531, 46, 21, 'اور یاد کرو عاد کے ہم قوم کو جب اس نے ان کو سرزمینِ احقاف میں ڈرایا اور بیشک اس سے پہلے ڈر سنانے والے گزر چکے اور اس کے بعد آئے کہ اللہ کے سوا کسی کو نہ پُوجو، بیشک مجھے تم پر ایک بڑے دن کے عذاب کا اندیشہ ہے،'),
(4532, 46, 22, 'بولے کیا تم اس لیے آئے کہ ہمیں ہمارے معبودوں سے پھیر دو، تو ہم پر لاؤ جس کا ہمیں وعدہ دیتے ہو اگر تم سچے ہو،'),
(4533, 46, 23, 'اس نے فرمایا اس کی خبر تو اللہ ہی کے پاس ہے میں تو تمہیں اپنے رب کے پیام پہنچاتا ہوں ہاں میری دانست میں تم نرے جاہل لوگ ہو'),
(4534, 46, 24, 'پھر جب انہوں نے عذاب کو دیکھا بادل کی طرح آسمان کے کنارے میں پھیلا ہوا ان کی وادیوں کی طرف آتا بولے یہ بادل ہے کہ ہم پر برسے گا بلکہ یہ تو وہ ہے جس کی تم جلدی مچاتے تھے، ایک آندھی ہے جس میں دردناک عذاب،'),
(4535, 46, 25, 'ہر چیز کو تباہ کر ڈالتی ہے اپنے رب کے حکم سے تو صبح رہ گئے کہ نظر نہ آتے تھے مگر ان کے سُونے مکان، ہم ایسی ہی سزا دیتے ہیں مجرموں کو،'),
(4536, 46, 26, 'اور بیشک ہم نے انہیں وہ مقدور دیے تھے جو تم کو نہ دیے اور ان کے لیے کان اور آنکھ اور دل بنائے تو ان کے کام کان اور آنکھیں اور دل کچھ کام نہ آئے جبکہ وہ اللہ کی آیتوں کا انکار کرتے تھے اور انہیں گھیرلیا اس عذاب نے جس کی ہنسی بناتے تھے،'),
(4537, 46, 27, 'اور بیشک ہم نے ہلاک کردیں تمہارے آس پاس کی بستیاں اور طرح طرح کی نشانیاں لائے کہ وہ باز آئیں'),
(4538, 46, 28, 'تو کیوں نہ مدد کی ان کی جن کو انہوں نے اللہ کے سوا قرب حاصل کرنے کو خدا ٹھہرا رکھا تھا بلکہ وہ ان سے گم گئے اور یہ ان کا بہتان و افتراء ہے'),
(4539, 46, 29, 'اور جبکہ ہم نے تمہاری طرف کتنے جن پھیرے کان لگا کر قرآن سنتے، پھر جب وہاں حاضر ہوئے آپس میں بولے خاموش رہو پھر جب پڑھنا ہوچکا اپنی قوم کی طرف ڈر سناتے پلٹے'),
(4540, 46, 30, 'بولے اے ہماری قوم ہم نے ایک کتاب سنی کہ موسیٰ کے بعد اتاری گئی اگلی کتابوں کی تصدیق فرمائی حق اور سیدھی راہ دکھائی،'),
(4541, 46, 31, 'اے ہماری قوم! اللہ کے منادی کی بات مانو اور اس پر ایمان لاؤ کہ وہ تمہارے کچھ گناہ بخش دے اور تمہیں دردناک عذاب سے بچالے،'),
(4542, 46, 32, 'اور جو اللہ کے منادی کی بات نہ مانے وہ زمین میں قابو سے نکل کر جانے والا نہیں اور اللہ کے سامنے اس کا کوئی مددگار نہیں وہ کھلی گمراہی میں ہیں،'),
(4543, 46, 33, 'کیا انہوں نے نہ جانا کہ وہ اللہ جس نے آسمان اور زمین بنائے اور ان کے بنانے میں نہ تھکا قادر ہے کہ مردے جِلائے، کیوں نہیں بیشک وہ سب کچھ کرسکتا ہے،'),
(4544, 46, 34, 'اور جس دن کافر آگ پر پیش کیے جائیں گے، ان سے فرمایا جائے گا کیا یہ حق نہیں کہیں گے کیوں نہیں ہمارے رب کی قسم، فرمایا جائے گا تو عذاب چکھو بدلہ اپنے کفر کا'),
(4545, 46, 35, 'تو تم صبر کرو جیسا ہمت والے رسولوں نے صبر کیا اور ان کے لیے جلدی نہ کرو گویا وہ جس دن دیکھیں گے جو انہیں وعدہ دیا جاتا ہے دنیا میں نہ ٹھہرے تھے مگر دن کی ایک گھڑی بھر یہ پہنچانا ہے تو کون ہلاک کیے جائیں گے مگر بے حکم لوگ');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'جنہوں نے کفر کیا اور اللہ کی راہ سے روکا اللہ نے ان کے عمل برباد کیے'),
(4547, 47, 2, 'اور ایمان لائے اور اچھے کام کیے اور اس پر ایمان لائے جو محمد پر اتارا گیا اور وہی ان کے رب کے پاس سے حق ہے اللہ نے ان کی برائیاں اتار دیں اور ان کی حالتیں سنوار دیں'),
(4548, 47, 3, 'یہ اس لیے کہ کافر باطل کے پیرو ہوئے اور ایمان والوں نے حق کی پیروی کی جو ان کے رب کی طرف سے ہے اللہ لوگوں سے ان کے احوال یونہی بیان فرماتا ہے'),
(4549, 47, 4, 'تو جب کافروں سے تمہارا سامناہو تو گردنیں مارنا ہے یہاں تک کہ جب انہیں خوب قتل کرلو تو مضبوط باندھو، پھر اس کے بعد چاہے احسان کرکے چھوڑ دو چاہے فدیہ لے لو یہاں تک کہ لڑائی اپنابوجھ رکھ دے بات یہ ہے اوراللہ چاہتا تو آپ ہی اُن سے بدلہ لیتا مگر اس لئے تم میں ایک کو دوسرے سے جانچے اور جو اللہ کی راہ میں مارے گئے اللہ ہرگز ان کے عمل ضائع نہ فرمائے گا'),
(4550, 47, 5, 'جلد انہیں راہ دے گا اور ان کا کام بنادے گا،'),
(4551, 47, 6, 'اور انہیں جنت میں لے جائے گا انہیں اس کی پہچان کرادی ہے'),
(4552, 47, 7, 'اے ایمان والو اگر تم دین خدا کی مدد کرو گے اللہ تمہاری مدد کرے گا اور تمہارے قدم جمادے گا'),
(4553, 47, 8, 'اور جنہوں نے کفر کیا تو ان پر تباہی پڑے اور اللہ ان کے اعمال برباد کرے،'),
(4554, 47, 9, 'یہ اس لیے کہ انہیں ناگوار ہوا جو اللہ نے اتارا تو اللہ نے ان کا کیا دھرا اِکارت کیا،'),
(4555, 47, 10, 'تو کیا انہوں نے زمین میں سفر نہ کیا کہ دیکھتے ان سے اگلوں کا کیسا انجام ہوا، اللہ نے ان پر تباہی ڈالی اور ان کافروں کے لیے بھی ویسی کتنی ہی ہیں'),
(4556, 47, 11, 'یہ اس لیے کہ مسلمانوں کا مولیٰ اللہ ہے اور کافروں کا کوئی مولیٰ نہیں،'),
(4557, 47, 12, 'بیشک اللہ داخل فرمائے گا انہیں جو ایمان لائے اور اچھے کام کیے باغوں میں جن کے نیچے نہریں رواں، اور کافر برتتے ہیں اور کھاتے ہیں جیسے چوپائے کھائیں اور آگ میں ان کا ٹھکانا ہے،'),
(4558, 47, 13, 'اور کتنے ہی شہر کہ اس شہر سے قوت میں زیادہ تھے جس نے تمہیں تمہارے شہر سے باہر کیا، ہم نے انہیں ہلاک فرمایا تو ان کا کوئی مددگار نہیں'),
(4559, 47, 14, 'تو کیا جو اپنے رب کی طرف سے روشن دلیل پر ہو اُس جیسا ہوگا جس کے برے عمل اسے بھلے دکھائے گئے اور وہ اپنی خواہشوں کے پیچھے چلے'),
(4560, 47, 15, 'احوال اس جنت کا جس کا وعدہ پرہیزگاروں سے ہے، اس میں ایسی پانی کی نہریں ہیں جو کبھی نہ بگڑے اور ایسے دودھ کی نہریں ہیں جس کا مزہ نہ بدلا اور ایسی شراب کی نہریں ہیں جس کے پینے میں لذت ہے اور ایسی شہد کی نہریں ہیں جو صاف کیا گیا اور ان کے لیے اس میں ہر قسم کے پھل ہیں، اور اپنے رب کی مغفرت کیا ایسے چین والے ان کی برابر ہوجائیں گے جنہیں ہمیشہ آگ میں رہنا اور انہیں کھولتا پانی پلایا جائے گا کہ آنتوں کے ٹکڑے ٹکڑے کردے،'),
(4561, 47, 16, 'اور ان میں سے بعض تمہارے ارشاد سنتے ہیں یہاں تک کہ جب تمہارے پاس سے نکل کر جائیں علم والوں سے کہتے ہیں ابھی انہوں نے کیا فرمایا یہ ہیں وہ جن کے دلوں پر اللہ نے مہر کردی اور اپنی خواہشوں کے تابع ہوئے'),
(4562, 47, 17, 'اور جنہوں نے راہ پائی اللہ نے ان کی ہدایت اور زیادہ فرمائی اور ان کی پرہیزگاری انہیں عطا فرمائی'),
(4563, 47, 18, 'تو کاہے کے انتظار میں ہیں مگر قیامت کے کہ ان پر اچانک آجائے، کہ اس کی علامتیں تو آہی چکی ہیں پھر جب آجائے گی تو کہاں وہ اور کہاں ان کا سمجھنا،'),
(4564, 47, 19, 'تو جان لو کہ اللہ کے سوا کسی کی بندگی نہیں اور اے محبوب! اپنے خاصوں اور عام مسلمان مردوں اور عورتوں کے گناہوں کی معافی مانگو اور اللہ جانتا ہے دن کو تمہارا پھرنا اور رات کو تمہارا آرام لینا'),
(4565, 47, 20, 'اور مسلمان کہتے ہیں کوئی سورت کیوں نہ اتاری گئی پھر جب کوئی پختہ سورت اتاری گئی اور اس میں جہاد کا حکم فرمایا گیا تو تم دیکھو گے انہیں جن کے دلوں میں بیماری ہے کہ تمہاری طرف اس کا دیکھنا دیکھتے ہیں جس پر مُرونی چھائی ہو، تو ان کے حق میں بہتر یہ تھا کہ فرمانبرداری کرتے'),
(4566, 47, 21, 'اور اچھی بات کہتے پھر جب حکم ناطق ہوچکا تو اگر اللہ سے سچے رہتے تو ان کا بھلا تھا،'),
(4567, 47, 22, 'تو کیا تمہارے یہ لچھن نظر آتے ہیں کہ اگر تمہیں حکومت ملے تو زمین میں فساد پھیلاؤ اور اپنے رشتے کاٹ دو،'),
(4568, 47, 23, 'یہ ہیں وہ لوگ جن پر اللہ نے لعنت کی اور انہیں حق سے بہرا کردیا اور ان کی آنکھیں پھوڑ دیں'),
(4569, 47, 24, 'تو کیا وہ قرآن کو سوچتے نہیں یا بعضے دلوں پر ان کے قفل لگے ہیں'),
(4570, 47, 25, 'بیشک وہ جو اپنے پیچھے پلٹ گئے بعد اس کے کہ ہدایت ان پر کھل چکی تھی شیطان نے انہیں فریب دیا اور انہیں دنیا میں مدتوں رہنے کی امید دلائی'),
(4571, 47, 26, 'یہ اس لیے کہ انہوں نے کہا ان لوگوں سے جنہیں اللہ کا اتارا ہوا ناگوار ہے ایک کام میں ہم تمہاری مانیں گے اور اللہ ان کی چھپی ہوئی جانتا ہے،'),
(4572, 47, 27, 'تو کیسا ہوگا جب فرشتے ان کی روح قبض کریں گے ان کے منہ اور ان کی پیٹھیں مارتے ہوئے'),
(4573, 47, 28, 'یہ اس لیے کہ وہ ایسی بات کے تابع ہوئے جس میں اللہ کی ناراضی ہے اور اس کی خوشی انہیں گوارا نہ ہوئی تو اس نے ان کے اعمال اَکارت کردیے،'),
(4574, 47, 29, 'کیا جن کے دلوں میں بیماری ہے اس گھمنڈ میں ہیں کہ اللہ ان کے چھپے بَیر ظاہر نہ فرمائے گا'),
(4575, 47, 30, 'اور اگر ہم چاہیں تو تمہیں ان کو دکھادیں کہ تم ان کی صورت سے پہچان لو اور ضرور تم انہیں بات کے اسلوب میں پہچان لو گے اور اللہ تمہارے عمل جانتا ہے'),
(4576, 47, 31, 'اور ضرور ہم تمہیں جانچیں گے یہاں تک کہ دیکھ لیں تمہارے جہاد کرنے والوں اور صابروں کو اور تمہاری خبریں آزمالیں'),
(4577, 47, 32, 'بیشک وہ جنہوں نے کفر کیا اور اللہ کی راہ سے روکا اور رسول کی مخالفت کی بعد اس کے کہ ہدایت ان پر ظاہر ہوچکی تھی وہ ہرگز اللہ کو کچھ نقصان نہ پہچائیں گے، اور بہت جلد اللہ ان کا کیا دھرا اَکارت کردے گا'),
(4578, 47, 33, 'اے ایمان والو اللہ کا حکم مانو اور رسول کا حکم مانو اور اپنے عمل باطل نہ کرو'),
(4579, 47, 34, 'بیشک جنہوں نے کفر کیا اور اللہ کی راہ سے روکا پھر کافر ہی مرگئے تو اللہ ہر گز انہیں نہ بخشے گا'),
(4580, 47, 35, 'تو تم سستی نہ کرو اور آپ صلح کی طرف نہ بلاؤ اور تم ہی غالب آؤ گے، اور اللہ تمہارے ساتھ ہے اور وہ ہرگز تمہارے اعمال میں تمہیں نقصان نہ دے گا'),
(4581, 47, 36, 'دنیا کی زندگی تو یہی کھیل کود ہے اور اگر تم ایمان لاؤ اور پرہیزگاری کرو تو وہ تم کو تمہارے ثواب عطا فرمائے گا اور کچھ تم سے تمہارے مال نہ مانگے گا'),
(4582, 47, 37, 'اگر انہیں تم سے طلب کرے اور زیادہ طلب کرے تم بخل کرو گے اور وہ بخل تمہارے دلوں کے میل ظاہر کردے گا،'),
(4583, 47, 38, 'ہاں ہاں یہ جو تم ہو بلائے جاتے ہو کہ اللہ کی راہ میں خرچ کرو تو تم میں کوئی بخل کرتا ہے اور جو بخل کرے وہ اپنی ہی جان پر بخل کرتا ہے اور اللہ بے نیاز ہے اور تم سب محتاج اور اگر تم منہ پھیرو تو وہ تمہارے سوا اور لوگ بدل لے گا پھر وہ تم جیسے نہ ہوں گے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'بیشک ہم نے تمہارے لیے روشن فتح دی'),
(4585, 48, 2, 'تاکہ اللہ تمہارے سبب سے گناہ بخشے تمہارے اگلوں کے اور تمہارے پچھلوں کے اور اپنی نعمتیں تم پر تمام کردے اور تمہیں سیدھی راہ دکھادے'),
(4586, 48, 3, 'اور اللہ تمہاری زبردست مدد فرمائے'),
(4587, 48, 4, 'وہی ہے جس نے ایمان والوں کے دلوں میں اطمینان اتارا تاکہ انہیں یقین پر یقین بڑھے اور اللہ ہی کی ملک ہیں تمام لشکر آسمانوں اور زمین کے اور اللہ علم و حکمت والا ہے'),
(4588, 48, 5, 'تاکہ ایمان والے مردوں اور ایمان والی عورتوں کو باغوں میں لے جائے جن کے نیچے نہریں رواں ہمیشہ ان میں رہیں اور انکی برائیاں ان سے اتار دے، اور یہ اللہ کے یہاں بڑی کامیابی ہے،'),
(4589, 48, 6, 'اور عذاب دے منافق مَردوں اور منافق عورتوں اور مشرک مَردوں اور مشرک عورتوں کو جو اللہ پر گمان رکھتے ہیں انہیں پر ہے بری گردش اور اللہ نے اُن پر غضب فرمایا اور انہیں لعنت کی اور ان کے لیے جہنم تیار فرمایا، اور وہ کیا ہی برا انجام ہے،'),
(4590, 48, 7, 'اور اللہ ہی کی ملک ہیں آسمانوں اور زمین کے سب لشکر، اور اللہ عزت و حکمت والا ہے،'),
(4591, 48, 8, 'بیشک ہم نے تمہیں بھیجا حاضر و ناظر اور خوشی اور ڈر سناتا'),
(4592, 48, 9, 'تاکہ اے لوگو تم اللہ اور اس کے رسول پر ایمان لاؤ اور رسول کی تعظیم و توقیر کرو اور صبح و شام اللہ کی پاکی بولو'),
(4593, 48, 10, 'وہ جو تمہاری بیعت کرتے ہیں وہ تو اللہ ہی سے بیعت کرتے ہیں ان کے ہاتھوں پر اللہ کا ہاتھ ہے، تو جس نے عہد توڑا اس نے اپنے بڑے عہد کو توڑا اور جس نے پورا کیا وہ عہد جو اس نے اللہ سے کیا تھا تو بہت جلد اللہ اسے بڑا ثواب دے گا'),
(4594, 48, 11, 'اب تم سے کہیں گے جو گنوار (اعرابی) پیچھے رہ گئے تھے کہ ہمیں ہمارے مال اور ہمارے گھر والوں نے مشغول رکھا اب حضور ہماری مغفرت چاہیں اپنی زبانوں سے وہ بات کہتے ہیں جو ان کے دلوں میں نہیں تم فرماؤ تو اللہ کے سامنے کسے تمہارا کچھ اختیار ہے اگر وہ تمہارا برا چاہے یا تمہاری بھلائی کا ارادہ فرمائے، بلکہ اللہ کو تمہارے کاموں کی خبر ہے،'),
(4595, 48, 12, 'بلکہ تم تو یہ سمجھے ہوئے تھے کہ رسول اور مسلمان ہرگز گھروں کو واپس نہ آئیں گے اور اسی کو اپنے دلوں میں بھلا سمجھیں ہوئے تھے اور تم نے برا گمان کیا اور تم ہلاک ہونے والے لوگ تھے'),
(4596, 48, 13, 'اور جو ایمان نہ لائے اللہ اور اس کے رسول پر تو بیشک ہم نے کافروں کے لیے بھڑکتی آگ تیار کر رکھی ہے،'),
(4597, 48, 14, 'اور اللہ ہی کے لیے ہے آسمانوں اور زمین کی سلطنت، جسے چاہے بخشے اور جسے چاہے عذاب کرے اور اللہ بخشنے والا مہربان ہے،'),
(4598, 48, 15, 'اب کہیں گے پیچھے بیٹھ رہنے والے جب تم غنیمتیں لینے چلو تو ہمیں بھی اپنے پیچھے آنے دو وہ چاہتے ہیں اللہ کا کلام بدل دیں تم فرماؤ ہرگز ہمارے ساتھ نہ آؤ اللہ نے پہلے سے یونہی فرمادیا تو اب کہیں گے بلکہ تم ہم سے جلتے ہو بلکہ وہ بات نہ سمجھتے تھے مگر تھوڑی'),
(4599, 48, 16, 'ان پیچھے رہ گئے ہوئے گنواروں سے فرماؤ عنقریب تم ایک سخت لڑائی والی قوم کی طرف بلائے جاؤ گے کہ ان سے لڑو یا وہ مسلمان ہوجائیں، پھر اگر تم فرمان مانو گے اللہ تمہیں اچھا ثواب دے گا، ور اگر پھر گے جیسے پہلے پھر گئے تو تمھیں درد ناک عذاب دے گا،'),
(4600, 48, 17, 'اندھے پر تنگی نہیں اور نہ لنگڑے پر مضائقہ اور نہ بیمار پر مواخذہ اور جو اللہ اور اس کے رسول کا حکم مانے اللہ اسے باغوں میں لے جائے گا جن کے نیچے نہریں رواں اور جو پھر جائے گا اسے دردناک عذاب فرمائے گا،'),
(4601, 48, 18, 'بیشک اللہ راضی ہوا ایمان والوں سے جب وہ اس پیڑ کے نیچے تمہاری بیعت کرتے تھے تو اللہ نے جانا جو ان کے دلوں میں ہے تو ان پر اطمینان اتارا اور انہیں جلد آنے والی فتح کا انعام دیا'),
(4602, 48, 19, 'اور بہت سی غنیمتیں جن کو لیں، اور اللہ عزت و حکمت والا ہے،'),
(4603, 48, 20, 'اور اللہ نے تم سے وعدہ کیا ہے بہت سی غنیمتوں کا کہ تم لو گے تو تمہیں یہ جلد عطا فرمادی اور لوگوں کے ہاتھ تم سے روک دیے اور اس لیے کہ ایمان والوں کے لیے نشانی ہو اور تمہیں سیدھی راہ دکھائے'),
(4604, 48, 21, 'اور ایک اور جو تمہارے بل (بس) کی نہ تھی وہ اللہ کے قبضہ میں ہے، اور اللہ ہر چیز پر قادر ہے،'),
(4605, 48, 22, 'اور اگر کافر تم سے لڑیں تو ضرور تمہارے مقابلہ سے پیٹھ پھیردیں گے پھر کوئی حمایتی نہ پائیں گے نہ مددگار،'),
(4606, 48, 23, 'اللہ کا دستور ہے کہ پہلے سے چلا آتا ہے اور ہرگز تم اللہ کا دستور بدلتا نہ پاؤ گے،'),
(4607, 48, 24, 'اور وہی ہے جس نے ان کے ہاتھ تم سے روک دیے اور تمہارے ہاتھ ان سے روک دیے وادی مکہ میں بعد اس کے کہ تمہیں ان پر قابو دے دیا تھا، اور اللہ تمہارے کام دیکھتا ہے،'),
(4608, 48, 25, 'وہ وہ ہیں جنہوں نے کفر کیا اور تمہیں مسجدِ حرام سے روکا اور قربانی کے جانور رُکے پڑے اپنی جگہ پہنچنے سے اور اگر یہ نہ ہوتا کچھ مسلمان مرد اور کچھ مسلمان عورتیں جن کی تمہیں خبر نہیں کہیں تم انہیں روند ڈالو تو تمہیں ان کی طرف سے انجانی میں کوئی مکروہ پہنچے تو ہم تمہیں ان کی قتال کی اجازت دیتے ان کا یہ بچاؤ اس لیے ہے کہ اللہ اپنی رحمت میں داخل کرے جسے چاہے، اگر وہ جدا ہوجاتے تو ہم ضرور ان میں کے کافروں کو دردناک عذاب دیتے'),
(4609, 48, 26, 'جبکہ کافروں نے اپنے دلوں میں اَڑ رکھی وہی زمانہٴ جاہلیت کی اَڑ (ضد) تو اللہ نے اپنا اطمینان اپنے رسول اور ایمان والوں پر اتارا اور پرہیزگاری کا کلمہ ان پر لازم فرمایا اور وہ اس کے زیادہ سزاوار اور اس کے اہل تھے اور اللہ سب کچھ جانتا ہے'),
(4610, 48, 27, 'بیشک اللہ نے سچ کردیا اپنے رسول کا سچا خواب بیشک تم ضرور مسجد حرام میں داخل ہوگے اگر اللہ چاہے امن و امان سے اپنے سروں کے بال منڈاتے یا ترشواتے بے خوف، تو اس نے جانا جو تمہیں معلوم نیں تو اس سے پہلے ایک نزدیک آنے والی فتح رکھی'),
(4611, 48, 28, 'وہی ہے جس نے اپنے رسول کو ہدایت اور سچے دین کے ساتھ بھیجا کہ اسے سب دینوں پر غالب کرے اور اللہ کافی ہے گواہ'),
(4612, 48, 29, 'محمد اللہ کے رسول ہیں، اور ان کے ساتھ والے کافروں پر سخت ہیں اور آپس میں نرم دل تو انہیں دیکھے گا رکوع کرتے سجدے میں گرتے اللہ کا فضل و رضا چاہتے، ان کی علامت ان کے چہروں میں ہے سجدوں کے نشان سے یہ ان کی صفت توریت میں ہے، اور ان کی صفت انجیل میں جیسے ایک کھیتی اس نے اپنا پٹھا نکالا پھر اسے طاقت دی پھر دبیز ہوئی پھر اپنی ساق پر سیدھی کھڑی ہوئی کسانوں کو بھلی لگتی ہے تاکہ ان سے کافروں کے دل جلیں، اللہ نے وعدہ کیا ان سے جو ان میں ایمان اور اچھے کاموں والے ہیں بخشش اور بڑے ثواب کا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اے ایمان والو اللہ اور اس کے رسول سے آگے نہ بڑھو اور اللہ سے ڈرو بیشک اللہ سنتا جانتا ہے،'),
(4614, 49, 2, 'اے ایمان والو اپنی آوازیں اونچی نہ کرو اس غیب بتانے والے (نبی) کی آواز سے اور ان کے حضور بات چلا کر نہ کہو جیسے آپس میں ایک دوسرے کے سامنے چلاتے ہو کہ کہیں تمہارے عمل اَکارت نہ ہوجائیں اور تمہیں خبر نہ ہو'),
(4615, 49, 3, 'بیشک وہ جو اپنی آوازیں پست کرتے ہیں رسول اللہ کے پاس وہ ہیں جن کا دل اللہ نے پرہیزگاری کے لیے پرکھ لیا ہے، ان کے لیے بخشش اور بڑا ثواب ہے،'),
(4616, 49, 4, 'بیشک وہ جو تمہیں حجروں کے باہر سے پکارتے ہیں ان میں اکثر بے عقل ہیں'),
(4617, 49, 5, 'اور اگر وہ صبر کرتے یہاں تک کہ تم آپ ان کے پاس تشریف لاتے تو یہ ان کے لیے بہتر تھا، اور اللہ بخشنے والا مہربان ہے'),
(4618, 49, 6, 'اے ایمان والو اگر کوئی فاسق تمہارے پاس کوئی خبر لائے تو تحقیق کرلو کہ کہیں کسی قوم کو بے جانے ایذا نہ دے بیٹھو پھر اپنے کیے پر پچھتاتے رہ جاؤ،'),
(4619, 49, 7, 'اور جان لو کہ تم میں اللہ کے رسول ہیں بہت معاملوں میں اگر یہ تمہاری خوشی کریں تو تم ضرور مشقت میں پڑو لیکن اللہ نے تمہیں ایمان پیارا کردیا ہے اور اسے تمہارے دلوں میں آراستہ کردیا اور کفر اور حکم عدولی اور نافرمانی تمہیں ناگوار کر دی، ایسے ہی لوگ راہ پر ہیں'),
(4620, 49, 8, 'اللہ کا فضل اور احسان، اور اللہ علم و حکمت والا ہے،'),
(4621, 49, 9, 'اور اگر مسلمانوں کے دو گروہ آپس میں لڑیں تو ان میں صلح کراؤ پھر اگر ایک دوسرے پر زیادتی کرے تو اس زیادتی والے سے لڑو یہاں تک کہ وہ اللہ کے حکم کی طرف پلٹ آئے، پھر اگر پلٹ آئے تو انصاف کے ساتھ ان میں اصلاح کردو اور عدل کرو، بیشک عدل والے اللہ کو پیارے ہیں،'),
(4622, 49, 10, 'مسلمان مسلمان بھائی ہیں تو اپنے دو بھائیوں میں صلح کرو اور اللہ سے ڈرو کہ تم پر رحمت ہو'),
(4623, 49, 11, 'اے ایمان والو نہ مَرد مَردوں سے ہنسیں عجب نہیں کہ وہ ان ہنسنے والوں سے بہتر ہوں اور نہ عورتیں عورتوں سے، دور نہیں کہ وہ ان ہنسے والیوں سے بہتر ہوں اور آپس میں طعنہ نہ کرو اور ایک دوسرے کے برے نام نہ رکھو کیا ہی برا نام ہے مسلمان ہوکر فاسق کہلانا اور جو توبہ نہ کریں تو وہی ظالم ہیں،'),
(4624, 49, 12, 'اے ایمان والو بہت گمانوں سے بچو بیشک کوئی گمان گناہ ہوجاتا ہے اور عیب نہ ڈھونڈھو اور ایک دوسرے کی غیبت نہ کرو کیا تم میں کوئی پسند رکھے گا کہ اپنے مرے بھائی کا گوشت کھائے تو یہ تمہیں گوارا نہ ہوگا اور اللہ سے ڈرو بیشک اللہ بہت توبہ قبول کرنے والا مہربان ہے،'),
(4625, 49, 13, 'اے لوگو! ہم نے تمہیں ایک مرد اورایک عورت سے پیدا کیا اور تمہیں شاخیں اور قبیلے کیا کہ آپس میں پہچان رکھو بیشک اللہ کے یہاں تم میں زیادہ عزت والا وہ جو تم میں زیادہ پرہیزگارہے بیشک اللہ جاننے والا خبردار ہے،'),
(4626, 49, 14, 'گنوار بولے ہم ایمان لائے تم فرماؤ تم ایمان تو نہ لائے ہاں یوں کہوں کہ ہم مطیع ہوئے اور ابھی ایمان تمہارے دلوں میں کہاں داخل ہوا اور اگر تم اللہ اور اس کے رسول کی فرمانبرداری کرو گے تو تمہارے کسی عمل کا تمہیں نقصان نہ دے گا بیشک اللہ بخشنے والا مہربان ہے،'),
(4627, 49, 15, 'ایمان والے تو وہی ہیں جو اللہ اور اس کے رسول پر ایمان لائے پھر شک نہ کیا اور اپنی جان اور مال سے اللہ کی راہ میں جہاد کیا وہی سچے ہیں'),
(4628, 49, 16, 'تم فرماؤ کیا تم اللہ کو اپنا دین بتاتے ہو، اور اللہ جانتا ہے جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے اور اللہ سب کچھ جانتا ہے'),
(4629, 49, 17, 'اے محبوب وہ تم پر احسان جتاتے ہیں کہ مسلمان ہوگئے، تم فرماؤ اپنے اسلام کا احسان مجھ پر نہ رکھو بلکہ اللہ تم پر احسان رکھتا ہے کہ اس نے تمہیں اسلام کی ہدایت کی اگر تم سچے ہو'),
(4630, 49, 18, 'بیشک اللہ جانتا ہے آسمانوں اور زمین کے سب غیب، اور اللہ تمہارے کام دیکھ رہا ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'عزت والے قرآن کی قسم'),
(4632, 50, 2, 'بلکہ انھیں اس کا اچنبھا ہوا کہ ان کے پاس انہی میں کا ایک ڈر سنانے والا تشریف لایا تو کافر بولے یہ تو عجیب بات ہے،'),
(4633, 50, 3, 'کیا جب ہم مرجائیں اور مٹی ہوجائیں گے پھر جیئں گے یہ پلٹنا دور ہے'),
(4634, 50, 4, 'ہم جانتے ہیں جو کچھ زمین ان میں سے گھٹاتی ہے اور ہمارے پاس ایک یاد رکھنے والی کتاب ہے'),
(4635, 50, 5, 'بلکہ انہوں نے حق کو جھٹلایا جب وہ ان کے پاس آیا تو وہ ایک مضطرب بے ثبات بات میں ہیں'),
(4636, 50, 6, 'تو کیا انہوں نے اپنے اوپر آسمان کو نہ دیکھا ہم نے اسے کیسے بنایا اور سنوارا اور اس میں کہیں رخنہ نہیں'),
(4637, 50, 7, 'اور زمین کو ہم نے پھیلایا اور اس میں لنگر ڈالے (بھاری وزن رکھے) (ف۹۱۴ اور اس میں ہر بارونق جوڑا اُگایا،'),
(4638, 50, 8, 'سوجھ اور سمجھ ہر رجوع والے بندے کے لیے'),
(4639, 50, 9, 'اور ہم نے آسمان سے برکت والا پانی اتارا تو اس سے باغ اُگائے اور اناج کہ کاٹا جاتا ہے'),
(4640, 50, 10, 'اور کھجور کے لمبے درخت جن کا پکا گابھا،'),
(4641, 50, 11, 'بندووں کی روزی کے لیے اور ہم نے اس سے مردہ شہر جِلایا یونہی قبروں سے تمہارا نکلنا ہے'),
(4642, 50, 12, 'ان سے پہلے جھٹلایا نوح کی قوم اور رس والوں اور ثمود'),
(4643, 50, 13, 'اور عاد اور فرعون اور لوط کے ہم قوموں'),
(4644, 50, 14, 'اور بَن والوں اور تبع کی قوم نے ان میں ہر ایک نے رسولوں کو جھٹلایا تو میرے عذاب کا وعدہ ثابت ہوگیا'),
(4645, 50, 15, 'تو کیا ہم پہلی بار بناکر تھک گئے بلکہ وہ نئے بننے سے شبہ میں ہیں،'),
(4646, 50, 16, 'اور بیشک ہم نے آدمی کو پیدا کیا اور ہم جانتے ہیں جو وسوسہ اس کا نفس ڈالتا ہے اور ہم دل کی رگ سے بھی اس سے زیادہ نزدیک ہیں'),
(4647, 50, 17, 'اور جب اس سے لیتے ہیں دو لینے والے ایک داہنے بیٹھا اور ایک بائیں'),
(4648, 50, 18, 'کوئی بات وہ زبان سے نہیں نکالتا کہ اس کے پاس ایک محافظ تیار نہ بیٹھا ہو'),
(4649, 50, 19, 'اور آئی موت کی سختی حق کے ساتھ یہ ہے جس سے تو بھاگتا تھا،'),
(4650, 50, 20, 'اور صُور پھونکا گیا یہ ہے وعدہٴ عذاب کا دن'),
(4651, 50, 21, 'اور ہر جان یوں حاضر ہوئی کہ اس کے ساتھ ایک ہانکنے والا اور ایک گواہ'),
(4652, 50, 22, 'بیشک تو اس سے غفلت میں تھا تو ہم نے تجھ پر سے پردہ اٹھایا تو آج تیری نگاہ تیز ہے'),
(4653, 50, 23, 'اور اس کا ہمنشین فرشتہ بولا یہ ہے جو میرے پاس حاضر ہے،'),
(4654, 50, 24, 'حکم ہوگا تم دونوں جہنم میں ڈال دو ہر بڑے ناشکرے ہٹ دھرم کو،'),
(4655, 50, 25, 'جو بھلائی سے بہت روکنے والا حد سے بڑھنے والا شک کرنے والا'),
(4656, 50, 26, 'جس نے اللہ کے ساتھ کوئی اور معبود ٹھہرایا تم دونوں اسے سخت عذاب میں ڈالو،'),
(4657, 50, 27, 'اس کے ساتھی شیطان نے کہا ہمارے رب میں نے اسے سرکش نہ کیا ہاں یہ آپ ہی دور کی گمراہی میں تھا'),
(4658, 50, 28, 'فرمائے گا میرے پاس نہ جھگڑو میں تمہیں پہلے ہی عذاب کا ڈر سنا چکا تھا'),
(4659, 50, 29, 'میرے یہاں بات بدلتی نہیں اور نہ میں بندوں پر ظلم کروں،'),
(4660, 50, 30, 'جس دن ہم جہنم سے فرمائیں گے کیا تو بھر گئی وہ عرض کرے گی کچھ اور زیادہ ہے'),
(4661, 50, 31, 'اور پاس لائی جائے گی جنت پرہیزگاروں کے کہ ان سے دور نہ ہوگی'),
(4662, 50, 32, 'یہ ہے وہ جس کا تم وعدہ دیے جاتے ہو ہر رجوع لانے والے نگہداشت والے کے لیے'),
(4663, 50, 33, 'جو رحمن سے بے دیکھے ڈرتا ہے اور رجوع کرتا ہوا دل لایا'),
(4664, 50, 34, 'ان سے فرمایا جائے گا جنت میں جاؤ سلامتی کے ساتھ یہ ہمیشگی کا دن ہے'),
(4665, 50, 35, 'ان کے لیے ہے اس میں جو چاہیں اور ہمارے پاس اس سے بھی زیادہ ہے'),
(4666, 50, 36, 'اور ان سے پہلے ہم نے کتنی سنگتیں (قومیں) ہلاک فرمادیں کہ گرفت میں ان سے سخت تھیں تو شہروں میں کاوشیں کیں ہے کہیں بھاگنے کی جگہ'),
(4667, 50, 37, 'بیشک اس میں نصیحت ہے اس کے لیے جو دِل رکھتا ہو یا کان لگائے اور متوجہ ہو،'),
(4668, 50, 38, 'اور بیشک ہم نے آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے چھ دن میں بنایا، اور تکان ہمارے پاس نہ آئی'),
(4669, 50, 39, 'تو ان کی باتوں پر صبر کرو اور اپنے رب کی تعریف کرتے ہوئے اس کی پاکی بولو سورج چمکنے سے پہلے اور ڈوبنے سے پہلے'),
(4670, 50, 40, 'اور کچھ رات گئے اس کی تسبیح کرو اور نمازوں کے بعد'),
(4671, 50, 41, 'اور کان لگا کر سنو جس دن پکارنے والا پکارے گا ایک پاس جگہ سے'),
(4672, 50, 42, 'جس دن چنگھاڑ سنیں گے حق کے ساتھ، یہ دن ہے قبروں سے باہر آنے کا،'),
(4673, 50, 43, 'بیشک ہم جِلائیں اور ہم ماریں اور ہماری طرف پھرنا ہے'),
(4674, 50, 44, 'جس دن زمین ان سے پھٹے گی تو جلدی کرتے ہوئے نکلیں گے یہ حشر ہے ہم کو آسان،'),
(4675, 50, 45, 'ہم خوب جان رہے ہیں جو وہ کہہ رہے ہیں اور کچھ تم ان پر جبر کرنے والے نہیں تو قرآن سے نصیحت کرو اسے جو میری دھمکی سے ڈرے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'قسم ان کی جو بکھیر کر اڑانے والیاں'),
(4677, 51, 2, 'پھر بوجھ اٹھانے والیاں'),
(4678, 51, 3, 'پھر نرم چلنے والیاں'),
(4679, 51, 4, 'پھر حکم سے بانٹنے والیاں'),
(4680, 51, 5, 'بیشک جس بات کا تمہیں وعدہ دیا جاتا ہے ضروری سچ ہے،'),
(4681, 51, 6, 'اور بیشک انصاف ضرور ہونا'),
(4682, 51, 7, 'آرائش والے آسمان کی قسم'),
(4683, 51, 8, 'تم مختلف بات میں ہو'),
(4684, 51, 9, 'اس قرآن سے وہی اوندھا کیا جاتا ہے جس کی قسمت ہی میں اوندھایا جانا ہو'),
(4685, 51, 10, 'مارے جایں دل سے تراشنے والے'),
(4686, 51, 11, 'جو نشے میں بھولے ہوئے ہیں'),
(4687, 51, 12, 'پوچھتے ہیں انصاف کا دن کب ہوگا'),
(4688, 51, 13, 'اس دن ہوگا جس دن وہ آگ پر تپائے جائیں گے'),
(4689, 51, 14, 'اور فرمایا جائے گا چکھو اپنا تپنا، یہ ہے وہ جس کی تمہیں جلدی تھی'),
(4690, 51, 15, 'بیشک پرہیزگار باغوں اور چشموں میں ہیں'),
(4691, 51, 16, 'اپنے رب کی عطائیں لیتے ہوئے، بیشک وہ اس سے پہلے نیکو کار تھے،'),
(4692, 51, 17, 'وہ رات میں کم سویا کرتے'),
(4693, 51, 18, 'اور پچھلی رات استغفار کرتے'),
(4694, 51, 19, 'اور ان کے مالوں میں حق تھا منگتا اور بے نصیب کا'),
(4695, 51, 20, 'اور زمین میں نشانیاں ہیں یقین والوں کو'),
(4696, 51, 21, 'اور خود تم میں تو کیا تمہیں سوجھتا نہیں،'),
(4697, 51, 22, 'اور آسمان میں تمہارا رزق ہے اور جو تمہیں وعدہ دیا جاتا ہے'),
(4698, 51, 23, 'تو آسمان اور زمین کے رب کی قسم بیشک یہ قرآن حق ہے ویسی ہی زبان میں جو تم بولتے ہو،'),
(4699, 51, 24, 'اے محبوب! کیا تمہارے پاس ابراہیم کے معزز مہمانوں کی خبر آئی'),
(4700, 51, 25, 'جب وہ اس کے پاس آکر بولے سلام کہا، سلام ناشناسا لوگ ہیں'),
(4701, 51, 26, 'پھر اپنے گھر گیا تو ایک فربہ بچھڑا لے آیا'),
(4702, 51, 27, 'پھر اسے ان کے پاس رکھا کہا کیا تم کھاتے نہیں،'),
(4703, 51, 28, 'تو اپنے جی میں ان سے ڈرنے لگا وہ بولے ڈریے نہیں اور اسے ایک علم والے لڑکے کی بشارت دی،'),
(4704, 51, 29, 'اس پر اس کی بی بی چلاتی آئی پھر اپنا ماتھا ٹھونکا اور بولی کیا بڑھیا بانجھ'),
(4705, 51, 30, 'انہوں نے کہا تمہارے رب نے یونہی فرمادیا، اور وہی حکیم دانا ہے،'),
(4706, 51, 31, 'ابراہیم نے فرمایا تو اے فرشتو! تم کس کام سے آئے'),
(4707, 51, 32, 'بولے ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں'),
(4708, 51, 33, 'کہ ان پر گارے کے بنائے ہوئے پتھر چھوڑیں،'),
(4709, 51, 34, 'جو تمہارے رب کے پاس حد سے بڑھنے والوں کے لیے نشان کیے رکھے ہیں'),
(4710, 51, 35, 'تو ہم نے اس شہر میں جو ایمان والے تھے نکال لیے،'),
(4711, 51, 36, 'تو ہم نے وہاں ایک ہی گھر مسلمان پایا'),
(4712, 51, 37, 'اور ہم نے اس میں نشانی باقی رکھی ان کے لیے جو دردناک عذاب سے ڈرتے ہیں'),
(4713, 51, 38, 'اور موسیٰ میں جب ہم نے اسے روشن سند لے کر فرعون کے پاس بھیجا'),
(4714, 51, 39, 'تو اپنے لشکر سمیت پھر گیا اورت بولا جادوگر ہے یا دیوانہ،'),
(4715, 51, 40, 'تو ہم نے اسے اور اس کے لشکر کو پکڑ کر دریا میں ڈال دیا اس حال میں کہ وہ اپنے آپ کو ملامت کررہا تھا'),
(4716, 51, 41, 'اور عاد میں جب ہم نے ان پر خشک آندھی بھیجی'),
(4717, 51, 42, 'جس چیز پر گزرتی اسے گلی ہوئی چیز کی طرح چھوڑتی'),
(4718, 51, 43, 'اور ثمود میں جب ان سے فرمایا گیا ایک وقت تک برت لو'),
(4719, 51, 44, 'تو انہوں نے اپنے رب کے حکم سے سرکشی کی تو ان کی آنکھوں کے سامنے انہیں کڑک نے آلیا'),
(4720, 51, 45, 'تو وہ نہ کھڑے ہوسکے اور نہ وہ بدلہ لے سکتے تھے،'),
(4721, 51, 46, 'اور ان سے پہلے قوم نوح کو ہلاک فرمایا، بیشک وہ فاسق لوگ تھے،'),
(4722, 51, 47, 'اور آسمان کو ہم نے ہاتھوں سے بنایا اور بیشک ہم وسعت دینے والے ہیں'),
(4723, 51, 48, 'اور زمین کو ہم نے فرش کیا تو ہم کیا ہی اچھے بچھالے والے،'),
(4724, 51, 49, 'اور ہم نے ہر چیز کے دو جوڑ بنائے کہ تم دھیان کرو'),
(4725, 51, 50, 'تو اللہ کی طرف بھاگو بیشک میں اس کی طرف سے تمہارے لیے صریح ڈر سنانے والا ہوں،'),
(4726, 51, 51, 'اور اللہ کے ساتھ اور معبود نہ ٹھہراؤ، بیشک میں اس کی طرف سے تمہارے لیے صریح ڈر سنانے والا ہوں،'),
(4727, 51, 52, 'یونہی جب ان سے اگلوں کے پاس کوئی رسول تشریف لایا تو یہی بولے کہ جادوگر ہے یا دیوانہ،'),
(4728, 51, 53, 'کیا آپس میں ایک دوسرے کو یہ بات کہہ مرے ہیں بلکہ وہ سرکش لوگ ہیں'),
(4729, 51, 54, 'تو اے محبوب! تم ان سے منہ پھیر لو تو تم پر کچھ الزام نہیں'),
(4730, 51, 55, 'اور سمجھاؤ کہ سمجھانا مسلمانوں کو فائدہ دیتا ہے،'),
(4731, 51, 56, 'اور میں نے جن اور آدمی اتنے ہی لیے بنائے کہ میری بندگی کریں'),
(4732, 51, 57, 'میں ان سے کچھ رزق نہیں مانگتا اور نہ یہ چاہتا ہوں کہ وہ مجھے کھانا دیں'),
(4733, 51, 58, 'بیشک اللہ ہی بڑا رزق دینے والا قوت والا قدرت والا ہے'),
(4734, 51, 59, 'تو بیشک ان ظالموں کے لیے عذاب کی ایک باری ہے جیسے ان کے ساتھ والوں کے لیے ایک باری تھی تو مجھ سے جلدی نہ کریں'),
(4735, 51, 60, 'تو کافروں کی خرابی ہے ان کے اس دن سے جس کا وعدہ دیے جاتے ہیں');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'طور کی قسم'),
(4737, 52, 2, 'اور اس نوشتہ کی'),
(4738, 52, 3, 'جو کھلے دفتر میں لکھا ہے'),
(4739, 52, 4, 'اور بیت معمور'),
(4740, 52, 5, 'اور بلند چھت'),
(4741, 52, 6, 'اور سلگائے ہوئے سمندر کی'),
(4742, 52, 7, 'بیشک تیرے رب کا عذاب ضرور ہونا ہے'),
(4743, 52, 8, 'اسے کوئی ٹالنے والا نہیں'),
(4744, 52, 9, 'جس دن آسمان ہلنا سا ہلنا ہلیں گے'),
(4745, 52, 10, 'اور پہاڑ چلنا سا چلنا چلیں گے'),
(4746, 52, 11, 'تو اس دن جھٹلانے والوں کی خرابی ہے'),
(4747, 52, 12, 'وہ جو مشغلہ میں کھیل رہے ہیں،'),
(4748, 52, 13, 'جس دن جہنم کی طرف دھکا دے کر دھکیلے جائیں گے'),
(4749, 52, 14, 'یہ ہے وہ آگ جسے تم جھٹلاتے تھے'),
(4750, 52, 15, 'تو کیا یہ جادو ہے یا تمہیں سوجھتا نہیں'),
(4751, 52, 16, 'اس میں جاؤ اب چاہے صبر کرو یا نہ کرو، سب تم پر ایک سا ہے تمہیں اسی کا بدلہ جو تم کرتے تھے'),
(4752, 52, 17, 'بیشک پرہیزگار باغوں اور چین میں ہیں'),
(4753, 52, 18, 'اپنے رب کے دین پر شاد شاد اور انہیں ان کے رب نے آگ کے عذاب سے بچالیا'),
(4754, 52, 19, 'کھاؤ اور پیو خوشگواری سے صِلہ اپنے اعمال کا'),
(4755, 52, 20, 'تختوں پر تکیہ لگائے جو قطار لگا کر بچھے ہیں اور ہم نے انہیں بیاہ دیا بڑی آنکھوں والی حوروں سے،'),
(4756, 52, 21, 'اور جو ایمان لائے اور ان کی اولاد نے ایمان کے ساتھ ان کی پیروی کی ہم نے ان کی اولاد ان سے ملادی اور ان کے عمل میں انہیں کچھ کمی نہ دی سب آدمی اپنے کیے میں گرفتار ہیں'),
(4757, 52, 22, 'اور ہم نے ان کی مدد فرمائی میوے اور گوشت سے جو چاہیں'),
(4758, 52, 23, 'ایک دوسرے سے لیتے ہیں وہ جام جس میں نہ بیہودگی اور گنہگاری'),
(4759, 52, 24, 'اور ان کے خدمت گار لڑکے ان کے گرد پھریں گے گویا وہ موتی ہیں چھپا کر رکھے گئے'),
(4760, 52, 25, 'اور ان میں ایک نے دوسرے کی طرف منہ کیا پوچھتے ہوئے'),
(4761, 52, 26, 'بولے بیشک ہم اس سے پہلے اپنے گھروں میں سہمے ہوئے تھے'),
(4762, 52, 27, 'تو اللہ نے ہم پر احسان کیا اور ہمیں لُو کے عذاب سے بچالیا'),
(4763, 52, 28, 'بیشک ہم نے اپنی پہلی زندگی میں اس کی عبادت کی تھی، بیشک وہی احسان فرمانے والا مہربان ہے،'),
(4764, 52, 29, 'تو اے محبوب! تم نصیحت فرماؤ کہ تم اپنے رب کے فضل سے نہ کاہن ہو نہ مجنون،'),
(4765, 52, 30, 'یا کہتے ہیں یہ شاعر ہیں ہمیں ان پر حوادثِ زمانہ کا انتظارہے'),
(4766, 52, 31, 'تم فرماؤ انتظار کیے جاؤ میں بھی تمہارے انتظار میں ہوں'),
(4767, 52, 32, 'کیا ان کی عقلیں انہیں یہی بتاتی ہیں یا وہ سرکش لوگ ہیں'),
(4768, 52, 33, 'یا کہتے ہیں انہوں نے یہ قرآن بنالیا، بلکہ وہ ایمان نہیں رکھتے'),
(4769, 52, 34, 'تو اس جیسی ایک بات تو لے آئیں اگر سچے ہیں،'),
(4770, 52, 35, 'کیا وہ کسی اصل سے نہ بنائے گئے یا وہی بنانے والے ہیں'),
(4771, 52, 36, 'یا آسمان اور زمین انہوں نے پیدا کیے بلکہ انہیں یقین نہیں'),
(4772, 52, 37, 'یا ان کے پاس تمہارے رب کے خزانے ہیں یا وہ کڑوڑے (حاکمِ اعلیٰ) ہیں'),
(4773, 52, 38, 'یا ان کے پاس کوئی زینہ ہے جس میں چڑھ کر سن لیتے ہیں تو ان کا سننے والا کوئی روشن سند لائے،'),
(4774, 52, 39, 'کیا اس کو بیٹیاں اور تم کو بیٹے'),
(4775, 52, 40, 'یا تم ان سے کچھ اجرت مانگتے ہہو تو وہ چٹی کے بوجھ میں دبے ہیں'),
(4776, 52, 41, 'یا ان کے پاس غیب ہیں جس سے وہ حکم لگاتے ہیں'),
(4777, 52, 42, 'یا کسی داؤ ں (فریب) کے ارادہ میں ہیں تو کافروں پر ہی داؤں (فریب) پڑنا ہے'),
(4778, 52, 43, 'یا اللہ کے سوا ان کا کوئی اور خدا ہے اللہ کو پاکی ان کے شرک سے،'),
(4779, 52, 44, 'اور اگر آسمان سے کوئی ٹکڑا گرتا دیکھیں تو کہیں گے تہ بہ تہ بادل ہے'),
(4780, 52, 45, 'تو تم انہیں چھوڑ دو یہاں تک کہ وہ اپنے اس دن سے ملیں جس میں بے ہوش ہوں گے'),
(4781, 52, 46, 'جس دن ان کا داؤ ں (فریب) کچھ کام نہ دے گا اور نہ ان کی مدد ہو'),
(4782, 52, 47, 'اور بیشک ظالموں کے لیے اس سے پہلے ایک عذاب ہے مگر ان میں اکثر کو خبر نہیں'),
(4783, 52, 48, 'اور اے محبوب! تم اپنے رب کے حکم پر ٹھہرے رہو کہ بیشک تم ہماری نگہداشت میں ہو اور اپنے رب کی تعریف کرتے ہوئے اس کی پاکی بولو جب تم کھڑے ہو'),
(4784, 52, 49, 'اور کچھ رات میں اس کی پاکی بولو اور تاروں کے پیٹھ دیتے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'اس پیارے چمکتے تارے محمد کی قسم! جب یہ معراج سے اترے'),
(4786, 53, 2, 'تمہارے صاحب نہ بہکے نہ بے راہ چلے'),
(4787, 53, 3, 'اور وہ کوئی بات اپنی خواہش سے نہیں کرتے،'),
(4788, 53, 4, 'وہ تو نہیں مگر وحی جو انہیں کی جاتی ہے'),
(4789, 53, 5, 'انہیں سکھایا سخت قوتوں والے طاقتور نے'),
(4790, 53, 6, 'پھر اس جلوہ نے قصد فرمایا'),
(4791, 53, 7, 'اور وہ آسمان بریں کے سب سے بلند کنارہ پر تھا'),
(4792, 53, 8, 'پھر وہ جلوہ نزدیک ہوا پھر خوب اتر آیا'),
(4793, 53, 9, 'تو اس جلوے اور اس محبوب میں دو ہاتھ کا فاصلہ رہا بلکہ اس سے بھی کم'),
(4794, 53, 10, 'اب وحی فرمائی اپنے بندے کو جو وحی فرمائی'),
(4795, 53, 11, 'دل نے جھوٹ نہ کہا جو دیکھا'),
(4796, 53, 12, 'تو کیا تم ان سے ان کے دیکھے ہوئے پر جھگڑ تے ہو'),
(4797, 53, 13, 'اور انہوں نے تو وہ جلوہ دوبار دیکھا'),
(4798, 53, 14, 'سدرة المنتہیٰ کے پاس'),
(4799, 53, 15, 'اس کے پاس جنت الماویٰ ہے،'),
(4800, 53, 16, 'جب سدرہ پر چھا رہا تھا جو چھا رہا تھا'),
(4801, 53, 17, 'آنکھ نہ کسی طرف پھر نہ حد سے بڑھی'),
(4802, 53, 18, 'بیشک اپنے رب کی بہت بڑی نشانیاں دیکھیں'),
(4803, 53, 19, 'تو کیا تم نے دیکھ ا لات اور عزیٰ'),
(4804, 53, 20, 'اور اس تیسری منات کو'),
(4805, 53, 21, 'کیا تم کو بیٹا اور اس کو بیٹی'),
(4806, 53, 22, 'جب تو یہ سخت بھونڈی تقسیم ہے'),
(4807, 53, 23, 'وہ تو نہیں مگر کچھ نام کہ تم نے اور تمہارے باپ دادا نے رکھ لیے ہیں اللہ نے ان کی کوئی سند نہیں اتاری، وہ تو نرے گمان اور نفس کی خواہشوں کے پیچھے ہیں حالانکہ بیشک ان کے پاس ان کے رب کی طرف سے ہدایت آئی'),
(4808, 53, 24, 'کیا آدمی کو مل جائے گا جو کچھ وہ خیال باندھے'),
(4809, 53, 25, 'تو آخرت اور دنیا سب کا مالک اللہ ہی ہے'),
(4810, 53, 26, 'اور کتنے ہی فرشتے ہیں آسمانوں میں کہ ان کی سفارش کچھ کام نہیں آتی مگر جبکہ اللہ اجازت دے دے جس کے لیے چاہے اور پسند فرمائے'),
(4811, 53, 27, 'بیشک وہ جو آخرت پر ایمان رکھتے نہیں ملائکہ کا نام عورتوں کا سا رکھتے ہیں'),
(4812, 53, 28, 'اور انہیں اس کی کچھ خبر نہیں، وہ تو نرے گمان کے پیچھے ہیں، اور بیشک گمان یقین کی جگہ کچھ کام نہیں دیتا'),
(4813, 53, 29, 'تو تم اس سے منہ پھیر لو، جو ہماری یاد سے پھرا اور اس نے نہ چاہی مگر دنیا کی زندگی'),
(4814, 53, 30, 'یہاں تک ان کے علم کی پہنچ ہے بیشک تمہارا خوب جانتا ہے جو اس کی راہ سے بہکا اور وہ خوب جانتا ہے جس نے راہ پائی،'),
(4815, 53, 31, 'اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں تاکہ برائی کرنے والوں کو ان کے کیے کا بدلہ دے اور نیکی کرنے والوں کو نہایت اچھا صلہ عطا فرمائے،'),
(4816, 53, 32, 'وہ جو بڑے گناہوں اور بے حیائیوں سے بچتے ہیں مگر اتنا کہ گناہ کے پاس گئے اور رک گئے بیشک تمہارے رب کی مغفرت وسیع ہے، وہ تمہیں خوب جانتا ہے تمہیں مٹی سے پیدا کیا اور جب تم اپنی ماؤں کے پیٹ میں حمل تھے، تو آپ اپنی جانوں کو ستھرا نہ بتاؤ وہ خوب جانتا ہے جو پرہیزگار ہیں'),
(4817, 53, 33, 'تو کیا تم نے دیکھا جو پھر گیا'),
(4818, 53, 34, 'اور کچھ تھوڑا سا دیا اور روک رکھا'),
(4819, 53, 35, 'کیا اس کے پاس غیب کا علم ہے تو وہ دیکھ رہا ہے'),
(4820, 53, 36, 'کیا اسے اس کی خبر نہ آئی جو صحیفوں میں ہے موسیٰ کے'),
(4821, 53, 37, 'اور ابراہیم کے جو پورے احکام بجالایا'),
(4822, 53, 38, 'کہ کوئی بوجھ اٹھانے والی جان دوسری کا بوجھ نہیں اٹھاتی'),
(4823, 53, 39, 'اور یہ کہ آدمی نہ پاے گا مگر اپنی کوشش'),
(4824, 53, 40, 'اور یہ کہ اس کی کو شش عنقر یب دیکھی جاے گی'),
(4825, 53, 41, 'پھر اس کا بھرپور بدلا دیا جائے گا'),
(4826, 53, 42, 'اور یہ کہ بیشک تمہارے رب ہی کی طرف انتہا ہے'),
(4827, 53, 43, 'اور یہ کہ وہی ہے جس نے ہنسایا اور رلایا'),
(4828, 53, 44, 'اور یہ کہ وہی ہے جس نے مارا اور جِلایا'),
(4829, 53, 45, 'اور یہ کہ اسی نے دو جوڑے بنائے نر اور مادہ'),
(4830, 53, 46, 'نطفہ سے جب ڈالا جائے'),
(4831, 53, 47, 'اور یہ کہ اسی کے ذمہ ہے پچھلا اٹھانا (دوبارہ زندہ کرنا)'),
(4832, 53, 48, 'اور یہ کہ اسی نے غنیٰ دی اور قناعت دی'),
(4833, 53, 49, 'او ریہ کہ وہی ستارہ شِعریٰ کا رب ہے'),
(4834, 53, 50, 'اور یہ کہ اسی نے پہلی عاد کو ہلاک فرمایا'),
(4835, 53, 51, 'اور ثمود کو تو کوئی باقی نہ چھوڑا'),
(4836, 53, 52, 'اور ان سے پہلے نوح کی قوم کو بیشک وہ ان سے بھی ظالم اور سرکش تھے'),
(4837, 53, 53, 'اور اس نے الٹنے والی بستی کو نیچے گرایا'),
(4838, 53, 54, 'تو اس پر چھایا جو کچھ چھایا'),
(4839, 53, 55, 'تو اے سننے والے اپنے رب کی کون سی نعمتوں میں شک کرے گا،'),
(4840, 53, 56, 'یہ ایک ڈر سنانے والے ہیں اگلے ڈرانے والوں کی طرح'),
(4841, 53, 57, 'پاس آئی پاس آنے والی'),
(4842, 53, 58, 'اللہ کے سوا اس کا کوئی کھولنے والا نہیں'),
(4843, 53, 59, 'تو کیا اس بات سے تم تعجب کرتے ہو'),
(4844, 53, 60, 'اور ہنستے ہو اور روتے نہیں'),
(4845, 53, 61, 'اور تم کھیل میں پڑے ہو،'),
(4846, 53, 62, 'تو اللہ کے لیے سجدہ اور اس کی بندگی کرو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'پاس آئی قيامت اور شق ہوگیا چاند'),
(4848, 54, 2, 'اور اگر دیکھیں کوئی نشانی تو منہ پھیرتے اور کہتے ہیں یہ تو جادو ہے چلا آتا،'),
(4849, 54, 3, 'اور انہوں نے جھٹلایا اور اپنی خواہشوں کے پیچھے ہوئے اور ہر کام قرار پاچکا ہے'),
(4850, 54, 4, 'اور بیشک ان کے پاس وہ خبریں آئیں جن میں کافی روک تھی'),
(4851, 54, 5, 'انتہاء کو پہنچی ہوئی حکمت پھر کیا کام دیں ڈر سنانے والے،'),
(4852, 54, 6, 'تو تم ان سے منہ پھیرلو جس دن بلانے والا ایک سخت بے پہچانی بات کی طرف بلائے گا'),
(4853, 54, 7, 'نیچی آنکھیں کیے ہوئے قبروں سے نکلیں گے گویا وہ ٹڈی ہیں پھیلی ہوئی'),
(4854, 54, 8, 'بلانے والے کی طرف لپکتے ہوئے کافر کہیں گے یہ دن سخت ہے،'),
(4855, 54, 9, 'ان سے پہلے نوح کی قوم نے جھٹلایا تو ہمارے بندہ کو جھوٹا بتایا اور بولے وہ مجنون ہے اور اسے جھڑکا'),
(4856, 54, 10, 'تو اس نے اپنے رب سے دعا کی کہ میں مغلوب ہوں تو میرا بدلہ لے،'),
(4857, 54, 11, 'تو ہم نے آسمان کے دروازے کھول دیے زور کے بہتے پانی سے'),
(4858, 54, 12, 'اور زمین چشمے کرکے بہا دی تو دونوں پانی مل گئے اس مقدار پر جو مقدر تھی'),
(4859, 54, 13, 'اور ہم نے نوح کو سوار کیا تختوں اور کیلوں والی پر کہ'),
(4860, 54, 14, 'ہماری نگاہ کے روبرو بہتی اس کے صلہ میں جس کے ساتھ کفر کیا گیا تھا،'),
(4861, 54, 15, 'اور ہم نے اس نشانی چھوڑا تو ہے کوئی دھیان کرنے والا'),
(4862, 54, 16, 'تو کیسا ہوا میرا عذاب اور میری دھمکیاں،'),
(4863, 54, 17, 'اور بیشک ہم نے قرآن یاد کرنے کے لیے آسان فرمادیا تو ہے کوئی یاد کرنے والا'),
(4864, 54, 18, 'عاد نے جھٹلایا تو کیسا ہوا میرا عذاب اور میرے ڈر دلانے کے فرمان'),
(4865, 54, 19, 'بیشک ہم نے ان پر ایک سخت آندھی بھیجی ایسے دن میں جس کی نحوست ان پر ہمیشہ کے لیے رہی'),
(4866, 54, 20, 'لوگوں کو یوں دے مارتی تھی کہ گویا اکھڑی ہوئی کھجوروں کے ڈنڈ (سوکھے تنے) ہیں'),
(4867, 54, 21, 'تو کیا کیسا ہوا میرا عذاب اور ڈر کے فرمان،'),
(4868, 54, 22, 'اور بیشک ہم نے آسان کیا قرآن یاد کرنے کے لیے تو ہے کوئی یاد کرنے والا،'),
(4869, 54, 23, 'ثمود نے رسولوں کو جھٹلایا'),
(4870, 54, 24, 'تو بولے کیا ہم اپنے میں کے ایک آدمی کی تابعداری کریں جب تو ہم ضرور گمراہ اور دیوانے ہیں'),
(4871, 54, 25, 'کیا ہم سب میں سے اس پر بلکہ یہ سخت جھوٹا اترونا (شیخی باز) ہے'),
(4872, 54, 26, 'بہت جلد کل جان جائیں گے کون تھا بڑا جھوٹا اترونا (شیخی باز)'),
(4873, 54, 27, 'ہم ناقہ بھیجنے والے ہیں انکی جانچ کو تو اے صا لح! تو راہ دیکھ اور صبر کر'),
(4874, 54, 28, 'اور انہیں خبر دے دے کہ پانی ان میں حصوں سے ہے ہر حصہ پر وہ حاضر ہو جس کی باری ہے'),
(4875, 54, 29, 'تو انہوں نے اپنے ساتھی کو پکارا تو اس نے لے کر اس کی کونچیں کاٹ دیں'),
(4876, 54, 30, 'پھر کیسا ہوا میرا عذاب اور ڈر کے فرمان'),
(4877, 54, 31, 'بیشک ہم نے ان پر ایک چنگھاڑ بھیجی جبھی وہ ہوگئے جیسے گھیرا بنانے والے کی بچی ہوئی گھاس سوکھی روندی ہوئی'),
(4878, 54, 32, 'اور بیشک ہم نے آسان کیا قرآن یاد کرنے کے لیے تو ہے کوئی یاد کرنے والا،'),
(4879, 54, 33, 'لوط کی قوم نے رسولوں کو جھٹلایا،'),
(4880, 54, 34, 'بیشک ہم نے ان پر پتھراؤ بھیجا سوائے لو ط کے گھر والوں کے ہم نے انہیں پچھلے پہر بچالیا،'),
(4881, 54, 35, 'اپنے پاس کی نعمت فرماکر، ہم یونہی صلہ دیتے ہیں اسے جو شکر کرے'),
(4882, 54, 36, 'اور بیشک اس نے انہیں ہماری گرفت سے ڈرایا تو انہوں نے ڈر کے فرمانوں میں شک کیا'),
(4883, 54, 37, 'انہوں نے اسے اس کے مہمانوں سے پھسلانا چاہا تو ہم نے ان کی آنکھیں میٹ دی (چوپٹ کردیں) فرمایا چکھو میرا عذاب اور ڈر کے فرمان'),
(4884, 54, 38, 'اور بیشک صبح تڑکے ان پر ٹھہرنے والا عذاب آیا'),
(4885, 54, 39, 'تو چکھو میرا عذاب اور ڈر کے فرمان،'),
(4886, 54, 40, 'اور بیشک ہم نے آسان کیا قرآن یاد کرنے کے لیے تو ہے کوئی یاد کرنے والا،'),
(4887, 54, 41, 'اور بیشک فرعون والوں کے پاس رسول آئے'),
(4888, 54, 42, 'انہوں نے ہماری سب نشانیاں جھٹلائیں تو ہم نے ان پر گرفت کی جو ایک عزت والے اور عظیم قدرت والے کی شان تھی،'),
(4889, 54, 43, 'کیا تمہارے کافر ان سے بہتر ہیں یا کتابوں میں تمہاری چھٹی لکھی ہوئی ہے'),
(4890, 54, 44, 'یا یہ کہتے ہیں کہ ہم سب مل کر بدلہ لے لیں گے'),
(4891, 54, 45, 'اب بھگائی جاتی ہے یہ جماعت اور پیٹھیں پھیردیں گے'),
(4892, 54, 46, 'بلکہ ان کا وعدہ قیامت پر ہے اور قیامت نہایت کڑوی اور سخت کڑوی'),
(4893, 54, 47, 'بیشک مجرم گمراہ اور دیوانے ہیں'),
(4894, 54, 48, 'جس دن آگ میں اپنے مونہوں پر گھسیٹے جائیں گے اور فرمایا جائے گا، چکھو دوزخ کی آنچ،'),
(4895, 54, 49, 'بیشک ہم نے ہر چیز ایک اندازہ سے پیدا فرمائی'),
(4896, 54, 50, 'اور ہمارا کام تو ایک بات کی بات ہے جیسے پلک مارنا'),
(4897, 54, 51, 'اور بیشک ہم نے تمہاری وضع کے ہلاک کردیے تو ہے کوئی دھیان کرنے والا'),
(4898, 54, 52, 'اور انہوں نے جو کچھ کیا سب کتابوں میں ہے'),
(4899, 54, 53, 'اور ہر چھوٹی بڑی چیز لکھی ہوئی ہے'),
(4900, 54, 54, 'بیشک پرہیزگار باغوں اور نہر میں ہیں،'),
(4901, 54, 55, 'سچ کی مجلس میں عظیم قدرت والے بادشاہ کے حضور');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'رحمٰن'),
(4903, 55, 2, 'نے اپنے محبوب کو قرآن سکھایا'),
(4904, 55, 3, 'انسانیت کی جان محمد کو پیدا کیا،'),
(4905, 55, 4, 'ما کان وما یکون کا بیان انہیں سکھایا'),
(4906, 55, 5, 'سورج اور چاند حساب سے ہیں'),
(4907, 55, 6, 'اور سبزے اور پیڑ سجدہ کرتے ہیں'),
(4908, 55, 7, 'اور آسمان کو اللہ نے بلند کیا اور ترازو رکھی'),
(4909, 55, 8, 'کہ ترازو میں بے اعتدالی نہ کرو'),
(4910, 55, 9, 'اور انصاف کے ساتھ تول قائم کرو اور وزن نہ گھٹاؤ،'),
(4911, 55, 10, 'اور زمین رکھی مخلوق کے لیے'),
(4912, 55, 11, 'اس میں میوے اور غلاف والی کھجوریں'),
(4913, 55, 12, 'اور بھُس کے ساتھ اناج اور خوشبو کے پھول،'),
(4914, 55, 13, 'تو اے جن و انس! تم دونوں اپنے رب کی کون سی نعمت کو جھٹلاؤ گے'),
(4915, 55, 14, 'اس نے آدمی کو بنا یا بجتی مٹی سے جیسے ٹھیکری'),
(4916, 55, 15, 'اور جن کو پیدا فرمایا آگ کے لُوکے (لپیٹ) سے'),
(4917, 55, 16, 'تو تم دونوں اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4918, 55, 17, 'دونوں پورب کا رب اور دونوں پچھم کا رب'),
(4919, 55, 18, 'تو تم دونوں اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4920, 55, 19, 'اس نے دو سمندر بہائے کہ دیکھنے میں معلوم ہوں ملے ہوئے'),
(4921, 55, 20, 'اور ہے ان میں روک کہ ایک دوسرے پر بڑھ نہیں سکتا'),
(4922, 55, 21, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4923, 55, 22, 'ان میں سے موتی اور مونگا نکلتا ہے،'),
(4924, 55, 23, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4925, 55, 24, 'اور اسی کی ہیں وہ چلنے والیاں کہ دریا میں اٹھی ہوئی ہیں جیسے پہاڑ'),
(4926, 55, 25, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4927, 55, 26, 'زمین پر جتنے ہیں سب کو فنا ہے'),
(4928, 55, 27, 'اور باقی ہے تمہارے رب کی ذات عظمت اور بزرگی والا'),
(4929, 55, 28, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4930, 55, 29, 'اسی کے منگتا ہیں جتنے آسمانوں اور زمین میں ہیں اسے ہر دن ایک کام ہے'),
(4931, 55, 30, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4932, 55, 31, 'جلد سب کام نبٹا کر ہم تمہارے حساب کا قصد فرماتے ہیں اے دونوں بھاری گروہ'),
(4933, 55, 32, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4934, 55, 33, 'اے جن و انسان کے گروہ اگر تم سے ہوسکے کہ آسمانوں اور زمین کے کناروں سے نکل جاؤ تو نکل جاؤ، جہاں نکل کر جاؤ گے اسی کی سلطنت ہے'),
(4935, 55, 34, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4936, 55, 35, 'تم پر چھوڑی جائے گی بے دھویں کی آگ کی لپٹ اور بے لپٹ کا کالا دھواں تو پھر بدلا نہ لے سکو گے'),
(4937, 55, 36, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4938, 55, 37, 'پھر جب آسمان پھٹ ائے گا تو گلاب کے پھول کا سا ہوجائے گا جیسے سرخ نری (بکرے کی رنگی ہوئی کھال)'),
(4939, 55, 38, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4940, 55, 39, 'تو اس دن گنہگار کے گناہ کی پوچھ نہ ہوگی کسی آدمی اور جِن سے'),
(4941, 55, 40, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4942, 55, 41, 'مجرم اپنے چہرے سے پہچانے جائیں گے تو ماتھا اور پاؤں پکڑ کر جہنم میں ڈالے جائیں گے'),
(4943, 55, 42, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے'),
(4944, 55, 43, 'یہ ہے وہ جہنم جسے مجرم جھٹلاتے ہیں،'),
(4945, 55, 44, 'پھیرے کریں گے اس میں اور انتہا کے جلتے کھولتے پانی میں'),
(4946, 55, 45, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4947, 55, 46, 'اور جو اپنے رب کے حضور کھڑے ہونے سے ڈرے اس کے لیے دو جنتیں ہیں'),
(4948, 55, 47, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4949, 55, 48, 'بہت سی ڈالوں والیاں'),
(4950, 55, 49, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4951, 55, 50, 'ان میں دو چشمے بہتے ہیں'),
(4952, 55, 51, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4953, 55, 52, 'ان میں ہر میوہ دو دو قسم کا،'),
(4954, 55, 53, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4955, 55, 54, 'اور ایسے بچھونوں پر تکیہ لگائے جن کا اَستر قناویز کا اور دونوں کے میوے اتنے جھکے ہوئے کہ نیچے سے چن لو'),
(4956, 55, 55, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4957, 55, 56, 'ان بچھونوں پر وہ عورتیں ہیں کہ شوہر کے سوا کسی کو آنکھ اٹھا کر نہیں دیکھتیں ان سے پہلے انہیں نہ چھوا کسی آدمی اور نہ جِن نے،'),
(4958, 55, 57, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4959, 55, 58, 'گویا وہ لعل اور یاقوت اور مونگا ہیں'),
(4960, 55, 59, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4961, 55, 60, 'نیکی کا بدلہ کیا ہے مگر نیکی'),
(4962, 55, 61, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4963, 55, 62, 'اور ان کے سوا دو جنتیں اور ہیں'),
(4964, 55, 63, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4965, 55, 64, 'نہایت سبزی سے سیاہی کی جھلک دے رہی ہیں،'),
(4966, 55, 65, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4967, 55, 66, 'ان میں دو چشمے ہیں چھلکتے ہوئے،'),
(4968, 55, 67, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4969, 55, 68, 'ان میں میوے اور کھجوریں اور انار ہیں،'),
(4970, 55, 69, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4971, 55, 70, 'ان میں عورتیں ہیں عادت کی نیک صورت کی اچھی'),
(4972, 55, 71, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4973, 55, 72, 'حوریں ہیں خیموں میں پردہ نشین'),
(4974, 55, 73, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4975, 55, 74, 'ان سے پہلے انہیں ہاتھ نہ لگایا کسی آدمی اور نہ کسی جِن نے،'),
(4976, 55, 75, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے'),
(4977, 55, 76, 'تکیہ لگائے ہوئے سبز بچھونوں اور منقش خوبصورت چاندنیوں پر،'),
(4978, 55, 77, 'تو اپنے رب کی کونسی نعمت جھٹلاؤ گے،'),
(4979, 55, 78, 'بڑی برکت والا ہے تمہارے رب کا نام جو عظمت اور بزرگی والا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'جب ہولے گی وہ ہونے والی'),
(4981, 56, 2, 'اس وقت اس کے ہونے میں کسی کو انکار کی گنجائش نہ ہوگی،'),
(4982, 56, 3, 'کسی کو پست کرنے والی کسی کو بلندی دینے والی'),
(4983, 56, 4, 'جب زمین کانپے گی تھرتھرا کر'),
(4984, 56, 5, 'اور پہاڑ ریزہ ریزہ ہوجائیں گے چُورا ہوکر'),
(4985, 56, 6, 'تو ہوجائیں گے جیسے روزن کی دھوپ میں غبار کے باریک ذرے پھیلے ہوئے'),
(4986, 56, 7, 'اور تین قسم کے ہوجاؤ گے،'),
(4987, 56, 8, 'تو دہنی طرف والے کیسے دہنی طرف والے'),
(4988, 56, 9, 'اور بائیں طرف والے کیسے بائیں طرف والے'),
(4989, 56, 10, 'اور جو سبقت لے گئے وہ تو سبقت ہی لے گئے'),
(4990, 56, 11, 'وہی مقربِ بارگاہ ہیں،'),
(4991, 56, 12, 'چین کے باغوں میں،'),
(4992, 56, 13, 'اگلوں میں سے ایک گروہ'),
(4993, 56, 14, 'اور پچھلوں میں سے تھوڑے'),
(4994, 56, 15, 'جڑاؤ تختوں پر ہوں گے'),
(4995, 56, 16, 'ان پر تکیہ لگائے ہوئے آمنے سامنے'),
(4996, 56, 17, 'ان کے گرد لیے پھریں گے ہمیشہ رہنے والے لڑکے'),
(4997, 56, 18, 'کوزے اور آفتابے اور جام اور آنکھوں کے سامنے بہتی شراب'),
(4998, 56, 19, 'کہ اس سے نہ انہیں درد سر ہو اور نہ ہوش میں فرق آئے'),
(4999, 56, 20, 'اور میوے جو پسند کریں'),
(5000, 56, 21, 'اور پرندوں کا گوشت جو چاہیں'),
(5001, 56, 22, 'اور بڑی آنکھ والیاں حوریں'),
(5002, 56, 23, 'جیسے چھپے رکھے ہوئے موتی'),
(5003, 56, 24, 'صلہ ان کے اعمال کا'),
(5004, 56, 25, 'اس میں نہ سنیں گے نہ کوئی بیکار با ت نہ گنہگاری'),
(5005, 56, 26, 'ہاں یہ کہنا ہوگا سلام سلام'),
(5006, 56, 27, 'اور دہنی طرف والے کیسے دہنی طرف والے'),
(5007, 56, 28, 'بے کانٹوں کی بیریوں میں'),
(5008, 56, 29, 'اور کیلے کے گچھوں میں'),
(5009, 56, 30, 'اور ہمیشہ کے سائے میں'),
(5010, 56, 31, 'اور ہمیشہ جاری پانی میں'),
(5011, 56, 32, 'اور بہت سے میووں میں'),
(5012, 56, 33, 'جو نہ ختم ہوں اور نہ روکے جائیں'),
(5013, 56, 34, 'اور بلند بچھونوں میں'),
(5014, 56, 35, 'بیشک ہم نے ان عورتوں کو اچھی اٹھان اٹھایا،'),
(5015, 56, 36, 'تو انہیں بنایا کنواریاں اپنے شوہر پر پیاریاں،'),
(5016, 56, 37, 'انہیں پیار دلائیاں ایک عمر والیاں'),
(5017, 56, 38, 'دہنی طرف والوں کے لیے،'),
(5018, 56, 39, 'اگلوں میں سے ایک گروہ،'),
(5019, 56, 40, 'اور پچھلوں میں سے ایک گروہ'),
(5020, 56, 41, 'اور بائیں طرف والے کیسے بائیں طرف والے'),
(5021, 56, 42, 'جلتی ہوا اور کھولتے پانی میں،'),
(5022, 56, 43, 'اور جلتے دھوئیں کی چھاؤں میں'),
(5023, 56, 44, 'جو نہ ٹھنڈی نہ عزت کی،'),
(5024, 56, 45, 'بیشک وہ اس سے پہلے نعمتوں میں تھے'),
(5025, 56, 46, 'اور اس بڑے گناہ کی ہٹ (ضد) رکھتے تھے،'),
(5026, 56, 47, 'اور کہتے تھے کیا جب ہم مرجائیں اور ہڈیاں ہوجائیں تو کیا ضرور ہم اٹھائے جائیں گے،'),
(5027, 56, 48, 'اور کیا ہمارے اگلے باپ دادا بھی،'),
(5028, 56, 49, 'تم فرماؤ بیشک سب اگلے اور پچھلے'),
(5029, 56, 50, 'ضرور اکٹھے کیے جائیں گے، ایک جانے ہوئے دن کی میعاد پر'),
(5030, 56, 51, 'پھر بیشک تم اے گمراہو جھٹلانے والو'),
(5031, 56, 52, 'ضرور تھوہر کے پیڑ میں سے کھاؤ گے،'),
(5032, 56, 53, 'پھر اس سے پیٹ بھرو گے،'),
(5033, 56, 54, 'پھر اس پر کھولتا پانی پیو گے،'),
(5034, 56, 55, 'پھر ایسا پیو گے جیسے سخت پیاسے اونٹ پئیں'),
(5035, 56, 56, 'یہ ان کی مہمانی ہے انصاف کے دن،'),
(5036, 56, 57, 'ہم نے تمہیں پیدا کیا تو تم کیوں نہیں سچ مانتے'),
(5037, 56, 58, 'تو بھلا دیکھو تو وہ منی جو گراتے ہو'),
(5038, 56, 59, 'کیا تم اس کا آدمی بناتے ہو یا ہم بنانے والے ہیں'),
(5039, 56, 60, 'ہم نے تم میں مرنا ٹھہرایا اور ہم اس سے ہارے نہیں،'),
(5040, 56, 61, 'کہ تم جیسے اور بدل دیں اور تمہاری صورتیں وہ کردیں جس کی تمہیں حبر نہیں'),
(5041, 56, 62, 'اور بیشک تم جان چکے ہو پہلی اٹھان پھر کیوں نہیں سوچتے'),
(5042, 56, 63, 'تو بھلا بتاؤ تو جو بوتے ہو،'),
(5043, 56, 64, 'کیا تم اس کی کھیتی بناتے ہو یا ہم بنانے والے ہیں'),
(5044, 56, 65, 'ہم چاہیں تو اسے روندن (پامال) کردیں پھر تم باتیں بناتے رہ جاؤ'),
(5045, 56, 66, 'کہ ہم پر چٹی پڑی'),
(5046, 56, 67, 'بلکہ ہم بے نصیب رہے،'),
(5047, 56, 68, 'تو بھلا بتاؤ تو وہ پانی جو پیتے ہو،'),
(5048, 56, 69, 'کیا تم نے اسے بادل سے اتارا یا ہم ہیں اتارنے والے'),
(5049, 56, 70, 'ہم چاہیں تو اسے کھاری کردیں پھر کیوں نہیں شکر کرتے'),
(5050, 56, 71, 'تو بھلا بتاؤں تو وہ آگ جو تم روشن کرتے ہو'),
(5051, 56, 72, 'کیا تم نے اس کا پیڑ پیدا کیا یا ہم ہیں پیدا کرنے والے،'),
(5052, 56, 73, 'ہم نے اسے جہنم کا یادگار بنایا اور جنگل میں مسافروں کا فائدہ'),
(5053, 56, 74, 'تو اے محبوب تم پاکی بولو اپنے عظمت والے رب کے نام کی،'),
(5054, 56, 75, 'تو مجھے قسم ہے ان جگہوں کی جہاں تارے ڈوبتے ہیں'),
(5055, 56, 76, 'اور تم سمجھو تو یہ بڑی قسم ہے،'),
(5056, 56, 77, 'بیشک یہ عزت والا قرآن ہے'),
(5057, 56, 78, 'محفوظ نوشتہ میں'),
(5058, 56, 79, 'اسے نہ چھوئیں مگر باوضو'),
(5059, 56, 80, 'اتارا ہوا ہے سارے جہان کے رب کا،'),
(5060, 56, 81, 'تو کیا اس بات میں تم سستی کرتے ہو'),
(5061, 56, 82, 'اور اپنا حصہ یہ رکھتے ہو کہ جھٹلاتے ہو'),
(5062, 56, 83, 'پھر کیوں نہ ہو جب جان گلے تک پہنچے'),
(5063, 56, 84, 'اور تم اس وقت دیکھ رہے ہو'),
(5064, 56, 85, 'اور ہم اس کے زیادہ پاس ہیں تم سے مگر تمہیں نگاہ نیں'),
(5065, 56, 86, 'تو کیوں نہ ہوا اگر تمہیں بدلہ ملنا نہیں'),
(5066, 56, 87, 'کہ اسے لوٹا لاتے اگر تم سچے ہو'),
(5067, 56, 88, 'پھر وہ مرنے والا اگر مقربوں سے ہے'),
(5068, 56, 89, 'تو راحت ہے اور پھول اور چین کے باغ'),
(5069, 56, 90, 'اور اگر دہنی طرف والوں سے ہو'),
(5070, 56, 91, 'تو اے محبوب تم پر سلام دہنی طرف والوں سے'),
(5071, 56, 92, 'اور اگر جھٹلانے والے گمراہوں میں سے ہو'),
(5072, 56, 93, 'تو اس کی مہمانی کھولتا پانی،'),
(5073, 56, 94, 'اور بھڑکتی آگ میں دھنسانا'),
(5074, 56, 95, 'یہ بیشک اعلیٰ درجہ کی یقینی بات ہے،'),
(5075, 56, 96, 'تو اے محبوب تم اپنے عظمت والے رب کے نام کی پاکی بولو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'اللہ کی پاکی بولتا ہے جو کچھ آسمان اور زمین میں ہے اور وہی عزت و حکمت والا ہے،'),
(5077, 57, 2, 'اسی کے لیے ہے آسمانوں اور زمین کی سلطنت، جِلاتا ہے اور مارتا اور وہ سب کچھ کرسکتا ہے،'),
(5078, 57, 3, 'وہی اول وہی آخر وہی ظاہر وہی باطن اور وہی سب کچھ جانتا ہے،'),
(5079, 57, 4, 'وہی ہے جس نے آسمان اور زمین چھ دن میں پیدا کیے پھر عرش پر استوا فرمایا جیسا کہ اس کی شان کے لائق ہے، جانتا ہے جو زمین کے اندر جا تا ہے اور جو اس سے باہر نکلتا ہے اور جو آسمان سے اترتا ہے اور جو اس میں چڑھتا ہے اور وہ تمہارے ساتھ ہے تم کہیں ہو، اور اللہ تمہارے کام دیکھ رہا ہے'),
(5080, 57, 5, 'اسی کی ہے آسمانوں اور زمین کی سلطنت، اور اللہ ہی کی طرف، سب کاموں کی رجوع،'),
(5081, 57, 6, 'رات کو دن کے حصے میں لاتا ہے اور دن کو رات کے حصے میں لاتا ہے اور وہ دلوں کی بات جانتا ہے'),
(5082, 57, 7, 'اللہ اور اس کے رسول پر ایمان لاؤ اور اس کی راہ میں کچھ وہ خرچ کرو جس میں تمہیں اَوروں کا جانشین کیا تو جو تم میں ایمان لائے اور اس کی راہ میں خرچ کیا ان کے لیے بڑا ثواب ہے،'),
(5083, 57, 8, 'اور تمہیں کیا ہے کہ اللہ پر ایمان نہ لاؤ، حالانکہ یہ رسول تمہیں بلارہے ہیں کہ اپنے ر ب پر ایمان لاؤ اور بیشک وہ تم سے پہلے سے عہد لے چکا ہے اگر تمہیں یقین ہو،'),
(5084, 57, 9, 'وہی ہے کہ اپنے بندہ پر روشن آیتیں اتارتا ہے تاکہ تمہیں اندھیریوں سے اجالے کی طرف لے جائے اور بیشک ا لله تم پر ضرور مہربان رحم والا،'),
(5085, 57, 10, 'اور تمہیں کیا ہے کہ اللہ کی راہ میں خرچ نہ کرو حالانکہ آسمانوں اور زمین میں سب کا وارث اللہ ہی ہے تم میں برابر نہیں وہ جنہوں نے فتحِ مکہ سے قبل خرچ اور جہاد کیا وہ مرتبہ میں ان سے بڑے ہیں جنہوں نے بعد فتح کے خرچ اور جہاد کیا، اور ان سب سے اللہ جنت کا وعدہ فرماچکا اور اللہ کو تمہارے کاموں کی خبر ہے،'),
(5086, 57, 11, 'کون ہے جو اللہ کو قرض دے اچھا قرض تو وہ اس کے لیے دونے کرے اور اللہ کو عزت کا ثواب دے،'),
(5087, 57, 12, 'جس دن تم ایمان والے مردوں اور ایمان والی عورتوں کو دیکھو گے کہ ان کا نور ہے ان کے آگے اور ان کے دہنے دوڑتا ہے ان سے فرمایا جارہا ہے کہ آج تمہاری سب سے زیادہ خوشی کی بات وہ جنتیں ہیں جن کے نیچے نہریں بہیں تم ان میں ہمیشہ رہو، یہی بڑی کامیابی ہے،'),
(5088, 57, 13, 'جس دن منافق مرد اور منافق عورتیں مسلمانوں سے کہیں گے کہ ہمیں یک نگاہ دیکھو کہ ہم تمہارے نور سے کچھ حصہ لیں، کہا جائے گا اپنے پیچھے لوٹو (ُ۳۴) وہاں نور ڈھونڈو وہ لوٹیں گے، جبھی ان کے درمیان ایک دیوار کھڑی کردی جائے گی جس میں ایک دروازہ ہے اس کے اندر کی طرف رحمت اور اس کے باہر کی طرف عذاب،'),
(5089, 57, 14, 'منافق مسلمانوں کو پکاریں گے کیا ہم تمہارے ساتھ نہ تھے وہ کہیں گے کیوں نہیں مگر تم نے تو اپنی جانیں فتنہ میں ڈالیں اور مسلمانوں کی برائی تکتے اور شک رکھتے اور جھوٹی طمع نے تمھیں فریب دیا یہاں تک کہ اللہ کا حکم آگیا اور تمہیں اللہ کے حکم پر اس بڑے فریبی نے مغرور رکھا'),
(5090, 57, 15, 'تو آج نہ تم سے کوئی فدیہ لیا جائے اور نہ کھلے کافروں سے، تمہارا ٹھکانا آگ ہے، وہ تمہاری رفیق ہے، اور کیا ہی برا انجام،'),
(5091, 57, 16, 'کیا ایمان والوں کو ابھی وہ وقت نہ آیا کہ ان کے دل جھک جائیں اللہ کی یاد اور اس حق کے لیے جو اترا اور ان جیسے نہ ہوں جن کو پہلے کتاب دی گئی پھر ان پر مدت دراز ہوئی تو ان کے دل سخت ہوگئے اور ان میں بہت فاسق ہیں'),
(5092, 57, 17, 'جان لو کہ اللہ زمین کو زندہ کرتا ہے اس کے مرے پیچھے بیشک ہم نے تمہارے لیے نشانیاں بیان فرمادیں کہ تمہیں سمجھ ہو،'),
(5093, 57, 18, 'بیشک صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور وہ جنہوں نے اللہ کو اچھا قرض دیا ان کے دونے ہیں اور ان کے لیے عزت کا ثواب ہے'),
(5094, 57, 19, 'اور وہ جو اللہ اور اس کے سب رسولوں پر ایمان لائیں وہی ہیں کامل سچے، اور اَوروں پر گواہ اپنے رب کے یہاں، ان کے لیے ان کا ثواب اور ان کا نور ہے اور جنہوں نے کفر کیا اور ہماری آیتیں جھٹلائیں وہ دوزخی ہیں،'),
(5095, 57, 20, 'جان لو کہ دنیا کی زندگی تو نہیں مگر کھیل کود اور آرائش اور تمہارا آپس میں بڑائی مارنا اور مال اور اولاد میں ایک دوسرے پر زیادتی چاہنا اس مینھ کی طرح جس کا اُگایاسبزہ کسانوں کو بھایا پھر سوکھا کہ تو اسے زرد دیکھے پھر روندن (پامال کیا ہوا) ہوگیا اور آخرت میں سخت عذاب ہے اور اللہ کی طرف سے بخشش اور اس کی رضا اور دنیا کا جینا تو نہیں مگر دھوکے کا مال'),
(5096, 57, 21, 'بڑھ کر چلو اپنے رب کی بخشش اور اس جنت کی طرف جس کی چوڑائی جیسے آسمان اور زمین کا پھیلاؤ تیار ہوئی ہے ان کے لیے جو اللہ اور اس کے سب رسولوں پر ایمان لائے، یہ اللہ کا فضل ہے جسے چاہے دے، اور اللہ بڑا فضل والا ہے،'),
(5097, 57, 22, 'نہیں پہنچتی کوئی مصیبت زمین میں اور نہ تمہاری جانوں میں مگر وہ ایک کتاب میں ہے قبل اس کے کہ ہم اسے پیدا کریں بیشک یہ اللہ کو آسان ہے،'),
(5098, 57, 23, 'اس لیے کہ غم نہ کھاؤ اس پر جو ہاتھ سے جائے اور خوش نہ ہو اس پر جو تم کو دیا اور اللہ کو نہیں کوئی اترونا (شیخی بگھارنے والا) بڑائی مارنے والا،'),
(5099, 57, 24, 'وہ جو آپ بخل کریں اور اوروں سے بخل کو کہیں اور جو منہ پھیرے تو بیشک اللہ ہی بے نیاز ہے سب خوبیوں سراہا،'),
(5100, 57, 25, 'بیشک ہم نے اپنے رسولوں کو دلیلوں کے ساتھ بھیجا اور ان کے ساتھ کتاب اور عدل کی ترازو اتاری کہ لوگ انصاف پر قائم ہوں اور ہم نے لوہا اتارا اس میں سخت آنچ (نقصان) اور لوگوں کے فائدے اور اس لیے کہ اللہ دیکھے اس کو جو بے دیکھے اس کی اور اس کے رسولوں کی مدد کرتا ہے، بیشک اللہ قورت والا غالب ہے'),
(5101, 57, 26, 'اور بیشک ہم نے نوح اور ابراہیم کو بھیجا اور ان کی اولاد میں نبوت اور کتاب رکھی تو ان میں کوئی راہ پر آیا اور ان میں بہتیرے فاسق ہیں،'),
(5102, 57, 27, 'پھر ہم نے ان کے پیچھے اسی راہ پر اپنے اور رسول بھیجے اور ان کے پیچھے عیسیٰ بن مریم کو بھیجا اور اسے انجیل عطا فرمائی اور اس کے پیروں کے دل میں نرمی اور رحمت رکھی اور راہب بننا تو یہ بات انہوں نے دین میں اپنی طرف سے نکالی ہم نے ان پر مقرر نہ کی تھی ہاں یہ بدعت انہوں نے ا لله کی رضا چاہنے کو پیدا کی پھر اسے نہ نباہا جیسا اس کے نباہنے کا حق تھا تو ان کے ایمان والوں کو ہم نے ان کا ثواب عطا کیا، اور ان میں سے بہتیرے فاسق ہیں،'),
(5103, 57, 28, 'اے ایمان والو! اللہ سے ڈرو اور اس کے رسول پر ایمان لاؤ وہ اپنی رحمت کے دو حصے تمہیں عطا فرمائے گا اور تمہارے لیے نور کردے گا جس میں چلو اور تمہیں بخش دے گا، اور اللہ بخشنے والا مہربان ہے،'),
(5104, 57, 29, 'یہ اس لیے کہ کتاب والے کافر جان جائیں کہ اللہ کے فضل پر ان کا کچھ قابو نہیں اور یہ کہ فضل اللہ کے ہاتھ ہے دیتا ہے جسے چاہے، اور اللہ بڑے فضل والا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'بیشک اللہ نے سنی اس کی بات جو تم سے اپنے شوہر کے معاملہ میں بحث کرتی ہے اور اللہ سے شکایت کرتی ہے، اور اللہ تم دونوں کی گفتگو سن رہا ہے، بیشک اللہ سنتا دیکھتا ہے،'),
(5106, 58, 2, 'وہ جو تم میں اپنی بیبیوں کو اپنی ماں کی جگہ کہہ بیٹھتے ہیں وہ ان کی مائیں نہیں ان کی مائیں تو وہی ہیں جن سے وہ پیدا ہیں اور وہ بیشک بری اور نری جھوٹ بات کہتے ہیں اور بیشک اللہ ضرور معاف کرنے والا اور بخشنے والا ہے،'),
(5107, 58, 3, 'اور وہ جو اپنی بیبیوں کو اپنی ماں کی جگہ کہیں پھر وہی کرنا چاہیں جس پر اتنی بری بات کہہ چکے تو ان پر لازم ہے ایک بردہ آزاد کرنا قبل اس کے کہ ایک دوسرے کو ہاتھ لگائیں یہ ہے جو نصیحت تمہیں کی جاتی ہے، اور اللہ تمہارے کاموں سے خبردار ہے،'),
(5108, 58, 4, 'پھر جسے بردہ نہ ملے تو لگاتار دو مہینے کے روزے قبل اس کے کہ ایک دوسرے کو ہاتھ لگائیں پھر جس سے روزے بھی نہ ہوسکیں تو ساٹھ مسکینوں کا پیٹ بھرنا یہ اس لیے کہ تم اللہ اور اس کے رسول پر ایمان رکھو اور یہ اللہ کی حدیں ہیں اور کافروں کے لیے دردناک عذاب ہے،'),
(5109, 58, 5, 'بیشک وہ جو مخالفت کرتے ہیں اللہ اور اس کے رسول کی ذلیل کیے گئے جیسے ان سے اگلوں کو ذلت دی گئی اور بیشک ہم نے روشن آیتیں اتاریں اور کافروں کے لیے خواری کا عذاب ہے،'),
(5110, 58, 6, 'جس دن اللہ ان سب کو اٹھائے گا پھر انہیں ان کے کو تک جتادے گا اللہ نے انہیں گن رکھا ہے اور وہ بھول گئے اور ہر چیز اللہ کے سامنے ہے،'),
(5111, 58, 7, 'اے سننے والے کیا تو نے نہ دیکھا کہ اللہ جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں جہاں کہیں تین شخصوں کی سرگوشی ہو تو چوتھا وہ موجود ہے اور پانچ کی تو چھٹا وہ اور نہ اس سے کم اور نہ اس سے زیادہ کی مگر یہ کہ وہ ان کے ساتھ ہے جہاں کہیں ہوں پھر انہیں قیامت کے دن بتادے گا جو کچھ انہوں نے کیا، بیشک اللہ سب کچھ جانتا ہے،'),
(5112, 58, 8, 'کیا تم نے انہیں نہ دیکھا جنہیں بری مشورت سے منع فرمایا گیا تھا پھر وہی کرتے ہیں جس کی ممانعت ہوئی تھی اور آپس میں گناہ اور حد سے بڑھنے اور رسول کی نافرمانی کے مشورے کرتے ہیں اور جب تمہارے حضور حاضر ہوتے ہیں تو ان لفظوں سے تمہیں مجرا کرتے ہیں جو لفظ اللہ نے تمہارے اعزاز میں نہ کہے اور اپنے دلوں میں کہتے ہیں ہمیں اللہ عذاب کیوں نہیں کرتا ہمارے اس کہنے پر انہیں جہنم بس ہے، اس میں دھنسیں گے، تو کیا ہی برا انجام،'),
(5113, 58, 9, 'اے ایمان والو تم جب آپس میں مشورت کرو تو گناہ اور حد سے بڑھنے اور رسول کی نافرمانی کی مشورت نہ کرو اور نیکی اورپرہیزگاری کی مشورت کرو، اور اللہ سے ڈرو جس کی طرف اٹھائے جاؤ گے،'),
(5114, 58, 10, 'وہ مشورت تو شیطان ہی کی طرف سے ہے اس لیے کہ ایمان والوں کو رنج دے اور وہ اس کا کچھ نہیں بگاڑ سکتا بے حکم خدا کے، اور مسلمانوں کو اللہ ہی پر بھروسہ چاہیے'),
(5115, 58, 11, 'اے ایمان والو جب تم سے کہا جائے مجلسوں میں جگہ دو تو جگہ دو اللہ تمہیں جگہ دے گا اور جب کہا جائے اٹھ کھڑے ہو تو اٹھ کھڑے ہو اللہ تمہارے ایمان والوں کے اور ان کے جن کو علم دیا گیا درجے بلند فرمائے گا، اور اللہ کو تمہارے کاموں کی خبر ہے،'),
(5116, 58, 12, 'اے ایمان والو جب تم رسول سے کوئی بات آہستہ عرض کرنا چاہو تو اپنی عرض سے پہلے کچھ صدقہ دے لو یہ تمہارے لیے بہت بہتر اور بہت ستھرا ہے، پھر اگر تمہیں مقدور نہ ہو تو اللہ بخشنے والا مہربان ہے،'),
(5117, 58, 13, 'کیا تم اس سے ڈرے کہ تم اپنی عرض سے پہلے کچھ صدقے دو پھر جب تم نے یہ نہ کیا، اور اللہ نے اپنی مہر سے تم پر رجوع فرمائی تو نماز قائم رکھو اور زکوٰة دو اور اللہ اور اس کے رسول کے فرمانبردار رہو، اور اللہ تمہارے کاموں کو جانتا ہے،'),
(5118, 58, 14, 'کیا تم نے انہیں نہ دیکھا جو ایسوں کے دوست ہوئے جن پر اللہ کا غضب ہے وہ نہ تم میں سے نہ ان میں سے وہ دانستہ جھوٹی قسم کھاتے ہیں'),
(5119, 58, 15, 'اللہ نے ان کے لیے سخت عذاب تیار کر رکھا ہے، بیشک وہ بہت ہی برے کام کرتے ہیں،'),
(5120, 58, 16, 'انہوں نے اپنی قسموں کو ڈھال بنالیا ہے تو اللہ کی راہ سے روکا تو ان کے لیے خواری کا عذاب ہے'),
(5121, 58, 17, 'ان کے مال اور ان کی اولاد اللہ کے سامنے انہیں کچھ کام نہ دیں گے وہ دوزخی ہیں، انہیں اس میں ہمیشہ رہنا،'),
(5122, 58, 18, 'جس دن اللہ ان سب کو اٹھائے گا تو اس کے حضور بھی ایسے ہی قسمیں کھائیں گے جیسی تمہارے سامنے کھارہے ہیں اور وہ یہ سمجھتے ہیں کہ انہوں نے کچھ کیا سنتے ہو بیشک وہی جھوٹے ہیں'),
(5123, 58, 19, 'ان پر شیطان غالب آگیا تو انہیں اللہ کی یاد بھلا دی، وہ شیطان کے گروہ ہیں، سنتا ہے بیشک شیطان ہی کا گروہ ہار میں ہے'),
(5124, 58, 20, 'بیشک وہ جو اللہ اور اس کے رسول کی مخالفت کرتے ہیں وہ سب سے زیادہ ذلیلوں میں ہیں،'),
(5125, 58, 21, 'اللہ لکھ چکا کہ ضرور میں غالب آؤں گا اور میرے رسول بیشک اللہ قوت والا عزت والا ہے،'),
(5126, 58, 22, 'تم نہ پاؤ گے ان لوگوں کو جو یقین رکھتے ہیں اللہ اور پچھلے دن پر کہ دوستی کریں ان سے جنہوں نے اللہ اور اس کے رسول سے مخالفت کی اگرچہ وہ ان کے باپ یا بیٹے یا بھائی یا کنبے والے ہوں یہ ہیں جن کے دلوں میں اللہ نے ایمان نقش فرمادیا اور اپنی طرف کی روح سے ان کی مدد کی اور انہیں باغوں میں لے جائے گا جن کے نیچے نہریں بہیں ان میں ہمیشہ رہیں، اللہ ان سے راضی اور وہ اللہ سے راضی یہ اللہ کی جماعت ہے، سنتا ہے اللہ ہی کی جماعت کامیاب ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'اللہ کی پاکی بولتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں، اور وہ وہی عزت و حکمت والا ہے'),
(5128, 59, 2, 'وہی ہے جس نے ان کافر کتابیوں کو ان کے گھروں سے نکالا ان کے پہلے حشر کے لیے تمہیں گمان نہ تھا کہ وہ نکلیں گے اور وہ سمجھتے تھے کہ ان کے قلعے انہیں اللہ سے بچالیں گے تو اللہ کا حکم ان کے پاس آیا جہاں سے ان کا گمان بھی نہ تھا اور اس نے ان کے دلوں میں رُعب ڈالا کہ اپنے گھر ویران کرتے ہیں اپنے ہاتھوں اور مسلمانوں کے ہاتھوں تو عبرت لو اے نگاہ والو،'),
(5129, 59, 3, 'اور اگر نہ ہوتا کہ اللہ نے ان پر گھر سے اجڑنا لکھ دیا تھا تو دنیا ہی میں ان پر عذاب فرماتا اور ان کے لیے آخرت میں آگ کا عذاب ہے،'),
(5130, 59, 4, 'یہ اس لیے کہ وہ اللہ سے اور اس کے رسول سے پھٹے (جدا) رہے اور جو اللہ اور اس کے رسول سے پھٹا رہے تو بیشک اللہ کا عذاب سخت ہے،'),
(5131, 59, 5, 'جو درخت تم نے کاٹے یا ان کی جڑوں پر قائم چھوڑ دیے یہ سب اللہ کی اجازت سے تھا اور اس لیے کہ فاسقوں کو رسوا کرے'),
(5132, 59, 6, 'اور جو غنیمت دلائی اللہ نے اپنے رسول کو ان سے تو تم نے ان پر نہ اپنے گھوڑے دوڑائے تھے اور نہ اونٹ ہاں اللہ اپنے رسولوں کے قابو میں دے دیتا ہے جسے چاہے اور اللہ سب کچھ کرسکتا ہے،'),
(5133, 59, 7, 'جو غنیمت دلائی اللہ نے اپنے رسول کو شہر والوں سے وہ اللہ اور رسول کی ہے اور رشتہ داروں اور یتیموں اور مسکینوں اور مسافروں کے لیے کہ تمہارے اغنیا کا مال نہ جائے (ف۹۲۱ اور جو کچھ تمہیں رسول عطا فرمائیں وہ لو اور جس سے منع فرمائیں باز رہو، اور اللہ سے ڈرو بیشک اللہ کا عذاب سخت ہے'),
(5134, 59, 8, 'ان فقیر ہجرت کرنے والوں کے لیے جو اپنے گھروں اور مالوں سے نکالے گئے اللہ کا فضل اور اس کی رضا چاہتے اور اللہ و رسول کی مدد کرتے وہی سچے ہیں'),
(5135, 59, 9, 'اور جنہوں نے پہلے سے اس شہر اور ایمان میں گھر بنالیا دوست رکھتے ہیں انہیں جو ان کی طرف ہجرت کرکے گئے اور اپنے دلوں میں کوئی حاجت نہیں پاتے اس چیز کو جو دیے گئے اور اپنی جانوں پر ان کو ترجیح دیتے ہیں اگرچہ انہیں شدید محتاجی ہو اور جو اپنے نفس کے لالچ سے بچایا گیا تو وہی کامیاب ہیں،'),
(5136, 59, 10, 'اور وہ جو ان کے بعد آئے عرض کرتے ہیں اے ہمارے رب ہمیں بخش دے اور ہمارے بھائیوں کو جو ہم سے پہلے ایمان لائے اور ہمارے دل میں ایمان والوں کی طرف سے کینہ نہ رکھ اے ہمارے رب بیشک تو ہی نہایت مہربان رحم والا ہے،'),
(5137, 59, 11, 'کیا تم نے منافقوں کو نہ دیکھا کہ اپنے بھائیوں کافر کتابیوں سے کہتے ہیں کہ اگر تم نکالے گئے تو ضرور ہم تمہارے ساتھ جائیں گے اور ہرگز تمہارے بارے میں کسی کی نہ مانیں گے اور اگر تم سے لڑائی ہوئی تو ہم ضرور تمہاری مدد کریں گے، اور اللہ گواہ ہے کہ وہ جھوٹے ہیں'),
(5138, 59, 12, 'اگر وہ نکالے گئے تو یہ ان کے ساتھ نہ نکلیں گے، اور ان سے لڑائی ہوئی تو یہ ان کی مدد نہ کریں گے اگر ان کی مدد کی بھی تو ضرور پیٹھ پھیر کر بھاگیں گے، پھر مدد نہ پائیں گے،'),
(5139, 59, 13, 'بیشک ان کے دلوں میں اللہ سے زیادہ تمہارا ڈر ہے یہ اس لیے کہ وہ ناسمجھ لوگ ہیں'),
(5140, 59, 14, 'یہ سب مل کر بھی تم سے نہ لڑیں گے مگر قلعہ بند شہروں میں یا دُھسّوں (شہر پناہ) کے پیچھے، آپس میں ان کی آنچ (جنگ) سخت ہے تم انہیں ایک جتھا سمجھو گے اور ان کے دل الگ الگ ہیں، یہ اس لیے کہ وہ بے عقل لوگ ہیں'),
(5141, 59, 15, 'ان کی سی کہاوت جو ابھی قریب زمانہ میں ان سے پہلے تھے انہوں نے اپنے کام کا وبال چکھا اور ان کے لیے دردناک عذاب ہے'),
(5142, 59, 16, 'شیطان کی کہاوت جب اس نے آدمی سے کہا کفر کر پھر جب اس نے کفر کرلیا بولا میں تجھ سے الگ ہوں میں اللہ سے ڈرتا ہوں جو سارے جہان کا رب'),
(5143, 59, 17, 'تو ان دونوں کا انجام یہ ہوا کہ وہ دونوں آگ میں ہیں ہمیشہ اس میں رہے، اور ظالموں کو یہی سزا ہے،'),
(5144, 59, 18, 'اے ایمان والو اللہ سے ڈرو اور ہر جان دیکھے کہ کل کے لیے آگے کیا بھیجا اور اللہ سے ڈرو بیشک اللہ کو تمہارے کاموں کی خبر ہے،'),
(5145, 59, 19, 'اور ان جیسے نہ ہو جو اللہ کو بھول بیٹھے تو اللہ نے انہیں بلا میں ڈالا کہ اپنی جانیں یاد نہ رہیں وہی فاسق ہیں،'),
(5146, 59, 20, 'دوزخ والے اور جنت والے برابر نہیں جنت والے ہی مراد کو پہنچے،'),
(5147, 59, 21, 'اگر ہم یہ قرآن کسی پہاڑ پر اتارتے تو ضرور تو اسے دیکھتا جھکا ہوا پاش پاش ہوتا اللہ کے خوف سے اور یہ مثالیں لوگوں کے لیے ہم بیان فرماتے ہیں کہ وہ سوچیں،'),
(5148, 59, 22, 'وہی اللہ ہے جس کے سوا کوئی معبود نہیں، ہر نہاں و عیاں کا جاننے والا وہی ہے بڑا مہربان رحمت والا،'),
(5149, 59, 23, 'وہی ہے اللہ جس کے سوا کوئی معبود نہیں بادشاہ نہایت پاک سلامتی دینے والا امان بخشنے والا حفاظت فرمانے والا عزت والا عظمت والا تکبر والا اللہ کو پاکی ہے ان کے شرک سے،'),
(5150, 59, 24, 'وہی ہے اللہ بنانے والا پیدا کرنے والا ہر ایک کو صورت دینے والا اسی کے ہیں سب اچھے نام اس کی پاکی بولتا ہے جو کچھ آسمانوں اور زمین میں ہے اور وہی عزت و حکمت والا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اے ایمان والو میرے اور اپنے دشمنوں کو دوست نہ بناؤ تم انہیں خبریں پہنچاتے ہو دوستی سے حالانکہ وہ منکر ہیں اس حق کے جو تمہارے پاس آیا گھر سے جدا کرتے ہیں رسول کو اور تمہیں اس پر کہ تم اپنے رب پر ایمان لائے، اگر تم نکلے ہو میری راہ میں جہاد کرنے اور میری رضا چاہنے کو تو ان سے دوستی نہ کرو تم انہیں خفیہ پیامِ محبت بھیجتے ہو، اور میں خوب جانتا ہوں جو تم چھپاؤ اور جو ظاہر کرو، اور تم میں جو ایسا کرے بیشک وہ سیدھی راہ سے بہکا،'),
(5152, 60, 2, 'اگر تمہیں پائیں تو تمہارے دشمن ہوں گے اور تمہاری طرف اپنے ہاتھ اور اپنی زبانیں برائی کے ساتھ دراز کریں گے اور ان کی تمنا ہے کہ کسی طرح تم کافر ہوجاؤ'),
(5153, 60, 3, 'ہرگز کام نہ آئیں گے تمہیں تمہارے رشتے اور نہ تمہاری اولاد قیامت کے دن، تمہیں ان سے الگ کردے گا اور اللہ تمہارے کام دیکھ رہا ہے،'),
(5154, 60, 4, 'بیشک تمہارے لیے اچھی پیروی تھی ابراہیم اور اس کے ساتھ والوں میں جب انہوں نے اپنی قوم سے کہا بیشک ہم بیزار ہیں تم سے اور ان سے جنہیں اللہ کے سوا پوجتے ہو، ہم تمہارے منکر ہوئے اور ہم میں اور تم میں دشمنی اور عداوت ظاہر ہوگئی ہمیشہ کے لیے جب تک تم ایک اللہ پر ایمان نہ لاؤ مگر ابراہیم کا اپنے باپ سے کہنا کہ میں ضرور تیری مغفرت چاہوں گا اور میں اللہ کے سامنے تیر ے کسی نفع کا مالک نہیں اے ہمارے رب ہم نے تجھی پر بھروسہ کیا اور تیری ہی طرف رجوع لائے اور تیری ہی طرف پھرنا ہے'),
(5155, 60, 5, 'اے ہمارے رب ہمیں کافروں کی آزمائش میں نہ ڈال اور ہمیں بخش دے، اے ہمارے رب بیشک تو ہی عزت و حکمت والا ہے،'),
(5156, 60, 6, 'بیشک تمہارے لیے ان میں اچھی پیروی تھی اسے جو اللہ اور پچھلے دن کا امیدوار ہو اور جو منہ پھیرے تو بیشک اللہ ہی بے نیاز ہے سب خوبیوں سراہا،'),
(5157, 60, 7, 'قریب ہے کہ اللہ تم میں اور ان میں جو ان میں سے تمہارے دشمن ہیں دوستی کردے اور اللہ قادر ہے اور بخشنے والا مہربان ہے،'),
(5158, 60, 8, 'اللہ تمہیں ان سے منع نہیں کرتا جو تم سے دین میں نہ لڑے اور تمہیں تمہارے گھروں سے نہ نکالا کہ ان کے ساتھ احسان کرو اور ان سے انصاف کا برتاؤ برتو، بیشک انصاف والے اللہ کو محبوب ہیں،'),
(5159, 60, 9, 'اللہ تمہیں انہی سے منع کرتا ہے جو تم سے دین میں لڑے یا تمہیں تمہارے گھروں سے نکالا یا تمہارے نکالنے پر مدد کی کہ ان سے دوستی کرو اور جو ان سے دوستی کرے تو وہی ستمگار ہیں،'),
(5160, 60, 10, 'اے ایمان والو جب تمہارے پاس مسلمان عورتیں کفرستان سے اپنے گھر چھوڑ کر آئیں تو ان کا امتحان کرو اللہ ان کے ایمان کا حال بہتر جانتا ہے، پھر اگر تمہیں ایمان والیاں معلوم ہوں تو انہیں کافروں کو واپس نہ دو، نہ یہ انہیں حلال نہ وہ انہیں حلال اور ان کے کافر شوہروں کو دے دو جو ان کا خرچ ہوا اور تم پر کچھ گناہ نہیں کہ ان سے نکاح کرلو جب ان کے مہر انہیں دو اور کافرنیوں کے نکاح پر جمے نہ رہو اور مانگ لو جو تمہارا خرچ ہوا اور کافر مانگ لیں جو انہوں نے خرچ کیا یہ اللہ کا حکم ہے، وہ تم میں فیصلہ فرماتا ہے، اور اللہ علم و حکمت والا ہے،'),
(5161, 60, 11, 'اور اگر مسلمانوں کے ہاتھ سے کچھ عورتیں کافروں کی طرف نکل جائیں پھر تم کافروں کو سزا دو تو جن کی عورتیں جاتی رہی تھیں غنیمت میں سے انہیں اتنا دیدو جو ان کا خرچ ہوا تھا اور اللہ سے ڈرو جس پر تمہیں ایمان ہے،'),
(5162, 60, 12, 'اے نبی جب تمہارے حضور مسلمان عورتیں حاضر ہوں اس پر بیعت کرنے کو کہ اللہ کا کچھ شریک نہ ٹھہرائیں گی اور نہ چوری کریں گی اور نہ بدکاری اور نہ اپنی اولاد کو قتل کریں گی اور نہ وہ بہتان لائیں گی جسے اپنے ہاتھوں اور پاؤں کے درمیان یعنی موضع ولادت میں اٹھائیں اور کسی نیک بات میں تمہاری نافرانی نہ کریں گی تو ان سے بیعت لو اور اللہ سے ان کی مغفرت چاہو بیشک اللہ بخشنے والا مہربان ہے،'),
(5163, 60, 13, 'اے ایمان والو ان لوگوں سے دوستی نہ کرو جن پر اللہ کا غضب ہے وہ آخرت سے آس توڑ بیٹھے ہیں جیسے کافر آس توڑ بیٹھے قبر والوں سے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'اللہ کی پاکی بولتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے، اور وہی عزت و حکمت والا ہے،'),
(5165, 61, 2, 'اے ایمان والو کیوں کہتے ہو وہ جو نہیں کرتے'),
(5166, 61, 3, 'کیسی سخت ناپسند ہے اللہ کو وہ بات کہ وہ کہو جو نہ کرو،'),
(5167, 61, 4, 'بیشک اللہ دوست رکھتا ہے انہیں جو اس کی راہ میں لڑتے ہیں پرا (صف) باندھ کر گویا وہ عمارت ہیں رانگا پلائی (سیسہ پلائی دیوار)'),
(5168, 61, 5, 'اور یاد کرو جب موسیٰ نے اپنی قوم سے کہا، اے میری قوم مجھے کیوں ستاتے ہو حالانکہ تم جانتے ہو کہ میں تمہاری طرف اللہ کا رسول ہوں پھر جب وہ ٹیڑھے ہوئے اللہ نے ان کے دل ٹیڑھے کردیے اور اللہ فاسق لوگوں کو راہ نہیں دیتا'),
(5169, 61, 6, 'اور یاد کرو جب عیسیٰ بن مریم نے کہا اے بنی اسرائیل میں تمہاری طرف اللہ کا رسول ہوں اپنے سے پہلی کتاب توریت کی تصدیق کرتا ہوا اور ان رسول کی بشارت سناتا ہوا جو میرے بعد تشریف لائیں گے ان کا نام احمد ہے پھر جب احمد ان کے پاس روشن نشانیاں لے کر تشریف لائے بولے یہ کھلا جادو،'),
(5170, 61, 7, 'اور اس سے بڑھ کر ظالم کون جو اللہ پر جھوٹ باندھے حالانکہ اسے اسلام کی طرف بلایا جاتا ہو اور ظالم لوگوں کو اللہ راہ نہیں دیتا،'),
(5171, 61, 8, 'چاہتے ہیں کہ اللہ کا نور اپنے مونھوں سے بجھادیں اور اللہ کو اپنا نور پورا کرنا پڑے برا مانیں کافر،'),
(5172, 61, 9, 'وہی ہے جس نے اپنے رسول کو ہدایت اور سچے دین کے ساتھ بھیجا کہ اسے سب دینوں پر غالب کرے پڑے برا مانیں مشرک،'),
(5173, 61, 10, 'اے ایمان والو کیا میں بتادوں وہ تجارت جو تمہیں دردناک عذاب سے بچالے'),
(5174, 61, 11, 'ایمان رکھو اللہ اور اس کے رسول پر اور اللہ کی راہ میں اپنے مال و جان سے جہاد کرو، یہ تمہارے لیے بہتر ہے اگر تم جانو'),
(5175, 61, 12, 'وہ تمہارے گناہ بخش دے گا اور تمہیں باغوں میں لے جائے گا جن کے نیچے نہریں رواں اور پاکیزہ محلوں میں جو بسنے کے باغوں میں ہیں، یہی بڑی کامیابی ہے،'),
(5176, 61, 13, 'اور ایک نعمت تمہیں اور دے گا جو تمہیں پیاری ہے اللہ کی مدد اور جلد آنے والی فتح اور اے محبوب مسلمانوں کو خوشی سنادو'),
(5177, 61, 14, 'اے ایمان والو دین خدا کے مددگار ہو جیسے عیسیٰ بن مریم نے حواریوں سے کہا تھا کون ہے جو اللہ کی طرف ہو کر میری مدد کریں حواری بولے ہم دینِ خدا کے مددگار ہیں تو بنی اسرائیل سے ایک گروہ ایمان لایا اور ایک گروہ نے کفر کیا تو ہم نے ایمان والوں کو ان کے دشمنوں پر مدد دی تو غالب ہوگئے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'اللہ کی پاکی بولتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے بادشاہ کمال پاکی والا عزت والا حکمت والا،'),
(5179, 62, 2, 'وہی ہے جس نے اَن پڑھوں میں انہی میں سے ایک رسول بھیجا کہ ان پر اس کی آیتیں پڑھتے ہیں اور انہیں پاک کرتے ہیں اور انہیں کتاب و حکمت کا علم عطا فرماتے ہیں اور بیشک وہ اس سے پہلے ضرور کھلی گمراہی میں تھے'),
(5180, 62, 3, 'اور ان میں سے اوروں کو پاک کرتے اور علم عطا فرماتے ہیں، جو ان اگلوں سے نہ ملے اور وہی عزت و حکمت والا ہے،'),
(5181, 62, 4, 'یہ اللہ کا فضل ہے جسے چاہے دے، اور اللہ بڑے فضل والا ہے'),
(5182, 62, 5, 'ان کی مثال جن پر توریت رکھی گئی تھی پھر انہوں نے اس کی حکم برداری نہ کی گدھے کی مثال ہے جو پیٹھ پر کتابیں اٹھائے کیا ہی بری مثال ہے ان لوگوں کی جنہوں نے اللہ کی آیتیں جھٹلائیں، اور اللہ ظالموں کو راہ نہیں دیتا،'),
(5183, 62, 6, 'تم فرماؤ اے یہودیو! اگر تمہیں یہ گمان ہے کہ تم اللہ کے دوست ہو اور لوگ نہیں تو مرنے کی آرزو کرو اگر تم سچے ہو'),
(5184, 62, 7, 'اور وہ کبھی اس کی آرزو نہ کریں گے ان کوتکوں کے سبب جو ان کے ہاتھ آگے بھیج چکے ہیں اور اللہ ظالموں کو جانتا ہے،'),
(5185, 62, 8, 'تم فرماؤ وہ موت جس سے تم بھاگتے ہو وہ تو ضرور تمہیں ملنی ہے پھر اس کی طرف پھیرے جاؤ گے جو چھپا اور ظاہر سب کچھ جانتا ہے پھر وہ تمہیں بتادے گا جو تم نے کیا تھا،'),
(5186, 62, 9, 'اے ایمان والو جب نماز کی اذان ہو جمعہ کے دن تو اللہ کے ذکر کی طرف دوڑو اور خرید و فروخت چھوڑ دو یہ تمہارے لیے بہتر ہے اگر تم جانو،'),
(5187, 62, 10, 'پھر جب نماز ہوچکے تو زمین میں پھیل جاؤ اور اللہ کا فضل تلاش کرو اور اللہ کو بہت یاد کرو اس امید پر کہ فلاح پاؤ،'),
(5188, 62, 11, 'اور جب انہوں نے کوئی تجارت یا کھیل دیکھا اس کی طرف چل دیے اور تمہیں خطبے میں کھڑا چھوڑ گئے تم فرماؤ وہ جو اللہ کے پاس ہے کھیل سے اور تجارت سے بہتر ہے، اور اللہ کا رزق سب سے اچھا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'جب منافق تمہارے حضور حاضر ہوتے ہیں کہتے ہیں کہ ہم گواہی دیتے ہیں کہ حضور بیشک یقیناً اللہ کے رسول ہیں اور اللہ جانتا ہے کہ تم اس کے رسول ہو، اور اللہ گواہی دیتا ہے کہ منافق ضرور جھوٹے ہیں'),
(5190, 63, 2, 'اور انہوں نے اپنی قسموں کو ڈھال ٹھہرالیا تو اللہ کی راہ سے روکا بیشک وہ بہت ہی برے کام کرتے ہیں'),
(5191, 63, 3, 'یہ اس لیے کہ وہ زبان سے ایمان لائے پھر دل سے کافر ہوئے تو ان کے دلوں پر مہر کردی گئی تو اب وہ کچھ نہیں سمجھتے،'),
(5192, 63, 4, 'اور جب تو انہیں دیکھے ان کے جسم تجھے بھلے معلوم ہوں، اور اگر بات کریں تو تُو ان کی بات غور سے سنے گویا وہ کڑیاں ہیں دیوار سے ٹکائی ہوئی ہر بلند آواز اپنے ہی اوپر لے جاتے ہیں وہ دشمن ہیں تو ان سے بچتے رہو اللہ انہیں مارے کہاں اوندھے جاتے ہیں'),
(5193, 63, 5, 'اور جب ان سے کہا جائے کہ آؤ رسول اللہ تمہارے لیے معافی چاہیں تو اپنے سر گھماتے ہیں اور تم انہیں دیکھو کہ غور کرتے ہوئے منہ پھیر لیتے ہیں'),
(5194, 63, 6, 'ان پر ایک سا ہے تم ان کی معافی چاہو یا نہ چاہو، اللہ انہیں ہر گز نہ بخشے گا بیشک اللہ فاسقوں کو راہ نہیں دیتا،'),
(5195, 63, 7, 'وہی ہیں جو کہتے ہیں کہ ان پر خرچ نہ کرو جو رسول اللہ کے پاس ہیں یہاں تک کہ پریشان ہوجائیں، اور اللہ ہی کے لیے ہیں آسمانوں اور زمین کے خزانے مگر منافقوں کو سمجھ نہیں،'),
(5196, 63, 8, 'کہتے ہیں ہم مدینہ پھر کر گئے تو ضرور جو بڑی عزت والا ہے وہ اس میں سے نکال دے گا اسے جو نہایت ذلت والا ہے اور عزت تو اللہ اور اس کے رسول اور مسلمانوں ہی کے لیے ہے مگر منافقوں کو خبر نہیں'),
(5197, 63, 9, 'اے ایمان والو تمہارے مال نہ تمہاری اولاد کوئی چیز تمہیں اللہ کے ذکر سے غافل نہ کرے اور جو ایسا کرے تو وہی لوگ نقصان میں ہیں'),
(5198, 63, 10, 'اور ہمارے دیے میں سے کچھ ہماری راہ میں خرچ کرو قبل اس کے کہ تم میں کسی کو موت آئے پھر کہنے لگے، اے میرے رب تو نے مجھے تھوڑی مدت تک کیوں مہلت نہ دی کہ میں صدقہ دیتا اور نیکوں میں ہوتا،'),
(5199, 63, 11, 'اور ہرگز اللہ کسی جان کو مہلت نہ دے گا جب اس کا وعدہ آجائے اور اللہ کو تمہارے کاموں کی خبر ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'اللہ کی پاکی بولتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں، اسی کا ملک ہے اور اسی کی تعریف اور وہ ہر چیز پر قادر ہے،'),
(5201, 64, 2, 'وہی ہے جس نے تمہیں پیدا کیا تو تم میں کوئی کافر اور تم میں کوئی مسلمان اور اللہ تمہارے کام دیکھ رہا ہے،'),
(5202, 64, 3, 'اس نے آسمان اور زمین حق کے ساتھ بنائے اور تمہاری تصویر کی تو تمہاری اچھی صورت بنائی اور اسی کی طرف پھرنا ہے'),
(5203, 64, 4, 'جانتا ہے جو کچھ آسمانوں اور زمین میں ہے اور جانتا ہے جو تم چھپاتے اور ظاہر کرتے ہو، اور اللہ دلوں کی بات جانتا ہے،'),
(5204, 64, 5, 'کیا تمہیں ان کی خبر نہ آئی جنہوں نے تم سے پہلے کفر کیا اور اپنے کام کا وبال چکھا اور ان کے لیے دردناک عذاب ہے'),
(5205, 64, 6, 'یہ اس لیے کہ ان کے پاس ان کے رسول روشن دلیلیں لاتے تو بولے، کیا آدمی ہمیں راہ بتائیں گے تو کافر ہوئے اور پھر گئے اور اللہ نے بے نیازی کو کام فرمایا، اور اللہ بے نیاز ہے سب خوبیوں سراہا،'),
(5206, 64, 7, 'کافروں نے بکا کہ وہ ہرگز نہ اٹھائے جائیں گے، تم فرماؤ کیوں نہیں میرے رب کی قسم تم ضرور اٹھائے جاؤ گے پھر تمہارے کوتک تمہیں جتا دیے جائیں گے، اور یہ اللہ کو آسان ہے،'),
(5207, 64, 8, 'تو ایمان لاؤ اللہ اور اس کے رسول اور اس نور پر جو ہم نے اتارا، اور اللہ تمہارے کاموں سے خبردار ہے،'),
(5208, 64, 9, 'جس دن تمہیں اکٹھا کرے گا سب جمع ہونے کے دن وہ دن ہے ہار والوں کی ہار کھلنے کا اور جو اللہ پر ایمان لائے اور اچھا کام کرے اللہ اس کی برائیاں اتاردے گا اور اسے باغوں میں لے جائے گا جن کے نیچے نہریں بہیں کہ وہ ہمیشہ ان میں رہیں، یہی بڑی کامیابی ہے،'),
(5209, 64, 10, 'اور جنہوں نے کفر کیا اور ہماری آیتیں جھٹلائیں وہ آگ والے ہیں ہمیشہ اس میں رہیں، اور کیا ہی برا انجام،'),
(5210, 64, 11, 'کوئی مصیبت نہیں پہنچتی مگر اللہ کے حکم سے، اور جو اللہ پر ایمان لائے اللہ اس کے دل کو ہدایت فرمادے گا اور اللہ سب کچھ جانتا ہے،'),
(5211, 64, 12, 'اور اللہ کا حکم مانو اور رسول کا حکم مانو، پھر اگر تم منہ پھیرو تو جان لو کہ ہمارے رسول پر صرف صریح پہنچا دینا ہے'),
(5212, 64, 13, 'اللہ ہے جس کے سوا کسی کی بندگی نہیں اور اللہ ہی پر ایمان والے بھروسہ کریں،'),
(5213, 64, 14, 'اے ایمان والو تمہاری کچھ بی بیا ں اور بچے تمہارے دشمن ہیں تو ان سے احتیاط رکھو اور اگر معاف کرو اور درگزرو اور بخش دو تو بیشک اللہ بخشنے والا مہربان ہے،'),
(5214, 64, 15, 'تمہارے مال اور تمہارے بچے جانچ ہی ہیں اور اللہ کے پاس بڑا ثواب ہے'),
(5215, 64, 16, 'تو اللہ سے ڈرو جہاں تک ہوسکے اور فرمان سنو اور حکم مانو اور اللہ کی راہ میں خرچ کرو اپنے بھلے کو، اور جو اپنی جان کے لالچ سے بچایا گیا تو وہی فلاح پانے والے ہیں،'),
(5216, 64, 17, 'اگر تم اللہ کو اچھا قرض دو گے وہ تمہارے لیے اس کے دونے کردے گا اور تمہیں بخش دے گا اور اللہ قدر فرمانے والے حلم والا ہے'),
(5217, 64, 18, 'ہر نہاں اور عیاں کا جاننے والا عزت والا حکمت والا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اے نبی جب تم لوگ عورتوں کو طلاق دو تو ان کی عدت کے وقت پر انہیں طلاق دو اور عدت کا شمار رکھو اور اپنے رب اللہ سے ڈرو، عدت میں انہیں ان کے گھروں سے نہ نکالو اور نہ وہ آپ نکلیں مگر یہ کہ کوئی صریح بے حیائی کی بات لائیں اور یہ اللہ کی حدیں ہیں، اور جو اللہ کی حدوں سے آگے بڑھا بیشک اس نے اپنی جان پر ظلم کیا، تمہیں نہیں معلوم شاید اللہ اس کے بعد کوئی نیا حکم بھیجے'),
(5219, 65, 2, 'تو جب وہ اپنی میعاد تک پہنچنے کو ہوں تو انہیں بھلائی کے ساتھ روک لو یا بھلائی کے ساتھ جدا کرو اور اپنے میں دو ثقہ کو گواہ کرلو اور اللہ کے لیے گواہی قائم کرو اس سے نصیحت فرمائی جاتی ہے اسے جو اللہ اور پچھلے دن پر ایمان رکھتا ہو اور جو اللہ سے ڈرے اللہ اس کے لیے نجات کی راہ نکال دے گا'),
(5220, 65, 3, 'اور اسے وہاں سے روزی دے گا جہاں اس کا گمان نہ ہو، اور جو اللہ پر بھروسہ کرے تو وہ اسے کافی ہے بیشک اللہ اپنا کام پورا کرنے والا ہے، بیشک اللہ نے ہر چیز کا ایک اندازہ رکھا ہے،'),
(5221, 65, 4, 'اور تمہاری عورتوں میں جنہیں حیض کی امید نہ رہی اگر تمہیں کچھ شک ہو تو ان کی عدت تین مہینے ہے اور ان کی جنہیں ابھی حیض نہ آیا اور حمل والیوں کی میعاد یہ ہے کہ وہ اپنا حمل جَن لیں اور جو اللہ سے ڈرے اللہ اس کے کام میں آسانی فرمادے گا،'),
(5222, 65, 5, 'یہ اللہ کا حکم ہے کہ اس نے تمہاری طرف اتارا، اور جو اللہ سے ڈرے اللہ اس کی برائیاں اتار دے گا اور اسے بڑا ثواب دے گا،'),
(5223, 65, 6, 'عورتوں کو وہاں رکھو جہاں خود رہتے ہو اپنی طاقت بھر اور انہیں ضرر نہ دو کہ ان پر تنگی کرو اور اگر حمل والیاں ہوں تو انہیں نان و نفقہ دو یہاں تک کہ ان کے بچہ پیدا ہو پھر اگر وہ تمہارے لیے بچہ کو دودھ پلائیں تو انہیں اس کی اجرت دو اور آپس میں معقول طور پر مشورہ کرو پھر اگر باہم مضائقہ کرو (دشوار سمجھو) تو قریب ہے کہ اسے اور دودھ پلانے والی مل جائے گی،'),
(5224, 65, 7, 'مقدور والا اپنے مقدور کے قابل نفقہ دے، اور جس پر اس کا رزق تنگ کیا گیا وہ اس میں سے نفقہ دے جو اسے اللہ نے دیا، اللہ کسی جان پر بوجھ نہیں رکھتا مگر اسی قابل جتنا اسے دیا ہے، قریب ہے اللہ دشواری کے بعد آسانی فرمادے گا'),
(5225, 65, 8, 'اور کتنے ہی شہر تھے جنہوں نے اپنے رب کے حکم اور اس کے رسولوں سے سرکشی کی تو ہم نے ان سے سخت حسا ب لیا اور انہیں بری مار دی'),
(5226, 65, 9, 'تو انہوں نے اپنے کیے کا وبال چکھا اور ان کے کام انجام گھاٹا ہوا،'),
(5227, 65, 10, 'اللہ نے ان کے لیے سخت عذاب تیار کر رکھا ہے تو اللہ سے ڈرو اے عقل والو! وہ جو ایمان لائے ہو، بیشک اللہ نے تمہارے لیے عزت اتاری ہے،'),
(5228, 65, 11, 'وہ رسول کہ تم پر اللہ کی روشن آیتیں پڑھتا ہے تاکہ انہیں جو ایمان لائے اور اچھے کام کیے اندھیریوں سے اجالے کی طرف لے جائے، اور جو اللہ پر ایمان لائے اور اچھا کام کرے وہ اسے باغوں میں لے جائے گا جن کے نیچے نہریں بہیں جن میں ہمیشہ ہمیشہ رہیں، بیشک اللہ نے اس کے لیے اچھی روزی رکھی'),
(5229, 65, 12, 'اللہ ہے جس نے سات آسمان بنائے اور انہی کی برابر زمینیں حکم ان کے درمیان اترتا ہے تاکہ تم جان لو کہ اللہ سب کچھ کرسکتا ہے، اللہ کا علم ہر چیز کو محیط ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اے غیب بتا نے والے (نبی) تم اپنے اوپر کیوں حرام کئے لیتے ہو وه چیز جو اللہ نے تمھارے لئے حلال کی اپنی بیبیوں کی مرضی چاہتے ہو اور اللہ بخشنے والا ٴ مہربان ہے،'),
(5231, 66, 2, 'بیشک اللہ نے تمہارے لیے تمہاری قسموں کا اتار مقرر فرمادیا اور اللہ تمہارا مولیٰ ہے، اور اللہ علم و حکمت والا ہے،'),
(5232, 66, 3, 'اور جب نبی نے اپنی ایک بی بی سے ایک راز کی بات فرمائی پھر جب وہ اس کا ذکر کر بیٹھی اور اللہ نے اسے نبی پر ظاہر کردیا تو نبی نے اسے کچھ جتایا اور کچھ سے چشم پوشی فرمائی پھر جب نبی نے اسے اس کی خبر دی بولی حضور کو کس نے بتایا، فرمایا مجھے علم والے خبردار نے بتایا'),
(5233, 66, 4, 'نبی کی دونوں بیبیو! اگر اللہ کی طرف تم رجوع کرو تو ضرور تمہارے دل راہ سے کچھ ہٹ گئے ہیں اور اگر ان پر زور باندھو تو بیشک اللہ ان کا مددگار ہے اور جبریل اور نیک ایمان والے، اور اس کے بعد فرشتے مدد پر ہیں،'),
(5234, 66, 5, 'ان کا رب قریب ہے اگر وہ تمہیں طلاق دے دیں کہ انہیں تم سے بہتر بیبیاں بدل دے اطاعت والیاں ایمان والیاں ادب والیاں توبہ والیاں بندگی والیاں روزہ داریں بیاہیاں اور کنواریاں'),
(5235, 66, 6, 'اے ایمان والو! اپنی جانوں اور اپنے گھر والوں کو آگ سے بچاؤ جس کے ایندھن آدمی اور پتھر ہیں اس پر سخت کرّے (طاقتور) فرشتے مقرر ہیں جو اللہ کا حکم نہیں ٹالتے اور جو انہیں حکم ہو وہی کرتے ہیں'),
(5236, 66, 7, 'اے کافرو! آج بہانے نہ بناؤ تمہیں وہی بدلہ ملے گا جو تم کرتے تھے،'),
(5237, 66, 8, 'اے ایمان والو! اللہ کی طرف ایسی توبہ کرو جو آگے کو نصیحت ہوجائے قریب ہے تمہارا رب تمہاری برائیاں تم سے اتار دے اور تمہیں باغوں میں لے جائے جن کے نیچے نہریں بہیں جس دن اللہ رسوا نہ کرے گا نبی اور ان کے ساتھ کے ایمان والوں کو ان کا نور دوڑتا ہوگا ان کے آگے اور ان کے دہنے عرض کریں گے، اے ہمارے رب! ہمارے لیے ہمارا نور پورا کردے اور ہمیں بخش دے، بیشک تجھے ہر چیز پر قدرت ہے،'),
(5238, 66, 9, 'اے غیب بتانے والے! (نبی) کافروں پر اور منافقوں پر جہاد کرو اور ان پر سختی فرماؤ، اور ان کا ٹھکانا جہنم ہے، اور کیا ہی برا انجام،'),
(5239, 66, 10, 'اللہ کافروں کی مثال دیتا ہے نوح کی عورت اور لوط کی عورت، وہ ہمارے بندوں میں دو سزا وارِ (لائق) قرب بندوں کے نکاح میں تمہیں پھر انہوں نے ان سے دغا کی تو وہ اللہ کے سامنے انہیں کچھ کام نہ آئے اور فرما دیا گیا کے تم دونوں عورتیں جہنم میں جاؤ جانے والوں کے ساتھ'),
(5240, 66, 11, 'اور اللہ مسلمانو ں کی مثال بیان فر ماتا ہے فرعون کی بی بی جب اس نے عرض کی، اے میرے رب! میرے لیے اپنے پاس جنت میں گھر بنا اور مجھے فرعون اور اس کے کام سے نجات دے اور مجھے ظالم لوگوں سے نجات بخش'),
(5241, 66, 12, 'اور عمران کی بیٹی مریم جس نے اپنی پارسائی کی حفاظت کی تو ہم نے اس میں اپنی طرف کی روح پھونکی اور اس نے اپنے رب کی باتوں اور اس کی کتابوں کی تصدیق کی اور فرمانبرداروں میں ہوئی،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بڑی برکت والا ہے وہ جس کے قبضہ میں سارا ملک اور وہ ہر چیز پر قادر ہے،'),
(5243, 67, 2, 'وہ جس نے موت اور زندگی پیدا کی کہ تمہاری جانچ ہو تم میں کس کا کام زیادہ اچھا ہے اور وہی عزت والا بخشش والا ہے،'),
(5244, 67, 3, 'جس نے سات آسمان بنائے ایک کے اوپر دوسرا، تو رحمٰن کے بنانے میں کیا فرق دیکھتا ہے تو نگاہ اٹھا کر دیکھ تجھے کوئی رخنہ نظر آتا ہے،'),
(5245, 67, 4, 'پھر دوبارہ نگاہ اٹھا نظر تیری طرف ناکام پلٹ آئے گی تھکی ماندی'),
(5246, 67, 5, 'اور بیشک ہم نے نیچے کے آسمان کو چراغوں سے آراستہ کیا اور انہیں شیطانوں کے لیے مار کیا اور ان کے لیے بھڑکتی آگ کا عذاب تیار فرمایا'),
(5247, 67, 6, 'اور جنہوں نے اپنے رب کے ساتھ کفر کیا ان کے لیے جہنم کا عذاب ہے، اور کیا ہی برا انجام،'),
(5248, 67, 7, 'جب اس میں ڈالے جائیں گے اس کا رینکنا (چنگھاڑنا) سنیں گے کہ جوش مارتی ہے'),
(5249, 67, 8, 'معلوم ہوتا ہے کہ شدت غضب میں پھٹ جائے گی، جب کبھی کوئی گروہ اس میں ڈالا جائے گا اس کے داروغہ ان سے پوچھیں گے کیا تمہارے پاس کوئی ڈر سنانے والا نہ آیا تھا'),
(5250, 67, 9, 'کہیں گے کیوں نہیں بیشک ہمارے پاس ڈر سنانے والے تشریف لائے پھر ہم نے جھٹلایا اور کہا اللہ نے کچھ نہیں اُتارا، تم تو نہیں مگر بڑی گمراہی میں،'),
(5251, 67, 10, 'اور کہیں گے اگر ہم سنتے یا سمجھتے تو دوزخ والوں میں نہ ہوتے،'),
(5252, 67, 11, 'اب اپنے گناہ کا اقرار کیا تو پھٹکار ہو دوزخیوں کو،'),
(5253, 67, 12, 'بیشک جو بے دیکھے اپنے رب سے ڈرتے ہیں ان کے لیے بخشش اور بڑا ثواب ہے'),
(5254, 67, 13, 'اور تم اپنی بات آہستہ کہو یا آواز سے، وہ تو دلوں کی جانتا ہے'),
(5255, 67, 14, 'کیا وہ نہ جانے جس نے پیدا کیا؟ اور وہی ہے ہر باریکی جانتا خبردار،'),
(5256, 67, 15, 'وہی ہے جس نے تمہارے لیے زمین رام (تابع) کر دی تو اس کے رستوں میں چلو اور اللہ کی روزی میں سے کھاؤ اور اسی کی طرف اٹھنا ہے'),
(5257, 67, 16, 'کیا تم اس سےنڈر ہوگئے جس کی سلطنت آسمان میں ہے کہ تمہیں زمین میں دھنسادے جبھی وہ کانپتی رہے'),
(5258, 67, 17, 'یا تم نڈر ہوگئے اس سے جس کی سلطنت آسمان میں ہے کہ تم پر پتھراؤ بھیجے تو اب جانو گے کیسا تھا میرا ڈرانا،'),
(5259, 67, 18, 'اور بیشک ان سے اگلوں نے جھٹلایا تو کیسا ہوا میرا انکار'),
(5260, 67, 19, 'اور کیا انہوں نے اپنے اوپر پرندے نہ دیکھے پر پھیلاتے اور سمیٹتے انہیں کوئی نہیں روکتا سوا رحمٰن کے بیشک وہ سب کچھ دیکھتا ہے،'),
(5261, 67, 20, 'یا وہ کونسا تمہارا لشکر ہے کہ رحمٰن کے مقابل تمہاری مدد کرے کافر نہیں مگر دھوکے میں'),
(5262, 67, 21, 'یا کونسا ایسا ہے جو تمہیں روزی دے اگر وہ اپنی روزی روک لے بلکہ وہ سرکش اور نفرت میں ڈھیٹ بنے ہوئے ہیں'),
(5263, 67, 22, 'تو کیا وہ جو اپنے منہ کے بل اوندھا چلے زیادہ راہ پر ہے یا وہ جو سیدھا چلے سیدھی راہ پر'),
(5264, 67, 23, 'تم فرماؤ وہی ہے جس نے تمہیں پیدا کیا اور تمہارے لیے کان اور آنکھ اور دل بنائے کتنا کم حق مانتے ہو'),
(5265, 67, 24, 'تم فرماؤ وہی ہے جس نے تمہیں زمین میں پھیلایا اور اسی کی طرف اٹھائے جاؤ گے'),
(5266, 67, 25, 'اور کہتے ہیں یہ وعدہ کب آئے گا اگر تم سچے ہو،'),
(5267, 67, 26, 'تم فرماؤ یہ علم تو اللہ کے پاس ہے، اور میں تو یہی صاف ڈر سنانے والا ہوں'),
(5268, 67, 27, 'پھر جب اسے پاس دیکھیں گے کافروں کے منہ بگڑ جائیں گے اور ان سے فرمادیا جائے گا یہ ہے جو تم مانگتے تھے'),
(5269, 67, 28, 'تم فرماؤ بھلا دیکھو تو اگر اللہ مجھے اور میرے ساتھ والوں کو بلاک کردے یا ہم پر رحم فرمائے تو وہ کونسا ہے جو کافروں کو دکھ کے عذاب سے بچالے گا'),
(5270, 67, 29, 'تم فرماؤ وہی رحمٰن ہے ہم اس پر ایمان لائے اور اسی پر بھروسہ کیا، تو اب جان جاؤ گے کون کھلی گمراہی میں ہے،'),
(5271, 67, 30, 'تم فرماؤ بھلا دیکھو تو اگر صبح کو تمہارا پانی زمین میں دھنس جائے تو وہ کون ہے جو تمہیں پانی لادے نگاہ کے سامنے بہتا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'قلم اور ان کے لکھے کی قسم'),
(5273, 68, 2, 'تم اپنے رب کے فضل سے مجنون نہیں'),
(5274, 68, 3, 'اور ضرور تمہارے لیے بے انتہا ثواب ہے'),
(5275, 68, 4, 'اور بیشک تمہاری خُو بُو (خُلق) بڑی شان کی ہے'),
(5276, 68, 5, 'تو اب کوئی دم جاتا ہے کہ تم بھی دیکھ لو گے اور وہ بھی دیکھ لیں گے'),
(5277, 68, 6, 'کہ تم میں کون مجنون تھا،'),
(5278, 68, 7, 'بیشک تمہارا رب خوب جانتا ہے جو اس کی راہ سے بہکے، اور وہ خوب جانتا ہے جو راہ پر ہے،'),
(5279, 68, 8, 'تو جھٹلانے والوں کی بات نہ سننا،'),
(5280, 68, 9, 'وہ تو اس آرزو میں ہیں کہ کسی طرح تم نرمی کرو تو وہ بھی نرم پڑجائیں،'),
(5281, 68, 10, 'اور ہر ایسے کی بات نہ سننا جو بڑا قسمیں کھانے والا ذلیل'),
(5282, 68, 11, 'بہت طعنے دینے والا بہت اِدھر کی اُدھر لگاتا پھرنے والا'),
(5283, 68, 12, 'بھلائی سے بڑا روکنے والا حد سے بڑھنے والا گنہگار'),
(5284, 68, 13, 'درشت خُو اس سب پر طرہ یہ کہ اس کی اصل میں خطا'),
(5285, 68, 14, 'اس پر کہ کچھ مال اور بیٹے رکھتا ہے،'),
(5286, 68, 15, 'جب اس پر ہماری آیتیں پڑھی جائیں کہتا ہے کہ اگلوں کی کہانیاں ہیں'),
(5287, 68, 16, 'قریب ہے کہ ہم اس کی سور کی سی تھوتھنی پر داغ دیں گے'),
(5288, 68, 17, 'بیشک ہم نے انہیں جانچا جیسا اس باغ والوں کو جانچا تھا جب انہوں نے قسم کھائی کہ ضرور صبح ہوتے اس کھیت کو کاٹ لیں گے'),
(5289, 68, 18, 'اور انشاء اللہ نہ کہا'),
(5290, 68, 19, 'تو اس پر تیرے رب کی طرف سے ایک پھیری کرنے والا پھیرا کر گیا اور وہ سوتے تھے،'),
(5291, 68, 20, 'تو صبح رہ گیا جیسے پھل ٹوٹا ہوا'),
(5292, 68, 21, 'پھر انہوں نے صبح ہوتے ایک دوسرے کو پکارا،'),
(5293, 68, 22, 'کہ تڑکے اپنی کھیتی چلو اگر تمہیں کاٹنی ہے،'),
(5294, 68, 23, 'تو چلے اور آپس میں آہستہ آہستہ کہتے جاتے تھے کہ'),
(5295, 68, 24, 'ہرگز آج کوئی مسکین تمہارے باغ میں آنے نہ پائے،'),
(5296, 68, 25, 'اور تڑکے چلے اپنے اس ارادہ پر قدرت سمجھتے'),
(5297, 68, 26, 'پھر جب اسے بولے بیشک ہم راستہ بہک گئے'),
(5298, 68, 27, 'بلکہ ہم بے نصیب ہوئے'),
(5299, 68, 28, 'ان میں جو سب سے غنیمت تھا بولا کیا میں تم سے نہیں کہتا تھا کہ تسبیح کیوں نہیں کرتے'),
(5300, 68, 29, 'بولے پاکی ہے ہمارے رب کو بیشک ہم ظالم تھے،'),
(5301, 68, 30, 'اب ایک دوسرے کی طرف ملامت کرتا متوجہ ہوا'),
(5302, 68, 31, 'بولے ہائے خرابی ہماری بیشک ہم سرکش تھے'),
(5303, 68, 32, 'امید ہے ہمیں ہمارا رب اس سے بہتر بدل دے ہم اپنے رب کی طرف رغبت لاتے ہیں'),
(5304, 68, 33, 'مار ایسی ہوتی ہے اور بیشک آخرت کی مار سب سے بڑی، کیا اچھا تھا اگر وہ جانتے'),
(5305, 68, 34, 'بیشک ڈر والوں کے لیے ان کے رب کے پاس چین کے باغ ہیں'),
(5306, 68, 35, 'کیا ہم مسلمانوں کو مجرموں کا سا کردیں'),
(5307, 68, 36, 'تمہیں کیا ہوا کیسا حکم لگاتے ہو'),
(5308, 68, 37, 'کیا تمہارے لیے کوئی کتاب ہے اس میں پڑھتے ہو،'),
(5309, 68, 38, 'کہ تمہارے لیے اس میں جو تم پسند کرو،'),
(5310, 68, 39, 'یا تمہارے لیے ہم پر کچھ قسمیں ہیں قیامت تک پہنچتی ہوئی کہ تمہیں ملے گا جو کچھ دعویٰ کرتے ہو'),
(5311, 68, 40, 'تم ان سے پوچھو ان میں کون سا اس کا ضامن ہے'),
(5312, 68, 41, 'یا ان کے پاس کچھ شریک ہیں تو اپنے شریکوں کو لے کر آئیں اگر سچے ہیں'),
(5313, 68, 42, 'جس دن ایک ساق کھولی جائے گی (جس کے معنی اللہ ہی جانتا ہے) اور سجدہ کو بلائے جائیں گے تو نہ کرسکیں گے'),
(5314, 68, 43, 'نیچی نگاہیں کیے ہوئے ان پر خواری چڑھ رہی ہوگی، اور بیشک دنیا میں سجدہ کے لیے بلائے جاتے تھے جب تندرست تھے'),
(5315, 68, 44, 'تو جو اس بات کو جھٹلاتا ہے اسے مجھ پر چھوڑ دو قریب ہے کہ ہم انہیں آہستہ آہستہ لے جائیں گے جہاں سے انہیں خبر نہ ہوگی،'),
(5316, 68, 45, 'اور میں انہیں ڈھیل دوں گا، بیشک میری خفیہ تدبیر بہت پکی ہے'),
(5317, 68, 46, 'یا تم ان سے اجرت مانگتے ہو کہ وہ چٹی کے بوجھ میں دبے ہیں'),
(5318, 68, 47, 'یا ان کے پاس غیب ہے کہ وہ لکھ رہے ہیں'),
(5319, 68, 48, 'تو تم اپنے رب کے حکم کا انتظار کر و اور اس مچھلی والے کی طرح نہ ہونا جب اس حال میں پکارا کہ اس کا دل گھٹ رہا تھا'),
(5320, 68, 49, 'اگر اس کے رب کی نعمت اس کی خبر کو نہ پہنچ جاتی تو ضرور میدان پر پھینک دیا جاتا الزام دیا ہوا'),
(5321, 68, 50, 'تو اسے اس کے رب نے چن لیا اور اپنے قربِ خاص کے سزاواروں (حقداروں) میں کرلیا،'),
(5322, 68, 51, 'اور ضرور کافر تو ایسے معلوم ہوتے ہیں کہ گویا اپنی بد نظر لگا کر تمہیں گرادیں گے جب قرآن سنتے ہیں اور کہتے ہیں یہ ضرور عقل سے دور ہیں،'),
(5323, 68, 52, 'اور وہ تو نہیں مگر نصیحت سارے جہاں کے لیے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'وہ حق ہونے والی'),
(5325, 69, 2, 'کیسی وہ حق ہونے والی'),
(5326, 69, 3, 'اور تم نے کیا جانا کیسی وہ حق ہونے والی'),
(5327, 69, 4, 'ثمود اور عاد نے اس سخت صدمہ دینے والی کو جھٹلایا،'),
(5328, 69, 5, 'تو ثمود تو ہلاک کیے گئے حد سے گزری ہوئی چنگھاڑ سے'),
(5329, 69, 6, 'اور رہے عاد وہ ہلاک کیے گئے نہایت سخت گرجتی آندھی سے،'),
(5330, 69, 7, 'وہ ان پر قوت سے لگادی سات راتیں اور آٹھ دن لگاتار تو ان لوگوں کو ان میں دیکھو بچھڑے ہوئے گویا وہ کھجور کے ڈھنڈ (سوکھے تنے) ہیں گرے ہوئے،'),
(5331, 69, 8, 'تو تم ان میں کسی کو بچا ہوا دیکھتے ہو'),
(5332, 69, 9, 'اور فرعون اور اس سے اگلے اور الٹنے والی بستیاں خطا لائے'),
(5333, 69, 10, 'تو انہوں نے اپنے رب کے رسولوں کا حکم نہ مانا تو اس نے انہیں بڑھی چڑھی گرفت سے پکڑا،'),
(5334, 69, 11, 'بیشک جب پانی نے سر اٹھایا تھا ہم نے تمہیں کشتی میں سوار کیا'),
(5335, 69, 12, 'کہ اسے تمہارے لیے یادگار کریں اور اسے محفوظ رکھے وہ کان کہ سن کر محفوظ رکھتا ہو'),
(5336, 69, 13, 'پھرجب صور پھونک دیا جائے ایک دم،'),
(5337, 69, 14, 'اور زمین اور پہاڑ اٹھا کر دفعتا ً چُورا کردیے جائیں،'),
(5338, 69, 15, 'وہ دن ہے کہ ہو پڑے گی وہ ہونے والی'),
(5339, 69, 16, 'اور آسمان پھٹ جائے گا تو اس دن اس کا پتلا حال ہوگا'),
(5340, 69, 17, 'اور فرشتے اس کے کناروں پر کھڑے ہوں گے اور اس دن تمہارے رب کا عرش اپنے اوپر آٹھ فرشتے اٹھائیں گے'),
(5341, 69, 18, 'اس دن تم سب پیش ہو گے کہ تم میں کوئی چھپنے والی جان چھپ نہ سکے گی،'),
(5342, 69, 19, 'تو وہ جو اپنا نامہٴ اعمال دہنے ہاتھ میں دیا جائے گا کہے گا لو میرے نامہٴ اعمال پڑھو،'),
(5343, 69, 20, 'مجھے یقین تھا کہ میں اپنے حساب کو پہنچوں گا'),
(5344, 69, 21, 'تو وہ من مانتے چین میں ہے،'),
(5345, 69, 22, 'بلند باغ میں،'),
(5346, 69, 23, 'جس کے خوشے جھکے ہوئے'),
(5347, 69, 24, 'کھاؤ اور پیو رچتا ہوا صلہ اس کا جو تم نے گزرے دنوں میں آگے بھیجا'),
(5348, 69, 25, 'اور وہ جو اپنا نامہٴ اعمال بائیں ہاتھ میں دیا جائے گا کہے گا ہائے کسی طرح مجھے اپنا نوشتہ نہ دیا جاتا،'),
(5349, 69, 26, 'اور میں نہ جانتا کہ میرا حساب کیا ہے،'),
(5350, 69, 27, 'ہائے کسی طرح موت ہی قصہ چکا جاتی'),
(5351, 69, 28, 'میرے کچھ کام نہ آیا میرا مال'),
(5352, 69, 29, 'میرا سب زور جاتا رہا'),
(5353, 69, 30, 'اسے پکڑو پھر اسے طوق ڈالو'),
(5354, 69, 31, 'پھر اسے بھڑکتی آگ میں دھنساؤ،'),
(5355, 69, 32, 'پھر ایسی زنجیر میں جس کا ناپ ستر ہاتھ ہے اسے پُرو دو'),
(5356, 69, 33, 'بیشک وہ عظمت والے اللہ پر ایمان نہ لاتا تھا'),
(5357, 69, 34, 'اور مسکین کو کھانے دینے کی رغبت نہ دیتا'),
(5358, 69, 35, 'تو آج یہاں اس کا کوئی دوست نہیں'),
(5359, 69, 36, 'اور نہ کچھ کھانے کو مگر دوزخیوں کا پیپ،'),
(5360, 69, 37, 'اسے نہ کھائیں گے مگر خطاکار'),
(5361, 69, 38, 'تو مجھے قسم ان چیزوں کی جنہیں تم دیکھتے ہو،'),
(5362, 69, 39, 'اور جنہیں تم نہیں دیکھتے'),
(5363, 69, 40, 'بیشک یہ قرآن ایک کرم والے رسول سے باتیں ہیں'),
(5364, 69, 41, 'اور وہ کسی شاعر کی بات نہیں کتنا کم یقین رکھتے ہو'),
(5365, 69, 42, 'اور نہ کسی کاہن کی بات کتنا کم دھیان کرتے ہو'),
(5366, 69, 43, 'اس نے اتارا ہے جو سارے جہان کا رب ہے،'),
(5367, 69, 44, 'اور اگر وہ ہم پر ایک بات بھی بنا کر کہتے'),
(5368, 69, 45, 'ضرور ہم ان سے بقوت بدلہ لیتے،'),
(5369, 69, 46, 'پھر ان کی رگِ دل کاٹ دیتے'),
(5370, 69, 47, 'پھر تم میں کوئی ان کا بچانے والا نہ ہوتا،'),
(5371, 69, 48, 'اور بیشک یہ قرآن ڈر والوں کو نصیحت ہے،'),
(5372, 69, 49, 'اور ضرور ہم جانتے ہیں کہ تم کچھ جھٹلانے والے ہیں،'),
(5373, 69, 50, 'اور بیشک وہ کافروں پر حسرت ہے'),
(5374, 69, 51, 'اور بیشک وہ یقین حق ہے'),
(5375, 69, 52, 'تو اے محبوب تم اپنے عظمت والے رب کی پاکی بولو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'ایک مانگنے والا وہ عذاب مانگتا ہے،'),
(5377, 70, 2, 'جو کافروں پر ہونے والا ہے، اس کا کوئی ٹالنے والا نہیں،'),
(5378, 70, 3, 'وہ ہوگا اللہ کی طرف سے جو بلندیوں کا مالک ہے'),
(5379, 70, 4, 'ملائکہ اور جبریل اس کی بارگاہ کی طرف عروج کرتے ہیں وہ عذاب اس دن ہوگا جس کی مقدار پچاس ہزار برس ہے'),
(5380, 70, 5, 'تو تم اچھی طرح صبر کرو،'),
(5381, 70, 6, 'وہ اسے دور سمجھ رہے ہیں'),
(5382, 70, 7, 'اور ہم اسے نزدیک دیکھ رہے ہیں'),
(5383, 70, 8, 'جس دن آسمان ہوگا جیسی گلی چاندی،'),
(5384, 70, 9, 'اور پہاڑ ایسے ہلکے ہوجائیں گے جیسے اون'),
(5385, 70, 10, 'اور کوئی دوست کسی دوست کی بات نہ پوچھے گا'),
(5386, 70, 11, 'ہوں گے انہیں دیکھتے ہوئے مجرم آرزو کرے گا، کاش! اس دن کے عذاب سے چھٹنے کے بدلے میں دے دے اپنے بیٹے،'),
(5387, 70, 12, 'اور اپنی جورو اور اپنا بھائی،'),
(5388, 70, 13, 'اور اپنا کنبہ جس میں اس کی جگہ ہے،'),
(5389, 70, 14, 'اور جتنے زمین میں ہیں سب پھر یہ بدلہ دنیا اسے بچالے،'),
(5390, 70, 15, 'ہرگز نہیں وہ تو بھڑکتی آگ ہے،'),
(5391, 70, 16, 'کھال اتار لینے والی بلارہی ہے'),
(5392, 70, 17, 'اس کو جس نے پیٹھ دی اور منہ پھیرا'),
(5393, 70, 18, 'اور جوڑ کر سینت رکھا (محفوظ کرلیا)'),
(5394, 70, 19, 'بیشک آدمی بنایا گیا ہے بڑا بے صبرا حریص،'),
(5395, 70, 20, 'جب اسے برائی پہنچے تو سخت گھبرانے والا،'),
(5396, 70, 21, 'اور جب بھلائی پہنچے تو روک رکھنے والا'),
(5397, 70, 22, 'مگر نمازی،'),
(5398, 70, 23, 'جو اپنی نماز کے پابند ہیں'),
(5399, 70, 24, 'اور وہ جن کے مال میں ایک معلوم حق ہے'),
(5400, 70, 25, 'اس کے لیے جو مانگے اور جو مانگ بھی نہ سکے تو محروم رہے'),
(5401, 70, 26, 'اور ہو جو انصاف کا دن سچ جانتے ہیں'),
(5402, 70, 27, 'اور وہ جو اپنے رب کے عذاب سے ڈر رہے ہیں،'),
(5403, 70, 28, 'بیشک ان کے رب کا عذاب نڈر ہونے کی چیز نہیں'),
(5404, 70, 29, 'اور ہو جو اپنی شرمگاہوں کی حفاظت کرتے ہیں،'),
(5405, 70, 30, 'مگر اپنی بیبیوں یا اپنے ہاتھ کے مال کنیزوں سے کہ ان پر کچھ ملامت نہیں،'),
(5406, 70, 31, 'تو جو ان دو کے سوا اور چاہے وہی حد سے بڑھنے والے ہیں'),
(5407, 70, 32, 'اور وہ جو اپنی امانتوں اور اپنے عہد کی حفاظت کرتے ہیں'),
(5408, 70, 33, 'اور وہ جو اپنی گواہیوں پر قائم ہیں'),
(5409, 70, 34, 'اور وہ جو اپنی نماز کی حفاظت کرتے ہیں'),
(5410, 70, 35, 'یہ ہیں جن کا باغوں میں اعزاز ہوگا'),
(5411, 70, 36, 'تو ان کافروں کو کیا ہوا تمہاری طرف تیز نگاہ سے دیکھتے ہیں'),
(5412, 70, 37, 'داہنے اور بائیں گروہ کے گروہ،'),
(5413, 70, 38, 'کیا ان میں ہر شخص یہ طمع کرتا ہے کہ چین کے باغ میں داخل کیا جائے،'),
(5414, 70, 39, 'ہرگز نہیں، بیشک ہم نے انہیں اس چیز سے بنایا جسے جانتے ہیں'),
(5415, 70, 40, 'تو مجھے قسم ہے اس کی جو سب پُوربوں سب پچھموں کا مالک ہے کہ ضرور ہم قادر ہیں،'),
(5416, 70, 41, 'کہ ان سے اچھے بدل دیں اور ہم سے کوئی نکل کر نہیں جاسکتا'),
(5417, 70, 42, 'تو انہیں چھوڑ دو ان کی بیہودگیوں میں پڑے اور کھیلتے ہوئے یہاں تک کہ اپنے اس دن سے ملیں جس کا انہیں وعدہ دیا جاتا ہے،'),
(5418, 70, 43, 'جس دن قبروں سے نکلیں گے جھپٹتے ہوئے گویا وہ نشانیوں کی طرف لپک رہے ہیں'),
(5419, 70, 44, 'آنکھیں نیچی کیے ہوئے ان پر ذلت سوار، یہ ہے ان کا وہ دن جس کا ان سے وعدہ تھا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بیشک ہم نے نوح کو اس کی قوم کی طرف بھیجا کہ ان کو ڈرا اس سے پہلے کہ ان پر دردناک عذاب آئے'),
(5421, 71, 2, 'اس نے فرمایا اے میری قوم! میں تمہارے لیے صریح ڈر سنانے والا ہوں،'),
(5422, 71, 3, 'کہ اللہ کی بندگی کرو اور اس سے ڈرو اور میرا حکم مانو،'),
(5423, 71, 4, 'وہ تمہارے کچھ گناہ بخش دے گا اور ایک مقرر میعاد تک تمہیں مہلت دے گا بیشک اللہ کا وعدہ جب آتا ہے ہٹایا نہیں جاتا کسی طرح تم جانتے'),
(5424, 71, 5, 'عرض کی اے میرے رب! میں نے اپنی قوم کو رات دن بلایا'),
(5425, 71, 6, 'تو میرے بلانے سے انہیں بھاگنا ہی بڑھا'),
(5426, 71, 7, 'اور میں نے جتنی بار انہیں بلایا کہ تو ان کو بخشے انہوں نے اپنے کانوں میں انگلیاں دے لیں اور اپنے کپڑے اوڑھ لیے اور ہٹ کی اور بڑا غرور کیا'),
(5427, 71, 8, 'پھر میں نے انہیں علانیہ بلایا'),
(5428, 71, 9, 'پھر میں نے ان سے با علان بھی کہا اور آہستہ خفیہ بھی کہا'),
(5429, 71, 10, 'تو میں نے کہا اپنے رب سے معافی مانگو وہ بڑا معاف فرمانے والا ہے'),
(5430, 71, 11, 'تم پر شراٹے کا (موسلا دھار) مینھ بھیجے گا،'),
(5431, 71, 12, 'اور مال اور بیٹوں سے تمہاری مدد کرے گا اور تمہارے لیے باغ بنادے گا اور تمہارے لیے نہریں بنائے گا'),
(5432, 71, 13, 'تمہیں کیا ہوا اللہ سے عزت حاصل کرنے کی امید نہیں کرتے'),
(5433, 71, 14, 'حالانکہ اس نے تمہیں طرح طرح بنایا'),
(5434, 71, 15, 'کیا تم نہیں دیکھتے اللہ نے کیونکر سات آسمان بنائے ایک پر ایک،'),
(5435, 71, 16, 'اور ان میں چاند کو روشن کیا اور سورج کو چراغ'),
(5436, 71, 17, 'اور اللہ نے تمہیں سبزے کی طرح زمین سے اُگایا'),
(5437, 71, 18, 'پھر تمہیں اسی میں لے جائے گا اور دبارہ نکالے گا'),
(5438, 71, 19, 'اور اللہ نے تمہارے لیے زمین کو بچھونا بنایا،'),
(5439, 71, 20, 'کہ اس کے وسیع راستوں میں چلو،'),
(5440, 71, 21, 'نوح نے عرض کی، اے میرے رب! انہوں نے میری نافرمانی کی اور ایسے کے پیچھے ہولیے جیسے اس کے مال اور اولاد نے نقصان ہی بڑھایا'),
(5441, 71, 22, 'اور بہت بڑا داؤ ں کھیلے'),
(5442, 71, 23, 'اور بولے ہرگز نہ چھوڑنا اپنے خداؤں کو اور ہرگز نہ چھوڑنا ودّ اور سواع اور یغوث اور یعوق اور نسر کو'),
(5443, 71, 24, 'اور بیشک انہوں نے بہتوں کو بہکایا اور تو ظالموں کو زیادہ نہ کرنا مگر گمراہی'),
(5444, 71, 25, 'اپنی کیسی خطاؤں پر ڈبوئے گئے پھر آگ میں داخل کیے گئے تو انہوں نے اللہ کے مقابل اپنا کوئی مددگار نہ پایا'),
(5445, 71, 26, 'اور نوح نے عرض کی، اے میرے رب! زمین پر کافروں میں سے کوئی بسنے والا نہ چھوڑ،'),
(5446, 71, 27, 'بیشک اگر تو انہیں رہنے دے گا تو تیرے بندوں کو گمراہ کردیں گے اور ان کے اولاد ہوگی تو وہ بھی نہ ہوگی مگر بدکار بڑی ناشکر'),
(5447, 71, 28, 'اے میرے رب مجھے بخش دے اور میرے ماں باپ کو اور اسے جو ایمان کے ساتھ میرے گھر میں ہے اور سب مسلمان مردوں اور سب مسلمان عورتوں کو، اور کافروں کو نہ بڑھا مگر تباہی');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'تم فرماؤ مجھے وحی ہوئی کہ کچھ جنوں نے میرا پڑھنا کان لگا کر سنا تو بولے ہم نے ایک عجیب قرآن سنا'),
(5449, 72, 2, 'کہ بھلائی کی راہ بتا تا ہے تو ہم اس پر ایمان لائے، اور ہم ہرگز کسی کو اپنے رب کا شریک نہ کریں گے،'),
(5450, 72, 3, 'اور یہ کہ ہمارے رب کی شان بہت بلند ہے نہ اس نے عورت اختیار کی اور نہ بچہ'),
(5451, 72, 4, 'اور یہ کہ ہم میں کا بے وقوف اللہ پر بڑھ کر بات کہتا تھا'),
(5452, 72, 5, 'اور یہ کہ ہمیں خیال تھا کہ ہرگز آدمی اور جِن اللہ پر جھوٹ نہ باندھیں گے'),
(5453, 72, 6, 'اور یہ کہ آدمیوں میں کچھ مرد جنوں کے کچھ مردوں کے پناہ لیتے تھے تو اس سے اور بھی ان کا تکبر بڑھا،'),
(5454, 72, 7, 'اور یہ کہ انہوں نے گمان کیا جیسا تمہیں گمان ہے کہ اللہ ہرگز کوئی رسول نہ بھیجے گا،'),
(5455, 72, 8, 'اور یہ کہ ہم نے آسمان کو چھوا تو اسے پایا کہ سخت پہرے اور آگ کی چنگاریوں سے بھردیا گیا ہے'),
(5456, 72, 9, 'اور یہ کہ ہم پہلے آسمان میں سننے کے لیے کچھ موقعوں پر بیٹھا کرتے تھے، پھر اب جو کوئی سنے وہ اپنی تاک میں آگ کا لُوکا (لپٹ) پائے'),
(5457, 72, 10, 'اور یہ کہ ہمیں نہیں معلوم کہ زمین والوں سے کوئی برائی کا ارادہ فرمایا گیا ہے یا ان کے نے کوئی بھلائی چاہی ہے،'),
(5458, 72, 11, 'اور یہ کہ ہم میں کچھ نیک ہیں اور کچھ دوسری طرح کے ہیں، ہم کئی راہیں پھٹے ہوئے ہیں'),
(5459, 72, 12, 'اور یہ کہ ہم کو یقین ہوا کہ ہر گز زمین اللہ کے قابو سے نہ نکل سکیں گے اور نہ بھاگ کر اس کے قبضہ سے باہر ہوں،'),
(5460, 72, 13, 'اور یہ کہ ہم نے جب ہدایت سنی اس پر ایمان لائے، تو جو اپنے رب پر ایمان لائے اسے نہ کسی کمی کا خوف اور نہ زیادگی کا'),
(5461, 72, 14, 'اور یہ کہ ہم میں کچھ مسلمان ہیں اور کچھ ظالم تو جو اسلام لائے انہوں نے بھلائی سوچی'),
(5462, 72, 15, 'اور رہے ظالم وہ جہنم کے ایندھن ہوئے'),
(5463, 72, 16, 'اور فرماؤ کہ مجھے یہ وحی ہوئی ہے کہ اگر وہ راہ پر سیدھے رہتے تو ضرور ہم انہیں وافر پانی دیتے'),
(5464, 72, 17, 'کہ اس پر انہیں جانچیں اور جو اپنے رب کی یاد سے منہ پھیرے وہ اسے چڑھتے عذاب میں ڈالے گا'),
(5465, 72, 18, 'اور یہ کہ مسجدیں اللہ ہی کی ہیں تو اللہ کے ساتھ کسی کی بندگی نہ کرو'),
(5466, 72, 19, 'اور یہ کہ جب اللہ کا بندہ اس کی بندگی کرنے کھڑا ہوا تو قریب تھا کہ وہ جن اس پر ٹھٹھ کے ٹھٹھ ہوجائیں'),
(5467, 72, 20, 'تم فرماؤ میں تو اپنے رب ہی کی بندگی کرتا ہوں اور کسی کو اس کا شریک نہیں ٹھہراتا،'),
(5468, 72, 21, 'تم فرماؤ میں تمہارے کسی برے بھلے کا مالک نہیں،'),
(5469, 72, 22, 'تم فرماؤ ہرگز مجھے اللہ سے کوئی نہ بچائے گا اور ہرگز اس کے سوا کوئی پناہ نہ پاؤں گا،'),
(5470, 72, 23, 'مگر اللہ کے پیام پہنچاتا اور اس کی رسالتیں اور جو اللہ اور اس کے رسول کا حکم نہ مانے تو بیشک ان کے لیے جہنم کی آگ ہے جس میں ہمیشہ ہمیشہ رہیں،'),
(5471, 72, 24, 'یہاں تک کہ جب دیکھیں گے جو وعدہ دیا جاتا ہے تو اب جان جائیں گے کہ کس ک مددگار کمزور اور کس کی گنتی کم'),
(5472, 72, 25, 'تم فرماؤ میں نہیں جانتا آیا نزدیک ہے وہ جس کا تمہیں وعدہ دیا جاتا ہے یا میرا رب اسے کچھ وقفہ دے گا'),
(5473, 72, 26, 'غیب کا جاننے والا تو اپنے غیب پر کسی کو مسلط نہیں کرتا'),
(5474, 72, 27, 'سوائے اپنے پسندیدہ رسولوں کے کہ ان کے آگے پیچھے پہرا مقرر کر دیتا ہے'),
(5475, 72, 28, 'تا کہ دیکھ لے کہ انہوں نے اپنے رب کے پیام پہنچا دیے اور جو کچھ ان کے پاس سب اس کے علم میں ہے اور اس نے ہر چیز کی گنتی شمار کر رکھی ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اے جھرمٹ مارنے والے'),
(5477, 73, 2, 'رات میں قیام فرما سوا کچھ رات کے'),
(5478, 73, 3, 'آدھی رات یا اس سے کچھ تم کرو،'),
(5479, 73, 4, 'یا اس پر کچھ بڑھاؤ اور قرآن خوب ٹھہر ٹھہر کر پڑھو'),
(5480, 73, 5, 'بیشک عنقریب ہم تم پر ایک بھاری بات ڈالیں گے'),
(5481, 73, 6, 'بیشک رات کا اٹھنا وہ زیادہ دباؤ ڈالتا ہے اور بات خوب سیدھی نکلتی ہے'),
(5482, 73, 7, 'بیشک دن میں تو تم کو بہت سے کام ہیں'),
(5483, 73, 8, 'اور اپنے رب کا نام یاد کرو اور سب سے ٹوٹ کر اسی کے ہو رہو'),
(5484, 73, 9, 'وہ پورب کا رب اور پچھم کا رب اس کے سوا کوئی معبود نہیں تو تم اسی کو اپنا کارساز بناؤ'),
(5485, 73, 10, 'اور کافروں کی باتوں پر صبر فرماؤ اور انہیں اچھی طرح چھوڑ دو'),
(5486, 73, 11, 'اور مجھ پر چھوڑو ان جھٹلانے والے مالداروں کو اور انہیں تھوڑی مہلت دو'),
(5487, 73, 12, 'بیشک ہمارے پاس بھاری بیڑیاں ہیں اور بھڑکتی آگ،'),
(5488, 73, 13, 'اور گلے میں پھنستا کھانا اور دردناک عذاب'),
(5489, 73, 14, 'جس دن تھرتھرائیں گے زمین اور پہاڑ اور پہاڑ ہوجائیں گے ریتے کا ٹیلہ بہتا ہوا،'),
(5490, 73, 15, 'بیشک ہم نے تمہاری طرف ایک رسول بھیجے کہ تم پر حاضر ناظر ہیں جیسے ہم نے فرعون کی طرف رسول بھیجے'),
(5491, 73, 16, 'تو فرعون نے اس رسول کا حکم نہ مانا تو ہم نے اسے سخت گرفت سے پکڑا،'),
(5492, 73, 17, 'پھر کیسے بچو گے اگر کفر کرو اس دن جو بچوں کو بوڑھا کردے گا'),
(5493, 73, 18, 'آسمان اس کے صدمے سے پھٹ جائے گا، اللہ کا وعدہ ہوکر رہنا،'),
(5494, 73, 19, 'بیشک یہ نصیحت ہے، تو جو چاہے اپنے رب کی طرف راہ لے'),
(5495, 73, 20, 'بیشک تمہارا رب جانتا ہے کہ تم قیام کرتے ہو کبھی دو تہائی رات کے قریب، کبھی آدمی رات، کبھی تہائی اور ایک جماعت تمہارے ساتھ والی اور اللہ رات اور دن کا اندازہ فرماتا ہے، اسے معلوم ہے کہ اے مسلمانو! تم سے رات کا شمار نہ ہوسکے گا تو اس نے اپنی مہر سے تم پر رجوع فرمائی اب قرآن میں سے جتنا تم پر آسان ہو اتنا پڑھو اسے معلوم ہے کہ عنقریب کچھ تم میں سے بیمار ہوں گے اور کچھ زمین میں سفر کریں گے اللہ کا فضل تلاش کرنے اور کچھ اللہ کی راہ میں لڑتے ہوں گے تو جتنا قرآن میسر ہو پڑھو اور نماز قائم رکھو اور زکوٰة دو اور اللہ کو اچھا قرض دو اور اپنے لیے جو بھلائی آگے بھیجو گے اسے اللہ کے پاس بہتر اور بڑے ثواب کی پاؤ گے، اور اللہ سے بخشش مانگو، بیشک اللہ بخشنے والا مہربان ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اے بالا پوش اوڑھنے والے!'),
(5497, 74, 2, 'کھڑے ہوجاؤ پھر ڈر سناؤ'),
(5498, 74, 3, 'اور اپنے رب ہی کی بڑائی بولو'),
(5499, 74, 4, 'اور اپنے کپڑے پاک رکھو'),
(5500, 74, 5, 'اور بتوں سے دور رہو،'),
(5501, 74, 6, 'اور زیادہ لینے کی نیت سے کسی پر احسان نہ کرو'),
(5502, 74, 7, 'اور اپنے رب کے لیے صبر کیے رہو'),
(5503, 74, 8, 'پھر جب صور پھونکا جائے گا'),
(5504, 74, 9, 'تو وہ دن کڑا (سخت) دن ہے،'),
(5505, 74, 10, 'کافروں پر آسان نہیں'),
(5506, 74, 11, 'اسے مجھ پر چھوڑ جسے میں نے اکیلا پیدا کیا'),
(5507, 74, 12, 'اور اسے وسیع مال دیا'),
(5508, 74, 13, 'اور بیٹے دیے سامنے حاضر رہتے'),
(5509, 74, 14, 'اور میں نے اس کے لیے طرح طرح کی تیاریاں کیں'),
(5510, 74, 15, 'پھر یہ طمع کرتا ہے کہ میں اور زیادہ دوں'),
(5511, 74, 16, 'ہرگز نہیں وہ تو میری آیتوں سے عناد رکھتا ہے،'),
(5512, 74, 17, 'قریب ہے کہ میں اسے آگ کے پہاڑ صعود پر چڑھاؤں،'),
(5513, 74, 18, 'بیشک وہ سوچا اور دل میں کچھ بات ٹھہرائی'),
(5514, 74, 19, 'تو اس پر لعنت ہو کیسی ٹھہرائی،'),
(5515, 74, 20, 'پھر اس پر لعنت ہو کیسی ٹھہرائی،'),
(5516, 74, 21, 'پھر نظر اٹھا کر دیکھا،'),
(5517, 74, 22, 'پھر تیوری چڑھائی اور منہ بگاڑا،'),
(5518, 74, 23, 'پھر پیٹھ پھیری اور تکبر کیا،'),
(5519, 74, 24, 'پھر بولا یہ تو وہی جادو ہے اگلوں سے سیکھا،'),
(5520, 74, 25, 'یہ نہیں مگر آدمی کا کلام'),
(5521, 74, 26, 'کوئی دم جاتا ہے کہ میں اسے دوزخ میں دھنساتا ہوں،'),
(5522, 74, 27, 'اور تم نے کیا جانا دوزخ کیا ہے،'),
(5523, 74, 28, 'نہ چھوڑے نہ لگی رکھے'),
(5524, 74, 29, 'آدمی کی کھال اتار لیتی ہے'),
(5525, 74, 30, 'اس پر اُنیس داروغہ ہیں'),
(5526, 74, 31, 'اور ہم نے دوزخ کے داروغہ نہ کیے مگر فرشتے، اور ہم نے ان کی یہ گنتی نہ رکھی مگر کافروں کی جانچ کو اس لیے کہ کتاب والوں کو یقین آئے اور ایمان والوں کا ایمان بڑھے اور کتاب والوں اور مسلمانوں کو کوئی شک نہ رہے اور دل کے روگی (مریض) اور کافر کہیں اس اچنبھے کی بات میں اللہ کا کیا مطلب ہے، یونہی اللہ گمراہ کرتا ہے جسے چاہے اور ہدایت فرماتا ہے جسے چاہے، اور تمہارے رب کے لشکروں کو اس کے سوا کوئی نہیں جانتا، اور وہ تو نہیں مگر آدمی کے لیے نصیحت،'),
(5527, 74, 32, 'ہاں ہاں چاند کی قسم،'),
(5528, 74, 33, 'اور رات کی جب پیٹھ پھیرے،'),
(5529, 74, 34, 'اور صبح کی جب اجا لا ڈالے'),
(5530, 74, 35, 'بیشک دوزخ بہت بڑی چیزوں میں کی ایک ہے،'),
(5531, 74, 36, 'آدمیوں کو ڈراؤ،'),
(5532, 74, 37, 'اسے جو تم میں چاہے، کہ آگے آئے یا پیچھے رہے'),
(5533, 74, 38, 'ہر جان اپنی کرنی (اعمال) میں گروی ہے،'),
(5534, 74, 39, 'مگر دہنی طرف والے'),
(5535, 74, 40, 'باغوں میں پوچھتے ہیں،'),
(5536, 74, 41, 'مجرموں سے،'),
(5537, 74, 42, 'تمہیں کیا بات دوزخ میں لے گئی،'),
(5538, 74, 43, 'وہ بولے ہم نماز نہ پڑھتے تھے،'),
(5539, 74, 44, 'اور مسکین کو کھانا نہ دیتے تھے'),
(5540, 74, 45, 'اور بیہودہ فکر والوں کے ساتھ بیہودہ فکریں کرتے تھے،'),
(5541, 74, 46, 'اور ہم انصاف کے دن کو جھٹلاتے رہے،'),
(5542, 74, 47, 'یہاں تک کہ ہمیں موت آئی،'),
(5543, 74, 48, 'تو انہیں سفارشیوں کی سفارش کام نہ دے گی'),
(5544, 74, 49, 'تو انہیں کیا ہوا نصیحت سے منہ پھیرتے ہیں'),
(5545, 74, 50, 'گویا وہ بھڑکے ہوئے گدھے ہوں،'),
(5546, 74, 51, 'کہ شیر سے بھاگے ہوں'),
(5547, 74, 52, 'بلکہ ان میں کا ہر شخص چاہتا ہے کہ کھلے صحیفے اس کے ہاتھ میں دے دیے جائیں'),
(5548, 74, 53, 'ہرگز نہیں بلکہ ان کو آخرت کا ڈر نہیں'),
(5549, 74, 54, 'ہاں ہاں بیشک وہ نصیحت ہے،'),
(5550, 74, 55, 'تو جو چاہے اس سے نصیحت لے،'),
(5551, 74, 56, 'اور وہ کیا نصیحت مانیں مگر جب اللہ چاہے، وہی ہے ڈرنے کے لائق اور اسی کی شان ہے مغفرت فرمانا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'روزِ قیامت کی قسم! یاد فرماتا ہوں،'),
(5553, 75, 2, 'اور اس جان کی قسم! جو اپنے اوپر ملامت کرے'),
(5554, 75, 3, 'کیا آدمی یہ سمجھتا ہے کہ ہم ہرگز اس کی ہڈیاں جمع نہ فرمائیں گے،'),
(5555, 75, 4, 'کیوں نہیں ہم قادر ہیں کہ اس کے پور ٹھیک بنادیں'),
(5556, 75, 5, 'بلکہ آدمی چاہتا ہے کہ اس کی نگاہ کے سامنے بدی کرے'),
(5557, 75, 6, 'پوچھتا ہے قیامت کا دن کب ہوگا،'),
(5558, 75, 7, 'پھر جس دن آنکھ چوندھیائے گی'),
(5559, 75, 8, 'اور چاند کہے گا'),
(5560, 75, 9, 'اور سورج اور چاند ملادیے جائیں گے'),
(5561, 75, 10, 'اس دن آدمی کہے گا کدھر بھاگ کر جاؤں'),
(5562, 75, 11, 'ہرگز نہیں کوئی پناہ نہیں،'),
(5563, 75, 12, 'اس دن تیرے رب ہی کی طرف جاکر ٹھہرنا ہے'),
(5564, 75, 13, 'اس دن آدمی کو اس کا سب اگلا پچھلا جتادیا جائے گا'),
(5565, 75, 14, 'بلکہ آدمی خود ہی اپنے حال پر پوری نگاہ رکھتا ہے،'),
(5566, 75, 15, 'اور اگر اس کے پاس جتنے بہانے ہوں سب لا ڈالے،'),
(5567, 75, 16, 'جب بھی نہ سنا جائے گا تم یاد کرنے کی جلدی میں قرآن کے ساتھ اپنی زبان کو حرکت نہ دو'),
(5568, 75, 17, 'بیشک اس کا محفوظ کرنا اور پڑھنا ہمارے ذمہ ہے،'),
(5569, 75, 18, 'تو جب ہم اسے پڑھ چکیں اس وقت اس پڑھے ہوئے کی اتباع کرو'),
(5570, 75, 19, 'پھر بیشک اس کی باریکیوں کا تم پر ظاہر فرمانا ہمارے ذمہ ہے،'),
(5571, 75, 20, 'کوئی نہیں بلکہ اے کافرو! تم پاؤں تلے کی (دنیاوی فائدے کو) عزیز دوست رکھتے ہو'),
(5572, 75, 21, 'اور آخرت کو چھوڑ بیٹھے ہو،'),
(5573, 75, 22, 'کچھ منہ اس دن تر و تازہ ہوں گے'),
(5574, 75, 23, 'اپنے رب کا دیکھتے'),
(5575, 75, 24, 'اور کچھ منہ اس دن بگڑے ہوئے ہوں گے'),
(5576, 75, 25, 'سمجھتے ہوں گے کہ ان کے ساتھ وہ کی جائے گی جو کمر کو توڑ دے'),
(5577, 75, 26, 'ہاں ہاں جب جان گلے کو پہنچ جائے گی'),
(5578, 75, 27, 'اور کہیں گے کہ ہے کوئی جھاڑ پھونک کرے'),
(5579, 75, 28, 'سمجھ لے گا کہ یہ جدائی کی گھڑی ہے'),
(5580, 75, 29, 'اور پنڈلی سے پنڈلی لپٹ جائے گی'),
(5581, 75, 30, 'اس دن تیرے رب ہی کی طرف ہانکنا ہے'),
(5582, 75, 31, 'اس نے نہ تو سچ مانا اور نہ نماز پڑھی،'),
(5583, 75, 32, 'ہاں جھٹلایا اور منہ پھیرا'),
(5584, 75, 33, 'پھر اپنے گھر کو اکڑتا چلا'),
(5585, 75, 34, 'تیری خرابی ا ٓ لگی اب آ لگی،'),
(5586, 75, 35, 'پھر تیری خرابی آ لگی اب آ لگی،'),
(5587, 75, 36, 'کیا آدمی اس گھمنڈ میں ہے کہ آزاد چھوڑ دیا جائے گا'),
(5588, 75, 37, 'کیا وہ ایک بوند نہ تھا اس منی کا کہ گرائی جائے'),
(5589, 75, 38, 'پھر خون کی پھٹک ہوا تو اس نے پیدا فرمایا پھر ٹھیک بنایا'),
(5590, 75, 39, 'تو اس سے دو جوڑ بنائے مرد اور عورت،'),
(5591, 75, 40, 'کیا جس نے یہ کچھ کیا وہ مردے نہ جِلا سکے گا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'بیشک آدمی پر ایک وقت وہ گزرا کہ کہیں اس کا نام بھی نہ تھا'),
(5593, 76, 2, 'بیشک ہم نے آدمی کو کیا ملی ہوئی منی سے کہ وہ اسے جانچیں تو اسے سنتا دیکھتا کردیا'),
(5594, 76, 3, 'بیشک ہم نے اسے راہ بتائی یا حق مانتا یا ناشکری کرتا'),
(5595, 76, 4, 'بیشک ہم نے کافروں کے لیے تیار کر رکھی ہیں زنجیریں اور طوق اور بھڑکتی آگ'),
(5596, 76, 5, 'بیشک نیک پئیں گے اس جام میں سے جس کی ملونی کافور ہے وہ کافور کیا ایک چشمہ ہے'),
(5597, 76, 6, 'جس میں سے اللہ کے نہایت خاص بندے پئیں گے اپنے محلوں میں اسے جہاں چاہیں بہا کر لے جائیں گے'),
(5598, 76, 7, 'اپنی منتیں پوری کرتے ہیں اور اس دن سے ڈرتے ہیں جس کی برائی پھیلی ہوئی ہے'),
(5599, 76, 8, 'اور کھانا کھلاتے ہیں اس کی محبت پر مسکین اور یتیم اور اسیر کو،'),
(5600, 76, 9, 'ان سے کہتے ہیں ہم تمہیں خاص اللہ کے لیے کھانا دیتےہیں تم سے کوئی بدلہ یا شکر گزاری نہیں مانگتے،'),
(5601, 76, 10, 'بیشک ہمیں اپنے رب سے ایک ایسے دن کا ڈر ہے جو بہت ترش نہایت سخت ہے'),
(5602, 76, 11, 'تو انہیں اللہ نے اس دن کے شر سے بچالیا اور انہیں تازگی اور شادمانی دی،'),
(5603, 76, 12, 'اور ان کے صبر پر انہیں جنت اور ریشمی کپڑے صلہ میں دیے،'),
(5604, 76, 13, 'جنت میں تختوں پر تکیہ لگائے ہوں گے، نہ اس میں دھوپ دیکھیں گے نہ ٹھٹر (سخت سردی)'),
(5605, 76, 14, 'اور اس کے سائے ان پر جھکے ہوں گے اور اس کے گچھے جھکا کر نیچے کردیے گئے ہوں گے'),
(5606, 76, 15, 'اور ان پر چاندی کے برتنوں اور کوزوں کا دور ہوگا جو شیشے کے مثل ہورہے ہوں گے،'),
(5607, 76, 16, 'کیسے شیشے چاندی کے ساقیوں نے انہیں پورے اندازہ پر رکھا ہوگا'),
(5608, 76, 17, 'اور اس میں وہ جام پلائے جائیں گے جس کی ملونی ادرک ہوگی'),
(5609, 76, 18, 'وہ ادرک کیا ہے جنت میں ایک چشمہ ہے جسے سلسبیل کہتے ہیں'),
(5610, 76, 19, 'اور ان کے آس پاس خدمت میں پھریں گے ہمیشہ رہنے والے لڑکے جب تو انہیں دیکھے تو انہیں سمجھے کہ موتی ہیں بکھیرے ہوئے'),
(5611, 76, 20, 'اور جب تو ادھر نظر اٹھائے ایک چین دیکھے اور بڑی سلطنت'),
(5612, 76, 21, 'ان کے بدن پر ہیں کریب کے سبز کپڑے اور قنا ویز کے اور انہیں چاندی کے کنگن پہنائے گئے اور انہیں ان کے رب نے ستھری شراب پلائی'),
(5613, 76, 22, 'ان سے فرمایا جائے گا یہ تمہارا صلہ ہے اور تمہاری محنت ٹھکانے لگی'),
(5614, 76, 23, 'بیشک ہم نے تم پر قرآن بتدریج اتارا'),
(5615, 76, 24, 'تو اپنے رب کے حکم پر صابر رہو اور ان میں کسی گنہگار یا ناشکرے کی بات نہ سنو'),
(5616, 76, 25, 'اور اپنے رب کا نام صبح و شام یاد کرو'),
(5617, 76, 26, 'اور کچھ میں اسے سجدہ کرو اور بڑی رات تک اس کی پاکی بولو'),
(5618, 76, 27, 'بیشک یہ لوگ پاؤں تلے کی (دنیاوی فائدے کو) عزیز رکھتے ہیں اور اپنے پیچھے ایک بھاری دن کو چھوڑ بیٹھے ہیں'),
(5619, 76, 28, 'ہم نے انہیں پیدا کیا اور ان کے جوڑ بند مضبوط کیے اور ہم جب چاہیں ان جیسے اور بدل دیں'),
(5620, 76, 29, 'بیشک یہ نصیحت ہے تو جو چاہے اپنے رب کی طرف راہ لے'),
(5621, 76, 30, 'اور تم کیا چاہو مگر یہ کہ اللہ چاہے بیشک وہ علم و حکمت والا ہے،'),
(5622, 76, 31, 'اپنی رحمت میں لیتا ہے جسے چاہے اور ظالموں کے لیے اس نے دردناک عذاب تیار کر رکھا ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'قسم ان کی جو بھیجی جاتی ہیں لگاتار'),
(5624, 77, 2, 'پھر زور سے جھونکا دینے والیاں،'),
(5625, 77, 3, 'پھر ابھار کر اٹھانے والیاں'),
(5626, 77, 4, 'پھر حق ناحق کو خوب جدا کرنے والیاں،'),
(5627, 77, 5, 'پھر ان کی قسم جو ذکر کا لقا کرتی ہیں'),
(5628, 77, 6, 'حجت تمام کرنے یا ڈرانے کو،'),
(5629, 77, 7, 'بیشک جس بات کا تم وعدہ دیے جاتے ہو ضرور ہونی ہے'),
(5630, 77, 8, 'پھر جب تارے محو کردیے جائیں،'),
(5631, 77, 9, 'اور جب آسمان میں رخنے پڑیں،'),
(5632, 77, 10, 'اور جب پہاڑ غبار کرکے اڑا دیے جا ئیں،'),
(5633, 77, 11, 'اور جب رسولوں کا وقت آئے'),
(5634, 77, 12, 'کس دن کے لیے ٹھہرائے گئے تھے،'),
(5635, 77, 13, 'روز فیصلہ کے لیے،'),
(5636, 77, 14, 'اور تو کیا جانے وہ روز فیصلہ کیا ہے'),
(5637, 77, 15, 'جھٹلانے والوں کی اس دن خرابی'),
(5638, 77, 16, 'کیا ہم نے اگلوں کو ہلاک نہ فرمایا'),
(5639, 77, 17, 'پھر پچھلوں کو ان کے پیچھے پہنچائیں گے'),
(5640, 77, 18, 'مجرموں کے ساتھ ہم ایسا ہی کرتے ہیں،'),
(5641, 77, 19, 'اس دن جھٹلانے والوں کی خرابی،'),
(5642, 77, 20, 'کیا ہم نے تمہیں ایک بے قدر پانی سے پیدا نہ فرمایا'),
(5643, 77, 21, 'پھر اسے ایک محفوظ جگہ میں رکھا'),
(5644, 77, 22, 'ایک معلوم اندازہ تک'),
(5645, 77, 23, 'پھر ہم نے اندازہ فرمایا، تو ہم کیا ہی اچھے قادر'),
(5646, 77, 24, 'اس دن جھٹلانے والوں کی خرابی،'),
(5647, 77, 25, 'کیا ہم نے زمین کو جمع کرنے والی نہ کیا،'),
(5648, 77, 26, 'تمہارے زندوں اور مردوں کی'),
(5649, 77, 27, 'اور ہم نے اس میں اونچے اونچے لنگر ڈالے اور ہم نے تمہیں خوب میٹھا پانی پلایا'),
(5650, 77, 28, 'اس دن جھٹلانے والوں کی خرابی'),
(5651, 77, 29, 'چلو اس کی طرف جسے جھٹلاتے تھے،'),
(5652, 77, 30, 'چلو اس دھوئیں کے سائے کی طرف جس کی تین شاخیں'),
(5653, 77, 31, 'نہ سایہ دے نہ لپٹ سے بچائے'),
(5654, 77, 32, 'بیشک دوزخ چنگاریاں اڑاتی ہے'),
(5655, 77, 33, 'جیسے اونچے محل گویا وہ زرد رنگ کے اونٹ ہیں،'),
(5656, 77, 34, 'اس دن جھٹلانے والوں کی خرابی،'),
(5657, 77, 35, 'یہ دن ہے کہ وہ بول نہ سکیں گے'),
(5658, 77, 36, 'اور نہ انہیں اجازت ملے کہ عذر کریں'),
(5659, 77, 37, 'اس دن جھٹلانے والوں کی خرابی،'),
(5660, 77, 38, 'یہ ہے فیصلہ کا دن، ہم نے تمہیں جمع کیا اور سب اگلوں کو'),
(5661, 77, 39, 'اب اگر تمہارا کوئی داؤ ہو تو مجھ پر چل لو'),
(5662, 77, 40, 'اس دن جھٹلانے والوں کی خرابی،'),
(5663, 77, 41, 'بیشک ڈر والے سایوں اور چشموں میں ہیں،'),
(5664, 77, 42, 'اور میووں میں جو ان کا جی چاہے'),
(5665, 77, 43, 'کھاؤ اور پیو رچتا ہوا اپنے اعمال کا صلہ'),
(5666, 77, 44, 'بیشک نیکوں کو ہم ایسا ہی بدلہ دیتے ہیں،'),
(5667, 77, 45, 'اس دن جھٹلانے والوں کی خرابی'),
(5668, 77, 46, 'کچھ دن کھالو اور برت لو ضرور تم مجرم ہو'),
(5669, 77, 47, 'اس دن جھٹلانے والوں کی خرابی،'),
(5670, 77, 48, 'اور جب ان سے کہا جائے کہ نماز پڑھو تو نہیں پڑھتے،'),
(5671, 77, 49, 'اس دن جھٹلانے والوں کی خرابی،'),
(5672, 77, 50, 'پھر اس کے بعد کون سی بات پر ایمان لائیں گے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'یہ آپس میں کاہے کی پوچھ گچھ کررہے ہیں'),
(5674, 78, 2, 'بڑی خبر کی'),
(5675, 78, 3, 'جس میں وہ کئی راہ ہیں'),
(5676, 78, 4, 'ہاں ہاں اب جائیں گے،'),
(5677, 78, 5, 'پھر ہاں ہاں جان جائیں گے'),
(5678, 78, 6, 'کیا ہم نے زمین کو بچھونا نہ کیا'),
(5679, 78, 7, 'اور پہاڑوں کو میخیں'),
(5680, 78, 8, 'اور تمہیں جوڑے بنایا'),
(5681, 78, 9, 'اور تمہاری نیند کو آرام کیا'),
(5682, 78, 10, 'اور رات کو پردہ پوش کیا'),
(5683, 78, 11, 'اور دن کو روزگار کے لیے بنایا'),
(5684, 78, 12, 'اور تمہارے اوپر سات مضبوط چنائیاں چنیں (تعمیر کیں)'),
(5685, 78, 13, 'اور ان میں ایک نہایت چمکتا چراغ رکھا'),
(5686, 78, 14, 'اور پھر بدلیوں سے زور کا پانی اتارا،'),
(5687, 78, 15, 'کہ اس سے پیدا فرمائیں اناج اور سبزہ،'),
(5688, 78, 16, 'اور گھنے باغ'),
(5689, 78, 17, 'بیشک فیصلہ کا دن ٹھہرا ہوا وقت ہے،'),
(5690, 78, 18, 'جس دن صور پھونکا جائے گا تو تم چلے آؤ گے فوجوں کی فوجیں،'),
(5691, 78, 19, 'اور آسمان کھولا جائے گا کہ دروازے ہوجائے گا'),
(5692, 78, 20, 'اور پہاڑ چلائے جائیں گے کہ ہوجائیں گے جیسے چمکتا ریتا دور سے پانی کا دھوکا دیتا،'),
(5693, 78, 21, 'بیشک جہنم تاک میں ہے،'),
(5694, 78, 22, 'سرکشوں کا ٹھکانا،'),
(5695, 78, 23, 'اس میں قرنوں (مدتوں) رہیں گے'),
(5696, 78, 24, 'اس میں کسی طرح کی ٹھنڈک کا مزہ نہ پائیں گے اور نہ کچھ پینے کو،'),
(5697, 78, 25, 'مگر کھولتا پانی اور دوزخیوں کا جلتا پیپ،'),
(5698, 78, 26, 'جیسے کو تیسا بدلہ'),
(5699, 78, 27, 'بیشک انہیں حساب کا خوف نہ تھا'),
(5700, 78, 28, 'اور انہوں نے ہماری آیتیں حد بھر جھٹلائیں،'),
(5701, 78, 29, 'اور ہم نے ہر چیز لکھ کر شمار کر رکھی ہے'),
(5702, 78, 30, 'اب چکھو کہ ہم تمہیں نہ بڑھائیں گے مگر عذاب،'),
(5703, 78, 31, 'بیشک ڈر والوں کو کامیابی کی جگہ ہے'),
(5704, 78, 32, 'باغ ہیں اور انگور،'),
(5705, 78, 33, 'اور اٹھتے جوبن والیاں ایک عمر کی،'),
(5706, 78, 34, 'اور چھلکتا جام'),
(5707, 78, 35, 'جس میں نہ کوئی بیہودہ بات سنیں نہ جھٹلانا'),
(5708, 78, 36, 'صلہ تمہارے رب کی طرف سے نہایت کافی عطا،'),
(5709, 78, 37, 'وہ جو رب ہے آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے رحمن کہ اس سے بات کرنے کا اختیار نہ رکھیں گے'),
(5710, 78, 38, 'جس دن جبریل کھڑا ہوگا اور سب فرشتے پرا باندھے (صفیں بنائے) کوئی نہ بول سکے گا مگر جسے رحمن نے اذن دیا اور اس نے ٹھیک بات کہی'),
(5711, 78, 39, 'وہ سچا دن ہے اب جو چاہے اپنے رب کی طرف راہ بنالے'),
(5712, 78, 40, 'ہم تمہیں ایک عذاب سے ڈراتے ہیں کہ نزدیک آگیا جس دن آدمی دیکھے گا جو کچھ اس کے ہاتھوں نے آگے بھیجا اور کافر کہے گا ہائے میں کسی طرح خاک ہوجاتا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'قسم ان کی کہ سختی سے جان کھینچیں'),
(5714, 79, 2, 'اور نرمی سے بند کھولیں)'),
(5715, 79, 3, 'اور آسانی سے پیریں'),
(5716, 79, 4, 'پھر آگے بڑھ کر جلد پہنچیں'),
(5717, 79, 5, 'پھر کام کی تدبیر کریں'),
(5718, 79, 6, 'کہ کافروں پر ضرور عذاب ہوگا جس دن تھر تھرائے گی تھرتھرانے والی'),
(5719, 79, 7, 'اس کے پیچھے آئے گی پیچھے آنے والی'),
(5720, 79, 8, 'کتنے دل اس دن دھڑکتے ہوں گے،'),
(5721, 79, 9, 'آنکھ اوپر نہ اٹھا سکیں گے'),
(5722, 79, 10, 'کافر کہتے ہیں کیا ہم پھر الٹے پاؤں پلٹیں گے'),
(5723, 79, 11, 'کیا ہم جب گلی ہڈیاں ہوجائیں گے'),
(5724, 79, 12, 'بولے یوں تو یہ پلٹنا تو نرا نقصان ہے'),
(5725, 79, 13, 'تو وہ نہیں مگر ایک جھڑکی'),
(5726, 79, 14, 'جبھی وہ کھلے میدان میں آپڑے ہوں گے'),
(5727, 79, 15, 'کیا تمہیں موسیٰ کی خبر آئی'),
(5728, 79, 16, 'جب اسے اس کے رب نے پاک جنگل طویٰ میں ندا فرمائی،'),
(5729, 79, 17, 'کہ فرعون کے پاس جا اس نے سر اٹھایا'),
(5730, 79, 18, 'اس سے کہہ کیا تجھے رغبت اس طرف ہے کہ ستھرا ہو'),
(5731, 79, 19, 'اور تجھے تیرے رب کی طرف راہ بتاؤں کہ تو ڈرے'),
(5732, 79, 20, 'پھر موسیٰ نے اسے بہت بڑی نشانی دکھائی'),
(5733, 79, 21, 'اس پر اس نے جھٹلایا اور نافرمانی کی،'),
(5734, 79, 22, 'پھر پیٹھ دی اپنی کوشش میں لگا'),
(5735, 79, 23, 'تو لوگوں کو جمع کیا پھر پکارا،'),
(5736, 79, 24, 'پھر بولا میں تمہارا سب سے اونچا رب ہوں'),
(5737, 79, 25, 'تو اللہ نے اسے دنیا و آخرت دونوں کے عذاب میں پکڑا'),
(5738, 79, 26, 'بیشک اس میں سیکھ ملتا ہے اسے جو ڈرے'),
(5739, 79, 27, 'کیا تمہاری سمجھ کے مطابق تمہارا بنانا مشکل یا آسمان کا اللہ نے اسے بنایا،'),
(5740, 79, 28, 'اس کی چھت اونچی کی پھر اسے ٹھیک کیا'),
(5741, 79, 29, 'اس کی رات اندھیری کی اور اس کی روشنی چمکائی'),
(5742, 79, 30, 'اور اس کے بعد زمین پھیلائی'),
(5743, 79, 31, 'اس میں سے اس کا پانی اور چارہ نکا لا'),
(5744, 79, 32, 'اور پہاڑوں کو جمایا'),
(5745, 79, 33, 'تمہارے اور تمہارے چوپایوں کے فائدہ کو،'),
(5746, 79, 34, 'پھر جب آئے گی وہ عام مصیبت سب سے بڑی'),
(5747, 79, 35, 'اس دن آدمی یاد کرے گا جو کوشش کی تھی'),
(5748, 79, 36, 'اور جہنم ہر دیکھنے والے پر ظاہر کی جائے گی'),
(5749, 79, 37, 'تو وہ جس نے سرکشی کی'),
(5750, 79, 38, 'اور دنیا کی زندگی کو ترجیح دی'),
(5751, 79, 39, 'تو بیشک جہنم ہی اس کا ٹھکانا ہے،'),
(5752, 79, 40, 'اور وہ جو اپنے رب کے حضور کھڑے ہونے سے ڈرا اور نفس کو خواہش سے روکا'),
(5753, 79, 41, 'تو بیشک جنت ہی ٹھکانا ہے'),
(5754, 79, 42, 'تم سے قیامت کو پوچھتے ہیں کہ وہ کب کے لیے ٹھہری ہوئی ہے،'),
(5755, 79, 43, 'تمہیں اس کے بیان سے کیا تعلق'),
(5756, 79, 44, 'تمہارے رب ہی تک اس کی انتہا ہے،'),
(5757, 79, 45, 'تم تو فقط اسے ڈرا نے والے ہو جو اس سے ڈرے،'),
(5758, 79, 46, 'گویا جس دن وہ اسے دیکھیں گے دنیا میں نہ رہے تھے مگر ایک شام یا اس کے دن چڑھے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'تیوری چڑھائی اور منہ پھیرا'),
(5760, 80, 2, 'اس پر کہ اس کے پاس وہ نابینا حاضر ہوا'),
(5761, 80, 3, 'اور تمہیں کیا معلوم شاید وہ ستھرا ہو'),
(5762, 80, 4, 'یا نصیحت لے تو اسے نصیحت فائدہ دے،'),
(5763, 80, 5, 'وہ جو بے پرواہ بنتا ہے'),
(5764, 80, 6, 'تم اس کے تو پیچھے پڑتے ہو'),
(5765, 80, 7, 'اور تمہارا کچھ زیاں نہیں اس میں کہ وہ ستھرا نہ ہو'),
(5766, 80, 8, 'اور وہ جو تمہارے حضور ملکتا (ناز سے دوڑتا ہوا) آتا'),
(5767, 80, 9, 'اور وہ ڈر رہا ہے'),
(5768, 80, 10, 'تو اسے چھوڑ کر اور طرف مشغول ہوتے ہو،'),
(5769, 80, 11, 'یوں نہیں یہ تو سمجھانا ہے'),
(5770, 80, 12, 'تو جو چاہے اسے یا د کرے'),
(5771, 80, 13, 'ان صحیفوں میں کہ عزت والے ہیں'),
(5772, 80, 14, 'بلندی والے پاکی والے'),
(5773, 80, 15, 'ایسوں کے ہاتھ لکھے ہوئے،'),
(5774, 80, 16, 'جو کرم والے نکوئی والے'),
(5775, 80, 17, 'آدمی مارا جائیو کیا ناشکر ہے'),
(5776, 80, 18, 'اسے کاہے سے بنایا،'),
(5777, 80, 19, 'پانی کی بوند سے اسے پیدا فرمایا، پھر اسے طرح طرح کے اندازوں پر رکھا'),
(5778, 80, 20, 'پھر اسے راستہ آسان کیا'),
(5779, 80, 21, 'پھر اسے موت دی پھر قبر میں رکھوایا'),
(5780, 80, 22, 'پھر جب چاہا اسے باہر نکالا'),
(5781, 80, 23, 'کوئی نہیں، اس نے اب تک پورا نہ کیا جو اسے حکم ہوا تھا'),
(5782, 80, 24, 'تو آدمی کو چاہیے اپنے کھانوں کو دیکھے'),
(5783, 80, 25, 'کہ ہم نے اچھی طرح پانی ڈالا'),
(5784, 80, 26, 'پھر زمین کو خوب چیرا،'),
(5785, 80, 27, 'تو اس میں اُگایا اناج،'),
(5786, 80, 28, 'اور انگور اور چارہ،'),
(5787, 80, 29, 'اور زیتون اور کھجور،'),
(5788, 80, 30, 'اور گھنے باغیچے،'),
(5789, 80, 31, 'اور میوے اور دُوب (گھاس)'),
(5790, 80, 32, 'تمہارے فائدے کو اور تمہارے چوپایوں کے،'),
(5791, 80, 33, 'پھر جب آئے گی وہ کان پھاڑنے والی چنگھاڑ'),
(5792, 80, 34, 'اس دن آدمی بھاگے گا اپنے بھائی،'),
(5793, 80, 35, 'اور ماں اور باپ،'),
(5794, 80, 36, 'اور جُورو اور بیٹوں سے'),
(5795, 80, 37, 'ان میں سے ہر ایک کو اس دن ایک فکر ہے کہ وہی اسے بس ہے'),
(5796, 80, 38, 'کتنے منہ اس دن روشن ہوں گے'),
(5797, 80, 39, 'ہنستے خوشیاں مناتے'),
(5798, 80, 40, 'اور کتنے مونہوں پر اس دن گرد پڑی ہوگی،'),
(5799, 80, 41, 'ان پر سیاہی چڑھ رہی ہے'),
(5800, 80, 42, 'یہ وہی ہیں کافر بدکار،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'جب دھوپ لپیٹی جائے'),
(5802, 81, 2, 'اور جب تارے جھڑ پڑیں'),
(5803, 81, 3, 'اور جب پہاڑ چلائے جائیں'),
(5804, 81, 4, 'اور جب تھلکی (گابھن) اونٹنیاں چھوٹی پھریں'),
(5805, 81, 5, 'اور جب وحشی جانور جمع کیے جائیں'),
(5806, 81, 6, 'اور جب سمندر سلگائے جائیں'),
(5807, 81, 7, 'اور جب جانوں کے جوڑ بنیں'),
(5808, 81, 8, 'اور جب زندہ دبائی ہوئی سے پوچھا جائے'),
(5809, 81, 9, 'کس خطا پر ماری گئی'),
(5810, 81, 10, 'اور جب نامہٴ اعمال کھولے جائیں،'),
(5811, 81, 11, 'اور جب آسمان جگہ سے کھینچ لیا جائے'),
(5812, 81, 12, 'اور جب جہنم بھڑکایا جائے'),
(5813, 81, 13, 'اور جب جنت پاس لائی جائے'),
(5814, 81, 14, 'ہر جان کو معلوم ہوجائے گا جو حاضر لائی'),
(5815, 81, 15, 'تو قسم ہے ان کی جو الٹے پھریں،'),
(5816, 81, 16, 'سیدھے چلیں تھم رہیں'),
(5817, 81, 17, 'اور رات کی جب پیٹھ دے'),
(5818, 81, 18, 'اور صبح کی جب دم لے'),
(5819, 81, 19, 'بیشک یہ عزت والے رسول کا پڑھنا ہے،'),
(5820, 81, 20, 'جو قوت والا ہے مالک عرش کے حضور عزت والا وہاں اس کا حکم مانا جاتا ہے'),
(5821, 81, 21, 'امانت دار ہے'),
(5822, 81, 22, 'اور تمہارے صاحب مجنون نہیں'),
(5823, 81, 23, 'اور بیشک انہوں نے اسے روشن کنارہ پر دیکھا'),
(5824, 81, 24, 'اور یہ نبی غیب بتانے میں بخیل نہیں،'),
(5825, 81, 25, 'اور قرآن، مردود شیطان کا پڑھا ہوا نہیں،'),
(5826, 81, 26, 'پھر کدھر جاتے ہو'),
(5827, 81, 27, 'وہ تو نصیحت ہی ہے سارے جہان کے لیے،'),
(5828, 81, 28, 'اس کے لیے جو تم میں سیدھا ہونا چاہے'),
(5829, 81, 29, 'اور تم کیا چا ہو مگر یہ کہ چاہے اللہ سارے جہان کا رب،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'جب آسمان پھٹ پڑے،'),
(5831, 82, 2, 'اور جب تارے جھڑ پڑیں،'),
(5832, 82, 3, 'اور جب سمندر بہادیے جائیں'),
(5833, 82, 4, 'اور جب قبریں کریدی جائیں'),
(5834, 82, 5, 'ہر جان، جان لے گی جو اس نے آگے بھیجا اور جو پیچھے'),
(5835, 82, 6, 'اے آدمی! تجھے کس چیز نے فریب دیا اپنے کرم والے رب سے'),
(5836, 82, 7, 'جس نے تجھے پیدا کیا پھر ٹھیک بنایا پھر ہموار فرمایا'),
(5837, 82, 8, 'جس صورت میں چاہا تجھے ترکیب دیا'),
(5838, 82, 9, 'کوئی نہیں بلکہ تم انصاف ہونے کو جھٹلانتے ہو'),
(5839, 82, 10, 'اور بیشک تم پر کچھ نگہبان ہیں'),
(5840, 82, 11, 'معزز لکھنے والے'),
(5841, 82, 12, 'جانتے ہیں جو کچھ تم کرو'),
(5842, 82, 13, 'بیشک نِکو کار ضرور چین میں ہیں'),
(5843, 82, 14, 'اور بیشک بدکار ضرور دوزخ میں ہیں،'),
(5844, 82, 15, 'انصاف کے دن اس میں جائیں گے،'),
(5845, 82, 16, 'اور اس سے کہیں چھپ نہ سکیں گے،'),
(5846, 82, 17, 'اور تو کیا جانیں کیسا انصاف کا دن،'),
(5847, 82, 18, 'پھر تو کیا جانے کیسا انصاف کا دن،'),
(5848, 82, 19, 'جس دن کوئی جان کسی جان کا کچھ اختیار نہ رکھے گی اور سارا حکم اس دن اللہ کا ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'کم تولنے والوں کی خرابی ہے،'),
(5850, 83, 2, 'وہ کہ جب اوروں سے ناپ لیں پورا لیں،'),
(5851, 83, 3, 'اور جب انہیں ناپ تول کردی کم کردیں،'),
(5852, 83, 4, 'کیا ان لوگوں کو گمان نہیں کہ انہیں اٹھنا ہے،'),
(5853, 83, 5, 'ایک عظمت والے دن کے لیے'),
(5854, 83, 6, 'جس دن سب لوگ رب العالمین کے حضور کھڑے ہوں گے،'),
(5855, 83, 7, 'بیشک کافروں کی لکھت سب سے نیچی جگہ سجین میں ہے'),
(5856, 83, 8, 'اور تو کیا جانے سجین کیسی ہے'),
(5857, 83, 9, 'وہ لکھت ایک مہر کیا نوشتہ ہے'),
(5858, 83, 10, 'اس دن جھٹلانے والوں کی خرابی ہے،'),
(5859, 83, 11, 'جو انصاف کے دن کو جھٹلاتے ہیں'),
(5860, 83, 12, 'اور اسے نہ جھٹلائے گا مگر ہر سرکش'),
(5861, 83, 13, 'جب اس پر ہماری آیتیں پڑھی جائیں کہے اگلوں کی کہانیاں ہیں،'),
(5862, 83, 14, 'کوئی نہیں بلکہ ان کے دلوں پر زنگ چڑھادیا ہے ان کی کمائیوں نے'),
(5863, 83, 15, 'ہاں ہاں بیشک وہ اس دن اپنے رب کے دیدار سے محروم ہیں'),
(5864, 83, 16, 'پھر بیشک انہیں جہنم میں داخل ہونا،'),
(5865, 83, 17, 'پھر کہا جائے گا، یہ ہے وہ جسے تم جھٹلاتے تھے'),
(5866, 83, 18, 'ہاں ہاں بیشک نیکوں کی لکھت سب سے اونچا محل علیین میں ہے'),
(5867, 83, 19, 'اور تو کیا جانے علیین کیسی ہے'),
(5868, 83, 20, 'وہ لکھت ایک مہر کیا نوشتہ ہے'),
(5869, 83, 21, 'کہ مقرب جس کی زیارت کرتے ہیں،'),
(5870, 83, 22, 'بیشک نیکوکار ضرور چین میں ہیں،'),
(5871, 83, 23, 'تختوں پر دیکھتے ہیں'),
(5872, 83, 24, 'تو ان کے چہروں میں چین کی تازگی پہنچانے'),
(5873, 83, 25, 'نتھری شراب پلائے جائیں گے جو مہُر کی ہوئی رکھی ہے'),
(5874, 83, 26, 'اس کی مہُر مشک پر ہے، اور اسی پر چاہیے کہ للچائیں للچانے والے'),
(5875, 83, 27, 'اور اس کی ملونی تسنیم سے ہے'),
(5876, 83, 28, 'وہ چشمہ جس سے مقربانِ بارگاہ پیتے ہیں'),
(5877, 83, 29, 'بیشک مجرم لوگ ایمان والوں سے ہنسا کرتے تھے،'),
(5878, 83, 30, 'اور جب وہ ان پر گزرتے تو یہ آپس میں ان پر آنکھوں سے اشارے کرتے'),
(5879, 83, 31, 'اور جب (ف۹۳۳ اپنے گھر پلٹتے خوشیاں کرتے پلٹتے'),
(5880, 83, 32, 'اور جب مسلمانوں کو دیکھتے کہتے بیشک یہ لوگ بہکے ہوئے ہیں'),
(5881, 83, 33, 'اور یہ کچھ ان پر نگہبان بناکر نہ بھیجے گئے'),
(5882, 83, 34, 'تو آج ایمان والے کافروں سے ہنستے ہیں'),
(5883, 83, 35, 'تختوں پر بیٹھے دیکھتے ہیں'),
(5884, 83, 36, 'کیوں کچھ بدلا ملا کافروں کو اپنے کیے کا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'جب آسمان شق ہو'),
(5886, 84, 2, 'اور اپنے رب کا حکم سنے اور اسے سزاوار ہی یہ ہے،'),
(5887, 84, 3, 'اور جب زمین دراز کی جائے'),
(5888, 84, 4, 'اور جو کچھ اس میں ہے ڈال دے اور خالی ہوجائے،'),
(5889, 84, 5, 'اور اپنے رب کا حکم سنے اور اسے سزاوار ہی یہ ہے'),
(5890, 84, 6, 'اے آدمی! بیشک تجھے اپنے رب کی طرف ضرور دوڑنا ہے پھر اس سے ملنا'),
(5891, 84, 7, 'تو وہ وہ اپنا نامہٴ اعمال دہنے ہاتھ میں دیا جائے'),
(5892, 84, 8, 'اس سے عنقریب سہل حساب لیا جائے گا'),
(5893, 84, 9, 'اور اپنے گھر والوں کی طرف شاد شاد پلٹے گا'),
(5894, 84, 10, 'اور وہ جس کا نامہٴ اعمال اس کی پیٹھ کے پیچھے دیا جائے'),
(5895, 84, 11, 'وہ عنقریب موت مانگے گا'),
(5896, 84, 12, 'اور بھڑکتی ا ٓ گ میں جائے گا،'),
(5897, 84, 13, 'بیشک وہ اپنے گھر میں خوش تھا'),
(5898, 84, 14, 'وہ سمجھا کہ اسے پھرنا نہیں'),
(5899, 84, 15, 'ہاں کیوں نہیں بیشک اس کا رب اسے دیکھ رہا ہے،'),
(5900, 84, 16, 'تو مجھے قسم ہے شام کے اجالے کی'),
(5901, 84, 17, 'اور رات کی اور جو چیزیں اس میں جمع ہوتی ہیں'),
(5902, 84, 18, 'اور چاند کی جب پورا ہو'),
(5903, 84, 19, 'ضرور تم منزل بہ منزل چڑھو گے'),
(5904, 84, 20, 'تو کیا ہوا انہیں ایمان نہیں لاتے'),
(5905, 84, 21, 'اور جب قرآن پڑھا جائے سجدہ نہیں کرتے السجدة ۔۱۳'),
(5906, 84, 22, 'بلکہ کافر جھٹلا رہے ہیں'),
(5907, 84, 23, 'اور اللہ خوب جانتا ہے جو اپنے جی میں رکھتے ہیں'),
(5908, 84, 24, 'تو تم انہیں دردناک عذاب کی بشارت دو'),
(5909, 84, 25, 'مگر جو ایمان لائے اور اچھے کام کیے ان کے لیے وہ ثواب ہے جو کبھی ختم نہ ہوگا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'قسم آسمان کی، جس میں برج ہیں'),
(5911, 85, 2, 'اور اس دن کی جس کا وعدہ ہے'),
(5912, 85, 3, 'اور اس دن کی جو گواہ ہے اور اس دن کی جس میں حاضر ہوتے ہیں'),
(5913, 85, 4, 'کھائی والوں پر لعنت ہو'),
(5914, 85, 5, 'اس بھڑکتی آ گ والے،'),
(5915, 85, 6, 'جب وہ اس کے کناروں پر بیٹھے تھے'),
(5916, 85, 7, 'اور وه خد گواه ہیں جو کہ مسلما نوں کے ساتھ کر رہے تھے'),
(5917, 85, 8, 'اور انھیں مسلمانوں کا کیا برا لگا یہی نہ کے وہ ایمان لا ئے اللہ والے سب خو بیوں سرا ہے پر،'),
(5918, 85, 9, 'کے اس کے لئے آسمانوں اور زمین کی سلطنت ہے اور اللہ ہر چیز پر گواه ہے،'),
(5919, 85, 10, 'بے شک جنھو ں نے ایذا دی مسلمان مردوں اور مسلمان عورتوں کو پھر تو بہ نہ کی ان کے لئے جہنم کا عذاب ہے اور ان کے لئے آگ کا عذاب'),
(5920, 85, 11, 'بے شک جو ایما ن لائے اور اچھے کام کئے ان کے لئے باغ ہیں جن کے نیچے نہریں رواں یہی بڑی کامیابی ہے،'),
(5921, 85, 12, 'بے شک تیرے رب کی گرفت بہت سخت ہے'),
(5922, 85, 13, 'بے شک وه پہلے اور پھر کرے'),
(5923, 85, 14, 'اور وہی ہے بخشنے والا اپنے نیک بندوں پر پیارا،'),
(5924, 85, 15, 'عزت والے عرش کا مالک،'),
(5925, 85, 16, 'ہمیشہ جو چاہے کہ لینے والا،'),
(5926, 85, 17, 'کیا تمھارے پاس لشکروں کے بات آئی'),
(5927, 85, 18, 'وه لشکر کون فرعون اور ثمود'),
(5928, 85, 19, 'بلکہ کافر جھٹلا نے میں ہیں'),
(5929, 85, 20, 'اور اللہ ان کے پیچھے سے انھیں گھیرے ہوئے ہے'),
(5930, 85, 21, 'بلکہ وه کمال شرف والا قران ہے،'),
(5931, 85, 22, 'لو ح محفوظ میں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'آسمان کی قسم اور رات کے آنے والے کی، ف۲)'),
(5933, 86, 2, 'اور کچھ تم نے جا نا وه رات کو آنے والا کیا ہے،'),
(5934, 86, 3, 'خوب چمکتا تارا،'),
(5935, 86, 4, 'کوئی جان نہیں جس پر نگہبان نہ ہو'),
(5936, 86, 5, 'تو چاہئے کہ آدمی غور کرے کہ کس چیز سے بنا یا گیا'),
(5937, 86, 6, 'جَست کرتے (اوچھلتے ہوئے) پانی سے، ف۵)'),
(5938, 86, 7, 'جو نکلتا ہے پیٹھ اور سینوں کے بیچ سے'),
(5939, 86, 8, 'بے شک اللہ اس کے واپس کرینے پر قادر ہے'),
(5940, 86, 9, 'جس دن چھپی باتوں کی جانچ ہوگی'),
(5941, 86, 10, 'تو آدمی کے پاس نہ کچھ زور ہوگا نہ کوئی مددگار'),
(5942, 86, 11, 'آسمان کی قسم! جس سے مینھ اترتا ہے'),
(5943, 86, 12, 'اور زمین کی جو اس سے کھلتی ہے'),
(5944, 86, 13, 'بیشک قرآن ضرور فیصلہ کی بات ہے'),
(5945, 86, 14, 'اور کوئی ہنسی کی بات نہیں'),
(5946, 86, 15, 'بیشک کافر اپنا سا داؤ چلتے ہیں'),
(5947, 86, 16, 'اور میں اپنی خفیہ تدبیر فرماتا ہوں'),
(5948, 86, 17, 'تو تم کافروں کو ڈھیل دو انہیں کچھ تھوڑی مہلت دو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'اپنے رب کے نام کی پاکی بولو جب سے بلند ہے'),
(5950, 87, 2, 'جس نے بناکر ٹھیک کیا'),
(5951, 87, 3, 'اور جس نے اندازہ پر رکھ کر راہ دی'),
(5952, 87, 4, 'اور جس نے چارہ نکالا،'),
(5953, 87, 5, 'پھر اسے خشک سیاہ کردیا،'),
(5954, 87, 6, 'اب ہم تمہیں پڑھائیں گے کہ تم نہ بھولو گے'),
(5955, 87, 7, 'مگر جو اللہ چاہے بیشک وہ جانتا ہے ہر کھلے اور چھپے کو،'),
(5956, 87, 8, 'اور ہم تمہارے لیے آسانی کا سامان کردیں گے'),
(5957, 87, 9, 'تو تم نصیحت فرماؤ اگر نصیحت کام دے'),
(5958, 87, 10, 'عنقریب نصیحت مانے گا جو ڈرتا ہے'),
(5959, 87, 11, 'اور اس سے وہ بڑا بدبخت دور رہے گا،'),
(5960, 87, 12, 'جو سب سے بڑی آگ میں جائے گا'),
(5961, 87, 13, 'پھر نہ اس میں مرے اور نہ جیے'),
(5962, 87, 14, 'بیشک مراد کو پہنچا جو ستھرا ہوا'),
(5963, 87, 15, 'اور اپنے رب کا نام لے کر نماز پڑھی'),
(5964, 87, 16, 'بلکہ تم جیتی دنیا کو ترجیح دیتے ہو'),
(5965, 87, 17, 'اور آخرت بہتر اور باقی رہنے والی،'),
(5966, 87, 18, 'بیشک یہ اگلے صحیفوں میں ہے'),
(5967, 87, 19, 'ابراہیم اور موسیٰ کے صحیفوں میں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'بیشک تمہارے پاس اس مصیبت کی خبر آئی جو چھا جائے گی'),
(5969, 88, 2, 'کتنے ہی منہ اس دن ذلیل ہوں گے،'),
(5970, 88, 3, 'کام کریں مشقت جھیلیں،'),
(5971, 88, 4, 'جائیں بھڑکتی آگ میں'),
(5972, 88, 5, 'نہایت جلتے چشمہ کا پانی پلائے جائیں،'),
(5973, 88, 6, 'ان کے لیے کچھ کھانا نہیں مگر آگ کے کانٹے'),
(5974, 88, 7, 'کہ نہ فربہی لائیں اور نہ بھوک میں کام دیں'),
(5975, 88, 8, 'کتنے ہی منہ اس دن چین میں ہیں'),
(5976, 88, 9, 'اپنی کوشش پر راضی'),
(5977, 88, 10, 'بلند باغ میں،'),
(5978, 88, 11, 'کہ اس میں کوئی بیہودہ بات نہ سنیں گے،'),
(5979, 88, 12, 'اس میں رواں چشمہ ہے،'),
(5980, 88, 13, 'اس میں بلند تخت ہیں،'),
(5981, 88, 14, 'اور چنے ہوئے کوزے'),
(5982, 88, 15, 'اور برابر برابر بچھے ہوئے قالین'),
(5983, 88, 16, 'اور پھیلی ہوئی چاندنیاں'),
(5984, 88, 17, 'تو کیا اونٹ کو نہیں دیکھتے کیسا بنایا گیا،'),
(5985, 88, 18, 'اور آسمان کو کیسا اونچا کیا گیا'),
(5986, 88, 19, 'اور پہاڑوں کو، کیسے قائم کیے گئے،'),
(5987, 88, 20, 'اور زمین کو، کیسے بچھائی گئی،'),
(5988, 88, 21, 'تو تم نصیحت سناؤ تم تو یہی نصیحت سنانے والے ہو،'),
(5989, 88, 22, 'تم کچھ ان پر کڑ وڑا (ضامن) نہیں'),
(5990, 88, 23, 'ہاں جو منہ پھیرے اور کفر کرے'),
(5991, 88, 24, 'تو اسے اللہ بڑا عذاب دے گا'),
(5992, 88, 25, 'بیشک ہماری ہی طرف ان کا پھرنا'),
(5993, 88, 26, 'پھر بیشک ہماری ہی طرف ان کا حساب ہے،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'اس صبح کی قسم'),
(5995, 89, 2, 'اور دس راتوں کی'),
(5996, 89, 3, 'اور جفت اور طاق کی'),
(5997, 89, 4, 'اور رات کی جب چل دے'),
(5998, 89, 5, 'کیوں اس میں عقلمند کے لیے قسم ہوئی'),
(5999, 89, 6, 'کیا تم نے نہ دیکھا تمہارے رب نے عاد کے ساتھ کیسا کیا،'),
(6000, 89, 7, 'وہ اِرم حد سے زیادہ طول والے'),
(6001, 89, 8, 'کہ اس جیسا شہروں میں پیدا نہ ہوا'),
(6002, 89, 9, 'اور ثمود جنہوں نے وادی میں پتھر کی چٹانیں کاٹیں'),
(6003, 89, 10, 'اور فرعون کہ چومیخا کرتا (سخت سزائیں دیتا)'),
(6004, 89, 11, 'جنہوں نے شہروں میں سرکشی کی'),
(6005, 89, 12, 'پھر ان میں بہت فساد پھیلایا'),
(6006, 89, 13, 'تو ان پر تمہارے رب نے عذاب کا کوڑا بقوت مارا،'),
(6007, 89, 14, 'بیشک تمہارے رب کی نظر سے کچھ غائب نہیں،'),
(6008, 89, 15, 'لیکن آدمی تو جب اسے اس کا رب آزمائے کہ اس کو جاہ اور نعمت دے، جب تو کہتا ہے میرے رب نے مجھے عزت دی،'),
(6009, 89, 16, 'اور اگر آزمائے اور اس کا رزق اس پر تنگ کرے، تو کہتا ہے میرے رب نے مجھے خوار کیا،'),
(6010, 89, 17, 'یوں نہیں بلکہ تم یتیم کی عزت نہیں کرتے'),
(6011, 89, 18, 'اور آپس میں ایک دوسرے کو مسکین کے کھلانے کی رغبت نہیں دیتے،'),
(6012, 89, 19, 'اور میراث کا مال ہپ ہپ کھاتے ہو'),
(6013, 89, 20, 'اور مال کی نہایت محبت رکھتے ہو'),
(6014, 89, 21, 'ہاں ہاں جب زمین ٹکرا کر پاش پاش کردی جائے'),
(6015, 89, 22, 'اور تمہارے رب کا حکم آئے اور فرشتے قطار قطار،'),
(6016, 89, 23, 'اور اس دن جہنم لائے جائے اس دن آدمی سوچے گا اور اب اسے سوچنے کا وقت کہاں'),
(6017, 89, 24, 'کہے گا ہائے کسی طرح میں نے جیتے جی نیکی آگے بھیجی ہوتی،'),
(6018, 89, 25, 'تو اس دن اس کا سا عذاب کوئی نہیں کرتا،'),
(6019, 89, 26, 'اور اس کا سا باندھنا کوئی نہیں باندھتا،'),
(6020, 89, 27, 'اے اطمینان والی جان'),
(6021, 89, 28, 'اپنے رب کی طرف واپس ہو یوں کہ تو اس سے راضی وہ تجھ سے راضی،'),
(6022, 89, 29, 'پھر میرے خاص بندوں میں داخل ہو،'),
(6023, 89, 30, 'اور میری جنت میں آ،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'مجھے اس شہر کی قسم'),
(6025, 90, 2, 'کہ اے محبوب! تم اس شہر میں تشریف فرما ہو'),
(6026, 90, 3, 'اور تمہارے باپ ابراہیم کی قسم اور اس کی اولاد کی کہ تم ہو'),
(6027, 90, 4, 'بیشک ہم نے آدمی کو مشقت میں رہتا پیدا کیا'),
(6028, 90, 5, 'کیا آدمی یہ سمجھتا ہے کہ ہرگز اس پر کوئی قدرت نہیں پائے گا'),
(6029, 90, 6, 'کہتا ہے میں نے ڈھیروں مال فنا کردیا'),
(6030, 90, 7, 'کیا آدمی یہ سمجھتا ہے کہ اسے کسی نے نہ دیکھا'),
(6031, 90, 8, 'کیا ہم نے اس کی دو آنکھیں نہ بنائیں'),
(6032, 90, 9, 'اور زبان اور دو ہونٹ'),
(6033, 90, 10, 'اور اسے دو ابھری چیزوں کی راہ بتائی'),
(6034, 90, 11, 'پھر بے تامل گھاٹی میں نہ کودا'),
(6035, 90, 12, 'اور تو نے کیا جانا وہ گھاٹی کیا ہے'),
(6036, 90, 13, 'کسی بندے کی گردن چھڑانا'),
(6037, 90, 14, 'یا بھوک کے دن کھانا دینا'),
(6038, 90, 15, 'رشتہ دار یتیم کو،'),
(6039, 90, 16, 'یا خاک نشین مسکین کو'),
(6040, 90, 17, 'پھر ہو ان سے جو ایمان لائے اور انہوں نے آپس میں صبر کی وصیتیں کیں اور آپس میں مہربانی کی وصیتیں کیں'),
(6041, 90, 18, 'یہ دہنی طرف والے ہیں'),
(6042, 90, 19, 'اور جنہوں نے ہماری آیتوں سے کفر کیا وہ بائیں طرف والے'),
(6043, 90, 20, 'ان پر آگ ہے کہ اس میں ڈال کر اوپر سے بند کردی گئی');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سورج اور اس کی روشنی کی قسم،'),
(6045, 91, 2, 'اور چاند کی جب اس کے پیچھے آئے'),
(6046, 91, 3, 'اور دن کی جب اسے چمکائے'),
(6047, 91, 4, 'اور رات کی جب اسے چھپائے'),
(6048, 91, 5, 'اور آسمان اور اس کے بنانے والے کی قسم،'),
(6049, 91, 6, 'اور زمین اور اس کے پھیلانے والے کی قسم،'),
(6050, 91, 7, 'اور جان کی اور اس کی جس نے اسے ٹھیک بنایا'),
(6051, 91, 8, 'پھر اس کی بدکاری اور اس کی پرہیزگاری دل میں ڈالی'),
(6052, 91, 9, 'بیشک مراد کو پہنچایا جس نے اسے ستھرا کیا'),
(6053, 91, 10, 'اور نامراد ہوا جس نے اسے معصیت میں چھپایا،'),
(6054, 91, 11, 'ثمود نے اپنی سرکشی سے جھٹلایا'),
(6055, 91, 12, 'جبکہ اس کا سب سے بدبخت اٹھ کھڑا ہوا،'),
(6056, 91, 13, 'تو ان سے اللہ کے رسول نے فرمایا اللہ کے ناقہ اور اس کی پینے کی باری سے بچو'),
(6057, 91, 14, 'تو انہوں نے اسے جھٹلایا پھر ناقہ کی کوچیں کاٹ دیں تو ان پر ان کے رب نے ان کے گناہ کے سبب تباہی ڈال کر وہ بستی برابر کردی'),
(6058, 91, 15, 'اور اس کے پیچھا کرنے والے کا اسے خوف نہیں');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'اور رات کی قسم جب چھائے'),
(6060, 92, 2, 'اور دن کی جب چمکے'),
(6061, 92, 3, 'اور اس کی جس نے نر و مادہ بنائے'),
(6062, 92, 4, 'بیشک تمہاری کوشش مختلف ہے'),
(6063, 92, 5, 'تو وہ جس نے دیا اور پرہیزگاری کی'),
(6064, 92, 6, 'اور سب سے اچھی کو سچ مانا'),
(6065, 92, 7, 'تو بہت جلد ہم اسے آسانی مہیا کردیں گے'),
(6066, 92, 8, 'اور وہ جس نے بخل کیا اور بے پرواہ بنا'),
(6067, 92, 9, 'اور سب سے اچھی کو جھٹلایا'),
(6068, 92, 10, 'تو بہت جلد ہم اسے دشواری مہیا کردیں گے'),
(6069, 92, 11, 'اور اس کا مال اسے کام نہ آئے گا جب ہلاکت میں پڑے گا'),
(6070, 92, 12, 'بیشک ہدایت فرمانا ہمارے ذمہ ہے،'),
(6071, 92, 13, 'اور بیشک آخرت اور دنیا دونوں کے ہمیں مالک ہیں،'),
(6072, 92, 14, 'تو میں تمہیں ڈراتا ہوں اس آگ سے جو بھڑک رہی ہے،'),
(6073, 92, 15, 'نہ جائے گا اس میں مگر بڑا بدبخت،'),
(6074, 92, 16, 'جس نے جھٹلایا اور منہ پھیرا'),
(6075, 92, 17, 'اور بہت اس سے دور رکھا جائے گا جو سب سے زیادہ پرہیزگار،'),
(6076, 92, 18, 'جو اپنا مال دیتا ہے کہ ستھرا ہو'),
(6077, 92, 19, 'اور کسی کا اس پر کچھ احسان نہیں جس کا بدلہ دیا جائے'),
(6078, 92, 20, 'صرف اپنے رب کی رضا چاہتا ہے جو سب سے بلند ہے،'),
(6079, 92, 21, 'اور بیشک قریب ہے کہ وہ راضی ہوگا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'چاشت کی قسم'),
(6081, 93, 2, 'اور رات کی جب پردہ ڈالے'),
(6082, 93, 3, 'کہ تمہیں تمہارے رب نے نہ چھوڑا، اور نہ مکروہ جانا،'),
(6083, 93, 4, 'اور بیشک پچھلی تمہارے لیے پہلی سے بہتر ہے'),
(6084, 93, 5, 'اور بیشک قریب ہے کہ تمہارا رب تمہیں اتنا دے گا کہ تم راضی ہوجاؤ گے'),
(6085, 93, 6, 'کیا اس نے تمہیں یتیم نہ پایا پھر جگہ دی'),
(6086, 93, 7, 'اور تمہیں اپنی محبت میں خود رفتہ پایا تو اپنی طرف راہ دی'),
(6087, 93, 8, 'اور تمہیں حاجت مند پایا پھر غنی کردیا'),
(6088, 93, 9, 'تو یتیم پر دباؤ نہ ڈالو'),
(6089, 93, 10, 'اور منگتا کو نہ جھڑکو'),
(6090, 93, 11, 'اور اپنے رب کی نعمت کا خوب چرچا کرو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'کیا ہم نے تمہارا سینہ کشادہ نہ کیا'),
(6092, 94, 2, 'اور تم پر سے تمہارا بوجھ اتار لیا،'),
(6093, 94, 3, 'جس نے تمہاری پیٹھ توڑی تھی'),
(6094, 94, 4, 'اور ہم نے تمہارے لیے تمہارا ذکر بلند کردیا'),
(6095, 94, 5, 'تو بیشک دشواری کے ساتھ آسانی ہے،'),
(6096, 94, 6, 'بیشک دشواری کے ساتھ آسانی ہے'),
(6097, 94, 7, 'تو جب تم نماز سے فارغ ہو تو دعا میں محنت کرو'),
(6098, 94, 8, 'اور اپنے رب ہی کی طرف رغبت کرو');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'انجیر کی قسم اور زیتون'),
(6100, 95, 2, 'اور طور سینا'),
(6101, 95, 3, 'اور اس امان والے شہر کی'),
(6102, 95, 4, 'بیشک ہم نے آدمی کو اچھی صورت پر بنایا،'),
(6103, 95, 5, 'پھر اسے ہر نیچی سے نیچی حالت کی طرف پھیردیا'),
(6104, 95, 6, 'مگر جو ایمان لائے اور اچھے کام کیے کہ انہیں بے حد ثواب ہے'),
(6105, 95, 7, 'تو اب کیا چیز تجھے انصاف کے جھٹلانے پر باعث ہے'),
(6106, 95, 8, 'کیا اللہ سب حاکموں سے بڑھ کر حاکم نہیں،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'پڑھو اپنے رب کے نام سے جس نے پیدا کیا'),
(6108, 96, 2, 'آدمی کو خون کی پھٹک سے بنایا، پڑھو'),
(6109, 96, 3, 'اور تمہارا رب ہی سب سے بڑا کریم،'),
(6110, 96, 4, 'جس نے قلم سے لکھنا سکھایا'),
(6111, 96, 5, 'آدمی کو سکھایا جو نہ جانتا تھا'),
(6112, 96, 6, 'ہاں ہاں بیشک آدمی سرکشی کرتا ہے،'),
(6113, 96, 7, 'اس پر کہ اپنے آپ کو غنی سمجھ لیا'),
(6114, 96, 8, 'بیشک تمہارے رب ہی کی طرف پھرنا ہے'),
(6115, 96, 9, 'بھلا دیکھو تو جو منع کرتا ہے،'),
(6116, 96, 10, 'بندے کو جب وہ نماز پڑھے'),
(6117, 96, 11, 'بھلا دیکھو تو اگر وہ ہدایت پر ہوتا،'),
(6118, 96, 12, 'یا پرہیزگاری بتاتا تو کیا خوب تھا،'),
(6119, 96, 13, 'بھلا دیکھو تو اگر جھٹلایا اور منہ پھیرا'),
(6120, 96, 14, 'تو کیا حال ہوگا کیا نہ جانا کہ اللہ دیکھ رہا ہے'),
(6121, 96, 15, 'ہاں ہاں اگر باز نہ آیا تو ضرور ہم پیشانی کے بال پکڑ کر کھینچیں گے'),
(6122, 96, 16, 'کیسی پیشانی جھوٹی خطاکار،'),
(6123, 96, 17, 'اب پکارے اپنی مجلس کو'),
(6124, 96, 18, 'ابھی ہم سپاہیوں کو بلاتے ہیں'),
(6125, 96, 19, 'ہاں ہاں، اس کی نہ سنو اور سجدہ کرو اور ہم سے قریب ہوجاؤ، (السجدة ۔۱۴)');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بیشک ہم نے اسے شبِ قدر میں اتارا'),
(6127, 97, 2, 'اور تم نے کیا جانا کیا شبِ قدر،'),
(6128, 97, 3, 'شبِ قدر ہزار مہینوں سے بہتر'),
(6129, 97, 4, 'اس میں فرشتے اور جبریل اترتے ہیں اپنے رب کے حکم سے ہر کام کے لیے'),
(6130, 97, 5, 'وہ سلامتی ہے، صبح چمکتے تک');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کتابی کافر اور مشرک اپنا دین چھوڑنے کو نہ تھے جب تک ان کے پاس روشن دلیل نہ آئے'),
(6132, 98, 2, 'وہ کون وہ اللہ کا رسول کہ پاک صحیفے پڑھتا ہے'),
(6133, 98, 3, 'ان میں سیدھی باتیں لکھی ہیں'),
(6134, 98, 4, 'اور پھوٹ نہ پڑی کتاب والوں میں مگر بعد اس کے کہ وہ روشن دلیل ان کے پاس تشریف لائے'),
(6135, 98, 5, 'اور ان لوگوں کو تو یہی حکم ہوا کہ اللہ کی بندگی کریں نرے اسی پر عقیدہ لاتے ایک طرف کے ہوکر اور نماز قائم کریں اور زکوٰة دیں اور یہ سیدھا دین ہے،'),
(6136, 98, 6, 'بیشک جتنے کافر ہیں کتابی اور مشرک سب جہنم کی آگ میں ہیں ہمیشہ اس میں رہیں گے، وہی تمام مخلوق میں بدتر ہیں،'),
(6137, 98, 7, 'بیشک جو ایمان لائے اور اچھے کام کیے وہی تمام مخلوق ہیں بہتر ہیں،'),
(6138, 98, 8, 'ان کا صلہ ان کے رب کے پاس بسنے کے باغ ہیں جن کے نیچے نہریں بہیں ان میں ہمیشہ ہمیشہ رہیں، اللہ ان سے راضی اور وہ اس سے راضی یہ اس کے لیے ہے جو اپنے رب سے ڈرے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'جب زمین تھرتھرا دی جائے جیسا اس کا تھرتھرانا ٹھہرا ہے'),
(6140, 99, 2, 'اور زمین اپنے بوجھ باہر پھینک دے'),
(6141, 99, 3, 'اور آدمی کہے اسے کیا ہوا'),
(6142, 99, 4, 'اس دن وہ اپنی خبریں بتائے گی'),
(6143, 99, 5, 'اس لیے کہ تمہارے رب نے اسے حکم بھیجا'),
(6144, 99, 6, 'اس دن لوگ اپنے رب کی طرف پھریں گے کئی راہ ہوکر تاکہ اپنا کیا دکھائیں جائیں تو،'),
(6145, 99, 7, 'جو ایک ذرہ بھر بھلائی کرے اسے دیکھے گا،'),
(6146, 99, 8, 'اور جو ایک ذرہ بھر برائی کرے اسے دیکھے گا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'قسم ان کی جو دوڑتے ہیں سینے سے آواز نکلتی ہوئی'),
(6148, 100, 2, 'پھر پتھروں سے آگ نکالتے ہیں سم مارکر'),
(6149, 100, 3, 'پھر صبح ہوتے تاراج کرتے ہیں'),
(6150, 100, 4, 'پھر اس وقت غبار اڑاتے ہیں،'),
(6151, 100, 5, 'پھر دشمن کے بیچ لشکر میں جاتے ہیں،'),
(6152, 100, 6, 'بیشک آدمی اپنے رب کا بڑا ناشکرا ہے'),
(6153, 100, 7, 'اور بیشک وہ اس پر خود گواہ ہے،'),
(6154, 100, 8, 'اور بیشک وہ مال کی چاہت میں ضرور کرّا (تیز) ہے'),
(6155, 100, 9, 'تو کیا نہیں جانتا جب اٹھائے جائیں گے جو قبروں میں ہیں،'),
(6156, 100, 10, 'اور کھول دی جائے گی جو سینوں میں ہے،'),
(6157, 100, 11, 'بیشک ان کے رب کو اس دن ان کی سب خبر ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'دل دہلانے والی،'),
(6159, 101, 2, 'کیا وہ دہلانے والی،'),
(6160, 101, 3, 'اور تو نے کیا جانا کیا ہے دہلانے والی'),
(6161, 101, 4, 'جس دن آدمی ہوں گے جیسے پھیلے پتنگے'),
(6162, 101, 5, 'اور پہاڑ ہوں گے جیسے دھنکی اون'),
(6163, 101, 6, 'تو جس کی تولیں بھاری ہوئیں'),
(6164, 101, 7, 'وہ تو من مانتے عیش میں ہیں'),
(6165, 101, 8, 'اور جس کی تولیں ہلکی پڑیں'),
(6166, 101, 9, 'وہ نیچا دکھانے والی گود میں ہے'),
(6167, 101, 10, 'اور تو نے کیا جانا، کیا نیچا دکھانے والی،'),
(6168, 101, 11, 'ایک آگ شعلے مارتی');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تمہیں غافل رکھا مال کی زیادہ طلبی نے'),
(6170, 102, 2, 'یہاں تک کہ تم نے قبروں کا منہ دیکھا'),
(6171, 102, 3, 'ہاں ہاں جلد جان جاؤ گے'),
(6172, 102, 4, 'پھر ہاں ہاں جلد جان جاؤ گے'),
(6173, 102, 5, 'ہاں ہاں اگر یقین کا جاننا جانتے تو مال کی محبت نہ رکھتے'),
(6174, 102, 6, 'بیشک ضرور جنہم کو دیکھو گے'),
(6175, 102, 7, 'پھر بیشک ضرور اسے یقینی دیکھنا دیکھو گے،'),
(6176, 102, 8, 'پھر بیشک ضرور اس دن تم سے نعمتوں کی پرسش ہوگی');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'اس زمانہ محبوب کی قسم'),
(6178, 103, 2, 'بیشک آدمی ضرور نقصان میں ہے'),
(6179, 103, 3, 'مگر جو ایمان لائے اور اچھے کام کیے اور ایک دورے کو حق کی تاکید کی اور ایک دوسرے کو صبر کی وصیت کی');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'خرابی ہے اس کے لیے جو لوگوں کے منہ پر عیب کرے پیٹھ پیچھے بدی کرے'),
(6181, 104, 2, 'جس نے مال جوڑا اور گن گن کر رکھا،'),
(6182, 104, 3, 'کیا یہ سمجھتا ہے کہ اس کا مال اسے دنیا میں ہمیشہ رکھے گا'),
(6183, 104, 4, 'ہر گز نہیں ضرور وہ روندنے والی میں پھینکا جائے گا'),
(6184, 104, 5, 'اور تو نے کیا جانا کیا روندنے والی،'),
(6185, 104, 6, 'اللہ کی آگ کہ بھڑک رہی ہے'),
(6186, 104, 7, 'وہ جو دلوں پر چڑھ جائے گی'),
(6187, 104, 8, 'بیشک وہ ان پر بند کردی جائے گی'),
(6188, 104, 9, 'لمبے لمبے ستونوں میں');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'اے محبوب! کیا تم نے نہ دیکھا تمہارے رب نے ان ہاتھی والوں کیا حال کیا'),
(6190, 105, 2, 'کیا ان کا داؤ تباہی میں نہ ڈالا،'),
(6191, 105, 3, 'اور ان پر پرندوں کی ٹکڑیاں (فوجیں) بھیجیں'),
(6192, 105, 4, 'کہ انہیں کنکر کے پتھروں سے مارتے'),
(6193, 105, 5, 'تو انہیں کر ڈالا جیسے کھائی کھیتی کی پتی (بھوسہ)');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'اس لیے کہ قریش کو میل دلایا'),
(6195, 106, 2, 'ان کے جاڑے اور گرمی دونوں کے کوچ میں میل دلایا (رغبت دلائی)'),
(6196, 106, 3, 'تو انہیں چاہیے اس گھر کے رب کی بندگی کریں،'),
(6197, 106, 4, 'جس نے انہیں بھوک میں کھانا دیا، اور انہیں ایک بڑے خوف سے امان بخشا');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'بھلا دیکھو تو جو دین کو جھٹلاتا ہے'),
(6199, 107, 2, 'پھر وہ وہ ہے جو یتیم کو دھکے دیتا ہے'),
(6200, 107, 3, 'اور مسکین کو کھانا دینے کی رغبت نہیں دیتا'),
(6201, 107, 4, 'تو ان نمازیوں کی خرابی ہے،'),
(6202, 107, 5, 'جو اپنی نماز سے بھولے بیٹھے ہیں'),
(6203, 107, 6, 'وہ جو دکھاوا کرتے ہیں'),
(6204, 107, 7, 'اور بر تنے کی چیز مانگے نہیں دیتے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'اے محبوب! بیشک ہم نے تمہیں بے شمار خوبیاں عطا فرمائیں'),
(6206, 108, 2, 'تو تم اپنے رب کے لیے نماز پڑھو اور قربانی کرو'),
(6207, 108, 3, 'بیشک جو تمہارا دشمن ہے وہی ہر خیر سے محروم ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'تم فرماؤ اے کافرو،'),
(6209, 109, 2, 'نہ میں پوجتا ہوں جو تم پوجتے ہو،'),
(6210, 109, 3, 'اور نہ تم پوجتے ہو جو میں پوجتا ہوں،'),
(6211, 109, 4, 'اور نہ میں پوجوں گا جو تم نے پوجا،'),
(6212, 109, 5, 'اور نہ تم پوجو گے جو میں پوجتا ہوں،'),
(6213, 109, 6, 'تمہیں تمہارا دین اور مجھے میرا دین');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'جب اللہ کی مدد اور فتح آئے'),
(6215, 110, 2, 'اور لوگوں کو تم دیکھو کہ اللہ کے دین میں فوج فوج داخل ہوتے ہیں'),
(6216, 110, 3, 'تو اپنے رب کی ثناء کرتے ہوئے اس کی پاکی بولو اور اس سے بخشش چاہو بیشک وہ بہت توبہ قبول کرنے والا ہے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'تباہ ہوجائیں ابولہب کے دونوں ہاتھ اور وہ تباہ ہوہی گیا'),
(6218, 111, 2, 'اسے کچھ کام نہ آیا اس کا مال اور نہ جو کمایا'),
(6219, 111, 3, 'اب دھنستا ہے لپٹ مارتی آگ میں وہ،'),
(6220, 111, 4, 'اور اس کی جُورو لکڑیوں کا گٹھا سر پر اٹھاتی،'),
(6221, 111, 5, 'اس کے گلے میں کھجور کی چھال کا رسّا،');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'تم فرماؤ وہ اللہ ہے وہ ایک ہے'),
(6223, 112, 2, 'اللہ بے نیاز ہے'),
(6224, 112, 3, 'نہ اس کی کوئی اولاد اور نہ وہ کسی سے پیدا ہوا'),
(6225, 112, 4, 'اور نہ اس کے جوڑ کا کوئی');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'تم فرماؤ میں اس کی پناہ لیتا ہوں جو صبح کا پیدا کرنے والا ہے'),
(6227, 113, 2, 'اس کی سب مخلوق کی شر سے'),
(6228, 113, 3, 'اور اندھیری ڈالنے والے کے شر سے جب وہ ڈوبے'),
(6229, 113, 4, 'اور ان عورتوں کے شر سے جو گرہوں میں پھونکتی ہیں'),
(6230, 113, 5, 'اور حسد والے کے شر سے جب وہ مجھ سے جلے');
INSERT INTO `ur_kanzuliman` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'تم کہو میں اس کی پناہ میں آیا جو سب لوگوں کا رب'),
(6232, 114, 2, 'سب لوگوں کا بادشاہ'),
(6233, 114, 3, 'سب لوگوں کا خدا'),
(6234, 114, 4, 'اس کے شر سے جو دل میں برے خطرے ڈالے اور دبک رہے'),
(6235, 114, 5, 'وہ جو لوگوں کے دلوں میں وسوسے ڈالتے ہیں،'),
(6236, 114, 6, 'جن اور آدمی');
