INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, '(شروع کرتا ہوں) اللہ کے نام سے جو بڑا مہربان نہایت رحم والا ہے۔'),
(2, 1, 2, 'ہر قسم کی تعریف اس اللہ کے لیے جو سب جہانوں کا پروردگار ہے۔'),
(3, 1, 3, 'جو (سب پر) بڑا مہربان (اور خاص بندوں پر) نہایت رحم کرنے والا ہے۔'),
(4, 1, 4, 'جزا و سزا کے دن کا مالک (و مختار) ہے۔'),
(5, 1, 5, '(اے اللہ!) ہم تیری ہی عبادت کرتے ہیں اور تجھ ہی سے مدد مانگتے ہیں۔'),
(6, 1, 6, 'ہمیں سیدھے راستے کی (اور اس پر چلنے کی) ہدایت کرتا رہ۔'),
(7, 1, 7, 'راستہ ان لوگوں کا جن پر تو نے انعام و احسان کیا نہ ان کا (راستہ) جن پر تیرا قہر و غضب نازل ہوا۔ اور نہ ان کا جو گمراہ ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف۔ لام۔ میم۔'),
(9, 2, 2, 'یہ (قرآن) وہ کتاب ہے جس (کے کلام اللہ ہونے) میں کوئی شک و شبہ نہیں ہے۔ (یہ) ہدایت ہے ان پرہیزگاروں کے لیے۔'),
(10, 2, 3, 'جو غیب پر ایمان رکھتے ہیں اور پورے اہتمام سے نماز ادا کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے کچھ (میری راہ میں) خرچ کرتے ہیں۔'),
(11, 2, 4, 'اور جو ایمان رکھتے ہیں اس پر جو آپ پر نازل کیا گیا ہے اور اس پر بھی جو آپ سے پہلے (سابقہ انبیاء) پر نازل کیا گیا۔ اور آخرت پر یقین رکھتے ہیں۔'),
(12, 2, 5, 'یہی لوگ اپنے پروردگار کی ہدایت پر (قائم) ہیں اور یہی وہ ہیں جو (آخرت میں) فوز و فلاح پانے والے ہیں۔'),
(13, 2, 6, '(اے رسول) جن لوگوں نے کفر اختیار کیا۔ ان کے لیے برابر ہے۔ آپ انہیں ڈرائیں یا نہ ڈرائیں۔ بہرحال وہ ایمان نہیں لائیں گے۔'),
(14, 2, 7, 'خدا نے ان کے دلوں اور ان کے کانوں پر مہر لگا دی ہے اور ان کی آنکھوں پر پردہ پڑ گیا ہے۔ اور (آخرت میں) ان کے لیے بڑا عذاب ہے۔'),
(15, 2, 8, 'اور کچھ لوگ ایسے بھی ہیں جو کہتے ہیں کہ ہم اللہ اور روزِ آخرت پر ایمان لائے ہیں۔ حالانکہ وہ مؤمن نہیں ہیں۔'),
(16, 2, 9, 'خدا اور اہل ایمان کو دھوکہ دے رہے ہیں حالانکہ وہ خود اپنے سوا کسی کو دھوکہ نہیں دیتے۔ مگر انہیں اس کا (احساس) نہیں ہے۔'),
(17, 2, 10, 'ان کے دلوں میں (نفاق) کی ایک بیماری ہے۔ سو اللہ نے ان کی بیماری اور بڑھا دی ہے اور ان کے (مسلسل) جھوٹ بولنے کی وجہ سے ان کے لیے دردناک عذاب ہے۔'),
(18, 2, 11, 'اور جب ان سے کہا جائے کہ زمین میں فساد نہ پھیلاؤ۔ تو کہتے ہیں کہ ہم تو اصلاح کرنے والے ہیں۔'),
(19, 2, 12, 'خبردار۔ درحقیقت وہی فساد پھیلانے والے ہیں۔ لیکن انہیں شعور نہیں ہے۔'),
(20, 2, 13, 'اور جب ان سے کہا جاتا ہے کہ تم بھی اسی طرح ایمان لاؤ جس طرح اور لوگ ایمان لائے ہیں تو وہ کہتے ہیں کہ کیا ہم بیوقوفوں کی طرح ایمان لائیں؟ خبردار۔ یہی لوگ خود بیوقوف ہیں لیکن جانتے نہیں ہیں۔'),
(21, 2, 14, 'اور یہ لوگ جب اہل ایمان سے ملتے ہیں تو کہتے ہیں کہ ہم بھی ایمان لائے ہیں اور جب علیحدگی میں اپنے شیطانوں سے ملتے ہیں تو کہتے ہیں کہ ہم تو تمہارے ساتھ ہیں ان (مسلمانوں) سے تو ہم صرف مذاق کر رہے ہیں۔'),
(22, 2, 15, 'خود اللہ ان سے مذاق کر رہا ہے اور انہیں ڈھیل دے رہا ہے اور وہ اپنی سرکشی میں اندھوں کی طرح بھٹک رہے ہیں۔'),
(23, 2, 16, 'یہی وہ لوگ ہیں جنہوں نے ہدایت کے بدلے گمراہی خریدلی۔ سو نہ تو ان کی تجارت سود مند ہوئی اور نہ ہی انہیں ہدایت نصیب ہوئی۔'),
(24, 2, 17, 'ان کی مثال اس شخص کی سی ہے جس نے آگ روشن کی۔ اور جب آگ نے اس کے گرد و نواح کو روشن کر دیا تو اللہ نے ان کی روشنی (بینائی) سلب کر لی اور ان کو اندھیروں میں چھوڑ دیا اب انہیں کچھ سجھائی نہیں دیتا۔'),
(25, 2, 18, 'پس وہ ایسے بہرے، گونگے اور اندھے ہیں کہ اب (گمراہی سے راہِ ہدایت کی طرف) نہیں لوٹیں گے۔'),
(26, 2, 19, 'یا (پھر ان کی مثال ایسی ہے جیسے) آسمان سے زور دار بارش برس رہی ہو جس میں تاریکیاں ہوں۔ اور گرج چمک بھی۔ اور وہ گرنے والی بجلیوں سے مرنے کے ڈر سے اپنی انگلیاں اپنے کانوں میں ٹھونس لیں۔ حالانکہ اللہ ہر طرف سے کافروں کو گھیرے ہوئے ہے۔'),
(27, 2, 20, 'قریب ہے کہ بجلی (کی چمک) ان کی آنکھوں کو اچک لے (انہیں خیرہ کر دے) جب بجلی ان کے لئے اجالا کرتی ہے تو وہ اس کی روشنی میں چلنے لگتے ہیں اور جب اندھیرا چھا جاتا ہے تو کھڑے رہ جاتے ہیں اگر اللہ چاہتا تو ان کی سماعت و بصارت (سننے اور دیکھنے کی طاقت) کو زائل کر دیتا۔ بے شک اللہ ہر چیز پر قادر ہے۔'),
(28, 2, 21, 'اے لوگو! اپنے اس پروردگار کی عبادت (پرستش) کرو جس نے تمہیں بھی پیدا کیا اور انہیں بھی جو تم سے پہلے ہو گزرے تاکہ تم پرہیزگار بن جاؤ۔'),
(29, 2, 22, 'وہی (پروردگار) جس نے تمہارے لئے زمین کا فرش بچھایا اور آسمان کا شامیانہ لگایا (اور اسے چھت بنایا) اور آسمان (بلندی) سے پانی برسایا۔ اس سے تمہاری روزی کے لئے کچھ پھل برآمد کئے سو جان بوجھ کر (کسی کو) اللہ کا ہمسر و شریک نہ بناؤ۔'),
(30, 2, 23, 'اور جو کچھ (قرآن) ہم نے اپنے بندہ (خاص) پر نازل کیا ہے اگر تمہیں اس (کے کلام اللہ ہونے) میں شک ہے تو تم اس کے مانند ایک ہی سورہ لے آؤ۔ اور اگر تم سچے ہو تو ایک اللہ کو چھوڑ کر اپنے سب حمایتیوں و ہم نواؤں کو بھی بلا لو۔'),
(31, 2, 24, 'لیکن اگر تم یہ کام نہ کر سکو اور ہرگز ایسا نہیں کر سکوگے تو پھر (دوزخ کی) اس آگ سے ڈرو۔ جس کا ایندھن آدمی اور پتھر ہیں جو کافروں کے لئے تیار کی گئی ہے۔'),
(32, 2, 25, 'اور (اے رسول) ان لوگوں کو خوشخبری دے دو جو ایمان لائے اور نیک عمل کئے۔ کہ ان کے لئے (بہشت کے) ایسے باغ ہیں کہ جن کے نیچے سے نہریں جاری ہیں۔ جب بھی انہیں ان (باغات) سے کوئی پھل کھانے کو ملے گا تو وہ (اس کی صورت دیکھ کر) کہیں گے کہ یہ تو وہی ہے جو اس سے پہلے ہمیں (دنیا میں) کھانے کو مل چکا ہے۔ حالانکہ انہیں جو دیا گیا ہے وہ (صورت میں دنیا کے پھل سے) ملتا جلتا ہے (مگر ذائقہ الگ ہوگا) اور ان کے لئے وہاں پاکیزہ بیویاں ہوں گی اور وہ ان (بہشتوں) میں ہمیشہ ہمیشہ رہیں گے۔'),
(33, 2, 26, 'بے شک اللہ اس بات سے ہرگز نہیں شرماتا کہ (کسی مطلب کی وضاحت کے لئے) مچھر اور اس سے بھی بڑھ کر (کسی حقیر چیز) کی مثال بیان کرے۔ پس جو لوگ مؤمن ہیں وہ تو جانتے ہیں کہ یہ (مثال یقینا) حق ہے ان کے پروردگار کی طرف سے اور جو کافر ہیں وہ کہتے ہیں کہ ایسی مثال سے اللہ کا کیا مقصد ہے؟ اللہ ایسی مثال سے بہت سوں کو گمراہی میں چھوڑ دیتا ہے اور بہت سوں کو ہدایت کرتا ہے اور وہ گمراہی میں نہیں چھوڑتا مگر فاسقوں (نافرمانوں) کو۔'),
(34, 2, 27, 'جو خدا سے مستحکم عہد و پیمان کرنے کے بعد اسے توڑ دیتے ہیں اور جس (رشتہ) کے جوڑنے کا اللہ نے حکم دیا ہے وہ اسے توڑتے ہیں۔ اور زمین میں فساد پھیلاتے ہیں (دراصل) یہی لوگ ہیں جو نقصان اٹھانے والے ہیں۔'),
(35, 2, 28, 'بھلا تم کس طرح اللہ کا انکار کرتے ہو۔ حالانکہ تم مردہ (بے جان) تھے تو اسی نے تمہیں زندہ کیا (جاندار بنایا) پھر وہی تمہیں موت دے گا پھر وہی تمہیں زندگی دے گا۔ پھر تم اسی کی طرف لوٹائے جاؤگے۔'),
(36, 2, 29, 'وہی تو وہ (اللہ) ہے جس نے تمہارے لئے پیدا کیا وہ سب کچھ جو زمین میں ہے پھر آسمان کی طرف توجہ فرمائی تو سات آسمان ہموار و استوار کر دیئے۔ اور وہ ہر چیز کا جاننے والا ہے۔'),
(37, 2, 30, '(اے رسول وہ وقت یاد کرو) جب تمہارے پروردگار نے فرشتوں سے کہا کہ میں زمین پر ایک خلیفہ (جانشین) بنانے والا ہوں تو انہوں نے کہا کیا تو اس میں اس کو (خلیفہ) بنائے گا جو اس میں فساد پھیلائے گا اور خون ریزی کرے گا۔ حالانکہ ہم تیری حمد و ثنائ کے ساتھ تسبیح کرتے ہیں اور تیری تقدیس (پاکیزگی بیان) کرتے ہیں۔ فرمایا: یقینا میں وہ کچھ جانتا ہوں جو تم نہیں جانتے۔'),
(38, 2, 31, 'اور اس (اللہ) نے آدم (ع) کو (تمام چیزوں کے) نام سکھائے۔ پھر ان کو (جن کے نام سکھائے تھے) فرشتوں کے سامنے پیش کیا اور فرمایا اگر تم سچے ہو (کہ تم خلافت کے زیادہ حقدار ہو) تو مجھے ان اشخاص کے نام بتاؤ۔'),
(39, 2, 32, 'انہوں نے کہا (ہر نقص و عیب سے) تیری ذات پاک ہے۔ ہمیں اس کے سوا جو تو نے ہمیں تعلیم دیا ہے (سکھایا ہے) اور مزید کچھ علم نہیں ہے۔ بے شک تو بڑا علم والا اور بڑی حکمت والا ہے۔'),
(40, 2, 33, 'فرمایا: اے آدم تم ان کو ان کے نام بتاؤ۔ تو جب آدم نے ان (فرشتوں) کو ان کے نام بتا دیئے تو خدا نے فرمایا کیا میں نے تم سے نہیں کہا تھا کہ میں آسمانوں اور زمین کے سب مخفی رازوں کو جانتا ہوں اور وہ بھی جانتا ہوں جو تم ظاہر کر رہے ہو اور وہ بھی جو تم (اندرونِ دل) چھپائے ہوئے تھے۔'),
(41, 2, 34, 'اور (وہ وقت یاد کرو) جب ہم نے فرشتوں سے کہا کہ آدم کے سامنے سجدہ میں گر جاؤ ابلیس کے سوا سب سجدہ میں گر گئے۔ اس نے انکار و تکبر کیا اور کافروں میں سے ہوگیا۔'),
(42, 2, 35, 'اور ہم نے کہا: اے آدم! تم اور تمہاری بیوی دونوں بہشت میں رہو۔ اور اس سے جہاں سے تمہارا دل چاہے مزے اور فراغت کے ساتھ کھاؤ۔ لیکن اس (مخصوص) درخت کے پاس نہ جانا (اس کا پھل نہ کھانا) ورنہ تم زیاں کاروں میں سے ہو جاؤگے۔'),
(43, 2, 36, 'تب شیطان نے (اس درخت کے باعث) ان کے قدم پھسلائے۔ اور انہیں اس (عیش و آرام) سے نکلوا دیا جس میں وہ تھے اور ہم نے کہا اب تم (زمین پر) اتر جاؤ۔ ایک دوسرے کے دشمن ہو کر۔ اور تمہارے لئے زمین میں ایک (خاص) وقت تک ٹھہرنے اور فائدہ ا ٹھانے کا سامان موجود ہے۔'),
(44, 2, 37, 'اس کے بعد آدم نے اپنے پروردگار سے کچھ دعا کے کلمات (حاصل کئے) تو اس نے ان کی توبہ قبول کی کیونکہ وہ بڑا توبہ قبول کرنے والا اور بڑا رحم کرنے والا ہے۔'),
(45, 2, 38, 'ہم نے کہا تم سب اس سے اتر جاؤ۔ اس کے بعد اگر تمہارے پاس میری طرف سے کوئی ہدایت پہنچے تو جو لوگ میری اس ہدایت کی پیروی کریں گے۔ تو ان کے لئے نہ کوئی خوف ہوگا اور نہ ہی وہ غمگین ہوں گے۔'),
(46, 2, 39, 'اور جو کفر اختیار کریں گے اور ہماری آیتوں (نشانیوں) کو جھٹلائیں گے وہی لوگ دوزخ والے ہوں گے۔ وہ اس میں ہمیشہ ہمیشہ رہیں گے۔'),
(47, 2, 40, 'اے بنی اسرائیل (اولادِ یعقوب) میری وہ نعمت یاد کرو جو میں نے تم کو عطا کی تھی اور تم مجھ سے کئے ہوئے عہد و پیمان کو پورا کرو میں بھی تم سے کئے ہوئے اپنے عہد کو پورا کروں گا اور تم مجھ سے ڈرتے رہو۔'),
(48, 2, 41, 'اور اس کتاب (قرآن) پر ایمان لاؤ جو میں نے (اب) نازل کی ہے۔ جو اس (توراۃ) کی تصدیق کرتی ہے جو تمہارے پاس ہے اور تم اس کے اولین منکر نہ بنو اور آیتوں کو (ان میں تحریف کرکے) تھوڑی قیمت (دنیوی مفاد) پر فروخت نہ کرو۔ اور مجھ ہی سے (میری نافرمانی سے) ڈرو۔'),
(49, 2, 42, 'اور حق کو باطل کے ساتھ خلط ملط نہ کرو اور جانتے بوجھتے ہوئے حق کو نہ چھپاؤ۔'),
(50, 2, 43, 'اور نماز قائم کرو اور زکوٰۃ ادا کرو۔ اور (میری بارگاہ میں) رکوع کرنے (جھکنے) والوں کے ساتھ رکوع کرو (باجماعت نماز ادا کرو)۔'),
(51, 2, 44, 'کیا تم دوسرے لوگوں کو تو نیکی کا حکم دیتے ہو مگر اپنے آپ کو بھول جاتے ہو؟ حالانکہ تم کتابِ خدا کی تلاوت کرتے رہتے ہو کیا تم عقل سے کام نہیں لیتے۔'),
(52, 2, 45, 'اور (مشکلات و مصائب کے وقت) صبر (روزہ) اور نماز کے ذریعہ (خدا سے) مدد مانگو اور یہ (نماز یقینا بہت گراں ہے سوائے ان بندوں کے جو خشوع و خضوع رکھنے والے ہیں'),
(53, 2, 46, 'اور (بارگاہِ خداوندی میں) عاجزی کرنے والے ہیں جو سمجھتے ہیں کہ انہیں اپنے پروردگار کا سامنا کرنا ہے (اس کے حضور پیش ہونا ہے) اور (آخرکار) اسی کی طرف پلٹ کر جانے والے ہیں۔'),
(54, 2, 47, 'اے بنی اسرائیل! میری وہ نعمت یاد کرو جس سے میں نے تمہیں نوازا تھا اور یہ کہ میں نے تمہیں دنیا جہاں کے لوگوں پر فضیلت دی۔'),
(55, 2, 48, 'اور اس دن (قیامت) سے ڈرو جب کوئی کسی کو فائدہ نہیں پہنچا سکے گا اور نہ ہی کسی کی طرف سے کوئی سفارش قبول ہوگی۔ اور نہ کسی سے کوئی معاوضہ لیا جائے گا۔ اور نہ ان کی کوئی مدد کی جائے گی۔'),
(56, 2, 49, 'اور (وہ وقت یاد کرو) جب ہم نے تمہیں فرعونیوں سے نجات دی تھی۔ جو تمہیں بدترین عذاب کا مزہ چکھاتے تھے (یعنی) تمہارے لڑکوں کو قتل کر ڈالتے تھے اور تمہاری عورتوں (بیٹیوں) کو (اپنی خدمت گزاری کے لئے) زندہ رہنے دیتے تھے اور اس میں تمہارے پروردگار کی بڑی سخت آزمائش تھی۔'),
(57, 2, 50, 'اور (وہ وقت یاد کرو) جب ہم نے تمہارے لئے سمندر کو شگافتہ کیا (اسے پھاڑ کر تمہارے لئے راستہ بنایا) اور تمہیں نجات دی اور تمہارے دیکھتے دیکھتے فرعونیوں کو غرق کر دیا۔'),
(58, 2, 51, 'اور (وہ وقت یاد کرو) جب ہم نے موسیٰ سے (توراۃ دینے کے لئے) چالیس راتوں کا وعدہ کیا تھا پھر تم نے ان کے بعد گو سالہ (بچھڑے) کو (معبود) بنا لیا۔ جبکہ تم ظالم تھے۔'),
(59, 2, 52, 'پھر ہم نے اس (ظلم) کے بعد بھی معاف کر دیا۔ تاکہ تم شکر گزار بنو۔'),
(60, 2, 53, 'اور (وہ وقت یاد کرو) جب فرعونی (تم پر ظلم کر رہے تھے تو) ہم نے موسیٰ کو کتاب فرقان عطا کی تاکہ تم ہدایت حاصل کرو۔'),
(61, 2, 54, 'اور (وہ وقت یاد کرو) جب (موسیٰ نے اپنی قوم سے کہا) اے میری قوم! یقینا تم نے گو سالہ کو (معبود) بنا کر اپنی جانوں پر بڑا ظلم کیا ہے۔ لہٰذا تم اپنے خالق کی بارگاہ میں (اس طرح) توبہ کرو۔ کہ اپنی جانوں کو قتل کرو۔ یہی (طریقہ کار) تمہارے خالق کے نزدیک تمہارے لئے بہتر ہے۔ اس صورت میں اس نے تمہاری توبہ قبول کی۔ بے شک وہ بڑا توبہ قبول کرنے والا، نہایت رحم والا ہے۔'),
(62, 2, 55, 'اور (وہ وقت یاد کرو) جب تم نے کہا اے موسیٰ! اس وقت تک ہم ہرگز ایمان نہیں لائیں گے جب تک ظاہر بظاہر (اعلانیہ) خدا کو دیکھ نہ لیں۔ سو (اس پر) تمہارے دیکھتے بجلی نے تمہیں اپنی گرفت میں لے لیا۔'),
(63, 2, 56, 'پھر ہم نے تمہاری موت کے بعد تمہیں زندہ کیا تاکہ (اس احسان کے بعد) تم شکرگزار بن جاؤ۔'),
(64, 2, 57, 'اور ہم نے (صحراء میں) تمہارے اوپر ابر کا سایہ کیا اور تم پر من و سلویٰ نازل کیا (اور کہا) کہ ان پاکیزہ چیزوں میں سے کھاؤ جو ہم نے تم کو عطا کی ہیں۔ اور ان لوگوں نے (ناشکری کرکے) ہم پر کوئی زیادتی نہیں کی بلکہ وہ اپنی ہی جانوں پر ظلم کرتے رہے۔'),
(65, 2, 58, 'اور (یاد کرو وہ وقت) جب ہم نے کہا کہ اس بستی (بیت المقدس یا اریحا) میں داخل ہو جاؤ اور اس میں سے جہاں سے چاہو (اور جو چاہو) مزے سے بافراغت کھاؤ۔ (پیو) اور دروازے سے سجدہ کرتے ہوے حطہ (بخشش) کہتے ہوئے داخل ہو جاؤ۔ ہم تمہاری خطائیں معاف کر دیں گے اور ہم نیکی کرنے والوں کو کچھ زیادہ ہی (ثواب) عطا کریں گے۔'),
(66, 2, 59, 'مگر ان ظالموں نے وہ بات (حطہ) جو ان سے کہی گئی تھی اسے ایک اور بات سے بدل دیا (اور حطۃ کی بجائے حنطۃ کہا) لہٰذا ہم نے ان کی نافرمانیوں کی وجہ سے ان پر آسمان سے بڑا عذاب نازل کیا'),
(67, 2, 60, 'اور (وہ وقت بھی یاد کرو) جب موسیٰ نے اپنی قوم کے لئے (خدا سے) پانی مانگا۔ تو ہم نے کہا اپنا عصا چٹان پر مارو جس کے نتیجہ میں بارہ چشمے پھوٹ نکلے اس طرح ہر گروہ نے اپنا اپنا گھاٹ معلوم کر لیا۔ (ہم نے کہا) خدا کے دیئے ہوئے رزق سے کھاؤ پیو اور زمین میں فساد پھیلاتے نہ پھرو۔'),
(68, 2, 61, 'اور (وہ وقت بھی یاد کرو) جب تم نے کہا اے موسیٰ! ہم ایک ہی (قسم کے) کھانے پر ہرگز صبر نہیں کر سکتے۔ اس لئے آپ اپنے پروردگار سے ہمارے لئے دعا کیجئے کہ وہ (من و سلویٰ کی بجائے) ہمارے لئے وہ چیزیں نکالے (پیدا کرے) جو زمین اگاتی ہے جیسے ساگ پات، ترکاری، کھیرا، ککڑی، مسور اور لہسن پیاز۔ موسیٰ نے کہا کیا تم کمتر و کہتر چیز لینا چاہتے ہو اس کے بدلہ میں جو برتر و بہتر ہے۔ اچھا شہر میں اتر پڑو۔ بے شک (وہاں) تمہیں وہ کچھ مل جائے گا جو تم مانگتے ہو۔ اور (انجام کار) ان پر ذلت و خواری اور افلاس و ناداری مسلط ہوگئی۔ اور وہ (اللہ کی نشانیوں کا ) انکار کرتے تھے اور انبیاء کو ناحق قتل کرتے تھے (اور یہ اس لئے بھی ہوا) کہ وہ نافرمانی کرتے تھے اور حد سے بڑھ بڑھ جاتے تھے (سرکشی کرتے تھے)۔'),
(69, 2, 62, 'بے شک جو لوگ مؤمن یہودی نصرانی اور صابی (ستارہ پرست) کہلاتے ہیں (غرض) جو کوئی بھی (واقعی) اللہ اور آخرت پر ایمان لائے اور نیک عمل کرے۔ تو ان (سب) کے لئے ان کے پروردگار کے پاس ان کا اجر و ثواب (محفوظ) ہے اور ان کے لئے نہ کوئی خوف ہے اور نہ ہی وہ غمگین ہوں گے۔'),
(70, 2, 63, 'اور (اس وقت کو یاد کرو) جب ہم نے (کوہ) طور کو تم پر اٹھا کر (اور لٹکا کر) یہ عہد و پیمان لیا تھا کہ ہم نے جو کچھ تم کو دیا ہے (توراۃ) اسے مضبوطی سے پکڑو۔ اور جو کچھ اس میں ہے اسے یاد رکھو (اس پر عمل کرو) تاکہ تم پرہیزگار بن جاؤ۔'),
(71, 2, 64, 'پھر تم اس (پختہ عہد) کے بعد پھر گئے۔ سو اگر تم پر اللہ کا خاص فضل و کرم اور اس کی خاص رحمت نہ ہوتی تو تم سخت خسارہ (گھاٹا) اٹھانے والوں میں ہو جاتے۔'),
(72, 2, 65, 'اور یقینا تمہیں ان لوگوں کا تو علم ہے جنہوں نے تم میں سے یوم السبت (ہفتہ کے دن) کے بارے میں زیادتی کی۔ (اور اس دن شکار کرکے قانون شکنی کی) تو ہم نے ان سے کہا کہ تم دھتکارے ہوئے بندر بن جاؤ۔'),
(73, 2, 66, 'پس ہم نے اس واقعہ کو اس زمانہ کے لوگوں اور بعد میں آنے والوں کے لئے (سامان) عبرت اور پرہیزگاروں کے لئے نصیحت بنا دیا۔'),
(74, 2, 67, 'اور (اس وقت کو یاد کرو) جب موسیٰ نے اپنی قوم سے کہا کہ اللہ تمہیں حکم دیتا ہے کہ ایک گائے ذبح کرو۔ کہنے لگے آپ ہم سے مذاق کرتے ہیں فرمایا: میں اس سے خدا کی پناہ مانگتا ہوں کہ میں جاہلوں میں سے ہو جاؤں۔'),
(75, 2, 68, 'انہوں نے کہا ہماری خاطر اپنے پروردگار سے التجا کیجئے کہ وہ ہمیں کھول کر بتائے کہ گائے کیسی ہو؟ (موسیٰ نے) کہا وہ (اللہ) فرماتا ہے کہ وہ نہ بالکل بوڑھی ہو اور نہ بالکل بن بیاہی بچھڑی۔ بلکہ ان دونوں کے بین (اوسط عمر کی ہو) جو حکم تمہیں دیا جا رہا ہے اس کی تعمیل کرو۔'),
(76, 2, 69, 'کہنے لگے کہ ہماری طرف سے اپنے پروردگار سے درخواست کیجئے کہ وہ ہمیں کھول کر بتائے کہ اس کا رنگ کیسا ہو؟ کہا وہ (اللہ) فرماتا ہے کہ وہ ایسے شوخ گہرے زرد رنگ کی ہو کہ دیکھنے والے کا جی خوش کر دے۔'),
(77, 2, 70, 'وہ بولے ہماری طرف سے اپنے پروردگار سے عرض کیجئے کہ وہ ہمیں کھول کر بتائے کہ وہ گائے کیسی ہو چونکہ یہ گائے ہم پر مشتبہ ہوگئی ہے؟ اور اگر اللہ نے چاہا تو ہم (اس گائے تک) صحیح راستہ پا جائیں گے۔'),
(78, 2, 71, '(موسیٰ نے) کہا وہ (پروردگار) فرماتا ہے کہ وہ ایسی گائے ہے جو سدھائی ہوئی نہیں ہے۔ نہ زمین کو جوتتی ہے اور نہ کھیتی کو پانی دیتی ہے۔ وہ صحیح سالم اور بے عیب ہے (اور ایسی یک رنگی ہے کہ) اس میں کوئی داغ دھبہ نہیں ہے اس پر پکار اٹھے اب آپ ٹھیک بات لائے غرض اب انہوں نے اسے ذبح کیا جبکہ وہ ایسا کرتے معلوم نہیں ہوتے تھے۔'),
(79, 2, 72, 'اور (وہ وقت یاد کرو) جب تم نے ایک شخص کو قتل کر ڈالا تھا۔ اور پھر اس کے بارے میں باہم جھگڑنے لگے (ایک دوسرے پر قتل کا الزام لگانے لگے) اور اللہ اس چیز کو ظاہر کرنے والا تھا جسے تم چھپا رہے تھے'),
(80, 2, 73, '(اس لئے) ہم نے کہا کہ اس گائے کا کوئی ٹکڑا اس (مقتول کی لاش) پر مارو۔ اسی طرح اللہ مردوں کو زندہ کرتا ہے۔ اور تمہیں اپنی قدرت کی نشانیاں دکھاتا ہے تاکہ تم عقل سے کام لو۔'),
(81, 2, 74, '(اے بنی اسرائیل) پھر اس کے بعد تمہارے دل ایسے سخت ہوگئے کہ گویا وہ پتھر ہیں بلکہ اس سے بھی زیادہ سخت کیونکہ پتھروں میں سے کچھ ایسے بھی ہوتے ہیں جن سے نہریں پھوٹ نکلتی ہیں۔ اور کچھ ایسے بھی ہوتے ہیں جو پھٹ جاتے ہیں اور ان سے پانی نکل آتا ہے۔ اور کچھ ایسے بھی ہیں کہ جو خدا کے خوف و خشیہ سے گر پڑتے ہیں۔ اور تم لوگ جو کچھ بھی کرتے ہو اللہ اس سے غافل (بے خبر) نہیں ہے۔'),
(82, 2, 75, '(اے مسلمانو) کیا تم یہ توقع رکھتے ہو کہ یہ (یہودی) تمہارے کہنے سے ایمان لائیں گے حالانکہ ان میں سے ایک گروہ ایسا بھی گزرا ہے جو اللہ کا کلام سنتا تھا اور پھر اسے سمجھنے کے بعد دیدہ و دانستہ اس میں تحریف (رد و بدل) کر دیتا تھا۔'),
(83, 2, 76, 'اور (یہ منافق لوگ) جب اہل ایمان سے ملتے ہیں تو کہتے ہیں ہم بھی ایمان لائے ہیں اور جب آپس میں تنہا ہوتے ہیں تو کہتے ہیں کہ کیا تم ان (مسلمانوں) کو وہ باتیں بتاتے ہو جو اللہ نے (توراۃ میں) تم پر ظاہر کی ہیں۔ تاکہ وہ انہیں تمہارے پروردگار کے حضور تمہارے خلاف بطور حجت پیش کریں۔ کیا تم عقل سے کام نہیں لیتے۔'),
(84, 2, 77, 'کہا یہ لوگ اتنا بھی نہیں جانتے کہ اللہ وہ سب کچھ جانتا ہے جو کچھ یہ چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں۔'),
(85, 2, 78, 'اور ان (یہود) میں کچھ ایسے ان پڑھ بھی ہیں جو بے بنیاد امیدوں اور جھوٹی آرزوؤں کے سوا کتاب (توراۃ) کا کچھ بھی علم نہیں رکھتے۔ اور وہ صرف خام خیالیوں میں پڑے رہتے ہیں۔'),
(86, 2, 79, 'سو خرابی و بربادی ہے ان لوگوں کے لئے جو اپنے ہاتھوں سے (جعلی) کتاب (تحریر) لکھتے ہیں اور پھر کہتے ہیں کہ یہ اللہ کی طرف سے (آئی) ہے تاکہ اس کے عوض تھوڑی سی قیمت (دنیوی فائدہ) حاصل کریں پس خرابی ہے ان کے لئے ان کے ہاتھوں کی لکھائی پر اور بربادی ہے۔ ان کے لئے ان کی اس کمائی پر ۔'),
(87, 2, 80, 'اور یہ لوگ کہتے ہیں کہ گنتی کے چند دنوں کے سوا دوزخ کی آگ ہمیں چھو بھی نہیں سکتی (اے رسول) آپ ان سے کہیے! کیا تم نے خدا سے کوئی عہد و پیمان لے لیا ہے کہ خدا کبھی اپنے عہد کی خلاف ورزی نہیں کرے گا؟ یا اللہ کے ذمہ وہ بات لگا رہے ہو جس کا تمہیں کوئی علم نہیں ہے۔'),
(88, 2, 81, 'کیوں نہیں (چھوئے گی) جو بھی برا کام کرے گا اور اس کا گناہ (چاروں طرف سے) اسے گھیر لے گا یہی لوگ دوزخی ہیں جس میں وہ ہمیشہ ہمیشہ رہیں گے۔'),
(89, 2, 82, 'اور جو لوگ ایمان لائے اور (اس کے ساتھ ساتھ) نیک عمل بھی کئے یہی لوگ بہشتی ہیں جس میں وہ ہمیشہ ہمیشہ رہیں گے۔'),
(90, 2, 83, 'اور (وہ وقت یاد کرو) جب ہم نے بنی اسرائیل سے یہ پختہ عہد لیا تھا کہ اللہ کے سوا کسی کی عبادت نہ کرنا۔ ماں باپ سے (خصوصاً) رشتہ داروں، یتیموں اور مسکینوں سے (عموماً) نیک سلوک کرنا اور سب سے اچھی بات کہنا، نماز قائم کرنا اور زکوٰۃ ادا کرنا۔ مگر تم میں سے تھوڑے آدمیوں کے سوا باقی سب اس (عہد) سے پھر گئے۔ اور تم ہو ہی روگردانی کرنے والے۔'),
(91, 2, 84, 'اور (وہ وقت یاد کرو) جب ہم نے تم سے پختہ عہد لیا تھا کہ آپس میں ایک دوسرے کا خون نہ بہانا، ایک دوسرے کو اپنے وطن سے (نکال کر) بے وطن نہ کرنا۔ پھر تم نے اس کا اقرار کیا اور تم خود اس کے گواہ ہو۔'),
(92, 2, 85, 'پھر تم ہی وہ ہو۔ جو اپنوں کو قتل کرتے ہو۔ اور اپنے بھائی بندوں کے کچھ لوگوں کو ان کے وطن سے نکال بھی دیتے ہو اور ان کے خلاف گناہ اور ظلم و زیادتی کے ساتھ سازش کرتے ہو اور جتھے بندی کرتے ہو (ان کے مخالفین کی مدد کرتے ہو) اور (پھر لطف یہ ہے کہ) اگر وہی لوگ دشمنوں کے ہاتھوں قید ہو کر تمہارے پاس آجائیں۔ تو تم ہی فدیہ دے کر انہیں چھڑا لیتے ہو۔ حالانکہ ان کا وطن سے نکالنا تم پر حرام تھا۔ کیا تم کتاب (توراۃ) کے ایک حصہ (فدیہ دینے) پر ایمان لاتے ہو۔ اور دوسرے حصہ (جلا وطن کرنے کی حرمت) کا انکار کرتے ہو؟ تم میں سے جو ایسا کرے اس کی سزا اس کے سوا اور کیا ہے کہ دنیا کی زندگی میں ذلیل و خوار ہو اور قیامت کے دن سخت ترین عذاب کی طرف لوٹا دیئے جائیں اور تم جو کچھ بھی کرتے ہو اللہ اس سے غافل (بے خبر) نہیں ہے۔'),
(93, 2, 86, 'یہ وہ (بدقسمت) لوگ ہیں جنہوں نے آخرت کی (ابدی زندگی) کے عوض دنیوی (فانی) زندگی خرید لی ہے پس نہ ہی ان کے عذاب میں کوئی تخفیف کی جائے گی اور نہ ہی ان کی کوئی مدد کی جائے گی۔'),
(94, 2, 87, 'البتہ ہم نے موسیٰ کو کتاب (توراۃ) عطا کی۔ اور ان کے بعد ہم نے پے در پے رسول بھیجے اور عیسیٰ بن مریم کو کھلی نشانیاں عطا کیں اور روح القدس کے ذریعہ سے ان کی تائید کی۔ (اس کے باوجود جب بھی کوئی رسول تمہاری خواہشات نفسی کے خلاف کوئی حکم لے کر تمہارے پاس آیا تو تم نے تکبر کیا سو بعض کو جھٹلایا اور بعض کو قتل کر ڈالا۔'),
(95, 2, 88, 'اور وہ کہتے ہیں کہ ہمارے دلوں پر (قدرتی) غلاف چڑھے ہوئے ہیں (ہماری سمجھ میں کچھ نہیں آتا)۔ نہیں۔ بلکہ اللہ نے ان کے کفر کی وجہ سے ان پر لعنت کی ہے (اپنی رحمت سے دور کر دیا ہے) اس لئے وہ کم ہی ایمان لائیں گے۔'),
(96, 2, 89, 'اور جب ان کے پاس اللہ کی طرف سے وہ کتاب آئی جو ان کے پاس والی کتاب (توراۃ) کی تصدیق کرتی ہے باوجودیکہ اس کے آنے سے پہلے خود یہ لوگ کافروں کے خلاف اس کے ذریعہ سے فتح و ظفر طلب کیا کرتے تھے۔ مگر جب وہ (کتاب) ان کے پاس آگئی جسے وہ پہچانتے تھے تو اس کا انکار کر دیا۔ پس کافروں (منکروں) پر اللہ کی لعنت ہو۔'),
(97, 2, 90, 'سو کس قدر بری ہے وہ چیز جس کے عوض ان لوگوں نے اپنی جانوں کا سودا کیا ہے کہ محض اس ضد اور سرکشی کی بناء پر اللہ کی نازل کردہ کتاب کا انکار کر دیا کہ اس نے اپنے بندوں میں سے ایک خاص بندہ (نبی خاتم (ع)) پر اپنے فضل و کرم سے کیوں (وحی نبوت و کتاب) نازل کر دی؟ پس وہ اس روش کے نتیجہ میں (خدا کے) غضب بالائے غضب کے سزاوار ہوئے اور کافروں کے لئے ذلت آمیز عذاب ہے۔'),
(98, 2, 91, 'اور جب ان سے کہا جاتا ہے کہ جو (قرآن) اللہ نے نازل کیا ہے اس پر ایمان لاؤ۔ تو وہ کہتے ہیں کہ ہم اس پر تو ایمان رکھتے ہیں جو ہم (بنی اسرائیل) پر نازل کیا گیا ہے مگر اس کے علاوہ جو کچھ (قرآن، انجیل وغیرہ) ہے اس کا انکار کرتے ہیں حالانکہ وہ حق ہے اور جو ان کے پاس موجود ہے (توراۃ) اس کی بھی تصدیق کرتا ہے (اے رسول) آپ ان سے کہیے کہ اگر تم (توراۃ پر) ایمان رکھتے تھے تو اس سے پہلے (اگلے زمانہ) میں اللہ کے نبیوں کو کیوں قتل کرتے رہے ہو؟ ۔'),
(99, 2, 92, 'اور یقینا موسیٰ تمہارے پاس کھلے ہوئے معجزے لے کر آئے پھر بھی تم ایسے ظالم تھے کہ ان کے (کوہ طور پر جانے کے) بعد گوسالہ کو (معبود) بنا لیا۔'),
(100, 2, 93, 'اور (وہ وقت یاد کرو) کہ جب ہم نے تم سے عہد و پیمان لیا اور تمہارے اوپر کوہِ طور کو بلند کیا (اور کہا تھا کہ جو کچھ ہم نے تمہیں دیا ہے اسے مضبوطی سے پکڑو۔ اور جو کچھ اس میں ہے اسے غور سے) سنو تو (تمہارے اسلاف) نے زبان سے کہا کہ ہم نے سن تو لیا اور دل میں کہا ہم عمل نہیں کریں گے اور ان کے کفر کی وجہ سے ان کے دلوں میں گوسالہ کی محبت بیٹھ گئی تھی۔ (اے رسول) کہیے! اگر تم مؤمن ہو تو تمہارا ایمان تمہیں بہت ہی برے کاموں کا حکم دیتا ہے (یہ عجیب ایمان ہے)۔'),
(101, 2, 94, 'نیز ان سے کہیے کہ اگر خدا کے نزدیک آخرت کا گھر (جنت) دوسرے لوگوں کو چھوڑ کر صرف تمہارے لئے مخصوص ہے تو پھر موت کی آرزو کرو۔ اگر تم اپنے اس دعویٰ میں سچے ہو (تاکہ جلدی بہشت میں داخل ہو جاؤ)۔'),
(102, 2, 95, 'اور (سن لو) کہ یہ لوگ ان (اعمالِ بد) کی وجہ سے جو ان کے ہاتھ آگے بھیج چکے ہیں کبھی بھی موت کی آرزو نہیں کریں گے اور خدائے تعالیٰ ظالموں کو خوب جانتا ہے۔'),
(103, 2, 96, 'اور (اے نبی!) تم ان کو سب لوگوں سے حتیٰ کہ مشرکوں سے بھی بڑھ کر زندگی کا حریص پاؤ گے ان میں ہر ایک چاہتا ہے کہ کاش اسے ایک ہزار سال کی عمر ملے حالانکہ اس قدر عمر کا مل جانا بھی اسے خدا کے عذاب سے نہیں بچا سکتا اور یہ لوگ جو کچھ کر رہے ہیں اللہ اسے خوب دیکھ رہا ہے۔'),
(104, 2, 97, '(اے رسول(ص)) کہہ دیجیئے! کہ جو شخص جبرئیل کا دشمن ہے (ہوا کرے) اس نے تو وہ (قرآن) خدا کے حکم سے آپ کے دل میں اتارا ہے، جو اپنے سے پہلے (نازل شدہ) کتابوں کی تصدیق کرتا ہے اور اہل ایمان کے لئے ہدایت اور کامیابی کی بشارت ہے۔'),
(105, 2, 98, 'جو کوئی اللہ، اس کے فرشتوں، اس کے رسولوں اور (خاص کر) جبرئیل و میکائیل کا دشمن ہو تو بے شک اللہ بھی کافروں کا دشمن ہے۔'),
(106, 2, 99, '(اے رسول(ص)) بالیقین ہم نے تمہاری طرف واضح آیتیں نازل کی ہیں اور ان کا انکار نہیں کرتے مگر وہی جو فاسق (نافرمان) ہیں۔'),
(107, 2, 100, 'اور کیا (ہمیشہ ایسا نہیں ہوا کہ) جب بھی انہوں (یہود) نے کوئی عہد کیا۔ تو انہی کے ایک گروہ نے اسے پس پشت ڈال دیا (توڑ دیا) بلکہ ان میں سے اکثر بے ایمان ہیں۔'),
(108, 2, 101, 'اور جب ان کے پاس اللہ کی طرف سے وہ رسول (حضرت محمد(ص)) آیا جو ان کے پاس والی کتاب کی تصدیق کرنے والا ہے تو انہی اہل کتاب میں سے ایک گروہ نے کتابِ خدا (توراۃ) کو اس طرح پس پشت ڈال دیا کہ جیسے کہ وہ اسے جانتے ہی نہیں۔'),
(109, 2, 102, 'اور (یہ لوگ) ان (بے بنیاد) چیزوں کی پیروی کرنے لگے جو شیاطین سلیمان کے عہد سلطنت میں پڑھ کر سنایا کرتے تھے۔ حالانکہ سلیمان نے کبھی کفر نہیں کیا۔ بلکہ ان شیطانوں نے کفر کیا جو لوگوں کو جادو کی تعلیم دیتے تھے (نیز) وہ اس چیز (جادو) کی پیروی کرنے لگے جو بابل کے مقام پر ہاروت و ماروت نامی دو فرشتوں پر اتاری گئی۔ حالانکہ یہ دونوں فرشتے اس وقت تک کسی کو کچھ تعلیم نہیں دیتے تھے جب تک پہلے یہ نہیں کہتے تھے کہ ہم محض آزمائش ہیں۔ لہٰذا (اس علم کو غلط استعمال کرکے) کافر نہ ہو جانا (بایں ہمہ) لوگ ان سے وہ کچھ سیکھتے تھے جس سے مرد اور اس کی بیوی میں جدائی ڈال دیں۔ حالانکہ وہ اذنِ خدا کے بغیر کسی کو کوئی نقصان نہیں پہنچا سکتے تھے۔ الغرض وہ لوگ ان سے وہ چیز سیکھتے تھے جو ان کو ضرر پہنچاتی تھی اور کوئی فائدہ نہیں پہنچاتی تھی۔ اور وہ اچھی طرح جانتے تھے کہ جو شخص (دین کے بدلے) ان چیزوں کو خریدے گا۔ اس کا آخرت میں کوئی حصہ نہیں ہے اور کس قدر برا (معاوضہ) ہے جس پر انہوں نے اپنی جانوں کا سودا کیا۔ کاش انہیں اس کا علم ہوتا۔'),
(110, 2, 103, 'اور اگر یہ لوگ ایمان لاتے اور پرہیزگاری اختیار کرتے تو خدا کی بارگاہ سے انہیں جو ثواب ملتا وہ (اس سے) بہتر ہوتا کاش انہیں اس کا علم ہوتا۔'),
(111, 2, 104, 'اے ایمان والو (پیغمبر (ع) سے) راعنا نہ کہا کرو (بلکہ) انظرنا کہا کرو اور (توجہ سے ان کی بات) سنا کرو اور کافروں کے لئے دردناک عذاب ہے۔'),
(112, 2, 105, 'جو لوگ کافر ہیں (خواہ) اہل کتاب میں سے ہوں یا مشرکین میں سے وہ ہرگز پسند نہیں کرتے کہ تمہارے پروردگار کی طرف سے تمہارے لئے کوئی بھلائی نازل ہو۔ حالانکہ اللہ جسے چاہتا ہے اپنی رحمت کے لئے مخصوص کر لیتا ہے اور اللہ بڑے فضل و کرم والا ہے۔'),
(113, 2, 106, 'ہم جس آیت کو منسوخ کر دیتے ہیں یا اسے بھلا دیتے ہیں تو اس سے بہتر یا اس جیسی لاتے ہیں۔ کیا تم نہیں جانتے کہ اللہ ہر چیز پر بڑی قدرت رکھتا ہے۔'),
(114, 2, 107, 'کیا تمہیں معلوم ہے کہ آسمانوں اور زمین کی سلطنت اللہ ہی کے لئے ہے۔ اور اللہ کے سوا تمہارا کوئی سرپرست اور مددگار نہیں ہے۔'),
(115, 2, 108, '(اے مسلمانو) کیا تم چاہتے ہو کہ اپنے رسول سے ویسے ہی (احمقانہ) سوال کرو۔ جیسے اس سے پہلے موسیٰ سے کئے گئے تھے؟ اور جس شخص نے ایمان کے عوض کفر اختیار کیا وہ راہِ راست سے بھٹک گیا۔'),
(116, 2, 109, '(اے مسلمانو!) بہت سے اہل کتاب اپنے ذاتی حسد کی وجہ سے چاہتے ہیں کہ ایمان لانے کے بعد تمہیں پھر کافر بنا دیں باوجودیکہ ان پر حق واضح ہو چکا ہے سو تم عفو و درگزر سے کام لو یہاں تک کہ اللہ ان کے بارے میں اپنا حکم بھیجے یقینا اللہ ہر چیز پر قادر ہے۔'),
(117, 2, 110, 'اور نماز قائم کرو۔ اور زکوٰۃ ادا کرو۔ اور جو کچھ نیکی اور بھلائی اپنے لئے (زادِ راہ کے طور پر) آگے بھیج دوگے اسے اللہ کے یہاں موجود پاؤگے جو کچھ تم کرتے ہو اللہ اسے دیکھ رہا ہے۔'),
(118, 2, 111, 'اور وہ (یہود و نصاریٰ) کہتے ہیں کہ کوئی ہرگز جنت میں داخل نہیں ہوگا مگر وہی جو یہودی و نصرانی ہوگا۔ یہ ان کی خیال بندیاں اور خالی تمنائیں ہیں۔ ان سے کہہ دیجئے کہ اگر تم (اپنے دعویٰ میں) سچے ہو تو اپنی کوئی دلیل پیش کرو۔'),
(119, 2, 112, 'ہاں۔ (کسی کی کوئی خصوصیت نہیں ہے) جو شخص (حق سن کر) اپنا سر تسلیم خدا کے سامنے خم کر دے اور (مقام عمل میں) نیکوکار بھی ہو تو اس کے پروردگار کے پاس اس کا اجر و ثواب ہے اور (قیامت کے دن) ایسے لوگوں کو کوئی خوف نہ ہوگا اور نہ وہ غمگین ہوں گے۔'),
(120, 2, 113, 'یہودی کہتے ہیں کہ نصرانی کسی چیز (راہ راست) پر نہیں اور نصرانی کہتے ہیں کہ یہودی کسی چیز (راہ راست) پر نہیں حالانکہ یہ سب (آسمانی) کتاب کے پڑھنے والے ہیں اسی طرح (بے بنیاد) باتیں ان لوگوں (کفار و مشرکین) نے بھی کی ہیں جو کچھ بھی علم نہیں رکھتے۔ پس جن باتوں میں یہ لوگ آپس میں جھگڑ رہے ہیں قیامت کے دن اللہ ان کے درمیان فیصلہ کر دے گا۔'),
(121, 2, 114, 'اور اس شخص سے بڑھ کر ظالم کون ہو سکتا ہے جو اللہ کی مسجدوں میں اللہ کے ذکر سے (اللہ کے بندوں کو) روکے اور ان کی ویرانی و بربادی کی کوشش کرے حالانکہ (روکنے والوں) کو یہ حق نہیں پہنچتا کہ وہ مسجدوں میں داخل ہوں مگر ڈرتے ڈرتے۔ ان لوگوں کے لئے دنیا میں ذلت و رسوائی اور آخرت میں بڑا عذاب ہے۔'),
(122, 2, 115, 'اور مشرق و مغرب (پورب و پچھم) سب اللہ کے ہی ہیں سو تم جدھر بھی رخ کرو ادھر ہی اللہ کی ذات موجود ہے یقینا اللہ بڑی وسعت والا، بڑا علم والا ہے۔'),
(123, 2, 116, 'اور وہ (اہل کتاب) کہتے ہیں کہ اللہ نے کوئی بیٹا بنا لیا ہے۔ وہ (ایسی باتوں سے) پاک ہے۔ بلکہ جو کچھ آسمانوں اور زمین میں ہے وہ سب کچھ اسی کا ہے سب اسی کے مطیع و فرمانبردار ہیں۔'),
(124, 2, 117, '(وہی) آسمانوں اور زمین کا موجد ہے۔ جب وہ کسی کام کے کرنے کا فیصلہ کر لیتا ہے۔ تو اسے بس اتنا ہی کہتا ہے کہ ہو جا اور وہ ہو جاتا ہے۔'),
(125, 2, 118, 'جاہل و ناداں کہتے ہیں کہ اللہ ہم سے (براہ راست) کلام کیوں نہیں کرتا۔ یا ہمارے پاس (اس کی) کوئی نشانی کیوں نہیں آتی؟ جو لوگ ان سے پہلے گزر چکے ہیں وہ بھی اسی طرح کی (بے پرکی) باتیں کیا کرتے تھے ان سب کے دل (اور ذہن) ملتے جلتے ہیں۔ بے شک ہم نے یقین کرنے والے لوگوں کے لئے (اپنی نشانیاں) کھول کر بیان کر دی ہیں۔'),
(126, 2, 119, '(اے رسول) بے شک ہم نے آپ کو برحق بشیر و نذیر (خوشخبری دینے والا اور ڈرانے والا) بنا کر بھیجا ہے۔ اور (اس اتمام حجت کے بعد) دوزخ میں جانے والوں کے بارے میں آپ سے بازپرس نہیں کی جائے گی۔'),
(127, 2, 120, 'اے رسول یہود و نصاریٰ اس وقت تک آپ سے راضی نہیں ہوں گے جب تک آپ ان کے دین و مذہب کے پیرو نہ ہو جائیں۔ کہہ دیجئے کہ اللہ کی ہدایت ہی حقیقی ہدایت ہے (جو اس نے بتائی ہے) اور اگر آپ نے (اللہ کی طرف سے) علم آجانے کے بعد بھی ان لوگوں کی خواہشات و خیالات کی پیروی کی۔ تو پھر اللہ (کی گرفت) سے آپ کو بچانے والا نہ کوئی سرپرست ہوگا اور نہ کوئی یار و مددگار'),
(128, 2, 121, 'جن لوگوں کو ہم نے کتاب دی ہے وہ اسے اس طرح پڑھتے ہیں جس طرح پڑھنے کا حق ہے وہی لوگ ہیں جو اس پر ایمان لاتے ہیں اور جو اس کا انکار کرتے ہیں تو یہی لوگ نقصان اٹھانے والے ہیں۔'),
(129, 2, 122, 'اے بنی اسرائیل! میری وہ نعمت یاد کرو جس سے میں نے تمہیں نوازا۔ اور یہ بھی کہ تمہیں دنیا و جہان والوں پر فضیلت دی۔'),
(130, 2, 123, 'اور (قیامت کے) اس دن سے ڈرو۔ جب کوئی کسی کو کوئی فائدہ نہیں پہنچا سکے گا اور نہ کسی سے فدیہ قبول کیا جائے گا۔ اور نہ ہی سفارش کسی کو کچھ فائدہ پہنچا سکے گی اور نہ ہی (کسی اور طریقہ سے) ان کی امداد کی جائے گی۔'),
(131, 2, 124, '(اور وہ وقت یاد کرو) جب ابراہیم کا ان کے پروردگار نے چند باتوں کے ساتھ امتحان لیا۔ اور جب انہوں نے پوری کر دکھائیں ارشاد ہوا۔ میں تمہیں تمام انسانوں کا امام بنانے والا ہوں۔ انہوں نے کہا اور میری اولاد (میں سے بھی(؟ ارشاد ہوا: میرا عہدہ (امامت) ظالموں تک نہیں پہنچے گا۔'),
(132, 2, 125, 'اور (وہ وقت یاد کرو) جب ہم نے اس گھر (خانہ کعبہ) کو تمام لوگوں کے لئے مرکز اور مقام امن قرار دیا۔ اور (حکم دیا کہ) مقام ابراہیم (جہاں عبادت کے لئے کھڑے ہوئے تھے) کو (اپنی) نماز کی جگہ بناؤ اور ہم نے ابراہیم و اسماعیل کو وصیت کی (حکم دیا) کہ تم دونوں میرے (اس) گھر کو طواف کرنے والوں، اعتکاف کرنے والوں اور رکوع کرنے والوں کے لئے پاک و صاف رکھو۔'),
(133, 2, 126, 'اور (وہ وقت یاد کرو) جب ابراہیم (ع) نے کہا (دعا کی) اے میرے پروردگار اس شہر (مکہ) کو امن والا شہر بنا۔ اور اس کے رہنے والوں میں سے جو اللہ اور آخرت کے دن پر ایمان لائیں انہیں ہر قسم کے پھلوں سے روزی عطا فرما۔ ارشاد ہوا: اور جو کفر اختیار کرے گا اس کو بھی چند روزہ (زندگی میں) فائدہ اٹھانے دوں گا۔ اور پھر اسے کشاں کشاں دوزخ کے عذاب کی طرف لے جاؤں گا۔ اور وہ کیسا برا ٹھکانا ہے۔'),
(134, 2, 127, 'اور (وہ وقت بھی یاد کرو) جب ابراہیم اور اسماعیل اس گھر (خانہ کعبہ) کی بنیادیں بلند کر رہے تھے۔ (اور اس کے ساتھ ساتھ یہ دعا کرتے جاتے تھے) اے ہمارے پروردگار ہم سے (یہ عمل) قبول فرما۔ بے شک تو بڑا سننے والا اور بڑا جاننے والا ہے۔'),
(135, 2, 128, 'اے ہمارے پروردگار! ہمیں اپنا (حقیقی) مسلمان (فرمانبردار بندہ) بنائے رکھ۔ اور ہماری اولاد میں سے بھی ایک امت مسلمہ (فرمانبردار امت) قرار دے۔ اور ہمیں ہماری عبادت کے طریقے بتا۔ اور ہماری توبہ قبول فرما بے شک تو بڑا توبہ قبول کرنے والا بڑا مہربان ہے۔'),
(136, 2, 129, 'اے ہمارے پروردگار ان (امت مسلمہ) میں انہی میں سے ایک ایسا پیغمبر بھیج جو انہیں تیری آیتیں پڑھ کر سنائے اور ان کو کتاب و حکمت کی تعلیم دے اور ان کے نفوس کو پاکیزہ بنائے یقینا تو بڑا زبردست اور بڑی حکمت والا ہے۔'),
(137, 2, 130, 'اور کون ہے جو ابراہیم (ع) کی ملت (و مذہب) سے روگردانی کرے سوا اس کے جو اپنے کو احمق بنائے اور ہم نے انہیں دنیا میں منتخب کیا اور آخرت میں بھی ان کا شمار نیکوکاروں میں ہوگا۔'),
(138, 2, 131, '(یہ انتخاب اس وقت ہوا) جب ان کے پروردگار نے ان سے فرمایا: ’’مسلم‘‘ ہو جا (سر تسلیم جھکا کر فرمانبردار ہو جا)۔ تو انہوں نے کہا میں نے تمام جہانوں کے رب کے سامنے سر جھکا دیا۔'),
(139, 2, 132, 'اور ابراہیم (ع) نے اپنے بیٹوں کو اسی (ملت پر قائم رہنے) کی وصیت کی اور یعقوب (ع) نے بھی۔ اے میرے فرزند! بے شک اللہ نے تمہارے لئے ایک خاص دین (اسلام) منتخب کیا ہے سو تم ہرگز نہ مرنا مگر اس حالت میں کہ تم (حقیقی) مسلمان ہو۔'),
(140, 2, 133, '(اے یہود) کیا تم اس وقت موجود تھے جب یعقوب (ع) کی موت کا وقت آیا؟ اس وقت انہوں نے اپنے بیٹوں سے کہا (پوچھا) کہ تم میرے بعد کس کی عبادت کروگے؟ انہوں نے کہا کہ ہم آپ کے اور آپ کے آباء (و اجداد) ابراہیم (ع)، اسماعیل (ع) اور اسحاق (ع)کے واحد و یکتا معبود کی عبادت کریں گے۔ اور ہم اسی کے مسلمان (فرمانبردار) ہیں۔'),
(141, 2, 134, 'یہ ایک جماعت تھی جو گزر گئی اس کے لئے وہ ہے جو اس نے کمایا اور تمہارے لئے وہ ہے جو تم کماؤگے اور وہ جو کچھ کرتے تھے اس کے بارے میں تم سے پوچھ گچھ نہیں ہوگی۔'),
(142, 2, 135, 'اور وہ (یہود و نصاریٰ) کہتے ہیں کہ یہودی ہو جاؤ یا نصرانی تاکہ ہدایت پا جاؤ۔ (اے رسول(ص)) کہہ دیجئے کہ (نہیں) بلکہ ہم ابراہیم (ع) کی ملت پر ہیں جو نرے کھرے موحد تھے اور مشرکوں میں سے نہیں تھے۔'),
(143, 2, 136, '(مسلمانو) کہو ہم تو ایمان لائے ہیں اللہ پر۔ اور اس (قرآن) پر جو ہماری طرف نازل ہوا ہے اور اس پر جو ابراہیم (ع)، اسماعیل (ع)، اسحاق (ع)، یعقوب (ع) اور اسباط (ع) پر اتارا گیا اور ہم ان (سب کے برحق ہونے میں اور ان پر ایمان لانے) میں کسی کے درمیان تفریق نہیں کرتے اور ہم اس (اللہ) کے مسلمان (فرمانبردار) ہیں۔'),
(144, 2, 137, 'تو اگر یہ لوگ (اہل کتاب) اسی طرح ایمان لے آئیں جس طرح تم لائے ہو تو ہدایت پا گئے اور اگر روگردانی کریں تو وہ بڑی مخالفت اور ہٹ دھرمی میں پڑ گئے ہیں سو ان کے مقابلہ میں اللہ تمہارے لئے کافی ہے اور وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(145, 2, 138, 'ہم نے اللہ کا رنگ اختیار کیا ہے اور اللہ کے رنگ (دین اسلام) سے بہتر کس کا رنگ ہے اور ہم اسی کے عبادت گزار ہیں۔'),
(146, 2, 139, '(اے رسول(ص)) کہہ دیجئے کہ کیا تم ہم سے اللہ کے بارے میں جھگڑتے ہو؟ حالانکہ وہی ہمارا بھی پروردگار ہے اور تمہارا بھی پروردگار ہے اور ہمارے اعمال ہمارے لئے ہیں اور تمہارے اعمال تمہارے لئے اور ہم تو اخلاص کے ساتھ اسی کی عبادت کرنے والے ہیں۔'),
(147, 2, 140, 'کیا تم یہ کہتے ہو کہ ابراہیم (ع)، اسماعیل (ع)، اسحاق (ع)، یعقوب (ع) اور اسباط (ع) یہودی تھے؟ آپ کہیئے کیا تم زیادہ جانتے ہو یا اللہ؟ اور اس شخص سے بڑا ظالم کون ہوگا جس کے پاس اللہ کی کوئی گواہی ہو جسے وہ چھپائے اور اللہ تمہارے اعمال سے غافل نہیں ہے۔'),
(148, 2, 141, 'یہ ایک جماعت تھی جو گزر گئی اس کے لئے وہ ہے جو اس نے کمایا اور تمہارے لئے وہ ہے جو تم کماؤگے اور جو کچھ وہ کرتے تھے اس کے بارے میں تم سے پوچھ گچھ نہیں کی جائے گی۔'),
(149, 2, 142, 'عنقریب بے وقوف لوگ کہیں گے کہ ان (مسلمانوں) کو کس چیز نے روگردان کر دیا۔ اس قبلہ (بیت المقدس) سے، جس پر وہ پہلے تھے (اے پیغمبر(ص)) کہہ دیجئے اللہ ہی کے لیے ہے مشرق اور مغرب بھی۔ وہ جسے چاہتا ہے سیدھے راستہ کی طرف ہدایت کرتا ہے (اس پر لگا دیتا ہے)۔'),
(150, 2, 143, '(مسلمانو! جس طرح ہم نے تم کو صحیح قبلہ بتا دیا ہے) اسی طرح ہم نے تم کو ایک درمیانی (میانہ رو) امت بنایا ہے تاکہ تم عام لوگوں پر گواہ ہو اور پیغمبر(ص) تم پر گواہ ہو اور تم پہلے جس قبلہ پر تھے، ہم نے اسے صرف اسی لیے قبلہ بنایا تھا کہ ہم جان لیں کہ رسول(ص) کی پیروی کون کرتا ہے، اور کون پچھلے پاؤں پلٹ جاتا ہے۔ اگرچہ یہ (تحویل قبلہ) ان لوگوں کے سوا، جنہیں اللہ نے خاص ہدایت دی ہے اور سب پر بہت گراں تھی۔ اور اللہ ایسا نہیں ہے کہ تمہارے ایمان کو ضائع کر دے۔ بے شک اللہ لوگوں پر بڑی شفقت اور بڑا رحم کرنے والا ہے۔'),
(151, 2, 144, '(اے رسول(ص)) تحویل قبلہ کی خاطر) تیرا بار بار آسمان کی طرف منہ کرنا ہم دیکھ رہے ہیں۔ تو ضرور اب ہم تمہیں موڑ دیں گے اس قبلہ کی طرف جو تمہیں پسند ہے۔ بس اپنا رخ مسجد الحرام کی طرف موڑ دیجئے۔ اور (اے اہل ایمان) تم جہاں کہیں بھی ہو اپنے منہ (نماز پڑھتے وقت) اسی طرف کیا کرو۔ جن لوگوں کو (آسمانی) کتاب (تورات وغیرہ) دی گئی ہے وہ بخوبی جانتے ہیں کہ یہ (تحویل قبلہ کا فیصلہ) ان کے پروردگار کی طرف سے ہے اور یہ حق ہے۔ اور جو کچھ (یہ لوگ) کر رہے ہیں اللہ اس سے بے خبر نہیں ہے۔'),
(152, 2, 145, 'اور اگر آپ اہل کتاب کے سامنے سارے معجزے (دنیا جہان کے سارے دلائل) پیش کر دیں۔ تب بھی وہ آپ کے قبلہ کی پیروی نہیں کریں گے۔ اور نہ ہی آپ ان کے قبلہ کی پیروی کریں گے۔ اور وہ خود بھی ایک دوسرے کے قبلہ کو نہ مانتے ہیں اور نہ پیروی کرتے ہیں۔ اور اس علم کے بعد جو (منجانب اللہ) تمہارے پاس آچکا ہے اگر ان لوگوں کی خواہشات کی پیروی کروگے تو تمہارا شمار ظالموں (حد سے تجاوز کرنے والوں) میں ہو جائے گا۔'),
(153, 2, 146, 'جن لوگوں کو ہم نے کتاب (تورات وغیرہ) دی ہے وہ اس (رسول(ص)) کو اس طرح پہچانتے ہیں، جس طرح اپنی اولاد کو پہچانتے ہیں (مگر اس کے باوجود) ان کا ایک گروہ جان بوجھ کر حق کو چھپا رہا ہے۔'),
(154, 2, 147, '(اے رسول(ص)! تحویل قبلہ وغیرہ) برحق ہے اور تمہارے پروردگار کی طرف سے ہے لہٰذا ہرگز شک و شبہ کرنے والوں یا جھگڑا کرنے والوں میں سے نہ ہونا۔'),
(155, 2, 148, 'اور ہر ایک کے لئے ایک سمت ہے جدھر وہ (نماز کے لیے) منہ کرتا ہے (اے مسلمانو! تم اس نزاع کو چھوڑ دو) پس تم بڑھ چڑھ کر نیکیوں کی طرف سبقت کرو۔ تم جہاں بھی ہوگے اللہ تم سب کو (جزا و سزا کے لئے ایک جگہ) لے آئے گا۔ بے شک اللہ ہر چیز پر قادر ہے۔'),
(156, 2, 149, 'اور (اے پیغمبر(ص)) آپ جب بھی کہیں نکلیں تو (نماز کے وقت) اپنا رخ مسجد حرام کی طرف رکھیں۔ بے شک یہ (تبدیلی قبلہ) آپ کے پروردگار کی طرف سے حق ہے اور اللہ تمہارے کاموں سے غافل و بے خبر نہیں ہے۔'),
(157, 2, 150, 'اور آپ جب بھی کہیں نکلیں تو (نماز کے وقت) اپنا منہ مسجد الحرام کی طرف ہی موڑیں اور (اے مسلمانو!) تم بھی جہاں کہیں ہو (نماز کے وقت) اپنے مونہوں کو اسی (مسجد الحرام) کی طرف ہی کر لیا کرو۔ (اس حکم کی بار بار تکرار سے ایک غرض یہ ہے کہ) تاکہ لوگوں (مخالفین) کو تمہارے خلاف کوئی دلیل نہ ملے۔ سوا ان لوگوں کے لیے جو ظالم ہیں (کہ ان کی زبان تو کسی طرح بھی بند نہیں ہو سکتی)۔ تو تم ان سے نہ ڈرو بلکہ مجھ سے ڈرو (دوسری غرض یہ ہے) کہ تم پر میری نعمت تام و تمام ہو جائے (اور تیسری غرض یہ ہے) کہ شاید تم ہدایت یافتہ ہو جاؤ ۔'),
(158, 2, 151, '(اے مسلمانو!) یہ تحویل قبلہ تم پر ایسا احسان ہے جس طرح ہم نے تمہارے درمیان تم ہی میں سے ایک پیغمبر بھیجا ہے۔ جو تمہیں ہماری آیات پڑھ کر سناتا ہے اور تمہیں (بدعقیدتی، بدعملی اور بداخلاقی کی نجاست و کثافت) سے پاک کرتا ہے اور تمہیں کتاب و حکمت کی تعلیم دیتا ہے۔ اور تمہیں وہ باتیں سکھاتا ہے جو تم نہیں جانتے تھے ۔'),
(159, 2, 152, 'پس تم مجھے یاد رکھو۔ میں تمہیں یاد رکھوں گا۔ اور میرا شکر ادا کرو۔ اور میری ناشکری نہ کرو۔'),
(160, 2, 153, 'اے ایمان والو! (مصیبت کے وقت) صبر اور نماز (کے ذریعے خدا) سے مدد لیا کرو۔ بے شک اللہ تعالیٰ صبر کرنے والوں کے ساتھ ہے۔'),
(161, 2, 154, 'اور جو لوگ اللہ تعالیٰ کی راہ میں قتل کیے جاتے ہیں، انہیں مردہ نہ کہو بلکہ وہ زندہ ہیں۔ مگر تمہیں (ان کی زندگی کی حقیقت کا) شعور (سمجھ) نہیں ہے۔'),
(162, 2, 155, 'اور ہم ضرور تمہیں آزمائیں گے خوف و خطر، اور کچھ بھوک (و پیاس) اور کچھ مالوں، جانوں اور پھلوں کے نقصان کے ساتھ (یعنی ان میں سے کسی نہ کسی چیز کے ساتھ)۔ (اے رسول(ص)) خوشخبری دے دو ان صبر کرنے والوں کو۔'),
(163, 2, 156, 'کہ جب بھی ان پر کوئی مصیبت آپڑے تو وہ کہتے ہیں بے شک ہم صرف اللہ ہی کے لیے ہیں۔ اور اسی کی طرف پلٹ کر جانے والے ہیں۔'),
(164, 2, 157, 'یہی وہ (خوش نصیب) ہیں، جن پر ان کے پروردگار کی طرف سے درود (خاص عنایتیں اور نوازشیں) ہیں اور رحمت و مہربانی ہے۔ اور یہی لوگ ہدایت یافتہ ہیں۔'),
(165, 2, 158, 'بیشک صفا و مروہ نامی دو پہاڑیاں اللہ تعالیٰ کی نشانیوں میں سے ہیں۔ لہٰذا جو شخص خانہ کعبہ کا حج یا عمرہ بجا لائے، اس پر کوئی گناہ نہیں ہے کہ ان دونوں کے درمیان چکر لگائے اور جو شخص برضا و رغبت کچھ (مزید) نیکی کرے تو اللہ تعالیٰ (نیکی کا) قدر دان ہے اور خوب جاننے والا ہے۔'),
(166, 2, 159, 'بالتحقیق جو لوگ چھپاتے ہیں ہماری نازل کردہ روشن تعلیمات و ہدایت کو جبکہ ہم تمام لوگوں کے لئے انہیں کتاب میں واضح طور پر بیان کر چکے ہیں یہی وہ لوگ ہیں جن پر اللہ لعنت کرتا ہے اور تمام لعنت کرنے والے بھی لعنت کرتے ہیں۔'),
(167, 2, 160, 'مگر وہ جنہوں نے (حق پر پردہ ڈالنے سے) توبہ کی اور اپنی اصلاح کر لی۔ اور (حق بات کو) ظاہر کر دیا یہ وہ ہیں جن کی میں توبہ قبول کروں گا (انہیں معاف کر دوں گا) اور میں بڑا توبہ قبول کرنے والا اور رحم کرنے والا (مہربان) ہوں۔'),
(168, 2, 161, 'بلاشبہ جن لوگوں نے کفر اختیار کیا اور پھر اسی کفر کی حالت میں مر گئے۔ یہی وہ لوگ ہیں جن پر اللہ، فرشتوں اور تمام انسانوں کی لعنت ہے۔'),
(169, 2, 162, 'وہ اس (لعنت) میں ہمیشہ ہمیشہ رہیں گے نہ تو ان کے عذاب میں کوئی کمی کی جائے گی۔ اور نہ ہی انہیں کوئی مہلت دی جائے گی۔'),
(170, 2, 163, 'اور تمہارا خدا بس ایک ہی خدا ہے۔ اس کے سوا کوئی خدا نہیں ہے۔ وہ بڑا مہربان بہت رحم والا ہے۔'),
(171, 2, 164, 'بے شک آسمانوں اور زمینوں کی پیدائش میں اور رات دن کی آمد و رفت (الٹ پھیر) میں اور ان کشتیوں میں جو لوگوں کو نفع پہنچانے والی چیزیں لے کر سمندر میں چلتی ہیں اور (بارش کے) اس پانی میں جسے خدا نے آسمان (بلندی) سے برسایا اور پھر اس کے ذریعہ زمین کو اس کے بے جان (بے کار) ہو جانے کے بعد جاندار بنا دیا (سرسبز و شاداب بنا دیا) اور اس میں ہر قسم کے چلنے پھرنے والے (جانور) پھیلا دیئے۔ اور ہواؤں کی گردش (ہیر پھیر) میں اور ان بادلوں میں جو آسمان و زمین کے درمیان مسخر کرکے (تابع فرمان بنا کے) رکھے گئے ہیں۔ (ان سب باتوں میں) خدا کے وجود اور اس کی قدرت کی بڑی نشانیاں ہیں ان لوگوں کے لئے جو عقل و خرد سے کام لیتے ہیں۔'),
(172, 2, 165, '(ان حقائق کے باوجود) کچھ لوگ ایسے بھی ہیں جو خدا کے سوا اوروں کو اس کا ہمسر قرار دیتے ہیں اور ان سے اس طرح محبت کرتے ہیں جس طرح خدا سے کرنی چاہیے۔ مگر جو صاحب ایمان ہیں وہ سب سے بڑھ کر خدا سے محبت کرتے ہیں اور کاش یہ ظالم (اس وقت) وہ حقیقت دیکھ لیتے (سمجھ لیتے) جو عذاب دیکھتے وقت سمجھیں گے کہ ساری قوت و طاقت بس اللہ کے لئے ہی ہے اور یہ کہ اللہ سخت عذاب کرنے والا ہے۔'),
(173, 2, 166, 'اور (وہ مرحلہ کتنا کھٹن ہوگا) جب وہ (پیر) لوگ جن کی (دنیا میں) پیروی کی گئی اپنے پیرؤوں (مریدوں) سے برأت اور لاتعلقی ظاہر کریں گے اور خدا کا عذاب ان سب کی آنکھوں کے سامنے ہوگا اور سب وسائل اور تعلقات بالکل قطع ہو چکے ہوں گے۔'),
(174, 2, 167, 'اور پیروی کرنے والے (مرید) کہتے ہوں گے کہ کاش! ہمیں ایک بار (دنیا میں) واپسی کا موقع مل جاتا تو ہم بھی بالکل اسی طرح ان سے بیزاری اور بے تعلقی ظاہر کرتے جس طرح انہوں نے آج ہم سے بیزاری اور لاتعلقی ظاہر کی ہے اسی طرح خدا ان کے (برے) کاموں کو حسرت و یاس کی شکل میں دکھائے گا اور وہ کبھی دوزخ سے باہر نہیں نکل سکیں گے۔'),
(175, 2, 168, 'اے انسانو! زمین میں جو حلال اور پاکیزہ چیزیں ہیں وہ کھاؤ۔ اور شیطان کے نقش قدم پر نہ چلو۔ بلاشبہ وہ تمہارا کھلم کھلا دشمن ہے۔'),
(176, 2, 169, 'وہ تو تمہیں برائی و بے حیائی اور بدکاری کا حکم دیتا ہے اور (چاہتا ہے کہ) تم خدا کے خلاف ایسی باتیں منسوب کرو جن کا تمہیں علم نہیں ہے۔'),
(177, 2, 170, 'اور جب ان (کفار و مشرکین) سے کہا جاتا ہے کہ جو کچھ خدا نے اتارا ہے اس کی پیروی کرو تو کہتے ہیں کہ ہم تو اس کی پیروی کریں گے جس پر ہم نے اپنے باپ، دادا کو پایا ہے اگرچہ ان کے باپ، دادا بے عقل ہوں اور راہ راست پر بھی نہ ہوں (تو پھر بھی انہی کی پیروی کریں گے)۔'),
(178, 2, 171, 'اور جن لوگوں نے کفر اختیار کیا ان کی مثال دعوت حق دینے میں اس شخص (چرواہے) کی سی ہے جو ایسے (جانور) کو پکارنے میں اپنا گلہ پھاڑے (چیخ و پکار مچائے) جو چیخ و پکار کی آواز کے سوا کچھ سنتا ہی نہیں ہے۔ (یہ کافر) ایسے بہرے، گونگے اور اندھے ہیں کہ عقل سے کچھ کام ہی نہیں لیتے۔'),
(179, 2, 172, 'اے ایمان والو! جو پاک و پاکیزہ چیزیں ہم نے تمہیں دی ہیں ان میں سے کھاؤ۔ اور اللہ کا شکر کرو۔ اگر تم اس کی عبادت و پرستش کرتے ہو۔'),
(180, 2, 173, 'اس نے (چوپایوں میں سے) صرف تمہارے او پر مردار، خون، سور کا گوشت اور وہ (ذبیحہ) جسے اللہ کے سوا کسی اور کا نام لے کر ذبح کیا گیا ہو۔ حرام قرار دیا ہے۔ پس جو شخص (شدت گرسنگی سے) مجبور ہو جائے درآنحالیکہ وہ بغاوت یا سرکشی کرنے والا نہ ہو۔ تو اس پر کوئی گناہ نہیں ہے بے شک خدا بڑا بخشنے والا، بڑا مہربان ہے۔'),
(181, 2, 174, 'بالتحقیق جو لوگ کتابِ خدا میں سے ان باتوں کو چھپاتے ہیں جو خدا نے نازل کی ہیں اور ان کے عوض تھوڑی سی قیمت وصول کرتے ہیں وہ اپنے پیٹوں میں آگ کے سوا کچھ بھی نہیں بھر رہے ہیں قیامت کے دن وہ توجہ ربانی سے اس قدر محروم ہوں گے کہ خدائے تعالیٰ ان سے بات تک نہیں کرے گا۔ اور نہ ہی انہیں (گناہوں کی کثافت سے) پاک کرے گا۔ اور ان کے لیے دردناک عذاب ہے۔'),
(182, 2, 175, 'یہی وہ لوگ ہیں جنہوں نے ہدایت کے عوض گمراہی اور بخشش کے عوض عذاب کو خرید لیا۔ یہ کس قدر صبر کرنے والے ہیں دوزخ پر۔'),
(183, 2, 176, 'یہ (سب کچھ) اس لئے ہے کہ خدا نے حق اور سچائی کے ساتھ کتاب اتاری تھی مگر وہ لوگ جنہوں نے اس میں اختلاف کیا (طرح طرح کی باتیں کیں) بے شک وہ بڑی تفرقہ اندازی اور فتنہ پروری میں پڑ گئے ہیں (اور ضد میں بہت دور جا چکے ہیں)۔'),
(184, 2, 177, 'نیکی صرف یہی تو نہیں ہے کہ تم (نماز میں) اپنے منہ مشرق اور مغرب کی طرف پھیر لو۔ بلکہ (حقیقی) نیکی تو یہ ہے کہ آدمی خدا پر، روزِ آخرت پر، فرشتوں پر، (اللہ کی) کتابوں پر اور سب پیغمبروں پر ایمان لائے۔ اور اس (خدا) کی محبت میں اپنا مال (باوجودِ مال کی محبت کے) رشتہ داروں، یتیموں، مسکینوں، مسافروں، مانگنے والوں اور (غلاموں، کنیزوں اور مقروضوں کی) گردنیں چھڑانے میں صرف کرے، نماز قائم کرے، زکوٰۃ ادا کرے (دراصل) نیک لوگ تو وہ ہوتے ہیں کہ جب کوئی عہد کر لیں تو اپنا عہد و پیمان پورا کرتے ہیں اور تنگ دستی ہو یا بیماری اور تکلیف ہو یا ہنگام جنگ ہو وہ بہرحال ثابت قدم رہتے ہیں، یہی وہ لوگ ہیں جو (سچے) ہیں اور یہی متقی و پرہیزگار ہیں۔'),
(185, 2, 178, 'اے ایمان والو! ان کے بارے میں جو (ناحق قتل کر دیئے گئے ہوں) تم پر قصاص (خون کا بدلہ خون) لکھ دیا گیا ہے یعنی آزاد کے بدلے آزاد، غلام کے بدلے غلام اور عورت کے بدلے عورت۔ ہاں جس (قاتل) کے لیے اس کے (ایمانی) بھائی (مقتول کے ولی) کی جانب سے کچھ (قصاص) معاف کر دیا جائے تو اس (معاف کرنے والے) کو (چاہیے کہ) نیکی کا اتباع کرے (خون بہا کا مطالبہ کرنے میں سختی نہ کرے) اور (جسے معاف کیا گیا ہے اسے بھی چاہیے کہ) خوش اسلوبی کے ساتھ (خون بہا) ادا کرے۔ یہ تمہارے پروردگار کی طرف سے رعایت و رحمت (اور آسانی و مہربانی) ہے۔ پس اس کے بعد بھی جو زیادتی کرے تو اس کیلئے دردناک عذاب ہے'),
(186, 2, 179, 'اے صاحبانِ عقل! تمہارے لئے قصاص یعنی جان کے بدلے جان والے قانون میں زندگی ہے تاکہ تم (خون ریزی سے) بچتے رہو۔'),
(187, 2, 180, 'تم پر لکھ دیا گیا ہے کہ جب تم میں سے کسی کے سامنے موت آکھڑی ہو۔ بشرطیکہ وہ کچھ مال چھوڑے جا رہا ہو تو مناسب وصیت کر جائے اپنے ماں، باپ اور زیادہ قریبی رشتہ داروں کیلئے۔ یہ پرہیزگاروں کے ذمہ حق ہے (لازم ہے)۔'),
(188, 2, 181, 'جو شخص اس (وصیت) کو سننے کے بعد اس میں رد و بدل کرے تو اس کا گناہ انہی لوگوں پر ہوگا جو رد و بدل کریں گے۔ بے شک خدا خوب سننے والا اور خوب جاننے والا ہے۔'),
(189, 2, 182, 'اب جو شخص کسی وصیت کرنے والے کی طرف سے کسی (وارث) کی حق تلفی یا گناہ کا خوف محسوس کرے اور ان (ورثہ) کے درمیان اصلاح (سمجھوتہ) کرا دے، تو اس پر (اس ردوبدل کرنے کا) کوئی گناہ نہیں ہے۔ بے شک اللہ بڑا بخشنے والا اور رحم کرنے والا ہے۔'),
(190, 2, 183, 'اے ایمان والو! روزہ اس طرح تم پر لکھ دیا گیا ہے (فرض کر دیا گیا ہے) جس طرح تم سے پہلے والوں پر لکھ دیا گیا تھا۔ تاکہ تم پرہیزگار بن جاؤ۔'),
(191, 2, 184, 'یہ گنتی کے چند دن ہیں (اس کے باوجود) اگر تم میں سے کوئی شخص بیمار ہو جائے یا سفر میں ہو۔ تو اتنے ہی دن اور دنوں میں پورے کرے۔ اور جو اپنی پوری طاقت صرف کرکے بمشکل روزہ رکھ سکتے ہوں تو وہ فی روزہ ایک مسکین کی خوراک فدیہ ادا کریں۔ اور جو اپنی مرضی سے کچھ (زیادہ) بھلائی کرے تو وہ اس کیلئے بہتر ہے اور اگر تم (فدیہ دینے کی بجائے) روزہ رکھو تو یہ تمہارے لئے زیادہ بہتر ہے۔ اگر تم علم و واقفیت رکھتے ہو۔'),
(192, 2, 185, 'ماہ رمضان وہ (مقدس) مہینہ ہے جس میں قرآن نازل کیا گیا جو تمام انسانوں کے لئے ہدایت ہے اور اس میں راہنمائی اور حق و باطل میں امتیاز کی کھلی ہوئی نشانیاں ہیں جو اس (مہینہ) میں (وطن میں) حاضر ہو (یا جو اسے پائے) تو وہ روزے رکھے اور جو بیمار ہو یا سفر میں ہو تو اتنے ہی روزے کسی اور وقت میں (رکھے)۔ اللہ تمہاری آسانی و آسائش چاہتا ہے تمہاری تنگی و سختی نہیں چاہتا اور یہ بھی (چاہتا ہے) کہ تم (روزوں کی) تعداد مکمل کرو۔ اور اس (احسان) پر کہ اس نے تمہیں سیدھا راستہ دکھایا تم اس کی کبریائی و بڑائی کا اظہار کرو۔ تاکہ تم شکر گزار (بندے) بن جاؤ۔'),
(193, 2, 186, 'اور جب میرے بندے آپ سے میرے بارے میں سوال کریں تو (آپ کہہ دیں) میں یقینا قریب ہوں جب کوئی پکارنے والا مجھے پکارتا ہے تو میں اس کی دعا و پکار کو سنتا ہوں اور جواب دیتا ہوں۔ تو ان پر لازم ہے کہ وہ میری آواز پر لبیک کہیں اور مجھ پر ایمان لائیں (یقین رکھیں) تاکہ وہ نیک راستہ پر آجائیں۔'),
(194, 2, 187, '(اے مسلمانو) روزوں کی رات تمہارے لئے اپنی عورتوں سے مباشرت کرنا حلال کر دیا گیا ہے۔ وہ (عورتیں) تمہارا لباس ہیں اور تم ان کا لباس ہو خدا جانتا ہے کہ تم اپنی ذات کے ساتھ خیانت کرتے رہے ہو تو اس نے تمہاری توبہ قبول کی اور تمہیں معاف کر دیا۔ پس اب تم ان سے (روزوں کی راتوں میں) مباشرت کرو۔ اور جو کچھ (اولاد) خدا نے تمہارے مقدر میں لکھ دی ہے اسے طلب کرو۔ اور (رات کو) کھاؤ پیؤ۔ یہاں تک کہ صبح کا سفید ڈورا (رات کی) سیاہ ڈوری سے الگ ہو کر ظاہر ہو جائے پھر رات تک روزہ کو پورا کرو۔ اور جب تم مسجدوں میں اعتکاف (قیام) کئے ہوئے ہو تو (رات کو بھی) بیویوں سے مباشرت نہ کرو۔ یہ اللہ تعالیٰ کی مقرر کی ہوئی حدیں ہیں ان کے قریب نہ جاؤ اس طرح خدا اپنی آیتوں (احکام) کو لوگوں کے لئے بیان کرتا ہے تاکہ وہ پرہیزگار بن جائیں۔'),
(195, 2, 188, 'اور تم آپس میں ایک دوسرے کا مال غلط طریقہ سے نہ کھاؤ۔ اور نہ ہی (رشوت کے طور پر) وہ (مال) اس غرض سے حکام تک پہنچاؤ تاکہ گناہ کے طور پر لوگوں کا کچھ مال تم بھی خوردبرد کر جاؤ۔ درآنحالیکہ تم جانتے ہو۔'),
(196, 2, 189, '(اے رسول(ص)) لوگ آپ سے نئے چاندوں کے بارے میں پوچھتے ہیں (کہ وہ گھٹے بڑھتے کیوں ہیں؟) کہہ دیجئے کہ یہ لوگوں کے (دنیوی معاملات) کی تاریخیں اور حج کے لئے اوقات مقرر کرنے کا ذریعہ ہیں اور یہ کوئی اچھی بات نہیں ہے کہ تم گھروں میں پچھواڑے کی طرف سے (پھاند کر) آؤ۔ بلکہ نیکی تو یہ ہے کہ (آدمی غلط کاری سے) پرہیزگاری اختیار کرے۔ اور گھروں میں ان کے دروازوں سے داخل ہوا کرو۔ اور خدا سے ڈرو (اس کے قہر و غضب سے بچو) تاکہ تم فلاح پاؤ۔'),
(197, 2, 190, 'اور تم اللہ کی راہ میں ان لوگوں سے لڑو جو تم سے لڑیں۔ اور زیادتی نہ کرو۔ کیونکہ یقینا اللہ زیادتی کرنے والوں کو ہرگز دوست نہیں رکھتا۔'),
(198, 2, 191, 'اور ان (خواہ مخواہ لڑنے والے کفار و مشرکین) کو جہاں کہیں پاؤ۔ قتل کر دو۔ اور انہیں نکال دو جہاں (مکہ) سے انہوں نے تمہیں نکالا ہے اور فتنہ پروری قتل سے بھی بڑھ کر (بُری) ہے۔ اور مسجد الحرام میں، ان سے اس وقت تک نہ لڑو۔ جب تک وہ اس میں تم سے نہ لڑیں۔ اور اگر وہ (اس میں) تم سے لڑیں تو تم بھی انہیں قتل کرو یہی کافروں کی سزا ہے۔'),
(199, 2, 192, 'پھر اگر وہ لوگ باز آجائیں تو یقینا اللہ بڑا بخشنے والا اور بڑا رحم کرنے والا ہے۔'),
(200, 2, 193, 'اور ان سے اس وقت تک جنگ جاری رکھو جب تک تمام فتنہ و فساد ختم نہ ہو جائے اور دین (و اطاعت) صرف اللہ کے لئے نہ رہ جائے ہاں اگر وہ جنگ سے باز آجائیں تو پھر ظلم و زیادتی کرنے والوں کے سوا کسی پر زیادتی نہیں کی جا سکتی۔'),
(201, 2, 194, 'حرمت والے مہینہ کا بدلہ حرمت والا مہینہ ہے اور حرمتوں میں بھی قصاص (ادلا بدلا) ہے لہٰذا جو شخص تم پر زیادتی کرے تو تم بھی اس کے ساتھ اسی طرح زیادتی کرو جس طرح اس نے تم پر زیادتی کی ہے اور خدا (کی نافرمانی) سے ڈرو۔ اور سمجھ لو کہ اللہ یقینا پرہیزگاروں کے ساتھ ہے۔'),
(202, 2, 195, 'اور خدا کی راہ میں (مال و جان) خرچ کرو اور (اپنے آپ) کو اپنے ہاتھوں ہلاکت میں نہ ڈالو اور اچھا کام کرو۔ یقینا اللہ اچھے کام کرنے والوں سے محبت کرتا ہے۔'),
(203, 2, 196, 'اور خدا کی (خوشنودی) کے واسطے حج و عمرہ کو تمام کرو۔ اور اگر کسی مجبوری میں گھر جاؤ تو پھر جو قربانی میسر ہو وہی کرو مگر اپنے سر اس وقت تک نہ منڈواؤ جب تک قربانی اپنے ٹھکانہ تک نہ پہنچ جائے (جہاں اسے ذبح یا نحر کرنا ہے) اور جب تم میں سے کوئی شخص بیمار ہو یا اس کے سر میں کوئی تکلیف ہو اور سر منڈوالے تو اس کا فدیہ (بدلہ) روزہ، خیرات یا قربانی ہے۔ پھر جب تمہیں امن و اطمینان حاصل ہو جائے تو جو شخص (حج تمتع کا) عمرہ کرکے حج کے موقع تک لذائذ سے فائدہ اٹھائے۔ تو وہ لازمی طور پر ممکنہ قربانی کرے۔ اور جسے قربانی نہ مل سکے تو وہ تین روزے تو حج کے دوران رکھے اور سات اس وقت جب واپس جائے۔ یہ ہو جائیں گے مکمل دس (روزے) یہ (حج تمتع) اس کے لئے ہے جس کے اہل و عیال مسجد الحرام کے باشندہ نہ ہوں۔ (حدود حرم کے اندر نہ رہتے ہوں) اور اللہ (کی نافرمانی) سے ڈرو اور سمجھ لو کہ خدا سخت سزا دینے والا ہے۔'),
(204, 2, 197, 'حج کے چند خاص جانے پہچانے مقررہ مہینے ہیں۔ تو جو شخص ان (مہینوں) میں (اپنے اوپر) حج کی پابندی احرام باندھ کر فرض کر لے تو پھر حج کے دوران نہ (بیوی سے) مباشرت کرے۔ نہ (خدا کی) نافرمانی کرے اور نہ (کسی سے) لڑائی جھگڑا کرے اور تم جو کچھ نیکی کا کام کروگے تو خدا اسے جانتا ہے اور (آخرت کے سفر کے لئے) زادِ راہ مہیا کرو (اور سمجھ لو) کہ بہترین زادِ راہ تقویٰ (پرہیزگاری) ہے۔ اور اے عقل مندو! مجھ سے (میری نافرمانی سے) ڈرو۔'),
(205, 2, 198, 'اس میں تمہارے لئے کوئی مضائقہ نہیں ہے کہ تم حج کے دوران اپنے پروردگار سے فضل (رزق) کے طلب گار رہو۔ ہاں تو جب عرفات سے روانہ ہو تو مشعر الحرام (مزدلفہ) کے پاس (اس کی حدود) میں اللہ کو یاد کرو اور یاد بھی اس طرح کرو جس طرح اس نے تمہیں ہدایت کی ہے اگرچہ اس سے پہلے تم گمراہوں میں سے تھے۔'),
(206, 2, 199, 'پھر جہاں سے اور لوگ چل کھڑے ہوں تم بھی چل کھڑے ہو اور خدا سے مغفرت و بخشش طلب کرو یقینا خدا بڑا بخشنے والا، بڑا مہربان ہے۔'),
(207, 2, 200, 'جب تم اپنے مناسک حج (اور اس کے اعمال و ارکان) کو بجا لا چکو۔ تو پھر اس طرح اللہ کو یاد کرو جس طرح اپنے باپ داداؤں کو یاد کرتے تھے بلکہ اس سے بھی بڑھ کر خدا کو یاد کرو اب لوگوں میں سے کچھ تو ایسے ہیں جو کہتے ہیں اے ہمارے پروردگار! ہمیں (جو کچھ دینا ہے) اسی دنیا ہی میں دے دے ایسے شخص کا آخرت میں کوئی حصہ نہیں ہے۔'),
(208, 2, 201, 'اور کچھ ایسے ہیں جو کہتے ہیں کہ ہمارے پروردگار ہمیں دنیا میں بھی بھلائی عطا کر اور آخرت میں بھی بھلائی عطا کر اور ہمیں دوزخ کی سزا سے بچا۔'),
(209, 2, 202, 'یہ وہ لوگ ہیں جو کچھ انہوں نے کمایا ہے اس سے (دنیا و آخرت میں) ان کو حصہ ملے گا اور اللہ بہت جلد حساب بے باق کرنے والا ہے۔'),
(210, 2, 203, 'اور گنتی کے چند دنوں میں خدا کو یاد کرو پس جو جلدی کرے گا اور (منٰی سے) دو ہی دن میں چلا جائے تو اس پر کوئی گناہ نہیں ہے۔ اور جو دیر کرے (تیسرے دن تک ٹھہرا رہے) اس پر بھی کوئی گناہ نہیں ہے مگر یہ (رعایت) اس کے لئے ہے جو (شکار سے) بچتا رہا ہو اور اللہ (کی نافرمانی سے) ڈرو اور یقین رکھو کہ تم اسی کے حضور اکھٹے کئے جاؤگے (پلٹ کر جاؤ گے)۔'),
(211, 2, 204, 'اور بعض لوگ (منافقین) ایسے بھی ہیں کہ جن کی (چکنی چپڑی) باتیں تمہیں زندگانی دنیا میں بہت اچھی لگتی ہیں اور وہ اپنی دلی حالت پر خدا کو گواہ بناتے ہیں حالانکہ وہ (تمہارے) بدترین دشمن ہیں۔'),
(212, 2, 205, 'اور جب آپ کے پاس سے منہ پھیرتے ہیں (یا برسر اقتدار آتے ہیں) تو زمین میں فساد برپا کرنے کے لیے دوڑتے پھرتے ہیں اور کھیتی اور نسل کو برباد کرتے ہیں جبکہ خدا فساد کو ہرگز پسند نہیں کرتا۔'),
(213, 2, 206, 'اور جب ان سے کہا جاتا ہے کہ خدا (کی حکم عدولی) سے ڈرو تو ان کی (ظاہری) عزت اور غرورِ نفس ان کو گناہ پر ابھارتا اور اصرار کراتا ہے ایسے (بدبخت) لوگوں کے لیے دوزخ کافی ہے اور وہ بہت برا ٹھکانہ ہے۔'),
(214, 2, 207, 'اور انسانوں میں سے کچھ ایسے (انسان) بھی ہیں جو خدا کی خوشنودی حاصل کرنے کی خاطر اپنی جان بیچ ڈالتے ہیں (خطرے میں ڈال دیتے ہیں) اور اللہ (ایسے جاں نثار) بندوں پر بڑا شفیق و مہربان ہے۔'),
(215, 2, 208, 'اے ایمان والو! تم سب کے سب امن و سلامتی (والے دین) میں مکمل طور پر داخل ہو جاؤ اور شیطان کے نقش قدم پر نہ چلو کیونکہ وہ تمہارا کھلا ہوا دشمن ہے۔'),
(216, 2, 209, 'اور اگر روشن دلیلوں (واضح احکام) کے آجانے کے بعد بھی تم ڈگمگا گئے تو خوب جان لو کہ اللہ زبردست (سب پر غالب) اور بڑی حکمت والا ہے۔'),
(217, 2, 210, 'کیا یہ لوگ اس بات کا انتظار کر رہے ہیں کہ خود اللہ اور فرشتے سفید بادلوں کے سایہ میں ان کے پاس آئیں اور ہر بات کا فیصلہ ہو جائے آخر سب امور کی بازگشت تو خدا ہی کی طرف ہے۔'),
(218, 2, 211, '(اے رسول) بنو اسرائیل سے پوچھو کہ ہم نے ان کو انبیاء کے ذریعے کس قدر کھلی ہوئی نشانیاں عنایت کیں اور جو (شخص و قوم) خدا کی نعمت کو اس کے پاس آجانے کے بعد بدل ڈالے (اسے برائی میں صرف کرے) تو خدا سخت عذاب والا ہے۔'),
(219, 2, 212, 'جن لوگوں نے کفر اختیار کیا ان کے لئے زندگانی دنیا بڑی آراستہ کر دی گئی ہے۔ اور وہ ایمان والوں کا مذاق اڑاتے ہیں۔ حالانکہ جنہوں نے پرہیزگاری اختیار کی وہ قیامت کے دن ان لوگوں (کافروں) سے بہت بلند مقام پر ہوں گے اور خدا جسے چاہتا ہے بے حساب روزی عطا کرتا ہے۔'),
(220, 2, 213, '(پہلے) سب انسان ایک ہی دین (فطرت) پر تھے، (پھر جب ان میں باہمی اختلاف پیدا ہوئے) تو خدا نے انبیاء بھیجے۔ (جو نیکوکاروں کو) خوشخبری دینے والے (اور بدکاروں) کو ڈرانے والے تھے اور ان کے ساتھ برحق کتاب نازل کی (جس میں قانون تھا)۔ تاکہ لوگوں کے اختلاف کا فیصلہ کرے اور یہ اختلاف انہی لوگوں نے کیا جن کو وہ (کتاب) دی گئی تھی اور وہ بھی تب کہ جب کھلی ہوئی دلیلیں ان کے سامنے آچکی تھیں۔ محض بغاوت اور زیادتی کی بنا پر۔ تو خدا نے اپنے حکم سے ایمان والوں کو ان اختلافی باتوں میں راہِ حق کی طرف راہنمائی فرمائی۔ اور خدا جسے چاہتا ہے سیدھے راستے کی طرف راہنمائی فرماتا ہے۔'),
(221, 2, 214, 'کیا تمہارا یہ خیال ہے کہ تم یوں ہی جنت میں داخل ہو جاؤگے حالانکہ ابھی تک تمہارے سامنے تم سے پہلے گزرے ہوئے (اہل ایمان) کی سی صورتیں (اور شکلیں) آئی ہی نہیں۔ جنہیں فقر و فاقہ اور سختیوں نے گھیر لیا تھا۔ اور انہیں (تکلیف و مصائب کے) اس قدر جھٹکے دیئے گئے کہ خود رسول اور ان پر ایمان لانے والے کہہ اٹھے کہ آخر اللہ کی مدد کب آئے گی؟ آگاہ ہو جاؤ کہ اللہ کی مدد یقینا نزدیک ہی ہے۔'),
(222, 2, 215, 'لوگ آپ سے دریافت کرتے ہیں کہ وہ (راہِ خدا میں) کیا خرچ کریں؟ کہہ دیجیے! کہ تم جو مال بھی صرف کرو (اس میں تمہارے) ماں باپ کے رشتہ داروں، یتیموں، مسکینوں اور مسافروں کا حق ہے اور تم جو بھی نیک کام کرو اللہ اسے خوب جانتا ہے۔'),
(223, 2, 216, '(اے مسلمانو!) تم پر جنگ کرنا فرض کیا گیا ہے جب کہ وہ تمہیں ناگوار ہے۔ اور یہ ممکن ہے کہ جس چیز کو تم ناپسند کرتے ہو وہ تمہارے لئے اچھی ہو۔ اور یہ بھی ممکن ہے کہ جس چیز کو تم پسند کرتے ہو وہ تمہارے لئے بری ہو (اچھی نہ ہو) اصل بات یہ ہے کہ اللہ بہتر جانتا ہے تم نہیں جانتے۔'),
(224, 2, 217, '(مشرک) لوگ آپ سے حرمت والے مہینے میں جنگ کرنے کے متعلق سوال کرتے ہیں۔ کہہ دیجیے اس میں جنگ کرنا بڑا جرم ہے (مگر) خدا کی راہ سے روکنا، اور اس کا انکار کرنا اور مسجد (کی حرمت) کا انکار کرنا (اور لوگوں کو اس سے روکنا) اور اس کے باشندوں کو وہاں سے نکالنا۔ خدا کے نزدیک اس (جنگ) سے بھی بڑا جرم ہے۔ اور فتنہ گری قتل سے بھی زیادہ سنگین گناہ ہے۔ اور وہ لوگ برابر تم سے لڑتے رہیں گے یہاں تک کہ اگر ان کا بس چلے تو وہ تمہیں تمہارے دین سے ہی پلٹا دیں۔ (مگر یاد رکھو) کہ تم میں سے جو بھی اپنے دین سے پھر کر کفر کی حالت میں مرے گا۔ تو یہ وہ بدنصیب لوگ ہیں جن کے اعمال دنیا و آخرت میں رائیگاں ہو جائیں گے۔ یہی لوگ دوزخی ہیں جو ہمیشہ ہمیشہ اس میں رہیں گے۔'),
(225, 2, 218, 'بے شک جو لوگ ایمان لائے اور جنہوں نے ہجرت کی اور خدا کی راہ میں جہاد کیا یہ ہیں وہ لوگ جو خدا کی رحمت کے امیدوار ہیں اور خدا بڑا بخشنے والا مہربان ہے۔'),
(226, 2, 219, 'لوگ آپ سے شراب اور جوئے کے متعلق دریافت کرتے ہیں۔ کہہ دیجئے کہ ان میں بڑا گناہ ہے۔ اگرچہ ان میں لوگوں کے لئے کچھ فائدے بھی ہیں مگر ان کا گناہ ان کے فائدے سے بہت بڑا ہے اور لوگ آپ سے پوچھتے ہیں کہ (راہِ خدا میں) کیا خرچ کریں؟ کہہ دیجیے جو کچھ (تمہاری ضروریات سے) زیادہ ہو! اس طرح خدا تمہارے لئے اپنی آیات و ہدایات واضح کرتا ہے۔ تاکہ تم دنیا و آخرت کے بارے میں غور و فکر کرو۔'),
(227, 2, 220, 'اور لوگ آپ سے یتیموں کے متعلق پوچھتے ہیں۔ تو کہہ دیجیے کہ ان کی ہر طرح اصلاح (احوال کرنا) بہتر ہے۔ اور اگر تم ان سے مل جل کر رہو (تو اس میں کوئی مضائقہ نہیں آخر) وہ تمہارے بھائی ہی تو ہیں۔ اور اللہ بہتر جانتا ہے کہ مفسد (خرابی کرنے والا) اور اصلاح کرنے والا کون ہے اور اگر خدا چاہتا تو تمہیں مشکل و مشقت میں ڈال دیتا یقینا خدا زبردست ہے اور بڑی حکمت والا ہے۔'),
(228, 2, 221, '(اے مسلمانو!) خبردار مشرک عورتوں سے اس وقت تک نکاح نہ کرو۔ جب تک وہ ایمان نہ لے آئیں۔ (کیونکہ) ایک مؤمنہ کنیز مشرکہ (آزاد عورت) سے اچھی ہے اگرچہ (حسن و جمال میں) تمہیں کتنی ہی بھلی معلوم ہو۔ اور مشرک مردوں کے نکاح میں اس وقت تک نہ دو جب تک وہ ایمان نہ لے آئیں۔ بے شک مسلمان غلام مشرک (آزاد شوہر) سے بہتر ہے۔ اگرچہ وہ (مشرک) تمہیں کتنا ہی بھلا معلوم ہو۔ یہ لوگ تمہیں آگ کی طرف بلاتے ہیں اور خدا اپنے حکم سے (تمہیں) جنت اور مغفرت (بخشش) کی طرف بلاتا ہے۔ اور اپنے احکام کو لوگوں کے سامنے واضح طور پر بیان کرتا ہے تاکہ وہ نصیحت قبول کریں (اثر لیں)۔'),
(229, 2, 222, 'اور لوگ آپ سے حیض کے بارے میں دریافت کرتے ہیں۔ کہہ دیجیے کہ وہ گندگی ہے، لہٰذا تم ایامِ حیض میں عورتوں سے الگ رہو اور جب تک وہ پاک نہ ہو جائیں اس وقت تک ان کے نزدیک نہ جاؤ۔ پھر جب وہ طہارت کر لیں تو جدھر سے خدا نے حکم دیا ہے ان کے پاس جاؤ۔ بے شک اللہ دوست رکھتا ہے توبہ کرنے والوں کو اور طہارت کرنے والوں کو۔'),
(230, 2, 223, 'تمہاری عورتیں کھیتی ہیں۔ (اس لئے) اپنی کھیتی میں جس طرح چاہو آؤ۔ اور اپنے لئے (خدا کی بارگاہ میں) پیشگی (اعمال) بھیج دو۔ اور اللہ اور ایمان والوں کو خوشخبری (مبارکباد) دے دو۔'),
(231, 2, 224, 'اور اللہ کو اپنی قسموں کا نشانہ نہ بناؤ تاکہ تم نیکوکار، پرہیزگار بن سکو۔ اور لوگوں میں صلح کرا سکو اور اللہ سننے والا اور سب کچھ جاننے والا ہے۔'),
(232, 2, 225, 'خدا تمہاری لایعنی (غیر ارادی) قسموں پر تم سے مواخذہ نہیں کرے گا۔ لیکن جو قسمیں تم دل سے کروگے (کھاؤ گے) اس کا مواخذہ کرے گا اور خدا بڑا بخشنے والا اور بڑا برداشت کرنے والا ہے۔'),
(233, 2, 226, 'جو لوگ اپنی بیویوں سے الگ رہنے (مباشرت نہ کرنے) کی قسم کھا لیتے ہیں (ان کے لیے) چار مہینے کی مہلت ہے۔ پس اگر اس کے بعد (اپنی قسم سے) رجوع کریں تو بے شک خدا بخشنے والا، بڑا مہربان ہے۔'),
(234, 2, 227, 'اور وہ اگر طلاق دینے کا ہی عزم بالجزم کر لیں تو بے شک خدا (سب کچھ) سننے اور جاننے والا ہے۔'),
(235, 2, 228, 'اور جن عورتوں کو طلاق دی جائے تو وہ اپنے آپ کو (عقد ثانی) سے روکیں۔ تین مرتبہ ایام ماہواری سے پاک ہونے تک۔ اور اگر وہ خدا اور روزِ جزاء پر ایمان رکھتی ہیں تو ان کے لئے یہ جائز نہیں ہے کہ اسے چھپائیں۔ جوکہ خدا نے ان کے رحموں (شکموں) میں پیدا کیا ہے۔ اور اس دوران ان کے شوہر اگر تعلقات بحال کرنا چاہیں تو وہ انہیں واپس بلانے (اور اپنی زوجیت میں رکھنے کے) زیادہ حقدار ہیں اور اس دوران دستورِ شریعت کے مطابق عورتوں کے کچھ حقوق ہیں جس طرح کہ مردوں کے حقوق ہیں ہاں البتہ مردوں کو ان پر ایک درجہ فوقیت ہے۔ خدا زبردست (حاکم) اور حکمت والا ہے۔'),
(236, 2, 229, 'اور طلاق (رجعی) دو ہی مرتبہ ہے اس کے بعد یا تو اچھے طریقہ سے روک لیا جائے گا یا بھلائی کے ساتھ رخصت کر دیا جائے گا۔ اور تمہارے لئے جائز نہیں ہے کہ جو کچھ انہیں (بطورِ زر مہر اور ہدیہ و تحفہ) دے چکے ہو اس میں سے کچھ واپس لو مگر یہ کہ ان دونوں (میاں بیوی) کو اندیشہ ہو کہ وہ خدا کی قائم کردہ حدود کو قائم نہیں رکھ سکیں گے تو پھر (اے مسلمانو!) تمہیں بھی یہ خوف ہو کہ وہ حدودِ الٰہیہ کو قائم نہیں رکھ سکیں گے تو (اس صورت میں) عورت کو (بطورِ فدیہ خلع) کچھ معاوضہ دینا چاہیے (اور دے کر اپنی جان چھڑانا چاہیے) تو اس میں دونوں پر کوئی گناہ نہیں ہے۔ یہ خدا کی مقرر کردہ حدیں ہیں ان سے آگے نہ بڑھو۔ اور جو لوگ خدا کی مقرر کی ہوئی حدوں سے آگے بڑھتے ہیں وہی لوگ ظالم ہیں۔'),
(237, 2, 230, 'اب اگر (تیسری بار) طلاق (بائن) دے تو اس کے بعد (یہ عورت) اس مرد کے لئے اس وقت تک حلال نہ ہوگی جب تک کسی دوسرے شخص سے شادی نہ کرے۔ اب جب کہ وہ (دوسرا شوہر) اسے طلاق دے دے اور ان دونوں سابقہ میاں بیوی کا خیال ہو کہ وہ حدودِ الٰہیہ کو برقرار رکھ سکیں گے تو ان کے لئے کوئی گناہ نہیں ہے کہ وہ آپس میں (دوبارہ) شادی کر لیں۔ یہ خدا کی مقرر کردہ حدیں ہیں جنہیں وہ صاحبانِ علم کے لئے واضح طور پر بیان کرتا ہے۔'),
(238, 2, 231, 'اور جب تم عورتوں کو طلاق دو اور وہ اپنی میعاد (عدت کے خاتمہ) کے قریب پہنچ جائیں تو یا تو انہیں اچھے طریقے سے (اپنے پاس) رکھو۔ یا اچھے طریقے سے انہیں رخصت کر دو اور انہیں ضرر و زیاں پہنچانے اور زیادتی کرنے کیلئے نہ روکو اور جو ایسا کرے گا وہ اپنے نفس پر ظلم کرے گا۔ اور خدا کے آیات و احکام کا ہنسی مذاق نہ بناؤ اور خدا کے اس انعام و احسان کو یاد کرو جو اس نے تم پر کیا ہے اور جو اس نے کتاب و حکمت تم پر نازل کی ہے۔ اس کے ذریعہ سے تمہیں نصیحت کرتا ہے اور اللہ (کی نافرمانی) سے ڈرو اور خوب جان لو کہ خدا ہر شی کا جاننے والا ہے۔'),
(239, 2, 232, 'اور جب تم عورتوں کو طلاق دو اور جب وہ اپنی (عدّت کی) مدت پوری کر لیں تو انہیں اپنے شوہروں سے نکاح کرنے سے نہ روکو۔ جب کہ وہ مناسب طریقے پر (شریعت کے مطابق) ایک دوسرے (نئے یا پرانے) سے نکاح کرنے پر راضی ہو جائیں۔ اس (حکم) کے ذریعہ سے اس شخص کو نصیحت کی جاتی ہے (اور وہی اسے قبول کرے گا) جو خدا اور جزاء پر ایمان رکھتا ہے۔ یہ (ان احکام پر عمل کرنا)۔ تمہارے لئے زیادہ تزکیہ و طہارت کا باعث ہے اللہ بہتر جانتا ہے۔'),
(240, 2, 233, 'اور (طلاق کے بعد) جو شخص (اپنی اولاد کو) پوری مدت تک دودھ پلوانا چاہے تو مائیں اپنی اولاد کو کامل دو برس تک دودھ پلائیں گی۔ اور بچہ کے باپ پر (اس دوران) ان (ماؤں) کا کھانا اور کپڑا مناسب و معروف طریقہ پر لازم ہوگا۔ کسی بھی شخص کو اس کی وسعت و آسائش سے زیادہ تکلیف نہیں دی جا سکتی۔ نہ تو ماں کو نقصان پہنچایا جائے اس کے بچہ کی وجہ سے اور نہ ہی باپ کو زیاں پہنچایا جائے اس کے بچہ کے سبب سے اور (اگر باپ نہ ہو تو پھر) وارث پر (دودھ پلانے اور نان و نفقہ کی) یہی ذمہ داری ہے۔ اور اگر دونوں (ماں باپ اس مدت سے پہلے) باہمی مشورہ و رضامندی سے بچہ کا دودھ چھڑانا چاہیں تو ان پر کوئی گناہ نہیں ہے۔ اور اگر تم اپنی اولاد کو (کسی دایہ سے) دودھ پلوانا چاہو تو اس میں کوئی جرم نہیں ہے بشرطیکہ جو مناسب طریقہ سے دینا ٹھہرایا ہے (ان کے) حوالے کر دو۔ اور خدا (کی نافرمانی سے) ڈرو اور خوب جان لو کہ تم جو کچھ کرتے ہو۔ اللہ اسے خوب دیکھ رہا ہے۔'),
(241, 2, 234, 'اور تم میں سے جو لو گ بیویاں چھوڑ کر مر جائیں تو وہ (بیویاں) چار مہینے دس دن تک اپنے کو (عقد ثانی سے) روکیں۔ اور جب یہ مدت (عدت) پوری کر لیں تو وہ اپنے حق میں جو مناسب کام (فیصلہ) کریں اس میں تم پر کوئی گناہ نہیں ہے۔ اور تم جو کچھ کرتے ہو خدا اس سے خوب باخبر ہے۔'),
(242, 2, 235, 'اگر تم (عدت کے دوران) عورتوں کی اشارہ و کنایہ میں خواستگاری کرو (پیغام نکاح دو) یا اسے اپنے دلوں میں چھپائے رکھو تو اس میں تم پر کچھ گناہ نہیں ہے۔ اللہ کو معلوم ہے کہ تم جلد ان کو یاد کروگے (تو بے شک کرو) مگر ان سے کوئی خفیہ قول و قرار نہ کرو۔ سوا اس کے کہ مناسب طریقہ سے کوئی بات کرو۔ (اشارہ و کنایہ سے) اور جب تک مقررہ مدت پوری نہ ہو جائے۔ اس وقت تک عقد نکاح کا ارادہ بھی نہ کرو۔ اور خوب جان لو کہ خدا تمہارے دلوں کے اندر کی بات کو بھی خوب جانتا ہے۔ لہٰذا اس سے ڈرتے رہو اور خوب جان لو کہ خدا بخشنے والا اور حلیم و بردبار ہے۔'),
(243, 2, 236, 'اگر تم (اپنی) عورتوں کو ہاتھ لگانے (خلوت صحیحہ کرنے) اور حق مہر مقرر کرنے سے پہلے ہی طلاق دے دو تو اس میں تم پر کوئی گناہ نہیں ہے ہاں البتہ (بطور خرچہ) انہیں کچھ مال و متاع دے دو۔ مالدار اپنی حیثیت کے مطابق اور غریب و نادار اپنے مقدور کے موافق مناسب متاع (خرچہ) دے۔ یہ نیکوکار لوگوں کے ذمہ ایک حق ہے۔'),
(244, 2, 237, 'اور اگر تم عورتوں کو ہاتھ لگانے سے پہلے طلاق دے دو۔ لیکن تم ان کا کچھ حق مہر معین کر چکے ہو۔ تو (اس صورت میں) اس مقررہ حق مہر کا آدھا لازم ہوگا۔ مگر یہ کہ وہ (عورتیں) خود معاف کر دیں یا وہ (ولی) معاف کر دے جس کے ہاتھ میں نکاح کا معاملہ ہے۔ اور اگر تم درگزر کرو (معاف کر دو) تو یہ پرہیزگاری کے زیادہ قریب ہے۔ اور باہمی معاملات میں احسان اور بھلائی کو نہ بھولو۔ بے شک تم جو کچھ کر رہے ہو خدا اسے خوب دیکھ رہا ہے۔'),
(245, 2, 238, 'اور تم تمام نمازوں کی پابندی کرو۔ خصوصاً نماز وسطی کی اور خدا کے سامنے قنوت پڑھتے ہوئے (ادب و نیاز سے) کھڑے ہو۔'),
(246, 2, 239, 'اور اگر تم خوف (کی حالت) میں ہو تو پھر پیدل ہو یا سوار (جس طرح بھی ممکن ہو نمازپڑھ لو) پھر جب تمہیں امن و اطمینان ہو جائے تو خدا کو اس طرح یاد کرو جس طرح اس نے تمہیں سکھا دیا ہے جو تم نہیں جانتے تھے۔'),
(247, 2, 240, 'اور جو تم میں سے اپنی بیویاں چھوڑ کر دنیا سے جا رہے ہوں تو ان کو اپنی بیویوں کے حق میں وصیت کرنا چاہیے کہ ان کو سال بھر تک نان و نفقہ (خرچہ) دیا جاتا رہے اور گھر سے بھی نہ نکالا جائے۔ ہاں اگر وہ خود نکل جائیں تو اور بات ہے اور اپنے بارے میں جو مناسب فیصلہ کریں اس میں تم پر کوئی گناہ نہیں ہے اور خدا زبردست اور حکمت والا ہے۔'),
(248, 2, 241, 'اور جن عورتوں کو (مہر مقرر کئے اور صحبت کئے بغیر) طلاق دی گئی ہے۔ انہیں مناسب مال و متاع (خرچہ) دینا لازم ہے۔ یہ پرہیزگاروں کے ذمہ ایک حق ہے۔'),
(249, 2, 242, 'اسی طرح خدا تمہارے لئے اپنے آیات و احکام واضح طور پر بیان کرتا ہے تاکہ تم سمجھ بوجھ سے کام لو۔'),
(250, 2, 243, 'کیا تم نے ان لوگوں کو نہیں دیکھا جو موت کے ڈر سے ہزاروں کی تعداد میں اپنے گھروں سے نکل پڑے۔ خدا نے ان سے کہا: مر جاؤ (پس وہ سب کے سب مر گئے) پھر انہیں زندہ کیا۔ بے شک خدا لوگوں پر بڑا لطف و کرم کرنے والا ہے۔ لیکن اکثر لوگ شکر ادا نہیں کرتے۔'),
(251, 2, 244, 'اور خدا کی راہ میں جنگ کرو اور خوب جانتے رہو کہ خدا (سب کچھ) سننے اور جاننے والا ہے۔'),
(252, 2, 245, 'ہے کوئی ایسا جو خدا کو قرض حسنہ دے تاکہ خدا اسے کئی گُنا کرکے واپس کرے۔ خدا ہی تنگی کرتا ہے اور وہی کشادگی دیتا ہے۔ اور (تم سب) اسی کی طرف پلٹائے جاؤگے۔'),
(253, 2, 246, 'کیا تم نے جناب موسیٰ کے بعد بنی اسرائیل کے سرداروں کو نہیں دیکھا کہ جب انہوں نے نبی(ص) سے کہا کہ ہمارے لئے ایک بادشاہ مقرر کر دیجئے تاکہ ہم (اس کی زیر قیادت) راہِ خدا میں جنگ کریں۔ اس نبی نے فرمایا کہیں ایسا نہ ہو کہ جب جنگ تم پر واجب ہو جائے۔ تو پھر تم جنگ نہ کرو۔ انہوں نے کہا کہ ہم کیونکر جنگ نہیں کریں گے؟ جبکہ ہمیں اپنے گھروں اور بال بچوں سے باہر نکال دیا گیا ہے۔ مگر جب جنگ ان پر واجب قرار دی گئی تو ان کے تھوڑے سے آدمیوں کے سوا باقی سب کے سب منحرف ہوگئے (پیٹھ پھیر گئے) اور خدا ظالموں کو خوب جانتا ہے۔'),
(254, 2, 247, 'اور ان کے پیغمبر نے ان سے کہا کہ بے شک خدا نے تمہارے لئے طالوت کو بادشاہ مقرر کیا ہے۔ ان لوگو ں نے کہا، اس کو ہم پر کس طرح حکومت کرنے کا حق ہو سکتا ہے۔ اس سے تو ہم حکومت کرنے کے زیادہ حقدار ہیں اسے تو مالی وسعت ملی ہی نہیں ہے (کوئی بڑا مالدار آدمی نہیں ہے)۔ نبی نے جواب دیا کہ اللہ نے اسے اس لئے تم پر فضیلت اور ترجیح دی ہے کہ اسے علم اور جسمانی طاقت میں زیادتی عطا کی ہے اور اللہ جسے چاہتا ہے۔ اپنا ملک عطا کرتا ہے۔ (کیونکہ) اللہ بڑی وسعت والا اور بڑا علم والا ہے۔'),
(255, 2, 248, 'اور ان کے نبی نے ان سے یہ بھی کہا کہ اس کے بادشاہ ہونے کی نشانی یہ ہے کہ تمہارے پاس وہ صندوق آجائے گا۔ جس میں تمہارے پروردگار کی جانب سے سکون کا سامان ہو گا اور موسیٰ (ع) و ہارون (ع) کے خاندان کے بچے کچھے تبرکات و متروکات بھی اور اسے فرشتے اٹھائے ہوئے ہوں گے اس میں بے شک تمہارے لئے (خدا کی قدرت کی) بڑی نشانی ہے۔ اگر تم ایماندار ہو۔'),
(256, 2, 249, 'اب جو طالوت فوجیں لے کر چلے تو (اپنے ہمراہیوں سے) کہا کہ خدا ایک نہر کے ساتھ تمہاری آزمائش کرنے والا ہے (دیکھو) جو شخص اس سے پانی پی لے گا اس کا مجھ سے کوئی واسطہ نہ ہوگا اور جو اسے چکھے گا بھی نہیں اس کا مجھ سے تعلق ہوگا۔ مگر یہ کہ اپنے ہاتھ سے ایک چُلو بھر لے۔ (انجام کار وقت آنے پر) تھوڑے لوگوں کے سوا باقی سب نے اس (نہر) سے پانی پی لیا۔ پس جب وہ اور ان کے ساتھ ایمان لانے والے آگے بڑھے (نہر پار کی) تو (پانی پینے والے) کہنے لگے کہ آج ہم میں جالوت اور اس کی افواج سے لڑنے کی طاقت نہیں ہے (مگر) جن لوگوں کو خدا کو منہ دکھانے کا یقین تھا کہنے لگے خدا کے حکم سے کئی چھوٹی جماعتیں بڑی جماعتوں پر غالب آجاتی ہیں اور خدا صبر کرنے والوں کے ساتھ ہے۔'),
(257, 2, 250, '(غرضیکہ) یہ لوگ جالوت اور اس کی افواج کے مقابلہ کے لئے باہر نکلے۔ تو (یوں) دعا کی: اے پروردگار! ہم پر صبر انڈیل دے (بے پناہ صبر عطا کر) اور (میدانِ جنگ میں) ہمارے قدم جمائے رکھ اور کافروں کے مقابلہ میں ہماری مدد و نصرت فرما۔'),
(258, 2, 251, 'پس ان لوگوں نے خدا کے حکم سے ان لوگوں (دشمنوں) کو شکست دے دی۔ اور داؤد (ع) نے جالوت کو قتل کر دیا۔ اور خدا نے انہیں سلطنت و حکمت عطا فرمائی۔ اور جس جس چیز کا چاہا علم عطا فرمایا۔ اور اگر خدا ایک گروہ کا دوسرے گروہ کے ذریعہ سے دفاع نہ کرے تو زمین تباہ و برباد ہو جائے۔ مگر خدا تمام جہانوں پر بڑا لطف و کرم کرنے والا ہے۔'),
(259, 2, 252, 'یہ آیاتِ الٰہیہ (قدرتِ خدا کی نشانیاں) ہیں جنہیں ہم سچائی کے ساتھ بیان کرتے ہیں اور بے شک و شبہ آپ رسولوں میں سے ہیں۔'),
(260, 2, 253, 'یہ سب پیغمبر ہیں جن میں سے ہم نے بعض کو بعض پر فضیلت دی ہے۔ ان میں سے بعض وہ ہیں جن سے خدا نے کلام کیا اور ان میں سے بعض کے درجے بلند کئے۔ اور ہم نے عیسیٰ بن مریم کو کئی معجزے دیئے۔ اور روح القدس کے ذریعہ سے ان کی تائید کی اور اگر خدا (جبراً) چاہتا تو ان (پیغمبروں) کے بعد آنے والے لوگ اپنے پاس معجزے آجانے کے بعد آپس میں نہ لڑتے۔ لیکن انہوں نے آپس میں اختلاف کیا۔ پھر ان میں سے کچھ ایمان لائے اور بعض نے کفر اختیار کیا۔ اور اگر خدا (جبراً) چاہتا تو یہ لوگ آپس میں نہ لڑتے۔ مگر خدا جو چاہتا ہے وہی کرتا ہے۔'),
(261, 2, 254, 'اے ایمان والو! جو کچھ ہم نے تمہیں رزق دیا ہے اس میں سے کچھ (راہِ خدا میں) خرچ کرو۔ قبل اس کے کہ وہ دن (قیامت) آجائے کہ جس میں نہ سودے بازی ہوگی، نہ دوستی (کام آئے گی) اور نہ ہی سفارش۔ اور جو کافر ہیں وہی دراصل ظالم ہیں۔'),
(262, 2, 255, 'اللہ (ہی وہ ذات) ہے۔ جس کے علاوہ کوئی الٰہ نہیں ہے۔ زندہ (جاوید) ہے۔ جو (ساری کائنات کا) بندوبست کرنے والا ہے۔ اسے نہ اونگھ آتی ہے اور نہ نیند۔ جو کچھ آسمانوں اور جو کچھ زمین میں ہے وہ سب اسی کا ہے۔ کون ہے جو اس کی (پیشگی) اجازت کے بغیر اس کی بارگاہ میں (کسی کی) سفارش کرے؟ جو کچھ بندوں کے سامنے ہے وہ اسے بھی جانتا ہے۔ اور جو کچھ ان کے پیچھے ہے اسے بھی جانتا ہے۔ اور وہ (بندے) اس کے علم میں سے کسی چیز (ذرا) پر بھی احاطہ نہیں کر سکتے۔ مگر جس قدر وہ چاہے۔ اس کی کرسی (علم و اقتدار) آسمان و زمین کو گھیرے ہوئے ہے۔ ان دونوں (آسمان و زمین) کی نگہداشت اس پر گراں نہیں گزرتی وہ برتر ہے اور بڑی عظمت والا ہے۔'),
(263, 2, 256, 'دین کے معاملہ میں کوئی زبردستی نہیں ہے۔ ہدایت گمراہی سے الگ واضح ہو چکی ہے۔ اب جو شخص طاغوت (شیطان اور ہر باطل قوت) کا انکار کرے اور خدا پر ایمان لائے اس نے یقینا مضبوط رسی تھام لی ہے۔ جو کبھی ٹوٹنے والی نہیں ہے۔ اور خدا (سب کچھ) سننے والا اور خوب جاننے والا ہے۔'),
(264, 2, 257, 'اللہ ان لوگوں کا سرپرست ہے جو ایمان لائے وہ انہیں (گمراہی کے) اندھیروں سے (ہدایت کی) روشنی کی طرف نکالتا ہے۔ اور جن لوگوں نے کفر اختیار کیا ان کے سرپرست طاغوت (شیطان اور باطل کی قوتیں) ہیں جو انہیں (ایمان کی) روشنی سے نکال کر (کفر کے) اندھیروں کی طرف لے جاتے ہیں۔ یہی دوزخی لوگ ہیں جو اس میں ہمیشہ ہمیشہ رہیں گے۔'),
(265, 2, 258, 'کیا تم نے اس شخص کو نہیں دیکھا (اس کے حال پر غور نہیں کیا) جس نے جناب ابراہیم سے ان کے پروردگار کے بارے میں صرف اس بنا پر بحث و تکرار کی تھی کہ خدا نے اسے سلطنت دے رکھی تھی۔ جب ابراہیم نے کہا تھا کہ میرا پروردگار وہ ہے جو جلاتا بھی ہے اور مارتا بھی ہے۔ اس (شخص) نے کہا میں بھی جلاتا اور مارتا ہوں (اس پر) ابراہیم نے کہا میرا خدا سورج کو مشرق سے نکالتا ہے تو اسے مغرب سے نکال۔ اس پر کافر مبہوت (ہکا بکا) ہوگیا۔ خدا ظالموں کو ہدایت نہیں کرتا (ان کو منزل مقصود تک نہیں پہنچاتا)۔'),
(266, 2, 259, 'یا اسی طرح تم نے وہ آدمی نہیں دیکھا (اس کے حال پر غور نہیں کیا) جو ایک ایسے گاؤں سے گزرا جس کے در و دیوار کا ملبہ اس کی گری ہوئی چھتوں پر گر پڑا تھا۔ (اپنی چھتوں پر اوندھی گری پڑی تھی)۔ اس (آدمی) نے (یہ منظر دیکھ کر) کہا اللہ اس گاؤں (اس کے رہنے والوں) کو کیوں کر اس کی موت کے بعد زندہ کرے گا؟ اس پر اللہ نے اسے سو سال تک کے لئے موت دے دی۔ پھر اسے زندہ کیا۔ اور کہا (پوچھا) تم کتنی دیر اس طرح پڑے رہے ہو؟ کہا ایک دن یا دن کا کچھ حصہ! ۔ فرمایا بلکہ تم سو سال پڑے رہے ہو۔ ذرا اپنے کھانے پینے کی چیزوں کو دیکھو کہ ذرا بھی خراب نہیں ہوئی ہیں۔ (دوسری طرف) ذرا اپنے گدھے کو دیکھو (کہ کس طرح گل سڑ گیا ہے؟) یہ (سب کچھ) اس لئے کیا ہے کہ تمہیں لوگوں کے لئے (اپنی قدرت کی) نشانی بناؤں۔ اور (گدھے) کی سڑی گلی ہڈیوں کو ہم کس طرح انہیں (جوڑ جاڑ کر) کھڑا کرتے ہیں اور پھر کس طرح ان پر گوشت چڑھاتے ہیں۔ (اور اسے زندہ کرتے ہیں) جب اس (آدمی) پر یہ بات واضح ہوگئی۔ تو (بے ساختہ) کہا میں یقین سے جانتا ہوں کہ اللہ ہر شئی پر قادر ہے۔'),
(267, 2, 260, '(اے رسول(ص)) وہ واقعہ یاد کرو۔ جب ابراہیم (ع) نے کہا: اے پروردگار! مجھے دکھلا کہ تو مُردوں کو کیوں کر زندہ کرتا ہے؟ خدا نے فرمایا: کیا تمہارا (اس پر) ایمان نہیں ہے؟ کہا: کیوں نہیں؟ (ایمان تو ہے) مگر چاہتا ہوں کہ (آنکھوں سے دیکھوں) تاکہ (دل) کو اطمینان ہو جائے (اور اسے قرار آئے) فرمایا: چار پرندے لو۔ اور انہیں اپنے پاس اکٹھا کرو اور اپنے سے مانوس بناؤ۔ پھر (انہیں ٹکڑے ٹکڑے کرکے) ہر پہاڑ پر ایک ٹکڑا رکھ دو۔ پھر انہیں آواز دو۔ تو وہ دوڑتے ہوئے تمہارے پاس آئیں گے اور خوب جان لو کہ خدا زبردست اور حکمت والا ہے۔'),
(268, 2, 261, 'جو لوگ خدا کی راہ میں اپنے مال خرچ کرتے ہیں ان کی مثال اس ایک دانے کے مثل ہے جس نے (اپنے بوئے جانے کے بعد) سات بالیاں اگائیں اور ہر بالی میں سو دانے ہیں اور خدا جس کے لئے چاہتا ہے اور بڑھا دیتا ہے (دونا کر دیتا ہے) خدا بڑی وسعت والا اور بڑا جاننے والا ہے۔'),
(269, 2, 262, 'جو لوگ راہِ خدا میں اپنے مال خرچ کرتے ہیں اور خرچ کرنے کے بعد نہ احسان جتاتے ہیں اور نہ ایذا پہنچاتے ہیں ان کا اجر و ثواب ان کے پروردگار کے یہاں ہے نہ انہیں کوئی خوف ہے اور نہ وہ غمگین ہوں گے۔'),
(270, 2, 263, '(سائل کو) اچھائی سے جواب دینا اور بخش دینا۔ اس صدقہ و خیرات سے بہتر ہے۔ جس کے بعد (سائل کو) ایذا پہنچائی جائے۔ اور اللہ بے نیاز ہے اور بڑا بردبار ہے۔'),
(271, 2, 264, 'اے ایمان والو! (سائل کو) احسان جتا کر اور ایذا پہنچا کر اپنے صدقہ و خیرات کو اس شخص کی طرح اکارت و برباد نہ کرو جو محض لوگوں کو دکھانے کیلئے اپنا مال خرچ کرتا ہے اور خدا اور روزِ آخرت پر ایمان نہیں رکھتا۔ اس کی (خیرات کی) مثال اس چکنی چٹان کی سی ہے۔ جس پر کچھ خاک پڑی ہوئی ہو اور اس پر بڑے دانے والی زور کی بارش برسے۔ اور (خاک کو بہا کر لے جائے) اور اس (چٹان) کو صاف چکنا چھوڑ جائے۔ اسی طرح یہ (ریاکار) لوگ جو کچھ (صدقہ و خیرات وغیرہ) کرتے ہیں اس سے کچھ بھی ان کے ہاتھ نہیں آئے گا اور خدا کافر لوگوں کو ہدایت نہیں کرتا (انہیں منزل مقصود تک نہیں پہنچاتا)۔'),
(272, 2, 265, 'اور جو لوگ خدا کی خوشنودی کے لئے دل کے پورے ثبات و قرار کے ساتھ اپنے مال (راہِ خدا میں) خرچ کرتے ہیں ان کی مثال اس (ہرے بھرے اور گھنے) باغ کی مانند ہے جو کسی اونچی جگہ پر ہو۔ جس پر بڑی بڑی بوندوں والی زور کی بارش برسے اور وہ دوگنا پھل دے۔ اور اگر اس پر بڑے قطروں والا زور کا مینہ نہ برسے تو پھر ہلکی پھلکی بارش ہی کافی ہے۔ اور تم لوگ جو کچھ کرتے ہو خدا اسے دیکھ رہا ہے۔'),
(273, 2, 266, 'تم میں سے کوئی بھی یہ بات پسند کرے گا کہ اس کے پاس کھجوروں، انگوروں کا ہرا بھرا گھنا باغ ہو جس کے نیچے نہریں جاری ہوں اور اس کے لئے اس میں ہر قسم کے پھل اور میوے موجود ہوں اور اسے بڑھاپا گھیر لے۔ جبکہ اس کی (چھوٹی چھوٹی) کمزور و ناتواں اولاد ہو اور اچانک اس (باغ) کو ایک ایسا بگولہ اپنی لپیٹ میں لے لے جس میں آگ ہو جس سے وہ باغ جل کر رہ جائے۔ اس طرح خدا تمہارے لئے اپنے احکام کھول کر بیان کرتا ہے تاکہ تم غور و فکر کرو۔'),
(274, 2, 267, 'اے ایمان والو! اپنی کمائی میں سے پاک و پاکیزہ چیزوں سے اور ان چیزوں سے جو ہم نے زمین سے تمہارے لئے برآمد کی ہیں (خدا کی راہ میں) خرچ کرو۔ اور اس میں سے بری اور خراب چیز کے خیرات کرنے کا ارادہ بھی نہ کرو۔ جبکہ خود تم ایسی چیز کے لینے پر (خوشی سے) آمادہ نہیں ہو۔ مگر یہ کہ مروت کی وجہ سے چشم پوشی سے کام لو۔ اور خوب جان لو کہ خدا بے نیاز ہے اور حمد و ستائش کے قابل ہے۔'),
(275, 2, 268, 'شیطان تمہیں مفلسی اور تنگدستی سے ڈراتا ہے۔ اور بے حیائی و غلط کاری کا تم کو حکم (ترغیب) دیتا ہے۔ اور اللہ تم سے اپنی بخشش اور اپنے فضل و کرم کا وعدہ کرتا ہے۔ خدا بڑی وسعت والا اور بڑا جاننے والا ہے۔'),
(276, 2, 269, 'جسے وہ چاہتا ہے حکمت و دانائی عطا فرماتا ہے اور جسے (منجانب اللہ) حکمت عطا ہوئی، بے شک اسے درحقیقت خیر کثیر (بڑی دولت) مل گئی۔ عقلمندوں کے سوا کوئی نصیحت قبول نہیں کرتا۔'),
(277, 2, 270, 'اور تم جو کچھ بھی خرچ کرتے ہو یا منت مانتے ہو تو یقینا اللہ اسے جانتا ہے اور ظالموں کا کوئی مددگار نہیں ہے۔'),
(278, 2, 271, 'اگر تم اپنی خیرات علانیہ طور پر دو تو بھی اچھا ہے اور اگر اسے پوشیدہ رکھو اور حاجتمندوں کو دو تو وہ تمہارے لئے زیادہ بہتر ہے اور تمہارے کچھ گناہوں اور برائیوں کا کفارہ ہو جائے گا (انہیں مٹا دے گا)۔ تم جو کچھ کرتے ہو اللہ اس سے پوری طرح باخبر ہے۔'),
(279, 2, 272, '(اے رسول!) ان لوگوں کو منزل مقصود تک پہنچانے کی آپ پر ذمہ داری نہیں ہے (تمہارا فرض صرف راستہ دکھانا ہے)۔ اللہ جسے چاہتا ہے منزل مقصود تک پہنچاتا ہے اور تم جو کچھ مال خرچ کرتے ہو۔ تو وہ تمہارے اپنے (فائدہ) کے لئے ہے اور تم جو بھی خرچ کرتے ہو وہ اللہ کی خوشنودی کے لئے کرتے ہو۔ اور تم جو مال و دولت خیرات میں دوگے۔ وہ پورا پورا تمہیں ادا کر دیا جائے گا اور تم پر ہرگز ظلم نہیں کیا جائے گا۔'),
(280, 2, 273, '(یہ خیرات) خاص طور پر ان حاجتمندوں کے لئے ہے جو اللہ کی راہ میں اس طرح گھر گئے ہیں کہ روئے زمین پر سفر نہیں کر سکتے، ناواقف انہیں خود داری برتنے اور رکھ رکھاؤ کی وجہ سے مالدار سمجھتا ہے۔ مگر تم انہیں ان کے چہروں سے پہچان سکتے ہو۔ وہ لوگوں سے لپٹ کر اور ان کے پیچھے پڑ کر سوال نہیں کرتے۔ اور تم جو کچھ مال و دولت (راہِ خدا میں) خرچ کروگے بے شک اللہ اسے جانتا ہے۔'),
(281, 2, 274, 'جو لوگ اپنے مال رات اور دن میں خفیہ و علانیہ (راہِ خدا میں) خرچ کرتے ہیں ان کا اجر و ثواب ان کے پروردگار کے پاس ہے۔ ان کے لئے نہ کوئی خوف ہے اور نہ وہ غمگین ہوں گے۔'),
(282, 2, 275, 'اور جو لوگ سود کھاتے ہیں وہ (قیامت کے دن) نہیں کھڑے ہوں گے مگر اس شخص کی طرح جسے شیطان نے اپنے آسیب سے مخبوط الحواس بنا دیا ہو۔ (جس سے وہ پہچانے جائیں گے کہ وہ سود خور ہیں)۔ یہ اس لئے ہے کہ وہ قائل ہیں کہ تجارت بھی تو سود کی مانند ہے۔ حالانکہ اللہ نے تجارت کو حلال اور سود کو حرام کیا ہے۔ پس جس شخص کے پاس اس کے پروردگار کی طرف سے نصیحت (فہمائش) آئی اور وہ (سودخوری سے) باز آگیا۔ تو جو پہلے ہو چکا وہ اس کا ہے اس کا معاملہ خدا کے حوالے ہے اور جو (ممانعت کے بعد) پھر ایسا کرے تو ایسے لوگ جہنمی ہیں جو ہمیشہ ہمیشہ اس میں رہیں گے۔'),
(283, 2, 276, 'اللہ سود کو مٹاتا ہے اور خیر و خیرات کو بڑھاتا ہے اور جتنے ناشکرے اور گنہگار ہیں اللہ ان کو دوست نہیں رکھتا۔'),
(284, 2, 277, 'بے شک جو لوگ ایمان لائے اور اچھے عمل کئے۔ اور نماز پڑھی اور زکوٰۃ ادا کی ان کا اجر و ثواب ان کے پروردگار کے پاس ہے۔ اور (قیامت کے دن) ان کے لئے نہ کوئی خوف ہوگا اور نہ وہ غمگین ہوں گے۔'),
(285, 2, 278, 'اے ایمان والو! خدا (کی نافرمانی) سے ڈرو۔ اور جو کچھ سود (لوگوں کے ذمے) باقی رہ گیا ہے۔ اسے چھوڑ دو اگر تم ایمان والے ہو۔'),
(286, 2, 279, 'لیکن اگر تم نے ایسا نہ کیا تو پھر خدا اور اس کے رسول سے جنگ کرنے کے لئے تیار ہو جاؤ۔ اور اگر اب بھی تم توبہ کرلو۔ تو تمہارا اصل مال تمہارا ہوگا (جو تمہیں مل جائے گا)۔ نہ تم (زائد لے کر) کسی پر ظلم کروگے اور نہ (اصل مال خوردبرد کرکے) تم پر ظلم کیا جائے گا۔'),
(287, 2, 280, 'اور اگر (مقروض) تنگ دست ہے تو اسے فراخ دستی تک مہلت دینا ہوگی۔ اور اگر (قرضہ معاف کرکے) خیرات کرو۔ تو یہ چیز تمہارے لئے بہتر ہے اگر تم جانتے ہو۔'),
(288, 2, 281, 'اور اس دن (کی سزا اور رسوائی) سے ڈرو۔ جس دن اللہ کی طرف پلٹائے جاؤگے اور پھر جس شخص نے جو کچھ (نیکی یا بدی) کمائی ہوگی۔ وہ (اس کا بدلہ) اسے پورا پورا دیا جائے گا۔ اور (ان کی حق تلفی کرکے) ان پر ظلم نہیں کیا جائے گا۔'),
(289, 2, 282, 'اے ایمان والو! جب تم کسی مقررہ مدت تک آپس میں قرضہ کا لین دین کرو۔ تو اسے لکھ لیا کرو۔ اور چاہیے کہ کوئی لکھنے والا عدل و انصاف کے ساتھ تمہارے باہمی قول و قرار کو لکھے۔ اور جس طرح اللہ نے لکھنے والے کو (لکھنا پڑھنا) سکھایا ہے وہ لکھنے سے انکار نہ کرے (بلکہ) اسے چاہیے کہ لکھ دے۔ اور جس (مقروض) کے ذمہ (قرضہ کی ادائیگی) کا حق عائد ہوتا ہے اسے چاہیے کہ تحریر کا مضمون لکھوا دے اور اس (کاتب) کو چاہیے کہ اپنے پروردگار سے ڈرے اور اس میں کمی نہ کرے یااور اگر وہ شخص (مقروض) جس پر حق عائد ہو رہا ہے کم عقل ہو یا کمزور اور معذور ہو یا خود نہ لکھا سکتا ہو۔ تو پھر اس کا سرپرست (وکیل یا ولی) عدل و انصاف سے (تمسک کا مضمون) لکھوائے اور اپنے آدمیوں میں سے دو گواہوں کی گواہی کرالو۔ تاکہ اگر ان میں سے کوئی ایک بھولے تو دوسرا اسے یاد دلائے اور جب گواہوں کو (گواہی کے لئے) بلایا جائے تو وہ انکار نہ کریں۔ اور معاملہ چھوٹا ہو یا بڑا جس کی میعاد مقرر ہے۔ اس کے لکھنے میں سہل انگیزی نہ کرو۔ یہ (لکھا پڑھی) اللہ کے نزدیک زیادہ منصفانہ کاروائی ہے اور گواہی کے لئے تو زیادہ مضبوطی ہے۔ اور اس طرح زیادہ امکان ہے کہ تم شک و شبہ میں نہ پڑو۔ مگر جب نقدا نقد خرید و فروخت ہو کہ جو تم آپس میں ہاتھوں ہاتھ کرتے ہو تو اس صورت میں کوئی مضائقہ نہیں ہے کہ اسے نہ لکھو اور جب (اس طرح) خرید و فروخت کرو تو گواہ کر لیا کرو۔ اور (زبردستی کرکے) کاتب اور گواہ کو ضرر و زیاں نہ پہنچایا جائے اور اگر ایسا کروگے تو یہ تمہاری نافرمانی ہوگی (گناہ ہوگا) خدا (کی نافرمانی) سے ڈرو اللہ تمہیں (یہی) سکھاتا ہے اور اللہ ہر چیز کو خوب جانتا ہے۔'),
(290, 2, 283, 'اور اگر تم سفر میں ہو اور تمہیں کاتب نہ ملے تو رہن باقبضہ رکھ لو۔ اور اگر تم میں سے ایک کو دوسرے پر اعتبار ہو۔ تو جس پر اعتبار کیا گیا ہے۔ اسے چاہیے کہ اپنی امانت واپس کر دے اور اپنے پروردگار (کی مخالفت) سے ڈرے۔ اور گواہی کو نہ چھپاؤ۔ اور جو اسے چھپائے گا اس کا دل گنہگار ہوگا۔ اور تم جو کچھ کرتے ہو اللہ اس کو خوب جانتا ہے۔'),
(291, 2, 284, 'جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے (سب کچھ) خدا کا ہے۔ اور جو کچھ تمہارے دلوں میں ہے۔ خواہ تم اس کو ظاہر کرو یا اسے چھپاؤ۔ خدا سب کا تم سے محاسبہ کرے گا۔ اور پھر جسے چاہے گا۔ بخش دے گا اور جسے چاہے گا سزا دے گا۔ اللہ ہر چیز پر قادر ہے۔'),
(292, 2, 285, 'رسول(ص) ان تمام باتوں پر ایمان رکھتا ہے جو ان کے پروردگار کی طرف سے ان پر اتاری گئی ہیں اور مؤمنین بھی (سب) خدا پر اس کے ملائکہ پر، اس کی کتابوں پر اور اس کے رسولوں پر ایمان رکھتے ہیں۔ (وہ کہتے ہیں کہ) ہم خدا کے رسولوں میں تفریق نہیں کرتے اور کہتے ہیں کہ ہم نے فرمانِ الٰہی سنا اور اس کی اطاعت کی! پروردگار ہمیں تیری مغفرت درکار ہے۔ اور تیری ہی طرف پلٹ کر آنا ہے۔'),
(293, 2, 286, 'خدا کسی کو اس کی وسعت سے زیادہ تکلیف نہیں دیتا وہ جو (نیکی) کرے گا۔ اس کا نفع اس کو ہوگا اور وہ جو (برائی) کرے گا اس کا نقصان بھی اسی کو ہوگا۔ پروردگار! اگر ہم بھول جائیں یا چوک جائیں تو ہماری گرفت نہ کر۔ پروردگار! ہم پر ویسا بوجھ نہ ڈال جیسا ہم سے پہلے لوگوں پر ڈالا تھا۔ پروردگار! ہم پر وہ بار نہ ڈال جس کے اٹھانے کی ہم میں طاقت نہیں ہے۔ اور ہمیں (ہمارے قصور) معاف کر۔ اور ہمیں (ہمارے گناہ) بخش دے۔ اور ہم پر رحم فرما تو ہمارا مالک و سرپرست اعلیٰ ہے۔ کافروں کے مقابلہ میں تو ہی ہماری مدد فرما۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف۔ لام۔ میم۔'),
(295, 3, 2, 'اللہ ہی (کی ذات) ہے جس کے علاوہ کوئی الٰہ نہیں ہے۔ زندہ (جاوید) ہے جو (ساری کائنات کا) بندوبست کرنے والا ہے۔'),
(296, 3, 3, 'اسی نے آپ پر حق کے ساتھ وہ کتاب اتاری ہے جو اس سے پہلے موجود (آسمانی کتابوں) کی تصدیق کرتی ہے۔'),
(297, 3, 4, 'اور اسی نے اس سے پہلے لوگوں کی ہدایت کے لئے تورات و انجیل نازل کی۔ اور (حق و باطل کا) فیصلہ کن کلام نازل کیا۔ بلاشبہ جو لوگ آیاتِ الٰہی کا انکار کرتے ہیں۔ ان کے لیے بڑا سخت عذاب ہے۔ خدا زبردست ہے (اور برائی کا) بدلہ لینے والا ہے۔'),
(298, 3, 5, 'بے شک خدا پر کوئی چیز پوشیدہ نہیں ہے زمین میں اور نہ آسمان میں۔'),
(299, 3, 6, 'وہ (خدا) وہی ہے جو ماں کے پیٹ میں جس طرح چاہتا ہے، تمہاری صورتیں بناتا ہے۔ اس کے سوا کوئی الٰہ نہیں ہے۔ وہ زبردست اور بڑی حکمت والا ہے۔'),
(300, 3, 7, 'وہ وہی ہے جس نے آپ پر ایسی کتاب نازل کی جس میں کچھ آیتیں تو محکم ہیں۔ جو کتاب کی اصل و بنیاد ہیں اور کچھ متشابہ ہیں اب جن لوگوں کے دلوں میں کجی (ٹیڑھ) ہے۔ تو وہ فتنہ برپا کرنے اور من مانی تاویلیں کرنے کی خاطر متشابہ آیتوں کے پیچھے پڑے رہتے ہیں۔ حالانکہ خدا اور ان لوگوں کے سوا جو علم میں مضبوط و پختہ کار ہیں اور کوئی ان کی تاویل (اصل معنی) کو نہیں جانتا۔ جو کہتے ہیں کہ ہم اس (کتاب) پر ایمان لائے ہیں یہ سب (آیتیں) ہمارے پروردگار کی طرف سے ہیں اور نصیحت کا اثر صرف عقل والے ہی لیتے ہیں۔'),
(301, 3, 8, '(جو دعا کرتے ہیں) اے ہمارے پروردگار! ہمیں سیدھے راستے پر لگانے کے بعد ہمارے دلوں کو ٹیڑھا نہ ہونے دے اور ہمیں اپنی جناب سے رحمت عطا فرما۔ یقینا تو بڑا عطا کرنے والا ہے۔'),
(302, 3, 9, 'اے ہمارے پروردگار! بے شک تو ایک دن سب لوگوں کو جمع کرنے والا ہے جس (کے آنے) میں کوئی شک نہیں ہے۔ بلاشبہ خدا کبھی وعدہ خلافی نہیں کرتا۔'),
(303, 3, 10, 'بے شک جن لوگوں نے کفر اختیار کیا۔ اللہ کے یہاں ان کے مال اور اولاد ہرگز ان کو کوئی فائدہ نہیں پہنچا سکتے اور یہی لوگ جہنم کا ایندھن ہیں۔'),
(304, 3, 11, 'ان کا حال (معاملہ بھی وہی ہے) کہ جو فرعونی گروہ اور اس سے پہلے لوگوں کا تھا کہ انہوں نے ہماری نشانیوں کو جھٹلایا۔ تو اللہ نے ان کے گناہوں کی پاداش میں ان کو گرفت میں لے لیا اور اللہ سخت سزا دینے والا ہے۔'),
(305, 3, 12, '(اے رسول!) کافروں سے کہہ دو کہ عنقریب تم (اہل اسلام کے مقابلہ میں) مغلوب ہوگے اور جہنم کی طرف محشور ہوگے اور وہ کیا بری آرام گاہ ہے۔'),
(306, 3, 13, 'تمہارے لیے ان دو گروہوں (کے حالات) میں جن کی (میدانِ بدر میں) مڈبھیڑ ہوئی تھی (صداقت رسول(ص) کی) ایک بڑی نشانی (معجزہ) موجود ہے۔ ایک گروہ خدا کی راہ میں جنگ کر رہا تھا۔ اور دوسرا گروہ کافر تھا جن کو (مسلمان) اپنی آنکھوں سے دوگنا دیکھ رہے تھے اور اللہ اپنی مدد سے جس کی چاہتا ہے، تائید و تقویت کرتا ہے بے شک اس (واقعہ) میں بڑی عبرت و نصیحت ہے۔ نگاہ (عبرت رکھنے) والوں کے لیے۔'),
(307, 3, 14, 'لوگوں کے لیے خوش نما بنا دی گئی ہے عورتوں، بیٹوں اور ڈھیروں سونے و چاندی پر مشتمل مال کی محبت اور (عمدہ) گھوڑے، چوپائے اور کھیتی باڑی۔ یہ سب (چیزیں) دنیاوی زندگی کا اثاثہ اور متاع ہیں۔ جبکہ (آخرت کا) اچھا ٹھکانہ اور بہترین انجام خدا کے یہاں ہے۔'),
(308, 3, 15, '(اے رسول(ص)) کہو۔ کیا میں تمہیں ان سب سے بہتر چیز بتاؤں؟ جن لوگوں نے پرہیزگاری اختیار کی ان کے لئے ان کے پروردگار کے یہاں ایسے بہشت ہیں جن کے نیچے نہریں جاری ہیں اور وہ ان میں ہمیشہ رہیں گے (ان کے علاوہ) پاک و پاکیزہ بیویاں ہیں اور (سب سے بڑی نعمت) خدا کی خوشنودی ہے۔ اور خدا (اپنے) بندوں کو خوب دیکھ رہا ہے۔'),
(309, 3, 16, '(ان متقیوں کی صفت یہ ہے کہ) کہتے ہیں (دعا کرتے ہیں) ہمارے پروردگار! بے شک ہم ایمان لائے ہیں تو ہمارے گناہوں کو بخش دے اور ہمیں آتش دوزخ کے عذاب سے بچا۔'),
(310, 3, 17, 'یہ لوگ صبر کرنے والے ہیں (قول و فعل میں) سچ بولنے والے، اطاعت کرنے والے، راہِ خدا میں خیرات کرنے والے اور سحر کے وقت طلب مغفرت کرنے والے ہیں۔'),
(311, 3, 18, 'خود اللہ، اس کے ملائکہ اور صاحبانِ علم گواہ ہیں کہ اس کے سوا کوئی خدا نہیں ہے اور وہ عدل و انصاف کے ساتھ قائم و برقرار ہے اس کے سوا کوئی خدا نہیں ہے جو زبردست، حکمت والا (دانا) ہے۔'),
(312, 3, 19, 'بے شک خدا کے نزدیک سچا دین صرف اسلام ہے اور اہل کتاب نے (دین حق میں) اختلاف نہیں کیا مگر اپنے پاس علم آجانے (اور اصل حقیقت معلوم ہو جانے) کے بعد۔ محض آپس کی شرارت و ضد اور ناحق کوشی کی بنا پر اور جو بھی آیاتِ الٰہیہ کا انکار کرے گا تو یقینا خدا بہت جلد حساب لینے والا ہے۔'),
(313, 3, 20, 'پس اگر یہ لوگ آپ سے خواہ مخواہ حجت بازی کریں۔ تو کہہ دو کہ میں نے اور میری پیروی کرنے والوں نے تو اپنا سر خدا کے سامنے جھکا دیا ہے (اپنے کو اس کے سپرد کر دیا ہے) اور اہل کتاب اور جو کسی کتاب کے پڑھنے والے نہیں ہیں سے کہو: کیا تم بھی اسلام لائے ہو؟ پس اگر انہوں نے اسلام کا اقرار کر لیا تو گویا ہدایت پا گئے اور اگر (اس سے) منہ موڑا تو آپ کا فرض تو صرف پہنچا دینا ہے (منوانا نہیں ہے)۔ اور اللہ (اپنے) بندوں کو خوب دیکھنے (اور پہچاننے والا ہے)۔'),
(314, 3, 21, 'جو لوگ اللہ کی آیات کا انکار کرتے ہیں اور پیغمبروں کو ناحق قتل کرتے ہیں اور ایسے لوگوں کو بھی قتل کرتے ہیں جو عدل و انصاف کا حکم دیتے ہیں انہیں دردناک عذاب کا مژدہ سناؤ۔'),
(315, 3, 22, 'یہ وہ (بدنصیب) ہیں جن کے اعمال دنیا و آخرت میں اکارت و برباد ہوگئے۔ اور ان کے لئے کوئی مددگار نہیں ہے۔'),
(316, 3, 23, 'کیا تم نے ان (علماءِ یہود) کو نہیں دیکھا جن کو کتاب (تورات کے علم) سے تھوڑا سا حصہ ملا ہے جب انہیں کتابِ خدا کی طرف بلایا جاتا ہے تاکہ وہ ان کے درمیان فیصلہ کرے اس پر ان کا ایک گروہ پیٹھ پھیر لیتا ہے۔ درآنحالیکہ وہ روگردانی کرنے والے ہوتے ہیں۔'),
(317, 3, 24, 'یہ (انداز) اس لئے ہے کہ وہ اس بات کے قائل ہیں کہ گنتی کے چند دنوں کے سوا ہمیں آتش جہنم چھوئے گی بھی نہیں۔ یہ لوگ جو افتراء پردازیاں کرتے رہتے ہیں۔ انہوں نے ان کو دین کے بارے میں دھوکہ دیا ہے۔'),
(318, 3, 25, 'اس وقت ان کی کیا حالت ہوگی جب ہم ان کو ایک دن (بروزِ قیامت) اکٹھا کریں گے جس (کے آنے) میں کوئی شک و شبہ نہیں ہے۔ اور ہر شخص کو جو کچھ اس نے کمایا ہوگا اس کا پورا پورا بدلہ دیا جائے گا۔ اور ان پر کوئی ظلم نہیں کیا جائے گا۔'),
(319, 3, 26, '(اے رسول(ص)!) کہو: اے خدا تمام جہان کے مالک! تو جسے چاہتا ہے حکومت دیتا ہے اور جس سے چاہتا ہے حکومت چھین لیتا ہے۔ تو جسے چاہتا ہے عزت دیتا ہے اور جسے چاہتا ہے ذلیل کرتا ہے، ہر قسم کی بھلائی تیرے قبضہ میں ہے۔ بے شک تو ہر چیز پر قادر ہے۔'),
(320, 3, 27, 'تو ہی رات کو (بڑھا کر) دن میں اور دن کو (بڑھا کر) رات میں داخل کرتا ہے اور تو جاندار کو بے جان اور بے جان کو جاندار سے نکالتا ہے اور جسے چاہتا ہے، بے حساب روزی عطا کرتا ہے۔'),
(321, 3, 28, '(خبردار) اہل ایمان، اہل ایمان کو چھوڑ کر کافروں کو دوست (اور سرپرست) نہ بنائیں۔ اور جو ایسا کرے گا۔ اس کا خدا سے کوئی تعلق اور سروکار نہیں ہوگا۔ مگر یہ کہ تمہیں ان (کافروں) سے خوف ہو تو پھر (بقصد تقیہ اور بچاؤ) ایسا کرنے میں کوئی حرج نہیں ہے۔ اور خدا تمہیں اپنی ذات سے ڈراتا ہے اور خدا ہی کی طرف پلٹ کر جانا ہے۔'),
(322, 3, 29, 'کہہ دیجئے! جو کچھ تمہارے سینوں میں ہے، تم اسے چھپاؤ یا اسے ظاہر کرو، بہرحال خدا اسے جانتا ہے۔ اور جو کچھ آسمانوں اور زمین میں ہے وہ سب کچھ جانتا ہے اور خدا ہر چیز پر قادر ہے۔'),
(323, 3, 30, 'اس دن کو یاد کرو جب ہر شخص اس بھلائی کو اپنے سامنے پائے گا جو اس نے کی ہوگی۔ اور برائی کو بھی (جنہیں دیکھ کر) خواہش کرے گا کہ کاش اس کے اور اس کے برے اعمال کے درمیان بڑا فاصلہ ہوتا۔ اور خدا تمہیں اپنی ذات (اپنے عذاب) سے ڈراتا ہے اور خدا اپنے بندوں پر بڑا مہربان ہے۔'),
(324, 3, 31, '(اے رسول! میری محبت کے دعویداروں سے) کہہ دیں کہ اگر تم خدا سے (سچی) محبت رکھتے ہو تو میری پیروی کرو خدا بھی تم سے محبت کرے گا اور تمہارے گناہوں کو بخش دے گا اور اللہ بڑا بخشنے والا مہربان ہے۔'),
(325, 3, 32, 'کہہ دیں خدا اور رسول(ص) کی اطاعت و فرماں برداری کرو اور اگر روگردانی کریں تو بے شک خدا کافروں (نافرمانوں) کو دوست نہیں رکھتا۔'),
(326, 3, 33, 'بے شک اللہ نے آدم، نو ح، خاندانِ ابراہیم اور خاندانِ عمران کو سارے جہانوں سے منتخب کیا ہے۔'),
(327, 3, 34, 'جو ایک نسل ہے جن کے بعض بعض سے ہیں (یہ اولاد ہے ایک دوسرے کی) اور خدا بڑا سننے والا، بڑا جاننے والا ہے۔'),
(328, 3, 35, '(اس وقت کو یاد کرو) جب عمران کی بیوی نے کہا اے میرے پروردگار! جو بچہ میرے پیٹ میں ہے اسے میں (دنیا کے کاموں سے) آزاد کرکے (خانہ کعبہ کی) جاروب کشی اور تیری عبادت کے لئے تیری بارگاہ میں نذر کرتی ہوں تو میری (نذر) قبول فرما۔ بے شک تو بڑا سننے والا، بڑا جاننے والا ہے۔'),
(329, 3, 36, 'پھر جب اس کے یہاں وہ (بچی) پیدا ہوئی۔ تو اس نے کہا: اے میرے پروردگار! میرے ہاں تو لڑکی پیدا ہوئی ہے (میں تو یہ لڑکی جنی ہوں) حالانکہ خدا خود خوب جانتا ہے کہ وہ کیا جنی ہے۔ اور وہ جانتا ہے کہ لڑکا، لڑکی یکساں نہیں ہوتے۔ اور میں نے اس کا نام مریم رکھا ہے اور میں اسے اور اس کی اولاد کو شیطان رجیم (کے شر) سے (بچنے کے لیے) تیری پناہ میں دیتی ہوں۔'),
(330, 3, 37, 'تو اس کے پروردگار نے اس لڑکی (مریم) کو احسن طریقہ سے قبول فرما لیا۔ اور اچھی طرح اس کی نشوونما کا انتظام کیا (یعنی) جناب زکریا کو اس کا کفیل (اور سرپرست) بنایا۔ جب بھی زکریا محرابِ عبادت میں اس (مریم) کے پاس آتے تھے تو اس کے پاس کھانے کی کوئی چیز موجود پاتے۔ (اور) پوچھتے: اے مریم! یہ تمہارے پاس کہاں سے آیا ہے؟ وہ جواب دیتی۔ یہ خدا کے یہاں سے آیا ہے۔ بے شک خدا جسے چاہتا ہے اسے بے حساب رزق عطا فرماتا ہے۔'),
(331, 3, 38, 'اس موقع پر زکریا نے اپنے پروردگار سے دعا کی اور عرض کیا اے میرے پروردگار! مجھے اپنی طرف سے پاک و پاکیزہ اولاد عطا فرما۔ بے شک تو (ہر ایک کی) دعا کا سننے والا ہے۔'),
(332, 3, 39, 'تو فرشتوں نے انہیں اس حالت میں آواز دی جب کہ وہ محراب میں کھڑے ہوئے نماز پڑھ رہے تھے۔ کہ خدا آپ کو یحییٰ کی بشارت دیتا ہے جو اللہ کے ایک کلمہ (عیسیٰ) کی تصدیق کرنے والا ہوگا۔ سردار، ضبط نفس کرنے والا پارسا اور نیکوکار جماعت میں سے نبی ہوگا۔'),
(333, 3, 40, 'جناب زکریا نے (یہ خوشخبری سن کر) کہا: اے پروردگار! میرے یہاں لڑکا کس طرح ہوگا جبکہ میرا بڑھاپا آگیا۔ اور میری بیوی بانجھ ہے؟ ارشاد ہوا اسی طرح (خدا) جو چاہتا ہے وہ کرتا ہے۔'),
(334, 3, 41, 'عرض کیا: اے میرے پروردگار! میرے (اطمینانِ قلب) کیلئے کوئی علامت مقرر فرما فرمایا: تمہاری علامت یہ ہے کہ تم تین دن رات تک لوگوں سے اشارہ کے سوا بات نہیں کر سکوگے۔ (شکرانۂ نعمت کے طور پر) اپنے پروردگار کا کثرت سے ذکر کرو۔ اور صبح و شام اسی کی تسبیح کرو۔'),
(335, 3, 42, 'اور جب فرشتوں نے کہا اے مریم! بے شک اللہ نے تمہیں منتخب کر لیا ہے اور تمہیں پاک و پاکیزہ بنایا ہے۔ اور تمہیں تمام جہانوں کی عورتوں سے برگزیدہ بنا دیا ہے۔'),
(336, 3, 43, 'اے مریم۔ اپنے پروردگار کی اطاعت کرو۔ اور سجدہ کرو۔ اور رکوع کرنے والوں کے ساتھ رکوع کرتی رہو۔'),
(337, 3, 44, '(اے رسول(ص)) یہ (خبر) غیب کی خبروں میں سے ایک ہے جو وحی کے ذریعہ ہم آپ کی طرف بھیج رہے ہیں اور آپ ان (دعویداران سرپرستی) کے پاس موجود نہ تھے جب وہ (قرعہ اندازی کیلئے) اپنے قلم ڈال رہے تھے کہ مریم کی کفالت کون کرے؟ اور آپ ان کے پاس موجود نہ تھے جب وہ آپس میں جھگڑ رہے تھے۔'),
(338, 3, 45, 'اور (وہ وقت یاد کرو) جب فرشتوں نے کہا اے مریم! بے شک خدا آپ کو اپنے کلمہ کی خوشخبری دیتا ہے۔ جس کا نام مسیح عیسیٰ بن مریم ہوگا۔ جو دنیا و آخرت میں عزت و آبرو والا ہوگا۔ اور (خدا کے) مقرب بندوں میں سے ہوگا۔'),
(339, 3, 46, 'اور وہ گہوارے میں بھی لوگوں سے باتیں کرے گا۔ اور ادھیڑ عمر میں بھی۔ اور نیکوکاروں میں سے ہوگا۔'),
(340, 3, 47, '(یہ بشارت سن کر) مریم نے کہا: اے میرے پروردگار! میرے یہاں بچہ کہاں سے ہوگا۔ حالانکہ مجھے کسی انسان (مرد) نے ہاتھ تک نہیں لگایا؟ ارشاد ہوا: بات اسی طرح ہے مگر خدا جو چاہتا ہے (بلا اسباب بھی) پیدا کرتا ہے۔ جب وہ کسی کام کے کرنے کا فیصلہ کر لیتا ہے تو اسے حکم دیتا ہے کہ ہو جا تو بس وہ ہو جاتا ہے۔'),
(341, 3, 48, 'اور خدا اس (بچہ) کو کتاب و حکمت اور توراۃ و انجیل کی تعلیم دے گا۔'),
(342, 3, 49, 'اور اسے بنی اسرائیل کی طرف اپنا رسول بنائے گا۔ (اور جب وہ مبعوث ہوگا تو کہے گا کہ) میں تمہارے پاس تمہارے پروردگار کی طرف سے معجزہ لے کر آیا ہوں کہ میں تمہارے لئے مٹی سے پرندہ کی صورت بناتا ہوں اور اس میں پھونک مارتا ہوں تو وہ خدا کے حکم سے پرندہ بن جاتا ہے۔ اور میں خدا کے حکم سے مادر زاد اندھے اور کوڑھی کو شفا دیتا ہوں اور مردوں کو زندہ کرتا ہوں۔ اور جو کچھ تم کھاتے ہو۔ اور اپنے گھروں میں جمع کرتے ہو وہ تمہیں بتاتا ہوں بے شک اس میں تمہارے لئے (خدا کی قدرت اور میری نبوت کی) بڑی نشانی ہے اگر تم ایمان لانے والے ہو۔'),
(343, 3, 50, 'اور جو مجھ سے پہلے (توراۃ) موجود ہے میں اس کی تصدیق کرتا ہوں اور (میرے آنے کا ایک مقصد یہ بھی ہے کہ) میں تمہارے لئے ان چیزوں میں سے بعض کو حلال کروں جو تم پر حرام تھیں۔ اور میں تمہارے پروردگار کی طرف سے اعجاز کے ساتھ تمہارے پاس آیا ہوں۔ لہٰذا اللہ (کی نافرمانی) سے ڈرو۔ اور میری اطاعت کرو۔'),
(344, 3, 51, 'بے شک اللہ میرا اور تمہارا پروردگار ہے پس تم اس کی عبادت کرو۔ یہی سیدھا راستہ ہے۔'),
(345, 3, 52, 'پھر جب عیسیٰ نے ان (بنی اسرائیل) کی طرف سے کفر و انکار محسوس کیا۔ تو کہا کون ہے جو اللہ کی راہ میں میرا مددگار ہو؟ حواریوں نے کہا ہم اللہ کے مددگار ہیں۔ ہم اللہ پر ایمان لائے ہیں اور آپ گواہ رہیں کہ ہم مسلمان (فرمانبردار) ہیں۔'),
(346, 3, 53, 'اور کہا اے ہمارے پروردگار جو کچھ تو نے نازل کیا ہے ہم اس پر ایمان لائے ہیں اور ہم نے رسول (عیسیٰ) کی پیروی اختیار کی۔ لہٰذا ہمیں (حق کی) گواہی دینے والوں (کے دفتر) میں درج فرما۔'),
(347, 3, 54, 'انہوں (بنی اسرائیل) نے (عیسیٰ کے خلاف) مکاری کی اور اللہ نے بھی اپنی (جوابی) تدبیر و ترکیب کی۔ اور اللہ سب سے بہتر تدبیر و ترکیب کرنے والا ہے۔'),
(348, 3, 55, '(اس وقت کو یاد کرو) جب اللہ نے کہا (اے عیسیٰ) میں تمہیں پورا قبض کرنے والا اور تمہیں اپنی جانب اٹھانے والا ہوں اور تمہیں کافروں سے پاک کرنے والا ہوں اور تمہاری پیروی کرنے والوں کو کافروں پر قیامت تک غالب اور برتر کرنے والا ہوں۔ پھر تم سب کی بازگشت میری طرف ہے تو اس وقت میں تمہارے درمیان ان باتوں کا فیصلہ کروں گا جن میں تم اختلاف کرتے تھے۔'),
(349, 3, 56, 'تو جن لوگوں نے کفر اختیار کیا انہیں دنیا و آخرت میں سخت سزا دوں گا۔ اور ان کا کوئی مددگار نہ ہوگا۔'),
(350, 3, 57, 'اور جو لوگ ایمان لائے اور نیک عمل کئے تو خدا ان کو پورا پورا اجر و ثواب دے گا۔ اور اللہ ظالموں کو دوست نہیں رکھتا۔'),
(351, 3, 58, '(اے رسول) یہ جو ہم آپ کو پڑھ کر سنا رہے ہیں (کتابِ الٰہی کی) آیات ہیں (یا خدا کی قدرت اور آپ کی حقانیت کی نشانیاں ہیں) اور وہ دانائی سے لبریز تذکرے ہیں۔'),
(352, 3, 59, 'بے شک اللہ کے نزدیک عیسیٰ کی مثال آدم کی سی ہے کہ اللہ نے انہیں مٹی سے پیدا کیا پھر حکم دیا کہ ہو جا سو وہ ہوگیا۔'),
(353, 3, 60, 'یہ بات تمہارے پروردگار کی طرف سے حق ہے تو تم شک کرنے والوں میں سے نہ ہونا۔'),
(354, 3, 61, '(اے پیغمبر(ص)) اس معاملہ میں تمہارے پاس صحیح علم آجانے کے بعد جو آپ سے حجت بازی کرے تو آپ ان سے کہیں کہ آؤ ہم اپنے اپنے بیٹوں، اپنی اپنی عورتوں اور اپنے اپنے نفسوں کو بلائیں اور پھر مباہلہ کریں (بارگاہِ خدا میں دعا و التجا کریں) اور جھوٹوں پر خدا کی لعنت قرار دیں۔ (یعنی ہم اپنے بیٹوں کو بلائیں تم اپنے بیٹوں کو، ہم اپنی عورتوں کو بلائیں تم اپنی عورتوں کو اور ہم اپنے نفسوں کو بلائیں تم اپنے نفسوں کو پھر اللہ کے سامنے گڑگڑائیں اور جھوٹوں پر خدا کی لعنت کریں)۔'),
(355, 3, 62, '(جناب عیسیٰ کی) یہ برحق سرگزشت ہے (وہ خدا یا خدا کے بیٹے نہیں بلکہ اس کے خاص بندے ہیں) اللہ کے سوا کوئی الٰہ نہیں ہے وہ توانا اور بڑا حکمت والا ہے۔'),
(356, 3, 63, 'اب اس کے بعد بھی یہ لوگ انحراف کریں تو خدا مفسدین کو خوب جانتا ہے۔'),
(357, 3, 64, '(اے رسول) کہیے اے اہل کتاب! آؤ ایک ایسی بات کی طرف جو ہمارے اور تمہارے درمیان مشترک اور یکساں ہے (اور وہ یہ کہ) ہم اللہ کے سوا کسی کی عبادت نہ کریں۔ اور کسی کو اس کا شریک نہ بنائیں، اور ہم میں سے کوئی کسی کو اللہ کے سوا اپنا رب (مالک و مختار) نہ بنائے۔ اور اگر یہ لوگ اس (دعوت) سے منہ موڑیں تو (اے مسلمانو) تم کہہ دو کہ گواہ رہنا ہم مسلمان (خدا کے فرمانبردار و اطاعت گزار ہیں)۔'),
(358, 3, 65, 'اے اہل کتاب! تم ابراہیم کے بارے میں کیوں (ہم سے) حجت بازی کرتے ہو۔ حالانکہ تورات اور انجیل ان کے بعد اتری ہیں کیا تم میں اتنی بھی عقل (سمجھ) نہیں ہے۔'),
(359, 3, 66, 'تم وہی لوگ ہو۔ جو (ہم سے) ان باتوں کے بارے میں بحث و تکرار کرتے رہے جن کا تمہیں کچھ علم تھا۔ اب ان باتوں کے بارے میں کیوں حجت بازی کرتے ہو جن کا تمہیں کچھ علم نہیں (حقیقت حال) صرف اللہ جانتا ہے تم نہیں جانتے۔'),
(360, 3, 67, 'ابراہیم نہ یہودی تھے اور نہ نصرانی بلکہ وہ تو نرے کھرے خالص مسلمان (خدا کے فرمانبردار بندے) تھے اور وہ مشرکوں میں سے نہیں تھے۔'),
(361, 3, 68, 'بے شک تمام لوگوں سے ابراہیم کے زیادہ قریب اور زیادہ تعلق رکھنے والے وہ ہیں جنہوں نے اُن کی پیروی کی اور یہ نبی اور وہ جو (ان پر) ایمان لائے ہیں اور اللہ ایمان والوں کا ساتھی و سرپرست ہے۔'),
(362, 3, 69, 'اہل کتاب کا ایک گروہ چاہتا ہے کہ کاش وہ تمہیں گمراہ کر سکے حالانکہ وہ اپنے سوا کسی کو بھی گمراہ نہیں کرتے مگر انہیں اس کا شعور نہیں۔'),
(363, 3, 70, 'اے اہل کتاب تم آیاتِ الٰہیہ کا کیوں انکار کرتے ہو؟ حالانکہ تم خود گواہ ہو (کہ وہ آیاتِ الٰہیہ ہیں) اور برحق ہیں۔'),
(364, 3, 71, 'اے اہل کتاب! حق کو باطل کے ساتھ کیوں گڈمڈ کرتے ہو؟ اور حق کو کیوں چھپاتے ہو حالانکہ تم جانتے ہو (کہ حق کیا ہے)۔'),
(365, 3, 72, 'اہل کتاب کا ایک گروہ (اپنے لوگوں سے) کہتا ہے کہ جو کچھ مسلمانوں پر نازل ہوا ہے اس پر صبح کے وقت ایمان لے آؤ اور شام کے وقت انکار کر دو۔ شاید (اس ترکیب سے) وہ مسلمان (اپنے دین سے) پھر جائیں۔'),
(366, 3, 73, 'خبردار۔ صرف اسی کی بات مانو جو تمہارے دین کی پیروی کرے۔ کہہ دیجیے کہ حقیقی ہدایت تو اللہ کی ہدایت ہے (اور یہ اسی کی دین ہے) کہ کسی کو ویسی ہی چیز مل جائے جو (کبھی) تم کو دی گئی تھی۔ یا وہ دلیل و حجت میں تمہارے پروردگار کے ہاں تم پر غالب آجائیں۔ کہہ دیجیے کہ بے شک فضل و کرم اللہ کے ہاتھ میں ہے وہ جسے چاہتا ہے عطا کرتا ہے۔ وہ بڑی وسعت والا اور بڑا جاننے والا ہے۔'),
(367, 3, 74, 'وہ جس کو چاہتا ہے اپنی رحمت کے لئے مخصوص کر لیتا ہے اللہ بڑے فضل و کرم والا ہے۔'),
(368, 3, 75, 'اور اہل کتاب میں سے کوئی تو ایسا ہے۔ کہ اگر تم ڈھیر بھر روپیہ بھی بطور امانت اس کے پاس رکھو تو وہ تمہیں ادا کر دے گا۔ اور ان میں کوئی ایسا بھی ہے کہ اگر اس کے پاس ایک دینار (اشرفی) بھی بطور امانت رکھو تو جب تک تم اس کے سر پر کھڑے نہ رہو وہ تمہیں واپس نہیں کرے گا (یہ بدمعاملگی) اس وجہ سے ہے کہ ان کا قول ہے کہ ہم پر امیّون (ان پڑھ عربوں جوکہ اہل کتاب نہیں) کے بارے میں کوئی پابندی نہیں ہے۔ اور وہ جان بوجھ کر اللہ پر جھوٹ منڈھتے ہیں۔'),
(369, 3, 76, 'ہاں جو شخص اپنے عہد و پیمان کو پورا کرے اور پرہیزگاری اختیار کرے تو بے شک خدا پرہیزگاروں سے محبت کرتا ہے۔'),
(370, 3, 77, 'جو لوگ اللہ سے کئے گئے عہد و پیمان اور قسم اقسام کو تھوڑی سی قیمت پر بیچ دیتے ہیں ان کا آخرت میں کوئی حصہ نہیں ہے۔ خدا قیامت کے دن نہ ان سے بات کرے گا اور نہ ان کی طرف نظر (رحمت) کرے گا اور نہ ان کو (گناہوں کی کثافت سے) پاک کرے گا۔ اور ان کے لئے دردناک عذاب ہے۔'),
(371, 3, 78, 'بے شک ان (اہل کتاب) سے ایک گروہ ایسا بھی ہے۔ جو اپنی زبانوں کو کتاب (توراۃ وغیرہ) کے پڑھنے میں مروڑتا ہے اور کچھ کا کچھ پڑھتا ہے تاکہ تم یہ سمجھو کہ یہ (توڑ موڑ) بھی کتاب خدا میں سے ہے حالانکہ وہ کتاب میں سے نہیں ہے۔ اور کہتا ہے کہ وہ خدا کی طرف سے (آیا) ہے حالانکہ وہ خدا کی طرف سے نہیں ہے یہ جان بوجھ کر خدا پر جھوٹ باندھتا ہے۔'),
(372, 3, 79, 'کسی ایسے انسان کو یہ بات زیب نہیں دیتی جسے خدا کتاب، حکمت (یا اقتدار) اور نبوت عطا فرمائے اور وہ لوگوں سے کہے کہ تم خدا کو چھوڑ کر میرے بندے بن جاؤ (میری عبادت کرو) بلکہ (وہ تو یہ کہے گا کہ) تم اللہ والے ہو جاؤ۔ اس بنا پر کہ تم کتابِ الٰہی کی تعلیم دیتے ہو۔ اور اسے پڑھتے بھی رہتے ہو۔'),
(373, 3, 80, 'اور وہ کبھی تمہیں یہ حکم نہیں دے گا کہ تم فرشتوں اور نبیوں کو اپنا پروردگار بنا لو۔ بھلا وہ تمہیں کفر کا حکم دے گا۔ اس کے بعد کہ تم مسلمان ہو؟ ۔'),
(374, 3, 81, 'اور (یاد کرو) جب خدا نے تمام پیغمبروں سے یہ عہد و اقرار لیا تھا کہ میں نے جو تمہیں کتاب و حکمت عطا کی ہے۔ اس کے بعد جب تمہارے پاس ایک عظیم رسول آئے جو تمہارے پاس کی چیزوں (کتابوں) کی تصدیق کرنے والا ہو تو تم اس پر ضرور ایمان لانا اور ضرور اس کی مدد کرنا ارشاد ہوا۔ کیا تم اقرار کرتے ہو؟ اور اس پر میرے عہد و پیمان کو قبول کرتے ہو اور یہ ذمہ داری اٹھاتے ہو؟ انہوں نے کہا ہاں ہم نے اقرار کیا۔ ارشاد ہوا۔ تم سب گواہ رہنا اور میں بھی تمہارے ساتھ گواہوں میں سے ہوں۔'),
(375, 3, 82, 'اب جو کوئی بھی اس (عہد) سے منہ پھیرے گا تو ایسے ہی لوگ فاسق (نافرمان) ہیں۔'),
(376, 3, 83, 'کیا وہ اللہ کے دین (اسلام) کے سوا کسی اور (دین) کو تلاش کرتے ہیں حالانکہ جو آسمانوں میں ہیں یا زمین میں ہیں سب خوشی سے یا ناخوشی سے (چار و ناچار) اسی کی بارگاہ میں سر تسلیم خم کئے ہوئے ہیں اور بالآخر سب اسی کی طرف لوٹائے جائیں گے۔'),
(377, 3, 84, '(اے رسول) کہہ دیجئے! کہ ہم اللہ پر ایمان لائے اور اس پر بھی جو ہم پر اتارا اور جو ابراہیم، اسماعیل، اسحاق، یعقوب اور اسباط پر اتارا گیا۔ اور اس پر بھی جو کچھ موسیٰ و عیسیٰ اور دوسرے پیغمبروں کو جو کچھ ان کے پروردگار کی طرف سے عطا ہوا۔ ہم (بحیثیت نبی ہونے کے) ان کے درمیان تفریق نہیں کرتے۔ اور ہم اسی (خدائے یکتا) کے مسلمان (اطاعت گزار بندے) ہیں۔'),
(378, 3, 85, 'اور جو شخص اسلام کے علاوہ کوئی بھی دین اختیار کرے گا تو اس کا دین ہرگز قبول نہ کیا جائے گا اور وہ آخرت میں خسارہ پانے والوں میں سے ہوگا۔'),
(379, 3, 86, 'بھلا خدا ان لوگوں کو کیوں کر ہدایت دے گا (منزل مقصود پر پہنچائے گا) جو ایمان لانے کے بعد پھر کافر ہوگئے۔ حالانکہ وہ گواہی دے چکے تھے کہ رسول برحق ہے۔ اور ان کے پاس آیات بینات (واضح معجزات) بھی آچکے تھے۔ بے شک خدا ظلم و جور کرنے والوں کو ہدایت نہیں دیتا۔'),
(380, 3, 87, 'ایسے لوگوں کی سزا یہ ہے کہ ان پر اللہ، فرشتوں اور سب انسانوں کی لعنت ہے۔'),
(381, 3, 88, 'وہ ہمیشہ اس لعنت (پھٹکار) میں گرفتار رہیں گے۔ نہ ان کے عذاب میں تخفیف (کمی) کی جائے گی اور نہ انہیں مہلت دی جائے گی۔'),
(382, 3, 89, 'مگر وہ جنہوں نے اس کے بعد توبہ کرلی اور اپنی اصلاح کر لی تو بے شک خدا بڑا بخشنے والا اور بڑا رحم کرنے والا ہے۔'),
(383, 3, 90, 'بے شک وہ لوگ جو ایمان لانے کے بعد کافر ہوگئے اور کفر میں بڑھتے ہی چلے گئے ان کی توبہ قبول نہیں ہوگی اور یہی لوگ حقیقی گمراہ ہیں۔'),
(384, 3, 91, 'بے شک جن لوگوں نے کفر اختیار کیا اور پھر کفر ہی کی حالت میں مر گئے تو ان میں سے کسی ایک سے ساری زمین بھر سونا بطور فدیہ و معاوضہ ہرگز قبول نہیں کیا جائے گا یہ وہ لوگ ہیں جن کے لئے دردناک عذاب ہے اور ان کے لئے کوئی مددگار نہیں ہوں گے۔'),
(385, 3, 92, 'لوگو! تم ہرگز اس وقت تک نیکی (کو حاصل) نہیں کر سکتے جب تک اپنی پسندیدہ چیزوں میں سے کچھ راہِ خدا میں خرچ نہ کرو۔ اور تم (راہِ خدا میں) جو کچھ خرچ کرتے ہو خدا اس کو خوب جانتا ہے۔'),
(386, 3, 93, 'تورات نازل ہونے سے پہلے کھانے کی سب چیزیں (جو اسلام میں حلال ہیں) بنی اسرائیل کے لیے بھی حلال تھیں۔ سوائے اس کے جو اسرائیل (یعقوب) نے اپنے اوپر حرام کر رکھی تھی (یہود سے) کہو کہ اگر تم سچے ہو تو تورات لاؤ اور اسے پڑھو۔'),
(387, 3, 94, 'پھر اس کے بعد جو شخص خدا پر بہتان باندھے۔ تو (سمجھ لو کہ) ایسے لوگ ہی ظالم ہیں۔'),
(388, 3, 95, '(اے رسول(ص)) کہہ دیجیے خدا نے سچ فرمایا! پس تم ملت (دین) ابراہیم کی پیروی کرو جو باطل سے کنارہ کش ہوکر صرف اللہ کا ہو رہا تھا اور مشرکین میں سے نہ تھا۔'),
(389, 3, 96, 'بے شک سب سے پہلا گھر جو لوگوں (کی عبادت) کے لیے بنایا گیا یقینا وہی ہے جو مکہ میں ہے بڑی برکت والا اور عالمین کے لیے مرکز ہدایت ہے۔'),
(390, 3, 97, 'اس گھر میں کھلی ہوئی نشانیاں ہیں (منجملہ ان کے ایک) مقام ابراہیم ہے۔ اور جو اس میں داخل ہوا اسے امن مل گیا۔ اور لوگوں پر واجب ہے کہ محض خدا کے لیے اس گھر کا حج کریں جو اس کی استطاعت (قدرت) رکھتے ہیں اور جو (باوجود قدرت) کفر کرے (انکار کرے) تو بے شک خدا تمام عالمین سے بے نیاز ہے۔'),
(391, 3, 98, '(اے رسول(ص)) کہہ دیجیے! اے اہل کتاب تم آیاتِ الٰہیہ کا کیوں انکار کرتے ہو؟ حالانکہ تم جو کچھ کرتے ہو خدا اس کا گو اہ ہے (سب کچھ دیکھ رہا ہے) ۔'),
(392, 3, 99, 'کہیے۔ اے اہل کتاب! آخر تم اس شخص کو جو ایمان لانا چاہتا ہے کیوں خدا کی راہ سے روکتے ہو؟ اور چاہتے ہو کہ اس راہ (راست) کو ٹیڑھا بنا دو حالانکہ تم خود گواہ ہو (کہ سیدھی راہ یہی ہے) اور جو کچھ تم کرتے ہو خدا اس سے بے خبر نہیں ہے۔'),
(393, 3, 100, 'اے ایمان والو! اگر تم نے اہل کتاب میں سے کسی گروہ کی بات مان لی تو یہ تمہیں ایمان کے بعد کفر کی طرف پلٹا دیں گے۔'),
(394, 3, 101, 'اور بھلا تم کیوں کر کفر اختیار کر سکتے ہو۔ جبکہ تمہارے سامنے برابر خدا کی آیتیں پڑھی جا رہی ہیں اور تمہارے درمیان اس کا رسول موجود ہے۔ اور جو شخص خدا سے وابستہ ہوگیا وہ ضرور سیدھے راستے پر لگا دیا گیا۔'),
(395, 3, 102, 'اے ایمان والو! خدا سے اس طرح ڈرو جیساکہ ڈرنے کا حق ہے اور ہرگز نہ مرو مگر اس حالت میں کہ تم مسلمان ہو۔'),
(396, 3, 103, 'اور سب مل کر اللہ کی رسی کو مضبوطی سے تھام لو اور آپس میں تفرقہ پیدا نہ کرو اور اللہ کے اس احسان کو یاد کرو جو اس نے تم پر کیا ہے کہ تم آپس میں دشمن تھے اس نے تمہارے دلوں میں الفت پیدا کر دی اور تم اس کی نعمت (فضل و کرم) سے بھائی بھائی ہوگئے۔ اور تم آگ کے بھرے ہوئے گڑھے (دوزخ) کے کنارے پر کھڑے تھے جو اس نے تمہیں اس سے بچا لیا اس طرح اللہ تمہارے لیے اپنی نشانیاں ظاہر کرتا ہے تاکہ تم ہدایت پا جاؤ۔'),
(397, 3, 104, 'اور تم میں سے ایک گروہ ایسا ہونا چاہیے جو نیکی کی دعوت دے اور اچھے کاموں کا حکم دے اور برے کاموں سے منع کرے یہی وہ لوگ ہیں (جو دین و دنیا کے امتحان میں) کامیاب و کامران ہوں گے۔'),
(398, 3, 105, 'اور خبردار تم ان لوگوں کی طرح نہ بننا جو انتشار کا شکار ہوگئے اور کھلی ہوئی نشانیوں (دلیلوں) کے آجانے کے بعد اختلاف میں مبتلا ہوگئے۔ یہی وہ لوگ ہیں جن کے لیے بڑا عذاب ہے۔'),
(399, 3, 106, 'جس دن کچھ چہرے سفید (نورانی) ہوں گے اور کچھ چہرے سیاہ ہوں گے۔ تو جن کے چہرے سیاہ ہوں گے (ان سے کہا جائے گا) کہ تم نے ہی ایمان لانے کے بعد کفر کیا تھا؟ تو اب اپنے کفر کے نتیجہ میں عذاب کا مزا چکھو۔'),
(400, 3, 107, 'اور جن کے چہرے سفید (نورانی) ہوں گے وہ خدا کی رحمت میں ہوں گے اور ہمیشہ ہمیشہ اس میں رہیں گے۔'),
(401, 3, 108, 'یہ آیاتِ الٰہیہ ہیں جو ہم سچائی کے ساتھ تمہیں پڑھ کر سنا رہے ہیں کیونکہ خدا جہان والوں پر ظلم کرنے کا ارادہ بھی نہیں کرتا۔'),
(402, 3, 109, 'اور جو کچھ آسمانوں میں ہے اور زمین میں ہے۔ سب اللہ کا ہے اور سب معاملات کی بازگشت اللہ ہی کی طرف ہوتی ہے۔'),
(403, 3, 110, 'تم بہترین امت ہو جسے لوگوں (کی راہنمائی) کے لیے پیدا کیا گیا ہے۔ تم اچھے کاموں کا حکم دیتے ہو اور برے کاموں سے منع کرتے ہو۔ اور اللہ پر ایمان رکھتے ہو۔ اور اگر اہل کتاب بھی ایمان لے آتے تو ان کے لئے بہتر ہوتا، ان میں سے کچھ تو ایمان لائے ہیں مگر بہت سے فاسق و نافرمان ہیں۔'),
(404, 3, 111, 'یہ معمولی اذیت کے سوا ہرگز تمہیں کوئی نقصان نہیں پہنچا سکتے۔ اور اگر یہ تم سے جنگ کریں گے تو تمہیں پیٹھ دکھائیں گے پھر ان کی (کہیں سے) مدد نہ کی جائے گی۔'),
(405, 3, 112, 'یہ جہاں بھی پائے جائیں ان پر ذلت کی مار پڑ گئی مگر یہ کہ خدا کے کسی عہد پر یا انسانوں کے کسی معاہدہ کے سہارے کچھ پناہ مل جائے۔ اور یہ خدا کے قہر و غضب میں گھر گئے ہیں اور ان پر محتاجی کی مار پڑی یہ سب کچھ اس لئے ہے کہ وہ آیاتِ الٰہی کے ساتھ کفر کرتے رہے اور ناحق نبیوں کو قتل کرتے رہے نیز اس لئے کہ (خدا کی) نافرمانی اور زیادتیاں کیا کرتے تھے۔'),
(406, 3, 113, 'یہ لوگ سب برابر نہیں ہیں۔ اہل کتاب میں ایسی ثابت قدم جماعت بھی ہے جو رات کے مختلف اوقات میں آیاتِ الٰہی کی تلاوت کرتی ہے۔ اور سجدہ ریز ہوتی ہے۔'),
(407, 3, 114, 'اللہ اور روزِ آخرت پر ایمان رکھتے ہیں نیکی کا حکم دیتے ہیں اور برائی سے منع کرتے ہیں اور بھلائی کے کاموں میں تیزی کرتے ہیں اور یہ لوگ نیک لوگوں میں سے ہیں۔'),
(408, 3, 115, 'یہ جو نیکی بھی کریں گے اس کی ہرگز ناقدری نہیں کی جائے گی۔ اور خدا پرہیزگاروں کو خوب جانتا ہے۔'),
(409, 3, 116, 'بے شک جن لوگوں نے کفر اختیار کیا انہیں ان کے اموال اور اولاد اللہ (کی گرفت) سے کچھ بھی نہیں بچا سکیں گے۔ وہ تو دوزخی ہیں اور وہ اس میں ہمیشہ ہمیشہ رہیں گے۔'),
(410, 3, 117, 'یہ لوگ جو کچھ اس دنیوی زندگی میں خرچ کرتے ہیں اس کی مثال اس ہوا کی سی ہے جس میں سخت سردی ہو اور وہ ایسے لوگوں کی کھیتی پر چلے جنہوں نے اپنے اوپر ظلم کیا ہے۔ اور اسے تباہ و برباد کرکے رکھ دے۔ اللہ نے ان پر کوئی ظلم نہیں کیا۔ بلکہ وہ خود اپنے اوپر ظلم کرتے ہیں۔'),
(411, 3, 118, 'اے ایمان والو! اپنے (لوگوں کے) سوا دوسرے ایسے لوگوں کو اپنا جگری دوست (رازدار) نہ بناؤ جو تمہیں نقصان پہنچانے میں کوئی کسر اٹھا نہیں رکھتے۔ جو چیز تمہیں مصیبت و زحمت میں مبتلا کرے وہ اسے محبوب رکھتے ہیں بغض و عناد ان کے مونہوں سے ٹپکتا ہے۔ اور جو کچھ ان کے سینے چھپائے ہوئے ہیں وہ اس سے کہیں بڑھ کر ہے۔ ہم نے تمہارے لئے نشانیاں کھول کر بیان کر دی ہیں اگر تم عقل سے کام لو۔'),
(412, 3, 119, 'تم ایسے (سیدھے) ہو کہ تم ان سے محبت کرتے ہو مگر وہ تم سے محبت نہیں کرتے۔ حالانکہ تم (آسمانی) کتاب پر ایمان رکھتے ہو۔ (ان کی حالت یہ ہے کہ) جب تم سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لائے۔ اور جب اکیلے ہوتے ہیں تو تمہارے خلاف غیظ و غضب سے اپنی انگلیاں چبانے لگتے ہیں۔ ان سے کہہ دو کہ اپنے غم و غصہ سے مر جاؤ۔ بے شک اللہ سینے کے اندر والی باتوں کو خوب جانتا ہے۔'),
(413, 3, 120, 'جب تمہیں کوئی بھلائی چھو بھی جائے تو ان کو برا لگتا ہے اور اگر تمہیں کوئی برائی پہنچے تو وہ اس سے خوش ہوتے ہیں اور اگر تم صبر سے کام کرو۔ اور پرہیزگاری اختیار کرو تو ان کی ترکیبیں اور چالیں تمہیں کچھ نقصان نہیں پہنچائیں گی۔ بے شک جو کچھ یہ لوگ کر رہے ہیں خدا اس کا علمی احاطہ کئے ہوئے ہے۔'),
(414, 3, 121, 'اور وہ موقع (یاد رکھنے کے لائق ہے کہ) جب تم صبح سویرے اپنے گھر سے نکلے تھے اور (احد کے مقام پر) مؤمنوں کو جنگی مورچوں پر بٹھا رہے تھے اور جگہ دے رہے تھے اور اللہ بڑا سننے والا اور بڑا جاننے والا ہے۔'),
(415, 3, 122, 'جب تم میں سے دو گروہوں نے بزدلی دکھانے کا ارادہ کیا (مگر پھر سنبھل گئے) حالانکہ اللہ ان کا حامی و مددگار تھا اور مؤمنوں کو اللہ پر ہی بھروسہ کرنا چاہیے۔'),
(416, 3, 123, 'بے شک (اس سے پہلے) جنگ بدر میں اللہ تمہاری مدد کر چکا تھا حالانکہ تم (اس وقت) بہت کمزور تھے سو اللہ کی نافرمانی سے ڈرو۔ تاکہ تم شکرگزار بنو۔'),
(417, 3, 124, '(اے پیغمبر(ص)) یاد کرو۔ جب تم مؤمنوں سے کہہ رہے تھے کہ کیا یہ بات تمہارے (اطمینان) کے لئے کافی نہیں ہے۔ کہ تمہارا پروردگار تین ہزار فرشتوں سے تمہاری مدد کرے جو (آسمان سے) اتارے گئے ہوں۔'),
(418, 3, 125, 'ہاں کیوں نہیں! اگر تم صبر کرو اور تقویٰ الٰہی اختیار کرو۔ اور وہ (دشمن جوش میں آکر) اس وقت فوری طور پر تم پر چڑھ آئے تو تمہارا پروردگار پانچ ہزار نشان زدہ فرشتوں سے تمہاری مدد کرے گا۔'),
(419, 3, 126, 'اللہ نے اس (غیبی امداد) کو قرار نہیں دیا۔ مگر اس لئے کہ تمہارے لئے خوشخبری ہو۔ اور تمہارے دل مطمئن ہو جائیں اور (یہ بات تو واضح ہے کہ) مدد اور فتح و نصرت اللہ ہی کی طرف سے ہے جو زبردست ہے اور بڑی حکمت والا ہے۔'),
(420, 3, 127, '(اور یہ امداد اس لئے ہے) تاکہ کافروں کے ایک بڑے حصہ کو کاٹ دے (قلع قمع کر دے) یا ان کو ایسا ذلیل کرے کہ وہ ناکام و نامراد ہوکر واپس چلے جائیں۔'),
(421, 3, 128, 'اے رسول(ص)! خدا کے امر (فیصلہ) میں تمہیں کوئی اختیار نہیں ہے (بلکہ یہ اختیار اللہ کے پاس ہے) چاہے تو ان کی توبہ قبول کرے اور چاہے تو انہیں سزا دے۔ کیونکہ یہ لوگ بہرحال ظالم ہیں۔'),
(422, 3, 129, 'اور جو کچھ آسمانوں میں ہے اور جو کچھ زمینوں میں ہے سب اللہ ہی کا ہے۔ وہ جسے چاہتا ہے بخش دیتا ہے۔ اور جسے چاہتا ہے سزا دیتا ہے اور اللہ بڑا بخشنے والا مہربان ہے۔'),
(423, 3, 130, 'اے ایمان والو! یہ دوگنا چوگنا بڑھتا چڑھتا سود نہ کھاؤ (اللہ کی نافرمانی) سے ڈرو تاکہ تم فلاح پاؤ۔'),
(424, 3, 131, 'اور اس آگ سے بچو جو کافروں کے لئے تیار کی گئی ہے۔'),
(425, 3, 132, 'اور اللہ اور رسول(ص) کی فرمانبرداری کرو تاکہ تم پر رحم کیا جائے۔'),
(426, 3, 133, 'اور تیزی سے دوڑو اپنے پروردگار کی طرف سے بخشش۔ اور اس کے بہشت کی طرف جس کی چوڑائی تمام آسمانوں اور زمین کے برابر ہے جو پرہیزگاروں کے لئے تیار کیا گیا ہے۔'),
(427, 3, 134, 'وہ پرہیزگار جو خوشحالی اور بدحالی (غرضیکہ ہر حال میں راہِ خدا میں مال) خرچ کرتے ہیں جو غصے کو پی جاتے ہیں اور لوگوں (کے قصور) معاف کر دیتے ہیں۔ اور اللہ بھلائی کرنے والوں سے محبت کرتا ہے۔'),
(428, 3, 135, 'یہ لوگ اگر (اتفاقاً) کوئی فحش کام (کوئی بڑا برا کام) کر بیٹھیں یا کوئی عام گناہ کرکے اپنے اوپر ظلم کر گذریں تو (فوراً) اللہ کو یاد کرکے اس سے اپنے گناہوں کی مغفرت طلب کرتے ہیں اور اللہ کے سوا کون ہے جو گناہوں کو معاف کرے۔'),
(429, 3, 136, 'اور وہ اپنے کئے پر دیدہ دانستہ اصرار نہیں کرتے ایسے لوگوں کی جزاء ان کے پروردگار کی طرف سے مغفرت و بخشش ہے اور وہ جنات (باغات) کہ جن کے نیچے سے نہریں جاری ہیں۔ وہ ہمیشہ ان میں رہیں گے اور کتنا اچھا صلہ ہے نیک عمل کرنے والوں کا۔'),
(430, 3, 137, 'تم سے پہلے بہت سے نمونے (اور دور) گزر چکے ہیں سو زمین میں چل پھر کر دیکھو کہ (احکام خداوندی) جھٹلانے والوں کا کیا انجام ہوا؟ ۔'),
(431, 3, 138, 'یہ (عام) لوگوں کے لیے تو واضح تذکرہ اور تنبیہ ہے اور پرہیزگاروں کے لیے ہدایت اور نصیحت ہے۔'),
(432, 3, 139, 'اے مسلمانو! کمزوری نہ دکھاؤ اور غمگین نہ ہو اگر تم مؤمن ہو تو تم ہی غالب و برتر ہوگے۔'),
(433, 3, 140, 'اگر تمہیں زخم لگا ہے تو اس جیسا زخم ان لوگوں (کافروں) کو بھی (جنگ بدر میں) لگ چکا ہے۔ یہ تو ایام (زمانہ کے اتفاقات اور نشیب و فراز) ہیں جنہیں ہم لوگوں میں باری باری ادلتے بدلتے رہتے ہیں (تمہیں یہ چوٹ اس لئے لگی) کہ خدا (خالص) ایمان والوں کو جان لے۔ اور بعض کو (چھانٹ کر) شہید (گواہ) بنائے اور اللہ ظالموں کو دوست نہیں رکھتا۔'),
(434, 3, 141, 'نیز اللہ اس (ابتلا و آزمائش) سے یہ چاہتا تھا کہ خالص اہل ایمان کو چھانٹ کر الگ کر دے اور کافروں کو رفتہ رفتہ مٹا دے۔'),
(435, 3, 142, '(اے مسلمانو!) کیا تم کو یہ خیال ہے کہ تم یونہی جنت میں داخل ہو جاؤگے حالانکہ ابھی تک اللہ نے (جانچ کر) معلوم ہی نہیں کیا کہ تم میں واقعی مجاہد کون ہیں؟ اور نہ ابھی یہ معلوم کیا ہے کہ صابر اور ثابت قدم کون ہیں؟ ۔'),
(436, 3, 143, 'اور تم لوگ موت کا سامنا کرنے سے پہلے تو اس کی بہت تمنا کیا کرتے تھے۔ لو اب تم نے اسے دیکھ لیا۔ اور اب بھی (اپنی آنکھوں سے دیکھ رہے ہو) (تو پھر اس سے کنّی کیوں کتراتے ہو؟)۔'),
(437, 3, 144, 'اور حضرت محمد (ص) نہیں ہیں مگر پیغمبر جن سے پہلے تمام پیغمبر گزر چکے ہیں تو کیا اگر وہ وفات پا جائیں یا قتل کر دیئے جائیں تو تم الٹے پاؤں (کفر کی طرف) پلٹ جاؤگے اور جو کوئی الٹے پاؤں پھرے گا تو وہ ہرگز اللہ تعالیٰ کو کوئی نقصان نہیں پہنچائے گا اور عنقریب خدا شکرگزار بندوں کو جزا دے گا۔'),
(438, 3, 145, 'کوئی ذی روح خدا کے حکم کے بغیر مر نہیں سکتا موت کا وقت تو لکھا ہوا (معیّن) ہے اور جو شخص (اپنے اعمال کا) بدلہ دنیا میں چاہتا ہے تو ہم اسی (دنیا) میں دے دیتے ہیں اور جو آخرت میں بدلہ چاہتا ہے تو ہم اسے آخرت میں دیتے ہیں اور ہم عنقریب شکرگزار بندوں کو جزا (خیر) عطا کریں گے۔'),
(439, 3, 146, 'اور بہت سے ایسے نبی (گزر چکے) ہیں جن کے ساتھ مل کر بہت سے اللہ والوں نے جنگ کی تو اللہ کی راہ میں ان پر جو مصیبتیں پڑیں ان پر وہ نہ پست ہمت ہوئے اور نہ انہوں نے کمزوری دکھائی اور نہ (دشمن کے سامنے) سرنگوں ہوئے اور اللہ صبر و تحمل رکھنے والے (ثابت قدموں) سے محبت رکھتا ہے۔'),
(440, 3, 147, '(ایسے مواقع پر) ان کا قول اس (دعا) کے سوا کچھ نہیں تھا کہ اے ہمارے پروردگار ہمارے گناہ اور اپنے کام میں ہماری زیادتی معاف فرما اور ہمیں ثابت قدم رکھ اور ہمیں کافروں پر فتح و نصرت عطا فرما۔'),
(441, 3, 148, 'تو خدا نے ان کو دنیا میں بھی صلہ عطا کیا اور آخرت کا بہترین ثواب بھی عنایت فرمایا۔ اور اللہ نیک کام کرنے والوں کو دوست رکھتا ہے۔'),
(442, 3, 149, 'اے ایمان والو! اگر تم نے ان لوگوں کی اطاعت کی جنہوں نے کفر اختیار کیا تو وہ تم کو الٹے پاؤں (کفر کی طرف) پھیر کر لے جائیں گے اور تم بڑا خسارہ اٹھا کر واپس ہوگے۔'),
(443, 3, 150, '(تمہیں کسی کی اطاعت کی کیا ضرورت ہے) بلکہ تمہارا حامی و سرپرست خدا ہے اور وہ بہترین مددگار ہے۔'),
(444, 3, 151, '(تم پریشان نہ ہو) ہم عنقریب کافروں کے دلوں میں (تمہارا) رعب ڈال دیں گے اس لئے کہ انہوں نے خدائی میں ان بتوں کو شریک بنایا ہے۔ جن کے بارے میں خدا نے کوئی دلیل نازل نہیں کی ہے اور ان کا انجام جہّنم ہوگا اور وہ ظالمین کا بدترین ٹھکانہ ہے۔'),
(445, 3, 152, 'خدا نے (جنگ احد میں) اپنا وعدہ (نصرت) اس وقت سچا کر دکھایا جب تم اس کے حکم سے ان (کافروں) کا قلع قمع کر رہے تھے یہاں تک کہ جب تم نے کمزوری دکھائی تو تم نے حکم عدولی کی۔ (یہ اس لئے کہ) تم میں کچھ دنیا کے طلب گار تھے اور کچھ آخرت کے طلبگار تھے پھر اس نے تمہیں ان کے مقابلے میں پسپا کر دیا تاکہ تمہارے ایمان و اخلاص کی آزمائش کرے۔ اور (پھر بھی) تمہیں معاف کر دیا۔ اور اللہ اہل ایمان پر بڑا فضل کرنے والا ہے۔'),
(446, 3, 153, '(اس وقت کو یاد کرو) جب تم بے تحاشا بھاگے چلے جا رہے تھے اور کسی کی طرف مڑ کر بھی نہیں دیکھتے تھے۔ حالانکہ پیغمبر(ص) تمہارے پیچھے سے تمہیں پکار رہے تھے۔ (تمہاری اس روش کی وجہ سے) خدا نے تمہیں ثواب کے بدلے رنج پر رنج دیا۔ تاکہ (آئندہ) جو چیز تمہارے ہاتھ سے نکل جائے اس پر ملول نہ ہو اور جو مصیبت درپیش ہو اس پر رنج نہ کرو۔ اور اللہ تمہارے سب اعمال سے خوب باخبر ہے۔'),
(447, 3, 154, 'پھر اس (خدا) نے رنج و غم کے بعد نیند کی صورت میں تم پر سکون و اطمینان اتارا۔ جو تم میں سے ایک گروہ پر طاری ہوگئی۔ اور ایک گروہ ایسا تھا کہ جسے صرف اپنی جانوں کی فکر تھی وہ اللہ کے ساتھ ناحق زمانۂ جاہلیت والے گمان کر رہا تھا وہ کہہ رہا تھا۔ کہ آیا اس معاملہ میں ہمیں بھی کچھ اختیار ہے؟ کہہ دیجیے۔ ہر امر کا اختیار صرف اللہ کو ہے۔ یہ لوگ اپنے دلوں میں ایسی باتیں چھپائے ہوئے ہیں۔ جن کا آپ سے اظہار نہیں کرتے کہتے ہیں کہ اگر ہمارے ہاتھ میں بھی کچھ اختیار ہوتا تو ہم یہاں مارے نہ جاتے۔ کہہ دیجیے! اگر تم لوگ اپنے گھروں میں بھی ہوتے تو بھی جن کے لئے قتل ہونا لکھا جا چکا تھا وہ ضرور اپنے مقتل کی طرف نکل کر جاتے (یہ سب کچھ اس لئے ہوا) کہ خدا اسے آزمائے جو کچھ تمہارے سینوں کے اندر ہے اور نکھار کے سامنے لائے اس (کھوٹ) کو جو تمہارے دلوں میں ہے اور اللہ سینوں کے اندر کی باتوں کا خوب جاننے والا ہے۔'),
(448, 3, 155, 'بے شک جن لوگو ں نے دو جماعتوں کی مڈبھیڑ کے دن پیٹھ پھرائی (اس کا سبب یہ تھا) کہ ان کی بعض بدعملیوں کے نتیجہ میں جو وہ کر بیٹھے تھے شیطان نے ان کے قدم ڈگمگائے تھے اور بے شک اللہ نے انہیں معاف کر دیا۔ یقینا اللہ بڑا بخشنے والا نہایت بردبار ہے۔'),
(449, 3, 156, 'اے ایمان والو! کافروں کی طرح نہ ہو جاؤ جو اپنے ان بھائی بندوں کے بارے میں کہتے ہیں جو سفر میں گئے۔ یا جہاد کے لیے نکلے (اور وہاں وفات پا گئے) کہ اگر وہ ہمارے پاس ہوتے تو نہ (طبعی موت) مرتے اور نہ قتل کئے جاتے۔ یہ لوگ یہ بات اس لئے کہتے ہیں کہ خدا اسے ان کے دلوں میں رنج و حسرت کا باعث بنا دے۔ حالانکہ اللہ ہی زندہ رکھتا ہے اور مارتا ہے۔ اور تم جو کچھ بھی کرتے ہو اللہ اسے خوب دیکھ رہا ہے۔'),
(450, 3, 157, 'اور اگر تم راہِ خدا میں قتل کئے جاؤ۔ یا اپنی موت سے مر جاؤ۔ تو بے شک اللہ کی بخشش اور اس کی رحمت بہت ہی بہتر ہے اس (مال و دولت) سے جو لوگ جمع کرتے ہیں۔'),
(451, 3, 158, 'اور تم اپنی موت مرو۔ یا قتل کئے جاؤ۔ بہرحال اللہ کے ہی حضور میں محشور کئے جاؤگے۔'),
(452, 3, 159, '(اے رسول(ص)) یہ اللہ کی بہت بڑی مہربانی ہے کہ تم ان لوگوں کے لیے اتنے نرم مزاج ہو۔ ورنہ اگر تم درشت مزاج اور سنگدل ہوتے تو یہ سب آپ کے گرد و پیش سے منتشر ہو جاتے۔ انہیں معاف کر دیا کریں۔ ان کے لیے دعائے مغفرت کیا کریں اور معاملات میں ان سے مشورہ بھی لے لیا کریں۔ مگر جب کسی کام کے کرنے کا حتمی ارادہ ہو جائے تو پھر خدا پر بھروسہ کریں بے شک اللہ بھروسہ کرنے والوں کو دوست رکھتا ہے۔'),
(453, 3, 160, 'اگر اللہ تمہاری مدد کرے۔ تو کوئی بھی تم پر غالب نہیں آسکتا اور اگر وہ تمہیں چھوڑ دے (مدد نہ کرے) تو اس کے بعد کون ہے جو تمہاری مدد کرے اور اہل ایمان کو صرف خدا پر ہی بھروسہ کرنا چاہیے۔'),
(454, 3, 161, 'کسی نبی کی یہ شان اور کام نہیں ہے کہ وہ خیانت کرے۔ اور جو خیانت کرے گا۔ وہ اپنی خیانت کردہ چیز سمیت قیامت کے دن حاضر ہو جائے گا۔ پھر ہر ایک شخص کو اس کی کمائی کا پورا پورا بدلہ دیا جائے گا اور ان پر ظلم نہیں ہوگا۔'),
(455, 3, 162, 'بھلا وہ شخص جو ہمیشہ خدا کی رضا پر چلنے والا ہو۔ وہ اس شخص کی مانند ہو سکتا ہے جو خدا کے غضب میں گھر گیا ہو۔ اور جس کا آخری ٹھکانہ دوزخ ہو اور وہ کیا برا ٹھکانہ ہے؟ ۔'),
(456, 3, 163, '(ان دو قسم کے لوگوں کے) اللہ کے یہاں (مختلف درجے ہیں)۔ اور وہ جو کچھ کرتے ہیں اللہ اسے خوب دیکھ رہا ہے۔'),
(457, 3, 164, 'اللہ نے ہمیشہ اہل ایمان پر یہ بہت بڑا احسان کیا ہے کہ ان میں انہی میں سے ایک پیغمبر بھیجا۔ جو ان کے سامنے آیات الٰہی کی تلاوت کرتا ہے۔ ان کو پاکیزہ کرتا ہے (ان کی اصلاح کرتا ہے) اور انہیں کتاب و حکمت کی تعلیم دیتا ہے اگرچہ وہ اس سے پہلے کھلی ہوئی گمراہی میں تھے۔'),
(458, 3, 165, '(اے مسلمانو! تمہارا کیا حال ہے) کہ جب تم پر (جنگ احد میں) کوئی ایسی مصیبت آپڑی جس سے دگنی مصیبت تمہاری اپنی لائی ہوئی ہے۔ اللہ ہر چیز پر قادر ہے۔'),
(459, 3, 166, 'اور جو دو جماعتوں کی مڈبھیڑ والے دن (جنگ احد میں) تم پر جو مصیبت آئی وہ خدا کے اذن سے آئی تاکہ اللہ دیکھ لے (مخلص) مؤمن کون ہیں۔'),
(460, 3, 167, 'اور منافق کون؟ جن سے جب کہا گیا کہ آؤ اللہ کی راہ میں جنگ کرو۔ یا (کم از کم) دفاع ہی کرو۔ تو انہوں نے کہا۔ کہ اگر ہمیں علم ہوتا کہ جنگ ہوگی تو تمہارے پیچھے آتے جب وہ یہ بات کہہ رہے تھے اس وقت وہ بہ نسبت ایمان کے کفر کے زیادہ قریب تھے۔ وہ اپنے منہ سے وہ باتیں کہتے ہیں جو ان کے دلوں میں نہیں ہوتیں۔ اور جو کچھ وہ چھپاتے ہیں اللہ اسے خوب جانتا ہے۔'),
(461, 3, 168, 'یہی وہ لوگ ہیں جو خود تو (گھروں میں) بیٹھے رہے مگر اپنے (شہید ہونے والے) بھائی بندوں کے بارے میں کہا کہ اگر یہ لوگ ہماری پیروی کرتے (اور جہاد کرنے نہ جاتے) تو مارے نہ جاتے۔ (اے رسول) ان سے کہو۔ کہ اگر تم سچے ہو تو جب خود تمہاری موت آئے تو اسے ٹال کر دکھا دینا۔'),
(462, 3, 169, 'اور جو لوگ اللہ کی راہ میں مارے گئے ہیں انہیں ہرگز مردہ نہ سمجھو بلکہ وہ زندہ ہیں اپنے پروردگار کے یہاں رزق پا رہے ہیں۔'),
(463, 3, 170, 'اللہ نے اپنے فضل و کرم سے انہیں جو کچھ دیا ہے وہ اس پر خوش و خرم ہیں۔ اور اپنے ان پسماندہ گان کے بارے میں بھی جو ہنوز ان کے پاس نہیں پہنچے خوش اور مطمئن ہیں کہ انہیں کوئی خوف نہیں ہے۔ اور نہ کوئی حزن و ملال ہے۔'),
(464, 3, 171, 'وہ اللہ کے فضل و انعام پر خوش اور شاداں ہیں اور اس بات پر فرحاں ہیں کہ اللہ اہل ایمان کے اجر و ثواب کو ضائع و برباد نہیں کرتا۔'),
(465, 3, 172, 'اور جن لوگوں نے زخم کھانے کے بعد بھی اللہ اور رسول کی آواز پر لبیک کہی۔ ان میں سے جو نیکوکار اور پرہیزگار ہیں ان کے لیے بڑا اجر و ثواب ہے۔'),
(466, 3, 173, 'وہ کہ جن سے لوگوں نے کہا کہ لوگوں نے تمہارے خلاف بڑا لشکر جمع کیا ہے لہٰذا تم ان سے ڈرو۔ تو اس بات نے ان کے ایمان میں اور اضافہ کر دیا۔ اور انہوں نے کہا کہ ہمارے لئے اللہ کافی ہے اور وہ بڑا اچھا کارساز ہے۔'),
(467, 3, 174, 'پس یہ لوگ اللہ کی عنایت اور اس کے فضل و کرم سے اس طرح (اپنے گھروں کی طرف) لوٹے کہ انہیں کسی قسم کی تکلیف نے چھوا بھی نہیں تھا۔ اور وہ رضاءِ الٰہی کے تابع رہے اور اللہ بڑے فضل و کرم والا ہے۔'),
(468, 3, 175, 'دراصل یہ تمہارا شیطان تھا جو تمہیں اپنے حوالی موالی (دوستوں) سے ڈراتا ہے۔ اور تم ان سے نہ ڈرو اور صرف مجھ سے ڈرو۔ اگر سچے مؤمن ہو۔'),
(469, 3, 176, '(اے رسول) وہ لوگ جو کفر کی راہ میں بڑی بھاگ دوڑ کر رہے ہیں وہ تمہیں غمزدہ نہ کریں یہ اللہ کا کچھ نہیں بگاڑ سکیں گے۔ اللہ کا ارادہ ہے کہ آخرت میں ان کے لئے کوئی حصہ نہ رکھے۔ اور ان کے لئے بہت بڑا عذاب ہے۔'),
(470, 3, 177, 'بے شک جن لوگوں نے ایمان کے بدلے کفر خرید لیا ہے وہ ہرگز اللہ کو کچھ نقصان نہیں پہنچائیں گے اور ان کے لیے دردناک عذاب (تیار) ہے۔'),
(471, 3, 178, 'اور کافر یہ ہرگز خیال نہ کریں کہ ہم جو انہیں ڈھیل دے رہے ہیں (ان کی رسی دراز رکھتے ہیں) یہ ان کے لیے کوئی اچھی بات ہے یہ ڈھیل تو ہم صرف اس لئے انہیں دے رہے ہیں کہ وہ زیادہ گناہ کر لیں۔ (آخرکار) ان کے لیے رسوا کرنے والا عذاب ہے۔'),
(472, 3, 179, 'اللہ مؤمنوں کو اس حال پر نہیں چھوڑے گا جس حال پر تم اب ہو۔ جب تک وہ ناپاک کو پاک سے الگ نہ کر دے اور اللہ کی یہ شان نہیں ہے کہ تمہیں غیب پر مطلع کرے۔ البتہ اللہ (غیب کی باتیں بتانے کے لیے) اپنے رسولوں میں سے جسے چاہتا ہے منتخب کرتا ہے۔ سو تم اللہ اور اس کے رسولوں پر ایمان لاؤ۔ اور اگر تم ایمان لاؤ۔ اور پرہیزگاری اختیار کرو تو تمہارے لئے بڑا اجر و ثواب ہے۔'),
(473, 3, 180, 'اور جن لوگوں کو اللہ نے اپنے فضل و کرم سے (بہت کچھ) دے رکھا ہے۔ اور وہ بخل کرتے ہیں وہ ہرگز یہ خیال نہ کریں کہ یہ (بخل) ان کے لیے اچھا ہے بلکہ یہ ان کے لیے بہت برا ہے۔ عنقریب قیامت کے دن اس مال کا طوق انہیں پہنایا جائے گا جس میں وہ بخل کر رہے ہیں۔ اور آسمانوں اور زمین کی میراث اللہ ہی کے لیے ہے اور جو کچھ تم کرتے ہو اللہ اس سے خوب خبردار ہے ۔'),
(474, 3, 181, 'بے شک اللہ نے ان لوگوں (یہودیوں) کا قول سن لیا ہے جنہوں نے کہا کہ خدا مفلس ہے اور ہم مالدار ہیں سو ان کی یہ باتیں ہم لکھ لیں گے نیز ان کا نبیوں کو ناحق قتل کرنا بھی لکھ لیں گے۔ اور (فیصلے کے وقت) ہم ان سے کہیں گے کہ اب آتش دوزخ کا مزہ چکھو۔'),
(475, 3, 182, 'یہ بدلہ ہے اس کا جو تمہارے ہاتھوں نے (زاد سفر کے طور پر) آگے بھیجا ہے اور یقینا اللہ اپنے بندوں پر ظلم کرنے والا نہیں ہے۔'),
(476, 3, 183, 'یہ وہ لوگ ہیں جو کہتے ہیں کہ اللہ نے ہم سے عہد و اقرار لیا ہے کہ ہم اس وقت تک کسی رسول پر ایمان نہ لائیں۔ جب تک وہ ہمارے سامنے ایسی قربانی پیش نہ کرے جسے (غیبی) آگ آکر کھا لے۔ ان سے کہیئے۔ مجھ سے پہلے تمہارے پاس بہت سے رسول(ص) روشن نشانیاں (معجزے) لے کر اور وہ (نشانی) بھی جو تم کہتے ہو لے کر آئے۔ تو اگر تم (اس شرط میں) سچے ہو تو تم نے ان کو کیوں قتل کیا تھا؟ ۔'),
(477, 3, 184, '(اے رسول) اگر یہ آپ کو جھٹلاتے ہیں تو (آزردہ خاطر نہ ہوں) آپ سے پہلے بہت سے رسول(ص) جھٹلائے جا چکے ہیں جو معجزات، صحیفے اور روشن کتاب (آئین حیات) لے کر آئے تھے۔'),
(478, 3, 185, 'ہر جاندار نے (انجام کار) موت کا مزہ چکھنا ہے۔ اور تم سب کو قیامت کے دن تمہیں (اپنے اعمال کا) پورا پورا بدلہ دیا جائے گا پس جو شخص (اس دن) جہنم سے بچا لیا گیا۔ اور جنت میں داخل کیا گیا وہ کامیاب ہوگیا۔ اور دنیا کی زندگی تو محض دھوکے کا سامان اور سرمایہ فریب ہے۔'),
(479, 3, 186, '(مسلمانو) ضرور تمہیں تمہارے مالوں اور جانوں کے بارے میں آزمایا جائے گا اور تمہیں اہل کتاب مشرکین سے بڑي دل آزار باتیں سننا پڑیں گی۔ اور اگر تم صبر و ضبظ سے کام لو اور تقویٰ اختیار کرو۔ تو بے شک یہ بڑی ہمت اور حوصلے کا کام ہے۔'),
(480, 3, 187, '(اے رسول(ص)) وہ وقت یاد کرو۔ جب خدا نے اہل کتاب سے عہد و پیمان لیا تھا کہ تم اسے ضرور لوگوں کے سامنے کھول کر بیان کروگے۔ اور اسے ہرگز نہیں چھپاؤگے مگر انہوں نے اسے اپنے پس پشت ڈال دیا۔ اور اس کے عوض تھوڑی سی قیمت وصول کرلی۔ کتنا برا کاروبار ہے جو یہ کر رہے ہیں۔'),
(481, 3, 188, 'آپ ان لوگوں کے بارے میں خیال کریں جو اپنی کارستانیوں پر اتراتے ہیں اور چاہتے ہیں کہ جو کام انہوں نے نہیں کیا اس پر بھی ان کی تعریف و ستائش کی جائے۔ ان کے بارے میں خیال نہ کرنا کہ وہ عذاب سے محفوظ ہیں (نہیں بلکہ) ان کے لیے دردناک عذاب (تیار) ہے۔'),
(482, 3, 189, 'اللہ کے لیے ہی ہے آسمانوں اور زمین کی حکومت (اور وہی ان کا مالک ہے) اور اللہ ہر چیز پر قادر ہے۔'),
(483, 3, 190, 'بے شک آسمانوں اور زمین کی پیدائش میں اور رات دن کی ادل بدل میں صاحبانِ عقل کے لیے بڑی نشانیاں ہیں۔'),
(484, 3, 191, 'جو اٹھتے، بیٹھتے اور پہلوؤں پر لیٹے ہوئے (برابر) اللہ کو یاد کرتے ہیں اور آسمانوں و زمین کی تخلیق و ساخت میں غور و فکر کرتے ہیں (اور بے ساختہ بول اٹھتے ہیں) اے ہمارے پروردگار! تو نے یہ سب کچھ فضول و بیکار پیدا نہیں کیا۔ تو (عبث کام کرنے سے) پاک ہے ہمیں دوزخ کے عذاب سے بچا۔'),
(485, 3, 192, 'اے ہمارے پروردگار! بے شک جسے تو نے دوزخ میں داخل کر دیا اسے تو نے ذلیل و رسوا کر دیا۔ اور ظالموں کے لئے کوئی یار و مددگار نہیں ہیں۔'),
(486, 3, 193, 'اے ہمارے پروردگار! ہمارے گناہوں کو بخش دے اور ہماری برائیاں مٹا اور دور فرما اور نیکوکاروں کے ساتھ ہمارا خاتمہ فرما۔'),
(487, 3, 194, 'پروردگارا! تو نے ہم سے اپنے رسولوں کی زبانی جو وعدہ کیا ہے وہ ہمیں عطا فرما قیامت کے دن ہم کو رسوا نہ فرما تو کبھی وعدہ خلافی نہیں کرتا۔'),
(488, 3, 195, 'سو ان کے پروردگار نے ان کی دعا و پکار کو قبول کر لیا (اور فرمایا) میں کسی عمل کرنے والے کے عمل کو خواہ مرد ہو یا عورت کبھی ضائع نہیں کرتا۔ تم سب ایک دوسرے کے ہم جنس ہی تو ہو۔ سو جن لوگوں نے ہجرت کی اور اپنے گھروں سے نکالے گئے۔ اور میری راہ میں انہیں ایذائیں دی گئیں۔ اور (دین کی خاطر) لڑے اور مارے گئے، تو میں ان کی برائیاں مٹا دوں گا اور ان کو ایسے بہشتوں میں داخل کروں گا جن کے نیچے نہریں بہتی ہوں گی۔ یہ ہے اللہ کے ہاں ان کی جزا۔ اور اللہ کے پاس بہترین ثواب و جزاء ہے۔'),
(489, 3, 196, '(اے رسول) کافروں کا مختلف شہروں میں چلنا پھرنا (اور دندناتے پھرنا) تمہیں ہرگز دھوکے میں نہ ڈال دے۔'),
(490, 3, 197, 'یہ صرف چند روزہ فائدہ (بہار) ہے۔ پھر ان کا ٹھکانہ دوزخ ہے اور وہ کیسی بُری آرام گاہ ہے۔'),
(491, 3, 198, 'مگر وہ لوگ جنہوں نے تقویٰ اختیار کیا (واجبات ادا کئے اور محرمات سے اجتناب کیا) ان کے لیے ایسے بہشت ہیں جن کے نیچے نہریں جاری ہیں وہ ان میں ہمیشہ رہیں گے یہ اللہ کی طرف سے ان کی مہمان نوازی ہوگی اور جو کچھ اس کے پاس ہے وہ نیکوکاروں کے لیے بہت اچھا ہے۔'),
(492, 3, 199, 'اور یقیناً اہل کتاب میں سے کچھ ایسے ہیں جو اللہ پر اور ان باتوں پر جو ہماری طرف اور جو ان کی طرف اتاری گئی ہیں پر ایمان رکھتے ہیں اللہ کے سامنے (نیازمندی سے) جھکے ہوئے ہیں اور آیاتِ الٰہیہ کو تھوڑی قیمت پر فروخت بھی نہیں کرتے یہ وہ ہیں جن کا اجر اپنے پروردگار کے پاس ہے بے شک اللہ بہت جلد حساب لینے والا ہے۔'),
(493, 3, 200, 'اے ایمان والو! صبر و تحمل سے کام لو۔ اور (کفار کے) مقابلہ میں پامردی دکھاؤ۔ اور (خدمت دین کے لیے) کمربستہ رہو۔ تاکہ تم فوز و فلاح پاؤ (اور دنیا و آخرت میں کامیاب ہو جاؤ)۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, '(اے انسانو!) اپنے اس پروردگار سے ڈرو۔ جس نے تمہیں ایک متنفس سے پیدا کیا اور اس کا جوڑا بھی اسی سے (اس کی جنس سے) پیدا کیا اور پھر انہی دونوں سے بہت سے مردوں اور عورتوں کو پھیلا دیا۔ اللہ سے ڈرو جس کا واسطہ دے کر ایک دوسرے سے سوال کرتے ہو۔ اور رشتہ قرابت کے بارے میں بھی ڈرو۔ بے شک اللہ تعالیٰ تم پر حاضر و ناظر ہے۔'),
(495, 4, 2, 'اور یتیموں کے مال ان کے سپرد کرو۔ اور پاک مال کے بدلے ناپاک مال حاصل نہ کرو۔ اور ان کے مالوں کو اپنے مال کے ساتھ ملا کر نہ کھاؤ۔ بے شک یہ بہت بڑا گناہ ہے۔'),
(496, 4, 3, 'اور اگر تمہیں یہ خوف ہو کہ تم یتیموں کے ساتھ انصاف نہیں کر سکوگے۔ تو جو عورتیں تمہیں پسند ہوں ان سے نکاح کر لو دو دو، تین تین، چار چار سے اور اگر تمہیں اندیشہ ہو کہ (ان کے ساتھ) عدل نہ کر سکوگے تو پھر ایک ہی (بیوی) کرو۔ یا جو تمہاری ملکیت میں ہوں (ان پر اکتفا کرو) یہ زیادہ قریب ہے اس کے کہ بے انصافی نہ کرو۔ (ایک ہی طرف نہ جھک جاؤ)۔'),
(497, 4, 4, 'اور عورتوں کے حق مہر خوشدلی سے ادا کرو اور اگر وہ اپنی خوشی سے اس میں سے تمہیں بخش دیں تو تم اسے شوق سے کھا سکتے ہو۔'),
(498, 4, 5, 'اور اپنے وہ مال جنہیں اللہ نے تمہاری زندگی کا سامان اور اسے قائم رکھنے کا ذریعہ بنایا ہے ناسمجھ لوگوں کے حوالے نہ کرو۔ ہاں البتہ ان کو (کھانا و طعام) کھلاؤ اور (کپڑا) پہناؤ اور ان سے اچھی گفتگو کرو۔'),
(499, 4, 6, 'اور یتیموں کی جانچ پڑتال کرتے رہو۔ یہاں تک کہ جب وہ نکاح کی عمر تک پہنچ جائیں اور تم ان میں اہلیت و سمجھداری بھی پاؤ تو پھر ان کے مال ان کے حوالے کر دو۔ اور فضول خرچی سے کام لے کر اور اس جلد بازی میں کہ وہ کہیں بڑے نہ ہو جائیں ان کا مال مت کھاؤ۔ اور جو سرپرست مالدار ہو تو اسے تو بہرحال ان کے مال سے پرہیز ہی کرنا چاہیے۔ اور جو نادار ہو اسے معروف (مناسب) طریقہ سے کھانا چاہیے۔ پھر جب ان کے مال ان کے حوالے کرو۔ تو ان لوگوں کو گواہ بنا لو۔ اور یوں تو حساب کے لیے اللہ ہی کافی ہے۔'),
(500, 4, 7, 'مردوں کے لیے اس ترکہ سے حصہ ہے جو ماں باپ اور رشتہ دار چھوڑ جائیں۔ اور عورتوں کے لیے بھی حصہ ہے اس ترکہ سے جو ماں باپ اور رشتہ دار چھوڑ جائیں خواہ وہ (ترکہ) تھوڑا ہو یا زیادہ یہ حصہ (خدا کی طرف سے) لازمی مقرر ہے۔'),
(501, 4, 8, 'اور جب تقسیم کے موقع پر رشتہ دار، یتیم اور مسکین موجود ہوں تو انہیں بھی اس (مال) میں سے کچھ دے دو اور ان سے درست اور مناسب طریقہ سے بات کرو۔'),
(502, 4, 9, 'اور ترکہ تقسیم کرنے والوں کو ڈرنا چاہیے کہ اگر وہ خود اپنے پیچھے بے بس و کمزور بچے چھوڑ جاتے۔ تو انہیں ان کی کس قدر فکر ہوتی۔ لہٰذا وہ دوسروں کے یتیموں کے بارے میں اللہ سے ڈریں اور بالکل درست اور سیدھی بات کریں'),
(503, 4, 10, 'بے شک جو لوگ ظلم کے ساتھ یتیموں کا مال کھاتے ہیں وہ اپنے پیٹوں میں آگ بھر رہے ہیں اور عنقریب بھڑکتی ہوئی آگ میں داخل ہوں گے (واصل جہنم ہوں گے۔)'),
(504, 4, 11, 'اللہ تمہاری اولاد کے بارے میں تمہیں ہدایت کرتا ہے کہ لڑکے کا حصہ برابر ہے دو لڑکیوں کے اب اگر لڑکیاں ہی وارث ہوں اور ہوں بھی دو سے اوپر تو ان کو دو تہائی ترکہ دیا جائے گا اور ایک لڑکی (وارث) ہو تو اسے آدھا ترکہ دیا جائے گا۔ اور اگر میت صاحب اولاد ہو تو اس کے والدین میں سے ہر ایک کے لئے ترکہ کا چھٹا حصہ ہوگا (اور باقی اولاد کا) اور اگر بے اولاد ہو۔ اور اس کے والدین ہی اس کے وارث ہوں تو اس کی ماں کو تیسرا حصہ ملے گا (باقی ۳/۲ باپ کو ملے گا) اور اگر اس (مرنے والے) کے بھائی (بہن) ہوں تو پھر اس کی ماں کو چھٹا حصہ ملے گا (باقی ۶/۵ بہن بھائیوں کو ملے گا) مگر یہ تقسیم اس وقت ہوگی، جب میت کی وصیت پوری کر دی جائے۔ جو وہ کر گیا ہو اور اس کا قرضہ ادا کر دیا جائے گا جو اس پر ہو۔ تم نہیں جانتے کہ تمہارے ماں باپ یا تمہاری اولاد میں سے نفع رسانی کے لحاظ سے کون تمہارے زیادہ قریب ہے۔ یہ حصے اللہ کی طرف سے مقرر ہیں، یقینا اللہ بڑا جاننے والا اور بڑا حکمت والا ہے۔'),
(505, 4, 12, 'اور جو ترکہ تمہاری بیویاں چھوڑ جائیں اس میں سے آدھا تمہارے لئے ہوگا اگر ان کی اولاد نہ ہو اور اگر ان کے اولاد ہو تو پھر تمہیں ان کے ترکہ کا چوتھا حصہ ملے گا۔ یہ تقسیم اس وصیت کو پورا کرنے کے بعد ہوگی، جو انہوں نے کی ہو اور اس قرضہ کی ادائیگی کے بعد جو ان کے ذمہ ہو۔ اور وہ (بیویاں) تمہارے ترکہ میں سے چوتھے حصہ کی حقدار ہوں گی۔ اگر تمہاری اولاد نہ ہو۔ اور اگر تمہارے اولاد ہو تو پھر ان کا آٹھواں حصہ ہوگا اور (یہ تقسیم) اس وصیت کو پورا کرنے کے بعد ہوگی جو تم نے کی ہو اور اس قرض کو ادا کرنے کے بعد جو تم نے چھوڑا ہو۔ اور اگر میت (جس کی وراثت تقسیم کی جانے والی ہے) کلالہ ہو یعنی مرد ہو یا عورت بے اولاد ہو اور اس کے ماں باپ نہ ہوں۔ مگر بھائی بہن ہوں تو اگر اس کا صرف ایک بھائی یا ایک بہن موجود ہو تو بھائی یا بہن کو چھٹا حصہ ملے گا۔ اور اگر بھائی بہن ایک سے زیادہ ہوں تو پھر وہ کل ترکہ کی ایک تہائی میں برابر کے شریک ہوں گے۔ مگر (یہ تقسیم) اس وصیت کو پورا کرنے اور قرضہ کے ادا کرنے کے بعد ہوگی جو کی گئی (جبکہ وصیت کرنے والا وارثوں کو) ضرر پہنچانے کے درپے نہ ہو۔ یہ اللہ کی طرف سے لازمی ہدایت ہے، اور اللہ بڑے علم و حلم والا ہے۔'),
(506, 4, 13, 'یہ اللہ کی مقرر کردہ حدیں ہیں جو خدا اور رسول کی اطاعت کرے گا اللہ اسے ان بہشتوں میں داخل کرے گا۔ جن کے نیچے سے نہریں جاری ہوں گی۔ جن میں وہ ہمیشہ ہمیشہ رہیں گے اور یہ بہت بڑی کامیابی ہے۔'),
(507, 4, 14, 'اور جو اللہ اور اس کے رسول کی نافرمانی کرے گا اور اس کی مقرر کردہ حدوں سے تجاوز کرے گا۔ تو اللہ اسے آتش دوزخ میں داخل کرے گا۔ جس میں وہ ہمیشہ رہے گا۔ اور اس کے لیے ذلیل کرنے والا عذاب ہے۔'),
(508, 4, 15, 'اور جو تمہاری عورتوں میں سے بدکاری کریں تو ان کی بدکاری پر اپنے میں سے چار آدمیوں کی گواہی لو۔ اور اگر وہ گواہی دے دیں تو انہیں گھروں میں بند کر دو یہاں تک کہ انہیں موت آجائے یا اللہ ان کے لیے کوئی اور راستہ مقرر کرے۔'),
(509, 4, 16, 'اور تم میں سے جو دو شخص (مرد و عورت) بدکاری کا ارتکاب کریں تو ان کو اذیت پہنچاؤ۔ پھر اگر وہ توبہ کر لیں اور اپنی اصلاح کرلیں۔ تو انہیں چھوڑ دو بے شک اللہ بہت توبہ قبول کرنے والا اور بہت رحم کرنے والا ہے۔'),
(510, 4, 17, 'توبہ قبول کرنے کا حق اللہ کے ذمہ صرف انہی لوگوں کا ہے جو جہالت (نادانی) کی وجہ سے کوئی برائی کرتے ہیں پھر جلدی توبہ کر لیتے ہیں یہ ہیں وہ لوگ جن کی توبہ خدا قبول کرتا ہے۔ اور اللہ بڑا جاننے والا اور بڑی حکمت والا ہے۔'),
(511, 4, 18, 'ان لوگوں کی توبہ (قبول) نہیں ہے۔ جو (زندگی بھر) برائیاں کرتے رہتے ہیں، یہاں تک کہ جب ان میں سے کسی کی موت کا وقت آجاتا ہے، تو وہ کہتا ہے، اس وقت میں توبہ کرتا ہوں۔ اور نہ ہی ان کے لیے توبہ ہے۔ جو کفر کی حالت میں مرتے ہیں۔ یہ وہ ہیں جن کے لیے ہم نے دردناک عذاب مہیا کر رکھا ہے۔'),
(512, 4, 19, 'اے ایمان والو! تمہارے لئے حلال نہیں ہے۔ کہ تم زبردستی عورتوں کے وارث بن جاؤ۔ اور نہ یہ جائز ہے۔ کہ ان پر سختی کرو اور روکے رکھو تاکہ جو کچھ تم نے (جہیز وغیرہ) انہیں دیا ہے اس میں سے کچھ حصہ لے اڑو مگر یہ کہ وہ صریح بدکاری کا ارتکاب کریں (کہ اس صورت میں سختی جائز ہے) اور عورتوں کے ساتھ عمدہ طریقہ سے زندگی گزارو۔ اور اگر تم انہیں ناپسند کرتے ہو تو ہو سکتا ہے کہ ایک چیز تمہیں پسند نہ ہو۔ مگر اللہ نے اس میں بہت کچھ بھلائی رکھ دی ہو۔'),
(513, 4, 20, 'اور اگر تم ایک بیوی کو بدل کر اس کی جگہ دوسری لانا چاہو اور تم ان میں سے ایک کو (جسے فارغ کرنا چاہتے ہو) بہت سا مال دے چکے ہو۔ تو اس میں سے کچھ بھی واپس نہ لو کیا تم جھوٹا الزام لگا کر اور صریح گناہ کرکے (واپس) لینا چاہتے ہو۔'),
(514, 4, 21, 'اور بھلا تم وہ (مال) کیسے واپس لے سکتے ہو۔ جبکہ تم (مقاربت کرکے) ایک دوسرے سے لطف اندوز ہو چکے ہو؟ اور وہ تم سے پختہ عہد و پیمان لی چکی ہیں۔'),
(515, 4, 22, 'جن عورتوں سے تمہارے باپ دادا نکاح کر چکے ہوں۔ ان سے شادی (نکاح) نہ کرو۔ سوا اس کے جو پہلے ہو چکا۔ بے شک یہ کھلی بے حیائی اور (خدا کی) ناراضی کی بات ہے اور بہت برا طریقہ ہے۔'),
(516, 4, 23, 'تم پر حرام کر دی گئی ہیں تمہاری مائیں، تمہاری بیٹیاں، تمہاری بہنیں، تمہاری پھوپھیاں، تمہاری خالائیں، بھتیجیاں، بھانجیاں اور تمہاری وہ مائیں جنہوں نے تمہیں دودھ پلایا ہے۔ اور تمہاری رضاعی (دودھ شریک) بہنیں اور تمہاری بیویوں کی مائیں (خوشدامنیں) اور تمہاری ان بیویوں کی جن سے تم نے مباشرت کی ہے وہ لڑکیاں جو تمہاری گودوں میں پرورش پا رہی ہیں اور اگر صرف نکاح ہوا ہے مگر مباشرت نہیں ہوئی ہے۔ تو پھر (ان کی لڑکیوں سے نکاح کرنے میں) کوئی مضائقہ نہیں ہے۔ اور تمہارے صلبی بیٹوں کی بیویاں (بہوؤیں) اور یہ بھی حرام قرار دیا گیا کہ دو بہنوں کو ایک نکاح میں اکھٹا کرو۔ مگر جو پہلے ہو چکا ہے۔ بے شک اللہ بڑا بخشنے والا اور بڑا رحم کرنے والا ہے۔'),
(517, 4, 24, 'اور وہ عورتیں بھی تم پر حرام ہیں، جو شوہردار ہوں۔ مگر وہ ایسی عورتیں جو (شرعی جہاد میں) تمہاری ملکیت میں آئیں۔ یہ آئین ہے، اللہ کا جو تم پر لازم ہے۔ اور ان کے علاوہ جو عورتیں ہیں وہ تمہارے لئے حلال ہیں کہ اپنے مال (حق مہر) کے عوض ان سے شادی کرلو۔ بدکاری سے بچنے اور پاکدامنی کو قائم رکھنے کے لیے اور ان عورتوں میں سے جن سے تم متعہ کرو۔ تو ان کی مقررہ اجرتیں ادا کر دو۔ اور اگر زرِ مہر مقرر کرنے کے بعد تم آپس میں (کم و بیش پر) راضی ہو جاؤ تو اس میں کوئی حرج نہیں ہے۔ بے شک اللہ بہت جاننے والا اور بڑی رحمت والا ہے۔'),
(518, 4, 25, 'اور تم میں سے جو شخص مالی حیثیت سے اس کی قدرت نہ رکھتا ہو کہ وہ (خاندانی) مسلمان آزاد عورتوں سے نکاح کر سکے۔ تو وہ ان مسلمان لونڈیوں سے (نکاح کرلے) جو تمہاری ملکیت میں ہوں اور اللہ تمہارے ایمان کو خوب جانتا ہے تم (اسلام کی حیثیت سے) سب ایک دوسرے سے ہو۔ پس ان (لونڈیوں) کے مالکوں کی اجازت سے نکاح کرو۔ اور بھلائی کے ساتھ ان کے حق مہر ادا کرو۔ تاکہ وہ (قید نکاح میں آکر) پاکدامن رہیں۔ اور شہوت رانی نہ کرتی پھریں۔ اور نہ ہی چوری چھپے آشنا بناتی پھریں۔ سو جب وہ قید نکاح میں محفوظ ہو جائیں تو اگر اب بدکاری کا ارتکاب کریں تو ان کے لئے آزاد عورتوں کی نصف سزا ہوگی۔ یہ رعایت (کہ خاندانی مسلمان آزاد عورت سے نکاح کرنے کی قدرت نہ ہو تو کنیز سے نکاح کرنے کا جواز) اس شخص کے لئے ہے جسے (نکاح نہ کرنے سے) بدکاری میں مبتلا ہونے کا اندیشہ ہو اور اگر ضبط سے کام لو تو یہ تمہارے لیے بہتر ہے اور اللہ بڑا بخشنے والا اور بڑا رحم کرنے والا ہے۔'),
(519, 4, 26, 'اللہ چاہتا ہے کہ (تم سے اپنے احکام) کھول کر بیان کر دے اور تمہیں ان لوگوں کے طریقوں پر چلائے جو تم سے پہلے گزرے ہیں اور تمہاری توبہ قبول کرے اللہ بڑا جاننے والا اور بڑی حکمت والا ہے۔'),
(520, 4, 27, 'اور اللہ تو چاہتا ہے کہ تمہاری توبہ قبول کرے مگر وہ لوگ جو خواہشات نفس کی پیروی کرتے ہیں وہ چاہتے ہیں کہ تم (راہ راست سے بھٹک کر) بہت دور نکل جاؤ۔'),
(521, 4, 28, 'اللہ چاہتا ہے کہ تمہارا بوجھ ہلکا کرے (کیونکہ) انسان کمزور پیدا کیا گیا ہے۔'),
(522, 4, 29, 'اے ایمان والو! آپس میں ایک دوسرے کے مال باطل طریقہ سے نہ کھاؤ۔ مگر یہ کہ تمہاری باہمی رضامندی سے تجارتی معاملہ ہو۔ اور اپنے آپ کو قتل نہ کرو۔ بے شک اللہ تم پر بڑا مہربان ہے۔'),
(523, 4, 30, 'اور جو شخص ظلم و تعدی سے ایسا کرے گا۔ تو ہم عنقریب اسے آتش (دوزخ) میں ڈالیں گے۔ اور یہ بات اللہ کے لیے بالکل آسان ہے۔'),
(524, 4, 31, 'اگر تم ان بڑے (گناہوں) سے بچتے رہو، جن سے تمہیں منع کیا گیا ہے۔ تو ہم تمہاری (چھوٹی) برائیاں دور کر دیں گے (معاف کر دیں گے) اور تمہیں ایک معزز (جنت) میں داخل کریں گے۔'),
(525, 4, 32, 'اور اللہ نے تم سے بعض کو دوسرے بعض پر جو زیادتی (بڑائی) عطا کی ہے۔ تم اس کی تمنا نہ کرو۔ مردوں کا حصہ ہے (مال و اعمال وغیرہ سے) جو انہوں نے کمایا اور عورتوں کا حصہ ہے جو کچھ انہوں نے کمایا۔ اور اللہ سے اس کے فضل (مال اور اعمال وغیرہ کا) سوال کرو بے شک اللہ ہر چیز کا جاننے والا ہے۔'),
(526, 4, 33, 'اور ہم نے ہر شخص کے لئے وارث مقرر کر دیئے ہیں، جو کچھ ماں، باپ اور قریب ترین رشتہ دار اور جن سے تم نے عہدوپیمان کر رکھا ہے۔ چھوڑ جائیں انہیں (وارثوں کو) ان کا حصہ دو۔ بے شک اللہ ہر چیز پر گواہ (مطلع) ہے۔'),
(527, 4, 34, 'مرد عورتوں کے محافظ و منتظم (حاکم) ہیں (ایک) اس لئے کہ اللہ نے بعض (مردوں) کو بعض (عورتوں) پر فضیلت دی ہے اور (دوسرے) اس لئے کہ مرد (عورتوں پر) اپنے مال سے خرچ کرتے ہیں۔ سو نیک عورتیں تو اطاعت گزار ہوتی ہیں اور جس طرح اللہ نے (شوہروں کے ذریعہ) ان کی حفاظت کی ہے۔ اسی طرح پیٹھ پیچھے (شوہروں کے مال اور اپنی ناموس کی) حفاظت کرنے والی ہوتی ہیں اور وہ عورتیں جن کی سرکشی کا تمہیں اندیشہ ہو انہیں (نرمی سے) سمجھاؤ (بعد ازاں) انہیں ان کی خواب گاہوں میں چھوڑ دو اور (آخرکار)۔ انہیں مارو۔ پھر اگر وہ تمہاری اطاعت کرنے لگیں تو پھر ان کے خلاف کوئی اور اقدام کرنے کے راستے تلاش نہ کرو۔ یقینا اللہ (اپنی کبریائی میں) سب سے بالا اور بڑا ہے۔'),
(528, 4, 35, 'اور اگر تمہیں دونوں (میاں بیوی) کی ناچاقی کا خوف دامنگیر ہو تو ایک ثالث مرد کے کنبے سے اور ایک ثالث عورت کے کنبے سے مقرر کرو۔ اگر دونوں اصلاح کا ارادہ کریں گے تو خدا ان دونوں میں موافقت پیدا کر دے گا۔ بے شک اللہ بڑا جاننے والا بڑا باخبر ہے۔'),
(529, 4, 36, 'اور اللہ کی عبادت کرو اور کسی چیز کو اس کا شریک نہ بناؤ۔ اور ماں باپ کے ساتھ نیکی کرو اور رشتہ داروں، یتیموں، مسکینوں اور قریبی ہمسایہ اور اجنبی ہمسایہ اور پہلو میں بیٹھنے والے رفیق اور مسافر، اپنے مملوکہ (غلاموں کنیزوں) کے ساتھ اچھا برتاؤ کرو۔ بے شک اللہ مغرور و متکبر اور شیخی بگھارنے والوں کو دوست نہیں رکھتا۔'),
(530, 4, 37, 'جو خود کنجوسی سے کام لیتے ہیں اور دوسروں کو بھی کنجوسی کرنے کا حکم (ترغیب) دیتے ہیں۔ اور جو کچھ اللہ نے ان کو اپنے فضل و کرم سے دیا ہے وہ اسے چھپاتے ہیں ہم نے ایسے ناشکروں کے لئے رسوا کن عذاب مہیا کر رکھا ہے۔'),
(531, 4, 38, 'اور جو اپنے مال صرف لوگوں کو دکھانے کے لیے خرچ کرتے ہیں دراصل وہ خدا اور روزِ جزا پر ایمان و یقین نہیں رکھتے (اللہ ایسے لوگوں کو دوست نہیں رکھتا)۔ اور جس کا مصاحب شیطان ہو۔ وہ اس کا بہت بُرا مصاحب ہے۔'),
(532, 4, 39, 'اور ان کا کیا نقصان ہوتا اگر وہ اللہ اور روزِ آخرت پر ایمان لاتے اور جو کچھ اللہ نے انہیں عطا کیا ہے اس میں سے کچھ (راہِ خدا میں) خرچ کرتے اور اللہ ان سے خوب واقف ہے۔'),
(533, 4, 40, 'بے شک اللہ ذرہ برابر (کسی پر) ظلم نہیں کرتا اور اگر وہ (ذرہ برابر) نیکی ہو تو اسے دوگنا (بلکہ چوگنا) کر دیتا ہے اور اپنی طرف سے بہت بڑا اجر عطا فرماتا ہے۔'),
(534, 4, 41, 'اس وقت کیا حال ہوگا جب ہم ہر امت میں سے ایک گواہ لائیں گے اور آپ کو ان سب پر گواہ بنا کر لائیں گے۔'),
(535, 4, 42, 'اس دن کافر اور پیغمبر کے نافرمان تمنا کریں گے کہ کاش وہ زمین کے برابر کر دیئے جاتے۔ اور وہ اللہ سے کوئی بات چھپا نہ سکیں گے۔'),
(536, 4, 43, 'اے ایمان والو۔ نماز کے قریب مت جاؤ جبکہ نشہ کی حالت میں ہو یہاں تک کہ (نشہ اتر جائے اور) تمہیں معلوم ہو کہ تم کیا کہہ رہے ہو؟ اور نہ ہی جنابت کی حالت میں (نماز کے قریب جاؤ) یہاں تک کہ غسل کر لو۔ الا یہ کہ تم راستے سے گزر رہے ہو (سفر میں ہو)۔ اور اگر تم بیمار ہو یا سفر میں ہو یا تم سے کوئی بیت الخلاء سے ہو کے آئے۔ یا تم نے عورتوں سے مباشرت کی ہو اور تمہیں پانی نہ ملے تو پھر پاک مٹی سے تیمم کر لو۔ کہ (اس سے) اپنے چہروں اور ہاتھوں کے کچھ حصہ پر مسح کرلو۔ بے شک اللہ بڑا معاف کرنے والا ہے، بڑا بخشنے والا ہے۔'),
(537, 4, 44, 'کیا تم نے ان لوگوں کی طرف نہیں دیکھا جن کو کتاب (الٰہی) کا کچھ حصہ دیا گیا کہ وہ گمراہی خرید رہے ہیں اور چاہتے ہیں کہ تم بھی سیدھے راستے سے بہک جاؤ۔'),
(538, 4, 45, 'اور اللہ تمہارے دشمنوں کو خوب جانتا ہے اور اللہ تمہاری کارسازی و سرپرستی اور مددگاری کے لیے کافی ہے'),
(539, 4, 46, 'یہودیوں میں سے کچھ ایسے بھی ہیں جو کلموں کو ان کے موقع و محل سے پھیر دیتے ہیں اور زبانوں کو توڑ موڑ کر اور دین پر اعتراض کرتے ہوئے کہتے ہیں کہ ’’سمعنا و عصینا‘‘ (ہم نے سنا مگر مانا نہیں) اور ’’اسمع غیر مسمع‘‘ (اور سنو تمہاری بات نہ سنی جائے) اور ’’راعنا‘‘ (ہمارا لحاظ کرو)۔ اگر وہ اس کی بجائے یوں کہتے ’’سمعنا و اطعنا‘‘ (ہم نے سنا اور مانا) اور ‘‘اسمع‘‘ (اور سنیئے) ‘‘وانظرنا‘‘ (اور ہماری طرف دیکھئے) تو یہ ان کے لئے بہتر اور زیادہ درست ہوتا۔ مگر اللہ نے کفر کی وجہ سے ان پر لعنت کی ہے (اپنی رحمت سے دور کر دیا ہے) اس لئے وہ بہت کم ایمان لائیں گے۔'),
(540, 4, 47, 'اے اہل کتاب اس (قرآن) پر (ایمان لاؤ) اس سے پہلے کہ ہم کچھ چہروں کو بگاڑ دیں اور انہیں ان کی پیٹھ کی طرف پھیر دیں یا ان پر اس طرح لعنت کریں جس طرح ہم نے ہفتہ کے دن والوں پر لعنت کی تھی اور اللہ کا حکم نافذ ہوکر رہتا ہے۔'),
(541, 4, 48, 'یقینا اللہ اس بات کو نہیں بخشتا کہ اس کے ساتھ شرک کیا جائے اور اس کے سوا (ہر گناہ) جس کے لئے چاہتا ہے بخش دیتا ہے اور جس نے کسی کو اللہ کا شریک ٹھہرایا اس نے بہت بڑے گناہ کے ساتھ بہتان باندھا۔'),
(542, 4, 49, 'کیا تم نے ان لوگوں کو نہیں دیکھا جو اپنی پاکیزگی بیان کرتے ہیں (اور خود اپنی تعریف کرتے ہیں) حالانکہ اللہ جسے چاہتا ہے پاکیزگی عطا کرتا ہے (جس کی چاہتا ہے تعریف کرتا ہے)۔ اور ان پر سوت برابر (ذرہ برابر) ظلم نہیں کیا جائے گا۔'),
(543, 4, 50, 'دیکھو کہ یہ کس طرح خدا پر جھوٹی تہمت لگاتے ہیں۔ اور ان کے گنہگار ہونے کے لیے یہی کھلا ہوا گناہ کافی ہے۔'),
(544, 4, 51, 'کیا تم نے ان لوگوں کو نہیں دیکھا جن کو کتاب (الٰہی) سے کچھ حصہ دیا گیا ہے وہ جبت (بت) اور طاغوت (شیطان) پر ایمان رکھتے ہیں (انہیں مانتے ہیں) اور کافروں کے متعلق کہتے ہیں کہ وہ ایمان لانے والوں سے زیادہ ہدایت یافتہ ہیں۔'),
(545, 4, 52, 'یہی وہ لوگ ہیں جن پر اللہ نے لعنت کی ہے اور جس پر اللہ لعنت کر دے تم اس کا ہرگز کوئی یار و مددگار نہیں پاؤگے۔'),
(546, 4, 53, 'کیا ان کا سلطنت میں کوئی حصہ ہے؟ اگر ایسا ہوتا تو یہ لوگوں کو تل بھر بھی نہ دیتے۔'),
(547, 4, 54, 'یا پھر وہ لوگوں پر اس لئے حسد کرتے ہیں کہ اللہ نے ان کو اپنے فضل و کرم سے نوازا ہے۔ (اگر یہ بات ہے) تو ہم نے آلِ ابراہیم (ع) کو کتاب و حکمت عطا کی ہے اور ان کو بہت بڑی سلطنت عطا کی۔'),
(548, 4, 55, 'پھر ان میں کچھ تو اس پر ایمان لائے اور کچھ روگردان ہوگئے اور (ایسوں کے لئے) دوزخ کی بھڑکتی ہوئی آگ کافی ہے۔'),
(549, 4, 56, 'بے شک جن لوگوں نے ہماری آیتوں کا انکار کیا ہم عنقریب انہیں (دوزخ) کی آگ میں جھونکیں گے۔ جب ان کی (پہلی) کھا لیں پک (جل) جائیں گی تو ہم ان کی کھالیں اور کھالوں سے بدل دیں گے تاکہ وہ عذاب کا مزہ چکھتے رہیں بے شک اللہ زبردست ہے اور بڑی حکمت والا ہے ۔'),
(550, 4, 57, 'اور جو لوگ ایمان لائے اور نیک عمل کئے ہم عنقریب ان کو ان بہشتوں میں داخل کریں گے جن کے نیچے سے نہریں جاری ہوں گی اور وہ ان میں ہمیشہ ہمیشہ رہیں گے۔ ان کے لئے وہاں پاک و پاکیزہ بیویاں ہوں گی اور ہم انہیں (اپنی رحمت) کے گنجان سایہ (گھنی چھاؤں) میں اتاریں گے۔'),
(551, 4, 58, '(اے مسلمانو) اللہ تمہیں حکم دیتا ہے کہ امانتیں اہل امانت کو ادا کرو۔ اور جب لوگوں کے درمیان فیصلہ کرو تو عدل کے ساتھ کرو۔ بے شک اللہ تمہیں بہت ہی اچھی بات کی نصیحت کرتا ہے۔ بے شک اللہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(552, 4, 59, 'اے ایمان والو اطاعت کرو اللہ کی اور اطاعت کرو رسول کی اور ان لوگوں کی جو تم میں سے صاحبانِ امر ہیں (فرمان روائی کے حقدار ہیں)۔ پھر اگر تمہارے درمیان کسی بات میں نزاع (یا جھگڑا) ہو جائے تو اسے اللہ اور رسول کی طرف پلٹاؤ اگر تم اللہ اور آخرت کے روز پر ایمان رکھتے ہو تو یہ طریقہ کار تمہارے لئے اچھا ہے اور انجام کے اعتبار سے عمدہ ہے۔'),
(553, 4, 60, 'کیا تم نے ان لوگوں کو نہیں دیکھا جن کا دعویٰ ہے کہ وہ اس پر ایمان لائے جو آپ پر نازل کیا گیا ہے اور اس پر جو آپ سے پہلے نازل کیا گیا۔ (اس کے باوجود) وہ چاہتے ہیں کہ طاغوت کی طرف رجوع کریں۔ (غیر شرعی عدالت میں مقدمہ لے جائیں) حالانکہ انہیں اس کا انکار کرنے کا حکم دیا گیا ہے۔ شیطان چاہتا ہے کہ انہیں بھٹکا کر گمراہی میں بہت دور لے جائے۔'),
(554, 4, 61, 'اور جب ان سے کہا جائے کہ آؤ اس کی طرف جو اللہ نے اتارا ہے (قرآن) اور آؤ رسول (سنت) کی طرف تو آپ منافقین کو دیکھیں گے کہ وہ آپ سے بڑی سخت روگردانی کرتے ہیں۔'),
(555, 4, 62, 'پھر کیسی گزرتی ہے جب ان پر کوئی مصیبت آپڑتی ہے اپنے ہاتھوں کی لائی ہوئی (ان کے کرتوتوں کی وجہ سے) تو آپ کی خدمت میں اللہ کے نام کی قسمیں کھاتے ہوئے آتے ہیں اور کہتے ہیں کہ ہمارا مقصد تو بھلائی اور (فریقین میں) مصالحت کرانا تھا۔'),
(556, 4, 63, 'یہ وہ ہیں کہ اللہ خوب جانتا ہے کہ جو کچھ ان کے دلوں میں ہے آپ ان سے چشم پوشی کریں اور انہیں نصیحت کریں اور ان سے مؤثر باتیں کریں۔'),
(557, 4, 64, 'اور ہم نے کوئی رسول نہیں بھیجا مگر اس لئے کہ اللہ کے حکم سے اس کی اطاعت کی جائے کاش جب یہ لوگ (گناہ کرکے) اپنی جانوں پر ظلم کر بیٹھے تھے اگر آپ کے پاس آجاتے اور اللہ سے بخشش طلب کرتے اور رسول بھی ان کے لئے دعائے مغفرت کرتے تو یقینا اللہ کو بڑا توبہ قبول کرنے والا اور بڑا مہربان پاتے۔'),
(558, 4, 65, 'نہیں۔ آپ کے پروردگار کی قسم! یہ لوگ اس وقت تک مؤمن نہیں ہو سکتے۔ جب تک اپنے تمام باہمی جھگڑوں میں آپ کو حَکَم نہ مانیں۔ اور پھر آپ جو فیصلہ کریں (زبان سے اعتراض کرنا تو کجا) اپنے دلوں میں بھی تنگی محسوس نہ کریں اور اس طرح تسلیم کریں جس طرح تسلیم کرنے کا حق ہے۔'),
(559, 4, 66, 'اور اگر ہم ان پر یہ فرض کر دیتے کہ اپنے آپ کو قتل کرو۔ یا اپنے گھروں سے نکل جاؤ۔ تو اس پر معدودے چند لوگوں کے سوا اور کوئی عمل نہ کرتا اور اگر یہ اس نصیحت پر عمل کرتے، جو ان کو کی جاتی ہے، تو یہ ان کے حق میں بہتر ہوتا۔ اور ثابت قدمی کا زیادہ موجب ہوتا۔'),
(560, 4, 67, 'اور اس وقت ہم انہیں اپنی طرف سے بڑا اجر دیتے۔'),
(561, 4, 68, 'اور انہیں سیدھے راستہ پر چلنے کی خاص توفیق عطا کرتے۔'),
(562, 4, 69, 'جو اللہ اور رسول(ص) کی اطاعت کرے گا۔ تو ایسے لوگ ان کے ساتھ ہوں گے جن پر اللہ نے خاص انعام کیا ہے۔ یعنی انبیاء، صدیقین، شہداء اور صالحین اور یہ بہت اچھے رفیق ہیں۔'),
(563, 4, 70, 'یہ اللہ کا خاص فضل ہے اور اللہ کا علم کافی ہے۔'),
(564, 4, 71, 'اے ایمان والو! اپنی حفاظت کا سامان مکمل رکھو۔ ہر وقت تیار رہو پھر (موقع کی مناسبت سے) دستہ دستہ ہوکر نکلو یا اکھٹے ہوکر نکلو۔'),
(565, 4, 72, 'اور تم میں سے کچھ ایسے بھی ہیں جو پیچھے رکا رہنا پسند کرتے ہیں پس اگر تم پر کوئی مصیبت نازل ہو تو کہتا ہے کہ یہ مجھ پر اللہ کا احسان تھا کہ ان کے ساتھ حاضر نہ تھا۔'),
(566, 4, 73, 'اور اگر تم پر اللہ کا فضل و کرم ہو (فتح ہو اور مالِ غنیمت مل جائے) تو کہتا ہے جیسے اس کے اور تمہارے درمیان کبھی محبت اور دوستی تھی ہی نہیں کہ کاش میں بھی ان کے ساتھ ہوتا تو بڑی کامیابی حاصل کرتا۔'),
(567, 4, 74, 'اللہ کی راہ میں ان لوگوں کو جنگ کرنا چاہیے جنہوں نے دنیا کی پست زندگی آخرت کے عوض فروخت کر دی ہے جو اللہ کی راہ میں جہاد کرے، پھر مارا جائے یا غالب آجائے تو ہم ضرور اسے اجر عظیم عطا کریں گے۔'),
(568, 4, 75, 'آخر تمہیں کیا ہوگیا ہے؟ کہ تم جنگ نہیں کرتے راہ خدا میں اور ان کمزور مردوں، عورتوں اور بچوں کی خاطر جو فریاد کر رہے ہیں۔ پروردگار! ہمیں اس بستی سے نکال دے۔ جس کے باشندے ظالم ہیں اور اپنی طرف سے ہمارے لئے کوئی سرپرست اور حامی و مددگار بنا۔'),
(569, 4, 76, 'جو لوگ ایمان لائے ہیں وہ تو اللہ کی راہ میں جنگ کرتے ہیں اور جو کافر ہیں وہ شیطان کی راہ میں جنگ کرتے ہیں۔ پس تم شیطان کے حوالی، موالی (حامیوں) سے جنگ کرو۔ یقینا شیطان کا مکر و فریب نہایت کمزور ہے۔'),
(570, 4, 77, 'کیا تم نے ان لوگوں کو نہیں دیکھا جن سے کہا گیا تھا کہ اپنے ہاتھ روکے رہو اور نماز پڑھو اور زکوٰۃ ادا کرو۔ پھر جب ان پر جدال و قتال فرض کر دیا گیا۔ تو ان میں سے ایک گروہ انسانوں سے اس طرح ڈرنے لگا، جیسے اللہ سے ڈرنا چاہیے یا اس سے بھی زیادہ۔ اور کہنے لگا پروردگار! تو نے کیوں (اتنا جلدی) ہم پر جہاد فرض کر دیا۔ اور تھوڑی مدت تک ہمیں مہلت کیوں نہ دی؟ (اے نبی) کہہ دیجیے کہ دنیا کا سامان بہت تھوڑا ہے اور جو متقی و پرہیزگار ہے اس کے لئے آخرت بہت بہتر ہے اور تم پر کھجور کی گھٹلی کے ریشہ برابر (ذرہ بھی) ظلم نہیں کیا جائے گا۔'),
(571, 4, 78, 'تم جہاں کہیں بھی ہوگے، موت تمہیں آئے گی اگرچہ تم مضبوط قلعوں میں کیوں نہ ہو اور جب انہیں بھلائی پہنچتی ہے۔ تو کہتے ہیں کہ یہ اللہ کی طرف سے ہے اور جب کوئی برائی اور تکلیف پہنچتی ہے تو کہتے ہیں کہ یہ آپ کی وجہ سے ہے۔ کہہ دیجیے کہ یہ سب کچھ اللہ ہی کی طرف سے ہے آخر ان لوگوں کو کیا ہوگیا ہے کہ کوئی بات سمجھتے ہی نہیں ہیں؟ ۔'),
(572, 4, 79, 'آپ کو جو بھلائی پہنچتی ہے۔ وہ تو اللہ کی طرف سے ہے اور آپ کو جو تکلیف پہنچتی ہے۔ وہ تمہاری وجہ سے ہے اور ہم نے آپ کو تمام لوگوں کی طرف رسول بنا کر بھیجا ہے اور گواہی کے لیے اللہ کافی ہے۔'),
(573, 4, 80, 'اور جس نے رسول کی اطاعت کی، اس نے اللہ کی اطاعت کی، اور جس نے روگردانی کی، تو ہم نے آپ کو ان پر نگران و پاسبان بنا کر نہیں بھیجا۔'),
(574, 4, 81, 'اور وہ زبان سے اطاعت و فرمانبرداری کی لفظ کہتے ہیں اور جب آپ کے پاس سے باہر نکلتے ہیں۔ تو ان میں سے ایک گروہ آپ کے فرمان (یا اپنے قول و قرار کے) برعکس رات بھر تدبیریں کرتا ہے جو کچھ وہ رات کے وقت کرتے ہیں۔ اللہ وہ سب کچھ (ان کے صحیفۂ اعمال میں) لکھ رہا ہے۔ آپ ان کی طرف توجہ نہ کریں۔ اور اللہ پر توکل و بھروسہ کریں، کارسازی کے لیے اللہ کافی ہے۔'),
(575, 4, 82, 'آخر یہ لوگ قرآن میں غور و فکر کیوں نہیں کرتے۔ اگر یہ اللہ کے علاوہ کسی اور کی طرف سے ہوتا تو اس میں بہت کچھ اختلاف پاتے۔'),
(576, 4, 83, 'اور جب ان کے پاس امن یا خوف کی کوئی بات پہنچتی ہے تو اسے پھیلا دیتے ہیں حالانکہ اگر وہ اسے رسول اور اولی الامر کی طرف لوٹاتے تو (حقیقت کو) وہ لوگ جان لیتے جو استنباط کی صلاحیت رکھتے ہیں۔ اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی، تو چند آدمیوں کے سوا باقی شیطان کی پیروی کرنے لگ جاتے۔'),
(577, 4, 84, 'تو آپ(ص) اللہ کی راہ میں جہاد کریں۔ آپ پر ذمہ داری نہیں ڈالی جاتی۔ سوائے اپنی ذات کے اور اہل ایمان کو (جہاد پر) آمادہ کریں۔ امید ہے کہ اللہ کافروں کا زور روک دے اور اللہ کا زور زبردست اور اس کی سزا بہت سخت ہے۔'),
(578, 4, 85, 'اور جو اچھی سفارش کرے گا۔ اسے اس میں سے حصہ ملے گا اور جو کوئی بری سفارش کرے گا۔ اسے بھی اس کا حصہ (وزر و بال) ملے گا۔ اور اللہ ہر شی پر قدرت رکھنے والا ہے۔'),
(579, 4, 86, 'اور جب تمہیں سلام کیا جائے (یا کوئی تحفہ پیش کیا جائے) تو اس سے بہتر طریقہ پر جواب دو۔ یا (کم از کم) اسی کو لوٹادو۔ بے شک اللہ ہر چیز کا محاسبہ کرنے والا ہے۔'),
(580, 4, 87, 'اللہ وہ ہے جس کے سوا کوئی الٰہ نہیں ہے۔ وہ ضرور تمہیں قیامت کے دن اکٹھا کرے گا۔ جس میں کوئی شک نہیں ہے اور اللہ سے بڑھ کر کون بات میں سچا ہے۔'),
(581, 4, 88, 'تمہیں کیا ہوگیا ہے کہ تم منافقین کے بارے میں دو گروہ ہوگئے ہو۔ حالانکہ اللہ نے انہیں ان کے کرتوتوں کی وجہ سے (ان کے کفر کی طرف) الٹا پھیر دیا ہے۔ کیا تم چاہتے ہو کہ اسے ہدایت دو جس سے اس کی بدعملی کی وجہ سے خدا نے توفیق ہدایت سلب کر لی ہے۔ اور جس سے اللہ توفیق ہدایت سلب کرے، تم اس کے لیے (ہدایت کا) راستہ نہیں پاؤگے۔'),
(582, 4, 89, 'وہ چاہتے ہیں کہ تم بھی اسی طرح کفر اختیار کرو جیسے انہوں نے کفر اختیار کیا ہے۔ تاکہ پھر تم سب برابر ہو جاؤ۔ لہٰذا تم انہیں اپنا حامی و سرپرست نہ بناؤ جب تک اللہ کی راہ میں ہجرت نہ کریں اور اگر وہ اس ہجرت کرنے سے روگردانی کریں تو انہیں پکڑو۔ اور جہاں کہیں انہیں پاؤ انہیں قتل کر ڈالو اور ان میں کسی کو اپنا سرپرست اور مددگار نہ بناؤ۔'),
(583, 4, 90, 'سوا ان کے جو ان لوگوں سے جا ملیں جن کے اور تمہارے درمیان (جنگ نہ کرنے کا) معاہدہ ہے یا اس حالت میں تمہارے پاس آئیں کہ ان کے سینے (دل) میں تمہارے ساتھ یا اپنی قوم کے ساتھ جنگ کرنے سے تنگ آچکے ہوں (ایسے منافق قتل کے اس حکم سے مستثنیٰ ہیں) اگر خدا چاہتا تو ان کو تم پر مسلط کر دیتا اور وہ ضرور تم سے لڑتے تو پھر اگر وہ تم سے کنارہ کشی کر لیں اور تمہارے ساتھ جنگ نہ کریں اور صلح کا پیغام بھیجیں تو اللہ نے تمہارے لئے ان کے خلاف کوئی قدم اٹھانے کی کوئی راہ نہیں رکھی ہے۔'),
(584, 4, 91, 'عنقریب تم کچھ ایسے لوگ (منافق) پاؤگے۔ جو چاہتے ہیں کہ تم سے بھی امن میں رہیں اور اپنی قوم سے بھی امن میں رہیں لیکن جب وہ کبھی فتنہ و فساد کی طرف بلائے جاتے ہیں تو اندھے منہ اس میں جا پڑتے ہیں۔ لہٰذا اگر یہ لوگ تم سے کنارہ کشی نہ کریں اور تمہاری طرف صلح و آشتی کا ہاتھ نہ بڑھائیں اور اپنے ہاتھ نہ روکیں تو پھر انہیں پکڑو۔ اور جہاں کہیں انہیں پاؤ۔ قتل کرو۔ یہی وہ لوگ ہیں! جن پر ہم نے تمہیں کھلا غلبہ عطا کیا ہے۔'),
(585, 4, 92, 'کسی مسلمان کے لئے روا نہیں ہے کہ وہ کسی مسلمان کو قتل کرے مگر غلطی سے۔ اور جو کوئی کسی مؤمن کو غلطی سے قتل کرے (تو اس کا کفارہ یہ ہے کہ) ایک مؤمن غلام آزاد کیا جائے اور خون بہا اس کے گھر والوں (وارثوں) کو ادا کیا جائے، مگر یہ کہ وہ لوگ خود (خون بہا) معاف کر دیں۔ پھر اگر مقتول تمہاری دشمن قوم سے تعلق رکھتا ہو، مگر وہ خود مسلمان ہو تو پھر (اس کا کفارہ) ایک مؤمن غلام کا آزاد کرنا ہے اور اگر وہ (مقتول) ایسی (غیر مسلم) قوم کا فرد ہو جس کے اور تمہارے درمیان (جنگ نہ کرنے کا) معاہدہ ہے۔ تو (کفارہ میں) ایک خون بہا اس کے وارثوں کے حوالے کیا جائے گا۔ اور ایک مسلمان غلام بھی آزاد کیا جائے گا اور جس کو (غلام) میسر نہ ہو تو وہ مسلسل دو مہینے روزے رکھے گا۔ یہ اللہ کی طرف سے (اس گناہ کی) توبہ مقرر ہے۔ خدا بڑا جاننے والا اور بڑا حکمت والا ہے۔'),
(586, 4, 93, 'اور جو کوئی مسلمان کو جان بوجھ کر قتل کرے، اس کی سزا دوزخ ہے۔ جس میں وہ ہمیشہ رہے گا اور اس پر اللہ کا غضب ہوگا۔ اور اللہ اس پر لعنت کرے گا اور اس نے اس کے لیے عذاب عظیم تیار کر رکھا ہے۔'),
(587, 4, 94, 'اے ایمان والو! جب خدا کی راہ میں (جہاد کے لیے) نکلو تو پہلے خوب تحقیق کر لیا کرو۔ اور جو تمہیں سلام کرے تم اسے نہ کہو کہ تو مسلمان نہیں ہے۔ تم دنیاوی زندگی کا سازوسامان حاصل کرنا چاہتے ہو تو اللہ کے پاس بڑی غنیمتیں ہیں۔ اس سے پہلے تم خود ایسے (کافر) ہی تھے۔ پھر اللہ نے تم پر احسان کیا (کہ تم مسلمان ہوگئے) پس خوب تحقیق کر لیا کرو۔ یقینا اللہ اس سے باخبر ہے۔ جو کچھ تم کرتے ہو۔'),
(588, 4, 95, 'مسلمانوں میں سے بلا عذر گھر میں بیٹھے رہنے والے اور راہِ خدا میں مالوں اور اپنی جانوں سے جہاد کرنے والے برابر نہیں ہو سکتے۔ اللہ نے مالوں اور اپنی جانوں سے جہاد کرنے والوں کو درجہ کے اعتبار سے بیٹھ رہنے والوں پر فضیلت دی ہے اور یوں تو اللہ نے ہر ایک سے بھلائی کا وعدہ کیا ہے۔ مگر اس نے جہاد کرنے والوں کو بیٹھ رہنے والوں پر اجر عظیم کے ساتھ فضیلت دی ہے۔'),
(589, 4, 96, 'یعنی اس کی طرف سے (ان کے لئے) بڑے درجے، بخشش اور رحمت ہے اور اللہ بڑا بخشنے والا بڑا رحم کرنے والا ہے۔'),
(590, 4, 97, 'بے شک وہ لوگ جو اپنی جانوں پر ظلم کر رہے تھے۔ جب فرشتوں نے ان کی روحوں کو قبض کیا، تو ان سے کہا تم کس حال میں تھے؟ تو انہوں نے کہا کہ ہم زمین میں کمزور و بے بس تھے۔ فرشتوں نے کہا کہ اللہ کی زمین وسیع نہ تھی کہ تم اس میں ہجرت کرتے یہی وہ لوگ ہیں جن کا ٹھکانہ جہنم ہے اور وہ بُری جائے بازگشت ہے۔'),
(591, 4, 98, 'علاوہ ان کمزور مردوں، عورتوں اور بچوں کے جن کے اختیار میں کوئی تدبیر نہ تھی اور وہ کوئی راستہ نہ نکال سکتے تھے۔'),
(592, 4, 99, 'یہی وہ لوگ ہیں جن کو عنقریب خدا معاف کر دے گا کہ وہ بڑا معاف کرنے والا اور بڑا بخشنے والا ہے۔'),
(593, 4, 100, 'اور جو کوئی راہِ خدا میں ہجرت کرے گا۔ وہ زمین میں بہت ہجرت گاہ، جائے پناہ اور بڑی کشائش پائے گا۔ اور جو شخص اپنے گھر سے خدا اور رسول(ص) کی طرف ہجرت کے ارادہ سے نکلے پھر اسے موت آجائے، تو اس کا اجر و ثواب اللہ کے ذمہ ہوگیا۔ اور اللہ بڑا بخشنے والا، رحم کرنے والا ہے۔'),
(594, 4, 101, 'اور جب تم زمین میں سفر کرو۔ تو اس میں کوئی مضائقہ نہیں ہے (بلکہ واجب ہے کہ) نماز میں قصر کر دو۔ (بالخصوص) جب تمہیں خوف ہو کہ کافر لوگ تمہیں کوئی تکلیف پہنچائیں گے۔ بے شک کافر تمہارے کھلے ہوئے دشمن ہیں۔'),
(595, 4, 102, 'اے رسول(ص) جب آپ(ص) ان (مسلمانوں) میں ہوں اور انہیں نماز (باجماعت) پڑھانے لگیں تو چاہیے کہ ان کا ایک گروہ آپ کے ساتھ کھڑا ہو۔ اور وہ لوگ اپنے ہتھیار لئے رہیں۔ اور جب یہ سجدہ کر چکیں (نماز پڑھ چکیں) تو یہ (پشت پناہی کے لیے) تمہارے پیچھے چلے جائیں اور پھر وہ دوسرا گروہ جس نے ہنوز نماز نہیں پڑھی ہے۔ آجائے اور آپ کے ساتھ نماز پڑھے۔ اور چوکنا رہے اور اپنا اسلحہ لئے رہے، کفار کی تو یہ خواہش ہے کہ تم اپنے ہتھیاروں اور اپنے سازوسامان سے (ذرا) غافل ہو جاؤ اور وہ تم پر یکبارگی ٹوٹ پڑیں۔ اور اگر تمہیں بارش سے تکلیف ہو یا تم بیمار ہو تو کوئی مضائقہ نہیں ہے کہ تم اپنے ہتھیاروں کو رکھ دو۔ ہاں اپنی حفاظت کا خیال رکھو (چوکنا رہو) بے شک اللہ نے کافروں کے لئے رسوا کن عذاب مہیا کر رکھا ہے۔'),
(596, 4, 103, 'پھر جب تم نماز ادا کر چکو تو اللہ کو یاد کرو کھڑے ہوئے، بیٹھے ہوئے، اور اپنے پہلوؤں پر (لیٹے ہوئے) اور جب تمہیں اطمینان ہو جائے تو نماز کو پورے طور پر ادا کرو۔ بے شک نماز اہل ایمان پر پابندی وقت کے ساتھ فرض کی گئی ہے۔'),
(597, 4, 104, 'اس (مخالف) جماعت کی تلاش اور تعاقب میں سستی نہ دکھاؤ۔ اگر اس میں تمہیں تکلیف پہنچتی ہے۔ تو انہیں بھی اسی طرح تکلیف پہنچتی ہے۔ جس طرح تمہیں پہنچتی ہے اور تم اللہ سے اس چیز (ثواب) کے امیدوار ہو جس کے وہ امیدوار نہیں ہیں اور اللہ بڑا جاننے والا، بڑی حکمت والا ہے۔'),
(598, 4, 105, 'بے شک ہم نے (یہ) کتاب حق کے ساتھ آپ پر اتاری ہے۔ تاکہ آپ لوگوں میں اس کے مطابق فیصلہ کریں، جو اللہ نے آپ کو بتا دیا ہے اور آپ خیانت کاروں کے طرفدار نہ بنیں۔'),
(599, 4, 106, 'اور اللہ سے مغفرت طلب کریں، یقینا اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(600, 4, 107, 'اور جو لوگ اپنی ذات سے خیانت کرتے ہیں۔ آپ ان کی وکالت نہ کریں۔ بے شک اللہ اسے دوست نہیں رکھتا، جو بڑا خیانت کار اور بڑا گنہگار ہے۔'),
(601, 4, 108, 'یہ لوگ (اپنی حرکات) لوگوں سے تو چھپا سکتے ہیں مگر اللہ سے نہیں چھپا سکتے، کیونکہ وہ تو اس وقت بھی ان کے ساتھ ہوتا ہے، جب وہ راتوں کو اس کی پسند کے خلاف گفتگوئیں (مشورے) کرتے ہیں اور وہ جو کچھ کرتے ہیں۔ اللہ اس کا احاطہ کئے ہوئے ہے۔'),
(602, 4, 109, '(اے مسلمانو!) یہ تم ہو جو دنیاوی زندگی میں ان کی طرف سے جھگڑتے ہو (ان کی طرفداری کرتے ہو)۔ تو قیامت کے دن ان کی طرف سے خدا سے کون بحث کرے گا؟ یا کون ان کا وکیل (نمائندہ) ہوگا؟ ۔'),
(603, 4, 110, 'اور جو کوئی برائی کرے یا قبیح کام کرے یا کوئی اپنے اوپر ظلم کرے (گناہ کا ارتکاب کرے) پھر اللہ سے مغفرت طلب کرے، تو وہ اللہ کو بڑا بخشنے والا اور بڑا رحم کرنے والا پائے گا۔'),
(604, 4, 111, 'اور جو گناہ کرتا ہے تو وہ اپنی ہی جان کے خلاف کرتا ہے۔ اور اللہ بڑا جاننے والا ہے اور بڑی حکمت والا ہے۔'),
(605, 4, 112, 'اور جو کوئی غلطی یا گناہ کرے یا کسی بے قصور پر تہمت لگائے، تو اس نے ایک بڑے بہتان اور کھلے ہوئے گناہ کا بوجھ اٹھایا ہے۔'),
(606, 4, 113, 'اور اگر اللہ کا فضل و کرم اور اس کی خاص رحمت آپ(ص) کے شامل حال نہ ہوتی تو ان کے ایک گروہ نے تو یہ تہیّہ کر لیا تھا کہ آپ کو گمراہ کرکے رہے گا۔ حالانکہ وہ اپنے آپ کو ہی گمراہ کر رہے ہیں اور آپ کو کوئی نقصان نہیں پہنچا سکتے۔ اللہ نے آپ پر کتاب و حکمت نازل کی ہے اور آپ کو وہ کچھ پڑھا دیا ہے۔ جو آپ نہیں جانتے تھے اور آپ پر اللہ کا بڑا فضل و کرم ہے۔'),
(607, 4, 114, 'لوگوں کی زیادہ تر سرگوشیوں میں کوئی خیر و خوبی نہیں ہے سوا اس کے کہ کوئی صدقہ دینے، نیکی کرنے یا لوگوں کے درمیان صلح صفائی کرانے کی بات کرے اور جو شخص خدا کی خوشنودی حاصل کرنے کی خاطر ایسا کرے ہم اسے اجر عظیم عطا فرمائیں گے۔'),
(608, 4, 115, 'اور جو راہِ ہدایت کے واضح ہو جانے کے بعد رسول کی مخالفت کرے اور اہل ایمان کے راستے کے خلاف چلے، تو ہم اسے ادھر ہی جانے دیں گے، جدھر وہ گیا ہے (ہم اسے کرنے دیں گے جو کچھ وہ کرتا ہے)۔ اور اسے آتش دوزخ کا مزا چکھائیں گے اور یہ بہت بری جاءِ بازگشت ہے۔'),
(609, 4, 116, 'بے شک اللہ اس جرم کو نہیں بخشتا کہ اس کے ساتھ شرک کیا جائے اور اس کے سوا (جو کم درجہ کے جرائم ہیں) جس کے لئے چاہتا ہے، بخش دیتا ہے۔ اور جو کسی کو اس کا شریک ٹھرائے، وہ گمراہ ہوا (اور اس میں) بہت دور نکل گیا۔'),
(610, 4, 117, 'یہ (مشرک) لوگ اللہ کو چھوڑ کر نہیں پکارتے، مگر زنانی چیزوں (دیویوں) کو (ان کی عبادت کرتے ہیں) اور نہیں پکارتے، مگر سرکش شیطان کو (اس کی پرستش کرتے ہیں)۔'),
(611, 4, 118, 'جس پر اللہ نے لعنت کی ہے اور جس نے کہا کہ میں تیرے بندوں میں سے اپنا مقررہ حصہ لے کر رہوں گا۔'),
(612, 4, 119, 'اور انہیں ضرور گمراہ کروں گا اور میں انہیں مختلف آرزوؤں میں الجھاؤں گا (انہیں سبز باغ دکھاؤں گا) اور انہیں حکم دوں گا کہ وہ ضرور چوپاؤں کے کان شگافتہ کریں گے اور میں انہیں حکم دوں گا کہ وہ خدا کی خدائی ساخت کو ضرور بدل دیں گے اور جو اللہ کو چھوڑ کر شیطان کو اپنا سرپرست بنائے گا وہ کھلا ہوا نقصان اٹھائے گا۔'),
(613, 4, 120, 'وہ شیطان لوگوں سے وعدے کرتا ہے اور انہیں (جھوٹی) امیدیں دلاتا ہے۔ اور ان سے شیطان وعدہ نہیں کرتا مگر فریب کے طور پر۔'),
(614, 4, 121, 'یہ وہ لوگ ہیں جن کا ٹھکانہ دوزخ ہے۔ جس سے وہ چھٹکارا کی راہ نہیں پائیں گے۔'),
(615, 4, 122, 'جو لوگ ایمان لائے اور نیک عمل کیے ہم عنقریب انہیں ان بہشتوں میں داخل کریں گے جن کے نیچے سے نہریں جاری ہوں گی۔ وہ ان میں ہمیشہ ہمیشہ رہیں گے (یہ) اللہ کا سچا وعدہ ہے اور اللہ سے بڑھ کر کون بات کا سچا ہے۔'),
(616, 4, 123, '(اے مسلمانو) نہ تمہاری تمناؤں سے کچھ ہوتا ہے اور نہ اہلِ کتاب کی تمناؤں سے۔ جو بھی برائی کرے گا، اس کو اس کی سزا دی جائے گی اور وہ اللہ کو چھوڑ کر اپنے لئے نہ کوئی سرپرست پائے گا اور نہ کوئی حامی و مددگار۔'),
(617, 4, 124, 'اور جو کوئی نیک عمل کرے گا خواہ مرد ہو یا عورت درانحالیکہ وہ مؤمن ہو تو ایسے ہی لوگ جنت میں داخل ہوں گے اور ان پر تل برابر ظلم نہیں کیا جائے گا۔'),
(618, 4, 125, 'اور اس شخص سے بڑھ کر کس کا دین اچھا ہوگا۔ جو اللہ کے لئے اپنا چہرہ جھکا دے (اپنے کو اللہ کے سپرد کر دے) اور وہ احسان کرنے والا ہو۔ اور اس ابراہیم حنیف کی ملت کا پیرو ہو۔ جسے اللہ نے اپنا خلیل بنایا تھا۔'),
(619, 4, 126, 'جو کچھ آسمانوں اور زمین میں ہے۔ وہ اللہ ہی کا ہے۔ اور اللہ ہر چیز کا احاطہ کئے ہوئے ہے۔'),
(620, 4, 127, 'اے رسول! لوگ عورتوں کے معاملہ میں آپ سے فتویٰ طلب کرتے ہیں کہہ دیجیے! کہ اللہ تمہیں ان کے بارے میں فتویٰ دیتا ہے اور متوجہ کرتا ہے ان آیات کی طرف جو کلام الٰہی کے اندر ہیں اور تمہیں ان یتیم لڑکیوں کے بارے میں پڑھ کر سنائی جا رہی ہیں۔ جن کا تم مقررہ حق تو ادا نہیں کرتے مگر چاہتے ہو کہ ان سے نکاح کرلو۔ اور متوجہ کرتا ہے ان (آیات) کی طرف جو ان کمزور و بے بس لڑکوں کے بارے میں ہیں۔ اور جو (آیات) اس بارے میں ہیں کہ یتیم بچوں کے بارے میں انصاف پر قائم رہو۔ اور تم جو بھلائی کروگے تو اللہ اس کا خوب علم رکھتا ہے۔'),
(621, 4, 128, 'اور اگر کوئی عورت اپنے شوہر سے حق تلفی یا بے توجہی محسوس کرے تو ان دونوں کے لیے کوئی مضائقہ نہیں ہے کہ (کچھ لو کچھ دو کی بنا پر) آپس میں صلح کر لیں اور صلح بہرحال بہتر ہے اور نفوس میں بخل موجود رہتا ہے (تنگ دلی پر آمادہ رہتے ہیں) اور اگر تم بھلائی کرو۔ اور پرہیزگاری اختیار کرو۔ تو بے شک اللہ تمہارے اس طرزِ عمل سے باخبر ہے۔'),
(622, 4, 129, 'یہ ٹھیک ہے کہ تم جس قدر چاہو مگر بیویوں میں پورا پورا عدل نہیں کر سکتے۔ مگر بالکل تو ایک طرف نہ جھک جاؤ۔ کہ (دوسری کو) بیچوں بیچ لٹکا چھوڑ دو۔ اور اگر تم اپنی اصلاح کر لو۔ اور تقویٰ اختیار کرو۔ تو خدا بڑا بخشنے والا، بڑا مہربان ہے۔'),
(623, 4, 130, 'اور اگر دونوں ایک دوسرے سے الگ ہو جائیں تو اللہ ہر ایک کو اپنے فضل و کرم کی وسعت سے بے نیاز کر دے گا۔ اور اللہ بڑی وسعت والا بڑی حکمت والا ہے۔'),
(624, 4, 131, 'اور جو کچھ آسمانوں اور زمین میں ہے سب اللہ ہی کا ہے اور ہم نے ان لوگوں کو بھی جن کو تم سے پہلے کتاب دی گئی تھی اور تمہیں بھی ہدایت کی ہے کہ تقویٰ و پرہیزگاری اختیار کرو۔ وہ تو آسمانوں اور زمین کی سب چیزوں کا مالک ہے۔ اللہ بڑا بے نیاز ہے حمد و ثناء کا حقدار ہے۔'),
(625, 4, 132, 'جو کچھ آسمانوں اور زمین میں ہے۔ وہ سب اللہ کا ہے اور اللہ کارسازی کے لیے کافی ہے۔'),
(626, 4, 133, 'اے لوگو! اگر وہ چاہے، تو تم سب کو لے جائے (ختم کر دے) اور دوسروں کو لے آئے اور اللہ اس پر پوری قدرت رکھتا ہے۔'),
(627, 4, 134, 'جو شخص صرف دنیاوی صلہ و ثواب کا طلبگار ہو (تو اس کی مرضی ورنہ) اللہ کے پاس تو دنیا و آخرت دونوں کا صلہ و ثواب ہے اور اللہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(628, 4, 135, 'اے ایمان والو! مضبوطی کے ساتھ انصاف کے علمبردار ہو جاؤ۔ اور محض اللہ کے لئے گواہی دینے والے بنو۔ خواہ تمہیں اپنے یا والدین کے اور اپنے قریبی رشتہ داروں کے خلاف ہی گواہی دینا پڑے۔ وہ (جس کے خلاف گواہی دی جا رہی ہے) چاہے امیر ہو چاہے فقیر ہو بہرحال اللہ ان دونوں کا تم سے زیادہ خیرخواہ ہے پس اس طرح خواہش نفس کی پیروی نہ کرو کہ حق سے ہٹ جاؤ۔ اور اگر تم نے ہیرپھیر کیا یا (حق سے) منہ موڑا تو بے شک جو کہ تم کرتے ہو اللہ اس سے باخبر ہے۔'),
(629, 4, 136, 'اے ایمان والو! اللہ پر، اس کے رسول(ص) پر اور اس کی کتاب پر ایمان لاؤ جو اس نے اپنے رسول(ص) پر نازل کی ہے۔ اور اس کتاب پر جو اس نے پہلے اتاری ہے اور جو کوئی اللہ کا، اس کے فرشتوں کا، اس کی کتابوں کا، اس کے رسولوں کا، اور آخرت کے دن کا انکار کرے۔ وہ گمراہ ہوا اور اس میں بہت دور نکل گیا۔'),
(630, 4, 137, 'جو لوگ ایمان لائے پھر کافر ہوگئے پھر ایمان لائے پھر کافر ہوگئے۔ پھر کفر میں بڑھتے گئے۔ اللہ ہرگز نہ ان کی مغفرت فرمائے گا اور نہ انہیں سیدھی راہ تک پہنچائے گا۔'),
(631, 4, 138, 'منافقوں کو سنا دیجیے! کہ ان کے لئے دردناک عذاب ہے۔'),
(632, 4, 139, 'وہ (منافق) جو اہل ایمان کو چھوڑ کر کافروں کو اپنا دوست بناتے ہیں۔ کیا یہ ان کے پاس عزت تلاش کرتے ہیں۔ عزت تو ساری کی ساری اللہ ہی کے لئے ہے (اس کے اختیار میں ہے)۔'),
(633, 4, 140, 'اور اس نے کتاب (قرآن) میں تم پر یہ حکم نازل کیا ہے کہ جب سنو کہ (کسی جگہ) آیات الٰہیہ کا انکار کیا جا رہا ہے اور ان کا مذاق اڑایا جا رہا ہے۔ تو ایسے لوگوں کے ساتھ مت بیٹھو۔ جب تک وہ کسی اور بات میں مشغول نہ ہو جائیں (ورنہ) اس حالت میں تم بھی انہی جیسے ہو جاؤگے۔ بے شک خدا سب منافقوں اور سب کافروں کو دوزخ میں اکھٹا کرنے والا ہے۔'),
(634, 4, 141, 'یہ وہ لوگ ہیں جو تمہارے (انجام) کے منتظر رہتے ہیں۔ پھر اگر تمہیں اللہ کی طرف سے فتح و فیروزی حاصل ہوگئی تو کہتے ہیں کیا ہم تمہارے ساتھ نہ تھے؟ اور اگر کافروں کو (فتح کا) کچھ حصہ مل گیا تو (کافروں سے) کہتے ہیں۔ کیا ہم تم پر غالب نہیں آنے لگے تھے؟ اور کیا ہم نے تمہیں مسلمانوں سے نہیں بچایا؟ اب اللہ ہی قیامت کے دن تمہارے درمیان فیصلہ کرے گا۔ اور اللہ کبھی کافروں کے لیے مسلمانوں پر غالب آنے کا کوئی راستہ نہیں رکھے گا۔'),
(635, 4, 142, 'منافق (بزعم خود) خدا کو دھوکہ دے رہے ہیں۔ حالانکہ وہ انہیں دھوکے میں رکھ رہا ہے (انہیں ان کی دھوکہ بازی کی سزا دینے والا ہے) اور جب وہ نماز کے لئے کھڑے ہوتے ہیں۔ تو بے دلی اور کاہلی کے ساتھ وہ بھی خلوص نیت سے نہیں بلکہ محض لوگوں کو دکھانے کے لیے۔ اور اللہ کو یاد نہیں کرتے مگر تھوڑ۔'),
(636, 4, 143, 'وہ (کفر و اسلام کے) درمیان ڈانواں ڈول ہیں نہ پورے اس طرف نہ پورے اس طرف جسے اللہ بھٹکنے دے (اس کی کج رفتاری کی وجہ سے توفیق ہدایت سلب کر لے) تو تو اس کے لئے ہدایت کا کوئی راستہ نہیں پائے گا۔'),
(637, 4, 144, 'اے ایمان والو! اہل ایمان کو چھوڑ کر کافروں کو اپنا دوست نہ بناؤ۔ کیا تم چاہتے ہو کہ اپنے خلاف اللہ کو کھلی ہوئی دلیل مہیا کر دو۔'),
(638, 4, 145, 'بے شک منافق دوزخ کے سب سے نیچے والے طبقہ میں ہوں گے۔ اور تم ان کے کوئی یار و مددگار نہیں پاؤگے۔'),
(639, 4, 146, 'سوا ان کے جنہوں نے توبہ کی، اپنی اصلاح کرلی۔ اور اللہ سے وابستہ ہوگئے اور اللہ کے لئے اپنا دین خالص کر دیا (خلوص کے ساتھ اس کی اطاعت کرنے لگے) تو یہ لوگ مؤمنین کے ساتھ ہوں گے اور اللہ اہل ایمان کو اجر عظیم عطا فرمائے گا۔'),
(640, 4, 147, 'اگر تم شکرگزار بن جاؤ۔ اور ایمان لے آؤ تو اللہ تمہیں عذاب کرکے کیا کرے گا؟ اور اللہ تو بڑا قدردان ہے (اور) بڑا جاننے والا ہے۔'),
(641, 4, 148, 'اللہ علانیہ بدگوئی کو پسند نہیں کرتا سوائے اس کے کہ جس پر ظلم ہوا ہو (کہ اس کے لیے ظالم کی بدگوئی جائز ہے) اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(642, 4, 149, 'اگر تم کوئی نیکی ظاہر کرو یا اسے چھپاؤ۔ یا کسی برائی سے درگزر کرو۔ تو بے شک اللہ (بھی) بڑا معاف کرنے والا، بڑا قدرت والا ہے۔'),
(643, 4, 150, 'بے شک جو لوگ اللہ اور اس کے رسولوں کا انکار کرتے ہیں اور چاہتے ہیں کہ اللہ اور اس کے پیغمبروں کے درمیان تفریق کریں اور کہتے ہیں کہ ہم بعض پر ایمان لاتے ہیں اور بعض کا انکار کرتے ہیں اور وہ چاہتے ہیں کہ (ایمان و کفر کے) درمیان کوئی راستہ نکالیں۔'),
(644, 4, 151, 'یہی وہ لوگ ہیں، جو حقیقی کافر ہیں اور ہم نے کافروں کے لیے رسوا کرنے والا عذاب مہیا کر رکھا ہے۔'),
(645, 4, 152, 'اور جو لوگ اللہ اور اس کے رسولوں پر ایمان لائے اور ان میں سے کسی کے درمیان تفریق نہیں کی۔ یہی وہ لوگ ہیں جنہیں وہ (اللہ) ان کا اجر و ثواب عطا فرمائے گا۔ اور اللہ تو ہے ہی بڑا بخشنے والا، بڑا رحم کرنے والا۔'),
(646, 4, 153, 'آپ سے اہل کتاب مطالبہ کرتے ہیں کہ آپ ان پر آسمان سے کوئی (لکھی ہوئی) کتاب اتروا دیں سو یہ تو جناب موسیٰ سے اس سے بھی بڑا مطالبہ کر چکے ہیں یعنی کہا تھا کہ ہمیں کھلم کھلا اللہ دکھا دو تو ان کے (اسی) ظلم کی وجہ سے ان کو بجلی نے پکڑ لیا۔ اور پھر ان لوگوں نے آیات و معجزات آجانے کے بعد گوسالے کو (خدا) بنا لیا۔ مگر ہم نے انہیں معاف کر دیا اور موسیٰ کو کھلا ہوا غلبہ عطا کیا۔'),
(647, 4, 154, 'اور ہم نے ان سے (فرمانبرداری کا) پختہ عہد لینے کے لیے ان کے اوپر کوہ طور کو بلند کیا اور ہم نے ان سے کہا سجدہ ریز ہوکر دروازہ (شہر) میں داخل ہو جاؤ اور ان سے کہا کہ قانون سبت کے بارے میں زیادتی نہ کرو (حد سے نہ بڑھو) اور ہم نے ان سے پختہ عہد و پیمان لیا تھا۔'),
(648, 4, 155, 'سو (ان کو جو سزا ملی وہ) ان کے عہد شکنی کرنے، آیاتِ الٰہیہ کا انکار کرنے، نبیوں کو ناحق قتل کرنے اور ان کے اس کہنے کی وجہ سے کہ ہمارے دلوں پر غلاف چڑھے ہوئے ہیں۔ بلکہ ان کے کفر کی وجہ سے اللہ نے ان کے (دلوں) پر مہر لگا دی ہے۔ اس لیے وہ بہت کم ایمان لائیں گے۔'),
(649, 4, 156, 'نیز ان کے ساتھ یہ جو کچھ ہوا یہ ان کے کفر کی وجہ سے ہوا۔ اور جناب مریم پر بہتان عظیم لگانے کی وجہ سے۔'),
(650, 4, 157, 'اور ان کے اس کہنے کی وجہ سے کہ ہم نے خدا کے رسول مسیح عیسیٰ بن مریم کو قتل کر دیا ہے۔ حالانکہ انہوں نے نہ انہیں قتل کیا اور نہ سولی پر چڑھایا، بلکہ ان پر اصل معاملہ مشتبہ کر دیا گیا۔ اور جن لوگوں نے اس بارے میں اختلاف کیا ہے وہ یقینا اس کے متعلق شک میں ہیں اور انہیں گمان کی پیروی کرنے کے سوا کوئی علم نہیں ہے۔ اور انہوں نے یقینا ان کو قتل نہیں کیا۔'),
(651, 4, 158, 'بلکہ اللہ تعالیٰ نے انہیں اپنی طرف اٹھا لیا اور اللہ زبردست ہے، بڑا حکمت والا ہے۔'),
(652, 4, 159, 'اور اہل کتاب میں سے کوئی ایسا نہیں، جو اپنی موت (یا ان کی موت) سے پہلے ان پر ایمان نہ لائے اور وہ قیامت کے دن ان کے خلاف گواہ ہوں گے۔'),
(653, 4, 160, 'یہودیوں کے ایسے ہی مظالم اور زیادتیوں کیوجہ سے ہم نے وہ بہت سی پاکیزہ چیزیں ان پر حرام کر دیں، جو پہلے ان کے لئے حلال تھیں۔ نیز ان کے (لوگوں کو) اللہ کے راستہ سے بکثرت روکنے کی وجہ سے۔'),
(654, 4, 161, 'اور ان کے سود لینے کے سبب سے، حالانکہ انہیں اس سے منع کیا گیا تھا اور لوگوں کا ناحق مال کھانے کے باعث (یہ سب کچھ ہوا)۔ اور ان میں سے جو لوگ کافر ہیں، ہم نے ان کے لئے دردناک عذاب مہیا کر رکھا ہے۔'),
(655, 4, 162, 'البتہ ان میں سے جو علم میں پختہ ہیں اور جو ایمان دار ہیں، وہ سب اس پر ایمان لاتے ہیں، جو کچھ آپ پر نازل کیا گیا اور اس پر بھی جو آپ سے پہلے نازل کیا گیا اور وہ نماز کو قائم کرتے ہیں، زکوٰۃ ادا کرتے ہیں اور اللہ اور آخرت کے دن پر ایمان رکھتے ہیں، یہ وہ لوگ ہیں جن کو ہم بہت بڑا اجر و ثواب عطا کریں گے۔'),
(656, 4, 163, 'بے شک ہم نے آپ کی طرف اسی طرح وحی بھیجی ہے۔ جس طرح (جناب) نوح اور ان کے بعد والے نبیوں کی طرف بھیجی تھی۔ اور ہم نے ابراہیم، اسماعیل، اسحاق، یعقوب، اولادِ یعقوب، عیسیٰ، ایوب، یونس، ہارون اور سلیمان کی طرف وحی بھیجی تھی اور ہم نے داؤد کو زبور عطا کی تھی۔'),
(657, 4, 164, 'کچھ پیغمبر وہ ہیں جن کے حالات ہم نے آپ سے پہلے بیان کر دیئے ہیں اور کچھ وہ ہیں جن کے واقعات ہم نے آپ سے بیان نہیں کئے اور اللہ نے موسیٰ سے اس طرح کلام کیا جیساکہ کلام کرنے کا حق ہے۔'),
(658, 4, 165, 'اور (ہم نے) رسولوں کو خوشخبری دینے والا اور ڈرانے والا بنا کر بھیجا تاکہ رسولوں کے آجانے کے بعد لوگوں کے لیے اللہ کے مقابلہ میں کوئی حجت باقی نہ رہ جائے اور اللہ زبردست، بڑا حکمت والا ہے۔'),
(659, 4, 166, 'اللہ اس بات کی گواہی دیتا ہے کہ اس نے جو کچھ آپ پر نازل کیا۔ وہ اپنے علم سے نازل کیا ہے اور فرشتے (بھی) اس کی گواہی دیتے ہیں۔ اگرچہ گواہی کے لیے اللہ کافی ہے۔'),
(660, 4, 167, 'بے شک جن لوگوں نے کفر اختیار کیا اور (دوسروں کو) اللہ کی راہ سے روکا گمراہی میں بہت دور نکل گئے۔'),
(661, 4, 168, 'بے شک جن لوگوں نے کفر اختیار کیا اور ظلم و ستم کیا، اللہ انہیں بخشنے والا نہیں ہے۔ اور نہ ہی انہیں کوئی راستہ دکھائے گا۔'),
(662, 4, 169, 'سوا جہنم کے راستہ کے جس میں وہ ہمیشہ ہمیشہ رہیں گے اور یہ بات اللہ کے لئے بالکل آسان ہے۔'),
(663, 4, 170, 'اے لوگو! تمہارے پاس پروردگار کی طرف سے رسول حق لے کر آگیا ہے۔ پس ان پر ایمان لاؤ۔ یہ بات تمہارے لئے بہتر ہے۔ اور اگر انکار کروگے تو (اللہ کا کیا نقصان کروگے) جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ وہ اللہ ہی کا ہے۔ اور اللہ بڑا علم والا، بڑا حکمت والا ہے۔'),
(664, 4, 171, 'اے اہل کتاب اپنے دین میں غلو نہ کرو۔ اور اللہ کے بارے میں نہ کہو مگر سچی بات جناب عیسیٰ بن مریم اللہ کے رسول ہیں اور اس کا کلمہ جسے اس نے مریم کی طرف بھیجا۔ اور اللہ کی طرف سے ایک خاص روح ہیں۔ پس اللہ اور اس کے رسولوں پر ایمان لاؤ۔ اور تثلیث (یعنی تین خداؤں) کے قائل نہ ہو۔ (اس سے باز آجاؤ) (یہ) تمہارے لئے بہتر ہے۔ اللہ تو صرف ایک ہے۔ اس کی ذات اس سے پاک ہے کہ اس کی کوئی اولاد ہو۔ جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ سب اسی کا ہے اور اللہ کارسازی کے لئے کافی ہے۔'),
(665, 4, 172, 'عیسیٰ مسیح اللہ کا بندہ ہونے میں اپنے لئے عار نہیں سمجھتے اور نہ ہی مقرب فرشتے اس میں اپنے لئے کوئی عار محسوس کرتے ہیں اور جو اس کی بندگی میں ننگ و عار محسوس کرے گا اور تکبر کرے گا۔ اللہ سب کو جلد اپنی طرف محشور فرمائے گا۔'),
(666, 4, 173, 'پھر جو لوگ ایمان لائے ہوں گے اور نیک عمل کئے ہوں گے انہیں ان کا پورا پورا صلہ و ثواب دے گا اور اپنے فضل و کرم سے انہیں مزید بھی عطا فرمائے گا۔ اور جنہوں نے (اس کی بندگی سے) ننگ و عار محسوس کیا ہوگا اور تکبر کیا ہوگا ان کو دردناک عذاب کی سزا دے گا۔ اور وہ اللہ کے سوا نہ کوئی سرپرست پائیں گے اور نہ کوئی یار و مددگار۔'),
(667, 4, 174, 'اے لوگو! تمہارے پاس تمہارے پروردگار کی طرف سے (روشن) دلیل آگئی ہے۔ اور ہم نے تمہاری طرف نمایاں نور اتارا ہے۔'),
(668, 4, 175, 'سو جو لوگ اللہ پر ایمان لائے اور مضبوطی سے اس کا دامن پکڑا تو ضرور اللہ انہیں اپنی رحمت اور فضل میں داخل کرے گا۔ اور ان کو اپنے سیدھے راستہ پر لگا دے گا۔'),
(669, 4, 176, 'اے رسول یہ لوگ آپ سے فتویٰ پوچھتے ہیں۔ کہہ دیجیے! کہ اللہ تمہیں کلالہ کے بارے میں فتویٰ دیتا ہے کہ اگر کوئی ایسا شخص مر جائے جس کی اولاد نہ ہو اور اس کی ایک بہن ہو تو اس کا آدھا ترکہ اس کا ہوگا۔ اور اگر وہ (بہن) مر جائے اور اس کی کوئی اولاد نہ ہو (اور ایک بھائی ہو) تو وہ اس کے پورے ترکہ کا وارث ہوگا۔ اور اگر کسی مرنے والے کی دو بہنیں ہوں تو ان کا اس کے ترکہ میں سے دو تہائی حصہ ہوگا۔ اور اگر کئی بھائی بہن ہوں یعنی مرد عورت دونوں ہوں تو ایک مرد کو دو عورتوں کے برابر حصہ ملے گا۔ اللہ تعالیٰ تمہارے لئے کھول کر احکام بیان کرتا ہے۔ تاکہ تم گمراہ نہ ہو اور اللہ ہر چیز کا بڑا جاننے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اے ایمان والو! اپنے عہدوں کو پورا کرو تمہارے لئے چوپائے، مویشی حلال کر دیئے گئے ہیں۔ سوائے ان کے جن کا ذکر تمہیں پڑھ کر سنایا جائے گا۔ ہاں جب تم احرام کی حالت میں ہو تو شکار کو حلال نہ سمجھو۔ بے شک اللہ جو چاہتا ہے وہ حکم دیتا ہے۔'),
(671, 5, 2, 'اے ایمان والو! خدا کی نشانیوں کی بے حرمتی نہ کرو۔ اور نہ حرمت والے مہینہ کی اور نہ قربانی والے جانور کی اور نہ گلے میں پٹے ڈالے ہوئے جانوروں کی اور نہ ان لوگوں کی (بے حرمتی کرو) جو اپنے پروردگار کا فضل و کرم اور اس کی رضامندی کے طلبگار بن کر بیت الحرام (مقدس گھر) کی طرف جا رہے ہیں اور جب احرام ختم ہو جائے (یا حرم سے باہر نکل جاؤ) تو شکار کر سکتے ہو۔ اور خبردار، تمہیں کسی قوم سے عداوت کہ اس نے تمہیں مسجد الحرام سے روک دیا تھا۔ اس بات پر آمادہ نہ کرے کہ تم (اس پر) ظلم و زیادتی کرو۔ اور نیکی و پرہیزگاری کے کاموں میں ایک دوسرے کی مدد کرو اور گناہ اور زیادتی کے کاموں میں ایک دوسرے کی مدد نہ کرو۔ اور اللہ (کی نافرمانی) سے ڈرو بے شک اللہ سخت سزا دینے والا ہے۔'),
(672, 5, 3, 'تم پر حرام کیا گیا ہے۔ مردار، خون، خنزیر کا گوشت اور وہ جانور جس پر ذبح کے وقت غیر خدا کا نام لیا جائے۔ اور گلا گھوٹا ہوا، یا جو چوٹ لگنے سے یا بلندی سے گر کر، یا سینگ لگنے سے مر جائے یا جسے کسی درندہ نے کھایا ہو۔ سوائے اس کے جسے تم نے ذبح کر لیا ہو۔ اور وہ (بھی حرام ہے) جو قربان کیا جائے بتوں پر (یا کسی آستانے پر) نیز (وہ بھی حرام ہے) جو قرعہ کے تیروں سے تقسیم کیا جائے۔ یہ سب کام فسق (گناہ) ہیں۔ آج میں نے تمہارے لئے دین کو مکمل کر دیا ہے۔ اور اپنی نعمت تم پر تمام کر دی ہے اور تمہارے لئے دین کی حیثیت سے اسلام کو پسند کر لیا ہے ہاں جو بھوک کی شدت سے مجبور ہو جائے (اور حرام چیزوں سے کوئی کھا لے) جبکہ گناہ کی طرف راغب نہ ہو تو اللہ بڑا بخشنے والا بڑا رحم کرنے والا ہے۔'),
(673, 5, 4, 'لوگ آپ سے پوچھتے ہیں کہ ان کے لئے کون سی چیزیں حلال کی گئی ہیں؟ کہہ دیجیئے کہ تمہارے لئے سب پاک و پاکیزہ چیزیں حلال کر دی گئیں اور جن شکاری جانوروں کو تم نے سدھایا ہو جن کو خدا کے سکھائے ہوئے طریقہ کے مطابق (شکار پکڑنے کا) طریقہ سکھایا ہو تو جسے وہ تمہارے لئے پکڑ رکھیں۔ اس میں سے بھی کھا سکتے ہو۔ اور اس پر (شکار پر چھوڑتے وقت) خدا کا نام لے لو۔ اور اللہ سے (اس کی معصیت کاری سے) ڈرو۔ بے شک اللہ بہت جلد حساب لینے والا ہے۔'),
(674, 5, 5, 'آج تمہارے لئے سب پاک و پاکیزہ چیزیں حلال کر دی گئی ہیں۔ اور اہل کتاب کا طعام (اناج) تمہارے لئے حلال ہے اور تمہارا طعام (اناج) ان کے لئے حلال ہے اور پاکدامن مسلمان عورتیں اور ان لوگوں (اہل کتاب) کی پاکدامن عورتیں جنہیں تم سے پہلے کتاب دی گئی ہے۔ (وہ تمہارے لئے حلال ہیں) بشرطیکہ تم ان کی اجرتیں (حق مہر) دے دو۔ اور وہ بھی اپنی پاکدامنی کے تحفظ کی خاطر نہ کہ آزاد شہوت رانی کی خاطر اور نہ ہی چوری چھپے ناجائز تعلقات قائم کرنے کے لیے۔ اور جو ایمان کا انکار کرے (اور کفر اختیار کرے) تو اس کے سب عمل اکارت ہوگئے اور وہ آخرت میں نقصان اٹھانے والوں میں ہوگا۔'),
(675, 5, 6, 'اے ایمان والو! جب نماز کے لیے کھڑے ہونے لگو تو اپنے چہروں اور کہنیوں سمیت اپنے ہاتھوں کو دھوؤ۔ اور سروں کے بعض حصہ کا اور ٹخنوں تک پاؤں کا مسح کرو۔ اور اگر تم جنابت کی حالت میں ہو تو پھر غسل کرو اور اگر تم بیمار ہو یا سفر میں ہو یا تم میں سے کوئی بیت الخلاء سے آیا ہو۔ یا تم نے عورتوں سے مقاربت کی ہو۔ اور پھر تمہیں پانی نہ ملے تو پاک مٹی سے تیمم کر لو۔ یعنی اپنے چہروں اور ہاتھوں پر اس سے مسح کر لو (مَل لو) اللہ نہیں چاہتا کہ تم پر کوئی سختی کرے۔ وہ تو چاہتا ہے کہ تمہیں پاک صاف رکھے اور اپنی نعمت تم پر تمام کر دے تاکہ تم اس کا شکر ادا کرو۔'),
(676, 5, 7, 'اور یاد کرو اللہ کا وہ احسان جو اس نے تم پر کیا۔ اور اس کے عہد و پیمان کو جو اس نے تم سے لیا۔ جب تم نے کہا تھا کہ ہم نے سنا اور مانا۔ اللہ سے ڈرو۔ بے شک اللہ دلوں کے رازوں کو خوب جانتا ہے۔'),
(677, 5, 8, 'اے ایمان والو! اللہ کے لیے پوری پابندی کرنے والے اور راستی پر قائم رہنے والے۔ اور عدل و انصاف کے ساتھ گواہی دینے والے بنو (خبردار) کسی قوم سے دشمنی تمہیں اس بات پر آمادہ نہ کرے کہ تم انصاف نہ کرو۔ اور عدل سے پھر جاؤ۔ عدل کرو۔ کہ وہ تقویٰ کے زیادہ قریب ہے۔ اللہ سے ڈرو بے شک تم جو کچھ کرتے ہو اللہ اس سے پوری طرح باخبر ہے۔'),
(678, 5, 9, 'اللہ تعالیٰ نے ان لوگوں سے جو ایمان لائے اور نیک عمل کیے وعدہ کیا ہے کہ ان کے لیے بخشش اور بڑا اجر و ثواب ہے۔'),
(679, 5, 10, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیتوں کو جھٹلایا یہی دوزخ والے ہیں۔'),
(680, 5, 11, 'اے ایمان والو! اللہ کے اس احسان کو یاد کرو جو اس نے اس وقت تم پر کیا جب ایک گروہ نے ارادہ کیا تھا کہ وہ تمہاری طرف دست درازی کرے مگر اللہ نے (اپنی قدرت کاملہ سے) ان کو تم سے روک دیا۔ اللہ سے ڈرو۔ اور اہل ایمان کو اللہ پر ہی بھروسہ کرنا چاہیے۔'),
(681, 5, 12, 'اور بلاشبہ اللہ نے بنی اسرائیل سے عہدوپیمان لیا تھا اور ہم نے بھی بارہ نقیب (سردار) مقرر کئے تھے۔ اور اللہ نے (ان سے) کہا تھا کہ بے شک میں تمہارے ساتھ ہوں۔ تو اگر تم نماز قائم رکھوگے، اور زکوٰۃ دیتے رہوگے اور میرے رسولوں پر ایمان لاتے رہوگے، اور ان کی مدد کرتے رہوگے اور اللہ کو قرضہ حسنہ دیتے رہوگے تو میں ضرور تمہارے گناہ تم سے دور کر دوں گا۔ (معاف کر دوں گا) اور تمہیں ان بہشتوں میں داخل کروں گا۔ جن کے نیچے سے نہریں جاری ہوں گی مگر تم میں سے جس نے اس کے بعد کفر اختیار کیا وہ ضرور سیدھے راستے سے بھٹک گیا۔'),
(682, 5, 13, 'ان کی عہد شکنی کی وجہ سے ہم نے ان پر لعنت کی (اپنی رحمت سے دور کر دیا) اور ان کے دلوں کو سخت کر دیا کہ وہ اب کلامِ (الٰہی) کو اس کی اصلی جگہ سے ہٹا دیتے ہیں (تحریف کرتے ہیں) اور جس چیز کی ان کو نصیحت کی گئی تھی اس کا بڑا حصہ بھلا بیٹھے ہیں (اے پیغمبر(ص)) ان کے معدودے چند آدمیوں کے سوا برابر ان کی کسی نہ کسی خیانت پر مطلع ہوتے رہیں گے لہٰذا انہیں معاف کر دیجیے اور ان سے درگزر کیجیے بے شک اللہ معاف کرنے والوں کو دوست رکھتا ہے۔'),
(683, 5, 14, 'اور جو لوگ کہتے ہیں کہ ہم نصرانی ہیں ان سے بھی پختہ عہد لیا تھا۔ سو جو کچھ بھی انہیں نصیحت کی گئی تھی، جب وہ اس کا بڑا حصہ بھلا بیٹھے۔ تو ہم نے (بطورِ سزا) قیامت تک ان کے درمیان بغض و عناد ڈال دیا۔ اور عنقریب اللہ ان کو بتائے گا کہ وہ (دنیا میں کیا کرتے تھے)۔'),
(684, 5, 15, 'اے اہل کتاب! تمہارے پاس ہمارا وہ پیغمبر آگیا ہے جو بہت سی ان باتوں کو کھول کر بیان کر رہا ہے۔ جنہیں تم اس کتاب میں سے چھپاتے رہے ہو اور بہت سی باتوں کو نظر انداز بھی کر دیتا ہے۔ بے شک تمہارے پاس اللہ کی طرف سے نور (روشنی) اور واضح کتاب آگئی ہے۔'),
(685, 5, 16, 'جس کے ذریعہ سے خدا ان لوگوں کو سلامتی کے راستوں پر چلاتا ہے جو اس کی رضا کی اتباع و پیروی کرتے ہیں اور ان کو تاریکیوں (گمراہی) سے اپنے اذن و توفیق سے نور (ہدایت) کی طرف لاتا ہے اور انہیں سیدھے راستہ پر لگاتا ہے۔'),
(686, 5, 17, 'یقینا وہ لوگ (نصرانی) کافر ہوگئے، جنہوں نے کہا کہ مسیح بن مریم ہی اللہ ہے۔ کہہ دیجیے کہ اگر اللہ مسیح مریم کو ان کی ماں اور تمام اہل زمین کو ہلاک کرنا چاہے۔ تو اس کے آگے کس کا ذرا بھی بس چل سکتا ہے (کہ اسے روک دے) آسمانوں پر، زمین پر اور جو کچھ ان کے درمیان ہے۔ ان سب پر، اللہ ہی کی حکومت ہے۔ وہ جو چاہتا ہے، پیدا کرتا ہے اور اللہ تو ہر چیز پر پوری قدرت رکھتا ہے۔'),
(687, 5, 18, 'یہود و نصاریٰ کہتے ہیں کہ ہم اللہ کے بیٹے اور اس کے چہیتے ہیں۔ ان سے کہو (پوچھو) پھر خدا تمہیں تمہارے گناہوں پر سزا کیوں دیتا ہے؟ (نہیں) بلکہ تم بھی اس کی مخلوقات میں سے محض بشر (انسان) ہو۔ وہ جسے چاہتا ہے، بخش دیتا ہے اور جسے چاہتا ہے۔ سزا دیتا ہے اور اللہ ہی کے لئے ہے، سلطنت آسمانوں کی، زمین کی اور جو کچھ ان کے درمیان ہے اس کی اور سب کی بازگشت اسی کی طرف ہے۔'),
(688, 5, 19, 'اے اہلِ کتاب، ہمارا رسول جو کھول کر (ہمارے احکام) بیان کرتا ہے۔ ایسے وقت تمہارے پاس آیا کہ جب کچھ عرصہ سے رسولوں کی آمد کا سلسلہ بند تھا، تاکہ تم یہ نہ کہہ سکو کہ ہمارے پاس کوئی خوشخبری دینے والا اور ڈرانے والا نہیں آیا، تو لو تمہارے پاس بشارت دینے والا اور ڈرانے والا آگیا ہے اور اللہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(689, 5, 20, 'وہ وقت یاد کرو۔ جب جناب موسیٰ نے اپنی قوم سے کہا اے میری قوم! تم اللہ کا وہ احسان یاد کرو۔ جو اس نے تم پر کیا۔ اس نے تم میں نبی بنائے اور تمہیں فرمانروا اور خودمختار بنایا۔ اور تمہیں وہ کچھ دیا جو دنیا جہان والوں میں سے کسی کو نہیں دیا۔'),
(690, 5, 21, 'اے میری قوم! اس مقدس زمین میں داخل ہو جاؤ جو اللہ نے تمہارے لئے لکھ دی ہے اور (مقابلہ کے وقت) پیٹھ دکھا کر نہ بھاگنا۔ ورنہ نقصان اٹھا کر لوٹو گے۔'),
(691, 5, 22, 'انہوں نے (جواب میں) کہا: اے موسیٰ اس زمین میں تو بڑے زبردست (جابر) لوگ رہتے ہیں اور ہم وہاں ہرگز داخل نہیں ہوں گے جب تک وہ وہاں سے نکل نہ جائیں۔ ہاں البتہ اگر وہ وہاں سے نکل جائیں تو پھر ہم ضرور داخل ہو جائیں گے۔'),
(692, 5, 23, 'اس وقت دو شخصوں نے جو (اللہ سے) ڈرنے والوں میں سے تھے۔ جنہیں اللہ نے (خصوصی) نعمت سے نوازا تھا۔ کہا۔ کہ تم ان (جباروں) پر چڑھائی کرکے دروازہ کے اندر گھس جاؤ اور جب تم اس کے اندر داخل ہوگے (تو وہ بھاگ کھڑے ہوں گے اور) تم غالب آجاؤگے اور اللہ پر بھروسہ کرو اگر تم ایمان دار ہو۔'),
(693, 5, 24, 'انہوں نے کہا موسیٰ! ہم ہرگز اس میں داخل نہیں ہوں گے۔ جب تک وہ اس میں ہیں۔ لہٰذا آپ اور آپ کا خدا دونوں چلے جائیں اور جا کر لڑیں ہم تو یہیں بیٹھے ہیں۔'),
(694, 5, 25, 'موسیٰ نے کہا پروردگار! میں اپنے اور اپنے بھائی کے سوا اور کسی پر کوئی قابو (اختیار) نہیں رکھتا۔ پس تو ہمارے اور اس نافرمان قوم کے درمیان فیصلہ کر دے۔'),
(695, 5, 26, 'ﷲ نے فرمایا! پھر اب وہ (زمین) چالیس سال تک حرام ہے وہ لق و دق صحراء میں سرگردان پھرتے رہیں گے پس آپ اس نافرمان قوم پر افسوس نہ کریں۔'),
(696, 5, 27, '(اے رسول(ص)) آپ انہیں آدم کے دونوں بیٹوں کا سچا قصہ پڑھ کر سنائیے۔ جب کہ ان دونوں نے قربانی پیش کی تو ایک کی تو قبول ہوگئی اور دوسرے کی قبول نہ ہوئی۔ اس (دوسرے) نے کہا میں تمہیں ضرور قتل کروں گا۔ پہلے نے کہا اللہ تو صرف متقیوں (پرہیزگاروں) کا عمل قبول کرتا ہے۔'),
(697, 5, 28, 'اگر تو مجھے قتل کرنے کے لئے ہاتھ بڑھائے گا۔ تو میں تمہیں قتل کرنے کے لئے ہاتھ نہیں بڑھاؤں گا۔ (کیونکہ) میں تو ڈرتا ہوں اس اللہ سے، جو تمام جہانوں کا پروردگار ہے۔'),
(698, 5, 29, 'میں تو یہ چاہتا ہوں کہ تو میرا گناہ اور اپنا گناہ سمیٹ لے جائے اور پھر دوزخیوں میں سے ہو جائے کہ ظالموں کی یہی سزا ہے۔'),
(699, 5, 30, 'تو (بالآخر) اس کے نفس نے اپنے بھائی کے قتل کو اس کے لئے آسان بنا دیا۔ چنانچہ اس نے اسے قتل کر دیا اور وہ نقصان اٹھانے والوں میں سے ہوگیا۔'),
(700, 5, 31, 'پھر اللہ نے ایک کوا بھیجا جو زمین کھودتا تھا تاکہ اسے دکھائے کہ وہ کیونکر اپنے بھائی کی لاش چھپائے اس پر اس نے کہا، ہائے افسوس! کیا میں اس کوے سے بھی زیادہ گیا گزرا ہوں؟ کہ اپنے بھائی کی لاش چھپاؤں اب وہ پشیمان ہونے والوں میں سے ہوگیا۔'),
(701, 5, 32, 'اسی وجہ سے ہم نے بنی اسرائیل پر لکھ دیا کہ جس نے کسی انسان کو قتل کیا بغیر اس کے کہ اس نے کسی کی جان لی ہو، یا زمین میں فساد برپا کیا ہو، تو وہ ایسا ہے جیسے کہ اس نے تمام انسانوں کو قتل کر دیا اور جس نے کسی ایک جان کو بچا لیا، تو وہ ایسا ہے جیسے کہ اس نے تمام انسانوں کو بچا لیا اور بے شک ان (بنی اسرائیل) کے پاس ہمارے رسول کھلی ہوئی نشانیاں لے کر آئے۔ پھر بھی ان میں سے بہت سے لوگ اس کے بعد بھی زمین میں ظلم و تعدی کرنے والے ہی رہے۔'),
(702, 5, 33, 'بے شک جو لوگ خدا اور رسول سے لڑتے ہیں اور زمین میں فساد برپا کرنے کے لیے دوڑتے پھرتے ہیں، ان کی سزا یہ ہے کہ (۱) انہیں قتل کر دیا جائے۔ (۲) یا سولی پر چڑھا دیا جائے۔ (۳) یا ان کے ہاتھ پاؤں مخالف سمتوں سے کاٹ دیے جائیں۔ (۴) یا جلا وطن کر دیئے جائیں۔ یہ تو ہوئی ان کی رسوائی دنیا میں اور آخرت میں ان کے لئے بڑا عذاب ہے۔'),
(703, 5, 34, 'مگر وہ لوگ جو توبہ کر لیں قبل اس کے کہ تم ان پر قابو پاؤ۔ تو جان لو کہ اللہ بڑا بخشنے والا بڑا مہربان ہے۔'),
(704, 5, 35, 'اے ایمان والو اللہ سے ڈرو! اور اس تک پہنچنے کے لیے وسیلہ تلاش کرو۔ اور اس کی راہ میں جہاد کرو۔ تاکہ تم کامیاب ہو جاؤ۔'),
(705, 5, 36, 'بے شک وہ لوگ جنہوں نے کفر اختیار کیا اگر ان کے پاس وہ سب کچھ ہو جو زمین میں ہے اور اتنا اور بھی ہو، تاکہ اسے بطور فدیہ دیں اور قیامت کے دن عذاب سے بچ جائیں، تو وہ بھی ان سے قبول نہ کیا جائے گا۔ اور ان کے لئے دردناک عذاب ہے۔'),
(706, 5, 37, 'وہ چاہیں گے کہ آگ سے نکل جائیں، مگر وہ اس سے کبھی نہیں نکل پائیں گے۔ اور ان کے لئے دائمی عذاب ہے۔'),
(707, 5, 38, 'اور چور مرد ہو یا چور عورت دونوں کے ہاتھ کاٹ دو۔ ان کے کرتوتوں کے عوض میں اللہ کی طرف سے بطور عبرتناک سزا کے اللہ زبردست ہے، بڑا حکمت والا ہے۔'),
(708, 5, 39, 'پھر جو شخص توبہ کرلے اپنے ظلم سے اور (اپنی) اصلاح کر لے، تو یقینا اللہ اس کی توبہ قبول کرے گا۔ بیشک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(709, 5, 40, 'کیا تم نہیں جانتے کہ اللہ کے لئے آسمانوں اور زمین کی حکومت ہے۔ جسے چاہتا ہے، سزا دیتا ہے، اور جسے چاہتا ہے بخش دیتا ہے۔ اور اللہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(710, 5, 41, 'اے رسول! وہ لوگ آپ کو غمگین نہ کریں جو کفر کی طرف تیزگامی سے بڑھ رہے ہوں۔ خواہ یہ ان لوگوں میں سے ہوں جو منہ سے تو کہتے ہیں کہ ہم ایمان لائے، مگر ان کے دل ایمان نہیں لائے اور یا ان میں سے ہوں جو یہودی ہیں جھوٹ کے بڑے سننے والے ہیں اور سن گن لیتے ہیں دوسرے لوگوں کے لئے جو آپ کے پاس نہیں آئے۔ وہ (کتاب خدا کے) کلمات کو ان کے صحیح موقعوں سے ہٹا دیتے ہیں اور (تحریف کرنے کے بعد) کہتے ہیں کہ اگر تمہیں یہ حکم دیا جائے تو قبول کرو۔ اور اگر یہ نہ دیا جائے تو پرہیز کرو۔ اور اللہ جسے عذاب کرنا چاہے، تو آپ اس کے لئے کچھ نہیں کر سکتے۔ اللہ کے ہاں یہ وہ ہیں کہ اللہ نے (زبردستی) ان کے دلوں کو پاک کرنے کا ارادہ ہی نہیں کیا۔ ان کے لئے دنیا میں رسوائی ہے اور آخرت میں بہت بڑا عذاب ہے۔'),
(711, 5, 42, 'جھوٹ کے بڑے سننے والے ہیں، حرام مال کے بڑے کھانے والے ہیں اگر وہ تمہارے پاس آئیں تو (آپ کو اختیار ہے) خواہ ان کا فیصلہ کریں یا ان سے روگردانی کریں اور اگر آپ ان سے روگردانی کریں۔ تو وہ آپ کا کچھ نہیں بگاڑ سکتے اور اگر فیصلہ کریں تو پھر انصاف کے ساتھ کریں کیونکہ اللہ انصاف کرنے والوں کو دوست رکھتا ہے۔'),
(712, 5, 43, 'اور یہ لوگ آپ کو کس طرح حَکَم (ثالث) بناتے ہیں، حالانکہ ان کے پاس توراۃ موجود ہے جس میں اللہ کا حکم لکھا ہوا ہے اور پھر یہ (آپ کے فیصلہ کے) بعد اس سے منہ موڑ لیتے ہیں (بات دراصل یہ ہے کہ) یہ مؤمن ہی نہیں ہیں (ہرگز ماننے والے نہیں ہیں)۔'),
(713, 5, 44, 'ہم نے توراۃ نازل کی جس میں ہدایت اور نور (روشنی) تھی اس کے مطابق یہودیوں کے لئے فیصلے کرتے تھے۔ وہ تمام نبی جو اللہ کے مطیع و فرمانبردار تھے اللہ والے علماء و احبار بھی اس کے مطابق فیصلے کرتے تھے جس کی حفاظت کے وہ ذمہ دار بنائے گئے تھے اور جس کے وہ گواہ تھے۔ پس تم لوگوں سے نہ ڈرو۔ بلکہ مجھ سے ڈرو۔ اور میری آیات کو تھوڑی سی قیمت کے عوض فروخت نہ کرو اور جو اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں، وہ کافر ہیں۔'),
(714, 5, 45, 'اور ہم نے اس (توراۃ) میں ان (یہودیوں) پر یہ حکم لکھ دیا تھا کہ جان کا بدلہ جان ہے اور آنکھ کے بدلے آنکھ، ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور زخموں میں بھی برابر کا بدلہ ہے۔ پھر جو (قصاص) معاف کر دے، تو وہ اس کے لئے کفارہ ہوگا اور جو قانونِ خدا کے مطابق فیصلہ نہ کریں، وہ ظالم ہیں۔'),
(715, 5, 46, 'ہم نے ان (انبیاء) کے نقش قدم پر عیسیٰ کو بھیجا اپنے سے پہلے موجود کتاب یعنی تورات کی تصدیق کرنے والا بنا کر اور ہم نے ان کو انجیل عطا کی۔ جس میں ہدایت ہے اور نور بھی جو تصدیق کرنے والی تھی۔ اپنے سے پہلے موجود کتاب یعنی تورات کی اور یہ (انجیل) صحیح رہنمائی اور نصیحت تھی پرہیزگاروں کے لیے۔'),
(716, 5, 47, 'چاہیے کہ انجیل والے (نصرانی) اس (قانون) کے مطابق فیصلہ کریں، جو اللہ نے اس (انجیل) میں نازل کیا ہے اور جو اللہ کے نازل کردہ (قانون) کے مطابق فیصلہ نہیں کرتے وہی فاسق (نافرمان) ہیں۔'),
(717, 5, 48, 'اور (اے رسول(ص)) ہم نے آپ کی طرف حق کے ساتھ کتاب نازل کی ہے۔ جو تصدیق کرتی ہے۔ ان (آسمانی) کتابوں کی جو اس سے پہلے موجود ہیں اور ان کی محافظ و نگہبان ہے۔ لہٰذا آپ ان کے درمیان وہی فیصلہ کریں جو اللہ نے نازل کیا ہے اور اس حق سے منہ موڑ کر جو آپ کے پاس آگیا ہے۔ ان کی خواہشات کی پیروی نہ کریں، ہم نے تم میں سے ہر ایک کے لئے ایک شریعت اور ایک راہ مقرر کی ہے اور اگر خدا (زبردستی) چاہتا، تو تم سب کو ایک ہی (شریعت) کی ایک ہی امت بنا دیتا۔ مگر وہ چاہتا ہے کہ تمہیں آزمائے (ان احکام میں) جو (مختلف اوقات میں) تمہیں دیتا رہا ہے بس تم نیکیوں میں ایک دوسرے سے بڑھنے کی کوشش کرو۔ تم سب کی بازگشت اللہ ہی کی طرف ہے، پھر وہ تمہیں آگاہ کرے گا۔ ان باتوں سے جن میں تم اختلاف کرتے ہو۔'),
(718, 5, 49, 'اور ان کے درمیان اس کے مطابق فیصلہ کریں، جو اللہ نے تم پر نازل کیا ہے اور ان کی خواہشات کی پیروی نہ کریں اور ان سے ہوشیار رہیں کہ کہیں وہ آپ کو اللہ کے نازل کردہ کسی حکم سے بھٹکا نہ دیں اور اگر وہ روگردانی کریں، تو جان لیجیے کہ اللہ بس یہ چاہتا ہے کہ وہ انہیں ان کے بعض گناہوں کی سزا دے۔ بے شک زیادہ تر لوگ فاسق (نافرمان ہیں)۔'),
(719, 5, 50, 'کیا وہ دورِ جاہلیت کے فیصلے چاہتے ہیں؟ حالانکہ یقین رکھنے والے لوگوں کے لئے اللہ سے بہتر فیصلہ کرنے والا کون ہے؟'),
(720, 5, 51, 'اے ایمان والو! یہود و نصاریٰ کو اپنا دوست نہ بناؤ، وہ آپس میں ایک دوسرے کے دوست ہیں اور تم میں سے جو ان سے دوستی کرے گا وہ انہی میں سے شمار ہوگا۔ بے شک خدا ظالموں کو ہدایت نہیں کرتا۔'),
(721, 5, 52, '(اے رسول(ص)) آپ دیکھیں گے کہ جن لوگوں کے دلوں میں نفاق کی بیماری ہے۔ وہ دوڑ دوڑ کر ان (یہود و نصاریٰ) کی طرف جاتے ہیں، کہتے ہیں کہ ہمیں اندیشہ ہے کہ کہیں ہم پر کوئی گردش زمانہ نہ آجائے سو قریب ہے کہ اللہ (تمہیں) فتح سے ہمکنار کر دے یا (کامیابی کی) کوئی اور صورت اپنی طرف سے ظاہر کر دے، تو پھر وہ اس پر جو انہوں نے اپنے دلوں میں چھپا رکھا ہے نادم و پشیمان ہوں گے۔'),
(722, 5, 53, 'اور جو ایمان والے ہیں۔ وہ (حیرت سے) کہیں گے کیا یہ وہ لوگ ہیں۔ جنہوں نے بڑی سخت قسمیں کھائی تھیں کہ بے شک وہ تمہارے ساتھ ہیں۔ ان کے سب عمل ضائع ہوگئے اور وہ نقصان اٹھانے والے ہوگئے۔'),
(723, 5, 54, 'اے ایمان والو! جو تم میں سے اپنے دین سے مرتد ہو جائے (پھر جائے) تو خدا کو کیا پروا؟ ﷲ عنقریب ایسے لوگوں کو لے آئے گا جن سے وہ محبت کرتا ہوگا اور وہ اس سے محبت کرتے ہوں گے۔ وہ مؤمنوں پر نرم اور کافروں پر سخت ہوں گے وہ اللہ کی راہ میں جہاد کریں گے اور کسی ملامت کرنے والے کی ملامت کی پرواہ نہ کریں گے۔ یہ اللہ کا فضل ہے، جسے چاہتا ہے، عطا کرتا ہے اور اللہ بڑا وسعت والا بڑا جاننے والا ہے۔'),
(724, 5, 55, 'اے ایمان والو! تمہارا حاکم و سرپرست اللہ ہے۔ اس کا رسول ہے اور وہ صاحبان ایمان ہیں۔ جو نماز پڑھتے ہیں اور حالت رکوع میں زکوٰۃ ادا کرتے ہیں۔'),
(725, 5, 56, 'اور جو تولاّ رکھے، اللہ سے، اس کے رسول سے اور صاحبانِ ایمان سے (وہ اللہ کا گروہ ہے) اور بے شک اللہ کا گروہ ہی غالب آنے والا ہے۔'),
(726, 5, 57, 'اے ایمان والو! وہ لوگ جنہیں تم سے پہلے کتاب دی گئی اور انہوں نے تمہارے دین کو مذاق اور کھیل بنا رکھا ہے۔ ان کو اور دوسرے عام کفار کو اپنا دوست نہ بناؤ اور اللہ سے ڈرو، اگر تم مؤمن ہو۔'),
(727, 5, 58, 'اور جب تم (اذان دے کر لوگوں کو) نماز کی طرف بلاتے ہو تو وہ اسے مذاق اور کھیل بناتے ہیں۔ یہ اس لئے ہے کہ وہ بے وقوف ہیں، عقل سے کام نہیں لیتے۔'),
(728, 5, 59, '(اے رسول(ص)) کہیے! اے اہل کتاب تم ہم پر کیا عیب لگاتے ہو؟ یہی کہ ہم اللہ پر، جو کچھ ہم پر اتارا گیا ہے۔ اس پر اور جو اس سے پہلے اتارا گیا، اس پر ایمان لائے ہیں (حقیقت یہ ہے کہ) تم میں سے زیادہ تر فاسق (نافرمان) ہیں۔'),
(729, 5, 60, 'کہیئے! کیا میں بتاؤں؟ کہ اللہ کے نزدیک انجام کے اعتبار سے زیادہ بُرا کون ہے؟ وہ ہے جن پر اللہ نے لعنت کی ہے اور جس پر وہ غضبناک ہے۔ اور جس میں سے اس نے بعض کو بندر اور بعض کو سور بنایا ہے اور جس نے شیطان (معبود باطل) کی عبادت کی ہو یہی وہ لوگ ہیں جوکہ درجہ کے لحاظ سے بدترین ہیں اور راہِ راست سے زیادہ بھٹکے ہوئے ہیں۔'),
(730, 5, 61, 'اور وہ جب آپ لوگوں کے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لائے ہیں حالانکہ وہ یہاں داخل ہوئے تو بھی کفر کے ساتھ اور یہاں سے نکلے تو بھی کفر کے ساتھ اور وہ لوگ جو (نفاق) چھپائے ہوئے ہیں اللہ اسے خوب جانتا ہے۔'),
(731, 5, 62, 'تم ان میں سے بہتوں کو دیکھوگے کہ وہ گناہ، ظلم و تعدی کرنے اور حرام خوری میں بڑی تیزی دکھاتے ہیں، کتنا برا ہے، وہ کام جو یہ کرتے ہیں۔'),
(732, 5, 63, 'خدا والے فضلاء اور علماء ان کو گناہ کی بات کرنے (جھوٹ بولنے) اور حرام کھانے سے منع کیوں نہیں کرتے؟ کتنا برا ہے وہ کام جو یہ (علماء) کر رہے ہیں۔'),
(733, 5, 64, 'یہودی کہتے ہیں کہ خدا کا ہاتھ بندھا ہوا ہے (کچھ نہیں کر سکتا) ان کے ہاتھ بندھیں اور اس (بے ادبانہ) قول کی وجہ سے ان پر لعنت ہو۔ بلکہ اس کے ہاتھ کھلے ہوئے ہیں۔ وہ جس طرح چاہتا ہے خرچ کرتا ہے اور جو آپ کے پروردگار کی طرف سے آپ پر نازل کیا گیا ہے۔ وہ ان میں سے بہت سوں کی سرکشی اور کفر میں اضافہ کرتا ہے اور ہم نے ان کے درمیان قیامت تک دشمنی اور بغض و کینہ ڈال دیا ہے۔ وہ جب بھی لڑائی کی آگ بھڑکاتے ہیں۔ تو اللہ اسے بجھا دیتا ہے اور یہ زمین میں فساد برپا کرنے کی سعی و کوشش کرتے ہیں اور اللہ فساد برپا کرنے والوں کو پسند نہیں کرتا۔'),
(734, 5, 65, 'اور اگر اہلِ کتاب (یہود و نصاریٰ) ایمان لاتے اور تقویٰ اختیار کرتے، تو ہم ان کی برائیاں دور کر دیتے اور انہیں نعمت و راحت والے بہشتوں میں داخل کرتے۔'),
(735, 5, 66, 'اور اگر وہ (اہلِ کتاب) تورات، انجیل اور جو ان کے پروردگار کی طرف سے ان کی طرف نازل کیا گیا تھا کو قائم رکھتے، تو وہ اپنے اوپر اور نیچے سے کھاتے پیتے۔ ان میں سے ایک گروہ تو میانہ رو ہے۔ مگر ان میں سے زیادہ لوگ ایسے ہیں جو بہت برا کر رہے ہیں۔'),
(736, 5, 67, 'اے رسول! جو کچھ آپ کے پروردگار کی طرف سے آپ پر اتارا گیا ہے۔ اسے (لوگوں تک) پہنچا دیجیے اور اگر آپ نے ایسا نہ کیا تو (پھر یہ سمجھا جائے گا کہ) آپ نے اس کا کوئی پیغام پہنچایا ہی نہیں۔ اور اللہ آپ کی لوگوں (کے شر) سے حفاظت کرے گا بے شک خدا کافروں کو ہدایت نہیں کرتا۔'),
(737, 5, 68, 'اے اہلِ کتاب تم کسی راہ (حق) پر نہیں ہو جب تک کہ تورات، انجیل کو اور جو کچھ پروردگار کی طرف سے نازل ہوا ہے۔ اس کو قائم نہ رکھو اور جو کچھ آپ کے پروردگار کی طرف سے آپ پر اتارا گیا ہے (قرآن) وہ ان میں سے بہت سوں کی سرکشی اور کفر میں اضافہ کرے گا آپ کافروں پر افسوس نہ کریں۔'),
(738, 5, 69, 'بے شک جو لوگ مؤمن، یہودی، نصرانی اور صابی (ستارہ پرست) کہلاتے ہیں (غرض) جو کوئی بھی واقعی اللہ اور آخرت پر ایمان لائے اور نیک عمل کرے تو ان (سب) کے لئے ان کے پروردگار کے پاس ان کا اجر و ثواب (محفوظ) ہے اور ان کے لئے نہ کوئی خوف ہے۔ اور نہ ہی وہ غمگین ہوں گے۔'),
(739, 5, 70, 'ہم نے بنی اسرائیل سے عہد لیا تھا اور ان کی طرف کئی رسول بھیجے تھے جب بھی کوئی رسول ان کے پاس کوئی ایسی بات لے کر آتا جسے ان کے نفوس پسند نہیں کرتے تھے تو بعضوں کو جھٹلا دیتے تھے اور بعضوں کو قتل کر دیتے تھے۔'),
(740, 5, 71, 'اور خیال کرتے تھے کہ (انہیں) کوئی سزا نہیں ملے گی۔ اس لئے وہ اندھے اور بہرے ہوگئے پھر توبہ کی تو اللہ نے ان کی توبہ قبول کر لی (لیکن) اس کے بعد پھر ان میں سے بہت سے لوگ اندھے اور بہرے ہوگئے۔ وہ جو کچھ کر رہے ہیں۔ اللہ اسے خوب دیکھ رہا ہے۔'),
(741, 5, 72, 'یقینا وہ لوگ کافر ہیں۔ جنہوں نے کہا کہ مسیح بن مریم ہی اللہ ہے حالانکہ خود عیسیٰ نے یہ کہا تھا کہ اے بنی اسرائیل اللہ کی عبادت کرو۔ جو میرا بھی پروردگار ہے اور تمہارا بھی پروردگار ہے۔ بے شک جو شخص کسی کو اللہ کے ساتھ شریک ٹھہرائے گا۔ تو اللہ اس پر جنت حرام کر دے گا اور اس کا ٹھکانا جہنم ہے اور ظالموں کا کوئی یار و مددگار نہیں ہے۔'),
(742, 5, 73, 'یقینا وہ لوگ (بھی) کافر ہو گئے جنہوں نے کہا کہ اللہ تین میں سے تیسرا ہے حالانکہ ایک خدا کے سوا کوئی خدا نہیں ہے۔ اور اگر یہ لوگ ان باتوں سے باز نہ آئے تو جو ان میں سے کفر پر برقرار رہیں گے تو انہیں ضرور دردناک عذاب پہنچے گا۔'),
(743, 5, 74, 'یہ لوگ خدا کی بارگاہ میں توبہ کیوں نہیں کرتے اور اس سے (اپنے گناہوں کی) معافی کیوں نہیں مانگتے؟ درآنحالیکہ اللہ بڑا بخشنے والا بڑا رحم کرنے والا ہے۔'),
(744, 5, 75, 'مسیح گزر چکے ہیں ان کی ماں صدیقہ (راست باز خاتون) تھیں وہ دونوں کھانا کھاتے تھے۔ دیکھو ہم کس طرح ان کے سامنے واضح دلائل بیان کر رہے ہیں۔ پھر دیکھو یہ کدھر الٹے پھرے جا رہے ہیں۔'),
(745, 5, 76, '(اے رسول(ص)) ان سے کہیئے! کیا تم اللہ کو کو چھوڑ کر اس کی عبادت کرتے ہو جو تمہیں نہ نفع پہنچا سکتا ہے نہ نقصان (نہ سود نہ زیاں؟) اور اللہ وہ ہے جو ہر بات کا سننے والا ہر چیز کا جاننے والا ہے۔'),
(746, 5, 77, 'کہیئے! اے اہل کتاب اپنے دین میں ناحق غلو نہ کرو۔ اور ان لوگوں کی خواہشات اور ذاتی خیالات کی پیروی نہ کرو جو پہلے خود گمراہ ہو چکے ہیں۔ اور بہت سوں کو گمراہ کر چکے ہیں اور راہ راست سے بھٹک گئے ہیں۔'),
(747, 5, 78, 'بنی اسرائیل میں سے جن لوگوں نے کفر اختیار کیا ان پر داؤد اور عیسیٰ بن مریم کی زبان سے لعنت کی گئی یہ اس لیے کہ انہوں نے برابر نافرمانی کی اور حد سے بڑھ جاتے تھے۔'),
(748, 5, 79, 'جو برائی وہ کرتے تھے۔ اس سے ایک دوسرے کو منع نہیں کرتے تھے۔ کیسا برا تھا۔ وہ کام جو وہ کرتے تھے۔'),
(749, 5, 80, 'آپ ان میں سے بہتوں کو دیکھیں گے کہ وہ اہلِ اسلام کے بالمقابل کافروں سے دوستی رکھتے ہیں بہت ہی برا ہے وہ (سامان) جو ان کے نفسوں نے ان کے لئے آگے بھیجا ہے۔ (جس سے) اللہ ان پر غضبناک ہوگیا اور وہ ہمیشہ ہمیشہ عذاب میں رہیں گے۔'),
(750, 5, 81, 'اور اگر وہ خدا پر، رسول(ص) پر اور جو کچھ اس (رسول(ص)) پر نازل کیا گیا ہے۔ اس پر ایمان لاتے، تو ان کافروں کو اپنا دوست نہ بناتے۔ لیکن (بات دراصل یہ ہے کہ) ان میں سے زیادہ تر لوگ فاسق و فاجر (نافرمان) ہیں۔'),
(751, 5, 82, 'اے پیغمبر(ص)! آپ اہل ایمان سے دشمنی کرنے میں سب لوگوں سے زیادہ سخت ترین دشمن یہودیوں اور مشرکوں کو پائیں گے اور اہل ایمان سے دوستی کرنے میں آپ سب سے زیادہ قریب ان لوگوں کو پائیں گے جو کہتے ہیں کہ ہم نصرانی ہیں یہ اس لئے ہے کہ ان میں پادری اور تارک الدنیا عابد پائے جاتے ہیں اور اس لیے کہ وہ تکبر نہیں کرتے۔'),
(752, 5, 83, 'اور جب وہ اس کلام کو سنتے ہیں جو (ہمارے) پیغمبر پر اتارا گیا ہے تو تم دیکھوگے کہ ان کی آنکھیں آنسوؤں سے چھلک رہی ہوتی ہیں اس لئے کہ انہوں نے حق کو پہچان لیا ہے وہ کہتے ہیں پروردگار ہم ایمان لائے سو تو ہم کو (صداقتِ اسلام کی) گواہی دینے والوں میں درج فرما۔'),
(753, 5, 84, 'اور ہمیں کیا ہے کہ ہم اللہ پر اور اس حق پر جو ہمیں پہنچا ہے ایمان نہ لائیں حالانکہ ہم خواہش رکھتے ہیں کہ ہمارا پروردگار ہمیں (اپنے) نیک بندوں میں شامل کرے۔'),
(754, 5, 85, 'اللہ نے ان کو ان کے اس قول کے صلہ میں ایسے بہشت عطا فرمائے جن کے نیچے نہریں جاری ہیں جن میں وہ ہمیشہ رہیں گے اور یہ نیکوکاروں کا معاوضہ ہے۔'),
(755, 5, 86, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیتوں کو جھٹلایا تو وہی دوزخ والے ہیں۔'),
(756, 5, 87, 'اے ایمان والو! (اپنے اوپر) حرام نہ کرو ان پاکیزہ چیزوں کو جو اللہ نے تمہارے لئے حلال کی ہیں اور (حد سے) تجاوز نہ کرو بے شک اللہ (حد سے) تجاوز کرنے والوں کو پسند نہیں فرماتا۔'),
(757, 5, 88, 'اور اللہ نے تمہیں جو حلال اور پاکیزہ چیزیں عطا کی ہیں ان میں سے کھاؤ۔ اور اسی اللہ سے ڈرو (اس کی نافرمانی سے بچو) جس پر تم ایمان رکھتے ہو۔'),
(758, 5, 89, 'اللہ تم سے تمہاری لایعنی قَسموں پر مواخذہ (بازپرس) نہیں کرے گا۔ مگر جو قسمیں تم نے قصداً کھائی ہیں (اور اس طرح مضبوط کی ہیں) تو ان پر ضرور تم سے مواخذہ کرے گا۔ اور (ایسی قسم توڑنے کا) کفارہ یہ ہے: (۱)دس مسکینوں کو کھانا کھلانا اوسط درجے کا جو تم اپنے گھر والوں کو کھلاتے ہو۔ (۲) یا انہیں کپڑے پہناؤ۔ (۳) یا پھر ایک غلام آزاد کرو۔ اور جس کو اس کا مقدور نہ ہو تو وہ تین روزہ رکھے۔ یہ تمہاری قَسموں کا کفارہ ہے جب تم قَسم کھاؤ اور اپنی قَسموں کی حفاظت کرو (خیال رکھو) اسی طرح اللہ تمہارے لئے اپنے آیات و احکام کھول کر بیان کرتا ہے تاکہ تم شکر گزار بنو۔'),
(759, 5, 90, 'اے ایمان والو! شراب، جوا، بت (یا آستانے، اور پانسے) سب گندے اور شیطانی کام ہیں ان سے اجتناب کرو۔ تاکہ تم فلاح پاؤ۔'),
(760, 5, 91, 'شیطان تو بس یہی چاہتا ہے کہ تمہارے درمیان شراب اور جوئے کے ذریعہ سے بغض و عداوت ڈالے۔ اور تمہیں اللہ کی یاد اور نماز سے باز رکھے (روکے)۔ کیا اب تم (ان چیزوں سے) باز آؤگے؟ ۔'),
(761, 5, 92, 'اور اطاعت کرو اللہ کی اور اطاعت کرو رسول کی اور (نافرمانی سے) بچتے رہو اور اگر تم نے روگردانی کی تو پھر تمہیں معلوم ہونا چاہیے کہ ہمارے رسول کے ذمہ تو صرف واضح طور پر (ہمارا پیغام) پہنچا دینا ہے اور بس۔'),
(762, 5, 93, 'جو لوگ ایمان لائے اور نیک عمل کئے ان پر اس میں کوئی گناہ نہیں ہے کہ جو وہ (پہلے) کھا پی چکے ہوں۔ جبکہ (اب) پرہیز کریں اور ایمان لائیں اور نیک عمل کریں۔ اور پھر اس ایمان و پرہیز پر قائم و ثابت قدم بھی رہیں اور پھر تمام برے کاموں سے پرہیز کرتے رہیں اور نیکیاں کرتے رہیں اور اللہ نیک عمل کرنے والوں کو دوست رکھتا ہے۔'),
(763, 5, 94, 'اے ایمان والو! خدا تمہیں ضرور اس شکار کے ذریعہ سے آزمائے گا جسے تمہارے یہ ہاتھ اور نیزے پا جائیں (تمہاری زد میں ہو) تاکہ وہ (حسب ظاہر) یہ دیکھے کہ غائبانہ طور پر کون اس سے ڈرتا ہے؟ اور جس نے تنبیہ کے بعد بھی حد سے تجاوز کیا تو اس کے لئے دردناک عذاب ہے۔'),
(764, 5, 95, 'اے ایمان والو! شکار کو نہ مارو جبکہ تم احرام باندھے ہوئے ہو اور جو تم میں سے جان بوجھ کر اسے مارے گا تو اس کا جرمانہ مویشیوں سے کوئی جانور ہوگا۔ جو اس جانور کا ہم پلہ ہو جو اس نے مارا ہے جس کا فیصلہ تم میں سے دو عادل شخص کریں گے اور یہ جرمانہ بطور قربانی و نذرانہ خانہ کعبہ پہنچایا جائے گا۔ اس کا کفارہ چند مسکینوں کو کھانا کھلانا ہوگا یا پھر اس کے برابر روزے رکھے جائیں گے۔ تاکہ وہ اپنے کئے کی سزا کا مزہ چکھے۔ تو پہلے جو ہو چکا اسے تو اللہ نے معاف کر دیا۔ اور جو پھر ایسا کرے گا تو اس سے اللہ انتقام (بدلہ) لے گا۔ اور اللہ غالب ہے (زبردست ہے) اور بدلہ لینے والا ہے۔'),
(765, 5, 96, 'تمہارے لئے سمندری شکار (تری والا) اور اس کا کھانا اور قافلہ والوں (مسافروں) کے فائدہ کے لئے حلال قرار دیا گیا ہے اور جب تک تم حالت احرام میں ہو خشکی کا شکار تم پر حرام کیا گیا ہے اور اللہ کی نافرمانی سے ڈرتے رہو۔ جس کے پاس تم جمع کئے جاؤ گے۔'),
(766, 5, 97, 'خدا نے کعبہ کو جو حرمت والا گھر ہے۔ حرمت والے مہینوں کو اور قربانی کے جانوروں کو اور گلے میں پٹہ بندھے جانوروں کو لوگوں کی بقاء و فلاح کا سبب اور مرکز بنایا ہے تاکہ تمہیں معلوم ہو کہ اللہ وہ سب کچھ جانتا ہے جو آسمانوں میں ہے اور جو زمین میں ہے اور یہ کہ اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(767, 5, 98, 'جان لو کہ اللہ (مقامِ عقاب میں) سخت سزا دینے والا بھی ہے اور مقامِ عفو و صفح میں بڑا بخشنے والا اور بڑا رحم کرنے والا بھی ہے۔'),
(768, 5, 99, 'پیغمبر کی ذمہ داری صرف پیغام پہنچانا ہے اور بس۔ اور اللہ اسے بھی جانتا ہے جو تم ظاہر کرتے ہو اور اسے بھی جسے تم چھپاتے ہو۔'),
(769, 5, 100, 'کہہ دیجیے کہ خبیث (ناپاک) اور طیب (پاک) برابر نہیں ہو سکتے اگرچہ خبیث (ناپاک) کی کثرت اور بہتات تمہیں تعجب میں بھی ڈال دے (تمہیں بھلی لگے) اے عقل والو! خدا کی نافرمانی سے ڈرو تاکہ تم فلاح پاؤ۔'),
(770, 5, 101, 'اے ایمان والو! ایسی چیزوں کے بارے میں سوال نہ کرو۔ کہ جو اگر ظاہر کر دی جائیں تو تمہیں بری لگیں۔ اور اگر ان کے متعلق ایسے وقت میں سوال کروگے جبکہ قرآن اتر رہا ہے تو وہ تم پر ظاہر کر دی جائیں گی اللہ نے انہیں نظر انداز کر دیا ہے (یا اللہ نے تمہیں معاف کر دیا ہے) اور اللہ بڑا بخشنے والا، بڑا بردبار ہے۔'),
(771, 5, 102, 'تم سے پہلے بھی کچھ لوگوں نے اس قسم کے سوال (اپنے نبیوں سے) کئے تھے۔ پھر انہیں کی وجہ سے کافر (منکر) ہوگئے۔'),
(772, 5, 103, 'اللہ تعالیٰ نے کوئی بحیرہ، سائبہ، وصیلہ اور حام مقرر نہیں کیا مگر کافر اللہ پر جھوٹا بہتان باندھتے ہیں۔ اور ان میں سے اکثر عقل و شعور نہیں رکھتے۔'),
(773, 5, 104, 'اور جب ان سے کہا جاتا ہے کہ آؤ اس (قرآن) کی طرف جو اللہ نے اتارا ہے اور آؤ پیغمبر(ص) کی طرف تو وہ کہتے ہیں کہ ہمارے لئے تو وہی (طریقہ) کافی ہے جس پر ہم نے اپنے آباء و اجداد کو پایا ہے اگرچہ ان کے باپ دادا نہ علم رکھتے ہوں اور نہ ہدایت یافتہ ہوں (تو بھی وہ انہی کے طریقہ کو کافی جانیں گے؟)'),
(774, 5, 105, 'اے ایمان والو! تم پر لازم ہے کہ اپنی جانوں کی فکر کرو۔ جو گمراہ ہے وہ تمہارا کچھ بھی نہیں بگاڑ سکتا جب کہ تم ہدایت یافتہ ہو (راہِ راست پر ہو) تم سب کی بازگشت اللہ ہی کی طرف ہے پھر وہ تمہیں بتائے گا کہ تم دنیا میں کیا کرتے تھے؟ ۔'),
(775, 5, 106, 'اے ایمان والو! جب تم میں سے کسی کی موت کا وقت آجائے جبکہ وہ وصیت کر رہا ہو تو تمہارے درمیان گواہی کا ضابطہ یہ ہے کہ تم مسلمانوں میں سے دو عادل گواہ ہونے چاہئیں۔ ہاں البتہ اگر تم سفر میں ہو اور تم پر موت کی مصیبت آپڑے (اور مسلمان گواہ نہ مل سکیں) تو پھر دو گواہ غیروں میں سے ہی لے لئے جائیں۔ اور اگر تمہیں شک پڑ جائے تو ان دونوں کو نماز کے بعد روک رکھو اور وہ دونوں ان الفاظ کے ساتھ اللہ کی قَسم کھائیں کہ ہم اس قَسم کے عوض کوئی قیمت (فائدہ) حاصل نہیں کر رہے ہیں۔ اگرچہ ہمارا رشتہ دار ہی کیوں نہ ہو اور ہم اللہ واسطے کی گواہی نہیں چھپائیں گے ورنہ یقینا ہم گنہگاروں میں سے ہوں گے۔'),
(776, 5, 107, 'اور اگر بعد میں پتہ چلے کہ وہ (جھوٹی گواہی دے کر) گناہ کے مستوجب ہوئے ہیں تو پھر ان کی جگہ دو اور گواہ کھڑے ہوں (جو مرنے والے کے زیادہ) قریبی رشتہ دار ہوں جن کی حق تلفی ہوئی ہے اور اللہ کی قَسم کھائیں کہ ہماری گواہی ان (پہلے) دونوں کی گواہی سے زیادہ برحق ہے اور ہم نے کوئی زیادتی نہیں کی ورنہ ہم ظالموں میں سے ہوں گے۔'),
(777, 5, 108, 'یہ طریقہ کار زیادہ قریب ہے اس کے کہ گواہ ٹھیک طریقہ پر گواہی دیں گے یا کم از کم وہ اس بات کا خوف تو کریں گے کہ ان کی قَسموں کے بعد کہیں اور قَسموں سے ان کی تردید نہ ہو جائے (یا انہیں یہ اندیشہ تو ہوگا کہ ان کی قَسموں کے بعد دوسرے گواہوں سے بھی قَسمیں لی جا سکتی ہیں) اللہ (کی نافرمانی) سے ڈرو۔ اور سنو۔ اور اللہ نافرمان لوگوں کو منزل تک نہیں پہنچاتا۔'),
(778, 5, 109, '(اس وقت کو یاد کرو) جب اللہ تمام رسولوں کو جمع کرکے پوچھے گا کہ تمہیں (اپنی امتوں کی طرف سے) کیا جواب ملا تھا تو وہ عرض کریں گے کہ ہمیں تو کچھ علم نہیں ہے۔ تو ہی غیب کی تمام باتوں کا بڑا جاننے والا ہے۔'),
(779, 5, 110, '(اور وہ وقت یاد کرو) جب خدا فرمائے گا اے مریم کے فرزند عیسیٰ میرا وہ انعام یاد کرو۔ جو میں نے تم پر اور تمہاری والدہ پر کیا تھا۔ جب میں نے روح القدس سے تمہاری تائید کی (جس کی وجہ سے) تم گہوارے میں اور ادھیڑ عمر میں لوگوں سے (یکساں) باتیں کرتے تھے اور جب میں نے تمہیں کتاب (لکھنے) حکمت (دانائی)، توراۃ اور انجیل کی تعلیم دی تم میرے حکم سے مٹی سے پرندہ کی شکل (پتلا) بناتے تھے اور پھر اس میں پھونک مارتے تھے اور وہ میرے حکم سے (سچ مچ) پرندہ بن جاتا تھا۔ اور تم میرے حکم سے پیدائشی اندھے اور کوڑھی کو اچھا کر دیتے تھے اور تم میرے حکم سے مردوں کو (زندہ کرکے قبروں سے) نکالا کرتے تھے۔ اور جب میں نے بنی اسرائیل کو تم سے روکا تھا۔ جب تم ان کے پاس بینات و معجزات لائے تھے تو ان میں سے جو کافر (منکر حق) تھے انہوں نے کہا تھا کہ یہ نہیں ہے، مگر کھلا ہوا جادو۔'),
(780, 5, 111, 'اور وہ وقت قابل ذکر ہے جب میں نے حواریون کو الہام کیا کہ مجھ پر اور میرے رسول پر ایمان لاؤ۔ انہوں نے کہا کہ ہم ایمان لائے اور گواہ رہنا کہ ہم مسلمان (تیرے فرمانبردار بندے) ہیں۔'),
(781, 5, 112, '(حواریوں) کے بارے میں یہ قصہ بھی یاد رکھنے کے لائق ہے کہ انہوں نے کہا اے عیسیٰ بن مریم(ع) کیا آپ کا پروردگار اس بات پر قدرت رکھتا ہے کہ ہم پر آسمان سے کھانے کا ایک خوان اتارے عیسیٰ نے کہا اللہ (کی نافرمانی) سے ڈرو۔ اگر تم مؤمن ہو؟ ۔'),
(782, 5, 113, 'کہنے لگے ہم چاہتے ہیں کہ اس میں سے کھائیں اور ہمارے دل مطمئن ہو جائیں اور ہمیں یقین ہو جائے کہ آپ نے ہم سے سچ کہا تھا۔ اور ہم اس پر گواہی دینے والوں میں سے ہو جائیں۔'),
(783, 5, 114, 'عیسیٰ بن مریم(ع) نے (دعا کرتے ہوئے) کہا اے اللہ! اے ہمارے پروردگار! ہم پر آسمان سے ایک خوان نازل کر جو ہمارے لئے اور ہمارے اگلوں پچھلوں کے لئے عید قرار پائے اور تیری طرف سے قدرتی نشانی بن جائے۔ اور ہمیں روزی عطا فرما تو بہترین روزی عطا کرنے والا ہے۔'),
(784, 5, 115, 'اللہ تعالیٰ نے فرمایا بے شک میں وہ (خوان) تم پر اتارنے والا ہوں اب اس کے بعد جو کفر اختیار کرے گا تو میں اسے ایسی سزا دوں گا جیسی دنیا جہان میں کسی کو بھی نہیں دی ہوگی۔'),
(785, 5, 116, 'وہ وقت یاد کرو جب اللہ تعالیٰ فرمائے گا اے عیسیٰ بن مریم کیا تم نے لوگوں سے کہا تھا کہ اللہ کو چھوڑ کر مجھے اور میری ماں کو خدا بنا لو۔ عیسیٰ کہیں گے پاک ہے تیری ذات بھلا میں وہ بات کس طرح کہہ سکتا ہوں جس کا مجھے کوئی حق نہیں ہے اگر میں نے کوئی ایسی بات کہی ہوتی تو تجھے ضرور اس کا علم ہوتا۔ کیونکہ تو وہ سب کچھ جانتا ہے جو میرے دل میں ہے مگر میں تیرے اسرارِ نہانی کو نہیں جانتا۔ بلاشبہ تو غیب کی تمام باتوں کا بڑا جاننے والا ہے۔'),
(786, 5, 117, 'میں نے ان سے کچھ نہیں کہا تھا سوائے اس بات کے جس کا تو نے مجھے حکم دیا تھا کہ عبادت کرو اللہ کی جو میرا بھی پروردگار ہے اور تمہارا بھی پروردگار ہے اور میں اس وقت تک ان پر گواہ تھا جب تک ان میں تھا پھر جب تو نے مجھے (دنیا سے) اٹھا لیا تو پھر تو ہی ان کا نگران تھا اور تو ہی ہر چیز پر گواہ ہے۔'),
(787, 5, 118, 'اگر تو انہیں سزا دے تو یہ تیرے بندے ہیں اور اگر انہیں بخش دے تو تُو غالب ہے اور بڑا حکمت والا ہے۔'),
(788, 5, 119, 'ارشادِ قدرت ہوگا یہ وہ دن ہے کہ جس میں سچوں کی سچائی ان کو فائدہ پہنچائے گی۔ ان کے لئے ایسے بہشت ہوں گے جن کے نیچے سے نہریں جاری ہوں گی وہ ان میں ہمیشہ ہمیشہ رہیں گے۔ اللہ ان سے راضی اور وہ اللہ سے راضی (دونوں ایک دوسرے سے خوش) یہی سب سے بڑی کامیابی ہے۔'),
(789, 5, 120, 'اللہ ہی کے لئے ہے حکومت آسمانوں کی اور زمین کی اور جو کچھ ان میں ہے اس سب کی اور وہ ہر شی پر قادر ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سب تعریف اللہ کے لئے ہے جس نے آسمانوں اور زمین کو پیدا کیا اور تاریکیوں اور روشنی کو بنایا پھر بھی جو کافر ہیں وہ دوسروں کو اپنے پروردگار کے برابر ٹھہراتے ہیں۔'),
(791, 6, 2, 'وہ وہی ہے جس نے تم کو مٹی سے پیدا کیا۔ پھر (زندگی کی) ایک مدت مقرر کی اور ایک مقررہ مدت اور بھی ہے جو اسی کے پاس ہے پھر بھی تم شک کرتے ہو۔'),
(792, 6, 3, 'اور وہی (ایک) اللہ ہے آسمانوں میں بھی اور زمین میں بھی، جو تمہارے باطن و ظاہر اور جو کچھ تم بھلائی و برائی کرتے ہو سب کو جانتا ہے۔'),
(793, 6, 4, 'ان لوگوں کی حالت یہ ہے کہ ان کے پروردگار کی نشانیوں میں سے کوئی نشانی ان کے پاس نہیں آتی مگر یہ کہ وہ اس سے منہ پھیر لیتے ہیں۔'),
(794, 6, 5, 'چنانچہ انہوں نے اس حق (قرآن) کو بھی جھٹلایا جب وہ ان کے پاس آیا۔ سو عنقریب ان باتوں کی خبریں ان کے پاس آجائیں گی جن کا وہ مذاق اڑاتے رہے ہیں۔'),
(795, 6, 6, 'کیا انہوں نے نہیں دیکھا کہ ہم نے ان سے پہلے کتنے دوروں کے لوگوں کو ہلاک و برباد کر دیا جنہیں ہم نے زمین میں وہ اقتدار دیا تھا جو تمہیں بھی نہیں دیا ہے اور ہم نے ان پر آسمان سے موسلادھار بارش برسائی۔ اور ہم نے ان کے نیچے نہریں بہائیں (انجامِ کار) ہم نے ان کے گناہوں (اور کفرانِ نعمت) کی پاداش میں ہلاک کر دیا اور ان کے بعد دوسرے دور کی نسلیں پیدا کیں۔'),
(796, 6, 7, '(اے رسول(ص)) اگر ہم کاغذ میں لکھی لکھائی کتاب بھی آپ پر اتارتے جسے یہ اپنے ہاتھوں سے چھو بھی لیتے۔ جب بھی کافر یہی کہتے کہ یہ نہیں ہے مگر کھلا ہوا جادو۔'),
(797, 6, 8, 'اور وہ کہتے ہیں کہ اس (نبی(ص)) پر کوئی فرشتہ کیوں نہیں اتارا گیا؟ حالانکہ اگر ہم (کھلم کھلا) فرشتہ اتارتے تو پھر فیصلہ ہو چکا ہوتا پھر انہیں مہلت نہ دی جاتی۔'),
(798, 6, 9, 'اور اگر ہم کوئی فرشتہ اتارتے تو اسے بھی آدمی کی شکل میں اتارتے اور اس طرح گویا ہم انہیں وہ شبہ کرنے کا موقع دیتے جو اب وہ کر رہے ہیں۔'),
(799, 6, 10, 'آپ سے پہلے بھی بہت سے پیغمبروں کا مذاق اڑایا جاتا رہا ہے تو (آخرکار) ان کا مذاق اڑانے والوں کو اسی عذاب نے آکر گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(800, 6, 11, 'اے رسول(ص) ان سے کہیے کہ زمین میں چلو پھرو اور پھر دیکھو۔ کہ جھٹلانے والوں کا کیا (بھیانک) انجام ہوا۔'),
(801, 6, 12, '(اے رسول(ص)) ان سے کہیے (پوچھئے) کہ جو کچھ آسمانوں اور زمین میں ہے وہ کس کا ہے؟ کہو سب کچھ اللہ ہی کا ہے اس نے مخلوق پر رحمت کرنا اپنے اوپر لازم کر لی ہے (اس لئے جلدی سزا نہیں دیتا) وہ قیامت تک جس میں کوئی شک نہیں ہے تم سب کو اکٹھا کرتا رہے گا (پھر یکجا کرکے لائے گا) جنہوں نے اپنے آپ کو خسارے اور نقصان میں ڈال دیا ہے وہ ایمان نہیں لائیں گے۔'),
(802, 6, 13, 'اور اسی (اللہ تعالیٰ) کا ہے جو کچھ رات اور دن میں سکونت پذیر ہے وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(803, 6, 14, '(اے رسول(ص)) کہو کیا میں اللہ کو چھوڑ کر کسی اور کو اپنا سرپرست بناؤں؟ جو آسمانوں اور زمین کا بنانے والا ہے جو (ساری مخلوق کو) روزی دیتا ہے (کھلاتا ہے) مگر اس کو کوئی روزی نہیں دیتا (اسے کوئی نہیں کھلاتا)۔ کہو۔ مجھے حکم دیا گیا ہے کہ میں سب سے پہلا مسلمان (خدا کے سامنے سر تسلیم جھکانے والا) بن کر رہوں اور یہ حکم بھی دیا گیا ہے کہ شرک کرنے والوں میں سے نہ ہونا۔'),
(804, 6, 15, 'کہیے کہ اگر میں اپنے پروردگار کی نافرمانی کروں۔ تو میں ایک بہت بڑے دن کے عذاب سے ڈرتا ہوں۔'),
(805, 6, 16, 'اس دن جس شخص سے عذاب ٹال دیا گیا اس پر اس (خدا) نے بڑا رحم کیا ہے۔ اور یہ بڑی نمایاں کامیابی ہے۔'),
(806, 6, 17, 'اگر خدا تمہیں کوئی ضرر و زیاں پہنچانا چاہے تو اس کے پاس اس کا کوئی دور کرنے والا نہیں ہے۔ اور اگر کوئی بھلائی پہنچانا چاہے تو وہ ہر چیز پر قادر ہے۔'),
(807, 6, 18, 'وہ اپنے بندوں پر مکمل اختیار اور قابو رکھنے والا ہے اور وہ بڑا حکمت والا بڑا باخبر ہے۔'),
(808, 6, 19, '(اے رسول(ص)) کہو کہ گواہی میں سب سے بڑھ کر کون سی چیز ہے؟ کہہ دیجیے کہ اللہ! جو میرے اور تمہارے درمیان گواہ ہے۔ اور یہ قرآن بذریعہ وحی میری طرف بھیجا گیا ہے تاکہ میں اس کے ذریعہ سے تمہیں اور جس تک یہ پہنچے سب کو ڈراؤں۔ کیا تم اس بات کی گواہی دیتے ہو کہ اللہ کے ساتھ کوئی اور خدا بھی ہیں؟ کہیے کہ میں تو یہ گواہی نہیں دیتا۔ کہو وہ واحد و یکتا ہے۔ اور میں تمہارے شرک سے بری و بیزار ہوں۔'),
(809, 6, 20, 'جن لوگوں کو ہم نے کتاب دی ہے وہ ان (پیغمبر(ص)) کو اس طرح پہنچاتے ہیں جس طرح اپنے بیٹوں کو پہنچاتے ہیں۔ مگر جن لوگوں نے اپنے آپ کو خسارے و نقصان میں ڈال رکھا ہے وہ ایمان نہیں لائیں گے۔'),
(810, 6, 21, 'اور اس شخص سے بڑا ظالم کون ہے جو خدا پر جھوٹا بہتان باندھے یا اس کی آیتوں کو جھٹلائے۔ یقینا ظالم لوگ کبھی فلاح نہیں پائیں گے۔'),
(811, 6, 22, 'اور جس دن ہم سب کو (میدانِ حشر میں) اکٹھا کریں گے۔ اور جو لوگ (دنیا میں) شرک کرتے رہے ہیں ان سے کہیں گے کہ کہاں ہیں تمہارے وہ (معبود) جن کو تم خدا کا شریک گمان کرتے تھے۔'),
(812, 6, 23, 'اس وقت ان کے پاس اس کے سوا اور کوئی عذر نہ ہوگا کہ وہ کہیں گے ہمیں اپنے پروردگار اللہ کی قسم کہ ہم مشرک نہ تھے۔'),
(813, 6, 24, 'دیکھو وہ کس طرح اپنے ہی برخلاف جھوٹ بولنے لگے؟ اور ان کی وہ تمام افترا پردازیاں گم ہوگئیں جو وہ کیا کرتے تھے۔'),
(814, 6, 25, 'اور ان میں سے کچھ ایسے بھی ہیں جو آپ کی بات غور سے سنتے ہیں اور ہم نے ان کے دلوں پر پردے ڈال دیئے ہیں کہ وہ اسے سمجھتے نہیں ہیں اور کانوں میں گرانی ڈال دی ہے کہ سنتے نہیں ہیں۔ اور اگر وہ سارے کے سارے معجزے دیکھ لیں جب بھی ان پر ایمان نہیں لائیں گے۔ یہاں تک کہ جب آپ کے پاس آتے ہیں تو آپ سے بھی بحث و تکرار کرتے ہیں اور جو کافر ہیں (اور جنہوں نے بہرحال نہ ماننے کا فیصلہ کر لیا ہے) وہ سب کچھ سن کر بھی کہتے ہیں کہ یہ (قرآن) نہیں ہے مگر اگلے لوگوں کی (جھوٹی) داستانیں۔'),
(815, 6, 26, 'یہ ایسے (ناہنجار) ہیں کہ دوسروں کو بھی اس (پیغمبرِ اسلام یا قرآن) سے روکتے ہیں۔ اور خود بھی اس سے دور بھاگتے ہیں اور وہ (اپنی اس روش سے کسی اور کو نہیں) اپنے آپ کو تباہ کر رہے ہیں مگر انہیں اس کا شعور نہیں ہے۔'),
(816, 6, 27, 'اور کاش تم (ان کی وہ حالت) دیکھو جب وہ آتشِ دوزخ پر کھڑے کئے جائیں گے۔ اور وہ کہیں گے کہ کاش ہم واپس (دنیا میں) بھیج دیئے جائیں اور اپنے پروردگار کی نشانیوں کو نہ جھٹلائیں اور ایمان لانے والوں میں سے ہو جائیں۔'),
(817, 6, 28, '(یہ اسے لئے کہیں گے کہ) ان پر وہ بات واضح و عیاں ہوگئی ہے جسے وہ پہلے چھپایا کرتے تھے (وہ ایسے ناہنجار ہیں کہ) اگر انہیں واپس بھیج دیا جائے تو پھر بھی وہی کریں گے جس سے ان کو منع کیا گیا تھا اور یقینا وہ باکل جھوٹے ہیں۔'),
(818, 6, 29, 'اور وہ یہ بھی کہتے ہیں کہ زندگی تو بس یہی ہماری دنیا کی زندگی ہے اور ہم (قبروں سے) دوبارہ نہیں اٹھائے جائیں گے۔'),
(819, 6, 30, 'اور کاش تم وہ منظر دیکھو جب وہ اپنے پروردگار کے سامنے کھڑے کئے جائیں گے اور وہ ان سے پوچھے گا کیا یہ (قیامت) حق نہیں ہے؟ یہ کہیں گے ہاں حق ہے ہمیں اپنے رب کی قسم فرمائے گا تو اب اپنے کفر و انکار کی وجہ سے عذاب کا مزا چکھو۔'),
(820, 6, 31, 'بے شک ان لوگوں نے گھاٹا اٹھایا ہے جنہوں نے اللہ کی بارگاہ میں حاضری کو جھٹلایا۔ یہاں تک کہ جب اچانک قیامت آجائے گی تو وہ لوگ کہیں گے ہائے افسوس ہم سے اس کے بارے میں کیسی کوتاہی ہوئی؟ اور وہ اپنے (گناہوں کے) بوجھ اپنی پشتوں پر اٹھائے ہوں گے۔ کیا برا بوجھ ہے جو وہ اٹھائے ہوئے ہیں؟ ۔'),
(821, 6, 32, 'اور دنیا کی زندگی نہیں ہے مگر کھیل تماشا اور بے شک آخرت والا گھر پرہیزگاروں کے لئے بہت اچھا ہے کیا تم عقل سے کام نہیں لیتے۔'),
(822, 6, 33, 'ہمیں معلوم ہے کہ یہ لوگ جو باتیں کرتے ہیں وہ بے شک آپ کو رنج پہنچاتی ہیں۔ (لیکن امر واقع یہ ہے کہ) یہ لوگ آپ کو نہیں جھٹلاتے بلکہ یہ ظالم لوگ (دراصل) اللہ کی آیتوں کا انکار کر رہے ہیں۔'),
(823, 6, 34, 'بے شک آپ سے پہلے بھی بہت سے پیغمبروں کو جھٹلایا گیا۔ تو انہوں نے جھٹلائے جانے اور اذیت پہنچائے جانے پر صبر کیا یہاں تک کہ ہماری مدد ان کے پاس آئی اور اللہ کی باتوں کو کوئی بدلنے والا نہیں ہے اور پیغمبروں کی کچھ خبریں تو آپ کو پہنچ ہی چکی ہیں۔'),
(824, 6, 35, 'اور اگر حق سے ان کی روگردانی کرنا آپ پر بہت گراں ہے تو پھر اگر ہو سکتا ہے تو زمین میں کوئی سرنگ لگا کر یا آسمان میں کوئی سیڑھی لگا کر ان کے پاس کوئی معجزہ لاؤ۔ مگر وہ پھر بھی ایمان نہیں لائیں گے اور اگر اللہ (زبردستی) چاہتا تو ان سب کو ہدایت پر جمع کر دیتا۔ لہٰذا آپ ناواقف لوگوں میں سے نہ بنیں۔'),
(825, 6, 36, 'دعوتِ حق صرف وہی لوگ قبول کرتے ہیں جو سنتے ہیں اور جو مردے ہیں ان کو بس اللہ ہی (قیامت کے دن) اٹھائے گا پھر وہ اس کی طرف لوٹائے جائیں گے۔'),
(826, 6, 37, 'اور وہ کفار کہتے ہیں کہ ان پر ان کے پروردگار کی طرف سے کوئی معجزہ کیوں نازل نہیں ہوتا؟ کہہ دیجیے کہ بے شک اللہ تو کوئی بھی معجزہ نازل کرنے پر قادر ہے لیکن ان میں سے اکثر جانتے نہیں ہیں۔'),
(827, 6, 38, 'کوئی زمین میں چلنے والا جانور اور اپنے دونوں پروں سے اڑنے والا پرندہ نہیں ہے مگر تمہاری ہی طرح مختلف اقسام اور گروہ ہیں ہم نے کتاب میں کوئی کمی و کوتاہی نہیں کی ہے پھر وہ سب کے سب اپنے رب کے پاس محشور (جمع) کئے جائیں گے۔'),
(828, 6, 39, 'بہرے اور گونگے ہیں جوکہ طرح طرح کے اندھیروں میں پڑے ہیں۔ اللہ جسے چاہتا ہے اسے گمراہی میں چھوڑ دیتا ہے اور جسے چاہتا ہے سیدھے راستے پر لگا دیتا ہے۔'),
(829, 6, 40, 'اے رسول(ص) کہو کہ تم غور کرکے بتاؤ کہ اگر تمہارے پاس اللہ کا عذاب آجائے یا (اچانک) قیامت آجائے تو اللہ کے سوا کسی اور کو پکاروگے؟ بتاؤ اگر سچے ہو۔'),
(830, 6, 41, 'بلکہ اسی کو ہی پکاروگے اور اگر وہ چاہے گا تو (مصیبت) کو دور کر دے گا جس کے لئے تم نے اسے پکارا ہے اور تم انہیں بھول جاؤگے جن کو اس کا شریک ٹھہراتے ہو۔'),
(831, 6, 42, 'بلکہ اے رسول(ص) بے شک ہم نے آپ سے پہلے بہت سی امتوں کی طرف پیغمبر بھیجے اور ان کی نافرمانی پر پہلے تو ہم نے ان کو تنگدستی اور تکلیف میں مبتلا کیا۔ تاکہ تضرع و زاری کریں۔'),
(832, 6, 43, 'جب ان کے پاس ہماری طرف سے سختی آئی تو آخر انہوں نے تضرع و زاری کیوں نہ کی؟ لیکن ان کے دل تو اور بھی سخت ہوگئے اور شیطان نے (ان کی نظروں میں) ان کاموں کو جو وہ کرتے تھے آراستہ کرکے پیش کیا۔'),
(833, 6, 44, 'اور جب انہوں نے اس نصیحت کو بھلا دیا جو انہیں کی گئی تھی تو ہم نے (ابتلا و امتحان کے طور پر) ان کے لئے ہر چیز کے دروازے کھول دیئے۔ یہاں تک کہ وہ عطا کردہ چیزوں (نعمتوں) سے خوب خوش ہوگئے اور اترانے لگے تو ہم نے اچانک انہیں پکڑ لیا۔ تو وہ ایک دم مایوس ہوگئے۔'),
(834, 6, 45, 'پس ظالموں کی جڑ کاٹ دی گئی (ان کی نسل قطع ہوگئی) اور سب تعریف اللہ کے لئے ہے جو سارے جہانوں کا پروردگار ہے۔'),
(835, 6, 46, 'اے رسول کہیے غور کرکے بتاؤ کہ اگر اللہ تعالیٰ تمہارے کان اور تمہاری آنکھیں لے لے (یعنی قوت شنوائی اور بینائی چھین لے) اور تمہارے دلوں پر مہر لگا دے تو اللہ کے سوا اور کون خدا ہے جو تمہیں یہ چیزیں لا دے؟ دیکھئے ہم کس طرح الٹ پلٹ کر اپنی توحید کی نشانیاں واضح کرتے ہیں پھر بھی وہ روگردانی کرتے ہیں۔'),
(836, 6, 47, 'کہیے ذرا غور کرکے بتاؤ کہ اگر تم پر اللہ کا عذاب اچانک یا علانیہ آجائے تو ظالم لوگوں کے سوا اور کون ہلاک و برباد ہوگا؟'),
(837, 6, 48, 'اور ہم پیغمبروں کو نہیں بھیجتے مگر نیکوکاروں کو خوشخبری دینے والا اور بدکاروں کو ڈرانے والا بنا کر جو ایمان لے آئیں اور اپنی اصلاح کر لیں نیک عمل کریں۔ تو نہ ان کے لئے کوئی خوف ہے اور نہ ہی وہ غمگین ہوں گے۔'),
(838, 6, 49, 'اور جن لوگوں نے ہماری آیتوں کو جھٹلایا ان کو ان کی نافرمانی کی وجہ سے عذاب پہنچے گا۔'),
(839, 6, 50, 'اے رسول کہہ دیجیے میں تم سے یہ نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں اور نہ یہ کہ میں غیب جانتا ہوں اور نہ تم سے یہ کہتا ہوں کہ میں فرشتہ ہوں میں تو صرف اس وحی کی پیروی کرتا ہوں جو مجھے کی جاتی ہے آپ کہیے! کیا اندھا اور آنکھوں والا برابر ہے؟ تم غور و فکر کیوں نہیں کرتے؟'),
(840, 6, 51, 'اور آپ اس (وحی شدہ قرآن) کے ذریعہ سے ان لوگوں کو ڈرائیں۔ جنہیں خوف (اندیشہ) ہے کہ وہ اپنے پروردگار کی طرف محشور ہوں گے کہ اس (اللہ) کے سوا ان کا نہ کوئی سرپرست ہوگا اور نہ کوئی سفارشی شاید کہ وہ متقی و پرہیزگار بن جائیں۔'),
(841, 6, 52, '(اے رسول(ص)) ان لوگوں کو اپنے پاس سے نہ دھتکارو۔ جو اپنے پروردگار کی رضا جوئی کی خاطر صبح و شام اسے پکارتے ہیں۔ ان کا کچھ بھی حساب کتاب آپ کے ذمہ نہیں ہے اور نہ ہی آپ کا کچھ بھی حساب کتاب ان کے ذمہ ہے اور اگر آپ نے پھر بھی انہیں دھتکار دیا تو آپ بے انصافی کرنے والوں میں سے ہو جائیں گے۔'),
(842, 6, 53, 'اس طرح ہم نے بعض لوگوں کو بعض کے ذریعہ سے آزمائش میں ڈال رکھا ہے تاکہ وہ کہیں کہ کیا یہی وہ ہیں جن کو ہم میں سے اللہ نے اپنے (خاص) احسان سے نوازا ہے؟ کیا اللہ اپنے شکر گزار بندوں کو (ان سے) زیادہ نہیں جانتا ہے؟'),
(843, 6, 54, 'اور جب آپ کے پاس وہ لوگ آئیں جو ہماری آیتوں پر ایمان لائے ہیں تو ان سے کہو سلامٌ علیکم (تم پر سلامتی ہو)۔ تمہارے پروردگار نے اپنے اوپر رحمت لازم قرار دی ہے (لہٰذا) اگر تم میں سے کوئی شخص جہالت و نادانی کی وجہ سے کوئی برائی کرے پھر اس کے بعد توبہ کرلے اور اپنی اصلاح کرلے تو بےشک وہ (اللہ) بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(844, 6, 55, 'اور اسی طرح ہم اپنی آیتوں کو تفصیل کے ساتھ بیان کرتے ہیں تاکہ مجرموں کا راستہ بالکل واضح ہو جائے۔'),
(845, 6, 56, 'اے رسول(ص) کہہ دیجیے! کہ مجھے منع کیا گیا ہے کہ میں ان (معبودانِ باطل) کی عبادت کروں جن کو تم اللہ کے سوا خدا کہہ کر پکارتے ہو میں تمہاری خواہشات کی پیروی نہیں کروں گا۔ اس صورت میں تو میں گمراہ ہو جاؤں گا۔ اور ہدایت یافتہ لوگوں سے نہ رہوں گا۔'),
(846, 6, 57, 'کہہ دیجیے! کہ میں اپنے پروردگار کی طرف سے روشن دلیل پر قائم ہوں۔ اور تم نے اسے جھٹلا دیا ہے جس (عذاب) کی تم جلدی کرتے ہو وہ میرے پاس نہیں ہے فیصلہ کرنے کا اختیار اللہ کے پاس ہے وہ حق بیان کرتا ہے اور وہ بہترین فیصلہ کرنے والا ہے۔'),
(847, 6, 58, 'کہہ دیجیے! کہ جس عذاب کے لئے تم جلدی کرتے ہو اگر وہ میرے پاس ہوتا تو میرے اور تمہارے درمیان کب کا فیصلہ ہو چکا ہوتا۔ اور اللہ ظالموں کو زیادہ بہتر جانتا ہے۔'),
(848, 6, 59, 'اور اس (اللہ) کے پاس ہیں غیب کی کنجیاں، جنہیں اس کے سوا اور کوئی نہیں جانتا اور وہ اسے بھی جانتا ہے جو خشکی میں ہے۔ اور اسے بھی جانتا ہے جو تری میں ہے اور کوئی پتا نہیں گرتا مگر وہ اسے جانتا ہے۔ اور زمین کی تاریکیوں میں کوئی دانہ نہیں ہے اور نہ کوئی تر ہے اور نہ کوئی خشک ہے مگر یہ کہ وہ کتابِ مبین میں موجود ہے۔'),
(849, 6, 60, 'وہی (اللہ) ہے جو رات کے وقت تمہاری روحیں قبض کر لیتا ہے۔ اور تم دن میں جو کچھ کرتے ہو اسے جانتا ہے پھر تمہیں اس (دن) میں اٹھاتا ہے۔ تاکہ (زندگی کی) مقررہ مدت پوری ہو پھر تمہاری بازگشت اسی کی طرف ہے پھر وہ تمہیں بتائے گا جو کچھ (بھلا یا برا) تم کرتے تھے۔'),
(850, 6, 61, 'اور وہ اپنے بندوں پر غالب ہے (اور پوری قدرت رکھتا ہے) اور وہ تم پر حفاظت (نگرانی) کرنے والے (فرشتے) بھیجتا ہے۔ یہاں تک کہ جب تم میں سے کسی کی موت کا وقت آتا ہے تو ہمارے بھیجے ہوئے (فرشتے) اس کی روح قبض کر لیتے ہیں اور وہ کوتاہی نہیں کرتے۔'),
(851, 6, 62, 'پھر وہ اللہ کے پاس جو ان کا حقیقی مالک ہے لوٹائے جاتے ہیں۔ خبردار فیصلہ کرنے کا اختیار اسی کو حاصل ہے اور وہ سب سے زیادہ جلدی حساب لینے والا ہے۔'),
(852, 6, 63, '(اے رسول(ص)) کہیے کہ تمہیں صحرا و سمندر کے اندھیروں سے (مصیبتوں سے) کون نجات دیتا ہے؟ جب تم اسے گڑگڑا کر اور چپکے چپکے پکارتے ہو۔ (اور دل میں کہتے ہو) کہ اگر وہ ہمیں ان (خطرات) سے نجات دے دے تو ہم شکر گزاروں میں سے ہوں گے۔'),
(853, 6, 64, '(اے رسول(ص)) کہیئے اللہ تمہیں ان سے اور ہر رنج و غم سے نجات دیتا ہے پھر بھی تم شرک کرتے ہو۔'),
(854, 6, 65, 'کہہ دو وہ (اللہ) اس پر قادر ہے کہ تم پر کوئی عذاب نازل کرے تمہارے اوپر سے یا تمہارے پاؤں کے نیچے سے (بھیج دے)۔ یا تمہیں مختلف گروہوں میں تقسیم کرکے اور باہم خلط ملط کرکے ٹکرا دے اور تم میں سے بعض کو بعض کی طاقت و سختی کا مزا چکھا دے۔ دیکھیے ہم کس طرح الٹ پھیر کرکے اپنی آیات (دلیلوں) اور نشانیوں کو بیان کرتے ہیں تاکہ وہ سمجھ جائیں۔'),
(855, 6, 66, 'اور آپ کی قوم نے اس (قرآن) کو جھٹلایا حالانکہ وہ حق ہے کہہ دیجیے کہ میں تمہارا وکیل (نگہبان) نہیں ہوں۔'),
(856, 6, 67, 'اور ہر خبر (کے وقوع) کا ایک وقت مقرر ہے۔ اور عنقریب تمہیں معلوم ہو جائے گا۔'),
(857, 6, 68, 'اور جب دیکھو کہ لوگ ہماری آیتوں کے بارے میں نکتہ چینی اور بے ہودہ بحث کر رہے ہیں تو تم ان سے کنارہ کش ہو جاؤ۔ یہاں تک کہ وہ کسی اور بات میں مشغول ہو جائیں اور (اے مخاطب) اگر کبھی شیطان تجھے بھلا دے تو یاد آنے کے بعد ظالم لوگوں کے پاس مت بیٹھ۔'),
(858, 6, 69, 'ان (غلط کار لوگوں) کے حساب و کتاب کی ذمہ داری پرہیزگار لوگوں پر نہیں ہے (جو ان برے کاموں سے بچتے رہتے ہیں) ہاں البتہ (ان کے ذمہ صرف) نصیحت کرنا ہے تاکہ وہ (برے لوگ) پرہیزگاری اختیار کریں۔'),
(859, 6, 70, 'چھوڑو ان لوگوں کو جنہوں نے اپنے دین و مذہب کو کھیل اور تماشا بنا رکھا ہے اور انہیں دنیا کی زندگی نے دھوکہ و فریب میں مبتلا کر رکھا ہے۔ اور ان کو اس (قرآن) کے ذریعہ سے نصیحت کرو۔ ایسا نہ ہو کہ کوئی شخص اپنے کئے کے وبال میں پھنس جائے حالانکہ اللہ کے سوا اس کا نہ کوئی سرپرست ہوگا اور نہ کوئی سفارشی۔ اور اگر وہ اپنے چھٹکارے کے لیے معاوضہ دینا چاہے گا تو وہ قبول نہیں کیا جائے گا یہ وہ لوگ ہیں جو اپنے کرتوتوں کے وبال میں پھنسے ہوئے ہیں ان کے کفر کی وجہ سے ان کے پینے کے لیے کھولتا ہوا گرم پانی ہوگا اور دردناک عذاب۔'),
(860, 6, 71, 'کہیے! کیا ہم اللہ کو چھوڑ کر ان کو پکاریں جو نہ ہمیں نفع پہنچا سکتے ہیں اور نہ نقصان اور بعد اس کے کہ خدا ہم کو سیدھا راستہ دکھا چکا ہے ہم الٹے پاؤں پھر جائیں اس شخص کی طرح جسے شیطانوں (بھوتوں پریوں) نے (کہیں) زمین میں بے راہ اور سرگردان کر دیا جبکہ اس کے کچھ ساتھی اسے سیدھے راستے کی طرف بلا رہے ہوں کہ ہماری طرف آؤ مگر اسے کچھ سجھائی نہیں دیتا کہو ہدایت و راہنمائی تو بس اللہ کی ہدایت و راہنمائی ہے۔ اور ہمیں حکم دیا گیا ہے کہ ہم تمام جہانوں کے پروردگار کے سامنے سر تسلیم خم کر دیں۔'),
(861, 6, 72, 'اور (ہم سے کہا گیا ہے کہ) نماز ادا کرو۔ اور اس سے (اس کی نافرمانی سے) ڈرو اور وہی وہ ہے جس کی بارگاہ میں تم محشور ہوگے۔'),
(862, 6, 73, 'وہی تو وہ (خدا) ہے جس نے آسمانوں اور زمین کو برحق پیدا کیا۔ جس دن وہ کہے گا کہ ہوجا بس وہ ہو جائے گا۔ اس کا قول حق اور سچ ہے اسی کی حکومت ہوگی جس دن صور پھونکا جائے گا وہ غیب اور حاضر سب کا جاننے والا ہے اور وہ بڑا حکمت والا، بڑا باخبر ہے۔'),
(863, 6, 74, '(وہ وقت یاد کرو) جب ابراہیم (ع) نے اپنے باپ (یعنی چچا) آزر سے کہا۔ کیا تم بتوں کو خدا بناتے ہو؟ میں تمہیں اور تمہاری قوم کو کھلی ہوئی گمراہی میں دیکھ رہا ہوں۔'),
(864, 6, 75, 'اور اسی طرح ہم ابراہیم(ع) کو آسمانوں اور زمین کی سلطنت دکھا رہے تھے تاکہ وہ یقین کرنے والوں میں سے ہو جائیں۔'),
(865, 6, 76, 'چنانچہ جب ان (ابراہیم) پر رات کی تاریکی چھا گئی تو انہوں نے ایک ستارہ کو دیکھا تو کہا یہ میرا رب ہے؟ تو جب وہ ڈوب گیا تو کہا۔ میں ڈوب جانے والوں سے محبت نہیں کرتا۔'),
(866, 6, 77, 'اور جب چاند کو چمکتے ہوئے دیکھا تو کہا یہ میرا پروردگار ہے؟ لیکن جب وہ بھی غروب ہوگیا۔ تو کہا کہ اگر میرا پروردگار میری ہدایت و راہنمائی نہ کرتا رہا تو میں گمراہ لوگوں میں سے ہو جاؤں گا۔'),
(867, 6, 78, 'پھر جب سورج کو چمکتے دیکھا تو کہا یہ میرا پروردگار ہے؟ یہ سب سے بڑا ہے۔ مگر جب وہ بھی غروب ہوگیا تو کہا اے میری قوم! میں اس شرک سے بری و بیزار ہوں جو تم کرتے ہو۔'),
(868, 6, 79, 'میں ہر طرف سے ہٹ کر اپنا رخ اس کی طرف کرتا ہوں جس نے آسمانوں اور زمین کو پیدا کیا ہے اور میں مشرکوں میں سے نہیں ہوں۔'),
(869, 6, 80, 'اور ان کی قوم ان سے بحث کرنے لگی۔ فرمایا تم مجھ سے اللہ کے بارے میں بحث کرتے ہو؟ حالانکہ وہ مجھے ہدایت دے چکا ہے اور میں ان سے نہیں ڈرتا جنہیں تم خدا کا شریک ٹھہراتے ہو۔ مگر یہ کہ میرا پروردگار کوئی بات چاہے میرا پروردگار علم کے اعتبار سے ہر چیز پر حاوی اور محیط ہے کیا تم نصیحت قبول نہیں کرتے؟'),
(870, 6, 81, 'اور میں بھلا ان سے کس طرح ڈروں جن کو تم خدا کا شریک ٹھہراتے ہو۔ جب کہ تم اس سے نہیں ڈرتے کہ تم نے ایسی چیزوں کو خدا کا شریک بنا رکھا ہے۔ جن کے متعلق اس نے تمہارے پاس کوئی دلیل نہیں بھیجی ہے۔ سو دونوں گروہوں میں سے کون امن و اطمینان کا زیادہ حقدار ہے (بتاؤ) اگر تم کچھ علم رکھتے ہو؟'),
(871, 6, 82, 'جو لوگ ایمان لائے اور اپنے ایمان کو ظلم سے آلودہ نہیں کیا انہی کے لئے امن و امان ہے اور وہی ہدایت یافتہ ہیں۔'),
(872, 6, 83, 'یہ تھی ہماری وہ دلیل جو ہم نے ابراہیم (ع) کو ان کی قوم کے مقابلہ میں عطا کی تھی۔ ہم جس کے چاہتے ہیں درجے بلند کرتے ہیں آپ کا پروردگار بڑا حکمت والا، بڑا جاننے والا ہے۔'),
(873, 6, 84, 'اور ہم نے انہیں اسحاق جیسا (بیٹا) اور یعقوب (جیسا پوتا) عطا فرمایا۔ ہر ایک کو ہم نے ہدایت دی اور ان سے پہلے نوح کو ہدایت دی۔ اور ان (نوح یا ابراہیم) کی اولاد میں سے داؤد، سلیمان، ایوب، یوسف، موسیٰ، ہارون کو (بھی ہدایت دی) اور اسی طرح ہم نیکوکاروں کو جزا دیا کرتے ہیں۔'),
(874, 6, 85, 'اور زکریا، یحییٰ، عیسیٰ اور الیاس کو (بھی ہم نے ہدایت دی) جو سب صالحین میں سے تھے۔'),
(875, 6, 86, 'اور اسماعیل، یسع، یونس اور لوط کو (بھی ہدایت دی) اور ان سب کو ہم نے تمام جہانوں پر فضیلت دی۔'),
(876, 6, 87, 'اور ان کے آباء و اجداد اور اولاد اور بھائیوں میں سے بھی (کچھ کو ہدایت دی) اور ہم نے انہیں منتخب کیا اور سیدھے راستے کی ہدایت دی۔'),
(877, 6, 88, 'یہ اللہ کی خاص ہدایت ہے جس کے ذریعے سے وہ بندوں میں سے جس کی چاہتا ہے راہنمائی کرتا ہے اور اگر انہوں نے شرک کیا ہوتا تو ان کے وہ سب عمل برباد ہو جاتے جو وہ کیا کرتے تھے۔'),
(878, 6, 89, 'یہ وہ لوگ ہیں جن کو ہم نے کتاب کا حکم اور نبوت عطا کی اب اگر یہ (مکہ والے) لوگ ان چیزوں کا انکار کرتے ہیں تو کوئی پروا نہیں ہے ہم نے ان چیزوں کا ذمہ دار ان لوگوں کو بنایا ہے جو انکار کرنے والے نہیں ہیں۔'),
(879, 6, 90, 'یہ وہ لوگ ہیں جن کو اللہ نے ہدایت دی ہے سو آپ بھی ان کے راستہ اور طریقہ پر چلیں۔ کہہ دیجئے کہ میں اس پر تم سے کوئی معاوضہ نہیں مانگتا وہ (قرآن) تو تمام عالمین کے لئے نصیحت ہے۔'),
(880, 6, 91, 'ان لوگوں نے اللہ کی اس طرح قدر نہیں کی جس طرح قدر کرنے کا حق تھا۔ جب انہوں نے یہ کہا کہ اللہ نے کسی بشر پر کوئی چیز نازل نہیں کی ان سے کہیے کہ وہ کتاب کس نے نازل کی تھی جسے موسیٰ لے کر آئے تھے؟ جو لوگوں کے لئے روشنی اور ہدایت (کا ذریعہ) تھی؟ جسے تم نے ورق ورق کر رکھا ہے۔ اس کا کچھ حصہ تو تم ظاہر کرتے ہو (مگر) بہت سا حصہ چھپاتے ہو۔ حالانکہ تمہیں وہ باتیں سکھائی گئیں ہیں جو نہ تمہیں معلوم تھیں اور نہ تمہارے باپ دادا کو کہیے اللہ نے (وہ کتاب نازل کی تھی)۔ پھر ان کو چھوڑ دیجیے کہ وہ اپنی بے ہودہ نکتہ چینی میں کھیلتے رہیں۔'),
(881, 6, 92, 'اور یہ وہ بابرکت کتاب ہے جسے ہم نے نازل کیا ہے جو اپنے سے پہلے والی کتابوں کی تصدیق کرنے والی ہے (اس لئے نازل کی ہے) کہ تم مکہ اور اس کے اطراف و جوانب والے لوگوں کو ڈراؤ۔ اور جو لوگ قیامت پر ایمان رکھتے ہیں (اسے برحق) مانتے ہیں وہ اس (قرآن) پر بھی ایمان لاتے ہیں اور وہ اپنی نمازوں پر پابندی کرتے ہیں۔'),
(882, 6, 93, 'اور اس شخص سے بڑا ظالم اور کون ہوگا جو خدا پر جھوٹا بہتان باندھے یا یہ کہے کہ مجھ پر وحی آتی ہے حالانکہ اس پر کوئی وحی نہ کی گئی ہو اور (اس سے بڑا ظالم کون ہے) جو کہے کہ جیسا کلام اللہ نے نازل کیا ہے ایسا میں بھی نازل کروں گا۔ (یعنی نازل کر سکتا ہوں) اے کاش! تم وہ منظر دیکھو جب کہ ظالم لوگ موت کے سکرات (سختیوں) میں ہوں گے اور موت والے فرشتے اپنے ہاتھ پھیلائے ہوئے ہوں گے (اور کہیں گے) اپنی روحوں کو ان (سکرات) سے نکالو۔ آج تمہیں ذلت آمیز عذاب کیا جائے گا تمہارے خدا کے بارے میں غلط باتیں کرنے اور اس کی آیتوں کے مقابلہ میں تکبر کرنے کی پاداش میں۔'),
(883, 6, 94, 'اور تم اسی طرح ہماری بارگاہ میں تن تنہا آئے ہو جس طرح ہم نے پہلی بار تمہیں پیدا کیا تھا۔ اور اپنے پیچھے چھوڑ آئے جو کچھ ساز و سامان ہم نے تمہیں عطا کیا تھا۔ اور (آج) ہم تمہارے ساتھ وہ سفارشی نہیں دیکھتے جن کے متعلق تم گمان کرتے تھے کہ وہ تمہارے معاملہ میں ہمارے شریک ہیں؟ (آج) تمہارے باہمی تعلقات منقطع ہوگئے اور جو تمہارا گمان فاسد تھا وہ غائب ہوگیا۔'),
(884, 6, 95, 'بے شک اللہ ہی ہے جو دانہ اور گھٹلی کو شگافتہ کرنے والا ہے جو زندہ کو مردہ سے اور مردہ کو زندہ سے نکالتا ہے یہ ہے اللہ تم کدھر بہکے جاتے ہو؟'),
(885, 6, 96, 'وہ (رات کے اندھیرے سے) صبح کا برآمد کرنے والا ہے۔ اور اسی نے رات کو سکون و راحت کے لئے بنایا اور سورج و چاند کو (ماہ و سال کے) حساب کے لئے بنایا۔ یہ غالب اور بڑے علم والے (خدا) کا مقرر کردہ نظام ہے۔'),
(886, 6, 97, 'اور وہ وہی ہے جس نے تمہارے لئے ستارے بنائے تاکہ تم ان کے ذریعہ سے خشکی اور تری کے اندھیروں میں راہنمائی حاصل کرو۔ بے شک ہم نے علم رکھنے والوں کے لئے دلائل کھول کر بیان کر دیے ہیں۔'),
(887, 6, 98, 'اور وہ (اللہ) وہی تو ہے جس نے تم (سب) کو ایک شخص (آدم(ع)) سے پیدا کیا ہے پھر ہر ایک کے لیے ایک قرارگاہ ہے (باپ کی پشت) اور ایک سونپے جانے کا مقام ہے (رحم مادر) اور ہم نے سمجھ بوجھ رکھنے والوں کے لئے (اپنی قدرت کی) نشانیاں تفصیل سے بیان کر دی ہیں۔'),
(888, 6, 99, 'وہ وہی تو ہے جس نے آسمان کی (بلندی) سے پانی نازل کیا پھر ہم نے اس کے ذریعہ سے ہر قسم کے نباتات اگائے پھر اس سے ہری بھری شاخیں نکالیں کہ ہم اس سے تہہ بہ تہہ (گھتے ہوئے) دانے نکالتے ہیں اور کھجور کے درختوں سے یعنی ان کے شگوفوں سے گچھے نکالتے ہیں جو زمین کی طرف جھکے ہوئے ہیں اور ہم نے انگور، زیتون اور انار کے باغات پیدا کئے جو باہم مشابہ بھی ہیں اور غیر مشابہ بھی اور اس کے پھل کو دیکھو جب وہ پھلتا ہے اور اس کے (پکنے کی کیفیت) کو دیکھو اور بے شک اس میں ایمان لانے والوں کے لئے (اللہ کی توحید و قدرت کی) نشانیاں ہیں۔'),
(889, 6, 100, 'اور ان لوگوں نے جنات کو اللہ کا شریک بنایا ہے حالانکہ اس نے انہیں پیدا کیا ہے اور انہوں نے اس کے لئے جہالت کی وجہ سے بیٹے اور بیٹیاں تراش کر رکھی ہیں۔ وہ پاک ہے اور برتر ہے اس سے جو وہ اس کے بارے میں بیان کرتے ہیں۔'),
(890, 6, 101, 'وہ آسمانوں اور زمین کا موجد ہے اس کے لئے اولاد کیونکر ہو سکتی ہے حالانکہ اس کی کوئی بیوی ہی نہیں ہے۔ اور اسی نے ہر چیز کو پیدا کیا ہے اور وہ ہر چیز کا خوب جاننے والا ہے۔'),
(891, 6, 102, 'یہ ہے اللہ جو تمہارا پروردگار ہے اس کے سوا کوئی خدا نہیں ہے وہی ہر چیز کا خالق ہے لہٰذا اسی کی عبادت کرو۔ اور وہ ہر چیز کا وکیل و کفیل ہے۔'),
(892, 6, 103, 'نگاہیں اسے نہیں پا سکتیں اور وہ نگاہوں کو پا لیتا ہے اور وہ لطیف (جسم و جسمانیات سے مبرا ہے) اور خبیر (بڑا باخبر) ہے۔'),
(893, 6, 104, 'تمہارے پاس تمہارے پروردگار کی طرف سے بصیرتیں (روشن دلیلیں) آچکیں اب جو بصیرت سے کام لے گا وہ اپنا فائدہ کرے گا۔ اور جو اندھا بنا رہے گا وہ اپنا نقصان کرے گا۔ اور میں تمہارے اوپر نگران نہیں ہوں۔'),
(894, 6, 105, 'اور ہم اپنی آیتیں یونہی الٹ پھیر کر بیان کرتے ہیں اور اس لئے کرتے ہیں کہ یہ لوگ کہیں کہ آپ نے (کسی سے) پڑھا ہے اور تاکہ ہم اسے علم رکھنے والوں کے لئے واضح کر دیں۔'),
(895, 6, 106, 'آپ اس وحی کی پیروی کریں جو آپ کے پروردگار کی طرف سے آپ کو کی جاتی ہے اس کے سوا کوئی خدا نہیں ہے۔ اور مشرکوں سے بےتوجہی اختیار کریں۔'),
(896, 6, 107, 'اور اگر خدا (زبردستی) چاہتا تو وہ شرک نہ کرتے اور ہم نے آپ کو ان پر نگہبان مقرر نہیں کیا اور نہ ہی آپ ان کے ذمہ دار ہیں۔'),
(897, 6, 108, 'اور (خبردار) تم ان کو گالیاں نہ دو جن کو یہ اللہ کے سوا پکارتے ہیں ورنہ یہ لوگ اپنی جہالت و ناسمجھی کی بنا پر حد سے گزر کر اللہ کو گالیاں دیں گے۔ اسی طرح ہم نے ہر گروہ کے عمل کو (اس کی نظروں میں) آراستہ کیا ہے۔ پھر ان کی بازگشت ان کے پروردگار کی طرف ہے پھر وہ انہیں بتائے گا جو کچھ وہ کیا کرتے تھے۔'),
(898, 6, 109, 'اور انہوں نے اللہ کے نام کی بڑی سخت قسمیں کھائی ہیں کہ اگر ان کی مرضی کے مطابق کوئی معجزہ ان کے پاس آجائے تو وہ ضرور ایمان لائیں گے۔ کہہ دیجیے کہ معجزہ تو بس اللہ ہی کے پاس ہے جب کوئی ایسا معجزہ آبھی جائے گا تو یہ جب بھی ایمان نہیں لائیں گے۔'),
(899, 6, 110, '(تمہیں کیا خبر کہ) ہم ان کے دلوں اور آنکھوں کو پھیر دیں گے (تہہ و بالا کر دیں گے) لہٰذا جس طرح وہ اس قرآن پر پہلی مرتبہ ایمان نہیں لائے تھے اب بھی نہیں لائیں گے اور ہم انہیں چھوڑ دیں گے تاکہ یہ اپنی سرکشی میں اندھے بنے پھرتے رہیں۔'),
(900, 6, 111, 'اور اگر ہم ان کی طرف فرشتے بھی نازل کر دیں اور ان سے مردے بھی کلام کریں اور خواہ ہم ہر چیز کو ان کے سامنے لاکھڑا کر دیں تب بھی یہ ایمان لانے والے نہیں ہیں مگر یہ کہ اللہ (اپنی قدرتِ قاہرہ سے) چاہے۔ لیکن اکثر لوگ جاہل ہیں۔'),
(901, 6, 112, 'اور اسی طرح ہم نے ہر نبی کے لیے انسانوں اور جنوں میں سے شیطانوں کو دشمن قرار دیا ہے جو ایک دوسرے کو دھوکہ و فریب دینے کے لیے بناوٹی باتوں کی سرگوشی کرتے ہیں اور اگر آپ کا پروردگار (زبردستی) چاہتا، تو یہ ایسا نہ کرتے پس آپ انہیں اور جو کچھ وہ افترا کر رہے ہیں اسے چھوڑ دیں۔'),
(902, 6, 113, 'تاکہ ان (بناوٹی باتوں) کی طرف ان لوگوں کے دل مائل ہوں جو آخرت پر ایمان نہیں رکھتے اور تاکہ وہ اسے پسند کریں اور تاکہ وہ ان (برائیوں) کا ارتکاب کریں جن کے یہ مرتکب ہو رہے ہیں۔'),
(903, 6, 114, 'کیا میں اللہ کے سوا کسی اور کو فیصل اور منصف تلاش کروں؟ حالانکہ وہ وہی ہے جس نے تمہاری طرف مفصل اور واضح کتاب نازل کی ہے۔ اور ہم نے جن کو (آسمانی) کتاب دی وہ (اہل کتاب) جانتے ہیں کہ یہ (قرآن) آپ کے پروردگار کی طرف سے حق کے ساتھ نازل ہوا ہے پس آپ ہرگز شک کرنے والوں میں سے نہ ہوں۔'),
(904, 6, 115, 'اور آپ کے پروردگار کی بات صدق و سچائی اور عدل و انصاف کے لحاظ سے مکمل ہے اور اس کی باتوں کا کوئی بدلنے والا نہیں ہے اور وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(905, 6, 116, '(اے رسول(ص)) اگر آپ زمین کے رہنے والوں کی اکثریت کی اطاعت کریں گے (ان کا کہنا مانیں گے) تو وہ آپ کو اللہ کے راستہ سے بھٹکا دیں گے یہ لوگ پیروی نہیں کرتے مگر گمان کی اور وہ محض تخمینے لگاتے اور اٹکل پچو باتیں کرتے ہیں۔'),
(906, 6, 117, 'بے شک آپ کا پروردگار ہی بہتر جانتا ہے کہ اس کی راہ سے بھٹکا ہوا کون ہے؟ اور وہ خوب جانتا ہے ہدایت یافتہ لوگوں کو۔'),
(907, 6, 118, 'پس جس (ذبیحہ) پر اللہ کا نام لیا گیا ہے اس میں سے کھاؤ۔ اگر تم اس کی آیتوں پر ایمان رکھتے ہو۔'),
(908, 6, 119, 'اور تمہیں کیا ہوگیا ہے کہ تم اس (ذبیحہ) میں سے نہیں کھاتے جس پر اللہ کا نام لیا گیا ہے؟ حالانکہ اس نے جن (جانوروں) کو تم پر حرام قرار دیا ہے ان کو تمہارے لئے تفصیل سے بیان کر دیا ہے۔ سوائے اس کے جس کے کھانے کی طرف تم مضطر و مجبور ہو جاؤ (تو پھر حرام بھی کھا سکتے ہو) اور یقیناً بہت سے لوگ علم کے بغیر محض اپنی خواہشات کی بنا پر گمراہ کرتے ہیں بے شک آپ کا پروردگار حد سے بڑھنے والوں کو خوب جانتا ہے۔'),
(909, 6, 120, 'اور (اے لوگو) تمام گناہوں کو چھوڑ دو خواہ علانیہ ہوں اور خواہ خفیہ بے شک جو لوگ گناہ کما (کر) رہے ہیں عنقریب ان کو بدلہ دیا جائے گا اس کا جس کا وہ ارتکاب کرتے ہیں۔'),
(910, 6, 121, 'اور اس (جانور) کو نہ کھاؤ جس پر اللہ کا نام نہ لیا گیا ہو۔ کہ یہ (کھانا) فسق (نافرمانی) ہے اور بے شک شیطان اپنے دوستوں کو خفیہ اشارے کرتے ہیں (پٹی پڑھاتے ہیں) تاکہ وہ تم سے جھگڑا کریں اور اگر تم نے ان کی اطاعت کر لی (ان کا کہنا مانا) تو یقینا تم مشرک ہو جاؤ گے۔'),
(911, 6, 122, 'اور کیا وہ شخص جو (پہلے) مردہ تھا پھر ہم نے اسے زندہ کیا اور اس کے لئے ایک نور بنایا جس کے ساتھ وہ لوگوں میں چلتا پھرتا ہے اس شخص کی طرح ہو سکتا ہے جو تاریکیوں میں پڑا ہوا ہے اور ان سے نکل نہیں سکتا۔ جس طرح مؤمن کی نگاہ میں ایمان آراستہ کیا گیا ہے اسی طرح کافروں کی نگاہ میں ان کے اعمال آراستہ کر دیئے گئے ہیں۔'),
(912, 6, 123, 'اور (جس طرح ہم نے مکہ کے فاسقوں کو بڑا بنایا) اسی طرح ہم نے ہر بستی میں اس کے مجرمین کو بڑا سردار بنایا ہے تاکہ وہ وہاں مکر و فریب کیا کریں حالانکہ وہ فریب نہیں دیتے مگر اپنے آپ کو لیکن انہیں اس کا شعور نہیں ہے۔'),
(913, 6, 124, 'اور جب ان کے پاس کوئی معجزہ آتا ہے تو وہ کہتے ہیں کہ ہم اس وقت تک ہرگز ایمان نہیں لائیں گے جب تک ہم کو بھی وہی نہ دیا جائے جو اللہ کے رسولوں کو دیا گیا ہے۔ اللہ بہتر جانتا ہے کہ اپنی رسالت کو کہاں رکھے؟ (اس کا اہل کون ہے؟) عنقریب مجرموں کو اللہ کے یہاں ذلت نصیب ہوگی اور سخت عذاب بھی ہوگا ان کی ان مکاریوں کی وجہ سے جو وہ کیا کرتے تھے۔'),
(914, 6, 125, 'پس جب اللہ کسی کو ہدایت بخشنے کا ارادہ کرتا ہے تو اس کے سینہ کو اسلام کیلئے کھول دیتا ہے اور جسے گمراہی میں چھوڑنا چاہتا ہے تو اس کے سینہ کو تنگ کر دیتا ہے جیسے کہ وہ زبردستی آسمان پر چڑھ رہا ہے (اس کی طرف اونچا ہو رہا ہے) اسی طرح اللہ ان لوگوں پر کثافت مسلط کر دیتا ہے جو ایمان نہیں لاتے۔'),
(915, 6, 126, 'یہ تمہارے پروردگار کا سیدھا راستہ ہے۔ ہم نے نصیحت قبول کرنے والوں کے لیے آیتوں کو تفصیل سے بیان کر دیا ہے۔'),
(916, 6, 127, 'ان کیلئے سلامتی کا گھر ہے ان کے پروردگار کے ہاں اور ان کے اچھے اعمال کی وجہ سے جو وہ کرتے ہیں اللہ ان کا سرپرست ہے۔'),
(917, 6, 128, 'اور (وہ دن یاد کرو) جس دن وہ (اللہ) سب (جن و انس) کو محشر میں جمع کرے گا (اور جنات سے خطاب کرکے فرمائے گا) کہ اے گروہِ جن! تم نے بہت انسانوں کو گمراہ کیا (اس وقت) وہ آدمی جو ان (جنات) کے دوست و رفیق ہوں گے کہیں گے اے ہمارے پروردگار! ہم سب نے ایک دوسرے سے خوب فائدہ اٹھایا اور اب ہم اپنی اس میعاد کو پہنچ گئے ہیں جو تو نے ہمارے لئے مقرر کی تھی۔ اللہ فرمائے گا اب تمہارا ٹھکانہ دوزخ ہے جس میں تم ہمیشہ رہوگے۔ سوائے اس کے جسے اللہ (بچانا) چاہے گا۔ بے شک تمہارا پروردگار حکیم و علیم ہے (بڑی حکمت اور بڑے علم والا ہے)۔'),
(918, 6, 129, 'اور اسی طرح ہم بعض ظالموں کو ان کے اعمال (کرتوتوں) کی وجہ سے بعض کا سرپرست بناتے ہیں (یا ایک دوسرے پر مسلط کرتے ہیں)۔'),
(919, 6, 130, 'اے گروہِ جن و انس! کیا تمہارے پاس تم ہی میں سے (میرے) کچھ رسول نہیں آئے تھے جو تمہیں میری آیتیں سناتے تھے۔ اور تمہیں اس دن کی پیشی سے ڈراتے تھے؟ وہ جواب میں کہیں گے کہ ہم اپنے خلاف گواہی دیتے ہیں ان کو دنیوی زندگی نے دھوکہ میں مبتلا کر دیا تھا اور اب وہ اپنے خلاف گواہی دیں گے کہ وہ بے شک کافر تھے۔'),
(920, 6, 131, 'یہ (رسولوں کا بھیجنا) اس لئے ہے کہ تمہارا پروردگار بستیوں کو ظلم و جور سے ہلاک نہیں کرتا۔ جبکہ ان کے رہنے والے بے خبر ہوں۔'),
(921, 6, 132, 'اور ہر ایک کے لیے اس کے عمل کے مطابق درجے ہیں۔ اور جو کچھ لوگ کرتے رہتے ہیں آپ کا پروردگار اس سے غافل نہیں ہے۔'),
(922, 6, 133, 'آپ کا پروردگار بے نیاز ہے، رحمت والا ہے، وہ اگر چاہے تو تم لوگوں کو لے جائے اور تمہاری جگہ تمہارے بعد جن کو چاہے لے آئے جس طرح اس نے تمہیں پیدا کیا اور لوگوں کی نسل سے۔'),
(923, 6, 134, 'بے شک جو کچھ تم سے وعدہ وعید کیا گیا ہے وہ بے شک آکر رہے گا۔ اور تم (خدا کو) عاجز نہیں کر سکتے۔'),
(924, 6, 135, '(اے رسول(ص)) کہہ دیجئے! اے میری قوم تم اپنے مرتبہ و مقام کے مطابق عمل کرتے رہو میں بھی اپنے مرتبہ کے مطابق عمل کر رہا ہوں عنقریب تمہیں معلوم ہو جائے گا کہ انجامِ کار کس کے حق میں بہتر ہے جو ظالم ہیں وہ فلاح نہیں پائیں گے۔'),
(925, 6, 136, 'ان لوگوں نے اللہ کے لیے اسی کی پیدا کی ہوئی کھیتی اور مویشیوں میں ایک حصہ مقرر کر رکھا ہے اور اپنے خیال کے مطابق کہتے ہیں کہ یہ حصہ اللہ کا ہے اور یہ ہمارے شریکوں (دیوتاؤں) کا ہے۔ جو حصہ ان کے شریکوں کا ہوتا ہے وہ تو اللہ کو نہیں پہنچتا اور جو حصہ اللہ کا ہے وہ ان کے شریکوں تک پہنچ جاتا ہے یہ لوگ کیا ہی برا فیصلہ کرتے ہیں۔'),
(926, 6, 137, 'اور اسی طرح بہت سے مشرکوں کے لیے ان کے شریکوں نے ان کی اولاد کے قتل کرنے کو خوشنما بنا رکھا ہے تاکہ (انجامِ کار) انہیں تباہ کریں اور ان کے دین و مذہب کو ان پر مشتبہ کریں اگر اللہ (اپنی قدرتِ قاہرہ سے) چاہتا تو یہ ایسا نہ کرتے لہٰذا انہیں چھوڑیے اور ان کی افترا پردازیوں کو۔ (تاکہ وہ ان میں لگے رہیں)'),
(927, 6, 138, 'اور وہ اپنی خام خیالی سے کہتے ہیں کہ یہ چوپائے اور کھیت ممنوع ہیں انہیں کوئی نہیں کھا سکتا مگر وہ جسے ہم چاہیں اور جو کچھ چوپائے ہیں جن کی پشت پر سواری اور باربرداری کو حرام قرار دے دیا گیا ہے اور کچھ چوپائے ایسے ہیں جن پر وہ اللہ کا نام نہیں لیتے اور یہ سب کچھ انہوں نے اللہ پر افترا پردازی کرتے ہوئے کیا ہے عنقریب اللہ انہیں ان کی افترا پردازی کا بدلہ دے گا۔'),
(928, 6, 139, 'اور وہ کہتے ہیں کہ جو ان چوپاؤں کے پیٹ میں ہے وہ ہمارے مردوں کے لیے مخصوص ہے اور ہماری عورتوں پر حرام ہے اور اگر وہ مردار ہوں تو وہ سب اس میں شریک ہیں عنقریب اللہ ان کو اس بات بنانے کا بدلہ دے گا۔'),
(929, 6, 140, 'یقینا وہ لوگ بڑے گھاٹے میں ہیں جنہوں نے علم کے بغیر محض جہالت اور حماقت کی وجہ سے اپنی اولاد کو قتل کیا۔ اور اللہ پر افترا پردازی کرکے اللہ کے دیے ہوئے رزق کو حرام قرار دیا بے شک وہ گمراہ ہوئے اور ہدایت یافتہ اور راہ یاب نہیں ہیں۔'),
(930, 6, 141, 'اور وہ (اللہ) وہی ہے۔ جس نے طرح طرح کے باغات پیدا کیے (ٹیٹوں پر) چڑھائے ہوئے بھی اور بغیر چڑھائے ہوئے بھی اور کھجور کے درخت اور طرح طرح کی کھیتی جس کے مزے مختلف ہیں اور زیتون اور انار جو مشابہ بھی ہیں اور غیر مشابہ بھی اس کے پھلوں میں سے کھاؤ جب وہ پھلیں اور اس کی کٹائی کے دن اس کا حق ادا کرو اور اسراف مت کرو کیونکہ وہ (خدا) اسراف کرنے والوں کو دوست نہیں رکھتا۔'),
(931, 6, 142, 'اور اللہ وہی ہے جس نے چوپاؤں میں سے کچھ ایسے پیدا کیے ہیں جن سے سواری اور باربرداری کا کام لیا جاتا ہے اور کچھ وہ ہیں جو (کھانے اور) بچھانے کے کام آتے ہیں جو کچھ اللہ نے تمہیں روزی عطا کی ہے اس سے کھاؤ اور شیطان کے نقشِ قدم پر نہ چلو کیونکہ وہ تمہارا کھلا ہوا دشمن ہے۔'),
(932, 6, 143, '(خدا نے) آٹھ قسم کے جوڑے پیدا کیے ہیں دو قسمیں بھیڑ سے اور دو قسمیں بکری سے آپ کہیے کہ آیا اس نے ان دونوں قسم کے نروں کو حرام کیا ہے یا دونوں قسم کے ماداؤں کو؟ یا جو دونوں، ماداؤں کے پیٹ میں ہے؟ مجھے علم کی بنیاد پر بتاؤ۔ اگر تم سچے ہو۔'),
(933, 6, 144, 'اور اس طرح دو قسمیں اونٹ سے اور دو قسمیں گائے سے۔ کہو اس (اللہ) نے دونوں قسموں کے نروں کو حرام کیا ہے یا دونوں قسم کی ماداؤں کو؟ یا جو دونوں ماداؤں کے پیٹ میں ہے؟ کیا تم اس وقت موجود تھے جب اللہ نے تمہیں اس بات کا حکم دیا تھا؟ تو اس شخص سے بڑھ کر کون ظالم ہوگا جو اللہ پر جھوٹا الزام لگائے تاکہ کسی علم کے بغیر لوگوں کو گمراہ کرے بے شک اللہ ظالم قوم کو ہدایت نہیں کرتا (اسے منزلِ مقصود تک نہیں پہنچاتا)۔'),
(934, 6, 145, 'کہہ دیجئے جو وحی میرے پاس آئی ہے میں اس میں کوئی چیز ایسی نہیں پاتا جو کھانے والے پر حرام ہو سوا اس کے کہ مردار ہو یا بہایا ہوا خون ہو یا سور کا گوشت ہو یہ سب رجس اور گندگی ہے یا پھر فسق ہو (نافرمانی کا ذریعہ ہو یعنی) جو اللہ کے سوا کسی اور نام پر ذبح کیا گیا ہو (یا غیر اللہ کے لیے نامزد کیا گیا ہو) ہاں البتہ اگر کوئی مجبور ہو جائے جبکہ وہ نہ باغی و سرکش ہو اور نہ ہی حد سے تجاوز کرنے والا ہو تو یقینا تمہارا پروردگار بڑا بخشنے والا بڑا رحم کرنے والا ہے۔'),
(935, 6, 146, 'اور جو لوگ یہودی ہوئے ہم نے ان پر تمام کھر والے جانور حرام کر دیئے تھے۔ اور گائے بیل اور بھیڑ بکری کی چربی بھی حرام کر دی۔ سوا اس کے جو ان کی پیٹھ یا ان کی آنتڑیوں سے لگی ہوئی یا ہڈی سے ملی ہوئی ہو ہم نے ان کو بغاوت و سرکشی کی سزا دی تھی اور یقینا ہم بالکل سچے ہیں۔'),
(936, 6, 147, 'سو اگر یہ آپ کو جھٹلائیں تو آپ کہہ دیجئے! کہ آپ کا پروردگار بڑی وسیع رحمت والا ہے اور مجرموں سے اس کا عذاب ٹالا نہیں جا سکتا۔'),
(937, 6, 148, 'عنقریب مشرک لوگ کہیں گے کہ اگر اللہ نہ چاہتا تو ہم شرک نہ کرتے اور نہ ہمارے باپ دادا اور نہ ہی ہم کسی چیز کو حرام قرار دیتے اسی طرح ان لوگوں نے بھی جھٹلایا تھا جو ان سے پہلے تھے یہاں تک کہ انہوں نے ہمارے عذاب کا مزہ چکھا۔ آپ کہہ دیجئے کہ اگر تمہارے پاس کوئی علمی دلیل ہے تو اسے ہمارے سامنے ظاہر کرو۔ تم تو محض گمان کی پیروی کر رہے ہو اور صرف اٹکل پچو باتیں کرتے ہو۔'),
(938, 6, 149, 'کہہ دیجئے! کہ اللہ کی دلیل زبردست ہے۔ اگر اللہ (اپنی مشیت قاہرہ سے) چاہتا تو تم سب کو ہدایت دے دیتا۔'),
(939, 6, 150, 'کہہ دیجئے! لاؤ اپنے ان گواہوں کو جو اس بات کی گواہی دیں کہ اللہ نے اس کو حرام کیا ہے؟ اور اگر وہ (جھوٹی) گواہی دے بھی دیں تو آپ نہ ان کے ساتھ گواہی دیجئے اور نہ ہی ان لوگوں کی خواہشات کی پیروی کیجئے۔ جنہوں نے ہماری آیتوں کو جھٹلایا ہے۔ اور جو آخرت پر ایمان نہیں رکھتے اور وہ اپنے پروردگار کے برابر دوسروں کو ٹھہراتے ہیں۔'),
(940, 6, 151, '(اے رسول) ان سے کہو۔ آؤ میں تمہیں سناؤں وہ چیزیں جو تمہارے پروردگار نے تم پر حرام کی ہیں: (۱) یہ کہ اس کے ساتھ کسی کو شریک نہ ٹھہراؤ۔ (۲) اور والدین کے ساتھ حسن سلوک کرو۔ (۳) اور اپنی اولاد کو فقر و فاقہ کے ڈر سے قتل نہ کرو۔ ہم تمہیں بھی روزی دیتے ہیں اور انہیں بھی (دیں گے)۔ (۴) اور بے شرمی و بے حیائی کے کاموں (جیسے جنسی غلط کاری) کے قریب بھی نہ جاؤ۔ خواہ وہ علانیہ ہوں اور خواہ پوشیدہ۔ (۵) اور نہ قتل کرو کسی ایسی جان کو جس کے قتل کو خدا نے حرام قرار دیا ہے مگر (شرعی) حق (جیسے قصاص وغیرہ) کے ساتھ۔ یہ وہ ہے جس کی اللہ نے تمہیں وصیت کی ہے۔ تاکہ تم عقل سے کام لو۔'),
(941, 6, 152, '(۶) اور یتیم کے مال کے قریب نہ جاؤ مگر ایسے طریقہ سے جو بہترین ہو یہاں تک کہ وہ اپنے سنِ رشد و کمال تک پہنچ جائے۔ (۷) اور ناپ تول انصاف کے ساتھ پوری کرو۔ ہم کسی شخص کو اس کی وسعت سے زیادہ تکلیف نہیں دیتے۔ (۸) اور جب کوئی بات کہو تو عدل و انصاف کے ساتھ۔ اگرچہ وہ (شخص) تمہارا قرابتدار ہی کیوں نہ ہو۔ (۹) اور اللہ کے عہد و پیمان کو پورا کرو۔ یہ وہ ہے جس کی اس (اللہ) نے تمہیں وصیت کی ہے شاید کہ تم عبرت حاصل کرو۔'),
(942, 6, 153, '(۱۰) اور (یہ بھی کہو) یہ ہے میرا سیدھا راستہ اسی کی پیروی کرو۔ اور دوسرے راستوں کی پیروی نہ کرو ورنہ وہ تمہیں اس (اللہ) کے راستہ سے جدا کر دیں گے یہ ہے جس کی اللہ نے تمہیں وصیت کی ہے شاید کہ تم پرہیزگار بن جاؤ۔'),
(943, 6, 154, 'پھر ہم نے موسیٰ کو وہ کتاب عطا کی جو نیک عمل کرنے والے پر نعمت کی تکمیل ہے۔ اور اس میں ہر چیز کی تفصیل ہے اور (لوگوں کے لیے) سراسر ہدایت و رحمت ہے تاکہ وہ اپنے پروردگار کی بارگاہ میں حاضری و حضوری پر ایمان لائیں۔'),
(944, 6, 155, 'اور یہ (قرآن) بڑی بابرکت کتاب ہے جسے ہم نے نازل کیا ہے پس تم اس کی پیروی کرو اور تقویٰ اختیار کرو۔ شاید کہ تم پر رحم کیا جائے۔'),
(945, 6, 156, '(اور اس لیے بھی نازل کی ہے کہ) تم یہ (نہ) کہو کہ ہم سے پہلے دو گروہوں (یہود و نصاریٰ) پر تو کتاب (تورات و انجیل) نازل کی گئی۔ اور ہم تو (عرب ہونے کی وجہ سے) ان کے پڑھنے پڑھانے سے غافل و بے خبر تھے۔'),
(946, 6, 157, 'یا یہ (نہ) کہو کہ اگر ہم پر کتاب نازل کی گئی ہوتی تو ہم ان لوگوں سے زیادہ راہِ راست پر ہوتے۔ تو اب (تمہارے تمام عذر بہانے قطع کرنے کے لیے) تمہارے پروردگار کی طرف سے کھلی ہوئی دلیل اور ہدایت و رحمت (قرآن کی صورت میں) آگئی ہے سو اب اس سے بڑھ کر ظالم کون ہوگا جو آیاتِ الٰہی کو جھٹلائے اور ان سے روگردانی کرے۔ ہم عنقریب ان لوگوں کو جو ہماری آیات سے روگردانی کرتے ہیں۔ بڑے عذاب کی سزا دیں گے۔'),
(947, 6, 158, 'کیا یہ لوگ اب صرف اس بات کا انتظار کر رہے ہیں کہ ان کے پاس فرشتے آئیں؟ یا تمہارا پروردگار بذات خود آجائے یا تمہارے پروردگار کی بعض خاص نشانیاں آجائیں۔ یہ تب (ایمان لائیں گے)۔ تو پھر (یاد رکھو) جس دن تمہارے پروردگار کی بعض مخصوص نشانیاں آجائیں گی تو اس دن ایسے شخص کو ایمان لانا کوئی فائدہ نہیں دے گا جو پہلے ایمان نہیں لایا ہوگا اور اپنے ایمان میں کوئی بھلائی نہ کمائی ہوگی۔ ان سے کہو کہ تم بھی انتظار کرو۔ ہم بھی انتظار کرتے ہیں۔'),
(948, 6, 159, 'بے شک جن لوگوں نے اپنے دین میں تفرقہ ڈالا اور گروہ گروہ بن گئے آپ کا ان سے کوئی تعلق نہیں ہے ان کا معاملہ بس اللہ کے حوالہ ہے۔ پھر وہ انہیں بتلائے گا کہ وہ کیا کیا کرتے تھے؟'),
(949, 6, 160, 'جو شخص ایک نیکی لے کر (اللہ کی بارگاہ میں) آئے گا اس کو دس گنا (اجر) ملے گا۔ اور ان پر ظلم نہیں کیا جائے گا۔'),
(950, 6, 161, 'آپ کہیں! بے شک میرے پروردگار نے مجھے بڑے سیدھے راستہ کی طرف راہنمائی کر دی ہے یعنی اس صحیح اور راست دین کی طرف جو باطل سے ہٹ کر صرف حق کی طرف راغب ابراہیم (ع) کی ملت ہے اور وہ شرک کرنے والوں میں سے نہیں تھے۔'),
(951, 6, 162, '(اے رسول(ص)) کہو میری نماز اور میری تمام (مختلف) عبادتیں اور میری زندگی اور میری موت صرف اللہ کے لئے ہے جو تمام جہانوں کا پروردگار ہے۔'),
(952, 6, 163, 'اس کا کوئی شریک نہیں ہے اور مجھے اسی کا حکم دیا گیا ہے اور میں سب سے پہلے سرِ تسلیم خم کرنے والا ہوں۔'),
(953, 6, 164, '(اے رسول(ص)) کہہ دیجیے! کیا میں اللہ کے علاوہ کوئی اور رب تلاش کروں؟ حالانکہ وہ ہر چیز کا پروردگار ہے اور ہر نفس جو کچھ کماتا ہے اس کا وبال اسی پر ہے۔ اور کوئی بوجھ اٹھانے والا کسی دوسرے کا بوجھ نہیں اٹھائے گا پھر تم سب کی بازگشت تمہارے پروردگار کی طرف ہے سو وہی تمہیں بتائے گا جس جس چیز میں تم اختلاف کرتے تھے۔'),
(954, 6, 165, 'وہ (خدا) وہی ہے جس نے تمہیں زمین میں (پہلوں) کا خلیفہ اور جانشین بنایا اور تم سے بعض کو بعض پر درجات کے اعتبار سے بلندی عطا فرمائی تاکہ جو کچھ تمہیں عطا کیا ہے اس میں تمہاری آزمائش کرے۔ بے شک تمہارا پروردگار بہت جلد سزا دینے والا اور بے شک وہ بڑا بخشنے والا اور بڑا رحم کرنے والا بھی ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف، لام، میم، ص۔'),
(956, 7, 2, 'یہ ایک کتاب ہے جو آپ کی طرف نازل کی گئی ہے۔ لہٰذا تمہارے دل میں اس کی وجہ سے کوئی تنگی نہیں ہونا چاہیے۔ (یہ کتاب اس لئے نازل کی گئی ہے) تاکہ آپ اس کے ذریعہ سے (لوگوں کو خدا کے عذاب سے) ڈرائیں اور اہلِ ایمان کے لئے نصیحت اور یاد دہانی ہو۔'),
(957, 7, 3, '(اے لوگو) جو کچھ تمہارے پروردگار کی طرف سے تمہاری طرف نازل کیا گیا ہے اس کی پیروی کرو۔ اور اس (پروردگار) کو چھوڑ کر دوسرے سرپرستوں (حوالی موالی) کی پیروی نہ کرو۔ تم لوگ بہت ہی کم نصیحت قبول کرتے ہو۔'),
(958, 7, 4, 'اور کتنی ہی بستیاں ہیں کہ ہم نے انہیں تباہ کر دیا پس ہمارا عذاب ان پر راتوں رات آیا یا جب وہ دن میں قیلولہ کر رہے تھے (سو رہے تھے)۔'),
(959, 7, 5, 'جب ہمارا عذاب ان پر آیا تو اس کے سوا ان کی اور کوئی پکار نہ تھی کہ بے شک ہم ظالم تھے۔'),
(960, 7, 6, 'بے شک ہم ان لوگوں سے بھی باز پرس کریں گے۔ جن کی طرف رسول بھیجے گئے تھے۔ اور خود رسولوں سے بھی پوچھ گچھ کریں گے۔'),
(961, 7, 7, 'پھر ہم پورے علم کے ساتھ ان کے سامنے سب (حالات و واقعات) بیان کریں گے آخر ہم غیر حاضر تو تھے نہیں۔'),
(962, 7, 8, 'اور اس دن وزن (اعمال کا تولا جانا) بالکل حق ہے پس جس کے پلے بھاری ہوں گے وہی کامیاب ہوں گے۔'),
(963, 7, 9, 'اور جس کے پلے ہلکے ہوں گے۔ وہ اپنے آپ کو خسارے میں مبتلا کرنے والے ہوں گے کیونکہ وہ ناانصافی کرتے تھے۔'),
(964, 7, 10, 'اور ہم نے تمہیں زمین میں تمکین دی (اقتدار و اختیار دیا) اور اس میں تمہارے لئے زندگی گزارنے کے سامان فراہم کئے۔ مگر تم بہت ہی کم شکر کرتے ہو۔'),
(965, 7, 11, 'بے شک ہم نے تمہاری تخلیق کا آغاز کیا اور پھر تمہاری صورت گری کی اس کے بعد فرشتوں سے کہا کہ آدم(ع) کے سامنے سجدہ ریز ہو جاؤ چنانچہ سب نے سجدہ کیا سوا ابلیس کے کہ وہ سجدہ کرنے والوں میں سے نہ تھا۔'),
(966, 7, 12, '(خدا نے) فرمایا: جب میں نے تجھے حکم دیا تھا تو تجھے کس چیز نے روکا؟ کہا میں اس (آدم(ع)) سے بہتر ہوں کیونکہ تو نے مجھے آگ سے پیدا کیا ہے اور اسے مٹی سے پیدا کیا ہے۔'),
(967, 7, 13, 'ارشاد ہوا۔ تو پھر تو یہاں سے نیچے اتر جا۔ کیونکہ تجھے یہ حق نہیں ہے کہ تو یہاں رہ کر تکبر و غرور کرے۔ بس یہاں سے نکل جا۔ یقینا تو ذلیلوں میں سے ہے۔'),
(968, 7, 14, 'کہا۔ مجھے اس دن تک مہلت دے جب سب لوگ دوبارہ اٹھائے جائیں گے۔'),
(969, 7, 15, 'فرمایا: اچھا تو ان میں سے ہے جنہیں مہلت دی گئی ہے۔'),
(970, 7, 16, 'کہا چونکہ تو نے مجھے گمراہ کیا ہے تو اب میں تیرے سیدھے راستہ پر بیٹھوں گا۔'),
(971, 7, 17, 'پھر میں ان کے آگے کی طرف سے اور ان کے پیچھے کی جانب سے اور ان کی دائیں طرف سے اور ان کی بائیں طرف سے آؤں گا (اور انہیں ورغلاؤں گا) اور تو ان میں سے اکثر کو شکر گزار نہیں پائے گا۔'),
(972, 7, 18, 'فرمایا: ذلیل اور راندہ ہوکر یہاں سے نکل جا میں تجھے اور اسے جو ان میں سے تیری پیروی کرے گا تم سب سے جہنم بھر دوں گا۔'),
(973, 7, 19, 'اور اے آدم(ع)! تم اور تمہاری بیوی دونوں اس بہشت میں رہو۔ اور جہاں سے چاہو (اور جو چیز دل چاہے) کھاؤ۔ مگر اس درخت کے پاس نہ جانا۔ ورنہ ظالموں میں سے ہو جاؤگے۔'),
(974, 7, 20, 'تو شیطان نے جھوٹی قسم کھا کر ان دونوں کو وسوسہ میں ڈالا۔ تاکہ ان کے وہ ستر والے مقام جو ایک دوسرے سے پوشیدہ تھے ظاہر کر دے اور کہا کہ تمہارے پروردگار نے تمہیں صرف اس لئے اس درخت سے روکا ہے کہ کہیں فرشتے نہ بن جاؤ یا ہمیشہ زندہ رہنے والوں میں سے نہ ہو جاؤ۔'),
(975, 7, 21, 'اور اس نے دونوں سے قسم کھائی کہ میں تمہارے سچے خیرخواہوں میں سے ہوں۔'),
(976, 7, 22, 'اس طرح اس نے ان دونوں کو فریب سے مائل کر دیا (اور اس طرح جنت) کی بلندیوں سے زمین کی پستیوں تک پہنچا دیا۔ پس جب انہوں نے اس درخت کا مزہ چکھا۔ تو ان کے جسم کے چھپے ہوئے حصے نمودار ہوگئے اور وہ جنت کے پتوں کو جوڑ کر اپنے یہ مقام (ستر) چھپانے لگے تب ان کے پروردگار نے ان کو ندا دی۔ کیا میں نے تمہیں اس درخت کے پاس جانے سے منع نہیں کیا تھا؟ اور نہیں کہا تھا کہ شیطان تمہارا کھلا ہوا دشمن ہے۔'),
(977, 7, 23, 'اس وقت ان دونوں نے کہا۔ اے ہمارے پروردگار! ہم نے اپنے اوپر ظلم کیا۔ اور اگر تو درگزر نہیں کرے گا اور ہم پر رحم نہیں کرے گا تو ہم گھاٹا اٹھانے والوں میں سے ہوں گے۔'),
(978, 7, 24, 'ارشاد ہوا! تم دونوں (بہشت سے) نیچے اترو۔ تم ایک دوسرے کے دشمن ہو اور ایک وقت معلوم تک تمہارا زمین میں ہی ٹھکانہ اور سامانِ زیست ہے۔'),
(979, 7, 25, '(یہ بھی فرمایا) اسی زمین میں تم نے جینا ہے اور وہیں مرنا ہے اور پھر اسی سے دوبارہ نکالے جاؤگے۔'),
(980, 7, 26, 'اے اولادِ آدم(ع)! ہم نے تمہارے لئے لباس نازل کیا ہے جو تمہارے جسم کے قابلِ ستر حصوں کو چھپاتا ہے اور (باعث) زینت بھی ہے۔ اور تقویٰ و پرہیزگاری کا لباس یہ سب سے بہتر ہے یہ (لباس بھی) اللہ کی نشانیوں میں سے ہے تاکہ لوگ نصیحت حاصل کریں۔'),
(981, 7, 27, 'اے اولادِ آدم! (ہوشیار رہنا) کہیں ایسا نہ ہو کہ شیطان تمہیں اسی طرح فتنہ میں مبتلا کر دے جس طرح اس نے تمہارے ماں باپ کو جنت سے نکلوایا تھا۔ اور ان کے جسم سے کپڑے تک اتروا دیئے تھے تاکہ ان کے جسم کے قابلِ ستر حصے ان کو دکھا دے یقینا وہ (شیطان) اور اس کا قبیلہ تمہیں جس طرح اور جہاں سے دیکھتا ہے تم انہیں اس طرح نہیں دیکھ سکتے بے شک ہم نے شیطانوں کو ان کا سرپرست بنایا ہے جو ایمان نہیں رکھتے۔'),
(982, 7, 28, 'اور جب وہ کوئی بے حیائی کا کام کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے آباء و اجداد کو اسی طریقہ پر پایا ہے اور خدا نے (بھی) ہمیں یہی حکم دیا ہے اے رسول کہیئے کہ یقینا کبھی اللہ بے حیائی کا حکم نہیں دیتا کیا تم اللہ پر ایسی بات کی تہمت لگاتے ہو جو تم نہیں جانتے؟'),
(983, 7, 29, 'اے رسول(ص) کہو! میرے پروردگار نے مجھے عدل و انصاف کا حکم دیا ہے اور یہ کہ اپنے چہرہ کو سیدھ پر رکھو ہر نماز کے وقت اور اسی کو پکارو دین کو اسی کے لئے خالص کرکے جس طرح اس نے پہلی بار تمہیں پیدا کیا تھا اسی طرح تم (اس کے حضور پلٹ) کر جاؤگے۔'),
(984, 7, 30, 'ایک گروہ کو تو اس نے ہدایت دے دی ہے اور ایک گروہ پر گمراہی ثبت ہوگئی ہے۔ انہوں نے اللہ کو چھوڑ کر شیاطین کو اپنا سرپرست بنا لیا ہے اور پھر وہ (اپنے متعلق) خیال کرتے ہیں کہ وہ ہدایت یافتہ ہیں۔'),
(985, 7, 31, 'اے اولادِ آدم! ہر نماز کے وقت زینت کرو اور کھاؤ پیو۔ اور اسراف (فضول خرچی) نہ کرو۔ کیونکہ وہ بے شک اسراف کرنے والوں کو دوست نہیں رکھتا۔'),
(986, 7, 32, '(اے رسول ان لوگوں سے) کہو کہ اللہ کی زیب و زینت کو جو اس نے اپنے بندوں کے لئے پیدا کی ہے کس نے حرام کیا ہے؟ اور کھانے کی اچھی اور پاکیزہ غذاؤں کو (کس نے حرام کیا ہے؟)! وہ تو دراصل ہیں ہی اہلِ ایمان کے لئے دنیوی زندگی میں بھی اور خاص کر قیامت کے دن تو خالص انہی کے لئے ہیں۔ اسی طرح ہم اپنی آیتوں کو تفصیل کے ساتھ بیان کرتے ہیں ان لوگوں کے لئے جو علم رکھتے ہیں۔'),
(987, 7, 33, 'تم کہہ دو! میرے پروردگار نے صرف بے حیائی و بدکاری کے کاموں کو حرام قرار دیا ہے خواہ ظاہری ہوں یا باطنی۔ اور اثم (ہر گناہ یا شراب) کو اور کسی پر ناحق زیادتی کو اور یہ کہ کسی کو اللہ کا شریک بناؤ جس کے لئے اللہ نے کوئی دلیل نہیں اتاری اور یہ کہ اللہ کے بارے میں کوئی ایسی بات کہو جس کا تمہیں علم و یقین نہ ہو۔'),
(988, 7, 34, 'اور ہر امت (قوم) کے لئے ایک وقت مقرر ہوتا ہے۔ پس جب وہ مقررہ وقت آجاتا ہے تو نہ وہ ایک ساعت کے لیے پیچھے ہٹ سکتے ہیں اور نہ ایک ساعت آگے بڑھ سکتے ہیں۔'),
(989, 7, 35, 'اے اولادِ آدم اگر تمہارے پاس تم ہی سے میرے کچھ رسول آئیں جو تمہیں میری آیات پڑھ کر سنائیں (اور میرے احکام تم تک پہنچائیں) تو جو شخص پرہیزگاری اختیار کرے گا۔ اور اپنی اصلاح کرے گا۔ ان کے لئے نہ کوئی خوف ہوگا اور نہ وہ غمگین ہوں گے۔'),
(990, 7, 36, 'اور جو لوگ ہماری آیتوں کو جھٹلائیں گے اور ان کے مقابلہ میں تکبر کریں گے وہی لوگ جہنم والے ہوں گے جو اس میں ہمیشہ ہمیشہ رہیں گے۔'),
(991, 7, 37, 'اور اس شخص سے بڑھ کر کون ظالم ہوگا جو اللہ پر جھوٹا بہتان باندھے (خود جھوٹی باتیں گھڑ کر اس کی طرف منسوب کرے) یا اس کی آیتوں کو جھٹلائے، ایسے لوگوں کو نوشتۂ کتاب (اپنے مقدر) کا حصہ تو ملے گا۔ لیکن جب ان کے پاس ہمارے فرستادہ (فرشتے) ان کی روحوں کو قبض کرنے کے لیے آئیں گے تو وہ ان سے کہیں گے۔ (آج) وہ کہاں ہیں جن کو تم اللہ کے سوا پکارتے تھے؟ وہ جواب دیں گے کہ وہ تو ہم سے غائب ہوگئے اور وہ خود اپنے خلاف گواہی دیں گے کہ بے شک وہ کافر تھے۔'),
(992, 7, 38, 'ارشاد ہوگا تم بھی اسی جہنم میں داخل ہو جاؤ جس میں تم سے پہلے گزرے ہوئے جن و انس کے گروہ داخل ہو چکے ہیں (اور) جب بھی کوئی (جہنم میں) داخل ہوگا تو اپنے ساتھ (پیش رو) گروہ پر لعنت کرے گا۔ یہاں تک کہ جب سب وہاں جمع ہو جائیں گے (سب واصلِ جہنم ہو جائیں گے) تو بعد والا گروہ پہلے گروہ کے بارے میں کہے گا اے پروردگار! انہوں نے ہمیں گمراہ کیا تھا اس لئے انہیں دوزخ کا دوگنا عذاب دے۔ ارشاد ہوگا ہر ایک کے لئے دونا عذاب ہے لیکن تم جانتے نہیں ہو۔'),
(993, 7, 39, 'اور ان کا پہلا گروہ دوسرے گروہ سے کہے گا۔ کہ (اگر ہم مستوجبِ الزام تھے) تو پھر تم کو ہم پر کوئی فضیلت تو نہ ہوئی؟ بس (ہماری طرح) تم بھی اپنے کئے کی پاداش میں عذاب کا مزہ چکھو۔'),
(994, 7, 40, 'یقینا جن لوگوں نے ہماری آیتوں کو جھٹلایا اور ان کے مقابلہ میں تکبر و غرور کیا۔ ان کے لئے آسمان کے دروازے نہیں کھولے جائیں گے۔ اور اس وقت تک وہ جنت میں داخل نہیں ہوں گے جب تک اونٹ سوئی کے ناکے میں سے نہیں گزر جائے گا ہم اسی طرح مجرموں کو بدلہ دیا کرتے تھے۔'),
(995, 7, 41, 'ان کا بچھونا بھی جہنم کا ہوگا اور اوپر سے اوڑھنا بھی جہنم کا ہم اسی طرح ظالموں کو بدلہ دیتے ہیں۔'),
(996, 7, 42, 'اور جو لوگ ایمان لائے اور نیک عمل کرتے رہے ہم کسی نفس کو اس کی وسعت سے زیادہ تکلیف نہیں دیتے یہی لوگ جنتی ہیں وہ اس میں ہمیشہ ہمیشہ رہیں گے۔'),
(997, 7, 43, 'اور جو کچھ ان کے دلوں میں کدورت ہوگی ہم اسے باہر نکال دیں گے ان کے نیچے سے نہریں بہتی ہوں گی۔ اور وہ شکر کرتے ہوئے کہیں گے ہر قسم کی تعریف اس اللہ کے لئے ہے جس نے ہمیں اس منزلِ مقصود تک پہنچایا اور ہم کبھی یہاں تک نہیں پہنچ سکتے تھے اگر وہ ہمیں نہ پہنچاتا۔ یقینا ہمارے پروردگار کے رسول حق کے ساتھ آئے اور انہیں ندا دی جائے گی کہ یہ بہشت ہے جس کے تم اپنے ان اعمال کی بدولت وارث بنائے گئے ہو۔ جو تم انجام دیا کرتے تھے۔'),
(998, 7, 44, 'اور جنتی لوگ دوزخیوں کو ندا دیں گے کہ ہمارے پروردگار نے ہم سے جو وعدہ کیا تھا اس کو ہم نے تو سچا پا لیا ہے کیا تم نے بھی اس وعدہ کو جو تمہارے پروردگار نے تم سے کیا تھا سچا پایا ہے؟ وہ (جواب میں) کہیں گے ہاں تب ایک منادی ان کے درمیان ندا کرے گا کہ خدا کی لعنت ان ظالموں پر۔'),
(999, 7, 45, 'جو اللہ کے راہ سے (لوگوں کو) روکتے رہے ہیں اور اس میں کجی تلاش کرتے رہے ہیں۔ اور وہ آخرت کا انکار کرتے رہے ہیں۔'),
(1000, 7, 46, 'اور ان دونوں (بہشت و دوزخ) کے درمیان پردہ ہے (حدِ فاصل ہے) اور اعراف پر کچھ لوگ ہوں گے جو ہر ایک کو اس کی علامت سے پہچان لیں گے۔ اور وہ بہشت والوں کو پکار کر کہیں گے کہ تم پر سلام ہو اور یہ لوگ (ابھی) اس میں داخل نہیں ہوئے ہوں گے۔ حالانکہ وہ اس کی خواہش رکھتے ہوں گے۔'),
(1001, 7, 47, 'اور جب ان کی نگاہیں دوزخ والوں کی طرف پھریں گی تو کہیں گے اے ہمارے پروردگار! ہمیں ظالم لوگوں کے ساتھ شامل نہ کر۔'),
(1002, 7, 48, 'اور پھر اعراف والے لوگ (دوزخ والے چند آدمیوں کو) آواز دیں گے جنہیں وہ علامتوں سے پہچانتے ہوں گے۔ کہ آج تمہاری جمعیت اور تمہارا غرور و پندار کچھ کام نہ آیا اور تمہیں کوئی فائدہ نہ پہنچایا۔'),
(1003, 7, 49, '(پھر کچھ مستحقین جنت کی طرف اشارہ کرتے ہوئے کہیں گے) کیا یہی وہ لوگ ہیں جن کے متعلق تم قسمیں کھا کھا کر کہتے تھے کہ اللہ ان تک اپنی کچھ بھی رحمت نہیں پہنچائے گا۔ (پھر ان مستحقین جنت سے کہیں گے) تم بہشت میں داخل ہو جاؤ۔ نہ تم پر کوئی خوف ہے اور نہ ہی تم مغموم ہوگے۔'),
(1004, 7, 50, 'اور دوزخ والے بہشت والوں کو پکاریں گے کہ تھوڑا سا پانی یا جو کچھ اللہ تعالیٰ نے تمہیں کھانے کو عطا فرمایا ہے اس میں سے کچھ ہماری طرف انڈیل دو وہ (جواب میں) کہیں گے کہ اللہ تعالیٰ نے یہ دونوں چیزیں کافروں پر حرام کر دی ہیں۔'),
(1005, 7, 51, 'جنہوں نے اپنے دین کو کھیل تماشہ بنا لیا تھا اور جنہیں زندگانئ دنیا نے دھوکہ دیا تھا آج ہم بھی انہیں اسی طرح بھلا دیں گے جس طرح انہوں نے آج کے اس دن کی حاضری کو بھلا دیا تھا۔ اور جیسے وہ ہماری آیتوں کو برابر جھٹلاتے رہے تھے۔'),
(1006, 7, 52, 'اور بے شک ہم ان کے پاس ایک ایسی کتاب لائے ہیں جسے ہم نے پورے علم کے ساتھ خوب واضح کر دیا ہے اور جو ایمان لانے والوں کے لئے برابر ہدایت و رحمت ہے۔'),
(1007, 7, 53, 'کیا یہ لوگ اس (قرآن کی دھمکی) کے انجام کا انتظار کر رہے ہیں؟ (کہ سامنے آئے) حالانکہ جس دن اس کا انجام (سامنے) آئے گا تو وہی لوگ جنہوں نے پہلے اس کو بھلایا ہوگا کہیں گے بے شک ہمارے پروردگار کے رسول ہمارے پاس سچائی کے ساتھ آئے تھے تو کیا آج ہمارے لئے کچھ سفارشی ہیں؟ جو ہماری سفارش کریں یا (یہ ممکن ہے کہ) ہمیں دوبارہ واپس بھیج دیا جائے تاکہ جو کچھ ہم (پہلے) کیا کرتے تھے اس کے خلاف کچھ اور (نیک عمل) کریں؟ یقینا انہوں نے اپنے آپ کو خسارہ میں ڈال دیا اور وہ افترا پردازیاں جو وہ کیا کرتے تھے آج ان سے گم ہوگئیں۔'),
(1008, 7, 54, 'بے شک تمہارا پروردگار وہی اللہ ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا۔ پھر عرش کی جانب متوجہ ہوا (اور کائنات کے اقتدارِ اعلیٰ پر قابض ہوا) جو رات سے دن کو ڈھانپ لیتا ہے۔ کہ اسے تیزی کے ساتھ جا پکڑتی ہے۔ اور جس نے سورج، چاند اور ستاروں کو پیدا کیا۔ کہ وہ سب طبعی طور پر اس کے حکم کے تابع ہیں خبردار! پیدا کرنا اور حکم دینا اس سے مخصوص ہے۔ بابرکت ہے اللہ جو سارے جہانوں کا پروردگار ہے۔'),
(1009, 7, 55, 'اپنے پروردگار سے گڑگڑا کر اور چپکے چپکے دعا کرو۔ بے شک وہ حد سے تجاوز کرنے والوں کو دوست نہیں رکھتا۔'),
(1010, 7, 56, 'اور زمین میں اس کی اصلاح کے بعد فساد برپا نہ کرو۔ اور خدا سے خوف اور امید کے ساتھ دعا کرو۔ بے شک اللہ کی رحمت نیکوکاروں سے قریب ہے۔'),
(1011, 7, 57, 'وہ وہی (خدا) ہے جو اپنی رحمت (بارش) سے پہلے ہواؤں کو خوشخبری کے لئے بھیجتا ہے یہاں تک کہ جب وہ بھاری بادلوں کو اٹھا لیتی ہیں تو ہم انہیں کسی مردہ بستی کی طرف ہانک کر لے جاتے ہیں اور پھر وہاں پانی برسا دیتے ہیں۔ اور پھر ہم اس کے ذریعہ سے طرح طرح کے پھل نکالتے ہیں اسی طرح ہم مردوں کو (بھی زندہ کرکے) باہر نکالیں گے شاید کہ تم عبرت اور نصیحت حاصل کرو۔'),
(1012, 7, 58, 'اور جو زمین عمدہ و پاکیزہ ہوتی ہے خدا کے حکم سے اس کی نبات (پیداوار) خوب نکلتی ہے اور جو خراب اور ناکارہ ہوتی ہے تو اس کی پیداوار بھی خراب اور بہت کم نکلتی ہے ہم اسی طرح شکر گزار قوم کے لیے اپنی نشانیاں الٹ پھیر کر بیان کرتے ہیں۔'),
(1013, 7, 59, 'ہم نے نوح کو ان کی قوم کی طرف (رسول بنا کر) بھیجا۔ تو انہوں نے کہا اے میری قوم اللہ کی عبادت کرو اس کے علاوہ تمہارا کوئی اللہ نہیں ہے۔ یقینا مجھے تمہاری نسبت ایک بڑے سخت دن (قیامت) کے عذاب کا اندیشہ ہے۔'),
(1014, 7, 60, 'ان کی قوم کے سرداروں نے کہا۔ ہم تو تم کو کھلی ہوئی گمراہی میں دیکھتے ہیں۔'),
(1015, 7, 61, 'نوح نے کہا اے میری قوم! مجھ میں کوئی گمراہی نہیں ہے بلکہ میں تو سارے جہانوں کے پروردگار کا رسول ہوں۔'),
(1016, 7, 62, 'تمہیں اپنے پروردگار کے پیغامات پہنچاتا ہوں (اور تمہاری خیر خواہی کے لیے) تمہیں نصیحت کرتا ہوں۔ اور اللہ کی طرف سے وہ کچھ جانتا ہوں جو تم نہیں جانتے۔'),
(1017, 7, 63, 'کیا تمہیں اس بات پر تعجب ہے کہ تمہارے پروردگار کی طرف سے تمہاری اپنی قوم کے ایک شخص کے پاس وعظ و نصیحت کا پیغام آیا ہے۔ تاکہ وہ تمہیں (خدا کے عذاب سے) ڈرائے! اور تاکہ تم پرہیزگار بنو۔ اور تاکہ تم پر رحم کیا جائے۔'),
(1018, 7, 64, '(مگر) ان لوگوں نے انہیں جھٹلایا۔ (جس کا نتیجہ یہ نکلا کہ) ہم نے انہیں اور جو کشتی میں ان کے ساتھ تھے ان کو نجات دی اور جنہوں نے ہماری آیتوں کو جھٹلایا تھا ان کو غرق کر دیا بے شک وہ اندھے (بے بصیرت) لوگ تھے۔'),
(1019, 7, 65, 'اور ہم نے قومِ عاد کی طرف ان کے (قومی) بھائی ہود کو بھیجا۔ انہوں نے کہا! اے میری قوم! اللہ کی عبادت کرو۔ اس کے سوا تمہارا کوئی الٰہ نہیں ہے۔ آخر تم پرہیزگاری کیوں اختیار نہیں کرتے؟'),
(1020, 7, 66, 'ان کی قوم کے کافر سرداروں نے کہا ہم تمہیں حماقت میں مبتلا دیکھتے ہیں اور ہم تمہیں جھوٹا خیال کرتے ہیں۔'),
(1021, 7, 67, 'ہود نے کہا! اے میری قوم، مجھ میں کوئی حماقت نہیں ہے بلکہ میں تو سارے جہانوں کے پروردگار کا رسول ہوں۔'),
(1022, 7, 68, 'تمہارے پروردگار کے پیغامات پہنچاتا ہوں۔ اور تمہارا امانتدار ناصح ہوں۔'),
(1023, 7, 69, 'کیا تمہیں اس بات پر تعجب ہے کہ تمہارے پروردگار کی طرف سے تمہاری اپنی قوم کے ایک شخص کے پاس وعظ و نصیحت کا پیغام آیا ہے تاکہ وہ تمہیں (خدا کے عذاب سے) ڈرائے اور یاد کرو (خدا کے اس احسان کو) کہ اس نے تمہیں قومِ نوح کے بعد ان کا جانشین بنایا۔ اور (تمہاری) خلقت میں یعنی قوت و طاقت اور قد و قامت میں زیادتی اور وسعت عطا کی پس اللہ کی نعمتوں کو یاد کرو تاکہ تم (ہر طرح) فلاح پاؤ۔'),
(1024, 7, 70, 'انہوں نے کہا (اے ہود (ع)) کیا تم ہمارے پاس اس لئے آئے ہو کہ ہم ایک اللہ کی عبادت کریں اور ان (معبودوں) کو چھوڑ دیں جن کی ہمارے باپ دادا عبادت کرتے چلے آئے ہیں اگر تم سچے ہو تو۔ ہمارے پاس لاؤ وہ (عذاب) جس کی تم ہمیں دھمکی دیتے ہو۔'),
(1025, 7, 71, 'اس (ہود) نے کہا (سمجھ لو کہ) تمہارے پروردگار کی طرف سے تم پر عذاب اور غضب نازل ہوگیا ہے کیا تم مجھ سے ایسے ناموں کے بارے میں جھگڑتے ہو جو تم نے اور تمہارے باپ دادا نے (از خود) رکھ لئے ہیں۔ جن کے متعلق اللہ نے کوئی سند نازل نہیں کی۔ اچھا تو پھر تم (خدا کے عذاب کا) انتظار کرو مَیں بھی تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔'),
(1026, 7, 72, 'پس (آخرکار) ہم نے اپنی رحمت سے انہیں اور ان کے ساتھیوں کو نجات دی (بچا لیا) اور جن لوگوں نے ہماری آیتوں کو جھٹلایا تھا اور وہ ایمان لانے والے نہیں تھے ان کی جڑ ہی کاٹ دی۔'),
(1027, 7, 73, 'اور قومِ ثمود کی طرف ان کے (قومی) بھائی صالح کو بھیجا۔ انہوں نے کہا اے میری قوم! اللہ ہی کی عبادت کرو اس کے سوا تمہارے پروردگار کی طرف سے (میری صداقت کا) ایک خاص معجزہ آچکا ہے جو یہ اللہ کی خاص اونٹنی ہے جو تمہارے لئے ایک (قدرتی) نشانی (معجزہ) ہے اسے کھلا چھوڑ دو کہ خدا کی زمین میں چَرتی پھرے۔ اور (خبردار) اسے برے ارادے سے ہاتھ نہ لگانا (اسے تکلیف نہ پہنچانا) ورنہ دردناک عذاب تمہیں اپنی گرفت میں لے لے گا۔'),
(1028, 7, 74, 'اور وہ وقت یاد کرو جب اللہ نے تمہیں قومِ عاد کا جانشین بنایا۔ اور تمہیں زمین میں رہنے کے لئے ٹھکانا دیا کہ تم نرم اور ہموار زمین میں محلات بناتے ہو اور پہاڑوں کو تراش کر مکانات بناتے ہو۔ سو اللہ کی نعمتوں کو یاد کرو۔ اور زمین میں فساد مت پھیلاتے پھرو۔'),
(1029, 7, 75, 'قوم کے بڑے لوگوں نے جو متکبر تھے۔ ان کمزور لوگوں سے کہا جو ایمان لائے تھے۔ کیا تم جانتے ہو صالح واقعی اپنے پروردگار کی طرف سے بھیجے ہوئے (رسول) ہیں انہوں نے کہا بے شک ہم تو اس (پیغام) پر ایمان لائے جسے دے کر ان کو بھیجا گیا ہے۔'),
(1030, 7, 76, '(یہ سن کر) متکبر مزاج بڑے کہنے لگے کہ جس چیز پر تم ایمان لائے ہو ہم اس کے کافر و منکر ہیں۔'),
(1031, 7, 77, 'پھر انہوں نے اس اونٹنی کو پئے کر دیا (اس کی کونچیں کاٹ دیں) اور اپنے پروردگار کے حکم سے سرکشی و سرتابی کی اور کہا اے صالح! اگر تم رسولوں میں سے ہو تو پھر ہمارے پاس لاؤ وہ (عذاب) جس کی ہمیں دھمکی دیتے ہو۔'),
(1032, 7, 78, 'پس انہیں زلزلہ نے آپکڑا اور وہ اپنے گھروں میں اوندھے پڑے رہ گئے۔'),
(1033, 7, 79, 'پس وہ (صالح) یہ کہتے ہوئے وہاں سے منہ موڑ کر نکل گئے اے میری قوم! میں نے تمہیں اپنے پروردگار کا پیغام پہنچا دیا اور تم کو نصیحت کی مگر تم نصیحت کرنے والے خیر خواہوں کو پسند ہی نہیں کرتے۔'),
(1034, 7, 80, 'اور ہم نے لوط کو بھیجا انہوں نے اپنی قوم سے کہا کہ تم ایسا بے حیائی کا کام کرتے ہو کہ ایسا کام تم سے پہلے دنیا والوں میں سے کسی نے نہیں کیا۔'),
(1035, 7, 81, 'یعنی تم عورتوں کو چھوڑ کر مردوں کے ساتھ شہوت رانی کرتے ہو؟ تم بڑے حد سے گزرے ہوئے لوگ ہو۔'),
(1036, 7, 82, 'ان کی قوم کا اس کے سوا کوئی جواب نہ تھا کہ آپس میں کہنے لگے کہ انہیں اس بستی سے نکال دو۔ یہ لوگ بڑے پاکباز بنتے ہیں۔'),
(1037, 7, 83, 'تو ہم نے انہیں اور ان کے گھر والوں کو سوا ان کی بیوی کے نجات دی (بچا لیا) ہاں البتہ وہ پیچھے رہ جانے والوں میں سے تھی۔'),
(1038, 7, 84, 'اور ہم نے ان پر (پتھروں) کی بارش برسائی تو دیکھو مجرموں کا کیسا انجام ہوا؟'),
(1039, 7, 85, 'اور ہم نے مدین کی طرف ان کے (قومی) بھائی شعیب کو بھیجا۔ انہوں نے کہا اے میری قوم! اللہ ہی کی عبادت کرو۔ اس کے سوا تمہارا کوئی الٰہ نہیں ہے۔ یقینا تمہارے پاس تمہارے پروردگار کی طرف سے کھلا نشان بھی آچکا ہے۔ پس ناپ تول پوری کیا کرو اور لوگوں کو ان کی (خرید کردہ) چیزیں کم نہ دیا کرو۔ اور زمین میں اس کی اصلاح کے بعد فساد نہ پھیلاؤ۔ یہ بات تمہارے لئے بہتر ہے اگر تم ایمان والے ہو۔'),
(1040, 7, 86, 'اور ہر راستے پر اس طرح نہ بیٹھو کہ تم (راہ گیروں) کو ڈراؤ دھمکاؤ اور ایمان لانے والوں کو اللہ کی راہ سے روکو۔ اور اس راہ میں کجی تلاش کرو۔ اور وہ وقت یاد کرو۔ جب تم تھوڑے تھے تو اس (خدا) نے تمہیں بڑھا دیا اور دیکھو کہ فساد پھیلانے والوں کا انجام کیسا ہوتا رہا ہے۔'),
(1041, 7, 87, 'اور اگر تم میں سے ایک گروہ اس پر ایمان نہیں لایا تو صبر کرو۔ یہاں تک کہ اللہ ہمارے درمیان فیصلہ کرے اور وہ بہترین فیصلہ کرنے والا ہے۔'),
(1042, 7, 88, 'اور ان کی قوم کے متکبر سرداروں نے کہا۔ اے شعیب ہم تم کو اور جو لوگ تمہارے ساتھ ایمان لائے ہیں ان کو اپنی بستی سے نکال دیں گے یا یہ کہ تم لوگ ہمارے مذہب میں واپس آجاؤ۔ شعیب نے کہا اگرچہ ہم اسے ناپسند ہی کرتے ہوں؟'),
(1043, 7, 89, 'جب اللہ نے ہمیں تمہارے مذہب سے نجات دے دی ہے۔ اگر اس کے بعد بھی ہم تمہارے مذہب میں واپس آجائیں تو پھر تو ہم نے اللہ پر جھوٹا بہتان باندھا۔ اور ہمارے لئے یہ ممکن نہیں ہے کہ ہم لوٹ آئیں مگر یہ کہ اللہ جو ہمارا پروردگار ہے چاہے ہمارا پروردگار اپنے علم سے ہر چیز کا احاطہ کئے ہوئے ہے۔ ہمارا اللہ ہی پر بھروسہ ہے۔ اے ہمارے پروردگار! تو ہی ہمارے اور ہماری قوم کے درمیان حق کے ساتھ فیصلہ کر دے۔ اور تو بہترین فیصلہ کرنے والا ہے۔'),
(1044, 7, 90, 'اور ان کی قوم کے ان سرداروں نے جو کافر تھے آپس میں کہا کہ اگر تم شعیب کی پیروی کروگے تو نقصان اٹھاؤگے۔'),
(1045, 7, 91, 'سو زلزلہ نے انہیں آپکڑا جس کے بعد وہ اپنے گھروں میں اوندھے منہ پڑے رہ گئے۔'),
(1046, 7, 92, 'جن لوگوں نے شعیب کو جھٹلایا تھا وہ (اس طرح برباد ہوئے کہ) گویا کبھی ان گھروں میں بستے ہی نہ تھے۔ جنہوں نے شعیب کو جھٹلایا تھا وہی نقصان اٹھانے والے ثابت ہوئے۔'),
(1047, 7, 93, '(اس وقت) وہ ان سے منہ موڑ کر چلے اور کہا اے میری قوم! میں نے تو یقیناً اپنے پروردگار کے پیغامات تمہیں پہنچا دیئے تھے اور تمہیں نصیحت بھی کی تھی تو (اب) کافر قوم (کے عبرتناک انجام) پر کیونکر افسوس کروں؟'),
(1048, 7, 94, 'اور ہم نے کبھی کسی بستی میں کوئی نبی نہیں بھیجا مگر یہ کہ اس کے باشندوں کو (ان کی تکذیب پر پہلے) سختی اور تکلیف میں مبتلا کیا تاکہ وہ تضرع و زاری کریں۔'),
(1049, 7, 95, 'پھر ہم نے ان کی بدحالی کو خوش حالی سے بدل دیا یہاں تک کہ وہ خوب بڑھے (اور پھلے پھولے) اور کہنے لگے کہ ہمارے باپ دادا کو بھی کبھی تکلیف اور کبھی راحت یونہی پہنچتی رہی ہے۔ تو ایک دم ہم نے ان کو اس طرح پکڑ لیا کہ انہیں اس کا شعور بھی نہ ہو سکا۔'),
(1050, 7, 96, 'اور اگر ان بستیوں کے باشندے ایمان لاتے اور پرہیزگاری اختیار کرتے تو ہم ان پر آسمان و زمین کی برکتوں کے دروازے کھول دیتے۔ لیکن انہوں نے جھٹلایا۔ لہٰذا ہم نے ان کو ان کے کرتوتوں کی پاداش میں پکڑ لیا۔'),
(1051, 7, 97, 'آیا ان بستیوں والے اس سے بے خوف ہوگئے ہیں کہ ان پر ہمارا عذاب راتوں رات آجائے جبکہ وہ سو رہے ہوں؟'),
(1052, 7, 98, 'یا کیا بستیوں والے اس سے بے خوف ہوگئے ہیں کہ ان پر ہمارا عذاب دن چڑھے (چاشت کے وقت) آجائے جبکہ وہ کھیل کود رہے ہوں؟'),
(1053, 7, 99, 'کیا یہ لوگ اللہ کے عذاب یا اس کی خفیہ تدبیر سے بے خوف ہوگئے ہیں۔ حالانکہ اللہ کے عذاب یا اس کی خفیہ تدبیر سے وہی لوگ بے خوف ہوتے ہیں جو گھاٹا اٹھانے والے ہوں۔'),
(1054, 7, 100, 'کیا ان لوگوں پر جو پہلے اہل زمین کی تباہی کے بعد زمین کے وارث بنے ہیں یہ بات واضح نہیں ہوئی ہے کہ اگر ہم چاہتے تو ان کو گناہوں کی وجہ سے کسی مصیبت میں مبتلا کر دیتے اور ان کے دلوں پر ایسی مہر لگا دیتے کہ وہ سن (سمجھ) نہ سکیں۔'),
(1055, 7, 101, 'یہ وہ بستیاں ہیں کہ جن کی کچھ خبریں ہم آپ سے بیان کر رہے ہیں بے شک ان کے پاس ان کے رسول کھلے ہوئے نشان (معجزے) لے کر آئے۔ مگر وہ ایسے نہیں تھے کہ اس بات پر ایمان لاتے جسے وہ پہلے جھٹلا چکے تھے۔ اللہ اسی طرح کافروں کے دلوں پر مہر لگاتا ہے۔'),
(1056, 7, 102, 'اور ہم نے ان کی اکثریت میں عہد و پیمان کی پاسداری نہیں پائی اور ہم نے اکثر کو فاسق و فاجر پایا ہے۔'),
(1057, 7, 103, 'پھر ہم نے ان (نبیوں) کے بعد موسیٰ کو اپنی نشانیاں (معجزے) دے کر فرعون اور اس کے سرداروں کی طرف بھیجا۔ مگر انہوں نے ان (نشانیوں) کے ساتھ ظلم کیا (انکار کر دیا) دیکھو مفسدین کا کیا برا انجام ہوا؟'),
(1058, 7, 104, 'اور موسیٰ نے کہا اے فرعون میں تمام جہانوں کے پروردگار کا رسول ہوں۔'),
(1059, 7, 105, 'میرے لئے ضروری ہے کہ اللہ کے بارے میں حق کے سوا کوئی بات نہ کہوں میں تمہارے پروردگار کی طرف سے معجزہ لے کر آیا ہوں۔ پس تو بنی اسرائیل کو (آزاد کرکے) میرے ساتھ بھیج دے۔'),
(1060, 7, 106, 'فرعون نے کہا اگر تم کوئی معجزہ لائے ہو تو اگر تم سچے ہو تو اسے پیش کرو۔'),
(1061, 7, 107, 'پس انہوں نے اپنا عصا پھینک دیا اور وہ یکایک کھلا ہوا اژدھا بن گیا۔'),
(1062, 7, 108, 'اور اپنا ہاتھ باہر نکالا۔ تو وہ ایک دم دیکھنے والوں کے لیے سفید اور روشن تھا۔'),
(1063, 7, 109, 'قومِ فرعون کے سرداروں نے (یہ منظر دیکھ کر) کہا کہ یہ شخص (موسیٰ) بڑا ماہر جادوگر ہے۔'),
(1064, 7, 110, '(اس پر فرعون نے کہا) یہ تمہیں تمہارے ملک سے نکال دینا چاہتا ہے تو تم کیا مشورہ دیتے ہو؟'),
(1065, 7, 111, 'ان لوگوں نے کہا کہ انہیں اور ان کے بھائی کو روک رکھیے۔ اور شہروں میں ہرکارے بھیج دیجئے۔'),
(1066, 7, 112, 'جو ہر بڑے ماہر جادوگر کو لے آئیں۔'),
(1067, 7, 113, 'چنانچہ جادوگر فرعون کے پاس آگئے اور کہنے لگے کہ اگر ہم غالب آگئے تو یقینا ہم ایک بڑے معاوضے کے حقدار ہوں گے۔'),
(1068, 7, 114, 'فرعون نے کہا ہاں۔ اور اس کے علاوہ تم ہمارے مقرب بارگاہ لوگوں میں سے ہو جاؤگے۔'),
(1069, 7, 115, 'انہوں نے کہا اے موسیٰ یا تم (پہلے) پھینکو یا پھر ہم ہی پھینکتے ہیں۔'),
(1070, 7, 116, 'انہوں نے (جواب میں) کہا تم ہی پھینکو! پھر جب انہوں نے (اپنی لاٹھیاں اور رسیاں) پھینکیں تو لوگوں کی نگاہوں کو مسحور کر دیا (ان کی نظر بندی کر دی) اور انہیں خوف زدہ کر دیا اور انہوں نے بڑا زبردست جادو پیش کیا۔'),
(1071, 7, 117, 'ہم نے موسیٰ کو وحی کی کہ اپنا عصا پھینکو۔ تو یکایک وہ (اژدھا بن کر) ان کے طلسموں (جادو کے جھوٹے سانپوں) کو نگلنے لگا۔'),
(1072, 7, 118, '(نتیجہ یہ نکلا کہ) حق ثابت ہوگیا۔ اور جو جھوٹے کاروائی کر رہے تھے وہ باطل ہوگئی۔'),
(1073, 7, 119, 'اس طرح وہ (فرعون اور فرعونی) مغلوب ہوئے اور ذلیل و رسوا ہوکر رہ گئے۔'),
(1074, 7, 120, 'اور تمام جادوگر (کسی غیبی طاقت سے) سجدہ میں گرا دیئے گئے۔'),
(1075, 7, 121, 'اور کہنے لگے ہم سارے جہانوں کے پروردگار پر ایمان لائے۔'),
(1076, 7, 122, 'جو موسیٰ و ہارون کا پروردگار ہے۔'),
(1077, 7, 123, 'فرعون نے کہا تم اس پر ایمان لے آئے ہو اس سے پہلے کہ میں تمہیں اجازت دوں؟ یقینا یہ کوئی خفیہ سازش ہے جو تم لوگوں نے کی ہے۔ تاکہ تم شہر کے باشندوں کو اس سے باہر نکالو۔ عنقریب تمہیں (اس کا انجام) معلوم ہو جائے گا۔'),
(1078, 7, 124, 'میں تمہارے ہاتھ پاؤں کو مختلف سمتوں سے کاٹ دوں گا پھر تم سب کو سولی پر لٹکا دوں گا۔'),
(1079, 7, 125, 'انہوں نے کہا (ہمیں کیا پروا ہے) ہم لوگ بہرحال اپنے پروردگار کی طرف پلٹ کر جانے والے ہیں۔'),
(1080, 7, 126, 'اور تم ہم سے صرف اس بات کا انتقام لینا چاہتے ہو کہ ہم اپنے پروردگار کی نشانیوں پر ایمان لائے ہیں جب وہ ہمارے پاس آئی ہیں۔ (پھر یوں دعا کی) اے ہمارے پروردگار! ہم پر صبر انڈیل دے اور ہمیں اس حالت میں وفات دے کہ ہم تیرے سچے مسلمان (فرمانبردار) ہوں۔'),
(1081, 7, 127, 'قومِ فرعون کے سرداروں نے کہا! تو موسیٰ اور اس کی قوم کے آدمیوں کو اس لئے چھوڑے رکھے گا کہ وہ ملک میں فساد پھیلاتے رہیں اور تمہیں اور تمہارے معبودوں کو چھوڑتے رہیں۔ اس (فرعون) نے (چین بجبین ہوکر) کہا ہم عنقریب ان کے لڑکوں کو قتل کریں گے اور ان کی عورتوں کو زندہ رہنے دیں گے اور ہم (پوری طرح) ان پر غالب ہیں۔'),
(1082, 7, 128, 'موسیٰ نے اپنی قوم سے کہا! تم اللہ سے مدد طلب کرو۔ اور صبر و تحمل سے کام لو بے شک زمین اللہ کی ہے۔ وہ جسے چاہتا ہے اپنے بندوں میں سے اس کا وارث بنا دیتا ہے اور اچھا انجام تو پرہیزگاروں کا ہی ہے۔'),
(1083, 7, 129, 'ان لوگوں (بنی اسرائیل) نے کہا ہم آپ کے آنے سے پہلے بھی ستائے جاتے رہے اور آپ کے آنے کے بعد بھی۔ انہوں نے کہا! عنقریب تمہارا پروردگار تمہارے دشمن کو ہلاک کر دے گا اور تمہیں زمین میں (ان کا) جانشین بنائے گا (تمہیں اقتدار عطا فرمائے گا) تاکہ دیکھے کہ تم کیسے عمل کرتے ہو؟'),
(1084, 7, 130, 'اور ہم نے فرعونیوں کو قحط اور پھلوں کی پیداوار کی کمی میں گرفتار کیا کہ شاید وہ نصیحت حاصل کریں۔'),
(1085, 7, 131, '(مگر ان کی حالت یہ تھی کہ) جب خوش حالی آتی تو کہتے کہ یہ تو ہمارا حق ہے اور جب بدحالی آتی تو اسے موسیٰ اور ان کے ساتھیوں کی نحوست اور فالِ بد قرار دیتے۔ حالانکہ ان کی نحوست اور بدشگونی خدا کے ہاں ہے لیکن اکثر لوگ (یہ حقیقت) جانتے نہیں ہیں۔'),
(1086, 7, 132, 'اور انہوں نے (موسیٰ سے) کہا تم جو بھی نشانی (معجزہ) ہمارے سامنے لے آؤ اور اس سے ہمیں مسحور کرنا چاہو ہم پھر بھی ایمان نہیں لائیں گے۔'),
(1087, 7, 133, 'پھر ہم نے بھیجا ان پر عذاب، طوفان، ٹڈی دل، جوؤں، مینڈکوں اور خون (کی صورت میں) یہ کھلی ہوئی نشانیاں تھیں مگر وہ پھر بھی تکبر اور سرکشی ہی کرتے رہے اور وہ بڑے ہی مجرم لوگ تھے۔'),
(1088, 7, 134, 'اور جب کبھی ان پر کوئی عذاب نازل ہوتا تو کہتے اے موسیٰ ہمارے لئے اپنے پروردگار سے دعا کرو۔ جس کا اس نے تم سے وعدہ کر رکھا ہے۔ اگر تم نے یہ عذاب ہم سے ٹلوا دیا۔ تو ہم تم پر ایمان لے آئیں گے اور تمہارے ساتھ بنی اسرائیل کو بھیج دیں گے۔'),
(1089, 7, 135, 'تو جب ہم وہ عذاب ایک مقررہ مدت تک ہٹا دیتے جس تک وہ بہرحال پہنچنے والے تھے۔ تو وہ اپنے کئے ہوئے عہد و پیمان کو توڑ دیتے۔'),
(1090, 7, 136, 'تب ہم نے ان سے اس طرح انتقام لیا کہ انہیں سمندر میں غرق کر دیا۔ کیونکہ وہ ہماری نشانیوں کو جھٹلاتے تھے اور ان کی طرف سے غفلت برتتے تھے۔'),
(1091, 7, 137, 'اور ان کی جگہ ہم نے ان لوگوں کو وارث (اور مالک) بنایا جن کو کمزور سمجھا جاتا تھا۔ اور وارث بھی اس مشرق و مغرب کا بنایا جس میں ہم نے برکت دی ہے اور اسی طرح آپ کے پروردگار کا وہ اچھا وعدہ پورا ہوگیا جو اس نے بنی اسرائیل سے کیا تھا کیونکہ انہوں نے صبر و ضبط سے کام لیا تھا اور ہم نے اسے مٹا دیا جو کچھ فرعون اور اس کی قوم والے کرتے تھے اور برباد کر دیئے وہ اونچے مکان جو وہ تعمیر کرتے تھے۔'),
(1092, 7, 138, 'اور جب ہم نے بنی اسرائیل کو دریا کے اس پار اتار دیا تو ان کا گزر ایک ایسی قوم سے ہوا جو اپنے (خود ساختہ) معبودوں کی پرستش میں مگن بیٹھی تھی۔ انہوں نے کہا اے موسیٰ ہمارے لئے بھی ایک الٰہ بنا دیں جیسے ان کے الٰہ ہیں۔ موسیٰ نے کہا تم بڑے ہی جاہل لوگ ہو۔'),
(1093, 7, 139, 'جس طریقہ پر یہ لوگ ہیں وہ یقینا تباہ ہو کر رہے گا۔ اور جو کچھ وہ کر رہے ہیں وہ باطل ہوکر رہے گا۔'),
(1094, 7, 140, 'کہا: کیا میں تمہارے لئے اللہ کے سوا کوئی (اور) خدا تلاش کروں حالانکہ اس نے تمہیں تمام جہانوں کی قوموں پر فضیلت دی۔'),
(1095, 7, 141, 'اور یاد کرو وہ وقت جب ہم نے تمہیں فرعونیوں سے نجات عطا کی۔ جو تمہیں بدترین عذاب کا مزہ چکھاتے تھے تمہارے لڑکوں کو قتل کرتے تھے اور تمہاری عورتوں کو زندہ چھوڑتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑی آزمائش تھی۔'),
(1096, 7, 142, 'اور ہم نے موسیٰ سے (توراۃ دینے کے لیے) تیس راتوں کا وعدہ کیا تھا اور اسے مزید دس (راتوں) سے مکمل کیا۔ اس طرح ان کے پروردگار کی مدت چالیس راتوں میں پوری ہوگئی اور جناب موسیٰ نے (کوہ طور پر جاتے وقت) اپنے بھائی ہارون سے کہا تم میری قوم میں میرے جانشین بن کر رہو۔ اور اصلاح کرتے رہو اور (خبردار) مفسدین کے راستہ پر نہ چلنا۔'),
(1097, 7, 143, 'اور جب جنابِ موسیٰ ہمارے مقرر کردہ وقت پر آگئے اور ان کے پروردگار نے ان سے کلام کیا۔ تو انہوں نے کہا پروردگار! مجھے اپنا جلوہ دکھا تاکہ میں تیری طرف نگاہ کر سکوں۔ ارشاد ہوا تم مجھے کبھی نہیں دیکھ سکوگے۔ مگر ہاں اس پہاڑ کی طرف دیکھو۔ اگر یہ (تجلئِ حق کے وقت) اپنی جگہ پر برقرار رہا تو پھر امید کرنا کہ مجھے دیکھ سکوگے پس جب ان کے پروردگار نے پہاڑ پر اپنی تجلی ڈالی تو اسے ریزہ ریزہ کر دیا اور موسیٰ غش کھا کر گر گئے۔ اور جب ہوش میں آئے تو بولے پاک ہے تیری ذات! میں تیرے حضور توبہ کرتا ہوں اور میں سب سے پہلا ایمان لانے والا ہوں (کہ تو نظر نہیں آتا)۔'),
(1098, 7, 144, 'ارشاد ہوا اے موسیٰ میں نے تمہیں اپنی پیغمبری اور ہمکلامی کے لیے تمام لوگوں سے منتخب کیا ہے پس جو چیز (توراۃ) میں نے تمہیں عطا کی ہے اسے لو اور شکر گزار بندوں میں سے ہو جاؤ۔'),
(1099, 7, 145, 'اور ہم نے موسیٰ کے لیے ان تختیوں میں ہر قسم کی نصیحت اور ہر چیز کی تفصیل لکھ دی ہے۔ پس اسے مضبوطی سے پکڑ لو اور اپنی قوم کو حکم دو کہ وہ اس کی بہترین باتوں کو لے لیں (اس کے احکام پر کاربند ہو جائیں) میں عنقریب تم لوگوں کو نافرمان لوگوں کا گھر دکھا دوں گا۔'),
(1100, 7, 146, 'جو لوگ ناحق خدا کی زمین میں سرکشی کرتے ہیں میں اپنی نشانیوں سے ان کی نگاہیں پھرا دوں گا۔ وہ اگر ہر معجزہ بھی دیکھ لیں۔ تو پھر بھی ایمان نہیں لائیں گے۔ اور اگر وہ ہدایت کا راستہ دیکھ بھی لیں تو اسے اپنا راستہ نہیں بنائیں گے۔ اور اگر گمراہی کا راستہ دیکھ لیں تو (فوراً) اسے (اپنا) راستہ بنا لیں گے ان کی یہ حالت اس وجہ سے ہے کہ انہوں نے ہماری آیتوں (نشانیوں) کو جھٹلایا اور ان سے غفلت برتتے رہے۔'),
(1101, 7, 147, 'اور جن لوگوں نے ہماری آیتوں کو اور آخرت کی پیشی کو جھٹلایا ان کے سارے اعمال ضائع ہوگئے پس انہیں جزا یا سزا نہیں دی جائے گی مگر انہی اعمال کی جو وہ (دار دنیا میں) کرتے تھے۔'),
(1102, 7, 148, 'اور موسیٰ کی قوم نے ان کے (کوہِ طور پر چلے جانے کے) بعد اپنے زیوروں سے (ان کو گلا کر) ایک بچھڑے کا پتلا بنا لیا جس سے بیل کی سی آواز نکلتی تھی۔ کیا انہوں نے اتنا بھی نہ دیکھا (نہ سوچا) کہ وہ نہ ان سے بات کرتا ہے اور نہ ہی کسی طرح کی راہنمائی کر سکتا ہے پھر بھی انہوں نے اسے (معبود) بنا لیا اور وہ (اپنے اوپر) سخت ظلم کرنے والے تھے۔'),
(1103, 7, 149, 'پھر جب وہ نادم ہوئے اور کفِ افسوس مَلنے لگے اور محسوس کیا کہ وہ گمراہ ہوگئے ہیں۔ تو کہنے لگے کہ ہمارے پروردگار نے ہم پر رحم نہ کیا اور ہمیں نہ بخشا تو ہم نقصان اٹھانے والوں میں سے ہو جائیں گے۔'),
(1104, 7, 150, 'اور جب موسیٰ خشمناک اور افسوس کرتے ہوئے اپنی قوم کے پاس واپس آئے۔ تو فرمایا: اے قوم! تم نے میرے بعد بہت ہی بری جانشینی کی۔ کیا تم نے اپنے پروردگار کا حکم آنے سے پہلے جلد بازی کر لی (وعدہ خداوندی چالیس راتوں کے پورا ہونے کی انتظار بھی نہ کی) اور پھر (للہی جوش میں آخر تورات کی) تختیاں (ہاتھ سے) پھینک دیں اور اپنے بھائی (ہارون) کا سر ہاتھ میں لے کر اپنی طرف کھینچنے لگے۔ انہوں نے کہا اے میرے ماں جائے! قوم نے مجھے کمزور اور بے بس کر دیا تھا اور قریب تھا کہ مجھے قتل کر ڈالیں پس تم مجھ پر دشمنوں کو ہنسنے کا موقع نہ دو۔ اور نہ ہی مجھے ظالم گروہ کے ساتھ شامل کرو۔'),
(1105, 7, 151, 'تب موسیٰ نے کہا۔ پروردگار! مجھے اور میرے بھائی کو بخش دے اور ہمیں اپنی رحمت میں داخل فرما اور تو سب رحم کرنے والوں میں سے بڑا رحم کرنے والا ہے۔'),
(1106, 7, 152, 'بے شک جن لوگوں نے گو سالہ کو (اپنا معبود) بنایا۔ عنقریب ان پر ان کے پروردگار کا غضب نازل ہوگا۔ اور وہ دنیاوی زندگی میں ذلیل و رسوا ہوں گے اور ہم بہتان باندھنے والوں کو اسی طرح سزا دیا کرتے ہیں۔'),
(1107, 7, 153, 'اور جو لوگ برے کام کریں اور پھر توبہ کر لیں۔ اور ایمان لے آئیں تو یقینا تمہارا پروردگار اس (توبہ و ایمان) کے بعد بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1108, 7, 154, 'اور جب موسیٰ کا غصہ فرو ہوا۔ تو انہوں نے تختیاں اٹھا لیں تو ان کی کتابت (تحریر) میں اپنے پروردگار سے ڈرنے والوں کے لیے ہدایت اور رحمت تھی۔'),
(1109, 7, 155, 'ہمارے مقرر کردہ وقت پر حاضر ہونے کے لیے موسیٰ نے اپنی قوم کے ستر آدمیوں کو منتخب کیا پھر جب ایک سخت زلزلہ نے انہیں آپکڑا (اور وہ ہلاک ہوگئے) تو موسیٰ نے کہا اے میرے پروردگار اگر تو چاہتا تو ان سب کو پہلے ہی ہلاک کر دیتا اور مجھے بھی۔ کیا تو ایسی بات کی وجہ سے جو ہمارے چند احمقوں نے کی ہے ہم سب کو ہلاک کرتا ہے؟ یہ نہیں ہے مگر تیری طرف سے ایک آزمائش! اس کی وجہ سے تو جسے چاہتا ہے (توفیق سلب کرکے) گمراہی میں چھوڑ دیتا ہے اور جسے چاہتا ہے ہدایت کرتا ہے تو ہی ہمارا ولی و سرپرست ہے۔ ہمیں بخش دے اور ہم پر رحم فرما اور تو بہترین مغفرت کرنے والا ہے۔'),
(1110, 7, 156, 'اور ہمارے لئے اس دنیا میں بھی بھلائی لکھ دے اور آخرت میں بھی ہم تیری ہی طرف رجوع کرتے ہیں (ہم نے تجھ سے لو لگائی ہے) ارشاد ہوا (میرے عذاب کا یہ حال ہے کہ) جس کو چاہتا ہوں دیتا ہوں اور میری رحمت (کا یہ حال ہے کہ وہ) ہر چیز کو گھیرے ہوئے ہے۔ پس میں رحمت ان لوگوں کے لیے لکھ دوں گا جو پرہیزگاری کریں گے (برائیوں سے بچیں گے) اور زکوٰۃ ادا کریں گے اور ہماری آیتوں (نشانیوں) پر ایمان لے آئیں گے۔'),
(1111, 7, 157, 'جو اس پیغمبر(ص) کی پیروی کریں گے جو نبیِ امی ہے جسے وہ اپنے ہاں توراۃ و انجیل میں لکھا ہوا پاتے ہیں جو انہیں نیک کاموں کا حکم دیتا ہے اور برے کاموں سے روکتا ہے جو ان کے لیے پاک و پسندیدہ چیزوں کو حلال اور گندی و ناپاک چیزوں کو حرام قرار دیتا ہے اور ان پر سے ان کے بوجھ اتارتا ہے اور وہ زنجیریں کھولتا ہے جن میں وہ جکڑے ہوئے تھے۔ پس جو لوگ اس (نبیِ امی) پر ایمان لائے اور اس کی تعظیم کی اور اس کی مدد و نصرت کی اور اس نور (روشنی) کی پیروی کی جو اس کے ساتھ نازل کیا گیا ہے یہی لوگ فوز و فلاح پانے والے ہیں۔'),
(1112, 7, 158, 'اے پیغمبر! (لوگوں سے) کہو اے انسانو! میں تم سب کی طرف اس اللہ کا بھیجا ہوا رسول ہوں۔ جس کے لیے سارے آسمانوں اور زمین کی بادشاہی ہے اس کے سوا کوئی الٰہ نہیں ہے وہی زندہ کرتا ہے وہی مارتا ہے پس اللہ پر ایمان لاؤ۔ اور اس کے نبیِ امی رسول پر۔ جو خود بھی اللہ اور اس کے کلمات (کتابوں) پر ایمان رکھتا ہے اور اس کی پیروی کرو تاکہ تم ہدایت پاؤ۔'),
(1113, 7, 159, 'اور موسیٰ کی قوم میں سے ایک گروہ ایسا بھی ہے جو لوگوں کو حق کے راستہ پر چلاتا ہے اور حق کے ساتھ منصفانہ فیصلہ کرتا ہے۔'),
(1114, 7, 160, 'اور ہم نے ان (بنی اسرائیل) کو بارہ خاندانوں کے بارہ گروہوں میں تقسیم کر دیا اور ہم نے موسیٰ کو وحی کی جب ان کی قوم نے ان سے پینے کے لیے پانی مانگا کہ ایک (خاص) چٹان پر اپنی لاٹھی مارو۔ چنانچہ اس سے بارہ چشمے پھوٹ نکلے۔ اور ہر گروہ نے اپنا اپنا گھاٹ معلوم کر لیا اور ہم نے ان پر ابر کا سایہ کیا اور (غذا کے لیے) ان پر من و سلویٰ نازل کیا (اور ان سے کہا) کھاؤ ان پاک اور پسندیدہ چیزوں میں سے جو ہم نے تمہیں عطا کی ہیں۔ انہوں نے (نافرمانی و ناشکری کرکے) ہم پر ظلم نہیں کیا بلکہ اپنے ہی اوپر ظلم و زیادتی کرتے رہے۔'),
(1115, 7, 161, '(وہ وقت یاد کرو) جب ان (بنی اسرائیل) سے کہا گیا کہ اس گاؤں میں جاکر آباد ہو جاؤ اور وہاں جہاں سے تمہارا جی چاہے (غذا حاصل کرکے) کھاؤ اور حطۃ حطۃ کہتے ہوئے سجدہ ریز ہوکر (عاجزی سے جھکے ہوئے) شہر کے دروازہ میں داخل ہو جاؤ۔ تو ہم تمہاری خطائیں معاف کر دیں گے۔ اور جو نیک کردار ہیں ان کو مزید اجر سے نوازیں گے۔'),
(1116, 7, 162, 'مگر ان میں سے جو ظالم تھے انہوں نے وہ کلمہ بدل دیا ایسے کلمہ سے جو مختلف تھا اس کلمہ سے جو ان سے کہا گیا تھا (اور حطۃ کی جگہ حنطہ حنطہ کہنا شروع کیا، ان کی اس روش کا نتیجہ یہ نکلا کہ) ہم نے آسمان سے ان پر عذاب بھیجا۔ اس ظلم کی وجہ سے جو وہ کیا کرتے تھے۔'),
(1117, 7, 163, 'اور (اے پیغمبر) ان سے اس بستی کا حال پوچھو جو سمندر کے کنارے واقع تھی۔ جبکہ وہ (بنی اسرائیل) ہفتہ کے دن خدا کی مقرر کردہ حد سے باہر ہو جاتے تھے۔ جبکہ ہفتہ کے دن مچھلیاں ابھر ابھر کر سطح آب پر تیرتی ہوئی آجاتی تھیں اور جس دن ہفتہ نہیں ہوتا ہے (باقی دنوں میں) تو نہیں آتی تھیں۔ ان کی نافرمانی کی وجہ سے جو وہ کیا کرتے تھے اس طرح ہم ان کی آزمائش کرتے تھے۔'),
(1118, 7, 164, 'اور (اس وقت کو یاد کرو) جب ان میں سے ایک گروہ نے کہا (ان لوگوں سے جو ہفتہ کے دن شکار کرنے والوں کو وعظ و نصیحت کرتے تھے) کہ تم ایسے لوگوں کو (بے فائدہ) کیوں نصیحت کرتے ہو۔ جن کو خدا ہلاک کرنے والا ہے یا سخت عذاب میں مبتلا کرنے والا ہے؟ انہوں نے (جواب میں) کہا کہ ہم یہ اس لیے کرتے ہیں کہ تمہارے پروردگار کے سامنے معذرت پیش کر سکیں (کہ ہم نے اپنا فریضۂ امر و نہی ادا کر دیا ہے) اور اس لئے کہ شاید یہ لوگ اس روش سے پرہیز اختیار کریں۔'),
(1119, 7, 165, 'آخرکار جب ان لوگوں نے وہ تمام نصیحتیں بھلا دیں جو ان کو کی گئی تھیں تو ہم نے ان لوگوں کو تو بچا لیا جو برائی سے روکتے رہتے تھے اور باقی سب ظالموں کو ان کی نافرمانیوں کی وجہ سے سخت عذاب میں گرفتار کیا۔'),
(1120, 7, 166, '(جب یہ عذاب بھی ان کو سرکشی سے باز نہ رکھ سکا) اور وہ برابر سرکشی کرتے چلے گئے ان چیزوں کے بارے میں جن سے ان کو روکا گیا تھا تو ہم نے کہا بندر ہو جاؤ ذلیل اور راندے ہوئے۔'),
(1121, 7, 167, 'اور (اے پیغمبر(ص)) یاد کرو جب تمہارے پروردگار نے اعلان کر دیا تھا (کہ اگر یہ لوگ اپنی سیاہ کاریوں سے باز نہ آئے) تو خدا ان پر قیامت تک ایسے لوگ مسلط کرے گا جو انہیں بدترین عذاب میں گرفتار کریں گے یقینا تمہارا پروردگار بہت جلد سزا دینے والا ہے اور بڑا بخشنے والا اور بڑا رحم کرنے والا بھی ہے۔'),
(1122, 7, 168, 'اور ہم نے ان کو زمین میں مختلف فرقوں کی صورت میں ٹکڑے ٹکڑے کر دیا۔ ان میں سے ناپائیدار ہوگا کچھ لوگ تو نیک ہیں اور کچھ اس کے خلاف اور طرح کے (بدکار) ہیں۔ اور ہم ان کو اچھے اور برے حالات میں مبتلا کرکے آزماتے ہیں تاکہ وہ باز آجائیں۔'),
(1123, 7, 169, 'پھر ان کے بعد ایسے ناخلف لوگ ان کے جانشین ہوئے اور کتابِ الٰہی کے وارث بنے جو (دین فروشی کرکے) دنیائے دوں کے مال و متاع اور ساز و سامان جمع کرتے اور سمیٹتے ہیں اور کہتے ہیں کہ امید ہے کہ ہم بخش دیے جائیں گے اور اگر اتنا ہی اور عارضی ساز و سامان ان کے ہاتھ لگ جائے تو اسے بھی بے دریغ لے لیتے ہیں۔ کیا ان سے کتاب (توراۃ) والا یہ عہد و پیمان نہیں لیا گیا تھا کہ وہ اللہ کی نسبت حق کے سوا اور کوئی بات نہیں کہیں گے؟ اور یہ خود کتاب میں پڑھ چکے ہیں جو اس میں لکھا ہے اور آخرت والا گھر پرہیزگاروں کے لیے بہتر ہے کیا تم عقل سے کام نہیں لیتے؟'),
(1124, 7, 170, 'اور جو لوگ کتابِ الٰہی سے تمسک رکھتے ہیں اور نماز کو قائم کرتے ہیں۔ بے شک ہم اصلاح کرنے والوں کا اجر و ثواب ضائع نہیں کریں گے۔'),
(1125, 7, 171, 'اور (وہ وقت یاد کرو) جب ہم نے پہاڑ کو ہلا کر (اور جڑ سے اکھاڑ کر)۔ اس طرح ان کے اوپر بلند کیا۔ کہ گویا سائبان ہے اور انہوں نے گمان کیا کہ یہ ان پر گرا ہی چاہتا ہے ( اور ان سے کہا کہ) جو کچھ (کتاب) ہم نے تمہیں عطا کی ہے اسے مضبوطی کے ساتھ لو۔ اور جو کچھ اس میں لکھا ہے اسے یاد رکھو۔ تاکہ تم پرہیزگار بن جاؤ۔'),
(1126, 7, 172, 'اور (وہ وقت یاد کرو) جب تمہارے پروردگار نے بنی آدم کی پشتوں سے ان کی نسل کو نکالا (جو نسلاً بعد نسل پیدا ہونے والی تھی) اور ان کو خود ان کے اوپر گواہ بناتے ہوئے پوچھا کیا میں تمہارا پروردگار نہیں ہوں؟ سب نے جواب دیا ہاں بے شک ہم گواہی دیتے ہیں کہ تو ہمارا پروردگار ہے (یہ کاروائی اس لئے کی تھی کہ) کہیں تم قیامت کے دن یہ نہ کہہ دو کہ ہم تو اس سے بے خبر تھے۔'),
(1127, 7, 173, 'یا کہو کہ (خدایا) شرک تو ہم سے پہلے ہمارے باپ دادا نے کیا تھا۔ اور ہم تو ان کے بعد ان کی نسل سے تھے (اس لئے لاچار ان کی روش پر چل پڑے) تو کیا تو ہمیں اس (شرک) کی وجہ سے ہلاک کرے گا جو باطل پرستوں نے کیا تھا۔'),
(1128, 7, 174, 'اسی طرح ہم اپنی نشانیاں تفصیل سے بیان کرتے ہیں تاکہ وہ (حق کی طرف) رجوع کریں۔'),
(1129, 7, 175, '(اے رسول(ص)) ان لوگوں کے سامنے اس شخص کا واقعہ بیان کرو جس کو ہم نے اپنی بعض نشانیاں عطا کی تھیں مگر وہ ان سے عاری ہوگیا (وہ جامہ اتار دیا) پس شیطان اس کے پیچھے لگ گیا۔ اور آخرکار وہ گمراہوں میں سے ہوگیا۔'),
(1130, 7, 176, 'اور اگر ہم چاہتے تو ان نشانیوں کی وجہ سے اس کا مرتبہ بلند کرتے۔ مگر وہ تو زمین (پستی) کی طرف جھک گیا۔ اور اپنی خواہشِ نفس کا پیرو ہوگیا۔ تو اب اس کی مثال کتے کی سی ہوگئی کہ اگر تم اس پر حملہ کرو تب بھی ہانپتا ہے اور یونہی چھوڑ دو تب بھی ایسا کرتا ہے۔ (زبان لٹکائے ہانپتا ہے) یہ مثال ان لوگوں کی ہے جنہوں نے ہماری نشانیوں کو جھٹلایا (اے رسول) تم یہ قصص و حکایات سناتے رہو شاید وہ غور و فکر کریں۔'),
(1131, 7, 177, 'کیا بری مثال ہے ان لوگوں کی جنہوں نے ہماری نشانیوں کو جھٹلایا۔ اور جو خود اپنے ہی اوپر ظلم کرتے رہے۔'),
(1132, 7, 178, 'جس کو اللہ ہدایت دے وہی ہدایت یافتہ ہے اور جسے وہ گمراہی میں چھوڑ دے وہی لوگ گھاٹا اٹھانے والے ہیں۔'),
(1133, 7, 179, 'اور کتنے جن و انسان ایسے ہیں جنہیں ہم نے جہنم کے لیے پیدا کیا ہے (یعنی ان کا انجامِ کار جہنم ہے) ان کے دل و دماغ ہیں مگر سوچتے نہیں ہیں۔ ان کی آنکھیں ہیں مگر وہ ان سے دیکھتے نہیں اور ان کے کان ہیں مگر وہ ان سے سنتے نہیں ہیں۔ یہ لوگ چوپایوں کی طرح ہیں۔ بلکہ ان سے بھی زیادہ گمراہ (اور گئے گزرے) ہیں۔ یہی وہ لوگ ہیں جو بالکل غافل و بے خبر ہیں۔'),
(1134, 7, 180, 'اور اللہ ہی کے لیے اچھے اچھے نام ہیں اسے انہی کے ذریعہ سے پکارو اور ان لوگوں کو چھوڑ دو جو اس کے ناموں میں کجروی کرتے ہیں۔ عنقریب انہیں ان کے کئے کا بدلہ مل جائے گا۔'),
(1135, 7, 181, 'ہماری مخلوق میں سے ایک گروہ ایسا بھی ہے جو حق کے مطابق ہدایت کرتا ہے اور اسی کے مطابق (لوگوں کا) فیصلہ کرتا ہے۔'),
(1136, 7, 182, 'اور جن لوگوں نے ہماری آیتوں (نشانیوں) کو جھٹلایا ہم انہیں بتدریج (آہستہ آہستہ ان کے انجام بد کی طرف) لے جائیں گے کہ انہیں اس کی خبر تک نہ ہوگی۔'),
(1137, 7, 183, 'اور میں انہیں ڈھیل دیتا ہوں بے شک میری تدبیر بڑی مضبوط ہے۔'),
(1138, 7, 184, 'کیا ان لوگوں نے اتنا بھی نہیں سوچا کہ ان کے ساتھی (پیغمبر اسلام(ص)) میں ذرا بھی جنون نہیں ہے۔ وہ تو بس کھلم کھلا (خدا کے عذاب سے) ڈرانے والا ہے۔'),
(1139, 7, 185, 'کیا ان لوگوں نے آسمانوں اور زمین کی بادشاہی میں اور ان چیزوں میں جو اللہ نے پیدا کی ہیں کبھی غور و فکر نہیں کیا؟ اور کبھی نظر اٹھا کر ان کو نہیں دیکھا؟ اور اس بات پر کہ شاید ان کا (مقررہ) وقت آگیا ہو۔ تو اب وہ اس کے بعد کس بات پر ایمان لائیں گے؟'),
(1140, 7, 186, 'جس کو اللہ گمراہی میں چھوڑ دے اس کو کوئی ہدایت نہیں کر سکتا اور وہ (خدا) انہیں چھوڑ دیتا ہے تاکہ اپنی سرکشی میں بھٹکتے رہیں۔'),
(1141, 7, 187, '(اے رسول) لوگ آپ سے قیامت کی گھڑی کے متعلق سوال کرتے ہیں کہ اس کا وقوع کب ہوگا؟ کہہ دو کہ اس کا علم تو میرے پروردگار کے ہی پاس ہے اسے اس کا وقت آنے پر وہی ظاہر کرے گا۔ وہ گھڑی آسمانوں اور زمین میں بڑی بھاری ہے (وہ بڑا بھاری حادثہ ہے جو ان میں رونما ہوگا) وہ نہیں آئے گی تمہارے پاس مگر اچانک۔ لوگ اس طرح آپ سے پوچھتے ہیں جیسے آپ اس کی تحقیق اور کاوش میں لگے ہوئے ہیں؟ کہہ دو۔ کہ اس کا علم تو بس اللہ ہی کے پاس ہے۔ لیکن اکثر لوگ یہ حقیقت جانتے نہیں ہیں۔'),
(1142, 7, 188, '(اے رسول(ص)) کہہ دو۔ کہ میں اپنے نفع و نقصان پر کوئی قدرت و اختیار نہیں رکھتا۔ مگر جو اللہ چاہے (وہی ہوتا ہے) اور اگر مجھے غیب کا علم ہوتا تو بہت سے فائدے حاصل کر لیتا اور (زندگی میں) مجھے کبھی کوئی نقصان نہ پہنچتا۔ میں تو بس (نافرمانوں کو) ڈرانے والا اور ایمانداروں کو خوشخبری دینے والا ہوں۔'),
(1143, 7, 189, 'اللہ وہی ہے جس نے تمہیں ایک متنفس (جاندار) سے پیدا کیا۔ اور پھر اس کی جنس سے اس کا جوڑا (شریک حیات) بنایا۔ تاکہ وہ (اس کی رفاقت میں) سکون حاصل کرے۔ پھر جب مرد عورت کو ڈھانک لیتا ہے۔ تو اسے ہلکا سا حمل رہ جاتا ہے جسے لئے وہ چلتی پھرتی رہتی ہے اور جب وہ بوجھل ہو جاتی ہے (وضعِ حمل کا وقت آجاتا ہے) تو دونوں (میاں بیوی) اپنے اللہ سے جو ان کا پروردگار ہے دعا مانگتے ہیں کہ اگر تو نے ہمیں صحیح و سالم اولاد دے دی تو ہم تیرے بڑے شکر گزار ہوں گے۔'),
(1144, 7, 190, 'مگر جب وہ ان دونوں کو صحیح و سالم فرزند عطا کر دیتا ہے۔ تو یہ دونوں اس کی عطا کردہ چیز (اولاد) میں دوسروں کو اس کا شریک ٹھہرانے لگتے ہیں۔ اللہ اس سے بہت برتر ہے جو مشرک کرتے ہیں۔'),
(1145, 7, 191, 'آیا یہ ان کو شریک ٹھہراتے ہیں جو کسی چیز کو پیدا نہیں کر سکتے اور وہ خود کسی کے پیدا کئے ہوئے ہیں۔'),
(1146, 7, 192, 'اور وہ نہ ان کی مدد کرنے کی طاقت رکھتے ہیں اور نہ ہی اپنی مدد پر قادر ہیں۔'),
(1147, 7, 193, 'اور اگر تم ان کو سیدھے راستہ کی طرف بلاؤ تو وہ تمہارے پیچھے قدم نہیں اٹھا سکتے۔ تمہارے لئے برابر ہے کہ ان کو بلاؤ یا خاموش رہو (دونوں باتوں کا نتیجہ ایک ہی ہے)۔'),
(1148, 7, 194, '(اے کافرو) یقینا وہ جنہیں تم اللہ کو چھوڑ کر (اور خدا سمجھ کر) پکارتے ہو وہ تمہاری ہی طرح (خدا کے) بندے ہیں۔ اگر تم اس دعویٰ میں سچے ہو (کہ ان میں مافوقِ بشریت طاقتیں ہیں) تو انہیں پکارو۔ پھر تو انہیں چاہیے کہ تمہاری دعا و پکار کا جواب دیں۔'),
(1149, 7, 195, 'آیا ان (بتوں) کے پاؤں ہیں کہ جن سے وہ چلتے ہوں آیا ان کے ہاتھ ہیں جن سے وہ پکڑتے ہوں کیا ان کی آنکھیں ہیں جن سے وہ دیکھتے ہوں اور کیا ان کے کان ہیں جن سے وہ سنتے ہوں۔ (اے پیغمبر(ص)) کہو کہ تم اپنے تمام شریکوں کو پکارو۔ پھر میرے خلاف سب تدبیریں کرو اور مجھے بالکل مہلت نہ دو۔'),
(1150, 7, 196, 'میرا ولی و سرپرست وہ اللہ ہے جس نے (یہ) کتاب نازل کی ہے اور وہ نیک بندوں کا سرپرست اور کارساز ہے۔'),
(1151, 7, 197, 'اور جنہیں تم اللہ کے سوا خدا پکارتے ہو۔ وہ نہ تمہاری مدد کر سکتے ہیں اور نہ خود اپنی ہی مدد کر سکتے ہیں۔'),
(1152, 7, 198, 'اور اگر انہیں سیدھے راستہ کی دعوت دو تو وہ تمہاری بات سنتے نہیں ہیں اور تم انہیں دیکھوگے کہ گویا وہ تمہاری طرف دیکھ رہے ہیں حالانکہ وہ دیکھتے نہیں ہیں۔'),
(1153, 7, 199, '(اے نبی(ص)) عفو و درگزر سے کام لو۔ اور نیکی کا حکم دیں اور جاہلوں سے روگردانی کریں (ان سے نہ الجھیں)۔'),
(1154, 7, 200, 'اور اگر شیطان کی طرف سے جوش دلانے کی کوئی کوشش ہو تو خدا سے پناہ مانگیں۔ یقینا وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1155, 7, 201, 'جو لوگ پرہیزگار ہیں جب انہیں کوئی شیطانی خیال چھو بھی جائے تو وہ چوکنے ہو جاتے ہیں اور یادِ الٰہی میں لگ جاتے ہیں اور ان کی بصیرت تازہ ہو جاتی ہے (اور حقیقتِ حال کو دیکھنے لگتے ہیں)۔'),
(1156, 7, 202, 'اور جو شیطانوں کے بھائی بند ہیں تو وہ (شیاطینِ انسی و جنی) ان کو گمراہی میں کھینچتے لئے جاتے ہیں اور (انہیں گمراہ کرتے ہیں) کوئی کسر اٹھا نہیں رکھتے۔'),
(1157, 7, 203, '(اے پیغمبر(ص)) جب تم ان کے سامنے (ان کا کوئی مطلوبہ) معجزہ لے کر نہ جاؤ تو وہ کہتے ہیں کہ تم نے خود کوئی معجزہ کیوں نہ منتخب کر لیا؟ (یعنی اپنی مرضی سے کیوں نہ بنا لیا؟) کہہ دیجیے کہ میں تو صرف اسی وحی کی پیروی کرتا ہوں جو میرا پروردگار مجھے کرتا ہے (اس میں میری پسند کا کیا دخل؟) یہ (قرآن) تمہارے پروردگار کی طرف سے سامانِ ہدایت اور سراسر رحمت ہے۔'),
(1158, 7, 204, 'اور (اے مسلمانو) جب قرآن پڑھا جائے تو کان لگا کر (توجہ سے) سنو اور خاموش ہو جاؤ تاکہ تم پر رحمت کی جائے۔'),
(1159, 7, 205, 'اور (اے پیغمبر(ص)) صبح و شام اپنے پروردگار کو یاد کرو۔ اپنے دل میں عجز و انکساری اور خوف و ہراس کے ساتھ۔ اور زبان سے بھی چِلائے بغیر (یعنی دھیمی آواز کے ساتھ) اور (یادِ خدا سے) غفلت کرنے والوں میں سے نہ ہو جاؤ۔'),
(1160, 7, 206, 'جو تمہارے پروردگار کے خاص مقرب ہیں وہ کبھی اس کی عبادت سے سرکشی نہیں کرتے اور اس کی تسبیح و تقدیس کرتے ہیں (اس کی پاکی بیان کرتے ہیں)اور اس کی بارگاہ میں سر بسجود ہوتے ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '(اے رسول) لوگ آپ سے انفال کے بارے میں سوال کرتے ہیں۔ کہہ دیجیے کہ انفال اللہ اور اللہ کے رسول کے لئے ہیں۔ پس اگر تم مؤمن ہو تو اللہ سے ڈرو۔ اور اپنے باہمی تعلقات و معاملات کی اصلاح کرو۔ اور اللہ اور اس کے رسول کی اطاعت کرو۔'),
(1162, 8, 2, '(کامل) ایمان والے تو بس وہ ہیں کہ جب اللہ کا ذکر کیا جائے تو ان کے دل دھل جاتے ہیں اور جب ان کے سامنے اس کی آیتوں کی تلاوت کی جائے تو ان کے ایمان بڑھ جاتے ہیں اور وہ ہر ایک حال میں اپنے پروردگار پر توکل (بھروسہ) رکھتے ہیں۔'),
(1163, 8, 3, 'جو نماز قائم کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے کچھ (ہماری راہ میں) خرچ کرتے ہیں۔'),
(1164, 8, 4, 'بے شک ایسے لوگ حقیقی مؤمن ہیں۔ ان کے لئے ان کے پروردگار کے ہاں مرتبے ہیں۔ اور بخشش ہے اور بہترین روزی ہے۔'),
(1165, 8, 5, '(یہ انفال کا معاملہ ایسا ہی ہے) جیساکہ آپ کے پروردگار نے (جنگِ بدر میں) حق کے ساتھ آپ کو آپ کے گھر سے نکالا اور اہلِ ایمان کا ایک گروہ اس کو ناپسند کر رہا تھا۔'),
(1166, 8, 6, 'باوجودیکہ حق واضح ہوگیا تھا مگر وہ گروہ آپ سے اس امرِ حق میں یوں جھگڑ رہا تھا۔ کہ گویا اسے موت کی طرف ہانک کر لے جایا جا رہا ہے اور وہ اپنی آنکھوں سے موت کو دیکھ رہا ہے۔'),
(1167, 8, 7, 'اور (یاد کرو وہ وقت) کہ جب خدا نے تم سے دو گروہوں میں سے ایک کا وعدہ کیا تھا کہ وہ تمہارے لئے ہے (تمہارے ہاتھ آئے گا) اور تم یہ چاہتے تھے کہ غیر مسلح گروہ تمہارے ہاتھ آجائے اور اللہ یہ چاہتا تھا کہ اپنے کلام و احکام کے ذریعہ سے حق کو ثابت کر دے اور کافروں کی جڑ کاٹ دے۔'),
(1168, 8, 8, 'تاکہ حق کو حق کرکے اور باطل کو باطل کرکے دکھا دے اگرچہ مجرم لوگ اس بات کو کتنا ہی ناپسند کریں (یعنی خدا چاہتا تھا کہ تمہاری مسلح گروہ سے مڈبھیڑ ہو)۔'),
(1169, 8, 9, '(اس وقت کو یاد کرو) جب تم اپنے پروردگار سے فریاد کر رہے تھے اور اس نے تمہاری فریاد سن لی (اور فرمایا) کہ تمہاری مدد کے لیے یکے بعد دیگرے ایک ہزار فرشتے بھیج رہا ہوں۔'),
(1170, 8, 10, 'اور اللہ نے ایسا اس لئے کیا کہ تمہارے لئے خوشخبری ہو اور تمہارے مضطرب دلوں کو اطمینان ہو ورنہ فتح و فیروزی تو بہرحال اللہ ہی کی طرف سے ہے۔ یقینا اللہ زبردست اور حکمت والا ہے۔'),
(1171, 8, 11, 'اور وہ وقت (بھی یاد رکھنے کے قابل ہے کہ) جب اللہ نے تمہیں غنودگی سے ڈھانپ دیا تھا تاکہ اس کی طرف سے تمہیں امن و سکون حاصل ہو۔ اور آسمان سے تم پر پانی برسا رہا تھا۔ تاکہ تمہیں پاک صاف کرے اور تم سے شیطان کی نجاست و ناپاکی دور کرے اور تمہارے دلوں کو مضبوط کرے (تمہاری ڈھارس بندھائے) اور تمہارے قدموں کو جمائے۔'),
(1172, 8, 12, 'اور وہ وقت بھی (یاد رکھنے کے قابل ہے) جب تمہارا پروردگار فرشتوں کو وحی کر رہا تھا کہ میں تمہارے ساتھ ہوں۔ تم اہلِ ایمان کو ثابت قدم رکھو۔ میں عنقریب کافروں کے دلوں میں (مؤمنوں کا) رعب ڈال دوں گا سو (اے مسلمانو) تم کافروں کی گردنوں پر ضرب لگاؤ اور جوڑ جوڑ پر چوٹ لگاؤ۔'),
(1173, 8, 13, 'یہ اس لئے ہے کہ ان لوگوں نے خدا اور رسول(ص) کی مخالفت کی۔ یاد رکھو کہ جو کوئی بھی خدا اور رسول کی مخالفت کرے گا تو اللہ (مکافاتِ عمل میں) سخت سزا دینے والا ہے۔'),
(1174, 8, 14, '(اے مخالفینِ حق) دنیا میں تمہاری یہ سزا ہے۔ پس اس کا مزہ چکھو۔ اور (آخرت میں) آتشِ دوزخ کا عذاب بھی ہے۔'),
(1175, 8, 15, 'اے ایمان والو! جب کافروں کے لشکر سے تمہاری مڈبھیڑ ہو جائے تو خبردار! ان کے مقابلہ میں ان کو پیٹھ نہ دکھانا (بلکہ سیسہ پلائی ہوئی دیوار کی طرح جم کر لڑنا)۔'),
(1176, 8, 16, 'اور جو ایسے (جنگ والے) موقع پر ان کو پیٹھ دکھائے گا۔ سوا اس کے جو جنگی چال کے طور پر ہٹ جائے۔ یا کسی (اپنے) فوجی دستہ کے پاس جگہ لینے کے لیے ایسا کرے (کہ اس میں کوئی حرج نہیں ہے) تو وہ خدا کے قہر و غضب میں آجائے گا۔ اور اس کا ٹھکانا دوزخ ہوگا۔ اور وہ بہت بری جائے بازگشت ہے۔'),
(1177, 8, 17, '(اے مسلمانو) تم نے ان (کفار) کو قتل نہیں کیا بلکہ اللہ نے قتل کیا اور (اے رسول(ص)) وہ سنگریزے تو نے نہیں پھینکے جبکہ تو نے پھینکے بلکہ خدا نے پھینکے (یہ سب کچھ اس لئے ہوا کہ) خدا اہلِ ایمان پر خوب احسان فرمائے (یا اللہ اس کے ذریعہ سے ایمان والوں کو بہترین آزمائش میں ڈال کر آزمائے) یقینا اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1178, 8, 18, 'یہ معاملہ تو تمہارے ساتھ ہو چکا ہے اور (کفار کے ساتھ معاملہ یہ ہے کہ) اللہ ان کی مخفی تدبیروں کو کمزور کرنے والا ہے۔'),
(1179, 8, 19, 'اور (اے کفارِ مکہ) اگر تم فتحمندی کے طلبگار ہو (کہ جو حق پر ہے اس کی فتح ہو) تو (مسلمانوں کی) فتح تمہارے سامنے آگئی! اور اگر تم اب بھی (جنگ و جدال) سے باز آجاؤ تو یہ تمہارے لئے بہتر ہے اور اگر پلٹ کر پھر وہی (شرارت) کروگے تو ہم بھی وہی کریں گے (مسلمانوں کی نصرت کریں گے اور تمہیں سزا دیں گے) اور تمہاری جمعیت چاہے کتنی ہی زیادہ ہو تمہیں کوئی فائدہ نہ دے گی۔ بے شک اللہ ایمان والوں کے ساتھ ہے۔'),
(1180, 8, 20, 'اے ایمان والو! اللہ اور اس کے رسول کی اطاعت کرو۔ اور اس سے روگردانی نہ کرو حالانکہ تم (آوازِ حق) سن رہے ہو۔'),
(1181, 8, 21, 'اور ان لوگوں کی طرح نہ ہو جانا جو کہتے تو ہیں کہ ہم نے سن لیا حالانکہ (دراصل) وہ کچھ بھی سنتے (سناتے) نہیں ہیں۔'),
(1182, 8, 22, 'بے شک اللہ کے نزدیک سب جانوروں سے بدتر جانور وہ (انسان) ہیں جو بہرے گونگے ہیں جو عقل سے ذرا کام نہیں لیتے۔'),
(1183, 8, 23, 'اگر اللہ جانتا کہ ان میں کچھ بھی بھلائی ہے تو ضرور انہیں سنوا دیتا اور اگر (اس بھلائی کے بغیر) انہیں سنواتا۔ تو وہ بے رخی کرتے ہوئے پیٹھ پھیر دیتے۔'),
(1184, 8, 24, 'اے ایمان والو اللہ اور رسول کی آواز پر لبیک کہو۔ جب کہ وہ (رسول) تمہیں بلائیں۔ اس چیز کی طرف جو تمہیں (روحانی) زندگی بخشنے والی ہے۔ اور جان لو۔ کہ اللہ (اپنے مقررہ اسباب کے تحت) انسان اور اس کے دل کے درمیان حائل ہو جاتا ہے اور (یہ بھی جان لو کہ) تم سب اسی کے حضور جمع کئے جاؤگے۔'),
(1185, 8, 25, 'اور اس فتنہ سے بچو جو صرف انہی تک محدود نہیں رہے گا جنہوں نے تم میں سے ظلم و تعدی کی (بلکہ سب اس کی لپیٹ میں آجائیں گے) اور جان لو کہ اللہ سخت سزا دینے والا ہے۔'),
(1186, 8, 26, 'اور وہ وقت یاد کرو۔ جب تم تھوڑے تھے اور ملک میں کمزور سمجھے جاتے تھے اور ڈرتے رہتے تھے کہ کہیں لوگ تمہیں اچک نہ لے جائیں تو اللہ نے تمہیں (مدینہ میں) پناہ دی اور اپنی نصرت سے تمہاری تائید کی اور تمہیں پاک و پاکیزہ چیزیں دے کر رزق کا سامان مہیا کر دیا۔ تاکہ تم شکر گزار ہو۔'),
(1187, 8, 27, 'اے ایمان والو! سمجھتے بوجھتے ہوئے اللہ اور رسول سے خیانت نہ کرو اور نہ ہی اپنی امانتوں میں خیانت کرو۔'),
(1188, 8, 28, 'اور جان لو۔ کہ تمہارے مال اور تمہاری اولاد (تمہارے لئے) ایک آزمائش ہیں اور یہ بھی کہ اللہ ہی کے پاس بڑا اجر ہے۔'),
(1189, 8, 29, 'اے ایمان والو اگر تم تقوائے الٰہی اختیار کرو۔ تو خدا تمہیں حق و باطل میں تفرقہ کرنے کی قوت و صلاحیت عطا فرمائے گا۔ اور تمہاری برائیوں کو ڈھانپ لے گا (پردہ پوشی کرے گا) اور تمہارے گناہ بخش دے گا۔ اور اللہ بڑا فضل (و کرم) والا ہے۔'),
(1190, 8, 30, 'اور (اے رسول(ص)) وہ وقت یاد کرو جب کافر آپ کے خلاف منصوبے بنا رہے تھے کہ آپ کو قید کر دیں۔ یا قتل کریں۔ یا شہر بدر کر دیں وہ بھی تدبیریں کر رہے تھے اور اللہ بھی تدبیر کر رہا تھا اور اللہ بہترین تدبیر کرنے والا ہے۔'),
(1191, 8, 31, 'اور جب ان کے سامنے ہماری آیات پڑھی جاتی ہیں تو کہتے ہیں ہاں ہم نے سن لیا۔ اگر ہم چاہیں تو ہم بھی ایسا کلام پیش کر سکتے ہیں۔ یہ نہیں ہیں مگر گزرے ہوئے لوگوں کی داستانیں۔'),
(1192, 8, 32, '(اے رسول(ص)) وہ وقت یاد کرو۔ جب انہوں نے کہا۔ اے اللہ! اگر یہ (اسلام) تیری طرف سے برحق ہے۔ تو ہم پر آسمان سے پتھر برسا یا کوئی اور دردناک عذاب لا۔'),
(1193, 8, 33, 'اور اللہ ایسا نہیں ہے کہ ان پر عذاب نازل کرے جبکہ آپ ان کے درمیان موجود ہیں اور اللہ ایسا بھی نہیں ہے کہ ان پر عذاب نازل کرے جبکہ وہ استغفار کر رہے ہیں۔'),
(1194, 8, 34, 'لیکن اللہ کیوں نہ ان پر عذاب نازل کرے جبکہ وہ مسجد الحرام سے (مسلمانوں کو) روک رہے ہیں حالانکہ وہ اس کے متولی نہیں ہیں اس کے متولی تو صرف پرہیزگار لوگ ہیں۔ لیکن اکثر لوگ علم نہیں رکھتے۔'),
(1195, 8, 35, 'اور خانہ کعبہ کے پاس ان کی نماز نہیں تھی۔ مگر سیٹیاں اور تالیاں بجانا۔ سو اب عذاب کا مزہ چکھو۔ یہ تمہارے کفر کی پاداش ہے جو تم کیا کرتے تھے۔'),
(1196, 8, 36, 'بے شک جو لوگ کافر ہیں وہ اس لئے اپنے مال خرچ کرتے ہیں کہ (لوگوں کو) خدا کی راہ سے روکیں یہ آئندہ بھی اسی طرح خرچ کریں گے اور پھر انجام کار یہ (مال خرچ کرنا) ان کے لئے حسرت اور پچھتاوے کا باعث بن جائے گا۔ اور بالآخر وہ مغلوب ہو جائیں گے۔ اور جو کافر ہیں وہ گھیر گھار کر جہنم کی طرف جمع کئے جائیں گے۔'),
(1197, 8, 37, 'اور یہ سب کچھ اس لئے ہوگا کہ اللہ ناپاک (لوگوں) کو پاک لوگوں سے جدا کر دے اور جو ناپاک ہیں ان کو ایک دوسرے پر تہہ بہ تہہ رکھ کر ڈھیر بنائے اور پھر اس سارے ڈھیر کو جہنم میں جھونک دے۔ یہی وہ لوگ ہیں جو خسارہ (نقصان) اٹھانے والے ہیں۔'),
(1198, 8, 38, '(اے رسول(ص)) کافروں سے کہہ دو کہ اگر وہ اب بھی (شرارت سے) باز آجائیں۔ تو جو کچھ گزر چکا وہ انہیں معاف کر دیا جائے گا۔ اور اگر وہ اپنی سابقہ روش کا اعادہ کریں گے تو پھر گزشتہ (نافرمان) قوموں کے ساتھ (خدا کی روش) بھی گزر چکی ہے (ان کے ساتھ بھی وہی ہوگا)۔'),
(1199, 8, 39, '(اے مسلمانو) ان (کفار) سے جنگ جاری رکھو یہاں تک کہ فتنہ و فساد ختم ہو جائے اور دین پورے کا پورا صرف اللہ کے لئے ہو جائے پھر اگر وہ (کفر و فتنہ پردازی سے) باز آجائیں تو وہ جو کچھ کر رہے ہیں اللہ اس کو خوب دیکھنے والا ہے۔'),
(1200, 8, 40, 'تو پھر جان لو کہ اللہ تمہارا سرپرست و کارساز ہے وہ کیا ہی اچھا سرپرست ہے۔ کیا ہی اچھا یار و مددگار ہے۔'),
(1201, 8, 41, 'اور جان لو کہ جو چیز بھی تمہیں بطورِ غنیمت حاصل ہو اس کا پانچواں حصہ اللہ کے لئے، رسول کے لئے، (اور رسول کے) قرابتداروں، یتیموں، مسکینوں اور مسافروں کے لئے ہے (یہ واجب ہے) اگر تم اللہ پر اور اس (غیبی نصرت) پر ایمان رکھتے ہو جو ہم نے اپنے بندۂ خاص پر حق و باطل کا فیصلہ کر دینے والے دن نازل کی تھی جس دن (مسلمانوں اور کافروں کی) دو جمعیتوں میں مڈبھیڑ ہوئی تھی اور اللہ ہر چیز پر قادر ہے۔'),
(1202, 8, 42, 'جس وقت تم قریب کے ناکہ پر تھے اور وہ دور کے ناکہ پر اور قافلہ تم سے ادھر نیچے (ساحل سمندر پر) تھا اگر تم ایک دوسرے سے وقت مقرر بھی کر لیتے تو بھی تم میں اختلاف ہو جاتا۔ لیکن خدا نے تو اس بات کو پورا کرنا تھا جو (مڈبھیڑ) ہونے والی تھی۔ تاکہ جو ہلاک ہو وہ اتمامِ حجت کے بعد ہلاک ہو اور جو زندہ رہے تو وہ بھی اتمام حجت کے بعد زندہ رہے بے شک اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1203, 8, 43, '(اور اے نبی) وہ وقت یاد کرو۔ جب اللہ نے خواب میں آپ کو ان (کفار) کی تعداد تھوڑی کرکے دکھائی تھی اور اگر وہ انہیں زیادہ کرکے دکھاتا تو تم ہمت ہار جاتے۔ اور آپس میں جھگڑنے لگتے۔ لیکن اللہ نے اس سے بچایا۔ بے شک اللہ سینوں کے اندر والی باتوں کا خوب جاننے والا ہے۔'),
(1204, 8, 44, 'وہ وقت یاد کرو۔ کہ جب اللہ نے انہیں تمہاری نگاہوں میں کم کرکے دکھایا۔ اور تمہاری تعداد کو ان کی نظروں میں کم کرکے دکھایا تاکہ اللہ اس کو پورا کرے جو ہو کر رہنا تھا۔ اور تمام معاملات کی بازگشت اللہ ہی کی طرف ہے۔'),
(1205, 8, 45, 'اے ایمان والو! جب کسی جماعت سے تمہارا مقابلہ ہو جائے تو ثابت قدم رہا کرو۔ اور اللہ کو یاد کرو۔ تاکہ تم کامیاب ہو جاؤ۔'),
(1206, 8, 46, 'اور اللہ اور اس کے رسول کی اطاعت کرو۔ اور آپس میں جھگڑا نہ کرو۔ ورنہ کمزور پڑ جاؤگے اور تمہاری ہوا اکھڑ جائے گی۔ اور (ہر قسم کی مصیبت و تکلیف میں) صبر سے کام لو بے شک اللہ صبر کرنے والوں کے ساتھ ہے۔'),
(1207, 8, 47, 'اور ان لوگوں کی مانند نہ ہو جو اپنے گھروں سے اتراتے ہوئے اور لوگوں کو اپنی شان و شوکت دکھاتے ہوئے نکلے اور ان کا حال یہ ہے کہ وہ خدا کی راہ سے روکتے ہیں اور وہ جو کچھ کرتے ہیں اللہ (اپنے علم و قدرت سے) اس کا احاطہ کئے ہوئے ہے۔'),
(1208, 8, 48, 'اور وہ وقت یاد کرو جب شیطان نے ان کے اعمال ان کے لئے آراستہ کر دیئے (انہیں خوشنما کرکے دکھایا) اور کہا آج کے دن لوگوں میں سے کوئی بھی تم پر غالب آنے والا نہیں ہے۔ اور میں تمہارا حامی ہوں۔ پھر جب دونوں جماعتیں آمنے سامنے ہوئیں تو وہ الٹے پاؤں واپس ہوا اور کہا میں تم سے بری الذمہ ہوں۔ میں وہ کچھ دیکھ رہا ہوں جو تم نہیں دیکھ رہے میں اللہ سے ڈرتا ہوں۔ اور اللہ بڑی سخت سزا دینے والا ہے۔'),
(1209, 8, 49, 'اور (وہ وقت بھی قابل یاد ہے) جب منافق اور وہ لوگ جن کے دلوں میں (نفاق کی) بیماری ہے کہہ رہے تھے کہ ان لوگوں (مسلمانوں) کو ان کے دین نے فریب اور خبط میں مبتلا کر دیا ہے اور جو کوئی اللہ پر توکل (بھروسہ) کرتا ہے سو اللہ بڑا زبردست، بڑا حکمت والا ہے۔'),
(1210, 8, 50, 'اور کاش تم وہ موقع دیکھو کہ جب فرشتے کافروں کی روحیں قبض کرتے ہیں اور ان کے چہروں اور پیٹھوں پر ضربیں لگاتے ہیں اور کہتے ہیں کہ اب عذابِ آتش کا مزہ چکھو۔'),
(1211, 8, 51, '(اے دشمنانِ حق!) یہ سزا ہے اس کی جو تمہارے ہاتھ پہلے بھیج چکے ہیں۔ اور اللہ اپنے بندوں پر ظلم کرنے والا نہیں ہے۔'),
(1212, 8, 52, 'ان کا حال قومِ فرعون اور ان لوگوں جیسا ہے۔ جو ان سے پہلے گزر چکے جنہوں نے آیاتِ الٰہی کا انکار کیا اور خدا نے انہیں گناہوں پر پکڑ لیا۔ بے شک اللہ بڑا طاقتور ہے اور بڑی سزا دینے والا ہے۔'),
(1213, 8, 53, 'یہ اس بنا پر ہے کہ اللہ کبھی اس نعمت کو تبدیل نہیں کرتا جو اس نے کسی قوم کو عطا کی ہے جب تک وہ خود اپنی حالت تبدیل نہ کر دیں اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1214, 8, 54, '(کفارِ مکہ کا) حال قومِ فرعون اور ان سے پہلے گزرے ہوئے (سرکشوں) کا سا ہے جنہوں نے آیاتِ الٰہی کو جھٹلایا اور ہم نے انہیں ان کے گناہوں کی وجہ سے ہلاک کر دیا اور فرعونیوں کو (سمندر میں) غرق کر دیا۔ اور وہ سب کے سب ظالم تھے۔'),
(1215, 8, 55, 'بے شک زمین پر چلنے والی سب مخلوق میں سے بدترین وہ لوگ ہیں جو کافر ہیں اور اب وہ کسی طرح بھی ایمان لانے والے نہیں ہیں۔'),
(1216, 8, 56, '(اے رسول(ص)) جن لوگوں سے آپ نے (کئی بار صلح کا) معاہدہ کیا اور انہوں نے ہر بار اسے توڑا۔ اور وہ (اللہ سے) نہیں ڈرتے۔'),
(1217, 8, 57, 'پس اگر وہ جنگ میں آپ کے ہاتھ آجائیں تو (انہیں ایسی عبرتناک سزا دیں کہ) جو ان کے بعد والے ہیں (ان کے پشت پناہ ہیں) ان کو منتشر کر دیں۔ تاکہ وہ اس سے عبرت حاصل کریں۔'),
(1218, 8, 58, 'اور اگر آپ کو کسی جماعت کی طرف سے خیانت (عہد شکنی کا) اندیشہ ہو تو پھر ان کا معاہدہ اس طرح ان کی طرف پھینک دیں کہ معاملہ برابر ہو جائے۔ بے شک اللہ خیانت کرنے والوں کو دوست نہیں رکھتا۔'),
(1219, 8, 59, 'اور کافر لوگ یہ خیال نہ کریں کہ وہ بازی لے گئے (بچ گئے)۔ یقینا وہ اللہ کو عاجز نہیں کر سکتے۔'),
(1220, 8, 60, '(اے مسلمانو!) تم جس قدر استطاعت رکھتے ہو ان (کفار) کے لئے قوت و طاقت اور بندھے ہوئے گھوڑے تیار رکھو۔ تاکہ تم اس (جنگی تیاری) سے خدا کے دشمن اور اپنے دشمن کو اور ان کھلے دشمنوں کے علاوہ دوسرے لوگوں (منافقوں) کو خوفزدہ کر سکو۔ جن کو تم نہیں جانتے البتہ اللہ ان کو جانتا ہے اور تم جو کچھ اللہ کی راہ (جہاد) میں خرچ کروگے تمہیں اس کا پورا پورا اجر و ثواب عطا کیا جائے گا اور تمہارے ساتھ کسی طرح ظلم نہیں کیا جائے گا۔'),
(1221, 8, 61, 'اور اگر وہ صلح کی طرف مائل ہوں تو آپ بھی اس کی طرف مائل ہو جائیں۔ اور اللہ پر بھروسہ رکھیں بے شک وہ بڑا سننے والا، بڑا علم والا ہے۔'),
(1222, 8, 62, 'اور اگر ان کا ارادہ یہ ہو کہ آپ کو دھوکہ دیں۔ تو (فکر نہ کریں) اللہ تمہارے لئے کافی ہے وہ وہی ہے جس نے اپنی نصرت اور مؤمنین کی جماعت سے آپ کی تائید کی۔'),
(1223, 8, 63, 'اور اسی نے ان (اہلِ ایمان) کے دلوں میں الفت پیدا کی۔ اگر آپ تمام روئے زمین کی دولت بھی خرچ کر دیتے تو ان کے دلوں میں الفت پیدا نہیں کر سکتے تھے۔ مگر اللہ نے (اپنی قدرتِ کاملہ سے) ان کے درمیان الفت پیدا کر دی بے شک وہ غالب اور بڑا حکمت والا ہے۔'),
(1224, 8, 64, 'اے نبی! آپ کے لئے اللہ اور وہ اہلِ ایمان کافی ہیں جو آپ کے پیروکار ہیں۔'),
(1225, 8, 65, 'اے نبی(ص)! اہلِ ایمان کو جنگ پر آمادہ کرو۔ اگر تم میں سے بیس صابر (ثابت قدم) آدمی ہوئے تو وہ دو سو (کافروں) پر غالب آئیں گے اور اگر تم میں سے ایک سو ہوئے تو کافروں کے ایک ہزار پر غالب آجائیں گے کیونکہ یہ وہ لوگ ہیں جو سمجھ بوجھ نہیں رکھتے۔'),
(1226, 8, 66, 'اب خدا نے تم پر بوجھ ہلکا کر دیا اور اسے معلوم ہوگیا کہ تم میں کمزوری ہے۔ تو اب اگر تم میں سے ایک سو صابر (ثابت قدم) ہوں گے تو دو سو پر غالب آئیں گے اور اگر تم میں سے ایک ہزار ہوں گے تو وہ اللہ کے حکم سے دو ہزار پر غالب آئیں گے۔ اور اللہ تو صبر کرنے والوں کے ساتھ ہے۔'),
(1227, 8, 67, 'اور یہ بات نبی کے شایانِ شان نہیں ہے کہ اس کے لیے قیدی ہوں۔ جب تک وہ زمین میں خوب خون ریزی نہ کرے (فتنہ کو کچل نہ دے) تم دنیا کا مال و متاع چاہتے ہو اور اللہ آخرت چاہتا ہے اور اللہ زبردست حکمت والا ہے۔'),
(1228, 8, 68, 'اگر خدا کی جانب سے پہلے ایک نوشتہ موجود نہ ہوتا۔ تو تم نے جو کچھ کیا ہے اس کی پاداش میں ضرور تمہیں بڑا عذاب پہنچتا۔'),
(1229, 8, 69, 'بہرکیف جو کچھ تمہیں بطورِ غنیمت حاصل ہو اسے حلال اور پاکیزہ سمجھ کر کھاؤ اور اللہ (کی نافرمانی سے) ڈرو۔ بے شک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1230, 8, 70, 'اے نبی! ان قیدیوں سے کہو جو آپ کے قبضہ میں ہیں کہ اگر اللہ نے تمہارے دلوں میں کچھ نیکی اور بھلائی پائی تو جو کچھ تم سے لیا گیا ہے اس سے بہتر تمہیں عطا فرمائے گا۔ اور تمہیں بخش بھی دے گا کیونکہ وہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1231, 8, 71, 'اور اگر انہوں نے آپ کو فریب دینا چاہا۔ تو وہ اس سے پہلے خدا کو فریب دے چکے ہیں اور (اس کی پاداش میں) اللہ نے (آپ کو) ان پر قابو دے دیا کیونکہ خدا بڑا علم والا، بڑا حکمت والا ہے۔'),
(1232, 8, 72, 'یقینا جو لوگ ایمان لائے اور ہجرت کی اور اللہ کی راہ میں اپنے مال و جان سے جہاد کیا اور جنہوں نے مہاجرین کو پناہ دی اور امداد کی یہ لوگ آپس میں ایک دوسرے کے حامی و مددگار ہیں۔ اور وہ لوگ جو ایمان تو لائے مگر ہجرت نہیں کی تو ان سے تمہارا ولایت کا کوئی تعلق نہیں ہے۔ یہاں تک کہ ہجرت کریں۔ اور اگر وہ کسی دینی معاملہ میں تم سے مدد چاہیں تو تم پر (ان کی) مدد کرنا فرض ہے۔ سوا اس صورت کے کہ یہ مدد اس قوم کے خلاف مانگیں جس سے تمہارا معاہدۂ امن ہو۔ اور تم جو کچھ کرتے ہو اللہ اسے دیکھنے والا ہے۔'),
(1233, 8, 73, 'اور جو کافر ہیں وہ آپس میں ایک دوسرے کے حامی و مددگار ہیں اگر تم ایسا نہیں کروگے تو زمین میں بڑا فتنہ اور فساد پھیل جائے گا۔'),
(1234, 8, 74, 'جو لوگ ایمان لائے، ہجرت کی اور اللہ کی راہ میں جہاد کیا اور جنہوں نے پناہ دی اور امداد کی یہی لوگ سچے مؤمن ہیں۔ ان کے لئے بخشش ہے اور عزت کی روزی۔'),
(1235, 8, 75, 'اور جو لوگ بعد میں ایمان لائے اور ہجرت کی اور تمہارے ساتھ مل کر جہاد کیا تو وہ بھی تم ہی میں داخل ہیں اور جو صاحبانِ قرابت ہیں، وہ اللہ کی کتاب میں (میراث کے سلسلہ میں) ایک دوسرے کے زیادہ حقدار ہیں۔ بے شک اللہ ہر چیز کا بڑا جاننے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, '(اے مسلمانو) خدا اور اس کے رسول کی طرف سے اعلانِ برأت ہے ان مشرکین سے جن سے تم نے (صلح کا) معاہدہ کیا تھا۔'),
(1237, 9, 2, '(اے مشرکو) اب تم صرف چار ماہ تک اس سر زمین پر چل پھر لو اور جان لو کہ تم اللہ کو عاجز نہیں کر سکتے اور یہ کہ اللہ کافروں کو ذلیل و رسوا کرنے والا ہے۔'),
(1238, 9, 3, 'اللہ اور اس کے رسول کی طرف سے تمام لوگوں کو حجِ اکبر والے دن اعلانِ عام ہے کہ اللہ اور اس کا رسول مشرکین سے بری و بیزار ہیں۔ اب اگر تم (کفر و شرارت سے) توبہ کرلو تو یہ تمہارے لئے بہتر ہے اور اگر تم نے اس سے روگردانی کی تو پھر جان لو کہ تم اللہ کو عاجز نہیں کر سکتے اور (اے نبی) کافروں کو دردناک عذاب کی خوش خبری سنا دو۔'),
(1239, 9, 4, 'سوا ان مشرکوں کے کہ جن سے تم نے معاہدہ کیا تھا اور انہوں نے (اپنا قول و قرار نبھانے میں) کوئی کمی نہیں کی اور نہ تمہارے خلاف کسی کی امداد کی سو تم ان سے کیا ہوا معاہدہ اس کی مقررہ مدت تک پورا کرو۔ بے شک اللہ پرہیزگاروں کو دوست رکھتا ہے۔'),
(1240, 9, 5, 'پس جب محترم مہینے گزر جائیں تو مشرکوں کو جہاں کہیں بھی پاؤ قتل کرو اور انہیں گرفتار کرو۔ اور ان کا گھیراؤ کرو اور ہر گھات میں ان کی تاک میں بیٹھو۔ پھر اگر وہ توبہ کر لیں نماز پڑھنے لگیں اور زکوٰۃ ادا کرنے لگیں تو ان کا راستہ چھوڑ دو۔ بے شک خدا بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1241, 9, 6, 'اور اے رسول(ص) اگر مشرکین میں سے کوئی آپ سے پناہ مانگے تو اسے پناہ دے دو تاکہ وہ اللہ کا کلام سنے اور پھر اسے اس کی امن کی جگہ پہنچا دو۔ یہ (حکم) اس لئے ہے کہ یہ لوگ (دعوتِ حق کا) علم نہیں رکھتے۔'),
(1242, 9, 7, 'بھلا ان مشرکوں کا اللہ اور اس کے رسول کے ہاں کس طرح کوئی عہد و پیمان ہو سکتا ہے بجز ان لوگوں کے جن سے تم نے مسجدِ حرام کے پاس (بمقامِ حدیبیہ) معاہدہ کیا تھا سو جب تک وہ لوگ تم سے سیدھے رہیں (معاہدہ پر قائم رہیں) تو تم بھی ان سے سیدھے رہو (قائم رہو)۔ بے شک اللہ پرہیزگاروں کو دوست رکھتا ہے۔'),
(1243, 9, 8, '(ان کے سوا دوسرے) مشرکین سے کس طرح کوئی معاہدہ ہو سکتا ہے؟ حالانکہ اگر وہ تم پر غلبہ پا جائیں تو وہ تمہارے بارے میں نہ کسی قرابت کا پاس کریں اور نہ کسی عہد و پیمان کی ذمہ داری کا۔ وہ صرف تمہیں اپنے منہ (کی باتوں) سے راضی کرنا چاہتے ہیں اور ان کے دل انکار کرتے ہیں۔ اور ان میں سے زیادہ تر لوگ فاسق (نافرمان) ہیں۔'),
(1244, 9, 9, 'انہوں نے تھوڑی سی قیمت پر آیاتِ الٰہی فروخت کر دی ہیں اور (لوگوں کو) اللہ کی راہ سے روکنے لگے۔ بہت ہی برا ہے وہ کام جو یہ کر رہے ہیں۔'),
(1245, 9, 10, 'وہ کسی مؤمن کے بارے میں نہ قرابت کا پاس کرتے ہیں اور نہ کسی عہد و پیمان کی ذمہ داری کا اور یہی لوگ ہیں جو ظلم و تعدی کرنے والے ہیں۔'),
(1246, 9, 11, '(بہرحال اب بھی) اگر یہ لوگ توبہ کر لیں۔ اور نماز قائم کریں اور زکوٰۃ دیں تو تمہارے دینی بھائی ہیں ہم اپنی آیات کھول کھول کر بیان کرتے ہیں ان لوگوں کے لئے جو علم رکھتے ہیں۔'),
(1247, 9, 12, 'اور اگر یہ لوگ اپنے عہد و پیمان کے بعد اپنی قَسموں کو توڑ دیں اور تمہارے دین پر طعن و تشنیع کریں تو تم کفر کے ان سرغنوں سے جنگ کرو، ان کی قَسموں کا کوئی اعتبار نہیں تاکہ یہ باز آجائیں۔'),
(1248, 9, 13, 'تم ان لوگوں سے کیوں جنگ نہیں کرتے جنہوں نے اپنی قَسموں کو توڑ ڈالا پیغمبر(ص) کو (وطن سے) نکالنے کا ارادہ کیا اور پھر تمہارے برخلاف لڑائی میں پہل بھی کی۔ تم ان سے ڈرتے ہو؟ اللہ زیادہ حقدار ہے اس بات کا کہ اس سے ڈرو اگر تم مؤمن ہو۔'),
(1249, 9, 14, 'ان سے جنگ کرو۔ اللہ انہیں تمہارے ہاتھوں سے سزا دلوائے گا۔ اور انہیں ذلیل و خوار کرے گا اور ان کے مقابلہ میں تمہاری مدد کرے گا اور جماعتِ مؤمنین کے دلوں کو شفا دے گا (ان کے سارے دکھ دور کر دے گا)۔'),
(1250, 9, 15, 'اور ان کے دلوں کے غم و غصہ کو دور کرے گا اور جسے چاہے گا توبہ کی توفیق دے گا (اور اس کی توبہ کو قبول کرے گا) اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1251, 9, 16, '(اے مسلمانو) کیا تم یہ سمجھتے ہو کہ تم یونہی چھوڑ دیے جاؤگے حالانکہ ابھی تک اللہ نے (ظاہری طور پر) ان لوگوں کو معلوم ہی نہیں کیا جنہوں نے تم میں سے جہاد کیا اور خدا و رسول اور اہل ایمان کو چھوڑ کر کسی کو اپنا جگری دوست اور (محرمِ راز) نہیں بنایا۔ اور اللہ اس سے باخبر ہے جو کچھ تم کر تے ہو۔'),
(1252, 9, 17, 'اور مشرکین کے لئے روا نہیں ہے کہ وہ مسجدوں کو آباد کریں جبکہ وہ خود اپنے اوپر اپنے کفر کی گواہی دے رہے ہیں۔ یہ وہ لوگ ہیں جن کے سب اعمال ضائع ہوگئے اور وہ ہمیشہ آتشِ دوزخ میں رہیں گے۔'),
(1253, 9, 18, 'درحقیقت مسجدوں کو آباد وہ کرتا ہے جو اللہ اور آخرت کے دن پر ایمان رکھتا ہے، نماز قائم کرتا ہے اور زکوٰۃ ادا کرتا ہے اور اللہ کے سوا اور کسی سے نہیں ڈرتا۔ انہی کے متعلق یہ امید کی جا سکتی ہے کہ وہ ہدایت یافتہ لوگوں میں سے ہوں گے۔'),
(1254, 9, 19, 'کیا تم نے حاجیوں کو پانی پلانے اور مسجد الحرام کے آباد رکھنے کو اس شخص (کے کام کے) برابر قرار دیا ہے جو اللہ اور قیامت کے دن پر ایمان لایا اور اللہ کی راہ میں جہاد کیا۔ یہ دونوں اللہ کے نزدیک برابر نہیں ہیں۔ اور اللہ ظالموں کو منزلِ مقصود تک نہیں پہنچاتا۔'),
(1255, 9, 20, 'جو لوگ ایمان لائے اور ہجرت کی اور راہ خدا میں اپنے مال و جان سے جہاد کیا، اللہ کے نزدیک وہ درجہ میں بہت بڑے ہیں اور یہی لوگ ہیں جو کامیاب ہونے والے ہیں۔'),
(1256, 9, 21, 'ان کا پروردگار انہیں اپنی رحمتِ خاص اور خوشنودی اور ایسے بہشتوں کی خوشخبری دیتا ہے جن میں دائمی نعمت ہوگی۔'),
(1257, 9, 22, 'ان میں وہ ہمیشہ رہیں گے بے شک اللہ ہی کے پاس بہت بڑا اجر ہے۔'),
(1258, 9, 23, 'اے ایمان والو! اگر تمہارے باپ اور تمہارے بھائی ایمان کے مقابلہ میں کفر کو ترجیح دیں تو پھر ان کو اپنا رفیق و کارساز نہ بناؤ۔ اور جو کوئی ان کو رفیق و کارساز بنائے گا تو وہی لوگ ظالم ہیں۔'),
(1259, 9, 24, '(اے رسول) کہہ دو کہ اگر تمہارے باپ، تمہارے بیٹے، تمہارے بھائی، تمہاری بیویاں اور تمہارا کنبہ قبیلہ اور تمہارا وہ مال جو تم نے کمایا ہے۔ اور تمہاری وہ تجارت جس کے مندا پڑ جانے سے ڈرتے ہو اور تمہارے وہ رہائشی مکانات جن کو تم پسند کرتے ہو۔ تم کو اللہ، اس کے رسول اور راہِ خدا میں جہاد کرنے سے زیادہ عزیز ہیں۔ تو پھر انتظار کرو۔ یہاں تک کہ اللہ اپنا فیصلہ (تمہارے سامنے) لے آئے اور اللہ فاسق و فاجر قوم کو منزل مقصود تک نہیں پہنچاتا۔'),
(1260, 9, 25, 'بے شک اللہ نے بہت سے مقامات پر تمہاری مدد فرمائی ہے۔ اور حنین کے موقع پر بھی جب تمہاری کثرت تعداد نے تمہیں مغرور کر دیا تھا مگر اس (کثرت) نے تمہیں کوئی فائدہ نہ پہنچایا اور زمین اپنی ساری وسعت کے باوجود تم پر تنگ ہوگئی پھر تم پیٹھ دکھا کر بھاگ کھڑے ہوئے۔'),
(1261, 9, 26, 'پھر اللہ نے اپنی طرف سے اپنے رسول اور اہلِ ایمان پر سکون و اطمینان نازل کیا اور ایسے لشکر نازل کئے جو تمہیں نظر نہیں آئے۔ اور اللہ نے کافروں کو سزا دی اور یہی کافروں کا بدلہ ہے۔'),
(1262, 9, 27, 'اس کے بعد اللہ جس کی چاہتا ہے توبہ قبول کرتا ہے (اور اسے توفیقِ توبہ دیتا ہے) اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1263, 9, 28, 'اے ایمان والو! مشرکین سراسر نجس و ناپاک ہیں سو وہ اس سال (سنہ ۹ ہجری) کے بعد مسجد الحرام کے قریب بھی نہ آنے پائیں۔ اور اگر تمہیں ان کی آمد و رفت کے بند ہونے سے تنگدستی کا اندیشہ ہے تو عنقریب خدا اپنے فضل و کرم سے تمہیں تونگر بنا دے گا۔ بے شک اللہ تعالیٰ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1264, 9, 29, '(اے مسلمانو!) اہلِ کتاب میں سے جو لوگ اللہ اور روزِ آخرت پر ایمان نہیں رکھتے اور خدا و رسول کی حرام کردہ چیزوں کو حرام نہیں جانتے اور دینِ حق (اسلام) کو اختیار نہیں کرتے ان سے جنگ کرو یہاں تک کہ وہ چھوٹے بن کر (ذلیل ہوکر) ہاتھ سے جزیہ دیں۔'),
(1265, 9, 30, 'یہودی کہتے ہیں کہ عزیر اللہ کا بیٹا ہے اور نصرانی کہتے ہیں کہ عیسیٰ اللہ کے بیٹے ہیں۔ یہ ان کی باتیں ہیں (بغیر سوچے سمجھے) ان کے منہ سے نکالی ہوئی۔ یہ لوگ بھی ان جیسی باتیں کرنے لگے ہیں جو ان سے پہلے کفر کر چکے ہیں اللہ ان کو ہلاک کرے یہ کدھر بہکے جا رہے ہیں۔'),
(1266, 9, 31, 'انہوں نے اللہ کو چھوڑ کر اپنے عالموں اور راہبوں (علماء و مشائخ) کو پروردگار بنا لیا ہے، اور مریم کے فرزند مسیح کو بھی۔ حالانکہ ان کو صرف یہ حکم دیا گیا تھا کہ وہ ایک ہی معبودِ برحق کی عبادت کریں۔ اللہ کے سوا اور کوئی الٰہ نہیں ہے۔ وہ پاک ہے ان مشرکانہ باتوں سے جو یہ کرتے ہیں۔'),
(1267, 9, 32, 'وہ چاہتے ہیں کہ اللہ کے نور کو اپنے منہ سے (پھونک مار کر) بجھا دیں اور اللہ انکاری ہے ہر بات سے سوا اس کے کہ وہ اپنے نور کو کمال تک پہنچائے اگرچہ کافر اس کو ناپسند کریں۔'),
(1268, 9, 33, 'وہ (اللہ) وہی ہے جس نے ہدایت اور دینِ حق دے کر اپنے رسول کو بھیجا تاکہ اسے تمام دینوں پر غالب کر دے اگرچہ مشرک اسے ناپسند ہی کریں۔'),
(1269, 9, 34, 'اے ایمان والو! بہت سے عالم اور راہب لوگوں کا مال ناجائز طریقہ پر کھاتے ہیں اور لوگوں کو خدا کی راہ سے روکتے ہیں۔ اور جو لوگ سونا چاندی جمع کرکے رکھتے رہتے ہیں اور اسے اللہ کی راہ میں خرچ نہیں کرتے، انہیں دردناک عذاب کی بشارت دے دو۔'),
(1270, 9, 35, 'یہ (واقعہ) اس دن ہوگا جب کہ اس (سونے چاندی) کو دوزخ کی آگ میں تپایا جائے گا اور پھر اس سے ان کی پیشانیوں، پہلوؤں اور پشتوں کو داغا جائے گا (اور انہیں بتایا جائے گا کہ) یہ ہے وہ جو تم نے اپنے لئے جمع کرکے رکھا تھا تو اب مزا چکھو اس کا جو تم نے جمع کرکے رکھا تھا۔'),
(1271, 9, 36, 'بے شک اللہ کے نزدیک مہینوں کی تعداد نوشتہ خداوندی میں جب سے اللہ نے آسمانوں اور زمین کو پیدا کیا ہے بارہ ہے، جن میں سے چار مہینے حرمت والے ہیں۔ یہی دینِ مستقیم ہے۔ پس ان میں اپنے اوپر ظلم نہ کرو اور تمام مشرکین سے اسی طرح جنگ کرو جس طرح کہ وہ تم سب سے کرتے ہیں اور جان لو کہ بے شک اللہ پرہیزگاروں کے ساتھ ہے۔'),
(1272, 9, 37, 'سوا اس کے نہیں ہے کہ نسیئی (حرمت والے مہینوں کو مؤخر کرنا) کفر میں زیادتی کا موجب ہے۔ اس سے وہ لوگ گمراہ کئے جاتے ہیں جو کافر ہیں۔ وہ اسے ایک سال حلال قرار دیتے ہیں اور اسی کو دوسرے سال حرام قرار دیتے ہیں تاکہ ان مہینوں کی گنتی پوری کریں جنہیں اللہ نے حرام کیا ہے تاکہ اس (حیلے) سے اللہ کی حرام کردہ کو اپنے لئے حلال قرار دیں۔ ان کے برے اعمال ان کے لئے آراستہ کر دیے گئے ہیں اور اللہ کافروں کو منزلِ مقصود تک نہیں پہنچاتا۔'),
(1273, 9, 38, 'اے ایمان والو! تمہیں کیا ہوگیا ہے کہ جب تم سے کہا جاتا ہے کہ اللہ کی راہ میں (جہاد کرنے کے لیے) نکلو تو تم بوجھل ہوکر زمین گیر ہو جاتے ہو۔ کیا تم نے آخرت کے بدلے دنیا کی زندگی کو پسند کر لیا ہے؟ (اگر ایسا ہی ہے تو یاد رکھو کہ) دنیا کا ساز و سامان آخرت کے مقابلہ میں بالکل قلیل ہے۔'),
(1274, 9, 39, 'اگر تم نہیں نکلوگے تو اللہ تمہیں دردناک عذاب کے ساتھ سزا دے گا اور تمہاری جگہ کسی دوسرے گروہ کو لاکھڑا کرے گا۔ اور تم اسے کچھ بھی نقصان نہیں پہنچا سکوگے۔ اور اللہ ہر چیز پر قادر ہے۔'),
(1275, 9, 40, 'اور اگر تم ان (رسول(ص)) کی نصرت نہیں کروگے (تو نہ کرو) اللہ نے ان کی اس وقت نصرت کی جب کافروں نے ان کو (وطن سے) نکال دیا تھا۔ اور آپ دو میں سے دوسرے تھے۔ جب دونوں غار میں تھے اس وقت وہ اپنے ساتھی سے کہہ رہے تھے کہ غمگین نہ ہو یقینا اللہ ہمارے ساتھ ہے۔ سو اللہ نے ان (رسول(ص)) پر اپنی تسکین نازل کی اور ان کی ایسے لشکروں سے مدد کی جن کو تم نے نہیں دیکھا۔ اور اللہ نے کافروں کے بول کو نیچا کر دیا اور اللہ کا بول ہی بالا ہے۔ اور اللہ زبردست ہے بڑا حکمت والا ہے۔'),
(1276, 9, 41, '(اے مسلمانو!) ہلکے پھلکے اور بھاری بوجھل نکل پڑو۔ اور اللہ کی راہ میں اپنے مال و جان کے ساتھ جہاد کرو۔ یہ بات تمہارے لئے بہتر ہے اگر تم علم رکھتے ہو۔'),
(1277, 9, 42, '(اے رسول(ص)) اگر فائدہ قریب اور سفر آسان ہوتا تو یہ (منافق) ضرور تمہارے پیچھے چلتے لیکن انہیں راہ دور کی دکھائی دی (اس لئے قطعِ مسافت دشوار ہوگئی) وہ عنقریب اللہ کے نام کی قَسمیں کھا کھا کر کہیں گے کہ اگر ہم مقدور رکھتے تو ضرور آپ کے ساتھ نکلتے۔ یہ لوگ (جھوٹی قَسمیں کھا کر) اپنے کو ہلاکت میں ڈال رہے ہیں اور اللہ جانتا ہے کہ وہ قطعاً جھوٹے ہیں۔'),
(1278, 9, 43, '(اے رسول(ص)) اللہ آپ کو معاف کرے آپ نے انہیں کیوں (پیچھے رہ جانے کی) اجازت دے دی۔ جب تک آپ پر واضح نہ ہو جاتا کہ سچے کون ہیں اور معلوم نہ ہو جاتا کہ جھوٹے کون؟'),
(1279, 9, 44, 'جو لوگ اللہ اور آخرت کے دن پر ایمان رکھتے ہیں وہ کبھی آپ سے اجازت طلب نہیں کریں گے کہ وہ اپنے مال و جان سے (اللہ کی راہ میں) جہاد کریں۔ اور اللہ پرہیز گاروں کو خوب جانتا ہے۔'),
(1280, 9, 45, 'اس قسم کی اجازت وہی لوگ طلب کرتے ہیں جو اللہ اور آخرت کے دن پر ایمان نہیں رکھتے۔ اور ان کے دل شک میں پڑ گئے ہیں اور وہ اپنے شک میں بھٹک رہے ہیں۔'),
(1281, 9, 46, 'اگر ان لوگوں نے نکلنے کا ارادہ کیا ہوتا تو اس کے لئے کچھ نہ کچھ سروسامان کی تیاری ضرور کرتے لیکن (حقیقت الامر تو یہ ہے کہ) اللہ کو ان کا اٹھنا اور نکالنا ناپسند تھا۔ اس لئے اس نے ان کو کاہل (اور سست) کر دیا۔ اور (گویا ان سے) کہہ دیا گیا کہ بیٹھے رہو بیٹھنے والوں کے ساتھ۔'),
(1282, 9, 47, 'اگر وہ تمہارے ساتھ نکلتے تو تمہاری خرابی میں اضافہ ہی کرتے اور فتنہ پردازی کے لئے دوڑ دھوپ کرتے اور تمہارے اندر (ہنوز) ایسے لوگ (جاسوس) موجود ہیں جو ان کی باتوں پر کان دھرنے والے ہیں (جاسوسی کرتے ہیں)۔ اور اللہ ظالموں کو خوب جانتا ہے۔'),
(1283, 9, 48, 'وہ لوگ اس سے پہلے بھی فتنہ پردازی میں کوشاں رہے ہیں اور آپ کے معاملات کو درہم برہم اور الٹ پلٹ کرتے رہے ہیں یہاں تک کہ حق (سامنے) آگیا (نمایاں ہوگیا) اور اللہ کا حکم غالب ہوا۔ جبکہ وہ ناپسند کرتے تھے۔'),
(1284, 9, 49, 'اور ان میں کچھ ایسے بھی ہیں جو کہتے ہیں کہ مجھے (گھر میں بیٹھے رہنے کی) اجازت دے دیں۔ اور مجھے فتنہ میں نہ ڈالیں خبردار! وہ فتنہ میں تو خود پڑ ہی چکے ہیں اور بلاشبہ جہنم کافروں کو گھیرے ہوئے ہے۔'),
(1285, 9, 50, 'اگر آپ کو کوئی بھلائی پہنچے تو ان کو بری لگتی ہے اور اگر آپ پر کوئی مصیبت آجائے تو کہتے ہیں کہ ہم نے تو پہلے ہی اپنا معاملہ ٹھیک کر لیا تھا۔ اور وہ (یہ کہہ کر) خوش خوش واپس لوٹ جاتے ہیں۔'),
(1286, 9, 51, '(اے پیغمبر(ص)) کہہ دیجیے! کہ ہمیں ہرگز کوئی (بھلائی یا برائی) نہیں پہنچتی مگر وہی جو اللہ نے ہمارے لئے لکھ دی ہے وہی ہمارا مولیٰ و سرپرست ہے۔ اور اللہ ہی پر اہلِ ایمان کو توکل (بھروسہ) کرنا چاہیے۔'),
(1287, 9, 52, 'اے پیغمبر(ص) کہہ دیجیے! کہ تم ہمارے بارے میں دو بھلائیوں (فتح یا شہادت) میں سے ایک کے سوا اور کس چیز کا انتظار کر رہے ہو؟ اور ہم تمہارے بارے میں اس بات کا انتظار کر رہے ہیں کہ اللہ تمہیں سزا دے۔ خواہ اپنی طرف سے دے یا ہمارے ہاتھوں سے؟ سو تم انتظار کرو۔ اور ہم بھی تمہارے ساتھ انتظار کر رہے ہیں۔'),
(1288, 9, 53, 'کہہ دیجیے! کہ تم خوشی سے (اپنا مال) خرچ کرو یا ناخوشی سے۔ بہرحال وہ قبول نہیں کیا جائے گا۔ کیونکہ تم فاسق (نافرمان) لوگ ہو (انما یتقبل اللّٰہ من المتقین یعنی اللہ تو صرف متقیوں کا عمل قبول کرتا ہے)۔'),
(1289, 9, 54, 'اور ان کی خیرات کے قبول کئے جانے میں اس کے سوا اور کوئی امر مانع نہیں ہے کہ انہوں نے خدا و رسول کے ساتھ کفر کیا ہے (ان کا انکار کیا ہے) اور نماز کی طرف نہیں آتے مگر کاہلی اور سستی سے اور خیرات نہیں کرتے مگر بادلِ ناخواستہ۔'),
(1290, 9, 55, 'سو ان کے مال و اولاد تمہیں حیرت و تعجب میں نہ ڈالیں۔ (اللہ تو یہ چاہتا ہے کہ) ان کو یہ انہی چیزوں کے ذریعہ سے دنیاوی زندگی میں سزا دے۔ اور ان کی جانیں ایسی حالت میں نکلیں کہ وہ کافر ہوں۔'),
(1291, 9, 56, 'اور وہ اللہ کی قسمیں کھاتے ہیں کہ وہ تم ہی میں سے ہیں۔ حالانکہ وہ تم میں سے نہیں ہیں لیکن وہ (تم سے) خوف زدہ ہیں۔'),
(1292, 9, 57, 'اگر وہ کوئی جائے پناہ پا لیں یا کوئی غار یا گھس بیٹھنے کی کوئی جگہ تو فوراً ادھر کا رخ کریں منہ زوری کرتے ہوئے (اور رسیاں تڑاتے ہوئے)۔'),
(1293, 9, 58, 'اور (اے نبی) ان میں کچھ ایسے بھی ہیں جو صدقات (کی تقسیم) کے بارے میں آپ(ص) پر عیب لگاتے ہیں۔ پھر اگر اس سے کچھ (معقول مقدار) انہیں دے دی جائے تو راضی ہو جاتے ہیں اور اگر کچھ نہ دیا جائے تو وہ ایک دم ناراض ہو جاتے ہیں۔'),
(1294, 9, 59, 'اور (کیا اچھا ہوتا ہے) اگر وہ اس پر راضی رہتے جو کچھ اللہ اور اس کے رسول نے انہیں دیا تھا اور کہتے کہ اللہ ہمارے لئے کافی ہے۔ وہ عنقریب ہمیں اپنے فضل و کرم سے عطا فرمائے گا اور اس کا رسول بھی۔ ہم تو بس اللہ کی طرف ہی رغبت کرنے والے ہیں۔'),
(1295, 9, 60, 'صدقات (مالِ زکوٰۃ) تو اور کسی کے لئے نہیں صرف فقیروں کے لئے ہے مسکینوں کے لئے ہے اور ان کارکنوں کے لئے ہے جو اس کی وصولی کے لئے مقرر ہیں۔ اور ان کے لئے ہے جن کی (دلجوئی) مطلوب ہے۔ نیز (غلاموں اور کنیزوں کی) گردنیں (چھڑانے) کے لئے ہے اور مقروضوں (کا قرضہ ادا کرنے) کے لئے ہے اور اللہ کی راہ میں خرچ کرنے کے لئے ہے اور مسافروں (کی مدد) کے لئے ہے یہ اللہ کی طرف سے فرض ہے اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1296, 9, 61, 'اور ان میں کچھ ایسے بھی ہیں جو نبی کو اذیت پہنچاتے ہیں اور کہتے ہیں کہ وہ تو بس کان ہیں (یعنی کان کے کچے ہیں) کہہ دیجیے! وہ تمہارے لئے بہتری کا کان ہیں (ان کے ایسا ہونے میں تمہارا ہی بھلا ہے) جو اللہ پر ایمان رکھتے ہیں اور اہلِ ایمان پر اعتماد کرتے ہیں اور جو تم میں سے ایمان لائے ہیں ان کے لئے سراپا رحمت ہیں اور جو لوگ اللہ کے رسول کو اذیت پہنچاتے ہیں۔ ان کے لئے دردناک عذاب ہے۔'),
(1297, 9, 62, 'وہ تمہارے سامنے اللہ کی قَسمیں کھاتے ہیں تاکہ تمہیں راضی کریں۔ حالانکہ اللہ اور اس کا رسول اس کے زیادہ حقدار ہیں کہ ان کو راضی کریں۔ اگر وہ ایمان لائے ہیں۔'),
(1298, 9, 63, 'کیا انہیں (یہ بات) معلوم نہیں ہے کہ جو شخص خدا و رسول کی مخالفت کرے گا۔ اس کے لئے آتشِ دوزخ ہے جس میں وہ ہمیشہ رہے گا۔ یہ بہت بڑی رسوائی ہے۔'),
(1299, 9, 64, 'منافق لوگ ڈرتے رہتے ہیں کہ مبادا ان کے بارے میں کوئی ایسی سورہ نہ نازل ہو جائے جو انہیں بتلا دے جو کچھ ان کے دلوں میں (چھپا ہوا) ہے آپ کہہ دیجیے! کہ تم مذاق اڑاؤ۔ یقینا (اب) اللہ اس بات کا ظاہر کرنے والا ہے جس کے شکار ہونے سے تم ڈرتے ہو۔ (تمہارے نفاق کا پردہ چاک ہونے والا ہے)۔'),
(1300, 9, 65, '(اے رسول(ص)) اگر آپ ان سے پوچھ گچھ کریں تو وہ ضرور کہیں گے کہ ہم تو صرف بحث کر رہے تھے اور تفریح طبعی کر رہے تھے۔ کیا تم اللہ، اس کے رسول اور اس کی آیات سے تمسخر کرتے ہو؟'),
(1301, 9, 66, 'اب عذر نہ تراشو! تم نے (اظہارِ) ایمان کے بعد کفر اختیار کیا ہے۔ اگر ہم تم میں سے ایک گروہ سے درگزر بھی کریں گے تو دوسرے گروہ کو تو ضرور سزا دیں گے کیونکہ وہ (حقیقی) مجرم ہیں۔'),
(1302, 9, 67, 'منافق مرد اور منافق عورتیں ایک دوسرے کے ہم جنس ہیں برائی کا حکم دیتے ہیں اور اچھائی سے روکتے ہیں اور (راہِ حق پر خرچ کرنے سے) اپنے ہاتھ بند رکھتے ہیں (دراصل) انہوں نے خدا کو بھلا دیا ہے اور خدا نے (گویا) ان کو بھلا دیا ہے اور انہیں (نظر انداز کر دیا ہے) بے شک منافق ہی بڑے فاسق (نافرمان) ہیں۔'),
(1303, 9, 68, 'اللہ نے منافق مردوں منافق عورتوں اور کافروں سے آتشِ دوزخ (میں داخل کرنے) کا وعدہ کر رکھا ہے جس میں وہ ہمیشہ رہیں گے وہ ان کے لئے کافی ہے اور اللہ نے ان پر لعنت کی ہے اور ان کے لئے دائمی عذاب ہے۔'),
(1304, 9, 69, '(اے منافقو! تمہاری حالت) ان لوگوں جیسی ہے جو تم سے پہلے گزر چکے۔ جو تم سے زیادہ طاقتور تھے اور مال و اولاد بھی تم سے زیادہ رکھتے تھے پس انہوں نے اپنے (دنیوی) حصہ سے فائدہ اٹھایا اور تم نے بھی اپنے (دنیوی) حصہ سے اسی طرح فائدہ اٹھایا جس طرح تم سے پہلے گزرے ہوؤں نے فائدہ اٹھایا تھا۔ اور تم بھی ویسے ہی لغو بحثوں میں پڑے جیسے وہ پڑے تھے۔ یہی وہ لوگ ہیں جن کے اعمال دنیا و آخرت میں اکارت ہوئے اور یہی لوگ ہیں جو گھاٹا اٹھانے والے ہیں۔'),
(1305, 9, 70, 'کیا انہیں ان لوگوں کی خبر نہیں ملی جو ان سے پہلے گزر چکے ہیں (جیسے) نوح کی قوم، اور عاد و ثمود اور ابراہیم کی قوم! اور مدین والے اور وہ جن کی بستیاں الٹ دی گئی تھیں (قومِ لوط) ان کے پاس ان کے پیغمبر کھلی ہوئی نشانیاں (معجزات) لے کر آئے تھے۔ (مگر وہ اپنی گمراہی سے باز نہ آئے)۔ اللہ تو ایسا نہ تھا کہ ان پر ظلم کرتا مگر وہ خود ہی اپنے اوپر ظلم کرتے تھے۔'),
(1306, 9, 71, 'اور مؤمن مرد اور مؤمن عورتیں یہ سب باہم دگریکرنگ و ہم آہنگ ہیں وہ بھلائی کا حکم دیتے ہیں اور برائی سے روکتے ہیں اور نماز قائم کرتے ہیں۔ اور زکوٰۃ ادا کرتے ہیں۔ اور اللہ اور اس کے رسول کی اطاعت کرتے ہیں۔ یہی وہ لوگ ہیں جن پر اللہ ضرور رحم فرمائے گا۔ یقینا اللہ زبردست ہے، بڑا حکمت والا ہے۔'),
(1307, 9, 72, 'اللہ نے مؤمن مردوں اور مؤمن عورتوں سے ان بہشتوں کا وعدہ کیا ہے جن کے نیچے نہریں بہہ رہی ہوں گی وہ ان میں ہمیشہ رہیں گے نیز ان کے لئے ان ہمیشگی کے بہشتوں (سدا بہار باغوں) میں پاک و پاکیزہ مکانات ہوں گے۔ اور اللہ کی خوشنودی سب سے بڑی ہے۔ یہی ہے بہت بڑی کامیابی۔'),
(1308, 9, 73, 'اے نبی! کافروں اور منافقوں سے جہاد کریں۔ اور ان پر سختی کریں۔ ان کا ٹھکانا جہنم ہے اور وہ بہت بری جائے بازگشت ہے۔'),
(1309, 9, 74, 'وہ تمہارے سامنے اللہ کی قَسمیں کھاتے ہیں کہ انہوں نے (وہ بات) نہیں کہی۔ حالانکہ انہوں نے یقینا کفر کا کلمہ کہا ہے۔ اور اسلام لانے کے بعد کفر اختیار کیا ہے اور انہوں نے وہ کام کرنے کا قصد کیا ہے جسے وہ کر نہ سکے ان کی یہ سب خشمناکی اور عیب جوئی صرف اس وجہ سے ہے کہ خدا و رسول نے (مالِ غنیمت دے کر) ان کو تونگر کر دیا ہے۔ سو اگر وہ اب بھی توبہ کر لیں تو یہ ان کے لئے بہتر ہے اور اگر وہ روگردانی کریں تو اللہ انہیں دنیا و آخرت میں دردناک عذاب دے گا۔ اور تمام روئے زمین پر ان کا کوئی حامی اور یار و مددگار نہ ہوگا۔'),
(1310, 9, 75, 'اور ان میں سے کچھ ایسے بھی ہیں جنہوں نے اللہ سے عہد کیا تھا کہ اگر اس نے ہمیں اپنے فضل سے (کچھ مال و دولت) سے نوازا تو ہم ضرور خیرات کریں گے اور ضرور نیکوکار بندوں میں سے ہو جائیں گے۔'),
(1311, 9, 76, 'پھر جب اللہ نے انہیں اپنے فضل و کرم سے نوازا تو وہ اس میں بخل کرنے لگے اور روگردانی کرتے ہوئے (اپنے عہد سے) پھر گئے۔'),
(1312, 9, 77, 'پس (اس روش کا نتیجہ یہ نکلا کہ) خدا نے ان کی وعدہ خلافی کرنے اور جھوٹ بولنے کی وجہ سے یومِ لقا (قیامت) تک ان کے دلوں میں نفاق قائم کر دیا۔'),
(1313, 9, 78, 'کیا یہ لوگ نہیں جانتے کہ اللہ ان کے دلی راز اور ان کی سرگوشی کو جانتا ہے اور وہ غیب کی تمام باتوں کو بڑا جاننے والا ہے۔'),
(1314, 9, 79, 'یہ ایسے ہیں کہ خوش دلی سے خیرات کرنے والے مؤمنین پر ریاکاری کا عیب لگاتے ہیں۔ اور جو اپنی محنت مزدوری کے سوا اور کچھ نہیں رکھتے (اس لئے راہِ خدا میں تھوڑا مال دیتے ہیں) یہ ان کا مذاق اڑاتے ہیں اللہ ان کا مذاق اڑائے گا۔ (اس کی سزا دے گا) اور ان کے لئے دردناک عذاب ہے۔'),
(1315, 9, 80, '(اے رسول(ص)) آپ ان کے لئے مغفرت طلب کریں اور خواہ نہ کریں اگر (بالفرض) آپ ان کے لئے ستر بار بھی مغفرت طلب کریں جب بھی اللہ انہیں ہرگز نہیں بخشے گا۔ یہ اس لئے ہے کہ انہوں نے اللہ اور رسول کے ساتھ کفر کیا۔ اور خدا فاسق (نافرمان) لوگوں کو منزلِ مقصود تک نہیں پہنچاتا۔'),
(1316, 9, 81, 'جو (منافق جنگ تبوک میں) پیچھے چھوڑ دیے گئے وہ رسولِ خدا(ص) کے پیچھے (ان کی خواہش کے خلاف) اپنے گھروں میں بیٹھے رہنے پر خوش ہوئے اور خدا کی راہ میں اپنے مال و جان سے جہاد کرنے کو ناپسند کیا۔ اور (دوسروں سے بھی) کہا کہ گرمی میں سفر نہ کرو۔ کہہ دیجیے! کہ دوزخ کی آگ (اس سے) زیادہ گرم ہے کاش وہ یہ بات سمجھتے۔'),
(1317, 9, 82, 'ان (برے) کاموں کے بدلہ میں جو یہ لوگ کرتے ہیں انہیں چاہیے کہ اب وہ ہنسیں کم اور روئیں زیادہ۔'),
(1318, 9, 83, '(اے نبی(ص)) اگر اللہ آپ کو واپس لائے ان کے کسی گروہ کی طرف۔ اور وہ آپ سے جہاد کے لیے نکلنے کی اجازت مانگے تو کہہ دیجیے کہ اب تم میرے ساتھ کبھی نہیں نکل سکتے۔ اور میرے ہمراہ ہو کر کبھی دشمن سے جنگ نہیں کر سکتے تم نے پہلی بار (گھر میں) بیٹھ رہنا پسند کیا۔ تو اب بھی بیٹھے رہو پیچھے رہ جانے والوں (بچوں اور عورتوں) کے ساتھ۔'),
(1319, 9, 84, 'اور ان میں سے جو کوئی مر جائے تو اس کی نمازِ جنازہ نہ پڑھیں اور نہ ہی اس کی قبر پر کھڑے ہوں۔ بلاشبہ انہوں نے خدا و رسول کے ساتھ کفر کیا اور وہ فسق و نافرمانی کی حالت میں مرے ہیں۔'),
(1320, 9, 85, 'اور ان کے مال و اولاد آپ کو حیرت و تعجب میں نہ ڈالیں خدا چاہتا ہے کہ انہی چیزوں کے ذریعہ سے انہیں سزا دے اور ان کی جانیں اس حال میں نکلیں کہ وہ کافر ہوں جب کوئی اس قسم کی سورت نازل ہوتی ہے کہ اللہ پر ایمان لاؤ اور اس کے رسول کے ہمراہ ہو کر جہاد کرو۔ تو ان میں سے جو مقدور والے ہیں وہ آپ سے رخصت مانگتے ہیں۔'),
(1321, 9, 86, 'اور کہتے ہیں کہ ہمیں چھوڑ دیں تاکہ ہم گھر میں بیٹھ رہنے والوں کے ساتھ بیٹھے رہیں۔'),
(1322, 9, 87, 'ان لوگوں نے یہ بات پسند کی کہ گھر میں پیچھے رہ جانے والی عورتوں کے ساتھ رہیں اور (ان کے نفاق و کفر کی وجہ سے) ان کے دلوں پر مہر لگ گئی ہے۔ بس یہ کچھ سمجھتے ہی نہیں ہیں۔'),
(1323, 9, 88, 'لیکن اللہ کے رسول اور جو ان کے ساتھ ایمان لائے ہیں انہوں نے اپنے مال اور اپنی جان کے ساتھ جہاد کیا اور یہی وہ ہیں جن کے لئے ساری بھلائیاں ہیں۔ اور یہی کامیاب ہونے والے ہیں۔'),
(1324, 9, 89, 'اللہ نے ان کے لئے ایسے بہشت (باغات) تیار کر رکھے ہیں جن کے نیچے نہریں بہتی ہیں جن میں وہ ہمیشہ رہیں گے اور یہ بہت بڑی کامیابی ہے۔'),
(1325, 9, 90, 'اور صحرائی بَدوؤں میں عذر کرنے والے (آپ کے پاس) آئے کہ انہیں پیچھے رہ جانے کی اجازت دے دی جائے۔ اور جنہوں نے (اسلام کا اظہار کرکے) خدا و رسول سے جھوٹ بولا تھا وہ (بلا اجازت) گھروں میں بیٹھے رہے۔ ان میں سے جنہوں نے کفر اختیار کیا ہے انہیں عنقریب دردناک عذاب پہنچے گا۔'),
(1326, 9, 91, 'کمزوروں، بیماروں پر اور ان ناداروں پر خرچ کرنے کے لیے کچھ نہیں رکھتے کوئی گناہ نہیں ہے (اگر جہاد میں شریک نہ ہوں) بشرطیکہ وہ خلوصِ دل سے اللہ اور رسول کے وفادار ہوں۔ نیکوکاروں پر کوئی الزام نہیں ہے اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1327, 9, 92, 'اور نہ ہی ان لوگوں پر کوئی گناہ ہے جو آپ کے پاس آتے ہیں کہ آپ ان کے لئے سواری کا کوئی انتظام کریں۔ اور آپ نے کہا کہ میرے پاس سواری کے لئے کچھ نہیں ہے۔ تو وہ اس حال میں مجبوراً واپس گئے کہ ان کی آنکھوں سے آنسو جاری تھے اس رنج میں کہ ان کے پاس (راہِ خدا میں) خرچ کرنے کے لیے کچھ میسر نہیں ہے۔'),
(1328, 9, 93, 'ہاں البتہ الزام (اور اعتراض) ان لوگوں پر ہے کہ باوجود دولت مند ہونے کے آپ سے (بیٹھے رہنے کی) اجازت مانگتے ہیں انہوں نے اس بات کو پسند کیا کہ وہ پیچھے رہ جانے والوں کے ساتھ رہیں اور اللہ نے ان کے دلوں پر مہر لگا دی ہے اس لئے وہ کچھ جانتے بوجھتے نہیں ہیں۔'),
(1329, 9, 94, 'جب تم لوگ (جہاد سے) لوٹ کر ان کے پاس جاؤگے۔ تو وہ (منافقین) طرح طرح کے عذر پیش کریں گے۔ (اے رسول(ص)) تم کہہ دو بہانے نہ بناؤ۔ ہم ہرگز تمہاری کسی بات پر اعتبار نہیں کریں گے۔ (کیونکہ) اللہ تعالیٰ نے ہمیں تمہارے حالات بتا دیئے ہیں۔ اب آئندہ اللہ تمہارا طرزِ عمل دیکھے گا اور اس کا رسول بھی پھر تم غائب اور حاضر کے جاننے والے (خدا) کی طرف لوٹائے جاؤگے وہ تمہیں بتلائے گا جو کچھ تم (دنیا میں) کرتے رہے ہو۔'),
(1330, 9, 95, 'جب تم ان کے پاس لوٹ جاؤگے تو وہ ضرور تمہارے سامنے اللہ کی قَسمیں کھائیں گے۔ تاکہ تم ان سے درگزر کرو۔ سو تم ان سے رخ پھیر ہی لو۔ یہ ناپاک ہیں اور ان کا ٹھکانہ جہنم ہے یہ ان کے کئے کی سزا ہے۔ جو وہ کرتے رہے ہیں۔'),
(1331, 9, 96, 'یہ لوگ تمہارے سامنے قَسمیں کھائیں گے تاکہ تم ان سے راضی ہو جاؤ۔ سو تم اگر ان سے راضی بھی ہو جاؤ تو اللہ تو کبھی فاسق (نافرمان) قوم سے راضی ہونے والا نہیں ہے۔'),
(1332, 9, 97, 'صحرائی عرب کفر و نفاق میں سب سے زیادہ سخت ہیں اور اسی قابل ہیں کہ ان احکام کے حدود و قیود کو نہ سمجھیں جو خدا نے اپنے رسول(ص) پر نازل کئے ہیں اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1333, 9, 98, 'صحرائی عربوں میں سے کچھ ایسے لوگ ہیں کہ (راہ خدا میں) جو کچھ خرچ کرتے ہیں اسے جرمانہ سمجھتے ہیں اور تمہارے بارے میں زمانہ کی گردشوں کے منتظر ہیں۔ (کہ تم پر کوئی گردش آجائے اور وہ اسلام سے گلو خلاصی کرائیں) حالانکہ بڑی گردش انہی (منافقین) کے لیے ہے۔ خدا سب کچھ جاننے والا ہے۔'),
(1334, 9, 99, 'اور انہی بدوؤں میں کچھ ایسے لوگ بھی ہیں جو اللہ اور روزِ آخرت پر ایمان رکھتے ہیں اور جو کچھ (راہِ خدا میں) خرچ کرتے ہیں۔ اسے اللہ کے تقرب اور رسول کی دعاؤں کا وسیلہ سمجھتے ہیں۔ بے شک وہ (خرچ کرنا) ان کے لیے تقرب کا ذریعہ ہے۔ اللہ ضرور ان کو اپنی رحمت میں داخل کرے گا۔ بے شک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1335, 9, 100, 'اور مہاجرین اور انصار میں سے جو ایمان لانے میں سبقت کرنے والے ہیں اور جن لوگوں نے حسنِ عمل میں ان کی پیروی کی اللہ ان سے راضی ہے۔ اور وہ اللہ سے راضی ہیں۔ اور اس نے ان کے لیے ایسے بہشت مہیا کر رکھے ہیں جن کے نیچے نہریں بہہ رہی ہیں۔ اور وہ ان میں ہمیشہ رہیں گے۔ یہ بہت بڑی کامیابی ہے۔'),
(1336, 9, 101, 'اور جو تمہارے اردگرد صحرائی عرب بستے ہیں ان میں کچھ منافق ہیں۔ اور خود مدینہ کے باشندوں میں بھی (منافق موجود ہیں) جو نفاق پر اڑ گئے ہیں (اس میں مشاق ہوگئے ہیں) (اے رسول(ص)) آپ انہیں نہیں جانتے لیکن ہم جانتے ہیں۔ ہم ان کو (دنیا میں) دوہری سزا دیں گے۔ پھر وہ بہت بڑے عذاب کی طرف لوٹائے جائیں گے۔'),
(1337, 9, 102, 'دوسرے کچھ ایسے لوگ بھی ہیں جنہوں نے اپنے گناہوں کا اعتراف کیا ہے (مگر) انہوں نے نیک اور بدعمل خلط ملط کر دیے ہیں بہت ممکن ہے کہ اللہ ان کی توبہ قبول کرے۔ (اور ان پر رحمت کی نظر ڈالے) بے شک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1338, 9, 103, 'اے رسول! ان لوگوں کے مال سے صدقہ (زکوٰۃ) لیں اور اس کے ذریعہ سے انہیں پاک و پاکیزہ کریں۔ اور (برکت دے کر) انہیں بڑھائیں نیز ان کے لیے دعائے خیر کریں کیونکہ آپ کی دعا ان کے لیے تسکین کا باعث ہے اور خدا بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1339, 9, 104, 'کیا انہیں معلوم نہیں ہے کہ اللہ ہی ہے جو اپنے بندوں کی توبہ قبول کرتا ہے اور وہی صدقات کو قبول کرتا ہے یقینا اللہ ہی توبہ کا بڑا قبول کرنے والا، بڑا رحم کرنے والا ہے۔'),
(1340, 9, 105, '(اے رسول(ص)) ان لوگوں سے کہہ دو کہ تم عمل کیے جاؤ اللہ، اس کا رسول اور مؤمنین تمہارے عمل کو دیکھیں گے (کہ تم کیسے عمل کرتے ہو) اور پھر غائب اور حاضر کے جاننے والے (خدا) کی طرف لوٹائے جاؤگے بس وہ تمہیں بتلائے گا تم کیا عمل کرتے ہو۔'),
(1341, 9, 106, 'اور کچھ اور لوگ ایسے بھی ہیں جن کا معاملہ خدا کے حکم پر موقوف ہے وہ انہیں سزا دے یا ان کی توبہ کرے اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1342, 9, 107, 'اور (منافقین میں سے) وہ لوگ بھی ہیں۔ جنہوں نے اس غرض سے ایک مسجد بنائی کہ نقصان پہنچائیں، کفر کریں اور مؤمنین میں تفرقہ ڈالیں۔ اور ان لوگوں کے لیے کمین گاہ بنائیں جو اس سے پہلے خدا و رسول سے جنگ کر چکے ہیں وہ ضرور قَسمیں کھائیں گے کہ بھلائی کے سوا ہمارا کوئی مقصد نہیں ہے اور اللہ گواہی دیتا ہے کہ یہ جھوٹے ہیں۔'),
(1343, 9, 108, '(اے رسول(ص)) تم ہرگز اس عمارت میں کھڑے نہ ہونا۔ بے شک وہ مسجد جس کی اول دن سے تقویٰ و پرہیزگاری پر بنیاد رکھی گئی ہے۔ اس کی زیادہ مستحق ہے کہ آپ اس میں کھڑے ہوں (مسجد قبا و مسجد نبوی) اس میں ایسے لوگ ہیں جو پاک و صاف رہنا پسند کرتے ہیں اور اللہ پاک و صاف رہنے والوں کو ہی پسند کرتا ہے'),
(1344, 9, 109, 'آیا وہ شخص بہتر ہے جو اپنی عمارت کی بنیاد خوفِ خدا اور اس کی خوشنودی پر رکھے یا وہ جس نے اپنی عمارت کی بنیاد ایک کھائی کے گرتے ہوئے کنارے پر رکھی اور پھر وہ اسے لے کر دوزخ کی آگ میں جاگری اور اللہ ظالموں کو منزلِ مقصود تک نہیں پہنچاتا۔'),
(1345, 9, 110, 'یہ عمارت جو ان لوگوں نے بنائی ہے (مسجدِ ضرار) یہ ہمیشہ ان کے دلوں کو مضطرب رکھے گی مگر یہ کہ ان کے دل ٹکڑے ٹکڑے ہو جائیں بلاشبہ خدا بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1346, 9, 111, 'بے شک اللہ تعالیٰ نے مؤمنین سے ان کی جانیں خرید لی ہیں اور ان کے مال بھی اس قیمت پر کہ ان کے لیے بہشت ہے وہ اللہ کی راہ میں جنگ کرتے ہیں پس وہ مارتے بھی ہیں اور خود بھی مارے جاتے ہیں (ان سے) یہ وعدہ اس (اللہ تعالیٰ) کے ذمہ ہے تورات، انجیل اور قرآن (سب) میں اور اللہ سے بڑھ کر کون اپنے وعدہ کا پورا کرنے والا ہے؟ پس اے مسلمانو! تم اس سودے پر جو تم نے خدا سے کیا ہے خوشیاں مناؤ۔ یہی تو بڑی کامیابی ہے۔'),
(1347, 9, 112, '(ان لوگوں کے اوصاف یہ ہیں کہ) یہ توبہ کرنے والے (اللہ کی عبادت کرنے والے (اس کی) حمد و ثنا کرنے والے (اس کی راہ میں) سیر و سیاحت کرنے والے (یا روزہ رکھنے والے) رکوع و سجود کرنے والے، نیکی کا حکم دینے والے، برائی سے روکنے والے اور اللہ کی مقررہ حدود کی حفاظت کرنے والے ہیں اور (اے رسول) مؤمنوں کو خوشخبری دے دو (اور یہی وہ مؤمن ہیں)۔'),
(1348, 9, 113, 'نبی کو اور ان لوگوں کو جو ایمان لائے ہیں یہ زیبا نہیں ہے کہ مشرکین کے لیے مغفرت طلب کریں اگرچہ وہ ان کے عزیز و اقارب ہی کیوں نہ ہوں۔ جبکہ ان پر واضح ہوگیا کہ وہ دوزخی ہیں۔'),
(1349, 9, 114, 'اور ابراہیم نے جو اپنے باپ (تایا) کے لیے دعائے مغفرت کی تھی تو وہ ایک وعدہ کی بنا پر تھی جو وہ کر چکے تھے۔ مگر جب ان پر واضح ہوگیا کہ وہ اللہ کا دشمن ہے تو آپ اس سے بیزار ہوگئے بے شک جناب ابراہیم بڑے ہی دردمند اور غمخوار و بردبار انسان تھے۔'),
(1350, 9, 115, 'اور اللہ کسی قوم کو ہدایت دینے کے بعد اس وقت تک گمراہ نہیں قرار دیتا جب تک ان پر یہ واضح نہ کر دے کہ انہیں کن چیزوں سے پرہیز کرنا ہے۔ بیشک اللہ ہر شے کا جاننے والا ہے۔'),
(1351, 9, 116, 'اللہ ہی کے لئے زمین و آسمان کی حکومت ہے اور وہی حیات و موت کا دینے والا ہے اس کے علاوہ تمہارا نہ کوئی سرپرست ہے نہ مددگار۔'),
(1352, 9, 117, 'بیشک خدا نے پیغمبر اور ان مہاجرین و انصار پر رحم کیا ہے جنہوں نے تنگی کے وقت میں پیغمبر کا ساتھ دیا ہے جب کہ ایک جماعت کے دلوں میں کجی پیدا ہو رہی تھی پھر خدا نے ان کی توبہ کو قبول کر لیا کہ وہ ان پر بڑا ترس کھانے والا اور مہربانی کرنے والا ہے۔'),
(1353, 9, 118, 'اور اللہ نے ان تینوں پر بھی رحم کیا جو جہاد سے پیچھے رہ گئے تھے یہاں تک کہ زمین جب اپنی وسعتوں سمیت ان پر تنگ ہوگئی اور ان کے دم پر بن گئی اور انہوں نے یہ سمجھ لیا کہ اب اللہ کے علاوہ کوئی پناہ گاہ نہیں ہے تو اللہ نے ان کی طرف توجہ فرمائی کہ وہ توبہ کر لیں اس لئے کہ وہ بڑا توبہ قبول کرنے والا اور مہربان ہے۔'),
(1354, 9, 119, 'اے ایمان والو! اللہ سے ڈرو اور سچوں کے ساتھ ہو جاؤ۔'),
(1355, 9, 120, 'مدینہ کے باشندوں اور صحرائی عربوں کے لیے یہ درست نہیں ہے کہ پیغمبر خدا کا ساتھ چھوڑ کر پیچھے بیٹھیں۔ اور ان کی جان کی پرواہ نہ کرکے اپنی جانوں کی فکر میں لگ جائیں یہ اس لیے ہے کہ ان (مجاہدین) کو راہ خدا میں (جو بھی مصیبت پیش آتی ہے۔ وہ خواہ) پیاس ہو، جسمانی زحمت و مشقت ہو، بھوک ہو۔ یا کسی ایسے راستہ پر چلنا جو کافروں کے غم و غصہ کا باعث ہو یا دشمن کے مقابلہ میں کوئی کامیابی حاصل کرنا مگر یہ کہ ان تمام تکلیفوں کے عوض ان کے لیے ان کے نامۂ اعمال میں نیک عمل لکھا جاتا ہے یقینا اللہ نیکوکاروں کا اجر و ثواب ضائع نہیں کرتا۔'),
(1356, 9, 121, 'اسی طرح وہ راہِ خدا میں کوئی تھوڑا یا بہت مال خرچ نہیں کرتے اور نہ کوئی وادی (میدان) طئے کرتے ہیں مگر یہ کہ ان کے لئے لکھ لیا جاتا ہے تاکہ خدا ان کی کار گزاریوں کا اچھے سے اچھا بدلہ دے۔'),
(1357, 9, 122, 'اور یہ تو نہیں ہو سکتا کہ تمام اہلِ ایمان نکل کھڑے ہوں تو ایسا کیوں نہیں ہو سکتا۔ کہ ہر جماعت میں سے کچھ لوگ نکل آئیں تاکہ وہ دین میں تفقہ (دین کی سمجھ بوجھ) حاصل کریں اور جب (تعلیم و تربیت کے بعد) اپنی قوم کے پاس لوٹ کر آئیں۔ تو اسے (جہالت بے ایمانی اور بد عملی کے نتائج سے) ڈرائیں تاکہ وہ ڈریں۔'),
(1358, 9, 123, 'اے ایمان والو! ان کافروں سے جنگ کرو جو تمہارے آس پاس ہیں اور چاہیے کہ وہ جنگ میں تمہارے اندر سختی محسوس کریں۔ اور جان لو کہ اللہ پرہیزگاروں کے ساتھ ہے۔'),
(1359, 9, 124, 'اور جب کوئی سورہ نازل ہوتی ہے تو ان (منافقوں) میں سے کچھ لوگ ایسے ہیں جو (طنزیہ طور پر کہتے ہیں) کہ اس نے تم لوگوں میں سے کس کا ایمان زیادہ کیا ہے؟ (اس کا جواب یہ ہے) کہ جو لوگ ایمان والے ہیں اس نے ان کا ایمان تو زیادہ کر دیا ہے اور وہ خوش ہو رہے ہیں۔'),
(1360, 9, 125, 'اور جن لوگوں کے دلوں میں (نفاق کی) بیماری ہے تو یہ سورہ ان کی موجودہ نجاست و خباثت میں اور اضافہ کر دیتی ہے اور وہ کفر کی حالت میں مر جاتے ہیں۔'),
(1361, 9, 126, 'کیا (یہ لوگ) نہیں دیکھتے کہ وہ ہر سال ایک یا دو مرتبہ آزمائش میں ڈالے جاتے ہیں۔ پھر بھی یہ نہ توبہ کرتے ہیں اور نہ نصیحت حاصل کرتے ہیں۔'),
(1362, 9, 127, 'اور جب کوئی سورہ نازل ہوتی ہے( جس میں منافقوں کا تذکرہ ہوتا ہے) تو وہ ایک دوسرے کی طرف دیکھنے لگتے ہیں اور (آنکھوں کے اشارہ سے) کہتے ہیں تمہیں کوئی دیکھ تو نہیں رہا؟ (پھر چپکے سے) پلٹ جاتے ہیں دراصل اللہ نے ان کے دلوں کو پھیر دیا ہے کیونکہ یہ بالکل ناسمجھ لوگ ہیں۔'),
(1363, 9, 128, '(اے لوگو!) تمہارے پاس (اللہ کا) ایک ایسا رسول آگیا ہے جو تم ہی میں سے ہے جس پر تمہارا زحمت میں پڑنا شاق ہے تمہاری بھلائی کا حریص ہے اور ایمان والوں کے ساتھ بڑی شفقت اور مہربانی کرنے والا ہے۔'),
(1364, 9, 129, '(اے رسول(ص)) اگر اس پر بھی یہ لوگ روگردانی کرتے ہیں تو کہہ دیجیے کہ میرے لیے اللہ کافی ہے اس کے سوا کوئی الٰہ نہیں ہے۔ اسی پر میرا بھروسہ ہے اور وہی عرشِ عظیم کا مالک ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، را۔ یہ حکمت والی کتاب کی آیتیں ہیں۔'),
(1366, 10, 2, 'کیا یہ بات لوگوں کے لئے تعجب کا باعث ہے کہ ہم نے خود انہی میں سے ایک آدمی پر وحی بھیجی کہ (بے ایمان اور بدکردار لوگوں کو) ڈراؤ۔ اور ایمان لانے والوں کو خوشخبری دے دو۔ کہ ان کے لیے ان کے پروردگار کے ہاں اچھا مقام و مرتبہ ہے (لیکن) کافروں نے کہا کہ یہ شخص تو کھلا جادوگر ہے۔'),
(1367, 10, 3, 'بے شک تمہارا پروردگار اللہ ہے۔ جس نے آسمانوں اور زمینوں کو چھ دنوں میں پیدا کیا ہے۔ پھر عرش پر غالب ہوا وہی (کائنات کے) ہر کام کی تدبیر اور اس کا بندوبست کرتا ہے اس کی بارگاہ میں کوئی سفارشی نہیں ہو سکتا مگر اس کی اجازت کے بعد۔ یہ ہے اللہ تمہارا پروردگار پس اسی کی عبادت کرو کیا تم غور و فکر نہیں کرتے (اور نصیحت قبول نہیں کرتے)۔'),
(1368, 10, 4, 'تم سب نے اسی کی طرف لوٹ کر جانا ہے یہ اللہ کا سچا وعدہ ہے بے شک وہی مخلوق کی پیدائش کی ابتداء کرتا ہے پھر وہی دوبارہ اسے زندہ کرے گا تاکہ جو لوگ ایمان لائیں اور نیک عمل کریں انہیں انصاف کے ساتھ جزا دے اور جنہوں نے کفر کیا انہیں ان کے کفر کی پاداش میں کھولتا ہوا پانی پینے کو ملے گا اور ان کے لئے دردناک عذاب ہے۔'),
(1369, 10, 5, 'وہ وہی ہے جس نے سورج کو چمکدار اور چاند کو نور (روشن) بنایا اور پھر چاند کے لئے مختلف منزلیں قرار دیں تاکہ تم برسوں کی گنتی اور دوسرے حساب معلوم کر سکو اللہ نے یہ سب کچھ حق و حکمت کے ساتھ پیدا کیا ہے وہ ان لوگوں کے لیے جو سمجھنا اور جاننا چاہیں اپنی آیتیں کھول کھول کر بیان کرتا ہے۔'),
(1370, 10, 6, 'بے شک رات اور دن کی ادل بدل اور الٹ پھیر میں اور ان چیزوں میں جو خدا نے آسمانوں اور زمین میں پیدا کی ہیں پرہیزگاروں کے لیے (خدا کی قدرت و حکمت کی) نشانیاں ہیں۔'),
(1371, 10, 7, 'بے شک جو لوگ (مرنے کے بعد) ہماری بارگاہ میں حاضری کی امید نہیں رکھتے اور صرف دنیوی زندگی میں مگن ہیں اور اس پر مطمئن ہیں اور جو لوگ ہماری آیتوں سے غافل ہیں۔'),
(1372, 10, 8, 'یہ وہ ہیں جن کا ٹھکانا دوزخ ہے ان کرتوتوں کی پاداش میں جو وہ دنیا میں کرتے رہے ہیں۔'),
(1373, 10, 9, 'بے شک جو لوگ ایمان لائے اور نیک عمل کئے۔ ان کا پروردگار ان کے ایمان کی بدولت انہیں اس منزل مقصود تک پہنچائے گا۔ یعنی وہ نعمتِ الٰہی کے ان باغوں میں ہوں گے جن کے نیچے نہریں بہہ رہی ہوں گی۔'),
(1374, 10, 10, 'وہاں ان کی دعا و صدا یہ ہوگی۔ اے اللہ! پاک ہے تیری ذات اور ان کی صاحب سلامت سلام سے ہوگی اور ان کی آخری دعا و پکار یہ ہوگی کہ ہر قسم کی تعریف اللہ کے لئے ہے جو سب جہانوں کا پروردگار ہے۔'),
(1375, 10, 11, 'اگر خدا لوگوں کو نقصان پہنچانے میں اس طرح جلدی کرتا جس طرح یہ لوگ اپنی بھلائی کے لئے جلدی کرتے ہیں یعنی اگر ہر برائی کا برا نتیجہ فوراً سامنے آجاتا تو ان کی مدت (عمر) کبھی کی پوری ہو چکی ہوتی مگر ہمارے قانون (سزا میں ڈھیل ہے) اس لئے ہم تو ان لوگوں کو بھی اپنی سرکشیوں میں بھٹکنے کے لئے چھوڑ دیتے ہیں جو مرنے کے بعد ہماری بارگاہ میں حضوری کی امید نہیں رکھتے۔'),
(1376, 10, 12, 'جب انسان کو کوئی تکلیف پہنچتی ہے تو وہ کروٹ کے بل لیٹے یا بیٹھے یا کھڑے ہوئے (ہر حال میں) ہمیں پکارتا ہے اور جب ہم اس کی تکلیف دور کر دیتے ہیں تو وہ اس طرح (منہ موڑ کے) چل دیتا ہے جیسے اس نے کسی تکلیف میں جو اسے پہنچی تھی کبھی ہمیں پکارا ہی نہ تھا اسی طرح حد سے گزرنے والوں کے لیے ان کے وہ عمل خوشنما بنا دیئے گئے ہیں جو وہ کرتے رہے ہیں۔'),
(1377, 10, 13, 'اور بالیقین ہم تم سے پہلے (بہت سی) نسلوں کو ہلاک کر چکے ہیں جب انہوں نے ظلم کی راہ و روش اختیار کی۔ اور ان کے رسول ان کے پاس کھلی نشانیاں (معجزے) لے آئے مگر وہ ایسے تھے ہی نہیں کہ ایمان لاتے ہم اسی طرح مجرم لوگوں کو سزا دیتے ہیں۔'),
(1378, 10, 14, 'پھر ہم نے ان (نسلوں) کے بعد تمہیں روئے زمین پر ان کا جانشین بنایا تاکہ ہم دیکھیں کہ تم کیسے کام کرتے ہو؟'),
(1379, 10, 15, 'اور جب انہیں ہماری آیاتِ بینات (واضح آیتیں) پڑھ کر سنائی جاتی ہیں تو جو لوگ مرنے کے بعد ہماری بارگاہ میں حاضری کی توقع نہیں رکھتے وہ کہتے ہیں کہ اس کے علاوہ کوئی اور قرآن لاؤ یا اس میں رد و بدل کر دو۔ آپ کہہ دیجیے! کہ مجھے یہ حق نہیں ہے کہ میں اپنی طرف سے اس میں کچھ رد و بدل کر دوں! میں تو بس اسی کی پیروی کرتا ہوں جو میری طرف وحی کی جاتی ہے اگر میں اپنے پروردگار کی نافرمانی کروں تو میں ایک بڑے دن کے عذاب سے ڈرتا ہوں۔'),
(1380, 10, 16, '(اے رسول(ص)) کہہ دیجیے! اگر خدا چاہتا تو میں تمہیں قرآن پڑھ کر نہ سناتا اور نہ ہی وہ تمہیں اس پر مطلع فرماتا۔ آخر میں تو اس سے پہلے تمہارے درمیان ایک عمر گزار چکا ہوں کیا تم عقل سے کام نہیں لیتے۔'),
(1381, 10, 17, 'پھر اس سے بڑھ کر کون ظالم ہو سکتا ہے جو خدا پر جھوٹا افترا باندھے؟ یا اس کی آیتوں کو جھٹلائے یقینا مجرم کبھی کامیاب نہیں ہوتے۔'),
(1382, 10, 18, 'یہ لوگ اللہ کو چھوڑ کر ایسی چیزوں کی عبادت کرتے ہیں جو نہ انہیں نقصان پہنچا سکتی ہیں اور نہ فائدہ اور کہتے ہیں کہ یہ اللہ کے ہاں ہمارے سفارشی ہیں (اے رسول(ص)) تم کہو۔ کیا تم اللہ کو اس چیز کی اطلاع دیتے ہو جو خود اسے نہ آسمانوں میں معلوم ہے اور نہ زمین میں۔ پاک ہے اس کی ذات اور بلند و بالا ہے اس شرک سے جو یہ لوگ کر رہے ہیں۔'),
(1383, 10, 19, 'اور (ابتداء میں) سب انسان ایک ہی امت تھے (دینِ فطرت پر تھے) پھر آپس میں اختلاف کیا اور اگر تمہارے پروردگار کی طرف سے ایک طئے شدہ بات نہ ہوتی (کہ اعمال کی جزا و سزا آخرت میں ہوگی) تو جن باتوں میں یہ لوگ باہم اختلاف کر رہے ہیں اس کا فیصلہ کر دیا گیا ہوتا۔'),
(1384, 10, 20, 'اور یہ لوگ کہتے ہیں کہ ان (پیغمبر(ص)) پر ان کے پروردگار کی طرف سے (ان کی مطلوبہ) کوئی نشانی کیوں نازل نہیں ہوتی؟ کہہ دیجیئے کہ غیب کا علم اللہ سے مخصوص ہے سو تم انتظار کرو۔ میں بھی تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔'),
(1385, 10, 21, 'اور جب ہم لوگوں کو تکلیف اور دکھ درد کے بعد (اپنی) رحمت کا مزہ چکھاتے ہیں تو وہ فوراً ہماری آیتوں میں چال بازی و حیلہ سازی کرنے لگتے ہیں۔ کہہ دیجیئے کہ اللہ تدبیر و ترکیب میں (تم سے) زیادہ تیز ہے بے شک ہمارے فرستادہ (فرشتے) تمہاری سب چالبازیاں اور مکاریاں برابر لکھتے جا رہے ہیں۔'),
(1386, 10, 22, 'وہ (خدا) وہی ہے جو تمہیں خشکی و تری میں سیر و سفر کراتا ہے یہاں تک کہ جب تم کشتیوں میں سوار ہوتے ہو اور وہ موافق ہوا کے مطابق مسافروں کو لے کر چلتی ہیں اور وہ خوش و خرم ہوتے ہیں تو پھر اچانک بادِ مخالف کا تھپیڑ آجاتا ہے اور ہر طرف سے موجیں اٹھتی چلی آتی ہیں اور وہ سمجھنے لگتے ہیں کہ اب ہم بالکل گھر گئے ہیں (اور زندہ بچنے کی کوئی امید باقی نہیں رہتی) تو اس وقت دین کو اللہ کے لئے خالص کرکے یعنی بڑے اخلاص کے ساتھ خدا کو پکارتے ہیں کہ (یا اللہ) اگر تو ہمیں اس (مصیبت) سے نجات دے دے تو ہم ضرور تیرے شکر گزار بندوں میں سے ہوں گے۔'),
(1387, 10, 23, 'اور جب اللہ انہیں نجات دے دیتا ہے تو (اپنا سب عہد و پیمان بھول کر) زمین میں ناحق بغاوت اور سرکشی کرنے لگتے ہیں اے لوگو! تمہاری اس بغاوت کا وِزر و وبال تو خود تمہاری ہی جانوں پر پڑ رہا ہے یہ (چند روزہ) زندگی کے مزے ہیں (سو لوٹ لو) پھر تمہاری بازگشت ہماری ہی طرف ہے اس وقت ہم تمہیں بتائیں گے کہ تم کیا کرتے رہے ہو؟'),
(1388, 10, 24, 'دنیاوی زندگی کی مثال تو اس پانی جیسی ہے جسے ہم نے آسمان سے برسایا اور زمین سے وہ نباتات پیدا ہوئیں جن کو انسان اور مویشی سب کھاتے ہیں یہاں تک کہ جب زمین اپنی زیب و زینت کو لے چکی اور فصل کے سبزہ زار سے آراستہ ہوگئی۔ اور اس کے مالک سمجھے کہ انہیں اس (فصل) پر قابو حاصل ہے (جب چاہیں گے کاٹیں گے) تو ایک دم رات یا دن کو ہمارا حکم آگیا۔ تو ہم نے اسے اس طرح بیخ و بن سے کاٹ کے رکھ دیا کہ گویا کل وہاں کچھ تھا ہی نہیں۔ ہم غور و فکر کرنے والوں کیلئے اسی طرح کھول کھول کر اپنی آیتیں پیش کرتے ہیں۔'),
(1389, 10, 25, 'اور اللہ سلامتی کے گھر کی طرف بلاتا ہے اور جسے چاہتا ہے سیدھے راستہ پر لگا دیتا ہے۔'),
(1390, 10, 26, 'جن لوگوں نے (دنیا میں) بھلائی کی ہوگی ان کیلئے (آخرت میں) بھلائی ہوگی اور اس سے بھی کچھ زیادہ ان کے چہروں پر نہ غبار چھائے گا اور نہ ذلت و رسوائی (نمایاں ہوگی) یہی لوگ جنتی ہیں جو اس میں ہمیشہ رہیں گے۔'),
(1391, 10, 27, 'اور جن لوگوں نے برائیاں کمائیں تو برائی کی سزا بھی ویسی ہی برائی ہے اور ان پر ذلت و خواری چھا جائے گی ان کو اللہ (کے قانون مکافاتِ عمل) سے کوئی بچانے والا نہ ہوگا (ان کے چہرے سیاہ ہوں گے کہ) گویا ان کے چہروں کو اندھیری رات کے ٹکڑوں سے ڈھانپ دیا گیا ہے یہی لوگ دوزخی ہیں جو اس میں ہمیشہ رہیں گے۔'),
(1392, 10, 28, 'جس دن ہم سب کو (اپنے حضور) محشور (اکھٹا) کریں گے پھر ان لوگوں سے جنہوں نے شرک کیا تھا کہیں گے کہ تم اور جو تمہارے بنائے ہوئے شریک ہیں اپنی جگہ پر ٹھہر جاؤ۔ پھر ہم ان میں امتیاز پیدا کر دیں گے (الگ الگ کر دیں گے) (اس وقت) ان کے خود ساختہ شریک کہیں گے کہ (اے مشرکو) تم ہماری عبادت تو نہیں کیا کرتے تھے۔'),
(1393, 10, 29, 'آج ہمارے اور تمہارے درمیان گواہی کیلئے اللہ کافی ہے کہ ہم تمہاری عبادت سے بالکل غافل و بے خبر تھے۔'),
(1394, 10, 30, 'اس موقع پر ہر شخص اسے جانچ لے گا جو کچھ اس نے آگے بھیجا ہے (کہ اس کا نتیجہ کیا ہے؟) اور سب اللہ کی بارگاہ میں لوٹائے جائیں گے جو ان کا حقیقی مالک ہے اور جو (شریک) انہوں نے گھڑ رکھے تھے وہ سب غائب ہو جائیں گے (اور ان کے کچھ کام نہ آئیں گے)'),
(1395, 10, 31, 'اے پیغمبر) ان لوگوں سے کہو (پوچھو) وہ کون ہے جو تمہیں آسمان و زمین سے روزی دیتا ہے؟ وہ کون ہے جو سمع و بصر (سننے اور دیکھنے کی طاقت) کا مالک ہے؟ وہ کون ہے جو زندہ کو مردہ سے نکالتا ہے اور مردہ کو زندہ سے؟ اور وہ کون ہے جو کائنات کا انتظام کر رہا ہے؟ اس سوال پر وہ ضرور کہیں گے کہ اللہ پس تم کہو (کہ اگر حقیقت حال یہی ہے) تو تم کیوں نہیں ڈرتے۔'),
(1396, 10, 32, 'یہی اللہ ہے جو تمہارا حقیقی پروردگار ہے پھر حق کے بعد گمراہی کے سوا کیا ہے؟ تمہیں (حق سے) کدھر (غلط سمت) موڑا جا رہا ہے۔'),
(1397, 10, 33, 'اسی طرح تمہارے پروردگار کی بات فاسقوں (نافرمانوں) پر سچی ثابت ہو کر رہی کہ وہ ایمان نہیں لائیں گے۔'),
(1398, 10, 34, '(اے پیغمبر(ص)) آپ کہیے۔ تمہارے ساختہ پرداختہ شریکوں میں سے کوئی ہے جو تخلیقِ کائنات کا آغاز بھی کرے اور (فنا کے بعد) اسے دوبارہ زندہ کرے؟ کہیے! اللہ وہ ہے جو کائنات کو پہلے پیدا بھی کرتا ہے اور پھر اسے دوبارہ زندہ بھی کرے گا تم کدھر الٹے جا رہے ہو؟'),
(1399, 10, 35, '(اے پیغمبر(ص)) ان سے کہیے (پوچھئے) کہ تمہارے بنائے ہوئے (خدا کے) شریکوں میں سے کوئی ایسا ہے جو حق کی طرف راہنمائی کرتا ہو؟ کہئے! وہ اللہ ہی ہے جو حق کی طرف راہنمائی کرتا ہے پھر (بتاؤ) جو حق کی طرف راہنمائی کرتا ہے وہ اس کا زیادہ حقدار ہے کہ اس کی پیروی کی جائے یا وہ جو خود اس وقت تک راہ نہیں پا سکتا جب تک اسے راہ نہ دکھائی جائے؟ تمہیں کیا ہوگیا تم کیسے فیصلے کرتے ہو؟'),
(1400, 10, 36, 'لوگوں میں اکثر ایسے ہیں جو صرف گمان کی پیروی کرتے ہیں حالانکہ گمان حق کی پہچان اور اس تک رسائی حاصل کرنے میں کچھ فائدہ نہیں دیتا (اور نہ ہی یہ حق و یقین سے بے نیاز کرتا ہے) بے شک اللہ اسے خوب جانتا ہے جو کچھ لوگ کر رہے ہیں۔'),
(1401, 10, 37, 'اور یہ قرآن ایسا نہیں ہے کہ اللہ کے سوا کسی اور کی طرف سے گھڑ لیا جائے بلکہ یہ تو تمام سابقہ کتابوں اور وحیوں کی تصدیق ہے اور الکتاب کی تفصیل ہے (یعنی اس میں آسمانی کتابوں کی تعلیم کی تفصیل ہے) اس میں کچھ شک و شبہ نہیں ہے کہ یہ تمام جہانوں کے پروردگار کی طرف سے ہے۔'),
(1402, 10, 38, 'کیا یہ (کافر) لوگ کہتے ہیں کہ اس شخص (پیغمبرِ اسلام(ص)) نے اسے خود گھڑ لیا ہے؟ آپ کہیے! اگر تم اپنے اس الزام میں سچے ہو تو تم اللہ کے سوا اپنی مدد کے لئے جس جس کو بلا سکتے ہو بلا لو اور پھر قرآن کی مانند ایک ہی سورہ لے آؤ۔'),
(1403, 10, 39, 'بلکہ یہ لوگ اس چیز کو جھٹلا دیتے ہیں جس کا علمی احاطہ نہیں کر سکتے اور جس کی تاویل ابھی ان کے سامنے نہیں آئی ہے اسی طرح ان لوگوں نے بھی (حقائق کو) جھٹلایا تھا جو ان سے پہلے تھے تو دیکھو کہ ظلم کرنے والوں کا کیا انجام ہوا؟'),
(1404, 10, 40, 'ان لوگوں میں کچھ تو ایسے ہیں جو اس (قرآن) پر ایمان لاتے ہیں اور کچھ ایسے بھی ہیں جو ایمان نہیں لاتے اور آپ کا پروردگار مفسدین کو خوب جانتا ہے۔'),
(1405, 10, 41, 'اور اگر وہ آپ کو جھٹلائیں تو فرما دیجیے! میرا عمل میرے لئے ہے اور تمہارا تمہارے لئے جو کچھ میں کرتا ہوں اس سے تم بری الذمہ ہو اور جو کچھ تم کرتے ہو اس سے میں بری الذمہ ہوں۔'),
(1406, 10, 42, 'اور (اے رسول) ان لوگوں میں کچھ ایسے بھی ہیں جو آپ کی باتوں کی طرف کان لگاتے ہیں (حالانکہ سنتے نہیں ہیں) تو کیا آپ بہروں کو سنائیں گے اگرچہ وہ عقل سے کام نہ لیتے ہوں؟'),
(1407, 10, 43, 'اور ان میں سے کچھ ایسے ہیں (جو بظاہر) آپ کی طرف دیکھتے ہیں (حالانکہ وہ دیکھتے نہیں ہیں) تو کیا آپ اندھوں کو راہ دکھائیں گے چاہے وہ کچھ نہ دیکھتے ہوں؟'),
(1408, 10, 44, 'یقینا اللہ لوگوں پر ذرہ برابر بھی ظلم نہیں کرتا۔ مگر لوگ خود اپنے اوپر ظلم کرتے ہیں۔'),
(1409, 10, 45, 'جس دن اللہ لوگوں کو اپنی بارگاہ میں جمع کرے گا (اس دن انہیں ایسا معلوم ہوگا کہ) جیسے دن کی ایک گھڑی آپس میں جان پہچان کے لئے ٹھہرے ہوں۔ بے شک وہ لوگ گھاٹے میں ہیں جنہوں نے (مرنے کے بعد) خدا کی بارگاہ میں حضوری کو جھٹلایا اور وہ ہدایت یافتہ نہیں تھے۔'),
(1410, 10, 46, 'اور (اے رسول(ص)) خواہ ہم آپ کو ابھی بعض وہ باتیں (برے اعمال کے نتائج) دکھا دیں جن کا ان (منکرین) سے وعدہ کیا ہے یا اس سے پہلے آپ کو (دنیا سے) اٹھا لیں بہرحال انہیں لوٹ کر آنا تو ہماری ہی طرف ہے پھر اللہ گواہ ہے اس پر جو کچھ وہ کر رہے ہیں۔'),
(1411, 10, 47, 'اور ہر امت کے لئے ایک رسول ہوتا ہے تو جب ان کا رسول ان کے پاس آجاتا ہے تو پورے انصاف کے ساتھ ان کے درمیان فیصلہ کر دیا جاتا ہے اور ان پر ظلم نہیں کیا جاتا۔'),
(1412, 10, 48, 'اور یہ لوگ کہتے ہیں کہ اگر تم سچے ہو تو پھر بتاؤ (عذابِ الٰہی کا) وہ وعدہ کب پورا ہوگا؟'),
(1413, 10, 49, '(اے رسول) کہہ دیجیے! (یہ معاملہ میرے ہاتھ میں نہیں ہے) میں تو خود اپنے نفع و نقصان کا مالک نہیں ہوں۔ مگر جو اللہ چاہے (وہی ہوتا ہے) ہر امت (قوم) کیلئے (مہلت کی) ایک مدت ہوتی ہے جب وہ مدت پوری ہو جاتی ہے (اور مقررہ وقت آجاتا ہے) تو پھر ایک گھڑی کی بھی نہ تاخیر ہو سکتی ہے اور نہ تقدیم۔'),
(1414, 10, 50, '(اے رسول) ان لوگوں سے کہیے کیا تم نے اس بات پر غور کیا ہے کہ اگر اس کا عذاب (اچانک) رات کے وقت تم پر آجائے یا دن دہاڑے (تو تم کیا کروگے؟) آخر وہ کون سی چیز ہے جس کی مجرم جلدی کر رہے ہیں؟'),
(1415, 10, 51, 'کیا جب وہ (عذاب) واقع ہو جائے گا تو تب اس پر تم ایمان لاؤگے؟ (اس پر یقین کروگے؟) (اس وقت تو کہا جائے گا) اب؟ (اس پر یقین کرکے اس سے بچنا چاہتے ہو؟) حالانکہ تم ہی اس کی جلدی کر رہے تھے۔'),
(1416, 10, 52, 'پھر ظالموں سے کہا جائے گا کہ اب دائمی عذاب کا مزہ چکھو۔ تم جو کچھ (کرتوت) کیا کرتے تھے اس (دائمی عذاب) کے سوا تمہیں اور کیا بدلہ دیا جا سکتا ہے؟'),
(1417, 10, 53, 'اور وہ آپ سے پوچھتے ہیں کہ کیا یہ سچ ہے؟ (جو آپ کہتے ہیں؟) کہیے ہاں خدا کی قسم یہ بالکل سچ ہے اور تم (خدا کو) عاجز و بے بس نہیں بنا سکتے۔'),
(1418, 10, 54, 'جو کچھ روئے زمین پر موجود ہے اگر وہ ہر ظالم شخص کے قبضہ میں آجائے تو (وہ عذاب اس قدر سخت ہے) کہ وہ یہ سب کچھ بطور فدیہ دے دے۔ جب یہ لوگ عذابِ الٰہی کو دیکھیں گے تو ندامت و پشیمانی کو دل میں چھپائیں گے (اور اندر ہی اندر پچھتائیں گے) لیکن پورے انصاف کے ساتھ ان کے درمیان فیصلہ کر دیا جائے گا اور ان پر ظلم نہیں کیا جائے گا۔'),
(1419, 10, 55, 'یاد رکھو! جو کچھ آسمانوں اور زمین میں ہے وہ سب اللہ ہی کا ہے اور یہ بھی یاد رکھو کہ اللہ کا وعدہ سچا ہے لیکن اکثر لوگ (یہ حقیقت نہیں جانتے)۔'),
(1420, 10, 56, 'وہی زندگی دیتا ہے اور وہی موت دیتا ہے اور اسی کی طرف تم لوٹائے جاؤگے۔'),
(1421, 10, 57, 'اے لوگو! تمہارے پاس تمہارے پروردگار کی طرف سے (قرآن کی شکل میں) پند و موعظہ، دلوں کی بیماریوں کی شفا اور اہلِ ایمان کے لئے ہدایت و رحمت کا مجموعہ آگیا ہے۔'),
(1422, 10, 58, '(اے رسول) کہیے کہ (یہ سب کچھ) خدا کے خاص فضل اور اس کی خصوصی رحمت کا نتیجہ ہے اس لئے چاہیے کہ لوگ خوشی منائیں یہ چیز اس دولتِ دنیا سے بہتر ہے جسے وہ جمع کرتے ہیں۔'),
(1423, 10, 59, '(اے رسول(ص)) کہیے! لوگو ذرا یہ تو بتاؤ کہ تم نے غور کیا ہے کہ خدا نے تمہارے لئے جو رزق اتارا ہے تو تم نے خود (اپنی خواہشِ نفس سے) کسی کو حرام اور کسی کو حلال قرار دے دیا ہے ان سے پوچھئے! کیا اللہ نے تمہیں اس کی اجازت دی ہے یا تم اللہ پر بہتان باندھ رہے ہو؟'),
(1424, 10, 60, 'جو لوگ خدا پر افترا پردازی کرتے ہیں ان کا قیامت کے دن کے بارے میں کیا گمان ہے؟ بے شک اللہ لوگوں پر فضل و کرم کرنے والا ہے لیکن اکثر لوگ شکر گزاری نہیں کرتے۔'),
(1425, 10, 61, '(اے رسول(ص)) آپ جس حال میں بھی ہوں اور قرآن میں سے جو کچھ بھی پڑھ کر سنائیں اور (اے لوگو) تم بھی جو کوئی کام کرتے ہو ہم ضرور تم پر ناظر و نگران ہوتے ہیں جب تم اس (کام) میں مشغول و منہمک ہوتے ہو۔ اور آپ کے پروردگار سے کوئی ذرہ بھر چیز بھی پوشیدہ نہیں ہے نہ زمین میں اور نہ آسمان میں۔ اور کوئی چیز خواہ ذرہ سے چھوٹی ہو یا اس سے بڑی مگر یہ کہ وہ ایک واضح کتاب میں موجود ہے۔'),
(1426, 10, 62, 'آگاہ ہو کہ جو اللہ کے دوست ہیں ان کے لئے نہ کوئی خوف ہے اور نہ وہ غمگین ہوں گے۔'),
(1427, 10, 63, 'یہ (اللہ کے دوست) وہ لوگ ہیں جو ایمان لائے اور تقویٰ اختیار کیا۔'),
(1428, 10, 64, 'ان کے لئے دنیا کی زندگی میں بھی بشارت ہے اور آخرت میں بھی۔ اللہ کے کلمات میں کوئی تبدیلی نہیں ہو سکتی یہی بڑی کامیابی ہے۔'),
(1429, 10, 65, 'اور (اے رسول) ان (کافروں) کی (معاندانہ) باتیں آپ کو غمزدہ نہ کریں۔ یقینا تمام کی تمام عزت اللہ کے لئے ہے (وہ جسے چاہے عزت و ذلت دے) وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1430, 10, 66, 'آگاہ ہو جاؤ وہ تمام ہستیاں جو آسمان میں ہیں اور وہ سب جو زمین میں ہیں اللہ ہی کے مملوک اور اسی کے تابعِ فرمان ہیں۔ اور جو (مشرک) لوگ اللہ کے سوا خود ساختہ شریکوں کو پکارتے ہیں وہ کاہے کی پیروی کر رہے ہیں؟ وہ محض اپنے گمان کی پیروی کر رہے ہیں اور صرف اپنی اٹکلیں دوڑا رہے ہیں۔'),
(1431, 10, 67, 'وہ (خدا) وہی ہے جس نے تمہارے لئے رات بنائی تاکہ اس میں آرام کرو اور دیکھنے کے لئے دن کو روشن بنایا (تاکہ اس میں کام کرو) بے شک اس میں ان لوگوں کیلئے (قدرتِ خدا کی) نشانیاں ہیں جو (کلامِ حق) سنتے ہیں۔'),
(1432, 10, 68, 'یہ لوگ کہتے ہیں کہ اللہ نے اپنا کوئی بیٹا بنایا ہے حالانکہ وہ پاک و بے نیاز ہے اور اس کیلئے زمین و آسمان کی ساری کائنات ہے۔ تمہارے پاس تو تمہاری بات کی کوئی دلیل بھی نہیں ہے۔ کیا تم لوگ خدا پر وہ الزام لگاتے ہو جس کا تمہیں علم بھی نہیں ہے۔'),
(1433, 10, 69, '(اے رسول) کہہ دیجئے جو لوگ خدا پر جھوٹا بہتان باندھتے ہیں وہ کبھی فلاح نہیں پائیں گے۔'),
(1434, 10, 70, '(ان کے لئے صرف) دنیا میں تھوڑا سا فائدہ ہے پھر ان کی بازگشت ہماری طرف ہے پھر ہم ان کو ان کے کفر کی پاداش میں سخت عذاب کا مزہ چکھائیں گے۔'),
(1435, 10, 71, 'اور (اے رسول(ص)) انہیں (کفار کو) نوح کا حال سنائیں جب کہ انہوں نے اپنی قوم سے کہا تھا کہ اے میری قوم! اگر (تمہارے درمیان) میرا قیام کرنا اور آیاتِ الٰہیہ کا یاد دلانا (اور ان کے ساتھ پند و نصیحت کرنا) شاق گزرتا ہے تو میرا بھروسہ صرف اللہ پر ہے تو تم (میرے خلاف) اپنے خود ساختہ شریکوں کو بھی ساتھ ملا کر کوئی متفقہ فیصلہ کرلو اور (پھر خوب سوچ سمجھ لو تاکہ) تمہارا فیصلہ تم پر پوشیدہ نہ رہے اور تمہیں اس میں کوئی تذبذب باقی نہ رہے پھر میرے ساتھ جو کرنا ہے کر گزرو اور مجھے ذرا بھی مہلت نہ دو۔'),
(1436, 10, 72, 'اب اگر (اس کے باوجود) تم روگردانی کرتے ہو (تو تمہارا ہی نقصان ہے میرا کیا نقصان ہے) میں نے تم سے کوئی اجر تو نہیں مانگا۔ میرا اجر تو اللہ کے ذمے ہے مجھے تو حکم دیا گیا ہے کہ میں اس کے فرمانبردار بندوں سے ہو کر رہوں۔'),
(1437, 10, 73, '(بایں ہمہ) ان لوگوں نے انہیں جھٹلایا پس ہم نے انہیں اور جو کشتی میں ان کے ساتھ سوار تھے نجات دی اور انہیں (غرق ہونے والوں کا) جانشین بنایا اور جنہوں نے ہماری آیتوں کو جھٹلایا تھا ان سب کو غرق کر دیا تو دیکھو جن کو متنبہ کیا گیا تھا اور ڈرایا گیا تھا (مگر وہ نہ مانے) ان کا کیا انجام ہوا؟'),
(1438, 10, 74, 'پھر ہم نے ان (نوح(ع)) کے بعد متعدد رسولوں کو ان کی قوموں کی طرف بھیجا اور وہ ان کے پاس کھلی کھلی نشانیاں (معجزے) لے کر آئے اس پر بھی وہ تیار نہ تھے کہ جس چیز کو وہ پہلے جھٹلا چکے ہیں (اب معجزے دیکھ کر) اس پر ایمان لائیں۔ ہم یونہی ظلم و تعدی کرنے والوں کے دلوں پر مہر لگا دیتے ہیں۔'),
(1439, 10, 75, 'پھر ہم نے ان (رسولوں) کے بعد موسیٰ (ع)و ہارون (ع)کو اپنی نشانیوں (معجزوں) کے ساتھ فرعون اور اس کے سرداروں کی طرف بھیجا تو انہوں نے تکبر کیا اور وہ لوگ (عادی) مجرم تھے۔'),
(1440, 10, 76, 'اور جب ہماری طرف سے ان کے پاس حق آیا (اور حقیقت واضح ہوگئی) تو انہوں نے کہہ دیا کہ یہ تو کھلا ہوا جادو ہے۔'),
(1441, 10, 77, 'موسیٰ (ع) نے کہا کیا تم حق کے بارے میں ایسی بات کہتے ہو جبکہ وہ تمہارے پاس آیا؟ کیا یہ جادو ہے؟ حالانکہ جادوگر کبھی فلاح و کامیابی نہیں پا سکتے۔'),
(1442, 10, 78, 'انہوں نے (جواب میں) کہا کہ کیا تم اس لئے ہمارے پاس آئے ہو کہ ہمیں اس راہ سے ہٹا دو جس پر ہم نے اپنے باپ دادوں کو پایا ہے۔ اور اس سر زمین میں تم دونوں (بھائیوں) کی بڑائی (سرداری) قائم ہو جائے اور ہم تم دونوں کی بات تسلیم کرنے والے نہیں ہے۔'),
(1443, 10, 79, 'اور فرعون نے کہا (میرے ملک کے) تمام ماہر جادوگروں کو میرے پاس لاؤ۔'),
(1444, 10, 80, 'اور جب جادوگر آگئے تو موسیٰ نے ان سے کہا (جادو کے سامان سے) تمہیں جو کچھ پھینکنا ہے پھینکو۔'),
(1445, 10, 81, 'جب وہ (رسیاں وغیرہ) پھینک چکے۔ تو موسیٰ نے کہا جو کچھ تم لائے ہو یہ جادو ہے (نہ وہ جو میں لایا ہوں) یقینا اللہ اسے بھی ملیامیٹ کر دے گا کیونکہ قانونِ قدرت ہے کہ اللہ مفسدین کے کام کو بننے نہیں دیتا۔'),
(1446, 10, 82, 'اور اللہ حق کو اپنے کلمات کے ذریعہ سے ضرور حق ثابت کر دکھاتا ہے اگرچہ مجرموں کو ناپسند ہی ہو۔'),
(1447, 10, 83, 'پس موسیٰ پر ان کی قوم کے چند نوجوانوں کے ایک گروہ کے سوا اور کوئی ایمان نہیں لایا اور وہ (ایمان لانے والے) بھی فرعون اور اپنی قوم کے سرداروں سے ڈرتے ہوئے ایمان لائے۔ کہ کہیں وہ انہیں آزمائش (اور مصیبت) میں نہ ڈال دے اور بے شک فرعون بڑا سرکش (بادشاہ) تھا اور حد سے بڑھ جانے والوں میں سے تھا۔'),
(1448, 10, 84, 'اور موسیٰ نے کہا اے میری قوم! اگر تم واقعی اللہ پر ایمان لائے ہو تو پھر اسی پر بھروسہ کرو۔ اگر تم فی الحقیقت مسلمان ہو۔'),
(1449, 10, 85, 'انہوں نے (جواب میں) کہا ہم اللہ ہی پر بھروسہ کرتے ہیں (اور دعا کرتے ہیں) اے ہمارے پروردگار ہمیں ظالم لوگوں کیلئے آزمائش کا موجب نہ بنا (ان کے ظلم کا تختہ مشق نہ بنا)۔'),
(1450, 10, 86, 'اور ہمیں اپنی رحمت سے ظالموں (کے پنجۂ ظلم) سے نجات عطا فرما۔'),
(1451, 10, 87, 'اور ہم نے موسیٰ اور ان کے بھائی (ہارون) کی طرف وحی کی کہ مصر میں اپنی قوم کے لئے چند گھر مہیا کرو (بناؤ) اور اپنے گھروں کو قبلہ رخ بنائیں (یا اپنے گھروں کو ہی قبلہ بنائیں) اور نماز قائم کریں اور (اے موسیٰ اہلِ ایمان کو) کامیابی کی بشات دیں۔'),
(1452, 10, 88, 'اور موسیٰ نے (دعا مانگتے ہوئے) کہا اے ہمارے پروردگار تو نے فرعون اور اس کے سرداروں کو دنیاوی زندگی میں زیب و زینت (کی چیزوں) اور بہت سے مال و دولت سے نوازا ہے۔ اے پروردگار! اس کا نتیجہ اور انجام یہ ہے کہ وہ (تیرے بندوں کو) تیرے راستہ سے بہکاتے ہیں۔ اے ہمارے مالک، ان کے مالوں کو نابود کر دے اور ان کے دلوں کو سخت کر دے تاکہ جب تک دردناک عذاب نہ دیکھ لیں ایمان نہ لائیں (اور اس وقت ایمان کا لانا سودمند نہ ہوگا)۔'),
(1453, 10, 89, 'اللہ تعالیٰ نے فرمایا تم دونوں کی دعا قبول کر لی گئی ہے سو تم ثابت قدم رہو۔ اور ان لوگوں کی پیروی نہ کرو۔ جو (حق و حقیقت کا) علم نہیں رکھتے۔'),
(1454, 10, 90, 'اور ہم نے بنی اسرائیل کو دریا سے پار اتار دیا پھر فرعون اور اس کے لشکر نے سرکشی اور ظلم و تعدی سے ان کا پیچھا کیا یہاں تک کہ جب وہ (فرعون) (دریا میں) غرق ہونے لگا تو کہنے لگا کہ میں ایمان لاتا ہوں (مانتا ہوں) کہ اس ہستی کے سوا کوئی الٰہ نہیں ہے جس پر بنی اسرائیل ایمان لائے ہیں اور میں مسلمانوں (فرمانبرداروں) میں سے ہوں۔'),
(1455, 10, 91, '(اس سے کہا گیا) اب؟ (ایمان لاتا ہے؟) حالانکہ اس سے پہلے تو مسلسل نافرمانی کرتا رہا ہے اور تو فسادیوں میں سے ایک (بڑا) مفسد تھا۔'),
(1456, 10, 92, 'پس آج ہم صرف تیرے بدن (لاش) کو بچائیں گے تاکہ تو اپنے بعد آنے والوں کے لئے (قدرت و عبرت کی) ایک نشانی بن جائے (اگرچہ) لوگوں کی اکثریت ہماری نشانیوں سے غافل ہی رہتی ہے۔'),
(1457, 10, 93, 'اور ہم نے (حسب الوعدہ) بنی اسرائیل کو (رہنے کے لئے) بہت اچھا ٹھکانا دیا اور پاکیزہ چیزوں سے ان کی روزی کا انتظام کیا پس جب تک ان کے پاس توراۃ وغیرہ کی شکل میں علم نہیں آگیا تب تک انہوں نے باہم اختلاف نہیں کیا۔ (پھر جان بوجھ کر اختلاف کیا)۔ یقینا آپ کا پروردگار قیامت کے دن ان کے درمیان فیصلہ کرے گا ان باتوں میں جن میں وہ اختلاف کیا کرتے تھے۔'),
(1458, 10, 94, 'اگر (بالفرض) آپ کو اس (قرآن میں) کچھ شک ہے جو ہم نے آپ کی طرف نازل کیا ہے تو پھر ان لوگوں (اہل کتاب) سے پوچھ لو جو آپ سے پہلے کتابیں (توراۃ و انجیل وغیرہ) پڑھتے رہتے ہیں۔ بے شک آپ کے پروردگار کی طرف سے آپ کے پاس حق آیا ہے لہٰذا ہرگز شک کرنے والوں میں سے نہ ہونا۔'),
(1459, 10, 95, 'اور نہ ہی ان لوگوں میں سے ہونا جنہوں نے آیاتِ الٰہیہ کو جھٹلایا ورنہ نقصان اٹھانے والوں میں سے ہو جاؤگے۔'),
(1460, 10, 96, 'بے شک وہ لوگ جن پر آپ کے پروردگار کی بات ثابت ہو چکی ہے۔'),
(1461, 10, 97, 'سو اگرچہ دنیا جہاں کی تمام نشانیاں (معجزات) ان کے سامنے آجائیں مگر وہ جب تک دردناک عذاب اپنی آنکھوں سے نہ دیکھ لیں تب تک وہ کبھی ایمان نہیں لائیں گے۔'),
(1462, 10, 98, 'قومِ یونس کے سوا کیوں کوئی ایسی بستی نہیں ہوئی جو (عذاب دیکھ کر) ایمان لائی ہو اور اس کے ایمان نے اسے فائدہ بھی پہنچایا ہو؟ جب وہ (قومِ یونس والے) ایمان لائے تو ہم نے اس دنیاوی زندگی میں ان سے رسوائی والا عذاب ٹال دیا اور ان کو ایک مدت تک زندگی کے سرو سامان سے فائدہ اٹھانے کی مہلت دے دی۔'),
(1463, 10, 99, 'اگر آپ کا پروردگار (جبراً) چاہتا تو روئے زمین کے سب لوگ ایمان لے آتے۔ تو کیا آپ لوگوں کو مجبور کرتے ہیں کہ وہ مؤمن ہو جائیں۔'),
(1464, 10, 100, 'کوئی بھی متنفس ایسا نہیں ہے جو اللہ کے اذن کے بغیر ایمان لے آئے اور اللہ ان لوگوں پر (کفر و شرک) کی نجاست ڈال دیتا ہے جو عقل سے کام نہیں لیتے۔'),
(1465, 10, 101, '(اے رسول(ص)) کہیئے۔ (اے لوگو) غور سے دیکھو آسمانوں اور زمین میں کیا کیا (عجائبات قدرت) موجود ہیں (اور کس بات کی گواہی دے رہے ہیں؟) مگر جو لوگ ایمان نہیں لاتے ان کو نہ نشانیاں کوئی فائدہ دیتی ہیں اور نہ ڈرانے والوں کی تنبیہیں۔'),
(1466, 10, 102, 'کیا یہ لوگ ان جیسے (عذاب کے) دنوں کا انتظار کر رہے ہیں جو ان لوگوں پر آئے جو ان سے پہلے تھے (کہ ایسے ہی ان پر بھی آئیں؟) ان سے کہہ دیجیئے کہ پھر تم انتظار کرو۔ میں بھی تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔'),
(1467, 10, 103, 'پھر (جب ایسے دن آتے ہیں) تو ہم اپنے رسولوں کو اور ایمان لانے والوں کو نجات دے دیتے ہیں اسی طرح ہمارے ذمہ حق ہے کہ ہم اہلِ ایمان کو نجات دیا کریں۔'),
(1468, 10, 104, 'کہیے! اے لوگو اگر تمہیں میرے دین (اسلام) کے بارے میں کچھ شک ہے تو (سن لو) میں ان (بتوں) کی عبادت نہیں کرتا جن کی تم اللہ کو چھوڑ کر عبادت کرتے ہو بلکہ میں اس اللہ کی عبادت کرتا ہوں جو تمہاری روح قبض کرتا ہے اور مجھے حکم دیا گیا ہے کہ میں اہلِ ایمان کے زمرہ میں سے ہوں۔'),
(1469, 10, 105, 'نیز (مجھے حکم دیا گیا ہے کہ) ہر طرف سے اپنا رخ موڑ کر سیدھا دین کی طرف رکھوں اور مشرکوں میں سے نہ ہوں۔'),
(1470, 10, 106, 'نیز (مجھے حکم دیا گیا ہے کہ) اللہ کے سوا کسی ایسی ہستی کو نہ پکاریں جو تمہیں نہ فائدہ پہنچا سکے اور نہ نقصان اور اگر ایسا کیا تو پھر ظالموں میں سے ہو جاؤ گے۔'),
(1471, 10, 107, 'اور اگر اللہ تمہیں کوئی نقصان پہنچانا چاہے تو اس کے سوا کوئی اس کا دور کرنے والا نہیں ہے اور اگر وہ تمہارے ساتھ بھلائی کرنا چاہے تو اس کے فضل کو کوئی روکنے والا نہیں ہے وہ اپنے بندوں میں سے جس پر چاہے اپنا فضل و کرم فرمائے وہ بڑا بخشنے والا، رحم کرنے والا ہے۔'),
(1472, 10, 108, '(اے رسول) کہہ دیجئے! اے لوگو تمہارے پروردگار کی طرف سے حق آچکا ہے پس جو ہدایت حاصل کرے گا تو وہ اپنے فائدہ کے لئے کرے گا اور جو گمراہ ہوگا اس کا نقصان بھی اسی کو ہوگا میں تم پر نگہبان و نگران نہیں ہوں۔'),
(1473, 10, 109, 'اور (اے رسول) آپ اس وحی کی پیروی کریں جو آپ کی طرف کی جاتی ہے اور (مخالفین کی ایذا رسانیوں پر) صبر کریں یہاں تک کہ اللہ (آپ کے اور ان کے درمیان) فیصلہ کر دے۔ جو فیصلہ کرنے والوں میں سب سے بہتر فیصلہ کرنے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف، لام، را، یہ وہ کتاب ہے جس کی آیتیں محکم اور مضبوط بنائی گئی ہیں اور پھر تفصیل کے ساتھ کھول کھول کر بیان کی گئی ہیں یہ اس کی طرف سے ہے جو بڑا حکمت والا، بڑا باخبر ہے۔'),
(1475, 11, 2, '(اس کا خلاصہ یہ ہے کہ) اللہ کے سوا کسی کی عبادت (بندگی) نہ کرو بلاشبہ میں اس کی طرف سے تمہیں ڈرانے والا اور خوشخبری دینے والا ہوں۔'),
(1476, 11, 3, 'اور یہ کہ اپنے پروردگار سے مغفرت طلب کرو پھر اس کی بارگاہ میں توبہ کرو (اس کی طرف رجوع کرو) وہ تمہیں مقررہ مدت (تک زندگی کے) اچھے فوائد سے بہرہ مند کرے گا۔ اور ہر صاحبِ فضل (زیادہ عمل کرنے والے) کو اس کے درجہ کے مطابق عطا فرمائے گا اور اگر تم نے روگردانی کی تو میں تمہارے بارے میں ایک بڑے دن کے عذاب سے ڈرتا ہوں۔'),
(1477, 11, 4, 'تم سب کو اللہ ہی کی طرف لوٹ کر جانا ہے اور وہ ہر چیز پر پوری قدرت رکھنے والا ہے۔'),
(1478, 11, 5, 'آگاہ ہو جاؤ یہ (منافق) لوگ اپنے سینوں کو دوہرا کر رہے ہیں تاکہ اس (اللہ یا رسول) سے چھپ جائیں مگر یاد رکھو جب یہ لوگ اپنے (سارے) کپڑے اپنے اوپر اوڑھ لیتے ہیں تو (تب بھی) وہ (اللہ) وہ بھی جانتا ہے جو کچھ وہ چھپاتے ہیں اور وہ بھی جو کچھ وہ ظاہر کرتے ہیں (کیونکہ) بے شک وہ تو ان رازوں سے بھی خوف واقف ہے جو سینوں کے اندر ہیں۔'),
(1479, 11, 6, 'اور زمین میں کوئی چلنے پھرنے والا جانور نہیں ہے مگر یہ کہ اس کا رزق خدا کے ذمے ہے اور وہ جانتا ہے کہ اس کا مستقل ٹھکانہ کہاں ہے اور اس کے سونپے جانے (کم رہنے) کی جگہ کہاں ہے؟ سب کچھ ایک واضح کتاب میں محفوظ ہے۔'),
(1480, 11, 7, 'اور وہی وہ ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا اور (اس سے پہلے) اس کا عرش پانی پر تھا (یہ سب کچھ کیوں پیدا کیا؟) تاکہ تمہیں آزمائے کہ تم میں سب سے بہتر عمل کرنے والا کون ہے؟ اور (اے رسول(ص)) اگر آپ ان سے کہیں کہ تم مرنے کے بعد (دوبارہ) اٹھائے جاؤگے تو کافر لوگ ضرور کہیں گے۔ یہ تو کھلا ہوا جادو ہے۔'),
(1481, 11, 8, 'اور اگر ہم ان پر عذاب نازل کرنے میں ایک مقررہ مدت تک تاخیر بھی کر دیں تو وہ ضرور یہ کہیں گے کہ کون سی چیز نے اسے روک رکھا ہے؟ یاد رکھو جس دن ان پر وہ (عذاب) آئے گا تو پھر اسے پھیرا نہ جا سکے گا۔ اور انہیں وہ (عذاب) گھیر لے گا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(1482, 11, 9, 'اور اگر ہم انسان کو اپنی طرف سے رحمت کا مزہ چکھائیں (کسی نعمت سے نوازیں) اور پھر اسے اس سے چھین لیں تو وہ بڑا مایوس اور بڑا ناشکرا ہو جاتا ہے۔'),
(1483, 11, 10, 'اور اگر ہم کسی سختی و تکلیف کے پہنچنے کے بعد کسی نعمت و راحت کا مزہ چکھائیں تو (غافل ہوکر) کہہ اٹھتا ہے کہ میری تمام برائیاں چلی گئیں (دکھ درد دور ہوگئے)۔ بالیقین وہ (انسان) بڑا خوش ہونے والا، بڑا اترانے والا ہے۔'),
(1484, 11, 11, 'مگر ہاں جو لوگ صبر کرتے ہیں اور نیک عمل کرتے ہیں (وہ ایسے نہیں ہیں بلکہ) یہی وہ ہیں جن کے لئے بخشش ہے اور بہت بڑا اجر ہے۔'),
(1485, 11, 12, '(اے پیغمبر(ص)) آپ کی طرف جو وحی کی جاتی ہے تو کیا آپ (کفار کی باتوں سے متاثر ہوکر) اس کا کچھ حصہ چھوڑ دیں گے اور کیا آپ کا سینہ تنگ ہو جائے گا کیونکہ وہ کہتے ہیں کہ ان پر کوئی خزانہ کیوں نہیں اتارا گیا؟ یا ان کے ساتھ کوئی فرشتہ کیوں نہیں آیا؟ (آپ ہرگز ایسا نہیں کر سکتے اور نہ ہی کرنا چاہیئے کیونکہ) آپ تو بس (عذابِ الٰہی سے) ڈرانے والے ہیں اور اللہ ہی ہر چیز پر نگہبان ہے۔'),
(1486, 11, 13, 'یا وہ یہ کہتے ہیں کہ اس شخص (آپ) نے یہ (قرآن) خود گھڑ لیا ہے؟ آپ فرمائیے اگر تم اس دعویٰ میں سچے ہو تو تم بھی اس جیسی گھڑی ہوئی دس سورتیں لے آؤ۔ اور بے شک اللہ کے سوا جس کو (اپنی مدد کیلئے) بلانا چاہتے ہو بلا لو۔'),
(1487, 11, 14, '(اے مسلمانو) اب اگر وہ (کفار) تمہاری دعوت پر لبیک نہ کہیں تو پھر سمجھ لو کہ جو کچھ (قرآن) نازل کیا گیا ہے وہ اللہ کے علم و قدرت سے اتارا گیا ہے اور یہ بھی (سمجھ لو) کہ اللہ کے سوا کوئی الٰہ نہیں ہے (اے کفار بتاؤ) کیا اب اسلام لاؤگے۔ اور یہ حقیقت تسلیم کروگے؟'),
(1488, 11, 15, 'اور جو صرف دنیا کی زندگی اور اس کی زیب و زینت چاہتے ہیں تو ہم انہیں ان کی سعی و عمل کی پوری پوری جزا دیتے ہیں۔ اور ان کے ساتھ کوئی کمی نہیں کی جاتی۔'),
(1489, 11, 16, 'یہ وہ (بدنصیب) لوگ ہیں جن کیلئے آخرت (کی زندگی) میں دوزخ کی آگ کے سوا کچھ نہیں ہے۔ اور انہوں نے اس (دنیا) میں جو کچھ کیا تھا وہ سب اکارت ہو جائے گا اور وہ جو کچھ کرتے تھے وہ سب باطل ہو جائے گا۔'),
(1490, 11, 17, 'کیا جو شخص اپنے پروردگار کی طرف سے (اپنی صداقت کی) کھلی ہوئی دلیل رکھتا ہے اور جس کے پیچھے ایک گواہ بھی ہے جو اسی کا جزء ہے اور اس سے پہلے موسیٰ کی کتاب (توراۃ) بھی رحمت و راہنمائی کے طور پر آچکی ہے (اور اس کی تصدیق بھی کر رہی ہے) یہ لوگ اس پر ایمان لائیں گے اور جو مختلف گروہوں میں سے اس کا انکار کرے گا اس کی وعدہ گاہ آتشِ دوزخ ہے خبردار تم! اس کے بارے میں شک میں نہ پڑنا وہ آپ کے پروردگار کی طرف سے برحق ہے مگر اکثر لوگ (حق پر) ایمان نہیں لاتے۔'),
(1491, 11, 18, 'اور اس شخص سے بڑھ کر کون ظالم ہوگا جو اللہ پر جھوٹا بہتان باندھے ایسے لوگ اپنے پروردگار کی بارگاہ میں پیش کئے جائیں گے۔ اور گواہ گواہی دیں گے کہ یہ ہیں وہ لوگ جنہوں نے اپنے رب پر جھوٹا بہتان باندھا۔ خبردار ان ظالموں پر اللہ کی لعنت ہے۔'),
(1492, 11, 19, 'جو اللہ کی راہ سے (اس کے بندوں کو) روکتے ہیں اور اسے ٹیڑھا کرنا چاہتے ہیں اور آخرت کا انکار کرتے ہیں۔'),
(1493, 11, 20, 'یہ لوگ اللہ تعالیٰ کو عاجز کرنے والے نہیں تھے اور نہ خدا کے علاوہ ان کا کوئی مددگار اور کارساز تھا۔ ان کو دوگنا عذاب ہوگا۔ (کیونکہ) یہ نہ (حق بات) سن سکتے ہیں۔ اور نہ (حقیقت) دیکھ سکتے تھے۔'),
(1494, 11, 21, 'یہی وہ لوگ ہیں جنہوں نے اپنے آپ کو خسارے میں مبتلا کیا اور وہ (خلافِ حق) جو افترا پردازیاں کیا کرتے تھے وہ سب ان سے گم ہوگئیں۔'),
(1495, 11, 22, 'بلاشبہ یہی لوگ آخرت میں سب سے زیادہ گھاٹا اٹھانے والے ہوں گے۔'),
(1496, 11, 23, 'بے شک جو لوگ ایمان لائے اور نیک عمل کئے اور عجز و نیاز کے ساتھ اپنے پروردگار کے حضور جھک گئے یہی لوگ جنتی ہیں جو ہمیشہ ہمیشہ اس میں رہیں گے۔'),
(1497, 11, 24, 'ان دونوں فریقوں کی مثال ایسی ہے جیسے ایک اندھا، بہرا ہو اور دوسرا آنکھ و کان والا۔ (بتاؤ) یہ دونوں برابر ہو سکتے ہیں؟ کیا تم غور و فکر نہیں کرتے (اور نصیحت حاصل نہیں کرتے؟)'),
(1498, 11, 25, 'بے شک ہم نے نوح(ع) کو ان کی قوم کی طرف بھیجا (انہوں نے کہا لوگو!) میں تمہیں کھلا ہوا (عذابِ الٰہی سے) ڈرانے والا ہوں۔'),
(1499, 11, 26, 'اور یہ کہ اللہ کے سوا کسی اور کی عبادت نہ کرو (ورنہ) میں تمہارے بارے میں ایک ہولناک دن کے عذاب سے ڈرتا ہوں۔'),
(1500, 11, 27, 'اس پر ان کی قوم کے سردار جو کافر تھے کہنے لگے کہ (اے نوح) ہم تو تمہیں اس کے سوا کچھ نہیں دیکھتے کہ تم ہم جیسے ایک انسان ہو۔ اور ہم تو یہی دیکھ رہے ہیں کہ جن لوگوں نے آپ کی پیروی کی ہے وہ ہم میں سے بالکل رذیل لوگ ہیں اور انہوں نے بھی بے سوچے سمجھے سرسری رائے سے کی ہے اور ہم تم میں اپنے اوپر کوئی برتری نہیں دیکھتے بلکہ تم لوگوں کو جھوٹا خیال کرتے ہیں۔'),
(1501, 11, 28, 'نوح(ع) نے کہا اے میری قوم! کیا تم نے (اس بات پر) غور کیا ہے کہ اگر میں اپنے پروردگار کی طرف سے روشن دلیل رکھتا ہوں؟ اور اس نے مجھے اپنی رحمت بھی عطا فرمائی ہے۔ جو تمہیں سجھائی نہیں دیتی۔ تو کہا ہم زبردستی اسے تمہارے اوپر مسلط کر سکتے ہیں۔ جبکہ تم اسے ناپسند کرتے ہو؟'),
(1502, 11, 29, 'اے میری قوم! میں اس (تبلیغِ حق) پر تم سے کوئی مالی معاوضہ طلب نہیں کرتا۔ میرا معاوضہ اللہ کے ذمے ہے اور جو لوگ ایمان لائے ہیں (وہ تمہاری نگاہ میں جس قدر پست ہوں) میں ان کو اپنے پاس سے نکال نہیں سکتا۔ بے شک وہ اپنے پروردگار کی بارگاہ میں حاضری دینے والے ہیں البتہ میں دیکھتا ہوں کہ تم جہالت سے کام لینے والے لوگ ہو۔'),
(1503, 11, 30, 'اے میری قوم! اگر میں ان کو اپنے پاس سے نکال دوں تو اللہ کے مقابلہ میں کون میری مدد کرے گا۔ کیا تم اتنا بھی غور نہیں کرتے (اور نصیحت قبول نہیں کرتے؟)'),
(1504, 11, 31, 'اور (دیکھو) میں تم سے یہ نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں اور نہ یہ کہتا ہوں کہ میں غیب کا علم رکھتا ہوں اور نہ یہ کہتا ہوں کہ میں کوئی فرشتہ ہوں اور نہ ہی میں یہ کہتا ہوں کہ جن لوگوں کو تم حقارت کی نگاہ سے دیکھتے ہو کہ اللہ انہیں کوئی بھلائی عطا نہیں کرے گا اللہ ہی بہتر جانتا ہے جو کچھ ان کے دلوں میں ہے اگر میں ایسا کہوں تو ظالموں میں سے ہو جاؤں گا۔'),
(1505, 11, 32, 'ان لوگوں نے کہا اے نوح! تم نے ہم سے بحث و تکرار کی ہے اور بہت کر لی (اس کا کوئی فائدہ نہیں ہے) اگر تم سچے ہو تو وہ (عذاب) ہمارے پاس لاؤ جس کی تم ہمیں دھمکی دیتے ہو۔'),
(1506, 11, 33, 'نوح(ع) نے کہا وہ (عذاب) تو اللہ ہی لائے گا جب چاہے گا اور تم اسے عاجز نہیں کر سکتے۔'),
(1507, 11, 34, 'اگر اللہ تمہیں گمراہی میں چھوڑ دینا چاہے (اور اس طرح تمہیں ہلاک کرنا چاہے تو) میں جس قدر تمہیں نصیحت کرنا چاہوں میری نصیحت تمہیں کوئی فائدہ نہیں پہنچا سکتی۔ وہی تمہارا پروردگار ہے اور اسی کی طرف تمہیں لوٹ کر جانا ہے۔'),
(1508, 11, 35, 'کیا وہ یہ کہتے ہیں کہ (آپ) نے (یہ قرآن) خود گھڑ لیا ہے۔ تو کہیئے! اگر میں نے یہ گھڑا ہے تو اس جرم کا وبال مجھ پر ہے اور جو جرم تم کر رہے ہو اس سے میں بری الذمہ ہوں۔'),
(1509, 11, 36, 'اور نوح(ع) کی طرف وحی کی گئی کہ ان لوگوں کے سوا جو ایمان لا چکے ہیں تمہاری قوم میں سے اور کوئی ایمان نہیں لائے گا۔ پس تم اس پر غمگین نہ ہو جو کچھ یہ لوگ کر رہے ہیں۔'),
(1510, 11, 37, 'اور ہماری نگرانی اور ہماری وحی کے مطابق ایک کشتی بناؤ۔ اور ظالموں کے بارے میں مجھ سے کوئی بات (سفارش) نہ کرنا (کیونکہ) یقینا یہ سب لوگ غرق ہو جانے والے ہیں۔'),
(1511, 11, 38, 'چنانچہ نوح(ع) کشتی بنانے لگے اور جب بھی ان کی قوم کے سردار ان کے پاس سے گزرتے تو وہ ان کا مذاق اڑاتے وہ (نوح) کہتے اگر (آج) تم ہمارا مذاق اڑاتے ہو تو (کل کلاں) ہم بھی تمہارا اسی طرح مذاق اڑائیں گے جس طرح تم اڑا رہے ہو۔'),
(1512, 11, 39, 'عنقریب تمہیں معلوم ہو جائے گا کہ کس پر عذاب آتا ہے جو اسے رسوا کر دے گا اور کس پر دائمی عذاب نازل ہوتا ہے۔'),
(1513, 11, 40, 'یہاں تک کہ جب ہمارا حکم آگیا۔ اور تنور نے جوش مارا (ابل پڑا) تو ہم نے (نوح(ع) سے) کہا کہ ہر قسم کے جوڑوں میں سے دو دو (نر و مادہ) کو اس (کشتی) میں سوار کر لو۔ اور اپنے گھر والوں کو بھی بجز ان کے جن کی (ہلاکت کی) بات پہلے طئے ہو چکی ہے نیز ان کو بھی (سوار کر لو) جو ایمان لا چکے ہیں اور بہت ہی تھوڑے لوگ ان کے ساتھ ایمان لائے تھے۔'),
(1514, 11, 41, 'نوح(ع) نے کہا (کشتی میں) سوار ہو جاؤ اللہ کے نام کے سہارے اس کا چلنا بھی ہے اور اس کا ٹھہرنا بھی بے شک میرا پروردگار بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1515, 11, 42, 'اور وہ (کشتی) انہیں ایسی موجوں (لہروں) میں سے لئے جا رہی تھی جو پہاڑوں جیسی تھیں اور نوح(ع) نے اپنے بیٹے کو آواز دی جوکہ الگ ایک گوشہ میں (کھڑا) تھا۔ اے میرے بیٹے! ہمارے ساتھ (کشتی میں) سوار ہو جا۔ اور کافروں کے ساتھ نہ ہو۔'),
(1516, 11, 43, 'اس نے کہا میں ابھی کسی پہاڑ پر پناہ لے لوں گا جو مجھے پانی سے بچائے گا نوح(ع) نے کہا (بیٹا) آج اللہ کے امر (عذاب) سے کوئی بچانے والا نہیں ہے۔ سوائے اس کے جس پر وہ (اللہ) رحم فرمائے۔ اور پھر (اچانک) ان کے درمیان موج حائل ہوگئی پس وہ ڈوبنے والوں میں سے ہوگیا۔'),
(1517, 11, 44, 'اور ارشاد ہوا اے زمین! اپنا پانی نگل لے اور اے آسمان تھم جا۔ اور پانی اتر گیا اور معاملہ طئے کر دیا گیا اور کشی کوہِ جودی پر ٹھہر گئی اور کہا گیا ہلاکت ہو ظلم کرنے والوں کے لئے۔'),
(1518, 11, 45, 'اور نوح(ع) نے اپنے پروردگار کو پکارا اور کہا اے میرے پروردگار میرا بیٹا میرے اہل میں سے ہے اور یقینا تیرا وعدہ سچا ہے اور تو سب فیصلہ کرنے والوں سے بہتر فیصلہ کرنے والا ہے۔'),
(1519, 11, 46, 'ارشاد ہوا۔ اے نوح وہ تیرے اہل میں سے نہیں ہے وہ تو مجسم عملِ بد ہے پس جس چیز کا تمہیں علم نہیں ہے اس کے بارے میں مجھ سے سوال نہ کر میں تمہیں نصیحت کرتا ہوں کہ جاہلوں میں سے نہ ہو جانا۔'),
(1520, 11, 47, 'عرض کیا اے میرے پروردگار! میں اس بات سے تیری بارگاہ میں پناہ مانگتا ہوں کہ میں تجھ سے ایسی چیز کا سوال کروں جس کا مجھے علم نہیں ہے۔ اور اگر تو نے مجھے نہ بخشا اور مجھ پر رحم نہ کیا تو میں نقصان اٹھانے والوں میں سے ہو جاؤں گا۔'),
(1521, 11, 48, 'ارشاد ہوا۔ اے نوح(ع) اترو۔ ہماری طرف سے سلامتی اور برکتیں ہوں تم پر اور ان گروہوں پر جو تمہارے ساتھ ہیں (یا بالفاظ مناسب تمہارے ساتھ والوں سے پیدا ہونے والی نسلوں پر) اور تمہارے بعد کچھ ایسے گروہ بھی ہوں گے جنہیں ہم کچھ مدت کیلئے (دنیاوی) فائدہ اٹھانے کا موقع دیں گے پھر (انجامِ کار) انہیں ہماری طرف سے دردناک عذاب پہنچے گا۔'),
(1522, 11, 49, '(اے رسول) یہ (قصہ) ان غیب کی خبروں میں سے ہے جسے ہم وحی کے ذریعہ سے آپ تک پہنچا رہے ہیں اس سے پہلے نہ آپ کو ان کا (تفصیلی) علم تھا اور نہ آپ کی قوم کو۔ آپ صبر کریں۔ بے شک (اچھا) انجام پرہیزگاروں کے لئے ہے۔'),
(1523, 11, 50, 'اور ہم نے قومِ عاد کی طرف ان کے بھائی ہود کو بھیجا۔ انہوں نے کہا اے میری قوم! اللہ ہی کی عبادت کرو اس کے سوا تمہارا کوئی الٰہ نہیں ہے تم محض افترا پردازی کر رہے ہو۔'),
(1524, 11, 51, 'اے میری قوم! میں اس (تبلیغ) پر تم سے کوئی مالی معاوضہ نہیں مانگتا۔ میرا معاوضہ تو اس کے ذمے ہے جس نے مجھے پیدا کیا ہے تم کیوں عقل سے کام نہیں لیتے۔'),
(1525, 11, 52, 'اور اے میری قوم! اپنے پروردگار سے مغفرت طلب کرو۔ اور اس کی بارگاہ میں توبہ کرو (اس کی طرف رجوع کرو) وہ تم پر آسمان سے موسلادھار بارش برسائے گا۔ اور تمہاری (موجودہ) قوت میں مزید اضافہ کر دے گا۔ اور (میری دعوت سے) جرم کرتے ہوئے منہ نہ موڑو۔'),
(1526, 11, 53, 'انہوں نے کہا اے ہود(ع) تم ہمارے پاس کوئی کھلی ہوئی دلیل لے کر نہیں آئے۔ اور ہم تمہارے کہنے سے اپنے خداؤں کو چھوڑنے والے نہیں ہیں۔ اور نہ ہی ہم آپ کی بات تسلیم کرنے والے ہیں۔'),
(1527, 11, 54, 'ہم تو بس یہی کہتے ہیں کہ ہمارے خداؤں میں سے کسی خدا نے آپ کو کچھ (دماغی) نقصان پہنچا دیا ہے۔ انہوں (ہود) نے ان لوگوں کو کہا کہ میں اللہ کو گواہ ٹھہراتا ہوں۔ اور تم بھی گواہ رہو۔ کہ میں ان سے بیزار ہوں جن کو تم نے (حقیقی خدا کو چھوڑ کر) اس کا شریک بنا رکھا ہے۔'),
(1528, 11, 55, 'تم سب مل کر میرے خلاف اپنی تدبیریں کرلو۔ اور مجھے (ذرا بھی) مہلت نہ دو۔'),
(1529, 11, 56, 'میرا بھروسہ اللہ پر ہے جو میرا بھی پروردگار ہے اور تمہارا بھی۔ کوئی بھی چلنے پھرنے والا ایسا جاندار نہیں جس کی چوٹی اس کے قبضۂ قدرت میں نہ ہو یقینا میرا پروردگار (عدل و انصاف کی) سیدھی راہ پر ہے۔'),
(1530, 11, 57, 'اس پر بھی اگر تم روگردانی کرو تو جو (پیغام) دے کر مجھے تمہاری طرف بھیجا گیا تھا وہ میں نے تمہیں پہنچا دیا ہے (اور حجت تمام کر دی ہے) اب اگر تم نے یہ پیغام قبول نہ کیا تو میرا پروردگار تمہاری جگہ کسی اور قوم کو لائے گا۔ اور تم اس کو کچھ بھی نقصان نہیں پہنچا سکو گے بے شک میرا پروردگار ہر چیز پر نگہبان و نگران ہے۔'),
(1531, 11, 58, 'اور جب ہمارا حکم (عذاب) آگیا تو ہم نے اپنی خاص رحمت سے ہود(ع) کو اور ان لوگوں کو جو ان کے ساتھ ایمان لائے تھے نجات دے دی اور انہیں ایک سخت عذاب سے بچا لیا۔'),
(1532, 11, 59, 'اور یہ ہے قومِ عاد جس نے جان بوجھ کر اپنے پروردگار کی آیتوں (نشانیوں) کا انکار کیا اور اس کے رسولوں کی نافرمانی کی اور ہر سرکش اور دشمن (حق) کی اطاعت کی۔'),
(1533, 11, 60, '(اس کی پاداش میں) اس دنیا میں ان کے پیچھے لعنت لگا دی گئی اور آخرت میں بھی آگاہ ہو جاؤ کہ قومِ عاد نے اپنے پروردگار کا انکار کیا۔ سو ہلاکت و بربادی ہے ہود کی قومِ عاد کے لئے۔'),
(1534, 11, 61, 'اور ہم نے قومِ ثمود کی طرف ان کے بھائی صالح(ع) کو بھیجا انہوں نے کہا اے میری قوم! اللہ کی عبادت کرو اس کے سوا تمہارا کوئی الٰہ نہیں ہے اسی نے تمہیں زمین سے پیدا کیا اور اسی نے تمہیں اس میں آباد کیا لہٰذا اس سے مغفرت طلب کرو۔ اور اس کی جناب میں توبہ کرو بے شک میرا پروردگار قریب بھی ہے اور مجیب (دعاؤں کا قبول کرنے والا) بھی۔'),
(1535, 11, 62, 'انہوں نے کہا اے صالح! اس سے پہلے تو تو ہمارے اندر ایک ایسا شخص تھا جس سے (بڑی) امیدیں وابستہ تھیں کیا تم ہمیں اس سے روکتے ہو کہ ہم ان (معبودوں) کی پرستش نہ کریں جن کی ہمارے باپ دادا پرستش کیا کرتے تھے؟ اور ہمیں تو اس بات میں بڑا ہی شک ہے جس کی طرف تم ہمیں دعوت دیتے ہو۔'),
(1536, 11, 63, 'صالح(ع) نے کہا اے میری قوم! کیا تم نے کبھی اس بات پر غور کیا ہے کہ اگر میں اپنے پروردگار کی طرف سے ایک روشن دلیل پر ہوں اور اس نے مجھے اپنی خاص رحمت بھی عطا فرمائی ہے تو اگر میں اس کی نافرمانی کروں تو اس کے مقابلہ میں کون میری مدد کرے گا؟ تم تو میرے خسارے میں اضافہ کے سوا اور کچھ نہیں کر سکتے۔'),
(1537, 11, 64, 'اور اے میری قوم! یہ اللہ کی خاص اونٹنی ہے جو تمہارے لئے ایک خاص نشانی (معجزہ) ہے پس اسے آزاد چھوڑ دو۔ تاکہ اللہ کی زمین سے کھائے اور خبردار اسے کسی قسم کی اذیت نہ پہنچانا۔ ورنہ بہت جلد عذاب تمہیں آپکڑے گا۔'),
(1538, 11, 65, 'پس ان لوگوں نے اس کی کونچیں کاٹ دیں (اسے مار ڈالا) تب صالح(ع) نے کہا کہ اب تین دن تک اپنے گھروں میں فائدہ اٹھا لو (کھا پی لو) یہ وہ وعدہ ہے جو جھوٹا نہیں ہے۔'),
(1539, 11, 66, 'پس جب ہمارا حکم (عذاب) آگیا تو ہم نے اپنی رحمت سے صالح اور اس کے ساتھ ایمان لانے والوں کو نجات دے دی اور اس دن کی رسوائی سے بچا لیا بے شک پروردگار بڑا طاقتور، بڑا زبردست ہے۔'),
(1540, 11, 67, 'اور وہ لوگ جنہوں نے ظلم کیا تھا ان کو ایک زوردار کڑک نے آپکڑا وہ اس طرح اپنے گھروں میں اوندھے پڑے رہ گئے۔'),
(1541, 11, 68, 'کہ گویا وہ کبھی ان گھروں میں بسے ہی نہ تھے آگاہ ہو جاؤ قومِ ثمود(ع) نے اپنے پروردگار کا انکار کیا آگاہ ہو کہ ہلاکت و بربادی ہے قوم ثمود کے لئے۔'),
(1542, 11, 69, 'بے شک ہمارے بھیجے ہوئے (فرشتے) ابراہیم(ع) کے پاس خوشخبری لے کر آئے اور کہا تم پر سلام انہوں نے (جواب میں) کہا تم پر بھی سلام پھر دیر نہیں لگائی کہ ایک بھنا ہوا بچھڑا لے آئے۔'),
(1543, 11, 70, 'مگر جب دیکھا کہ ان کے ہاتھ اس (کھانے) کی طرف نہیں بڑھ رہے تو انہیں اجنبی خیال کیا اور دل ہی دل میں ان سے خوف محسوس کیا۔ انہوں نے کہا آپ ڈریں نہیں۔ ہم تو (اللہ کی طرف سے) قومِ لوط کی طرف بھیجے گئے ہیں۔'),
(1544, 11, 71, 'اور ان کی بیوی (سارہ) پاس کھڑی ہوئی تھیں وہ ہنس پڑیں بس ہم نے انہیں اسحاق کی خوشخبری دی اور اسحاق کے بعد یعقوب(ع) کی۔'),
(1545, 11, 72, '(اس پر) وہ کہنے لگیں۔ ہائے میری مصیبت! کیا اب میرے ہاں اولاد ہوگی جبکہ میں بوڑھی ہوگئی ہوں۔ اور یہ میرے شوہر بھی بوڑھے ہو چکے ہیں یہ تو بڑی عجیب بات ہے ۔'),
(1546, 11, 73, 'انہوں (فرشتوں) نے کہا کیا تم اللہ کے کام پر تعجب کرتی ہو؟ اللہ کی رحمت اور اس کی برکتیں ہوں تم پر اے (ابراہیم(ع) کے) گھر والو بے شک وہ بڑا ستائش کیا ہوا ہے، بڑی شان والا ہے۔'),
(1547, 11, 74, 'جب ابراہیم (ع) کا خوف دور ہوگیا اور انہیں خوشخبری مل گئی تو قومِ لوط(ع) کے بارے میں ہم سے جھگڑنے لگے۔'),
(1548, 11, 75, 'بے شک ابراہیم(ع) بڑے بردبار، بڑے نرم دل اور ہر حال میں ہماری طرف رجوع کرنے والے تھے۔'),
(1549, 11, 76, '(ہم نے کہا) اے ابراہیم(ع) اس بات کو چھوڑ دیں تمہارے پروردگار کا حکم (عذاب) آچکا ہے یقینا ان لوگوں پر وہ عذاب آکے رہے گا۔ جو پلٹایا نہیں جا سکتا۔'),
(1550, 11, 77, 'اور جب ہمارے فرستادے لوط(ع) کے پاس آئے تو وہ ان کی وجہ سے مغموم ہوئے اور تنگدل ہوگئے اور کہا بڑا سخت دن ہے۔'),
(1551, 11, 78, 'ان کی قوم کے لوگ (نوخیز مہمانوں کی آمد کی خبر سن کر) دوڑتے ہوئے ان کے پاس آئے وہ پہلے سے ہی ایسی بدکاریوں کے عادی ہو رہے تھے لوط(ع) نے کہا اے میری قوم! یہ میری بیٹیاں موجود ہیں یہ تمہارے لئے مناسب اور پاکیزہ ہیں سو اللہ سے ڈرو۔ اور میرے مہمانوں کے معاملہ میں مجھے رسوا نہ کرو۔ کیا تم میں کوئی بھلامانس نہیں ہے۔'),
(1552, 11, 79, 'ان لوگوں نے کہا تمہیں معلوم ہے کہ ہمیں تمہاری بیٹیوں سے کوئی سروکار نہیں ہے اور تم اچھی طرح جانتے ہو کہ ہم کیا چاہتے ہیں؟'),
(1553, 11, 80, 'لوط نے کہا کاش مجھے تمہارے مقابلہ کی طاقت ہوتی یا یہ کہ میں کسی مضبوط پایہ کا سہارا لے سکتا۔'),
(1554, 11, 81, 'تب انہوں (مہمانوں) نے کہا اے لوط(ع) ہم آپ کے پروردگار کے بھیجے ہوئے (فرشتے) ہیں یہ لوگ ہرگز آپ تک نہیں پہنچ سکیں گے تم رات کے کسی حصہ میں اپنے اہل و عیال کو لے کر نکل جاؤ۔ سوائے اپنی بیوی کے اور خبردار تم میں سے کوئی پیچھے مڑ کر نہ دیکھے۔ کیونکہ اس (بیوی) پر وہی عذاب نازل ہونے والا ہے جو ان لوگوں پر نازل ہونے والا ہے اور ان (کے عذاب) کا مقرر وقت صبح ہے کیا صبح (بالکل) قریب نہیں ہے؟'),
(1555, 11, 82, 'پھر جب ہمارا حکم (عذاب) آپہنچا۔ تو ہم نے اس سرزمین کو تہہ و بالا کر دیا اور اس پر سنگِ گِل یعنی پکی ہوئی مٹی کے پتھر مسلسل برسائے۔'),
(1556, 11, 83, 'جن میں سے ہر کنکر تمہارے پروردگار کے ہاں سے نشان زدہ تھا اور یہ (پتھر) ظالموں سے کچھ دور نہیں ہیں۔'),
(1557, 11, 84, 'اور ہم نے اہلِ مدین کی طرف ان کے بھائی شعیب کو بھیجا انہوں نے کہا اے میری قوم! اللہ کی عبادت کرو۔ اس کے سوا تمہارا کوئی الٰہ نہیں ہے اور ناپ تول میں کمی نہ کرو۔ میں تمہیں اچھے حال میں دیکھ رہا ہوں۔ مجھے اندیشہ ہے کہ (کفرانِ نعمت اور غلط کاری سے) عذاب کا ایسا دن نہ آجائے جو سب کو گھیر لے۔'),
(1558, 11, 85, 'اور اے میری قوم! عدل و انصاف کے ساتھ ناپ تول پورا کیا کرو۔ اور لوگوں کو ان کی چیزیں کم نہ دو۔ اور زمین میں شر و فساد نہ پھیلاتے پھرو۔'),
(1559, 11, 86, 'اللہ کا بقیہ تمہارے لئے بہتر ہے اگر تم ایماندار ہو؟ اور میں تم پر نگران و نگہبان نہیں ہوں۔'),
(1560, 11, 87, 'ان لوگوں نے کہا اے شعیب(ع)! کیا تمہاری نماز تمہیں حکم دیتی ہے کہ ہم ان (معبودوں) کو چھوڑ دیں جن کی ہمارے باپ دادا پرستش کیا کرتے تھے یا اپنے اپنے اموال میں اپنی مرضی کے مطابق تصرف نہ کریں؟ بس تم ہی عاقل و بردبار اور نیکوکار آدمی ہو؟'),
(1561, 11, 88, 'آپ(ع) نے کہا میری قوم! مجھے غور کرکے بتاؤ اگر میں اپنے پروردگار کی طرف سے (اپنی صداقت کی) روشن دلیل رکھتا ہوں اور پھر اس نے اپنی بارگاہ سے مجھے اچھی روزی بھی دی ہو (تو پھر کس طرح تبلیغِ حق نہ کروں؟) اور میں یہ بھی نہیں چاہتا کہ جن چیزوں سے تمہیں روکتا ہوں۔ خود ان کے خلاف چلوں۔ میں تو صرف اصلاح (احوال) چاہتا ہوں جہاں تک میرے بس میں ہے۔ اور میری توفیق تو صرف اللہ کی طرف سے ہے اسی پر میرا بھروسہ ہے اور اسی کی طرف (اپنے تمام معاملات میں) رجوع کرتا ہوں۔'),
(1562, 11, 89, 'اور اے میری قوم! میری مخالفت تمہیں اس بات پر آمادہ نہ کرے کہ تم ایسے کام کرو کہ جن کی پاداش میں تم پر بھی وہی آفت آپڑے جو نوح(ع) یا ہود(ع) یا صالح(ع) کی قوم پر آئی تھی۔ اور قومِ لوط(ع) تو تم سے کچھ دور نہیں ہے۔'),
(1563, 11, 90, 'اور اپنے پروردگار سے مغفرت طلب کرو اور پھر اس کی جناب میں توبہ کرو (اس کی طرف رجوع کرو) بے شک میرا پروردگار بڑا رحم کرنے والا، بڑا محبت کرنے والا ہے۔'),
(1564, 11, 91, 'ان لوگوں نے کہا اے شعیب! تم جو کچھ کہتے ہو اس میں سے اکثر باتیں تو ہماری سمجھ میں نہیں آتیں اور ہم تمہیں اپنے درمیان ایک کمزور آدمی دیکھتے ہیں اور اگر تمہارا قبیلہ نہ ہوتا تو ہم تمہیں سنگسار کر دیتے۔ اور تم ہم پر غالب نہیں ہو۔'),
(1565, 11, 92, 'آپ نے کہا! اے میری قوم! کیا میرا قبیلہ تمہارے نزدیک اللہ سے زیادہ معزز ہے؟ (اللہ سے زیادہ طاقتور ہے) جسے تم نے پسِ پشت ڈال دیا ہے (یاد رکھو) تم جو کچھ کر رہے ہو میرا پروردگار اس کا (علمی) احاطہ کئے ہوئے ہے۔'),
(1566, 11, 93, 'اور اے میری قوم! تم اپنی جگہ (اور اپنے طریقہ) پر عمل کئے جاؤ میں اپنی جگہ (اور اپنے طریقہ) پر عمل کر رہا ہوں عنقریب تمہیں معلوم ہو جائے گا کہ کس پر وہ عذاب آتا ہے جو اسے رسوا کر دے گا؟ اور کون جھوٹا ہے؟ اور تم انتظار کرو۔ میں بھی تمہارے ساتھ انتظار کرتا ہوں۔'),
(1567, 11, 94, 'اور جب ہمارا حکم (عذاب) آیا تو ہم نے اپنی خاص رحمت سے شیعب کو اور ان کو جو آپ کے ساتھ ایمان لائے تھے نجات دے دی اور جن لوگوں نے ظلم کیا تھا ان کو ایک خوفناک کڑک نے آپکڑا۔ پس وہ اپنے گھروں میں اوندھے پڑے رہ گئے۔'),
(1568, 11, 95, 'گویا وہ ان میں کبھی آباد ہوئے ہی نہ تھے۔ آگاہ ہو۔ ہلاکت و بربادی ہے مدین کیلئے جس طرح ہلاکت تھی قومِ ثمود کے لیے۔'),
(1569, 11, 96, 'اور یقیناً ہم نے موسیٰ کو واضح نشانیوں (معجزات) اور واضح سند (عصا) کے ساتھ بھیجا۔'),
(1570, 11, 97, 'فرعون اور اس کے سرداروں کی طرف مگر انہوں نے فرعون کے حکم کی پیروی کی حالانکہ فرعون کا حکم درست نہ تھا (بلکہ بالکل غلط تھا)۔'),
(1571, 11, 98, 'قیامت کے دن وہ اپنی قوم کے آگے آگے ہوگا۔ اور اس طرح انہیں آتشِ دوزخ میں پہنچا دے گا کیا ہی اترنے کی بری ہے وہ جگہ جہاں وہ اتارے جائیں گے۔'),
(1572, 11, 99, 'اس دنیا میں لعنت ان کے پیچھے لگا دی گئی اور قیامت کے دن (دوزخ) کیا ہی برا عطیہ ہے جو انہیں عطا کیا جائے گا۔'),
(1573, 11, 100, 'اے رسول(ص) یہ ان بستیوں کی چند خبریں ہیں جو ہم آپ کے سامنے بیان کر رہے ہیں۔ ان (بستیوں) میں سے کچھ تو اب تک قائم ہیں اور بعض کی فصل کٹ چکی ہے (بالکل اجڑ گئی ہیں)۔'),
(1574, 11, 101, 'اور ہم نے ان پر ظلم نہیں کیا بلکہ خود انہوں نے اپنے اوپر ظلم کیا۔ اور جب آپ کے پروردگار کا حکم (عذاب) آگیا تو انہیں ان کے ان خداؤں نے کچھ بھی فائدہ نہ پہنچایا جنہیں وہ اللہ کو چھوڑ کر پکارا کرتے تھے اور انہوں نے ان کی ہلاکت میں اضافہ کے سوا اور کچھ نہ کیا۔'),
(1575, 11, 102, 'اور آپ کا پروردگار جب ظالم بستیوں کو پکڑتا ہے (یعنی ان کے باشندوں کو ان کے ظلم و تعدی کی وجہ سے پکڑتا ہے) تو اس کی پکڑ ایسی ہوتی ہے بے شک اس کی پکڑ بڑی دردناک (اور) سخت ہوتی ہے۔'),
(1576, 11, 103, 'بے شک اس بات میں (عبرت کی) نشانی ہے اس شخص کے لئے جو آخرت کے عذاب سے ڈرتا ہے وہ ایسا دن ہے جس میں سب لوگ اکھٹے کئے جائیں گے اور وہ (سب کے) پیش ہونے کا دن ہے۔'),
(1577, 11, 104, 'اور ہم اسے صرف ایک مدت (کے پورا ہونے) تک مؤخر کر رہے ہیں۔'),
(1578, 11, 105, 'تو اس (خدا) کی اجازت کے بغیر کوئی متنفس بات نہیں کر سکے گا (اس دن) کچھ لوگ بدبخت ہوں گے اور کچھ نیک بخت۔'),
(1579, 11, 106, 'جب وہ دن آئے گا تو جو بدبخت ہیں وہ آتشِ دوزخ میں ہوں گے ان کیلئے وہاں چیخنا چلانا ہوگا۔'),
(1580, 11, 107, 'وہ اس میں ہمیشہ رہیں گے جب تک آسمان و زمین قائم ہیں۔ اِلا یہ کہ آپ کا پروردگار (کچھ اور) چاہے بے شک آپ کا پروردگار (قادرِ مختار ہے) جو چاہتا ہے کر گزرتا ہے۔'),
(1581, 11, 108, 'اور جو نیک بخت ہیں وہ بہشت میں ہوں گے وہ اس میں ہمیشہ ہمیشہ رہیں گے جب تک آسمان و زمین قائم ہیں۔ اِلا یہ کہ آپ کا پروردگار (کچھ اور) چاہے (یہ وہ) عطیہ ہے جو کبھی منقطع نہ ہوگا۔'),
(1582, 11, 109, '(اے رسول(ص)) جن کی یہ (مشرک لوگ) عبادت کرتے ہیں آپ ان کے (باطل پرست ہونے) میں کسی شک میں نہ رہیں یہ اسی طرح (کورکورانہ) عبادت کر رہے ہیں جس طرح ان سے پہلے ان کے باپ دادا کرتے تھے اور ہم ان کو ان (کے نتائجِ اعمال) کا پورا پورا حصہ دیں گے جس میں کوئی کمی نہیں کی جائے گی۔'),
(1583, 11, 110, 'اور بے شک ہم نے موسیٰ کو کتاب عطا کی تو اس میں اختلاف کیا گیا اور اگر آپ کے پروردگار کی طرف سے ایک بات پہلے سے طئے نہ کر دی گئی ہوتی (کہ سزا دینے میں جلدی نہیں کرنی) تو کبھی کا ان کے درمیان فیصلہ کر دیا جاتا اور وہ اس (عذاب) کے بارے میں تردد انگیز شک میں مبتلا ہیں۔'),
(1584, 11, 111, 'اور یقیناً آپ کا پروردگار ان سب کو ان کے اعمال کا (بدلہ) پورا پورا دے گا بے شک جو کچھ یہ لوگ کر رہے ہیں خدا اس سے پوری طرح باخبر ہے۔'),
(1585, 11, 112, 'پس (اے رسول(ص)) جس طرح آپ کو حکم دیا گیا ہے آپ خود اور وہ لوگ بھی جنہوں نے (کفر و عصیان سے) توبہ کر لی ہے اور آپ کے ساتھ ہیں (راہِ راست پر) ثابت قدم رہیں اور حد سے نہ بڑھیں (یقین کرو کہ) تم جو کچھ کرتے ہو وہ (اللہ) اسے دیکھ رہا ہے۔'),
(1586, 11, 113, 'اور خبردار! ظالموں کی طرف مائل نہ ہونا ورنہ (ان کی طرح) تمہیں بھی آتشِ دوزخ چھوئے گی اور اللہ کے سوا تمہارا کوئی سرپرست نہ ہوگا اور نہ ہی تمہاری کوئی مدد کی جائے گی۔'),
(1587, 11, 114, '(اے نبی) دن کے دونوں کناروں (صبح و شام) اور رات کے کچھ حصوں میں نماز قائم کریں بے شک نیکیاں برائیوں کو دور کر دیتی ہیں یہ نصیحت ہے ان لوگوں کیلئے جو نصیحت حاصل کرتے ہیں۔'),
(1588, 11, 115, 'اور آپ صبر کیجئے! بے شک اللہ نیکوکاروں کا اجر ضائع نہیں کرتا۔'),
(1589, 11, 116, 'ان قوموں اور نسلوں میں جو تم سے پہلے گزر چکی ہیں معدودے چند افراد کے سوا جن کو ہم نے نجات دی تھی ایسے سمجھدار اور نیکوکار لوگ کیوں نہ ہوئے جو لوگوں کو زمین میں فساد پھیلانے سے روکتے اور ظالم لوگ تو اسی عیش و عشرت کے پیچھے پڑے رہے جو ان کو دی گئی تھی اور یہ سب لوگ مجرم تھے۔'),
(1590, 11, 117, 'اور آپ کا پروردگار ایسا نہیں ہے کہ وہ ظلم کرکے (ناحق) بستیوں کو ہلاک کر دے جبکہ ان کے باشندے نیکوکار اور اصلاح کرنے والے ہوں۔'),
(1591, 11, 118, 'اگر آپ کا پروردگار (زبردستی) چاہتا تو سب لوگوں کو ایک ہی امت (گروہ) بنا دیتا مگر اپنی حکمتِ کاملہ سے اس نے ایسا نہیں کیا لہٰذا یہ لوگ ہمیشہ آپس میں اختلاف کرتے رہیں گے۔'),
(1592, 11, 119, 'سوائے اس کے جس پر آپ کا پروردگار رحم فرمائے اور اسی (رحمت) کے لئے تو ان کو پیدا کیا ہے۔ اور آپ کے پروردگار کی بات پوری ہوگئی کہ میں جہنم کو تمام جنوں اور انسانوں سے بھر دوں گا۔'),
(1593, 11, 120, 'اور (اے رسول) پیغمبروں کے قصوں میں سے یہ سب قصے جو ہم آپ کو سناتے ہیں (یہ اس لئے ہے کہ) ہم آپ کے دل کو مضبوط کر دیں اور پھر ان (قصوں) کے اندر تمہارے پاس حق آگیا۔ اور اہلِ ایمان کیلئے پند و موعظہ اور نصیحت و یاددہانی ہے۔'),
(1594, 11, 121, 'اور (اے پیغمبر(ص)) جو لوگ ایمان نہیں لاتے ان سے کہہ دیجیے! تم اپنی جگہ اور اپنے طور پر کام کرو (اور ہم) اپنی جگہ اور اپنے طور پر کام کر رہے ہیں۔'),
(1595, 11, 122, 'تم بھی (نتیجہ کا) انتظار کرو ہم بھی انتظار کرتے ہیں۔'),
(1596, 11, 123, 'اور آسمانوں اور زمین کا علمِ غیب اللہ ہی سے مخصوص ہے اور اسی کی طرف تمام معاملات کی بازگشت ہے تو آپ(ص) اسی کی عبادت کریں اور اسی پر بھروسہ کریں اور آپ کا پروردگار اس سے غافل نہیں ہے جو کچھ تم کرتے ہو۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، را۔ یہ ایک واضح کتاب کی آیتیں ہیں۔'),
(1598, 12, 2, 'بے شک ہم نے اسے عربی زبان کا قرآن بنا کر نازل کیا ہے تاکہ تم اسے سمجھ سکو۔'),
(1599, 12, 3, '(اے رسول(ص)) ہم اس قرآن کے ذریعہ سے جو ہم نے آپ کی طرف وحی کیا ہے ایک بہترین قصہ بیان کرتے ہیں اگرچہ اس سے پہلے آپ غافلوں میں سے تھے۔'),
(1600, 12, 4, '(اس وقت کو یاد کرو) جب یوسف نے اپنے باپ سے کہا کہ میں نے خواب میں گیارہ ستاروں اور سورج و چاند کو دیکھا ہے کہ میرے سامنے سجدہ کر رہے ہیں (جھک رہے ہیں)۔'),
(1601, 12, 5, 'آپ (ع) نے کہا اے میرے بیٹے! اپنا یہ خواب اپنے بھائیوں کے سامنے بیان نہ کرنا کہیں وہ (تمہیں تکلیف پہنچانے کے لئے) کوئی سازش نہ کرنے لگ جائیں بے شک شیطان انسان کا کھلا ہوا دشمن ہے۔'),
(1602, 12, 6, 'اور اسی طرح تمہارا پروردگار تمہیں منتخب کرے گا اور تمہیں تعبیرِ خواب کا علم عطا فرمائے گا اور تم پر نیز یعقوب(ع) کے خانوادہ پر اسی طرح اپنی نعمت پوری کرے گا جس طرح اس سے پہلے تمہارے دادا، پردادا ابراہیم و اسحاق پر اپنی نعمت پوری کر چکا ہے بے شک آپ کا پروردگار بڑا علم والا، بڑا حکمت والا ہے۔'),
(1603, 12, 7, 'بے شک یوسف اور اس کے بھائیوں کے قصہ میں پوچھنے والوں کے لیے (غیب کی) بڑی نشانیاں ہیں۔'),
(1604, 12, 8, 'جب انہوں (یوسف کے سوتیلے بھائیوں) نے آپس میں کہا کہ باوجودیکہ ہم پوری جماعت ہیں مگر یوسف اور اس کا (سگا) بھائی (بن یامین) ہمارے باپ کو زیادہ پیارے ہیں بے شک ہمارے باپ کھلی ہوئی غلطی پر ہیں۔'),
(1605, 12, 9, 'یوسف کو قتل کر دو یا کسی اور سرزمین پر پھینک دو۔ تاکہ تمہارے باپ کا رخ (توجہ) صرف تمہاری طرف ہو جائے اس کے بعد تم بھلے آدمی ہو جاؤگے (تمہارے سب کام بن جائیں گے)۔'),
(1606, 12, 10, 'ان (برادرانِ یوسف) میں سے ایک نے کہا کہ یوسف کو قتل نہ کرو البتہ اگر کچھ کرنا ہے تو اسے کسی اندھے کنویں میں ڈال دو۔ مسافروں کا کوئی قافلہ اسے اٹھا لے جائے گا۔'),
(1607, 12, 11, '(اس قرارداد کے بعد) انہوں نے کہا اے ہمارے والد! کیا بات ہے کہ آپ یوسف کے بارے میں ہمارا اعتبار نہیں کرتے حالانکہ ہم تو اس کے بڑے خیرخواہ ہیں۔'),
(1608, 12, 12, 'وہ کچھ کھائے پیئے اور کھیلے کودے ہم اس کی حفاظت کے ذمہ دار ہیں۔'),
(1609, 12, 13, 'آپ (ع) نے کہا تمہارا اسے اپنے ہمراہ لے جانا مجھے غمگین کرتا ہے اور مجھے یہ اندیشہ بھی ہے کہ تم غفلت کرو اور اسے کوئی بھیڑیا کھا جائے۔'),
(1610, 12, 14, 'انہوں نے کہا کہ اگر اسے بھیڑیا کھا جائے جبکہ ہماری پوری جماعت (حفاظت کے لئے) موجود ہے تو پھر تو ہم گھاٹا اٹھانے والے (اور بالکل گئے گزرے) ہوں گے۔'),
(1611, 12, 15, 'پھر جب وہ (اصرار کرکے) اسے لے گئے اور سب نے اس بات پر اتفاق کیا کہ اسے اندھے کنویں میں ڈال دیں (چنانچہ ایسا ہی کیا)۔ اور ہم نے (اس وقت) اس (یوسف) کی طرف وحی کی کہ (ایک وقت آئے گا کہ) تم ان لوگوں کو ان کی یہ حرکت جتاؤگے جبکہ انہیں اس کا شعور نہیں ہوگا۔'),
(1612, 12, 16, 'اور وہ شام کے وقت اپنے والد کے پاس روتے ہوئے آئے۔'),
(1613, 12, 17, 'اور کہا اے ہمارے والد! ہم تو آپس میں دوڑ میں لگ گئے اور یوسف کو اپنے سامان کے پاس چھوڑ گئے بس ایک بھیڑیا آیا اور اسے کھا گیا۔ اور اگرچہ ہم سچے ہی ہوں مگر آپ تو ہماری بات کا یقین نہیں کرتے۔'),
(1614, 12, 18, 'اور وہ اس (یوسف (ع)) کے کُرتے پر جھوٹا خون لگا کر لائے۔ آپ (ع) نے کہا (یہ جھوٹ ہے) بلکہ تمہارے نفسوں نے (اپنے بچاؤ کیلئے) یہ بات گھڑ کر تمہیں خوشنما دکھا دی ہے (بہرحال اس سانحہ پر) صبر کرنا ہی بہتر ہے اور جو کچھ تم بیان کر رہے ہو اس سلسلہ میں اللہ ہی سے مدد مانگی جا سکتی ہے۔'),
(1615, 12, 19, 'اور ایک قافلہ آیا (اور وہاں اترا) تو انہوں نے (پانی لانے کیلئے) اپنا سقا بھیجا۔ پس اس نے جونہی اپنا ڈول ڈالا۔ (تو یوسف اس میں بیٹھ گئے جب ڈول کھینچا) تو پکار اٹھا۔ مبارک باد یہ تو ایک لڑکا ہے اور ان لوگوں نے اسے سرمایۂ تجارت سمجھ کر چھپا رکھا اور وہ لوگ جو کچھ کر رہے تھے اللہ اس سے خوب واقف تھا۔'),
(1616, 12, 20, 'اور (ادھر برادرانِ یوسف بھی آپہنچے اور یوسف کو اپنا غلام ظاہر کرکے) بالکل کم قیمت پر یعنی گنتی کے چند درہموں کے عوض بیج ڈالا۔ اور ان (بھائیوں) کو اس میں کوئی دلچسپی نہ تھی (بلکہ اس سے بیزار تھے)۔'),
(1617, 12, 21, 'اور پھر (اس قافلہ والوں سے) مصر کے جس شخص (عزیزِ مصر) نے اسے (دوبارہ) خریدا تھا اس نے اپنی بیوی (زلیخا) سے کہا اسے عزت کے ساتھ رکھنا ہو سکتا ہے کہ ہمیں کچھ فائدہ پہنچائے یا ہم اسے اپنا بیٹا بنا لیں! اور اسی طرح (حکمتِ عملی سے) ہم نے اس (یوسف (ع)) کو سر زمینِ مصر میں تمکین دی (زمین ہموار کی تاکہ اسے اقتدار کیلئے منتخب کریں) اور خوابوں کی تعبیر کا علم عطا کریں اور اللہ اپنے ہر کام پر غالب ہے (اور اس کے انجام دینے پر قادر ہے) لیکن اکثر لوگ (یہ حقیقت) نہیں جانتے۔'),
(1618, 12, 22, 'اور جب وہ (یوسف) پوری جوانی کو پہنچا۔ تو ہم نے اس کو حکمت اور علم عطا کیا اور ہم اسی طرح نیکوکاروں کو بدلہ دیا کرتے ہیں۔'),
(1619, 12, 23, 'اور پھر وہ (یوسف) جس عورت کے گھر میں تھا وہ اپنی مطلب براری کے لئے اس پر ڈورے ڈالنے لگی (چنانچہ) ایک دن سب دروازے بند کر دیے اور کہا بس آجاؤ! یوسف نے کہا معاذ اللہ! (اللہ کی پناہ) وہ (تیرا شوہر) میرا مربی و محسن ہے اس نے مجھے بڑی عزت سے (اپنے گھر میں) ٹھہرایا ہے تو اس کے بدلے میں اس کی امانت میں خیانت کروں؟ (اے معاذ اللہ) بے شک ظالم کبھی فلاح نہیں پاتے۔'),
(1620, 12, 24, 'اس عورت (زلیخا) نے گناہ کرنے کا ارادہ کر لیا تھا۔ اور وہ مرد (یوسف) بھی اس کا ارادہ کرتا اگر اپنے پروردگار کا برہان نہ دیکھ لیتا ایسا ہوا تاکہ ہم اس (یوسف) سے برائی اور بے حیائی کو دور رکھیں بے شک وہ ہمارے برگزیدہ بندوں میں سے تھا۔'),
(1621, 12, 25, 'اور وہ دونوں ایک دوسرے سے پہلے دروازہ تک پہنچنے کے لئے دوڑے اور اس عورت نے اس مرد (یوسف) کا کرتہ پیچھے سے (کھینچ کر) پھاڑ دیا اور پھر دونوں نے اس عورت کے خاوند کو دروازے کے پاس (کھڑا ہوا) پایا۔ اسے دیکھتے ہی اپنی بے گناہی ثابت کرنے کے لیے اس عورت نے بات بنائی اور کہا جو شخص تمہاری بیوی کے ساتھ بدکاری کرنے کا ارادہ کرے اس کی سزا اس کے سوا اور کیا ہے کہ اسے قید کر دیا جائے یا کوئی دردناک عذاب دیا جائے۔'),
(1622, 12, 26, 'یوسف (ع) نے کہا (حقیقت یہ ہے کہ) خود اسی نے اپنی مطلب برآری کے لئے مجھ پر ڈورے ڈالے اور مجھے پھسلانا چاہا اس پر اس عورت کے کنبہ والوں میں سے ایک گواہ نے گواہی دی کہ اگر یوسف کا کرتہ آگے سے پھٹا ہوا ہے تو عورت سچی ہے اور یہ جھوٹا۔'),
(1623, 12, 27, 'اور اگر اس کا کرتہ پیچھے سے پھٹا ہوا ہے تو یہ جھوٹی ہے اور وہ سچا۔'),
(1624, 12, 28, 'پس جب اس (خاوند) نے یوسف کا کرتہ دیکھا کہ پیچھے سے پھٹا ہوا ہے تو عورت سے کہا یہ تمہاری مکاریوں سے ایک مکاری ہے بے شک تمہاری مکاریاں بڑی سخت ہوتی ہیں۔'),
(1625, 12, 29, 'پھر یوسف (ع) سے کہا اے یوسف! اس بات سے درگزر کر (اسے جانے دے) اور بیوی سے کہا اپنے گناہ کی معافی مانگ یقینا تو خطاکاروں میں سے ہے۔'),
(1626, 12, 30, 'پھر (جب اس واقعہ کا چرچا ہوا تو) شہر کی عورتیں کہنے لگیں کہ عزیز کی بیوی اپنے نوجوان پر اپنی مطلب براری کے لئے ڈورے ڈال رہی ہے اس کی محبت اس کے دل کی گہرائیوں میں اتر گئی ہے ہم تو اسے صریح غلطی میں مبتلا دیکھتی ہیں۔'),
(1627, 12, 31, 'جب زلیخا نے ان عورتوں کی یہ مکارانہ باتیں سنیں تو اس نے انہیں بلوا بھیجا اور ان کے لئے مسندیں لگا دیں اور ہر ایک کو (پھل کاٹنے کیلئے) ایک چھری دے دی۔ (پھر جب وہ پھل کاٹ کر کھانے لگیں تو) اس نے یوسف (ع) سے کہا ذرا ان کے سامنے نکل آ پس جب انہوں نے دیکھا تو (حسن و جمال میں) انہیں بڑھا ہوا پایا (لہٰذا حیران رہ گئیں اور پھل کی بجائے) اپنے ہاتھ کاٹ بیٹھیں (اور احساس تک نہ ہوا) اور (بے ساختہ) کہہ اٹھیں ماشاء اللہ۔ یہ انسان نہیں ہے بلکہ کوئی معزز فرشتہ ہے۔'),
(1628, 12, 32, 'عزیز کی بیوی نے کہا یہی وہ ہے جس کے بارے میں تم میری ملامت کرتی تھیں بے شک میں نے اس پر ڈورے ڈالے اور پھسلانے کی کوشش کی۔ مگر وہ بچا ہی رہا۔ (ہاں البتہ اب برملا کہتی ہوں کہ) اگر اس نے وہ نہ کیا جس کا میں اسے حکم دیتی ہوں تو پھر اسے ضرور قید کر دیا جائے گا۔ اور ضرور ذلیل بھی ہوگا۔'),
(1629, 12, 33, 'یوسف (ع) نے (بات سن کر کہا) اے میرے پروردگار! اس کام کی نسبت جس کی یہ مجھے دعوت دے رہی ہیں مجھے قیدخانہ پسند ہے اور اگر تو مجھ سے ان کے مکر و فریب کو دور نہ کرے تو ہو سکتا ہے کہ میں ان کی طرف مائل ہو جاؤں اور اس طرح جاہلوں میں سے ہو جاؤں۔'),
(1630, 12, 34, 'پس اس کے پروردگار نے اس کی دعا قبول کر لی اور اس سے ان عورتوں کے مکر و فریب کو دور کر دیا بے شک وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(1631, 12, 35, 'پھر ایسا ہوا کہ باوجودیکہ وہ (یوسف کی پاکدامنی کی) نشانیاں دیکھ چکے تھے۔ تاہم انہیں یہی مناسب معلوم ہوا کہ ایک مدت تک اسے قید کر دیں۔'),
(1632, 12, 36, 'اور یوسف (ع) کے ساتھ دو اور جوان آدمی بھی قید خانہ میں داخل ہوئے ان میں سے ایک نے کہا کہ میں نے خواب میں دیکھا ہے کہ میں شراب (بنانے کے لیے انگور کا رس) نچوڑ رہا ہوں اور دوسرے نے کہا کہ میں نے دیکھا ہے کہ سر پر کچھ روٹیاں اٹھائے ہوں۔ جن میں سے پرندے کھا رہے ہیں ہمیں ذرا اس کی تعبیر بتائیے ہم تمہیں نیکوکاروں میں سے دیکھتے ہیں۔'),
(1633, 12, 37, 'یوسف (ع) نے کہا جو (مقررہ) کھانا تمہیں دیا جاتا ہے اس کے آنے سے پہلے میں تمہیں تمہارے خوابوں کی تعبیر بتا دوں گا۔ یہ (تعبیرِ خواب) بھی منجملہ ان علوم کے ہے جو میرے پروردگار نے مجھے تعلیم دیے ہیں۔ میں ان لوگوں کا دین و مذہب چھوڑے ہوئے ہوں جو اللہ پر ایمان نہیں رکھتے اور آخرت کے بھی منکر ہیں۔'),
(1634, 12, 38, 'اور میں تو اپنے باپ دادا ابراہیم (ع)، اسحاق(ع) اور یعقوب(ع) کے مذہب کا پیرو ہوں۔ ہمیں یہ زیبا نہیں ہے کہ ہم کسی چیز کو بھی خدا کا شریک ٹھہرائیں۔ یہ اللہ کا فضل ہے جو اس نے ہم اور سب لوگوں پر کیا ہے لیکن اکثر لوگ شکر ادا نہیں کرتے۔'),
(1635, 12, 39, 'اے قید خانہ کے میرے دونوں ساتھیو (یہ بتاؤ) آیا بہت سے جدا جدا خدا اچھے ہیں یا اللہ جو یگانہ بھی ہے اور سب پر غالب بھی؟'),
(1636, 12, 40, 'اللہ کو چھوڑ کر تم جن کی پرستش کرتے ہو ان کی حقیقت اس کے سوا اور کیا ہے؟ کہ وہ چند نام ہیں جو تم نے اور تمہارے باپ دادا نے رکھ لئے ہیں۔ اللہ نے ان کے لئے کوئی سند نہیں اتاری ہے حکم و حکومت کا حق صرف اللہ کو حاصل ہے اسی نے حکم دیا ہے کہ اس کے سوا اور کسی کی عبادت نہ کرو۔ یہی دینِ مستقیم (سیدھا دین) ہے مگر اکثر لوگ جانتے نہیں ہیں۔'),
(1637, 12, 41, 'اے قید خانہ کے میرے دونوں ساتھیو! (اب اپنے خوابوں کی تعبیر سنو) تم میں سے ایک (پہلا) تو وہ ہے جو اپنے مالک (شاہِ مصر) کو شراب پلائے گا اور جو دوسرا ہے اسے سولی پر لٹکایا جائے گا اور پرندے اس کے سر کو (نوچ نوچ کر) کھائیں گے اس بات کا فیصلہ ہو چکا جس کے بارے میں تم دریافت کرتے ہو۔'),
(1638, 12, 42, 'اور یوسف (ع) نے اس شخص سے کہا جس کے متعلق وہ سمجھتے تھے کہ وہ ان دنوں میں سے رہا ہو جائے گا۔ کہ اپنے مالک سے میرا تذکرہ بھی کر دینا لیکن شیطان نے اسے اپنے مالک سے یہ تذکرہ کرنا بھلا دیا۔ پس یوسف کئی سال قید خانہ میں پڑا رہا۔'),
(1639, 12, 43, '(کچھ مدت کے بعد) بادشاہ نے کہا میں نے (خواب میں) سات موٹی تازی گائیں دیکھی ہیں جنہیں سات دبلی پتلی گائیں کھا رہی ہیں اور سات بالیاں ہری ہیں اور سات دوسری سوکھی اے سردارو (درباریو) اگر تم خواب کی تعبیر دے سکتے ہو تو پھر مجھے میرے خواب کی تعبیر بتاؤ۔'),
(1640, 12, 44, 'انہوں نے کہا یہ تو پریشان خواب و خیالات ہیں (جن کی کوئی خاص تعبیر نہیں ہوتی) اور ہم خوابہائے پریشان کی تعبیر نہیں جانتے۔'),
(1641, 12, 45, 'اس وقت وہ شخص جو ان دو قیدیوں میں رہا ہوا تھا اور مدت کے بعد اسے (یوسف (ع) کا پیغام) یاد آیا تھا بولا میں تمہیں اس خواب کی تعبیر بتاتا ہوں ذرا مجھے (ایک جگہ) بھیج تو دو۔'),
(1642, 12, 46, '(چنانچہ وہ سیدھا قید خانہ گیا اور کہا) اے یوسف! اے بڑے سچے! ذرا ہمیں اس خواب کی تعبیر بتاؤ کہ سات موٹی تازی گائیں ہیں جنہیں سات دبلی پتلی گائیں کھا رہی ہیں اور سات بالیاں ہری ہیں اور سات سوکھی تاکہ میں ان لوگوں کے پاس جاؤں (اور انہیں جا کر بتاؤں) شاید وہ (تمہاری قدر و منزلت یا تعبیر) جان لیں۔'),
(1643, 12, 47, 'یوسف (ع) نے کہا (اس خواب کی تعبیر یہ ہے) کہ تم متواتر سات سال کاشتکاری کروگے پھر جو فصل کاٹو اسے اس کی بالی میں رہنے دو ہاں البتہ تھوڑا سا حصہ نکال لو جسے تم کھاؤ۔'),
(1644, 12, 48, 'پھر اس کے بعد سات بڑے سخت سال آئیں گے جو وہ سب کچھ کھا جائیں گے جو تم نے ان کے لئے ذخیرہ کیا تھا مگر تھوڑا سا بچے گا جسے تم محفوظ کر لوگے۔'),
(1645, 12, 49, 'پھر اس کے بعد ایک سال ایسا آئے گا جس میں لوگوں پر خوب بارش برسائی جائے گی اور اس کے ذریعہ سے ان کی فریاد رسی کی جائے گی۔ اور جس میں وہ (پھلوں کا رس) نچوڑیں گے۔'),
(1646, 12, 50, '(یہ تعبیر سن کر) بادشاہ نے کہا اس شخص (یوسف) کو میرے پاس لاؤ۔ پس جب قاصد اس کے پاس آیا تو اس نے کہا اپنے بادشاہ کے پاس واپس جا اور اس سے پوچھ کہ ان عورتوں کے معاملہ کی حقیقت کیا ہے جنہوں نے اپنے ہاتھ کاٹ ڈالے تھے؟ بے شک میرا پروردگار ان کے مکر و فریب سے خوب واقف ہے۔'),
(1647, 12, 51, '(چنانچہ بادشاہ نے ان سب عورتوں کو بلایا اور) پوچھا۔ تمہارا کیا معاملہ تھا جب تم نے اپنی مطلب براری کے لئے یوسف پر ڈورے ڈالے تھے اور اسے پھسلانا چاہا تھا؟ سب نے (بیک زبان) کہا حاشا ﷲ! ہمیں تو اس کی ذرا بھر کوئی برائی معلوم نہیں ہوئی (اس وقت) عزیزِ مصر کی بیوی نے کہا اب جبکہ حق ظاہر ہوگیا (اور حقیقتِ حال بالکل آشکارا ہوگئی) تو وہ میں تھی جس نے اس (یوسف) پر ڈورے ڈالے تھے بے شک وہ سچے لوگوں میں سے ہے۔'),
(1648, 12, 52, '(یوسف نے کہا) یہ سب کچھ اس لئے کیا تاکہ وہ عزیز مصر کو (مزید) علم ہو جائے کہ میں نے اس کی پس پشت (اس کی امانت میں) خیانت نہیں کی اور یقینا اللہ خیانت کاروں کے مکر و فریب کو کامیاب نہیں ہونے دیتا۔'),
(1649, 12, 53, 'میں اپنے نفس کو (بھی) بری قرار نہیں دیتا بے شک نفس تو برائی کا بڑا حکم دینے والا (اور اس پر اکسانے والا) ہے مگر یہ کہ میرا پروردگار (کسی کے حال پر) رحم کرے یقینا میرا پروردگار بڑا بخشنے والا، بڑا رحم کرنے والا۔'),
(1650, 12, 54, 'اور بادشاہ نے کہا کہ اسے میرے پاس بلاؤ تاکہ میں اسے اپنے (ذاتی کاموں کی انجام دہی) کے لیے مخصوص کر لوں۔ پس جب (یوسف آئے اور) بادشاہ نے اس سے گفتگو کی تو (متاثر ہوکر) کہا آج سے تم ہمارے ہاں صاحب مرتبہ اور امانت دار ہو۔'),
(1651, 12, 55, 'آپ (ع) نے کہا مجھے (اس) زمین کے خزانوں پر مقرر کر دیجیے میں یقیناً (مال کی) حفاظت کرنے والا اور اس کام کا جاننے والا بھی ہوں۔'),
(1652, 12, 56, 'اور اس طرح ہم نے یوسف کو اس سرزمین میں اختیار و اقتدار دیا کہ وہ اس میں جہاں چاہے رہے ہم جسے چاہتے ہیں اپنی رحمت سے نوازتے ہیں اور ہم نیکوکاروں کا اجر ضائع نہیں کرتے۔'),
(1653, 12, 57, 'اور البتہ جو لوگ ایمان لائے اور تقویٰ اختیار کیا ان کے لئے آخرت کا اجر و ثواب یقیناً (اس سے) بہتر ہے۔'),
(1654, 12, 58, 'اور (قحط سالی کے دنوں میں غلہ خریدنے) یوسف کے بھائی (مصر) آئے اور (یوسف) کے پاس گئے تو اس نے انہیں پہچان لیا جبکہ وہ اسے نہ پہچان سکے۔'),
(1655, 12, 59, 'اور جب یوسف نے ان کی (خوراک) کا سامان تیار کروا دیا تو (جاتے وقت) کہا (اب کی بار) اپنے (سوتیلے) پدری بھائی کو بھی میرے پاس لیتے آنا۔ کیا تم نہیں دیکھتے کہ میں پورا پورا ناپ (تول) کر (غلہ) دیتا ہوں اور میں کتنا اچھا مہمان نواز ہوں۔'),
(1656, 12, 60, 'اور اگر تم اسے میرے پاس نہ لائے تو پھر تمہارے لئے نہ میرے پاس تولنے کے لئے (غلہ) ہوگا اور نہ ہی میرے قریب آنا۔'),
(1657, 12, 61, 'ان لوگوں نے کہا کہ ہم اس کے باپ پر ڈورے ڈالیں گے کہ وہ (آمادہ ہو جائیں) اور ہم ضرور ایسا کریں گے۔'),
(1658, 12, 62, 'اور یوسف نے اپنے جوانوں (غلاموں) سے کہا کہ ان کی پونجی (جس کے عوض غلہ خریدا ہے) ان کے سامان میں رکھ دو۔ تاکہ جب اپنے گھر والوں کے پاس لوٹ کر جائیں تو اسے پہچانیں (اور) شاید دوبارہ آئیں۔'),
(1659, 12, 63, 'اور جب وہ لوگ لوٹ کر اپنے والد کے پاس گئے تو کہا اے ہمارے باپ! ہمارے لئے (ناپ تول کر) غلہ دیا جانا بند کر دیا گیا ہے (جب تک بھائی کو ہمراہ نہ لے جائیں) اس لئے ہمارے بھائی (بنیامین) کو ہمارے ساتھ بھیجئے تاکہ ہم غلہ لا سکیں اور ہم یقیناً اس کی (پوری) حفاظت کریں گے۔'),
(1660, 12, 64, 'آپ (ع) نے کہا کیا میں اس کے بارے میں تم پر اسی طرح اعتماد کروں جس طرح اس سے پہلے اس کے بھائی کے بارے میں کیا تھا؟ بہرحال اللہ سب سے بہتر حفاظت کرنے والا ہے اور وہی سب سے بڑھ کر رحم کرنے والا ہے۔'),
(1661, 12, 65, 'اور جب انہوں نے اپنا سامان کھولا تو دیکھا کہ ان کی پونجی انہیں واپس کر دی گئی ہے تو کہنے لگے ابا جان! ہمیں اور کیا چاہیئے؟ (دیکھے) یہ ہماری پونجی ہے جو ہمیں واپس کر دی گئی ہے (اب کی بار جو بھائی کو اپنے ساتھ لے جائیں گے) تو جہاں اپنے گھر والوں کے لیے رسد لائیں گے اپنے بھائی کی حفاظت کریں گے (وہاں) ایک اونٹ کا بار اور زیادہ بھی لائیں گے اور یہ غلہ (جو اب کی بار ہم لائے ہیں) بہت تھوڑا ہے۔'),
(1662, 12, 66, 'آپ (ع) نے کہا میں اسے کبھی تمہارے ساتھ نہیں بھیجوں گا جب تک اللہ کی قسم کھا کر مجھ سے عہد و پیمان نہ کرو کہ تم اسے ضرور اپنے ساتھ لاؤگے سوا اس کے کہ تم سب ہی گھیر لئے جاؤ (اور بے بس ہو جاؤ) پھر جب انہوں نے قسم کھا کر اپنا قول و قرار دے دیا تو آپ نے کہا اللہ ہمارے قول قرار پر نگہبان ہے۔'),
(1663, 12, 67, 'اور آپ (ع) نے کہا اے میرے بیٹو (جب مصر پہنچو) تو ایک دروازے سے (شہر میں) داخل نہ ہونا بلکہ مختلف دروازوں سے داخل ہونا اور میں تمہیں اللہ (کی مشیت اور اس کی قضا و قدر) سے بچا تو نہیں سکتا (ہر قسم کا) حکم (اور فیصلہ) اللہ کے قبضہ قدرت میں ہے اسی پر میرا بھروسہ ہے اور اسی پر سب بھروسہ کرنے والوں کو بھروسہ کرنا چاہیئے (میں نے تو صرف احتیاط کے طور پر یہ تدبیر بنائی ہے)۔'),
(1664, 12, 68, 'اور جب وہ لوگ (مصر میں) اسی طرح داخل ہوئے جس طرح ان کے باپ نے انہیں حکم دیا تھا ان کا اس طرح داخل ہونا انہیں خدا (کی مشیت اور اس کی تقدیر) سے بچا تو نہیں سکتا تھا مگر یہ (احتیاطی تدبیر) یعقوب کے دل میں ایک تمنا تھی جسے انہوں نے پورا کر لیا بے شک وہ ہماری دی ہوئی تعلیم سے صاحبِ علم تھا لیکن اکثر لوگ (اس حقیقت کو) نہیں جانتے۔'),
(1665, 12, 69, 'اور جب یہ لوگ یوسف (ع) کے پاس پہنچے تو اس نے اپنے (حقیقی) بھائی (بنیامین) کو اپنے پاس جگہ دی (اور آہستگی سے) کہا میں تیرا بھائی (یوسف) ہوں بس تو اس پر غمگین نہ ہو جو کچھ یہ لوگ سلوک کرتے رہے ہیں۔'),
(1666, 12, 70, 'پھر جب اس (یوسف) نے ان کا سامان تیار کرایا تو پانی پینے کا کٹورا اپنے (سگے) بھائی کے سامان میں رکھوا دیا پھر ایک منادی نے ندا دی کہ اے قافلہ والو! تم چور ہو۔'),
(1667, 12, 71, 'وہ لوگ ان کی طرف متوجہ ہوئے اور (پریشان ہوکر) کہا تم نے کون سی چیز گم کی ہے؟'),
(1668, 12, 72, 'انہوں نے کہا کہ ہم نے بادشاہ کے پینے کا (قیمتی) کٹورا گم کیا ہے اور جو اسے لائے گا اسے ایک بار شتر (غلہ) انعام دیا جائے گا اور میں (منادی) اس بات کا ضامن ہوں۔'),
(1669, 12, 73, 'انہوں نے کہا کہ تم جانتے ہو کہ ہم اس لئے نہیں آئے کہ زمین میں فساد برپا کریں اور نہ ہی ہم چور ہیں۔'),
(1670, 12, 74, 'انہوں (ملازمین) نے کہا اگر تم جھوٹے نکلے تو اس (چور) کی سزا کیا ہے؟'),
(1671, 12, 75, 'انہوں نے کہا اس کی سزا یہ ہے کہ جس کے سامان سے مل جائے وہ خود ہی اس کی سزا ہے ہم اسی طرح ظلم کرنے والوں کو سزا دیتے ہیں۔'),
(1672, 12, 76, 'تب یوسف (ع) نے اپنے (سگے) بھائی کی خرجین سے پہلے دوسروں کی خرجینوں کی تلاشی لینا شروع کی پھر اپنے بھائی کی خرجین سے وہ گم شدہ کٹورا نکال لیا ہم نے اس طرح (بنیامین کو اپنے پاس رکھنے) کیلئے یوسف کے لیے تدبیر کی کیونکہ وہ (مصر کے) بادشاہ کے قانون میں اپنے بھائی کو نہیں لے سکتے تھے مگر یہ کہ خدا چاہتا ہم جس کے چاہتے ہیں مرتبے بلند کر دیتے ہیں اور ہر صاحبِ علم سے بڑھ کر ایک عالم ہوتا ہے۔'),
(1673, 12, 77, 'ان لوگوں (برادرانِ یوسف(ع)) نے کہا اگر اس نے چوری کی ہے تو اس پر کیا تعجب اس سے پہلے اس کے ایک حقیقی بھائی نے بھی چوری کی تھی یوسف نے اس بات کو اپنے دل میں پوشیدہ رکھا اور ان پر ظاہر نہیں کیا (البتہ صرف اتنا) کہا تم بہت ہی برے لوگ ہو اور جو کچھ تم بیان کر رہے ہو اسے اللہ ہی بہتر جانتا ہے۔'),
(1674, 12, 78, 'انہوں نے کہا اے عزیز (مصر) اس کا ایک بہت بوڑھا باپ ہے (وہ اس کی جدائی برداشت نہیں کر سکے گا) اس لئے اس کی جگہ ہم میں سے کسی کو رکھ لیجئے ہم آپ کو احسان کرنے والوں میں سے دیکھتے ہیں۔'),
(1675, 12, 79, 'یوسف (ع) نے کہا معاذ اللہ (اللہ کی پناہ) کہ ہم اس آدمی کے سوا جس کے پاس ہم نے اپنا مال پایا ہے کسی اور شخص کو پکڑیں اس صورت میں تو ہم ظالم قرار پائیں گے۔'),
(1676, 12, 80, 'پھر جب وہ لوگ اس (یوسف) سے مایوس ہوگئے تو علیٰحدہ جاکر باہم سرگوشی (مشورہ) کرنے لگے جو ان میں (سب سے) بڑا تھا اس نے کہا کیا تم نہیں جانتے کہ تمہارے باپ (بنیامین کے بارے میں) خدا کے نام پر تم سے عہد و پیمان لے چکے ہیں اور اس سے پہلے یوسف(ع) کے بارے میں جو تقصیر تم کر چکے ہو (وہ بھی تم جانتے ہو) اس لئے میں تو اس سر زمین کو نہیں چھوڑوں گا جب تک میرا باپ مجھے اجازت نہ دے یا پھر اللہ میرے لئے کوئی فیصلہ نہ کرے اور وہ بہترین فیصلہ کرنے والا ہے۔'),
(1677, 12, 81, 'لہٰذا تم لوگ اپنے باپ کے پاس واپس جاؤ۔ اور جاکر کہو اے ہمارے باپ! آپ کے بیٹے (بنیامین) نے چوری کی ہے اور ہم نے اسی بات کی گواہی دی ہے۔ جس کا ہمیں علم ہے اور ہم غیب کی نگہبانی کرنے والے نہیں ہیں (غیبی باتوں کی ہمیں خبر نہیں ہے)۔'),
(1678, 12, 82, 'اور آپ اس بستی (مصر) کے لوگوں سے پوچھ لیجئے جس میں ہم تھے اور قافلہ والوں سے دریافت کیجئے جس کے ساتھ ہم آئے ہیں اور بے شک ہم سچے ہیں۔'),
(1679, 12, 83, 'آپ (ع) نے (یہ قصہ سن کر) کہا (ایسا نہیں ہے) بلکہ تمہارے نفسوں نے یہ بات تمہارے لئے گھڑ لی ہے (اور خوشنما کرکے سجھائی ہے) تو اب (میرے لئے) صبرِ جمیل ہی اولیٰ ہے امید ہے کہ اللہ تعالیٰ سب کو میرے پاس لائے گا بے شک وہ بڑا علم والا، بڑا حکمت والا ہے۔'),
(1680, 12, 84, '(یہ کہہ کر) ان لوگوں کی طرف سے منہ پھیر لیا اور کہا ہائے یوسف (ہائے یوسف) اور رنج و غم (کی شدت) سے (رو رو کر) ان کی دونوں آنکھیں سفید ہوگئیں اور وہ (باوجود مصیبت زدہ ہونے) کے بڑے ضبط کرنے والے اور خاموش تھے۔'),
(1681, 12, 85, 'ان لوگوں (بیٹوں) نے کہا خدا کی قسم معلوم ہوتا ہے کہ آپ برابر یوسف (ع) کو یاد کرتے رہیں گے یہاں تک کہ سخت بیمار ہو جائیں گے یا ہلاک ہو جائیں گے۔'),
(1682, 12, 86, 'آپ (ع) نے کہا کہ میں اپنے رنج و غم کی شکایت بس اللہ ہی سے کر رہا ہوں اور میں اللہ کی طرف سے وہ کچھ جانتا ہوں جو تم نہیں جانتے۔'),
(1683, 12, 87, 'اے میرے بیٹو! (ایک بار پھر مصر) جاؤ اور یوسف (ع) اور اس کے بھائی کی تلاش کرو اور اللہ کی رحمت سے مایوس نہ ہو بے شک اللہ کی رحمت سے صرف کافر لوگ ہی مایوس ہوتے ہیں۔'),
(1684, 12, 88, '(چنانچہ حسب الحکم) جب یہ لوگ (مصر گئے) اور یوسف (ع) کے پاس پہنچے تو کہنے لگے اے عزیزِ مصر! ہمیں اور ہمارے گھر والوں کو بڑی تکلیف پہنچی ہے (اس لئے اب کی بار) ہم بالکل حقیر سی پونجی لائے ہیں (اسے قبول کریں اور) ہمیں پیمانہ پورا ناپ کر دیجیئے (بھرپور غلہ دیجئے) اور (مزید برآں) ہم کو صدقہ و خیرات بھی دیجئے بے شک اللہ صدقہ خیرات کرنے والوں کو جزاءِ خیر دیتا ہے۔'),
(1685, 12, 89, 'آپ (ع) نے کہا تمہیں کچھ معلوم ہے کہ تم نے یوسف اور اس کے (سگے) بھائی کے ساتھ کیا سلوک کیا تھا جبکہ تم جاہل و نادان تھے؟'),
(1686, 12, 90, 'اس پر وہ لوگ چونکے اور کہا کیا تم یوسف ہو؟ کہا ہاں میں یوسف (ع) ہوں اور یہ میرا بھائی ہے اللہ نے ہم پر احسان کیا بے شک جو پرہیزگاری اختیار کرتا ہے اور صبر سے کام لیتا ہے (وہ بالآخر ضرور کامیاب ہوتا ہے کیونکہ) اللہ نیکوکاروں کا اجر ضائع نہیں کرتا۔'),
(1687, 12, 91, 'انہوں (بھائیوں) نے (شرمسار ہوکر) کہا بے شک اللہ نے تمہیں ہم پر برتری عطا فرمائی ہے اور بے شک ہم خطاکار ہیں۔'),
(1688, 12, 92, 'آپ (ع) نے کہا آج تم پر کوئی الزام (اور لعنت ملامت) نہیں ہے اللہ تمہیں معاف کرے اور وہ بڑا رحم کرنے والا (مہربان) ہے۔'),
(1689, 12, 93, '(بعد ازاں) کہا میری قمیص لے جاؤ اور اسے میرے والد کے چہرے پر ڈال دو ان کی بینائی پلٹ آئے گی (وہ بینا ہو جائیں گے) اور پھر اپنے سب اہل و عیال کو (یہاں) میرے پاس لے آؤ۔'),
(1690, 12, 94, 'اور جب (مصر سے) قافلہ روانہ ہوا تو ان کے باپ نے (کنعان میں) کہا اگر تم مجھے مخبوط الحواس نہ سمجھو تو میں یوسف (ع) کی خوشبو محسوس کر رہا ہوں۔'),
(1691, 12, 95, 'ان (گھر والوں) نے کہا خدا کی قسم آپ اپنی پرانی غلطی میں مبتلا ہیں۔'),
(1692, 12, 96, 'پھر جب خوشخبری دینے والا آیا (اور) وہ قمیص ان کے چہرہ پر ڈالی تو وہ فوراً بینا ہوگئے اور کہا کیا میں نے تم سے نہیں کہا تھا کہ میں اللہ کی طرف سے وہ کچھ جانتا ہوں جو تم نہیں جانتے۔'),
(1693, 12, 97, 'انہوں (بیٹوں) نے کہا اے ہمارے باپ (خدا سے) ہمارے گناہوں کی مغفرت طلب کریں یقینا ہم خطاکار تھے۔'),
(1694, 12, 98, 'آپ (ع) نے کہا میں عنقریب تمہارے لئے اپنے پروردگار سے مغفرت طلب کروں گا بے شک وہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1695, 12, 99, 'جب وہ سب لوگ کنعان سے روانہ ہو کر یوسف (ع)کے پاس (مصر میں) پہنچے تو انہوں نے اپنے ماں باپ کو اپنے پاس جگہ دی اور کہا (اب) مصر میں داخل ہو خدا نے چاہا تو یہاں امن و اطمینان سے رہوگے۔'),
(1696, 12, 100, 'اور (دربار میں پہنچ کر) اپنے ماں باپ کو تختِ شاہی پر (اونچا) بٹھایا اور سب اس کے سامنے سجدہ (شکر) میں جھک گئے (اس وقت) یوسف (ع) نے کہا اے بابا یہ میرے اس خواب کی تعبیر ہے جو (بہت عرصہ) پہلے میں نے دیکھا تھا جسے میرے پروردگار نے سچ کر دکھایا ہے اور اس نے مجھ پر بڑا احسان کیا کہ مجھے قید خانہ سے نکالا اور آپ لوگوں کو صحراء (گاؤں) سے یہاں (شہر میں) لایا۔ بعد اس کے کہ شیطان نے میرے اور میرے بھائیوں کے درمیان اختلاف و فساد ڈال دیا تھا بے شک میرا پروردگار جو کام کرنا چاہتا ہے اس کی بہترین تدبیر کرنے والا ہے بلاشبہ وہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(1697, 12, 101, 'اے میرے پروردگار! تو نے مجھے سلطنت عطا فرمائی ہے اور خوابوں کی تعبیر کا علم بھی تو نے ہی مجھے بخشا ہے اے آسمانوں اور زمین کے خالق تو دنیا و آخرت میں میرا سرپرست اور کارساز ہے میرا خاتمہ (حقیقی) اسلام اور فرمانبرداری پر کر اور مجھے اپنے نیکوکار بندوں میں داخل فرما۔'),
(1698, 12, 102, '(اے پیغمبر(ص)) یہ (داستان) غیب کی خبروں میں سے ہے جس کی ہم آپ کی طرف وحی کر رہے ہیں اور آپ ان (برادرانِ یوسف (ع)) کے پاس اس وقت موجود نہیں تھے جب کہ وہ آپس میں اتفاق کرکے یوسف کے خلاف سازش کر رہے تھے۔'),
(1699, 12, 103, 'اور آپ کتنا ہی حرص کریں (اور کتنا ہی چاہیں) مگر اکثر لوگ ایمان نہیں لائیں گے۔'),
(1700, 12, 104, 'حالانکہ آپ اس بات (تبلیغِ رسالت) پر ان سے کوئی اجرت بھی نہیں مانگتے یہ (قرآن) تو تمام جہانوں کے لئے یاددہانی اور پند و موعظہ ہے۔'),
(1701, 12, 105, 'اور آسمانوں اور زمین میں (خدا کے وجود اور اس کی قدرت کی) کتنی ہی نشانیاں موجود ہیں مگر یہ لوگ ان سے روگردانی کرتے ہوئے گزر جاتے ہیں (اور کوئی توجہ نہیں کرتے)۔'),
(1702, 12, 106, 'اور ان میں سے اکثر اللہ پر ایمان بھی لاتے ہیں تو اس حالت میں کہ (عملی طور پر) برابر شرک بھی کئے جاتے ہیں۔'),
(1703, 12, 107, 'کیا وہ اس بات سے مطمئن ہوگئے ہیں کہ ان پر کوئی عذابِ الٰہی آجائے اور چھا جائے یا اچانک ان کے سامنے اس حال میں قیامت آجائے کہ انہیں خبر بھی نہ ہو۔'),
(1704, 12, 108, '(اے پیغمبر) آپ کہہ دیجئے! کہ میرا راستہ تو یہ ہے کہ میں اور جو میرا (حقیقی) پیروکار ہے ہم اللہ کی طرف بلاتے ہیں اس حال میں کہ ہم واضح دلیل پر ہیں اور اللہ ہر نقص و عیب سے پاک ہے اور میں شرک کرنے والوں میں سے نہیں ہوں۔'),
(1705, 12, 109, '(اے رسول(ص)) ہم نے آپ سے پہلے جن کو بھی رسول بنا کر بھیجا وہ مرد تھے اور آبادیوں کے باشندے تھے جن کی طرف ہم وحی کیا کرتے تھے کیا یہ لوگ زمین میں چلے پھرے نہیں کہ دیکھتے کہ ان لوگوں کا انجام کیسا ہوا جو ان سے پہلے تھے یقینا آخرت کا گھر پرہیزگاروں کے لئے بہتر ہے کیا تم عقل سے کام نہیں لیتے؟'),
(1706, 12, 110, 'یہاں تک کہ جب رسول مایوس ہونے لگے اور خیال کرنے لگے کہ (شاید) ان سے جھوٹ بولا گیا ہے۔ تو (اچانک) ان کے پاس ہماری مدد پہنچ گئی پس جسے ہم نے چاہا وہ نجات پا گیا اور مجرموں سے ہمارا عذاب ٹالا نہیں جا سکتا۔'),
(1707, 12, 111, 'یقیناً ان لوگوں کے (عروج و زوال کے) قصہ میں صاحبانِ عقل کے لئے بڑی عبرت و نصیحت ہے وہ (قرآن) کوئی گھڑی ہوئی بات نہیں ہے بلکہ یہ تو اس کی تصدیق کرنے والا ہے جو اس سے پہلے موجود ہے اور ہر چیز کی تفصیل ہے اور ایمان لانے والوں کے لئے (سراسر) ہدایت و رحمت ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، میم، را۔ یہ الکتاب (قرآن) کی آیتیں ہیں اور جو کچھ آپ پر آپ کے پروردگار کی طرف سے نازل کیا گیا ہے وہ سب بالکل حق ہے لیکن اکثر لوگ (اس پر) ایمان نہیں لاتے۔'),
(1709, 13, 2, 'اللہ وہی تو ہے جس نے آسمانوں کو بلند کیا ہے بغیر ایسے ستونوں کے جو تم کو نظر آتے ہوں پھر وہ عرش (اقتدار) پر متمکن ہوا۔ اور سورج و چاند کو (اپنے قانون قدرت کا) پابند بنایا (چنانچہ) ہر ایک معینہ مدت تک رواں دواں ہے وہی (اس کارخانۂ قدرت کے) ہر کام کا انتظام کر رہا ہے اور اپنی قدرت کی نشانیاں کھول کھول کر بیان کرتا ہے تاکہ تم اپنے پروردگار کی بارگاہ میں حاضر ہونے کا یقین کرو۔'),
(1710, 13, 3, 'اور وہی ہے جس نے زمین کو پھیلایا اور اس میں مضبوط پہاڑ بنا دیے اور نہریں جاری کر دیں اور اس میں ہر ایک پھل کے جوڑے دو قسم کے پیدا کر دیئے وہ رات (کی تاریکی) سے دن (کی روشنی) کو ڈھانپ دیتا ہے بے شک ان سب چیزوں میں غور و فکر کرنے والوں کے لئے بڑی نشانیاں ہیں۔'),
(1711, 13, 4, 'اور خود زمین میں مختلف ٹکڑے آس پاس واقع ہیں (پھر ان میں) انگوروں کے باغ ہیں (غلہ کی) کھیتیاں ہیں اور کھجور کے درخت ہیں کچھ ایسے جو ایک ہی جڑ سے کئی درخت نکلے ہیں اور کچھ وہ جو ایسے نہیں ہیں سب ایک ہی پانی سے سیراب کئے جاتے ہیں مگر ہم (ذائقہ میں) بعض کو بعض پر برتری دیتے ہیں۔ یقیناً ان امور میں ان لوگوں کیلئے بڑی نشانیاں ہیں جو عقل سے کام لیتے ہیں۔'),
(1712, 13, 5, '(اے مخاطب) اگر تمہیں تعجب کرنا ہے تو تعجب کے قابل ان (کفار) کا یہ قول ہے کہ جب ہم (مر کر) خاک ہو جائیں گے تو کیا ہم از سر نو پیدا ہوں گے؟ یہی وہ لوگ ہیں جنہوں نے اپنے پروردگار کا انکار کیا اور یہ ہیں جن کی گردنوں میں طوق پڑے ہوں گے اور یہی جہنمی ہیں جو ہمیشہ ہمیشہ اس میں رہیں گے۔'),
(1713, 13, 6, 'اور (اے رسول(ص)) یہ لوگ آپ سے نیکی (مغفرت) سے پہلے برائی (عذاب) کے لئے جلدی کرتے ہیں حالانکہ ان سے پہلے (ایسے لوگوں پر) خدائی سزاؤں کے نمونے گزر چکے ہیں اور آپ کا پروردگار لوگوں کو ان کے ظلم و زیادتی کے باوجود بڑا بخشنے والا ہے اور یقیناً آپ کا پروردگار سخت سزا دینے والا بھی ہے۔'),
(1714, 13, 7, 'اور کافر لوگ کہتے ہیں ان (پیغمبر اسلام(ص)) پر ان کے پروردگار کی طرف سے کوئی نشانی (ہماری مرضی کے مطابق) کیوں نہیں اتاری جاتی؟ حالانکہ تم تو بس (اللہ کے عذاب سے) ڈرانے والے ہو اور ہر قوم کے لئے ایک راہنما ہوتا ہے۔'),
(1715, 13, 8, 'اللہ ہی جانتا ہے کہ ہر عورت (اپنے پیٹ میں) کیا اٹھائے پھرتی ہے؟ اور اس کو بھی (جانتا ہے) جو کچھ رحموں میں کمی یا بیشی ہوتی رہتی ہے اور اس کے نزدیک ہر چیز کی ایک مقدار مقرر ہے۔'),
(1716, 13, 9, 'وہ ہر پوشیدہ اور ظاہر سب چیزوں کا جاننے والا ہے وہ بزرگ ہے (اور) عالی شان ہے۔'),
(1717, 13, 10, 'تم میں سے کوئی چپکے سے کوئی بات کرے یا اونچی آواز میں کرے اور جو رات کے وقت چھپا رہتا ہے یا دن کے وقت (صاف ظاہر) چلتا ہے اس کے علم میں سب یکساں (برابر) ہیں۔'),
(1718, 13, 11, 'انسان کے آگے اور پیچھے (خدا کے مقرر کردہ) نگہبان ہیں جو اللہ کے حکم سے باری باری اس کی حفاظت کرتے ہیں بے شک اللہ کسی قوم کی اس حالت کو نہیں بدلتا جو اس کی ہے جب تک قوم خود اپنی حالت کو نہ بدلے اور جب خدا کسی قوم کو (اس کے عمل کی پاداش میں) کوئی تکلیف پہنچانا چاہتا ہے تو وہ ٹل نہیں سکتی (پہنچ کر ہی رہتی ہے)۔ اور نہ ہی اللہ کے سوا ان کا کوئی حامی و مددگار ہے۔'),
(1719, 13, 12, 'وہ وہی (خدا) ہے جو ڈرانے اور امید دلانے کے لئے بجلی (کی چمک) دکھاتا ہے اور (دوش ہوا پر) بوجھل بادل پیدا کرتا ہے۔'),
(1720, 13, 13, 'بادل کی گرج اور فرشتے بھی اس کے خوف سے اس کی حمد و ثنا کے ساتھ اس کی تسبیح کرتے ہیں اور وہ آسمانی بجلیاں گراتا ہے اور جسے چاہتا ہے ان کی زد میں لاتا ہے درآنحالیکہ وہ لوگ اللہ کے بارے میں جھگڑ رہے ہوتے ہیں جبکہ وہ بڑا زبردست قوت والا ہے۔'),
(1721, 13, 14, '(تکلیف کے وقت) اسی کو پکارنا برحق ہے اور اسے چھوڑ کر جنہیں یہ لوگ پکارتے ہیں وہ انہیں کچھ بھی جواب نہیں دے سکتے ان کی مثال ایسی ہے جیسے کوئی (پیاسا) اپنے دونوں ہاتھ پانی کی طرف پھیلائے کہ وہ (پانی) اس کے منہ تک پہنچ جائے حالانکہ وہ اس تک پہنچنے والا نہیں ہے اور کافروں کی دعا و پکار گمراہی میں بھٹکتی پھرتی ہے۔'),
(1722, 13, 15, 'اور جو آسمانوں اور زمینوں میں ہیں وہ سب خوشی یا ناخوشی سے اللہ کو ہی سجدہ کر رہے ہیں اور ان کے سائے بھی صبح و شام (اسی کو سجدہ کناں ہیں)۔'),
(1723, 13, 16, '(اے رسول) ان سے کہو (پوچھو) آسمانوں اور زمین کا پروردگار کون ہے؟ (خود ہی) بتائیے کہ اللہ (نیز) ان سے کہو۔ کیا تم نے اللہ کو چھوڑ کر کچھ کارساز بنا لئے ہیں؟ جو اپنے نفع و نقصان کا اختیار نہیں رکھتے کہہ دیجئے کہ کیا اندھا اور آنکھوں والا دونوں برابر ہیں اور کیا نور و ظلمت (اندھیرا اور اجالا) یکساں ہیں؟ کیا ان لوگوں نے اللہ کے کچھ ایسے شریک بنائے ہیں جنہوں نے اللہ کی طرح کچھ مخلوق خلق کی ہے؟ جس کی وجہ سے تخلیق کا یہ معاملہ ان پر مشتبہ ہوگیا ہے؟ کہہ دیجیئے کہ ہر چیز کا خالق اللہ ہی ہے وہ یگانہ ہے اور سب پر غالب ہے۔'),
(1724, 13, 17, 'اسی (اللہ) نے آسمان سے پانی برسایا جس سے ندی نالے اپنی مقدار کے مطابق بہنے لگے اور (میل کچیل سے جھاگ اٹھا تو) سیلاب کی رو نے اس ابھرے ہوئے جھاگ کو اٹھا لیا اور جن چیزوں (دھاتوں) کو لوگ زیور یا کوئی اور چیز (برتن وغیرہ) بنانے کے لیے آگ کے اندر تپاتے ہیں ان سے بھی ایسا ہی جھاگ اٹھتا ہے اسی طرح اللہ تعالیٰ حق و باطل کی مثال بیان کرتا ہے پس جو جھاگ ہے وہ تو رائیگاں چلا جاتا ہے اور جو چیز (پانی اور دھات) لوگوں کو فائدہ پہنچاتی ہے وہ زمین میں باقی رہ جاتی ہے اسی طرح اللہ تعالیٰ سمجھانے کے لیے مثالیں بیان کرتا ہے۔'),
(1725, 13, 18, 'جن لوگوں نے اپنے پروردگار کی دعوت پر لبیک کہا (اسے قبول کیا) ان کے لئے بھلائی (ہی بھلائی) ہے اور جنہوں نے اسے قبول نہیں کیا۔ تو اگر ان کو روئے زمین کی سب دولت مل جائے اور اس کے ساتھ اتنی ہی اور ان کے اختیار میں آجائے تو یہ لوگ اسے اپنے بدلے (عذاب سے بچنے کے لیے) بطور فدیہ دے دیں۔ یہی لوگ ہیں جن کا سخت حساب ہوگا۔ اور ان کا ٹھکانا جہنم ہے اور (وہ) کیا ہی برا ٹھکانا ہے۔'),
(1726, 13, 19, 'اے (رسول(ص)) کیا جو شخص یہ جانتا ہے کہ جو کچھ آپ پر آپ کے پروردگار کی طرف سے نازل کیا گیا ہے وہ حق ہے وہ اس شخص کی مانند ہو سکتا ہے جو بالکل اندھا ہے؟ نصیحت تو بس وہی قبول کرتے ہیں جو دانشمند ہوتے ہیں (اور وہی اس بات کو سمجھ سکتے ہیں)۔'),
(1727, 13, 20, 'وہ جو اللہ سے کئے ہوئے عہد وپیمان کو پورا کرتے ہیں اور عہد شکنی نہیں کرتے۔'),
(1728, 13, 21, 'اور جو ان رشتوں کو جوڑے رکھتے ہیں جن کے جوڑنے کا خدا نے حکم دیا ہے (صلہ رحمی کرتے ہیں) اور اپنے پروردگار سے ڈرتے ہیں اور سخت حساب سے خائف و ترساں رہتے ہیں۔'),
(1729, 13, 22, 'اور جو اپنے پروردگار کی خوشنودی کی طلب میں صبر کرتے ہیں، نماز قائم کرتے ہیں اور جو کچھ ہم نے انہیں دیا ہے اس میں سے اعلانیہ اور پوشیدہ طور پر( ہماری راہ میں) خرچ کرتے ہیں اور جو برائی کو بھلائی سے دور کرتے ہیں عاقبت کا گھر انہی کیلئے ہے۔'),
(1730, 13, 23, 'یعنی ہمیشگی کے باغ ہیں۔ جن میں وہ خود بھی داخل ہوں گے۔ اور ان کے آباء و اجداد، بیویوں اور اولاد میں سے جو صالح اور نیکوکار ہوں گے۔ وہ بھی (ان کے ہمراہ داخل ہوں گے) اور فرشتے ہر دروازہ سے ان کے پاس آئیں گے۔'),
(1731, 13, 24, '(اور کہیں گے) سلام علیکم اس دار آخرت کا انجام کیسا اچھا ہے؟'),
(1732, 13, 25, 'اور جو لوگ اللہ کے عہد و پیمان کو مضبوط باندھ لینے کے بعد توڑتے ہیں اور جن رشتوں کے جوڑنے کا خدا نے حکم دیا ہے ان کو توڑتے ہیں اور جو زمین میں فساد پھیلاتے ہیں یہ وہ ہیں جن کے لئے لعنت ہے اور ان کے لئے برا گھر (جہنم) ہے۔'),
(1733, 13, 26, 'اللہ جس کے رزق کو چاہتا ہے کشادہ کر دیتا ہے اور (جس کے لئے چاہتا ہے) تنگ کر دیتا ہے اور یہ (کافر) لوگ دنیوی زندگی سے خوش ہیں حالانکہ دنیا کی زندگی آخرت کی زندگی کے مقابلہ میں صرف ناپائیدار فائدہ ہے۔'),
(1734, 13, 27, 'اور کافر لوگ کہتے ہیں کہ (اگر یہ نبی برحق ہیں) تو ان کے پروردگار کی طرف سے ان پر (ہماری پسند کی) کوئی نشانی کیوں نہیں اتاری گئی؟ کہہ دیجیئے! اللہ جسے چاہتا ہے گمراہی میں چھوڑتا ہے اور جو اس کی طرف رجوع کرتا ہے اسے ہدایت کرتا ہے۔'),
(1735, 13, 28, 'یعنی یہ وہ لوگ ہیں جو ایمان لائے ہیں اور جن کے دل یادِ خدا سے مطمئن ہوتے ہیں یاد رکھو ذکرِ الٰہی سے ہی دلوں کو اطمینان نصیب ہوتا ہے۔'),
(1736, 13, 29, 'جو لوگ ایمان لائے اور نیک کام کئے ان کے لئے خوش حالی اور خوش انجامی ہے۔'),
(1737, 13, 30, 'اسی طرح ہم نے آپ کو ایک ایسی قوم میں رسول بنا کر بھیجا جس سے پہلے بہت سی قومیں گزر چکی ہیں تاکہ آپ انہیں وہ (کلام و پیغام) پڑھ کر سنائیں جو ہم نے بطور وحی آپ پر اتارا ہے حالانکہ وہ لوگ اپنے مہربان خدا کا انکار کر رہے ہیں۔ آپ کہہ دیجئے! وہی میرا پروردگار ہے اس کے سوا کوئی الٰہ نہیں ہے۔ اسی پر میرا بھروسہ ہے اور اسی کی طرف بازگشت ہے۔'),
(1738, 13, 31, 'اور اگر کوئی ایسا قرآن ہوتا جس کے ذریعہ سے پہاڑ چلنے لگتے، یا زمین (کی مسافتیں) جلدی طے ہو جاتیں یا مردوں سے کلام کیا جا سکتا (تو وہ یہی قرآن ہوتا مگر وہ پھر بھی ایمان نہ لاتے) بلکہ یہ سب کام اللہ کے اختیار میں ہیں۔ کیا ایمان لانے والے اس بات سے مایوس نہیں ہوگئے اگر خدا (زبردستی) چاہتا تو سب لوگوں کو ہدایت کر دیتا! اور کافروں پر ان کے کرتوتوں کی پاداش میں کوئی نہ کوئی آفت آتی ہی رہے گی۔ یا ان کے گھروں کے آس پاس آتی رہے گی۔ یہاں تک کہ اللہ کے وعدہ کے (ظہور) کا وقت آجائے بے شک اللہ کبھی وعدہ خلافی نہیں کرتا۔'),
(1739, 13, 32, '(اے رسول(ص)) آپ سے پہلے بھی بہت سے پیغمبروں کا مذاق اڑایا گیا ہے مگر میں نے کافروں کو (کچھ مدت تک) ڈھیل دی پھر میں نے انہیں پکڑ لیا۔ تو (دیکھو) میرا عذاب کیسا تھا؟'),
(1740, 13, 33, 'کیا وہ ذات جو ہر نفس کے (نیک و بد) اعمال پر نگران ہے کہ اس نے کیا کمایا ہے؟ (وہ ان کے خود ساختہ معبودوں جیسا ہے؟) ان لوگوں نے اللہ کے کچھ شریک بنا لئے ہیں؟ اے رسول ان سے کہو کہ آخر ان کے نام تو بتاؤ یا تم اس (اللہ) کو ایسی چیز کی خبر دیتے ہو جسے وہ (ہمہ دان ہو کر بھی) زمین میں نہیں جانتا کہ کہاں ہے؟ یا یونہی یہ ظاہری الفاظ ہیں (جن کا کوئی مصداق نہیں ہے) بلکہ کافروں کے لئے ان کا مکر و فریب خوشنما بنا دیا گیا ہے اور وہ راہ (راست) سے روک دیئے گئے ہیں اور جسے اللہ گمراہی میں چھوڑ دے (اور اسے ہدایت نہ دے) تو اسے کوئی ہدایت کرنے والا نہیں ہے۔'),
(1741, 13, 34, 'ان کے لئے دنیا کی زندگی میں بھی عذاب ہے اور آخرت کا عذاب تو اور بھی زیادہ سخت ہے اور کوئی نہیں ہے جو انہیں اللہ (کی گرفت) سے بچائے۔'),
(1742, 13, 35, 'جس جنت کا پرہیزگاروں سے وعدہ کیا گیا ہے اس کی صفت یہ ہے کہ اس کے نیچے سے نہریں جاری ہیں اس کے پھل دائمی ہیں اور اس کا سایہ بھی (لازوال) ہے یہ پرہیزگاروں کا انجام ہے اور کافروں کا انجام آتشِ دوزخ ہے۔'),
(1743, 13, 36, 'اور (اے رسول) جن کو ہم نے (پہلے) کتاب دی ہے وہ اس (کتاب) سے خوش ہوتے ہیں جو ہم نے آپ پر نازل کی ہے اور ان جماعتوں میں کچھ ایسے لوگ بھی ہیں جو اس کتاب کے بعض حصوں کا انکار کرتے ہیں آپ کہہ دیجئے کہ مجھے تو صرف یہی حکم دیا گیا ہے کہ میں اللہ کی عبادت کروں اور کسی کو اس کا شریک نہ ٹھہراؤں میں اسی کی طرف دعوت دیتا ہوں اور اسی کی طرف میری بازگشت ہے۔'),
(1744, 13, 37, 'اسی طرح ہم نے اس قرآن کو عربی فرمان کی شکل میں نازل کیا ہے اور اگر آپ اپنے پاس علم (قرآن) کے آجانے کے بعد بھی انکی خواہشات کی پیروی کریں گے تو اللہ کے مقابلہ میں آپ کا نہ کوئی سرپرست و کارساز ہوگا اور نہ کوئی بچانے والا۔'),
(1745, 13, 38, 'اور بے شک ہم نے آپ سے پہلے بہت سے رسول بھیجے ہیں اور ان کے لئے بیوی بچے قرار دیئے اور کسی رسول کے لیے یہ ممکن نہیں ہے کہ وہ اللہ کے حکم کے بغیر کوئی معجزہ پیش کرے ہر وقت کے لیے ایک کتاب (نوشتہ) ہے۔'),
(1746, 13, 39, 'اللہ جو چاہتا ہے وہ (لکھا ہوا) مٹا دیتا ہے اور (جو چاہتا ہے) برقرار رکھتا ہے اور اس کے پاس ام الکتاب (اصل کتاب یعنی لوح محفوظ) ہے۔'),
(1747, 13, 40, 'اور اگر ہم آپ کو کچھ وہ باتیں آنکھوں سے دکھا دیں جن کا ہم ان (کفار) سے وعدہ وعید کر رہے ہیں یا ہم (ان کے ظاہر ہونے سے پہلے) آپ کو اٹھا لیں بہرحال (ہمارا پیغام) پہنچانا آپ کا کام ہے اور حساب لینا ہمارا کام ہے۔'),
(1748, 13, 41, 'کیا یہ لوگ نہیں دیکھتے کہ ہم زمین کو اس کے اطراف و جوانب سے برابر گھٹاتے چلے آتے ہیں اللہ ہی حکم دینے والا (اور فیصلہ کرنے والا) ہے اس کے حکم کو کوئی ٹالنے والا نہیں ہے اور وہ بہت جلد حساب لینے والا ہے۔'),
(1749, 13, 42, 'جو لوگ ان سے پہلے گزر چکے ہیں انہوں نے بڑی مخفی تدبیریں کیں سو ہر قسم کی تدبیریں اور ترکیبیں اللہ ہی کے قبضۂ قدرت میں ہیں وہی جانتا ہے کہ ہر شخص کیا کمائی کر رہا ہے اور بہت جلد کافروں کو معلوم ہو جائے گا کہ اس گھر کا انجام (بخیر) کس کا ہے؟'),
(1750, 13, 43, 'اور کافر لوگ کہتے ہیں کہ آپ رسول نہیں ہیں کہہ دیجیے کہ میرے اور تمہارے درمیان بطور گواہ اللہ کافی ہے اور وہ شخص جس کے پاس کتاب کا علم ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف، لام، را۔ (اے رسول(ص)) یہ ایک کتاب ہے جو ہم نے آپ پر اس لئے نازل کی ہے کہ آپ لوگوں کو ان کے پروردگار کے حکم سے (کفر کی) تاریکیوں سے نکال کر (ایمان کی) روشنی کی طرف لائیں (یعنی) اس خدا کے راستہ کی طرف جو غالب (اور) قابل تعریف ہے۔'),
(1752, 14, 2, 'وہ اللہ کہ جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب اسی کا ہے اور تباہی و بربادی ہے سخت عذاب کی وجہ سے ان کافروں کیلئے۔'),
(1753, 14, 3, 'جو دنیا کی زندگی کو آخرت کی زندگی پر ترجیح دیتے ہیں اور جو اللہ کی راہ سے (لوگوں کو) روکتے ہیں اور چاہتے ہیں کہ اسے ٹیڑھا بنا دیں یہ لوگ بڑی دور کی گمراہی میں ہیں۔'),
(1754, 14, 4, 'اور ہم نے (دنیا میں) جب بھی کوئی رسول بھیجا ہے تو اس کی قوم کی زبان میں تاکہ وہ ان کیلئے (پیغامِ خداوندی) کھول کر بیان کرے پس اللہ جسے چاہتا ہے (توفیق سلب کرکے) گمراہی میں چھوڑ دیتا ہے اور جسے چاہتا ہے ہدایت دے دیتا ہے وہ (سب پر) غالب ہے، بڑا حکمت والا ہے۔'),
(1755, 14, 5, 'اور بے شک ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ بھیجا تھا (اور حکم دیا) کہ اپنی قوم کو (کفر کے) اندھیروں سے نکال کر (ایمان کی) روشنی میں لائے اور انہیں اللہ کے مخصوص دن یاد دلاؤ یقینا اس (یاددہانی) میں ہر بڑے صبر و شکر کرنے والے کیلئے بڑی نشانیاں ہیں۔'),
(1756, 14, 6, 'اور یاد کرو جب موسیٰ نے اپنی قوم سے کہا اللہ کے اس احسان کو یاد کرو جو اس نے تم پر کیا یعنی اس نے تمہیں فرعون والوں سے نجات دی جو تمہیں سخت عذاب پہنچاتے تھے اور وہ تمہارے لڑکوں کو ذبح کر ڈالتے تھے اور لڑکیوں کو زندہ چھوڑ دیتے تھے اور اس میں تمہارے پروردگار کی طرف سے تمہاری بڑی آزمائش تھی۔'),
(1757, 14, 7, 'اور یاد کرو جب تمہارے پروردگار نے تمہیں مطلع کر دیا تھا کہ اگر (میرا) شکر ادا کروگے تو میں تمہیں اور زیادہ دوں گا اور اگر کفرانِ نعمت (ناشکری) کروگے تو میرا عذاب بھی بڑا سخت ہے۔'),
(1758, 14, 8, 'اور موسیٰ نے کہا کہ اگر تم اور روئے زمین کے سب لوگ کفر اختیار کریں تو اللہ کو اس کی کیا پروا ہے وہ یقیناً بے نیاز (اور) قابلِ ستائش ہے۔'),
(1759, 14, 9, 'کیا تمہیں ان لوگوں کی خبر نہیں پہنچی جو تم سے پہلے گزر چکے (یعنی) قومِ نوح(ع) اور عاد و ثمود(ع) اور وہ لوگ جو ان کے بعد ہوئے ہیں جنہیں اللہ کے سوا کوئی نہیں جانتا کہ جب ان کے رسول ان کے پاس روشن دلیلیں (معجزے) لے کر آئے تو انہوں نے اپنے ہاتھ اپنے منہ میں دبا لئے اور کہا کہ جس پیغام کے ساتھ تم بھیجے گئے ہو ہم اس کا انکار کرتے ہیں اور تم جس (دین) کی طرف ہمیں دعوت دیتے ہو ہم اس کی طرف سے تردد آمیز شک میں ہیں۔'),
(1760, 14, 10, 'ان کے رسولوں نے کہا کیا (تمہیں) اللہ کے بارے میں شک ہے جو آسمانوں اور زمین کا خالق ہے؟ وہ تمہیں اس لئے بلا رہا ہے کہ تمہارے گناہ بخش دے اور تمہیں ایک مقررہ وقت تک مہلت دے اس پر ان لوگوں نے کہا تم نہیں ہو مگر ہمارے جیسے بشر (اور انسان) تم چاہتے ہو کہ جن کی ہمارے باپ دادا عبادت کرتے آئے ہیں ہمیں ان کی عبادت سے روکو! سو ہمارے پاس کوئی کھلا ہوا (ہمارا مطلوبہ) معجزہ لاؤ۔'),
(1761, 14, 11, 'ان کے رسولوں نے ان سے کہا کہ بے شک ہم نہیں ہیں مگر تمہارے جیسے بشر (اور انسان) مگر اللہ اپنے بندوں میں سے جس پر چاہتا ہے اپنا احسان فرماتا ہے اور ہمارے لئے یہ ممکن نہیں ہے کہ ہم تمہیں کوئی کھلی ہوئی دلیل (معجزہ) پیش کریں مگر اللہ کے حکم سے اور اللہ پر ہی اہل ایمان کو بھروسہ کرنا چاہیئے۔'),
(1762, 14, 12, 'آخر ہمیں کیا ہے کہ ہم اللہ پر بھروسہ نہ کریں حالانکہ اسی نے ہمیں ہماری (زندگی کی) راہوں کی راہنمائی کی ہے اور تم ہمیں جو ایذائیں پہنچا رہے ہو ہم ان پر صبر کریں گے اور اللہ ہی پر بھروسہ کرنے والوں کو بھروسہ کرنا چاہیئے۔'),
(1763, 14, 13, 'اور کافروں نے اپنے رسولوں سے کہا کہ ہم تمہیں اپنی سر زمین سے نکال دیں گے یا پھر تم ہمارے مذہب میں لوٹ آؤ اور تب ان کے پروردگار نے ان پر وحی بھیجی کہ ہم ان ظالموں کو ضرور ہلاک کر دیں گے۔'),
(1764, 14, 14, 'اور ان کے بعد ہم تمہیں اس سر زمین میں آباد کریں گے یہ (وعدہ) ہر اس شخص کے لئے ہے جو میری بارگاہ میں کھڑے ہونے سے ڈرے اور میری تہدید سے خائف ہو۔'),
(1765, 14, 15, 'اور انہوں (رسولوں) نے (ہم سے) فتح مندی طلب کی (جو قبول ہوئی) اور ہر سرکش ضدی (حق کی مخالفت کرنے والا) نامراد ہوا۔'),
(1766, 14, 16, 'اس (نامرادی) کے پیچھے جہنم ہے اور اسے کچ لہو قسم کا پانی پلایا جائے گا۔'),
(1767, 14, 17, 'جسے وہ گھونٹ گھونٹ کرکے پئے گا اور گلے سے اتار نہ سکے گا اور ہر طرف سے اس کے پاس موت آئے گی مگر وہ مرے گا نہیں اور (مزید برآں) اس کے پیچھے ایک اور سخت عذاب ہوگا۔'),
(1768, 14, 18, 'جن لوگوں نے اپنے پروردگار کا انکار کیا ان کے اعمال کی حالت اس راکھ جیسی ہے جسے سخت آندھی والے دن ہوا اڑا لے جائے جو کچھ انہوں نے کیا (کمایا) اس سے کچھ بھی ان کے ہاتھ نہ آئے گا یہی بہت بڑی گمراہی ہے۔'),
(1769, 14, 19, 'کیا تم نے نہیں دیکھا کہ خدا نے آسمانوں اور زمین کو حق (و حکمت) کے ساتھ پیدا کیا ہے وہ چاہے تو تم سب کو لے جائے۔ (فنا کر دے) اور نئی مخلوق کو لے آئے۔'),
(1770, 14, 20, 'اور یہ بات اللہ کے لئے کچھ بھی مشکل نہیں ہے۔'),
(1771, 14, 21, 'اور (قیامت کے دن) وہ سب ایک ساتھ اللہ کی بارگاہ میں حاضر ہوں گے۔ تو جو کمزور (پیروکار) ہوں گے وہ ان سے کہیں گے جو بڑے (سردار) بنے ہوئے تھے کہ ہم تو (زندگی بھر) تمہارے پیروکار تھے تو تم آج ہمیں اللہ کے عذاب سے کچھ بچا سکتے ہو؟ وہ (بڑے) کہیں گے اگر اللہ ہمیں ہدایت دیتا تو ہم بھی تمہیں ہدایت دیتے اب ہمارے لئے برابر ہے جزع فزع کریں یا صبر کریں۔ آج ہمارے لئے چھٹکارا حاصل کرنے کی کوئی سبیل نہیں ہے۔'),
(1772, 14, 22, 'اور جب (ہر قسم کا) فیصلہ ہو جائے گا تو اس وقت شیطان کہے گا کہ بے شک اللہ نے تم سے جو وعدہ کیا تھا وہ سچا تھا اور میں نے بھی تم سے وعدہ کیا تھا مگر میں نے تم سے وعدہ خلافی کی اور مجھے تم پر کوئی تسلط (زور) تو تھا نہیں سوائے اس کے کہ میں نے تمہیں (کفر اور گناہ کی) دعوت دی اور تم نے میری دعوت قبول کر لی پس تم مجھے ملامت نہ کرو (بلکہ) خود اپنے آپ کو ملامت کرو (آج) نہ میں تمہاری فریاد رسی کر سکتا ہوں۔ اور نہ تم میری فریاد رسی کر سکتے ہو اس سے پہلے (دنیا میں) جو تم نے مجھے (خدا کا) شریک بنایا تھا میں اس سے بیزار ہوں بے شک ظالموں کے لئے دردناک عذاب ہے۔'),
(1773, 14, 23, 'جو لوگ ایمان لائے اور نیک عمل کئے وہ ان بہشتوں میں داخل کئے جائیں گے جن کے نیچے سے نہریں بہہ رہی ہوں گی اور اپنے پروردگار کے حکم سے ان میں ہمیشہ ہمیشہ رہیں گے اس میں ان کی باہمی دعا (بوقت ملاقات) سلام ہوگا۔ (سلامٌ علیکم تم پر سلامتی ہو)۔'),
(1774, 14, 24, 'کیا تم نے نہیں دیکھا کہ اللہ نے کس طرح اچھی مثال بیان کی ہے کہ کلمۂ طیبہ (پاک کلمہ) شجرۂ طیبہ (پاکیزہ) درخت کی مانند ہے۔ جس کی جڑ مضبوط ہے اور اس کی شاخ آسمان تک پہنچی ہوئی ہے۔'),
(1775, 14, 25, 'جو اپنے پروردگار کے حکم سے ہر وقت پھل دے رہا ہے اور اللہ لوگوں کے لئے مثالیں پیش کرتا ہے تاکہ وہ نصیحت حاصل کریں (اور سمجھیں)۔'),
(1776, 14, 26, 'اور کلمۂ خبیثہ (ناپاک کلمہ) کی مثال شجرۂ خبیثہ (ناپاک درخت) کی سی ہے جسے زمین کے اوپر سے اکھاڑ لیا جائے اور اس کے لئے ثبات و قرار نہ ہو۔'),
(1777, 14, 27, 'اللہ ایمان والوں کو قولِ ثابت پر ثابت قدم رکھتا ہے دنیا کی زندگی میں بھی اور آخرت میں بھی اور ظالموں کو گمراہی میں چھوڑ دیتا ہے اور اللہ جو چاہتا ہے وہ کرتا ہے۔'),
(1778, 14, 28, 'کیا تم نے ان لوگوں کی طرف نہیں دیکھا جنہوں نے اللہ کی عطا کردہ نعمت کو کفرانِ نعمت سے بدل دیا اور اپنی قوم کو ہلاکت کے گھر میں یعنی دوزخ میں جا اتارا۔'),
(1779, 14, 29, 'جس میں وہ داخل ہوں گے اور وہ (کیسا) برا ٹھکانا ہے۔'),
(1780, 14, 30, 'اور انہوں نے اللہ کے لئے کچھ ہمسر (شریک) بنا لئے ہیں تاکہ (لوگوں کو) اس کے راستے سے بھٹکائیں (اے رسول) آپ کہہ دیجیے کہ (چندے) عیش کر لو آخر یقینا تمہاری بازگشت آتش دوزخ کی طرف ہے۔'),
(1781, 14, 31, 'آپ میرے ان بندوں سے کہہ دیجیے جو ایمان لائے ہیں کہ نماز قائم کریں اور جو کچھ ہم نے انہیں دیا ہے اس سے پوشیدہ اور علانیہ خرچ کریں اس سے پہلے کہ وہ دن آئے کہ جس میں نہ خرید و فروخت ہوگی اور نہ مخلصانہ دوستی ہوگی۔'),
(1782, 14, 32, 'اللہ وہی ہے جس نے آسمانوں اور زمین کو پیدا کیا اور پھر کشتی کو تمہارے لئے مسخر کر دیا تاکہ وہ اس کے حکم سے سمندر میں چلے اور دریاؤں کو بھی تمہارے لئے مسخر کر دیا۔'),
(1783, 14, 33, 'اور تمہارے لئے سورج و چاند کو (بھی) مسخر کر دیا جو برابر چلے جا رہے ہیں اور رات اور دن کو بھی تمہارے لئے مسخر کر دیا۔'),
(1784, 14, 34, 'اور جس نے تمہیں ہر چیز میں سے دیا جو تم نے مانگا اور اگر تم اللہ کی نعمتوں کو شمار کرنا چاہو تو شمار نہیں کر سکتے بے شک انسان بڑا ظالم اور بڑا ناشکرا ہے۔'),
(1785, 14, 35, 'اور وہ وقت یاد کرو جب ابراہیم نے (بارگاہ ایزدی میں) دعا کی تھی اے میرے پروردگار! اس شہر (مکہ) کو امن کی جگہ اور مجھے اور میری اولاد کو اس بات سے بچا کہ ہم بتوں کی پرستش کریں۔'),
(1786, 14, 36, 'اے میرے پروردگار ان (بتوں) نے بہت سے لوگوں کو گمراہ کر دیا ہے سو جو میری پیروی کرے گا وہ مجھ سے ہوگا اور جو میری نافرمانی کرے تو یقیناً تو بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(1787, 14, 37, 'اے ہمارے پروردگار میں نے اپنی کچھ اولاد کو تیرے محترم گھر کے پاس بے زراعت میدان میں آباد کیا ہے اے ہمارے مالک تاکہ (وہ یہاں) نماز قائم کریں اب تو کچھ لوگوں کے دلوں کو ایسا کر دے کہ وہ ان کی طرف مائل ہوں اور ان کو پھلوں سے رزق عطا فرما تاکہ وہ (تیرا) شکر ادا کریں۔'),
(1788, 14, 38, 'اے ہمارے پروردگار! بے شک جو کچھ ہم چھپاتے ہیں تو اسے بھی جانتا ہے اور جو ظاہر کرتے ہیں اسے بھی اور زمین و آسمان کی کوئی چیز اللہ سے پوشیدہ نہیں ہے۔'),
(1789, 14, 39, 'ساری ستائش اللہ کے لئے ہے جس نے باوجود بڑھاپے کے مجھے اسماعیل(ع) و اسحاق(ع) (دو بیٹے) عطا فرمائے بے شک میرا پروردگار دعا کا بڑا سننے والا ہے۔'),
(1790, 14, 40, 'اے میرے پروردگار! مجھے نماز کا قائم کرنے والا بنا اور میری اولاد کو بھی۔ اے ہمارے پروردگار اور میری دعا کو قبول فرما۔'),
(1791, 14, 41, 'اے ہمارے پروردگار! مجھے اور میرے والدین کو اور سب ایمان لانے والوں کو بخش دے جس دن حساب قائم ہوگا (لیا جائے گا)۔'),
(1792, 14, 42, '(اے رسول(ص)) جو کچھ (یہ) ظالم لوگ کر رہے ہیں تم اللہ کو اس سے غافل نہ سمجھو۔ وہ تو انہیں اس دن کے لئے مہلت دے رہا ہے جس میں (شدتِ خوف و حیرت سے) آنکھیں کھلی کی کھلی رہ جائیں گی۔'),
(1793, 14, 43, 'وہ تیزی سے دوڑ رہے ہوں گے اپنے سر اوپر اٹھاتے ہوئے اس عالم میں کہ ان کی نگاہ خود ان کی طرف نہیں پلٹے گی اور ان کے دل (خوف و دہشت کے سوا ہر خیال سے) خالی ہو رہے ہوں گے۔'),
(1794, 14, 44, '(اے رسول) لوگوں کو اس دن سے ڈراؤ جب ان پر عذاب آئے گا تو ظالم لوگ کہیں گے اے ہمارے رب تھوڑی سی مدت کے لئے ہمیں مہلت دے دے ہم تیری دعوت پر لبیک کہیں گے اور (تیرے) رسولوں کی پیروی کریں گے (انہیں جواب دیا جائے گا) کیا تم نے اس سے پہلے قسمیں نہیں کھائی تھیں کہ تمہیں کبھی زوال نہ ہوگا۔'),
(1795, 14, 45, 'حالانکہ تم انہی لوگوں کے مسکنوں میں آباد تھے جنہوں نے اپنے اوپر ظلم کیا تھا اور تم پر واضح ہوگیا تھا کہ ہم نے ان کے ساتھ کیا سلوک کیا تھا؟ اور ہم نے (تمہیں سمجھانے کے لئے) مثالیں بھی بیان کر دیں تھیں۔'),
(1796, 14, 46, 'اور انہوں نے اپنی ساری تدبیریں کیں (اور چالیں چلیں) اور اللہ کے پاس ان کی ہر تدبیر اور چال (کا جواب) ہے اگرچہ ان کی تدبیریں و ترکیبیں ایسی تھیں کہ ان سے پہاڑ بھی اپنی جگہ سے ہٹ جائیں۔'),
(1797, 14, 47, 'خبردار! یہ خیال نہ کرنا کہ اللہ نے اپنے رسولوں سے جو وعدہ کیا ہے وہ اس کے خلاف کرے گا بے شک اللہ (سب پر) غالب ہے (اور) انتقام لینے والا ہے۔'),
(1798, 14, 48, '(اور یہ اس دن ہوگا) جس دن یہ زمین دوسری قسم کی زمین سے بدل دی جائے گی اور آسمان بھی (بدل جائیں گے) اور سب لوگ اللہ کی بارگاہ میں حاضر ہو جائیں گے جو ایک ہے اور سب پر غالب ہے۔'),
(1799, 14, 49, 'اور تم اس دن مجرموں کو دیکھوگے کہ زنجیروں میں جکڑے ہوئے ہوں گے۔'),
(1800, 14, 50, 'ان کے کرتے تارکول کے ہوں گے اور ان کے چہروں کو آگ ڈھانپ رہی ہوگی۔'),
(1801, 14, 51, 'یہ اس لئے ہوگا کہ اللہ ہر شخص کو اس کے کئے کا بدلہ دے گا بے شک اللہ بہت جلد حساب لینے والا ہے۔'),
(1802, 14, 52, 'یہ سب انسانوں کے لئے ایک پیغام ہے تاکہ انہیں اس کے ذریعہ سے ڈرایا جائے اور یہ کہ انہیں معلوم ہو جائے کہ الٰہ بس ایک ہی ہے نیز یہ کہ عقل والے لوگ اس سے نصیحت حاصل کریں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف، لام، را۔ یہ کتاب (الٰہی) یعنی روشن قرآن کی آیتیں ہیں۔'),
(1804, 15, 2, 'جن لوگوں نے کفر اختیار کیا ہے وہ بہت تمنا کریں گے کہ کاش وہ مسلمان ہوتے۔'),
(1805, 15, 3, '(اے رسول(ص)) انہیں چھوڑ دو کہ وہ کھائیں (پئیں) اور عیش و آرام کریں اور (بے شک جھوٹی) امید انہیں غفلت میں رکھے عنقریب انہیں (سب کچھ) معلوم ہو جائے گا۔'),
(1806, 15, 4, 'اور ہم نے کبھی کوئی بستی ہلاک نہیں کی مگر یہ کہ اس کے لئے وقتِ معلوم لکھا ہوا تھا۔'),
(1807, 15, 5, 'کوئی قوم اپنے مقررہ وقت سے نہ آگے بڑھ سکتی ہے اور نہ پیچھے ہٹ سکتی ہے۔'),
(1808, 15, 6, 'اور وہ (کفار) کہتے ہیں: اے وہ جس پر ذکر (قرآن) اتارا گیا ہے یقینا تم دیوانہ ہو۔'),
(1809, 15, 7, 'اگر تم سچے ہو تو پھر ہمارے پاس فرشتوں کو کیوں نہیں لے آتے؟'),
(1810, 15, 8, 'ہم فرشتوں کو نہیں اتارتے مگر (صحیح موقع پر فیصلہ) حق کے ساتھ اور پھر لوگوں کو مہلت نہیں دی جاتی۔'),
(1811, 15, 9, 'بے شک ہم نے ہی ذکر (قرآن) اتارا ہے اور ہم ہی اس کے محافظ ہیں۔'),
(1812, 15, 10, 'اور ہم نے آپ سے پہلے مختلف جماعتوں میں رسول بھیجے۔'),
(1813, 15, 11, 'اور ان کے پاس کوئی ایسا رسول نہیں آیا جس کے ساتھ انہوں نے مذاق نہ کیا ہو۔'),
(1814, 15, 12, 'اسی طرح ہم اس (ذکر) کو مجرموں کے دلوں میں ڈال دیتے ہیں۔'),
(1815, 15, 13, '(مگر) وہ اس (ذکر) پر ایمان نہیں لاتے اور جو پہلے گزر چکے ہیں ان کا طریقہ بھی یہی رہ چکا ہے۔'),
(1816, 15, 14, 'اور اگر ہم ان پر آسمان کا ایک دروازہ کھول دیں جس سے وہ دن دہاڑے چڑھنے لگیں۔'),
(1817, 15, 15, 'تو پھر بھی وہ یہی کہیں گے کہ ہماری آنکھوں کو مدہوش کر دیا گیا ہے بلکہ ہم پر جادو کر دیا گیا ہے۔'),
(1818, 15, 16, 'اور بے شک ہم نے آسمان میں بہت سے برج بنا دیئے اور اس (آسمان) کو دیکھنے والوں کے لئے آراستہ کر دیا ہے۔'),
(1819, 15, 17, 'اور ہم نے اسے ہر مردود شیطان سے محفوظ کر دیا۔'),
(1820, 15, 18, 'مگر یہ کہ کوئی (شیطان) چوری چھپے کچھ سن لے تو (اس صورت میں) ایک چمکتا ہوا شعلہ اس کا پیچھا کرتا ہے۔'),
(1821, 15, 19, 'اور ہم نے زمین کو پھیلا دیا اور اس میں پہاڑ گاڑ دیے اور اس میں ہر قسم کی چیزیں نپی تلی اگائیں۔'),
(1822, 15, 20, 'اور ہم نے اس میں تمہارے لئے بھی معاش کے وسائل بنائے اور ان کیلئے بھی جن کے روزی رساں تم نہیں ہو۔'),
(1823, 15, 21, 'اور کوئی چیز ایسی نہیں ہے جس کے خزانے ہمارے پاس نہ ہوں اور ہم اسے نہیں اتارتے مگر ایک معیّن مقدار میں۔'),
(1824, 15, 22, 'اور ہم ہواؤں کو باردار بنا کر بھیجتے ہیں پھر آسمان سے پانی برساتے ہیں پھر وہ پانی ہم تمہیں پلاتے ہیں حالانکہ تم اسکے خزانہ دار نہیں ہو۔'),
(1825, 15, 23, 'اور بلاشبہ ہم ہی زندہ کرتے ہیں اور ہم ہی مارتے ہیں اور ہم ہی (سب کے) وارث ہیں۔'),
(1826, 15, 24, 'اور یقیناً ہم ان کو بھی جانتے ہیں جو تم سے پہلے ہو گزرے اور ان کو بھی جانتے ہیں جو بعد میں آنے والے ہیں۔'),
(1827, 15, 25, 'بے شک تمہارا پروردگار (بروزِ قیامت) سب کو جمع کرے گا وہ بڑا حکمت والا، بڑا علم والا ہے۔'),
(1828, 15, 26, 'اور بلاشبہ ہم نے انسان کو سڑے ہوئے گارے کی کھنکھناتی مٹی سے پیدا کیا ہے۔'),
(1829, 15, 27, 'اور اس سے پہلے ہم نے جان کو بے دھواں تیز گرم آگ سے پیدا کیا۔'),
(1830, 15, 28, '(وہ وقت یاد کرو) جب آپ کے پروردگار نے فرشتوں سے کہا کہ میں سڑے ہوئے گارے کی کھنکھناتی ہوئی مٹی سے ایک بشر پیدا کرنے والا ہوں۔'),
(1831, 15, 29, 'پس جب میں اسے مکمل کر لوں اور اس میں اپنی (خاص) روح پھونک دوں تو تم اس کے سامنے سجدہ میں گر جاؤ۔'),
(1832, 15, 30, 'چنانچہ سب فرشتوں نے سجدہ کیا۔'),
(1833, 15, 31, 'سوائے ابلیس کے کہ اس نے سجدہ کرنے والوں کے ساتھ ہونے سے انکار کیا۔'),
(1834, 15, 32, 'ارشاد ہوا: اے ابلیس! تجھے کیا ہوا کہ تو نے سجدہ کرنے والوں کا ساتھ نہ دیا؟'),
(1835, 15, 33, 'اس نے کہا میں ایسا نہیں ہوں کہ ایسے بشر کو سجدہ کروں جسے تو نے سڑے ہوئے گارے کی کھنکھناتی ہوئی مٹی سے پیدا کیا ہے۔'),
(1836, 15, 34, 'ارشاد ہوا (یہاں سے) نکل جا کہ تو مردود (راندہ ہوا) ہے۔'),
(1837, 15, 35, 'اور روزِ جزا (قیامت) تک تجھ پر لعنت ہے۔'),
(1838, 15, 36, 'اس نے کہا کہ اے میرے رب! مجھے اس دن تک مہلت دے جب لوگ دوبارہ اٹھائے جائیں گے۔'),
(1839, 15, 37, 'فرمایا بے شک تو مہلت پانے والوں میں سے ہے۔'),
(1840, 15, 38, '(جنہیں) وقتِ معلوم تک مہلت دی گئی ہے۔'),
(1841, 15, 39, 'اس نے کہا اے میرے رب! چونکہ تو نے مجھے گمراہ کیا ہے تو میں بھی زمین میں ان (بندوں) کے لئے گناہوں کو خوشنما بناؤں گا اور سب کو گمراہ کروں گا۔'),
(1842, 15, 40, 'سوائے تیرے مخلص بندوں کے۔'),
(1843, 15, 41, 'فرمایا یہ سیدھا راستہ ہے مجھ تک پہنچنے والا۔'),
(1844, 15, 42, 'جو میرے خاص بندے ہیں ان پر تیرا کوئی قابو نہ ہوگا سوائے ان گمراہوں کے جو تیری پیروی کریں گے۔'),
(1845, 15, 43, 'اور بے شک جہنم ان سب کی وعدہ گاہ ہے۔'),
(1846, 15, 44, 'اس کے سات دروازے ہیں (اور) ہر دروازے کے لئے ان (لوگوں) میں سے ایک حصہ ہے۔'),
(1847, 15, 45, 'بے شک پرہیزگار لوگ بہشتوں اور چشموں میں ہوں گے۔'),
(1848, 15, 46, '(ان سے کہا جائے گا) کہ تم سلامتی اور امن و امان کے ساتھ ان میں داخل ہو جاؤ۔'),
(1849, 15, 47, 'اور ہم ان کے سینوں سے ہر قسم کی کدورت نکال دیں گے اور وہ بھائیوں کی طرح تختوں پر آمنے سامنے بیٹھے ہوں گے۔'),
(1850, 15, 48, 'ان کے اندر انہیں کوئی تکلیف و زحمت چھوئے گی بھی نہیں اور نہ ہی وہ وہاں سے نکالے جائیں گے۔'),
(1851, 15, 49, '(اے رسول) میرے بندوں کو آگاہ کر دو کہ میں بڑا بخشنے والا، بڑا رحم کرنے والا ہوں۔'),
(1852, 15, 50, 'اور یہ بھی (بتا دو) کہ میرا عذاب بھی بڑا دردناک عذاب ہے۔'),
(1853, 15, 51, 'اور انہیں ابراہیم(ع) کے مہمانوں کا واقعہ بھی سنا دو۔'),
(1854, 15, 52, 'جبکہ وہ ان کے پاس آئے اور سلام کیا اور انہوں نے (جوابِ سلام کے بعد) کہا ہم کو تم سے ڈر لگ رہا ہے۔'),
(1855, 15, 53, '(مہمانوں) نے کہا کہ ڈرئیے نہیں ہم آپ کو ایک صاحبِ علم بچہ کی (ولادت کی) بشارت دیتے ہیں۔'),
(1856, 15, 54, 'ابراہیم(ع) نے کہا تم مجھے اس حال میں بشارت دیتے ہو کہ مجھ پر بڑھاپا آچکا ہے یہ کس چیز کی بشارت ہے جو تم مجھے دیتے ہو؟'),
(1857, 15, 55, 'انہوں نے کہا ہم آپ کو بالکل سچی بشارت دے رہے ہیں تو آپ ناامید ہونے والوں میں سے نہ ہوں۔'),
(1858, 15, 56, 'ابراہیم(ع) نے کہا اپنے پروردگار کی رحمت سے تو گمراہوں کے سوا کون مایوس ہوتا ہے؟'),
(1859, 15, 57, '(پھر) کہا اے اللہ کے فرستادو آخر تمہیں کیا مہم درپیش ہے؟'),
(1860, 15, 58, 'انہوں نے کہا کہ ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں۔'),
(1861, 15, 59, 'سوا لوط(ع) کے گھر والوں کے کہ ہم ان سب کو بچا لیں گے۔'),
(1862, 15, 60, 'بجز اس کی بیوی کے اس کی نسبت ہم نے یہ طے کیا ہے کہ وہ پیچھے رہ جانے والوں میں سے ہوگی۔'),
(1863, 15, 61, 'پس جب اللہ کے فرستادہ خاندانِ لوط (ع) کے پاس آئے۔'),
(1864, 15, 62, 'تو لوط نے کہا کہ تم تو اجنبی لوگ معلوم ہوتے ہو۔'),
(1865, 15, 63, 'انہوں نے کہا (نہیں) بلکہ ہم تمہارے پاس وہ چیز (عذاب) لے کر آئے ہیں جس میں (یہ) لوگ شک کیا کرتے تھے۔'),
(1866, 15, 64, 'اور ہم آپ کے پاس حق (عذاب) لے کر آئے ہیں اور بلاشبہ ہم بالکل سچے ہیں۔'),
(1867, 15, 65, 'تو آپ رات کے کسی حصہ میں اپنے اہل و عیال کو لے کر نکل جائیں اور خود آپ ان کے پیچھے پیچھے چلیں اور آپ میں سے کوئی پیچھے مڑ کر نہ دیکھے اور جدھر جانے کا آپ کو حکم دیا گیا ہے ادھر ہی چلے جائیں۔'),
(1868, 15, 66, 'اور ہم نے ان (لوط(ع)) کو بذریعۂ وحی اس فیصلہ سے آگاہ کر دیا کہ صبح ہوتے ہوتے ان کی جڑ بالکل کاٹ دی جائے گی۔'),
(1869, 15, 67, 'اور شہر والے (نوجوان اور خوبصورت مہمانوں کو دیکھ کر) خوشیاں مناتے ہوئے آگئے۔'),
(1870, 15, 68, '(لوط(ع) نے) کہا یہ لوگ میرے مہمان ہیں تم میری فضیحت نہ کرو۔'),
(1871, 15, 69, 'اور اللہ سے ڈرو اور مجھے رسوا نہ کرو۔'),
(1872, 15, 70, 'انہوں نے کہا کہ کیا ہم نے آپ کو دنیا بھر کے لوگوں (کو مہمان کرنے) سے منع نہیں کر دیا تھا۔'),
(1873, 15, 71, 'آپ نے کہا اگر تم نے کچھ کرنا ہے تو پھر یہ میری (قوم کی) بیٹیاں موجود ہیں۔'),
(1874, 15, 72, 'آپ کی جان کی قسم! یہ لوگ اپنے نشہ میں بالکل اندھے ہو رہے تھے۔'),
(1875, 15, 73, 'آخرکار سورج نکلتے نکلتے انہیں ایک ہولناک آواز (چیخ) نے آلیا۔'),
(1876, 15, 74, 'پس ہم نے اس (بستی) کو تہہ و بالا کر دیا (اوپر کا طبقہ نیچے کر دیا)۔ اور ان پر پکی ہوئی مٹی کے پتھروں کی بارش کر دی۔'),
(1877, 15, 75, 'بے شک اس (واقعہ) میں حقیقت کی پہچان رکھنے والوں کے لئے بڑی نشانیاں ہیں۔'),
(1878, 15, 76, 'اور وہ (بستی) ایک عام گزرگاہ پر واقع ہے جو اب تک قائم ہے۔'),
(1879, 15, 77, 'بے شک اس (واقعہ) میں اہلِ ایمان کے لئے بڑی نشانی ہے۔'),
(1880, 15, 78, 'اور بے شک ایک (گھنے جنگل) والے بڑے ظالم تھے۔'),
(1881, 15, 79, 'ہم نے ان سے انتقام لیا اور یہ دونوں (بستیاں) شارعِ عام پر واقع ہیں۔'),
(1882, 15, 80, 'اور بے شک حجر کے لوگوں نے رسولوں کو جھٹلایا۔'),
(1883, 15, 81, 'اور ہم نے انہیں نشانیاں عطا کیں مگر وہ ان سے روگردانی ہی کرتے رہے۔'),
(1884, 15, 82, 'اور وہ پہاڑوں کو تراش کر گھر بناتے تھے تاکہ امن و اطمینان سے رہیں۔'),
(1885, 15, 83, 'پس صبح کے وقت انہیں ایک ہولناک آواز نے آپکڑا۔'),
(1886, 15, 84, 'سو جو کچھ انہوں نے کمایا تھا وہ ان کے کام نہ آیا۔'),
(1887, 15, 85, 'اور ہم نے آسمانوں اور زمین کو نیز جو کچھ ان کے درمیان ہے اس کو پیدا نہیں کیا مگر حق و حکمت کے ساتھ اور قیامت یقینا آنے والی ہے پس (اے رسول) آپ شائستہ طریقہ سے درگزر کریں۔'),
(1888, 15, 86, 'بے شک آپ کا پروردگار ہی بڑا پیدا کرنے والا (اور) بڑا جاننے والا ہے۔'),
(1889, 15, 87, 'اور بلاشبہ ہم نے آپ کو دہرائی جانے والی سات آیتیں عطا کی ہیں اور قرآنِ عظیم بھی۔'),
(1890, 15, 88, 'آپ اپنی آنکھ اٹھا کر بھی ان چیزوں کی طرف نہ دیکھیں جن سے ہم نے مختلف قسم کے لوگوں (کافروں) کو بہرہ مند کیا ہے اور نہ ہی ان کے بارے میں غمگین ہوں اور اہلِ ایمان کے لئے اپنے بازو پھیلا دیں (ان کے ساتھ شفقت سے پیش آئیں)۔'),
(1891, 15, 89, 'اور کہہ دیجیے کہ میں تو عذاب سے کھلا ہوا ڈرانے والا ہوں۔'),
(1892, 15, 90, '(اے رسول) جس طرح ہم نے تقسیم کرنے والوں پر اپنا کلام نازل کیا تھا۔ (اسی طرح) آپ پر بھی نازل کیا ہے۔'),
(1893, 15, 91, '(لیکن یہ وہ لوگ تھے) جنہوں نے قرآن کے ٹکڑے ٹکڑے کر دئیے۔'),
(1894, 15, 92, 'آپ کے پروردگار کی قسم ہم ان سے اس کی بابت ضرور سوال کریں گے۔'),
(1895, 15, 93, 'جو کچھ وہ کیا کرتے تھے۔'),
(1896, 15, 94, 'پس جس چیز کا آپ کو حکم دیا گیا ہے اس کا واضح اعلان کر دیں اور مشرکوں سے اعراض کریں (ان کی کچھ پروا نہ کریں)۔'),
(1897, 15, 95, 'جو آپ کا مذاق اڑاتے ہیں ہم ان کے لئے کافی ہیں۔'),
(1898, 15, 96, 'جو خدا کے ساتھ دوسرا الٰہ قرار دیتے ہیں انہیں عنقریب (اپنا انجام) معلوم ہو جائے گا۔'),
(1899, 15, 97, 'اور بے شک ہم جانتے ہیں کہ جو کچھ (یہ لوگ) کہتے رہتے ہیں اس سے آپ کا دل تنگ ہوتا ہے۔'),
(1900, 15, 98, 'تو اپنے پروردگار کی حمد کے ساتھ اس کی تسبیح کریں اور سجدہ کرنے والوں میں سے ہو جائیں۔'),
(1901, 15, 99, 'اور اس وقت تک برابر اپنے پروردگار کی عبادت کرتے رہیں جب تک کہ تمہارے پاس موت نہ آجائے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'اللہ کا حکم (عذاب) آگیا ہے پس تم اس کے لئے جلدی نہ کرو وہ پاک اور برتر ہے ان چیزوں سے جن کو وہ اس کا شریک ٹھہراتے ہیں۔'),
(1903, 16, 2, 'وہ اپنے بندوں میں سے جس پر چاہتا ہے اپنے حکم سے فرشتوں کو روح کے ساتھ نازل کر دیتا ہے کہ لوگوں کو خبردار کر دو (ڈراؤ) کہ میرے سوا کوئی الٰہ نہیں ہے پس مجھ ہی سے ڈرو۔'),
(1904, 16, 3, 'اس نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا ہے وہ جن چیزوں کو اس کا شریک قرار دیتے ہیں وہ ان سے بلند و بالا ہے۔'),
(1905, 16, 4, 'اس نے انسان کو نطفہ (پانی کی ایک بوند) سے پیدا کیا پھر وہ ایک دم کھلم کھلا جھگڑالو بن گیا۔'),
(1906, 16, 5, 'اور اسی نے تمہارے لئے چوپائے پیدا کئے جن میں تمہارے لئے گرم لباس بھی ہے اور دوسرے فائدے بھی اور انہی سے بعض کا تم گوشت کھاتے ہو۔'),
(1907, 16, 6, 'اور ان (چوپاؤں) میں تمہارے لئے زیب و زینت بھی ہے جب شام کو واپس لاتے ہو اور صبح جب (چراگاہ کی طرف) لے جاتے ہو (اس وقت ان کا منظر کیسا خوش آئند ہوتا ہے)۔'),
(1908, 16, 7, 'اور یہ (جانور) تمہارے بوجھوں کو اٹھاتے ہیں۔ اور ان (دور دراز) شہروں تک پہنچاتے ہیں جن تک تم بڑی جانکاہی کے بغیر نہیں پہنچ سکتے تھے بے شک تمہارا پروردگار تمہارا شفیق و بڑا مہربان ہے۔'),
(1909, 16, 8, 'اور اس نے گھوڑے، خچر اور گدھے پیدا کئے تاکہ تم ان پر سوار ہو اور اپنے لئے انہیں زینت بناؤ اور خدا وہ کچھ پیدا کرتا ہے (اور کرے گا) جو تم نہیں جانتے۔'),
(1910, 16, 9, 'اور سیدھے راستہ کی طرف راہنمائی کرنا اللہ کی ذمہ داری ہے اور ان میں کچھ راستے کج بھی ہوتے ہیں اور اگر اللہ تعالیٰ (زبردستی) چاہتا تو تم سب کو ہدایت دے دیتا۔'),
(1911, 16, 10, 'وہ وہی ہے جس نے تمہارے (فائدے کے لئے) آسمان سے پانی برسایا جس سے تم پیتے بھی ہو اور جس سے وہ درخت (اور سبزے اگتے ہیں) جن میں تم (اپنے جانور) چراتے ہو۔'),
(1912, 16, 11, 'اسی (پانی) سے وہ (خدا) تمہارے لئے کھیتی، زیتون، کھجور، انگور اور ہر قسم کے پھل پیدا کرتا ہے بے شک اس میں غور و فکر کرنے والوں کے لئے ایک بڑی نشانی ہے۔'),
(1913, 16, 12, 'اور اسی نے تمہارے لئے رات، دن، سورج اور چاند کو مسخر کر دیا ہے (تمہارے کام میں لگا دیا ہے) اور ستارے بھی مسخّر ہیں (یہ سب تسخیر) اسی کے حکم سے ہے بے شک اس میں عقل سے کام لینے والوں کے لئے بہت سی نشانیاں موجود ہیں۔'),
(1914, 16, 13, 'اور اس نے تمہارے لئے زمین میں جو رنگ برنگ کی چیزیں پیدا کی ہیں اور مسخر کی ہیں اس میں سوچنے سمجھنے اور نصیحت حاصل کرنے والوں کے لئے ایک بڑی نشانی ہے۔'),
(1915, 16, 14, 'اور وہ وہی ہے جس نے سمندر (کو تمہارا) مسخر کر دیا تاکہ تم اس سے تروتازہ گوشت کھاؤ اور اس سے زیور کی چیزیں (موتی وغیرہ) نکالو جنہیں تم (آرائش کیلئے) پہنتے ہو اور تم دیکھتے ہو کہ اس میں کشتیاں پانی کو چیرتی پھاڑتی ہوئی چلی جاتی ہیں۔'),
(1916, 16, 15, 'اور اس (خدا) نے زمین میں بھاری بھر کم پہاڑوں کے لنگر ڈال دیئے ہیں تاکہ وہ تمہیں لے کر کہیں ڈھلک نہ جائے اور اس نے نہریں رواں دواں کر دیں اور راستے بنائے تاکہ تم (خشکی و تری میں) راہ پاؤ (اور منزل مقصود تک پہنچ جاؤ)۔'),
(1917, 16, 16, 'اور اس نے (قطعِ مسافت اور راستہ بتانے کے لئے) مختلف علامتیں مقرر کیں اور ستاروں سے بھی لوگ راستہ پاتے ہیں۔'),
(1918, 16, 17, 'کیا وہ (خدا) جو پیدا کرتا ہے، اس کی مانند ہے جو کچھ پیدا نہیں کرتا؟ کیا تم اتنا بھی غور نہیں کرتے اور نصیحت حاصل نہیں کرتے؟'),
(1919, 16, 18, 'اور اگر تم اللہ کی نعمتوں کو شمار کرنا چاہو تو شمار نہیں کر سکتے بےشک اللہ بڑا ہی بخشنے والا اور بڑا مہربان ہے۔'),
(1920, 16, 19, 'اللہ وہ (سب کچھ) جانتا ہے جو کچھ تم چھپاتے ہو اور جو کچھ ظاہر کرتے ہو۔'),
(1921, 16, 20, 'اور اللہ کو چھوڑ کر جن کو یہ (مشرک) لوگ پکارتے ہیں وہ کوئی چیز پیدا نہیں کر سکتے (بلکہ) وہ خود پیدا کئے ہوئے ہیں۔'),
(1922, 16, 21, 'وہ مردہ ہیں زندہ نہیں ہیں۔ انہیں تو یہ بھی خبر نہیں ہے کہ انہیں یا ان کے پجاریوں کو (دوبارہ) کب اٹھایا جائے گا؟'),
(1923, 16, 22, 'تمہارا الٰہ بس ایک ہی الٰہ ہے سو جو لوگ آخرت پر ایمان نہیں رکھتے ان کے دل منکر ہیں اور وہ بڑے مغرور ہیں۔'),
(1924, 16, 23, 'یقینا اللہ وہ سب کچھ جانتا ہے جو وہ (دلوں میں) چھپاتے ہیں اور اسے بھی جو وہ ظاہر کرتے ہیں بے شک وہ تکبر کرنے والوں کو پسند نہیں کرتا۔'),
(1925, 16, 24, 'اور جب ان سے کہا (پوچھا) جاتا ہے کہ تمہارے پروردگار نے کیا نازل کیا ہے؟ تو وہ کہتے ہیں (کچھ بھی نہیں) بس اگلے لوگوں کی (فرسودہ) داستانیں ہیں۔'),
(1926, 16, 25, 'اس کا انجام یہ ہے کہ قیامت کے دن یہ اپنے گناہوں کا بھی پورا پورا بوجھ اٹھائیں گے اور کچھ بوجھ ان کا بھی اٹھائیں گے جنہیں یہ بے سمجھے گمراہ کر رہے ہیں (دیکھو) کیا ہی برا بوجھ ہے وہ جو یہ اٹھا رہے ہیں؟'),
(1927, 16, 26, 'جو لوگ ان سے پہلے گزر چکے ہیں انہوں نے بھی (دعوتِ حق کے خلاف) مکاریاں کی تھیں تو اللہ تعالیٰ نے ان کی (مکاریوں والی) عمارت بنیاد سے اکھیڑ دی اور اس کی چھت اوپر سے ان پر آپڑی اور ان پر اس طرف سے عذاب آیا جدھر سے ان کو وہم و گمان بھی نہ تھا۔'),
(1928, 16, 27, 'پھر قیامت کے دن وہ (اللہ) انہیں ذلیل و خوار کرے گا اور کہے گا کہ (بتاؤ) کہاں ہیں وہ میرے شریک جن کے بارے میں تم (اہلِ حق سے) لڑا جھگڑا کرتے تھے (اس وقت) وہ لوگ کہیں گے جن کو (حقیقت کا) علم دیا گیا تھا کہ آج کے دن رسوائی اور برائی ان کافروں کے لئے ہے۔'),
(1929, 16, 28, 'کہ جن کی روحیں فرشتوں نے اس حال میں قبض کی تھیں کہ وہ اپنے اوپر ظلم کر رہے تھے اس وقت انہوں نے (سپر ڈال دی تھی اور) صلح کی پیشکش کرتے ہوئے کہا تھا کہ ہم تو کوئی برائی نہیں کرتے تھے (ان سے کہا گیا) ہاں (تم نے ضرور برائی کی تھی) تم لوگ جو کچھ کرتے رہے ہو اللہ اس سے بخوبی واقف ہے۔'),
(1930, 16, 29, 'پس اب جہنم کے دروازوں میں سے (جہنم میں) داخل ہو جاؤ اب تمہیں ہمیشہ کے لئے اسی میں رہنا ہے پس کیا ہی برا ٹھکانہ ہے تکبر کرنے والوں کا۔'),
(1931, 16, 30, 'اور جب صاحبانِ تقویٰ سے کہا جاتا ہے کہ تمہارے پروردگار نے کیا نازل کیا ہے؟ تو وہ کہتے ہیں کہ سراسر خیر و خوبی نازل کی ہے جن لوگوں نے اس دنیا میں نیکی اور بھلائی کی ان کے لئے یہاں بھی بھلائی ہے اور آخرت کا گھر تو یقیناً اور بھی بہتر ہے اور متقیوں کا گھر کیا ہی خوب ہے؟'),
(1932, 16, 31, '(یعنی) وہاں ہمیشہ رہنے والے باغات ہیں جن میں وہ داخل ہوں گے جن کے نیچے نہریں جاری ہوں گی وہاں وہ جو کچھ چاہیں گے ان کو مل جائے گا خدا اسی طرح پرہیزگاروں کو جزا دیتا ہے۔'),
(1933, 16, 32, 'وہ متقی جن کی روحیں اس حال میں فرشتے قبض کرتے ہیں کہ وہ (کفر و شرک) سے پاک و صاف ہوتے ہیں (اس وقت) فرشتے کہتے ہیں تم پر سلام ہو بہشت میں داخل ہو جاؤ ان اعمال کی بدولت جو تم کیا کرتے تھے۔'),
(1934, 16, 33, '(اے رسول) یہ (منکرین) صرف اس بات کا انتظار کر رہے ہیں کہ ان کے پاس (عذاب والے) فرشتے آجائیں یا آپ کے پروردگار کا حکم (عذاب) آجائے؟ ایسا ہی ان لوگوں نے کیا تھا جو ان سے پہلے خود ہی اپنے اوپر ظلم کرتے رہے۔'),
(1935, 16, 34, 'آخرکار ان کے کرتوتوں کی برائیاں ان تک پہنچ گئیں اور انہیں اس (عذاب) نے گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(1936, 16, 35, 'اور مشرک لوگ کہتے ہیں کہ اگر اللہ چاہتا تو ہم اور ہمارے آباء و اجداد اس کے سوا نہ کسی اور کی عبادت کرتے اور نہ ہی ہم اس کے حکم کے بغیر کسی چیز کو حرام کرتے ایسا ہی ان لوگوں نے کیا جو ان سے پہلے تھے (بتاؤ) پیغمبروں کے ذمے کھلا پیغام دینے کے سوا اور کیا ہے؟'),
(1937, 16, 36, 'اور یقیناً ہم نے ہر ایک امت میں کوئی نہ کوئی رسول (یہ پیغام دے کر) ضرور بھیجا ہے کہ اللہ کی عبادت کرو اور طاغوت (کی بندگی) سے بچو پس ان (امتوں) میں سے بعض کو اللہ نے ہدایت دی اور بعض پر گمراہی مستقر اور ثابت ہوگئی پس تم زمین پر چلو پھرو اور دیکھو کہ جھٹلانے والوں کا کیا انجام ہوا؟'),
(1938, 16, 37, '(اے رسول) آپ ان کے ہدایت پانے کے کتنے ہی حریص ہوں مگر (یہ ہدایت پانے والے نہیں) کیونکہ اللہ جس کو (اس کے کفر و سرکشی کی وجہ سے) گمراہی میں چھوڑ دیتا ہے اس کو ہدایت نہیں کرتا اور ان کے لئے کوئی مددگار نہیں ہے۔'),
(1939, 16, 38, 'وہ اللہ کی سخت قسمیں کھا کر کہتے ہیں کہ جو مر گیا خدا اسے ہرگز (دوبارہ) نہیں اٹھائے گا۔ ہاں (ضرور اٹھائے گا) یہ اس کا وعدہ ہے جس کا پورا کرنا اس پر لازم ہے لیکن اکثر لوگ اس کا علم نہیں رکھتے۔'),
(1940, 16, 39, '(یہ دوبارہ اٹھانا اس لئے ضروری ہے) تاکہ جن باتوں میں یہ لوگ اختلاف کرتے ہیں ان کے سامنے ان کی حقیقت کھول دے اور تاکہ کافروں کو معلوم ہو جائے کہ وہ جھوٹے تھے۔'),
(1941, 16, 40, 'ہم جب کسی چیز (کے پیدا کرنے) کا ارادہ کرتے ہیں تو ہمارا کہنا بس اتنا ہی ہوتا ہے کہ اس سے کہتے ہیں کہ ہو جا بس وہ ہو جاتی ہے۔'),
(1942, 16, 41, 'اور جن لوگوں نے خدا کے واسطے ہجرت کی اس کے بعد کہ (ایمان لانے کی وجہ سے) ان پر ظلم کئے گئے ہم ان کو دنیا میں بھی اچھا ٹھکانہ دیں گے اور آخرت کا اجر تو بہت بڑا ہے کاش یہ لوگ جان لیتے۔'),
(1943, 16, 42, 'یہی وہ لوگ ہیں جنہوں نے صبر کیا اور اپنے پروردگار پر بھروسہ کرتے ہیں۔'),
(1944, 16, 43, '(اے رسول) ہم نے آپ سے پہلے مردوں کو ہی رسول بنا کر بھیجا، کھلی ہوئی دلیلوں اور کتابوں کے ساتھ جن کی طرف ہم وحی کرتے تھے اگر تم لوگ نہیں جانتے تو اہلِ ذکر سے پوچھ لو۔'),
(1945, 16, 44, 'اور ہم نے آپ پر الذکر (قرآن مجید) اس لئے نازل کیا ہے کہ آپ لوگوں کے لئے (وہ معارف و احکام) کھول کر بیان کریں جو ان کی طرف نازل کئے گئے ہیں تاکہ وہ غور و فکر کریں۔'),
(1946, 16, 45, 'کیا وہ لوگ جو (پیغمبر(ص) کے خلاف) بری تدبیریں اور ترکیبیں کر رہے ہیں وہ اس بات سے مطمئن ہوگئے ہیں کہ اللہ انہیں زمین میں دھنسا دے یا ان پر ایسے موقع سے عذاب آجائے جہاں سے انہیں وہم و گمان بھی نہ ہو؟'),
(1947, 16, 46, 'یا انہیں اپنے کاروبار میں ان کے چلتے پھرتے وقت آپکڑے پس وہ اپنی چالبازیوں سے اللہ کو عاجز نہیں کر سکتے۔'),
(1948, 16, 47, 'یا انہیں اس وقت پکڑے جب وہ اس سے خوف زدہ ہوں بلاشبہ تمہارا پروردگار بڑا شفیق اور بڑا مہربان ہے۔'),
(1949, 16, 48, 'کیا ان لوگوں نے کبھی اللہ کی پیدا کی ہوئی مختلف چیزوں میں غور نہیں کیا کہ ان کے سائے کس طرح اللہ کو سجدہ کرتے ہوئے دائیں بائیں جھکتے ہیں اس طرح وہ سب اپنے عجز کا اظہار کر رہے ہیں۔'),
(1950, 16, 49, 'اور جو (جاندار) چیزیں آسمانوں میں ہیں اور زمین میں جتنے جانور ہیں اور فرشتے سب اللہ کے لئے سربسجود ہیں اور وہ سرکشی نہیں کرتے۔'),
(1951, 16, 50, 'وہ اپنے پروردگار سے ڈرتے رہتے ہیں جو ان پر بالادست ہے اور انہیں جو کچھ حکم دیا جاتا ہے وہ وہی کرتے ہیں۔'),
(1952, 16, 51, 'اور اللہ تعالیٰ نے فرمایا ہے کہ دو اِلٰہ (خدا) نہ بناؤ۔ الٰہ (خدا) تو صرف ایک ہی ہے سو تم صرف مجھ ہی سے ڈرو۔'),
(1953, 16, 52, 'اور اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اسی کا دین دائمی اور واجب الاطاعت ہے تو کیا تم اللہ کے سوا غیروں سے ڈرتے ہو؟'),
(1954, 16, 53, 'اور تمہارے پاس جو بھی نعمت ہے وہ سب اللہ ہی کی طرف سے ہے پھر جب تمہیں کوئی تکلیف پہنچتی ہے تو اسی کی جناب میں داد و فریاد کرتے ہو۔'),
(1955, 16, 54, 'پھر جب وہ تکلیف کو دور کر دیتا ہے تو ایک دم تم میں سے ایک گروہ اپنے پروردگار کے ساتھ شرک کرنے لگتا ہے۔'),
(1956, 16, 55, 'تاکہ جو کچھ ہم نے انہیں عطا کیا ہے اس کا کفران کریں سو (اے ناشکرو) چند روزہ فائدہ اٹھا لو۔ عنقریب تمہیں (اپنا انجام) معلوم ہو جائے گا۔'),
(1957, 16, 56, 'یہ لوگ ہمارے دیے ہوئے رزق میں سے ان (بتوں وغیرہ) کے لئے حصہ مقرر کرتے ہیں جن کو یہ جانتے بھی نہیں ہیں خدا کی قسم تم سے ضرور پوچھا جائے گا کہ تم کیسی افترا پردازیاں کرتے تھے۔'),
(1958, 16, 57, 'اور یہ اللہ کے لئے لڑکیاں قرار دیتے ہیں پاک ہے اس کی ذات اور خود ان کے لئے وہ ہے جس کے یہ خواہشمند ہیں۔'),
(1959, 16, 58, 'اور جب ان میں سے کسی کو بیٹی کے پیدا ہونے کی اطلاع دی جاتی ہے تو اس کا چہرہ سیاہ پڑ جاتا ہے اور وہ رنج و غم سے بھر جاتا ہے۔'),
(1960, 16, 59, 'وہ اس بری خبر سے جو اسے دی گئی ہے لوگوں سے چھپتا پھرتا ہے (اور سوچتا ہے) کہ ذلت کے ساتھ اسے لئے رہے یا اسے مٹی کے تلے گاڑ دے؟ کیا ہی بُرا فیصلہ ہے جو یہ کرتے ہیں۔'),
(1961, 16, 60, 'جو لوگ آخرت پر ایمان نہیں رکھتے ان کی حالت بری ہے اور اللہ کے لئے اعلیٰ صفت ہے وہ زبردست ہے بڑا حکمت والا ہے۔'),
(1962, 16, 61, 'اگر اللہ لوگوں کو ان کی زیادتی پر (فوراً) پکڑ لیا کرتا تو پھر روئے زمین پر کسی جاندار کو نہ چھوڑتا مگر وہ انہیں ایک مقررہ مدت تک مہلت دیتا ہے پھر جب وہ مقررہ وقت آجاتا ہے تو اس سے وہ ایک گھڑی پیچھے آگے نہیں ہو سکتے۔'),
(1963, 16, 62, 'اور وہ اللہ کے لئے وہ چیزیں (بیٹیاں) قرار دیتے ہیں جن کو اپنے لئے ناپسند کرتے ہیں اور ان کی زبانیں غلط بیانی کرتی ہیں کہ ان کے لئے (بہرحال) بھلائی ہی بھلائی ہے (نہیں) ہاں البتہ ان کے لئے دوزخ کی آگ ہے اور بے شک یہ سب سے پہلے اس میں پہنچائے (جھونکے) جائیں گے۔'),
(1964, 16, 63, 'خدا کی قسم! ہم نے آپ سے پہلے کئی امتوں کی طرف رسول بھیجے ہیں پس شیطان نے ان کے (برے) اعمال خوشنما کرکے انہیں دکھائے سو وہی آج ان کا سرپرست ہے اور ان کے لئے دردناک عذاب ہے۔'),
(1965, 16, 64, 'اور ہم نے آپ پر یہ کتاب نہیں اتاری مگر اس لئے کہ آپ ان باتوں کو کھول کر بیان کر دیں جن میں وہ اختلاف کر رہے ہیں اور یہ (کتاب) ایمان لانے والوں کے لئے سراسر ہدایت اور رحمت ہے۔'),
(1966, 16, 65, 'اور اللہ نے آسمان سے پانی اتارا ہے اور اس کے ذریعہ سے زمین کو اس کے مردہ (بنجر) ہو جانے کے بعد زندہ کر دیتا ہے۔ بے شک اس میں آوازِ حق سننے والوں کے لئے بڑی نشانی ہے۔'),
(1967, 16, 66, 'اور بے شک تمہارے لئے چوپایوں میں عبرت کا سامان موجود ہے ہم ان کے پیٹ سے گوبر اور خون کے درمیان سے نکال کر تمہیں خالص دودھ پلاتے ہیں جو پینے والوں کے لئے خوشگوار ہے۔'),
(1968, 16, 67, 'اور ہم کھجور اور انگور کے پھلوں سے بھی (تمہیں اس حالت میں) جن سے تم نشہ آور عرق بھی بنا لیتے ہو اور بہترین رزق بھی یقیناً اس میں ایک نشانی ہے ان لوگوں کے لئے جو عقل سے کام لیتے ہیں۔'),
(1969, 16, 68, 'اور آپ کے پروردگار نے شہد کی مکھی کو یہ وحی کی کہ پہاڑوں میں، درختوں میں اور ان چھپروں میں جن پر لوگ بیلیں چڑھاتے ہیں گھر (چھتے) بنایا کر۔'),
(1970, 16, 69, 'پھر ہر قسم کے پھلوں (اور پھولوں) کا رس چوسا کر پس اپنے پروردگار کی ہموار کردہ راہوں پر چلتی رہ۔ شہد کی ان مکھیوں کے پیٹ سے مختلف رنگوں کا وہ مشروب نکلتا ہے جس میں لوگوں کے لئے شفا ہے اس میں ان لوگوں کے لئے نشانی ہے جو غور و فکر کرتے ہیں۔'),
(1971, 16, 70, 'اور اللہ نے تمہیں پیدا کیا ہے پھر وہی تمہیں وفات دے گا اور تم میں سے بعض ایسے بھی ہیں کہ جن کو (بڑھاپے کی) بدترین عمر تک لوٹا دیا جاتا ہے تاکہ (بہت کچھ) جاننے کے بعد کچھ بھی نہ جانیں بے شک اللہ بڑا جاننے والا، بڑا قدرت والا ہے۔'),
(1972, 16, 71, 'اور اللہ نے تم میں سے بعض کو بعض پر رزق کے معاملہ میں فضیلت (برتری) دی ہے پھر جن لوگوں کو یہ فضیلت دی گئی ہے وہ اپنا رزق اپنے غلاموں (اور زیر دستوں) کو لوٹا دینے والے نہیں ہیں تاکہ وہ سب اس میں برابر ہو جائیں تو کیا وہ اللہ کی نعمت کا انکار کرتے ہیں؟'),
(1973, 16, 72, 'اور اللہ نے ہی تمہاری جنس سے تمہارے لئے بیویاں بنائیں اور پھر ان بیویوں سے بیٹے اور پوتے عنایت فرمائے اور تمہاری روزی کیلئے تمہیں پاکیزہ چیزیں عطا فرمائیں تو کیا (پھر بھی) یہ لوگ باطل کو مانتے رہیں گے اور اللہ کی نعمتوں کا انکار کرتے رہیں گے؟'),
(1974, 16, 73, 'اور یہ اللہ کو چھوڑ کر ایسوں کی عبادت (پرستش) کرتے ہیں جو ان کو آسمان و زمین سے روزی پہنچانے کا کوئی اختیار نہیں رکھتے اور نہ ہی وہ یہ کام کر سکتے ہیں۔'),
(1975, 16, 74, 'پس تم اللہ کے لئے مثالیں نہ دیا کرو بے شک اللہ بہتر جانتا ہے تم نہیں جانتے۔'),
(1976, 16, 75, 'اللہ ایک مثال پیش کرتا ہے کہ ایک غلام ہے جو دوسرے کا مملوک ہے اور کسی چیز پر اختیار نہیں رکھتا اور ایک دوسرا ہے جسے ہم نے بہترین رزق عطا کر رکھا ہے اور وہ اس میں سے پوشیدہ اور علانیہ طور پر خرچ بھی کرتا ہے کیا وہ سب برابر ہو سکتے ہیں؟ الحمد ﷲ (سب تعریفیں اللہ کے لئے ہیں) مگر اکثر لوگ (اتنی سیدھی بات بھی) نہیں جانتے۔'),
(1977, 16, 76, 'اور اللہ دو آدمیوں کی مثال پیش کرتا ہے جن میں سے ایک گونگا ہے کسی بات پر قدرت نہیں رکھتا اور وہ اپنے آقا پر بوجھ ہے وہ اسے جدھر بھی بھیجے وہ کسی بھلائی کے ساتھ واپس نہیں آتا اور دوسرا وہ ہے جو عدل و انصاف کا حکم دیتا ہے اور خود راہِ راست پر قائم ہے کیا وہ (پہلا) اور یہ (دوسرا) برابر ہو سکتے ہیں؟'),
(1978, 16, 77, 'آسمان و زمین کا سارا غیب اللہ ہی کے لئے ہے اور قیامت کا حکم تو صرف ایک پل جھپکنے کے برابر یا اس سے بھی قریب تر ہے اور یقینا اللہ ہر شے پر قدرت رکھنے والا ہے۔'),
(1979, 16, 78, 'اور اللہ ہی نے تمہیں شکمِ مادر سے اس طرح نکالا ہے کہ تم کچھ نہیں جانتے تھے اور اسی نے تمہارے لئے کان، آنکھ اور دل قرار دیئے ہیں کہ شاید تم شکر گزار بن جاؤ۔'),
(1980, 16, 79, 'کیا ان لوگوں نے پرندوں کی طرف نہیں دیکھا کہ وہ کس طرح فضائے آسمان میں مسخر ہیں کہ اللہ کے علاوہ انہیں کوئی روکنے والا اور سنبھالنے والا نہیں ہے بے شک اس میں بھی اس قوم کے لئے بہت سی نشانیاں ہیں جو ایمان رکھنے والی قوم ہے۔'),
(1981, 16, 80, 'اور اللہ ہی نے تمہارے لئے تمہارے گھروں کو وجہِ سکون بنایا ہے اور تمہارے لئے جانوروں کی کھالوں سے ایسے گھر بنا دیئے ہیں جن کو تم روزِ سفر بھی ہلکا سمجھتے ہو اور روزِ قیامت بھی ہلکا محسوس کرتے ہو اور پھر ان کے اون، روئیں اور بالوں سے مختلف سامانِ زندگی اور ایک مدت کے لئے کارآمد چیزیں بنا دیں۔'),
(1982, 16, 81, 'اور اللہ ہی نے تمہارے لئے مخلوقات کا سایہ قرار دیا ہے اور پہاڑوں میں چھپنے کی جگہیں بنائی ہیں اور ایسے پیراہن بنائے ہیں جو گرمی سے بچا سکیں اور پھر ایسے پیراہن بنائے جو ہتھیاروں کی زد سے بچا سکیں۔ وہ اسی طرح اپنی نعمتوں کو تمہارے اوپر تمام کر دیتا ہے کہ شاید تم اطاعت گزار بن جاؤ۔'),
(1983, 16, 82, 'پھر اس کے بعد بھی اگر یہ ظالم منہ پھیر لیں تو آپ کا کام صرف واضح پیغام کا پہنچا دینا ہے اور بس۔'),
(1984, 16, 83, 'یہ لوگ اللہ کی نعمت کو پہچانتے ہیں اور پھر انکار کرتے ہیں اور ان کی اکثریت کافر ہے۔'),
(1985, 16, 84, 'اور جس دن ہم ہر امت سے ایک گواہ اٹھا کر کھڑا کریں گے پھر کافروں کو اجازت نہیں دی جائے گی اور نہ ہی ان سے اللہ کو راضی کرنے کی فرمائش کی جائے گی۔'),
(1986, 16, 85, 'اور جب ظالم لوگ (ایک بار) عذاب کو دیکھ لیں گے تو پھر نہ ان کے عذاب میں کوئی تخفیف کی جائے گی اور نہ ہی انہیں مہلت دی جائے گی۔'),
(1987, 16, 86, 'اور جن لوگوں نے (دنیا میں) شرک کیا تھا جب وہ (قیامت کے دن) اپنے بنائے ہوئے شریکوں کو دیکھیں گے تو کہیں گے اے ہمارے پروردگار یہ ہیں ہمارے وہ شرکاء جنہیں ہم تجھے چھوڑ کر پکارا کرتے تھے تو وہ (شرکاء) یہ بات ان کی طرف پھینک دیں گے کہ تم جھوٹے ہو۔'),
(1988, 16, 87, 'اور اس دن سب اللہ کے سامنے جھک جائیں گے اور وہ ساری افتراء پردازیاں غائب ہو جائیں گی جو وہ کیا کرتے تھے۔'),
(1989, 16, 88, 'جن لوگوں نے کفر کیا اور لوگوں کو اللہ کی راہ سے روکا۔ تو ہم ان کے فساد پھیلانے کی پاداش میں ان کے عذاب پر ایک اور عذاب کا اضافہ کر دیں گے۔'),
(1990, 16, 89, 'اور جس دن ہم ہر امت میں سے ایک گواہ اٹھا کھڑا کریں گے جو ان کے بالمقابل گواہی دے گا اور آپ کو ان سب کے بالمقابل گواہ بنا کر لائیں گے اور ہم نے آپ پر وہ کتاب نازل کی ہے جو ہر بات کو کھول کر بیان کرتی ہے اور سرِ تسلیم خم کرنے والوں کے لئے سراسر ہدایت، رحمت اور بشارت ہے۔'),
(1991, 16, 90, 'بے شک اللہ عدل، احسان اور قرابتداروں کو (ان کا حق) دینے کا حکم دیتا ہے اور بے حیائی، برائی اور ظلم و زیادتی کرنے سے منع کرتا ہے اور تمہیں نصیحت کرتا ہے تاکہ تم نصیحت قبول کرو۔'),
(1992, 16, 91, 'اور اللہ کے عہد کو پورا کرو جب بھی تم کوئی عہد کرو اور اپنی قسموں کو پختہ کرنے کے بعد نہ توڑو جبکہ تم اللہ کو ضامن بنا چکے ہو بے شک اللہ جانتا ہے جو کچھ تم کرتے ہو۔'),
(1993, 16, 92, 'خبردار تم اس عورت کی مانند نہ ہو جانا جس نے بڑی مضبوطی سے سوت کاتنے کے بعد اسے ٹکڑے ٹکڑے کر دیا تم اپنی قسموں کو اپنے درمیان مکر و فریب کا ذریعہ بناتے ہو۔ تاکہ ایک گروہ دوسرے سے زیادہ فائدہ حاصل کرے اور اللہ اس بات سے تمہاری آزمائش کرتا ہے اور یقیناً قیامت کے دن وہ تمہارے لئے وہ حکمت ظاہر کر دے گا جس میں تم اختلاف کرتے ہو۔'),
(1994, 16, 93, 'اور اگر اللہ چاہتا تو تم سب کو ایک امت بنا دیتا لیکن وہ جسے چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے اور تم جو کچھ کر رہے ہو اس کے بارے میں تم سے ضرور سوال کیا جائے گا۔'),
(1995, 16, 94, 'اور تم اپنے درمیان اپنی قسموں کو مکر و فریب کا ذریعہ نہ بناؤ۔ کہیں کوئی قدم جم جانے کے بعد پھسل نہ جائے اور اس طرح تمہیں اللہ کی راہ سے روکنے کی پاداش میں برائی کا مزہ چکھنا پڑے اور تمہارے لئے عذابِ عظیم ہے۔'),
(1996, 16, 95, 'اور اللہ کے عہد و پیمان کو تھوڑی سی قیمت کے عوض فروخت نہ کرو۔ بے شک جو کچھ اللہ کے پاس ہے وہ تمہارے لئے بہتر ہے اگر تم جانتے ہو۔'),
(1997, 16, 96, 'جو کچھ تمہارے پاس ہے وہ ختم ہو جائے گا اور جو کچھ اللہ کے پاس ہے وہ باقی رہنے والا ہے اور ہم یقیناً صبر کرنے والوں کو ان کا اجر ان کے بہترین اعمال کے مطابق عطا کریں گے۔'),
(1998, 16, 97, 'جو کوئی بھی نیک عمل کرے خواہ مرد ہو یا عورت بشرطیکہ وہ مؤمن ہو تو ہم اسے (دنیا) میں پاک و پاکیزہ زندگی بسر کرائیں گے اور (آخرت میں) ان کا اجر ان کے بہترین اعمال کے مطابق عطا کریں گے۔'),
(1999, 16, 98, 'پس جب آپ قرآن پڑھنے لگیں تو مردود شیطان سے خدا کی پناہ مانگ لیا کریں۔'),
(2000, 16, 99, 'جو لوگ ایماندار ہیں اور اپنے پروردگار پر بھروسہ کرتے ہیں ان پر اس (شیطان) کا کوئی تسلط نہیں ہے۔'),
(2001, 16, 100, 'اس کا قابو تو صرف ان لوگوں پر چلتا ہے جو اسے اپنا دوست بناتے ہیں اور اس کی وجہ سے شرک کرتے ہیں۔'),
(2002, 16, 101, 'اور جب ہم ایک آیت کو کسی اور آیت سے بدلتے ہیں تو اللہ بہتر جانتا ہے کہ وہ کیا نازل کر رہا ہے تو یہ لوگ کہتے ہیں کہ تم (یہ کلام) اپنے دل سے گھڑ لیا کرتے ہو حالانکہ ان میں سے اکثر لوگ (حقیقتِ حال کو) نہیں جانتے۔'),
(2003, 16, 102, 'آپ کہہ دیجیے کہ اس (قرآن) کو تمہارے پروردگار کی طرف سے روح القدس نے حق کے ساتھ اتارا ہے تاکہ ایمان لانے والوں کو ثابت قدم رکھے اور اطاعت گزاروں کے لئے ہدایت اور نجات کی خوشخبری ثابت ہو۔'),
(2004, 16, 103, 'اور بے شک ہم جانتے ہیں کہ وہ (قرآن کے بارے میں) کہتے ہیں کہ اس شخص (پیغمبر) کو ایک آدمی سکھاتا ہے حالانکہ جس شخص کی طرف یہ نسبت دیتے ہیں اس کی زبان عجمی ہے اور یہ (قرآن) تو فصیح عربی زبان میں ہے۔'),
(2005, 16, 104, 'جو لوگ آیاتِ الٰہی پر ایمان نہیں لاتے اللہ کبھی انہیں ہدایت نہیں دیتا اور ان کے لئے دردناک عذاب ہے۔'),
(2006, 16, 105, 'جھوٹ تو صرف وہی لوگ گھڑتے ہیں جو اللہ کی آیتوں پر ایمان نہیں رکھتے اور یہی لوگ جھوٹے ہیں۔'),
(2007, 16, 106, 'جو کوئی اللہ پر ایمان لانے کے بعد کفر کرے سوائے اس صورت کے کہ اسے مجبور کیا جائے جبکہ اس کا دل ایمان پر مطمئن ہو (کہ اس پر کوئی مؤاخذہ نہیں ہے) لیکن جو کشادہ دلی سے کفر اختیار کرے (زبان سے کفر کرے اور اس کا دل اس کفر پر رضامند ہو) تو ایسے لوگوں پر اللہ کا غضب ہے اور ان کے لئے بڑا عذاب ہے۔'),
(2008, 16, 107, 'یہ اس لئے ہے کہ انہوں نے دنیوی زندگی کو آخرت پر ترجیح دی ہے اور اللہ کافروں کو ہدایت نہیں کیا کرتا۔'),
(2009, 16, 108, 'یہی وہ لوگ ہیں کہ جن کے دلوں، کانوں اور آنکھوں پر اللہ نے مہر لگا دی اور یہی لوگ بالکل غافل ہیں۔'),
(2010, 16, 109, 'اور یقیناً یہی لوگ آخرت میں نقصان و زیاں اٹھانے والے ہیں۔'),
(2011, 16, 110, 'پھر آپ کا پروردگار ان لوگوں کے لئے جنہوں نے (سخت) آزمائش میں مبتلا ہونے کے بعد ہجرت کی پھر جہاد کیا اور صبر سے کام لیا یقیناً (آپ کا) پروردگار ان اعمال کے بعد بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2012, 16, 111, '(اس دن کو یاد کرو) جس دن ہر شخص اپنی ذات کی خاطر جھگڑا کرتا ہوا آئے گا اور ہر شخص کو اس کے عمل کا پورا پورا بدلہ دیا جائے گا اور ان پر (کسی طرح بھی) کوئی ظلم نہیں کیا جائے گا۔'),
(2013, 16, 112, 'اور اللہ نے ایک بستی کی مثال بیان کی ہے جو امن و اطمینان سے (آباد) تھی اس کی روزی فراغت سے آرہی تھی پس اس نے کفرانِ نعمت شروع کیا تو اللہ نے اس کے باشندوں کے کرتوتوں کی پاداش میں اسے یہ مزہ چکھایا کہ بھوک اور خوف کو اس کا اوڑھنا بنا دیا۔'),
(2014, 16, 113, 'اور پھر خود انہی میں سے ایک رسول ان کے پاس آیا مگر انہوں نے اسے جھٹلا دیا پس عذاب نے انہیں اس حال میں آپکڑا کہ وہ ظالم تھے۔'),
(2015, 16, 114, '(اے لوگو) خدا نے تمہیں جو حلال (اور) پاک و پاکیزہ رزق دیا ہے اس میں سے کھاؤ (پیو) اور اس کے ساتھ ساتھ اللہ کی نعمت کا شکر بھی ادا کرو اگر تم صرف اسی کی عبادت کرتے ہو۔'),
(2016, 16, 115, 'اس نے تم پر صرف مردار، خون، خنزیر کا گوشت اور وہ جس پر ذبح کے وقت غیر اللہ کا نام بلند کیا گیا ہو حرام کیا ہے۔ پس جو (ان کے کھانے پر) مجبور ہو جائے نہ باغی ہو اور نہ حد سے تجاوز کرنے والا (تو کوئی مضائقہ نہیں) بے شک خدا بڑا بخشنے والا، بڑا مہربان ہے۔'),
(2017, 16, 116, '(خبردار) تمہاری زبانوں پر جو جھوٹی بات آجائے (اور وہ جھوٹے احکام لگائیں) ان کے متعلق نہ کہو کہ یہ حلال ہے اور یہ حرام! اس طرح تم اللہ پر جھوٹا افترا باندھوگے بے شک جو لوگ خدا پر جھوٹا بہتان باندھتے ہیں وہ کبھی فلاح نہیں پاتے۔'),
(2018, 16, 117, 'اس افترا پردازی کا چند روزہ فائدہ ہے (آخرکار) ان کے لئے دردناک عذاب ہے۔'),
(2019, 16, 118, 'اور ہم نے یہودیوں پر وہ چیزیں حرام کر دیں جن کا ذکر ہم اس سے پہلے (سورہ انعام میں) آپ سے کر چکے ہیں اور ہم نے ان پر کوئی ظلم نہیں کیا تھا بلکہ وہ خود ہی اپنے اوپر ظلم کرتے تھے۔'),
(2020, 16, 119, 'بے شک آپ کا پروردگار ان لوگوں کے لئے جو جہالت و نادانی سے برائی کر گزرتے ہیں اور پھرا س کے بعد توبہ کر لیتے ہیں اور اپنی اصلاح کر لیتے ہیں یقیناً آپ کا پروردگار اس کے بعد بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2021, 16, 120, 'بے شک ابراہیم(ع) (اپنی ذات میں) ایک پوری امت تھے اللہ کے مطیع فرمان تھے (سب سے کٹ کر) یکسوئی سے (خدا کی طرف) مائل تھے اور ہرگز مشرکوں میں سے نہیں تھے۔'),
(2022, 16, 121, 'وہ اللہ کی نعمتوں کے شکر گزار تھے اللہ نے انہیں منتخب کر لیا تھا اور انہیں سیدھے راستہ پر لگا دیا تھا۔'),
(2023, 16, 122, 'ہم نے انہیں دنیا میں بھی بھلائی دی اور آخرت میں تو وہ صالحین میں سے ہی ہیں۔'),
(2024, 16, 123, 'اے رسول(ص) پھر ہم نے آپ کی طرف وحی کی کہ یکسو ہوکر ابراہیم کی ملت (طریقہ) کی پیروی کریں جو یقیناً مشرکوں میں سے نہیں تھے۔'),
(2025, 16, 124, '(باقی رہا) سبت (کے احترام کا معاملہ) تو یہ صرف ان لوگوں پر عائد کیا گیا تھا جنہوں نے اس کے بارے میں اختلاف کیا تھا اور اللہ قیامت کے دن ان کے درمیان فیصلہ کرے گا ان باتوں کے بارے میں جن میں وہ باہم اختلاف کرتے تھے۔'),
(2026, 16, 125, '(اے پیغمبر(ص)) آپ اپنے پروردگار کے راستے کی طرف (لوگوں کو) بلائیں حکمت اور عمدہ نصیحت کے ساتھ اور لوگوں سے بہترین انداز میں بحث و مباحثہ کریں۔ آپ کا پروردگار ہی بہتر جانتا ہے کہ اس کے راستہ سے بھٹکا ہوا کون ہے اور وہی بہتر جانتا ہے کہ ہدایت یافتہ کون ہے؟'),
(2027, 16, 126, 'اور اگر تم بدلہ لینا چاہو تو اس قدر لو جس قدر تم پر زیادتی کی گئی ہے اور اگر تم صبر کرو تو وہ صبر کرنے والوں کے حق میں بہتر ہے۔'),
(2028, 16, 127, 'اور (اے پیغمبر) آپ صبر کیجئے اور آپ کا صبر کرنا تو محض اللہ کی توفیق سے ہے اور آپ ان لوگوں کے حال پر غم نہ کیجئے اور نہ ہی ان کی مکاریوں سے دل تنگ ہو جایئے۔'),
(2029, 16, 128, 'بے شک اللہ ان لوگوں کے ساتھ ہے جو تقویٰ اختیار کرتے ہیں اور جو (خلقِ خدا سے) حسنِ سلوک کرتے ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاک ہے وہ (خدا) جو اپنے بندہ (خاص) کو رات کے ایک حصہ میں مسجد الحرام سے اس مسجدِ اقصیٰ تک لے گیا جس کے گرد و پیش ہم نے برکت دی ہے تاکہ ہم انہیں اپنی (قدرت کی) کچھ نشانیاں دکھائیں بے شک وہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(2031, 17, 2, 'اور ہم نے موسیٰ کو کتاب دی اور اسے بنی اسرائیل کے لئے ذریعۂ ہدایت بنایا کہ (دیکھو) میرے سوا کسی کو اپنا کارساز نہ بنانا۔'),
(2032, 17, 3, 'اے ان لوگوں کی اولاد جن کو ہم نے نوح کے ساتھ (کشتی میں) سوار کر لیا تھا بے شک وہ (نوح) ایک شکر گزار بندہ تھا۔'),
(2033, 17, 4, 'اور ہم نے اس کتاب (توراۃ) میں بنی اسرائیل کو آگاہ کر دیا تھا کہ تم ضرور زمین میں دو مرتبہ فساد برپا کروگے اور بڑی سرکشی کروگے۔'),
(2034, 17, 5, 'چنانچہ جب ان دونوں میں سے پہلے وعدہ کا وقت آگیا تو ہم نے (تمہاری سرکوبی کیلئے) اپنے کچھ ایسے سخت جنگجو بندے بھیج دیئے جو تمہاری آبادیوں کے اندر گھس گئے اور (خدا) کا وعدہ پورا ہو کر رہا۔'),
(2035, 17, 6, 'اور پھر ہم نے گردشِ زمانہ کو تمہارے حق میں دشمن کے خلاف کر دیا (تمہیں ان پر غلبہ دے دیا) اور مال اور اولاد سے تمہاری مدد کی اور تمہیں کثیر التعداد بنا دیا۔'),
(2036, 17, 7, '(دیکھو) اگر تم اچھے کام کروگے تو اپنے لئے ہی کروگے اور اگر برے کام کروگے تو وہ بھی اپنے لئے کروگے پس جب دوسرا وعدہ آگیا (تو ہم نے اور بندوں کو مسلط کر دیا) تاکہ تمہارے چہروں کو بگاڑ دیں اور مسجد (بیت المقدس) میں اسی طرح (جبراً) داخل ہو جائیں جس طرح پہلی بار (حملہ آور) داخل ہوئے تھے اور تاکہ جس جس چیز پر وہ قابو پائیں اسے تباہ و برباد کر دیں۔'),
(2037, 17, 8, 'ہو سکتا ہے کہ تمہارا پروردگار تم پر رحم فرمائے اور اگر تم پھر (فساد و سرکشی کی طرف) لوٹے تو پھر ہم بھی اسی طرح لوٹیں گے اور ہم نے جہنم کو کافروں کے لئے قید خانہ بنا دیا ہے۔'),
(2038, 17, 9, 'یقیناً یہ قرآن اس راستہ کی طرف راہنمائی کرتا ہے جو سب سے زیادہ سیدھا ہے اور خوشخبری دیتا ہے ان اہلِ ایمان کو جو نیک عمل کرتے ہیں کہ ان کے لئے بہت بڑا اجر ہے۔'),
(2039, 17, 10, 'اور جو لوگ آخرت پر ایمان نہیں رکھتے ان کے لئے ہم نے دردناک عذاب تیار کر رکھا ہے۔'),
(2040, 17, 11, 'اور انسان برائی کی دعا اسی طرح کرتا ہے جس طرح اچھائی کی دعا کرتا ہے (دراصل) انسان بڑا ہی جلد باز ہے۔'),
(2041, 17, 12, 'اور ہم نے رات اور دن کو (اپنی قدرت کی) دو نشانیاں بنایا ہے سو ہم نے رات کی نشانی کو دھندلا بنایا اور دن کی نشانی کو روشن بنایا تاکہ تم اپنے پروردگار کا فضل و کرم (رزق) تلاش کرو اور تاکہ برسوں کی گنتی اور (ہر طرح کا) حساب معلوم کر سکو اور ہم نے ہر (ضروری) چیز کو تفصیل سے بیان کر دیا ہے۔'),
(2042, 17, 13, 'اور ہم نے ہر انسان کا (نامہ) عمل اس کی گردن میں ڈال دیا ہے اور قیامت کے دن ہم ایک کتاب (نوشتہ) نکالیں گے جسے وہ اپنے سامنے کھلا ہوا پائے گا۔'),
(2043, 17, 14, '(ہم حکم دیں گے کہ) اپنا نامۂ عمل پڑھ آج تو خود ہی اپنے حساب کے لئے کافی ہے۔'),
(2044, 17, 15, 'جو راہِ راست اختیار کرتا ہے وہ اپنے فائدہ کے لئے ہی کرتا ہے اور جو گمراہی اختیار کرتا ہے تو اس کا وبال اسی پر ہے اور کوئی بوجھ اٹھانے والا کسی دوسرے کا بوجھ نہیں اٹھائے گا اور ہم اس وقت تک عذاب نازل نہیں کرتے جب تک (اتمامِ حجت کی خاطر) کوئی رسول بھیج نہیں دیتے۔'),
(2045, 17, 16, 'اور جب ہم کسی بستی کو ہلاک کرنا چاہتے ہیں تو ہم وہاں کے خوشحال لوگوں کو (انبیاء کے ذریعہ سے اپنی اطاعت کا) حکم دیتے ہیں اور وہ (اطاعت کی بجائے) نافرمانی کرنے لگتے ہیں تب اس پر (عذاب کی) بات ثابت ہو جاتی ہے اور ہم اسے تباہ و برباد کر دیتے ہیں۔'),
(2046, 17, 17, 'اور ہم نے نوح کے بعد کتنی ہی قوموں کو (ان کے گناہوں کی پاداش میں) ہلاک کیا اور آپ کا پروردگار اپنے بندوں کے گناہوں سے باخبر ہونے اور دیکھنے کی حیثیت سے کافی ہے۔'),
(2047, 17, 18, 'جو کوئی دنیا کا خواہشمند ہو تو ہم جسے جتنا دینا چاہتے ہیں اسی (دنیا) میں جلدی دے دیتے ہیں پھر اس کے لئے دوزخ قرار دیتے ہیں جس میں وہ ملامت زدہ اور راندہ ہوا داخل ہوگا (اور اسے تاپے گا)۔'),
(2048, 17, 19, 'اور جو کوئی آخرت کا طلبگار ہوتا ہے اور اس کے لئے ایسی کوشش بھی کرے جیسی کہ کرنی چاہیئے درآنحالیکہ وہ مؤمن بھی ہو تو یہ وہ ہیں جن کی کوشش مشکور ہوگی۔'),
(2049, 17, 20, 'ہم ہر ایک کی امداد کرتے ہیں اِن کی بھی اور اُن کی بھی آپ کے پروردگار کی عطا و بخشش سے اور آپ کے پروردگار کی عطا کسی پر بند نہیں ہے۔'),
(2050, 17, 21, '(دیکھو) ہم نے (یہاں) کس طرح بعض لوگوں کو بعض پر فضیلت دی ہے اور آخرت تو درجات کے اعتبار سے بہت بڑی ہے اور فضیلت کے لحاظ سے بھی بہت بڑی ہے۔'),
(2051, 17, 22, 'اللہ کے ساتھ کوئی الٰہ (معبود) نہ ٹھہراؤ۔ ورنہ ملامت زدہ ہوکر اور بے یار و مددگار ہو کر بیٹھے رہوگے۔'),
(2052, 17, 23, 'اور آپ کے پروردگار نے فیصلہ کر دیا ہے کہ تم اس کے سوا اور کسی کی عبادت نہ کرو اور ماں باپ کے ساتھ بھلائی کرو اگر تمہارے پاس ان میں سے ایک یا دونوں بڑھاپے تک پہنچ جائیں تو انہیں اف تک نہ کہو اور نہ انہیں جھڑکو۔ اور ان سے احترام کے ساتھ بات کرو۔'),
(2053, 17, 24, 'اور ان کے سامنے مہر و مہربانی کے ساتھ انکساری کا پہلو جھکائے رکھو اور کہو اے پروردگار تو ان دونوں پر اسی طرح رحم و کرم فرما جس طرح انہوں نے میرے بچپنے میں مجھے پالا( اور میری پرورش کی)۔'),
(2054, 17, 25, 'تمہارا پروردگار بہتر جانتا ہے جو کچھ تمہارے دلوں میں ہے اگر تم صالح اور نیک کردار ہوئے تو وہ توبہ و انابہ کرنے والوں کے لئے بڑا ہی بخشنے والا ہے۔'),
(2055, 17, 26, 'اور (دیکھو) قریبی رشتہ دار کو اس کا حق دے دو اور مسکین و مسافر کو بھی اور (خبردار) فضول خرچی نہ کرو۔'),
(2056, 17, 27, 'بے شک فضول خرچی کرنے والے شیطانوں کے بھائی بند ہیں اور شیطان اپنے پروردگار کا بڑا ناشکرا ہے۔'),
(2057, 17, 28, 'اور اگر تمہیں ان لوگوں سے پہلوتہی کرنی پڑے اس انتظار میں کہ تمہارے پروردگار کی طرف سے رحمت (کشائش) آئے جس کے تم امیدوار ہو تو ان سے نرم انداز میں بات کرو۔'),
(2058, 17, 29, 'اور (دیکھو) نہ تو اپنا ہاتھ اپنی گردن سے باندھ لو اور نہ ہی اسے بالکل کھول دو کہ (ایسا کرنے سے) ملامت زدہ (اور) تہی دست ہو کر بیٹھ جاؤگے۔'),
(2059, 17, 30, 'تمہارا پروردگار جس کسی کی چاہتا ہے روزی فراخ کر دیتا ہے اور جس کی چاہتا ہے تنگ کر دیتا ہے بے شک وہ اپنے بندوں کے حال سے بڑا باخبر ہے اور بڑا نگاہ رکھنے والا ہے۔'),
(2060, 17, 31, 'اور فقر و فاقہ کے خوف سے اپنی اولاد کو قتل نہ کرو۔ ہم ہی انہیں اور تمہیں روزی دیتے ہیں بے شک انہیں قتل کرنا بڑا جرم ہے۔'),
(2061, 17, 32, 'اور زناکاری کے قریب بھی نہ جاؤ یقینا وہ بہت بڑی بے حیائی ہے اور بہت برا راستہ ہے۔'),
(2062, 17, 33, 'اور جس جان کا مارنا اللہ نے حرام قرار دیا ہے اس کو قتل نہ کرو۔ مگر حق کے ساتھ اور جو شخص ناحق قتل کیا جائے تو ہم نے اس کے وارث کو (قصاص کا) اختیار دے دیا ہے پس چاہیئے کہ وہ قتل میں حد سے آگے نہ بڑھے ضرور اس کی مدد کی جائے گی۔'),
(2063, 17, 34, 'اور یتیم کے مال کے قریب بھی نہ جاؤ سوائے احسن طریقہ کے یہاں تک کہ وہ اپنی پختگی کے سن و سال (جوانی) تک پہنچ جائے اور (اپنے عہد) کو پورا کرو بے شک عہد کے بارے میں تم سے بازپرس کی جائے گی۔'),
(2064, 17, 35, 'اور جب ناپو تولو تو ناپ پوری پوری رکھا کرو اور جب تولو تو صحیح ترازو سے تولو یہی بہتر ہے اور انجام کے اعتبار سے بھی بہت اچھا ہے۔'),
(2065, 17, 36, 'اور جس چیز کا تمہیں علم نہیں ہے اس کے پیچھے نہ پڑو یقیناً کان، آنکھ اور دل ان سب کے بارے میں (تم سے) بازپرس کی جائے گی۔'),
(2066, 17, 37, 'اور زمین میں اکڑتے ہوئے نہ چلو کیونکہ تم (اس طرح چلنے سے) نہ زمین کو پھاڑ سکتے ہو اور نہ لمبائی میں پہاڑوں تک پہنچ سکتے ہو۔'),
(2067, 17, 38, 'ان سب باتوں میں سے ہر بری بات اللہ کو سخت ناپسند ہے۔'),
(2068, 17, 39, 'یہ سب باتیں حکمت و دانائی کی ان باتوں میں سے ہیں جو آپ کے پروردگار کی طرف سے آپ پر وحی کی گئی ہیں اللہ کے ساتھ کوئی دوسرا الٰہ (خدا) قرار نہ دو ورنہ اس حال میں جہنم میں ڈالے جاؤگے کہ تم ملامت زدہ اور راندۂ بارگاہ ہوگے۔'),
(2069, 17, 40, 'کیا تمہارے پروردگار نے تمہیں تو بیٹوں کے لئے منتخب کر لیا اور خود اپنے لئے فرشتوں کو بیٹیاں بنا لیا؟ اس میں شک نہیں ہے کہ تم بڑی سخت بات کہتے ہو۔'),
(2070, 17, 41, 'اور بے شک ہم نے (مطالبِ حقہ کو) اس قرآن میں مختلف انداز میں بار بار بیان کیا ہے تاکہ وہ نصیحت حاصل کریں مگر (اس تکرار) نے ان کی نفرت میں اضافہ کیا ہے۔'),
(2071, 17, 42, '(اے پیغمبر(ص)!) آپ کہہ دیجیے کہ اگر اس (خدا) کے ساتھ اور خدا بھی ہوتے جیساکہ یہ (کافر) کہتے ہیں تو وہ ضرور مالکِ عرش تک پہنچنے کا کوئی راستہ تلاش کرتے (اور مقابلہ تک نوبت پہنچ جاتی)۔'),
(2072, 17, 43, 'وہ پاک ہے اور کہیں برتر و بالا ہے ان باتوں سے جو یہ لوگ کہتے ہیں۔'),
(2073, 17, 44, 'ساتوں آسمان اور زمین اور جو کوئی بھی ان میں موجود ہیں اس کی تسبیح کرتے ہیں اور کوئی بھی چیز ایسی نہیں ہے جو حمد کے ساتھ اس کی تسبیح نہ کرتی ہو۔ لیکن تم ان کی تسبیح کو سمجھتے نہیں ہو بے شک وہ بڑا بردبار، بڑا بخشنے والا ہے۔'),
(2074, 17, 45, '(اے پیغمبر(ص)!) جب آپ قرآن پڑھتے ہیں تو ہم آپ کے اور ان لوگوں کے درمیان جو آخرت پر ایمان نہیں رکھتے ایک پوشیدہ پردہ ڈال دیتے ہیں۔'),
(2075, 17, 46, 'اور (ان کے پیہم انکار کی وجہ سے گویا) ہم نے ان کے دلوں پر پردے ڈال دیے ہیں تاکہ وہ اسے نہ سمجھیں اور کانوں میں گرانی (پیدا کر دیتے ہیں) اور جب آپ قرآن میں اپنے واحد و یکتا پروردگار کا ذکر کرتے ہیں تو وہ لوگ نفرت کرتے ہوئے پچھلے پاؤں پلٹ جاتے ہیں۔'),
(2076, 17, 47, 'ہم خوب جانتے ہیں کہ وہ غور سے کیا سنتے ہیں؟ جب یہ آپ کی طرف کان لگاتے ہیں اور (ہم خوب جانتے ہیں) جب یہ ظالم سرگوشیاں کرتے ہوئے کہتے ہیں کہ تم تو ایک سحر زدہ آدمی کی پیروی کر رہے ہو۔'),
(2077, 17, 48, 'دیکھیے یہ کیسی کیسی مثالیں آپ کے لئے بناتے ہیں جس کی وجہ سے وہ ایسے گمراہ ہوگئے ہیں کہ راستہ پا ہی نہیں سکتے۔'),
(2078, 17, 49, 'اور وہ کہتے ہیں کہ جب ہم (مر کر) ہڈیاں اور چُورا ہو جائیں گے تو کیا از سرِ نو پیدا کرکے اٹھائے جائیں گے؟'),
(2079, 17, 50, 'کہہ دیجیے! کہ تم پتھر ہو جاؤ یا لوہا۔'),
(2080, 17, 51, 'یا کوئی اور ایسی چیز (جس کا دوبارہ پیدا ہونا) تمہارے خیال میں بڑا (مشکل) ہو (بہرحال تمہیں دوبارہ زندہ کیا جائے گا) وہ (یہ سن کر) ضرور کہیں گے کہ کون ہمیں دوبارہ (زندہ کرکے) لائے گا؟ کہہ دیجیے! وہی جس نے تمہیں پہلی بار پیدا کیا پس وہ اپنے سر جھکا کر کہیں گے کہ کب ایسا ہوگا؟ کہہ دیجئے! کہ شاید بالکل قریب ہو۔'),
(2081, 17, 52, 'وہی دن کہ جب (خدا) تمہیں بلائے گا تو تم اس کی حمد کے ساتھ لبیک کہوگے اور خیال کروگے تم بہت تھوڑی مدت (عالمِ برزخ میں) رہے ہو۔'),
(2082, 17, 53, '(اے رسول) میرے بندوں سے کہہ دو کہ وہ (لوگوں سے) ایسی بات کریں جو بہترین ہو۔ بے شک شیطان ان کے درمیان فساد ڈالتا ہے۔ یقیناً شیطان انسان کا کھلا ہوا دشمن ہے۔'),
(2083, 17, 54, 'تمہارا پروردگار تمہیں خوب جانتا ہے وہ اگر چاہے تو تم پر رحم کرے اور اگر چاہے تو تمہیں سزا دے۔ (اے رسول(ص)) ہم نے آپ کو ان پر داروغہ بنا کر نہیں بھیجا۔'),
(2084, 17, 55, 'آپ کا پروردگار خوب جانتا ہے ان کو جو آسمانوں اور زمین میں ہیں اور ہم نے بعض بندوں کو بعض پر فضیلت دی ہے اور ہم نے داؤد کو زبور عطا کی۔'),
(2085, 17, 56, '(اے رسول(ص)) ان لوگوں سے کہہ دو کہ جنہیں تم اللہ کے سوا (کارساز) سمجھتے ہو۔ ذرا انہیں پکار کر دیکھو۔ وہ نہ تو تم سے تکلیف دور کر سکتے ہیں اور نہ ہی (اسے) بدل سکتے ہیں۔'),
(2086, 17, 57, 'جن کو یہ لوگ پکارتے ہیں وہ تو خود اپنے پروردگار کی بارگاہ میں وسیلہ تلاش کر رہے ہیں کہ کون زیادہ قرب رکھنے والا ہے؟ اور وہ اللہ کی رحمت کی امید رکھتے ہیں اور اس کے عذاب سے ڈرتے ہیں۔ بے شک تمہارے پروردگار کا عذاب ڈرنے کے قابل ہے۔'),
(2087, 17, 58, 'کوئی بستی ایسی نہیں ہے جسے ہم قیامت سے پہلے ہلاک نہ کر دیں یا سخت عذاب میں مبتلا کر دیں یہ بات کتاب (نوشتۂ تقدیر) میں لکھی ہوئی ہے۔'),
(2088, 17, 59, 'اور (منکرین کی مطلوبہ) نشانیاں بھیجنے سے ہمیں کسی چیز نے نہیں روکا۔ مگر اس بات نے کہ پہلے لوگ انہیں جھٹلا چکے ہیں۔ اور ہم نے قومِ ثمود کو ایک (خاص) اونٹنی دی جوکہ روشن نشانی تھی۔ تو انہوں نے اس پر ظلم کیا اور ہم نشانیاں صرف ڈرانے کیلئے بھیجتے ہیں۔'),
(2089, 17, 60, '(اے رسول وہ وقت یاد کرو) جب ہم نے آپ سے کہا تھا کہ آپ کے پروردگار نے لوگوں کو گھیرے میں لے لیا ہے اور جو منظر ہم نے آپ کو دکھایا تھا۔ اس کو اور اس درخت کو جس پر قرآن میں لعنت کی گئی ہے، ہم نے لوگوں کیلئے آزمائش کا ذریعہ بنایا ہے اور ہم انہیں ڈراتے ہیں مگر یہ ڈرانا ان کی سرکشی میں اضافہ ہی کر رہا ہے۔'),
(2090, 17, 61, '(وہ وقت یاد کرو) جب ہم نے فرشتوں سے کہا تھا آدم(ع) کے سامنے سجدہ ریز ہو جاؤ تو سب نے سجدہ کیا سوائے ابلیس کے۔ اس نے کہا کیا میں اس کو سجدہ کروں جسے تو نے مٹی سے پیدا کیا ہے؟'),
(2091, 17, 62, 'نیز اس نے کہا بھلا مجھے بتا تو سہی یہ شخص جس کو تو نے مجھ پر فضیلت دی ہے (آخر کیوں افضل ہے) اگر تو مجھے قیامت کے دن تک مہلت دے دے تو میں چند افراد کے سوا اس کی پوری اولاد کی بیخ کنی کر ڈالوں گا۔'),
(2092, 17, 63, 'ارشاد ہوا چل نکل جا! ان میں سے جو کوئی بھی تیری پیروی کرے گا تو بے شک جہنم تم سب کی پوری پوری سزا ہے۔'),
(2093, 17, 64, 'اور ان میں سے جس پر تیرا قابو چلتا ہے اپنی پکار سے اسے ورغلا اور ان پر اپنے سواروں اور پیادوں سے چڑھائی کر اور ان کے مالوں اور اولاد میں شریک ہو جا۔ اور ان سے وعدے کر۔ اور شیطان تو دھوکے اور فریب کے سوا ان سے کوئی وعدہ کرتا ہی نہیں ہے۔'),
(2094, 17, 65, 'بے شک جو میرے خاص بندے ہیں ان پر تیرا کوئی قابو نہیں ہے اور کارسازی کیلئے آپ کا پروردگار کافی ہے۔'),
(2095, 17, 66, 'تمہارا پروردگار وہ ہے جو سمندر میں تمہارے لئے کشتی چلاتا ہے تاکہ تم اس کا فضل (مال) تلاش کرو یقینا وہ تم پر بڑا مہربان ہے۔'),
(2096, 17, 67, 'اور جب تمہیں سمندر میں کوئی تکلیف پہنچتی ہے۔ تو اس کے سوا جس جس کو تم پکارتے ہو وہ سب غائب ہو جاتے ہیں اور جب وہ تمہیں (خیریت سے) خشکی پر پہنچا دیتا ہے تو تم روگردانی کرنے لگتے ہو اور انسان بڑا ہی ناشکرا ہے۔'),
(2097, 17, 68, 'کیا تم اس بات سے بے فکر ہوگئے ہو کہ وہ تمہیں خشکی کے کسی گوشے میں زمین کے اندر دھنسا دے یا تم پر کوئی پتھر برسانے والی آندھی بھیج دے۔ پھر تم کسی کو بھی اپنا کارساز نہیں پاؤگے۔'),
(2098, 17, 69, 'کیا تم اس بات سے بے فکر ہوگئے ہو کہ وہ تمہیں دوبارہ اسی (سمندر) میں پہنچائے اور پھر تم پر طوفانی ہوا چلا کر تمہیں ناشکرے پن کی پاداش میں غرق کر دے۔ پھر تم اس کی ہم سے بازپرس کرنے والا کوئی نہیں پاؤگے۔'),
(2099, 17, 70, 'بے شک ہم نے اولادِ آدم کو بڑی عزت و بزرگی دی ہے اور ہم نے انہیں خشکی اور تری میں سوار کیا (سواریاں دیں) پاک و پاکیزہ چیزوں سے روزی دی اور انہیں اپنی مخلوقات پر (جو بہت ہیں) فضیلت دی۔'),
(2100, 17, 71, 'اس دن (کو یاد کرو) جب ہم (ہر دور کے) تمام انسانوں کو انکے امام (پیشوا) کے ساتھ بلائیں گے پس جس کسی کو اس کا نامۂ اعمال دائیں ہاتھ میں دیا جائے گا تو یہ لوگ اپنا صحیفۂ اعمال (خوش خوش) پڑھیں گے اور ان پر ذرہ برابر بھی ظلم نہیں کیا جائے گا۔'),
(2101, 17, 72, 'اور جو کوئی اس دنیا میں اندھا بنا رہا وہ آخرت میں بھی اندھا ہی ہوگا اور بڑا راہ گم کردہ ہوگا۔'),
(2102, 17, 73, '(اے رسول(ص)) یہ (کافر) اس بات میں کوشاں تھے (اور اس کا ارادہ کر لیا تھا کہ) آپ کو اس (کتاب) سے پھیر دیں جو ہم نے آپ کی طرف بذریعۂ وحی بھیجی ہے تاکہ آپ اس کے خلاف کوئی بات گڑھ گھڑ کر ہماری طرف منسوب کریں اور اس صورت میں وہ ضرور آپ کو اپنا گہرا دوست بنا لیتے۔'),
(2103, 17, 74, 'اور اگر ہم آپ کو ثابت قدم نہ رکھتے تو آپ کچھ نہ کچھ ضرور ان کی طرف جھک جاتے۔'),
(2104, 17, 75, 'اور اگر آپ ایسا کرتے تو ہم آپ کو دنیا میں عذاب کا مزہ چکھاتے اور مرنے پر بھی دوہرے عذاب کا مزہ چکھاتے۔ پھر آپ ہمارے مقابلہ میں کوئی یار و مددگار نہ پاتے۔'),
(2105, 17, 76, 'اور یہ لوگ اس بات میں بھی کوشاں تھے کہ اس سر زمین سے آپ کے قدم اکھیڑ دیں اور اس طرح آپ کو یہاں سے نکال دیں اور اگر وہ ایسا کریں گے تو وہ خود بھی آپ کے بعد زیادہ دیر نہیں ٹھہریں گے۔'),
(2106, 17, 77, 'یہی ہمارا دستور رہا ہے ان رسولوں کے بارے میں جنہیں ہم نے پہلے بھیجا ہے اور آپ ہمارے اس دستور میں کوئی تبدیلی نہیں پائیں گے۔'),
(2107, 17, 78, '(اے پیغمبر(ص)) زوالِ آفتاب سے لے کر رات کے اندھیرے تک نماز قائم کرو۔ اور نمازِ صبح بھی۔ کیونکہ صبح کی نماز حضوری کا وقت ہے (اس وقت شب و روز کے فرشتے حاضر ہوتے ہیں)۔'),
(2108, 17, 79, 'اور رات کے کچھ حصہ (پچھلے پہر) میں قرآن کے ساتھ بیدار رہیں (نمازِ تہجد پڑھیں) یہ آپ کے لئے اضافہ ہے۔ عنقریب آپ کا پروردگار آپ کو مقامِ محمود پر فائز کرے گا۔'),
(2109, 17, 80, 'اور کہیے اے میرے پروردگار! مجھے (ہر جگہ) سچائی کے ساتھ داخل کر اور نکال بھی سچائی کے ساتھ اور میرے لئے اپنی جانب سے ایسی قوت قرار دے جو مددگار ہو۔'),
(2110, 17, 81, 'اور کہیئے! کہ حق آگیا اور باطل مٹ گیا۔ یقیناً باطل تو تھا ہی مٹنے والا۔'),
(2111, 17, 82, 'اور ہم تنزیلِ قرآن کے سلسلہ میں وہ کچھ نازل کر رہے ہیں جو اہلِ ایمان کے لئے شفاء اور رحمت ہے اور ظالموں کے لئے خسارہ کے سوا اور کسی چیز میں اضافہ نہیں کرتا۔'),
(2112, 17, 83, 'اور جب ہم انسان کو کوئی نعمت عطا کرتے ہیں تو وہ منہ پھیر لیتا ہے اور پہلو تہی کرتا ہے اور جب اسے کوئی تکلیف پہنچتی ہے تو مایوس ہو جاتا ہے۔'),
(2113, 17, 84, 'کہہ دیجیے! ہر شخص اپنے طریقہ پر عمل پیرا ہے۔ اب یہ تو تمہارا پروردگار ہی بہتر جانتا ہے کہ ٹھیک راہ پر کون ہے؟'),
(2114, 17, 85, '(اے رسول(ص)!) لوگ آپ سے روح کے بارے میں سوال کرتے ہیں۔ کہہ دیجئے کہ روح میرے پروردگار کے امر سے ہے اور تمہیں بہت ہی تھوڑا سا علم دیا گیا ہے۔'),
(2115, 17, 86, 'اور اگر ہم چاہیں تو وہ سب کچھ سلب کر لیں جو ہم نے آپ کی طرف وحی کی ہے۔ اور پھر آپ ہمارے مقابلہ میں کوئی وکیل (حمایتی) بھی نہ پائیں (جو اسے واپس دلا سکے)۔'),
(2116, 17, 87, 'مگر یہ آپ کے پروردگار کی خاص رحمت ہے (کہ وہ ایسا نہیں کرتا) بے شک آپ پر اس کا بہت بڑا فضل ہے۔'),
(2117, 17, 88, 'کہہ دیجئے کہ اگر سارے انسان اور جن اکٹھے ہو جائیں (اور چاہیں) اس جیسا قرآن لے آئیں تو جب بھی اس جیسا نہیں لا سکیں گے۔ اگرچہ ایک دوسرے کے مددگار بھی بن جائیں۔'),
(2118, 17, 89, 'اور یقیناً ہم نے اس قرآن میں ہر طرح کی مثالیں (بار بار) ادل بدل کر بیان کی ہیں لوگوں کے لئے (تاکہ وہ سمجھیں) مگر اکثر لوگ انکار کئے بغیر نہ رہے۔'),
(2119, 17, 90, 'اور انہوں نے کہا ہم اس وقت تک آپ کی بات نہیں مانیں گے جب تک آپ یہ کام کرکے نہ دکھا دیں (مثلاً) ہمارے لئے زمین سے چشمہ جاری کریں۔'),
(2120, 17, 91, 'یا آپ کے لئے کھجوروں اور انگوروں کا ایک باغ ہو اور آپ اس کے درمیان بہت سی نہریں جاری کریں۔'),
(2121, 17, 92, 'یا جیساکہ آپ کا خیال ہے آسمان کے ٹکڑے ٹکڑے کرکے ہم پر گرا دیں یا پھر خدا اور فرشتوں کو کھلم کھلا ہمارے سامنے لے آئیں۔'),
(2122, 17, 93, 'یا آپ کا خالص سونے کا ایک گھر ہو۔ یا پھر آپ آسمان پر چڑھ جائیں اور ہم آپ کے (آسمان پر) چڑھنے کو بھی اس وقت تک نہیں مانیں گے جب تک ایک (لکھی لکھائی) کتاب اتار کے نہ لے آئیں جسے ہم خود پڑھیں۔ کہہ دیجئے پاک ہے میرا پروردگار میں پیغام پہنچانے والا انسان کے سوا کیا ہوں؟'),
(2123, 17, 94, 'جب کبھی لوگوں کے پاس ہدایت پہنچی تو ان کو ایمان لانے سے نہیں روکا مگر اس بات نے کہ کہنے لگے کیا اللہ نے کسی بشر کو رسول بنا کر بھیجا ہے؟'),
(2124, 17, 95, 'کہہ دیجئے! کہ اگر زمین میں (انسان کی بجائے) فرشتے ہوتے جو اطمینان و آرام سے چلتے پھرتے تو ہم ضرور ان پر آسمان سے کسی فرشتہ کو رسول(ص) بنا کر بھیجتے۔'),
(2125, 17, 96, '(اے رسول(ص)!) کہہ دیئے! کہ میرے اور تمہارے درمیان گواہی کیلئے اللہ ہی کافی ہے۔ بے شک وہ اپنے بندوں کو خوب جانتا ہے۔ (اور) خوب دیکھتا ہے۔'),
(2126, 17, 97, 'اور جس کو اللہ ہدایت دے دے وہی ہدایت یافتہ ہے اور جسے (وہ اپنی توفیق سلب کرکے) گمراہی میں چھوڑ دے تو تم اللہ کے سوا اس کا کوئی حامی و مددگار نہیں پاؤگے اور قیامت کے دن ہم ایسے لوگوں کو منہ کے بل اس حال میں اٹھائیں گے کہ وہ اندھے، گونگے اور بہرے ہوں گے۔ ان کا ٹھکانہ جہنم ہے جب بھی وہ بجھنے لگے گی تو ہم اسے اور بھڑکا دیں گے۔'),
(2127, 17, 98, 'یہ ان کی سزا ہے کیونکہ انہوں نے ہماری آیتوں کا انکار کیا۔ اور کہا کہ جب ہم (گل سڑ کر) ہڈیاں اور چورہ ہو جائیں گے تو کیا ہم ازسرِنو پیدا کرکے اٹھائے جائیں گے؟'),
(2128, 17, 99, 'کیا انہوں نے (اس بات پر) غور نہیں کیا کہ وہ اللہ جس نے آسمانوں اور زمین کو پیدا کیا ہے وہ اس پر بھی ضرور قادر ہے کہ ان جیسوں کو (دوبارہ) پیدا کر دے اور اس نے ان کے (حشر کے) لئے ایک میعاد مقرر کر رکھی ہے جس میں کوئی شک نہیں ہے مگر اس امر میں ظالم انکار کئے بغیر نہ رہے۔'),
(2129, 17, 100, 'کہہ دیجئے! کہ اگر تم میرے پروردگار کی رحمت کے خزانوں کے مالک بھی ہوتے تو تم ان کے خرچ ہو جانے کے خوف سے (ہاتھ) روک لیتے اور انسان بڑا بخیل ہے۔'),
(2130, 17, 101, 'اور ہم نے موسیٰ کو نو کھلی نشانیاں عطا فرمائیں۔ آپ بنی اسرائیل سے پوچھ لیں جب کہ وہ (موسیٰ) ان کے پاس آئے تھے! تو فرعون نے اس سے کہا۔ اے موسیٰ! میں تو تمہیں سحر زدہ (یا ساحر) خیال کرتا ہوں۔'),
(2131, 17, 102, 'موسیٰ نے کہا (اے فرعون) تو (دل میں تو) یقیناً جانتا ہے کہ یہ نشانیاں آسمانوں اور زمین کے پروردگار نے بصیرت کا سامان بنا کر نازل کی ہیں۔ اور اے فرعون! میں سمجھتا ہوں کہ تو ایک ہلاکت زدہ آدمی ہے۔'),
(2132, 17, 103, 'اس (فرعون) نے ارادہ کیا کہ وہ (موسیٰ اور بنی اسرائیل) کو اس سر زمین سے نکال باہر کرے لیکن ہم نے اسے اور اس کے سب ساتھیوں کو غرق کر دیا۔'),
(2133, 17, 104, 'اور اس (واقعہ) کے بعد ہم نے بنی اسرائیل سے کہا کہ تم اس سر زمین میں سکونت اختیار کرو۔ پس جب آخرت کا وعدہ آجائے گا تو ہم تم (سب) کو (اپنے حضور) سمیٹ لائیں گے۔'),
(2134, 17, 105, 'اور ہم نے اس (قرآن) کو حق کے ساتھ نازل کیا ہے اور وہ حق کے ساتھ نازل ہوا ہے۔ اور ہم نے آپ کو بشارت دینے والا اور ڈرانے والا بنا کر بھیجا ہے۔'),
(2135, 17, 106, 'اور ہم نے قرآن کو متفرق طور پر (جدا جدا کرکے) نازل کیا ہے تاکہ آپ اسے لوگوں کے سامنے ٹھہر ٹھہر کر پڑھیں اور ہم نے اسے (موقع بموقع) بتدریج نازل کیا ہے۔'),
(2136, 17, 107, 'کہہ دیجیے! کہ تم (لوگ) اس پر ایمان لاؤ یا نہ لاؤ۔ لیکن جن کو اس سے پہلے علم دیا گیا ہے جب اسے ان کے سامنے پڑھا جاتا ہے تو وہ منہ کے بل سجدہ میں گر پڑتے ہیں۔'),
(2137, 17, 108, 'اور کہتے ہیں کہ پاک ہے ہمارا پروردگار! بے شک ہمارے پروردگار کا وعدہ تو پورا ہو کر ہی رہتا ہے۔'),
(2138, 17, 109, 'اور وہ روتے ہوئے منہ کے بل گر پڑتے ہیں۔ اور یہ (قرآن) ان کے خشوع و خضوع میں اضافہ کر دیتا ہے۔'),
(2139, 17, 110, 'کہہ دیجیے! کہ اسے اللہ کہہ کر پکارو یا رحمٰن کہہ کر پکارو جس نام سے بھی اسے پکارو۔ اس کے سارے نام اچھے ہیں اور نہ ہی اپنی نماز زیادہ بلند آواز سے پڑھو اور نہ ہی بالکل آہستہ سے بلکہ ان دونوں کے درمیان کا راستہ اختیار کرو۔'),
(2140, 17, 111, 'اور کہہ دیجئے! سب تعریفیں اللہ کے لئے ہیں جس نے نہ کسی کو بیٹا بنایا ہے اور نہ ہی کسی کو شریک بنایا ہے اور نہ ہی درماندگی اور عاجزی کی وجہ سے اس کا کوئی مددگار ہے اور اس کی کبریائی اور بڑائی کا بیان کرو جس طرح بڑائی بیان کرنی چاہیئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ہر قسم کی تعریف اس اللہ کے لئے ہے جس نے اپنے بندہ (خاص) پر کتاب نازل کی اور اس میں کسی طرح کی کوئی کمی نہیں رکھی۔'),
(2142, 18, 2, 'بالکل سیدھی اور ہموار تاکہ وہ (مکذبین کو) اللہ کی طرف سے آنے والے سخت عذاب سے ڈرائے اور ان اہل ایمان کو خوشخبری دے جو نیک عمل کرتے ہیں کہ ان کے لئے بہترین اجر ہے۔'),
(2143, 18, 3, 'جس میں وہ ہمیشہ ہمیشہ رہیں گے۔'),
(2144, 18, 4, 'اور تاکہ وہ ان لوگوں کو ڈرائے جو کہتے ہیں کہ اللہ نے کسی کو اپنا بیٹا بنا لیا ہے۔'),
(2145, 18, 5, 'اس بارے میں نہ انہیں کوئی علم ہے اور نہ ان کے باپ دادوں کو کوئی علم تھا۔ یہ بہت بڑی بات ہے جو ان کے منہ سے نکلتی ہے۔ یہ لوگ بالکل جھوٹ بولتے ہیں۔'),
(2146, 18, 6, '(اے پیغمبر(ص)!) شاید آپ ان کے پیچھے اس رنج و افسوس میں اپنی جان دے دیں گے کہ وہ اس کلام پر ایمان نہیں لائے۔'),
(2147, 18, 7, 'بے شک جو کچھ زمین پر ہے ہم نے اسے زمین کی زینت بنایا ہے۔ تاکہ ہم ان لوگوں کو آزمائیں کہ عمل کے لحاظ سے کون بہتر ہے۔'),
(2148, 18, 8, 'اور جو کچھ زمین پر ہے ہم اسے (ایک دن) چٹیل میدان بنانے والے ہیں۔'),
(2149, 18, 9, 'کیا آپ خیال کرتے ہیں کہ کہف و رقیم (غار اور کتبے) والے ہماری نشانیوں میں سے کوئی عجیب نشانی تھے؟'),
(2150, 18, 10, 'جب ان جوانوں نے غار میں پناہ لی اور کہا اے ہمارے پروردگار! اپنی بارگاہ سے رحمت عطا فرما اور ہمارے لئے اس کام میں ہدایت کا سامان مہیا فرما۔'),
(2151, 18, 11, 'تو ہم نے غار میں کئی برسوں تک ان کے کانوں پر (نیند کا) پردہ ڈال دیا۔'),
(2152, 18, 12, 'پھر ہم نے انہیں اٹھایا۔ تاکہ ہم دیکھیں کہ ان دو گروہوں میں سے کون اپنے ٹھہرنے کی مدت کا زیادہ ٹھیک شمار کر سکتا ہے۔'),
(2153, 18, 13, '(اے رسول(ص)) ہم آپ کو ان کا اصل قصہ سناتے ہیں وہ چند نوجوان تھے جو اپنے پروردگار پر ایمان لائے اور ہم نے ان کی ہدایت میں اضافہ کر دیا۔'),
(2154, 18, 14, 'اور ہم نے ان کے دل اس وقت اور مضبوط کر دیئے جب وہ کھڑے ہوکر کہنے لگے کہ ہمارا پروردگار وہ ہے جو آسمانوں اور زمین کا پروردگار ہے۔ ہم اس کے سوا اور کسی کو معبود ہرگز نہیں پکاریں گے۔ ورنہ ہم بالکل ناحق بات کے مرتکب ہوں گے۔'),
(2155, 18, 15, 'یہ ہماری قوم ہے جنہوں نے خدا کو چھوڑ کر اور خدا اپنا لئے ہیں، یہ لوگ ان کی خدائی پر کوئی واضح دلیل کیوں نہیں لاتے؟ پس اس شخص سے بڑا ظالم کون ہوگا جو اللہ پر جھوٹ باندھے۔'),
(2156, 18, 16, 'اور (پھر آپس میں کہنے لگے) اب جب کہ تم نے ان لوگوں سے اور ان کے ان معبودوں سے جن کی وہ اللہ کے سوا عبادت کرتے ہیں علیٰحدگی اختیار کر لی ہے تو غار میں چل کر پناہ لو۔ تمہارا پروردگار تم پر اپنی رحمت (کا سایہ) تم پر پھیلائے گا۔ اور تمہارے اس کام کے لئے سروسامان مہیا کر دے گا۔'),
(2157, 18, 17, 'اور (وہ غار اس طرح واقع ہوئی ہے کہ) تم دیکھوگے کہ جب سورج طلوع ہوتا ہے تو ان کے غار سے دائیں طرف مڑ جاتا ہے اور جب ڈوبتا ہے تو ان سے بائیں طرف کترا کر نکل جاتا ہے۔ اور وہ ایک کشادہ جگہ پر (سوئے ہوئے) ہیں۔ یہ اللہ کی نشانیوں میں سے ایک (نشانی) ہے جسے اللہ ہدایت دے وہی ہدایت یافتہ ہے اور جسے وہ گمراہی میں چھوڑ دے تو تم اس کے لئے کوئی یار و مددگار اور راہنما نہیں پاؤگے۔'),
(2158, 18, 18, 'اور تم انہیں دیکھو تو خیال کرو کہ وہ جاگ رہے ہیں حالانکہ وہ سوئے ہوئے ہیں اور ہم انہیں دائیں اور بائیں کروٹ بدلواتے رہتے ہیں اور ان کا کتا غار کے دہانے پر اپنے دونوں بازو پھیلائے بیٹھا ہے اگر تم انہیں جھانک کر دیکھو تو تم الٹے پاؤں بھاگ کھڑے ہو۔ اور تمہارے دل میں دہشت سما جائے۔'),
(2159, 18, 19, 'اور (جس طرح انہیں اپنی قدرت سے سلایا تھا) اسی طرح ہم نے انہیں اٹھایا تاکہ آپس میں سوال و جواب کریں۔ چنانچہ ان میں سے ایک کہنے والے نے کہا کہ تم کتنی دیر ٹھہرے ہوگے؟ دوسروں نے کہا ہم ایک دن ٹھہرے ہوں گے یا دن کا کچھ حصہ (پھر) بولے تمہارا پروردگار ہی بہتر جانتا ہے کہ تم کتنا ٹھہرے؟ اچھا اب اپنے میں سے کسی کو چاندی کا یہ سکہ دے کر شہر میں بھیجو۔ وہ (جا کر) دیکھے کہ کون سا کھانا زیادہ پاک و پاکیزہ ہے۔ تو وہ اس میں سے کچھ کھانا تمہارے لئے لائے۔ اور اسے چاہیے کہ ہوش و تدبر سے کام لے اور کسی کو تمہاری خبر نہ ہونے دے۔'),
(2160, 18, 20, 'یقیناً اگر ان لوگوں کو تمہاری اطلاع ہوگئی تو وہ تمہیں سنگسار کر دیں گے یا پھر (زبردستی) تمہیں اپنے دین کی طرف واپس لے جائیں گے اور اس طرح تم کبھی بھی فلاح نہیں پا سکوگے۔'),
(2161, 18, 21, 'اور اسی طرح ہم نے لوگوں کو ان پر مطلع کیا تاکہ وہ لوگ جان لیں کہ اللہ کا وعدہ سچھا ہے اور یہ کہ قیامت میں کوئی شک نہیں ہے۔ یہ اس وقت کی بات ہے جب وہ (بستی والے) لوگ ان (اصحافِ کہف) کے معاملہ میں آپس میں جھگڑ رہے تھے (کہ کیا کیا جائے؟) تو (کچھ) لوگوں نے کہا کہ ان پر (یعنی غار پر) ایک (یادگاری) عمارت بنا دو۔ ان کا پروردگار ہی ان کو بہتر جانتا ہے۔ (آخرکار) جو لوگ ان کے معاملات پر غالب آئے تھے وہ بولے کہ ہم ان پر ایک مسجد (عبادت گاہ) بنائیں گے۔'),
(2162, 18, 22, 'عنقریب کچھ لوگ کہہ دیں گے کہ وہ تین تھے اور چوتھا ان کا کتا تھا۔ اور کچھ کہہ دیں گے کہ وہ پانچ تھے اور چھٹا ان کا کتا تھا۔ (اے رسول(ص)) کہہ دیجیئے کہ میرا پروردگار ہی ان کی تعداد کو بہتر جانتا ہے۔ اور بہت کم لوگوں کو ان کا علم ہے۔ سو ان کے بارے میں سوائے سرسری گفتگو کے لوگوں سے زیادہ بحث نہ کریں اور نہ ہی ان کے بارے میں ان میں سے کسی سے کچھ پوچھیں۔'),
(2163, 18, 23, 'اور آپ کسی چیز کے بارے میں یہ نہ کہیں کہ میں کل اسے ضرور کروں گا۔'),
(2164, 18, 24, 'مگر یہ کہ خدا چاہے (یعنی اس کے ساتھ انشاء اللہ کہا کرو) اور جب بھی بھول جائیں تو اپنے پروردگار کو یاد کریں۔ اور آپ کہہ دیجئے! کہ امید ہے کہ میرا پروردگار ایسی بات کی مجھے راہنمائی کرے جو رشد و ہدایت میں اس سے بھی زیادہ قریب ہو۔'),
(2165, 18, 25, 'اور وہ لوگ غار میں رہے نو برس اوپر تین سو سال۔'),
(2166, 18, 26, 'کہہ دیجئے! کہ اللہ ہی بہتر جانتا ہے کہ وہ کتنا رہے؟ تمام آسمانوں اور زمین کا (علمِ) غیب اسی کے لئے ہے وہ کتنا بڑا دیکھنے والا اور کتنا بڑا سننے والا ہے۔ اللہ کے سوا ان لوگوں کا کوئی سرپرست نہیں ہے اور نہ وہ کسی کو اپنے حکم میں شریک کرتا ہے۔'),
(2167, 18, 27, '(اے رسول) آپ کے پروردگار کی طرف کتاب (قرآن) کے ذریعہ سے جو وحی کی گئی ہے۔ اسے پڑھ سنائیں۔ اس کی باتوں کو کوئی بدلنے والا نہیں ہے۔ اور آپ اس کے سوا کوئی جائے پناہ نہیں پائیں گے۔'),
(2168, 18, 28, '(اے رسول(ص)!) جو لوگ صبح و شام اپنے پروردگار کو پکارتے ہیں اور اس کی رضا کے طلبگار ہیں آپ ان کی معیت پر صبر کریں۔ اور دنیا کی زینت کے طلبگار ہوتے ہوئے ان کی طرف سے آپ کی آنکھیں نہ پھر جائیں۔ اور جس کے دل کو ہم نے اپنی یاد سے غافل چھوڑ رکھا ہے اور وہ اپنی خواہش کی پیروی کرتا ہے اور اس کا معاملہ حد سے گزر گیا ہے اس کی اطاعت نہ کریں۔'),
(2169, 18, 29, 'اور آپ کہہ دیجئے کہ حق تمہارے پروردگار کی طرف سے ہے تو جو چاہے ایمان لائے اور جو چاہے کفر اختیار کرے۔ بے شک ہم نے ظالموں کیلئے ایسی آگ تیار کر رکھی ہے جس کی قناطیں انہیں گھیر لیں گی اور اگر وہ فریاد کریں گے تو ان کی فریاد رسی ایسے پانی سے کی جائے گی جو پگھلے ہوئے تانبے کی طرح ہوگا۔ جو چہروں کو بھون ڈالے گا کیا بری چیز ہے پینے کی۔ اور کیا بری آرام گاہ ہے۔'),
(2170, 18, 30, 'بے شک جو لوگ ایمان لائے اور نیک عمل کئے۔ یقینا ہم نیکوکار لوگوں کا اجر ضائع نہیں کرتے۔'),
(2171, 18, 31, 'یہ وہ لوگ ہیں جن کے لئے ہمیشگی کے باغات ہیں جن کے نیچے نہریں جاری ہوں گی انہیں ان میں سونے کے کنگن پہنائے جائیں گے۔ سبز ریشم کے باریک اور دبیز کپڑے پہنیں گے اور وہ ان میں مسندوں پر گاؤ تکیہ لگائے بیٹھے ہوں گے۔ کیا ہی اچھا ہے ان کا ثواب اور کیا ہی اچھی ہے ان کی آرام گاہ۔'),
(2172, 18, 32, '(اے رسول(ص)!) ان لوگوں کے سامنے ان دو شخصوں کی مثال پیش کریں کہ ہم نے ان میں سے ایک کو انگور کے دو باغ دے رکھے تھے اور انہیں کھجور کے درختوں سے گھیر رکھا تھا۔ اور ان کے درمیان کھیتی اگا دی تھی۔'),
(2173, 18, 33, 'یہ دونوں باغ خوب پھل دیتے تھے اور اس میں کچھ بھی کمی نہیں کی اور ہم نے ان کے درمیان (آبپاشی کیلئے) ایک نہر بھی جاری کر دی تھی۔'),
(2174, 18, 34, 'اور اس کے پاس اور بھی تمول کا سامان تھا۔ (ایک دن گھمنڈ میں آکر) اس نے اپنے (غریب) ساتھی سے باتیں کرتے ہوئے کہا کہ میں مال کے اعتبار سے تجھ سے زیادہ (مالدار) ہوں اور نفری کے لحاظ سے بھی تجھ سے زیادہ طاقتور ہوں۔'),
(2175, 18, 35, 'اور وہ (ایک دن) اپنے باغ میں داخل ہوا جبکہ وہ اپنے اوپر ظلم کرنے والا تھا (اور) کہنے لگا کہ میں خیال نہیں کرتا کہ یہ (باغ) کبھی تباہ ہو جائے گا۔'),
(2176, 18, 36, 'اور میں خیال نہیں کرتا کہ کبھی قیامت برپا ہوگی اور اگر (بالفرض) میں کبھی اپنے پروردگار کی طرف لوٹایا گیا تو اس (باغ) سے بہتر ٹھکانہ پاؤں گا۔'),
(2177, 18, 37, 'اس کے ساتھی نے اس سے باتیں کرتے ہوئے کہا کہ کیا تو اس ہستی کا انکار کرتا ہے جس نے تجھے پہلے مٹی سے اور پھر نطفہ سے پیدا کیا پھر تجھے اچھا خاصا مرد بنایا۔'),
(2178, 18, 38, 'لیکن میں! تو میرا پروردگار تو وہی اللہ ہے اور میں اپنے پروردگار کے ساتھ کسی کو شریک نہیں کرتا۔'),
(2179, 18, 39, 'اور تو جب اپنے باغ میں داخل ہوا تو کیوں نہ کہا؟ ماشاء اللہ لا قوۃ الا باللہ (جو اللہ چاہتا ہے وہی ہوتا ہے اور خدا کی قوت کے بغیر کوئی قوت نہیں ہے)۔ اور اگر تو مجھے مال و اولاد میں (اپنے سے) کمتر دیکھتا ہے۔'),
(2180, 18, 40, 'تو قریب ہے کہ میرا پروردگار مجھے تیرے باغ سے بہتر (باغ) عطا فرمائے اور تیرے باغ پر آسمان سے بجلیاں بھیج دے جس سے وہ چٹیل چکنا میدان بن کر رہ جائے۔'),
(2181, 18, 41, 'یا اس کا پانی اس طرح نیچے اتر جائے کہ تو کسی طرح بھی اسے حاصل نہ کر سکے۔'),
(2182, 18, 42, '(چنانچہ) اس کے پھلوں کو گھیرے میں لے لیا گیا (ان پر آفت آگئی) تو اس نے باغ پر جو کچھ خرچ کیا تھا اس پر کفِ افسوس ملتا تھا اور وہ (باغ) اپنے چھپروں پر گرا پڑا تھا۔ اور وہ کہتا تھا کہ اے کاش کہ میں نے کسی کو اپنے پروردگار کا شریک نہ بنایا ہوتا۔'),
(2183, 18, 43, 'اب اس کے پاس کوئی ایسا گروہ بھی نہیں تھا جو اللہ کے مقابلہ میں اس کی مدد کرتا اور نہ ہی وہ غالب اور بدلہ لینے کے قابل تھا۔'),
(2184, 18, 44, '(ثابت ہوا) کہ ہر قسم کا اختیار خدائے برحق کیلئے ہے۔ وہی بہتر ثواب دینے والا اور وہی بہترین انجام والا ہے۔'),
(2185, 18, 45, 'اور آپ ان لوگوں کے سامنے زندگانئ دنیا کی (ایک اور) مثال پیش کریں کہ وہ ایسی ہے جیسے ہم نے آسمان سے پانی برسایا تو اس سے زمین کی نباتات مل گئی (اور خوب پھلی پھولی) پھر وہ ریزہ ریزہ ہوگئی جسے ہوائیں اڑائے پھرتی ہیں اور اللہ ہر چیز پر قدرت رکھتا ہے۔'),
(2186, 18, 46, 'مال اور اولادِ زندگانی دنیا کی (ہنگامی) زیب و زینت ہیں اور (دراصل) باقی رہنے والی نیکیاں تمہارے پروردگار کے نزدیک ثواب کے لحاظ سے بھی اور امید کے اعتبار سے بھی کہیں بہتر ہیں۔'),
(2187, 18, 47, 'اور وہ دن (یاد کرو) جب ہم پہاڑوں کو چلائیں گے اور تم زمین کو دیکھوگے کہ وہ کھلا ہوا میدان ہے اور ہم اس طرح سب لوگوں کو جمع کریں گے کہ کسی ایک کو بھی نہیں چھوڑیں گے۔'),
(2188, 18, 48, 'اور وہ تمہارے پروردگار کے حضور صفیں باندھے پیش کئے جائیں گے (تب ان سے کہا جائے گا) آخر تم اسی طرح ہمارے پاس آگئے ہو جس طرح ہم نے تمہیں پہلی مرتبہ پیدا کیا تھا۔ ہاں البتہ تم نے یہ خیال کیا تھا کہ ہم نے تمہارے لئے (دوبارہ پیدا کرنے کا) کوئی وعدہ گاہ مقرر نہیں کیا ہے۔'),
(2189, 18, 49, 'اور نامۂ اعمال (سامنے) رکھ دیا جائے گا اور تم دیکھو گے کہ مجرم لوگ اس کے مندرجات سے ڈر رہے ہوں گے اور کہتے ہوں گے۔ ہائے ہماری کم بختی! یہ کیسا نامۂ عمل ہے؟ جس نے (ہمارا) کوئی چھوٹا بڑا (گناہ) نہیں چھوڑا مگر سب کو درج کر لیا ہے۔ اور جو کچھ ان لوگوں نے کیا تھا وہ سب اپنے اپنے سامنے پائیں گے اور تمہارا پروردگار کسی پر ظلم نہیں کرتا۔'),
(2190, 18, 50, 'اور (وہ وقت یاد کرو) جب ہم نے فرشتوں سے کہا کہ آدم کے سامنے سجدہ ریز ہو جاؤ۔ تو سب نے سجدہ کیا سوائے ابلیس کے وہ جنات میں سے تھا۔ اس نے اپنے پروردگار کے حکم کی سرتابی کی۔ کیا تم (اے منکرینِ حق) مجھے چھوڑ کر اس کو اور اس کی اولاد کو اپنا سرپرست و کارساز بناتے ہو؟ حالانکہ وہ تمہارا دشمن ہے۔ ظالموں کیلئے کیا ہی برا بدل ہے۔'),
(2191, 18, 51, 'اور میں نے انہیں آسمان و زمین کی پیدائش کے وقت حاضر نہیں کیا اور نہ ہی ان کی اپنی خلقت کے وقت اور میں ایسا نہیں ہوں کہ گمراہ کرنے والوں کو اپنا دست و بازو بناؤں۔'),
(2192, 18, 52, 'اور وہ دن (یاد رکھو) جب وہ (اللہ مشرکین سے) فرمائے گا کہ بلاؤ میرے ان شریکوں کو جن کو تم میرا شریک خیال کرتے تھے۔ چنانچہ وہ انہیں پکاریں گے لیکن وہ انہیں کوئی جواب نہیں دیں گے اور ہم ان کے درمیان ایک آڑ مقرر کر دیں گے کہ وہ ایک دوسرے کی بات نہیں سن سکتے۔'),
(2193, 18, 53, 'اور جب مجرم لوگ (دوزخ کی) آگ دیکھیں گے تو وہ خیال کریں گے کہ وہ اس میں گرنے والے ہیں اور وہ اس سے بچنے کی کوئی راہ نہیں پائیں گے۔'),
(2194, 18, 54, 'اور ہم نے اس قرآن میں لوگوں (کی ہدایت) کے لئے ہر قسم کی مثالیں الٹ پلٹ کر بیان کر دی ہیں۔ (مگر) انسان ہر چیز سے بڑھ کر جھگڑالو ہے۔'),
(2195, 18, 55, 'اور جب لوگوں کے پاس ہدایت آگئی ہے تو ان کو ایمان لانے اور مغفرت طلب کرنے سے منع نہیں کیا۔ مگر اس چیز نے کہ اگلی قوموں کا معاملہ انہیں بھی پیش آئے یا عذاب ان کے سامنے آئے۔'),
(2196, 18, 56, 'اور ہم رسولوں کو نہیں بھیجتے۔ مگر خوشخبری دینے والا اور ڈرانے والا بنا کر۔ اور جو کافر ہیں وہ باطل کے (ہتھیار سے) جھگڑتے ہیں تاکہ اس کے ذریعہ سے حق کو باطل کر دیں اور انہوں نے میری آیتوں کو اور جس چیز (عذاب) سے انہیں ڈرایا گیا تھا اس کو مذاق بنا رکھا ہے۔'),
(2197, 18, 57, 'اور اس شخص سے بڑھ کر ظالم کون ہے جسے اس کے پروردگار کی نشانیوں کے ذریعہ سے نصیحت کی جائے مگر وہ ان سے روگردانی کرے اور اپنے ان (گناہوں) کو بھول جائے۔ جو وہ اپنے ہاتھوں پہلے بھیج چکا ہے ہم نے ان کے دلوں پر پردے ڈال دیئے ہیں کہ اس کو سمجھیں اور ان کے کانوں میں گرانی ڈال دی ہے۔ کہ اس کو سنیں (یہ حق سے اعراض کرنے کی سزا ہے) اور اگر انہیں ہدایت کی طرف بلائیں تو وہ اس حالت میں کبھی راہِ راست پر نہیں آئیں گے۔'),
(2198, 18, 58, 'اور آپ کا پروردگار بڑا بخشنے والا، رحمت والا ہے اگر وہ لوگوں سے ان کے کئے (گناہوں) کا مواخذہ کرتا تو جلدی ہی میں ان پر عذاب نازل کر دیتا مگر ان کے لئے ایک میعاد ٹھہرا دی گئی ہے اس سے ادھر (پہلے) کوئی جائے پناہ نہیں پائیں گے۔'),
(2199, 18, 59, 'اور یہ بستیاں ہیں جب (ان کے باشندوں نے) ظلم و زیادتی کی تو ہم نے انہیں ہلاک کر ڈالا اور ہم نے ان کی ہلاکت کے لئے ایک میعاد مقرر کر دی تھی۔'),
(2200, 18, 60, 'اور (وہ وقت یاد کرو) جب موسیٰ (ع) نے اپنے جوان سے کہا کہ میں برابر سفر جاری رکھوں گا یہاں تک کہ اس جگہ پہنچوں جہاں دو دریا اکٹھے ہوتے ہیں یا پھر (یونہی چلتے چلتے) سالہا سال گزار دوں گا۔'),
(2201, 18, 61, 'تو جب وہ دونوں ان دونوں دریاؤں کے اکٹھا ہونے کی جگہ پر پہنچے تو وہ دونوں اپنی مچھلی کو بھول گئے اور اس نے دریا میں داخل ہونے کے لئے سرنگ کی طرح اپنا راستہ بنا لیا۔'),
(2202, 18, 62, 'جب وہ دونوں وہاں سے آگے بڑھ گئے۔ تو موسیٰ نے اپنے جوان سے کہا ہمارا صبح کا کھانا (ناشتہ) لاؤ۔ یقینا ہمیں اس سفر میں بڑی مشقت اٹھانا پڑی ہے۔ (ہم بہت تھک گئے ہیں)۔'),
(2203, 18, 63, 'اس (جوان) نے کہا کیا آپ نے دیکھا تھا؟ کہ جب ہم اس چٹان کے پاس (سستانے کیلئے) ٹھہرے ہوئے تھے تو میں مچھلی کو بھول گیا۔ اور شیطان نے مجھے ایسا غافل کیا کہ میں (آپ سے) اس کا ذکر کرنا بھی بھول گیا اور اس نے عجیب طریقہ سے دریا میں اپنا راستہ بنا لیا۔'),
(2204, 18, 64, 'موسیٰ نے کہا یہی تو وہ (مقام) تھا جس کی ہمیں تلاش تھی (اور جسے ہم چاہتے تھے) چنانچہ وہ دونوں اپنے پاؤں کا نشان دیکھتے ہوئے واپس ہوئے۔'),
(2205, 18, 65, 'سو انہوں نے وہاں ہمارے بندوں میں سے ایک (خاص) بندہ کو پایا۔ جسے ہم نے اپنی خاص رحمت سے نوازا تھا۔ اور اسے اپنی طرف سے (خاص) علم عطا کیا تھا۔'),
(2206, 18, 66, 'موسیٰ نے اس سے کہا! کیا میں آپ کے ساتھ رہ سکتا ہوں بشرطیکہ رشد و ہدایت کا وہ خصوصی علم جو آپ کو سکھایا گیا ہے اس میں سے کچھ مجھے بھی سکھا دیں۔'),
(2207, 18, 67, 'اس نے (جواب میں) کہا کہ آپ میرے ساتھ (رہ کر) صبر نہیں کر سکتے۔'),
(2208, 18, 68, 'اور بھلا اس بات پر آپ صبر کر بھی کیسے سکتے ہیں جو تمہارے علمی دائرہ سے باہر ہے؟'),
(2209, 18, 69, 'موسیٰ نے کہا انشاء اللہ آپ مجھے صابر پائیں گے اور میں آپ کے کسی حکم کی خلاف ورزی نہیں کروں گا۔'),
(2210, 18, 70, 'اس نے کہا کہ اگر آپ میرے ساتھ رہنا چاہتے ہیں تو آپ کسی چیز کے بارے میں مجھ سے سوال نہ کریں۔ جب تک میں خود آپ سے اس کا ذکر نہ کروں۔'),
(2211, 18, 71, 'اس کے بعد دونوں چل پڑے یہاں تک کہ جب کشتی میں سوار ہوگئے تو خضر (ع) نے اس میں سوراخ کر دیا۔ موسیٰ نے کہا۔ آپ نے اس لئے کشتی میں سوراخ کیا ہے کہ سب کشتی والوں کو غرق کر دیں؟ آپ نے بڑی عجیب حرکت کی ہے۔'),
(2212, 18, 72, 'خضر نے کہا۔ کیا میں نے نہیں کہا تھا کہ آپ صبر نہیں کر سکیں گے؟'),
(2213, 18, 73, 'موسیٰ نے کہا جو بھول ہوگئی اس پر مجھ سے مؤاخذہ نہ کریں اور اس معاملہ میں مجھ سے زیادہ سختی نہ کریں۔'),
(2214, 18, 74, 'چنانچہ پھر دونوں چل پڑے یہاں تک کہ ایک لڑکے سے ملے تو اس بندہ نے اسے قتل کر دیا۔ موسیٰ نے کہا آپ نے ایک پاکیزہ نفس (بے گناہ) کو قصاص کے بغیر قتل کر دیا۔ یہ تو آپ نے سخت برا کام کیا۔'),
(2215, 18, 75, 'خضر (ع) نے کہا کیا میں نے تم سے کہا نہ تھا کہ آپ میرے ساتھ صبر نہیں کر سکیں گے۔'),
(2216, 18, 76, 'موسیٰ (ع) نے کہا (اچھا) اگر اس کے بعد آپ سے کسی چیز کے متعلق پوچھوں تو آپ مجھے اپنے ساتھ نہ رکھیں۔ بےشک آپ میری طرف سے عذر کی حد تک پہنچ گئے ہیں (اب آپ معذور ہیں)۔'),
(2217, 18, 77, 'اس کے بعد وہ دونوں آگے چلے یہاں تک کہ ایک گاؤں والوں کے پاس پہنچے اور ان سے کھانا طلب کیا۔ مگر انہوں نے ان کی مہمانداری کرنے سے انکار کر دیا۔ پھر ان دونوں نے وہاں ایک دیوار پائی جو گرا چاہتی تھی۔ تو خضر (ع) نے اسے (بنا کر) سیدھا کھڑا کر دیا۔ (اس پر) موسیٰ (ع) نے کہا اگر آپ چاہتے تو اس کام کی (ان لوگوں سے) کچھ اجرت ہی لے لیتے۔'),
(2218, 18, 78, 'خضر (ع) نے کہا بس (ساتھ ختم ہوا) اب میری اور تمہاری جدائی ہے اب میں تمہیں ان باتوں کی حقیقت بتاتا ہوں جن پر تم صبر نہ کر سکے۔'),
(2219, 18, 79, 'جہاں تک کشتی کا معاملہ ہے تو وہ چند مسکینوں کی تھی جو دریا پر کام کرتے تھے۔ میں نے چاہا کہ اسے عیب دار بنا دوں کیونکہ ادھر ایک (ظالم) بادشاہ تھا جو ہر (بے عیب) کشتی پر زبردستی قبضہ کر لیتا تھا۔'),
(2220, 18, 80, 'اور جہاں تک اس بچے کا معاملہ ہے تو اس کے ماں باپ مؤمن تھے ہمیں اندیشہ ہوا کہ یہ (بڑا ہوکر) ان کو بھی سرکشی اور کفر میں مبتلا کر دے گا۔'),
(2221, 18, 81, 'تو ہم نے چاہا کہ ان کا پروردگار اس کے عوض انہیں ایسی اولاد دے جو پاکیزگی میں اس سے بہتر ہو اور مہر و محبت میں اس سے بڑھ کر ہو۔'),
(2222, 18, 82, 'باقی رہا دیوار کا معاملہ تو وہ اس شہر کے دو یتیم بچوں کی تھی اور اس کے نیچے ان بچوں کا خزانہ (دفن) تھا۔ اور ان کا باپ نیک آدمی تھا۔ تو تمہارے پروردگار نے چاہا کہ وہ بچے اپنی جوانی کو پہنچیں اور اپنا خزانہ نکالیں اور یہ سب کچھ پروردگار کی رحمت کی بناء پر ہوا ہے۔ اور میں نے جو کچھ کیا ہے اپنی مرضی سے نہیں کیا (بلکہ خدا کے حکم سے کیا) یہ ہے حقیقت ان باتوں کی جن پر تم صبر نہ کر سکے۔'),
(2223, 18, 83, '(اے رسول(ص)) لوگ آپ سے ذوالقرنین کے بارے میں سوال کرتے ہیں۔ آپ کہہ دیجئے کہ میں عنقریب ان کا کچھ حال تمہیں سناتا ہوں۔'),
(2224, 18, 84, 'ہم نے اسے زمین میں اقتدار عطا کیا تھا اور اسے ہر طرح کا ساز و سامان مہیا کر دیا تھا۔'),
(2225, 18, 85, 'پھر اس نے (ایک مہم کیلئے) ساز و سامان کیا۔'),
(2226, 18, 86, 'یہاں تک کہ وہ (چلتے چلتے) غروبِ آفتاب کے مقام پر پہنچ گیا تو اسے ایک سیاہ چشمے میں غروب ہوتا ہوا پایا۔ (محسوس کیا) اور اس کے پاس ایک قوم کو (آباد) پایا۔ ہم نے کہا اے ذوالقرنین! (تمہیں اختیار ہے) خواہ انہیں سزا دو یا ان کے ساتھ حسن سلوک کا رویہ اختیار کرو۔'),
(2227, 18, 87, 'ذوالقرنین نے کہا کہ (ہم بے انصافی نہیں کریں گے) جو ظلم و سرکشی کرے گا ہم اسے ضرور سزا دیں گے اور پھر وہ اپنے پروردگار کی طرف پلٹایا جائے گا تو وہ اسے سخت سزا دے گا۔'),
(2228, 18, 88, 'اور جو کوئی ایمان لائے گا اور نیک عمل کرے گا اس کیلئے اچھی جزا ہے۔'),
(2229, 18, 89, 'اور ہم بھی اس کے ساتھ معاملہ میں نرم بات کریں گے۔'),
(2230, 18, 90, 'پھر اس نے (دوسری مہم کیلئے) ساز و سامان کیا۔'),
(2231, 18, 91, 'یہاں تک کہ (چلتے چلتے) وہ طلوعِ آفتاب کی جگہ پہنچا۔ تو اسے ایک ایسی قوم پر طلوع ہوتا ہوا پایا کہ جس کیلئے ہم نے سورج کے سامنے کوئی پردہ نہیں رکھا ہے۔'),
(2232, 18, 92, 'ایسا ہی تھا اور جو کچھ ذوالقرنین کے پاس تھا اس کی ہم کو پوری خبر ہے۔'),
(2233, 18, 93, 'پھر (ایک اور مہم کی) تیاری کی۔ یہاں تک کہ (جب چلتے چلتے) وہ (دو پہاڑوں کی) دیواروں کے درمیان میں پہنچا تو ان کے ادھر ایک ایسی قوم کو پایا۔ جو (ان کی) کوئی بات نہیں سمجھتی تھی۔'),
(2234, 18, 94, 'انہوں نے (اشاروں سے) کہا اے ذوالقرنین! یاجوج اور ماجوج اس سر زمین میں فساد کرنے والے ہیں تو کیا (یہ ممکن ہے کہ) ہم آپ کیلئے خرچ کا کچھ انتظام کر دیں اکہ آپ ہمارے اور ان کے درمیان کوئی دیوار تعمیر کر دیں۔'),
(2235, 18, 95, 'ذوالقرنین نے کہا جو طاقت میرے پروردگار نے مجھے دے رکھی ہے وہ (تمہارے سامان سے) بہتر ہے۔ تم بس اپنی (بدنی) قوت سے میری مدد کرو۔ تو میں تمہارے اور ان کے درمیان بند باندھ دوں گا۔'),
(2236, 18, 96, 'میرے پاس لوہے کے بڑے بڑے ٹکڑے لاؤ (چنانچہ وہ لائے) یہاں تک کہ جب دو پہاڑوں کے (درمیانی خلا کو پُر کرکے) دونوں کے سروں کے درمیان کو برابر کر دیا۔ تو کہا آگ پھونکو۔ یہاں تک کہ جب اس (آہنی دیوار) کو بالکل آگ بنا دیا تو کہا اب پگھلا ہوا تانبا لاؤ تاکہ اس پر انڈیل دوں۔'),
(2237, 18, 97, '(چنانچہ یہ دیوار اتنی مضبوط بن گئی کہ) یاجوج و ماجوج نہ اس پر چڑھ سکتے تھے اور نہ ہی اس میں سرنگ لگا سکتے تھے۔'),
(2238, 18, 98, 'ذوالقرنین نے کہا یہ میرے پرورگار کی رحمت ہے تو جب میرے پروردگار کے وعدے کے وقت آجائے گا تو وہ اسے ڈھا کر زمین کے برابر کر دے گا۔ اور میرے پروردگار کا وعدہ برحق ہے۔'),
(2239, 18, 99, 'اور ہم اس (قیامت کے) دن ہم اس طرح ان کو چھوڑ دیں گے کہ دریا کی لہروں کی طرح گڈمڈ ہو جائیں گے۔ اور صور پھونکا جائے گا۔ اور پھر ہم سب کو پوری طرح جمع کر دیں گے۔'),
(2240, 18, 100, 'اور اس دن ہم دوزخ کو کافروں کے سامنے پیش کر دیں گے۔'),
(2241, 18, 101, 'جن کی آنکھوں پر میری یاد سے پردہ پڑا ہوا تھا (کہ میری آیات نہیں دیکھتے تھے) اور وہ سن نہیں سکتے تھے۔'),
(2242, 18, 102, 'کیا کافروں کا یہ خیال ہے کہ وہ مجھے چھوڑ کر میرے بندوں کو اپنا کارساز اور سرپرست بنا لیں گے۱ (اور ہم باز پرس نہیں کریں گے) بےشک ہم نے دوزخ کو کافروں کی مہمانی کیلئے تیار کر رکھا ہے۔'),
(2243, 18, 103, '(اے پیغمبر(ص)) آپ کہہ دیجئے (اے لوگو) کیا ہم تمہیں بتا دیں کہ اعمال کے اعتبار سے سب سے زیادہ گھاٹے میں کون ہیں۔'),
(2244, 18, 104, 'جن کی دنیا کی زندگی کی تمام سعی و کوشش اکارت ہوگئی حالانکہ وہ یہ خیال کرتے ہیں کہ وہ بڑے اچھے کام کر رہے ہیں۔'),
(2245, 18, 105, 'یہ وہ لوگ ہیں جنہوں نے اپنے پروردگار کی آیتوں کا اور اس کی بارگاہ میں حاضری کا انکار کیا پس ان کے سارے اعمال ضائع ہوگئے۔ اس لئے ہم قیامت کے دن ان کے لئے کوئی وزن قائم نہیں کریں گے۔'),
(2246, 18, 106, 'یہ ان کی سزا ہے جہنم۔ ان کے کفر کرنے اور میری آیات اور میرے رسولوں کا مذاق اڑانے کی پاداش میں۔'),
(2247, 18, 107, 'بےشک جو لوگ ایمان لائے اور نیک عمل بھی کئے تو ان کی مہمانی کیلئے فردوس کے باغ ہیں۔'),
(2248, 18, 108, 'جن میں وہ ہمیشہ رہیں گے وہاں سے منتقل ہونا پسند نہیں کریں گے۔'),
(2249, 18, 109, 'کہہ دیجئے! کہ اگر میرے پروردگار کے کلمات لکھنے کیلئے سمندر سیاہی بن جائے تو وہ ختم ہو جائے گا قبل اس کے کہ میرے پروردگار کے کلمات ختم ہوں اگرچہ ہم اس کی مدد کیلئے ویسا ہی ایک سمندر لے آئیں۔'),
(2250, 18, 110, '(اے رسول(ص)) کہہ دیجئے! کہ میں (بھی) تمہاری طرح ایک بشر (انسان) ہوں البتہ میری طرف وحی کی جاتی ہے کہ تمہارا خدا ایک ہی خدا ہے پس جو کوئی اپنے پروردگار کی بارگاہ میں حاضر ہونے کا امیدوار ہے۔ اسے چاہیے کہ نیک عمل کرتا رہے اور اپنے پروردگار کی عبادت میں کسی کو بھی شریک نہ کرے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کاف۔ ہا۔ یا۔ عین۔ صاد۔'),
(2252, 19, 2, '(اے رسول (ص)) آپ کے پروردگار نے اپنے (خاص) بندے زکریا(ع) پر جو (خاص) رحمت کی تھی یہ اس کا تذکرہ ہے۔'),
(2253, 19, 3, 'جب انہوں نے اپنے پروردگار کو چپکے چپکے پکارا۔'),
(2254, 19, 4, 'کہا اے میرے پروردگار! میری ہڈیاں کمزور ہوگئی ہیں اور سر میں بڑھاپے کے شعلے بھڑک اٹھے ہیں۔ (بال سفید ہوگئے ہیں) اور میرے پروردگار میں کبھی تجھ سے دعا مانگ کر نامراد نہیں رہا ہوں۔'),
(2255, 19, 5, 'اور میں اپنے بعد بھائی بندوں سے ڈرتا ہوں۔ اور میری بیوی بانجھ ہے سو تو ہی مجھے (خاص) اپنے پاس سے ایک وارث عطا کر۔'),
(2256, 19, 6, 'جو میرا بھی وارث بنے اور آل یعقوب کا بھی اور اے میرے پروردگار! تو اسے پسندیدہ بنا۔'),
(2257, 19, 7, '(ارشاد ہوا) اے زکریا! ہم تمہیں ایک لڑکے کی خوشخبری دیتے ہیں۔ جس کا نام یحییٰ ہوگا جس کا اس سے پہلے ہم نے کوئی ہمنام نہیں بنایا۔'),
(2258, 19, 8, 'زکریا نے (از راہ تعجب) کہا اے میرے پروردگار! میرے ہاں لڑکا کیسے ہوگا؟ جبکہ میری بیوی بانجھ ہے اور میں بڑھاپے کی انتہا کو پہنچا ہوا ہوں۔'),
(2259, 19, 9, 'ارشاد ہوا: ایسا ہی ہوگا۔ تمہارا پروردگار فرماتا ہے کہ وہ مجھ پر آسان ہے اور میں نے ہی اس سے پہلے تمہیں پیدا کیا جبکہ تم کچھ بھی نہ تھے۔'),
(2260, 19, 10, 'زکریا (ع) نے عرض کیا: اے میرے پروردگار! میرے لئے کوئی علامت قرار دے۔ ارشاد ہوا تمہارے لئے علامت یہ ہے کہ تم تندرست ہوتے ہوئے بھی برابر تین رات (دن) تک لوگوں سے بات نہیں کر سکوگے۔'),
(2261, 19, 11, 'پس وہ عبادت گاہ سے نکل کر اپنی قوم کے پاس آئے اور انہیں اشارہ سے کہا کہ صبح و شام (خدا کی) تسبیح و تقدیس کرو۔'),
(2262, 19, 12, '(جب حکم خدا کے مطابق وہ لڑکا پیدا ہوا اور بڑھا تو ہم نے کہا) اے یحییٰ! کتاب کو مضبوطی سے پکڑو اور ہم نے اسے بچپن ہی میں علم و حکمت سے نوازا۔'),
(2263, 19, 13, 'اور وہ اپنے والدین سے نیکی کرنے والا تھا وہ سرکش اور نافرمان نہ تھا۔'),
(2264, 19, 14, 'اور ہم نے خاص اپنے پاس سے (اسے) رحمدلی اور پاکیزگی عطا کی اور وہ پرہیزگار تھا۔'),
(2265, 19, 15, 'سلام ہو اس پر جس دن وہ پیدا ہوا اور جس دن وفات پائے گا اور جس دن وہ زندہ کرکے اٹھایا جائے گا۔'),
(2266, 19, 16, '(اے رسول(ص)) اس کتاب میں مریم کا ذکر کیجئے۔ جب کہ وہ اپنے گھر والوں سے الگ ہوکر ایک مشرقی مکان میں گئیں۔'),
(2267, 19, 17, 'پھر اس نے ان لوگوں کی طرف سے پردہ کر لیا پس ہم نے اس کی طرف اپنی جانب سے روح (یعنی فرشتہ) کو بھیجا تو وہ اس کے سامنے ایک تندرست انسان کی صورت میں نمودار ہوا۔'),
(2268, 19, 18, 'مریم نے (گھبرا کر) کہا کہ اگر تو پرہیزگار آدمی ہے تو میں تجھ سے خدائے رحمن کی پناہ مانگتی ہوں۔'),
(2269, 19, 19, 'اس (فرشتہ) نے کہا میں تو تمہارے پروردگار کا فرستادہ ہوں تاکہ تمہیں ایک پاکیزہ لڑکا دوں۔'),
(2270, 19, 20, 'مریم نے کہا میرے ہاں لڑکا کیسے ہو سکتا ہے جبکہ کسی بشر نے مجھے چھوا تک نہیں ہے اور نہ ہی میں بدکردار ہوں۔'),
(2271, 19, 21, 'فرشتہ نے کہا یونہی ہے (مگر) تمہارے پروردگار نے فرمایا ہے وہ کام میرے لئے آسان ہے۔ اور یہ اس لئے بھی ہے کہ ہم اسے لوگوں کے لئے (اپنی قدرت کی) ایک نشانی قرار دیں اور اپنی طرف سے رحمت اور یہ ایک طے شدہ بات ہے۔'),
(2272, 19, 22, 'پس وہ اس (لڑکے) کے ساتھ حاملہ ہوگئی پھر وہ اس حمل کو لئے ہوئے دور جگہ چلی گئی۔'),
(2273, 19, 23, 'اس کے بعد دردِ زہ اسے کھجور کے درخت کے تنا کے پاس لے گیا (اور) کہا کاش میں اس سے پہلے مر گئی ہوتی اور بالکل نسیاً منسیاً (بھول بسری) ہوگئی ہوتی۔'),
(2274, 19, 24, 'اور ایک منادی نے اس کے نیچے سے آواز دی کہ (اے مریم) رنجیدہ نہ ہو۔ تیرے پروردگار نے تیرے نیچے ایک چھوٹی سی نہر جاری کر دی ہے۔'),
(2275, 19, 25, 'اور کھجور کے تنا کو پکڑ کر اپنی طرف ہلا۔ وہ تم پر تر و تازہ اور پکی ہوئی کھجوریں گرائے گی۔'),
(2276, 19, 26, 'پس تو (خرمے) کھا اور (نہر کا پانی) پی اور (بیٹے کو دیکھ کر) اپنی آنکھیں ٹھنڈی کر۔ پھر اگر کسی آدمی کو دیکھے اور (وہ تم سے کچھ پوچھے) تو (اشارہ سے) کہہ دینا کہ میں نے خدائے رحمن کے لئے (چپ کے) روزہ کی منت مانی ہے تو میں آج کسی آدمی سے بات نہیں کروں گی۔'),
(2277, 19, 27, 'اس کے بعد وہ بچہ کو (گود میں) اٹھائے اپنی قوم کے پاس لائی۔ انہوں نے کہا اے مریم تو نے بڑا برا کام کیا ہے۔'),
(2278, 19, 28, 'اے ہارون کی بہن! نہ تیرا باپ برا آدمی تھا اور نہ ہی تیری ماں بدکار تھی۔'),
(2279, 19, 29, 'مریم نے (نومولود) بچہ کی طرف اشارہ کیا (کہ اس سے پوچھو کہ کہاں سے آیا؟) وہ لوگ کہنے لگے کہ ہم اس سے کس طرح بات کریں گے جو ابھی گہوارہ میں (کمسن) بچہ ہے؟'),
(2280, 19, 30, '(مگر) وہ بچہ بولا: میں اللہ کا بندہ ہوں اس نے مجھے کتاب عطا کی ہے اور مجھے نبی بنایا ہے۔'),
(2281, 19, 31, 'اور اس نے مجھے نماز (پڑھنے) اور زکوٰۃ (دینے) کا حکم دیا ہے جب تک کہ میں زندہ ہوں۔'),
(2282, 19, 32, 'اور اس نے مجھے اپنی ماں کے ساتھ نیک سلوک کرنے والا بنایا ہے اور مجھے سرکش اور بدبخت نہیں بنایا۔'),
(2283, 19, 33, 'سلامتی ہے مجھ پر جس دن میں پیدا ہوا، جس دن میں مروں گا اور جس دن زندہ کرکے اٹھایا جاؤں گا۔'),
(2284, 19, 34, 'یہ ہے عیسیٰ بن مریم (اور یہ ہے) اس کے بارے میں وہ سچی بات جس میں لوگ شک کرتے ہیں۔'),
(2285, 19, 35, 'یہ بات اللہ کے شایانِ شان نہیں ہے کہ وہ کسی کو اپنا بیٹا بنائے۔ پاک ہے اس کی ذات جب وہ کسی کام کا فیصلہ کر لیتا ہے تو اسے کہتا ہے ہو جا تو وہ (کام) ہو جاتا ہے۔'),
(2286, 19, 36, '(عیسیٰ نے کہا) بےشک میرا اور تمہارا پروردگار اللہ ہے پس تم اس کی عبادت کرو۔ یہی سیدھا راستہ ہے۔'),
(2287, 19, 37, 'پھر مختلف گروہ آپ کے بارے میں اختلاف کرنے لگے تو جن لوگوں نے کفر اختیار کیا ان کے لئے ہلاکت ہے جب وہ ایک بہت بڑے سخت دن کا سامنا کریں گے۔'),
(2288, 19, 38, 'جس دن وہ ہمارے پاس آئیں گے تو اس دن ان کے کان کیسے سننے والے اور آنکھیں کیسی دیکھنے والی ہوں گی۔ مگر آج یہ ظالم کھلی ہوئی گمراہی میں مبتلا ہیں۔'),
(2289, 19, 39, 'اور (اے رسول(ص)) انہیں حسرت و ندامت کے دن سے ڈرائیں جبکہ ہر بات کا (آخری) فیصلہ کر دیا جائے گا۔ اور یہ لوگ غفلت میں پڑے ہیں اور ایمان نہیں لاتے۔'),
(2290, 19, 40, '(آخرکار) ہم ہی زمین کے اور جو کچھ اس کے اوپر ہے اس کے وارث ہوں گے اور سب ہماری طرف لوٹائے جائیں گے۔'),
(2291, 19, 41, 'اور قرآن میں ابراہیم (ع) کا ذکر کیجئے۔ بےشک وہ بڑے راست باز تھے۔'),
(2292, 19, 42, 'جب انہوں نے اپنے (منہ بولے) باپ (حقیقی چچا) آذر سے کہا کہ اے باپ! آپ ان چیزوں کی عبادت کیوں کرتے ہیں جو نہ سنتی ہیں اور نہ دیکھتی ہیں اور نہ آپ کو کوئی فائدہ پہنچا سکتی ہیں۔'),
(2293, 19, 43, 'اے باپ! میرے پاس وہ علم آیا ہے جو آپ کے پاس نہیں آیا۔ آپ میری پیروی کیجئے میں آپ کو سیدھا راستہ دکھا دوں گا۔'),
(2294, 19, 44, 'اے باپ! شیطان کی پرستش نہ کیجئے (کیونکہ) بےشک خدائے رحمن کا وہ نافرمان ہے۔'),
(2295, 19, 45, 'اے باپ! مجھے اندیشہ ہے کہ آپ پر خدائے رحمن کا عذاب نازل نہ ہو جائے اور آپ شیطان کے ساتھی بن جائیں۔'),
(2296, 19, 46, 'آزر نے کہا: اے ابراہیم کیا تم میرے خداؤں سے روگردانی کرتے ہو؟ اگر تم باز نہ آئے تو میں تمہیں سنگسار کر دوں گا۔ اور تم ایک مدت تک مجھ سے دور ہو جاؤ۔'),
(2297, 19, 47, 'ابراہیم نے کہا: (اچھا خدا حافظ) آپ پر میرا سلام! میں عنقریب اپنے پروردگار سے آپ کے لئے مغفرت طلب کروں گا۔ بےشک وہ مجھ پر بڑا مہربان ہے۔'),
(2298, 19, 48, 'اور میں آپ لوگوں سے اور ان سے جنہیں آپ اللہ کو چھوڑ کر پکارتے ہیں کنارہ کرتا ہوں۔ میں تو اپنے پروردگار کو ہی پکاروں گا۔ مجھے امید ہے کہ میں اپنے پروردگار کی دعا و پکار کی برکت سے نامراد نہیں رہوں گا۔'),
(2299, 19, 49, 'پس جب وہ ان لوگوں کے اور ان کے خداؤں سے کنارہ کش ہوگئے تو ہم نے ان کو اسحاق و یعقوب جیسی اولاد عنایت فرمائی اور ہم نے سب کو نبی بنایا۔'),
(2300, 19, 50, 'اور ہم نے ان ہستیوں کو اپنی (خاص) رحمت سے حصہ عطا کیا اور ان کے لئے سچائی کی زبان کو بلند قرار دیا۔ (یعنی آئندہ نسلوں میں ان کے ذکر جمیل کی آواز بلند کی)۔'),
(2301, 19, 51, 'اور (اے رسول) کتاب (قرآن) میں موسیٰ کا ذکر کیجئے! بےشک وہ اللہ کے برگزیدہ بندے اور نبی مرسل تھے۔'),
(2302, 19, 52, 'اور ہم نے کوہِ طور کی دائیں جانب سے آواز دی اور راز و نیاز کی باتیں کرنے کیلئے انہیں اپنا مقرب بنایا۔'),
(2303, 19, 53, 'اور ہم نے انہیں اپنی خاص رحمت سے آواز دی اور راز و نیاز کی باتیں کرنے کے لئے اپنا مقرب بنایا اور (بطور وزیر) ان کو ان کا بھائی ہارون عطا کیا جو نبی تھا۔'),
(2304, 19, 54, 'اور (اے پیغمبر(ص)) آپ کتاب (قرآن) میں اسماعیل (ع) کا ذکر کیجئے جو وعدہ کے سچے اور نبی مرسل تھے۔'),
(2305, 19, 55, 'اور اپنے گھر والوں کو نماز اور زکوٰۃ کا حکم دیتے تھے اور وہ اپنے پروردگار کے نزدیک پسندیدہ تھے۔'),
(2306, 19, 56, 'اور (اے رسول(ص)) کتاب (قرآن) میں ادریس کا ذکر کیجئے بےشک وہ بڑے سچے نبی تھے۔'),
(2307, 19, 57, 'اور ہم نے انہیں بڑے ہی اونچے مقام تک بلند کیا تھا۔'),
(2308, 19, 58, 'یہ وہ نبی ہیں جن پر اللہ نے انعام فرمایا۔ آدم (ع) کی نسل سے اور ان کی نسل سے جنہیں ہم نے نوح کے ساتھ کشتی میں سوار کیا تھا اور ابراہیم و اسرائیل (یعقوب) کی نسل سے اور (یہ سب) ان لوگوں میں سے تھے جنہیں ہم نے راہ رست دکھائی اور منتخب کیا جب ان کے سامنے خدائے رحمن کی آیتین پڑھی جاتی تھیں تو وہ روتے ہوئے سجدے میں گر پڑتے تھے۔'),
(2309, 19, 59, 'پھر ان کے بعد کچھ وہ ناخلف ان کے جانشین ہوئے جنہوں نے نمازوں کو ضائع کیا اور خواہشات کی پیروی کی پس وہ عنقریب گمراہی (کے انجام) سے دوچار ہوں گے۔'),
(2310, 19, 60, 'ہاں البتہ جو توبہ کر لیں، ایمان لائیں اور نیک عمل بجا لائیں تو یہ لوگ بہشت میں داخل ہوں گے اور ان پر کچھ بھی ظلم نہیں کیا جائے گا۔'),
(2311, 19, 61, 'وہ ہمیشہ رہنے والی جنتیں جن کا خدائے رحمن نے غائبانہ وعدہ کر رکھا ہے۔ بےشک اس کا وعدہ (سامنے) آنے والا ہے۔'),
(2312, 19, 62, 'وہ وہاں سلامتی کی صداؤں کے سوا کوئی بے ہودہ بات نہیں سنیں گے اور ان کا مقررہ رزق انہیں صبح و شام ملتا رہے گا۔'),
(2313, 19, 63, 'یہ ہے وہ بہشت جس کا وارث ہم اپنے بندوں میں سے اس کو بنائیں گے جو پرہیزگار ہوگا۔'),
(2314, 19, 64, '(اے رسول(ص)) ہم (فرشتے) آپ کے پروردگار کے حکم کے بغیر (زمین پر) نازل نہیں ہوتے جو کچھ ہمارے آگے ہے یا جو کچھ ہمارے پیچھے ہے اور جو کچھ ان کے درمیان ہے سب کچھ اسی کا ہے اور آپ کا پروردگار بھولنے والا نہیں ہے۔'),
(2315, 19, 65, 'وہ آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے کا پروردگار ہے پس اسی کی عبادت کرو اور اس کی عبادت پر ثابت قدم رہو کیا تم اس کا کوئی ہمنام و ہمسر جانتے ہو؟'),
(2316, 19, 66, 'اور (غافل) انسان کہتا ہے کہ جب میں مر جاؤں گا تو کیا پھر زندہ کرکے نکالا جاؤں گا؟'),
(2317, 19, 67, 'کیا انسان کو یاد نہیں رہا کہ ہم ہی نے اس کو اس سے پہلے پیدا کیا جبکہ وہ کچھ بھی نہیں تھا۔'),
(2318, 19, 68, 'تو قسم ہے تمہارے پروردگار کی کہ ہم ان کو اور شیطانوں کو اکٹھا کریں گے پھر ان سب کو جہنم کے اردگرد گھٹنوں کے بل حاضر کریں گے۔'),
(2319, 19, 69, 'پھر ہم ہر گروہ میں سے اس شخص کو جدا کریں گے جو خدائے رحمن کے مقابلہ میں زیادہ سرکش تھا۔'),
(2320, 19, 70, 'پھر ہم ہی زیادہ بہتر جانتے ہیں کہ کون اس (جہنم) میں داخل ہونے کا زیادہ سزاوار ہے؟'),
(2321, 19, 71, 'اور تم میں سے کوئی بھی ایسا نہیں ہے جو جہنم پر وارد نہ ہو (جو وہاں سے نہ گزرے) یہ حتمی طے شدہ فیصلہ ہے جس کا پورا کرنا تمہارے پروردگار کے ذمہ ہے۔'),
(2322, 19, 72, 'پھر جو پرہیزگار ہوں گے ہم انہیں نجات دے دیں گے اور ظالموں کو اس میں گھٹنوں کے بل گرا چھوڑ دیں گے۔'),
(2323, 19, 73, 'جب ان کے سامنے ہماری واضح آیتیں پڑھی جاتی ہیں تو کافر لوگ ایمان لانے والوں سے کہتے ہیں کہ (دیکھو ہم) دونوں گروہوں میں سے رہائش گاہ کس کی اچھی ہے اور محفل کس کی زیادہ شاندار ہے؟'),
(2324, 19, 74, 'حالانکہ ہم ان سے پہلے کتنی ایسی قوموں کو ہلاک کر چکے ہیں جو ساز و سامان اور ظاہری شان و شوکت میں ان سے بڑھی ہوئی تھیں۔'),
(2325, 19, 75, 'آپ کہہ دیجئے! کہ جو کوئی گمراہی میں مبتلا ہو تو خدائے رحمن اسے برابر ڈھیل دیتا رہتا ہے یہاں تک کہ جب یہ لوگ اسے دیکھیں گے جس کا ان سے وعدہ وعید ہوا ہے خواہ وہ (یہاں کا) عذاب ہو یا قیامت کی گھڑی! تب انہیں معلوم ہوگا کہ مکان کے لحاظ سے زیادہ برا کون ہے اور لاؤ لشکر کے اعتبار سے زیادہ کمزور کون ہے؟'),
(2326, 19, 76, 'اور اللہ ہدایت یافتہ لوگوں کی ہدایت میں اور اضافہ کرتا ہے اور جو باقی رہنے والی نیکیاں ہیں وہ تمہارے پروردگار کے نزدیک ثواب اور انجام کے اعتبار سے بہتر ہیں۔'),
(2327, 19, 77, 'کیا آپ نے اس شخص کو دیکھا ہے جس نے ہماری آیتوں کا انکار کیا ہے اور کہتا ہے کہ مجھے مال ضرور دیا جائے گا اور اولاد بھی۔'),
(2328, 19, 78, 'کیا وہ غیب پر مطلع ہوگیا ہے؟ یا اس نے خدائے رحمن سے کوئی عہد و پیمان حاصل کر لیا ہے؟'),
(2329, 19, 79, 'ہرگز ایسا نہیں ہے جو کچھ یہ کہتا ہے ہم اسے لکھ لیں گے۔ اور اس کے عذاب میں برابر اضافہ کرتے جائیں گے۔'),
(2330, 19, 80, 'وہ جو کہتا ہے (کہ اس کے پاس مال و اولاد ہے) اس کے وارث ہم ہی ہوں گے۔ اور یہ تو اکیلا ہماری بارگاہ میں حاضر ہوگا۔'),
(2331, 19, 81, 'اور ان لوگوں نے خدا کو چھوڑ کر بہت سے خدا بنا رکھے ہیں تاکہ وہ ان کیلئے عزت و قوت کا باعث ہوں۔'),
(2332, 19, 82, 'ہرگز ایسا نہیں ہے وہ عنقریب ان کی عبادت کا انکار کریں گے اور ان کے مخالف ہوں گے۔'),
(2333, 19, 83, 'اے (رسول(ص)) کیا آپ نے نہیں دیکھا کہ ہم نے شیطانوں کو کافروں پر چھوڑ رکھا ہے جو انہیں برابر اکساتے رہتے ہیں۔'),
(2334, 19, 84, 'آپ ان کے بارے میں جلدی نہ کیجئے ہم تو اچھی طرح ان کے دن گن رہے ہیں۔'),
(2335, 19, 85, 'جس دن ہم پرہیزگاروں کو اپنے حضور مہمانوں کی طرح لائیں گے۔'),
(2336, 19, 86, 'اور مجرموں کو پیاسے جانوروں کی طرح جہنم کی طرف ہانک کر لے جائیں گے۔'),
(2337, 19, 87, 'انہیں شفاعت کا کوئی اختیار نہ ہوگا سوائے اس کے جس نے اللہ سے عہد لے لیا ہوگا۔'),
(2338, 19, 88, 'اور وہ (نصاریٰ) کہتے ہیں کہ خدا نے اپنا ایک بیٹا بنا رکھا ہے۔'),
(2339, 19, 89, 'تم نے یہ ایسی سخت بُری بات کہی ہے۔'),
(2340, 19, 90, 'کہ قریب ہے کہ آسمان پھٹ پڑیں اور زمین شق ہو جائے اور پہاڑ ٹکڑے ٹکڑے ہو کر گر پڑیں۔'),
(2341, 19, 91, 'کیونکہ وہ کہہ رہے ہیں کہ خدائے رحمن کا بیٹا ہے۔'),
(2342, 19, 92, 'اور یہ بات رحمن کے شایانِ شان نہیں ہے کہ وہ کسی کو اپنا بیٹا بنائے۔'),
(2343, 19, 93, 'آسمانوں اور زمین میں جو کوئی بھی ہے وہ خدائے رحمن کی بارگاہ میں بندہ بن کر حاضر ہونے والے ہیں۔'),
(2344, 19, 94, 'اس (اللہ) نے ان سب کا احاطہ کر رکھا ہے اور انہیں اچھی طرح شمار کر رکھا ہے۔'),
(2345, 19, 95, 'اور قیامت کے دن ان میں سے ہر ایک تنہا تنہا اس کی بارگاہ میں حاضر ہوگا۔'),
(2346, 19, 96, 'بےشک جو لوگ ایمان لائے اور نیک عمل بجا لائے۔ ان کیلئے عنقریب خدائے رحمن (لوگوں کے دلوں میں) محبت قرار دے گا۔'),
(2347, 19, 97, 'اے (رسول(ص)) ہم نے اس (قرآن) کو آپ کی زبان میں آسان کر دیا ہے تاکہ آپ پرہیزگاروں کو خوشخبری سنائیں اور جھگڑالو قوم کو ڈرائیں۔'),
(2348, 19, 98, 'اور ہم نے ان سے پہلے کتنی ہی قومیں ہلاک کی ہیں کیا آپ ان میں سے کسی کو بھی محسوس کرتے ہیں یا ان کی کوئی بھنک بھی سنتے ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طا۔ ھا۔'),
(2350, 20, 2, 'ہم نے اس لئے آپ پر قرآن نازل نہیں کیا کہ آپ مشقت میں پڑ جائیں۔'),
(2351, 20, 3, 'بلکہ (اس لئے نازل کیا ہے کہ) جو (خدا سے) ڈرنے والا ہے اس کی یاددہانی ہو۔'),
(2352, 20, 4, '(یہ) اس ہستی کی طرف سے نازل ہوا ہے جس نے زمین اور بلند آسمانوں کو پیدا کیا ہے۔'),
(2353, 20, 5, 'وہ خدائے رحمن ہے جس کا عرش پر اقتدار قائم ہے۔'),
(2354, 20, 6, 'جو کچھ آسمانوں اور زمین میں ہے اور جو کچھ ان دونوں کے درمیان ہے اور جو کچھ زمین کے نیچے ہے سب اسی کا ہے۔'),
(2355, 20, 7, 'اگر تم پکار کر بات کرو (تو تمہاری مرضی) وہ تو راز کو بلکہ اس سے بھی زیادہ مخفی بات کو جانتا ہے۔'),
(2356, 20, 8, 'وہ الٰہ ہے اس کے سوا کوئی الہ نہیں ہے۔ سب اچھے اچھے نام اسی کے لئے ہیں۔'),
(2357, 20, 9, 'کیا آپ(ص) تک موسیٰ کا واقعہ پہنچا ہے؟'),
(2358, 20, 10, 'جب انہوں نے آگ دیکھی تو انہوں نے اپنے گھر والوں سے کہا کہ تم (یہیں) ٹھہرو۔ میں نے آگ دیکھی ہے شاید تمہارے لئے ایک آدھ انگارا لے آؤں یا آگ کے پاس راستہ کا کوئی پتّہ پاؤں؟'),
(2359, 20, 11, 'تو جب اس کے پاس گئے تو انہیں آواز دی گئی کہ اے موسیٰ!'),
(2360, 20, 12, 'میں ہی تمہارا پروردگار ہوں! پس اپنی جوتیاں اتار دو (کیونکہ) تم طویٰ نامی ایک مقدس وادی میں ہو۔'),
(2361, 20, 13, 'اور میں نے تمہیں (پیغمبری کیلئے) منتخب کیا ہے۔ پس (تمہیں) جو کچھ وحی کی جاتی ہے اسے غور سے سنو۔'),
(2362, 20, 14, 'بےشک میں ہی اللہ ہوں۔ میرے سوا کوئی الٰہ نہیں ہے پس میری عبادت کرو اور میری یاد کیلئے نماز قائم کرو۔'),
(2363, 20, 15, 'یقیناً قیامت آنے والی ہے میں اسے پوشیدہ رکھنا چاہتا ہوں تاکہ ہر شخص کو اس کی سعی و کوشش کا معاوضہ مل جائے۔'),
(2364, 20, 16, 'پس (خیال رکھنا) کہیں وہ شخص جو اس پر ایمان نہیں رکھتا اور اپنی خواہش نفس کا پیرو ہے تمہیں اس کی فکر سے روک نہ دے ورنہ تم تباہ ہو جاؤگے۔'),
(2365, 20, 17, 'اور اے موسیٰ! تمہارے دائیں ہاتھ میں کیا ہے؟'),
(2366, 20, 18, 'کہا وہ میرا عصا ہے میں اس پر ٹیک لگاتا ہوں اور اس سے اپنی بکریوں کیلئے (درختوں سے) پتے جھاڑتا ہوں اور میرے لئے اس میں اور بھی کئی فائدے ہیں۔'),
(2367, 20, 19, 'ارشاد ہوا اے موسیٰ! اسے پھینک دو۔'),
(2368, 20, 20, 'چنانچہ موسیٰ نے اسے پھینک دیا تو وہ ایک دم دوڑتا ہوا سانپ بن گیا۔'),
(2369, 20, 21, 'ارشاد ہوا اسے پکڑ لو۔ اور ڈرو نہیں ہم ابھی اسے اس کی پہلی حالت کی طرف پلٹا دیں گے۔'),
(2370, 20, 22, 'اور اپنے ہاتھ کو سمیٹ کر اپنے بازو کے نیچے (بغل میں) کر لو وہ کسی برائی و بیماری کے بغیر چمکتا ہوا نکلے گا یہ دوسری نشانی ہوگی۔'),
(2371, 20, 23, 'تاکہ ہم آپ کو اپنی بڑی نشانیوں سے کچھ دکھائیں۔'),
(2372, 20, 24, 'جاؤ فرعون کے پاس کہ وہ بڑا سرکش ہو گیا ہے۔'),
(2373, 20, 25, 'موسیٰ نے کہا اے میرے پروردگار! میرا سینہ کشادہ فرما۔ (حوصلہ فراخ کر)۔'),
(2374, 20, 26, 'اور میرے کام کو میرے لئے آسان کر۔'),
(2375, 20, 27, 'اور میری زبان کی گرہ کھول دے۔'),
(2376, 20, 28, 'تاکہ لوگ میری بات سمجھ سکیں۔'),
(2377, 20, 29, 'اور میرے خاندان میں سے میرے بھائی۔'),
(2378, 20, 30, 'ہارون کو میرا وزیر بنا۔'),
(2379, 20, 31, 'اس کے ذریعے سے میری کمر کو مضبوط بنا۔'),
(2380, 20, 32, 'اسے میرے کام (رسالت) میں میرا شریک بنا۔'),
(2381, 20, 33, 'تاکہ ہم کثرت سے تیری تسبیح کریں۔'),
(2382, 20, 34, 'اور کثرت سے تیرا ذکر کریں۔'),
(2383, 20, 35, 'بےشک تو ہمارے حال کو خوب دیکھ رہا ہے۔'),
(2384, 20, 36, 'خدا نے فرمایا اے موسیٰ! تمہاری درخواست منظور کر لی گئی ہے۔'),
(2385, 20, 37, 'اور ہم ایک مرتبہ اور بھی تم پر احسان کر چکے ہیں۔'),
(2386, 20, 38, 'جب ہم نے تمہاری ماں کی طرف وحی بھیجی جو بھیجنا تھی (جو اب بذریعہ وحی تمہیں بتائی جا رہی ہے)۔'),
(2387, 20, 39, 'کہ اس (موسیٰ) کو صندوق میں رکھ اور پھر صندوق کو دریا میں ڈال دے پھر دریا اسے کنارہ پر پھینک دے گا (اور) اسے وہ شخص (فرعون) اٹھائے گا جو میرا بھی دشمن ہے اور اس (موسیٰ) کا بھی دشمن ہے میں نے تم پر اپنی محبت کا اثر ڈال دیا۔ (جو دیکھتا وہ پیار کرتا) اور اس لئے کہ تم میری خاص نگرانی میں پرورش پائے۔'),
(2388, 20, 40, 'اور وہ وقت یاد کرو جب تمہاری بہن چل رہی تھی اور (فرعون کے اہل خانہ سے) کہہ رہی تھی کہ کیا میں تم لوگوں کو ایسی (دایہ) بتاؤں جو اس کی پرورش کرے؟ اور اس طرح ہم نے تمہیں تمہاری ماں کی طرف لوٹا دیا تاکہ اس کی آنکھ ٹھنڈی ہو اور رنجیدہ نہ ہو اور تم نے ایک شخص کو قتل کر دیا تھا تو ہم نے تمہیں اس غم سے نجات دی اور ہم نے تمہاری ہر طرح آزمائش کی۔ پھر تم کئی برس تک مدین کے لوگوں میں رہے اور پھر اے موسیٰ! تم اپنے معین وقت پر (یہاں) آگئے۔'),
(2389, 20, 41, 'اور میں نے تمہیں اپنی ذات کیلئے منتخب کر لیا۔'),
(2390, 20, 42, '(سو اب) تم اور تمہارا بھائی میری نشانیوں کے ساتھ (فرعون کے پاس) جاؤ اور میری یاد میں سستی نہ کرنا۔'),
(2391, 20, 43, 'تم دونوں فرعون کے پاس جاؤ کہ وہ سرکش ہوگیا ہے۔'),
(2392, 20, 44, 'اس سے نرمی کے ساتھ بات کرنا کہ شاید وہ نصیحت قبول کرے یا ڈر جائے۔'),
(2393, 20, 45, 'ان دونوں نے کہا اے ہمارے پروردگار! ہمیں اندیشہ ہے کہ وہ ہم پر زیادتی کرے یا سرکشی کرے؟'),
(2394, 20, 46, 'ارشاد ہوا تم ڈرو نہیں میں تمہارے ساتھ ہوں۔ سب کچھ سن رہا ہوں اور دیکھ رہا ہوں۔'),
(2395, 20, 47, 'تم (بے دھڑک) اس کے پاس جاؤ۔ اور کہو کہ ہم تیرے پروردگار کے پیغمبر ہیں۔ سو تو بنی اسرائیل کو ہمارے ساتھ روانہ کر اور ان کو تکلیف نہ پہنچا۔ ہم تیرے پاس تیرے پروردگار کی طرف سے معجزہ لے کر آئے ہیں اور سلامتی ہے اس کے لئے جو ہدایت کی پیروی کرے۔'),
(2396, 20, 48, 'بےشک ہماری طرف یہ وحی کی گئی ہے کہ عذاب اس کیلئے ہے جو (آیات اللہ کو) جھٹلائے اور (اس کے احکام سے) روگردانی کرے۔'),
(2397, 20, 49, '(چنانچہ وہ گئے) اور فرعون نے کہا اے موسیٰ! تمہارا پروردگار کون ہے؟'),
(2398, 20, 50, 'موسیٰ نے کہا ہمارا پروردگار وہ ہے جس نے ہر چیز کو خلقت بخشی پھر راہنمائی فرمائی۔'),
(2399, 20, 51, 'فرعون نے کہا پھر ان نسلوں کا کیا ہوگا جو پہلے گزر چکی ہیں؟'),
(2400, 20, 52, 'موسیٰ نے کہا ان کا علم میرے پروردگار کے پاس ایک کتاب میں ہے میرا پروردگار نہ بھٹکتا ہے اور نہ بھولتا ہے۔'),
(2401, 20, 53, 'وہ وہی ہے جس نے تمہارے لئے زمین کو گہوارہ بنایا ہے اور اس میں تمہارے لئے راستے بنائے اور آسمان سے پانی برسایا۔ تو ہم نے اس سے مختلف اقسام کے نباتات کے جوڑے پیدا کئے۔'),
(2402, 20, 54, 'خود بھی کھاؤ اور اپنے مویشیوں کو بھی چراؤ۔ بےشک اس (نظام قدرت) میں صاحبانِ عقل کیلئے بہت سی نشانیاں ہیں۔'),
(2403, 20, 55, 'اسی زمین سے ہم نے تمہیں پیدا کیا ہے اور اسی میں ہم تمہیں لوٹائیں گے اور اسی سے دوبارہ تمہیں نکالیں گے۔'),
(2404, 20, 56, 'اور ہم نے اس (فرعون) کو اپنی سب نشانیاں دکھائیں مگر اس پر بھی اس نے جھٹلایا اور انکار کیا۔'),
(2405, 20, 57, 'اور کہا اے موسیٰ! کیا تم اس لئے ہمارے پاس آئے ہو کہ اپنے جادو کے زور سے ہمیں ہماری سرزمین سے نکال دو؟'),
(2406, 20, 58, 'سو ہم بھی تمہارے مقابلہ میں ویسا ہی جادو لائیں گے لہٰذا تم (مقابلہ کیلئے) ہمارے اور اپنے درمیان ایک وعدہ گاہ مقرر کرو۔ جس کی نہ ہم خلاف ورزی کریں گے اور نہ تم۔ اور وہ وعدہ گاہ ہو بھی ہموار اور کھلے میدان میں۔'),
(2407, 20, 59, 'موسیٰ نے کہا تمہارے لئے وعدہ کا دن جشن والا دن ہے اور یہ کہ دن چڑھے لوگ جمع کر لئے جائیں۔'),
(2408, 20, 60, 'اس کے بعد فرعون واپس چلا گیا اور اپنے سب مکر و فریب (داؤ) جمع کئے اور پھر (مقابلہ کیلئے) آگیا۔'),
(2409, 20, 61, 'موسیٰ نے (فرعونیوں سے) کہا افسوس ہے تم پر۔ اللہ پر جھوٹا بہتان نہ باندھو۔ ورنہ وہ کسی عذاب سے تمہارا قلع قمع کر دے گا۔ اور جو کوئی بہتان باندھتا ہے وہ ناکام و نامراد ہوتا ہے۔'),
(2410, 20, 62, 'پھر وہ اپنے معاملہ میں باہم جھگڑنے لگے اور پوشیدہ سرگوشیاں کرنے لگے۔'),
(2411, 20, 63, '(آخرکار) انہوں نے کہا کہ یہ دونوں جادوگر ہیں جو چاہتے ہیں کہ اپنے جادو کے زور سے تمہیں تمہاری سرزمین سے نکال دیں اور تمہارے اعلیٰ و مثالی طریقہ کار کو مٹا دیں۔'),
(2412, 20, 64, 'لہٰذا تم اپنی سب تدبیریں (داؤ پیچ) جمع کرو۔ اور پرا باندھ کر (مقابلہ میں) آجاؤ۔ یقیناً فلاح وہی پائے گا جو غالب آئے گا۔'),
(2413, 20, 65, 'ان لوگوں (جادوگروں) نے کہا اے موسیٰ تم پہلے پھینکوگے یا پہلے ہم پھینکیں؟'),
(2414, 20, 66, 'موسیٰ علیہ السلام نے کہا: نہیں۔ بلکہ تم ہی (پہلے) پھینکو! پس اچانک ان کی رسیاں اور لاٹھیاں ان کے جادو کی وجہ سے موسیٰ کو دوڑتی ہوئی محسوس ہوئیں۔'),
(2415, 20, 67, '(یہ منظر دیکھ کر) موسیٰ نے اپنے دل میں کچھ خوف محسوس کیا۔'),
(2416, 20, 68, 'ہم نے کہا (اے موسیٰ) ڈرو نہیں بےشک تم ہی غالب رہوگے۔'),
(2417, 20, 69, 'اور جو تمہارے دائیں ہاتھ میں (عصا) ہے اسے پھینک دو۔ یہ ان کی سب بناوٹی چیزوں کو نگل جائے گا۔ جو کچھ انہوں نے بنایا ہے وہ جادوگر کا فریب ہے اور جادوگر کہیں بھی آئے (جائے) کبھی کامیاب نہیں ہو سکتا۔'),
(2418, 20, 70, 'چنانچہ (ایسا ہی ہوا کہ) سب جادوگر (بے ساختہ) سجدے میں گرا دیئے گئے (اور)کہنے لگے کہ ہم ہارون اور موسیٰ کے پروردگار پر ایمان لائے ہیں۔'),
(2419, 20, 71, 'فرعون نے کہا تم اس پر ایمان لے آئے قبل اس کے کہ میں تمہیں اجازت دوں یہی تمہارا وہ بڑا (جادوگر) ہے جس نے تمہیں جادو سکھایا ہے اب میں ضرور تمہارے ہاتھ پاؤن مخالف سمت سے کٹواتا ہوں اور تمہیں کھجور کے تنوں پر سولی دیتا ہوں پھر تمہیں معلوم ہو جائے گا کہ ہم (دونوں) میں اور موسیٰ میں سے کس کا عذاب سخت اور دیرپا ہے؟'),
(2420, 20, 72, 'جادوگروں نے کہا ہمارے پاس جو کھلی نشانیاں آچکی ہیں ہم ان پر اور اس ذات پر جس نے ہمیں پیدا کیا ہے۔ کبھی تجھے ترجیح نہیں دیں گے بےشک تو جو فیصلہ کرنا چاہتا ہے وہ تو (زیادہ سے زیادہ) اسی دنیاوی زندگی (کے ختم کرنے) کا فیصلہ کر سکتا ہے۔'),
(2421, 20, 73, 'ہم تو اپنے پروردگار پر ایمان لا چکے ہیں تاکہ وہ ہماری خطاؤں کو اور اس جادوگری کو جس پر تو نے ہمیں مجبور کیا تھا معاف کر دے۔ ہمارے لئے اللہ ہی بہتر ہے اور وہی زیادہ دیرپا ہے۔'),
(2422, 20, 74, 'بےشک جو کوئی مجرم بن کر اپنے پروردگار کی بارگاہ میں حاضر ہوگا اس کیلئے وہ جہنم ہے جس میں وہ نہ مرے گا اور نہ جیئے گا۔'),
(2423, 20, 75, 'اور جو کوئی مؤمن بن کر اس کی بارگاہ میں حاضر ہوگا۔ جب کہ اس نے نیک عمل بھی کئے ہوں گے ان کے لئے بڑے بلند درجے ہیں۔'),
(2424, 20, 76, '(اور) ہمیشہ رہنے والے باغات جن کے نیچے نہریں جاری ہوں گی۔ وہ ان میں ہمیشہ رہیں گے۔ یہ اس کی جزاء ہے جو پاکباز رہا۔'),
(2425, 20, 77, 'اور ہم نے موسیٰ کی طرف وحی کی کہ میرے بندوں (بنی اسرائیل) کو لے کر نکل جاؤ پھر (عصا مار کر) ان کیلئے سمندر سے خشک راستہ بناؤ۔ نہ تمہیں پیچھے سے ان کے پکڑے جانے کا خطرہ ہو اور نہ ہی (غرق وغیرہ کا) کوئی اندیشہ۔'),
(2426, 20, 78, 'پھر فرعون نے اپنی فوجوں کے ساتھ ان کا پیچھا کیا تو انہیں سمندر نے ڈھانپ لیا جیساکہ ڈھانپنے کا حق تھا۔'),
(2427, 20, 79, 'اور فرعون نے اپنی قوم کو گمراہ ہی کیا کوئی راہنمائی نہیں کی۔'),
(2428, 20, 80, 'اے بنی اسرائیل! ہم نے تمہیں تمہارے دشمن سے نجات دی اور تم سے کوہ طور کی دائیں جانب توریت دینے کا قول و قرار کیا۔ اور تم پر من و سلویٰ نازل کیا۔'),
(2429, 20, 81, 'تم سے کہا گیا کہ جو پاکیزہ روزی تمہیں دی گئی ہے اس سے کھاؤ اور اس کے بارے میں سرکشی نہ کرو (حد سے نہ گزرو) ورنہ تم پر میرا غضب نازل ہوگا اور جس پر میرا غضب نازل ہو جائے وہ ہلاک ہی ہوگیا۔'),
(2430, 20, 82, 'اور جو کوئی توبہ کرے اور ایمان لائے اور نیک عمل بجا لائے اور پھر راہِ راست پر قائم رہے تو میں اس کو بہت ہی بخشنے والا ہوں۔'),
(2431, 20, 83, 'اے موسیٰ! اپنی قوم سے پہلے کیا چیز تمہیں جلدی لے آئی؟'),
(2432, 20, 84, 'موسیٰ نے کہا: وہ لوگ میرے نقش قدم پر آرہے ہیں۔ اور اے میرے پروردگار! میں اس لئے جلدی تیری بارگاہ میں حاضر ہوگیا ہوں کہ تو خوش ہو جائے۔'),
(2433, 20, 85, 'ارشاد ہوا۔ ہم نے تمہارے بعد تمہاری قوم کو آزمائش میں ڈال دیا ہے۔ اور سامری نے انہیں گمراہ کر دیا ہے۔'),
(2434, 20, 86, 'پس موسیٰ غصہ میں افسوس کرتے ہوئے اپنی قوم کی طرف لوٹے (اور) کہا اے میری قوم! کیا تمہارے پروردگار نے تم سے بڑا اچھا وعدہ نہیں کیا تھا؟ تو کیا تم پر (وعدہ سے) زیادہ مدت گزر گئی؟ یا تم نے چاہا کہ تمہارے رب کا غضب تم پر نازل ہو؟ اس لئے تم نے مجھ سے وعدہ خلافی کی؟'),
(2435, 20, 87, 'قوم نے کہا کہ ہم نے اپنے اختیار سے تو آپ سے وعدہ خلافی نہیں کی (البتہ بات یوں ہوئی) کہ ہمیں اس جماعت کے زیورات جمع کرکے لانے پر آمادہ کیا گیا اور یہ سامری ایک (سنہرا) بچھڑا نکال کر لایا۔ جس سے گائے کی سی آواز نکلتی تھی۔'),
(2436, 20, 88, 'تو لوگوں سے کہا یہی تمہارا خدا ہے اور موسیٰ کا بھی جسے وہ بھول گئے ہیں۔'),
(2437, 20, 89, 'کیا وہ اتنا بھی نہیں دیکھتے کہ وہ (گؤ سالہ) ان کی کسی بات کا جواب نہیں دیتا اور نہ ہی وہ ان کو نقصان یا نفع پہنچانے کا کوئی اختیار رکھتا ہے۔'),
(2438, 20, 90, 'اور ہارون نے اس سے پہلے ہی ان سے کہہ دیا تھا کہ اے میری قوم! تم اس (گو سالہ) کی وجہ سے آزمائش میں پڑ گئے ہو۔ اور یقیناً تمہارا پروردگار خدائے رحمن ہے سو تم میری پیروی کرو۔ اور میرے حکم کی تعمیل کرو۔'),
(2439, 20, 91, 'مگر قوم نے کہا کہ ہم تو برابر اس کی عبادت پر جمے رہیں گے یہاں تک کہ موسیٰ ہماری طرف آجائیں۔'),
(2440, 20, 92, 'موسیٰ نے کہا اے ہارون! جب تم نے دیکھا کہ یہ لوگ گمراہ ہوگئے ہیں۔'),
(2441, 20, 93, 'تو تمہیں کس چیز نے میری پیروی کرنے سے روکا؟ کیا تم نے میرے حکم کی خلاف ورزی کی ہے؟'),
(2442, 20, 94, 'ہارون نے کہا: اے میرے ماں جائے! میری ڈاڑھی اور میرا سر نہ پکڑئیے! مجھے تو یہ ڈر تھا کہ کہیں آپ یہ نہ کہیں کہ تم نے بنی اسرائیل میں تفرقہ ڈال دیا۔ اور میری بات کا خیال نہیں کیا (یا میرے حکم کا انتظار نہ کیا؟)۔'),
(2443, 20, 95, '(بعد ازاں) کہا اے سامری! تیرا کیا معاملہ ہے؟'),
(2444, 20, 96, 'اس نے کہا کہ میں نے ایک ایسی چیز دیکھی جو اور لوگوں نے نہیں دیکھی تو میں نے (خدا کے) فرستادہ کے نقش قدم سے ایک مٹھی (خاک) اٹھا لی۔ اور اسے (اس گو سالہ میں) ڈال دیا۔ میرے نفس نے مجھے یہ بات سجھائی (اور میرے لئے آراستہ کر دی)۔'),
(2445, 20, 97, 'موسیٰ نے کہا جا چلا جا! تیرے لئے اس زندگی میں یہ (سزا) ہے کہ تو کہتا رہے گا کہ مجھے کوئی نہ چھوئے (کہ میں اچھوت ہوں) اور تیرے لئے (آخرت میں عذاب) کا ایک وعدہ ہے جو تجھ سے ٹلنے والا نہیں ہے۔ اور اب دیکھ اپنے اس معبود کو جس کی پرستش پر تو جما بیٹھا رہا۔ ہم (پہلے) اسے جلائیں گے اور پھر اس کی راکھ کو اڑا کر سمندر میں بہائیں گے۔'),
(2446, 20, 98, 'اے لوگو! تمہارا الٰہ تو بس اللہ ہی ہے جس کے سوا کوئی الہ نہیں ہے۔ اور وہ ہر چیز کا علمی احاطہ کئے ہوئے ہے۔'),
(2447, 20, 99, '(اے رسول) ہم اسی طرح گزرے ہوئے واقعات کی کچھ خبریں آپ سے بیان کرتے ہیں اور ہم نے اپنی طرف سے آپ کو ایک نصیحت نامہ (قرآن) عطا کیا ہے۔'),
(2448, 20, 100, 'جو کوئی اس سے روگردانی کرے گا تو وہ قیامت کے دن (اپنے اس جرم کا) بوجھ خود اٹھائے گا۔'),
(2449, 20, 101, 'ایسے لوگ ہمیشہ اسی حالت میں گرفتار رہیں گے اور قیامت کے دن یہ بوجھ بڑا برا بوجھ ہوگا۔'),
(2450, 20, 102, 'جس دن صور پھونکا جائے گا تو ہم مجرموں کو اس طرح محشور کریں گے کہ ان کی آنکھیں نیلی ہوں گی۔'),
(2451, 20, 103, 'وہ آپس میں چپکے چپکے کہیں گے کہ تم (دنیا و برزخ میں) کوئی دس دن ہی رہے ہوگے۔'),
(2452, 20, 104, 'ہم خوب جانتے ہیں جو وہ کہہ رہے ہوں گے جبکہ ان کا سب سے زیادہ صائب الرائے یہ کہتا ہوگا کہ تم تو بس ایک دن رہے ہو۔'),
(2453, 20, 105, '(اے رسول(ص)) لوگ آپ سے پہاڑوں کے بارے میں پوچھتے ہیں کہ (قیامت کے دن کہاں جائیں گے)؟ تو آپ کہہ دیجئے! کہ میرا پروردگار ان کو (ریزہ ریزہ کرکے) اڑا دے گا۔'),
(2454, 20, 106, 'پھر ان کی جگہ زمین کو اس طرح چٹیل میدان بنا دے گا۔'),
(2455, 20, 107, 'کہ تم اس میں نہ کوئی ناہمواری دیکھوگے اور نہ بلندی۔'),
(2456, 20, 108, 'اس روز لوگ ایک پکارنے والے (اسرافیل) کے پیچھے اس طرح سیدھے آئیں گے جس میں کوئی کجی نہ ہوگی اور خدا کے سامنے اور سب آوازیں دب جائیں گی پس تم قدموں کی آہٹ کے سوا کچھ نہیں سنوگے۔'),
(2457, 20, 109, 'اس دن کوئی شفاعت فائدہ نہیں دے گی سوائے اس کے جس کو خدا اجازت دے گا اور اس کے بولنے کو پسند کرے گا۔'),
(2458, 20, 110, 'جو کچھ لوگوں کے آگے ہے (آنے والے حالات) اور جو کچھ ان کے پیچھے ہے (گزرے ہوئے واقعات) وہ سب کچھ جانتا ہے مگر لوگ اپنے علم سے اس کا احاطہ نہیں کر سکتے۔'),
(2459, 20, 111, 'سب کے چہرے حی و قیوم کے سامنے جھکے ہوئے ہوں گے اور جو شخص ظلم کا بوجھ اٹھائے گا وہ ناکام و نامراد ہو جائے گا۔'),
(2460, 20, 112, 'اور جو کوئی نیک کام کرے درآنحالیکہ وہ مؤمن بھی ہو تو اسے نہ ظلم و زیادتی کا اندیشہ ہوگا اور نہ کمی و حق تلفی کا۔'),
(2461, 20, 113, 'اور اس طرح ہم نے اس (کتاب) کو عربی زبان میں قرآن بنا کر نازل کیا ہے اس میں وعید و تہدید بیان کی ہے تاکہ وہ پرہیزگار بن جائیں یا وہ ان میں نصیحت پذیری پیدا کر دے۔'),
(2462, 20, 114, 'بلند و برتر ہے اللہ جو حقیقی بادشاہ ہے اور (اے پیغمبر(ص)) جب تک قرآن کی وحی آپ پر پوری نہ ہو جائے اس (کے پڑھنے) میں جلدی نہ کیا کیجئے۔ اور دعا کیجئے کہ (اے پروردگار) میرے علم میں اور اضافہ فرما۔'),
(2463, 20, 115, 'اور ہم نے اس سے پہلے آدم سے عہد لیا تھا مگر وہ بھول گئے اور ہم نے ان میں عزم و ثبات نہ پایا۔'),
(2464, 20, 116, 'اور (وہ وقت یاد کرو) جب ہم نے فرشتوں سے کہا کہ آدم (ع) کے سامنے سجدہ میں گر جاؤ۔ (چنانچہ) ابلیس کے سوا سب سجدے میں گر گئے۔'),
(2465, 20, 117, 'سو ہم نے کہا اے آدم (ع)! یہ آپ کا اور آپ کی زوجہ کا دشمن ہے یہ کہیں آپ دونوں کو جنت سے نکلوا نہ دے؟ ورنہ مشقت میں پڑ جائیں گے۔'),
(2466, 20, 118, 'بیشک تم اس میں نہ کبھی بھوکے رہوگے اور نہ ننگے۔'),
(2467, 20, 119, 'اور نہ یہاں پیاسے رہوگے اور نہ دھوپ کھاؤگے۔'),
(2468, 20, 120, 'پھر شیطان نے ان کے دل میں وسوسہ ڈالا (اور) کہا اے آدم! کیا میں تمہیں بتاؤں ہمیشگی والا درخت اور نہ زائل ہونے والی سلطنت؟'),
(2469, 20, 121, 'پس ان دونوں نے اس (درخت) میں سے کچھ کھایا۔ تو ان پر ان کے قابل ستر مقامات ظاہر ہوگئے اور وہ اپنے اوپر جنت کے پتے چپکانے لگے اور آدم نے اپنے پروردگار (کے امر ارشادی) کی خلاف ورزی کی اور (اپنے مقصد میں) ناکام ہوئے۔'),
(2470, 20, 122, 'اس کے بعد ان کے پروردگار نے انہیں برگزیدہ کیا (چنانچہ) ان کی توبہ قبول کی اور ہدایت بخشی۔'),
(2471, 20, 123, 'فرمایا (اب) تم زمین پر اتر جاؤ ایک دوسرے کے دشمن ہوکر پھر اگر تمہارے پاس میری طرف سے کوئی ہدایت پہنچے تو جو کوئی میری ہدایت کی پیروی کرے گا وہ نہ گمراہ ہوگا اور نہ بدبخت ہوگا۔'),
(2472, 20, 124, 'اور جو کوئی میری یاد سے روگردانی کرے گا تو اس کے لئے تنگ زندگی ہوگی۔ اور ہم اسے قیامت کے دن اندھا محشور کریں گے۔'),
(2473, 20, 125, 'وہ کہے گا اے میرے پروردگار! تو نے مجھے اندھا کیوں محشور کیا ہے حالانکہ میں آنکھوں والا تھا؟'),
(2474, 20, 126, 'ارشاد ہوگا اسی طرح ہماری آیات تیرے پاس آئی تھیں اور تو نے انہیں بھلا دیا تھا اسی طرح آج تجھے بھی بھلا دیا جائے گا اور نظر انداز کر دیا جائے گا۔'),
(2475, 20, 127, 'اور جو کوئی حد سے تجاوز کرے اور اپنے پروردگار کی بات پر ایمان نہ لائے تو ہم اسے اسی طرح (دنیا میں) سزا دیتے ہیں اور آخرت کا عذاب تو اور بھی بڑا سخت اور پائیدار ہے۔'),
(2476, 20, 128, 'کیا (اس بات سے بھی) انہیں ہدایت نہ ملی کہ ہم نے ان سے پہلے کتنی نسلیں (ان گناہوں کی پاداش میں) ہلاک کر دیں جن کے مکانوں میں (آج) یہ چلتے پھرتے ہیں بےشک اس میں صاحبان عقل کیلئے خدا کی قدرت کی بڑی نشانیاں ہیں۔'),
(2477, 20, 129, 'اور (اے رسول(ص)) اگر آپ کے پروردگار کی طرف سے ایک بات طے نہ کر دی گئی ہوتی اور ایک (مہلت کی) مدت معین نہ ہو چکی ہوتی تو (عذاب) لازمی طور پر آچکا ہوتا۔'),
(2478, 20, 130, 'سو آپ ان کی باتوں پر صبر کیجئے اور طلوع آفتاب سے پہلے اور غروب آفتاب سے پہلے اور رات کے اوقات میں بھی اور دن کے اول و آخر میں بھی اپنے پروردگار کی حمد و ثناء کے ساتھ تسبیح کیجئے تاکہ آپ راضی ہو جائیں۔'),
(2479, 20, 131, 'اور جو کچھ ہم نے مختلف لوگوں کو آزمائش کیلئے دنیا کی زیب و زینت اور آرائش دے رکھی ہے اس کی طرف نگاہیں اٹھا کر بھی نہ دیکھیں اور آپ کے پروردگار کا دیا ہوا رزق بہتر ہے اور زیادہ پائیدار ہے۔'),
(2480, 20, 132, 'اور اپنے گھر والوں کو نماز کا حکم دیں اور خود بھی اس پر قائم و برقرار رہیں ہم آپ سے روزی طلب نہیں کرتے۔ ہم تو خود آپ کو روزی دیتے ہیں اور انجام بخیر تو پرہیزگاری کا ہی ہے۔'),
(2481, 20, 133, 'اور یہ لوگ (اہل مکہ) کہتے ہیں کہ یہ (رسول) ہمارے پاس اپنے پروردگار کی طرف سے کوئی نشانی (معجزہ) کیوں نہیں لاتے۔ کیا ان کے پاس اگلی کتابوں کا کھلا ہوا ثبوت نہیں آیا؟'),
(2482, 20, 134, 'اور اگر ہم اس (رسول) سے پہلے انہیں عذاب سے ہلاک کر دیتے تو یہ کہتے اے ہمارے پروردگار! تو نے ہماری طرف کوئی رسول کیوں نہ بھیجا؟ کہ ہم ذلیل و رسوا ہونے سے پہلے تیری آیتوں کی پیروی کرتے۔'),
(2483, 20, 135, 'آپ کہہ دیجئے! کہ ہر ایک اپنے (انجام کا) انتظار کر رہا ہے سو تم بھی انتظار کرو۔ عنقریب تمہیں معلوم ہو جائے گا کہ سیدھی راہ والے کون ہیں؟ اور ہدایت یافتہ کون ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'لوگوں کے حساب کتاب (کا وقت) قریب آگیا ہے اور وہ غفلت میں پڑے منہ پھیرے ہوئے ہیں۔'),
(2485, 21, 2, 'ان کے پروردگار کی طرف سے کوئی نئی یاددہانی ان کے پاس آتی ہے۔ تو وہ اسے کھیل کود میں لگے ہوئے سنتے ہیں۔'),
(2486, 21, 3, 'ان کے دل بالکل غافل ہیں اور یہ ظالم چپکے چپکے سرگوشیاں کرتے ہیں کہ یہ شخص (رسول) اس کے سوا کیا ہے؟ تمہاری ہی طرح کا ایک بشر ہے کیا تم آنکھیں دیکھتے ہوئے (اور سوجھ بوجھ رکھتے ہوئے) جادو (کی بات) سنتے جاؤگے؟'),
(2487, 21, 4, '(پیغمبر اسلام(ص)) نے کہا کہ میرا پروردگار آسمان اور زمین کی ہر بات کو جانتا ہے (کیونکہ) وہ بڑا سننے والا (اور) بڑا جاننے والا ہے۔'),
(2488, 21, 5, 'بلکہ یہ لوگ کہتے ہیں کہ یہ (قرآن) خواب ہائے پریشان ہیں (نہیں) بلکہ یہ اس نے خود گھڑ لیا ہے۔ بلکہ وہ تو ایک شاعر ہے (اور اگر ایسا نہیں ہے) تو پھر ہمارے پاس کوئی معجزہ لائے جس طرح پہلے رسول (معجزات کے ساتھ) بھیجے گئے تھے۔'),
(2489, 21, 6, 'ان سے پہلے کوئی بستی بھی جسے ہم نے ہلاک کیا وہ تو ایمان لائی نہیں تو کیا یہ ایمان لائیں گے؟'),
(2490, 21, 7, '(اے رسول) اور ہم نے آپ سے پہلے بھی مردوں کو ہی رسول بنا کر بھیجا تھا جن کی طرف وحی کیا کرتے تھے۔ اگر تم نہیں جانتے تو اہل ذکر سے پوچھ لو۔'),
(2491, 21, 8, 'اور ہم نے ان رسولوں کو کبھی ایسے جسم کا نہیں بنایا کہ وہ کھانا نہ کھاتے ہوں۔ اور نہ وہ ہمیشہ زندہ رہنے والے تھے۔'),
(2492, 21, 9, 'پھر ہم نے ان سے جو وعدہ کیا تھا اسے سچا کر دکھایا اور انہیں اور ان کے ساتھ جن کو چاہا بچالیا اور زیادتی کرنے والوں کو تباہ و برباد کردیا۔'),
(2493, 21, 10, 'بےشک ہم نے تمہاری طرف ایک ایسی کتاب نازل کی ہے جس میں تمہارا ہی ذکر ہے کیا تم عقل سے کام نہیں لیتے؟ (نہیں سمجھتے؟)۔'),
(2494, 21, 11, 'اور کتنی ہی بستیاں ہیں جو ہم نے بریاد کر دیں۔ کیونکہ وہ ظالم تھیں (ان کے باشندے ظالم تھے) اور ان کی جگہ اور قوم کو پیدا کر دیا۔'),
(2495, 21, 12, 'جب انہوں نے ہمارا عذاب محسوس کیا تو ایک دم وہاں سے بھاگنے لگے۔'),
(2496, 21, 13, '(ان سے کہا گیا) بھاگو نہیں (بلکہ) اپنی آسائش کی طرف لوٹو جو تمہیں دی گئی تھی۔ اور اپنے گھروں کی طرف لوٹو۔ تاکہ تم سے پوچھ گچھ کی جائے۔'),
(2497, 21, 14, 'ان لوگوں نے کہا ہائے ہماری بدبختی بےشک ہم ظالم تھے۔'),
(2498, 21, 15, 'وہ برابر یہی پکارتے رہے یہاں تک کہ ہم نے انہیں کٹی ہوئی کھیتی کی طرح (اور) بجھی ہوئی آگ کی طرح کر دیا۔'),
(2499, 21, 16, 'اور ہم نے آسمان اور زمین کو اور جو کچھ ان کے درمیان ہے ان کو کھیل تماشے کے طور پر پیدا نہیں کیا۔'),
(2500, 21, 17, 'اگر ہم کوئی دل بستگی کا سامان چاہتے تو اپنے پاس سے ہی کر لیتے مگر ہم ایسا کرنے والے نہیں ہیں۔'),
(2501, 21, 18, 'بلکہ ہم باطل پر حق کی چوٹ لگاتے ہیں جو اس کا بھیجا نکال دیتی ہے (سر کچل دیتی ہے) پھر یکایک باطل مٹ جاتا ہے۔ افسوس ہے تم پر ان باتوں کی وجہ سے جو تم بناتے ہو۔'),
(2502, 21, 19, 'جو کوئی آسمانوں میں ہے اور جو کوئی زمین ہے سب اسی کیلئے ہے اور جو اس کی بارگاہ میں ہیں وہ اس کی عبادت سے تکبر و سرکشی نہیں کرتے اور نہ ہی تھکتے ہیں۔'),
(2503, 21, 20, 'وہ رات دن اس کی تسبیح کرتے ہیں کبھی ملول ہوکر وقفہ نہیں کرتے۔'),
(2504, 21, 21, 'کیا انہوں نے زمینی (مخلوق) سے ایسے معبود بنائے ہیں جو مردوں کو زندہ کر دیتے ہیں۔'),
(2505, 21, 22, 'اگر زمین و آسمان میں اللہ کے سوا کوئی اور خدا ہوتا تو دونوں کا نظام برباد ہو جاتا پس پاک ہے اللہ جو عرش کا مالک ہے ان باتوں سے جو لوگ بناتے ہیں۔'),
(2506, 21, 23, 'وہ جو کچھ کرتا ہے اس کے بارے میں اس سے بازپرس نہیں کی جا سکتی اور وہ ہر ایک کا حساب لینے والا ہے۔'),
(2507, 21, 24, 'کیا ان لوگوں نے اللہ کے علاوہ اور خدا بنائے ہیں؟ کہئے کہ اپنی دلیل پیش کرو۔ یہ (قرآن) میرے ساتھ والوں کی (کتاب) ہے اور مجھ سے پہلے والوں کی (کتابیں بھی) ہیں (ان سے کوئی بات میرے دعویٰ کے خلاف نکال کر لاؤ) بلکہ اکثر لوگ حق کو نہیں جانتے (اس لئے) اس سے روگردانی کرتے ہیں۔'),
(2508, 21, 25, 'اور ہم نے آپ سے پہلے کوئی ایسا رسول نہیں بھیجا جس کی طرف یہ وحی نہ کی ہو کہ میرے سوا کوئی خدا نہیں ہے۔ پس تم میری ہی عبادت کرو۔'),
(2509, 21, 26, 'اور وہ کہتے ہیں کہ خدائے رحمن نے اولاد بنا رکھی ہے وہ (اس سے) پاک ہے بلکہ وہ فرشتے تو اس کے بندے ہیں۔'),
(2510, 21, 27, 'جو بات کرنے میں بھی اس سے سبقت نہیں کرتے اور اسی کے حکم پر عمل کرتے ہیں۔'),
(2511, 21, 28, 'اللہ جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے ہے اور وہ کسی کی شفاعت نہیں کرتے سوائے اس کے جس سے خدا راضی ہو۔ اور وہ اس کے خوف و خشیہ سے لرزتے رہتے ہیں۔'),
(2512, 21, 29, 'اور جو (بالفرض) ان میں سے یہ کہہ دے کہ اللہ کے علاوہ میں خدا ہوں۔ تو ہم اسے جہنم کی سزا دیں گے ہم ظالموں کو ایسی ہی سزا دیا کرتے ہیں۔'),
(2513, 21, 30, 'کیا کافر اس بات پر غور نہیں کرتے کہ آسمان اور زمین پہلے آپس میں ملے ہوئے تھے پھر ہم نے دونوں کو جدا کیا۔ اور ہم نے (پہلے) ہر زندہ چیز کو پانی سے پیدا کیا ہے۔ کیا یہ لوگ پھر بھی ایمان نہیں لاتے؟'),
(2514, 21, 31, 'اور ہم نے زمین میں پہاڑ قرار دیئے تاکہ وہ لوگوں کو لے کر ڈھلک نہ جائے اور ہم نے ان (پہاڑوں) میں کشادہ راستے بنائے تاکہ وہ راہ پائیں (اور منزل مقصود تک پہنچ جائیں)۔'),
(2515, 21, 32, 'اور ہم نے آسمان کو محفوظ چھت بنایا مگر یہ لوگ پھر بھی اس کی نشانیوں سے منہ پھیرے ہوئے ہیں۔'),
(2516, 21, 33, 'اور وہ وہی ہے جس نے رات اور دن اور سورج اور چاند کو پیدا کیا۔ سب (اپنے اپنے) دائرہ (مدار) میں تیر رہے ہیں۔'),
(2517, 21, 34, 'اور ہم نے آپ سے پہلے کسی آدمی کو ہمیشگی نہیں دی۔ تو اگر آپ انتقال کر جائیں تو کیا یہ لوگ ہمیشہ (یہاں) رہیں گے؟'),
(2518, 21, 35, 'ہر جاندار موت کا مزہ چکھنے والا ہے اور ہم تمہیں برائی اور اچھائی کے ساتھ آزماتے ہیں (آخرکار) تم ہماری ہی طرف لوٹائے جاؤگے۔'),
(2519, 21, 36, 'اور (اے رسول(ص)) جب کافر آپ کو دیکھتے ہیں تو بس وہ آپ کا مذاق اڑاتے ہیں (اور کہتے ہیں) کیا یہی وہ شخص ہے جو (برائی سے) تمہارے خداؤں کا ذکر کرتا ہے؟ حالانکہ وہ خود خدائے رحمن کے ذکر کے منکر ہیں۔'),
(2520, 21, 37, 'انسان جلد بازی سے پیدا ہوا ہے میں عنقریب تمہیں اپنی (قدرت کی) نشانیاں دکھلاؤں گا۔ پس تم مجھ سے جلدی کا مطالبہ نہ کرو۔'),
(2521, 21, 38, 'اور وہ کہتے ہیں کہ اگر آپ سچے ہیں تو یہ (قیامت کا) وعدہ کب پورا ہوگا؟'),
(2522, 21, 39, 'کاش ان کافروں کو اس کا کچھ علم ہوتا۔ جب یہ لوگ آگ کو نہیں روک سکیں گے نہ اپنے چہروں سے اور نہ اپنی پشتوں سے اور نہ ہی ان کی کوئی مدد کی جائے گی۔'),
(2523, 21, 40, 'بلکہ وہ (گھڑی) اچانک ان پر آجائے گی۔ اور انہیں مبہوت کر دے گی پھر نہ تو وہ اسے ہٹا سکیں گے اور نہ ہی انہیں مہلت دی جائے گی۔'),
(2524, 21, 41, 'بےشک ان پیغمبروں کا مذاق اڑایا گیا جو آپ سے پہلے تھے تو اسی (عذاب) نے ان مذاق اڑانے والوں کو گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(2525, 21, 42, '(اے رسول(ص)) کہہ دیجئے! کون ہے جو رات میں یا دن میں خدائے رحمن (کے عذاب) سے تمہاری حفاظت کر سکتا ہے؟ بلکہ یہ لوگ اپنے پروردگار کے ذکر سے منہ موڑے ہوئے ہیں۔'),
(2526, 21, 43, 'کیا ہمارے علاوہ ان کے ایسے خدا ہیں جو ان کی حفاظت کر سکیں؟ وہ (خود ساختہ) خدا تو خود اپنی مدد نہیں کر سکتے اور نہ ہی ہماری طرف سے ان کو پناہ دی جائے گی (اور نہ تائید کی جائے گی)۔'),
(2527, 21, 44, 'بلکہ ہم نے انہیں اور ان کے آباء و اجداد کو (زندگی کا) سر و سامان دیا یہاں تک کہ ان کی لمبی لمبی عمریں گزر گئیں (عرصہ دراز گزر گیا) کیا وہ نہیں دیکھتے کہ ہم زمین کو اس کے اطراف سے برابر گھٹاتے چلے آرہے ہیں تو کیا وہ غالب آسکتے ہیں؟'),
(2528, 21, 45, 'آپ کہہ دیجئے کہ میں تو تمہیں صرف وحی کی بناء پر (عذاب سے) ڈراتا ہوں مگر جو بہرے ہوتے ہیں وہ دعا و پکار نہیں سنتے جب انہیں ڈرایا جائے۔'),
(2529, 21, 46, 'اور جب انہیں تمہارے پروردگار کے عذاب کا ایک جھونکا بھی چھو جائے تو وہ کہہ اٹھیں گے کہ ہائے افسوس بےشک ہم ظالم تھے۔'),
(2530, 21, 47, 'ہم قیامت کے دن صحیح تولنے والے میزان (ترازو) قائم کر دیں گے۔ پس کسی شخص پر ذرا بھی ظلم نہیں کیا جائے گا اور اگر کوئی (عمل) رائی کے دانہ کے برابر بھی ہوگا تو ہم اسے (وزن میں) لے آئیں گے اور حساب لینے والے ہم ہی کافی ہیں۔'),
(2531, 21, 48, 'بےشک ہم نے موسیٰ و ہارون کو فرقان، روشنی اور پرہیزگاروں کیلئے نصیحت نامہ عطا کیا۔'),
(2532, 21, 49, 'جو بے دیکھے اپنے پروردگار سے ڈرتے ہیں نیز جو قیامت سے بھی خوف زدہ رہتے ہیں۔'),
(2533, 21, 50, 'اور یہ (قرآن) بابرکت نصیحت نامہ ہے جسے ہم نے نازل کیا ہے۔ کیا تم اس کا انکار کرتے ہو؟'),
(2534, 21, 51, 'اور یقیناً ہم نے اس (عہد موسوی) سے پہلے ابراہیم (ع) کو (ان کے مقام کے مطابق) سوجھ بوجھ عطا فرمائی تھی اور ہم ان کو خوب جانتے تھے۔'),
(2535, 21, 52, 'جب آپ نے (منہ بولے) باپ اور اپنی قوم سے کہا کہ یہ مورتیں کیسی ہیں جن (کی عبادت) پر تم جمے بیٹھے ہو۔'),
(2536, 21, 53, 'انہوں نے کہا کہ ہم نے اپنے باپ دادا کو ان کی عبادت کرتے ہوئے پایا ہے۔'),
(2537, 21, 54, 'آپ نے کہا تم بھی اور تمہارے باپ دادا بھی کھلی ہوئی گمراہی میں تھے۔'),
(2538, 21, 55, 'انہوں نے کہا کیا تم ہمارے پاس کوئی حق بات (سچا پیغام) لے کر آئے ہو یا صرف دل لگی کر رہے ہو؟'),
(2539, 21, 56, 'آپ نے کہا (دل لگی نہیں) بلکہ تمہارا پروردگار وہ ہے جو آسمانوں اور زمین کا پروردگار ہے جس نے انہیں پیدا کیا ہے اور میں اس بات کے گواہوں میں سے ایک گواہ ہوں۔'),
(2540, 21, 57, 'اور بخدا میں تمہارے بتوں کے ساتھ (کوئی نہ کوئی) چال ضرور چلوں گا جب تم پیٹھ پھیر کر چلے جاؤ گے۔'),
(2541, 21, 58, 'چنانچہ آپ نے ان کے ایک بڑے بت کے سوا باقی سب کے ٹکڑے ٹکڑے کر دیئے تاکہ وہ اس کی طرف رجوع کریں۔'),
(2542, 21, 59, 'انہوں نے کہا ہمارے خداؤں کے ساتھ کس نے یہ سلوک کیا ہے؟ بےشک وہ ظالموں میں سے ہے۔'),
(2543, 21, 60, '(بعض) لوگ کہنے لگے کہ ہم نے ایک نوجوان کو سنا ہے جو ان کا ذکر (برائی سے) کیا کرتا ہے جسے ابراہیم کہا جاتا ہے۔'),
(2544, 21, 61, 'انہوں نے کہا تو پھر اسے (پکڑ کر) سب لوگوں کے سامنے لاؤ تاکہ وہ گواہی دیں۔'),
(2545, 21, 62, '(چنانچہ آپ لائے گئے) ان لوگوں نے کہا اے ابراہیم (ع)! کیا تم نے ہمارے خداؤں کے ساتھ یہ سلوک کیا ہے؟'),
(2546, 21, 63, 'ابراہیم (ع)نے کہا: بلکہ ان کے بڑے بت نے یہ سب کاروائی کی ہے ان بتوں سے ہی پوچھ لو اگر وہ بول سکتے ہیں؟'),
(2547, 21, 64, 'وہ لوگ (ابراہیم کا یہ جواب سن کر) اپنے دلوں میں سوچنے لگے اور آپس میں کہنے لگے واقعی تم خود ہی ظالم ہو۔'),
(2548, 21, 65, 'پھر انہوں نے (خجالت سے) سر جھکا کر کہا کہ تم جانتے ہو کہ یہ (بت) بات نہیں کرتے۔'),
(2549, 21, 66, 'آپ نے کہا تو پھر تم اللہ کو چھوڑ کر ایسی چیزوں کی عبادت کرتے ہو جو نہ تمہیں فائدہ پہنچا سکتی ہیں اور نہ نقصان پہنچا سکتی ہیں۔'),
(2550, 21, 67, 'تف ہے تم پر اور ان (بتوں) پر جن کی تم اللہ کو چھوڑ کر پرستش کرتے ہو۔ کیا تم عقل سے کام نہیں لیتے؟'),
(2551, 21, 68, 'ان لوگوں نے کہا اگر کچھ کرنا چاہتے ہو تو اسے آگ میں جلا دو۔ اور اپنے خداؤں کی مدد کرو۔'),
(2552, 21, 69, '(چنانچہ انہوں نے آپ کو آگ میں ڈال دیا تو) ہم نے کہا اے آگ! ٹھنڈی ہوکر اور ابراہیم کے لئے سلامتی کا باعث بن جا۔'),
(2553, 21, 70, 'ان لوگوں نے ابراہیم کے ساتھ چالبازی کرنا چاہی تھی مگر ہم نے انہیں ناکام کر دیا۔'),
(2554, 21, 71, 'اور ہم نے انہیں اور لوط کو نجات دی اور اس سر زمین (شام) کی طرف لے گئے جسے ہم نے دنیا جہان والوں کیلئے بابرکت بنایا ہے۔'),
(2555, 21, 72, 'اور ہم نے اسحاق (جیسا بیٹا) اور مزید یعقوب (جیسا پوتا) عطا فرمایا اور سب کو صالح بنایا۔'),
(2556, 21, 73, 'اور ہم نے انہیں ایسا امام (پیشوا) بنایا جو ہمارے حکم سے (لوگوں کو) ہدایت کرتے تھے اور ہم نے انہیں نیک کاموں کے کرنے، نماز پڑھنے اور زکوٰۃ دینے کی وحی کی اور وہ ہمارے عبادت گزار تھے۔'),
(2557, 21, 74, 'اور ہم نے لوط (ع) کو حکمت اور علم عطا کیا۔ اور انہیں اس بستی سے نجات دی جس کے باشندے گندے کام کیا کرتے تھے واقعی وہ قوم بڑی بری اور نافرمان تھی۔'),
(2558, 21, 75, 'اور ہم نے اس (لوط(ع)) کو اپنی رحمت میں داخل کیا۔ بےشک وہ بڑے نیکوکاروں میں سے تھے۔'),
(2559, 21, 76, 'اور نوح(ع) (کا ذکر کیجئے) جب انہوں نے (ان سب سے) پہلے پکارا اور ہم نے ان کی دعا و پکار قبول کی اور انہیں اور ان کے اہل کو سخت غم و کرب سے نجات دی۔'),
(2560, 21, 77, 'اور ان لوگوں کے مقابلہ میں جو ہماری آیتوں کو جھٹلاتے تھے ان کی مدد کی بےشک وہ بڑے برے لوگ تھے۔ پس ہم نے ان سب کو غرق کر دیا۔'),
(2561, 21, 78, 'اور داؤد (ع) اور سلیمان (ع) (کا تذکرہ کیجئے) جب وہ کھیت کے بارے میں فیصلہ کر رہے تھے جب ایک گروہ کی بکریاں رات کے وقت اس میں گھس گئی تھیں اور ہم ان کے فیصلہ کا مشاہدہ کر رہے تھے۔'),
(2562, 21, 79, 'اور ہم نے اس کا فیصلہ سلیمان کو سمجھا دیا تھا اور ہم نے ہر ایک کو حکمت اور علم عطا کیا تھا اور ہم نے داؤد کے ساتھ پہاڑوں اور پرندوں کو مسخر کر دیا تھا جو ان کے ساتھ تسبیح کیا کرتے تھے اور (یہ کام) کرنے والے ہم ہی تھے۔'),
(2563, 21, 80, 'اور ہم نے انہیں تمہارے فائدہ کیلئے زرہ بنانے کی صنعت سکھائی تھی تاکہ وہ تمہیں تمہاری لڑائی میں ایک دوسرے کی زد سے بچائے۔ کیا تم اس (احسان) کے شکرگزار ہو؟'),
(2564, 21, 81, 'اور ہم نے تیز و تند ہوا کو سلیمان کیلئے مسخر کر دیا تھا جو ان کے حکم سے اس سرزمین کی طرف چلتی تھی جس میں ہم نے برکت قرار دی ہے اور ہم ہر چیز کا علم رکھنے والے تھے۔'),
(2565, 21, 82, 'اور ہم نے کچھ شیطانوں (جنات) کو ان کا تابع بنا دیا تھا جو ان کے لئے غوطہ زنی کرتے تھے۔ اور اس کے علاوہ اور کام بھی کرتے تھے اور ہم ہی ان کے نگہبان تھے۔'),
(2566, 21, 83, 'اور ایوب (کا ذکر کیجئے) جب انہوں نے اپنے پروردگار کو پکارا مجھے (بیماری کی) تکلیف پہنچ رہی ہے اور تو ارحم الراحمین ہے (میرے حال پر رحم فرما)۔'),
(2567, 21, 84, 'ہم نے ان کی دعا قبول کی اور ان کو جو تکلیف تھی وہ دور کر دی اور اپنی خاص رحمت سے ہم نے ان کو ان کے اہل و عیال عطا کئے اور ان کے برابر اور بھی اور اس لئے کہ یہ عبادت گزاروں کیلئے یادگار ہے۔'),
(2568, 21, 85, 'اور اسماعیل (ع)، ادریس (ع) اور ذوالکفل (کا تذکرہ کیجئے) یہ سب (راہ حق میں) صبر کرنے والوں میں سے تھے۔'),
(2569, 21, 86, 'اور ہم نے ان سب کو اپنی (خاص) رحمت میں داخل کر لیا تھا۔ یقیناً وہ نیکوکار بندوں میں سے تھے۔'),
(2570, 21, 87, 'اور ذوالنون (مچھلی والے) کا (ذکر کیجئے) جب وہ خشمناک ہوکر چلے گئے اور وہ سمجھے کہ ہم ان پر تنگی نہیں کریں گے۔ پھر انہوں نے اندھیروں میں سے پکارا۔ تیرے سوا کوئی الہ نہیں ہے۔ پاک ہے تیری ذات بےشک میں زیاں کاروں میں سے ہوں۔'),
(2571, 21, 88, 'ہم نے ان کی دعا قبول کی اور انہیں غم سے نجات دی اور ہم اسی طرح ایمان والوں کو نجات دیا کرتے ہیں۔'),
(2572, 21, 89, 'اور زکریا (ع) کا (ذکر کیجئے) جب انہوں نے پکارا اے میرے پروردگار! مجھے (وارث کے بغیر) اکیلا نہ چھوڑ۔ جبکہ تو خود بہترین وارث ہے۔'),
(2573, 21, 90, 'ہم نے ان کی دعا قبول کی اور انہیں یحییٰ (جیسا بیٹا) عطا کیا اور ان کی بیوی کو ان کیلئے تندرست کر دیا۔ یہ لوگ نیک کاموں میں جلدی کرتے تھے اور ہم کو شوق و خوف (اور امید و بیم) کے ساتھ پکارتے تھے اور وہ ہمارے لئے (عجز و نیاز سے) جھکے ہوئے تھے۔'),
(2574, 21, 91, 'اور اس خاتون (کا ذکر کیجئے) جس نے اپنے ناموس کی حفاظت کی تو ہم نے اس میں اپنی (خاص) روح پھونک دی۔ اور انہیں اور ان کے بیٹے (عیسیٰ) کو دنیا جہان والوں کیلئے (اپنی قدرت کی) نشانی بنا دیا۔'),
(2575, 21, 92, '(اے ایمان والو) یہ تمہاری (ملت) ہے جو درحقیقت ملت واحدہ ہے اور میں تمہارا پروردگار ہوں۔ بس تم میری ہی عبادت کرو۔'),
(2576, 21, 93, 'لیکن لوگوں نے اپنے (دینی) معاملہ کو ٹکڑے ٹکڑے کر ڈالا (انجام کار) سب ہماری ہی طرف لوٹ کر آنے والے ہیں۔'),
(2577, 21, 94, 'پس جو کوئی نیک کام کرے درآنحالیکہ وہ مؤمن ہو تو اس کی کوشش کی ناقدری نہ ہوگی اور ہم اسے لکھ رہے ہیں۔'),
(2578, 21, 95, 'اور جس بستی کو ہم نے ہلاک کر دیا اس کیلئے حرام ہے۔ یعنی وہ (دنیا میں) دوبارہ لوٹ کر نہیں آئیں گے۔'),
(2579, 21, 96, 'یہاں تک کہ جب یاجوج و ماجوج کھول دیئے جائیں گے تو وہ ہر بلندی سے دوڑتے ہوئے نکل پڑیں گے۔'),
(2580, 21, 97, 'اور (جب) اللہ کا سچا وعدہ (قیامت کا) قریب آجائے گا تو ایک دم کافروں کی آنکھیں پھٹی رہ جائیں گی (اور کہیں گے) ہائے افسوس! ہم اس سے غفلت میں رہے بلکہ ہم ظالم تھے۔'),
(2581, 21, 98, 'بےشک تم اور وہ چیزیں جن کی تم اللہ کو چھوڑ کر عبادت کرتے تھے جہنم کا ایندھن ہیں اس میں تم سب کو داخل ہونا ہے۔'),
(2582, 21, 99, 'اگر یہ چیزیں برحق خدا ہوتیں تو جہنم میں نہ جاتیں اب تم سب کو اس میں ہمیشہ رہنا ہے۔'),
(2583, 21, 100, 'ان کی اس میں چیخ و پکار ہوگی اور وہ اس میں کچھ نہیں سنیں گے۔'),
(2584, 21, 101, 'ہاں البتہ وہ لوگ جن کیلئے ہماری طرف سے پہلے بھلائی مقدر ہو چکی ہوگی وہ اس سے دور رہیں گے۔'),
(2585, 21, 102, 'وہ اس کی آہٹ بھی نہیں سنیں گے اور وہ اپنی من پسند نعمتوں میں ہمیشہ رہیں گے۔'),
(2586, 21, 103, 'ان کو بڑی گھبراہٹ بھی غمزدہ نہیں کر سکے گی اور فرشتے ان کا استقبال کریں گے (اور بتائیں گے کہ) یہ ہے آپ کا وہ دن جس کا تم سے وعدہ کیا جاتا تھا۔'),
(2587, 21, 104, 'جس دن ہم آسمان کو اس طرح لپیٹ دیں گے جس طرح طومار میں خطوط لپیٹے جاتے ہیں جس طرح ہم نے پہلے تخلیق کی ابتداء کی تھی اسی طرح ہم اس کا اعادہ کریں گے۔ یہ ہمارے ذمہ وعدہ ہے یقیناً ہم اسے (پورا) کرکے رہیں گے۔'),
(2588, 21, 105, 'اور ہم نے ذکر (توراۃ یا پند و نصیحت) کے بعد زبور میں لکھ دیا تھا کہ زمین کے وارث میرے نیک بندے ہوں گے۔'),
(2589, 21, 106, 'بےشک اس میں عبادت گزار لوگوں کیلئے بڑا انعام ہے۔'),
(2590, 21, 107, '(اے رسول) ہم نے آپ کو تمام عالمین کیلئے رحمت بنا کر بھیجا ہے۔'),
(2591, 21, 108, 'آپ کہہ دیجئے! کہ میری طرف جو کچھ وحی کی گئی ہے وہ یہ ہے کہ تمہارا الٰہ بس ایک الہ ہے۔ تو کیا تم اس کے آگے سر جھکاتے ہو؟'),
(2592, 21, 109, 'پس اگر وہ اس سے روگردانی کریں تو آپ کہہ دیجئے! کہ میں نے برابر آپ کو خبردار کر دیا ہے اب مجھے معلوم نہیں ہے کہ وہ دن جس کا تم سے وعدہ کیا گیا ہے وہ قریب ہے یا دور؟'),
(2593, 21, 110, 'بےشک وہ (اللہ) بلند آواز سے کہی گئی بات کو بھی جانتا ہے اور اسے بھی جسے تم چھپاتے ہو۔'),
(2594, 21, 111, 'اور میں کیا جانوں؟ کہ یہ تاخیر) تمہارے لئے آزمائش ہے یا ایک خاص وقت تک زندگی کا لطف اٹھانے کی مہلت ہے۔'),
(2595, 21, 112, '(آخرکار رسول(ص) نے) کہا اے میرے پروردگار! تو حق کے ساتھ فیصلہ کر دے۔ (اے لوگو) ہمارا پروردگار وہی خدائے رحمن ہے جس سے ان باتوں کے خلاف مدد مانگی جاتی ہے جو تم بنا رہے ہو۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اے لوگو! اپنے پروردگار (کی ناراضی) سے ڈرو۔ بیشک قیامت کا زلزلہ بہت بڑی شے ہے۔'),
(2597, 22, 2, 'جس دن تم اسے دیکھوگے کہ ہر دودھ پلانے والی (ماں) اس (بچہ) سے غافل ہو جائے گی جسے وہ دودھ پلاتی ہے اور ہر حاملہ عورت اپنا حمل گرا دے گی۔ اور تم لوگوں کو نشہ میں مدہوش دیکھوگے۔ حالانکہ وہ نشہ میں مدہوش نہیں ہوں گے۔ مگر اللہ کا عذاب بڑا سخت ہوگا۔'),
(2598, 22, 3, 'اور لوگوں میں سے کچھ ایسے بھی ہیں کہ جو اللہ کے بارے میں علم کے بغیر کج بحثی کرتے ہیں اور سرکش شیطان کی پیروی کرتے ہیں۔'),
(2599, 22, 4, 'جس (مردود) کے بارے میں لکھا جا چکا ہے کہ جو کوئی اس کو دوست بنائے گا وہ ضرور اسے گمراہ کرے گا۔ اور اسے بھڑکتی ہوئی آگ کے عذاب کا راستہ دکھائے گا۔'),
(2600, 22, 5, 'اے لوگو! اگر تمہیں (دوبارہ) اٹھائے جانے میں شک ہے تو (اس میں تو کوئی شک نہیں کہ) ہم نے تمہیں مٹي سے پیدا کیا ہے پھر نطفہ سے، پھر جمے ہوئے خون سے، پھر گوشت کے لوتھڑے سے جو متشکل (مکمل تخلیق والا) بھی ہوتا ہے اور غیر متشکل (نامکمل تخلیق والا) بھی۔ اور یہ اس لئے ہے کہ ہم تم پر (اپنی قدرت کی کار فرمائی) واضح کریں اور ہم جس (نطفے) کو چاہتے ہیں ایک مقررہ مدت تک رحموں میں ٹھہرائے رکھتے ہیں پھر تمہیں بچے کی صورت میں باہر نکالتے ہیں پھر (تمہاری پرورش کرتے ہیں) تاکہ اپنی پوری قوت کی منزل (جوانی) تک پہنچو۔ پھر تم میں سے بعض کو تو (بڑھاپے سے پہلے) اٹھا لیا جاتا ہے اور بعض کو پست ترین عمر کی طرف لوٹا دیا جاتا ہے تاکہ جاننے کے بعد کچھ نہ جانے اور تم دیکھتے ہو کہ زمین خشک پڑی ہے تو جب ہم اس پر پانی برساتے ہیں تو وہ لہلہانے اور ابھرنے لگتی ہے اور ہر قسم کی خوشنما نباتات اگاتی ہے۔'),
(2601, 22, 6, 'یہ (سب کچھ) اس لئے ہے کہ اللہ ہی کی ذات حق ہے۔ اور بےشک وہی مردوں کو زندہ کرتا ہے اور وہ ہر چیز پر قادر ہے۔'),
(2602, 22, 7, 'اور قیامت ضرور آنے والی ہے۔ جس میں کوئی شک نہیں ہے اور یقیناً اللہ انہیں زندہ کرے گا جو قبروں میں ہیں۔'),
(2603, 22, 8, 'اور لوگوں میں سے کچھ ایسے بھی ہیں جو اللہ کے بارے میں بغیر کسی علم کے، بغیر کسی راہنمائی کے اور بغیر کسی روشن کتاب کے کج بحثی کرتے ہیں۔'),
(2604, 22, 9, '(غرور سے) اپنے شانے کو موڑے ہوئے (اور گردن اکڑائے ہوئے) تاکہ (لوگوں کو) اللہ کی راہ سے گمراہ کریں ان کیلئے دنیا میں رسوائی ہے اور قیامت کے دن ہم انہیں جلانے والی آگ کے عذاب کا مزہ چکھائیں گے۔'),
(2605, 22, 10, 'تب اسے بتایا جائے گا کہ یہ سب کچھ سزا ہے اس کی جو تیرے دونوں ہاتھوں نے آگے بھیجا ہے اور خدا ہرگز اپنے بندوں پر ظلم کرنے والا نہیں ہے۔'),
(2606, 22, 11, 'اور لوگوں میں کوئی ایسا بھی ہوتا ہے جو کنارہ پر رہ کر خدا کی عبادت کرتا ہے۔ سو اگر اسے کوئی فائدہ پہنچ جائے تو وہ اس سے مطمئن ہو جاتا ہے اور اگر اسے کوئی آزمائش پیش آجائے (یا کوئی مصیبت پہنچ جائے) تو فوراً (اسلام سے) منہ موڑ لیتا ہے۔ اس نے دنیا و آخرت کا گھاٹا اٹھایا اور یہ کھلا ہوا خسارہ ہے۔'),
(2607, 22, 12, 'وہ اللہ کو چھوڑ کر اس کو پکارتا ہے جو نہ اسے نقصان پہنچاتا ہے اور نہ نفع۔ یہی تو انتہائی گمراہی ہے۔'),
(2608, 22, 13, 'وہ ایسے کو پکارتا ہے جس کا ضرر اس کے فائدہ سے زیادہ قریب ہے۔ کیا ہی برا ہے آقا اور کیا ہی برا ہے ساتھی۔'),
(2609, 22, 14, 'بےشک اللہ ان لوگوں کو جو ایمان لائے اور نیک عمل بھی کئے ان بہشتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی۔ بیشک اللہ وہ کرتا ہے جو کچھ وہ چاہتا ہے۔'),
(2610, 22, 15, 'جو شخص یہ خیال کرتا ہے کہ خدا دنیا اور آخرت میں اس کی مدد نہیں کرے گا تو اسے چاہیے کہ ایک رسی کے ذریعہ آسمان تک پہنچ کر شگاف لگائے۔ پھر دیکھے کہ آیا اس کی یہ تدبیر اس چیز کو دور کر سکتی ہے جو اسے غصہ میں لا رہی تھی۔'),
(2611, 22, 16, 'اور اسی طرح ہم نے اس (قرآن) کو نازل کیا ہے روشن دلیلوں کی صورت میں اور بےشک اللہ جسے چاہتا ہے ہدایت دیتا ہے۔'),
(2612, 22, 17, 'بےشک جو اہلِ ایمان ہیں، جو یہودی ہیں، جو صابی ہیں، جو عیسائی ہیں، جو مجوسی ہیں اور جو مشرک ہیں یقیناً قیامت کے دن اللہ ان کے درمیان فیصلہ کرے گا۔ بیشک اللہ ہر چیز پر گواہ ہے۔'),
(2613, 22, 18, 'کیا تم نے نہیں دیکھا کہ ہر چیز اللہ کو سجدہ کر رہی ہے خواہ وہ آسمانوں میں ہے یا زمین میں۔ سورج، چاند، ستارے، پہاڑ، درخت، چوپائے اور بہت سارے انسان بھی اور بہت سے انسان وہ ہیں جن پر عذاب مقرر ہو چکا ہے اور جس کو اللہ ذلیل کر دے اسے کوئی عزت دینے والا نہیں ہے بیشک اللہ کرتا ہے جو وہ چاہتا ہے۔'),
(2614, 22, 19, 'یہ دو فریق ہیں جو اپنے پروردگار کے بارے میں باہم جھگڑ رہے ہیں تو ان میں سے جو لوگ کافر ہیں ان کیلئے آگ کے کپڑے کاٹے جا چکے ہیں ان کے سروں پر کھولتا ہوا پانی انڈیلا جائے گا۔'),
(2615, 22, 20, '(اور) جس سے ان کے پیٹ کے اندر کی چیزیں اور کھالیں گل جائیں گی۔'),
(2616, 22, 21, 'اور ان کے لئے لوہے کے گرز ہوں گے۔'),
(2617, 22, 22, 'جب بھی چاہیں گے کہ اس (جہنم) سے نکل کر رنج و غم سے چھٹکارا پائیں تو پھر اسی میں لوٹا دئیے جائیں گے اور (ان سے کہا جائے گا کہ) جلانے والی آگ کا مزہ چکھو۔'),
(2618, 22, 23, 'جو لوگ ایمان لائے اور اس کے ساتھ نیک عمل بھی کئے بےشک اللہ ان کو ان بہشتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی وہاں ان کو سونے کے کنگن اور موتی (کے ہار) پہنائے جائیں گے اور وہاں ان کے لباس ریشم کے ہوں گے۔'),
(2619, 22, 24, '(یہ اس لئے ہے کہ) انہیں پاکیزہ گفتگو کی طرف ہدایت دی گئی اور انہیں لائق ستائش (خدا) کی راہ کی طرف راہنمائی کی گئی۔'),
(2620, 22, 25, 'جن لوگوں نے کفر اختیار کیا اور (لوگوں) کو خدا کے راستے سے روکتے ہیں اور اس مسجد سے جسے ہم نے (بلا امتیاز) سب لوگوں کیلئے (عبادت گاہ) بنایا ہے جس میں وہاں کے رہنے والے اور باہر سے آنے والے برابر ہیں اور جو بھی اس میں ظلم و زیادتی کے ساتھ غلط روی کا ارادہ کرے تو ہم اسے دردناک عذاب کا مزہ چکھائیں گے۔'),
(2621, 22, 26, 'اور (وہ وقت یاد کرو) جب ہم نے ابراہیم (ع) کیلئے خانہ کعبہ کی جگہ معین کر دی۔ (اور حکم دیا کہ) میرے ساتھ کسی کو شریک نہ کرنا۔ اور میرے گھر کو طواف کرنے والوں، قیام کرنے والوں اور رکوع و سجود کرنے والوں کیلئے پاک رکھنا۔'),
(2622, 22, 27, 'اور لوگوں میں حج کا اعلان کر دو۔ کہ لوگ (آپ کی آواز پر لبیک کہتے ہوئے) آپ کے پاس آئیں گے۔ پیادہ پا اور ہر لاغر سواری پر کہ وہ سواریاں دور دراز سے آئی ہوں گی۔'),
(2623, 22, 28, 'تاکہ وہ اپنے فائدوں کیلئے حاضر ہوں اور مقررہ (دنوں میں خدا کا نام لیں) ان چوپایوں پر (ذبح کے وقت) جو اللہ نے انہیں عنایت فرمائے۔ پس خود بھی ان سے کھاؤ اور حاجت مند فقیر کو بھی کھلاؤ۔'),
(2624, 22, 29, 'پھر لوگوں کو چاہیئے کہ اپنی میل کچیل دور کریں اور اپنی نذریں پوری کریں اور قدیم گھر (خانہ کعبہ) کا طواف کریں۔'),
(2625, 22, 30, 'یہ ہے حج اور اس کے مناسک کی بات! جو اللہ کی محترم چیزوں کی تعظیم کرتا ہے۔ تو یہ اس کے پروردگار کے ہاں بہتر ہے اور تمہارے لئے چوپائے حلال ہیں۔ بجز ان کے جو تم سے بیان کئے جاتے رہتے ہیں۔ پس تم بتوں کی گندگی سے پرہیز کرو۔ اور راگ گانے کی مجلس سے اجتناب کرو۔'),
(2626, 22, 31, 'صرف اللہ ہی کیلئے ہو کر رہو اس کے ساتھ کسی کو شریک نہ ٹھہراتے ہوئے۔ اور جو کوئی اللہ کے ساتھ کسی کو شریک ٹھہراتا ہے تو گویا وہ آسمان سے گرا تو اسے کسی پرندہ نے اچک لیا یا ہوا نے اسے کسی دور دراز جگہ پر پھینک دیا۔'),
(2627, 22, 32, 'یہ ہے حقیقت حال! اور جو کوئی شعائر اللہ کی تعظیم کرے تو یہ دلوں کے تقویٰ سے ہے۔'),
(2628, 22, 33, 'تمہارے ان جانوروں میں ایک مقررہ مدت تک فائدے ہیں۔ پھر ان کے ذبح کرنے کا مقام اسی قدیم گھر کی طرف ہے۔'),
(2629, 22, 34, 'اور ہم نے ہر قوم کیلئے قربانی کا ایک طور طریقہ مقرر کیا ہے۔ تاکہ وہ ان چوپایوں پر (ذبح کے وقت) اللہ کا نام لیں، جو اس نے انہیں عطا کئے ہیں (الغرض) تمہارا اللہ ایک ہی ہے لہٰذا تم اس کی بارگاہ میں سر جھکاؤ اور ان عاجزی کرنے والوں کو خوشخبری دے دو۔'),
(2630, 22, 35, 'کہ جب (ان کے سامنے) اللہ کا ذکر کیا جاتا ہے تو ان کے دل دھل جاتے ہیں۔ اور جو ان مصائب و آلام پر صبر کرنے والے ہیں جو انہیں پہنچتے ہیں اور جو پابندی سے نماز ادا کرتے ہیں۔ اور ہم نے انہیں جو کچھ عطا کیا ہے اس میں سے (راہ خدا میں) خرچ کرتے رہتے ہیں۔'),
(2631, 22, 36, 'اور ہم نے قربانی کے اونٹوں کو تمہارے لئے شعائر اللہ سے قرار دیا ہے۔ ان میں تمہارے لئے بھلائی ہے پس تم ان کو صف بستہ کھڑا کرکے خدا کا نام لو۔ اور جب وہ کروٹ کے بل گر پڑیں تو ان میں سے خود بھی کھاؤ اور بے سوال اور سوالی کو بھی کھلاؤ اسی طرح ہم نے ان جانوروں کو تمہارے لئے مسخر کیا ہے تاکہ تم شکرگزار (بندے) بن جاؤ۔'),
(2632, 22, 37, 'نہ ان کا گوشت اللہ تک پہنچتا ہے اور نہ ان کا خون ہاں البتہ اس تک تمہارا تقویٰ پہنچتا ہے۔ اسی طرح ہم نے انہیں تمہارے لئے مسخر کیا ہے تاکہ تم اس کی دی ہوئی ہدایت پر اس کی کبریائی و بڑائی بیان کرو۔ اور (اے رسول) احسان کرنے والوں کو خوشخبری دے دو۔'),
(2633, 22, 38, 'بےشک اللہ اہلِ ایمان کی طرف سے دفاع کرتا ہے۔ یقیناً اللہ کسی خیانت کار کفرانِ نعمت کرنے والے کو دوست نہیں رکھتا۔'),
(2634, 22, 39, 'ان مظلوموں کو (دفاعی جہاد کی) اجازت دی جاتی ہے جن سے جنگ کی جا رہی ہے اس بناء پر کہ ان پر ظلم کیا گیا ہے۔ اور بیشک اللہ ان کی مدد کرنے پر قادر ہے۔'),
(2635, 22, 40, 'یہ وہ (مظلوم) ہیں جو ناحق اپنے گھروں سے نکال دیئے گئے صرف اتنی بات پر کہ وہ کہتے تھے کہ ہمارا پروردگار اللہ ہے اور اگر خدا بعض لوگوں کو بعض کے ذریعہ سے دفع نہ کرتا رہتا تو نصرانیوں کی خانقاہیں اور گرجے اور (یہود کے) عبادت خانے اور (مسلمانوں کی) مسجدیں جن میں بکثرت ذکر خدا ہوتا ہے۔ سب گرا دی جاتیں جو کوئی اللہ (کے دین) کی مدد کرے گا اللہ ضرور اس کی مدد کرے گا۔ بیشک وہ طاقت والا (اور) غالب آنے والا ہے۔'),
(2636, 22, 41, 'یہ وہ لوگ ہیں کہ اگر ہم انہیں زمین میں اقتدار عطا کریں تو وہ نماز قائم کریں گے، زکوٰۃ دیں گے، نیکی کا حکم دیں گے اور برائی سے روکیں گے اور تمام کاموں کا انجام اللہ ہی کے ہاتھ (اختیار) میں ہے۔'),
(2637, 22, 42, '(اے رسول(ص)) اگر لوگ آپ کو جھٹلاتے ہیں تو (یہ کوئی نئی بات نہیں ہے) ان سے پہلے (کئی قومیں اپنے نبیوں کو جھٹلا چکی ہیں جیسے) قوم نوح نے، عاد و ثمود نے۔'),
(2638, 22, 43, 'اور قوم ابراہیم (ع) نے اور قوم لوط نے (جھٹلایا)۔'),
(2639, 22, 44, 'اور اہل مدین نے بھی۔ اور موسیٰ کو بھی جھٹلایا گیا۔ سو پہلے میں نے کافروں کو (کچھ) مہلت دی اور پھر انہیں پکڑ لیا۔ تو (دیکھو) میرا عذاب کیسا تھا؟'),
(2640, 22, 45, 'غرض کتنی ہی بستیاں ہیں جنہیں ہم نے تباہ و برباد کر دیا کیونکہ وہ ظالم تھیں (چنانچہ) وہ اپنی چھتوں پر گری پڑی ہیں اور کتنے کنویں ہیں جو بیکار پڑے ہیں اور کتنے مضبوط محل ہیں (جو کھنڈر بن گئے ہیں)۔'),
(2641, 22, 46, 'کیا یہ لوگ زمین میں چلے پھرے نہیں ہیں تاکہ( یہ منظر دیکھ کر) ان کے دل ایسے ہو جاتے کہ جن سے وہ (حق کو) سمجھ سکتے اور کان ایسے ہو جاتے جن سے (آواز حق) سن سکتے۔ مگر (حقیقت یہ ہے کہ) آنکھیں اندھی نہیں ہوتیں وہ دل اندھے ہو جاتے ہیں جو سینوں میں ہیں۔'),
(2642, 22, 47, '(اے رسول(ص)) یہ لوگ آپ سے عذاب کے مطالبہ میں جلدی کر رہے ہیں اور اللہ کبھی اپنے وعدہ کی خلاف ورزی نہیں کرے گا اور آپ کے پروردگار کے نزدیک ایک دن تم لوگوں کے شمار کئے ہوئے ایک ہزار سال کے برابر ہے۔'),
(2643, 22, 48, 'اور کتنی ہی ایسی بستیاں ہیں جن کو میں نے مہلت دی۔ حالانکہ وہ ظالم تھیں۔ پھر میں نے انہیں پکڑ لیا اور (آخرکار) میری طرف لوٹ کر آنا ہے۔'),
(2644, 22, 49, '(اے رسول(ص)) آپ کہہ دیجئے! اے لوگو! میں تو صرف تمہیں ایک کھلا ہوا (عذاب خدا سے) ڈرانے والا ہوں۔'),
(2645, 22, 50, 'سنو جو لوگ ایمان لائے اور نیک عمل کئے ان کیلئے (گناہوں کی) بخشش بھی ہے اور عزت کی روزی بھی۔'),
(2646, 22, 51, 'اور جو لوگ ہماری آیتوں کے بارے میں (ہمیں) عاجز کرنے کی کوشش کرتے ہیں۔ وہی لوگ (دوزخی) ہیں۔'),
(2647, 22, 52, 'اور ہم نے آپ سے پہلے کوئی رسول اور کوئی نبی نہیںبھیجا مگر یہ کہ جب اس نے (اصلاح احوال کی) آرزو کی تو شیطان نے اس کی آرزو میں خلل اندازی کی۔ پس شیطان جو خلل اندازی کرتا ہے خدا اسے مٹا دیتا ہے اور اپنی نشانیوں کو زیادہ مضبوط کر دیتا ہے اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(2648, 22, 53, '(یہ سب اس لئے ہے) تاکہ اللہ شیطانوں کی خلل اندازی کو ان لوگوں کیلئے آزمائش کا ذریعہ بنائے جن کے دلوں میں بیماری ہے اور جن کے دل سخت ہیں بےشک ظالم لوگ بڑی گہری مخالفت میں پڑے ہوئے ہیں۔'),
(2649, 22, 54, 'اور اس میں (یہ مصلحت بھی ہے) کہ وہ لوگ جنہیں علم عطا کیا گیا ہے۔ جان لیں کہ یہ (وحی) آپ کے پروردگار کی طرف سے حق ہے تاکہ وہ اس پر ایمان لائیں اور ان کے دلوں میں اس کیلئے عجز و نیاز پیدا ہو جائے۔ بےشک اللہ ایمان لانے والوں کو سیدھے راستہ تک پہنچانے والا ہے۔'),
(2650, 22, 55, 'اور جو کافر ہیں وہ تو ہمیشہ اس کی طرف سے شک میں پڑے رہیں گے یہاں تک کہ اچانک قیامت ان پر آجائے۔ یا پھر منحوس دن کا عذاب ان پر آجائے۔'),
(2651, 22, 56, 'اس دن بادشاہی صرف اللہ ہی کی ہوگی (لہٰذا) وہی لوگوں کے درمیان فیصلہ کرے گا۔ پس جو ایمان لائے اور نیک عمل کرے وہ نعمتوں والے بہشتوں میں ہوں گے۔'),
(2652, 22, 57, 'اور جنہوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا ان کیلئے رسوا کن عذاب ہے۔'),
(2653, 22, 58, 'اور جن لوگوں نے اللہ کی راہ میں ہجرت کی پھر قتل کر دیئے گئے۔ یا اپنی موت مر گئے (دونوں صورتوں میں) اللہ ضرور انہیں اچھی روزی عطا فرمائے گا (کیونکہ) وہ بہترین روزی عطا کرنے والا ہے۔'),
(2654, 22, 59, '(اور) وہ ضرور انہیں ایسی جگہ داخل کرے گا جسے وہ پسند کریں گے۔ بےشک وہ بڑا جاننے والا، بڑا بردبار ہے۔'),
(2655, 22, 60, 'یہ بات تو ہو چکی۔ اور جو شخص ویسی ہی سزا دے جیسی اسے سزا ملی ہے پھر اس پر زیادتی کی جائے تو اللہ ضرور اس کی مدد کرے گا۔ بےشک اللہ بڑا معاف کرنے والا، بڑا بخشنے والا ہے۔'),
(2656, 22, 61, 'یہ اس لئے ہے کہ اللہ رات کو دن میں اور دن کو رات میں داخل کرتا ہے اور بےشک اللہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(2657, 22, 62, 'اس کی وجہ یہ ہے کہ اللہ ہی حق ہے۔ اور اس کے علاوہ جسے وہ پکارتے ہیں وہ باطل ہے۔ اور بےشک اللہ ہی بلند (مرتبہ والا) بزرگ ہے۔'),
(2658, 22, 63, 'کیا تم نے نہیں دیکھا کہ خدا آسمان سے پانی برساتا ہے تو (خشک) زمین سرسبز و شاداب ہو جاتی ہے۔ بےشک اللہ بڑا لطف و کرم کرنے والا (اور) بڑا خبر رکھنے والا ہے۔'),
(2659, 22, 64, 'جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے سب اسی کا ہے اور بےشک اللہ سب سے بے نیاز (اور) ہر تعریف کا حقدار ہے۔'),
(2660, 22, 65, 'کیا تم نہیں دیکھتے کہ جو کچھ زمین میں ہے۔ اللہ نے وہ سب کچھ تمہارے لئے مسخر کر دیا ہے اور کشتی کو بھی جو سمندر میں اس کے حکم سے چلتی ہے۔ اور وہی آسمان کو روکے ہوئے ہے کہ اس کے حکم کے بغیر زمین پر نہ گر پڑے۔ بےشک اللہ لوگوں پر بڑا شفقت والا، بڑا رحمت والا ہے۔'),
(2661, 22, 66, 'وہ وہی ہے جس نے تمہیں زندگی عطا کی ہے پھر وہی تمہیں موت دے گا۔ اور پھر (دوبارہ) زندہ کرے گا۔ بےشک انسان بڑا ناشکرا ہے۔'),
(2662, 22, 67, '(اے رسول(ص)) ہم نے ہر قوم کیلئے عبادت کا ایک طریقہ مقرر کر دیا ہے جس کے مطابق وہ عبادت کر رہی ہے۔ اس لئے انہیں آپ سے جھگڑا نہیں کرنا چاہیے اور آپ اپنے پروردگار کی طرف بلاتے رہیے! یقیناً آپ ہدایت کے سیدھے راستہ پر ہیں۔'),
(2663, 22, 68, 'اور اگر وہ لوگ (خواہ مخواہ) آپ سے بحث کریں تو آپ کہہ دیجئے! کہ اللہ بہتر جانتا ہے جو کچھ تم کر رہے ہو۔'),
(2664, 22, 69, 'قیامت کے دن اللہ تمہارے درمیان ان باتوں کے بارے میں فیصلہ کر دے گا جن میں تم باہم اختلاف کرتے ہو۔'),
(2665, 22, 70, 'کیا تم نہیں جانتے کہ اللہ وہ سب کچھ جانتا ہے جو آسمان اور زمین میں ہے۔ بےشک یہ سب کچھ ایک کتاب میں درج ہے۔ اور یہ بات اللہ پر آسان ہے۔'),
(2666, 22, 71, 'اور یہ لوگ اللہ کو چھوڑ کر ایسوں کی عبادت کرتے ہیں جن کیلئے نہ تو اللہ نے کوئی دلیل (سند) نازل کی ہے اور نہ ہی انہیں ان کے بارے میں کوئی علم ہے اور (ان) ظالموں کیلئے کوئی مددگار نہیں ہے۔'),
(2667, 22, 72, 'اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو تم کافروں کے چہروں پر ناگواری کے آثار واضح محسوس کرتے ہو۔ قریب ہے وہ ان پر حملہ کر دیں جو ان کے سامنے ہماری آیتیں پڑھ کر سناتے ہیں۔ کیا میں تمہیں بتاؤں کہ اس سے بڑھ کر ناگوار چیز کیا ہے؟ وہ دوزخ کی آگ ہے۔ جس کا اللہ نے کافروں سے وعدہ کر رکھا ہے۔ اور وہ بڑا برا ٹھکانہ ہے۔'),
(2668, 22, 73, 'اے لوگو! ایک مثال دی جاتی ہے اسے غور سے سنو۔ اللہ کو چھوڑ کر تم جن معبودوں کو پکارتے ہو وہ ایک مکھی بھی پیدا نہیں کر سکتے۔ اگرچہ وہ سب کے سب اس کام کے لئے اکٹھے ہو جائیں۔ اور اگر ایک مکھی ان سے کوئی چیز چھین کر لے جائے تو وہ اسے اس (مکھی) سے چھڑا نہیں سکتے۔ طالب و مطلوب دونوں ہی کمزور و ناتواں ہیں۔'),
(2669, 22, 74, '(آہ) ان لوگوں نے اللہ کی اس طرح قدر نہیں کی جس طرح قدر کرنی چاہیے۔ بےشک اللہ بڑا طاقتور ہے (اور) سب پر غالب ہے۔'),
(2670, 22, 75, 'اللہ پیغام پہنچانے والے فرشتوں میں سے منتخب کر لیتا ہے اور انسانوں میں سے بھی۔ بیشک اللہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(2671, 22, 76, 'وہ جانتا ہے جو کچھ ان کے سامنے ہے اور جو کچھ ان کے پیچھے ہے اور سب معاملات کی بازگشت اللہ ہی کی طرف ہے۔'),
(2672, 22, 77, 'اے ایمان والو! رکوع و سجود کرو۔ اور اپنے پروردگار کی عبادت کرو۔ اور نیک کام کرو۔ تاکہ تم فلاح پاؤ۔'),
(2673, 22, 78, 'اور اللہ کی راہ میں جہاد کرو۔ جیساکہ جہاد کرنے کا حق ہے اس نے تمہیں منتخب کیا ہے۔ اور دین کے معاملہ میں اس نے تمہارے لئے کوئی تنگی روا نہیں رکھی۔ اپنے باپ (مورث اعلیٰ) ابراہیم (ع) کی ملت کی پیروی کرو۔ اسی (اللہ) نے اس سے پہلے بھی تمہارا نام مسلمان رکھا اور اس (قرآن) میں بھی تاکہ پیغمبر تم پر گواہ ہوں۔ اور تم لوگوں پر گواہ ہو۔ پس تم نماز قائم کرو۔ اور زکوٰۃ دو اور اللہ سے وابستہ ہو جاؤ۔ وہی تمہارا مولا ہے سو کیسا اچھا مولا ہے اور کیسا اچھا مددگار ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'یقیناً وہ اہلِ ایمان فلاح پاگئے۔'),
(2675, 23, 2, 'جو اپنی نمازوں میں خشوع و خضوع کرتے ہیں۔'),
(2676, 23, 3, 'اور جو بیہودہ باتوں سے منہ موڑتے ہیں۔'),
(2677, 23, 4, 'اور جو زکوٰۃ ادا کرتے ہیں۔'),
(2678, 23, 5, 'اور جو اپنی شرمگاہوں کی حفاظت کرتے ہیں۔'),
(2679, 23, 6, 'سوائے اپنی بیویوں کے اور ان کنیزوں کے جو ان کی ملکیت ہیں کہ اس صورت میں وہ قابل ملامت نہیں ہیں۔'),
(2680, 23, 7, 'اور جو اس (دو طرح) کے علاوہ کوئی خواہش کرے تو ایسے ہی لوگ (حد سے) تجاوز کرنے والے ہیں۔'),
(2681, 23, 8, 'اور جو اپنی امانتوں اور اپنے عہد و پیمان کا لحاظ رکھتے ہیں۔'),
(2682, 23, 9, 'اور جو اپنی نمازوں کی پوری حفاظت کرتے ہیں۔'),
(2683, 23, 10, 'یہی لوگ وارث ہیں۔'),
(2684, 23, 11, 'جو فردوس (بریں) کے وارث ہوں گے (اور) وہ اس میں ہمیشہ رہیں گے۔'),
(2685, 23, 12, 'یقیناً ہم نے انسان کو مٹی کے جوہر سے پیدا کیا۔'),
(2686, 23, 13, 'پھر ہم نے اسے نطفہ کی صورت میں ایک محفوظ مقام (رحم) میں رکھا۔'),
(2687, 23, 14, 'پھر ہم نے نطفہ کو منجمد خون بنایا اور پھر ہم نے اس منجمد خون کو گوشت کا لوتھڑا بنایا پھر اس لوتھڑے سے ہڈیاں پیدا کیں پھر ان ہڈیوں پر گوشت چڑھایا پھر ہم نے (اس میں روح ڈال کر) ایک دوسری مخلوق بنا دیا۔ پس بڑا بابرکت ہے وہ اللہ جو بہترین خالق ہے۔'),
(2688, 23, 15, 'پھر تم اس کے بعد ضرور مرنے والے ہو۔'),
(2689, 23, 16, 'پھر اس کے بعد تم قیامت کے دن اٹھائے جاؤگے۔'),
(2690, 23, 17, 'اور یقیناً ہم نے تمہارے اوپر سات راستے (آسمان) پیدا کئے ہیں اور ہم (اپنی) مخلوق سے غافل نہیں تھے۔'),
(2691, 23, 18, 'اور ہم نے ایک خاص اندازہ کے ساتھ آسمان سے پانی برسایا اور اسے زمین میں ٹھہرایا اور یقیناً ہم اسے لے جانے پر بھی قادر ہیں۔'),
(2692, 23, 19, 'پھر ہم نے اس کے ذریعہ سے تمہارے لئے کھجوروں اور انگوروں کے باغات اگائے ان باغوں میں تمہارے لئے بہت سے پھل ہیں اور ان میں سے تم کھاتے بھی ہو۔'),
(2693, 23, 20, 'اور (ہم نے) ایک درخت ایسا بھی (پیدا کیا) جو طور سینا سے نکلتا ہے (زیتون کا درخت) جو کھانے والوں کیلئے تیل اور سالن لے کر اگتا ہے۔'),
(2694, 23, 21, 'اور تمہارے لئے چوپایوں میں بڑا سامانِ عبرت ہے ہم تمہیں اس سے (دودھ) پلاتے ہیں جو ان کے پیٹوں میں ہے اور تمہارے لئے ان میں اور بہت سے فائدے ہیں اور انہی کے (گوشت) سے تم کھاتے بھی ہو۔'),
(2695, 23, 22, 'اور ان پر اور کشتیوں پر تمہیں سوار کیا جاتا ہے۔'),
(2696, 23, 23, 'اور بےشک ہم نے نوح(ع) کو ان کی قوم کی طرف ہدایت کیلئے بھیجا۔ تو آپ نے کہا اے میری قوم! اللہ کی عبادت کرو۔ اس کے سوا کوئی اللہ نہیں ہے۔ کیا تم (اس کی حکم عدولی سے) نہیں ڈرتے؟'),
(2697, 23, 24, 'تو ان کی قوم کے کافر سرداروں نے کہا کہ یہ (نوح(ع)) نہیں ہے مگر تم ہی جیسا ایک بشر (آدمی) ہے جو تم پر بڑائی حاصل کرنا چاہتا ہے اور اگر خدا (یہی) چاہتا تو وہ فرشتوں کو بھیجتا۔ ہم نے تو یہ بات اپنے پہلے باپ داداؤں سے نہین سنی۔'),
(2698, 23, 25, 'بس یہ ایک آدمی ہے۔ جسے کچھ جنون ہوگیا ہے۔ پس تم اس کے بارے میں کچھ مدت تک انتظار کرو۔'),
(2699, 23, 26, 'آپ نے کہا اے میرے پروردگار! تو میری مدد کر کیونکہ انہوں نے مجھے جھٹلایا ہے۔'),
(2700, 23, 27, 'سو ہم نے ان کی طرف وحی بھیجی کہ تم ہماری نگرانی اور ہماری وحی کے مطابق کشتی بناؤ۔ پس جب ہمارا عذاب آجائے۔ اور تنور سے پانی ابلنے لگے تو ہر قسم کے نر اور مادہ (جانوروں) میں سے جوڑا جوڑا (اس میں) داخل کرلو۔ اور اپنے گھر والوں کو بھی سوا ان کے جن کے خلاف پہلے سے فیصلہ ہو چکا ہے۔ اور ظالموں کے بارے میں مجھ سے کوئی بات نہ کرنا کیونکہ یہ اب یقیناً غرق ہونے والے ہیں۔'),
(2701, 23, 28, 'اور جب تم اور تمہارے ساتھی کشتی پر سوار ہو جائیں تو کہنا۔ الحمدﷲ۔ سب تعریف اس اللہ کیلئے ہے۔ جس نے ہمیں ظالم قوم سے نجات دی ہے۔'),
(2702, 23, 29, 'اور کہنا اے میرے پروردگار! مجھے بابرکت، اتار تو سب سے بہتر اتارنے والا ہے۔'),
(2703, 23, 30, 'بےشک اس (واقعہ) میں بڑی نشانیاں ہیں اور ہم (لوگوں کی) آزمائش کیا کرتے ہیں۔'),
(2704, 23, 31, 'پھر ہم نے ان کے بعد ایک اور نسل پیدا کی۔'),
(2705, 23, 32, 'پھر ہم نے انہی میں سے ایک پیغمبر بھیجا (جس نے ان سے کہا) اللہ کی عبادت کرو۔ اس کے سوا کوئی الٰہ نہیں ہے۔ کیا تم (اس کی حکم عدولی) سے ڈرتے نہیں ہو؟'),
(2706, 23, 33, 'اور اس کی قوم کے وہ سردار جو کافر تھے اور آخرت کی حاضری کو جھٹلاتے تھے اور جنہیں ہم نے دنیوی زندگی میں آسودگی دے رکھی تھی۔ وہ کہنے لگے کہ یہ نہیں ہے مگر تمہارے ہی جیسا ایک بشر (آدمی) ہے یہ وہی (خوراک) کھاتا ہے جو تم کھاتے ہو اور وہی (مشروب) پیتا ہے۔ جو تم پیتے ہو۔'),
(2707, 23, 34, 'اور اگر تم نے اپنے ہی جیسے ایک بشر کی اطاعت کر لی تو تم گھاٹے میں رہوگے۔'),
(2708, 23, 35, 'کیا وہ تم سے وعدہ کرتا ہے کہ جب تم مر جاؤگے اور مٹی اور ہڈیاں ہو جاؤگے تو پھر تم (قبروں سے) نکالے جاؤگے؟'),
(2709, 23, 36, '(حاشا و کلا) بہت بعید ہے بہت بعید ہے وہ بات جس کا تم سے وعدہ کیا جا رہا ہے۔'),
(2710, 23, 37, 'کوئی اور زندگی نہیں سوائے اس دنیوی زندگی کے جس میں ہمیں مرنا بھی ہے اور جینا بھی۔ اور ہمیں (دوبارہ) نہیں اٹھایا جائے گا۔'),
(2711, 23, 38, 'وہ نہیں ہے مگر ایک ایسا شخص جس نے خدا پر جھوٹا بہتان باندھا ہے اور ہم تو (اس کو) ماننے والے نہیں ہیں۔'),
(2712, 23, 39, 'اس (رسول) نے کہا اے میرے پروردگار! تو میری مدد کر! کیونکہ ان لوگوں نے مجھے جھٹلایا ہے۔'),
(2713, 23, 40, 'ارشاد ہوا کہ عنقریب یہ لوگ (اپنے کئے پر) پشیمان ہوں گے۔'),
(2714, 23, 41, 'چنانچہ بجاطور پر انہیں ایک سخت آواز نے آپکڑا اور ہم نے انہیں خس و خاشاک بنا دیا سو ہلاکت ہے ظالم قوم کیلئے۔'),
(2715, 23, 42, 'پھر ہم نے ان کے بعد دوسری قومیں پیدا کیں۔'),
(2716, 23, 43, 'کوئی قوم اپنے مقررہ وقت سے نہ آگے بڑھ سکتی ہے۔ اور نہ پیچھے ہٹ سکتی ہے۔'),
(2717, 23, 44, 'پھر ہم نے لگاتار اپنے رسول بھیجے جب بھی کسی امت کے پاس اس کا رسول آیا تو انہوں نے اسے جھٹلایا تو ہم بھی ایک کے بعد دوسرے کو ہلاک کرتے رہے۔ اور ہم نے انہیں قصہ پارینہ (اور افسانہ) بنا دیا۔ پس تباہی ہو اس قوم کیلئے جو ایمان نہیں لاتے۔'),
(2718, 23, 45, 'پھر ہم نے موسیٰ اور ان کے بھائی ہارون کو اپنی نشانیوں اور کھلی ہوئی دلیل کے ساتھ۔'),
(2719, 23, 46, 'فرعون اور اس کے سرداروں کی طرف بھیجا تو انہوں نے تکبر کیا اور وہ لوگ بڑے سرکش لوگ تھے۔'),
(2720, 23, 47, 'چنانچہ وہ کہنے لگے کہ ہم اپنے ہی جیسے دو آدمیوں پر ایمان لائیں حالانکہ ان کی قوم ہماری خدمت گزار ہے۔'),
(2721, 23, 48, 'پس ان لوگوں نے ان دونوں کو جھٹلایا (نتیجہ یہ نکلا کہ) وہ بھی ہلاک ہونے والوں میں شامل ہوگئے۔'),
(2722, 23, 49, 'اور ہم نے موسیٰ کو کتاب (توراۃ) عطا کی تاکہ وہ لوگ ہدایت پائیں۔'),
(2723, 23, 50, 'اور ہم نے ابن مریم اور اس کی ماں کو (اپنی قدرت کی) نشانی بنایا اور ان دونوں کو ایک بلند مقام پر پناہ دی جو ٹھہرنے کے قابل بھی تھا اور جہاں چشمے جاری تھے۔'),
(2724, 23, 51, 'اے (میرے) پیغمبرو! پاکیزہ چیزیں کھاؤ اور نیک عمل کرو۔ بیشک تم جو کچھ کرتے ہو میں اسے خوب جانتا ہوں۔'),
(2725, 23, 52, 'اور یہ تمہاری امت (جماعت) ایک امت ہے اور میں تمہارا پروردگار ہوں سو تم مجھ ہی سے ڈرو۔'),
(2726, 23, 53, 'مگر ان لوگوں نے اپنے (دینی) معاملہ کو آپس میں ٹکڑے ٹکڑے کر ڈالا پس ہر گروہ کے پاس جو (دین) ہے وہ اس پر خوش ہے۔'),
(2727, 23, 54, 'پس (اے رسول(ص)) آپ ان کو ایک خاص وقت تک اپنی غفلت و مدہوشی میں پڑا رہنے دیجئے۔'),
(2728, 23, 55, 'کیا یہ لوگ خیال کرتے ہیں کہ ہم جو مال و اولاد (کی فراوانی) سے ان کی امداد کر رہے ہیں۔'),
(2729, 23, 56, 'تو ہم ان کے ساتھ بھلائیاں کرنے میں جلدی کر رہے ہیں؟ (ایسا نہیں) بلکہ انہیں (اصل حقیقت کا) شعور نہیں ہے۔'),
(2730, 23, 57, 'بےشک جو لوگ اپنے پروردگار کی ہیبت سے ڈرتے ہیں۔'),
(2731, 23, 58, 'اور جو اپنے پروردگار کی آیتوں پر ایمان لاتے ہیں۔'),
(2732, 23, 59, 'اور جو اپنے پروردگار کے ساتھ کسی کو شریک نہیں ٹھہراتے۔'),
(2733, 23, 60, 'اور وہ جو دیتے ہیں جو کچھ بھی دیتے ہیں (پھر بھی) ان کے دل اس خیال سے ترساں رہتے یں کہ انہوں نے اپنے پروردگار کی طرف پلٹ کر جانا ہے۔'),
(2734, 23, 61, 'یہی لوگ بھلائیاں کرنے میں جلدی کیا کرتے ہیں اور بھلائیوں میں سبقت لے جانے والے ہیں۔'),
(2735, 23, 62, 'ہم کسی شخص کو اس کی وسعت سے زیادہ تکلیف نہیں دیتے اور ہمارے پاس وہ کتاب (نامۂ اعمال) ہے جو حق کے ساتھ بولتی ہے اور ان پر ظلم نہیں کیا جائے گا۔'),
(2736, 23, 63, 'بلکہ ان کے دل اس (دینی) معاملہ میں غفلت و سرشاری میںپڑے ہیں اور اس کے علاوہ بھی ان کے کچھ (برے) عمل ہیں جو یہ کرتے رہتے ہیں۔'),
(2737, 23, 64, 'یہاں تک کہ جب ہم ان کے خوشحال لوگوں کو عذاب کی گرفت میں لیتے ہیں تو وہ چیخنے چلانے لگتے ہیں۔'),
(2738, 23, 65, 'آج کے دن چیخو چلاؤ نہیں (آج) ہماری طرف سے تمہاری کوئی مدد نہیں کی جائے گی۔'),
(2739, 23, 66, '(وہ وقت یاد کرو جب) میری آیتیں تمہارے سامنے پڑھی جاتی تھیں تو تم (سنتے ہی) الٹے پاؤں پلٹ جایا کرتے تھے۔'),
(2740, 23, 67, 'تکبر کرتے ہوئے داستان سرائی کرتے ہوئے (اور) بے ہودہ بکتے ہوئے۔'),
(2741, 23, 68, 'کیا ان لوگوں نے اس کلام میں کبھی غور نہیں کیا یا ان کے پاس ایسی چیز آئی ہے جو ان کے پہلے والے باپ داداؤں کے پاس نہیں آئی تھی؟'),
(2742, 23, 69, 'یا کیا انہوں نے اپنے رسول کو پہچانا نہیں ہے؟ اس لئے منکر ہوگئے۔'),
(2743, 23, 70, 'یا وہ کہتے ہیں کہ یہ مجنون ہے؟ بلکہ وہ تو ان کے پاس حق لے کر آیا ہے اور اکثر لوگ حق کو ناپسند کرتے ہیں۔'),
(2744, 23, 71, 'اور اگر حق لوگوں کی خواہشات کی پیروی کرتا تو آسمان و زمین اور جو ان کے اندر ہے سب درہم برہم ہو جاتے۔ بلکہ ہم تو ان کے پاس نصیحت لے کر آئے مگر وہ اپنی نصیحت سے ہی روگردانی کرتے ہیں۔'),
(2745, 23, 72, 'کیا آپ ان سے کوئی معاوضہ طلب کرتے ہیں؟ تمہارے لئے تو تمہارے پروردگار کا معاوضہ بہت بہتر ہے اور وہ بہترین رازق ہے۔'),
(2746, 23, 73, 'اور بےشک آپ ان لوگوں کو سیدھے راستے کی طرف بلاتے ہیں۔'),
(2747, 23, 74, 'اور جو آخرت پر ایمان نہیں رکھتے وہ راہ سے ہٹے ہوئے ہیں۔'),
(2748, 23, 75, 'اور اگر ہم ان پر رحم کریں اور جس تکلیف میں مبتلا ہیں وہ بھی دور کر دیں تو بھی یہ لوگ اپنی سرکشی میں اندھا دھند بڑھتے جائیں گے۔'),
(2749, 23, 76, 'اور ہم نے انہیں سزا میں گرفتار بھی کیا مگر وہ پھر بھی اپنے پروردگار کے سامنے نہ جھکے اور نہ تضرع و زاری کی۔'),
(2750, 23, 77, 'یہاں تک کہ جب ہم ان پر سخت عذاب کا دروازہ کھول دیں گے تو وہ ایک دم ناامید ہو جائیں گے۔'),
(2751, 23, 78, 'اور اللہ وہی ہے جس نے تمہارے لئے جان، آنکھیں اور دل بنائے (لیکن) تم لوگ بہت کم شکر ادا کرتے ہو۔'),
(2752, 23, 79, 'وہ وہی ہے جس نے تمہیں روئے زمین پر (پیدا کرکے) پھیلا دیا ہے اور اسی کی طرف تم اکٹھے کئے جاؤگے۔'),
(2753, 23, 80, 'وہ وہی ہے جو زندہ کرتا ہے اور مارتا ہے اور رات اور دن کی آمد و رفت اسی کے قبضۂ قدرت میں ہے۔ کیا تم عقل سے کام نہیں لیتے؟'),
(2754, 23, 81, 'ان لوگوں نے وہی بات کہی ہے جو ان کے پہلے (کافر) کہتے رہے ہیں۔'),
(2755, 23, 82, 'کہتے ہیں کہ جب ہم مر جائیں گے اور خاک اور ہڈیاں ہو جائیں گے تو کیا ہم دوبارہ اٹھائے جائیں گے۔'),
(2756, 23, 83, 'ہم سے بھی اور اس سے پہلے ہمارے باپ داداؤں سے بھی یہ وعدہ ہوتا آیا ہے۔ یہ کچھ بھی نہیں صرف پہلے عہدوں کے افسانے ہیں۔'),
(2757, 23, 84, 'آپ کہہ دیجیے اگر تم جانتے ہو تو (بتاؤ) کہ زمین اور جو اس میں رہتے ہیں کس کیلئے ہیں؟'),
(2758, 23, 85, 'وہ ضرور کہیں گے کہ اللہ کیلئے ہیں۔ تو کہئے کہ تم غور کیوں نہیں کرتے؟'),
(2759, 23, 86, 'آپ کہئے کہ سات آسمانوں اور اس بڑے تخت سلطنت کا پروردگار کون ہے؟'),
(2760, 23, 87, 'وہ ضرور کہیں گے کہ اللہ ہے! کہئے کیا تم پھر بھی پرہیزگار نہیں بنتے (اس سے نہیں ڈرتے؟)۔'),
(2761, 23, 88, 'آپ کہئے کہ اگر جانتے ہو تو بتاؤ کہ ہر چیز کی بادشاہی کس کے قبضہ میں ہے۔ جو سب کو پناہ دیتا ہے۔ مگر اس کے مقابلہ میں پناہ نہیں دی جا سکتی۔'),
(2762, 23, 89, 'وہ ضرور کہیں گے کہ اللہ کے! تو کہئے! کہ پھر تم پر کہاں سے جادو کیا جا رہا ہے؟'),
(2763, 23, 90, 'بلکہ ہم ان کے سامنے حق لے آئے ہیں۔ اور یقیناً وہ جھوٹے ہیں۔'),
(2764, 23, 91, 'اللہ نے کسی کو اپنی اولاد نہیں بنایا اور نہ ہی اس کے ساتھ کوئی الہ ہے۔ اگر ایسا ہوتا تو ہر خدا اپنی مخلوق کو لے کر الگ ہو جاتا اور پھر ایک خدا دوسرے خدا پر چڑھ دوڑتا۔ پاک ہے خدا اس سے جو لوگ بیان کرتے ہیں۔'),
(2765, 23, 92, 'وہ غیب و شہادت یعنی پوشیدہ اور ظاہر سب کا جاننے والا ہے۔ اور وہ اس شرک سے بلند و بالا ہے جو یہ لوگ بیان کرتے ہیں۔'),
(2766, 23, 93, '(اے رسول(ص)) آپ کہئے کہ اے میرے پروردگار اگر تو مجھے وہ عذاب دکھا دے جس کی ان لوگوں کو دھمکی دی جا رہی ہے۔'),
(2767, 23, 94, 'تو اے میرے پروردگار! مجھے ظالم لوگوں میں شامل نہ کرنا۔'),
(2768, 23, 95, 'اور ہم اس عذاب کے آپ کو دکھانے پر قادر ہیں جس کی ان کو دھمکی دی جا رہی ہے۔'),
(2769, 23, 96, '(اے رسول(ص)) آپ برائی کو احسن طریقہ سے دفع کریں۔ ہم خوب جانتے ہیں جو وہ (آپ کی نسبت) بیان کرتے ہیں۔'),
(2770, 23, 97, 'اور آپ کہئے! اے میرے پروردگار! میں شیطانوں کے وسوسوں سے تجھ سے پناہ مانگتا ہوں۔'),
(2771, 23, 98, 'اور اے میرے پروردگار! میں تجھ سے پناہ مانگتا ہوں اس سے کہ وہ (شیطان) میرے پاس آئیں۔'),
(2772, 23, 99, '(کافروں کی یہی حالت رہتی ہے) یہاں تک کہ جب ان میں سے کسی کے پاس موت آکھڑی ہوتی ہے تو (تب) کہتا ہے کہ اے میرے پروردگار! مجھے (ایک بار) اسی دنیا میں واپس بھیج دے جسے میں چھوڑ آیا ہوں۔'),
(2773, 23, 100, 'تاکہ میں نیک عمل کر سکوں (ارشاد ہوگا) ہرگز نہیں! یہ محض ایک (فضول) بات ہے جو وہ کہہ رہا ہے اور ان کے (مرنے کے) بعد برزخ کا زمانہ ہے ان کے (دوبارہ) اٹھائے جانے تک۔'),
(2774, 23, 101, 'پھر جب صور پھونکا جائے گا تو اس دن نہ تو ان کے درمیان رشتے ناطے رہیں گے اور نہ ہی ایک دوسرے کو پوچھیں گے۔'),
(2775, 23, 102, '(ہاں البتہ) جن کے پلڑے بھاری ہوں گے تو وہی کامیاب ہوں گے۔'),
(2776, 23, 103, 'اور جن کے پلڑے ہلکے ہوں گے تو یہ وہی لوگ ہوں گے جنہوں نے اپنے آپ کو نقصان پہنچایا (اور) وہ ہمیشہ جہنم میں رہیں گے۔'),
(2777, 23, 104, 'آگ ان کے چہروں کو جھلسا رہی ہوگی اور ان کی شکلیں اس میں بگڑ گئی ہوں گی۔'),
(2778, 23, 105, '(ان سے کہا جائے گا) کیا تمہارے سامنے ہماری آیتیں نہیں پڑھی جاتی تھیں اور تم انہیں جھٹلاتے تھے؟'),
(2779, 23, 106, '(وہ اس وقت) کہیں گے اے ہمارے پروردگار! ہم پر ہماری بدبختی غالب آگئی تھی۔ اور واقعی ہم گمراہ لوگ تھے۔'),
(2780, 23, 107, 'اے ہمارے پروردگار! (ایک بار) ہمیں اس سے نکال دے۔ پھر اگر ہم ایسا کریں گے تو بےشک ہم سراسر ظالم ہوں گے۔'),
(2781, 23, 108, 'ارشاد ہوگا اور مجھ سے کوئی بات نہ کرو۔'),
(2782, 23, 109, '(تمہیں یاد ہے) کہ بےشک میرے بندوں میں ایک گروہ ایسا بھی تھا جو کہتا تھا: اے ہمارے پروردگار! ہم ایمان لائے ہیں! پس تو ہمیں بخش دے اور ہم پر رحم کر اور تو بہترین رحم کرنے والا ہے۔'),
(2783, 23, 110, 'تو تم ان کا مذاق اڑاتے تھے یہاں تک کہ (گویا) انہوں نے تمہیں میری یاد بھی بھلا دی اور تم ان پر ہنستے تھے۔'),
(2784, 23, 111, '(دیکھو) آج میں نے انہیں ان کے صبر کی جزا دی ہے۔ وہی کامران ہیں۔'),
(2785, 23, 112, 'ارشاد ہوگا تم کتنے برس تک زمین میں رہے ہو؟'),
(2786, 23, 113, 'وہ کہیں گے: ایک دن یا ایک دن کا بھی کچھ عرصہ۔ سو تو گننے والوں سے پوچھ لے۔'),
(2787, 23, 114, 'ارشاد ہوگا: تم نہیں رہے۔ مگر تھوڑا عرصہ کاش تم نے یہ بات سمجھی ہوتی۔'),
(2788, 23, 115, 'کیا تمہارا یہ خیال تھا کہ ہم نے تمہیں بلا مقصد پیدا کیا ہے اور تم نے کبھی ہماری طرف پلٹ کر نہیں آنا ہے؟'),
(2789, 23, 116, 'اللہ جو حقیقی بادشاہ ہے (ایسی بات سے) بلند و برتر ہے اس کے سوا کوئی الہ نہیں ہے۔ وہ عزت و عظمت والے عرش کا مالک ہے۔'),
(2790, 23, 117, 'اور جو کوئی اللہ کے سوا کسی دوسرے (خود ساختہ) الہ (خدا) کو پکارتا ہے تو اس کے پاس اس کے لئے کوئی دلیل نہیں ہے تو اس کا حساب و کتاب پروردگار کے پاس ہے۔ اور جو کافر ہیں وہ کبھی فلاح نہیں پائیں گے۔'),
(2791, 23, 118, 'اور (اے رسول(ص)) آپ کہئے! اے میرے پروردگار! تو بخش دے اور رحم فرما۔ اور تو بہترین رحم کرنے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(شروع کرتا ہوں) اللہ کے نام سے جو بڑا مہربان نہایت رحم والا ہے۔یہ ایک سورہ ہے جسے ہم نے نازل کیا ہے اور ہم نے (ہی) اس (کے احکام) کو فرض کیا ہے۔ اور ہم نے ہی اس میں کھلی ہوئی آیتیں نازل کی ہیں۔ تاکہ تم نصیحت حاصل کرو۔'),
(2793, 24, 2, 'زناکار عورت اور زناکار مرد ان دونوں میں سے ہر ایک کو سو سو کوڑے لگاؤ۔ اور اللہ کے دین کے معاملہ میں تمہیں ان پر ترس نہ آئے۔ اگر تم اللہ اور آخرت کے دن پر ایمان رکھتے ہو اور ان دونوں کو سزا دیتے وقت اہلِ ایمان کی ایک جماعت کو موجود رہنا چاہیے۔'),
(2794, 24, 3, 'زناکار مرد نکاح نہیں کرتا مگر زناکار عورت یا مشرک عورت کے ساتھ اور زناکار عورت نکاح نہیں کرتی مگر زناکار مرد یا مشرک مرد کے ساتھ اور یہ (زنا) اہلِ ایمان پر حرام قرار دے دیا گیا ہے۔'),
(2795, 24, 4, 'اور جو لوگ پاکدامن عورتوں پر زنا کی تہمت لگائیں اور پھر چار گواہ پیش نہ کریں تو انہیں اسّی (۰۸) کوڑے لگاؤ اور کبھی ان کی کوئی گواہی قبول نہ کرو اور یہ لوگ فاسق ہیں۔'),
(2796, 24, 5, 'سوائے ان کے جو اس کے بعد توبہ کر لیں اور (اپنی) اصلاح کر لیں۔ تو بےشک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2797, 24, 6, 'اور جو (خاوند) اپنی بیویوں پر تہمت (زنا) لگائیں اور (ثبوت کیلئے) ان کے پاس خود ان کے سوا کوئی گواہ نہ ہو تو ان میں سے کسی کی گواہی اس طرح معتبر ہوگی کہ وہ چار مرتبہ خدا کی قسم کھائے کہ وہ (اپنے دعویٰ میں) سچا ہے۔'),
(2798, 24, 7, 'اور پانچویں مرتبہ یوں کہے کہ اس پر اللہ کی لعنت ہو اگر وہ (اپنے دعویٰ میں) جھوٹا ہے۔'),
(2799, 24, 8, 'اور اس عورت سے یہ صورت شرعی حد کو ٹال سکتی ہے کہ وہ چار بار اللہ کی قسم کھا کر کہے کہ وہ (خاوند) جھوٹا ہے۔'),
(2800, 24, 9, 'اور پانچویں بار یوں قسم کھائے کہ اس پر اللہ کا غضب ہو اگر وہ (مرد) سچا ہے۔'),
(2801, 24, 10, 'اگر اللہ کا فضل و کرم اور اس کا رحم نہ ہوتا اور یہ (بات نہ ہوتی) کہ اللہ بڑا توبہ قبول کرنے والا، بڑا حکمت والا ہے (تو اس الزام تراشی کا انجام بڑا دردناک ہوتا)۔'),
(2802, 24, 11, 'بےشک جو لوگ جھوٹا بہتان گھڑ کر لائے ہیں وہ تم ہی میں سے ایک گروہ ہے۔ تم اسے اپنے حق میں برا نہ سمجھو۔ بلکہ وہ تمہارے لئے بہتر ہے۔ اس (گروہ) میں سے ہر آدمی کیلئے اتنا حصہ ہے جتنا اس نے گناہ کمایا ہے۔ اور جو ان میں سے اس (گناہ) کے بڑے حصہ کا ذمہ دار ہے اس کیلئے عذاب بھی بہت بڑا ہے۔'),
(2803, 24, 12, 'ایسا کیوں نہ ہوا کہ جب تم لوگوں نے یہ (بہتان) سنا تھا تو مؤمن مرد اور مؤمن عورتیں اپنوں کے حق میں نیک گمان کرتے اور کہتے کہ یہ تو ایک کھلا ہوا بہتان ہے۔'),
(2804, 24, 13, 'وہ اس پر چار گواہ کیوں نہیں لائے؟ سو جب یہ لوگ گواہ نہیں لائے تو یہ اللہ کے نزدیک جھوٹے ہی ہیں۔'),
(2805, 24, 14, 'اور اگر تم پر دنیا و آخرت میں خدا کا فضل و کرم اور اس کی رحمت نہ ہوتی تو جس بات میں تم پڑ گئے تھے (اور اس کا چرچا کیا تھا) تمہیں سخت عذاب پہنچتا۔'),
(2806, 24, 15, 'جب تم لوگ اس (بہتان) کو ایک دوسرے کی زبان سے نقل کر رہے تھے اور اپنے مونہوں سے وہ بات کہہ رہے تھے جس کا خود تمہیں علم نہیں تھا۔ اور تم اسے ایک معمولی بات سمجھ رہے تھے۔ حالانکہ اللہ کے نزدیک وہ بہت بڑی بات تھی۔'),
(2807, 24, 16, 'اور ایسا کیوں نہ ہوا کہ جب تم نے یہ (افواہ) سنی تھی تو کہہ دیتے کہ ہمارے لئے زیبا نہیں ہے کہ یہ بات منہ سے نکالیں۔ سبحان اللہ! یہ تو بڑا بہتان ہے۔'),
(2808, 24, 17, 'اللہ تمہیں نصیحت کرتا ہے کہ تم مؤمن ہو تو آئندہ کبھی اس قسم کی بات نہ کرنا۔'),
(2809, 24, 18, 'اور اللہ تمہارے لئے (اپنی) آیتیں کھول کر بیان کرتا ہے۔ اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(2810, 24, 19, 'اور جو لوگ چاہتے ہیں کہ اہلِ ایمان میں بے حیائی و برائی کی اشاعت ہو (اور اس کا چرچا ہو) ان کیلئے دنیا و آخرت میں دردناک عذاب ہے۔ اور اللہ جانتا ہے اور تم نہیں جانتے۔'),
(2811, 24, 20, 'اور اگر تم پر اللہ کا فضل و کرم اور اس کی رحمت نہ ہوتی اور یہ کہ اللہ بڑا شفقت والا، بڑا رحم کرنے والا ہے (تو تم دیکھ لیتے کہ اس بہتان تراشی کا کیا انجام ہوتا)۔'),
(2812, 24, 21, 'اے ایمان والو! شیطان کے نقش قدم پر نہ چلو۔ اور جو کوئی شیطان کے نقش قدم پر چلتا ہے تو وہ اسے بے حیائی اور ہر طرح کی برائی کا حکم دیتا ہے اور اگر تم پر خدا کا فضل و کرم اور اس کی رحمت نہ ہوتی تو تم میں سے کوئی بھی کبھی پاک و صاف نہ ہوتا۔ لیکن اللہ جسے چاہتا ہے اسے پاک و صاف کر دیتا ہے اور اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(2813, 24, 22, 'اور جو لوگ تم میں سے مال و دولت اور وسعت والے ہیں وہ قسم نہ کھائیں کہ وہ قرابتداروں، مسکینوں اور راہِ خدا میں ہجرت کرنے والوں کو کچھ نہیں دیں گے۔ چاہئے کہ یہ لوگ معاف کر دیں اور درگزر کریں۔ کیا تم یہ نہیں چاہتے کہ اللہ تمہیں بخش دے؟ اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2814, 24, 23, 'جو لوگ پاکدامن، بے خبر اور ایماندار عورتوں پر تہمت لگاتے ہیں ان پر دنیا و آخرت میں لعنت ہے اور ان کیلئے بہت بڑا عذاب ہے۔'),
(2815, 24, 24, 'جس دن ان کی زبانیں اور ان کے ہاتھ پاؤں ان کے خلاف گواہی دیں گے۔ ان کے اعمال کے متعلق جو وہ کیا کرتے تھے۔'),
(2816, 24, 25, 'اس دن اللہ انہیں پورا پورا بدلہ دے گا جس کے وہ حقدار ہیں اور انہیں معلوم ہو جائے گا کہ اللہ ہی حق ہے جو نمایاں ہے (اور حق کا ظاہر کرنے والا ہے)۔'),
(2817, 24, 26, 'ناپاک باتیں، ناپاک لوگوں کے لئے (زیبا) ہیں اور ناپاک آدمی ناپاک باتوں کیلئے موزوں (مناسب) ہیں اور اچھی اور ستھری باتیں اچھے آدمیوں کیلئے (مناسب) ہیں۔ اور اچھے اور ستھرے آدمی اچھی اور ستھتری باتوں کے (لائق) ہیں اور یہ ان باتوں سے بری الذمہ ہیں جو لوگ (ان کے بارے میں) کہتے ہیں ان کیلئے مغفرت ہے اور باعزت روزی۔'),
(2818, 24, 27, 'اے ایمان والو! اپنے گھروں کے سوا دوسرے گھروں میں داخل نہ ہوا کرو۔ جب تک کہ اجازت نہ لے لو۔ اور گھر والوں پر سلام نہ کرو۔ یہی تمہارے لئے بہتر ہے۔ تاکہ نصیحت حاصل کرو۔'),
(2819, 24, 28, 'پھر اگر ان (گھروں) میں کسی (آدمی) کو نہ پاؤ تو ان میں داخل نہ ہو جب تک کہ تمہیں اجازت نہ مل جائے اور اگر تم سے کہا جائے کہ واپس چلے جاؤ تو واپس چلے جاؤ یہ (طریقہ کار) تمہارے لئے زیادہ پاکیزہ ہے۔ اور تم جو کچھ کرتے ہو اللہ اسے خوب جانتا ہے۔'),
(2820, 24, 29, '(ہاں البتہ) ایسے گھروں میں داخل ہونے میں تمہارے لئے کوئی مضائقہ نہیں ہے جن میں کوئی رہائش نہیں رکھتا۔ جبکہ ان میں تمہارا کچھ سامان ہو اور اللہ اسے بھی خوب جانتا ہے جو کچھ تم ظاہر کرتے ہو اور اسے بھی جانتا ہے جو کچھ تم چھپاتے ہو۔'),
(2821, 24, 30, '(اے رسول(ص)) آپ مؤمن مردوں سے کہہ دیجئے! کہ وہ اپنی نگاہیں نیچی رکھیں اور اپنی شرمگاہوں کی حفاظت کریں یہ (طریقہ) ان کیلئے زیادہ پاکیزگی کا باعث ہے۔ بےشک لوگ جو کچھ کیا کرتے ہیں اللہ اس سے خوب واقف ہے۔'),
(2822, 24, 31, 'اور (اے رسول(ص)) آپ مؤمن عورتوں سے کہہ دیجئے! کہ وہ اپنی نگاہیں نیچی رکھیں اور اپنی شرمگاہوں کی حفاطت کریں اور اپنی زینت و آرائش کو ظاہر نہ کریں سوائے اس کے جو خود ظاہر ہو اور چاہیے کہ وہ اپنی اوڑھنیاں اپنے گریبانوں پر ڈالے رہیں اور اپنی زیبائش و آرائش ظاہر نہ کریں سوائے اپنے شوہروں کے، یا اپنے باپ داداؤں کے، یا اپنے شوہروں کے باپ داداؤں کے، یا اپنے بیٹوں کے یا اپنے شوہروں کے بیٹوں کے، یا اپنے بھائیوں کے یا اپنے بھتیجوں کے یا اپنے بھانجوں کے یا اپنے (ہم مذہب) عورتوں کے یا اپنے غلاموں یا لونڈیوں کے یا اپنے نوکروں چاکروں کے جو جنسی خواہش نہ رکھتے ہوں۔ یا ان بچوں کے جو ابھی عورتوں کی پردہ والی باتوں سے واقف نہیں ہیں اور وہ اپنے پاؤں (زمین پر) اس طرح نہ ماریں کہ جس سے ان کی وہ زیبائش و آرائش معلوم ہو جائے جسے وہ چھپائے ہوئے ہیں۔ اے اہل ایمان! تم سب مل کر اللہ کی بارگاہ میں توبہ کرو۔ تاکہ تم فلاح پاؤ۔'),
(2823, 24, 32, 'اور تم میں جو (مرد و زن) بے نکاح ہیں ان کا نکاح کرو۔ نیز اپنے غلاموں اور کنیزوں میں سے بھی جو (نکاح کے) قابل ہوں۔ ان کا بھی نکاح کرو۔ اگر وہ غریب و نادار ہوں گے تو اللہ انہیں اپنے فضل و کرم سے مالدار بنا دے گا۔ اور اللہ بڑی وسعت والا، بڑا علم والا ہے۔'),
(2824, 24, 33, 'اور جو لوگ نکاح کرنے کی قدرت نہیں رکھتے انہیں چاہیے کہ عفت اور ضبط نفس سے کام لیں۔ یہاں تک کہ اللہ انہیں اپنے فضل سے غنی کر دے اور تمہارے مملوکہ غلاموں اور کنیزوں میں سے جو مکاتب بننے کے خواہشمند ہوں۔ تو اگر تمہیں ان میں کوئی بھلائی معلوم ہو تو ان سے مکاتبہ کرلو۔ اور تم اللہ کے مال میں سے انہیں عطا کرو اور اپنی جوان کنیزوں کو محض دنیوی رنگ کا کچھ سامان حاصل کرنے کیلئے بدکاری پر مجبور نہ کرو جبکہ وہ پاکدامن رہنا چاہتی ہوں اگر کوئی انہیں مجبور کرے تو بیشک ان کو مجبور کرنے کے بعد بھی اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2825, 24, 34, 'بےشک ہم نے تم لوگوں کی طرف واضح آیتیں نازل کی ہیں اور تم سے پہلے گزرے ہوئے لوگوں کی مثالیں بھی اور پرہیزگاروں کیلئے پند و نصیحت بھی۔'),
(2826, 24, 35, 'اللہ آسمانوں اور زمین کا نور ہے۔ اس کے نور کی مثال یہ ہے جیسے ایک طاق میں چراغ رکھا ہوا ہو (اور) چراغ شیشہ کی قندیل میں ہو۔ اور (وہ) قندیل گویا موتی کی طرح چمکتا ہوا ایک ستارہ ہے (اور وہ چراغ) زیتون کے بابرکت درخت (کے تیل) سے روشن کیا جاتا ہے۔ جو نہ شرقی ہے نہ غربی قریب ہے کہ اس کا تیل خود بخود بھڑک اٹھے اگرچہ آگ نے اسے چھوا بھی نہ ہو۔ یہ نور بالائے نور ہے۔ اللہ جس کو چاہتا ہے اپنے نور کی طرف ہدایت فرماتا ہے۔ اور اللہ لوگوں کیلئے مثالیں بیان کرتا ہے۔ اور اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(2827, 24, 36, '(یہ ہدایت پانے والے) ایسے گھروں میں ہیں جن کے بارے میں اللہ نے حکم دیا ہے کہ انہیں بلند کیا جائے اور ان میں خدا کا نام لیا جائے ان میں ایسے لوگ صبح و شام اس کی تسبیح کرتے ہیں۔'),
(2828, 24, 37, 'جنہیں کوئی تجارت اور (خرید) فروخت اللہ کی یاد سے نماز پڑھنے سے اور زکوٰۃ دینے سے غافل نہیں کرتی (کیونکہ) وہ اس دن سے ڈرتے ہیں جس میں دل اور نگاہیں الٹ پلٹ ہو جائیں گی۔'),
(2829, 24, 38, 'تاکہ اللہ ان کو ان کے بہترین اعمال کی جزا دے اور اپنے فضل سے مزید نوازے اور اللہ جسے چاہتا ہے اسے بے حساب رزق عطا کرتا ہے۔'),
(2830, 24, 39, 'اور جن لوگوں نے کفر اختیار کیا ان کے اعمال سراب کی مانند ہیں جو ایک چٹیل میدان میں ہو۔ جسے آدمی پانی خیال کرتا ہے یہاں تک کہ جب اس کے پاس جاتا ہے تو اسے کچھ نہیں پاتا بلکہ وہاں اللہ کو موجود پاتا ہے۔ جس نے اس کا پورا پورا حساب چکا دیا۔ اور اللہ بہت جلدی حساب لینے والا ہے۔'),
(2831, 24, 40, 'یا پھر ان کی مثال ایسی ہے جیسے گہرے سمندر میں اندھیرا۔ کہ اسے ایک موج ڈھانپ لے پھر اس (موج) کے اوپر ایک اور موج ہو۔ اور پھر اس کے اوپر بادل ہو۔ الغرض اندھیروں پر اندھیرا کہ اگر کوئی اپنا ہاتھ نکالے تو اسے دیکھ نہ پائے اور جسے اللہ نور (ہدایت) نہ دے تو اس کے لئے کوئی نور نہیں ہے۔'),
(2832, 24, 41, 'کیا تم نے نہیں دیکھا کہ جو کوئی آسمانوں اور زمین میں ہیں اللہ کی تسبیح کرتے ہیں اور پر پھیلائے ہوئے پرندے بھی ہر ایک اپنی اپنی (مخصوص) نماز و تسبیح کو جانتا ہے۔'),
(2833, 24, 42, 'اور آسمانوں اور زمین کی بادشاہی اللہ ہی کیلئے ہے اور اللہ ہی کی طرف (سب کی) بازگشت ہے۔'),
(2834, 24, 43, 'کیا تم نے نہیں دیکھا کہ اللہ بادل کو آہستہ آہستہ چلاتا ہے۔ پھر اس کے ٹکڑوں کو باہم ملاتا ہے۔ پھر اسے تہہ بہ تہہ کر دیتا ہے پھر تم دیکھتے ہو کہ اس کے درمیان سے بارش نکلواتا ہے اور وہ (اللہ) آسمان سے پہاڑوں کی شکل کے بادلوں سے برف یعنی اولے برساتا ہے پھر ان کو جس پر چاہتا ہے گرا دیتا ہے اور جس سے چاہتا ہے ہٹا دیتا ہے قریب ہے کہ اس کی بجلی کی چمک آنکھوں (کی بینائی) کو لے جائے۔'),
(2835, 24, 44, 'اللہ رات دن کو ادلتا بدلتا رہتا ہے۔ بےشک اس میں آنکھوں والوں کیلئے (سامان) عبرت ہے۔'),
(2836, 24, 45, 'اور اللہ ہی نے زمین پر چلنے والے ہر جاندار کو (ایک خاص) پانی سے پیدا کیا ہے تو ان میں سے کوئی پیٹ کے بل چلتا ہے۔ اور کوئی دو ٹانگوں پر چلتا ہے اور کوئی چار ٹانگوں پر۔ اللہ جو چاہتا ہے پیدا کرتا ہے۔ بےشک اللہ ہر شئے پر پوری قدرت رکھتا ہے۔'),
(2837, 24, 46, 'یقیناً ہم نے حقیقت واضح کرنے والی آیتیں نازل کر دی ہیں اور اللہ جسے چاہتا ہے سیدھے راستے کی طرف راہنمائی کر دیتا ہے۔'),
(2838, 24, 47, 'اور وہ کہتے ہیں کہ ہم اللہ اور رسول پر ایمان لائے ہیں۔ اور ہم نے اطاعت بھی کی۔ مگر اس کے بعد ان میں سے ایک گروہ منہ پھر لیتا ہے اور ایسے لوگ (ہرگز) مؤمن نہیں ہیں۔'),
(2839, 24, 48, 'اور جب انہیں خدا اور رسول کی طرف بلایا جاتا ہے تاکہ رسول ان کے درمیان فیصلہ کر دیں تو ایک دم ان میں سے ایک گروہ روگردان ہو جاتا ہے۔'),
(2840, 24, 49, 'اور اگر حق ان کے موافق ہو (اس میں ان کا فائدہ) تو پھر سر تسلیم خم کئے اس (رسول) کی طرف آجاتے ہیں۔'),
(2841, 24, 50, 'ان کے دلوں میں (نفاق وغیرہ) کی کوئی بیماری ہے یا (اسلام کے متعلق) شک میں مبتلا ہیں یا پھر ڈرتے ہیں کہ اللہ اور اس کا رسول ان کے ساتھ زیادتی کریں گے؟ (نہیں) بلکہ یہ لوگ خود ظلم و زیادتی کرنے والے ہیں۔'),
(2842, 24, 51, 'اہلِ ایمان کو جب خدا اور رسول کی طرف بلایا جائے کہ وہ (رسول) ان کے درمیان فیصلہ کریں تو ان کا قول یہ ہوتا ہے کہ وہ کہتے ہیں ہم نے سنا اور اطاعت کی اور یہی لوگ فلاح پانے والے ہیں۔'),
(2843, 24, 52, 'اور جو کوئی اللہ اور اس کے رسول کی اطاعت کرے اور اللہ سے ڈرے اور پرہیزگاری اختیار کرے (اس کی نافرمانی سے بچے) تو ایسے ہی لوگ کامیاب ہونے والے ہیں۔'),
(2844, 24, 53, 'اور یہ (منافق) اپنے مقدور بھر اللہ کی قسمیں کھا کر کہتے ہیں کہ اگر آپ انہیں حکم دیں گے تو وہ (اپنے گھروں سے بھی) نکل کھڑے ہوں گے آپ کہیئے کہ تم قسمیں نہ کھاؤ۔ پس معروف و معلوم فرمانبرداری مطلوب ہے۔ بےشک جو کچھ تم کرتے ہو اللہ اس سے خوب واقف ہے۔'),
(2845, 24, 54, 'آپ کہہ دیجئے! کہ تم اللہ کی اطاعت کرو۔ اور رسول کی اطاعت کرو اور اگر تم روگردانی کروگے تو اس (رسول) کے ذمہ وہی ہے جو بار اس پر ڈالا گیا ہے اور تمہارے ذمہ وہ ہے جو بار تم پر ڈالا گیا ہے۔ اور تم اگر اس کی اطاعت کروگے تو خود ہی ہدایت پاؤگے۔ ورنہ رسول کے ذمہ واضح طور پر پیغام پہنچانے کے سوا کچھ نہیں ہے۔'),
(2846, 24, 55, 'جو لوگ تم میں سے ایمان لائے اور نیک عمل کئے اللہ نے ان سے وعدہ کیا ہے کہ وہ انہیں زمین میں اسی طرح جانشین بنائے گا جس طرح ان سے پہلے گزرے ہوئے لوگوں کو بنایا تھا۔ اور جس دین کو اللہ نے پسند کیا ہے وہ انہیں ضرور اس پر قدرت دے گا۔ اور ان کے خوف کو امن سے بدل دے گا۔ پس وہ میری عبادت کریں اور کسی کو میرا شریک نہ بنائیں۔ اور جو اس کے بعد کفر اختیار کرے وہی لوگ فاسق ہیں۔'),
(2847, 24, 56, 'اور نماز قائم کرو اور زکوٰۃ دو۔ اور رسول(ص) کی اطاعت کرو۔ تاکہ تم پر رحم کیا جائے۔'),
(2848, 24, 57, 'جو لوگ کافر ہیں ان کے بارے میں خیال نہ کرو کہ وہ زمین میں (خدا کو) عاجز کر دیں گے ان کا ٹھکانہ جہنم ہے اور وہ بہت ہی برا ٹھکانہ ہے۔'),
(2849, 24, 58, 'اے ایمان والو! چاہیے کہ تمہارے غلام اور وہ بچے جو ابھی حدِ بلوغ کو نہیں پہنچے (گھروں میں داخل ہوتے وقت) تین بار تم سے اجازت طلب کریں۔ نمازِ صبح سے پہلے، اور جس وقت تم دوپہر کو اپنے کپڑے اتار دیتے ہو اور نماز عشاء کے بعد۔ یہ تین وقت (آرام کرنے کیلئے) تمہارے لئے پردے کے وقت ہیں ان اوقات کے علاوہ تم پر اور ان پر کوئی حرج نہیں ہے تم لوگ ایک دوسرے کے پاس بار بار چکر لگاتے رہتے ہو۔ اسی طرح اللہ آیتوں کو کھول کر بیان کرتا ہے اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(2850, 24, 59, 'اور جب تمہارے لڑکے حدِ بلوغ کو پہنچ جائیں تو انہیں بھی اسی طرح اجازت لینی چاہیے جس طرح ان سے پہلے لوگ (اپنے بڑوں سے) اجازت لیتے رہے ہیں اسی طرح اللہ تعالیٰ تمہارے لئے اپنی آیتیں کھول کر بیان کرتا ہے۔ اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(2851, 24, 60, 'اور وہ عورتیں جو (بڑھاپے کی وجہ سے) خانہ نشین ہوگئی ہوں جنہیں نکاح کی کوئی آرزو نہ ہو ان کیلئے کوئی حرج نہیں ہے کہ وہ اپنے (پردہ کے) کپڑے اتار کر رکھ دیں بشرطیکہ زینت کی نمائش کرنے والیاں نہ ہوں۔ اور اگر اس سے بھی باز رہیں (اور پاکدامنی سے کام لیں) تو یہ اور بہتر ہے۔ اور اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(2852, 24, 61, 'اور اندھے کیلئے کوئی مضائقہ نہیں ہے اور نہ ہی لنگڑے کیلئے کوئی مضائقہ ہے اور نہ بیمار کیلئے اور نہ خود تمہارے لئے کوئی حرج ہے کہ تم اپنے گھروں سے کھاؤ۔ یا اپنے باپ دادا کے گھروں سے یا اپنی ماؤں کے گھروں سے یا اپنے بھائیوں یا اپنی بہنوں کے گھروں سے یا اپنے چچاؤں کے گھروں سے یا اپنی پھوپھیوں کے گھروں سے یا اپنے ماموؤں کے گھروں سے یا اپنی خالاؤں کے گھروں سے یا ان کے گھروں سے جن کی کنجیاں تمہارے اختیار میں ہیں یا اپنے دوستوں کے گھروں سے تمہارے لئے کوئی مضائقہ نہیں ہے کہ اکٹھے ہوکر کھاؤ۔ یا الگ الگ (ہاں البتہ) جب گھروں میں داخل ہونے لگو تو اپنے لوگوں کو سلام کر لیا کرو۔ یہ اللہ کی طرف سے پاکیزہ اور بابرکت ہدیہ ہے اللہ اسی طرح آیتیں کھول کر بیان کرتا ہے تاکہ تم عقل سے کام لو۔'),
(2853, 24, 62, 'مؤمن تو صرف وہ لوگ ہیں جو اللہ اور اس کے رسول(ص) پر ایمان رکھتے ہیں اور جب کسی اجتماعی معاملہ میں رسول کے ساتھ ہوتے ہیں تو جب تک آپ سے اجازت نہیں لیتے کہیں نہیں جاتے۔ بےشک جو لوگ آپ سے اجازت مانگتے ہیں وہی اللہ اور اس کے رسول پر ایمان رکھتے ہیں۔ پس جب وہ آپ سے اپنے کسی کام کیلئے اجازت مانگیں تو آپ ان میں سے جسے چاہیں اجازت دے دیں اور ان کیلئے اللہ سے مغفرت طلب کریں۔ بیشک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2854, 24, 63, 'اے ایمان والو! اپنے درمیان رسول کے بلانے کو آپس میں ایک دوسرے کو بلانے کی طرح نہ بناؤ۔ اللہ ان لوگوں کو خوب جانتا ہے جو تم میں سے ایک دوسرے کی آڑ لے کر کھسک جاتے ہیں۔ جو لوگ حکمِ خدا سے انحراف کرتے ہیں ان کو اس بات سے ڈرنا چاہیے کہ وہ کسی فتنہ میں مبتلا نہ ہو جائیں یا انہیں کوئی دردناک عذاب نہ پہنچ جائے۔'),
(2855, 24, 64, 'خبردار! جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے وہ اللہ ہی کا ہے تم جس حال میں ہو اللہ اسے خوب جانتا ہے اور اس دن کو بھی جب لوگ اس کی بارگاہ میں لوٹائے جائیں گے تو وہ انہیں بتائے گا جو کچھ وہ کرتے رہے تھے۔ اور اللہ ہر چیز کا بڑا جاننے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بابرکت ہے وہ خدا جس نے اپنے (خاص) بندہ پر فرقان نازل کیا ہے تاکہ وہ تمام جہانوں کیلئے ڈرانے والا بن جائے۔'),
(2857, 25, 2, 'جس کیلئے آسمانوں اور زمین کی بادشاہی ہے اور اس نے کسی کو اولاد نہیں بنایا۔ اور نہ بادشاہی میں اس کا کوئی شریک ہے اور اس نے ہر چیز کو پیدا کیا ہے اور ہر چیز کا ایک اندازہ (پیمانہ) مقرر کیا ہے۔'),
(2858, 25, 3, 'اور ان (مشرکوں) نے اللہ کو چھوڑ کر ایسے خدا بنائے ہیں جو کوئی چیز پیدا نہیں کر سکتے اور وہ خود پیدا کئے ہوئے ہیں اور وہ خود اپنے لئے نقصان یا نفع کے مالک و مختار نہیں ہیں۔ جو نہ مار سکتے ہیں نہ جلا سکتے ہیں اور نہ ہی کسی مرے ہوئے کو دوبارہ اٹھا سکتے ہیں۔'),
(2859, 25, 4, 'اور جو لوگ کافر ہیں وہ کہتے ہیں کہ یہ (قرآن) محض ایک جھوٹ ہے جسے اس شخص نے گھڑ لیا ہے اور کچھ دوسرے لوگوں نے اس کام میں اس کی مدد کی ہے (یہ بات کہہ کر) خود یہ لوگ بڑے ظلم اور جھوٹ کے مرتکب ہوئے ہیں۔'),
(2860, 25, 5, 'اور کہتے ہیں کہ یہ تو پہلے لوگوں کی لکھی ہوئی داستانیں ہیں جو اس شخص نے لکھوائی ہیں اور وہ صبح و شام اس کے سامنے پڑھی جاتی ہیں (یا اس سے لکھوائی جاتی ہیں)۔'),
(2861, 25, 6, 'آپ(ص) کہہ دیجئے! کہ اس (قرآن) کو اس (خدا) نے نازل کیا ہے جو آسمانوں اور زمین کے رازوں کو جانتا ہے۔ بےشک وہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2862, 25, 7, 'اور وہ کہتے ہیں کہ یہ کیسا رسول ہے جو کھانا کھاتا ہے اور بازاروں میں چلتا پھرتا ہے؟ اس پر کوئی فرشتہ کیوں نہ اتارا گیا جو اس کے ساتھ ڈرانے والا ہوتا؟'),
(2863, 25, 8, 'یا اس پر کوئی خزانہ اتارا جاتا یا اس کے لئے کوئی باغ ہی ہوتا جس سے یہ کھاتا؟ اور ظالموں نے تو (یہاں تک) کہہ دیا کہ تم ایک ایسے شخص کی پیروی کر رہے ہو جس پر جادو کیا گیا ہے۔'),
(2864, 25, 9, 'دیکھئے! یہ لوگ آپ کے متعلق کیسی کیسی (کٹ حجتیاں) باتیں بیان کرتے ہیں سو وہ گمراہ ہوگئے ہیں سو اب وہ راہ نہیں پا سکتے۔'),
(2865, 25, 10, 'بڑا بابرکت ہے وہ خدا جو اگر چاہے تو آپ کو اس (کفار کی بیان کردہ چیزوں) سے بہتر دے دے۔ یعنی ایسے باغات کہ جن کے نیچے نہریں جاری ہوں۔ اور آپ کیلئے (عالیشان) محل بنوا دے۔'),
(2866, 25, 11, 'بلکہ یہ لوگ قیامت کو جھٹلاتے ہیں اور جو لوگ قیامت کو جھٹلاتے ہیں ہم نے ان کیلئے بھڑکتی ہوئی آگ تیار کر رکھی ہے۔'),
(2867, 25, 12, 'اور وہ (آگ) جب انہیں دور سے دیکھے گی تو وہ اس کا جوش مارنا اور چنگھاڑنا سنیں گے۔'),
(2868, 25, 13, 'اور جب انہیں زنجیروں میں جکڑ کر آتش (دوزخ) کی کسی تنگ جگہ میں ڈال دیا جائے گا تو وہاں (اپنی) ہلاکت کو پکاریں گے۔'),
(2869, 25, 14, '(ان سے کہا جائے گا کہ) آج ایک ہلاکت کو نہ پکارو بلکہ بہت سی ہلاکتوں کو پکارو۔'),
(2870, 25, 15, 'آپ ان سے کہیے کہ آیا یہ (آتش دوزخ) اچھی ہے یا وہ دائمی جنت جس کا پرہیزگاروں سے وعدہ کیا گیا ہے۔ یہ ان (کے اعمال) کا صلہ ہے اور (آخری) ٹھکانا۔'),
(2871, 25, 16, 'اس میں ان کیلئے وہ سب کچھ ہوگا جو وہ چاہیں گے وہ اس میں ہمیشہ رہیں گے اور ایک وعدہ جو آپ کے رب کے ذمہ ہے جس کے متعلق سوال کیا جائے گا۔'),
(2872, 25, 17, 'اور جس دن اللہ ان لوگوں کو اور ان کے ان معبودوں کو جنہیں وہ اللہ کو چھوڑ کر پوجتے رہے ہیں۔ اکٹھا کرے گا تو اللہ ان (معبودوں) سے پوچھے گا کہ آیا تم نے میرے ان بندوں کو گمراہ کیا تھا؟ یا وہ خود ہی (سیدھے) راستہ سے بھٹک گئے تھے؟'),
(2873, 25, 18, 'وہ کہیں گے کہ پاک ہے تیری ذات ہمیں یہ حق نہیں تھا کہ ہم تجھے چھوڑ کر کسی اور کو اپنا مولا بنائیں لیکن تو نے انہیں اور ان کے باپ دادا کو خوب آسودگی اور آسائش عطا کی۔ یہاں تک کہ انہوں نے (تیری) یاد بھلا دی۔ اور اس طرح تباہ و برباد ہوگئے۔'),
(2874, 25, 19, '(اے کافرو) اس طرح وہ (تمہارے معبود) تمہاری ان باتوں کو جھٹلا دیں گے جو تم کرتے ہو۔ پھر نہ تو تم (عذاب کو) ٹال سکوگے اور نہ ہی اپنی کوئی مدد کر سکوگے۔ اور تم میں سے جو ظلم کرے گا ہم اسے بڑے عذاب کا مزہ چکھائیں گے۔'),
(2875, 25, 20, '(اے رسول) ہم نے آپ سے پہلے جتنے رسول بھیجے ہیں وہ جو سب کھانا بھی کھاتے تھے اور بازاروں میں چلتے پھرتے بھی تھے اور ہم نے تم کو ایک دوسرے کیلئے ذریعہ آزمائش بنایا ہے کیا تم صبر کروگے؟ اور آپ کا پروردگار بڑا دیکھنے والا ہے۔'),
(2876, 25, 21, 'اور جو لوگ ہمارے پاس آنے کی امید نہیں رکھتے وہ کہتے ہیں کہ ہمارے اوپر فرشتے کیوں نہیں اتارے گئے؟ یا ہم اپنے پروردگار کو ہی دیکھ لیتے! انہوں نے اپنے دلوں میں اپنے کو بہت بڑا سمجھا اور سرکشی میں حد سے گزر گئے ہیں۔'),
(2877, 25, 22, 'یہ لوگ جس دن فرشتوں کو دیکھیں گے تو اس دن مجرموں کیلئے کوئی بشارت نہیں ہوگی اور وہ کہیں گے پناہ ہے پناہ (یا حرام ہے حرام)۔'),
(2878, 25, 23, 'اور ہم ان کے ان کاموں کی طرف متوجہ ہوں گے جو انہوں نے کئے ہوں گے۔ اور انہیں پراگندہ غبار بنا دیں گے۔'),
(2879, 25, 24, 'اس دن بہشت والوں کا ٹھکانہ بہترین ہوگا اور دوپہر کی آرام گاہ بھی عمدہ ہوگی۔'),
(2880, 25, 25, 'اور جس دن بادل کے ساتھ آسمان شق ہو جائے گا اور فرشتے جوق در جوق اتارے جائیں گے۔'),
(2881, 25, 26, 'اس دن حقیقی بادشاہی (خدائے) رحمن کی ہوگی اور وہ دن کافروں کیلئے بڑا سخت ہوگا۔'),
(2882, 25, 27, 'اور اس دن ظالم (حسرت سے) اپنے ہاتھوں کو کاٹے گا (اور) کہے گا: کاش! میں نے رسول(ص) کے ساتھ (سیدھا) راستہ اختیار کیا ہوتا۔'),
(2883, 25, 28, 'ہائے میری بدبختی! کاش میں نے فلاں شخص کو اپنا دوست نہ بنایا ہوتا۔'),
(2884, 25, 29, 'اس نے نصیحت (یا قرآن) کے میرے پاس آجانے کے بعد مجھے اس کے قبول کرنے سے بہکا دیا اور شیطان تو ہے ہی (مشکل وقت میں) بے یار و مددگار چھوڑ دینے والا (دغا باز)۔'),
(2885, 25, 30, 'اور رسول(ص) کہیں گے اے میرے پروردگار! میری قوم (امت) نے اس قرآن کو بالکل چھوڑ دیا تھا۔'),
(2886, 25, 31, 'اور اسی طرح ہم نے ہر نبی کیلئے مجرموں میں سے بعض کو دشمن بنایا اور آپ کا پروردگار راہنمائی اور مدد کیلئے کافی ہے۔'),
(2887, 25, 32, 'اور کافر لوگ کہتے ہیں کہ اس شخص پر پورا قرآن یکبارگی کیوں نہیں نازل کیا گیا؟ ہاں اس طرح اس لئے کیا کہ ہم تمہارے دل کو ثبات و تقویت دیں اور (اسی لئے) اسے عمدہ ترتیب کے ساتھ ٹھہر ٹھہر کر نازل کیا ہے۔'),
(2888, 25, 33, 'اور یہ لوگ جب بھی کوئی (نیا) اعتراض اٹھاتے ہیں تو ہم اس کا صحیح جواب اور عمدہ تشریح آپ کے سامنے لاتے ہیں۔'),
(2889, 25, 34, 'وہ لوگ جو اپنے مونہوں کے بل جہنم کی طرف لے جائے جائیں گے وہ ٹھکانے کے لحاظ سے بدتر اور راستہ کے اعتبار سے گمراہ تر ہیں۔'),
(2890, 25, 35, 'بےشک ہم نے موسیٰ کو کتاب عطا کی اور ان کے ساتھ ان کے بھائی ہارون کو ان کا وزیر مقرر کیا۔'),
(2891, 25, 36, 'پھر ہم نے ان سے کہا کہ تم دونوں جاؤ اس قوم کی طرف جنہوں نے ہماری آیتوں کو جھٹلایا ہے (جب انہوں نے نہ مانا) تو ہم نے ان کو بالکل ہلاک و برباد کر دیا۔'),
(2892, 25, 37, 'اور نوح(ع) کی قوم کو بھی ہم نے غرق کر دیا جب انہوں نے رسولوں کو جھٹلایا۔ اور انہیں لوگوں کیلئے نشانِ عبرت بنا دیا اور ہم نے ظالموں کیلئے دردناک عذاب تیار کر رکھا ہے۔'),
(2893, 25, 38, 'اور (اسی طرح) ہم نے قومِ عاد و ثمود اور اصحاب الرس کو اور ان کے درمیان، بہت سی نسلوں (اور) قوموں کو بھی ہلاک کیا۔'),
(2894, 25, 39, 'اور ہم نے ہر ایک کیلئے (پہلے برباد ہونے والوں) کی مثالیں بیان کیں اور (آخرکار) ہم نے سب کو نیست و نابود کر دیا۔'),
(2895, 25, 40, 'اور (یہ لوگ) اس بستی سے گزرے ہیں! جس پر (پتھروں کی) بڑی بارش برسائی گئی تھی کیا (وہاں سے گزرتے ہوئے) اسے دیکھتے نہیں رہتے بلکہ (دراصل بات یہ ہے کہ) یہ حشر و نشر کی امید ہی نہیں رکھتے۔'),
(2896, 25, 41, 'اور وہ جب بھی آپ کو دیکھتے ہیں تو آپ کا مذاق اڑانے لگتے ہیں (اور کہتے ہیں) کیا یہ وہ ہے جسے خدا نے رسول بنا کر بھیجا ہے؟'),
(2897, 25, 42, 'وہ تو قریب تھا کہ یہ شخص ہمارے معبودوں سے ہمیں بہکا دے۔ اگر ہم ان (کی پرستش) پر ثابت قدم نہ رہتے تو عنقریب جب وہ عذاب (الٰہی) کو دیکھیں گے تو انہیں معلوم ہو جائے گا کہ راہِ راست سے کون زیادہ بھٹکا ہوا ہے؟'),
(2898, 25, 43, '(اے رسول(ص)) کیا آپ نے وہ شخص دیکھا ہے جس نے اپنی خواہش (نفس) کو اپنا خدا بنا رکھا ہے؟ کیا آپ اس کے ذمہ دار ہو سکتے ہیں؟'),
(2899, 25, 44, 'کیا آپ خیال کرتے ہیں کہ ان میں سے اکثر سنتے یا سمجھتے ہیں؟ یہ تو محض چوپایوں کی طرح ہیں بلکہ ان سے بھی زیادہ گم کردہ راہ ہیں۔'),
(2900, 25, 45, 'کیا تم نے اپنے پروردگار کی (قدرت کی) طرف نہیں دیکھا کہ اس نے سایہ کو کیونکر پھیلایا ہے؟ اور اگر وہ چاہتا تو اسے (ایک جگہ) ٹھہرا ہوا بنا دیتا۔ پھر ہم نے سورج کو اس پر دلیل راہ بنایا۔'),
(2901, 25, 46, 'پھر ہم اس (سایہ) کو تھوڑا تھوڑا کرکے اپنی طرف سمیٹتے جاتے ہیں۔'),
(2902, 25, 47, 'اور وہ وہی ہے جس نے تمہارے لئے رات کو پردہ پوش اور نیند کو (باعث) راحت اور دن کو اٹھ کھڑے ہونے کا وقت بنایا۔'),
(2903, 25, 48, 'اور وہ وہی ہے جو ہواؤں کو اپنی رحمت (بارش) سے پہلے خوشخبری بنا کر بھیجتا ہے۔ اور ہم آسمان سے پاک اور پاک کرنے والا پانی برساتے ہیں تاکہ اس کے ذریعہ کسی مردہ شہر (غیر آباد) کو زندہ کریں اور اپنی مخلوق میں سے بہت سے جانوروں اور انسانوں کو پلائیں۔'),
(2904, 25, 49, 'اور ہم اس (پانی) کو طرح طرح سے لوگوں کے درمیان تقسیم کرتے ہیں تاکہ وہ نصیحت حاصل کریں مگر اکثر لوگوں نے ناشکری کے سوا ہر بات سے انکار کر دیا ہے۔'),
(2905, 25, 50, 'اور اگر ہم چاہتے تو ایک ایک بستی میں ایک ایک ڈرانے والا بھیجتے۔'),
(2906, 25, 51, '(اے نبی(ص)) آپ کافروں کی پیروی نہ کریں اور اس (قرآن) کے ذریعہ سے ان سے بڑا جہاد کریں۔'),
(2907, 25, 52, 'اور وہ وہی ہے جس نے دو دریاؤں کو آپس میں ملا دیا ہے یہ شیریں و خوشگوار ہے اور یہ سخت کھاری و تلخ ہے اور ان دونوں کے درمیان ایک حد فاصل اور مضبوط رکاوٹ بنا دی ہے۔'),
(2908, 25, 53, 'اور وہ وہی ہے جس نے پانی سے انسان کو پیدا کیا اور پھر اس کو خاندان والا اور سسرال والا بنایا اور تمہارا پروردگار بڑی قدرت والا ہے۔'),
(2909, 25, 54, 'اور وہ (مشرک) اللہ کو چھوڑ کر ان کی پرستش کرتے ہیں جو ان کو نہ نفع پہنچا سکتے ہیں نہ نقصان اور کافر تو (ہمیشہ) اپنے پروردگار کے مقابلے میں (مخالفوں کی) پشت پناہی کرتا ہے۔'),
(2910, 25, 55, 'اور ہم نے آپ کو نہیں بھیجا مگر خوشخبری دینے والا اور ڈرانے والا بنا کر۔'),
(2911, 25, 56, 'اور آپ کہہ دیجئے! کہ میں اس (کار رسالت) پر تم سے کوئی معاوضہ نہیں مانگتا سوائے اس کے کہ جو چاہے اپنے پروردگار کا راستہ اختیار کرے۔'),
(2912, 25, 57, '(اے رسول(ص)) آپ اس زندہ (خدا) پر بھروسہ کیجئے جس کے لئے موت نہیں ہے۔ اور اس کی حمد کے ساتھ تسبیح کیجئے اور وہ اپنے بندوں کے گناہوں کی خبر رکھنے کیلئے خود کافی ہے۔'),
(2913, 25, 58, 'وہ خدا جس نے آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے سب کو چھ (۶) دن میں پیدا کیا۔ پھر عرش پر اقتدار قائم کیا۔ وہی (خدائے) رحمن ہے اس کے بارے میں کسی باخبر سے پوچھو۔'),
(2914, 25, 59, 'اور جب ان سے کہا جاتا ہے کہ رحمن کو سجدہ کرو۔ تو وہ کہتے ہیں کہ یہ رحمن کیا چیز ہے؟ کیا ہم اسے سجدہ کریں جس کے بارے میں تم ہمیں حکم دو؟ اور یہ چیز ان کی نفرت میں اور اضافہ کر دیتی ہے۔'),
(2915, 25, 60, 'بڑا بابرکت ہے وہ (خدا) جس نے آسمان میں (بارہ) برج بنائے اور ان میں ایک چراغ (سورج) اور ایک چمکتا ہوا چاند بنایا۔'),
(2916, 25, 61, 'اور وہ وہی ہے جس نے رات اور دن کو ایک دوسرے کا جانشین بنایا ہے اس شخص کیلئے جو نصیحت حاصل کرنا چاہے یا شکر ادا کرنا چاہے۔'),
(2917, 25, 62, 'اور (خدائے) رحمن کے (خاص) بندے وہ ہیں جو زمین پر آہستہ آہستہ (فروتنی کے ساتھ) چلتے ہیں اور جب جاہل لوگ ان سے خطاب کرتے ہیں تو وہ کہتے ہیں تم پر سلام۔'),
(2918, 25, 63, 'اور جو اپنے پروردگار کی بارگاہ میں سجدہ اور قیام کرتے ہوئے رات گزارتے ہیں۔'),
(2919, 25, 64, 'اور جو کہتے ہیں کہ اے ہمارے پروردگار! ہم سے جہنم کا عذاب دور رکھ۔'),
(2920, 25, 65, 'بےشک اس کا عذاب پوری ہلاکت ہے۔'),
(2921, 25, 66, 'وہ (جہنم) بہت برا ٹھکانا ہے اور بہت بری جگہ ہے۔'),
(2922, 25, 67, 'اور وہ جب خرچ کرتے ہیں تو نہ فضول خرچی کرتے ہیں اور نہ کنجوسی (بلکہ) ان کا خرچ کرنا ان (دونوں) کے درمیان (حد اعتدال پر) ہوتا ہے۔'),
(2923, 25, 68, 'اور وہ اللہ کے ساتھ کسی اور خدا کو نہیں پکارتے اور جس جان (کے مارنے) کو اللہ نے حرام قرار دیا ہے اسے ناحق قتل نہیں کرتے اور نہ زنا کرتے ہیں اور جو کوئی ایسا کرے گا تو وہ گناہ کی سزا پائے گا۔'),
(2924, 25, 69, 'قیامت کے دن اس کا عذاب دوگنا کر دیا جائے گا۔ اور وہ اس میں ذلیل و خوار ہوکر ہمیشہ رہے گا۔'),
(2925, 25, 70, 'سوائے اس کے جو توبہ کرے اور ایمان لائے اور نیک عمل کرے کہ اللہ ایسے لوگوں کی برائیوں کو بھلائیوں سے بدل دے گا اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(2926, 25, 71, 'اور جو کوئی توبہ کرے اور نیک عمل کرے تو وہ اللہ کی طرف اس طرح رجوع کرتا ہے جو رجوع کرنے کا حق ہے۔'),
(2927, 25, 72, 'اور جو جھوٹی گواہی نہیں دیتے (یا جو زُور یعنی غنا کی جگہ پر حاضر نہیں ہوتے) اور جو جب کسی بیہودہ کام کے پاس سے گزرتے ہیں تو شریفانہ انداز میں گزر جاتے ہیں۔'),
(2928, 25, 73, 'اور جب انہیں ان کے پروردگار کی آیتوں کے ذریعہ سے نصیحت کی جاتی ہے تو وہ بہرے اور اندھے ہوکر ان پر گر نہیں پڑتے (بلکہ ان میں غور و فکر کرتے ہیں)۔'),
(2929, 25, 74, 'اور وہ کہتے ہیں اے ہمارے پروردگار! ہمیں ہماری بیویوں اور ہماری اولاد کی طرف سے آنکھوں کی ٹھنڈک عطا فرما اور ہمیں پرہیزگاروں کا پیش رو بنا۔'),
(2930, 25, 75, 'یہ وہ ہیں جنہیں ان کے صبر و ثبات کے صلہ میں بہشت میں بالاخانہ عطا کیا جائے گا۔ اور وہاں ان کا تحیہ و سلام سے استقبال کیا جائے گا۔'),
(2931, 25, 76, 'وہ ہمیشہ اس میں رہیں گے کیا اچھا ہے وہ ٹھکانہ اور وہ مقام۔'),
(2932, 25, 77, 'آپ کہہ دیجئے! اگر تمہاری دعا و پکار نہ ہو تو میرا پروردگار تمہاری کوئی پروا نہ کرے سو اب جبکہ تم نے (رسول کو) جھٹلا دیا ہے تو یہ (تکذیب) عنقریب (تمہارے لئے) وبال جان بن کر رہے گی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سین، میم۔'),
(2934, 26, 2, 'یہ ایک واضح کتاب کی آیتیں ہیں۔'),
(2935, 26, 3, 'شاید آپ (اس غم میں) جان دے دیں گے کہ یہ لوگ ایمان نہیں لاتے۔'),
(2936, 26, 4, 'اگر ہم چاہیں تو ان پر آسمان سے کوئی ایسی نشانی اتاریں جس کے آگے ان کی گردنیں جھک جائیں۔'),
(2937, 26, 5, 'اور جب بھی ان کے پاس خدائے رحمن کی طرف سے کوئی نئی نصیحت آتی ہے تو یہ اس سے منہ پھیر لیتے ہیں۔'),
(2938, 26, 6, 'بےشک یہ جھٹلا چکے ہیں تو عنقریب ان کے سامنے اس چیز کی خبریں آئیں گی جس کا یہ مذاق اڑاتے رہے ہیں۔'),
(2939, 26, 7, 'کیا انہوں نے زمین کی طرف (غور سے) نہیں دیکھا کہ ہم نے اس میں کتنی کثرت سے عمدہ نباتات اگائی ہیں۔'),
(2940, 26, 8, 'بےشک اس کے اندر ایک بڑی نشانی ہے لیکن ان میں سے اکثر ایمان لانے والے نہیں ہیں۔'),
(2941, 26, 9, 'اور بےشک آپ کا پروردگار بڑا غالب، بڑا رحم کرنے والا ہے۔'),
(2942, 26, 10, '(اے رسول(ص)) وہ وقت یاد کرو جب آپ کے پروردگار نے موسیٰ کو پکارا کہ ظالم قوم کے پاس جاؤ۔'),
(2943, 26, 11, 'یعنی فرعون کی قوم کے پاس۔ کیا وہ نہیں ڈرتے؟'),
(2944, 26, 12, 'موسیٰ نے کہا اے میرے پروردگار! میں ڈرتا ہوں کہ وہ مجھے جھٹلائیں گے۔'),
(2945, 26, 13, 'اور میرا سینہ تنگ ہوتا ہے اور میری زبان نہیں چلتی سو ہارون کے پاس (وحی) بھیج (کہ وہ میرا ساتھ دے)۔'),
(2946, 26, 14, 'اور ان لوگوں کا میرے ذمہ ایک جرم بھی ہے اس لئے مجھے اندیشہ ہے کہ وہ مجھے قتل کر دیں گے۔'),
(2947, 26, 15, 'ارشاد ہوا ہرگز نہیں! تم دونوں ہماری نشانیاں لے کر جاؤ ہم تمہارے ساتھ (سب کچھ) سن رہے ہیں۔'),
(2948, 26, 16, 'پس تم دونوں فرعون کے پاس جاؤ۔ اور اس سے کہو کہ ہم تمام جہانوں کے پروردگار کے رسول ہیں۔'),
(2949, 26, 17, 'کہ تو بنی اسرائیل کو ہمارے ساتھ بھیج دے۔'),
(2950, 26, 18, 'فرعون نے کہا (اے موسیٰ) کیا تمہارے بچپن میں ہم نے تمہاری پرورش نہیں کی ہے؟ اور تو نے اپنی عمر کے کئی سال ہم میں گزارے ہیں۔'),
(2951, 26, 19, 'اور تو نے اپنی وہ حرکت بھی کی تھی جو کی تھی اور تو ناشکروں میں سے ہے۔'),
(2952, 26, 20, 'موسیٰ نے کہا: ہاں میں نے وہ کام اس وقت کیا تھا جب میں راستہ سے بھٹکا ہوا تھا۔'),
(2953, 26, 21, 'تو جب میں تم سے ڈرا تو بھاگ کھڑا ہوا۔ اس کے بعد میرے پروردگار نے مجھے علم و حکمت عطا کیا اور مجھے رسولوں میں سے قرار دے دیا۔'),
(2954, 26, 22, 'اور یہی (پرورش وغیرہ کا) وہ احسان ہے جو تو مجھے جتا رہا ہے جب کہ تو نے بنی اسرائیل کو غلام بنا رکھا ہے۔'),
(2955, 26, 23, 'فرعون نے کہا اور یہ رب العالمین کیا چیز ہے۔'),
(2956, 26, 24, 'موسیٰ نے کہا وہ آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے سب کا پروردگار ہے۔ اگر تم یقین کرنے والے ہو۔'),
(2957, 26, 25, 'فرعون نے اپنے اردگرد والوں سے کہا کیا تم سنتے نہیں ہو؟'),
(2958, 26, 26, 'موسیٰ نے کہا وہ تمہارا بھی پروردگار ہے اور تمہارے اگلے آباء و اجداد کا بھی پروردگار۔'),
(2959, 26, 27, 'فرعون نے کہا تمہارا یہ رسول جو تمہاری طرف بھیجا گیا ہے یہ تو بالکل دیوانہ ہے۔'),
(2960, 26, 28, 'موسیٰ نے کہا کہ وہ مشرق و مغرب اور جو کچھ ان کے درمیان ہے سب کا پروردگار ہے اگر تم عقل سے کام لو۔'),
(2961, 26, 29, 'فرعون نے کہا کہ اگر تو نے میرے سوا کوئی معبود بنایا تو میں تمہیں قیدیوں میں شامل کر دوں گا۔'),
(2962, 26, 30, 'موسیٰ نے کہا: اگرچہ میں تیرے پاس کوئی واضح نشانی بھی لے کر آؤں؟'),
(2963, 26, 31, 'فرعون نے کہا: اچھا تو لے آ۔ اگر تو سچا ہے۔'),
(2964, 26, 32, 'اس پر موسیٰ نے اپنا عصا پھینک دیا تو ایک دم وہ کھلا ہوا اژدھا بن گیا۔'),
(2965, 26, 33, 'اس نے اپنا ہاتھ نکالا تو وہ ایک دم دیکھنے والوں کیلئے چمک رہا تھا۔'),
(2966, 26, 34, 'فرعون نے اپنے اردگرد کے عمائدین سے کہا کہ یہ تو بڑا ماہر جادوگر ہے۔'),
(2967, 26, 35, 'یہ اپنے جادو (کے زور) سے تمہیں اپنے ملک سے باہر نکالنا چاہتا ہے۔ اب تم کیا رائے دیتے ہو؟'),
(2968, 26, 36, 'انہوں نے کہا اسے اور اس کے بھائی کو روک لیجئے اور تمام شہروں میں جمع کرنے والے آدمی (ہرکارے) بھیجئے۔'),
(2969, 26, 37, 'جو ہر بڑے جادوگر کو تمہارے پاس لے آئیں۔'),
(2970, 26, 38, 'چنانچہ تمام جادوگر ایک خاص مقررہ وقت پر جمع کر لئے گئے۔'),
(2971, 26, 39, 'اور عام لوگوں سے کہا گیا۔ کیا تم لوگ بھی (مقابلہ دیکھنے کیلئے) جمع ہوگے؟'),
(2972, 26, 40, 'تاکہ اگر جادوگر غالب آجائیں تو ہم سب ان کی پیروی کریں۔'),
(2973, 26, 41, 'تو جب جادوگر (میدان میں) آگئے تو انہوں نے فرعون سے کہا کہ اگر ہم غالب آگئے تو ہمیں کچھ صلہ تو ملے گا۔'),
(2974, 26, 42, 'فرعون نے کہا: ہاں ضرور! اور تم اس وقت مقرب بارگاہ ہو جاؤگے۔'),
(2975, 26, 43, 'موسیٰ نے ان سے کہا کہ تم پھینکو جو کچھ تمہیں پھینکنا ہے۔'),
(2976, 26, 44, 'چنانچہ انہوں نے اپنی رسیاں اور اپنی لاٹھیاں پھینک دیں اور کہا کہ فرعون کے جلال و اقبال کی قَسم ہم ہی غالب آئیں گے۔'),
(2977, 26, 45, 'پھر موسیٰ نے اپنا عصا پھینکا تو وہ ایک دم ان کے بنائے ہوئے جھوٹ کو نگلنے لگا۔'),
(2978, 26, 46, '(یہ معجزہ دیکھ کر) سب جادوگر سجدہ میں گر پڑے۔'),
(2979, 26, 47, 'اور کہنے لگے کہ ہم رب العالمین پر ایمان لائے۔'),
(2980, 26, 48, 'جو موسیٰ و ہارون کا رب ہے فرعون نے کہا تم اس پر ایمان لائے قبل اس کے کہ میں تمہیں اجازت دوں یقیناً یہ تمہارا بڑا (جادوگر) ہے۔'),
(2981, 26, 49, 'جس نے تمہیں جادو سکھایا ہے ابھی تمہیں (اس کا انجام) معلوم ہو جائے گا کہ میں تمہارے ہاتھ پاؤں کو مخالف سمتوں سے کٹوا دوں گا۔ اور تم سب کو سولی چڑھا دوں گا۔'),
(2982, 26, 50, 'انہوں نے کہا کوئی جرم نہیں ہم اپنے پروردگار کی بارگاہ میں لوٹ جائیں گے۔'),
(2983, 26, 51, 'ہمیں امید ہے کہ وہ ہماری خطاؤں کو معاف کرے گا کیونکہ ہم سب سے پہلے ایمان لا رہے ہیں۔'),
(2984, 26, 52, 'اور ہم نے موسیٰ کو وحی کی کہ راتوں رات میرے بندوں کو لے کر نکل جاؤ کیونکہ تمہارا پیچھا کیا جائے گا۔'),
(2985, 26, 53, 'پس فرعون نے تمام شہروں میں (لشکر) جمع کرنے والے (ہرکارے) بھیجے۔'),
(2986, 26, 54, '(اور کہلا بھیجا کہ) یہ لوگ ایک چھوٹی سی جماعت ہے۔'),
(2987, 26, 55, 'اور انہوں نے ہمیں بہت غصہ دلایا ہے۔'),
(2988, 26, 56, 'اور بےشک ہم پورے محتاط اور چوکنا ہیں۔'),
(2989, 26, 57, 'پھر ہم نے انہیں (مصر کے) باغوں اور چشموں'),
(2990, 26, 58, 'اور خزانوں اور شاندر عمدہ مکانوں سے نکال باہر کیا۔'),
(2991, 26, 59, 'ایسا ہی ہوا اور ہم نے ان چیزوں کا وارث بنی اسرائیل کو بنایا۔'),
(2992, 26, 60, 'چنانچہ صبح تڑکے وہ لوگ (فرعونی) ان کے تعاقب میں نکلے۔'),
(2993, 26, 61, 'پس جب دونوں جماعتیں ایک دوسرے کو دیکھنے لگیں (آمنے سامنے ہوئیں) تو موسیٰ کے ساتھیوں نے (گھبرا کر) کہا کہ بس ہم تو پکڑے گئے۔'),
(2994, 26, 62, 'موسیٰ نے کہا ہرگز نہیں! یقیناً میرا پروردگار میرے ساتھ ہے جو ضرور میری راہنمائی کرے گا۔'),
(2995, 26, 63, 'سو ہم نے موسیٰ کو وحی کی کہ اپنا عصا دریا پر مارو۔ چنانچہ وہ دریا پھٹ گیا اور (پانی کا) ہر حصہ ایک بڑے پہاڑ کی طرح ہوگیا۔'),
(2996, 26, 64, 'اور ہم وہاں دوسرے فریق کو بھی نزدیک لائے۔'),
(2997, 26, 65, 'اور ہم نے موسیٰ اور ان کے ساتھیوں کو نجات دی۔'),
(2998, 26, 66, 'اور پھر ہم نے دوسرے فریق کو غرق کر دیا۔'),
(2999, 26, 67, 'بےشک اس واقعہ میں ایک بڑی نشانی ہے مگر ان سے اکثر لوگ ایمان لانے والے نہیں ہیں۔'),
(3000, 26, 68, 'اور بےشک آپ کا پروردگار بڑا غالب آنے والا، بڑا رحم کرنے والا ہے۔'),
(3001, 26, 69, 'اور آپ ان لوگوں کے سامنے ابراہیم (ع) کا قصہ بیان کیجئے۔'),
(3002, 26, 70, 'جب انہوں نے اپنے باپ (یعنی چچا) اور اپنی قوم سے کہا کہ تم کس چیز کی پرستش کرتے ہو؟'),
(3003, 26, 71, 'انہوں نے کہا کہ ہم تو بتوں کی پرستش کرتے ہیں اور اس پر قائم ہیں۔'),
(3004, 26, 72, 'ابراہیم نے کہا کہ جب تم انہیں پکارتے ہو تو کیا یہ تمہاری آواز سنتے ہیں۔'),
(3005, 26, 73, 'یا تمہیں کچھ نفع یا نقصان پہنچاتے ہیں؟'),
(3006, 26, 74, 'انہوں نے کہا بلکہ ہم نے اپنے باپ داداؤں کو ایسے ہی کرتے پایا ہے۔'),
(3007, 26, 75, 'ابراہیم نے کہا کیا تم نے ان چیزوں کی (اصل حقیقت) دیکھی ہے کہ جن کی تم'),
(3008, 26, 76, 'اور تمہارے باپ دادا پرستش کرتے رہے ہیں؟ (کہ وہ کیا ہے؟)'),
(3009, 26, 77, 'بہرکیف یہ سب میرے دشمن ہیں سوائے رب العالمین کے۔'),
(3010, 26, 78, 'جس نے مجھے پیدا کیا ہے۔ پھر وہی میری راہنمائی کرتا ہے۔'),
(3011, 26, 79, 'اور وہ جو مجھے کھلاتا ہے اور پلاتا بھی۔'),
(3012, 26, 80, 'اور جب میں بیمار ہوتا ہوں تو وہ مجھے شفا دیتا ہے۔'),
(3013, 26, 81, 'اور جو مجھے موت دے گا اور پھر مجھے (دوبارہ) زندہ کرے گا۔'),
(3014, 26, 82, 'اور جس سے میں امید رکھتا ہوں کہ وہ جزا و سزا کے دن میری خطا معاف کر دے گا۔'),
(3015, 26, 83, 'اے میرے پروردگار! مجھے (علم و حکمت) عطا فرما۔ اور مجھے نیکوکاروں میں شامل فرما۔'),
(3016, 26, 84, 'اور آئندہ آنے والوں میں میرا ذکرِ خیر جاری رکھ۔'),
(3017, 26, 85, 'اور مجھے جنتِ نعیم کے وارثوں میں شامل فرما۔'),
(3018, 26, 86, 'اور میرے باپ (یعنی چچا) کی مغفرت فرما۔ بےشک وہ گمراہوں میں سے تھا۔'),
(3019, 26, 87, 'اور جس دن لوگ (قبروں سے) اٹھائے جائیں گے اس دن مجھے رسوا نہ کر۔'),
(3020, 26, 88, 'جس دن نہ مال فائدہ دے گا اور نہ اولاد۔'),
(3021, 26, 89, 'اور سوائے اس کے جو اللہ کی بارگاہ میں قلبِ سلیم کے ساتھ حاضر ہوگا۔'),
(3022, 26, 90, 'اور جنت پرہیزگاروں کے قریب کر دی جائے گی۔'),
(3023, 26, 91, 'اور دوزخ گمراہوں کے سامنے ظاہر کر دی جائے گی۔'),
(3024, 26, 92, 'اور ان سے کہا جائے گا کہ تمہارے وہ معبود کہاں ہیں جن کی تم اللہ کو چھوڑ کر عبادت کرتے تھے؟'),
(3025, 26, 93, 'کیا وہ (آج) تمہاری کچھ مدد کرتے ہیں یا وہ اپنا ہی بچاؤ کر سکتے ہیں؟'),
(3026, 26, 94, 'پھر وہ معبود اور (یہ) گمراہ لوگ۔'),
(3027, 26, 95, 'اور ابلیس اور اس کے سارے لشکر اس (دوزخ) میں اندھے منہ ڈال دیئے جائیں گے۔'),
(3028, 26, 96, 'اور وہ دوزخ میں باہم جھگڑتے ہوئے کہیں گے۔'),
(3029, 26, 97, 'خدا کی قَسم ہم کھلی ہوئی گمراہی میں تھے۔'),
(3030, 26, 98, 'جب تمہیں رب العالمین کے برابر قرار دیتے تھے۔'),
(3031, 26, 99, 'اور ہم کو تو بس (بڑے) مجرموں نے گمراہ کیا تھا۔'),
(3032, 26, 100, 'نہ اب ہمارا کوئی سفارشی ہے۔'),
(3033, 26, 101, 'اور نہ کوئی مخلص دوست۔'),
(3034, 26, 102, 'کاش! ہم دوبارہ (دنیا میں) بھیجے جاتے تو ضرور ہم اہلِ ایمان سے ہو جاتے۔'),
(3035, 26, 103, 'بےشک اس میں بڑی نشانی ہے مگر ان میں سے اکثر ایمان لانے والے نہیں ہیں۔'),
(3036, 26, 104, 'اور یقیناً آپ کا پروردگار بڑا غالب اور بڑا رحم کرنے والا ہے۔'),
(3037, 26, 105, 'نوح(ع) کی قوم نے پیغمبروں کو جھٹلایا۔'),
(3038, 26, 106, 'جب کہ ان کے بھائی نوح نے ان سے کہا کیا تم ڈرتے نہیں ہو؟ ۔'),
(3039, 26, 107, 'بےشک میں تمہارے لئے امانت دار رسول ہوں۔'),
(3040, 26, 108, 'سو تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3041, 26, 109, 'اور میں تم سے اس (تبلیغ) پر کوئی اجرت نہیں مانگتا۔ میری اجرت تو رب العالمین کے ذمہ ہے۔'),
(3042, 26, 110, 'پس تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3043, 26, 111, 'انہوں نے کہا ہم کس طرح تجھے مان لیں (اور اطاعت کریں) جبکہ رذیل لوگ تمہاری پیروی کر رہے ہیں۔'),
(3044, 26, 112, 'آپ نے کہا مجھے اس کی کیا خبر (اور کیا غرض؟) جو وہ کرتے رہے ہیں؟'),
(3045, 26, 113, 'ان کا حساب کتاب کرنا تو میرے پروردگار ہی کا کام ہے کاش تم شعور سے کام لیتے۔'),
(3046, 26, 114, 'اور میں اہلِ ایمان کو دھتکارنے والا نہیں ہوں۔'),
(3047, 26, 115, 'میں تو صرف ایک کھلا ہوا ڈرانے والا ہوں۔'),
(3048, 26, 116, 'ان لوگوں نے کہا اے نوح! اگر تم باز نہ آئے تو تم ضرور سنگسار کر دیئے جاؤگے۔'),
(3049, 26, 117, 'آپ نے کہا اے میرے پروردگار! میری قوم نے تو مجھے جھٹلایا دیا ہے۔'),
(3050, 26, 118, 'پس اب تو ہی میرے اور ان کے درمیان فیصلہ فرما اور مجھے اور میرے ساتھ ایمان لانے والوں کو نجات دے۔'),
(3051, 26, 119, 'پس ہم نے انہیں اور جو اس بھری ہوئی کشتی میں ان کے ساتھ تھے سب کو نجات دی۔'),
(3052, 26, 120, 'پھر اس کے بعد باقی لوگوں کو غرق کر دیا۔'),
(3053, 26, 121, 'بےشک اس میں ایک بڑی نشانی ہے (مگر) ان میں سے اکثر لوگ ایمان نہیں لانے والے ہیں۔'),
(3054, 26, 122, 'اور آپ کا پروردگار بڑا غالب ہے، بڑا رحم کرنے والا ہے۔'),
(3055, 26, 123, '(اسی طرح قوم) عاد نے بھی رسولوں کو جھٹلایا۔'),
(3056, 26, 124, 'جبکہ ان کے بھائی ہود نے ان سے کہا کیا تم ڈرتے نہیں ہو؟'),
(3057, 26, 125, 'بےشک میں تمہارے لئے امانت دار رسول ہوں۔'),
(3058, 26, 126, 'سو تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3059, 26, 127, 'اور میں تم سے اس (تبلیغ) پر کوئی اجرت نہیں مانگتا۔ میری اجرت تو رب العالمین کے ذمہ ہے۔'),
(3060, 26, 128, 'کیا تم ہر بلندی پر بے فائدہ ایک یادگار تعمیر کرتے ہو؟'),
(3061, 26, 129, 'اور تم بڑے بڑے محل تعمیر کرتے ہو شاید کہ تم ہمیشہ زندہ رہوگے۔'),
(3062, 26, 130, 'اور جب تم کسی پر حملہ کرتے ہو تو جبار و سرکش بن کر حملہ کرتے ہو؟'),
(3063, 26, 131, 'پس تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3064, 26, 132, 'اور اس ذات سے ڈرو جس نے ان چیزوں سے تمہاری مدد کی ہے جنہیں تم جانتے ہو۔'),
(3065, 26, 133, '(یعنی) اس نے مویشیوں اور اولاد سے تمہاری مدد فرمائی۔'),
(3066, 26, 134, 'اور باغات اور چشموں سے۔'),
(3067, 26, 135, 'میں تمہارے متعلق ایک بہت بڑے (سخت) دن کے عذاب سے ڈرتا ہوں۔'),
(3068, 26, 136, 'ان لوگوں نے کہا کہ تم نصیحت کرو یا نہ کرو۔ ہمارے لئے برابر ہے۔'),
(3069, 26, 137, 'یہ (ڈراوا) تو بس اگلے لوگوں کی عادت ہے۔'),
(3070, 26, 138, 'اور ہم کبھی عذاب میں مبتلا ہونے والے نہیں ہیں۔'),
(3071, 26, 139, 'پس انہوں نے اس (ہود) کو جھٹلایا اور ہم نے انہیں ہلاک کر دیا بےشک اس میں ایک بڑی نشانی ہے مگر ان میں سے اکثر لوگ ایمان لائے نہیں تھے۔'),
(3072, 26, 140, 'اور یقیناً آپ کا پروردگار بڑا غالب، بڑا رحم کرنے والا ہے۔'),
(3073, 26, 141, 'قومِ ثمود نے رسولوں کو جھٹلایا۔'),
(3074, 26, 142, 'جبکہ ان کے بھائی صالح نے ان سے کہا کیا تم ڈرتے نہیں ہو؟'),
(3075, 26, 143, 'میں تمہارے لئے امانت دار رسول ہوں۔'),
(3076, 26, 144, 'سو تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3077, 26, 145, 'اور میں تم سے اس (تبلیغ رسالت) پر کوئی اجرت نہیں مانگتا۔ میری اجرت تو رب العالمین کے ذمہ ہے۔'),
(3078, 26, 146, 'کیا تمہیں ان چیزوں میں جو یہاں ہیں امن و اطمینان سے چھوڑ دیا جائے گا۔'),
(3079, 26, 147, '(یعنی) باغوں اور چشموں میں۔'),
(3080, 26, 148, 'اور ان کھجوروں میں جن کے خوشے نرم اور خوب بھرے ہوئے ہیں۔'),
(3081, 26, 149, 'اور تم پہاڑوں کو تراش تراش کر اتراتے ہوئے (یا مہارت سے) مکانات بناتے ہو۔'),
(3082, 26, 150, 'پس اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3083, 26, 151, 'اور ان حد سے تجاوز کرنے والوں کے حکم کی اطاعت نہ کرو۔'),
(3084, 26, 152, 'جو زمین میں فساد پھیلاتے ہیں اور اصلاح نہیں کرتے۔'),
(3085, 26, 153, 'ان لوگوں نے کہا: (اے صالح) تم ان لوگوں میں سے ہو جن پر سخت (یا بار بار) جادو کر دیا گیا ہے۔'),
(3086, 26, 154, '(اور) تم تو بس ہمارے جیسے ایک بندہ ہو۔ اگر تم سچے ہو تو کوئی معجزہ لاؤ۔'),
(3087, 26, 155, 'صالح نے کہا یہ ایک اونٹنی ہے ایک دن اس کے پانی پینے کا ہوگا اور ایک مقررہ دن کا پانی تمہارے پینے کیلئے ہوگا۔'),
(3088, 26, 156, 'اور خبردار اسے کوئی تکلیف نہ پہنچانا ورنہ تمہیں ایک بڑے دن کا عذاب آپکڑے گا۔'),
(3089, 26, 157, 'پس انہوں نے اس کی کونچیں کاٹ دیں پھر پشیمان ہوئے۔'),
(3090, 26, 158, 'پھر ان کو عذاب نے آپکڑا بےشک اس (واقعہ) میں ایک بڑی نشانی ہے مگر ان میں سے اکثر لوگ ایمان لانے والے نہیں تھے۔'),
(3091, 26, 159, 'اور یقیناً آپ کا پروردگار بڑا غالب ہے، بڑا رحم کرنے والا ہے۔'),
(3092, 26, 160, 'قومِ لوط نے رسولوں کو جھٹلایا۔'),
(3093, 26, 161, 'جبکہ ان کے بھائی لوط نے ان سے کہا کیا تم ڈرتے نہیں ہو۔'),
(3094, 26, 162, 'میں تمہارے لئے امانت دار رسول ہوں۔'),
(3095, 26, 163, 'پس تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3096, 26, 164, 'اور میں تم سے اس (تبلیغ رسالت) پر کوئی اجرت نہیں مانگتا میری اجرت تو بس رب العالمین کے ذمہ ہے۔'),
(3097, 26, 165, 'کیا تم دنیا جہان والوں میں سے (بدفعلی کیلئے) مَردوں کے پاس ہی جاتے ہو۔'),
(3098, 26, 166, 'اور تمہارے پروردگار نے تمہارے لئے جو بیویاں پیدا کی ہیں انہیں چھوڑ دیتے ہو۔ بلکہ تم حد سے گزر جانے والے لوگ ہو۔'),
(3099, 26, 167, 'ان لوگوں نے کہا: اے لوط (ع)! (اگر تم ان باتوں سے) باز نہ آئے تو تمہیں ضرور باہر نکال دیا جائے گا۔'),
(3100, 26, 168, 'آپ نے کہا میں تمہارے (اس) کردار سے سخت نفرت کرنے والوں میں سے ہوں۔'),
(3101, 26, 169, 'اے میرے پروردگار! مجھے اور میرے گھر والوں کو اس عمل (کے وبال) سے جو یہ کرتے ہیں نجات عطا فرما۔'),
(3102, 26, 170, 'چنانچہ ہم نے انہیں اور ان کے گھر والوں کو نجات عطا کی ہے۔'),
(3103, 26, 171, 'سوائے ایک بڑھیا کے جو پیچھے رہ جانے والوں میں سے تھی۔'),
(3104, 26, 172, 'پھر ہم نے دوسرے سب لوگوں کو تباہ و برباد کر دیا۔'),
(3105, 26, 173, 'اور ہم نے ان پر (پتھروں کی) بارش برسائی۔ سو کیا بڑی بارش تھی جو ڈرائے ہوؤوں پر برسی۔'),
(3106, 26, 174, 'بےشک اس میں ایک بڑی نشانی ہے۔ مگر ان میں سے اکثر لوگ ایمان لانے والے نہیں تھے۔'),
(3107, 26, 175, 'اور یقیناً آپ کا پروردگار بڑا غالب ہے، بڑا رحم کرنے والا ہے۔'),
(3108, 26, 176, 'ایکہ والوں نے رسولوں کو جھٹلایا۔'),
(3109, 26, 177, 'جبکہ ان کے بھائی شعیب (ع) نے کہا کیا تم ڈرتے نہیں ہو؟'),
(3110, 26, 178, 'بےشک میں تمہارے لئے امانت دار رسول ہوں۔'),
(3111, 26, 179, 'سو تم اللہ سے ڈرو اور میری اطاعت کرو۔'),
(3112, 26, 180, 'میں تم سے اس (تبلیغ رسالت) پر کوئی اجرت نہیں مانگتا۔ میری اجرت تو بس رب العالمین کے ذمہ ہے۔'),
(3113, 26, 181, '(دیکھو) پورا ناپا کرو (پیمانہ بھرا کرو) اور نقصان پہنچانے والوں میں سے نہ ہو۔'),
(3114, 26, 182, 'اور صحیح ترازو سے تولا کرو (ڈنڈی سیدھی رکھا کرو)۔'),
(3115, 26, 183, 'اور لوگوں کو ان کی چیزیں کم نہ دیا کرو۔ اور زمین میں فساد پھیلاتے نہ پھرو۔'),
(3116, 26, 184, 'اور اس (اللہ) سے ڈرو جس نے تمہیں اور پہلی مخلوق کو پیدا کیا ہے۔'),
(3117, 26, 185, 'ان لوگوں نے کہا کہ تم تو بس سخت سحر زدہ آدمی ہو۔'),
(3118, 26, 186, 'اور تم ہمارے ہی جیسے ایک انسان ہو اور ہمیں تو جھوٹے بھی معلوم ہوتے ہو۔'),
(3119, 26, 187, 'اگر تم سچے ہو تو ہم پر آسمان کا کوئی ٹکڑا گرا دو۔'),
(3120, 26, 188, 'آپ نے کہا میرا پروردگار بہتر جانتا ہے جو کچھ تم کرتے ہو۔'),
(3121, 26, 189, 'پس ان لوگوں نے ان (شعیب (ع)) کو جھٹلایا تو سائبان والے دن کے عذاب نے انہیں اپنی گرفت میں لے لیا۔ بےشک وہ ایک بڑے سخت دن کا عذاب تھا۔'),
(3122, 26, 190, 'بےشک اس میں ایک بڑی نشانی ہے۔ مگر ان میں سے اکثر لوگ ایمان لانے والے نہیں تھے۔'),
(3123, 26, 191, 'اور بےشک آپ کا پروردگار بڑا غالب ہے، بڑا رحم کرنے والا ہے۔'),
(3124, 26, 192, 'اور بےشک یہ (قرآن) رب العالمین کا نازل کردہ ہے۔'),
(3125, 26, 193, 'جسے روح الامین (جبرائیل) نے آپ کے دل پر اتارا ہے۔'),
(3126, 26, 194, 'تاکہ آپ (عذابِ الٰہی سے) ڈرانے والوں میں سے ہو جائیں۔'),
(3127, 26, 195, 'یہ کھلی ہوئی عربی زبان میں ہے۔'),
(3128, 26, 196, 'اور یہ پہلے لوگوں کی کتابوں میں (بھی) ہے۔'),
(3129, 26, 197, 'کیا یہ نشانی اس (اہل مکہ) کیلئے کافی نہیں ہے کہ اس (قرآن) کو بنی اسرائیل کے علماء جانتے ہیں۔'),
(3130, 26, 198, 'اور اگر ہم اسے کسی عجمی (غیر عرب) پر نازل کرتے۔'),
(3131, 26, 199, 'پھر وہ اسے پڑھ کر ان کو سناتا تب بھی وہ اس پر ایمان نہ لاتے۔'),
(3132, 26, 200, 'اسی طرح ہم نے اس (انکار) کو مجرموں کے دلوں میں داخل کر دیا ہے۔'),
(3133, 26, 201, 'یہ اس پر ایمان نہیں لائیں گے جب تک دردناک عذاب کو نہیں دیکھ لیں گے۔'),
(3134, 26, 202, 'چنانچہ وہ (عذاب) اس طرح اچانک ان پر آجائے گا کہ انہیں احساس بھی نہ ہوگا۔'),
(3135, 26, 203, 'تب وہ کہیں گے کیا ہمیں مہلت مل سکتی ہے؟'),
(3136, 26, 204, 'کیا یہ لوگ ہمارے عذاب کیلئے جلدی کر رہے ہیں۔'),
(3137, 26, 205, 'کیا تم دیکھتے ہو کہ اگر ہم انہیں کئی سال تک فائدہ اٹھانے کا موقع دیں۔'),
(3138, 26, 206, 'پھر ان پر وہ عذاب آجائے جس سے انہیں ڈرایا جاتا تھا۔'),
(3139, 26, 207, 'تو وہ سب ساز و سامان جس سے وہ فائدہ اٹھاتے رہے تھے انہیں کوئی فائدہ نہ دے گا۔'),
(3140, 26, 208, 'اور ہم نے کبھی کسی بستی کو اس وقت تک ہلاک نہیں کیا جب تک اس کے پاس عذاب الٰہی سے ڈرانے والے نہ آچکے ہوں۔'),
(3141, 26, 209, 'نصیحت اور یاد دہانی کیلئے! اور ہم کبھی ظالم نہیں تھے۔'),
(3142, 26, 210, 'اور اس (قرآن) کو شیطانوں نے نہیں اتارا۔'),
(3143, 26, 211, 'اور نہ ہی یہ ان کیلئے مناسب ہے اور نہ ہی وہ اس کی طاقت رکھتے ہیں۔'),
(3144, 26, 212, 'وہ تو اس (وحی) کے سننے سے بھی معزول کئے جا چکے ہیں۔'),
(3145, 26, 213, 'پس آپ اللہ کے ساتھ کسی اور الہ کو نہ پکاریں ورنہ آپ بھی سزا پانے والوں میں شامل ہو جائیں گے۔'),
(3146, 26, 214, 'اور (اے رسول) اپنے قریب ترین رشتہ داروں کو (عذاب سے) ڈراؤ۔'),
(3147, 26, 215, 'اور جو اہلِ ایمان آپ کی پیروی کریں ان کیلئے اپنا بازو جھکاؤ (ان کے ساتھ تواضع و فروتنی سے پیش آؤ)۔'),
(3148, 26, 216, 'اور اگر وہ تمہاری نافرمانی کریں تو کہہ دو کہ میں اس سے بیزار ہوں جو کچھ تم کرتے ہو۔'),
(3149, 26, 217, 'اور اس (پروردگار) پر بھروسہ کیجئے جو غالب ہے اور بڑا رحم کرنے والا ہے۔'),
(3150, 26, 218, 'جو آپ کو اس وقت دیکھتا ہے جب آپ کھڑے ہوتے ہیں۔'),
(3151, 26, 219, 'اور جب آپ سجدہ کرنے والوں میں نشست و برخاست کرتے ہیں تب بھی دیکھتا ہے۔'),
(3152, 26, 220, 'بےشک وہ بڑا سننے والا، بڑا علم والا ہے۔'),
(3153, 26, 221, 'کیا میں تمہیں بتاؤں کہ شیاطین کس پر اترتے ہیں۔'),
(3154, 26, 222, 'وہ ہر جھوٹ گھڑنے والے گنہگار پر اترتے ہیں۔'),
(3155, 26, 223, 'جو ان (شیاطین) کی طرف کان لگائے رہتے ہیں اور ان میں اکثر جھوٹے ہوتے ہیں۔'),
(3156, 26, 224, 'اور جو شعراء ہیں ان کی پیروی گمراہ لوگ کرتے ہیں۔'),
(3157, 26, 225, 'کیا تم نہیں دیکھتے کہ وہ ہر وادی میں حیران و سرگرداں پھرا کرتے ہیں۔'),
(3158, 26, 226, 'اور وہ کہتے ہیں ایسی باتیں جو کرتے نہیں ہیں۔'),
(3159, 26, 227, 'سوائے ان کے جو ایمان لائے اور نیک عمل کئے اور بکثرت اللہ کا ذکر کیا اور بعد اس کے کہ ان پر ظلم ہوا انہوں نے بدلہ لیا اور جن لوگوں نے ظلم کیا انہیں عنقریب معلوم ہو جائے گا کہ وہ کس جگہ لوٹ کر جا رہے ہیں؟');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا۔ سین۔ یہ قرآن واضح کتاب کی آیتیں ہیں۔'),
(3161, 27, 2, 'جو ان ایمان والوں کیلئے (سراسر) ہدایت و بشارت ہیں۔'),
(3162, 27, 3, 'جو نماز پڑھتے اور زکوٰۃ دیتے ہیں اور آخرت پر یقین رکھتے ہیں۔'),
(3163, 27, 4, 'جو لوگ ایمان نہیں رکھتے۔ ہم نے ان کے اعمال (ان کی نظر میں) خوشنما بنا دیئے ہیں پس وہ سرگردان پھر رہے ہیں۔'),
(3164, 27, 5, 'یہ وہ لوگ ہیں جن کیلئے (دنیا میں) بڑا عذاب ہے اور وہ آخرت میں سب سے زیادہ نقصان اٹھانے والے ہیں۔'),
(3165, 27, 6, '(اے رسول(ص)) بےشک آپ کو یہ قرآن ایک بڑے حکمت والے، بڑے علم والے کی طرف سے عطا کیا جا رہا ہے۔'),
(3166, 27, 7, '(وہ وقت یاد کرو) جب موسیٰ نے اپنے گھر والوں سے کہا کہ میں نے آگ دیکھی ہے تو میں ابھی وہاں سے کوئی خبر لاتا ہوں یا آگ کا کوئی انگارہ لاتا ہوں تاکہ تم تاپو۔'),
(3167, 27, 8, 'پھر جب وہ اس کے پاس آئے تو (انہیں) ندا دی گئی کہ مبارک ہے وہ جس کا جلوہ آگ میں ہے۔ اور جس کا جلوہ اس کے اردگرد ہے اور پاک ہے اللہ جو پروردگارِ عالمیان ہے۔'),
(3168, 27, 9, 'اے موسیٰ! بلاشبہ یہ میں اللہ ہوں، بڑا غلبہ والا، بڑا حکمت والا۔'),
(3169, 27, 10, 'اور تم اپنا عصا پھینکو۔ پھر جب انہوں نے اسے دیکھا کہ وہ حرکت کر رہا ہے جیسے کہ وہ ایک سانپ ہے تو وہ پیٹھ پھیر کر مڑے اور پیچھے پلٹ کر بھی نہ دیکھا (ہم نے آواز دی) اے موسیٰ خوف نہ کرو۔ میں وہ ہوں کہ میرے حضور پیغمبر خوف نہیں کرتے۔'),
(3170, 27, 11, 'سوائے اس کے جو کوئی زیادتی کرے پھر وہ برائی کو اچھائی سے بدل دے تو بےشک میں بڑا بخشنے والا، بڑا رحم کرنے والا ہوں۔'),
(3171, 27, 12, 'اور اپنا ہاتھ اپنے گریبان میں ڈالو تو وہ کسی بیماری کے بغیر چمکتا ہوا نکلے گا (یہ دو معجزے ہیں) ان نو معجزات میں سے جنہیں لے کر آپ نے فرعون اور اس کی قوم کے پاس جانا ہے۔ وہ بڑے ہی نافرمان لوگ ہیں۔'),
(3172, 27, 13, 'سو جب ان کے پاس ہمارے واضح معجزات پہنچے تو انہوں نے کہا کہ یہ تو کھلا ہوا جادو ہے۔'),
(3173, 27, 14, 'اور ان لوگوں نے ظلم و تکبر کی راہ سے ان (معجزات) کا انکار کر دیا۔ حالانکہ ان کے نفوس (دلوں) کو ان کا یقین تھا۔ اب دیکھو کہ فساد کرنے والوں کا انجام کیا ہوا؟'),
(3174, 27, 15, 'اور ہم نے داؤد اور سلیمان کو (خاص) علم عطا فرمایا۔ اور ان دونوں نے کہا کہ ہر قِسم کی تعریف اس اللہ کے لئے ہے جس نے ہمیں اپنے بہت سے مؤمن بندوں پر فضیلت عطا کی ہے۔'),
(3175, 27, 16, 'اور سلیمان داؤد کے وارث ہوئے اور کہا اے لوگو! ہمیں پرندوں کی بولی کی تعلیم دی گئی ہے اور ہمیں ہر قسم کی چیزیں عطا کی گئی ہیں۔ بےشک یہ (اللہ کا) کھلا ہوا فضل و کرم ہے۔'),
(3176, 27, 17, 'اور سلیمان کے لئے جنوں، انسانوں اور پرندوں میں سے لشکر جمع کئے گئے۔ پس ان کی ترتیب وار صف بندی کی جاتی تھی۔'),
(3177, 27, 18, 'یہاں تک کہ جب وہ چیونٹیوں کی وادی میں پہنچے تو ایک چیونٹی نے کہا اے چیونٹیو! اپنے اپنے بلوں میں گھس جاؤ۔ ایسا نہ ہو کہ سلیمان اور ان کا لشکر تمہیں کچل ڈالے اور انہیں خبر بھی نہ ہو۔'),
(3178, 27, 19, 'وہ (سلیمان) اس کی بات پر مسکرا کر ہنس پڑے۔ اور کہا اے میرے پروردگار! مجھے ہمیشہ توفیق دے کہ میں تیری ان نعمتوں کا شکریہ ادا کر سکوں جن سے تو نے مجھے اور میرے والدین کو نوازا ہے اور ایسا نیک عمل کروں جسے تو پسند کرے اور مجھے اپنی رحمت سے اپنے نیک بندوں میں داخل کر۔'),
(3179, 27, 20, 'اور سلیمان (ع) نے (ایک دن) پرندوں کا جائزہ لیا تو کہا کیا بات ہے کہ میں ہُدہُد کو نہیں دیکھ رہا ہوں کیا وہ کہیں غائب ہے؟'),
(3180, 27, 21, '(اگر ایسا ہی ہے) تو میں اسے سخت سزا دوں گا۔ یا اسے ذبح کر دوں گا۔ یا پھر وہ کوئی واضح دلیل (عذر) میرے سامنے پیش کرے۔'),
(3181, 27, 22, 'پس کچھ زیادہ دیر نہیں گزری تھی کہ ہد ہد نے آکر کہا کہ میں نے وہ بات معلوم کی ہے جو آپ کو معلوم نہیں ہے۔ اور میں (ملک) سبا سے آپ کے پاس ایک یقینی خبر لایا ہوں۔'),
(3182, 27, 23, 'میں نے ایک عورت کو پایا ہے جو ان لوگوں پر حکومت کرتی ہے اور اسے ہر چیز دی گئی ہے اور اس کا ایک بڑا تخت (سلطنت) ہے۔'),
(3183, 27, 24, 'میں نے اسے اور اس کی قوم کو پایا ہے کہ وہ اللہ کو چھوڑ کر سورج کو سجدہ کرتے ہیں اور شیطان نے ان کے عملوں کو ان کی نظر میں خوشنما بنا دیا ہے۔ پس اس نے انہیں اصل (سیدھے) راستہ سے ہٹا دیا ہے۔ سو وہ ہدایت نہیں پاتے۔'),
(3184, 27, 25, 'وہ کیوں اس خدا کو سجدہ نہ کریں جو آسمانوں اور زمین میں پوشیدہ چیزوں کو باہر لاتا ہے اور وہ جانتا ہے جو کچھ تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہو۔'),
(3185, 27, 26, 'اللہ کے سوا کوئی الہ نہیں ہے جو عرش عظیم کا مالک ہے۔'),
(3186, 27, 27, 'سلیمان نے کہا ہم ابھی دیکھتے ہیں کہ تو نے سچ کہا ہے یا تو جھوٹوں میں سے ہے۔'),
(3187, 27, 28, 'میرا یہ خط لے جا اور اسے ان کے پاس ڈال دے پھر ان سے ہٹ جا۔ پھر دیکھ کہ وہ کیا جواب دیتے ہیں؟'),
(3188, 27, 29, 'اس (ملکہ) نے کہا اے (دربار کے) سردارو! مجھے ایک معزز خط پہنچایا گیا ہے۔'),
(3189, 27, 30, 'وہ سلیمان کی طرف سے ہے اور وہ یہ ہے بسم اللہ الرحمن الرحیم۔'),
(3190, 27, 31, 'تم میرے مقابلہ میں سرکشی نہ کرو اور فرمانبردار ہوکر میرے پاس آجاؤ۔'),
(3191, 27, 32, 'چنانچہ ملکہ نے کہا: اے سردارانِ قوم! تم مجھے میرے اس معاملہ میں رائے دو (کیونکہ) میں کبھی کسی معاملہ کا کوئی قطعی فیصلہ نہیں کرتی جب تک تم لوگ میرے پاس موجود نہ ہو۔'),
(3192, 27, 33, 'انہوں نے کہا ہم طاقتور اور سخت لڑنے والے ہیں لیکن اختیار آپ کو ہی ہے۔ آپ دیکھ لیں (غور کریں) کہ کیا حکم دیتی ہیں؟'),
(3193, 27, 34, 'ملکہ نے کہا بادشاہ جب کسی بستی میں داخل ہوتے ہیں تو اسے تباہ کر دیتے ہیں اور اس کے معزز باشندوں کو ذلیل کر دیتے ہیں اور ایسا ہی (یہ لوگ) کریں گے۔'),
(3194, 27, 35, '(لہٰذا جنگ مناسب نہیں البتہ) میں ان کے پاس ایک ہدیہ بھیجتی ہوں۔ اور پھر دیکھتی ہوں کہ میرے فرستادے (قاصد) کیا جواب لاتے ہیں؟'),
(3195, 27, 36, 'سو جب وہ فرستادہ (یا ملکہ کا سفیر) سلیمان کے پاس پہنچا تو آپ نے کہا کیا تم لوگ مال سے میری مدد کرنا چاہتے ہو؟ تو اللہ نے مجھے جو کچھ دے رکھا ہے وہ اس سے بہتر ہے جو تمہیں دیا ہے البتہ تم ہی اپنے ہدیہ پر خوش ہو سکتے ہو۔'),
(3196, 27, 37, '(اے سفیر) تو ان لوگوں کے پاس واپس لوٹ جا (اور ان کو بتا کہ) ہم ان کے پاس ایسے لشکر لے کر آرہے ہیں جن کے مقابلہ کی ان میں تاب نہ ہوگی اور ہم ان کو وہاں سے اس طرح ذلیل کرکے نکالیں گے کہ وہ خوار ہو چکے ہوں گے۔'),
(3197, 27, 38, 'آپ نے کہا اے عمائدین سلطنت! تم میں سے کون ہے؟ جو اس (ملکہ سبا) کا تخت میرے پاس لائے قبل اس کے کہ وہ مسلمان ہوکر میرے پاس آئیں؟'),
(3198, 27, 39, 'جنات میں سے ایک شریر و چالاک جن نے کہا کہ میں اسے آپ کے پاس لے آؤں گا اس سے پہلے کہ آپ اپنی جگہ سے اٹھیں۔ بےشک میں اس کی طاقت رکھتا ہوں (اور میں) امانت دار بھی ہوں۔'),
(3199, 27, 40, 'اور اس شخص نے کہا جس کے پاس کتاب کا کچھ علم تھا میں اسے آپ کے پاس لے آؤں گا اس سے پہلے کہ آپ کی آنکھ جھپکے پھر جب سلیمان نے اسے اپنے پاس رکھا ہوا دیکھا تو کہا کہ یہ میرے پروردگار کا فضل و کرم ہے تاکہ وہ مجھے آزمائے کہ میں اس کا شکر ادا کرتا ہوں یا ناشکری کرتا ہوں اور جو کوئی شکر کرتا ہے تو وہ اپنے فائدہ کیلئے شکر کرتا ہے اور جو ناشکری کرتا ہے تو وہ (اپنا نقصان کرتا ہے) میرا پروردگار بے نیاز اور کریم ہے۔'),
(3200, 27, 41, 'آپ نے کہا اس (ملکہ) کے تخت کی صورت بدل دو تاکہ ہم دیکھیں کہ آیا وہ صحیح بات تک راہ پاتی ہے یا وہ ان لوگوں میں سے ہے جو صحیح بات تک راہ نہیں پاتے۔'),
(3201, 27, 42, 'الغرض جب ملکہ آئی تو اس سے کہا گیا کیا تمہارا تخت ایسا ہی ہے؟ اس نے کہا یہ تو گویا وہی ہے اور ہمیں تو پہلے ہی سے حقیقت معلوم ہوگئی تھی۔ اور ہم تو مسلمان (مطیع و فرمانبردار) ہو چکے ہیں۔'),
(3202, 27, 43, 'اور سلیمان نے اس (ملکہ) کو غیر اللہ کی عبادت سے روک دیا جو وہ کیا کرتی تھی کیونکہ وہ کافر قوم میں سے تھی۔'),
(3203, 27, 44, 'اس سے کہا گیا کہ محل میں داخل ہو۔ تو جب اس نے اس کو دیکھا تو (بلوریں فرش کو گہرا) پانی خیال کیا اور (گزرنے کیلئے اس طرح پائیچے اٹھائے کہ) اپنی دونوں پنڈلیاں کھول دیں۔ آپ نے کہا یہ (پانی نہیں ہے بلکہ) شیشوں سے مرصع محل ہے (اور بلوریں فرش ہے) ملکہ نے کہا اے میرے پروردگار! میں نے اپنے اوپر ظلم کیا تھا اور میں سلیمان کے ساتھ رب العالمین پر ایمان لاتی ہوں۔'),
(3204, 27, 45, 'اور بےشک ہم نے قوم ثمود کی طرف ان کے بھائی صالح کو رسول بنا کر (اس پیغام کے ساتھ) بھیجا کہ اللہ کی عبادت کرو۔ تو وہ دو فریق (مؤمن و کافر) ہوگئے جو آپس میں جھگڑنے لگے۔'),
(3205, 27, 46, 'آپ نے کہا اے میری قوم! تم بھلائی سے پہلے برائی کیلئے کیوں جلدی کرتے ہو؟ تم اللہ سے کیوں بخشش طلب نہیں کرتے؟ تاکہ تم پر رحم کیا جائے۔'),
(3206, 27, 47, 'انہوں نے کہا کہ ہم تو تمہیں اور تمہارے ساتھیوں کو برا شگون سمجھتے ہیں آپ (ع) نے کہا تمہاری بدشگونی تو اللہ کے پاس ہے بلکہ تم وہ لوگ ہو جو آزمائے جا رہے ہو۔'),
(3207, 27, 48, 'اور (اس) شہر میں نو گروہ تھے جو زمین میں فساد برپا کرتے تھے اور اصلاح نہیں کرتے تھے۔'),
(3208, 27, 49, 'انہوں نے کہا کہ آپس میں خدا کی قسم کھا کر عہد کرو کہ ہم رات کے وقت صالح اور اس کے گھر والوں کو ہلاک کر دیں گے اور پھر ان کے وارث سے کہیں گے کہ ہم ان کے گھر والوں کی ہلاکت کے موقع پر موجود ہی نہیں تھے اور ہم بالکل سچے ہیں۔'),
(3209, 27, 50, 'اور (اس طرح) انہوں نے ایک چال چلی اور ہم نے بھی ایک چال چلی جس کی انہیں خبر نہ تھی۔'),
(3210, 27, 51, 'تو دیکھو ان کی چال کا انجام کیا ہوا؟ ہم نے ان کو اور ان کی قوم کو تباہ و برباد کر دیا۔'),
(3211, 27, 52, 'سو یہ ان کے گھر ہیں جو ان کے ظلم کی وجہ سے ویران پڑے ہیں۔ بےشک اس (واقعہ) میں ایک بڑی نشانی ہے ان لوگوں کیلئے جو علم رکھتے ہیں۔'),
(3212, 27, 53, 'اور ہم نے ان لوگوں کو نجات دی جو ایمان لائے اور تقویٰ اختیار کئے ہوئے تھے۔'),
(3213, 27, 54, 'اور ہم نے لوط کو بھی (رسول بنا کر بھیجا) جب انہوں نے اپنی قوم سے کہا کہ تم بے حیائی کا کام کرتے ہو حالانکہ تم دیکھ رہے ہو (جانتے بوجھتے ہو)۔'),
(3214, 27, 55, 'کیا تم عورتوں کو چھوڑ کر مردوں سے شہوت رانی کرتے ہو؟ بلکہ تم بڑے ہی جاہل لوگ ہو۔'),
(3215, 27, 56, 'ان کی قوم کا جواب اس کے سوا کوئی نہ تھا کہ لوط کے گھرانے کو اپنی بستی سے نکال دو یہ لوگ بڑے پاکباز بنتے ہیں۔'),
(3216, 27, 57, 'پس ہم نے آپ کو اور آپ کے گھر والوں کو نجات دی سوائے آپ کی بیوی کے کہ جس کے بارے میں ہم نے فیصلہ کر دیا تھا کہ وہ پیچھے رہ جانے والوں میں سے ہوگی۔'),
(3217, 27, 58, 'اور ہم نے ان پر ایک خاص (پتھروں کی) بارش برسائی سو کیسی بری بارش تھی ان لوگوں کیلئے جن کو ڈرایا جا چکا تھا۔'),
(3218, 27, 59, 'آپ کہئے! ہر قِسم کی تعریف اللہ کیلئے ہے اور اس کے ان بندوں پر سلام ہو جنہیں اس نے منتخب کیا ہے (ان سے پوچھو) کیا اللہ بہتر ہے یا وہ (معبود) جنہیں وہ (اللہ کا) شریک ٹھہراتے ہیں؟'),
(3219, 27, 60, '(ان مشرکین سے پوچھو) بھلا وہ کون ہے جس نے آسمانوں اور زمین کو پیدا کیا؟ اور تمہارے لئے آسمان سے پانی اتارا؟ پھر ہم نے اس (پانی) سے خوش منظر (بارونق) باغات اگائے۔ ان درختوں کا اگانا تمہارے بس میں نہیں تھا۔ کیا اللہ کے ساتھ کوئی اور الٰہ ہے؟ بلکہ یہ (راہِ حق سے) انحراف کرنے والے لوگ ہیں۔'),
(3220, 27, 61, 'بھلا وہ کون ہے جس نے زمین کو قرارگاہ بنایا؟ اور اس کے درمیان نہریں (ندیاں) جاری کیں اور اس کیلئے بھاری پہاڑ بنائے اور دو دریاؤں کے درمیان پردہ حائل کر دیا۔ کیا اللہ کے ساتھ کوئی اور الہ ہے؟ بلکہ ان میں سے اکثر لوگ جانتے نہیں ہیں۔'),
(3221, 27, 62, 'کون ہے جو مضطر و بے قرار کی دعا و پکار کو قبول کرتا ہے۔ جب وہ اسے پکارتا ہے؟ اور اس کی تکلیف و مصیبت کو دور کر دیتا ہے؟ اور تمہیں زمین میں (اگلوں کا) جانشین بناتا ہے؟ کیا اللہ کے ساتھ کوئی اور الہ ہے؟ نہیں - بلکہ یہ لوگ بہت کم نصیحت حاصل کرتے ہیں۔'),
(3222, 27, 63, 'جو خشکی اور تری کی تاریکیوں میں تمہاری راہنمائی کرتا ہے۔ اور اپنی (باران) رحمت سے پہلے ہواؤں کو خوشخبری دینے والا بنا کر بھیجتا ہے کیا اللہ کے ساتھ کوئی اور الہ ہے؟ اللہ برتر و بالا ہے ان چیزوں سے جنہیں وہ اس کا شریک ٹھہراتے ہیں۔'),
(3223, 27, 64, 'وہ کون ہے جو خلقت کی ابتداء کرتا ہے اور پھر اسے دوبارہ پیدا کرے گا اور کون ہے جو تمہیں آسمان و زمین سے روزی دیتا ہے؟ کیا اللہ کے ساتھ کوئی اور الہ ہے؟ کہہ دیجئے کہ اگر تم سچے ہو تو اپنی دلیل لاؤ۔'),
(3224, 27, 65, 'آپ کہہ دیجئے! کہ آسمانوں اور زمین میں جو بھی ہیں اللہ کے سوا کوئی بھی غیب کا علم نہیں رکھتا۔ اور نہ وہ یہ جانتے ہیں کہ وہ کب (دوبارہ) اٹھائے جائیں گے؟'),
(3225, 27, 66, 'بلکہ آخرت کی منزل میں رفتہ رفتہ انہیں پورا علم ہوگا۔ بلکہ وہ اس کے بارے میں شک میں مبتلا ہیں بلکہ یہ اس کی طرف سے بالکل اندھے ہیں۔'),
(3226, 27, 67, 'اور کافر لوگ کہتے ہیں کہ جب ہم اور ہمارے باپ دادا مٹی ہو جائیں گے تب ہم (دوبارہ قبروں سے) نکالے جائیں گے۔'),
(3227, 27, 68, 'اس کا وعدہ ہم سے بھی کیا گیا اور اس سے پہلے ہمارے باپ دادا سے بھی کیا گیا یہ تو محض اگلے لوگوں کی کہانیاں ہیں۔'),
(3228, 27, 69, 'آپ کہئے کہ زمین میں چلو پھرو اور دیکھو کہ مجرموں کا کیا انجام ہوا؟'),
(3229, 27, 70, 'اور (اے رسول(ص)) آپ ان (کے حال) پر غمزدہ نہ ہوں۔ اور جو چالیں یہ چل رہے ہیں اس سے دل تنگ نہ ہوں۔'),
(3230, 27, 71, 'اور وہ کہتے ہیں کہ یہ وعدہ وعید کب پورا ہوگا؟ اگر تم سچے ہو؟'),
(3231, 27, 72, 'آپ کہئے! بہت ممکن ہے کہ جس (عذاب) کیلئے تم جلدی کر رہے ہو اس کا کچھ حصہ تمہارے پیچھے آ لگا ہو(قریب آگیا ہو)۔'),
(3232, 27, 73, 'اور بےشک آپ کا پروردگار لوگوں پر بڑا فضل و کرم کرنے والا ہے لیکن اکثر لوگ شکر نہیں کرتے۔'),
(3233, 27, 74, 'اور یقیناً آپ کا پروردگار خوب جانتا ہے اسے جو ان کے سینے چھپائے ہوئے ہیں اور جو کچھ وہ ظاہر کرتے ہیں۔'),
(3234, 27, 75, 'اور آسمانوں و زمین میں کوئی ایسی پوشیدہ چیز نہیں ہے۔ جو ایک واضح کتاب میں موجود نہ ہو۔'),
(3235, 27, 76, 'بےشک یہ قرآن بنی اسرائیل کے سامنے اکثر وہ باتیں بیان کرتا ہے جن میں وہ اختلاف کیا کرتے ہیں۔'),
(3236, 27, 77, 'اور بلاشبہ وہ (قرآن) مؤمنین کیلئے سراپا ہدایت اور رحمت ہے۔'),
(3237, 27, 78, 'اور بےشک آپ کا پروردگار اپنے حکم سے ان کے درمیان فیصلہ کرے گا۔ وہ بڑا غالب اور بڑا جاننے والا ہے۔'),
(3238, 27, 79, 'سو آپ اللہ پر بھروسہ کریں۔ بےشک آپ واضح حق پر ہیں۔'),
(3239, 27, 80, 'آپ مُردوں کو (آواز) نہیں سنا سکتے۔ اور نہ بہروں کو (اپنی) پکار سنا سکتے ہیں جو پیٹھ پھیر کر چل دیں۔'),
(3240, 27, 81, 'اور نہ ہی آپ (دل کے) اندھوں کو ان کی گمراہی سے (ہٹا کر) راستہ دکھا سکتے ہیں۔ آپ تو صرف انہی لوگوں کو سنا سکتے ہیں جو ہماری آیتوں پر ایمان لاتے ہیں۔ بس یہی لوگ ماننے والے ہیں۔'),
(3241, 27, 82, 'اور جب ان لوگوں پر وعدہ پورا ہونے کو ہوگا تو ہم زمین سے چلنے پھرنے والا نکالیں گے جو ان سے کلام کرے گا۔ (اس بناء پر) کہ لوگ ہماری آیتوں پر یقین نہیں کرتے تھے۔'),
(3242, 27, 83, 'اور (اس دن کو یاد کرو) جس دن ہم ہر امت میں سے ایک ایسا گروہ محشور (جمع) کریں گے جو ہماری آیتوں کو جھٹلایا کرتا تھا۔ پھر اس کو روک کر جماعت بندی کی جائے گی۔'),
(3243, 27, 84, 'یہاں تک کہ جب وہ (سب) آجائیں گے تو اللہ ان سے فرمائے گا کیا تم نے میری آیتوں کو اس حالت میں جھٹلایا تھا کہ تم نے ان کا علمی احاطہ بھی نہ کیا تھا؟ یا وہ کیا تھا جو تم کیا کرتے تھے؟'),
(3244, 27, 85, 'اور ان لوگوں کے ظلم کی وجہ سے ان پر (وعدۂ عذاب کی) بات پوری ہو جائے گی تو اب وہ نہیں بولیں گے۔'),
(3245, 27, 86, 'کیا انہوں نے نہیں دیکھا کہ ہم نے رات (تاریک) اس لئے بنائی کہ وہ اس میں آرام کریں اور دن کو روشن اس لئے بنایا (تاکہ اس میں کام کریں) بےشک اس میں ایمان لانے والوں کیلئے (قدرت خدا کی) نشانیاں ہیں۔'),
(3246, 27, 87, 'اور جس دن صور پھونکا جائے گا۔ تو جو بھی آسمانوں اور زمین میں ہیں سب گھبرا جائیں گے سوائے ان کے جن کو خدا (بچانا) چاہے گا اور سب ذلت وعاجزی کے ساتھ اس کی بارگاہ میں حاضر ہوں گے۔'),
(3247, 27, 88, '(اس وقت) تم پہاڑوں کو دیکھوگے تو خیال کروگے کہ وہ ساکن و برقرار ہیں حالانکہ وہ بادلوں کی مانند رواں دواں ہوں گے۔ یہ خدا کی کاریگری ہے جس نے ہر شئے کو محکم و پائیدار بنایا ہے۔ بیشک وہ خوب جانتا ہے جو کچھ تم کرتے ہو۔'),
(3248, 27, 89, 'جو شخص نیکی لے کر آئے گا اسے اس سے بہتر ملے گا اور اس دن کی گھبراہٹ سے محفوظ ہوں گے۔'),
(3249, 27, 90, 'اور جو شخص برائی لے کر آئے گا تو ان کو اوندھے منہ آتش (دوزخ) میں پھینک دیا جائے گا۔ کیا تمہیں تمہارے عمل کے علاوہ کوئی بدلہ مل سکتا ہے؟ (جیسا کروگے ویسا بھروگے)۔'),
(3250, 27, 91, '(اے رسول(ص)۔ کہو) مجھے تو بس یہی حکم ملا ہے کہ میں فرمانبرداروں میں سے رہوں۔'),
(3251, 27, 92, 'اور یہ کہ قرآن پڑھ کر سناؤں سو اب جو ہدایت اختیار کرے گا وہ اپنے لئے ہدایت اختیار کرے گا۔ اور جو کوئی گمراہی اختیار کرے گا تو آپ کہہ دیجئے! کہ میں تو صرف ڈرانے والوں میں سے ہوں۔'),
(3252, 27, 93, 'اور آپ کہہ دیجئے! کہ سب تعریف اللہ کیلئے ہے وہ عنقریب تمہیں اپنی نشانیاں دکھائے گا اور تم انہیں پہچان لوگے اور تمہارا پروردگار اس سے غافل نہیں ہے جو کچھ تم کرتے ہو۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا۔ سین۔ میم۔'),
(3254, 28, 2, 'یہ کتابِ مبین کی آیتیں ہیں۔'),
(3255, 28, 3, 'ہم اہلِ ایمان کے فائدہ کیلئے موسیٰ و فرعون کی کچھ خبریں سچائی کے ساتھ آپ کے سامنے پڑھ کر سناتے ہیں۔'),
(3256, 28, 4, 'بےشک فرعون زمین (مصر) میں سرکش ہوگیا تھا اور اس کے باشندوں کو مختلف گروہوں میں تقسیم کر دیا تھا اور اس نے ان میں سے ایک گروہ کو کمزور بنا رکھا تھا (چنانچہ) ان کے بیٹوں کو ذبح کر دیتا تھا اور ان کی عورتوں (لڑکیوں) کو زندہ چھوڑ دیتا تھا۔ بےشک وہ (زمین میں) فساد برپا کرنے والوں میں سے تھا۔'),
(3257, 28, 5, 'اور ہم چاہتے ہیں کہ ان لوگوں پر احسان کریں جنہیں زمین میں کمزور کر دیا گیا تھا اور انہیں پیشوا بنائیں اور انہیں (زمین کا) وارث قرار دیں۔'),
(3258, 28, 6, 'اور انہیں زمین میں اقتدار عطا کریں اور فرعون، ہامان اور ان کی فوجوں کو ان (کمزوروں) کی جانب سے وہ کچھ دکھلائیں جس سے وہ ڈرتے تھے۔'),
(3259, 28, 7, 'اور ہم نے مادر موسیٰ کو وحی کی کہ اس (بچہ) کو دودھ پلا۔ اور جب اس کے متعلق خطرہ محسوس ہو تو اسے دریا میں ڈال دے اور (کسی قسم کا) خوف اور غم نہ کر، بلاشبہ ہم اسے تیری طرف واپس لوٹا دیں گے اور اسے رسولوں میں سے بنائیں گے۔'),
(3260, 28, 8, 'چنانچہ فرعون کے گھر والوں نے اسے (دریا سے) اٹھا لیا تاکہ وہ ان کیلئے دشمن اور رنج و غم (کا باعث) بنے۔ بیشک فرعون، ہامان اور دونوں کے لشکر والے خطاکار (اور غلط کار) تھے۔'),
(3261, 28, 9, 'اور فرعون کی بیوی نے (اس سے) کہا کہ یہ (بچہ) تو میری اور تیری آنکھوں کی ٹھنڈک ہے۔ اسے قتل نہ کرو۔ ممکن ہے کہ ہمیں نفع پہنچائے یا ہم اسے اپنا بیٹا ہی بنا لین اور انہیں (انجام کی) کچھ خبر نہ تھی۔'),
(3262, 28, 10, 'اور مادرِ موسیٰ کا دل بے چین ہوگیا اور قریب تھا کہ وہ اس (بچہ) کا حال ظاہر کر دے۔ اگر ہم اس کے دل کو مضبوط نہ کرتے تاکہ وہ (ہمارے وعدہ پر) ایمان لانے والوں میں سے ہو۔'),
(3263, 28, 11, 'اور ہم نے اس (بچہ) کی بہن سے کہا کہ تو اس کے پیچھے پیچھے جا۔ چنانچہ وہ دور سے اسے دیکھتی رہی جبکہ ان لوگوں کو خبر بھی نہ ہوئی۔'),
(3264, 28, 12, 'اور ہم نے اس (بچہ) پر دائیوں کو پہلے سے حرام کر دیا تھا تو اس (بچہ کی بہن) نے کہا کیا میں تم لوگوں کو ایک ایسے گھرانے کا پتہ بتاؤں جو تمہارے لئے اس کی پرورش کرے؟ اور وہ اس کے خیرخواہ بھی ہوں؟'),
(3265, 28, 13, 'اس طرح ہم نے وہ (بچہ) اس کی ماں کی طرف لوٹا دیا۔ تاکہ اس کی آنکھ ٹھنڈی اور غمناک نہ ہو اور یہ بھی جان لے کہ اللہ کا وعدہ سچا ہوتا ہے لیکن اکثر لوگ (یہ بات) نہیں جانتے۔'),
(3266, 28, 14, 'اور جب وہ (بچہ) اپنے پورے شباب کو پہنچ گیا اور (قد و قامت) پورا درست ہوگیا تو ہم نے اس کو (خاص) حکمت و علم عطا کیا اور ہم اسی طرح نیکوکاروں کو صلہ دیتے ہیں۔'),
(3267, 28, 15, 'وہ (جوان) ایسے وقت شہر میں داخل ہوا جب کہ اس شہر کے باشندے بے خبر تھے۔ تو اس نے وہاں دو آدمیوں کو آپس میں لڑتے ہوئے پایا۔ یہ ایک ان کے گروہ میں سے تھا۔ اور یہ دوسرا ان کے دشمنوں میں سے تھا پس جو اس کے گروہ میں سے تھا اس نے (موسیٰ) کو اس کے خلاف جو اس کے دشمنوں میں سے تھا مدد کیلئے پکارا تو موسیٰ نے اسے ایک مکا مارا۔ بس اس کا کام تمام کر دیا اور کہا یہ (ان کی لڑائی) شیطان کی کاروائی تھی۔ بےشک وہ کھلا ہوا گمراہ کرنے والا دشمن ہے۔'),
(3268, 28, 16, 'کہا اے میرے پروردگار میں نے اپنے اوپر ظلم کیا تو مجھے بخش دے تو خدا نے اسے بخش دیا۔ بےشک وہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(3269, 28, 17, '(پھر) کہا اے میرے پروردگار تو نے مجھے اپنی نعمت سے نوازا ہے۔ میں کبھی مجرموں کا پشت پناہ نہیں بنوں گا۔'),
(3270, 28, 18, 'پھر موسیٰ نے شہر میں اس حال میں صبح کی کہ وہ خوف زدہ اور منتظر تھا (کہ کیا ہوتا ہے؟) کہ اچانک دیکھا کہ وہی شخص جس نے کل اس سے مدد طلب کی تھی (آج پھر چیخ کر( اس کو مدد کیلئے پکار رہا ہے موسیٰ نے اس سے کہا کہ تو کھلا ہوا بد راہ ہے۔'),
(3271, 28, 19, 'پھر جب موسیٰ نے اس شخص پر سخت ہاتھ ڈالنا چاہا جو ان دونوں کا دشمن تھا تو اس نے کہا اے موسیٰ تو (آج) مجھے اسی طرح قتل کرنا چاہتا ہے جس طرح کل ایک شخص کو قتل کیا؟ تو بس یہی چاہتا ہے کہ زمین میں سرکش بن جائے اور یہ نہیں چاہتا کہ اصلاح کرنے والوں میں سے ہو۔'),
(3272, 28, 20, 'اور شہر کے آخری حصہ سے ایک آدمی دوڑتا ہوا آیا (اور) کہا اے موسیٰ! قوم کے بڑے لوگ تمہارے بارے میں مشورہ کر رہے ہیں کہ تجھے قتل کر دیں۔ (لہٰذا) یہاں سے نکل جا یقیناً میں تیرے خیر خواہوں میں سے ہوں۔'),
(3273, 28, 21, 'چنانچہ موسیٰ وہاں سے خوفزدہ ہوکر نتیجہ کا انتظار کرتا ہوا نکلا (اور) کہا اے میرے پروردگار! مجھے ظالم لوگوں سے نجات عطا فرما۔'),
(3274, 28, 22, 'اور جب موسیٰ نے (مصر سے نکل کر) مدین کا رخ کیا تو کہا امید ہے کہ میرا پروردگار سیدھے راستہ کی طرف میری راہنمائی کرے گا۔'),
(3275, 28, 23, 'اور جب وہ مدین کے پانی (کنویں) پر پہنچا تو وہاں لوگوں کا ایک مجمع پایا جو (اپنے مویشیوں کو) پانی پلا رہا ہے اور ان لوگوں سے الگ دو عورتوں کو پایا کہ وہ (اپنے ریوڑ کو) روکے ہوئے کھڑی ہیں۔ موسیٰ نے کہا تمہارا کیا معاملہ ہے؟ ان دونوں نے کہا ہم اس وقت تک (اپنے جانوروں کو) پانی نہیں پلاتیں جب تک (یہ) چرواہے اپنے مویشیوں کو (پانی پلا کر) نہ لے جائیں اور ہمارے والد بہت بوڑھے ہیں۔'),
(3276, 28, 24, '(یہ سن کر) موسیٰ نے (ان کے ریوڑ کو) پانی پلا دیا اور پھر وہاں سے ہٹ کر سایہ میں آگیا اور کہا اے میرے پروردگار! تو جو خیر (نعمت) بھی مجھ پر اتارے میں اس کا محتاج ہوں۔'),
(3277, 28, 25, 'پس ان دو عورتوں مین سے ایک شرم و حیا کے ساتھ چلتی ہوئی اس کے پاس آئی (اور) کہا میرے والد تمہیں بلاتے ہیں تاکہ تمہیں اس کا معاوضہ دیں جو تم نے ہماری خاطر پانی پلایا تو جب موسیٰ اس کے پاس پہنچا اور اسے سارا اپنا قصہ سنایا تو اس (بزرگ) نے (تسلی دیتے ہوئے) کہا خوف نہ کر اب تم ظالم لوگوں سے نجات پا گئے ہو۔'),
(3278, 28, 26, 'تو ان دو میں سے ایک لڑکی نے کہا اے ابا! اس کو اجرت پر رکھ لیجئے کیونکہ اچھا مزدور جسے آپ اجرت پر رکھیں وہی ہے جو طاقت ور بھی ہو اور امانت دار بھی۔'),
(3279, 28, 27, 'اس (شعیب) نے (موسیٰ سے) کہا کہ میں چاہتا ہوں کہ اپنی دو بیٹیوں میں سے ایک کا عقد نکاح تمہارے ساتھ اس شرط پر کر دوں کہ تم آٹھ سال تک میری خدمت کرو اور اگر دس سال پورے کرو تو یہ تمہاری طرف سے (احسان) ہے اور میں تم پر کوئی سختی نہیں کرنا چاہتا۔ ان شاء اللہ تم مجھے نیکوکار لوگوں میں سے پاؤگے۔'),
(3280, 28, 28, 'موسیٰ نے کہا (اچھا) یہ بات میرے اور آپ کے درمیان طے ہوگئی۔ ان دونوں میں سے میں جو مدت بھی پوری کر دوں (اس کے بعد) مجھ پر کوئی زیادتی نہ ہوگی اور ہم جو کچھ کہہ رہے ہیں اللہ اس پر نگہبان ہے۔'),
(3281, 28, 29, 'پھر جب موسیٰ نے (مقررہ) مدت پوری کر دی اور اپنے اہل و (عیال) کو ساتھ لے کر مصر روانہ ہوئے تو طور کی جانب سے آگ محسوس کی تو گھر والوں سے کہا کہ تم ٹھہرو میں نے آگ محسوس کی ہے شاید میں تمہارے پاس (وہاں سے) کوئی خبر لاؤں یا آگ کا کوئی انگارہ لاؤں تاکہ تم تاپو۔'),
(3282, 28, 30, 'تو جب آپ وہاں گئے تو اس وادی کے داہنے کنارے سے اس مبارک مقام پر درخت سے انہیں ندا دی گئی اے موسیٰ! میں ہی اللہ ہوں سارے جہانوں کا پروردگار۔'),
(3283, 28, 31, 'اور یہ کہ تم اپنا عصا پھینک دو تو جب اسے دیکھا کہ اس طرح حرکت کر رہا ہے جیسے وہ سانپ ہے تو وہ منہ موڑ کر الٹے مڑے اور پیچھے مڑ کر بھی نہ دیکھا (آواز آئی) اے موسیٰ! آگے آؤ۔ اور ڈرو نہیں تم (ہر طرح) امن میں ہو۔'),
(3284, 28, 32, '(اور) اپنا ہاتھ اپنے گریبان میں ڈالو وہ بغیر کسی تکلیف (اور بیماری) کے سفید چمکتا ہوا نکلے گا اور رفع خوف کیلئے اپنا بازو اپنی طرف سمیٹ لو یہ تمہارے پروردگار کی طرف سے دو نشانیاں ہیں فرعون اور اس کے سرداروں کے سامنے پیش کرنے کیلئے۔ بیشک وہ (بڑے) نافرمان لوگ ہیں۔'),
(3285, 28, 33, 'موسیٰ نے کہا اے میرے پروردگار! میں نے ان کا ایک آدمی قتل کیا تھا ڈرتا ہوں کہ وہ مجھے قتل کر دیں گے۔'),
(3286, 28, 34, 'اور میرا بھائی ہارون جو مجھ سے زیادہ فصیح اللسان ہے تو اسے میرا مددگار بنا کر میرے ساتھ بھیج تاکہ وہ میری تصدیق کرے کیونکہ مجھے اندیشہ ہے کہ وہ مجھے جھٹلائیں گے۔'),
(3287, 28, 35, 'ارشاد ہوا: ہم تمہارے بازو کو تمہارے بھائی کے ذریعہ سے مضبوط کریں گے اور ہم تم دونوں کو غلبہ عطا کریں گے کہ وہ تم تک پہنچ بھی نہیں سکیں گے۔ ہماری نشانیوں کی برکت سے تم اور تمہارے پیروکار ہی غالب آئیں گے۔'),
(3288, 28, 36, 'تو جب موسیٰ ان لوگوں کے پاس کھلی ہوئی نشانیوں کے ساتھ آئے تو انہوں نے کہا یہ نہیں ہے مگر گھڑا ہوا جادو اور یہ باتیں تو ہم نے اپنے باپ داداؤں کے زمانہ میں بھی نہیں سنیں۔'),
(3289, 28, 37, 'اور موسیٰ نے کہا میرا پروردگار بہتر جانتا ہے کہ اس کی طرف سے کون ہدایت لے کر آیا ہے اور کس کیلئے دار آخرت کا انجام بخیر ہے؟ بیشک ظالم لوگ کبھی فلاح نہیں پاتے۔'),
(3290, 28, 38, 'اور فرعون نے کہا اے سردارو! میں تو اپنے سوا تمہارے لئے کوئی خدا نہیں جانتا تو اے ہامان! تو میرے لئے مٹی کو آگ میں پکا (مٹی کو کچی اینٹوں کو پکوا) پھر میرے لئے ایک بلند عمارت بنوا۔ تاکہ میں (اس پر چڑھ کر) موسیٰ کے خدا کا کوئی سراغ لگا سکوں اور میں تو اسے (اس دعویٰ میں) جھوٹا ہی خیال کرتا ہوں۔'),
(3291, 28, 39, 'اور فرعون اور اس کی فوجوں نے زمین میں ناحق تکبر کیا اور انہوں نے خیال کیا کہ وہ ہماری طرف نہیں لوٹائے جائیں گے۔'),
(3292, 28, 40, 'سو ہم نے اسے اور اس کی فوجوں کو پکڑا اور سمندر میں پھینک دیا تو دیکھو ظالموں کا کیسا انجام ہوا؟'),
(3293, 28, 41, 'اور ہم نے انہیں ایسا پیشوا قرار دیا ہے جو (لوگوں کو) آتش (دوزخ) کی طرف بلاتے ہیں اور قیامت کے دن ان کی کوئی مدد نہ کی جائے گی۔'),
(3294, 28, 42, 'اور ہم نے اس دنیا میں ان کے پیچھے لعنت لگا دی ہے اور قیامت کے دن قبیح المنظر لوگوں میں سے ہوں گے۔'),
(3295, 28, 43, 'اور پہلی امتوں کو ہلاک کرنے کے بعد بالیقین ہم نے موسیٰ کو کتاب عطا کی جو لوگوں کیلئے بصیرتوں کا مجموعہ اور سراپا ہدایت و رحمت تھی تاکہ وہ نصیحت حاصل کریں۔'),
(3296, 28, 44, 'اور (اے رسول(ص)) آپ (کوہِ طور کی) مغربی جانب موجود نہ تھے جب ہم نے موسیٰ سے (نبوت و رسالت) کا معاملہ طے کیا تھا اور نہ ہی آپ وہاں حاضرین میں سے تھے۔'),
(3297, 28, 45, 'لیکن ہم نے (اس اثناء میں) کئی نسلیں پیدا کیں اور ان پر زمانہ دراز گزر گیا ہے (اور ہمارے عہد و پیمان کو بھلا دیا) اور نہ ہی آپ مدین والوں میں مقیم تھے کہ ان کو ہماری آیتیں پڑھ کر سناتے لیکن ہم (آپ کو) رسول بنا کر بھیجنے والے تھے (اس لئے ان کے حالات سے آپ کو آگاہ کیا)۔'),
(3298, 28, 46, 'اور نہ آپ کوہ طور کے دامن میں موجود تھے جب ہم نے (موسیٰ کو) ندا دی تھی لیکن یہ (آپ کا مبعوث برسالت ہونا) آپ کے پروردگار کی رحمت ہے تاکہ آپ اس قوم کو ڈرائیں جس کے پاس آپ سے پہلے کوئی ڈرانے والا نہیں آیا تاکہ وہ نصیحت حاصل کریں۔'),
(3299, 28, 47, '(اور شاید ہم رسول نہ بھیجتے) اگر یہ بات نہ ہوتی کہ اگر ان کے کرتوتوں کی پاداش میں انہیں کوئی مصیبت پہنچے تو وہ یہ نہ کہنے لگ جائیں کہ اے ہمارے پروردگار تو نے ہماری طرف کوئی رسول کیوں نہ بھیجا تاکہ ہم تیری آیتوں کی پیروی کرتے اور ہم ایمان لانے والوں میں سے ہوتے۔'),
(3300, 28, 48, 'جب ہماری طرف سے ان کے پاس حق آگیا (آخری برحق رسول آگیا) تو کہنے لگے اس (رسول) کو وہ کچھ کیوں نہ دیا گیا جو موسیٰ کو دیا گیا تھا کیا انہوں نے اس کا انکار نہیں کیا تھا؟ وہ کہتے ہیں کہ یہ دونوں جادوگر ہیں جو ایک دوسرے کی مدد کرتے ہیں اور کہتے تھے کہ ہم ہر ایک کے منکر ہیں۔'),
(3301, 28, 49, 'آپ کہیے! کہ تم سچے ہو تو اللہ کی طرف سے کوئی ایسی کتاب لاؤ جو ان دونوں (توراۃ، قرآن) سے زیادہ ہدایت کرنے والی ہو تو میں اسی کی پیروی کروں گا۔'),
(3302, 28, 50, 'اور اگر وہ آپ کی یہ بات قبول نہ کریں تو پھر سمجھ لیں کہ وہ صرف اپنی نفسانی خواہشوں کی پیروی کرتے ہیں اور اس شخص سے بڑھ کر گمراہ کون ہوگا جو خدائی ہدایت کے بغیر اپنی خواہش نفس کی پیروی کرے؟ بےشک خدا ظالم لوگوں کو ہدایت نہیں کرتا۔'),
(3303, 28, 51, 'اور ہم نے ان کیلئے کلام (ہدایت نظام) کا تسلسل جاری رکھا تاکہ وہ نصیحت حاصل کریں۔'),
(3304, 28, 52, 'جن کو ہم نے اس (نزولِ قرآن) سے پہلے کتاب عطا کی وہ اس پر ایمان لاتے ہیں۔'),
(3305, 28, 53, 'اور جب یہ (قرآن) ان کے سامنے پڑھا جاتا ہے تو وہ کہتے ہیں کہ ہم اس پر ایمان لائے بےشک یہ ہمارے پروردگار کی طرف سے حق ہے اور ہم تو اس سے پہلے ہی مسلم (اسے ماننے والے) ہیں۔'),
(3306, 28, 54, 'یہ وہ لوگ ہیں جنہیں ان کے صبر و ثبات کی وجہ سے دوہرا اجر عطا کیا جائے گا اور وہ برائی کا دفعیہ بھلائی سے کرتے ہیں اور ہم نے انہیں جو رزق دے رکھا ہے وہ اس میں سے (راہِ خدا میں) خرچ کرتے ہیں۔'),
(3307, 28, 55, 'اور وہ جب کوئی فضول بات سنتے ہیں تو اس سے روگردانی کرتے ہیں اور کہتے ہیں کہ ہمارے اعمال ہمارے لئے ہیں اور تمہارے اعمال تمہارے لئے! تم کو سلام! ہم جاہلوں کو پسند نہیں کرتے۔'),
(3308, 28, 56, '(اے رسول(ص)) آپ جسے چاہیں اسے ہدایت نہیں کر سکتے لیکن اللہ جسے چاہتا ہے اسے ہدایت دے دیتا ہے اور وہ ہدایت پانے والوں کو بہتر جانتا ہے۔'),
(3309, 28, 57, 'اور وہ (کفارِ مکہ) کہتے ہیں کہ اگر ہم آپ کے ساتھ ہدایت کی پیروی کریں تو ہمیں اپنی سر زمین سے اچک لیا جائے گا (یہاں سے نکال دیا جائے گا) کیا ہم نے ان کو ایسے امن والے محترم مقام میں جگہ نہیں دی؟ جہاں ہماری طرف سے رزق کے طور پر ہر قسم کے پھل لائے جاتے ہیں مگر ان میں سے اکثر لوگ جانتے نہیں ہیں۔'),
(3310, 28, 58, 'اور ہم نے کتنی ہی ایسی بستیاں ہلاک کر دیں جو اپنی معیشت (خوشحالی) پر اترا گئی تھیں (دیکھو) یہ ان کے مکانات ہیں جو (اجڑے) پڑے ہیں جو ان کے بعد کم ہی آباد ہوئے ہیں آخرکار ہم ہی وارث ہیں۔'),
(3311, 28, 59, 'اور آپ کا پروردگار بستیوں کو ہلاک کرنے والا نہیں ہے جب تک ان کے مرکزی مقام میں کوئی رسول نہ بھیج دے جو ہماری آیتوں کی تلاوت کرے اور ہم بستیوں کو ہلاک کرنے والے نہیں ہیں جب تک ان کے باشندے ظالم نہ ہوں۔'),
(3312, 28, 60, 'اور تمہیں جو کچھ بھی دیا گیا ہے یہ محض زندگانی دنیا کا سرمایہ ہے اور اسی کی زیب و زینت اور جو کچھ اللہ کے پاس ہے وہ بہتر ہے اور زیادہ پائیدار بھی تم عقل سے کیوں کام نہیں لیتے۔'),
(3313, 28, 61, 'کیا وہ شخص جس سے ہم نے اچھا وعدہ کیا ہے اور وہ اسے پانے والا بھی ہے۔ اس شخص کی مانند ہو سکتا ہے جسے ہم نے صرف دنیوی زندگانی کا (چند روزہ) سامان دیا ہے۔ اور پھر قیامت کے دن (سزا کیلئے پکڑ کر) حاضر کیا جائے گا۔'),
(3314, 28, 62, 'اور (وہ دن یاد رکھو) جس دن اللہ انہیں پکارے گا اور فرمائے گا (آج) کہاں ہیں میرے وہ شریک جن کو تم (میرا شریک) گمان کرتے تھے۔'),
(3315, 28, 63, 'جن پر خدا کا حکم (عذاب) نافذ ہو چکا ہوگا۔ کہیں گے اے ہمارے پروردگار! یہ ہیں وہ لوگ جنہیں ہم نے گمراہ کیا تھا ہم نے انہیں اسی طرح گمراہ کیا تھا جیسے ہم خود گمراہ تھے (اب) ہم تیری بارگاہ میں (ان سے) برأت کا اظہار کرتے ہیں کہ یہ ہماری پرستش نہیں کیا کرتے تھے۔'),
(3316, 28, 64, 'پھر (ان سے) کہا جائے گا کہ اپنے (مزعومہ) شریکوں کو بلاؤ چنانچہ وہ انہیں پکاریں گے مگر وہ انہیں کوئی جواب نہیں دیں گے۔ اور عذاب کو دیکھیں گے (اس پر وہ تمنا کریں گے کہ) کاش وہ ہدایت یافتہ ہوتے۔'),
(3317, 28, 65, 'اور (وہ دن یاد کرو) جس دن اللہ انہیں پکارے گا اور کہے گا کہ تم نے پیغمبروں کو کیا جواب دیا تھا؟'),
(3318, 28, 66, 'اس دن ان پر خبریں تاریک ہو جائیں گی (کوئی جواب نہ بن پڑے گا) اور نہ ہی یہ آپس میں ایک دوسرے سے پوچھ گچھ کر سکیں گے۔'),
(3319, 28, 67, 'البتہ جو توبہ کرے اور ایمان لائے اور نیک عمل کرے تو امید ہے کہ وہ فلاح پانے والوں میں سے ہوگا۔'),
(3320, 28, 68, 'اور (اے رسول) آپ کا پروردگار جو چاہتا ہے پیدا کرتا ہے اور (جسے چاہتا ہے) منتخب کرتا ہے لوگوں کو کوئی اختیار نہیں ہے اللہ پاک ہے اور جو وہ شرک کرتے ہیں وہ اس سے بلند و برتر ہے۔'),
(3321, 28, 69, 'اور آپ کا پروردگار جانتا ہے وہ جو کچھ ان کے سینے چھپائے ہوئے ہیں اور وہ بھی جو وہ ظاہر کرتے ہیں۔'),
(3322, 28, 70, 'وہی (ایک) اللہ ہے اس کے سوا کوئی اللہ نہیں ہے اسی کیلئے ہر قسم کی تعریف ہے دنیا میں بھی اور آخرت میں بھی اور اسی کی حکومت (فرمانروائی) ہے اور تم اسی کی طرف لوٹائے جاؤگے۔'),
(3323, 28, 71, 'آپ کہئے آیا تم نے کبھی غور کیا ہے کہ اگر اللہ قیامت تک تم پر رات مقرر (مسلط) کر دے تو اللہ کے سوا کون الہ ہے جو تمہارے پاس روشنی لائے؟ کیا تم سنتے نہیں ہو؟'),
(3324, 28, 72, 'اور کہئے! آیا تم نے کبھی غور کیا ہے کہ اگر قیامت تک تم پر دن مقرر (مسلط) کر دے تو اللہ کے سوا کون الٰہ ہے جو تمہارے پاس رات لائے جس میں تم آرام کرو کیا تم دیکھتے نہیں ہو؟'),
(3325, 28, 73, 'اور اس نے اپنی (خاص) رحمت سے تمہارے لئے رات اور دن بنائے تاکہ (رات میں) آرام کرو۔ اور (دن میں) اس کا فضل (روزی) تلاش کرو اور تاکہ تم شکر گزار بنو۔'),
(3326, 28, 74, 'اور (یاد کرو) وہ دن جس میں اللہ ان (مشرکین) کو پکارے گا اور کہے گا کہاں ہیں میرے شریک جن کے متعلق تم گمان فاسد کیا کرتے تھے۔'),
(3327, 28, 75, 'اور ہم ہر امت میں سے ایک گواہ نکال کر لائیں گے پھر ان سے کہیں گے کہ اپنی دلیل و برہان لاؤ تب انہیں معلوم ہوگا کہ حق اللہ ہی کیلئے ہے اور وہ جو افتراء پردازیاں کیا کرتے تھے وہ سب ان سے غائب ہو جائیں گی۔'),
(3328, 28, 76, 'بےشک قارون موسیٰ کی قوم میں سے تھا پھر وہ ان کے خلاف سرکش ہوگیا۔ اور ہم نے اسے اتنے خزانے عطا کئے تھے کہ اس کی چابیاں ایک طاقتور جماعت کو بھی گرانبار کر دیتی تھیں (اس سے مشکل سے اٹھتی تھیں) جبکہ اس کی قوم نے اس سے کہا کہ (اپنی منزلت پر) مت اترا بےشک اللہ اترانے والوں کو دوست نہیں رکھتا۔'),
(3329, 28, 77, 'اور اللہ نے تجھے جو کچھ (مال و زر) دیا ہے۔ اس سے آخرت کے گھر کی جستجو کر اور دنیا سے بھی اپنا حصہ فراموش نہ کر۔ اور جس طرح اللہ نے تیرے ساتھ احسان کیا ہے تو بھی اسی طرح (اس کے بندوں کے ساتھ) احسان کر۔ اور زمین میں فساد برپا کرنے کی خواہش نہ کر یقیناً اللہ فساد برپا کرنے والوں کو دوست نہیں رکھتا۔'),
(3330, 28, 78, 'قارون نے کہا کہ مجھے جو کچھ (مال و منال) ملا ہے وہ اس (خاص) علم کی بدولت ملا ہے جو میرے پاس ہے کیا وہ یہ نہیں جانتا کہ اللہ نے اس سے پہلے بہت سی ایسی نسلوں کو ہلاک کر دیا ہے جو قوت میں اس سے سخت تر اور جمعیت میں زیادہ تھیں اور مجرموں سے ان کے گناہوں کے بارے میں سوال نہیں کرنا پڑتا۔'),
(3331, 28, 79, 'وہ (ایک دن) اپنی قوم کے سامنے اپنی زیب و زینت کے ساتھ نکلا تو جو لوگ دنیاوی زندگی کے طلبگار تھے وہ کہنے لگے کہ کاش ہمیں بھی وہ (ساز و سامان) ملتا جو قارون کو دیا گیا ہے۔ بیشک وہ بڑا نصیب والا ہے۔'),
(3332, 28, 80, 'اور جن لوگوں کو علم دیا گیا تھا انہوں نے کہا وائے ہو تم پر! جو شخص ایمان لائے اور نیک عمل کرے اس کیلئے اللہ کا صلہ و ثواب اس سے بہتر ہے اور یہ (حکمت و منزلت) صرف صبر و ثبات کرنے والوں کو مرحمت کی جاتی ہے۔'),
(3333, 28, 81, 'پھر ہم نے قارون کو اور اس کے گھر کو زمین میں دھنسا دیا سو نہ اس کے حامیوں کی کوئی جماعت تھی جو اللہ کے مقابلہ میں اس کی مدد کرتی اور نہ ہی وہ اپنی مدد آپ کر سکا۔'),
(3334, 28, 82, 'اور وہ لوگ جو کل اس کے جاہ و مرتبہ کی تمنا کر رہے تھے اب کہنے لگے افسوس (اب پتہ چلا) کہ اللہ اپنے بندوں میں سے جس کا رزق چاہتا ہے کشادہ کر دیتا ہے اور جس کا چاہتا ہے تنگ کر دیتا ہے۔ اگر اللہ ہم پر (تنگدست بنا کر) احسان نہ کرتا تو ہمیں بھی زمین میں دھنسا دیتا اور کافر کبھی فلاح نہیں پاتے۔'),
(3335, 28, 83, 'یہ آخرت کا گھر ہم ان لوگوں کیلئے قرار دیتے ہیں جو زمین میں تکبر و سرکشی اور فساد برپا کرنے کا ارادہ بھی نہیں کرتے اور (نیک) انجام تو پرہیزگاروں کے ہی لئے ہے۔'),
(3336, 28, 84, 'جو کوئی بھلائی لے کر آئے گا اسے اس سے بہتر صلہ ملے گا اور جو کوئی برائی لے کر آئے گا تو ایسے لوگوں کو جو برائیاں کرتے ہیں تو انہیں بدلہ بھی اتنا ہی ملے گا جتنا وہ (برائی) کرتے تھے۔'),
(3337, 28, 85, '(اے رسول(ص)) جس (خدا) نے آپ پرقرآن (کا پہنچانا) فرض کیا ہے وہ آپ کو واپسی کی منزل (مکہ) تک پھر پہنچا کر رہے گا۔ آپ کہئے! میرا پروردگار بہتر جانتا ہے کہ ہدایت لے کر کون آیا ہے اور کھلی ہوئی گمراہی میں کون ہے؟'),
(3338, 28, 86, 'اور آپ کو اس بات کی امید نہیں تھی کہ آپ پر (یہ) کتاب نازل کی جائے گی۔ یہ تو بس آپ کے پروردگار کی رحمت ہے۔ لہٰذا آپ بھی کافروں کے پشت پناہ نہ بنئے گا۔'),
(3339, 28, 87, 'اور (خیال رکھنا) کہ یہ لوگ آپ کو اللہ کی آیتوں (کی تبلیغ) سے کہیں روک نہ دیں بعد اس کے کہ وہ آپ پر نازل ہو چکی ہیں اور آپ اپنے پروردگار کی طرف (لوگوں کو) بلائیں اور ہرگز مشرکوں میں سے نہ ہونا۔'),
(3340, 28, 88, 'اور اللہ کے ساتھ کسی اور الٰہ کو نہ پکاریں اس کے سوا کوئی الٰہ نہیں ہے ہر شئ ہلاک ہونے والی ہے سوائے اس کے وجہ (ذات) کے اسی کی حکومت (اور فرمانروائی) ہے اور اسی کی طرف تم (سب) لوٹائے جاؤگے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف۔ لام۔ میم۔'),
(3342, 29, 2, 'کیا لوگوں نے یہ گمان کر رکھا ہے کہ ان کے (زبانی) کہنے سے کہ ہم ایمان لے آئے ہیں چھوڑ دیئے جائیں گے اور ان کی آزمائش نہیں کی جائیگی؟'),
(3343, 29, 3, 'حالانکہ ہم نے ان (سب) کی آزمائش کی تھی جو ان سے پہلے گزر چکے ہیں سو اللہ ضرور معلوم کرے گا ان کو جو (دعوائے ایمان میں) سچے ہیں اور ان کو بھی معلوم کرے گا جو جھوٹے ہیں۔'),
(3344, 29, 4, 'جو لوگ برے کام کرتے ہیں کیا انہوں نے یہ گمان کر رکھا ہے کہ وہ ہم سے آگے نکل جائیں گے؟ وہ کتنا برا حکم لگاتے ہیں۔'),
(3345, 29, 5, 'جو کوئی اللہ کے حضور حاضری کی امید رکھتا ہے تو بیشک اللہ کا مقررہ وقت ضرور آنے والا ہے اور وہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(3346, 29, 6, 'اور جو کوئی جہاد کرتا ہے تو وہ اپنے ہی لئے جہاد کرتا ہے۔ بےشک اللہ تمام جہانوں سے بے نیاز ہے۔'),
(3347, 29, 7, 'اور جو لوگ ایمان لائے اور نیک عمل بھی کئے تو ہم ان کی برائیاں دور کر دیں گے (ان کی غلطیوں کو نظر انداز کر دیں گے) اور ہم انہیں ان کے اعمال کی بہترین جزا دیں گے۔'),
(3348, 29, 8, 'اور ہم نے انسان کو وصیت کی ہے یعنی حکم دیا ہے کہ وہ اپنے والدین کے ساتھ اچھا سلوک کرے اور (یہ بھی کہ) اگر وہ تجھ پر زور ڈالیں کہ تو کسی ایسی چیز کو میرا شریک بنا جس کا تجھے کوئی علم نہیں ہے تو پھر ان کی اطاعت نہ کر تم سب کی بازگشت میری ہی طرف ہے تو میں تمہیں بتاؤں گا جو کچھ تم کیا کرتے تھے۔'),
(3349, 29, 9, 'اور جو لوگ ایمان لائے اور نیک عمل بھی بجا لائے ہم ضرور انہیں صالحین میں داخل کریں گے۔'),
(3350, 29, 10, 'اور لوگوں میں سے کچھ ایسے بھی ہیں جو (زبان سے) کہتے ہیں کہ ہم اللہ پر ایمان لائے پھر اللہ کی راہ میں جب اسے اذیت پہنچائی جاتی ہے تو وہ ان لوگوں کی آزمائش کو اللہ کے عذاب کے مانند قرار دے دیتا ہے اور اگر آپ کے پروردگار کی طرف سے کوئی مدد پہنچ جائے تو وہ کہتے ہیں کہ ہم (بھی) تو تمہارے ساتھ تھے کیا جو کچھ دنیا جہاں والوں کے دلوں میں ہے کیا اللہ اس کا سب سے بہتر جاننے والا نہیں ہے۔'),
(3351, 29, 11, 'اور اللہ ضرور معلوم کرے گا کہ مؤمن کون ہیں اور منافق کون؟'),
(3352, 29, 12, 'اور کافر لوگ اہلِ ایمان سے کہتے ہیں کہ تم ہمارے راستہ پر چلو تمہارے گناہوں (کا بوجھ) ہم اٹھائیں گے۔ حالانکہ یہ ان کے گناہوں کو کچھ بھی اٹھانے والے نہیں وہ بالکل جھوٹے ہیں۔'),
(3353, 29, 13, '(ہاں البتہ) یہ لوگ اپنے بوجھ اٹھائیں گے اور اپنے بوجھوں کے ساتھ کچھ اور بوجھ بھی۔ اور قیامت کے دن ضرور ان سے بازپرس کی جائے گی ان افترا پردازیوں کے بارے میں جو وہ کرتے تھے۔'),
(3354, 29, 14, 'اور یقیناً ہم نے نوح کو ان کی قوم کی طرف بھیجا اور وہ اس میں پچاس سال کم ایک ہزار سال رہے پھر (آخرکار) اس قوم کو طوفان نے آپکڑا اس حال میں کہ وہ ظالم تھے۔'),
(3355, 29, 15, 'پس ہم نے نوح کو اور کشتی والوں کو نجات دی اور اس (کشتی) کو تمام جہانوں کیلئے (اپنی ایک) نشانی بنا دیا۔'),
(3356, 29, 16, 'اور (ہم نے) ابراہیم کو (بھیجا) جبکہ انہوں نے اپنی قوم سے کہا کہ اللہ کی عبادت کرو اور اس (کی نافرمانی) سے ڈرو اگر تم کچھ سمجھ رکھتے ہو تو یہ تمہارے لئے بہتر ہے۔'),
(3357, 29, 17, 'تم اللہ کو چھوڑ کر محض بتوں کی پرستش کرتے ہو اور تم ایک جھوٹ گھڑتے ہو۔ بےشک یہ جن کی تم اللہ کو چھوڑ کر عبادت کرتے ہو وہ تمہاری روزی کے مالک و مختار نہیں ہیں۔ پس تم اللہ سے روزی طلب کرو اور اسی کی عبادت کرو اور اسی کا شکر ادا کرو۔ اور اسی کی طرف تم لوٹائے جاؤگے۔'),
(3358, 29, 18, 'اور اگر تم (مجھے) جھٹلاتے ہو (تو یہ کوئی نئی بات نہیں ہے) تم سے پہلے بھی کئی امتیں (اپنے پیغمبروں کو) جھٹلا چکی ہیں اور رسول کی ذمہ داری صرف واضح تبلیغ کرنا ہے۔ و بس۔'),
(3359, 29, 19, 'کیا ان لوگوں نے نہیں دیکھا کہ اللہ کس طرح پہلی بار مخلوق کو پیدا کرتا ہے پھر کس طرح اسے دوبارہ پلٹاتا ہے؟ بےشک یہ بات اللہ کیلئے آسان ہے۔'),
(3360, 29, 20, 'آپ کہئے! کہ زمین میں چلو پھرو پھر (غور سے) دیکھو کہ اللہ نے کس طرح مخلوق کو پہلی بار پیدا کیا؟ پھر (اسی طرح) اللہ پچھلی بار بھی پیدا کرے گا۔ بےشک اللہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(3361, 29, 21, 'وہ جسے چاہتا ہے سزا دیتا ہے اور جس پر چاہتا ہے رحم کرتا ہے اور اسی کی طرف تم سب لوٹائے جاؤگے۔'),
(3362, 29, 22, 'اور تم (اللہ کو) نہ زمین میں عاجز کر سکتے ہو اور نہ آسمان میں اور نہ ہی اللہ کے سوا تمہارا کوئی سرپرست و کارساز ہے اور نہ ہی کوئی مددگار۔'),
(3363, 29, 23, 'اور جن لوگوں نے اللہ کی آیتوں اور اس کی بارگاہ میں حاضری کا انکار کیا ہے وہ میری رحمت سے مایوس ہوگئے ہیں اور ان کیلئے دردناک عذاب ہے۔'),
(3364, 29, 24, 'اور ان (ابراہیم) کی قوم کا جواب اس کے سوا اور کوئی نہیں تھا کہ انہیں قتل کر دو یا آگ میں جلا دو تو اللہ نے انہیں آگ سے بچا لیا۔ بےشک اس میں ایمان لانے والوں کیلئے (بڑی) نشانیاں ہیں۔'),
(3365, 29, 25, 'اور ابراہیم نے (اپنی قوم سے) کہا کہ تم نے اللہ کو چھوڑ کر زندگانی دنیا میں باہمی محبت کا ذریعہ سمجھ کر ان بتوں کو اختیار کر رکھا ہے پھر قیامت کے دن تم ایک دوسرے کا انکار کروگے اور ایک دوسرے پر لعنت کروگے اور تمہارا ٹھکانہ دوزخ ہوگا۔ اور تمہارا کوئی مددگار نہ ہوگا۔'),
(3366, 29, 26, 'اور لوط نے ان (ابراہیم) کی بات مانی اور اطاعت کی اور انہوں نے کہا میں اپنے پروردگار کی طرف ہجرت کرنے والا ہوں۔ بیشک وہ غالب (اور) بڑا حکمت والا ہے۔'),
(3367, 29, 27, 'اور ہم نے انہیں اسحاق اور یعقوب عطا کئے۔ اور نبوت اور کتاب ان کی نسل میں قرار دے دی۔ اور ہم نے ان کو دنیا میں بھی ان کا صلہ دیا اور آخرت میں یقیناً وہ صالحین میں سے ہوں گے۔'),
(3368, 29, 28, 'اور (ہم نے) لوط کو (بھیجا) جبکہ انہوں نے اپنی قوم سے کہا کہ تم بے حیائی کا وہ کام کرتے ہو جو تم سے پہلے دنیا جہان والوں میں سے کسی نے نہیں کیا۔'),
(3369, 29, 29, 'کیا تم (عورتوں کو چھوڑ کر) مردوں کے پاس جاتے ہو؟ اور راہزنی کرتے ہو اور اپنے مجمع میں برے کام کرتے ہو۔ تو ان کی قوم کے پاس اس کے سوا کوئی جواب نہ تھا کہ انہوں نے کہا (اے لوط) اگر تم سچے ہو تو ہم پر اللہ کا عذاب لاؤ۔'),
(3370, 29, 30, 'لوط نے کہا اے میرے پروردگار! ان مفسد لوگوں کے مقابلہ میں میری مدد فرما۔'),
(3371, 29, 31, 'اور جب ہمارے بھیجے ہوئے (فرشتے) ابراہیم کے پاس خوشخبری لے کر آئے۔ تو انہوں نے (اثنائے گفتگو میں یہ بھی) کہا کہ ہم اس (لوط کی) بستی والوں کو ہلاک کرنے والے ہیں (کیونکہ) اس بستی کے لوگ (بڑے) ظالم ہیں۔'),
(3372, 29, 32, 'ابراہیم نے کہا اس بستی میں تو لوط بھی ہیں؟ فرشتوں نے کہا ہم بہتر جانتے ہیں کہ اس میں کون کون ہیں؟ ہم انہیں اور ان کے گھر والوں کو بچا لیں گے۔ سوائے ان کی بیوی کے کہ وہ پیچھے رہ جانے والوں میں سے ہے۔'),
(3373, 29, 33, 'اور جب (وہ) ہمارے بھیجے ہوئے (فرشتے) لوط کے پاس آئے تو وہ ان کی آمد سے رنجیدہ اور دل تنگ ہوئے اور فرشتوں نے (ان کی حالت دیکھ کر) کہا کہ نہ ڈرو اور نہ رنج کرو ہم آپ کو اور آپ کے گھر والوں کو بچا لیں گے سوائے آپ کی بیوی کے جو پیچھے رہ جانے والوں میں سے ہے۔'),
(3374, 29, 34, 'ہم اس بستی والوں پر ان کی نافرمانیوں کی وجہ سے آسمان سے عذاب نازل کرنے والے ہیں۔'),
(3375, 29, 35, 'اور ہم نے اس بستی کے کچھ نشان باقی چھوڑ دیئے (ان لوگوں کی عبرت کیلئے) جو عقل سے کام لیتے ہیں۔'),
(3376, 29, 36, 'اور ہم نے مدین (والوں) کی طرف ان کے بھائی شعیب کو بھیجا۔ تو انہوں نے کہا: اے میری قوم! اللہ کی عبادت کرو اور روز آخرت کی امید رکھو۔ اور زمین میں فساد پھیلاتے نہ پھرو۔'),
(3377, 29, 37, 'پس ان لوگوں نے آپ کو جھٹلایا تو (انجام کار) ایک (سخت) زلزلے نے انہیں آپکڑا اور وہ اپنے گھروں میں گھٹنوں کے بل (مردہ ہوکر) گر گئے۔'),
(3378, 29, 38, 'اور ہم نے عاد و ثمود کو بھی ہلاک کر دیا۔ اور (یہ بات) ان کے (تباہ شدہ) مکانات سے واضح ہو چکی ہے اور شیطان نے ان کے (برے) کاموں کو خوشنما کرکے پیش کیا اور انہیں (سیدھے) راستہ سے روک دیا۔ حالانکہ وہ سمجھ دار تھے۔'),
(3379, 29, 39, 'اور ہم نے قارون، فرعون اور ہامان کو (بھی ہلاک کیا) اور البتہ موسیٰ ان کے پاس کھلی نشانیاں لے کر آئے۔ مگر انہوں نے زمین میں تکبر و غرور کیا حالانکہ وہ (ہم سے) آگے نکل جانے والے نہیں تھے۔'),
(3380, 29, 40, 'پس ہم نے ہر ایک کو اس کے گناہ کی پاداش میں پکڑا۔ سو ان میں سے بعض پر (پتھراؤ کرنے والی) تیز آندھی بھیجی اور بعض کو ہولناک چنگھاڑ نے آدبایا اور بعض کو ہم نے زمین میں دھنسایا اور بعض کو (دریا میں) غرق کر دیا اور اللہ ایسا نہ تھا کہ ان پر ظلم کرتا بلکہ وہ خود اپنے اوپر ظلم کرتے تھے۔'),
(3381, 29, 41, 'جن لوگوں نے اللہ کو چھوڑ کر اور سرپرست اور کارساز بنا لئے ہیں ان کی مثال مکڑی جیسی ہے جس نے (جالے کا) ایک گھر بنایا اور یقیناً تمام گھروں سے زیادہ کمزور مکڑی کا گھر ہے۔ کاش لوگ (یہ حقیقت) جانتے۔'),
(3382, 29, 42, 'اللہ کو چھوڑ کر یہ لوگ جس شئے کو پکارتے ہیں۔ بےشک اللہ اسے جانتا ہے۔ وہ غالب ہے، بڑا حکمت والا ہے۔'),
(3383, 29, 43, 'اور یہ مثالیں ہم پیش تو سب لوگوں کے سامنے کرتے ہیں مگر انہیں سمجھتے صرف اہل علم ہیں۔'),
(3384, 29, 44, 'اللہ نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا ہے۔ بےشک اس میں اہلِ ایمان کیلئے (اس کی قدرت کی) ایک نشانی موجود ہے۔'),
(3385, 29, 45, '(اے رسول(ص)) آپ اس (کتاب) کی تلاوت کریں جو آپ کی طرف وحی کی گئی ہے اور نماز قائم کریں۔ بے شک نماز بے حیائی اور برائی سے روکتی ہے اور اللہ کا ذکر سب سے بڑی چیز ہے اور جو کچھ تم کرتے ہو اللہ اسے جانتا ہے.'),
(3386, 29, 46, 'اور (اے مسلمانو!) تم اہلِ کتاب سے بحث و مباحثہ نہ کرو مگر بہترین انداز سے سوائے ان کے جو ان میں سے ظالم ہیں اور (ان سے) کہو کہ ہم تو اس (کتاب) پر بھی ایمان لائے ہیں جو ہماری طرف نازل کی گئی ہے اور اس (کتاب پر بھی) جو تمہاری طرف نازل کی گئی ہے اور ہمارا اور تمہارا الٰہ (اللہ) ایک ہے اور ہم سب اسی کے فرمانبردار ہیں۔'),
(3387, 29, 47, 'اور (اے رسول(ص)) ہم نے اسی طرح آپ پر کتاب نازل کی ہے (جس طرح پہلے رسولوں پر کتابیں نازل کی تھیں) تو جن کو ہم نے (پہلے) کتاب دی تھی وہ اس (قرآن) پر ایمان لاتے ہیں اور ان (اہلِ مکہ و عرب) میں سے بھی بعض اس پر ایمان لا رہے ہیں اور ہماری آیتوں کا کافروں کے سوا اور کوئی انکار نہیں کرتا۔'),
(3388, 29, 48, 'اور (اے رسول(ص)) آپ اس (نزولِ قرآن) سے پہلے نہ کوئی کتاب پڑھتے تھے اور نہ ہی اپنے دائیں ہاتھ سے کچھ لکھتے تھے اگر ایسا ہوتا تو یہ اہلِ باطل ضرور (آپ کی نبوت میں) شک کرتے۔'),
(3389, 29, 49, 'بلکہ وہ (قرآن) کھلی ہوئی آیتیں ہیں جو ان لوگوں کے سینہ میں ہیں جنہیں علم دیا گیا ہے اور ہماری آیتوں کا ظالموں کے سوا اور کوئی انکار نہیں کرتا۔'),
(3390, 29, 50, 'اور وہ کہتے ہیں کہ اس شخص (پیغمبرِ اسلام(ص)) پر ان کے پروردگار کی طرف سے معجزات کیوں نہیں اتارے گئے؟ آپ کہہ دیجئے! کہ معجزات تو بس اللہ کے پاس ہیں میں تو صرف (عذابِ الٰہی) سے واضح طور پر ڈرانے والا ہوں۔'),
(3391, 29, 51, 'کیا ان کیلئے یہ (معجزہ) کافی نہیں ہے کہ ہم نے آپ پر (یہ) کتاب نازل کی ہے۔ جو انہیں پڑھ کر سنائی جاتی ہے؟ بے شک اس میں ایمان والوں کیلئے رحمت اور نصیحت موجود ہے۔'),
(3392, 29, 52, '(اے رسول(ص)) آپ کہہ دیجئے! کہ میرے اور تمہارے درمیان گواہی کیلئے اللہ کافی ہے جو کچھ آسمانوں اور زمین میں ہے وہ سب کچھ جانتا ہے اور جو لوگ باطل پر ایمان لاتے ہیں اور اللہ کا ذکر کرتے ہیں وہی نقصان اٹھانے والے ہیں۔'),
(3393, 29, 53, 'اور یہ لوگ آپ سے جلدی عذاب لانے کا مطالبہ کرتے ہیں حالانکہ اگر (اس کیلئے) ایک وقتِ مقرر نہ ہوتا تو ان پر (کبھی کا) عذاب آچکا ہوتا۔ اور وہ اس طرح ان پر اچانک آپڑے گا کہ انہیں خبر بھی نہ ہوگی۔'),
(3394, 29, 54, 'وہ آپ سے جلدی عذاب لانے کا مطالبہ کرتے ہیں حالانکہ جہنم کافروں کو گھیرے ہوئے ہے۔'),
(3395, 29, 55, 'جس دن عذاب ان کو اوپر سے اور پاؤں کے نیچے سے ڈھانک لے گا اور خدا کہے گا کہ (اب) مزہ چکھو اس کا جو کچھ تم (دنیا میں) کرتے رہے ہو (تب ان کو پتہ چلے گا)۔'),
(3396, 29, 56, 'اے میرے وہ بندو جو ایمان لائے ہو! میری زمین (بہت) وسیع ہے۔ پس تم میری ہی عبادت کرو۔'),
(3397, 29, 57, 'ہر نفس موت کا مزہ چکھنے والا ہے پھر تم سب ہماری طرف لوٹائے جاؤگے۔'),
(3398, 29, 58, 'اور جو لوگ ایمان لائے اور نیک عمل کئے ہم ان کو جنت کے بالا خانوں میں جگہ دیں گے جن کے نیچے نہریں جاری ہوں گی (اور) وہ ان میں ہمیشہ رہیں گے کیا اچھا اجر ہے (نیک) عمل کرنے والوں کا۔'),
(3399, 29, 59, 'جنہوں نے صبر کیا اور اپنے پروردگار پر بھروسہ کرتے ہیں۔'),
(3400, 29, 60, 'اور کتنے زمین پر چلنے والے (جانور) ایسے ہیں جو اپنا رزق اٹھائے نہیں پھرتے اللہ ہی انہیں رزق دیتا ہے اور تمہیں بھی۔ اور وہ بڑا سننے والا (اور) بڑا جاننے والا ہے۔'),
(3401, 29, 61, 'اور اگر آپ ان (مشرکوں) سے پوچھیں کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے؟ اور سورج اور چاند کو کس نے مسخر کیا ہے؟ تو وہ ضرور کہیں گے کہ اللہ نے۔ تو پھر وہ کدھر جا رہے ہیں؟'),
(3402, 29, 62, 'اللہ ہی اپنے بندوں میں سے جس کو چاہتا ہے روزی کشادہ کر دیتا ہے اور جس کی چاہتا ہے تنگ کر دیتا ہے۔ بے شک اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(3403, 29, 63, 'اور اگر آپ ان سے پوچھیں کہ آسمان سے پانی کس نے نازل کیا؟ اور زمین کو مردہ ہونے (بنجر ہونے) کے بعد کس نے زندہ (آباد) کیا؟ تو وہ ضرور کہیں گے اللہ نے! آپ کہیے! الحمد ﷲ۔ لیکن اکثر لوگ عقل سے کام نہیں لیتے۔'),
(3404, 29, 64, 'اور یہ دنیاوی زندگی تو محض کھیل تماشہ ہے اور حقیقی زندگی تو آخرت والی ہے۔ کاش لوگوں کو اس (حقیقت) کا علم ہوتا۔'),
(3405, 29, 65, 'پس جب یہ لوگ کشتی میں سوار ہوتے ہیں تو اپنے دین (و اعتقاد) کو اللہ کیلئے خالص کرکے اس سے دعا مانگتے ہیں پھر جب انہیں نجات دے کر خشکی کی طرف لے آتا ہے تو ایک دم وہ شرک کرنے لگتے ہیں۔'),
(3406, 29, 66, 'تاکہ جو نعمت ہم نے انہیں عطا کی ہے وہ اس کا کفران کریں اور تاکہ وہ لطف اٹھا لیں سو انہیں (اس کا انجام) عنقریب معلوم ہو جائے گا۔'),
(3407, 29, 67, 'کیا یہ لوگ نہیں دیکھتے کہ ہم نے (ان کے شہر کو) امن والا حرم بنا دیا ہے۔ حالانکہ ان کے گرد و پیش کے لوگوں کو اچک لیا جاتا ہے (پھر بھی) یہ لوگ باطل پر ایمان لاتے ہیں اور اللہ کی نعمت کا کفران (ناشکری) کرتے ہیں۔'),
(3408, 29, 68, 'اس شخص سے بڑا ظالم کون ہے جو اللہ پر جھوٹا بہتان باندھے یا حق کو جھٹلائے جبکہ وہ اس کے پاس آچکا ہے! کیا کافروں کا ٹھکانا جہنم نہیں ہے؟'),
(3409, 29, 69, 'اور جو لوگ ہماری خاطر جدوجہد کرتے ہیں ہم ان کو ضرور اپنے راستے دکھا دیتے ہیں اور بے شک اللہ محسنین کے ساتھ ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف، لام، میم۔'),
(3411, 30, 2, 'رومی مغلوب ہوگئے۔'),
(3412, 30, 3, 'قریب ترین زمین میں (اہلِ فارس سے) اور یہ مغلوب ہونے کے بعد عنقریب غالب آجائیں گے۔'),
(3413, 30, 4, 'چند سالوں میں اختیار اللہ کو ہی حاصل ہے پہلے بھی اور بعد بھی۔ اور اس دن اہلِ ایمان خوش ہوں گے۔'),
(3414, 30, 5, 'اللہ کی نصرت سے، اللہ جسے چاہتا ہے نصرت عطا فرماتا ہے اور وہ غالب ہے (اور) بڑا رحم کرنے والا ہے۔'),
(3415, 30, 6, 'یہ اللہ کا وعدہ ہے اللہ اپنے وعدہ کی خلاف ورزی نہیں کرتا لیکن اکثر لوگ جانتے نہیں ہیں۔'),
(3416, 30, 7, 'یہ لوگ صرف دنیاوی زندگی کے ظاہر کو جانتے ہیں اور آخرت سے بالکل غافل ہیں۔'),
(3417, 30, 8, 'کیا انہوں نے اپنے آپ میں کبھی غور و فکر نہیں کیا؟ اللہ نے آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے سب کو (حق اور حکمت کے) ساتھ ایک مقررہ مدت تک کیلئے پیدا کیا ہے اور یقیناً بہت سے لوگ اپنے پروردگار کی بارگاہ میں حضوری کے منکر ہیں۔'),
(3418, 30, 9, 'آیا یہ لوگ زمین میں چلتے پھرتے نہیں ہیں کہ دیکھتے کہ جو لوگ ان سے پہلے تھے ان کا انجام کیا ہوا؟ وہ قوت میں ان سے زیادہ مضبوط تھے اور انہوں نے ان سے زیادہ زمین کو جَوتا بویا اور آباد کیا تھا۔ اور ان کے پاس ان کے رسول واضح نشانیاں لے کر آئے تو اللہ ایسا نہ تھا کہ ان پر ظلم کرتا لیکن وہ خود اپنے آپ پر ظلم کیا کرتے تھے۔'),
(3419, 30, 10, 'پھر ان لوگوں کا انجام جنہوں نے (مسلسل) برائیاں کیں برا ہی ہوا کیونکہ انہوں نے اللہ کی آیتوں کو جھٹلایا تھا اور ان کا مذاق اڑایا کرتے تھے۔'),
(3420, 30, 11, 'اللہ ہی تخلیق کی ابتداء کرتا ہے وہی پھر اس کا اعادہ کرے گا۔ پھر اسی کی طرف تم لوٹائے جاؤگے۔'),
(3421, 30, 12, 'اور جس دن قیامت قائم ہوگی (اس دن) مجرم مایوس اور بےآس ہو جائیں گے۔'),
(3422, 30, 13, 'اور ان کے بنائے شریکوں میں سے کوئی ان کا سفارشی نہ ہوگا اور وہ بھی اپنے شریکوں سے انکار کر جائیں گے۔'),
(3423, 30, 14, 'اور جس دن قیامت قائم ہوگی اس دن (مؤمن و کافر) الگ الگ ہو جائیں گے۔'),
(3424, 30, 15, 'پس وہ لوگ جو ایمان لائے تھے اور نیک عمل کئے تھے وہ باغ (بہشت) میں خوش و خرم اور محترم ہوں گے۔'),
(3425, 30, 16, 'اور جنہوں نے کفر کیا اور ہماری آیتوں اور آخرت کی حضوری کو جھٹلایا وہ (ہر وقت) عذاب میں حاضر رکھے جائیں گے۔'),
(3426, 30, 17, 'پس تم جب شام کرو اور جب صبح کرو تو اللہ کی تسبیح کرو (اس کی پاکیزگی بیان کرو)۔'),
(3427, 30, 18, 'اور آسمانوں میں اور زمین میں اور سہ پہر کو اور جب تم ظہر کرتے ہو ساری حمد و ثنا اسی خدا کے لئے ہے۔'),
(3428, 30, 19, 'وہ زندہ کو مردے سے نکالتا ہے اور مردے کو زندہ سے نکالتا ہے۔ اور زمین کو اس کی موت کے بعد زندہ (سرسبز) کر دیتا ہے۔ اسی طرح تم (مرنے کے بعد) نکالے جاؤگے۔'),
(3429, 30, 20, 'اور اس کی (قدرت) کی نشانیوں سے (ایک) یہ بھی ہے کہ اس نے تمہیں مٹی سے پیدا کیا اور پھر تم ایک دم آدمی بن کر پھیلتے جا رہے ہو۔'),
(3430, 30, 21, 'اور اس کی (قدرت کی) نشانیوں میں سے یہ بھی ہے کہ اس نے تمہارے لئے تمہاری ہی جنس سے بیویاں پیدا کیں تاکہ تم ان سے سکون حاصل کرو۔ اور تمہارے درمیان محبت اور رحمت (نرم دلی و ہمدردی) پیدا کر دی۔ بے شک اس میں غور و فکر کرنے والوں کیلئے بہت نشانیاں ہیں۔'),
(3431, 30, 22, 'اور اس کی نشانیوں میں سے آسمانوں اور زمین کا پیدا کرنا اور تمہاری زبانوں اور تمہارے رنگوں کا مختلف ہونا بھی ہے۔ بے شک اس میں اہلِ علم کے لئے بہت سی نشانیاں موجود ہیں۔'),
(3432, 30, 23, 'اور اس کی نشانیوں میں سے تمہارا رات اور دن میں سونا ہے اور (دن میں) اس کے فضل (روزی) کا تلاش کرنا بھی بے شک اس میں (غور سے) سننے والوں کیلئے بہت سی نشانیاں ہیں۔'),
(3433, 30, 24, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ وہ تمہیں ڈرانے اور امید دلانے کے لئے بجلی دکھاتا ہے اور آسمان سے پانی برساتا ہے پھر اس کے ذریعہ سے زمین کو اس کی موت (خشکی) کے بعد زندہ (شاداب) کر دیتا ہے۔ بے شک اس میں عقل سے کام لینے والے لوگوں کیلئے بہت سی نشانیاں ہیں۔'),
(3434, 30, 25, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ آسمان اور زمین اس کے حکم سے قائم ہیں پھر جب وہ تمہیں زمین سے ایک ہی بار پکارے گا تو تم یکبارگی نکل پڑوگے۔'),
(3435, 30, 26, 'جو کوئی آسمانوں اور زمین میں ہے اسی کا ہے (اور) سب اسی کے تابعِ فرمان ہیں۔'),
(3436, 30, 27, 'اور وہ وہی ہے جو تخلیق کی ابتداء کرتا ہے پھر وہی اس کو دوبارہ پیدا کرے گا۔ اور یہ اس کیلئے زیادہ آسان ہے اور آسمانوں اور زمین میں اس کی صفت سب سے اعلیٰ ہے۔ وہ غالب ہے اور بڑا حکمت والا ہے۔'),
(3437, 30, 28, 'وہ (اللہ) تمہارے لئے تمہاری اپنی ذات (اور تمہارے حالات) سے ایک مثل بیان کرتا ہے کہ تمہارے غلاموں میں سے کوئی تمہارا اس طرح شریک ہے ان چیزوں میں جو ہم نے تمہیں عطا کر رکھی ہیں کہ تم اور وہ برابر کے حصہ دار بن جاؤ کہ تم ان سے اس طرح ڈرنے لگو۔ جس طرح تم آپس میں ایک دوسرے (برابر والے) سے ڈرتے ہو؟ ہم عقل سے کام لینے والوں کیلئے اسی طرح آیتیں کھول کر بیان کرتے ہیں۔'),
(3438, 30, 29, 'بلکہ ظالم لوگ سمجھے بوجھے بھی اپنی خواہشات کی پیروی کر رہے ہیں۔ جسے اللہ (اس کی نافرمانی کی وجہ سے) گمراہی میں چھوڑ دے اسے کون ہدایت دے سکتا ہے؟ اور ان کا کوئی ناصر و مددگار نہیں ہے۔'),
(3439, 30, 30, 'پس (اے رسول(ص)) آپ باطل سے کنارہ کش ہوکر اپنا رخ دین (حق) کی طرف رکھیں یعنی اس (دینِ) فطرت کی پیروی کریں جس پر اس نے انسانوں کو پیدا کیا ہے۔ اللہ کی تخلیق میں کوئی تبدیلی نہیں ہے یہی سیدھا دین ہے مگر اکثر لوگ جانتے نہیں ہیں۔'),
(3440, 30, 31, '(تم اپنا رخ اسلام کی طرف رکھو) اللہ کی طرف رجوع کرتے ہوئے اور اس سے ڈرو اور نماز قائم کرو اور (ان) مشرکین میں سے نہ ہو جاؤ۔'),
(3441, 30, 32, 'جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کر دیا اور خود گروہ گروہ ہو گئے۔ (پھر) ہر گروہ اس پر خوش ہے جو کچھ اس کے پاس ہے۔'),
(3442, 30, 33, 'اور جب لوگوں کو کوئی تکلیف پہنچتی ہے تو وہ اپنے پروردگار کی طرف رجوع کرتے ہوئے اسے پکارتے ہیں پھر جب وہ انہیں اپنی طرف سے رحمت کا مزہ چکھاتا ہے تو یکایک ان میں سے ایک گروہ پروردگار کے ساتھ شرک کرنے لگتا ہے۔'),
(3443, 30, 34, 'تاکہ اس (نعمت) کی ناشکری کریں جو ہم نے انہیں عطا کی ہے۔ اچھا کچھ دن مزہ حاصل کر لو۔ عنقریب تمہیں اس کا انجام معلوم ہو جائے گا۔'),
(3444, 30, 35, 'کیا ہم نے ان پر کوئی سند (دلیل) نازل کی ہے جو انہیں شرک کرنے کو کہہ رہی ہے جو وہ کر رہے ہیں۔'),
(3445, 30, 36, 'اور جب ہم لوگوں کو (اپنی) زحمت کا مزہ چکھاتے ہیں تو وہ اس سے خوش ہو جاتے ہیں اور جب ان پر ان کے اعمال کی پاداش میں جو وہ پہلے اپنے ہاتھوں کر چکے ہیں کوئی مصیبت آتی ہے تو ایک دم مایوس ہو جاتے ہیں۔'),
(3446, 30, 37, 'کیا یہ لوگ نہیں دیکھتے کہ اللہ جس کا چاہتا ہے رزق کشادہ کر دیتا ہے اور جس کا چاہتا ہے تنگ کر دیتا ہے۔ بے شک اس میں ایمان والوں کیلئے (قدرتِ خدا کی) بہت سی نشانیاں ہیں۔'),
(3447, 30, 38, 'پس (اے رسول(ص)) آپ (اپنے) قرابتدار مسکین اور مسافر کو ان کا حق دے دیں۔ یہ ان لوگوں کیلئے بہتر ہے جو رضائے خدا کے طلبگار ہیں اور یہی لوگ فلاح پانے والے ہیں۔'),
(3448, 30, 39, 'اور جو چیز (روپیہ) تم اس لئے سود پر دیتے ہو کہ لوگوں کے مال میں شامل ہوکر بڑھ جائے تو وہ اللہ کے نزدیک نہیں بڑھتا اور جو زکوٰۃ تم خوشنودئ خدا کیلئے دیتے ہو ایسے ہی لوگ اپنا مال (کئی گنا) بڑھانے والے ہیں۔'),
(3449, 30, 40, 'وہ اللہ ہی ہے جس نے (پہلے) تمہیں پیدا کیا۔ پھر تمہیں رزق دیا۔ پھر وہ تمہیں موت دے گا اور پھر تمہیں زندہ کرے گا۔ کیا تمہارے بنائے ہوئے شریکوں میں سے کوئی ایسا ہے جو ان کاموں میں سے کوئی کام کر سکے؟ اللہ پاک ہے اور بلند و بالا ہے ان کے شرک سے جو وہ کرتے ہیں۔'),
(3450, 30, 41, 'لوگوں کے ہاتھوں کی کارستانیوں کی وجہ سے خشکی و تری (ساری دنیا) میں فساد پھیل گیا ہے تاکہ اللہ انہیں ان کے بعض (برے) اعمال کا مزہ چکھائے تاکہ وہ لوگ باز آجائیں۔'),
(3451, 30, 42, '(اے رسول(ص)) آپ کہہ دیجئے! کہ زمین میں چلو پھرو۔ پھر دیکھو کہ پہلے گزرے ہوئے لوگوں کا انجام کیسا ہوا؟ ان میں سے اکثر مشرک تھے۔'),
(3452, 30, 43, 'پس (اے رسول(ص)) آپ اپنا رخ دینِ مستقیم کی طرف رکھیں قبل اس کے کہ وہ دن آجائے کہ جس کیلئے اللہ کی طرف سے ٹلنا نہ ہوگا۔ اس دن لوگ الگ الگ ہو جائیں گے۔'),
(3453, 30, 44, 'جس نے کفر کیا۔ پس اس کے کفر کا وبال اسی پر ہوگا اور جنہوں نے نیک عمل کئے وہ اپنے ہی لئے راہ ہموار کر رہے ہیں۔'),
(3454, 30, 45, 'تاکہ اللہ اپنے فضل و کرم سے ایمان لانے والوں اور نیک عمل کرنے والوں کو جزاء (خیر) دے۔ بے شک وہ کافروں کو پسند نہیں کرتا۔'),
(3455, 30, 46, 'اور اس کی (قدرت کی) نشانیوں میں سے ایک یہ ہے کہ وہ ہواؤں کو (بارش کی) بشارت دینے کیلئے بھیجتا ہے اور تاکہ وہ تمہیں اپنی رحمت کا مزہ چکھائے اور تاکہ اس کے حکم سے (تمہاری) کشتیاں چلیں اور تاکہ تم اس کا فضل (رزق) تلاش کرو اور تاکہ تم شکر ادا کرو۔'),
(3456, 30, 47, 'بے شک ہم نے آپ سے پہلے (بہت سے) پیغمبر ان کی قوموں کی طرف بھیجے جو ان کے پاس کھلی ہوئی نشانیاں (معجزات) لے کر آئے۔ پھر ہم نے مجرموں سے بدلہ لیا اور ہم پر اہلِ ایمان کی مدد کرنا لازم ہے۔'),
(3457, 30, 48, 'اللہ ہی وہ ہے جو ہواؤں کو بھیجتا ہے۔ تو وہ بادل کو اٹھاتی ہیں۔ پھر اللہ جس طرح چاہتا ہے اسے آسمان پر پھیلا دیتا ہے اور پھر اسے ٹکڑے ٹکڑے کر دیتا ہے۔ پھر تم دیکھتے ہو کہ اس کے درمیان سے بارش نکلنے لگتی ہے۔ پھر وہ اپنے بندوں سے جسے چاہتا ہے پہنچاتا ہے۔ تو بس وہ خوش ہو جاتے ہیں۔'),
(3458, 30, 49, 'اگرچہ وہ اس کے برسنے سے پہلے بالکل مایوس ہو رہے تھے۔'),
(3459, 30, 50, 'پس اللہ کی رحمت کے آثار کو دیکھو کہ وہ زمین کو اس کے مردہ (بنجر) ہو جانے کے بعد کس طرح زندہ (سرسبز و شاداب) کرتا ہے۔ بے شک وہی مُردوں کو زندہ کرنے والا ہے اور وہ ہر چیز پر پوری قدرت رکھنے والا ہے۔'),
(3460, 30, 51, 'اور اگر ہم ایسی ہوا بھیج دیں جس سے وہ اپنی (سرسبز) کھیتی کو زرد ہوتے دیکھ لیں تو وہ اس کے بعد ناشکری کرنے لگیں۔'),
(3461, 30, 52, '(اے رسول(ص)) آپ مُردوں کو نہیں سنا سکتے اور نہ ہی بہروں کو (اپنی) پکار سنا سکتے ہیں جب وہ پیٹھ پھیرے چلے جا رہے ہیں۔'),
(3462, 30, 53, 'اور نہ آپ اندھوں کو ان کی گمراہی سے نکال کر ہدایت دے سکتے ہیں آپ تو بس انہی لوگوں کو سنا سکتے ہیں جو ہماری آیتوں پر ایمان لاتے ہیں پھر وہی لوگ تو ماننے والے ہیں۔'),
(3463, 30, 54, 'اللہ ہی وہ ہے جس نے کمزوری کی حالت سے تمہاری پیدائش کا آغاز کیا۔ پھر کمزوری کے بعد (تمہیں جوانی کی) قوت بخشی اور پھر اس نے قوت کے بعد کمزوری اور بڑھاپا پیدا کر دیا وہ جو چاہتا ہے پیدا کرتا ہے اور وہ بڑا جاننے والا، بڑی قدرت والا ہے۔'),
(3464, 30, 55, 'اور جس دن قیامت قائم ہوگی تو مجرم لوگ قَسمیں کھائیں گے کہ وہ (دنیا) میں گھڑی بھر سے زیادہ نہیں ٹھہرے۔ یہ لوگ (دنیا میں بھی) اسی طرح الٹے چلا کرتے تھے۔'),
(3465, 30, 56, 'اور جن لوگوں کو علم و ایمان دیا گیا تھا وہ کہیں گے کہ تم اللہ کے نوشتہ کے مطابق حشر کے دن تک ٹھہرے رہے ہو۔ چنانچہ یہ وہی حشر کا دن ہے لیکن تمہیں (اس کا) علم و یقین نہیں تھا۔'),
(3466, 30, 57, 'سو اس دن ظالموں کو ان کی معذرت کوئی فائدہ نہیں دے گی اور نہ ہی ان سے (توبہ کرکے) خدا کو راضی کرنے کیلئے کہا جائے گا۔'),
(3467, 30, 58, 'بے شک ہم نے اس قرآن میں لوگوں کے (سمجھانے) کیلئے ہر قسم کی مثال پیش کی ہے اور اگر آپ ان کے پاس کوئی نشانی (معجزہ) لے بھی آئیں تو بھی کافر لوگ یہی کہیں گے کہ تم باطل پر ہو۔'),
(3468, 30, 59, 'اسی طرح اللہ ان لوگوں کے دلوں پر مہر لگا دیتا ہے جو جانتے نہیں۔'),
(3469, 30, 60, '(اے رسول(ص)) آپ صبر و ثبات سے کام لیں۔ بے شک اللہ کا وعدہ سچا ہے اور جو لوگ یقین نہیں رکھتے وہ آپ کو (جوش دلا کر) بے برداشت نہ کر دیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف، لام، میم۔'),
(3471, 31, 2, 'یہ کتابِ حکیم کی آیتیں ہیں۔'),
(3472, 31, 3, 'جو (سراپا) ہدایت اور رحمت ان نیکوکار لوگوں کیلئے ہے۔'),
(3473, 31, 4, 'جو پورے اہتمام سے نماز ادا کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے کچھ (میری راہ میں) خرچ کرتے ہیں اور وہ آخرت پر یقین رکھتے ہیں۔'),
(3474, 31, 5, 'یہی لوگ اپنے پروردگار کی ہدایت پر (قائم) ہیں اور یہی وہ ہیں جو (آخرت میں) فلاح پانے والے ہیں۔'),
(3475, 31, 6, 'اور انسانوں میں سے کوئی ایسا بھی ہوتا ہے جو اللہ سے غافل کر دینے والا بے ہودہ کلام خریدتا ہے تاکہ بغیر سوچے سمجھے لوگوں کو خدا کی راہ سے بھٹکائے اور اس (راہ یا آیتوں) کا مذاق اڑائے۔ ایسے لوگوں کیلئے رسوا کرنے والا عذاب ہے۔'),
(3476, 31, 7, 'اور جب اس کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو وہ تکبر سے اس طرح منہ موڑ لیتا ہے کہ گویا اس نے انہیں سنا ہی نہیں گویا کہ اس کے کانوں میں گرانی (بہرہ پن) ہے تو اسے دردناک عذاب کی خبر سنا دیں۔'),
(3477, 31, 8, 'بے شک جو لوگ ایمان لائے اور انہوں نے نیک عمل کئے ان کیلئے نعمت (اور آرام) والی جنتیں ہیں۔'),
(3478, 31, 9, 'جن میں وہ ہمیشہ رہیں گے۔ یہ اللہ کا سچا (اور پکا) وعدہ ہے۔ وہ بڑا غالب ہے (اور) بڑا حکمت والا ہے۔'),
(3479, 31, 10, 'اس نے آسمانوں کو ایسے ستونوں کے بغیر پیدا کیا ہے جو تمہیں نظر آئیں اور اس نے زمین میں بھاری پہاڑ گاڑ دیئے تاکہ وہ تمہیں لے کر (ایک طرف) ڈھلک نہ جائے اور اس میں ہر قسم کے چلنے پھرنے والے (جانور) پھیلا دیئے اور ہم نے آسمانوں (بلندی) سے پانی برسایا اور اس سے زمین میں ہر قسم کی عمدہ چیزیں اگائیں۔'),
(3480, 31, 11, 'یہ تو ہے اللہ کی تخلیق! (اور اس کی پیدا کی ہوئی چیزیں)۔ اب تم مجھے دکھاؤ کہ جو اس کے علاوہ (تمہارے شرکاء) ہیں انہوں نے کیا پیدا کیا ہے؟ بلکہ ظالم لوگ کھلی ہوئی گمراہی میں ہیں۔'),
(3481, 31, 12, 'اور ہم نے لقمان کو حکمت (و دانائی) عطا کی (اور کہا) کہ خدا کا شکر ادا کرو اور جو شکر ادا کرتا ہے وہ اپنے ہی فائدہ کیلئے شکر ادا کرتا ہے اور جو کفرانِ نعمت (ناشکری) کرتا ہے (وہ اپنا نقصان کرتا ہے کیونکہ) بے شک اللہ بے نیاز ہے (اور) لائقِ حمد و ثنا ہے۔'),
(3482, 31, 13, 'اور (وہ وقت یاد کرو) جب لقمان نے اپنے بیٹے کو نصیحت کرتے ہوئے کہا کہ اے بیٹے! (خبردار کسی کو) اللہ کا شریک نہ بنانا۔ بے شک شرک بہت بڑا ظلم ہے۔'),
(3483, 31, 14, 'اور ہم نے انسان کو اس کے ماں باپ کے بارے میں (حسنِ سلوک کرنے کا) تاکیدی حکم دیا (کیونکہ) اس کی ماں نے کمزوری پر کمزوری سہہ کر اسے (پیٹ میں) اٹھائے رکھا اور دو برس میں اس کا دودھ چھوٹا (وہ تاکیدی حکم یہ تھا کہ) میرا اور اپنے ماں باپ کا شکریہ ادا کر (آخرکار) میری ہی طرف (تمہاری) بازگشت ہے۔'),
(3484, 31, 15, 'اور اگر وہ تجھ پر دباؤ ڈالیں کہ تو کسی ایسی چیز کو میرا شریک ٹھہرائے جس کا تجھے کوئی علم نہیں ہے تو پھر ان کی اطاعت نہ کر اور دنیا میں ان کے ساتھ نیک سلوک کر اور اس شخص کے راستہ کی پیروی کر جو (ہر معاملہ میں) میری طرف رجوع کرے پھر تم سب کی بازگشت میری ہی طرف ہے۔ تو (اس وقت) میں تمہیں بتاؤں گا کہ جو کچھ تم کیا کرتے تھے۔'),
(3485, 31, 16, 'اے بیٹا! اگر کوئی (نیک یا بد) عمل رائی کے دانہ کے برابر بھی ہو اور کسی پتھر کے نیچے ہو یا آسمانوں میں یا زمین میں تو اللہ اسے لے ہی آئے گا بے شک اللہ بڑا باریک بین اور بڑا باخبر ہے۔'),
(3486, 31, 17, 'اے بیٹا! نماز قائم کر اور نیکی کا حکم دے اور برائی سے منع کر اور جو مصیبت پیش آئے اس پر صبر کر۔ بے شک یہ (صبر) عزم و ہمت کے کاموں میں سے ہے۔'),
(3487, 31, 18, 'اور (تکبر سے) اپنا رخسار لوگوں سے نہ پھیر اور زمین پر ناز و انداز سے نہ چل۔ بے شک خدا تکبر کرنے والے (اور) بہت فخر کرنے والے کو دوست نہیں رکھتا۔'),
(3488, 31, 19, 'اور اپنی رفتار (چال) میں میانہ روی اختیار کر اور اپنی آواز دھیمی رکھ۔ بے شک سب آوازوں میں سے زیادہ ناگوار آواز گدھوں کی ہوتی ہے۔'),
(3489, 31, 20, 'کیا تم نہیں دیکھتے کہ جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اللہ تعالیٰ نے سب کو تمہارے لئے مسخر کر دیا ہے اور اپنی سب ظاہری اور باطنی نعمتیں تم پر تمام کر دی ہیں اور لوگوں میں سے کچھ ایسے بھی ہیں جو علم، ہدایت اور روشن کتاب کے بغیر خدا کے بارے میں بحث و تکرار کرتے ہیں۔'),
(3490, 31, 21, 'اور جب ان سے کہا جاتا ہے کہ اس چیز کی پیروی کرو جو اللہ نے نازل کی ہے تو وہ کہتے ہیں کہ (نہیں) بلکہ ہم تو اس کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کو پایا ہے۔ کیا (یہ جب بھی انہی کی پیروی کریں گے) اگرچہ شیطان ان (بڑوں) کو بھڑکتی آگ کے عذاب کی طرف بلا رہا ہو؟'),
(3491, 31, 22, 'اور جو کوئی اپنا رخ اللہ کی طرف جھکا دے (اپنے آپ کو اللہ کے حوالے کر دے) درآنحالیکہ وہ نیکوکار بھی ہو تو اس نے مضبوط راستہ (سہارا) پکڑ لیا ان (سب) نے ہماری ہی طرف لوٹ کر آنا ہے۔'),
(3492, 31, 23, 'اور جو کوئی کفر اختیار کرے تو (اے رسول(ص)) اس کا کفر آپ کو غمگین نہ کرے۔ پس (اس وقت) ہم انہیں بتائیں گے کہ جو کچھ وہ کیا کرتے تھے۔ بے شک اللہ سینوں کے اندر کی باتوں کو خوب جاننے والا ہے۔'),
(3493, 31, 24, 'ہم انہیں چند روزہ عیش کا موقع دیں گے اور پھر انہیں سخت عذاب کی طرف کشاں کشاں لے جائیں گے۔'),
(3494, 31, 25, '(اے رسول(ص)) اگر آپ ان (مشرکین) سے پوچھیں کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے؟ تو وہ (جواب میں) ضرور کہیں گے کہ اللہ نے! آپ کہئے! الحمدﷲ مگر ان میں سے اکثر لوگ جانتے نہیں ہیں۔'),
(3495, 31, 26, 'جو کچھ آسمانوں اور زمین میں ہے وہ سب اللہ ہی کا ہے۔ بے شک اللہ بے نیاز ہے اور لائقِ حمد و ثنا ہے۔'),
(3496, 31, 27, 'اور جتنے درخت زمین میں ہیں اگر وہ سب قَلمیں بن جائیں اور سمندر (سیاہی بن جائیں) اور اس کے علاوہ سات سمندر اسے سہارا دیں (سیاہی مہیا کریں) تب بھی اللہ کے کلمات ختم نہیں ہوں گے بے شک اللہ بڑا غالب (اور) بڑا حکمت والا ہے۔'),
(3497, 31, 28, 'تم سب کا پیدا کرنا اور دوبارہ زندہ کرکے اٹھانا (اللہ کیلئے) ایک شخص کے پیدا کرنے اور دوبارہ اٹھانے کی مانند ہے بے شک اللہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(3498, 31, 29, 'کیا تم نہیں دیکھتے کہ اللہ رات کو دن میں اور دن کو رات میں داخل کر دیتا ہے اور اس نے سورج اور چاند کو مسخر کر رکھا ہے۔ ہر ایک اپنے مقررہ وقت تک (اپنے مدار میں) چل رہا ہے۔ اور تم جو کچھ کرتے ہو اللہ اسے خوب جانتا ہے۔'),
(3499, 31, 30, 'یہ سب اس لئے ہے کہ اللہ ہی حق ہے اور اس کے سوا جن چیزوں کو (مشرک) پکارتے ہیں وہ سب باطل ہیں اور بے شک اللہ بلند (شان والا) ہے اور بزرگ ہے۔'),
(3500, 31, 31, 'کیا تم نے نہیں دیکھا کہ محض اللہ کے فضل سے سمندر میں کشتی چلتی ہے۔ تاکہ وہ تمہیں اپنی قدرت کی کچھ نشانیاں دکھائے۔ بے شک اس میں بہت سی نشانیاں ہیں ہر بہت صبر و شکر کرنے والے کیلئے۔'),
(3501, 31, 32, 'اور جب انہیں ایک موج سائبانوں کی طرح ڈھانپ لیتی ہے تو یہ اپنے دین کو اللہ کیلئے خالص کرکے اسے پکارتے ہیں پھر جب وہ ان کو نجات دے کر خشکی تک پہنچا دیتا ہے تو پھر ان میں سے کچھ تو اعتدال پر رہتے ہیں (اور اکثر انکار کر دیتے ہیں) اور ہماری نشانیوں کا انکار نہیں کرتا مگر وہی شخص جو بڑا غدار (بے وفا) اور بڑا ناشکرا ہے۔'),
(3502, 31, 33, 'اے لوگو! اپنے پروردگار (کی نافرمانی) سے ڈرو۔ اور اس دن کا خوف کرو۔ جب نہ کوئی باپ اپنے بیٹے کو فائدہ پہنچا سکے گا اور نہ کوئی بیٹا اپنے باپ کو کوئی فائدہ پہنچا سکے گا۔ بے شک اللہ کا وعدہ سچا ہے تو تمہیں دنیا کی (عارضی) زندگی دھوکہ نہ دے اور نہ ہی کوئی دھوکہ باز (شیطان وغیرہ) تمہیں اللہ کے بارے میں دھوکہ دے۔'),
(3503, 31, 34, 'بے شک اللہ ہی کے پاس قیامت کا علم ہے اور وہی بارش برساتا ہے اور وہی جانتا ہے کہ ماؤں کے رحموں میں کیا ہے؟ اور کوئی نہیں جانتا کہ وہ کل کیا کرے گا اور کوئی نہیں جانتا کہ وہ کس سرزمین میں مرے گا۔ بے شک اللہ بڑا جاننے والا (اور) بڑا باخبر ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف، لام، میم۔'),
(3505, 32, 2, 'بلا شک و شبہ یہ کتاب (قرآن) کی تنزیل تمام جہانوں کے پروردگار کی طرف سے ہے۔'),
(3506, 32, 3, 'کیا وہ لوگ کہتے ہیں کہ اس (رسول(ص)) نے اسے خود گھڑ لیا ہے؟ (نہیں) بلکہ یہ آپ کے پروردگار کی طرف سے حق ہے تاکہ آپ اس قوم کو ڈرائیں جس کے پاس آپ سے پہلے کوئی ڈرانے والا نہیں آیا۔ شاید کہ وہ ہدایت پا جائیں۔'),
(3507, 32, 4, 'اللہ ہی وہ ہے جس نے آسمانوں اور زمین اور ان کے درمیان کی سب چیزوں کو چھ دنوں میں پیدا کیا پھر عرش پر متمکن ہوا اس کے سوا نہ تمہارا کوئی سرپرست ہے اور نہ کوئی سفارشی کیا تم نصیحت حاصل نہیں کرتے؟'),
(3508, 32, 5, 'وہ آسمان سے لے کر زمین تک ہر معاملہ کی تدبیر کرتا ہے اور پھر ہر معاملہ اس کی بارگاہ میں اس دن پیش ہوگا جس کی مقدار تمہارے شمار کے مطابق ایک ہزار سال ہوگی۔'),
(3509, 32, 6, 'یہ ہے ہر پوشیدہ اور ظاہر کا جاننے والا جو بڑا غالب ہے (اور) بڑا رحم کرنے والا ہے۔'),
(3510, 32, 7, 'جس نے جو چیز بنائی بہترین بنائی اور انسان (آدم (ع)) کی خلقت کی ابتدائ گیلی مٹی سے کی۔'),
(3511, 32, 8, 'پھر اس کی نسل کو ایک حقیر پانی (نطفہ) کے نچوڑ سے قرار دیا۔'),
(3512, 32, 9, 'پھر اس کو درست کیا (اس کی نوک پلک سنواری) اور پھر اس میں اپنی (خاص) روح پھونک دی اور تمہارے لئے کان، آنکھیں اور دل (دماغ) بنائے مگر تم لوگ بہت کم شکر ادا کرتے رہو۔'),
(3513, 32, 10, 'اور وہ (کفار) کہتے ہیں کہ جب ہم زمین میں گم (ناپید) ہو جائیں گے تو کیا ہم از سرِ نو پیدا کئے جائیں گے بلکہ (اصل بات یہ ہے کہ) یہ لوگ اپنے پروردگار کی بارگاہ میں حاضری کے منکر ہیں۔'),
(3514, 32, 11, 'آپ کہہ دیجئے! کہ موت کا وہ فرشتہ جو تم پر مقرر کیا گیا ہے وہ تمہیں پورا پورا اپنے قبضے میں لیتا ہے پھر تم اپنے پروردگار کی طرف لوٹائے جاؤگے۔'),
(3515, 32, 12, 'اور کاش! تم دیکھتے جب مجرم اپنے سر جھکائے اپنے پروردگار کی بارگاہ میں (کھڑے) ہوں گے اور (عرض کریں گے) اے ہمارے پروردگار! ہم نے دیکھ لیا اور سن لیا تو (ایک بار دنیا میں) ہمیں واپس بھیج دے۔ (اب) ہم نیک کام کریں گے اب ہمیں یقین آگیا ہے۔'),
(3516, 32, 13, 'اور اگر ہم (مشیتِ قاہرہ سے) چاہتے تو ہر متنفس کو اس کی ہدایت دے دیتے لیکن میری طرف سے یہ بات طے ہو چکی ہے کہ میں جہنم کو سب (نافرمان) جنوں اور انسانوں سے بھر دوں گا۔'),
(3517, 32, 14, 'سو اس دن کی حاضری کو بھلا دینے کا (آج) مزا چکھو۔ (اب) ہم نے بھی تمہیں نظر انداز کر دیا ہے اور اپنے برے اعمال کی پاداش میں دائمی عذاب کا مزہ چکھو۔'),
(3518, 32, 15, 'ہماری آیتوں پر بس وہی لوگ ایمان لاتے ہیں جن کو جب بھی ان (آیتوں) کے ساتھ نصیحت کی جائے تو وہ سجدے میں گر جاتے ہیں اور اپنے پروردگار کی حمد کے ساتھ تسبیح کرتے ہیں اور وہ تکبر نہیں کرتے۔'),
(3519, 32, 16, '(رات کے وقت) ان کے پہلو بستروں سے الگ رہتے ہیں (اور) وہ بیم و امید سے اپنے پروردگار کو پکارتے ہیں۔'),
(3520, 32, 17, 'پس کوئی شخص نہیں جانتا کہ ان کے (اچھے) اعمال کے صلہ میں ان کی آنکھوں کی ٹھنڈک کی کیا کیا نعمتیں چھپا کر رکھی گئی ہیں۔'),
(3521, 32, 18, 'تو کیا جو مؤمن ہے وہ فاسق کی مانند ہو سکتا ہے؟ یہ برابر نہیں ہو سکتے۔'),
(3522, 32, 19, 'پس جو لوگ ایمان لائے اور نیک عمل (بھی) کئے تو ان کے (اچھے) اعمال کے صلہ میں مہمانی کے طور پر ان کے (آرام) کے لئے جنتوں کی قیام گاہیں ہیں۔'),
(3523, 32, 20, 'اور جنہوں نے نافرمانی کی ان کا ٹھکانا آتش دوزخ ہے وہ جب بھی اس میں سے نکلنا چاہیں گے تو اسی میں لوٹا دیئے جائیں گے اور ان سے کہا جائے گا کہ اسی آگ کے عذاب کا مزہ چکھو جسے تم جھٹلاتے تھے۔'),
(3524, 32, 21, 'اور ہم انہیں (قیامت والے) بڑے عذاب سے پہلے چھوٹے عذاب کا مزہ چکھائیں گے تاکہ یہ باز آجائیں۔'),
(3525, 32, 22, 'اور اس شخص سے بڑا ظالم کون ہوگا جسے اس کے پروردگار کی آیتوں کے ذریعہ سے نصیحت کی جائے (اور) پھر وہ ان سے روگردانی کرے۔ بے شک ہم مجرموں سے انتقام لینے والے ہیں۔'),
(3526, 32, 23, 'اور بیشک ہم نے موسیٰ (ع)کو کتاب (توراۃ) عطا کی تھی۔ تو آپ کو ایسی کتاب کے ملنے پر شک میں نہیں پڑنا چاہیے اور ہم نے اسے بنی اسرائیل کے لئے ذریعۂ ہدایت بنایا تھا۔'),
(3527, 32, 24, 'اور ہم نے ان میں سے بعض کو ایسا امام و پیشوا قرار دیا تھا جو ہمارے حکم سے ہدایت کیا کرتے تھے جب کہ انہوں نے صبر کیا تھا اور وہ ہماری آیتوں پر یقین رکھتے تھے۔'),
(3528, 32, 25, 'بے شک آپ کا پروردگار قیامت کے دن ان کے درمیان فیصلہ کرے گا۔ ان باتوں میں جن میں وہ باہم اختلاف کیا کرتے تھے۔'),
(3529, 32, 26, 'کیا اس بات سے بھی اللہ نے انہیں ہدایت نہیں کی کہ ہم نے ان سے پہلے کتنی قوموں کو ہلاک کر دیا ہے جن کے مکانات میں یہ (آج) چل پھر رہے ہیں۔ بے شک اس میں (عبرت کیلئے) بڑی نشانیاں ہیں کیا یہ لوگ سنتے نہیں ہیں۔'),
(3530, 32, 27, 'کیا انہوں نے نہیں دیکھا کہ ہم بنجر زمین کی طرف پانی کو بہا لے جاتے ہیں پھر اس سے ایسی کھیتی اگاتے ہیں جس میں ان کے چوپائے بھی کھاتے ہیں اور خود بھی تو کیا یہ لوگ دیکھتے نہیں ہیں۔'),
(3531, 32, 28, 'اور وہ لوگ یہ کہتے ہیں کہ اگر تم سچے ہو تو (بتاؤ) یہ فتح (فیصلہ) کب ہوگا؟'),
(3532, 32, 29, 'آپ کہئے کہ فتح (فیصلہ) والے دن کافروں کو ان کا ایمان لانا کوئی فائدہ نہ دے گا اور نہ ہی انہیں مہلت دی جائے گی۔'),
(3533, 32, 30, 'سو آپ ان سے بے اعتنائی کریں اور انتظار کریں۔ وہ بھی انتظار کر رہے ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اے نبی! اللہ سے ڈرتے رہیں اور کافروں اور منافقوں کی اطاعت نہ کریں۔ بے شک اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(3535, 33, 2, 'اور جو کچھ آپ کے پروردگار کی طرف سے آپ کو وحی کی جاتی ہے اس کی پیروی کریں بے شک تم لوگ جو کچھ کرتے ہو اللہ اس سے خوب باخبر ہے۔'),
(3536, 33, 3, 'اور آپ اپنے پروردگار پر توکل کریں بے شک اللہ تعالیٰ کارسازی کیلئے کافی ہے۔'),
(3537, 33, 4, 'اور اللہ نے کسی مرد کے سینہ میں دو دل نہیں بنائے اور نہ ہی تمہاری ان بیویوں کو تمہاری مائیں بنایا ہے جن سے تم ظہار کرتے ہو اور نہ ہی اس نے تمہارے منہ بولے بیٹوں کو تمہارا (حقیقی) بیٹا بنایا ہے اور وہی (سیدھے) راستے کی ہدایت کرتا ہے۔'),
(3538, 33, 5, 'ان (منہ بولے بیٹوں) کو ان کے (حقیقی) باپوں کے نام سے پکارا کرو۔ یہ بات اللہ کے نزدیک زیادہ قرینِ انصاف ہے اور اگر تمہیں ان کے (حقیقی) باپوں کا علم نہ ہو تو پھر وہ تمہارے دینی بھائی اور تمہارے دوست ہیں اور تم سے جو بھول چوک ہو جائے اس کا تم پر کوئی گناہ نہیں ہے۔ ہاں البتہ (گناہ اس پر ہے) جو تم دل سے ارادہ کرکے کرو۔ اور اللہ بڑا بخشنے والا (اور) رحم کرنے والا ہے۔'),
(3539, 33, 6, 'نبی مؤمنین پر ان کی جانوں سے بھی زیادہ حق (تصرف) رکھتے ہیں۔ اور آپ کی بیویاں ان (مؤمنین) کی مائیں ہیں اور کتاب اللہ کی رو سے رشتہ دار بہ نسبت عام مؤمنین و مہاجرین کے (وراثت میں) ایک دوسرے کے زیادہ حقدار ہیں مگر یہ کہ تم اپنے دوستوں کے ساتھ حسن سلوک کرنا چاہو۔ یہ حکم کتاب (الٰہی) میں لکھا ہوا ہے۔'),
(3540, 33, 7, 'اور وہ وقت یاد کرو۔ جب ہم نے نبیوں سے عہد و پیمان لیا تھا اور آپ سے بھی اور نوح، ابراہیم، موسیٰ اور عیسیٰ بن مریم سے بھی اور ہم نے ان سب سے سخت عہد لیا تھا۔'),
(3541, 33, 8, 'تاکہ وہ (پروردگار) ان سچے لوگوں سے ان کی سچائی کے متعلق سوال کرے اور اس نے کافروں کیلئے دردناک عذاب تیار کر رکھا ہے۔'),
(3542, 33, 9, 'اے ایمان والو! اللہ کے اس احسان کو یاد کرو جو اس نے تم پر کیا ہے جب (کفار کے) لشکر تم پر چڑھ آئے اور ہم نے (تمہاری مدد کیلئے) ان پر ہوا (آندھی) بھیجی اور (فرشتوں کے) ایسے لشکر بھیجے جن کو تم نے نہیں دیکھا اور جو کچھ تم کر رہے تھے۔ اللہ اسے خوب دیکھ رہا تھا۔'),
(3543, 33, 10, 'جب وہ تم پر اوپر اور نیچے سے چڑھ آئے اور (شدتِ خوف سے) آنکھیں پتھرا گئیں اور دل (کلیجے) منہ کو آگئے اور تم اللہ کی نسبت طرح طرح کے گمان کرنے لگے۔'),
(3544, 33, 11, 'اس وقت ایمان والوں کو خوب آزمایا گیا اور انہیں سخت زلزلہ میں ڈال دیا (سخت جھنجھوڑا گیا)۔'),
(3545, 33, 12, 'اور جب منافق اور وہ لوگ جن کے دلوں میں بیماری تھی کہنے لگے کہ خدا اور رسول نے ہم سے (فتح کا) جو وعدہ کیا تھا وہ دھوکہ کے سوا کچھ نہ تھا۔'),
(3546, 33, 13, 'اور وہ وقت یاد کرو جب ان میں سے ایک گروہ کہنے لگا کہ اے یثرب والو! اب (یہاں) تمہارے ٹھہرنے کا موقع نہیں ہے سو واپس چلو اور ان میں سے ایک گروہ یہ کہہ کر پیغمبرِ خدا (صلی اللہ علیہ و آلہ وسلم) سے اجازت مانگ رہا تھا کہ ہمارے گھر خالی (غیر محفوظ) ہیں حالانکہ وہ خالی (غیر محفوظ) نہیں تھے وہ تو صرف (محاذ سے) بھاگنا چاہتے تھے۔'),
(3547, 33, 14, 'اور اگر ان پر اسی (مدینہ) کے اطراف سے (دشمن) گھس آتے اور پھر انہیں اس فتنہ (میں شرکت) کی دعوت دی جاتی تو یہ اس میں پڑ جاتے اور اس میں زیادہ توقف نہ کرتے۔'),
(3548, 33, 15, 'حالانکہ انہوں نے اللہ سے عہد کیا تھا کہ وہ (مقابلہ میں) پیٹھ نہیں پھیریں گے اور اللہ سے جو وعدہ کیا تھا اس کے متعلق بازپرس کی جائے گی۔'),
(3549, 33, 16, 'آپ کہہ دیجئے! کہ اگر تم موت یا قتل سے بھاگو تو یہ بھاگنا تمہیں کوئی فائدہ نہ دے گا اگر ایسا کیا بھی تو پھر بھی (زندگانی دنیا سے) تمہیں لطف اندوز ہونے کا بہت تھوڑا موقع دیا جائے گا۔'),
(3550, 33, 17, 'آپ کہئے! کون ہے جو تمہیں اللہ سے بچا سکے اگر وہ تمہیں نقصان پہنچانا چاہے یا اگر وہ تم پر مہربانی کرنا چاہے (تو اسے کون روک سکتا ہے؟) اور وہ لوگ اللہ کے سوا اپنا نہ کوئی سرپرست پائیں گے اور نہ کوئی مددگار۔'),
(3551, 33, 18, 'اللہ تم میں سے ان لوگوں کو خوب جانتا ہے جو (جہاد سے) روکتے ہیں اور انہیں بھی جو اپنے بھائی بندوں سے کہتے ہیں کہ (محاذِ جنگ چھوڑ کر) ہماری طرف آجاؤ اور یہ لوگ میدانِ جنگ میں بہت کم آتے ہیں۔'),
(3552, 33, 19, 'وہ تمہارے معاملہ میں سخت بخیل ہیں اور جب خوف (کا وقت) آجائے تو آپ انہیں دیکھیں گے کہ وہ آپ کی طرف اس طرح دیکھتے ہیں کہ ان کی آنکھیں اس شخص کی طرح گھوم رہی ہیں جس پر موت کی غشی طاری ہو پھر جب خوف جاتا رہے تو یہ لوگ (اپنی) تیز زبانوں سے طعنہ دیتے ہیں یہ مال (غنیمت) کے بڑے حریص ہیں (دراصل) یہ لوگ ایمان لائے ہی نہیں چنانچہ اللہ نے ان کے اعمال اکارت کر دیئے ہیں اور ایسا کرنا اللہ کیلئے بالکل آسان ہے۔'),
(3553, 33, 20, '(دشمن چلا گیا مگر) یہ لوگ خیال کرتے ہیں کہ ابھی لشکر گئے نہیں ہیں اور اگر وہ لشکر (دوبارہ) آجائیں تو یہ پسند کریں گے کہ کاش ہم صحرا میں بدوؤں کے ساتھ جا کر رہیں اور وہاں سے تمہاری خبریں پوچھتے رہیں اور اگر تم میں ہوتے تو جب بھی (دشمن سے) جنگ نہ کرتے مگر بہت کم۔'),
(3554, 33, 21, 'بے شک تمہارے لئے پیغمبرِ اسلام (صلی اللہ علیہ و آلہ وسلم) کی ذات میں (پیروی کیلئے) بہترین نمونہ موجود ہے۔ ہر اس شخص کیلئے جو اللہ (کی بارگاہ میں حاضری) اور قیامت (کے آنے) کی امید رکھتا ہے اور اللہ کو بکثرت یاد کرتا ہے۔'),
(3555, 33, 22, 'اور (سچے) اہلِ ایمان کا حال یہ تھا کہ جب انہوں نے لشکروں کو دیکھا تو کہنے لگے کہ یہ ہے وہ (لشکر) جس کا خدا اور رسول نے وعدہ کیا تھا اور خدا اور رسول نے سچ فرمایا تھا اور اس (بات) نے ان کے ایمان اور (جذبۂ) تسلیم میں مزید اضافہ کر دیا۔'),
(3556, 33, 23, 'اور اہلِ ایمان میں سے کچھ ایسے بھی ہیں جنہوں نے وہ عہد و پیمان سچا کر دکھایا جو اللہ سے کیا تھا۔ سو ان میں سے کچھ وہ ہیں جو اپنا وقت پورا کر چکے ہیں اور کچھ اس (وقت) کا انتظار کر رہے ہیں اور انہوں نے (اپنی روش میں) ذرا بھی تبدیلی نہیں کی۔'),
(3557, 33, 24, 'تاکہ خدا سچوں کو ان کی سچائی کی جزا دے اور منافقوں کو چاہے تو سزا دے اور چاہے تو ان کی توبہ قبول کرے۔ بے شک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(3558, 33, 25, 'اور اللہ نے کافروں کو غم و غصہ کی حالت میں (بے نیل مُرام) لوٹایا کہ وہ کوئی فائدہ حاصل نہ کر سکے اور اللہ نے مؤمنوں کو جنگ (کی زحمت) سے بچا لیا اور اللہ بڑا طاقتور (اور) غالب ہے۔'),
(3559, 33, 26, 'خدا نے ان اہلِ کتاب کو جنہوں نے ان (کفار) کی مدد کی تھی ان کے قلعوں سے اتار دیا اور ان کے دلوں میں رعب ڈال دیا۔ چنانچہ تم ان میں سے بعض کو قتل کرنے لگے اور بعض کو قید کر لیا۔'),
(3560, 33, 27, 'اور (خدا نے) تمہیں ان (کافروں) کی زمین کا اور ان کے گھروں کا اور ان کے اموال کا اور ان کی اس زمین کا جس پر تم نے قدم بھی نہیں رکھا تھا وارث بنا دیا اور اللہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(3561, 33, 28, 'اے نبی! اپنی بیویوں سے کہہ دیجئے کہا گر تم دنیاوی زندگی اور اس کی زیب و زینت چاہتی ہو تو آؤ کہ تمہیں کچھ مال و متاع دے کر اچھے طریقے سے رخصت کروں۔'),
(3562, 33, 29, 'اور اگر تم خدا اور اس کے رسول اور دارِ آخرت کی طلبگار ہو تو بے شک اللہ نے تم میں سے جو نیکوکار ہیں ان کیلئے اجرِ عظیم تیار کر رکھا ہے۔'),
(3563, 33, 30, 'اے نبی کی بیویو! تم میں سے جو کوئی بے حیائی اور برائی کرے گی تو اسے دوہری سزا دی جائے گی اور یہ بات اللہ کیلئے بالکل آسان ہے۔'),
(3564, 33, 31, 'اور تم میں سے جو خدا اور اس کے رسول کی اطاعت کرے گی اور نیک عمل کرتی رہے گی تو ہم اس کو اس کا اجر و دہرا دیں گے اور ہم نے اس کیلئے (جنت میں) عمدہ روزی تیار کر رکھی ہے۔'),
(3565, 33, 32, 'اے نبی کی بیویو! تم اور (عام) عورتوں کی طرح نہیں ہوا اگر تم پرہیزگاری اختیار کرو۔ پس تم ایسے نرم لہجہ میں بات نہ کرو کہ جس کے دل میں کوئی بیماری ہے وہ طمع کرنے لگے اور قاعدے کے مطابق (باوقار طریقہ سے) بات کیا کرو۔'),
(3566, 33, 33, 'اور اپنے گھروں میں قرار سے رہو اور سابقہ زمانۂ جاہلیت کی طرح اپنی آرائش کی نمائش نہ کرتی پھرو (باہر نہ نکلا کرو) اور نماز قائم کرو اور زکوٰۃ ادا کرو اور اللہ اور اس کے رسول کی اطاعت کیا کرو۔ اے اہل بیت! اللہ تو بس یہی چاہتا ہے کہ تم سے ہر قسم کے رجس (آلودگی) کو دور رکھے اور تمہیں اس طرح پاک و پاکیزہ رکھے جس طرح پاک رکھنے کا حق ہے۔'),
(3567, 33, 34, 'اور اللہ کی جو آیتیں اور حکمت کی جو باتیں تمہارے گھروں میں پڑھ کر سنائی جاتی ہیں ان کو یاد رکھو بے شک اللہ بڑا باریک بین (اور) بڑا باخبر ہے۔'),
(3568, 33, 35, 'بے شک مسلمان مرد اور مسلمان عورتیں، مؤمن مرد اور مؤمن عورتیں، اطاعت گزار مرد اور اطاعت گزار عورتیں، سچے مرد اور سچی عورتیں، صابر مرد اور صابر عورتیں، عاجزی کرنے والے مرد اور عاجزی کرنے والی عورتیں، صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں، روزہ دار مرد اور روزہ دار عورتیں، اپنی شرمگاہوں کی حفاظت کرنے والے مرد اور حفاظت کرنے والی عورتیں اور اللہ کو بکثرت یاد کرنے والے مرد اور یاد کرنے والی عورتیں اللہ نے ان کیلئے مغفرت اور بڑا اجر و ثواب مہیا کر رکھا ہے۔'),
(3569, 33, 36, 'کسی مؤمن مرد اور کسی مؤمن عورت کو یہ حق نہیں ہے کہ جب خدا اور اس کا رسول کسی معاملے کا فیصلہ کر دیں تو انہیں اپنے (اس) معاملے میں کوئی اختیار ہو اور جو کوئی اللہ اور اس کے رسول کی نافرمانی کرے گا وہ کھلی ہوئی گمراہی میں پڑے گا۔'),
(3570, 33, 37, '(اے رسول! وہ وقت یاد کرو) جب آپ اس شخص سے کہہ رہے تھے جس پر اللہ نے اور آپ نے احسان کیا تھا کہ اپنی بیوی کو اپنے پاس رہنے دے (اسے نہ چھوڑ) اور اللہ سے ڈر۔ اور آپ (اس وقت) وہ بات اپنے دل میں چھپا رہے تھے جسے اللہ ظاہر کرنے والا تھا اور آپ لوگوں (کی طعن و تشنیع) سے ڈر رہے تھے حالانکہ اللہ اس بات کا زیادہ حقدار ہے کہ آپ اس سے ڈریں (بہرحال) جب زید نے اس عورت (زینب) سے اپنی حاجت پوری کر لی (اسے طلاق دے دی) تو ہم نے اس خاتون کی شادی آپ سے کر دی تاکہ اہلِ ایمان پر اپنے منہ بولے بیٹوں کی بیویوں سے (نکاح کرنے) کے معاملے میں کوئی تنگی نہ رہ جائے جب وہ ان سے (اپنی) حاجت پوری کر چکے ہوں (اور انہیں طلاق دے کر فارغ کر چکے ہوں) اور اللہ کا حکم تو بہرحال ہوکر رہتا ہے۔'),
(3571, 33, 38, 'اس کام کے کرنے میں نبی(ص) پر کوئی مضائقہ نہیں ہے جو خدا نے ان کیلئے مقرر کیا ہے جو لوگ (انبیاء(ع)) اس سے پہلے گزر چکے ہیں ان کے بارے میں بھی خدا کا یہی معمول رہا ہے اور اللہ کا حکم حقیقی اندازے کے مطابق مقرر کیا ہوا ہوتا ہے۔'),
(3572, 33, 39, 'وہ (پیغمبر(ص)) ایسے لوگ ہیں جو اللہ کے پیغامات (اس کے بندوں تک) پہنچاتے ہیں اور اسی سے ڈرتے ہیں اور وہ اللہ کے سوا اور کسی سے نہیں ڈرتے اور محاسبہ کیلئے اللہ ہی کافی ہے۔'),
(3573, 33, 40, 'محمد (صلی اللہ علیہ و آلہٰ وسلم) تمہارے مردوں میں سے کسی مرد کے باپ نہیں ہیں۔ ہاں البتہ وہ اللہ کے رسول(ص) اور خاتم النبیین(ص) (سلسلۂ انبیاء کے ختم کرنے والے اور مہرِ اختتام) ہیں اور خدا ہر چیز کا خوب جاننے والا ہے۔'),
(3574, 33, 41, 'اے ایمان والو! اللہ کو بکثرت یاد کیا کرو۔'),
(3575, 33, 42, 'اور صبح و شام اس کی تسبیح کیا کرو۔'),
(3576, 33, 43, 'وہ (اللہ) وہی ہے جو تم پر رحمت بھیجتا ہے اور اس کے فرشتے بھی (دعائے مغفرت) کرتے ہیں تاکہ تمہیں (دوزخ اور جحیم کی) تاریکیوں سے نور (جنت و نعیم) کی طرف نکال لائے اور وہ اہلِ ایمان پر بڑا رحم کرنے والا ہے۔'),
(3577, 33, 44, 'جس دن وہ اس کی بارگاہ میں حاضر ہوں گے تو ان کو سلام سے دعا دی جائے گی (کہ تم سلامت رہو) اور اس نے ان کیلئے باعزت اجر (بہشت) تیار کر رکھا ہے۔'),
(3578, 33, 45, 'اے نبی(ص)! ہم نے آپ کو (لوگوں کا) گواہ بنا کر اور (نیکوکاروں کو) خوشخبری دینے والا اور (بدکاروں کو) ڈرانے والا'),
(3579, 33, 46, 'اور اللہ کی طرف اس کے حکم سے بلانے والا اور (رشد و ہدایت کا) روشن چراغ بنا کر بھیجا ہے۔'),
(3580, 33, 47, 'اور ایمان والوں کو خوشخبری دیجئے کہ اللہ کی طرف سے ان کیلئے بڑا فضل و کرم ہے۔'),
(3581, 33, 48, 'اور (خبردار) کافروں اور منافقوں کی اطاعت نہ کیجئے گا اور ان کی اذیت رسانی کو چھوڑ دیجئے (اس کی پرواہ نہ کریں) اور اللہ پر بھروسہ کیجئے اور کارسازی کیلئے اللہ کافی ہے۔'),
(3582, 33, 49, 'اے ایمان والو! جب تم مؤمن عورتوں سے نکاح کرو اور پھر انہیں ہاتھ لگانے (مباشرت کرنے) سے پہلے طلاق دے دو تو تمہاری طرف سے ان پر کوئی عدت نہیں ہے جسے تم شمار کرو (اور جس کے دنوں کو گنو) لہٰذا انہیں کچھ مال دے کر اور خوبصورتی سے رخصت کر دو۔'),
(3583, 33, 50, 'اے نبی(ص)! ہم نے آپ کیلئے آپ کی وہ بیویاں حلال کر دی ہیں جن کے مہر آپ نے ادا کر دیئے ہیں اور وہ مملوکہ کنیزیں جو اللہ نے بطورِ غنیمت آپ کو عطا کی ہیں اور آپ کے چچا کی بیٹیاں اور آپ کی پھوپھیوں کی بیٹیاں اور آپ کے ماموں کی بیٹیاں اور آپ کی خالاؤں کی بیٹیاں جنہوں نے آپ کے ساتھ ہجرت کی ہے (یہ سب بھی حلال ہیں) اور اس مؤمن عورت کو بھی (حلال کیا ہے) اگر وہ اپنا نفس نبی(ص) کو ھبہ کر دے بشرطیکہ نبی(ص) بھی اس سے نکاح کرنا چاہیں یہ (اجازت) صرف آپ کیلئے ہے دوسرے مؤمنوں کیلئے نہیں ہے ہم جانتے ہیں جو (احکام) ہم نے ان پر ان کی بیویوں اور مملوکہ کنیزوں کے بارے میں مقرر کئے ہیں تاکہ آپ پر کسی قسم کی تنگی نہ ہو اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(3584, 33, 51, '(آپ کو اختیار ہے کہ) اپنی ازواج میں سے جس کو چاہیں دور کر دیں اور جس کو چاہیں اپنے پاس رکھیں اور جن کو آپ نے علیٰحدہ کر دیا تھا اگر ان میں سے کسی کو (دوبارہ) طلب کرنا چاہیں تو اس میں بھی آپ کیلئے کوئی مضائقہ نہیں ہے یہ (اختیار جو آپ کو دیا گیا ہے) اس سے قریب تر ہے کہ ان (ازواج) کی آنکھیں ٹھنڈی ہوں اور وہ رنجیدہ نہ ہوں اور آپ انہیں جو کچھ عطا فرمائیں وہ سب کی سب اس پر خوش ہو جائیں اور جو کچھ تمہارے دلوں میں ہے اللہ اسے جانتا ہے اور (بے شک) اللہ بڑا جاننے والا (اور) بڑا بردبار ہے۔'),
(3585, 33, 52, 'اب اس کے بعد اور عورتیں آپ کیلئے حلال نہیں ہیں اور نہ ہی اس کی اجازت ہے کہ ان کے بدلے اور بیویاں لے آئیں اگرچہ ان کا حسن و جمال آپ کو کتنا ہی پسند ہو۔ سوائے ان (کنیزوں) کے جو آپ کی ملکیت میں ہیں اور اللہ ہر چیز کا نگران ہے۔'),
(3586, 33, 53, 'اے ایمان والو! نبی(ص) کے گھروں میں داخل نہ ہوا کرو۔ مگر جب تمہیں کھانے کیلئے (اندر آنے کی) اجازت دی جائے (اور) نہ ہی اس کے پکنے کا انتظار (نبی(ص) کے گھر میں بیٹھ کر کیا) کرو۔ لیکن جب تمہیں بلایا جائے تو (عین وقت پر) اندر داخل ہو جاؤ پھر جب کھانا کھا چکو تو منتشر ہو جاؤ اور دل بہلانے کیلئے باتوں میں نہ لگے رہو کیونکہ تمہاری باتیں نبی(ص) کو اذیت پہنچاتی ہیں مگر وہ تم سے شرم کرتے ہیں (اور کچھ نہیں کہتے) اور اللہ حق بات (کہنے سے) نہیں شرماتا اور جب تم ان (ازواجِ نبی(ص)) سے کوئی چیز مانگو تو پردے کے پیچھے سے مانگا کرو۔ یہ (طریقۂ کار) تمہارے دلوں کیلئے اور ان کے دلوں کیلئے پاکیزگی کا زیادہ باعث ہے اور تمہارے لئے جائز نہیں ہے کہ تم رسولِ(ص) خدا کو اذیت پہنچاؤ اور نہ یہ جائز ہے کہ ان کے بعد کبھی بھی ان کی بیویوں سے نکاح کرو بیشک یہ بات اللہ کے نزدیک بہت بڑی (برائی گناہ کی) بات ہے۔'),
(3587, 33, 54, 'تم اگر کسی چیز کو ظاہر کرو یا اسے چھپاؤ۔ بہرحال اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(3588, 33, 55, 'ان (ازواج النبی(ص)) کیلئے اس بات میں کوئی مضائقہ نہیں کہ ان کے باپ، ان کے بیٹے، ان کے بھائی، ان کے بھتیجے، بھانجے اور ان کی اپنی (مسلمان) عورتیں اور ان کی مملوکہ کنیزیں (ان کے) سامنے آئیں اور اللہ سے ڈرتی رہو۔ بے شک اللہ ہر چیز پر گواہ ہے۔'),
(3589, 33, 56, 'بے شک اللہ اور اس کے فرشتے نبی(ص) پر درود بھیجتے ہیں اے ایمان والو! تم بھی ان پر درود و سلام بھیجو جس طرح بھیجنے کا حق ہے۔'),
(3590, 33, 57, 'بے شک جو لوگ اللہ اور اس کے رسول(ص) کو اذیت پہنچاتے ہیں اللہ ان پر دنیا و آخرت میں لعنت کرتا ہے اور ان کیلئے رسوا کرنے والا عذاب مہیا کر رکھا ہے۔'),
(3591, 33, 58, 'اور جو لوگ مؤمن مردوں اور مؤمن عورتوں کو اذیت پہنچاتے ہیں بغیر اس کے کہ انہوں نے کوئی (جرم) کیا ہو۔ بے شک وہ ایک بڑے بہتان اور کھلے ہوئے گناہ کا بوجھ اٹھاتے ہیں۔'),
(3592, 33, 59, 'اے نبی(ص)! آپ اپنی بیویوں، بیٹیوں اور (عام) اہلِ ایمان کی عورتوں سے کہہ دیجئے کہ وہ (باہر نکلتے وقت) اپنے اوپر چادر (بطور گھونگھٹ) لٹکا لیا کریں یہ طریقہ قریب تر ہے کہ وہ پہچان لی جائیں اور ستائی نہ جائیں اور اللہ بڑا بخشنے والا، رحم کرنے والا ہے۔'),
(3593, 33, 60, 'اگر منافقین اور وہ لوگ جن کے دلوں میں بیماری ہے اور مدینہ میں افواہیں پھیلانے والے (اپنی حرکتوں سے) باز نہ آئے تو ہم آپ کو ان کے خلاف حرکت میں لے آئیں گے پھر وہ (مدینہ) میں آپ کے پڑوس میں نہیں رہ سکیں گے مگر بہت کم۔'),
(3594, 33, 61, 'وہ بھی لعنت کے مارے ہوئے وہ جہاں کہیں پائے جائیں گے پکڑے جائیں گے اور بری طرح قتل کئے جائیں گے۔'),
(3595, 33, 62, 'جو (ایسے لوگ) ان سے پہلے گزر چکے ہیں ان میں بھی اللہ کا یہی دستور رہا ہے اور آپ اللہ کے دستور میں کوئی تبدیلی نہیں پائیں گے۔'),
(3596, 33, 63, 'لوگ آپ سے قیامت کے بارے میں دریافت کرتے ہیں (کب آئے گی؟) آپ کہہ دیجئے! کہ اس کا علم تو بس اللہ ہی کے پاس ہے (اے سائل!) تمہیں کیا خبر شاید قیامت قریب ہی ہو؟'),
(3597, 33, 64, 'بے شک اللہ نے کافروں پر لعنت کی ہے اور ان کیلئے بھڑکتی ہوئی آگ تیار کر رکھی ہے۔'),
(3598, 33, 65, 'جس میں وہ ہمیشہ ہمیشہ رہیں گے وہ (وہاں) کوئی حامی و مددگار نہیں پائیں گے۔'),
(3599, 33, 66, 'جس دن ان کے چہرے (دوزخ کی) آگ میں الٹ پلٹ کئے جائیں گے تو وہ کہیں کہ کاش ہم نے اللہ کی اطاعت کی ہوتی اور (کاش) ہم نے رسول(ص) کی اطاعت کی ہوتی۔'),
(3600, 33, 67, 'اور وہی کہیں گے اے ہمارے پروردگار! ہم نے اپنی سرداروں کی اطاعت کی۔'),
(3601, 33, 68, 'سو انہوں نے (سیدھے) راستہ سے ہمیں بھٹکا دیا اے ہمارے پرورگار! انہیں دوہرا عذاب دے اور ان پر بہت بڑی لعنت کر۔'),
(3602, 33, 69, 'اے ایمان والو! ان لوگوں کی طرح نہ ہو جاؤ جنہوں نے موسیٰ(ع) کو اذیت پہنچائی تھی تو اللہ نے انہیں ان کی ان باتوں (تہمتوں) سے بَری کر دیا اور وہ اللہ کے نزدیک بڑے معزز تھے۔'),
(3603, 33, 70, 'اے ایمان والو! اللہ (کی نافرمانی) سے ڈرو اور درستی و راستی کی بات کہا کرو۔'),
(3604, 33, 71, 'اللہ تمہارے اعمال کی اصلاح کرے گا۔'),
(3605, 33, 72, 'بے شک ہم نے امانت کو آسمانوں اور زمین اور پہاڑوں پر پیش کیا مگر ان سب نے اس کے اٹھانے سے انکار کیا اور وہ اس سے ڈر گئے اور انسان نے اسے (بلا تامل) اٹھا لیا بے شک وہ بڑا ظالم اور جاہل ہے۔'),
(3606, 33, 73, 'تاکہ خدا منافق مردوں اور منافق عورتوں اور مشرک مردوں اور منافق عورتوں کو سزا دے اور (تاکہ) مؤمن مردوں اور مؤمن عورتوں کی توبہ قبول کرے (ان پر نظرِ توجہ فرمائے) اور بے شک اللہ بڑا بخشنے والا، رحم کرنے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ہر قِسم کی تعریف اس خدا کیلئے ہے جو آسمانوں اور زمین کی ہر چیز کا مالک ہے اور آخرت میں بھی ہر قسم کی تعریف اسی کیلئے ہے اور وہ بڑا حکمت والا، بڑا باخبر ہے۔'),
(3608, 34, 2, 'وہ جانتا ہے جو کچھ زمین کے اندر داخل ہوتا ہے اور جو کچھ اس سے نکلتا ہے اور جو کچھ آسمان سے اترتا ہے اور جو کچھ اس میں چڑھتا ہے وہ بڑا رحم کرنے والا، بڑا بخشنے والا ہے۔'),
(3609, 34, 3, 'اور کافر کہتے ہیں کہ ہم پر قیامت نہیں آئے گی آپ کہہ دیجئے! کہ وہ ضرور آئے گی مجھے قَسم ہے اپنے پروردگار کی جو عالم الغیب ہے جس سے آسمانوں اور زمین کی ذرہ برابر کوئی چیز پوشیدہ نہیں ہے اور نہ اس (ذرہ) سے کوئی چھوٹی چیز ہے اور نہ بڑی مگر وہ کتابِ مبین میں (درج) ہے۔'),
(3610, 34, 4, '(اور قیامت اس لئے آئے گی) تاکہ خدا ان لوگوں کو جزا دے جو ایمان لائے اور نیک عمل کئے یہی وہ (خوش قسمت) لوگ ہیں جن کیلئے بخشش اور باعزت رزق ہے۔'),
(3611, 34, 5, 'اور جو لوگ ہماری آیتوں کے بارے میں (انہیں جھٹلا کر ہمیں) ہرانے کی کوشش کرتے ہیں یہی وہ (بدبخت) لوگ ہیں جن کیلئے سخت قسم کا دردناک عذاب ہے۔'),
(3612, 34, 6, 'اور جنہیں علم دیا گیا ہے وہ جانتے ہیں وہ کچھ (قرآن) آپ کے پروردگار کی طرف سے آپ پر نازل کیا گیا ہے وہ حق ہے اور وہ غالب اور لائقِ ستائش (خدا) کے راستہ کی طرف راہنمائی کرتا ہے۔'),
(3613, 34, 7, 'اور کافر (ایک دوسرے سے) کہتے ہیں کہ کیا ہم تمہیں ایسا آدمی بتائیں جو تمہیں خبر دیتا ہے کہ جب تم (مرنے کے بعد) بالکل ریزہ ریزہ کر دیئے جاؤگے تو تم ازسرِنو پیدا کئے جاؤگے۔'),
(3614, 34, 8, 'کیا یہ اللہ پر جھوٹا بہتان باندھتا ہے یا اسے جنون ہے (پیغمبرِ اسلام(ص) تو نہیں) بلکہ وہ لوگ جو آخرت پر ایمان نہیں رکھتے وہ عذاب اور دور دراز کی گمراہی (سخت گمراہی) میں مبتلا ہیں۔'),
(3615, 34, 9, 'کیا انہوں نے اسے نہیں دیکھا جو ان کے آگے پیچھے ہے (ان کو محیط ہے) یعنی آسمان و زمین! اگر ہم چاہیں تو انہیں زمین میں دھنسا دیں یا ان پر آسمان کے کچھ ٹکڑے گرا دیں۔ بے شک اس میں اللہ کی طرف رجوع کرنے والے بندہ کیلئے ایک نشانی ہے۔'),
(3616, 34, 10, 'اور بلاشبہ ہم نے داؤد(ع) کو اپنے ہاں سے (بڑی) فضیلت عطا کی تھی (اور پہاڑوں سے کہا) اے پہاڑو ان کے ساتھ تسبیح کرو اور پرندوں کو بھی (یہی حکم دیا) اور ہم نے ان کیلئے لوہا نرم کر دیا۔'),
(3617, 34, 11, 'اور ان سے کہا کہ کشادہ زرہیں بناؤ اور کڑیوں کے جوڑنے میں مناسب اندازہ رکھو اور (اے آلِ داؤد(ع)) تم سب نیک عمل کرو (کیونکہ) تم لوگ جو کچھ کرتے ہو میں خوب دیکھ رہا ہوں۔'),
(3618, 34, 12, 'اور ہم نے سلیمان(ع) کیلئے ہوا کو مسخر کر دیا جس کا صبح کا سفر ایک مہینہ کی راہ تک تھا اور شام کا ایک مہینہ کی راہ تک اور ہم نے ان کیلئے پگھلے ہوئے تانبے کا چشمہ جاری کیا اور جنات میں سے کچھ ایسے تھے جو ان (سلیمان(ع)) کے پروردگار کے حکم سے ان کے سامنے کام کرتے تھے اور ان (جنوں) میں سے جو ہمارے حکم سے سرتابی کرتا تھا ہم اسے بھڑکتی ہوئی آگ کا مزہ چکھاتے تھے۔'),
(3619, 34, 13, 'وہ ان کیلئے وہ کچھ کرتے تھے جو وہ چاہتے تھے (جیسے) محرابیں (مقدس اونچی عمارتیں) اور مجسمے اور بڑے بڑے پیالے جیسے حوض اور گڑی ہوئی دیگیں۔ اے داؤد(ع) کے خاندان والو! (میرا) شکر ادا کرو اور میرے بندوں میں شکر گزار بہت ہی کم ہیں۔'),
(3620, 34, 14, 'اور جب ہم نے سلیمان(ع) پر موت کا فیصلہ نافذ کیا (اور وہ فوت ہوگئے) تو ان کی موت کا پتہ ان (جنات) کو نہیں دیا مگر اس دیمک نے جو ان کے عصا کو کھا رہی تھی تو جب (عصا کھوکھلا ہوگیا اور) آپ زمین پر گرے تو جنات پر اصلی حالت کھل گئی کہ اگر وہ غیب جانتے ہوتے تو (اتنے عرصے تک) اس رسوا کن عذاب میں مبتلا نہ رہتے۔'),
(3621, 34, 15, 'قبیلہ سباء والوں کیلئے ان کی آبادی میں (قدرتِ خدا کی) ایک نشانی موجود تھی (یعنی) دو باغ تھے دائیں اور بائیں (اور ان سے کہہ دیا گیا) کہ اپنے پروردگار کے رزق سے کھاؤ اور اس کا شکر ادا کرو شہر ہے پاک و پاکیزہ اور پروردگار ہے بخشنے والا۔'),
(3622, 34, 16, 'پس انہوں نے روگردانی کی تو ہم نے ان پر بند توڑ سیلاب چھوڑ دیا اور ان کے ان دو باغوں کو ایسے دو باغوں سے بدل دیا جن کے پھل بدمزہ تھے اور جھاؤ کے کچھ درخت اور کچھ تھوڑی سی بیریاں۔'),
(3623, 34, 17, 'یہ ہم نے انہیں ان کے ناشکراپن کی پاداش میں سزا دی اور کیا ہم ایسی سزا ناشکرے انسان کے سوا کسی اور کو دیتے ہیں؟'),
(3624, 34, 18, 'اور ہم نے ان کے اور ان کی بستیوں کے درمیان جن کو ہم نے برکت دی تھی (سرِ راہ) کئی اور نمایاں بستیاں بسا دیں اور ہم نے ان میں سفر کی منزلیں ایک اندازے پر مقرر کر دیں کہ (جب چاہو) راتوں میں، دنوں میں امن و عافیت سے چلو پھرو۔'),
(3625, 34, 19, '(مگر) انہوں نے کہا اے ہمارے پروردگار ہمارے سفروں (ان کی مسافتوں) کو دور دراز کر دے اور (یہ کہہ کر) انہوں نے اپنے اوپر ظلم کیا سو ہم نے انہیں (برباد) کرکے افسانہ بنا دیا اور بالکل ٹکڑے ٹکڑے کر دیا بے شک اس میں نشانیاں ہیں ہر بہت صبر کرنے (اور) بہت شکر کرنے والے کیلئے۔'),
(3626, 34, 20, 'بیشک شیطان نے ان لوگوں کے بارے میں اپنا خیال سچا کر دکھایا چنانچہ انہوں نے اس کی پیروی کی۔ سوائے اہلِ ایمان کے ایک گروہ کے۔'),
(3627, 34, 21, 'اور شیطان کو ان پر ایسا اقتدار نہیں تھا کہ جس سے وہ بے اختیار ہو جائیں لیکن یہ سب کچھ اس لئے کہ ہم معلوم کریں کہ کون آخرت پر ایمان رکھتا ہے اور کون اس سے متعلق شک میں ہے اور آپ کا پروردگار ہر چیز پر نگران و نگہبان ہے۔'),
(3628, 34, 22, 'اے رسول(ص)! (ان مشرکین سے) کہیے! تم ان کو پکارو جنہیں تم اللہ کے سوا (معبود) سمجھتے ہو( پھر دیکھو) وہ ذرہ بھر چیز کے بھی مالک نہیں ہیں، نہ آسمانوں اور زمین میں اور نہ ہی ان کی ان دونوں (کی ملکیت) میں کوئی شرکت ہے اور نہ ہی ان میں سے کوئی اس (اللہ) کا مددگار ہے۔'),
(3629, 34, 23, 'اور نہ اس (اللہ) کے ہاں کوئی سفارش (کسی کو کوئی) فائدہ دے گی مگر اس کے حق میں جس کیلئے وہ اجازت دے گا (اور جس کو دے گا) یہاں تک کہ جب ان کے دلوں سے گھبراہٹ دور کر دی جائے گی تو وہ پوچھیں گے کہ تمہارے پروردگار نے کیا کہا ہے؟ تو وہ کہیں گے کہ اس نے حق کہا ہے اور وہ بلند و بالا اور بزرگ و برتر ہے۔'),
(3630, 34, 24, 'آپ کہیے! کہ آسمانوں اور زمین سے تمہیں کون روزی دیتا ہے؟ (پھر) فرمائیے! کہ اللہ! اور بے شک ہم یا تم ہدایت پر ہیں یا کھلی ہوئی گمراہی میں ہیں؟ ۔'),
(3631, 34, 25, 'آپ کہہ دیجئے! کہ تم سے ہمارے جرائم کی بابت بازپرس نہیں ہوگی اور نہ ہم سے تمہارے اعمال کے بارے میں سوال ہوگا۔'),
(3632, 34, 26, 'آپ کہیے (ایک دن) ہمارا پروردگار ہم سب کو جمع کرے گا پھر ہمارے درمیان حق کے ساتھ فیصلہ کرے گا اور وہ بڑا فیصلہ کرنے والا (حاکم اور) بڑا جاننے والا ہے۔'),
(3633, 34, 27, 'آپ کہیے! (ذرا) مجھے تو دکھاؤ وہ جنہیں تم نے شریک بنا کر اس (اللہ) کے ساتھ ملا رکھا ہے؟ ہرگز نہیں! بلکہ وہ اللہ ہی ہے جو بڑا غالب (اور) بڑا حکمت والا ہے۔'),
(3634, 34, 28, 'اور ہم نے آپ کو نہیں بھیجا مگر تمام انسانوں کیلئے بشیر و نذیر بنا کر مگر اکثر لوگ جانتے نہیں ہیں۔'),
(3635, 34, 29, 'اور وہ کہتے ہیں کہ اگر تم سچے ہو تو (بتاؤ) یہ وعدہ وعید کب پورا ہوگا؟'),
(3636, 34, 30, 'آپ کہیے! تمہارے لئے وعدہ کا ایک دن مقرر ہے جس سے نہ تم ایک گھڑی پیچھے ہٹ سکتے ہو اور نہ آگے بڑھ سکتے ہو۔'),
(3637, 34, 31, 'اور کافر کہتے ہیں کہ ہم نہ اس قرآن پر ایمان لائیں گے اور نہ اس سے پہلے نازل شدہ کتابوں پر (یا نہ اس پر جو اس کے آگے ہے یعنی قیامت اور اس کے عذاب پر)۔ (اے پیغمبر(ص)) کاش تم دیکھتے جب ظالم لوگ اپنے پروردگار کے حضور (سوال و جواب کیلئے) کھڑے کئے جائیں گے (اور) ایک دوسرے کی بات رد کر رہا ہوگا چنانچہ وہ لوگ جو دنیا میں کمزور سمجھے جاتے تھے وہ ان سے کہیں گے جو بڑے بنے ہوئے تھے کہ اگر تم نہ ہوتے تو ہم ضرور ایمان لے آئے ہوتے۔'),
(3638, 34, 32, 'بڑے لوگ کمزور لوگوں سے (جواب میں) کہیں گے کیا ہم نے تمہیں ہدایت سے روکا تھا جبکہ وہ تمہارے پاس آئی تھی۔ بلکہ تم خود ہی مجرم ہو۔'),
(3639, 34, 33, 'اور کمزور لوگ (جواب میں) بڑا بننے والوں سے کہیں گے کہ (اس طرح نہیں) بلکہ یہ (تمہارے) شب و روز کے مکر و فریب نے ہمیں قبولِ حق سے باز رکھا جبکہ تم ہمیں حکم دیتے تھے کہ ہم اللہ کے ساتھ کفر کریں اور اس کے ہمسر (شریک) ٹھہرائیں اور یہ لوگ جب عذابِ الٰہی کو دیکھیں گے تو ندامت و پشیمانی کو دل میں چھپائیں گے (دلوں میں پچھتائیں گے) اور ہم کافروں کی گردنوں میں طوق ڈالیں گے یہ لوگ (دنیا میں) جیسے عمل کرتے تھے (آج) ان کا ہی ان کو بدلہ دیا جائے گا۔'),
(3640, 34, 34, 'اور ہم نے جب بھی کسی بستی میں کوئی ڈرانے والا (نبی(ع)) بھیجا تو وہاں کے آسودہ حال لوگوں نے یہی کہا کہ جس (دین) کے ساتھ تم بھیجے گئے ہو ہم اسے نہیں مانتے۔'),
(3641, 34, 35, 'اور ہمیشہ یہ بھی کہا ہم مال و اولاد میں تم سے بڑھے ہوئے ہیں اور ہم کو کبھی عذاب نہیں دیا جائے گا۔'),
(3642, 34, 36, 'کہہ دیجئے! کہ میرا پروردگار (اپنے بندوں میں سے) جس کیلئے چاہتا ہے رزق کشادہ کر دیتا ہے اور جس کیلئے چاہتا ہے تنگ کر دیتا ہے لیکن اکثر لوگ (اس حقیقت کو) جانتے نہیں ہیں۔'),
(3643, 34, 37, 'اور تمہارے مال اور تمہاری اولاد (کوئی ایسی چیز نہیں ہیں) جو تمہیں ہمارا مقرب بارگاہ بنائیں مگر وہ جو ایمان لائے اور نیک عمل کرے (اسے ہمارا قرب حاصل ہوگا) ایسے لوگوں کیلئے ان کے عمل کی دوگنی جزا ہے اور وہ بہشت کے اونچے اونچے درجوں میں امن و اطمینان سے رہیں گے۔'),
(3644, 34, 38, 'اور جو لوگ ہماری آیتوں کے بارے میں (ہمیں نیچا دکھانے کیلئے) کوشش کرتے ہیں وہ عذاب میں حاضر کئے جائیں گے۔'),
(3645, 34, 39, 'آپ(ص) کہہ دیجئے! میرا پروردگار اپنے بندوں میں سے جس کا چاہتا ہے رزق کشادہ کر دیتا ہے اور جس کا چاہتا ہے تنگ کر دیتا ہے اور جو کچھ تم (راہِ خدا میں) خرچ کرتے ہو تو وہ اس کی جگہ عطا کرتا ہے اور وہ بہترین روزی دینے والا ہے۔'),
(3646, 34, 40, 'اور جس دن وہ لوگوں کو جمع کرے گا پھر فرشتوں سے کہے گا (پوچھے گا) کیا یہ لوگ تمہاری ہی عبادت کرتے تھے؟'),
(3647, 34, 41, 'وہ کہیں گے پاک ہے تیری ذات! تو ہمارا آقا ہے نہ کہ وہ (ہمارا تعلق تجھ سے ہے نہ کہ ان سے) بلکہ یہ تو جنات کی عبادت کیا کرتے تھے ان کی اکثریت انہی پر ایمان و اعتقاد رکھتی تھی۔'),
(3648, 34, 42, '(ارشادِ قدرت ہوگا) آج تم میں سے کوئی نہ کسی دوسرے کو نفع پہنچا سکے گا اور نہ نقصان اور ہم ظالموں سے کہیں گے کہ اب عذاب جہنم کا مزہ چکھو جسے تم جھٹلایا کرتے تھے۔'),
(3649, 34, 43, 'اور جب ان کے سامنے ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو وہ کہتے ہیں کہ یہ شخص تو بس یہ چاہتا ہے کہ تمہیں تمہارے باپ دادا کے معبودوں سے روکے اور کہتے ہیں کہ یہ (قرآن) نہیں ہے مگر گھڑا ہوا جھوٹ اور جب بھی کافروں کے پاس حق آیا تو انہوں نے یہی کہا کہ یہ تو کھلا ہوا جادو ہے۔'),
(3650, 34, 44, 'حالانکہ ہم نے نہ انہیں کوئی کتابیں دیں جنہیں وہ پڑھتے ہیں اور نہ ہی آپ سے پہلے ان کی طرف کوئی ڈرانے والا (نبی) بھیجا ہے۔'),
(3651, 34, 45, 'اور وہ ان سے پہلے گزرے ہوئے (نبیوں کو) جھٹلا چکے ہیں اور جو کچھ (ساز و سامان اور قوت و طاقت) ان کو عطا کیا تھا یہ (کفارِ مکہ) تو اس کے عشرِ عشیر کو بھی نہیں پہنچے۔ لیکن جب انہوں نے میرے رسولوں کو جھٹلایا تو میری سزا کیسی (سخت) تھی؟'),
(3652, 34, 46, '(اے نبی(ص)) آپ کہہ دیجئے! میں تمہیں صرف ایک بات کی نصیحت کرتا ہوں کہ تم دو، دو اور ایک ایک ہو کر خدا کیلئے کھڑے ہو جاؤ اور پھر غور و فکر کرو (ضرور تمہیں معلوم ہو جائے گا کہ) تمہارے ساتھی (رسولِ(ص) خدا) میں کسی قسم کا کوئی جنون (اور دیوانگی) نہیں ہے وہ تو صرف ایک سخت عذاب کے آنے سے پہلے تمہیں ڈرانے والا ہے۔'),
(3653, 34, 47, 'کہہ دیجئے! میں نے (تبلیغِ رسالت(ص) کے سلسلہ میں) تم سے جو اجر مانگا ہے وہ تمہارے ہی (فائدہ کے) لئے ہے میری اجرت تو بس اللہ کے ذمہ ہے اور وہ ہر چیز پر گواہ ہے۔'),
(3654, 34, 48, 'کہہ دیجئے! کہ میرا پروردگار جوکہ بڑا غیب دان ہے حق کو (باطل پر) ڈالتا (مارتا) ہے۔'),
(3655, 34, 49, 'آپ(ص) کہہ دیجئے! حق آگیا ہے اور باطل نہ کچھ ایجاد کر سکتا ہے اور نہ دوبارہ پلٹا سکتا ہے (بالکل مٹ گیا ہے)۔'),
(3656, 34, 50, 'کہہ دیجئے! اگر (تمہارے خیال کے مطابق) میں گمراہ ہوگیا ہوں تو اس کا وِزر و وبال میری جان پر ہے اور اگر میں ہدایت یافتہ ہوں تو یہ اس وحی کا ثمرہ ہے جو میرا پروردگار میری طرف بھیجتا ہے بے شک وہ بڑا سننے والا بالکل قریب ہے۔'),
(3657, 34, 51, 'اور کاش تم دیکھو کہ جب یہ لوگ گھبرائے ہوئے (پریشان حال) ہوں گے لیکن بچ نکلنے کا کوئی موقع نہ ہوگا اور وہ قریبی جگہ سے پکڑ لئے جائیں گے۔'),
(3658, 34, 52, 'اور (اب) وہ کہیں گے کہ ہم اس پر ایمان لے آئے۔ مگر اتنی دور دراز جگہ سے اب اس (ایمان) پر دسترس کہاں؟'),
(3659, 34, 53, 'حالانکہ اس سے پہلے تو وہ اس (ایمان) کا انکار کرتے رہے اور (بلاتحقیق) بہت دور سے اَٹکَل کے تُکے چلاتے رہے۔'),
(3660, 34, 54, 'سو اب ان کے اور ان چیزوں کے درمیان جن کے یہ خواہش مند ہیں رکاوٹ کھڑی کر دی جائے گی۔ جیساکہ اس سے پہلے ان کے ہم مشربوں کے ساتھ کیا جا چکا ہے یقیناً وہ بڑے شبہ اور شک میں مبتلا تھے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سب تعریفیں اللہ ہی کیلئے ہیں جو آسمانوں اور زمین کا پیدا کرنے والا ہے (اور) فرشتوں کو پیغام رساں بنانے والا ہے جن کے دو دو، تین تین اور چار چار پر اور بازو ہیں۔ وہ (اپنی مخلوق کی) خلقت میں جب چاہتا ہے اضافہ کرتا ہے۔ بیشک اللہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(3662, 35, 2, 'اور اللہ لوگوں کیلئے (اپنی) رحمت (کا جو دروازہ) کھول دے اسے کوئی روکنے والا نہیں ہے اور جسے وہ بند کر دے اسے اس (اللہ) کے بعد کوئی کھولنے والا نہیں ہے وہ زبردست (اور) بڑا حکمت والا ہے۔'),
(3663, 35, 3, 'اے لوگو! اللہ کے وہ احسانات یاد کرو جو اس نے تم پر کئے ہیں کیا اللہ کے سوا کوئی اور ہے جو تمہیں آسمان اور زمین سے روزی دیتا ہو؟ اس کے سوا کوئی الٰہ نہیں ہے تم کہاں الٹے چلے جا رہے ہو؟'),
(3664, 35, 4, '(اے رسول(ص)!) اور اگر یہ لوگ آپ(ص) کو جھٹلاتے ہیں تو آپ(ص) سے پہلے بھی بہت سے پیغمبر جھٹلائے گئے اور تمام معاملات کی بازگشت خدا ہی کی طرف ہے۔'),
(3665, 35, 5, 'اے لوگو! اللہ کا وعدہ یقیناً سچا ہے (خیال رکھنا) زندگانیٔ دنیا کہیں تمہیں دھوکے میں نہ ڈال دے اور نہ ہی بڑا دھوکہ باز (شیطان) تمہیں اللہ کے بارے میں دھوکہ دے۔'),
(3666, 35, 6, 'بے شک شیطان تمہارا دشمن ہے۔ لہٰذا تم بھی اسے (اپنا) دشمن ہی سمجھو، وہ اپنے گروہ کو اس لئے بلاتا ہے کہ وہ دوزخیوں میں سے ہو جائیں۔'),
(3667, 35, 7, 'جن لوگوں نے کفر اختیار کیا ان کیلئے سخت عذاب ہے اور جو لوگ ایمان لائے اور نیک عمل کئے ان کیلئے مغفرت اور بڑا اجر ہے۔'),
(3668, 35, 8, 'بھلا وہ شخص جس کا برا عمل (اس کی نگاہ میں) خوشنما بنا دیا گیا ہو اور وہ اسے اچھا سمجھنے لگے (کیا وہ ایک مؤمنِ صالح کی مانند ہو سکتا ہے؟ یا کیا اس کی اصلاح کی کوئی امید ہو سکتی ہے؟) بے شک اللہ جسے چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے۔ پس ان (بدبختوں) کے بارے میں افسوس کرتے کرتے آپ کی جان نہ چلی جائے۔ جو کچھ یہ لوگ کر رہے ہیں یقیناً اللہ اسے خوب جانتا ہے۔'),
(3669, 35, 9, 'اور اللہ ہی وہ (قادر) ہے جو ہواؤں کو بھیجتا ہے تو وہ بادل کو حرکت میں لاتی (اٹھاتی) ہیں پھر ہم اسے ایک مردہ (اجاڑ) شہر کی طرف لے جاتے ہیں پھر ہم اس کے ذریعہ سے زمین کو اس کے مردہ (اجاڑ) ہونے کے بعد زندہ (سرسبز) کر دیتے ہیں اسی طرح (قیامت کے دن لوگوں کا) جی اٹھنا ہوگا۔'),
(3670, 35, 10, 'جو کوئی عزت کا طلبگار ہے (تو وہ سمجھ لے) کہ ساری عزت اللہ ہی کیلئے ہے (لہٰذا وہ خدا سے طلب کرے) اچھے اور پاکیزہ کلام اس کی طرف بلند ہوتے ہیں اور نیک عمل انہیں بلند کرتا ہے (یا اللہ نیک عمل کو بلند کرتا ہے) اور جو لوگ (آپ کے خلاف) برائیوں کے منصوبے بناتے ہیں (بری تدبیریں کرتے ہیں) ان کیلئے سخت عذاب ہے (اور ان کا مکر و فریب آخرکار) نیست و نابود ہو جائے گا۔'),
(3671, 35, 11, 'اور اللہ ہی نے تمہیں مٹی سے پیدا کیا ہے پھر نطفے سے پھر تمہیں جوڑا جوڑا (نر و مادہ) بنا دیا اور کوئی عورت حاملہ نہیں ہوتی اور بچہ نہیں جنتی مگر اس (اللہ) کے علم سے اور نہ کسی شخص کی عمر میں زیادتی ہوتی ہے اور نہ کمی ہوتی ہے مگر یہ کہ وہ ایک کتاب (لوحِ محفوظ) میں موجود ہے اور یقیناً یہ بات اللہ کیلئے آسان ہے۔'),
(3672, 35, 12, 'اور پانی کے دونوں (ذخیرے) برابر نہیں ہیں یہ ایک میٹھا پیاس بجھانے والا ہے (اور) پینے میں خوشگوار اور دوسرا سخت کھاری اور کڑوا ہے اور تم ہر ایک سے تازہ گوشت کھاتے ہو اور زیور برآمد کرکے پہنتے ہو اور تم اس کی کشتیوں کو دیکھتے ہو کہ وہ پانی کو چیرتی پھاڑتی چلی جاتی ہیں تاکہ تم اللہ کا فضل (رزق) تلاش کرو اور تاکہ شکر گزار بنو۔'),
(3673, 35, 13, 'وہ رات کو دن کے اندر داخل کرتا ہے اور اسی نے سورج اور چاند کو مسخر کر رکھا ہے (ان میں سے) ہر ایک (اپنی) مقررہ میعاد تک رواں دواں ہے یہ ہے اللہ تمہارا پروردگار! اسی کی حکومت ہے اور اسے چھوڑ کر جنہیں تم پکارتے ہو وہ کھجور کی گٹھلی کے چھلکے کے بھی مالک نہیں ہیں۔'),
(3674, 35, 14, 'اگر تم انہیں پکارو تو وہ تمہاری پکار کو سن نہیں سکتے۔ اور اگر (بالفرض) سنیں بھی تو وہ تمہاری التجا کو قبول نہیں کر سکتے اور قیامت کے دن تمہارے اس شرک کا انکار کریں گے (اس سے بیزاری کا اعلان کریں گے) اور خدائے خبیر کی طرح تمہیں اور کوئی خبر نہیں دے سکتا۔'),
(3675, 35, 15, 'اے لوگو! تم (سب) اللہ کے محتاج ہو اور اللہ ہی بے نیاز ہے جو قابلِ تعریف ہے۔'),
(3676, 35, 16, 'وہ اگر چاہے تو تم سب کو (عدم آباد کی طرف) لے جائے اور (تمہاری جگہ) کوئی نئی مخلوق لے آئے۔'),
(3677, 35, 17, 'اور ایسا کرنا اللہ کیلئے کوئی مشکل کام نہیں ہے۔'),
(3678, 35, 18, 'اور کوئی بوجھ اٹھانے والا (گنہگار) کسی دوسرے کا بوجھ نہیں اٹھائے گا اور اگر کوئی بھاری بوجھ والا اس کے اٹھانے کیلئے (کسی کو) بلائے گا تو اس میں سے کچھ بھی نہ اٹھایا جائے گا اگرچہ وہ رشتہ دار ہی کیوں نہ ہو؟ (اے رسول(ص)!) آپ صرف انہی لوگوں کو ڈرا سکتے ہیں جو بے دیکھے اپنے پروردگار سے ڈرتے ہیں اور جو (باقاعدہ) نماز پڑھتے ہیں اور جو (گناہوں سے) پاکیزگی اختیار کرتا ہے تو وہ اپنے ہی فائدہ کیلئے پاکباز رہتا ہے (آخرکار) اللہ ہی کی طرف (سب کی) بازگشت ہے۔'),
(3679, 35, 19, 'اور اندھا اور آنکھوں والا برابر نہیں ہیں۔'),
(3680, 35, 20, 'اور نہ اندھیرا (کفر) اور اجالا (ایمان) برابر ہیں۔'),
(3681, 35, 21, 'اور نہ ہی (ٹھنڈا) سایہ (بہشت) اور تیز دھوپ (جہنم) برابر ہیں۔'),
(3682, 35, 22, 'اور نہ ہی زندے (مؤمنین) اور مردے (کافرین) برابر ہیں۔ بے شک اللہ جسے چاہتا ہے سنوارتا ہے (اے رسول) جو (کفار مردوں کی طرح) قبروں میں دفن ہیں آپ انہیں نہیں سنا سکتے۔'),
(3683, 35, 23, 'آپ تو صرف ڈرانے والے ہیں۔'),
(3684, 35, 24, 'بیشک ہم نے آپ کو حق کے ساتھ بشیر و نذیر (بشارت دینے والا اور ڈرانے والا) بنا کر بھیجا ہے اور کوئی امت ایسی نہیں (گزری) جس میں کوئی ڈرانے والا نہ گزرا ہو۔'),
(3685, 35, 25, 'اور اگر یہ لوگ آپ کو جھٹلاتے ہیں (تو یہ کوئی نئی بات نہیں) ان سے پہلے والے لوگ بھی جھٹلا چکے ہیں ان کے رسول(ع) ان کے پاس کھلے ہوئے نشان (معجزے) اور (آسمانی) صحیفے اور روشن (واضح ہدایت دینے والی) کتابیں لے کر آئے تھے۔'),
(3686, 35, 26, 'پھر میں نے کافروں کو پکڑ لیا (دیکھو) میری سزا کیسی سخت تھی؟'),
(3687, 35, 27, 'کیا تم نے نہیں دیکھا کہ اللہ نے آسمان سے پانی برسایا اور پھر ہم نے اس کے ذریعہ سے مختلف رنگوں کے پھل نکالے اور (اسی طرح) پہاڑوں میں بھی مختلف رنگوں کی گھاٹیاں ہیں کوئی سفید اور کوئی سرخ اور بعض حصے گہرے سیاہ۔'),
(3688, 35, 28, 'اور انسانوں، چلنے پھرنے والے جانوروں اور چوپایوں میں بھی اسی طرح مختلف رنگ پائے جاتے ہیں اور اللہ کے بندوں میں سے صرف علماء ہی اس سے ڈرتے ہیں بے شک اللہ غالب ہے (اور) بڑا بخشنے والا ہے۔'),
(3689, 35, 29, 'بے شک جو لوگ (کماحقہٗ) کتاب اللہ کی تلاوت کرتے ہیں اور باقاعدہ نماز پڑھتے ہیں اور جو کچھ ہم نے انہیں دیا ہے اس سے پوشیدہ اور اعلانیہ طور پر خرچ کرتے ہیں وہ ایک ایسی تجارت کے امیدوار ہیں جس میں تباہی (خسارہ) نہیں۔'),
(3690, 35, 30, 'تاکہ وہ (اللہ) انہیں پورا پورا اجر عطا فرمائے اور اپنے فضل و کرم سے اور زیادہ دے۔ بیشک وہ بڑا بخشنے والا، بڑا قدر دان ہے۔'),
(3691, 35, 31, 'اور جو کتاب ہم نے بذریعۂ وحی آپ(ص) کی طرف بھیجی ہے وہ برحق ہے اور اپنے سے پہلے والی کتابوں کی تصدیق کرنے والی ہے بیشک اللہ اپنے بندوں (کے حالات) سے باخبر (اور) بڑا دیکھنے والا نگران ہے۔'),
(3692, 35, 32, 'پھر ہم نے اس کا وارث ان کو بنایا جن کو ہم نے اپنے بندوں میں سے منتخب کر لیا ہے پس ان بندوں سے بعض تو اپنے نفس پر ظلم کرنے والے ہیں اور بعض معتدل ہیں اور بعض اللہ کے اذن (اور اس کی توفیق) سے نیکیوں میں سبقت لے جانے والے ہیں یہی تو بہت بڑا فضل ہے۔'),
(3693, 35, 33, '(ان کا صلہ) سدا بہار بہشت ہیں جن میں وہ داخل ہوں گے وہاں ان کو سونے کے کنگن اور موتی پہنائے جائیں گے اور وہاں ان کا لباس ریشمی ہوگا۔'),
(3694, 35, 34, 'اور وہ (بطور شکرانۂ نعمت کہیں گے سب تعریف اللہ) کیلئے ہے (اور اس کا شکر ہے) جس نے ہم سے رنج و غم دور کر دیا ہے بیشک ہمارا پروردگار بڑا بخشنے والا، بڑا قدر دان ہے۔'),
(3695, 35, 35, 'جس نے ہمیں اپنے فضل و کرم سے ایسی دائمی قیامت کی جگہ پر اتارا ہے جہاں نہ کوئی زحمت ہوتی ہے اور نہ ہی تھکن محسوس ہوتی ہے۔'),
(3696, 35, 36, 'اور جن لوگوں نے کفر اختیار کیا ان کیلئے دوزخ کی آگ ہے (وہاں) نہ ان کی موت کا فیصلہ کیا جائے گا کہ وہ مر جائیں اور نہ ہی ان کیلئے عذابِ جہنم میں کوئی کمی کی جائے گی ہم اسی طرح ہر ناشکرے کو سزا دیتے ہیں۔'),
(3697, 35, 37, 'اور وہ (دوزخی) اس میں چیخیں چلائیں گے اے ہمارے پروردگار! ہمیں یہاں سے نکال (اور دارِ دنیا میں بھیج) تاکہ ہم نیک عمل کریں ایسے نہیں جیسے پہلے کرتے تھے (ارشادِ قدرت ہوگا) کیا ہم نے تمہیں اتنی عمر نہیں دی تھی جس میں نصیحت قبول کرنے والا نصیحت قبول کر سکتا تھا؟ اور (علاوہ بریں) تمہارے پاس ڈرانے والا بھی آیا تھا اب (اپنے کئے کا) مزہ چکھو (آج) ظالموں کا کوئی یار و مددگار نہیں ہے۔'),
(3698, 35, 38, 'بیشک اللہ آسمانوں اور زمین کی پوشیدہ چیزوں کا جاننے والا ہے۔ یقیناً وہ دلوں کے رازوں سے خوب واقف ہے۔'),
(3699, 35, 39, 'وہ وہی ہے جس نے تمہیں (گزشتہ لوگوں کا) جانشین بنایا سو جو کوئی کفر کرے گا۔ تو اس کے کفر (کا وبال) اسی پر پڑے گا اور کافروں کا کفران کے پروردگار کے ہاں سوائے قہر و غضبِ الٰہی کے اور کسی چیز میں اضافہ نہیں کرتا اور کافروں کا کفر صرف ان کے خسارے اور گھاٹے میں اضافہ کرتا ہے۔'),
(3700, 35, 40, 'آپ(ص) کہہ دیجئے! (اے مشرکو) کیا تم نے کبھی اپنے (خودساختہ) شریکوں کو دیکھا بھی ہے جنہیں تم اللہ کو چھوڑ کر پکارتے ہو مجھے دکھاؤ انہوں نے زمین میں کیا پیدا کیا ہے؟ یا ان کی آسمانوں میں شرکت ہے یا ہم نے انہیں کوئی کتاب دی ہے کہ وہ اس کی بناء پر (اپنے شرک کے جواز کی) کوئی سند رکھتے ہوں؟ (کچھ بھی نہیں) بلکہ (یہ) ظالم لوگ ایک دوسرے سے محض پُر فریب وعدے کرتے ہیں۔'),
(3701, 35, 41, 'بے شک اللہ (اپنی قدرتِ کاملہ) سے آسمانوں اور زمین کو تھامے ہوئے ہے کہ وہ اپنی جگہ سے ہٹ نہ جائیں اور اگر وہ (بالفرض) ہٹ جائیں تو اس کے سوا انہیں کون تھام سکتا ہے؟ بے شک وہ (اللہ) بڑا بردبار (اور) بڑا بخشنے والا ہے۔'),
(3702, 35, 42, 'اور یہ لوگ (کفارِ مکہ) بڑی سخت قَسمیں کھا کر کہتے تھے کہ اگر ان کے پاس کوئی ڈرانے والا (پیغمبر) آیا تو وہ ہر ایک قوم سے بڑھ کر ہدایت قبول کرنے والے ہوں گے لیکن جب ان کے پاس ڈرانے والا آگیا تو اس (کی آمد) نے ان کی نفرت۔'),
(3703, 35, 43, 'زمین میں سرکشی کرنے اور بری سازشیں کرنے میں اضافہ ہی کیا حالانکہ بری سازشیں تو اس کو گھیرتی ہیں جو بری سازش کرتا ہے سو کیا اب یہ لوگ پہلے والے لوگوں کے ساتھ خدا کے طریقۂ کار کا انتظار کر رہے ہیں (کہ ان کے ساتھ بھی وہی ہو) تم خدا کے دستور (طریقۂ کار) میں کوئی تبدیلی نہیں پاؤگے (کہ مقررہ قاعدے سے پھر جائے)۔'),
(3704, 35, 44, 'کیا یہ لوگ زمین میں چلتے پھرتے نہیں ہیں کہ وہ دیکھتے کہ ان سے پہلے والے لوگوں کا انجام کیا ہوا تھا حالانکہ وہ قوت و طاقت میں ان سے بڑھے ہوئے تھے آسمانوں اور زمین میں کوئی ایسی چیز نہیں ہے جو اللہ کو عاجز (بے بس) کر سکے۔ وہ بڑا جاننے والا، بڑا قدرت رکھنے والا ہے۔'),
(3705, 35, 45, 'اور اگر خدا لوگوں کا ان کے (برے) اعمال کی پاداش میں جلدی مواخذہ کرتا (پکڑتا) تو روئے زمین پر کوئی جاندار نہ چھوڑتا لیکن ان کو ایک مقررہ مدت تک مہلت دیتا ہے تو جب ان کی مدت پوری ہو جائے گی تو اللہ اپنے بندوں کو خوب دیکھنے والا (نگران) ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یاسین(ص)۔'),
(3707, 36, 2, 'قرآنِ حکیم کی قسم۔'),
(3708, 36, 3, 'یقیناً آپ(ص) (خدا کے) رسولوں میں سے ہیں۔'),
(3709, 36, 4, '(اور) سیدھے راستے پر ہی ہیں۔'),
(3710, 36, 5, '(یہ قرآنِ) عزیز (غالب اور) بڑے مہربان کا نازل کیا ہوا ہے۔'),
(3711, 36, 6, 'تاکہ آپ اس قوم کو ڈرائیں جس کے باپ دادا کو (براہِ راست کسی پیغمبر سے) ڈرایا نہیں گیا اس لئے وہ غافل ہیں۔'),
(3712, 36, 7, 'ان میں سے اکثر پر (ان کے تعصب و عناد کی وجہ سے) یہ بات ثابت ہو چکی ہے کہ وہ ایمان نہیں لائیں گے۔'),
(3713, 36, 8, 'ہم نے (ان کے پیہم کفر و عناد کی وجہ سے گویا) ان کی گردنوں میں (گمراہی اور شیطان کی غلامی کے) طوق ڈال دیئے ہیں جو ان کی ٹھوڑیوں تک آگئے ہیں اس لئے وہ سر اوپر اٹھائے ہوئے ہیں (لہٰذا وہ سر جھکا نہیں سکتے)۔'),
(3714, 36, 9, 'اور ہم نے ایک دیوار ان کے آگے کھڑی کر دی ہے اور ایک دیوار ان کے پیچھے کھڑی کر دی ہے (اس طرح) ہم نے انہیں ڈھانک دیا ہے پس وہ کچھ دیکھ نہیں سکتے۔'),
(3715, 36, 10, 'ان کیلئے (دونوں باتیں) برابر ہیں خواہ آپ انہیں ڈرائیں یا نہ ڈرائیں وہ ایمان نہیں لائیں گے۔'),
(3716, 36, 11, 'آپ تو صرف اس شخص کو ڈرا سکتے ہیں جو نصیحت کی پیروی کرے اور بے دیکھے خدائے رحمن سے ڈرے پس ایسے شخص کو مغفرت (بخشش) اور عمدہ اجر کی خوشخبری دے دیں۔'),
(3717, 36, 12, 'بے شک ہم ہی مُردوں کو زندہ کرتے ہیں اور ان کے وہ اعمال لکھ لیتے ہیں جو وہ آگے بھیج چکے ہیں اور ان کے وہ آثار بھی ثبت کرتے ہیں جو وہ اپنے پیچھے چھوڑ جاتے ہیں اور ہم نے ہر چیز کو امامِ مبین میں جمع کر دیا ہے۔'),
(3718, 36, 13, 'اور (ان کو سمجھانے کیلئے) بطورِ مثال ایک خاص بستی کا قصہ سنائیں جب ان کے پاس (ہمارے) پیغام رساں آئے۔'),
(3719, 36, 14, '(پہلے) ہم نے ان کی طرف دو (رسول) بھیجے تو انہوں نے ان دونوں کو جھٹلایا تو ہم نے انہیں تیسرے (رسول) کے ساتھ تقویت دی۔ چنانچہ تینوں نے کہا کہ ہم تمہارے پاس بھیجے گئے ہیں۔'),
(3720, 36, 15, 'ان لوگوں نے کہا تم بس ہمارے ہی جیسے انسان ہو اور خدائے رحمٰن نے (تم پر) کوئی چیز نازل نہیں کی ہے تم بالکل جھوٹ بول رہے ہو۔'),
(3721, 36, 16, 'رسولوں(ع) نے کہا کہ ہمارا پروردگار خوب جانتا ہے کہ ہم تمہاری طرف بھیجے گئے ہیں۔'),
(3722, 36, 17, 'اور ہماری ذمہ داری صرف یہ ہے کہ ہم (خدا کا پیغام) صاف صاف پہنچا دیں۔'),
(3723, 36, 18, 'تو بستی والوں نے کہا کہ ہم تو تمہیں اپنے لئے فالِ بد سمجھتے ہیں (کہ تمہارے آنے سے قحط پڑ گیا ہے) اگر تم باز نہ آئے تو ہم تمہیں سنگسار کر دیں گے اور ہماری طرف سے تمہیں دردناک سزا ملے گی۔'),
(3724, 36, 19, 'رسولوں نے کہا کہ تمہاری فالِ بد تو تمہارے ساتھ ہے! کیا اگر تمہیں نصیحت کی جائے (تو یہ بات بدشگونی ہے)؟ حقیقت یہ ہے کہ تم حد سے بڑھ جانے والے لوگ ہو۔'),
(3725, 36, 20, 'اور ایک شخص شہر کے پرلے سرے سے دوڑتا ہوا آیا اور کہا اے میری قوم! رسولوں(ع) کی پیروی کرو۔'),
(3726, 36, 21, 'ان لوگوں کی پیروی کرو جو تم سے کوئی معاوضہ نہیں مانگتے اور خود ہدایت یافتہ بھی ہیں۔'),
(3727, 36, 22, 'مجھے کیا ہوگیا ہے کہ میں اس ہستی کی عبادت نہ کروں جس نے مجھے پیدا کیا ہے حالانکہ تم سب اسی کی طرف لوٹائے جاؤگے۔'),
(3728, 36, 23, 'کیا میں اسے چھوڑ کر ایسے خداؤں کو اختیار کروں کہ اگر خدائے رحمن مجھے کوئی نقصان پہنچانا چاہے تو ان کی سفارش مجھے کوئی فائدہ نہ پہنچا سکے اور نہ ہی وہ مجھے چھڑا سکیں۔'),
(3729, 36, 24, '(اگر میں ایسا کروں) تو میں اس وقت کھلی ہوئی گمراہی میں مبتلا ہوں گا۔'),
(3730, 36, 25, 'میں تو تمہارے پروردگار پر ایمان لے آیا ہوں تم میری بات (کان لگا کر) سنو (اور ایمان لے آؤ)۔'),
(3731, 36, 26, '(انجامِ کار ان لوگوں نے اس مؤمن کو سنگسار کر دیا اور اس سے) کہا گیا کہ بہشت میں داخل ہو جا۔ وہ کہنے لگا کاش میری قوم کو معلوم ہو جاتا۔'),
(3732, 36, 27, 'کہ میرے پروردگار نے کس وجہ سے مجھے بخش دیا ہے اور مجھے معزز لوگوں میں سے قرار دیا ہے۔'),
(3733, 36, 28, 'اور ہم نے اس (کی شہادت) کے بعد نہ تو اس کی قوم پر آسمان سے کوئی لشکر اتارا اور نہ ہی ہم کو (اتنی سی بات کیلئے لشکر) اتارنے کی ضرورت تھی۔'),
(3734, 36, 29, 'وہ (سزا) تو صرف ایک مہیب آواز (چنگھاڑ) تھی جس سے وہ ایک دم بجھ کر رہ گئے۔'),
(3735, 36, 30, 'افسوس ہے (ایسے) بندوں پر کہ جب بھی ان کے پاس کوئی رسول آتا ہے تو یہ اس کا مذاق اڑاتے ہیں۔'),
(3736, 36, 31, 'کیا انہوں نے نہیں دیکھا کہ ہم ان سے پہلے کتنی قوموں کو ہلاک کر چکے ہیں (اور) وہ کبھی ان کی طرف لوٹ کر نہیں آئیں گے۔'),
(3737, 36, 32, '(ہاں البتہ) وہ سب (ایک بار) ہماری بارگاہ میں حاضر کئے جائیں گے۔'),
(3738, 36, 33, 'اور (ہماری نشانیوں میں سے) ان کیلئے ایک نشانی وہ مردہ زمین ہے جسے ہم نے زندہ کر دیا اور اس سے دانہ (غلہ) نکالا جس سے وہ کھاتے ہیں۔'),
(3739, 36, 34, 'اور ہم نے اس (زمین) میں کھجوروں اور انگوروں کے باغات پیدا کئے اور اس سے چشمے جاری کر دیئے۔'),
(3740, 36, 35, 'تاکہ وہ اس کے پھلوں سے کھائیں حالانکہ یہ ان کے ہاتھوں کی کارگزاری نہیں ہے کیا یہ لوگ (اس پر بھی) شکر ادا نہیں کرتے؟'),
(3741, 36, 36, 'پاک ہے وہ ذات جس نے سب اقسام کے جوڑے جوڑے پیدا کئے ہیں خواہ (نباتات) ہو جسے زمین اگاتی ہے یا خود ان کے نفوس ہوں (بنی نوع انسان) یا وہ چیزیں ہوں جن کو یہ نہیں جانتے۔'),
(3742, 36, 37, 'اور ان کیلئے ایک نشانی رات بھی ہے جس سے ہم دن کو کھینچ لیتے ہیں تو ایک دم وہ اندھیرے میں رہ جاتے ہیں۔'),
(3743, 36, 38, 'اور (ایک نشانی) سورج ہے جو اپنے مقررہ ٹھکانے (مدار) کی طرف چل رہا ہے (گردش کر رہا ہے) یہ اندازہ اس (خدا) کا مقرر کیا ہوا ہے جو غالب ہے (اور) بڑا علم والا۔'),
(3744, 36, 39, 'اور (ایک نشانی) چاند بھی ہے جس کی ہم نے منزلیں مقرر کر دی ہیں یہاں تک کہ وہ (آخر میں) کھجور کی پرانی شاخ کی طرح ہو جاتا ہے۔'),
(3745, 36, 40, 'نہ سورج کے بس میں ہے کہ چاند کو جا پکڑے اور نہ ہی رات کیلئے ممکن ہے کہ وہ دن پر سبقت لے جائے اور سب (اپنے) ایک ایک فلک (دائرہ) میں تیر رہے ہیں۔'),
(3746, 36, 41, 'اور ان کیلئے (قدرتِ خدا کی) ایک نشانی یہ بھی ہے کہ ہم نے ان کی نسل کو بھری ہوئی کشتی (نوح(ع)) میں سوار کیا۔'),
(3747, 36, 42, 'اور ان کیلئے (خشکی اور تری) میں کشتی کی مانند اور سواریاں پیدا کی ہیں جن پر وہ سوار ہوتے ہیں۔'),
(3748, 36, 43, 'اور اگر ہم چاہیں تو انہیں غرق کر دیں پھر نہ کوئی ان کا فریاد رس ہو اور نہ ہی وہ چھڑائے جا سکیں۔'),
(3749, 36, 44, 'مگر یہ کہ ہماری رحمت (شاملِ حال) ہو جائے اور ایک (خاص) وقت تک بہرہ مند کرنا منظور ہو۔'),
(3750, 36, 45, 'اور جب ان سے کہا جاتا ہے کہ ڈرو اس (انجام و عذاب) سے جو تمہارے آگے ہے اور اس سے ڈرو جو تمہارے پیچھے ہے کہ تم پر رحم کیا جائے (تو وہ اس کی پرواہ نہیں کرتے)۔'),
(3751, 36, 46, 'اور ان کے پروردگار کی نشانیوں میں سے کوئی نشانی بھی ان کے پاس نہیں آتی مگر یہ کہ وہ اس سے روگردانی کرتے ہیں۔'),
(3752, 36, 47, 'اور جب ان سے کہا جاتا ہے کہ اللہ نے تمہیں جو کچھ عطا کیا ہے اس سے (کچھ) راہِ خدا میں بھی خرچ کرو تو کافر اہلِ ایمان سے کہتے ہیں کیا ہم اسے کھلائیں جسے اگر خدا چاہتا تو خود کھلا دیتا؟ تم لوگ کھلی ہوئی گمراہی میں مبتلا ہو۔'),
(3753, 36, 48, 'اور وہ (کافر) کہتے ہیں کہ اگر تم سچے ہو تو بتاؤ کہ یہ (قیامت کا) وعدہ وعید کب پورا ہوگا؟'),
(3754, 36, 49, 'وہ صرف ایک مہیب آواز (چنگھاڑ) یعنی نَفَخ صُور کا انتظار کر رہے ہیں جو انہیں اپنی گرفت میں لے لے گی جبکہ یہ لوگ (اپنے معاملات میں) باہم لڑ جھگڑ رہے ہوں گے۔'),
(3755, 36, 50, '(اس وقت) وہ نہ کوئی وصیت کر سکیں گے اور نہ ہی اپنے گھر والوں کی طرف واپس جا سکیں گے۔'),
(3756, 36, 51, 'اور جب (دوبارہ) صور پھونکا جائے گا تو وہ ایک دم اپنی قبروں سے (نکل کر) اپنے پروردگار کی طرف تیز تیز چلنے لگیں گے۔'),
(3757, 36, 52, '(اس وقت گھبرا کر) کہیں گے ہائے افسوس! کس نے ہمیں ہماری خوابگاہ سے اٹھایا؟ (جواب دیا جائے گا) یہ وہی (قیامت) ہے جس کا خدائے رحمن نے وعدہ (وعید) کیا تھا اور پیغمبروں(ع) نے بھی سچ کہا تھا۔'),
(3758, 36, 53, 'بس وہ ایک مہیب آواز (چنگھاڑ) ہوگی پھر سب کے سب ہمارے حضور حاضر کر دیئے جائیں گے۔'),
(3759, 36, 54, 'پس آج کے دن کسی پر کچھ بھی ظلم نہیں کیا جائے گا اور تمہیں بدلہ نہیں دیا جائے گا مگر انہی اعمال کا جو تم کیا کرتے تھے۔'),
(3760, 36, 55, 'بے شک آج بہشتی لوگ اپنے مشغلہ میں خوش ہوں گے۔'),
(3761, 36, 56, 'وہ اور ان کی بیویاں (گھنے) سایوں میں مسندوں پر تکیہ لگائے بیٹھے ہوں گے۔'),
(3762, 36, 57, 'وہاں ان کے (کھانے پینے) کیلئے (ہر قسم کے) پھل ہوں گے اور وہ سب کچھ ہوگا جو وہ طلب کریں گے۔'),
(3763, 36, 58, '(انہیں) مہربان (پروردگار) کی طرف سے سلام کہا جائے گا۔'),
(3764, 36, 59, '(ارشادِ قدرت ہوگا) اے مجرمو! الگ ہو جاؤ۔'),
(3765, 36, 60, 'اے اولادِ آدم! کیا میں نے تمہیں حکم نہیں دیا تھا کہ شیطان کی پرستش نہ کرنا؟ کہ وہ تمہارا کھلا ہوا دشمن ہے۔'),
(3766, 36, 61, 'ہاں البتہ میری عبادت کرو کہ یہی سیدھا راستہ ہے۔'),
(3767, 36, 62, '(اس کے باوجود) اس نے تم میں سے ایک کثیر گروہ کو گمراہ کر دیا کیا تم اتنا بھی نہیں سمجھتے تھے؟'),
(3768, 36, 63, 'یہ ہے وہ جہنم جس سے تمہیں ڈرایا جاتا تھا۔'),
(3769, 36, 64, 'آج اپنے کفر کی پاداش میں اس میں داخل ہو جاؤ (اور اس کی آگ تاپو)۔'),
(3770, 36, 65, 'آج ہم ان کے مونہوں پر مہر لگا دیں گے اور ہم سے ان کے ہاتھ کلام کریں گے اور ان کے پاؤں گواہی دیں گے اس کے بارے میں جو کچھ وہ کیا کرتے تھے؟'),
(3771, 36, 66, 'اور اگر ہم چاہتے تو ان کی آنکھوں کو مٹا دیتے پھر وہ راستہ کی طرف دوڑتے تو ان کو کہاں نظر آتا؟'),
(3772, 36, 67, 'اور اگر ہم چاہتے تو انہیں ان کی جگہ پر مسخ کرکے رکھ دیتے کہ نہ آگے بڑھ سکتے اور نہ پیچھے پلٹ سکتے۔'),
(3773, 36, 68, 'اور جس کو ہم لمبی عمر دیتے ہیں اسے خلقت (ساخت) میں الٹ دیتے ہیں کیا وہ عقل سے کام نہیں لیتے؟'),
(3774, 36, 69, 'اور ہم نے انہیں شعر و شاعری نہیں سکھائی اور نہ ہی یہ ان کے شایانِ شان ہے یہ تو بس ایک نصیحت اور واضح قرآن ہے۔'),
(3775, 36, 70, 'تاکہ آپ(ص) (اس کی ذریعہ سے) انہیں ڈرائیں جو زندہ (دل) ہیں اور تاکہ کافروں پر حجت تمام ہو جائے۔'),
(3776, 36, 71, 'کیا یہ لوگ نہیں دیکھتے (غور نہیں کرتے) کہ ہم نے ان کیلئے اپنے دست ہائے قدرت سے بنائی ہوئی چیزوں میں سے مویشی پیدا کئے (چنانچہ اب) یہ ان کے مالک ہیں۔'),
(3777, 36, 72, 'اور ہم نے ان (مویشیوں) کو ان کا مطیع کر دیا ہے پس ان میں سے بعض پر وہ سوار ہوتے ہیں اور بعض کا گوشت کھاتے ہیں۔'),
(3778, 36, 73, 'اور ان کیلئے ان میں اور بھی طرح طرح کے فائدے اور پینے کی چیزیں (دودھ وغیرہ) بھی ہیں تو کیا یہ لوگ شکر ادا نہیں کرتے؟'),
(3779, 36, 74, 'اور کیا انہوں نے اللہ کو چھوڑ کر اور خدا بنا لئے ہیں کہ شاید ان کی مدد کی جائے گی۔'),
(3780, 36, 75, 'حالانکہ وہ ان کی (کچھ بھی) مدد نہیں کر سکتے (بلکہ) وہ (کفار) ان (بتوں) کی حفاظت اور ان کے (دفاع) کیلئے حاضر باش لشکر ہیں۔'),
(3781, 36, 76, '(اے پیغمبر(ص)) ان لوگوں کی باتیں آپ کو رنجیدہ نہ کریں بے شک ہم خوب جانتے ہیں جو کچھ وہ چھپاتے ہیں اور جو کچھ وہ ظاہر کرتے ہیں۔'),
(3782, 36, 77, 'کیا انسان یہ نہیں دیکھتا (اس پر غور نہیں کرتا) کہ ہم نے اسے نطفہ سے پیدا کیا ہے سو وہ یکایک (ہمارا) کھلا ہوا مخالف بن گیا۔'),
(3783, 36, 78, 'وہ ہمارے لئے مثالیں پیش کرتا ہے اور اپنی خلقت کو بھول گیا ہے وہ کہتا ہے کہ ان ہڈیوں کو کون زندہ کرے گا جبکہ وہ بالکل بوسیدہ ہو چکی ہوں؟'),
(3784, 36, 79, 'آپ(ص) کہہ دیجئے! انہیں وہی (خدا دوبارہ) زندہ کرے گا جس نے انہیں پہلی بار پیدا کیا تھا وہ ہر مخلوق کو خوب جانتا ہے۔'),
(3785, 36, 80, '(خدا) جس نے تمہارے لئے سرسبز درخت سے آگ پیدا کی ہے اس سے (اور) آگ سلگاتے ہو۔'),
(3786, 36, 81, 'کیا وہ (قادرِ مطلق) جس نے آسمانوں اور زمین کو پیدا کیا اس بات پر قادر نہیں ہے کہ ان (لوگوں) جیسے (دوبارہ) پیدا کرے؟ ہاں وہ بڑا پیدا کرنے والا (اور) بڑا جاننے والا ہے۔'),
(3787, 36, 82, 'اس کا معاملہ تو بس یوں ہے کہ جب وہ کسی چیز کے پیدا کرنے کا ارادہ کرتا ہے تو کہہ دیتا ہے کہ ہو جا تو وہ (فوراً) ہو جاتی ہے۔'),
(3788, 36, 83, 'پس پاک ہے وہ ذات جس کے قبضۂ قدرت میں ہر چیز کی حکومت ہے اور اسی کی طرف تم لوٹائے جاؤگے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'باقاعدہ طور پر صف باندھنے والی ہستیوں کی قسم۔'),
(3790, 37, 2, '(برائی سے) جھڑکنے (اور) ڈانٹنے والی ہستیوں کی قسم۔'),
(3791, 37, 3, 'پھر ذکر (قرآن) کی تلاوت کرنے والی ہستیوں کی قسم۔'),
(3792, 37, 4, 'تمہارا الٰہ (خدا) ایک ہی ہے۔'),
(3793, 37, 5, 'جو آسمانوں اور زمین کا اور جو کچھ ان کے درمیان ہے (سب کا) پروردگار ہے۔'),
(3794, 37, 6, 'بے شک ہم نے آسمانِ دنیا کو ستاروں کی آرائش سے آراستہ کیا ہے۔'),
(3795, 37, 7, 'اور ہر سرکش و شیطان کی (دراندازی سے اسے) محفوظ کر دیا ہے۔'),
(3796, 37, 8, '(اب) ملاءِ اعلیٰ (عالمِ بالا) کی باتوں کو کان لگا کر نہیں سن سکتے اور وہ ہر طرف سے مارے جاتے ہیں۔'),
(3797, 37, 9, '(ان کو) دھتکارنے (اور بھگانے) کیلئے اور ان کیلئے دائمی عذاب ہے۔'),
(3798, 37, 10, 'مگر یہ کہ کوئی شیطان (فرشتوں کی) کوئی اڑتی ہوئی خبر اچک لے تو ایک تیز شعلہ اس کا پیچھا کرتا ہے۔'),
(3799, 37, 11, 'پس آپ(ص) ان سے پوچھئے کہ آیا ان کی خلقت زیادہ سخت ہے یا ان کی جن کو ہم نے پیدا کیا؟ بے شک ہم نے انہیں ایک لیس دار مٹی سے پیدا کیا ہے۔'),
(3800, 37, 12, 'تم تو (ان کے انکار پر) تعجب کرتے ہو مگر وہ اس کا مذاق اڑا رہے ہیں۔'),
(3801, 37, 13, 'اور جب انہیں نصیحت کی جاتی ہے تو وہ قبول نہیں کرتے۔'),
(3802, 37, 14, 'اور جب کوئی نشانی (معجزہ) دیکھتے ہیں تو اس کا مذاق اڑاتے ہیں۔'),
(3803, 37, 15, 'اور کہتے ہیں کہ یہ تو کھلا ہوا جادو ہے۔'),
(3804, 37, 16, 'بھلا جب ہم مر جائیں گے اور (مر کر) مٹی اور ہڈیاں ہو جائیں گے تو کیا ہم (زندہ کر کے) اٹھائے جائیں گے۔'),
(3805, 37, 17, 'یا کیا ہمارے اگلے باپ دادا بھی (اٹھائے جائیں گے؟)'),
(3806, 37, 18, 'آپ کہیے کہ ہاں اور تم (اس وقت) ذلیل و خوار ہوگے۔'),
(3807, 37, 19, 'بس وہ قیامت تو صرف ایک جھڑک ہوگی (اس کے بعد) ایک دم (زندہ ہو کر ادھر اُدھر) دیکھ رہے ہوں گے۔'),
(3808, 37, 20, 'اور کہیں گے ہائے ہماری بدبختی! یہ تو جزا (و سزا) کا دن ہے۔'),
(3809, 37, 21, '(ہاں) یہ وہی فیصلہ کا دن ہے جسے تم جھٹلایا کرتے تھے۔'),
(3810, 37, 22, '(فرشتوں کو حکم ہوگا کہ) جن لوگوں نے ظلم کیا اور ان کے ہم مشربوں کو اور اللہ کو چھوڑ کر جن کی یہ پرستش کرتے تھے ان سب کو جمع کرو۔'),
(3811, 37, 23, 'اور پھر سب کو دوزخ کا راستہ دکھاؤ۔'),
(3812, 37, 24, 'اور انہیں ٹھہراؤ (ابھی) ان سے کچھ پوچھنا ہے۔'),
(3813, 37, 25, 'تمہیں کیا ہوگیا ہے کہ تم ایک دوسرے کی مدد نہیں کرتے؟'),
(3814, 37, 26, 'بلکہ وہ تو آج سرِ تسلیم خم کئے ہوئے ہیں۔'),
(3815, 37, 27, 'اور (اس کے بعد) ایک دوسرے کی طرف متوجہ ہوں گے (اور) باہم سوال و جواب کریں گے۔'),
(3816, 37, 28, '(کمزور گروہ دوسرے بڑے گروہ سے) کہے گا کہ تم ہی ہمارے پاس دائیں جانب سے (بڑی شد و مد سے) آیا کرتے تھے (اور ہمیں کفر پر آمادہ کرتے تھے)۔'),
(3817, 37, 29, 'وہ کہیں گے بلکہ تم خود ہی ایمان نہیں لائے تھے۔'),
(3818, 37, 30, 'اور ہمارا تم پر کوئی زور نہیں تھا بلکہ تم خود ہی سرکش لوگ تھے۔'),
(3819, 37, 31, 'سو ہم پر ہمارے پروردگار کی بات پوری ہوگئی ہے اب ہم (سب عذابِ خداوندی کا مزہ) چکھنے والے ہیں۔'),
(3820, 37, 32, 'پس ہم نے تمہیں گمراہ کیا (کیونکہ) ہم خود بھی گمراہ تھے۔'),
(3821, 37, 33, 'سو وہ سب اس دن عذاب میں باہم شریک ہوں گے۔'),
(3822, 37, 34, 'بے شک ہم مجرموں کے ساتھ ایسا ہی سلوک کرتے ہیں۔'),
(3823, 37, 35, 'وہ ایسے (شریر) لوگ تھے کہ جب ان سے کہا جاتا تھا کہ اللہ کے سوا کوئی حقیقی معبود نہیں ہے تو یہ تکبر کیا کرتے تھے۔'),
(3824, 37, 36, 'اور کہتے تھے کہ آیا ایک دیوانے شاعر کی خاطر ہم اپنے خداؤں کو چھوڑ دیں۔'),
(3825, 37, 37, 'حالانکہ وہ (دینِ) حق لے کر آیا تھا اور (سارے) رسولوں(ص) کی تصدیق کی تھی۔'),
(3826, 37, 38, '(اے مجرمو!) تم ضرور دردناک عذاب کا مزہ چکھنے والے ہو۔'),
(3827, 37, 39, 'اور تمہیں اسی کا بدلہ دیا جائے گا جو کچھ تم کیا کرتے تھے۔'),
(3828, 37, 40, 'ہاں البتہ اللہ کے برگزیدہ بندے (اس سے مستثنیٰ ہوں گے)۔'),
(3829, 37, 41, 'یہ وہ (خوش نصیب) ہیں جن کیلئے معلوم و معین رزق ہے۔'),
(3830, 37, 42, 'یعنی طرح طرح کے میوے اور وہ عزت و اکرام کے ساتھ ہوں گے۔'),
(3831, 37, 43, 'باغہائے بہشت میں۔'),
(3832, 37, 44, '(مرصع) پلنگوں پر آمنے سامنے بیٹھے ہوں گے۔'),
(3833, 37, 45, '(شرابِ طہور کے) جام چشموں سے پُر کرکے ان کے درمیان گردش کر رہے ہوں گے۔'),
(3834, 37, 46, '(یعنی بالکل) سفید براق شراب جو پینے والوں کیلئے بڑی لذیذ ہوگی۔'),
(3835, 37, 47, 'نہ اس میں دردِ سر و بدحواسی ہوگی اور نہ ہی مدہوشی کی وجہ سے وہ بہکی ہوئی باتیں کریں گے۔'),
(3836, 37, 48, 'اور ان کے پاس نگاہیں نیچے رکھنے والی (باحیا) اور غزال چشم (بیویاں) ہوں گی۔'),
(3837, 37, 49, '(وہ رنگ و روپ اور نزاکت میں) گویا چھپے ہوئے انڈے ہیں۔'),
(3838, 37, 50, 'پھر وہ (بہشتی لوگ) ایک دوسرے کی طرف متوجہ ہوں گے اور سوال و جواب کریں گے۔'),
(3839, 37, 51, '(چنانچہ) ان میں سے ایک کہے گا کہ (دنیا میں) میرا ایک رفیق تھا۔'),
(3840, 37, 52, 'جو (مجھ سے) کہتا تھا کہ آیا تم بھی (قیامت کی) تصدیق کرتے ہو؟'),
(3841, 37, 53, 'کب جب مر جائیں گے اور (سڑ گل کر) مٹی اور ہڈیاں ہو جائیں گے تو (اس وقت زندہ کرکے) ہمیں جزا و سزا دی جائے گی؟'),
(3842, 37, 54, 'پھر وہ (اپنے جنتی ساتھیوں سے) کہے گا کیا تم اسے جھانک کر دیکھنا چاہتے ہو؟ (کہ اب وہ رفیق کہاں اور کس حال میں ہے؟)'),
(3843, 37, 55, 'پھر جب وہ جھانک کر دیکھے گا تو اسے جہنم کے وسط میں پائے گا۔'),
(3844, 37, 56, 'اس وقت (اس سے خطاب کرکے) کہے گا کہ خدا کی قسم تو تو مجھے ہلاک کرنا ہی چاہتا تھا۔'),
(3845, 37, 57, 'اور اگر میرے پروردگار کا فضل و کرم نہ ہوتا تو آج میں بھی اس گروہ میں شامل ہوتا جو پکڑ کر لایا گیا ہے۔'),
(3846, 37, 58, '(جنتی لوگ) کہیں گے کیا اب تو ہم نہیں مریں گے؟'),
(3847, 37, 59, 'سوائے پہلی موت کے اور نہ ہی ہمیں کوئی عذاب ہوگا۔'),
(3848, 37, 60, 'بے شک یہ بڑی کامیابی ہے۔'),
(3849, 37, 61, 'ایسی ہی کامیابی کیلئے عمل کرنے والوں کو عمل کرنا چاہیے۔'),
(3850, 37, 62, 'کیا یہ ضیافت اچھی ہے یا زقوم کا درخت؟'),
(3851, 37, 63, 'جسے ہم نے ظالموں کیلئے آزمائش بنایا۔'),
(3852, 37, 64, 'وہ ایک درخت ہے جو جہنم کی گہرائی سے نکلتا ہے۔'),
(3853, 37, 65, 'اس کے شگوفے ایسے ہیں جیسے شیطانوں کے سر۔'),
(3854, 37, 66, 'جہنمی لوگ اسے کھائیں گے اور اسی سے پیٹ بھریں گے۔'),
(3855, 37, 67, '(پھر زقوم کھانے کے بعد) انہیں (پینے کیلئے) کھولتا ہوا پانی ملا کر دیا جائے گا۔'),
(3856, 37, 68, 'پھر ان کی بازگشت جہنم ہی کی طرف ہوگی۔'),
(3857, 37, 69, 'ان لوگوں نے اپنے باپ دادا کو گمراہ پایا۔'),
(3858, 37, 70, 'اور یہ (بے سوچے سمجھے) انہی کے نقش قدم پر دوڑتے رہے۔'),
(3859, 37, 71, 'اور بے شک ان سے پہلے بھی اگلوں میں اکثر لوگ گمراہ ہو چکے تھے۔'),
(3860, 37, 72, 'اور ہم نے ان میں ڈرانے والے (رسول) بھیجے تھے۔'),
(3861, 37, 73, 'تو دیکھو کہ ڈرائے ہوؤں کا انجام کیا ہوا؟'),
(3862, 37, 74, 'ہاں البتہ اللہ کے برگزیدہ بندے اس سے مستثنیٰ ہیں۔'),
(3863, 37, 75, 'اور یقینا نوح(ع) نے ہم کو پکارا تو ہم کیا اچھا جواب دینے والے تھے۔'),
(3864, 37, 76, 'اور ہم نے انہیں اور ان کے اہل کو سخت تکلیف سے نجات دی۔'),
(3865, 37, 77, 'اور ہم نے انہی کی نسل کو باقی رہنے والا بنایا۔'),
(3866, 37, 78, 'اور ہم نے بعد میں آنے والے لوگوں میں ان کا ذکرِ خیر باقی رکھا۔'),
(3867, 37, 79, 'تمام جہانوں میں نوح(ع) پر سلام ہو۔'),
(3868, 37, 80, 'بیشک ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں۔'),
(3869, 37, 81, 'بے شک وہ ہمارے ایماندار بندوں میں سے تھے۔'),
(3870, 37, 82, 'پھر دوسرے لوگوں کو ہم نے غرق کر دیا۔'),
(3871, 37, 83, 'اور انہی (نوح(ع)) کے پیروکاروں میں سے ابراہیم(ع) بھی تھے۔'),
(3872, 37, 84, 'جبکہ وہ اپنے پروردگار کی بارگاہ میں قلبِ سلیم کے ساتھ حاضر ہوئے۔'),
(3873, 37, 85, 'جبکہ انہوں نے اپنے باپ (یعنی چچا) اور اپنی قوم سے کہا کہ تم کس چیز کی عبادت کرتے ہو؟'),
(3874, 37, 86, 'کیا تم اللہ کو چھوڑ کر جھوٹے گھڑے ہوئے خداؤں کو چاہتے ہو۔'),
(3875, 37, 87, 'آخر تمام جہانوں کے پروردگار کے بارے میں تمہارا کیا خیال ہے؟'),
(3876, 37, 88, 'پھر آپ نے ستاروں پر ایک نگاہ ڈالی۔'),
(3877, 37, 89, 'اور کہا کہ میں بیمار ہونے والا ہوں۔'),
(3878, 37, 90, 'اس پر وہ لوگ پیٹھ پھیر کر ان کے پاس سے چلے گئے۔'),
(3879, 37, 91, 'پس چپکے سے ان کے دیوتاؤں کی طرف گئے اور (ان سے) کہا کہ تم کھاتے کیوں نہیں؟'),
(3880, 37, 92, 'تمہیں کیا ہوگیا ہے کہ تم بولتے بھی نہیں ہو۔'),
(3881, 37, 93, 'پھر ان پر پَل پڑے اور اپنے دائیں ہاتھ سے خوب ضربیں لگائیں۔'),
(3882, 37, 94, 'پھر وہ لوگ دوڑتے ہوئے ان (ابراہیم(ع)) کے پاس آئے۔'),
(3883, 37, 95, 'آپ(ع) نے کہا کیا تم ان چیزوں کی پرستش کرتے ہو جنہیں خود تراشتے ہو؟'),
(3884, 37, 96, 'حالانکہ اللہ ہی نے تم کو پیدا کیا ہے اور ان چیزوں کو بھی جو تم بناتے ہو۔'),
(3885, 37, 97, 'ان لوگوں نے کہا کہ ان کیلئے ایک آتش کدہ بناؤ اور اسے دہکتی ہوئی آگ میں ڈال دو۔'),
(3886, 37, 98, 'سو انہوں نے آپ(ع) کے خلاف چال چلنا چاہی مگر ہم نے انہی کو نیچا دکھا دیا۔'),
(3887, 37, 99, 'آپ(ع) نے فرمایا کہ میں اپنے پروردگار کی طرف جاتا ہوں وہ میری راہنمائی فرمائے گا۔'),
(3888, 37, 100, '(اور عرض کیا) اے میرے پروردگار مجھے ایک بیٹا عطا فرما جو صالحین میں سے ہو۔'),
(3889, 37, 101, '(اس دعا کے بعد) ہم نے ان کو ایک حلیم و بردبار بیٹے کی بشارت دی۔'),
(3890, 37, 102, 'پس جب وہ لڑکا آپ کے ساتھ چلنے پھرنے کے قابل ہوا تو آپ(ع) نے فرمایا (اے بیٹا) میں نے خواب میں دیکھا ہے کہ میں تمہیں ذبح کر رہا ہوں تم غور کرکے بتاؤ کہ تمہاری رائے کیا ہے؟ عرض کیا بابا جان! آپ کو جو حکم دیا گیا ہے وہ بجا لائیے اللہ نے چاہا تو آپ مجھے صبر کرنے والوں میں سے پائیں گے۔'),
(3891, 37, 103, 'پس جب دونوں (باپ بیٹے) نے سرِ تسلیم خم کر دیا اور باپ نے بیٹے کو پیشانی کے بل لٹا دیا۔'),
(3892, 37, 104, 'تو ہم نے ندا دی اے ابراہیم(ع)!'),
(3893, 37, 105, 'تم نے (اپنے) خواب کو سچ کر دکھایا بے شک ہم نیکوکاروں کو اسی طرح جزا دیتے ہیں۔'),
(3894, 37, 106, 'بے شک یہ ایک کھلی ہوئی آزمائش تھی۔'),
(3895, 37, 107, 'اور ہم نے ایک عظیم قربانی کے عوض اس کو چھڑا لیا۔'),
(3896, 37, 108, 'اور ہم نے اس کا ذکر خیر آنے والوں میں چھوڑا۔'),
(3897, 37, 109, 'سلام ہو ابراہیم(ع) پر۔'),
(3898, 37, 110, 'ہم اسی طرح نیکوکاروں کو جزا دیتے ہیں۔'),
(3899, 37, 111, 'یقیناً وہ ہمارے ایمانداروں میں سے تھے۔'),
(3900, 37, 112, 'اور ہم نے انہیں اسحاق(ع) کی بشارت دی جو نیک بندوں میں سے نبی ہوں گے۔'),
(3901, 37, 113, 'اور ہم نے انہیں اور اسحاق(ع) کو برکت عطا کی اور ان دونوں کی اولاد میں سے نیوکار بھی ہوں گے اور اپنے نفس پر کھلا ظلم کرنے والے بھی۔'),
(3902, 37, 114, 'بے شک ہم نے موسیٰ(ع) و ہارون(ع) پر (بھی) احسان کیا۔'),
(3903, 37, 115, 'اور ہم نے ان دونوں کو اور ان کی قوم کو عظیم کرب و بلا سے نجات دی۔'),
(3904, 37, 116, 'اور ہم نے ان کی مدد کی (جس کی وجہ سے) وہی غالب رہے۔'),
(3905, 37, 117, 'اور ہم نے ان دونوں کو واضح کتاب (توراۃ) عطا کی۔'),
(3906, 37, 118, 'اور ہم نے (ہی) ان دونوں کو راہِ راست دکھائی۔'),
(3907, 37, 119, 'اور ہم نے آئندہ آنے والوں میں ان کا ذکرِ خیر باقی رکھا۔'),
(3908, 37, 120, 'موسیٰ(ع) و ہارون(ع) پر سلام ہو۔'),
(3909, 37, 121, 'اسی طرح ہم نیکوکاروں کو جزا دیتے ہیں۔'),
(3910, 37, 122, 'بیشک یہ دونوں ہمارے مؤمن بندوں میں سے تھے۔'),
(3911, 37, 123, 'اور بے شک الیاس(ع) (بھی) پیغمبروں میں سے تھے۔'),
(3912, 37, 124, '(وہ وقت یاد کرو) جب انہوں نے اپنی قوم سے کہا کہ تم (اللہ سے) ڈرتے نہیں ہو؟'),
(3913, 37, 125, 'کیا تم بعل (نامی بت) کو پکارتے ہو (اور اس کی پرستش کرتے ہو) اور بہترین خالق اللہ کو چھوڑتے ہو۔'),
(3914, 37, 126, 'جو تمہارا بھی پروردگار ہے اور تمہارے پہلے والے آباء و اجداد کا بھی پروردگار ہے۔'),
(3915, 37, 127, 'سو ان لوگوں نے انہیں جھٹلایا۔ لہٰذا وہ (سزا کیلئے) ضرور (پکڑ کر) حاضر کئے جائیں گے۔'),
(3916, 37, 128, 'ہاں البتہ جو اللہ کے مخلص اور برگزیدہ بندے ہیں وہ اس سے مستثنیٰ ہیں۔'),
(3917, 37, 129, 'اور ہم نے آئندہ آنے والوں میں ان کا ذکرِ خیر باقی رکھا۔'),
(3918, 37, 130, 'سلام ہو الیاس(ع)‌(ع)(ع) پر۔'),
(3919, 37, 131, 'ہم اسی طرح نیکوکاروں کو جزا دیتے ہیں۔'),
(3920, 37, 132, 'بیشک وہ مؤمن بندوں میں سے تھے۔'),
(3921, 37, 133, 'اور یقیناً لوط(ع) (بھی) پیغمبروں میں سے تھے۔'),
(3922, 37, 134, 'جب کہ ہم نے انہیں اور ان کے گھر والوں کو نجات دی۔'),
(3923, 37, 135, 'سوائے ایک بڑھیا (بیوی) کے جو پیچھے رہ جانے والوں میں سے تھی۔'),
(3924, 37, 136, 'پھر دوسروں کو ہم نے تہس نہس کر دیا۔'),
(3925, 37, 137, 'اور تم لوگ صبح بھی ان (کی ویران شدہ بستیوں) پر سے گزرتے ہو۔'),
(3926, 37, 138, 'اور شام کو بھی کیا پھر بھی عقل سے کام نہیں لیتے؟'),
(3927, 37, 139, 'اور یقیناً یونس(ع) (بھی) پیغمبروں میں سے تھے۔'),
(3928, 37, 140, 'جبکہ وہ (سوار ہونے کیلئے) بھری ہوئی کشتی کی طرف بھاگ کر گئے۔'),
(3929, 37, 141, 'پھر وہ قرعہ اندازی میں شریک ہوئے تو وہ پھینک دیئے گئے۔'),
(3930, 37, 142, 'تو انہیں مچھلی نے نگل لیا درآنحالیکہ کہ وہ (اپنے آپ کو) ملامت کر رہے تھے۔'),
(3931, 37, 143, 'پس اگر وہ تسبیح (خدا) کرنے والوں میں سے نہ ہوتے۔'),
(3932, 37, 144, 'تو (دوبارہ) اٹھائے جانے والے دن (قیامت) تک اسی (مچھلی) کے پیٹ میں رہتے۔'),
(3933, 37, 145, 'سو ہم نے انہیں (مچھلی کے پیٹ سے نکال کر) ایک کھلے میدان میں ڈال دیا۔ اس حال میں کہ وہ بیمار تھے۔'),
(3934, 37, 146, 'اور ہم نے ان پر (سایہ کرنے کیلئے) کدو کا ایک درخت لگا دیا۔'),
(3935, 37, 147, 'اور ہم نے ان کو ایک لاکھ یا اس سے کچھ زیادہ لوگوں کی طرف بھیجا۔'),
(3936, 37, 148, 'پس وہ ایمان لائے تو ہم نے انہیں ایک مدت تک (زندگی سے) لطف اندوز ہونے دیا۔'),
(3937, 37, 149, 'ان سے ذرا پوچھئے! کہ تمہارے پروردگار کیلئے بیٹیاں ہیں اور خود ان کیلئے بیٹے؟'),
(3938, 37, 150, 'کیا ہم نے فرشتوں کو عورت پیدا کیا اور وہ دیکھ رہے تھے؟'),
(3939, 37, 151, 'آگاہ ہو جاؤ کہ یہ دروغ بافی کرتے ہیں۔'),
(3940, 37, 152, 'جب کہتے ہیں کہ اللہ کے اولاد ہے اور بلاشبہ وہ جھوٹے ہیں۔'),
(3941, 37, 153, 'کیا اللہ نے بیٹوں کے مقابلہ میں بیٹیوں کو ترجیح دی۔'),
(3942, 37, 154, 'تمہیں کیا ہوگیا ہے تم کیا فیصلہ کرتے ہو؟'),
(3943, 37, 155, 'کیا تم اتنا بھی نہیں سمجھتے؟'),
(3944, 37, 156, 'کیا تمہارے پاس کوئی واضح دلیل ہے؟'),
(3945, 37, 157, 'اگر تم سچے ہو تو پھر اپنی وہ دستاویز پیش کرو۔'),
(3946, 37, 158, 'کیا ان لوگوں نے اس (خدا) اور جنوں کے درمیان کوئی رشتہ ناطہ قرار دیا ہے؟ حالانکہ خود جنات جانتے ہیں کہ وہ (جزا و سزا کیلئے) پکڑ کر حاضر کئے جائیں گے۔'),
(3947, 37, 159, 'پاک ہے اللہ (ان غلطیوں سے) جو وہ بیان کرتے ہیں۔'),
(3948, 37, 160, 'ہاں البتہ اللہ کے مخلص اور برگزیدہ بندے اس سے مستثنیٰ ہیں۔'),
(3949, 37, 161, 'سو تم اور جن کی تم پرستش کرتے ہو۔'),
(3950, 37, 162, 'تم (سب مل کر بھی) اللہ سے کسی کو پھیر نہیں سکتے۔'),
(3951, 37, 163, 'سوائے اس کے جو دوزخ کی بھڑکتی آگ کو تاپنے والا ہے۔'),
(3952, 37, 164, 'اور (فرشتوں) میں سے کوئی ایسا نہیں مگر یہ کہ اس کا ایک خاص مقام ہے۔'),
(3953, 37, 165, 'اور بے شک ہم صف بستہ حاضر ہیں۔'),
(3954, 37, 166, 'اور بے شک ہم تسبیح و تقدیس کرنے والے ہیں۔'),
(3955, 37, 167, 'اور (کفار بعثتِ نبوی(ص) سے پہلے) کہا کرتے تھے۔'),
(3956, 37, 168, 'تو اگر ہمارے پاس پہلے والے لوگوں کی طرح کوئی نصیحت (والی کتاب) ہوتی۔'),
(3957, 37, 169, 'تو یقیناً ہم اللہ کے برگزیدہ بندوں میں سے ہوتے۔'),
(3958, 37, 170, 'اور جب وہ (کتاب آئی) تو اس کا انکار کر دیا سو عنقریب (اس کا انجام) معلوم کر لیں گے۔'),
(3959, 37, 171, 'اور ہمارا وعدہ پہلے ہو چکا ہے اپنے ان بندوں کے ساتھ جو رسول ہیں۔'),
(3960, 37, 172, 'کہ یقیناً ان کی نصرت اور مدد کی جائے گی۔'),
(3961, 37, 173, 'اور ہمارا لشکر بہرحال غالب آنے والا ہے۔'),
(3962, 37, 174, 'سو ایک مدت تک ان سے بے اعتنائی کیجئے۔'),
(3963, 37, 175, 'اور انہیں دیکھتے رہئے اور عنقریب وہ خود بھی (اپنا انجام) دیکھ لیں گے۔'),
(3964, 37, 176, 'کیا وہ ہمارے عذاب کیلئے جلدی کرتے ہیں؟'),
(3965, 37, 177, 'تو جب وہ ان کے ہاں نازل ہو جائے گا تو وہ بڑی بری صبح ہوگی ڈرائے ہوؤں کیلئے۔'),
(3966, 37, 178, 'اور ایک مدت تک ان سے بے توجہی کیجئے۔'),
(3967, 37, 179, 'اور دیکھئے وہ بھی دیکھ رہے ہیں۔'),
(3968, 37, 180, 'پاک ہے آپ کا پروردگار جوکہ عزت کا مالک ہے ان (غلط) باتوں سے جو وہ بیان کرتے ہیں۔'),
(3969, 37, 181, 'اور سلام ہو رسولوں(ع) پر۔'),
(3970, 37, 182, 'اور ہر طرح کی تعریف ہے اس اللہ کیلئے جو سارے جہانوں کا پروردگار ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص! قَسم ہے نصیحت والے قرآن کی۔'),
(3972, 38, 2, 'آپ(ص) کی (نبوت برحق ہے) بلکہ وہ لوگ جنہوں نے کفر اختیار کیا ہے وہ تکبر اور مخالفت میں مبتلا ہیں۔'),
(3973, 38, 3, 'ہم نے ان سے پہلے کتنی قومیں ہلاک کر دیں۔ پس (عذاب کے وقت) وہ پکارنے لگیں مگر وہ وقت خلاصی کا نہ تھا۔'),
(3974, 38, 4, 'اور وہ اس بات پر تعجب کر رہے ہیں کہ ان کے پاس ایک ڈرانے والا (پیغمبر(ص)) انہی میں سے آیا ہے اور کافر کہتے ہیں کہ یہ (شخص) جادوگر (اور) بڑا جھوٹا ہے۔'),
(3975, 38, 5, 'کیا اس نے بہت سے خداؤں کو ایک خدا بنا دیا؟ بے شک یہ بڑی عجیب بات ہے۔'),
(3976, 38, 6, 'اور ان کے بڑے لوگ (سردار) یہ کہتے ہوئے (آپ(ص) کے پاس سے) اٹھ کھڑے ہوئے (اور قوم سے کہا) چلو اور اپنے معبودوں (کی عبادت) پر صبر و ثبات سے قائم رہو (ڈٹے رہو) یقیناً اس بات میں (اس شخص کی) کوئی غرض ہے۔'),
(3977, 38, 7, 'ہم نے تو یہ بات (کسی) پچھلے مذہب (عیسائیت) میں (بھی) نہیں سنی یہ تو محض ایک من گھڑت بات ہے۔'),
(3978, 38, 8, 'کیا ہم میں سے صرف اسی شخص پر الذکر (قرآن) اتارا گیا ہے؟ دراصل یہ لوگ میرے ذکر (کتاب) کے بارے میں شک میں مبتلا ہیں بلکہ انہوں نے ابھی میرے عذاب کا مزہ نہیں چکھا۔'),
(3979, 38, 9, 'کیا تمہارے غالب اور فیاض پروردگار کی رحمت کے خزانے ان کے پاس ہیں؟'),
(3980, 38, 10, 'یا کیا آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے کی حکومت ان کیلئے ہے (اگر ایسا ہے) تو پھر انہیں چاہیے کہ سیڑھیاں لگا کر چڑھ جائیں۔'),
(3981, 38, 11, 'یہ (کفار کے) لشکروں میں سے ایک لشکر ہے جسے وہاں (بمقامِ بدر) شکست ہوگی۔'),
(3982, 38, 12, 'اور ان سے پہلے قومِ نوح و عاد اور میخوں والا فرعون۔'),
(3983, 38, 13, 'اور ثمود اور قومِ لوط(ع) اور ایکہ والے (اپنے نبیوں کو) جھٹلا چکے ہیں یہ لوگ بڑے بڑے گروہ تھے۔'),
(3984, 38, 14, 'ان سب نے رسولوں(ع) کو جھٹلایا تو ہمارے عذاب کے مستوجب ہوئے۔'),
(3985, 38, 15, 'اور یہ لوگ بس ایک چنگھاڑ (دوسری نَفَخ) کا انتظار کر رہے ہیں جس کے بعد کچھ دیر نہ ہوگی۔'),
(3986, 38, 16, 'اور وہ (ازراہِ مذاق) کہتے ہیں کہ ہمارے پروردگار کے (عذاب میں سے) ہمارا حصہ ہمیں حساب کے دن (قیامت) سے پہلے دے دے۔'),
(3987, 38, 17, '(اے میرے رسول(ص)) جو کچھ یہ لوگ کہتے ہیں اس پر صبر کیجئے! اور ہمارے بندے داؤد(ع) کو یاد کیجئے جو قوت والے تھے بے شک وہ ہر معاملہ میں (اللہ کی طرف) بڑے رجوع کرنے والے تھے۔'),
(3988, 38, 18, 'ہم نے پہاڑوں کو ان کے ساتھ مسخر کر دیا تھا جو شام اور صبح کے وقت ان کے ساتھ تسبیح کرتے تھے۔'),
(3989, 38, 19, 'اور پرندوں کو بھی (جو تسبیح کے وقت) جمع ہو جاتے تھے (اور) سب (پہاڑ و پرند) ان کے فرمانبردار تھے (اور ان کی آواز پر آواز ملاتے تھے)۔'),
(3990, 38, 20, 'اور ہم نے ان کی حکومت کو مضبوط کر دیا تھا اور ان کو حکمت و دانائی اور فیصلہ کُن بات کرنے کی صلاحیت عطا کی تھی۔'),
(3991, 38, 21, '(اے رسول(ص)) کیا آپ تک مقدمہ والوں کی خبر پہنچی ہے جب وہ دیوار پھاند کر (ان کے) محرابِ عبادت میں داخل ہوگئے۔'),
(3992, 38, 22, 'جب وہ اس طرح (اچانک اندر) جنابِ داؤد(ع) کے پاس پہنچ گئے تو وہ ان سے گھبرا گئے انہوں نے کہا کہ آپ ڈریں نہیں ہم دو فریقِ مقدمہ ہیں ہم میں سے ایک نے دوسرے پر زیادتی کی آپ ہمارے درمیان حق کے ساتھ فیصلہ کر دیجئے اور بے انصافی نہ کیجئے اور راہِ راست کی طرف ہماری راہنمائی کیجئے۔'),
(3993, 38, 23, '(وجۂ نزاع یہ ہے کہ) یہ میرا بھائی ہے اس کے پاس ننانوے دنبیاں ہیں اور میرے پاس صرف ایک دنبی ہے اس پر بھی وہ کہتا ہے کہ یہ ایک دنبی بھی میرے حوالے کر دے اور گفتگو میں مجھے دباتا ہے۔'),
(3994, 38, 24, 'داؤد(ع) نے کہا اس شخص نے اتنی دنبیوں کے ساتھ تیری دنبی ملانے کا مطالبہ کرکے تجھ پر ظلم کیا ہے اور اکثر شرکاء اسی طرح ایک دوسرے پر زیادتی کرتے ہیں سوائے ان کے جو ایمان لاتے ہیں اور نیک عمل کرتے ہیں اور ایسے لوگ بہت کم ہیں اور (یہ بات کہتے ہوئے) داؤد (ع) نے خیال کیا کہ ہم نے ان کا امتحان لیا ہے تو انہوں نے اپنے پروردگار سے مغفرت طلب کی اور اس کے حضور جھک گئے اور توبہ و انابہ کرنے لگے۔'),
(3995, 38, 25, 'اور ہم نے انہیں معاف کر دیا اور بے شک ان کیلئے ہمارے ہاں (خاص) تقرب اور اچھا انجام ہے۔'),
(3996, 38, 26, 'اے داؤد(ع)! ہم نے تم کو زمین میں خلیفہ مقرر کیا ہے پس حق و انصاف کے ساتھ لوگوں میں فیصلہ کیا کرو اور خواہشِ نفس کی پیروی نہ کرو کہ وہ تمہیں اللہ کی راہ سے بھٹکا دے گی اور جو اللہ کی راہ سے بھٹک جاتے ہیں ان کیلئے سخت عذاب ہے اس لئے انہوں نے یوم الحساب (قیامت) کو بھلا دیا ہے۔'),
(3997, 38, 27, 'اور ہم نے آسمان و زمین کو اور جو کچھ ان کے درمیان ہے بے فائدہ پیدا نہیں کیا یہ ان لوگوں کا گمان ہے جو کافر ہیں پس کافروں کیلئے دوزخ کی بربادی ہے۔'),
(3998, 38, 28, 'کیا ہم ان لوگوں کو جو ایمان لائے اور نیک عمل کرتے رہے ان لوگوں کی مانند بنا دیں گے جو زمین میں فساد کرتے ہیں؟ یا کیا ہم پرہیزگاروں کو بدکاروں جیسا کر دیں گے؟'),
(3999, 38, 29, 'یہ وہ بابرکت کتاب ہے جو ہم نے آپ پر نازل کی ہے تاکہ لوگ اس کی آیتوں میں غور کریں اور تاکہ اہلِ عقل و فہم نصیحت حاصل کریں۔'),
(4000, 38, 30, 'اور ہم نے داؤد(ع) کو سلیمان(ع) (جیسا بیٹا) عطا کیا وہ بہت اچھا بندہ (اور اللہ کی طرف) بڑا رجوع کرنے والا تھا۔'),
(4001, 38, 31, '(اور وہ وقت یاد کرنے کے لائق ہے) جب شام (سہ پہر) کے وقت میں عمدہ گھوڑے (معائنہ کیلئے) ان پر پیش کئے گئے۔'),
(4002, 38, 32, 'تو انہوں نے کہا کہ میں مال (گھوڑوں) کی محبت میں اپنے پروردگار کی یاد سے غافل ہوگیا یہاں تک کہ وہ (آفتاب) پردہ میں چھپ گیا۔'),
(4003, 38, 33, '(حکم دیا) ان (گھوڑوں) کو میرے پاس واپس لاؤ اور وہ ان کی پنڈلیوں اور گردنوں پر ہاتھ پھیرنے لگے (قطع کرنا شروع کیا)۔'),
(4004, 38, 34, 'اور ہم نے سلیمان(ع) کو (اس طرح) آزمائش میں ڈالا کہ ان کے تخت پر ایک (بے جان) جسم ڈال دیا پھر انہوں نے (ہماری طرف) رجوع کیا۔'),
(4005, 38, 35, '(اور) کہا اے میرے پروردگار مجھے معاف کر اور مجھے ایسی سلطنت عطا کر جو میرے بعد کسی کیلئے زیبا نہ ہو۔ بیشک تو بڑا عطا کرنے والا ہے۔'),
(4006, 38, 36, 'اور ہم نے ان کیلئے ہوا کو مسخر کر دیا تھا جو ان کے حکم سے جہاں وہ چاہتے تھے آرام سے چلتی تھی۔'),
(4007, 38, 37, 'اور شیاطین بھی (مسخر کر دیئے) جو کچھ معمار تھے اور اور کچھ غوطہ خور۔'),
(4008, 38, 38, 'اور ان کے علاوہ (کچھ ایسے سرکش بھی تھے) جو زنجیروں میں جکڑے ہوئے تھے۔'),
(4009, 38, 39, 'یہ ہماری عطا ہے (آپ(ع) کو اختیار ہے) جسے چاہے عطا کر اور جس سے چاہے روک رکھ۔'),
(4010, 38, 40, 'اور بے شک ان (سلیمان(ع)) کیلئے ہمارے ہاں (خاص) قرب حاصل ہے اور بہتر انجام ہے۔'),
(4011, 38, 41, 'اور ہمارے بندۂ (خاص) ایوب(ع) کو یاد کرو جب انہوں نے اپنے پروردگار کو پکارا کہ شیطان نے مجھے سختی اور اذیت پہنچائی ہے۔'),
(4012, 38, 42, '(ہم نے حکم دیا کہ) اپنا پاؤں (زمین پر) مارو۔ یہ ٹھنڈا پانی ہے نہانے کیلئے اور پینے کیلئے۔'),
(4013, 38, 43, 'اور ہم نے اپنی خاص رحمت اور صاحبانِ عقل و فکر کیلئے بطورِ نصیحت انہیں نہ صرف ان کے اہل و عیال واپس دیئے (بلکہ) اتنے ہی اور بڑھا دیئے۔'),
(4014, 38, 44, 'اور (ہم نے کہا) اپنے ہاتھ میں (شاخوں کا) ایک مُٹھا لو اور اس سے مارو اور قَسم نہ توڑو۔ بے شک ہم نے انہیں صبر کرنے والا پایا بڑا اچھا بندہ (اور ہماری طرف) بڑا رجوع کرنے والا۔'),
(4015, 38, 45, 'اور ہمارے (خاص) بندوں ابراہیم(ع)، اسحاق(ع) اور یعقوب(ع) کو یاد کرو جو قوت و بصیرت والے تھے۔'),
(4016, 38, 46, 'اور ہم نے ان کو ایک خاص صفت کے ساتھ مخصوص کیا تھا اور وہ آخرت کی یاد تھی۔'),
(4017, 38, 47, 'اور وہ یقیناً ہمارے نزدیک برگزیدہ (اور) نیکوکار لوگوں میں سے تھے۔'),
(4018, 38, 48, 'اور اسماعیل(ع)، الیسع(ع) اور ذوالکفل(ع) کو یاد کرو یہ سب نیک لوگوں میں سے تھے۔'),
(4019, 38, 49, 'یہ ایک نصیحت ہے اور بے شک پرہیزگاروں کیلئے اچھا ٹھکانہ ہے۔'),
(4020, 38, 50, '(یعنی) ہمیشہ رہنے والے (سدابہار) باغات جن کے سب دروازے ان کیلئے کھلے ہوں گے۔'),
(4021, 38, 51, 'وہ ان میں تکیے لگائے بیٹھے ہوں گے اور وہ وہاں بہت سے میوے اور مشروبات طلب کرتے ہوں گے۔'),
(4022, 38, 52, 'اور ان کے پاس نیچی نگاہوں والی ہم عمر بیویاں ہوں گی۔'),
(4023, 38, 53, 'یہ وہ (نعمت) ہے جن کا حساب کے دن تمہیں عطا کرنے کا وعدہ کیا جاتا ہے۔'),
(4024, 38, 54, 'یہ ہمارا (خاص) رزق ہے جو ختم ہونے والا نہیں ہے۔'),
(4025, 38, 55, 'یہ بات تو ہو چکی (کہ پرہیزگاروں کا انجام یہ ہے) اور سرکش کا انجام بہت برا ہے۔'),
(4026, 38, 56, '(یعنی) دوزخ جس میں داخل ہوں گے پس وہ بہت ہی برا بچھونا ہے۔'),
(4027, 38, 57, 'یہ (حاضر ہے) کھولتا ہوا پانی اور پیپ پس چاہیے کہ وہ اسے چکھیں۔'),
(4028, 38, 58, 'اور اس کے علاوہ اسی قسم کی دوسری چیزیں بھی ہوں گی۔'),
(4029, 38, 59, 'یہ ایک اور فوج ہے جو تمہارے ساتھ (جہنم میں) داخل ہو رہی ہے ان کیلئے کوئی خوش آمدید نہیں ہے یہ بھی جہنم میں داخل ہونے والے ہیں۔'),
(4030, 38, 60, 'وہ (ان کے جواب میں کہیں گے) تمہارے لئے خوش آمدید نہ ہو تم ہی یہ (دن) ہمارے سامنے لائے ہو! تو (جہنم) کیا برا ٹھکانا ہے۔'),
(4031, 38, 61, 'پھر وہ کہیں گے اے ہمارے پروردگار! جو (کمبخت) روزِ بد ہمارے آگے لایا ہے اسے دوزخ میں عذاب دے۔'),
(4032, 38, 62, 'وہ کہیں گے کہ کیا بات ہے ہم یہاں ان لوگوں کو نہیں دیکھ رہے جن کو ہم (دنیا میں) برے لوگوں میں شمار کرتے تھے؟'),
(4033, 38, 63, 'کیا ہم نے بلاوجہ ان کا مذاق اڑایا تھا یا نگاہیں ان کی طرف سے چوک رہی ہیں؟'),
(4034, 38, 64, 'یہ بات بالکل سچی ہے کہ دوزخ والے آپس میں جھگڑیں گے۔'),
(4035, 38, 65, 'آپ کہہ دیجئے! کہ میں تو صرف (عذابِ خدا سے) ایک ڈرانے والا ہوں اور اللہ کے سوا جو یکتا اور غالب ہے اور کوئی خدا نہیں ہے۔'),
(4036, 38, 66, 'وہی آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے کا پروردگار ہے (اور وہ) بڑا زبردست (اور) بڑا بخشنے والا ہے۔'),
(4037, 38, 67, 'کہیے وہ (قرآن یا قیامت) بہت بڑی خبر ہے۔'),
(4038, 38, 68, 'جس سے تم منہ پھیرتے ہو۔'),
(4039, 38, 69, 'مجھے تو ملاءِ اعلیٰ (عالمِ بالا) کی کوئی خبر نہ تھی جبکہ وہ (فرشتے) آپس میں بحث کر رہے تھے۔'),
(4040, 38, 70, 'میری طرف اس لئے وحی کی جاتی ہے کہ میں ایک کھلا ہوا ڈرانے والا ہوں۔'),
(4041, 38, 71, '(وہ وقت یاد کرنے کے قابل ہے) جب تمہارے پروردگار نے فرشتوں سے کہا کہ میں گیلی مٹی سے ایک بشر (انسان) پیدا کرنے والا ہوں۔'),
(4042, 38, 72, 'جب میں اسے تیار کر لوں اور اس میں اپنی خاص روح پھونک دوں تو تم اس کے سامنے سجدہ ریز ہو جانا۔'),
(4043, 38, 73, 'تو (حسب الحکم) سب فرشتوں نے سجدہ کیا۔'),
(4044, 38, 74, 'سوائے ابلیس کے کہ اس نے تکبر کیا اور کافروں میں سے ہوگیا۔'),
(4045, 38, 75, 'ارشاد ہوا اے ابلیس! تجھے کس چیز نے روکا کہ اس کے سامنے سجدہ کرے جسے میں نے اپنی طاقت و قدرت سے پیدا کیا ہے؟ کیا تو نے تکبر کیا ہے یا تو سرکشوں میں سے ہوگیا ہے؟'),
(4046, 38, 76, 'اس نے کہا میں اس (آدم(ع)) سے بہتر ہوں (کیونکہ) تو نے مجھے آگ سے پیدا کیا ہے اور اسے گیلی مٹی سے پیدا کیا ہے۔'),
(4047, 38, 77, 'ارشاد ہوا تو یہاں (جنت) سے نکل جا کیونکہ تو راندۂ درگاہ ہے۔'),
(4048, 38, 78, 'اور یقیناً قیامت تک تجھ پر میری لعنت ہے۔'),
(4049, 38, 79, 'اس نے کہا اے پروردگار! مجھے اس دن تک مہلت دے جب لوگ دوبارہ اٹھائے جائیں گے۔'),
(4050, 38, 80, 'ارشاد ہوا (جا) تجھے مہلت دی گئی ہے۔'),
(4051, 38, 81, 'مگر اس دن تک جس کا وقت (تجھے) معلوم ہے۔'),
(4052, 38, 82, 'ابلیس نے کہا تیری عزت کی قسم! میں سب لوگوں کو گمراہ کروں گا۔'),
(4053, 38, 83, 'سوائے تیرے منتخب اور برگزیدہ بندوں کے۔'),
(4054, 38, 84, 'ارشاد ہوا کہ پھر حق یہ ہے اور میں حق ہی کہتا ہوں۔'),
(4055, 38, 85, 'کہ میں بھی دوزخ بھر دوں گا تجھ سے اور ان لوگوں سے جو تیری پیروی کریں گے۔'),
(4056, 38, 86, 'آپ(ص) کہہ دیجئے! کہ اس (تبلیغِ رسالت) پر تم سے کوئی معاوضہ نہیں مانگتا اور نہ ہی میں بناوٹ کرنے والوں میں سے ہوں۔'),
(4057, 38, 87, 'یہ (قرآن) نہیں ہے مگر سب جہانوں کیلئے نصیحت۔'),
(4058, 38, 88, 'اور تمہیں کچھ مدت کے بعد اس کی خبر معلوم ہو جائے گی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'یہ کتاب (قرآن) اس خدا کی طرف سے نازل کی گئی ہے جو (سب پر) غالب ہے (اور) بڑا حکمت والا ہے۔'),
(4060, 39, 2, '(اے رسول(ص)) بے شک ہم نے حق کے ساتھ یہ کتاب آپ کی طرف نازل کی ہے تو بس آپ اسی کی عبات کریں اس کے لئے دین کو خالص کرتے ہوئے۔'),
(4061, 39, 3, 'خبردار! خالص دین صرف اللہ کیلئے ہے اور جن لوگوں نے اس کے سوا ولی و سرپرست بنائے ہیں اور (تاویل یہ کرتے ہیں کہ) ہم ان کی صرف اسی لئے عبادت کرتے ہیں کہ وہ ہمیں اللہ کا مقرب بنا دیں گے۔ بے شک اللہ ان کے درمیان فیصلہ کرے گا ان باتوں میں جن کے بارے میں وہ اختلاف کرتے ہیں۔ بے شک اللہ ایسے شخص کو ہدایت نہیں دیتا جو جھوٹا (اور) بڑا ناشکرا ہو۔'),
(4062, 39, 4, 'اگر اللہ چاہتا کہ (کسی کو) اپنا بیٹا بنائے تو اپنی مخلوق میں سے جس کو چاہتا منتخب کر لیتا وہ اس سے پاک ہے وہی اللہ ہے جو یکتا (اور سب پر) غالب ہے۔'),
(4063, 39, 5, 'اس نے آسمانوں اور زمین کو حق (حکمت) کے ساتھ پیدا کیا ہے وہ رات کو دن پر اور دن کو رات پر لپیٹتا ہے اور اس نے سورج اور چاند کو (اس طرح) مسخر کر رکھا ہے کہ ہر ایک ایک مقررہ مدت تک رواں دواں ہے آگاہ ہو جاؤ کہ وہ (سب پر) غالب ہے (اور) بڑا بخشنے والا ہے۔'),
(4064, 39, 6, 'اس نے تم (سب) کو ایک نفس سے پیدا کیا پھر اسی (کی جنس) سے اس کا جوڑا (شریک حیات) پیدا کیا اور پھر تمہارے لئے (تعداد میں) چوپایوں کے آٹھ جوڑے پیدا کئے وہ تمہیں تمہاری ماؤں کے پیٹوں میں (تدریجاً) پیدا کرتا ہے۔ ایک خلقت کے بعد دوسری خلقت تین تاریک پردوں میں یہ اللہ تمہارا پروردگار ہے اسی کی حکومت ہے اس کے سوا کوئی الٰہ (خدا) نہیں ہے تم کدھر پھیرے جاتے ہو؟'),
(4065, 39, 7, 'اگر تم کفر (انکار و ناشکری کرو) تو بے شک وہ تم سے بے نیاز ہے اور وہ اپنے بندوں کیلئے (ناشکراپن) پسند نہیں کرتا اور اللہ کا تم شکر ادا کرو تو وہ اسے تمہارے لئے پسند کرتا ہے اور کوئی بوجھ اٹھانے والا کسی کا بوجھ نہیں اٹھائے گا اور پھر تم سب کی بازگشت تمہارے پروردگار کی طرف ہے وہ تمہیں بتائے گا جو کچھ تم کرتے رہے ہو بے شک وہ سینوں کے رازوں کو جاننے والا ہے۔'),
(4066, 39, 8, 'اور جب انسان کو کوئی تکلیف پہنچتی ہے تو وہ دل سے رجوع کرکے اپنے پروردگار کو پکارتا ہے اور پھر جب وہ اپنی طرف سے اسے کوئی نعمت عطا کرتا ہے تو وہ اس (تکلیف) کو بھول جاتا ہے جس کیلئے وہ پہلے اسے پکار رہا تھا اور اللہ کیلئے ہمسر (شریک) ٹھہرانے لگتا ہے تاکہ اس کے راستہ سے (لوگوں کو) گمراہ کرے۔ آپ(ص) کہہ دیجئے! تھوڑے دن اپنے کفر (ناشکرے پن) سے فائدہ اٹھا لے (انجامِ کار) یقیناً تو دوزخ والوں سے ہے۔'),
(4067, 39, 9, 'بھلا جو شخص رات کی گھڑیوں میں کبھی سجدہ کرکے اور کبھی قیام کرکے خدا کی عبادت کرتا ہے اور (اس کے باوجود) آخرت سے ڈرتا ہے اور اپنے پروردگار کی رحمت کا امیدوار ہے؟ (تو کیا وہ شخص جو ایسا نہیں ہے یکساں ہو سکتے ہیں؟) کہیے کیا علم والے اور جاہل برابر ہو سکتے ہیں؟ بے شک نصیحت تو صرف صاحبانِ عقل ہی حاصل کرتے ہیں۔'),
(4068, 39, 10, 'کہہ دیجئے! اے میرے وہ بندو جو ایمان لائے ہو! اپنے پروردگار (کی نافرمانی) سے ڈرو۔ (اور یاد رکھو) جو لوگ اس دنیا میں نیک عمل کرتے ہیں ان کیلئے (آخرت میں) نیک صلہ ہے اور اللہ کی زمین وسیع (کشادہ) ہے بے شک صبر کرنے والوں کو ان کا اجر بے حساب دیا جائے گا۔'),
(4069, 39, 11, 'کہہ دیجئے! کہ مجھے حکم دیا گیا ہے کہ میں دین کو اس کیلئے خالص کرکے (مکمل اخلاص کے ساتھ) اس کی عبادت کروں۔'),
(4070, 39, 12, 'اور مجھے حکم دیا گیا ہے کہ میں سب مسلمانوں سے پہلے مسلمان ہوں۔'),
(4071, 39, 13, 'کہئے کہ اگر میں اپنے پروردگار کی نافرمانی کروں تو ایک بڑے دن (قیامت) کے عذاب سے ڈرتا ہوں۔'),
(4072, 39, 14, 'آپ(ص) کہئے کہ میں اللہ کی عبادت کرتا ہوں اسی کیلئے اپنے دین کو خالص کرتے ہوئے۔'),
(4073, 39, 15, 'تم اس کے سوا جن کی چاہو عبادت (کرو) نیز کہہ دیجئے کہ اصلی خسارے والے تو وہ ہیں جنہوں نے اپنے آپ کو اور اپنے گھر والوں کو قیامت کے دن خسارے میں میں ڈالا آگاہ ہو جاؤ کہ یہی کھلا ہوا خسارہ ہے۔'),
(4074, 39, 16, 'ان (بدبختوں) کیلئے ان کے اوپر بھی آگ کے سائبان ہوں گے اور ان کے نیچے سے بھی یہی وہ (عذاب) ہے جس سے خدا اپنے بندوں کو ڈراتا ہے اے میرے بندو! مجھ سے ڈرو۔'),
(4075, 39, 17, 'اور جن (خوش بخت) لوگوں نے طاغوت (معبودانِ باطل) کی عبادت سے اجتناب کیا اور اللہ کی طرف رجوع کیا ان کیلئے خوشخبری ہے (اے نبی(ص)) میرے ان بندوں کو خوشخبری دے دو۔'),
(4076, 39, 18, 'جو (ہر کہنے والے کی) بات کو غور سے سنتے ہیں اور اس میں سے بہترین بات کی پیروی کرتے ہیں یہی وہ لوگ ہیں جن کو اللہ ہدایت دیتا ہے اور یہی صاحبانِ عقل ہیں۔'),
(4077, 39, 19, 'بھلا جس پر عذاب کا حکم ثابت ہو چکا تو کیا آپ اسے چھڑا سکتے ہیں جو (دوزخ کی) آگ میں ہیں۔'),
(4078, 39, 20, 'البتہ جو لوگ اپنے پروردگار سے ڈرتے رہے ان کیلئے ایسے بالا خانے ہیں کہ جن کے اوپر اور بالا خانے بنے ہوئے ہیں جن کے نیچے سے نہریں جاری ہیں یہ اللہ کا وعدہ ہے اور اللہ کبھی (اپنے) وعدے کے خلاف نہیں کرتا۔'),
(4079, 39, 21, 'کیا تم نے نہیں دیکھا کہ اللہ آسمان سے پانی اتارتا ہے اور پھر زیرِ زمین اس کے چشمے جاری کر دیتا ہے پھر اس سے مختلف قسم کی رنگ برنگی کھیتیاں برآمد کرتا ہے پھر وہ (پک کر) خشک ہونے لگتی ہیں تم انہیں زرد دیکھتے ہو پھر انہیں ریزہ ریزہ کر دیتا ہے۔ بے شک اس میں صاحبانِ عقل کیلئے نصیحت ہے۔'),
(4080, 39, 22, 'کیا وہ شخص جس کے سینہ کو اللہ نے (قبولِ) اسلام کیلئے کھول دیا ہے اور وہ اپنے پروردگار کی طرف سے نور (ہدایت) پر ہے (قسی القلب لوگوں کی طرح ہو سکتا ہے) بربادی ہے ان کیلئے جن کے دل ذکرِ خدا سے سخت ہوگئے ہیں یہ لوگ کھلی ہوئی گمراہی میں ہیں۔'),
(4081, 39, 23, 'اللہ ہی نے بہترین کلام نازل کیا ہے یعنی ایسی کتاب جس کی آیتیں آپس میں ملتی جلتی ہیں (اور) بار بار دہرائی جاتی ہیں اس (کے پڑھنے) سے ان لوگوں کے رونگٹے کھڑے ہو جاتے ہیں جو اپنے پروردگار سے ڈرتے ہیں پھر ان کے جسم اور ان کے دل اللہ کے ذکر کی طرف نرم (مائل) ہو جاتے ہیں یہ اللہ کی ہدایت ہے جس کے ذریعہ سے وہ جس کی چاہتا ہے راہنمائی کرتا ہے اور جسے چاہتا ہے گمراہی میں چھوڑ دیتا ہے اس کیلئے کوئی راہنما نہیں۔'),
(4082, 39, 24, 'بھلا وہ (احمق) شخص جو قیامت کے دن اپنے چہرہ سے اپنے آپ کو سخت عذاب سے بچاتا ہے؟ (اس عذاب سے محفوظ شخص کی مانند ہو سکتا ہے؟) اور ظالموں سے کہا جائے گا کہ (آج) مزہ چکھو اس کمائی کا جو تم کیا کرتے تھے۔'),
(4083, 39, 25, 'جو لوگ ان سے پہلے تھے انہوں نے (حق کو) جھٹلایا آخر ان پر وہاں سے عذاب آیا جدھر سے ان کو خیال نہ تھا۔'),
(4084, 39, 26, 'تو خدا نے ان کو اس دنیاوی زندگی میں ذلت و رسوائی کا مزہ چکھایا اور آخرت کا عذاب اس سے بھی بڑا ہے کاش وہ لوگ جانتے۔'),
(4085, 39, 27, 'اور بے شک ہم نے اس قرآن میں ہر قسم کی مثالیں پیش کی ہیں تاکہ وہ نصیحت حاصل کریں۔'),
(4086, 39, 28, 'یہ ایسا قرآن جو عربی زبان میں ہے جس میں کوئی کَجی نہیں ہے تاکہ وہ (ڈریں) پرہیزگاری اختیار کریں۔ اللہ نے ایک مثال بیان کی ہے کہ ایک غلام ہے جس کے کئی بد اخلاق مالک ہیں اور ایک دوسرا شخص ہے جو پورا ایک مالک کا ہے (ان دونوں) کا حال یکساں ہے؟ الحمد ﷲ! مگر اکثر لوگ (یہ حقیقت) جانتے نہیں ہیں۔'),
(4087, 39, 29, '(اے پیغمبر(ص)) بے شک آپ نے بھی مرنا ہے اور وہ لوگ بھی مرنے والے ہیں۔'),
(4088, 39, 30, 'پھر تم سب قیامت کے دن اپنے پروردگار کی بارگاہ میں جھگڑوگے (اور وہ حق و باطل کا فیصلہ کرے گا)۔'),
(4089, 39, 31, 'پس اس شخص سے بڑھ کر اور کون ظالم ہوگا جو خدا پر جھوٹ باندھے اور جب سچائی اس کے پاس آئے تو وہ اسے جھٹلائے۔ کیا جہنم میں کافروں کا ٹھکانہ نہیں ہے؟'),
(4090, 39, 32, 'اور جو شخص سچائی لے کر آیا اور جس نے اس کی تصدیق کی تو یہی لوگ پرہیزگار ہیں۔'),
(4091, 39, 33, 'ان کیلئے پروردگار کے پاس وہ سب کچھ ہے جو وہ چاہیں گے یہ نیکوکاروں کی جزا ہے۔'),
(4092, 39, 34, 'تاکہ اللہ ان کے بدترین اعمال کو معاف کرے اور انہیں ان کے بہترین اعمال کی جزا عطا فرمائے۔'),
(4093, 39, 35, 'کیا اللہ اپنے بندہ کیلئے کافی نہیں ہے؟ اور یہ لوگ آپ کو اللہ کے سوا دوسروں سے ڈراتے ہیں اور جس کو خدا گمراہی میں چھوڑ دے اس کو کوئی ہدایت دینے والا نہیں ہے۔'),
(4094, 39, 36, 'اور جسے خدا ہدایت دے اسے کوئی گمراہ کرنے والا نہیں ہے۔'),
(4095, 39, 37, 'کیا اللہ زبردست (اور) انتقام لینے والا نہیں ہے؟'),
(4096, 39, 38, 'اور اگر آپ(ص) اُن سے پوچھیں کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے؟ تو وہ یقینا کہیں گے کہ اللہ نے آپ(ص) کہیے! تمہارا کیا خیال ہے؟ جن کو تم اللہ کے سوا پکارتے ہو اگر اللہ مجھے کچھ نقصان پہنچانا چاہے تو کیا یہ اس کی جانب سے نقصان کو دور کر دیں گے؟ یا اگر اللہ مجھ پر کچھ رحمت کرنا چاہے تو کیا یہ اس کی رحمت کو روک سکتے ہیں؟ بس آپ(ص) کہہ دیجئے! کہ میرے لئے اللہ کافی ہے۔ بھروسہ کرنے والے اسی پر بھروسہ کرتے ہیں۔'),
(4097, 39, 39, 'آپ(ص) کہہ دیجئے! اے میری قوم! تم اپنی جگہ (اپنے طریقہ پر) کام کئے جاؤ۔ میں اپنا کام کرتا ہوں عنقریب تمہیں معلوم ہو جائے گا۔'),
(4098, 39, 40, 'کہ کس پر رسوا کرنے والا عذاب آتا ہے اور کس پر دائمی عذاب اترتا ہے؟'),
(4099, 39, 41, '(اے رسول(ص)) بیشک ہم نے یہ کتاب سب انسانوں (کی ہدایت) کیلئے حق کے ساتھ آپ(ص) پر اتاری ہے جو ہدایت حاصل کرے گا تو وہ اپنے لئے کرے گا اور جو گمراہی اختیار کرے گا تو اس کی گمراہی کا وبال اسی پر پڑے گا آپ(ص) ان کے ذمہ دار نہیں ہیں۔'),
(4100, 39, 42, 'اللہ ہی روحوں کو ان کی موت کے وقت قبض کرتا ہے اور جن کی موت (ابھی) نہیں آئی (تو ان کی روحیں) نیند کے وقت قبض کرتا ہے پھر جن (روحوں کی) موت کا فیصلہ کرتا ہے انہیں روک لیتا ہے اور دوسری روحوں کو ایک مقررہ مدت تک چھوڑ دیتا ہے۔ بیشک اس میں غور و فکر کرنے والوں کیلئے (قدرتِ خدا کی) نشانیاں ہیں۔'),
(4101, 39, 43, 'کیا ان لوگوں نے اللہ کو چھوڑ کر دوسروں کو شفیع (سفارشی) بنا رکھا ہے آپ(ص) کہہ دیجئے! کہ اگرچہ وہ (سفارشی) نہ کسی چیز کے مالک ہوں اور نہ ہی عقل و شعور رکھتے ہوں؟'),
(4102, 39, 44, 'آپ(ص) کہہ دیجئے کہ شفاعت پوری کی پوری اللہ کے قبضۂ قدرت میں ہے (اور) اسی کیلئے آسمانوں اور زمین کی بادشاہی ہے پھر تم اسی کی طرف لوٹائے جاؤگے۔'),
(4103, 39, 45, 'اور جب خدا کی توحید (اس کی وحدت و یکتائی) کا ذکر کیا جاتا ہے تو جو لوگ آخرت پر ایمان نہیں رکھتے ان کے دل کڑھنے لگتے ہیں اور جب اس کے دوسرے (مزعومہ شرکاء) کا ذکر کیا جاتا ہے تو وہ یکایک خوش ہو جاتے ہیں۔'),
(4104, 39, 46, 'آپ(ص) کہہ دیجئے! اے اللہ! اے آسمانوں اور زمین کے پیدا کرنے والے (اور) اے غائب و حاضر کے جاننے والے! تو ہی اپنے بندوں کے درمیان فیصلہ کرے گا ان چیزوں کے بارے میں جن میں وہ باہم اختلاف کیا کرتے تھے۔'),
(4105, 39, 47, 'اور جن لوگوں نے (کفر و شرک کرکے) ظلم کیا اگر ان کے پاس وہ سب کچھ ہو جو زمین میں ہے اور اتنی اور بھی تو وہ یہ سب کچھ قیامت کے دن برے عذاب سے بچنے کیلئے فدیہ دے دیں اور (اس دن) اللہ کی طرف سے وہ کچھ ظاہر ہوگا جس کا انہیں گمان بھی نہیں تھا۔'),
(4106, 39, 48, 'اور وہاں ان پر ان کی کمائی کی برائیاں ظاہر ہو جائیں گی اور انہیں وہ (عذاب) گھیر لے گا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(4107, 39, 49, 'اور جب انسان کو کوئی تکلیف پہنچتی ہے تو وہ ہمیں پکارتا ہے اور پھر جب ہم اسے اپنی جانب سے کوئی نعمت عطا کرتے ہیں تو وہ کہتا ہے کہ یہ تو مجھے اپنے علم و ہنر کی بنا پر دی گئی ہے بلکہ وہ ایک آزمائش ہے لیکن اکثر لوگ نہیں جانتے۔'),
(4108, 39, 50, 'ایسی بات ان لوگوں نے بھی کہی تھی جو ان سے پہلے تھے تو (ہماری گرفت کے وقت) ان کی کوئی کمائی ان کے کام نہ آئی۔'),
(4109, 39, 51, 'تو انہوں نے جو کچھ کمایا تھا اس کے برے نتائج ان تک پہنچے اور جو ان لوگوں میں سے ظالم ہیں ان تک بھی۔ ان کی برائی کے برے نتائج پہنچیں گے اور وہ (اللہ کو) عاجز نہیں کر سکتے۔'),
(4110, 39, 52, 'کیا وہ نہیں جانتے کہ اللہ جس کا چاہتا ہے رزق کشادہ کرتا ہے اور جس کا چاہتا ہے تنگ کر دیتا ہے بے شک اس میں ایمان والوں کیلئے (قدرت کی) بڑی نشانیاں ہیں۔'),
(4111, 39, 53, 'آپ(ص) کہہ دیجئے! کہ اے میرے بندو! جنہوں نے (گناہ پر گناہ کرکے) اپنی جانوں پر زیادتی کی ہے اللہ کی رحمت سے ناامید نہ ہو۔ بے شک اللہ سب گناہوں کو بخش دیتا ہے کیونکہ وہ بڑا بخشنے والا (اور) بڑا رحم کرنے والا ہے۔'),
(4112, 39, 54, 'اور اپنے پروردگار کی طرف رجوع کرو اور اس کے فرمانبردار بن جاؤ قبل اس کے کہ تم پر عذاب آجائے پھر تمہاری کوئی مدد نہیں کی جائے گی۔'),
(4113, 39, 55, 'اور اس بہترین کلام و پیغام کی پیروی کرو جو تمہارے پروردگار کی طرف سے تمہاری طرف اتارا گیا ہے پیشتر اس کے کہ تم پر اچانک عذاب آجائے اور تمہیں خبر بھی نہ ہو۔'),
(4114, 39, 56, 'مبادا کوئی شخص یہ کہے ہائے افسوس! میری اس کوتاہی پر جو میں نے خدا کی جناب میں کی اور میں تو مذاق اڑانے والوں میں شامل رہا۔'),
(4115, 39, 57, 'یا کوئی یہ کہے کہ اگر اللہ مجھے ہدایت دیتا تو میں بھی متقیوں میں سے ہوتا۔'),
(4116, 39, 58, 'یا کوئی عذاب دیکھ کر یہ کہے کاش مجھے ایک بار (دنیا میں دوبارہ جانے کا) موقع ملتا تو میں بھی نیکوکاروں میں سے ہو جاتا۔'),
(4117, 39, 59, 'ہاں (کیوں نہیں) تیرے پاس میری آیتیں آئی تھیں مگر تو نے انہیں جھٹلایا اور تکبر کیا اور تو کافروں میں سے تھا۔'),
(4118, 39, 60, 'اور قیامت کے دن تم دیکھوگے کہ جن لوگوں نے خدا پر جھوٹ باندھا تھا ان کے چہرے سیاہ ہوں گے۔ کیا تکبر کرنے والوں کا ٹھکانا جہنم نہیں ہے؟'),
(4119, 39, 61, 'اور اللہ متقیوں کو ان کی کامیابی کی وجہ سے اس طرح نجات دے گا کہ انہیں نہ کوئی تکلیف پہنچے گی اور نہ ہی وہ غمگین ہوں گے۔'),
(4120, 39, 62, 'اور وہی ہر چیز کا پیدا کرنے والا ہے اور وہی ہر چیز پر نگہبان ہے۔'),
(4121, 39, 63, 'اور آسمانوں اور زمین (کے خزانوں) کی کنجیاں اسی کے پاس ہیں اور جو لوگ اللہ کی آیتوں کا انکار کرتے ہیں وہی نقصان اٹھانے والے ہیں۔'),
(4122, 39, 64, 'آپ(ص) کہہ دیجئے! اے جاہلو! کیا تم (پھر بھی) مجھ سے غیر اللہ کی عبادت کرنے کی فرمائش کرتے ہو؟'),
(4123, 39, 65, 'بیشک آپ(ص) کی طرف بھی وحی کی جا چکی ہے اور ان (انبیاء(ع)) کی طرف بھی جو آپ سے پہلے تھے کہ اگر (بفرضِ محال) آپ نے بھی شرک کیا تو آپ کے عمل ضائع ہو جائیں گے اور آپ نقصان اٹھانے والوں میں سے ہو جائیں گے۔'),
(4124, 39, 66, '(اے نبی(ص)) بلکہ آپ بس اللہ ہی کی عبادت کیجئے اور شکر گزار بندوں میں سے ہو جائیے۔'),
(4125, 39, 67, 'اور ان لوگوں نے اللہ کی اس طرح قدر نہیں کی جس طرح کہ اس کی قدر کرنی چاہیے تھی۔ حالانکہ قیامت کے دن ساری زمین اس کی مٹھی میں ہوگی اور سب آسمان بھی اس کے دائیں ہاتھ میں لپٹے ہوں گے وہ پاک ہے اور برتر ہے اس شرک سے جو وہ کرتے ہیں۔'),
(4126, 39, 68, 'اور (پہلی بار) صور پھونکا جائے گا تو جو آسمانوں میں ہیں وہ بھی اور جو زمین میں وہ بھی سب بے ہوش ہو کر گر پڑیں گے سوائے ان کے جن کو اللہ چاہے گا (وہ بے ہوش نہیں ہوں گے) پھر دوبارہ پھونکا جائے گا تو وہ ایک دم کھڑے ہوکر (ِادھر اُدھر) دیکھنے لگیں گے۔'),
(4127, 39, 69, 'اور زمین اپنے پروردگار کے نور سے چمک اٹھے گی اور نامۂ اعمال رکھ دیا جائے گا اور ان پر ظلم نہیں کیا جائے گا۔'),
(4128, 39, 70, 'اور ہر شخص نے جو کچھ عمل کیا ہوگا اسے اس کا پورا پورا بدلہ دیا جائے گا اور لوگ جو کچھ کرتے ہیں ہیں اللہ اسے خوب جانتا ہے۔'),
(4129, 39, 71, 'اور جن لوگوں نے کفر اختیار کیا وہ (اس خدائی فیصلہ کے بعد) جہنم کی طرف گروہ در گروہ ہانکے جائیں گے یہاں تک کہ جب وہ اس کے پاس پہنچیں گے تو اس کے دروازے کھول دیئے جائیں گے اور اس کے داروغے ان سے کہیں گے کہ کیا تمہارے پاس تمہی میں سے کچھ رسول(ع) نہیں آئے تھے جو تمہیں تمہارے پروردگار کی آیتیں پڑھ کر سناتے تھے اور تمہیں اس دن کے پیش آنے سے ڈراتے تھے وہ (جواب میں) کہیں گے کہ ہاں (آئے تو تھے) لیکن (ہم نے نہ مانا کیونکہ) کافروں پر عذاب کا فیصلہ ہو چکا تھا۔'),
(4130, 39, 72, '(پھر ان سے) کہا جائے گا کہ جہنم کے دروازوں میں داخل ہو جاؤ اس میں ہمیشہ رہنے کیلئے کتنا برا ٹھکانا ہے متکبروں کا۔'),
(4131, 39, 73, 'اور جو لوگ اپنے پروردگار سے ڈرتے رہتے تھے وہ گروہ در گروہ بہشت کی طرف لے جائے جائیں گے یہاں تک کہ جب وہ اس کے پاس پہنچیں گے اور اس کے دروازے کھولے جا چکے ہوں گے اور اس کے نگہبان ان سے کہیں گے سلامٌ علیکم (سلام ہو تم پر) تم پاک ہو! اب تم اس میں داخل ہو جاؤ ہمیشہ رہنے کیلئے۔'),
(4132, 39, 74, 'اور وہ کہیں گے کہ سب تعریف (اور شکر) ہے اس خدا کا جس نے ہم سے کیا ہوا اپنا وعدہ سچا کر دکھایا اور ہمیں (اس) زمین کا وارث بنایا کہ اب ہم جنت میں جہاں چاہیں گے وہاں رہیں گے۔ کتنا اچھا صلہ ہے (نیک) عمل کرنے والوں کا۔'),
(4133, 39, 75, 'اور آپ فرشتوں کو دیکھیں گے کہ وہ عرش (الٰہی) کے اردگرد اپنے پروردگار کی تسبیح (تقدیس) کر رہے ہوں گے اور ان (لوگوں) کے درمیان حق (و انصاف) کے ساتھ فیصلہ کر دیا جائے گا ہر قِسم کی تعریف اس اللہ کیلئے ہے جو تمام جہانوں کا پروردگار ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حا۔ میم۔'),
(4135, 40, 2, 'اس کتاب (قرآن) کا نزول اس اللہ کی طرف سے ہے جو زبردست (اور) بڑا جاننے والا ہے۔'),
(4136, 40, 3, 'جو گناہ معاف کرنے والا (اور) توبہ قبول کرنے والا، سخت سزا دینے والا (اور) عطا و بخشش والا ہے اس کے سوا کوئی الٰہ (خدا) نہیں ہے سب کی بازگشت اسی کی طرف ہے۔'),
(4137, 40, 4, 'اللہ کی آیتوں کے بارے میں صرف کافر ہی جھگڑا کرتے ہیں۔ لہٰذا ان لوگوں کا (مختلف) شہروں میں (آزادی) کے ساتھ گھومنا پھرنا کہیں آپ کو دھوکہ میں نہ ڈال دے (کہ انہیں عذاب نہیں ہوگا)۔'),
(4138, 40, 5, 'ان سے پہلے نوح(ع) کی قوم نے (نوح(ع) کو) جھٹلایا اور ان کے بعد اور بہت سے گروہوں نے (اپنے رسولوں(ع) کو) جھٹلایا اور ہر امت نے ارادہ کیا کہ اپنے رسول(ع) کو گرفتار کرے (اور پھر شہید کر دے) اور ناحق طریقہ پر جھگڑا کیا تاکہ اس کے ذریعہ سے حق کو شکست دے۔ سو (انجامِ کار) میں نے ان کو پکڑ لیا تو میرا عذاب کیسا (سخت) تھا۔'),
(4139, 40, 6, 'اور اسی طرح آپ(ص) کے پروردگار کا یہ فیصلہ کافروں پر ثابت ہو چکا کہ وہ سب دوزخی ہیں۔'),
(4140, 40, 7, 'جو (فرشتے) عرش کو اٹھائے ہوئے ہیں اور جو اس کے اردگرد میں وہ سب اپنے پروردگار کی حمد کے ساتھ تسبیح کرتے ہیں اور وہ اس پر ایمان رکھتے ہیں اور ایمان والوں کیلئے مغفرت طلب کرتے ہیں کہ اے ہمارے پروردگار تو اپنی رحمت اور اپنے علم سے ہر چیز کا احاطہ کئے ہوئے ہے۔ پس تو ان لوگوں کو بخش دے جو توبہ کرتے ہیں اور تیرے راستہ کی پیروی کرتے ہیں اور انہیں دوزخ کے عذاب سے بچا لے۔'),
(4141, 40, 8, 'اے ہمارے پروردگار! اور ان کو ان ہمیشہ رہنے والے بہشتوں میں داخل کر جن کا تو نے ان سے وعدہ کیا ہے اور ان کے آباء و اجداد، بیویوں اور اولاد میں سے جو صالح اور لائق ہوں ان کو بھی (بخش دے اور وہیں ان کے ہمراہ داخل کر) بے شک تو غالب ہے (اور) بڑا حکمت والا ہے۔'),
(4142, 40, 9, 'اور ان کو برائیوں سے بچا اور جس کو تو نے قیامت کے دن برائیوں سے بچا لیا بس تو نے اس پر رحمت فرمائی اور یہی بہت بڑی کامیابی ہے۔'),
(4143, 40, 10, 'بے شک جن لوگوں نے کفر اختیار کیا (قیامت کے دن) ان کو پکار کر کہا جائے گا کہ جتنا (آج) تم اپنے آپ سے بیزار ہو۔ اللہ اس سے زیادہ اس وقت تم سے بیزار (ناراض) ہوتا تھا جب تمہیں ایمان کی دعوت دی جاتی تھی اور تم کفر اختیار کرتے تھے۔'),
(4144, 40, 11, 'وہ کہیں گے اے ہمارے پروردگار! تو نے ہمیں دو بار موت دی اور دو بار زندہ کیا بس اب ہم اپنے گناہوں کا اعتراف کرتے ہیں کیا اب (یہاں سے) نکلنے کی کوئی صورت ہے؟'),
(4145, 40, 12, '(جواب ملے گا کہ) یہ اس لئے ہے کہ جب (تمہیں) خدائے واحد و یکتا کی طرف بلایا جاتا تھا تو تم انکار کر دیتے تھے اور اگر کسی کو اس کا شریک بنایا جاتا تھا تو تم جان لیتے تھے اب فیصلہ اللہ کے اختیار میں ہے جو عالیشان ہے (اور) بزرگ ہے۔'),
(4146, 40, 13, 'وہ (اللہ) وہی ہے جو تمہیں اپنی (قدرت کی ) نشانیاں دکھاتا ہے اور تمہارے لئے آسمان سے رزق نازل کرتا ہے اور ان (نشانیوں) سے نصیحت صرف وہی حاصل کرتا ہے جو (اس کی طرف) رجوع کرتا ہے۔'),
(4147, 40, 14, 'پس اللہ ہی کو پکارو اس کے لئے دین کو (شرک و ریا سے) خالص کرتے ہوئے چاہے کافر ناپسند کریں۔'),
(4148, 40, 15, '(وہ) بلند درجوں والا (اور) عرش کا مالک ہے وہ اپنے بندوں میں سے جس پر چاہتا ہے اپنے حکم سے روح (وحی) کو نازل کرتا ہے تاکہ وہ بارگاہِ الٰہی میں حضوری والے دن سے ڈرائے۔'),
(4149, 40, 16, 'وہ دن جب لوگ ظاہر ہوں گے ان کی کوئی بات اللہ پر پوشیدہ نہ ہوگی (آواز آئے گی) آج کس کی حکومت ہے؟ (جواب ملے گا) صرف اللہ کی جو واحد و یکتا ہے اور ہر چیز پر غالب ہے۔'),
(4150, 40, 17, 'آج ہر شخص کو اس کے کئے کا بدلہ دیا جائے گا آج کچھ بھی ظلم نہ ہوگا بیشک اللہ جلد حساب لینے والا ہے۔'),
(4151, 40, 18, 'اور لوگوں کو اس دن (قیامت) سے ڈرائیے جو قریب آنے والا ہے جس دن دل (کھینچ کر) گلوں تک آجائیں گے (اور) وہ غم و غصہ سے بھرے ہوئے ہوں گے۔ ظالموں کیلئے کوئی (مخلص) دوست نہ ہوگا اور نہ کوئی ایسا سفارشی جس کی بات مانی جائے۔'),
(4152, 40, 19, 'وہ آنکھوں کی مجرمانہ جنبش کو جانتا ہے اور ان باتوں کو بھی جن کو سینے چھپاتے ہیں۔'),
(4153, 40, 20, 'اور اللہ حق کے ساتھ فیصلہ کرے گا اور جنہیں وہ لوگ اللہ کے سوا پکارتے ہیں وہ کسی چیز کا فیصلہ نہیں کر سکتے۔ بیشک اللہ بڑا سننے والا (اور) بڑا دیکھنے والا ہے۔'),
(4154, 40, 21, 'کیا یہ لوگ زمین پر چلتے پھرتے نہیں ہیں کہ دیکھیں کہ ان لوگوں کا انجام کیا ہوا جو ان سے پہلے تھے؟ قوت کے لحاظ سے اور زمین میں چھوڑے ہوئے آثار کے اعتبار سے ان سے زیادہ طاقتور تھے تو اللہ نے ان کے گناہوں کی پاداش میں ان کو پکڑا تو ان کو اللہ (کی گرفت) سے بچانے والا کوئی نہ تھا۔'),
(4155, 40, 22, '(ان کا یہ انجام) اس لئے ہوا کہ ان کے رسول(ع) ان کے پاس کھلی نشانیاں (معجزے) لے کر آتے تھے تو وہ کفر کرتے تھے۔ آخرکار اللہ نے انہیں پکڑ لیا بیشک وہ بڑا طاقتور (اور) سخت سزا دینے والا ہے۔'),
(4156, 40, 23, 'بیشک ہم نے موسیٰ(ع) کو فرعون، ہامان اور قارون کی طرف اپنی نشانیوں (معجزوں) اور کھلی ہوئی دلیلوں کے ساتھ بھیجا۔'),
(4157, 40, 24, 'تو انہوں نے کہا کہ یہ ایک جادوگر (اور) بڑا جھوٹا ہے۔'),
(4158, 40, 25, 'سو جب وہ ہماری طرف سے حق لے کر ان کے پاس آئے تو انہوں نے کہا کہ جو لوگ ان کے ساتھ ایمان لائے ہیں ان کے بیٹوں کو قتل کر دو اور ان کی عورتوں (لڑکیوں) کو زندہ چھوڑ دو۔ اور کافروں کی( ہر) تدبیر گمراہی میں (رائیگاں) ہے۔'),
(4159, 40, 26, 'اور فرعون نے کہا کہ مجھے چھوڑ دو۔ میں موسیٰ(ع) کو قتل کر دوں اور وہ (اپنی مدد کیلئے) اپنے پرورگار کو پکارے مجھے اندیشہ ہے کہ وہ کہیں تمہارا دین (شرک) نہ بدل دے۔ یا زمین میں فساد برپا نہ کر دے۔'),
(4160, 40, 27, 'اور موسیٰ(ع) نے کہا کہ میں اپنے اور تمہارے پروردگار سے پناہ مانگتا ہوں جو مجھے ہر اس متکبر (کے شر) سے بچائے جو روزِ حساب (قیامت) پر ایمان نہیں رکھتا۔'),
(4161, 40, 28, 'اور فرعون کے خاندان میں سے ایک مردِ مؤمن نے کہا جو اپنے ایمان کو چھپائے رکھتا تھا۔ کیا تم ایک شخص کو صرف اس بات پر قتل کرنا چاہتے ہو کہ وہ کہتا ہے کہ میرا پروردگار اللہ ہے؟ حالانکہ وہ تمہارے پروردگار کی طرف سے تمہارے پاس بیّنات (معجزات) لے کر آیا ہے اگر وہ جھوٹا ہے تو اس کے جھوٹ کا وبال اسی پر پڑے گا اور اگر وہ سچا ہے تو جس (عذاب) کی وہ تمہیں دھمکی دیتا ہے تو اس کا کچھ حصہ تو تمہیں پہنچ کر رہے گا۔ بیشک اللہ اس شخص کو ہدایت نہیں دیتا جو حد سے بڑھنے والا اور بڑا جھوٹا ہو۔'),
(4162, 40, 29, 'اے میری قوم! (بے شک) آج تمہاری حکومت ہے (اور) اس سر زمین میں تمہیں غلبہ بھی حاصل ہے لیکن اگر اللہ کا عذاب ہم پر آگیا تو کون ہماری مدد کرے گا؟ (اور اس سے ہمیں کون بچائے گا؟) (مؤمن کی یہ بات سن کر) فرعون نے کہا میں تمہیں وہی رائے دیتا ہوں۔ جو میں (درست) سمجھتا ہوں اور میں تمہاری راہنمائی صرف اسی راستہ کی طرف کرتا ہوں جو سیدھا ہے۔'),
(4163, 40, 30, 'اور جو شخص ایمان لایا تھا وہ کہنے لگا۔ اے میری قوم! مجھے تمہاری نسبت ڈر ہے کہ تم پر (تباہ شدہ) گروہوں جیسا دن نہ آجائے۔'),
(4164, 40, 31, 'جیسا حال قومِ نوح(ع) اور عاد و ثمود اور ان کے بعد والوں کا ہوا تھا اور اللہ تو (اپنے بندوں پر ظلم کا ارادہ (بھی) نہیں کرتا۔'),
(4165, 40, 32, 'اے میری قوم! میں تمہارے بارے میں چیخ و پکار والے دن سے ڈرتا ہوں۔'),
(4166, 40, 33, 'جس دن تم پیٹھ پھیر کر (دوزخ کی طرف) بھاگوگے تمہیں اللہ کے (عذاب) سے کوئی بچانے والا نہ ہوگا اور جس کو اللہ گمراہی میں چھوڑ دے اسے کوئی ہدایت کرنے والا نہیں ہوتا۔'),
(4167, 40, 34, 'اور (اے میری قوم) اس سے پہلے تمہارے پاس یوسف(ع) کھلی ہوئی دلیلیں (معجزات) لے کر آئے مگر تم برابر شک ہی میں پڑے رہے یہاں تک کہ جب وہ وفات پا گئے تو تم نے کہا (اب) اللہ ان کے بعد ہرگز کوئی رسول نہیں بھیجے گا۔ اسی طرح اللہ اس شخص کو گمراہی میں چھوڑتا ہے جو حد سے بڑھنے والا (اور) شک کرنے والا ہوتا ہے۔'),
(4168, 40, 35, 'جو لوگ آیاتِ الٰہی میں جھگڑے کرتے ہیں بغیر کسی دلیل کے جو ان کے پاس آئی ہو۔ یہ روش اللہ اور اہلِ ایمان کے نزدیک سخت مبغوض (اور ناپسندیدہ) ہے اللہ اسی طرح ہر منکر و سرکش کے دل پر مہر لگا دیتا ہے۔'),
(4169, 40, 36, 'اور فرعون نے کہا اے ہامان! میرے لئے ایک اونچا محل بنا (بنوا) تاکہ میں (اس پر چڑھ کر) راستوں تک پہنچ جاؤں۔'),
(4170, 40, 37, 'یعنی آسمانوں کے راستوں تک (اور پھر وہاں سے) موسیٰ(ع) کے خدا کو جھانک کر دیکھوں اور میں تو اسے جھوٹا سمجھتا ہوں اور اسی طرح فرعون کیلئے اس کا بدعمل خوشنما بنا دیا گیا اور وہ (سیدھے) راستہ سے روک دیا گیا اور فرعون کی ہر تدبیر (چالبازی) غارت ہی گئی (اور اس کی تباہی پر منتج ہوئی)۔'),
(4171, 40, 38, 'اور جو شخص ایمان لا چکا تھا اس نے کہا اے میری قوم! میری پیروی کرو۔ میں تمہیں سیدھا راستہ دکھاؤں گا۔'),
(4172, 40, 39, 'اے میری قوم! یہ دنیا کی زندگی محض (چند روزہ) فائدہ ہے اور مستقل قیام گاہ تو آخرت ہی ہے۔'),
(4173, 40, 40, 'کوئی برائی کرتا ہے تو اسے بدلہ بھی اس کے برابر ملے گا اور جو کوئی نیک کام کرتا ہے خواہ مرد ہو یا عورت بشرطیکہ وہ مؤمن ہو تو یہ بہشت میں داخل ہوں گے جہاں انہیں بے حساب رزق دیا جائے گا۔'),
(4174, 40, 41, 'اور اے میری قوم! (یہ کیا ہے؟) میں تمہیں نجات کی طرف بلاتا ہوں اور تم مجھے آتشِ دوزخ کی طرف بلاتے ہو۔'),
(4175, 40, 42, 'تم مجھے دعوت دیتے ہو کہ میں اللہ کا انکار کروں اور ایسی چیز کو اس کا شریک بناؤں جس کا مجھے علم نہیں ہے اور میں تمہیں اس خدا کی طرف بلاتا ہوں جو (ہر چیز پر) غالب ہے (اور) بڑا بخشنے والا ہے۔'),
(4176, 40, 43, 'یقیناً تم مجھے ایسی چیز کی دعوت دیتے ہو جو نہ دنیا میں کارآمد ہے اور نہ آخرت میں اور ہم سب کی بازگشت خدا ہی کی طرف سے اور جو حد سے بڑھنے والے ہیں وہی جہنمی ہیں۔'),
(4177, 40, 44, 'تم عنقریب یاد کروگے جو میں (آج) کہہ رہا ہوں اور میں اپنا معاملہ اللہ کے سپرد کرتا ہوں بے شک وہ (اپنے) بندوں کا خوب نگران ہے۔'),
(4178, 40, 45, 'پس اللہ نے اس (مردِ مؤمن) کو ان لوگوں کی تدبیروں اور چالوں کی برائیوں سے بچا لیا اور فرعون والوں کو برے عذاب نے گھیر لیا۔'),
(4179, 40, 46, '(اور اب تو) دوزخ کی آگ ہے جس پر انہیں صبح و شام پیش کیا جا رہا ہے اور جس دن قیامت قائم ہوگی (حکم ہوگا) فرعون والوں کو سخت ترین عذاب میں داخل کرو۔'),
(4180, 40, 47, 'اور وہ وقت یاد کرو جب یہ لوگ جہنم میں ایک دوسرے سے جھگڑیں گے تو کمزور لوگ بڑا بننے والوں سے کہیں گے کہ ہم تو تمہارے تابع تھے کیا تم آگ کا کچھ حصہ ہم سے ہٹا سکتے ہو؟'),
(4181, 40, 48, 'وہ بڑا بننے والے کہیں گے ہم سب اسی میں پڑے ہیں اور اللہ نے اپنے بندوں کے درمیان فیصلہ کر دیا ہے۔'),
(4182, 40, 49, 'اور جو لوگ دوزخ میں ہوں گے وہ جہنم کے داروغوں سے کہیں گے کہ اپنے پروردگار سے دعا کرو کہ ہمارے عذاب میں ایک دن کی ہی تخفیف کر دے۔'),
(4183, 40, 50, 'وہ (جواب میں کہیں گے) کیا تمہارے پاس کھلی ہوئی دلیلیں لے کر تمہارے رسول(ع) نہیں آتے رہے تھے؟ وہ کہیں گے کہ ہاں اس پر داروغے کہیں گے کہ تم خود ہی دعا مانگو اور کافروں کی دعا و پکار بالکل گم شدہ ہے (اکارت جانے والی ہے)۔'),
(4184, 40, 51, 'بے شک ہم اپنے رسولوں(ع) کی اور ایمان والوں کی اس دنیاوی زندگی میں بھی مدد کرتے رہتے ہیں اور اس دن بھی کریں گے جب گواہ (گواہی دینے کیلئے) کھڑے ہوں گے۔'),
(4185, 40, 52, 'جس دن ظالموں کو ان کی معذرت کوئی فائدہ نہ دے گی اور ان کیلئے لعنت ہوگی ان کیلئے (دوزخ کا) بہت برا گھر ہوگا۔'),
(4186, 40, 53, 'اور بالیقین ہم نے موسیٰ(ع) کو ہدایت عطا فرمائی اور بنی اسرائیل کو ایسی کتاب (توراۃ) کا وارث بنایا۔'),
(4187, 40, 54, 'جو صاحبانِ عقل کیلئے سراپا ہدایت اور نصیحت تھی۔'),
(4188, 40, 55, 'آپ صبر (و ثبات) سے کام لیجئے! اللہ کا وعدہ برحق ہے اور (تہمت) گناہ پر استغفار کرتے رہیے اور صبح و شام اپنے پروردگار کی حمد کے ساتھ اس کی تسبیح کیجئے۔'),
(4189, 40, 56, 'یقیناً جو لوگ اللہ کی آیتوں کے بارے میں (آپ(ص) سے) جھگڑتے ہیں بغیر کسی سند کے جو ان کے پاس آئی ہو ان کے سینوں میں تکبر (بڑائی) کے سوا اور کچھ نہیں ہے جس تک وہ کبھی پہنچنے والے نہیں ہیں آپ(ص) اللہ کی پناہ مانگئے! بے شک وہ بڑا سننے (اور) بڑا دیکھنے والا ہے۔'),
(4190, 40, 57, 'بے شک آسمانوں اور زمین کا پیدا کرنا انسانوں کے پیدا کرنے سے زیادہ بڑا کام ہے لیکن اکثر لوگ جانتے نہیں ہیں۔'),
(4191, 40, 58, 'اور اندھا اور آنکھ والا برابر نہیں ہو سکتے اور اسی طرح ایماندار اور نیکوکار اور بدکار بھی یکساں نہیں ہو سکتے مگر تم لوگ بہت کم نصیحت قبول کرتے ہو۔'),
(4192, 40, 59, 'یقیناً قیامت آنے والی ہے اس میں کوئی شک نہیں ہے مگر اکثر لوگ نہیں مانتے۔'),
(4193, 40, 60, 'اور تمہارا پروردگار کہتا ہے کہ تم مجھ سے دعا کرو۔ میں تمہاری دعا قبول کروں گا اور جو لوگ میری (اس) عبادت سے تکبر کرتے ہیں وہ عنقریب ذلیل و خوار ہوکر جہنم میں داخل ہوں گے۔'),
(4194, 40, 61, 'وہ اللہ ہی ہے جس نے تمہارے لئے رات بنائی تاکہ تم اس میں آرام (اور سکون حاصل) کرو اور دن کو روشن بنایا (تاکہ تم اس میں کام کرو) بے شک اللہ لوگوں پر بڑا فضل و کرم کرنے والا ہے مگر اکثر لوگ شکر ادا نہیں کرتے۔'),
(4195, 40, 62, 'یہی اللہ تمہارا پروردگار ہے جو ہر چیز کا پیدا کرنے والا ہے اس کے سوا کوئی الٰہ (خدا) نہیں ہے تم کدھر بہکائے جا رہے ہو؟'),
(4196, 40, 63, 'اسی طرح وہ لوگ (بھی) بہکائے جاتے رہے ہیں جو اللہ کی آیتوں کا انکار کرتے تھے۔'),
(4197, 40, 64, 'وہ اللہ ہی ہے جس نے زمین کو تمہارے لئے قرارگاہ اور آسمان کو عمارت (چھت) بنایا اور تمہاری صورت گری کی اور تمہاری صورتوں کو حسین (و جمیل) بنایا اور تمہیں کھانے کیلئے پاکیزہ غذائیں عطا کیں یہی اللہ تمہارا پروردگار ہے پس بڑا ہی بابرکت الٰہ ہے جو سارے جہانوں کا پروردگار ہے۔'),
(4198, 40, 65, 'وہی (حقیقی) زندہ ہے اس کے سوا کوئی الٰہ (خدا) نہیں ہے سو اسی کو پکارو دین کو اس کیلئے خالص کرتے ہوئے۔ ہر قسم کی تعریف اللہ کیلئے ہے جو تمام جہانوں کا پروردگار ہے۔'),
(4199, 40, 66, 'آپ(ص) کہہ دیجئے! کہ مجھے منع کر دیا گیا ہے کہ میں ان کی عبادت کروں جنہیں تم اللہ کو چھوڑ کر پکارتے ہو جبکہ میرے پروردگار کی طرف سے میرے پاس کھلی ہوئی دلیلیں آچکی ہیں اور مجھے حکم دیا گیا ہے کہ میں رب العالمین کے سامنے اپنا سر تسلیم خم کر دوں (اس کا فرمانبردار بندہ بنوں)۔'),
(4200, 40, 67, 'اللہ وہی ہے جس نے تمہیں مٹی سے پیدا کیا پھر نطفہ سے پھر خون کے لوتھڑے سے پھر تمہیں (شکمِ مادر سے) بچہ بنا کر نکالا۔ پھر (تمہاری پرورش کی) تاکہ تم اپنی پوری طاقت (جوانی) کو پہنچو (پھر زندہ رکھتا ہے) تاکہ تم بوڑھے ہو جاؤ اور تم میں سے کوئی پہلے اٹھا لیا جاتا ہے (یہ سب کچھ اس لئے ہے کہ تم مقررہ وقت تک پہنچ جاؤ اور عقل سے کام لو (خدا کی قدرت و حکمت کو سمجھو)۔'),
(4201, 40, 68, 'وہ وہی ہے جو زندہ کرتا ہے اور مارتا ہے وہ جب کسی کام کا فیصلہ کر لیتا ہے تو اس سے کہتا ہے کہ ہو جا تو وہ ہو جاتا ہے۔'),
(4202, 40, 69, 'کیا تم نے ان لوگوں کو نہیں دیکھا جو اللہ کی آیتوں کے بارے میں جھگڑتے رہتے ہیں وہ کدھر پھرائے جا رہے ہیں؟'),
(4203, 40, 70, 'جن لوگوں نے اس کتاب (قرآنِ مجید) کو جھٹلایا اور اس (پیغام) کو بھی جس کے ساتھ ہم نے اپنے پیغمبروں کو بھیجا تو انہیں بہت جلد (اس کا انجام) معلوم ہو جائے گا۔'),
(4204, 40, 71, 'جب طوق ان کی گردنوں میں ہوں گے اور (پاؤں میں) زنجیریں ان کو گھسیٹتے ہوئے لایا جائے گا'),
(4205, 40, 72, 'کھولتے ہوئے پانی میں۔ پھر آتشِ دوزخ میں جھونک دیئے جائیں گے۔'),
(4206, 40, 73, 'پھر ان سے کہا جائے گا (آج) وہ کہاں ہیں جنہیں تم اللہ کو چھوڑ کر شریک مانتے تھے؟'),
(4207, 40, 74, 'وہ کہیں گے وہ تو ہم سے غائب ہوگئے بلکہ ہم تو اس سے پہلے (خدا کے سوا) کسی چیز کو پکارتے ہی نہ تھے خدا اسی طرح کافروں کو گمراہی میں چھوڑ دیتا ہے۔'),
(4208, 40, 75, 'یہ اس لئے ہے کہ تم زمین میں ناحق خوش ہوا کرتے تھے اور (اپنے مال و اقتدار پر) اِترایا کرتے تھے۔'),
(4209, 40, 76, '(اب تم جہنم کے دروازوں میں داخل ہو جاؤ اس میں ہمیشہ پڑے رہنے کیلئے۔ پس کیا برا ٹھکانہ ہے تکبر کرنے والوں کا۔'),
(4210, 40, 77, 'آپ(ص) صبر کیجئے یقیناً اللہ کا وعدہ برحق ہے پھر جس (عذاب) کی ہم انہیں دھمکی دیتے رہے ہیں خواہ اس کا کچھ حصہ آپ کو (آنکھوں سے) دکھا دیں یا اس سے پہلے آپ کو دنیا سے اٹھا لیں۔ بہرحال ان سب کی بازگشت تو ہماری طرف ہی ہے (وہ اس سے بچ نہیں سکتے)۔'),
(4211, 40, 78, 'ہم نے آپ(ص) سے پہلے بہت سے رسول بھیجے جن میں سے بعض کے حالات آپ سے بیان کر دیئے ہیں اور بعض ایسے ہیں کہ جن کے حالات آپ سے بیان نہیں کئے اور کسی رسول کیلئے یہ ممکن نہ تھا کہ اللہ کے اذن (حکم) کے بغیر کوئی معجزہ پیش کرے اور جب اللہ کا حکم آجائے گا تو حق کے ساتھ فیصلہ کر دیا جائے گا اور اس وقت باطل والے خسارے میں رہیں گے۔'),
(4212, 40, 79, 'اللہ وہی ہے جس نے تمہارے لئے چوپائے بنائے ہیں تاکہ تم ان میں سے بعض پر سوار ہو اور بعض کا (گوشت) کھاؤ۔'),
(4213, 40, 80, 'اور تمہارے لئے ان میں اور بھی فائدے ہیں اور تاکہ تم ان پر سوار ہو کر اپنے دلوں کی حاجت تک پہنچ سکو اور تم ان پر اور کشتیوں پر سوار کئے جاتے ہو۔'),
(4214, 40, 81, 'اور وہ (اللہ) تمہیں اپنی (قدرت و حکمت کی) نشانیاں دکھاتا ہے سو تم اللہ کی کن کن نشانیوں کا انکار کروگے؟'),
(4215, 40, 82, 'کیا وہ زمین میں چلتے پھرتے نہیں کہ ان لوگوں کا انجام دیکھتے جو ان سے پہلے گزرے ہیں جو تعداد میں ان سے زیادہ تھے اور طاقت میں اور زمین میں اپنی چھوڑی ہوئی نشانیوں کے اعتبار سے ان سے بڑھے ہوئے تھے لیکن ان کی سب کمائی کچھ ان کے کام نہ آئی۔'),
(4216, 40, 83, 'جب ان کے رسول ان کے پاس بیّنات (معجزات) لے کر آئے تو وہ اپنے اس علم پر نازاں و فرحاں رہے جو ان کے پاس تھا اور (انجامِ کار) اسی (عذاب) نے انہیں گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(4217, 40, 84, 'پھر جب انہوں نے ہمارا عذاب کو دیکھا تو کہنے لگے کہ ہم خدائے واحد و یکتا پر ایمان لاتے ہیں اور ان سب چیزوں کا انکار کرتے ہیں جنہیں ہم اس کے ساتھ شریک ٹھہراتے تھے۔'),
(4218, 40, 85, 'پس ہمارا عذاب دیکھ لینے کے بعد ان کے ایمان نے انہیں کوئی فائدہ نہ پہنچایا۔ یہی اللہ کا طریقہ ہے جو (ہمیشہ سے) اس کے بندوں میں جاری ہے اور اس وقت کافر لوگ خسارے میں رہ گئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حا۔ میم۔'),
(4220, 41, 2, 'یہ (قرآن) رحمٰن و رحیم خدا کی طرف سے نازل کردہ ہے۔'),
(4221, 41, 3, 'یہ ایسی کتاب ہے جس کی آیتیں تفصیل سے بیان کر دی گئی ہیں ان لوگوں کیلئے جو علم رکھتے ہیں عربی زبان کے قرآن کی صورت میں۔'),
(4222, 41, 4, 'یہ (قرآن) خوشخبری دینے والا اور ڈرانے والا ہے مگر اکثر لوگوں نے اس سے روگردانی کی ہے۔ پس وہ سنتے ہی نہیں ہیں۔'),
(4223, 41, 5, 'اور وہ کہتے ہیں کہ آپ(ص) جس چیز کی طرف ہمیں دعوت دیتے ہیں اس سے ہمارے دلوں پر غلاف چڑھے ہوئے ہیں اور ہمارے کانوں میں گرانی ہے اور ہمارے تمہارے درمیان پردہ حائل ہے آپ اپنا کام کیجئے ہم اپنا کام کر رہے ہیں۔'),
(4224, 41, 6, 'آپ(ص) کہہ دیجئے! کہ میں بھی تمہاری ہی طرح بشر ہوں (مگر فرق یہ ہے) کہ مجھے وحی کی جاتی ہے کہ تمہارا الٰہ (خدا) بس ایک ہی الٰہ (خدا) ہے تو تم سیدھے اسی کی طرف رخ رکھو اور اس سے مغفرت طلب کرو اور بربادی ہے ان مشرکوں کیلئے۔'),
(4225, 41, 7, 'جو زکوٰۃ ادا نہیں کرتے اور وہ قیامت کے منکر ہیں۔'),
(4226, 41, 8, 'جو لوگ ایمان لائے اور نیک عمل کئے ان کیلئے وہ اجر ہے جو ختم ہونے والا نہیں ہے۔'),
(4227, 41, 9, 'آپ(ص) کہہ دیجئے! کیا تم اس ذات کا انکار کرتے ہو جس نے زمین کو پیدا کیا اور دوسروں کو اس کا ہمسر قرار دیتے ہو؟ وہی تو سارے جہانوں کا پروردگار ہے۔'),
(4228, 41, 10, 'اور اسی نے زمین میں اس کے اوپر پہاڑ بنائے اور اس میں برکت رکھ دی اور اس میں تمام طلبگاروں کی ضرورت کے برابر چار دن کی مدت میں صحیح انداز سے سامانِ معیشت مہیا کر دیا۔'),
(4229, 41, 11, 'پھر وہ آسمان کی طرف متوجہ ہوا جبکہ وہ دھوئیں کی شکل میں تھا اور اس سے اور زمین سے کہا کہ تم خوشی سے آؤ یا نخوشی سے تو دونوں نے کہا ہم خوشی خوشی آتے ہیں۔'),
(4230, 41, 12, 'پس اس (اللہ) نے انہیں دو دن میں سات آسمان بنا دیا اور ہر آسمان میں اس کے معاملہ کی وحی کر دی اور ہم نے آسمان دنیا کو چراغوں (ستاروں) سے زینت بخشی اور اسے محفوظ کر دیا یہ اس (خدا) کی منصوبہ بندی ہے جو (ہر چیز پر) غالب ہے اور بڑا جاننے والا ہے۔'),
(4231, 41, 13, 'پس اگر یہ لوگ روگردانی کریں تو آپ(ص) کہہ دیجئے! کہ میں نے تو تمہیں اس (آسمانی) کڑک سے ڈرا دیا ہے جو عاد والی کڑک کی مانند ہے۔'),
(4232, 41, 14, 'جبکہ ان کے پاس (اللہ کے) رسول(ع) ان کے آگے اور ان کے پیچھے سے آئے (اس پیغام کے ساتھ) کہ اللہ کے سوا کسی کی عبادت نہ کرو۔ مگر انہوں نے (جواب میں) کہا کہ اگر ہمارا پروردگار چاہتا تو (ہماری طرف) فرشتے نازل کرتا۔ لہٰذا جس (پیغام) کے ساتھ تم بھیجے گئے ہو ہم اس کا انکار کرتے ہیں۔'),
(4233, 41, 15, 'سو قومِ عاد نے زمین میں ناحق سرکشی کی اور کہا کہ ہم سے بڑھ کر کون طاقتور ہے؟ کیا انہوں نے یہ نہ دیکھا کہ (اور نہ یہ سوچا) کہ جس نے انہیں پیدا کیا ہے کہ وہ ان سے بڑھ کر طاقتور ہے وہ (ہمیشہ) ہماری آیتوں کا انکار کرتے رہے۔'),
(4234, 41, 16, 'سو ہم نے ان پر خاص منحوس دنوں میں تیز و تند (طوفانی) ہوا بھیجی تاکہ ہم انہیں زندگانئِ دنیا میں ذلت آمیز عذاب کا مزہ چکھائیں اور آخرت کا عذاب تو زیادہ رسوا کن ہوگا اور ان کی کوئی مدد نہیں کی جائے گی۔'),
(4235, 41, 17, 'رہے ثمود! ہم نے ان کی راہنمائی کی مگر انہوں نے ہدایت پر اندھے پن (گمراہی) کو ترجیح دی۔ پس ان کو ذلت کے عذاب کی کڑک نے پکڑ لیا ان کے کرتوتوں کی پاداش میں جو وہ کیا کرتے تھے۔'),
(4236, 41, 18, 'اور ہم نے ان لوگوں کو نجات دی جو ایمان لاتے تھے اور (ہماری نافرمانی سے) ڈرتے تھے۔'),
(4237, 41, 19, 'اور جس دن اللہ کے دشمن دوزخ کی طرف جمع کئے جائیں گے اور پھر ترتیب وار کھڑے کئے جائیں گے (یا پھر روکے جائیں گے)۔'),
(4238, 41, 20, 'یہاں تک کہ جب وہ وہاں پہنچ جائیں تو ان کے کان اور ان کی آنکھیں اور ان کی کھالیں ان کے برخلاف ان کے اعمال کی گواہی دیں گی۔'),
(4239, 41, 21, 'اور وہ اپنی کھالوں سے کہیں گے کہ تم نے ہمارے خلاف گواہی کیوں دی؟ تو وہ کہیں گی کہ ہمیں اسی اللہ نے گویائی عطا کی ہے جس نے ہر چیز کو گویائی دی اور اسی نے تمہیں پہلی مرتبہ پیدا کیا تھا اور پھر اسی کی طرف تمہاری بازگشت ہوگی۔'),
(4240, 41, 22, 'اور تم (گناہ کرتے وقت) اپنے آپ کو اس بات سے چھپا ہی نہیں سکتے تھے کہ تمہارے کان، تمہاری آنکھیں اور تمہاری کھالیں تمہاری خلاف گواہی دیں لیکن تم نے یہ گمان کیا کہ اللہ تمہارے بہت سے اعمال کو نہیں جانتا جو تم کرتے ہو۔'),
(4241, 41, 23, 'تمہارے اسی گمان نے جو تم نے اپنے پروردگار کی نسبت کیا تھا تمہیں تباہ کر دیا اور تم خسارہ اٹھانے والوں میں سے ہوگئے۔'),
(4242, 41, 24, 'پس اب اگر وہ صبر کریں بھی تو ان کا ٹھکانا دوزخ ہے اور اگر وہ (خدا کو) راضی کرنا چاہیں تو وہ ان لوگوں میں سے نہیں ہوں گے جن پر (اللہ) راضی ہوا۔'),
(4243, 41, 25, 'اور ہم نے ان کے لئے کچھ ساتھی مقرر کر دیئے ہیں جنہوں نے ان کے اگلے اور پچھلے (کرتوتوں کو) خوشنما بنا دیا اور ان پر وہی بات ثابت ہوگئی (عذابِ الٰہی) جو جِنوں اور انسانوں کے ان گروہوں پر ثابت ہے جو ان سے پہلے گزر چکے تھے بے شک وہ سب نقصان اٹھانے والوں میں سے تھے۔'),
(4244, 41, 26, 'اور کافر کہتے ہیں کہ اس قرآن کو نہ سنو اور اس کی (تلاوت کے دوران) شوروغل مچا دیا کرو شاید اس طرح تم غالب آجاؤ۔'),
(4245, 41, 27, 'ہم ان کافروں کو ضرور سخت عذاب کا مزہ چکھائیں گے اور وہ بدترین عمل کرتے ہیں ہم انہیں ضرور ان کی بدترین سزا دیں گے۔'),
(4246, 41, 28, 'یہ دشمنانِ خدا کی سزا ہے یعنی دوزخ۔ ان کیلئے اسی میں ہمیشہ ہمیشہ رہنے کا گھر ہے یہ ان کے اس جرم کی سزا ہے کہ وہ ہماری آیتوں کا انکار کیا کرتے تھے۔'),
(4247, 41, 29, 'اور کافر لوگ کہیں گے کہ اے ہمارے پروردگار! ہمیں دونوں قسم یعنی جنوں اور انسانوں میں سے وہ (شیطان) دکھا جنہوں نے ہمیں گمراہ کیا تھا تاکہ ہم انہیں اپنے پاؤں کے نیچے روندیں تاکہ وہ پست ترین اشخاص سے ہو جائیں (ذلیل و خوار ہوں)۔'),
(4248, 41, 30, 'بے شک جن لوگوں نے کہا کہ ہمارا پروردگار اللہ ہے اور پھر اس پر قائم اور ثابت قدم رہے ان پر فرشتے نازل ہوتے ہیں (ان سے کہتے ہیں کہ) تم نہ ڈرو اور نہ غم کرو اور اس جنت کی بشارت پر خوش ہو جاؤ جس کا تم سے وعدہ کیا گیا ہے۔'),
(4249, 41, 31, 'ہم زندگانئِ دنیا میں بھی تمہارے ساتھی ہیں اور آخرت میں بھی اور وہاں تمہارے لئے وہ سب کچھ ہے جو تمہارے دل چاہیں گے اور اس میں ہر چیز موجود ہے جو تم طلب کروگے۔'),
(4250, 41, 32, '(یہ سب کچھ) غفور و رحیم پروردگار کی طرف سے مہمانی کے طور پر ہے۔'),
(4251, 41, 33, 'اور اس شخص سے بہتر بات کس کی ہوگی جو اللہ کی طرف بلائے اور نیک عمل بجا لائے اور کہے کہ میں مسلمانوں (خدا کے فرمانبردار بندوں) میں سے ہوں۔'),
(4252, 41, 34, 'اور بھلائی اور برائی برابر نہیں ہیں آپ(ص) (بدی کا) احسن طریقہ سے دفعیہ کریں تو آپ دیکھیں گے کہ آپ میں اور جس میں دشمنی تھی وہ گویا آپ کا جگری دوست بن گیا۔'),
(4253, 41, 35, 'اور یہ صفت انہی لوگوں کو نصیب ہوتی ہے جو صبر کرتے ہیں اور یہ طریقۂ کار انہی کو سکھایا جاتا ہے جو بڑے نصیب والے ہوتے ہیں۔'),
(4254, 41, 36, 'اور (اے مخاطب) اگر تمہیں شیطان کی طرف سے کوئی وسوسہ محسوس ہو تو اس سے اللہ کی پناہ مانگ۔ بے شک وہ بڑا سننے والا (اور) بڑا جاننے والا ہے۔'),
(4255, 41, 37, 'اور اللہ کی نشانیوں میں رات، دن، سورج اور چاند بھی ہیں تم نہ سورج کو سجدہ کرو اور نہ چاند کو (بلکہ) اس اللہ کو سجدہ کرو جس نے انہیں پیدا کیا ہے اگر تم اسی کی عبادت کرتے ہو (اور اسی کو مستحقِ عبادت سمجھتے ہو)۔'),
(4256, 41, 38, 'اور اگر یہ لوگ تکبر کریں (اور قبولِ حق سے انکار کریں) تو جو فرشتے آپ کے پروردگار کے پاس ہیں وہ رات دن اس کی تسبیح (و تقدیس) کرتے ہیں اور وہ تھکتے نہیں ہیں۔'),
(4257, 41, 39, 'اور اس کی نشانیوں میں سے ایک یہ بھی ہے کہ تم دیکھتے ہو کہ زمین جمود اور افسردگی کی حالت میں پڑی ہے اور جب ہم اس پر پانی برساتے ہیں تو جنبش میں آتی ہے (ابھرتی ہے) اور اس میں بالیدگی آجاتی ہے (پھول جاتی ہے) بیشک جس نے اسے زندہ کیا ہے وہی مُردوں کو زندہ کرنے والا ہے بلاشبہ وہ ہر چیز پر بڑی قدرت رکھنے والا ہے۔'),
(4258, 41, 40, 'بے شک وہ لوگ جو ہماری آیتوں میں کجروی کرتے ہیں (اور انہیں الٹے معنی پہناتے ہیں) وہ ہم پر پوشیدہ نہیں ہیں بھلا وہ شخص جو دوزخ میں ڈالا جائے وہ بہتر ہے یا وہ جو قیامت کے دن امن و اطمینان کے ساتھ آئے؟ تم جو چاہو کرو جو کچھ تم کرتے ہو اللہ اسے خوب دیکھ رہا ہے۔'),
(4259, 41, 41, 'جن لوگوں نے ذکر (قرآن) کا انکار کیا جب وہ ان کے پاس آیا حالانکہ وہ ایک زبردست (معزز) کتاب ہے۔'),
(4260, 41, 42, 'باطل کا اس کے پاس گزر نہیں ہے وہ نہ اس کے آگے سے آسکتا ہے اور نہ پیچھے سے یہ اس ذات کی طرف سے نازل شدہ ہے جو بڑی حکمت والی ہے (اور) قابلِ ستائش ہے۔'),
(4261, 41, 43, '(اے رسول(ص)) آپ سے وہی کچھ کہا جا رہا ہے جو آپ سے پہلے گزرے ہوئے رسولوں(ع) سے کہا گیا ہے بیشک آپ کا پروردگار بڑا بخشش والا بھی ہے اور بڑے دردناک عذاب والا بھی۔'),
(4262, 41, 44, 'اگر ہم اس (قرآن) کو عجمی زبان میں بنا کر نازل کرتے تو یہ لوگ کہتے کہ اس کی آیتیں صاف صاف (کھول کر) کیوں بیان نہیں کی گئیں؟ آپ(ص) کہہ دیجئے کہ یہ ایمان لانے والوں کیلئے ہدایت اور شفا ہے اور جو ایمان نہیں لاتے ان کے کانوں میں گرانی (بہرہ پن) ہے اور وہ ان کے حق میں نابینائی ہے اور یہ لوگ (بہرہ پن کی وجہ سے گویا) بہت دور راستہ کی جگہ سے پکارے جا رہے ہیں۔'),
(4263, 41, 45, 'اور ہم ہی نے موسیٰ(ع) کو کتاب (توراۃ) عطا کی تھی تو اس میں اختلاف کیا گیا ہے اور اگر تمہارے پروردگار کی طرف سے ایک بات پہلے طے نہ ہو چکی ہوتی تو ان (اختلاف کرنے والوں) میں فیصلہ کر دیا جاتا اور بیشک وہ ایک تردد انگیز شک میں پڑے ہوئے ہیں۔'),
(4264, 41, 46, 'جو کوئی نیک کام کرتا ہے وہ اپنے ہی لئے کرتا ہے اور جو شخص برائی کرتا ہے تو اس کا وبال بھی اسی پر ہے اور آپ(ص) کا پروردگار بندوں پر ظلم کرنے والا نہیں ہے۔'),
(4265, 41, 47, 'ساعت (قیامت) کا علم اسی (اللہ) کی طرف لوٹایا جاتا ہے اور کوئی پھل اپنے غلافوں سے نہیں نکلتا اور نہ کوئی مادہ حاملہ ہوتی ہے اور نہ بچہ جنتی ہے مگر اسی (اللہ) کے علم کے ساتھ اور جس دن وہ (اللہ) ان (مشرکین) کو پکارے گا (اور پوچھے گا کہ)میرے شریک کہاں ہیں؟ وہ کہیں گے کہ ہم تجھے بتا چکے ہیں (عرض کر چکے ہیں) کہ ہم میں سے کوئی بھی اس کی گواہی دینے والا نہیں ہے(اور نہ کوئی ہے)۔'),
(4266, 41, 48, 'اور جن کو وہ پہلے پکارا کرتے تھے وہ سب (اس دن) گم ہو جائیں گے اور وہ سمجھ جائیں گے کہ اب ان کے لئے چھٹکارا نہیں ہے۔'),
(4267, 41, 49, 'انسان بھلائی کی دعا کرتے ہوئے نہیں تھکتا اور اگر اسے کوئی تکلیف پہنچ جائے تو ایکدم بالکل مایوس و ناامید ہو جاتا ہے۔'),
(4268, 41, 50, 'اور اگر ہم اسے اس تکلیف کے بعد جو اسے پہنچتی تھی اپنی رحمت کامزہ چکھا دیتے ہیں تو وہ کہتا ہے یہ تو میرے ہی لئے ہے (میں اس کامستحق ہوں) نیز کہتا ہے کہ میں خیال نہیں کرتا کہ قیامت قائم ہوگی اور اگر میں اپنے پروردگار کی طرف کبھی لوٹایا بھی گیا تو یقیناً میرے لئے اس کے ہاں بھی بہتری ہوگی ہم کافروں کو ضرور ان کے ان اعمال سے آگاہ کریں گے جو وہ کرتے رہے اور انہیں سخت عذاب کامزہ چکھائیں گے۔'),
(4269, 41, 51, 'اور جب ہم انسان کو کوئی کچھ نعمت عطا کرتے ہیں تو وہ (تکبر سے)منہ پھیر لیتا ہے اور پہلو تہی کرنے لگتا ہے اورجب اسے کوئی تکلیف پہنچتی ہے تو پھر لمبی چوڑی دعائیں کرنے لگتا ہے۔'),
(4270, 41, 52, 'آپ(ص) کہہ دیجئے! اے کافرو! تم(غور کر کے)مجھے بتاؤ کہ اگر یہ (قرآن) اللہ کی طرف سے ہے پھر تم نے اس کا انکار کیا تو اس سے بڑھ کر کون گمراہ ہے جو مخالفت میں بہت دور نکل جائے؟'),
(4271, 41, 53, 'ہم انہیں نشانیاں دکھائیں گے آفاق و کائنات میں بھی اور خود ان کی ذات میں بھی تاکہ ان پر واضح ہو جائے کہ وہ (اللہ) بالکل حق ہے۔ کیا تمہارے پروردگار کے لئے یہ امر کافی نہیں ہے کہ وہ ہر چیز پر شاہد ہے۔'),
(4272, 41, 54, 'یاد رکھو۔ یہ لوگ اپنے پروردگارکی بارگاہ میں حاضری و حضوری کے بارے میں شک میں مبتلا ہیں آگاہ ہو جاؤ کہ وہ (اللہ) ہر چیز کااحاطہ کئے ہوئے ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حا۔ میم۔'),
(4274, 42, 2, 'عین۔ سین۔ قاف۔'),
(4275, 42, 3, '(اے رسول(ص)) اسی طرح اللہ غالب اور بڑا حکمت والا ہے آپ(ص) کی طرف اور آپ(ص) سے پہلے گزرے ہوئے رسولوں(ع) کی طرف وحی کرتا رہا ہے۔'),
(4276, 42, 4, 'جو کچھ آسمانوں میں ہے اور زمین میں ہے وہ اسی (اللہ) کا ہے وہ بلند و برتر اور عظیم الشان ہے۔'),
(4277, 42, 5, 'قریب ہے کہ آسمان اپنے اوپر سے شگافتہ ہوجائیں اور فرشتے اپنے پروردگار کی حمد و تعریف کے ساتھ اس کی تسبیح کرتے ہیں اور اہلِ زمین کیلئے مغفرت طلب کرتے ہیں۔آگاہ رہو کہ اللہ یقیناً بڑابخشنے والا (اور) بڑا مہربان ہے۔'),
(4278, 42, 6, 'اور جن لوگوں نے اس کے سوا سرپرست (اور کارساز) بنا رکھے ہیں اللہ ان سب پر نگہبان ہے اور آپ ان کے ذمہ دار نہیں ہیں۔'),
(4279, 42, 7, 'اور اسی طرح ہم نے وحی کے ذریعہ یہ قرآن عربی زبان میں آپ کی طرف بھیجا ہے تاکہ آپ اہلِ مکہ اور اس کے گِردو پیش رہنے والوں کو ڈرائیں اورجمع ہو نے کے دن (قیامت) سے ڈرائیں جس (کے آنے) میں کوئی شک نہیں ہے (اس دن) ایک گروہ جنت میں جائے گااور ایک گروہ دوزخ میں۔'),
(4280, 42, 8, 'اور اگر اللہ (زبردستی) چاہتا تو سب کو ایک امت بنا دیتا۔ لیکن وہ جسے چاہتا ہے اپنی رحمت میں داخل کرتا ہے اور جو ظالم ہیں ان کا کوئی سرپرست اور مددگار نہیں ہے۔'),
(4281, 42, 9, 'کیا ان لوگوں نے اللہ کو چھوڑ کر اور سرپرست (کارساز) بنا لئے ہیں (حالانکہ) حقیقی سرپرست (اور کارساز) تواللہ ہی ہے اور وہی مُردوں کو زندہ کرتا ہے اور وہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(4282, 42, 10, 'اور جس چیز کے بارے میں تمہارے درمیان اختلاف پیدا ہو تو اس کا فیصلہ اللہ کے حوالے ہے۔ یہی اللہ میرا پروردگار ہے جس پرمیں توکل کرتا ہوں اور اسی کی طرف رجوع کرتا ہوں۔'),
(4283, 42, 11, '(وہ) آسمانوں اور زمین کا پیدا کرنے والا ہے اسی نے تمہاری جنس سے تمہارے لئے جوڑے پیدا کئے اور چوپایوں میں سے بھی جوڑے بنائے ہیں اس کے ذریعہ سے تمہیں پھیلاتا ہے (کائنات کی) کوئی چیز اس کی مثل (مانند) نہیں ہے وہ بڑا سننے والا اور بڑا دیکھنے والا ہے۔'),
(4284, 42, 12, 'آسمانوں اور زمین (کے خزانوں) کی کنجیاں اسی کے قبضۂ قدرت میں ہیں جس کا چاہتا ہے رزق کشادہ کرتا ہے اور (جس کا چاہتا ہے) تنگ کرتا ہے۔ بےشک وہ ہر چیز کا بڑا جا ننے والا ہے۔'),
(4285, 42, 13, 'اس نے تمہارے لئے وہی دین مقرر کیا ہے جس کا اس نے نوح(ع) کو حکم دیا تھا اور جو ہم نے بذریعۂ وحی آپ(ص) کی طرف بھیجا ہے اور جس کا ہم نے ابراہیم(ع)، موسیٰ(ع) اور عیسیٰ(ع) کو حکم دیا تھا یعنی اس دین کو قائم رکھنا اور اس میں تفرقہ نہ ڈالنا۔ مشرکین پر وہ بات بہت شاق ہے جس کی طرف آپ(ص) انہیں دعوت دیتے ہیں اللہ جسے چاہتا ہے اپنی بارگاہ کیلئے منتخب کر لیتا ہے اور جو اس کی طرف رجوع کرتا ہے وہ اسے اپنی طرف (پہنچنے کا) راستہ دکھاتا ہے۔'),
(4286, 42, 14, 'اور وہ لوگ تفرقہ میں نہیں پڑے مگر بعد اس کے کہ ان کے پاس علم آچکا تھا یہ (تفرقہ) محض باہمی ضدا ضدی (اور باہمی حسد) سے ہے اور اگر آپ کے پروردگار کی طرف سے یہ بات طے نہ ہوچکی ہوتی کہ ان لوگوں کو ایک مقررہ مدت تک مہلت دی جائے تو ان کے درمیان فیصلہ ہوچکا ہوتا اور جو لوگ ان کے بعد کتاب کے وارث بنائے گئے وہ اس کے بارے میں اضطراب انگیز شک میں گرفتار ہیں۔'),
(4287, 42, 15, 'پس آپ(ص) اس (دین) کی طرف دعوت دیتے رہیں اور ثابت قدم رہیں جس طرح آپ(ص) کو حکم دیا گیا ہے اور ان لوگوں کی خواہشات کی پیروی نہ کریں اور کہیں کہ میں ہر اس کتاب پر ایمان رکھتا ہوں جو اللہ نے نازل کی ہے اور مجھے حکم دیا گیا ہے کہ میں تمہارے درمیان عدل و انصاف کروں۔ اللہ ہی ہمارا پروردگار ہے اور تمہارا بھی پروردگار ہے ہمارے اعمال ہمارے لئے ہیں اور تمہارے اعمال تمہارے لئے ہیں۔ ہمارے، تمہارے درمیان کوئی بحث درکار نہیں ہے اللہ ہم سب کو (ایک دن) جمع کرے گا اور اسی کی طرف بازگشت ہے۔'),
(4288, 42, 16, 'اور جو لوگ اللہ کے(دین) کے بارے میں(بلا وجہ) جھگڑا کرتے ہیں بعد اس کے کہ اسے قبول بھی کیا جاچکا ہے ان کی حجت بازی ان کے پروردگار کے نزدیک باطل ہے اور ان پر(خدا کا) غضب ہے ان کیلئے سخت عذاب ہے۔'),
(4289, 42, 17, 'اللہ وہ ہے جس نے کتاب کو حق کے ساتھ نازل کیا ہے اور میزان کو بھی اور تمہیں کیا خبر شاید قیامت قریب ہی ہو۔'),
(4290, 42, 18, 'جو لوگ اس پر ایمان نہیں رکھتے تو اس کے لئے جلدی کرتے ہیں اور جو ایمان رکھتے ہیں وہ اس سے ڈرتے ہیں اور جا نتے ہیں کہ وہ برحق ہے آگاہ رہو جو قیامت کے بارے میں شک کرتے ہیں (تکرار کرتے ہیں) وہ بڑی کھلی گمراہی میں ہیں۔'),
(4291, 42, 19, 'اللہ اپنے بندوں پر بہت مہربان ہے جسے چاہتا ہے رزق عطا کرتا ہے وہ بڑا طاقتور (اور) زبردست ہے۔'),
(4292, 42, 20, 'جوشخص آخرت کی کھیتی چاہتا ہے تو ہم اس کی کھیتی میں اضافہ کردیتے ہیں اور جو (صرف) دنیا کی کھیتی چاہتا ہے تو ہم اس میں سے اسے کچھ دے دیتے ہیں مگر اس کا آخرت میں کوئی حصہ نہیں ہوتا۔'),
(4293, 42, 21, 'کیا ان کے کچھ ایسے شریک (خدا) ہیں جنہوں نے ان کیلئے ایسا دین مقرر کیا ہے جس کی اللہ نے اجازت نہیں دی اور اگر فیصلہ کے متعلق طےشدہ بات نہ ہوتی تو ان کے درمیان فیصلہ کر دیا گیا ہوتا اور جو لوگ ظالم ہیں ان کے لئے دردناک عذاب ہے۔'),
(4294, 42, 22, 'تم ظالموں کو دیکھو گے کہ وہ اپنے کئے ہوئے کاموں (کے انجام) سے ڈر رہے ہوں گے اور وہ (وبال) ان پر پڑ کر رہے گا اور جو لوگ ایمان لائے اور انہوں نے نیک عمل بھی کئے وہ بہشتوں کے باغوں میں ہوں گے وہ جو کچھ چاہیں گے اپنے پروردگار کے ہاں سے پائیں گے یہی بڑا فضل ہے۔'),
(4295, 42, 23, 'یہ وہ بات ہے جس کی خوشخبری خدا اپنے ان بندوں کو دیتا ہے جو ایمان لائے اور نیک اعمال بھی کئے آپ(ص) کہیے کہ میں تم سے اس(تبلیغ و رسالت) پر کوئی معاوضہ نہیں مانگتا سوائے اپنے قرابتداروں کی محبت کے اورجو کوئی نیک کام کرے گا ہم اسکی نیکی میں اضافہ کردیں گے یقیناً اللہ بڑا بخشنے والا(اور) بڑا قدردان ہے۔'),
(4296, 42, 24, 'کیا وہ کہتے ہیں کہ اس(رسول(ص)) نے خدا پر جھوٹا بہتان باندھا ہے اگر اللہ چاہے تو تمہارے دل پر مہر لگا دے اور اللہ باطل کو مٹا دیتا ہے اور حق کو اپنی باتوں سے حق ثابت کرتا ہے بےشک وہ سینوں کی چھپی ہوئی باتوں (رازوں) کو خوب جانتا ہے۔ وہ وہی ہے جو اپنے بندوں کو خوب جانتا ہے۔'),
(4297, 42, 25, 'اور اللہ اپنے بندوں کی توبہ قبول کرتا ہے اور برائیوں کو معاف کرتا ہے اور وہ جانتا ہے جو کچھ تم کرتے ہو۔'),
(4298, 42, 26, 'اور وہ ان لوگوں کی دعائیں قبول کرتا ہے جو ایمان لائے اور نیک عمل بھی کئے اورانہیں اپنے فضل و کرم سے اور زیادہ عطا کرتا ہے اور کافروں کیلئے سخت عذاب ہے۔'),
(4299, 42, 27, 'اور اگر خدا اپنے تمام بندوں کی روزی کشادہ کر دیتا تو وہ زمین میں بغاوت پھیلا دیتے اور وہ بندوں کے حالات کو جا ننے والا اور خوب دیکھنے والا ہے۔'),
(4300, 42, 28, 'وہ وہی ہے جو بارش برساتا ہے قابلِ ستائش ہے۔'),
(4301, 42, 29, 'اور اس کی نشانیوں میں سے آسمانوں اور زمین کی تخلیق اور جو چلنے پھر نے والے جاندار ان کے درمیان پھیلائے ہیں ان کا پیدا کرنا بھی ہے اور وہ ان کو جمع کرنے پر جب چاہے گا قادر ہے۔'),
(4302, 42, 30, 'اور تمہیں جو مصیبت بھی پہنچتی ہے وہ تمہارے اپنے ہاتھوں کے کئے ہوئے(کاموں) کی وجہ سے پہنچتی ہے اور وہ بہت سے(کاموں سے) درگزرکرتا ہے۔'),
(4303, 42, 31, 'اور تم زمین میں خدا کو عاجز و بےبس نہیں کر سکتے اور نہ ہی اللہ کے سوا تمہارا کوئی کارساز ہے اور نہ کوئی مددگار۔'),
(4304, 42, 32, 'اور اس کی قدرت کی نشانیوں میں سے وہ بڑے بڑے پہاڑ جیسے (بحری) جہاز ہیں جو سمندر میں چلتے ہیں۔'),
(4305, 42, 33, 'اگر وہ چاہے تو ہوا کو ٹھہرا دے تو وہ (جہاز) اس کی سطح پر کھڑے کے کھڑے رہ جائیں بےشک اس میں ہر بڑے صبر کرنے(اور) بڑے شکر کرنے والے کیلئے نشانیاں ہیں۔'),
(4306, 42, 34, 'یا اگر وہ چاہے تو ان (جہازوں) کو تباہ کر دے ان لوگوں کے (برے) اعمال کی وجہ سے اور اگر چاہے تو (ان کے) بہت سے (لوگوںیا گناہوں سے) درگزر فرمائے۔'),
(4307, 42, 35, 'اور تاکہ ان لوگوں کو معلوم ہو جائے جو ہماری آیتوں کے بارے میں جھگڑتے ہیں کہ ان کیلئے کوئی جائے فرار نہیں ہے۔'),
(4308, 42, 36, 'جو کچھ تمہیں دیا گیا ہے وہ صرف دنیٰوی زندگی کا (چند روزہ) ساز و سامان ہے اور جو کچھ اللہ کے پاس ہے وہ بہتر بھی ہے اور زیادہ پائیدار بھی ان لوگوں کیلئے جو ایمان لائے ہیں اور اپنے پروردگار پر بھروسہ رکھتے ہیں۔'),
(4309, 42, 37, 'اور وہ جو بڑے گناہوں اور بےحیائی کے کاموں سے پرہیز کرتے ہیں اور جب ان کو غصہ آتا ہے تو معاف کر دیتے ہیں۔'),
(4310, 42, 38, 'اور جو اپنے پروردگار کے حکم کو مانتے ہیں اور نماز قائم کرتے ہیں اور ان کے (تمام) کام باہمی مشورہ سے طے ہوتے ہیں اور جو کچھ ہم نے انہیں دیا ہے وہ اس سے خرچ کرتے ہیں۔'),
(4311, 42, 39, 'اور جب ان پر تعدی و زیادتی کی جاتی ہے تو وہ (واجبی) بدلہ لیتے ہیں۔'),
(4312, 42, 40, 'اور برائی کا بدلہ تو ویسے ہی برائی ہے اور جو معاف کر دے اورا صلاح کرے تو اس کا اجر خدا کے ذمہ ہے بیشک وہ (اللہ) ظالموں کو پسند نہیں کرتا۔'),
(4313, 42, 41, 'اور جو شخض بھی ظلم کے بعد بدلہ لے اس کے اوپر کوئی الزام نہیں ہے۔'),
(4314, 42, 42, 'اور (ملامت) تو صرف ان لوگوں پر ہے جو لوگوں پر ظلم کرتے ہیں اور زمین میں ناحق سرکشی کرتے ہیں ایسے ہی لوگوں کیلئے دردناک عذاب ہے۔'),
(4315, 42, 43, 'البتہ جوشخص صبر کرے اور درگزر کرے تو یہ بڑے ہمت کے کاموں میں سے ہے۔'),
(4316, 42, 44, 'اور جس کو اللہ گمراہی میں چھوڑ دے تو اس کے بعد اس کا کوئی سرپرست و کارساز نہیں ہے اورتم ظالموں کو دیکھو گے کہ جب وہ عذاب دیکھیں گے تو (گھبرا کر) کہیں گے کہایا (دنیا میں) واپسی کا کوئی راستہ ہے؟'),
(4317, 42, 45, 'اور تم انہیں دیکھو گے کہ جب وہ دوزخ کے سامنے پیش کئے جائیں گے تو ذلت سے جھکے ہوئے ہوں گے (اور) کنکھیوں (چھپی نگاہ) سے دیکھتے ہوں گے اور اہلِ ایمان کہیں گے کہ حقیقی خسارہ اٹھا نے والے وہی لوگ ہیں جنہوں نے قیامت کے دن اپنے آپ کو اور اپنے اہل و عیال کو خسارہ میں ڈالا۔ آگاہ ہو کہ ظالم لوگ دائمی عذاب میں رہیں گے۔'),
(4318, 42, 46, 'اور اللہ کے سوا ان کے کوئی حامی و مددگار نہیں ہوں گے جو اللہ کے مقابلہ میں ان کی مدد کریں اور جسے اللہ گمراہی میں چھوڑ دے تو اس کے لئے کوئی راستہ نہیں ہے۔'),
(4319, 42, 47, '(اے لوگو) اپنے پروردگار کی دعوت پر لبیک کہواس سے پہلے کہ وہ دن آئے جو اللہ کی طرف سے ٹلنے والا نہیں ہے اس دن نہ تمہارے لئے کوئی جائے پناہ ہوگی اور نہ انکار کی کوئی گنجائش ہوگی (اور نہ ہی تمہاری طرف سے کوئی روک ٹوک کرنے والا ہوگا)۔'),
(4320, 42, 48, 'اور اگر وہ رُوگردانی کریں تو ہم نے آپ کو ان کا نگہبان مقرر کر کے نہیں بھیجا آپ کی ذمہداری صرف پہنچا دینا ہے اور ہم جب انسان کو اپنی رحمت کامزہ چکھاتے ہیں تو وہ اس سے خوش ہوجاتا ہے اور اگر انہیں ان کے اعمال کی پاداش میں کوئی تکلیف پہنچتی ہے تو انسان بڑا ناشکرا بن جاتا ہے۔'),
(4321, 42, 49, 'آسمانوں اور زمین کی بادشاہی اللہ ہی کے لئے ہے وہ جو چاہتا ہے پیدا کرتا ہے اور جس کو چاہتا ہے لڑکیاں عطا کرتا ہے اور جسے چاہتا ہے لڑکے عنایت کرتا ہے۔'),
(4322, 42, 50, 'اورجسے چاہتا ہے لڑکے لڑکیاں جمع کر دیتا ہے اور جسے چاہتا ہے بانجھ بنا دیتا ہے۔ بےشک وہ بڑا جا ننے والا (اور) بڑا قدرت رکھنے والا ہے۔'),
(4323, 42, 51, 'اور کسی بشر کا یہ مقام نہیں کہ اللہ اس سے کلام کرے مگر وحی کے ذریعہ سےیا پردہ کے پیچھے سےیا وہ کوئی پیغام بر (فرشتہ) بھیجے اور اس کے حکم سے جو وہ چاہے وحی کرے۔ بیشک وہ بزرگ و برتر (اور) بڑا حکمت والا ہے۔'),
(4324, 42, 52, 'اور اسی طرح ہم نے آپ کی طرف وحی کی صورت میں اپنی ایک (خاص) روح بھیجی آپ نہیں جانتے تھے کہ کتاب کیا ہے اور نہ یہ جا نتے تھے کہ ایمان کیا ہے؟ لیکن ہم نے اسے ایک نور بنایا جس سے ہم اپنے بندوں میں سے جسے چاہتے ہیں ہدایت دیتے ہیں اور یقیناً آپ سیدھے راستہ کی طرف راہنمائی کرتے ہیں۔'),
(4325, 42, 53, 'اس اللہ کے راستہ کی طرف جس کا وہ سب کچھ ہے جو آسمانوں اور زمین میں ہے خبردار! سب (تمام) معاملات کی بازگشت اللہ ہی کی طرف ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حا۔ میم۔'),
(4327, 43, 2, 'قَسم ہے اس واضح کتاب کی۔'),
(4328, 43, 3, 'کہ ہم نے اس کو عربی زبان کا قرآن بنایا ہے تاکہ تم سمجھو۔'),
(4329, 43, 4, 'بےشک وہ (قرآن) اصل کتاب (لوحِ محفوظ) میں جو ہمارے پاس ہے بلند مرتبہ (اور) حکمت والا ہے۔'),
(4330, 43, 5, 'کیا ہم محض اس لئے نصیحت سے منہ پھیرلیں کہ تم حد سے تجاوز کرنے والے لوگ ہو۔'),
(4331, 43, 6, 'اور ہم نے پہلے لوگوں میں کتنے ہی نبی(ع) بھیجے ہیں۔'),
(4332, 43, 7, 'اور ان کے پاس کوئی نبی(ع) نہیںایا مگریہ کہ وہ اس کامذاق اڑاتے تھے۔'),
(4333, 43, 8, 'پھر ہم نے ان لوگوں کو ہلاک کر دیا جو ان (موجودہ منکرین) سے زیادہ طاقتور تھے اور پہلے لوگوں کی مثال (حالت) گزر چکی ہے۔'),
(4334, 43, 9, 'اور اگر آپ ان سے پوچھیں کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے؟ تو وہ ضرور کہیں گے کہ انہیں اس ہستی نے پیدا کیا ہے جو غالب ہے اور ہمہ دان۔'),
(4335, 43, 10, 'وہی جس نے زمین کو تمہارے لئے گہوارہ بنایا اور اس میں تمہارے لئے راستے بنا دئیے تاکہ تم (منزل تک) راہ پاؤ۔'),
(4336, 43, 11, 'اور جس نے ایک خاص مقدارمیں آسمان سے پا نی اتارا پھر ہم نے اس سے مردہ (خشک) زمین کو زندہ کر دیا اسی طرح تم (قبروں سے) نکالے جاؤ گے۔'),
(4337, 43, 12, 'اور جس نے تمام اِقسام کی چیزیں پیدا کیں اور تمہارے لئے کشتیاں اور چوپائے بنائے جن پر تم سوار ہوتے ہو۔'),
(4338, 43, 13, 'تاکہ تم ان کی پیٹھوں پر جم کر بیٹھو اور پھر اپنے پروردگار کی نعمت کو یاد کرو اور کہو پاک ہے وہ ذات جس نے ان چیزوں کو ہمارے لئے مسخر کر دیا اور ہم ایسے نہ تھے کہ ان کو اپنے قابو میں کرتے۔'),
(4339, 43, 14, 'اور بےشک ہم اپنے پروردگار کی طرف لو ٹنے والے ہیں۔'),
(4340, 43, 15, 'اور ان لوگوں نے خدا کے بندوں میں سے اس کا جزو قرار دیا ہے بیشک انسان کھلا ہوا ناشکراہے۔'),
(4341, 43, 16, 'کیا خدا نے اپنی مخلوق میں سے اپنے لئے بیٹیاں منتخب کی ہیں اور تمہیں بیٹوں کے ساتھ مخصوص کر دیا؟'),
(4342, 43, 17, 'اور جب ان میں سے کسی کو اس (بیٹی) کی بشارت دی جاتی ہے جسے وہ خدا کی صفت قرار دیتا ہے تو اس کا چہرہ سیاہ ہو جاتا ہے اور اس کا دل غم سے بھر جاتا ہے۔'),
(4343, 43, 18, 'کیا (خدا کیلئے وہ ہے) جو زیوروں میں پرورش پائے اور بحث و تکرار میں اپنا مدعا واضح نہ کر سکے؟'),
(4344, 43, 19, 'اور انہوں نے فرشتوں کو جو خدائے رحمٰن کے خاص بندے ہیں عورتیں قرار دے رکھا ہے کیا وہ ان کی پیدائش (اور جسمانی ساخت) کے وقت موجود تھے ان کی گواہی لکھ لی جائے گی اور ان سے بازپرس کی جائے گی۔'),
(4345, 43, 20, 'اور وہ کہتے ہیں کہ اگر اللہ چاہتا تو ہم ان (بتوں) کی عبادت نہ کرتے انہیں اس بارے میں کوئی علم نہیں ہے۔ وہ محض اٹکل پچو باتیں کرتے ہیں۔'),
(4346, 43, 21, 'کیا ہم نے انہیں اس سے پہلے کوئی کتاب دی ہے جسے وہ مضبوطی سے پکڑے ہوئے ہیں۔'),
(4347, 43, 22, 'بلکہ وہ تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک طریقہ پر دیکھا ہے اور ہم انہی کے نقشِ قدم پر راہ یاب ہیں۔'),
(4348, 43, 23, 'اور اسی طرح ہم نے آپ(ص) سے پہلے کسی بستی میں کوئی ڈرا نے والا (پیغمبر(ع)) نہیں بھیجا مگر یہ کہ وہاں آسودہ حال لوگوں نے کہا کہ ہم نے اپنے باپ دادا کو ایک طریقہ پر پایا اور ہم تو انہی کے نقوشِ پاکی پیروی کر رہے ہیں۔'),
(4349, 43, 24, 'اس (نذیر) نے کہا گرچہ میں تمہارے پاس اس سے زیادہ ہدایت بخش طریقہلے کرایا ہوں جس پر تم نے اپنے باپ دادا کو پایا ہے؟ ان لوگوں نے کہا کہ تم جس(دین) کے ساتھ بھیجے گئے ہو ہم اس کے منکر ہیں۔'),
(4350, 43, 25, 'سو ہم نے ان سے انتقام لیا تو دیکھو کہ جھٹلا نے والوں کا کیا انجام ہوا؟'),
(4351, 43, 26, 'اور وہ وقت یاد کرو جب ابراہیم(ع) نے اپنے باپ (تایا) اور اپنی قوم سے کہا میں ان سے بیزار ہوں جن کی تم عبادت کرتے ہو۔'),
(4352, 43, 27, 'مگر وہ جس نے مجھے پیدا کیا اور وہی میری راہنمائی کرے گا (میں صرف اسی کی عبادت کرتا ہوں)۔'),
(4353, 43, 28, 'اور وہ (ابراہیم(ع)) اسی (عقیدۂ توحید) کو اپنی اولاد میں باقی رہنے والا کلمہ قرار دے گئے تاکہ وہ اس کی طرف رجوع کریں۔'),
(4354, 43, 29, 'اور (باوجود ان کی خلاف ورزی کرنے کے) میں ان کو اور ان کے باپ دادا کو دنیا کے ساز و سامان سے بہرہ مندہ کرتا رہا یہاں تک کہ انکے پاس حق اور کھول کر بیان کرنے والا رسول آگیا۔'),
(4355, 43, 30, 'اور جب ان کے پاس حق آگیا تو انہوں نے کہا کہ یہ تو جادو ہے اور ہم اس کے منکر ہیں۔'),
(4356, 43, 31, 'اور وہ کہتے ہیں کہ یہ قرآن دو (مشہور) بستیوں (مکہ و طائف) کے کسی بڑے آدمی پر کیوں نہیں نازل کیا گیا؟'),
(4357, 43, 32, 'کیا یہ لوگ آپ کے پروردگار کی رحمت تقسیم کرتے ہیں؟ (حالانکہ) دنیاوی زندگی میں ان کے درمیان ان کی روزی ہم نے تقسیم کر دی ہے اور ہم نے بعض کو بعض پر کئی درجے فوقیت دی ہے تاکہ وہ ایک دوسرے سے خد متلے سکیں اور آپ کے پروردگار کی رحمت اس مال و دولت سے بہتر ہے جو وہ جمع کرتے ہیں۔'),
(4358, 43, 33, 'اور اگر یہ بات نہ ہوتی کہ سب لوگ ایک ہی طریقے کے ہو جائیں گے تو ہم خدائے رحمٰن کا انکار کرنے والوں کے مکانوں کی چھتیں اور سیڑھیاں جن پر وہ چڑھتے ہیں۔'),
(4359, 43, 34, 'اور ان کے گھروں کے دروازے اور تخت جن پر وہ تکیہ لگاتے ہیں سب چا ندی اور سونے کے بنوا دیتے۔'),
(4360, 43, 35, 'اور یہ سب کچھ دنیاوی زندگی کا ساز و سامان ہے اور تمہارے پروردگار کے ہاں آخرت تو متقیوں کے لئے ہے۔'),
(4361, 43, 36, 'اور جوشخص خدا کی یاد سے اندھا بنتا ہے تو ہم اس کیلئے ایک شیطان مقرر کر دیتے ہیں جو اس کا ساتھی ہوتا ہے۔'),
(4362, 43, 37, 'اور وہ (شیاطین) ان (اندھوں) کو راہ (راست) سے روکتے ہیں اور وہ خیال کرتے ہیں کہ وہ ہدایت یافتہ ہیں۔'),
(4363, 43, 38, 'یہاں تک کہ جب وہ ہمارے پاس آئیگا تو (اپنے شیطان سے) کہے گا اے کاش! میرے اور تیرے درمیان مشرقومغرب کی دوری ہوتی تو بہت برا ساتھی ہے۔'),
(4364, 43, 39, '(ان سے کہا جائے گا کہ) جب تم (دنیا میں) ظلم کر چکے تو (آج) یہ بات تمہیں کوئی فائدہ نہیں پہنچا سکتی تم سب عذاب میں ایک دوسرے کے شریک ہو۔'),
(4365, 43, 40, 'کیا آپ(ص) بہروں کو سنائیں گےیا اندھوں کو راہ دکھائیں گے اور ان کو جو کھلی ہوئی گمراہی میں ہیں؟'),
(4366, 43, 41, 'پس اگر ہم آپ کو (دنیا سے) اٹھائیں تو پھر بھی ہم ان لوگوں سے بدلہ لینے والے ہیں۔'),
(4367, 43, 42, 'یا ہم ان کو (آپ کے عینِ حیات) وہ (عذاب) دکھا دیں گے جس کا ہم نے ان سے وعدہ کیا ہے سو ہم ان پر پوری طرح قادر ہیں۔'),
(4368, 43, 43, 'پس آپ(ص) مضبوطی سے اسے تھامے رہیں جس کی آپ(ص) کی طرف وحی کی گئی ہے۔ یقیناً آپ سیدھے راستہ پر ہیں۔'),
(4369, 43, 44, 'اور بےشک وہ (قرآن) آپ(ص) کیلئے اور آپ(ص) کی قوم کیلئے بڑا شرف اور اعزاز ہے اور عنقریب تم لوگوں سے اس کے بارے میں پوچھا جائے گا۔'),
(4370, 43, 45, 'آپ(ص) پیغمبروں(ع) سے پوچھیں جنہیں ہم نے آپ(ص) سے پہلے بھیجا تھا کہایا ہم نے خدائے رحمٰن کے علاوہ بھی کوئی ایسے خدا مقرر کئے تھے جن کی عبادت کی جائے۔'),
(4371, 43, 46, 'اور بےشک ہم نے موسیٰ (ع) کو اپنی نشانیاں (معجزے) دے کر فرعون اور اس کے عمائدِ سلطنت کے پاس بھیجا۔ پس آپ(ع) نے (ان سے) کہا کہ میں عالمین کے پروردگار کا رسول(ع) ہوں۔'),
(4372, 43, 47, 'پس جب وہ ہماری نشانیوں کے ساتھ ان کے پاس آئے تو وہ لوگ ان (نشانیوں) پر ہنسنے لگے (مذاق اڑا نے لگے)۔'),
(4373, 43, 48, 'اور ہم انہیں کوئی نشانی نہیں دکھاتے تھے مگر وہ پہلی سے بڑی ہوتی تھی اور ہم نے انہیں عذاب میں پکڑا تاکہ وہ (اپنی روش سے) باز آئیں۔'),
(4374, 43, 49, 'اور انہوں نے کہا کہ اے جادوگر! اپنے پروردگار سے ہمارے لئے دعا کرو۔ہم ضرور ہدایت پا جائیں گے۔'),
(4375, 43, 50, 'تو جب ہم نے ان سے عذاب دور کر دیا تو ایکدم وہ عہد توڑ دیتے تھے۔'),
(4376, 43, 51, 'اور فرعون نے اپنی قوم میں منادی کرائی اے میری قوم! کیا مصر کی سلطنت میری نہیں ہے؟ اور یہ نہریں جو میرے نیچے بہہ رہی ہیں تو کیا تم دیکھتے نہیں ہو؟'),
(4377, 43, 52, '(مجھے بتا‎ؤ) کیا میں اس سے بہتر نہیں ہوں جو ذلیل و حقیر ہے اور صاف بول بھی نہیں سکتا۔'),
(4378, 43, 53, '(اگر یہ برحق نبی ہے) تو اس پر سو نے کے کنگن کیوں نہیں اتارے گئے؟ یا اس کے ہمراہ فرشتے پر باندھ کر آتے۔'),
(4379, 43, 54, 'اس طرح اس (فرعون) نے اپنی قوم کو احمق بنایا اور انہوں نے اس کی اطاعت کی۔ بےشک یہ نافرمان قِسم کے لوگ تھے۔'),
(4380, 43, 55, 'پس انہوں نے ہمیں ناراض کیا تو ہم نے ان سے انتقام لیا اور ان سب کو غرق کر دیا۔'),
(4381, 43, 56, 'اور انہیں گیا گزرا اور بعد والوں کیلئے نمونۂ عبرت بنا دیا۔'),
(4382, 43, 57, 'اور جب ابنِ مریم (ع) (عیسےٰ(ع)) کی مثال دی گئی تو ایک دم آپ(ص) کی قوم والے چیخنے چلا نے لگے۔'),
(4383, 43, 58, 'اور کہنے لگے کہ ہمارے معبود بہتر ہیں یاوہ(عیسیٰ(ع))؟ وہ یہ بات محض آپ(ص) سے کج بحثی کیلئے کرتے ہیں بلکہ یہ لوگ تو ہیں ہی بڑے جھگڑالو۔'),
(4384, 43, 59, 'وہ (عیسیٰ (ع)) تو بس ہمارا ایک بندہ تھا جس پر ہم نے انعام کیا اور اسے بنی اسرائیل کیلئے ایک مثال (نمونہ) بنا دیا۔'),
(4385, 43, 60, 'اگر ہم چاہیں تو تمہارے بدلے زمین میں فرشتے بسا دیں جو تمہارے جانشین ہوں۔'),
(4386, 43, 61, 'وہ(عیسیٰ(ع)) تو صرف قیامت کی ایک نشانی ہے تم لوگ اس میں شک نہ کرو اور میری پیروی کرو۔ یہی سیدھا راستہ ہے۔'),
(4387, 43, 62, '(دیکھو خیال رکھنا کہیں) شیطان تمہیں اس سے روک نہ دے۔ بےشک وہ تمہارا کھلا ہوا دشمن ہے۔'),
(4388, 43, 63, 'اور جب عیسیٰ(ع) کھلی نشانیاں (معجزے)لے کر آئے تو کہا میں تمہارے پاس حکمتلے کرایا ہوں تاکہ تم پر بعض وہ باتیں واضح کردوں جن میں تم اختلاف کرتے ہو سو تم اللہ سے (آپ(ع) کی نافرمانی سے) ڈرو اور میری اطاعت کرو۔'),
(4389, 43, 64, 'بےشک اللہ ہی میرا پروردگار ہے اور تمہارا بھی پروردگار۔ تو تم اسی کی عبادت کرو یہی سیدھا راستہ ہے۔'),
(4390, 43, 65, 'پس مختلف گروہوں نے آپس میں اختلاف کیا پس تباہی ظالموں کیلئے ایک بڑے دردناک دن (قیامت) کے عذاب سے۔'),
(4391, 43, 66, 'کیا یہ لوگ بس قیامت کا انتظار کر رہے ہیں کہ وہ اچانک ان پر آجائے کہ انہیں خبر بھی نہ ہو۔'),
(4392, 43, 67, 'اس دن سب دوست ایک دوسرے کے دشمن ہوں گے سوائے پرہیزگاروں کے۔'),
(4393, 43, 68, '(خدا ان سے فرمائے گا) اے میرے بندو! آج نہ تم پر کوئی خوف ہے اور نہ ہی تم غمگین ہو گے۔'),
(4394, 43, 69, '(یہ وہ لوگ ہیں) جو ہماری آیتوں پر ایمان لائے اور مطیع و فرمانبردار بن کر رہے۔'),
(4395, 43, 70, '(حکم ہوگا) تم اور تمہاری بیویاں جنت میں داخل ہو جاؤ تم خوش کئے جاؤ گے۔'),
(4396, 43, 71, 'ان پر سو نے کے تھال اور جام گردش میں ہوں گے اور وہاں ہر وہ چیز موجود ہوگی جو دل چاہیں گے اور آنکھوں کو لذت ملے گی اور تم اس میں ہمیشہ رہو گے۔'),
(4397, 43, 72, 'یہ وہ جنت ہے جس کے تم وارث بنائے گئے ہواپنے ان اعمال کے صلہ میں جو تم کرتے تھے۔'),
(4398, 43, 73, 'تمہارے لئے اس میں بہت سے میوے ہیں جن سے تم کھاؤ گے۔'),
(4399, 43, 74, 'بےشک مجرم لوگ دوزخ کے عذاب میں ہمیشہ رہیں گے۔'),
(4400, 43, 75, 'وہ عذاب کبھی ان سے ہلکا نہیں کیا جائے گا اور وہ اس میں مایوس ہو کر پڑے رہیں گے۔'),
(4401, 43, 76, 'اور ہم نے ان پر ظلم نہیں کیا لیکن وہ خود (اپنے اوپر) ظلم کرنے والے تھے۔'),
(4402, 43, 77, 'اور وہ پکاریں گے کہ اے مالک! تمہارا پروردگار ہمارا کام ہی تمام کر دے (تو بہتر) وہ کہے گا کہ تم یوں ہی پڑے رہو گے۔'),
(4403, 43, 78, 'بےشک ہم تمہارے پاس (دین) حقلے کر آئے مگر تم میں سے اکثر حق کو ناپسند کرتے رہے۔'),
(4404, 43, 79, 'کیا انہوں نے کوئی قطعی فیصلہ کر لیا ہے تو ہم بھی کوئی فیصلہ کر لیتے ہیں۔'),
(4405, 43, 80, 'کیا وہ خیال کرتے ہیں کہ ہم ان کے رازوں اور ان کی سرگوشیوں کو نہیں سنتے؟ ہاں (ہم سنتے ہیں) اور ہمارے فرشتے ان کے پاس لکھتے بھی جاتے ہیں۔'),
(4406, 43, 81, 'آپ(ص) کہہ دیجئے! کہ اگر خدائے رحمٰن کی کوئی اولاد ہوتی تو سب سے پہلا عبادت گزار میں ہوتا۔'),
(4407, 43, 82, 'پاک ہے آسمانوں اور زمین کا پروردگار جو عرش کامالک ہے اس سے جو یہ لوگ بیان کرتے ہیں۔'),
(4408, 43, 83, 'آپ ان کو چھوڑیں کہ وہ بحث کریں اور تفریح کریں (کھیلیں) یہاں تک کہ ان کو اس دن سے سابقہ پڑے جس کا ان سے وعدہ کیا جا رہا ہے۔'),
(4409, 43, 84, 'اور وہ وہی ہے جو آسمان میں بھی خدا ہے اور زمین میں بھی وہی خدا ہے اور وہ بڑا حکمت والا، بڑا جاننے والا ہے۔'),
(4410, 43, 85, 'بڑی بابرکت ہے وہ ذات جس کے قبضۂ قدرت میں آسمانوں اور زمین اور ہر اس چیز کی بادشاہی ہے جو کچھ ان دونوں کے درمیان ہے اسی کے پاس قیامت کا عِلم ہے اور اسی کی طرف تم لوٹائے جاؤ گے۔'),
(4411, 43, 86, 'اورجن کو یہ لوگ اللہ کے سوا پکارتے ہیں وہ سفارش کرنے کا کوئی اختیار نہیں رکھتے۔ سوائے ان کے جو حق کی گواہی دیں اور وہ جانتے بھی ہوں۔'),
(4412, 43, 87, 'اور اگر آپ(ص) ان سے پوچھیں کہ انہیں کس نے پیدا کیا؟ تو وہ کہیں گے کہ اللہ نے تو وہ کہاں بھٹکتے پھرتے ہیں۔'),
(4413, 43, 88, 'اور اسی (اللہ) کو رسول(ص) کے اس قول کا عِلم ہے کہ اے میرے پروردگار! یہ لوگ ایمان نہیں لاتے۔'),
(4414, 43, 89, '(اے رسول(ص)) درگزر کرو۔اور کہو سلام ہو (خداحافظ) عنقریب انہیں (اپنا انجام) معلوم ہو جائے گا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حا۔ میم۔'),
(4416, 44, 2, 'قَسم ہے اس کتابِ مبین کی۔'),
(4417, 44, 3, 'ہم نے اس (کتاب) کو ایک بابرکت رات (شبِ قدر) میں نازل کیا ہے بےشک ہم (عذاب سے) ڈرانے والے رہے ہیں۔'),
(4418, 44, 4, 'اس(رات) میں ہر حکمت والے معاملہ کا فیصلہ کیا جاتا ہے۔'),
(4419, 44, 5, 'ہمارے(خاص) حکم سے بےشک ہم ہی (ہر کتاب اور رسول(ع) کے) بھیجنے والے ہیں۔'),
(4420, 44, 6, 'خاص تمہارے پروردگار کی رحمت سے یقیناً وہ بڑا سننے والا (اور) بڑا جاننے والا ہے۔'),
(4421, 44, 7, 'جو آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے سب کا پروردگار ہے اگر تم یقین کرنے والے ہو۔'),
(4422, 44, 8, 'اس کے سوا کوئی الٰہ (خدا) نہیں ہے وہی زندہ کرتا ہے اور وہی مارتا ہے تمہارا بھی پروردگار ہے اور تمہارے اگلے باپ داداؤں کا بھی پروردگار ہے۔'),
(4423, 44, 9, 'مگر وہ لوگ تو شک میں پڑے ہوئے کھیل رہے ہیں۔'),
(4424, 44, 10, 'آپ اس دن کا انتظار کریں جب آسمان ایک کھلے ہوئے دھویں کے ساتھ آئے گا۔'),
(4425, 44, 11, 'جو لوگوں پر چھا جائے گا یہ ایک دردناک عذاب ہوگا۔'),
(4426, 44, 12, '(اس وقت کفار بھی کہیں گے) اے ہمارے پروردگار! ہم سے یہ عذاب دور فرما۔ ہم ایمان لاتے ہیں۔'),
(4427, 44, 13, 'اب ان کو کہاں سے نصیحت حاصل ہوگی؟ جبکہ ان کے پاس کھول کر بیان کرنے والا رسول(ع) آچکا۔'),
(4428, 44, 14, 'پھر انہوں نے اس سے منہ موڑا اور کہا کہ یہ تو سکھایا ہوا ایک دیوانہ ہے۔'),
(4429, 44, 15, 'بےشک (اگر) ہم کچھ وقت کیلئے یہ عذاب ہٹا بھی دیں تو تم لوگ لوٹ کر وہی کچھ کرو گے جو پہلے کرتے تھے۔'),
(4430, 44, 16, 'جس دن ہم سخت پکڑ پکڑیں گے اس دن ہم پورا بدلہ لیں گے۔'),
(4431, 44, 17, 'ہم نے ان سے پہلے فرعون کی قوم کو آزمایا تھا اور ان کے پاس ایک معزز رسول(ع)ایا تھا۔'),
(4432, 44, 18, '(اوران سے کہاتھا کہ) اللہ کے بندوں کو میرے حوالے کرو میں ایک امانت دار رسول(ع) ہوں۔'),
(4433, 44, 19, 'اور یہ کہ تم اللہ کے مقابلہ میں سرکشی نہ کرو میں تمہارے پاس واضح دلیل (سند) پیش کرتا ہوں۔'),
(4434, 44, 20, 'اور میں نے اپنے اور تمہارے پروردگار سے پناہ مانگ لی ہے کہ تم مجھے سنگسار کرو۔'),
(4435, 44, 21, 'اور اگر تم مجھ پر ایمان نہیں لاتے تو پھر مجھ سے الگ ہی رہو۔'),
(4436, 44, 22, 'پس اس (رسول) نے اپنے پروردگار سے دعا مانگی کہ یہ بڑے مجرم لوگ ہیں۔'),
(4437, 44, 23, '(ارشاد ہوا) تم میرے بندوں کو ہمراہلے کر راتوں رات نکل جاؤ کیونکہ تمہاراتعاقب کیا جائے گا۔'),
(4438, 44, 24, 'اور تم سمندر کو اس کے حال (سکون) پہ چھوڑ دو۔ بیشک وہ (دشمن) ایسا لشکر ہے جو غرق ہو نے والا ہے۔'),
(4439, 44, 25, 'وہ کتنے باغ اور چشمے چھوڑ گئے۔'),
(4440, 44, 26, 'اور کھیتیاں اور عمدہ مقامات۔'),
(4441, 44, 27, 'اور آرام و راحت کا ساز و سامان جس میں وہ خوش و خرم رہتے تھے۔'),
(4442, 44, 28, 'اور ہم نے ان چیزوں کا وارث ایک قوم کو بنا دیا۔'),
(4443, 44, 29, 'پس ان کی بربادی پر نہ آسمان رویا اور نہ زمین اور نہ ہی ان کو مہلت دی گئی۔'),
(4444, 44, 30, 'اور ہم نے بنی اسرائیل کو رسوا کن عذاب سے نجات دی۔'),
(4445, 44, 31, 'جو فرعون کی طرف سے تھا بےشک وہ سرکش اور حد سے نکل جا نے والوں میں سے تھا۔'),
(4446, 44, 32, 'اور ہم نے جان بوجھ کر ان کو تمام جہانوں والوں پر ترجیح دی۔'),
(4447, 44, 33, 'اور ہم نے ان کو ایسی نشانیاں عطا کیں جن میں کھلی ہوئی آزمائش تھی۔'),
(4448, 44, 34, 'بیشک یہ لوگ کہتے ہیں۔'),
(4449, 44, 35, 'کہ بس ہماری یہی پہلی موت ہی ہے اس کے بعد ہم (دوبارہ) نہیں اٹھائے جائیں گے۔'),
(4450, 44, 36, 'اگر تم سچے ہو تو ہمارے باپ داداؤں کو لاؤ۔'),
(4451, 44, 37, '(قوت کے لحاظ سے) یہ بہتر ہیں یا قومِ تبع اور ان سے پہلے ہم نے ان کو ہلاک کر دیا کہ وہ مجرم تھے۔'),
(4452, 44, 38, 'ہم نے آسمانوں کو، زمین کو اور جو کچھ ان کے درمیان ہے کو کھیل کے طور پر پیدانہیں کیا۔'),
(4453, 44, 39, 'ہم نے ان کو پیدا نہیں کیا مگر حق (و حکمت) کے ساتھ مگر ان کے اکثر لوگ (یہ حقیقت) نہیں جانتے۔'),
(4454, 44, 40, 'بےشک فیصلہ کا دن (قیامت) ان سب کا طے شدہ وقت ہے۔'),
(4455, 44, 41, 'جس دن کوئی دوست کسی دوست کوکوئی فائدہ نہیں پہنچاسکے گا اور نہ ہی ان کی مدد کی جائے گی۔'),
(4456, 44, 42, 'مگر وہ جس پر اللہ رحم کرے بےشک وہ بڑا غالب (اور) بڑا رحم کرنے والا ہے۔'),
(4457, 44, 43, 'بیشک زقوم کا درخت۔'),
(4458, 44, 44, 'گنہگار کی غذا ہوگا۔'),
(4459, 44, 45, 'پگھلے ہوئے تانبےکی طرح پیٹوں میں جوش کھائے گا۔'),
(4460, 44, 46, 'جس طرح گرم کھولتا ہوا پانی ہے۔'),
(4461, 44, 47, 'اس کو پکڑو۔اور اسے گھسیٹتے ہوئے جہنم کے وسط تکلے جاؤ۔'),
(4462, 44, 48, 'اور پھر اس کے سر پر کھولتے ہوئے پانی کا عذاب انڈیل دو۔'),
(4463, 44, 49, 'مزا چکھ تُو تو بڑا معزز اور مکرم ہے۔'),
(4464, 44, 50, 'یہ وہی ہے جس میں تم شک کیا کرتے تھے۔'),
(4465, 44, 51, 'بےشک جو پرہیزگار لوگ ہیں وہ امن و سکون کی جگہ پر ہوں گے۔'),
(4466, 44, 52, 'یعنی باغوں میں اور چشموں میں۔'),
(4467, 44, 53, 'وہ سندس و اسبترق (باریک اور دبیز ریشم) کے کپڑے پہنے ہوئے آمنے سامنے بیٹھے ہوں گے۔'),
(4468, 44, 54, 'یہ بات اسی طرح ہے اور ہم ان کی گوری رنگت اور بڑی آنکھوں والی عورتوں (حوروں) سے شادی کریں گے۔'),
(4469, 44, 55, 'وہ اس میں بڑے امن و سکون کے ساتھ ہر قِسم کے میوے طلب کریں گے۔'),
(4470, 44, 56, 'وہ وہاں پہلے والی موت کے سوا موت کامزہ نہیں چکھیں گے اور اللہ نے انہیں دوزخ کے عذاب سے بچا لیا ہے۔'),
(4471, 44, 57, 'یہ سب کچھ آپ کے پروردگار کے فضل و کرم سے ہوگا اور یہ بڑی کامیابی ہے۔'),
(4472, 44, 58, 'پس ہم نے اس (قرآن) کو آپ کی زبان پر بالکل آسان بنا دیا تاکہ وہ نصیحت حاصل کریں۔'),
(4473, 44, 59, '(اچھا) آپ انتظار کیجئے! یقیناً وہ بھی انتظار کر رہے ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حا۔ میم۔'),
(4475, 45, 2, 'یہ کتاب اللہ کی طرف سے نازل کی گئی ہے جو غالب ہے (اور) بڑا حکمت والا ہے۔'),
(4476, 45, 3, 'بےشک آسمانوں اور زمین میں اہلِ ایمان کے لئے (خدا کی توحید و قدرت کی) بہت سی نشانیاں ہیں۔'),
(4477, 45, 4, 'اورخود تمہاری پیدائش میں اور ان حیوانات میں جن کو وہ (زمین میں) پھیلا رہا ہے یقین رکھنے والوں کیلئے نشانیاں ہیں۔'),
(4478, 45, 5, 'اور رات اور دن کی آمد ورفت میں اس (ذریعہ) رزق (بارش) میں جو اللہ نے آسمانوں سے نازل کیا اور اس سے زمین کو زندہ کر دیا اس کی موت کے بعد اور ہواؤں کی گردش میں بھی نشانیاں ہیں ان لوگوں کیلئے جو عقل سے کام لیتے ہیں۔'),
(4479, 45, 6, 'یہ اللہ کی آیتیں ہیں جو ہم حق کے ساتھ آپ کو پڑھ کر سنا رہے ہیں تو آخر اللہ اور اس کی آیتوں کے بعد وہ کونسی بات ہے جس پر وہ ایمان لائیں گے؟'),
(4480, 45, 7, 'ہلاکت ہے ہر اس بڑے جھوٹے (اور) بڑے گنہگار کے لئے۔'),
(4481, 45, 8, 'جو اللہ کی آیتوں کو سنتا ہے جب اس کے سامنے پڑھی جاتی ہیں پھر وہ تکبر کے ساتھ اس طرح اَڑا رہتا ہے کہ گویا اس نے انہیں سنا ہی نہیں۔ بس تم اسے دردناک عذاب کی خبر دے دو۔'),
(4482, 45, 9, 'اور جب اس کو ہماری آیات میں سے کسی کا علم ہوتا ہے تو وہ ان کو مذاق بنا لیتا ہے ایسے لوگوں کیلئے رسوا کُن عذاب ہے۔'),
(4483, 45, 10, 'ان کے آگے جہنم ہے اور انہوں نے جو کچھ کیا کمایا ہے وہ انہیں کوئی فائدہ نہ دے گا اور نہ ہی وہ ان کے کام آئیں گے جن کو انہوں نے اللہ کے سوا کارساز بنا رکھا ہے اور ان کیلئے بڑا عذاب ہے۔'),
(4484, 45, 11, 'یہ ہدایت ہے (اور وہ لوگ جنہوں نے اپنے پرودرگار کی آیتوں کا انکار کیا ان کیلئے) سخت قِسم کا دردناک عذاب ہے۔'),
(4485, 45, 12, 'اللہ وہی ہے جس نے تمہارے لئے سمندر کومسخر کر دیا تاکہ اس کے حکم سے اس میں کشتیاں چلیں اور تاکہ تم اس کا فضل (رزق) تلاش کرو۔ اور تاکہ تم شکر کرو۔'),
(4486, 45, 13, 'اوراس نے آسمانوں اور زمین کی سب چیزوں کو تمہارے لئے مسخر کر دیا ہے۔ بےشک اس میں غورو فکر کرنے والوں کیلئے (خدا کی قدرت و حکمت کی) نشانیاں ہیں۔'),
(4487, 45, 14, '(اے رسول(ص)) ایمان والوں سے کہیے! کہ وہ ان لوگوں سے درگزر کریں جو اللہ کے (خاص) دنوں کی امید نہیں رکھتے تاکہ اللہ ایک قوم کو اس کے ان سارے اعمال کا بدلہ دے جو وہ کیا کرتے تھے۔'),
(4488, 45, 15, 'جو کوئی نیک عمل کرتا ہے وہ اپنے فائدہ کیلئے کرتا ہے اورجو کوئی بر ائی کرتا ہے اس کا وبال بھی اسی پر پڑتا ہے پھر تم سب اپنے پروردگار کی طرف لوٹائے جاؤ گے۔'),
(4489, 45, 16, 'اور یقیناً ہم نے بنی اسرائیل کو کتاب، حُکم اور نبوت عطا فرمائی اور ان کو پاکیزہ رزق عطا فرمایا اور ان کو دنیا جہان والوں پر فضیلت عطا کی۔'),
(4490, 45, 17, 'اورہم نے انہیں دین کے معاملہ میں کھلے دلائل عطا کئے پس انہوں نے اس میں اختلاف نہیں کیا مگر علم آجانے کے بعد محض باہمی ضد اور ظلم کی وجہ سے۔ بےشک قیامت کے دن آپ(ص) کا پروردگار ان کے درمیان فیصلہ کرے گا جن چیزوں میں وہ باہم اختلاف کرتے تھے۔'),
(4491, 45, 18, '(اے رسول(ص)) پھر ہم نے آپ(ص) کو ایک واضح شریعت پر قائم کیا ہے پس آپ(ص) اس کی پیروی کیجئے! اور ان لوگوں کی خواہش کی پیروی نہ کیجئے جو علم نہیں رکھتے۔'),
(4492, 45, 19, 'وہ اللہ کے مقابلہ میں آپ(ص) کو ہرگز کچھ بھی فائدہ نہیں پہنچائیں گے۔ یقیناً ظالم لوگ ایک دوسرے کے ساتھی ہوتے ہیں اور اللہ تو پرہیزگاروں کا ساتھی (اور پُشت پناہ) ہے۔'),
(4493, 45, 20, 'یہ(قرآن) لوگوں کے لئے بصیرتوں کا سرمایہ اور یقین رکھنے والوں کیلئے ہدایت و رحمت کا باعث ہے۔'),
(4494, 45, 21, 'کیا وہ لوگ جنہوں نے برائیوں کا ارتکاب کیا ہے وہ خیال کرتے ہیں کہ ہم ان کو ان لوگوں کی مانند قرار دیں گے جو ایمان لائے اور نیک عمل کرتے رہے؟ ان کا جینا اور مرنا یکساں ہو جائے گا؟ بہت براہے وہ فیصلہ جو وہ کر رہے ہیں۔'),
(4495, 45, 22, 'اور اللہ نے آسمانوں اور زمین کو حق و حکمت کے ساتھ پیدا کیا ہے اور تاکہ ہر شخص کو اس کے کئے کا بدلہ دیاجائے اور ان پر (ہرگز) ظلم نہیں کیا جائے گا۔'),
(4496, 45, 23, 'کیا آپ(ص) نے اس شخص کو دیکھا ہے جس نے اپنی خواہشِ نفس کو اپنا خدا بنا رکھاہے اوراللہ نے باوجود علم کے اسے گمراہی میں چھوڑ دیا ہے اور اس کے کان اور دل پر مہر لگا دی ہے اور اس کی آنکھ پر پر دہ ڈال دیا ہے اللہ کے بعد کون ایسے شخص کو ہدایت کر سکتا ہے؟ کیا تم نصیحت حاصل نہیں کرتے۔'),
(4497, 45, 24, 'اور وہ کہتے ہیں کہ ہماری زندگی تو بس یہی دنیٰوی زندگی ہے۔ یہیں ہم مرتے ہیں اور یہیں جیتے ہیں اور ہمیں صرف گردشِ زمانہ ہلاک کرتی ہے حالانکہ انہیں اس (حقیقت) کا کوئی علم نہیں ہے وہ محض گمان کی بناء پر ایسا کہتے ہیں۔'),
(4498, 45, 25, 'اور جب ان کے سامنے ہماری کھلی ہوئی آیتیں پڑھی جاتی ہیں تو ان کے پاس کوئی حجت اور دلیل نہیں ہوتی سوائے اس کے کہ کہتے ہیں کہ اگر تم سچے ہو تو ہمارے باپ دادا کو (زندہ کر کے) لاؤ۔'),
(4499, 45, 26, 'آپ(ص) کہہ دیجئے! اللہ ہی تمہیں زندہ رکھتا ہے وہی تمہیں موت دیتا ہے پھر وہی تمہیں قیامت کے دن اکٹھا کرے گا جس کے آنے میں کوئی شک نہیں ہے لیکن اکثر لوگ (اس حقیقت کو) جانتے نہیں ہیں۔'),
(4500, 45, 27, 'آسمانوں اور زمین میں اللہ ہی کی بادشاہی ہے اور جس دن قیامت قائم ہوگی اس دن باطل پرست لوگ خسارہ اٹھائیں گے۔'),
(4501, 45, 28, 'اور آپ دیکھیں گے کہ اس وقت ہر گروہ گھٹنوں کے بل گرا ہوا اپنے (فیصلے کامنتظر) ہوگا اور ہر گروہ کو اس کی کتاب (صحیفۂ عمل) کی طرف بلایا جائے گا (اور ان سے کہا جائے گا کہ) آج تمہیں اس کا بدلہ دیا جائے گا جو کچھ تم کیا کرتے تھے۔'),
(4502, 45, 29, 'یہ ہماری کتاب (دفترِ عمل) ہے جو تمہارے بارے میں حق و سچ کے ساتھ بولتی ہے ہم لکھتے (لکھواتے) رہتے تھے جو کچھ تم کرتے تھے۔'),
(4503, 45, 30, 'جو لوگ ایمان لائے اور نیک عمل کرتے رہے ان کا پروردگار انہیں اپنی رحمت میں داخل کرے گااوریہی واضح کامیابی ہے۔'),
(4504, 45, 31, 'اور جن لوگوں نے کفر اختیار کیا (ان سے کہا جائے گا) میری آیتیں تمہیں پڑھ کر نہیں سنائی جاتی تھیں؟ مگر تم نے تکبر کیا اور تم مجرم لوگ تھے۔'),
(4505, 45, 32, 'اورجب (تم سے) کہا جاتا تھا کہ اللہ کا وعدہ برحق ہے اور قیامت میں کوئی شک نہیں ہے تو تم کہتے تھے کہ ہم نہیں جانتے کہ قیامت کیا ہے؟ بس ایک گمان ہے جو ہم کرتے ہیں اور ہمیں اس کا یقین نہیں ہے۔'),
(4506, 45, 33, '(اس وقت) ان کے اعمال کی برائیاں واضح ہو جائیں گی اور وہ چیز ان کو گھیرے گی جس کا وہ مذاق اڑایا کرتے تھے۔'),
(4507, 45, 34, 'اور (ان سے کہا) جائے گا کہ آج ہم تمہیں اس طرح نظر انداز کریں گے جس طرح تم نے اس دن کی ملاقات (حاضری) کو بُھلا دیا تھا۔ تمہارا ٹھکانہ دوزخ ہے اور تمہارا کوئی مددگار نہیں ہے۔'),
(4508, 45, 35, 'یہ اس لئے کہ تم نے اللہ کی آیتوں کامذاق اڑایا تھا اوردنیاوی زندگی نے تمہیں دھوکہ میں مبتلا کیا۔ پس وہ آج نہ تو اس (دوزخ) سے نکالے جائیں گے اور نہ ان کو معذرت (خدا کو راضی) کرنے کاموقع دیا جائے گا۔'),
(4509, 45, 36, 'ہر قِسم کی تعریف اللہ کیلئے ہے جو آسمانوں اور زمین کا بلکہ سارے جہانوں کا پروردگار ہے۔'),
(4510, 45, 37, 'اسی کیلئے کِبریائی ہے اور وہ بڑا غالب (اور) بڑی حکمت والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حا۔ میم۔'),
(4512, 46, 2, '(یہ) کتابِ عزیز و حکیم (غالب اور بڑے حکمت والے خدا) کی طرف سے نازل کی گئی ہے۔'),
(4513, 46, 3, 'ہم نے آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے کو پیدا نہیں کیا مگر حق و حکمت کے ساتھ اور ایک مقررہ مدت تک اور جو کافر لوگ ہیں وہ جس چیز سے ڈرائے جاتے ہیں وہ اس سے رُوگردانی کرتے ہیں۔'),
(4514, 46, 4, 'آپ(ص) کہہ دیجئے! کہ کیا تم نے کبھی غور کیا ہے کہ جن کو تم اللہ کے سوا پکارتے ہو مجھے دکھاؤ کہ انہوں نے زمین میں سے کیا پیدا کیا ہے؟ یا آسمانوں کی تخلیق میں ان کی کچھ شرکت ہے؟ میرے پاس پہلے کی کوئی کتاب لاؤ! یا کوئی عِلمی روایت(اور علمی ثبوت) لاؤ اگر تم سچے ہو۔'),
(4515, 46, 5, 'اور اس شخص سے زیادہ گمراہ کون ہوگا جو اللہ کو چھوڑ کر ان کو پکارے جو قیامت تک اس کو جواب نہیں دے سکتے بلکہ وہ ان کی دعا و پکار سے غافل ہیں۔'),
(4516, 46, 6, 'اور جب سب لوگ جمع کئے جائیں گے تو وہ (معبود) ان کے دشمن ہوں گے اور ان کی عبادت کا انکار کریں گے۔'),
(4517, 46, 7, 'اور جب ان کو ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو (یہ) کافر لوگ حق کے بارے میں کہتے ہیں جبکہ ان کے پاس آگیا کہ یہ تو کھلا ہوا جادو ہے۔'),
(4518, 46, 8, 'کیا وہ کہتے ہیں کہ اس (رسول(ص)) نے اسے خود گھڑ لیا ہے؟ آپ(ص) کہیے کہ اگر میں نے اسے گھڑ لیا ہے تو تم لوگ اللہ کے مقابلہ میں میرے لئے کچھ بھی نہیں کر سکتے (ذرا بھی مجھے نہیں بچا سکتے) (قرآن کے متعلق) تم جن باتوں میں مشغول ہو وہ (اللہ) ان کو خوب جانتا ہے اور وہی میرے اور تمہارے درمیان بطور گواہ کافی ہے اور بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(4519, 46, 9, 'آپ کہہ دیجئے کہ رسولوں(ع) میں سے میں کوئی انوکھا نہیں ہوں اور میں (از خود) نہیں جانتا کہ میرے ساتھ کیا کیاجائے گا؟ اور تمہارے ساتھ کیا کیا جائے گا اور میں تو صرف اس کی پیروی کرتا ہوں جس کی مجھے وحی کی جاتی ہے اور میں نہیں ہوں مگر کھلا ہوا ڈرانے والا۔'),
(4520, 46, 10, 'آپ کہیے کیا تم نے کبھی غور کیا ہے کہ اگر وہ (قرآن) اللہ کی طرف سے ہوا اور تم نے اس کا انکار کیا درآنحالیکہ تم بنی اسرائیل میں سے ایک گواہ اس جیسی (کتاب پر) گواہی دے چکا اور ایمان بھی لا چکا مگر تم نے تکبر کیا؟ بیشک اللہ ظالم لوگوں کو ہدایت نہیں دیتا۔'),
(4521, 46, 11, 'اور کافر لوگ ایمان لانے والوں کی نسبت کہتے ہیں کہ اگر وہ (قرآن و اسلام) کوئی اچھی چیز ہوتا تو یہ لوگ اس کی طرف ہم پر سبقت نہلے جاتے اب چونکہ انہوں نے اس (قرآن) سے ہدایت نہیں پائی تو وہ ضرور کہیں گے کہ یہ تو پرانا جھوٹ ہے۔'),
(4522, 46, 12, 'حالانکہ اس سے پہلے موسیٰ(ع) کی کتاب (توراۃ) بھی راہنماورحمت تھی اور یہ کتاب (قرآن) تو اس کی تصدیق کرنے والی ہے (اور) عربی زبان میں ہے تاکہ ظالموں کو ڈرائے اور نیکوکاروں کے لئے خوشخبری ہے۔'),
(4523, 46, 13, 'بےشک جن لوگوں نے کہا کہ ہمارا پروردگار اللہ ہے اور پھر اس (اقرار) پر ثابت و برقرار رہے تو انہیں کوئی خوف نہیں ہے اور نہ ہی وہ غمگین ہوں گے۔'),
(4524, 46, 14, 'یہی لوگ بہشتی ہیں جس میں وہ ہمیشہ رہیں گے یہ صِلہ ہے ان کے ان اعمال کا جو وہ (دنیا میں) کیا کرتے تھے۔'),
(4525, 46, 15, 'اور ہم نے انسان کو اپنے والدین کے ساتھ نیک سلوک کرنے کا حکم دیا ہے اس کی ماں نے زحمت کے ساتھ اسے پیٹ میں رکھا اور زحمت کے ساتھ اسے جنم دیا اور اس کا حمل اور دودھ چھڑانا تیس مہینوں میں ہوا۔ یہاں تک کہ جب وہ اپنی پوری طاقت کو پہنچا (جوان ہوا) اور پورے چالیس سال کا ہوگیا تو اس نے کہا اے میرے پروردگار! تو مجھے توفیق عطا فرما کہ میں تیرے اس احسان کاشکر ادا کروں جو تو نے مجھ پر اور میرے والدین پر کیا ہے اور یہ کہ میں ایسا نیک عمل کروں جسے تو پسند کرتا ہے اور میرے لئے میری اولاد میں بھی صالحیت پیدا فرما میں تیری بارگاہ میں توبہ (رجوع) کرتا ہوں اور میں مسلمانوں(فرمانبرداروں) میں سے ہوں۔'),
(4526, 46, 16, 'یہی وہ لوگ ہیں جن کے بہترین اعمال کو ہم قبول کرتے ہیں اور ان کی برائیوں سے درگزر کرتے ہیں یہ بہشتیوں میں ہوں گے اس سچے وعدہ کی بناء پر جو برابر ان سے کیا جاتا رہا ہے۔'),
(4527, 46, 17, 'اور جس نے اپنے والدین سے کہا تُف ہے تم پر کیا تم دونوں مجھ سے وعدہ کرتے ہو کہ میں (دوبارہ قبر سے) نکالا جاؤں گا۔ حالانکہ مجھ سے پہلے کئی نسلیں گزر چکی ہیں (اور کوئی ایک بھی زندہ نہ ہوا) اور وہ دونوں اللہ سے فریاد کرتے ہیں (اور کہتے ہیں) وائے ہو تجھ پر ایمانلے آ۔ بےشک اللہ کا وعدہ سچاہے(مگر) وہ کہتا ہے کہ یہ سب اگلے لوگوں کی (پرانی) کہانیاں ہیں۔'),
(4528, 46, 18, 'یہ وہ لوگ ہیں جن پر اللہ کے قول (عذاب) کا فیصلہ نافذ ہو چکا ہے۔ ان گروہوں کے ساتھ جو جنوں اور انسانوں میں سے پہلے گزر چکے ہیں۔یقیناً وہ لوگ گھاٹے میں تھے۔'),
(4529, 46, 19, 'اور ہر ایک کیلئے ان کے اعمال کے مطابق درجے ہوں گے تاکہ ان کو ان کے اعمال کا پورا پورا بدلہ مل جائے اور ان پر ظلم نہیں کیا جائے گا۔'),
(4530, 46, 20, 'اور جس دن کافروں کو دوزخ کے سامنے لایا جائے گا (تو ان سے کہا جائے گا کہ) تم اپنے حصے کی نعمتیں دنیاوی زندگی میںلے چکے اور ان سے فائدہ اٹھا چکے آج تمہیں ذلت کا عذاب دیا جائے گا بوجہ اس کے کہ تم زمین میں ناحق تکبر کرتے تھے اور نافرمانیاں کرتے تھے۔'),
(4531, 46, 21, '(اے رسول(ص)) قومِ عاد کے بھائی (جنابِ ہود(ع)) کاذکر کیجئے جبکہ انہوں نے اپنی قوم کو مقامِ احقاف (ریت کے ٹیلوں والی بستی) میں ڈرایا تھا جبکہ بہت سے ڈرانے والے ان سے پہلے بھی گزر چکے اوران کے بعد بھی (جنابِ ہود(ع) نے کہا کہ) اللہ کے سوا کسی کی عبادت نہ کرو (ورنہ) مجھے تمہارے بارے میں ایک بڑے (ہولناک) دن کے عذاب کا اندیشہ ہے۔'),
(4532, 46, 22, 'ان قوم والوں نے کہا کہ کیا تم اس لئے ہمارے پاس آئے ہو کہ ہمیں اپنے معبودوں سے منحرف کردو۔ لاؤ ہمارے پاس وہ (عذاب) جس کی دھمکی تم ہمیں دیتے ہواگر تم سچے ہو۔'),
(4533, 46, 23, 'جنابِ ہود(ع) نے کہا اس (نزولِ عذاب) کا علم تو بس اللہ کے پاس ہے میں تو صرف تم تک وہ (پیغام) پہنچا رہا ہوں جس کے ساتھ میں بھیجا گیا ہوں۔ لیکن میں تمہیں دیکھ رہا ہوں کہ تم بالکل جاہل قوم ہو۔'),
(4534, 46, 24, 'پس جب انہوں نے (عذاب) کو ایک بادل کی صورت میں اپنی وادیوں کی طرف آتے ہوئے دیکھا تو کہنے لگے کہ یہ بادل تو ہم پر برسنے والا ہے بلکہ یہ وہ (عذاب) ہے جس کی تم جلدی کر رہے تھے (یہ تند و تیز) ہوا ہے جس میں دردناک عذاب ہے۔'),
(4535, 46, 25, 'وہ اپنے پروردگار کے حکم سے ہر چیز کو تباہ و برباد کر دے گی۔ چنانچہ وہ ایسے ہو گئے کہ ان کے مکانوں کے سوا وہاں کچھ نظر نہیں آتا تھا ہم مجرم قوم کو اسی طرح سزا دیا کرتے ہیں۔'),
(4536, 46, 26, 'ہم نے ان لوگوں کو ان کاموں کی قدرت دی تھی جو تمہیں بھی نہیں دی اور ہم نے ان کو کان، آنکھیں اور دل دیئے تھے مگر ان کے کانوں اور آنکھوں اور دلوں نے کچھ بھی فائدہ نہ پہنچایا کیونکہ وہ آیاتِ الہیہ کا انکار کیا کرتے تھے اور ان کو اس چیز نے گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے۔'),
(4537, 46, 27, 'اور ہم نے تمہارے اردگرد کی کئی بستیوں کو ہلاک و برباد کر دیا اور ہم نے اپنی (قدرت کی) نشانیاں پھیر پھیر کر(مختلف انداز سے) پیش کیں تاکہ وہ باز آجائیں۔'),
(4538, 46, 28, 'تو انہوں نے ان کی مدد کیوں نہ کی جن کو انہوں نے خدا کے تقرب کیلئے معبود بنا رکھا تھا بلکہ وہ تو ان سے غائب ہو گئے، یہ تھا ان کا جھوٹ اور ان کا بہتان جو وہ باندھتے تھے۔'),
(4539, 46, 29, 'اور (وہ وقت یاد کرو) جب ہم نے جنات کے کچھ افراد کو آپ(ص) کی طرف متوجہ کیا تاکہ وہ غور سے قرآن سنیں۔ پس وہ اس جگہ پہنچے (جہاں قرآن پڑھا جا رہاتھا) تو (آپس میں کہنے لگے) خاموش ہو کر سنو پس جب (قرآن) پڑھا جا چکا تو وہ اپنی قوم کی طرف ڈرانے والے بن کر لوٹے۔'),
(4540, 46, 30, 'انہوں نے کہا اے ہماری قوم! ہم نے ایک کتاب سنی ہے جو موسٰی (ع)ٰ کے بعد نازل کی گئی ہے جو پہلی کتابوں کی تصدیق کرتی ہے (اور جو) حق اور سیدھے راستے کی طرف راہنمائی کرتی ہے۔'),
(4541, 46, 31, 'اے ہماری قوم! اللہ کیطرف بلانے والے کی دعوت پر لبیک کہو اور اس پر ایمانلے آؤ۔ اللہ تمہارے گناہ معاف کر دے گا اور تمہیں دردناک عذاب سے پناہ دے گا۔'),
(4542, 46, 32, 'اور جو کوئی اللہ کی طرف بلانے والے کی آواز پر لبیک نہیں کہے گا تو وہ زمین میں اللہ کو عاجز نہیں کرسکتا (اس کے قابو سے باہر نہیں نکل سکتا) اور نہ اللہ کے سوا اس کے لئے کوئی مددگار و سازگار ہے ایسے لوگ کھلی ہوئی گمراہی میں ہیں۔'),
(4543, 46, 33, 'کیا ان لوگوں نے نہیں دیکھا کہ اللہ نے آسمانوں اور زمین کو پیدا کیا ہے اور ان کے پیدا کرنے سے تھکا نہیں ہے وہ اس پر قادر ہے کہ مُردوں کو زندہ کرے۔ ہاں بےشک وہ ہر چیز پر بڑی قدرت رکھتا ہے۔'),
(4544, 46, 34, 'اور جس دن کافر دوزخ کے سامنے پیش کئے جائیں گے (ان سے کہا جائے گا) کیا یہ حقیقت نہیں ہے؟ وہ کہیں گے کہ ہاں ہمیں قَسم ہے اپنے پروردگار کی! ارشاد ہوگا اپنے کفر و انکارکی پاداش میں عذاب کا مزہ چکھو۔'),
(4545, 46, 35, '(اے رسول(ص)) آپ(ص) اسی طرح صبر کریں جس طرح اولوالعزم رسولوں(ع) نے صبر کیا ہے اور ان کیلئے جلدی نہ کریں جس دن وہ دیکھیں گے (وہ عذاب) جس کا ان سے وعدہ کیا جا رہا ہے تو وہ محسوس کریں گے کہ وہ (دنیا میں) دن کی ایک گھڑی سے زیادہ نہیں رہے۔ بس یہ پیغام پہنچا دینا ہے۔ انجامِ کار ہلاک و برباد وہی ہوں گے جو فاسق و فاجر ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'جن لوگوں نے کفر کیا اور (دوسرے لوگوں کو بھی) اللہ کے راستے سے روکا اس (اللہ) نے ان کے اعمال کو رائیگاں کر دیا۔'),
(4547, 47, 2, 'اور جو لوگ ایمان لائے اور نیک اعمال بھی کئے اور اس (قرآن) پر بھی ایمان لائے جو (حضرت) محمد (ص) پر نازل کیا گیا ہے جو کہ ان کے پروردگار کی طرف سے حق ہے تو اللہ نے ان کی برائیوں کا کفارہ ادا کر دیا (برائیاں دور کر دیں) اور انکی حالت کو درست کر دیا۔'),
(4548, 47, 3, 'یہ اس لئے ہوا کہ جن لوگوں نے کفر کیا انہوں نے باطل کی پیروی کی اور جو ایمان لائے انہوں نے اپنے پروردگار کی طرف سے حق کی پیروی کی اسی طرح خدا لوگوں کے حالات و اوصاف بیان کرتا ہے۔'),
(4549, 47, 4, 'پس جب کافروں سے تمہاری مڈبھیڑ ہوجائے تو ان کی گردنیں اڑاؤ۔ یہاں تک کہ جب خوب خون ریزی کر چکو (خوب قتل کر لو) تو پھر ان کو مضبوط باندھ لو اس کے بعد (تمہیں اختیار ہے)یا تو احسان کرو (رہا کردو)یا فدیہلے لو یہاں تک کہ جنگ میں اپنے ہتھیار ڈال دے (یہ حکم) اسی طرح ہے اور اگر خدا چاہتا تو خود ہی ان سے انتقاملے لیتا لیکن وہ تم لوگوں کے بعض کو بعض سے آزمانا چاہتا ہے اور جو لوگ اللہ کی راہ میں قتل ہو گئے تو اللہ ان کے اعمال کو ہرگز ضائع نہیں کرے گا۔'),
(4550, 47, 5, 'اور (منزلِ مقصود کی طرف) ان کی راہنمائی کرے گا اور ان کی حالت کو درست کرے گا۔'),
(4551, 47, 6, 'اور ان کو بہشت میں داخل کرے گا جس کا وہ ان سے تعارف کرا چکا ہے۔'),
(4552, 47, 7, 'اے ایمان والو! اگر تم اللہ کی مدد کرو گے تو اللہ تمہاری مدد کرے گا (اور تمہیں ثابت قدم رکھے گا)۔'),
(4553, 47, 8, 'اور جن لوگوں نے کفر اختیار کیا تو ان کیلئے ہلاکت ہے اور اللہ نے ان کے اعمال کو رائیگاں کر دیا۔'),
(4554, 47, 9, 'یہ اس لئے ہے کہ انہوں نے اسے ناپسند کیا جو اللہ نے نازل کیا ہے۔ پس اس (اللہ) نے ان کے سب اعمال اکارت کر دئیے۔'),
(4555, 47, 10, 'کیا یہ لوگ زمین میں چلتے پھرتے نہیں کہ دیکھتے کہ ان لوگوں کا انجام کیا ہوا جو ان سے پہلے تھے اللہ نے ان کو ہلاک کر دیا اور ان کافروں کیلئے بھی ایسے ہی انجام ہیں۔'),
(4556, 47, 11, 'یہ اس لئے ہے کہ اللہ اہلِ ایمان کا سرپرست ہے اور جو کافر ہیں ان کا کوئی سرپرست اور کارساز نہیں ہے۔'),
(4557, 47, 12, 'بےشک اللہ ان لوگوں کو جو ایمان لائے اور نیک عمل کئے ان بہشتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور جن لوگوں نے کفر کیا وہ (دنیا میں) بہرہ مند ہو رہے ہیں (عیش کر رہے ہیں) اور اس طرح کھا رہے ہیں جس طرح چوپائے کھاتے ہیں اور (ان کا آخری) ٹھکانہ دوزخ ہے۔'),
(4558, 47, 13, 'اور کتنی ہی ایسی بستیاں تھیں جو تمہاری اس بستی سے زیادہ طاقتور تھیں جس نے تمہیں نکالا ہے کہ ہم نے انہیں ہلاک کر دیا پس ان کا کوئی مددگار نہ تھا۔'),
(4559, 47, 14, 'کیا وہ جو اپنے پروردگار کی طرف سے واضح دلیل پر ہے اس شخص کی طرح ہو سکتا ہے جس کی بدعملی اس کی نگاہ میں خوشنما بنا دی گئی ہے اور وہ اپنی خواہشات کی پیروی کرتے ہیں؟'),
(4560, 47, 15, 'وہ جنت جس کا پرہیزگاروں سے وعدہ کیا گیا ہے اس کی صفت یہ ہے کہ اس میں ایسے پانی کی نہریں ہیں جس میں تغیر نہیں ہوتا اور ایسے دودھ کی نہریں ہیں جس کا مزہ نہیں بدلتا اور ایسے شراب کی نہریں ہیں جو پینے والوں کے لئے لذیذ ہے اور ایسے شہد کی نہریں ہیں جو صاف شفاف ہے اور ان کیلئے وہاں ہر قسم کے پھل ہیں اور ان کے پروردگار کی طرف سے بخشش ہے کیا ایسے (پرہیزگار) لوگ ان لوگوں جیسے ہو سکتے ہیں جو ہمیشہ دوزخ میں رہیں گے اور ان کو ایسا گرم پانی پلایا جائے گا جو ان کی آنتوں کو ٹکڑے ٹکڑے کر دے گا۔'),
(4561, 47, 16, 'اور ان میں سے ایسے لوگ بھی ہیں جو (بظاہر) تمہاری طرف کان لگا کر (بات سنتے ہیں) یہاں تک کہ جب آپ کے پاس سے باہر جاتے ہیں تو ان (خاص) لوگوں سے پوچھتے ہیں جن کو علم عطا کیا گیا ہے کہ انہوں (رسول(ص)) نے ابھی کیا کہاتھا؟ یہی وہ لوگ ہیں جن کے دلوں پر خدا نے مہر لگا دی ہے اور اپنی خواہشات کی پیروی کرتے ہیں۔'),
(4562, 47, 17, 'اور جن لوگوں نے ہدایت طلب کی ہے اللہ ان کی ہدایت میں اور اضافہ کرتا ہے اور ان کو (ان کے حصے کی) پرہیزگاری عطا کرتا ہے۔'),
(4563, 47, 18, 'سو یہ لوگ تو بس اب قیامت کا انتظار کر رہے ہیں کہ اچانک ان پر آجائے تو اس کے آثار و علا مات تو آہی چکے ہیں اور جب وہ آجائے گی تو پھر ان کو نصیحت حاصل کرنے کاموقع کہاں رہے گا؟'),
(4564, 47, 19, '(اے رسول(ص)) خوب جان لیں کہ اللہ کے سوا کوئی خدانہیں ہے اور خدا سے اپنے ذنب اور مؤمن مردوں اور مؤمن عورتوں کی مغفرت طلب کریں۔ اللہ تمہاری گردش (چلنے پھر نے) کی جگہ کو اور تمہارے ٹھکانے کو جانتا ہے۔'),
(4565, 47, 20, 'اور جو لوگ ایمان لائے ہیں وہ کہتے ہیں کہ (جہاد کے بارے میں) کوئی سورہ کیوں نازل نہیں کی جاتی؟ پس جب ایک محکم سورہ نازل کی جاتی ہے اور اس میں قتل و قتال کا ذکر ہوتا ہے تو آپ(ص) ان لوگوں کو دیکھئے گا کہ جن کے دلوں میں (نفاق کی) بیماری ہے وہ آپ(ص) کی طرف اس طرح دیکھتے ہیں جس طرح وہ شخص دیکھتا ہے جس پر موت کی غشی طاری ہو۔'),
(4566, 47, 21, 'ان کے لئے بہتر تو یہ تھا کہ اطاعت کرتے اور اچھی بات کہتے اور جب (جہاد کا) قطعی فیصلہ ہو جاتا تو اگر یہ اللہ سے (اپنے عہد میں) سچے ثابت ہوتے تو ان کے لئے بہتر ہوتا۔'),
(4567, 47, 22, 'پھرتم سے یہی توقع ہے کہ اگر تم حاکم بن جاؤ (تمہیں اقتدار مل جائے) تو تم زمین میں فساد برپا کرو۔اور اپنی قرابتوں کو قطع کرو۔'),
(4568, 47, 23, 'یہی وہ لوگ ہیں جن پر اللہ نے لعنت کی ہے اور انہیں بہرا بنا دیا ہے اور ان کی آنکھوں کو اندھا کر دیا ہے۔'),
(4569, 47, 24, 'کیا یہ لوگ قرآن میں غور و فکر نہیں کرتے؟ یا ان کے دلوں پر تالے لگے ہوئے ہیں۔'),
(4570, 47, 25, 'جو لوگ پیٹھ پھیر کر (کفر کی طرف) پیچھے پلٹ گئے بعد اس کے کہ ان پر ہدایت واضح ہو گئی تھی۔ شیطان نے انہیں فریب دیا اورانہیں دور دور کی سجھائی۔'),
(4571, 47, 26, 'یہ اس لئے ہے کہ انہوں نے ان لوگوں سے کہا جو اللہ کی نازل کردہ (کتاب) کو ناپسند کرتے ہیں کہ ہم بعض معاملات میں تمہاری بات مان لیں گے اللہ ان کی رازداری کو جانتا ہے۔'),
(4572, 47, 27, 'اس وقت ان کا کیا حال ہوگا جب فرشتے ان کی روحیں قبض کریں گے اور ان کے چہروں اور پشتوں پر مارتے ہوں گے۔'),
(4573, 47, 28, 'یہ سب اس وجہ سے ہوگا کہ انہوں نے اس چیز کی پیروی کی جو اللہ کی ناراضی کا با عث تھی اور اس کی خوشنودی کو ناپسند کیا پس اللہ نے ان کے اعمال کو اکارت کر دیا۔'),
(4574, 47, 29, 'کیا وہ لوگ جن کے دلوں میں بیماری ہے یہ خیال کرتے ہیں کہ اللہ کبھی ان کے (سینوں کے) کینوں کو ظاہر نہیں کرے گا۔'),
(4575, 47, 30, 'اگر ہم چاہیں تو وہ لوگ آپ کو دکھا دیں اور آپ انہیں علامتوں سے پہچان لیں اور (خاص کر) آپ انہیں ان کے اندازِ گفتگو سے تو ضرور پہچان لیں گے اور اللہ تم سب کے اعمال کو جانتا ہے۔'),
(4576, 47, 31, 'اور ہم ضرور تم لوگوں کو آزمائیںگے تاکہ ہم تم میں سے جہاد کرنے والوں اور ثابت قدم رہنے والوں کو معلوم (و ممیز) کریں اور تمہارے حالات کی جانچ کر لیں۔'),
(4577, 47, 32, 'بےشک جن لوگوں نے کفر کیا اور (دوسرے لوگوں کو) اللہ کی راہ سے روکا اور پیغمبر کی مخالفت کی بعد اس کے کہ ان پر ہدایت واضح ہو چکی تھی وہ اللہ کو ہرگز کوئی نقصان نہیں پہنچا سکیں گے (ہاں البتہ) اللہ ان کے اعمال کو اکارت کر دے گا۔'),
(4578, 47, 33, 'اے ایمان والو! اطاعت کرو اللہ کی اور اطاعت کرو رسول(ص) کی اور (ان کی مخالفت کر کے) اپنے اعمال کو برباد نہ کرو۔'),
(4579, 47, 34, 'بےشک جن لوگوں نے کفر کیا اور (لوگوں کو) اللہ کے راستے سے روکا اور پھر وہ کفر ہی کی حالت میں مر گئے اللہ ان کو کبھی نہیں بخشے گا۔'),
(4580, 47, 35, 'پس تم کمزور نہ پڑو (اور ہمت نہ ہارو) اور (دشمن کو) صلح کی دعوت نہ دو حالانکہ تم ہی غالب رہو گے اور اللہ تمہارے اعمال (کے ثوا ب) میں ہرگز کمی نہیں کرے گا۔'),
(4581, 47, 36, 'یہ دنیا کی زندگی تو بس کھیل اور تماشا ہے اگر تم ایمان لاؤ اور تقویٰ اختیار کرو تو وہ (خدا) تمہیں تمہارے اجر عطا کرے گا اور تم سے تمہارے مال نہیں مانگے گا۔'),
(4582, 47, 37, 'اور اگر وہ تم سے وہ (مال) مانگے اورپھر اصرار بھی کرے تو تم بخل کرو گے اور وہ تمہارے دلی کینوں (اور دلی ناگواریوں) کو ظاہر کر دے گا۔'),
(4583, 47, 38, 'ہاں تم ہی ایسے لوگ ہو کہ تمہیں دعوت دی جاتی ہے کہ اللہ کی راہ میں خرچ کرو تو تم میں سے کچھ لوگ بُخل کرنے لگتے ہیں اور جو بُخل کرتا ہے وہ (دراصل) اپنے ہی نفس سے بُخل کرتا ہے اور اللہ بےنیاز ہے البتہ تم (اس کے) محتاج ہو اور اگر تم رُوگردانی کروگے تو وہ تمہاری جگہ دوسری قوم کولے آئے گا پھر وہ تم جیسے نہیں ہوں گے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, '(اے رسول(ص)) بےشک ہم نے آپ(ص) کو ایک فتحِ مبین (نمایاں فتح) عطا کی ہے۔'),
(4585, 48, 2, 'تاکہ اللہ ان (لوگوں کی نظر میں) آپ(ص) کے تمام اگلے اور پچھلے گناہ بخش دے اور آپ پر اپنی نعمت تام و تمام کرے اور آپ(ص) کو منزلِ مقصود تک پہنچا ئے۔'),
(4586, 48, 3, 'اور تاکہ اللہ آپ کی زبردست مدد کرے۔'),
(4587, 48, 4, 'وہ (اللہ) وہی ہے جس نے اہلِ ایمان کے دلوں میں سکون و اطمینان اتارا تاکہ وہ اپنے (پہلے) ایمان کے ساتھ ایمان میں اور بڑھ جائیں اور آسمانوں اور زمین کے سب لشکر اللہ ہی کے ہیں اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(4588, 48, 5, '(یہ اس لئے ہے) تاکہ وہ مؤمن مَردوں اور مؤمن عورتوں کو ایسے بہشتوں میں داخل کرے جن کے نیچے نہریں رواں دواں ہیں اور وہ ان میں ہمیشہ رہیں گے اور تاکہ ان کی برائیاں ان سے دور کر دے (ان کا کفارہ کر دے) اور یہ اللہ کے نزدیک بڑی کامیابی ہے۔'),
(4589, 48, 6, 'اور تاکہ وہ (اللہ) منافق مَردوں اور منافق عورتوں، مشرک مَردوں اور مشرک عورتوں کو سزا دے جو اللہ کے بارے میں برے گمان کرتے ہیں برائی کی گردش انہی پر ہے اللہ ان پر غضبناک ہے اور ان پر لعنت کی ہے اور ان کیلئے جہنم تیار کر رکھی ہے اور وہ بہت برا انجام ہے۔'),
(4590, 48, 7, 'اور آسمانوں اور زمین کے تمام لشکر اللہ ہی کے ہیں اور خدا (سب پر) غالب اور بڑا حکمت والا ہے۔'),
(4591, 48, 8, '(اے رسول(ص)) بےشک ہم نے آپ(ص) کو گواہ بنا کر اور بشارت دینے والا اور ڈرانے والا بنا کر بھیجا ہے۔'),
(4592, 48, 9, 'تاکہ تم لوگ اللہ پر اور اس کے رسول(ص) پر ایمان لاؤ اور رسول(ص) کی مدد کرو اور اس کی تعظیم و توقیر کرو اورصبح و شام اس (اللہ) کی تسبیح کرو۔'),
(4593, 48, 10, '(اے رسول(ص)) جو لوگ آپ سے بیعت کرتے ہیں وہ (دراصل) اللہ کی بیعت کرتے ہیں اللہ کا ہاتھ ان کے ہاتھوں کے اوپر ہے پس جو (اس عہد کو) توڑے گا تو اس کے توڑنے کا وبال اسی کی ذات پر ہوگا اور جو اس عہد کو پورا کرے گا جو اس نے اللہ سے کیا ہے تو اللہ اسے بڑا اجر عطا کرے گا۔'),
(4594, 48, 11, 'صحرائی عربوں میں سے جو (سفرِ حدیبیہ میں) پیچھے چھوڑ دئیے گئے تھے وہ عنقریب کہیں گے کہ ہمارے مال اور اہل و عیال نے ہمیں مشغول رکھا (اس لئے آپ(ص) کے ہمراہ نہ جا سکے) تو آپ(ص) ہمارے لئے (خدا سے) بخشش کی دعا کیجئے! وہ اپنی زبانوں سے وہ بات کہتے ہیں جو ان کے دلوں میں نہیں ہے آپ(ص) کہیے! کون ہے جو اللہ کے مقابلے میں تمہارے لئے کچھ اختیار رکھتا ہے؟ اگر وہ تمہیں نقصان پہنچانا چاہے یانفع پہنچانا چاہے بلکہ تم جو کچھ کرتے ہو اللہ اس سے خوب باخبر ہے۔'),
(4595, 48, 12, '(اصل بات یہ ہے کہ) تم نے خیال کیا تھا کہ رسول(ص) اور اہلِ ایمان اب کبھی بھی اپنے گھر والوں کی طرف لوٹ کر نہیں آئیں گے اور یہ بات تمہارے دلوں میں خوشنما بنا دی گئی اور تم نے بہت برے گمان کئے (اسی طرح) تم برباد ہو نے والی جماعت بن گئے۔'),
(4596, 48, 13, 'اور جو کوئی اللہ اور اس کے رسول(ص) پر ایمان نہ لائے تو ہم نے ان کافروں کیلئے بھڑکتی ہوئی آگ (دوزخ) تیار کر رکھی ہے۔'),
(4597, 48, 14, 'اور آسمانوں اور زمین کی بادشاہی اللہ ہی کیلئے ہے جس کو چاہے بخش دے اور جس کو چاہے عذاب دے اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(4598, 48, 15, 'جب تم (جنگِ خیبر میں) غنیمتیں حاصل کرنے کیلئے چلوگے تو جو پیچھے چھوڑ دئیے گئے وہ ضرور کہیں گے کہ ہمیں بھی اجازت دو کہ ہم آپ کے پیچھے آئیں وہ چاہتے ہیں کہ اللہ کی بات کو بدل دیں۔ آپ(ص) کہہ دیجئے! کہ تم اب کبھی ہمارے پیچھے نہیں آسکتے! اللہ پہلے ہی ایسی بات کہہ چکا ہے اس پر وہ ضرور کہیں گے کہ بلکہ تم ہم سے حسد کرتے ہو بلکہ یہ (کم عقل) بہت ہی کم بات سمجھتے ہیں۔'),
(4599, 48, 16, 'آپ(ص) ان پیچھے والے صحرائی عربوں سے کہیے! عنقریب تمہیں ایک سخت جنگجو قوم کے خلاف جہاد کیلئے بلایا جائے گا تم ان سے لڑوگےیا وہ اسلام لائیں گے پس اگر تم نے اطاعت کی تو اللہ تمہیں بہت اچھا اجر عطا کرے گا۔ اور اگر تم نے اسی طرح منہ موڑا جس طرح پہلے منہ موڑا تھا تو وہ تمہیں (دردناک) عذاب دے گا۔'),
(4600, 48, 17, 'اندھے پر کوئی مضائقہ نہیں ہے اورنہ ہی لنگڑے پر کوئی حرج ہے اور نہ مریض پر کوئی گناہ اور جو خدا اور رسول(ص) کی اطاعت کرے گا۔ تو اللہ اسے ان بہشتوں میں داخل کرے گا جن کے نیچے نہریں رواں دواں ہیں اور جو رُوگردانی کرے گا تو اسے دردناک عذاب دے گا۔'),
(4601, 48, 18, 'بےشک اللہ مؤمنین سے راضی ہوا جب کہ وہ درخت کے نیچے آپ(ص) سے بیعت کر رہے تھے تو اس نے جان لیا جو کچھ ان کے دلوں میں تھا۔ پس اس نے ان پر سکون و اطمینان نازل کیا اور انہیں انعام میں ایک قریبی فتح عنا یت فرمائی۔'),
(4602, 48, 19, 'اور بہت سی غنیمتیں بھی جن کو وہ (عنقریب) حاصل کریں گے اللہ (ہر چیز) پرغالب ہے، بڑا حکمت والا ہے۔'),
(4603, 48, 20, 'اللہ نے تم سے (اور بھی) بہت سی غنیمتوں کا وعدہ کیا ہے جن کو تم حاصل کرو گے پس یہ (فتحِ خیبر) تو اس نے فوری طور پر تمہیں دے دی اور لوگوں کے ہاتھ تم سے روک دئیے تاکہ یہ مؤمنین کیلئے (ہماری قدرت و نصرت) کی ایک نشانی ہو جائے اور وہ تمہیں سیدھے راستے کی طرف رہنمائی کرے (اس پر چلائے)۔'),
(4604, 48, 21, 'اور غنیمتیں بھی ہیں جن پر تم ابھی قادر نہیں ہوئے اور اللہ نے ان کو گھیر رکھا ہے (اس کے قبضہ میں ہیں) اور اللہ ہر چیز پر پوری طرح قادر ہے۔'),
(4605, 48, 22, 'اور اگر (یہ) کافر لوگ تم سے جنگ کرتے تو ضرور پیٹھ پھیر کر بھاگ جاتے اور پھر وہ (اپنے لئے) کوئی حامی و مددگار نہ پاتے۔'),
(4606, 48, 23, 'یہی اللہ کا دستور ہے جوپہلے سے چلا آرہا ہے اور تم اللہ کے دستور میں ہرگز کوئی تبدیلی نہیں پاؤ گے۔'),
(4607, 48, 24, 'اور وہ (اللہ) وہی ہے جس نے مکہ کی وادی میں روک دئیے تھے ان کے ہاتھ تم سے اور تمہارے ہاتھ ان سے بعد اس کے کہ تمہیں ان پر غلبہ عطا کر دیا تھا اور تم جو کچھ کر رہے ہو اللہ اسے خوب دیکھ رہا ہے۔'),
(4608, 48, 25, 'یہ وہی لوگ ہیں جنہوں نے کفر کیا اور تمہیں مسجد الحرام سے روکا اور قربانی کے جانوروں کو بھی روکے رکھا کہ وہ اپنی قربانی کی جگہ نہ پہنچ سکے اور اگر (مکہ میں) ایسے مؤمن مرد اور مؤمن عورتیں نہ ہوتیں جن کو تم نہیں جانتے (اور یہ خوف نہ ہوتا کہ) تم انہیں لاعلمی میں روند ڈالوگے اور پھر ان کی وجہ سے تم پر الزام آتا (تو جنگ نہ روکی جاتی) مگر روکی اس لئے گئی تاکہ اللہ جسے چاہے اپنی رحمت میں داخل کرے اگر وہ (اہلِ ایمان) الگ ہو جاتے تو ہم ان (اہلِ مکہ) میں سے کافروں کو دردناک سزا دیتے۔'),
(4609, 48, 26, '(وہ وقت یاد کرو) جب کافروں نے اپنے دلوں میں عصبیت پیدا کی اور عصبیت بھی جاہلیت والی اللہ نے اپنا سکون و اطمینان اپنے رسول(ص) اور اہلِ ایمان پر نازل فرمایا اور انہیں پرہیزگاری کی بات کا پابند رکھا کہ وہی اس کے زیادہ حقدار تھے اور اس کے اہل بھی اور اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(4610, 48, 27, '(اللہ) اس بات کو جانتا تھا جو تم نہیں جانتے تھے تو اس نے اس (تعبیرِ خواب) سے پہلے تمہیں ایک قریبی فتح عطا کر دی۔'),
(4611, 48, 28, 'وہ (اللہ) وہی ہے جس نے اپنے رسول(ص) کو ہدایت اور دینِ حق کے ساتھ بھیجا تاکہ اس کو تمام دینوں پر غالب کر دے اور (اس بات کی) گواہی کیلئے اللہ کافی ہے۔'),
(4612, 48, 29, 'محمد (ص) اللہ کے رسول ہیں اور جو لوگ ان کے ساتھ ہیں وہ کافروں پر سخت ہیں اور آپس میں مہربان ہیں تم انہیں دیکھو گے کہ وہ (کبھی) رکوع (اور کبھی) سجود کر رہے ہیں (اور) اللہ کے فضل و کرم اور اس کی خوشنودی کے طلبگار ہیں ان کی علامت ان کے چہروں پر سجدہ کے نشان سے نمایاں ہے ان کی توراۃ میں اور انجیل میں توصیف یوں ہے جیسے ایک کھیتی جس نے اپنی کونپل نکالی پھر اس کو تقویت دی اور وہ مضبوط و موٹی ہوئی پھر اپنے تنے پر کھڑی ہوگئی وہ کسانوں کو خوش کرتی ہے تاکہ ان کے ذریعہ سے کفار کے دل جلائے اللہ نے ان لوگوں سے جو ایمان لائے اور نیک عمل کئے، مغفرت اور بڑے اجرو ثواب کا وعدہ کر رکھا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اے ایمان والو! تم (کسی کام میں) خدا اور رسول(ص) سے آگے نہ بڑھو (سبقت نہ کرو) اور اللہ (کی نافرمانی) سے ڈرو۔ بےشک اللہ بڑا سننے والا، بڑا جاننے والا ہے۔'),
(4614, 49, 2, 'اے ایمان والو! تم اپنی آوازوں کو نبی(ص) کی آواز پر بلند نہ کیا کرو جس طرح تم آپس میں ایک دوسرے کے ساتھ اونچی آواز میں کرتے ہو کہیں ایسا نہ ہو کہ تمہارے اعمال اکارت ہو جائیں اورتمہیں خبر بھی نہ ہو۔'),
(4615, 49, 3, 'جو لوگ رسولِ خدا(ص) کے پاس (بات کرتے ہوئے) اپنی آوازیں پست رکھتے ہیں یہی وہ لوگ ہیں جن کے دلوں کو اللہ نے پرہیزگاری کیلئے آزمایا ہے ان کے لئے مغفرت اور اجرِ عظیم ہے۔'),
(4616, 49, 4, 'اے رسول(ص)) جو لوگ آپ کو حجروں میں باہر سے پکارتے ہیں ان میں سے اکثر عقل سے کام نہیں لیتے۔'),
(4617, 49, 5, 'اور اگر وہ اتنا صبر کرتے کہ آپ خود ان کے پاس باہر نکل کر آجائیں تو یہ ان کیلئے بہتر ہوتا اور اللہ بڑا بخشنے والا اور بڑا رحم کرنے والا ہے۔'),
(4618, 49, 6, 'اے ایمان والو! اگر کوئی فاسق تمہارے پاس کوئی خبر لائے تو تحقیق کر لیا کرو کہیں ایسا نہ ہو کہ تم کسی قوم کو لاعلمی میں نقصان پہنچا دو اور پھر اپنے کئے پر پچھتاؤ۔'),
(4619, 49, 7, 'اور خوب جان لو کہ تمہارے درمیان رسولِ خدا(ص) موجود ہیں اگر وہ بہت سے معاملات میں تمہاری بات مان لیا کریں تو تم زحمت و مشقت میں پڑ جاؤ گے لیکن اللہ نے ایمان کو تمہارا محبوب بنایا ہے اور اسے تمہارے دلوں میں مرغوب بنایا (آراستہ کیا) اور کفر، فسق اور نافرمانی کو تمہارے نزدیک مبغوض بنایا (اور تم کو ان سے متنفر کیا) یہی لوگ راہِ راست پر ہیں۔'),
(4620, 49, 8, 'اللہ کے فضل و کرم اور اس کے انعام و احسان سے اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(4621, 49, 9, 'اور اگر مسلمانوں کے دو گروہ آپس میں لڑ پڑیں تو تم ان کے درمیان صلح کراؤ پھر اگر ان میں سے ایک گروہ دوسرے پر تعدی و زیادتی کرے تو تم سب ظلم وزیادتی کرنے والے سے لڑو۔ یہاں تک کہ وہ حکمِ الٰہی کی طرف لوٹ آئے پس اگر وہ لوٹ آئے تو پھر تم ان دونوں کے درمیان عدل و انصاف کے ساتھ صلح کرا دو اور انصاف کرو۔ بےشک اللہ انصاف کرنے والوں سے محبت کرتا ہے۔'),
(4622, 49, 10, 'بےشک اہلِ اسلام و ایمان آپس میں بھائی بھائی ہیں تو تم اپنے دو بھائیوں میں صلح کراؤ اور اللہ (کی عصیاں کاری) سے ڈرو تاکہ تم پر رحم کیا جائے۔'),
(4623, 49, 11, 'اے ایمان والو! کوئی قوم کسی قوم کامذاق نہ اڑائے ہو سکتا ہے کہ وہ ان سے بہتر ہوں اور نہ عورتیں عورتوں کامذاق اڑائیں ہو سکتا ہے کہ وہ ان سے بہتر ہوں اور نہ آپس میں ایک دوسرے کو طعنہ دو اور نہ ایک دوسرے کو برے القاب کے ساتھ پکارو۔ ایمان لانے کے بعد فاسق کہلانا کتنا برا نام ہے اور جو توبہ نہیں کریں گے (باز نہیں آئیں گے) وہی لوگ ظالم ہیں۔'),
(4624, 49, 12, 'اے ایمان والو! بہت سے گمانوں سے پرہیز کرو (بچو) کیونکہ بعض گمان گناہ ہوتے ہیں اور تجسس نہ کرو اور کوئی کسی کی غیبت نہ کرے کیا تم میں سے کوئی اس بات کو پسند کرے گا کہ وہ اپنے مُردہ بھائی کا گوشت کھا ئے؟ اس سے تمہیں کراہت آتی ہے اور اللہ (کی نافرمانی) سے ڈرو بےشک اللہ بڑا توبہ قبول کرنے والا، رحم کرنے والا ہے۔'),
(4625, 49, 13, 'اے لوگو! ہم نے تمہیں ایک مرد (آدم(ع)) اور ایک عورت (حوا(ع)) سے پیدا کیا ہے اور پھر تمہیں مختلف خاندانوں اور قبیلوں میں تقسیم کر دیا ہے تاکہ تم ایک دوسرے کو پہچان سکو۔ بےشک اللہ کے نزدیک تم میں سے زیادہ معزز و مکرم وہ ہے جو تم میں سے زیادہ پرہیزگار ہے یقیناً اللہ بڑا جاننے والا ہے، بڑا باخبر ہے۔'),
(4626, 49, 14, 'اعراب (صحرائی عرب) کہتے ہیں کہ ہم ایمان لائے ہیں ان سے کہئے کہ تم ایمان نہیں لائے بلکہ یوں کہو کہ ہم اسلام لائے ہیں اور ایمان تو ابھی تمہارے دلوں میں داخل ہوا ہی نہیں ہے اور اگر تم اللہ اور اس کے رسول(ص) کی اطاعت کرو گے تو وہ (اللہ) تمہارے اعمال سے کچھ بھی کمی نہیں کرے گا۔ بےشک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(4627, 49, 15, 'مؤمن تو پس وہی ہیں جو اللہ اور اس کے رسول پر ایمان لائے اور پھر کبھی شک نہیں کیا اور اپنے مالوں اور جانوں کے ساتھ اللہ کی راہ میں جہاد (بھی) کیا یہی لوگ سچے ہیں۔'),
(4628, 49, 16, 'آپ(ص) کہہ دیجئے کہ کیا تم اللہ کو اپنے دین سے آگاہ کرتے ہو؟ حالانکہ اللہ وہ کچھ جانتا ہے جو آسمانوں میں ہے اور جو زمین میں ہے اللہ ہر شے کو جانتا ہے۔'),
(4629, 49, 17, '(اے رسول(ص)) یہ لوگ اسلام لا کر آپ(ص) پر احسان جتاتے ہیں کہئے تم اپنے اسلام کا مجھ پر احسان نہ جتاؤ بلکہ تم پر اللہ کا احسان ہے کہ اس نے تمیں ایمان لانے کی ہدایت (توفیق) دی اگر تم (اپنے دعوائے ایمان میں) سچے ہو۔'),
(4630, 49, 18, 'بےشک اللہ آسمانوں اور زمین کے سب غیب جانتا ہے اور تم جو کچھ کر رہے ہواللہ وہ سب کچھ خوب دیکھ رہا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'ق۔'),
(4632, 50, 2, 'قَسم ہے باعظمت قرآن کی (پیغمبرِ اسلام (ص) میرے سچے رسول ہیں) لیکن ان لوگوں کو اس بات پر تعجب ہے کہ انہی میں سے ایک ڈرانے والا (رسول) ان کے پاسایا ہے تو کافر کہتے ہیں کہ یہ ایک عجیب چیز ہے۔'),
(4633, 50, 3, 'کیا جب ہم مر جائیں گے اور مٹی ہو جائیں گے (تو دوبارہ زندہ ہوں گے) یہ تو بڑی بعید (از عقل) بات ہے۔'),
(4634, 50, 4, 'ہم خوب جانتے ہیں (ان اجزاء کو) جو زمین (کھا کر) ان میں سے کم کرتی ہے اور ہمارے پاس تو ایک ایسی کتاب ہے جس میں (سب کچھ) محفوظ ہے۔'),
(4635, 50, 5, 'بلکہ (دراصل بات تو یہ ہے کہ) انہوں نے (دینِ) حق کو جھٹلایا جبکہ وہ ان کے پاسایا پس وہ ایک الجھی ہوئی بات میں مبتلا ہیں۔'),
(4636, 50, 6, 'کیا انہوں نے آسمان کی طرف نہیں دیکھا جو ان کے اوپر ہے کہ ہم نے اسے کس طرح بنایا ہے؟ اور آراستہ کیا ہے جس میں کوئی رخنہ نہیں ہے۔'),
(4637, 50, 7, 'اور ہم نے زمین کو پھیلایا اور اس میں پہاڑ گاڑ دئیے اور اس میں ہر قِسم کی خوشنما چیزیں اگائیں۔'),
(4638, 50, 8, 'ہر اس بندے کی بصیرت اور یاددہانی کے لئے جو اپنے رب کی طرف رجوع کرنے والا ہے۔'),
(4639, 50, 9, 'اور ہم نے آسمان سے برکت والا پانی اتارا اور اس سے باغات اور کاٹے جانے والی کھیتی کا غلہ اگایا۔'),
(4640, 50, 10, 'اور کھجور کے بلند وبالا درخت جن میں تہہ بہ تہہ خوشے لگتے ہیں۔'),
(4641, 50, 11, 'بندوں کی روزی کے لئے اور ہم اس مردہ زمین کو زندہ کرتے ہیں اسی طرح (مردوں کا) زمین سے نکلنا ہوگا۔'),
(4642, 50, 12, 'ان سے پہلے نوح(ع) کی قوم، اصحابِ رس اور ثمود نے جھٹلایا۔'),
(4643, 50, 13, 'نیز عاد، فرعون، لوط(ع) کے بھائی۔'),
(4644, 50, 14, 'اور ایکہ والے اور تبع کی قوم ان سب نے پیغمبروں(ع) کو جھٹلایا آخر میری وعید کے مستحق ہو ئے۔'),
(4645, 50, 15, 'کیا ہم پہلی بار کی پیدائش سے تھک گئے ہیں؟ (ایسا نہیں ہے) بلکہ یہ لوگ ازسرِنو پیدائش کے بارے میں شبہ میں مبتلا ہیں۔'),
(4646, 50, 16, 'بےشک ہم نے انسان کو پیدا کیا ہے اور ہم وہ وسوسے جانتے ہیں جو اس کا نفس ڈالتا ہے اور ہم اس کی شہ رگ (حیات) سے بھی زیادہ اس کے قریب ہیں۔'),
(4647, 50, 17, 'جبکہ دو اخذ کرنے والے (کراماً کاتبین) ایک دائیں جانب بیٹھا ہے اور دوسرا بائیں جانب (اور ہر چیز کو لکھ رہے ہیں)۔'),
(4648, 50, 18, 'وہ کوئی لفظ بھی نہیں بولتا مگر یہ کہ اس کے پاس نگران تیار موجود ہوتا ہے۔'),
(4649, 50, 19, 'اور موت کی بےہوشی حق کے ساتھ آپہنچی۔ یہی وہ چیز ہے جس سے تو گریز کرتا رہتا تھا۔'),
(4650, 50, 20, 'اور صور پھونکا جائے گا یہی وعید کا دن ہوگا۔'),
(4651, 50, 21, 'اور ہر شخص اس طرح آئے گا کہ اس کے ساتھ ہانکنے والا اور ایک گواہ ہوگا۔'),
(4652, 50, 22, 'اے غا فل) تو اس دن سے غفلت میں رہا تو (آج) ہم نے تیری آنکھوں سے تیرا پردہ ہٹا دیا سو آج تیری نگاہ بڑی تیز ہے۔'),
(4653, 50, 23, 'اور اس کا (زندگی بھر کا) ساتھی کہے گا کہ جو میرے پاس تھا وہ حاضر ہے۔'),
(4654, 50, 24, 'تم دونوں جہنم میں جھونک دو ہر بڑے کافر کو جو(حق سے) عناد رکھتا تھا۔'),
(4655, 50, 25, 'جو نیکی و خیرات سے بڑا روکنے والا، حد سے بڑھنے والا اور شک کرنے والا اور دوسروں کو شک میں ڈالنے والا تھا۔'),
(4656, 50, 26, 'جس نے اللہ کے ساتھ اوروں کو بھی خدا بنا رکھا تھا۔ پس اس کو سخت عذاب میں جھونک دو۔'),
(4657, 50, 27, 'اس کا ساتھی (شیطان) کہے گا اے ہمارے پروردگار! میں نے تو اسے سرکش نہیں بنایا تھا مگر وہ خود ہی سخت گمراہی میں تھا۔'),
(4658, 50, 28, 'ارشاد ہوگا میرے سامنے جھگڑا نہ کرو اور میں نے تو تمہیں پہلے عذاب سے ڈرایا تھا۔'),
(4659, 50, 29, 'میرے ہاں بات بدلی نہیں جاتی اور نہ ہی میں بندوں پر ظلم کرنے والا ہوں۔'),
(4660, 50, 30, 'وہ دن یاد کرو جس دن ہم جہنم سے کہیں گے کیا تو بھر گئی ہے؟ اور وہ کہے گی کیا کچھ اور بھی ہے؟'),
(4661, 50, 31, 'اور جنت پرہیزگاروں کے قریب کر دی جائے گی اور دور نہیں ہوگی۔'),
(4662, 50, 32, 'یہ (جنت) وہ ہے جس کا تم سے وعدہ کیا جاتا تھا (تم میں سے) ہر اس شخص کے لئے جو اللہ کی طرف رجوع کرنے والا (اور حدودِ الٰہی) کی بڑی حفاظت کرنے والا ہے۔'),
(4663, 50, 33, 'جو بِن دیکھے خدائے رحمٰن سے ڈرتا ہے اور ایسے دل کے ساتھایا ہے جو (خدا کی طرف) رجوع کرنے والا ہے۔'),
(4664, 50, 34, 'تم اس(جنت) میں سلامتی کے ساتھ داخل ہو جاؤ یہ (حیاتِ) ابدی کا دن ہے۔'),
(4665, 50, 35, 'وہاں ان کیلئے وہ سب کچھ ہوگا جو وہ چاہیں گے اور ہمارے پاس تو (ان کے لئے) اور بھی زیادہ ہے۔'),
(4666, 50, 36, 'اور ہم (کفارِ مکہ) سے پہلے کتنی ہی قوموں کو ہلاک کر چکے ہیں جو قوت و طاقت میں ان سے زیادہ طاقتور تھیں وہ (مختلف) شہروں میں گھو متے پھرتے تھے تو ان کو کوئی جائے پناہ ملی؟'),
(4667, 50, 37, 'اس میں بڑی عبرت و نصیحت ہے اس کے لئے جس کے پاس دل ہویا حضورِ قلب سے کان لگائے۔'),
(4668, 50, 38, 'بےشک ہم نے آسمانوں اور زمین کو اور جو کچھ ان کے درمیان ہے کو چھ دن میں پیدا کیا ہے اور ہم کو تھکان نے چھوا تک نہیں۔'),
(4669, 50, 39, 'وہ لوگ جو کچھ کہتے ہیں اس پر صبر کیجئے اور سورج کے طلوع و غروب سے پہلے اپنے پروردگار کی حمد کے ساتھ تسبیح کیجئے۔'),
(4670, 50, 40, 'اور رات کے کچھ حصے میں اس کی تسبیح کیجئے اور (نمازوں کا) سجدہ کرنے کے بعد بھی۔'),
(4671, 50, 41, 'اور غور سے سنو اس دن کا حال جب ایک منادی بہت قریب سے ندا دے گا۔'),
(4672, 50, 42, 'جس دن سب لوگ ایک زوردار آواز کو بالیقین سنیں گے وہی دن (قبروں سے) نکلنے کا ہوگا۔'),
(4673, 50, 43, 'یقیناً ہم ہی جِلاتے ہیں ہم ہی مارتے ہیں اور ہماری ہی طرف سب کی بازگشت ہے۔'),
(4674, 50, 44, 'اس دن زمین ان پر پھٹ جائے گی اور وہ تیز تیز برآمد ہوں گے یہ حشر (جمع کرنا) ہمارے لئے بالکل آسان ہے۔'),
(4675, 50, 45, 'جو کچھ لوگ کہتے ہیں ہم خوب جانتے ہیں اور آپ(ص) ان پر جبر کرنے والے نہیں ہیں پس آپ(ص) قرآن کے ذریعہ سے اسے نصیحت کریں جو میری تہدید سے ڈرے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'قَسم ہے ان (ہواؤں) کی جو گرد و غبار اڑاتی ہیں۔'),
(4677, 51, 2, 'پھر ان (بادلوں) کی جو پانی کا بوجھ اٹھانے والے ہیں۔'),
(4678, 51, 3, 'پھر ان (کشتیوں) کی جو دھیمی رفتار سے چلنے والی ہیں۔'),
(4679, 51, 4, 'پھر ان (فرشتوں کی) جو معاملہ کے تقسیم کرنے والے ہیں۔'),
(4680, 51, 5, 'بےشک تم سے جو وعدہ کیا گیا ہے وہ سچا ہے۔'),
(4681, 51, 6, 'اور بےشک جزا وسزا ضرور واقع ہو نے والی ہے۔'),
(4682, 51, 7, 'اور قَسم ہے راستوں والے آسمان کی۔'),
(4683, 51, 8, 'تم مختلف (بےجوڑ) باتوں میں پڑے ہوئے ہو۔'),
(4684, 51, 9, 'اس (دین) سے پھرتا وہی ہے جو (حق سے) پھرا ہوا ہے۔'),
(4685, 51, 10, 'غارت ہوں اٹکل پچو باتیں بنانے والے۔'),
(4686, 51, 11, 'جو غفلت کے عالم میں مدہوش ہیں (بھولے پڑے ہیں)۔'),
(4687, 51, 12, 'وہ پوچھتے ہیں کہ جزا و سزا کا دن کب آئے گا؟'),
(4688, 51, 13, 'جس دن یہ لوگ آگ میں تپائے جائیں گے۔'),
(4689, 51, 14, 'اور) ان سے کہا جائے گا کہ اپنے فتنہ کا مزا چکھو یہی وہ عذاب ہے جس کی تم جلدی کیا کرتے تھے۔'),
(4690, 51, 15, 'بےشک پرہیزگار لوگ بہشتوں اور باغوں میں ہوں گے۔'),
(4691, 51, 16, 'اور ان کا پروردگار جو کچھ انہیں عطا کرے گا وہلے رہے ہوں گے بےشک وہ اس (دن) سے پہلے ہی (دنیا میں) نیکوکار تھے۔'),
(4692, 51, 17, 'یہ لوگ رات کو بہت کم سویا کرتے تھے۔'),
(4693, 51, 18, 'اور صبح سحر کے وقت مغفرت طلب کیا کرتے تھے۔'),
(4694, 51, 19, 'اور ان کے مالوں میں سے سوال کرنے والے اور سوال نہ کرنے والے محتاج سب کا حصہ تھا۔'),
(4695, 51, 20, 'اور زمین میں یقین کرنے والوں کے لئے (ہماری قدرت کی) نشانیاں ہیں۔'),
(4696, 51, 21, 'اور خود تمہارے وجود کے اندر بھی۔ کیا تم دیکھتے نہیں ہو؟'),
(4697, 51, 22, 'اور آسمان میں تمہاری روزی بھی ہے اور وہ چیز بھی جس کا تم سے وعدہ وعید کیا جاتا ہے۔'),
(4698, 51, 23, 'پس قَسم ہے آسمان و زمین کے پروردگار کی کہ یہ بات حق ہے جیسے کہ تم بو لتے ہو۔'),
(4699, 51, 24, '(اے حبیب(ص)!) کیا آپ تک ابراہیم (‌ع) کے معزز مہمانوں کی بات پہنچی ہے؟'),
(4700, 51, 25, 'کہ جب وہ آپ(ع) کے پاس آئے تو سلام کیا آپ(ع) نے بھی (جواب میں) سلام کیا۔ (پھر دل میں کہا) کچھ اجنبی لوگ ہیں۔'),
(4701, 51, 26, 'پس چپکے سے اپنے گھر والوں کے پاس گئے اور (تھوڑی دیر میں) ایک موٹا تازہ (بھنا ہوا) بچھڑالے آئے۔'),
(4702, 51, 27, 'جسے مہمانوں کے سامنے پیش کیا (مگر جب انہوں نے ہاتھ نہ بڑھائے تو) کہا آپ کھاتے کیوں نہیں؟'),
(4703, 51, 28, '(مگر جب انہوں نے پھر بھی کچھ نہ کھایا) تو آپ(ع) نے ان لوگوں سے اپنے اندر کچھ خوف محسوس کیا ان (مہمانوں نے) کہا ڈرئیے نہیں! اور پھر انہیں ایک صاحبِ علم بیٹے کی ولادت کی خوشخبری دی۔'),
(4704, 51, 29, 'پس آپ(ع) کی بیوی (سارہ) چیختی ہوئی آئی اور اس نے اپنا منہ پیٹ لیا اور کہا بوڑھی، بانجھ؟ (بچہ کس طرح ہوگا؟)'),
(4705, 51, 30, 'انہوں نے کہا! تمہارے پروردگار نے ایسا ہی کہا ہے بےشک وہ بڑا علیم و حکیم ہے۔'),
(4706, 51, 31, 'آپ (ابراہیم(ع)) نے کہا اے فرستادو آخر تمہاری مہم کیا ہے؟'),
(4707, 51, 32, 'انہوں نے کہا کہ ہم ایک مجرم قوم (قومِ لوط(ع)) کی طرف بھیجے گئے ہیں۔'),
(4708, 51, 33, 'تاکہ ہم ان پر ایسے سنگ گل (سخت مٹی کے پتھر) برسائیں۔'),
(4709, 51, 34, 'جو آپ(ع) کے پروردگار کی طرف سے نشان زدہ ہیں حد سے بڑھنے والوں کیلئے۔'),
(4710, 51, 35, 'سو وہاں جتنے اہلِ ایمان تھے ہم نے ان کو نکال لیا۔'),
(4711, 51, 36, 'اور ہم نے ایک گھر کے سوا اور مسلمانوں کا کوئی گھر نہ پایا۔'),
(4712, 51, 37, 'اور ہم نے اس (واقعہ) میں ایک نشانی چھوڑ دی ہے ان لوگوں کے لئے جو دردناک عذاب سے ڈرتے ہیں۔'),
(4713, 51, 38, 'اور جنابِ موسیٰ (ع) کے قصہ میں بھی (ایک نشانی ہے) جبکہ ہم نے ان کو فرعون کے پاس ایک واضح سند کے ساتھ بھیجا۔'),
(4714, 51, 39, 'تو اس نے اپنی طاقت کے بِرتے پر رُوگردانی کی اور کہا کہ (یہ شخص) جادوگر ہےیا دیوانہ؟'),
(4715, 51, 40, 'انجامِ کار ہم نے اسے اور اس کے لشکر کو پکڑا اور سب کو سمندر میں پھینک دیا درآنحالیکہ وہ قابلِ ملامت تھا۔'),
(4716, 51, 41, 'اور قبیلۂ عاد (کی داستان) میں بھی (نشانی موجود ہے) جبکہ ہم نے ان پر ایک ایسی بےبرکت ہوا بھیجی۔'),
(4717, 51, 42, 'جو جس چیز سے بھی گزرتی تھی اسے ریزہ ریزہ کر دیتی تھی۔'),
(4718, 51, 43, 'اور قبیلۂ ثمود کی سرگزشت میں بھی (ایک نشانی ہے) جبکہ ان سے کہا گیا کہ تم ایک خاص وقت تک (دنیا کی نعمتوں) سے فائدہ اٹھا لو۔'),
(4719, 51, 44, 'تو انہوں نے اپنے پروردگار کے حکم سے سرکشی کی تو ان کو ایک کڑک نے پکڑ لیا درآنحالیکہ وہ دیکھ رہے تھے۔'),
(4720, 51, 45, '(اس کے بعد) وہ نہ تو کھڑے ہی ہو سکے اور نہ ہی اپنی کوئی مدافعت کر سکے۔'),
(4721, 51, 46, 'اور قومِ نوح(ع) کا بھی اس سے پہلے (یہی حال ہوا) ہم نے اسے ہلاک کر دیا کیونکہ وہ بڑے نافرمان لوگ تھے۔'),
(4722, 51, 47, 'اور ہم نے آسمان کو (اپنی) قدرت سے بنایا اور بیشک ہم زیادہ وسعت دینے والے ہیں۔'),
(4723, 51, 48, 'اور ہم نے زمین کافرش بچھایا تو ہم کتنے اچھے فرش بچھانے والے ہیں۔'),
(4724, 51, 49, 'اور ہم نے ہر چیز کے جو ڑے پیدا کئے (ہر چیز کو دو دو قِسم کابنایا) تاکہ تم نصیحت حاصل کرو۔'),
(4725, 51, 50, 'بس تم اللہ کی طرف دوڑو۔ میں تمہیں اس سے کھلا ہوا ڈرانے والا ہوں۔'),
(4726, 51, 51, 'اور اللہ کے ساتھ کوئی اور خدا نہ بناؤ۔ بےشک میں تمہیں اس (کے قہر) سے کھلا ہوا ڈرانے والا ہوں۔'),
(4727, 51, 52, 'اسی طرح وہ لوگ جو ان سے پہلے گزرے ہیں (ان کے پاس) کوئی ایسا رسول نہیںایا جسے انہوں نے جادوگریا دیوانہ نہ کہا ہو۔'),
(4728, 51, 53, 'اور کیا یہ ایک دوسرے کو اس بات کی وصیت کرتے آرہے ہیں؟ (نہیں) بلکہ یہ لوگ سرکش ہیں۔'),
(4729, 51, 54, 'سو آپ(ص) ان سے منہ پھیر لیجئے آپ پر کوئی الزام نہیں ہے۔'),
(4730, 51, 55, 'اور انہیں سمجھاتے رہیے کہ یہ یاددہانی ایمان والوں کو فائدہ پہنچاتی ہے۔'),
(4731, 51, 56, 'اور میں نے جِنوں اور انسانوں کو پیدا نہیں کیا مگر اس لئے کہ وہ میری عبادت کریں۔'),
(4732, 51, 57, 'میں ان سے روزی نہیں چاہتا اور نہ یہ چاہتا ہوں کہ وہ مجھے کھلائیں۔'),
(4733, 51, 58, 'بےشک اللہ بڑا روزی دینے والا ہے، قوت والا (اور) بڑا مضبوط ہے۔'),
(4734, 51, 59, 'جو لوگ ظالم ہیں ان کیلئے (عذاب کا) وہی حصہ ہے جیسا ان جیسوں کا تھا۔ پس وہ جلدی نہ کریں۔'),
(4735, 51, 60, 'انجامِ کار تباہی ہے کافروں کے لئے اس دن سے جس کا ان سے وعدہ وعید کیا جا رہاہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'قَسم ہے طور کی۔'),
(4737, 52, 2, 'اور ایک کتاب کی جو لکھی ہوئی ہے۔'),
(4738, 52, 3, 'کھلے ورق میں۔'),
(4739, 52, 4, 'اور قَسم ہے اس گھر کی جو آباد ہے۔'),
(4740, 52, 5, 'اور اس چھت کی جو بلند ہے۔'),
(4741, 52, 6, 'اور اس سمندر کی جو موجزن ہے۔'),
(4742, 52, 7, 'بےشک تمہارے پروردگار کا عذاب واقع ہونے والا ہے۔'),
(4743, 52, 8, 'جس کو ٹالنے والا کوئی نہیں ہے۔'),
(4744, 52, 9, 'جس دن آسمان تھرتھرانے لگے گا۔'),
(4745, 52, 10, 'اور پہاڑ چلنے لگیں گے۔'),
(4746, 52, 11, 'پس تباہی ہوگی اس دن جھٹلانے والوں کیلئے۔'),
(4747, 52, 12, 'جو بےہودہ اور فضول باتوں میں کھیل رہے ہیں۔'),
(4748, 52, 13, 'جس دن ان کو دھکیل دھکیل کر آتشِ دوزخ کی طرف لایا جائے گا۔'),
(4749, 52, 14, 'یہی ہے وہ آگ جسے تم جھٹلاتے تھے۔'),
(4750, 52, 15, 'کیا یہ(آگ) جادو ہے؟ یا تمہیں نظر نہیں آتا؟'),
(4751, 52, 16, 'پس اس میں داخل ہو جاؤ! اب تم صبر کرویا نہ کرو دونوں تمہارے حق میں برابر ہیں تمہیں ویسا ہی بدلہ دیا جائے گا جیسا تم کیا کرتے تھے۔'),
(4752, 52, 17, 'بےشک پرہیزگار لوگ باغہائے بہشت اور نعمتوں میں ہوں گے۔'),
(4753, 52, 18, 'وہ خوش و خرم ہوں گے ان نعمتوں سے جو ان کا پروردگار انہیں عطا فر مائے گا اور ان کا پروردگار انہیں دوزخ کے عذاب سے بچائے گا۔'),
(4754, 52, 19, '(ارشاد ہوگا) کھاؤ اور پیؤ مبارک ہو! ان اعمال کے صلہ میں جو تم کرتے تھے۔'),
(4755, 52, 20, 'وہ بچھے ہوئے پلنگوں پر تکیہ لگائے ہوئے ہوں گے اور ہم ان کی حور العین (یعنی گوری رنگت کی کشادہ چشم عورتوں) سے شادی کر دیں گے۔'),
(4756, 52, 21, 'اور جو لوگ ایمان لائے اور ان کی اولاد نے بھی ایمان کے ساتھ ان کی اتباع (پیروی) کی تو ہم ان کی اولاد کو (جنت میں) ان کے ساتھ ملا دیں گے اور ان کے اعمال میں کچھ بھی کمی نہیں کریں گے ہر شخص اپنے عمل کے بدلے میں گروی ہے۔'),
(4757, 52, 22, 'اور ہم انہیں پسند کے میوے اور گوشت دیتے رہیں گے۔'),
(4758, 52, 23, 'اور وہ وہاں ایسے جامِ شراب پر چھینا جھپٹی بھی کریں گے جس میں نہ کوئی لغو بات ہوگی اور نہ گنہگار ٹھہرانے کی۔'),
(4759, 52, 24, 'اور ان کے غلام (خدمت کیلئے) چکر لگا رہے ہوں گے (جو حُسن و جمال میں) گویا چھپے ہوئے موتی ہوں گے۔'),
(4760, 52, 25, 'وہ (جنتی) ایک دوسرے کی طرف متوجہ ہو کر باہم سوال و جواب کریں گے۔'),
(4761, 52, 26, 'وہ کہیں گے کہ ہم اس سے پہلے اپنے گھر بار میں (اپنے انجام سے) ڈرتے رہتے تھے۔'),
(4762, 52, 27, 'پس اللہ نے ہم پر (اپنا) فضل و کرم فرمایا اور ہمیں گرم لو کے عذاب سے بچا لیا۔'),
(4763, 52, 28, 'بےشک ہم اس سے پہلے (دنیا میں) بھی اسی سے دعا مانگا کرتے تھے یقیناً وہ بڑا احسان کرنے والا، بڑا رحم کرنے والا ہے۔'),
(4764, 52, 29, 'آپ(ص) یاددہانی کرتے رہیے! کہ آپ اپنے پروردگار کے فضل و کرم سے نہ کاہن ہیں اور نہ مجنون۔'),
(4765, 52, 30, 'کیا وہ یہ کہتے ہیں کہ یہ ایک شاعر ہیں (اور) ہم ان کے بارے میں گردشِ زمانہ کا انتظار کر رہے ہیں؟'),
(4766, 52, 31, 'آپ کہئے! کہ تم انتظار کرو میں بھی تمہارے ساتھ انتظار کرنے والوں میں سے ہوں۔'),
(4767, 52, 32, 'کیا ان کی عقلیں ان کو ان (فضول) باتوں کا حُکم دیتی ہیںیا یہ سرکش لوگ ہیں؟'),
(4768, 52, 33, 'یا وہ یہ کہتے ہیں کہ انہوں (رسول(ص)) نے خود قرآن گھڑ لیا ہے بلکہ (اصل بات یہ ہے کہ) وہ ایمان لانا ہی نہیں چاہتے۔'),
(4769, 52, 34, 'اگر وہ سچے ہیں تو پھر ایسا کلاملے آئیں۔'),
(4770, 52, 35, 'آیا وہ بغیر کسی (خالق) کے پیدا کئے گئے ہیںیا وہ خود (اپنے) پیدا کرنے والے ہیں؟'),
(4771, 52, 36, 'یا انہوں نے آسمانوں اور زمین کو پیدا کیا ہے؟ بلکہ وہ یقین ہی نہیں رکھتے۔'),
(4772, 52, 37, 'یا ان کے پاس آپ(ص) کے پروردگار کے خزانے ہیںیا یہ ان پر حاکم (مجاز) ہیں؟'),
(4773, 52, 38, 'یا ان کے پاس کوئی سیڑھی ہے (جس پر چڑھ کر) وہ (آسمانی خفیہ باتیں) سن لیتے ہیں؟ (اگر ایسا ہے) تو پھر ان کا سننے والا کوئی کھلی ہوئی دلیل پیش کرے۔'),
(4774, 52, 39, 'کیا اللہ کے لئے بیٹیاں ہیں اور تمہارے لئے بیٹے؟'),
(4775, 52, 40, 'کیا آپ ان سے کوئی اجرت مانگتے ہیں کہ وہ تاوان کے بوجھ سے دبےجا رہے رہیں؟'),
(4776, 52, 41, 'یاان کے پاس غیب کا علم ہے کہ وہ (اس کی بناء پر) لکھتے جاتے ہیں؟'),
(4777, 52, 42, 'یا وہ کوئی چال چلنا چاہتے ہیں؟ تو جو لوگ کافر ہیں وہ خود اپنی چال کا شکار ہیں۔'),
(4778, 52, 43, 'یا اللہ کے سوا ان کا کوئی اور خدا ہے؟ پاک ہے اللہ اس شرک سے جو وہ کرتے ہیں۔'),
(4779, 52, 44, 'اور اگر وہ آسمان سے کوئی ٹکڑا گرتا ہوا دیکھیں تو کہتے ہیں کہ یہ تہہ در تہہ بادل ہے۔'),
(4780, 52, 45, 'پس انہیں (اپنے حال پر) چھوڑ دیجئے یہاں تک کہ وہ اپنے اس دن تک پہنچ جائیں جس میں وہ غش کھا کر گر جائیں گے۔'),
(4781, 52, 46, 'جس دن ان کی کوئی چال ان کے کسی کام نہ آئے گی اور نہ ہی ان کی کوئی مدد کی جائے گی۔'),
(4782, 52, 47, 'بےشک جو لوگ ظالم ہیں ان کیلئے اس سے پہلے (دنیا میں) بھی ایک عذاب ہے لیکن ان میں سے اکثر جانتے نہیں ہیں۔'),
(4783, 52, 48, 'آپ(ص) اپنے پروردگار کے فیصلے کیلئے صبر کیجئے! آپ(ص) ہماری نظروں (نگہبانی) میں ہیں آپ(ص) اپنے پروردگار کی تسبیح کیجئے اس کی حمد کے ساتھ۔'),
(4784, 52, 49, 'جس وقت آپ(ص) اٹھتے ہیں اوررات کے کچھ حصہ میں بھی اس کی تسبیح کریں اور ستاروں کے پیچھے ہٹتے وقت بھی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'قَسم ہے ستارے کی جبکہ وہ ڈوبنے لگے۔'),
(4786, 53, 2, 'کہ تمہارا یہ ساتھی (پیغمبرِ اسلام(ص)) نہ گمراہ ہوا ہے اور نہ بہکا ہے۔'),
(4787, 53, 3, 'اور وہ (اپنی) خواہشِ نفس سے بات نہیں کرتا۔'),
(4788, 53, 4, 'وہ تو بس وحی ہے جو ان کی طرف کی جاتی ہے۔'),
(4789, 53, 5, 'ان کو ایک زبردست قوت والے نے تعلیم دی ہے۔'),
(4790, 53, 6, 'جو بڑا صاحبِ قدرت (یا بڑا دانا و حکیم) ہے پھر (وہ اپنی اصلی شکل میں) کھڑا ہوا۔'),
(4791, 53, 7, 'جبکہ وہ آسمان کے بلند ترین کنارہ پر تھا۔'),
(4792, 53, 8, 'پھر وہ قریب ہوا اور زیادہ قریب ہوا۔'),
(4793, 53, 9, 'یہاں تک دو کمان کے برابریا اس سے بھی کم فاصلہ رہ گیا۔'),
(4794, 53, 10, 'پس اس (اللہ) نے اپنے بندہ (خاص) کی طرف وحی کی جو وحی کی۔'),
(4795, 53, 11, '(رسول(ص) کے) دل نے جھوٹ نہیں کہا (جھٹلایا نہیں) جو کچھ (مصطفٰی(ص) کی) آنکھ نے دیکھا۔'),
(4796, 53, 12, 'کیا تم لوگ آپ(ص) سے اس بات پر جھگڑتے ہو جو کچھ انہوں نے دیکھا۔'),
(4797, 53, 13, 'اور آپ(ص) نے ایک بار اور بھی۔'),
(4798, 53, 14, 'اترتے ہوئے سدرۃ المنتہی کے پاس دیکھا۔'),
(4799, 53, 15, 'جہاں جنت الماوی (آرام سے رہنے کا بہشت) ہے۔'),
(4800, 53, 16, 'جب کہ سدرہ پر چھا رہا تھا (وہ نور) جو چھا رہا تھا۔'),
(4801, 53, 17, 'نہ آنکھ چندھیائی اور نہ حدسے بڑھی۔'),
(4802, 53, 18, 'یقیناً آپ(ص) نے اپنے پروردگار کی بڑی بڑی نشانیاں دیکھیں۔'),
(4803, 53, 19, 'کیا تم نے کبھی لات و عزیٰ۔'),
(4804, 53, 20, 'اور تیسرے منات کو کبھی دیکھا ہے (ان کی اصلیت میں غور کیا ہے؟)'),
(4805, 53, 21, 'کیا تمہارے لئے لڑکے ہیں اور اس(اللہ) کے لئے لڑکیاں؟'),
(4806, 53, 22, 'یہ تقسیم تو بڑی ظالمانہ ہے۔'),
(4807, 53, 23, 'یہ نہیں ہیں مگر صرف چند نام جو تم نے اور تمہارے باپ دادانے رکھ لئے ہیں جس پر اللہ نے کوئی دلیل (سند) نہیں نازل کی یہ لوگ محض وہم و گمان کی پیروی کر رہے ہیں اور خواہشاتِ نفس کی حالانکہ ان کے پاس ان کے پروردگار کی طرف سے ہدایت و رہنمائی آچکی۔'),
(4808, 53, 24, 'کیا انسان کو وہ چیز مل سکتی ہے جس کی وہ تمنا کرتا ہے؟'),
(4809, 53, 25, 'پس اللہ کے قبضۂ قدرت میں ہے آخرت بھی اور دنیا بھی (یعنی انجام بھی اور آغاز بھی)۔'),
(4810, 53, 26, 'اور آسمانوں میں کتنے ہی فرشتے ہیں جن کی سفارش کچھ بھی فائدہ نہیں دے سکتی مگر بعد اس کے کہ اللہ جس کے لئے چاہے اجازت دے اور پسند کرے۔'),
(4811, 53, 27, 'بےشک جو لوگ آخرت پر ایمان نہیں رکھتے وہ فرشتوں کو عورتوں کے نام سے موسوم کرتے ہیں۔'),
(4812, 53, 28, 'حالانکہ انہیں اس کا کوئی علم نہیں ہے وہ مخصوص ظن و گمان کی پیروی کرتے ہیں اور ظنِ حق کے مقابلہ میں ذرا بھی کام نہیں دے سکتا۔'),
(4813, 53, 29, '(اے رسول(ص)) آپ اس سے رُوگردانی کیجئے! جو ہمارے ذکر سے رُوگردانی کرتا ہے اور جو زندگانئ دنیا کے سوا اور کچھ نہیں چاہتا۔'),
(4814, 53, 30, 'ان لوگوں کامبلغ علم یہی ہے (ان کے علم کی رسائی کی حد یہی ہے) آپ(ص) کا پروردگار ہی بہتر جانتا ہے کہ اس کے راستہ سے بھٹکا ہوا کون ہے؟ اور وہی بہتر جانتا ہے کہ راہِ راست پر کون ہے؟'),
(4815, 53, 31, 'اور جو آسمانوں میں ہے اور جو کچھ زمین میں ہے وہ اللہ ہی کا ہے تاکہ وہ برا کام کرنے والوں کو ان کے (برے) کاموں کا بدلہ دے اور نیک کرنے والوں کو اچھی جزا عطا کرے۔'),
(4816, 53, 32, 'جو کہ بڑے گناہوں اور بےحیائی کے کاموں سے پرہیز کرتے ہیں مگر یہ کہ کچھ ہلکے گناہ سر زد ہو جائیں۔ بےشک آپ کا پروردگار وسیع مغفرت والا ہے وہ تمہیں (اس وقت سے) خوب جانتا ہے جب اس نے تمہیں زمین سے پیدا کیا اور جب تم اپنی ماؤں کے پیٹوں میں جنین کی صورت میں تھے پس تم اپنے آپ کی پاکی کے دعوے نہ کرو۔ وہ (اللہ) بہتر جانتا ہے کہ واقعی پرہیزگار کون ہے؟'),
(4817, 53, 33, '(اے رسول(ص)) کیا آپ(ص) نے اس شخص کو دیکھا ہے جس نے رُوگردانی کی۔'),
(4818, 53, 34, 'اس نے تھوڑا سا (مال) دیا اور پھر ہاتھ روک لیا۔'),
(4819, 53, 35, 'کیا اس کے پاس غیب کا علم ہے پس وہ دیکھ رہا ہے؟'),
(4820, 53, 36, 'کیا اسے اس بات کی خبر نہیں پہنچی جو موسیٰ (ع) کے صحیفوں میں ہے۔'),
(4821, 53, 37, 'اور اس ابراہیم (ع) کے صحیفوں میں جس نے وفاداری کا حق ادا کر دیا۔'),
(4822, 53, 38, 'کہ کوئی بوجھ اٹھانے والا کسی دوسرے کا بوجھ نہیں اٹھائے گا۔'),
(4823, 53, 39, 'اور یہ کہ انسان کے لئے وہی کچھ ہے جس کی وہ کوشش کرتا ہے۔'),
(4824, 53, 40, 'اور اس کی سعی و کوشش عنقریب دیکھی جائے گی۔'),
(4825, 53, 41, 'اور پھر اسے پوری جزا دی جائے گی۔'),
(4826, 53, 42, 'اور یہ کہ سب کی انتہا آپ(ص) کے پروردگار کی طرف ہے۔'),
(4827, 53, 43, 'بےشک وہی ہنساتا ہے اور رلاتا ہے۔'),
(4828, 53, 44, 'اور وہی مارتا ہے اور جِلاتا ہے۔'),
(4829, 53, 45, 'اور یہ کہ اسی نے نر اور مادہ کے جوڑے پیدا کئے۔'),
(4830, 53, 46, '(وہ بھی) ایک نطفہ سے (جو رحم میں) ٹپکایا جاتا ہے۔'),
(4831, 53, 47, 'اور بیشک دوبارہ پیدا کرنا اسی (اللہ) کے ذمہ ہے۔'),
(4832, 53, 48, 'اور یہ کہ وہی سرمایہ دار بناتا ہے اور وہی فقیر و نادار بناتا ہے۔'),
(4833, 53, 49, 'اور یہ کہ وہ شعریٰ نامی ستارے کا پروردگار ہے۔'),
(4834, 53, 50, 'اور یہ کہ اسی نے عادِ اول (قومِ ہود) کو ہلاک کیا۔'),
(4835, 53, 51, 'اور ثمود کو بھی اس طرح ہلاک کیا کہ کسی کو باقی نہیں چھوڑا۔'),
(4836, 53, 52, 'اوران سب سے پہلے قومِ نوح(ع) کو بھی (برباد کیا) بےشک وہ بڑے ظالم اور بڑے سرکش تھے۔'),
(4837, 53, 53, '(لوط (ع)) کی الٹی ہوئی ان (بستیوں) کو بھی دے مارا۔'),
(4838, 53, 54, 'پس ان (بستیوں) کو ڈھانک لیا اس (آفت) نے جس نے ڈھانک لیا۔'),
(4839, 53, 55, 'پس تو اے مخاطب اپنے پروردگار کی کن کن نعمتوں میں شک کرے گا۔'),
(4840, 53, 56, 'یہ (رسول(ص)) بھی ڈرانے والا ہے پہلے ڈرانے والے (رسولوں(ع)) سے۔'),
(4841, 53, 57, 'قریب آنے والی (قیامت) قریب آگئی ہے۔'),
(4842, 53, 58, 'اللہ کے سوا اس کا کوئی ہٹانے والانہیں ہے۔'),
(4843, 53, 59, 'کیا تم اس بات سے تعجب کرتے ہو؟'),
(4844, 53, 60, 'اور ہنستے ہو اور روتے نہیں۔'),
(4845, 53, 61, 'اور تم غفلت میں مدہوش ہو۔'),
(4846, 53, 62, 'پس اللہ کے لئے سجدہ کرو اور(اسی کی) عبادت کرو۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, '(قیامت کی) گھڑی قریب آگئی اور چاند پھٹ گیا۔'),
(4848, 54, 2, 'اور اگر وہ کوئی نشانی (معجزہ) دیکھتے ہیں تو منہ پھیر لیتے ہیں اور کہتے ہیں کہ یہ تو مستقل جادو ہے (جو پہلے سے چلا آرہا ہے)۔'),
(4849, 54, 3, 'اور انہوں نے (رسول(ص) کو) جھٹلایا اور اپنی خواہشات کی پیروی کی اور ہر کام کا وقت مقرر ہے اور ثابت ہے۔'),
(4850, 54, 4, 'اور ان کے پاس ایسی (سابقہ قوموں کی) ایسی خبریں آچکی ہیں جن میں (سرکشی کرنے والوں کیلئے) کافی سامانِ عبرت ہے۔'),
(4851, 54, 5, 'یعنی نہایت اعلیٰ حکمت اور دانشمندی مگر تنبیہات (ان کو) کوئی فائدہ نہیں دیتیں۔'),
(4852, 54, 6, '(اے رسول(ص)) ان لوگوں سے رخ پھیر لیجئے جس دن ایک پکارنے والا ایک سخت ناگوار چیز کی طرف بلائے گا۔'),
(4853, 54, 7, '(شدتِ خوف سے) آنکھیں جھکائے ہوئے قبروں سے یوں نکل پڑیں گے کہ گویا بکھری ہوئی ہڈیاں ہیں۔'),
(4854, 54, 8, '(دہشت سے) گردنیں بڑھائے پکارنے والے کی طرف دوڑتے جا رہے ہوں گے (اس دن) کافر کہیں گے کہ یہ بڑا دشوار دن ہے۔'),
(4855, 54, 9, 'ان سے پہلے قومِ نوح(ع) نے جھٹلایا سو انہوں نے ہمارے بندۂ خاص کو جھٹلایا اور ان پر جھوٹا ہو نے کا الزام لگایا اور اسے جھڑکا بھی گیا۔'),
(4856, 54, 10, 'آخر کار اس (نوح(ع)) نے اپنے پروردگار کو پکارا کہ میں مغلوب ہوں۔ لہٰذا تو (ان سے) بدلہلے۔'),
(4857, 54, 11, 'پس ہم نے موسلادھار بارش کے ساتھ آسمان کے دروازے کھول دئیے۔'),
(4858, 54, 12, 'اور ہم نے زمین سے چشمے جاری کر دئیے پس (دونوں) پانی آپس میں مل گئے ایک کام کیلئے جو مقدر ہو چکا تھا۔'),
(4859, 54, 13, 'اور ہم نے اس (نوح(ع)) کو تختوں اور میخوں والی (کشتی) پر سوار کیا۔'),
(4860, 54, 14, 'جو ہماری آنکھوں کے سا منے (ہماری نگرانی میں) چلتی تھی اور (یہ سب کچھ) اس شخص (نوح(ع)) کا بدلہ لینے کیلئے کیا گیا جس کی ناقدری کی گئی تھی۔'),
(4861, 54, 15, 'اور ہم نے اسکو نشانی کے طور پر باقی رکھا تو کوئی ہے نصیحت قبول کرنے والا؟'),
(4862, 54, 16, 'پس(دیکھو) کیسا تھا میرا عذاب اور میرا ڈراوا؟'),
(4863, 54, 17, 'بیشک ہم نے قرآن کو نصیحت حاصل کرنے کیلئے آسان بنا دیا ہے سو کوئی ہے نصیحت حاصل کرنے والا۔'),
(4864, 54, 18, 'قومِ عاد نے بھی (اپنے نبی(ع) کو) جھٹلایا تو دیکھو کیسا تھا میرا عذاب اور میرے ڈراوے؟'),
(4865, 54, 19, 'ہم نے ان پر تیز و تند آندھی بھیجی دائمی نحوست والے دن میں۔'),
(4866, 54, 20, 'وہ (آندھی) اس طرح آدمیوں کو اکھاڑ پھینکتی تھی کہ گویا وہ جڑ سے اکھڑے ہوئے کھجور کے تنے ہیں۔'),
(4867, 54, 21, 'پس (دیکھو) کیسا تھا میرا عذاب اور میرے ڈراوے؟'),
(4868, 54, 22, 'بلاشبہ ہم نے نصیحت حاصل کرنے کیلئے قرآن کو آسان بنا دیا تو کوئی ہے نصیحت حاصل کرنے والا؟'),
(4869, 54, 23, 'قومِ ثمود نے ڈرانے والوں (پیغمبروں(ع)) کو جھٹلایا۔'),
(4870, 54, 24, 'پس انہوں نے کہا کہ ہم ایک ایسے شخص کی پیروی کریں جو ہم ہی میں سے ہے؟ اس صو رت میں تو ہم گمراہی اور دیوانگی میں پڑ جائیں گے۔'),
(4871, 54, 25, 'کیا ہم سب میں سے صرف اسی پر وحی نازل کی گئی ہے؟ بلکہ وہ بڑا جھوٹا (اور) شیخی باز ہے۔'),
(4872, 54, 26, 'انہیں بہت جلد کل کلاں معلوم ہو جائے گا کہ کون بڑا جھوٹا (اور) شیخی باز ہے۔'),
(4873, 54, 27, 'ہم ایک (خاص) اونٹنی ان کی آزمائش کے لئے بھیج رہے ہیں پس تم (اے صالح(ع) انجامِ کار) انتظار کرو اور صبر کرو۔'),
(4874, 54, 28, 'اور انہیں آگاہ کر دو کہ پانی ان کے درمیان تقسیم ہوگیا ہے اور ہر ایک اپنی باری پر حاضر ہوگا۔'),
(4875, 54, 29, 'پس ان لوگوں (یہودیوں) نے اپنے ساتھی (قدار) کو پکارا پس اس نے (اونٹنی کو) پکڑ کر اس کی کونچیں کاٹ دیں۔'),
(4876, 54, 30, 'تو کیسا تھا میرا عذاب اورمیرے ڈراوے؟'),
(4877, 54, 31, 'ہم نے ان پر ایک ہی چنگھاڑ بھیجی تو وہ باڑھ لگانے والے کی روندی ہوئی باڑہ کی طرح ہو کر رہ گئے۔'),
(4878, 54, 32, 'اور ہم نے نصیحت حاصل کرنے کیلئے قرآن کو آسان بنا دیاتو کوئی ہے نصیحت حاصل کرنے والا؟'),
(4879, 54, 33, 'قومِ لوط(ع) نے ڈرانے والوں کو جھٹلایا۔'),
(4880, 54, 34, 'ہم نے ان پر پتھر برسانے والی ہوا بھیجی صرف آلِ لوط(ع) اس سے محفوظ رہے ہم نے سحر کے وقت ان کو نجات دی۔'),
(4881, 54, 35, 'خاص اپنے فضل و کرم سے ہم اسی طرح شکر کرنے والے کو جزا دیتے ہیں۔'),
(4882, 54, 36, 'بلاشبہ انہوں (لوط(ع)) نے انہیں سخت گرفت سے ڈرایا تھا مگر وہ ڈرانے کے بارے میں شک میں مبتلا رہے (اورجھگڑتے رہے)۔'),
(4883, 54, 37, 'اور ان لوگوں نے ان کے مہمانوں کے بارے میں برے ارادہ سے ان پر ڈول ڈالے تو ہم نے ان کی آنکھیں موند دیں تو میرے عذاب اور ڈراوے کا مزہ چکھو۔'),
(4884, 54, 38, 'اور (پھر) صبح سویرے ان پر دائمی عذاب آپہنچا۔'),
(4885, 54, 39, 'پس میرے عذاب اور ڈراوے کامزہ چکھو۔'),
(4886, 54, 40, 'بےشک ہم نے قرآن کو نصیحت حاصل کرنے کیلئے آسان بنا دیا ہے کوئی ہے نصیحت حاصل کرنے والا؟'),
(4887, 54, 41, 'اور فرعون والوں کے پاس بھی ڈرانے والے آئے۔'),
(4888, 54, 42, 'تو انہوں نے ہماری ساری نشانیوں کو جھٹلایا تو ہم نے انہیں اس طرح پکڑا جس طرح کوئی زبردست اقتدار والا (طا قتور) پکڑتا ہے۔'),
(4889, 54, 43, 'کیا تمہارے کافر ان لوگوں سے بہتر ہیں یا تمہارے لئے (آسمانی) کتابوں میں معافی کا پروانہ لکھا ہوا ہے؟'),
(4890, 54, 44, 'یا وہ کہتے ہیں کہ ہم ایک ایسی جماعت ہیں جو غالب رہیںگے۔'),
(4891, 54, 45, 'عنقریب یہ جماعت شکست کھائے گی اور یہ سب پیٹھ پھیر کر بھاگ جائیں گے۔'),
(4892, 54, 46, 'بلکہ ان کے وعدہ کا اصل وقت تو قیامت ہے اور قیامت بڑی سخت (ہولناک) اور بڑی تلخ ہے۔'),
(4893, 54, 47, 'بےشک مجرم لوگ گمراہی اور دیوانگی میں مبتلا ہیں۔'),
(4894, 54, 48, 'جس دن یہ لوگ منہ کے بل آگ میں گھسیٹے جائیں گے (کہا جائے گا) اب آگ کا مزہ چکھو۔'),
(4895, 54, 49, 'بےشک ہم نے ہر چیز کو خاص اندازہ سے پیدا کیا ہے۔'),
(4896, 54, 50, 'اور ہمارا حکم نہیں ہوتا مگر ایک جو آنکھ جھپکنے میں واقع ہو جاتا ہے۔'),
(4897, 54, 51, 'بےشک ہم تمہارے ہم مشرب لوگوں کو ہلاک کر چکے ہیں تو ہے کوئی نصیحت حاصل کرنے والا؟'),
(4898, 54, 52, 'اور جو کچھ ان لوگوں نے کیا تھا وہ سب نامۂ اعمال میں درج ہے۔'),
(4899, 54, 53, 'اور ہر چھوٹی اور بڑی بات (اس میں) درج ہے۔'),
(4900, 54, 54, 'بےشک پرہیزگار لوگ بہشتوں اور نہروں میں ہوں گے۔'),
(4901, 54, 55, 'صداقت و سچائی کے محل میں بڑی عظیم قدرت والے بادشاہ (خدا) کے حضور میں (مقرب ہوں گے)۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'خدائے رحمٰن۔'),
(4903, 55, 2, 'نے قرآن کی تعلیم دی۔'),
(4904, 55, 3, 'اسی نے انسان کو پیدا کیا۔'),
(4905, 55, 4, 'اسی نے اسے بولنا (مافی الضمیر بیان کرنا) سکھایا۔'),
(4906, 55, 5, 'سورج اور چاند ایک (خاص) حساب کے پابند ہیں۔'),
(4907, 55, 6, 'اور سبزیاں، بیلیں اور درخت (اسی کو) سجدہ کرتے ہیں۔'),
(4908, 55, 7, 'اس نے آسمان کو بلند کیا اور میزان (عدل) رکھ دی۔'),
(4909, 55, 8, 'تاکہ تم میزان (تولنے) میں زیادتی نہ کرو۔'),
(4910, 55, 9, 'اور انصاف کے ساتھ ٹھیک طریقہ پر تولو اور وزن (تولنے) میں کمی نہ کرو۔'),
(4911, 55, 10, 'اور اس نے زمین کو تمام مخلوق کیلئے بنایا۔'),
(4912, 55, 11, 'اس میں (ہر طرح کے) میوے ہیں اور کھجور کے غلافوں والے درخت ہیں۔'),
(4913, 55, 12, 'اور بھوسہ والا اناج بھی اور خوشبودار پھول بھی۔'),
(4914, 55, 13, 'سو (اے جن و انس) تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4915, 55, 14, 'اس نے انسان کو ٹھیکرے کی طرح کھنکھناتی ہوئی مٹی سے پیدا کیا۔'),
(4916, 55, 15, 'اور جنات کو پیدا کیا آگ کے شعلہ سے۔'),
(4917, 55, 16, 'سو (اے جن و انس) تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4918, 55, 17, 'وہی دونوں مشرقوں کا پروردگار ہے اور وہی دونوں مغربوں کا مالک و پروردگار ہے۔'),
(4919, 55, 18, 'سو (اے جن و انس) تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4920, 55, 19, 'اسی نے دو دریا جاری کئے جو آپس میں مل رہے ہیں۔'),
(4921, 55, 20, 'ان دونوں کے درمیان ایک فاصلہ ہے جس سے وہ تجاوز نہیں کرتے۔'),
(4922, 55, 21, 'سو(اے جن و انس) تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4923, 55, 22, 'ان دونوں دریاؤں سے موتی اور مونگے نکلتے ہیں۔'),
(4924, 55, 23, 'سو (اے جن و انس) تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤگے؟'),
(4925, 55, 24, 'اور اسی کے لئے وہ (جہاز) ہیں جو سمندروں میں پہاڑوں کی طرح اٹھے ہوئے ہیں۔'),
(4926, 55, 25, 'سو (اے جن و انس) تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4927, 55, 26, 'جو بھی روئے زمین پر ہیں وہ سب فنا ہونے والے ہیں۔'),
(4928, 55, 27, 'اور آپ(ص) کے پروردگار کی ذات باقی رہے گی جو عظمت و اکرام والی ہے۔'),
(4929, 55, 28, 'تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4930, 55, 29, 'آسمانوں اور زمین میں جو بھی ہیں اسی سے (اپنی حاجتیں) مانگتے ہیں وہ ہر روز (بلکہ ہر آن) ایک نئی شان میں ہے۔'),
(4931, 55, 30, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4932, 55, 31, 'اے جن وانس ہم عنقریب (تمہاری خبر لینے کیلئے) تمہاری طرف متوجہ ہو نے والے ہیں۔'),
(4933, 55, 32, 'تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4934, 55, 33, 'اے گروہِ جن و انس! اگر تم سے ہو سکے تو تم آسمانوں اور زمین کی حدود سے باہر نکل جاؤ (لیکن) تم طاقت اور زور کے بغیر نہیں نکل سکتے۔'),
(4935, 55, 34, 'تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4936, 55, 35, 'تم دونوں پر آگ کاشعلہ اور دھواں چھوڑا جائے گا پھر تم اپنا بچاؤ نہ کر سکو گے۔'),
(4937, 55, 36, 'تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4938, 55, 37, '(اس وقت کیا حالت ہوگی) جب آسمان پھٹ جائے گا اور (رنگے ہوئے) سرخ چمڑے کی طرح لال ہو جائے گا۔'),
(4939, 55, 38, 'تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4940, 55, 39, 'پس اس دن کسی انسان یا جن سے اس کے گناہ کے متعلق سوال نہیں کیا جائے گا۔'),
(4941, 55, 40, 'تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4942, 55, 41, 'مجرم اپنے چہروں (اپنی علامتوں) سے پہچان لئے جائیں گے اور پھر پیشانیوں اور پاؤں سے پکڑے جائیں گے (اور جہنم میں پھینک دئیے جائیں گے)۔'),
(4943, 55, 42, 'سو تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4944, 55, 43, 'یہی وہ دوزخ ہے جسے مجرم لوگ جھٹلایا کرتے تھے۔'),
(4945, 55, 44, 'وہ (مجرم) اس (دوزخ) اور اس کے انتہائی کھولتے ہوئے پانی کے درمیان گردش کرتے رہیں گے۔'),
(4946, 55, 45, 'پس اے جن و انس! سو تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4947, 55, 46, 'اور جو شخص اپنے پروردگار کی بارگاہ میں حاضری سے ڈرتا ہے اس کے لئے دو باغ ہیں۔'),
(4948, 55, 47, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟ ۔'),
(4949, 55, 48, 'دونوں باغ بہت سی شاخوں والے۔'),
(4950, 55, 49, 'پس تم اپنے پروردگار کی کن کن نعمتوں جھٹلاؤگے؟'),
(4951, 55, 50, 'ان دونوں باغوں میں دو چشمے جاری ہوں گے۔'),
(4952, 55, 51, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4953, 55, 52, 'ان دونوں باغوں میں ہر پھل کی دو قِسمیں ہوں گی۔'),
(4954, 55, 53, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤگے؟'),
(4955, 55, 54, 'وہ ایسے بچھونوں پر تکیہ لگائے بیٹھے ہوں گے جن کے استر دبیز ریشم کے ہوں گے۔'),
(4956, 55, 55, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4957, 55, 56, 'اور دونوں باغوں کے تیار پھل (ان کے) بہت ہی نزدیک ہوں گے۔'),
(4958, 55, 57, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4959, 55, 58, 'ان جنتوں میں نیچی نگاہ والی (حوریں) ہوں گی جن کو ان (جنتیوں) سے پہلے نہ کسی انسان نے چھوا ہوگا اور نہ کسی جن نے۔'),
(4960, 55, 59, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4961, 55, 60, 'وہ(حوریں) ایسی ہوں گی جیسے یاقوت اور مرجان۔'),
(4962, 55, 61, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤگے؟'),
(4963, 55, 62, 'نیکی کا بدلہ نیکی کے سوا اور کیا ہے؟'),
(4964, 55, 63, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4965, 55, 64, 'ان دونوں باغوں کے علاوہ دو باغ اور بھی ہیں۔'),
(4966, 55, 65, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4967, 55, 66, 'دونوں گہرے سبز سیاہی مائل۔'),
(4968, 55, 67, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4969, 55, 68, 'ان دو باغوں میں دو چشمے ہوں گے جوش مارتے ہوئے۔'),
(4970, 55, 69, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4971, 55, 70, 'ان میں پھل، کھجور اور انار ہوں گے۔'),
(4972, 55, 71, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤگے؟'),
(4973, 55, 72, 'ان میں اچھی سیرت و صورت والی (حوریں) ہوں گی۔'),
(4974, 55, 73, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4975, 55, 74, 'ان (جنتیوں سے پہلے کسی انسان یا جن نے انہیں چھوا بھی نہیں ہوگا۔'),
(4976, 55, 75, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4977, 55, 76, 'وہ (جنتی لوگ) سبز رنگ کے بچھونوں اور خوبصورت گدوں پر تکیہ لگائے بیٹھے ہوں گے۔'),
(4978, 55, 77, 'پس تم اپنے پروردگار کی کن کن نعمتوں کو جھٹلاؤ گے؟'),
(4979, 55, 78, 'بابرکت ہے تمہارے پروردگار کانام جو بڑی عظمت اور کرا مت والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'جب واقعہ ہو نے والی (قیامت) واقع ہو جائے گی۔'),
(4981, 56, 2, 'جس کے واقع ہو نے میں کوئی جھوٹ نہیں ہے۔'),
(4982, 56, 3, 'وہ (کسی کو) پست کرنے والی اور (کسی کو) بلند کرنے والی ہوگی۔'),
(4983, 56, 4, 'جب زمین بالکل ہلا ڈالی جائے گی۔'),
(4984, 56, 5, 'اور پہاڑ ٹوٹ کر ریزہ ریزہ ہو جائیں گے۔'),
(4985, 56, 6, '(اور) پراگندہ غبار کی طرح ہو جائیں گے۔'),
(4986, 56, 7, 'اورتم لوگ تین قسم کے ہو جاؤ گے۔'),
(4987, 56, 8, 'پس (ایک قِسم) دائیں ہاتھ والوں کی ہوگی وہ دائیں ہاتھ والے کیا اچھے ہیں؟'),
(4988, 56, 9, 'اور(دوسری قِسم) بائیں ہاتھ والوں کی ہوگی اور بائیں ہاتھ والے کیا برے ہیں؟'),
(4989, 56, 10, 'اور (تیسری قِسم) سبقت کرنے والوں کی ہوگی وہ تو سبقت کرنے والے ہی ہیں۔'),
(4990, 56, 11, 'وہی لوگ خاص مقرب (بارگاہ) ہیں۔'),
(4991, 56, 12, '(یہ لوگ) عیش و آرام کے باغوں میں ہوں گے۔'),
(4992, 56, 13, 'ان کی بڑی جماعت اگلوں میں سے ہوگی۔'),
(4993, 56, 14, 'اور بہت تھوڑے پچھلوں میں سے ہوں گے۔'),
(4994, 56, 15, 'مرصع تختوں پر تکیہ لگائے۔'),
(4995, 56, 16, 'آمنے سامنے بیٹھے ہوں گے۔'),
(4996, 56, 17, 'ان کے پاس ایسے غلمان (نوخیز لڑکے) ہوں گے جو ہمیشہ غلمان ہی رہیں گے۔'),
(4997, 56, 18, '(شرابِ طہور سے بھرے ہو ئے) آبخورے، آفتابے اور پاک و صاف شراب کے پیالے (یعنی جام، صراحی سبو و ساغر) لئے گردش کر رہے ہوں گے۔'),
(4998, 56, 19, 'جس سے نہ دردِ سر ہوگا اور نہ عقل میں فتور آئے گا۔'),
(4999, 56, 20, 'اور وہ (غلمان) طرح طرح کے پھل پیش کریں گے وہ جسے چاہیں چن لیں۔'),
(5000, 56, 21, 'اور پرندوں پرندوں کے گوشت بھی جس کی وہ خواہش کریں گے۔'),
(5001, 56, 22, '(اور ان کیلئے) گوری رنگت والی غزال چشم حوریں بھی ہوں گی۔'),
(5002, 56, 23, 'جو چھپا کر رکھے ہوئے موتیوں کی طرح (حسین) ہوں گی۔'),
(5003, 56, 24, 'یہ سب کچھ ان کے ان اعمال کی جزا ہے جو وہ کیا کرتے تھے۔'),
(5004, 56, 25, 'وہ اس میں کوئی فضول اور گناہ والی بات نہیں سنیں گے۔'),
(5005, 56, 26, 'مگر صرف سلام و دعا کی آواز آئے گی۔'),
(5006, 56, 27, 'اور وہ دائیں ہاتھ والے اور وہ دائیں والے کیا اچھے ہیں۔'),
(5007, 56, 28, 'وہ ایسی بیری کے درختوں میں ہوں گے جن میں کانٹے نہیں ہوں گے۔'),
(5008, 56, 29, 'اورتہہ بہ تہہ کیلوں میں۔'),
(5009, 56, 30, 'اور (دور تک) پھیلے ہوئے سایوں میں۔'),
(5010, 56, 31, 'اور بہتے ہوئے پانی میں۔'),
(5011, 56, 32, 'اور بہت سے پھلوں میں۔'),
(5012, 56, 33, 'جو نہ کبھی ختم ہوں گے اور نہ کوئی روک ٹوک ہوگی۔'),
(5013, 56, 34, 'اور اونچے بچھونوں پر ہوں گے۔'),
(5014, 56, 35, 'ہم نے (حوروں) کو خاص نئے سرے سے پیدا کیا ہے۔'),
(5015, 56, 36, 'پھر ان کو کنواریاں بنایا ہے۔'),
(5016, 56, 37, '(اور) پیار کرنے والیاں اور ہم عمر ہیں۔'),
(5017, 56, 38, 'یہ سب نعمتیں دائیں ہاتھ والوں کے لئے ہیں۔'),
(5018, 56, 39, 'ایک بڑی جماعت اگلوں میں سے۔'),
(5019, 56, 40, 'اور ایک بڑی جماعت پچھلوں میں سے ہے۔'),
(5020, 56, 41, 'اور بائیں ہاتھ والے اور کیا برے ہیں بائیں ہاتھ والے۔'),
(5021, 56, 42, 'وہ لو کی لپٹ (سخت تپش) اور کھولتے ہوئے پانی۔'),
(5022, 56, 43, 'اور سیاہ دھوئیں کے سایہ میں ہوں گے۔'),
(5023, 56, 44, 'جو نہ ٹھنڈا ہوگااور نہ نفع بخش۔'),
(5024, 56, 45, '(کیونکہ) وہ اس سے پہلے (دنیا میں) خوشحال (اور عیش و عشرت میں) تھے۔'),
(5025, 56, 46, 'اور وہ بڑے گناہ پر اصرار کرتے تھے۔'),
(5026, 56, 47, 'اور کہتے تھے کہ جب ہم مر جائیں گے اور مٹی اور ہڈیاں ہو جائیں گے تو کیا ہم دوبارہ اٹھائے جائیں گے؟'),
(5027, 56, 48, 'اور کیا ہمارے پہلے باپ دادا بھی (اٹھائے جائیں گے؟)'),
(5028, 56, 49, 'آپ(ص) کہہ دیجئے! کہ بےشک اگلے اور پچھلے۔'),
(5029, 56, 50, 'سب ایک دن کے مقرروقت پر اکٹھے کئے جائیں گے۔'),
(5030, 56, 51, 'پھر تم اے گمراہو اور جھٹلانے والو۔'),
(5031, 56, 52, 'تم (تلخ ترین درخت) زقوم سے کھاؤ گے۔'),
(5032, 56, 53, 'اور اسی سے (اپنے) پیٹ بھرو گے۔'),
(5033, 56, 54, 'اور اوپر سے کھولتا ہوا پانی پیو گے۔'),
(5034, 56, 55, 'جس طرح سخت پیاسے اونٹ (بےتحاشا) پانی پیتے ہیں۔'),
(5035, 56, 56, 'جزا و سزا کے دن یہ ان کی مہمانی ہوگی۔'),
(5036, 56, 57, 'ہم نے ہی تمہیں پیدا کیا ہے پھر تم (قیامت کی) تصدیق کیوں نہیں کرتے؟'),
(5037, 56, 58, 'کیا تم نے کبھی غور کیا ہے کہ جو نطفہ تم ٹپکاتے ہو۔'),
(5038, 56, 59, 'کیا تم اسے(آدمی بنا کر) پیدا کرتے ہو یا ہم پیدا کرنے و الے ہیں۔'),
(5039, 56, 60, 'ہم نے ہی تمہارے درمیان موت (کا نظام) مقرر کیا ہے اور ہم اس سے عاجز نہیں ہیں۔'),
(5040, 56, 61, 'کہ ہم تمہاری جگہ تم جیسے اور لوگ پیدا کر دیں اور تم کو ایسی صورت میں (یا ایسے عالَم میں) پیدا کر دیں جس کو تم نہیں جانتے۔'),
(5041, 56, 62, 'اور تم (اپنی) پہلی پیدائش کو تو جانتے ہی ہو پھر نصیحت کیوں نہیں حاصل کرتے؟'),
(5042, 56, 63, 'کیا تم نے کبھی غور کیا ہے کہ تم جو کچھ (بیج) بوتے ہو۔'),
(5043, 56, 64, 'کیا تم اس کو اگاتے ہو یا ہم اگانے والے ہیں۔'),
(5044, 56, 65, 'اگر ہم چاہیں تو اس (پیداوار) کو (خشک کر کے) چُورا چُورا کر دیں تو تم باتیں بناتے رہ جاؤ۔'),
(5045, 56, 66, 'کہ ہم پر تاوان پڑگیا۔'),
(5046, 56, 67, 'بلکہ ہم بالکل محروم ہو گئے۔'),
(5047, 56, 68, 'کیا تم نے کبھی غور کیا ہے کہ وہ پانی جو تم پیتے ہو۔'),
(5048, 56, 69, 'کیا تم نے اسے بادل سے اتارا ہے یا ہم (اس کے) اتارنے والے ہیں؟'),
(5049, 56, 70, 'اگر ہم چاہتے تو اسے سخت بھاری بنا دیتے پھر تم شکر کیوں نہیں کرتے؟'),
(5050, 56, 71, 'کیا تم نے کبھی غور کیا ہے کہ وہ آگ جو تم سلگاتے ہو۔'),
(5051, 56, 72, 'آیا تم نے اس کا درخت پیدا کیا ہے یا ہم پیدا کرنے والے ہیں۔'),
(5052, 56, 73, 'ہم نے ہی اسے یاددہانی کا ذریعہ اور مسافروں کیلئے فائدہ کی چیز بنایا ہے۔'),
(5053, 56, 74, 'پس تم اپنے عظیم پروردگار کے نام کی تسبیح کرو۔'),
(5054, 56, 75, 'پس میں ستاروں کے (ڈو بنے کے) مقامات کی قَسم کھاتا ہوں۔'),
(5055, 56, 76, 'اور اگر تم سمجھو تو یہ بہت بڑی قَسم ہے۔'),
(5056, 56, 77, 'بےشک یہ قرآن بڑی عزت والا ہے۔'),
(5057, 56, 78, 'نگاہوں سے پوشیدہ ایک کتاب کے اندر ہے۔'),
(5058, 56, 79, 'اسے پاک لوگوں کے سوا کوئی نہیں چھو سکتا۔'),
(5059, 56, 80, 'یہ تمام جہانوں کے پروردگار کی طرف سے نازل کردہ ہے۔'),
(5060, 56, 81, 'کیا تم اس کتاب سے بےاعتنائی کرتے ہو؟'),
(5061, 56, 82, 'اور تم نے اپنے حصہ کی روزی یہی قرار دی ہے کہ اسے جھٹلاتے ہو۔'),
(5062, 56, 83, '(اگر تم کسی کے محکوم نہیں) تو جب (مرنے والے کی) روح حلق تک پہنچ جاتی ہے۔'),
(5063, 56, 84, 'اور تم اس وقت دیکھ رہے ہوتے ہو۔'),
(5064, 56, 85, 'اور اس وقت ہم تم سے زیادہ مرنے والے کے قریب ہوتے ہیں مگر تم دیکھتے نہیں۔'),
(5065, 56, 86, 'پس اگر تمہیں کوئی جزا و سزا ملنے والی نہیں ہے۔'),
(5066, 56, 87, 'تو پھر اس (روح) کو کیوں واپس لوٹا نہیں لیتے اگر تم (اس انکار میں) سچے ہو۔'),
(5067, 56, 88, 'پس اگر وہ (مرنے والا) مقربین میں سے ہے۔'),
(5068, 56, 89, 'تو اس کے لئے راحت، خوشبودار عذاب اور نعمت بھری جنت ہے۔'),
(5069, 56, 90, 'اور اگر وہ اصحاب الیمین میں سے ہے (تواس سے کہا جاتا ہے)۔'),
(5070, 56, 91, 'تمہارے لئے سلامتی ہو تو اصحاب الیمین میں سے ہے۔'),
(5071, 56, 92, 'اور اگر وہ جھٹلانے والے گمراہوں میں سے ہے۔'),
(5072, 56, 93, 'تو پھر اس کی مہمانی کھولتے ہوئے پانی سے ہوگی۔'),
(5073, 56, 94, 'اور دوزخ کی تپش (اور) اس میں داخلہ ہے۔'),
(5074, 56, 95, '(جو کچھ بیان ہوا) بےشک یہ حق الیقین (قطعی حق) ہے۔'),
(5075, 56, 96, 'پس اپنے عظیم پروردگار کے نام کی تسبیح کرو۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'ہر وہ چیز جو آسمانوں اور زمین میں ہے وہ اللہ کی تسبیح کرتی ہے وہ (ہر چیز پر) غالب (اور) بڑا حکمت والا ہے۔'),
(5077, 57, 2, 'آسمانوں اور زمین کی سلطنت اسی کے لئے ہے وہی حیات دیتا ہے اور وہی موت دیتا ہے اور وہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(5078, 57, 3, 'وہی اول ہے اور وہی آخر، وہی ظاہرہے اور وہی باطن اور وہ ہر چیز کا خوب جاننے والا ہے۔'),
(5079, 57, 4, 'وہ وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر غالب ہوا (اپنا اقتدار قائم کیا) وہ اسے بھی جانتا ہے جو زمین کے اندر داخل ہوتا ہے اور اسے بھی جو اس سے باہر نکلتا ہے اور اسے بھی جانتا ہے جو کچھ آسمان سے اترتا ہے اور جو کچھ اس کی طرف چڑھتا ہے اور وہ تمہارے ساتھ ہوتا ہے تم جہاں بھی ہو اور تم جو کچھ کرتے ہواللہ اسے دیکھ رہا ہے۔'),
(5080, 57, 5, 'آسمانوں اور زمین کی بادشاہت اسی کیلئے ہے اور تمام معاملات کی باز گشت اللہ ہی کی طرف ہے۔'),
(5081, 57, 6, 'وہی رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور وہ دلوں کے بھیدوں کو خوب جانتا ہے۔'),
(5082, 57, 7, 'اللہ اور اس کے رسول(ص) پر ایمان لاؤ اور اس (مال) میں سے (اس کی راہ میں) خرچ کرو جس میں اس نے تمہیں (دوسروں) کا جانشین بنایا ہے پس جو لوگ تم میں سے ایمان لائے اور (مال) خرچ کیا ان کیلئے بڑا اجر ہے۔'),
(5083, 57, 8, 'تمہیں کیا ہوا ہے کہ تم اللہ پر ایمان نہیں لاتے حالانکہ (اس کا) رسول تمہیں بلا رہا ہے کہ اپنے پروردگار پر ایمان لاؤ اور وہ (اللہ) تم سے عہد و پیمان لے چکا ہے اگر تم مؤمن ہو۔'),
(5084, 57, 9, 'وہ وہی ہے جو اپنے بندۂ خاص پر واضح آیات نازل کرتا ہے تاکہ تمہیں (کفر و شرک) کی تاریکیوں سے نکال کر (ایمان کی) روشنی کی طرف لے آئے اور بےشک اللہ تم پر بڑا مہربان ہے۔'),
(5085, 57, 10, 'اورتمہیں کیا ہوگیا ہے کہ تم (اپنے مال) اللہ کی راہ میں خرچ نہیں کرتے حالانکہ آسمانوں اور زمین کی میراث (ترکہ) اللہ ہی کیلئے ہے تم میں سے جنہوں نے فتح (مکہ) سے پہلے مال خرچ کیا اور جنگ کی وہ اور وہ جنہوں نے فتحِ مکہ کے بعد مال خرچ کیا اور جنگ کی برابر نہیں ہو سکتے (بلکہ پہلے والوں کا) درجہ بہت بڑا ہے اگرچہ اللہ نے دونوں سے بھلائی کا وعدہ کیا ہے تم جو کچھ کرتے ہواللہ اس سے بڑا باخبر ہے۔'),
(5086, 57, 11, 'کون ہے جو اللہ کو قرضۂ حسنہ دے تاکہ وہ اسے اس کے لئے (کئی گنا) بڑھائے اور اس کے لئے بہترین اجر ہے۔'),
(5087, 57, 12, 'جس دن تم دیکھو گے کہ مؤمن مَردوں اور مؤمن عورتوں کا نور ان کے آگے اور ان کے دائیں جانب دوڑ رہا ہوگا آج تمہیں مبارک ہو ان باغہائے بہشت کی جن کے نیچے نہریں جاری ہیں ہمیشہ ان میں رہو گے یہی وہ کامیابی ہے۔'),
(5088, 57, 13, 'جس دن منافق مَرد اور منافق عورتیں ایمان والوں سے کہیں گے کہ ذرا ہمارا انتظار کروکہ ہم بھی تمہارے نور سے کچھ روشنی حاصل کر لیں (ان سے کہا جائے گا کہ) تم پیچھے کی (دنیا میں) لوٹ جاؤ پھر وہیں روشنی تلاش کرو پس ان کے اور اہلِ ایمان کے درمیان ایک دیوار کھڑی کر دی جائے گی جس کا ایک دروزاہ ہوگا اس کے اندر کی طرف رحمت ہوگی اور اس کے باہر کے طرف عذاب ہوگا۔'),
(5089, 57, 14, 'وہ (منافق) اہلِ ایمان کو پکا ریں گے کہ کیا ہم تمہارے ساتھ نہ تھے؟ وہ کہیں گے ہاں تھے مگر تم نے اپنے آپ کو فتنہ (گمراہی) میں ڈالا اور (ہمارے بارے میں گردشوں کا) انتظار کیا (کہ نتیجہ کیا برآمد ہوتا ہے) اور شک و شبہ میں مبتلا رہے اور جھوٹی امیدوں نے تمہیں دھوکہ دیا یہاں تک کہ اللہ کا فیصلہ آگیا اور دھوکہ باز (شیطان) نے تمہیں اللہ کے بارے میں دھوکہ میں رکھا۔'),
(5090, 57, 15, 'پس آج نہ تم سے کوئی فدیہ قبول کیا جائے گا اور نہ ان سے جنہوں نے کفر کیا تم سب کا ٹھکانہ آتشِ دوزخ ہے وہی تمہارے لئے اولیٰ اور (سزوار) ہے وہ بہت بری جائے بازگشت ہے۔'),
(5091, 57, 16, 'کیا اہلِ ایمان کیلئے ابھی وہ وقت نہیںایا کہ ان کے دل خدا کی یاد اور (خدا کے) نازل کردہ حق کیلئے نرم ہوں اور وہ ان لوگوں کی طرح نہ ہو جائیں جن کو اس سے پہلے کتاب دی گئی تھی پس ان پر طویل مدت گزر گئی تو ان کے دل سخت ہوئے اور (آج) ان میں سے اکثر فاسق (نافرمان) ہیں۔'),
(5092, 57, 17, 'خوب جان لو کہ اللہ زمین کو زندہ کرتا ہے اس کی موت کے بعد اور ہم نے اپنی آیات تمہارے لئے واضح طور پر بیان کر دی ہیں تاکہ تم عقل سے کام لو۔'),
(5093, 57, 18, 'بیشک صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور جنہوں نے اللہ کو قرضۂ حسنہ دیا ان کیلئے اس میں (کئی گنا) اضافہ کر دیا جائے گا اور ان کیلئے بہترین اجر ہے۔'),
(5094, 57, 19, 'اور جو لوگ اللہ اور اس کے رسولوں(ع) پر ایمان لائے وہی لوگ اپنے پروردگار کے نزدیک صدیق اور شہید ہیں ان کیلئے ان کا اجر اور ان کا ثواب ہے اور جنہوں نے کفر کیا اور ہماری آیات کو جھٹلایا وہی لوگ دوزخ والے ہیں۔'),
(5095, 57, 20, 'اور خوب جان لو کہ دنیا کی زندگی کھیل تماشہ، ظاہری زیب و زینت، باہمی فخر و مباہات اورمال و اولاد میں ایک دوسرے سے بڑھ جانے کی کوشش ہے اس کے سو اکچھ نہیں ہے۔ اس (کے عارضی ہو نے) کی مثال ایسی ہے جیسے بارش کہ اس کی نباتات (پیداوار) کافروں (کسانوں) کو حیرت میں ڈال دیتی ہے پھر وہ خشک ہو کر پکنے لگتی ہے تو تم دیکھتے ہو کہ وہ زرد ہو گئی ہے پھر وہ ریزہ ریزہ ہوجاتی ہے اور (کفار کیلئے) آخرت میں سخت عذاب ہے اور (مؤمنین کیلئے) اللہ کی طرف سے بخشش اور خوشنودی ہے اور دنیاوی زندگی دھوکے کے ساز و سامان کے سوا کچھ نہیں ہے۔'),
(5096, 57, 21, 'ایک دوسرے سے آگے بڑھنے کی کوشش کرو اپنے پروردگار کی مغفرت اور اس جنت کی طرف جس کی چوڑائی آسمان و زمین کی چوڑائی کے برابر ہے جو ان لوگوں کیلئے تیار کی گئی ہے جو اللہ اور اس کے رسولوں(ع) پر ایمان لائیں اور یہ اللہ کا فضل و کرم ہے جسے چاہتا ہے عطا فرماتا ہے اور اللہ بڑا ہی فضل والا ہے۔'),
(5097, 57, 22, 'کوئی بھی مصیبت زمین میں آتی ہے اور نہ تمہاری جانوں میں مگر وہ ایک کتاب (لوحِ محفوظ) میں لکھی ہوئی ہوتی ہے قبل اس کے کہ ہم ان (جانوں) کو پیدا کر دیں بیشک یہ بات اللہ کیلئے آسان ہے۔'),
(5098, 57, 23, '(یہ سب اس لئے ہے) تاکہ جو چیز تم سے کھو جائے اس پر افسوس نہ کرو اور جو کچھ وہ (اللہ) تمہیں دے اس پر اِتراؤ نہیں کیونکہ اللہ ہر اِترانے والے، فخر کرنے والے کو پسند نہیں کرتا۔'),
(5099, 57, 24, 'جو خود بُخل کرتے ہیں اور دوسروں کو بھی بُخل کا حکم دیتے ہیں اور جو (احکامِ الٰہی سے) رُوگردانی کرے تو بےشک اللہ بےنیاز ہے (اور) سِتُودہ صفات ہے۔'),
(5100, 57, 25, 'یقیناً ہم نے اپنے رسولوں(ع) کو کھلی ہوئی دلیلوں (معجزوں) کے ساتھ بھیجا اور ان کے ساتھ کتاب اور میزان نازل کی تاکہ لوگ عدل و انصاف پر قائم ہوں اور ہم نے لوہا نازل کیا (پیدا کیا) جس میں بڑی قوت ہے (اور شدید ہیبت ہے) اور لوگوں کے لئے فائدے بھی ہیں تاکہ اللہ دیکھے کہ کون دیکھے بغیر اس کی اور اس کے رسولوں(ع) کی کون مدد کرتا ہے؟ بےشک اللہ بڑا طاقتور (اور) زبردست ہے۔'),
(5101, 57, 26, 'اور بےشک ہم نے نوح (ع) اور ابراہیم (ع) کو رسول بنا کر بھیجا اور ان دونوں کی اولاد میں نبوت اور کتاب قرار دی (جاری کر دی) پس ان میں سے بعض تو ہدایت یافتہ ہیں اور ان میں اکثر فاسق (بدکا ر) ہیں۔'),
(5102, 57, 27, 'پھر ہم نے انہی کے نقشِ قدم پر اپنے اور رسول(ع) بھیجے اور انہی کے پیچھے عیسیٰ (ع) بن مریم (ع) کو بھیجا اور انہیں انجیل عطا کی اور جن لوگوں نے ان کی پیروی کی ہم نے ان کے دلوں میں شفقت اور رحمت رکھ دی اور رہبانیت انہوں نے خود ایجاد کی ہم نے اسے ان پر فرض نہیں کیا تھا۔ ہاں البتہ انہوں نے اسے خدا کی خوشنودی کی خاطر اسے اختیار کیا تھا پھر انہوں نے اس کی پوری رعایت نہ کی تو جو لوگ ان میں ایمان لائے ہم نے ان کو ان کا اجر عطا کیا اور ان میں سے اکثر لوگ فاسق (نافرمان) ہیں۔'),
(5103, 57, 28, 'اے ایمان والو! اللہ (کی نافرمانی) سے ڈرو اور اس کے رسول(ع) پر (کماحقہٗ) ایمان لاؤ وہ تمہیں اپنی رحمت کے دو حصے عطا فر مائے گا اور تمہیں وہ نور عطا کرے گا کہ جس کی روشنی میں تم چلوگے اور (تمہارے قصور) تمہیں بخش دے گا اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(5104, 57, 29, 'تاکہ اہلِ کتاب یہ نہ جانیں کہ یہ لوگ (مسلمان) اللہ کے فضل میں سے کسی چیز پر کوئی اختیار نہیں رکھتے اور یہ کہ فضل اللہ کے قبضۂ قدرت میں ہے جسے چاہتا ہے عطا کرتا ہے اور اللہ بڑے فضل و کرم والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'بےشک اللہ نے اس عورت کی بات سن لی ہے جو اپنے شوہر کے بارے میں آپ(ص) سے بحث و تکرار کررہی ہے اور اللہ سے شکوہ و شکایت کر رہی ہے اور اللہ تم دونوں کی گفتگو سن رہا ہے بےشک اللہ بڑا سننے والا، بڑا دیکھنے والا ہے۔'),
(5106, 58, 2, 'تم میں سے جو لوگ اپنی بیویوں سے ظہار کرتے ہیں وہ ان کی مائیں نہیں ہیں (کیونکہ) ان کی مائیں تو بس وہی ہیں جنہوں نے انہیں جَنا ہے البتہ یہ لوگ ایک بہت بری بات اور جھوٹ کہتے ہیں اور بلاشبہ اللہ بڑا درگزر کرنے والا، بڑا بخشنے والا ہے۔'),
(5107, 58, 3, 'اور جو لوگ اپنی بیویوں سے ظہار کریں اور پھر اپنی کہی ہوئی بات سے رجوع کرنا چاہیں تو قبل اس کے کہ باہمی ازدواجی تعلق قائم کریں (شوہر کو) ایک غلام آزاد کرنا ہوگا۔ یہ بات ہے جس کی تمہیں نصیحت کی جاتی ہے اور تم جو کچھ کرتے ہو اللہ اس سے پوری طرح باخبر ہے۔'),
(5108, 58, 4, 'اور جوشخص (غلام) نہ پائے تو جنسی تعلق قائم کرنے سے پہلے دو ماہ پے در پے (لگاتار) روزے رکھنا ہوں گے اور جو اس پر بھی قدرت نہ رکھتا ہو تو پھر ساٹھ مسکینوں کو کھانا کھلانا ہے یہ اس لئے کہ تم اللہ اور اس کے رسول(ص) پر ایمان لاؤ (تمہارا ایمان راسخ ہو) یہ اللہ کی مقرر کردہ حدیں ہیں اور کافروں کیلئے دردناک عذاب ہے۔'),
(5109, 58, 5, 'بےشک جو لوگ اللہ اور اس کے رسول(ص) کی مخالفت کرتے ہیں وہ اسی طرح ذلیل و خوار ہوں گے جس طرح وہ (مخالف) لوگ ذلیل و خوار ہوئے جو ان سے پہلے تھے اور ہم نے کھلی ہوئی آیتیں نازل کر دی ہیں اور کافروں کیلئے ذلیل کرنے والا عذاب ہے۔'),
(5110, 58, 6, 'جس دن اللہ ان سب کو زندہ کر کے اٹھائے گا تو انہیں بتائے گا جو کچھ انہوں نے کیا تھا اللہ نے تو (ان کے اعمال کو) محفوظ رکھا مگر وہ بھول گئے اور اللہ ہر چیز پر شاہد ہے۔'),
(5111, 58, 7, 'کیا تم نے نہیں دیکھا (غور نہیں کیا) کہ اللہ وہ سب کچھ جانتا ہے جو آسمانوں میں ہے اور جو کچھ زمین میں ہے کہیں بھی تین آدمیوں کی سرگوشی نہیں ہوتی مگر وہ ان کا چوتھا ہوتاہے اور نہ پانچ کی ہوتی ہے مگر وہ ان کا چھٹا ہوتا ہے اور نہ اس سے کم اور نہ زیادہ مگر یہ کہ وہ جہاں بھی ہوں اللہ ان کے ساتھ ہوتا ہے پھر وہ قیامت کے دن انہیں بتائے گا کہ انہوں نے کیا کچھ کیا ہے؟ یقیناً اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(5112, 58, 8, 'کیا تم نے ان لوگوں کی طرف نہیں دیکھاجنہیں (اسلام کے خلاف) سرگوشیاں کرنے سے منع کیا گیا تھا وہ پھر وہی کام کرتے ہیں جس سے ان کو منع کیا گیا تھا اور وہ گناہ، ظلم و زیادتی اور رسول(ص) کی نافرمانی کی سرگوشیاں (خفیہ میٹنگیں) کرتے ہیں اور جب آپ(ص) کے پاس آتے ہیں تو آپ(ص) کو کس طرح سلام کرتے ہیں جس طرح اللہ نے آپ(ص) کو سلام نہیں کیا اور اپنے دلوں میں کہتے ہیں کہ (اگر یہ برحق نبی(ص) ہیں تو) اللہ ہماری ان باتوں پر ہم پر عذاب کیوں نازل نہیں کرتا؟ ان کے لئے جہنم کافی ہے جس میں وہ پڑیں گے (اور اس کی تپش اٹھائیں گے) سو وہ کیا برا ٹھکانہ ہے؟'),
(5113, 58, 9, 'اے ایمان والو! تم جب کوئی سرگوشی (خفیہ بات) کرو تو گناہ، ظلم و زیادتی اور رسول(ص) کی نافرمانی کے بارے میں سرگوشی نہ کرو بلکہ نیکی کرنے اور تقویٰ اختیار کرنے کی سرگوشی کرو اور اللہ (کی نافرمانی سے) ڈرو جس کے پاس جمع کئے جاؤ گے۔'),
(5114, 58, 10, '(کافروں کی یہ) سرگوشی شیطان کی طرف سے ہے تاکہ وہ اہلِ ایمان کو رنج و غم پہنچائے حالانکہ وہ اللہ کے اذن کے بغیر انہیں کچھ بھی نقصان نہیں پہنچا سکتا اور ایمان والوں کو اللہ پر ہی بھروسہ کرنا چاہیے۔'),
(5115, 58, 11, 'اے ایمان والو! جب تم سے کہا جائے کہ (آنے والوں کیلئے) مجلسوں میں جگہ کشادہ کرو تو کشادہ کر دیا کرو اللہ تمہیں کشادگی دے گااور جب کہا جائے کہ اٹھ جاؤ تو اٹھ جایا کرو اللہ ان لوگوں کے جو تم میں سے ایمان لائے ہیں اور جن کو علم دیا گیا ہے ان کے در جے بلند کرتا ہے اور تم جو کچھ کرتے ہواللہ اس سے بڑا باخبر ہے۔'),
(5116, 58, 12, 'اے ایمان والو! جب تم پیغمبر سے تنہائی میں کوئی بات کرنا چاہو تو اپنی اس رازدارانہ بات سے پہلے کچھ صدقہ دے دیا کرو یہ بات تمہارے لئے بہتر ہے اور پاکیزہ تر ہے اور اگر اس کے لئے کچھ نہ پاؤ تو بلاشبہ اللہ بڑا بخشنے والا، بڑا مہربان ہے۔'),
(5117, 58, 13, 'کیا تم اس بات سے ڈر گئے کہ (رسول(ص) سے) تنہائی میں بات کرنے سے پہلے صدقہ دو! اب جبکہ تم نے ایسانہیں کیا اور اللہ نے تمہاری توبہ قبول کر لی تو بس نماز قائم کرو اور زکوٰۃد و اور اللہ اور اس کے رسول(ص) کی اطاعت کرو اور تم جو کچھ کرتے ہواللہ اس سے اچھی طرح باخبر ہے۔'),
(5118, 58, 14, 'کیا تم نے ایسے لوگوں کو نہیں دیکھا جو ایسے لوگوں سے دوستی کرتے ہیں جن پر اللہ نے غضب نازل کیا ہے وہ نہ تم میں سے ہیں اور نہ ان میں سے ہیں اور وہ جانتے بوجھتے ہوئے جھوٹی بات پر قَسمیں کھاتے ہیں۔'),
(5119, 58, 15, 'اللہ نے ان کیلئے سخت عذاب تیار کر رکھا ہے بےشک یہ لوگ بہت برے کام کرتے ہیں۔'),
(5120, 58, 16, 'انہوں نے اپنی قَسموں کو ڈھال بنا رکھا ہے پس وہ (لوگوں کو) اللہ کی راہ سے روکتے ہیں تو ان کے لئے ذلیل کرنے والا عذاب ہے۔'),
(5121, 58, 17, 'ان کے مال اور ان کی اولاد انہیں ذرا بھی اللہ (کے عذاب) سے نہیں بچا سکیں گے یہ لوگ دوزخ والے ہیں وہ اسی میں ہمیشہ رہیں گے۔'),
(5122, 58, 18, 'جس دن اللہ ان سب کو (دوبارہ) اٹھائے گا تو وہ اس کے سامنے بھی اسی طرح قَسمیں کھائیں گے جس طرح تمہارے سامنے کھاتے ہیں اور خیال کریں گے کہ وہ کسی چیز (بنیاد) پر ہیں خبردار! یہ لوگ بالکل جھوٹے ہیں۔'),
(5123, 58, 19, 'شیطان نے ان پر غلبہ پا لیا ہے اور اس نے انہیں خدا کی یاد بُھلا دی ہے یہ لوگ شیطان کا گروہ ہیں آگاہ ہو کہ شیطانی گروہ ہی گھاٹا اٹھانے والا ہے۔'),
(5124, 58, 20, 'جو لوگ اللہ اور اس کے رسول(ص) کی مخالفت کرتے ہیں وہ ذلیل ترین لوگوں میں سے ہیں۔'),
(5125, 58, 21, 'اللہ نے لکھ دیا ہے کہ میں اور میرے رسول(ص) ہی غالب آکر رہیں گے بےشک اللہ طاقتور (اور) زبردست ہے۔'),
(5126, 58, 22, 'تم کوئی ایسی قوم نہیں پاؤگے جو اللہ اور آخرت کے دن پر ایمان رکھتی ہو (اور پھر) وہ دوستی رکھے ان لوگوں سے جو خدا و رسول(ص) کے مخالف ہیں اگرچہ وہ (مخالف) ان کے باپ یا ان کے بیٹے یا ان کے بھائی ہی ہوں یا ان کے قبیلے والے یہ وہ لوگ ہیں کہ اللہ نے ان کے دلوں میں ایمان ثبت کر دیا ہے اور اپنی ایک خاص روح سے ان کی تائید کی ہے اور انہیں ایسے باغہائے بہشت میں داخل کرے گا جن کے نیچے نہریں جاری ہیں جن میں وہ ہمیشہ رہیں گے اللہ ان سے راضی ہے اور وہ اس سے راضی ہیں یہ لوگ اللہ کا گروہ ہیں آگاہ رہو کہ اللہ کا گروہ ہی فلاح پانے والا (اور کامیاب ہونے والا) ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'اللہ ہی کی تسبیح کرتی ہے ہر وہ چیز جو آسمانوں میں ہے اور جو زمین میں ہے اور وہ زبردست، بڑا حکمت والا ہے۔'),
(5128, 59, 2, 'وہ وہی ہے جس نے اہلِ کتاب کے کافروں (بنی نضیر) کو پہلی بار اکٹھا کر کے ان کے گھروں سے نکال دیا۔ تمہیں گمان بھی نہ تھا کہ وہ نکل جائیںگے اور وہ بھی خیال کرتے تھے کہ ان کے قلعے انہیں اللہ (کی گر فت) سے بچا لیں گے تو اللہ (کا قہر) ایسی جگہ سےایا جہاں سے ان کو خیال بھی نہیں تھا اور اس نے ان کے دلوں میں (رسول(ص) کا) رعب ڈال دیا تو وہ اپنے ہی ہاتھوں سے اپنے گھروں کو خراب و برباد کر رہے تھے اور اہلِ ایمان کے ہاتھوں سے بھی عبرت حاصل کرو اے دیدۂ بینا رکھنے والو۔'),
(5129, 59, 3, 'اور اگر اللہ نے ان کے حق میں جلا وطنی نہ لکھ دی ہوتی تووہ دنیا ہی میں ان کو عذاب دیتا اور آخرت میں تو ان کے لئے دوزخ کا عذاب ہے ہی۔'),
(5130, 59, 4, 'یہ اس لئے ہے کہ انہوں نے اللہ اور اس کے رسول(ص) کی مخالفت کی اور جو اللہ کی مخالفت کرتا ہے تو یقیناً اللہ سخت سزا دینے والا ہے۔'),
(5131, 59, 5, 'تم لوگوں نے کھجوروں کے جو درخت کاٹے یا جن کو ان کی جڑوں پر کھڑا رہنے دیا یہ سب اللہ کی اجازت سے تھا اور تاکہ وہ فاسقوں کو رسوا کرے۔'),
(5132, 59, 6, 'اور اللہ نے ان لوگوں (بنی نضیر) سے جو مال بطور فئے اپنے رسول(ص) کو دلوایا تو تم لوگوں نے اس پر نہ گھوڑے دوڑائے اور نہ اونٹ پس اس میں تمہارا کوئی حق نہیں ہے لیکن اللہ اپنے رسولوں(ع) کو جس پر چاہتا تسلط دے دیتا ہے اور اللہ ہر چیز پر بڑی قدرت رکھتا ہے۔'),
(5133, 59, 7, 'تو اللہ نے ان بستیوں والوں کی طرف سے جو مال بطور فئے اپنے رسول(ص) کو دلوایا ہے وہ بس اللہ کا ہے اور پیغمبر(ص) کا اور(رسول(ص) کے) قرابتداروں (ان کے) یتیموں اور (ان کے) مسکینوں اور مسافروں کا ہے تاکہ وہ مالِ فئے تمہارے دولتمندوں کے درمیان ہی گردش نہ کرتا رہے اور جو کچھ رسول(ص) تمہیں دیں وہ لے لو اور جس سے منع کریں اس سے رک جاؤ اور اللہ (کی نافرمانی) سے ڈرو۔ بےشک اللہ سخت سزا دینے والا ہے۔'),
(5134, 59, 8, '(نیز وہ مال) ان غریب مہاجروں کیلئے ہے جن کو اپنے گھروں اور مالوں سے نکال دیا گیا یہ اللہ کا فضل اور اس کی خوشنودی چاہتے ہیں اور اللہ اور اس کے رسول(ص) کی مدد کرتے ہیں اور یہی سچے لوگ ہیں۔'),
(5135, 59, 9, '(اور یہ مال) ان کیلئے بھی ہے جو ان (مہاجرین) سے پہلے ان دیار (دارالہجرت مدینہ) میں ٹھکانا بنائے ہوئے ہیں اور ایمان لائے ہوئے ہیں اور جو ہجرت کرکے ان کے پاس آتا ہے وہ اس سے محبت کرتے ہیں اور جو کچھ ان (مہاجرین) کو دیا جائے وہ اس کی اپنے دلوں میں کوئی ناخوشی محسوس نہیں کرتے اور وہ اپنے اوپر دوسروں کو ترجیح دیتے ہیں اگرچہ خود ضرورت مند ہوں (فاقہ میں ہوں) اور جسے اپنے نفس کے حرص سے بچا لیا گیا وہی فلاح پانے والے ہیں۔'),
(5136, 59, 10, 'اور جو ان کے بعد آئے (ان کا بھی اس مال میں حصہ ہے) جو کہتے ہیں اے ہمارے پروردگار ہمیں بخش دے اور ہمارے ان بھائیوں کو بھی بخش دے جنہوں نے ایمان لانے میں ہم پر سبقت کی اور ہمارے دلوں میں اہلِ ایمان کے لئے کینہ پیدا نہ کر۔ اے ہمارے پروردگار یقیناً تو بڑا شفقت کرنے والا، بڑا رحم کرنے والا ہے۔'),
(5137, 59, 11, 'کیا تم نے منافقوں کو نہیں دیکھا جو اپنے کافر اہلِ کتاب بھائیوں سے کہتے ہیں کہ اگر تم (اپنے گھروں سے) نکالے گئے تو ہم بھی تمہارے ساتھ (شہر سے) نکل جائیں گے اور ہم تمہارے معاملہ میں کبھی کسی کی اطاعت نہیں کریں گے اور اگرتم سے جنگ کی گئی تو ہم ضرور تمہاری مدد کریں گے اور اللہ گواہی دیتا ہے کہ یہ لوگ بالکل جھوٹے ہیں۔'),
(5138, 59, 12, 'اگر وہ نکالے گئے تو یہ ان کے ساتھ نہیں نکلیں گے اور اگر ان سے جنگ کی گئی تو یہ ان کی مدد نہیں کریں گے اور اگر ان کی مدد کی بھی تو ضرور پیٹھ پھیر کر بھاگیں گے پھر ان کی کوئی مدد نہیں کی جائے گی۔'),
(5139, 59, 13, 'یقیناً تم لوگوں کا ڈر ان کے دلوں میں اللہ سے زیادہ ہے یہ اس لئے کہ یہ ایسے لوگ ہیں جو سمجھ نہیں رکھتے۔'),
(5140, 59, 14, 'یہ سب مل کر کبھی تم سے (کھلے میدان میں) جنگ نہیں کریں گے مگر قلعہ بند بسیتوں میں (بیٹھ کر) یا دیواروں کے پیچھے سے (چھپ کر) وہ آپس میں بڑے قوی ہیں اور تم خیال کرتے ہو کہ وہ متحد ہیں حالانکہ ان کے دل متفرق ہیں یہ اس لئے ہے کہ یہ بےعقل لوگ ہیں۔'),
(5141, 59, 15, 'یہ ان لوگوں کی طرح ہیں جو ان سے کچھ ہی پہلے اپنے کرتوتوں کا مزہ چکھ چکے ہیں اور ان کیلئے دردناک عذاب ہے۔'),
(5142, 59, 16, 'اور یہ شیطان کی مانند ہیں جو (پہلے) انسان سے کہتا ہے کہ کفر اختیار کر اور جب وہ کافر ہو جاتا ہے تو وہ (شیطان) کہتا ہے کہ میں تجھ سے بیزار ہوں میں اللہ سے ڈرتا ہوں جو رب العالمین ہے۔'),
(5143, 59, 17, 'تو ان دونوں (شیطان اور اس کے مرید) کا انجام یہ ہوا کہ دونوں دوزخ میں ہیں (اور) ہمیشہ ہمیشہ اسی میں رہیں گے اور یہی ظالموں کی سزا ہے۔'),
(5144, 59, 18, 'اے ایمان والو! اللہ (کی مخالفت سے) ڈرو اور ہر شخص کو یہ دیکھنا چاہیے کہ اس نے کل (قیامت) کیلئے آگے کیا بھیجا ہے اللہ سے ڈرو بےشک جو کچھ تم کرتے ہو اللہ اس سے اچھی طرح باخبر ہے۔'),
(5145, 59, 19, 'اور ان لوگوں کی طرح نہ ہو جاؤ جو اللہ کو بھول گئے تواللہ نے ان کو اپنا آپ بھلا دیا یہی لوگ فاسق (نافرمان) ہیں۔'),
(5146, 59, 20, 'دوزخ والے اور جنت والے برابر نہیں ہیں جنت والے کامیاب ہو نے والے ہیں۔'),
(5147, 59, 21, 'اگر ہم اس قرآن کو کسی پہاڑ پر نازل کرتے تو تم دیکھتے کہ وہ خدا کے خوف سے جھک جاتا اور پاش پاش ہو جاتا اور یہ مثالیں ہم اس لئے لوگوں کیلئے بیان کرتے ہیں تاکہ وہ غور و فکر کریں۔'),
(5148, 59, 22, 'وہی وہ اللہ ہے جس کے سوا کوئی الٰہ نہیں ہے ہر غائب اور حاضر کا جاننے والا ہے (اور) وہی رحمٰن و رحیم ہے۔'),
(5149, 59, 23, 'وہی اللہ ہے جس کے سوا کوئی الٰہ نہیں ہے وہ (حقیقی) بادشاہ، (ہر عیب سے) مقدس (پاک)، سراپا سلامتی، امن و امان دینے والا، نگہبانی کرنے والا، غالب آنے والا زور آور (یا سرکشوں کو زیر کرنے والا) اور کبریائی و بڑائی والا ہے (اور) اللہ اس شرک سے پاک ہے جو لوگ کرتے ہیں۔'),
(5150, 59, 24, 'وہی اللہ (ہر چیز کا) ٹھیک اندازے کے مطابق پیدا کرنے والا (وجود میں لانے والا) اور صورت گری کرنے والا ہے اسی کیلئے سارے اچھے نام ہیں (اور) اسی کی تسبیح کرتی ہے ہر وہ چیز جو آسمانوں میں ہے اور جو زمین میں ہے اور وہ غالب آنے والا (اور) بڑا حکمت والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اے ایمان والو! تم میرے اور اپنے دشمن کو اپنا ایسا (جگری) دوست نہ بناؤ کہ ان سے محبت کا اظہار کرنے لگو حالانکہ وہ اس (دینِ) حق کے منکر ہیں جو تمہارے پاس آیا ہے اور وہ رسولِ خدا(ص) کو اور خود تم کو محض اس بنا پر جلا وطن کرتے ہیں کہ تم اپنے پروردگار اللہ پر ایمان لائے ہو (یہ سب کچھ کرو) اگر تم میری راہ میں جہاد کرنے اور میری خوشنودی حاصل کرنے کیلئے (اپنے گھروں سے) نکلے ہو؟ تم چھپ کر ان کو دوستی کا پیغام بھیجتے ہو حالانکہ میں خوب جانتا ہوں جو کچھ تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہواور جو تم میں سے ایسا کرے وہ راہِ راست سے بھٹک گیا۔'),
(5152, 60, 2, 'اگر یہ لوگ تم پر قابو پا جائیں تو وہ تمہارے دشمن ہی ہوں گے اور برائی کے ساتھ (تمہیں آزار پہنچانے کیلئے) تمہاری طرف اپنے ہاتھ بڑھائیں گے اور اپنی زبانیں بھی اور وہ چاہیں گے کہ کاش تم کافر ہو جاؤ۔'),
(5153, 60, 3, 'تمہارے رشتہ دار اور تمہاری اولاد قیامت کے دن تمہارے کام نہ آئیں گے اللہ تمہارے درمیان جدائی ڈال دے گا اور جو کچھ تم کرتے ہو اللہ اسے اچھی طرح دیکھ رہا ہے۔'),
(5154, 60, 4, 'یقیناً تم لوگوں کیلئے (جنابِ) ابراہیم(ع) اوران کے ساتھیوں میں ایک عمدہ نمونہ ہے جبکہ انہوں نے اپنی قوم سے کہا کہ ہم تم سے بیزار ہیں اور ان سے بھی جن کی تم اللہ کے سوا پرستش کرتے ہو ہم تمہارے (اور تمہارے دین کے) منکر ہیں اور ہمارے اور تمہارے درمیان ہمیشہ کیلئے عداوت اور بغض پیدا ہوگیا یہاں تک کہ تم اللہ واحد (لا شریک لہٗ) پر ایمان لاؤ۔ ہاں البتہ ابراہیم (ع) نے اپنے (منہ بولے) باپ (اور اصل چچا) سے صرف یہ کہا تھا کہ میں تمہارے لئے مغفرت طلب کروں گا مگر میں اللہ کے سامنے تمہارے لئے کسی نفع کا مالک و مختار نہیں ہوں اے ہمارے پروردگار! ہم نے تجھ پر بھروسہ کیا ہے اور تیری ہی طرف رجوع کیا ہے اور تیری ہی طرف لَوٹنا ہے۔'),
(5155, 60, 5, 'اے ہمارے پروردگار! ہمیں کافروں کیلئے فتنہ نہ بنا اور ہمیں بخش دے اے ہمارے پروردگار! یقیناً تو غالب (اور) بڑی حکمت والا ہے۔'),
(5156, 60, 6, 'بےشک تمہارے لئے ان لوگوں کے اندر عمدہ نمونہ موجود ہے جو اللہ اور روزِ آخرت کا امیدوار ہے (اعتقاد رکھتا ہے) اور جو رُوگردانی کرے تو اللہ بےنیاز ہے اور حمد کا سزاوار ہے۔'),
(5157, 60, 7, 'بہت ممکن ہے کہ اللہ تمہارے درمیان اور ان لوگون کے درمیان جن سے (آج) تمہاری دشمنی ہے کبھی محبت پیدا کر دے اور اللہ بڑی قدرت والا ہے اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(5158, 60, 8, 'اللہ تمہیں اس بات سے منع نہیں کرتا کہ جن لوگون نے دین کے معاملہ میں تم سے جنگ نہیں کی اور نہ ہی تمہیں تمہارے گھروں سے نکالا ہے کہ تم ان کے ساتھ نیکی کرو اور ان کے ساتھ انصاف کرو بےشک اللہ انصاف کرنے والوں کو دوست رکھتا ہے۔'),
(5159, 60, 9, 'اللہ تو صرف تمہیں ان لوگوں سے دوستی کرنے سے منع کرتا ہے جنہوں نے دین کے بارے میں تم سے جنگ کی اور تمہیں تمہارے گھروں سے نکالا اور تمہارے نکا لنے میں ایک دوسرے کی مدد کی اور جو ان سے دوستی کرے گا وہی ظالم ہیں۔'),
(5160, 60, 10, 'اے ایمان والو! جب مؤمن عورتیں ہجرت کر کے تمہارے پاس آئیں تو ان کا امتحان لے لیا کرو اللہ ان کے ایمان کی (اصلیت) کو بہتر جانتا ہے پس اگر تمہیں معلوم ہو جائے کہ وہ مؤمن ہیں تو پھر انہیں کافروں کی طرف واپس نہ کرو۔ نہ وہ (مؤمن عورتیں) ان (کافروں) کیلئے حلال ہیں اور نہ وہ (کافر) ان عورتوں کیلئے حلال ہیں اور ان (کافر شوہروں) نے (حق مہر وغیرہ کے سلسلہ میں) جو کچھ خرچ کیا ہے وہ انہیں ادا کر دو اور تمہارے لئے کوئی مضائقہ نہیں ہے کہ تم ان سے نکاح کرو جبکہ تم ان کے مہر ان کو ادا کر دو اور تم (بھی) کافر عورتوں کو (اپنے نکاح میں) روکے نہ رکھو اور تم نے (ان پر جو مہر) خرچ کیا ہے وہ (ان سے) واپس مانگ لو اورکافر بھی (مسلمان بیویوں سے مہر واپس) مانگ لیں جو انہوں نے خرچ کیا ہے یہ اللہ کا فیصلہ ہے جو تمہارے درمیان فیصلہ کرتا ہے اور اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(5161, 60, 11, 'اور اگر تمہاری بیویوں میں سے کوئی (کفر یا ارتداد کی وجہ سے) کافروں کے پاس چلی جائے پھر تم ظفریاب ہو جاؤ (تو مالِ غنیمت میں سے) ان لوگوں کو جن کی بیویاں چلی گئی ہیں اتنا مال دو جتنا انہوں نے خرچ کیا ہو اور اللہ سے ڈرو جس پر تم ایمان رکھتے ہو۔'),
(5162, 60, 12, 'اے نبی(ص)! جب مؤمن عورتیں بیعت کرنے کیلئے آپ کے پاس آئیں تو ان سے ان شرائط پر لو کہ وہ کسی چیز کو اللہ کا شریک نہیں ٹھہرائیں گی، چوری نہیں کریں گی، زنا نہیں کریں گی، اپنی اولاد کو قتل نہیں کریں گی، اور اپنے ہاتھوں اور پاؤں کے درمیان کوئی بہتان گھڑ کر (اپنے شوہروں پر) نہیں باندھیں گی (کسی غیر کے بچے کو شوہر کا بچہ قرار نہیں دیں گی)، اور نہ ہی کسی نیکی کے کام میں آپ(ص) کی نافرمانی کریں گی ان سے بیعت لے لیجئے اور اللہ سے ان کیلئے دعائے مغفرت کیجئے۔ بےشک اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(5163, 60, 13, 'اے ایمان والو! ان لوگوں سے دوستی نہ کرو جن پر اللہ نے اپنا غضب نازل کیا ہے وہ آخرت سے اس طرح ناامید ہیں جس طرح کافر لوگ قبروں میں گڑے ہوئے مردوں (کے دوبارہ زندہ ہونے) سے ناامید ہیں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'ہر وہ چیز جو آسمانوں میں ہے اور ہر وہ چیز جو زمین میں ہے اللہ کی تسبیح کرتی ہے اور وہ (سب پر) غالب ہے (اور) بڑا حکمت والا ہے۔'),
(5165, 61, 2, 'اے ایمان والو! تم وہ بات کیوں کہتے ہو جو کرتے نہیں ہو؟'),
(5166, 61, 3, 'اللہ کے نزدیک یہ بڑی ناراضی کی بات ہے کہ تم کہو ایسی بات جو کرو نہیں۔'),
(5167, 61, 4, 'بےشک خدا ان (مجاہدوں) کو دوست رکھتا ہے جو اس کی راہ میں یوں صف بستہ ہو کر (پَراجما کر) جنگ کرتے ہیں کہ گویا وہ سیسہ پلائی ہوئی دیوار ہیں۔'),
(5168, 61, 5, 'اور وہ وقت یاد کرو جب موسیٰ(ع) نے اپنی قوم سے کہا کہ میری قوم! تم کیوں مجھے اذیت دیتے ہو؟ حالانکہ تم خوب جانتے ہو کہ میں تمہاری طرف اللہ کا(بھیجا ہوا) رسول ہوں۔ پس جب وہ لوگ ٹیڑھے ہوگئے تو اللہ نے ان کے دلوں کو (اور بھی) ٹیڑھا کر دیا اور اللہ فاسقوں کو ہدایت نہیں دیتا (انہیں منزلِ مقصود تک نہیں پہنچاتا)'),
(5169, 61, 6, 'اور وہ وقت یاد کرو جب عیسیٰ(ع) بن مریم(ع) نے کہا کہ اے بنی اسرائیل! میں تمہاری طرف اللہ کا (بھیجا ہوا) رسول ہوں میں اس تورات کی تصدیق کرنے والا ہوں جو مجھ سے پہلے آئی ہے (اور جو موجود ہے) اورخوشخبری دینے والا ہوں ایک رسول(ص) کی جو میرے بعد آئے گا جس کا نام احمد(ص) ہوگا پھر جب وہ کھلی نشانیاں (معجزے) لے کر ان کے پاس آئے تو ان لوگوں نے کہا کہ یہ تو کھلا ہو جادو ہے۔'),
(5170, 61, 7, 'اور اس شخص سے بڑا ظالم کون ہوگا جو اللہ پر جھوٹا بہتان باندھے حالانکہ اسے اسلام کی طرف بلایا جا رہا ہے اور اللہ ظالم لوگوں کو ہدایت نہیں دیتا (انہیں منزلِ مقصود تک نہیں پہنچاتا)'),
(5171, 61, 8, 'یہ لوگ چاہتے ہیں کہ اللہ کے نور کو اپنے منہ (پھونکوں) سے بجھا دیں حالانکہ اللہ اپنے نور کو کامل کرکے رہے گا اگرچہ کافر لوگ ناپسند ہی کریں۔'),
(5172, 61, 9, 'وہ وہی ہے جس نے اپنے رسول(ص) کو ہدایت اور دینِ حق کے ساتھ بھیجا تاکہ اسے تمام دینوں پر غالب کر دے اگرچہ مشرکین ناپسند کریں۔'),
(5173, 61, 10, 'اے ایمان والو! کیا میں تمہیں ایک تجارت بتا‎ؤں جو (اگر کرو تو) تمہیں دردناک عذاب سے بچالے۔'),
(5174, 61, 11, '(اور وہ یہ ہے کہ) تم اللہ اور اس کے رسول(ص) پر ایمان لاؤ اور اللہ کی راہ میں اپنے مال اور اپنی جان سے جہاد کرو یہ بات تمہارے لئے بہتر ہے اگر تم علم رکھتے ہو۔'),
(5175, 61, 12, '(اگر ایسا کروگے تو) اللہ تمہارے گناہ بخش دے گا اور تمہیں ان باغہائے بہشت میں داخل کرے گا جن کے نیچے نہریں جاری ہیں اور ان پاکیزہ مکانوں میں داخل کرے گا جو ہمیشہ رہنے والے باغات میں ہوں گے یہی بہت بڑی کامیابی ہے۔'),
(5176, 61, 13, 'اور ایک اور چیز بھی ہے جسے تم پسند کرتے ہو (وہ اس کے علاوہ ہے) یعنی اللہ کی مدد اور قریبی فتح و کامیابی اور ایمان والوں کو بشارت دے دیجئے۔'),
(5177, 61, 14, 'اے ایمان والو! اللہ کے (دین کے) انصار و مددگار بنو جیسا کہ عیسٰی(ع) بن مریم(ع) نے حواریوں سے کہا تھا کہ اللہ کی طرف بلانے میں کون میرا مددگار ہے؟ حواریوں نے (جواب میں) کہا ہم اللہ کے (دین کے) مددگار ہیں تو بنی اسرائیل میں سے ایک گروہ ایمان لایا اور ایک گروہ نے کفر اختیار کیا تو جو ایمان لائے تھے ہم نے ان کے دشمن کے مقابلہ میں ان کی تائید کی پس وہ غالب ہو گئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'وہ سب چیزیں جو آسمانوں میں اور زمین میں ہیں اس اللہ کی تسبیح کرتی ہیں جو بادشاہ ہے (اور نقائص سے) پاک ہے زبردست ہے (اور) بڑا حکمت والا ہے۔'),
(5179, 62, 2, 'وہ (اللہ) وہی ہے جس نے اُمّی قوم میں انہیں میں سے ایک رسول(ص) بھیجا جو ان کو اس (اللہ) کی آیتیں پڑھ کر سناتا ہے اور ان کو پاکیزہ بناتا ہے اور ان کو کتاب و حکمت کی تعلیم دیتا ہے اگرچہ اس سے پہلے یہ لوگ کھلی ہوئی گمراہی میں مبتلا تھے۔'),
(5180, 62, 3, 'اور (آپ(ص) کو) ان لوگوں میں سے دوسروں کی طرف بھی بھیجا جو ابھی ان سے نہیں ملے ہیں اور وہ زبردست (اور) بڑا حکمت والا ہے۔'),
(5181, 62, 4, 'یہ اللہ کا فضل و کرم ہے جسے چاہتا ہے عطا کرتا ہے اور اللہ بڑے فضل و کرم والا ہے۔'),
(5182, 62, 5, 'اور جن لوگوں (یہود) کو توراۃ کا حامل بنایا گیا مگر انہوں نے اسکو نہ اٹھایا ان کی مثال اس گدھے کی سی ہے جس نے بڑی بڑی کتابیں اٹھائی ہوئی ہوں اور کیا بری مثال ہے ان لوگوں کی جنہوں نے اللہ کی آیتوں کو جھٹلایا اور اللہ ظالموں کو ہدایت نہیں دیتا (انہیں منزلِ مقصود تک نہیں پہنچاتا)۔'),
(5183, 62, 6, 'آپ(ص) کہیے! اے یہودیو! اگر تمہارا خیال ہے کہ سب لوگوں کو چھوڑ کر تم ہی اللہ کے دوست ہو تو پھر موت کی تمنا کرو اگر تم (اپنے دعویٰ میں) سچے ہو۔'),
(5184, 62, 7, 'اور وہ ہرگز اس کی تمنا نہیں کریں گے اپنے ان (برے) اعمال کی وجہ سے جو وہ آگے بھیج چکے ہیں اور اللہ ظالموں کو اچھی طرح جانتا ہے۔'),
(5185, 62, 8, 'آپ(ص) کہہ دیجئے! کہ وہ موت جس سے تم بھاگتے ہو وہ بہرحال تم سے مل کر رہے گی پھر تم اس (خدا) کی بارگاہ میں لوٹائے جاؤ گے جو غائب اور حاضر کا جاننے والا ہے پھر وہ تمہیں بتائے گا جو کچھ تم کرتے رہے ہو۔'),
(5186, 62, 9, 'اے ایمان والو! جب تمہیں جمعہ کے دن والی نمازکے لئے پکارا جائے (اس کی اذان دی جائے) تو اللہ کے ذکر (نمازِ جمعہ) کی طرف تیز چل کر جاؤ اورخرید و فروخت چھوڑ دو۔ یہ بات تمہارے لئے بہتر ہے اگر تم جانتے ہو۔'),
(5187, 62, 10, 'پھر جب نماز تمام ہو چکے تو زمین میں پھیل جاؤ اور اللہ کا فضل (روزی) تلاش کرو اور اللہ کو بہت یاد کرو تاکہ تم فلاح پاؤ۔'),
(5188, 62, 11, 'اور (کئی لوگوں کی حالت یہ ہے کہ) جب کوئی تجارت یا کھیل تماشہ دیکھتے ہیں تو اس کی طرف دوڑ پڑتے ہیں اور آپ(ص) کو کھڑا ہوا چھوڑ دیتے ہیں آپ(ص) کہہ دیجئے! کہ جو کچھ (اجر و ثوا ب) اللہ کے پاس ہے وہ کھیل تماشہ اور تجارت سے بہتر ہے اور اللہ بہترین رزق دینے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, '(اے رسول(ص)) جب منافق لوگ آپ کے پاس آتے ہیں تو کہتے ہیں کہ ہم گواہی دیتے ہیں کہ بےشک آپ اللہ کے رسول(ص) ہیں اور اللہ جانتا ہے کہ یقیناً آپ اس کے رسول(ص) ہیں لیکن اللہ گواہی دیتا ہے کہ منافق بالکل جھوٹے ہیں۔'),
(5190, 63, 2, 'انہوں نے اپنی قَسموں کو ڈھال بنا رکھا ہے پس اس طرح وہ خود اللہ کی راہ سے رکتے ہیں اور دوسروں کو روکتے ہیں بیشک بہت برا ہے وہ کام جو یہ کر رہے ہیں۔'),
(5191, 63, 3, 'یہ اس لئے ہے کہ یہ پہلے (بظاہر) ایمان لائے پھر کفر کیا تو ان کے دلوں پر (گویا) مہر لگا دی گئی ہے لہذا وہ کچھ نہیں سمجھتے ہیں۔'),
(5192, 63, 4, 'اور جب آپ(ص) انہیں دیکھیں گے تو ان کے جسم (اور ان کے قد و قا مت) آپ(ص) کو اچھے لگیں گے اور اگر وہ بات کریں گے تو آپ(ص) ان کی بات (توجہ) سے سنیں گے (مگر وہ عقل و ایمان سے خالی ہیں) گویا (کھوکھلی) لکڑیاں ہیں جو (دیوار وغیرہ سے) ٹیک لگا دی گئی ہیں وہ ہر چیخ کی آواز کو اپنے خلاف سمجھتے ہیں یہی (اصلی) دشمن ہیں ان سے بچ کے رہو اللہ ان کو غارت کرے یہ کہاں الٹے پھرائے جا رہے ہیں۔'),
(5193, 63, 5, 'اور جب ان سے کہاجائے کہ آؤ اللہ کا پیغمبر تمہارے لئے مغفرت طلب کرے تو وہ اپنا سر پھیر لیتے ہیں اور آپ انہیں دیکھیں گے کہ وہ تکبر کرتے ہو ئے (آنے سے) رک جاتے ہیں۔'),
(5194, 63, 6, 'ان کیلئے برابر ہے کہ آپ ان کیلئے مغفرت طلب کریں یا طلب نہ کریں اللہ ہرگز ان کو نہیں بخشے گا بےشک اللہ فاسقوں کو ہدایت نہیں دیتا (اورانہیں منزلِ مقصود تک نہیں پہنچاتا)۔'),
(5195, 63, 7, 'یہی وہ لوگ ہیں جو کہتے ہیں کہ جو لوگ رسولِ خدا(ص) کے پاس ہیں ان پر (اپنا مال) خرچ نہ کرو تاکہ وہ منتشر ہو جائیں حالانکہ آسمان اور زمین کے خزانے اللہ ہی کے ہیں مگر منافق لوگ سمجھتے نہیں ہیں۔'),
(5196, 63, 8, 'وہ کہتے ہیں کہ اگر ہم لَوٹ کرمدینہ گئے تو عزت والے لوگ وہاں سے ذلیل لوگوں کو نکال دیں گے حالانکہ ساری عزت تو صرف اللہ، اس کے رسول(ص) اور مؤمنین کیلئے ہے لیکن منافق لوگ (یہ حقیقت) جانتے نہیں ہیں۔'),
(5197, 63, 9, 'اے ایمان والو! تمہارا مال اور تمہاری اولاد تمہیں خدا کی یاد سے غافل نہ کردے اور جو ایسا کرے گا وہی لوگ گھاٹا اٹھانے والے ہیں۔'),
(5198, 63, 10, 'اور جو کچھ ہم نے تمہیں رزق دیا ہے اس سے (میری راہ میں) خرچ کرو قبل اس سے کہ تم میں سے کسی کو موت آجائے اور پھر وہ کہے کہ میرے پروردگار! تونے مجھے کیوں نہ تھوڑی سی مدت تک مہلت دی کہ میں صدقہ دیتا اور نیکوکار بندوں میں سے ہو جاتا۔'),
(5199, 63, 11, 'اورجب کسی شخص کی مدت پوری ہو جائے توخدا مزید مہلت نہیں دیتا اور اللہ جانتا ہے جو کچھ تم کرتے ہو۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'وہ سب چیزیں جو آسمانوں اور زمین میں ہیں اس اللہ کی تسبیح کرتی ہیں جو (حقیقی) بادشاہ ہے اور اسی کیلئے (ہر) تعریف ہے اور وہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(5201, 64, 2, 'وہ وہی ہے جس نے تم (سب) کو پیدا کیا پھر تم میں سے کوئی کافر ہے اور کوئی مؤمن اور تم جو کچھ کرتے ہواللہ اسے خوب دیکھ رہا ہے۔'),
(5202, 64, 3, 'اس نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا ہے اوراس نے تمہاری صورت گری کی تو تمہاری بہت اچھی صورتیں بنائیں اور اسی کی طرف (سب نے) لوٹ کر جانا ہے۔'),
(5203, 64, 4, 'وہ ہر اس چیز کو جانتا ہے جو آسمانوں اور زمین میں ہے اوروہ اسے بھی جانتا ہے جو کچھ تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہو اور اللہ تو سینوں کے بھیدوں کا بھی خوب جاننے والا ہے۔'),
(5204, 64, 5, 'کیا تمہیں ان لوگوں کی خبر نہیں پہنچی جنہوں نے اس سے پہلے کفر کیا اور اپنے کئے (کفر) کا وبال چکھا اور ان کے لئے دردناک عذاب ہے۔'),
(5205, 64, 6, 'یہ اس لئے ہوا کہ ان کے رسول(ص) کھلی ہوئی دلیلیں لے کر ان کے پاس آتے رہے تو انہوں نے کہا کیا بشر ہماری رہبری کریں گے؟ پس(اس طرح) انہوں نے کفر اختیار کیا اور رُوگردانی کی اور اللہ نے (بھی ان کی) کوئی پروا نہ کی (کیونکہ) اللہ بےنیاز (اور) سزاوارِ حمد و ثنا ہے۔'),
(5206, 64, 7, 'کافر لوگ خیال کرتے ہیں کہ وہ (مر نے کے بعد) دوبارہ نہیں اٹھائے جائیں گے آپ(ص) کہئے! ہاں میرے پروردگار کی قَسم تم ضرور اٹھائے جاؤ گے پھر تمہیں بتایا جائے گا جو کچھ تم نے کیا ہوگا اور یہ کام اللہ کیلئے بالکل آسان ہے۔'),
(5207, 64, 8, 'تو تم اللہ اور اس کے رسول(ص) پر ایمان لاؤ اور اس نور پر جو ہم نے نازل کیا ہے اور جو کچھ تم کرتے ہواللہ اس سے اچھی طرح باخبر ہے۔'),
(5208, 64, 9, '(اس دن کو یاد کرو) جس دن اللہ تمہیں اجتماع والے دن جمع کرے گا یہی دن باہمی گھاٹا اٹھانے کا دن ہوگا اور جو اللہ پر ایمان لائے گا اور نیک عمل کرے گا اللہ اس سے اس کی برائیاں (گناہ) دور کرے گا اور اسے ان باغہائے بہشت میں داخل کرے گا جن کے نیچے سے نہریں رواں دواں ہوں گی وہ ان میں ہمیشہ ہمیشہ رہیں گے یہ بہت بڑی کامیابی ہے۔'),
(5209, 64, 10, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیتوں کو جھٹلایا یہی لوگ دوزخی ہیں جس میں وہ ہمیشہ رہیں گے اور وہ بہت برا ٹھکانا ہے۔'),
(5210, 64, 11, 'کوئی مصیبت (کسی پر) نہیں آتی مگر اللہ کے اذن و اجازت سے اور جو شخص اللہ پر ایمان لائے اللہ اس کے دل کو صحیح راستہ دکھاتا ہے اور اللہ ہر چیز کا بڑا جاننے والا ہے۔'),
(5211, 64, 12, 'اور اطاعت کرو اللہ کی اور اطاعت کرو رسول(ص) کی پھر اگر تم رُوگردانی کرو گے تو ہمارے رسول(ص) پر کھول کر پیغام پہنچا دینے کے سوا کوئی ذمہ داری نہیں ہے۔'),
(5212, 64, 13, 'اللہ وہ ہے جس کے سوا کوئی الٰہ نہیں ہے بس ایمان لانے والوں کو اللہ ہی پر بھروسہ کرنا چاہیے۔'),
(5213, 64, 14, 'اے ایمان والو! تمہاری بعض بیویاں اور تمہاری بعض اولادیں تمہاری دشمن ہیں بس تم ان سے ڈرتے رہو (ہوشیار رہو) اور اگر معاف کرو، درگزر کرو اور بخش دو تو اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(5214, 64, 15, 'تمہارے مال، تمہاری اولاد ایک بڑی آزمائش ہیں اور اللہ ہی ہے جس کے پاس بڑا اجر ہے۔'),
(5215, 64, 16, 'اور جہاں تک ہو سکے اللہ (کی نافرمانی) سے ڈرو اور سنو اور اطاعت کرو اور (مال) خرچ کرو یہ تمہارے لئے بہتر ہے اور جو شخص اپنے نفس کے بُخل سے بچایا گیا تو وہی لوگ فلاح پانے والے ہیں۔'),
(5216, 64, 17, 'اگر خدا کو قرضۂ حسنہ دو تو اللہ اسے تمہارے لئے کئی گنا بڑھا دے گا اور تمہیں بخش دے گا اللہ بڑا قدردان (اور) بڑا بردبار ہے۔'),
(5217, 64, 18, 'وہ غائب اور حاضر کا جاننے والا، زبردست(اور) بڑا حکمت والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اے نبی(ص)! جب تم لوگ (اپنی) عورتوں کو طلاق دینے لگو تو انہیں عدت کے حساب سے طلاق دو اور پھر عدت کا شمار کرو اور اللہ (کی نافرمانی) سے ڈرو جو تمہارا پروردگار ہے۔ انہیں ان کے گھروں سے نہ نکالو اور نہ ہی وہ خود نکلیں مگر یہ کہ وہ کسی کھلی ہوئی بےحیائی کا ارتکاب کریں اور یہ اللہ کی (مقرر کی ہوئی) حدیں ہیں اور جو اللہ کے حدود سے تجاوز کرے گا تو وہ خود اپنے اوپر ظلم کرے گا تم نہیں جانتے شاید اللہ اس کے بعد کوئی نئی بات پیدا کر دے۔'),
(5219, 65, 2, 'تو جب وہ اپنی (مقررہ) عدت کی مدت کو پہنچ جائیں تو ان کو یا تو قاعدہ (اور شائستہ طریقہ) سے (اپنے نکاح میں) روک لو یا پھر قاعدہ (اور اچھے طریقہ) سے جدا کر دو اور اپنے میں سے دو عادل آدمیوں کو گواہ بناؤ اور خدا کیلئے صحیح گواہی دو ان باتوں سے اس شخص کو نصیحت کی جاتی ہے جو اللہ اور آخرت کے دن پر ایمان رکھتا ہے اور جو کوئی خدا سے ڈرتا ہے اللہ اس کیلئے (مشکل سے نجات کا) راستہ پیدا کر دیتا ہے۔'),
(5220, 65, 3, 'اور اسے وہاں سے رزق دیتا ہے جہاں سے اسے سان گمان بھی نہیں ہوتا ہے اور جو کوئی اللہ پر بھروسہ کرتا ہے وہ (اللہ) اس کے لئے کافی ہے بےشک اللہ اپنا کام پورا کرنے والا ہے اور اللہ نے ہر چیز کیلئے ایک اندازہ مقرر کر رکھا ہے۔'),
(5221, 65, 4, 'اور تمہاری (مطلقہ) عورتوں میں سے جو حیض سے مایوس ہو چکی ہوں اگر ان کے بارے میں تمہیں کوئی شک ہو تو ان کی عدت تین ماہ ہے اور یہی حکم ان عورتوں کا ہے جنہیں (باوجود حیض کے سن و سال میں ہو نے کے کسی وجہ سے) حیض نہ آتا ہو اور حاملہ عورتوں کی میعاد وضعِ حمل ہے اور جو کوئی اللہ سے ڈرتا ہے تو اللہ اس کے معاملہ میں آسانی پیدا کر دیتا ہے۔'),
(5222, 65, 5, 'یہ اللہ کا حکم ہے جو اس نے تمہاری طرف نازل کیا ہے اور جو اللہ سے ڈرتا ہے تو اللہ اس سے اس کی برائیاں دور کر دیتا ہے اور اس کے اجر کو بڑا کرتا (بڑھاتا) ہے۔'),
(5223, 65, 6, 'ان (مطلقہ عورتوں) کو (زمانۂ عدت میں) وہاں رکھو جہاں اپنی حیثیت کے مطابق تم خود رہتے ہو اور انہیں ضرر و زیاں نہ پہنچاؤ تاکہ ان پر تنگی کرو۔ اور اگر وہ حاملہ ہوں تو پھر ان پر خرچ کرو یہاں تک ان کا وضعِ حمل ہو جائے پھر اگر تمہارے لئے (بچہ) کو دودھ پلائیں تو تم انہیں ان کی اجرت دو اور منا سب طریقہ پر باہم طے کر لو اور اگر تمہیں باہم کوئی دشواری پیش آئے (اور باہمی اتفاق نہ ہو سکے) تو پھر کوئی اور عورت اسے دودھ پلائے گی۔'),
(5224, 65, 7, 'وسعت والے کو چاہیے کہ اپنی وسعت کے مطابق خرچ کرے اور جس کی روزی تنگ ہو تو جتنا اللہ نے اسے دیا ہے اس میں سے خرچ کرے اللہ نے جتنا کسی کو دیا ہے اس سے زیادہ اسے تکلیف نہیں دیتا عنقر یب اللہ تنگی کے بعد آسانی اور کشادگی پیدا کرے گا۔'),
(5225, 65, 8, 'اور کتنی ہی ایسی بستیاں ہیں جنہوں نے اپنے پروردگار اور اس کے رسولوں(ع) کے حکم سے سرتابی کی تو ہم نے انکا سخت محاسبہ کیا اور انہیں انوکھی (سخت) سزا دی۔'),
(5226, 65, 9, 'پس انہوں نے اپنے کئے (کرتوت) کا وبال چکھا اور ان کے کام کا انجام گھاٹا تھا۔'),
(5227, 65, 10, 'اللہ نے (آخرت میں) ان کیلئے سخت عذاب تیار کر رکھا ہے تواللہ (کی مخالفت) سے ڈرو۔ اے عقلمندو جو ایمان لائے ہو خدا نے تمہاری طرف ذکر نازل کیا ہے۔'),
(5228, 65, 11, 'یعنی ایک ایسا رسول(ص) جو اللہ کی واضح آیتیں تمہیں پڑھ کر سناتا ہے تاکہ ان لوگوں کو جو ایمان لائیں اور نیک عمل کریں (کفر و شرک) کی تاریکیوں سے نکال کر (ایمان کے) نور کی طرف لائے اور جو کوئی اللہ پر ایمان لائے گا اور نیک عمل کرے گا تواللہ اسے ایسے باغہائے بہشت میں داخل کرے گا جن کے نیچے نہریں جاری ہیں وہ ان میں ہمیشہ ہمیشہ رہیں گے۔ بےشک اللہ نے ایسے (مؤمن) کو بہت اچھی روزی دی ہے۔'),
(5229, 65, 12, 'اللہ وہ ہے جس نے سات آسمان پیدا کئے اور انہی کی طرح زمین بھی ان کے درمیان حکم (خدا) نازل ہوتا رہتا ہے تاکہ تمہیں معلوم ہو کہ اللہ ہر چیز پر بڑی قدرت رکھتا ہے اور یہ کہ اللہ نے ہر چیز کا (علمی) احاطہ کر رکھا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اے نبی(ص)! جو چیز اللہ نے آپ کیلئے حلال قرار دی ہے آپ اسے اپنی بیویوں کی خوشنودی حاصل کرنے کیلئے (اپنے اوپر) کیوں حرام ٹھہراتے ہیں اور اللہ بڑا بخشنے والا، بڑا رحم کرنے والا ہے۔'),
(5231, 66, 2, 'بےشک اللہ نے تمہاری قَسموں (کی گرہ کے) کھولنے کا طریقہ مقررکر دیا ہے اللہ تمہارا مولیٰ (سرپرست و کارساز ہے) اور وہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(5232, 66, 3, '(وہ وقت یاد رکھنے کے لائق ہے) جب نبی(ص) نے اپنی بعض بیویوں سے راز کی ایک بات کہی اور جب اس نے وہ بات (کسی اور کو) بتا دی اور اللہ نے آپ(ص) کو اس (خیانت کاری) سے آگاہ کر دیا تو آپ(ص) نے اس (بیوی) کو کچھ بات جتا دی اور کچھ سے اعراض کیا (چشم پوشی کی) تو جب آپ(ص) نے اس (بیوی) کو یہ بات بتائی تو اس نے (ازراہِ تعجب) کہا کہ آپ(ص) کو اس کی کس نے خبر دی؟ فرمایا مجھے بڑے جاننے والے (اور) بڑے باخبر (خدا) نے خبر دی ہے۔'),
(5233, 66, 4, 'اگر تم دونوں (اللہ کی بارگاہ میں) توبہ کر لو (تو بہتر ہے) کیونکہ تم دونوں کے دل ٹیڑھے ہو چکے ہیں اور اگر تم ان (پیغمبر(ص)) کے خلاف ایکا کرو گی (تو تم پیغمبر کا کچھ بھی نہیں بگاڑ سکو گی) کیونکہ یقیناً اللہ ان کا حامی ہے اور جبرائیل(ع) اور نیکوکار مؤمنین اور اس کے بعد سب فرشتے ان کے پشت پناہ (اور مددگار) ہیں۔'),
(5234, 66, 5, 'اگر پیغمبر(ص) تمہیں طلاق دے دیں تو بہت جلد اللہ ان کو تمہارے بدلے تم سے اچھی بیویاں دے دے گا جو (پکی و سچی) مسلمان ہونگی اور باایمان، اطاعت گزار اور فرمانبردار، توبہ کرنے والیاں عبادت گزار، روزہ دار اور شوہر دیدہ اور ابکار (کنواریاں)۔'),
(5235, 66, 6, 'اے ایمان والو! اپنے آپ کو اور اپنے اہل و عیال کو اس آتشِ دوزخ سے بچاؤ جس کا ایندھن آدمی اور پتھر ہوں گے اس پر ایسے فرشتے مقرر ہیں جو تُندخو اور درشت مزا ج ہیں انہیں جس بات کا حکم دیا گیا ہے وہ اس کی نافرمانی نہیں کرتے اور وہ وہی کام کرتے ہیں جس کا انہیں حکم دیا گیا ہے۔'),
(5236, 66, 7, 'اے کافر لوگو! آج تم عذر و معذرت نہ کرو تمہیں اسی کا بدلہ دیا جا رہا ہے جو تم کیا کرتے تھے۔'),
(5237, 66, 8, 'اے ایمان والو! اللہ کی بارگاہ میں (سچے دل سے) خالص توبہ کرو۔ امید ہے کہ تمہارا پروردگار تم سے تمہاری برائیاں دور کر دے گا اور تمہیں ایسے بہشتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہیں جس دن خدا (اپنے) نبی(ص) کو اور ان لوگوں کو جو آپ(ص) کے ساتھ ایمان لائے ہیں رسوا نہیں کرے گا (اس دن) ان کا نور ان کے آگے اور ان کے دائیں جانب تیز تیز چل رہا ہوگا (اور) وہ کہہ رہے ہوں گۓ اے ہمارے پروردگار! ہمارے لئے ہمارا نور مکمل کر اور ہماری مغفرت فرما بےشک تو ہر چیز پر بڑی قدرت رکھتا ہے۔'),
(5238, 66, 9, 'اے نبی(ص)! کافروں اور منافقوں سے جہاد کیجئے اور ان پر سختی کیجئے اور ان کا ٹھکانہ جہنم ہے اور وہ بہت بری جائے بازگشت ہے۔'),
(5239, 66, 10, 'اور اللہ کافروں کیلئے نوح(ع) اور لوط(ع) کی بیویوں کی مثال بیان کرتا ہے جو دونوں ہمارے بندوں میں سے دو نیک بندوں کی زوجیت میں تھیں پس انہوں نے ان کے ساتھ خیانت (غداری) کی تو وہ دونوں نیک بندے اللہ کے مقابلہ میں انہیں کچھ بھی فائدہ نہیں پہنچا سکے اور ان دونوں (بیویوں) سے کہا گیا کہ تم بھی دوزخ میں داخل ہو جاؤ اور داخل ہو نے والوں کے ساتھ۔'),
(5240, 66, 11, 'اوراللہ اہلِ ایمان کے لئے فرعون کی بیوی (آسیہ(ع)) کی مثال پیش کرتا ہے جبکہ اس نے کہا اے میرے پروردگار! میرے لئے جنت میں ایک گھر بنا اور مجھے فرعون اور اس کے (کافرانہ) عمل سے نجات دے اور مجھے ظالم قوم سے نجات دے۔'),
(5241, 66, 12, 'اور (دوسری) مریم(ع) بنت عمران کی مثال بیان کرتا ہے جس نے اپنی شرمگاہ کی حفاظت کی تو ہم نے اس میں اپنی ایک (خاص) روح پھونک دی اور اس نے اپنے پروردگار کی باتوں (پیاموں) اور اس کی کتابوں کی تصدیق کی اور وہ فرمانبرداروں میں سے تھی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بابرکت ہے وہ (خدا) جس کے ہاتھ (قبضۂ قدرت) میں (کائنات کی) بادشاہی ہے اور وہ ہر چیز پر پوری قدرت رکھتا ہے۔'),
(5243, 67, 2, 'جس نے موت و حیات کو پیدا کیا تاکہ تمہیں آزمائے کہ تم میں سے عمل کے لحاظ سے سب سے زیادہ اچھا کون ہے؟ وہ بڑا زبردست (اور) بڑا بخشنے والا ہے۔'),
(5244, 67, 3, 'جس نے اوپر نیچے سات آسمان بنائے تم خدائے رحمٰن کی تخلیق میں کوئی خلل (اور بےنظمی) نہیں پاؤ گے پھر نگاہ اٹھا کر دیکھو تمہیں کوئی رخنہ نظر آتا ہے؟'),
(5245, 67, 4, 'پھر دوبارہ نگاہ دوڑاؤ۔ تمہاری نگاہ تھک کر (ناکام) تمہاری طرف لوٹ آئے گی۔'),
(5246, 67, 5, 'بےشک ہم نے قریبی آسمان کو (تاروں) کے چراغوں سے آراستہ کیا ہے اور انہیں شیطانوں کو سنگسار کرنے (مار بھگانے) کا ذریعہ بنایا ہے اور ہم نے ان (شیطانوں) کیلئے دہکتی ہوئی آگ کا عذاب بھی تیار کر رکھا ہے۔'),
(5247, 67, 6, 'اور جن لوگوں نے اپنے پروردگار کا کفر (انکار) کیا ان کے لئے جہنم کا عذاب ہے اور وہ بہت ہی برا ٹھکانا ہے۔'),
(5248, 67, 7, 'جب وہ اس میں جھونکے جائیں گے تو وہ اس کی زوردار (اور مہیب) آواز سنیں گے اور وہ جوش مار رہی ہوگی۔'),
(5249, 67, 8, '(گویا) وہ غیظ و غصہ سے پھٹی جاتی ہوگی جب اس میں کوئی گروہ جھونکا جائے گا تو اس کے دارو غے ان سے پو چھیں گے کہ تمہارے پاس کوئی ڈرانے والا (ہادی) نہیں آیا تھا؟'),
(5250, 67, 9, 'وہ کہیں گے ہاں بےشک ہمارے پاس ڈرانے والا آیا تھا مگر ہم نے (اسے) جھٹلایا اور کہا کہ اللہ نے کوئی بھی چیز نازل نہیں کی تم بڑی گمراہی میں مبتلا ہو۔'),
(5251, 67, 10, 'اور کہیں گے کاش ہم (اس وقت) سنتے یا سمجھتے تو (آج) دوزخ والوں میں سے نہ ہو تے۔'),
(5252, 67, 11, 'پس وہ (اس وقت) اپنے گناہ کا اعتراف کر لیں گے پس لعنت ہوان دوزخ والوں پر۔'),
(5253, 67, 12, 'بےشک جو لوگ اپنے پروردگار سے اَن دیکھے ڈرتے ہیں ان کیلئے مغفرت ہے اور بڑا اجر و ثواب ہے۔'),
(5254, 67, 13, 'اور تم اپنی بات آہستہ کرو یا بلند آواز سے کرو وہ (خدا) تو سینوں کے رازوں کو بھی جانتا ہے۔'),
(5255, 67, 14, 'کیا جس نے پیدا کیا ہے وہ نہیں جانتا حالانکہ وہ بڑا باریک بین اور بڑا باخبر ہے۔'),
(5256, 67, 15, 'وہ (اللہ) وہی ہے جس نے زمین کو تمہارے لئے رام کر دیا تاکہ تم اس کے کاندھوں (راستوں) پر چلو اور اس (اللہ) کے (دئیے ہو ئے) رزق سے کھاؤ پھر اسی کی طرف(قبروں سے) اٹھ کر جانا ہے۔'),
(5257, 67, 16, 'کیا تم اس (اللہ) سے بےخوف ہو گئے جس (کی حکومت) آسمان میں ہے کہ وہ تمہیں زمین میں دھنسا دے پھر وہ ایک دم تھر تھرانے لگے۔'),
(5258, 67, 17, 'کیا تم اس بات سے بےخوف ہو گئے ہو کہ آسمان والا تم پر پتھر بر سانے والی ہوا بھیج دے پھر تمہیں بہت جلد معلوم ہو جائے گا کہ میرا ڈرانا کیسا ہوتا ہے؟'),
(5259, 67, 18, 'اور جو لوگ ان سے پہلے تھے انہوں نے بھی (حق کو) جھٹلایا تھا تو میری سخت گیری کیسی تھی؟'),
(5260, 67, 19, 'کیا یہ لوگ اپنے اوپر پرندوں کو نہیں دیکھتے جو پروں کو پھیلائے ہوئے (اڑتے ہیں) اور وہ (کبھی ان کو) سمیٹ بھی لیتے ہیں انہیں (خدا ئے) رحمٰن کے سوا اور کوئی (فضا میں) تھامے ہوئے نہیں ہے بےشک وہ ہر چیز کو دیکھ رہا ہے۔'),
(5261, 67, 20, 'آخر وہ کون ہے جو خدائے رحمٰن کے مقابلہ میں تمہارا لشکر بن کر تمہاری مدد کرے؟ کافر لوگ بالکل دھوکے میں پڑے ہوئے ہیں۔'),
(5262, 67, 21, '(بتا‎ؤ) وہ کون ہے جو تمہیں روزی دے اگر وہ (اللہ) اپنی روزی روک لے؟ بلکہ یہ لوگ سرکشی اور (حق سے) نفرت پر اڑے ہوئے ہیں۔'),
(5263, 67, 22, 'بھلا جوشخص منہ کے بل گرتا ہوا چل رہا ہو وہ زیادہ ہدایت یافتہ (اورمنزلِ مقصود پر زیادہ پہنچنے والا) ہے یا وہ جو سیدھا راہِ راست پر چل رہا ہے؟'),
(5264, 67, 23, 'آپ(ص) کہہ دیجئے! وہی تو (خدا) ہے جس نے تمہیں پیدا کیا اور تمہارے لئے کان، آنکھیں اور دل (و دماغ) بنا ئے۔ (مگر) تم بہت کم شکر ادا کرتے ہو۔'),
(5265, 67, 24, 'آپ(ص) کہہ دیجئے! کہ وہ (اللہ) وہی ہے جس نے تمہیں زمین میں پھیلایا اور (قیامت کے دن) تم اسی کے پاس اکٹھے کئے جاؤ گے۔'),
(5266, 67, 25, 'اور وہ (کافر لوگ) کہتے ہیں کہ (بتا‎ؤ) یہ وعدہ وعید کب پوراہوگا؟ اگر تم سچے ہو۔'),
(5267, 67, 26, 'آپ(ص) کہہ دیجئے کہ (اس کا) علم تو بس اللہ کے پاس ہے اور میں تو صرف ایک کھلا ہوا ڈرانے والا ہوں۔'),
(5268, 67, 27, 'پس وہ جب اس (قیامت) کو قریب آتے دیکھیں گے تو کافروں کے چہرے بگڑ جائیں گے اور (ان سے) کہاجائے گا کہ یہی وہ ہے جس کا تم مطالبہ کیا کرتے تھے۔'),
(5269, 67, 28, 'آپ(ص) کہہ دیجئے! کہ کیا تم نے غور کیا ہے اگر (تمہاری خواہش کے مطابق) خدا مجھے اور میرے ساتھیوں کو ہلاک کر دے یا (ہماری خواہش کے مطابق) ہم پر رحم فرمائے (بہرحال) کافروں کو دردناک عذاب سے کون پناہ دے گا؟'),
(5270, 67, 29, 'آپ(ص) کہہ دیجئے! وہ خدائے رحمان ہے جس پر ہم ایمان لائے ہیں اور اسی پر توکل (بھروسہ) کرتے ہیں عنقریب تمہیں معلوم ہو جائے گا کہ کھلی ہوئی گمراہی میں کون ہے؟'),
(5271, 67, 30, 'آپ(ص) کہیے! کیا تم نے غور کیا ہے کہ اگر تمہارا پانی زمین کی تہہ میں اترجائے تو پھر تمہارے لئے (شیریں پانی کا) چشمہ کون لائے گا؟');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون! قَسم ہے قلم کی اور اس کی جو کچھ لوگ لکھتے ہیں۔'),
(5273, 68, 2, 'کہ آپ(ص) اپنے پروردگار کے فضل و کرم سے دیوانے نہیں ہیں۔'),
(5274, 68, 3, 'اور بےشک آپ(ص) کے لئے ایسا اجر ہے جو کبھی ختم ہو نے والانہیں ہے۔'),
(5275, 68, 4, 'اور بےشک آپ(ص) خلقِ عظیم کے مالک ہیں۔'),
(5276, 68, 5, 'عنقریب آپ بھی دیکھیں گے اور وہ بھی دیکھ لیں گے۔'),
(5277, 68, 6, 'کہ تم میں سے کون جُنون میں مبتلا ہے۔'),
(5278, 68, 7, 'بےشک آپ(ص) کا پروردگار بہتر جانتا ہے کہ اس کی راہ سے بھٹکا ہواکون ہے؟ اور وہی انہیں خوب جانتا ہے جو ہدایت یافتہ ہیں۔'),
(5279, 68, 8, 'تو آپ(ص) جھٹلانے والوں کا کہنا نہ مانیں۔'),
(5280, 68, 9, 'وہ (کفار) تو چاہتے ہیں کہ آپ(ص) (اپنے فرضِ منصبی کی ادائیگی میں) ڈھیلے پڑجائیں تو وہ بھی (مخالفت میں) ڈھیلے ہو جائیں۔'),
(5281, 68, 10, 'اور آپ(ص) ہرگز ہر اس شخص کا کہنا نہ مانیں جو بہت (جھوٹی) قَسمیں کھانے والا ہے (اور) ذلیل ہے۔'),
(5282, 68, 11, 'جو بڑی نکتہ چینی کرنے والا (اور) چلتا پھرتا چغل خور ہے۔'),
(5283, 68, 12, 'کارِ خیر سے بڑا منع کرنے والا، حد سے بڑھنے والا (اور) بڑا گنہگار ہے۔'),
(5284, 68, 13, 'سخت مزاج ہے اور ان (سب بری صفتوں) کے علاوہ وہ بداصل بھی ہے۔'),
(5285, 68, 14, 'یہ سب کچھ اس بناء پر ہے کہ وہ مالدار اور اولاد والا ہے۔'),
(5286, 68, 15, 'جب اس کو ہماری آیتیں پڑھ کر سنائی جاتی ہیں تو وہ کہتا ہے کہ یہ تو اگلے لوگوں کے افسانے ہیں۔'),
(5287, 68, 16, 'ہم عنقریب اس کی سونڈ (نا ک) پر داغ لگائیں گے۔'),
(5288, 68, 17, 'ہم نے ان (کفارِ مکہ) کو اسی طرح آزمائش میں ڈالا ہے جس طرح ایک باغ والوں کو آزمائش میں ڈالا تھا جب انہوں نے قَسم کھائی تھی کہ وہ صبح سویرے ضرور اس کا پھل توڑ لیں گے۔'),
(5289, 68, 18, 'اور انہوں نے کوئی استثناء نہیں کیا تھا (انشاء اللہ نہیں کہا تھا)۔'),
(5290, 68, 19, 'پھر (راتوں رات) جب کہ وہ لوگ ابھی سوئے ہوئے تھے آپ(ص) کے پروردگار کی طرف سے ایک آفت چکر لگا گئی۔'),
(5291, 68, 20, 'تو وہ (باغ) کٹی ہوئی فصل کی طرح ہوگیا۔'),
(5292, 68, 21, 'پس انہوں نے صبح ہوتے ہی ایک دوسرے کو آواز دی۔'),
(5293, 68, 22, 'کہ اگر تم نے پھل توڑنا ہے تو سویرے سویرے اپنی کھیتی کی طرف چلو۔'),
(5294, 68, 23, 'تو وہ اس حال میں چل پڑے کہ چپکے چپکے ایک دوسرے سے کہتے جاتے تھے۔'),
(5295, 68, 24, 'کہ خبردار! آج تمہارے پاس اس باغ میں کوئی مسکین نہ آنے پا ئے۔'),
(5296, 68, 25, 'اور وہ اس (مسکین کو کچھ نہ دینے) پر قادر سمجھ کر نکلے۔'),
(5297, 68, 26, 'اور جب باغ کو (برباد) دیکھا تو کہا کہ ہم راستہ بھول گئے ہیں۔'),
(5298, 68, 27, 'بلکہ ہم محروم ہو گئے ہیں۔'),
(5299, 68, 28, 'جو ان میں سے بہتر آدمی تھا اس نے کہا کہ کیا میں نے تم سے نہیں کہا تھا کہ (خدا کی) تسبیح کیوں نہیں کرتے؟'),
(5300, 68, 29, '(تب) انہوں نے کہا کہ پاک ہے ہمارا پروردگار بےشک ہم ظالم تھے۔'),
(5301, 68, 30, 'پھر وہ ایک دوسرے کی طرف متوجہ ہو کر باہم لعنت ملامت کرنے لگے۔'),
(5302, 68, 31, '(اور) کہنے لگے وائے ہو ہم پر! بےشک ہم سرکش ہو گئے تھے۔'),
(5303, 68, 32, 'امید ہے کہ ہمارا پروردگار ہمیں اس (باغ) کے بدلے اس سے بہتر باغ عطا کر دے ہم اپنے پروردگار کی طرف رجوع کرتے ہیں۔'),
(5304, 68, 33, 'اسی طرح عذاب آتا ہے اور آخرت کا عذاب تو (اس سے بھی) بہت بڑا ہے کاش کہ یہ لوگ جانتے۔'),
(5305, 68, 34, 'بےشک پرہیزگاروں کیلئے ان کے پروردگار کے ہاں نعمت و آسائش کے باغات ہیں۔'),
(5306, 68, 35, 'کیا ہم فرمانبرداروں کو مجرموں کی مانند کر دیں گے؟'),
(5307, 68, 36, 'تمہیں کیا ہوگیا ہے؟ تم کیسے فیصلے کرتے ہو؟'),
(5308, 68, 37, 'کیا تمہارے پاس کوئی (آسمانی) کتاب ہے جس میں تم (یہ) پڑھتے ہو؟'),
(5309, 68, 38, 'کہ تمہارے لئے اس میں وہ کچھ ہے جو تم پسند کرتے ہو؟'),
(5310, 68, 39, 'یا کیا تم نے ہم سے کچھ قَسمیں لی ہیں کہ قیامت میں تمہارے لئے وہی کچھ ہے جس کا تم حکم لگاؤ گے (فیصلہ کرو گے؟)'),
(5311, 68, 40, 'ان سے پوچھئے کہ ان میں سے کون ان (بےبنیاد باتوں) کا ضامن ہے؟'),
(5312, 68, 41, 'یا انکے کچھ آدمی (ہمارے) شریک ہیں؟ تو اگر وہ سچے ہیں تو پھر اپنے وہ شریک پیش کریں۔'),
(5313, 68, 42, '(وہ دن یاد کرنے کے لائق ہے) جب پنڈلی کھولی جائے گی (یعنی سخت وقت ہوگا) اور ان (کافروں) کو سجدہ کیلئے بلایا جائے گا تو (اس وقت) وہ سجدہ نہیں کر سکیں گے۔'),
(5314, 68, 43, 'ان کی نگاہیں جھکی ہوئی ہوں گی (اور) ان پر ذلت چھائی ہوگی حالانکہ انہیں اس وقت (بھی دنیا میں) سجدہ کی دعوت دی جاتی تھی جب وہ صحیح و سالم تھے۔'),
(5315, 68, 44, 'پس (اے رسول(ص)) آپ(ص) مجھے اور اس کو چھوڑ دیں جو اس کتاب کو جھٹلاتا ہے ہم انہیں اس طرح بتدریج تباہی کی طرف لے جائیںگے کہ ان کو خبر بھی نہ ہوگی۔'),
(5316, 68, 45, 'اور میں انہیں مہلت دے رہا ہوں۔ بےشک میری تدبیر بڑی مضبوط ہے۔'),
(5317, 68, 46, 'کیا آپ(ص) ان سے کوئی اجرت طلب کرتے ہیں کہ وہ اس تاوان کے بوجھ تلے دبےجاتے ہیں؟'),
(5318, 68, 47, 'یا کیا ان کے پاس غیب ہے سو جسے وہ لکھ رہے ہیں؟'),
(5319, 68, 48, 'پس آپ(ص) اپنے پروردگار کے فیصلے تک صبر کریں اور مچھلی والے (جنابِ یونس(ع)) کی طرح نہ ہوں جب انہوں نے اس حال میں (اپنے پروردگار کو) پکارا کہ وہ غم و غصہ سے بھرے ہوۓ تھے (یا مغموم تھے)۔'),
(5320, 68, 49, 'اگر ان کے پروردگار کا فضل و کرم ان کے شاملِ حال نہ ہوتا تو انہیں اس حال میں چٹیل میدان میں پھینک دیا جاتا کہ وہ مذموم ہوتے۔'),
(5321, 68, 50, 'مگر ان کے پروردگار نے انہیں منتخب کر لیا اور انہیں (اپنے) نیکوکار بندوں سے بنا دیا۔'),
(5322, 68, 51, 'اور جب کافر ذکر (قرآن) سنتے ہیں تو ایسا معلوم ہوتا ہے کہ وہ اپنی (تیز و تند) نظروں سے آپ(ص) کو (راہِ راست) سے پھسلا دیں گے اور کہتے ہیں کہ یہ ضرور دیوانہ ہے۔'),
(5323, 68, 52, 'حالانکہ وہ (قرآن) تو تمام جہانوں کیلئے نصیحت ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'برحق واقع ہو نے والی۔'),
(5325, 69, 2, 'کیا ہے وہ برحق واقع ہو نے والی۔'),
(5326, 69, 3, 'اور (اے مخاطب) تم کیا جانو کہ کیا ہے وہ برحق واقع ہو نے والی۔'),
(5327, 69, 4, 'قبیلۂ ثمود اور عاد نے کھڑکھڑانے والی (قیامت) کو جھٹلایا۔'),
(5328, 69, 5, 'پس ثمود تو ایک حد سے بڑھے ہوئے حادثہ سے ہلاک کئے گئے۔'),
(5329, 69, 6, 'اور بنی عاد ایک حد سے زیادہ تیز و تند (اور سرد) آندھی سے ہلاک کئے گئے۔'),
(5330, 69, 7, 'اللہ نے اسے مسلسل سات رات اور آٹھ دن تک ان پر مسلط رکھا تم (اگر وہاں ہوتے تو) دیکھتے کہ وہ اس طرح گرے پڑے ہیں کہ گویا وہ کھجور کے کھوکھلے تنے ہیں۔'),
(5331, 69, 8, 'تو تمہیں ان کا کوئی باقی بچ جانے والا نظر آتا ہے؟'),
(5332, 69, 9, 'اور فرعون اور اس سے پہلے والوں اور الٹی ہوئی بسیتوں (والوں) نے (یہی) خطا کی۔'),
(5333, 69, 10, '(یعنی) اپنے پروردگار کے رسول(ع) کی نافرمانی کی تو اس (اللہ) نے ان کو حد سے بڑھی ہوئی گرفت میں لے لیا۔'),
(5334, 69, 11, 'اور پانی جب حد سے بڑھ گیا تو ہم نے تم کو (یعنی تمہارے آباء و اجداد کو) کشتی میں سوار کیا۔'),
(5335, 69, 12, 'تاکہ ہم اس (وا قعہ) کو تمہارے لئے یادگار بنا دیں اور یاد رکھنے والے کان اسے محفوظ رکھیں۔'),
(5336, 69, 13, 'سو جب ایک بار صور پھونکا جائے گا۔'),
(5337, 69, 14, 'اور زمین اور پہاڑوں کو ایک ہی دفعہ پاش پاش کر دیا جائے گا۔'),
(5338, 69, 15, 'تو اس دن واقع ہو نے والی (قیامت) واقع ہو جائے گی۔'),
(5339, 69, 16, 'اور آسمان پھٹ جائے گا اور وہ اس دن بالکل کمزور ہو جائے گا۔'),
(5340, 69, 17, 'اور فرشتے اس کے کناروں پر ہوں گے اور آپ(ص) کے پرورگار کے عرش کو اس دن آٹھ (فرشتے) اٹھائے ہوں گے۔'),
(5341, 69, 18, 'اس دن تم (اپنے پروردگار کی بارگاہ میں) پیش کئے جاؤ گے اور تمہاری کوئی بات پوشیدہ نہ رہے گی۔'),
(5342, 69, 19, 'پس جس کا نامۂ اعمال اس کے دائیں ہاتھ میں دیا جائے گا وہ کہے گا آؤ پڑھو میرانامۂ اعمال۔'),
(5343, 69, 20, 'میں سمجھتا تھا کہ میں اپنے حساب کتاب سے دو چار ہو نے والا ہوں۔'),
(5344, 69, 21, 'وہ ایک پسندیدہ زندگی میں ہوگا۔'),
(5345, 69, 22, '(یعنی) اس عالیشان بہشت میں ہوگا۔'),
(5346, 69, 23, 'جس کے تیار پھلوں کے خوشے جھکے ہوئے ہوں گے۔'),
(5347, 69, 24, '(ان سے کہاجائے گا) کھاؤ اور پیو مزے اور خوشگواری کے ساتھ ان اعمال کے صلے میں جو تم نے گزرے ہوئے دنوں میں کئے ہیں۔'),
(5348, 69, 25, 'اور جس کا نامۂ اعمال اس کے بائیں ہاتھ میں دیا جائے گا وہ کہے گا کہ کاش میرا نامۂ اعمال مجھے نہ دیا جاتا۔'),
(5349, 69, 26, 'اور میں نہ جانتا کہ میرا حساب کتاب کیا ہے؟'),
(5350, 69, 27, 'اے کاش وہی موت (جو مجھے آئی تھی) فیصلہ کُن ہوتی۔'),
(5351, 69, 28, '(ہا ئے) میرے مال نے مجھے کوئی فائدہ نہ دیا۔'),
(5352, 69, 29, '(آہ) میرا اقتدار ختم ہوگیا۔'),
(5353, 69, 30, '(پھر حکم ہوگا) اسے پکڑو اور اسے طوق پہناؤ۔'),
(5354, 69, 31, 'پھر اسے جہنم میں جھونک دو۔'),
(5355, 69, 32, 'پھر اسے ایک ایسی زنجیر میں جس کی لمبائی ستر ہاتھ ہے جکڑ دو۔'),
(5356, 69, 33, '(کیونکہ) یہ بزر گ و برتر خدا پر ایمان نہیں رکھتا تھا۔'),
(5357, 69, 34, 'اور غریب کو کھانا کھلانے پر آمادہ نہیں کرتا تھا۔'),
(5358, 69, 35, 'پس آج یہاں اس کا کوئی ہمدرد نہیں ہے۔'),
(5359, 69, 36, 'اور نہ ہی اس کے لئے پیپ کے سوا کوئی کھانا ہے۔'),
(5360, 69, 37, 'جسے خطا کاروں کے سوا اور کوئی نہیں کھاتا۔'),
(5361, 69, 38, 'پس نہیں! میں قَسم کھاتا ہوں ان چیزوں کی جن کو تم دیکھتے ہو۔'),
(5362, 69, 39, 'اور ان کی جن کو تم نہیں دیکھتے۔'),
(5363, 69, 40, 'بےشک یہ (قرآن) ایک معزز رسول(ص) کا کلام ہے۔'),
(5364, 69, 41, 'یہ کسی شاعر کا کلام نہیں ہے (مگر) تم لوگ بہت کم ایمان لاتے ہو۔'),
(5365, 69, 42, 'اور نہ ہی یہ کسی کاہن کا کلام ہے (مگر) تم لوگ بہت کم نصیحت حاصل کرتے ہو۔'),
(5366, 69, 43, '(بلکہ) یہ تو تمام جہانوں کے پروردگار کی طرف سے نازل شدہ ہے۔'),
(5367, 69, 44, 'اگر وہ (نبی(ص)) اپنی طرف سے کوئی بات گھڑ کر ہماری طرف منسوب کرتا۔'),
(5368, 69, 45, 'تو ہم اس کا دایاں ہاتھ پکڑ لیتے۔'),
(5369, 69, 46, 'اور پھر ہم اس کی رگِ حیات کاٹ دیتے۔'),
(5370, 69, 47, 'پھر تم میں سے کوئی بھی ہمیں اس سے روکنے والا نہ ہوتا۔'),
(5371, 69, 48, 'بےشک یہ(قرآن) پرہیزگاروں کیلئے ایک نصیحت ہے۔'),
(5372, 69, 49, 'اور ہم خوب جانتے ہیں کہ تم میں سے کچھ لوگ (اس کے) جھٹلانے والے ہیں۔'),
(5373, 69, 50, 'اور یہ کافروں کیلئے با عثِ حسرت ہے۔'),
(5374, 69, 51, 'اور بےشک وہ یقینی حق ہے۔'),
(5375, 69, 52, 'پس آپ(ص) اپنے عظمت والے پروردگار کے نام کی تسبیح کریں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'ایک سوال کرنے والے نے اس عذاب کا سوال کیا۔'),
(5377, 70, 2, 'جو کافروں پر واقع ہونے والا ہے (اور) اسے کوئی ٹالنے والانہیں ہے۔'),
(5378, 70, 3, 'جو اس خدا کی طرف سے ہے جو بلندی کے زینوں والا ہے۔'),
(5379, 70, 4, 'فرشتے اور روح اس کی بارگاہ میں ایک ایسے دن میں چڑھ کر جاتے ہیں جس کی مقدار پچاس ہزار سال ہے۔'),
(5380, 70, 5, '(اے نبی(ص)) پس آپ(ص) بہترین صبر کیجئے۔'),
(5381, 70, 6, 'یہ لوگ تو اس (روز) کو بہت دور سمجھتے ہیں۔'),
(5382, 70, 7, 'مگر ہم اسے بالکل قریب دیکھ رہے ہیں۔'),
(5383, 70, 8, 'جس دن آسمان پگھلی ہوئی دھات کی طرح ہو جائے گا۔'),
(5384, 70, 9, 'اور پہاڑ دھنکی ہوئی رُوئی کی مانند ہو جائیں گے۔'),
(5385, 70, 10, 'اور کوئی دوست کسی دو ست کو نہ پو چھے گا۔'),
(5386, 70, 11, 'حالانکہ وہ ایک دوسرے کو دکھائے جائیں گے مجرم چاہے گا کہ اس دن کے عذاب سے بچنے کیلئے اپنے بیٹوں،'),
(5387, 70, 12, 'اپنی بیوی اور اپنے بھائی،'),
(5388, 70, 13, 'اور اپنے قریبی کنبہ جو اسے پناہ دینے والا ہے۔'),
(5389, 70, 14, 'اور رُوئے زمین کے سب لوگوں کو فدیہ میں دے دے اور پھر یہ (فدیہ) اسے نجات دلا دے۔'),
(5390, 70, 15, 'ہرگز نہیں وہ تو بھڑکتی ہوئی آگ کا شعلہ ہے۔'),
(5391, 70, 16, 'جو کھال کو ادھیڑ کر رکھ دے گا۔'),
(5392, 70, 17, 'اور ہر اس شخص کو (اپنی طرف) بلائے گا جو پیٹھ پھرائے گااور رُوگردانی کرے گا۔'),
(5393, 70, 18, 'اور جس نے (مال) جمع کیا اور پھر اسے سنبھال کر رکھا۔'),
(5394, 70, 19, 'بےشک انسان بےصبرا پیدا ہوا ہے۔'),
(5395, 70, 20, 'جب اسے کوئی تکلیف پہنچتی ہے تو وہ بہت گھبرا جاتا ہے۔'),
(5396, 70, 21, 'اورجب اسے خوشحالی نصیب ہوتی ہے تو بہت بُخل کرتا ہے۔'),
(5397, 70, 22, 'مگر وہ نمازی (اس عیب سے محفوظ ہیں)۔'),
(5398, 70, 23, 'جو اپنی نمازوں پر مُداومَت کرتے ہیں۔'),
(5399, 70, 24, 'اورجن کے مالوں میں مقررہ حق ہے۔'),
(5400, 70, 25, 'سا ئل اور محروم کا۔'),
(5401, 70, 26, 'اور جو جزا و سزا کے دن کی تصدیق کرتے ہیں۔'),
(5402, 70, 27, 'اور جو اپنے پروردگار کے عذاب سے ڈرتے رہتے ہیں۔'),
(5403, 70, 28, 'کیونکہ ان کے پروردگار کا عذاب وہ چیز ہے جس سے کسی کو مطمئن نہیں ہونا چاہئے۔'),
(5404, 70, 29, 'اور جو اپنی شرم گاہوں کی حفاظت کرتے ہیں۔'),
(5405, 70, 30, 'سوائے اپنی بیویوں کے یا اپنی مملوکہ کنیزوں کے کہ اس میں ان پر کوئی ملامت نہیں ہے۔'),
(5406, 70, 31, 'اور جو اس سے آگے بڑھے وہ حد سے تجاوز کرنے والے ہیں۔'),
(5407, 70, 32, 'اورجو اپنی امانتوں اورعہد و پیمان کا لحاظ رکھتے ہیں۔'),
(5408, 70, 33, 'اور جو اپنی گواہیوں پر قائم رہتے ہیں۔'),
(5409, 70, 34, 'اور جو اپنی نمازوں کی حفاظت کرتے ہیں۔'),
(5410, 70, 35, 'یہی وہ لوگ ہیں جو عزت کے ساتھ باغہائے بہشت میں رہیں گے۔'),
(5411, 70, 36, '(اے نبی(ص)) ان کافروں کو کیا ہوگیا ہے کہ وہ ٹکٹکی باندھے آپ(ص) کی طرف دو ڑے چلے آرہے ہیں۔'),
(5412, 70, 37, 'دائیں اور بائیں طرف سے گروہ در گروہ۔'),
(5413, 70, 38, 'کیا ان میں سے ہر ایک یہ طمع و لالچ رکھتا ہے کہ اسے آرام و آسائش والے بہشت میں داخل کر دیا جائے۔'),
(5414, 70, 39, 'ہرگز نہیں! ہم نے انہیں اس چیز (مادہ) سے پیدا کیا ہے جسے وہ خود جانتے ہیں۔'),
(5415, 70, 40, 'پس نہیں! میں قَسم کھاتا ہوں مشرقوں اور مغربوں کے پروردگار کی ہم پوری قدرت رکھتے ہیں۔'),
(5416, 70, 41, 'اس بات پر کہ ہم ان لوگوں کے بدلے ان سے بہتر لے آئیں اور ہم (ایسا کرنے سے) عاجز نہیں ہیں۔'),
(5417, 70, 42, 'تو آپ(ص) انہیں چھوڑئیے کہ وہ بےہودہ باتوں اور کھیل کود میں مشغول رہیں یہاں تک کہ وہ اپنے اس دن سے دوچار ہوں جس کا ان سے وعدہ وعید کیا جا رہا ہے۔'),
(5418, 70, 43, 'جس دن وہ قبروں سے اس طرح جلدی جلدی نکلیں گے گویا (اپنے بتوں کے) استھانوں کی طرف دوڑ رہے ہیں۔'),
(5419, 70, 44, 'ان کی آنکھیں جھکی ہوئی ہوں گی (اور) ان پر ذلت و رسوائی چھائی ہوگی۔ یہی وہ دن ہے جس کا ان سے وعدہ وعید کیا جاتا تھا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بےشک ہم نے نوح(ع) کو ان کی قوم کی طرف بھیجا کہ اپنی قوم کو ڈراؤ۔ اس سے پہلے کہ ان پر دردناک عذاب آجائے۔'),
(5421, 71, 2, '(چنانچہ) انہوں نے کہا کہ اے میری قوم! میں تمہیں ایک کھلا ہوا ڈرانے والا ہوں۔'),
(5422, 71, 3, 'کہ تم (صرف) اللہ کی عبادت کرو اور اس (کی مخالفت) سے ڈرو اور میری اطاعت کرو۔'),
(5423, 71, 4, 'وہ (خدا) تمہارے (پہلے) گناہ بخش دے گا اور تمہیں ایک مقررہ مدت تک مہلت دے گا (باقی رکھے گا) بےشک جب اللہ کا مقررہ وقت آجاتا ہے تو پھر اسے مؤخر نہیں کیا جا سکتا کاش کہ تم (اس حقیقت کو) سمجھتے۔'),
(5424, 71, 5, 'آپ(ع) (نوح(ع)) نے کہا اے مرے پروردگار میں نے اپنی قوم کو رات دن دعوت دی۔'),
(5425, 71, 6, 'مگر میری دعوت نے ان کے فرار (گریز) میں اور بھی اضافہ کیا۔'),
(5426, 71, 7, 'اور میں نے جب بھی انہیں دعوت دی کہ (وہ اس لائق ہوں کہ) تو انہیں بخش دے تو انہوں نے اپنی انگلیاں اپنے کانوں میں ٹھونس لیں اور اپنے کپڑے اپنے اوپر لپیٹ لئے اور (اپنی روش پر) اڑے رہے اور بڑا تکبر کرتے رہے۔'),
(5427, 71, 8, 'پھر بھی میں نے ان کو بآوازِ بلند دعوت دی۔'),
(5428, 71, 9, 'پھر میں نے انہیں (ہر طرح دعوت دی) کھلم کھلا بھی اور چپکے چپکے بھی۔'),
(5429, 71, 10, '(چنانچہ) میں نے (ان سے) کہا کہ اپنے پروردگار سے مغفرت طلب کرو وہ یقیناً بڑا بخشنے والا ہے۔'),
(5430, 71, 11, 'وہ تم پر آسمان سے موسلادھار بارش برسائے گا۔'),
(5431, 71, 12, 'اور مال و اولاد سے تمہاری مدد فرمائے گا اور تمہارے لئے باغات پیدا کرے گا اور تمہارے لئے نہریں قرار دے گا۔'),
(5432, 71, 13, 'تمہیں کیا ہوگیا ہے کہ تم اللہ کی عظمت کی پروانہیں کرتے؟'),
(5433, 71, 14, 'حالانکہ اس نے تمہیں طرح طرح سے خلق کیا ہے۔'),
(5434, 71, 15, 'کیا تم نہیں دیکھتے کہ اللہ نے کس طرح سات آسمان تہہ بر تہہ بنا ئے؟'),
(5435, 71, 16, 'اور ان میں چاند کو نور اور سورج کو چراغ بنایا۔'),
(5436, 71, 17, 'اور تمہیں خاص طرح زمین سے اگایا ہے۔'),
(5437, 71, 18, 'پھر تمہیں اس میں لوٹائے گا اور پھر اسی سے (دوبارہ) نکالے گا۔'),
(5438, 71, 19, 'اور اللہ نے ہی تمہارے لئے زمین کو بچھونا بنایا ہے۔'),
(5439, 71, 20, 'تاکہ تم اس کے کشادہ راستوں میں چلو پھرو۔'),
(5440, 71, 21, 'نوح(ع) نے(بارگاہِ خداوندی میں) عرض کیا اے میرے پروردگار ان لوگوں نے میری نافرمانی کی ہے اور ان (بڑے) لوگوں کی پیروی کی ہے جن کے مال و اولاد نے ان کے خسارے میں اضافہ کیا ہے (اور کوئی فائدہ نہیں پہنچایا)۔'),
(5441, 71, 22, 'اور انہوں نے (میرے خلاف) بڑا مکر و فریب کیا ہے۔'),
(5442, 71, 23, 'اور انہوں نے(عام لوگوں سے) کہا کہ (نوح(ع) کے کہنے پر) اپنے خداؤں کو ہرگز نہ چھوڑو۔ اور نہ ہی وَد اور سُواع کو چھوڑو اور نہ ہی یغوث و یعوق اور نسر کو چھوڑو۔'),
(5443, 71, 24, 'اور انہوں نے بہت سے لوگوں کو گمراہ کر دیا ہے (یا اللہ) تو بھی ان کی گمراہی کے سوا اور کسی چیز میں اضافہ نہ کر۔'),
(5444, 71, 25, '(چنانچہ) وہ اپنی خطاؤں کی وجہ سے غرق کر دئیے گئے پھر آگ میں ڈال دئیے گئے پھر انہوں نے اللہ (کے عذاب) سے بچانے والے کوئی مددگار نہ پا ئے۔'),
(5445, 71, 26, 'اور نوح(ع) نے کہا اے میرے پروردگار! تو ان کافروں میں سے کوئی بھی رُوئے زمین پر بسنے والا نہ چھوڑ۔'),
(5446, 71, 27, 'اگر تو انہیں چھوڑے گا تو یہ تیرے بندوں کو گمراہ کرینگے اور بدکار اور سخت کافر کے سوا کسی اور کو جنم نہیں دیں گے۔'),
(5447, 71, 28, 'اے میرے پروردگار! مجھے اور میرے والدین کو بخش دے اور ہر اس شخص کو بخش دے جو مؤمن ہو کر میرے گھر میں داخل ہو اور سب مؤمنین و مؤمنات کی مغفرت فرما اور کافروں کی ہلاکت کے سوا اور کسی چیز میں اضافہ نہ کر۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, '(اے نبی (ص)) آپ(ص) کہئے! کہ مجھے وحی کی گئی ہے کہ جنات کے ایک گروہ نے (قرآن کو) توجہ کے ساتھ سنا پھر (جا کر اپنی قوم سے) کہا کہ ہم نے بڑا عجیب قرآن سنا ہے۔'),
(5449, 72, 2, 'جو بھلائی کی طرف راہنمائی کرتا ہے اس لئے ہم تو اس پر ایمان لے آئے اور اب ہم کسی کوبھی اپنے پروردگار کا شریک نہیں بنائیں گے۔'),
(5450, 72, 3, 'اور یہ کہ ہمارے پروردگار کی شان بہت بلند ہے اس نے (اپنے لئے) نہ کوئی بیوی بنائی ہے اور نہ اولاد۔'),
(5451, 72, 4, 'اور یہ کہ ہمارے بےوقوف لوگ خدا کے بارے میں خلافِ حق (ناروا) باتیں کرتے رہتے ہیں۔'),
(5452, 72, 5, 'اور یہ کہ ہمارا خیال تھا کہ انسان اور جن خدا کے بارے میں ہرگز جھوٹی بات نہیں کہیں گے۔'),
(5453, 72, 6, 'اور یہ کہ انسانوں میں سے کچھ لوگ جنات میں سے کچھ لوگوں کی پناہ لیا کرتے تھے۔'),
(5454, 72, 7, 'اور یہ کہ انہوں (انسانوں) نے بھی تمہاری طرح یہ خیال کیا کہ اللہ کسی کو (دوبارہ زندہ کر کے) نہیں اٹھائے گا (یا کسی کو رسول بنا کر نہیں بھیجے گا)۔'),
(5455, 72, 8, 'اور یہ کہ ہم نے آسمان کو ٹٹولا تو اسے پایا کہ وہ سخت پہریداروں اور شہابوں سے بھر دیا گیا ہے۔'),
(5456, 72, 9, 'اور یہ کہ ہم (پہلے کچھ) سن گن لینے کیلئے آسمان کے بعض خاص مقامات پر بیٹھا کرتے تھے مگر اب جو (جن) سننے کی کوشش کرتا ہے تو وہ ایک شہاب کو اپنی گھات میں پاتا ہے۔'),
(5457, 72, 10, 'اور یہ کہ ہم نہیں سمجھتے کہ آیا زمین والوں کیلئے کسی برائی کا ارادہ کیا گیا ہے یا ان کے پروردگار نے ان کے ساتھ بھلائی کرنے کا ارادہ کیا ہے۔'),
(5458, 72, 11, 'اور یہ کہ ہم میں سے کچھ نیک ہیں اور کچھ اور طرح اور ہم مختلف الرّائے لوگ تھے۔'),
(5459, 72, 12, 'اور یہ کہ ہم سمجھتے ہیں کہ ہم زمین میں اللہ کو عاجز نہیں کر سکتے اور نہ ہی بھاگ کر اسے بےبس کر سکتے ہیں۔'),
(5460, 72, 13, 'اور یہ کہ ہم نے جب ہدایت کی بات سنی تو اس پر ایمان لے آئے پس جو اپنے پروردگار پر ایمان لائے گا تو اسے نہ کسی کمی (نقصان) کا خوف ہوگا اور نہ زیادتی کا۔'),
(5461, 72, 14, 'اور یہ کہ ہم میں سے بعض مسلم (فرمانبردار) ہیں اور کچھ راہِ راست سے منحرف (نافرمان) ہیں پس جنہوں نے اسلام (فرمانبرداری) کی راہ اختیار کی تو انہوں نے بھلائی کا راستہ ڈھونڈھ لیا۔'),
(5462, 72, 15, 'اور جو راہِ راست سے منحرف ہوں گے وہ جہنم کا ایندھن بنیں گے۔'),
(5463, 72, 16, 'اور یہ کہ اگر یہ لوگ راہِ راست پر ثابت قدم رہتے تو ہم انہیں خوب سیراب کرتے۔'),
(5464, 72, 17, 'تاکہ اس نعمت سے ان کی آزمائش کریں اور جو شخص اپنے پروردگار کی یاد سے منہ موڑے گا خدا اسے ایسے سخت عذاب میں داخل کرے گا جو بڑھتا ہی جائے گا۔'),
(5465, 72, 18, 'اور یہ کہ سجدہ کے مقامات خاص اللہ کیلئے ہیں لہذا اللہ کے ساتھ کسی اور کو نہ پکارو۔'),
(5466, 72, 19, 'اور یہ کہ جب بھی اللہ کا خاص بندہ (رسول (ص)) اس کو پکارنے کیلئے کھڑا ہوتا ہے تو (ایسا معلوم ہوتاہے کہ) لوگ اس پر ٹوٹ پڑیں گے۔'),
(5467, 72, 20, 'آپ(ص) کہئے! کہ میں تو صرف اپنے پروردگار کو پکارتا ہوں اور کسی کو اس کا شریک قرار نہیں دیتا۔'),
(5468, 72, 21, 'کہیے! میں تمہارے لئے نہ کسی نقصان اور برائی کا اختیار رکھتا ہوں اور نہ کسی بھلائی کا۔'),
(5469, 72, 22, 'آپ کہیے! کہ مجھے اللہ سے کوئی پناہ نہیں دے سکتا اور میں اس کے سوا اور کوئی جائے پناہ نہیں پاتا۔'),
(5470, 72, 23, '(ہاں البتہ) میرا کام صرف یہ ہے کہ اللہ کی طرف سے تبلیغ کروں اور اس کے پیغامات (لوگوں تک) پہنچاؤں اور جو خدا اور اس کے رسول(ع) کی نافرمانی کرے گا تو اس کے لئے دوزخ کی آگ ہے جس میں (ایسے لوگ) ہمیشہ رہیں گے۔'),
(5471, 72, 24, '(یہ لوگ اپنی کجروی سے باز نہیں آئیں گے) یہاں تک کہ جب وہ وہ چیز (عذاب) دیکھ لیں گے جس کا ان سے وعدہ وعید کیا جا رہا ہے تو انہیں معلوم ہو جائے گا کہ مددگار اور تعداد کی حیثیت سے کون زیادہ کمزور ہے۔'),
(5472, 72, 25, 'آپ(ص) کہہ دیجیے! کہ میں نہیں جانتا کہ جس چیز (عذاب) کا تم لوگوں سے وعدہ وعید کیا جارہا ہے کہ آیا وہ قریب ہے یا میرا پروردگار اس کیلئے کوئی طویل مدت مقرر کرتا ہے۔'),
(5473, 72, 26, 'وہ (اللہ) عالِمُ الغیب ہے وہ اپنے غیب پر کسی کو مطلع نہیں کرتا۔'),
(5474, 72, 27, 'سوائے اپنے اس رسول کے جسے وہ اس بات کے لئے منتخب کرتا ہے تو وہ اس کے آگے پیچھے محافظ (فرشتے) لگا دیتا ہے۔'),
(5475, 72, 28, 'تاکہ وہ معلوم کرے کہ انہوں نے اپنے پروردگار کے پیغامات کو پہنچا دیا ہے اور وہ ان کے حالات کا احاطہ کئے ہوئے ہے اور اس نے ہر چیز کو گن رکھا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اے کمبلی اوڑھنے والے (رسول(ص))۔'),
(5477, 73, 2, 'رات کو(نماز میں) کھڑے رہا کیجئے مگر (پوری رات نہیں بلکہ) تھوڑی رات۔'),
(5478, 73, 3, 'یعنی آدھی رات یا اس میں سے بھی کچھ کم کر دیجئے۔'),
(5479, 73, 4, 'یا اس سے کچھ بڑھا دیجئے! اور قرآن کو ٹھہر ٹھہر کر (اور واضح کر کے) پڑھا کیجئے! ۔'),
(5480, 73, 5, 'بےشک ہم آپ(ص) پر ایک بھاری کلام ڈالنے والے ہیں۔'),
(5481, 73, 6, 'بےشک رات کا اٹھنا سخت روندتا ہے (اور سخت کوفت کا باعث ہے) مگر ذکر (خدا اور قرآن خوانی) کیلئے زیادہ موزوں اور ٹھیک ہے۔'),
(5482, 73, 7, 'بلاشبہ دن میں آپ(ص) کیلئے بڑی مشغولیت ہے۔'),
(5483, 73, 8, 'اور اپنے پروردگار کے نام کا ذکر کیجئے اور (سب سے کٹ کر) اسی کی طرف متوجہ ہو جائیے۔'),
(5484, 73, 9, 'وہ مشرق و مغرب کا پروردگار ہے اس کے سوا کوئی خدا نہیں ہے۔ پس اسی کو اپنا کارساز بنائیے۔'),
(5485, 73, 10, 'اور ان (کفار) کی باتوں پر صبر کیجئے! اور بڑی خوبصورتی کے ساتھ ان سے الگ ہو جائیے۔'),
(5486, 73, 11, 'اور مجھے اور ان جھٹلانے والے اہلِ دولت کو چھوڑ دیجئے اور انہیں تھوڑی سی مہلت دیجئے!'),
(5487, 73, 12, 'ہمارے پاس ان کیلئے بیٹریاں اور دوزخ کی آگ ہے۔'),
(5488, 73, 13, 'اور گلے میں پھنس جانے والی غذا اور دردناک عذاب ہے۔'),
(5489, 73, 14, 'یہ اس دن ہوگا جب زمین اور پہاڑ ہلنے لگیں گے اور پہاڑ ریت کے بکھر نے والے تودے بن جائیں گے۔'),
(5490, 73, 15, 'ہم نے تمہاری طرف ایک رسول(ص) بھیجا ہے تم پر گواہ بنا کر جس طرح ہم نے فر عون کی طرف ایک رسول(ع) بھیجا تھا۔'),
(5491, 73, 16, 'پھر جب فرعون نے رسول(ع) کی نافرمانی کی تو ہم نے اسے بڑا سخت پکڑا۔'),
(5492, 73, 17, 'اگر تم نے کفر اختیار کیا تو اس دن کے عذاب سے کیونکر بچوگے جو بچوں کو بوڑھا بنا دے گا۔'),
(5493, 73, 18, 'جس کی سختی سے آسمان پھٹ جائے گا (اور یہ) اللہ کا وعدہ ہے جو پورا ہو کر رہے گا۔'),
(5494, 73, 19, 'بےشک یہ ایک بڑی نصیحت ہے پس جو چاہے وہ اپنے پروردگار کی طرف راستہ اختیار کرے۔'),
(5495, 73, 20, '(اے رسول (ص)) یقیناً آپ(ص) کا پروردگار جانتا ہے کہ آپ(ص) کبھی رات کی دو تہائی کے قریب، کبھی نصف شب اور کبھی ایک تہائی (نماز کیلئے) قیام کرتے ہیں اور ایک گروہ آپ کے ساتھیوں میں سے بھی اور اللہ ہی رات اور دن کا اندازہ ٹھہراتا ہے (انہیں چھوٹا بڑا کرتا ہے) وہ جانتا ہے کہ تم لوگ (اس) وقت پر پوری طرح حاوی نہیں ہو سکتے (شمار نہیں کر سکتے) تو اس نے تمہارے حال پر توجہ (مہربانی) فرمائی تو اب جتنا قرآن تم آسانی سے پڑھ سکتے ہو اتنا ہی پڑھ لیا کرو وہ یہ بھی جانتا ہے کہ تم میں کچھ بیمار ہوں گے اور کچھ ایسے بھی ہوں گے جو اللہ کے فضل (روزی) کی تلاش میں زمین میں سفر کریں گے اور دوسرے لوگ ایسے بھی ہوں گے جو اللہ کی راہ میں جہاد کریں گے پس اتنا قرآن پڑھو جتنا میسر ہو اور نماز قائم کرو (پابندی سے پڑھو) اور زکوٰۃ ادا کرو اور اللہ کو قرضۂ حسنہ دو اور تم لوگ جو کچھ بھلائی (نیک عمل) اپنے لئے آگے بھیجو گے اسے اللہ کے ہاں موجود پاؤ گے وہی تمہارے لئے بہتر ہے اس کا ثواب بہت بڑا ہے اور اللہ سے مغفرت طلب کرو بےشک اللہ بڑا بخشنے والا اوربڑا رحم کرنے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اے چادر اوڑھنے والے (رسول(ص))۔'),
(5497, 74, 2, 'اٹھئے اور (لوگوں کو عذابِ الٰہی) سے ڈرائیے۔'),
(5498, 74, 3, 'اور اپنے پروردگار کی بڑائی بیان کیجئے۔'),
(5499, 74, 4, 'اور اپنے کپڑے پاک رکھئے۔'),
(5500, 74, 5, 'اور (بتوں کی) نجاست سے دور رہیے۔'),
(5501, 74, 6, 'اور (کسی پر) احسان نہ کیجئے زیادہ حاصل کرنے کیلئے۔'),
(5502, 74, 7, 'اور اپنے پروردگار کیلئے صبر کیجئے۔'),
(5503, 74, 8, 'اور جب صور پھونکا جائے گا۔'),
(5504, 74, 9, 'تو وہ دن بڑا سخت دن ہوگا۔'),
(5505, 74, 10, 'اور کافروں پر آسان نہ ہوگا۔'),
(5506, 74, 11, 'مجھے اور اس شخص کو چھوڑ دیجئے جسے میں نے تنہا پیدا کیا۔'),
(5507, 74, 12, 'اور اسے پھیلا ہوا (فراواں) مال و زر دیا۔'),
(5508, 74, 13, 'اور پاس حاضر رہنے والے بیٹے دے۔'),
(5509, 74, 14, 'اور اس کیلئے (سرداری کا) ہر قِسم کا سامان مہیا کیا۔'),
(5510, 74, 15, 'پھر بھی وہ طمع رکھتا ہے کہ میں اسے زیادہ دوں۔'),
(5511, 74, 16, 'ہرگز نہیں وہ تو ہماری آیتوں کا سخت مخالف ہے۔'),
(5512, 74, 17, 'میں عنقریب (دوزخ کی) ایک سخت چڑھائی پر اسے چڑھا دوں گا۔'),
(5513, 74, 18, 'اس نے سوچا اور ایک بات تجویز کی۔'),
(5514, 74, 19, 'سو وہ غارت ہو اس نے کیسی بات تجویز کی؟'),
(5515, 74, 20, 'پھر وہ غارت ہو اس نے کیسی بات تجویز کی؟'),
(5516, 74, 21, 'پھر اس نے دیکھا۔'),
(5517, 74, 22, 'پھر اس نے تیوری چڑھائی اور منہ بنایا۔'),
(5518, 74, 23, 'پھر پیٹھ پھیری اور تکبر کیا۔'),
(5519, 74, 24, 'پھر کہا کہ یہ تو محض جادو ہے جو پہلوں سے نقل ہوتا ہوا آرہا ہے۔'),
(5520, 74, 25, 'یہ نہیں ہے مگر آدمی کا کلام۔'),
(5521, 74, 26, 'میں عنقریب اسے دوزخ میں جھونکوں گا۔'),
(5522, 74, 27, 'اور تم کیا سمجھو کہ دوزخ کیا ہے؟'),
(5523, 74, 28, 'وہ نہ (کوئی چیز) باقی رکھتی ہے اور نہ چھوڑتی ہے۔'),
(5524, 74, 29, 'وہ کھال کو جھلس دینے والی ہے۔'),
(5525, 74, 30, 'اس پر اُنیس فرشتے (دارو غے) مقرر ہیں۔'),
(5526, 74, 31, 'اور ہم نے دوزخ کے داروغے صرف فرشتے بنائے ہیں اور ہم نے ان کی تعداد کو کافروں کیلئے آزمائش کا ذریعہ بنایا ہے تاکہ اہلِ کتاب یقین کریں اور اہلِ ایمان کے ایمان میں اضافہ ہو جائے اور اہلِ کتاب اور اہلِ ایمان شک و شبہ نہ کریں اور جن کے دلوں میں بیماری ہے اور کافر لوگ کہیں گے کہ اس بیان سے اللہ کی کیا مراد ہے؟ اسی طرح اللہ جسے چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور یہ (دوزخ کا) بیان نہیں ہے مگر انسانوں کے لئے نصیحت۔'),
(5527, 74, 32, 'ہرگز نہیں! قَسم ہے چاند کی۔'),
(5528, 74, 33, 'اور رات کی جب وہ جانے لگے۔'),
(5529, 74, 34, 'اور صبح کی جب وہ روشن ہو جائے۔'),
(5530, 74, 35, 'وہ (دوزخ) بڑی چیزوں میں سے ایک بڑی چیز ہے۔'),
(5531, 74, 36, '(جو) انسانوں کیلئے ڈراوا ہے۔'),
(5532, 74, 37, 'یعنی تم میں سے (ہر اس) شخص کیلئے جو آگے بڑھنا چاہے یا پیچھے ہٹنا چاہے۔'),
(5533, 74, 38, 'ہر شخص اپنے اعمال کے بدلے میں گروی ہے۔'),
(5534, 74, 39, 'سوائے دائیں ہاتھ والے (جنتیوں) کے۔'),
(5535, 74, 40, 'جو بہشت کے باغوں میں ہوں گے۔'),
(5536, 74, 41, '(اور) مجرموں سے سوال و جواب کرتے ہوں گے۔'),
(5537, 74, 42, 'کہ تمہیں کیا چیز دوزخ میں لے گئی؟'),
(5538, 74, 43, 'وہ کہیں گے کہ ہم نماز نہیں پڑھا کرتے تھے۔'),
(5539, 74, 44, 'اور مسکینوں کو کھانا نہیں کھلاتے تھے۔'),
(5540, 74, 45, 'اور بےہودہ باتیں کرنے والوں کے ساتھ مل کر ہم بھی بےہودہ کام کرتے تھے۔'),
(5541, 74, 46, 'اور ہم جزا و سزا کے دن کو جھٹلاتے تھے۔'),
(5542, 74, 47, 'یہاں تک کہ یقینی چیز (موت) ہمارے سامنے آگئی۔'),
(5543, 74, 48, 'تو ایسے لوگوں کو شفاعت کرنے والوں کی شفاعت کوئی فائدہ نہیں پہنچائے گی۔'),
(5544, 74, 49, 'سو انہیں کیا ہوگیا ہے کہ وہ نصیحت سے رُوگردانی کرتے ہیں؟'),
(5545, 74, 50, 'گویا وہ بدکے ہوئے گدھے ہیں۔'),
(5546, 74, 51, 'جوشیر سے (ڈر کر) بھاگے جا رہے ہیں۔'),
(5547, 74, 52, 'بلکہ ان میں سے ہر ایک شخص یہ چاہتا ہے کہ اسے کھلی ہوئی کتابیں دے دی جائیں۔'),
(5548, 74, 53, 'ایسا نہیں ہے بلکہ وہ آخرت کا خوف نہیں رکھتے۔'),
(5549, 74, 54, 'ہرگز نہیں یہ تو ایک نصیحت ہے۔'),
(5550, 74, 55, 'جو چاہے اس سے نصیحت حاصل کرے۔'),
(5551, 74, 56, 'اور وہ اس سے نصیحت حاصل نہیں کریں گے مگر یہ کہ اللہ چاہے وہی اس کا حق دار ہے کہ اس سے ڈرا جائے اور وہی اس قابل ہے کہ مغفرت فرمائے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'نہیں! میں قَسم کھاتا ہوں قیامت کے دن کی۔'),
(5553, 75, 2, 'اور نہیں! میں قَسم کھاتا ہوں ملامت کرنے والے نفس کی۔'),
(5554, 75, 3, 'کیا انسان یہ گمان کرتا ہے کہ ہم اس کی (بوسیدہ) ہڈیوں کو جمع نہیں کریں گے؟'),
(5555, 75, 4, 'ہاں ضرور جمع کریں گے ہم اس کی انگلیوں کے پور پور درست کرنے پر قادر ہیں۔'),
(5556, 75, 5, 'بلکہ انسان چاہتا ہے کہ اپنے آگے (آئندہ زندگی میں) بھی بدعملی کرتا رہے۔'),
(5557, 75, 6, '(اس لئے) پوچھتا ہے کہ قیامت کا دن کب آئے گا؟'),
(5558, 75, 7, 'پس جب نگاہ خیرہ ہو جائے گی۔'),
(5559, 75, 8, 'اور چاند کو گہن لگ جائے گا۔'),
(5560, 75, 9, 'اور سورج اور چاند اکٹھے کر دئیے جائیں گے۔'),
(5561, 75, 10, 'اس دن انسان کہے گا کہ بھاگنے کی جگہ کہاں ہے؟'),
(5562, 75, 11, 'ہرگز نہیں! کہیں کوئی پناہ گاہ نہیں ہے۔'),
(5563, 75, 12, 'اس دن صرف آپ(ص) کے پروردگار کی طرف ٹھکانہ ہوگا۔'),
(5564, 75, 13, 'اس دن انسان کو بتایا جائے گا کہ اس نے کیا (عمل) آگے بھیجا اور کیا پیچھے چھوڑا؟'),
(5565, 75, 14, 'بلکہ خود انسان اپنے حال کو خوب جانتا ہے۔'),
(5566, 75, 15, 'اگرچہ کتنے ہی بہانے پیش کرے۔'),
(5567, 75, 16, '(اے رسول (ص)) آپ(ص) اپنی زبان کو اس (قرآن) کے ساتھ حرکت نہ دیجئے تاکہ اسے جلدی جلدی (حفظ) کر لیں۔'),
(5568, 75, 17, 'بیشک اس کا جمع کرنا اور اس کا پڑھانا ہماے ذمہ ہے۔'),
(5569, 75, 18, 'پس جب ہم اسے پڑھیں تو آپ(ص) بھی اسی کے مطابق پڑھیں۔'),
(5570, 75, 19, 'پھر اس کا واضح کرنا بھی ہمارے ذمہ ہے۔'),
(5571, 75, 20, 'ہرگز نہیں بلکہ تم جلدی ملنے والی (دنیا) سے محبت کرتے ہو۔'),
(5572, 75, 21, 'اور آخرت (دیر سے آنے والی) کو چھوڑتے ہو۔'),
(5573, 75, 22, 'اس دن کچھ چہرے تر و تازہ ہوں گے۔'),
(5574, 75, 23, 'اپنے پروردگار کی نعمت (و رحمت) کو دیکھ رہے ہوں گے۔'),
(5575, 75, 24, 'اور کئی چہرے اس دن بےرونق ہوں گے۔'),
(5576, 75, 25, 'وہ سمجھ رہے ہوں گے کہ ان کے ساتھ کمر توڑ دینے والا سلوک کیا جائے گا۔'),
(5577, 75, 26, 'ہرگز نہیں جب جان (کھینچ کر) حلق تک پہنچ جائے گی۔'),
(5578, 75, 27, 'اور کہاجائے گا کہ اب کون ہے جھاڑ پھونک کرنے والا؟'),
(5579, 75, 28, 'اور وہ سمجھ لے گا کہ اب (دنیا سے) جدائی کا وقت ہے۔'),
(5580, 75, 29, 'اور پنڈلی سے پنڈلی لپٹ جائے گی۔'),
(5581, 75, 30, 'اس دن تمہارے پروردوگار کی طرف کھینچ کرجانا ہوگا۔'),
(5582, 75, 31, '(اتنا کچھ سمجھانے کے باوجود اس مخصوص آدمی نے) نہ تصدیق کی اور نہ نماز پڑھی۔'),
(5583, 75, 32, 'بلکہ اس نے جھٹلایا اور منہ پھیر لیا۔'),
(5584, 75, 33, 'پھر اکڑتا ہوا اپنے گھر والوں کی طرف چلا گیا۔'),
(5585, 75, 34, 'یہ (روش) تیرے ہی لئے سزاوار ہے اور تیرے ہی لائق ہے۔'),
(5586, 75, 35, 'پھر یہ تیرے ہی لائق ہے اور تیرے ہی لئے سزوار ہے۔'),
(5587, 75, 36, 'کیا انسان یہ گمان کرتا ہے کہ اسے یونہی مہمل چھوڑ دیا جائے گا؟'),
(5588, 75, 37, 'کیاوہ شروع میں منی کا ایک قطرہ نہ تھا جو (رحم میں) ٹپکایا جاتا ہے؟'),
(5589, 75, 38, 'پھر وہ ایک لوتھڑا بنا پھر اس (خدا) نے اسے پیدا کیا اور پھر (اس کے) اعضاء درست کئے۔'),
(5590, 75, 39, 'پھر اس سے دو قِسمیں بنائیں مرد و عورت۔'),
(5591, 75, 40, 'کیا وہ اس بات پر قادر نہیں ہے کہ وہ مُردوں کو پھر زندہ کر دے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'بےشک انسان پر زمانہ میں ایک ایسا وقت گزر چکا ہے جب وہ کوئی قابلِ ذکر چیز نہ تھا۔'),
(5593, 76, 2, 'بےشک ہم نے انسان کو مخلوط نطفے سے پیدا کیا تاکہ ہم اسے آزمائیں پس اس لئے ہم نے اسے سننے والا (اور) دیکھنے والا بنایا۔'),
(5594, 76, 3, 'بلاشبہ ہم نے اسے راستہ دکھا دیا ہے اب چاہے شکر گزار بنے اور چاہے ناشکرا بنے۔'),
(5595, 76, 4, 'بےشک ہم نے کافروں کیلئے زنجیریں، طوق اور بھڑکتی ہوئی آگ تیار کر رکھی ہے۔'),
(5596, 76, 5, 'بلاشبہ نیکوکار (جنت میں شرابِ طہور کے) ایسے جام پئیں گے جن میں (آبِ کافور) کی آمیزش ہوگی۔'),
(5597, 76, 6, 'یعنی وہ ایک چشمہ ہے جس سے اللہ کے (خاص) بندے پئیں گے اور جدھر جائیں گے ادھر بہا کرلے جائیں گے۔'),
(5598, 76, 7, 'یہ وہ لوگ ہیں جو اپنی نذریں (منتیں) پوری کرتے ہیں اور اس دن سے ڈرتے ہیں جس کی سختی عام ہوگی۔'),
(5599, 76, 8, 'اور وہ اس (اللہ) کی محبت میں مسکین، یتیم اور قیدی کو کھانا کھلاتے ہیں۔'),
(5600, 76, 9, '(اور کہتے ہیں کہ) ہم تمہیں صرف اللہ کی خوشنودی کیلئے کھلاتے ہیں نہ تم سے کوئی جزا چاہتے ہیں اورنہ شکریہ۔'),
(5601, 76, 10, 'ہم تو اپنے پروردگار کی طرف سے ایک ایسے دن (کے عذاب) سے ڈرتے ہیں جو بڑا ترش اور سخت ہوگا۔'),
(5602, 76, 11, 'پس اللہ ان کو اس دن کے شر سے بچائے گا اور انہیں (چہروں کی) تر و تازگی اور (دلوں کا) سُرور عطا فرمائے گا۔'),
(5603, 76, 12, 'اور ان کے صبر کے صلہ میں انہیں بہشت اور ریشمی لباس عطا فرمائے گا۔'),
(5604, 76, 13, 'وہ وہاں اونچی مسندوں پر تکیہ لگائے بیٹھے ہوں گے وہ وہاں نہ سورج (کی گرمی) دیکھیں گے اور نہ سردی کی ٹھٹھر۔'),
(5605, 76, 14, 'ان (باغہائے بہشت) کے سائے ان پر جھکے ہوئے ہوں گے اور ان کے میوے ان کی دسترس میں ہوں گے (کہ ہر وقت بلاتکلف کھا سکیں گے)۔'),
(5606, 76, 15, 'اور ان کے سامنے چاندی کے برتن اور شیشہ کے چمکدار گلاس گردش میں ہوں گے۔'),
(5607, 76, 16, 'اور شیشے بھی (کانچ کے نہیں بلکہ) چاندی کی قِسم سے ہوں گے جنہیں (منتظمین نے) پورے اندازے کے مطابق بنایا اور بھرا ہوگا۔'),
(5608, 76, 17, 'اور انہیں ایسی شرابِ طہور کے جام پلائے جائیں گے جن میں زنجیل (سونٹھ کے پانی) کی آمیزش ہوگی۔'),
(5609, 76, 18, 'یہ اس (جنت) میں ایک چشمہ ہے جسے سلسبیل کہاجاتا ہے۔'),
(5610, 76, 19, 'اور ان کی خدمت کیلئے ایسے لڑکے گردش کر رہے ہوں گے جو ہمیشہ ایک حالت میں رہیں گے جب تم انہیں دیکھو گے تو سمجھو گے کہ وہ بکھرے ہوئے موتی ہیں۔'),
(5611, 76, 20, 'تم وہاں جدھر بھی دیکھو گے وہیں عظیم نعمت اور عظیم بادشاہی دیکھو گے۔'),
(5612, 76, 21, 'ان کے اوپر باریک ریشم کے سبز کپڑے ہوں گے اور دبیز ریشم کے کپڑے بھی اور انہیں چاندی کے کنگن پہنائے جائیں گے اور انکا پروردگار انہیں پاک و پاکیزہ شراب پلائے گا۔'),
(5613, 76, 22, 'یہ تمہاری جزا ہے اور تمہاری کوشش شکرگزاری کے قابل ہے۔'),
(5614, 76, 23, 'ہم ہی نے آپ پر تھوڑا تھوڑا کر کے قرآن نازل کیا ہے۔'),
(5615, 76, 24, 'اور اپنے پروردگار کے حکم کی خاطر صبر کیجئے اور ان میں سے کسی گنہگار یا ناشکرگزار (منکر) کی بات نہ مانئیے۔'),
(5616, 76, 25, 'اور صبح و شام اپنے پروردگار کانام یاد کیجئے۔'),
(5617, 76, 26, 'اور رات کے ایک حصے میں اس کو سجدہ کیجئے اور رات کے طویل حصہ میں اس کی تسبیح کیجئے۔'),
(5618, 76, 27, 'بےشک یہ لوگ دنیائے عاجل سے محبت رکھتے ہیں اور (آگے آنے والے) ایک بھاری دن (قیامت) کو اپنے پسِ پشت ڈال رکھا ہے۔'),
(5619, 76, 28, 'بےشک ہم نے ان کو پیدا کیا ہے اور ہم نے ہی ان کے جوڑ بند مضبوط کئے ہیں اور ہم ہی جب چاہیں گے تو ان کی جگہ انہی جیسے لوگ بدل دیں گے۔'),
(5620, 76, 29, 'بےشک یہ ایک بڑی نصیحت ہے تو جو چاہے اپنے پروردگار کا راستہ اختیار کرے۔'),
(5621, 76, 30, 'اور تم نہیں چاہتے ہو مگر وہی جو خدا چاہتا ہے بےشک اللہ بڑا جاننے والا، بڑا حکمت والا ہے۔'),
(5622, 76, 31, 'وہ جسے چاہتا ہے اپنی رحمت میں داخل کرتا ہے اور اس نے ظالموں کیلئے دردناک عذاب تیار کر رکھا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'قَسم ہے ان کی جو مسلسل چھوڑ دی جاتی ہیں۔'),
(5624, 77, 2, 'پھر (آندھی کی طرح) تیز و تند چلتی ہیں۔'),
(5625, 77, 3, 'جو (بادلوں کو) پھیلانے والی ہیں۔'),
(5626, 77, 4, 'پھر (انہیں) متفرق کر دیتی ہیں۔'),
(5627, 77, 5, 'پھر (دلوں میں) یاد (الٰہی) ڈالتی ہیں۔'),
(5628, 77, 6, '(حجت تمام کر کے) عذر قطع کرنے کیلئے یا ڈراوے کیلئے۔'),
(5629, 77, 7, 'بےشک جس چیز کا تم سے وعدہ وعید کیا جا رہا ہے وہ ضرور واقع ہو نے والی ہے۔'),
(5630, 77, 8, 'پس جب ستارے گرائے جائیں گے۔'),
(5631, 77, 9, 'اور آسمان پھاڑ دیا جائے گا۔'),
(5632, 77, 10, 'اور جب پہاڑ (ریزہ ریزہ کر کے) اڑا دیئے جائیں گے۔'),
(5633, 77, 11, 'اور جب رسول(ع) وقتِ معیّن پر حاضر کئے جائیں گے۔'),
(5634, 77, 12, '(آخر) کس دن کیلئے یہ تاخیر کی گئی؟'),
(5635, 77, 13, 'فیصلے کے دن کے لئے۔'),
(5636, 77, 14, 'تجھے کیا معلوم کہ فیصلے کا دن کیا ہے؟'),
(5637, 77, 15, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5638, 77, 16, 'کیا ہم نے پہلے والوں کو ہلاک نہیں کیا۔'),
(5639, 77, 17, 'پھر ان کے پیچھے بھیجے گئے لوگوں کو۔'),
(5640, 77, 18, 'ہم مجرموں کے ساتھ ایسا ہی (سلوک) کرتے ہیں۔'),
(5641, 77, 19, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5642, 77, 20, 'کیا ہم نے تمہیں ایک حقیر پانی سے پیدانہیں کیا؟'),
(5643, 77, 21, 'پھر ہم نے اس کو ایک محفوظ مقام (رحمِ مادر) میں رکھا۔'),
(5644, 77, 22, 'ایک مقررہ مدت تک۔'),
(5645, 77, 23, '(اس سے ثابت ہوا کہ) ہم قادر ہیں پس ہم کیسے اچھے قادر ہیں۔'),
(5646, 77, 24, '(ہم کیسا اچھا اندازہ کرنے والے ہیں) تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5647, 77, 25, 'کیا ہم نے زمین کو سمیٹ کر رکھنے والی نہیں بنایا۔'),
(5648, 77, 26, 'زندوں کو بھی اور مُردوں کو بھی۔'),
(5649, 77, 27, 'اور ہم نے اس میں بلند و بالا پہاڑ بنائے اور تمہیں خوشگوار (اور میٹھے) پانی سے سیراب کیا۔'),
(5650, 77, 28, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5651, 77, 29, '(حکم ہوگا) جاؤ اس (دوزخ) کی طرف جسے تم جھٹلایا کرتے تھے۔'),
(5652, 77, 30, 'جاؤ اس سایہ کی طرف جس کی تین شاخیں ہیں۔'),
(5653, 77, 31, 'جو نہ سایہدار ہے اور نہ آگ کے شعلوں سے بچاتا ہے۔'),
(5654, 77, 32, 'وہ (دوزخ) اونچے مَحلوں کی مانند انگارے پھینکتی ہے۔'),
(5655, 77, 33, 'گویا کہ وہ زرد رنگ کے اونٹ ہیں۔'),
(5656, 77, 34, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5657, 77, 35, 'یہ وہ دن ہوگا جس میں وہ بول نہیں سکیں گے۔'),
(5658, 77, 36, 'اور نہ ہی انہیں اجازت دی جائے گی کہ معذرت کر سکیں۔'),
(5659, 77, 37, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5660, 77, 38, 'یہ فیصلے کا دن ہے جس میں ہم نے تمہیں اور پہلے والوں کو جمع کر دیا ہے۔'),
(5661, 77, 39, 'اگر تمہارے پاس (دوزخ سے بچنے کے لئے) کوئی تدبیر ہے تو میرے مقابلہ میں چلاؤ۔'),
(5662, 77, 40, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5663, 77, 41, 'بےشک پرہیزگار لوگ (اس دن) (رحمتِ خدا کے) سایوں میں اور چشموں میں ہوں گے۔'),
(5664, 77, 42, 'اور ان پھلوں میں ہوں گے جنہیں وہ چاہیں گے۔'),
(5665, 77, 43, '(ان سے کہاجائے گا) مزے سے کھاؤ اور پیو اپنے ان اعمال کے صلے میں جو تم (دنیا میں) کرتے رہے ہو۔'),
(5666, 77, 44, 'بےشک ہم نیکوکاروں کو اسی طرح جزا دیتے ہیں۔'),
(5667, 77, 45, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5668, 77, 46, '(اے جھٹلانے والو) تم تھوڑے دن (دنیا میں) کھا لو اورفائدہ اٹھا لو (بہرحال) تم لوگ مجرم ہو۔'),
(5669, 77, 47, 'تباہی ہے اس دن کے جھٹلانے والوں کیلئے۔'),
(5670, 77, 48, 'اور جب ان سے کہا جاتا ہے کہ کہ رکوع کرو تو وہ رکوع نہیں کرتے۔'),
(5671, 77, 49, 'تباہی ہے اس دن کے جھٹلانے والوں کے لئے۔'),
(5672, 77, 50, '(آخر) وہ لوگ اس (قرآن) کے بعد کس کلام پر ایمان لائیں گے؟');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'یہ لوگ کس چیز کے بارے میں ایک دوسرے سے سوال و جواب کر رہے ہیں؟'),
(5674, 78, 2, 'کیا اس بڑی خبر کے بارے میں۔'),
(5675, 78, 3, 'جس کے متعلق وہ باہم اختلاف کر رہے ہیں۔'),
(5676, 78, 4, 'ہرگز نہیں! عنقریب انہیں معلوم ہو جائے گا۔'),
(5677, 78, 5, 'پھر ہرگز نہیں! عنقریب انہیں معلوم ہو جائے گا۔'),
(5678, 78, 6, 'کیا ہم نے زمین کو بچھونا نہیں بنایا؟'),
(5679, 78, 7, 'اور پہاڑوں کو میخیں۔'),
(5680, 78, 8, 'اور ہم نے تمہیں جوڑا جوڑا پیدا کیا۔'),
(5681, 78, 9, 'اور تمہاری نیند کو راحت و آرام کا ذریعہ۔'),
(5682, 78, 10, 'اور رات کو پردہ پوش۔'),
(5683, 78, 11, 'اور دن کو تحصیلِ معاش کا وقت بنایا۔'),
(5684, 78, 12, 'اور ہم نے تم پر سات مضبوط (آسمان) بنائے۔'),
(5685, 78, 13, 'اور ہم ہی نے (دن میں) ایک نہایت روشن چراغ (سورج) بنایا۔'),
(5686, 78, 14, 'اور ہم نے پانی سے لبریز بادلوں سے موسلادھار پانی برسایا۔'),
(5687, 78, 15, 'تاکہ ہم اس کے ذریعہ سے غلہ اور سبزی اگائیں۔'),
(5688, 78, 16, 'اور گھنے باغات۔'),
(5689, 78, 17, 'بےشک فیصلے کے دن (قیامت) کا ایک معیّن وقت ہے۔'),
(5690, 78, 18, 'جس دن صور پھونکا جائے گا تو تم لوگ فوج در فوج آؤ گے۔'),
(5691, 78, 19, 'اور آسمان کھول دیا جائے گا اور وہ دروازے ہی دروازے ہو جائے گا۔'),
(5692, 78, 20, 'اور پہاڑ چلائے جائیں گے اور وہ بالکل سراب ہو جائیں گے۔'),
(5693, 78, 21, 'بےشک جہنم گھات میں ہے۔'),
(5694, 78, 22, 'جو سرکشوں کا ٹھکانہ ہے۔'),
(5695, 78, 23, 'وہ اس میں مدتہائے دراز تک پڑے رہیں گے۔'),
(5696, 78, 24, 'وہ اس میں نہ ٹھنڈک کامزہ چکھیں گے اور نہ کوئی پینے کی چیز۔'),
(5697, 78, 25, 'سوائے گرم پانی اور پیپ کے۔'),
(5698, 78, 26, 'یہ (ان کے اعمال) کے مطابق بدلہ ہے۔'),
(5699, 78, 27, 'یہ لوگ (روزِ) حساب (قیامت) کی توقع ہی نہیں رکھتے تھے۔'),
(5700, 78, 28, 'اور یہ ہماری آیتوں کو بےدریغ جھٹلاتے تھے۔'),
(5701, 78, 29, 'اور ہم نے ہر چیز کو ایک نوشتہ میں شمار کر رکھا ہے۔'),
(5702, 78, 30, 'چکھو اس کامزہ ہم تمہارے عذاب میں اضافہ ہی کریں گے۔'),
(5703, 78, 31, 'بےشک پرہیزگاروں کیلئے کامیابی و کامرانی ہے۔'),
(5704, 78, 32, 'یعنی باغ اور انگور ہیں۔'),
(5705, 78, 33, 'اور اٹھتی جوانیوں والی ہم عمر لڑکیاں (حوریں)۔'),
(5706, 78, 34, 'اور چھلکتے ہوئے (شرابِ طہور کے) جام۔'),
(5707, 78, 35, 'وہ لوگ وہاں نہ کوئی بےہودہ بات سنیں گے اور نہ کوئی جھوٹ۔'),
(5708, 78, 36, 'یہ تمہارے پروردگار کی طرف سے بطور عطیہ صلہ ہے جو کافی و وافی ہے۔'),
(5709, 78, 37, 'یعنی وہ ربِ رحمان کی طرف سے جو آسمانوں اور زمین اور ان کے درمیان کی سب چیزوں کا پروردگار (اور مالک) ہے (جس کی ہیبت سے) لوگوں کو اس سے بات کرنے کایارا نہیں ہے۔'),
(5710, 78, 38, 'جس دن روح اور فرشتے صف باندھے کھڑے ہوں گے کوئی بات نہیں کرے گا مگر جسے خدائے رحمان اجازت دے گا اور وہ ٹھیک بات کہے گا۔'),
(5711, 78, 39, 'یہ دن برحق ہے پس جو چاہے اپنے پروردگار کی طرف اپنا ٹھکانہ بنائے۔'),
(5712, 78, 40, 'بےشک ہم نے تمہیں عنقریب آنے والے عذاب سے ڈرا دیا ہے جس دن آدمی وہ (کمائی) دیکھے گا جو اس کے ہاتھوں نے آگے بھیجی ہوگی اور کافر کہے گا کہ کاش میں مٹی ہوتا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'قَسم ہے ان (فرشتوں) کی جو (جسم میں ڈوب کر) سختی سے (جان) کھینچتے ہیں۔'),
(5714, 79, 2, 'اور قَسم ہے آہستگی اور آسانی سے (جان) نکالنے والوں کی۔'),
(5715, 79, 3, 'اور قَسم ہے (فضاؤں کے اندر) تیر نے پھرنے والوں کی۔'),
(5716, 79, 4, 'پھر قَسم ہے (تعمیلِ حکم میں) ایک دوسرے پر سبقت لے جانے والوں کی۔'),
(5717, 79, 5, 'پھر قَسم ہے ہر امر کا بندوبست کرنے والوں کی (کہ قیامت برحق ہے)۔'),
(5718, 79, 6, 'جس دن تھرتھرانے والی (زمین وغیرہ) تھرتھرائے گی۔'),
(5719, 79, 7, 'اس کے پیچھے ایک اور آنے والی (مصیبت) آئے گی۔'),
(5720, 79, 8, 'کچھ دل اس دن کانپ رہے ہوں گے۔'),
(5721, 79, 9, 'ان کی آنکھیں (شدتِ خوف سے) جھکی ہوئی ہوں گی۔'),
(5722, 79, 10, 'وہ (کافر لوگ) کہتے ہیں کیا ہم پہلی حالت میں (الٹے پاؤں) واپس لائے جائیں گے؟'),
(5723, 79, 11, 'کیا جب ہم بوسیدہ ہڈیاں ہو جائیں گے؟'),
(5724, 79, 12, 'کہتے ہیں یہ (واپسی) تو بڑے گھاٹے کی ہوگی۔'),
(5725, 79, 13, 'حالانکہ اس (واپسی) کیلئے ایک زور کی ڈانٹ پڑے گی۔'),
(5726, 79, 14, 'پھر وہ کھلے ہوئے میدان (قیامت) میں موجود ہوں گے۔'),
(5727, 79, 15, '(اے رسول(ص)) کیا آپ(ص) کو موسیٰ(ع) کے قصہ کی خبر پہنچی ہے؟'),
(5728, 79, 16, 'جب ان کے پروردگار نے طویٰ کی مقدس وادی میں انہیں پکارا تھا۔'),
(5729, 79, 17, 'کہ جاؤ فرعون کے پاس کہ وہ سرکش ہوگیا ہے۔'),
(5730, 79, 18, 'پس اس سے کہو کہ کیا تو چاہتا ہے کہ پاکیزگی اختیار کرے؟'),
(5731, 79, 19, 'اور (کیا تو چاہتا ہے کہ) میں تیرے پروردگار کی طرف تیری راہنمائی کروں تو تُو (اس سے) ڈرے؟'),
(5732, 79, 20, 'پس موسیٰ(ع) نے (وہاں جا کر) اسے ایک بہت بڑی نشانی دکھائی۔'),
(5733, 79, 21, 'تو اس نے جھٹلایا اور نافرمانی کی۔'),
(5734, 79, 22, 'پھر وہ پلٹا (اور مخالفانہ سرگرمیوں میں) کوشاں ہو گیا۔'),
(5735, 79, 23, 'یعنی (لوگوں کو) جمع کیا اور پکار کر کہا۔'),
(5736, 79, 24, 'کہ میں تمہارا سب سے بڑا پرورگار ہوں۔'),
(5737, 79, 25, 'پس اللہ نے اسے آخرت اور دنیا کے (دوہرے) عذاب میں پکڑا۔'),
(5738, 79, 26, 'بےشک اس (قصہ) میں بڑی عبرت ہے ہر اس شخص کیلئے جو (خدا سے) ڈرے۔'),
(5739, 79, 27, 'کیا تم لوگوں کا (دوبارہ) پیدا کرنا زیادہ مشکل ہے؟ یا آسمان کا؟ اللہ نے اس کو بنایا۔'),
(5740, 79, 28, 'اور اس کی چھت کو بلند کیا پھر اس کو درست کیا۔'),
(5741, 79, 29, 'اور اس کی رات کو تاریک بنایا اور اس کے دن کو ظاہر کیا۔'),
(5742, 79, 30, 'اس کے بعد زمین کو بچھایا۔'),
(5743, 79, 31, 'اور اس سے اس کا پانی اور چارہ نکالا۔'),
(5744, 79, 32, 'اور پہاڑوں کو اس میں گاڑا۔'),
(5745, 79, 33, 'تمہارے اور تمہارے مو یشیوں کے لئے سامانِ زندگی کے طور پر۔'),
(5746, 79, 34, 'پس جب بڑی آفت (قیامت) آئے گی۔'),
(5747, 79, 35, 'جس دن انسان یاد کرے گا جو کچھ اس نے کیا ہوگا۔'),
(5748, 79, 36, 'اور (ہر) دیکھنے والے کیلئے دوزخ ظاہر کر دی جائے گی۔'),
(5749, 79, 37, 'پس جس شخص نے سرکشی کی ہوگی۔'),
(5750, 79, 38, 'اور (آخرت پر) دنیٰوی زندگی کو ترجیح دی ہوگی۔'),
(5751, 79, 39, 'تو اس کا ٹھکانہ دوزخ ہوگا۔'),
(5752, 79, 40, 'اور جو شخص اپنے پروردگار کی بارگاہ میں حاضری سے ڈرتا رہا ہوگا اور (اپنے) نفس کو (اس کی) خواہش سے روکا ہوگا۔'),
(5753, 79, 41, 'تو اس کا ٹھکانہ جنت ہے۔'),
(5754, 79, 42, 'یہ لوگ آپ(ص) سے سوال کرتے ہیں کہ قیامت کب کھڑی (برپا) ہوگی۔'),
(5755, 79, 43, 'آپ(ص) کا اس کے وقت بتانے سے کیا تعلق؟'),
(5756, 79, 44, 'اس کی انتہا تو بس آپ(ص) کے پروردگار پر ہے۔'),
(5757, 79, 45, 'آپ(ص) تو بس ڈرانے والے ہیں اس شخص کو جو اس سے ڈرے۔'),
(5758, 79, 46, 'جس دن یہ لوگ اس (قیامت) کو دیکھیں گے تو (انہیں ایسا محسوس ہوگا کہ) وہ (دنیا میں) نہیں ٹھہرے تھے۔ مگر ایک شام یا اس کی ایک صبح۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, '(ایک شخص نے) تیوری چڑھائی اور منہ پھیر لیا۔'),
(5760, 80, 2, 'کہ اس (پیغمبرِ اسلام (ص)) کے پاس ایک نابینا آیا۔'),
(5761, 80, 3, 'اور تمہیں کیا معلوم شاید وہ پاکیزہ ہو جاتا۔'),
(5762, 80, 4, 'یا نصیحت حاصل کرتا اور نصیحت اسے فائدہ پہنچاتی۔'),
(5763, 80, 5, 'جوشخص مالدار ہے (یا بےپروائی کرتا ہے)۔'),
(5764, 80, 6, 'تو تم اس کی طرف تو توجہ کرتے ہو۔'),
(5765, 80, 7, 'حالانکہ تم پر کوئی الزام نہیں اگر وہ پاکیزہ نہیں ہوتا۔'),
(5766, 80, 8, 'اور جو تمہارے پاس دوڑتا ہوا (شوق سے) آتا ہے۔'),
(5767, 80, 9, 'اور (خدا سے) ڈرتا ہے۔'),
(5768, 80, 10, 'تو تم اس سے بےرُخی بر تتے ہو۔'),
(5769, 80, 11, 'ہرگز نہیں! یہ (قرآن) تو ایک نصیحت ہے۔'),
(5770, 80, 12, 'جو چاہے اسے قبول کرے۔'),
(5771, 80, 13, 'یہ (قرآن) ان صحیفوں میں درج ہے جو مکرم ہیں۔'),
(5772, 80, 14, 'بلند مر تبہ (اور) پاک و پاکیزہ ہیں۔'),
(5773, 80, 15, 'جو ایسے کاتبوں کے ہاتھوں سے (لکھے ہوئے) ہیں۔'),
(5774, 80, 16, 'جو معزز اور نیکوکار ہیں۔'),
(5775, 80, 17, 'غارت ہو (منکر) انسان یہ کتنا بڑا ناشکرا ہے؟'),
(5776, 80, 18, 'اللہ نے اسے کس چیز سے پیدا کیا ہے؟'),
(5777, 80, 19, 'نطفہ سے اسے پیدا کیا ہے اور پھر اس کے اعضاء و جوا رح کا اندازہ مقرر کیا ہے۔'),
(5778, 80, 20, 'پھر (زندگی کا) راستہ اس کے لئے آسان کر دیا۔'),
(5779, 80, 21, 'پھر اس کو موت دی پھر اسے قبر میں پہنچایا۔'),
(5780, 80, 22, 'پھر جب چاہے گا اسے دوبارہ زندہ کر دے گا۔'),
(5781, 80, 23, 'ہرگز نہیں (بایں ہمہ) اس نے اسے پورا نہ کیا جس کا (خدا نے) اسے حکم دیا تھا۔'),
(5782, 80, 24, 'انسان کو چاہیے کہ وہ اپنی غذا کی طرف دیکھے۔'),
(5783, 80, 25, 'ہم نے اچھی طرح خوب پانی برسایا۔'),
(5784, 80, 26, 'پھر ہم نے زمین کو اچھی طرح شگافتہ کیا۔'),
(5785, 80, 27, 'پھر ہم نے اس میں غلے۔'),
(5786, 80, 28, 'اور انگور اور ترکاریاں اگائیں۔'),
(5787, 80, 29, 'اور زیتون اور کجھوریں۔'),
(5788, 80, 30, 'اور گھنے باغ۔'),
(5789, 80, 31, 'اور میوے اور چارا۔'),
(5790, 80, 32, 'جو تمہارے اور تمہارے مویشوں کیلئے سامانِ زندگی کے طور پر ہے۔'),
(5791, 80, 33, 'پس جب کانوں کو پھاڑ دینے والی آواز آجائے گی۔'),
(5792, 80, 34, 'تو اس دن آدمی اپنے بھائی سے۔'),
(5793, 80, 35, 'اور اپنی ماں اور اپنے باپ سے۔'),
(5794, 80, 36, 'اور اپنی بیوی اور اپنے بیٹوں سے بھاگے گا۔'),
(5795, 80, 37, 'اس دن ان میں ہر شخص کا یہ عالم ہوگا جو اسے سب سے بےپروا کر دے گا۔'),
(5796, 80, 38, 'کچھ چہرے اس دن روشن ہوں گے۔'),
(5797, 80, 39, 'خنداں و شاداں (اور خوش و خرم) ہوں گے۔'),
(5798, 80, 40, 'ور کچھ چہرے ایسے ہوں گے جن پر گرد و غبار پڑی ہوئی ہوگی۔'),
(5799, 80, 41, 'اور ان پر سیاہی چھائی ہوگی۔'),
(5800, 80, 42, 'اور یہی لوگ کافر و فاجر ہوں گے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'جب سورج (کی بساط) لپیٹ دی جائے گی۔'),
(5802, 81, 2, 'اور ستارے (بکھر کر) بےنور ہو جائیں گے۔'),
(5803, 81, 3, 'اور جب پہاڑ چلا دئیے جائیں گے۔'),
(5804, 81, 4, 'اور جب حاملہ اونٹیاں آوارہ پھریں گی۔'),
(5805, 81, 5, 'اور جب وحشی جانور اکٹھے کر دئیے جائیں گے۔'),
(5806, 81, 6, 'اور جب سمندر بھڑکا دئیے جائیں گے۔'),
(5807, 81, 7, 'اور جب جانیں (جِسموں سے) ملا دی جائیں گی۔'),
(5808, 81, 8, 'اور جب زندہ درگور کی ہوئی (لڑکی) سے پوچھا جائے گا۔'),
(5809, 81, 9, 'کہ وہ کس گناہ پر قتل کی گئی؟'),
(5810, 81, 10, 'اور جب اعمال نامے کھولے جائیں گے۔'),
(5811, 81, 11, 'اور جب آسمان کھول دیا جائے گا۔'),
(5812, 81, 12, 'اور جب دوزخ بھڑکا دی جائے گی۔'),
(5813, 81, 13, 'اور جب جنت قریب لے آئی جائے گی۔'),
(5814, 81, 14, 'تب ہر شخص کو معلوم ہو جائے گا کہ اس نے کیا لا کر پیش کیا ہے۔'),
(5815, 81, 15, 'تو نہیں! میں قَسم کھاتا ہوں پیچھے ہٹنے والے۔'),
(5816, 81, 16, 'سیدھے چلنے اور چھپ جانے والے ستاروں کی۔'),
(5817, 81, 17, 'اور قَسم کھاتا ہوں رات کی جب وہ جانے لگے۔'),
(5818, 81, 18, 'اور صبح کی جب وہ سانس لے کر آنے لگے۔'),
(5819, 81, 19, 'بےشک یہ (قرآن) ایک معزز پیغامبر(ص) کا قول ہے۔'),
(5820, 81, 20, 'جو قوت والا ہے اور مالکِ عرش کے نزدیک بلند مرتبہ ہے۔'),
(5821, 81, 21, '(وہاں) اس کا حکم مانا جاتا ہے اور پھر وہ امانتدار (بھی) ہے۔'),
(5822, 81, 22, 'اور تمہارا ساتھی (پیغمبرِ اسلام (ص)) دیوانہ نہیں ہے۔'),
(5823, 81, 23, 'اور اس (پیغمبر(ص)) نے اس (پیغامبر) کو روشن افق (کنارے) پر دیکھا ہے۔'),
(5824, 81, 24, 'اور وہ غیب کی باتوں کے معاملہ میں بخیل نہیں ہے۔'),
(5825, 81, 25, 'اور وہ (قرآن) کسی مردود شیطان کا قول نہیں ہے۔'),
(5826, 81, 26, 'تم کدھر جا رہے ہو؟'),
(5827, 81, 27, 'وہ نہیں ہے مگر تمام دنیا جہان کے لئے نصیحت۔'),
(5828, 81, 28, 'یعنی اس کیلئے جو تم میں سے سیدھی راہ پر چلنا چاہے۔'),
(5829, 81, 29, 'اور تم نہیں چاہتے مگر وہی جو عالمین کا پرورگار چاہتا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'اور جب آسمان پھٹ جائے گا۔'),
(5831, 82, 2, 'اور جب ستارے بکھر جائیں گے۔'),
(5832, 82, 3, 'اور جب سمندر بہہ پڑیں گے۔'),
(5833, 82, 4, 'اور جب قبریں تہ و بالا کر دی جائیں گی۔'),
(5834, 82, 5, 'تب ہر شخص کو معلوم ہو جائے گا کہ اس نے آگے کیا بھیجا ہے اور پیچھے کیا چھوڑا ہے؟'),
(5835, 82, 6, 'اے انسان تجھے کس چیز نے اپنے (رحیم و) کریم پروردگار سے دھو کے میں ڈال رکھا ہے؟'),
(5836, 82, 7, 'جس نے تجھے پیدا کیا پھر (تیرے اعضاء کو) درست بنایا (اور) پھر تجھے متناسب بنایا۔'),
(5837, 82, 8, 'جس شکل میں چاہا تجھے ترتیب دے دیا۔'),
(5838, 82, 9, 'ہرگز نہیں! بلکہ (اصل حقیقت یہ ہے کہ) تم جزا و سزا (کے دن) کو جھٹلاتے ہو۔'),
(5839, 82, 10, 'حالانکہ تم پر نگران (ملائکہ) مقرر ہیں۔'),
(5840, 82, 11, 'معزز لکھنے والے۔'),
(5841, 82, 12, 'وہ جانتے ہیں جو کچھ تم کرتے ہو۔'),
(5842, 82, 13, 'بےشک نیکوکار لوگ عیش و آرام میں ہوں گے۔'),
(5843, 82, 14, 'اور بدکار لوگ دوزخ میں ہوں گے۔'),
(5844, 82, 15, 'وہ جزا (سزا) والے دن اس میں داخل ہوں گے۔'),
(5845, 82, 16, 'اور وہ اس سے اوجھل نہیں ہوں گے۔'),
(5846, 82, 17, 'تمہیں کیا معلوم کہ وہ جزا و سزا کا دن کیا ہے؟'),
(5847, 82, 18, '(پھر سنو) تمہیں کیا معلوم کہ جزا (و سزا) والا دن کیا ہے؟'),
(5848, 82, 19, 'جس دن کوئی کسی دوسرے کے کام نہ آسکے گا اور اس دن معاملہ (اختیار) اللہ ہی کے قبضۂ قدرت میں ہوگا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'بربادی ہے ناپ تول میں کمی کرنے (ڈنڈی مارنے) والوں کے لئے۔'),
(5850, 83, 2, 'جب وہ (اپنے لئے) لوگوں سےناپ کر لیں تو پورا لیتے ہیں۔'),
(5851, 83, 3, 'اور جب لوگوں کو ناپ کر یا تول کر دیتے ہیں تو انہیں گھاٹا دیتے ہیں۔'),
(5852, 83, 4, 'کیا یہ لوگ خیال نہیں کرتے کہ وہ (دوبارہ زندہ کر کے) اٹھائے جائیں گے۔'),
(5853, 83, 5, 'ایک بڑے (سخت) دن کیلئے۔'),
(5854, 83, 6, 'جس دن تمام لوگ ربُ العالمین کی بارگاہ میں پیشی کیلئے کھڑے ہوں گے۔'),
(5855, 83, 7, 'ہرگز نہیں! بےشک بدکاروں کا نامۂ اعمال سِجِّین (قید خانہ کے دفتر) میں ہے۔'),
(5856, 83, 8, 'تمہیں کیا معلوم کہ سِجِّین کیا ہے؟'),
(5857, 83, 9, 'یہ ایک لکھی ہوئی کتاب ہے (جس میں بدکاروں کے عمل درج ہیں)۔'),
(5858, 83, 10, 'بربادی ہے اس دن جھٹلانے والوں کیلئے۔'),
(5859, 83, 11, 'جو جزا و سزا کے دن (قیامت) کو جھٹلاتے ہیں۔'),
(5860, 83, 12, 'اور اس دن کو نہیں جھٹلاتا مگر وہ شخص جو حد سے گزر نے والا (اور) گنہگار ہے۔'),
(5861, 83, 13, 'کہ جب اس کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو وہ کہتا ہے کہ یہ تو پہلے زمانے والوں کے افسانے ہیں۔'),
(5862, 83, 14, 'ہرگز نہیں بلکہ ان کے دلوں پر ان کے اعمال کا زَنگ چڑھ گیا ہے جو وہ کرتے ہیں۔'),
(5863, 83, 15, 'ہرگز (ایسا نہیں کہ جزا وسزا نہ ہو) یہ لوگ اس دن اپنے پروردگار (کی رحمت سے) (محجوب اور محروم) رہیں گے۔'),
(5864, 83, 16, 'پھر یہ لوگ جہنم میں ڈالے جائیں گے۔'),
(5865, 83, 17, 'پھر (ان سے) کہا جائے گا کہ یہی وہ جہنم ہے جس کو تم جھٹلاتے تھے۔'),
(5866, 83, 18, 'ہرگز (ایسا) نہیں (کہ جزا و سزا نہ ہو) یقیناً نیکو کاروں کا نامۂ اعمال علیین (بلند مرتبہ لوگوں کے دفتر) میں ہے۔'),
(5867, 83, 19, 'اور تمہیں کیا معلوم کہ علیین (بلند مرتبہ لوگوں کا دفتر) کیا ہے؟'),
(5868, 83, 20, 'وہ ایک لکھی ہوئی کتاب ہے (جس میں نیکو کاروں کے عمل درج ہیں)۔'),
(5869, 83, 21, 'جس کامشاہدہ مقرب فرشتے کرتے ہیں۔'),
(5870, 83, 22, 'بےشک نیکوکار لوگ عیش و آرام میں ہوں گے۔'),
(5871, 83, 23, 'اونچی مسندوں پر بیٹھ کر دیکھ رہے ہونگے (نظارے کر رہے ہوں گے)۔'),
(5872, 83, 24, 'تم ان کے چہروں پر راحت و آرام کی شادابی محسوس کرو گے۔'),
(5873, 83, 25, 'انہیں سر بمہر عمدہ شراب (طہور) پلائی جائے گی۔'),
(5874, 83, 26, 'جس پر مشک کی مہر ہوگی۔ اس چیز میں سبقت لے جانے والوں کو سبقت (اور رغبت) کرنی چاہیے۔'),
(5875, 83, 27, 'ور اس (شراب میں) تسنیم کی آمیزش ہوگی۔'),
(5876, 83, 28, 'یہ وہ چشمہ ہے جس سے مقرب لوگ پئیں گے۔'),
(5877, 83, 29, 'بےشک جو مجرم لوگ تھے وہ (دارِ دنیا میں) اہلِ ایمان پر ہنستے تھے۔'),
(5878, 83, 30, 'جب ان کے پاس سے گزرتے تھے تو آنکھیں مارا کرتے تھے۔'),
(5879, 83, 31, 'اور جب اپنے گھر والوں کی طرف لوٹتے تھے تو دل لگیاں کرتے ہوئے لوٹتے تھے۔'),
(5880, 83, 32, 'اور جب ان (اہلِ ایمان) کو دیکھتے تھے تو کہتے تھے کہ یہ بھٹکے ہوئے لوگ ہیں۔'),
(5881, 83, 33, 'حالانکہ وہ انکے نگران بنا کر نہیں بھیجے گئے تھے۔'),
(5882, 83, 34, 'پس آج اہلِ ایمان کافروں (اور منکروں) پر ہنستے ہوں گے۔'),
(5883, 83, 35, 'اونچی مسندوں پر بیٹھے ہوئے (ان کی حالت) دیکھ رہے ہونگے۔'),
(5884, 83, 36, 'کیا کافروں کو ان کے کئے ہوئے (کرتوتوں) کا پورا بدلہ مل گیا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'اور جب آسمان پھٹ جائے گا۔'),
(5886, 84, 2, 'اور وہ اپنے پروردگار کا حکم سن لے گا (اور اس کی تعمیل کرے گا) اور اس پر لازم بھی یہی ہے۔'),
(5887, 84, 3, 'اور جب زمین پھیلا دی جائے گی۔'),
(5888, 84, 4, 'اور جو کچھ اس کے اندر ہے وہ اسے باہر پھینک دے گی اور خالی ہو جائے گی۔'),
(5889, 84, 5, 'اور اپنے پروردگار کا حکم سنے گی اور (اس کی تعمیل کرے گی) اور اس پر لازم بھی یہی ہے۔'),
(5890, 84, 6, 'اے انسان! تو کشاں کشاں اپنے پروردگار کی طرف (کھنچا چلا) جا رہا ہے اور اس کی بارگاہ میں حاضر ہو نے والا ہے۔'),
(5891, 84, 7, 'پس جس کا نامۂ اعمال اس کے دائیں ہاتھ میں دیا جائے گا۔'),
(5892, 84, 8, 'تو اس سے آسان حساب لیا جائے گا۔'),
(5893, 84, 9, 'اور وہ اپنے لوگوں کی طرف خوش و خرم لوٹے گا۔'),
(5894, 84, 10, 'اور جس کا نامۂ اعمال اس کے پس پشت دیا جائے گا۔'),
(5895, 84, 11, 'تو وہ موت (اور تباہی) کو پکارے گا۔'),
(5896, 84, 12, 'اور (دوزخ کی) بھڑکتی ہوئی آگ میں داخل ہوگا۔'),
(5897, 84, 13, 'یہ شخص (دنیا میں) اپنے لوگوں میں خوش خوش رہتا تھا۔'),
(5898, 84, 14, 'اس کا خیال تھا کہ وہ کبھی (اپنے خدا کے پاس) لوٹ کر نہیں جائے گا۔'),
(5899, 84, 15, 'کیوں نہیں! بےشک اس کا پروردگار اسے خوب دیکھ رہا تھا۔'),
(5900, 84, 16, 'پس نہیں! میں قَسم کھاتا ہوں شفق کی۔'),
(5901, 84, 17, 'اور رات کی اور ان چیزوں کی (قَسم کھاتا ہوں) جن کو وہ (رات) سمیٹ لیتی ہے۔'),
(5902, 84, 18, 'اور چاند کی (قَسم کھاتا ہوں) جب وہ پورا ہو جائے۔'),
(5903, 84, 19, 'تمہیں یونہی (تدریجاً) زینہ بہ زینہ چڑھنا ہے (اور ایک ایک منزل طے کرنی ہے)۔'),
(5904, 84, 20, 'تو انہیں کیا ہوگیا ہے کہ ایمان نہیں لاتے؟'),
(5905, 84, 21, 'اور جب ان کے سامنے قرآن پڑھا جاتا ہے تویہ سجدہ نہیں کرتے؟'),
(5906, 84, 22, 'بلکہ کافر لوگ تو الٹا (اسے) جھٹلاتے ہیں۔'),
(5907, 84, 23, 'اور اللہ بہتر جانتا ہے جو کچھ وہ (اپنے دلوں میں) جمع کر رہے ہیں۔'),
(5908, 84, 24, 'آپ(ص) انہیں دردناک عذاب کی خبر دے دیجئے۔'),
(5909, 84, 25, 'ہاں البتہ جو لوگ ایمان لائے اور نیک اعمال کئے ان کیلئے کبھی ختم نہ ہو نے والا اجر و ثواب ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'قَسم ہے بُرجوں (قلعوں) والے آسمان کی۔'),
(5911, 85, 2, 'اور وعدہ کئے گئے دن (قیامت) کی۔'),
(5912, 85, 3, 'اور گواہ کی قَسم اور اس چیز کی جس کی گواہی دی جائے گی۔'),
(5913, 85, 4, 'غارت ہوئے خندق والے۔'),
(5914, 85, 5, 'جس میں بھڑکتے ہوئے ایندھن والی آگ تھی۔'),
(5915, 85, 6, 'جب کہ وہ اس (خندق) کے کنارے پر بیٹھے ہوئے تھے۔'),
(5916, 85, 7, 'اور وہ جو کچھ اہلِ ایمان کے ساتھ کر رہے تھے اس کو دیکھ رہے تھے۔'),
(5917, 85, 8, 'اورانہوں نے اہلِ ایمان کی کسی چیز کو ناپسند نہیں کیا (اور ان میں کوئی عیب نظر نہیں آیا) سوائے اس کے کہ وہ اللہ پر ایمان لائے جو غالب ہے (اور) سزاوارِ ستائش ہے۔'),
(5918, 85, 9, 'وہ جس کی آسمانوں اور زمین میں بادشاہی ہے اور ہر چیز پر گواہ ہے۔'),
(5919, 85, 10, 'جن لوگوں نے مؤمن مردوں اور مؤمن عورتوں پر ظلم و ستم کیا اور پھر توبہ بھی نہ کی تو ان کیلئے جہنم کا عذاب ہے اور آگ سے جلنے کی سزا ہے۔'),
(5920, 85, 11, 'بےشک جو لوگ ایمان لائے اور نیک عمل کئے ان کیلئے (جنت کے) باغ ہیں جن کے نیچے نہریں جاری ہیں یہ بڑی کامیابی ہے۔'),
(5921, 85, 12, 'بےشک تمہارے پروردگار کی گرفت بڑی سخت ہے۔'),
(5922, 85, 13, 'وہی پہلی بار پیدا کرنے والا ہے اور وہی دوبارہ پیدا کرے گا۔'),
(5923, 85, 14, 'وہ بڑا بخشنے والا (اور) بڑا محبت کرنے والا ہے۔'),
(5924, 85, 15, 'وہ عرش کامالک (اور) بڑی شان والا ہے۔'),
(5925, 85, 16, 'وہ جو چاہتا ہے وہ کر گزرتا ہے۔'),
(5926, 85, 17, 'کیا تمہیں لشکروں کی خبر پہنچی ہے؟'),
(5927, 85, 18, 'یعنی فرعون اور ثمود (کے لشکروں) کی۔'),
(5928, 85, 19, 'بلکہ یہ کافر (و منکر) تو جھٹلانے میں لگے ہیں۔'),
(5929, 85, 20, 'حالانکہ اللہ ان کو آگے پیچھے سے گھیرے ہوئے ہے۔'),
(5930, 85, 21, 'بلکہ وہ بڑی شان والا قرآن ہے۔'),
(5931, 85, 22, 'جو لوحِ محفوظ میں ثبت ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'قَسم ہے آسمان کی اور رات کو نمودار ہو نے والے کی۔'),
(5933, 86, 2, 'اورتمہیں کیا معلوم کہ رات کو نمودار ہو نے والا کیا ہے؟'),
(5934, 86, 3, 'وہ چمکتا ہوا تاراہے۔'),
(5935, 86, 4, 'کوئی متنفس ایسا نہیں ہے جس پرکوئی نگہبان نہ ہو۔'),
(5936, 86, 5, 'سو انسان کو دیکھنا چاہیے کہ وہ کس چیز سے پیدا کیا گیا ہے؟'),
(5937, 86, 6, 'اچھل کر نکلنے والے پانی سے پیدا کیا گیا ہے۔'),
(5938, 86, 7, 'جو ریڑھ اور سینے کی ہڈیوں کے درمیان سے نکلتا ہے۔'),
(5939, 86, 8, 'بےشک وہ (خدا) اس کے لوٹا سکنے (دوبارہ پیدا کرنے) پر قادر ہے۔'),
(5940, 86, 9, 'جس دن سب پوشیدہ راز کھل جائیں گے۔'),
(5941, 86, 10, 'پس اس وقت نہ خود انسان کے پاس کوئی طاقت ہوگی اور نہ کوئی مددگار ہوگا۔'),
(5942, 86, 11, 'قَسم ہے بارش والے آسمان کی۔'),
(5943, 86, 12, 'اور (نباتات کے ذریعہ سے) پھٹ جانے والی زمین کی۔'),
(5944, 86, 13, 'کہ وہ (قرآن) قولِ فیصل ہے۔'),
(5945, 86, 14, 'کوئی ہنسی مذاق نہیں ہے۔'),
(5946, 86, 15, 'بےشک وہ (کافر لوگ) کچھ چالیں چل رہے ہیں۔'),
(5947, 86, 16, 'اور میں بھی (ان کیخلاف) ایک چال چل رہا ہوں۔'),
(5948, 86, 17, 'تو (اے رسول(ص)) ان (کافروں) کو مہلت دے دیجئے ان کو تھوڑی سی مہلت دے دیجئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, '(اے نبی(ص)) اپنے بلند و برتر پروردگار کے نام کی تسبیح کیجئے۔'),
(5950, 87, 2, 'جس نے (ہر چیزکو) پیدا کیا پھر درست کیا (تناسب قائم کیا)۔'),
(5951, 87, 3, 'جس نے تقدیر مقرر کی (ہر چیز کا اندازہ مقرر کیا) اور (سیدھی) راہ دکھائی۔'),
(5952, 87, 4, 'اس نے (جانوروں کیلئے) زمین سے چارہ نکالا۔'),
(5953, 87, 5, 'پھر اسے (خشک کرکے) سیاہی مائل کوڑا بنا دیا۔'),
(5954, 87, 6, 'ہم آپ(ص) کو (ایسا) پڑھائیں گے کہ پھر آپ(ص) نہیں بھولیں گے۔'),
(5955, 87, 7, 'مگر جو اللہ چاہے (بھلا دے) بےشک وہ ظاہر کو بھی جانتا ہے اور اس کو بھی جو چھپا ہوا ہے۔'),
(5956, 87, 8, 'اور ہم آپ(ص) کو آسان (شریعت) کی سہولت دیں گے۔'),
(5957, 87, 9, 'پس آپ(ص) نصیحت کیجئے اگر نصیحت کچھ فائدہ پہنچائے۔'),
(5958, 87, 10, 'وہ شخص نصیحت قبول کرے گا جو (خدا و آخرت سے) ڈرتا ہوگا۔'),
(5959, 87, 11, 'اور جو بدبخت ہے وہ اس سے گریز کرے گا۔'),
(5960, 87, 12, 'جو بڑی آگ میں داخل ہوگا۔'),
(5961, 87, 13, 'پھر وہ اس میں نہ مرے گا نہ جئے گا۔'),
(5962, 87, 14, 'وہ شخص فائز المرام ہوا جس نے اپنے آپ کو (بداعتقادی و بدعملی سے) پاک کیا۔'),
(5963, 87, 15, 'اور اپنے پروردگار کا نام یاد کیا اور نماز پڑھی۔'),
(5964, 87, 16, 'مگر تم لوگ دنیا کی زندگی کو (آخرت پر) ترجیح دیتے ہو۔'),
(5965, 87, 17, 'حالانکہ آخرت بہتر ہے اور پائیدار ہے۔'),
(5966, 87, 18, 'یقیناً یہ بات پہلے صحیفوں میں بھی ہے۔'),
(5967, 87, 19, 'یعنی ابراہیم(ع) اور موسیٰ(ع) کے صحیفوں میں۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'کیا تمہیں (کائنات پر) چھا جانے والی (مصیبت یعنی قیامت) کی خبر پہنچی ہے؟'),
(5969, 88, 2, 'اس دن کچھ چہرے ذلیل (اترے ہوئے) ہوں گے۔'),
(5970, 88, 3, 'سخت محنت کرنے والے (نڈھال اور) تھکے ماندے ہوں گے۔'),
(5971, 88, 4, 'وہ دہکتی ہوئی آگ میں پڑیں گے (اور جھلسیں گے)۔'),
(5972, 88, 5, 'انہیں کھولتے ہوئے چشمہ کا پانی پلایا جائے گا۔'),
(5973, 88, 6, '(وہاں) ان کیلئے کوئی کھانا نہ ہوگا سوائے خاردار جھاڑ کے۔'),
(5974, 88, 7, 'جو نہ (انہیں) موٹا کرے گا اور نہ بھوک دور کرے گا۔'),
(5975, 88, 8, '(اور) کچھ چہرے اس دن تر و تازہ (اور با رونق) ہوں گے۔'),
(5976, 88, 9, 'اپنی کاوش و کوشش پر خوش اور مطمئن ہوں گے۔'),
(5977, 88, 10, '(اور) عالیشان جنت میں ہوں گے۔'),
(5978, 88, 11, 'جہاں وہ کوئی بےہودہ بات نہیں سنیں گے۔'),
(5979, 88, 12, 'اس میں چشمہ رواں دواں ہوگا۔'),
(5980, 88, 13, 'اس میں اونچے اونچے تخت (بچھے ہوئے) ہوں گے۔'),
(5981, 88, 14, 'اور جام و ساغر رکھے ہوں گے۔'),
(5982, 88, 15, 'اور گاؤ تکیے قطار در قطار لگے ہوئے ہوں گے۔'),
(5983, 88, 16, 'اور طرح طرح کے نفیس فرش و فروش بچھے ہوئے ہوں گے۔'),
(5984, 88, 17, 'کیا یہ لوگ اونٹ کو (غور سے) نہیں دیکھتے کہ وہ کیونکر پیدا کیا گیا ہے؟'),
(5985, 88, 18, 'اور آسمان کی طرف نہیں دیکھتے کہ کیسے بلند کیا گیا ہے۔'),
(5986, 88, 19, 'اور پہاڑوں کی طرف نہیں دیکھتے کہ کیونکر کھڑے کئے گئے ہیں؟'),
(5987, 88, 20, 'اور زمین کی طرف نہیں دیکھتے کہ کیسے بچھائی گئی ہے۔'),
(5988, 88, 21, 'پس(اے رسول(ص)) آپ(ص) نصیحت کیجئے (اور سمجھائیے) کہ آپ(ص) نصیحت کرنے (اور سمجھانے) والے ہیں۔'),
(5989, 88, 22, 'آپ(ص) ان پر داروغہ نہیں ہیں۔'),
(5990, 88, 23, 'ہاں البتہ جو شخص رُوگردانی کرے گا اور کفر کرے گا۔'),
(5991, 88, 24, 'تو اللہ اس کو بڑا عذاب دے گا۔'),
(5992, 88, 25, 'یقیناً ان کی بازگشت ہماری طرف ہے۔'),
(5993, 88, 26, 'پھر بیشک ان کا حساب کتاب ہمارے ذمہ ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'قَسم ہے صبح کی۔'),
(5995, 89, 2, 'اور دس (مقدس) راتوں کی۔'),
(5996, 89, 3, 'اورجُفت اور طاق کی۔'),
(5997, 89, 4, 'اور رات کی جبکہ (جانے کیلئے) چلنے لگے۔'),
(5998, 89, 5, 'کیا اس میں صاحبِ عقل کیلئے کوئی قسم ہے؟ (یعنی یقیناً ہے)۔'),
(5999, 89, 6, 'کیا آپ(ص) نے نہیں دیکھا کہ آپ(ص) کے پروردگار نے قومِ عاد کے ساتھ کیا کیا؟'),
(6000, 89, 7, 'یعنی اونچے ستونوں والے ارم کے ساتھ۔'),
(6001, 89, 8, 'جن کامثل (دنیا کے) شہروں میں پیدا نہیں کیا گیا۔'),
(6002, 89, 9, 'اور قومِ ثمود کے ساتھ (کیا کیا؟) جنہوں نے وادی میں چٹانیں تراشی تھیں (اور عمارتیں بنائی تھیں)۔'),
(6003, 89, 10, 'اور فرعون میخوں والے کے ساتھ (کیا کیا؟)۔'),
(6004, 89, 11, 'ان لوگوں نے شہروں میں سرکشی کی تھی۔'),
(6005, 89, 12, 'اوران میں بہت فساد پھیلایا تھا۔'),
(6006, 89, 13, 'تو آپ کے پروردگار نے ان پر عذاب کا کَوڑا برسایا۔'),
(6007, 89, 14, 'بےشک آپ(ص) کا پروردگار (ایسے لوگوں کی) تاک میں ہے۔'),
(6008, 89, 15, 'لیکن انسان (کا حال یہ ہے کہ) جب اس کا پروردگار اسے آزماتا ہے اور اسے عزت و نعمت دیتا ہے تو وہ کہتا ہے کہ میرے پروردگار نے میری عزت افزائی کی۔'),
(6009, 89, 16, 'اور جب وہ (خدا) اسے اس طرح آزماتا ہے کہ اس کا رزق اس پر تنگ کر دیتا ہے تو وہ کہتا ہے کہ میرے پروردگار نے مجھے ذلیل کر دیا۔'),
(6010, 89, 17, 'ہرگز نہیں! بلکہ تم لوگ یتیم کی عزت نہیں کرتے۔'),
(6011, 89, 18, 'اور مسکین کو کھانا کھلانے پر ایک دوسرے کو آمادہ نہیں کرتے۔'),
(6012, 89, 19, 'اور وراثت کا سارا مال (حلال و حرام) سمیٹ کر کھا جاتے ہو۔'),
(6013, 89, 20, 'اورتم مال و منال سے بہت زیادہ محبت کرتے ہو۔'),
(6014, 89, 21, 'ہرگز نہیں! وہ وقت یاد کرو جب زمین کو توڑ کر ریزہ ریزہ کر دیا جائے گا۔'),
(6015, 89, 22, 'اور تمہارا پروردگار (یعنی اس کا حکم) آجائے گا اور فرشتے قطار اندر قطار آئیں گے۔'),
(6016, 89, 23, 'اور اس دن جہنم (سامنے) لائی جائے گی اور اس دن انسان کو سمجھ آئے گی مگر اب سمجھ آنے کا کیا فائدہ؟'),
(6017, 89, 24, 'وہ کہے گا کہ کاش! میں نے اپنی (اس) زندگی کیلئے کچھ آگے بھیجا ہوتا۔'),
(6018, 89, 25, 'پس اس دن نہ تو خدا کی طرح کوئی عذاب دے گا۔'),
(6019, 89, 26, 'اور نہ اس کے باندھنے کی طرح کوئی باندھ سکے گا۔'),
(6020, 89, 27, '(ارشاد ہوگا) اے نفسِ مطمئن۔'),
(6021, 89, 28, 'تو اس حالت میں اپنے پروردگار کی طرف چل کہ تواس سے راضی ہے اور وہ تجھ سے راضی ہے۔'),
(6022, 89, 29, 'پس تُو میرے (خاص) بندوں میں داخل ہو جا۔'),
(6023, 89, 30, 'اور میری جنت میں داخل ہو جا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'نہیں! میں قَسم کھاتا ہوں اس شہر کی۔'),
(6025, 90, 2, 'درآنحالیکہ آپ(ص) اس شہر میں قیام پذیر ہیں۔'),
(6026, 90, 3, 'اور قَسم کھاتا ہوں باپ (آدم(ع)) کی اوراس کی اولاد کی۔'),
(6027, 90, 4, 'یقیناً ہم نے انسان کو محنت و مشقت کیلئے پیدا کیا ہے۔'),
(6028, 90, 5, 'کیا وہ یہ خیال کرتا ہے کہ اس پر کوئی قابو نہ پاسکے گا۔'),
(6029, 90, 6, 'وہ کہتا ہے کہ میں نے بہت سا مال اڑادیا۔'),
(6030, 90, 7, 'کیا وہ خیال کرتا ہے کہ اسے کسی نے نہیں دیکھا۔'),
(6031, 90, 8, 'کیا ہم نے اس کیلئے دو آنکھیں نہیں بنائیں؟'),
(6032, 90, 9, 'اور ایک زبان اور دو ہونٹ؟'),
(6033, 90, 10, 'اور ہم نے اسے دونوں راستے دکھا دئیے ہیں۔'),
(6034, 90, 11, 'مگر وہ (نیکی کی) دشوار گزار گھاٹی سے نہیں گزرا۔'),
(6035, 90, 12, 'اور تمہیں کیا معلوم کہ وہ دشوار گزار گھاٹی کیا ہے؟'),
(6036, 90, 13, 'وہ کسی گردن کو (غلامی یا قرض سے) چھڑانا۔'),
(6037, 90, 14, 'یا بھوک کے دن۔'),
(6038, 90, 15, 'کسی یتیم رشتہ دار کو۔'),
(6039, 90, 16, 'یاخاکسار مسکین کو کھانا کھلانا۔'),
(6040, 90, 17, 'پھر وہ ان لوگوں میں سے بھی ہوتا جو ایمان لائے اور ایک دوسرے کو صبر کرنے اور رحم کرنے کی وصیت کی۔'),
(6041, 90, 18, 'یہی لوگ داہنے (ہاتھ) والے ہیں۔'),
(6042, 90, 19, 'اور جن لوگوں نے ہماری آیتوں کا انکار کیا وہ بائیں (ہاتھ والے ہیں)۔'),
(6043, 90, 20, 'ان پر چَو طرفہ بند کی ہوئی آگ محیط ہوگی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'قَسم ہے سورج اور اس کی ضیاء و شعا ع کی۔'),
(6045, 91, 2, 'اور چاند کی جب وہ اس (سورج) کے پیچھے آئے۔'),
(6046, 91, 3, 'دن کی جب وہ اس (سورج) کو خوب روشن کر دے۔'),
(6047, 91, 4, 'اور رات کی جب کہ وہ اسے ڈھانپ لے۔'),
(6048, 91, 5, 'اور آسمان کی اور اس (ذات) کی جس نے اسے بنایا۔'),
(6049, 91, 6, 'اور زمین کی اور جس نے اسے بچھایا۔'),
(6050, 91, 7, 'اور انسانی نفس کی اور اس کی جس نے اسے درست بنایا۔'),
(6051, 91, 8, 'پھر اسے اس کی بدکاری اور پرہیزگاری کا الہام (القاء) کیا۔'),
(6052, 91, 9, 'بےشک وہ شخص فلاح پاگیا جس نے اس (نفس) کا تزکیہ کیا۔'),
(6053, 91, 10, 'اور وہ شخص نامراد ہوا جس نے (گناہ سے آلودہ کر کے) اسے دبا دیا۔'),
(6054, 91, 11, 'قومِ ثمود نے اپنی سرکشی کی وجہ سے (اپنے پیغمبر(ص) کو) جھٹلایا۔'),
(6055, 91, 12, 'جب ان کا سب سے زیادہ بدبخت (آدمی) اٹھ کھڑا ہوا۔'),
(6056, 91, 13, 'تو اللہ کے رسول(ع) (صالح(ع)) نے ان لوگوں سے کہا کہ اللہ کی اونٹنی اور اس کے پانی پینے سے خبر دار رہنا (اس کا خیال رکھنا)۔'),
(6057, 91, 14, 'پس ان لوگوں نے ان (رسول(ع)) کو جھٹلایا اور اونٹنی کی کونچیں کاٹ دیں تو ان کے پروردگار نے ان کے گناہ (عظیم) کی پاداش میں ان پر ہلاکت نازل کی اور اسی (بستی) کو زمین کے برابر کر دیا۔'),
(6058, 91, 15, 'اور اللہ کو اس واقعہ کے انجام کا کوئی خوف نہیں ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'قَسم ہے رات کی جب کہ وہ (دن کو) ڈھانپ لے۔'),
(6060, 92, 2, 'اور قَسم ہے دن کی جب کہ وہ روشن ہو جائے۔'),
(6061, 92, 3, 'اور اس ذات کی قَسم جس نے نر اور مادہ کو پیدا کیا۔'),
(6062, 92, 4, 'یقیناً تمہاری کوششیں مختلف قِسم کی ہیں۔'),
(6063, 92, 5, 'تو جس نے (راہِ خدا میں) مال دیا اور پرہیزگاری اختیار کی۔'),
(6064, 92, 6, 'اور اچھی بات (اسلام) کی تصدیق کی۔'),
(6065, 92, 7, 'تو ہم اسے آسان راستہ کیلئے سہو لت دیں گے۔'),
(6066, 92, 8, 'اور جس نے بُخل کیا اور (خدا سے) بےپرواہی کی۔'),
(6067, 92, 9, 'اور اچھی بات کو جھٹلایا۔'),
(6068, 92, 10, 'تو ہم اسے سخت راستے کی سہولت دیں گے۔'),
(6069, 92, 11, 'اور اس کا مال اسے کوئی فائدہ نہ دے گا جب وہ (ہلاکت کے) گڑھے میں گرے گا۔'),
(6070, 92, 12, 'بےشک راہ دکھانا ہمارے ذمہ ہے۔'),
(6071, 92, 13, 'اور بےشک آخرت اور دنیا ہمارے ہی قبضہ میں ہیں۔'),
(6072, 92, 14, 'سو میں نے تمہیں بھڑکتی ہوئی آگ سے ڈرایا ہے۔'),
(6073, 92, 15, 'اس میں وہ داخل ہوگا(اور جلے گا) جو بڑا بدبخت ہوگا۔'),
(6074, 92, 16, 'جس نے جھٹلایا اور رُوگردانی کی۔'),
(6075, 92, 17, 'اور جو بڑا پرہیزگار ہوگا وہ اس سے دور رکھا جائے گا۔'),
(6076, 92, 18, 'جو پاکیزگی حاصل کرنے کیلئے اپنا مال دیتا ہے۔'),
(6077, 92, 19, 'اور اس پر کسی کا کوئی احسان نہیں ہے جس کا بدلہ دیا جائے۔'),
(6078, 92, 20, 'بلکہ وہ تو صرف اپنے بالا و برتر پروردگار کی خوشنودی چاہتا ہے۔'),
(6079, 92, 21, 'اور عنقریب وہ اس سے ضرور خوش ہوجائے گا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'قَسم ہے روزِ روشن کی۔'),
(6081, 93, 2, 'اور رات کی جب کہ وہ آرام و سکون کے ساتھ چھا جائے۔'),
(6082, 93, 3, '(اے رسول(ص)) نہ آپ کے پروردگار نے آپ کو چھوڑا ہے اور نہ وہ ناراض ہوا ہے۔'),
(6083, 93, 4, 'یقیناً آپ کے لئے انجام (اور بعد کا دور) آغاز (پہلے دور) سے بہتر ہے۔'),
(6084, 93, 5, 'اورعنقریب آپ(ص) کا پروردگار آپ(ص) کو اتنا عطا کرے گا کہ آپ(ص) خوش ہو جائینگے۔'),
(6085, 93, 6, 'کیا خدانے آپ(ص) کو یتیم نہیں پایا؟ تو پناہ کی جگہ دی؟'),
(6086, 93, 7, 'اور آپ(ص) کو گمنام پایا تو (لوگوں کو آپ(ص) کی طرف) راہنمائی کی۔'),
(6087, 93, 8, 'اور اللہ تعالیٰ نے آپ(ص) کونادار پایا سو مالدار بنا دیا۔'),
(6088, 93, 9, 'پس یتیم پر سختی نہ کیجئے۔'),
(6089, 93, 10, 'اور سائل کو نہ جھڑکئے۔'),
(6090, 93, 11, 'اور اپنے پروردگار کی نعمت کا اظہار کیجئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, '(اے رسول(ص)) کیا ہم نے آپ(ص) کے سینے کو کشادہ نہیں کر دیا؟'),
(6092, 94, 2, 'اور وہ بَوجھ آپ سے اتار دیا۔'),
(6093, 94, 3, 'جو آپ(ص) کی کمر کو توڑ رہا تھا۔'),
(6094, 94, 4, 'اور آپ(ص) کے ذکر (آوازہ) کو بلند کیا۔'),
(6095, 94, 5, 'یقیناً ہر دشواری کے ساتھ آسانی ہے۔'),
(6096, 94, 6, 'بےشک ہر تنگی کے ساتھ فراخی ہے۔'),
(6097, 94, 7, 'جب (کارِ رسا لت سے) فارغ ہوں تو (عبادت و ریا ضت کرنے میں) محنت کیجئے۔'),
(6098, 94, 8, 'اور اپنے پروردگار کی طرف رغبت کیجئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'قَسم ہے انجیر اور زیتون کی۔'),
(6100, 95, 2, 'اور طُورِ سینا کی۔'),
(6101, 95, 3, 'اور پُرامن شہر (مکہ) کی۔'),
(6102, 95, 4, 'ہم نے انسان کو بہترین ساخت و انداز کے ساتھ پیدا کیا ہے۔'),
(6103, 95, 5, 'پھر اسے (اس کی کج رفتاری کی وجہ سے) پست ترین حالت کی طرف لوٹا دیا۔'),
(6104, 95, 6, 'سوائے ان لوگوں کے جو ایمان لائے اور نیک عمل کئے سو ان کیلئے ختم نہ ہو نے والا اجر ہے۔'),
(6105, 95, 7, 'تواس کے بعد آپ(ص) کو جزا و سزا کے معاملہ میں کون جھٹلا سکتا ہے؟'),
(6106, 95, 8, 'کیا اللہ سبحانہٗ سب حاکموں سے بڑا حاکم نہیں ہے؟');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, '(اے رسول(ص)) اپنے پروردگار کے نام سے پڑھیے جس نے (سب کائنات کو) پیدا کیا۔'),
(6108, 96, 2, '(بالخصوص) انسان کو جمے ہوئے خون کے ایک لوتھڑے سے پیدا کیا۔'),
(6109, 96, 3, 'پڑھئیے! اور آپ کا پروردگار بڑا کریم ہے۔'),
(6110, 96, 4, 'جس نے قَلم کے ذریعہ سے علم سکھایا۔'),
(6111, 96, 5, 'اور انسان کو وہ کچھ پڑھایا جو وہ نہیں جانتا تھا۔'),
(6112, 96, 6, 'ہرگز نہیں! انسان (اس وقت) سرکشی کرنے لگتا ہے۔'),
(6113, 96, 7, 'جب وہ سمجھتا ہے کہ وہ (غنی) بےنیاز ہے۔'),
(6114, 96, 8, 'بےشک آپ(ص) کے پروردگار کی طرف ہی (سب کی) بازگشت ہے۔'),
(6115, 96, 9, 'کیا آپ(ص) نے اس شخص کو دیکھا ہے جو منع کرتا ہے۔'),
(6116, 96, 10, 'ایک بندہ (خاص) کو جب وہ نماز پڑھتا ہے۔'),
(6117, 96, 11, 'بھلا دیکھئے تو! اگر وہ (بندۂ خاص) ہدایت پر ہے؟'),
(6118, 96, 12, 'یا وہ پرہیزگاری کا حکم دیتا ہے؟'),
(6119, 96, 13, 'کیا آپ نے غور کیا ہے کہ اگر یہ شخص (حق کو) جھٹلاتا ہے اور (اس سے) رُوگردانی کرتا ہے توانجام کیا ہوگا؟'),
(6120, 96, 14, 'کیا وہ نہیں جانتا کہ اللہ (سب کچھ) دیکھ رہا ہے؟'),
(6121, 96, 15, 'ہرگز نہیں اگر وہ (اس سے) باز نہ آیا تو ہم اس کی پیشانی کے بال پکڑ کر کھینچیں گے۔'),
(6122, 96, 16, 'وہ پیشانی جو جھوٹی ہے اور گنہگار؟'),
(6123, 96, 17, 'پس وہ بلائے اپنے ہم نشینوں کو۔'),
(6124, 96, 18, 'ہم بھی دوزخ کے ہرکاروں (فرشتوں) کو بلائیں گے۔'),
(6125, 96, 19, 'ہرگز نہیں! اس کی بات نہ مانیے اورسجدہ کیجئے اور (اپنے پروردگار کا) قرب حاصل کیجئے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بےشک ہم نے اس (قرآن) کوشبِ قدر میں نازل کیا۔'),
(6127, 97, 2, 'اور آپ کو کیا معلوم کہ شب قدر کیا ہے؟'),
(6128, 97, 3, 'شبِ قدر ہزار مہینوں سے بہتر ہے۔'),
(6129, 97, 4, 'اس میں فرشتے اور روح اپنے پروردگار کی اجازت سے (سال بھر کی) ہر بات کا حکم لے کر اترتے ہیں۔'),
(6130, 97, 5, 'وہ (رات) سراسر سلامتی ہے طلوعِ صبح تک۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'اہلِ کتاب اور مشرکین میں سے جو لوگ کافر تھے وہ (اپنے کفر سے) باز آنے والے نہ تھے جب تک ان کے پاس ایک واضح دلیل نہ آجائے۔'),
(6132, 98, 2, 'یعنی اللہ کا ایک رسول(ع) جو انہیں پاک و پاکیزہ صحیفے پڑھ کر سنا ئے۔'),
(6133, 98, 3, 'جن میں بالکل درست باتیں لکھی ہوئی ہوں۔'),
(6134, 98, 4, 'اور جن لوگوں کو کتاب دی گئی تھی (اور وہ اہلِ کتاب تھے) وہ تو واضح دلیل کے آجانے کے بعد ہی تفرقہ میں پڑے۔'),
(6135, 98, 5, 'حالانکہ انہیں تو بس یہی حکم دیا گیا تھا کہ دین کو اسی کیلئے خالص کرتے ہوئے اللہ کی عبادت کریں بالکل یکسُو ہو کر اور نماز قائم کریں اور زکوٰۃ دیں اور یہی نہایت (صحیح اور) درست دین ہے۔'),
(6136, 98, 6, 'بےشک جو اہلِ کتاب اور مشرکین کفر میں مبتلا ہیں وہ جہنم کی آگ میں پڑیں گے (اور) ہمیشہ اس میں رہیں گے یہی لوگ بدترینِ خلائق ہیں۔'),
(6137, 98, 7, 'اور بےشک جو لوگ ایمان لائے اور نیک عمل کئے وہی بہترینِ خلائق ہیں۔'),
(6138, 98, 8, 'ان کی جزا ان کے پروردگار کے ہاں ہمیشگی کی جنتیں ہیں جن کے نیچے نہریں جاری ہیں وہ ان میں ہمیشہ ہمیشہ رہیں گے اللہ ان سے راضی ہے اور وہ اللہ سے راضی ہیں (یہ سب کچھ) اس کے لئے ہے جو اپنے پروردگار (کی حکم عدولی) سے ڈرتا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'جب زمین پوری شدت سے ہلا ڈالی جائے گی۔'),
(6140, 99, 2, 'اور زمین اپنے بوجھوں کو باہر نکال دے گی۔'),
(6141, 99, 3, 'اور انسان کہے گا کہ اسے کیا ہوگیا ہے؟'),
(6142, 99, 4, 'اس دن وہ اپنی تمام خبریں بیان کرے گی۔'),
(6143, 99, 5, 'کیونکہ تمہارے پروردگار کا اسے یہی حکم ہوگا۔'),
(6144, 99, 6, 'اس دن لوگ متفرق طور پر (اپنی قبروں سے) نکلیں گے تاکہ انہیں ان کے اعمال (کے نتائج) دکھائے جائیں۔'),
(6145, 99, 7, 'تو جس نے ذرہ برابر نیکی کی ہوگی وہ اس (کی جزا) دیکھ لے گا۔'),
(6146, 99, 8, 'اور جس نے ذرہ برابر بدی کی ہوگی وہ بھی اس (کی سزا) کو دیکھ لے گا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'قَسم ہے ان گھوڑوں کی جو پھنکارے مارتے ہوئے دوڑتے ہیں۔'),
(6148, 100, 2, 'پھر اپنی ٹاپوں کی ٹھوکر سے (پتھر سے) چنگاریاں نکا لتے ہیں۔'),
(6149, 100, 3, 'پھر صبح کے وقت (اچانک) چھاپہ مارتے ہیں۔'),
(6150, 100, 4, 'تو اس طرح گَرد و غبار اڑاتے ہیں۔'),
(6151, 100, 5, 'پھر اسی حالت میں (دشمن کی) جماعت (لشکر) میں گھس جاتے ہیں۔'),
(6152, 100, 6, 'بےشک انسان اپنے پروردگار کا بڑا ناشکرا ہے۔'),
(6153, 100, 7, 'اور وہ خود اس پر گواہ ہے۔'),
(6154, 100, 8, 'اور وہ مال و دولت کی محبت میں بڑا سخت (گرفتار) ہے۔'),
(6155, 100, 9, 'کیا وہ اس وقت کو نہیں جانتا جب قبروں سے نکال لیا جائے گا جو کچھ ان میں (دفن) ہے۔'),
(6156, 100, 10, 'اور جو کچھ سینوں میں (چھپا ہوا) ہے وہ ظاہر کر دیا جائے گا۔'),
(6157, 100, 11, 'اس دن ان کا پروردگار ان کے حالات سے بڑا باخبر ہوگا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کھڑکھڑانے والی (بڑاحادثہ)۔'),
(6159, 101, 2, 'کیا ہے وہ کھڑکھڑانے والی۔'),
(6160, 101, 3, 'اورآپ کو کیا معلوم کہ وہ کھڑکھڑانے والی کیا ہے۔'),
(6161, 101, 4, 'جس دن سب لوگ بکھرے ہوئے پروانوں کی طرح ہوں گے۔'),
(6162, 101, 5, 'اور پہاڑ دھنکی ہوئی روئی کی مانند ہوں گے۔'),
(6163, 101, 6, 'تو جس کی (نیکیوں کے) پلڑے بھاری ہوں گے۔'),
(6164, 101, 7, 'تو وہ دل پسند عیش و آرام میں ہوگا۔'),
(6165, 101, 8, 'اور جس کے (نیکیوں کے پلڑے) ہلکے ہوں گے۔'),
(6166, 101, 9, 'ان کا ٹھکانہ گہرا گڑھا (ھاویہ) ہوگا۔'),
(6167, 101, 10, 'اور تمہیں کیا معلوم کہ وہ (ھاویہ) کیا ہے۔'),
(6168, 101, 11, 'وہ بھڑکتی ہوئی آگ ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تمہیں کثرت (مال و اولاد) کی مسابقت نے غفلت میں رکھا۔'),
(6170, 102, 2, 'یہاں تک کہ تم قبروں میں جا پہنچے۔'),
(6171, 102, 3, 'ہرگز نہیں! عنقریب تمہیں معلوم ہو جائے گا۔'),
(6172, 102, 4, 'پھر ہرگز نہیں! عنقریب تمہیں معلوم ہو جائے گا۔'),
(6173, 102, 5, 'ہرگز نہیں! اگر تم یقینی طور پر (اس روش کے انجام کو) جانتے (تو ہرگز ایسا نہ کرتے)۔'),
(6174, 102, 6, 'تم ضرور دوزخ کو دیکھو گے۔'),
(6175, 102, 7, 'پھر تم لوگ (آخرت میں) ضرور یقین کی آنکھ سے اسے (یقینی دیکھنا) دیکھو گے۔'),
(6176, 102, 8, 'پھر اس دن تم سے نعمتوں کے بارے میں ضرور بازپُرس کی جائے گی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'قَسم ہے زمانے کی۔'),
(6178, 103, 2, 'یقیناً (ہر) انسان گھاٹے میں ہے۔'),
(6179, 103, 3, 'سوائے ان لوگوں کے جو ایمان لائے اور نیک عمل کئے اور ایک دوسرے کو حق کی وصیت کی اورصبر کی نصیحت کی۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'تباہی ہے ہر اس شخص کیلئے جو (رُوبرُو) طعن و تشنیع کرنے والا (اور پسِ پشت) عیب جوئی کرنے والا ہے۔'),
(6181, 104, 2, 'جو مال جمع کرتا ہے اور اسے گن گن کر رکھتا ہے۔'),
(6182, 104, 3, 'کیا وہ خیال کرتا ہے کہ اس کا مال اسے ہمیشہ زندہ رکھے گا۔'),
(6183, 104, 4, 'ہرگز نہیں وہ تو چکناچور کر دینے والی جگہ میں پھینک دیا جائے گا۔'),
(6184, 104, 5, 'اور تمہیں کیا معلوم ہے کہ وہ چکناچور کردینے والی جگہ کیا ہے؟'),
(6185, 104, 6, 'وہ اللہ کی خوب بھڑکائی ہوئی آگ ہے۔'),
(6186, 104, 7, 'جو دلوں تک جا پہنچے گی۔'),
(6187, 104, 8, 'جو چَو طرفہ طور پر ان پر بند کر دی جائے گی۔'),
(6188, 104, 9, 'وہ لمبے لمبے ستونوں (جیسے شعلوں) میں (گِھرے ہوئے ہوں گے)۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'کیا آپ نے نہیں دیکھا کہ آپ کے پروردگار نے ہاتھی والوں کے ساتھ کیا (سلوک) کیا؟'),
(6190, 105, 2, 'کیا اس نے ان کی تدبیر و ترکیب کو بےکار نہیں کر دیا؟'),
(6191, 105, 3, 'ان پر (ہر سمت سے) ابابیل نامی پرندوں کے جھنڈ کے جھنڈ بھیج دیئے۔'),
(6192, 105, 4, 'جو ان پر سنگِ گل (پکی ہوئی مٹی کے پتھر) مارتے تھے۔'),
(6193, 105, 5, 'آخرکار اللہ نے انہیں (مویشیوں کے) کھائے ہوئے بھو سے کی طرح کر دیا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'چونکہ (اللہ نے) قریش کو مانوس کر دیا۔'),
(6195, 106, 2, 'یعنی جاڑے اور گرمی کے تجارتی سفر سے مانوس کر دیا ہے۔'),
(6196, 106, 3, 'تو ان کو چاہیے کہ اس گھر (خانۂ کعبہ) کے پروردگار کی عبادت کریں۔'),
(6197, 106, 4, 'جس نے ان کو بھوک میں کھانے کو دیا اور خوف میں امن عطا کیا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'کیا آپ نے اس شخص کو دیکھا ہے جو جزا و سزا (کے دن) کو جھٹلاتا ہے۔'),
(6199, 107, 2, 'پس یہی وہ ہے جو یتیم کو دھکے دیتا ہے۔'),
(6200, 107, 3, 'اور کسی مسکین کو کھانا کھلانے پر (لوگوں) کو آمادہ نہیں کرتا۔'),
(6201, 107, 4, 'تباہی ہے ان نمازیوں کیلئے۔'),
(6202, 107, 5, 'جو اپنی نماز (کی ادائیگی) میں غفلت برتتے ہیں۔'),
(6203, 107, 6, 'جو ریاکاری کرتے ہیں۔'),
(6204, 107, 7, 'اور معمولی روزمرہ ضرورت کی چیزیں بھی (لوگوں کو) نہیں دیتے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, '(اے رسول(ص)) ہم نے آپ(ص) کو کوثر عطا کی ہے۔'),
(6206, 108, 2, 'پس آپ(ص) اپنے پروردگار کے لئے نماز پڑھا کریں اور قربانی کریں۔'),
(6207, 108, 3, 'بےشک آپ(ص) کا دشمن ہی نسل بُریدہ ہوگا۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, '(اے رسول(ص)) آپ(ص) کہہ دیجئے اے کافرو۔'),
(6209, 109, 2, 'میں ان (بتوں) کی عبادت نہیں کرتا جن کی تم عبادت کرتے ہو۔'),
(6210, 109, 3, 'اور نہ تم اس (اللہ) کی عبادت کرنے والے ہو جس کی میں عبادت کرتا ہوں۔'),
(6211, 109, 4, 'اور نہ میں ان (بتوں) کی عبادت کرنے والا ہوں جن کی تم عبادت کرتے ہو۔'),
(6212, 109, 5, 'اور نہ تم (اللہ) کی عبادت کرنے والے ہو جس کی میں عبادت کرتا ہوں۔'),
(6213, 109, 6, 'تمہارے لئے تمہارا دین ہے اور میرے لئے میرا دین۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'جب اللہ کی مدد اور اس کی فتح و فیروزی آجائے۔'),
(6215, 110, 2, 'اور آپ دیکھ لیں کہ لوگ فوج در فوج اللہ کے دین میں داخل ہو رہے ہیں۔'),
(6216, 110, 3, 'تو (اس وقت) اپنے پروردگار کی حمد کے ساتھ اس کی تسبیح کریں اور اس سے مغفرت طلب کیجئے۔ بیشک وہ بڑا توبہ قبول کرنے والا ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ابولہب کے دونوں ہاتھ ٹوٹ جائیں اور وہ ہلاک و برباد ہوا۔'),
(6218, 111, 2, 'اس کامال اور جو کچھ اس نے کمایا وہ اس کے کچھ کام نہ آیا۔'),
(6219, 111, 3, 'وہ عنقریب شعلہ زن آگ میں ڈالا جائے گا۔'),
(6220, 111, 4, 'اور (اس کے ساتھ) اس کی بیوی بھی جو ایندھن اٹھانے والی ہے۔'),
(6221, 111, 5, 'اور اس کی گردن میں مُونج کی بٹی ہوئی رسی ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, '(اے رسول(ص)) آپ(ص) کہہ دیجئے! کہ وہ (اللہ) ایک ہے۔'),
(6223, 112, 2, 'اللہ (ساری کائنات سے) بےنیاز ہے۔'),
(6224, 112, 3, 'نہ اس کی کوئی اولاد ہے اور نہ وہ کسی کی اولاد ہے۔'),
(6225, 112, 4, 'اور اس کا کوئی ہمسر نہیں ہے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, '(اے رسول(ص)) آپ(ص) کہہ دیجئے کہ میں پناہ لیتا ہوں صبح کے پروردگار کی۔'),
(6227, 113, 2, 'ہر اس چیز کے شر سے جو اس نے پیدا کی ہے۔'),
(6228, 113, 3, 'اور رات کی تاریکی کے شر سے جبکہ وہ چھا جائے۔'),
(6229, 113, 4, 'اوران کے شر سے جو گِرہوں میں پھونکے مارتی ہیں۔'),
(6230, 113, 5, 'اورحاسد کے شر سے جب وہ حسد کرے۔');
INSERT INTO `ur_najafi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, '(اے رسول(ص)) آپ(ص) کہہ دیجئے! کہ میں پناہ لیتا ہوں سب لوگوں کے پروردگار کی۔'),
(6232, 114, 2, 'سب انسانوں کے بادشاہ کی۔'),
(6233, 114, 3, 'سب لوگوں کے الٰہ (خدا) کی۔'),
(6234, 114, 4, 'بار بار وسوسہ ڈالنے بار بار پسپا ہونے والے کے شر سے۔'),
(6235, 114, 5, 'جو لوگوں کے دلوں میں وسوسہ ڈالتا ہے۔'),
(6236, 114, 6, 'خواہ وہ جِنوں میں سے ہوں یا انسانوں میں سے۔');
