


CREATE TABLE `quran_words` (
  `id` int(10) UNSIGNED NOT NULL,
  `aya` int(10) DEFAULT NULL,
  `sura` int(10) DEFAULT NULL,
  `position` int(10) DEFAULT NULL,
  `verse_key` varchar(100) DEFAULT NULL,
  `text` text CHARACTER SET utf8mb4,
  `simple` text CHARACTER SET utf8mb4,
  `juz` int(10) DEFAULT NULL,
  `hezb` int(10) DEFAULT NULL,
  `rub` int(10) DEFAULT NULL,
  `page` int(10) DEFAULT NULL,
  `class_name` varchar(100) DEFAULT NULL,
  `line` int(10) DEFAULT NULL,
  `code` varchar(100) DEFAULT NULL,
  `code_v3` varchar(100) DEFAULT NULL,
  `char_type` varchar(100) DEFAULT NULL,
  `audio` text,
  `translation` text
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


